INSERT INTO `Quran` (`rowid`, `id`, `Arabic`, `Chapter`, `Verse`, `Juz`, `Hizb`, `Page`, `tr`) VALUES
(1, 1, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ', 1, 1, 1, 1, 1, 'به نام خداوند بخشنده بخشایشگر'),
(2, 2, 'الْحَمْدُ لِلَّهِ رَبِّ الْعَالَمِينَ', 1, 2, 1, 1, 1, 'ستایش مخصوص خداوندی است که پروردگار جهانیان است.'),
(3, 3, 'الرَّحْمَٰنِ الرَّحِيمِ', 1, 3, 1, 1, 1, '(خداوندی که) بخشنده و بخشایشگر است (و رحمت عام و خاصش همگان را فرا گرفته).'),
(4, 4, 'مَالِكِ يَوْمِ الدِّينِ', 1, 4, 1, 1, 1, '(خداوندی که) مالک روز جزاست.'),
(5, 5, 'إِيَّاكَ نَعْبُدُ وَإِيَّاكَ نَسْتَعِينُ', 1, 5, 1, 1, 1, '(پروردگارا!) تنها تو را می‌پرستیم؛ و تنها از تو یاری می‌جوییم.'),
(6, 6, 'اهْدِنَا الصِّرَاطَ الْمُسْتَقِيمَ', 1, 6, 1, 1, 1, 'ما را به راه راست هدایت کن...'),
(7, 7, 'صِرَاطَ الَّذِينَ أَنْعَمْتَ عَلَيْهِمْ غَيْرِ الْمَغْضُوبِ عَلَيْهِمْ وَلَا الضَّالِّينَ', 1, 7, 1, 1, 1, 'راه کسانی که آنان را مشمول نعمت خود ساختی؛ نه کسانی که بر آنان غضب کرده‌ای؛ و نه گمراهان.'),
(8, 8, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ الم', 2, 1, 1, 1, 2, 'الم (بزرگ است خداوندی که این کتاب عظیم را، از حروف ساده الفبا به وجود آورده).'),
(9, 9, 'ذَٰلِكَ الْكِتَابُ لَا رَيْبَ ۛ فِيهِ ۛ هُدًى لِلْمُتَّقِينَ', 2, 2, 1, 1, 2, 'آن کتاب با عظمتی است که شک در آن راه ندارد؛ و مایه هدایت پرهیزکاران است.'),
(10, 10, 'الَّذِينَ يُؤْمِنُونَ بِالْغَيْبِ وَيُقِيمُونَ الصَّلَاةَ وَمِمَّا رَزَقْنَاهُمْ يُنْفِقُونَ', 2, 3, 1, 1, 2, '(پرهیزکاران) کسانی هستند که به غیب =آنچه از حس پوشیده و پنهان است‌ ایمان می‌آورند؛ و نماز را برپا می‌دارند؛ و از تمام نعمتها و مواهبی که به آنان روزی داده‌ایم، انفاق می‌کنند.'),
(11, 11, 'وَالَّذِينَ يُؤْمِنُونَ بِمَا أُنْزِلَ إِلَيْكَ وَمَا أُنْزِلَ مِنْ قَبْلِكَ وَبِالْآخِرَةِ هُمْ يُوقِنُونَ', 2, 4, 1, 1, 2, 'و آنان که به آنچه بر تو نازل شده، و آنچه پیش از تو (بر پیامبران پیشین) نازل گردیده، ایمان می‌آورند؛ و به رستاخیز یقین دارند.'),
(12, 12, 'أُولَٰئِكَ عَلَىٰ هُدًى مِنْ رَبِّهِمْ ۖ وَأُولَٰئِكَ هُمُ الْمُفْلِحُونَ', 2, 5, 1, 1, 2, 'آنان بر طریق هدایت پروردگارشانند؛ و آنان رستگارانند.'),
(13, 13, 'إِنَّ الَّذِينَ كَفَرُوا سَوَاءٌ عَلَيْهِمْ أَأَنْذَرْتَهُمْ أَمْ لَمْ تُنْذِرْهُمْ لَا يُؤْمِنُونَ', 2, 6, 1, 1, 3, 'کسانی که کافر شدند، برای آنان تفاوت نمی‌کند که آنان را (از عذاب الهی) بترسانی یا نترسانی؛ ایمان نخواهند آورد.'),
(14, 14, 'خَتَمَ اللَّهُ عَلَىٰ قُلُوبِهِمْ وَعَلَىٰ سَمْعِهِمْ ۖ وَعَلَىٰ أَبْصَارِهِمْ غِشَاوَةٌ ۖ وَلَهُمْ عَذَابٌ عَظِيمٌ', 2, 7, 1, 1, 3, 'خدا بر دلها و گوشهای آنان مهر نهاده؛ و بر چشمهایشان پرده‌ای افکنده شده؛ و عذاب بزرگی در انتظار آنهاست.'),
(15, 15, 'وَمِنَ النَّاسِ مَنْ يَقُولُ آمَنَّا بِاللَّهِ وَبِالْيَوْمِ الْآخِرِ وَمَا هُمْ بِمُؤْمِنِينَ', 2, 8, 1, 1, 3, 'گروهی از مردم کسانی هستند که می‌گویند: «به خدا و روز رستاخیز ایمان آورده‌ایم.» در حالی که ایمان ندارند.'),
(16, 16, 'يُخَادِعُونَ اللَّهَ وَالَّذِينَ آمَنُوا وَمَا يَخْدَعُونَ إِلَّا أَنْفُسَهُمْ وَمَا يَشْعُرُونَ', 2, 9, 1, 1, 3, 'می‌خواهند خدا و مؤمنان را فریب دهند؛ در حالی که جز خودشان را فریب نمی‌دهند؛ (اما) نمی‌فهمند.'),
(17, 17, 'فِي قُلُوبِهِمْ مَرَضٌ فَزَادَهُمُ اللَّهُ مَرَضًا ۖ وَلَهُمْ عَذَابٌ أَلِيمٌ بِمَا كَانُوا يَكْذِبُونَ', 2, 10, 1, 1, 3, 'در دلهای آنان یک نوع بیماری است؛ خداوند بر بیماری آنان افزوده؛ و به خاطر دروغهایی که میگفتند، عذاب دردناکی در انتظار آنهاست.'),
(18, 18, 'وَإِذَا قِيلَ لَهُمْ لَا تُفْسِدُوا فِي الْأَرْضِ قَالُوا إِنَّمَا نَحْنُ مُصْلِحُونَ', 2, 11, 1, 1, 3, 'و هنگامی که به آنان گفته شود: «در زمین فساد نکنید» می‌گویند: «ما فقط اصلاح‌کننده‌ایم»!'),
(19, 19, 'أَلَا إِنَّهُمْ هُمُ الْمُفْسِدُونَ وَلَٰكِنْ لَا يَشْعُرُونَ', 2, 12, 1, 1, 3, 'آگاه باشید! اینها همان مفسدانند؛ ولی نمی‌فهمند.'),
(20, 20, 'وَإِذَا قِيلَ لَهُمْ آمِنُوا كَمَا آمَنَ النَّاسُ قَالُوا أَنُؤْمِنُ كَمَا آمَنَ السُّفَهَاءُ ۗ أَلَا إِنَّهُمْ هُمُ السُّفَهَاءُ وَلَٰكِنْ لَا يَعْلَمُونَ', 2, 13, 1, 1, 3, 'و هنگامی که به آنان گفته شود: «همانند (سایر) مردم ایمان بیاورید!» می‌گویند: «آیا همچون ابلهان ایمان بیاوریم؟!» بدانید اینها همان ابلهانند ولی نمی‌دانند!'),
(21, 21, 'وَإِذَا لَقُوا الَّذِينَ آمَنُوا قَالُوا آمَنَّا وَإِذَا خَلَوْا إِلَىٰ شَيَاطِينِهِمْ قَالُوا إِنَّا مَعَكُمْ إِنَّمَا نَحْنُ مُسْتَهْزِئُونَ', 2, 14, 1, 1, 3, 'و هنگامی که افراد باایمان را ملاقات می‌کنند، و می‌گویند: «ما ایمان آورده‌ایم!» (ولی) هنگامی که با شیطانهای خود خلوت می‌کنند، می‌گویند: «ما با شمائیم! ما فقط (آنها را) مسخره می‌کنیم!»'),
(22, 22, 'اللَّهُ يَسْتَهْزِئُ بِهِمْ وَيَمُدُّهُمْ فِي طُغْيَانِهِمْ يَعْمَهُونَ', 2, 15, 1, 1, 3, 'خداوند آنان را استهزا می‌کند؛ و آنها را در طغیانشان نگه می‌دارد، تا سرگردان شوند.'),
(23, 23, 'أُولَٰئِكَ الَّذِينَ اشْتَرَوُا الضَّلَالَةَ بِالْهُدَىٰ فَمَا رَبِحَتْ تِجَارَتُهُمْ وَمَا كَانُوا مُهْتَدِينَ', 2, 16, 1, 1, 3, 'آنان کسانی هستند که «هدایت» را به «گمراهی» فروخته‌اند؛ و (این) تجارت آنها سودی نداده؛ و هدایت نیافته‌اند.'),
(24, 24, 'مَثَلُهُمْ كَمَثَلِ الَّذِي اسْتَوْقَدَ نَارًا فَلَمَّا أَضَاءَتْ مَا حَوْلَهُ ذَهَبَ اللَّهُ بِنُورِهِمْ وَتَرَكَهُمْ فِي ظُلُمَاتٍ لَا يُبْصِرُونَ', 2, 17, 1, 1, 4, 'آنان = منافقان‌ همانند کسی هستند که آتشی افروخته (تا در بیابان تاریک، راه خود را پیدا کند)، ولی هنگامی که آتش اطراف او را روشن ساخت، خداوند (طوفانی می‌فرستد و) آن را خاموش می‌کند؛ و در تاریکیهای وحشتناکی که چشم کار نمی‌کند، آنها را رها می‌سازد.'),
(25, 25, 'صُمٌّ بُكْمٌ عُمْيٌ فَهُمْ لَا يَرْجِعُونَ', 2, 18, 1, 1, 4, 'آنها کران، گنگها و کورانند؛ لذا (از راه خطا) بازنمی‌گردند!'),
(26, 26, 'أَوْ كَصَيِّبٍ مِنَ السَّمَاءِ فِيهِ ظُلُمَاتٌ وَرَعْدٌ وَبَرْقٌ يَجْعَلُونَ أَصَابِعَهُمْ فِي آذَانِهِمْ مِنَ الصَّوَاعِقِ حَذَرَ الْمَوْتِ ۚ وَاللَّهُ مُحِيطٌ بِالْكَافِرِينَ', 2, 19, 1, 1, 4, 'یا همچون بارانی از آسمان، که در شب تاریک همراه با رعد و برق و صاعقه (بر سر رهگذران) ببارد. آنها از ترس مرگ، انگشتانشان را در گوشهای خود می‌گذارند؛ تا صدای صاعقه را نشنوند. و خداوند به کافران احاطه دارد (و در قبضه قدرت او هستند).'),
(27, 27, 'يَكَادُ الْبَرْقُ يَخْطَفُ أَبْصَارَهُمْ ۖ كُلَّمَا أَضَاءَ لَهُمْ مَشَوْا فِيهِ وَإِذَا أَظْلَمَ عَلَيْهِمْ قَامُوا ۚ وَلَوْ شَاءَ اللَّهُ لَذَهَبَ بِسَمْعِهِمْ وَأَبْصَارِهِمْ ۚ إِنَّ اللَّهَ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ', 2, 20, 1, 1, 4, '(روشنائی خیره کننده) برق، نزدیک است چشمانشان را برباید. هر زمان که (برق جستن می‌کند، و صفحه بیابان را) برای آنها روشن می‌سازد، (چند گامی) در پرتو آن راه می‌روند؛ و چون خاموش می‌شود، توقف می‌کنند. و اگر خدا بخواهد، گوش و چشم آنها را از بین می‌برد؛ چرا که خداوند بر هر چیز تواناست.'),
(28, 28, 'يَا أَيُّهَا النَّاسُ اعْبُدُوا رَبَّكُمُ الَّذِي خَلَقَكُمْ وَالَّذِينَ مِنْ قَبْلِكُمْ لَعَلَّكُمْ تَتَّقُونَ', 2, 21, 1, 1, 4, 'ای مردم! پروردگار خود را پرستش کنید؛ آن کس که شما، و کسانی را که پیش از شما بودند آفرید، تا پرهیزکار شوید.'),
(29, 29, 'الَّذِي جَعَلَ لَكُمُ الْأَرْضَ فِرَاشًا وَالسَّمَاءَ بِنَاءً وَأَنْزَلَ مِنَ السَّمَاءِ مَاءً فَأَخْرَجَ بِهِ مِنَ الثَّمَرَاتِ رِزْقًا لَكُمْ ۖ فَلَا تَجْعَلُوا لِلَّهِ أَنْدَادًا وَأَنْتُمْ تَعْلَمُونَ', 2, 22, 1, 1, 4, 'آن کس که زمین را بستر شما، و آسمان = جو زمین‌ را همچون سقفی بالای سر شما قرار داد؛ و از آسمان آبی فرو فرستاد؛ و به وسیله آن، میوه‌ها را پرورش داد؛ تا روزی شما باشد. بنابر این، برای خدا همتایانی قرار ندهید، در حالی که می‌دانید (هیچ یک از آنها، نه شما را آفریده‌اند، و نه شما را روزی می‌دهند).'),
(30, 30, 'وَإِنْ كُنْتُمْ فِي رَيْبٍ مِمَّا نَزَّلْنَا عَلَىٰ عَبْدِنَا فَأْتُوا بِسُورَةٍ مِنْ مِثْلِهِ وَادْعُوا شُهَدَاءَكُمْ مِنْ دُونِ اللَّهِ إِنْ كُنْتُمْ صَادِقِينَ', 2, 23, 1, 1, 4, 'و اگر در باره آنچه بر بنده خود = پیامبر نازل کرده‌ایم شک و تردید دارید، (دست کم) یک سوره همانند آن بیاورید؛ و گواهان خود را - غیر خدا - برای این کار، فرا خوانید اگر راست می‌گویید!'),
(31, 31, 'فَإِنْ لَمْ تَفْعَلُوا وَلَنْ تَفْعَلُوا فَاتَّقُوا النَّارَ الَّتِي وَقُودُهَا النَّاسُ وَالْحِجَارَةُ ۖ أُعِدَّتْ لِلْكَافِرِينَ', 2, 24, 1, 1, 4, 'پس اگر چنین نکنید - که هرگز نخواهید کرد - از آتشی بترسید که هیزم آن، بدنهای مردم (گنهکار) و سنگها = بتها است، و برای کافران، آماده شده است!'),
(32, 32, 'وَبَشِّرِ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ أَنَّ لَهُمْ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهَارُ ۖ كُلَّمَا رُزِقُوا مِنْهَا مِنْ ثَمَرَةٍ رِزْقًا ۙ قَالُوا هَٰذَا الَّذِي رُزِقْنَا مِنْ قَبْلُ ۖ وَأُتُوا بِهِ مُتَشَابِهًا ۖ وَلَهُمْ فِيهَا أَزْوَاجٌ مُطَهَّرَةٌ ۖ وَهُمْ فِيهَا خَالِدُونَ', 2, 25, 1, 1, 5, 'به کسانی که ایمان آورده، و کارهای شایسته انجام داده‌اند، بشارت ده که باغهایی از بهشت برای آنهاست که نهرها از زیر درختانش جاریست. هر زمان که میوه‌ای از آن، به آنان داده شود، می‌گویند: «این همان است که قبلا به ما روزی داده شده بود. (ولی اینها چقدر از آنها بهتر و عالیتر است.)» و میوه‌هایی که برای آنها آورده می‌شود، همه (از نظر خوبی و زیبایی) یکسانند. و برای آنان همسرانی پاک و پاکیزه است، و جاودانه در آن خواهند بود.'),
(33, 33, ' إِنَّ اللَّهَ لَا يَسْتَحْيِي أَنْ يَضْرِبَ مَثَلًا مَا بَعُوضَةً فَمَا فَوْقَهَا ۚ فَأَمَّا الَّذِينَ آمَنُوا فَيَعْلَمُونَ أَنَّهُ الْحَقُّ مِنْ رَبِّهِمْ ۖ وَأَمَّا الَّذِينَ كَفَرُوا فَيَقُولُونَ مَاذَا أَرَادَ اللَّهُ بِهَٰذَا مَثَلًا ۘ يُضِلُّ بِهِ كَثِيرًا وَيَهْدِي بِهِ كَثِيرًا ۚ وَمَا يُضِلُّ بِهِ إِلَّا الْفَاسِقِينَ', 2, 26, 1, 1, 5, 'خداوند از این که (به موجودات ظاهرا کوچکی مانند) پشه، و حتی کمتر از آن، مثال بزند شرم نمی‌کند. (در این میان) آنان که ایمان آورده‌اند، می‌دانند که آن، حقیقتی است از طرف پروردگارشان؛ و اما آنها که راه کفر را پیموده‌اند، (این موضوع را بهانه کرده) می‌گویند: «منظور خداوند از این مثل چه بوده است؟!» (آری،) خدا جمع زیادی را با آن گمراه، و گروه بسیاری را هدایت می‌کند؛ ولی تنها فاسقان را با آن گمراه می‌سازد!'),
(34, 34, 'الَّذِينَ يَنْقُضُونَ عَهْدَ اللَّهِ مِنْ بَعْدِ مِيثَاقِهِ وَيَقْطَعُونَ مَا أَمَرَ اللَّهُ بِهِ أَنْ يُوصَلَ وَيُفْسِدُونَ فِي الْأَرْضِ ۚ أُولَٰئِكَ هُمُ الْخَاسِرُونَ', 2, 27, 1, 1, 5, 'فاسقان کسانی هستند که پیمان خدا را، پس از محکم ساختن آن، میشکنند؛ و پیوندهایی را که خدا دستور داده برقرار سازند، قطع نموده، و در روی زمین فساد میکنند؛ اینها زیانکارانند.'),
(35, 35, 'كَيْفَ تَكْفُرُونَ بِاللَّهِ وَكُنْتُمْ أَمْوَاتًا فَأَحْيَاكُمْ ۖ ثُمَّ يُمِيتُكُمْ ثُمَّ يُحْيِيكُمْ ثُمَّ إِلَيْهِ تُرْجَعُونَ', 2, 28, 1, 1, 5, 'چگونه به خداوند کافر می‌شوید؟! در حالی که شما مردگان (و اجسام بی‌روحی) بودید، و او شما را زنده کرد؛ سپس شما را می‌میراند؛ و بار دیگر شما را زنده می‌کند؛ سپس به سوی او بازگردانده می‌شوید. (بنابر این، نه حیات و زندگی شما از شماست، و نه مرگتان؛ آنچه دارید از خداست).'),
(36, 36, 'هُوَ الَّذِي خَلَقَ لَكُمْ مَا فِي الْأَرْضِ جَمِيعًا ثُمَّ اسْتَوَىٰ إِلَى السَّمَاءِ فَسَوَّاهُنَّ سَبْعَ سَمَاوَاتٍ ۚ وَهُوَ بِكُلِّ شَيْءٍ عَلِيمٌ', 2, 29, 1, 1, 5, 'او خدایی است که همه آنچه را (از نعمتها) در زمین وجود دارد، برای شما آفرید؛ سپس به آسمان پرداخت؛ و آنها را به صورت هفت آسمان مرتب نمود؛ و او به هر چیز آگاه است.'),
(37, 37, 'وَإِذْ قَالَ رَبُّكَ لِلْمَلَائِكَةِ إِنِّي جَاعِلٌ فِي الْأَرْضِ خَلِيفَةً ۖ قَالُوا أَتَجْعَلُ فِيهَا مَنْ يُفْسِدُ فِيهَا وَيَسْفِكُ الدِّمَاءَ وَنَحْنُ نُسَبِّحُ بِحَمْدِكَ وَنُقَدِّسُ لَكَ ۖ قَالَ إِنِّي أَعْلَمُ مَا لَا تَعْلَمُونَ', 2, 30, 1, 1, 6, '(به خاطر بیاور) هنگامی را که پروردگارت به فرشتگان گفت: «من در روی زمین، جانشینی = نماینده‌ای‌ قرار خواهم داد.» فرشتگان گفتند: «پروردگارا!» آیا کسی را در آن قرار می‌دهی که فساد و خونریزی کند؟! (زیرا موجودات زمینی دیگر، که قبل از این آدم وجود داشتند نیز، به فساد و خونریزی آلوده شدند. اگر هدف از آفرینش این انسان، عبادت است،) ما تسبیح و حمد تو را بجا می‌آوریم، و تو را تقدیس می‌کنیم.» پروردگار فرمود: «من حقایقی را می‌دانم که شما نمی‌دانید.»'),
(38, 38, 'وَعَلَّمَ آدَمَ الْأَسْمَاءَ كُلَّهَا ثُمَّ عَرَضَهُمْ عَلَى الْمَلَائِكَةِ فَقَالَ أَنْبِئُونِي بِأَسْمَاءِ هَٰؤُلَاءِ إِنْ كُنْتُمْ صَادِقِينَ', 2, 31, 1, 1, 6, 'سپس علم اسماء = علم اسرار آفرینش و نامگذاری موجودات‌ را همگی به آدم آموخت. بعد آنها را به فرشتگان عرضه داشت و فرمود: «اگر راست می‌گویید، اسامی اینها را به من خبر دهید!»'),
(39, 39, 'قَالُوا سُبْحَانَكَ لَا عِلْمَ لَنَا إِلَّا مَا عَلَّمْتَنَا ۖ إِنَّكَ أَنْتَ الْعَلِيمُ الْحَكِيمُ', 2, 32, 1, 1, 6, 'فرشتگان عرض کردند: «منزهی تو! ما چیزی جز آنچه به ما تعلیم داده‌ای، نمی‌دانیم؛ تو دانا و حکیمی.»'),
(40, 40, 'قَالَ يَا آدَمُ أَنْبِئْهُمْ بِأَسْمَائِهِمْ ۖ فَلَمَّا أَنْبَأَهُمْ بِأَسْمَائِهِمْ قَالَ أَلَمْ أَقُلْ لَكُمْ إِنِّي أَعْلَمُ غَيْبَ السَّمَاوَاتِ وَالْأَرْضِ وَأَعْلَمُ مَا تُبْدُونَ وَمَا كُنْتُمْ تَكْتُمُونَ', 2, 33, 1, 1, 6, 'فرمود: «ای آدم! آنان را از اسامی (و اسرار) این موجودات آگاه کن.» هنگامی که آنان را آگاه کرد، خداوند فرمود: «آیا به شما نگفتم که من، غیب آسمانها و زمین را میدانم؟! و نیز میدانم آنچه را شما آشکار میکنید، و آنچه را پنهان میداشتید!»'),
(41, 41, 'وَإِذْ قُلْنَا لِلْمَلَائِكَةِ اسْجُدُوا لِآدَمَ فَسَجَدُوا إِلَّا إِبْلِيسَ أَبَىٰ وَاسْتَكْبَرَ وَكَانَ مِنَ الْكَافِرِينَ', 2, 34, 1, 1, 6, 'و (یاد کن) هنگامی را که به فرشتگان گفتیم: «برای آدم سجده و خضوع کنید!» همگی سجده کردند؛ جز ابلیس که سر باز زد، و تکبر ورزید، (و به خاطر نافرمانی و تکبرش) از کافران شد.'),
(42, 42, 'وَقُلْنَا يَا آدَمُ اسْكُنْ أَنْتَ وَزَوْجُكَ الْجَنَّةَ وَكُلَا مِنْهَا رَغَدًا حَيْثُ شِئْتُمَا وَلَا تَقْرَبَا هَٰذِهِ الشَّجَرَةَ فَتَكُونَا مِنَ الظَّالِمِينَ', 2, 35, 1, 1, 6, 'و گفتیم: «ای آدم! تو با همسرت در بهشت سکونت کن؛ و از (نعمتهای) آن، از هر جا می‌خواهید، گوارا بخورید؛ (اما) نزدیک این درخت نشوید؛ که از ستمگران خواهید شد.'),
(43, 43, 'فَأَزَلَّهُمَا الشَّيْطَانُ عَنْهَا فَأَخْرَجَهُمَا مِمَّا كَانَا فِيهِ ۖ وَقُلْنَا اهْبِطُوا بَعْضُكُمْ لِبَعْضٍ عَدُوٌّ ۖ وَلَكُمْ فِي الْأَرْضِ مُسْتَقَرٌّ وَمَتَاعٌ إِلَىٰ حِينٍ', 2, 36, 1, 1, 6, 'پس شیطان موجب لغزش آنها از بهشت شد؛ و آنان را از آنچه در آن بودند، بیرون کرد. و (در این هنگام) به آنها گفتیم: «همگی (به زمین) فرود آیید! در حالی که بعضی دشمن دیگری خواهید بود. و برای شما در زمین، تا مدت معینی قرارگاه و وسیله بهره برداری خواهد بود.»'),
(44, 44, 'فَتَلَقَّىٰ آدَمُ مِنْ رَبِّهِ كَلِمَاتٍ فَتَابَ عَلَيْهِ ۚ إِنَّهُ هُوَ التَّوَّابُ الرَّحِيمُ', 2, 37, 1, 1, 6, 'سپس آدم از پروردگارش کلماتی دریافت داشت؛ (و با آنها توبه کرد.) و خداوند توبه او را پذیرفت؛ چرا که خداوند توبه‌پذیر و مهربان است.'),
(45, 45, 'قُلْنَا اهْبِطُوا مِنْهَا جَمِيعًا ۖ فَإِمَّا يَأْتِيَنَّكُمْ مِنِّي هُدًى فَمَنْ تَبِعَ هُدَايَ فَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَ', 2, 38, 1, 1, 7, 'گفتیم: «همگی از آن، فرود آیید! هرگاه هدایتی از طرف من برای شما آمد، کسانی که از آن پیروی کنند، نه ترسی بر آنهاست، و نه غمگین شوند.»'),
(46, 46, 'وَالَّذِينَ كَفَرُوا وَكَذَّبُوا بِآيَاتِنَا أُولَٰئِكَ أَصْحَابُ النَّارِ ۖ هُمْ فِيهَا خَالِدُونَ', 2, 39, 1, 1, 7, 'و کسانی که کافر شدند، و آیات ما را دروغ پنداشتند اهل دوزخند؛ و همیشه در آن خواهند بود.'),
(47, 47, 'يَا بَنِي إِسْرَائِيلَ اذْكُرُوا نِعْمَتِيَ الَّتِي أَنْعَمْتُ عَلَيْكُمْ وَأَوْفُوا بِعَهْدِي أُوفِ بِعَهْدِكُمْ وَإِيَّايَ فَارْهَبُونِ', 2, 40, 1, 1, 7, 'ای فرزندان اسرائیل! نعمتهایی را که به شما ارزانی داشتم به یاد آورید! و به پیمانی که با من بسته‌اید وفا کنید، تا من نیز به پیمان شما وفا کنم. (و در راه انجام وظیفه، و عمل به پیمانها) تنها از من بترسید!'),
(48, 48, 'وَآمِنُوا بِمَا أَنْزَلْتُ مُصَدِّقًا لِمَا مَعَكُمْ وَلَا تَكُونُوا أَوَّلَ كَافِرٍ بِهِ ۖ وَلَا تَشْتَرُوا بِآيَاتِي ثَمَنًا قَلِيلًا وَإِيَّايَ فَاتَّقُونِ', 2, 41, 1, 1, 7, 'و به آنچه نازل کرده‌ام = قرآن‌ ایمان بیاورید! که نشانه‌های آن، با آنچه در کتابهای شماست، مطابقت دارد؛ و نخستین کافر به آن نباشید! و آیات مرا به بهای ناچیزی نفروشید! (و به خاطر درآمد مختصری، نشانه‌های قرآن و پیامبر اسلام را، که در کتب شما موجود است، پنهان نکنید!) و تنها از من (و مخالفت دستورهایم) بترسید (نه از مردم)!'),
(49, 49, 'وَلَا تَلْبِسُوا الْحَقَّ بِالْبَاطِلِ وَتَكْتُمُوا الْحَقَّ وَأَنْتُمْ تَعْلَمُونَ', 2, 42, 1, 1, 7, 'و حق را با باطل نیامیزید! و حقیقت را با اینکه می‌دانید کتمان نکنید!'),
(50, 50, 'وَأَقِيمُوا الصَّلَاةَ وَآتُوا الزَّكَاةَ وَارْكَعُوا مَعَ الرَّاكِعِينَ', 2, 43, 1, 1, 7, 'و نماز را بپا دارید، و زکات را بپردازید، و همراه رکوع کنندگان رکوع کنید (و نماز را با جماعت بگزارید)!'),
(51, 51, ' أَتَأْمُرُونَ النَّاسَ بِالْبِرِّ وَتَنْسَوْنَ أَنْفُسَكُمْ وَأَنْتُمْ تَتْلُونَ الْكِتَابَ ۚ أَفَلَا تَعْقِلُونَ', 2, 44, 1, 2, 7, 'آیا مردم را به نیکی (و ایمان به پیامبری که صفات او آشکارا در تورات آمده) دعوت می‌کنید، اما خودتان را فراموش می‌نمایید؛ با اینکه شما کتاب (آسمانی) را می‌خوانید! آیا نمی‌اندیشید؟!'),
(52, 52, 'وَاسْتَعِينُوا بِالصَّبْرِ وَالصَّلَاةِ ۚ وَإِنَّهَا لَكَبِيرَةٌ إِلَّا عَلَى الْخَاشِعِينَ', 2, 45, 1, 2, 7, 'از صبر و نماز یاری جوئید؛ (و با استقامت و مهار هوسهای درونی و توجه به پروردگار، نیرو بگیرید؛) و این کار، جز برای خاشعان، گران است.'),
(53, 53, 'الَّذِينَ يَظُنُّونَ أَنَّهُمْ مُلَاقُو رَبِّهِمْ وَأَنَّهُمْ إِلَيْهِ رَاجِعُونَ', 2, 46, 1, 2, 7, 'آنها کسانی هستند که می‌دانند دیدارکننده پروردگار خویشند، و به سوی او بازمی‌گردند.'),
(54, 54, 'يَا بَنِي إِسْرَائِيلَ اذْكُرُوا نِعْمَتِيَ الَّتِي أَنْعَمْتُ عَلَيْكُمْ وَأَنِّي فَضَّلْتُكُمْ عَلَى الْعَالَمِينَ', 2, 47, 1, 2, 7, 'ای بنی اسرائیل! نعمتهایی را که به شما ارزانی داشتم به خاطر بیاورید؛ و (نیز به یاد آورید که) من، شما را بر جهانیان، برتری بخشیدم.'),
(55, 55, 'وَاتَّقُوا يَوْمًا لَا تَجْزِي نَفْسٌ عَنْ نَفْسٍ شَيْئًا وَلَا يُقْبَلُ مِنْهَا شَفَاعَةٌ وَلَا يُؤْخَذُ مِنْهَا عَدْلٌ وَلَا هُمْ يُنْصَرُونَ', 2, 48, 1, 2, 7, 'و از آن روز بترسید که کسی مجازات دیگری را نمی‌پذیرد و نه از او شفاعت پذیرفته می‌شود؛ و نه غرامت از او قبول خواهد شد؛ و نه یاری می‌شوند.'),
(56, 56, 'وَإِذْ نَجَّيْنَاكُمْ مِنْ آلِ فِرْعَوْنَ يَسُومُونَكُمْ سُوءَ الْعَذَابِ يُذَبِّحُونَ أَبْنَاءَكُمْ وَيَسْتَحْيُونَ نِسَاءَكُمْ ۚ وَفِي ذَٰلِكُمْ بَلَاءٌ مِنْ رَبِّكُمْ عَظِيمٌ', 2, 49, 1, 2, 8, 'و (نیز به یاد آورید) آن زمان که شما را از چنگال فرعونیان رهایی بخشیدیم؛ که همواره شما را به بدترین صورت آزار می‌دادند: پسران شما را سر می‌بریدند؛ و زنان شما را (برای کنیزی) زنده نگه می‌داشتند. و در اینها، آزمایش بزرگی از طرف پروردگارتان برای شما بود.'),
(57, 57, 'وَإِذْ فَرَقْنَا بِكُمُ الْبَحْرَ فَأَنْجَيْنَاكُمْ وَأَغْرَقْنَا آلَ فِرْعَوْنَ وَأَنْتُمْ تَنْظُرُونَ', 2, 50, 1, 2, 8, 'و (به خاطر بیاورید) هنگامی را که دریا را برای شما شکافتیم؛ و شما را نجات دادیم؛ و فرعونیان را غرق ساختیم؛ در حالی که شما تماشا می‌کردید.'),
(58, 58, 'وَإِذْ وَاعَدْنَا مُوسَىٰ أَرْبَعِينَ لَيْلَةً ثُمَّ اتَّخَذْتُمُ الْعِجْلَ مِنْ بَعْدِهِ وَأَنْتُمْ ظَالِمُونَ', 2, 51, 1, 2, 8, 'و (به یاد آورید) هنگامی را که با موسی چهل شب وعده گذاردیم؛ (و او، برای گرفتن فرمانهای الهی، به میعادگاه آمد؛) سپس شما گوساله را بعد از او (معبود خود) انتخاب نمودید؛ در حالی که ستمکار بودید.'),
(59, 59, 'ثُمَّ عَفَوْنَا عَنْكُمْ مِنْ بَعْدِ ذَٰلِكَ لَعَلَّكُمْ تَشْكُرُونَ', 2, 52, 1, 2, 8, 'سپس شما را بعد از آن بخشیدیم؛ شاید شکر (این نعمت را) بجا آورید.'),
(60, 60, 'وَإِذْ آتَيْنَا مُوسَى الْكِتَابَ وَالْفُرْقَانَ لَعَلَّكُمْ تَهْتَدُونَ', 2, 53, 1, 2, 8, 'و (نیز به خاطر آورید) هنگامی را که به موسی، کتاب و وسیله تشخیص (حق از باطل) را دادیم؛ تا هدایت شوید.'),
(61, 61, 'وَإِذْ قَالَ مُوسَىٰ لِقَوْمِهِ يَا قَوْمِ إِنَّكُمْ ظَلَمْتُمْ أَنْفُسَكُمْ بِاتِّخَاذِكُمُ الْعِجْلَ فَتُوبُوا إِلَىٰ بَارِئِكُمْ فَاقْتُلُوا أَنْفُسَكُمْ ذَٰلِكُمْ خَيْرٌ لَكُمْ عِنْدَ بَارِئِكُمْ فَتَابَ عَلَيْكُمْ ۚ إِنَّهُ هُوَ التَّوَّابُ الرَّحِيمُ', 2, 54, 1, 2, 8, 'و زمانی را که موسی به قوم خود گفت: «ای قوم من! شما با انتخاب گوساله (برای پرستش) به خود ستم کردید! پس توبه کنید؛ و به سوی خالق خود باز گردید! و خود را =یکدیگر را به قتل برسانید! این کار، برای شما در پیشگاه پروردگارتان بهتر است.» سپس خداوند توبه شما را پذیرفت؛ زیرا که او توبه‌پذیر و رحیم است.'),
(62, 62, 'وَإِذْ قُلْتُمْ يَا مُوسَىٰ لَنْ نُؤْمِنَ لَكَ حَتَّىٰ نَرَى اللَّهَ جَهْرَةً فَأَخَذَتْكُمُ الصَّاعِقَةُ وَأَنْتُمْ تَنْظُرُونَ', 2, 55, 1, 2, 8, 'و (نیز به یاد آورید) هنگامی را که گفتید: «ای موسی! ما هرگز به تو ایمان نخواهیم آورد؛ مگر اینکه خدا را آشکارا (با چشم خود) ببینیم!» پس صاعقه شما را گرفت؛ در حالی که تماشا می‌کردید.'),
(63, 63, 'ثُمَّ بَعَثْنَاكُمْ مِنْ بَعْدِ مَوْتِكُمْ لَعَلَّكُمْ تَشْكُرُونَ', 2, 56, 1, 2, 8, 'سپس شما را پس از مرگتان، حیات بخشیدیم؛ شاید شکر (نعمت او را) بجا آورید.'),
(64, 64, 'وَظَلَّلْنَا عَلَيْكُمُ الْغَمَامَ وَأَنْزَلْنَا عَلَيْكُمُ الْمَنَّ وَالسَّلْوَىٰ ۖ كُلُوا مِنْ طَيِّبَاتِ مَا رَزَقْنَاكُمْ ۖ وَمَا ظَلَمُونَا وَلَٰكِنْ كَانُوا أَنْفُسَهُمْ يَظْلِمُونَ', 2, 57, 1, 2, 8, 'و ابر را بر شما سایبان قرار دادیم؛ و «من» = شیره مخصوص و لذیذ درختان و «سلوی» = مرغان مخصوص شبیه کبوتر را بر شما فرستادیم؛ (و گفتیم:) «از نعمتهای پاکیزه‌ای که به شما روزی داده‌ایم بخورید!» (ولی شما کفران کردید!) آنها به ما ستم نکردند؛ بلکه به خود ستم می‌نمودند.'),
(65, 65, 'وَإِذْ قُلْنَا ادْخُلُوا هَٰذِهِ الْقَرْيَةَ فَكُلُوا مِنْهَا حَيْثُ شِئْتُمْ رَغَدًا وَادْخُلُوا الْبَابَ سُجَّدًا وَقُولُوا حِطَّةٌ نَغْفِرْ لَكُمْ خَطَايَاكُمْ ۚ وَسَنَزِيدُ الْمُحْسِنِينَ', 2, 58, 1, 2, 9, 'و (به خاطر بیاورید) زمانی را که گفتیم: «در این شهر = بیت المقدس‌ وارد شوید! و از نعمتهای فراوان آن، هر چه می‌خواهید بخورید! و از در (معبد بیت المقدس) با خضوع و خشوع وارد گردید! و بگویید: «خداوندا! گناهان ما را بریز!» تا خطاهای شما را ببخشیم؛ و به نیکوکاران پاداش بیشتری خواهیم داد.»'),
(66, 66, 'فَبَدَّلَ الَّذِينَ ظَلَمُوا قَوْلًا غَيْرَ الَّذِي قِيلَ لَهُمْ فَأَنْزَلْنَا عَلَى الَّذِينَ ظَلَمُوا رِجْزًا مِنَ السَّمَاءِ بِمَا كَانُوا يَفْسُقُونَ', 2, 59, 1, 2, 9, 'اما افراد ستمگر، این سخن را که به آنها گفته شده بود، تغییر دادند؛ (و به جای آن، جمله استهزاآمیزی گفتند؛) لذا بر ستمگران، در برابر این نافرمانی، عذابی از آسمان فرستادیم.'),
(67, 67, ' وَإِذِ اسْتَسْقَىٰ مُوسَىٰ لِقَوْمِهِ فَقُلْنَا اضْرِبْ بِعَصَاكَ الْحَجَرَ ۖ فَانْفَجَرَتْ مِنْهُ اثْنَتَا عَشْرَةَ عَيْنًا ۖ قَدْ عَلِمَ كُلُّ أُنَاسٍ مَشْرَبَهُمْ ۖ كُلُوا وَاشْرَبُوا مِنْ رِزْقِ اللَّهِ وَلَا تَعْثَوْا فِي الْأَرْضِ مُفْسِدِينَ', 2, 60, 1, 2, 9, 'و (به یاد آورید) زمانی را که موسی برای قوم خویش، آب طلبید، به او دستور دادیم: «عصای خود را بر آن سنگ مخصوص بزن!» ناگاه دوازده چشمه آب از آن جوشید؛ آن‌گونه که هر یک (از طوایف دوازده‌گانه بنی اسرائیل)، چشمه مخصوص خود را می‌شناختند! (و گفتیم:) «از روزیهای الهی بخورید و بیاشامید! و در زمین فساد نکنید!»'),
(68, 68, 'وَإِذْ قُلْتُمْ يَا مُوسَىٰ لَنْ نَصْبِرَ عَلَىٰ طَعَامٍ وَاحِدٍ فَادْعُ لَنَا رَبَّكَ يُخْرِجْ لَنَا مِمَّا تُنْبِتُ الْأَرْضُ مِنْ بَقْلِهَا وَقِثَّائِهَا وَفُومِهَا وَعَدَسِهَا وَبَصَلِهَا ۖ قَالَ أَتَسْتَبْدِلُونَ الَّذِي هُوَ أَدْنَىٰ بِالَّذِي هُوَ خَيْرٌ ۚ اهْبِطُوا مِصْرًا فَإِنَّ لَكُمْ مَا سَأَلْتُمْ ۗ وَضُرِبَتْ عَلَيْهِمُ الذِّلَّةُ وَالْمَسْكَنَةُ وَبَاءُوا بِغَضَبٍ مِنَ اللَّهِ ۗ ذَٰلِكَ بِأَنَّهُمْ كَانُوا يَكْفُرُونَ بِآيَاتِ اللَّهِ وَيَقْتُلُونَ النَّبِيِّينَ بِغَيْرِ الْحَقِّ ۗ ذَٰلِكَ بِمَا عَصَوْا وَكَانُوا يَعْتَدُونَ', 2, 61, 1, 2, 9, 'و (نیز به خاطر بیاورید) زمانی را که گفتید: «ای موسی! هرگز حاضر نیستیم به یک نوع غذا اکتفا کنیم! از خدای خود بخواه که از آنچه زمین می‌رویاند، از سبزیجات و خیار و سیر و عدس و پیازش، برای ما فراهم سازد.» موسی گفت: «آیا غذای پست‌تر را به جای غذای بهتر انتخاب می‌کنید؟! (اکنون که چنین است، بکوشید از این بیابان) در شهری فرود آئید؛ زیرا هر چه خواستید، در آنجا برای شما هست.» و (مهر) ذلت و نیاز، بر پیشانی آنها زده شد؛ و باز گرفتار خشم خدائی شدند؛ چرا که آنان نسبت به آیات الهی، کفر می‌ورزیدند؛ و پیامبران را به ناحق می‌کشتند. اینها به خاطر آن بود که گناهکار و متجاوز بودند.'),
(69, 69, 'إِنَّ الَّذِينَ آمَنُوا وَالَّذِينَ هَادُوا وَالنَّصَارَىٰ وَالصَّابِئِينَ مَنْ آمَنَ بِاللَّهِ وَالْيَوْمِ الْآخِرِ وَعَمِلَ صَالِحًا فَلَهُمْ أَجْرُهُمْ عِنْدَ رَبِّهِمْ وَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَ', 2, 62, 1, 2, 10, 'کسانی که (به پیامبر اسلام) ایمان آورده‌اند، و کسانی که به آئین یهود گرویدند و نصاری و صابئان = پیروان یحیی‌ هر گاه به خدا و روز رستاخیز ایمان آورند، و عمل صالح انجام دهند، پاداششان نزد پروردگارشان مسلم است؛ و هیچ‌گونه ترس و اندوهی برای آنها نیست. (هر کدام از پیروان ادیان الهی، که در عصر و زمان خود، بر طبق وظایف و فرمان دین عمل کرده‌اند، مأجور و رستگارند.)'),
(70, 70, 'وَإِذْ أَخَذْنَا مِيثَاقَكُمْ وَرَفَعْنَا فَوْقَكُمُ الطُّورَ خُذُوا مَا آتَيْنَاكُمْ بِقُوَّةٍ وَاذْكُرُوا مَا فِيهِ لَعَلَّكُمْ تَتَّقُونَ', 2, 63, 1, 2, 10, 'و (به یاد آورید) زمانی را که از شما پیمان گرفتیم؛ و کوه طور را بالای سر شما قرار دادیم؛ (و به شما گفتیم:) «آنچه را (از آیات و دستورهای خداوند) به شما داده‌ایم، با قدرت بگیرید؛ و آنچه را در آن است به یاد داشته باشید (و به آن عمل کنید)؛ شاید پرهیزکار شوید!»'),
(71, 71, 'ثُمَّ تَوَلَّيْتُمْ مِنْ بَعْدِ ذَٰلِكَ ۖ فَلَوْلَا فَضْلُ اللَّهِ عَلَيْكُمْ وَرَحْمَتُهُ لَكُنْتُمْ مِنَ الْخَاسِرِينَ', 2, 64, 1, 2, 10, 'سپس شما پس از این، روگردان شدید؛ و اگر فضل و رحمت خداوند بر شما نبود، از زیانکاران بودید.'),
(72, 72, 'وَلَقَدْ عَلِمْتُمُ الَّذِينَ اعْتَدَوْا مِنْكُمْ فِي السَّبْتِ فَقُلْنَا لَهُمْ كُونُوا قِرَدَةً خَاسِئِينَ', 2, 65, 1, 2, 10, 'به طور قطع از حال کسانی از شما، که در روز شنبه نافرمانی و گناه کردند، آگاه شده‌اید! ما به آنها گفتیم: «به صورت بوزینه‌هایی طردشده درآیید!»'),
(73, 73, 'فَجَعَلْنَاهَا نَكَالًا لِمَا بَيْنَ يَدَيْهَا وَمَا خَلْفَهَا وَمَوْعِظَةً لِلْمُتَّقِينَ', 2, 66, 1, 2, 10, 'ما این کیفر را درس عبرتی برای مردم آن زمان و نسلهای بعد از آنان، و پند و اندرزی برای پرهیزکاران قرار دادیم.'),
(74, 74, 'وَإِذْ قَالَ مُوسَىٰ لِقَوْمِهِ إِنَّ اللَّهَ يَأْمُرُكُمْ أَنْ تَذْبَحُوا بَقَرَةً ۖ قَالُوا أَتَتَّخِذُنَا هُزُوًا ۖ قَالَ أَعُوذُ بِاللَّهِ أَنْ أَكُونَ مِنَ الْجَاهِلِينَ', 2, 67, 1, 2, 10, 'و (به یاد آورید) هنگامی را که موسی به قوم خود گفت: «خداوند به شما دستور می‌دهد ماده‌گاوی را ذبح کنید (و قطعه‌ای از بدن آن را به مقتولی که قاتل او شناخته نشده بزنید، تا زنده شود و قاتل خویش را معرفی کند؛ و غوغا خاموش گردد.)» گفتند: «آیا ما را مسخره می‌کنی؟» (موسی) گفت: «به خدا پناه می‌برم از اینکه از جاهلان باشم!»'),
(75, 75, 'قَالُوا ادْعُ لَنَا رَبَّكَ يُبَيِّنْ لَنَا مَا هِيَ ۚ قَالَ إِنَّهُ يَقُولُ إِنَّهَا بَقَرَةٌ لَا فَارِضٌ وَلَا بِكْرٌ عَوَانٌ بَيْنَ ذَٰلِكَ ۖ فَافْعَلُوا مَا تُؤْمَرُونَ', 2, 68, 1, 2, 10, 'گفتند: «(پس) از خدای خود بخواه که برای ما روشن کند این ماده‌گاو چگونه ماده‌گاوی باشد؟» گفت: خداوند می‌فرماید: «ماده‌گاوی است که نه پیر و از کار افتاده باشد، و نه بکر و جوان؛ بلکه میان این دو باشد. آنچه به شما دستور داده شده، (هر چه زودتر) انجام دهید.»'),
(76, 76, 'قَالُوا ادْعُ لَنَا رَبَّكَ يُبَيِّنْ لَنَا مَا لَوْنُهَا ۚ قَالَ إِنَّهُ يَقُولُ إِنَّهَا بَقَرَةٌ صَفْرَاءُ فَاقِعٌ لَوْنُهَا تَسُرُّ النَّاظِرِينَ', 2, 69, 1, 2, 10, 'گفتند: «از پروردگار خود بخواه که برای ما روشن سازد رنگ آن چگونه باشد؟ «گفت: خداوند می‌گوید: «گاوی باشد زرد یکدست، که رنگ آن، بینندگان را شاد و مسرور سازد.»'),
(77, 77, 'قَالُوا ادْعُ لَنَا رَبَّكَ يُبَيِّنْ لَنَا مَا هِيَ إِنَّ الْبَقَرَ تَشَابَهَ عَلَيْنَا وَإِنَّا إِنْ شَاءَ اللَّهُ لَمُهْتَدُونَ', 2, 70, 1, 2, 11, 'گفتند: «از خدایت بخواه برای ما روشن کند که چگونه گاوی باید باشد؟ زیرا این گاو برای ما مبهم شده! و اگر خدا بخواهد ما هدایت خواهیم شد!»'),
(78, 78, 'قَالَ إِنَّهُ يَقُولُ إِنَّهَا بَقَرَةٌ لَا ذَلُولٌ تُثِيرُ الْأَرْضَ وَلَا تَسْقِي الْحَرْثَ مُسَلَّمَةٌ لَا شِيَةَ فِيهَا ۚ قَالُوا الْآنَ جِئْتَ بِالْحَقِّ ۚ فَذَبَحُوهَا وَمَا كَادُوا يَفْعَلُونَ', 2, 71, 1, 2, 11, 'گفت: خداوند می‌فرماید: «گاوی باشد که نه برای شخم زدن رام شده؛ و نه برای زراعت آبکشی کند؛ از هر عیبی برکنار باشد، و حتی هیچ‌گونه رنگ دیگری در آن نباشد.» گفتند: «الان حق مطلب را آوردی!» سپس (چنان گاوی را پیدا کردند و) آن را سر بریدند؛ ولی مایل نبودند این کار را انجام دهند.'),
(79, 79, 'وَإِذْ قَتَلْتُمْ نَفْسًا فَادَّارَأْتُمْ فِيهَا ۖ وَاللَّهُ مُخْرِجٌ مَا كُنْتُمْ تَكْتُمُونَ', 2, 72, 1, 2, 11, 'و (به یاد آورید) هنگامی را که فردی را به قتل رساندید؛ سپس درباره (قاتل) او به نزاع پرداختید؛ و خداوند آنچه را مخفی می‌داشتید، آشکار می‌سازد.'),
(80, 80, 'فَقُلْنَا اضْرِبُوهُ بِبَعْضِهَا ۚ كَذَٰلِكَ يُحْيِي اللَّهُ الْمَوْتَىٰ وَيُرِيكُمْ آيَاتِهِ لَعَلَّكُمْ تَعْقِلُونَ', 2, 73, 1, 2, 11, 'سپس گفتیم: «قسمتی از گاو را به مقتول بزنید! (تا زنده شود، و قاتل را معرفی کند.) خداوند این‌گونه مردگان را زنده می‌کند؛ و آیات خود را به شما نشان می‌دهد؛ شاید اندیشه کنید!»'),
(81, 81, 'ثُمَّ قَسَتْ قُلُوبُكُمْ مِنْ بَعْدِ ذَٰلِكَ فَهِيَ كَالْحِجَارَةِ أَوْ أَشَدُّ قَسْوَةً ۚ وَإِنَّ مِنَ الْحِجَارَةِ لَمَا يَتَفَجَّرُ مِنْهُ الْأَنْهَارُ ۚ وَإِنَّ مِنْهَا لَمَا يَشَّقَّقُ فَيَخْرُجُ مِنْهُ الْمَاءُ ۚ وَإِنَّ مِنْهَا لَمَا يَهْبِطُ مِنْ خَشْيَةِ اللَّهِ ۗ وَمَا اللَّهُ بِغَافِلٍ عَمَّا تَعْمَلُونَ', 2, 74, 1, 2, 11, 'سپس دلهای شما بعد از این واقعه سخت شد؛ همچون سنگ، یا سخت‌تر! چرا که پاره‌ای از سنگها می‌شکافد، و از آن نهرها جاری می‌شود؛ و پاره‌ای از آنها شکاف برمی‌دارد، و آب از آن تراوش می‌کند؛ و پاره‌ای از خوف خدا (از فراز کوه) به زیر می‌افتد؛ (اما دلهای شما، نه از خوف خدا می‌تپد، و نه سرچشمه علم و دانش و عواطف انسانی است!) و خداوند از اعمال شما غافل نیست.'),
(82, 82, ' أَفَتَطْمَعُونَ أَنْ يُؤْمِنُوا لَكُمْ وَقَدْ كَانَ فَرِيقٌ مِنْهُمْ يَسْمَعُونَ كَلَامَ اللَّهِ ثُمَّ يُحَرِّفُونَهُ مِنْ بَعْدِ مَا عَقَلُوهُ وَهُمْ يَعْلَمُونَ', 2, 75, 1, 3, 11, 'آیا انتظار دارید به (آئین) شما ایمان بیاورند، با اینکه عده‌ای از آنان، سخنان خدا را می‌شنیدند و پس از فهمیدن، آن را تحریف می‌کردند، در حالی که علم و اطلاع داشتند؟!'),
(83, 83, 'وَإِذَا لَقُوا الَّذِينَ آمَنُوا قَالُوا آمَنَّا وَإِذَا خَلَا بَعْضُهُمْ إِلَىٰ بَعْضٍ قَالُوا أَتُحَدِّثُونَهُمْ بِمَا فَتَحَ اللَّهُ عَلَيْكُمْ لِيُحَاجُّوكُمْ بِهِ عِنْدَ رَبِّكُمْ ۚ أَفَلَا تَعْقِلُونَ', 2, 76, 1, 3, 11, 'و هنگامی که مؤمنان را ملاقات کنند، می‌گویند: «ایمان آورده‌ایم.» ولی هنگامی که با یکدیگر خلوت می‌کنند، (بعضی به بعضی دیگر اعتراض کرده،) می‌گویند: «چرا مطالبی را که خداوند (در باره صفات پیامبر اسلام) برای شما بیان کرد، به مسلمانان بازگو می‌کنید تا (روز رستاخیز) در پیشگاه خدا، بر ضد شما به آن استدلال کنند؟! آیا نمی‌فهمید؟!»'),
(84, 84, 'أَوَلَا يَعْلَمُونَ أَنَّ اللَّهَ يَعْلَمُ مَا يُسِرُّونَ وَمَا يُعْلِنُونَ', 2, 77, 1, 3, 12, 'آیا اینها نمی‌دانند خداوند آنچه را پنهان می‌دارند یا آشکار می‌کنند می‌داند؟!'),
(85, 85, 'وَمِنْهُمْ أُمِّيُّونَ لَا يَعْلَمُونَ الْكِتَابَ إِلَّا أَمَانِيَّ وَإِنْ هُمْ إِلَّا يَظُنُّونَ', 2, 78, 1, 3, 12, 'و پاره‌ای از آنان عوامانی هستند که کتاب خدا را جز یک مشت خیالات و آرزوها نمی‌دانند؛ و تنها به پندارهایشان دل بسته‌اند.'),
(86, 86, 'فَوَيْلٌ لِلَّذِينَ يَكْتُبُونَ الْكِتَابَ بِأَيْدِيهِمْ ثُمَّ يَقُولُونَ هَٰذَا مِنْ عِنْدِ اللَّهِ لِيَشْتَرُوا بِهِ ثَمَنًا قَلِيلًا ۖ فَوَيْلٌ لَهُمْ مِمَّا كَتَبَتْ أَيْدِيهِمْ وَوَيْلٌ لَهُمْ مِمَّا يَكْسِبُونَ', 2, 79, 1, 3, 12, 'پس وای بر آنها که نوشته‌ای با دست خود می‌نویسند، سپس می‌گویند: «این، از طرف خداست.» تا آن را به بهای کمی بفروشند. پس وای بر آنها از آنچه با دست خود نوشتند؛ و وای بر آنان از آنچه از این راه به دست می‌آورند!'),
(87, 87, 'وَقَالُوا لَنْ تَمَسَّنَا النَّارُ إِلَّا أَيَّامًا مَعْدُودَةً ۚ قُلْ أَتَّخَذْتُمْ عِنْدَ اللَّهِ عَهْدًا فَلَنْ يُخْلِفَ اللَّهُ عَهْدَهُ ۖ أَمْ تَقُولُونَ عَلَى اللَّهِ مَا لَا تَعْلَمُونَ', 2, 80, 1, 3, 12, 'و گفتند: «هرگز آتش دوزخ، جز چند روزی، به ما نخواهد رسید.» بگو: «آیا پیمانی از خدا گرفته‌اید؟! -و خداوند هرگز از پیمانش تخلف نمی‌ورزد- یا چیزی را که نمی‌دانید به خدا نسبت می‌دهید»؟!'),
(88, 88, 'بَلَىٰ مَنْ كَسَبَ سَيِّئَةً وَأَحَاطَتْ بِهِ خَطِيئَتُهُ فَأُولَٰئِكَ أَصْحَابُ النَّارِ ۖ هُمْ فِيهَا خَالِدُونَ', 2, 81, 1, 3, 12, 'آری، کسانی که کسب گناه کنند، و آثار گناه، سراسر وجودشان را بپوشاند، آنها اهل آتشند؛ و جاودانه در آن خواهند بود.'),
(89, 89, 'وَالَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ أُولَٰئِكَ أَصْحَابُ الْجَنَّةِ ۖ هُمْ فِيهَا خَالِدُونَ', 2, 82, 1, 3, 12, 'و آنها که ایمان آورده، و کارهای شایسته انجام داده‌اند، آنان اهل بهشتند؛ و همیشه در آن خواهند ماند.'),
(90, 90, 'وَإِذْ أَخَذْنَا مِيثَاقَ بَنِي إِسْرَائِيلَ لَا تَعْبُدُونَ إِلَّا اللَّهَ وَبِالْوَالِدَيْنِ إِحْسَانًا وَذِي الْقُرْبَىٰ وَالْيَتَامَىٰ وَالْمَسَاكِينِ وَقُولُوا لِلنَّاسِ حُسْنًا وَأَقِيمُوا الصَّلَاةَ وَآتُوا الزَّكَاةَ ثُمَّ تَوَلَّيْتُمْ إِلَّا قَلِيلًا مِنْكُمْ وَأَنْتُمْ مُعْرِضُونَ', 2, 83, 1, 3, 12, 'و (به یاد آورید) زمانی را که از بنی اسرائیل پیمان گرفتیم که جز خداوند یگانه را پرستش نکنید؛ و به پدر و مادر و نزدیکان و یتیمان و بینوایان نیکی کنید؛ و به مردم نیک بگویید؛ نماز را برپا دارید؛ و زکات بدهید. سپس (با اینکه پیمان بسته بودید) همه شما -جز عده کمی- سرپیچی کردید؛ و (از وفای به پیمان خود) روی‌گردان شدید.'),
(91, 91, 'وَإِذْ أَخَذْنَا مِيثَاقَكُمْ لَا تَسْفِكُونَ دِمَاءَكُمْ وَلَا تُخْرِجُونَ أَنْفُسَكُمْ مِنْ دِيَارِكُمْ ثُمَّ أَقْرَرْتُمْ وَأَنْتُمْ تَشْهَدُونَ', 2, 84, 1, 3, 13, 'و هنگامی را که از شما پیمان گرفتیم که خون هم را نریزید؛ و یکدیگر را از سرزمین خود، بیرون نکنید. سپس شما اقرار کردید؛ (و بر این پیمان) گواه بودید.'),
(92, 92, 'ثُمَّ أَنْتُمْ هَٰؤُلَاءِ تَقْتُلُونَ أَنْفُسَكُمْ وَتُخْرِجُونَ فَرِيقًا مِنْكُمْ مِنْ دِيَارِهِمْ تَظَاهَرُونَ عَلَيْهِمْ بِالْإِثْمِ وَالْعُدْوَانِ وَإِنْ يَأْتُوكُمْ أُسَارَىٰ تُفَادُوهُمْ وَهُوَ مُحَرَّمٌ عَلَيْكُمْ إِخْرَاجُهُمْ ۚ أَفَتُؤْمِنُونَ بِبَعْضِ الْكِتَابِ وَتَكْفُرُونَ بِبَعْضٍ ۚ فَمَا جَزَاءُ مَنْ يَفْعَلُ ذَٰلِكَ مِنْكُمْ إِلَّا خِزْيٌ فِي الْحَيَاةِ الدُّنْيَا ۖ وَيَوْمَ الْقِيَامَةِ يُرَدُّونَ إِلَىٰ أَشَدِّ الْعَذَابِ ۗ وَمَا اللَّهُ بِغَافِلٍ عَمَّا تَعْمَلُونَ', 2, 85, 1, 3, 13, 'اما این شما هستید که یکدیگر را می‌کشید و جمعی از خودتان را از سرزمینشان بیرون می‌کنید؛ و در این گناه و تجاوز، به یکدیگر کمک می‌نمایید؛ (و اینها همه نقض پیمانی است که با خدا بسته‌اید) در حالی که اگر بعضی از آنها به صورت اسیران نزد شما آیند، فدیه می‌دهید و آنان را آزاد می‌سازید! با اینکه بیرون ساختن آنان بر شما حرام بود. آیا به بعضی از دستورات کتاب آسمانی ایمان می‌آورید، و به بعضی کافر می‌شوید؟! برای کسی از شما که این عمل (تبعیض در میان احکام و قوانین الهی) را انجام دهد، جز رسوایی در این جهان، چیزی نخواهد بود، و روز رستاخیز به شدیدترین عذابها گرفتار می‌شوند. و خداوند از آنچه انجام می‌دهید غافل نیست.'),
(93, 93, 'أُولَٰئِكَ الَّذِينَ اشْتَرَوُا الْحَيَاةَ الدُّنْيَا بِالْآخِرَةِ ۖ فَلَا يُخَفَّفُ عَنْهُمُ الْعَذَابُ وَلَا هُمْ يُنْصَرُونَ', 2, 86, 1, 3, 13, 'اینها همان کسانند که آخرت را به زندگی دنیا فروخته‌اند؛ از این رو عذاب آنها تخفیف داده نمی‌شود؛ و کسی آنها را یاری نخواهد کرد.'),
(94, 94, 'وَلَقَدْ آتَيْنَا مُوسَى الْكِتَابَ وَقَفَّيْنَا مِنْ بَعْدِهِ بِالرُّسُلِ ۖ وَآتَيْنَا عِيسَى ابْنَ مَرْيَمَ الْبَيِّنَاتِ وَأَيَّدْنَاهُ بِرُوحِ الْقُدُسِ ۗ أَفَكُلَّمَا جَاءَكُمْ رَسُولٌ بِمَا لَا تَهْوَىٰ أَنْفُسُكُمُ اسْتَكْبَرْتُمْ فَفَرِيقًا كَذَّبْتُمْ وَفَرِيقًا تَقْتُلُونَ', 2, 87, 1, 3, 13, 'ما به موسی کتاب (تورات) دادیم؛ و بعد از او، پیامبرانی پشت سر هم فرستادیم؛ و به عیسی بن مریم دلایل روشن دادیم؛ و او را به وسیله روح القدس تأیید کردیم. آیا چنین نیست که هر زمان، پیامبری چیزی بر خلاف هوای نفس شما آورد، در برابر او تکبر کردید (و از ایمان آوردن به او خودداری نمودید)؛ پس عده‌ای را تکذیب کرده، و جمعی را به قتل رساندید؟!'),
(95, 95, 'وَقَالُوا قُلُوبُنَا غُلْفٌ ۚ بَلْ لَعَنَهُمُ اللَّهُ بِكُفْرِهِمْ فَقَلِيلًا مَا يُؤْمِنُونَ', 2, 88, 1, 3, 13, 'و (آنها از روی استهزا) گفتند: دلهای ما در غلاف است! (و ما از گفته تو چیزی نمی‌فهمیم. آری، همین طور است!) خداوند آنها را به خاطر کفرشان، از رحمت خود دور ساخته، (به همین دلیل، چیزی درک نمی‌کنند؛) و کمتر ایمان می‌آورند.'),
(96, 96, 'وَلَمَّا جَاءَهُمْ كِتَابٌ مِنْ عِنْدِ اللَّهِ مُصَدِّقٌ لِمَا مَعَهُمْ وَكَانُوا مِنْ قَبْلُ يَسْتَفْتِحُونَ عَلَى الَّذِينَ كَفَرُوا فَلَمَّا جَاءَهُمْ مَا عَرَفُوا كَفَرُوا بِهِ ۚ فَلَعْنَةُ اللَّهِ عَلَى الْكَافِرِينَ', 2, 89, 1, 3, 14, 'و هنگامی که از طرف خداوند، کتابی برای آنها آمد که موافق نشانه‌هایی بود که با خود داشتند، و پیش از این، به خود نوید پیروزی بر کافران می‌دادند (که با کمک آن، بر دشمنان پیروز گردند.) با این همه، هنگامی که این کتاب، و پیامبری را که از قبل شناخته بودند نزد آنها آمد، به او کافر شدند؛ لعنت خدا بر کافران باد!'),
(97, 97, 'بِئْسَمَا اشْتَرَوْا بِهِ أَنْفُسَهُمْ أَنْ يَكْفُرُوا بِمَا أَنْزَلَ اللَّهُ بَغْيًا أَنْ يُنَزِّلَ اللَّهُ مِنْ فَضْلِهِ عَلَىٰ مَنْ يَشَاءُ مِنْ عِبَادِهِ ۖ فَبَاءُوا بِغَضَبٍ عَلَىٰ غَضَبٍ ۚ وَلِلْكَافِرِينَ عَذَابٌ مُهِينٌ', 2, 90, 1, 3, 14, 'ولی آنها در مقابل بهای بدی، خود را فروختند؛ که به ناروا، به آیاتی که خدا فرستاده بود، کافر شدند. و معترض بودند، چرا خداوند به فضل خویش، بر هر کس از بندگانش بخواهد، آیات خود را نازل می‌کند؟! از این رو به خشمی بعد از خشمی (از سوی خدا) گرفتار شدند. و برای کافران مجازاتی خوارکننده است.'),
(98, 98, 'وَإِذَا قِيلَ لَهُمْ آمِنُوا بِمَا أَنْزَلَ اللَّهُ قَالُوا نُؤْمِنُ بِمَا أُنْزِلَ عَلَيْنَا وَيَكْفُرُونَ بِمَا وَرَاءَهُ وَهُوَ الْحَقُّ مُصَدِّقًا لِمَا مَعَهُمْ ۗ قُلْ فَلِمَ تَقْتُلُونَ أَنْبِيَاءَ اللَّهِ مِنْ قَبْلُ إِنْ كُنْتُمْ مُؤْمِنِينَ', 2, 91, 1, 3, 14, 'و هنگامی که به آنها گفته شود: «به آنچه خداوند نازل فرموده، ایمان بیاورید!» می‌گویند: «ما به چیزی ایمان می‌آوریم که بر خود ما نازل شده است.» و به غیر آن، کافر می‌شوند؛ در حالی که حق است؛ و آیاتی را که بر آنها نازل شده، تصدیق می‌کند. بگو: «اگر (راست می‌گویید، و به آیاتی که بر خودتان نازل شده) ایمان دارید، پس چرا پیامبران خدا را پیش از این، به قتل می‌رساندید؟!»'),
(99, 99, ' وَلَقَدْ جَاءَكُمْ مُوسَىٰ بِالْبَيِّنَاتِ ثُمَّ اتَّخَذْتُمُ الْعِجْلَ مِنْ بَعْدِهِ وَأَنْتُمْ ظَالِمُونَ', 2, 92, 1, 3, 14, 'و (نیز) موسی آن همه معجزات را برای شما آورد، و شما پس از (غیبت) او، گوساله را انتخاب کردید؛ در حالی که ستمگر بودید.'),
(100, 100, 'وَإِذْ أَخَذْنَا مِيثَاقَكُمْ وَرَفَعْنَا فَوْقَكُمُ الطُّورَ خُذُوا مَا آتَيْنَاكُمْ بِقُوَّةٍ وَاسْمَعُوا ۖ قَالُوا سَمِعْنَا وَعَصَيْنَا وَأُشْرِبُوا فِي قُلُوبِهِمُ الْعِجْلَ بِكُفْرِهِمْ ۚ قُلْ بِئْسَمَا يَأْمُرُكُمْ بِهِ إِيمَانُكُمْ إِنْ كُنْتُمْ مُؤْمِنِينَ', 2, 93, 1, 3, 14, 'و (به یاد آورید) زمانی را که از شما پیمان گرفتیم؛ و کوه طور را بالای سر شما برافراشتیم؛ (و گفتیم:) «این دستوراتی را که به شما داده‌ایم محکم بگیرید، و درست بشنوید!» آنها گفتند: «شنیدیم؛ ولی مخالفت کردیم.» و دلهای آنها، بر اثر کفرشان، با محبت گوساله آمیخته شد. بگو: «ایمان شما، چه فرمان بدی به شما می‌دهد، اگر ایمان دارید!»'),
(101, 101, 'قُلْ إِنْ كَانَتْ لَكُمُ الدَّارُ الْآخِرَةُ عِنْدَ اللَّهِ خَالِصَةً مِنْ دُونِ النَّاسِ فَتَمَنَّوُا الْمَوْتَ إِنْ كُنْتُمْ صَادِقِينَ', 2, 94, 1, 3, 15, 'بگو: «اگر آن (چنان که مدعی هستید) سرای دیگر در نزد خدا، مخصوص شماست نه سایر مردم، پس آرزوی مرگ کنید اگر راست می‌گویید!»'),
(102, 102, 'وَلَنْ يَتَمَنَّوْهُ أَبَدًا بِمَا قَدَّمَتْ أَيْدِيهِمْ ۗ وَاللَّهُ عَلِيمٌ بِالظَّالِمِينَ', 2, 95, 1, 3, 15, 'ولی آنها، به خاطر اعمال بدی که پیش از خود فرستاده‌اند، هرگز آرزوی مرگ نخواهند کرد؛ و خداوند از ستمگران آگاه است.'),
(103, 103, 'وَلَتَجِدَنَّهُمْ أَحْرَصَ النَّاسِ عَلَىٰ حَيَاةٍ وَمِنَ الَّذِينَ أَشْرَكُوا ۚ يَوَدُّ أَحَدُهُمْ لَوْ يُعَمَّرُ أَلْفَ سَنَةٍ وَمَا هُوَ بِمُزَحْزِحِهِ مِنَ الْعَذَابِ أَنْ يُعَمَّرَ ۗ وَاللَّهُ بَصِيرٌ بِمَا يَعْمَلُونَ', 2, 96, 1, 3, 15, 'و آنها را حریص‌ترین مردم -حتی حریصتر از مشرکان- بر زندگی (این دنیا، و اندوختن ثروت) خواهی یافت؛ (تا آنجا) که هر یک از آنها آرزو دارد هزار سال عمر به او داده شود! در حالی که این عمر طولانی، او را از کیفر (الهی) باز نخواهد داشت. و خداوند به اعمال آنها بیناست.'),
(104, 104, 'قُلْ مَنْ كَانَ عَدُوًّا لِجِبْرِيلَ فَإِنَّهُ نَزَّلَهُ عَلَىٰ قَلْبِكَ بِإِذْنِ اللَّهِ مُصَدِّقًا لِمَا بَيْنَ يَدَيْهِ وَهُدًى وَبُشْرَىٰ لِلْمُؤْمِنِينَ', 2, 97, 1, 3, 15, '(آنها می‌گویند: «چون فرشته‌ای که وحی را بر تو نازل می‌کند، جبرئیل است، و ما با جبرئیل دشمن هستیم، به تو ایمان نمی‌آوریم!») بگو: «کسی که دشمن جبرئیل باشد (در حقیقت دشمن خداست) چرا که او به فرمان خدا، قرآن را بر قلب تو نازل کرده است؛ در حالی که کتب آسمانی پیشین را تصدیق می‌کند؛ و هدایت و بشارت است برای مؤمنان.»'),
(105, 105, 'مَنْ كَانَ عَدُوًّا لِلَّهِ وَمَلَائِكَتِهِ وَرُسُلِهِ وَجِبْرِيلَ وَمِيكَالَ فَإِنَّ اللَّهَ عَدُوٌّ لِلْكَافِرِينَ', 2, 98, 1, 3, 15, 'کسی که دشمن خدا و فرشتگان و رسولان او و جبرئیل و میکائیل باشد (کافر است؛ و) خداوند دشمن کافران است.'),
(106, 106, 'وَلَقَدْ أَنْزَلْنَا إِلَيْكَ آيَاتٍ بَيِّنَاتٍ ۖ وَمَا يَكْفُرُ بِهَا إِلَّا الْفَاسِقُونَ', 2, 99, 1, 3, 15, 'ما نشانه‌های روشنی برای تو فرستادیم؛ و جز فاسقان کسی به آنها کفر نمی‌ورزد.'),
(107, 107, 'أَوَكُلَّمَا عَاهَدُوا عَهْدًا نَبَذَهُ فَرِيقٌ مِنْهُمْ ۚ بَلْ أَكْثَرُهُمْ لَا يُؤْمِنُونَ', 2, 100, 1, 3, 15, 'و آیا چنین نیست که هر بار آنها =یهود پیمانی (با خدا و پیامبر) بستند، جمعی آن را دور افکندند (و مخالفت کردند.) آری، بیشتر آنان ایمان نمی‌آورند.'),
(108, 108, 'وَلَمَّا جَاءَهُمْ رَسُولٌ مِنْ عِنْدِ اللَّهِ مُصَدِّقٌ لِمَا مَعَهُمْ نَبَذَ فَرِيقٌ مِنَ الَّذِينَ أُوتُوا الْكِتَابَ كِتَابَ اللَّهِ وَرَاءَ ظُهُورِهِمْ كَأَنَّهُمْ لَا يَعْلَمُونَ', 2, 101, 1, 3, 15, 'و هنگامی که فرستاده‌ای از سوی خدا به سراغشان آمد، و با نشانه‌هایی که نزد آنها بود مطابقت داشت، جمعی از آنان که به آنها کتاب (آسمانی) داده شده بود، کتاب خدا را پشت سر افکندند؛ گویی هیچ از آن خبر ندارند!!'),
(109, 109, 'وَاتَّبَعُوا مَا تَتْلُو الشَّيَاطِينُ عَلَىٰ مُلْكِ سُلَيْمَانَ ۖ وَمَا كَفَرَ سُلَيْمَانُ وَلَٰكِنَّ الشَّيَاطِينَ كَفَرُوا يُعَلِّمُونَ النَّاسَ السِّحْرَ وَمَا أُنْزِلَ عَلَى الْمَلَكَيْنِ بِبَابِلَ هَارُوتَ وَمَارُوتَ ۚ وَمَا يُعَلِّمَانِ مِنْ أَحَدٍ حَتَّىٰ يَقُولَا إِنَّمَا نَحْنُ فِتْنَةٌ فَلَا تَكْفُرْ ۖ فَيَتَعَلَّمُونَ مِنْهُمَا مَا يُفَرِّقُونَ بِهِ بَيْنَ الْمَرْءِ وَزَوْجِهِ ۚ وَمَا هُمْ بِضَارِّينَ بِهِ مِنْ أَحَدٍ إِلَّا بِإِذْنِ اللَّهِ ۚ وَيَتَعَلَّمُونَ مَا يَضُرُّهُمْ وَلَا يَنْفَعُهُمْ ۚ وَلَقَدْ عَلِمُوا لَمَنِ اشْتَرَاهُ مَا لَهُ فِي الْآخِرَةِ مِنْ خَلَاقٍ ۚ وَلَبِئْسَ مَا شَرَوْا بِهِ أَنْفُسَهُمْ ۚ لَوْ كَانُوا يَعْلَمُونَ', 2, 102, 1, 3, 16, 'و (یهود) از آنچه شیاطین در عصر سلیمان بر مردم می‌خواندند پیروی کردند. سلیمان هرگز (دست به سحر نیالود؛ و) کافر نشد؛ ولی شیاطین کفر ورزیدند؛ و به مردم سحر آموختند. و (نیز یهود) از آنچه بر دو فرشته بابل «هاروت» و «ماروت»، نازل شد پیروی کردند. (آن دو، راه سحر کردن را، برای آشنایی با طرز ابطال آن، به مردم یاد می‌دادند. و) به هیچ کس چیزی یاد نمی‌دادند، مگر اینکه از پیش به او می‌گفتند: «ما وسیله آزمایشیم کافر نشو! (و از این تعلیمات، سوء استفاده نکن!)» ولی آنها از آن دو فرشته، مطالبی را می‌آموختند که بتوانند به وسیله آن، میان مرد و همسرش جدایی بیفکنند؛ ولی هیچ گاه نمی‌توانند بدون اجازه خداوند، به انسانی زیان برسانند. آنها قسمتهایی را فرامی‌گرفتند که به آنان زیان می‌رسانید و نفعی نمی‌داد. و مسلما می‌دانستند هر کسی خریدار این گونه متاع باشد، در آخرت بهره‌ای نخواهد داشت. و چه زشت و ناپسند بود آنچه خود را به آن فروختند، اگر می‌دانستند!!'),
(110, 110, 'وَلَوْ أَنَّهُمْ آمَنُوا وَاتَّقَوْا لَمَثُوبَةٌ مِنْ عِنْدِ اللَّهِ خَيْرٌ ۖ لَوْ كَانُوا يَعْلَمُونَ', 2, 103, 1, 3, 16, 'و اگر آنها ایمان می‌آوردند و پرهیزکاری پیشه می‌کردند، پاداشی که نزد خداست، برای آنان بهتر بود، اگر آگاهی داشتند!!'),
(111, 111, 'يَا أَيُّهَا الَّذِينَ آمَنُوا لَا تَقُولُوا رَاعِنَا وَقُولُوا انْظُرْنَا وَاسْمَعُوا ۗ وَلِلْكَافِرِينَ عَذَابٌ أَلِيمٌ', 2, 104, 1, 3, 16, 'ای افراد باایمان! (هنگامی که از پیغمبر تقاضای مهلت برای درک آیات قرآن می‌کنید) نگویید: «راعنا»؛ بلکه بگویید: «انظرنا». (زیرا کلمه اول، هم به معنی «ما را مهلت بده!»، و هم به معنی «ما را تحمیق کن!» می‌باشد؛ و دستاویزی برای دشمنان است.) و (آنچه به شما دستور داده می‌شود) بشنوید! و برای کافران (و استهزاکنندگان) عذاب دردناکی است.'),
(112, 112, 'مَا يَوَدُّ الَّذِينَ كَفَرُوا مِنْ أَهْلِ الْكِتَابِ وَلَا الْمُشْرِكِينَ أَنْ يُنَزَّلَ عَلَيْكُمْ مِنْ خَيْرٍ مِنْ رَبِّكُمْ ۗ وَاللَّهُ يَخْتَصُّ بِرَحْمَتِهِ مَنْ يَشَاءُ ۚ وَاللَّهُ ذُو الْفَضْلِ الْعَظِيمِ', 2, 105, 1, 3, 16, 'کافران اهل کتاب، و (همچنین) مشرکان، دوست ندارند که از سوی خداوند، خیر و برکتی بر شما نازل گردد؛ در حالی که خداوند، رحمت خود را به هر کس بخواهد، اختصاص می‌دهد؛ و خداوند، صاحب فضل بزرگ است.'),
(113, 113, ' مَا نَنْسَخْ مِنْ آيَةٍ أَوْ نُنْسِهَا نَأْتِ بِخَيْرٍ مِنْهَا أَوْ مِثْلِهَا ۗ أَلَمْ تَعْلَمْ أَنَّ اللَّهَ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ', 2, 106, 1, 4, 17, 'هر حکمی را نسخ کنیم، و یا نسخ آن را به تأخیر اندازیم، بهتر از آن، یا همانند آن را می‌آوریم. آیا نمی‌دانستی که خداوند بر هر چیز توانا است؟!'),
(114, 114, 'أَلَمْ تَعْلَمْ أَنَّ اللَّهَ لَهُ مُلْكُ السَّمَاوَاتِ وَالْأَرْضِ ۗ وَمَا لَكُمْ مِنْ دُونِ اللَّهِ مِنْ وَلِيٍّ وَلَا نَصِيرٍ', 2, 107, 1, 4, 17, 'آیا نمی‌دانستی که حکومت آسمانها و زمین، از آن خداست؟! (و حق دارد هر گونه تغییر و تبدیلی در احکام خود طبق مصالح بدهد؟!) و جز خدا، ولی و یاوری برای شما نیست. (و اوست که مصلحت شما را می‌داند و تعیین می‌کند).'),
(115, 115, 'أَمْ تُرِيدُونَ أَنْ تَسْأَلُوا رَسُولَكُمْ كَمَا سُئِلَ مُوسَىٰ مِنْ قَبْلُ ۗ وَمَنْ يَتَبَدَّلِ الْكُفْرَ بِالْإِيمَانِ فَقَدْ ضَلَّ سَوَاءَ السَّبِيلِ', 2, 108, 1, 4, 17, 'آیا می‌خواهید از پیامبر خود، همان تقاضای (نامعقولی را) بکنید که پیش از این، از موسی کردند؟! (و با این بهانه‌جویی‌ها، از ایمان آوردن سر باز زدند.) کسی که کفر را به جای ایمان بپذیرد، از راه مستقیم (عقل و فطرت) گمراه شده است.'),
(116, 116, 'وَدَّ كَثِيرٌ مِنْ أَهْلِ الْكِتَابِ لَوْ يَرُدُّونَكُمْ مِنْ بَعْدِ إِيمَانِكُمْ كُفَّارًا حَسَدًا مِنْ عِنْدِ أَنْفُسِهِمْ مِنْ بَعْدِ مَا تَبَيَّنَ لَهُمُ الْحَقُّ ۖ فَاعْفُوا وَاصْفَحُوا حَتَّىٰ يَأْتِيَ اللَّهُ بِأَمْرِهِ ۗ إِنَّ اللَّهَ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ', 2, 109, 1, 4, 17, 'بسیاری از اهل کتاب، از روی حسد -که در وجود آنها ریشه دوانده- آرزو می‌کردند شما را بعد از اسلام و ایمان، به حال کفر باز گردانند؛ با اینکه حق برای آنها کاملا روشن شده است. شما آنها را عفو کنید و گذشت نمایید؛ تا خداوند فرمان خودش (فرمان جهاد) را بفرستد؛ خداوند بر هر چیزی تواناست.'),
(117, 117, 'وَأَقِيمُوا الصَّلَاةَ وَآتُوا الزَّكَاةَ ۚ وَمَا تُقَدِّمُوا لِأَنْفُسِكُمْ مِنْ خَيْرٍ تَجِدُوهُ عِنْدَ اللَّهِ ۗ إِنَّ اللَّهَ بِمَا تَعْمَلُونَ بَصِيرٌ', 2, 110, 1, 4, 17, 'و نماز را برپا دارید و زکات را ادا کنید؛ و هر کار خیری را برای خود از پیش می‌فرستید، آن را نزد خدا (در سرای دیگر) خواهید یافت؛ خداوند به اعمال شما بیناست.'),
(118, 118, 'وَقَالُوا لَنْ يَدْخُلَ الْجَنَّةَ إِلَّا مَنْ كَانَ هُودًا أَوْ نَصَارَىٰ ۗ تِلْكَ أَمَانِيُّهُمْ ۗ قُلْ هَاتُوا بُرْهَانَكُمْ إِنْ كُنْتُمْ صَادِقِينَ', 2, 111, 1, 4, 17, 'آنها گفتند: «هیچ کس، جز یهود یا نصاری، هرگز داخل بهشت نخواهد شد.» این آرزوی آنهاست! بگو: «اگر راست می‌گویید، دلیل خود را (بر این موضوع) بیاورید!»'),
(119, 119, 'بَلَىٰ مَنْ أَسْلَمَ وَجْهَهُ لِلَّهِ وَهُوَ مُحْسِنٌ فَلَهُ أَجْرُهُ عِنْدَ رَبِّهِ وَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَ', 2, 112, 1, 4, 17, 'آری، کسی که روی خود را تسلیم خدا کند و نیکوکار باشد، پاداش او نزد پروردگارش ثابت است؛ نه ترسی بر آنهاست و نه غمگین می‌شوند. (بنابر این، بهشت خدا در انحصار هیچ گروهی نیست.)'),
(120, 120, 'وَقَالَتِ الْيَهُودُ لَيْسَتِ النَّصَارَىٰ عَلَىٰ شَيْءٍ وَقَالَتِ النَّصَارَىٰ لَيْسَتِ الْيَهُودُ عَلَىٰ شَيْءٍ وَهُمْ يَتْلُونَ الْكِتَابَ ۗ كَذَٰلِكَ قَالَ الَّذِينَ لَا يَعْلَمُونَ مِثْلَ قَوْلِهِمْ ۚ فَاللَّهُ يَحْكُمُ بَيْنَهُمْ يَوْمَ الْقِيَامَةِ فِيمَا كَانُوا فِيهِ يَخْتَلِفُونَ', 2, 113, 1, 4, 18, 'یهودیان گفتند: «مسیحیان هیچ موقعیتی (نزد خدا) ندارند»، و مسیحیان نیز گفتند: «یهودیان هیچ موقعیتی ندارند (و بر باطلند)»؛ در حالی که هر دو دسته، کتاب آسمانی را می‌خوانند (و باید از این گونه تعصبها برکنار باشند) افراد نادان (دیگر، همچون مشرکان) نیز، سخنی همانند سخن آنها داشتند! خداوند، روز قیامت، در باره آنچه در آن اختلاف داشتند، داوری می‌کند.'),
(121, 121, 'وَمَنْ أَظْلَمُ مِمَّنْ مَنَعَ مَسَاجِدَ اللَّهِ أَنْ يُذْكَرَ فِيهَا اسْمُهُ وَسَعَىٰ فِي خَرَابِهَا ۚ أُولَٰئِكَ مَا كَانَ لَهُمْ أَنْ يَدْخُلُوهَا إِلَّا خَائِفِينَ ۚ لَهُمْ فِي الدُّنْيَا خِزْيٌ وَلَهُمْ فِي الْآخِرَةِ عَذَابٌ عَظِيمٌ', 2, 114, 1, 4, 18, 'کیست ستمکارتر از آن کس که از بردن نام خدا در مساجد او جلوگیری کرد و سعی در ویرانی آنها نمود؟! شایسته نیست آنان، جز با ترس و وحشت، وارد این (کانونهای عبادت) شوند. بهره آنها در دنیا (فقط) رسوایی است و در سرای دیگر، عذاب عظیم (الهی)!!'),
(122, 122, 'وَلِلَّهِ الْمَشْرِقُ وَالْمَغْرِبُ ۚ فَأَيْنَمَا تُوَلُّوا فَثَمَّ وَجْهُ اللَّهِ ۚ إِنَّ اللَّهَ وَاسِعٌ عَلِيمٌ', 2, 115, 1, 4, 18, 'مشرق و مغرب، از آن خداست! و به هر سو رو کنید، خدا آنجاست! خداوند بی‌نیاز و داناست!'),
(123, 123, 'وَقَالُوا اتَّخَذَ اللَّهُ وَلَدًا ۗ سُبْحَانَهُ ۖ بَلْ لَهُ مَا فِي السَّمَاوَاتِ وَالْأَرْضِ ۖ كُلٌّ لَهُ قَانِتُونَ', 2, 116, 1, 4, 18, 'و (یهود و نصاری و مشرکان) گفتند: «خداوند، فرزندی برای خود انتخاب کرده است»! -منزه است او- بلکه آنچه در آسمانها و زمین است، از آن اوست؛ و همه در برابر او خاضعند!'),
(124, 124, 'بَدِيعُ السَّمَاوَاتِ وَالْأَرْضِ ۖ وَإِذَا قَضَىٰ أَمْرًا فَإِنَّمَا يَقُولُ لَهُ كُنْ فَيَكُونُ', 2, 117, 1, 4, 18, 'هستی بخش آسمانها و زمین اوست! و هنگامی که فرمان وجود چیزی را صادر کند، تنها می‌گوید: «موجود باش!» و آن، فوری موجود می‌شود.'),
(125, 125, 'وَقَالَ الَّذِينَ لَا يَعْلَمُونَ لَوْلَا يُكَلِّمُنَا اللَّهُ أَوْ تَأْتِينَا آيَةٌ ۗ كَذَٰلِكَ قَالَ الَّذِينَ مِنْ قَبْلِهِمْ مِثْلَ قَوْلِهِمْ ۘ تَشَابَهَتْ قُلُوبُهُمْ ۗ قَدْ بَيَّنَّا الْآيَاتِ لِقَوْمٍ يُوقِنُونَ', 2, 118, 1, 4, 18, 'افراد ناآگاه گفتند: «چرا خدا با ما سخن نمی‌گوید؟! و یا چرا آیه و نشانه‌ای برای خود ما نمی‌آید؟!» پیشینیان آنها نیز، همین گونه سخن می‌گفتند؛ دلها و افکارشان مشابه یکدیگر است؛ ولی ما (به اندازه کافی) آیات و نشانه‌ها را برای اهل یقین (و حقیقت‌جویان) روشن ساخته‌ایم.'),
(126, 126, 'إِنَّا أَرْسَلْنَاكَ بِالْحَقِّ بَشِيرًا وَنَذِيرًا ۖ وَلَا تُسْأَلُ عَنْ أَصْحَابِ الْجَحِيمِ', 2, 119, 1, 4, 18, 'ما تو را به حق، برای بشارت و بیم دادن (مردم جهان) فرستادیم؛ و تو مسئول (گمراهی) دوزخیان (پس از ابلاغ رسالت) نیستی!'),
(127, 127, 'وَلَنْ تَرْضَىٰ عَنْكَ الْيَهُودُ وَلَا النَّصَارَىٰ حَتَّىٰ تَتَّبِعَ مِلَّتَهُمْ ۗ قُلْ إِنَّ هُدَى اللَّهِ هُوَ الْهُدَىٰ ۗ وَلَئِنِ اتَّبَعْتَ أَهْوَاءَهُمْ بَعْدَ الَّذِي جَاءَكَ مِنَ الْعِلْمِ ۙ مَا لَكَ مِنَ اللَّهِ مِنْ وَلِيٍّ وَلَا نَصِيرٍ', 2, 120, 1, 4, 19, 'هرگز یهود و نصاری از تو راضی نخواهند شد، (تا به طور کامل، تسلیم خواسته‌های آنها شوی، و) از آیین (تحریف یافته) آنان، پیروی کنی. بگو: «هدایت، تنها هدایت الهی است!» و اگر از هوی و هوسهای آنان پیروی کنی، بعد از آنکه آگاه شده‌ای، هیچ سرپرست و یاوری از سوی خدا برای تو نخواهد بود.'),
(128, 128, 'الَّذِينَ آتَيْنَاهُمُ الْكِتَابَ يَتْلُونَهُ حَقَّ تِلَاوَتِهِ أُولَٰئِكَ يُؤْمِنُونَ بِهِ ۗ وَمَنْ يَكْفُرْ بِهِ فَأُولَٰئِكَ هُمُ الْخَاسِرُونَ', 2, 121, 1, 4, 19, 'کسانی که کتاب آسمانی به آنها داده‌ایم = یهود و نصاری‌ آن را چنان که شایسته آن است می‌خوانند؛ آنها به پیامبر اسلام ایمان می‌آورند؛ و کسانی که به او کافر شوند، زیانکارند.'),
(129, 129, 'يَا بَنِي إِسْرَائِيلَ اذْكُرُوا نِعْمَتِيَ الَّتِي أَنْعَمْتُ عَلَيْكُمْ وَأَنِّي فَضَّلْتُكُمْ عَلَى الْعَالَمِينَ', 2, 122, 1, 4, 19, 'ای بنی اسرائیل! نعمت مرا، که به شما ارزانی داشتم، به یاد آورید! و (نیز به خاطر آورید) که من شما را بر جهانیان برتری بخشیدم!'),
(130, 130, 'وَاتَّقُوا يَوْمًا لَا تَجْزِي نَفْسٌ عَنْ نَفْسٍ شَيْئًا وَلَا يُقْبَلُ مِنْهَا عَدْلٌ وَلَا تَنْفَعُهَا شَفَاعَةٌ وَلَا هُمْ يُنْصَرُونَ', 2, 123, 1, 4, 19, 'از روزی بترسید که هیچ کس از دیگری دفاع نمی‌کند؛ و هیچ‌گونه عوضی از او قبول نمی‌شود؛ و شفاعت، او را سود نمی‌دهد؛ و (از هیچ سوئی) یاری نمی‌شوند!'),
(131, 131, ' وَإِذِ ابْتَلَىٰ إِبْرَاهِيمَ رَبُّهُ بِكَلِمَاتٍ فَأَتَمَّهُنَّ ۖ قَالَ إِنِّي جَاعِلُكَ لِلنَّاسِ إِمَامًا ۖ قَالَ وَمِنْ ذُرِّيَّتِي ۖ قَالَ لَا يَنَالُ عَهْدِي الظَّالِمِينَ', 2, 124, 1, 4, 19, '(به خاطر آورید) هنگامی که خداوند، ابراهیم را با وسایل گوناگونی آزمود. و او به خوبی از عهده این آزمایشها برآمد. خداوند به او فرمود: «من تو را امام و پیشوای مردم قرار دادم!» ابراهیم عرض کرد: «از دودمان من (نیز امامانی قرار بده!)» خداوند فرمود: «پیمان من، به ستمکاران نمی‌رسد! (و تنها آن دسته از فرزندان تو که پاک و معصوم باشند، شایسته این مقامند)».'),
(132, 132, 'وَإِذْ جَعَلْنَا الْبَيْتَ مَثَابَةً لِلنَّاسِ وَأَمْنًا وَاتَّخِذُوا مِنْ مَقَامِ إِبْرَاهِيمَ مُصَلًّى ۖ وَعَهِدْنَا إِلَىٰ إِبْرَاهِيمَ وَإِسْمَاعِيلَ أَنْ طَهِّرَا بَيْتِيَ لِلطَّائِفِينَ وَالْعَاكِفِينَ وَالرُّكَّعِ السُّجُودِ', 2, 125, 1, 4, 19, 'و (به خاطر بیاورید) هنگامی که خانه کعبه را محل بازگشت و مرکز امن و امان برای مردم قرار دادیم! و (برای تجدید خاطره،) از مقام ابراهیم، عبادتگاهی برای خود انتخاب کنید! و ما به ابراهیم و اسماعیل امر کردیم که: «خانه مرا برای طواف‌کنندگان و مجاوران و رکوع‌کنندگان و سجده‌کنندگان، پاک و پاکیزه کنید!»');
INSERT INTO `Quran` (`rowid`, `id`, `Arabic`, `Chapter`, `Verse`, `Juz`, `Hizb`, `Page`, `tr`) VALUES
(133, 133, 'وَإِذْ قَالَ إِبْرَاهِيمُ رَبِّ اجْعَلْ هَٰذَا بَلَدًا آمِنًا وَارْزُقْ أَهْلَهُ مِنَ الثَّمَرَاتِ مَنْ آمَنَ مِنْهُمْ بِاللَّهِ وَالْيَوْمِ الْآخِرِ ۖ قَالَ وَمَنْ كَفَرَ فَأُمَتِّعُهُ قَلِيلًا ثُمَّ أَضْطَرُّهُ إِلَىٰ عَذَابِ النَّارِ ۖ وَبِئْسَ الْمَصِيرُ', 2, 126, 1, 4, 19, 'و (به یاد آورید) هنگامی را که ابراهیم عرض کرد: «پروردگارا! این سرزمین را شهر امنی قرار ده! و اهل آن را -آنها که به خدا و روز بازپسین، ایمان آورده‌اند- از ثمرات (گوناگون)، روزی ده!» گفت: «(ما دعای تو را اجابت کردیم؛ و مؤمنان را از انواع برکات، بهره‌مند ساختیم؛) اما به آنها که کافر شدند، بهره کمی خواهیم داد؛ سپس آنها را به عذاب آتش می‌کشانیم؛ و چه بد سرانجامی دارند»'),
(134, 134, 'وَإِذْ يَرْفَعُ إِبْرَاهِيمُ الْقَوَاعِدَ مِنَ الْبَيْتِ وَإِسْمَاعِيلُ رَبَّنَا تَقَبَّلْ مِنَّا ۖ إِنَّكَ أَنْتَ السَّمِيعُ الْعَلِيمُ', 2, 127, 1, 4, 20, 'و (نیز به یاد آورید) هنگامی را که ابراهیم و اسماعیل، پایه‌های خانه (کعبه) را بالا می‌بردند، (و می‌گفتند:) «پروردگارا! از ما بپذیر، که تو شنوا و دانایی!'),
(135, 135, 'رَبَّنَا وَاجْعَلْنَا مُسْلِمَيْنِ لَكَ وَمِنْ ذُرِّيَّتِنَا أُمَّةً مُسْلِمَةً لَكَ وَأَرِنَا مَنَاسِكَنَا وَتُبْ عَلَيْنَا ۖ إِنَّكَ أَنْتَ التَّوَّابُ الرَّحِيمُ', 2, 128, 1, 4, 20, 'پروردگارا! ما را تسلیم فرمان خود قرار ده! و از دودمان ما، امتی که تسلیم فرمانت باشند، به وجود آور! و طرز عبادتمان را به ما نشان ده و توبه ما را بپذیر، که تو توبه‌پذیر و مهربانی!'),
(136, 136, 'رَبَّنَا وَابْعَثْ فِيهِمْ رَسُولًا مِنْهُمْ يَتْلُو عَلَيْهِمْ آيَاتِكَ وَيُعَلِّمُهُمُ الْكِتَابَ وَالْحِكْمَةَ وَيُزَكِّيهِمْ ۚ إِنَّكَ أَنْتَ الْعَزِيزُ الْحَكِيمُ', 2, 129, 1, 4, 20, 'پروردگارا! در میان آنها پیامبری از خودشان برانگیز، تا آیات تو را بر آنان بخواند، و آنها را کتاب و حکمت بیاموزد، و پاکیزه کند؛ زیرا تو توانا و حکیمی (و بر این کار، قادری)!»'),
(137, 137, 'وَمَنْ يَرْغَبُ عَنْ مِلَّةِ إِبْرَاهِيمَ إِلَّا مَنْ سَفِهَ نَفْسَهُ ۚ وَلَقَدِ اصْطَفَيْنَاهُ فِي الدُّنْيَا ۖ وَإِنَّهُ فِي الْآخِرَةِ لَمِنَ الصَّالِحِينَ', 2, 130, 1, 4, 20, 'جز افراد سفیه و نادان، چه کسی از آیین ابراهیم، (با آن پاکی و درخشندگی،) روی‌گردان خواهد شد؟! ما او را در این جهان برگزیدیم؛ و او در جهان دیگر، از صالحان است.'),
(138, 138, 'إِذْ قَالَ لَهُ رَبُّهُ أَسْلِمْ ۖ قَالَ أَسْلَمْتُ لِرَبِّ الْعَالَمِينَ', 2, 131, 1, 4, 20, 'در آن هنگام که پروردگارش به او گفت: اسلام بیاور! (و در برابر حق، تسلیم باش! او فرمان پروردگار را، از جان و دل پذیرفت؛ و) گفت: «در برابر پروردگار جهانیان، تسلیم شدم.»'),
(139, 139, 'وَوَصَّىٰ بِهَا إِبْرَاهِيمُ بَنِيهِ وَيَعْقُوبُ يَا بَنِيَّ إِنَّ اللَّهَ اصْطَفَىٰ لَكُمُ الدِّينَ فَلَا تَمُوتُنَّ إِلَّا وَأَنْتُمْ مُسْلِمُونَ', 2, 132, 1, 4, 20, 'و ابراهیم و یعقوب (در واپسین لحظات عمر،) فرزندان خود را به این آیین، وصیت کردند؛ (و هر کدام به فرزندان خویش گفتند:) «فرزندان من! خداوند این آیین پاک را برای شما برگزیده است؛ و شما، جز به آیین اسلام = تسلیم در برابر فرمان خدا از دنیا نروید!»'),
(140, 140, 'أَمْ كُنْتُمْ شُهَدَاءَ إِذْ حَضَرَ يَعْقُوبَ الْمَوْتُ إِذْ قَالَ لِبَنِيهِ مَا تَعْبُدُونَ مِنْ بَعْدِي قَالُوا نَعْبُدُ إِلَٰهَكَ وَإِلَٰهَ آبَائِكَ إِبْرَاهِيمَ وَإِسْمَاعِيلَ وَإِسْحَاقَ إِلَٰهًا وَاحِدًا وَنَحْنُ لَهُ مُسْلِمُونَ', 2, 133, 1, 4, 20, 'آیا هنگامی که مرگ یعقوب فرا رسید، شما حاضر بودید؟! در آن هنگام که به فرزندان خود گفت: «پس از من، چه چیز را می‌پرستید؟» گفتند: «خدای تو، و خدای پدرانت، ابراهیم و اسماعیل و اسحاق، خداوند یکتا را، و ما در برابر او تسلیم هستیم.»'),
(141, 141, 'تِلْكَ أُمَّةٌ قَدْ خَلَتْ ۖ لَهَا مَا كَسَبَتْ وَلَكُمْ مَا كَسَبْتُمْ ۖ وَلَا تُسْأَلُونَ عَمَّا كَانُوا يَعْمَلُونَ', 2, 134, 1, 4, 20, 'آنها امتی بودند که درگذشتند. اعمال آنان، مربوط به خودشان بود و اعمال شما نیز مربوط به خود شماست؛ و شما هیچ‌گاه مسئول اعمال آنها نخواهید بود.'),
(142, 142, 'وَقَالُوا كُونُوا هُودًا أَوْ نَصَارَىٰ تَهْتَدُوا ۗ قُلْ بَلْ مِلَّةَ إِبْرَاهِيمَ حَنِيفًا ۖ وَمَا كَانَ مِنَ الْمُشْرِكِينَ', 2, 135, 1, 4, 21, '(اهل کتاب) گفتند: «یهودی یا مسیحی شوید، تا هدایت یابید!» بگو: «(این آیینهای تحریف شده، هرگز نمی‌تواند موجب هدایت گردد،) بلکه از آیین خالص ابراهیم پیروی کنید! و او هرگز از مشرکان نبود!»'),
(143, 143, 'قُولُوا آمَنَّا بِاللَّهِ وَمَا أُنْزِلَ إِلَيْنَا وَمَا أُنْزِلَ إِلَىٰ إِبْرَاهِيمَ وَإِسْمَاعِيلَ وَإِسْحَاقَ وَيَعْقُوبَ وَالْأَسْبَاطِ وَمَا أُوتِيَ مُوسَىٰ وَعِيسَىٰ وَمَا أُوتِيَ النَّبِيُّونَ مِنْ رَبِّهِمْ لَا نُفَرِّقُ بَيْنَ أَحَدٍ مِنْهُمْ وَنَحْنُ لَهُ مُسْلِمُونَ', 2, 136, 1, 4, 21, 'بگویید: «ما به خدا ایمان آورده‌ایم؛ و به آنچه بر ما نازل شده؛ و آنچه بر ابراهیم و اسماعیل و اسحاق و یعقوب و پیامبران از فرزندان او نازل گردید، و (همچنین) آنچه به موسی و عیسی و پیامبران (دیگر) از طرف پروردگار داده شده است، و در میان هیچ یک از آنها جدایی قائل نمی‌شویم، و در برابر فرمان خدا تسلیم هستیم؛ (و تعصبات نژادی و اغراض شخصی، سبب نمی‌شود که بعضی را بپذیریم و بعضی را رها کنیم.)»'),
(144, 144, 'فَإِنْ آمَنُوا بِمِثْلِ مَا آمَنْتُمْ بِهِ فَقَدِ اهْتَدَوْا ۖ وَإِنْ تَوَلَّوْا فَإِنَّمَا هُمْ فِي شِقَاقٍ ۖ فَسَيَكْفِيكَهُمُ اللَّهُ ۚ وَهُوَ السَّمِيعُ الْعَلِيمُ', 2, 137, 1, 4, 21, 'اگر آنها نیز به مانند آنچه شما ایمان آورده‌اید ایمان بیاورند، هدایت یافته‌اند؛ و اگر سرپیچی کنند، از حق جدا شده‌اند و خداوند، شر آنها را از تو دفع می‌کند؛ و او شنونده و داناست.'),
(145, 145, 'صِبْغَةَ اللَّهِ ۖ وَمَنْ أَحْسَنُ مِنَ اللَّهِ صِبْغَةً ۖ وَنَحْنُ لَهُ عَابِدُونَ', 2, 138, 1, 4, 21, 'رنگ خدایی (بپذیرید! رنگ ایمان و توحید و اسلام؛) و چه رنگی از رنگ خدایی بهتر است؟! و ما تنها او را عبادت می‌کنیم.'),
(146, 146, 'قُلْ أَتُحَاجُّونَنَا فِي اللَّهِ وَهُوَ رَبُّنَا وَرَبُّكُمْ وَلَنَا أَعْمَالُنَا وَلَكُمْ أَعْمَالُكُمْ وَنَحْنُ لَهُ مُخْلِصُونَ', 2, 139, 1, 4, 21, 'بگو: «آیا در باره خداوند با ما محاجه می‌کنید؟! در حالی که او، پروردگار ما و شماست؛ و اعمال ما از آن ما، و اعمال شما از آن شماست؛ و ما او را با اخلاص پرستش می‌کنیم، (و موحد خالصیم).»'),
(147, 147, 'أَمْ تَقُولُونَ إِنَّ إِبْرَاهِيمَ وَإِسْمَاعِيلَ وَإِسْحَاقَ وَيَعْقُوبَ وَالْأَسْبَاطَ كَانُوا هُودًا أَوْ نَصَارَىٰ ۗ قُلْ أَأَنْتُمْ أَعْلَمُ أَمِ اللَّهُ ۗ وَمَنْ أَظْلَمُ مِمَّنْ كَتَمَ شَهَادَةً عِنْدَهُ مِنَ اللَّهِ ۗ وَمَا اللَّهُ بِغَافِلٍ عَمَّا تَعْمَلُونَ', 2, 140, 1, 4, 21, 'یا می‌گویید: «ابراهیم و اسماعیل و اسحاق و یعقوب و اسباط، یهودی یا نصرانی بودند»؟! بگو: «شما بهتر می‌دانید یا خدا؟! (و با اینکه می‌دانید آنها یهودی یا نصرانی نبودند، چرا حقیقت را کتمان می‌کنید؟)» و چه کسی ستمکارتر است از آن کس که گواهی و شهادت الهی را که نزد اوست، کتمان می‌کند؟! و خدا از اعمال شما غافل نیست.'),
(148, 148, 'تِلْكَ أُمَّةٌ قَدْ خَلَتْ ۖ لَهَا مَا كَسَبَتْ وَلَكُمْ مَا كَسَبْتُمْ ۖ وَلَا تُسْأَلُونَ عَمَّا كَانُوا يَعْمَلُونَ', 2, 141, 1, 4, 21, '(به هر حال) آنها امتی بودند که درگذشتند. آنچه کردند، برای خودشان است؛ و آنچه هم شما کرده‌اید، برای خودتان است؛ و شما مسئول اعمال آنها نیستید.'),
(149, 149, ' سَيَقُولُ السُّفَهَاءُ مِنَ النَّاسِ مَا وَلَّاهُمْ عَنْ قِبْلَتِهِمُ الَّتِي كَانُوا عَلَيْهَا ۚ قُلْ لِلَّهِ الْمَشْرِقُ وَالْمَغْرِبُ ۚ يَهْدِي مَنْ يَشَاءُ إِلَىٰ صِرَاطٍ مُسْتَقِيمٍ', 2, 142, 2, 5, 22, 'به زودی سبک‌مغزان از مردم می‌گویند: «چه چیز آنها = مسلمانان‌ را، از قبله‌ای که بر آن بودند، بازگردانید؟!» بگو: «مشرق و مغرب، از آن خداست؛ خدا هر کس را بخواهد، به راه راست هدایت می‌کند.»'),
(150, 150, 'وَكَذَٰلِكَ جَعَلْنَاكُمْ أُمَّةً وَسَطًا لِتَكُونُوا شُهَدَاءَ عَلَى النَّاسِ وَيَكُونَ الرَّسُولُ عَلَيْكُمْ شَهِيدًا ۗ وَمَا جَعَلْنَا الْقِبْلَةَ الَّتِي كُنْتَ عَلَيْهَا إِلَّا لِنَعْلَمَ مَنْ يَتَّبِعُ الرَّسُولَ مِمَّنْ يَنْقَلِبُ عَلَىٰ عَقِبَيْهِ ۚ وَإِنْ كَانَتْ لَكَبِيرَةً إِلَّا عَلَى الَّذِينَ هَدَى اللَّهُ ۗ وَمَا كَانَ اللَّهُ لِيُضِيعَ إِيمَانَكُمْ ۚ إِنَّ اللَّهَ بِالنَّاسِ لَرَءُوفٌ رَحِيمٌ', 2, 143, 2, 5, 22, 'همان‌گونه (که قبله شما، یک قبله میانه است) شما را نیز، امت میانه‌ای قرار دادیم (در حد اعتدال، میان افراط و تفریط؛) تا بر مردم گواه باشید؛ و پیامبر هم بر شما گواه است. و ما، آن قبله‌ای را که قبلا بر آن بودی، تنها برای این قرار دادیم که افرادی که از پیامبر پیروی می‌کنند، از آنها که به جاهلیت بازمی‌گردند، مشخص شوند. و مسلماً این حکم، جز بر کسانی که خداوند آنها را هدایت کرده، دشوار بود. (این را نیز بدانید که نمازهای شما در برابر قبله سابق، صحیح بوده است؛) و خدا هرگز ایمان = نماز شما را ضایع نمی‌گرداند؛ زیرا خداوند، نسبت به مردم، رحیم و مهربان است.'),
(151, 151, 'قَدْ نَرَىٰ تَقَلُّبَ وَجْهِكَ فِي السَّمَاءِ ۖ فَلَنُوَلِّيَنَّكَ قِبْلَةً تَرْضَاهَا ۚ فَوَلِّ وَجْهَكَ شَطْرَ الْمَسْجِدِ الْحَرَامِ ۚ وَحَيْثُ مَا كُنْتُمْ فَوَلُّوا وُجُوهَكُمْ شَطْرَهُ ۗ وَإِنَّ الَّذِينَ أُوتُوا الْكِتَابَ لَيَعْلَمُونَ أَنَّهُ الْحَقُّ مِنْ رَبِّهِمْ ۗ وَمَا اللَّهُ بِغَافِلٍ عَمَّا يَعْمَلُونَ', 2, 144, 2, 5, 22, 'نگاه‌های انتظارآمیز تو را به سوی آسمان (برای تعیین قبله نهایی) می‌بینیم! اکنون تو را به سوی قبله‌ای که از آن خشنود باشی، باز می‌گردانیم. پس روی خود را به سوی مسجد الحرام کن! و هر جا باشید، روی خود را به سوی آن بگردانید! و کسانی که کتاب آسمانی به آنها داده شده، بخوبی می‌دانند این فرمانِ حقی است که از ناحیه پروردگارشان صادر شده؛ (و در کتابهای خود خوانده‌اند که پیغمبر اسلام، به سوی دو قبله، نماز می‌خواند). و خداوند از اعمال آنها (در مخفی داشتن این آیات) غافل نیست!'),
(152, 152, 'وَلَئِنْ أَتَيْتَ الَّذِينَ أُوتُوا الْكِتَابَ بِكُلِّ آيَةٍ مَا تَبِعُوا قِبْلَتَكَ ۚ وَمَا أَنْتَ بِتَابِعٍ قِبْلَتَهُمْ ۚ وَمَا بَعْضُهُمْ بِتَابِعٍ قِبْلَةَ بَعْضٍ ۚ وَلَئِنِ اتَّبَعْتَ أَهْوَاءَهُمْ مِنْ بَعْدِ مَا جَاءَكَ مِنَ الْعِلْمِ ۙ إِنَّكَ إِذًا لَمِنَ الظَّالِمِينَ', 2, 145, 2, 5, 22, 'سوگند که اگر برای (این گروه از) اهل کتاب، هرگونه آیه (و نشانه و دلیلی) بیاوری، از قبله تو پیروی نخواهند کرد؛ و تو نیز، هیچ‌گاه از قبله آنان، پیروی نخواهی نمود. (آنها نباید تصور کنند که بار دیگر، تغییر قبله امکان‌پذیر است!) و حتی هیچ‌یک از آنها، پیروی از قبله دیگری نخواهد کرد! و اگر تو، پس از این آگاهی، متابعت هوسهای آنها کنی، مسلماً از ستمگران خواهی بود!'),
(153, 153, 'الَّذِينَ آتَيْنَاهُمُ الْكِتَابَ يَعْرِفُونَهُ كَمَا يَعْرِفُونَ أَبْنَاءَهُمْ ۖ وَإِنَّ فَرِيقًا مِنْهُمْ لَيَكْتُمُونَ الْحَقَّ وَهُمْ يَعْلَمُونَ', 2, 146, 2, 5, 23, 'کسانی که کتاب آسمانی به آنان داده‌ایم، او = پیامبر را همچون فرزندان خود می‌شناسند؛ (ولی) جمعی از آنان، حق را آگاهانه کتمان می‌کنند!'),
(154, 154, 'الْحَقُّ مِنْ رَبِّكَ ۖ فَلَا تَكُونَنَّ مِنَ الْمُمْتَرِينَ', 2, 147, 2, 5, 23, 'این (فرمان تغییر قبله) حکمِ حقی از طرف پروردگار توست، بنابراین، هرگز از تردیدکنندگان در آن مباش!'),
(155, 155, 'وَلِكُلٍّ وِجْهَةٌ هُوَ مُوَلِّيهَا ۖ فَاسْتَبِقُوا الْخَيْرَاتِ ۚ أَيْنَ مَا تَكُونُوا يَأْتِ بِكُمُ اللَّهُ جَمِيعًا ۚ إِنَّ اللَّهَ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ', 2, 148, 2, 5, 23, 'هر طایفه‌ای قبله‌ای دارد که خداوند آن را تعیین کرده است؛ (بنابراین، زیاد در باره قبله گفتگو نکنید! و به جای آن،) در نیکی‌ها و اعمال خیر، بر یکدیگر سبقت جویید! هر جا باشید، خداوند همه شما را (برای پاداش و کیفر در برابر اعمال نیک و بد، در روز رستاخیز،) حاضر می‌کند؛ زیرا او، بر هر کاری تواناست.'),
(156, 156, 'وَمِنْ حَيْثُ خَرَجْتَ فَوَلِّ وَجْهَكَ شَطْرَ الْمَسْجِدِ الْحَرَامِ ۖ وَإِنَّهُ لَلْحَقُّ مِنْ رَبِّكَ ۗ وَمَا اللَّهُ بِغَافِلٍ عَمَّا تَعْمَلُونَ', 2, 149, 2, 5, 23, 'از هر جا (و از هر شهر و نقطه‌ای) خارج شدی، (به هنگام نماز،) روی خود را به جانب «مسجد الحرام» کن! این دستور حقی از طرف پروردگار توست! و خداوند، از آنچه انجام می‌دهید، غافل نیست!'),
(157, 157, 'وَمِنْ حَيْثُ خَرَجْتَ فَوَلِّ وَجْهَكَ شَطْرَ الْمَسْجِدِ الْحَرَامِ ۚ وَحَيْثُ مَا كُنْتُمْ فَوَلُّوا وُجُوهَكُمْ شَطْرَهُ لِئَلَّا يَكُونَ لِلنَّاسِ عَلَيْكُمْ حُجَّةٌ إِلَّا الَّذِينَ ظَلَمُوا مِنْهُمْ فَلَا تَخْشَوْهُمْ وَاخْشَوْنِي وَلِأُتِمَّ نِعْمَتِي عَلَيْكُمْ وَلَعَلَّكُمْ تَهْتَدُونَ', 2, 150, 2, 5, 23, 'و از هر جا خارج شدی، روی خود را به جانب مسجد الحرام کن! و هر جا بودید، روی خود را به سوی آن کنید! تا مردم، جز ظالمان (که دست از لجاجت برنمی‌دارند،) دلیلی بر ضدّ شما نداشته باشند؛ (زیرا از نشانه‌های پیامبر، که در کتب آسمانی پیشین آمده، این است که او، به سوی دو قبله، نماز می‌خواند.) از آنها نترسید! و (تنها) از من بترسید! (این تغییر قبله، به خاطر آن بود که) نعمت خود را بر شما تمام کنم، شاید هدایت شوید!'),
(158, 158, 'كَمَا أَرْسَلْنَا فِيكُمْ رَسُولًا مِنْكُمْ يَتْلُو عَلَيْكُمْ آيَاتِنَا وَيُزَكِّيكُمْ وَيُعَلِّمُكُمُ الْكِتَابَ وَالْحِكْمَةَ وَيُعَلِّمُكُمْ مَا لَمْ تَكُونُوا تَعْلَمُونَ', 2, 151, 2, 5, 23, 'همان‌گونه (که با تغییر قبله، نعمت خود را بر شما کامل کردیم،) رسولی از خودتان در میان شما فرستادیم؛ تا آیات ما را بر شما بخواند؛ و شما را پاک کند؛ و به شما، کتاب و حکمت بیاموزد؛ و آنچه را نمی‌دانستید، به شما یاد دهد.'),
(159, 159, 'فَاذْكُرُونِي أَذْكُرْكُمْ وَاشْكُرُوا لِي وَلَا تَكْفُرُونِ', 2, 152, 2, 5, 23, 'پس به یاد من باشید، تا به یاد شما باشم! و شکر مرا گویید و (در برابر نعمتهایم) کفران نکنید!'),
(160, 160, 'يَا أَيُّهَا الَّذِينَ آمَنُوا اسْتَعِينُوا بِالصَّبْرِ وَالصَّلَاةِ ۚ إِنَّ اللَّهَ مَعَ الصَّابِرِينَ', 2, 153, 2, 5, 23, 'ای افرادی که ایمان آورده‌اید! از صبر (و استقامت) و نماز، کمک بگیرید! (زیرا) خداوند با صابران است.'),
(161, 161, 'وَلَا تَقُولُوا لِمَنْ يُقْتَلُ فِي سَبِيلِ اللَّهِ أَمْوَاتٌ ۚ بَلْ أَحْيَاءٌ وَلَٰكِنْ لَا تَشْعُرُونَ', 2, 154, 2, 5, 24, 'و به آنها که در راه خدا کشته می‌شوند، مرده نگویید! بلکه آنان زنده‌اند، ولی شما نمی‌فهمید!'),
(162, 162, 'وَلَنَبْلُوَنَّكُمْ بِشَيْءٍ مِنَ الْخَوْفِ وَالْجُوعِ وَنَقْصٍ مِنَ الْأَمْوَالِ وَالْأَنْفُسِ وَالثَّمَرَاتِ ۗ وَبَشِّرِ الصَّابِرِينَ', 2, 155, 2, 5, 24, 'قطعاً همه شما را با چیزی از ترس، گرسنگی، و کاهش در مالها و جانها و میوه‌ها، آزمایش می‌کنیم؛ و بشارت ده به استقامت‌کنندگان!'),
(163, 163, 'الَّذِينَ إِذَا أَصَابَتْهُمْ مُصِيبَةٌ قَالُوا إِنَّا لِلَّهِ وَإِنَّا إِلَيْهِ رَاجِعُونَ', 2, 156, 2, 5, 24, 'آنها که هر گاه مصیبتی به ایشان می‌رسد، می‌گویند: «ما از آنِ خدائیم؛ و به سوی او بازمی‌گردیم!»'),
(164, 164, 'أُولَٰئِكَ عَلَيْهِمْ صَلَوَاتٌ مِنْ رَبِّهِمْ وَرَحْمَةٌ ۖ وَأُولَٰئِكَ هُمُ الْمُهْتَدُونَ', 2, 157, 2, 5, 24, 'اینها، همانها هستند که الطاف و رحمت خدا شامل حالشان شده؛ و آنها هستند هدایت‌یافتگان!'),
(165, 165, ' إِنَّ الصَّفَا وَالْمَرْوَةَ مِنْ شَعَائِرِ اللَّهِ ۖ فَمَنْ حَجَّ الْبَيْتَ أَوِ اعْتَمَرَ فَلَا جُنَاحَ عَلَيْهِ أَنْ يَطَّوَّفَ بِهِمَا ۚ وَمَنْ تَطَوَّعَ خَيْرًا فَإِنَّ اللَّهَ شَاكِرٌ عَلِيمٌ', 2, 158, 2, 5, 24, '«صفا» و «مروه» از شعائر (و نشانه‌های) خداست! بنابراین، کسانی که حجِ خانه خدا و یا عمره انجام می‌دهند، مانعی نیست که بر آن دو طواف کنند؛ (و سعیِ صفا و مروه انجام دهند. و هرگز اعمال بی‌رویّه مشرکان، که بتهایی بر این دو کوه نصب کرده بودند، از موقعیّت این دو مکان مقدّس نمی‌کاهد!) و کسی که فرمان خدا را در انجام کارهای نیک اطاعت کند، خداوند (در برابر عمل او) شکرگزار، و (از افعال وی) آگاه است.'),
(166, 166, 'إِنَّ الَّذِينَ يَكْتُمُونَ مَا أَنْزَلْنَا مِنَ الْبَيِّنَاتِ وَالْهُدَىٰ مِنْ بَعْدِ مَا بَيَّنَّاهُ لِلنَّاسِ فِي الْكِتَابِ ۙ أُولَٰئِكَ يَلْعَنُهُمُ اللَّهُ وَيَلْعَنُهُمُ اللَّاعِنُونَ', 2, 159, 2, 5, 24, 'کسانی که دلایل روشن، و وسیله هدایتی را که نازل کرده‌ایم، بعد از آنکه در کتاب برای مردم بیان نمودیم، کتمان کنند، خدا آنها را لعنت می‌کند؛ و همه لعن‌کنندگان نیز، آنها را لعن می‌کنند؛'),
(167, 167, 'إِلَّا الَّذِينَ تَابُوا وَأَصْلَحُوا وَبَيَّنُوا فَأُولَٰئِكَ أَتُوبُ عَلَيْهِمْ ۚ وَأَنَا التَّوَّابُ الرَّحِيمُ', 2, 160, 2, 5, 24, 'مگر آنها که توبه و بازگشت کردند، و (اعمال بد خود را، با اعمال نیک،) اصلاح نمودند، (و آنچه را کتمان کرده بودند؛ آشکار ساختند؛) من توبه آنها را می‌پذیرم؛ که من توّاب و رحیمم.'),
(168, 168, 'إِنَّ الَّذِينَ كَفَرُوا وَمَاتُوا وَهُمْ كُفَّارٌ أُولَٰئِكَ عَلَيْهِمْ لَعْنَةُ اللَّهِ وَالْمَلَائِكَةِ وَالنَّاسِ أَجْمَعِينَ', 2, 161, 2, 5, 24, 'کسانی که کافر شدند، و در حالِ کفر از دنیا رفتند، لعنت خداوند و فرشتگان و همه مردم بر آنها خواهد بود!'),
(169, 169, 'خَالِدِينَ فِيهَا ۖ لَا يُخَفَّفُ عَنْهُمُ الْعَذَابُ وَلَا هُمْ يُنْظَرُونَ', 2, 162, 2, 5, 24, 'همیشه در آن (لعن و دوری از رحمت پروردگار) باقی می‌مانند؛ نه در عذاب آنان تخفیف داده می‌شود، و نه مهلتی خواهند داشت!'),
(170, 170, 'وَإِلَٰهُكُمْ إِلَٰهٌ وَاحِدٌ ۖ لَا إِلَٰهَ إِلَّا هُوَ الرَّحْمَٰنُ الرَّحِيمُ', 2, 163, 2, 5, 24, 'و خدای شما، خداوند یگانه‌ای است، که غیر از او معبودی نیست! اوست بخشنده و مهربان (و دارای رحمت عام و خاصّ)!'),
(171, 171, 'إِنَّ فِي خَلْقِ السَّمَاوَاتِ وَالْأَرْضِ وَاخْتِلَافِ اللَّيْلِ وَالنَّهَارِ وَالْفُلْكِ الَّتِي تَجْرِي فِي الْبَحْرِ بِمَا يَنْفَعُ النَّاسَ وَمَا أَنْزَلَ اللَّهُ مِنَ السَّمَاءِ مِنْ مَاءٍ فَأَحْيَا بِهِ الْأَرْضَ بَعْدَ مَوْتِهَا وَبَثَّ فِيهَا مِنْ كُلِّ دَابَّةٍ وَتَصْرِيفِ الرِّيَاحِ وَالسَّحَابِ الْمُسَخَّرِ بَيْنَ السَّمَاءِ وَالْأَرْضِ لَآيَاتٍ لِقَوْمٍ يَعْقِلُونَ', 2, 164, 2, 5, 25, 'در آفرینش آسمانها و زمین، و آمد و شد شب و روز، و کشتیهایی که در دریا به سود مردم در حرکتند، و آبی که خداوند از آسمان نازل کرده، و با آن، زمین را پس از مرگ، زنده نموده، و انواع جنبندگان را در آن گسترده، و (همچنین) در تغییر مسیر بادها و ابرهایی که میان زمین و آسمان مسخرند، نشانه‌هایی است (از ذات پاک خدا و یگانگی او) برای مردمی که عقل دارند و می‌اندیشند!'),
(172, 172, 'وَمِنَ النَّاسِ مَنْ يَتَّخِذُ مِنْ دُونِ اللَّهِ أَنْدَادًا يُحِبُّونَهُمْ كَحُبِّ اللَّهِ ۖ وَالَّذِينَ آمَنُوا أَشَدُّ حُبًّا لِلَّهِ ۗ وَلَوْ يَرَى الَّذِينَ ظَلَمُوا إِذْ يَرَوْنَ الْعَذَابَ أَنَّ الْقُوَّةَ لِلَّهِ جَمِيعًا وَأَنَّ اللَّهَ شَدِيدُ الْعَذَابِ', 2, 165, 2, 5, 25, 'بعضی از مردم، معبودهایی غیر از خداوند برای خود انتخاب می‌کنند؛ و آنها را همچون خدا دوست می‌دارند. امّا آنها که ایمان دارند، عشقشان به خدا، (از مشرکان نسبت به معبودهاشان،) شدیدتر است. و آنها که ستم کردند، (و معبودی غیر خدا برگزیدند،) هنگامی که عذاب (الهی) را مشاهده کنند، خواهند دانست که تمامِ قدرت، از آنِ خداست؛ و خدا دارای مجازات شدید است؛ (نه معبودهای خیالی که از آنها می‌هراسند.)'),
(173, 173, 'إِذْ تَبَرَّأَ الَّذِينَ اتُّبِعُوا مِنَ الَّذِينَ اتَّبَعُوا وَرَأَوُا الْعَذَابَ وَتَقَطَّعَتْ بِهِمُ الْأَسْبَابُ', 2, 166, 2, 5, 25, 'در آن هنگام، رهبران (گمراه و گمراه‌کننده) از پیروانِ خود، بیزاری می‌جویند؛ و کیفر خدا را مشاهده می‌کنند؛ و دستشان از همه جا کوتاه می‌شود.'),
(174, 174, 'وَقَالَ الَّذِينَ اتَّبَعُوا لَوْ أَنَّ لَنَا كَرَّةً فَنَتَبَرَّأَ مِنْهُمْ كَمَا تَبَرَّءُوا مِنَّا ۗ كَذَٰلِكَ يُرِيهِمُ اللَّهُ أَعْمَالَهُمْ حَسَرَاتٍ عَلَيْهِمْ ۖ وَمَا هُمْ بِخَارِجِينَ مِنَ النَّارِ', 2, 167, 2, 5, 25, 'و (در این هنگام) پیروان می‌گویند: «کاش بار دیگر به دنیا برمی‌گشتیم، تا از آنها = پیشوایان گمراه‌ بیزاری جوییم، آن چنان که آنان (امروز) از ما بیزاری جستند! (آری،) خداوند این چنین اعمال آنها را به صورت حسرت‌زایی به آنان نشان می‌دهد؛ و هرگز از آتش (دوزخ) خارج نخواهند شد!'),
(175, 175, 'يَا أَيُّهَا النَّاسُ كُلُوا مِمَّا فِي الْأَرْضِ حَلَالًا طَيِّبًا وَلَا تَتَّبِعُوا خُطُوَاتِ الشَّيْطَانِ ۚ إِنَّهُ لَكُمْ عَدُوٌّ مُبِينٌ', 2, 168, 2, 5, 25, 'ای مردم! از آنچه در زمین است، حلال و پاکیزه بخورید! و از گامهای شیطان، پیروی نکنید! چه اینکه او، دشمن آشکار شماست!'),
(176, 176, 'إِنَّمَا يَأْمُرُكُمْ بِالسُّوءِ وَالْفَحْشَاءِ وَأَنْ تَقُولُوا عَلَى اللَّهِ مَا لَا تَعْلَمُونَ', 2, 169, 2, 5, 25, 'او شما را فقط به بدیها و کار زشت فرمان می‌دهد؛ (و نیز دستور می‌دهد) آنچه را که نمی‌دانید، به خدا نسبت دهید.'),
(177, 177, 'وَإِذَا قِيلَ لَهُمُ اتَّبِعُوا مَا أَنْزَلَ اللَّهُ قَالُوا بَلْ نَتَّبِعُ مَا أَلْفَيْنَا عَلَيْهِ آبَاءَنَا ۗ أَوَلَوْ كَانَ آبَاؤُهُمْ لَا يَعْقِلُونَ شَيْئًا وَلَا يَهْتَدُونَ', 2, 170, 2, 5, 26, 'و هنگامی که به آنها گفته شود: «از آنچه خدا نازل کرده است، پیروی کنید!» می‌گویند: «نه، ما از آنچه پدران خود را بر آن یافتیم، پیروی می‌نماییم.» آیا اگر پدران آنها، چیزی نمی‌فهمیدند و هدایت نیافتند (باز از آنها پیروی خواهند کرد)؟!'),
(178, 178, 'وَمَثَلُ الَّذِينَ كَفَرُوا كَمَثَلِ الَّذِي يَنْعِقُ بِمَا لَا يَسْمَعُ إِلَّا دُعَاءً وَنِدَاءً ۚ صُمٌّ بُكْمٌ عُمْيٌ فَهُمْ لَا يَعْقِلُونَ', 2, 171, 2, 5, 26, 'مَثَل (تو در دعوت) کافران، بسان کسی است که (گوسفندان و حیوانات را برای نجات از چنگال خطر،) صدا می‌زند؛ ولی آنها چیزی جز سر و صدا نمی‌شنوند؛ (و حقیقت و مفهوم گفتار او را درک نمی‌کنند. این کافران، در واقع) کر و لال و نابینا هستند؛ از این رو چیزی نمی‌فهمند!'),
(179, 179, 'يَا أَيُّهَا الَّذِينَ آمَنُوا كُلُوا مِنْ طَيِّبَاتِ مَا رَزَقْنَاكُمْ وَاشْكُرُوا لِلَّهِ إِنْ كُنْتُمْ إِيَّاهُ تَعْبُدُونَ', 2, 172, 2, 5, 26, 'ای کسانی که ایمان آورده‌اید! از نعمتهای پاکیزه‌ای که به شما روزی داده‌ایم، بخورید و شکر خدا را بجا آورید؛ اگر او را پرستش می‌کنید!'),
(180, 180, 'إِنَّمَا حَرَّمَ عَلَيْكُمُ الْمَيْتَةَ وَالدَّمَ وَلَحْمَ الْخِنْزِيرِ وَمَا أُهِلَّ بِهِ لِغَيْرِ اللَّهِ ۖ فَمَنِ اضْطُرَّ غَيْرَ بَاغٍ وَلَا عَادٍ فَلَا إِثْمَ عَلَيْهِ ۚ إِنَّ اللَّهَ غَفُورٌ رَحِيمٌ', 2, 173, 2, 5, 26, 'خداوند، تنها (گوشت) مردار، خون، گوشت خوک و آنچه را نام غیرِ خدا به هنگام ذبح بر آن گفته شود، حرام کرده است. (ولی) آن کس که مجبور شود، در صورتی که ستمگر و متجاوز نباشد، گناهی بر او نیست؛ (و می‌تواند برای حفظ جان خود، در موقع ضرورت، از آن بخورد؛) خداوند بخشنده و مهربان است.'),
(181, 181, 'إِنَّ الَّذِينَ يَكْتُمُونَ مَا أَنْزَلَ اللَّهُ مِنَ الْكِتَابِ وَيَشْتَرُونَ بِهِ ثَمَنًا قَلِيلًا ۙ أُولَٰئِكَ مَا يَأْكُلُونَ فِي بُطُونِهِمْ إِلَّا النَّارَ وَلَا يُكَلِّمُهُمُ اللَّهُ يَوْمَ الْقِيَامَةِ وَلَا يُزَكِّيهِمْ وَلَهُمْ عَذَابٌ أَلِيمٌ', 2, 174, 2, 5, 26, 'کسانی که کتمان می‌کنند آنچه را خدا از کتاب نازل کرده، و آن را به بهای کمی می‌فروشند، آنها جز آتش چیزی نمی‌خورند؛ (و هدایا و اموالی که از این رهگذر به دست می‌آورند، در حقیقت آتش سوزانی است.) و خداوند، روز قیامت، با آنها سخن نمی‌گوید؛ و آنان را پاکیزه نمی‌کند؛ و برای آنها عذاب دردناکی است.'),
(182, 182, 'أُولَٰئِكَ الَّذِينَ اشْتَرَوُا الضَّلَالَةَ بِالْهُدَىٰ وَالْعَذَابَ بِالْمَغْفِرَةِ ۚ فَمَا أَصْبَرَهُمْ عَلَى النَّارِ', 2, 175, 2, 5, 26, 'اینان، همانهایی هستند که گمراهی را با هدایت، و عذاب را با آمرزش، مبادله کرده‌اند؛ راستی چقدر در برابر عذاب خداوند، شکیبا هستند!!'),
(183, 183, 'ذَٰلِكَ بِأَنَّ اللَّهَ نَزَّلَ الْكِتَابَ بِالْحَقِّ ۗ وَإِنَّ الَّذِينَ اخْتَلَفُوا فِي الْكِتَابِ لَفِي شِقَاقٍ بَعِيدٍ', 2, 176, 2, 5, 26, 'اینها، به خاطر آن است که خداوند، کتاب (آسمانی) را به حق، (و توأم با نشانه‌ها و دلایل روشن،) نازل کرده؛ و آنها که در آن اختلاف می‌کنند، (و با کتمان و تحریف، اختلاف به وجود می‌آورند،) در شکاف و (پراکندگی) عمیقی قرار دارند.'),
(184, 184, ' لَيْسَ الْبِرَّ أَنْ تُوَلُّوا وُجُوهَكُمْ قِبَلَ الْمَشْرِقِ وَالْمَغْرِبِ وَلَٰكِنَّ الْبِرَّ مَنْ آمَنَ بِاللَّهِ وَالْيَوْمِ الْآخِرِ وَالْمَلَائِكَةِ وَالْكِتَابِ وَالنَّبِيِّينَ وَآتَى الْمَالَ عَلَىٰ حُبِّهِ ذَوِي الْقُرْبَىٰ وَالْيَتَامَىٰ وَالْمَسَاكِينَ وَابْنَ السَّبِيلِ وَالسَّائِلِينَ وَفِي الرِّقَابِ وَأَقَامَ الصَّلَاةَ وَآتَى الزَّكَاةَ وَالْمُوفُونَ بِعَهْدِهِمْ إِذَا عَاهَدُوا ۖ وَالصَّابِرِينَ فِي الْبَأْسَاءِ وَالضَّرَّاءِ وَحِينَ الْبَأْسِ ۗ أُولَٰئِكَ الَّذِينَ صَدَقُوا ۖ وَأُولَٰئِكَ هُمُ الْمُتَّقُونَ', 2, 177, 2, 6, 27, 'نیکی، (تنها) این نیست که (به هنگام نماز،) رویِ خود را به سوی مشرق و (یا) مغرب کنید؛ (و تمام گفتگوی شما، در باره قبله و تغییر آن باشد؛ و همه وقت خود را مصروف آن سازید؛) بلکه نیکی (و نیکوکار) کسی است که به خدا، و روز رستاخیز، و فرشتگان، و کتاب (آسمانی)، و پیامبران، ایمان آورده؛ و مال (خود) را، با همه علاقه‌ای که به آن دارد، به خویشاوندان و یتیمان و مسکینان و واماندگان در راه و سائلان و بردگان، انفاق می‌کند؛ نماز را برپا می‌دارد و زکات را می‌پردازد؛ و (همچنین) کسانی که به عهد خود -به هنگامی که عهد بستند-وفا می‌کنند؛ و در برابر محرومیتها و بیماریها و در میدان جنگ، استقامت به خرج می‌دهند؛ اینها کسانی هستند که راست می‌گویند؛ و (گفتارشان با اعتقادشان هماهنگ است؛) و اینها هستند پرهیزکاران!'),
(185, 185, 'يَا أَيُّهَا الَّذِينَ آمَنُوا كُتِبَ عَلَيْكُمُ الْقِصَاصُ فِي الْقَتْلَى ۖ الْحُرُّ بِالْحُرِّ وَالْعَبْدُ بِالْعَبْدِ وَالْأُنْثَىٰ بِالْأُنْثَىٰ ۚ فَمَنْ عُفِيَ لَهُ مِنْ أَخِيهِ شَيْءٌ فَاتِّبَاعٌ بِالْمَعْرُوفِ وَأَدَاءٌ إِلَيْهِ بِإِحْسَانٍ ۗ ذَٰلِكَ تَخْفِيفٌ مِنْ رَبِّكُمْ وَرَحْمَةٌ ۗ فَمَنِ اعْتَدَىٰ بَعْدَ ذَٰلِكَ فَلَهُ عَذَابٌ أَلِيمٌ', 2, 178, 2, 6, 27, 'ای افرادی که ایمان آورده‌اید! حکم قصاص در مورد کشتگان، بر شما نوشته شده است: آزاد در برابر آزاد، و برده در برابر برده، و زن در برابر زن، پس اگر کسی از سوی برادر (دینی) خود، چیزی به او بخشیده شود، (و حکم قصاص او، تبدیل به خونبها گردد،) باید از راه پسندیده پیروی کند. (و صاحب خون، حال پرداخت کننده دیه را در نظر بگیرد.) و او = قاتل‌ نیز، به نیکی دیه را (به ولی مقتول) بپردازد؛ (و در آن، مسامحه نکند.) این، تخفیف و رحمتی است از ناحیه پروردگار شما! و کسی که بعد از آن، تجاوز کند، عذاب دردناکی خواهد داشت.'),
(186, 186, 'وَلَكُمْ فِي الْقِصَاصِ حَيَاةٌ يَا أُولِي الْأَلْبَابِ لَعَلَّكُمْ تَتَّقُونَ', 2, 179, 2, 6, 27, 'و برای شما در قصاص، حیات و زندگی است، ای صاحبان خِرد! شاید شما تقوا پیشه کنید.'),
(187, 187, 'كُتِبَ عَلَيْكُمْ إِذَا حَضَرَ أَحَدَكُمُ الْمَوْتُ إِنْ تَرَكَ خَيْرًا الْوَصِيَّةُ لِلْوَالِدَيْنِ وَالْأَقْرَبِينَ بِالْمَعْرُوفِ ۖ حَقًّا عَلَى الْمُتَّقِينَ', 2, 180, 2, 6, 27, 'بر شما نوشته شده: «هنگامی که یکی از شما را مرگ فرا رسد، اگر چیز خوبی = مالی‌ از خود به جای گذارده، برای پدر و مادر و نزدیکان، بطور شایسته وصیت کند! این حقّی است بر پرهیزکاران!»'),
(188, 188, 'فَمَنْ بَدَّلَهُ بَعْدَمَا سَمِعَهُ فَإِنَّمَا إِثْمُهُ عَلَى الَّذِينَ يُبَدِّلُونَهُ ۚ إِنَّ اللَّهَ سَمِيعٌ عَلِيمٌ', 2, 181, 2, 6, 27, 'پس کسانی که بعد از شنیدنش آن را تغییر دهند، گناه آن، تنها بر کسانی است که آن (وصیّت) را تغییر می‌دهند؛ خداوند، شنوا و داناست.'),
(189, 189, 'فَمَنْ خَافَ مِنْ مُوصٍ جَنَفًا أَوْ إِثْمًا فَأَصْلَحَ بَيْنَهُمْ فَلَا إِثْمَ عَلَيْهِ ۚ إِنَّ اللَّهَ غَفُورٌ رَحِيمٌ', 2, 182, 2, 6, 28, 'و کسی که از انحرافِ وصیت کننده (و تمایل یک‌جانبه او به بعض ورثه)، یا از گناه او (که مبادا وصیّت به کار خلافی کند) بترسد، و میان آنها را اصلاح دهد، گناهی بر او نیست؛ (و مشمولِ حکم تبدیلِ وصیّت نمی‌باشد.) خداوند، آمرزنده و مهربان است.'),
(190, 190, 'يَا أَيُّهَا الَّذِينَ آمَنُوا كُتِبَ عَلَيْكُمُ الصِّيَامُ كَمَا كُتِبَ عَلَى الَّذِينَ مِنْ قَبْلِكُمْ لَعَلَّكُمْ تَتَّقُونَ', 2, 183, 2, 6, 28, 'ای افرادی که ایمان آورده‌اید! روزه بر شما نوشته شده، همان‌گونه که بر کسانی که قبل از شما بودند نوشته شد؛ تا پرهیزکار شوید.'),
(191, 191, 'أَيَّامًا مَعْدُودَاتٍ ۚ فَمَنْ كَانَ مِنْكُمْ مَرِيضًا أَوْ عَلَىٰ سَفَرٍ فَعِدَّةٌ مِنْ أَيَّامٍ أُخَرَ ۚ وَعَلَى الَّذِينَ يُطِيقُونَهُ فِدْيَةٌ طَعَامُ مِسْكِينٍ ۖ فَمَنْ تَطَوَّعَ خَيْرًا فَهُوَ خَيْرٌ لَهُ ۚ وَأَنْ تَصُومُوا خَيْرٌ لَكُمْ ۖ إِنْ كُنْتُمْ تَعْلَمُونَ', 2, 184, 2, 6, 28, 'چند روز معدودی را (باید روزه بدارید!) و هر کس از شما بیمار یا مسافر باشد تعدادی از روزهای دیگر را (روزه بدارد) و بر کسانی که روزه برای آنها طاقت‌فرساست؛ (همچون بیماران مزمن، و پیرمردان و پیرزنان،) لازم است کفّاره بدهند: مسکینی را اطعام کنند؛ و کسی که کارِ خیری انجام دهد، برای او بهتر است؛ و روزه داشتن برای شما بهتر است اگر بدانید!'),
(192, 192, 'شَهْرُ رَمَضَانَ الَّذِي أُنْزِلَ فِيهِ الْقُرْآنُ هُدًى لِلنَّاسِ وَبَيِّنَاتٍ مِنَ الْهُدَىٰ وَالْفُرْقَانِ ۚ فَمَنْ شَهِدَ مِنْكُمُ الشَّهْرَ فَلْيَصُمْهُ ۖ وَمَنْ كَانَ مَرِيضًا أَوْ عَلَىٰ سَفَرٍ فَعِدَّةٌ مِنْ أَيَّامٍ أُخَرَ ۗ يُرِيدُ اللَّهُ بِكُمُ الْيُسْرَ وَلَا يُرِيدُ بِكُمُ الْعُسْرَ وَلِتُكْمِلُوا الْعِدَّةَ وَلِتُكَبِّرُوا اللَّهَ عَلَىٰ مَا هَدَاكُمْ وَلَعَلَّكُمْ تَشْكُرُونَ', 2, 185, 2, 6, 28, '(روزه، در چند روز معدودِ) ماهِ رمضان است؛ ماهی که قرآن، برای راهنمایی مردم، و نشانه‌های هدایت، و فرق میان حق و باطل، در آن نازل شده است. پس آن کس از شما که در ماه رمضان در حضر باشد، روزه بدارد! و آن کس که بیمار یا در سفر است، روزهای دیگری را به جای آن، روزه بگیرد! خداوند، راحتی شما را می‌خواهد، نه زحمت شما را! هدف این است که این روزها را تکمیل کنید؛ و خدا را بر اینکه شما را هدایت کرده، بزرگ بشمرید؛ باشد که شکرگزاری کنید!'),
(193, 193, 'وَإِذَا سَأَلَكَ عِبَادِي عَنِّي فَإِنِّي قَرِيبٌ ۖ أُجِيبُ دَعْوَةَ الدَّاعِ إِذَا دَعَانِ ۖ فَلْيَسْتَجِيبُوا لِي وَلْيُؤْمِنُوا بِي لَعَلَّهُمْ يَرْشُدُونَ', 2, 186, 2, 6, 28, 'و هنگامی که بندگان من، از تو در باره من سؤال کنند، (بگو:) من نزدیکم! دعای دعا کننده را، به هنگامی که مرا می‌خواند، پاسخ می‌گویم! پس باید دعوت مرا بپذیرند، و به من ایمان بیاورند، تا راه یابند (و به مقصد برسند)!'),
(194, 194, 'أُحِلَّ لَكُمْ لَيْلَةَ الصِّيَامِ الرَّفَثُ إِلَىٰ نِسَائِكُمْ ۚ هُنَّ لِبَاسٌ لَكُمْ وَأَنْتُمْ لِبَاسٌ لَهُنَّ ۗ عَلِمَ اللَّهُ أَنَّكُمْ كُنْتُمْ تَخْتَانُونَ أَنْفُسَكُمْ فَتَابَ عَلَيْكُمْ وَعَفَا عَنْكُمْ ۖ فَالْآنَ بَاشِرُوهُنَّ وَابْتَغُوا مَا كَتَبَ اللَّهُ لَكُمْ ۚ وَكُلُوا وَاشْرَبُوا حَتَّىٰ يَتَبَيَّنَ لَكُمُ الْخَيْطُ الْأَبْيَضُ مِنَ الْخَيْطِ الْأَسْوَدِ مِنَ الْفَجْرِ ۖ ثُمَّ أَتِمُّوا الصِّيَامَ إِلَى اللَّيْلِ ۚ وَلَا تُبَاشِرُوهُنَّ وَأَنْتُمْ عَاكِفُونَ فِي الْمَسَاجِدِ ۗ تِلْكَ حُدُودُ اللَّهِ فَلَا تَقْرَبُوهَا ۗ كَذَٰلِكَ يُبَيِّنُ اللَّهُ آيَاتِهِ لِلنَّاسِ لَعَلَّهُمْ يَتَّقُونَ', 2, 187, 2, 6, 29, 'آمیزش جنسی با همسرانتان، در شبِ روزهایی که روزه می‌گیرید، حلال است. آنها لباس شما هستند؛ و شما لباس آنها (هر دو زینت هم و سبب حفظ یکدیگرید). خداوند می‌دانست که شما به خود خیانت می‌کردید؛ (و این کارِ ممنوع را انجام می‌دادید؛) پس توبه شما را پذیرفت و شما را بخشید. اکنون با آنها آمیزش کنید، و آنچه را خدا برای شما مقرر داشته، طلب نمایید! و بخورید و بیاشامید، تا رشته سپید صبح، از رشته سیاه (شب) برای شما آشکار گردد! سپس روزه را تا شب، تکمیل کنید! و در حالی که در مساجد به اعتکاف پرداخته‌اید، با زنان آمیزش نکنید! این، مرزهای الهی است؛ پس به آن نزدیک نشوید! خداوند، این چنین آیات خود را برای مردم، روشن می‌سازد، باشد که پرهیزکار گردند!'),
(195, 195, 'وَلَا تَأْكُلُوا أَمْوَالَكُمْ بَيْنَكُمْ بِالْبَاطِلِ وَتُدْلُوا بِهَا إِلَى الْحُكَّامِ لِتَأْكُلُوا فَرِيقًا مِنْ أَمْوَالِ النَّاسِ بِالْإِثْمِ وَأَنْتُمْ تَعْلَمُونَ', 2, 188, 2, 6, 29, 'و اموال یکدیگر را به باطل (و ناحق) در میان خود نخورید! و برای خوردن بخشی از اموال مردم به گناه، (قسمتی از) آن را (به عنوان رشوه) به قضات ندهید، در حالی که می‌دانید (این کار، گناه است)!'),
(196, 196, ' يَسْأَلُونَكَ عَنِ الْأَهِلَّةِ ۖ قُلْ هِيَ مَوَاقِيتُ لِلنَّاسِ وَالْحَجِّ ۗ وَلَيْسَ الْبِرُّ بِأَنْ تَأْتُوا الْبُيُوتَ مِنْ ظُهُورِهَا وَلَٰكِنَّ الْبِرَّ مَنِ اتَّقَىٰ ۗ وَأْتُوا الْبُيُوتَ مِنْ أَبْوَابِهَا ۚ وَاتَّقُوا اللَّهَ لَعَلَّكُمْ تُفْلِحُونَ', 2, 189, 2, 6, 29, 'در باره «هلالهای ماه» از تو سؤال می‌کنند؛ بگو: «آنها، بیان اوقات (و تقویم طبیعی) برای (نظامِ زندگیِ) مردم و (تعیینِ وقتِ) حج است». و (آن چنان که در جاهلیّت مرسوم بود که به هنگام حج، که جامه احرام می‌پوشیدند، از درِ خانه وارد نمی‌شدند، و از نقبِ پشتِ خانه وارد می‌شدند، نکنید!) کارِ نیک، آن نیست که از پشتِ خانه‌ها وارد شوید؛ بلکه نیکی این است که پرهیزگار باشید! و از درِ خانه‌ها وارد شوید و تقوا پیشه کنید، تا رستگار گردید!'),
(197, 197, 'وَقَاتِلُوا فِي سَبِيلِ اللَّهِ الَّذِينَ يُقَاتِلُونَكُمْ وَلَا تَعْتَدُوا ۚ إِنَّ اللَّهَ لَا يُحِبُّ الْمُعْتَدِينَ', 2, 190, 2, 6, 29, 'و در راه خدا، با کسانی که با شما می‌جنگند، نبرد کنید! و از حدّ تجاوز نکنید، که خدا تعدّی‌کنندگان را دوست نمی‌دارد!'),
(198, 198, 'وَاقْتُلُوهُمْ حَيْثُ ثَقِفْتُمُوهُمْ وَأَخْرِجُوهُمْ مِنْ حَيْثُ أَخْرَجُوكُمْ ۚ وَالْفِتْنَةُ أَشَدُّ مِنَ الْقَتْلِ ۚ وَلَا تُقَاتِلُوهُمْ عِنْدَ الْمَسْجِدِ الْحَرَامِ حَتَّىٰ يُقَاتِلُوكُمْ فِيهِ ۖ فَإِنْ قَاتَلُوكُمْ فَاقْتُلُوهُمْ ۗ كَذَٰلِكَ جَزَاءُ الْكَافِرِينَ', 2, 191, 2, 6, 30, 'و آنها را = بت پرستانی که از هیچ گونه جنایتی ابا ندارند هر کجا یافتید، به قتل برسانید! و از آن جا که شما را بیرون ساختند = مکه‌، آنها را بیرون کنید! و فتنه (و بت پرستی) از کشتار هم بدتر است! و با آنها، در نزد مسجد الحرام (در منطقه حرم)، جنگ نکنید! مگر اینکه در آن جا با شما بجنگند. پس اگر (در آن جا) با شما پیکار کردند، آنها را به قتل برسانید! چنین است جزای کافران!'),
(199, 199, 'فَإِنِ انْتَهَوْا فَإِنَّ اللَّهَ غَفُورٌ رَحِيمٌ', 2, 192, 2, 6, 30, 'و اگر خودداری کردند، خداوند آمرزنده و مهربان است.'),
(200, 200, 'وَقَاتِلُوهُمْ حَتَّىٰ لَا تَكُونَ فِتْنَةٌ وَيَكُونَ الدِّينُ لِلَّهِ ۖ فَإِنِ انْتَهَوْا فَلَا عُدْوَانَ إِلَّا عَلَى الظَّالِمِينَ', 2, 193, 2, 6, 30, 'و با آنها پیکار کنید! تا فتنه (و بت پرستی، و سلب آزادی از مردم،) باقی نماند؛ و دین، مخصوص خدا گردد. پس اگر (از روش نادرست خود) دست برداشتند، (مزاحم آنها نشوید! زیرا) تعدّی جز بر ستمکاران روا نیست.'),
(201, 201, 'الشَّهْرُ الْحَرَامُ بِالشَّهْرِ الْحَرَامِ وَالْحُرُمَاتُ قِصَاصٌ ۚ فَمَنِ اعْتَدَىٰ عَلَيْكُمْ فَاعْتَدُوا عَلَيْهِ بِمِثْلِ مَا اعْتَدَىٰ عَلَيْكُمْ ۚ وَاتَّقُوا اللَّهَ وَاعْلَمُوا أَنَّ اللَّهَ مَعَ الْمُتَّقِينَ', 2, 194, 2, 6, 30, 'ماهِ حرام، در برابر ماهِ حرام! (اگر دشمنان، احترام آن را شکستند، و در آن با شما جنگیدند، شما نیز حق دارید مقابله به مثل کنید.) و تمام حرامها، (قابلِ) قصاص است. و (به طور کلّی) هر کس به شما تجاوز کرد، همانند آن بر او تعدّی کنید! و از خدا بپرهیزید (و زیاده روی ننمایید)! و بدانید خدا با پرهیزکاران است!'),
(202, 202, 'وَأَنْفِقُوا فِي سَبِيلِ اللَّهِ وَلَا تُلْقُوا بِأَيْدِيكُمْ إِلَى التَّهْلُكَةِ ۛ وَأَحْسِنُوا ۛ إِنَّ اللَّهَ يُحِبُّ الْمُحْسِنِينَ', 2, 195, 2, 6, 30, 'و در راهِ خدا، انفاق کنید! و (با ترک انفاق،) خود را به دست خود، به هلاکت نیفکنید! و نیکی کنید! که خداوند، نیکوکاران را دوست می‌دارد.'),
(203, 203, 'وَأَتِمُّوا الْحَجَّ وَالْعُمْرَةَ لِلَّهِ ۚ فَإِنْ أُحْصِرْتُمْ فَمَا اسْتَيْسَرَ مِنَ الْهَدْيِ ۖ وَلَا تَحْلِقُوا رُءُوسَكُمْ حَتَّىٰ يَبْلُغَ الْهَدْيُ مَحِلَّهُ ۚ فَمَنْ كَانَ مِنْكُمْ مَرِيضًا أَوْ بِهِ أَذًى مِنْ رَأْسِهِ فَفِدْيَةٌ مِنْ صِيَامٍ أَوْ صَدَقَةٍ أَوْ نُسُكٍ ۚ فَإِذَا أَمِنْتُمْ فَمَنْ تَمَتَّعَ بِالْعُمْرَةِ إِلَى الْحَجِّ فَمَا اسْتَيْسَرَ مِنَ الْهَدْيِ ۚ فَمَنْ لَمْ يَجِدْ فَصِيَامُ ثَلَاثَةِ أَيَّامٍ فِي الْحَجِّ وَسَبْعَةٍ إِذَا رَجَعْتُمْ ۗ تِلْكَ عَشَرَةٌ كَامِلَةٌ ۗ ذَٰلِكَ لِمَنْ لَمْ يَكُنْ أَهْلُهُ حَاضِرِي الْمَسْجِدِ الْحَرَامِ ۚ وَاتَّقُوا اللَّهَ وَاعْلَمُوا أَنَّ اللَّهَ شَدِيدُ الْعِقَابِ', 2, 196, 2, 6, 30, 'و حج و عمره را برای خدا به اتمام برسانید! و اگر محصور شدید، (و مانعی مانند ترس از دشمن یا بیماری، اجازه نداد که پس از احرام‌بستن، وارد مکه شوید،) آنچه از قربانی فراهم شود (ذبح کنید، و از احرام خارج شوید)! و سرهای خود را نتراشید، تا قربانی به محلش برسد (و در قربانگاه ذبح شود)! و اگر کسی از شما بیمار بود، و یا ناراحتی در سر داشت، (و ناچار بود سر خود را بتراشد،) باید فدیه و کفّاره‌ای از قبیل روزه یا صدقه یا گوسفندی بدهد! و هنگامی که (از بیماری و دشمن) در امان بودید، هر کس با ختم عمره، حج را آغاز کند، آنچه از قربانی برای او میسّر است (ذبح کند)! و هر که نیافت، سه روز در ایام حج، و هفت روز هنگامی که باز می‌گردید، روزه بدارد! این، ده روز کامل است. (البته) این برای کسی است که خانواده او، نزد مسجد الحرام نباشد = اهل مکّه و اطرافِ آن نباشد. و از خدا بپرهیزید! و بدانید که او، سخت‌کیفر است!'),
(204, 204, 'الْحَجُّ أَشْهُرٌ مَعْلُومَاتٌ ۚ فَمَنْ فَرَضَ فِيهِنَّ الْحَجَّ فَلَا رَفَثَ وَلَا فُسُوقَ وَلَا جِدَالَ فِي الْحَجِّ ۗ وَمَا تَفْعَلُوا مِنْ خَيْرٍ يَعْلَمْهُ اللَّهُ ۗ وَتَزَوَّدُوا فَإِنَّ خَيْرَ الزَّادِ التَّقْوَىٰ ۚ وَاتَّقُونِ يَا أُولِي الْأَلْبَابِ', 2, 197, 2, 6, 31, 'حج، در ماه‌های معینی است! و کسانی که (با بستن احرام، و شروع به مناسک حج،) حج را بر خود فرض کرده‌اند، (باید بدانند که) در حج، آمیزش جنسی با زنان، و گناه و جدال نیست! و آنچه از کارهای نیک انجام دهید، خدا آن را می‌داند. و زاد و توشه تهیه کنید، که بهترین زاد و توشه، پرهیزکاری است! و از من بپرهیزید ای خردمندان!'),
(205, 205, 'لَيْسَ عَلَيْكُمْ جُنَاحٌ أَنْ تَبْتَغُوا فَضْلًا مِنْ رَبِّكُمْ ۚ فَإِذَا أَفَضْتُمْ مِنْ عَرَفَاتٍ فَاذْكُرُوا اللَّهَ عِنْدَ الْمَشْعَرِ الْحَرَامِ ۖ وَاذْكُرُوهُ كَمَا هَدَاكُمْ وَإِنْ كُنْتُمْ مِنْ قَبْلِهِ لَمِنَ الضَّالِّينَ', 2, 198, 2, 6, 31, 'گناهی بر شما نیست که از فضلِ پروردگارتان (و از منافع اقتصادی در ایّام حج) طلب کنید (که یکی از منافع حج، پی ریزیِ یک اقتصادِ صحیح است). و هنگامی که از «عرفات» کوچ کردید، خدا را نزد «مشعَر الحرام» یاد کنید! او را یاد کنید همان‌طور که شما را هدایت نمود و قطعاً شما پیش از این، از گمراهان بودید.'),
(206, 206, 'ثُمَّ أَفِيضُوا مِنْ حَيْثُ أَفَاضَ النَّاسُ وَاسْتَغْفِرُوا اللَّهَ ۚ إِنَّ اللَّهَ غَفُورٌ رَحِيمٌ', 2, 199, 2, 6, 31, 'سپس از همان‌جا که مردم کوچ می‌کنند، (به سوی سرزمین منی) کوچ کنید! و از خداوند، آمرزش بطلبید، که خدا آمرزنده مهربان است!'),
(207, 207, 'فَإِذَا قَضَيْتُمْ مَنَاسِكَكُمْ فَاذْكُرُوا اللَّهَ كَذِكْرِكُمْ آبَاءَكُمْ أَوْ أَشَدَّ ذِكْرًا ۗ فَمِنَ النَّاسِ مَنْ يَقُولُ رَبَّنَا آتِنَا فِي الدُّنْيَا وَمَا لَهُ فِي الْآخِرَةِ مِنْ خَلَاقٍ', 2, 200, 2, 6, 31, 'و هنگامی که مناسکِ (حج) ِ خود را انجام دادید، خدا را یاد کنید، همانند یادآوری از پدرانتان (آن‌گونه که رسم آن زمان بود) بلکه از آن هم بیشتر! (در این مراسم، مردم دو گروهند:) بعضی از مردم می‌گویند: «خداوندا! به ما در دنیا، (*نیکی*) عطا کن!» ولی در آخرت، بهره‌ای ندارند.'),
(208, 208, 'وَمِنْهُمْ مَنْ يَقُولُ رَبَّنَا آتِنَا فِي الدُّنْيَا حَسَنَةً وَفِي الْآخِرَةِ حَسَنَةً وَقِنَا عَذَابَ النَّارِ', 2, 201, 2, 6, 31, 'و بعضی می‌گویند: «پروردگارا! به ما در دنیا (*نیکی*) عطا کن! و در آخرت نیز (*نیکی*) مرحمت فرما! و ما را از عذابِ آتش نگاه دار!»'),
(209, 209, 'أُولَٰئِكَ لَهُمْ نَصِيبٌ مِمَّا كَسَبُوا ۚ وَاللَّهُ سَرِيعُ الْحِسَابِ', 2, 202, 2, 6, 31, 'آنها از کار (و دعای) خود، نصیب و بهره‌ای دارند؛ و خداوند، سریع الحساب است.'),
(210, 210, ' وَاذْكُرُوا اللَّهَ فِي أَيَّامٍ مَعْدُودَاتٍ ۚ فَمَنْ تَعَجَّلَ فِي يَوْمَيْنِ فَلَا إِثْمَ عَلَيْهِ وَمَنْ تَأَخَّرَ فَلَا إِثْمَ عَلَيْهِ ۚ لِمَنِ اتَّقَىٰ ۗ وَاتَّقُوا اللَّهَ وَاعْلَمُوا أَنَّكُمْ إِلَيْهِ تُحْشَرُونَ', 2, 203, 2, 7, 32, 'و خدا را در روزهای معیّنی یاد کنید! (روزهای 11 و 12 و 13 ماه ذی حجه). و هر کس شتاب کند، (و ذکر خدا را) در دو روز انجام دهد، گناهی بر او نیست، و هر که تأخیر کند، (و سه روز انجام دهد نیز) گناهی بر او نیست؛ برای کسی که تقوا پیشه کند. و از خدا بپرهیزید! و بدانید شما به سوی او محشور خواهید شد!'),
(211, 211, 'وَمِنَ النَّاسِ مَنْ يُعْجِبُكَ قَوْلُهُ فِي الْحَيَاةِ الدُّنْيَا وَيُشْهِدُ اللَّهَ عَلَىٰ مَا فِي قَلْبِهِ وَهُوَ أَلَدُّ الْخِصَامِ', 2, 204, 2, 7, 32, 'و از مردم، کسانی هستند که گفتار آنان، در زندگی دنیا مایه اعجاب تو می‌شود؛ (در ظاهر، اظهار محبّت شدید می‌کنند) و خدا را بر آنچه در دل دارند گواه می‌گیرند. (این در حالی است که) آنان، سرسخت‌ترین دشمنانند.'),
(212, 212, 'وَإِذَا تَوَلَّىٰ سَعَىٰ فِي الْأَرْضِ لِيُفْسِدَ فِيهَا وَيُهْلِكَ الْحَرْثَ وَالنَّسْلَ ۗ وَاللَّهُ لَا يُحِبُّ الْفَسَادَ', 2, 205, 2, 7, 32, '(نشانه آن، این است که) هنگامی که روی برمی‌گردانند (و از نزد تو خارج می‌شوند)، در راه فساد در زمین، کوشش می‌کنند، و زراعتها و چهارپایان را نابود می‌سازند؛ (با اینکه می‌دانند) خدا فساد را دوست نمی‌دارد.'),
(213, 213, 'وَإِذَا قِيلَ لَهُ اتَّقِ اللَّهَ أَخَذَتْهُ الْعِزَّةُ بِالْإِثْمِ ۚ فَحَسْبُهُ جَهَنَّمُ ۚ وَلَبِئْسَ الْمِهَادُ', 2, 206, 2, 7, 32, 'و هنگامی که به آنها گفته شود: «از خدا بترسید!» (لجاجت آنان بیشتر می‌شود)، و لجاجت و تعصب، آنها را به گناه می‌کشاند. آتش دوزخ برای آنان کافی است؛ و چه بد جایگاهی است!'),
(214, 214, 'وَمِنَ النَّاسِ مَنْ يَشْرِي نَفْسَهُ ابْتِغَاءَ مَرْضَاتِ اللَّهِ ۗ وَاللَّهُ رَءُوفٌ بِالْعِبَادِ', 2, 207, 2, 7, 32, 'بعضی از مردم (با ایمان و فداکار، همچون علی (ع) در «لیلة المبیت» به هنگام خفتن در جایگاه پیغمبر ص)، جان خود را به خاطر خشنودی خدا می‌فروشند؛ و خداوند نسبت به بندگان مهربان است.'),
(215, 215, 'يَا أَيُّهَا الَّذِينَ آمَنُوا ادْخُلُوا فِي السِّلْمِ كَافَّةً وَلَا تَتَّبِعُوا خُطُوَاتِ الشَّيْطَانِ ۚ إِنَّهُ لَكُمْ عَدُوٌّ مُبِينٌ', 2, 208, 2, 7, 32, 'ای کسانی که ایمان آورده‌اید همگی در صلح و آشتی درآیید! و از گامهای شیطان، پیروی نکنید؛ که او دشمن آشکار شماست‌'),
(216, 216, 'فَإِنْ زَلَلْتُمْ مِنْ بَعْدِ مَا جَاءَتْكُمُ الْبَيِّنَاتُ فَاعْلَمُوا أَنَّ اللَّهَ عَزِيزٌ حَكِيمٌ', 2, 209, 2, 7, 32, 'و اگر بعد از این همه نشانه‌های روشن، که برای شما آمده است، لغزش کردید (و گمراه شدید)، بدانید (از چنگال عدالت خدا، فرار نتوانید کرد؛) که خداوند، توانا و حکیم است.'),
(217, 217, 'هَلْ يَنْظُرُونَ إِلَّا أَنْ يَأْتِيَهُمُ اللَّهُ فِي ظُلَلٍ مِنَ الْغَمَامِ وَالْمَلَائِكَةُ وَقُضِيَ الْأَمْرُ ۚ وَإِلَى اللَّهِ تُرْجَعُ الْأُمُورُ', 2, 210, 2, 7, 32, 'آیا (پیروان فرمان شیطان، پس از این همه نشانه‌ها و برنامه‌های روشن) انتظار دارند که خداوند و فرشتگان، در سایه‌هائی از ابرها به سوی آنان بیایند (و دلایل تازه‌ای در اختیارشان بگذارند؟! با اینکه چنین چیزی محال است!) و همه چیز انجام شده، و همه کارها به سوی خدا بازمی‌گردد.'),
(218, 218, 'سَلْ بَنِي إِسْرَائِيلَ كَمْ آتَيْنَاهُمْ مِنْ آيَةٍ بَيِّنَةٍ ۗ وَمَنْ يُبَدِّلْ نِعْمَةَ اللَّهِ مِنْ بَعْدِ مَا جَاءَتْهُ فَإِنَّ اللَّهَ شَدِيدُ الْعِقَابِ', 2, 211, 2, 7, 33, 'از بنی اسرائیل بپرس: «چه اندازه نشانه‌های روشن به آنها دادیم؟» (ولی آنان، نعمتها و امکانات مادی و معنوی را که خداوند در اختیارشان گذاشته بود، در راه غلط به کار گرفتند.) و کسی که نعمت خدا را، پس از آن که به سراغش آمد، تبدیل کند (و در مسیر خلاف به کار گیرد، گرفتار عذاب شدید الهی خواهد شد) که خداوند شدید العقاب است.'),
(219, 219, 'زُيِّنَ لِلَّذِينَ كَفَرُوا الْحَيَاةُ الدُّنْيَا وَيَسْخَرُونَ مِنَ الَّذِينَ آمَنُوا ۘ وَالَّذِينَ اتَّقَوْا فَوْقَهُمْ يَوْمَ الْقِيَامَةِ ۗ وَاللَّهُ يَرْزُقُ مَنْ يَشَاءُ بِغَيْرِ حِسَابٍ', 2, 212, 2, 7, 33, 'زندگی دنیا برای کافران زینت داده شده است، از این‌رو افراد باایمان را (که گاهی دستشان تهی است)، مسخره می‌کنند؛ در حالی که پرهیزگاران در قیامت، بالاتر از آنان هستند؛ (چراکه ارزشهای حقیقی در آنجا آشکار می‌گردد، و صورت عینی به خود می‌گیرد؛) و خداوند، هر کس را بخواهد بدون حساب روزی می‌دهد.'),
(220, 220, 'كَانَ النَّاسُ أُمَّةً وَاحِدَةً فَبَعَثَ اللَّهُ النَّبِيِّينَ مُبَشِّرِينَ وَمُنْذِرِينَ وَأَنْزَلَ مَعَهُمُ الْكِتَابَ بِالْحَقِّ لِيَحْكُمَ بَيْنَ النَّاسِ فِيمَا اخْتَلَفُوا فِيهِ ۚ وَمَا اخْتَلَفَ فِيهِ إِلَّا الَّذِينَ أُوتُوهُ مِنْ بَعْدِ مَا جَاءَتْهُمُ الْبَيِّنَاتُ بَغْيًا بَيْنَهُمْ ۖ فَهَدَى اللَّهُ الَّذِينَ آمَنُوا لِمَا اخْتَلَفُوا فِيهِ مِنَ الْحَقِّ بِإِذْنِهِ ۗ وَاللَّهُ يَهْدِي مَنْ يَشَاءُ إِلَىٰ صِرَاطٍ مُسْتَقِيمٍ', 2, 213, 2, 7, 33, 'مردم (در آغاز) یک دسته بودند؛ (و تضادی در میان آنها وجود نداشت. بتدریج جوامع و طبقات پدید آمد و اختلافات و تضادهایی در میان آنها پیدا شد، در این حال) خداوند، پیامبران را برانگیخت؛ تا مردم را بشارت و بیم دهند و کتاب آسمانی، که به سوی حق دعوت می‌کرد، با آنها نازل نمود؛ تا در میان مردم، در آنچه اختلاف داشتند، داوری کند. (افراد باایمان، در آن اختلاف نکردند؛) تنها (گروهی از) کسانی که کتاب را دریافت داشته بودند، و نشانه‌های روشن به آنها رسیده بود، به خاطر انحراف از حق و ستمگری، در آن اختلاف کردند. خداوند، آنهایی را که ایمان آورده بودند، به حقیقت آنچه مورد اختلاف بود، به فرمان خودش، رهبری نمود. (امّا افراد بی‌ایمان، همچنان در گمراهی و اختلاف، باقی ماندند.) و خدا، هر کس را بخواهد، به راه راست هدایت می‌کند.'),
(221, 221, 'أَمْ حَسِبْتُمْ أَنْ تَدْخُلُوا الْجَنَّةَ وَلَمَّا يَأْتِكُمْ مَثَلُ الَّذِينَ خَلَوْا مِنْ قَبْلِكُمْ ۖ مَسَّتْهُمُ الْبَأْسَاءُ وَالضَّرَّاءُ وَزُلْزِلُوا حَتَّىٰ يَقُولَ الرَّسُولُ وَالَّذِينَ آمَنُوا مَعَهُ مَتَىٰ نَصْرُ اللَّهِ ۗ أَلَا إِنَّ نَصْرَ اللَّهِ قَرِيبٌ', 2, 214, 2, 7, 33, 'آیا گمان کردید داخل بهشت می‌شوید، بی‌آنکه حوادثی همچون حوادث گذشتگان به شما برسد؟! همانان که گرفتاریها و ناراحتیها به آنها رسید، و آن چنان ناراحت شدند که پیامبر و افرادی که ایمان آورده بودند گفتند: «پس یاری خدا کی خواهد آمد؟!» (در این هنگام، تقاضای یاری از او کردند، و به آنها گفته شد:) آگاه باشید، یاریِ خدا نزدیک است!'),
(222, 222, 'يَسْأَلُونَكَ مَاذَا يُنْفِقُونَ ۖ قُلْ مَا أَنْفَقْتُمْ مِنْ خَيْرٍ فَلِلْوَالِدَيْنِ وَالْأَقْرَبِينَ وَالْيَتَامَىٰ وَالْمَسَاكِينِ وَابْنِ السَّبِيلِ ۗ وَمَا تَفْعَلُوا مِنْ خَيْرٍ فَإِنَّ اللَّهَ بِهِ عَلِيمٌ', 2, 215, 2, 7, 33, 'از تو سؤال می‌کنند چه چیز انفاق کنند؟ بگو: «هر خیر و نیکی (و سرمایه سودمند مادی و معنوی) که انفاق می‌کنید، باید برای پدر و مادر و نزدیکان و یتیمان و مستمندان و درماندگان در راه باشد.» و هر کار خیری که انجام دهید، خداوند از آن آگاه است. (لازم نیست تظاهر کنید، او می‌داند).'),
(223, 223, 'كُتِبَ عَلَيْكُمُ الْقِتَالُ وَهُوَ كُرْهٌ لَكُمْ ۖ وَعَسَىٰ أَنْ تَكْرَهُوا شَيْئًا وَهُوَ خَيْرٌ لَكُمْ ۖ وَعَسَىٰ أَنْ تُحِبُّوا شَيْئًا وَهُوَ شَرٌّ لَكُمْ ۗ وَاللَّهُ يَعْلَمُ وَأَنْتُمْ لَا تَعْلَمُونَ', 2, 216, 2, 7, 34, 'جهاد در راه خدا، بر شما مقرّر شد؛ در حالی که برایتان ناخوشایند است. چه بسا چیزی را خوش نداشته باشید، حال آن که خیرِ شما در آن است. و یا چیزی را دوست داشته باشید، حال آنکه شرِّ شما در آن است. و خدا می‌داند، و شما نمی‌دانید.'),
(224, 224, 'يَسْأَلُونَكَ عَنِ الشَّهْرِ الْحَرَامِ قِتَالٍ فِيهِ ۖ قُلْ قِتَالٌ فِيهِ كَبِيرٌ ۖ وَصَدٌّ عَنْ سَبِيلِ اللَّهِ وَكُفْرٌ بِهِ وَالْمَسْجِدِ الْحَرَامِ وَإِخْرَاجُ أَهْلِهِ مِنْهُ أَكْبَرُ عِنْدَ اللَّهِ ۚ وَالْفِتْنَةُ أَكْبَرُ مِنَ الْقَتْلِ ۗ وَلَا يَزَالُونَ يُقَاتِلُونَكُمْ حَتَّىٰ يَرُدُّوكُمْ عَنْ دِينِكُمْ إِنِ اسْتَطَاعُوا ۚ وَمَنْ يَرْتَدِدْ مِنْكُمْ عَنْ دِينِهِ فَيَمُتْ وَهُوَ كَافِرٌ فَأُولَٰئِكَ حَبِطَتْ أَعْمَالُهُمْ فِي الدُّنْيَا وَالْآخِرَةِ ۖ وَأُولَٰئِكَ أَصْحَابُ النَّارِ ۖ هُمْ فِيهَا خَالِدُونَ', 2, 217, 2, 7, 34, 'از تو، در باره جنگ کردن در ماه حرام، سؤال می‌کنند؛ بگو: «جنگ در آن، (گناهی) بزرگ است؛ ولی جلوگیری از راه خدا (و گرایش مردم به آیین حق) و کفر ورزیدن نسبت به او و هتک احترام مسجد الحرام، و اخراج ساکنان آن، نزد خداوند مهمتر از آن است؛ و ایجاد فتنه، (و محیط نامساعد، که مردم را به کفر، تشویق و از ایمان بازمی‌دارد) حتّی از قتل بالاتر است. و مشرکان، پیوسته با شما می‌جنگند، تا اگر بتوانند شما را از آیینتان برگردانند؛ ولی کسی که از آیینش برگردد، و در حال کفر بمیرد، تمام اعمال نیک (گذشته) او، در دنیا و آخرت، برباد می‌رود؛ و آنان اهل دوزخند؛ و همیشه در آن خواهند بود.'),
(225, 225, 'إِنَّ الَّذِينَ آمَنُوا وَالَّذِينَ هَاجَرُوا وَجَاهَدُوا فِي سَبِيلِ اللَّهِ أُولَٰئِكَ يَرْجُونَ رَحْمَتَ اللَّهِ ۚ وَاللَّهُ غَفُورٌ رَحِيمٌ', 2, 218, 2, 7, 34, 'کسانی که ایمان آورده و کسانی که هجرت کرده و در راه خدا جهاد نموده‌اند، آنها امید به رحمت پروردگار دارند و خداوند آمرزنده و مهربان است.'),
(226, 226, ' يَسْأَلُونَكَ عَنِ الْخَمْرِ وَالْمَيْسِرِ ۖ قُلْ فِيهِمَا إِثْمٌ كَبِيرٌ وَمَنَافِعُ لِلنَّاسِ وَإِثْمُهُمَا أَكْبَرُ مِنْ نَفْعِهِمَا ۗ وَيَسْأَلُونَكَ مَاذَا يُنْفِقُونَ قُلِ الْعَفْوَ ۗ كَذَٰلِكَ يُبَيِّنُ اللَّهُ لَكُمُ الْآيَاتِ لَعَلَّكُمْ تَتَفَكَّرُونَ', 2, 219, 2, 7, 34, 'در باره شراب و قمار از تو سؤال می‌کنند، بگو: «در آنها گناه و زیان بزرگی است؛ و منافعی (از نظر مادی) برای مردم در بردارد؛ (ولی) گناه آنها از نفعشان بیشتر است. و از تو می‌پرسند چه چیز انفاق کنند؟ بگو: از مازاد نیازمندی خود.» اینچنین خداوند آیات را برای شما روشن می‌سازد، شاید اندیشه کنید!'),
(227, 227, 'فِي الدُّنْيَا وَالْآخِرَةِ ۗ وَيَسْأَلُونَكَ عَنِ الْيَتَامَىٰ ۖ قُلْ إِصْلَاحٌ لَهُمْ خَيْرٌ ۖ وَإِنْ تُخَالِطُوهُمْ فَإِخْوَانُكُمْ ۚ وَاللَّهُ يَعْلَمُ الْمُفْسِدَ مِنَ الْمُصْلِحِ ۚ وَلَوْ شَاءَ اللَّهُ لَأَعْنَتَكُمْ ۚ إِنَّ اللَّهَ عَزِيزٌ حَكِيمٌ', 2, 220, 2, 7, 35, '(تا اندیشه کنید) درباره دنیا و آخرت! و از تو در باره یتیمان سؤال می‌کنند، بگو: «اصلاح کار آنان بهتر است. و اگر زندگی خود را با زندگی آنان بیامیزید، (مانعی ندارد؛) آنها برادر (دینی) شما هستند.» (و همچون یک برادر با آنها رفتار کنید!) خداوند، مفسدان را از مصلحان، بازمی‌شناسد. و اگر خدا بخواهد، شما را به زحمت می‌اندازد؛ (و دستور می‌دهد در عین سرپرستی یتیمان، زندگی و اموال آنها را بکلی از اموال خود، جدا سازید؛ ولی خداوند چنین نمی‌کند؛) زیرا او توانا و حکیم است.'),
(228, 228, 'وَلَا تَنْكِحُوا الْمُشْرِكَاتِ حَتَّىٰ يُؤْمِنَّ ۚ وَلَأَمَةٌ مُؤْمِنَةٌ خَيْرٌ مِنْ مُشْرِكَةٍ وَلَوْ أَعْجَبَتْكُمْ ۗ وَلَا تُنْكِحُوا الْمُشْرِكِينَ حَتَّىٰ يُؤْمِنُوا ۚ وَلَعَبْدٌ مُؤْمِنٌ خَيْرٌ مِنْ مُشْرِكٍ وَلَوْ أَعْجَبَكُمْ ۗ أُولَٰئِكَ يَدْعُونَ إِلَى النَّارِ ۖ وَاللَّهُ يَدْعُو إِلَى الْجَنَّةِ وَالْمَغْفِرَةِ بِإِذْنِهِ ۖ وَيُبَيِّنُ آيَاتِهِ لِلنَّاسِ لَعَلَّهُمْ يَتَذَكَّرُونَ', 2, 221, 2, 7, 35, 'و با زنان مشرک و بت‌پرست، تا ایمان نیاورده‌اند، ازدواج نکنید! (اگر چه جز به ازدواج با کنیزان، دسترسی نداشته باشید؛ زیرا) کنیز باایمان، از زن آزاد بت‌پرست، بهتر است؛ هر چند (زیبایی، یا ثروت، یا موقعیت او) شما را به شگفتی آورد. و زنان خود را به ازدواج مردان بت‌پرست، تا ایمان نیاورده‌اند، در نیاورید! (اگر چه ناچار شوید آنها را به همسری غلامان باایمان درآورید؛ زیرا) یک غلام باایمان، از یک مرد آزاد بت‌پرست، بهتر است؛ هر چند (مال و موقعیت و زیبایی او،) شما را به شگفتی آورد. آنها دعوت به سوی آتش می‌کنند؛ و خدا دعوت به بهشت و آمرزش به فرمان خود می‌نماید، و آیات خویش را برای مردم روشن می‌سازد؛ شاید متذکر شوند!'),
(229, 229, 'وَيَسْأَلُونَكَ عَنِ الْمَحِيضِ ۖ قُلْ هُوَ أَذًى فَاعْتَزِلُوا النِّسَاءَ فِي الْمَحِيضِ ۖ وَلَا تَقْرَبُوهُنَّ حَتَّىٰ يَطْهُرْنَ ۖ فَإِذَا تَطَهَّرْنَ فَأْتُوهُنَّ مِنْ حَيْثُ أَمَرَكُمُ اللَّهُ ۚ إِنَّ اللَّهَ يُحِبُّ التَّوَّابِينَ وَيُحِبُّ الْمُتَطَهِّرِينَ', 2, 222, 2, 7, 35, 'و از تو، در باره خون حیض سؤال می‌کنند، بگو: «چیز زیانبار و آلوده‌ای است؛ از این‌رو در حالت قاعدگی، از آنان کناره‌گیری کنید! و با آنها نزدیکی ننماید، تا پاک شوند! و هنگامی که پاک شدند، از طریقی که خدا به شما فرمان داده، با آنها آمیزش کنید! خداوند، توبه‌کنندگان را دوست دارد، و پاکان را (نیز) دوست دارد.'),
(230, 230, 'نِسَاؤُكُمْ حَرْثٌ لَكُمْ فَأْتُوا حَرْثَكُمْ أَنَّىٰ شِئْتُمْ ۖ وَقَدِّمُوا لِأَنْفُسِكُمْ ۚ وَاتَّقُوا اللَّهَ وَاعْلَمُوا أَنَّكُمْ مُلَاقُوهُ ۗ وَبَشِّرِ الْمُؤْمِنِينَ', 2, 223, 2, 7, 35, 'زنان شما، محل بذرافشانی شما هستند؛ پس هر زمان که بخواهید، می‌توانید با آنها آمیزش کنید. و (سعی نمائید از این فرصت بهره گرفته، با پرورش فرزندان صالح) اثر نیکی برای خود، از پیش بفرستید! و از خدا بپرهیزید و بدانید او را ملاقات خواهید کرد و به مؤمنان، بشارت ده!'),
(231, 231, 'وَلَا تَجْعَلُوا اللَّهَ عُرْضَةً لِأَيْمَانِكُمْ أَنْ تَبَرُّوا وَتَتَّقُوا وَتُصْلِحُوا بَيْنَ النَّاسِ ۗ وَاللَّهُ سَمِيعٌ عَلِيمٌ', 2, 224, 2, 7, 35, 'خدا را در معرض سوگندهای خود قرار ندهید! و برای اینکه نیکی کنید، و تقوا پیشه سازید، و در میان مردم اصلاح کنید (سوگند یاد ننمایید)! و خداوند شنوا و داناست.'),
(232, 232, 'لَا يُؤَاخِذُكُمُ اللَّهُ بِاللَّغْوِ فِي أَيْمَانِكُمْ وَلَٰكِنْ يُؤَاخِذُكُمْ بِمَا كَسَبَتْ قُلُوبُكُمْ ۗ وَاللَّهُ غَفُورٌ حَلِيمٌ', 2, 225, 2, 7, 36, 'خداوند شما را به خاطر سوگندهایی که بدون توجه یاد می‌کنید، مؤاخذه نخواهد کرد، اما به آنچه دلهای شما کسب کرده، (و سوگندهایی که از روی اراده و اختیار، یاد می‌کنید،) مؤاخذه می‌کند. و خداوند، آمرزنده و بردبار است.'),
(233, 233, 'لِلَّذِينَ يُؤْلُونَ مِنْ نِسَائِهِمْ تَرَبُّصُ أَرْبَعَةِ أَشْهُرٍ ۖ فَإِنْ فَاءُوا فَإِنَّ اللَّهَ غَفُورٌ رَحِيمٌ', 2, 226, 2, 7, 36, 'کسانی که زنان خود را «ایلاء» می‌نمایند = سوگند یاد می‌کنند که با آنها، آمیزش‌جنسی ننمایند، حق دارند چهار ماه انتظار بکشند. (و در ضمن این چهار ماه، وضع خود را با همسر خویش، از نظر ادامه زندگی یا طلاق، روشن سازند.) اگر (در این فرصت،) بازگشت کنند، (چیزی بر آنها نیست؛ زیرا) خداوند، آمرزنده و مهربان است.'),
(234, 234, 'وَإِنْ عَزَمُوا الطَّلَاقَ فَإِنَّ اللَّهَ سَمِيعٌ عَلِيمٌ', 2, 227, 2, 7, 36, 'و اگر تصمیم به جدایی گرفتند، (آن هم با شرایطش مانعی ندارد؛) خداوند شنوا و داناست.'),
(235, 235, 'وَالْمُطَلَّقَاتُ يَتَرَبَّصْنَ بِأَنْفُسِهِنَّ ثَلَاثَةَ قُرُوءٍ ۚ وَلَا يَحِلُّ لَهُنَّ أَنْ يَكْتُمْنَ مَا خَلَقَ اللَّهُ فِي أَرْحَامِهِنَّ إِنْ كُنَّ يُؤْمِنَّ بِاللَّهِ وَالْيَوْمِ الْآخِرِ ۚ وَبُعُولَتُهُنَّ أَحَقُّ بِرَدِّهِنَّ فِي ذَٰلِكَ إِنْ أَرَادُوا إِصْلَاحًا ۚ وَلَهُنَّ مِثْلُ الَّذِي عَلَيْهِنَّ بِالْمَعْرُوفِ ۚ وَلِلرِّجَالِ عَلَيْهِنَّ دَرَجَةٌ ۗ وَاللَّهُ عَزِيزٌ حَكِيمٌ', 2, 228, 2, 7, 36, 'زنان مطلقه، باید به مدت سه مرتبه عادت ماهانه دیدن (و پاک شدن) انتظار بکشند! = عده نگه دارند و اگر به خدا و روز رستاخیز، ایمان دارند، برای آنها حلال نیست که آنچه را خدا در رحمهایشان آفریده، کتمان کنند. و همسرانشان، برای بازگرداندن آنها (و از سرگرفتن زندگی زناشویی) در این مدت، (از دیگران) سزاوارترند؛ در صورتی که (براستی) خواهان اصلاح باشند. و برای آنان، همانند وظایفی که بر دوش آنهاست، حقوق شایسته‌ای قرار داده شده؛ و مردان بر آنان برتری دارند؛ و خداوند توانا و حکیم است.'),
(236, 236, 'الطَّلَاقُ مَرَّتَانِ ۖ فَإِمْسَاكٌ بِمَعْرُوفٍ أَوْ تَسْرِيحٌ بِإِحْسَانٍ ۗ وَلَا يَحِلُّ لَكُمْ أَنْ تَأْخُذُوا مِمَّا آتَيْتُمُوهُنَّ شَيْئًا إِلَّا أَنْ يَخَافَا أَلَّا يُقِيمَا حُدُودَ اللَّهِ ۖ فَإِنْ خِفْتُمْ أَلَّا يُقِيمَا حُدُودَ اللَّهِ فَلَا جُنَاحَ عَلَيْهِمَا فِيمَا افْتَدَتْ بِهِ ۗ تِلْكَ حُدُودُ اللَّهِ فَلَا تَعْتَدُوهَا ۚ وَمَنْ يَتَعَدَّ حُدُودَ اللَّهِ فَأُولَٰئِكَ هُمُ الظَّالِمُونَ', 2, 229, 2, 7, 36, 'طلاق، (طلاقی که رجوع و بازگشت دارد،) دو مرتبه است؛ (و در هر مرتبه،) باید به طور شایسته همسر خود را نگاهداری کند (و آشتی نماید)، یا با نیکی او را رها سازد (و از او جدا شود). و برای شما حلال نیست که چیزی از آنچه به آنها داده‌اید، پس بگیرید؛ مگر اینکه دو همسر، بترسند که حدود الهی را برپا ندارند. اگر بترسید که حدود الهی را رعایت نکنند، مانعی برای آنها نیست که زن، فدیه و عوضی بپردازد (و طلاق بگیرد). اینها حدود و مرزهای الهی است؛ از آن، تجاوز نکنید! و هر کس از آن تجاوز کند، ستمگر است.');
INSERT INTO `Quran` (`rowid`, `id`, `Arabic`, `Chapter`, `Verse`, `Juz`, `Hizb`, `Page`, `tr`) VALUES
(237, 237, 'فَإِنْ طَلَّقَهَا فَلَا تَحِلُّ لَهُ مِنْ بَعْدُ حَتَّىٰ تَنْكِحَ زَوْجًا غَيْرَهُ ۗ فَإِنْ طَلَّقَهَا فَلَا جُنَاحَ عَلَيْهِمَا أَنْ يَتَرَاجَعَا إِنْ ظَنَّا أَنْ يُقِيمَا حُدُودَ اللَّهِ ۗ وَتِلْكَ حُدُودُ اللَّهِ يُبَيِّنُهَا لِقَوْمٍ يَعْلَمُونَ', 2, 230, 2, 7, 36, 'اگر (بعد از دو طلاق و رجوع، بار دیگر) او را طلاق داد، از آن به بعد، زن بر او حلال نخواهد بود؛ مگر اینکه همسر دیگری انتخاب کند (و با او، آمیزش‌جنسی نماید. در این صورت،) اگر (همسر دوم) او را طلاق گفت، گناهی ندارد که بازگشت کنند؛ (و با همسر اول، دوباره ازدواج نماید؛) در صورتی که امید داشته باشند که حدود الهی را محترم میشمرند. اینها حدود الهی است که (خدا) آن را برای گروهی که آگاهند، بیان می‌نماید.'),
(238, 238, 'وَإِذَا طَلَّقْتُمُ النِّسَاءَ فَبَلَغْنَ أَجَلَهُنَّ فَأَمْسِكُوهُنَّ بِمَعْرُوفٍ أَوْ سَرِّحُوهُنَّ بِمَعْرُوفٍ ۚ وَلَا تُمْسِكُوهُنَّ ضِرَارًا لِتَعْتَدُوا ۚ وَمَنْ يَفْعَلْ ذَٰلِكَ فَقَدْ ظَلَمَ نَفْسَهُ ۚ وَلَا تَتَّخِذُوا آيَاتِ اللَّهِ هُزُوًا ۚ وَاذْكُرُوا نِعْمَتَ اللَّهِ عَلَيْكُمْ وَمَا أَنْزَلَ عَلَيْكُمْ مِنَ الْكِتَابِ وَالْحِكْمَةِ يَعِظُكُمْ بِهِ ۚ وَاتَّقُوا اللَّهَ وَاعْلَمُوا أَنَّ اللَّهَ بِكُلِّ شَيْءٍ عَلِيمٌ', 2, 231, 2, 7, 37, 'و هنگامی که زنان را طلاق دادید، و به آخرین روزهای «عدّه» رسیدند، یا به طرز صحیحی آنها را نگاه دارید (و آشتی کنید)، و یا به طرز پسندیده‌ای آنها را رها سازید! و هیچ‌گاه به خاطر زیان رساندن و تعدّی کردن، آنها را نگاه ندارید! و کسی که چنین کند، به خویشتن ستم کرده است. (و با این اعمال، و سوء استفاده از قوانین الهی،) آیات خدا را به استهزا نگیرید! و به یاد بیاورید نعمت خدا را بر خود، و کتاب آسمانی و علم و دانشی که بر شما نازل کرده، و شما را با آن، پند می‌دهد! و از خدا بپرهیزید! و بدانید خداوند از هر چیزی آگاه است (و از نیات کسانی که از قوانین او، سوء استفاده می‌کنند، با خبر است)!'),
(239, 239, 'وَإِذَا طَلَّقْتُمُ النِّسَاءَ فَبَلَغْنَ أَجَلَهُنَّ فَلَا تَعْضُلُوهُنَّ أَنْ يَنْكِحْنَ أَزْوَاجَهُنَّ إِذَا تَرَاضَوْا بَيْنَهُمْ بِالْمَعْرُوفِ ۗ ذَٰلِكَ يُوعَظُ بِهِ مَنْ كَانَ مِنْكُمْ يُؤْمِنُ بِاللَّهِ وَالْيَوْمِ الْآخِرِ ۗ ذَٰلِكُمْ أَزْكَىٰ لَكُمْ وَأَطْهَرُ ۗ وَاللَّهُ يَعْلَمُ وَأَنْتُمْ لَا تَعْلَمُونَ', 2, 232, 2, 7, 37, 'و هنگامی که زنان را طلاق دادید و عدّه خود را به پایان رساندند، مانع آنها نشوید که با همسران (سابق) خویش، ازدواج کنند! اگر در میان آنان، به طرز پسندیده‌ای تراضی برقرار گردد. این دستوری است که تنها افرادی از شما، که ایمان به خدا و روز قیامت دارند، از آن، پند می‌گیرند (و به آن، عمل می‌کنند). این (دستور)، برای رشد (خانواده‌های) شما مؤثرتر، و برای شستن آلودگیها مفیدتر است؛ و خدا می‌داند و شما نمی‌دانید.'),
(240, 240, ' وَالْوَالِدَاتُ يُرْضِعْنَ أَوْلَادَهُنَّ حَوْلَيْنِ كَامِلَيْنِ ۖ لِمَنْ أَرَادَ أَنْ يُتِمَّ الرَّضَاعَةَ ۚ وَعَلَى الْمَوْلُودِ لَهُ رِزْقُهُنَّ وَكِسْوَتُهُنَّ بِالْمَعْرُوفِ ۚ لَا تُكَلَّفُ نَفْسٌ إِلَّا وُسْعَهَا ۚ لَا تُضَارَّ وَالِدَةٌ بِوَلَدِهَا وَلَا مَوْلُودٌ لَهُ بِوَلَدِهِ ۚ وَعَلَى الْوَارِثِ مِثْلُ ذَٰلِكَ ۗ فَإِنْ أَرَادَا فِصَالًا عَنْ تَرَاضٍ مِنْهُمَا وَتَشَاوُرٍ فَلَا جُنَاحَ عَلَيْهِمَا ۗ وَإِنْ أَرَدْتُمْ أَنْ تَسْتَرْضِعُوا أَوْلَادَكُمْ فَلَا جُنَاحَ عَلَيْكُمْ إِذَا سَلَّمْتُمْ مَا آتَيْتُمْ بِالْمَعْرُوفِ ۗ وَاتَّقُوا اللَّهَ وَاعْلَمُوا أَنَّ اللَّهَ بِمَا تَعْمَلُونَ بَصِيرٌ', 2, 233, 2, 8, 37, 'مادران، فرزندان خود را دو سال تمام، شیر می‌دهند. (این) برای کسی است که بخواهد دوران شیرخوارگی را تکمیل کند. و بر آن کس که فرزند برای او متولّد شده = پدر، لازم است خوراک و پوشاک مادر را به طور شایسته (در مدت شیر دادن بپردازد؛ حتی اگر طلاق گرفته باشد.) هیچ کس موظّف به بیش از مقدار توانایی خود نیست! نه مادر (به خاطر اختلاف با پدر) حق ضرر زدن به کودک را دارد، و نه پدر. و بر وارث او نیز لازم است این کار را انجام دهد = هزینه مادر را در دوران شیرخوارگی تأمین نماید. و اگر آن دو، با رضایت یکدیگر و مشورت، بخواهند کودک را (زودتر) از شیر بازگیرند، گناهی بر آنها نیست. و اگر (با عدم توانایی، یا عدم موافقت مادر) خواستید دایه‌ای برای فرزندان خود بگیرید، گناهی بر شما نیست؛ به شرط اینکه حق گذشته مادر را به طور شایسته بپردازید. و از (مخالفت فرمان) خدا بپرهیزید! و بدانید خدا، به آنچه انجام می‌دهید، بیناست!'),
(241, 241, 'وَالَّذِينَ يُتَوَفَّوْنَ مِنْكُمْ وَيَذَرُونَ أَزْوَاجًا يَتَرَبَّصْنَ بِأَنْفُسِهِنَّ أَرْبَعَةَ أَشْهُرٍ وَعَشْرًا ۖ فَإِذَا بَلَغْنَ أَجَلَهُنَّ فَلَا جُنَاحَ عَلَيْكُمْ فِيمَا فَعَلْنَ فِي أَنْفُسِهِنَّ بِالْمَعْرُوفِ ۗ وَاللَّهُ بِمَا تَعْمَلُونَ خَبِيرٌ', 2, 234, 2, 8, 38, 'و کسانی که از شما می‌میرند و همسرانی باقی می‌گذارند، باید چهار ماه و ده روز، انتظار بکشند (و عدّه نگه دارند)! و هنگامی که به آخر مدتشان رسیدند، گناهی بر شما نیست که هر چه می‌خواهند، در باره خودشان به طور شایسته انجام دهند (و با مرد دلخواه خود، ازدواج کنند). و خدا به آنچه عمل می‌کنید، آگاه است.'),
(242, 242, 'وَلَا جُنَاحَ عَلَيْكُمْ فِيمَا عَرَّضْتُمْ بِهِ مِنْ خِطْبَةِ النِّسَاءِ أَوْ أَكْنَنْتُمْ فِي أَنْفُسِكُمْ ۚ عَلِمَ اللَّهُ أَنَّكُمْ سَتَذْكُرُونَهُنَّ وَلَٰكِنْ لَا تُوَاعِدُوهُنَّ سِرًّا إِلَّا أَنْ تَقُولُوا قَوْلًا مَعْرُوفًا ۚ وَلَا تَعْزِمُوا عُقْدَةَ النِّكَاحِ حَتَّىٰ يَبْلُغَ الْكِتَابُ أَجَلَهُ ۚ وَاعْلَمُوا أَنَّ اللَّهَ يَعْلَمُ مَا فِي أَنْفُسِكُمْ فَاحْذَرُوهُ ۚ وَاعْلَمُوا أَنَّ اللَّهَ غَفُورٌ حَلِيمٌ', 2, 235, 2, 8, 38, 'و گناهی بر شما نیست که به طور کنایه، (از زنانی که همسرانشان مرده‌اند) خواستگاری کنید، و یا در دل تصمیم بر این کار بگیرید (بدون اینکه آن را اظهار کنید). خداوند می‌دانست شما به یاد آنها خواهید افتاد؛ (و با خواسته طبیعی شما به شکل معقول، مخالف نیست؛) ولی پنهانی با آنها قرار زناشویی نگذارید، مگر اینکه به طرز پسندیده‌ای (به طور کنایه) اظهار کنید! (ولی در هر حال،) اقدام به ازدواج ننمایید، تا عدّه آنها سرآید! و بدانید خداوند آنچه را در دل دارید، می‌داند! از مخالفت او بپرهیزید! و بدانید خداوند، آمرزنده و بردبار است (و در مجازات بندگان، عجله نمی‌کند)!'),
(243, 243, 'لَا جُنَاحَ عَلَيْكُمْ إِنْ طَلَّقْتُمُ النِّسَاءَ مَا لَمْ تَمَسُّوهُنَّ أَوْ تَفْرِضُوا لَهُنَّ فَرِيضَةً ۚ وَمَتِّعُوهُنَّ عَلَى الْمُوسِعِ قَدَرُهُ وَعَلَى الْمُقْتِرِ قَدَرُهُ مَتَاعًا بِالْمَعْرُوفِ ۖ حَقًّا عَلَى الْمُحْسِنِينَ', 2, 236, 2, 8, 38, 'اگر زنان را قبل از آمیزش‌جنسی یا تعیین مهر، (به عللی) طلاق دهید، گناهی بر شما نیست. (و در این موقع،) آنها را (با هدیه‌ای مناسب،) بهره‌مند سازید! آن کس که توانایی دارد، به اندازه تواناییش، و آن کس که تنگدست است، به اندازه خودش، هدیه‌ای شایسته (که مناسب حال دهنده و گیرنده باشد) بدهد! و این بر نیکوکاران، الزامی است.'),
(244, 244, 'وَإِنْ طَلَّقْتُمُوهُنَّ مِنْ قَبْلِ أَنْ تَمَسُّوهُنَّ وَقَدْ فَرَضْتُمْ لَهُنَّ فَرِيضَةً فَنِصْفُ مَا فَرَضْتُمْ إِلَّا أَنْ يَعْفُونَ أَوْ يَعْفُوَ الَّذِي بِيَدِهِ عُقْدَةُ النِّكَاحِ ۚ وَأَنْ تَعْفُوا أَقْرَبُ لِلتَّقْوَىٰ ۚ وَلَا تَنْسَوُا الْفَضْلَ بَيْنَكُمْ ۚ إِنَّ اللَّهَ بِمَا تَعْمَلُونَ بَصِيرٌ', 2, 237, 2, 8, 38, 'و اگر آنان را، پیش از آن که با آنها تماس بگیرید و (آمیزش‌جنسی کنید) طلاق دهید، در حالی که مهری برای آنها تعیین کرده‌اید، (لازم است) نصف آنچه را تعیین کرده‌اید (به آنها بدهید) مگر اینکه آنها (حق خود را) ببخشند؛ یا (در صورتی که صغیر و سفیه باشند، ولیّ آنها، یعنی) آن کس که گره ازدواج به دست اوست، آن را ببخشد. و گذشت کردن شما (و بخشیدن تمام مهر به آنها) به پرهیزکاری نزدیکتر است، و گذشت و نیکوکاری را در میان خود فراموش نکنید، که خداوند به آنچه انجام می‌دهید، بیناست!'),
(245, 245, 'حَافِظُوا عَلَى الصَّلَوَاتِ وَالصَّلَاةِ الْوُسْطَىٰ وَقُومُوا لِلَّهِ قَانِتِينَ', 2, 238, 2, 8, 39, 'در انجام همه نمازها، (به خصوص) نماز وسطی = نماز ظهر کوشا باشید! و از روی خضوع و اطاعت، برای خدا بپاخیزید!'),
(246, 246, 'فَإِنْ خِفْتُمْ فَرِجَالًا أَوْ رُكْبَانًا ۖ فَإِذَا أَمِنْتُمْ فَاذْكُرُوا اللَّهَ كَمَا عَلَّمَكُمْ مَا لَمْ تَكُونُوا تَعْلَمُونَ', 2, 239, 2, 8, 39, 'و اگر (به خاطر جنگ، یا خطر دیگری) بترسید، (نماز را) در حال پیاده یا سواره انجام دهید! اما هنگامی که امنیت خود را بازیافتید، خدا را یاد کنید! = نماز را به صورت معمولی بخوانید! همان‌گونه که خداوند، چیزهایی را که نمی‌دانستید، به شما تعلیم داد.'),
(247, 247, 'وَالَّذِينَ يُتَوَفَّوْنَ مِنْكُمْ وَيَذَرُونَ أَزْوَاجًا وَصِيَّةً لِأَزْوَاجِهِمْ مَتَاعًا إِلَى الْحَوْلِ غَيْرَ إِخْرَاجٍ ۚ فَإِنْ خَرَجْنَ فَلَا جُنَاحَ عَلَيْكُمْ فِي مَا فَعَلْنَ فِي أَنْفُسِهِنَّ مِنْ مَعْرُوفٍ ۗ وَاللَّهُ عَزِيزٌ حَكِيمٌ', 2, 240, 2, 8, 39, 'و کسانی که از شما در آستانه مرگ قرارمی‌گیرند و همسرانی از خود به‌جا می‌گذارند، باید برای همسران خود وصیت کنند که تا یک سال، آنها را (با پرداختن هزینه زندگی) بهره‌مند سازند؛ به شرط اینکه آنها (از خانه شوهر) بیرون نروند (و اقدام به ازدواج مجدد نکنند). و اگر بیرون روند، (حقی در هزینه ندارند؛ ولی) گناهی بر شما نیست نسبت به آنچه در باره خود، به طور شایسته انجام می‌دهند. و خداوند، توانا و حکیم است.'),
(248, 248, 'وَلِلْمُطَلَّقَاتِ مَتَاعٌ بِالْمَعْرُوفِ ۖ حَقًّا عَلَى الْمُتَّقِينَ', 2, 241, 2, 8, 39, 'و برای زنان مطلقه، هدیه مناسبی لازم است (که از طرف شوهر، پرداخت گردد). این، حقی است بر مردان پرهیزکار.'),
(249, 249, 'كَذَٰلِكَ يُبَيِّنُ اللَّهُ لَكُمْ آيَاتِهِ لَعَلَّكُمْ تَعْقِلُونَ', 2, 242, 2, 8, 39, 'این چنین، خداوند آیات خود را برای شما شرح می‌دهد؛ شاید اندیشه کنید!'),
(250, 250, ' أَلَمْ تَرَ إِلَى الَّذِينَ خَرَجُوا مِنْ دِيَارِهِمْ وَهُمْ أُلُوفٌ حَذَرَ الْمَوْتِ فَقَالَ لَهُمُ اللَّهُ مُوتُوا ثُمَّ أَحْيَاهُمْ ۚ إِنَّ اللَّهَ لَذُو فَضْلٍ عَلَى النَّاسِ وَلَٰكِنَّ أَكْثَرَ النَّاسِ لَا يَشْكُرُونَ', 2, 243, 2, 8, 39, 'آیا ندیدی جمعیتی را که از ترس مرگ، از خانه‌های خود فرار کردند؟ و آنان، هزارها نفر بودند (که به بهانه بیماری طاعون، از شرکت در میدان جهاد خودداری نمودند). خداوند به آنها گفت: بمیرید! (و به همان بیماری، که آن را بهانه قرار داده بودند، مردند.) سپس خدا آنها را زنده کرد؛ (و ماجرای زندگی آنها را درس عبرتی برای آیندگان قرار داد.) خداوند نسبت به بندگان خود احسان می‌کند؛ ولی بیشتر مردم، شکر (او را) بجا نمی‌آورند.'),
(251, 251, 'وَقَاتِلُوا فِي سَبِيلِ اللَّهِ وَاعْلَمُوا أَنَّ اللَّهَ سَمِيعٌ عَلِيمٌ', 2, 244, 2, 8, 39, 'و در راه خدا، پیکار کنید! و بدانید خداوند، شنوا و داناست.'),
(252, 252, 'مَنْ ذَا الَّذِي يُقْرِضُ اللَّهَ قَرْضًا حَسَنًا فَيُضَاعِفَهُ لَهُ أَضْعَافًا كَثِيرَةً ۚ وَاللَّهُ يَقْبِضُ وَيَبْسُطُ وَإِلَيْهِ تُرْجَعُونَ', 2, 245, 2, 8, 39, 'کیست که به خدا «قرض الحسنه‌ای» دهد، (و از اموالی که خدا به او بخشیده، انفاق کند،) تا آن را برای او، چندین برابر کند؟ و خداوند است (که روزی بندگان را) محدود یا گسترده می‌سازد؛ (و انفاق، هرگز باعث کمبود روزی آنها نمی‌شود). و به سوی او باز می‌گردید (و پاداش خود را خواهید گرفت).'),
(253, 253, 'أَلَمْ تَرَ إِلَى الْمَلَإِ مِنْ بَنِي إِسْرَائِيلَ مِنْ بَعْدِ مُوسَىٰ إِذْ قَالُوا لِنَبِيٍّ لَهُمُ ابْعَثْ لَنَا مَلِكًا نُقَاتِلْ فِي سَبِيلِ اللَّهِ ۖ قَالَ هَلْ عَسَيْتُمْ إِنْ كُتِبَ عَلَيْكُمُ الْقِتَالُ أَلَّا تُقَاتِلُوا ۖ قَالُوا وَمَا لَنَا أَلَّا نُقَاتِلَ فِي سَبِيلِ اللَّهِ وَقَدْ أُخْرِجْنَا مِنْ دِيَارِنَا وَأَبْنَائِنَا ۖ فَلَمَّا كُتِبَ عَلَيْهِمُ الْقِتَالُ تَوَلَّوْا إِلَّا قَلِيلًا مِنْهُمْ ۗ وَاللَّهُ عَلِيمٌ بِالظَّالِمِينَ', 2, 246, 2, 8, 40, 'آیا مشاهده نکردی جمعی از بنی اسرائیل را بعد از موسی، که به پیامبر خود گفتند: «زمامدار (و فرماندهی) برای ما انتخاب کن! تا (زیر فرمان او) در راه خدا پیکار کنیم. پیامبر آنها گفت: «شاید اگر دستور پیکار به شما داده شود، (سرپیچی کنید، و) در راه خدا، جهاد و پیکار نکنید!» گفتند: «چگونه ممکن است در راه خدا پیکار نکنیم، در حالی که از خانه‌ها و فرزندانمان رانده شده‌ایم، (و شهرهای ما به وسیله دشمن اشغال، و فرزندان ما اسیر شده‌اند)؟!» اما هنگامی که دستور پیکار به آنها داده شد، جز عدّه کمی از آنان، همه سرپیچی کردند. و خداوند از ستمکاران، آگاه است.'),
(254, 254, 'وَقَالَ لَهُمْ نَبِيُّهُمْ إِنَّ اللَّهَ قَدْ بَعَثَ لَكُمْ طَالُوتَ مَلِكًا ۚ قَالُوا أَنَّىٰ يَكُونُ لَهُ الْمُلْكُ عَلَيْنَا وَنَحْنُ أَحَقُّ بِالْمُلْكِ مِنْهُ وَلَمْ يُؤْتَ سَعَةً مِنَ الْمَالِ ۚ قَالَ إِنَّ اللَّهَ اصْطَفَاهُ عَلَيْكُمْ وَزَادَهُ بَسْطَةً فِي الْعِلْمِ وَالْجِسْمِ ۖ وَاللَّهُ يُؤْتِي مُلْكَهُ مَنْ يَشَاءُ ۚ وَاللَّهُ وَاسِعٌ عَلِيمٌ', 2, 247, 2, 8, 40, 'و پیامبرشان به آنها گفت: «خداوند (*طالوت*) را برای زمامداری شما مبعوث (و انتخاب) کرده است.» گفتند: «چگونه او بر ما حکومت کند، با اینکه ما از او شایسته‌تریم، و او ثروت زیادی ندارد؟!» گفت: «خدا او را بر شما برگزیده، و او را در علم و (قدرت) جسم، وسعت بخشیده است. خداوند، ملکش را به هر کس بخواهد، می‌بخشد؛ و احسان خداوند، وسیع است؛ و (از لیاقت افراد برای منصب‌ها) آگاه است.»'),
(255, 255, 'وَقَالَ لَهُمْ نَبِيُّهُمْ إِنَّ آيَةَ مُلْكِهِ أَنْ يَأْتِيَكُمُ التَّابُوتُ فِيهِ سَكِينَةٌ مِنْ رَبِّكُمْ وَبَقِيَّةٌ مِمَّا تَرَكَ آلُ مُوسَىٰ وَآلُ هَارُونَ تَحْمِلُهُ الْمَلَائِكَةُ ۚ إِنَّ فِي ذَٰلِكَ لَآيَةً لَكُمْ إِنْ كُنْتُمْ مُؤْمِنِينَ', 2, 248, 2, 8, 40, 'و پیامبرشان به آنها گفت: «نشانه حکومت او، این است که (*صندوق عهد*) به سوی شما خواهد آمد. (همان صندوقی که) در آن، آرامشی از پروردگار شما، و یادگارهای خاندان موسی و هارون قرار دارد؛ در حالی که فرشتگان، آن را حمل می‌کنند. در این موضوع، نشانه‌ای (روشن) برای شماست؛ اگر ایمان داشته باشید.»'),
(256, 256, 'فَلَمَّا فَصَلَ طَالُوتُ بِالْجُنُودِ قَالَ إِنَّ اللَّهَ مُبْتَلِيكُمْ بِنَهَرٍ فَمَنْ شَرِبَ مِنْهُ فَلَيْسَ مِنِّي وَمَنْ لَمْ يَطْعَمْهُ فَإِنَّهُ مِنِّي إِلَّا مَنِ اغْتَرَفَ غُرْفَةً بِيَدِهِ ۚ فَشَرِبُوا مِنْهُ إِلَّا قَلِيلًا مِنْهُمْ ۚ فَلَمَّا جَاوَزَهُ هُوَ وَالَّذِينَ آمَنُوا مَعَهُ قَالُوا لَا طَاقَةَ لَنَا الْيَوْمَ بِجَالُوتَ وَجُنُودِهِ ۚ قَالَ الَّذِينَ يَظُنُّونَ أَنَّهُمْ مُلَاقُو اللَّهِ كَمْ مِنْ فِئَةٍ قَلِيلَةٍ غَلَبَتْ فِئَةً كَثِيرَةً بِإِذْنِ اللَّهِ ۗ وَاللَّهُ مَعَ الصَّابِرِينَ', 2, 249, 2, 8, 41, 'و هنگامی که طالوت (به فرماندهی لشکر بنی اسرائیل منصوب شد، و) سپاهیان را با خود بیرون برد، به آنها گفت: «خداوند، شما را به وسیله یک نهر آب، آزمایش می‌کند؛ آنها (که به هنگام تشنگی،) از آن بنوشند، از من نیستند؛ و آنها که جز یک پیمانه با دست خود، بیشتر از آن نخورند، از من هستند» جز عده کمی، همگی از آن آب نوشیدند. سپس هنگامی که او، و افرادی که با او ایمان آورده بودند، (و از بوته آزمایش، سالم به‌در آمدند،) از آن نهر گذشتند، (از کمی نفرات خود، ناراحت شدند؛ و عده‌ای) گفتند: «امروز، ما توانایی مقابله با (*جالوت*) و سپاهیان او را نداریم.» اما آنها که می‌دانستند خدا را ملاقات خواهند کرد (و به روز رستاخیز، ایمان داشتند) گفتند: «چه بسیار گروه‌های کوچکی که به فرمان خدا، بر گروه‌های عظیمی پیروز شدند!» و خداوند، با صابران و استقامت‌کنندگان) است.'),
(257, 257, 'وَلَمَّا بَرَزُوا لِجَالُوتَ وَجُنُودِهِ قَالُوا رَبَّنَا أَفْرِغْ عَلَيْنَا صَبْرًا وَثَبِّتْ أَقْدَامَنَا وَانْصُرْنَا عَلَى الْقَوْمِ الْكَافِرِينَ', 2, 250, 2, 8, 41, 'و هنگامی که در برابر (*جالوت*) و سپاهیان او قرارگرفتند گفتند: «پروردگارا! پیمانه شکیبایی و استقامت را بر ما بریز! و قدمهای ما را ثابت بدار! و ما را بر جمعیّت کافران، پیروز بگردان!'),
(258, 258, 'فَهَزَمُوهُمْ بِإِذْنِ اللَّهِ وَقَتَلَ دَاوُودُ جَالُوتَ وَآتَاهُ اللَّهُ الْمُلْكَ وَالْحِكْمَةَ وَعَلَّمَهُ مِمَّا يَشَاءُ ۗ وَلَوْلَا دَفْعُ اللَّهِ النَّاسَ بَعْضَهُمْ بِبَعْضٍ لَفَسَدَتِ الْأَرْضُ وَلَٰكِنَّ اللَّهَ ذُو فَضْلٍ عَلَى الْعَالَمِينَ', 2, 251, 2, 8, 41, 'سپس به فرمان خدا، آنها سپاه دشمن را به هزیمت واداشتند. و «داوود» (نوجوان نیرومند و شجاعی که در لشکر «طالوت» بود)، «جالوت» را کشت؛ و خداوند، حکومت و دانش را به او بخشید؛ و از آنچه می‌خواست به او تعلیم داد. و اگر خداوند، بعضی از مردم را به وسیله بعضی دیگر دفع نمی‌کرد، زمین را فساد فرامی‌گرفت، ولی خداوند نسبت به جهانیان، لطف و احسان دارد.'),
(259, 259, 'تِلْكَ آيَاتُ اللَّهِ نَتْلُوهَا عَلَيْكَ بِالْحَقِّ ۚ وَإِنَّكَ لَمِنَ الْمُرْسَلِينَ', 2, 252, 2, 8, 41, 'اینها، آیات خداست که به حق، بر تو می‌خوانیم؛ و تو از رسولان (ما) هستی.'),
(260, 260, ' تِلْكَ الرُّسُلُ فَضَّلْنَا بَعْضَهُمْ عَلَىٰ بَعْضٍ ۘ مِنْهُمْ مَنْ كَلَّمَ اللَّهُ ۖ وَرَفَعَ بَعْضَهُمْ دَرَجَاتٍ ۚ وَآتَيْنَا عِيسَى ابْنَ مَرْيَمَ الْبَيِّنَاتِ وَأَيَّدْنَاهُ بِرُوحِ الْقُدُسِ ۗ وَلَوْ شَاءَ اللَّهُ مَا اقْتَتَلَ الَّذِينَ مِنْ بَعْدِهِمْ مِنْ بَعْدِ مَا جَاءَتْهُمُ الْبَيِّنَاتُ وَلَٰكِنِ اخْتَلَفُوا فَمِنْهُمْ مَنْ آمَنَ وَمِنْهُمْ مَنْ كَفَرَ ۚ وَلَوْ شَاءَ اللَّهُ مَا اقْتَتَلُوا وَلَٰكِنَّ اللَّهَ يَفْعَلُ مَا يُرِيدُ', 2, 253, 3, 9, 42, 'بعضی از آن رسولان را بر بعضی دیگر برتری دادیم؛ برخی از آنها، خدا با او سخن می‌گفت؛ و بعضی را درجاتی برتر داد؛ و به عیسی بن مریم، نشانه‌های روشن دادیم؛ و او را با «روح القدس» تأیید نمودیم؛ (ولی فضیلت و مقام آن پیامبران، مانع اختلاف امتها نشد.) و اگر خدا می‌خواست، کسانی که بعد از آنها بودند، پس از آن همه نشانه‌های روشن که برای آنها آمد، جنگ و ستیز نمی‌کردند؛ (اما خدا مردم را مجبور نساخته؛ و آنها را در پیمودن راه سعادت، آزاد گذارده است؛) ولی این امتها بودند که با هم اختلاف کردند؛ بعضی ایمان آوردند و بعضی کافر شدند؛ (و جنگ و خونریزی بروز کرد. و باز) اگر خدا می‌خواست، با هم پیکار نمی‌کردند؛ ولی خداوند، آنچه را می‌خواهد، (از روی حکمت) انجام می‌دهد (و هیچ‌کس را به قبول چیزی مجبور نمی‌کند).'),
(261, 261, 'يَا أَيُّهَا الَّذِينَ آمَنُوا أَنْفِقُوا مِمَّا رَزَقْنَاكُمْ مِنْ قَبْلِ أَنْ يَأْتِيَ يَوْمٌ لَا بَيْعٌ فِيهِ وَلَا خُلَّةٌ وَلَا شَفَاعَةٌ ۗ وَالْكَافِرُونَ هُمُ الظَّالِمُونَ', 2, 254, 3, 9, 42, 'ای کسانی که ایمان آورده‌اید! از آنچه به شما روزی داده‌ایم، انفاق کنید! پیش از آنکه روزی فرا رسد که در آن، نه خرید و فروش است (تا بتوانید سعادت و نجات از کیفر را برای خود خریداری کنید)، و نه دوستی (و رفاقتهای مادی سودی دارد)، و نه شفاعت؛ (زیرا شما شایسته شفاعت نخواهید بود.) و کافران، خود ستمگرند؛ (هم به خودشان ستم می‌کنند، هم به دیگران).'),
(262, 262, 'اللَّهُ لَا إِلَٰهَ إِلَّا هُوَ الْحَيُّ الْقَيُّومُ ۚ لَا تَأْخُذُهُ سِنَةٌ وَلَا نَوْمٌ ۚ لَهُ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ ۗ مَنْ ذَا الَّذِي يَشْفَعُ عِنْدَهُ إِلَّا بِإِذْنِهِ ۚ يَعْلَمُ مَا بَيْنَ أَيْدِيهِمْ وَمَا خَلْفَهُمْ ۖ وَلَا يُحِيطُونَ بِشَيْءٍ مِنْ عِلْمِهِ إِلَّا بِمَا شَاءَ ۚ وَسِعَ كُرْسِيُّهُ السَّمَاوَاتِ وَالْأَرْضَ ۖ وَلَا يَئُودُهُ حِفْظُهُمَا ۚ وَهُوَ الْعَلِيُّ الْعَظِيمُ', 2, 255, 3, 9, 42, 'هیچ معبودی نیست جز خداوند یگانه زنده، که قائم به ذات خویش است، و موجودات دیگر، قائم به او هستند؛ هیچگاه خواب سبک و سنگینی او را فرانمی‌گیرد؛ (و لحظه‌ای از تدبیر جهان هستی، غافل نمی‌ماند؛) آنچه در آسمانها و آنچه در زمین است، از آن اوست؛ کیست که در نزد او، جز به فرمان او شفاعت کند؟! (بنابراین، شفاعت شفاعت‌کنندگان، برای آنها که شایسته شفاعتند، از مالکیت مطلقه او نمی‌کاهد.) آنچه را در پیش روی آنها = بندگان‌ و پشت سرشان است می‌داند؛ (و گذشته و آینده، در پیشگاه علم او، یکسان است.) و کسی از علم او آگاه نمی‌گردد؛ جز به مقداری که او بخواهد. (اوست که به همه چیز آگاه است؛ و علم و دانش محدود دیگران، پرتوی از علم بی‌پایان و نامحدود اوست.) تخت (حکومت) او، آسمانها و زمین را دربرگرفته؛ و نگاهداری آن دو = آسمان و زمین، او را خسته نمیکند. بلندی مقام و عظمت، مخصوص اوست.'),
(263, 263, 'لَا إِكْرَاهَ فِي الدِّينِ ۖ قَدْ تَبَيَّنَ الرُّشْدُ مِنَ الْغَيِّ ۚ فَمَنْ يَكْفُرْ بِالطَّاغُوتِ وَيُؤْمِنْ بِاللَّهِ فَقَدِ اسْتَمْسَكَ بِالْعُرْوَةِ الْوُثْقَىٰ لَا انْفِصَامَ لَهَا ۗ وَاللَّهُ سَمِيعٌ عَلِيمٌ', 2, 256, 3, 9, 42, 'در قبول دین، اکراهی نیست. (زیرا) راه درست از راه انحرافی، روشن شده است. بنابر این، کسی که به طاغوت = بت و شیطان، و هر موجود طغیانگر کافر شود و به خدا ایمان آورد، به دستگیره محکمی چنگ زده است، که گسستن برای آن نیست. و خداوند، شنوا و داناست.'),
(264, 264, 'اللَّهُ وَلِيُّ الَّذِينَ آمَنُوا يُخْرِجُهُمْ مِنَ الظُّلُمَاتِ إِلَى النُّورِ ۖ وَالَّذِينَ كَفَرُوا أَوْلِيَاؤُهُمُ الطَّاغُوتُ يُخْرِجُونَهُمْ مِنَ النُّورِ إِلَى الظُّلُمَاتِ ۗ أُولَٰئِكَ أَصْحَابُ النَّارِ ۖ هُمْ فِيهَا خَالِدُونَ', 2, 257, 3, 9, 43, 'خداوند، ولی و سرپرست کسانی است که ایمان آورده‌اند؛ آنها را از ظلمتها، به سوی نور بیرون می‌برد. (اما) کسانی که کافر شدند، اولیای آنها طاغوتها هستند؛ که آنها را از نور، به سوی ظلمتها بیرون می‌برند؛ آنها اهل آتشند و همیشه در آن خواهند ماند.'),
(265, 265, 'أَلَمْ تَرَ إِلَى الَّذِي حَاجَّ إِبْرَاهِيمَ فِي رَبِّهِ أَنْ آتَاهُ اللَّهُ الْمُلْكَ إِذْ قَالَ إِبْرَاهِيمُ رَبِّيَ الَّذِي يُحْيِي وَيُمِيتُ قَالَ أَنَا أُحْيِي وَأُمِيتُ ۖ قَالَ إِبْرَاهِيمُ فَإِنَّ اللَّهَ يَأْتِي بِالشَّمْسِ مِنَ الْمَشْرِقِ فَأْتِ بِهَا مِنَ الْمَغْرِبِ فَبُهِتَ الَّذِي كَفَرَ ۗ وَاللَّهُ لَا يَهْدِي الْقَوْمَ الظَّالِمِينَ', 2, 258, 3, 9, 43, 'آیا ندیدی (و آگاهی نداری از) کسی = نمرود که با ابراهیم در باره پروردگارش محاجه و گفتگو کرد؟ زیرا خداوند به او حکومت داده بود؛ (و بر اثر کمی ظرفیت، از باده غرور سرمست شده بود؛) هنگامی که ابراهیم گفت: «خدای من آن کسی است که زنده می‌کند و می‌میراند.» او گفت: «من نیز زنده می‌کنم و می‌میرانم!» (و برای اثبات این کار و مشتبه‌ساختن بر مردم دستور داد دو زندانی را حاضر کردند، فرمان آزادی یکی و قتل دیگری را داد) ابراهیم گفت: «خداوند، خورشید را از افق مشرق می‌آورد؛ (اگر راست می‌گویی که حاکم بر جهان هستی تویی،) خورشید را از مغرب بیاور!» (در اینجا) آن مرد کافر، مبهوت و وامانده شد. و خداوند، قوم ستمگر را هدایت نمی‌کند.'),
(266, 266, 'أَوْ كَالَّذِي مَرَّ عَلَىٰ قَرْيَةٍ وَهِيَ خَاوِيَةٌ عَلَىٰ عُرُوشِهَا قَالَ أَنَّىٰ يُحْيِي هَٰذِهِ اللَّهُ بَعْدَ مَوْتِهَا ۖ فَأَمَاتَهُ اللَّهُ مِائَةَ عَامٍ ثُمَّ بَعَثَهُ ۖ قَالَ كَمْ لَبِثْتَ ۖ قَالَ لَبِثْتُ يَوْمًا أَوْ بَعْضَ يَوْمٍ ۖ قَالَ بَلْ لَبِثْتَ مِائَةَ عَامٍ فَانْظُرْ إِلَىٰ طَعَامِكَ وَشَرَابِكَ لَمْ يَتَسَنَّهْ ۖ وَانْظُرْ إِلَىٰ حِمَارِكَ وَلِنَجْعَلَكَ آيَةً لِلنَّاسِ ۖ وَانْظُرْ إِلَى الْعِظَامِ كَيْفَ نُنْشِزُهَا ثُمَّ نَكْسُوهَا لَحْمًا ۚ فَلَمَّا تَبَيَّنَ لَهُ قَالَ أَعْلَمُ أَنَّ اللَّهَ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ', 2, 259, 3, 9, 43, 'یا همانند کسی که از کنار یک آبادی (ویران شده) عبور کرد، در حالی که دیوارهای آن، به روی سقفها فرو ریخته بود، (و اجساد و استخوانهای اهل آن، در هر سو پراکنده بود؛ او با خود) گفت: «چگونه خدا اینها را پس از مرگ، زنده می‌کند؟!» (در این هنگام،) خدا او را یکصد سال میراند؛ سپس زنده کرد؛ و به او گفت: «چه‌قدر درنگ کردی؟» گفت: «یک روز؛ یا بخشی از یک روز.» فرمود: «نه، بلکه یکصد سال درنگ کردی! نگاه کن به غذا و نوشیدنی خود (که همراه داشتی، با گذشت سالها) هیچ‌گونه تغییر نیافته است! (خدایی که یک چنین مواد فاسدشدنی را در طول این مدت، حفظ کرده، بر همه چیز قادر است!) ولی به الاغ خود نگاه کن (که چگونه از هم متلاشی شده! این زنده شدن تو پس از مرگ، هم برای اطمینان خاطر توست، و هم) برای اینکه تو را نشانه‌ای برای مردم (در مورد معاد) قرار دهیم. (اکنون) به استخوانها (ی مرکب سواری خود نگاه کن که چگونه آنها را برداشته، به هم پیوند می‌دهیم، و گوشت بر آن می‌پوشانیم!» هنگامی که (این حقایق) بر او آشکار شد، گفت: «می‌دانم خدا بر هر کاری توانا است».'),
(267, 267, 'وَإِذْ قَالَ إِبْرَاهِيمُ رَبِّ أَرِنِي كَيْفَ تُحْيِي الْمَوْتَىٰ ۖ قَالَ أَوَلَمْ تُؤْمِنْ ۖ قَالَ بَلَىٰ وَلَٰكِنْ لِيَطْمَئِنَّ قَلْبِي ۖ قَالَ فَخُذْ أَرْبَعَةً مِنَ الطَّيْرِ فَصُرْهُنَّ إِلَيْكَ ثُمَّ اجْعَلْ عَلَىٰ كُلِّ جَبَلٍ مِنْهُنَّ جُزْءًا ثُمَّ ادْعُهُنَّ يَأْتِينَكَ سَعْيًا ۚ وَاعْلَمْ أَنَّ اللَّهَ عَزِيزٌ حَكِيمٌ', 2, 260, 3, 9, 44, 'و (به خاطر بیاور) هنگامی را که ابراهیم گفت: «خدایا! به من نشان بده چگونه مردگان را زنده می‌کنی؟» فرمود: «مگر ایمان نیاورده‌ای؟!» عرض کرد: «آری، ولی می‌خواهم قلبم آرامش یابد.» فرمود: «در این صورت، چهار نوع از مرغان را انتخاب کن! و آنها را (پس از ذبح کردن،) قطعه قطعه کن (و در هم بیامیز)! سپس بر هر کوهی، قسمتی از آن را قرار بده، بعد آنها را بخوان، به سرعت به سوی تو می‌آیند! و بدان خداوند قادر و حکیم است؛ (هم از ذرات بدن مردگان آگاه است، و هم توانایی بر جمع آنها دارد)».'),
(268, 268, 'مَثَلُ الَّذِينَ يُنْفِقُونَ أَمْوَالَهُمْ فِي سَبِيلِ اللَّهِ كَمَثَلِ حَبَّةٍ أَنْبَتَتْ سَبْعَ سَنَابِلَ فِي كُلِّ سُنْبُلَةٍ مِائَةُ حَبَّةٍ ۗ وَاللَّهُ يُضَاعِفُ لِمَنْ يَشَاءُ ۗ وَاللَّهُ وَاسِعٌ عَلِيمٌ', 2, 261, 3, 9, 44, 'کسانی که اموال خود را در راه خدا انفاق می‌کنند، همانند بذری هستند که هفت خوشه برویاند؛ که در هر خوشه، یکصد دانه باشد؛ و خداوند آن را برای هر کس بخواهد (و شایستگی داشته باشد)، دو یا چند برابر می‌کند؛ و خدا (از نظر قدرت و رحمت،) وسیع، و (به همه چیز) داناست.'),
(269, 269, 'الَّذِينَ يُنْفِقُونَ أَمْوَالَهُمْ فِي سَبِيلِ اللَّهِ ثُمَّ لَا يُتْبِعُونَ مَا أَنْفَقُوا مَنًّا وَلَا أَذًى ۙ لَهُمْ أَجْرُهُمْ عِنْدَ رَبِّهِمْ وَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَ', 2, 262, 3, 9, 44, 'کسانی که اموال خود را در راه خدا انفاق می‌کنند، سپس به دنبال انفاقی که کرده‌اند، منت نمی‌گذارند و آزاری نمی‌رسانند، پاداش آنها نزد پروردگارشان (محفوظ) است؛ و نه ترسی دارند، و نه غمگین می‌شوند.'),
(270, 270, ' قَوْلٌ مَعْرُوفٌ وَمَغْفِرَةٌ خَيْرٌ مِنْ صَدَقَةٍ يَتْبَعُهَا أَذًى ۗ وَاللَّهُ غَنِيٌّ حَلِيمٌ', 2, 263, 3, 9, 44, 'گفتار پسندیده (در برابر نیازمندان)، و عفو (و گذشت از خشونتهای آنها)، از بخششی که آزاری به دنبال آن باشد، بهتر است؛ و خداوند، بی‌نیاز و بردبار است.'),
(271, 271, 'يَا أَيُّهَا الَّذِينَ آمَنُوا لَا تُبْطِلُوا صَدَقَاتِكُمْ بِالْمَنِّ وَالْأَذَىٰ كَالَّذِي يُنْفِقُ مَالَهُ رِئَاءَ النَّاسِ وَلَا يُؤْمِنُ بِاللَّهِ وَالْيَوْمِ الْآخِرِ ۖ فَمَثَلُهُ كَمَثَلِ صَفْوَانٍ عَلَيْهِ تُرَابٌ فَأَصَابَهُ وَابِلٌ فَتَرَكَهُ صَلْدًا ۖ لَا يَقْدِرُونَ عَلَىٰ شَيْءٍ مِمَّا كَسَبُوا ۗ وَاللَّهُ لَا يَهْدِي الْقَوْمَ الْكَافِرِينَ', 2, 264, 3, 9, 44, 'ای کسانی که ایمان آورده‌اید! بخششهای خود را با منت و آزار، باطل نسازید! همانند کسی که مال خود را برای نشان دادن به مردم، انفاق می‌کند؛ و به خدا و روز رستاخیز، ایمان نمی‌آورد؛ (کار او) همچون قطعه سنگی است که بر آن، (قشر نازکی از) خاک باشد؛ (و بذرهایی در آن افشانده شود؛) و رگبار باران به آن برسد، (و همه خاکها و بذرها را بشوید،) و آن را صاف (و خالی از خاک و بذر) رها کند. آنها از کاری که انجام داده‌اند، چیزی به دست نمی‌آورند؛ و خداوند، جمعیت کافران را هدایت نمی‌کند.'),
(272, 272, 'وَمَثَلُ الَّذِينَ يُنْفِقُونَ أَمْوَالَهُمُ ابْتِغَاءَ مَرْضَاتِ اللَّهِ وَتَثْبِيتًا مِنْ أَنْفُسِهِمْ كَمَثَلِ جَنَّةٍ بِرَبْوَةٍ أَصَابَهَا وَابِلٌ فَآتَتْ أُكُلَهَا ضِعْفَيْنِ فَإِنْ لَمْ يُصِبْهَا وَابِلٌ فَطَلٌّ ۗ وَاللَّهُ بِمَا تَعْمَلُونَ بَصِيرٌ', 2, 265, 3, 9, 45, 'و (کار) کسانی که اموال خود را برای خشنودی خدا، و تثبیت (ملکات انسانی در) روح خود، انفاق می‌کنند، همچون باغی است که در نقطه بلندی باشد، و بارانهای درشت به آن برسد، (و از هوای آزاد و نور آفتاب، به حد کافی بهره گیرد،) و میوه خود را دو چندان دهد (که همیشه شاداب و با طراوت است.) و خداوند به آنچه انجام می‌دهید، بیناست.'),
(273, 273, 'أَيَوَدُّ أَحَدُكُمْ أَنْ تَكُونَ لَهُ جَنَّةٌ مِنْ نَخِيلٍ وَأَعْنَابٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهَارُ لَهُ فِيهَا مِنْ كُلِّ الثَّمَرَاتِ وَأَصَابَهُ الْكِبَرُ وَلَهُ ذُرِّيَّةٌ ضُعَفَاءُ فَأَصَابَهَا إِعْصَارٌ فِيهِ نَارٌ فَاحْتَرَقَتْ ۗ كَذَٰلِكَ يُبَيِّنُ اللَّهُ لَكُمُ الْآيَاتِ لَعَلَّكُمْ تَتَفَكَّرُونَ', 2, 266, 3, 9, 45, 'آیا کسی از شما دوست دارد که باغی از درختان خرما و انگور داشته باشد که از زیر درختان آن، نهرها بگذرد، و برای او در آن (باغ)، از هر گونه میوه‌ای وجود داشته باشد، در حالی که به سن پیری رسیده و فرزندانی (کوچک و) ضعیف دارد؛ (در این هنگام،) گردبادی (کوبنده)، که در آن آتش (سوزانی) است، به آن برخورد کند و شعله‌ور گردد و بسوزد؟! (همین‌طور است حال کسانی که انفاقهای خود را، با ریا و منت و آزار، باطل می‌کنند.) این چنین خداوند آیات خود را برای شما آشکار می‌سازد؛ شاید بیندیشید (و با اندیشه، راه حق را بیابید)!'),
(274, 274, 'يَا أَيُّهَا الَّذِينَ آمَنُوا أَنْفِقُوا مِنْ طَيِّبَاتِ مَا كَسَبْتُمْ وَمِمَّا أَخْرَجْنَا لَكُمْ مِنَ الْأَرْضِ ۖ وَلَا تَيَمَّمُوا الْخَبِيثَ مِنْهُ تُنْفِقُونَ وَلَسْتُمْ بِآخِذِيهِ إِلَّا أَنْ تُغْمِضُوا فِيهِ ۚ وَاعْلَمُوا أَنَّ اللَّهَ غَنِيٌّ حَمِيدٌ', 2, 267, 3, 9, 45, 'ای کسانی که ایمان آورده‌اید! از قسمتهای پاکیزه اموالی که (از طریق تجارت) به دست آورده‌اید، و از آنچه از زمین برای شما خارج ساخته‌ایم (از منابع و معادن و درختان و گیاهان)، انفاق کنید! و برای انفاق، به سراغ قسمتهای ناپاک نروید در حالی که خود شما، (به هنگام پذیرش اموال،) حاضر نیستید آنها را بپذیرید؛ مگر از روی اغماض و کراهت! و بدانید خداوند، بی‌نیاز و شایسته ستایش است.'),
(275, 275, 'الشَّيْطَانُ يَعِدُكُمُ الْفَقْرَ وَيَأْمُرُكُمْ بِالْفَحْشَاءِ ۖ وَاللَّهُ يَعِدُكُمْ مَغْفِرَةً مِنْهُ وَفَضْلًا ۗ وَاللَّهُ وَاسِعٌ عَلِيمٌ', 2, 268, 3, 9, 45, 'شیطان، شما را (به هنگام انفاق،) وعده فقر و تهیدستی می‌دهد؛ و به فحشا (و زشتیها) امر می‌کند؛ ولی خداوند وعده «آمرزش» و «فزونی» به شما می‌دهد؛ و خداوند، قدرتش وسیع، و (به هر چیز) داناست. (به همین دلیل، به وعده‌های خود، وفا می‌کند.)'),
(276, 276, 'يُؤْتِي الْحِكْمَةَ مَنْ يَشَاءُ ۚ وَمَنْ يُؤْتَ الْحِكْمَةَ فَقَدْ أُوتِيَ خَيْرًا كَثِيرًا ۗ وَمَا يَذَّكَّرُ إِلَّا أُولُو الْأَلْبَابِ', 2, 269, 3, 9, 45, '(خدا) دانش و حکمت را به هر کس بخواهد (و شایسته بداند) می‌دهد؛ و به هر کس دانش داده شود، خیر فراوانی داده شده است. و جز خردمندان، (این حقایق را درک نمی‌کنند، و) متذکر نمی‌گردند.'),
(277, 277, 'وَمَا أَنْفَقْتُمْ مِنْ نَفَقَةٍ أَوْ نَذَرْتُمْ مِنْ نَذْرٍ فَإِنَّ اللَّهَ يَعْلَمُهُ ۗ وَمَا لِلظَّالِمِينَ مِنْ أَنْصَارٍ', 2, 270, 3, 9, 46, 'و هر چیز را که انفاق می‌کنید، یا (اموالی را که) نذر کرده‌اید (در راه خدا انفاق کنید)، خداوند آنها را می‌داند. و ستمگران یاوری ندارند.'),
(278, 278, 'إِنْ تُبْدُوا الصَّدَقَاتِ فَنِعِمَّا هِيَ ۖ وَإِنْ تُخْفُوهَا وَتُؤْتُوهَا الْفُقَرَاءَ فَهُوَ خَيْرٌ لَكُمْ ۚ وَيُكَفِّرُ عَنْكُمْ مِنْ سَيِّئَاتِكُمْ ۗ وَاللَّهُ بِمَا تَعْمَلُونَ خَبِيرٌ', 2, 271, 3, 9, 46, 'اگر انفاقها را آشکار کنید، خوب است! و اگر آنها را مخفی ساخته و به نیازمندان بدهید، برای شما بهتر است! و قسمتی از گناهان شما را می‌پوشاند؛ (و در پرتو بخشش در راه خدا، بخشوده خواهید شد.) و خداوند به آنچه انجام می‌دهید، آگاه است.'),
(279, 279, ' لَيْسَ عَلَيْكَ هُدَاهُمْ وَلَٰكِنَّ اللَّهَ يَهْدِي مَنْ يَشَاءُ ۗ وَمَا تُنْفِقُوا مِنْ خَيْرٍ فَلِأَنْفُسِكُمْ ۚ وَمَا تُنْفِقُونَ إِلَّا ابْتِغَاءَ وَجْهِ اللَّهِ ۚ وَمَا تُنْفِقُوا مِنْ خَيْرٍ يُوَفَّ إِلَيْكُمْ وَأَنْتُمْ لَا تُظْلَمُونَ', 2, 272, 3, 10, 46, 'هدایت آنها (به‌طور اجبار،) بر تو نیست؛ (بنابر این، ترک انفاق به غیر مسلمانان، برای اجبار به اسلام، صحیح نیست؛) ولی خداوند، هر که را بخواهد (و شایسته بداند)، هدایت می‌کند. و آنچه را از خوبیها و اموال انفاق می‌کنید، برای خودتان است؛ (ولی) جز برای رضای خدا، انفاق نکنید! و آنچه از خوبیها انفاق می‌کنید، (پاداش آن) به طور کامل به شما داده می‌شود؛ و به شما ستم نخواهد شد.'),
(280, 280, 'لِلْفُقَرَاءِ الَّذِينَ أُحْصِرُوا فِي سَبِيلِ اللَّهِ لَا يَسْتَطِيعُونَ ضَرْبًا فِي الْأَرْضِ يَحْسَبُهُمُ الْجَاهِلُ أَغْنِيَاءَ مِنَ التَّعَفُّفِ تَعْرِفُهُمْ بِسِيمَاهُمْ لَا يَسْأَلُونَ النَّاسَ إِلْحَافًا ۗ وَمَا تُنْفِقُوا مِنْ خَيْرٍ فَإِنَّ اللَّهَ بِهِ عَلِيمٌ', 2, 273, 3, 10, 46, '(انفاقِ شما، مخصوصاً باید) برای نیازمندانی باشد که در راه خدا، در تنگنا قرار گرفته‌اند؛ (و توجّه به آیین خدا، آنها را از وطنهای خویش آواره ساخته؛ و شرکت در میدانِ جهاد، به آنها اجازه نمی‌دهد تا برای تأمین هزینه زندگی، دست به کسب و تجارتی بزنند؛) نمی‌توانند مسافرتی کنند (و سرمایه‌ای به دست آورند؛) و از شدّت خویشتن‌داری، افراد ناآگاه آنها را بی‌نیاز می‌پندارند؛ امّا آنها را از چهره‌هایشان می‌شناسی؛ و هرگز با اصرار چیزی از مردم نمی‌خواهند. (این است مشخّصات آنها!) و هر چیز خوبی در راه خدا انفاق کنید، خداوند از آن آگاه است.'),
(281, 281, 'الَّذِينَ يُنْفِقُونَ أَمْوَالَهُمْ بِاللَّيْلِ وَالنَّهَارِ سِرًّا وَعَلَانِيَةً فَلَهُمْ أَجْرُهُمْ عِنْدَ رَبِّهِمْ وَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَ', 2, 274, 3, 10, 46, 'آنها که اموال خود را، شب و روز، پنهان و آشکار، انفاق می‌کنند، مزدشان نزد پروردگارشان است؛ نه ترسی بر آنهاست، و نه غمگین می‌شوند.'),
(282, 282, 'الَّذِينَ يَأْكُلُونَ الرِّبَا لَا يَقُومُونَ إِلَّا كَمَا يَقُومُ الَّذِي يَتَخَبَّطُهُ الشَّيْطَانُ مِنَ الْمَسِّ ۚ ذَٰلِكَ بِأَنَّهُمْ قَالُوا إِنَّمَا الْبَيْعُ مِثْلُ الرِّبَا ۗ وَأَحَلَّ اللَّهُ الْبَيْعَ وَحَرَّمَ الرِّبَا ۚ فَمَنْ جَاءَهُ مَوْعِظَةٌ مِنْ رَبِّهِ فَانْتَهَىٰ فَلَهُ مَا سَلَفَ وَأَمْرُهُ إِلَى اللَّهِ ۖ وَمَنْ عَادَ فَأُولَٰئِكَ أَصْحَابُ النَّارِ ۖ هُمْ فِيهَا خَالِدُونَ', 2, 275, 3, 10, 47, 'کسانی که ربا می‌خورند، (در قیامت) برنمی‌خیزند مگر مانند کسی که بر اثر تماسّ شیطان، دیوانه شده (و نمی‌تواند تعادل خود را حفظ کند؛ گاهی زمین می‌خورد، گاهی بپا می‌خیزد). این، به خاطر آن است که گفتند: «داد و ستد هم مانند ربا است (و تفاوتی میان آن دو نیست.)» در حالی که خدا بیع را حلال کرده، و ربا را حرام! (زیرا فرق میان این دو، بسیار است.) و اگر کسی اندرز الهی به او رسد، و (از رباخواری) خودداری کند، سودهایی که در سابق = قبل از نزول حکم تحریم‌ به دست آورده، مال اوست؛ (و این حکم، گذشته را شامل نمی‌گردد؛) و کار او به خدا واگذار می‌شود؛ (و گذشته او را خواهد بخشید.) امّا کسانی که بازگردند (و بار دیگر مرتکب این گناه شوند)، اهل آتشند؛ و همیشه در آن می‌مانند.'),
(283, 283, 'يَمْحَقُ اللَّهُ الرِّبَا وَيُرْبِي الصَّدَقَاتِ ۗ وَاللَّهُ لَا يُحِبُّ كُلَّ كَفَّارٍ أَثِيمٍ', 2, 276, 3, 10, 47, 'خداوند، ربا را نابود می‌کند؛ و صدقات را افزایش می‌دهد! و خداوند، هیچ انسانِ ناسپاسِ گنهکاری را دوست نمی‌دارد.'),
(284, 284, 'إِنَّ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ وَأَقَامُوا الصَّلَاةَ وَآتَوُا الزَّكَاةَ لَهُمْ أَجْرُهُمْ عِنْدَ رَبِّهِمْ وَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَ', 2, 277, 3, 10, 47, 'کسانی که ایمان آوردند و اعمال صالح انجام دادند و نماز را برپا داشتند و زکات را پرداختند، اجرشان نزد پروردگارشان است؛ و نه ترسی بر آنهاست، و نه غمگین می‌شوند.'),
(285, 285, 'يَا أَيُّهَا الَّذِينَ آمَنُوا اتَّقُوا اللَّهَ وَذَرُوا مَا بَقِيَ مِنَ الرِّبَا إِنْ كُنْتُمْ مُؤْمِنِينَ', 2, 278, 3, 10, 47, 'ای کسانی که ایمان آورده‌اید! از (مخالفت فرمان) خدا بپرهیزید، و آنچه از (مطالبات) ربا باقی مانده، رها کنید؛ اگر ایمان دارید!'),
(286, 286, 'فَإِنْ لَمْ تَفْعَلُوا فَأْذَنُوا بِحَرْبٍ مِنَ اللَّهِ وَرَسُولِهِ ۖ وَإِنْ تُبْتُمْ فَلَكُمْ رُءُوسُ أَمْوَالِكُمْ لَا تَظْلِمُونَ وَلَا تُظْلَمُونَ', 2, 279, 3, 10, 47, 'اگر (چنین) نمی‌کنید، بدانید خدا و رسولش، با شما پیکار خواهند کرد! و اگر توبه کنید، سرمایه‌های شما، از آنِ شماست = اصل سرمایه، بدون سود؛ نه ستم می‌کنید، و نه بر شما ستم وارد می‌شود.'),
(287, 287, 'وَإِنْ كَانَ ذُو عُسْرَةٍ فَنَظِرَةٌ إِلَىٰ مَيْسَرَةٍ ۚ وَأَنْ تَصَدَّقُوا خَيْرٌ لَكُمْ ۖ إِنْ كُنْتُمْ تَعْلَمُونَ', 2, 280, 3, 10, 47, 'و اگر (بدهکار،) قدرت پرداخت نداشته باشد، او را تا هنگام توانایی، مهلت دهید! (و در صورتی که براستی قدرت پرداخت را ندارد،) برای خدا به او ببخشید بهتر است؛ اگر (منافع این کار را) بدانید!'),
(288, 288, 'وَاتَّقُوا يَوْمًا تُرْجَعُونَ فِيهِ إِلَى اللَّهِ ۖ ثُمَّ تُوَفَّىٰ كُلُّ نَفْسٍ مَا كَسَبَتْ وَهُمْ لَا يُظْلَمُونَ', 2, 281, 3, 10, 47, 'و از روزی بپرهیزید (و بترسید) که در آن روز، شما را به سوی خدا بازمی‌گردانند؛ سپس به هر کس، آنچه انجام داده، به طور کامل باز پس داده می‌شود، و به آنها ستم نخواهد شد. (چون هر چه می‌بینند، نتایج اعمال خودشان است.)'),
(289, 289, 'يَا أَيُّهَا الَّذِينَ آمَنُوا إِذَا تَدَايَنْتُمْ بِدَيْنٍ إِلَىٰ أَجَلٍ مُسَمًّى فَاكْتُبُوهُ ۚ وَلْيَكْتُبْ بَيْنَكُمْ كَاتِبٌ بِالْعَدْلِ ۚ وَلَا يَأْبَ كَاتِبٌ أَنْ يَكْتُبَ كَمَا عَلَّمَهُ اللَّهُ ۚ فَلْيَكْتُبْ وَلْيُمْلِلِ الَّذِي عَلَيْهِ الْحَقُّ وَلْيَتَّقِ اللَّهَ رَبَّهُ وَلَا يَبْخَسْ مِنْهُ شَيْئًا ۚ فَإِنْ كَانَ الَّذِي عَلَيْهِ الْحَقُّ سَفِيهًا أَوْ ضَعِيفًا أَوْ لَا يَسْتَطِيعُ أَنْ يُمِلَّ هُوَ فَلْيُمْلِلْ وَلِيُّهُ بِالْعَدْلِ ۚ وَاسْتَشْهِدُوا شَهِيدَيْنِ مِنْ رِجَالِكُمْ ۖ فَإِنْ لَمْ يَكُونَا رَجُلَيْنِ فَرَجُلٌ وَامْرَأَتَانِ مِمَّنْ تَرْضَوْنَ مِنَ الشُّهَدَاءِ أَنْ تَضِلَّ إِحْدَاهُمَا فَتُذَكِّرَ إِحْدَاهُمَا الْأُخْرَىٰ ۚ وَلَا يَأْبَ الشُّهَدَاءُ إِذَا مَا دُعُوا ۚ وَلَا تَسْأَمُوا أَنْ تَكْتُبُوهُ صَغِيرًا أَوْ كَبِيرًا إِلَىٰ أَجَلِهِ ۚ ذَٰلِكُمْ أَقْسَطُ عِنْدَ اللَّهِ وَأَقْوَمُ لِلشَّهَادَةِ وَأَدْنَىٰ أَلَّا تَرْتَابُوا ۖ إِلَّا أَنْ تَكُونَ تِجَارَةً حَاضِرَةً تُدِيرُونَهَا بَيْنَكُمْ فَلَيْسَ عَلَيْكُمْ جُنَاحٌ أَلَّا تَكْتُبُوهَا ۗ \r\nوَأَشْهِدُوا إِذَا تَبَايَعْتُمْ ۚ وَلَا يُضَارَّ كَاتِبٌ وَلَا شَهِيدٌ ۚ وَإِنْ تَفْعَلُوا فَإِنَّهُ فُسُوقٌ بِكُمْ ۗ وَاتَّقُوا اللَّهَ ۖ وَيُعَلِّمُكُمُ اللَّهُ ۗ وَاللَّهُ بِكُلِّ شَيْءٍ عَلِيمٌ', 2, 282, 3, 10, 48, 'ای کسانی که ایمان آورده‌اید! هنگامی که بدهی مدّت‌داری (به خاطر وام یا داد و ستد) به یکدیگر پیدا کنید، آن را بنویسید! و باید نویسنده‌ای از روی عدالت، (سند را) در میان شما بنویسد! و کسی که قدرت بر نویسندگی دارد، نباید از نوشتن -همان طور که خدا به او تعلیم داده- خودداری کند! پس باید بنویسد، و آن کس که حق بر عهده اوست، باید املا کند، و از خدا که پروردگار اوست بپرهیزد، و چیزی را فروگذار ننماید! و اگر کسی که حق بر ذمه اوست، سفیه (یا از نظر عقل) ضعیف (و مجنون) است، یا (به خاطر لال بودن،) توانایی بر املاکردن ندارد، باید ولیّ او (به جای او،) با رعایت عدالت، املا کند! و دو نفر از مردان (عادل) خود را (بر این حقّ) شاهد بگیرید! و اگر دو مرد نبودند، یک مرد و دو زن، از کسانی که مورد رضایت و اطمینان شما هستند، انتخاب کنید! (و این دو زن، باید با هم شاهد قرار گیرند،) تا اگر یکی انحرافی یافت، دیگری به او یادآوری کند. و شهود نباید به هنگامی که آنها را (برای شهادت) دعوت می‌کنند، خودداری نمایند! و از نوشتن (بدهیِ خود،) چه کوچک باشد یا بزرگ، ملول نشوید (هر چه باشد بنویسید)! این، در نزد خدا به\r\nعدالت نزدیکتر، و برای شهادت مستقیم تر، و برای جلوگیری از تردید و شک (و نزاع و گفتگو) بهتر می‌باشد؛ مگر اینکه داد و ستد نقدی باشد که بین خود، دست به دست می‌کنید. در این صورت، گناهی بر شما نیست که آن را ننویسید. ولی هنگامی که خرید و فروش (نقدی) می‌کنید، شاهد بگیرید! و نباید به نویسنده و شاهد، (به خاطر حقگویی،) زیانی برسد (و تحت فشار قرار گیرند)! و اگر چنین کنید، از فرمان پروردگار خارج شده‌اید. از خدا بپرهیزید! و خداوند به شما تعلیم می‌دهد؛ خداوند به همه چیز داناست.'),
(290, 290, ' وَإِنْ كُنْتُمْ عَلَىٰ سَفَرٍ وَلَمْ تَجِدُوا كَاتِبًا فَرِهَانٌ مَقْبُوضَةٌ ۖ فَإِنْ أَمِنَ بَعْضُكُمْ بَعْضًا فَلْيُؤَدِّ الَّذِي اؤْتُمِنَ أَمَانَتَهُ وَلْيَتَّقِ اللَّهَ رَبَّهُ ۗ وَلَا تَكْتُمُوا الشَّهَادَةَ ۚ وَمَنْ يَكْتُمْهَا فَإِنَّهُ آثِمٌ قَلْبُهُ ۗ وَاللَّهُ بِمَا تَعْمَلُونَ عَلِيمٌ', 2, 283, 3, 10, 49, 'و اگر در سفر بودید، و نویسنده‌ای نیافتید، گروگان بگیرید! (گروگانی که در اختیار طلبکار قرار گیرد.) و اگر به یکدیگر اطمینان (کامل) داشته باشید، (گروگان لازم نیست، و) باید کسی که امین شمرده شده (و بدون گروگان، چیزی از دیگری گرفته)، امانت (و بدهی خود را بموقع) بپردازد؛ و از خدایی که پروردگار اوست. بپرهیزد! و شهادت را کتمان نکنید! و هر کس آن را کتمان کند، قلبش گناهکار است. و خداوند، به آنچه انجام می‌دهید، داناست.'),
(291, 291, 'لِلَّهِ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ ۗ وَإِنْ تُبْدُوا مَا فِي أَنْفُسِكُمْ أَوْ تُخْفُوهُ يُحَاسِبْكُمْ بِهِ اللَّهُ ۖ فَيَغْفِرُ لِمَنْ يَشَاءُ وَيُعَذِّبُ مَنْ يَشَاءُ ۗ وَاللَّهُ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ', 2, 284, 3, 10, 49, 'آنچه در آسمانها و زمین است، از آنِ خداست. و (از این رو) اگر آنچه را در دل دارید، آشکار سازید یا پنهان، خداوند شما را بر طبق آن، محاسبه می‌کند. سپس هر کس را بخواهد (و شایستگی داشته باشد)، می‌بخشد؛ و هر کس را بخواهد (و مستحق باشد)، مجازات می‌کند. و خداوند به همه چیز قدرت دارد.'),
(292, 292, 'آمَنَ الرَّسُولُ بِمَا أُنْزِلَ إِلَيْهِ مِنْ رَبِّهِ وَالْمُؤْمِنُونَ ۚ كُلٌّ آمَنَ بِاللَّهِ وَمَلَائِكَتِهِ وَكُتُبِهِ وَرُسُلِهِ لَا نُفَرِّقُ بَيْنَ أَحَدٍ مِنْ رُسُلِهِ ۚ وَقَالُوا سَمِعْنَا وَأَطَعْنَا ۖ غُفْرَانَكَ رَبَّنَا وَإِلَيْكَ الْمَصِيرُ', 2, 285, 3, 10, 49, 'پیامبر، به آنچه از سوی پروردگارش بر او نازل شده، ایمان آورده است. (و او، به تمام سخنان خود، کاملاً مؤمن می‌باشد.) و همه مؤمنان (نیز)، به خدا و فرشتگان او و کتابها و فرستادگانش، ایمان آورده‌اند؛ (و می‌گویند:) ما در میان هیچ یک از پیامبران او، فرق نمی‌گذاریم (و به همه ایمان داریم). و (مؤمنان) گفتند: «ما شنیدیم و اطاعت کردیم. پروردگارا! (انتظارِ) آمرزش تو را (داریم)؛ و بازگشت (ما) به سوی توست.»'),
(293, 293, 'لَا يُكَلِّفُ اللَّهُ نَفْسًا إِلَّا وُسْعَهَا ۚ لَهَا مَا كَسَبَتْ وَعَلَيْهَا مَا اكْتَسَبَتْ ۗ رَبَّنَا لَا تُؤَاخِذْنَا إِنْ نَسِينَا أَوْ أَخْطَأْنَا ۚ رَبَّنَا وَلَا تَحْمِلْ عَلَيْنَا إِصْرًا كَمَا حَمَلْتَهُ عَلَى الَّذِينَ مِنْ قَبْلِنَا ۚ رَبَّنَا وَلَا تُحَمِّلْنَا مَا لَا طَاقَةَ لَنَا بِهِ ۖ وَاعْفُ عَنَّا وَاغْفِرْ لَنَا وَارْحَمْنَا ۚ أَنْتَ مَوْلَانَا فَانْصُرْنَا عَلَى الْقَوْمِ الْكَافِرِينَ', 2, 286, 3, 10, 49, 'خداوند هیچ کس را، جز به اندازه تواناییش، تکلیف نمی‌کند. (انسان،) هر کار (نیکی) را انجام دهد، برای خود انجام داده؛ و هر کار (بدی) کند، به زیان خود کرده است. (مؤمنان می‌گویند:) پروردگارا! اگر ما فراموش یا خطا کردیم، ما را مؤاخذه مکن! پروردگارا! تکلیف سنگینی بر ما قرار مده، آن چنان که (به خاطر گناه و طغیان،) بر کسانی که پیش از ما بودند، قرار دادی! پروردگارا! آنچه طاقت تحمل آن را نداریم، بر ما مقرّر مدار! و آثار گناه را از ما بشوی! ما را ببخش و در رحمت خود قرار ده! تو مولا و سرپرست مایی، پس ما را بر جمعیّت کافران، پیروز گردان!'),
(294, 294, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ الم', 3, 1, 3, 10, 50, 'الم‌'),
(295, 295, 'اللَّهُ لَا إِلَٰهَ إِلَّا هُوَ الْحَيُّ الْقَيُّومُ', 3, 2, 3, 10, 50, 'معبودی جز خداوندِ یگانه زنده و پایدار و نگهدارنده، نیست.'),
(296, 296, 'نَزَّلَ عَلَيْكَ الْكِتَابَ بِالْحَقِّ مُصَدِّقًا لِمَا بَيْنَ يَدَيْهِ وَأَنْزَلَ التَّوْرَاةَ وَالْإِنْجِيلَ', 3, 3, 3, 10, 50, '(همان کسی که) کتاب را بحق بر تو نازل کرد، که با نشانه‌های کتب پیشین، منطبق است؛ و «تورات» و «انجیل» را.'),
(297, 297, 'مِنْ قَبْلُ هُدًى لِلنَّاسِ وَأَنْزَلَ الْفُرْقَانَ ۗ إِنَّ الَّذِينَ كَفَرُوا بِآيَاتِ اللَّهِ لَهُمْ عَذَابٌ شَدِيدٌ ۗ وَاللَّهُ عَزِيزٌ ذُو انْتِقَامٍ', 3, 4, 3, 10, 50, 'پیش از آن، برای هدایت مردم فرستاد؛ و (نیز) کتابی که حق را از باطل مشخّص می‌سازد، نازل کرد؛ کسانی که به آیات خدا کافر شدند، کیفر شدیدی دارند؛ و خداوند (برای کیفرِ بدکاران و کافران لجوج،) توانا و صاحب انتقام است.'),
(298, 298, 'إِنَّ اللَّهَ لَا يَخْفَىٰ عَلَيْهِ شَيْءٌ فِي الْأَرْضِ وَلَا فِي السَّمَاءِ', 3, 5, 3, 10, 50, 'هیچ چیز، در آسمان و زمین، بر خدا مخفی نمی‌ماند. (بنابر این، تدبیر آنها بر او مشکل نیست.)'),
(299, 299, 'هُوَ الَّذِي يُصَوِّرُكُمْ فِي الْأَرْحَامِ كَيْفَ يَشَاءُ ۚ لَا إِلَٰهَ إِلَّا هُوَ الْعَزِيزُ الْحَكِيمُ', 3, 6, 3, 10, 50, 'او کسی است که شما را در رحمِ (مادران)، آنچنان که می‌خواهد تصویر می‌کند. معبودی جز خداوندِ توانا و حکیم، نیست.'),
(300, 300, 'هُوَ الَّذِي أَنْزَلَ عَلَيْكَ الْكِتَابَ مِنْهُ آيَاتٌ مُحْكَمَاتٌ هُنَّ أُمُّ الْكِتَابِ وَأُخَرُ مُتَشَابِهَاتٌ ۖ فَأَمَّا الَّذِينَ فِي قُلُوبِهِمْ زَيْغٌ فَيَتَّبِعُونَ مَا تَشَابَهَ مِنْهُ ابْتِغَاءَ الْفِتْنَةِ وَابْتِغَاءَ تَأْوِيلِهِ ۗ وَمَا يَعْلَمُ تَأْوِيلَهُ إِلَّا اللَّهُ ۗ وَالرَّاسِخُونَ فِي الْعِلْمِ يَقُولُونَ آمَنَّا بِهِ كُلٌّ مِنْ عِنْدِ رَبِّنَا ۗ وَمَا يَذَّكَّرُ إِلَّا أُولُو الْأَلْبَابِ', 3, 7, 3, 10, 50, 'او کسی است که این کتاب (آسمانی) را بر تو نازل کرد، که قسمتی از آن، آیات «محکم» = صریح و روشن‌ است؛ که اساس این کتاب می‌باشد؛ (و هر گونه پیچیدگی در آیات دیگر، با مراجعه به اینها، برطرف می‌گردد.) و قسمتی از آن، «متشابه» است = آیاتی که به خاطر بالا بودن سطح مطلب و جهات دیگر، در نگاه اول، احتمالات مختلفی در آن می‌رود؛ ولی با توجه به آیات محکم، تفسیر آنها آشکار می‌گردد. اما آنها که در قلوبشان انحراف است، به دنبال متشابهاتند، تا فتنه‌انگیزی کنند (و مردم را گمراه سازند)؛ و تفسیر (نادرستی) برای آن می‌طلبند؛ در حالی که تفسیر آنها را، جز خدا و راسخان در علم، نمی‌دانند. (آنها که به دنبال فهم و درکِ اسرارِ همه آیات قرآن در پرتو علم و دانش الهی) می‌گویند: «ما به همه آن ایمان آوردیم؛ همه از طرف پروردگارِ ماست.» و جز صاحبان عقل، متذکر نمی‌شوند (و این حقیقت را درک نمی‌کنند).'),
(301, 301, 'رَبَّنَا لَا تُزِغْ قُلُوبَنَا بَعْدَ إِذْ هَدَيْتَنَا وَهَبْ لَنَا مِنْ لَدُنْكَ رَحْمَةً ۚ إِنَّكَ أَنْتَ الْوَهَّابُ', 3, 8, 3, 10, 50, '(راسخانِ در علم، می‌گویند:) «پروردگارا! دلهایمان را، بعد از آنکه ما را هدایت کردی، (از راه حق) منحرف مگردان! و از سوی خود، رحمتی بر ما ببخش، زیرا تو بخشنده‌ای!'),
(302, 302, 'رَبَّنَا إِنَّكَ جَامِعُ النَّاسِ لِيَوْمٍ لَا رَيْبَ فِيهِ ۚ إِنَّ اللَّهَ لَا يُخْلِفُ الْمِيعَادَ', 3, 9, 3, 10, 50, 'پروردگارا! تو مردم را، برای روزی که تردیدی در آن نیست، جمع خواهی کرد؛ زیرا خداوند، از وعده خود، تخلّف نمی‌کند. (ما به تو و رحمت بی‌پایانت، و به وعده رستاخیز و قیامت ایمان داریم.)»'),
(303, 303, 'إِنَّ الَّذِينَ كَفَرُوا لَنْ تُغْنِيَ عَنْهُمْ أَمْوَالُهُمْ وَلَا أَوْلَادُهُمْ مِنَ اللَّهِ شَيْئًا ۖ وَأُولَٰئِكَ هُمْ وَقُودُ النَّارِ', 3, 10, 3, 10, 51, 'ثروتها و فرزندانِ کسانی که کافر شدند، نمی‌تواند آنان را از (عذابِ) خداوند باز دارد؛ (و از کیفر، رهایی بخشد.) و آنان خود، آتشگیره دوزخند.'),
(304, 304, 'كَدَأْبِ آلِ فِرْعَوْنَ وَالَّذِينَ مِنْ قَبْلِهِمْ ۚ كَذَّبُوا بِآيَاتِنَا فَأَخَذَهُمُ اللَّهُ بِذُنُوبِهِمْ ۗ وَاللَّهُ شَدِيدُ الْعِقَابِ', 3, 11, 3, 10, 51, '(عادت آنان در انکار و تحریفِ حقایق،) همچون عادتِ آل فرعون و کسانی است که پیش از آنها بودند؛ آیات ما را تکذیب کردند، و خداوند آنها را به (کیفر) گناهانشان گرفت؛ و خداوند، شدید العقاب است.'),
(305, 305, 'قُلْ لِلَّذِينَ كَفَرُوا سَتُغْلَبُونَ وَتُحْشَرُونَ إِلَىٰ جَهَنَّمَ ۚ وَبِئْسَ الْمِهَادُ', 3, 12, 3, 10, 51, 'به آنها که کافر شدند بگو: «(از پیروزی موقت خود در جنگ اُحُد، شاد نباشید!) بزودی مغلوب خواهید شد؛ (و سپس در رستاخیز) به سوی جهنم، محشور خواهید شد. و چه بد جایگاهی است!'),
(306, 306, 'قَدْ كَانَ لَكُمْ آيَةٌ فِي فِئَتَيْنِ الْتَقَتَا ۖ فِئَةٌ تُقَاتِلُ فِي سَبِيلِ اللَّهِ وَأُخْرَىٰ كَافِرَةٌ يَرَوْنَهُمْ مِثْلَيْهِمْ رَأْيَ الْعَيْنِ ۚ وَاللَّهُ يُؤَيِّدُ بِنَصْرِهِ مَنْ يَشَاءُ ۗ إِنَّ فِي ذَٰلِكَ لَعِبْرَةً لِأُولِي الْأَبْصَارِ', 3, 13, 3, 10, 51, 'در دو گروهی که (در میدان جنگ بدر،) با هم رو به رو شدند، نشانه (و درس عبرتی) برای شما بود: یک گروه، در راه خدا نبرد می‌کرد؛ و جمع دیگری که کافر بود، (در راه شیطان و بت،) در حالی که آنها (گروه مؤمنان) را با چشم خود، دو برابر آنچه بودند، می‌دیدند. (و این خود عاملی برای وحشت و شکست آنها شد.) و خداوند، هر کس را بخواهد (و شایسته بداند)، با یاری خود، تأیید می‌کند. در این، عبرتی است برای بینایان!'),
(307, 307, 'زُيِّنَ لِلنَّاسِ حُبُّ الشَّهَوَاتِ مِنَ النِّسَاءِ وَالْبَنِينَ وَالْقَنَاطِيرِ الْمُقَنْطَرَةِ مِنَ الذَّهَبِ وَالْفِضَّةِ وَالْخَيْلِ الْمُسَوَّمَةِ وَالْأَنْعَامِ وَالْحَرْثِ ۗ ذَٰلِكَ مَتَاعُ الْحَيَاةِ الدُّنْيَا ۖ وَاللَّهُ عِنْدَهُ حُسْنُ الْمَآبِ', 3, 14, 3, 10, 51, 'محبّت امور مادی، از زنان و فرزندان و اموال هنگفت از طلا و نقره و اسبهای ممتاز و چهارپایان و زراعت، در نظر مردم جلوه داده شده است؛ (تا در پرتو آن، آزمایش و تربیت شوند؛ ولی) اینها (در صورتی که هدف نهایی آدمی را تشکیل دهند،) سرمایه زندگی پست (مادی) است؛ و سرانجام نیک (و زندگیِ والا و جاویدان)، نزد خداست.'),
(308, 308, ' قُلْ أَؤُنَبِّئُكُمْ بِخَيْرٍ مِنْ ذَٰلِكُمْ ۚ لِلَّذِينَ اتَّقَوْا عِنْدَ رَبِّهِمْ جَنَّاتٌ تَجْرِي مِنْ تَحْتِهَا الْأَنْهَارُ خَالِدِينَ فِيهَا وَأَزْوَاجٌ مُطَهَّرَةٌ وَرِضْوَانٌ مِنَ اللَّهِ ۗ وَاللَّهُ بَصِيرٌ بِالْعِبَادِ', 3, 15, 3, 11, 51, 'بگو: «آیا شما را از چیزی آگاه کنم که از این (سرمایه‌های مادی)، بهتر است؟» برای کسانی که پرهیزگاری پیشه کرده‌اند، (و از این سرمایه‌ها، در راه مشروع و حق و عدالت، استفاده می‌کنند،) در نزد پروردگارشان (در جهان دیگر)، باغهایی است که نهرها از پایِ درختانش می‌گذرد؛ همیشه در آن خواهند بود؛ و همسرانی پاکیزه، و خشنودی خداوند (نصیب آنهاست). و خدا به (امورِ) بندگان، بیناست.'),
(309, 309, 'الَّذِينَ يَقُولُونَ رَبَّنَا إِنَّنَا آمَنَّا فَاغْفِرْ لَنَا ذُنُوبَنَا وَقِنَا عَذَابَ النَّارِ', 3, 16, 3, 11, 52, 'همان کسانی که می‌گویند: «پروردگارا! ما ایمان آورده‌ایم؛ پس گناهان ما را بیامرز، و ما را از عذابِ آتش، نگاهدار!»'),
(310, 310, 'الصَّابِرِينَ وَالصَّادِقِينَ وَالْقَانِتِينَ وَالْمُنْفِقِينَ وَالْمُسْتَغْفِرِينَ بِالْأَسْحَارِ', 3, 17, 3, 11, 52, 'آنها که (در برابر مشکلات، و در مسیر اطاعت و ترک گناه،) استقامت می‌ورزند، راستگو هستند، (در برابر خدا) خضوع، و (در راه او) انفاق می‌کنند، و در سحرگاهان، استغفار می‌نمایند.'),
(311, 311, 'شَهِدَ اللَّهُ أَنَّهُ لَا إِلَٰهَ إِلَّا هُوَ وَالْمَلَائِكَةُ وَأُولُو الْعِلْمِ قَائِمًا بِالْقِسْطِ ۚ لَا إِلَٰهَ إِلَّا هُوَ الْعَزِيزُ الْحَكِيمُ', 3, 18, 3, 11, 52, 'خداوند، (با ایجادِ نظامِ واحدِ جهانِ هستی،) گواهی می‌دهد که معبودی جز او نیست؛ و فرشتگان و صاحبان دانش، (هر کدام به گونه‌ای بر این مطلب،) گواهی می‌دهند؛ در حالی که (خداوند در تمام عالم) قیام به عدالت دارد؛ معبودی جز او نیست، که هم توانا و هم حکیم است.'),
(312, 312, 'إِنَّ الدِّينَ عِنْدَ اللَّهِ الْإِسْلَامُ ۗ وَمَا اخْتَلَفَ الَّذِينَ أُوتُوا الْكِتَابَ إِلَّا مِنْ بَعْدِ مَا جَاءَهُمُ الْعِلْمُ بَغْيًا بَيْنَهُمْ ۗ وَمَنْ يَكْفُرْ بِآيَاتِ اللَّهِ فَإِنَّ اللَّهَ سَرِيعُ الْحِسَابِ', 3, 19, 3, 11, 52, 'دین در نزد خدا، اسلام (و تسلیم بودن در برابر حق) است. و کسانی که کتاب آسمانی به آنان داده شد، اختلافی (در آن) ایجاد نکردند، مگر بعد از آگاهی و علم، آن هم به خاطر ظلم و ستم در میان خود؛ و هر کس به آیات خدا کفر ورزد، (خدا به حساب او می‌رسد؛ زیرا) خداوند، سریع الحساب است.'),
(313, 313, 'فَإِنْ حَاجُّوكَ فَقُلْ أَسْلَمْتُ وَجْهِيَ لِلَّهِ وَمَنِ اتَّبَعَنِ ۗ وَقُلْ لِلَّذِينَ أُوتُوا الْكِتَابَ وَالْأُمِّيِّينَ أَأَسْلَمْتُمْ ۚ فَإِنْ أَسْلَمُوا فَقَدِ اهْتَدَوْا ۖ وَإِنْ تَوَلَّوْا فَإِنَّمَا عَلَيْكَ الْبَلَاغُ ۗ وَاللَّهُ بَصِيرٌ بِالْعِبَادِ', 3, 20, 3, 11, 52, 'اگر با تو، به گفتگو و ستیز برخیزند، (با آنها مجادله نکن! و) بگو: «من و پیروانم، در برابر خداوند (و فرمان او)، تسلیم شده‌ایم.» و به آنها که اهل کتاب هستند = یهود و نصاری‌ و بی‌سوادان = مشرکان‌ بگو: «آیا شما هم تسلیم شده‌اید؟» اگر (در برابر فرمان و منطق حق، تسلیم شوند، هدایت می‌یابند؛ و اگر سرپیچی کنند، (نگران مباش! زیرا) بر تو، تنها ابلاغ (رسالت) است؛ و خدا نسبت به (اعمال و عقاید) بندگان، بیناست.'),
(314, 314, 'إِنَّ الَّذِينَ يَكْفُرُونَ بِآيَاتِ اللَّهِ وَيَقْتُلُونَ النَّبِيِّينَ بِغَيْرِ حَقٍّ وَيَقْتُلُونَ الَّذِينَ يَأْمُرُونَ بِالْقِسْطِ مِنَ النَّاسِ فَبَشِّرْهُمْ بِعَذَابٍ أَلِيمٍ', 3, 21, 3, 11, 52, 'کسانی که نسبت به آیات خدا کفر می‌ورزند و پیامبران را بناحق می‌کشند، و (نیز) مردمی را که امر به عدالت می‌کنند به قتل می‌رسانند، و به کیفر دردناک (الهی) بشارت ده!'),
(315, 315, 'أُولَٰئِكَ الَّذِينَ حَبِطَتْ أَعْمَالُهُمْ فِي الدُّنْيَا وَالْآخِرَةِ وَمَا لَهُمْ مِنْ نَاصِرِينَ', 3, 22, 3, 11, 52, 'آنها کسانی هستند که اعمال (نیکشان، به خاطر این گناهان بزرگ،) در دنیا و آخرت تباه شده، و یاور و مددکار (و شفاعت کننده‌ای) ندارند.'),
(316, 316, 'أَلَمْ تَرَ إِلَى الَّذِينَ أُوتُوا نَصِيبًا مِنَ الْكِتَابِ يُدْعَوْنَ إِلَىٰ كِتَابِ اللَّهِ لِيَحْكُمَ بَيْنَهُمْ ثُمَّ يَتَوَلَّىٰ فَرِيقٌ مِنْهُمْ وَهُمْ مُعْرِضُونَ', 3, 23, 3, 11, 53, 'آیا ندیدی کسانی را که بهره‌ای از کتاب (آسمانی) داشتند، به سوی کتاب الهی دعوت شدند تا در میان آنها داوری کند، سپس گروهی از آنان، (با علم و آگاهی،) روی می‌گردانند، در حالی که (از قبول حق) اعراض دارند؟'),
(317, 317, 'ذَٰلِكَ بِأَنَّهُمْ قَالُوا لَنْ تَمَسَّنَا النَّارُ إِلَّا أَيَّامًا مَعْدُودَاتٍ ۖ وَغَرَّهُمْ فِي دِينِهِمْ مَا كَانُوا يَفْتَرُونَ', 3, 24, 3, 11, 53, 'این عمل آنها، به خاطر آن است که می‌گفتند: «آتش (دوزخ)، جز چند روزی به ما نمی‌رسد. (و کیفر ما، به خاطر امتیازی که بر اقوام دیگر داریم، بسیار محدود است.)» این افترا (و دروغی که به خدا بسته بودند،) آنها را در دینشان مغرور ساخت (و گرفتار انواع گناهان شدند).'),
(318, 318, 'فَكَيْفَ إِذَا جَمَعْنَاهُمْ لِيَوْمٍ لَا رَيْبَ فِيهِ وَوُفِّيَتْ كُلُّ نَفْسٍ مَا كَسَبَتْ وَهُمْ لَا يُظْلَمُونَ', 3, 25, 3, 11, 53, 'پس چه گونه خواهند بود هنگامی که آنها را برای روزی که شکّی در آن نیست = روز رستاخیز جمع کنیم، و به هر کس، آنچه (از اعمال برای خود) فراهم کرده، بطور کامل داده شود؟ و به آنها ستم نخواهد شد (زیرا محصول اعمال خود را می‌چینند).'),
(319, 319, 'قُلِ اللَّهُمَّ مَالِكَ الْمُلْكِ تُؤْتِي الْمُلْكَ مَنْ تَشَاءُ وَتَنْزِعُ الْمُلْكَ مِمَّنْ تَشَاءُ وَتُعِزُّ مَنْ تَشَاءُ وَتُذِلُّ مَنْ تَشَاءُ ۖ بِيَدِكَ الْخَيْرُ ۖ إِنَّكَ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ', 3, 26, 3, 11, 53, 'بگو: «بارالها! مالک حکومتها تویی؛ به هر کس بخواهی، حکومت می‌بخشی؛ و از هر کس بخواهی، حکومت را می‌گیری؛ هر کس را بخواهی، عزت می‌دهی؛ و هر که را بخواهی خوار می‌کنی. تمام خوبیها به دست توست؛ تو بر هر چیزی قادری.'),
(320, 320, 'تُولِجُ اللَّيْلَ فِي النَّهَارِ وَتُولِجُ النَّهَارَ فِي اللَّيْلِ ۖ وَتُخْرِجُ الْحَيَّ مِنَ الْمَيِّتِ وَتُخْرِجُ الْمَيِّتَ مِنَ الْحَيِّ ۖ وَتَرْزُقُ مَنْ تَشَاءُ بِغَيْرِ حِسَابٍ', 3, 27, 3, 11, 53, 'شب را در روز داخل می‌کنی، و روز را در شب؛ و زنده را از مرده بیرون می‌آوری، و مرده را زنده؛ و به هر کس بخواهی، بدون حساب، روزی می‌بخشی.»'),
(321, 321, 'لَا يَتَّخِذِ الْمُؤْمِنُونَ الْكَافِرِينَ أَوْلِيَاءَ مِنْ دُونِ الْمُؤْمِنِينَ ۖ وَمَنْ يَفْعَلْ ذَٰلِكَ فَلَيْسَ مِنَ اللَّهِ فِي شَيْءٍ إِلَّا أَنْ تَتَّقُوا مِنْهُمْ تُقَاةً ۗ وَيُحَذِّرُكُمُ اللَّهُ نَفْسَهُ ۗ وَإِلَى اللَّهِ الْمَصِيرُ', 3, 28, 3, 11, 53, 'افراد باایمان نباید به جای مؤمنان، کافران را دوست و سرپرست خود انتخاب کنند؛ و هر کس چنین کند، هیچ رابطه‌ای با خدا ندارد (و پیوند او بکلّی از خدا گسسته می‌شود)؛ مگر اینکه از آنها بپرهیزید (و به خاطر هدفهای مهمتری تقیّه کنید). خداوند شما را از (نافرمانی) خود، برحذر می‌دارد؛ و بازگشت (شما) به سوی خداست.');
INSERT INTO `Quran` (`rowid`, `id`, `Arabic`, `Chapter`, `Verse`, `Juz`, `Hizb`, `Page`, `tr`) VALUES
(322, 322, 'قُلْ إِنْ تُخْفُوا مَا فِي صُدُورِكُمْ أَوْ تُبْدُوهُ يَعْلَمْهُ اللَّهُ ۗ وَيَعْلَمُ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ ۗ وَاللَّهُ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ', 3, 29, 3, 11, 53, 'بگو: «اگر آنچه را در سینه‌های شماست، پنهان دارید یا آشکار کنید، خداوند آن را می‌داند؛ و (نیز) از آنچه در آسمانها و زمین است، آگاه می‌باشد؛ و خداوند بر هر چیزی تواناست.'),
(323, 323, 'يَوْمَ تَجِدُ كُلُّ نَفْسٍ مَا عَمِلَتْ مِنْ خَيْرٍ مُحْضَرًا وَمَا عَمِلَتْ مِنْ سُوءٍ تَوَدُّ لَوْ أَنَّ بَيْنَهَا وَبَيْنَهُ أَمَدًا بَعِيدًا ۗ وَيُحَذِّرُكُمُ اللَّهُ نَفْسَهُ ۗ وَاللَّهُ رَءُوفٌ بِالْعِبَادِ', 3, 30, 3, 11, 54, 'روزی که هر کس، آنچه را از کار نیک انجام داده، حاضر می‌بیند؛ و آرزو می‌کند میان او، و آنچه از اعمال بد انجام داده، فاصله زمانیِ زیادی باشد. خداوند شما را از (نافرمانی) خودش، برحذر می‌دارد؛ و (در عین حال،) خدا نسبت به همه بندگان، مهربان است.»'),
(324, 324, 'قُلْ إِنْ كُنْتُمْ تُحِبُّونَ اللَّهَ فَاتَّبِعُونِي يُحْبِبْكُمُ اللَّهُ وَيَغْفِرْ لَكُمْ ذُنُوبَكُمْ ۗ وَاللَّهُ غَفُورٌ رَحِيمٌ', 3, 31, 3, 11, 54, 'بگو: «اگر خدا را دوست می‌دارید، از من پیروی کنید! تا خدا (نیز) شما را دوست بدارد؛ و گناهانتان را ببخشد؛ و خدا آمرزنده مهربان است.»'),
(325, 325, 'قُلْ أَطِيعُوا اللَّهَ وَالرَّسُولَ ۖ فَإِنْ تَوَلَّوْا فَإِنَّ اللَّهَ لَا يُحِبُّ الْكَافِرِينَ', 3, 32, 3, 11, 54, 'بگو: «از خدا و فرستاده (او)، اطاعت کنید! و اگر سرپیچی کنید، خداوند کافران را دوست نمی‌دارد.»'),
(326, 326, ' إِنَّ اللَّهَ اصْطَفَىٰ آدَمَ وَنُوحًا وَآلَ إِبْرَاهِيمَ وَآلَ عِمْرَانَ عَلَى الْعَالَمِينَ', 3, 33, 3, 11, 54, 'خداوند، آدم و نوح و آل ابراهیم و آل عمران را بر جهانیان برتری داد.'),
(327, 327, 'ذُرِّيَّةً بَعْضُهَا مِنْ بَعْضٍ ۗ وَاللَّهُ سَمِيعٌ عَلِيمٌ', 3, 34, 3, 11, 54, 'آنها فرزندان و (دودمانی) بودند که (از نظر پاکی و تقوا و فضیلت،) بعضی از بعض دیگر گرفته شده بودند؛ و خداوند، شنوا و داناست (و از کوششهای آنها در مسیر رسالت خود، آگاه می‌باشد).'),
(328, 328, 'إِذْ قَالَتِ امْرَأَتُ عِمْرَانَ رَبِّ إِنِّي نَذَرْتُ لَكَ مَا فِي بَطْنِي مُحَرَّرًا فَتَقَبَّلْ مِنِّي ۖ إِنَّكَ أَنْتَ السَّمِيعُ الْعَلِيمُ', 3, 35, 3, 11, 54, '(به یاد آورید) هنگامی را که همسرِ «عمران» گفت: «خداوندا! آنچه را در رحم دارم، برای تو نذر کردم، که «محرَّر» (و آزاد، برای خدمت خانه تو) باشد. از من بپذیر، که تو شنوا و دانایی!'),
(329, 329, 'فَلَمَّا وَضَعَتْهَا قَالَتْ رَبِّ إِنِّي وَضَعْتُهَا أُنْثَىٰ وَاللَّهُ أَعْلَمُ بِمَا وَضَعَتْ وَلَيْسَ الذَّكَرُ كَالْأُنْثَىٰ ۖ وَإِنِّي سَمَّيْتُهَا مَرْيَمَ وَإِنِّي أُعِيذُهَا بِكَ وَذُرِّيَّتَهَا مِنَ الشَّيْطَانِ الرَّجِيمِ', 3, 36, 3, 11, 54, 'ولی هنگامی که او را به دنیا آورد، (و او را دختر یافت،) گفت: «خداوندا! من او را دختر آوردم -ولی خدا از آنچه او به دنیا آورده بود، آگاهتر بود- و پسر، همانند دختر نیست. (دختر نمی‌تواند وظیفه خدمتگزاری معبد را همانند پسر انجام دهد.) من او را مریم نام گذاردم؛ و او و فرزندانش را از (وسوسه‌های) شیطان رانده شده، در پناه تو قرار می‌دهم.»'),
(330, 330, 'فَتَقَبَّلَهَا رَبُّهَا بِقَبُولٍ حَسَنٍ وَأَنْبَتَهَا نَبَاتًا حَسَنًا وَكَفَّلَهَا زَكَرِيَّا ۖ كُلَّمَا دَخَلَ عَلَيْهَا زَكَرِيَّا الْمِحْرَابَ وَجَدَ عِنْدَهَا رِزْقًا ۖ قَالَ يَا مَرْيَمُ أَنَّىٰ لَكِ هَٰذَا ۖ قَالَتْ هُوَ مِنْ عِنْدِ اللَّهِ ۖ إِنَّ اللَّهَ يَرْزُقُ مَنْ يَشَاءُ بِغَيْرِ حِسَابٍ', 3, 37, 3, 11, 54, 'خداوند، او = مریم‌ را به طرز نیکویی پذیرفت؛ و به طرز شایسته‌ای، (نهال وجود) او را رویانید (و پرورش داد)؛ و کفالت او را به «زکریا» سپرد. هر زمان زکریا وارد محراب او می‌شد، غذای مخصوصی در آن جا می‌دید. از او پرسید: «ای مریم! این را از کجا آورده‌ای؟!» گفت: «این از سوی خداست. خداوند به هر کس بخواهد، بی حساب روزی می‌دهد.»'),
(331, 331, 'هُنَالِكَ دَعَا زَكَرِيَّا رَبَّهُ ۖ قَالَ رَبِّ هَبْ لِي مِنْ لَدُنْكَ ذُرِّيَّةً طَيِّبَةً ۖ إِنَّكَ سَمِيعُ الدُّعَاءِ', 3, 38, 3, 11, 55, 'در آنجا بود که زکریا، (با مشاهده آن همه شایستگی در مریم،) پروردگار خویش را خواند و عرض کرد: «خداوندا! از طرف خود، فرزند پاکیزه‌ای (نیز) به من عطا فرما، که تو دعا را می‌شنوی!»'),
(332, 332, 'فَنَادَتْهُ الْمَلَائِكَةُ وَهُوَ قَائِمٌ يُصَلِّي فِي الْمِحْرَابِ أَنَّ اللَّهَ يُبَشِّرُكَ بِيَحْيَىٰ مُصَدِّقًا بِكَلِمَةٍ مِنَ اللَّهِ وَسَيِّدًا وَحَصُورًا وَنَبِيًّا مِنَ الصَّالِحِينَ', 3, 39, 3, 11, 55, 'و هنگامی که او در محراب ایستاده، مشغول نیایش بود، فرشتگان او را صدا زدند که: «خدا تو را به» یحیی «بشارت می‌دهد؛ (کسی) که کلمه خدا = مسیح‌ را تصدیق می‌کند؛ و رهبر خواهد بود؛ و از هوسهای سرکش برکنار، و پیامبری از صالحان است.»'),
(333, 333, 'قَالَ رَبِّ أَنَّىٰ يَكُونُ لِي غُلَامٌ وَقَدْ بَلَغَنِيَ الْكِبَرُ وَامْرَأَتِي عَاقِرٌ ۖ قَالَ كَذَٰلِكَ اللَّهُ يَفْعَلُ مَا يَشَاءُ', 3, 40, 3, 11, 55, 'او عرض کرد: «پروردگارا! چگونه ممکن است فرزندی برای من باشد، در حالی که پیری به سراغ من آمده، و همسرم نازاست؟!» فرمود: «بدین گونه خداوند هر کاری را بخواهد انجام می‌دهد.»'),
(334, 334, 'قَالَ رَبِّ اجْعَلْ لِي آيَةً ۖ قَالَ آيَتُكَ أَلَّا تُكَلِّمَ النَّاسَ ثَلَاثَةَ أَيَّامٍ إِلَّا رَمْزًا ۗ وَاذْكُرْ رَبَّكَ كَثِيرًا وَسَبِّحْ بِالْعَشِيِّ وَالْإِبْكَارِ', 3, 41, 3, 11, 55, '(زکریا) عرض کرد: «پروردگارا! نشانه‌ای برای من قرار ده!» گفت: «نشانه تو آن است که سه روز، جز به اشاره و رمز، با مردم سخن نخواهی گفت. (و زبان تو، بدون هیچ علّت ظاهری، برای گفتگو با مردم از کار می‌افتد.) پروردگار خود را (به شکرانه این نعمت بزرگ،) بسیار یاد کن! و به هنگام صبح و شام، او را تسبیح بگو!'),
(335, 335, 'وَإِذْ قَالَتِ الْمَلَائِكَةُ يَا مَرْيَمُ إِنَّ اللَّهَ اصْطَفَاكِ وَطَهَّرَكِ وَاصْطَفَاكِ عَلَىٰ نِسَاءِ الْعَالَمِينَ', 3, 42, 3, 11, 55, 'و (به یاد آورید) هنگامی را که فرشتگان گفتند: «ای مریم! خدا تو را برگزیده و پاک ساخته؛ و بر تمام زنان جهان، برتری داده است.'),
(336, 336, 'يَا مَرْيَمُ اقْنُتِي لِرَبِّكِ وَاسْجُدِي وَارْكَعِي مَعَ الرَّاكِعِينَ', 3, 43, 3, 11, 55, 'ای مریم! (به شکرانه این نعمت) برای پروردگار خود، خضوع کن و سجده بجا آور! و با رکوع‌کنندگان، رکوع کن!'),
(337, 337, 'ذَٰلِكَ مِنْ أَنْبَاءِ الْغَيْبِ نُوحِيهِ إِلَيْكَ ۚ وَمَا كُنْتَ لَدَيْهِمْ إِذْ يُلْقُونَ أَقْلَامَهُمْ أَيُّهُمْ يَكْفُلُ مَرْيَمَ وَمَا كُنْتَ لَدَيْهِمْ إِذْ يَخْتَصِمُونَ', 3, 44, 3, 11, 55, '(ای پیامبر!) این، از خبرهای غیبی است که به تو وحی می‌کنیم؛ و تو در آن هنگام که قلمهای خود را (برای قرعه‌کشی) به آب می‌افکندند تا کدامیک کفالت و سرپرستی مریم را عهده‌دار شود، و (نیز) به هنگامی که (دانشمندان بنی اسرائیل، برای کسب افتخار سرپرستی او،) با هم کشمکش داشتند، حضور نداشتی؛ و همه اینها، از راه وحی به تو گفته شد.)'),
(338, 338, 'إِذْ قَالَتِ الْمَلَائِكَةُ يَا مَرْيَمُ إِنَّ اللَّهَ يُبَشِّرُكِ بِكَلِمَةٍ مِنْهُ اسْمُهُ الْمَسِيحُ عِيسَى ابْنُ مَرْيَمَ وَجِيهًا فِي الدُّنْيَا وَالْآخِرَةِ وَمِنَ الْمُقَرَّبِينَ', 3, 45, 3, 11, 55, '(به یاد آورید) هنگامی را که فرشتگان گفتند: «ای مریم! خداوند تو را به کلمه‌ای = وجود باعظمتی‌ از طرف خودش بشارت می‌دهد که نامش «مسیح، عیسی پسر مریم» است؛ در حالی که در این جهان و جهان دیگر، صاحب شخصیّت خواهد بود؛ و از مقرّبان (الهی) است.'),
(339, 339, 'وَيُكَلِّمُ النَّاسَ فِي الْمَهْدِ وَكَهْلًا وَمِنَ الصَّالِحِينَ', 3, 46, 3, 11, 56, 'و با مردم، در گاهواره و در حالت کهولت (و میانسال شدن) سخن خواهد گفت؛ و از شایستگان است.»'),
(340, 340, 'قَالَتْ رَبِّ أَنَّىٰ يَكُونُ لِي وَلَدٌ وَلَمْ يَمْسَسْنِي بَشَرٌ ۖ قَالَ كَذَٰلِكِ اللَّهُ يَخْلُقُ مَا يَشَاءُ ۚ إِذَا قَضَىٰ أَمْرًا فَإِنَّمَا يَقُولُ لَهُ كُنْ فَيَكُونُ', 3, 47, 3, 11, 56, '(مریم) گفت: «پروردگارا! چگونه ممکن است فرزندی برای من باشد، در حالی که انسانی با من تماس نگرفته است؟!» فرمود: «خداوند، این‌گونه هر چه را بخواهد می‌آفریند! هنگامی که چیزی را مقرّر دارد (و فرمان هستی آن را صادر کند)، فقط به آن می‌گوید: «موجود باش!» آن نیز فوراً موجود می‌شود.'),
(341, 341, 'وَيُعَلِّمُهُ الْكِتَابَ وَالْحِكْمَةَ وَالتَّوْرَاةَ وَالْإِنْجِيلَ', 3, 48, 3, 11, 56, 'و به او، کتاب و دانش و تورات و انجیل، می‌آموزد.'),
(342, 342, 'وَرَسُولًا إِلَىٰ بَنِي إِسْرَائِيلَ أَنِّي قَدْ جِئْتُكُمْ بِآيَةٍ مِنْ رَبِّكُمْ ۖ أَنِّي أَخْلُقُ لَكُمْ مِنَ الطِّينِ كَهَيْئَةِ الطَّيْرِ فَأَنْفُخُ فِيهِ فَيَكُونُ طَيْرًا بِإِذْنِ اللَّهِ ۖ وَأُبْرِئُ الْأَكْمَهَ وَالْأَبْرَصَ وَأُحْيِي الْمَوْتَىٰ بِإِذْنِ اللَّهِ ۖ وَأُنَبِّئُكُمْ بِمَا تَأْكُلُونَ وَمَا تَدَّخِرُونَ فِي بُيُوتِكُمْ ۚ إِنَّ فِي ذَٰلِكَ لَآيَةً لَكُمْ إِنْ كُنْتُمْ مُؤْمِنِينَ', 3, 49, 3, 11, 56, 'و (او را به عنوان) رسول و فرستاده به سوی بنی اسرائیل (قرار داده، که به آنها می‌گوید:) من نشانه‌ای از طرف پروردگار شما، برایتان آورده‌ام؛ من از گِل، چیزی به شکل پرنده می‌سازم؛ سپس در آن می‌دمم و به فرمان خدا، پرنده‌ای می‌گردد. و به اذن خدا، کورِ مادرزاد و مبتلایان به برص = پیسی‌ را بهبودی می‌بخشم؛ و مردگان را به اذن خدا زنده می‌کنم؛ و از آنچه می‌خورید، و در خانه‌های خود ذخیره می‌کنید، به شما خبر می‌دهم؛ مسلماً در اینها، نشانه‌ای برای شماست، اگر ایمان داشته باشید!'),
(343, 343, 'وَمُصَدِّقًا لِمَا بَيْنَ يَدَيَّ مِنَ التَّوْرَاةِ وَلِأُحِلَّ لَكُمْ بَعْضَ الَّذِي حُرِّمَ عَلَيْكُمْ ۚ وَجِئْتُكُمْ بِآيَةٍ مِنْ رَبِّكُمْ فَاتَّقُوا اللَّهَ وَأَطِيعُونِ', 3, 50, 3, 11, 56, 'و آنچه را پیش از من از تورات بوده، تصدیق می‌کنم؛ و (آمده‌ام) تا پاره‌ای از چیزهایی را که (بر اثر ظلم و گناه،) بر شما حرام شده، (مانند گوشت بعضی از چهارپایان و ماهیها،) حلال کنم؛ و نشانه‌ای از طرف پروردگار شما، برایتان آورده‌ام؛ پس از خدا بترسید، و مرا اطاعت کنید!'),
(344, 344, 'إِنَّ اللَّهَ رَبِّي وَرَبُّكُمْ فَاعْبُدُوهُ ۗ هَٰذَا صِرَاطٌ مُسْتَقِيمٌ', 3, 51, 3, 11, 56, 'خداوند، پروردگار من و شماست؛ او را بپرستید (نه من، و نه چیز دیگر را)! این است راه راست!»'),
(345, 345, ' فَلَمَّا أَحَسَّ عِيسَىٰ مِنْهُمُ الْكُفْرَ قَالَ مَنْ أَنْصَارِي إِلَى اللَّهِ ۖ قَالَ الْحَوَارِيُّونَ نَحْنُ أَنْصَارُ اللَّهِ آمَنَّا بِاللَّهِ وَاشْهَدْ بِأَنَّا مُسْلِمُونَ', 3, 52, 3, 12, 56, 'هنگامی که عیسی از آنان احساس کفر (و مخالفت) کرد، گفت: «کیست که یاور من به سوی خدا (برای تبلیغ آیین او) گردد؟» حواریان = شاگردانِ مخصوصِ او گفتند: «ما یاوران خداییم؛ به خدا ایمان آوردیم؛ و تو (نیز) گواه باش که ما اسلام آورده‌ایم.'),
(346, 346, 'رَبَّنَا آمَنَّا بِمَا أَنْزَلْتَ وَاتَّبَعْنَا الرَّسُولَ فَاكْتُبْنَا مَعَ الشَّاهِدِينَ', 3, 53, 3, 12, 57, 'پروردگارا! به آنچه نازل کرده‌ای، ایمان آوردیم و از فرستاده (تو) پیروی نمودیم؛ ما را در زمره گواهان بنویس!»'),
(347, 347, 'وَمَكَرُوا وَمَكَرَ اللَّهُ ۖ وَاللَّهُ خَيْرُ الْمَاكِرِينَ', 3, 54, 3, 12, 57, 'و (یهود و دشمنان مسیح، برای نابودی او و آیینش،) نقشه کشیدند؛ و خداوند (بر حفظ او و آیینش،) چاره‌جویی کرد؛ و خداوند، بهترین چاره‌جویان است.'),
(348, 348, 'إِذْ قَالَ اللَّهُ يَا عِيسَىٰ إِنِّي مُتَوَفِّيكَ وَرَافِعُكَ إِلَيَّ وَمُطَهِّرُكَ مِنَ الَّذِينَ كَفَرُوا وَجَاعِلُ الَّذِينَ اتَّبَعُوكَ فَوْقَ الَّذِينَ كَفَرُوا إِلَىٰ يَوْمِ الْقِيَامَةِ ۖ ثُمَّ إِلَيَّ مَرْجِعُكُمْ فَأَحْكُمُ بَيْنَكُمْ فِيمَا كُنْتُمْ فِيهِ تَخْتَلِفُونَ', 3, 55, 3, 12, 57, '(به یاد آورید) هنگامی را که خدا به عیسی فرمود: «من تو را برمی‌گیرم و به سوی خود، بالا می‌برم و تو را از کسانی که کافر شدند، پاک می‌سازم؛ و کسانی را که از تو پیروی کردند، تا روز رستاخیز، برتر از کسانی که کافر شدند، قرارمی‌دهم؛ سپس بازگشت شما به سوی من است و در میان شما، در آنچه اختلاف داشتید، داوری می‌کنم.'),
(349, 349, 'فَأَمَّا الَّذِينَ كَفَرُوا فَأُعَذِّبُهُمْ عَذَابًا شَدِيدًا فِي الدُّنْيَا وَالْآخِرَةِ وَمَا لَهُمْ مِنْ نَاصِرِينَ', 3, 56, 3, 12, 57, 'امّا آنها که کافر شدند، (و پس از شناختن حق، آن را انکار کردند،) در دنیا و آخرت، آنان را مجازات دردناکی خواهم کرد؛ و برای آنها، یاورانی نیست.'),
(350, 350, 'وَأَمَّا الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ فَيُوَفِّيهِمْ أُجُورَهُمْ ۗ وَاللَّهُ لَا يُحِبُّ الظَّالِمِينَ', 3, 57, 3, 12, 57, 'امّا آنها که ایمان آوردند، و اعمال صالح انجام دادند، خداوند پاداش آنان را بطور کامل خواهد داد؛ و خداوند، ستمکاران را دوست نمی‌دارد.»'),
(351, 351, 'ذَٰلِكَ نَتْلُوهُ عَلَيْكَ مِنَ الْآيَاتِ وَالذِّكْرِ الْحَكِيمِ', 3, 58, 3, 12, 57, 'اینها را که بر تو می‌خوانیم، از نشانه‌ها (ی حقّانیّت تو) است، و یادآوری حکیمانه است.'),
(352, 352, 'إِنَّ مَثَلَ عِيسَىٰ عِنْدَ اللَّهِ كَمَثَلِ آدَمَ ۖ خَلَقَهُ مِنْ تُرَابٍ ثُمَّ قَالَ لَهُ كُنْ فَيَكُونُ', 3, 59, 3, 12, 57, 'مَثَل عیسی در نزد خدا، همچون آدم است؛ که او را از خاک آفرید، و سپس به او فرمود: «موجود باش!» او هم فوراً موجود شد. (بنابر این، ولادت مسیح بدون پدر، هرگز دلیل بر الوهیّت او نیست.)'),
(353, 353, 'الْحَقُّ مِنْ رَبِّكَ فَلَا تَكُنْ مِنَ الْمُمْتَرِينَ', 3, 60, 3, 12, 57, 'اینها حقیقتی است از جانب پروردگار تو؛ بنابر این، از تردید کنندگان مباش!'),
(354, 354, 'فَمَنْ حَاجَّكَ فِيهِ مِنْ بَعْدِ مَا جَاءَكَ مِنَ الْعِلْمِ فَقُلْ تَعَالَوْا نَدْعُ أَبْنَاءَنَا وَأَبْنَاءَكُمْ وَنِسَاءَنَا وَنِسَاءَكُمْ وَأَنْفُسَنَا وَأَنْفُسَكُمْ ثُمَّ نَبْتَهِلْ فَنَجْعَلْ لَعْنَتَ اللَّهِ عَلَى الْكَاذِبِينَ', 3, 61, 3, 12, 57, 'هرگاه بعد از علم و دانشی که (در باره مسیح) به تو رسیده، (باز) کسانی با تو به محاجّه و ستیز برخیزند، به آنها بگو: «بیایید ما فرزندان خود را دعوت کنیم، شما هم فرزندان خود را؛ ما زنان خویش را دعوت نماییم، شما هم زنان خود را؛ ما از نفوس خود دعوت کنیم، شما هم از نفوس خود؛ آنگاه مباهله کنیم؛ و لعنت خدا را بر دروغگویان قرار دهیم.'),
(355, 355, 'إِنَّ هَٰذَا لَهُوَ الْقَصَصُ الْحَقُّ ۚ وَمَا مِنْ إِلَٰهٍ إِلَّا اللَّهُ ۚ وَإِنَّ اللَّهَ لَهُوَ الْعَزِيزُ الْحَكِيمُ', 3, 62, 3, 12, 58, 'این همان سرگذشتِ واقعی (مسیح) است. (و ادعاهایی همچون الوهیّت او، یا فرزند خدا بودنش، بی‌اساس است.) و هیچ معبودی، جز خداوند یگانه نیست؛ و خداوند توانا و حکیم است.'),
(356, 356, 'فَإِنْ تَوَلَّوْا فَإِنَّ اللَّهَ عَلِيمٌ بِالْمُفْسِدِينَ', 3, 63, 3, 12, 58, 'اگر (با این همه شواهد روشن، باز هم از پذیرش حق) روی گردانند، (بدان که طالب حق نیستند؛ و) خداوند از مفسده‌جویان، آگاه است.'),
(357, 357, 'قُلْ يَا أَهْلَ الْكِتَابِ تَعَالَوْا إِلَىٰ كَلِمَةٍ سَوَاءٍ بَيْنَنَا وَبَيْنَكُمْ أَلَّا نَعْبُدَ إِلَّا اللَّهَ وَلَا نُشْرِكَ بِهِ شَيْئًا وَلَا يَتَّخِذَ بَعْضُنَا بَعْضًا أَرْبَابًا مِنْ دُونِ اللَّهِ ۚ فَإِنْ تَوَلَّوْا فَقُولُوا اشْهَدُوا بِأَنَّا مُسْلِمُونَ', 3, 64, 3, 12, 58, 'بگو: «ای اهل کتاب! بیایید به سوی سخنی که میان ما و شما یکسان است؛ که جز خداوند یگانه را نپرستیم و چیزی را همتای او قرار ندهیم؛ و بعضی از ما، بعضی دیگر را -غیر از خدای یگانه- به خدایی نپذیرد.» هرگاه (از این دعوت،) سرباز زنند، بگویید: «گواه باشید که ما مسلمانیم!»'),
(358, 358, 'يَا أَهْلَ الْكِتَابِ لِمَ تُحَاجُّونَ فِي إِبْرَاهِيمَ وَمَا أُنْزِلَتِ التَّوْرَاةُ وَالْإِنْجِيلُ إِلَّا مِنْ بَعْدِهِ ۚ أَفَلَا تَعْقِلُونَ', 3, 65, 3, 12, 58, 'ای اهل کتاب! چرا درباره ابراهیم، گفتگو و نزاع می‌کنید (و هر کدام، او را پیرو آیین خودتان معرفی می‌نمایید)؟! در حالی که تورات و انجیل، بعد از او نازل شده است! آیا اندیشه نمی‌کنید؟!'),
(359, 359, 'هَا أَنْتُمْ هَٰؤُلَاءِ حَاجَجْتُمْ فِيمَا لَكُمْ بِهِ عِلْمٌ فَلِمَ تُحَاجُّونَ فِيمَا لَيْسَ لَكُمْ بِهِ عِلْمٌ ۚ وَاللَّهُ يَعْلَمُ وَأَنْتُمْ لَا تَعْلَمُونَ', 3, 66, 3, 12, 58, 'شما کسانی هستید که درباره آنچه نسبت به آن آگاه بودید، گفتگو و ستیز کردید؛ چرا درباره آنچه آگاه نیستید، گفتگو می‌کنید؟! و خدا می‌داند، و شما نمی‌دانید.'),
(360, 360, 'مَا كَانَ إِبْرَاهِيمُ يَهُودِيًّا وَلَا نَصْرَانِيًّا وَلَٰكِنْ كَانَ حَنِيفًا مُسْلِمًا وَمَا كَانَ مِنَ الْمُشْرِكِينَ', 3, 67, 3, 12, 58, 'ابراهیم نه یهودی بود و نه نصرانی؛ بلکه موحّدی خالص و مسلمان بود؛ و هرگز از مشرکان نبود.'),
(361, 361, 'إِنَّ أَوْلَى النَّاسِ بِإِبْرَاهِيمَ لَلَّذِينَ اتَّبَعُوهُ وَهَٰذَا النَّبِيُّ وَالَّذِينَ آمَنُوا ۗ وَاللَّهُ وَلِيُّ الْمُؤْمِنِينَ', 3, 68, 3, 12, 58, 'سزاوارترین مردم به ابراهیم، آنها هستند که از او پیروی کردند، و (در زمان و عصر او، به مکتب او وفادار بودند؛ همچنین) این پیامبر و کسانی که (به او) ایمان آورده‌اند (از همه سزاوارترند)؛ و خداوند، ولیّ و سرپرست مؤمنان است.'),
(362, 362, 'وَدَّتْ طَائِفَةٌ مِنْ أَهْلِ الْكِتَابِ لَوْ يُضِلُّونَكُمْ وَمَا يُضِلُّونَ إِلَّا أَنْفُسَهُمْ وَمَا يَشْعُرُونَ', 3, 69, 3, 12, 58, 'جمعی از اهل کتاب (از یهود)، دوست داشتند (و آرزو می‌کردند) شما را گمراه کنند؛ (امّا آنها باید بدانند که نمی‌توانند شما را گمراه سازند،) آنها گمراه نمی‌کنند مگر خودشان را، و نمی‌فهمند!'),
(363, 363, 'يَا أَهْلَ الْكِتَابِ لِمَ تَكْفُرُونَ بِآيَاتِ اللَّهِ وَأَنْتُمْ تَشْهَدُونَ', 3, 70, 3, 12, 58, 'ای اهل کتاب! چرا به آیات خدا کافر می‌شوید، در حالی که (به درستی آن) گواهی می‌دهید؟!'),
(364, 364, 'يَا أَهْلَ الْكِتَابِ لِمَ تَلْبِسُونَ الْحَقَّ بِالْبَاطِلِ وَتَكْتُمُونَ الْحَقَّ وَأَنْتُمْ تَعْلَمُونَ', 3, 71, 3, 12, 59, 'ای اهل کتاب! چرا حق را با باطل (می‌زمیزید و) مشتبه می‌کنید (تا دیگران نفهمند و گمراه شوند)، و حقیقت را پوشیده می‌دارید در حالی که می‌دانید؟!'),
(365, 365, 'وَقَالَتْ طَائِفَةٌ مِنْ أَهْلِ الْكِتَابِ آمِنُوا بِالَّذِي أُنْزِلَ عَلَى الَّذِينَ آمَنُوا وَجْهَ النَّهَارِ وَاكْفُرُوا آخِرَهُ لَعَلَّهُمْ يَرْجِعُونَ', 3, 72, 3, 12, 59, 'و جمعی از اهل کتاب (از یهود) گفتند: «(بروید در ظاهر) به آنچه بر مؤمنان نازل شده، در آغاز روز ایمان بیاورید؛ و در پایان روز، کافر شوید (و باز گردید)! شاید آنها (از آیین خود) بازگردند! (زیرا شما را، اهل کتاب و آگاه از بشارات آسمانی پیشین می‌دانند؛ و این توطئه کافی است که آنها را متزلزل سازد).'),
(366, 366, 'وَلَا تُؤْمِنُوا إِلَّا لِمَنْ تَبِعَ دِينَكُمْ قُلْ إِنَّ الْهُدَىٰ هُدَى اللَّهِ أَنْ يُؤْتَىٰ أَحَدٌ مِثْلَ مَا أُوتِيتُمْ أَوْ يُحَاجُّوكُمْ عِنْدَ رَبِّكُمْ ۗ قُلْ إِنَّ الْفَضْلَ بِيَدِ اللَّهِ يُؤْتِيهِ مَنْ يَشَاءُ ۗ وَاللَّهُ وَاسِعٌ عَلِيمٌ', 3, 73, 3, 12, 59, 'و جز به کسی که از آیین شما پیروی می‌کند، (واقعاً) ایمان نیاورید!» بگو: «هدایت، هدایت الهی است! (و این توطئه شما، در برابر آن بی اثر است)»! (سپس اضافه کردند: «تصور نکنید) به کسی همانند شما (کتاب آسمانی) داده می‌شود، یا اینکه می‌توانند در پیشگاه پروردگارتان، با شما بحث و گفتگو کنند، (بلکه نبوّت و منطق، هر دو نزد شماست!)» بگو: «فضل (و موهبت نبوّت و عقل و منطق، در انحصار کسی نیست؛ بلکه) به دست خداست؛ و به هر کس بخواهد (و شایسته بداند،) می‌دهد؛ و خداوند، واسع = دارای مواهب گسترده‌ و آگاه (از موارد شایسته آن) است.'),
(367, 367, 'يَخْتَصُّ بِرَحْمَتِهِ مَنْ يَشَاءُ ۗ وَاللَّهُ ذُو الْفَضْلِ الْعَظِيمِ', 3, 74, 3, 12, 59, 'هر کس را بخواهد، ویژه رحمت خود می‌کند؛ و خداوند، دارای مواهب عظیم است.»'),
(368, 368, ' وَمِنْ أَهْلِ الْكِتَابِ مَنْ إِنْ تَأْمَنْهُ بِقِنْطَارٍ يُؤَدِّهِ إِلَيْكَ وَمِنْهُمْ مَنْ إِنْ تَأْمَنْهُ بِدِينَارٍ لَا يُؤَدِّهِ إِلَيْكَ إِلَّا مَا دُمْتَ عَلَيْهِ قَائِمًا ۗ ذَٰلِكَ بِأَنَّهُمْ قَالُوا لَيْسَ عَلَيْنَا فِي الْأُمِّيِّينَ سَبِيلٌ وَيَقُولُونَ عَلَى اللَّهِ الْكَذِبَ وَهُمْ يَعْلَمُونَ', 3, 75, 3, 12, 59, 'و در میان اهل کتاب، کسانی هستند که اگر ثروت زیادی به رسم امانت به آنها بسپاری، به تو باز می‌گردانند؛ و کسانی هستند که اگر یک دینار هم به آنان بسپاری، به تو باز نمی‌گردانند؛ مگر تا زمانی که بالای سر آنها ایستاده (و بر آنها مسلّط) باشی! این بخاطر آن است که می‌گویند: «ما در برابر امّیّین = غیر یهود، مسؤول نیستیم.» و بر خدا دروغ می‌بندند؛ در حالی که می‌دانند (این سخن دروغ است).'),
(369, 369, 'بَلَىٰ مَنْ أَوْفَىٰ بِعَهْدِهِ وَاتَّقَىٰ فَإِنَّ اللَّهَ يُحِبُّ الْمُتَّقِينَ', 3, 76, 3, 12, 59, 'آری، کسی که به پیمان خود وفا کند و پرهیزگاری پیشه نماید، (خدا او را دوست می‌دارد؛ زیرا) خداوند پرهیزگاران را دوست دارد.'),
(370, 370, 'إِنَّ الَّذِينَ يَشْتَرُونَ بِعَهْدِ اللَّهِ وَأَيْمَانِهِمْ ثَمَنًا قَلِيلًا أُولَٰئِكَ لَا خَلَاقَ لَهُمْ فِي الْآخِرَةِ وَلَا يُكَلِّمُهُمُ اللَّهُ وَلَا يَنْظُرُ إِلَيْهِمْ يَوْمَ الْقِيَامَةِ وَلَا يُزَكِّيهِمْ وَلَهُمْ عَذَابٌ أَلِيمٌ', 3, 77, 3, 12, 59, 'کسانی که پیمان الهی و سوگندهای خود (به نام مقدس او) را به بهای ناچیزی می‌فروشند، آنها بهره‌ای در آخرت نخواهند داشت؛ و خداوند با آنها سخن نمی‌گوید و به آنان در قیامت نمی‌نگرد و آنها را (از گناه) پاک نمی‌سازد؛ و عذاب دردناکی برای آنهاست.'),
(371, 371, 'وَإِنَّ مِنْهُمْ لَفَرِيقًا يَلْوُونَ أَلْسِنَتَهُمْ بِالْكِتَابِ لِتَحْسَبُوهُ مِنَ الْكِتَابِ وَمَا هُوَ مِنَ الْكِتَابِ وَيَقُولُونَ هُوَ مِنْ عِنْدِ اللَّهِ وَمَا هُوَ مِنْ عِنْدِ اللَّهِ وَيَقُولُونَ عَلَى اللَّهِ الْكَذِبَ وَهُمْ يَعْلَمُونَ', 3, 78, 3, 12, 60, 'در میان آنها = یهود کسانی هستند که به هنگام تلاوت کتاب (خدا)، زبان خود را چنان می‌گردانند که گمان کنید (آنچه را می‌خوانند،) از کتاب (خدا) است؛ در حالی که از کتاب (خدا) نیست! (و با صراحت) می‌گویند: «آن از طرف خداست!» با اینکه از طرف خدا نیست، و به خدا دروغ می‌بندند در حالی که می‌دانند!'),
(372, 372, 'مَا كَانَ لِبَشَرٍ أَنْ يُؤْتِيَهُ اللَّهُ الْكِتَابَ وَالْحُكْمَ وَالنُّبُوَّةَ ثُمَّ يَقُولَ لِلنَّاسِ كُونُوا عِبَادًا لِي مِنْ دُونِ اللَّهِ وَلَٰكِنْ كُونُوا رَبَّانِيِّينَ بِمَا كُنْتُمْ تُعَلِّمُونَ الْكِتَابَ وَبِمَا كُنْتُمْ تَدْرُسُونَ', 3, 79, 3, 12, 60, 'برای هیچ بشری سزاوار نیست که خداوند، کتاب آسمانی و حکم و نبوّت به او دهد سپس او به مردم بگوید: «غیر از خدا، مرا پرستش کنید!» بلکه (سزاوار مقام او، این است که بگوید:) مردمی الهی باشید، آن‌گونه که کتاب خدا را می‌آموختید و درس می‌خواندید! (و غیر از خدا را پرستش نکنید!)'),
(373, 373, 'وَلَا يَأْمُرَكُمْ أَنْ تَتَّخِذُوا الْمَلَائِكَةَ وَالنَّبِيِّينَ أَرْبَابًا ۗ أَيَأْمُرُكُمْ بِالْكُفْرِ بَعْدَ إِذْ أَنْتُمْ مُسْلِمُونَ', 3, 80, 3, 12, 60, 'و نه اینکه به شما دستور دهد که فرشتگان و پیامبران را، پروردگار خود انتخاب کنید. آیا شما را، پس از آنکه مسلمان شدید، به کفر دعوت می‌کند؟!'),
(374, 374, 'وَإِذْ أَخَذَ اللَّهُ مِيثَاقَ النَّبِيِّينَ لَمَا آتَيْتُكُمْ مِنْ كِتَابٍ وَحِكْمَةٍ ثُمَّ جَاءَكُمْ رَسُولٌ مُصَدِّقٌ لِمَا مَعَكُمْ لَتُؤْمِنُنَّ بِهِ وَلَتَنْصُرُنَّهُ ۚ قَالَ أَأَقْرَرْتُمْ وَأَخَذْتُمْ عَلَىٰ ذَٰلِكُمْ إِصْرِي ۖ قَالُوا أَقْرَرْنَا ۚ قَالَ فَاشْهَدُوا وَأَنَا مَعَكُمْ مِنَ الشَّاهِدِينَ', 3, 81, 3, 12, 60, 'و (به خاطر بیاورید) هنگامی را که خداوند، از پیامبران (و پیروان آنها)، پیمان مؤکّد گرفت، که هرگاه کتاب و دانش به شما دادم، سپس پیامبری به سوی شما آمد که آنچه را با شماست تصدیق می‌کند، به او ایمان بیاورید و او را یاری کنید! سپس (خداوند) به آنها گفت: «آیا به این موضوع، اقرار دارید؟ و بر آن، پیمان مؤکّد بستید؟» گفتند: «(آری) اقرار داریم!» (خداوند به آنها) گفت: «پس گواه باشید! و من نیز با شما از گواهانم.»'),
(375, 375, 'فَمَنْ تَوَلَّىٰ بَعْدَ ذَٰلِكَ فَأُولَٰئِكَ هُمُ الْفَاسِقُونَ', 3, 82, 3, 12, 60, 'پس کسی که بعد از این (پیمان محکم)، روی گرداند، فاسق است.'),
(376, 376, 'أَفَغَيْرَ دِينِ اللَّهِ يَبْغُونَ وَلَهُ أَسْلَمَ مَنْ فِي السَّمَاوَاتِ وَالْأَرْضِ طَوْعًا وَكَرْهًا وَإِلَيْهِ يُرْجَعُونَ', 3, 83, 3, 12, 60, 'آیا آنها غیر از آیین خدا می‌طلبند؟! (آیین او همین اسلام است؛) و تمام کسانی که در آسمانها و زمین هستند، از روی اختیار یا از روی اجبار، در برابرِ (فرمان) او تسلیمند، و همه به سوی او بازگردانده می‌شوند.'),
(377, 377, 'قُلْ آمَنَّا بِاللَّهِ وَمَا أُنْزِلَ عَلَيْنَا وَمَا أُنْزِلَ عَلَىٰ إِبْرَاهِيمَ وَإِسْمَاعِيلَ وَإِسْحَاقَ وَيَعْقُوبَ وَالْأَسْبَاطِ وَمَا أُوتِيَ مُوسَىٰ وَعِيسَىٰ وَالنَّبِيُّونَ مِنْ رَبِّهِمْ لَا نُفَرِّقُ بَيْنَ أَحَدٍ مِنْهُمْ وَنَحْنُ لَهُ مُسْلِمُونَ', 3, 84, 3, 12, 61, 'بگو: «به خدا ایمان آوردیم؛ و (همچنین) به آنچه بر ما و بر ابراهیم و اسماعیل و اسحاق و یعقوب و اسباط نازل گردیده؛ و آنچه به موسی و عیسی و (دیگر) پیامبران، از طرف پروردگارشان داده شده است؛ ما در میان هیچ یک از آنان فرقی نمی‌گذاریم؛ و در برابرِ (فرمان) او تسلیم هستیم.»'),
(378, 378, 'وَمَنْ يَبْتَغِ غَيْرَ الْإِسْلَامِ دِينًا فَلَنْ يُقْبَلَ مِنْهُ وَهُوَ فِي الْآخِرَةِ مِنَ الْخَاسِرِينَ', 3, 85, 3, 12, 61, 'و هر کس جز اسلام (و تسلیم در برابر فرمان حق،) آیینی برای خود انتخاب کند، از او پذیرفته نخواهد شد؛ و او در آخرت، از زیانکاران است.'),
(379, 379, 'كَيْفَ يَهْدِي اللَّهُ قَوْمًا كَفَرُوا بَعْدَ إِيمَانِهِمْ وَشَهِدُوا أَنَّ الرَّسُولَ حَقٌّ وَجَاءَهُمُ الْبَيِّنَاتُ ۚ وَاللَّهُ لَا يَهْدِي الْقَوْمَ الظَّالِمِينَ', 3, 86, 3, 12, 61, 'چگونه خداوند جمعیّتی را هدایت می‌کند که بعد از ایمان و گواهی به حقّانیّت رسول و آمدن نشانه‌های روشن برای آنها، کافر شدند؟! و خدا، جمعیّت ستمکاران را هدایت نخواهد کرد!'),
(380, 380, 'أُولَٰئِكَ جَزَاؤُهُمْ أَنَّ عَلَيْهِمْ لَعْنَةَ اللَّهِ وَالْمَلَائِكَةِ وَالنَّاسِ أَجْمَعِينَ', 3, 87, 3, 12, 61, 'کیفر آنها، این است که لعن (و طرد) خداوند و فرشتگان و مردم همگی بر آنهاست.'),
(381, 381, 'خَالِدِينَ فِيهَا لَا يُخَفَّفُ عَنْهُمُ الْعَذَابُ وَلَا هُمْ يُنْظَرُونَ', 3, 88, 3, 12, 61, 'همواره در این لعن (و طرد و نفرین) می‌مانند؛ مجازاتشان تخفیف نمی‌یابد؛ و به آنها مهلت داده نمی‌شود.'),
(382, 382, 'إِلَّا الَّذِينَ تَابُوا مِنْ بَعْدِ ذَٰلِكَ وَأَصْلَحُوا فَإِنَّ اللَّهَ غَفُورٌ رَحِيمٌ', 3, 89, 3, 12, 61, 'مگر کسانی که پس از آن، توبه کنند و اصلاح نمایند؛ (و در مقام جبران گناهان گذشته برآیند، که توبه آنها پذیرفته خواهد شد؛) زیرا خداوند، آمرزنده و بخشنده است.'),
(383, 383, 'إِنَّ الَّذِينَ كَفَرُوا بَعْدَ إِيمَانِهِمْ ثُمَّ ازْدَادُوا كُفْرًا لَنْ تُقْبَلَ تَوْبَتُهُمْ وَأُولَٰئِكَ هُمُ الضَّالُّونَ', 3, 90, 3, 12, 61, 'کسانی که پس از ایمان کافر شدند و سپس بر کفر (خود) افزودند، (و در این راه اصرار ورزیدند،) هیچ‌گاه توبه آنان، (که از روی ناچاری یا در آستانه مرگ صورت می‌گیرد،) قبول نمی‌شود؛ و آنها گمراهان (واقعی) اند (چرا که هم راه خدا را گم کرده‌اند، و هم راه توبه را!).'),
(384, 384, 'إِنَّ الَّذِينَ كَفَرُوا وَمَاتُوا وَهُمْ كُفَّارٌ فَلَنْ يُقْبَلَ مِنْ أَحَدِهِمْ مِلْءُ الْأَرْضِ ذَهَبًا وَلَوِ افْتَدَىٰ بِهِ ۗ أُولَٰئِكَ لَهُمْ عَذَابٌ أَلِيمٌ وَمَا لَهُمْ مِنْ نَاصِرِينَ', 3, 91, 3, 12, 61, 'کسانی که کافر شدند و در حال کفر از دنیا رفتند، اگر چه روی زمین پر از طلا باشد، و آن را بعنوان فدیه (و کفّاره اعمال بد خویش) بپردازند، هرگز از هیچ‌یک آنها قبول نخواهد شد؛ و برای آنان، مجازاتِ دردناک است؛ و یاورانی ندارند.'),
(385, 385, 'لَنْ تَنَالُوا الْبِرَّ حَتَّىٰ تُنْفِقُوا مِمَّا تُحِبُّونَ ۚ وَمَا تُنْفِقُوا مِنْ شَيْءٍ فَإِنَّ اللَّهَ بِهِ عَلِيمٌ', 3, 92, 3, 12, 62, 'هرگز به (حقیقت) نیکوکاری نمی‌رسید مگر اینکه از آنچه دوست می‌دارید، (در راه خدا) انفاق کنید؛ و آنچه انفاق می‌کنید، خداوند از آن آگاه است.'),
(386, 386, ' كُلُّ الطَّعَامِ كَانَ حِلًّا لِبَنِي إِسْرَائِيلَ إِلَّا مَا حَرَّمَ إِسْرَائِيلُ عَلَىٰ نَفْسِهِ مِنْ قَبْلِ أَنْ تُنَزَّلَ التَّوْرَاةُ ۗ قُلْ فَأْتُوا بِالتَّوْرَاةِ فَاتْلُوهَا إِنْ كُنْتُمْ صَادِقِينَ', 3, 93, 4, 13, 62, 'همه غذاها (ی پاک) بر بنی اسرائیل حلال بود، جز آنچه اسرائیل (یعقوب)، پیش از نزول تورات، بر خود تحریم کرده بود؛ (مانند گوشت شتر که برای او ضرر داشت.) بگو: «اگر راست می‌گویید تورات را بیاورید و بخوانید! (این نسبتهایی که به پیامبران پیشین می‌دهید، حتّی در تورات تحریف شده شما نیست!)»'),
(387, 387, 'فَمَنِ افْتَرَىٰ عَلَى اللَّهِ الْكَذِبَ مِنْ بَعْدِ ذَٰلِكَ فَأُولَٰئِكَ هُمُ الظَّالِمُونَ', 3, 94, 4, 13, 62, 'بنا بر این، آنها که بعد از این به خدا دروغ می‌بندند، ستمگرند! (زیرا از روی علم و عمد چنین می‌کنند).'),
(388, 388, 'قُلْ صَدَقَ اللَّهُ ۗ فَاتَّبِعُوا مِلَّةَ إِبْرَاهِيمَ حَنِيفًا وَمَا كَانَ مِنَ الْمُشْرِكِينَ', 3, 95, 4, 13, 62, 'بگو: «خدا راست گفته (و اینها در آیین پاک ابراهیم نبوده) است. بنا بر این، از آیین ابراهیم پیروی کنید، که به حق گرایش داشت، و از مشرکان نبود!»'),
(389, 389, 'إِنَّ أَوَّلَ بَيْتٍ وُضِعَ لِلنَّاسِ لَلَّذِي بِبَكَّةَ مُبَارَكًا وَهُدًى لِلْعَالَمِينَ', 3, 96, 4, 13, 62, 'نخستین خانه‌ای که برای مردم (و نیایش خداوند) قرار داده شد، همان است که در سرزمین مکّه است، که پر برکت، و مایه هدایت جهانیان است.'),
(390, 390, 'فِيهِ آيَاتٌ بَيِّنَاتٌ مَقَامُ إِبْرَاهِيمَ ۖ وَمَنْ دَخَلَهُ كَانَ آمِنًا ۗ وَلِلَّهِ عَلَى النَّاسِ حِجُّ الْبَيْتِ مَنِ اسْتَطَاعَ إِلَيْهِ سَبِيلًا ۚ وَمَنْ كَفَرَ فَإِنَّ اللَّهَ غَنِيٌّ عَنِ الْعَالَمِينَ', 3, 97, 4, 13, 62, 'در آن، نشانه‌های روشن، (از جمله) مقام ابراهیم است؛ و هر کس داخل آن = خانه خدا شود؛ در امان خواهد بود، و برای خدا بر مردم است که آهنگ خانه (او) کنند، آنها که توانایی رفتن به سوی آن دارند. و هر کس کفر ورزد (و حج را ترک کند، به خود زیان رسانده)، خداوند از همه جهانیان، بی‌نیاز است.'),
(391, 391, 'قُلْ يَا أَهْلَ الْكِتَابِ لِمَ تَكْفُرُونَ بِآيَاتِ اللَّهِ وَاللَّهُ شَهِيدٌ عَلَىٰ مَا تَعْمَلُونَ', 3, 98, 4, 13, 62, 'بگو: «ای اهل کتاب! چرا به آیات خدا کفر می‌ورزید؟! و خدا گواه است بر اعمالی که انجام می‌دهید!»'),
(392, 392, 'قُلْ يَا أَهْلَ الْكِتَابِ لِمَ تَصُدُّونَ عَنْ سَبِيلِ اللَّهِ مَنْ آمَنَ تَبْغُونَهَا عِوَجًا وَأَنْتُمْ شُهَدَاءُ ۗ وَمَا اللَّهُ بِغَافِلٍ عَمَّا تَعْمَلُونَ', 3, 99, 4, 13, 62, 'بگو: «ای اهل کتاب! چرا افرادی را که ایمان آورده‌اند، از راه خدا بازمی‌دارید، و می‌خواهید این راه را کج سازید؟! در حالی که شما (به درستی این راه) گواه هستید؛ و خداوند از آنچه انجام می‌دهید، غافل نیست!»'),
(393, 393, 'يَا أَيُّهَا الَّذِينَ آمَنُوا إِنْ تُطِيعُوا فَرِيقًا مِنَ الَّذِينَ أُوتُوا الْكِتَابَ يَرُدُّوكُمْ بَعْدَ إِيمَانِكُمْ كَافِرِينَ', 3, 100, 4, 13, 62, 'ای کسانی که ایمان آورده‌اید! اگر از گروهی از اهل کتاب، (که کارشان نفاق‌افکنی و شعله‌ورساختنِ آتش کینه و عداوت است) اطاعت کنید، شما را پس از ایمان، به کفر بازمی‌گردانند.'),
(394, 394, 'وَكَيْفَ تَكْفُرُونَ وَأَنْتُمْ تُتْلَىٰ عَلَيْكُمْ آيَاتُ اللَّهِ وَفِيكُمْ رَسُولُهُ ۗ وَمَنْ يَعْتَصِمْ بِاللَّهِ فَقَدْ هُدِيَ إِلَىٰ صِرَاطٍ مُسْتَقِيمٍ', 3, 101, 4, 13, 63, 'و چگونه ممکن است شما کافر شوید، با اینکه (در دامان وحی قرار گرفته‌اید، و) آیات خدا بر شما خوانده می‌شود، و پیامبر او در میان شماست؟! (بنابر این، به خدا تمسّک جویید!) و هر کس به خدا تمسّک جوید، به راهی راست، هدایت شده است.'),
(395, 395, 'يَا أَيُّهَا الَّذِينَ آمَنُوا اتَّقُوا اللَّهَ حَقَّ تُقَاتِهِ وَلَا تَمُوتُنَّ إِلَّا وَأَنْتُمْ مُسْلِمُونَ', 3, 102, 4, 13, 63, 'ای کسانی که ایمان آورده‌اید! آن گونه که حق تقوا و پرهیزکاری است، از خدا بپرهیزید! و از دنیا نروید، مگر اینکه مسلمان باشید! (باید گوهر ایمان را تا پایان عمر، حفظ کنید!)'),
(396, 396, 'وَاعْتَصِمُوا بِحَبْلِ اللَّهِ جَمِيعًا وَلَا تَفَرَّقُوا ۚ وَاذْكُرُوا نِعْمَتَ اللَّهِ عَلَيْكُمْ إِذْ كُنْتُمْ أَعْدَاءً فَأَلَّفَ بَيْنَ قُلُوبِكُمْ فَأَصْبَحْتُمْ بِنِعْمَتِهِ إِخْوَانًا وَكُنْتُمْ عَلَىٰ شَفَا حُفْرَةٍ مِنَ النَّارِ فَأَنْقَذَكُمْ مِنْهَا ۗ كَذَٰلِكَ يُبَيِّنُ اللَّهُ لَكُمْ آيَاتِهِ لَعَلَّكُمْ تَهْتَدُونَ', 3, 103, 4, 13, 63, 'و همگی به ریسمان خدا = قرآن و اسلام، و هرگونه وسیله وحدت‌، چنگ زنید، و پراکنده نشوید! و نعمت (بزرگِ) خدا را بر خود، به یاد آرید که چگونه دشمن یکدیگر بودید، و او میان دلهای شما، الفت ایجاد کرد، و به برکتِ نعمتِ او، برادر شدید! و شما بر لبِ حفره‌ای از آتش بودید، خدا شما را از آن نجات داد؛ این چنین، خداوند آیات خود را برای شما آشکار می‌سازد؛ شاید پذیرای هدایت شوید.'),
(397, 397, 'وَلْتَكُنْ مِنْكُمْ أُمَّةٌ يَدْعُونَ إِلَى الْخَيْرِ وَيَأْمُرُونَ بِالْمَعْرُوفِ وَيَنْهَوْنَ عَنِ الْمُنْكَرِ ۚ وَأُولَٰئِكَ هُمُ الْمُفْلِحُونَ', 3, 104, 4, 13, 63, 'باید از میان شما، جمعی دعوت به نیکی، و امر به معروف و نهی از منکر کنند! و آنها همان رستگارانند.'),
(398, 398, 'وَلَا تَكُونُوا كَالَّذِينَ تَفَرَّقُوا وَاخْتَلَفُوا مِنْ بَعْدِ مَا جَاءَهُمُ الْبَيِّنَاتُ ۚ وَأُولَٰئِكَ لَهُمْ عَذَابٌ عَظِيمٌ', 3, 105, 4, 13, 63, 'و مانند کسانی نباشید که پراکنده شدند و اختلاف کردند؛ (آن هم) پس از آنکه نشانه‌های روشن (پروردگار) به آنان رسید! و آنها عذاب عظیمی دارند.'),
(399, 399, 'يَوْمَ تَبْيَضُّ وُجُوهٌ وَتَسْوَدُّ وُجُوهٌ ۚ فَأَمَّا الَّذِينَ اسْوَدَّتْ وُجُوهُهُمْ أَكَفَرْتُمْ بَعْدَ إِيمَانِكُمْ فَذُوقُوا الْعَذَابَ بِمَا كُنْتُمْ تَكْفُرُونَ', 3, 106, 4, 13, 63, '(آن عذاب عظیم) روزی خواهد بود که چهره‌هائی سفید، و چهره‌هائی سیاه می‌گردد، اما آنها که صورتهایشان سیاه شده، (به آنها گفته می‌شود:) آیا بعد از ایمان، و (اخوّت و برادری در سایه آن،) کافر شدید؟! پس بچشید عذاب را، به سبب آنچه کفر می‌ورزیدید!'),
(400, 400, 'وَأَمَّا الَّذِينَ ابْيَضَّتْ وُجُوهُهُمْ فَفِي رَحْمَةِ اللَّهِ هُمْ فِيهَا خَالِدُونَ', 3, 107, 4, 13, 63, 'و امّا آنها که چهره‌هایشان سفید شده، در رحمت خداوند خواهند بود؛ و جاودانه در آن می‌مانند.'),
(401, 401, 'تِلْكَ آيَاتُ اللَّهِ نَتْلُوهَا عَلَيْكَ بِالْحَقِّ ۗ وَمَا اللَّهُ يُرِيدُ ظُلْمًا لِلْعَالَمِينَ', 3, 108, 4, 13, 63, 'اینها آیات خداست؛ که بحق بر تو می‌خوانیم. و خداوند (هیچ گاه) ستمی برای (احدی از) جهانیان نمی‌خواهد.'),
(402, 402, 'وَلِلَّهِ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ ۚ وَإِلَى اللَّهِ تُرْجَعُ الْأُمُورُ', 3, 109, 4, 13, 64, 'و (چگونه ممکن است خدا ستم کند؟! در حالی که) آنچه در آسمانها و آنچه در زمین است، مال اوست؛ و همه کارها، به سوی او باز می‌گردد (و به فرمان اوست.)'),
(403, 403, 'كُنْتُمْ خَيْرَ أُمَّةٍ أُخْرِجَتْ لِلنَّاسِ تَأْمُرُونَ بِالْمَعْرُوفِ وَتَنْهَوْنَ عَنِ الْمُنْكَرِ وَتُؤْمِنُونَ بِاللَّهِ ۗ وَلَوْ آمَنَ أَهْلُ الْكِتَابِ لَكَانَ خَيْرًا لَهُمْ ۚ مِنْهُمُ الْمُؤْمِنُونَ وَأَكْثَرُهُمُ الْفَاسِقُونَ', 3, 110, 4, 13, 64, 'شما بهترین امتی بودید که به سود انسانها آفریده شده‌اند؛ (چه اینکه) امر به معروف و نهی از منکر می‌کنید و به خدا ایمان دارید. و اگر اهل کتاب، (به چنین برنامه و آیین درخشانی،) ایمان آورند، برای آنها بهتر است! (ولی تنها) عده کمی از آنها با ایمانند، و بیشتر آنها فاسقند، (و خارج از اطاعت پروردگار)'),
(404, 404, 'لَنْ يَضُرُّوكُمْ إِلَّا أَذًى ۖ وَإِنْ يُقَاتِلُوكُمْ يُوَلُّوكُمُ الْأَدْبَارَ ثُمَّ لَا يُنْصَرُونَ', 3, 111, 4, 13, 64, 'آنها = اهل کتاب، مخصوصا» یهود هرگز نمی‌توانند به شما زیان برسانند، جز آزارهای مختصر؛ و اگر با شما پیکار کنند، به شما پشت خواهند کرد (و شکست می‌خورند؛ سپس کسی آنها را یاری نمی‌کند.'),
(405, 405, 'ضُرِبَتْ عَلَيْهِمُ الذِّلَّةُ أَيْنَ مَا ثُقِفُوا إِلَّا بِحَبْلٍ مِنَ اللَّهِ وَحَبْلٍ مِنَ النَّاسِ وَبَاءُوا بِغَضَبٍ مِنَ اللَّهِ وَضُرِبَتْ عَلَيْهِمُ الْمَسْكَنَةُ ۚ ذَٰلِكَ بِأَنَّهُمْ كَانُوا يَكْفُرُونَ بِآيَاتِ اللَّهِ وَيَقْتُلُونَ الْأَنْبِيَاءَ بِغَيْرِ حَقٍّ ۚ ذَٰلِكَ بِمَا عَصَوْا وَكَانُوا يَعْتَدُونَ', 3, 112, 4, 13, 64, 'هر جا یافت شوند، مهر ذلت بر آنان خورده است؛ مگر با ارتباط به خدا، (و تجدید نظر در روش ناپسند خود،) و (یا) با ارتباط به مردم (و وابستگی به این و آن)؛ و به خشم خدا، گرفتار شده‌اند؛ و مهر بیچارگی بر آنها زده شده؛ چرا که آنها به آیات خدا، کفر می‌ورزیدند و پیامبران را بناحق می‌کشتند. اینها بخاطر آن است که گناه کردند؛ و (به حقوق دیگران،) تجاوز می‌نمودند.'),
(406, 406, ' لَيْسُوا سَوَاءً ۗ مِنْ أَهْلِ الْكِتَابِ أُمَّةٌ قَائِمَةٌ يَتْلُونَ آيَاتِ اللَّهِ آنَاءَ اللَّيْلِ وَهُمْ يَسْجُدُونَ', 3, 113, 4, 13, 64, 'آنها همه یکسان نیستند؛ از اهل کتاب، جمعیّتی هستند که (به حق و ایمان) قیام می‌کنند؛ و پیوسته در اوقات شب، آیات خدا را می‌خوانند؛ در حالی که سجده می‌نمایند.'),
(407, 407, 'يُؤْمِنُونَ بِاللَّهِ وَالْيَوْمِ الْآخِرِ وَيَأْمُرُونَ بِالْمَعْرُوفِ وَيَنْهَوْنَ عَنِ الْمُنْكَرِ وَيُسَارِعُونَ فِي الْخَيْرَاتِ وَأُولَٰئِكَ مِنَ الصَّالِحِينَ', 3, 114, 4, 13, 64, 'به خدا و روز دیگر ایمان می‌آورند؛ امر به معروف و نهی از منکر می‌کنند؛ و در انجام کارهای نیک، پیشی می‌گیرند؛ و آنها از صالحانند.'),
(408, 408, 'وَمَا يَفْعَلُوا مِنْ خَيْرٍ فَلَنْ يُكْفَرُوهُ ۗ وَاللَّهُ عَلِيمٌ بِالْمُتَّقِينَ', 3, 115, 4, 13, 64, 'و آنچه از اعمال نیک انجام دهند، هرگز کفران نخواهد شد! (و پاداش شایسته آن را می‌بینند.) و خدا از پرهیزکاران، آگاه است.'),
(409, 409, 'إِنَّ الَّذِينَ كَفَرُوا لَنْ تُغْنِيَ عَنْهُمْ أَمْوَالُهُمْ وَلَا أَوْلَادُهُمْ مِنَ اللَّهِ شَيْئًا ۖ وَأُولَٰئِكَ أَصْحَابُ النَّارِ ۚ هُمْ فِيهَا خَالِدُونَ', 3, 116, 4, 13, 65, 'کسانی که کافر شدند، هرگز نمی‌توانند در پناه اموال و فرزندانشان، از مجازات خدا در امان بمانند! آنها اصحاب دوزخند؛ و جاودانه در آن خواهند ماند.'),
(410, 410, 'مَثَلُ مَا يُنْفِقُونَ فِي هَٰذِهِ الْحَيَاةِ الدُّنْيَا كَمَثَلِ رِيحٍ فِيهَا صِرٌّ أَصَابَتْ حَرْثَ قَوْمٍ ظَلَمُوا أَنْفُسَهُمْ فَأَهْلَكَتْهُ ۚ وَمَا ظَلَمَهُمُ اللَّهُ وَلَٰكِنْ أَنْفُسَهُمْ يَظْلِمُونَ', 3, 117, 4, 13, 65, 'آنچه آنها در این زندگی پست دنیوی انفاق می‌کنند، همانند باد سوزانی است که به زراعت قومی که بر خود ستم کرده (و در غیر محل و وقت مناسب، کشت نموده‌اند)، بوزد؛ و آن را نابود سازد. خدا به آنها ستم نکرده؛ بلکه آنها، خودشان به خویشتن ستم می‌کنند.'),
(411, 411, 'يَا أَيُّهَا الَّذِينَ آمَنُوا لَا تَتَّخِذُوا بِطَانَةً مِنْ دُونِكُمْ لَا يَأْلُونَكُمْ خَبَالًا وَدُّوا مَا عَنِتُّمْ قَدْ بَدَتِ الْبَغْضَاءُ مِنْ أَفْوَاهِهِمْ وَمَا تُخْفِي صُدُورُهُمْ أَكْبَرُ ۚ قَدْ بَيَّنَّا لَكُمُ الْآيَاتِ ۖ إِنْ كُنْتُمْ تَعْقِلُونَ', 3, 118, 4, 13, 65, 'ای کسانی که ایمان آورده‌اید! محرم اسراری از غیر خود، انتخاب نکنید! آنها از هرگونه شرّ و فسادی در باره شما، کوتاهی نمی‌کنند. آنها دوست دارند شما در رنج و زحمت باشید. (نشانه‌های) دشمنی از دهان (و کلام) شان آشکار شده؛ و آنچه در دلهایشان پنهان می‌دارند، از آن مهمتر است. ما آیات (و راه‌های پیشگیری از شرّ آنها) را برای شما بیان کردیم اگر اندیشه کنید!'),
(412, 412, 'هَا أَنْتُمْ أُولَاءِ تُحِبُّونَهُمْ وَلَا يُحِبُّونَكُمْ وَتُؤْمِنُونَ بِالْكِتَابِ كُلِّهِ وَإِذَا لَقُوكُمْ قَالُوا آمَنَّا وَإِذَا خَلَوْا عَضُّوا عَلَيْكُمُ الْأَنَامِلَ مِنَ الْغَيْظِ ۚ قُلْ مُوتُوا بِغَيْظِكُمْ ۗ إِنَّ اللَّهَ عَلِيمٌ بِذَاتِ الصُّدُورِ', 3, 119, 4, 13, 65, 'شما کسانی هستید که آنها را دوست می‌دارید؛ امّا آنها شما را دوست ندارند! در حالی که شما به همه کتابهای آسمانی ایمان دارید (و آنها به کتاب آسمانی شما ایمان ندارند). هنگامی که شما را ملاقات می‌کنند، (به دروغ) می‌گویند: «ایمان آورده‌ایم!» امّا هنگامی که تنها می‌شوند، از شدّت خشم بر شما، سر انگشتان خود را به دندان می‌گزند! بگو: «با همین خشمی که دارید بمیرید! خدا از (اسرار) درون سینه‌ها آگاه است.»'),
(413, 413, 'إِنْ تَمْسَسْكُمْ حَسَنَةٌ تَسُؤْهُمْ وَإِنْ تُصِبْكُمْ سَيِّئَةٌ يَفْرَحُوا بِهَا ۖ وَإِنْ تَصْبِرُوا وَتَتَّقُوا لَا يَضُرُّكُمْ كَيْدُهُمْ شَيْئًا ۗ إِنَّ اللَّهَ بِمَا يَعْمَلُونَ مُحِيطٌ', 3, 120, 4, 13, 65, 'اگر نیکی به شما برسد، آنها را ناراحت می‌کند؛ و اگر حادثه ناگواری برای شما رخ دهد، خوشحال می‌شوند. (امّا) اگر (در برابرشان) استقامت و پرهیزگاری پیشه کنید، نقشه‌های (خائنانه) آنان، به شما زیانی نمی‌رساند؛ خداوند به آنچه انجام می‌دهند، احاطه دارد.'),
(414, 414, 'وَإِذْ غَدَوْتَ مِنْ أَهْلِكَ تُبَوِّئُ الْمُؤْمِنِينَ مَقَاعِدَ لِلْقِتَالِ ۗ وَاللَّهُ سَمِيعٌ عَلِيمٌ', 3, 121, 4, 13, 65, 'و (به یاد آور) زمانی را که صبحگاهان، از میان خانواده خود، جهت انتخاب اردوگاه جنگ برای مؤمنان، بیرون رفتی! و خداوند، شنوا و داناست (گفتگوهای مختلفی را که درباره طرح جنگ گفته می‌شد، می‌شنید؛ و اندیشه‌هایی را که بعضی در سر می‌پروراندند، می‌دانست).'),
(415, 415, 'إِذْ هَمَّتْ طَائِفَتَانِ مِنْكُمْ أَنْ تَفْشَلَا وَاللَّهُ وَلِيُّهُمَا ۗ وَعَلَى اللَّهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُونَ', 3, 122, 4, 13, 66, '(و نیز به یاد آور) زمانی را که دو طایفه از شما تصمیم گرفتند سستی نشان دهند (و از وسط راه بازگردند)؛ و خداوند پشتیبان آنها بود (و به آنها کمک کرد که از این فکر بازگردند)؛ و افراد باایمان، باید تنها بر خدا توکّل کنند!'),
(416, 416, 'وَلَقَدْ نَصَرَكُمُ اللَّهُ بِبَدْرٍ وَأَنْتُمْ أَذِلَّةٌ ۖ فَاتَّقُوا اللَّهَ لَعَلَّكُمْ تَشْكُرُونَ', 3, 123, 4, 13, 66, 'خداوند شما را در «بدر» یاری کرد (و بر دشمنان خطرناک، پیروز ساخت)؛ در حالی که شما (نسبت به آنها)، ناتوان بودید. پس، از خدا بپرهیزید (و در برابر دشمن، مخالفتِ فرمانِ پیامبر نکنید)، تا شکر نعمت او را بجا آورده باشید!'),
(417, 417, 'إِذْ تَقُولُ لِلْمُؤْمِنِينَ أَلَنْ يَكْفِيَكُمْ أَنْ يُمِدَّكُمْ رَبُّكُمْ بِثَلَاثَةِ آلَافٍ مِنَ الْمَلَائِكَةِ مُنْزَلِينَ', 3, 124, 4, 13, 66, 'در آن هنگام که تو به مؤمنان می‌گفتنی: «آیا کافی نیست که پروردگارتان، شما را به سه هزار نفر از فرشتگان، که از آسمان فرود می‌آیند، یاری کند؟!»'),
(418, 418, 'بَلَىٰ ۚ إِنْ تَصْبِرُوا وَتَتَّقُوا وَيَأْتُوكُمْ مِنْ فَوْرِهِمْ هَٰذَا يُمْدِدْكُمْ رَبُّكُمْ بِخَمْسَةِ آلَافٍ مِنَ الْمَلَائِكَةِ مُسَوِّمِينَ', 3, 125, 4, 13, 66, 'آری، (امروز هم) اگر استقامت و تقوا پیشه کنید، و دشمن به همین زودی به سراغ شما بیاید، خداوند شما را به پنج هزار نفر از فرشتگان، که نشانه‌هایی با خود دارند، مدد خواهد داد!'),
(419, 419, 'وَمَا جَعَلَهُ اللَّهُ إِلَّا بُشْرَىٰ لَكُمْ وَلِتَطْمَئِنَّ قُلُوبُكُمْ بِهِ ۗ وَمَا النَّصْرُ إِلَّا مِنْ عِنْدِ اللَّهِ الْعَزِيزِ الْحَكِيمِ', 3, 126, 4, 13, 66, 'ولی اینها را خداوند فقط بشارت، و برای اطمینان خاطر شما قرار داده؛ وگرنه، پیروزی تنها از جانب خداوند توانای حکیم است!'),
(420, 420, 'لِيَقْطَعَ طَرَفًا مِنَ الَّذِينَ كَفَرُوا أَوْ يَكْبِتَهُمْ فَيَنْقَلِبُوا خَائِبِينَ', 3, 127, 4, 13, 66, '(این وعده را که خدا به شما داده،) برای این است که قسمتی از پیکر لشکر کافران را قطع کند؛ یا آنها را با ذلّت برگرداند؛ تا مأیوس و ناامید، (به وطن خود) بازگردند.'),
(421, 421, 'لَيْسَ لَكَ مِنَ الْأَمْرِ شَيْءٌ أَوْ يَتُوبَ عَلَيْهِمْ أَوْ يُعَذِّبَهُمْ فَإِنَّهُمْ ظَالِمُونَ', 3, 128, 4, 13, 66, 'هیچ‌گونه اختیاری (در باره عفو کافران، یا مؤمنان فراری از جنگ،) برای تو نیست؛ مگر اینکه (خدا) بخواهد آنها را ببخشد، یا مجازات کند؛ زیرا آنها ستمگرند.'),
(422, 422, 'وَلِلَّهِ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ ۚ يَغْفِرُ لِمَنْ يَشَاءُ وَيُعَذِّبُ مَنْ يَشَاءُ ۚ وَاللَّهُ غَفُورٌ رَحِيمٌ', 3, 129, 4, 13, 66, 'و آنچه در آسمانها و زمین است، از آن خداست. هر کس را بخواهد (و شایسته بداند)، می‌بخشد؛ و هر کس را بخواهد، مجازات می‌کند؛ و خداوند آمرزنده مهربان است.'),
(423, 423, 'يَا أَيُّهَا الَّذِينَ آمَنُوا لَا تَأْكُلُوا الرِّبَا أَضْعَافًا مُضَاعَفَةً ۖ وَاتَّقُوا اللَّهَ لَعَلَّكُمْ تُفْلِحُونَ', 3, 130, 4, 13, 66, 'ای کسانی که ایمان آورده‌اید! ربا (و سود پول) را چند برابر نخورید! از خدا بپرهیزید، تا رستگار شوید!'),
(424, 424, 'وَاتَّقُوا النَّارَ الَّتِي أُعِدَّتْ لِلْكَافِرِينَ', 3, 131, 4, 13, 66, 'و از آتشی بپرهیزید که برای کافران آماده شده است!'),
(425, 425, 'وَأَطِيعُوا اللَّهَ وَالرَّسُولَ لَعَلَّكُمْ تُرْحَمُونَ', 3, 132, 4, 13, 66, 'و خدا و پیامبر را اطاعت کنید، تا مشمول رحمت شوید!'),
(426, 426, ' وَسَارِعُوا إِلَىٰ مَغْفِرَةٍ مِنْ رَبِّكُمْ وَجَنَّةٍ عَرْضُهَا السَّمَاوَاتُ وَالْأَرْضُ أُعِدَّتْ لِلْمُتَّقِينَ', 3, 133, 4, 14, 67, 'و شتاب کنید برای رسیدن به آمرزش پروردگارتان؛ و بهشتی که وسعت آن، آسمانها و زمین است؛ و برای پرهیزگاران آماده شده است.'),
(427, 427, 'الَّذِينَ يُنْفِقُونَ فِي السَّرَّاءِ وَالضَّرَّاءِ وَالْكَاظِمِينَ الْغَيْظَ وَالْعَافِينَ عَنِ النَّاسِ ۗ وَاللَّهُ يُحِبُّ الْمُحْسِنِينَ', 3, 134, 4, 14, 67, 'همانها که در توانگری و تنگدستی، انفاق می‌کنند؛ و خشم خود را فرو می‌برند؛ و از خطای مردم درمی‌گذرند؛ و خدا نیکوکاران را دوست دارد.'),
(428, 428, 'وَالَّذِينَ إِذَا فَعَلُوا فَاحِشَةً أَوْ ظَلَمُوا أَنْفُسَهُمْ ذَكَرُوا اللَّهَ فَاسْتَغْفَرُوا لِذُنُوبِهِمْ وَمَنْ يَغْفِرُ الذُّنُوبَ إِلَّا اللَّهُ وَلَمْ يُصِرُّوا عَلَىٰ مَا فَعَلُوا وَهُمْ يَعْلَمُونَ', 3, 135, 4, 14, 67, 'و آنها که وقتی مرتکب عمل زشتی شوند، یا به خود ستم کنند، به یاد خدا می‌افتند؛ و برای گناهان خود، طلب آمرزش می‌کنند -و کیست جز خدا که گناهان را ببخشد؟- و بر گناه، اصرار نمی‌ورزند، با اینکه می‌دانند.'),
(429, 429, 'أُولَٰئِكَ جَزَاؤُهُمْ مَغْفِرَةٌ مِنْ رَبِّهِمْ وَجَنَّاتٌ تَجْرِي مِنْ تَحْتِهَا الْأَنْهَارُ خَالِدِينَ فِيهَا ۚ وَنِعْمَ أَجْرُ الْعَامِلِينَ', 3, 136, 4, 14, 67, 'آنها پاداششان آمرزش پروردگار، و بهشتهایی است که از زیر درختانش، نهرها جاری است؛ جاودانه در آن میمانند؛ چه نیکو است پاداش اهل عمل!'),
(430, 430, 'قَدْ خَلَتْ مِنْ قَبْلِكُمْ سُنَنٌ فَسِيرُوا فِي الْأَرْضِ فَانْظُرُوا كَيْفَ كَانَ عَاقِبَةُ الْمُكَذِّبِينَ', 3, 137, 4, 14, 67, 'پیش از شما، سنّت‌هایی وجود داشت؛ (و هر قوم، طبق اعمال و صفات خود، سرنوشتهایی داشتند؛ که شما نیز، همانند آن را دارید.) پس در روی زمین، گردش کنید و ببینید سرانجام تکذیب‌کنندگان (آیات خدا) چگونه بود؟!'),
(431, 431, 'هَٰذَا بَيَانٌ لِلنَّاسِ وَهُدًى وَمَوْعِظَةٌ لِلْمُتَّقِينَ', 3, 138, 4, 14, 67, 'این، بیانی است برای عموم مردم؛ و هدایت و اندرزی است برای پرهیزگاران!'),
(432, 432, 'وَلَا تَهِنُوا وَلَا تَحْزَنُوا وَأَنْتُمُ الْأَعْلَوْنَ إِنْ كُنْتُمْ مُؤْمِنِينَ', 3, 139, 4, 14, 67, 'و سست نشوید! و غمگین نگردید! و شما برترید اگر ایمان داشته باشید!'),
(433, 433, 'إِنْ يَمْسَسْكُمْ قَرْحٌ فَقَدْ مَسَّ الْقَوْمَ قَرْحٌ مِثْلُهُ ۚ وَتِلْكَ الْأَيَّامُ نُدَاوِلُهَا بَيْنَ النَّاسِ وَلِيَعْلَمَ اللَّهُ الَّذِينَ آمَنُوا وَيَتَّخِذَ مِنْكُمْ شُهَدَاءَ ۗ وَاللَّهُ لَا يُحِبُّ الظَّالِمِينَ', 3, 140, 4, 14, 67, 'اگر (در میدان احد،) به شما جراحتی رسید (و ضربه‌ای وارد شد)، به آن جمعیّت نیز (در میدان بدر)، جراحتی همانند آن وارد گردید. و ما این روزها(ی پیروزی و شکست) را در میان مردم می‌گردانیم؛ (-و این خاصیّت زندگی دنیاست-) تا خدا، افرادی را که ایمان آورده‌اند، بداند (و شناخته شوند)؛ و خداوند از میان شما، شاهدانی بگیرد. و خدا ظالمان را دوست نمی‌دارد.'),
(434, 434, 'وَلِيُمَحِّصَ اللَّهُ الَّذِينَ آمَنُوا وَيَمْحَقَ الْكَافِرِينَ', 3, 141, 4, 14, 68, 'و تا خداوند، افراد باایمان را خالص گرداند (و ورزیده شوند)؛ و کافران را به تدریج نابود سازد.'),
(435, 435, 'أَمْ حَسِبْتُمْ أَنْ تَدْخُلُوا الْجَنَّةَ وَلَمَّا يَعْلَمِ اللَّهُ الَّذِينَ جَاهَدُوا مِنْكُمْ وَيَعْلَمَ الصَّابِرِينَ', 3, 142, 4, 14, 68, 'آیا چنین پنداشتید که (تنها با ادّعای ایمان) وارد بهشت خواهید شد، در حالی که خداوند هنوز مجاهدان از شما و صابران را مشخصّ نساخته است؟!'),
(436, 436, 'وَلَقَدْ كُنْتُمْ تَمَنَّوْنَ الْمَوْتَ مِنْ قَبْلِ أَنْ تَلْقَوْهُ فَقَدْ رَأَيْتُمُوهُ وَأَنْتُمْ تَنْظُرُونَ', 3, 143, 4, 14, 68, 'و شما مرگ (و شهادت در راه خدا) را، پیش از آنکه با آن روبه‌رو شوید، آرزو می‌کردید؛ سپس آن را با چشم خود دیدید، در حالی که به آن نگاه می‌کردید (و حاضر نبودید به آن تن دردهید! چقدر میان گفتار و کردار شما فاصله است؟!)'),
(437, 437, 'وَمَا مُحَمَّدٌ إِلَّا رَسُولٌ قَدْ خَلَتْ مِنْ قَبْلِهِ الرُّسُلُ ۚ أَفَإِنْ مَاتَ أَوْ قُتِلَ انْقَلَبْتُمْ عَلَىٰ أَعْقَابِكُمْ ۚ وَمَنْ يَنْقَلِبْ عَلَىٰ عَقِبَيْهِ فَلَنْ يَضُرَّ اللَّهَ شَيْئًا ۗ وَسَيَجْزِي اللَّهُ الشَّاكِرِينَ', 3, 144, 4, 14, 68, 'محمد (ص) فقط فرستاده خداست؛ و پیش از او، فرستادگان دیگری نیز بودند؛ آیا اگر او بمیرد و یا کشته شود، شما به عقب برمی‌گردید؟ (و اسلام را رها کرده به دوران جاهلیّت و کفر بازگشت خواهید نمود؟) و هر کس به عقب باز گردد، هرگز به خدا ضرری نمی‌زند؛ و خداوند بزودی شاکران (و استقامت‌کنندگان) را پاداش خواهد داد.'),
(438, 438, 'وَمَا كَانَ لِنَفْسٍ أَنْ تَمُوتَ إِلَّا بِإِذْنِ اللَّهِ كِتَابًا مُؤَجَّلًا ۗ وَمَنْ يُرِدْ ثَوَابَ الدُّنْيَا نُؤْتِهِ مِنْهَا وَمَنْ يُرِدْ ثَوَابَ الْآخِرَةِ نُؤْتِهِ مِنْهَا ۚ وَسَنَجْزِي الشَّاكِرِينَ', 3, 145, 4, 14, 68, 'هیچ‌کس، جز به فرمان خدا، نمی‌میرد؛ سرنوشتی است تعیین شده؛ (بنابر این، مرگ پیامبر یا دیگران، یک سنّت الهی است.) هر کس پاداش دنیا را بخواهد (و در زندگی خود، در این راه گام بردارد،) چیزی از آن به او خواهیم داد؛ و هر کس پاداش آخرت را بخواهد، از آن به او می‌دهیم؛ و بزودی سپاسگزاران را پاداش خواهیم داد.'),
(439, 439, 'وَكَأَيِّنْ مِنْ نَبِيٍّ قَاتَلَ مَعَهُ رِبِّيُّونَ كَثِيرٌ فَمَا وَهَنُوا لِمَا أَصَابَهُمْ فِي سَبِيلِ اللَّهِ وَمَا ضَعُفُوا وَمَا اسْتَكَانُوا ۗ وَاللَّهُ يُحِبُّ الصَّابِرِينَ', 3, 146, 4, 14, 68, 'چه بسیار پیامبرانی که مردان الهی فراوانی به همراه آنان جنگ کردند! آنها هیچ‌گاه در برابر آنچه در راه خدا به آنان می‌رسید، سست و ناتوان نشدند (و تن به تسلیم ندادند)؛ و خداوند استقامت‌کنندگان را دوست دارد.'),
(440, 440, 'وَمَا كَانَ قَوْلَهُمْ إِلَّا أَنْ قَالُوا رَبَّنَا اغْفِرْ لَنَا ذُنُوبَنَا وَإِسْرَافَنَا فِي أَمْرِنَا وَثَبِّتْ أَقْدَامَنَا وَانْصُرْنَا عَلَى الْقَوْمِ الْكَافِرِينَ', 3, 147, 4, 14, 68, 'سخنشان تنها این بود که: «پروردگارا! گناهان ما را ببخش! و از تندرویهای ما در کارها، چشم‌پوشی کن! قدمهای ما را استوار بدار! و ما را بر جمعیّت کافران، پیروز گردان!'),
(441, 441, 'فَآتَاهُمُ اللَّهُ ثَوَابَ الدُّنْيَا وَحُسْنَ ثَوَابِ الْآخِرَةِ ۗ وَاللَّهُ يُحِبُّ الْمُحْسِنِينَ', 3, 148, 4, 14, 68, 'از این‌رو خداوند پاداش این جهان، و پاداش نیک آن جهان را به آنها داد؛ و خداوند نیکوکاران را دوست می‌دارد.'),
(442, 442, 'يَا أَيُّهَا الَّذِينَ آمَنُوا إِنْ تُطِيعُوا الَّذِينَ كَفَرُوا يَرُدُّوكُمْ عَلَىٰ أَعْقَابِكُمْ فَتَنْقَلِبُوا خَاسِرِينَ', 3, 149, 4, 14, 69, 'ای کسانی که ایمان آورده‌اید! اگر از کسانی که کافر شده‌اند اطاعت کنید، شما را به گذشته‌هایتان بازمی‌گردانند؛ و سرانجام، زیانکار خواهید شد.'),
(443, 443, 'بَلِ اللَّهُ مَوْلَاكُمْ ۖ وَهُوَ خَيْرُ النَّاصِرِينَ', 3, 150, 4, 14, 69, '(آنها تکیه‌گاه شما نیستند،) بلکه ولی و سرپرست شما، خداست؛ و او بهترین یاوران است.'),
(444, 444, 'سَنُلْقِي فِي قُلُوبِ الَّذِينَ كَفَرُوا الرُّعْبَ بِمَا أَشْرَكُوا بِاللَّهِ مَا لَمْ يُنَزِّلْ بِهِ سُلْطَانًا ۖ وَمَأْوَاهُمُ النَّارُ ۚ وَبِئْسَ مَثْوَى الظَّالِمِينَ', 3, 151, 4, 14, 69, 'بزودی در دلهای کافران، بخاطر اینکه بدون دلیل، چیزهایی را برای خدا همتا قرار دادند، رعب و ترس می‌افکنیم؛ و جایگاه آنها، آتش است؛ و چه بد جایگاهی است جایگاه ستمکاران!'),
(445, 445, 'وَلَقَدْ صَدَقَكُمُ اللَّهُ وَعْدَهُ إِذْ تَحُسُّونَهُمْ بِإِذْنِهِ ۖ حَتَّىٰ إِذَا فَشِلْتُمْ وَتَنَازَعْتُمْ فِي الْأَمْرِ وَعَصَيْتُمْ مِنْ بَعْدِ مَا أَرَاكُمْ مَا تُحِبُّونَ ۚ مِنْكُمْ مَنْ يُرِيدُ الدُّنْيَا وَمِنْكُمْ مَنْ يُرِيدُ الْآخِرَةَ ۚ ثُمَّ صَرَفَكُمْ عَنْهُمْ لِيَبْتَلِيَكُمْ ۖ وَلَقَدْ عَفَا عَنْكُمْ ۗ وَاللَّهُ ذُو فَضْلٍ عَلَى الْمُؤْمِنِينَ', 3, 152, 4, 14, 69, 'خداوند، وعده خود را به شما، (در باره پیروزی بر دشمن در احد،) تحقق بخشید؛ در آن هنگام (که در آغاز جنگ،) دشمنان را به فرمان او، به قتل می‌رساندید؛ (و این پیروزی ادامه داشت) تا اینکه سست شدید؛ و (بر سر رهاکردن سنگرها،) در کار خود به نزاع پرداختید؛ و بعد از آن که آنچه را دوست می‌داشتید (از غلبه بر دشمن) به شما نشان داد، نافرمانی کردید. بعضی از شما، خواهان دنیا بودند؛ و بعضی خواهان آخرت. سپس خداوند شما را از آنان منصرف ساخت؛ (و پیروزی شما به شکست انجامید؛) تا شما را آزمایش کند. و او شما را بخشید؛ و خداوند نسبت به مؤمنان، فضل و بخشش دارد.'),
(446, 446, ' إِذْ تُصْعِدُونَ وَلَا تَلْوُونَ عَلَىٰ أَحَدٍ وَالرَّسُولُ يَدْعُوكُمْ فِي أُخْرَاكُمْ فَأَثَابَكُمْ غَمًّا بِغَمٍّ لِكَيْلَا تَحْزَنُوا عَلَىٰ مَا فَاتَكُمْ وَلَا مَا أَصَابَكُمْ ۗ وَاللَّهُ خَبِيرٌ بِمَا تَعْمَلُونَ', 3, 153, 4, 14, 69, '(به خاطر بیاورید) هنگامی را که از کوه بالا میرفتید؛ و جمعی در وسط بیابان پراکنده شدند؛ و از شدت وحشت،) به عقب ماندگان نگاه نمی‌کردید، و پیامبر از پشت سر، شما را صدا می‌زد. سپس اندوه‌ها را یکی پس از دیگری به شما جزا داد؛ این بخاطر آن بود که دیگر برای از دست رفتن (غنایم جنگی) غمگین نشوید، و نه بخاطر مصیبت‌هایی که بر شما وارد می‌گردد. و خداوند از آنچه انجام می‌دهید، آگاه است.'),
(447, 447, 'ثُمَّ أَنْزَلَ عَلَيْكُمْ مِنْ بَعْدِ الْغَمِّ أَمَنَةً نُعَاسًا يَغْشَىٰ طَائِفَةً مِنْكُمْ ۖ وَطَائِفَةٌ قَدْ أَهَمَّتْهُمْ أَنْفُسُهُمْ يَظُنُّونَ بِاللَّهِ غَيْرَ الْحَقِّ ظَنَّ الْجَاهِلِيَّةِ ۖ يَقُولُونَ هَلْ لَنَا مِنَ الْأَمْرِ مِنْ شَيْءٍ ۗ قُلْ إِنَّ الْأَمْرَ كُلَّهُ لِلَّهِ ۗ يُخْفُونَ فِي أَنْفُسِهِمْ مَا لَا يُبْدُونَ لَكَ ۖ يَقُولُونَ لَوْ كَانَ لَنَا مِنَ الْأَمْرِ شَيْءٌ مَا قُتِلْنَا هَاهُنَا ۗ قُلْ لَوْ كُنْتُمْ فِي بُيُوتِكُمْ لَبَرَزَ الَّذِينَ كُتِبَ عَلَيْهِمُ الْقَتْلُ إِلَىٰ مَضَاجِعِهِمْ ۖ وَلِيَبْتَلِيَ اللَّهُ مَا فِي صُدُورِكُمْ وَلِيُمَحِّصَ مَا فِي قُلُوبِكُمْ ۗ وَاللَّهُ عَلِيمٌ بِذَاتِ الصُّدُورِ', 3, 154, 4, 14, 70, 'سپس بدنبال این غم و اندوه، آرامشی بر شما فرستاد. این آرامش، بصورت خواب سبکی بود که (در شب بعد از حادثه احد،) گروهی از شما را فرا گرفت؛ اماگروه دیگری در فکر جان خویش بودند؛ (و خواب به چشمانشان نرفت.) آنها گمانهای نادرستی -همچون گمانهای دوران جاهلیت- درباره خدا داشتند؛ و می‌گفتند: «آیا چیزی از پیروزی نصیب ما می‌شود؟!» بگو: «همه کارها (و پیروزیها) به دست خداست!» آنها در دل خود، چیزی را پنهان می‌دارند که برای تو آشکار نمی‌سازند؛ می‌گویند: «اگر ما سهمی از پیروزی داشتیم، در این جا کشته نمی‌شدیم!» بگو: «اگر هم در خانه‌های خود بودید، آنهایی که کشته‌شدن بر آنها مقرر شده بود، قطعاً به سوی آرامگاه‌های خود، بیرون می‌آمدند (و آنها را به قتل می‌رساندند). و اینها برای این است که خداوند، آنچه در سینه‌هایتان پنهان دارید، بیازماید؛ و آنچه را در دلهای شما (از ایمان) است، خالص گرداند؛ و خداوند از آنچه در درون سینه‌هاست، با خبر است.'),
(448, 448, 'إِنَّ الَّذِينَ تَوَلَّوْا مِنْكُمْ يَوْمَ الْتَقَى الْجَمْعَانِ إِنَّمَا اسْتَزَلَّهُمُ الشَّيْطَانُ بِبَعْضِ مَا كَسَبُوا ۖ وَلَقَدْ عَفَا اللَّهُ عَنْهُمْ ۗ إِنَّ اللَّهَ غَفُورٌ حَلِيمٌ', 3, 155, 4, 14, 70, 'کسانی که در روز روبرو شدن دو جمعیت با یکدیگر (در جنگ احد)، فرار کردند، شیطان آنها را بر اثر بعضی از گناهانی که مرتکب شده بودند، به لغزش انداخت؛ و خداوند آنها را بخشید. خداوند، آمرزنده و بردبار است.'),
(449, 449, 'يَا أَيُّهَا الَّذِينَ آمَنُوا لَا تَكُونُوا كَالَّذِينَ كَفَرُوا وَقَالُوا لِإِخْوَانِهِمْ إِذَا ضَرَبُوا فِي الْأَرْضِ أَوْ كَانُوا غُزًّى لَوْ كَانُوا عِنْدَنَا مَا مَاتُوا وَمَا قُتِلُوا لِيَجْعَلَ اللَّهُ ذَٰلِكَ حَسْرَةً فِي قُلُوبِهِمْ ۗ وَاللَّهُ يُحْيِي وَيُمِيتُ ۗ وَاللَّهُ بِمَا تَعْمَلُونَ بَصِيرٌ', 3, 156, 4, 14, 70, 'ای کسانی که ایمان آورده‌اید! همانند کافران نباشید که چون برادرانشان به مسافرتی می‌روند، یا در جنگ شرکت می‌کنند (و از دنیا می‌روند و یا کشته می‌شوند)، می‌گویند: «اگر آنها نزد ما بودند، نمی‌مردند و کشته نمی‌شدند!» (شما از این گونه سخنان نگویید،) تا خدا این حسرت را بر دل آنها = کافران‌ بگذارد. خداوند، زنده می‌کند و می‌میراند؛ (و زندگی و مرگ، به دست اوست؛) و خدا به آنچه انجام می‌دهید، بیناست.'),
(450, 450, 'وَلَئِنْ قُتِلْتُمْ فِي سَبِيلِ اللَّهِ أَوْ مُتُّمْ لَمَغْفِرَةٌ مِنَ اللَّهِ وَرَحْمَةٌ خَيْرٌ مِمَّا يَجْمَعُونَ', 3, 157, 4, 14, 70, 'اگر هم در راه خدا کشته شوید یا بمیرید، (زیان نکرده‌اید؛ زیرا) آمرزش و رحمت خدا، از تمام آنچه آنها (در طول عمر خود،) جمع آوری میکنند، بهتر است!');
INSERT INTO `Quran` (`rowid`, `id`, `Arabic`, `Chapter`, `Verse`, `Juz`, `Hizb`, `Page`, `tr`) VALUES
(451, 451, 'وَلَئِنْ مُتُّمْ أَوْ قُتِلْتُمْ لَإِلَى اللَّهِ تُحْشَرُونَ', 3, 158, 4, 14, 71, 'و اگر بمیرید یا کشته شوید، به سوی خدا محشور می‌شوید. (بنابراین، فانی نمی‌شوید که از فنا، وحشت داشته باشید.)'),
(452, 452, 'فَبِمَا رَحْمَةٍ مِنَ اللَّهِ لِنْتَ لَهُمْ ۖ وَلَوْ كُنْتَ فَظًّا غَلِيظَ الْقَلْبِ لَانْفَضُّوا مِنْ حَوْلِكَ ۖ فَاعْفُ عَنْهُمْ وَاسْتَغْفِرْ لَهُمْ وَشَاوِرْهُمْ فِي الْأَمْرِ ۖ فَإِذَا عَزَمْتَ فَتَوَكَّلْ عَلَى اللَّهِ ۚ إِنَّ اللَّهَ يُحِبُّ الْمُتَوَكِّلِينَ', 3, 159, 4, 14, 71, 'به (برکت) رحمت الهی، در برابر آنان = مردم‌ نرم (و مهربان) شدی! و اگر خشن و سنگدل بودی، از اطراف تو، پراکنده می‌شدند. پس آنها را ببخش و برای آنها آمرزش بطلب! و در کارها، با آنان مشورت کن! اما هنگامی که تصمیم گرفتی، (قاطع باش! و) بر خدا توکل کن! زیرا خداوند متوکلان را دوست دارد.'),
(453, 453, 'إِنْ يَنْصُرْكُمُ اللَّهُ فَلَا غَالِبَ لَكُمْ ۖ وَإِنْ يَخْذُلْكُمْ فَمَنْ ذَا الَّذِي يَنْصُرُكُمْ مِنْ بَعْدِهِ ۗ وَعَلَى اللَّهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُونَ', 3, 160, 4, 14, 71, 'اگر خداوند شما را یاری کند، هیچ کس بر شما پیروز نخواهد شد! و اگر دست از یاری شما بردارد، کیست که بعد از او، شما را یاری کند؟! و مؤمنان، تنها بر خداوند باید توکل کنند!'),
(454, 454, 'وَمَا كَانَ لِنَبِيٍّ أَنْ يَغُلَّ ۚ وَمَنْ يَغْلُلْ يَأْتِ بِمَا غَلَّ يَوْمَ الْقِيَامَةِ ۚ ثُمَّ تُوَفَّىٰ كُلُّ نَفْسٍ مَا كَسَبَتْ وَهُمْ لَا يُظْلَمُونَ', 3, 161, 4, 14, 71, '(گمان کردید ممکن است پیامبر به شما خیانت کند؟! در حالی که) ممکن نیست هیچ پیامبری خیانت کند! و هر کس خیانت کند، روز رستاخیز، آنچه را در آن خیانت کرده، با خود (به صحنه محشر) می‌آورد؛ سپس به هر کس، آنچه را فراهم کرده (و انجام داده است)، بطور کامل داده می‌شود؛ و (به همین دلیل) به آنها ستم نخواهد شد (چرا که محصول اعمال خود را خواهند دید).'),
(455, 455, 'أَفَمَنِ اتَّبَعَ رِضْوَانَ اللَّهِ كَمَنْ بَاءَ بِسَخَطٍ مِنَ اللَّهِ وَمَأْوَاهُ جَهَنَّمُ ۚ وَبِئْسَ الْمَصِيرُ', 3, 162, 4, 14, 71, 'آیا کسی که از رضای خدا پیروی کرده، همانند کسی است که به خشم و غضب خدا بازگشته؟! و جایگاه او جهنم، و پایان کار او بسیار بد است.'),
(456, 456, 'هُمْ دَرَجَاتٌ عِنْدَ اللَّهِ ۗ وَاللَّهُ بَصِيرٌ بِمَا يَعْمَلُونَ', 3, 163, 4, 14, 71, 'هر یک از آنان، درجه و مقامی در پیشگاه خدا دارند؛ و خداوند به آنچه انجام می‌دهند، بیناست.'),
(457, 457, 'لَقَدْ مَنَّ اللَّهُ عَلَى الْمُؤْمِنِينَ إِذْ بَعَثَ فِيهِمْ رَسُولًا مِنْ أَنْفُسِهِمْ يَتْلُو عَلَيْهِمْ آيَاتِهِ وَيُزَكِّيهِمْ وَيُعَلِّمُهُمُ الْكِتَابَ وَالْحِكْمَةَ وَإِنْ كَانُوا مِنْ قَبْلُ لَفِي ضَلَالٍ مُبِينٍ', 3, 164, 4, 14, 71, 'خداوند بر مؤمنان منت نهاد = نعمت بزرگی بخشید هنگامی که در میان آنها، پیامبری از خودشان برانگیخت؛ که آیات او را بر آنها بخواند، و آنها را پاک کند و کتاب و حکمت بیاموزد؛ هر چند پیش از آن، در گمراهی آشکاری بودند.'),
(458, 458, 'أَوَلَمَّا أَصَابَتْكُمْ مُصِيبَةٌ قَدْ أَصَبْتُمْ مِثْلَيْهَا قُلْتُمْ أَنَّىٰ هَٰذَا ۖ قُلْ هُوَ مِنْ عِنْدِ أَنْفُسِكُمْ ۗ إِنَّ اللَّهَ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ', 3, 165, 4, 14, 71, 'آیا هنگامی که مصیبتی (در میدان جنک احد) به شما رسید، در حالی که دو برابر آن را (در میدان جنگ بدر بر دشمن) وارد ساخته بودید، گفتید: «این مصیبت از کجاست؟!» بگو: «از ناحیه خود شماست (که در میدان جنگ احد، با دستور پیامبر مخالفت کردید)! خداوند بر هر چیزی قادر است. (و چنانچه روش خود را اصلاح کنید، در آینده شما را پیروز می‌کند.)»'),
(459, 459, 'وَمَا أَصَابَكُمْ يَوْمَ الْتَقَى الْجَمْعَانِ فَبِإِذْنِ اللَّهِ وَلِيَعْلَمَ الْمُؤْمِنِينَ', 3, 166, 4, 14, 72, 'و آنچه (در روز احد،) در روزی که دو دسته = مؤمنان و کافران‌ با هم نبرد کردند به شما رسید، به فرمان خدا (و طبق سنت الهی) بود؛ و برای این بود که مؤمنان را مشخص کند.'),
(460, 460, 'وَلِيَعْلَمَ الَّذِينَ نَافَقُوا ۚ وَقِيلَ لَهُمْ تَعَالَوْا قَاتِلُوا فِي سَبِيلِ اللَّهِ أَوِ ادْفَعُوا ۖ قَالُوا لَوْ نَعْلَمُ قِتَالًا لَاتَّبَعْنَاكُمْ ۗ هُمْ لِلْكُفْرِ يَوْمَئِذٍ أَقْرَبُ مِنْهُمْ لِلْإِيمَانِ ۚ يَقُولُونَ بِأَفْوَاهِهِمْ مَا لَيْسَ فِي قُلُوبِهِمْ ۗ وَاللَّهُ أَعْلَمُ بِمَا يَكْتُمُونَ', 3, 167, 4, 14, 72, 'و نیز برای این بود که منافقان شناخته شوند؛ آنهایی که به ایشان گفته شد: «بیایید در راه خدا نبرد کنید! یا (حداقل) از حریم خود، دفاع نمایید!» گفتند: «اگر می‌دانستیم جنگی روی خواهد داد، از شما پیروی می‌کردیم. (اما می‌دانیم جنگی نمی‌شود.)» آنها در آن هنگام، به کفر نزدیکتر بودند تا به ایمان؛ به زبان خود چیزی می‌گویند که در دلهایشان نیست! و خداوند از آنچه کتمان می‌کنند، آگاهتر است.'),
(461, 461, 'الَّذِينَ قَالُوا لِإِخْوَانِهِمْ وَقَعَدُوا لَوْ أَطَاعُونَا مَا قُتِلُوا ۗ قُلْ فَادْرَءُوا عَنْ أَنْفُسِكُمُ الْمَوْتَ إِنْ كُنْتُمْ صَادِقِينَ', 3, 168, 4, 14, 72, '(منافقان) آنها هستند که به برادران خود -در حالی که از حمایت آنها دست کشیده بودند- گفتند: «اگر آنها از ما پیروی می‌کردند، کشته نمی‌شدند!» بگو: «(مگر شما می‌توانید مرگ افراد را پیش‌بینی کنید؟!) پس مرگ را از خودتان دور سازید اگر راست می‌گویید!»'),
(462, 462, 'وَلَا تَحْسَبَنَّ الَّذِينَ قُتِلُوا فِي سَبِيلِ اللَّهِ أَمْوَاتًا ۚ بَلْ أَحْيَاءٌ عِنْدَ رَبِّهِمْ يُرْزَقُونَ', 3, 169, 4, 14, 72, '(ای پیامبر!) هرگز گمان مبر کسانی که در راه خدا کشته شدند، مردگانند! بلکه آنان زنده‌اند، و نزد پروردگارشان روزی داده می‌شوند.'),
(463, 463, 'فَرِحِينَ بِمَا آتَاهُمُ اللَّهُ مِنْ فَضْلِهِ وَيَسْتَبْشِرُونَ بِالَّذِينَ لَمْ يَلْحَقُوا بِهِمْ مِنْ خَلْفِهِمْ أَلَّا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَ', 3, 170, 4, 14, 72, 'آنها بخاطر نعمتهای فراوانی که خداوند از فضل خود به ایشان بخشیده است، خوشحالند؛ و بخاطر کسانی که هنوز به آنها ملحق نشده‌اند = مجاهدان و شهیدان آینده، خوشوقتند؛ (زیرا مقامات برجسته آنها را در آن جهان می‌بینند؛ و می‌دانند) که نه ترسی بر آنهاست، و نه غمی خواهند داشت.'),
(464, 464, ' يَسْتَبْشِرُونَ بِنِعْمَةٍ مِنَ اللَّهِ وَفَضْلٍ وَأَنَّ اللَّهَ لَا يُضِيعُ أَجْرَ الْمُؤْمِنِينَ', 3, 171, 4, 15, 72, 'و از نعمت خدا و فضل او (نسبت به خودشان نیز) مسرورند؛ و (می‌بینند که) خداوند، پاداش مؤمنان را ضایع نمی‌کند؛ (نه پاداش شهیدان، و نه پاداش مجاهدانی که شهید نشدند).'),
(465, 465, 'الَّذِينَ اسْتَجَابُوا لِلَّهِ وَالرَّسُولِ مِنْ بَعْدِ مَا أَصَابَهُمُ الْقَرْحُ ۚ لِلَّذِينَ أَحْسَنُوا مِنْهُمْ وَاتَّقَوْا أَجْرٌ عَظِيمٌ', 3, 172, 4, 15, 72, 'آنها که دعوت خدا و پیامبر (ص) را، پس از آن همه جراحاتی که به ایشان رسید، اجابت کردند؛ (و هنوز زخمهای میدان احد التیام نیافته بود، به سوی میدان «حمرار الاسد» حرکت نمودند؛) برای کسانی از آنها، که نیکی کردند و تقوا پیش گرفتند، پاداش بزرگی است.'),
(466, 466, 'الَّذِينَ قَالَ لَهُمُ النَّاسُ إِنَّ النَّاسَ قَدْ جَمَعُوا لَكُمْ فَاخْشَوْهُمْ فَزَادَهُمْ إِيمَانًا وَقَالُوا حَسْبُنَا اللَّهُ وَنِعْمَ الْوَكِيلُ', 3, 173, 4, 15, 72, 'اینها کسانی بودند که (بعضی از) مردم، به آنان گفتند: «مردم = لشکر دشمن برای (حمله به) شما اجتماع کرده‌اند؛ از آنها بترسید!» اما این سخن، بر ایمانشان افزود؛ و گفتند: «خدا ما را کافی است؛ و او بهترین حامی ماست.»'),
(467, 467, 'فَانْقَلَبُوا بِنِعْمَةٍ مِنَ اللَّهِ وَفَضْلٍ لَمْ يَمْسَسْهُمْ سُوءٌ وَاتَّبَعُوا رِضْوَانَ اللَّهِ ۗ وَاللَّهُ ذُو فَضْلٍ عَظِيمٍ', 3, 174, 4, 15, 73, 'به همین جهت، آنها (از این میدان،) با نعمت و فضل پروردگارشان، بازگشتند؛ در حالی که هیچ ناراحتی به آنان نرسید؛ و از رضای خدا، پیروی کردند؛ و خداوند دارای فضل و بخشش بزرگی است.'),
(468, 468, 'إِنَّمَا ذَٰلِكُمُ الشَّيْطَانُ يُخَوِّفُ أَوْلِيَاءَهُ فَلَا تَخَافُوهُمْ وَخَافُونِ إِنْ كُنْتُمْ مُؤْمِنِينَ', 3, 175, 4, 15, 73, 'این فقط شیطان است که پیروان خود را (با سخنان و شایعات بی‌اساس،) می‌ترساند. از آنها نترسید! و تنها از من بترسید اگر ایمان دارید!'),
(469, 469, 'وَلَا يَحْزُنْكَ الَّذِينَ يُسَارِعُونَ فِي الْكُفْرِ ۚ إِنَّهُمْ لَنْ يَضُرُّوا اللَّهَ شَيْئًا ۗ يُرِيدُ اللَّهُ أَلَّا يَجْعَلَ لَهُمْ حَظًّا فِي الْآخِرَةِ ۖ وَلَهُمْ عَذَابٌ عَظِيمٌ', 3, 176, 4, 15, 73, 'کسانی که در راه کفر، شتاب میکنند، تو را غمگین نسازند! به یقین، آنها هرگز زیانی به خداوند نمی‌رسانند. (بعلاوه) خدا می‌خواهد (آنها را به حال خودشان واگذارد؛ و در نتیجه،) بهره‌ای برای آنها در آخرت قرار ندهد. و برای آنها مجازات بزرگی است!'),
(470, 470, 'إِنَّ الَّذِينَ اشْتَرَوُا الْكُفْرَ بِالْإِيمَانِ لَنْ يَضُرُّوا اللَّهَ شَيْئًا وَلَهُمْ عَذَابٌ أَلِيمٌ', 3, 177, 4, 15, 73, '(آری،) کسانی که ایمان را دادند و کفر را خریداری کردند، هرگز به خدا زیانی نمی‌رسانند؛ و برای آنها، مجازات دردناکی است!'),
(471, 471, 'وَلَا يَحْسَبَنَّ الَّذِينَ كَفَرُوا أَنَّمَا نُمْلِي لَهُمْ خَيْرٌ لِأَنْفُسِهِمْ ۚ إِنَّمَا نُمْلِي لَهُمْ لِيَزْدَادُوا إِثْمًا ۚ وَلَهُمْ عَذَابٌ مُهِينٌ', 3, 178, 4, 15, 73, 'آنها که کافر شدند، (و راه طغیان پیش گرفتند،) تصور نکنند اگر به آنان مهلت می‌دهیم، به سودشان است! ما به آنان مهلت می‌دهیم فقط برای اینکه بر گناهان خود بیفزایند؛ و برای آنها، عذاب خوارکننده‌ای (آماده شده) است!'),
(472, 472, 'مَا كَانَ اللَّهُ لِيَذَرَ الْمُؤْمِنِينَ عَلَىٰ مَا أَنْتُمْ عَلَيْهِ حَتَّىٰ يَمِيزَ الْخَبِيثَ مِنَ الطَّيِّبِ ۗ وَمَا كَانَ اللَّهُ لِيُطْلِعَكُمْ عَلَى الْغَيْبِ وَلَٰكِنَّ اللَّهَ يَجْتَبِي مِنْ رُسُلِهِ مَنْ يَشَاءُ ۖ فَآمِنُوا بِاللَّهِ وَرُسُلِهِ ۚ وَإِنْ تُؤْمِنُوا وَتَتَّقُوا فَلَكُمْ أَجْرٌ عَظِيمٌ', 3, 179, 4, 15, 73, 'چنین نبود که خداوند، مؤمنان را به همان‌گونه که شما هستید واگذارد؛ مگر آنکه ناپاک را از پاک جدا سازد. و نیز چنین نبود که خداوند شما را از اسرار غیب، آگاه کند (تا مؤمنان و منافقان را از این راه بشناسید؛ این بر خلاف سنت الهی است؛) ولی خداوند از میان رسولان خود، هر کس را بخواهد برمیگزیند؛ (و قسمتی از اسرار نهان را که برای مقام رهبری او لازم است، در اختیار او می‌گذارد.) پس (اکنون که این جهان، بوته آزمایش پاک و ناپاک است،) به خدا و رسولان او ایمان بیاورید! و اگر ایمان بیاورید و تقوا پیشه کنید، پاداش بزرگی برای شماست.'),
(473, 473, 'وَلَا يَحْسَبَنَّ الَّذِينَ يَبْخَلُونَ بِمَا آتَاهُمُ اللَّهُ مِنْ فَضْلِهِ هُوَ خَيْرًا لَهُمْ ۖ بَلْ هُوَ شَرٌّ لَهُمْ ۖ سَيُطَوَّقُونَ مَا بَخِلُوا بِهِ يَوْمَ الْقِيَامَةِ ۗ وَلِلَّهِ مِيرَاثُ السَّمَاوَاتِ وَالْأَرْضِ ۗ وَاللَّهُ بِمَا تَعْمَلُونَ خَبِيرٌ', 3, 180, 4, 15, 73, 'کسانی که بخل می‌ورزند، و آنچه را خدا از فضل خویش به آنان داده، انفاق نمی‌کنند، گمان نکنند این کار به سود آنها است؛ بلکه برای آنها شر است؛ بزودی در روز قیامت، آنچه را نسبت به آن بخل ورزیدند، همانند طوقی به گردنشان می‌افکنند. و میراث آسمانها و زمین، از آن خداست؛ و خداوند، از آنچه انجام می‌دهید، آگاه است.'),
(474, 474, 'لَقَدْ سَمِعَ اللَّهُ قَوْلَ الَّذِينَ قَالُوا إِنَّ اللَّهَ فَقِيرٌ وَنَحْنُ أَغْنِيَاءُ ۘ سَنَكْتُبُ مَا قَالُوا وَقَتْلَهُمُ الْأَنْبِيَاءَ بِغَيْرِ حَقٍّ وَنَقُولُ ذُوقُوا عَذَابَ الْحَرِيقِ', 3, 181, 4, 15, 74, 'خداوند، سخن آنها را که گفتند: «خدا فقیر است، و ما بی‌نیازیم»، شنید! به زودی آنچه را گفتند، خواهیم نوشت؛ و (همچنین) کشتن پیامبران را بناحق (می‌نویسیم)؛ و به آنها می‌گوییم: «بچشید عذاب سوزان را (در برابر کارهایتان!)»'),
(475, 475, 'ذَٰلِكَ بِمَا قَدَّمَتْ أَيْدِيكُمْ وَأَنَّ اللَّهَ لَيْسَ بِظَلَّامٍ لِلْعَبِيدِ', 3, 182, 4, 15, 74, 'این بخاطر چیزی است که دستهای شما از پیش فرستاده (و نتیجه کار شماست) و بخاطر آن است که خداوند، به بندگان (خود)، ستم نمی‌کند.'),
(476, 476, 'الَّذِينَ قَالُوا إِنَّ اللَّهَ عَهِدَ إِلَيْنَا أَلَّا نُؤْمِنَ لِرَسُولٍ حَتَّىٰ يَأْتِيَنَا بِقُرْبَانٍ تَأْكُلُهُ النَّارُ ۗ قُلْ قَدْ جَاءَكُمْ رُسُلٌ مِنْ قَبْلِي بِالْبَيِّنَاتِ وَبِالَّذِي قُلْتُمْ فَلِمَ قَتَلْتُمُوهُمْ إِنْ كُنْتُمْ صَادِقِينَ', 3, 183, 4, 15, 74, '(اینها) همان کسانی (هستند) که گفتند: «خداوند از ما پیمان گرفته که به هیچ پیامبری ایمان نیاوریم تا (این معجزه را انجام دهد:) یک قربانی بیاورد، که آتش = صاعقه آسمانی‌ آن را بخورد!» بگو: «پیامبرانی پیش از من، برای شما آمدند؛ و دلایل روشن، و آنچه را گفتید آوردند؛ پس چرا آنها را به قتل رساندید اگر راست می‌گویید؟!»'),
(477, 477, 'فَإِنْ كَذَّبُوكَ فَقَدْ كُذِّبَ رُسُلٌ مِنْ قَبْلِكَ جَاءُوا بِالْبَيِّنَاتِ وَالزُّبُرِ وَالْكِتَابِ الْمُنِيرِ', 3, 184, 4, 15, 74, 'پس (اگر این بهانه‌جویان،) تو را تکذیب کنند، (چیز تازه‌ای نیست؛) رسولان پیش از تو (نیز) تکذیب شدند؛ پیامبرانی که دلایل آشکار، و نوشته‌های متین و محکم، و کتاب روشنی‌بخش آورده بودند.'),
(478, 478, 'كُلُّ نَفْسٍ ذَائِقَةُ الْمَوْتِ ۗ وَإِنَّمَا تُوَفَّوْنَ أُجُورَكُمْ يَوْمَ الْقِيَامَةِ ۖ فَمَنْ زُحْزِحَ عَنِ النَّارِ وَأُدْخِلَ الْجَنَّةَ فَقَدْ فَازَ ۗ وَمَا الْحَيَاةُ الدُّنْيَا إِلَّا مَتَاعُ الْغُرُورِ', 3, 185, 4, 15, 74, 'هر کسی مرگ را می‌چشد؛ و شما پاداش خود را بطور کامل در روز قیامت خواهید گرفت؛ آنها که از آتش (دوزخ) دور شده، و به بهشت وارد شوند نجات یافته و رستگار شده‌اند و زندگی دنیا، چیزی جز سرمایه فریب نیست!'),
(479, 479, ' لَتُبْلَوُنَّ فِي أَمْوَالِكُمْ وَأَنْفُسِكُمْ وَلَتَسْمَعُنَّ مِنَ الَّذِينَ أُوتُوا الْكِتَابَ مِنْ قَبْلِكُمْ وَمِنَ الَّذِينَ أَشْرَكُوا أَذًى كَثِيرًا ۚ وَإِنْ تَصْبِرُوا وَتَتَّقُوا فَإِنَّ ذَٰلِكَ مِنْ عَزْمِ الْأُمُورِ', 3, 186, 4, 15, 74, 'به یقین (همه شما) در اموال و جانهای خود، آزمایش می‌شوید! و از کسانی که پیش از شما به آنها کتاب (آسمانی) داده شده = یهود، و (همچنین) از مشرکان، سخنان آزاردهنده فراوان خواهید شنید! و اگر استقامت کنید و تقوا پیشه سازید، (شایسته‌تر است؛ زیرا) این از کارهای مهم و قابل اطمینان است.'),
(480, 480, 'وَإِذْ أَخَذَ اللَّهُ مِيثَاقَ الَّذِينَ أُوتُوا الْكِتَابَ لَتُبَيِّنُنَّهُ لِلنَّاسِ وَلَا تَكْتُمُونَهُ فَنَبَذُوهُ وَرَاءَ ظُهُورِهِمْ وَاشْتَرَوْا بِهِ ثَمَنًا قَلِيلًا ۖ فَبِئْسَ مَا يَشْتَرُونَ', 3, 187, 4, 15, 75, 'و (به خاطر بیاورید) هنگامی را که خدا، از کسانی که کتاب (آسمانی) به آنها داده شده، پیمان گرفت که حتماً آن را برای مردم آشکار سازید و کتمان نکنید! ولی آنها، آن را پشت سر افکندند؛ و به بهای کمی فروختند؛ و چه بد متاعی می‌خرند؟!'),
(481, 481, 'لَا تَحْسَبَنَّ الَّذِينَ يَفْرَحُونَ بِمَا أَتَوْا وَيُحِبُّونَ أَنْ يُحْمَدُوا بِمَا لَمْ يَفْعَلُوا فَلَا تَحْسَبَنَّهُمْ بِمَفَازَةٍ مِنَ الْعَذَابِ ۖ وَلَهُمْ عَذَابٌ أَلِيمٌ', 3, 188, 4, 15, 75, 'گمان مبر آنها که از اعمال (زشت) خود خوشحال می‌شوند، و دوست دارند در برابر کار (نیکی) که انجام نداده‌اند مورد ستایش قرار گیرند، از عذاب (الهی) برکنارند! (بلکه) برای آنها، عذاب دردناکی است!'),
(482, 482, 'وَلِلَّهِ مُلْكُ السَّمَاوَاتِ وَالْأَرْضِ ۗ وَاللَّهُ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ', 3, 189, 4, 15, 75, 'و حکومت آسمانها و زمین، از آن خداست؛ و خدا بر همه چیز تواناست.'),
(483, 483, 'إِنَّ فِي خَلْقِ السَّمَاوَاتِ وَالْأَرْضِ وَاخْتِلَافِ اللَّيْلِ وَالنَّهَارِ لَآيَاتٍ لِأُولِي الْأَلْبَابِ', 3, 190, 4, 15, 75, 'مسلماً در آفرینش آسمانها و زمین، و آمد و رفت شب و روز، نشانه‌های (روشنی) برای خردمندان است.'),
(484, 484, 'الَّذِينَ يَذْكُرُونَ اللَّهَ قِيَامًا وَقُعُودًا وَعَلَىٰ جُنُوبِهِمْ وَيَتَفَكَّرُونَ فِي خَلْقِ السَّمَاوَاتِ وَالْأَرْضِ رَبَّنَا مَا خَلَقْتَ هَٰذَا بَاطِلًا سُبْحَانَكَ فَقِنَا عَذَابَ النَّارِ', 3, 191, 4, 15, 75, 'همانها که خدا را در حال ایستاده و نشسته، و آنگاه که بر پهلو خوابیده‌اند، یاد می‌کنند؛ و در اسرار آفرینش آسمانها و زمین می‌اندیشند؛ (و می‌گویند:) بار الها! اینها را بیهوده نیافریده‌ای! منزهی تو! ما را از عذاب آتش، نگاه دار!'),
(485, 485, 'رَبَّنَا إِنَّكَ مَنْ تُدْخِلِ النَّارَ فَقَدْ أَخْزَيْتَهُ ۖ وَمَا لِلظَّالِمِينَ مِنْ أَنْصَارٍ', 3, 192, 4, 15, 75, 'پروردگارا! هر که را تو (بخاطر اعمالش،) به آتش افکنی، او را خوار و رسوا ساخته‌ای! و برای افراد ستمگر، هیچ یاوری نیست!'),
(486, 486, 'رَبَّنَا إِنَّنَا سَمِعْنَا مُنَادِيًا يُنَادِي لِلْإِيمَانِ أَنْ آمِنُوا بِرَبِّكُمْ فَآمَنَّا ۚ رَبَّنَا فَاغْفِرْ لَنَا ذُنُوبَنَا وَكَفِّرْ عَنَّا سَيِّئَاتِنَا وَتَوَفَّنَا مَعَ الْأَبْرَارِ', 3, 193, 4, 15, 75, 'پروردگارا! ما صدای منادی (تو) را شنیدیم که به ایمان دعوت می‌کرد که: «به پروردگار خود، ایمان بیاورید!» و ما ایمان آوردیم؛ پروردگارا! گناهان ما را ببخش! و بدیهای ما را بپوشان! و ما را با نیکان (و در مسیر آنها) بمیران!'),
(487, 487, 'رَبَّنَا وَآتِنَا مَا وَعَدْتَنَا عَلَىٰ رُسُلِكَ وَلَا تُخْزِنَا يَوْمَ الْقِيَامَةِ ۗ إِنَّكَ لَا تُخْلِفُ الْمِيعَادَ', 3, 194, 4, 15, 75, 'پروردگارا! آنچه را به وسیله پیامبرانت به ما وعده فرمودی، به ما عطا کن! و ما را در روز رستاخیز، رسوا مگردان! زیرا تو هیچ‌گاه از وعده خود، تخلف نمی‌کنی.'),
(488, 488, 'فَاسْتَجَابَ لَهُمْ رَبُّهُمْ أَنِّي لَا أُضِيعُ عَمَلَ عَامِلٍ مِنْكُمْ مِنْ ذَكَرٍ أَوْ أُنْثَىٰ ۖ بَعْضُكُمْ مِنْ بَعْضٍ ۖ فَالَّذِينَ هَاجَرُوا وَأُخْرِجُوا مِنْ دِيَارِهِمْ وَأُوذُوا فِي سَبِيلِي وَقَاتَلُوا وَقُتِلُوا لَأُكَفِّرَنَّ عَنْهُمْ سَيِّئَاتِهِمْ وَلَأُدْخِلَنَّهُمْ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهَارُ ثَوَابًا مِنْ عِنْدِ اللَّهِ ۗ وَاللَّهُ عِنْدَهُ حُسْنُ الثَّوَابِ', 3, 195, 4, 15, 76, 'خداوند، درخواست آنها را پذیرفت؛ (و فرمود:) من عمل هیچ عمل‌کننده‌ای از شما را، زن باشد یا مرد، ضایع نخواهم کرد؛ شما همنوعید، و از جنس یکدیگر! آنها که در راه خدا هجرت کردند، و از خانه‌های خود بیرون رانده شدند و در راه من آزار دیدند، و جنگ کردند و کشته شدند، بیقین گناهانشان را می‌بخشم؛ و آنها را در باغهای بهشتی، که از زیر درختانش نهرها جاری است، وارد می‌کنم. این پاداشی است از طرف خداوند؛ و بهترین پاداشها نزد پروردگار است.'),
(489, 489, 'لَا يَغُرَّنَّكَ تَقَلُّبُ الَّذِينَ كَفَرُوا فِي الْبِلَادِ', 3, 196, 4, 15, 76, 'رفت و آمد (پیروزمندانه) کافران در شهرها، تو را نفریبد!'),
(490, 490, 'مَتَاعٌ قَلِيلٌ ثُمَّ مَأْوَاهُمْ جَهَنَّمُ ۚ وَبِئْسَ الْمِهَادُ', 3, 197, 4, 15, 76, 'این متاع ناچیزی است؛ و سپس جایگاهشان دوزخ، و چه بد جایگاهی است!'),
(491, 491, 'لَٰكِنِ الَّذِينَ اتَّقَوْا رَبَّهُمْ لَهُمْ جَنَّاتٌ تَجْرِي مِنْ تَحْتِهَا الْأَنْهَارُ خَالِدِينَ فِيهَا نُزُلًا مِنْ عِنْدِ اللَّهِ ۗ وَمَا عِنْدَ اللَّهِ خَيْرٌ لِلْأَبْرَارِ', 3, 198, 4, 15, 76, 'ولی کسانی (که ایمان دارند، و) از پروردگارشان می‌پرهیزند، برای آنها باغهایی از بهشت است، که از زیر درختانش نهرها جاری است؛ همیشه در آن خواهند بود. این، نخستین پذیرایی است که از سوی خداوند به آنها می‌رسد؛ و آنچه در نزد خداست، برای نیکان بهتر است!'),
(492, 492, 'وَإِنَّ مِنْ أَهْلِ الْكِتَابِ لَمَنْ يُؤْمِنُ بِاللَّهِ وَمَا أُنْزِلَ إِلَيْكُمْ وَمَا أُنْزِلَ إِلَيْهِمْ خَاشِعِينَ لِلَّهِ لَا يَشْتَرُونَ بِآيَاتِ اللَّهِ ثَمَنًا قَلِيلًا ۗ أُولَٰئِكَ لَهُمْ أَجْرُهُمْ عِنْدَ رَبِّهِمْ ۗ إِنَّ اللَّهَ سَرِيعُ الْحِسَابِ', 3, 199, 4, 15, 76, 'و از اهل کتاب، کسانی هستند که به خدا، و آنچه بر شما نازل شده، و آنچه بر خودشان نازل گردیده، ایمان دارند؛ در برابر (فرمان) خدا خاضعند؛ و آیات خدا را به بهای ناچیزی نمی‌فروشند. پاداش آنها، نزد پروردگارشان است. خداوند، سریع الحساب است. (تمام اعمال نیک آنها را به سرعت حساب می‌کند، و پاداش می‌دهد.)'),
(493, 493, 'يَا أَيُّهَا الَّذِينَ آمَنُوا اصْبِرُوا وَصَابِرُوا وَرَابِطُوا وَاتَّقُوا اللَّهَ لَعَلَّكُمْ تُفْلِحُونَ', 3, 200, 4, 15, 76, 'ای کسانی که ایمان آورده‌اید! (در برابر مشکلات و هوسها،) استقامت کنید! و در برابر دشمنان (نیز)، پایدار باشید و از مرزهای خود، مراقبت کنید و از خدا بپرهیزید، شاید رستگار شوید!'),
(494, 494, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ يَا أَيُّهَا النَّاسُ اتَّقُوا رَبَّكُمُ الَّذِي خَلَقَكُمْ مِنْ نَفْسٍ وَاحِدَةٍ وَخَلَقَ مِنْهَا زَوْجَهَا وَبَثَّ مِنْهُمَا رِجَالًا كَثِيرًا وَنِسَاءً ۚ وَاتَّقُوا اللَّهَ الَّذِي تَسَاءَلُونَ بِهِ وَالْأَرْحَامَ ۚ إِنَّ اللَّهَ كَانَ عَلَيْكُمْ رَقِيبًا', 4, 1, 4, 16, 77, 'ای مردم! از (مخالفت) پروردگارتان بپرهیزید! همان کسی که همه شما را از یک انسان آفرید؛ و همسر او را (نیز) از جنس او خلق کرد؛ و از آن دو، مردان و زنان فراوانی (در روی زمین) منتشر ساخت. و از خدایی بپرهیزید که (همگی به عظمت او معترفید؛ و) هنگامی که چیزی از یکدیگر می‌خواهید، نام او را می‌برید! (و نیز) (از قطع رابطه با) خویشاوندان خود، پرهیز کنید! زیرا خداوند، مراقب شماست.'),
(495, 495, 'وَآتُوا الْيَتَامَىٰ أَمْوَالَهُمْ ۖ وَلَا تَتَبَدَّلُوا الْخَبِيثَ بِالطَّيِّبِ ۖ وَلَا تَأْكُلُوا أَمْوَالَهُمْ إِلَىٰ أَمْوَالِكُمْ ۚ إِنَّهُ كَانَ حُوبًا كَبِيرًا', 4, 2, 4, 16, 77, 'و اموال یتیمان را (هنگامی که به حد رشد رسیدند) به آنها بدهید! و اموال بد (خود) را، با اموال خوب (آنها) عوض نکنید! و اموال آنان را همراه اموال خودتان (با مخلوط کردن یا تبدیل نمودن) نخورید، زیرا این گناه بزرگی است!'),
(496, 496, 'وَإِنْ خِفْتُمْ أَلَّا تُقْسِطُوا فِي الْيَتَامَىٰ فَانْكِحُوا مَا طَابَ لَكُمْ مِنَ النِّسَاءِ مَثْنَىٰ وَثُلَاثَ وَرُبَاعَ ۖ فَإِنْ خِفْتُمْ أَلَّا تَعْدِلُوا فَوَاحِدَةً أَوْ مَا مَلَكَتْ أَيْمَانُكُمْ ۚ ذَٰلِكَ أَدْنَىٰ أَلَّا تَعُولُوا', 4, 3, 4, 16, 77, 'و اگر می‌ترسید که (بهنگام ازدواج با دختران یتیم،) عدالت را رعایت نکنید، (از ازدواج با آنان، چشم‌پوشی کنید و) با زنان پاک (دیگر) ازدواج نمائید، دو یا سه یا چهار همسر و اگر می‌ترسید عدالت را (درباره همسران متعدد) رعایت نکنید، تنها یک همسر بگیرید، و یا از زنانی که مالک آنهائید استفاده کنید، این کار، از ظلم و ستم بهتر جلوگیری می‌کند.'),
(497, 497, 'وَآتُوا النِّسَاءَ صَدُقَاتِهِنَّ نِحْلَةً ۚ فَإِنْ طِبْنَ لَكُمْ عَنْ شَيْءٍ مِنْهُ نَفْسًا فَكُلُوهُ هَنِيئًا مَرِيئًا', 4, 4, 4, 16, 77, 'و مهر زنان را (بطور کامل) بعنوان یک بدهی (یا عطیه،) به آنان بپردازید! (ولی) اگر آنها چیزی از آن را با رضایت خاطر به شما ببخشند، حلال و گوارا مصرف کنید!'),
(498, 498, 'وَلَا تُؤْتُوا السُّفَهَاءَ أَمْوَالَكُمُ الَّتِي جَعَلَ اللَّهُ لَكُمْ قِيَامًا وَارْزُقُوهُمْ فِيهَا وَاكْسُوهُمْ وَقُولُوا لَهُمْ قَوْلًا مَعْرُوفًا', 4, 5, 4, 16, 77, 'اموال خود را، که خداوند وسیله قوام زندگی شما قرار داده، به دست سفیهان نسپارید و از آن، به آنها روزی دهید! و لباس بر آنان بپوشانید و با آنها سخن شایسته بگویید!'),
(499, 499, 'وَابْتَلُوا الْيَتَامَىٰ حَتَّىٰ إِذَا بَلَغُوا النِّكَاحَ فَإِنْ آنَسْتُمْ مِنْهُمْ رُشْدًا فَادْفَعُوا إِلَيْهِمْ أَمْوَالَهُمْ ۖ وَلَا تَأْكُلُوهَا إِسْرَافًا وَبِدَارًا أَنْ يَكْبَرُوا ۚ وَمَنْ كَانَ غَنِيًّا فَلْيَسْتَعْفِفْ ۖ وَمَنْ كَانَ فَقِيرًا فَلْيَأْكُلْ بِالْمَعْرُوفِ ۚ فَإِذَا دَفَعْتُمْ إِلَيْهِمْ أَمْوَالَهُمْ فَأَشْهِدُوا عَلَيْهِمْ ۚ وَكَفَىٰ بِاللَّهِ حَسِيبًا', 4, 6, 4, 16, 77, 'و یتیمان را چون به حد بلوغ برسند، بیازمایید! اگر در آنها رشد (کافی) یافتید، اموالشان را به آنها بدهید! و پیش از آنکه بزرگ شوند، اموالشان را از روی اسراف نخورید! هر کس که بی‌نیاز است، (از برداشت حق الزحمه) خودداری کند؛ و آن کس که نیازمند است، به طور شایسته (و مطابق زحمتی که می‌کشد،) از آن بخورد. و هنگامی که اموالشان را به آنها بازمی‌گردانید، شاهد بگیرید! اگر چه خداوند برای محاسبه کافی است.'),
(500, 500, 'لِلرِّجَالِ نَصِيبٌ مِمَّا تَرَكَ الْوَالِدَانِ وَالْأَقْرَبُونَ وَلِلنِّسَاءِ نَصِيبٌ مِمَّا تَرَكَ الْوَالِدَانِ وَالْأَقْرَبُونَ مِمَّا قَلَّ مِنْهُ أَوْ كَثُرَ ۚ نَصِيبًا مَفْرُوضًا', 4, 7, 4, 16, 78, 'برای مردان، از آنچه پدر و مادر و خویشاوندان از خود بر جای می‌گذارند، سهمی است؛ و برای زنان نیز، از آنچه پدر و مادر و خویشاوندان می‌گذارند، سهمی؛ خواه آن مال، کم باشد یا زیاد؛ این سهمی است تعیین شده و پرداختنی.'),
(501, 501, 'وَإِذَا حَضَرَ الْقِسْمَةَ أُولُو الْقُرْبَىٰ وَالْيَتَامَىٰ وَالْمَسَاكِينُ فَارْزُقُوهُمْ مِنْهُ وَقُولُوا لَهُمْ قَوْلًا مَعْرُوفًا', 4, 8, 4, 16, 78, 'و اگر بهنگام تقسیم (ارث)، خویشاوندان (و طبقه‌ای که ارث نمی‌برند) و یتیمان و مستمندان، حضور داشته باشند، چیزی از آن اموال را به آنها بدهید! و با آنان به طور شایسته سخن بگویید!'),
(502, 502, 'وَلْيَخْشَ الَّذِينَ لَوْ تَرَكُوا مِنْ خَلْفِهِمْ ذُرِّيَّةً ضِعَافًا خَافُوا عَلَيْهِمْ فَلْيَتَّقُوا اللَّهَ وَلْيَقُولُوا قَوْلًا سَدِيدًا', 4, 9, 4, 16, 78, 'کسانی که اگر فرزندان ناتوانی از خود بیادگار بگذارند از آینده آنان می‌ترسند، باید (از ستم درباره یتیمان مردم) بترسند! از (مخالفت) خدا بپرهیزند، و سخنی استوار بگویند.'),
(503, 503, 'إِنَّ الَّذِينَ يَأْكُلُونَ أَمْوَالَ الْيَتَامَىٰ ظُلْمًا إِنَّمَا يَأْكُلُونَ فِي بُطُونِهِمْ نَارًا ۖ وَسَيَصْلَوْنَ سَعِيرًا', 4, 10, 4, 16, 78, 'کسانی که اموال یتیمان را به ظلم و ستم می‌خورند، (در حقیقت،) تنها آتش می‌خورند؛ و بزودی در شعله‌های آتش (دوزخ) می‌سوزند.'),
(504, 504, 'يُوصِيكُمُ اللَّهُ فِي أَوْلَادِكُمْ ۖ لِلذَّكَرِ مِثْلُ حَظِّ الْأُنْثَيَيْنِ ۚ فَإِنْ كُنَّ نِسَاءً فَوْقَ اثْنَتَيْنِ فَلَهُنَّ ثُلُثَا مَا تَرَكَ ۖ وَإِنْ كَانَتْ وَاحِدَةً فَلَهَا النِّصْفُ ۚ وَلِأَبَوَيْهِ لِكُلِّ وَاحِدٍ مِنْهُمَا السُّدُسُ مِمَّا تَرَكَ إِنْ كَانَ لَهُ وَلَدٌ ۚ فَإِنْ لَمْ يَكُنْ لَهُ وَلَدٌ وَوَرِثَهُ أَبَوَاهُ فَلِأُمِّهِ الثُّلُثُ ۚ فَإِنْ كَانَ لَهُ إِخْوَةٌ فَلِأُمِّهِ السُّدُسُ ۚ مِنْ بَعْدِ وَصِيَّةٍ يُوصِي بِهَا أَوْ دَيْنٍ ۗ آبَاؤُكُمْ وَأَبْنَاؤُكُمْ لَا تَدْرُونَ أَيُّهُمْ أَقْرَبُ لَكُمْ نَفْعًا ۚ فَرِيضَةً مِنَ اللَّهِ ۗ إِنَّ اللَّهَ كَانَ عَلِيمًا حَكِيمًا', 4, 11, 4, 16, 78, 'خداوند در باره فرزندانتان به شما سفارش می‌کند که سهم (میراث) پسر، به اندازه سهم دو دختر باشد؛ و اگر فرزندان شما، (دو دختر و) بیش از دو دختر باشند، دو سوم میراث از آن آنهاست؛ و اگر یکی باشد، نیمی (از میراث،) از آن اوست. و برای هر یک از پدر و مادر او، یک ششم میراث است، اگر (میت) فرزندی داشته باشد؛ و اگر فرزندی نداشته باشد، و (تنها) پدر و مادر از او ارث برند، برای مادر او یک سوم است (و بقیه از آن پدر است)؛ و اگر او برادرانی داشته باشد، مادرش یک ششم می‌برد (و پنج ششم باقیمانده، برای پدر است). (همه اینها،) بعد از انجام وصیتی است که او کرده، و بعد از ادای دین است -شما نمی‌دانید پدران و مادران و فرزندانتان، کدامیک برای شما سودمندترند!- این فریضه الهی است؛ و خداوند، دانا و حکیم است.'),
(505, 505, ' وَلَكُمْ نِصْفُ مَا تَرَكَ أَزْوَاجُكُمْ إِنْ لَمْ يَكُنْ لَهُنَّ وَلَدٌ ۚ فَإِنْ كَانَ لَهُنَّ وَلَدٌ فَلَكُمُ الرُّبُعُ مِمَّا تَرَكْنَ ۚ مِنْ بَعْدِ وَصِيَّةٍ يُوصِينَ بِهَا أَوْ دَيْنٍ ۚ وَلَهُنَّ الرُّبُعُ مِمَّا تَرَكْتُمْ إِنْ لَمْ يَكُنْ لَكُمْ وَلَدٌ ۚ فَإِنْ كَانَ لَكُمْ وَلَدٌ فَلَهُنَّ الثُّمُنُ مِمَّا تَرَكْتُمْ ۚ مِنْ بَعْدِ وَصِيَّةٍ تُوصُونَ بِهَا أَوْ دَيْنٍ ۗ وَإِنْ كَانَ رَجُلٌ يُورَثُ كَلَالَةً أَوِ امْرَأَةٌ وَلَهُ أَخٌ أَوْ أُخْتٌ فَلِكُلِّ وَاحِدٍ مِنْهُمَا السُّدُسُ ۚ فَإِنْ كَانُوا أَكْثَرَ مِنْ ذَٰلِكَ فَهُمْ شُرَكَاءُ فِي الثُّلُثِ ۚ مِنْ بَعْدِ وَصِيَّةٍ يُوصَىٰ بِهَا أَوْ دَيْنٍ غَيْرَ مُضَارٍّ ۚ وَصِيَّةً مِنَ اللَّهِ ۗ وَاللَّهُ عَلِيمٌ حَلِيمٌ', 4, 12, 4, 16, 79, 'و برای شما، نصف میراث زنانتان است، اگر آنها فرزندی نداشته باشند؛ و اگر فرزندی داشته باشند، یک چهارم از آن شماست؛ پس از انجام وصیتی که کرده‌اند، و ادای دین (آنها). و برای زنان شما، یک چهارم میراث شماست، اگر فرزندی نداشته باشید؛ و اگر برای شما فرزندی باشد، یک هشتم از آن آنهاست؛ بعد از انجام وصیتی که کرده‌اید، و ادای دین. و اگر مردی بوده باشد که کلاله = خواهر یا برادر از او ارث می‌برد، یا زنی که برادر یا خواهری دارد، سهم هر کدام، یک ششم است (اگر برادران و خواهران مادری باشند)؛ و اگر بیش از یک نفر باشند، آنها در یک سوم شریکند؛ پس از انجام وصیتی که شده، و ادای دین؛ بشرط آنکه (از طریق وصیت و اقرار به دین،) به آنها ضرر نزند. این سفارش خداست؛ و خدا دانا و بردبار است.'),
(506, 506, 'تِلْكَ حُدُودُ اللَّهِ ۚ وَمَنْ يُطِعِ اللَّهَ وَرَسُولَهُ يُدْخِلْهُ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهَارُ خَالِدِينَ فِيهَا ۚ وَذَٰلِكَ الْفَوْزُ الْعَظِيمُ', 4, 13, 4, 16, 79, 'اینها مرزهای الهی است؛ و هر کس خدا و پیامبرش را اطاعت کند، (و قوانین او را محترم بشمرد،) خداوند وی را در باغهایی از بهشت وارد می‌کند که همواره، آب از زیر درختانش جاری است؛ جاودانه در آن می‌مانند؛ و این، پیروزی بزرگی است!'),
(507, 507, 'وَمَنْ يَعْصِ اللَّهَ وَرَسُولَهُ وَيَتَعَدَّ حُدُودَهُ يُدْخِلْهُ نَارًا خَالِدًا فِيهَا وَلَهُ عَذَابٌ مُهِينٌ', 4, 14, 4, 16, 79, 'و آن کس که نافرمانی خدا و پیامبرش را کند و از مرزهای او تجاوز نماید، او را در آتشی وارد می‌کند که جاودانه در آن خواهد ماند؛ و برای او مجازات خوارکننده‌ای است.'),
(508, 508, 'وَاللَّاتِي يَأْتِينَ الْفَاحِشَةَ مِنْ نِسَائِكُمْ فَاسْتَشْهِدُوا عَلَيْهِنَّ أَرْبَعَةً مِنْكُمْ ۖ فَإِنْ شَهِدُوا فَأَمْسِكُوهُنَّ فِي الْبُيُوتِ حَتَّىٰ يَتَوَفَّاهُنَّ الْمَوْتُ أَوْ يَجْعَلَ اللَّهُ لَهُنَّ سَبِيلًا', 4, 15, 4, 16, 80, 'و کسانی از زنان شما که مرتکب زنا شوند، چهار نفر از مسلمانان را بعنوان شاهد بر آنها بطلبید! اگر گواهی دادند، آنان = زنان‌ را در خانه ها (ی خود) نگاه دارید تا مرگشان فرارسد؛ یا اینکه خداوند، راهی برای آنها قرار دهد.'),
(509, 509, 'وَاللَّذَانِ يَأْتِيَانِهَا مِنْكُمْ فَآذُوهُمَا ۖ فَإِنْ تَابَا وَأَصْلَحَا فَأَعْرِضُوا عَنْهُمَا ۗ إِنَّ اللَّهَ كَانَ تَوَّابًا رَحِيمًا', 4, 16, 4, 16, 80, 'و از میان شما، آن مردان و زنانی که (همسر ندارند، و) مرتکب آن کار (زشت) می‌شوند، آنها را آزار دهید (و حد بر آنان جاری نمایید)! و اگر توبه کنند، و (خود را) اصلاح نمایند، (و به جبران گذشته بپردازند،) از آنها درگذرید! زیرا خداوند، توبه‌پذیر و مهربان است.'),
(510, 510, 'إِنَّمَا التَّوْبَةُ عَلَى اللَّهِ لِلَّذِينَ يَعْمَلُونَ السُّوءَ بِجَهَالَةٍ ثُمَّ يَتُوبُونَ مِنْ قَرِيبٍ فَأُولَٰئِكَ يَتُوبُ اللَّهُ عَلَيْهِمْ ۗ وَكَانَ اللَّهُ عَلِيمًا حَكِيمًا', 4, 17, 4, 16, 80, 'پذیرش توبه از سوی خدا، تنها برای کسانی است که کار بدی را از روی جهالت انجام می‌دهند، سپس زود توبه می‌کنند. خداوند، توبه چنین اشخاصی را می‌پذیرد؛ و خدا دانا و حکیم است.'),
(511, 511, 'وَلَيْسَتِ التَّوْبَةُ لِلَّذِينَ يَعْمَلُونَ السَّيِّئَاتِ حَتَّىٰ إِذَا حَضَرَ أَحَدَهُمُ الْمَوْتُ قَالَ إِنِّي تُبْتُ الْآنَ وَلَا الَّذِينَ يَمُوتُونَ وَهُمْ كُفَّارٌ ۚ أُولَٰئِكَ أَعْتَدْنَا لَهُمْ عَذَابًا أَلِيمًا', 4, 18, 4, 16, 80, 'برای کسانی که کارهای بد را انجام می‌دهند، و هنگامی که مرگ یکی از آنها فرا می‌رسد می‌گوید: «الان توبه کردم!» توبه نیست؛ و نه برای کسانی که در حال کفر از دنیا می‌روند؛ اینها کسانی هستند که عذاب دردناکی برایشان فراهم کرده‌ایم.'),
(512, 512, 'يَا أَيُّهَا الَّذِينَ آمَنُوا لَا يَحِلُّ لَكُمْ أَنْ تَرِثُوا النِّسَاءَ كَرْهًا ۖ وَلَا تَعْضُلُوهُنَّ لِتَذْهَبُوا بِبَعْضِ مَا آتَيْتُمُوهُنَّ إِلَّا أَنْ يَأْتِينَ بِفَاحِشَةٍ مُبَيِّنَةٍ ۚ وَعَاشِرُوهُنَّ بِالْمَعْرُوفِ ۚ فَإِنْ كَرِهْتُمُوهُنَّ فَعَسَىٰ أَنْ تَكْرَهُوا شَيْئًا وَيَجْعَلَ اللَّهُ فِيهِ خَيْرًا كَثِيرًا', 4, 19, 4, 16, 80, 'ای کسانی که ایمان آورده‌اید! برای شما حلال نیست که از زنان، از روی اکراه (و ایجاد ناراحتی برای آنها،) ارث ببرید! و آنان را تحت فشار قرار ندهید که قسمتی از آنچه را به آنها داده‌اید (از مهر)، تملک کنید! مگر اینکه آنها عمل زشت آشکاری انجام دهند. و با آنان، بطور شایسته رفتار کنید! و اگر از آنها، (بجهتی) کراهت داشتید، (فوراً تصمیم به جدایی نگیرید!) چه بسا چیزی خوشایند شما نباشد، و خداوند خیر فراوانی در آن قرار می‌دهد!'),
(513, 513, 'وَإِنْ أَرَدْتُمُ اسْتِبْدَالَ زَوْجٍ مَكَانَ زَوْجٍ وَآتَيْتُمْ إِحْدَاهُنَّ قِنْطَارًا فَلَا تَأْخُذُوا مِنْهُ شَيْئًا ۚ أَتَأْخُذُونَهُ بُهْتَانًا وَإِثْمًا مُبِينًا', 4, 20, 4, 16, 81, 'و اگر تصمیم گرفتید که همسر دیگری به جای همسر خود انتخاب کنید، و مال فراوانی (بعنوان مهر) به او پرداخته‌اید، چیزی از آن را پس نگیرید! آیا برای بازپس گرفتن مهر آنان، به تهمت و گناه آشکار متوسل می‌شوید؟!'),
(514, 514, 'وَكَيْفَ تَأْخُذُونَهُ وَقَدْ أَفْضَىٰ بَعْضُكُمْ إِلَىٰ بَعْضٍ وَأَخَذْنَ مِنْكُمْ مِيثَاقًا غَلِيظًا', 4, 21, 4, 16, 81, 'و چگونه آن را باز پس می‌گیرید، در حالی که شما با یکدیگر تماس و آمیزش کامل داشته‌اید؟ و (از این گذشته،) آنها (هنگام ازدواج،) از شما پیمان محکمی گرفته‌اند!'),
(515, 515, 'وَلَا تَنْكِحُوا مَا نَكَحَ آبَاؤُكُمْ مِنَ النِّسَاءِ إِلَّا مَا قَدْ سَلَفَ ۚ إِنَّهُ كَانَ فَاحِشَةً وَمَقْتًا وَسَاءَ سَبِيلًا', 4, 22, 4, 16, 81, 'با زنانی که پدران شما با آنها ازدواج کرده‌اند، هرگز ازدواج نکنید! مگر آنچه درگذشته (پیش از نزول این حکم) انجام شده است؛ زیرا این کار، عملی زشت و تنفرآور و راه نادرستی است.'),
(516, 516, 'حُرِّمَتْ عَلَيْكُمْ أُمَّهَاتُكُمْ وَبَنَاتُكُمْ وَأَخَوَاتُكُمْ وَعَمَّاتُكُمْ وَخَالَاتُكُمْ وَبَنَاتُ الْأَخِ وَبَنَاتُ الْأُخْتِ وَأُمَّهَاتُكُمُ اللَّاتِي أَرْضَعْنَكُمْ وَأَخَوَاتُكُمْ مِنَ الرَّضَاعَةِ وَأُمَّهَاتُ نِسَائِكُمْ وَرَبَائِبُكُمُ اللَّاتِي فِي حُجُورِكُمْ مِنْ نِسَائِكُمُ اللَّاتِي دَخَلْتُمْ بِهِنَّ فَإِنْ لَمْ تَكُونُوا دَخَلْتُمْ بِهِنَّ فَلَا جُنَاحَ عَلَيْكُمْ وَحَلَائِلُ أَبْنَائِكُمُ الَّذِينَ مِنْ أَصْلَابِكُمْ وَأَنْ تَجْمَعُوا بَيْنَ الْأُخْتَيْنِ إِلَّا مَا قَدْ سَلَفَ ۗ إِنَّ اللَّهَ كَانَ غَفُورًا رَحِيمًا', 4, 23, 4, 16, 81, 'حرام شده است بر شما، مادرانتان، و دختران، و خواهران، و عمه‌ها، و خاله‌ها، و دختران برادر، و دختران خواهر شما، و مادرانی که شما را شیر داده‌اند، و خواهران رضاعی شما، و مادران همسرانتان، و دختران همسرتان که در دامان شما پرورش یافته‌اند از همسرانی که با آنها آمیزش جنسی داشته‌اید -و چنانچه با آنها آمیزش جنسی نداشته‌اید، (دختران آنها) برای شما مانعی ندارد- و (همچنین) همسرهای پسرانتان که از نسل شما هستند (-نه پسرخوانده‌ها-) و (نیز حرام است بر شما) جمع میان دو خواهر کنید؛ مگر آنچه در گذشته واقع شده؛ چرا که خداوند، آمرزنده و مهربان است.'),
(517, 517, ' وَالْمُحْصَنَاتُ مِنَ النِّسَاءِ إِلَّا مَا مَلَكَتْ أَيْمَانُكُمْ ۖ كِتَابَ اللَّهِ عَلَيْكُمْ ۚ وَأُحِلَّ لَكُمْ مَا وَرَاءَ ذَٰلِكُمْ أَنْ تَبْتَغُوا بِأَمْوَالِكُمْ مُحْصِنِينَ غَيْرَ مُسَافِحِينَ ۚ فَمَا اسْتَمْتَعْتُمْ بِهِ مِنْهُنَّ فَآتُوهُنَّ أُجُورَهُنَّ فَرِيضَةً ۚ وَلَا جُنَاحَ عَلَيْكُمْ فِيمَا تَرَاضَيْتُمْ بِهِ مِنْ بَعْدِ الْفَرِيضَةِ ۚ إِنَّ اللَّهَ كَانَ عَلِيمًا حَكِيمًا', 4, 24, 5, 17, 82, 'و زنان شوهردار (بر شما حرام است؛) مگر آنها را که (از راه اسارت) مالک شده‌اید؛ (زیرا اسارت آنها در حکم طلاق است؛) اینها احکامی است که خداوند بر شما مقرّر داشته است. اما زنان دیگر غیر از اینها (که گفته شد)، برای شما حلال است که با اموال خود، آنان را اختیار کنید؛ در حالی که پاکدامن باشید و از زنا، خودداری نمایید. و زنانی را که متعه = ازدواج موقت‌ می‌کنید، واجب است مهر آنها را بپردازید. و گناهی بر شما نیست در آنچه بعد از تعیین مهر، با یکدیگر توافق کرده‌اید. (بعداً می‌توانید با توافق، آن را کم یا زیاد کنید.) خداوند، دانا و حکیم است.'),
(518, 518, 'وَمَنْ لَمْ يَسْتَطِعْ مِنْكُمْ طَوْلًا أَنْ يَنْكِحَ الْمُحْصَنَاتِ الْمُؤْمِنَاتِ فَمِنْ مَا مَلَكَتْ أَيْمَانُكُمْ مِنْ فَتَيَاتِكُمُ الْمُؤْمِنَاتِ ۚ وَاللَّهُ أَعْلَمُ بِإِيمَانِكُمْ ۚ بَعْضُكُمْ مِنْ بَعْضٍ ۚ فَانْكِحُوهُنَّ بِإِذْنِ أَهْلِهِنَّ وَآتُوهُنَّ أُجُورَهُنَّ بِالْمَعْرُوفِ مُحْصَنَاتٍ غَيْرَ مُسَافِحَاتٍ وَلَا مُتَّخِذَاتِ أَخْدَانٍ ۚ فَإِذَا أُحْصِنَّ فَإِنْ أَتَيْنَ بِفَاحِشَةٍ فَعَلَيْهِنَّ نِصْفُ مَا عَلَى الْمُحْصَنَاتِ مِنَ الْعَذَابِ ۚ ذَٰلِكَ لِمَنْ خَشِيَ الْعَنَتَ مِنْكُمْ ۚ وَأَنْ تَصْبِرُوا خَيْرٌ لَكُمْ ۗ وَاللَّهُ غَفُورٌ رَحِيمٌ', 4, 25, 5, 17, 82, 'و آنها که توانایی ازدواج با زنان (آزاد) پاکدامن باایمان را ندارند، می‌توانند با زنان پاکدامن از بردگان باایمانی که در اختیار دارید ازدواج کنند -خدا به ایمان شما آگاه‌تر است؛ و همگی اعضای یک پیکرید- آنها را با اجازه صاحبان آنان تزویج نمایید، و مهرشان را به خودشان بدهید؛ به شرط آنکه پاکدامن باشند، نه بطور آشکار مرتکب زنا شوند، و نه دوست پنهانی بگیرند. و در صورتی که «محصنه» باشند و مرتکب عمل منافی عفت شوند، نصف مجازات زنان آزاد را خواهند داشت. این (اجازه ازدواج با کنیزان) برای کسانی از شماست که بترسند (از نظر غریزه جنسی) به زحمت بیفتند؛ و (با این حال نیز) خودداری (از ازدواج با آنان) برای شما بهتر است. و خداوند، آمرزنده و مهربان است.'),
(519, 519, 'يُرِيدُ اللَّهُ لِيُبَيِّنَ لَكُمْ وَيَهْدِيَكُمْ سُنَنَ الَّذِينَ مِنْ قَبْلِكُمْ وَيَتُوبَ عَلَيْكُمْ ۗ وَاللَّهُ عَلِيمٌ حَكِيمٌ', 4, 26, 5, 17, 82, 'خداوند می‌خواهد (با این دستورها، راه‌های خوشبختی و سعادت را) برای شما آشکار سازد، و به سنتهای (صحیح) پیشینیان رهبری کند. و خداوند دانا و حکیم است.'),
(520, 520, 'وَاللَّهُ يُرِيدُ أَنْ يَتُوبَ عَلَيْكُمْ وَيُرِيدُ الَّذِينَ يَتَّبِعُونَ الشَّهَوَاتِ أَنْ تَمِيلُوا مَيْلًا عَظِيمًا', 4, 27, 5, 17, 83, 'خدا می‌خواهد شما را ببخشد (و از آلودگی پاک نماید)، امّا آنها که پیرو شهواتند، می‌خواهند شما بکلی منحرف شوید.'),
(521, 521, 'يُرِيدُ اللَّهُ أَنْ يُخَفِّفَ عَنْكُمْ ۚ وَخُلِقَ الْإِنْسَانُ ضَعِيفًا', 4, 28, 5, 17, 83, 'خدا میخواهد (با احکام مربوط به ازدواج با کنیزان و مانند آن،) کار را بر شما سبک کند؛ و انسان، ضعیف آفریده شده؛ (و در برابر طوفان غرایز، مقاومت او کم است)'),
(522, 522, 'يَا أَيُّهَا الَّذِينَ آمَنُوا لَا تَأْكُلُوا أَمْوَالَكُمْ بَيْنَكُمْ بِالْبَاطِلِ إِلَّا أَنْ تَكُونَ تِجَارَةً عَنْ تَرَاضٍ مِنْكُمْ ۚ وَلَا تَقْتُلُوا أَنْفُسَكُمْ ۚ إِنَّ اللَّهَ كَانَ بِكُمْ رَحِيمًا', 4, 29, 5, 17, 83, 'ای کسانی که ایمان آورده‌اید! اموال یکدیگر را به باطل (و از طرق نامشروع) نخورید مگر اینکه تجارتی با رضایت شما انجام گیرد. و خودکشی نکنید! خداوند نسبت به شما مهربان است.'),
(523, 523, 'وَمَنْ يَفْعَلْ ذَٰلِكَ عُدْوَانًا وَظُلْمًا فَسَوْفَ نُصْلِيهِ نَارًا ۚ وَكَانَ ذَٰلِكَ عَلَى اللَّهِ يَسِيرًا', 4, 30, 5, 17, 83, 'و هر کس این عمل را از روی تجاوز و ستم انجام دهد، بزودی او را در آتشی وارد خواهیم ساخت؛ و این کار برای خدا آسان است.'),
(524, 524, 'إِنْ تَجْتَنِبُوا كَبَائِرَ مَا تُنْهَوْنَ عَنْهُ نُكَفِّرْ عَنْكُمْ سَيِّئَاتِكُمْ وَنُدْخِلْكُمْ مُدْخَلًا كَرِيمًا', 4, 31, 5, 17, 83, 'اگر از گناهان بزرگی که از آن نهی می‌شوید پرهیز کنید، گناهان کوچک شما را می‌پوشانیم؛ و شما را در جایگاه خوبی وارد می‌سازیم.'),
(525, 525, 'وَلَا تَتَمَنَّوْا مَا فَضَّلَ اللَّهُ بِهِ بَعْضَكُمْ عَلَىٰ بَعْضٍ ۚ لِلرِّجَالِ نَصِيبٌ مِمَّا اكْتَسَبُوا ۖ وَلِلنِّسَاءِ نَصِيبٌ مِمَّا اكْتَسَبْنَ ۚ وَاسْأَلُوا اللَّهَ مِنْ فَضْلِهِ ۗ إِنَّ اللَّهَ كَانَ بِكُلِّ شَيْءٍ عَلِيمًا', 4, 32, 5, 17, 83, 'برتریهایی را که خداوند برای بعضی از شما بر بعضی دیگر قرار داده آرزو نکنید! (این تفاوتهای طبیعی و حقوقی، برای حفظ نظام زندگی شما، و بر طبق عدالت است. ولی با این حال،) مردان نصیبی از آنچه به دست می‌آورند دارند، و زنان نیز نصیبی؛ (و نباید حقوق هیچ‌یک پایمال گردد). و از فضل (و رحمت و برکت) خدا، برای رفع تنگناها طلب کنید! و خداوند به هر چیز داناست.'),
(526, 526, 'وَلِكُلٍّ جَعَلْنَا مَوَالِيَ مِمَّا تَرَكَ الْوَالِدَانِ وَالْأَقْرَبُونَ ۚ وَالَّذِينَ عَقَدَتْ أَيْمَانُكُمْ فَآتُوهُمْ نَصِيبَهُمْ ۚ إِنَّ اللَّهَ كَانَ عَلَىٰ كُلِّ شَيْءٍ شَهِيدًا', 4, 33, 5, 17, 83, 'برای هر کسی، وارثانی قرار دادیم، که از میراث پدر و مادر و نزدیکان ارث ببرند؛ و (نیز) کسانی که با آنها پیمان بسته‌اید، نصیبشان را بپردازید! خداوند بر هر چیز، شاهد و ناظر است.'),
(527, 527, 'الرِّجَالُ قَوَّامُونَ عَلَى النِّسَاءِ بِمَا فَضَّلَ اللَّهُ بَعْضَهُمْ عَلَىٰ بَعْضٍ وَبِمَا أَنْفَقُوا مِنْ أَمْوَالِهِمْ ۚ فَالصَّالِحَاتُ قَانِتَاتٌ حَافِظَاتٌ لِلْغَيْبِ بِمَا حَفِظَ اللَّهُ ۚ وَاللَّاتِي تَخَافُونَ نُشُوزَهُنَّ فَعِظُوهُنَّ وَاهْجُرُوهُنَّ فِي الْمَضَاجِعِ وَاضْرِبُوهُنَّ ۖ فَإِنْ أَطَعْنَكُمْ فَلَا تَبْغُوا عَلَيْهِنَّ سَبِيلًا ۗ إِنَّ اللَّهَ كَانَ عَلِيًّا كَبِيرًا', 4, 34, 5, 17, 84, 'مردان، سرپرست و نگهبان زنانند، بخاطر برتریهایی که خداوند (از نظر نظام اجتماع) برای بعضی نسبت به بعضی دیگر قرار داده است، و بخاطر انفاقهایی که از اموالشان (در مورد زنان) می‌کنند. و زنان صالح، زنانی هستند که متواضعند، و در غیاب (همسر خود،) اسرار و حقوق او را، در مقابل حقوقی که خدا برای آنان قرار داده، حفظ می‌کنند. و (امّا) آن دسته از زنان را که از سرکشی و مخالفتشان بیم دارید، پند و اندرز دهید! (و اگر مؤثر واقع نشد،) در بستر از آنها دوری نمایید! و (اگر هیچ راهی جز شدت عمل، برای وادار کردن آنها به انجام وظایفشان نبود،) آنها را تنبیه کنید! و اگر از شما پیروی کردند، راهی برای تعدّی بر آنها نجویید! (بدانید) خداوند، بلندمرتبه و بزرگ است. (و قدرت او، بالاترین قدرتهاست.)'),
(528, 528, 'وَإِنْ خِفْتُمْ شِقَاقَ بَيْنِهِمَا فَابْعَثُوا حَكَمًا مِنْ أَهْلِهِ وَحَكَمًا مِنْ أَهْلِهَا إِنْ يُرِيدَا إِصْلَاحًا يُوَفِّقِ اللَّهُ بَيْنَهُمَا ۗ إِنَّ اللَّهَ كَانَ عَلِيمًا خَبِيرًا', 4, 35, 5, 17, 84, 'و اگر از جدایی و شکاف میان آن دو (همسر) بیم داشته باشید، یک داور از خانواده شوهر، و یک داور از خانواده زن انتخاب کنید (تا به کار آنان رسیدگی کنند). اگر این دو داور، تصمیم به اصلاح داشته باشند، خداوند به توافق آنها کمک می‌کند؛ زیرا خداوند، دانا و آگاه است (و از نیات همه، با خبر است).'),
(529, 529, ' وَاعْبُدُوا اللَّهَ وَلَا تُشْرِكُوا بِهِ شَيْئًا ۖ وَبِالْوَالِدَيْنِ إِحْسَانًا وَبِذِي الْقُرْبَىٰ وَالْيَتَامَىٰ وَالْمَسَاكِينِ وَالْجَارِ ذِي الْقُرْبَىٰ وَالْجَارِ الْجُنُبِ وَالصَّاحِبِ بِالْجَنْبِ وَابْنِ السَّبِيلِ وَمَا مَلَكَتْ أَيْمَانُكُمْ ۗ إِنَّ اللَّهَ لَا يُحِبُّ مَنْ كَانَ مُخْتَالًا فَخُورًا', 4, 36, 5, 17, 84, 'و خدا را بپرستید! و هیچ‌چیز را همتای او قرار ندهید! و به پدر و مادر، نیکی کنید؛ همچنین به خویشاوندان و یتیمان و مسکینان، و همسایه نزدیک، و همسایه دور، و دوست و همنشین، و واماندگان در سفر، و بردگانی که مالک آنها هستید؛ زیرا خداوند، کسی را که متکبر و فخر فروش است، (و از ادای حقوق دیگران سرباز می‌زند،) دوست نمی‌دارد.'),
(530, 530, 'الَّذِينَ يَبْخَلُونَ وَيَأْمُرُونَ النَّاسَ بِالْبُخْلِ وَيَكْتُمُونَ مَا آتَاهُمُ اللَّهُ مِنْ فَضْلِهِ ۗ وَأَعْتَدْنَا لِلْكَافِرِينَ عَذَابًا مُهِينًا', 4, 37, 5, 17, 84, 'آنها کسانی هستند که بخل می‌ورزند، و مردم را به بخل دعوت می‌کنند، و آنچه را که خداوند از فضل (و رحمت) خود به آنها داده، کتمان می‌نمایند. (این عمل، در حقیقت از کفرشان سرچشمه گرفته؛) و ما برای کافران، عذاب خوارکننده‌ای آماده کرده‌ایم.'),
(531, 531, 'وَالَّذِينَ يُنْفِقُونَ أَمْوَالَهُمْ رِئَاءَ النَّاسِ وَلَا يُؤْمِنُونَ بِاللَّهِ وَلَا بِالْيَوْمِ الْآخِرِ ۗ وَمَنْ يَكُنِ الشَّيْطَانُ لَهُ قَرِينًا فَسَاءَ قَرِينًا', 4, 38, 5, 17, 85, 'و آنها کسانی هستند که اموال خود را برای نشان‌دادن به مردم انفاق می‌کنند، و ایمان به خدا و روز بازپسین ندارند؛ (چرا که شیطان، رفیق و همنشین آنهاست؛) و کسی که شیطان قرین او باشد، بد همنشین و قرینی است.'),
(532, 532, 'وَمَاذَا عَلَيْهِمْ لَوْ آمَنُوا بِاللَّهِ وَالْيَوْمِ الْآخِرِ وَأَنْفَقُوا مِمَّا رَزَقَهُمُ اللَّهُ ۚ وَكَانَ اللَّهُ بِهِمْ عَلِيمًا', 4, 39, 5, 17, 85, 'چه می‌شد اگر آنها به خدا و روز بازپسین ایمان می‌آوردند، و از آنچه خدا به آنان روزی داده، (در راه او) انفاق می‌کردند؟! و خداوند از (اعمال و نیات) آنها آگاه است.'),
(533, 533, 'إِنَّ اللَّهَ لَا يَظْلِمُ مِثْقَالَ ذَرَّةٍ ۖ وَإِنْ تَكُ حَسَنَةً يُضَاعِفْهَا وَيُؤْتِ مِنْ لَدُنْهُ أَجْرًا عَظِيمًا', 4, 40, 5, 17, 85, 'خداوند (حتّی) به اندازه سنگینی ذره‌ای ستم نمی‌کند؛ و اگر کار نیکی باشد، آن را دو چندان می‌سازد؛ و از نزد خود، پاداش عظیمی (در برابر آن) می‌دهد.'),
(534, 534, 'فَكَيْفَ إِذَا جِئْنَا مِنْ كُلِّ أُمَّةٍ بِشَهِيدٍ وَجِئْنَا بِكَ عَلَىٰ هَٰؤُلَاءِ شَهِيدًا', 4, 41, 5, 17, 85, 'حال آنها چگونه است آن روزی که از هر امتی، شاهد و گواهی (بر اعمالشان) می‌آوریم، و تو را نیز بر آنان گواه خواهیم آورد؟'),
(535, 535, 'يَوْمَئِذٍ يَوَدُّ الَّذِينَ كَفَرُوا وَعَصَوُا الرَّسُولَ لَوْ تُسَوَّىٰ بِهِمُ الْأَرْضُ وَلَا يَكْتُمُونَ اللَّهَ حَدِيثًا', 4, 42, 5, 17, 85, 'در آن روز، آنها که کافر شدند و با پیامبر (ص) بمخالفت برخاستند، آرزو می‌کنند که ای کاش (خاک بودند، و) خاک آنها با زمینهای اطراف یکسان می‌شد (و بکلی محو و فراموش می‌شدند). در آن روز، (با آن همه گواهان،) سخنی را نمی‌توانند از خدا پنهان کنند.'),
(536, 536, 'يَا أَيُّهَا الَّذِينَ آمَنُوا لَا تَقْرَبُوا الصَّلَاةَ وَأَنْتُمْ سُكَارَىٰ حَتَّىٰ تَعْلَمُوا مَا تَقُولُونَ وَلَا جُنُبًا إِلَّا عَابِرِي سَبِيلٍ حَتَّىٰ تَغْتَسِلُوا ۚ وَإِنْ كُنْتُمْ مَرْضَىٰ أَوْ عَلَىٰ سَفَرٍ أَوْ جَاءَ أَحَدٌ مِنْكُمْ مِنَ الْغَائِطِ أَوْ لَامَسْتُمُ النِّسَاءَ فَلَمْ تَجِدُوا مَاءً فَتَيَمَّمُوا صَعِيدًا طَيِّبًا فَامْسَحُوا بِوُجُوهِكُمْ وَأَيْدِيكُمْ ۗ إِنَّ اللَّهَ كَانَ عَفُوًّا غَفُورًا', 4, 43, 5, 17, 85, 'ای کسانی که ایمان آورده‌اید! در حال مستی به نماز نزدیک نشوید، تا بدانید چه می‌گویید! و همچنین هنگامی که جنب هستید -مگر اینکه مسافر باشید- تا غسل کنید. و اگر بیمارید، یا مسافر، و یا «قضای حاجت» کرده‌اید، و یا با زنان آمیزش جنسی داشته‌اید، و در این حال، آب (برای وضو یا غسل) نیافتید، با خاک پاکی تیمّم کنید! (به این طریق که) صورتها و دستهایتان را با آن مسح نمایید. خداوند، بخشنده و آمرزنده است.'),
(537, 537, 'أَلَمْ تَرَ إِلَى الَّذِينَ أُوتُوا نَصِيبًا مِنَ الْكِتَابِ يَشْتَرُونَ الضَّلَالَةَ وَيُرِيدُونَ أَنْ تَضِلُّوا السَّبِيلَ', 4, 44, 5, 17, 85, 'آیا ندیدی کسانی را که بهره‌ای از کتاب (خدا) به آنها داده شده بود، (به جای اینکه از آن، برای هدایت خود و دیگران استفاده کنند، برای خویش) گمراهی می‌خرند، و می‌خواهند شما نیز گمراه شوید؟'),
(538, 538, 'وَاللَّهُ أَعْلَمُ بِأَعْدَائِكُمْ ۚ وَكَفَىٰ بِاللَّهِ وَلِيًّا وَكَفَىٰ بِاللَّهِ نَصِيرًا', 4, 45, 5, 17, 86, 'خدا به دشمنان شما آگاهتر است؛ (ولی آنها زیانی به شما نمی‌رسانند.) و کافی است که خدا ولیِّ شما باشد؛ و کافی است که خدا یاور شما باشد.'),
(539, 539, 'مِنَ الَّذِينَ هَادُوا يُحَرِّفُونَ الْكَلِمَ عَنْ مَوَاضِعِهِ وَيَقُولُونَ سَمِعْنَا وَعَصَيْنَا وَاسْمَعْ غَيْرَ مُسْمَعٍ وَرَاعِنَا لَيًّا بِأَلْسِنَتِهِمْ وَطَعْنًا فِي الدِّينِ ۚ وَلَوْ أَنَّهُمْ قَالُوا سَمِعْنَا وَأَطَعْنَا وَاسْمَعْ وَانْظُرْنَا لَكَانَ خَيْرًا لَهُمْ وَأَقْوَمَ وَلَٰكِنْ لَعَنَهُمُ اللَّهُ بِكُفْرِهِمْ فَلَا يُؤْمِنُونَ إِلَّا قَلِيلًا', 4, 46, 5, 17, 86, 'بعضی از یهود، سخنان را از جای خود، تحریف می‌کنند؛ و (به جای اینکه بگویند: «شنیدیم و اطاعت کردیم»)، می‌گویند: «شنیدیم و مخالفت کردیم! و (نیز می‌گویند:) بشنو! که هرگز نشنوی! و (از روی تمسخر می‌گویند:) راعنا = ما را تحمیق کن!» تا با زبان خود، حقایق را بگردانند و در آیین خدا، طعنه زنند. ولی اگر آنها (به جای این همه لجاجت) می‌گفتند: «شنیدیم و اطاعت کردیم؛ و سخنان ما را بشنو و به ما مهلت ده (تا حقایق را درک کنیم)»، برای آنان بهتر، و با واقعیت سازگارتر بود. ولی خداوند، آنها را بخاطر کفرشان، از رحمت خود دور ساخته است؛ از این رو جز عدّه کمی ایمان نمی‌آورند.'),
(540, 540, 'يَا أَيُّهَا الَّذِينَ أُوتُوا الْكِتَابَ آمِنُوا بِمَا نَزَّلْنَا مُصَدِّقًا لِمَا مَعَكُمْ مِنْ قَبْلِ أَنْ نَطْمِسَ وُجُوهًا فَنَرُدَّهَا عَلَىٰ أَدْبَارِهَا أَوْ نَلْعَنَهُمْ كَمَا لَعَنَّا أَصْحَابَ السَّبْتِ ۚ وَكَانَ أَمْرُ اللَّهِ مَفْعُولًا', 4, 47, 5, 17, 86, 'ای کسانی که کتاب (خدا) به شما داده شده! به آنچه (بر پیامبر خود) نازل کردیم -و هماهنگ با نشانه‌هایی است که با شماست- ایمان بیاورید، پیش از آنکه صورتهایی را محو کنیم، سپس به پشت سر بازگردانیم، یا آنها را از رحمت خود دور سازیم، همان گونه که «اصحاب سبت» = گروهی از تبهکاران بنی اسرائیل‌ را دور ساختیم؛ و فرمان خدا، در هر حال انجام شدنی است!'),
(541, 541, 'إِنَّ اللَّهَ لَا يَغْفِرُ أَنْ يُشْرَكَ بِهِ وَيَغْفِرُ مَا دُونَ ذَٰلِكَ لِمَنْ يَشَاءُ ۚ وَمَنْ يُشْرِكْ بِاللَّهِ فَقَدِ افْتَرَىٰ إِثْمًا عَظِيمًا', 4, 48, 5, 17, 86, 'خداوند (هرگز) شرک را نمی‌بخشد! و پایین‌تر از آن را برای هر کس (بخواهد و شایسته بداند) می‌بخشد. و آن کسی که برای خدا، شریکی قرار دهد، گناه بزرگی مرتکب شده است.'),
(542, 542, 'أَلَمْ تَرَ إِلَى الَّذِينَ يُزَكُّونَ أَنْفُسَهُمْ ۚ بَلِ اللَّهُ يُزَكِّي مَنْ يَشَاءُ وَلَا يُظْلَمُونَ فَتِيلًا', 4, 49, 5, 17, 86, 'آیا ندیدی کسانی را که خودستایی می‌کنند؟! (این خود ستاییها، بی‌ارزش است؛) بلکه خدا هر کس را بخواهد، ستایش می‌کند؛ و کمترین ستمی به آنها نخواهد شد.'),
(543, 543, 'انْظُرْ كَيْفَ يَفْتَرُونَ عَلَى اللَّهِ الْكَذِبَ ۖ وَكَفَىٰ بِهِ إِثْمًا مُبِينًا', 4, 50, 5, 17, 86, 'ببین چگونه بر خدا دروغ می‌بندند! و همین گناه آشکار، (برای مجازات آنان) کافی است.'),
(544, 544, 'أَلَمْ تَرَ إِلَى الَّذِينَ أُوتُوا نَصِيبًا مِنَ الْكِتَابِ يُؤْمِنُونَ بِالْجِبْتِ وَالطَّاغُوتِ وَيَقُولُونَ لِلَّذِينَ كَفَرُوا هَٰؤُلَاءِ أَهْدَىٰ مِنَ الَّذِينَ آمَنُوا سَبِيلًا', 4, 51, 5, 17, 86, 'آیا ندیدی کسانی را که بهره‌ای از کتاب (خدا) به آنان داده شده، (با این حال)، به «جبت» و «طاغوت» = بت و بت‌پرستان‌ ایمان می‌آورند، و درباره کافران می‌گویند: «آنها، از کسانی که ایمان آورده‌اند، هدایت یافته‌ترند»؟!'),
(545, 545, 'أُولَٰئِكَ الَّذِينَ لَعَنَهُمُ اللَّهُ ۖ وَمَنْ يَلْعَنِ اللَّهُ فَلَنْ تَجِدَ لَهُ نَصِيرًا', 4, 52, 5, 17, 87, 'آنها کسانی هستند که خداوند، ایشان را از رحمت خود، دور ساخته است؛ و هر کس را خدا از رحمتش دور سازد، یاوری برای او نخواهی یافت.'),
(546, 546, 'أَمْ لَهُمْ نَصِيبٌ مِنَ الْمُلْكِ فَإِذًا لَا يُؤْتُونَ النَّاسَ نَقِيرًا', 4, 53, 5, 17, 87, 'آیا آنها = یهود سهمی در حکومت دارند (که بخواهند چنین داوری کنند)؟ در حالی که اگر چنین بود، (همه چیز را در انحصار خود می‌گرفتند،) و کمترین حق را به مردم نمی‌دادند.'),
(547, 547, 'أَمْ يَحْسُدُونَ النَّاسَ عَلَىٰ مَا آتَاهُمُ اللَّهُ مِنْ فَضْلِهِ ۖ فَقَدْ آتَيْنَا آلَ إِبْرَاهِيمَ الْكِتَابَ وَالْحِكْمَةَ وَآتَيْنَاهُمْ مُلْكًا عَظِيمًا', 4, 54, 5, 17, 87, 'یا اینکه نسبت به مردم = پیامبر و خاندانش‌، و بر آنچه خدا از فضلش به آنان بخشیده، حسد می‌ورزند؟ ما به آل ابراهیم، (که یهود از خاندان او هستند نیز،) کتاب و حکمت دادیم؛ و حکومت عظیمی در اختیار آنها = پیامبران بنی اسرائیل‌ قرار دادیم.'),
(548, 548, 'فَمِنْهُمْ مَنْ آمَنَ بِهِ وَمِنْهُمْ مَنْ صَدَّ عَنْهُ ۚ وَكَفَىٰ بِجَهَنَّمَ سَعِيرًا', 4, 55, 5, 17, 87, 'ولی جمعی از آنها به آن ایمان آوردند؛ و جمعی راه (مردم را) بر آن بستند. و شعله فروزانِ آتش دوزخ، برای آنها کافی است!'),
(549, 549, 'إِنَّ الَّذِينَ كَفَرُوا بِآيَاتِنَا سَوْفَ نُصْلِيهِمْ نَارًا كُلَّمَا نَضِجَتْ جُلُودُهُمْ بَدَّلْنَاهُمْ جُلُودًا غَيْرَهَا لِيَذُوقُوا الْعَذَابَ ۗ إِنَّ اللَّهَ كَانَ عَزِيزًا حَكِيمًا', 4, 56, 5, 17, 87, 'کسانی که به آیات ما کافر شدند، بزودی آنها را در آتشی وارد می‌کنیم که هرگاه پوستهای تنشان (در آن) بریان گردد (و بسوزد)، پوستهای دیگری به جای آن قرار می‌دهیم، تا کیفر (الهی) را بچشند. خداوند، توانا و حکیم است (و روی حساب، کیفر می‌دهد).'),
(550, 550, 'وَالَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ سَنُدْخِلُهُمْ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهَارُ خَالِدِينَ فِيهَا أَبَدًا ۖ لَهُمْ فِيهَا أَزْوَاجٌ مُطَهَّرَةٌ ۖ وَنُدْخِلُهُمْ ظِلًّا ظَلِيلًا', 4, 57, 5, 17, 87, 'و کسانی که ایمان آوردند و کارهای شایسته انجام دادند، بزودی آنها را در باغهایی از بهشت وارد می‌کنیم که نهرها از زیر درختانش جاری است؛ همیشه در آن خواهند ماند؛ و همسرانی پاکیزه برای آنها خواهد بود؛ و آنان را در سایه‌های گسترده (و فرح بخش) جای میدهیم.'),
(551, 551, ' إِنَّ اللَّهَ يَأْمُرُكُمْ أَنْ تُؤَدُّوا الْأَمَانَاتِ إِلَىٰ أَهْلِهَا وَإِذَا حَكَمْتُمْ بَيْنَ النَّاسِ أَنْ تَحْكُمُوا بِالْعَدْلِ ۚ إِنَّ اللَّهَ نِعِمَّا يَعِظُكُمْ بِهِ ۗ إِنَّ اللَّهَ كَانَ سَمِيعًا بَصِيرًا', 4, 58, 5, 18, 87, 'خداوند به شما فرمان می‌دهد که امانتها را به صاحبانش بدهید! و هنگامی که میان مردم داوری می‌کنید، به عدالت داوری کنید! خداوند، اندرزهای خوبی به شما می‌دهد! خداوند، شنوا و بیناست.'),
(552, 552, 'يَا أَيُّهَا الَّذِينَ آمَنُوا أَطِيعُوا اللَّهَ وَأَطِيعُوا الرَّسُولَ وَأُولِي الْأَمْرِ مِنْكُمْ ۖ فَإِنْ تَنَازَعْتُمْ فِي شَيْءٍ فَرُدُّوهُ إِلَى اللَّهِ وَالرَّسُولِ إِنْ كُنْتُمْ تُؤْمِنُونَ بِاللَّهِ وَالْيَوْمِ الْآخِرِ ۚ ذَٰلِكَ خَيْرٌ وَأَحْسَنُ تَأْوِيلًا', 4, 59, 5, 18, 87, 'ای کسانی که ایمان آورده‌اید! اطاعت کنید خدا را! و اطاعت کنید پیامبر خدا و اولو الأمر = اوصیای پیامبر را! و هرگاه در چیزی نزاع داشتید، آن را به خدا و پیامبر بازگردانید (و از آنها داوری بطلبید) اگر به خدا و روز رستاخیز ایمان دارید! این (کار) برای شما بهتر، و عاقبت و پایانش نیکوتر است.'),
(553, 553, 'أَلَمْ تَرَ إِلَى الَّذِينَ يَزْعُمُونَ أَنَّهُمْ آمَنُوا بِمَا أُنْزِلَ إِلَيْكَ وَمَا أُنْزِلَ مِنْ قَبْلِكَ يُرِيدُونَ أَنْ يَتَحَاكَمُوا إِلَى الطَّاغُوتِ وَقَدْ أُمِرُوا أَنْ يَكْفُرُوا بِهِ وَيُرِيدُ الشَّيْطَانُ أَنْ يُضِلَّهُمْ ضَلَالًا بَعِيدًا', 4, 60, 5, 18, 88, 'آیا ندیدی کسانی را که گمان می‌کنند به آنچه (از کتابهای آسمانی که) بر تو و بر پیشینیان نازل شده، ایمان آورده‌اند، ولی می‌خواهند برای داوری نزد طاغوت و حکّام باطل بروند؟! با اینکه به آنها دستور داده شده که به طاغوت کافر شوند. امّا شیطان می‌خواهد آنان را گمراه کند، و به بیراهه‌های دور دستی بیفکند.'),
(554, 554, 'وَإِذَا قِيلَ لَهُمْ تَعَالَوْا إِلَىٰ مَا أَنْزَلَ اللَّهُ وَإِلَى الرَّسُولِ رَأَيْتَ الْمُنَافِقِينَ يَصُدُّونَ عَنْكَ صُدُودًا', 4, 61, 5, 18, 88, 'و هنگامی که به آنها گفته شود: «به سوی آنچه خداوند نازل کرده، و به سوی پیامبر بیایید»، منافقان را می‌بینی که (از قبول دعوت) ِ تو، اعراض می‌کنند!'),
(555, 555, 'فَكَيْفَ إِذَا أَصَابَتْهُمْ مُصِيبَةٌ بِمَا قَدَّمَتْ أَيْدِيهِمْ ثُمَّ جَاءُوكَ يَحْلِفُونَ بِاللَّهِ إِنْ أَرَدْنَا إِلَّا إِحْسَانًا وَتَوْفِيقًا', 4, 62, 5, 18, 88, 'پس چگونه وقتی به خاطر اعمالشان، گرفتار مصیبتی می‌شوند، سپس به سراغ تو می‌آیند، سوگند یاد می‌کنند که منظورِ (ما از بردنِ داوری نزد دیگران)، جز نیکی کردن و توافق (میان طرفین نزاع،) نبوده است؟!'),
(556, 556, 'أُولَٰئِكَ الَّذِينَ يَعْلَمُ اللَّهُ مَا فِي قُلُوبِهِمْ فَأَعْرِضْ عَنْهُمْ وَعِظْهُمْ وَقُلْ لَهُمْ فِي أَنْفُسِهِمْ قَوْلًا بَلِيغًا', 4, 63, 5, 18, 88, 'آنها کسانی هستند که خدا، آنچه را در دل دارند، می‌داند. از (مجازات) ِ آنان صرف نظر کن! و آنها را اندرز ده! و با بیانی رسا، نتایج اعمالشان را به آنها گوشزد نما!'),
(557, 557, 'وَمَا أَرْسَلْنَا مِنْ رَسُولٍ إِلَّا لِيُطَاعَ بِإِذْنِ اللَّهِ ۚ وَلَوْ أَنَّهُمْ إِذْ ظَلَمُوا أَنْفُسَهُمْ جَاءُوكَ فَاسْتَغْفَرُوا اللَّهَ وَاسْتَغْفَرَ لَهُمُ الرَّسُولُ لَوَجَدُوا اللَّهَ تَوَّابًا رَحِيمًا', 4, 64, 5, 18, 88, 'ما هیچ پیامبری را نفرستادیم مگر برای این که به فرمان خدا، از وی اطاعت شود. و اگر این مخالفان، هنگامی که به خود ستم می‌کردند (و فرمانهای خدا را زیر پا می‌گذاردند)، به نزد تو می‌آمدند؛ و از خدا طلب آمرزش می‌کردند؛ و پیامبر هم برای آنها استغفار می‌کرد؛ خدا را توبه پذیر و مهربان می‌یافتند.'),
(558, 558, 'فَلَا وَرَبِّكَ لَا يُؤْمِنُونَ حَتَّىٰ يُحَكِّمُوكَ فِيمَا شَجَرَ بَيْنَهُمْ ثُمَّ لَا يَجِدُوا فِي أَنْفُسِهِمْ حَرَجًا مِمَّا قَضَيْتَ وَيُسَلِّمُوا تَسْلِيمًا', 4, 65, 5, 18, 88, 'به پروردگارت سوگند که آنها مؤمن نخواهند بود، مگر اینکه در اختلافات خود، تو را به داوری طلبند؛ و سپس از داوری تو، در دل خود احساس ناراحتی نکنند؛ و کاملا تسلیم باشند.');
INSERT INTO `Quran` (`rowid`, `id`, `Arabic`, `Chapter`, `Verse`, `Juz`, `Hizb`, `Page`, `tr`) VALUES
(559, 559, 'وَلَوْ أَنَّا كَتَبْنَا عَلَيْهِمْ أَنِ اقْتُلُوا أَنْفُسَكُمْ أَوِ اخْرُجُوا مِنْ دِيَارِكُمْ مَا فَعَلُوهُ إِلَّا قَلِيلٌ مِنْهُمْ ۖ وَلَوْ أَنَّهُمْ فَعَلُوا مَا يُوعَظُونَ بِهِ لَكَانَ خَيْرًا لَهُمْ وَأَشَدَّ تَثْبِيتًا', 4, 66, 5, 18, 89, 'اگر (همانند بعضی از امتهای پیشین،) به آنان دستور می‌دادیم: «یکدیگر را به قتل برسانید»، و یا: «از وطن و خانه خود، بیرون روید»، تنها عده کمی از آنها عمل می‌کردند! و اگر اندرزهایی را که به آنان داده می‌شد انجام می‌دادند، برای آنها بهتر بود؛ و موجب تقویت ایمان آنها می‌شد.'),
(560, 560, 'وَإِذًا لَآتَيْنَاهُمْ مِنْ لَدُنَّا أَجْرًا عَظِيمًا', 4, 67, 5, 18, 89, 'و در این صورت، پاداش بزرگی از ناحیه خود به آنها می‌دادیم.'),
(561, 561, 'وَلَهَدَيْنَاهُمْ صِرَاطًا مُسْتَقِيمًا', 4, 68, 5, 18, 89, 'و آنان را به راه راست، هدایت می‌کردیم.'),
(562, 562, 'وَمَنْ يُطِعِ اللَّهَ وَالرَّسُولَ فَأُولَٰئِكَ مَعَ الَّذِينَ أَنْعَمَ اللَّهُ عَلَيْهِمْ مِنَ النَّبِيِّينَ وَالصِّدِّيقِينَ وَالشُّهَدَاءِ وَالصَّالِحِينَ ۚ وَحَسُنَ أُولَٰئِكَ رَفِيقًا', 4, 69, 5, 18, 89, 'و کسی که خدا و پیامبر را اطاعت کند، (در روز رستاخیز،) همنشین کسانی خواهد بود که خدا، نعمت خود را بر آنان تمام کرده؛ از پیامبران و صدّیقان و شهدا و صالحان؛ و آنها رفیقهای خوبی هستند!'),
(563, 563, 'ذَٰلِكَ الْفَضْلُ مِنَ اللَّهِ ۚ وَكَفَىٰ بِاللَّهِ عَلِيمًا', 4, 70, 5, 18, 89, 'این موهبتی از ناحیه خداست. و کافی است که او، (از حالِ بندگان، و نیّات و اعمالشان) آگاه است.'),
(564, 564, 'يَا أَيُّهَا الَّذِينَ آمَنُوا خُذُوا حِذْرَكُمْ فَانْفِرُوا ثُبَاتٍ أَوِ انْفِرُوا جَمِيعًا', 4, 71, 5, 18, 89, 'ای کسانی که ایمان آورده‌اید! آمادگی خود را (در برابر دشمن) حفظ کنید و در دسته‌های متعدّد، یا بصورت دسته واحد، (طبق شرایط هر زمان و هر مکان،) به سوی دشمن حرکت نمایید!'),
(565, 565, 'وَإِنَّ مِنْكُمْ لَمَنْ لَيُبَطِّئَنَّ فَإِنْ أَصَابَتْكُمْ مُصِيبَةٌ قَالَ قَدْ أَنْعَمَ اللَّهُ عَلَيَّ إِذْ لَمْ أَكُنْ مَعَهُمْ شَهِيدًا', 4, 72, 5, 18, 89, 'در میان شما، افرادی (منافق) هستند، که (هم خودشان سست می‌باشند، و هم) دیگران را به سستی می‌کشانند؛ اگر مصیبتی به شما برسد، می‌گویند: «خدا به ما نعمت داد که با مجاهدان نبودیم، تا شاهد (آن مصیبت) باشیم!»'),
(566, 566, 'وَلَئِنْ أَصَابَكُمْ فَضْلٌ مِنَ اللَّهِ لَيَقُولَنَّ كَأَنْ لَمْ تَكُنْ بَيْنَكُمْ وَبَيْنَهُ مَوَدَّةٌ يَا لَيْتَنِي كُنْتُ مَعَهُمْ فَأَفُوزَ فَوْزًا عَظِيمًا', 4, 73, 5, 18, 89, 'و اگر غنیمتی از جانب خدا به شما برسد، درست مثل اینکه هرگز میان شما و آنها دوستی و موّدتی نبوده، می‌گویند: «ای کاش ما هم با آنها بودیم، و به رستگاری (و پیروزی) ِ بزرگی می‌رسیدیم!»'),
(567, 567, ' فَلْيُقَاتِلْ فِي سَبِيلِ اللَّهِ الَّذِينَ يَشْرُونَ الْحَيَاةَ الدُّنْيَا بِالْآخِرَةِ ۚ وَمَنْ يُقَاتِلْ فِي سَبِيلِ اللَّهِ فَيُقْتَلْ أَوْ يَغْلِبْ فَسَوْفَ نُؤْتِيهِ أَجْرًا عَظِيمًا', 4, 74, 5, 18, 89, 'کسانی که زندگی دنیا را به آخرت فروخته‌اند، باید در راه خدا پیکار کنند! و آن کس که در راه خدا پیکار کند، و کشته شود یا پیروز گردد، پاداش بزرگی به او خواهیم داد.'),
(568, 568, 'وَمَا لَكُمْ لَا تُقَاتِلُونَ فِي سَبِيلِ اللَّهِ وَالْمُسْتَضْعَفِينَ مِنَ الرِّجَالِ وَالنِّسَاءِ وَالْوِلْدَانِ الَّذِينَ يَقُولُونَ رَبَّنَا أَخْرِجْنَا مِنْ هَٰذِهِ الْقَرْيَةِ الظَّالِمِ أَهْلُهَا وَاجْعَلْ لَنَا مِنْ لَدُنْكَ وَلِيًّا وَاجْعَلْ لَنَا مِنْ لَدُنْكَ نَصِيرًا', 4, 75, 5, 18, 90, 'چرا در راه خدا، و (در راه) مردان و زنان و کودکانی که (به دست ستمگران) تضعیف شده‌اند، پیکار نمی‌کنید؟! همان افراد (ستمدیده‌ای) که می‌گویند: «پروردگارا! ما را از این شهر (مکه)، که اهلش ستمگرند، بیرون ببر! و از طرف خود، برای ما سرپرستی قرار ده! و از جانب خود، یار و یاوری برای ما تعیین فرما!'),
(569, 569, 'الَّذِينَ آمَنُوا يُقَاتِلُونَ فِي سَبِيلِ اللَّهِ ۖ وَالَّذِينَ كَفَرُوا يُقَاتِلُونَ فِي سَبِيلِ الطَّاغُوتِ فَقَاتِلُوا أَوْلِيَاءَ الشَّيْطَانِ ۖ إِنَّ كَيْدَ الشَّيْطَانِ كَانَ ضَعِيفًا', 4, 76, 5, 18, 90, 'کسانی که ایمان دارند، در راه خدا پیکار می‌کنند؛ و آنها که کافرند، در راه طاغوت = بت و افراد طغیانگر. پس شما با یاران شیطان، پیکار کنید! (و از آنها نهراسید!) زیرا که نقشه شیطان، (همانند قدرتش) ضعیف است.'),
(570, 570, 'أَلَمْ تَرَ إِلَى الَّذِينَ قِيلَ لَهُمْ كُفُّوا أَيْدِيَكُمْ وَأَقِيمُوا الصَّلَاةَ وَآتُوا الزَّكَاةَ فَلَمَّا كُتِبَ عَلَيْهِمُ الْقِتَالُ إِذَا فَرِيقٌ مِنْهُمْ يَخْشَوْنَ النَّاسَ كَخَشْيَةِ اللَّهِ أَوْ أَشَدَّ خَشْيَةً ۚ وَقَالُوا رَبَّنَا لِمَ كَتَبْتَ عَلَيْنَا الْقِتَالَ لَوْلَا أَخَّرْتَنَا إِلَىٰ أَجَلٍ قَرِيبٍ ۗ قُلْ مَتَاعُ الدُّنْيَا قَلِيلٌ وَالْآخِرَةُ خَيْرٌ لِمَنِ اتَّقَىٰ وَلَا تُظْلَمُونَ فَتِيلًا', 4, 77, 5, 18, 90, 'آیا ندیدی کسانی را که (در مکّه) به آنها گفته شد: «فعلا) ً دست از جهاد بدارید! و نماز را برپا کنید! و زکات بپردازید!» (امّا آنها از این دستور، ناراحت بودند)، ولی هنگامی که (در مدینه) فرمان جهاد به آنها داده شد، جمعی از آنان، از مردم می‌ترسیدند، همان گونه که از خدا می‌ترسند، بلکه بیشتر! و گفتند: «پروردگارا! چرا جهاد را بر ما مقرّر داشتی؟! چرا این فرمان را تا زمان نزدیکی تأخیر نینداختی؟!» به آنها بگو: «سرمایه زندگی دنیا، ناچیز است! و سرای آخرت، برای کسی که پرهیزگار باشد، بهتر است! و به اندازه رشته شکافِ هسته خرمایی، به شما ستم نخواهد شد!'),
(571, 571, 'أَيْنَمَا تَكُونُوا يُدْرِكْكُمُ الْمَوْتُ وَلَوْ كُنْتُمْ فِي بُرُوجٍ مُشَيَّدَةٍ ۗ وَإِنْ تُصِبْهُمْ حَسَنَةٌ يَقُولُوا هَٰذِهِ مِنْ عِنْدِ اللَّهِ ۖ وَإِنْ تُصِبْهُمْ سَيِّئَةٌ يَقُولُوا هَٰذِهِ مِنْ عِنْدِكَ ۚ قُلْ كُلٌّ مِنْ عِنْدِ اللَّهِ ۖ فَمَالِ هَٰؤُلَاءِ الْقَوْمِ لَا يَكَادُونَ يَفْقَهُونَ حَدِيثًا', 4, 78, 5, 18, 90, 'هر جا باشید، مرگ شما را درمی‌یابد؛ هر چند در برجهای محکم باشید! و اگر به آنها = منافقان‌ حسنه (و پیروزی) برسد، می‌گویند: «این، از ناحیه خداست.» و اگر سیّئه (و شکستی) برسد، می‌گویند: «این، از ناحیه توست.» بگو: «همه اینها از ناحیه خداست.» پس چرا این گروه حاضر نیستند سخنی را درک کنند؟!'),
(572, 572, 'مَا أَصَابَكَ مِنْ حَسَنَةٍ فَمِنَ اللَّهِ ۖ وَمَا أَصَابَكَ مِنْ سَيِّئَةٍ فَمِنْ نَفْسِكَ ۚ وَأَرْسَلْنَاكَ لِلنَّاسِ رَسُولًا ۚ وَكَفَىٰ بِاللَّهِ شَهِيدًا', 4, 79, 5, 18, 90, '(آری،) آنچه از نیکیها به تو می‌رسد، از طرف خداست؛ و آنچه از بدی به تو می‌رسد، از سوی خود توست. و ما تو را رسول برای مردم فرستادیم؛ و گواهی خدا در این باره، کافی است!'),
(573, 573, 'مَنْ يُطِعِ الرَّسُولَ فَقَدْ أَطَاعَ اللَّهَ ۖ وَمَنْ تَوَلَّىٰ فَمَا أَرْسَلْنَاكَ عَلَيْهِمْ حَفِيظًا', 4, 80, 5, 18, 91, 'کسی که از پیامبر اطاعت کند، خدا را اطاعت کرده؛ و کسی که سرباز زند، تو را نگهبان (و مراقب) او نفرستادیم (و در برابر او، مسؤول نیستی).'),
(574, 574, 'وَيَقُولُونَ طَاعَةٌ فَإِذَا بَرَزُوا مِنْ عِنْدِكَ بَيَّتَ طَائِفَةٌ مِنْهُمْ غَيْرَ الَّذِي تَقُولُ ۖ وَاللَّهُ يَكْتُبُ مَا يُبَيِّتُونَ ۖ فَأَعْرِضْ عَنْهُمْ وَتَوَكَّلْ عَلَى اللَّهِ ۚ وَكَفَىٰ بِاللَّهِ وَكِيلًا', 4, 81, 5, 18, 91, 'آنها در حضور تو می‌گویند: «فرمانبرداریم»؛ امّا هنگامی که از نزد تو بیرون می‌روند، جمعی از آنان بر خلاف گفته‌های تو، جلسات سرّی شبانه تشکیل می‌دهند؛ آنچه را در این جلسات می‌گویند، خداوند می‌نویسد. اعتنایی به آنها نکن! (و از نقشه‌های آنان وحشت نداشته باش!) و بر خدا توکّل کن! کافی است که او یار و مدافع تو باشد.'),
(575, 575, 'أَفَلَا يَتَدَبَّرُونَ الْقُرْآنَ ۚ وَلَوْ كَانَ مِنْ عِنْدِ غَيْرِ اللَّهِ لَوَجَدُوا فِيهِ اخْتِلَافًا كَثِيرًا', 4, 82, 5, 18, 91, 'آیا درباره قرآن نمی‌اندیشند؟! اگر از سوی غیر خدا بود، اختلاف فراوانی در آن می‌یافتند.'),
(576, 576, 'وَإِذَا جَاءَهُمْ أَمْرٌ مِنَ الْأَمْنِ أَوِ الْخَوْفِ أَذَاعُوا بِهِ ۖ وَلَوْ رَدُّوهُ إِلَى الرَّسُولِ وَإِلَىٰ أُولِي الْأَمْرِ مِنْهُمْ لَعَلِمَهُ الَّذِينَ يَسْتَنْبِطُونَهُ مِنْهُمْ ۗ وَلَوْلَا فَضْلُ اللَّهِ عَلَيْكُمْ وَرَحْمَتُهُ لَاتَّبَعْتُمُ الشَّيْطَانَ إِلَّا قَلِيلًا', 4, 83, 5, 18, 91, 'و هنگامی که خبری از پیروزی یا شکست به آنها برسد، (بدون تحقیق،) آن را شایع می‌سازند؛ در حالی که اگر آن را به پیامبر و پیشوایان -که قدرت تشخیص کافی دارند- بازگردانند، از ریشه‌های مسائل آگاه خواهند شد. و اگر فضل و رحمت خدا بر شما نبود، جز عدّه کمی، همگی از شیطان پیروی می‌کردید (و گمراه می‌شدید).'),
(577, 577, 'فَقَاتِلْ فِي سَبِيلِ اللَّهِ لَا تُكَلَّفُ إِلَّا نَفْسَكَ ۚ وَحَرِّضِ الْمُؤْمِنِينَ ۖ عَسَى اللَّهُ أَنْ يَكُفَّ بَأْسَ الَّذِينَ كَفَرُوا ۚ وَاللَّهُ أَشَدُّ بَأْسًا وَأَشَدُّ تَنْكِيلًا', 4, 84, 5, 18, 91, 'در راه خدا پیکار کن! تنها مسؤول وظیفه خود هستی! و مؤمنان را (بر این کار،) تشویق نما! امید است خداوند از قدرت کافران جلوگیری کند (حتی اگر تنها خودت به میدان بروی)! و خداوند قدرتش بیشتر، و مجازاتش دردناکتر است.'),
(578, 578, 'مَنْ يَشْفَعْ شَفَاعَةً حَسَنَةً يَكُنْ لَهُ نَصِيبٌ مِنْهَا ۖ وَمَنْ يَشْفَعْ شَفَاعَةً سَيِّئَةً يَكُنْ لَهُ كِفْلٌ مِنْهَا ۗ وَكَانَ اللَّهُ عَلَىٰ كُلِّ شَيْءٍ مُقِيتًا', 4, 85, 5, 18, 91, 'کسی که شفاعت = تشویق و کمک‌ به کار نیکی کند، نصیبی از آن برای او خواهد بود؛ و کسی که شفاعت = تشویق و کمک‌ به کار بدی کند، سهمی از آن خواهد داشت. و خداوند، حسابرس و نگهدار هر چیز است.'),
(579, 579, 'وَإِذَا حُيِّيتُمْ بِتَحِيَّةٍ فَحَيُّوا بِأَحْسَنَ مِنْهَا أَوْ رُدُّوهَا ۗ إِنَّ اللَّهَ كَانَ عَلَىٰ كُلِّ شَيْءٍ حَسِيبًا', 4, 86, 5, 18, 91, 'هرگاه به شما تحیّت گویند، پاسخ آن را بهتر از آن بدهید؛ یا (لااقل) به همان گونه پاسخ گویید! خداوند حساب همه چیز را دارد.'),
(580, 580, 'اللَّهُ لَا إِلَٰهَ إِلَّا هُوَ ۚ لَيَجْمَعَنَّكُمْ إِلَىٰ يَوْمِ الْقِيَامَةِ لَا رَيْبَ فِيهِ ۗ وَمَنْ أَصْدَقُ مِنَ اللَّهِ حَدِيثًا', 4, 87, 5, 18, 92, 'خداوند، معبودی جز او نیست! و به یقین، همه شما را در روز رستاخیز -که شکی در آن نیست- جمع می‌کند! و کیست که از خداوند، راستگوتر باشد؟'),
(581, 581, ' فَمَا لَكُمْ فِي الْمُنَافِقِينَ فِئَتَيْنِ وَاللَّهُ أَرْكَسَهُمْ بِمَا كَسَبُوا ۚ أَتُرِيدُونَ أَنْ تَهْدُوا مَنْ أَضَلَّ اللَّهُ ۖ وَمَنْ يُضْلِلِ اللَّهُ فَلَنْ تَجِدَ لَهُ سَبِيلًا', 4, 88, 5, 19, 92, 'چرا درباره منافقین دو دسته شده‌اید؟! (بعضی جنگ با آنها را ممنوع، و بعضی مجاز می‌دانید.) در حالی که خداوند بخاطر اعمالشان، (افکار) آنها را کاملاً وارونه کرده است! آیا شما می‌خواهید کسانی را که خداوند (بر اثر اعمال زشتشان) گمراه کرده، هدایت کنید؟! در حالی که هر کس را خداوند گمراه کند، راهی برای او نخواهی یافت.'),
(582, 582, 'وَدُّوا لَوْ تَكْفُرُونَ كَمَا كَفَرُوا فَتَكُونُونَ سَوَاءً ۖ فَلَا تَتَّخِذُوا مِنْهُمْ أَوْلِيَاءَ حَتَّىٰ يُهَاجِرُوا فِي سَبِيلِ اللَّهِ ۚ فَإِنْ تَوَلَّوْا فَخُذُوهُمْ وَاقْتُلُوهُمْ حَيْثُ وَجَدْتُمُوهُمْ ۖ وَلَا تَتَّخِذُوا مِنْهُمْ وَلِيًّا وَلَا نَصِيرًا', 4, 89, 5, 19, 92, 'آنان آرزو می‌کنند که شما هم مانند ایشان کافر شوید، و مساوی یکدیگر باشید. بنابر این، از آنها دوستانی انتخاب نکنید، مگر اینکه (توبه کنند، و) در راه خدا هجرت نمایند. هرگاه از این کار سر باز زنند، (و به اقدام بر ضدّ شما ادامه دهند،) هر جا آنها را یافتید، اسیر کنید! و (در صورت احساس خطر) به قتل برسانید! و از میان آنها، دوست و یار و یاوری اختیار نکنید!.'),
(583, 583, 'إِلَّا الَّذِينَ يَصِلُونَ إِلَىٰ قَوْمٍ بَيْنَكُمْ وَبَيْنَهُمْ مِيثَاقٌ أَوْ جَاءُوكُمْ حَصِرَتْ صُدُورُهُمْ أَنْ يُقَاتِلُوكُمْ أَوْ يُقَاتِلُوا قَوْمَهُمْ ۚ وَلَوْ شَاءَ اللَّهُ لَسَلَّطَهُمْ عَلَيْكُمْ فَلَقَاتَلُوكُمْ ۚ فَإِنِ اعْتَزَلُوكُمْ فَلَمْ يُقَاتِلُوكُمْ وَأَلْقَوْا إِلَيْكُمُ السَّلَمَ فَمَا جَعَلَ اللَّهُ لَكُمْ عَلَيْهِمْ سَبِيلًا', 4, 90, 5, 19, 92, 'مگر آنها که با همپیمانان شما، پیمان بسته‌اند؛ یا آنها که به سوی شما می‌آیند، و از پیکار با شما، یا پیکار با قوم خود ناتوان شده‌اند؛ (نه سر جنگ با شما دارند، و نه توانایی مبارزه با قوم خود.) و اگر خداوند بخواهد، آنان را بر شما مسلطّ می‌کند تا با شما پیکار کنند. پس اگر از شما کناره‌گیری کرده و با شما پیکار ننمودند، (بلکه) پیشنهاد صلح کردند، خداوند به شما اجازه نمی‌دهد که متعرّض آنان شوید.'),
(584, 584, 'سَتَجِدُونَ آخَرِينَ يُرِيدُونَ أَنْ يَأْمَنُوكُمْ وَيَأْمَنُوا قَوْمَهُمْ كُلَّ مَا رُدُّوا إِلَى الْفِتْنَةِ أُرْكِسُوا فِيهَا ۚ فَإِنْ لَمْ يَعْتَزِلُوكُمْ وَيُلْقُوا إِلَيْكُمُ السَّلَمَ وَيَكُفُّوا أَيْدِيَهُمْ فَخُذُوهُمْ وَاقْتُلُوهُمْ حَيْثُ ثَقِفْتُمُوهُمْ ۚ وَأُولَٰئِكُمْ جَعَلْنَا لَكُمْ عَلَيْهِمْ سُلْطَانًا مُبِينًا', 4, 91, 5, 19, 92, 'بزودی جمعیّت دیگری را می‌یابید که می‌خواهند هم از ناحیه شما در امان باشند، و هم از ناحیه قوم خودشان (که مشرکند. لذا نزد شما ادّعای ایمان می‌کنند؛ ولی) هر زمان آنان را به سوی فتنه (و بت پرستی) بازگردانند، با سر در آن فرو می‌روند! اگر از درگیری با شما کنار نرفتند و پیشنهاد صلح نکردند و دست از شما نکشیدند، آنها را هر جا یافتید اسیر کنید و (یا) به قتل برسانید! آنها کسانی هستند که ما برای شما، تسلّط آشکاری نسبت به آنان قرار داده‌ایم.'),
(585, 585, 'وَمَا كَانَ لِمُؤْمِنٍ أَنْ يَقْتُلَ مُؤْمِنًا إِلَّا خَطَأً ۚ وَمَنْ قَتَلَ مُؤْمِنًا خَطَأً فَتَحْرِيرُ رَقَبَةٍ مُؤْمِنَةٍ وَدِيَةٌ مُسَلَّمَةٌ إِلَىٰ أَهْلِهِ إِلَّا أَنْ يَصَّدَّقُوا ۚ فَإِنْ كَانَ مِنْ قَوْمٍ عَدُوٍّ لَكُمْ وَهُوَ مُؤْمِنٌ فَتَحْرِيرُ رَقَبَةٍ مُؤْمِنَةٍ ۖ وَإِنْ كَانَ مِنْ قَوْمٍ بَيْنَكُمْ وَبَيْنَهُمْ مِيثَاقٌ فَدِيَةٌ مُسَلَّمَةٌ إِلَىٰ أَهْلِهِ وَتَحْرِيرُ رَقَبَةٍ مُؤْمِنَةٍ ۖ فَمَنْ لَمْ يَجِدْ فَصِيَامُ شَهْرَيْنِ مُتَتَابِعَيْنِ تَوْبَةً مِنَ اللَّهِ ۗ وَكَانَ اللَّهُ عَلِيمًا حَكِيمًا', 4, 92, 5, 19, 93, 'هیچ فرد باایمانی مجاز نیست که مؤمنی را به قتل برساند، مگر اینکه این کار از روی خطا و اشتباه از او سر زند؛ (و در عین حال،) کسی که مؤمنی را از روی خطا به قتل رساند، باید یک برده مؤمن را آزاد کند و خونبهایی به کسان او بپردازد؛ مگر اینکه آنها خونبها را ببخشند. و اگر مقتول، از گروهی باشد که دشمنان شما هستند (و کافرند)، ولی مقتول باایمان بوده، (تنها) باید یک برده مؤمن را آزاد کند (و پرداختن خونبها لازم نیست). و اگر از جمعیّتی باشد که میان شما و آنها پیمانی برقرار است، باید خونبهای او را به کسان او بپردازد، و یک برده مؤمن (نیز) آزاد کند. و آن کس که دسترسی (به آزاد کردن برده) ندارد، دو ماه پی در پی روزه می‌گیرد. این، (یک نوع تخفیف، و) توبه الهی است. و خداوند، دانا و حکیم است.'),
(586, 586, 'وَمَنْ يَقْتُلْ مُؤْمِنًا مُتَعَمِّدًا فَجَزَاؤُهُ جَهَنَّمُ خَالِدًا فِيهَا وَغَضِبَ اللَّهُ عَلَيْهِ وَلَعَنَهُ وَأَعَدَّ لَهُ عَذَابًا عَظِيمًا', 4, 93, 5, 19, 93, 'و هر کس، فرد باایمانی را از روی عمد به قتل برساند، مجازاتِ او دوزخ است؛ در حالی که جاودانه در آن می‌ماند؛ و خداوند بر او غضب می‌کند؛ و او را از رحمتش دور می‌سازد؛ و عذاب عظیمی برای او آماده ساخته است.'),
(587, 587, 'يَا أَيُّهَا الَّذِينَ آمَنُوا إِذَا ضَرَبْتُمْ فِي سَبِيلِ اللَّهِ فَتَبَيَّنُوا وَلَا تَقُولُوا لِمَنْ أَلْقَىٰ إِلَيْكُمُ السَّلَامَ لَسْتَ مُؤْمِنًا تَبْتَغُونَ عَرَضَ الْحَيَاةِ الدُّنْيَا فَعِنْدَ اللَّهِ مَغَانِمُ كَثِيرَةٌ ۚ كَذَٰلِكَ كُنْتُمْ مِنْ قَبْلُ فَمَنَّ اللَّهُ عَلَيْكُمْ فَتَبَيَّنُوا ۚ إِنَّ اللَّهَ كَانَ بِمَا تَعْمَلُونَ خَبِيرًا', 4, 94, 5, 19, 93, 'ای کسانی که ایمان آورده‌اید! هنگامی که در راه خدا گام می‌زنید (و به سفری برای جهاد می‌روید)، تحقیق کنید! و بخاطر اینکه سرمایه ناپایدار دنیا (و غنایمی) به دست آورید، به کسی که اظهار صلح و اسلام می‌کند نگویید: «مسلمان نیستی» زیرا غنیمتهای فراوانی (برای شما) نزد خداست. شما قبلاً چنین بودید؛ و خداوند بر شما منّت نهاد (و هدایت شدید). پس، (بشکرانه این نعمت بزرگ،) تحقیق کنید! خداوند به آنچه انجام می‌دهید آگاه است.'),
(588, 588, 'لَا يَسْتَوِي الْقَاعِدُونَ مِنَ الْمُؤْمِنِينَ غَيْرُ أُولِي الضَّرَرِ وَالْمُجَاهِدُونَ فِي سَبِيلِ اللَّهِ بِأَمْوَالِهِمْ وَأَنْفُسِهِمْ ۚ فَضَّلَ اللَّهُ الْمُجَاهِدِينَ بِأَمْوَالِهِمْ وَأَنْفُسِهِمْ عَلَى الْقَاعِدِينَ دَرَجَةً ۚ وَكُلًّا وَعَدَ اللَّهُ الْحُسْنَىٰ ۚ وَفَضَّلَ اللَّهُ الْمُجَاهِدِينَ عَلَى الْقَاعِدِينَ أَجْرًا عَظِيمًا', 4, 95, 5, 19, 94, '(هرگز) افراد باایمانی که بدون بیماری و ناراحتی، از جهاد بازنشستند، با مجاهدانی که در راه خدا با مال و جان خود جهاد کردند، یکسان نیستند! خداوند، مجاهدانی را که با مال و جان خود جهاد نمودند، بر قاعدان = ترک‌کنندگان جهاد برتری مهمّی بخشیده؛ و به هر یک از این دو گروه (به نسبت اعمال نیکشان،) خداوند وعده پاداش نیک داده، و مجاهدان را بر قاعدان، با پاداش عظیمی برتری بخشیده است.'),
(589, 589, 'دَرَجَاتٍ مِنْهُ وَمَغْفِرَةً وَرَحْمَةً ۚ وَكَانَ اللَّهُ غَفُورًا رَحِيمًا', 4, 96, 5, 19, 94, 'درجات (مهمّی) از ناحیه خداوند، و آمرزش و رحمت (نصیب آنان می‌گردد)؛ و (اگر لغزشهایی داشته‌اند،) خداوند آمرزنده و مهربان است.'),
(590, 590, 'إِنَّ الَّذِينَ تَوَفَّاهُمُ الْمَلَائِكَةُ ظَالِمِي أَنْفُسِهِمْ قَالُوا فِيمَ كُنْتُمْ ۖ قَالُوا كُنَّا مُسْتَضْعَفِينَ فِي الْأَرْضِ ۚ قَالُوا أَلَمْ تَكُنْ أَرْضُ اللَّهِ وَاسِعَةً فَتُهَاجِرُوا فِيهَا ۚ فَأُولَٰئِكَ مَأْوَاهُمْ جَهَنَّمُ ۖ وَسَاءَتْ مَصِيرًا', 4, 97, 5, 19, 94, 'کسانی که فرشتگان (قبض ارواح)، روح آنها را گرفتند در حالی که به خویشتن ستم کرده بودند، به آنها گفتند: «شما در چه حالی بودید؟ (و چرا با اینکه مسلمان بودید، در صفِ کفّار جای داشتید؟!)» گفتند: «ما در سرزمین خود، تحت فشار و مستضعف بودیم.» آنها = فرشتگان‌ گفتند: «مگر سرزمین خدا، پهناور نبود که مهاجرت کنید؟!» آنها (عذری نداشتند، و) جایگاهشان دوزخ است، و سرانجام بدی دارند.'),
(591, 591, 'إِلَّا الْمُسْتَضْعَفِينَ مِنَ الرِّجَالِ وَالنِّسَاءِ وَالْوِلْدَانِ لَا يَسْتَطِيعُونَ حِيلَةً وَلَا يَهْتَدُونَ سَبِيلًا', 4, 98, 5, 19, 94, 'مگر آن دسته از مردان و زنان و کودکانی که براستی تحت فشار قرار گرفته‌اند (و حقیقتاً مستضعفند)؛ نه چاره‌ای دارند، و نه راهی (برای نجات از آن محیط آلوده) می‌یابند.'),
(592, 592, 'فَأُولَٰئِكَ عَسَى اللَّهُ أَنْ يَعْفُوَ عَنْهُمْ ۚ وَكَانَ اللَّهُ عَفُوًّا غَفُورًا', 4, 99, 5, 19, 94, 'ممکن است خداوند، آنها را مورد عفو قرار دهد؛ و خداوند، عفو کننده و آمرزنده است.'),
(593, 593, ' وَمَنْ يُهَاجِرْ فِي سَبِيلِ اللَّهِ يَجِدْ فِي الْأَرْضِ مُرَاغَمًا كَثِيرًا وَسَعَةً ۚ وَمَنْ يَخْرُجْ مِنْ بَيْتِهِ مُهَاجِرًا إِلَى اللَّهِ وَرَسُولِهِ ثُمَّ يُدْرِكْهُ الْمَوْتُ فَقَدْ وَقَعَ أَجْرُهُ عَلَى اللَّهِ ۗ وَكَانَ اللَّهُ غَفُورًا رَحِيمًا', 4, 100, 5, 19, 94, 'کسی که در راه خدا هجرت کند، جاهای امنِ فراوان و گسترده‌ای در زمین می‌یابد. و هر کس بعنوان مهاجرت به سوی خدا و پیامبر او، از خانه خود بیرون رود، سپس مرگش فرا رسد، پاداش او بر خداست؛ و خداوند، آمرزنده و مهربان است.'),
(594, 594, 'وَإِذَا ضَرَبْتُمْ فِي الْأَرْضِ فَلَيْسَ عَلَيْكُمْ جُنَاحٌ أَنْ تَقْصُرُوا مِنَ الصَّلَاةِ إِنْ خِفْتُمْ أَنْ يَفْتِنَكُمُ الَّذِينَ كَفَرُوا ۚ إِنَّ الْكَافِرِينَ كَانُوا لَكُمْ عَدُوًّا مُبِينًا', 4, 101, 5, 19, 94, 'هنگامی که سفر می‌کنید، گناهی بر شما نیست که نماز را کوتاه کنید اگر از فتنه (و خطر) ِ کافران بترسید؛ زیرا کافران، برای شما دشمن آشکاری هستند.'),
(595, 595, 'وَإِذَا كُنْتَ فِيهِمْ فَأَقَمْتَ لَهُمُ الصَّلَاةَ فَلْتَقُمْ طَائِفَةٌ مِنْهُمْ مَعَكَ وَلْيَأْخُذُوا أَسْلِحَتَهُمْ فَإِذَا سَجَدُوا فَلْيَكُونُوا مِنْ وَرَائِكُمْ وَلْتَأْتِ طَائِفَةٌ أُخْرَىٰ لَمْ يُصَلُّوا فَلْيُصَلُّوا مَعَكَ وَلْيَأْخُذُوا حِذْرَهُمْ وَأَسْلِحَتَهُمْ ۗ وَدَّ الَّذِينَ كَفَرُوا لَوْ تَغْفُلُونَ عَنْ أَسْلِحَتِكُمْ وَأَمْتِعَتِكُمْ فَيَمِيلُونَ عَلَيْكُمْ مَيْلَةً وَاحِدَةً ۚ وَلَا جُنَاحَ عَلَيْكُمْ إِنْ كَانَ بِكُمْ أَذًى مِنْ مَطَرٍ أَوْ كُنْتُمْ مَرْضَىٰ أَنْ تَضَعُوا أَسْلِحَتَكُمْ ۖ وَخُذُوا حِذْرَكُمْ ۗ إِنَّ اللَّهَ أَعَدَّ لِلْكَافِرِينَ عَذَابًا مُهِينًا', 4, 102, 5, 19, 95, 'و هنگامی که در میان آنها باشی، و (در میدان جنگ) برای آنها نماز را برپا کنی، باید دسته‌ای از آنها با تو (به نماز) برخیزند، و سلاحهایشان را با خود برگیرند؛ و هنگامی که سجده کردند (و نماز را به پایان رساندند)، باید به پشتِ سرِ شما (به میدان نبرد) بروند، و آن دسته دیگر که نماز نخوانده‌اند (و مشغول پیکار بوده‌اند)، بیایند و با تو نماز بخوانند؛ آنها باید وسایل دفاعی و سلاحهایشان (را در حال نماز) با خود حمل کنند؛ (زیرا) کافران آرزو دارند که شما از سلاحها و متاعهای خود غافل شوید و یکباره به شما هجوم آورند. و اگر از باران ناراحتید، و یا بیمار (و مجروح) هستید، مانعی ندارد که سلاحهای خود را بر زمین بگذارید؛ ولی وسایل دفاعی (مانند زره و خود را) با خود بردارید خداوند، عذاب خوارکننده‌ای برای کافران فراهم ساخته است.'),
(596, 596, 'فَإِذَا قَضَيْتُمُ الصَّلَاةَ فَاذْكُرُوا اللَّهَ قِيَامًا وَقُعُودًا وَعَلَىٰ جُنُوبِكُمْ ۚ فَإِذَا اطْمَأْنَنْتُمْ فَأَقِيمُوا الصَّلَاةَ ۚ إِنَّ الصَّلَاةَ كَانَتْ عَلَى الْمُؤْمِنِينَ كِتَابًا مَوْقُوتًا', 4, 103, 5, 19, 95, 'و هنگامی که نماز را به پایان رساندید، خدا را یاد کنید؛ ایستاده، و نشسته، و در حالی که به پهلو خوابیده‌اید! و هرگاه آرامش یافتید (و حالت ترس زایل گشت)، نماز را (به طور معمول) انجام دهید، زیرا نماز، وظیفه ثابت و معیّنی برای مؤمنان است!'),
(597, 597, 'وَلَا تَهِنُوا فِي ابْتِغَاءِ الْقَوْمِ ۖ إِنْ تَكُونُوا تَأْلَمُونَ فَإِنَّهُمْ يَأْلَمُونَ كَمَا تَأْلَمُونَ ۖ وَتَرْجُونَ مِنَ اللَّهِ مَا لَا يَرْجُونَ ۗ وَكَانَ اللَّهُ عَلِيمًا حَكِيمًا', 4, 104, 5, 19, 95, 'و در راه تعقیب دشمن، (هرگز) سست نشوید! (زیرا) اگر شما درد و رنج می‌بینید، آنها نیز همانند شما درد و رنج می‌بینند؛ ولی شما امیدی از خدا دارید که آنها ندارند؛ و خداوند، دانا و حکیم است.'),
(598, 598, 'إِنَّا أَنْزَلْنَا إِلَيْكَ الْكِتَابَ بِالْحَقِّ لِتَحْكُمَ بَيْنَ النَّاسِ بِمَا أَرَاكَ اللَّهُ ۚ وَلَا تَكُنْ لِلْخَائِنِينَ خَصِيمًا', 4, 105, 5, 19, 95, 'ما این کتاب را بحق بر تو نازل کردیم؛ تا به آنچه خداوند به تو آموخته، در میان مردم قضاوت کنی؛ و از کسانی مباش که از خائنان حمایت نمایی!'),
(599, 599, 'وَاسْتَغْفِرِ اللَّهَ ۖ إِنَّ اللَّهَ كَانَ غَفُورًا رَحِيمًا', 4, 106, 5, 19, 96, 'و از خداوند، طلب آمرزش نما! که خداوند، آمرزنده و مهربان است.'),
(600, 600, 'وَلَا تُجَادِلْ عَنِ الَّذِينَ يَخْتَانُونَ أَنْفُسَهُمْ ۚ إِنَّ اللَّهَ لَا يُحِبُّ مَنْ كَانَ خَوَّانًا أَثِيمًا', 4, 107, 5, 19, 96, 'و از آنها که به خود خیانت کردند، دفاع مکن! زیرا خداوند، افراد خیانت‌پیشه گنهکار را دوست ندارد.'),
(601, 601, 'يَسْتَخْفُونَ مِنَ النَّاسِ وَلَا يَسْتَخْفُونَ مِنَ اللَّهِ وَهُوَ مَعَهُمْ إِذْ يُبَيِّتُونَ مَا لَا يَرْضَىٰ مِنَ الْقَوْلِ ۚ وَكَانَ اللَّهُ بِمَا يَعْمَلُونَ مُحِيطًا', 4, 108, 5, 19, 96, 'آنها زشتکاریِ خود را از مردم پنهان می‌دارند؛ اما از خدا پنهان نمی‌دارند، و هنگامی که در مجالس شبانه، سخنانی که خدا راضی نبود می‌گفتند، خدا با آنها بود، خدا به آنچه انجام می‌دهند، احاطه دارد.'),
(602, 602, 'هَا أَنْتُمْ هَٰؤُلَاءِ جَادَلْتُمْ عَنْهُمْ فِي الْحَيَاةِ الدُّنْيَا فَمَنْ يُجَادِلُ اللَّهَ عَنْهُمْ يَوْمَ الْقِيَامَةِ أَمْ مَنْ يَكُونُ عَلَيْهِمْ وَكِيلًا', 4, 109, 5, 19, 96, 'آری، شما همانها هستید که در زندگی این جهان، از آنان دفاع کردید! اما کیست که در برابر خداوند، در روز رستاخیز از آنها دفاع کند؟! یا چه کسی است که وکیل و حامی آنها باشد؟!'),
(603, 603, 'وَمَنْ يَعْمَلْ سُوءًا أَوْ يَظْلِمْ نَفْسَهُ ثُمَّ يَسْتَغْفِرِ اللَّهَ يَجِدِ اللَّهَ غَفُورًا رَحِيمًا', 4, 110, 5, 19, 96, 'کسی که کار بدی انجام دهد یا به خود ستم کند، سپس از خداوند طلب آمرزش نماید، خدا را آمرزنده و مهربان خواهد یافت.'),
(604, 604, 'وَمَنْ يَكْسِبْ إِثْمًا فَإِنَّمَا يَكْسِبُهُ عَلَىٰ نَفْسِهِ ۚ وَكَانَ اللَّهُ عَلِيمًا حَكِيمًا', 4, 111, 5, 19, 96, 'و کسی که گناهی مرتکب شود، به زیان خود مرتکب شده؛ خداوند، دانا و حکیم است.'),
(605, 605, 'وَمَنْ يَكْسِبْ خَطِيئَةً أَوْ إِثْمًا ثُمَّ يَرْمِ بِهِ بَرِيئًا فَقَدِ احْتَمَلَ بُهْتَانًا وَإِثْمًا مُبِينًا', 4, 112, 5, 19, 96, 'و کسی که خطا یا گناهی مرتکب شود، سپس بیگناهی را متهم سازد، بار بهتان و گناهِ آشکاری بر دوش گرفته است.'),
(606, 606, 'وَلَوْلَا فَضْلُ اللَّهِ عَلَيْكَ وَرَحْمَتُهُ لَهَمَّتْ طَائِفَةٌ مِنْهُمْ أَنْ يُضِلُّوكَ وَمَا يُضِلُّونَ إِلَّا أَنْفُسَهُمْ ۖ وَمَا يَضُرُّونَكَ مِنْ شَيْءٍ ۚ وَأَنْزَلَ اللَّهُ عَلَيْكَ الْكِتَابَ وَالْحِكْمَةَ وَعَلَّمَكَ مَا لَمْ تَكُنْ تَعْلَمُ ۚ وَكَانَ فَضْلُ اللَّهِ عَلَيْكَ عَظِيمًا', 4, 113, 5, 19, 96, 'اگر فضل و رحمت خدا شامل حال تو نبود، گروهی از آنان تصمیم داشتند تو را گمراه کنند؛ اما جز خودشان را گمراه نمی‌کنند؛ و هیچ‌گونه زیانی به تو نمی‌رسانند. و خداوند، کتاب و حکمت بر تو نازل کرد؛ و آنچه را نمی‌دانستی، به تو آموخت؛ و فضل خدا بر تو (همواره) بزرگ بوده است.'),
(607, 607, ' لَا خَيْرَ فِي كَثِيرٍ مِنْ نَجْوَاهُمْ إِلَّا مَنْ أَمَرَ بِصَدَقَةٍ أَوْ مَعْرُوفٍ أَوْ إِصْلَاحٍ بَيْنَ النَّاسِ ۚ وَمَنْ يَفْعَلْ ذَٰلِكَ ابْتِغَاءَ مَرْضَاتِ اللَّهِ فَسَوْفَ نُؤْتِيهِ أَجْرًا عَظِيمًا', 4, 114, 5, 20, 97, 'در بسیاری از سخنانِ درِگوشی (و جلسات محرمانه) آنها، خیر و سودی نیست؛ مگر کسی که (به این وسیله،) امر به کمک به دیگران، یا کار نیک، یا اصلاح در میان مردم کند؛ و هر کس برای خشنودی پروردگار چنین کند، پاداش بزرگی به او خواهیم داد.'),
(608, 608, 'وَمَنْ يُشَاقِقِ الرَّسُولَ مِنْ بَعْدِ مَا تَبَيَّنَ لَهُ الْهُدَىٰ وَيَتَّبِعْ غَيْرَ سَبِيلِ الْمُؤْمِنِينَ نُوَلِّهِ مَا تَوَلَّىٰ وَنُصْلِهِ جَهَنَّمَ ۖ وَسَاءَتْ مَصِيرًا', 4, 115, 5, 20, 97, 'کسی که بعد از آشکار شدن حق، با پیامبر مخالفت کند، و از راهی جز راه مؤمنان پیروی نماید، ما او را به همان راه که می‌رود می‌بریم؛ و به دوزخ داخل می‌کنیم؛ و جایگاه بدی دارد.'),
(609, 609, 'إِنَّ اللَّهَ لَا يَغْفِرُ أَنْ يُشْرَكَ بِهِ وَيَغْفِرُ مَا دُونَ ذَٰلِكَ لِمَنْ يَشَاءُ ۚ وَمَنْ يُشْرِكْ بِاللَّهِ فَقَدْ ضَلَّ ضَلَالًا بَعِيدًا', 4, 116, 5, 20, 97, 'خداوند، شرک به او را نمی‌آمرزد؛ (ولی) کمتر از آن را برای هر کس بخواهد (و شایسته بداند) می‌آمرزد. و هر کس برای خدا همتایی قرار دهد، در گمراهی دوری افتاده است.'),
(610, 610, 'إِنْ يَدْعُونَ مِنْ دُونِهِ إِلَّا إِنَاثًا وَإِنْ يَدْعُونَ إِلَّا شَيْطَانًا مَرِيدًا', 4, 117, 5, 20, 97, 'آنچه غیر از خدا می‌خوانند، فقط بتهایی است (بی‌روح)، که هیچ اثری ندارد؛ و (یا) شیطان سرکش و ویرانگر است.'),
(611, 611, 'لَعَنَهُ اللَّهُ ۘ وَقَالَ لَأَتَّخِذَنَّ مِنْ عِبَادِكَ نَصِيبًا مَفْرُوضًا', 4, 118, 5, 20, 97, 'خدا او را از رحمت خویش دور ساخته؛ و او گفته است: «از بندگان تو، سهم معیّنی خواهم گرفت!'),
(612, 612, 'وَلَأُضِلَّنَّهُمْ وَلَأُمَنِّيَنَّهُمْ وَلَآمُرَنَّهُمْ فَلَيُبَتِّكُنَّ آذَانَ الْأَنْعَامِ وَلَآمُرَنَّهُمْ فَلَيُغَيِّرُنَّ خَلْقَ اللَّهِ ۚ وَمَنْ يَتَّخِذِ الشَّيْطَانَ وَلِيًّا مِنْ دُونِ اللَّهِ فَقَدْ خَسِرَ خُسْرَانًا مُبِينًا', 4, 119, 5, 20, 97, 'و آنها را گمراه می‌کنم! و به آرزوها سرگرم می‌سازم! و به آنان دستور می‌دهم که (اعمال خرافی انجام دهند، و) گوش چهارپایان را بشکافند، و آفرینشِ پاک خدایی را تغییر دهند! (و فطرت توحید را به شرک بیالایند!)» و هر کس، شیطان را به جای خدا ولّیِ خود برگزیند، زیانِ آشکاری کرده است.'),
(613, 613, 'يَعِدُهُمْ وَيُمَنِّيهِمْ ۖ وَمَا يَعِدُهُمُ الشَّيْطَانُ إِلَّا غُرُورًا', 4, 120, 5, 20, 97, 'شیطان به آنها وعده‌ها (ی دروغین) می‌دهد؛ و به آرزوها، سرگرم می‌سازد؛ در حالی که جز فریب و نیرنگ، به آنها وعده نمی‌دهد.'),
(614, 614, 'أُولَٰئِكَ مَأْوَاهُمْ جَهَنَّمُ وَلَا يَجِدُونَ عَنْهَا مَحِيصًا', 4, 121, 5, 20, 97, 'آنها = پیروان شیطان‌ جایگاهشان جهنم است؛ و هیچ راه فراری ندارند.'),
(615, 615, 'وَالَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ سَنُدْخِلُهُمْ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهَارُ خَالِدِينَ فِيهَا أَبَدًا ۖ وَعْدَ اللَّهِ حَقًّا ۚ وَمَنْ أَصْدَقُ مِنَ اللَّهِ قِيلًا', 4, 122, 5, 20, 98, 'و کسانی که ایمان آورده‌اند و اعمال صالح انجام داده‌اند، بزودی آن را در باغهایی از بهشت وارد می‌کنیم که نهرها از زیر درختانش جاری است؛ جاودانه در آن خواهند ماند. وعده حق خداوند است و کیست که در گفتار و وعده‌هایش، از خدا صادقتر باشد؟!'),
(616, 616, 'لَيْسَ بِأَمَانِيِّكُمْ وَلَا أَمَانِيِّ أَهْلِ الْكِتَابِ ۗ مَنْ يَعْمَلْ سُوءًا يُجْزَ بِهِ وَلَا يَجِدْ لَهُ مِنْ دُونِ اللَّهِ وَلِيًّا وَلَا نَصِيرًا', 4, 123, 5, 20, 98, '(فضیلت و برتری) به آرزوهای شما و آرزوهای اهل کتاب نیست؛ هر کس عمل بدی انجام دهد، کیفر داده می‌شود؛ و کسی را جز خدا، ولّی و یاور خود نخواهد یافت.'),
(617, 617, 'وَمَنْ يَعْمَلْ مِنَ الصَّالِحَاتِ مِنْ ذَكَرٍ أَوْ أُنْثَىٰ وَهُوَ مُؤْمِنٌ فَأُولَٰئِكَ يَدْخُلُونَ الْجَنَّةَ وَلَا يُظْلَمُونَ نَقِيرًا', 4, 124, 5, 20, 98, 'و کسی که چیزی از اعمال صالح را انجام دهد، خواه مرد باشد یا زن، در حالی که ایمان داشته باشد، چنان کسانی داخل بهشت می‌شوند؛ و کمترین ستمی به آنها نخواهد شد.'),
(618, 618, 'وَمَنْ أَحْسَنُ دِينًا مِمَّنْ أَسْلَمَ وَجْهَهُ لِلَّهِ وَهُوَ مُحْسِنٌ وَاتَّبَعَ مِلَّةَ إِبْرَاهِيمَ حَنِيفًا ۗ وَاتَّخَذَ اللَّهُ إِبْرَاهِيمَ خَلِيلًا', 4, 125, 5, 20, 98, 'دین و آیین چه کسی بهتر است از آن کس که خود را تسلیم خدا کند، و نیکوکار باشد، و پیرو آیین خالص و پاکِ ابراهیم گردد؟ و خدا ابراهیم را به دوستیِ خود، انتخاب کرد.'),
(619, 619, 'وَلِلَّهِ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ ۚ وَكَانَ اللَّهُ بِكُلِّ شَيْءٍ مُحِيطًا', 4, 126, 5, 20, 98, 'آنچه در آسمانها و زمین است، از آن خداست؛ و خداوند به هر چیزی احاطه دارد.'),
(620, 620, 'وَيَسْتَفْتُونَكَ فِي النِّسَاءِ ۖ قُلِ اللَّهُ يُفْتِيكُمْ فِيهِنَّ وَمَا يُتْلَىٰ عَلَيْكُمْ فِي الْكِتَابِ فِي يَتَامَى النِّسَاءِ اللَّاتِي لَا تُؤْتُونَهُنَّ مَا كُتِبَ لَهُنَّ وَتَرْغَبُونَ أَنْ تَنْكِحُوهُنَّ وَالْمُسْتَضْعَفِينَ مِنَ الْوِلْدَانِ وَأَنْ تَقُومُوا لِلْيَتَامَىٰ بِالْقِسْطِ ۚ وَمَا تَفْعَلُوا مِنْ خَيْرٍ فَإِنَّ اللَّهَ كَانَ بِهِ عَلِيمًا', 4, 127, 5, 20, 98, 'از تو درباره حکم زنان سؤال می‌کنند؛ بگو: «خداوند درباره آنان به شما پاسخ می دهد: آنچه در قرآن درباره زنان یتیمی که حقوقشان را به آنها نمی‌دهید، و می‌خواهید با آنها ازدواج کنید، و نیز آنچه درباره کودکان صغیر و ناتوان برای شما بیان شده است، (قسمتی از سفارشهای خداوند در این زمینه می‌باشد؛ و نیز به شما سفارش می‌کند که) با یتیمان به عدالت رفتار کنید! و آنچه از نیکیها انجام می‌دهید؛ خداوند از آن آگاه است (و به شما پاداش شایسته می‌دهد).'),
(621, 621, 'وَإِنِ امْرَأَةٌ خَافَتْ مِنْ بَعْلِهَا نُشُوزًا أَوْ إِعْرَاضًا فَلَا جُنَاحَ عَلَيْهِمَا أَنْ يُصْلِحَا بَيْنَهُمَا صُلْحًا ۚ وَالصُّلْحُ خَيْرٌ ۗ وَأُحْضِرَتِ الْأَنْفُسُ الشُّحَّ ۚ وَإِنْ تُحْسِنُوا وَتَتَّقُوا فَإِنَّ اللَّهَ كَانَ بِمَا تَعْمَلُونَ خَبِيرًا', 4, 128, 5, 20, 99, 'و اگر زنی، از طغیان و سرکشی یا اعراضِ شوهرش، بیم داشته باشد، مانعی ندارد با هم صلح کنند (و زن یا مرد، از پاره‌ای از حقوق خود، بخاطر صلح، صرف نظر نماید.) و صلح، بهتر است؛ اگر چه مردم (طبق غریزه حبّ ذات، در این گونه موارد) بخل می‌ورزند. و اگر نیکی کنید و پرهیزگاری پیشه سازید (و بخاطر صلح، گذشت نمایید)، خداوند به آنچه انجام می‌دهید، آگاه است (و پاداش شایسته به شما خواهد داد).'),
(622, 622, 'وَلَنْ تَسْتَطِيعُوا أَنْ تَعْدِلُوا بَيْنَ النِّسَاءِ وَلَوْ حَرَصْتُمْ ۖ فَلَا تَمِيلُوا كُلَّ الْمَيْلِ فَتَذَرُوهَا كَالْمُعَلَّقَةِ ۚ وَإِنْ تُصْلِحُوا وَتَتَّقُوا فَإِنَّ اللَّهَ كَانَ غَفُورًا رَحِيمًا', 4, 129, 5, 20, 99, 'شما هرگز نمی‌توانید (از نظر محّبت قلبی) در میان زنان، عدالت برقرار کنید، هر چند کوشش نمایید! ولی تمایل خود را بکلّی متوجّه یک طرف نسازید که دیگری را بصورت زنی که شوهرش را از دست داده درآورید! و اگر راه صلاح و پرهیزگاری پیش گیرید، خداوند آمرزنده و مهربان است.'),
(623, 623, 'وَإِنْ يَتَفَرَّقَا يُغْنِ اللَّهُ كُلًّا مِنْ سَعَتِهِ ۚ وَكَانَ اللَّهُ وَاسِعًا حَكِيمًا', 4, 130, 5, 20, 99, '(اما) اگر (راهی برای اصلاح در میان خود نیابند، و) از هم جدا شوند، خداوند هر کدام از آنها را با فضل و کرم خود، بی‌نیاز می‌کند؛ و خداوند، دارای فضل و کرم، و حکیم است.'),
(624, 624, 'وَلِلَّهِ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ ۗ وَلَقَدْ وَصَّيْنَا الَّذِينَ أُوتُوا الْكِتَابَ مِنْ قَبْلِكُمْ وَإِيَّاكُمْ أَنِ اتَّقُوا اللَّهَ ۚ وَإِنْ تَكْفُرُوا فَإِنَّ لِلَّهِ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ ۚ وَكَانَ اللَّهُ غَنِيًّا حَمِيدًا', 4, 131, 5, 20, 99, 'آنچه در آسمانها و آنچه در زمین است، از آن خداست. و ما به کسانی که پیش از شما، کتاب آسمانی به آنها داده شده بود، سفارش کردیم، (همچنین) به شما (نیز) سفارش می‌کنیم که از (نافرمانی) خدا بپرهیزید! و اگر کافر شوید، (به خدا زیانی نمی‌رسد؛ زیرا) برای خداست آنچه در آسمانها و آنچه در زمین است، و خداوند، بی‌نیاز و ستوده است.'),
(625, 625, 'وَلِلَّهِ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ ۚ وَكَفَىٰ بِاللَّهِ وَكِيلًا', 4, 132, 5, 20, 99, 'برای خداست آنچه در آسمانها و زمین است؛ و کافی است که خدا، حافظ و نگاهبان آنها باشد.'),
(626, 626, 'إِنْ يَشَأْ يُذْهِبْكُمْ أَيُّهَا النَّاسُ وَيَأْتِ بِآخَرِينَ ۚ وَكَانَ اللَّهُ عَلَىٰ ذَٰلِكَ قَدِيرًا', 4, 133, 5, 20, 99, 'ای مردم! اگر او بخواهد، شما را از میان می‌برد و افراد دیگری را (به جای شما) می آورد، و خداوند، بر این کار تواناست.'),
(627, 627, 'مَنْ كَانَ يُرِيدُ ثَوَابَ الدُّنْيَا فَعِنْدَ اللَّهِ ثَوَابُ الدُّنْيَا وَالْآخِرَةِ ۚ وَكَانَ اللَّهُ سَمِيعًا بَصِيرًا', 4, 134, 5, 20, 99, 'کسانی که پاداش دنیوی بخواهند، (و در قید نتایج معنوی و اخروی نباشند، در اشتباهند؛ زیرا) پاداش دنیا و آخرت نزد خداست؛ و خداوند، شنوا و بیناست.'),
(628, 628, ' يَا أَيُّهَا الَّذِينَ آمَنُوا كُونُوا قَوَّامِينَ بِالْقِسْطِ شُهَدَاءَ لِلَّهِ وَلَوْ عَلَىٰ أَنْفُسِكُمْ أَوِ الْوَالِدَيْنِ وَالْأَقْرَبِينَ ۚ إِنْ يَكُنْ غَنِيًّا أَوْ فَقِيرًا فَاللَّهُ أَوْلَىٰ بِهِمَا ۖ فَلَا تَتَّبِعُوا الْهَوَىٰ أَنْ تَعْدِلُوا ۚ وَإِنْ تَلْوُوا أَوْ تُعْرِضُوا فَإِنَّ اللَّهَ كَانَ بِمَا تَعْمَلُونَ خَبِيرًا', 4, 135, 5, 20, 100, 'ای کسانی که ایمان آورده‌اید! کاملاً قیام به عدالت کنید! برای خدا شهادت دهید، اگر چه (این گواهی) به زیان خود شما، یا پدر و مادر و نزدیکان شما بوده باشد! (چرا که) اگر آنها غنیّ یا فقیر باشند، خداوند سزاوارتر است که از آنان حمایت کند. بنابراین، از هوی و هوس پیروی نکنید؛ که از حق، منحرف خواهید شد! و اگر حق را تحریف کنید، و یا از اظهار آن، اعراض نمایید، خداوند به آنچه انجام می‌دهید، آگاه است.'),
(629, 629, 'يَا أَيُّهَا الَّذِينَ آمَنُوا آمِنُوا بِاللَّهِ وَرَسُولِهِ وَالْكِتَابِ الَّذِي نَزَّلَ عَلَىٰ رَسُولِهِ وَالْكِتَابِ الَّذِي أَنْزَلَ مِنْ قَبْلُ ۚ وَمَنْ يَكْفُرْ بِاللَّهِ وَمَلَائِكَتِهِ وَكُتُبِهِ وَرُسُلِهِ وَالْيَوْمِ الْآخِرِ فَقَدْ ضَلَّ ضَلَالًا بَعِيدًا', 4, 136, 5, 20, 100, 'ای کسانی که ایمان آورده‌اید! به خدا و پیامبرش، و کتابی که بر او نازل کرده، و کتب (آسمانی) که پیش از این فرستاده است، ایمان (واقعی) بیاورید کسی که خدا و فرشتگان او و کتابها و پیامبرانش و روز واپسین را انکار کند، در گمراهی دور و درازی افتاده است.'),
(630, 630, 'إِنَّ الَّذِينَ آمَنُوا ثُمَّ كَفَرُوا ثُمَّ آمَنُوا ثُمَّ كَفَرُوا ثُمَّ ازْدَادُوا كُفْرًا لَمْ يَكُنِ اللَّهُ لِيَغْفِرَ لَهُمْ وَلَا لِيَهْدِيَهُمْ سَبِيلًا', 4, 137, 5, 20, 100, 'کسانی که ایمان آوردند، سپس کافر شدند، باز هم ایمان آوردند، و دیگربار کافر شدند، سپس بر کفر خود افزودند، خدا هرگز آنها را نخواهد بخشید، و آنها را به راه (راست) هدایت نخواهد کرد.'),
(631, 631, 'بَشِّرِ الْمُنَافِقِينَ بِأَنَّ لَهُمْ عَذَابًا أَلِيمًا', 4, 138, 5, 20, 100, 'به منافقان بشارت ده که مجازات دردناکی در انتظار آنهاست!'),
(632, 632, 'الَّذِينَ يَتَّخِذُونَ الْكَافِرِينَ أَوْلِيَاءَ مِنْ دُونِ الْمُؤْمِنِينَ ۚ أَيَبْتَغُونَ عِنْدَهُمُ الْعِزَّةَ فَإِنَّ الْعِزَّةَ لِلَّهِ جَمِيعًا', 4, 139, 5, 20, 100, 'همانها که کافران را به جای مؤمنان، دوست خود انتخاب می‌کنند. آیا عزّت و آبرو نزد آنان می‌جویند؟ با اینکه همه عزّتها از آن خداست؟!'),
(633, 633, 'وَقَدْ نَزَّلَ عَلَيْكُمْ فِي الْكِتَابِ أَنْ إِذَا سَمِعْتُمْ آيَاتِ اللَّهِ يُكْفَرُ بِهَا وَيُسْتَهْزَأُ بِهَا فَلَا تَقْعُدُوا مَعَهُمْ حَتَّىٰ يَخُوضُوا فِي حَدِيثٍ غَيْرِهِ ۚ إِنَّكُمْ إِذًا مِثْلُهُمْ ۗ إِنَّ اللَّهَ جَامِعُ الْمُنَافِقِينَ وَالْكَافِرِينَ فِي جَهَنَّمَ جَمِيعًا', 4, 140, 5, 20, 100, 'و خداوند (این حکم را) در قرآن بر شما نازل کرده که هرگاه بشنوید افرادی آیات خدا را انکار و استهزا می‌کنند، با آنها ننشینید تا به سخن دیگری بپردازند! وگرنه، شما هم مثل آنان خواهید بود. خداوند، منافقان و کافران را همگی در دوزخ جمع می‌کند.'),
(634, 634, 'الَّذِينَ يَتَرَبَّصُونَ بِكُمْ فَإِنْ كَانَ لَكُمْ فَتْحٌ مِنَ اللَّهِ قَالُوا أَلَمْ نَكُنْ مَعَكُمْ وَإِنْ كَانَ لِلْكَافِرِينَ نَصِيبٌ قَالُوا أَلَمْ نَسْتَحْوِذْ عَلَيْكُمْ وَنَمْنَعْكُمْ مِنَ الْمُؤْمِنِينَ ۚ فَاللَّهُ يَحْكُمُ بَيْنَكُمْ يَوْمَ الْقِيَامَةِ ۗ وَلَنْ يَجْعَلَ اللَّهُ لِلْكَافِرِينَ عَلَى الْمُؤْمِنِينَ سَبِيلًا', 4, 141, 5, 20, 101, 'منافقان همانها هستند که پیوسته انتظار می‌کشند و مراقب شما هستند؛ اگر فتح و پیروزی نصیب شما گردد، می‌گویند: مگر ما با شما نبودیم؟ (پس ما نیز در افتخارات و غنایم شریکیم!) «و اگر بهره‌ای نصیب کافران گردد، به آنان می‌گویند: مگر ما شما را به مبارزه و عدم تسلیم در برابر مؤمنان، تشویق نمی‌کردیم؟ (پس با شما شریک خواهیم بود!)» خداوند در روز رستاخیز، میان شما داوری می‌کند؛ و خداوند هرگز کافران را بر مؤمنان تسلّطی نداده است.'),
(635, 635, 'إِنَّ الْمُنَافِقِينَ يُخَادِعُونَ اللَّهَ وَهُوَ خَادِعُهُمْ وَإِذَا قَامُوا إِلَى الصَّلَاةِ قَامُوا كُسَالَىٰ يُرَاءُونَ النَّاسَ وَلَا يَذْكُرُونَ اللَّهَ إِلَّا قَلِيلًا', 4, 142, 5, 20, 101, 'منافقان می‌خواهند خدا را فریب دهند؛ در حالی که او آنها را فریب می‌دهد؛ و هنگامی که به نماز برمی‌خیزند، با کسالت برمی‌خیزند؛ و در برابر مردم ریا می‌کنند؛ و خدا را جز اندکی یاد نمی‌نمایند!'),
(636, 636, 'مُذَبْذَبِينَ بَيْنَ ذَٰلِكَ لَا إِلَىٰ هَٰؤُلَاءِ وَلَا إِلَىٰ هَٰؤُلَاءِ ۚ وَمَنْ يُضْلِلِ اللَّهُ فَلَنْ تَجِدَ لَهُ سَبِيلًا', 4, 143, 5, 20, 101, 'آنها افراد بی‌هدفی هستند که نه سوی اینها، و نه سوی آنهایند! (نه در صف مؤمنان قرار دارند، و نه در صف کافران!) و هر کس را خداوند گمراه کند، راهی برای او نخواهی یافت.'),
(637, 637, 'يَا أَيُّهَا الَّذِينَ آمَنُوا لَا تَتَّخِذُوا الْكَافِرِينَ أَوْلِيَاءَ مِنْ دُونِ الْمُؤْمِنِينَ ۚ أَتُرِيدُونَ أَنْ تَجْعَلُوا لِلَّهِ عَلَيْكُمْ سُلْطَانًا مُبِينًا', 4, 144, 5, 20, 101, 'ای کسانی که ایمان آورده‌اید! غیر از مؤمنان، کافران را ولّی و تکیه‌گاه خود قرار ندهید! آیا می‌خواهید (با این عمل،) دلیل آشکاری بر ضدّ خود در پیشگاه خدا قرار دهید؟!'),
(638, 638, 'إِنَّ الْمُنَافِقِينَ فِي الدَّرْكِ الْأَسْفَلِ مِنَ النَّارِ وَلَنْ تَجِدَ لَهُمْ نَصِيرًا', 4, 145, 5, 20, 101, 'منافقان در پایین‌ترین درکات دوزخ قرار دارند؛ و هرگز یاوری برای آنها نخواهی یافت! (بنابر این، از طرح دوستی با دشمنان خدا، که نشانه نفاق است، بپرهیزید!)'),
(639, 639, 'إِلَّا الَّذِينَ تَابُوا وَأَصْلَحُوا وَاعْتَصَمُوا بِاللَّهِ وَأَخْلَصُوا دِينَهُمْ لِلَّهِ فَأُولَٰئِكَ مَعَ الْمُؤْمِنِينَ ۖ وَسَوْفَ يُؤْتِ اللَّهُ الْمُؤْمِنِينَ أَجْرًا عَظِيمًا', 4, 146, 5, 20, 101, 'مگر آنها که توبه کنند، و جبران و اصلاح نمایند، به (دامن لطف) خدا، چنگ زنند، و دین خود را برای خدا خالص کنند؛ آنها با مؤمنان خواهند بود؛ و خداوند به افراد باایمان، پاداش عظیمی خواهد داد.'),
(640, 640, 'مَا يَفْعَلُ اللَّهُ بِعَذَابِكُمْ إِنْ شَكَرْتُمْ وَآمَنْتُمْ ۚ وَكَانَ اللَّهُ شَاكِرًا عَلِيمًا', 4, 147, 5, 20, 101, 'خدا چه نیازی به مجازات شما دارد اگر شکرگزاری کنید و ایمان آورید؟ خدا شکرگزار و آگاه است. (اعمال و نیّات شما را می‌داند، و به اعمال نیک، پاداش نیک می دهد.)'),
(641, 641, ' لَا يُحِبُّ اللَّهُ الْجَهْرَ بِالسُّوءِ مِنَ الْقَوْلِ إِلَّا مَنْ ظُلِمَ ۚ وَكَانَ اللَّهُ سَمِيعًا عَلِيمًا', 4, 148, 6, 21, 102, 'خداوند دوست ندارد کسی با سخنان خود، بدیها (ی دیگران) را اظهار کند؛ مگر آن کس که مورد ستم واقع شده باشد. خداوند، شنوا و داناست.'),
(642, 642, 'إِنْ تُبْدُوا خَيْرًا أَوْ تُخْفُوهُ أَوْ تَعْفُوا عَنْ سُوءٍ فَإِنَّ اللَّهَ كَانَ عَفُوًّا قَدِيرًا', 4, 149, 6, 21, 102, 'اگر نیکیها را آشکار یا مخفی سازید، و از بدیها گذشت نمایید، خداوند بخشنده و تواناست (و با اینکه قادر بر انتقام است، عفو و گذشت می‌کند).'),
(643, 643, 'إِنَّ الَّذِينَ يَكْفُرُونَ بِاللَّهِ وَرُسُلِهِ وَيُرِيدُونَ أَنْ يُفَرِّقُوا بَيْنَ اللَّهِ وَرُسُلِهِ وَيَقُولُونَ نُؤْمِنُ بِبَعْضٍ وَنَكْفُرُ بِبَعْضٍ وَيُرِيدُونَ أَنْ يَتَّخِذُوا بَيْنَ ذَٰلِكَ سَبِيلًا', 4, 150, 6, 21, 102, 'کسانی که خدا و پیامبرانِ او را انکار می‌کنند، و می‌خواهند میان خدا و پیامبرانش تبعیض قائل شوند، و می‌گویند: «به بعضی ایمان می‌آوریم، و بعضی را انکار می کنیم» و می‌خواهند در میان این دو، راهی برای خود انتخاب کنند...'),
(644, 644, 'أُولَٰئِكَ هُمُ الْكَافِرُونَ حَقًّا ۚ وَأَعْتَدْنَا لِلْكَافِرِينَ عَذَابًا مُهِينًا', 4, 151, 6, 21, 102, 'آنها کافران حقیقی‌اند؛ و برای کافران، مجازات خوارکننده‌ای فراهم ساخته‌ایم.'),
(645, 645, 'وَالَّذِينَ آمَنُوا بِاللَّهِ وَرُسُلِهِ وَلَمْ يُفَرِّقُوا بَيْنَ أَحَدٍ مِنْهُمْ أُولَٰئِكَ سَوْفَ يُؤْتِيهِمْ أُجُورَهُمْ ۗ وَكَانَ اللَّهُ غَفُورًا رَحِيمًا', 4, 152, 6, 21, 102, '(ولی) کسانی که به خدا و رسولان او ایمان آورده، و میان احدی از آنها فرق نمی‌گذارند، پاداششان را خواهد داد؛ خداوند، آمرزنده و مهربان است.'),
(646, 646, 'يَسْأَلُكَ أَهْلُ الْكِتَابِ أَنْ تُنَزِّلَ عَلَيْهِمْ كِتَابًا مِنَ السَّمَاءِ ۚ فَقَدْ سَأَلُوا مُوسَىٰ أَكْبَرَ مِنْ ذَٰلِكَ فَقَالُوا أَرِنَا اللَّهَ جَهْرَةً فَأَخَذَتْهُمُ الصَّاعِقَةُ بِظُلْمِهِمْ ۚ ثُمَّ اتَّخَذُوا الْعِجْلَ مِنْ بَعْدِ مَا جَاءَتْهُمُ الْبَيِّنَاتُ فَعَفَوْنَا عَنْ ذَٰلِكَ ۚ وَآتَيْنَا مُوسَىٰ سُلْطَانًا مُبِينًا', 4, 153, 6, 21, 102, 'اهل کتاب از تو می‌خواهند کتابی از آسمان (یکجا) بر آنها نازل کنی؛ (در حالی که این یک بهانه است؛) آنها از موسی، بزرگتر از این را خواستند، و گفتند: «خدا را آشکارا به ما نشان ده!» و بخاطر این ظلم و ستم، صاعقه آنها را فرا گرفت. سپس گوساله (سامری) را، پس از آن همه دلایل روشن که برای آنها آمد، (به خدایی) انتخاب کردند، ولی ما از آن درگذشتیم (و عفو کردیم) و به موسی، برهان آشکاری دادیم.'),
(647, 647, 'وَرَفَعْنَا فَوْقَهُمُ الطُّورَ بِمِيثَاقِهِمْ وَقُلْنَا لَهُمُ ادْخُلُوا الْبَابَ سُجَّدًا وَقُلْنَا لَهُمْ لَا تَعْدُوا فِي السَّبْتِ وَأَخَذْنَا مِنْهُمْ مِيثَاقًا غَلِيظًا', 4, 154, 6, 21, 102, 'و کوه طور را بر فراز آنها برافراشتیم؛ و در همان حال از آنها پیمان گرفتیم، و به آنها گفتیم: «(برای توبه،) از در (بیت المقدس) با خضوع درآیید!» و (نیز) گفتیم: «روز شنبه تعدّی نکنید (و دست از کار بکشید!)» و از آنان (در برابر همه اینها،) پیمان محکمی گرفتیم.'),
(648, 648, 'فَبِمَا نَقْضِهِمْ مِيثَاقَهُمْ وَكُفْرِهِمْ بِآيَاتِ اللَّهِ وَقَتْلِهِمُ الْأَنْبِيَاءَ بِغَيْرِ حَقٍّ وَقَوْلِهِمْ قُلُوبُنَا غُلْفٌ ۚ بَلْ طَبَعَ اللَّهُ عَلَيْهَا بِكُفْرِهِمْ فَلَا يُؤْمِنُونَ إِلَّا قَلِيلًا', 4, 155, 6, 21, 103, '(ولی) بخاطر پیمان‌شکنی آنها، و انکار آیات خدا، و کشتن پیامبران بناحق، و بخاطر اینکه (از روی استهزا) می‌گفتند: «بر دلهای ما، پرده افکنده (شده و سخنان پیامبر را درک نمی‌کنیم!» رانده درگاه خدا شدند.) آری، خداوند بعلّت کفرشان، بر دلهای آنها مهر زده؛ که جز عده کمی (که راه حق می‌پویند و لجاج ندارند،) ایمان نمی‌آورند.'),
(649, 649, 'وَبِكُفْرِهِمْ وَقَوْلِهِمْ عَلَىٰ مَرْيَمَ بُهْتَانًا عَظِيمًا', 4, 156, 6, 21, 103, 'و (نیز) بخاطر کفرشان، و تهمت بزرگی که بر مریم زدند.'),
(650, 650, 'وَقَوْلِهِمْ إِنَّا قَتَلْنَا الْمَسِيحَ عِيسَى ابْنَ مَرْيَمَ رَسُولَ اللَّهِ وَمَا قَتَلُوهُ وَمَا صَلَبُوهُ وَلَٰكِنْ شُبِّهَ لَهُمْ ۚ وَإِنَّ الَّذِينَ اخْتَلَفُوا فِيهِ لَفِي شَكٍّ مِنْهُ ۚ مَا لَهُمْ بِهِ مِنْ عِلْمٍ إِلَّا اتِّبَاعَ الظَّنِّ ۚ وَمَا قَتَلُوهُ يَقِينًا', 4, 157, 6, 21, 103, 'و گفتارشان که: «ما، مسیح عیسی بن مریم، پیامبر خدا را کشتیم!» در حالی که نه او را کشتند، و نه بر دار آویختند؛ لکن امر بر آنها مشتبه شد. و کسانی که در مورد (قتل) او اختلاف کردند، از آن در شک هستند و علم به آن ندارند و تنها از گمان پیروی می‌کنند؛ و قطعاً او را نکشتند!'),
(651, 651, 'بَلْ رَفَعَهُ اللَّهُ إِلَيْهِ ۚ وَكَانَ اللَّهُ عَزِيزًا حَكِيمًا', 4, 158, 6, 21, 103, 'بلکه خدا او را به سوی خود، بالا برد. و خداوند، توانا و حکیم است.'),
(652, 652, 'وَإِنْ مِنْ أَهْلِ الْكِتَابِ إِلَّا لَيُؤْمِنَنَّ بِهِ قَبْلَ مَوْتِهِ ۖ وَيَوْمَ الْقِيَامَةِ يَكُونُ عَلَيْهِمْ شَهِيدًا', 4, 159, 6, 21, 103, 'و هیچ یک از اهل کتاب نیست مگر اینکه پیش از مرگش به او = حضرت مسیح‌ ایمان می‌آورد؛ و روز قیامت، بر آنها گواه خواهد بود.'),
(653, 653, 'فَبِظُلْمٍ مِنَ الَّذِينَ هَادُوا حَرَّمْنَا عَلَيْهِمْ طَيِّبَاتٍ أُحِلَّتْ لَهُمْ وَبِصَدِّهِمْ عَنْ سَبِيلِ اللَّهِ كَثِيرًا', 4, 160, 6, 21, 103, 'بخاطر ظلمی که از یهود صادر شد، و (نیز) بخاطر جلوگیری بسیار آنها از راه خدا، بخشی از چیزهای پاکیزه را که بر آنها حلال بود، حرام کردیم.'),
(654, 654, 'وَأَخْذِهِمُ الرِّبَا وَقَدْ نُهُوا عَنْهُ وَأَكْلِهِمْ أَمْوَالَ النَّاسِ بِالْبَاطِلِ ۚ وَأَعْتَدْنَا لِلْكَافِرِينَ مِنْهُمْ عَذَابًا أَلِيمًا', 4, 161, 6, 21, 103, 'و (همچنین) بخاطر ربا گرفتن، در حالی که از آن نهی شده بودند؛ و خوردن اموال مردم بباطل؛ و برای کافران آنها، عذاب دردناکی آماده کرده‌ایم.'),
(655, 655, 'لَٰكِنِ الرَّاسِخُونَ فِي الْعِلْمِ مِنْهُمْ وَالْمُؤْمِنُونَ يُؤْمِنُونَ بِمَا أُنْزِلَ إِلَيْكَ وَمَا أُنْزِلَ مِنْ قَبْلِكَ ۚ وَالْمُقِيمِينَ الصَّلَاةَ ۚ وَالْمُؤْتُونَ الزَّكَاةَ وَالْمُؤْمِنُونَ بِاللَّهِ وَالْيَوْمِ الْآخِرِ أُولَٰئِكَ سَنُؤْتِيهِمْ أَجْرًا عَظِيمًا', 4, 162, 6, 21, 103, 'ولی راسخانِ در علم از آنها، و مؤمنان (از امّت اسلام،) به تمام آنچه بر تو نازل شده، و آنچه پیش از تو نازل گردیده، ایمان می‌آورند. (همچنین) نمازگزاران و زکات‌دهندگان و ایمان‌آورندگان به خدا و روز قیامت، بزودی به همه آنان پاداش عظیمی خواهیم داد.'),
(656, 656, ' إِنَّا أَوْحَيْنَا إِلَيْكَ كَمَا أَوْحَيْنَا إِلَىٰ نُوحٍ وَالنَّبِيِّينَ مِنْ بَعْدِهِ ۚ وَأَوْحَيْنَا إِلَىٰ إِبْرَاهِيمَ وَإِسْمَاعِيلَ وَإِسْحَاقَ وَيَعْقُوبَ وَالْأَسْبَاطِ وَعِيسَىٰ وَأَيُّوبَ وَيُونُسَ وَهَارُونَ وَسُلَيْمَانَ ۚ وَآتَيْنَا دَاوُودَ زَبُورًا', 4, 163, 6, 21, 104, 'ما به تو وحی فرستادیم؛ همان گونه که به نوح و پیامبران بعد از او وحی فرستادیم؛ و (نیز) به ابراهیم و اسماعیل و اسحاق و یعقوب و اسباط = بنی اسرائیل‌ و عیسی و ایّوب و یونس و هارون و سلیمان وحی نمودیم؛ و به داوود زبور دادیم.'),
(657, 657, 'وَرُسُلًا قَدْ قَصَصْنَاهُمْ عَلَيْكَ مِنْ قَبْلُ وَرُسُلًا لَمْ نَقْصُصْهُمْ عَلَيْكَ ۚ وَكَلَّمَ اللَّهُ مُوسَىٰ تَكْلِيمًا', 4, 164, 6, 21, 104, 'و پیامبرانی که سرگذشت آنها را پیش از این، برای تو باز گفته‌ایم؛ و پیامبرانی که سرگذشت آنها را بیان نکرده‌ایم؛ و خداوند با موسی سخن گفت. (و این امتیاز، از آن او بود.)'),
(658, 658, 'رُسُلًا مُبَشِّرِينَ وَمُنْذِرِينَ لِئَلَّا يَكُونَ لِلنَّاسِ عَلَى اللَّهِ حُجَّةٌ بَعْدَ الرُّسُلِ ۚ وَكَانَ اللَّهُ عَزِيزًا حَكِيمًا', 4, 165, 6, 21, 104, 'پیامبرانی که بشارت‌دهنده و بیم‌دهنده بودند، تا بعد از این پیامبران، حجتی برای مردم بر خدا باقی نماند، (و بر همه اتمام حجت شود؛) و خداوند، توانا و حکیم است.'),
(659, 659, 'لَٰكِنِ اللَّهُ يَشْهَدُ بِمَا أَنْزَلَ إِلَيْكَ ۖ أَنْزَلَهُ بِعِلْمِهِ ۖ وَالْمَلَائِكَةُ يَشْهَدُونَ ۚ وَكَفَىٰ بِاللَّهِ شَهِيدًا', 4, 166, 6, 21, 104, 'ولی خداوند گواهی می‌دهد به آنچه بر تو نازل کرده؛ که از روی علمش نازل کرده است، و فرشتگان (نیز) گواهی می‌دهند؛ هر چند گواهی خدا کافی است.'),
(660, 660, 'إِنَّ الَّذِينَ كَفَرُوا وَصَدُّوا عَنْ سَبِيلِ اللَّهِ قَدْ ضَلُّوا ضَلَالًا بَعِيدًا', 4, 167, 6, 21, 104, 'کسانی که کافر شدند، و (مردم را) از راه خدا بازداشتند، در گمراهی دوری گرفتار شده‌اند.'),
(661, 661, 'إِنَّ الَّذِينَ كَفَرُوا وَظَلَمُوا لَمْ يَكُنِ اللَّهُ لِيَغْفِرَ لَهُمْ وَلَا لِيَهْدِيَهُمْ طَرِيقًا', 4, 168, 6, 21, 104, 'کسانی که کافر شدند، و (به خود و دیگران) ستم کردند، هرگز خدا آنها را نخواهد بخشید، و آنان را به هیچ راهی هدایت نخواهد کرد،'),
(662, 662, 'إِلَّا طَرِيقَ جَهَنَّمَ خَالِدِينَ فِيهَا أَبَدًا ۚ وَكَانَ ذَٰلِكَ عَلَى اللَّهِ يَسِيرًا', 4, 169, 6, 21, 104, 'مگر به راه دوزخ! که جاودانه در آن خواهند ماند؛ و این کار برای خدا آسان است!'),
(663, 663, 'يَا أَيُّهَا النَّاسُ قَدْ جَاءَكُمُ الرَّسُولُ بِالْحَقِّ مِنْ رَبِّكُمْ فَآمِنُوا خَيْرًا لَكُمْ ۚ وَإِنْ تَكْفُرُوا فَإِنَّ لِلَّهِ مَا فِي السَّمَاوَاتِ وَالْأَرْضِ ۚ وَكَانَ اللَّهُ عَلِيمًا حَكِيمًا', 4, 170, 6, 21, 104, 'ای مردم! پیامبر (ی که انتظارش را می‌کشیدید،) حق را از جانب پروردگارتان آورد؛ به او ایمان بیاورید که برای شما بهتر است! و اگر کافر شوید، (به خدا زیانی نمی رسد، زیرا) آنچه در آسمانها و زمین است از آن خداست، و خداوند، دانا و حکیم است.'),
(664, 664, 'يَا أَهْلَ الْكِتَابِ لَا تَغْلُوا فِي دِينِكُمْ وَلَا تَقُولُوا عَلَى اللَّهِ إِلَّا الْحَقَّ ۚ إِنَّمَا الْمَسِيحُ عِيسَى ابْنُ مَرْيَمَ رَسُولُ اللَّهِ وَكَلِمَتُهُ أَلْقَاهَا إِلَىٰ مَرْيَمَ وَرُوحٌ مِنْهُ ۖ فَآمِنُوا بِاللَّهِ وَرُسُلِهِ ۖ وَلَا تَقُولُوا ثَلَاثَةٌ ۚ انْتَهُوا خَيْرًا لَكُمْ ۚ إِنَّمَا اللَّهُ إِلَٰهٌ وَاحِدٌ ۖ سُبْحَانَهُ أَنْ يَكُونَ لَهُ وَلَدٌ ۘ لَهُ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ ۗ وَكَفَىٰ بِاللَّهِ وَكِيلًا', 4, 171, 6, 21, 105, 'ای اهل کتاب! در دین خود، غلوّ (و زیاده روی) نکنید! و در باره خدا، غیر از حق نگویید! مسیح عیسی بن مریم فقط فرستاده خدا، و کلمه (و مخلوق) اوست، که او را به مریم القا نمود؛ و روحی (شایسته) از طرف او بود. بنابر این، به خدا و پیامبران او، ایمان بیاورید! و نگویید: «(خداوند) سه‌گانه است!» (از این سخن) خودداری کنید که برای شما بهتر است! خدا، تنها معبود یگانه است؛ او منزه است که فرزندی داشته باشد؛ (بلکه) از آن اوست آنچه در آسمانها و در زمین است؛ و برای تدبیر و سرپرستی آنها، خداوند کافی است.'),
(665, 665, 'لَنْ يَسْتَنْكِفَ الْمَسِيحُ أَنْ يَكُونَ عَبْدًا لِلَّهِ وَلَا الْمَلَائِكَةُ الْمُقَرَّبُونَ ۚ وَمَنْ يَسْتَنْكِفْ عَنْ عِبَادَتِهِ وَيَسْتَكْبِرْ فَسَيَحْشُرُهُمْ إِلَيْهِ جَمِيعًا', 4, 172, 6, 21, 105, 'هرگز مسیح از این ابا نداشت که بنده خدا باشد؛ و نه فرشتگان مقرّب او (از این ابا دارند). و آنها که از عبودیّت و بندگی او، روی برتابند و تکبّر کنند، بزودی همه آنها را (در قیامت) نزد خود جمع خواهد کرد.'),
(666, 666, 'فَأَمَّا الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ فَيُوَفِّيهِمْ أُجُورَهُمْ وَيَزِيدُهُمْ مِنْ فَضْلِهِ ۖ وَأَمَّا الَّذِينَ اسْتَنْكَفُوا وَاسْتَكْبَرُوا فَيُعَذِّبُهُمْ عَذَابًا أَلِيمًا وَلَا يَجِدُونَ لَهُمْ مِنْ دُونِ اللَّهِ وَلِيًّا وَلَا نَصِيرًا', 4, 173, 6, 21, 105, 'امّا آنها که ایمان آوردند و اعمال صالح انجام دادند، پاداششان را بطور کامل خواهد داد؛ و از فضل و بخشش خود، بر آنها خواهد افزود. و آنها را که ابا کردند و تکبّر ورزیدند، مجازات دردناکی خواهد کرد؛ و برای خود، غیر از خدا، سرپرست و یاوری نخواهند یافت.'),
(667, 667, 'يَا أَيُّهَا النَّاسُ قَدْ جَاءَكُمْ بُرْهَانٌ مِنْ رَبِّكُمْ وَأَنْزَلْنَا إِلَيْكُمْ نُورًا مُبِينًا', 4, 174, 6, 21, 105, 'ای مردم! دلیل روشن از طرف پروردگارتان برای شما آمد؛ و نور آشکاری به سوی شما نازل کردیم.'),
(668, 668, 'فَأَمَّا الَّذِينَ آمَنُوا بِاللَّهِ وَاعْتَصَمُوا بِهِ فَسَيُدْخِلُهُمْ فِي رَحْمَةٍ مِنْهُ وَفَضْلٍ وَيَهْدِيهِمْ إِلَيْهِ صِرَاطًا مُسْتَقِيمًا', 4, 175, 6, 21, 105, 'امّا آنها که به خدا ایمان آوردند و به (آن کتاب آسمانی) چنگ زدند، بزودی همه را در رحمت و فضل خود، وارد خواهد ساخت؛ و در راه راستی، به سوی خودش هدایت می‌کند.'),
(669, 669, 'يَسْتَفْتُونَكَ قُلِ اللَّهُ يُفْتِيكُمْ فِي الْكَلَالَةِ ۚ إِنِ امْرُؤٌ هَلَكَ لَيْسَ لَهُ وَلَدٌ وَلَهُ أُخْتٌ فَلَهَا نِصْفُ مَا تَرَكَ ۚ وَهُوَ يَرِثُهَا إِنْ لَمْ يَكُنْ لَهَا وَلَدٌ ۚ فَإِنْ كَانَتَا اثْنَتَيْنِ فَلَهُمَا الثُّلُثَانِ مِمَّا تَرَكَ ۚ وَإِنْ كَانُوا إِخْوَةً رِجَالًا وَنِسَاءً فَلِلذَّكَرِ مِثْلُ حَظِّ الْأُنْثَيَيْنِ ۗ يُبَيِّنُ اللَّهُ لَكُمْ أَنْ تَضِلُّوا ۗ وَاللَّهُ بِكُلِّ شَيْءٍ عَلِيمٌ', 4, 176, 6, 21, 106, 'از تو (در باره ارث خواهران و برادران) سؤال می‌کنند، بگو: «خداوند، حکم کلاله (خواهر و برادر) را برای شما بیان می‌کند: اگر مردی از دنیا برود، که فرزند نداشته باشد، و برای او خواهری باشد، نصف اموالی را که به جا گذاشته، از او (ارث) می‌برد؛ و (اگر خواهری از دنیا برود، وارث او یک برادر باشد،) او تمام مال را از آن خواهر به ارث می‌برد، در صورتی که (میّت) فرزند نداشته باشد؛ و اگر دو خواهر (از او) باقی باشند دو سوم اموال را می‌برند؛ و اگر برادران و خواهران با هم باشند، (تمام اموال را میان خود تقسیم می‌کنند؛ و) برای هر مذکّر، دو برابر سهم مؤنّث است. خداوند (احکام خود را) برای شما بیان می‌کند تا گمراه نشوید؛ و خداوند به همه چیز داناست.»'),
(670, 670, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ يَا أَيُّهَا الَّذِينَ آمَنُوا أَوْفُوا بِالْعُقُودِ ۚ أُحِلَّتْ لَكُمْ بَهِيمَةُ الْأَنْعَامِ إِلَّا مَا يُتْلَىٰ عَلَيْكُمْ غَيْرَ مُحِلِّي الصَّيْدِ وَأَنْتُمْ حُرُمٌ ۗ إِنَّ اللَّهَ يَحْكُمُ مَا يُرِيدُ', 5, 1, 6, 22, 106, 'ای کسانی که ایمان آورده‌اید! به پیمانها (و قراردادها) وفا کنید! چهارپایان (و جنین آنها) برای شما حلال شده است؛ مگر آنچه بر شما خوانده می‌شود (و استثنا خواهد شد). و به هنگام احرام، صید را حلال نشمرید! خداوند هر چه بخواهد (و مصلحت باشد) حکم می‌کند.'),
(671, 671, 'يَا أَيُّهَا الَّذِينَ آمَنُوا لَا تُحِلُّوا شَعَائِرَ اللَّهِ وَلَا الشَّهْرَ الْحَرَامَ وَلَا الْهَدْيَ وَلَا الْقَلَائِدَ وَلَا آمِّينَ الْبَيْتَ الْحَرَامَ يَبْتَغُونَ فَضْلًا مِنْ رَبِّهِمْ وَرِضْوَانًا ۚ وَإِذَا حَلَلْتُمْ فَاصْطَادُوا ۚ وَلَا يَجْرِمَنَّكُمْ شَنَآنُ قَوْمٍ أَنْ صَدُّوكُمْ عَنِ الْمَسْجِدِ الْحَرَامِ أَنْ تَعْتَدُوا ۘ وَتَعَاوَنُوا عَلَى الْبِرِّ وَالتَّقْوَىٰ ۖ وَلَا تَعَاوَنُوا عَلَى الْإِثْمِ وَالْعُدْوَانِ ۚ وَاتَّقُوا اللَّهَ ۖ إِنَّ اللَّهَ شَدِيدُ الْعِقَابِ', 5, 2, 6, 22, 106, 'ای کسانی که ایمان آورده‌اید! شعائر و حدود الهی (و مراسم حج را محترم بشمرید! و مخالفت با آنها) را حلال ندانید! و نه ماه حرام را، و نه قربانیهای بی‌نشان و نشاندار را، و نه آنها را که به قصد خانه خدا برای به دست آوردن فضل پروردگار و خشنودی او می‌آیند! اما هنگامی که از احرام بیرون آمدید، صیدکردن برای شما مانعی ندارد. و خصومت با جمعیّتی که شما را از آمدن به مسجد الحرام (در سال حدیبیه) بازداشتند، نباید شما را وادار به تعدّی و تجاوز کند! و (همواره) در راه نیکی و پرهیزگاری با هم تعاون کنید! و (هرگز) در راه گناه و تعدّی همکاری ننمایید! و از (مخالفت فرمان) خدا بپرهیزید که مجازات خدا شدید است!');
INSERT INTO `Quran` (`rowid`, `id`, `Arabic`, `Chapter`, `Verse`, `Juz`, `Hizb`, `Page`, `tr`) VALUES
(672, 672, 'حُرِّمَتْ عَلَيْكُمُ الْمَيْتَةُ وَالدَّمُ وَلَحْمُ الْخِنْزِيرِ وَمَا أُهِلَّ لِغَيْرِ اللَّهِ بِهِ وَالْمُنْخَنِقَةُ وَالْمَوْقُوذَةُ وَالْمُتَرَدِّيَةُ وَالنَّطِيحَةُ وَمَا أَكَلَ السَّبُعُ إِلَّا مَا ذَكَّيْتُمْ وَمَا ذُبِحَ عَلَى النُّصُبِ وَأَنْ تَسْتَقْسِمُوا بِالْأَزْلَامِ ۚ ذَٰلِكُمْ فِسْقٌ ۗ الْيَوْمَ يَئِسَ الَّذِينَ كَفَرُوا مِنْ دِينِكُمْ فَلَا تَخْشَوْهُمْ وَاخْشَوْنِ ۚ الْيَوْمَ أَكْمَلْتُ لَكُمْ دِينَكُمْ وَأَتْمَمْتُ عَلَيْكُمْ نِعْمَتِي وَرَضِيتُ لَكُمُ الْإِسْلَامَ دِينًا ۚ فَمَنِ اضْطُرَّ فِي مَخْمَصَةٍ غَيْرَ مُتَجَانِفٍ لِإِثْمٍ ۙ فَإِنَّ اللَّهَ غَفُورٌ رَحِيمٌ', 5, 3, 6, 22, 107, 'گوشت مردار، و خون، و گوشت خوک، و حیواناتی که به غیر نام خدا ذبح شوند، و حیوانات خفه‌شده، و به زجر کشته شده، و آنها که بر اثر پرت‌شدن از بلندی بمیرند، و آنها که به ضرب شاخ حیوان دیگری مرده باشند، و باقیمانده صید حیوان درنده -مگر آنکه (بموقع به آن حیوان برسید، و) آن را سرببرید- و حیواناتی که روی بتها (یا در برابر آنها) ذبح می‌شوند، (همه) بر شما حرام شده است؛ و (همچنین) قسمت کردن گوشت حیوان به وسیله چوبه‌های تیر مخصوص بخت آزمایی؛ تمام این اعمال، فسق و گناه است -امروز، کافران از (زوال) آیین شما، مأیوس شدند؛ بنابر این، از آنها نترسید! و از (مخالفت) من بترسید! امروز، دین شما را کامل کردم؛ و نعمت خود را بر شما تمام نمودم؛ و اسلام را به عنوان آیین (جاودان) شما پذیرفتم- امّا آنها که در حال گرسنگی، دستشان به غذای دیگری نرسد، و متمایل به گناه نباشند، (مانعی ندارد که از گوشتهای ممنوع بخورند؛) خداوند، آمرزنده و مهربان است.'),
(673, 673, 'يَسْأَلُونَكَ مَاذَا أُحِلَّ لَهُمْ ۖ قُلْ أُحِلَّ لَكُمُ الطَّيِّبَاتُ ۙ وَمَا عَلَّمْتُمْ مِنَ الْجَوَارِحِ مُكَلِّبِينَ تُعَلِّمُونَهُنَّ مِمَّا عَلَّمَكُمُ اللَّهُ ۖ فَكُلُوا مِمَّا أَمْسَكْنَ عَلَيْكُمْ وَاذْكُرُوا اسْمَ اللَّهِ عَلَيْهِ ۖ وَاتَّقُوا اللَّهَ ۚ إِنَّ اللَّهَ سَرِيعُ الْحِسَابِ', 5, 4, 6, 22, 107, 'از تو سؤال می‌کنند چه چیزهایی برای آنها حلال شده است؟ بگو: «آنچه پاکیزه است، برای شما حلال گردیده؛ (و نیز صید) حیوانات شکاری و سگهای آموخته (و تربیت یافته) که از آنچه خداوند به شما تعلیم داده به آنها یاد داده‌اید، (بر شما حلال است؛) پس، از آنچه این حیوانات برای شما (صید می‌کنند و) نگاه می‌دارند، بخورید؛ و نام خدا را (به هنگام فرستادن حیوان برای شکار،) بر آن ببرید؛ و از (معصیت) خدا بپرهیزید که خداوند سریع الحساب است!»'),
(674, 674, 'الْيَوْمَ أُحِلَّ لَكُمُ الطَّيِّبَاتُ ۖ وَطَعَامُ الَّذِينَ أُوتُوا الْكِتَابَ حِلٌّ لَكُمْ وَطَعَامُكُمْ حِلٌّ لَهُمْ ۖ وَالْمُحْصَنَاتُ مِنَ الْمُؤْمِنَاتِ وَالْمُحْصَنَاتُ مِنَ الَّذِينَ أُوتُوا الْكِتَابَ مِنْ قَبْلِكُمْ إِذَا آتَيْتُمُوهُنَّ أُجُورَهُنَّ مُحْصِنِينَ غَيْرَ مُسَافِحِينَ وَلَا مُتَّخِذِي أَخْدَانٍ ۗ وَمَنْ يَكْفُرْ بِالْإِيمَانِ فَقَدْ حَبِطَ عَمَلُهُ وَهُوَ فِي الْآخِرَةِ مِنَ الْخَاسِرِينَ', 5, 5, 6, 22, 107, 'امروز چیزهای پاکیزه برای شما حلال شده؛ و (همچنین) طعام اهل کتاب، برای شما حلال است؛ و طعام شما برای آنها حلال؛ و (نیز) زنان پاکدامن از مسلمانان، و زنان پاکدامن از اهل کتاب، حلالند؛ هنگامی که مهر آنها را بپردازید و پاکدامن باشید؛ نه زناکار، و نه دوست پنهانی و نامشروع گیرید. و کسی که انکار کند آنچه را باید به آن ایمان بیاورد، اعمال او تباه می‌گردد؛ و در سرای دیگر، از زیانکاران خواهد بود.'),
(675, 675, 'يَا أَيُّهَا الَّذِينَ آمَنُوا إِذَا قُمْتُمْ إِلَى الصَّلَاةِ فَاغْسِلُوا وُجُوهَكُمْ وَأَيْدِيَكُمْ إِلَى الْمَرَافِقِ وَامْسَحُوا بِرُءُوسِكُمْ وَأَرْجُلَكُمْ إِلَى الْكَعْبَيْنِ ۚ وَإِنْ كُنْتُمْ جُنُبًا فَاطَّهَّرُوا ۚ وَإِنْ كُنْتُمْ مَرْضَىٰ أَوْ عَلَىٰ سَفَرٍ أَوْ جَاءَ أَحَدٌ مِنْكُمْ مِنَ الْغَائِطِ أَوْ لَامَسْتُمُ النِّسَاءَ فَلَمْ تَجِدُوا مَاءً فَتَيَمَّمُوا صَعِيدًا طَيِّبًا فَامْسَحُوا بِوُجُوهِكُمْ وَأَيْدِيكُمْ مِنْهُ ۚ مَا يُرِيدُ اللَّهُ لِيَجْعَلَ عَلَيْكُمْ مِنْ حَرَجٍ وَلَٰكِنْ يُرِيدُ لِيُطَهِّرَكُمْ وَلِيُتِمَّ نِعْمَتَهُ عَلَيْكُمْ لَعَلَّكُمْ تَشْكُرُونَ', 5, 6, 6, 22, 108, 'ای کسانی که ایمان آورده‌اید! هنگامی که به نماز می‌ایستید، صورت و دستها را تا آرنج بشویید! و سر و پاها را تا مفصل = برآمدگی پشت پا مسح کنید! و اگر جنب باشید، خود را بشویید (و غسل کنید)! و اگر بیمار یا مسافر باشید، یا یکی از شما از محل پستی آمده = قضای حاجت کرده‌، یا با زنان تماس گرفته (و آمیزش جنسی کرده‌اید)، و آب (برای غسل یا وضو) نیابید، با خاک پاکی تیمم کنید! و از آن، بر صورت = پیشانی‌ و دستها بکشید! خداوند نمی‌خواهد مشکلی برای شما ایجاد کند؛ بلکه می‌خواهد شما را پاک سازد و نعمتش را بر شما تمام نماید؛ شاید شکر او را بجا آورید!'),
(676, 676, 'وَاذْكُرُوا نِعْمَةَ اللَّهِ عَلَيْكُمْ وَمِيثَاقَهُ الَّذِي وَاثَقَكُمْ بِهِ إِذْ قُلْتُمْ سَمِعْنَا وَأَطَعْنَا ۖ وَاتَّقُوا اللَّهَ ۚ إِنَّ اللَّهَ عَلِيمٌ بِذَاتِ الصُّدُورِ', 5, 7, 6, 22, 108, 'و به یاد آورید نعمت خدا را بر شما، و پیمانی را که با تأکید از شما گرفت، آن زمان که گفتید: «شنیدیم و اطاعت کردیم»! و از (مخالفت فرمان) خدا بپرهیزید که خدا، از آنچه درون سینه‌هاست، آگاه است!'),
(677, 677, 'يَا أَيُّهَا الَّذِينَ آمَنُوا كُونُوا قَوَّامِينَ لِلَّهِ شُهَدَاءَ بِالْقِسْطِ ۖ وَلَا يَجْرِمَنَّكُمْ شَنَآنُ قَوْمٍ عَلَىٰ أَلَّا تَعْدِلُوا ۚ اعْدِلُوا هُوَ أَقْرَبُ لِلتَّقْوَىٰ ۖ وَاتَّقُوا اللَّهَ ۚ إِنَّ اللَّهَ خَبِيرٌ بِمَا تَعْمَلُونَ', 5, 8, 6, 22, 108, 'ای کسانی که ایمان آورده‌اید! همواره برای خدا قیام کنید، و از روی عدالت، گواهی دهید! دشمنی با جمعیّتی، شما را به گناه و ترک عدالت نکشاند! عدالت کنید، که به پرهیزگاری نزدیکتر است! و از (معصیت) خدا بپرهیزید، که از آنچه انجام می‌دهید، با خبر است!'),
(678, 678, 'وَعَدَ اللَّهُ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ ۙ لَهُمْ مَغْفِرَةٌ وَأَجْرٌ عَظِيمٌ', 5, 9, 6, 22, 108, 'خداوند، به آنها که ایمان آورده و اعمال صالح انجام داده‌اند، وعده آمرزش و پاداش عظیمی داده است.'),
(679, 679, 'وَالَّذِينَ كَفَرُوا وَكَذَّبُوا بِآيَاتِنَا أُولَٰئِكَ أَصْحَابُ الْجَحِيمِ', 5, 10, 6, 22, 109, 'و کسانی که کافر شدند و آیات ما را تکذیب کردند، اهل دوزخند.'),
(680, 680, 'يَا أَيُّهَا الَّذِينَ آمَنُوا اذْكُرُوا نِعْمَتَ اللَّهِ عَلَيْكُمْ إِذْ هَمَّ قَوْمٌ أَنْ يَبْسُطُوا إِلَيْكُمْ أَيْدِيَهُمْ فَكَفَّ أَيْدِيَهُمْ عَنْكُمْ ۖ وَاتَّقُوا اللَّهَ ۚ وَعَلَى اللَّهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُونَ', 5, 11, 6, 22, 109, 'ای کسانی که ایمان آورده‌اید! نعمتی را که خدا به شما بخشید، به یاد آورید؛ آن زمان که جمعی (از دشمنان)، قصد داشتند دست به سوی شما دراز کنند (و شما را از میان بردارند)، اما خدا دست آنها را از شما باز داشت! از خدا بپرهیزید! و مؤمنان باید تنها بر خدا توکّل کنند!'),
(681, 681, ' وَلَقَدْ أَخَذَ اللَّهُ مِيثَاقَ بَنِي إِسْرَائِيلَ وَبَعَثْنَا مِنْهُمُ اثْنَيْ عَشَرَ نَقِيبًا ۖ وَقَالَ اللَّهُ إِنِّي مَعَكُمْ ۖ لَئِنْ أَقَمْتُمُ الصَّلَاةَ وَآتَيْتُمُ الزَّكَاةَ وَآمَنْتُمْ بِرُسُلِي وَعَزَّرْتُمُوهُمْ وَأَقْرَضْتُمُ اللَّهَ قَرْضًا حَسَنًا لَأُكَفِّرَنَّ عَنْكُمْ سَيِّئَاتِكُمْ وَلَأُدْخِلَنَّكُمْ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهَارُ ۚ فَمَنْ كَفَرَ بَعْدَ ذَٰلِكَ مِنْكُمْ فَقَدْ ضَلَّ سَوَاءَ السَّبِيلِ', 5, 12, 6, 22, 109, 'خدا از بنی اسرائیل پیمان گرفت. و از آنها، دوازده نقیب = سرپرست‌ برانگیختیم. و خداوند (به آنها) گفت: «من با شما هستم! اگر نماز را برپا دارید، و زکات را بپردازید، و به رسولان من ایمان بیاورید و آنها را یاری کنید، و به خدا قرض الحسن بدهید = در راه او، به نیازمندان کمک کنید، گناهان شما را می‌پوشانم = می بخشم‌؛ و شما را در باغهایی از بهشت، که نهرها از زیر درختانش جاری است، وارد می‌کنم. اما هر کس از شما بعد از این کافر شود، از راه راست منحرف گردیده است.'),
(682, 682, 'فَبِمَا نَقْضِهِمْ مِيثَاقَهُمْ لَعَنَّاهُمْ وَجَعَلْنَا قُلُوبَهُمْ قَاسِيَةً ۖ يُحَرِّفُونَ الْكَلِمَ عَنْ مَوَاضِعِهِ ۙ وَنَسُوا حَظًّا مِمَّا ذُكِّرُوا بِهِ ۚ وَلَا تَزَالُ تَطَّلِعُ عَلَىٰ خَائِنَةٍ مِنْهُمْ إِلَّا قَلِيلًا مِنْهُمْ ۖ فَاعْفُ عَنْهُمْ وَاصْفَحْ ۚ إِنَّ اللَّهَ يُحِبُّ الْمُحْسِنِينَ', 5, 13, 6, 22, 109, 'ولی بخاطر پیمان‌شکنی، آنها را از رحمت خویش دور ساختیم؛ و دلهای آنان را سخت و سنگین نمودیم؛ سخنان (خدا) را از موردش تحریف می‌کنند؛ و بخشی از آنچه را به آنها گوشزد شده بود، فراموش کردند؛ و هر زمان، از خیانتی (تازه) از آنها آگاه می‌شوی، مگر عده کمی از آنان؛ ولی از آنها درگذر و صرف‌نظر کن، که خداوند نیکوکاران را دوست می‌دارد!'),
(683, 683, 'وَمِنَ الَّذِينَ قَالُوا إِنَّا نَصَارَىٰ أَخَذْنَا مِيثَاقَهُمْ فَنَسُوا حَظًّا مِمَّا ذُكِّرُوا بِهِ فَأَغْرَيْنَا بَيْنَهُمُ الْعَدَاوَةَ وَالْبَغْضَاءَ إِلَىٰ يَوْمِ الْقِيَامَةِ ۚ وَسَوْفَ يُنَبِّئُهُمُ اللَّهُ بِمَا كَانُوا يَصْنَعُونَ', 5, 14, 6, 22, 110, 'و از کسانی که ادّعای نصرانیّت (و یاری مسیح) داشتند (نیز) پیمان گرفتیم؛ ولی آنها قسمت مهمّی را از آنچه به آنان تذکّر داده شده بود فراموش کردند؛ از این رو در میان آنها، تا دامنه قیامت، عداوت و دشمنی افکندیم. و خداوند، (در قیامت) آنها را از آنچه انجام می‌دادند (و نتایج آن)، آگاه خواهد ساخت.'),
(684, 684, 'يَا أَهْلَ الْكِتَابِ قَدْ جَاءَكُمْ رَسُولُنَا يُبَيِّنُ لَكُمْ كَثِيرًا مِمَّا كُنْتُمْ تُخْفُونَ مِنَ الْكِتَابِ وَيَعْفُو عَنْ كَثِيرٍ ۚ قَدْ جَاءَكُمْ مِنَ اللَّهِ نُورٌ وَكِتَابٌ مُبِينٌ', 5, 15, 6, 22, 110, 'ای اهل کتاب! پیامبر ما، که بسیاری از حقایق کتاب آسمانی را که شما کتمان می‌کردید روشن می‌سازد، به سوی شما آمد؛ و از بسیاری از آن، (که فعلاً افشای آن مصلحت نیست،) صرف نظر می‌نماید. (آری،) از طرف خدا، نور و کتاب آشکاری به سوی شما آمد.'),
(685, 685, 'يَهْدِي بِهِ اللَّهُ مَنِ اتَّبَعَ رِضْوَانَهُ سُبُلَ السَّلَامِ وَيُخْرِجُهُمْ مِنَ الظُّلُمَاتِ إِلَى النُّورِ بِإِذْنِهِ وَيَهْدِيهِمْ إِلَىٰ صِرَاطٍ مُسْتَقِيمٍ', 5, 16, 6, 22, 110, 'خداوند به برکت آن، کسانی را که از خشنودی او پیروی کنند، به راه‌های سلامت، هدایت می‌کند؛ و به فرمان خود، از تاریکیها به سوی روشنایی می‌برد؛ و آنها را به سوی راه راست، رهبری می‌نماید.'),
(686, 686, 'لَقَدْ كَفَرَ الَّذِينَ قَالُوا إِنَّ اللَّهَ هُوَ الْمَسِيحُ ابْنُ مَرْيَمَ ۚ قُلْ فَمَنْ يَمْلِكُ مِنَ اللَّهِ شَيْئًا إِنْ أَرَادَ أَنْ يُهْلِكَ الْمَسِيحَ ابْنَ مَرْيَمَ وَأُمَّهُ وَمَنْ فِي الْأَرْضِ جَمِيعًا ۗ وَلِلَّهِ مُلْكُ السَّمَاوَاتِ وَالْأَرْضِ وَمَا بَيْنَهُمَا ۚ يَخْلُقُ مَا يَشَاءُ ۚ وَاللَّهُ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ', 5, 17, 6, 22, 110, 'آنها که گفتند: «خدا، همان مسیح بن مریم است»، بطور مسلّم کافر شدند؛ بگو: «اگر خدا بخواهد مسیح بن مریم و مادرش و همه کسانی را که روی زمین هستند هلاک کند، چه کسی می‌تواند جلوگیری کند؟ (آری،) حکومت آسمانها و زمین، و آنچه میان آن دو قرار دارد از آن خداست؛ هر چه بخواهد، می‌آفریند؛ (حتّی انسانی بدون پدر، مانند مسیح؛) و او، بر هر چیزی تواناست.»'),
(687, 687, 'وَقَالَتِ الْيَهُودُ وَالنَّصَارَىٰ نَحْنُ أَبْنَاءُ اللَّهِ وَأَحِبَّاؤُهُ ۚ قُلْ فَلِمَ يُعَذِّبُكُمْ بِذُنُوبِكُمْ ۖ بَلْ أَنْتُمْ بَشَرٌ مِمَّنْ خَلَقَ ۚ يَغْفِرُ لِمَنْ يَشَاءُ وَيُعَذِّبُ مَنْ يَشَاءُ ۚ وَلِلَّهِ مُلْكُ السَّمَاوَاتِ وَالْأَرْضِ وَمَا بَيْنَهُمَا ۖ وَإِلَيْهِ الْمَصِيرُ', 5, 18, 6, 22, 111, 'یهود و نصاری گفتند: «ما، فرزندان خدا و دوستان (خاصّ) او هستیم.» بگو: «پس چرا شما را در برابر گناهانتان مجازات می‌کند؟! بلکه شما هم بشری هستید از مخلوقاتی که آفریده؛ هر کس را بخواهد (و شایسته بداند)، می‌بخشد؛ و هر کس را بخواهد (و مستحق بداند)، مجازات می‌کند؛ و حکومت آسمانها و زمین و آنچه در میان آنهاست، از آن اوست؛ و بازگشت همه موجودات، به سوی اوست.»'),
(688, 688, 'يَا أَهْلَ الْكِتَابِ قَدْ جَاءَكُمْ رَسُولُنَا يُبَيِّنُ لَكُمْ عَلَىٰ فَتْرَةٍ مِنَ الرُّسُلِ أَنْ تَقُولُوا مَا جَاءَنَا مِنْ بَشِيرٍ وَلَا نَذِيرٍ ۖ فَقَدْ جَاءَكُمْ بَشِيرٌ وَنَذِيرٌ ۗ وَاللَّهُ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ', 5, 19, 6, 22, 111, 'ای اهل کتاب! رسول ما، پس از فاصله و فترتی میان پیامبران، به سوی شما آمد؛ در حالی که حقایق را برای شما بیان می‌کند؛ تا مبادا (روز قیامت) بگویید: «نه بشارت دهنده‌ای به سراغ ما آمد، و نه بیم دهنده‌ای»! (هم اکنون، پیامبر) بشارت‌دهنده و بیم‌دهنده، به سوی شما آمد! و خداوند بر همه چیز تواناست.'),
(689, 689, 'وَإِذْ قَالَ مُوسَىٰ لِقَوْمِهِ يَا قَوْمِ اذْكُرُوا نِعْمَةَ اللَّهِ عَلَيْكُمْ إِذْ جَعَلَ فِيكُمْ أَنْبِيَاءَ وَجَعَلَكُمْ مُلُوكًا وَآتَاكُمْ مَا لَمْ يُؤْتِ أَحَدًا مِنَ الْعَالَمِينَ', 5, 20, 6, 22, 111, '(به یاد آورید) هنگامی را که موسی به قوم خود گفت: «ای قوم من! نعمت خدا را بر خود متذکّر شوید هنگامی که در میان شما، پیامبرانی قرار داد؛ (و زنجیر بندگی و اسارت فرعونی را شکست) و شما را حاکم و صاحب اختیار خود قرار داد؛ و به شما چیزهایی بخشید که به هیچ یک از جهانیان نداده بود!'),
(690, 690, 'يَا قَوْمِ ادْخُلُوا الْأَرْضَ الْمُقَدَّسَةَ الَّتِي كَتَبَ اللَّهُ لَكُمْ وَلَا تَرْتَدُّوا عَلَىٰ أَدْبَارِكُمْ فَتَنْقَلِبُوا خَاسِرِينَ', 5, 21, 6, 22, 111, 'ای قوم! به سرزمین مقدّسی که خداوند برای شما مقرّر داشته، وارد شوید! و به پشت سر خود بازنگردید (و عقب گرد نکنید) که زیانکار خواهید بود!»'),
(691, 691, 'قَالُوا يَا مُوسَىٰ إِنَّ فِيهَا قَوْمًا جَبَّارِينَ وَإِنَّا لَنْ نَدْخُلَهَا حَتَّىٰ يَخْرُجُوا مِنْهَا فَإِنْ يَخْرُجُوا مِنْهَا فَإِنَّا دَاخِلُونَ', 5, 22, 6, 22, 111, 'گفتند: «ای موسی! در آن (سرزمین)، جمعیّتی (نیرومند و) ستمگرند؛ و ما هرگز وارد آن نمی‌شویم تا آنها از آن خارج شوند؛ اگر آنها از آن خارج شوند، ما وارد خواهیم شد!»'),
(692, 692, 'قَالَ رَجُلَانِ مِنَ الَّذِينَ يَخَافُونَ أَنْعَمَ اللَّهُ عَلَيْهِمَا ادْخُلُوا عَلَيْهِمُ الْبَابَ فَإِذَا دَخَلْتُمُوهُ فَإِنَّكُمْ غَالِبُونَ ۚ وَعَلَى اللَّهِ فَتَوَكَّلُوا إِنْ كُنْتُمْ مُؤْمِنِينَ', 5, 23, 6, 22, 111, '(ولی) دو نفر از مردانی که از خدا می‌ترسیدند، و خداوند به آنها، نعمت (عقل و ایمان و شهامت) داده بود، گفتند: «شما وارد دروازه شهر آنان شوید! هنگامی که وارد شدید، پیروز خواهید شد. و بر خدا توکل کنید اگر ایمان دارید!»'),
(693, 693, 'قَالُوا يَا مُوسَىٰ إِنَّا لَنْ نَدْخُلَهَا أَبَدًا مَا دَامُوا فِيهَا ۖ فَاذْهَبْ أَنْتَ وَرَبُّكَ فَقَاتِلَا إِنَّا هَاهُنَا قَاعِدُونَ', 5, 24, 6, 22, 112, '(بنی اسرائیل) گفتند: «ای موسی! تا آنها در آنجا هستند، ما هرگز وارد نخواهیم شد! تو و پروردگارت بروید و (با آنان) بجنگید، ما همینجا نشسته‌ایم»!'),
(694, 694, 'قَالَ رَبِّ إِنِّي لَا أَمْلِكُ إِلَّا نَفْسِي وَأَخِي ۖ فَافْرُقْ بَيْنَنَا وَبَيْنَ الْقَوْمِ الْفَاسِقِينَ', 5, 25, 6, 22, 112, '(موسی) گفت: «پروردگارا! من تنها اختیار خودم و برادرم را دارم، میان ما و این جمعیّت گنهکار، جدایی بیفکن!»'),
(695, 695, 'قَالَ فَإِنَّهَا مُحَرَّمَةٌ عَلَيْهِمْ ۛ أَرْبَعِينَ سَنَةً ۛ يَتِيهُونَ فِي الْأَرْضِ ۚ فَلَا تَأْسَ عَلَى الْقَوْمِ الْفَاسِقِينَ', 5, 26, 6, 22, 112, 'خداوند (به موسی) فرمود: «این سرزمین (مقدس)، تا چهل سال بر آنها ممنوع است (و به آن نخواهند رسید)؛ پیوسته در زمین (در این بیابان)، سرگردان خواهند بود؛ و در باره (سرنوشت) این جمعیّت گنهکار، غمگین مباش!»'),
(696, 696, ' وَاتْلُ عَلَيْهِمْ نَبَأَ ابْنَيْ آدَمَ بِالْحَقِّ إِذْ قَرَّبَا قُرْبَانًا فَتُقُبِّلَ مِنْ أَحَدِهِمَا وَلَمْ يُتَقَبَّلْ مِنَ الْآخَرِ قَالَ لَأَقْتُلَنَّكَ ۖ قَالَ إِنَّمَا يَتَقَبَّلُ اللَّهُ مِنَ الْمُتَّقِينَ', 5, 27, 6, 23, 112, 'و داستان دو فرزند آدم را بحقّ بر آنها بخوان: هنگامی که هر کدام، کاری برای تقرّب (به پروردگار) انجام دادند؛ امّا از یکی پذیرفته شد، و از دیگری پذیرفته نشد؛ (برادری که عملش مردود شده بود، به برادر دیگر) گفت: «به خدا سوگند تو را خواهم کشت!» (برادر دیگر) گفت: «(من چه گناهی دارم؟ زیرا) خدا، تنها از پرهیزگاران می‌پذیرد!'),
(697, 697, 'لَئِنْ بَسَطْتَ إِلَيَّ يَدَكَ لِتَقْتُلَنِي مَا أَنَا بِبَاسِطٍ يَدِيَ إِلَيْكَ لِأَقْتُلَكَ ۖ إِنِّي أَخَافُ اللَّهَ رَبَّ الْعَالَمِينَ', 5, 28, 6, 23, 112, 'اگر تو برای کشتن من، دست دراز کنی، من هرگز به قتل تو دست نمی‌گشایم، چون از پروردگار جهانیان می‌ترسم!'),
(698, 698, 'إِنِّي أُرِيدُ أَنْ تَبُوءَ بِإِثْمِي وَإِثْمِكَ فَتَكُونَ مِنْ أَصْحَابِ النَّارِ ۚ وَذَٰلِكَ جَزَاءُ الظَّالِمِينَ', 5, 29, 6, 23, 112, 'من می‌خواهم تو با گناه من و خودت (از این عمل) بازگردی (و بار هر دو گناه را به دوش کشی)؛ و از دوزخیان گردی. و همین است سزای ستمکاران!'),
(699, 699, 'فَطَوَّعَتْ لَهُ نَفْسُهُ قَتْلَ أَخِيهِ فَقَتَلَهُ فَأَصْبَحَ مِنَ الْخَاسِرِينَ', 5, 30, 6, 23, 112, 'نفس سرکش، کم کم او را به کشتن برادرش ترغیب کرد؛ (سرانجام) او را کشت؛ و از زیانکاران شد.'),
(700, 700, 'فَبَعَثَ اللَّهُ غُرَابًا يَبْحَثُ فِي الْأَرْضِ لِيُرِيَهُ كَيْفَ يُوَارِي سَوْءَةَ أَخِيهِ ۚ قَالَ يَا وَيْلَتَا أَعَجَزْتُ أَنْ أَكُونَ مِثْلَ هَٰذَا الْغُرَابِ فَأُوَارِيَ سَوْءَةَ أَخِي ۖ فَأَصْبَحَ مِنَ النَّادِمِينَ', 5, 31, 6, 23, 112, 'سپس خداوند زاغی را فرستاد که در زمین، جستجو (و کندوکاو) می‌کرد؛ تا به او نشان دهد چگونه جسد برادر خود را دفن کند. او گفت: «وای بر من! آیا من نتوانستم مثل این زاغ باشم و جسد برادرم را دفن کنم؟!» و سرانجام (از ترس رسوایی، و بر اثر فشار وجدان، از کار خود) پشیمان شد.'),
(701, 701, 'مِنْ أَجْلِ ذَٰلِكَ كَتَبْنَا عَلَىٰ بَنِي إِسْرَائِيلَ أَنَّهُ مَنْ قَتَلَ نَفْسًا بِغَيْرِ نَفْسٍ أَوْ فَسَادٍ فِي الْأَرْضِ فَكَأَنَّمَا قَتَلَ النَّاسَ جَمِيعًا وَمَنْ أَحْيَاهَا فَكَأَنَّمَا أَحْيَا النَّاسَ جَمِيعًا ۚ وَلَقَدْ جَاءَتْهُمْ رُسُلُنَا بِالْبَيِّنَاتِ ثُمَّ إِنَّ كَثِيرًا مِنْهُمْ بَعْدَ ذَٰلِكَ فِي الْأَرْضِ لَمُسْرِفُونَ', 5, 32, 6, 23, 113, 'به همین جهت، بر بنی اسرائیل مقرّر داشتیم که هر کس، انسانی را بدون ارتکاب قتل یا فساد در روی زمین بکشد، چنان است که گویی همه انسانها را کشته؛ و هر کس، انسانی را از مرگ رهایی بخشد، چنان است که گویی همه مردم را زنده کرده است. و رسولان ما، دلایل روشن برای بنی اسرائیل آوردند، اما بسیاری از آنها، پس از آن در روی زمین، تعدّی و اسراف کردند.'),
(702, 702, 'إِنَّمَا جَزَاءُ الَّذِينَ يُحَارِبُونَ اللَّهَ وَرَسُولَهُ وَيَسْعَوْنَ فِي الْأَرْضِ فَسَادًا أَنْ يُقَتَّلُوا أَوْ يُصَلَّبُوا أَوْ تُقَطَّعَ أَيْدِيهِمْ وَأَرْجُلُهُمْ مِنْ خِلَافٍ أَوْ يُنْفَوْا مِنَ الْأَرْضِ ۚ ذَٰلِكَ لَهُمْ خِزْيٌ فِي الدُّنْيَا ۖ وَلَهُمْ فِي الْآخِرَةِ عَذَابٌ عَظِيمٌ', 5, 33, 6, 23, 113, 'کیفر آنها که با خدا و پیامبرش به جنگ برمی‌خیزند، و اقدام به فساد در روی زمین می‌کنند، (و با تهدید اسلحه، به جان و مال و ناموس مردم حمله می‌برند،) فقط این است که اعدام شوند؛ یا به دار آویخته گردند؛ یا (چهار انگشت از) دست (راست) و پای (چپ) آنها، بعکس یکدیگر، بریده شود؛ و یا از سرزمین خود تبعید گردند. این رسوایی آنها در دنیاست؛ و در آخرت، مجازات عظیمی دارند.'),
(703, 703, 'إِلَّا الَّذِينَ تَابُوا مِنْ قَبْلِ أَنْ تَقْدِرُوا عَلَيْهِمْ ۖ فَاعْلَمُوا أَنَّ اللَّهَ غَفُورٌ رَحِيمٌ', 5, 34, 6, 23, 113, 'مگر آنها که پیش از دست یافتن شما بر آنان، توبه کنند؛ پس بدانید (خدا توبه آنها را می‌پذیرد؛) خداوند آمرزنده و مهربان است.'),
(704, 704, 'يَا أَيُّهَا الَّذِينَ آمَنُوا اتَّقُوا اللَّهَ وَابْتَغُوا إِلَيْهِ الْوَسِيلَةَ وَجَاهِدُوا فِي سَبِيلِهِ لَعَلَّكُمْ تُفْلِحُونَ', 5, 35, 6, 23, 113, 'ای کسانی که ایمان آورده‌اید! از (مخالفت فرمان) خدا بپرهیزید! و وسیله‌ای برای تقرب به او بجوئید! و در راه او جهاد کنید، باشد که رستگار شوید!'),
(705, 705, 'إِنَّ الَّذِينَ كَفَرُوا لَوْ أَنَّ لَهُمْ مَا فِي الْأَرْضِ جَمِيعًا وَمِثْلَهُ مَعَهُ لِيَفْتَدُوا بِهِ مِنْ عَذَابِ يَوْمِ الْقِيَامَةِ مَا تُقُبِّلَ مِنْهُمْ ۖ وَلَهُمْ عَذَابٌ أَلِيمٌ', 5, 36, 6, 23, 113, 'بیقین کسانی که کافر شدند، اگر تمام آنچه روی زمین است و همانند آن، مال آنها باشد و همه آن را برای نجات از کیفر روز قیامت بدهند، از آنان پذیرفته نخواهد شد؛ و مجازات دردناکی خواهند داشت.'),
(706, 706, 'يُرِيدُونَ أَنْ يَخْرُجُوا مِنَ النَّارِ وَمَا هُمْ بِخَارِجِينَ مِنْهَا ۖ وَلَهُمْ عَذَابٌ مُقِيمٌ', 5, 37, 6, 23, 114, 'پیوسته می‌خواهند از آتش خارج شوند، ولی نمی‌توانند از آن خارج گردند؛ و برای آنها مجازاتی پایدار است.'),
(707, 707, 'وَالسَّارِقُ وَالسَّارِقَةُ فَاقْطَعُوا أَيْدِيَهُمَا جَزَاءً بِمَا كَسَبَا نَكَالًا مِنَ اللَّهِ ۗ وَاللَّهُ عَزِيزٌ حَكِيمٌ', 5, 38, 6, 23, 114, 'دست مرد دزد و زن دزد را، به کیفر عملی که انجام داده‌اند، بعنوان یک مجازات الهی، قطع کنید! و خداوند توانا و حکیم است.'),
(708, 708, 'فَمَنْ تَابَ مِنْ بَعْدِ ظُلْمِهِ وَأَصْلَحَ فَإِنَّ اللَّهَ يَتُوبُ عَلَيْهِ ۗ إِنَّ اللَّهَ غَفُورٌ رَحِيمٌ', 5, 39, 6, 23, 114, 'اما آن کس که پس از ستم کردن، توبه و جبران نماید، خداوند توبه او را می‌پذیرد؛ (و از این مجازات؛ معاف می‌شود، زیرا) خداوند، آمرزنده و مهربان است.'),
(709, 709, 'أَلَمْ تَعْلَمْ أَنَّ اللَّهَ لَهُ مُلْكُ السَّمَاوَاتِ وَالْأَرْضِ يُعَذِّبُ مَنْ يَشَاءُ وَيَغْفِرُ لِمَنْ يَشَاءُ ۗ وَاللَّهُ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ', 5, 40, 6, 23, 114, 'آیا نمی‌دانی که حکومت و فرمانروائی آسمانها و زمین از آن خداست؟ هر کس را بخواهد (و مستحق بداند)، کیفر می‌کند؛ و هر کس را بخواهد و شایسته بداند، می‌بخشد؛ و خداوند بر هر چیزی قادر است.'),
(710, 710, ' يَا أَيُّهَا الرَّسُولُ لَا يَحْزُنْكَ الَّذِينَ يُسَارِعُونَ فِي الْكُفْرِ مِنَ الَّذِينَ قَالُوا آمَنَّا بِأَفْوَاهِهِمْ وَلَمْ تُؤْمِنْ قُلُوبُهُمْ ۛ وَمِنَ الَّذِينَ هَادُوا ۛ سَمَّاعُونَ لِلْكَذِبِ سَمَّاعُونَ لِقَوْمٍ آخَرِينَ لَمْ يَأْتُوكَ ۖ يُحَرِّفُونَ الْكَلِمَ مِنْ بَعْدِ مَوَاضِعِهِ ۖ يَقُولُونَ إِنْ أُوتِيتُمْ هَٰذَا فَخُذُوهُ وَإِنْ لَمْ تُؤْتَوْهُ فَاحْذَرُوا ۚ وَمَنْ يُرِدِ اللَّهُ فِتْنَتَهُ فَلَنْ تَمْلِكَ لَهُ مِنَ اللَّهِ شَيْئًا ۚ أُولَٰئِكَ الَّذِينَ لَمْ يُرِدِ اللَّهُ أَنْ يُطَهِّرَ قُلُوبَهُمْ ۚ لَهُمْ فِي الدُّنْيَا خِزْيٌ ۖ وَلَهُمْ فِي الْآخِرَةِ عَذَابٌ عَظِيمٌ', 5, 41, 6, 23, 114, 'ای فرستاده (خدا)! آنها که در مسیر کفر شتاب می‌کنند و با زبان می‌گویند: «ایمان آوردیم» و قلب آنها ایمان نیاورده، تو را اندوهگین نسازند! و (همچنین) گروهی از یهودیان که خوب به سخنان تو گوش می‌دهند، تا دستاویزی برای تکذیب تو بیابند؛ آنها جاسوسان گروه دیگری هستند که خودشان نزد تو نیامده‌اند؛ آنها سخنان را از مفهوم اصلیش تحریف می‌کنند، و (به یکدیگر) می‌گویند: «اگر این (که ما می‌خواهیم) به شما داده شد (و محمد بر طبق خواسته شما داوری کرد،) بپذیرید، وگرنه (از او) دوری کنید!» (ولی) کسی را که خدا (بر اثر گناهان پی‌درپی او) بخواهد مجازات کند، قادر به دفاع از او نیستی؛ آنها کسانی هستند که خدا نخواسته دلهایشان را پاک کند؛ در دنیا رسوایی، و در آخرت مجازات بزرگی نصیبشان خواهد شد.'),
(711, 711, 'سَمَّاعُونَ لِلْكَذِبِ أَكَّالُونَ لِلسُّحْتِ ۚ فَإِنْ جَاءُوكَ فَاحْكُمْ بَيْنَهُمْ أَوْ أَعْرِضْ عَنْهُمْ ۖ وَإِنْ تُعْرِضْ عَنْهُمْ فَلَنْ يَضُرُّوكَ شَيْئًا ۖ وَإِنْ حَكَمْتَ فَاحْكُمْ بَيْنَهُمْ بِالْقِسْطِ ۚ إِنَّ اللَّهَ يُحِبُّ الْمُقْسِطِينَ', 5, 42, 6, 23, 115, 'آنها بسیار به سخنان تو گوش می‌دهند تا آن را تکذیب کنند؛ مال حرام فراوان می خورند؛ پس اگر نزد تو آمدند، در میان آنان داوری کن، یا (اگر صلاح دانستی) آنها را به حال خود واگذار! و اگر از آنان صرف‌نظر کنی، به تو هیچ زیانی نمی‌رسانند؛ و اگر میان آنها داوری کنی، با عدالت داوری کن، که خدا عادلان را دوست دارد!'),
(712, 712, 'وَكَيْفَ يُحَكِّمُونَكَ وَعِنْدَهُمُ التَّوْرَاةُ فِيهَا حُكْمُ اللَّهِ ثُمَّ يَتَوَلَّوْنَ مِنْ بَعْدِ ذَٰلِكَ ۚ وَمَا أُولَٰئِكَ بِالْمُؤْمِنِينَ', 5, 43, 6, 23, 115, 'چگونه تو را به داوری می‌طلبند؟! در حالی که تورات نزد ایشان است؛ و در آن، حکم خدا هست. (وانگهی) پس از داوری‌خواستن از حکم تو، (چرا) روی می گردانند؟! آنها مؤمن نیستند.'),
(713, 713, 'إِنَّا أَنْزَلْنَا التَّوْرَاةَ فِيهَا هُدًى وَنُورٌ ۚ يَحْكُمُ بِهَا النَّبِيُّونَ الَّذِينَ أَسْلَمُوا لِلَّذِينَ هَادُوا وَالرَّبَّانِيُّونَ وَالْأَحْبَارُ بِمَا اسْتُحْفِظُوا مِنْ كِتَابِ اللَّهِ وَكَانُوا عَلَيْهِ شُهَدَاءَ ۚ فَلَا تَخْشَوُا النَّاسَ وَاخْشَوْنِ وَلَا تَشْتَرُوا بِآيَاتِي ثَمَنًا قَلِيلًا ۚ وَمَنْ لَمْ يَحْكُمْ بِمَا أَنْزَلَ اللَّهُ فَأُولَٰئِكَ هُمُ الْكَافِرُونَ', 5, 44, 6, 23, 115, 'ما تورات را نازل کردیم در حالی که در آن، هدایت و نور بود؛ و پیامبران، که در برابر فرمان خدا تسلیم بودند، با آن برای یهود حکم می‌کردند؛ و (همچنین) علما و دانشمندان به این کتاب که به آنها سپرده شده و بر آن گواه بودند، داوری می‌نمودند. بنابر این، (بخاطر داوری بر طبق آیات الهی،) از مردم نهراسید! و از من بترسید! و آیات مرا به بهای ناچیزی نفروشید! و آنها که به احکامی که خدا نازل کرده حکم نمی‌کنند، کافرند.'),
(714, 714, 'وَكَتَبْنَا عَلَيْهِمْ فِيهَا أَنَّ النَّفْسَ بِالنَّفْسِ وَالْعَيْنَ بِالْعَيْنِ وَالْأَنْفَ بِالْأَنْفِ وَالْأُذُنَ بِالْأُذُنِ وَالسِّنَّ بِالسِّنِّ وَالْجُرُوحَ قِصَاصٌ ۚ فَمَنْ تَصَدَّقَ بِهِ فَهُوَ كَفَّارَةٌ لَهُ ۚ وَمَنْ لَمْ يَحْكُمْ بِمَا أَنْزَلَ اللَّهُ فَأُولَٰئِكَ هُمُ الظَّالِمُونَ', 5, 45, 6, 23, 115, 'و بر آنها = بنی اسرائیل‌ در آن = تورات‌، مقرر داشتیم که جان در مقابل جان، و چشم در مقابل چشم، و بینی در برابر بینی، و گوش در مقابل گوش، و دندان در برابر دندان می‌باشد؛ و هر زخمی، قصاص دارد؛ و اگر کسی آن را ببخشد (و از قصاص، صرف‌نظر کند)، کفاره (گناهان) او محسوب می‌شود؛ و هر کس به احکامی که خدا نازل کرده حکم نکند، ستمگر است.'),
(715, 715, 'وَقَفَّيْنَا عَلَىٰ آثَارِهِمْ بِعِيسَى ابْنِ مَرْيَمَ مُصَدِّقًا لِمَا بَيْنَ يَدَيْهِ مِنَ التَّوْرَاةِ ۖ وَآتَيْنَاهُ الْإِنْجِيلَ فِيهِ هُدًى وَنُورٌ وَمُصَدِّقًا لِمَا بَيْنَ يَدَيْهِ مِنَ التَّوْرَاةِ وَهُدًى وَمَوْعِظَةً لِلْمُتَّقِينَ', 5, 46, 6, 23, 116, 'و بدنبال آنها = پیامبران پیشین‌، عیسی بن مریم را فرستادیم در حالی که کتاب تورات را که پیش از او فرستاده شده بود تصدیق داشت؛ و انجیل را به او دادیم که در آن، هدایت و نور بود؛ و (این کتاب آسمانی نیز) تورات را، که قبل از آن بود، تصدیق می‌کرد؛ و هدایت و موعظه‌ای برای پرهیزگاران بود.'),
(716, 716, 'وَلْيَحْكُمْ أَهْلُ الْإِنْجِيلِ بِمَا أَنْزَلَ اللَّهُ فِيهِ ۚ وَمَنْ لَمْ يَحْكُمْ بِمَا أَنْزَلَ اللَّهُ فَأُولَٰئِكَ هُمُ الْفَاسِقُونَ', 5, 47, 6, 23, 116, 'اهل انجیل = پیروان مسیح‌ نیز باید به آنچه خداوند در آن نازل کرده حکم کنند! و کسانی که بر طبق آنچه خدا نازل کرده حکم نمی‌کنند، فاسقند.'),
(717, 717, 'وَأَنْزَلْنَا إِلَيْكَ الْكِتَابَ بِالْحَقِّ مُصَدِّقًا لِمَا بَيْنَ يَدَيْهِ مِنَ الْكِتَابِ وَمُهَيْمِنًا عَلَيْهِ ۖ فَاحْكُمْ بَيْنَهُمْ بِمَا أَنْزَلَ اللَّهُ ۖ وَلَا تَتَّبِعْ أَهْوَاءَهُمْ عَمَّا جَاءَكَ مِنَ الْحَقِّ ۚ لِكُلٍّ جَعَلْنَا مِنْكُمْ شِرْعَةً وَمِنْهَاجًا ۚ وَلَوْ شَاءَ اللَّهُ لَجَعَلَكُمْ أُمَّةً وَاحِدَةً وَلَٰكِنْ لِيَبْلُوَكُمْ فِي مَا آتَاكُمْ ۖ فَاسْتَبِقُوا الْخَيْرَاتِ ۚ إِلَى اللَّهِ مَرْجِعُكُمْ جَمِيعًا فَيُنَبِّئُكُمْ بِمَا كُنْتُمْ فِيهِ تَخْتَلِفُونَ', 5, 48, 6, 23, 116, 'و این کتاب = قرآن‌ را به حق بر تو نازل کردیم، در حالی که کتب پیشین را تصدیق می‌کند، و حافظ و نگاهبان آنهاست؛ پس بر طبق احکامی که خدا نازل کرده، در میان آنها حکم کن! از هوی و هوسهای آنان پیروی نکن! و از احکام الهی، روی مگردان! ما برای هر کدام از شما، آیین و طریقه روشنی قرار دادیم؛ و اگر خدا می‌خواست، همه شما را امت واحدی قرارمی‌داد؛ ولی خدا می‌خواهد شما را در آنچه به شما بخشیده بیازماید؛ (و استعدادهای مختلف شما را پرورش دهد). پس در نیکیها بر یکدیگر سبقت جویید! بازگشت همه شما، به سوی خداست؛ سپس از آنچه در آن اختلاف می‌کردید؛ به شما خبر خواهد داد.'),
(718, 718, 'وَأَنِ احْكُمْ بَيْنَهُمْ بِمَا أَنْزَلَ اللَّهُ وَلَا تَتَّبِعْ أَهْوَاءَهُمْ وَاحْذَرْهُمْ أَنْ يَفْتِنُوكَ عَنْ بَعْضِ مَا أَنْزَلَ اللَّهُ إِلَيْكَ ۖ فَإِنْ تَوَلَّوْا فَاعْلَمْ أَنَّمَا يُرِيدُ اللَّهُ أَنْ يُصِيبَهُمْ بِبَعْضِ ذُنُوبِهِمْ ۗ وَإِنَّ كَثِيرًا مِنَ النَّاسِ لَفَاسِقُونَ', 5, 49, 6, 23, 116, 'و در میان آنها = اهل کتاب‌، طبق آنچه خداوند نازل کرده، داوری کن! و از هوسهای آنان پیروی مکن! و از آنها برحذر باش، مبادا تو را از بعض احکامی که خدا بر تو نازل کرده، منحرف سازند! و اگر آنها (از حکم و داوری تو)، روی گردانند، بدان که خداوند می‌خواهد آنان را بخاطر پاره‌ای از گناهانشان مجازات کند؛ و بسیاری از مردم فاسقند.'),
(719, 719, 'أَفَحُكْمَ الْجَاهِلِيَّةِ يَبْغُونَ ۚ وَمَنْ أَحْسَنُ مِنَ اللَّهِ حُكْمًا لِقَوْمٍ يُوقِنُونَ', 5, 50, 6, 23, 116, 'آیا آنها حکم جاهلیّت را (از تو) می‌خواهند؟! و چه کسی بهتر از خدا، برای قومی که اهل یقین هستند، حکم می‌کند؟!'),
(720, 720, ' يَا أَيُّهَا الَّذِينَ آمَنُوا لَا تَتَّخِذُوا الْيَهُودَ وَالنَّصَارَىٰ أَوْلِيَاءَ ۘ بَعْضُهُمْ أَوْلِيَاءُ بَعْضٍ ۚ وَمَنْ يَتَوَلَّهُمْ مِنْكُمْ فَإِنَّهُ مِنْهُمْ ۗ إِنَّ اللَّهَ لَا يَهْدِي الْقَوْمَ الظَّالِمِينَ', 5, 51, 6, 24, 117, 'ای کسانی که ایمان آورده‌اید! یهود و نصاری را ولّی (و دوست و تکیه‌گاه خود،) انتخاب نکنید! آنها اولیای یکدیگرند؛ و کسانی که از شما با آنان دوستی کنند، از آنها هستند؛ خداوند، جمعیّت ستمکار را هدایت نمی‌کند'),
(721, 721, 'فَتَرَى الَّذِينَ فِي قُلُوبِهِمْ مَرَضٌ يُسَارِعُونَ فِيهِمْ يَقُولُونَ نَخْشَىٰ أَنْ تُصِيبَنَا دَائِرَةٌ ۚ فَعَسَى اللَّهُ أَنْ يَأْتِيَ بِالْفَتْحِ أَوْ أَمْرٍ مِنْ عِنْدِهِ فَيُصْبِحُوا عَلَىٰ مَا أَسَرُّوا فِي أَنْفُسِهِمْ نَادِمِينَ', 5, 52, 6, 24, 117, '(ولی) کسانی را که در دلهایشان بیماری است می‌بینی که در (دوستی با آنان)، بر یکدیگر پیشی می‌گیرند، و می‌گویند: «می‌ترسیم حادثه‌ای برای ما اتفاق بیفتد (و نیاز به کمک آنها داشته باشیم!)» شاید خداوند پیروزی یا حادثه دیگری از سوی خود (به نفع مسلمانان) پیش بیاورد؛ و این دسته، از آنچه در دل پنهان داشتند، پشیمان گردند!'),
(722, 722, 'وَيَقُولُ الَّذِينَ آمَنُوا أَهَٰؤُلَاءِ الَّذِينَ أَقْسَمُوا بِاللَّهِ جَهْدَ أَيْمَانِهِمْ ۙ إِنَّهُمْ لَمَعَكُمْ ۚ حَبِطَتْ أَعْمَالُهُمْ فَأَصْبَحُوا خَاسِرِينَ', 5, 53, 6, 24, 117, 'آنها که ایمان آورده‌اند می‌گویند: «آیا این (منافقان) همانها هستند که با نهایت تأکید سوگند یاد کردند که با شما هستند؟! (چرا کارشان به اینجا رسید؟!)» (آری،) اعمالشان نابود گشت، و زیانکار شدند.'),
(723, 723, 'يَا أَيُّهَا الَّذِينَ آمَنُوا مَنْ يَرْتَدَّ مِنْكُمْ عَنْ دِينِهِ فَسَوْفَ يَأْتِي اللَّهُ بِقَوْمٍ يُحِبُّهُمْ وَيُحِبُّونَهُ أَذِلَّةٍ عَلَى الْمُؤْمِنِينَ أَعِزَّةٍ عَلَى الْكَافِرِينَ يُجَاهِدُونَ فِي سَبِيلِ اللَّهِ وَلَا يَخَافُونَ لَوْمَةَ لَائِمٍ ۚ ذَٰلِكَ فَضْلُ اللَّهِ يُؤْتِيهِ مَنْ يَشَاءُ ۚ وَاللَّهُ وَاسِعٌ عَلِيمٌ', 5, 54, 6, 24, 117, 'ای کسانی که ایمان آورده‌اید! هر کس از شما، از آیین خود بازگردد، (به خدا زیانی نمی‌رساند؛ خداوند جمعیّتی را می‌آورد که آنها را دوست دارد و آنان (نیز) او را دوست دارند، در برابر مؤمنان متواضع، و در برابر کافران سرسخت و نیرومندند؛ آنها در راه خدا جهاد می‌کنند، و از سرزنش هیچ ملامتگری هراسی ندارند. این، فضل خداست که به هر کس بخواهد (و شایسته ببیند) می‌دهد؛ و (فضل) خدا وسیع، و خداوند داناست.'),
(724, 724, 'إِنَّمَا وَلِيُّكُمُ اللَّهُ وَرَسُولُهُ وَالَّذِينَ آمَنُوا الَّذِينَ يُقِيمُونَ الصَّلَاةَ وَيُؤْتُونَ الزَّكَاةَ وَهُمْ رَاكِعُونَ', 5, 55, 6, 24, 117, 'سرپرست و ولیّ شما، تنها خداست و پیامبر او و آنها که ایمان آورده‌اند؛ همانها که نماز را برپا می‌دارند، و در حال رکوع، زکات می‌دهند.'),
(725, 725, 'وَمَنْ يَتَوَلَّ اللَّهَ وَرَسُولَهُ وَالَّذِينَ آمَنُوا فَإِنَّ حِزْبَ اللَّهِ هُمُ الْغَالِبُونَ', 5, 56, 6, 24, 117, 'و کسانی که ولایت خدا و پیامبر او و افراد باایمان را بپذیرند، پیروزند؛ (زیرا) حزب و جمعیّت خدا پیروز است.'),
(726, 726, 'يَا أَيُّهَا الَّذِينَ آمَنُوا لَا تَتَّخِذُوا الَّذِينَ اتَّخَذُوا دِينَكُمْ هُزُوًا وَلَعِبًا مِنَ الَّذِينَ أُوتُوا الْكِتَابَ مِنْ قَبْلِكُمْ وَالْكُفَّارَ أَوْلِيَاءَ ۚ وَاتَّقُوا اللَّهَ إِنْ كُنْتُمْ مُؤْمِنِينَ', 5, 57, 6, 24, 117, 'ای کسانی که ایمان آورده‌اید! افرادی که آیین شما را به باد استهزاء و بازی می‌گیرند -از اهل کتاب و مشرکان- ولیّ خود انتخاب نکنید؛ و از خدا بپرهیزید اگر ایمان دارید!'),
(727, 727, 'وَإِذَا نَادَيْتُمْ إِلَى الصَّلَاةِ اتَّخَذُوهَا هُزُوًا وَلَعِبًا ۚ ذَٰلِكَ بِأَنَّهُمْ قَوْمٌ لَا يَعْقِلُونَ', 5, 58, 6, 24, 118, 'آنها هنگامی که (اذان می‌گویید، و مردم را) به نماز فرا می‌خوانید، آن را به مسخره و بازی می‌گیرند؛ این بخاطر آن است که آنها جمعی نابخردند.'),
(728, 728, 'قُلْ يَا أَهْلَ الْكِتَابِ هَلْ تَنْقِمُونَ مِنَّا إِلَّا أَنْ آمَنَّا بِاللَّهِ وَمَا أُنْزِلَ إِلَيْنَا وَمَا أُنْزِلَ مِنْ قَبْلُ وَأَنَّ أَكْثَرَكُمْ فَاسِقُونَ', 5, 59, 6, 24, 118, 'بگو: «ای اهل کتاب! آیا به ما خرده می‌گیرید؟ (مگر ما چه کرده‌ایم) جز اینکه به خداوند یگانه، و به آنچه بر ما نازل شده، و به آنچه پیش از این نازل گردیده، ایمان آورده‌ایم. و این، بخاطر آن است که بیشتر شما، از راه حق، خارج شده‌اید.»'),
(729, 729, 'قُلْ هَلْ أُنَبِّئُكُمْ بِشَرٍّ مِنْ ذَٰلِكَ مَثُوبَةً عِنْدَ اللَّهِ ۚ مَنْ لَعَنَهُ اللَّهُ وَغَضِبَ عَلَيْهِ وَجَعَلَ مِنْهُمُ الْقِرَدَةَ وَالْخَنَازِيرَ وَعَبَدَ الطَّاغُوتَ ۚ أُولَٰئِكَ شَرٌّ مَكَانًا وَأَضَلُّ عَنْ سَوَاءِ السَّبِيلِ', 5, 60, 6, 24, 118, 'بگو: «آیا شما را از کسانی که موقعیّت و پاداششان نزد خدا بدتر از این است، با خبر کنم؟ کسانی که خداوند آنها را از رحمت خود دور ساخته، و مورد خشم قرار داده، (و مسخ کرده،) و از آنها، میمونها و خوکهایی قرار داده، و پرستش بت کرده‌اند؛ موقعیّت و محل آنها، بدتر است؛ و از راه راست، گمراهترند.»'),
(730, 730, 'وَإِذَا جَاءُوكُمْ قَالُوا آمَنَّا وَقَدْ دَخَلُوا بِالْكُفْرِ وَهُمْ قَدْ خَرَجُوا بِهِ ۚ وَاللَّهُ أَعْلَمُ بِمَا كَانُوا يَكْتُمُونَ', 5, 61, 6, 24, 118, 'هنگامی که نزد شما می‌آیند، می‌گویند: «ایمان آورده‌ایم!» (امّا) با کفر وارد می‌شوند، و با کفر خارج می‌گردند؛ و خداوند، از آنچه کتمان می‌کردند، آگاهتر است!'),
(731, 731, 'وَتَرَىٰ كَثِيرًا مِنْهُمْ يُسَارِعُونَ فِي الْإِثْمِ وَالْعُدْوَانِ وَأَكْلِهِمُ السُّحْتَ ۚ لَبِئْسَ مَا كَانُوا يَعْمَلُونَ', 5, 62, 6, 24, 118, 'بسیاری از آنان را می‌بینی که در گناه و تعدّی، و خوردن مال حرام، شتاب می‌کنند! چه زشت است کاری که انجام می‌دادند!'),
(732, 732, 'لَوْلَا يَنْهَاهُمُ الرَّبَّانِيُّونَ وَالْأَحْبَارُ عَنْ قَوْلِهِمُ الْإِثْمَ وَأَكْلِهِمُ السُّحْتَ ۚ لَبِئْسَ مَا كَانُوا يَصْنَعُونَ', 5, 63, 6, 24, 118, 'چرا دانشمندان نصاری و علمای یهود، آنها را از سخنان گناه‌آمیز و خوردن مال حرام، نهی نمی‌کنند؟! چه زشت است عملی که انجام می‌دادند!'),
(733, 733, 'وَقَالَتِ الْيَهُودُ يَدُ اللَّهِ مَغْلُولَةٌ ۚ غُلَّتْ أَيْدِيهِمْ وَلُعِنُوا بِمَا قَالُوا ۘ بَلْ يَدَاهُ مَبْسُوطَتَانِ يُنْفِقُ كَيْفَ يَشَاءُ ۚ وَلَيَزِيدَنَّ كَثِيرًا مِنْهُمْ مَا أُنْزِلَ إِلَيْكَ مِنْ رَبِّكَ طُغْيَانًا وَكُفْرًا ۚ وَأَلْقَيْنَا بَيْنَهُمُ الْعَدَاوَةَ وَالْبَغْضَاءَ إِلَىٰ يَوْمِ الْقِيَامَةِ ۚ كُلَّمَا أَوْقَدُوا نَارًا لِلْحَرْبِ أَطْفَأَهَا اللَّهُ ۚ وَيَسْعَوْنَ فِي الْأَرْضِ فَسَادًا ۚ وَاللَّهُ لَا يُحِبُّ الْمُفْسِدِينَ', 5, 64, 6, 24, 118, 'و یهود گفتند: «دست خدا (با زنجیر) بسته است.» دستهایشان بسته باد! و بخاطر این سخن، از رحمت (الهی) دور شوند! بلکه هر دو دست (قدرت) او، گشاده است؛ هرگونه بخواهد، می‌بخشد! ولی این آیات، که از طرف پروردگارت بر تو نازل شده، بر طغیان و کفر بسیاری از آنها می‌افزاید. و ما در میان آنها تا روز قیامت عداوت و دشمنی افکندیم. هر زمان آتش جنگی افروختند، خداوند آن را خاموش ساخت؛ و برای فساد در زمین، تلاش می‌کنند؛ و خداوند، مفسدان را دوست ندارد.'),
(734, 734, 'وَلَوْ أَنَّ أَهْلَ الْكِتَابِ آمَنُوا وَاتَّقَوْا لَكَفَّرْنَا عَنْهُمْ سَيِّئَاتِهِمْ وَلَأَدْخَلْنَاهُمْ جَنَّاتِ النَّعِيمِ', 5, 65, 6, 24, 119, 'و اگر اهل کتاب ایمان بیاورند و تقوا پیشه کنند، گناهان آنها را می‌بخشیم؛ و آنها را در باغهای پرنعمت بهشت، وارد می‌سازیم.'),
(735, 735, 'وَلَوْ أَنَّهُمْ أَقَامُوا التَّوْرَاةَ وَالْإِنْجِيلَ وَمَا أُنْزِلَ إِلَيْهِمْ مِنْ رَبِّهِمْ لَأَكَلُوا مِنْ فَوْقِهِمْ وَمِنْ تَحْتِ أَرْجُلِهِمْ ۚ مِنْهُمْ أُمَّةٌ مُقْتَصِدَةٌ ۖ وَكَثِيرٌ مِنْهُمْ سَاءَ مَا يَعْمَلُونَ', 5, 66, 6, 24, 119, 'و اگر آنان، تورات و انجیل و آنچه را از سوی پروردگارشان بر آنها نازل شده = قرآن‌ برپا دارند، از آسمان و زمین، روزی خواهند خورد؛ جمعی از آنها، معتدل و میانه‌رو هستند، ولی بیشترشان اعمال بدی انجام می‌دهند.'),
(736, 736, ' يَا أَيُّهَا الرَّسُولُ بَلِّغْ مَا أُنْزِلَ إِلَيْكَ مِنْ رَبِّكَ ۖ وَإِنْ لَمْ تَفْعَلْ فَمَا بَلَّغْتَ رِسَالَتَهُ ۚ وَاللَّهُ يَعْصِمُكَ مِنَ النَّاسِ ۗ إِنَّ اللَّهَ لَا يَهْدِي الْقَوْمَ الْكَافِرِينَ', 5, 67, 6, 24, 119, 'ای پیامبر! آنچه از طرف پروردگارت بر تو نازل شده است، کاملاً (به مردم) برسان! و اگر نکنی، رسالت او را انجام نداده‌ای! خداوند تو را از (خطرات احتمالی) مردم، نگاه می‌دارد؛ و خداوند، جمعیّت کافران (لجوج) را هدایت نمی‌کند.'),
(737, 737, 'قُلْ يَا أَهْلَ الْكِتَابِ لَسْتُمْ عَلَىٰ شَيْءٍ حَتَّىٰ تُقِيمُوا التَّوْرَاةَ وَالْإِنْجِيلَ وَمَا أُنْزِلَ إِلَيْكُمْ مِنْ رَبِّكُمْ ۗ وَلَيَزِيدَنَّ كَثِيرًا مِنْهُمْ مَا أُنْزِلَ إِلَيْكَ مِنْ رَبِّكَ طُغْيَانًا وَكُفْرًا ۖ فَلَا تَأْسَ عَلَى الْقَوْمِ الْكَافِرِينَ', 5, 68, 6, 24, 119, 'ای اهل کتاب! شما هیچ آیین صحیحی ندارید، مگر اینکه تورات و انجیل و آنچه را از طرف پروردگارتان بر شما نازل شده است، برپا دارید. ولی آنچه بر تو از سوی پروردگارت نازل شده، (نه تنها مایه بیداری آنها نمی‌گردد، بلکه) بر طغیان و کفر بسیاری از آنها می‌افزاید. بنابر این، از این قوم کافر، (و مخالفت آنها،) غمگین مباش!'),
(738, 738, 'إِنَّ الَّذِينَ آمَنُوا وَالَّذِينَ هَادُوا وَالصَّابِئُونَ وَالنَّصَارَىٰ مَنْ آمَنَ بِاللَّهِ وَالْيَوْمِ الْآخِرِ وَعَمِلَ صَالِحًا فَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَ', 5, 69, 6, 24, 119, 'آنها که ایمان آورده‌اند، و یهود و صابئان و مسیحیان، هرگاه به خداوند یگانه و روز جزا، ایمان بیاورند، و عمل صالح انجام دهند، نه ترسی بر آنهاست، و نه غمگین خواهند شد.'),
(739, 739, 'لَقَدْ أَخَذْنَا مِيثَاقَ بَنِي إِسْرَائِيلَ وَأَرْسَلْنَا إِلَيْهِمْ رُسُلًا ۖ كُلَّمَا جَاءَهُمْ رَسُولٌ بِمَا لَا تَهْوَىٰ أَنْفُسُهُمْ فَرِيقًا كَذَّبُوا وَفَرِيقًا يَقْتُلُونَ', 5, 70, 6, 24, 119, 'ما از بنی اسرائیل پیمان گرفتیم؛ و رسولانی به سوی آنها فرستادیم؛ (ولی) هر زمان پیامبری حکمی بر خلاف هوسها و دلخواه آنها می‌آورد، عده‌ای را تکذیب می‌کردند؛ و عده‌ای را می‌کشتند.'),
(740, 740, 'وَحَسِبُوا أَلَّا تَكُونَ فِتْنَةٌ فَعَمُوا وَصَمُّوا ثُمَّ تَابَ اللَّهُ عَلَيْهِمْ ثُمَّ عَمُوا وَصَمُّوا كَثِيرٌ مِنْهُمْ ۚ وَاللَّهُ بَصِيرٌ بِمَا يَعْمَلُونَ', 5, 71, 6, 24, 120, 'گمان کردند مجازاتی در کار نخواهد بود! از این‌رو (از دیدن حقایق و شنیدن سخنان حق،) نابینا و کر شدند؛ سپس (بیدار گشتند، و) خداوند توبه آنها را پذیرفت، دیگربار (در خواب غفلت فرو رفتند، و) بسیاری از آنها کور و کر شدند؛ و خداوند، به آنچه انجام می‌دهند، بیناست.'),
(741, 741, 'لَقَدْ كَفَرَ الَّذِينَ قَالُوا إِنَّ اللَّهَ هُوَ الْمَسِيحُ ابْنُ مَرْيَمَ ۖ وَقَالَ الْمَسِيحُ يَا بَنِي إِسْرَائِيلَ اعْبُدُوا اللَّهَ رَبِّي وَرَبَّكُمْ ۖ إِنَّهُ مَنْ يُشْرِكْ بِاللَّهِ فَقَدْ حَرَّمَ اللَّهُ عَلَيْهِ الْجَنَّةَ وَمَأْوَاهُ النَّارُ ۖ وَمَا لِلظَّالِمِينَ مِنْ أَنْصَارٍ', 5, 72, 6, 24, 120, 'آنها که گفتند: «خداوند همان مسیح بن مریم است»، بیقین کافر شدند، (با اینکه خود) مسیح گفت: ای بنی اسرائیل! خداوند یگانه را، که پروردگار من و شماست، پرستش کنید! زیرا هر کس شریکی برای خدا قرار دهد، خداوند بهشت را بر او حرام کرده است؛ و جایگاه او دوزخ است؛ و ستمکاران، یار و یاوری ندارند.'),
(742, 742, 'لَقَدْ كَفَرَ الَّذِينَ قَالُوا إِنَّ اللَّهَ ثَالِثُ ثَلَاثَةٍ ۘ وَمَا مِنْ إِلَٰهٍ إِلَّا إِلَٰهٌ وَاحِدٌ ۚ وَإِنْ لَمْ يَنْتَهُوا عَمَّا يَقُولُونَ لَيَمَسَّنَّ الَّذِينَ كَفَرُوا مِنْهُمْ عَذَابٌ أَلِيمٌ', 5, 73, 6, 24, 120, 'آنها که گفتند: «خداوند، یکی از سه خداست» (نیز) بیقین کافر شدند؛ معبودی جز معبود یگانه نیست؛ و اگر از آنچه می‌گویند دست بر ندارند، عذاب دردناکی به کافران آنها (که روی این عقیده ایستادگی کنند،) خواهد رسید.'),
(743, 743, 'أَفَلَا يَتُوبُونَ إِلَى اللَّهِ وَيَسْتَغْفِرُونَهُ ۚ وَاللَّهُ غَفُورٌ رَحِيمٌ', 5, 74, 6, 24, 120, 'آیا به سوی خدا بازنمی‌گردند، و از او طلب آمرزش نمی‌کنند؟ (در حالی که) خداوند آمرزنده مهربان است.'),
(744, 744, 'مَا الْمَسِيحُ ابْنُ مَرْيَمَ إِلَّا رَسُولٌ قَدْ خَلَتْ مِنْ قَبْلِهِ الرُّسُلُ وَأُمُّهُ صِدِّيقَةٌ ۖ كَانَا يَأْكُلَانِ الطَّعَامَ ۗ انْظُرْ كَيْفَ نُبَيِّنُ لَهُمُ الْآيَاتِ ثُمَّ انْظُرْ أَنَّىٰ يُؤْفَكُونَ', 5, 75, 6, 24, 120, 'مسیح فرزند مریم، فقط فرستاده (خدا) بود؛ پیش از وی نیز، فرستادگان دیگری بودند، مادرش، زن بسیار راستگویی بود؛ هر دو، غذا می‌خوردند؛ (با این حال، چگونه دعوی الوهیّت مسیح و پرستش مریم را دارید؟!) بنگر چگونه نشانه را برای آنها آشکار می‌سازیم! سپس بنگر چگونه از حق بازگردانده می‌شوند!'),
(745, 745, 'قُلْ أَتَعْبُدُونَ مِنْ دُونِ اللَّهِ مَا لَا يَمْلِكُ لَكُمْ ضَرًّا وَلَا نَفْعًا ۚ وَاللَّهُ هُوَ السَّمِيعُ الْعَلِيمُ', 5, 76, 6, 24, 120, 'بگو: «آیا جز خدا چیزی را می‌پرستید که مالک سود و زیان شما نیست؟! و خداوند، شنوا و داناست.»'),
(746, 746, 'قُلْ يَا أَهْلَ الْكِتَابِ لَا تَغْلُوا فِي دِينِكُمْ غَيْرَ الْحَقِّ وَلَا تَتَّبِعُوا أَهْوَاءَ قَوْمٍ قَدْ ضَلُّوا مِنْ قَبْلُ وَأَضَلُّوا كَثِيرًا وَضَلُّوا عَنْ سَوَاءِ السَّبِيلِ', 5, 77, 6, 24, 121, 'بگو: «ای اهل کتاب! در دین خود، غلوّ (و زیاده روی) نکنید! و غیر از حق نگویید! و از هوسهای جمعیّتی که پیشتر گمراه شدند و دیگران را گمراه کردند و از راه راست منحرف گشتند، پیروی ننمایید!»'),
(747, 747, 'لُعِنَ الَّذِينَ كَفَرُوا مِنْ بَنِي إِسْرَائِيلَ عَلَىٰ لِسَانِ دَاوُودَ وَعِيسَى ابْنِ مَرْيَمَ ۚ ذَٰلِكَ بِمَا عَصَوْا وَكَانُوا يَعْتَدُونَ', 5, 78, 6, 24, 121, 'کافران بنی اسرائیل، بر زبان داوود و عیسی بن مریم، لعن (و نفرین) شدند! این بخاطر آن بود که گناه کردند، و تجاوز می‌نمودند.'),
(748, 748, 'كَانُوا لَا يَتَنَاهَوْنَ عَنْ مُنْكَرٍ فَعَلُوهُ ۚ لَبِئْسَ مَا كَانُوا يَفْعَلُونَ', 5, 79, 6, 24, 121, 'آنها از اعمال زشتی که انجام می‌دادند، یکدیگر را نهی نمی‌کردند؛ چه بدکاری انجام می‌دادند!'),
(749, 749, 'تَرَىٰ كَثِيرًا مِنْهُمْ يَتَوَلَّوْنَ الَّذِينَ كَفَرُوا ۚ لَبِئْسَ مَا قَدَّمَتْ لَهُمْ أَنْفُسُهُمْ أَنْ سَخِطَ اللَّهُ عَلَيْهِمْ وَفِي الْعَذَابِ هُمْ خَالِدُونَ', 5, 80, 6, 24, 121, 'بسیاری از آنها را می‌بینی که کافران (و بت‌پرستان) را دوست می‌دارند (و با آنها طرح دوستی می‌ریزند)؛ نفس (سرکش) آنها، چه بد اعمالی از پیش برای (معاد) آنها فرستاد! که نتیجه آن، خشم خداوند بود؛ و در عذاب (الهی) جاودانه خواهند ماند.'),
(750, 750, 'وَلَوْ كَانُوا يُؤْمِنُونَ بِاللَّهِ وَالنَّبِيِّ وَمَا أُنْزِلَ إِلَيْهِ مَا اتَّخَذُوهُمْ أَوْلِيَاءَ وَلَٰكِنَّ كَثِيرًا مِنْهُمْ فَاسِقُونَ', 5, 81, 6, 24, 121, 'و اگر به خدا و پیامبر (ص) و آنچه بر او نازل شده، ایمان می‌آوردند، (هرگز) آنان = کافران‌ را به دوستی اختیار نمی‌کردند؛ ولی بسیاری از آنها فاسقند.'),
(751, 751, ' لَتَجِدَنَّ أَشَدَّ النَّاسِ عَدَاوَةً لِلَّذِينَ آمَنُوا الْيَهُودَ وَالَّذِينَ أَشْرَكُوا ۖ وَلَتَجِدَنَّ أَقْرَبَهُمْ مَوَدَّةً لِلَّذِينَ آمَنُوا الَّذِينَ قَالُوا إِنَّا نَصَارَىٰ ۚ ذَٰلِكَ بِأَنَّ مِنْهُمْ قِسِّيسِينَ وَرُهْبَانًا وَأَنَّهُمْ لَا يَسْتَكْبِرُونَ', 5, 82, 7, 25, 121, 'بطور مسلّم، دشمنترین مردم نسبت به مؤمنان را، یهود و مشرکان خواهی یافت؛ و نزدیکترین دوستان به مؤمنان را کسانی می‌یابی که می‌گویند: «ما نصاری هستیم»؛ این بخاطر آن است که در میان آنها، افرادی عالم و تارک دنیا هستند؛ و آنها (در برابر حق) تکبّر نمی‌ورزند.'),
(752, 752, 'وَإِذَا سَمِعُوا مَا أُنْزِلَ إِلَى الرَّسُولِ تَرَىٰ أَعْيُنَهُمْ تَفِيضُ مِنَ الدَّمْعِ مِمَّا عَرَفُوا مِنَ الْحَقِّ ۖ يَقُولُونَ رَبَّنَا آمَنَّا فَاكْتُبْنَا مَعَ الشَّاهِدِينَ', 5, 83, 7, 25, 122, 'و هر زمان آیاتی را که بر پیامبر (اسلام) نازل شده بشنوند، چشمهای آنها را می‌بینی که (از شوق،) اشک می‌ریزد، بخاطر حقیقتی که دریافته‌اند؛ آنها می‌گویند: «پروردگارا! ایمان آوردیم؛ پس ما را با گواهان (و شاهدان حق، در زمره یاران محمد) بنویس!'),
(753, 753, 'وَمَا لَنَا لَا نُؤْمِنُ بِاللَّهِ وَمَا جَاءَنَا مِنَ الْحَقِّ وَنَطْمَعُ أَنْ يُدْخِلَنَا رَبُّنَا مَعَ الْقَوْمِ الصَّالِحِينَ', 5, 84, 7, 25, 122, 'چرا ما به خدا و آنچه از حق به ما رسیده است، ایمان نیاوریم، در حالی که آرزو داریم پروردگارمان ما را در زمره صالحان قرار دهد؟!»'),
(754, 754, 'فَأَثَابَهُمُ اللَّهُ بِمَا قَالُوا جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهَارُ خَالِدِينَ فِيهَا ۚ وَذَٰلِكَ جَزَاءُ الْمُحْسِنِينَ', 5, 85, 7, 25, 122, 'خداوند بخاطر این سخن، به آنها باغهایی از بهشت پاداش داد که از زیر درختانش، نهرها جاری است؛ جاودانه در آن خواهند ماند؛ و این است جزای نیکوکاران!'),
(755, 755, 'وَالَّذِينَ كَفَرُوا وَكَذَّبُوا بِآيَاتِنَا أُولَٰئِكَ أَصْحَابُ الْجَحِيمِ', 5, 86, 7, 25, 122, 'و کسانی که کافر شدند و آیات ما را تکذیب کردند، همانها اهل دوزخند.'),
(756, 756, 'يَا أَيُّهَا الَّذِينَ آمَنُوا لَا تُحَرِّمُوا طَيِّبَاتِ مَا أَحَلَّ اللَّهُ لَكُمْ وَلَا تَعْتَدُوا ۚ إِنَّ اللَّهَ لَا يُحِبُّ الْمُعْتَدِينَ', 5, 87, 7, 25, 122, 'ای کسانی که ایمان آورده‌اید! چیزهای پاکیزه را که خداوند برای شما حلال کرده است، حرام نکنید! و از حدّ، تجاوز ننمایید! زیرا خداوند متجاوزان را دوست نمی‌دارد.'),
(757, 757, 'وَكُلُوا مِمَّا رَزَقَكُمُ اللَّهُ حَلَالًا طَيِّبًا ۚ وَاتَّقُوا اللَّهَ الَّذِي أَنْتُمْ بِهِ مُؤْمِنُونَ', 5, 88, 7, 25, 122, 'و از نعمتهای حلال و پاکیزه‌ای که خداوند به شما روزی داده است، بخورید! و از (مخالفت) خداوندی که به او ایمان دارید، بپرهیزید!'),
(758, 758, 'لَا يُؤَاخِذُكُمُ اللَّهُ بِاللَّغْوِ فِي أَيْمَانِكُمْ وَلَٰكِنْ يُؤَاخِذُكُمْ بِمَا عَقَّدْتُمُ الْأَيْمَانَ ۖ فَكَفَّارَتُهُ إِطْعَامُ عَشَرَةِ مَسَاكِينَ مِنْ أَوْسَطِ مَا تُطْعِمُونَ أَهْلِيكُمْ أَوْ كِسْوَتُهُمْ أَوْ تَحْرِيرُ رَقَبَةٍ ۖ فَمَنْ لَمْ يَجِدْ فَصِيَامُ ثَلَاثَةِ أَيَّامٍ ۚ ذَٰلِكَ كَفَّارَةُ أَيْمَانِكُمْ إِذَا حَلَفْتُمْ ۚ وَاحْفَظُوا أَيْمَانَكُمْ ۚ كَذَٰلِكَ يُبَيِّنُ اللَّهُ لَكُمْ آيَاتِهِ لَعَلَّكُمْ تَشْكُرُونَ', 5, 89, 7, 25, 122, 'خداوند شما را بخاطر سوگندهای بیهوده (و خالی از اراده،) مؤاخذه نمی‌کند؛ ولی در برابر سوگندهایی که (از روی اراده) محکم کرده‌اید، مؤاخذه می‌نماید. کفاره این‌گونه قسمها، اطعام ده نفر مستمند، از غذاهای معمولی است که به خانواده خود می‌دهید؛ یا لباس پوشاندن بر آن ده نفر؛ و یا آزاد کردن یک برده؛ و کسی که هیچ کدام از اینها را نیابد، سه روز روزه می‌گیرد؛ این، کفاره سوگندهای شماست به هنگامی که سوگند یاد می‌کنید (و مخالفت می‌نمایید). و سوگندهای خود را حفظ کنید (و نشکنید!) خداوند آیات خود را این چنین برای شما بیان می‌کند، شاید شکر او را بجا آورید!'),
(759, 759, 'يَا أَيُّهَا الَّذِينَ آمَنُوا إِنَّمَا الْخَمْرُ وَالْمَيْسِرُ وَالْأَنْصَابُ وَالْأَزْلَامُ رِجْسٌ مِنْ عَمَلِ الشَّيْطَانِ فَاجْتَنِبُوهُ لَعَلَّكُمْ تُفْلِحُونَ', 5, 90, 7, 25, 123, 'ای کسانی که ایمان آورده‌اید! شراب و قمار و بتها و ازلام = نوعی بخت‌آزمایی، پلید و از عمل شیطان است، از آنها دوری کنید تا رستگار شوید!'),
(760, 760, 'إِنَّمَا يُرِيدُ الشَّيْطَانُ أَنْ يُوقِعَ بَيْنَكُمُ الْعَدَاوَةَ وَالْبَغْضَاءَ فِي الْخَمْرِ وَالْمَيْسِرِ وَيَصُدَّكُمْ عَنْ ذِكْرِ اللَّهِ وَعَنِ الصَّلَاةِ ۖ فَهَلْ أَنْتُمْ مُنْتَهُونَ', 5, 91, 7, 25, 123, 'شیطان می‌خواهد به وسیله شراب و قمار، در میان شما عداوت و کینه ایجاد کند، و شما را از یاد خدا و از نماز بازدارد. آیا (با این همه زیان و فساد، و با این نهی اکید،) خودداری خواهید کرد؟!'),
(761, 761, 'وَأَطِيعُوا اللَّهَ وَأَطِيعُوا الرَّسُولَ وَاحْذَرُوا ۚ فَإِنْ تَوَلَّيْتُمْ فَاعْلَمُوا أَنَّمَا عَلَىٰ رَسُولِنَا الْبَلَاغُ الْمُبِينُ', 5, 92, 7, 25, 123, 'اطاعت خدا و اطاعت پیامبر کنید! و (از مخالفت فرمان او) بترسید! و اگر روی برگردانید، (مستحق مجازات خواهید بود؛ و) بدانید بر پیامبر ما، جز ابلاغ آشکار، چیز دیگری نیست (و این وظیفه را در برابر شما، انجام داده است).'),
(762, 762, 'لَيْسَ عَلَى الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ جُنَاحٌ فِيمَا طَعِمُوا إِذَا مَا اتَّقَوْا وَآمَنُوا وَعَمِلُوا الصَّالِحَاتِ ثُمَّ اتَّقَوْا وَآمَنُوا ثُمَّ اتَّقَوْا وَأَحْسَنُوا ۗ وَاللَّهُ يُحِبُّ الْمُحْسِنِينَ', 5, 93, 7, 25, 123, 'بر کسانی که ایمان آورده و اعمال صالح انجام داده‌اند، گناهی در آنچه خورده‌اند نیست؛ (و نسبت به نوشیدن شراب، قبل از نزول حکم تحریم، مجازات نمی‌شوند؛) اگر تقوا پیشه کنند، و ایمان بیاورند، و اعمال صالح انجام دهند؛ سپس تقوا پیشه کنند و ایمان آورند؛ سپس تقوا پیشه کنند و نیکی نمایند. و خداوند، نیکوکاران را دوست می‌دارد.'),
(763, 763, 'يَا أَيُّهَا الَّذِينَ آمَنُوا لَيَبْلُوَنَّكُمُ اللَّهُ بِشَيْءٍ مِنَ الصَّيْدِ تَنَالُهُ أَيْدِيكُمْ وَرِمَاحُكُمْ لِيَعْلَمَ اللَّهُ مَنْ يَخَافُهُ بِالْغَيْبِ ۚ فَمَنِ اعْتَدَىٰ بَعْدَ ذَٰلِكَ فَلَهُ عَذَابٌ أَلِيمٌ', 5, 94, 7, 25, 123, 'ای کسانی که ایمان آورده‌اید! خداوند شما را به چیزی از شکار که (به نزدیکی شما می‌آید، بطوری که) دستها و نیزه‌هایتان به آن می‌رسد، می‌آزماید؛ تا معلوم شود چه کسی باایمان به غیب، از خدا می‌ترسد؛ و هر کس بعد از آن تجاوز کند، مجازات دردناکی خواهد داشت.'),
(764, 764, 'يَا أَيُّهَا الَّذِينَ آمَنُوا لَا تَقْتُلُوا الصَّيْدَ وَأَنْتُمْ حُرُمٌ ۚ وَمَنْ قَتَلَهُ مِنْكُمْ مُتَعَمِّدًا فَجَزَاءٌ مِثْلُ مَا قَتَلَ مِنَ النَّعَمِ يَحْكُمُ بِهِ ذَوَا عَدْلٍ مِنْكُمْ هَدْيًا بَالِغَ الْكَعْبَةِ أَوْ كَفَّارَةٌ طَعَامُ مَسَاكِينَ أَوْ عَدْلُ ذَٰلِكَ صِيَامًا لِيَذُوقَ وَبَالَ أَمْرِهِ ۗ عَفَا اللَّهُ عَمَّا سَلَفَ ۚ وَمَنْ عَادَ فَيَنْتَقِمُ اللَّهُ مِنْهُ ۗ وَاللَّهُ عَزِيزٌ ذُو انْتِقَامٍ', 5, 95, 7, 25, 123, 'ای کسانی که ایمان آورده‌اید! در حال احرام، شکار نکنید، و هر کس از شما عمداً آن را به قتل برساند، باید کفاره‌ای معادل آن از چهارپایان بدهد؛ کفاره‌ای که دو نفر عادل از شما، معادل بودن آن را تصدیق کنند؛ و به صورت قربانی به (حریم) کعبه برسد؛ یا (به جای قربانی،) اطعام مستمندان کند؛ یا معادل آن، روزه بگیرد، تا کیفر کار خود را بچشد. خداوند گذشته را عفو کرده، ولی هر کس تکرار کند، خدا از او انتقام می‌گیرد؛ و خداوند، توانا و صاحب انتقام است.'),
(765, 765, 'أُحِلَّ لَكُمْ صَيْدُ الْبَحْرِ وَطَعَامُهُ مَتَاعًا لَكُمْ وَلِلسَّيَّارَةِ ۖ وَحُرِّمَ عَلَيْكُمْ صَيْدُ الْبَرِّ مَا دُمْتُمْ حُرُمًا ۗ وَاتَّقُوا اللَّهَ الَّذِي إِلَيْهِ تُحْشَرُونَ', 5, 96, 7, 25, 124, 'صید دریا و طعام آن برای شما و کاروانیان حلال است؛ تا (در حال احرام) از آن بهره‌مند شوید؛ ولی مادام که محرم هستید، شکار صحرا برای شما حرام است؛ و از (نافرمانی) خدایی که به سوی او محشور می‌شوید، بترسید!'),
(766, 766, ' جَعَلَ اللَّهُ الْكَعْبَةَ الْبَيْتَ الْحَرَامَ قِيَامًا لِلنَّاسِ وَالشَّهْرَ الْحَرَامَ وَالْهَدْيَ وَالْقَلَائِدَ ۚ ذَٰلِكَ لِتَعْلَمُوا أَنَّ اللَّهَ يَعْلَمُ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ وَأَنَّ اللَّهَ بِكُلِّ شَيْءٍ عَلِيمٌ', 5, 97, 7, 25, 124, 'خداوند، کعبه -بیت الحرام- را وسیله‌ای برای استواری و سامان بخشیدن به کار مردم قرار داده؛ و همچنین ماه حرام، و قربانیهای بی‌نشان، و قربانیهای نشاندار را؛ این‌گونه احکام (حساب شده و دقیق،) بخاطر آن است که بدانید خداوند، آنچه در آسمانها و آنچه در زمین است، می‌داند؛ و خدا به هر چیزی داناست.'),
(767, 767, 'اعْلَمُوا أَنَّ اللَّهَ شَدِيدُ الْعِقَابِ وَأَنَّ اللَّهَ غَفُورٌ رَحِيمٌ', 5, 98, 7, 25, 124, 'بدانید خدا دارای مجازات شدید، و (در عین حال) آمرزنده و مهربان است.'),
(768, 768, 'مَا عَلَى الرَّسُولِ إِلَّا الْبَلَاغُ ۗ وَاللَّهُ يَعْلَمُ مَا تُبْدُونَ وَمَا تَكْتُمُونَ', 5, 99, 7, 25, 124, 'پیامبر وظیفه‌ای جز رسانیدن پیام (الهی) ندارد؛ (و مسؤول اعمال شما نیست). و خداوند آنچه را آشکار، و آنچه را پنهان می‌دارید می‌داند.'),
(769, 769, 'قُلْ لَا يَسْتَوِي الْخَبِيثُ وَالطَّيِّبُ وَلَوْ أَعْجَبَكَ كَثْرَةُ الْخَبِيثِ ۚ فَاتَّقُوا اللَّهَ يَا أُولِي الْأَلْبَابِ لَعَلَّكُمْ تُفْلِحُونَ', 5, 100, 7, 25, 124, 'بگو: «(هیچ‌گاه) ناپاک و پاک مساوی نیستند؛ هر چند فزونی ناپاکها، تو را به شگفتی اندازد! از (مخالفت) خدا بپرهیزید ای صاحبان خرد، شاید رستگار شوید!'),
(770, 770, 'يَا أَيُّهَا الَّذِينَ آمَنُوا لَا تَسْأَلُوا عَنْ أَشْيَاءَ إِنْ تُبْدَ لَكُمْ تَسُؤْكُمْ وَإِنْ تَسْأَلُوا عَنْهَا حِينَ يُنَزَّلُ الْقُرْآنُ تُبْدَ لَكُمْ عَفَا اللَّهُ عَنْهَا ۗ وَاللَّهُ غَفُورٌ حَلِيمٌ', 5, 101, 7, 25, 124, 'ای کسانی که ایمان آورده‌اید! از چیزهایی نپرسید که اگر برای شما آشکار گردد، شما را ناراحت می‌کند! و اگر به هنگام نزول قرآن، از آنها سؤال کنید، برای شما آشکار می‌شود؛ خداوند آنها را بخشیده (و نادیده گرفته) است. و خداوند، آمرزنده و بردبار است.'),
(771, 771, 'قَدْ سَأَلَهَا قَوْمٌ مِنْ قَبْلِكُمْ ثُمَّ أَصْبَحُوا بِهَا كَافِرِينَ', 5, 102, 7, 25, 124, 'جمعی از پیشینیان شما، از آن سئوال کردند؛ و سپس با آن به مخالفت برخاستند. (ممکن است شما هم چنین سرنوشتی پیدا کنید.)'),
(772, 772, 'مَا جَعَلَ اللَّهُ مِنْ بَحِيرَةٍ وَلَا سَائِبَةٍ وَلَا وَصِيلَةٍ وَلَا حَامٍ ۙ وَلَٰكِنَّ الَّذِينَ كَفَرُوا يَفْتَرُونَ عَلَى اللَّهِ الْكَذِبَ ۖ وَأَكْثَرُهُمْ لَا يَعْقِلُونَ', 5, 103, 7, 25, 124, 'خداوند هیچ‌گونه «بحیره» و «سائبه» و «وصیله» و «حام» قرار نداده است اشاره به چهار نوع از حیوانات اهلی است که در زمان جاهلیت، استفاده از آنها را بعللی حرام می‌دانستند؛ و این بدعت، در اسلام ممنوع شد. ولی کسانی که کافر شدند، بر خدا دروغ می‌بندند؛ و بیشتر آنها نمی‌فهمند!'),
(773, 773, 'وَإِذَا قِيلَ لَهُمْ تَعَالَوْا إِلَىٰ مَا أَنْزَلَ اللَّهُ وَإِلَى الرَّسُولِ قَالُوا حَسْبُنَا مَا وَجَدْنَا عَلَيْهِ آبَاءَنَا ۚ أَوَلَوْ كَانَ آبَاؤُهُمْ لَا يَعْلَمُونَ شَيْئًا وَلَا يَهْتَدُونَ', 5, 104, 7, 25, 125, 'و هنگامی که به آنها گفته شود: «به سوی آنچه خدا نازل کرده، و به سوی پیامبر بیایید!»، می‌گویند: «آنچه از پدران خود یافته‌ایم، ما را بس است!»؛ آیا اگر پدران آنها چیزی نمی‌دانستند، و هدایت نیافته بودند (باز از آنها پیروی می‌کنند)؟!'),
(774, 774, 'يَا أَيُّهَا الَّذِينَ آمَنُوا عَلَيْكُمْ أَنْفُسَكُمْ ۖ لَا يَضُرُّكُمْ مَنْ ضَلَّ إِذَا اهْتَدَيْتُمْ ۚ إِلَى اللَّهِ مَرْجِعُكُمْ جَمِيعًا فَيُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُونَ', 5, 105, 7, 25, 125, 'ای کسانی که ایمان آورده‌اید! مراقب خود باشید! اگر شما هدایت یافته‌اید، گمراهی کسانی که گمراه شده‌اند، به شما زیانی نمی‌رساند. بازگشت همه شما به سوی خداست؛ و شما را از آنچه عمل می‌کردید، آگاه می‌سازد.'),
(775, 775, 'يَا أَيُّهَا الَّذِينَ آمَنُوا شَهَادَةُ بَيْنِكُمْ إِذَا حَضَرَ أَحَدَكُمُ الْمَوْتُ حِينَ الْوَصِيَّةِ اثْنَانِ ذَوَا عَدْلٍ مِنْكُمْ أَوْ آخَرَانِ مِنْ غَيْرِكُمْ إِنْ أَنْتُمْ ضَرَبْتُمْ فِي الْأَرْضِ فَأَصَابَتْكُمْ مُصِيبَةُ الْمَوْتِ ۚ تَحْبِسُونَهُمَا مِنْ بَعْدِ الصَّلَاةِ فَيُقْسِمَانِ بِاللَّهِ إِنِ ارْتَبْتُمْ لَا نَشْتَرِي بِهِ ثَمَنًا وَلَوْ كَانَ ذَا قُرْبَىٰ ۙ وَلَا نَكْتُمُ شَهَادَةَ اللَّهِ إِنَّا إِذًا لَمِنَ الْآثِمِينَ', 5, 106, 7, 25, 125, 'ای کسانی که ایمان آورده‌اید! هنگامی که مرگ یکی از شما فرا رسد، در موقع وصیت باید از میان شما، دو نفر عادل را به شهادت بطلبد؛ یا اگر مسافرت کردید، و مصیبت مرگ شما فرا رسید، (و در آن جا مسلمانی نیافتید،) دو نفر از غیر خودتان را به گواهی بطلبید، و اگر به هنگام ادای شهادت، در صدق آنها شک کردید، آنها را بعد از نماز نگاه می‌دارید تا سوگند یاد کنند که: «ما حاضر نیستیم حق را به چیزی بفروشیم، هر چند در مورد خویشاوندان ما باشد! و شهادت الهی را کتمان نمی‌کنیم، که از گناهکاران خواهیم بود!»');
INSERT INTO `Quran` (`rowid`, `id`, `Arabic`, `Chapter`, `Verse`, `Juz`, `Hizb`, `Page`, `tr`) VALUES
(776, 776, 'فَإِنْ عُثِرَ عَلَىٰ أَنَّهُمَا اسْتَحَقَّا إِثْمًا فَآخَرَانِ يَقُومَانِ مَقَامَهُمَا مِنَ الَّذِينَ اسْتَحَقَّ عَلَيْهِمُ الْأَوْلَيَانِ فَيُقْسِمَانِ بِاللَّهِ لَشَهَادَتُنَا أَحَقُّ مِنْ شَهَادَتِهِمَا وَمَا اعْتَدَيْنَا إِنَّا إِذًا لَمِنَ الظَّالِمِينَ', 5, 107, 7, 25, 125, 'و اگر اطلاعی حاصل شود که آن دو، مرتکب گناهی شده‌اند (و حق را کتمان کرده‌اند)، دو نفر از کسانی که نسبت به میت، اولی هستند، به جای آنها قرار می‌گیرند، و به خدا سوگند یاد می‌کنند که: «گواهی ما، از گواهی آن دو، به حق نزدیکتر است! و ما تجاوزی نکرده‌ایم؛ که اگر چنین کرده باشیم، از ظالمان خواهیم بود!»'),
(777, 777, 'ذَٰلِكَ أَدْنَىٰ أَنْ يَأْتُوا بِالشَّهَادَةِ عَلَىٰ وَجْهِهَا أَوْ يَخَافُوا أَنْ تُرَدَّ أَيْمَانٌ بَعْدَ أَيْمَانِهِمْ ۗ وَاتَّقُوا اللَّهَ وَاسْمَعُوا ۗ وَاللَّهُ لَا يَهْدِي الْقَوْمَ الْفَاسِقِينَ', 5, 108, 7, 25, 125, 'این کار، نزدیکتر است به اینکه گواهی بحق دهند، (و از خدا بترسند،) و یا (از مردم) بترسند که (دروغشان فاش گردد، و) سوگندهایی جای سوگندهای آنها را بگیرد. از (مخالفت) خدا بپرهیزید، و گوش فرا دهید! و خداوند، جمعیّت فاسقان را هدایت نمی‌کند.'),
(778, 778, ' يَوْمَ يَجْمَعُ اللَّهُ الرُّسُلَ فَيَقُولُ مَاذَا أُجِبْتُمْ ۖ قَالُوا لَا عِلْمَ لَنَا ۖ إِنَّكَ أَنْتَ عَلَّامُ الْغُيُوبِ', 5, 109, 7, 26, 126, '(از) روزی (بترسید) که خداوند، پیامبران را جمع می‌کند، و به آنها می‌گوید: «(در برابر دعوت شما،) چه پاسخی به شما داده شد؟»، می‌گویند: «ما چیزی نمی‌دانیم؛ تو خود، از همه اسرار نهان آگاهی.»'),
(779, 779, 'إِذْ قَالَ اللَّهُ يَا عِيسَى ابْنَ مَرْيَمَ اذْكُرْ نِعْمَتِي عَلَيْكَ وَعَلَىٰ وَالِدَتِكَ إِذْ أَيَّدْتُكَ بِرُوحِ الْقُدُسِ تُكَلِّمُ النَّاسَ فِي الْمَهْدِ وَكَهْلًا ۖ وَإِذْ عَلَّمْتُكَ الْكِتَابَ وَالْحِكْمَةَ وَالتَّوْرَاةَ وَالْإِنْجِيلَ ۖ وَإِذْ تَخْلُقُ مِنَ الطِّينِ كَهَيْئَةِ الطَّيْرِ بِإِذْنِي فَتَنْفُخُ فِيهَا فَتَكُونُ طَيْرًا بِإِذْنِي ۖ وَتُبْرِئُ الْأَكْمَهَ وَالْأَبْرَصَ بِإِذْنِي ۖ وَإِذْ تُخْرِجُ الْمَوْتَىٰ بِإِذْنِي ۖ وَإِذْ كَفَفْتُ بَنِي إِسْرَائِيلَ عَنْكَ إِذْ جِئْتَهُمْ بِالْبَيِّنَاتِ فَقَالَ الَّذِينَ كَفَرُوا مِنْهُمْ إِنْ هَٰذَا إِلَّا سِحْرٌ مُبِينٌ', 5, 110, 7, 26, 126, '(به خاطر بیاور) هنگامی را که خداوند به عیسی بن مریم گفت: «یاد کن نعمتی را که به تو و مادرت بخشیدم! زمانی که تو را با» روح القدس «تقویت کردم؛ که در گاهواره و به هنگام بزرگی، با مردم سخن می‌گفتی؛ و هنگامی که کتاب و حکمت و تورات و انجیل را به تو آموختم؛ و هنگامی که به فرمان من، از گل چیزی بصورت پرنده می‌ساختی، و در آن می‌دمیدی، و به فرمان من، پرنده‌ای می‌شد؛ و کور مادرزاد، و مبتلا به بیماری پیسی را به فرمان من، شفا می‌دادی؛ و مردگان را (نیز) به فرمان من زنده می‌کردی؛ و هنگامی که بنی اسرائیل را از آسیب رساندن به تو، بازداشتم؛ در آن موقع که دلایل روشن برای آنها آوردی، ولی جمعی از کافران آنها گفتند: اینها جز سحر آشکار نیست!»'),
(780, 780, 'وَإِذْ أَوْحَيْتُ إِلَى الْحَوَارِيِّينَ أَنْ آمِنُوا بِي وَبِرَسُولِي قَالُوا آمَنَّا وَاشْهَدْ بِأَنَّنَا مُسْلِمُونَ', 5, 111, 7, 26, 126, 'و (به یاد آور) زمانی را که به حواریون وحی فرستادم که: «به من و فرستاده من، ایمان بیاورید!» آنها گفتند: «ایمان آوردیم، و گواه باش که ما مسلمانیم!»'),
(781, 781, 'إِذْ قَالَ الْحَوَارِيُّونَ يَا عِيسَى ابْنَ مَرْيَمَ هَلْ يَسْتَطِيعُ رَبُّكَ أَنْ يُنَزِّلَ عَلَيْنَا مَائِدَةً مِنَ السَّمَاءِ ۖ قَالَ اتَّقُوا اللَّهَ إِنْ كُنْتُمْ مُؤْمِنِينَ', 5, 112, 7, 26, 126, 'در آن هنگام که حواریون گفتند: «ای عیسی بن مریم! آیا پروردگارت می‌تواند مائده‌ای از آسمان بر ما نازل کند؟» او (در پاسخ) گفت: «از خدا بپرهیزید اگر با ایمان هستید!»'),
(782, 782, 'قَالُوا نُرِيدُ أَنْ نَأْكُلَ مِنْهَا وَتَطْمَئِنَّ قُلُوبُنَا وَنَعْلَمَ أَنْ قَدْ صَدَقْتَنَا وَنَكُونَ عَلَيْهَا مِنَ الشَّاهِدِينَ', 5, 113, 7, 26, 126, 'گفتند: «(ما نظر بدی نداریم،) می‌خواهیم از آن بخوریم، و دلهای ما (به رسالت تو) مطمئن گردد؛ و بدانیم به ما راست گفته‌ای؛ و بر آن، گواه باشیم.»'),
(783, 783, 'قَالَ عِيسَى ابْنُ مَرْيَمَ اللَّهُمَّ رَبَّنَا أَنْزِلْ عَلَيْنَا مَائِدَةً مِنَ السَّمَاءِ تَكُونُ لَنَا عِيدًا لِأَوَّلِنَا وَآخِرِنَا وَآيَةً مِنْكَ ۖ وَارْزُقْنَا وَأَنْتَ خَيْرُ الرَّازِقِينَ', 5, 114, 7, 26, 127, 'عیسی بن مریم عرض کرد: «خداوندا! پروردگارا! از آسمان مائده‌ای بر ما بفرست! تا برای اول و آخر ما، عیدی باشد، و نشانه‌ای از تو؛ و به ما روزی ده! تو بهترین روزی دهندگانی!»'),
(784, 784, 'قَالَ اللَّهُ إِنِّي مُنَزِّلُهَا عَلَيْكُمْ ۖ فَمَنْ يَكْفُرْ بَعْدُ مِنْكُمْ فَإِنِّي أُعَذِّبُهُ عَذَابًا لَا أُعَذِّبُهُ أَحَدًا مِنَ الْعَالَمِينَ', 5, 115, 7, 26, 127, 'خداوند (دعای او را مستجاب کرد؛ و) فرمود: «من آن را بر شما نازل می‌کنم؛ ولی هر کس از شما بعد از آن کافر گردد (و راه انکار پوید)، او را مجازاتی می‌کنم که احدی از جهانیان را چنان مجازات نکرده باشم!»'),
(785, 785, 'وَإِذْ قَالَ اللَّهُ يَا عِيسَى ابْنَ مَرْيَمَ أَأَنْتَ قُلْتَ لِلنَّاسِ اتَّخِذُونِي وَأُمِّيَ إِلَٰهَيْنِ مِنْ دُونِ اللَّهِ ۖ قَالَ سُبْحَانَكَ مَا يَكُونُ لِي أَنْ أَقُولَ مَا لَيْسَ لِي بِحَقٍّ ۚ إِنْ كُنْتُ قُلْتُهُ فَقَدْ عَلِمْتَهُ ۚ تَعْلَمُ مَا فِي نَفْسِي وَلَا أَعْلَمُ مَا فِي نَفْسِكَ ۚ إِنَّكَ أَنْتَ عَلَّامُ الْغُيُوبِ', 5, 116, 7, 26, 127, 'و آنگاه که خداوند به عیسی بن مریم می‌گوید: «آیا تو به مردم گفتی که من و مادرم را بعنوان دو معبود غیر از خدا انتخاب کنید؟!»، او می‌گوید: «منزهی تو! من حق ندارم آنچه را که شایسته من نیست، بگویم! اگر چنین سخنی را گفته باشم، تو می‌دانی! تو از آنچه در روح و جان من است، آگاهی؛ و من از آنچه در ذات (پاک) توست، آگاه نیستم! بیقین تو از تمام اسرار و پنهانیها باخبری.'),
(786, 786, 'مَا قُلْتُ لَهُمْ إِلَّا مَا أَمَرْتَنِي بِهِ أَنِ اعْبُدُوا اللَّهَ رَبِّي وَرَبَّكُمْ ۚ وَكُنْتُ عَلَيْهِمْ شَهِيدًا مَا دُمْتُ فِيهِمْ ۖ فَلَمَّا تَوَفَّيْتَنِي كُنْتَ أَنْتَ الرَّقِيبَ عَلَيْهِمْ ۚ وَأَنْتَ عَلَىٰ كُلِّ شَيْءٍ شَهِيدٌ', 5, 117, 7, 26, 127, 'من، جز آنچه مرا به آن فرمان دادی، چیزی به آنها نگفتم؛ (به آنها گفتم:) خداوندی را بپرستید که پروردگار من و پروردگار شماست! و تا زمانی که در میان آنها بودم، مراقب و گواهشان بودم؛ ولی هنگامی که مرا از میانشان برگرفتی، تو خود مراقب آنها بودی؛ و تو بر هر چیز، گواهی!'),
(787, 787, 'إِنْ تُعَذِّبْهُمْ فَإِنَّهُمْ عِبَادُكَ ۖ وَإِنْ تَغْفِرْ لَهُمْ فَإِنَّكَ أَنْتَ الْعَزِيزُ الْحَكِيمُ', 5, 118, 7, 26, 127, '(با این حال،) اگر آنها را مجازات کنی، بندگان تواند. (و قادر به فرار از مجازات تو نیستند)؛ و اگر آنان را ببخشی، توانا و حکیمی! (نه کیفر تو نشانه بی‌حکمتی است، و نه بخشش تو نشانه ضعف!)»'),
(788, 788, 'قَالَ اللَّهُ هَٰذَا يَوْمُ يَنْفَعُ الصَّادِقِينَ صِدْقُهُمْ ۚ لَهُمْ جَنَّاتٌ تَجْرِي مِنْ تَحْتِهَا الْأَنْهَارُ خَالِدِينَ فِيهَا أَبَدًا ۚ رَضِيَ اللَّهُ عَنْهُمْ وَرَضُوا عَنْهُ ۚ ذَٰلِكَ الْفَوْزُ الْعَظِيمُ', 5, 119, 7, 26, 127, 'خداوند می‌گوید: «امروز، روزی است که راستی راستگویان، به آنها سود می‌بخشد؛ برای آنها باغهایی از بهشت است که نهرها از زیر (درختان) آن می‌گذرد، و تا ابد، جاودانه در آن می‌مانند؛ هم خداوند از آنها خشنود است، و هم آنها از خدا خشنودند؛ این، رستگاری بزرگ است!»'),
(789, 789, 'لِلَّهِ مُلْكُ السَّمَاوَاتِ وَالْأَرْضِ وَمَا فِيهِنَّ ۚ وَهُوَ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ', 5, 120, 7, 26, 127, 'حکومت آسمانها و زمین و آنچه در آنهاست، از آن خداست؛ و او بر هر چیزی تواناست.'),
(790, 790, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ الْحَمْدُ لِلَّهِ الَّذِي خَلَقَ السَّمَاوَاتِ وَالْأَرْضَ وَجَعَلَ الظُّلُمَاتِ وَالنُّورَ ۖ ثُمَّ الَّذِينَ كَفَرُوا بِرَبِّهِمْ يَعْدِلُونَ', 6, 1, 7, 26, 128, 'ستایش برای خداوندی است که آسمانها و زمین را آفرید، و ظلمتها و نور را پدید آورد؛ اما کافران برای پروردگار خود، شریک و شبیه قرارمی‌دهند (با اینکه دلایل توحید و یگانگی او، در آفرینش جهان آشکار است)!'),
(791, 791, 'هُوَ الَّذِي خَلَقَكُمْ مِنْ طِينٍ ثُمَّ قَضَىٰ أَجَلًا ۖ وَأَجَلٌ مُسَمًّى عِنْدَهُ ۖ ثُمَّ أَنْتُمْ تَمْتَرُونَ', 6, 2, 7, 26, 128, 'او کسی است که شما را از گل آفرید؛ سپس مدتی مقرّر داشت (تا انسان تکامل یابد)؛ و اجل حتمی نزد اوست (و فقط او از آن آگاه است). با این همه، شما (مشرکان در توحید و یگانگی و قدرت او،) تردید می‌کنید!'),
(792, 792, 'وَهُوَ اللَّهُ فِي السَّمَاوَاتِ وَفِي الْأَرْضِ ۖ يَعْلَمُ سِرَّكُمْ وَجَهْرَكُمْ وَيَعْلَمُ مَا تَكْسِبُونَ', 6, 3, 7, 26, 128, 'اوست خداوند در آسمانها و در زمین؛ پنهان و آشکار شما را می‌داند؛ و از آنچه (انجام می‌دهید و) به دست می‌آورید، با خبر است.'),
(793, 793, 'وَمَا تَأْتِيهِمْ مِنْ آيَةٍ مِنْ آيَاتِ رَبِّهِمْ إِلَّا كَانُوا عَنْهَا مُعْرِضِينَ', 6, 4, 7, 26, 128, 'هیچ نشانه و آیه‌ای از آیات پروردگارشان برای آنان نمی‌آید، مگر اینکه از آن رویگردان می‌شوند!'),
(794, 794, 'فَقَدْ كَذَّبُوا بِالْحَقِّ لَمَّا جَاءَهُمْ ۖ فَسَوْفَ يَأْتِيهِمْ أَنْبَاءُ مَا كَانُوا بِهِ يَسْتَهْزِئُونَ', 6, 5, 7, 26, 128, 'آنان، حق را هنگامی که سراغشان آمد، تکذیب کردند! ولی بزودی خبر آنچه را به باد مسخره می‌گرفتند، به آنان می‌رسد؛ (و از نتایج کار خود، آگاه می‌شوند).'),
(795, 795, 'أَلَمْ يَرَوْا كَمْ أَهْلَكْنَا مِنْ قَبْلِهِمْ مِنْ قَرْنٍ مَكَّنَّاهُمْ فِي الْأَرْضِ مَا لَمْ نُمَكِّنْ لَكُمْ وَأَرْسَلْنَا السَّمَاءَ عَلَيْهِمْ مِدْرَارًا وَجَعَلْنَا الْأَنْهَارَ تَجْرِي مِنْ تَحْتِهِمْ فَأَهْلَكْنَاهُمْ بِذُنُوبِهِمْ وَأَنْشَأْنَا مِنْ بَعْدِهِمْ قَرْنًا آخَرِينَ', 6, 6, 7, 26, 128, 'آیا ندیدند چقدر از اقوام پیشین را هلاک کردیم؟! اقوامی که (از شما نیرومندتر بودند؛ و) قدرتهایی به آنها داده بودیم که به شما ندادیم؛ بارانهای پی‌درپی برای آنها فرستادیم؛ و از زیر (آبادیهای) آنها، نهرها را جاری ساختیم؛ (اما هنگامی که سرکشی و طغیان کردند،) آنان را بخاطر گناهانشان نابود کردیم؛ و جمعیت دیگری بعد از آنان پدید آوردیم.'),
(796, 796, 'وَلَوْ نَزَّلْنَا عَلَيْكَ كِتَابًا فِي قِرْطَاسٍ فَلَمَسُوهُ بِأَيْدِيهِمْ لَقَالَ الَّذِينَ كَفَرُوا إِنْ هَٰذَا إِلَّا سِحْرٌ مُبِينٌ', 6, 7, 7, 26, 128, '(حتّی) اگر ما نامه‌ای روی صفحه‌ای بر تو نازل کنیم، و (علاوه بر دیدن و خواندن،) آن را با دستهای خود لمس کنند، باز کافران می‌گویند: «این، چیزی جز یک سحر آشکار نیست»!'),
(797, 797, 'وَقَالُوا لَوْلَا أُنْزِلَ عَلَيْهِ مَلَكٌ ۖ وَلَوْ أَنْزَلْنَا مَلَكًا لَقُضِيَ الْأَمْرُ ثُمَّ لَا يُنْظَرُونَ', 6, 8, 7, 26, 128, '(از بهانه‌های آنها این بود که) گفتند: «چرا فرشته‌ای بر او نازل نشده (تا او را در دعوت مردم به سوی خدا همراهی کند؟!)» ولی اگر فرشته‌ای بفرستیم، (و موضوع، جنبه حسی و شهود پیدا کند،) کار تمام می‌شود؛ (یعنی اگر مخالفت کنند،) دیگر به آنها مهلت داده نخواهد شد (و همه هلاک می‌شوند).'),
(798, 798, 'وَلَوْ جَعَلْنَاهُ مَلَكًا لَجَعَلْنَاهُ رَجُلًا وَلَلَبَسْنَا عَلَيْهِمْ مَا يَلْبِسُونَ', 6, 9, 7, 26, 129, 'و اگر او را فرشته‌ای قرارمی‌دادیم، حتماً وی را بصورت انسانی درمی‌آوردیم؛ (باز به پندار آنان،) کار را بر آنها مشتبه می‌ساختیم؛ همان‌طور که آنها کار را بر دیگران مشتبه می‌سازند!'),
(799, 799, 'وَلَقَدِ اسْتُهْزِئَ بِرُسُلٍ مِنْ قَبْلِكَ فَحَاقَ بِالَّذِينَ سَخِرُوا مِنْهُمْ مَا كَانُوا بِهِ يَسْتَهْزِئُونَ', 6, 10, 7, 26, 129, '(با این حال، نگران نباش!) جمعی از پیامبران پیش از تو را استهزا کردند؛ امّا سرانجام، آنچه را مسخره می‌کردند، دامانشان را می‌گرفت؛ (و عذاب الهی بر آنها فرود آمد).'),
(800, 800, 'قُلْ سِيرُوا فِي الْأَرْضِ ثُمَّ انْظُرُوا كَيْفَ كَانَ عَاقِبَةُ الْمُكَذِّبِينَ', 6, 11, 7, 26, 129, 'بگو: «روی زمین گردش کنید! سپس بنگرید سرانجام تکذیب‌کنندگان آیات الهی چه شد؟!»'),
(801, 801, 'قُلْ لِمَنْ مَا فِي السَّمَاوَاتِ وَالْأَرْضِ ۖ قُلْ لِلَّهِ ۚ كَتَبَ عَلَىٰ نَفْسِهِ الرَّحْمَةَ ۚ لَيَجْمَعَنَّكُمْ إِلَىٰ يَوْمِ الْقِيَامَةِ لَا رَيْبَ فِيهِ ۚ الَّذِينَ خَسِرُوا أَنْفُسَهُمْ فَهُمْ لَا يُؤْمِنُونَ', 6, 12, 7, 26, 129, 'بگو: «آنچه در آسمانها و زمین است، از آن کیست؟» بگو: «از آن خداست؛ رحمت (و بخشش) را بر خود، حتم کرده؛ (و به همین دلیل،) بطور قطع همه شما را در روز قیامت، که در آن شکّ و تردیدی نیست، گرد خواهد آورد. (آری،) فقط کسانی که سرمایه‌های وجود خویش را از دست داده و گرفتار خسران شدند، ایمان نمی‌آورند.'),
(802, 802, ' وَلَهُ مَا سَكَنَ فِي اللَّيْلِ وَالنَّهَارِ ۚ وَهُوَ السَّمِيعُ الْعَلِيمُ', 6, 13, 7, 26, 129, 'و برای اوست آنچه در شب و روز قرار دارد؛ و او، شنوا و داناست.'),
(803, 803, 'قُلْ أَغَيْرَ اللَّهِ أَتَّخِذُ وَلِيًّا فَاطِرِ السَّمَاوَاتِ وَالْأَرْضِ وَهُوَ يُطْعِمُ وَلَا يُطْعَمُ ۗ قُلْ إِنِّي أُمِرْتُ أَنْ أَكُونَ أَوَّلَ مَنْ أَسْلَمَ ۖ وَلَا تَكُونَنَّ مِنَ الْمُشْرِكِينَ', 6, 14, 7, 26, 129, 'بگو: «آیا غیر خدا را ولیّ خود انتخاب کنم؟! (خدایی) که آفریننده آسمانها و زمین است؛ اوست که روزی می‌دهد، و از کسی روزی نمی‌گیرد.» بگو: «من مأمورم که نخستین مسلمان باشم؛ و (خداوند به من دستور داده که) از مشرکان نباش!»'),
(804, 804, 'قُلْ إِنِّي أَخَافُ إِنْ عَصَيْتُ رَبِّي عَذَابَ يَوْمٍ عَظِيمٍ', 6, 15, 7, 26, 129, 'بگو: «من (نیز) اگر نافرمانی پروردگارم کنم، از عذاب روزی بزرگ = روز رستاخیز می‌ترسم!'),
(805, 805, 'مَنْ يُصْرَفْ عَنْهُ يَوْمَئِذٍ فَقَدْ رَحِمَهُ ۚ وَذَٰلِكَ الْفَوْزُ الْمُبِينُ', 6, 16, 7, 26, 129, 'آن کس که در آن روز، مجازات الهی به او نرسد، خداوند او را مشمول رحمت خویش ساخته؛ و این همان پیروزی آشکار است.»'),
(806, 806, 'وَإِنْ يَمْسَسْكَ اللَّهُ بِضُرٍّ فَلَا كَاشِفَ لَهُ إِلَّا هُوَ ۖ وَإِنْ يَمْسَسْكَ بِخَيْرٍ فَهُوَ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ', 6, 17, 7, 26, 129, 'اگر خداوند زیانی به تو برساند، هیچ کس جز او نمی‌تواند آن را برطرف سازد! و اگر خیری به تو رساند، او بر همه چیز تواناست؛ (و از قدرت او، هرگونه نیکی ساخته است.)'),
(807, 807, 'وَهُوَ الْقَاهِرُ فَوْقَ عِبَادِهِ ۚ وَهُوَ الْحَكِيمُ الْخَبِيرُ', 6, 18, 7, 26, 129, 'اوست که بر بندگان خود، قاهر و مسلّط است؛ و اوست حکیم آگاه!'),
(808, 808, 'قُلْ أَيُّ شَيْءٍ أَكْبَرُ شَهَادَةً ۖ قُلِ اللَّهُ ۖ شَهِيدٌ بَيْنِي وَبَيْنَكُمْ ۚ وَأُوحِيَ إِلَيَّ هَٰذَا الْقُرْآنُ لِأُنْذِرَكُمْ بِهِ وَمَنْ بَلَغَ ۚ أَئِنَّكُمْ لَتَشْهَدُونَ أَنَّ مَعَ اللَّهِ آلِهَةً أُخْرَىٰ ۚ قُلْ لَا أَشْهَدُ ۚ قُلْ إِنَّمَا هُوَ إِلَٰهٌ وَاحِدٌ وَإِنَّنِي بَرِيءٌ مِمَّا تُشْرِكُونَ', 6, 19, 7, 26, 130, 'بگو: «بالاترین گواهی، گواهی کیست؟» (و خودت پاسخ بده و) بگو: «خداوند، گواه میان من و شماست؛ و (بهترین دلیل آن این است که) این قرآن بر من وحی شده، تا شما و تمام کسانی را که این قرآن به آنها می‌رسد، بیم دهم (و از مخالفت فرمان خدا بترسانم). آیا براستی شما گواهی می‌دهید که معبودان دیگری با خداست؟!» بگو: «من هرگز چنین گواهی نمی‌دهم». بگو: «اوست تنها معبود یگانه؛ و من از آنچه برای او شریک قرارمی‌دهید، بیزارم!»'),
(809, 809, 'الَّذِينَ آتَيْنَاهُمُ الْكِتَابَ يَعْرِفُونَهُ كَمَا يَعْرِفُونَ أَبْنَاءَهُمُ ۘ الَّذِينَ خَسِرُوا أَنْفُسَهُمْ فَهُمْ لَا يُؤْمِنُونَ', 6, 20, 7, 26, 130, 'آنان که کتاب آسمانی به ایشان داده‌ایم، بخوبی او = پیامبر را می‌شناسند، همان‌گونه که فرزندان خود را می‌شناسند؛ فقط کسانی که سرمایه وجود خود را از دست داده‌اند، ایمان نمی‌آورند.'),
(810, 810, 'وَمَنْ أَظْلَمُ مِمَّنِ افْتَرَىٰ عَلَى اللَّهِ كَذِبًا أَوْ كَذَّبَ بِآيَاتِهِ ۗ إِنَّهُ لَا يُفْلِحُ الظَّالِمُونَ', 6, 21, 7, 26, 130, 'چه کسی ستمکارتر است از آن کس که بر خدا دروغ بسته = همتایی برای او قائل شده‌، و یا آیات او را تکذیب کرده است؟! مسلماً ظالمان، رستگار نخواهند شد!'),
(811, 811, 'وَيَوْمَ نَحْشُرُهُمْ جَمِيعًا ثُمَّ نَقُولُ لِلَّذِينَ أَشْرَكُوا أَيْنَ شُرَكَاؤُكُمُ الَّذِينَ كُنْتُمْ تَزْعُمُونَ', 6, 22, 7, 26, 130, 'آن روز که همه آنها را محشور می‌کنیم؛ سپس به مشرکان می‌گوییم: «معبودهایتان، که همتای خدا می‌پنداشتید، کجایند؟» (چرا به یاری شما نمی‌شتابند؟!)'),
(812, 812, 'ثُمَّ لَمْ تَكُنْ فِتْنَتُهُمْ إِلَّا أَنْ قَالُوا وَاللَّهِ رَبِّنَا مَا كُنَّا مُشْرِكِينَ', 6, 23, 7, 26, 130, 'سپس پاسخ و عذر آنها، چیزی جز این نیست که می‌گویند: «به خداوندی که پروردگار ماست سوگند که ما مشرک نبودیم!»'),
(813, 813, 'انْظُرْ كَيْفَ كَذَبُوا عَلَىٰ أَنْفُسِهِمْ ۚ وَضَلَّ عَنْهُمْ مَا كَانُوا يَفْتَرُونَ', 6, 24, 7, 26, 130, 'ببین چگونه به خودشان (نیز) دروغ می‌گویند، و آنچه را بدروغ همتای خدا می‌پنداشتند، از دست می‌دهند!'),
(814, 814, 'وَمِنْهُمْ مَنْ يَسْتَمِعُ إِلَيْكَ ۖ وَجَعَلْنَا عَلَىٰ قُلُوبِهِمْ أَكِنَّةً أَنْ يَفْقَهُوهُ وَفِي آذَانِهِمْ وَقْرًا ۚ وَإِنْ يَرَوْا كُلَّ آيَةٍ لَا يُؤْمِنُوا بِهَا ۚ حَتَّىٰ إِذَا جَاءُوكَ يُجَادِلُونَكَ يَقُولُ الَّذِينَ كَفَرُوا إِنْ هَٰذَا إِلَّا أَسَاطِيرُ الْأَوَّلِينَ', 6, 25, 7, 26, 130, 'پاره‌ای از آنها به (سخنان) تو، گوش فرامی‌دهند؛ ولی بر دلهای آنان پرده‌ها افکنده‌ایم تا آن را نفهمند؛ و در گوش آنها، سنگینی قرار داده‌ایم. و (آنها بقدری لجوجند که) اگر تمام نشانه‌های حق را ببینند، ایمان نمی‌آورند؛ تا آنجا که وقتی به سراغ تو می‌آیند که با تو پرخاشگری کنند، کافران می‌گویند: «اینها فقط افسانه‌های پیشینیان است!»'),
(815, 815, 'وَهُمْ يَنْهَوْنَ عَنْهُ وَيَنْأَوْنَ عَنْهُ ۖ وَإِنْ يُهْلِكُونَ إِلَّا أَنْفُسَهُمْ وَمَا يَشْعُرُونَ', 6, 26, 7, 26, 130, 'آنها دیگران را از آن بازمی‌دارند؛ و خود نیز از آن دوری می‌کنند؛ آنها جز خود را هلاک نمی‌کنند، ولی نمی‌فهمند!'),
(816, 816, 'وَلَوْ تَرَىٰ إِذْ وُقِفُوا عَلَى النَّارِ فَقَالُوا يَا لَيْتَنَا نُرَدُّ وَلَا نُكَذِّبَ بِآيَاتِ رَبِّنَا وَنَكُونَ مِنَ الْمُؤْمِنِينَ', 6, 27, 7, 26, 130, 'کاش (حال آنها را) هنگامی که در برابر آتش (دوزخ) ایستاده‌اند، ببینی! می‌گویند: ای کاش (بار دیگر، به دنیا) بازگردانده می‌شدیم، و آیات پروردگارمان را تکذیب نمی‌کردیم، و از مؤمنان می‌بودیم!'),
(817, 817, 'بَلْ بَدَا لَهُمْ مَا كَانُوا يُخْفُونَ مِنْ قَبْلُ ۖ وَلَوْ رُدُّوا لَعَادُوا لِمَا نُهُوا عَنْهُ وَإِنَّهُمْ لَكَاذِبُونَ', 6, 28, 7, 26, 131, '(آنها در واقع پشیمان نیستند،) بلکه اعمال و نیّاتی را که قبلاً پنهان می‌کردند، در برابر آنها آشکار شده (و به وحشت افتاده‌اند). و اگر بازگردند، به همان اعمالی که از آن نهی شده بودند بازمی‌گردند؛ آنها دروغگویانند.'),
(818, 818, 'وَقَالُوا إِنْ هِيَ إِلَّا حَيَاتُنَا الدُّنْيَا وَمَا نَحْنُ بِمَبْعُوثِينَ', 6, 29, 7, 26, 131, 'آنها گفتند: «چیزی جز این زندگی دنیای ما نیست؛ و ما هرگز برانگیخته نخواهیم شد!»'),
(819, 819, 'وَلَوْ تَرَىٰ إِذْ وُقِفُوا عَلَىٰ رَبِّهِمْ ۚ قَالَ أَلَيْسَ هَٰذَا بِالْحَقِّ ۚ قَالُوا بَلَىٰ وَرَبِّنَا ۚ قَالَ فَذُوقُوا الْعَذَابَ بِمَا كُنْتُمْ تَكْفُرُونَ', 6, 30, 7, 26, 131, 'اگر آنها را به هنگامی که در پیشگاه پروردگارشان ایستاده‌اند، ببینی! (به آنها) می‌گوید: «آیا این حق نیست؟» می‌گویند: «آری، قسم به پروردگارمان (حق است!)» می‌گوید: «پس مجازات را بچشید به سزای آنچه انکار می‌کردید!»'),
(820, 820, 'قَدْ خَسِرَ الَّذِينَ كَذَّبُوا بِلِقَاءِ اللَّهِ ۖ حَتَّىٰ إِذَا جَاءَتْهُمُ السَّاعَةُ بَغْتَةً قَالُوا يَا حَسْرَتَنَا عَلَىٰ مَا فَرَّطْنَا فِيهَا وَهُمْ يَحْمِلُونَ أَوْزَارَهُمْ عَلَىٰ ظُهُورِهِمْ ۚ أَلَا سَاءَ مَا يَزِرُونَ', 6, 31, 7, 26, 131, 'آنها که لقای پروردگار را تکذیب کردند، مسلماً زیان دیدند؛ (و این تکذیب، ادامه می یابد) تا هنگامی که ناگهان قیامت به سراغشان بیاید؛ می‌گویند: «ای افسوس بر ما که درباره آن، کوتاهی کردیم!» و آنها (بار سنگین) گناهانشان را بر دوش می‌کشند؛ چه بد باری بر دوش خواهند داشت!'),
(821, 821, 'وَمَا الْحَيَاةُ الدُّنْيَا إِلَّا لَعِبٌ وَلَهْوٌ ۖ وَلَلدَّارُ الْآخِرَةُ خَيْرٌ لِلَّذِينَ يَتَّقُونَ ۗ أَفَلَا تَعْقِلُونَ', 6, 32, 7, 26, 131, 'زندگی دنیا، چیزی جز بازی و سرگرمی نیست! و سرای آخرت، برای آنها که پرهیزگارند، بهتر است! آیا نمی‌اندیشید؟!'),
(822, 822, 'قَدْ نَعْلَمُ إِنَّهُ لَيَحْزُنُكَ الَّذِي يَقُولُونَ ۖ فَإِنَّهُمْ لَا يُكَذِّبُونَكَ وَلَٰكِنَّ الظَّالِمِينَ بِآيَاتِ اللَّهِ يَجْحَدُونَ', 6, 33, 7, 26, 131, 'ما می‌دانیم که گفتار آنها، تو را غمگین می‌کند؛ ولی (غم مخور! و بدان که) آنها تو را تکذیب نمی‌کنند؛ بلکه ظالمان، آیات خدا را انکار می‌نمایند.'),
(823, 823, 'وَلَقَدْ كُذِّبَتْ رُسُلٌ مِنْ قَبْلِكَ فَصَبَرُوا عَلَىٰ مَا كُذِّبُوا وَأُوذُوا حَتَّىٰ أَتَاهُمْ نَصْرُنَا ۚ وَلَا مُبَدِّلَ لِكَلِمَاتِ اللَّهِ ۚ وَلَقَدْ جَاءَكَ مِنْ نَبَإِ الْمُرْسَلِينَ', 6, 34, 7, 26, 131, 'پیش از تو نیز پیامبرانی تکذیب شدند؛ و در برابر تکذیبها، صبر و استقامت کردند؛ و (در این راه،) آزار دیدند، تا هنگامی که یاری ما به آنها رسید. (تو نیز چنین باش! و این، یکی از سنتهای الهی است؛) و هیچ چیز نمی‌تواند سنن خدا را تغییر دهد؛ و اخبار پیامبران به تو رسیده است.'),
(824, 824, 'وَإِنْ كَانَ كَبُرَ عَلَيْكَ إِعْرَاضُهُمْ فَإِنِ اسْتَطَعْتَ أَنْ تَبْتَغِيَ نَفَقًا فِي الْأَرْضِ أَوْ سُلَّمًا فِي السَّمَاءِ فَتَأْتِيَهُمْ بِآيَةٍ ۚ وَلَوْ شَاءَ اللَّهُ لَجَمَعَهُمْ عَلَى الْهُدَىٰ ۚ فَلَا تَكُونَنَّ مِنَ الْجَاهِلِينَ', 6, 35, 7, 26, 131, 'و اگر اعراض آنها بر تو سنگین است، چنانچه بتوانی نقبی در زمین بزنی، یا نردبانی به آسمان بگذاری (و اعماق زمین و آسمانها را جستجو کنی، چنین کن) تا آیه (و نشانه دیگری) برای آنها بیاوری! (ولی بدان که این لجوجان، ایمان نمی‌آورند!) امّا اگر خدا بخواهد، آنها را (به اجبار) بر هدایت جمع خواهد کرد. (ولی هدایت اجباری، چه سودی دارد؟) پس هرگز از جاهلان مباش!'),
(825, 825, ' إِنَّمَا يَسْتَجِيبُ الَّذِينَ يَسْمَعُونَ ۘ وَالْمَوْتَىٰ يَبْعَثُهُمُ اللَّهُ ثُمَّ إِلَيْهِ يُرْجَعُونَ', 6, 36, 7, 27, 132, 'تنها کسانی (دعوت تو را) می‌پذیرند که گوش شنوا دارند؛ امّا مردگان (و آنها که روح انسانی را از دست داده‌اند، ایمان نمی‌آورند؛ و) خدا آنها را (در قیامت) برمی‌انگیزد؛ سپس به سوی او، بازمی‌گردند.'),
(826, 826, 'وَقَالُوا لَوْلَا نُزِّلَ عَلَيْهِ آيَةٌ مِنْ رَبِّهِ ۚ قُلْ إِنَّ اللَّهَ قَادِرٌ عَلَىٰ أَنْ يُنَزِّلَ آيَةً وَلَٰكِنَّ أَكْثَرَهُمْ لَا يَعْلَمُونَ', 6, 37, 7, 27, 132, 'و گفتند: «چرا نشانه (و معجزه‌ای) از طرف پروردگارش بر او نازل نمی‌شود؟!» بگو: «خداوند، قادر است که نشانه‌ای نازل کند؛ ولی بیشتر آنها نمی‌دانند!»'),
(827, 827, 'وَمَا مِنْ دَابَّةٍ فِي الْأَرْضِ وَلَا طَائِرٍ يَطِيرُ بِجَنَاحَيْهِ إِلَّا أُمَمٌ أَمْثَالُكُمْ ۚ مَا فَرَّطْنَا فِي الْكِتَابِ مِنْ شَيْءٍ ۚ ثُمَّ إِلَىٰ رَبِّهِمْ يُحْشَرُونَ', 6, 38, 7, 27, 132, 'هیچ جنبنده‌ای در زمین، و هیچ پرنده‌ای که با دو بال خود پرواز می‌کند، نیست مگر اینکه امتهایی همانند شما هستند. ما هیچ چیز را در این کتاب، فرو گذار نکردیم؛ سپس همگی به سوی پروردگارشان محشور می‌گردند.'),
(828, 828, 'وَالَّذِينَ كَذَّبُوا بِآيَاتِنَا صُمٌّ وَبُكْمٌ فِي الظُّلُمَاتِ ۗ مَنْ يَشَإِ اللَّهُ يُضْلِلْهُ وَمَنْ يَشَأْ يَجْعَلْهُ عَلَىٰ صِرَاطٍ مُسْتَقِيمٍ', 6, 39, 7, 27, 132, 'آنها که آیات ما را تکذیب کردند، کرها و لالهایی هستند که در تاریکیها قرار دارند. هر کس را خدا بخواهد (و مستحق باشد،) گمراه می‌کند؛ و هر کس را بخواهد (و شایسته بداند،) بر راه راست قرار خواهد داد.'),
(829, 829, 'قُلْ أَرَأَيْتَكُمْ إِنْ أَتَاكُمْ عَذَابُ اللَّهِ أَوْ أَتَتْكُمُ السَّاعَةُ أَغَيْرَ اللَّهِ تَدْعُونَ إِنْ كُنْتُمْ صَادِقِينَ', 6, 40, 7, 27, 132, 'بگو: «به من خبر دهید اگر عذاب پروردگار به سراغ شما آید، یا رستاخیز برپا شود، آیا (برای حل مشکلات خود،) غیر خدا را می‌خوانید اگر راست می‌گویید؟!»'),
(830, 830, 'بَلْ إِيَّاهُ تَدْعُونَ فَيَكْشِفُ مَا تَدْعُونَ إِلَيْهِ إِنْ شَاءَ وَتَنْسَوْنَ مَا تُشْرِكُونَ', 6, 41, 7, 27, 132, '(نه،) بلکه تنها او را می‌خوانید! و او اگر بخواهد، مشکلی را که بخاطر آن او را خوانده‌اید، برطرف می‌سازد؛ و آنچه را (امروز) همتای خدا قرارمی‌دهید، (در آن روز) فراموش خواهید کرد.'),
(831, 831, 'وَلَقَدْ أَرْسَلْنَا إِلَىٰ أُمَمٍ مِنْ قَبْلِكَ فَأَخَذْنَاهُمْ بِالْبَأْسَاءِ وَالضَّرَّاءِ لَعَلَّهُمْ يَتَضَرَّعُونَ', 6, 42, 7, 27, 132, 'ما به سوی امتهایی که پیش از تو بودند، (پیامبرانی) فرستادیم؛ (و هنگامی که با این پیامبران به مخالفت برخاستند،) آنها را با شدّت و رنج و ناراحتی مواجه ساختیم؛ شاید (بیدار شوند و در برابر حق،) خضوع کنند و تسلیم گردند!'),
(832, 832, 'فَلَوْلَا إِذْ جَاءَهُمْ بَأْسُنَا تَضَرَّعُوا وَلَٰكِنْ قَسَتْ قُلُوبُهُمْ وَزَيَّنَ لَهُمُ الشَّيْطَانُ مَا كَانُوا يَعْمَلُونَ', 6, 43, 7, 27, 132, 'چرا هنگامی که مجازات ما به آنان رسید، (خضوع نکردند و) تسلیم نشدند؟! بلکه دلهای آنها قساوت پیدا کرد؛ و شیطان، هر کاری را که می‌کردند، در نظرشان زینت داد!'),
(833, 833, 'فَلَمَّا نَسُوا مَا ذُكِّرُوا بِهِ فَتَحْنَا عَلَيْهِمْ أَبْوَابَ كُلِّ شَيْءٍ حَتَّىٰ إِذَا فَرِحُوا بِمَا أُوتُوا أَخَذْنَاهُمْ بَغْتَةً فَإِذَا هُمْ مُبْلِسُونَ', 6, 44, 7, 27, 132, '(آری،) هنگامی که (اندرزها سودی نبخشید، و) آنچه را به آنها یادآوری شده بود فراموش کردند، درهای همه چیز (از نعمتها) را به روی آنها گشودیم؛ تا (کاملا) ً خوشحال شدند (و دل به آن بستند)؛ ناگهان آنها را گرفتیم (و سخت مجازات کردیم)؛ در این هنگام، همگی مأیوس شدند؛ (و درهای امید به روی آنها بسته شد).'),
(834, 834, 'فَقُطِعَ دَابِرُ الْقَوْمِ الَّذِينَ ظَلَمُوا ۚ وَالْحَمْدُ لِلَّهِ رَبِّ الْعَالَمِينَ', 6, 45, 7, 27, 133, 'و (به این ترتیب،) دنباله (زندگی) جمعیّتی که ستم کرده بودند، قطع شد. و ستایش مخصوص خداوند، پروردگار جهانیان است.'),
(835, 835, 'قُلْ أَرَأَيْتُمْ إِنْ أَخَذَ اللَّهُ سَمْعَكُمْ وَأَبْصَارَكُمْ وَخَتَمَ عَلَىٰ قُلُوبِكُمْ مَنْ إِلَٰهٌ غَيْرُ اللَّهِ يَأْتِيكُمْ بِهِ ۗ انْظُرْ كَيْفَ نُصَرِّفُ الْآيَاتِ ثُمَّ هُمْ يَصْدِفُونَ', 6, 46, 7, 27, 133, 'بگو: «به من خبر دهید اگر خداوند، گوش و چشمهایتان را بگیرد، و بر دلهای شما مهر نهد (که چیزی را نفهمید)، چه کسی جز خداست که آنها را به شما بدهد؟!» ببین چگونه آیات را به گونه‌های مختلف برای آنها شرح می‌دهیم، سپس آنها روی می‌گردانند!'),
(836, 836, 'قُلْ أَرَأَيْتَكُمْ إِنْ أَتَاكُمْ عَذَابُ اللَّهِ بَغْتَةً أَوْ جَهْرَةً هَلْ يُهْلَكُ إِلَّا الْقَوْمُ الظَّالِمُونَ', 6, 47, 7, 27, 133, 'بگو: «به من خبر دهید اگر عذاب خدا بطور ناگهانی (و پنهانی) یا آشکارا به سراغ شما بیاید، آیا جز جمعیّت ستمکار هلاک می‌شوند؟!»'),
(837, 837, 'وَمَا نُرْسِلُ الْمُرْسَلِينَ إِلَّا مُبَشِّرِينَ وَمُنْذِرِينَ ۖ فَمَنْ آمَنَ وَأَصْلَحَ فَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَ', 6, 48, 7, 27, 133, 'ما پیامبران را، جز (به عنوان) بشارت دهنده و بیم دهنده، نمی‌فرستیم؛ آنها که ایمان بیاورند و (خویشتن را) اصلاح کنند، نه ترسی بر آنهاست و نه غمگین می‌شوند.'),
(838, 838, 'وَالَّذِينَ كَذَّبُوا بِآيَاتِنَا يَمَسُّهُمُ الْعَذَابُ بِمَا كَانُوا يَفْسُقُونَ', 6, 49, 7, 27, 133, 'و آنها که آیات ما را تکذیب کردند، عذاب (پروردگار) بخاطر نافرمانیها به آنان می‌رسد.'),
(839, 839, 'قُلْ لَا أَقُولُ لَكُمْ عِنْدِي خَزَائِنُ اللَّهِ وَلَا أَعْلَمُ الْغَيْبَ وَلَا أَقُولُ لَكُمْ إِنِّي مَلَكٌ ۖ إِنْ أَتَّبِعُ إِلَّا مَا يُوحَىٰ إِلَيَّ ۚ قُلْ هَلْ يَسْتَوِي الْأَعْمَىٰ وَالْبَصِيرُ ۚ أَفَلَا تَتَفَكَّرُونَ', 6, 50, 7, 27, 133, 'بگو: «من نمی‌گویم خزاین خدا نزد من است؛ و من، (جز آنچه خدا به من بیاموزد،) از غیب آگاه نیستم! و به شما نمی‌گویم من فرشته‌ام؛ تنها از آنچه به من وحی می‌شود پیروی می‌کنم.» بگو: «آیا نابینا و بینا مساویند؟! پس چرا نمی‌اندیشید؟!»'),
(840, 840, 'وَأَنْذِرْ بِهِ الَّذِينَ يَخَافُونَ أَنْ يُحْشَرُوا إِلَىٰ رَبِّهِمْ ۙ لَيْسَ لَهُمْ مِنْ دُونِهِ وَلِيٌّ وَلَا شَفِيعٌ لَعَلَّهُمْ يَتَّقُونَ', 6, 51, 7, 27, 133, 'و به وسیله آن (قرآن)، کسانی را که از روز حشر و رستاخیز می‌ترسند، بیم ده! (روزی که در آن،) یاور و سرپرست و شفاعت‌کننده‌ای جز او = خدا ندارند؛ شاید پرهیزگاری پیشه کنند!'),
(841, 841, 'وَلَا تَطْرُدِ الَّذِينَ يَدْعُونَ رَبَّهُمْ بِالْغَدَاةِ وَالْعَشِيِّ يُرِيدُونَ وَجْهَهُ ۖ مَا عَلَيْكَ مِنْ حِسَابِهِمْ مِنْ شَيْءٍ وَمَا مِنْ حِسَابِكَ عَلَيْهِمْ مِنْ شَيْءٍ فَتَطْرُدَهُمْ فَتَكُونَ مِنَ الظَّالِمِينَ', 6, 52, 7, 27, 133, 'و کسانی را که صبح و شام خدا را می‌خوانند، و جز ذات پاک او نظری ندارند، از خود دور مکن! نه چیزی از حساب آنها بر توست، و نه چیزی از حساب تو بر آنها! اگر آنها را طرد کنی، از ستمگران خواهی بود!'),
(842, 842, 'وَكَذَٰلِكَ فَتَنَّا بَعْضَهُمْ بِبَعْضٍ لِيَقُولُوا أَهَٰؤُلَاءِ مَنَّ اللَّهُ عَلَيْهِمْ مِنْ بَيْنِنَا ۗ أَلَيْسَ اللَّهُ بِأَعْلَمَ بِالشَّاكِرِينَ', 6, 53, 7, 27, 134, 'و این چنین بعضی از آنها را با بعض دیگر آزمودیم (توانگران را بوسیله فقیران)؛ تا بگویند: «آیا اینها هستند که خداوند از میان ما (برگزیده، و) بر آنها منّت گذارده (و نعمت ایمان بخشیده است؟!» آیا خداوند، شاکران را بهتر نمی‌شناسد؟!'),
(843, 843, 'وَإِذَا جَاءَكَ الَّذِينَ يُؤْمِنُونَ بِآيَاتِنَا فَقُلْ سَلَامٌ عَلَيْكُمْ ۖ كَتَبَ رَبُّكُمْ عَلَىٰ نَفْسِهِ الرَّحْمَةَ ۖ أَنَّهُ مَنْ عَمِلَ مِنْكُمْ سُوءًا بِجَهَالَةٍ ثُمَّ تَابَ مِنْ بَعْدِهِ وَأَصْلَحَ فَأَنَّهُ غَفُورٌ رَحِيمٌ', 6, 54, 7, 27, 134, 'هرگاه کسانی که به آیات ما ایمان دارند نزد تو آیند، به آنها بگو: «سلام بر شما پروردگارتان، رحمت را بر خود فرض کرده؛ هر کس از شما کار بدی از روی نادانی کند، سپس توبه و اصلاح (و جبران) نماید، (مشمول رحمت خدا می‌شود چرا که) او آمرزنده مهربان است.»'),
(844, 844, 'وَكَذَٰلِكَ نُفَصِّلُ الْآيَاتِ وَلِتَسْتَبِينَ سَبِيلُ الْمُجْرِمِينَ', 6, 55, 7, 27, 134, 'و این چنین آیات را برمیشمریم، (تا حقیقت بر شما روشن شود،) و راه گناهکاران آشکار گردد.'),
(845, 845, 'قُلْ إِنِّي نُهِيتُ أَنْ أَعْبُدَ الَّذِينَ تَدْعُونَ مِنْ دُونِ اللَّهِ ۚ قُلْ لَا أَتَّبِعُ أَهْوَاءَكُمْ ۙ قَدْ ضَلَلْتُ إِذًا وَمَا أَنَا مِنَ الْمُهْتَدِينَ', 6, 56, 7, 27, 134, 'بگو: «من از پرستش کسانی که غیر از خدا می‌خوانید، نهی شده‌ام!» بگو: «من از هوی و هوسهای شما، پیروی نمی‌کنم؛ اگر چنین کنم، گمراه شده‌ام؛ و از هدایت‌یافتگان نخواهم بود!»'),
(846, 846, 'قُلْ إِنِّي عَلَىٰ بَيِّنَةٍ مِنْ رَبِّي وَكَذَّبْتُمْ بِهِ ۚ مَا عِنْدِي مَا تَسْتَعْجِلُونَ بِهِ ۚ إِنِ الْحُكْمُ إِلَّا لِلَّهِ ۖ يَقُصُّ الْحَقَّ ۖ وَهُوَ خَيْرُ الْفَاصِلِينَ', 6, 57, 7, 27, 134, 'بگو: «من دلیل روشنی از پروردگارم دارم؛ و شما آن را تکذیب کرده‌اید! آنچه شما در باره آن (از نزول عذاب الهی) عجله دارید، به دستِ من نیست! حکم و فرمان، تنها از آنِ خداست! حق را از باطل جدا می‌کند، و او بهترین جداکننده (حق از باطل) است.»'),
(847, 847, 'قُلْ لَوْ أَنَّ عِنْدِي مَا تَسْتَعْجِلُونَ بِهِ لَقُضِيَ الْأَمْرُ بَيْنِي وَبَيْنَكُمْ ۗ وَاللَّهُ أَعْلَمُ بِالظَّالِمِينَ', 6, 58, 7, 27, 134, 'بگو: «اگر آنچه درباره آن عجله دارید نزد من بود، (و به درخواست شما ترتیب‌اثر می‌دادم، عذاب الهی بر شما نازل می‌گشت؛) و کار میان من و شما پایان گرفته بود؛ ولی خداوند ظالمان را بهتر می‌شناسد (و بموقع مجازات می‌کند.)»'),
(848, 848, ' وَعِنْدَهُ مَفَاتِحُ الْغَيْبِ لَا يَعْلَمُهَا إِلَّا هُوَ ۚ وَيَعْلَمُ مَا فِي الْبَرِّ وَالْبَحْرِ ۚ وَمَا تَسْقُطُ مِنْ وَرَقَةٍ إِلَّا يَعْلَمُهَا وَلَا حَبَّةٍ فِي ظُلُمَاتِ الْأَرْضِ وَلَا رَطْبٍ وَلَا يَابِسٍ إِلَّا فِي كِتَابٍ مُبِينٍ', 6, 59, 7, 27, 134, 'کلیدهای غیب، تنها نزد اوست؛ و جز او، کسی آنها را نمی‌داند. او آنچه را در خشکی و دریاست می‌داند؛ هیچ برگی (از درختی) نمی‌افتد، مگر اینکه از آن آگاه است؛ و نه هیچ دانه‌ای در تاریکیهای زمین، و نه هیچ تر و خشکی وجود دارد، جز اینکه در کتابی آشکار = در کتاب علم خدا ثبت است.'),
(849, 849, 'وَهُوَ الَّذِي يَتَوَفَّاكُمْ بِاللَّيْلِ وَيَعْلَمُ مَا جَرَحْتُمْ بِالنَّهَارِ ثُمَّ يَبْعَثُكُمْ فِيهِ لِيُقْضَىٰ أَجَلٌ مُسَمًّى ۖ ثُمَّ إِلَيْهِ مَرْجِعُكُمْ ثُمَّ يُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُونَ', 6, 60, 7, 27, 135, 'او کسی است که (روح) شما را در شب (به هنگام خواب) می‌گیرد؛ و از آنچه در روز کرده‌اید، با خبر است؛ سپس در روز شما را (از خواب) برمی‌انگیزد؛ و (این وضع همچنان ادامه می‌یابد) تا سرآمد معینی فرا رسد؛ سپس بازگشت شما به سوی اوست؛ و سپس شما را از آنچه عمل می‌کردید، با خبر می‌سازد.'),
(850, 850, 'وَهُوَ الْقَاهِرُ فَوْقَ عِبَادِهِ ۖ وَيُرْسِلُ عَلَيْكُمْ حَفَظَةً حَتَّىٰ إِذَا جَاءَ أَحَدَكُمُ الْمَوْتُ تَوَفَّتْهُ رُسُلُنَا وَهُمْ لَا يُفَرِّطُونَ', 6, 61, 7, 27, 135, 'او بر بندگان خود تسلّط کامل دارد؛ و مراقبانی بر شما می‌گمارد؛ تا زمانی که یکی از شما را مرگ فرا رسد؛ (در این موقع،) فرستادگان ما جان او را می‌گیرند؛ و آنها (در نگاهداری حساب عمر و اعمال بندگان،) کوتاهی نمی‌کنند.'),
(851, 851, 'ثُمَّ رُدُّوا إِلَى اللَّهِ مَوْلَاهُمُ الْحَقِّ ۚ أَلَا لَهُ الْحُكْمُ وَهُوَ أَسْرَعُ الْحَاسِبِينَ', 6, 62, 7, 27, 135, 'سپس (تمام بندگان) به سوی خدا، که مولای حقیقی آنهاست، بازمی‌گردند. بدانید که حکم و داوری، مخصوص اوست؛ و او، سریعترین حسابگران است!'),
(852, 852, 'قُلْ مَنْ يُنَجِّيكُمْ مِنْ ظُلُمَاتِ الْبَرِّ وَالْبَحْرِ تَدْعُونَهُ تَضَرُّعًا وَخُفْيَةً لَئِنْ أَنْجَانَا مِنْ هَٰذِهِ لَنَكُونَنَّ مِنَ الشَّاكِرِينَ', 6, 63, 7, 27, 135, 'بگو: «چه کسی شما را از تاریکیهای خشکی و دریا رهایی می‌بخشد؟ در حالی که او را با حالت تضرع (و آشکارا) و در پنهانی می‌خوانید؛ (و می‌گویید:) اگر از این (خطرات و ظلمتها) ما را رهایی می‌بخشد، از شکرگزاران خواهیم بود.»'),
(853, 853, 'قُلِ اللَّهُ يُنَجِّيكُمْ مِنْهَا وَمِنْ كُلِّ كَرْبٍ ثُمَّ أَنْتُمْ تُشْرِكُونَ', 6, 64, 7, 27, 135, 'بگو: «خداوند شما را از اینها، و از هر مشکل و ناراحتی، نجات می‌دهد؛ باز هم شما برای او شریک قرار می‌دهید! (و راه کفر می‌پویید.)»'),
(854, 854, 'قُلْ هُوَ الْقَادِرُ عَلَىٰ أَنْ يَبْعَثَ عَلَيْكُمْ عَذَابًا مِنْ فَوْقِكُمْ أَوْ مِنْ تَحْتِ أَرْجُلِكُمْ أَوْ يَلْبِسَكُمْ شِيَعًا وَيُذِيقَ بَعْضَكُمْ بَأْسَ بَعْضٍ ۗ انْظُرْ كَيْفَ نُصَرِّفُ الْآيَاتِ لَعَلَّهُمْ يَفْقَهُونَ', 6, 65, 7, 27, 135, 'بگو: «او قادر است که از بالا یا از زیر پای شما، عذابی بر شما بفرستد؛ یا بصورت دسته‌های پراکنده شما را با هم بیامیزد؛ و طعم جنگ (و اختلاف) را به هر یک از شما بوسیله دیگری بچشاند.» ببین چگونه آیات گوناگون را (برای آنها) بازگو می‌کنیم! شاید بفهمند (و بازگردند)!'),
(855, 855, 'وَكَذَّبَ بِهِ قَوْمُكَ وَهُوَ الْحَقُّ ۚ قُلْ لَسْتُ عَلَيْكُمْ بِوَكِيلٍ', 6, 66, 7, 27, 135, 'قوم و جمعیّت تو، آن (آیات الهی) را تکذیب و انکار کردند، در حالی که حق است! (به آنها) بگو: «من مسؤول (ایمان‌آوردن) شما نیستم! (وظیفه من، تنها ابلاغ رسالت است، نه اجبار شما بر ایمان.)»'),
(856, 856, 'لِكُلِّ نَبَإٍ مُسْتَقَرٌّ ۚ وَسَوْفَ تَعْلَمُونَ', 6, 67, 7, 27, 135, 'هر خبری (که خداوند به شما داده،) سرانجام قرارگاهی دارد، (و در موعد خود انجام میگیرد.) و بزودی خواهید دانست!'),
(857, 857, 'وَإِذَا رَأَيْتَ الَّذِينَ يَخُوضُونَ فِي آيَاتِنَا فَأَعْرِضْ عَنْهُمْ حَتَّىٰ يَخُوضُوا فِي حَدِيثٍ غَيْرِهِ ۚ وَإِمَّا يُنْسِيَنَّكَ الشَّيْطَانُ فَلَا تَقْعُدْ بَعْدَ الذِّكْرَىٰ مَعَ الْقَوْمِ الظَّالِمِينَ', 6, 68, 7, 27, 135, 'هرگاه کسانی را دیدی که آیات ما را استهزا می‌کنند، از آنها روی بگردان تا به سخن دیگری بپردازند! و اگر شیطان از یاد تو ببرد، هرگز پس از یادآمدن با این جمعیّت ستمگر منشین!'),
(858, 858, 'وَمَا عَلَى الَّذِينَ يَتَّقُونَ مِنْ حِسَابِهِمْ مِنْ شَيْءٍ وَلَٰكِنْ ذِكْرَىٰ لَعَلَّهُمْ يَتَّقُونَ', 6, 69, 7, 27, 136, 'و (اگر) افراد با تقوا (برای ارشاد و اندرز با آنها بنشینند)، چیزی از حساب (و گناه) آنها بر ایشان نیست؛ ولی (این کار، باید تنها) برای یادآوری آنها باشد، شاید (بشنوند و) تقوی پیشه کنند!'),
(859, 859, 'وَذَرِ الَّذِينَ اتَّخَذُوا دِينَهُمْ لَعِبًا وَلَهْوًا وَغَرَّتْهُمُ الْحَيَاةُ الدُّنْيَا ۚ وَذَكِّرْ بِهِ أَنْ تُبْسَلَ نَفْسٌ بِمَا كَسَبَتْ لَيْسَ لَهَا مِنْ دُونِ اللَّهِ وَلِيٌّ وَلَا شَفِيعٌ وَإِنْ تَعْدِلْ كُلَّ عَدْلٍ لَا يُؤْخَذْ مِنْهَا ۗ أُولَٰئِكَ الَّذِينَ أُبْسِلُوا بِمَا كَسَبُوا ۖ لَهُمْ شَرَابٌ مِنْ حَمِيمٍ وَعَذَابٌ أَلِيمٌ بِمَا كَانُوا يَكْفُرُونَ', 6, 70, 7, 27, 136, 'و رها کن کسانی را که آیین (فطری) خود را به بازی و سرگرمی گرفتند، و زندگی دنیا، آنها را مغرور ساخته، و با این (قرآن)، به آنها یادآوری نما، تا گرفتار (عواقب شوم) اعمال خود نشوند! (و در قیامت) جز خدا، نه یاوری دارند، و نه شفاعت‌کننده‌ای! و (چنین کسی) هر گونه عوضی بپردازد، از او پذیرفته نخواهد شد؛ آنها کسانی هستند که گرفتار اعمالی شده‌اند که خود انجام داده‌اند؛ نوشابه‌ای از آب سوزان برای آنهاست؛ و عذاب دردناکی بخاطر اینکه کفر می‌ورزیدند (و آیات الهی را انکار) می‌کردند.'),
(860, 860, 'قُلْ أَنَدْعُو مِنْ دُونِ اللَّهِ مَا لَا يَنْفَعُنَا وَلَا يَضُرُّنَا وَنُرَدُّ عَلَىٰ أَعْقَابِنَا بَعْدَ إِذْ هَدَانَا اللَّهُ كَالَّذِي اسْتَهْوَتْهُ الشَّيَاطِينُ فِي الْأَرْضِ حَيْرَانَ لَهُ أَصْحَابٌ يَدْعُونَهُ إِلَى الْهُدَى ائْتِنَا ۗ قُلْ إِنَّ هُدَى اللَّهِ هُوَ الْهُدَىٰ ۖ وَأُمِرْنَا لِنُسْلِمَ لِرَبِّ الْعَالَمِينَ', 6, 71, 7, 27, 136, 'بگو: «آیا غیر از خدا، چیزی را بخوانیم (و عبادت کنیم) که نه سودی به حال ما دارد، نه زیانی؛ و (به این ترتیب،) به عقب برگردیم بعد از آنکه خداوند ما را هدایت کرده است؟! همانند کسی که بر اثر وسوسه‌های شیطان، در روی زمین راه را گم کرده، و سرگردان مانده است؛ در حالی که یارانی هم دارد که او را به هدایت دعوت می‌کنند (و می‌گویند:) به سوی ما بیا!» بگو: «تنها هدایت خداوند، هدایت است؛ و ما دستور داریم که تسلیم پروردگار عالمیان باشیم.'),
(861, 861, 'وَأَنْ أَقِيمُوا الصَّلَاةَ وَاتَّقُوهُ ۚ وَهُوَ الَّذِي إِلَيْهِ تُحْشَرُونَ', 6, 72, 7, 27, 136, 'و (نیز به ما فرمان داده شده به) اینکه: نماز را برپا دارید! و از او بپرهیزید! و تنها اوست که به سویش محشور خواهید شد.»'),
(862, 862, 'وَهُوَ الَّذِي خَلَقَ السَّمَاوَاتِ وَالْأَرْضَ بِالْحَقِّ ۖ وَيَوْمَ يَقُولُ كُنْ فَيَكُونُ ۚ قَوْلُهُ الْحَقُّ ۚ وَلَهُ الْمُلْكُ يَوْمَ يُنْفَخُ فِي الصُّورِ ۚ عَالِمُ الْغَيْبِ وَالشَّهَادَةِ ۚ وَهُوَ الْحَكِيمُ الْخَبِيرُ', 6, 73, 7, 27, 136, 'اوست که آسمانها و زمین را بحق آفرید؛ و آن روز که (به هر چیز) می‌گوید: «موجود باش!» موجود می‌شود؛ سخن او، حق است؛ و در آن روز که در «صور» دمیده می‌شود، حکومت مخصوص اوست، از پنهان و آشکار با خبر است، و اوست حکیم و آگاه.'),
(863, 863, ' وَإِذْ قَالَ إِبْرَاهِيمُ لِأَبِيهِ آزَرَ أَتَتَّخِذُ أَصْنَامًا آلِهَةً ۖ إِنِّي أَرَاكَ وَقَوْمَكَ فِي ضَلَالٍ مُبِينٍ', 6, 74, 7, 28, 137, '(به خاطر بیاورید) هنگامی را که ابراهیم به پدرش = عمویش‌ «آزر» گفت: «آیا بتهائی را معبودان خود انتخاب می‌کنی؟! من، تو و قوم تو را در گمراهی آشکاری می‌بینم.»'),
(864, 864, 'وَكَذَٰلِكَ نُرِي إِبْرَاهِيمَ مَلَكُوتَ السَّمَاوَاتِ وَالْأَرْضِ وَلِيَكُونَ مِنَ الْمُوقِنِينَ', 6, 75, 7, 28, 137, 'و این چنین، ملکوت آسمانها و زمین (و حکومت مطلقه خداوند بر آنها) را به ابراهیم نشان دادیم؛ (تا به آن استدلال کند،) و اهل یقین گردد.'),
(865, 865, 'فَلَمَّا جَنَّ عَلَيْهِ اللَّيْلُ رَأَىٰ كَوْكَبًا ۖ قَالَ هَٰذَا رَبِّي ۖ فَلَمَّا أَفَلَ قَالَ لَا أُحِبُّ الْآفِلِينَ', 6, 76, 7, 28, 137, 'هنگامی که (تاریکی) شب او را پوشانید، ستاره‌ای مشاهده کرد، گفت: «این خدای من است؟» امّا هنگامی که غروب کرد، گفت: «غروب‌کنندگان را دوست ندارم!»'),
(866, 866, 'فَلَمَّا رَأَى الْقَمَرَ بَازِغًا قَالَ هَٰذَا رَبِّي ۖ فَلَمَّا أَفَلَ قَالَ لَئِنْ لَمْ يَهْدِنِي رَبِّي لَأَكُونَنَّ مِنَ الْقَوْمِ الضَّالِّينَ', 6, 77, 7, 28, 137, 'و هنگامی که ماه را دید که (سینه افق را) می‌شکافد، گفت: «این خدای من است؟» امّا هنگامی که (آن هم) غروب کرد، گفت: «اگر پروردگارم مرا راهنمایی نکند، مسلّماً از گروه گمراهان خواهم بود.»'),
(867, 867, 'فَلَمَّا رَأَى الشَّمْسَ بَازِغَةً قَالَ هَٰذَا رَبِّي هَٰذَا أَكْبَرُ ۖ فَلَمَّا أَفَلَتْ قَالَ يَا قَوْمِ إِنِّي بَرِيءٌ مِمَّا تُشْرِكُونَ', 6, 78, 7, 28, 137, 'و هنگامی که خورشید را دید که (سینه افق را) می‌شکافت، گفت: «این خدای من است؟ این (که از همه) بزرگتر است!» امّا هنگامی که غروب کرد، گفت: «ای قوم من از شریکهایی که شما (برای خدا) می‌سازید، بیزارم!'),
(868, 868, 'إِنِّي وَجَّهْتُ وَجْهِيَ لِلَّذِي فَطَرَ السَّمَاوَاتِ وَالْأَرْضَ حَنِيفًا ۖ وَمَا أَنَا مِنَ الْمُشْرِكِينَ', 6, 79, 7, 28, 137, 'من روی خود را به سوی کسی کردم که آسمانها و زمین را آفریده؛ من در ایمان خود خالصم؛ و از مشرکان نیستم!'),
(869, 869, 'وَحَاجَّهُ قَوْمُهُ ۚ قَالَ أَتُحَاجُّونِّي فِي اللَّهِ وَقَدْ هَدَانِ ۚ وَلَا أَخَافُ مَا تُشْرِكُونَ بِهِ إِلَّا أَنْ يَشَاءَ رَبِّي شَيْئًا ۗ وَسِعَ رَبِّي كُلَّ شَيْءٍ عِلْمًا ۗ أَفَلَا تَتَذَكَّرُونَ', 6, 80, 7, 28, 137, 'ولی قوم او = ابراهیم‌، با وی به گفتگو و ستیز پرداختند؛ گفت: «آیا درباره خدا با من گفتگو و ستیز می‌کنید؟! در حالی که خداوند، مرا با دلایل روشن هدایت کرده؛ و من از آنچه شما همتای (خدا) قرار می‌دهید، نمی‌ترسم (و به من زیانی نمی رسانند)! مگر پروردگارم چیزی را بخواهد! وسعت آگاهی پروردگارم همه چیز را در برمی‌گیرد؛ آیا متذکّر (و بیدار) نمی‌شوید؟!'),
(870, 870, 'وَكَيْفَ أَخَافُ مَا أَشْرَكْتُمْ وَلَا تَخَافُونَ أَنَّكُمْ أَشْرَكْتُمْ بِاللَّهِ مَا لَمْ يُنَزِّلْ بِهِ عَلَيْكُمْ سُلْطَانًا ۚ فَأَيُّ الْفَرِيقَيْنِ أَحَقُّ بِالْأَمْنِ ۖ إِنْ كُنْتُمْ تَعْلَمُونَ', 6, 81, 7, 28, 137, 'چگونه من از بتهای شما بترسم؟! در حالی که شما از این نمی‌ترسید که برای خدا، همتایی قرار داده‌اید که هیچ گونه دلیلی درباره آن، بر شما نازل نکرده است! (راست بگویید) کدام یک از این دو دسته (بت‌پرستان و خداپرستان)، شایسته‌تر به ایمنی (از مجازات) هستند اگر می‌دانید؟!'),
(871, 871, 'الَّذِينَ آمَنُوا وَلَمْ يَلْبِسُوا إِيمَانَهُمْ بِظُلْمٍ أُولَٰئِكَ لَهُمُ الْأَمْنُ وَهُمْ مُهْتَدُونَ', 6, 82, 7, 28, 138, '(آری،) آنها که ایمان آوردند، و ایمان خود را با شرک و ستم نیالودند، ایمنی تنها از آن آنهاست؛ و آنها هدایت‌یافتگانند!»'),
(872, 872, 'وَتِلْكَ حُجَّتُنَا آتَيْنَاهَا إِبْرَاهِيمَ عَلَىٰ قَوْمِهِ ۚ نَرْفَعُ دَرَجَاتٍ مَنْ نَشَاءُ ۗ إِنَّ رَبَّكَ حَكِيمٌ عَلِيمٌ', 6, 83, 7, 28, 138, 'اینها دلایل ما بود که به ابراهیم در برابر قومش دادیم! درجات هر کس را بخواهیم (و شایسته بدانیم،) بالا می‌بریم؛ پروردگار تو، حکیم و داناست.'),
(873, 873, 'وَوَهَبْنَا لَهُ إِسْحَاقَ وَيَعْقُوبَ ۚ كُلًّا هَدَيْنَا ۚ وَنُوحًا هَدَيْنَا مِنْ قَبْلُ ۖ وَمِنْ ذُرِّيَّتِهِ دَاوُودَ وَسُلَيْمَانَ وَأَيُّوبَ وَيُوسُفَ وَمُوسَىٰ وَهَارُونَ ۚ وَكَذَٰلِكَ نَجْزِي الْمُحْسِنِينَ', 6, 84, 7, 28, 138, 'و اسحاق و یعقوب را به او = ابراهیم‌ بخشیدیم؛ و هر دو را هدایت کردیم؛ و نوح را (نیز) پیش از آن هدایت نمودیم؛ و از فرزندان او، داوود و سلیمان و ایّوب و یوسف و موسی و هارون را (هدایت کردیم)؛ این‌گونه نیکوکاران را پاداش می‌دهیم!'),
(874, 874, 'وَزَكَرِيَّا وَيَحْيَىٰ وَعِيسَىٰ وَإِلْيَاسَ ۖ كُلٌّ مِنَ الصَّالِحِينَ', 6, 85, 7, 28, 138, 'و (همچنین) زکریّا و یحیی و عیسی و الیاس را؛ همه از صالحان بودند.'),
(875, 875, 'وَإِسْمَاعِيلَ وَالْيَسَعَ وَيُونُسَ وَلُوطًا ۚ وَكُلًّا فَضَّلْنَا عَلَى الْعَالَمِينَ', 6, 86, 7, 28, 138, 'و اسماعیل و الیسع و یونس و لوط را؛ و همه را بر جهانیان برتری دادیم.'),
(876, 876, 'وَمِنْ آبَائِهِمْ وَذُرِّيَّاتِهِمْ وَإِخْوَانِهِمْ ۖ وَاجْتَبَيْنَاهُمْ وَهَدَيْنَاهُمْ إِلَىٰ صِرَاطٍ مُسْتَقِيمٍ', 6, 87, 7, 28, 138, 'و از پدران و فرزندان و برادران آنها (افرادی را برتری دادیم) و برگزیدیم و به راه راست، هدایت نمودیم.'),
(877, 877, 'ذَٰلِكَ هُدَى اللَّهِ يَهْدِي بِهِ مَنْ يَشَاءُ مِنْ عِبَادِهِ ۚ وَلَوْ أَشْرَكُوا لَحَبِطَ عَنْهُمْ مَا كَانُوا يَعْمَلُونَ', 6, 88, 7, 28, 138, 'این، هدایت خداست؛ که هر کس از بندگان خود را بخواهد با آن راهنمایی می‌کند! و اگر آنها مشرک شوند، اعمال (نیکی) که انجام داده‌اند، نابود می‌گردد (و نتیجه‌ای از آن نمی‌گیرند).'),
(878, 878, 'أُولَٰئِكَ الَّذِينَ آتَيْنَاهُمُ الْكِتَابَ وَالْحُكْمَ وَالنُّبُوَّةَ ۚ فَإِنْ يَكْفُرْ بِهَا هَٰؤُلَاءِ فَقَدْ وَكَّلْنَا بِهَا قَوْمًا لَيْسُوا بِهَا بِكَافِرِينَ', 6, 89, 7, 28, 138, 'آنها کسانی هستند که کتاب و حکم و نبوّت به آنان دادیم؛ و اگر (بفرض) نسبت به آن کفر ورزند، (آیین حقّ زمین نمی‌ماند؛ زیرا) کسان دیگری را نگاهبان آن می‌سازیم که نسبت به آن، کافر نیستند.'),
(879, 879, 'أُولَٰئِكَ الَّذِينَ هَدَى اللَّهُ ۖ فَبِهُدَاهُمُ اقْتَدِهْ ۗ قُلْ لَا أَسْأَلُكُمْ عَلَيْهِ أَجْرًا ۖ إِنْ هُوَ إِلَّا ذِكْرَىٰ لِلْعَالَمِينَ', 6, 90, 7, 28, 138, 'آنها کسانی هستند که خداوند هدایتشان کرده؛ پس به هدایت آنان اقتدا کن! (و) بگو: «در برابر این (رسالت و تبلیغ)، پاداشی از شما نمی‌طلبم! این (رسالت)، چیزی جز یک یادآوری برای جهانیان نیست! (این وظیفه من است)»'),
(880, 880, 'وَمَا قَدَرُوا اللَّهَ حَقَّ قَدْرِهِ إِذْ قَالُوا مَا أَنْزَلَ اللَّهُ عَلَىٰ بَشَرٍ مِنْ شَيْءٍ ۗ قُلْ مَنْ أَنْزَلَ الْكِتَابَ الَّذِي جَاءَ بِهِ مُوسَىٰ نُورًا وَهُدًى لِلنَّاسِ ۖ تَجْعَلُونَهُ قَرَاطِيسَ تُبْدُونَهَا وَتُخْفُونَ كَثِيرًا ۖ وَعُلِّمْتُمْ مَا لَمْ تَعْلَمُوا أَنْتُمْ وَلَا آبَاؤُكُمْ ۖ قُلِ اللَّهُ ۖ ثُمَّ ذَرْهُمْ فِي خَوْضِهِمْ يَلْعَبُونَ', 6, 91, 7, 28, 139, 'آنها خدا را درست نشناختند که گفتند: «خدا، هیچ چیز بر هیچ انسانی، نفرستاده است!» بگو: «چه کسی کتابی را که موسی آورد، نازل کرد؟! کتابی که برای مردم، نور و هدایت بود؛ 0امّا شما) آن را بصورت پراکنده قرارمی‌دهید؛ قسمتی را آشکار، و قسمت زیادی را پنهان می‌دارید؛ و مطالبی به شما تعلیم داده شده که نه شما و نه پدرانتان، از آن با خبر نبودید!» بگو: «خدا!» سپس آنها را در گفتگوهای لجاجت‌آمیزشان رها کن، تا بازی کنند!'),
(881, 881, 'وَهَٰذَا كِتَابٌ أَنْزَلْنَاهُ مُبَارَكٌ مُصَدِّقُ الَّذِي بَيْنَ يَدَيْهِ وَلِتُنْذِرَ أُمَّ الْقُرَىٰ وَمَنْ حَوْلَهَا ۚ وَالَّذِينَ يُؤْمِنُونَ بِالْآخِرَةِ يُؤْمِنُونَ بِهِ ۖ وَهُمْ عَلَىٰ صَلَاتِهِمْ يُحَافِظُونَ', 6, 92, 7, 28, 139, 'و این کتابی است که ما آن را نازل کردیم؛ کتابی است پربرکت، که آنچه را پیش از آن آمده، تصدیق می‌کند؛ (آن را فرستادیم تا مردم را به پاداشهای الهی، بشارت دهی،) و تا (اهل) امّ‌القری = مکّه‌ و کسانی را که گرد آن هستند، بترسانی! (یقین بدان) آنها که به آخرت ایمان دارند، به آن ایمان می‌آورند؛ و بر نمازهای خویش، مراقبت می کنند!'),
(882, 882, 'وَمَنْ أَظْلَمُ مِمَّنِ افْتَرَىٰ عَلَى اللَّهِ كَذِبًا أَوْ قَالَ أُوحِيَ إِلَيَّ وَلَمْ يُوحَ إِلَيْهِ شَيْءٌ وَمَنْ قَالَ سَأُنْزِلُ مِثْلَ مَا أَنْزَلَ اللَّهُ ۗ وَلَوْ تَرَىٰ إِذِ الظَّالِمُونَ فِي غَمَرَاتِ الْمَوْتِ وَالْمَلَائِكَةُ بَاسِطُو أَيْدِيهِمْ أَخْرِجُوا أَنْفُسَكُمُ ۖ الْيَوْمَ تُجْزَوْنَ عَذَابَ الْهُونِ بِمَا كُنْتُمْ تَقُولُونَ عَلَى اللَّهِ غَيْرَ الْحَقِّ وَكُنْتُمْ عَنْ آيَاتِهِ تَسْتَكْبِرُونَ', 6, 93, 7, 28, 139, 'چه کسی ستمکارتر است از کسی که دروغی به خدا ببندد، یا بگوید: «بر من، وحی فرستاده شده»، در حالی که به او وحی نشده است، و کسی که بگوید: «من نیز همانند آنچه خدا نازل کرده است، نازل می‌کنم»؟! و اگر ببینی هنگامی که (این) ظالمان در شداید مرگ فرو رفته‌اند، و فرشتگان دستها را گشوده، به آنان می‌گویند: «جان خود را خارج سازید! امروز در برابر دروغهایی که به خدا بستید و نسبت به آیات او تکبّر ورزیدید، مجازات خوارکننده‌ای خواهید دید»! (به حال آنها تأسف خواهی خورد)'),
(883, 883, 'وَلَقَدْ جِئْتُمُونَا فُرَادَىٰ كَمَا خَلَقْنَاكُمْ أَوَّلَ مَرَّةٍ وَتَرَكْتُمْ مَا خَوَّلْنَاكُمْ وَرَاءَ ظُهُورِكُمْ ۖ وَمَا نَرَىٰ مَعَكُمْ شُفَعَاءَكُمُ الَّذِينَ زَعَمْتُمْ أَنَّهُمْ فِيكُمْ شُرَكَاءُ ۚ لَقَدْ تَقَطَّعَ بَيْنَكُمْ وَضَلَّ عَنْكُمْ مَا كُنْتُمْ تَزْعُمُونَ', 6, 94, 7, 28, 139, 'و (روز قیامت به آنها گفته می‌شود:) همه شما تنها به سوی ما بازگشت نمودید، همان‌گونه که روز اوّل شما را آفریدیم! و آنچه را به شما بخشیده بودیم، پشت سر گذاردید! و شفیعانی را که شریک در شفاعت خود می‌پنداشتید، با شما نمی‌بینیم! پیوندهای شما بریده شده است؛ و تمام آنچه را تکیه‌گاه خود تصوّر می‌کردید، از شما دور و گم شده‌اند!'),
(884, 884, ' إِنَّ اللَّهَ فَالِقُ الْحَبِّ وَالنَّوَىٰ ۖ يُخْرِجُ الْحَيَّ مِنَ الْمَيِّتِ وَمُخْرِجُ الْمَيِّتِ مِنَ الْحَيِّ ۚ ذَٰلِكُمُ اللَّهُ ۖ فَأَنَّىٰ تُؤْفَكُونَ', 6, 95, 7, 28, 140, 'خداوند، شکافنده دانه و هسته است؛ زنده را از مرده خارج می‌سازد، و مرده را از زنده بیرون می‌آورد؛ این است خدای شما! پس چگونه از حقّ منحرف می‌شوید؟!'),
(885, 885, 'فَالِقُ الْإِصْبَاحِ وَجَعَلَ اللَّيْلَ سَكَنًا وَالشَّمْسَ وَالْقَمَرَ حُسْبَانًا ۚ ذَٰلِكَ تَقْدِيرُ الْعَزِيزِ الْعَلِيمِ', 6, 96, 7, 28, 140, 'او شکافنده صبح است؛ و شب را مایه آرامش، و خورشید و ماه را وسیله حساب قرار داده است؛ این، اندازه‌گیری خداوند توانای داناست!'),
(886, 886, 'وَهُوَ الَّذِي جَعَلَ لَكُمُ النُّجُومَ لِتَهْتَدُوا بِهَا فِي ظُلُمَاتِ الْبَرِّ وَالْبَحْرِ ۗ قَدْ فَصَّلْنَا الْآيَاتِ لِقَوْمٍ يَعْلَمُونَ', 6, 97, 7, 28, 140, 'او کسی است که ستارگان را برای شما قرار داد، تا در تاریکیهای خشکی و دریا، به وسیله آنها راه یابید! ما نشانه‌ها (ی خود) را برای کسانی که می‌دانند، (و اهل فکر و اندیشه‌اند) بیان داشتیم!'),
(887, 887, 'وَهُوَ الَّذِي أَنْشَأَكُمْ مِنْ نَفْسٍ وَاحِدَةٍ فَمُسْتَقَرٌّ وَمُسْتَوْدَعٌ ۗ قَدْ فَصَّلْنَا الْآيَاتِ لِقَوْمٍ يَفْقَهُونَ', 6, 98, 7, 28, 140, 'او کسی است که شما را از یک نفس آفرید! و شما دو گروه هستید: بعضی پایدار (از نظر ایمان یا خلقت کامل)، و بعضی ناپایدار؛ ما آیات خود را برای کسانی که می‌فهمند، تشریح نمودیم!'),
(888, 888, 'وَهُوَ الَّذِي أَنْزَلَ مِنَ السَّمَاءِ مَاءً فَأَخْرَجْنَا بِهِ نَبَاتَ كُلِّ شَيْءٍ فَأَخْرَجْنَا مِنْهُ خَضِرًا نُخْرِجُ مِنْهُ حَبًّا مُتَرَاكِبًا وَمِنَ النَّخْلِ مِنْ طَلْعِهَا قِنْوَانٌ دَانِيَةٌ وَجَنَّاتٍ مِنْ أَعْنَابٍ وَالزَّيْتُونَ وَالرُّمَّانَ مُشْتَبِهًا وَغَيْرَ مُتَشَابِهٍ ۗ انْظُرُوا إِلَىٰ ثَمَرِهِ إِذَا أَثْمَرَ وَيَنْعِهِ ۚ إِنَّ فِي ذَٰلِكُمْ لَآيَاتٍ لِقَوْمٍ يُؤْمِنُونَ', 6, 99, 7, 28, 140, 'او کسی است که از آسمان، آبی نازل کرد، و به وسیله آن، گیاهان گوناگون رویاندیم؛ و از آن، ساقه‌ها و شاخه‌های سبز، خارج ساختیم؛ و از آنها دانه‌های متراکم، و از شکوفه نخل، شکوفه‌هایی با رشته‌های باریک بیرون فرستادیم؛ و باغهایی از انواع انگور و زیتون و انار، (گاه) شبیه به یکدیگر، و (گاه) بی‌شباهت! هنگامی که میوه می دهد، به میوه آن و طرز رسیدنش بنگرید که در آن، نشانه‌هایی (از عظمت خدا) برای افراد باایمان است!'),
(889, 889, 'وَجَعَلُوا لِلَّهِ شُرَكَاءَ الْجِنَّ وَخَلَقَهُمْ ۖ وَخَرَقُوا لَهُ بَنِينَ وَبَنَاتٍ بِغَيْرِ عِلْمٍ ۚ سُبْحَانَهُ وَتَعَالَىٰ عَمَّا يَصِفُونَ', 6, 100, 7, 28, 140, 'آنان برای خدا همتایانی از جنّ قرار دادند، در حالی که خداوند همه آنها را آفریده است؛ و برای خدا، به دروغ و از روی جهل، پسران و دخترانی ساختند؛ منزّه است خدا، و برتر است از آنچه توصیف می‌کنند!'),
(890, 890, 'بَدِيعُ السَّمَاوَاتِ وَالْأَرْضِ ۖ أَنَّىٰ يَكُونُ لَهُ وَلَدٌ وَلَمْ تَكُنْ لَهُ صَاحِبَةٌ ۖ وَخَلَقَ كُلَّ شَيْءٍ ۖ وَهُوَ بِكُلِّ شَيْءٍ عَلِيمٌ', 6, 101, 7, 28, 140, 'او پدید آورنده آسمانها و زمین است؛ چگونه ممکن است فرزندی داشته باشد؟! حال آنکه همسری نداشته، و همه چیز را آفریده؛ و او به همه چیز داناست.'),
(891, 891, 'ذَٰلِكُمُ اللَّهُ رَبُّكُمْ ۖ لَا إِلَٰهَ إِلَّا هُوَ ۖ خَالِقُ كُلِّ شَيْءٍ فَاعْبُدُوهُ ۚ وَهُوَ عَلَىٰ كُلِّ شَيْءٍ وَكِيلٌ', 6, 102, 7, 28, 141, '(آری، این است پروردگار شما! هیچ معبودی جز او نیست؛ آفریدگار همه چیز است؛ او را بپرستید و او نگهبان و مدّبر همه موجودات است.'),
(892, 892, 'لَا تُدْرِكُهُ الْأَبْصَارُ وَهُوَ يُدْرِكُ الْأَبْصَارَ ۖ وَهُوَ اللَّطِيفُ الْخَبِيرُ', 6, 103, 7, 28, 141, 'چشمها او را نمی‌بینند؛ ولی او همه چشمها را می‌بیند؛ و او بخشنده (انواع نعمتها، و با خبر از دقایق موجودات،) و آگاه (از همه) چیز است.'),
(893, 893, 'قَدْ جَاءَكُمْ بَصَائِرُ مِنْ رَبِّكُمْ ۖ فَمَنْ أَبْصَرَ فَلِنَفْسِهِ ۖ وَمَنْ عَمِيَ فَعَلَيْهَا ۚ وَمَا أَنَا عَلَيْكُمْ بِحَفِيظٍ', 6, 104, 7, 28, 141, 'دلایل روشن از طرف پروردگارتان برای شما آمد؛ کسی که (به وسیله آن، حقّ را) ببیند، به سود خود اوست؛ و کسی که از دیدن آن چشم بپوشد، به زیان خودش می‌باشد؛ و من نگاهبان شما نیستم (و شما را بر قبول ایمان مجبور نمی‌کنم)'),
(894, 894, 'وَكَذَٰلِكَ نُصَرِّفُ الْآيَاتِ وَلِيَقُولُوا دَرَسْتَ وَلِنُبَيِّنَهُ لِقَوْمٍ يَعْلَمُونَ', 6, 105, 7, 28, 141, 'و اینچنین آیات (خود) را تشریح می‌کنیم؛ بگذار آنها بگویند: «تو درس خوانده‌ای (و آنها را از دیگری آموخته‌ای)»! می‌خواهیم آن را برای کسانی که آماده درک حقایقند، روشن سازیم.'),
(895, 895, 'اتَّبِعْ مَا أُوحِيَ إِلَيْكَ مِنْ رَبِّكَ ۖ لَا إِلَٰهَ إِلَّا هُوَ ۖ وَأَعْرِضْ عَنِ الْمُشْرِكِينَ', 6, 106, 7, 28, 141, 'از آنچه که از سوی پروردگارت بر تو وحی شده، پیروی کن! هیچ معبودی جز او نیست! و از مشرکان، روی بگردان!'),
(896, 896, 'وَلَوْ شَاءَ اللَّهُ مَا أَشْرَكُوا ۗ وَمَا جَعَلْنَاكَ عَلَيْهِمْ حَفِيظًا ۖ وَمَا أَنْتَ عَلَيْهِمْ بِوَكِيلٍ', 6, 107, 7, 28, 141, 'اگر خدا می‌خواست، (همه به اجبار ایمان می‌آوردند،) و هیچ یک مشرک نمی‌شدند؛ و ما تو را مسؤول (اعمال) آنها قرار نداده‌ایم؛ و وظیفه نداری آنها را (به ایمان) مجبور سازی!'),
(897, 897, 'وَلَا تَسُبُّوا الَّذِينَ يَدْعُونَ مِنْ دُونِ اللَّهِ فَيَسُبُّوا اللَّهَ عَدْوًا بِغَيْرِ عِلْمٍ ۗ كَذَٰلِكَ زَيَّنَّا لِكُلِّ أُمَّةٍ عَمَلَهُمْ ثُمَّ إِلَىٰ رَبِّهِمْ مَرْجِعُهُمْ فَيُنَبِّئُهُمْ بِمَا كَانُوا يَعْمَلُونَ', 6, 108, 7, 28, 141, '(به معبود) کسانی که غیر خدا را می‌خوانند دشنام ندهید، مبادا آنها (نیز) از روی (ظلم و) جهل، خدا را دشنام دهند! اینچنین برای هر امّتی عملشان را زینت دادیم سپس بازگشت همه آنان به سوی پروردگارشان است؛ و آنها را از آنچه عمل می‌کردند، آگاه می‌سازد (و پاداش و کیفر می‌دهد).'),
(898, 898, 'وَأَقْسَمُوا بِاللَّهِ جَهْدَ أَيْمَانِهِمْ لَئِنْ جَاءَتْهُمْ آيَةٌ لَيُؤْمِنُنَّ بِهَا ۚ قُلْ إِنَّمَا الْآيَاتُ عِنْدَ اللَّهِ ۖ وَمَا يُشْعِرُكُمْ أَنَّهَا إِذَا جَاءَتْ لَا يُؤْمِنُونَ', 6, 109, 7, 28, 141, 'با نهایت اصرار، به خدا سوگند یاد کردند که اگر نشانه‌ای = معجزه‌ای‌ برای آنان بیاید، حتماً به آن ایمان می‌آورند؛ بگو: «معجزات فقط از سوی خداست (و در اختیار من نیست که به میل شما معجزه‌ای بیاورم)؛ و شما از کجا می‌دانید که هرگاه معجزه‌ای بیاید (ایمان می‌آورند؟ خیر،) ایمان نمی‌آورند!»'),
(899, 899, 'وَنُقَلِّبُ أَفْئِدَتَهُمْ وَأَبْصَارَهُمْ كَمَا لَمْ يُؤْمِنُوا بِهِ أَوَّلَ مَرَّةٍ وَنَذَرُهُمْ فِي طُغْيَانِهِمْ يَعْمَهُونَ', 6, 110, 7, 28, 141, 'و ما دلها و چشمهای آنها را واژگونه می‌سازیم؛ (آری آنها ایمان نمی‌آورند) همان‌گونه که در آغاز، به آن ایمان نیاوردند! و آنان را در حال طغیان و سرکشی، به خود وامی‌گذاریم تا سرگردان شوند!'),
(900, 900, ' وَلَوْ أَنَّنَا نَزَّلْنَا إِلَيْهِمُ الْمَلَائِكَةَ وَكَلَّمَهُمُ الْمَوْتَىٰ وَحَشَرْنَا عَلَيْهِمْ كُلَّ شَيْءٍ قُبُلًا مَا كَانُوا لِيُؤْمِنُوا إِلَّا أَنْ يَشَاءَ اللَّهُ وَلَٰكِنَّ أَكْثَرَهُمْ يَجْهَلُونَ', 6, 111, 8, 29, 142, '(و حتّی) اگر فرشتگان را بر آنها نازل می‌کردیم، و مردگان با آنان سخن می‌گفتند، و همه چیز را در برابر آنها جمع می‌نمودیم، هرگز ایمان نمی‌آوردند؛ مگر آنکه خدا بخواهد! ولی بیشتر آنها نمی‌دانند!'),
(901, 901, 'وَكَذَٰلِكَ جَعَلْنَا لِكُلِّ نَبِيٍّ عَدُوًّا شَيَاطِينَ الْإِنْسِ وَالْجِنِّ يُوحِي بَعْضُهُمْ إِلَىٰ بَعْضٍ زُخْرُفَ الْقَوْلِ غُرُورًا ۚ وَلَوْ شَاءَ رَبُّكَ مَا فَعَلُوهُ ۖ فَذَرْهُمْ وَمَا يَفْتَرُونَ', 6, 112, 8, 29, 142, 'اینچنین در برابر هر پیامبری، دشمنی از شیاطین انس و جنّ قرار دادیم؛ آنها بطور سری (و درگوشی) سخنان فریبنده و بی‌اساس (برای اغفال مردم) به یکدیگر می‌گفتند؛ و اگر پروردگارت می‌خواست، چنین نمی‌کردند؛ (و می‌توانست جلو آنها را بگیرد؛ ولی اجبار سودی ندارد.) بنابر این، آنها و تهمتهایشان را به حال خود واگذار!');
INSERT INTO `Quran` (`rowid`, `id`, `Arabic`, `Chapter`, `Verse`, `Juz`, `Hizb`, `Page`, `tr`) VALUES
(902, 902, 'وَلِتَصْغَىٰ إِلَيْهِ أَفْئِدَةُ الَّذِينَ لَا يُؤْمِنُونَ بِالْآخِرَةِ وَلِيَرْضَوْهُ وَلِيَقْتَرِفُوا مَا هُمْ مُقْتَرِفُونَ', 6, 113, 8, 29, 142, 'نتیجه (وسوسه‌های شیطان و تبلیغات شیطان‌صفتان) این خواهد شد که دلهای منکران قیامت، به آنها متمایل گردد؛ و به آن راضی شوند؛ و هر گناهی که بخواهند، انجام دهند!'),
(903, 903, 'أَفَغَيْرَ اللَّهِ أَبْتَغِي حَكَمًا وَهُوَ الَّذِي أَنْزَلَ إِلَيْكُمُ الْكِتَابَ مُفَصَّلًا ۚ وَالَّذِينَ آتَيْنَاهُمُ الْكِتَابَ يَعْلَمُونَ أَنَّهُ مُنَزَّلٌ مِنْ رَبِّكَ بِالْحَقِّ ۖ فَلَا تَكُونَنَّ مِنَ الْمُمْتَرِينَ', 6, 114, 8, 29, 142, '(با این حال،) آیا غیر خدا را به داوری طلبم؟! در حالی که اوست که این کتاب آسمانی را، که همه چیز در آن آمده، به سوی شما فرستاده است؛ و کسانی که به آنها کتاب آسمانی داده‌ایم می‌دانند این کتاب، بحق از طرف پروردگارت نازل شده؛ بنابر این از تردیدکنندگان مباش!'),
(904, 904, 'وَتَمَّتْ كَلِمَتُ رَبِّكَ صِدْقًا وَعَدْلًا ۚ لَا مُبَدِّلَ لِكَلِمَاتِهِ ۚ وَهُوَ السَّمِيعُ الْعَلِيمُ', 6, 115, 8, 29, 142, 'و کلام پروردگار تو، با صدق و عدل، به حدّ تمام رسید؛ هیچ کس نمی‌تواند کلمات او را دگرگون سازد؛ و او شنونده داناست.'),
(905, 905, 'وَإِنْ تُطِعْ أَكْثَرَ مَنْ فِي الْأَرْضِ يُضِلُّوكَ عَنْ سَبِيلِ اللَّهِ ۚ إِنْ يَتَّبِعُونَ إِلَّا الظَّنَّ وَإِنْ هُمْ إِلَّا يَخْرُصُونَ', 6, 116, 8, 29, 142, 'اگر از بیشتر کسانی که در روی زمین هستند اطاعت کنی، تو را از راه خدا گمراه می کنند؛ (زیرا) آنها تنها از گمان پیروی می‌نمایند، و تخمین و حدس (واهی) می‌زنند.'),
(906, 906, 'إِنَّ رَبَّكَ هُوَ أَعْلَمُ مَنْ يَضِلُّ عَنْ سَبِيلِهِ ۖ وَهُوَ أَعْلَمُ بِالْمُهْتَدِينَ', 6, 117, 8, 29, 142, 'پروردگارت به کسانی که از راه او گمراه گشته‌اند، آگاهتر است؛ و همچنین از کسانی که هدایت یافته‌اند.'),
(907, 907, 'فَكُلُوا مِمَّا ذُكِرَ اسْمُ اللَّهِ عَلَيْهِ إِنْ كُنْتُمْ بِآيَاتِهِ مُؤْمِنِينَ', 6, 118, 8, 29, 142, 'از (گوشت) آنچه نام خدا (هنگام سر بریدن) بر آن گفته شده، بخورید (و غیر از آن نخورید) اگر به آیات او ایمان دارید!'),
(908, 908, 'وَمَا لَكُمْ أَلَّا تَأْكُلُوا مِمَّا ذُكِرَ اسْمُ اللَّهِ عَلَيْهِ وَقَدْ فَصَّلَ لَكُمْ مَا حَرَّمَ عَلَيْكُمْ إِلَّا مَا اضْطُرِرْتُمْ إِلَيْهِ ۗ وَإِنَّ كَثِيرًا لَيُضِلُّونَ بِأَهْوَائِهِمْ بِغَيْرِ عِلْمٍ ۗ إِنَّ رَبَّكَ هُوَ أَعْلَمُ بِالْمُعْتَدِينَ', 6, 119, 8, 29, 143, 'چرا از چیزها = گوشتها ئی که نام خدا بر آنها برده شده نمی‌خورید؟! در حالی که (خداوند) آنچه را بر شما حرام بوده، بیان کرده است! مگر اینکه ناچار باشید؛ (که در این صورت، خوردن از گوشت آن حیوانات جایز است.) و بسیاری از مردم، به خاطر هوی و هوس و بی‌دانشی، (دیگران را) گمراه می‌سازند؛ و پروردگارت، تجاوزکاران را بهتر می‌شناسد.'),
(909, 909, 'وَذَرُوا ظَاهِرَ الْإِثْمِ وَبَاطِنَهُ ۚ إِنَّ الَّذِينَ يَكْسِبُونَ الْإِثْمَ سَيُجْزَوْنَ بِمَا كَانُوا يَقْتَرِفُونَ', 6, 120, 8, 29, 143, 'گناهان آشکار و پنهان را رها کنید! زیرا کسانی که گناه می‌کنند، بزودی در برابر آنچه مرتکب می‌شدند، مجازات خواهند شد.'),
(910, 910, 'وَلَا تَأْكُلُوا مِمَّا لَمْ يُذْكَرِ اسْمُ اللَّهِ عَلَيْهِ وَإِنَّهُ لَفِسْقٌ ۗ وَإِنَّ الشَّيَاطِينَ لَيُوحُونَ إِلَىٰ أَوْلِيَائِهِمْ لِيُجَادِلُوكُمْ ۖ وَإِنْ أَطَعْتُمُوهُمْ إِنَّكُمْ لَمُشْرِكُونَ', 6, 121, 8, 29, 143, 'و از آنچه نام خدا بر آن برده نشده، نخورید! این کار گناه است؛ و شیاطین به دوستان خود مطالبی مخفیانه القا می‌کنند، تا با شما به مجادله برخیزند؛ اگر از آنها اطاعت کنید، شما هم مشرک خواهید بود!'),
(911, 911, 'أَوَمَنْ كَانَ مَيْتًا فَأَحْيَيْنَاهُ وَجَعَلْنَا لَهُ نُورًا يَمْشِي بِهِ فِي النَّاسِ كَمَنْ مَثَلُهُ فِي الظُّلُمَاتِ لَيْسَ بِخَارِجٍ مِنْهَا ۚ كَذَٰلِكَ زُيِّنَ لِلْكَافِرِينَ مَا كَانُوا يَعْمَلُونَ', 6, 122, 8, 29, 143, 'آیا کسی که مرده بود، سپس او را زنده کردیم، و نوری برایش قرار دادیم که با آن در میان مردم راه برود، همانند کسی است که در ظلمتها باشد و از آن خارج نگردد؟! این گونه برای کافران، اعمال (زشتی) که انجام می‌دادند، تزیین شده (و زیبا جلوه کرده) است.'),
(912, 912, 'وَكَذَٰلِكَ جَعَلْنَا فِي كُلِّ قَرْيَةٍ أَكَابِرَ مُجْرِمِيهَا لِيَمْكُرُوا فِيهَا ۖ وَمَا يَمْكُرُونَ إِلَّا بِأَنْفُسِهِمْ وَمَا يَشْعُرُونَ', 6, 123, 8, 29, 143, 'و (نیز) این گونه در هر شهر و آبادی، بزرگان گنهکاری قرار دادیم؛ (افرادی که همه گونه قدرت در اختیارشان گذاردیم؛ اما آنها سوء استفاده کرده، و راه خطا پیش گرفتند؛) و سرانجام کارشان این شد که به مکر (و فریب مردم) پرداختند؛ ولی تنها خودشان را فریب می‌دهند و نمی‌فهمند!'),
(913, 913, 'وَإِذَا جَاءَتْهُمْ آيَةٌ قَالُوا لَنْ نُؤْمِنَ حَتَّىٰ نُؤْتَىٰ مِثْلَ مَا أُوتِيَ رُسُلُ اللَّهِ ۘ اللَّهُ أَعْلَمُ حَيْثُ يَجْعَلُ رِسَالَتَهُ ۗ سَيُصِيبُ الَّذِينَ أَجْرَمُوا صَغَارٌ عِنْدَ اللَّهِ وَعَذَابٌ شَدِيدٌ بِمَا كَانُوا يَمْكُرُونَ', 6, 124, 8, 29, 143, 'و هنگامی که آیه‌ای برای آنها بیاید، می‌گویند: «ما هرگز ایمان نمی‌آوریم، مگر اینکه همانند چیزی که به پیامبران خدا داده شده، به ما هم داده شود!» خداوند آگاهتر است که رسالت خویش را کجا قرار دهد! بزودی کسانی که مرتکب گناه شدند، (و مردم را از راه حق منحرف ساختند،) در مقابل مکر (و فریب و نیرنگی) که می‌کردند، گرفتار حقارت در پیشگاه خدا، و عذاب شدید خواهند شد.'),
(914, 914, 'فَمَنْ يُرِدِ اللَّهُ أَنْ يَهْدِيَهُ يَشْرَحْ صَدْرَهُ لِلْإِسْلَامِ ۖ وَمَنْ يُرِدْ أَنْ يُضِلَّهُ يَجْعَلْ صَدْرَهُ ضَيِّقًا حَرَجًا كَأَنَّمَا يَصَّعَّدُ فِي السَّمَاءِ ۚ كَذَٰلِكَ يَجْعَلُ اللَّهُ الرِّجْسَ عَلَى الَّذِينَ لَا يُؤْمِنُونَ', 6, 125, 8, 29, 144, 'آن کس را که خدا بخواهد هدایت کند، سینه‌اش را برای (پذیرش) اسلام، گشاده می سازد؛ و آن کس را که بخاطر اعمال خلافش بخواهد گمراه سازد، سینه‌اش را آنچنان تنگ می‌کند که گویا می‌خواهد به آسمان بالا برود؛ این گونه خداوند پلیدی را بر افرادی که ایمان نمی‌آورند قرار می‌دهد!'),
(915, 915, 'وَهَٰذَا صِرَاطُ رَبِّكَ مُسْتَقِيمًا ۗ قَدْ فَصَّلْنَا الْآيَاتِ لِقَوْمٍ يَذَّكَّرُونَ', 6, 126, 8, 29, 144, 'و این راه مستقیم (و سنت جاویدان) پروردگار توست؛ ما آیات خود را برای کسانی که پند می‌گیرند، بیان کردیم!'),
(916, 916, ' لَهُمْ دَارُ السَّلَامِ عِنْدَ رَبِّهِمْ ۖ وَهُوَ وَلِيُّهُمْ بِمَا كَانُوا يَعْمَلُونَ', 6, 127, 8, 29, 144, 'برای آنها (در بهشت) خانه امن و امان نزد پروردگارشان خواهد بود؛ و او، ولیّ و یاور آنهاست بخاطر اعمال (نیکی) که انجام می‌دادند.'),
(917, 917, 'وَيَوْمَ يَحْشُرُهُمْ جَمِيعًا يَا مَعْشَرَ الْجِنِّ قَدِ اسْتَكْثَرْتُمْ مِنَ الْإِنْسِ ۖ وَقَالَ أَوْلِيَاؤُهُمْ مِنَ الْإِنْسِ رَبَّنَا اسْتَمْتَعَ بَعْضُنَا بِبَعْضٍ وَبَلَغْنَا أَجَلَنَا الَّذِي أَجَّلْتَ لَنَا ۚ قَالَ النَّارُ مَثْوَاكُمْ خَالِدِينَ فِيهَا إِلَّا مَا شَاءَ اللَّهُ ۗ إِنَّ رَبَّكَ حَكِيمٌ عَلِيمٌ', 6, 128, 8, 29, 144, 'در آن روز که (خدا) همه آنها را جمع و محشور میسازد، (می‌گوید:) ای جمعیّت شیاطین و جن! ّ شما افراد زیادی از انسانها را گمراه ساختید! دوستان و پیروان آنها از میان انسانها می‌گویند: «پروردگارا! هر یک از ما دو گروه = پیشوایان و پیروان‌گمراه‌ از دیگری استفاده کردیم؛ (ما به لذّات هوس آلود و زودگذر رسیدیم؛ و آنها بر ما حکومت کردند؛) و به اجلی که برای ما مقرّر داشته بودی رسیدیم.» (خداوند) می‌گوید: «آتش جایگاه شماست؛ جاودانه در آن خواهید ماند، مگر آنچه خدا بخواهد» پروردگار تو حکیم و داناست.'),
(918, 918, 'وَكَذَٰلِكَ نُوَلِّي بَعْضَ الظَّالِمِينَ بَعْضًا بِمَا كَانُوا يَكْسِبُونَ', 6, 129, 8, 29, 144, 'ما این‌گونه بعضی از ستمگران را به بعضی دیگر وامی‌گذاریم به سبب اعمالی که انجام می‌دادند.'),
(919, 919, 'يَا مَعْشَرَ الْجِنِّ وَالْإِنْسِ أَلَمْ يَأْتِكُمْ رُسُلٌ مِنْكُمْ يَقُصُّونَ عَلَيْكُمْ آيَاتِي وَيُنْذِرُونَكُمْ لِقَاءَ يَوْمِكُمْ هَٰذَا ۚ قَالُوا شَهِدْنَا عَلَىٰ أَنْفُسِنَا ۖ وَغَرَّتْهُمُ الْحَيَاةُ الدُّنْيَا وَشَهِدُوا عَلَىٰ أَنْفُسِهِمْ أَنَّهُمْ كَانُوا كَافِرِينَ', 6, 130, 8, 29, 144, '(در آن روز به آنها می‌گوید:) ای گروه جنّ و انس! آیا رسولانی از شما به سوی شما نیامدند که آیات مرا برایتان بازگو می‌کردند، و شما را از ملاقات چنین روزی بیم می دادند؟! آنها می‌گویند: «بر ضدّ خودمان گواهی می‌دهیم؛ (آری، ما بد کردیم.)» و زندگی (پر زرق و برق) دنیا آنها را فریب داد؛ و به زیان خود گواهی می‌دهند که کافر بودند!'),
(920, 920, 'ذَٰلِكَ أَنْ لَمْ يَكُنْ رَبُّكَ مُهْلِكَ الْقُرَىٰ بِظُلْمٍ وَأَهْلُهَا غَافِلُونَ', 6, 131, 8, 29, 144, 'این بخاطر آن است که پروردگارت هیچ‌گاه (مردم) شهرها و آبادیها را بخاطر ستمهایشان در حال غفلت و بی‌خبری هلاک نمی‌کند. (بلکه قبلاً رسولانی برای آنها می‌فرستد.)'),
(921, 921, 'وَلِكُلٍّ دَرَجَاتٌ مِمَّا عَمِلُوا ۚ وَمَا رَبُّكَ بِغَافِلٍ عَمَّا يَعْمَلُونَ', 6, 132, 8, 29, 145, 'و برای هر یک (از این دو دسته)، درجات (و مراتبی) است از آنچه عمل کردند؛ و پروردگارت از اعمالی که انجام می‌دهند، غافل نیست.'),
(922, 922, 'وَرَبُّكَ الْغَنِيُّ ذُو الرَّحْمَةِ ۚ إِنْ يَشَأْ يُذْهِبْكُمْ وَيَسْتَخْلِفْ مِنْ بَعْدِكُمْ مَا يَشَاءُ كَمَا أَنْشَأَكُمْ مِنْ ذُرِّيَّةِ قَوْمٍ آخَرِينَ', 6, 133, 8, 29, 145, 'پروردگارت بی‌نیاز و مهربان است؛ (پس به کسی ستم نمی‌کند؛ بلکه همه، نتیجه اعمال خود را می‌گیرند؛) اگر بخواهد، همه شما را می‌برد؛ سپس هر کس را بخواهد جانشین شما می‌سازد؛ همان‌طور که شما را از نسل اقوام دیگری به وجود آورد.'),
(923, 923, 'إِنَّ مَا تُوعَدُونَ لَآتٍ ۖ وَمَا أَنْتُمْ بِمُعْجِزِينَ', 6, 134, 8, 29, 145, 'آنچه به شما وعده داده می‌شود، یقیناً می‌آید؛ و شما نمی‌توانید (خدا را) ناتوان سازید (و از عدالت و کیفر او فرار کنید)!'),
(924, 924, 'قُلْ يَا قَوْمِ اعْمَلُوا عَلَىٰ مَكَانَتِكُمْ إِنِّي عَامِلٌ ۖ فَسَوْفَ تَعْلَمُونَ مَنْ تَكُونُ لَهُ عَاقِبَةُ الدَّارِ ۗ إِنَّهُ لَا يُفْلِحُ الظَّالِمُونَ', 6, 135, 8, 29, 145, 'بگو: «ای قوم من! هر کار در قدرت دارید بکنید! من (هم به وظیفه خود) عمل می‌کنم؛ امّا بزودی خواهید دانست چه کسی سرانجام نیک خواهد داشت (و پیروزی با چه کسی است! امّا) به یقین، ظالمان رستگار نخواهند شد!»'),
(925, 925, 'وَجَعَلُوا لِلَّهِ مِمَّا ذَرَأَ مِنَ الْحَرْثِ وَالْأَنْعَامِ نَصِيبًا فَقَالُوا هَٰذَا لِلَّهِ بِزَعْمِهِمْ وَهَٰذَا لِشُرَكَائِنَا ۖ فَمَا كَانَ لِشُرَكَائِهِمْ فَلَا يَصِلُ إِلَى اللَّهِ ۖ وَمَا كَانَ لِلَّهِ فَهُوَ يَصِلُ إِلَىٰ شُرَكَائِهِمْ ۗ سَاءَ مَا يَحْكُمُونَ', 6, 136, 8, 29, 145, 'آنها = مشرکان‌ سهمی از آنچه خداوند از زراعت و چهارپایان آفریده، برای او قرار دادند؛ (و سهمی برای بتها!) و بگمان خود گفتند: «این مال خداست! و این هم مال شرکای ما = یعنی بتها است!» آنچه مال شرکای آنها بود، به خدا نمی‌رسید؛ ولی آنچه مال خدا بود، به شرکایشان می‌رسید! (آری، اگر سهم بتها با کمبودی مواجه می‌شد، مال خدا را به بتها می‌دادند؛ امّا عکس آن را مجاز نمی‌دانستند!) چه بد حکم می‌کنند (که علاوه بر شرک، حتّی خدا را کمتر از بتها می‌دانند)!'),
(926, 926, 'وَكَذَٰلِكَ زَيَّنَ لِكَثِيرٍ مِنَ الْمُشْرِكِينَ قَتْلَ أَوْلَادِهِمْ شُرَكَاؤُهُمْ لِيُرْدُوهُمْ وَلِيَلْبِسُوا عَلَيْهِمْ دِينَهُمْ ۖ وَلَوْ شَاءَ اللَّهُ مَا فَعَلُوهُ ۖ فَذَرْهُمْ وَمَا يَفْتَرُونَ', 6, 137, 8, 29, 145, 'همین‌گونه شرکای آنها = بتها، قتل فرزندانشان را در نظرشان جلوه دادند؛ (کودکان خود را قربانی بتها می‌کردند، و افتخار می‌نمودند!) سرانجام آنها را به هلاکت افکندند؛ و آیینشان را بر آنان مشتبه ساختند. و اگر خدا می‌خواست، چنین نمی‌کردند؛ (زیرا می‌توانست جلو آنان را بگیرد؛ ولی اجبار سودی ندارد.) بنابر این، آنها و تهمتهایشان را به حال خود واگذار (و به آنها اعتنا مکن)!'),
(927, 927, 'وَقَالُوا هَٰذِهِ أَنْعَامٌ وَحَرْثٌ حِجْرٌ لَا يَطْعَمُهَا إِلَّا مَنْ نَشَاءُ بِزَعْمِهِمْ وَأَنْعَامٌ حُرِّمَتْ ظُهُورُهَا وَأَنْعَامٌ لَا يَذْكُرُونَ اسْمَ اللَّهِ عَلَيْهَا افْتِرَاءً عَلَيْهِ ۚ سَيَجْزِيهِمْ بِمَا كَانُوا يَفْتَرُونَ', 6, 138, 8, 29, 146, 'و گفتند: «این قسمت از چهارپایان و زراعت (که مخصوص بتهاست، برای همه) ممنوع است؛ و جز کسانی که ما بخواهیم -به گمان آنها- نباید از آن بخورند! و (اینها) چهارپایانی است که سوارشدن بر آنها (بر ما) حرام شده است!» و (نیز) چهارپایانی (بود) که (هنگام ذبح،) نام خدا را بر آن نمی‌بردند، و به خدا دروغ می‌بستند؛ (و می‌گفتند: «این احکام، همه از ناحیه اوست.») بزودی (خدا) کیفر افتراهای آنها را می‌دهد!'),
(928, 928, 'وَقَالُوا مَا فِي بُطُونِ هَٰذِهِ الْأَنْعَامِ خَالِصَةٌ لِذُكُورِنَا وَمُحَرَّمٌ عَلَىٰ أَزْوَاجِنَا ۖ وَإِنْ يَكُنْ مَيْتَةً فَهُمْ فِيهِ شُرَكَاءُ ۚ سَيَجْزِيهِمْ وَصْفَهُمْ ۚ إِنَّهُ حَكِيمٌ عَلِيمٌ', 6, 139, 8, 29, 146, 'و گفتند: «آنچه (از بچّه) در شکم این حیوانات است، مخصوص مردان ماست؛ و بر همسران ما حرام است! امّا اگر مرده باشد = مرده متولّد شود، همگی در آن شریکند.» بزودی (خدا) کیفر این توصیف (و احکام دروغین) آنها را می‌دهد؛ او حکیم و داناست.'),
(929, 929, 'قَدْ خَسِرَ الَّذِينَ قَتَلُوا أَوْلَادَهُمْ سَفَهًا بِغَيْرِ عِلْمٍ وَحَرَّمُوا مَا رَزَقَهُمُ اللَّهُ افْتِرَاءً عَلَى اللَّهِ ۚ قَدْ ضَلُّوا وَمَا كَانُوا مُهْتَدِينَ', 6, 140, 8, 29, 146, 'به یقین آنها که فرزندان خود را از روی جهل و نادانی کشتند، گرفتار خسران شدند؛ (زیرا) آنچه را خدا به آنها روزی داده بود، بر خود تحریم کردند؛ و بر خدا افترا بستند. آنها گمراه شدند؛ و (هرگز) هدایت نیافته بودند.'),
(930, 930, ' وَهُوَ الَّذِي أَنْشَأَ جَنَّاتٍ مَعْرُوشَاتٍ وَغَيْرَ مَعْرُوشَاتٍ وَالنَّخْلَ وَالزَّرْعَ مُخْتَلِفًا أُكُلُهُ وَالزَّيْتُونَ وَالرُّمَّانَ مُتَشَابِهًا وَغَيْرَ مُتَشَابِهٍ ۚ كُلُوا مِنْ ثَمَرِهِ إِذَا أَثْمَرَ وَآتُوا حَقَّهُ يَوْمَ حَصَادِهِ ۖ وَلَا تُسْرِفُوا ۚ إِنَّهُ لَا يُحِبُّ الْمُسْرِفِينَ', 6, 141, 8, 30, 146, 'اوست که باغهای معروش = باغهایی که درختانش روی داربست‌ها قرار دارد، و باغهای غیرمعروش = باغهایی که نیاز به داربست ندارد را آفرید؛ همچنین نخل و انواع زراعت را، که از نظر میوه و طعم با هم متفاوتند؛ و (نیز) درخت زیتون و انار را، که از جهتی با هم شبیه، و از جهتی تفاوت دارند؛ (برگ و ساختمان ظاهریشان شبیه یکدیگر است، در حالی که طعم میوه آنها متفاوت می‌باشد.) از میوه آن، به هنگامی که به ثمر می‌نشیند، بخورید! و حقّ آن را به هنگام درو، بپردازید! و اسراف نکنید، که خداوند مسرفان را دوست ندارد!'),
(931, 931, 'وَمِنَ الْأَنْعَامِ حَمُولَةً وَفَرْشًا ۚ كُلُوا مِمَّا رَزَقَكُمُ اللَّهُ وَلَا تَتَّبِعُوا خُطُوَاتِ الشَّيْطَانِ ۚ إِنَّهُ لَكُمْ عَدُوٌّ مُبِينٌ', 6, 142, 8, 30, 146, '(او کسی است که) از چهارپایان، برای شما حیوانات باربر، و حیوانات کوچک (برای منافع دیگر) آفرید؛ از آنچه به شما روزی داده است، بخورید! و از گامهای شیطان پیروی ننمایید، که او دشمن آشکار شماست!'),
(932, 932, 'ثَمَانِيَةَ أَزْوَاجٍ ۖ مِنَ الضَّأْنِ اثْنَيْنِ وَمِنَ الْمَعْزِ اثْنَيْنِ ۗ قُلْ آلذَّكَرَيْنِ حَرَّمَ أَمِ الْأُنْثَيَيْنِ أَمَّا اشْتَمَلَتْ عَلَيْهِ أَرْحَامُ الْأُنْثَيَيْنِ ۖ نَبِّئُونِي بِعِلْمٍ إِنْ كُنْتُمْ صَادِقِينَ', 6, 143, 8, 30, 147, 'هشت جفت از چهارپایان (برای شما) آفرید؛ از میش دو جفت، و از بز دو جفت؛ بگو: «آیا خداوند نرهای آنها را حرام کرده، یا ماده‌ها را؟ یا آنچه شکم ماده‌ها در برگرفته؟ اگر راست می‌گویید (و بر تحریم اینها دلیلی دارید)، به من خبر دهید!»'),
(933, 933, 'وَمِنَ الْإِبِلِ اثْنَيْنِ وَمِنَ الْبَقَرِ اثْنَيْنِ ۗ قُلْ آلذَّكَرَيْنِ حَرَّمَ أَمِ الْأُنْثَيَيْنِ أَمَّا اشْتَمَلَتْ عَلَيْهِ أَرْحَامُ الْأُنْثَيَيْنِ ۖ أَمْ كُنْتُمْ شُهَدَاءَ إِذْ وَصَّاكُمُ اللَّهُ بِهَٰذَا ۚ فَمَنْ أَظْلَمُ مِمَّنِ افْتَرَىٰ عَلَى اللَّهِ كَذِبًا لِيُضِلَّ النَّاسَ بِغَيْرِ عِلْمٍ ۗ إِنَّ اللَّهَ لَا يَهْدِي الْقَوْمَ الظَّالِمِينَ', 6, 144, 8, 30, 147, 'و از شتر یک جفت، و از گاو هم یک جفت (برای شما آفرید)؛ بگو: «کدامیک از اینها را خدا حرام کرده است؟ نرها یا ماده‌ها را؟ یا آنچه را شکم ماده‌ها دربرگرفته؟ یا هنگامی که خدا شما را به این موضوع توصیه کرد، شما گواه (بر این تحریم) بودید؟! پس چه کسی ستمکارتر است از آن کس که بر خدا دروغ می‌بندد، تا مردم را از روی جهل گمراه سازد؟! خداوند هیچ گاه ستمگران را هدایت نمی‌کند»'),
(934, 934, 'قُلْ لَا أَجِدُ فِي مَا أُوحِيَ إِلَيَّ مُحَرَّمًا عَلَىٰ طَاعِمٍ يَطْعَمُهُ إِلَّا أَنْ يَكُونَ مَيْتَةً أَوْ دَمًا مَسْفُوحًا أَوْ لَحْمَ خِنْزِيرٍ فَإِنَّهُ رِجْسٌ أَوْ فِسْقًا أُهِلَّ لِغَيْرِ اللَّهِ بِهِ ۚ فَمَنِ اضْطُرَّ غَيْرَ بَاغٍ وَلَا عَادٍ فَإِنَّ رَبَّكَ غَفُورٌ رَحِيمٌ', 6, 145, 8, 30, 147, 'بگو: «در آنچه بر من وحی شده، هیچ غذای حرامی نمی‌یابم؛ بجز اینکه مردار باشد، یا خونی که (از بدن حیوان) بیرون ریخته، یا گوشت خوک -که اینها همه پلیدند- یا حیوانی که به گناه، هنگام سر بریدن، نام غیر خدا = نام بتها بر آن برده شده است.» اما کسی که مضطر (به خوردن این محرمات) شود، بی آنکه خواهان لذت باشد و یا زیاده روی کند (گناهی بر او نیست)؛ زیرا پروردگارت، آمرزنده مهربان است.'),
(935, 935, 'وَعَلَى الَّذِينَ هَادُوا حَرَّمْنَا كُلَّ ذِي ظُفُرٍ ۖ وَمِنَ الْبَقَرِ وَالْغَنَمِ حَرَّمْنَا عَلَيْهِمْ شُحُومَهُمَا إِلَّا مَا حَمَلَتْ ظُهُورُهُمَا أَوِ الْحَوَايَا أَوْ مَا اخْتَلَطَ بِعَظْمٍ ۚ ذَٰلِكَ جَزَيْنَاهُمْ بِبَغْيِهِمْ ۖ وَإِنَّا لَصَادِقُونَ', 6, 146, 8, 30, 147, 'و بر یهودیان، هر حیوان ناخن‌دار = حیواناتی که سم یکپارچه دارند را حرام کردیم؛ و از گاو و گوسفند، پیه و چربیشان را بر آنان تحریم نمودیم؛ مگر چربیهایی که بر پشت آنها قرار دارد، و یا در دو طرف پهلوها، و یا آنها که با استخوان آمیخته است؛ این را بخاطر ستمی که می‌کردند به آنها کیفر دادیم؛ و ما راست می‌گوییم.'),
(936, 936, 'فَإِنْ كَذَّبُوكَ فَقُلْ رَبُّكُمْ ذُو رَحْمَةٍ وَاسِعَةٍ وَلَا يُرَدُّ بَأْسُهُ عَنِ الْقَوْمِ الْمُجْرِمِينَ', 6, 147, 8, 30, 148, 'اگر تو را تکذیب کنند (و این حقایق را نپذیرند)، به آنها بگو: «پروردگار شما، رحمت گسترده‌ای دارد؛ اما مجازات او هم از مجرمان دفع شدنی نیست! (و اگر ادامه دهید کیفر شما حتمی است)'),
(937, 937, 'سَيَقُولُ الَّذِينَ أَشْرَكُوا لَوْ شَاءَ اللَّهُ مَا أَشْرَكْنَا وَلَا آبَاؤُنَا وَلَا حَرَّمْنَا مِنْ شَيْءٍ ۚ كَذَٰلِكَ كَذَّبَ الَّذِينَ مِنْ قَبْلِهِمْ حَتَّىٰ ذَاقُوا بَأْسَنَا ۗ قُلْ هَلْ عِنْدَكُمْ مِنْ عِلْمٍ فَتُخْرِجُوهُ لَنَا ۖ إِنْ تَتَّبِعُونَ إِلَّا الظَّنَّ وَإِنْ أَنْتُمْ إِلَّا تَخْرُصُونَ', 6, 148, 8, 30, 148, 'بزودی مشرکان (برای تبرئه خویش) می‌گویند: «اگر خدا می‌خواست، نه ما مشرک می‌شدیم و نه پدران ما؛ و نه چیزی را تحریم می‌کردیم!» کسانی که پیش از آنها بودند نیز، همین گونه دروغ می‌گفتند؛ و سرانجام (طعم) کیفر ما را چشیدند. بگو: «آیا دلیل روشنی (بر این موضوع) دارید؟ پس آن را به ما نشان دهید؟ شما فقط از پندارهای بی‌اساس پیروی می‌کنید، و تخمینهای نابجا می‌زنید.»'),
(938, 938, 'قُلْ فَلِلَّهِ الْحُجَّةُ الْبَالِغَةُ ۖ فَلَوْ شَاءَ لَهَدَاكُمْ أَجْمَعِينَ', 6, 149, 8, 30, 148, 'بگو: «دلیل رسا (و قاطع) برای خداست (دلیلی که برای هیچ کس بهانه‌ای باقی نمی‌گذارد). و اگر او بخواهد، همه شما را (به اجبار) هدایت می‌کند. (ولی چون هدایت اجباری بی‌ثمر است، این کار را نمی‌کند.)»'),
(939, 939, 'قُلْ هَلُمَّ شُهَدَاءَكُمُ الَّذِينَ يَشْهَدُونَ أَنَّ اللَّهَ حَرَّمَ هَٰذَا ۖ فَإِنْ شَهِدُوا فَلَا تَشْهَدْ مَعَهُمْ ۚ وَلَا تَتَّبِعْ أَهْوَاءَ الَّذِينَ كَذَّبُوا بِآيَاتِنَا وَالَّذِينَ لَا يُؤْمِنُونَ بِالْآخِرَةِ وَهُمْ بِرَبِّهِمْ يَعْدِلُونَ', 6, 150, 8, 30, 148, 'بگو: «گواهان خود را، که گواهی می‌دهند خداوند اینها را حرام کرده است، بیاورید!» اگر آنها (بدروغ) گواهی دهند، تو با آنان (همصدا نشو! و) گواهی نده! و از هوی و هوس کسانی که آیات ما را تکذیب کردند، و کسانی که به آخرت ایمان ندارند و برای خدا شریک قائلند، پیروی مکن!'),
(940, 940, ' قُلْ تَعَالَوْا أَتْلُ مَا حَرَّمَ رَبُّكُمْ عَلَيْكُمْ ۖ أَلَّا تُشْرِكُوا بِهِ شَيْئًا ۖ وَبِالْوَالِدَيْنِ إِحْسَانًا ۖ وَلَا تَقْتُلُوا أَوْلَادَكُمْ مِنْ إِمْلَاقٍ ۖ نَحْنُ نَرْزُقُكُمْ وَإِيَّاهُمْ ۖ وَلَا تَقْرَبُوا الْفَوَاحِشَ مَا ظَهَرَ مِنْهَا وَمَا بَطَنَ ۖ وَلَا تَقْتُلُوا النَّفْسَ الَّتِي حَرَّمَ اللَّهُ إِلَّا بِالْحَقِّ ۚ ذَٰلِكُمْ وَصَّاكُمْ بِهِ لَعَلَّكُمْ تَعْقِلُونَ', 6, 151, 8, 30, 148, 'بگو: «بیایید آنچه را پروردگارتان بر شما حرام کرده است برایتان بخوانم: اینکه چیزی را شریک خدا قرار ندهید! و به پدر و مادر نیکی کنید! و فرزندانتان را از (ترس) فقر، نکشید! ما شما و آنها را روزی می‌دهیم؛ و نزدیک کارهای زشت نروید، چه آشکار باشد چه پنهان! و انسانی را که خداوند محترم شمرده، به قتل نرسانید! مگر بحق (و از روی استحقاق)؛ این چیزی است که خداوند شما را به آن سفارش کرده، شاید درک کنید!'),
(941, 941, 'وَلَا تَقْرَبُوا مَالَ الْيَتِيمِ إِلَّا بِالَّتِي هِيَ أَحْسَنُ حَتَّىٰ يَبْلُغَ أَشُدَّهُ ۖ وَأَوْفُوا الْكَيْلَ وَالْمِيزَانَ بِالْقِسْطِ ۖ لَا نُكَلِّفُ نَفْسًا إِلَّا وُسْعَهَا ۖ وَإِذَا قُلْتُمْ فَاعْدِلُوا وَلَوْ كَانَ ذَا قُرْبَىٰ ۖ وَبِعَهْدِ اللَّهِ أَوْفُوا ۚ ذَٰلِكُمْ وَصَّاكُمْ بِهِ لَعَلَّكُمْ تَذَكَّرُونَ', 6, 152, 8, 30, 149, 'و به مال یتیم، جز به بهترین صورت (و برای اصلاح)، نزدیک نشوید، تا به حد رشد خود برسد! و حق پیمانه و وزن را بعدالت ادا کنید! -هیچ کس را، جز بمقدار تواناییش، تکلیف نمی‌کنیم- و هنگامی که سخنی می‌گویید، عدالت را رعایت نمایید، حتی اگر در مورد نزدیکان (شما) بوده باشد و به پیمان خدا وفا کنید، این چیزی است که خداوند شما را به آن سفارش می‌کند، تا متذکّر شوید!'),
(942, 942, 'وَأَنَّ هَٰذَا صِرَاطِي مُسْتَقِيمًا فَاتَّبِعُوهُ ۖ وَلَا تَتَّبِعُوا السُّبُلَ فَتَفَرَّقَ بِكُمْ عَنْ سَبِيلِهِ ۚ ذَٰلِكُمْ وَصَّاكُمْ بِهِ لَعَلَّكُمْ تَتَّقُونَ', 6, 153, 8, 30, 149, 'این راه مستقیم من است، از آن پیروی کنید! و از راه‌های پراکنده (و انحرافی) پیروی نکنید، که شما را از طریق حق، دور می‌سازد! این چیزی است که خداوند شما را به آن سفارش می‌کند، شاید پرهیزگاری پیشه کنید!»'),
(943, 943, 'ثُمَّ آتَيْنَا مُوسَى الْكِتَابَ تَمَامًا عَلَى الَّذِي أَحْسَنَ وَتَفْصِيلًا لِكُلِّ شَيْءٍ وَهُدًى وَرَحْمَةً لَعَلَّهُمْ بِلِقَاءِ رَبِّهِمْ يُؤْمِنُونَ', 6, 154, 8, 30, 149, 'سپس به موسی کتاب (آسمانی) دادیم؛ (و نعمت خود را) بر آنها که نیکوکار بودند، کامل کردیم؛ و همه چیز را (که مورد نیاز آنها بود، در آن) روشن ساختیم؛ کتابی که مایه هدایت و رحمت بود؛ شاید به لقای پروردگارشان (و روز رستاخیز)، ایمان بیاورند!'),
(944, 944, 'وَهَٰذَا كِتَابٌ أَنْزَلْنَاهُ مُبَارَكٌ فَاتَّبِعُوهُ وَاتَّقُوا لَعَلَّكُمْ تُرْحَمُونَ', 6, 155, 8, 30, 149, 'و این کتابی است پر برکت، که ما (بر تو) نازل کردیم؛ از آن پیروی کنید، و پرهیزگاری پیشه نمائید، باشد که مورد رحمت (خدا) قرار گیرید!'),
(945, 945, 'أَنْ تَقُولُوا إِنَّمَا أُنْزِلَ الْكِتَابُ عَلَىٰ طَائِفَتَيْنِ مِنْ قَبْلِنَا وَإِنْ كُنَّا عَنْ دِرَاسَتِهِمْ لَغَافِلِينَ', 6, 156, 8, 30, 149, '(ما این کتاب را با این امتیازات نازل کردیم) تا نگویید: «کتاب آسمانی تنها بر دو طایفه پیش از ما = یهود و نصاری‌ نازل شده بود؛ و ما از بحث و بررسی آنها بی خبر بودیم»!'),
(946, 946, 'أَوْ تَقُولُوا لَوْ أَنَّا أُنْزِلَ عَلَيْنَا الْكِتَابُ لَكُنَّا أَهْدَىٰ مِنْهُمْ ۚ فَقَدْ جَاءَكُمْ بَيِّنَةٌ مِنْ رَبِّكُمْ وَهُدًى وَرَحْمَةٌ ۚ فَمَنْ أَظْلَمُ مِمَّنْ كَذَّبَ بِآيَاتِ اللَّهِ وَصَدَفَ عَنْهَا ۗ سَنَجْزِي الَّذِينَ يَصْدِفُونَ عَنْ آيَاتِنَا سُوءَ الْعَذَابِ بِمَا كَانُوا يَصْدِفُونَ', 6, 157, 8, 30, 149, 'یا نگویید: «اگر کتاب آسمانی بر ما نازل می‌شد، از آنها هدایت یافته‌تر بودیم»! اینک آیات و دلایل روشن از جانب پروردگارتان، و هدایت و رحمت برای شما آمد! پس، چه کسی ستمکارتر است از کسی که آیات خدا را تکذیب کرده، و از آن روی گردانده است؟! اما بزودی کسانی را که از آیات ما روی می‌گردانند، بخاطر همین اعراض بی‌دلیلشان، مجازات شدید خواهیم کرد!'),
(947, 947, 'هَلْ يَنْظُرُونَ إِلَّا أَنْ تَأْتِيَهُمُ الْمَلَائِكَةُ أَوْ يَأْتِيَ رَبُّكَ أَوْ يَأْتِيَ بَعْضُ آيَاتِ رَبِّكَ ۗ يَوْمَ يَأْتِي بَعْضُ آيَاتِ رَبِّكَ لَا يَنْفَعُ نَفْسًا إِيمَانُهَا لَمْ تَكُنْ آمَنَتْ مِنْ قَبْلُ أَوْ كَسَبَتْ فِي إِيمَانِهَا خَيْرًا ۗ قُلِ انْتَظِرُوا إِنَّا مُنْتَظِرُونَ', 6, 158, 8, 30, 150, 'آیا جز این انتظار دارند که فرشتگان (مرگ) به سراغشان آیند، یا خداوند (خودش) به سوی آنها بیاید، یا بعضی از آیات پروردگارت (و نشانه‌های رستاخیز)؟! اما آن روز که بعضی از آیات پروردگارت تحقّق پذیرد، ایمان‌آوردن افرادی که قبلاً ایمان نیاورده‌اند، یا در ایمانشان عمل نیکی انجام نداده‌اند، سودی به حالشان نخواهد داشت! بگو: «(اکنون که شما چنین انتظارات نادرستی دارید،) انتظار بکشید ما هم انتظار (کیفر شما را) می‌کشیم!»'),
(948, 948, 'إِنَّ الَّذِينَ فَرَّقُوا دِينَهُمْ وَكَانُوا شِيَعًا لَسْتَ مِنْهُمْ فِي شَيْءٍ ۚ إِنَّمَا أَمْرُهُمْ إِلَى اللَّهِ ثُمَّ يُنَبِّئُهُمْ بِمَا كَانُوا يَفْعَلُونَ', 6, 159, 8, 30, 150, 'کسانی که آیین خود را پراکنده ساختند، و به دسته‌های گوناگون (و مذاهب مختلف) تقسیم شدند، تو هیچ گونه رابطه‌ای با آنها نداری! سر و کار آنها تنها با خداست؛ سپس خدا آنها را از آنچه انجام می‌دادند، با خبر می‌کند.'),
(949, 949, 'مَنْ جَاءَ بِالْحَسَنَةِ فَلَهُ عَشْرُ أَمْثَالِهَا ۖ وَمَنْ جَاءَ بِالسَّيِّئَةِ فَلَا يُجْزَىٰ إِلَّا مِثْلَهَا وَهُمْ لَا يُظْلَمُونَ', 6, 160, 8, 30, 150, 'هر کس کار نیکی بجا آورد، ده برابر آن پاداش دارد، و هر کس کار بدی انجام دهد، جز بمانند آن، کیفر نخواهد دید؛ و ستمی بر آنها نخواهد شد.'),
(950, 950, 'قُلْ إِنَّنِي هَدَانِي رَبِّي إِلَىٰ صِرَاطٍ مُسْتَقِيمٍ دِينًا قِيَمًا مِلَّةَ إِبْرَاهِيمَ حَنِيفًا ۚ وَمَا كَانَ مِنَ الْمُشْرِكِينَ', 6, 161, 8, 30, 150, 'بگو: «پروردگارم مرا به راه راست هدایت کرده؛ آیینی پابرجا (و ضامن سعادت دین و دنیا)؛ آیین ابراهیم؛ که از آیینهای خرافی روی برگرداند؛ و از مشرکان نبود.»'),
(951, 951, 'قُلْ إِنَّ صَلَاتِي وَنُسُكِي وَمَحْيَايَ وَمَمَاتِي لِلَّهِ رَبِّ الْعَالَمِينَ', 6, 162, 8, 30, 150, 'بگو: «نماز و تمام عبادات من، و زندگی و مرگ من، همه برای خداوند پروردگار جهانیان است.'),
(952, 952, 'لَا شَرِيكَ لَهُ ۖ وَبِذَٰلِكَ أُمِرْتُ وَأَنَا أَوَّلُ الْمُسْلِمِينَ', 6, 163, 8, 30, 150, 'همتایی برای او نیست؛ و به همین مأمور شده‌ام؛ و من نخستین مسلمانم!»'),
(953, 953, 'قُلْ أَغَيْرَ اللَّهِ أَبْغِي رَبًّا وَهُوَ رَبُّ كُلِّ شَيْءٍ ۚ وَلَا تَكْسِبُ كُلُّ نَفْسٍ إِلَّا عَلَيْهَا ۚ وَلَا تَزِرُ وَازِرَةٌ وِزْرَ أُخْرَىٰ ۚ ثُمَّ إِلَىٰ رَبِّكُمْ مَرْجِعُكُمْ فَيُنَبِّئُكُمْ بِمَا كُنْتُمْ فِيهِ تَخْتَلِفُونَ', 6, 164, 8, 30, 150, 'بگو: «آیا غیر خدا، پروردگاری را بطلبم، در حالی که او پروردگار همه چیز است؟! هیچ کس، عمل (بدی) جز به زیان خودش، انجام نمی‌دهد؛ و هیچ گنهکاری گناه دیگری را متحمّل نمی‌شود؛ سپس بازگشت همه شما به سوی پروردگارتان است؛ و شما را از آنچه در آن اختلاف داشتید، خبر خواهد داد.'),
(954, 954, 'وَهُوَ الَّذِي جَعَلَكُمْ خَلَائِفَ الْأَرْضِ وَرَفَعَ بَعْضَكُمْ فَوْقَ بَعْضٍ دَرَجَاتٍ لِيَبْلُوَكُمْ فِي مَا آتَاكُمْ ۗ إِنَّ رَبَّكَ سَرِيعُ الْعِقَابِ وَإِنَّهُ لَغَفُورٌ رَحِيمٌ', 6, 165, 8, 30, 150, 'و او کسی است که شما را جانشینان (و نمایندگان) خود در زمین ساخت، و درجات بعضی از شما را بالاتر از بعضی دیگر قرار داد، تا شما را به وسیله آنچه در اختیارتان قرار داده بیازماید؛ به یقین پروردگار تو سریع العقاب و آمرزنده مهربان است. (کیفر کسانی را که از بوته امتحان نادرست درآیند، زود می‌دهد؛ و نسبت به حق پویان مهربان است.)'),
(955, 955, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ المص', 7, 1, 8, 31, 151, 'المص‌'),
(956, 956, 'كِتَابٌ أُنْزِلَ إِلَيْكَ فَلَا يَكُنْ فِي صَدْرِكَ حَرَجٌ مِنْهُ لِتُنْذِرَ بِهِ وَذِكْرَىٰ لِلْمُؤْمِنِينَ', 7, 2, 8, 31, 151, 'این کتابی است که بر تو نازل شده؛ و نباید از ناحیه آن، ناراحتی در سینه داشته باشی! تا به وسیله آن، (مردم را از عواقب سوء عقاید و اعمال نادرستشان) بیم دهی؛ و تذکّری است برای مؤمنان.'),
(957, 957, 'اتَّبِعُوا مَا أُنْزِلَ إِلَيْكُمْ مِنْ رَبِّكُمْ وَلَا تَتَّبِعُوا مِنْ دُونِهِ أَوْلِيَاءَ ۗ قَلِيلًا مَا تَذَكَّرُونَ', 7, 3, 8, 31, 151, 'از چیزی که از طرف پروردگارتان بر شما نازل شده، پیروی کنید! و از اولیا و معبودهای دیگر جز او، پیروی نکنید! اما کمتر متذکّر می‌شوید!'),
(958, 958, 'وَكَمْ مِنْ قَرْيَةٍ أَهْلَكْنَاهَا فَجَاءَهَا بَأْسُنَا بَيَاتًا أَوْ هُمْ قَائِلُونَ', 7, 4, 8, 31, 151, 'چه بسیار شهرها و آبادیها که آنها را (بر اثر گناه فراوانشان) هلاک کردیم! و عذاب ما شب‌هنگام، یا در روز هنگامی که استراحت کرده بودند، به سراغشان آمد.'),
(959, 959, 'فَمَا كَانَ دَعْوَاهُمْ إِذْ جَاءَهُمْ بَأْسُنَا إِلَّا أَنْ قَالُوا إِنَّا كُنَّا ظَالِمِينَ', 7, 5, 8, 31, 151, 'و در آن موقع که عذاب ما به سراغ آنها آمد، سخنی نداشتند جز اینکه گفتند: «ما ظالم بودیم!» (ولی این اعتراف به گناه، دیگر دیر شده بود؛ و سودی به حالشان نداشت.)'),
(960, 960, 'فَلَنَسْأَلَنَّ الَّذِينَ أُرْسِلَ إِلَيْهِمْ وَلَنَسْأَلَنَّ الْمُرْسَلِينَ', 7, 6, 8, 31, 151, 'به یقین، (هم) از کسانی که پیامبران به سوی آنها فرستاده شدند سؤال خواهیم کرد؛ (و هم) از پیامبران سؤال می‌کنیم!'),
(961, 961, 'فَلَنَقُصَّنَّ عَلَيْهِمْ بِعِلْمٍ ۖ وَمَا كُنَّا غَائِبِينَ', 7, 7, 8, 31, 151, 'و مسلماً (اعمالشان را) با علم (خود) برای آنان شرح خواهیم داد؛ و ما هرگز غایب نبودیم (بلکه همه جا حاضر و ناظر اعمال بندگان هستیم)!'),
(962, 962, 'وَالْوَزْنُ يَوْمَئِذٍ الْحَقُّ ۚ فَمَنْ ثَقُلَتْ مَوَازِينُهُ فَأُولَٰئِكَ هُمُ الْمُفْلِحُونَ', 7, 8, 8, 31, 151, 'وزن کردن (اعمال، و سنجش ارزش آنها) در آن روز، حقّ است! کسانی که میزانهای (عمل) آنها سنگین است، همان رستگارانند!'),
(963, 963, 'وَمَنْ خَفَّتْ مَوَازِينُهُ فَأُولَٰئِكَ الَّذِينَ خَسِرُوا أَنْفُسَهُمْ بِمَا كَانُوا بِآيَاتِنَا يَظْلِمُونَ', 7, 9, 8, 31, 151, 'و کسانی که میزانهای (عمل) آنها سبک است، افرادی هستند که سرمایه وجود خود را، بخاطر ظلم و ستمی که نسبت به آیات ما می‌کردند، از دست داده‌اند.'),
(964, 964, 'وَلَقَدْ مَكَّنَّاكُمْ فِي الْأَرْضِ وَجَعَلْنَا لَكُمْ فِيهَا مَعَايِشَ ۗ قَلِيلًا مَا تَشْكُرُونَ', 7, 10, 8, 31, 151, 'ما تسلّط و مالکیّت و حکومت بر زمین را برای شما قرار دادیم؛ و انواع وسایل زندگی را برای شما فراهم ساختیم؛ اما کمتر شکرگزاری می‌کنید!'),
(965, 965, 'وَلَقَدْ خَلَقْنَاكُمْ ثُمَّ صَوَّرْنَاكُمْ ثُمَّ قُلْنَا لِلْمَلَائِكَةِ اسْجُدُوا لِآدَمَ فَسَجَدُوا إِلَّا إِبْلِيسَ لَمْ يَكُنْ مِنَ السَّاجِدِينَ', 7, 11, 8, 31, 151, 'ما شما را آفریدیم؛ سپس صورت بندی کردیم؛ بعد به فرشتگان گفتیم: «برای آدم خضوع کنید!» آنها همه سجده کردند؛ جز ابلیس که از سجده‌کنندگان نبود.'),
(966, 966, 'قَالَ مَا مَنَعَكَ أَلَّا تَسْجُدَ إِذْ أَمَرْتُكَ ۖ قَالَ أَنَا خَيْرٌ مِنْهُ خَلَقْتَنِي مِنْ نَارٍ وَخَلَقْتَهُ مِنْ طِينٍ', 7, 12, 8, 31, 152, '(خداوند به او) فرمود: «در آن هنگام که به تو فرمان دادم، چه چیز تو را مانع شد که سجده کنی؟» گفت: «من از او بهترم؛ مرا از آتش آفریده‌ای و او را از گل!»'),
(967, 967, 'قَالَ فَاهْبِطْ مِنْهَا فَمَا يَكُونُ لَكَ أَنْ تَتَكَبَّرَ فِيهَا فَاخْرُجْ إِنَّكَ مِنَ الصَّاغِرِينَ', 7, 13, 8, 31, 152, 'گفت: «از آن (مقام و مرتبه‌ات) فرود آی! تو حقّ نداری در آن (مقام و مرتبه) تکبّر کنی! بیرون رو، که تو از افراد پست و کوچکی!'),
(968, 968, 'قَالَ أَنْظِرْنِي إِلَىٰ يَوْمِ يُبْعَثُونَ', 7, 14, 8, 31, 152, 'گفت: «مرا تا روزی که (مردم) برانگیخته می‌شوند مهلت ده (و زنده بگذار!)»'),
(969, 969, 'قَالَ إِنَّكَ مِنَ الْمُنْظَرِينَ', 7, 15, 8, 31, 152, 'فرمود: «تو از مهلت داده شدگانی!»'),
(970, 970, 'قَالَ فَبِمَا أَغْوَيْتَنِي لَأَقْعُدَنَّ لَهُمْ صِرَاطَكَ الْمُسْتَقِيمَ', 7, 16, 8, 31, 152, 'گفت: «اکنون که مرا گمراه ساختی، من بر سر راه مستقیم تو، در برابر آنها کمین می کنم!'),
(971, 971, 'ثُمَّ لَآتِيَنَّهُمْ مِنْ بَيْنِ أَيْدِيهِمْ وَمِنْ خَلْفِهِمْ وَعَنْ أَيْمَانِهِمْ وَعَنْ شَمَائِلِهِمْ ۖ وَلَا تَجِدُ أَكْثَرَهُمْ شَاكِرِينَ', 7, 17, 8, 31, 152, 'سپس از پیش رو و از پشت سر، و از طرف راست و از طرف چپ آنها، به سراغشان می‌روم؛ و بیشتر آنها را شکرگزار نخواهی یافت!»'),
(972, 972, 'قَالَ اخْرُجْ مِنْهَا مَذْءُومًا مَدْحُورًا ۖ لَمَنْ تَبِعَكَ مِنْهُمْ لَأَمْلَأَنَّ جَهَنَّمَ مِنْكُمْ أَجْمَعِينَ', 7, 18, 8, 31, 152, 'فرمود: «از آن (مقام)، با ننگ و عار و خواری، بیرون رو! و سوگند یاد می‌کنم که هر کس از آنها از تو پیروی کند، جهنم را از شما همگی پر می‌کنم!'),
(973, 973, 'وَيَا آدَمُ اسْكُنْ أَنْتَ وَزَوْجُكَ الْجَنَّةَ فَكُلَا مِنْ حَيْثُ شِئْتُمَا وَلَا تَقْرَبَا هَٰذِهِ الشَّجَرَةَ فَتَكُونَا مِنَ الظَّالِمِينَ', 7, 19, 8, 31, 152, 'و ای آدم! تو و همسرت در بهشت ساکن شوید! و از هر جا که خواستید، بخورید! امّا به این درخت نزدیک نشوید، که از ستمکاران خواهید بود!»'),
(974, 974, 'فَوَسْوَسَ لَهُمَا الشَّيْطَانُ لِيُبْدِيَ لَهُمَا مَا وُورِيَ عَنْهُمَا مِنْ سَوْآتِهِمَا وَقَالَ مَا نَهَاكُمَا رَبُّكُمَا عَنْ هَٰذِهِ الشَّجَرَةِ إِلَّا أَنْ تَكُونَا مَلَكَيْنِ أَوْ تَكُونَا مِنَ الْخَالِدِينَ', 7, 20, 8, 31, 152, 'سپس شیطان آن دو را وسوسه کرد، تا آنچه را از اندامشان پنهان بود، آشکار سازد؛ و گفت: «پروردگارتان شما را از این درخت نهی نکرده مگر بخاطر اینکه (اگر از آن بخورید،) فرشته خواهید شد، یا جاودانه (در بهشت) خواهید ماند!»'),
(975, 975, 'وَقَاسَمَهُمَا إِنِّي لَكُمَا لَمِنَ النَّاصِحِينَ', 7, 21, 8, 31, 152, 'و برای آنها سوگند یاد کرد که من برای شما از خیرخواهانم.'),
(976, 976, 'فَدَلَّاهُمَا بِغُرُورٍ ۚ فَلَمَّا ذَاقَا الشَّجَرَةَ بَدَتْ لَهُمَا سَوْآتُهُمَا وَطَفِقَا يَخْصِفَانِ عَلَيْهِمَا مِنْ وَرَقِ الْجَنَّةِ ۖ وَنَادَاهُمَا رَبُّهُمَا أَلَمْ أَنْهَكُمَا عَنْ تِلْكُمَا الشَّجَرَةِ وَأَقُلْ لَكُمَا إِنَّ الشَّيْطَانَ لَكُمَا عَدُوٌّ مُبِينٌ', 7, 22, 8, 31, 152, 'و به این ترتیب، آنها را با فریب (از مقامشان) فرودآورد. و هنگامی که از آن درخت چشیدند، اندامشان = عورتشان‌ بر آنها آشکار شد؛ و شروع کردند به قرار دادن برگهای (درختان) بهشتی بر خود، تا آن را بپوشانند. و پروردگارشان آنها را نداد داد که: «آیا شما را از آن درخت نهی نکردم؟! و نگفتم که شیطان برای شما دشمن آشکاری است؟!»'),
(977, 977, 'قَالَا رَبَّنَا ظَلَمْنَا أَنْفُسَنَا وَإِنْ لَمْ تَغْفِرْ لَنَا وَتَرْحَمْنَا لَنَكُونَنَّ مِنَ الْخَاسِرِينَ', 7, 23, 8, 31, 153, 'گفتند: «پروردگارا! ما به خویشتن ستم کردیم! و اگر ما را نبخشی و بر ما رحم نکنی، از زیانکاران خواهیم بود!»'),
(978, 978, 'قَالَ اهْبِطُوا بَعْضُكُمْ لِبَعْضٍ عَدُوٌّ ۖ وَلَكُمْ فِي الْأَرْضِ مُسْتَقَرٌّ وَمَتَاعٌ إِلَىٰ حِينٍ', 7, 24, 8, 31, 153, 'فرمود: «(از مقام خویش،) فرود آیید، در حالی که بعضی از شما نسبت به بعض دیگر، دشمن خواهید بود! (شیطان دشمن شماست، و شما دشمن او!) و برای شما در زمین، قرارگاه و وسیله بهره‌گیری تا زمان معینی خواهد بود.»'),
(979, 979, 'قَالَ فِيهَا تَحْيَوْنَ وَفِيهَا تَمُوتُونَ وَمِنْهَا تُخْرَجُونَ', 7, 25, 8, 31, 153, 'فرمود: «در آن = زمین‌ زنده می‌شوید؛ و در آن می‌میرید؛ و (در رستاخیز) از آن خارج خواهید شد.»'),
(980, 980, 'يَا بَنِي آدَمَ قَدْ أَنْزَلْنَا عَلَيْكُمْ لِبَاسًا يُوَارِي سَوْآتِكُمْ وَرِيشًا ۖ وَلِبَاسُ التَّقْوَىٰ ذَٰلِكَ خَيْرٌ ۚ ذَٰلِكَ مِنْ آيَاتِ اللَّهِ لَعَلَّهُمْ يَذَّكَّرُونَ', 7, 26, 8, 31, 153, 'ای فرزندان آدم! لباسی برای شما فرستادیم که اندام شما را می‌پوشاند و مایه زینت شماست؛ اما لباس پرهیزگاری بهتر است! اینها (همه) از آیات خداست، تا متذکّر (نعمتهای او) شوند!'),
(981, 981, 'يَا بَنِي آدَمَ لَا يَفْتِنَنَّكُمُ الشَّيْطَانُ كَمَا أَخْرَجَ أَبَوَيْكُمْ مِنَ الْجَنَّةِ يَنْزِعُ عَنْهُمَا لِبَاسَهُمَا لِيُرِيَهُمَا سَوْآتِهِمَا ۗ إِنَّهُ يَرَاكُمْ هُوَ وَقَبِيلُهُ مِنْ حَيْثُ لَا تَرَوْنَهُمْ ۗ إِنَّا جَعَلْنَا الشَّيَاطِينَ أَوْلِيَاءَ لِلَّذِينَ لَا يُؤْمِنُونَ', 7, 27, 8, 31, 153, 'ای فرزندان آدم! شیطان شما را نفریبد، آن گونه که پدر و مادر شما را از بهشت بیرون کرد، و لباسشان را از تنشان بیرون ساخت تا عورتشان را به آنها نشان دهد! چه اینکه او و همکارانش شما را می‌بینند از جایی که شما آنها را نمی‌بینید؛ (امّا بدانید) ما شیاطین را اولیای کسانی قرار دادیم که ایمان نمی‌آورند!'),
(982, 982, 'وَإِذَا فَعَلُوا فَاحِشَةً قَالُوا وَجَدْنَا عَلَيْهَا آبَاءَنَا وَاللَّهُ أَمَرَنَا بِهَا ۗ قُلْ إِنَّ اللَّهَ لَا يَأْمُرُ بِالْفَحْشَاءِ ۖ أَتَقُولُونَ عَلَى اللَّهِ مَا لَا تَعْلَمُونَ', 7, 28, 8, 31, 153, 'و هنگامی که کار زشتی انجام می‌دهند می‌گویند: «پدران خود را بر این عمل یافتیم؛ و خداوند ما را به آن دستور داده است!» بگو: «خداوند (هرگز) به کار زشت فرمان نمی‌دهد! آیا چیزی به خدا نسبت می‌دهید که نمی‌دانید؟!»'),
(983, 983, 'قُلْ أَمَرَ رَبِّي بِالْقِسْطِ ۖ وَأَقِيمُوا وُجُوهَكُمْ عِنْدَ كُلِّ مَسْجِدٍ وَادْعُوهُ مُخْلِصِينَ لَهُ الدِّينَ ۚ كَمَا بَدَأَكُمْ تَعُودُونَ', 7, 29, 8, 31, 153, 'بگو: «پروردگارم امر به عدالت کرده است؛ و توجّه خویش را در هر مسجد (و به هنگام عبادت) به سوی او کنید! و او را بخوانید، در حالی که دین (خود) را برای او خالص گردانید! (و بدانید) همان گونه که در آغاز شما را آفرید، (بار دیگر در رستاخیز) بازمی‌گردید!'),
(984, 984, 'فَرِيقًا هَدَىٰ وَفَرِيقًا حَقَّ عَلَيْهِمُ الضَّلَالَةُ ۗ إِنَّهُمُ اتَّخَذُوا الشَّيَاطِينَ أَوْلِيَاءَ مِنْ دُونِ اللَّهِ وَيَحْسَبُونَ أَنَّهُمْ مُهْتَدُونَ', 7, 30, 8, 31, 153, 'جمعی را هدایت کرده؛ و جمعی (که شایستگی نداشته‌اند،) گمراهی بر آنها مسلّم شده است. آنها (کسانی هستند که) شیاطین را به جای خداوند، اولیای خود انتخاب کردند؛ و گمان می‌کنند هدایت یافته‌اند!'),
(985, 985, ' يَا بَنِي آدَمَ خُذُوا زِينَتَكُمْ عِنْدَ كُلِّ مَسْجِدٍ وَكُلُوا وَاشْرَبُوا وَلَا تُسْرِفُوا ۚ إِنَّهُ لَا يُحِبُّ الْمُسْرِفِينَ', 7, 31, 8, 31, 154, 'ای فرزندان آدم! زینت خود را به هنگام رفتن به مسجد، با خود بردارید! و (از نعمتهای الهی) بخورید و بیاشامید، ولی اسراف نکنید که خداوند مسرفان را دوست نمی‌دارد!'),
(986, 986, 'قُلْ مَنْ حَرَّمَ زِينَةَ اللَّهِ الَّتِي أَخْرَجَ لِعِبَادِهِ وَالطَّيِّبَاتِ مِنَ الرِّزْقِ ۚ قُلْ هِيَ لِلَّذِينَ آمَنُوا فِي الْحَيَاةِ الدُّنْيَا خَالِصَةً يَوْمَ الْقِيَامَةِ ۗ كَذَٰلِكَ نُفَصِّلُ الْآيَاتِ لِقَوْمٍ يَعْلَمُونَ', 7, 32, 8, 31, 154, 'بگو: «چه کسی زینتهای الهی را که برای بندگان خود آفریده، و روزیهای پاکیزه را حرام کرده است؟!» بگو: «اینها در زندگی دنیا، برای کسانی است که ایمان آورده‌اند؛ (اگر چه دیگران نیز با آنها مشارکت دارند؛ ولی) در قیامت، خالص (برای مؤمنان) خواهد بود.» این گونه آیات (خود) را برای کسانی که آگاهند، شرح می‌دهیم!'),
(987, 987, 'قُلْ إِنَّمَا حَرَّمَ رَبِّيَ الْفَوَاحِشَ مَا ظَهَرَ مِنْهَا وَمَا بَطَنَ وَالْإِثْمَ وَالْبَغْيَ بِغَيْرِ الْحَقِّ وَأَنْ تُشْرِكُوا بِاللَّهِ مَا لَمْ يُنَزِّلْ بِهِ سُلْطَانًا وَأَنْ تَقُولُوا عَلَى اللَّهِ مَا لَا تَعْلَمُونَ', 7, 33, 8, 31, 154, 'بگو: «خداوند، تنها اعمال زشت را، چه آشکار باشد چه پنهان، حرام کرده است؛ و (همچنین) گناه و ستم بناحق را؛ و اینکه چیزی را که خداوند دلیلی برای آن نازل نکرده، شریک او قرار دهید؛ و به خدا مطلبی نسبت دهید که نمی‌دانید.»'),
(988, 988, 'وَلِكُلِّ أُمَّةٍ أَجَلٌ ۖ فَإِذَا جَاءَ أَجَلُهُمْ لَا يَسْتَأْخِرُونَ سَاعَةً ۖ وَلَا يَسْتَقْدِمُونَ', 7, 34, 8, 31, 154, 'برای هر قوم و جمعیّتی، زمان و سرآمد (معیّنی) است؛ و هنگامی که سرآمد آنها فرا رسد، نه ساعتی از آن تأخیر می‌کنند، و نه بر آن پیشی می‌گیرند.'),
(989, 989, 'يَا بَنِي آدَمَ إِمَّا يَأْتِيَنَّكُمْ رُسُلٌ مِنْكُمْ يَقُصُّونَ عَلَيْكُمْ آيَاتِي ۙ فَمَنِ اتَّقَىٰ وَأَصْلَحَ فَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَ', 7, 35, 8, 31, 154, 'ای فرزندان آدم! اگر رسولانی از خود شما به سراغتان بیایند که آیات مرا برای شما بازگو کنند، (از آنها پیروی کنید؛) کسانی که پرهیزگاری پیشه کنند و عمل صالح انجام دهند (و در اصلاح خویش و دیگران بکوشند)، نه ترسی بر آنهاست و نه غمناک می شوند.'),
(990, 990, 'وَالَّذِينَ كَذَّبُوا بِآيَاتِنَا وَاسْتَكْبَرُوا عَنْهَا أُولَٰئِكَ أَصْحَابُ النَّارِ ۖ هُمْ فِيهَا خَالِدُونَ', 7, 36, 8, 31, 154, 'و آنها که آیات ما را تکذیب کنند، و در برابر آن تکبّر ورزند، اهل دوزخند؛ جاودانه در آن خواهند ماند.'),
(991, 991, 'فَمَنْ أَظْلَمُ مِمَّنِ افْتَرَىٰ عَلَى اللَّهِ كَذِبًا أَوْ كَذَّبَ بِآيَاتِهِ ۚ أُولَٰئِكَ يَنَالُهُمْ نَصِيبُهُمْ مِنَ الْكِتَابِ ۖ حَتَّىٰ إِذَا جَاءَتْهُمْ رُسُلُنَا يَتَوَفَّوْنَهُمْ قَالُوا أَيْنَ مَا كُنْتُمْ تَدْعُونَ مِنْ دُونِ اللَّهِ ۖ قَالُوا ضَلُّوا عَنَّا وَشَهِدُوا عَلَىٰ أَنْفُسِهِمْ أَنَّهُمْ كَانُوا كَافِرِينَ', 7, 37, 8, 31, 154, 'چه کسی ستمکارتر است از آنها که بر خدا دروغ می‌بندند، یا آیات او را تکذیب می کنند؟! آنها نصیبشان را از آنچه مقدّر شده (از نعمتها و مواهب این جهان) می‌برند؛ تا زمانی که فرستادگان ما = فرشتگان قبض ارواح‌ به سراغشان روند و جانشان را بگیرند؛ از آنها می‌پرسند: «کجایند معبودهایی که غیر از خدا می‌خواندید؟! (چرا به یاری شما نمی‌آیند؟!)» می‌گویند: «آنها (همه) گم شدند (و از ما دور گشتند!)» و بر ضدّ خود گواهی می‌دهند که کافر بودند!'),
(992, 992, 'قَالَ ادْخُلُوا فِي أُمَمٍ قَدْ خَلَتْ مِنْ قَبْلِكُمْ مِنَ الْجِنِّ وَالْإِنْسِ فِي النَّارِ ۖ كُلَّمَا دَخَلَتْ أُمَّةٌ لَعَنَتْ أُخْتَهَا ۖ حَتَّىٰ إِذَا ادَّارَكُوا فِيهَا جَمِيعًا قَالَتْ أُخْرَاهُمْ لِأُولَاهُمْ رَبَّنَا هَٰؤُلَاءِ أَضَلُّونَا فَآتِهِمْ عَذَابًا ضِعْفًا مِنَ النَّارِ ۖ قَالَ لِكُلٍّ ضِعْفٌ وَلَٰكِنْ لَا تَعْلَمُونَ', 7, 38, 8, 31, 155, '(خداوند به آنها) می‌گوید: «در صفّ گروه‌های مشابه خود از جنّ و انس در آتش وارد شوید!» هر زمان که گروهی وارد می‌شوند، گروه دیگر را لعن می‌کنند؛ تا همگی با ذلّت در آن قرار گیرند. (در این هنگام) گروه پیروان درباره پیشوایان خود می‌گویند: «خداوندا! اینها بودند که ما را گمراه ساختند؛ پس کیفر آنها را از آتش دو برابر کن! (کیفری برای گمراهیشان، و کیفری بخاطر گمراه ساختن ما.)» می‌فرماید: «برای هر کدام (از شما) عذاب مضاعف است؛ ولی نمی‌دانید! (چرا که پیروان اگر گرد پیشوایان گمراه را نگرفته بودند، قدرتی بر اغوای مردم نداشتند.)»'),
(993, 993, 'وَقَالَتْ أُولَاهُمْ لِأُخْرَاهُمْ فَمَا كَانَ لَكُمْ عَلَيْنَا مِنْ فَضْلٍ فَذُوقُوا الْعَذَابَ بِمَا كُنْتُمْ تَكْسِبُونَ', 7, 39, 8, 31, 155, 'و پیشوایان آنها به پیروان خود می‌گویند: «شما امتیازی بر ما نداشتید؛ پس بچشید عذاب (الهی) را در برابر آنچه انجام می‌دادید!'),
(994, 994, 'إِنَّ الَّذِينَ كَذَّبُوا بِآيَاتِنَا وَاسْتَكْبَرُوا عَنْهَا لَا تُفَتَّحُ لَهُمْ أَبْوَابُ السَّمَاءِ وَلَا يَدْخُلُونَ الْجَنَّةَ حَتَّىٰ يَلِجَ الْجَمَلُ فِي سَمِّ الْخِيَاطِ ۚ وَكَذَٰلِكَ نَجْزِي الْمُجْرِمِينَ', 7, 40, 8, 31, 155, 'کسانی که آیات ما را تکذیب کردند، و در برابر آن تکبّر ورزیدند، (هرگز) درهای آسمان به رویشان گشوده نمی‌شود؛ و (هیچ گاه) داخل بهشت نخواهند شد مگر اینکه شتر از سوراخ سوزن بگذرد! این گونه، گنهکاران را جزا می‌دهیم!'),
(995, 995, 'لَهُمْ مِنْ جَهَنَّمَ مِهَادٌ وَمِنْ فَوْقِهِمْ غَوَاشٍ ۚ وَكَذَٰلِكَ نَجْزِي الظَّالِمِينَ', 7, 41, 8, 31, 155, 'برای آنها بستری از (آتش) دوزخ، و روی آنها پوششهایی (از آن) است؛ و اینچنین ظالمان را جزا میدهیم!'),
(996, 996, 'وَالَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ لَا نُكَلِّفُ نَفْسًا إِلَّا وُسْعَهَا أُولَٰئِكَ أَصْحَابُ الْجَنَّةِ ۖ هُمْ فِيهَا خَالِدُونَ', 7, 42, 8, 31, 155, 'و کسانی که ایمان آورده و اعمال صالح انجام داده‌اند -البته هیچ کس را جز به اندازه تواناییش تکلیف نمی‌کنیم- آنها اهل بهشتند؛ و جاودانه در آن خواهند ماند.'),
(997, 997, 'وَنَزَعْنَا مَا فِي صُدُورِهِمْ مِنْ غِلٍّ تَجْرِي مِنْ تَحْتِهِمُ الْأَنْهَارُ ۖ وَقَالُوا الْحَمْدُ لِلَّهِ الَّذِي هَدَانَا لِهَٰذَا وَمَا كُنَّا لِنَهْتَدِيَ لَوْلَا أَنْ هَدَانَا اللَّهُ ۖ لَقَدْ جَاءَتْ رُسُلُ رَبِّنَا بِالْحَقِّ ۖ وَنُودُوا أَنْ تِلْكُمُ الْجَنَّةُ أُورِثْتُمُوهَا بِمَا كُنْتُمْ تَعْمَلُونَ', 7, 43, 8, 31, 155, 'و آنچه در دلها از کینه و حسد دارند، برمی‌کنیم (تا در صفا و صمیمیّت با هم زندگی کنند)؛ و از زیر (قصرها و درختان) آنها، نهرها جریان دارد؛ می‌گویند: «ستایش مخصوص خداوندی است که ما را به این (همه نعمتها) رهنمون شد؛ و اگر خدا ما را هدایت نکرده بود، ما (به اینها) راه نمی‌یافتیم! مسلّماً فرستادگان پروردگار ما حق را آوردند!» و (در این هنگام) به آنان ندا داده می‌شود که: «این بهشت را در برابر اعمالی که انجام می‌دادید، به ارث بردید!»'),
(998, 998, 'وَنَادَىٰ أَصْحَابُ الْجَنَّةِ أَصْحَابَ النَّارِ أَنْ قَدْ وَجَدْنَا مَا وَعَدَنَا رَبُّنَا حَقًّا فَهَلْ وَجَدْتُمْ مَا وَعَدَ رَبُّكُمْ حَقًّا ۖ قَالُوا نَعَمْ ۚ فَأَذَّنَ مُؤَذِّنٌ بَيْنَهُمْ أَنْ لَعْنَةُ اللَّهِ عَلَى الظَّالِمِينَ', 7, 44, 8, 31, 156, 'و بهشتیان دوزخیان را صدا می‌زنند که: «آنچه را پروردگارمان به ما وعده داده بود، همه را حق یافتیم؛ آیا شما هم آنچه را پروردگارتان به شما وعده داده بود حق یافتید؟!» گفتند: «بله!» در این هنگام، ندادهنده‌ای در میان آنها ندا می‌دهد که: «لعنت خدا بر ستمگران باد!'),
(999, 999, 'الَّذِينَ يَصُدُّونَ عَنْ سَبِيلِ اللَّهِ وَيَبْغُونَهَا عِوَجًا وَهُمْ بِالْآخِرَةِ كَافِرُونَ', 7, 45, 8, 31, 156, 'همانها که (مردم را) از راه خدا بازمی‌دارند، و (با القای شبهات) می‌خواهند آن را کج و معوج نشان دهند؛ و آنها به آخرت کافرند!»'),
(1000, 1000, 'وَبَيْنَهُمَا حِجَابٌ ۚ وَعَلَى الْأَعْرَافِ رِجَالٌ يَعْرِفُونَ كُلًّا بِسِيمَاهُمْ ۚ وَنَادَوْا أَصْحَابَ الْجَنَّةِ أَنْ سَلَامٌ عَلَيْكُمْ ۚ لَمْ يَدْخُلُوهَا وَهُمْ يَطْمَعُونَ', 7, 46, 8, 31, 156, 'و در میان آن دو = بهشتیان و دوزخیان‌، حجابی است؛ و بر «اعراف» مردانی هستند که هر یک از آن دو را از چهره‌شان می‌شناسند؛ و به بهشتیان صدا می‌زنند که: «درود بر شما باد!» امّا داخل بهشت نمی‌شوند، در حالی که امید آن را دارند.'),
(1001, 1001, ' وَإِذَا صُرِفَتْ أَبْصَارُهُمْ تِلْقَاءَ أَصْحَابِ النَّارِ قَالُوا رَبَّنَا لَا تَجْعَلْنَا مَعَ الْقَوْمِ الظَّالِمِينَ', 7, 47, 8, 32, 156, 'و هنگامی که چشمشان به دوزخیان می‌افتد می‌گویند: «پروردگارا! ما را با گروه ستمگران قرار مده!»'),
(1002, 1002, 'وَنَادَىٰ أَصْحَابُ الْأَعْرَافِ رِجَالًا يَعْرِفُونَهُمْ بِسِيمَاهُمْ قَالُوا مَا أَغْنَىٰ عَنْكُمْ جَمْعُكُمْ وَمَا كُنْتُمْ تَسْتَكْبِرُونَ', 7, 48, 8, 32, 156, 'و اصحاب اعراف، مردانی (از دوزخیان را) که از سیمایشان آنها را می‌شناسند، صدا می‌زنند و می‌گویند: «(دیدید که) گردآوری شما (از مال و ثروت و آن و فرزند) و تکبّرهای شما، به حالتان سودی نداد!»'),
(1003, 1003, 'أَهَٰؤُلَاءِ الَّذِينَ أَقْسَمْتُمْ لَا يَنَالُهُمُ اللَّهُ بِرَحْمَةٍ ۚ ادْخُلُوا الْجَنَّةَ لَا خَوْفٌ عَلَيْكُمْ وَلَا أَنْتُمْ تَحْزَنُونَ', 7, 49, 8, 32, 156, 'آیا اینها = این واماندگان بر اعراف‌ همانان نیستند که سوگند یاد کردید رحمت خدا هرگز شامل حالشان نخواهد شد؟! (ولی خداوند بخاطر ایمان و بعضی اعمال خیرشان، آنها را بخشید؛ هم اکنون به آنها گفته می‌شود:) داخل بهشت شوید، که نه ترسی دارید و نه غمناک می‌شوید!'),
(1004, 1004, 'وَنَادَىٰ أَصْحَابُ النَّارِ أَصْحَابَ الْجَنَّةِ أَنْ أَفِيضُوا عَلَيْنَا مِنَ الْمَاءِ أَوْ مِمَّا رَزَقَكُمُ اللَّهُ ۚ قَالُوا إِنَّ اللَّهَ حَرَّمَهُمَا عَلَى الْكَافِرِينَ', 7, 50, 8, 32, 156, 'و دوزخیان، بهشتیان را صدا می‌زنند که: «(محبّت کنید) و مقداری آب، یا از آنچه خدا به شما روزی داده، به ما ببخشید!» آنها (در پاسخ) می‌گویند: «خداوند اینها را بر کافران حرام کرده است!»'),
(1005, 1005, 'الَّذِينَ اتَّخَذُوا دِينَهُمْ لَهْوًا وَلَعِبًا وَغَرَّتْهُمُ الْحَيَاةُ الدُّنْيَا ۚ فَالْيَوْمَ نَنْسَاهُمْ كَمَا نَسُوا لِقَاءَ يَوْمِهِمْ هَٰذَا وَمَا كَانُوا بِآيَاتِنَا يَجْحَدُونَ', 7, 51, 8, 32, 156, 'همانها که دین و آیین خود را سرگرمی و بازیچه گرفتند؛ و زندگی دنیا آنان را مغرور ساخت؛ امروز ما آنها را فراموش می‌کنیم، همان گونه که لقای چنین روزی را فراموش کردند و آیات ما را انکار نمودند.'),
(1006, 1006, 'وَلَقَدْ جِئْنَاهُمْ بِكِتَابٍ فَصَّلْنَاهُ عَلَىٰ عِلْمٍ هُدًى وَرَحْمَةً لِقَوْمٍ يُؤْمِنُونَ', 7, 52, 8, 32, 157, 'ما کتابی برای آنها آوردیم که (اسرار و رموز) آن را با آگاهی شرح دادیم؛ (کتابی) که مایه هدایت و رحمت برای جمعیّتی است که ایمان می‌آورند.'),
(1007, 1007, 'هَلْ يَنْظُرُونَ إِلَّا تَأْوِيلَهُ ۚ يَوْمَ يَأْتِي تَأْوِيلُهُ يَقُولُ الَّذِينَ نَسُوهُ مِنْ قَبْلُ قَدْ جَاءَتْ رُسُلُ رَبِّنَا بِالْحَقِّ فَهَلْ لَنَا مِنْ شُفَعَاءَ فَيَشْفَعُوا لَنَا أَوْ نُرَدُّ فَنَعْمَلَ غَيْرَ الَّذِي كُنَّا نَعْمَلُ ۚ قَدْ خَسِرُوا أَنْفُسَهُمْ وَضَلَّ عَنْهُمْ مَا كَانُوا يَفْتَرُونَ', 7, 53, 8, 32, 157, 'آیا آنها جز انتظار تأویل آیات (و فرا رسیدن تهدیدهای الهی) دارند؟ آن روز که تأویل آنها فرا رسد، (کار از کار گذشته، و پشیمانی سودی ندارد؛ و) کسانی که قبلاً آن را فراموش کرده بودند می‌گویند: «فرستادگان پروردگار ما، حق را آوردند؛ آیا (امروز) شفیعانی برای ما وجود دارند که برای ما شفاعت کنند؟ یا (به ما اجازه داده شود به دنیا) بازگردیم، و اعمالی غیر از آنچه انجام می‌دادیم، انجام دهیم؟!» (ولی) آنها سرمایه وجود خود را از دست داده‌اند؛ و معبودهایی را که به دروغ ساخته بودند، همگی از نظرشان: گم می‌شوند. (نه راه بازگشتی دارند، و نه شفیعانی!)'),
(1008, 1008, 'إِنَّ رَبَّكُمُ اللَّهُ الَّذِي خَلَقَ السَّمَاوَاتِ وَالْأَرْضَ فِي سِتَّةِ أَيَّامٍ ثُمَّ اسْتَوَىٰ عَلَى الْعَرْشِ يُغْشِي اللَّيْلَ النَّهَارَ يَطْلُبُهُ حَثِيثًا وَالشَّمْسَ وَالْقَمَرَ وَالنُّجُومَ مُسَخَّرَاتٍ بِأَمْرِهِ ۗ أَلَا لَهُ الْخَلْقُ وَالْأَمْرُ ۗ تَبَارَكَ اللَّهُ رَبُّ الْعَالَمِينَ', 7, 54, 8, 32, 157, 'پروردگار شما، خداوندی است که آسمانها و زمین را در شش روز = شش دوران‌ آفرید؛ سپس به تدبیر جهان هستی پرداخت؛ با (پرده تاریک) شب، روز را می پوشاند؛ و شب به دنبال روز، به سرعت در حرکت است؛ و خورشید و ماه و ستارگان را آفرید، که مسخّر فرمان او هستند. آگاه باشید که آفرینش و تدبیر (جهان)، از آن او (و به فرمان او) ست! پر برکت (و زوال‌ناپذیر) است خداوندی که پروردگار جهانیان است!'),
(1009, 1009, 'ادْعُوا رَبَّكُمْ تَضَرُّعًا وَخُفْيَةً ۚ إِنَّهُ لَا يُحِبُّ الْمُعْتَدِينَ', 7, 55, 8, 32, 157, 'پروردگار خود را (آشکارا) از روی تضرّع، و در پنهانی، بخوانید! (و از تجاوز، دست بردارید که) او متجاوزان را دوست نمی‌دارد!'),
(1010, 1010, 'وَلَا تُفْسِدُوا فِي الْأَرْضِ بَعْدَ إِصْلَاحِهَا وَادْعُوهُ خَوْفًا وَطَمَعًا ۚ إِنَّ رَحْمَتَ اللَّهِ قَرِيبٌ مِنَ الْمُحْسِنِينَ', 7, 56, 8, 32, 157, 'و در زمین پس از اصلاح آن فساد نکنید، و او را با بیم و امید بخوانید! (بیم از مسؤولیتها، و امید به رحمتش. و نیکی کنید) زیرا رحمت خدا به نیکوکاران نزدیک است!'),
(1011, 1011, 'وَهُوَ الَّذِي يُرْسِلُ الرِّيَاحَ بُشْرًا بَيْنَ يَدَيْ رَحْمَتِهِ ۖ حَتَّىٰ إِذَا أَقَلَّتْ سَحَابًا ثِقَالًا سُقْنَاهُ لِبَلَدٍ مَيِّتٍ فَأَنْزَلْنَا بِهِ الْمَاءَ فَأَخْرَجْنَا بِهِ مِنْ كُلِّ الثَّمَرَاتِ ۚ كَذَٰلِكَ نُخْرِجُ الْمَوْتَىٰ لَعَلَّكُمْ تَذَكَّرُونَ', 7, 57, 8, 32, 157, 'او کسی است که بادها را بشارت دهنده در پیشاپیش (باران) رحمتش می‌فرستد؛ تا ابرهای سنگین‌بار را (بر دوش) کشند؛ (سپس) ما آنها را به سوی زمینهای مرده می‌فرستیم؛ و به وسیله آنها، آب (حیاتبخش) را نازل می‌کنیم؛ و با آن، از هرگونه میوه‌ای (از خاک تیره) بیرون می‌آوریم؛ این گونه (که زمینهای مرده را زنده کردیم،) مردگان را (نیز در قیامت) زنده می‌کنیم، شاید (با توجه به این مثال) متذکّر شوید!'),
(1012, 1012, 'وَالْبَلَدُ الطَّيِّبُ يَخْرُجُ نَبَاتُهُ بِإِذْنِ رَبِّهِ ۖ وَالَّذِي خَبُثَ لَا يَخْرُجُ إِلَّا نَكِدًا ۚ كَذَٰلِكَ نُصَرِّفُ الْآيَاتِ لِقَوْمٍ يَشْكُرُونَ', 7, 58, 8, 32, 158, 'سرزمین پاکیزه) و شیرین)، گیاهش به فرمان پروردگار می‌روید؛ امّا سرزمینهای بد طینت (و شوره‌زار)، جز گیاه ناچیز و بی‌ارزش، از آن نمی‌روید؛ این گونه آیات (خود) را برای آنها که شکرگزارند، بیان می‌کنیم!'),
(1013, 1013, 'لَقَدْ أَرْسَلْنَا نُوحًا إِلَىٰ قَوْمِهِ فَقَالَ يَا قَوْمِ اعْبُدُوا اللَّهَ مَا لَكُمْ مِنْ إِلَٰهٍ غَيْرُهُ إِنِّي أَخَافُ عَلَيْكُمْ عَذَابَ يَوْمٍ عَظِيمٍ', 7, 59, 8, 32, 158, 'ما نوح را به سوی قومش فرستادیم؛ او به آنان گفت: «ای قوم من! (تنها) خداوند یگانه را پرستش کنید، که معبودی جز او برای شما نیست! (و اگر غیر او را عبادت کنید،) من بر شما از عذاب روز بزرگی می‌ترسم!»'),
(1014, 1014, 'قَالَ الْمَلَأُ مِنْ قَوْمِهِ إِنَّا لَنَرَاكَ فِي ضَلَالٍ مُبِينٍ', 7, 60, 8, 32, 158, '(ولی) اشراف قومش به او گفتند: «ما تو را در گمراهی آشکاری می‌بینیم!»'),
(1015, 1015, 'قَالَ يَا قَوْمِ لَيْسَ بِي ضَلَالَةٌ وَلَٰكِنِّي رَسُولٌ مِنْ رَبِّ الْعَالَمِينَ', 7, 61, 8, 32, 158, 'گفت: «ای قوم من! هیچ گونه گمراهی در من نیست؛ ولی من فرستاده‌ای از جانب پروردگار جهانیانم!'),
(1016, 1016, 'أُبَلِّغُكُمْ رِسَالَاتِ رَبِّي وَأَنْصَحُ لَكُمْ وَأَعْلَمُ مِنَ اللَّهِ مَا لَا تَعْلَمُونَ', 7, 62, 8, 32, 158, 'رسالتهای پروردگارم را به شما ابلاغ می‌کنم؛ و خیرخواه شما هستم؛ و از خداوند چیزهایی می‌دانم که شما نمی‌دانید.'),
(1017, 1017, 'أَوَعَجِبْتُمْ أَنْ جَاءَكُمْ ذِكْرٌ مِنْ رَبِّكُمْ عَلَىٰ رَجُلٍ مِنْكُمْ لِيُنْذِرَكُمْ وَلِتَتَّقُوا وَلَعَلَّكُمْ تُرْحَمُونَ', 7, 63, 8, 32, 158, 'آیا تعجّب کرده‌اید که دستور آگاه کننده پروردگارتان به وسیله مردی از میان شما به شما برسد، تا (از عواقب اعمال خلاف) بیمتان دهد، و (در پرتو این دستور،) پرهیزگاری پیشه کنید و شاید مشمول رحمت (الهی) گردید؟!'),
(1018, 1018, 'فَكَذَّبُوهُ فَأَنْجَيْنَاهُ وَالَّذِينَ مَعَهُ فِي الْفُلْكِ وَأَغْرَقْنَا الَّذِينَ كَذَّبُوا بِآيَاتِنَا ۚ إِنَّهُمْ كَانُوا قَوْمًا عَمِينَ', 7, 64, 8, 32, 158, 'امّا سرانجام او را تکذیب کردند؛ و ما او و کسانی را که با وی در کشتی بودند، رهایی بخشیدیم؛ و کسانی که آیات ما را تکذیب کردند، غرق کردیم؛ چه اینکه آنها گروهی نابینا (و کوردل) بودند.'),
(1019, 1019, ' وَإِلَىٰ عَادٍ أَخَاهُمْ هُودًا ۗ قَالَ يَا قَوْمِ اعْبُدُوا اللَّهَ مَا لَكُمْ مِنْ إِلَٰهٍ غَيْرُهُ ۚ أَفَلَا تَتَّقُونَ', 7, 65, 8, 32, 158, 'و به سوی قوم عاد، برادرشان «هود» را (فرستادیم)؛ گفت: «ای قوم من! (تنها) خدا را پرستش کنید، که جز او معبودی برای شما نیست! آیا پرهیزگاری پیشه نمی‌کنید؟!»'),
(1020, 1020, 'قَالَ الْمَلَأُ الَّذِينَ كَفَرُوا مِنْ قَوْمِهِ إِنَّا لَنَرَاكَ فِي سَفَاهَةٍ وَإِنَّا لَنَظُنُّكَ مِنَ الْكَاذِبِينَ', 7, 66, 8, 32, 158, 'اشراف کافر قوم او گفتند: «ما تو را در سفاهت (و نادانی و سبک مغزی) می‌بینیم، و ما مسلّماً تو را از دروغگویان می‌دانیم!»'),
(1021, 1021, 'قَالَ يَا قَوْمِ لَيْسَ بِي سَفَاهَةٌ وَلَٰكِنِّي رَسُولٌ مِنْ رَبِّ الْعَالَمِينَ', 7, 67, 8, 32, 158, 'گرفت: «ای قوم من! هیچ گونه سفاهتی در من نیست؛ ولی فرستاده‌ای از طرف پروردگار جهانیانم.'),
(1022, 1022, 'أُبَلِّغُكُمْ رِسَالَاتِ رَبِّي وَأَنَا لَكُمْ نَاصِحٌ أَمِينٌ', 7, 68, 8, 32, 159, 'رسالتهای پروردگارم را به شما ابلاغ می‌کنم؛ و من خیرخواه امینی برای شما هستم.'),
(1023, 1023, 'أَوَعَجِبْتُمْ أَنْ جَاءَكُمْ ذِكْرٌ مِنْ رَبِّكُمْ عَلَىٰ رَجُلٍ مِنْكُمْ لِيُنْذِرَكُمْ ۚ وَاذْكُرُوا إِذْ جَعَلَكُمْ خُلَفَاءَ مِنْ بَعْدِ قَوْمِ نُوحٍ وَزَادَكُمْ فِي الْخَلْقِ بَسْطَةً ۖ فَاذْكُرُوا آلَاءَ اللَّهِ لَعَلَّكُمْ تُفْلِحُونَ', 7, 69, 8, 32, 159, 'آیا تعجّب کرده‌اید که دستور آگاه کننده پروردگارتان به وسیله مردی از میان شما به شما برسد تا (از مجازات الهی) بیمتان دهد؟! و به یاد آورید هنگامی که شما را جانشینان قوم نوح قرار داد؛ و شما را از جهت خلقت (جسمانی) گسترش (و قدرت) داد؛ پس نعمتهای خدا را به یاد آورید، شاید رستگار شوید!»'),
(1024, 1024, 'قَالُوا أَجِئْتَنَا لِنَعْبُدَ اللَّهَ وَحْدَهُ وَنَذَرَ مَا كَانَ يَعْبُدُ آبَاؤُنَا ۖ فَأْتِنَا بِمَا تَعِدُنَا إِنْ كُنْتَ مِنَ الصَّادِقِينَ', 7, 70, 8, 32, 159, 'گفتند: «آیا به سراغ ما آمده‌ای که تنها خدای یگانه را بپرستیم، و آنچه را پدران ما می‌پرستند، رها کنیم؟! پس اگر راست می‌گوئی آنچه را (از بلا و عذاب الهی) به ما وعده می‌دهی، بیاور»!');
INSERT INTO `Quran` (`rowid`, `id`, `Arabic`, `Chapter`, `Verse`, `Juz`, `Hizb`, `Page`, `tr`) VALUES
(1025, 1025, 'قَالَ قَدْ وَقَعَ عَلَيْكُمْ مِنْ رَبِّكُمْ رِجْسٌ وَغَضَبٌ ۖ أَتُجَادِلُونَنِي فِي أَسْمَاءٍ سَمَّيْتُمُوهَا أَنْتُمْ وَآبَاؤُكُمْ مَا نَزَّلَ اللَّهُ بِهَا مِنْ سُلْطَانٍ ۚ فَانْتَظِرُوا إِنِّي مَعَكُمْ مِنَ الْمُنْتَظِرِينَ', 7, 71, 8, 32, 159, 'گفت: «پلیدی و غضب پروردگارتان، شما را فرا گرفته است! آیا با من در مورد نامهایی مجادله می‌کنید که شما و پدرانتان 0بعنوان معبود و خدا، بر بتها) گذارده‌اید، در حالی که خداوند هیچ دلیلی درباره آن نازل نکرده است؟! پس شما منتظر باشید، من هم با شما انتظار می‌کشم! (شما انتظار شکست من، و من انتظار عذاب الهی برای شما!)»'),
(1026, 1026, 'فَأَنْجَيْنَاهُ وَالَّذِينَ مَعَهُ بِرَحْمَةٍ مِنَّا وَقَطَعْنَا دَابِرَ الَّذِينَ كَذَّبُوا بِآيَاتِنَا ۖ وَمَا كَانُوا مُؤْمِنِينَ', 7, 72, 8, 32, 159, 'سرانجام، او و کسانی را که با او بودند، برحمت خود نجات بخشیدیم؛ و ریشه کسانی که آیات ما را تکذیب کردند و ایمان نیاوردند، قطع کردیم!'),
(1027, 1027, 'وَإِلَىٰ ثَمُودَ أَخَاهُمْ صَالِحًا ۗ قَالَ يَا قَوْمِ اعْبُدُوا اللَّهَ مَا لَكُمْ مِنْ إِلَٰهٍ غَيْرُهُ ۖ قَدْ جَاءَتْكُمْ بَيِّنَةٌ مِنْ رَبِّكُمْ ۖ هَٰذِهِ نَاقَةُ اللَّهِ لَكُمْ آيَةً ۖ فَذَرُوهَا تَأْكُلْ فِي أَرْضِ اللَّهِ ۖ وَلَا تَمَسُّوهَا بِسُوءٍ فَيَأْخُذَكُمْ عَذَابٌ أَلِيمٌ', 7, 73, 8, 32, 159, 'و به سوی (قوم) ثمود، برادرشان صالح را (فرستادیم)؛ گفت: «ای قوم من! (تنها) خدا را بپرستید، که جز او، معبودی برای شما نیست! دلیل روشنی از طرف پروردگارتان برای شما آمده: این «ناقه» الهی برای شما معجزه‌ای است؛ او را به حال خود واگذارید که در زمین خدا (از علفهای بیابان) بخورد! و آن را آزار نرسانید، که عذاب دردناکی شما را خواهد گرفت!'),
(1028, 1028, 'وَاذْكُرُوا إِذْ جَعَلَكُمْ خُلَفَاءَ مِنْ بَعْدِ عَادٍ وَبَوَّأَكُمْ فِي الْأَرْضِ تَتَّخِذُونَ مِنْ سُهُولِهَا قُصُورًا وَتَنْحِتُونَ الْجِبَالَ بُيُوتًا ۖ فَاذْكُرُوا آلَاءَ اللَّهِ وَلَا تَعْثَوْا فِي الْأَرْضِ مُفْسِدِينَ', 7, 74, 8, 32, 160, 'و به خاطر بیاورید که شما را جانشینان قوم «عاد» قرار داد، و در زمین مستقر ساخت، که در دشتهایش، قصرها برای خود بنا می‌کنید؛ و در کوه‌ها، برای خود خانه‌ها می تراشید! بنابر این، نعمتهای خدا را متذکر شوید! و در زمین، به فساد نکوشید!»'),
(1029, 1029, 'قَالَ الْمَلَأُ الَّذِينَ اسْتَكْبَرُوا مِنْ قَوْمِهِ لِلَّذِينَ اسْتُضْعِفُوا لِمَنْ آمَنَ مِنْهُمْ أَتَعْلَمُونَ أَنَّ صَالِحًا مُرْسَلٌ مِنْ رَبِّهِ ۚ قَالُوا إِنَّا بِمَا أُرْسِلَ بِهِ مُؤْمِنُونَ', 7, 75, 8, 32, 160, '(ولی) اشراف متکبر قوم او، به مستضعفانی که ایمان آورده بودند، گفتند: «آیا (براستی) شما یقین دارید که صالح از طرف پروردگارش فرستاده شده است؟!» آنها گفتند: «ما به آنچه او بدان مأموریت یافته، ایمان آورده‌ایم.»'),
(1030, 1030, 'قَالَ الَّذِينَ اسْتَكْبَرُوا إِنَّا بِالَّذِي آمَنْتُمْ بِهِ كَافِرُونَ', 7, 76, 8, 32, 160, 'متکبران گفتند: «(ولی) ما به آنچه شما به آن ایمان آورده‌اید، کافریم!»'),
(1031, 1031, 'فَعَقَرُوا النَّاقَةَ وَعَتَوْا عَنْ أَمْرِ رَبِّهِمْ وَقَالُوا يَا صَالِحُ ائْتِنَا بِمَا تَعِدُنَا إِنْ كُنْتَ مِنَ الْمُرْسَلِينَ', 7, 77, 8, 32, 160, 'سپس «ناقه» را پی کردند، و از فرمان پروردگارشان سرپیچیدند؛ و گفتند: «ای صالح! اگر تو از فرستادگان (خدا) هستی، آنچه ما را با آن تهدید می‌کنی، بیاور!»'),
(1032, 1032, 'فَأَخَذَتْهُمُ الرَّجْفَةُ فَأَصْبَحُوا فِي دَارِهِمْ جَاثِمِينَ', 7, 78, 8, 32, 160, 'سرانجام زمین لرزه آنها را فرا گرفت؛ و صبحگاهان، (تنها) جسم بی‌جانشان در خانه‌هاشان باقی مانده بود.'),
(1033, 1033, 'فَتَوَلَّىٰ عَنْهُمْ وَقَالَ يَا قَوْمِ لَقَدْ أَبْلَغْتُكُمْ رِسَالَةَ رَبِّي وَنَصَحْتُ لَكُمْ وَلَٰكِنْ لَا تُحِبُّونَ النَّاصِحِينَ', 7, 79, 8, 32, 160, '(صالح) از آنها روی برتافت؛ و گفت: «ای قوم! من رسالت پروردگارم را به شما ابلاغ کردم، و شرط خیرخواهی را انجام دادم، ولی (چه کنم که) شما خیرخواهان را دوست ندارید!»'),
(1034, 1034, 'وَلُوطًا إِذْ قَالَ لِقَوْمِهِ أَتَأْتُونَ الْفَاحِشَةَ مَا سَبَقَكُمْ بِهَا مِنْ أَحَدٍ مِنَ الْعَالَمِينَ', 7, 80, 8, 32, 160, 'و (به خاطر آورید) لوط را، هنگامی که به قوم خود گفت: «آیا عمل بسیار زشتی را انجام می‌دهید که هیچیک از جهانیان، پیش از شما انجام نداده است؟!'),
(1035, 1035, 'إِنَّكُمْ لَتَأْتُونَ الرِّجَالَ شَهْوَةً مِنْ دُونِ النِّسَاءِ ۚ بَلْ أَنْتُمْ قَوْمٌ مُسْرِفُونَ', 7, 81, 8, 32, 160, 'آیا شما از روی شهوت به سراغ مردان می‌روید، نه زنان؟! شما گروه اسرافکار (و منحرفی) هستید!'),
(1036, 1036, 'وَمَا كَانَ جَوَابَ قَوْمِهِ إِلَّا أَنْ قَالُوا أَخْرِجُوهُمْ مِنْ قَرْيَتِكُمْ ۖ إِنَّهُمْ أُنَاسٌ يَتَطَهَّرُونَ', 7, 82, 8, 32, 161, 'ولی پاسخ قومش چیزی جز این نبود که گفتند: «اینها را از شهر و دیار خود بیرون کنید، که اینها مردمی هستند که پاکدامنی را می‌طلبند (و با ما همصدا نیستند!)»'),
(1037, 1037, 'فَأَنْجَيْنَاهُ وَأَهْلَهُ إِلَّا امْرَأَتَهُ كَانَتْ مِنَ الْغَابِرِينَ', 7, 83, 8, 32, 161, '(چون کار به اینجا رسید،) ما او و خاندانش را رهایی بخشیدیم؛ جز همسرش، که از بازماندگان (در شهر) بود.'),
(1038, 1038, 'وَأَمْطَرْنَا عَلَيْهِمْ مَطَرًا ۖ فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ الْمُجْرِمِينَ', 7, 84, 8, 32, 161, 'و (سپس چنان) بارانی (از سنگ) بر آنها فرستادیم؛ (که آنها را در هم کوبید و نابود ساخت.) پس بنگر سرانجام کار مجرمان چه شد!'),
(1039, 1039, 'وَإِلَىٰ مَدْيَنَ أَخَاهُمْ شُعَيْبًا ۗ قَالَ يَا قَوْمِ اعْبُدُوا اللَّهَ مَا لَكُمْ مِنْ إِلَٰهٍ غَيْرُهُ ۖ قَدْ جَاءَتْكُمْ بَيِّنَةٌ مِنْ رَبِّكُمْ ۖ فَأَوْفُوا الْكَيْلَ وَالْمِيزَانَ وَلَا تَبْخَسُوا النَّاسَ أَشْيَاءَهُمْ وَلَا تُفْسِدُوا فِي الْأَرْضِ بَعْدَ إِصْلَاحِهَا ۚ ذَٰلِكُمْ خَيْرٌ لَكُمْ إِنْ كُنْتُمْ مُؤْمِنِينَ', 7, 85, 8, 32, 161, 'و به سوی مدین، برادرشان شعیب را (فرستادیم)؛ گفت: «ای قوم من! خدا را بپرستید، که جز او معبودی ندارید! دلیل روشنی از طرف پروردگارتان برای شما آمده است؛ بنابر این، حق پیمانه و وزن را ادا کنید! و از اموال مردم چیزی نکاهید! و در روی زمین، بعد از آنکه (در پرتو ایمان و دعوت انبیاء) اصلاح شده است، فساد نکنید! این برای شما بهتر است اگر با ایمان هستید!'),
(1040, 1040, 'وَلَا تَقْعُدُوا بِكُلِّ صِرَاطٍ تُوعِدُونَ وَتَصُدُّونَ عَنْ سَبِيلِ اللَّهِ مَنْ آمَنَ بِهِ وَتَبْغُونَهَا عِوَجًا ۚ وَاذْكُرُوا إِذْ كُنْتُمْ قَلِيلًا فَكَثَّرَكُمْ ۖ وَانْظُرُوا كَيْفَ كَانَ عَاقِبَةُ الْمُفْسِدِينَ', 7, 86, 8, 32, 161, 'و بر سر هر راه ننشینید که (مردم با ایمان را) تهدید کنید و مؤمنان را از راه خدا باز دارید، و با (القای شبهات،) آن را کج و معوج نشان دهید! و به خاطر بیاورید زمانی را که اندک بودید، و او شما را فزونی داد! و بنگرید سرانجام مفسدان چگونه بود!'),
(1041, 1041, 'وَإِنْ كَانَ طَائِفَةٌ مِنْكُمْ آمَنُوا بِالَّذِي أُرْسِلْتُ بِهِ وَطَائِفَةٌ لَمْ يُؤْمِنُوا فَاصْبِرُوا حَتَّىٰ يَحْكُمَ اللَّهُ بَيْنَنَا ۚ وَهُوَ خَيْرُ الْحَاكِمِينَ', 7, 87, 8, 32, 161, 'و اگر گروهی از شما به آنچه من به آن فرستاده شده‌ام ایمان آورده‌اند، و گروهی ایمان نیاورده‌اند، صبر کنید تا خداوند میان ما داوری کند، که او بهترین داوران است!'),
(1042, 1042, ' قَالَ الْمَلَأُ الَّذِينَ اسْتَكْبَرُوا مِنْ قَوْمِهِ لَنُخْرِجَنَّكَ يَا شُعَيْبُ وَالَّذِينَ آمَنُوا مَعَكَ مِنْ قَرْيَتِنَا أَوْ لَتَعُودُنَّ فِي مِلَّتِنَا ۚ قَالَ أَوَلَوْ كُنَّا كَارِهِينَ', 7, 88, 9, 33, 162, 'اشراف زورمند و متکبّر از قوم او گفتند: «ای شعیب! به یقین، تو و کسانی را که به تو ایمان آورده‌اند، از شهر و دیار خود بیرون خواهیم کرد، یا به آیین ما بازگردید!» کفت: «آیا (می‌خواهید ما را بازگردانید) اگر چه مایل نباشیم؟!'),
(1043, 1043, 'قَدِ افْتَرَيْنَا عَلَى اللَّهِ كَذِبًا إِنْ عُدْنَا فِي مِلَّتِكُمْ بَعْدَ إِذْ نَجَّانَا اللَّهُ مِنْهَا ۚ وَمَا يَكُونُ لَنَا أَنْ نَعُودَ فِيهَا إِلَّا أَنْ يَشَاءَ اللَّهُ رَبُّنَا ۚ وَسِعَ رَبُّنَا كُلَّ شَيْءٍ عِلْمًا ۚ عَلَى اللَّهِ تَوَكَّلْنَا ۚ رَبَّنَا افْتَحْ بَيْنَنَا وَبَيْنَ قَوْمِنَا بِالْحَقِّ وَأَنْتَ خَيْرُ الْفَاتِحِينَ', 7, 89, 9, 33, 162, 'اگر ما به آیین شما بازگردیم، بعد از آنکه خدا ما را از آن نجات بخشیده، به خدا دروغ بسته‌ایم؛ و شایسته نیست که ما به آن بازگردیم مگر اینکه خدایی که پروردگار ماست بخواهد؛ علم پروردگار ما، به همه چیز احاطه دارد. تنها بر خدا توکل کرده‌ایم. پروردگارا! میان ما و قوم ما بحق داوری کن، که تو بهترین داورانی!»'),
(1044, 1044, 'وَقَالَ الْمَلَأُ الَّذِينَ كَفَرُوا مِنْ قَوْمِهِ لَئِنِ اتَّبَعْتُمْ شُعَيْبًا إِنَّكُمْ إِذًا لَخَاسِرُونَ', 7, 90, 9, 33, 162, 'اشراف زورمند از قوم او که کافر شده بودند گفتند: «اگر از شعیب پیروی کنید، شما هم زیانکار خواهید شد!»'),
(1045, 1045, 'فَأَخَذَتْهُمُ الرَّجْفَةُ فَأَصْبَحُوا فِي دَارِهِمْ جَاثِمِينَ', 7, 91, 9, 33, 162, 'سپس زمین لرزه آنها را فرا گرفت؛ و صبحگاهان بصورت اجسادی بی‌جان در خانه‌هاشان مانده بودند.'),
(1046, 1046, 'الَّذِينَ كَذَّبُوا شُعَيْبًا كَأَنْ لَمْ يَغْنَوْا فِيهَا ۚ الَّذِينَ كَذَّبُوا شُعَيْبًا كَانُوا هُمُ الْخَاسِرِينَ', 7, 92, 9, 33, 162, 'آنها که شعیب را تکذیب کردند، (آنچنان نابود شدند که) گویا هرگز در آن (خانه‌ها) سکونت نداشتند! آنها که شعیب را تکذیب کردند، زیانکار بودند!'),
(1047, 1047, 'فَتَوَلَّىٰ عَنْهُمْ وَقَالَ يَا قَوْمِ لَقَدْ أَبْلَغْتُكُمْ رِسَالَاتِ رَبِّي وَنَصَحْتُ لَكُمْ ۖ فَكَيْفَ آسَىٰ عَلَىٰ قَوْمٍ كَافِرِينَ', 7, 93, 9, 33, 162, 'سپس از آنان روی برتافت و گفت: «ای قوم من! من رسالتهای پروردگارم را به شما ابلاغ کردم و برای شما خیرخواهی نمودم؛ با این حال، چگونه بر حال قوم بی‌ایمان تأسف بخورم؟!»'),
(1048, 1048, 'وَمَا أَرْسَلْنَا فِي قَرْيَةٍ مِنْ نَبِيٍّ إِلَّا أَخَذْنَا أَهْلَهَا بِالْبَأْسَاءِ وَالضَّرَّاءِ لَعَلَّهُمْ يَضَّرَّعُونَ', 7, 94, 9, 33, 162, 'و ما در هیچ شهر و آبادی پیامبری نفرستادیم مگر اینکه اهل آن را به ناراحتیها و خسارتها گرفتار ساختیم؛ شاید (به خود آیند، و به سوی خدا) بازگردند و تضرع کنند!'),
(1049, 1049, 'ثُمَّ بَدَّلْنَا مَكَانَ السَّيِّئَةِ الْحَسَنَةَ حَتَّىٰ عَفَوْا وَقَالُوا قَدْ مَسَّ آبَاءَنَا الضَّرَّاءُ وَالسَّرَّاءُ فَأَخَذْنَاهُمْ بَغْتَةً وَهُمْ لَا يَشْعُرُونَ', 7, 95, 9, 33, 162, 'سپس (هنگامی که این هشدارها در آنان اثر نگذاشت)، نیکی (و فراوانی نعمت و رفاه) را به جای بدی (و ناراحتی و گرفتاری) قرار دادیم؛ آنچنان که فزونی گرفتند، (و همه‌گونه نعمت و برکت یافتند، و مغرور شدند،) و گفتند: «(تنها ما نبودیم که گرفتار این مشکلات شدیم؛) به پدران ما نیز ناراحتیهای جسمی و مالی رسید.» چون چنین شد، آنها را ناگهان (به سبب اعمالشان) گرفتیم (و مجازات کردیم)، در حالی که نمی‌فهمیدند.'),
(1050, 1050, 'وَلَوْ أَنَّ أَهْلَ الْقُرَىٰ آمَنُوا وَاتَّقَوْا لَفَتَحْنَا عَلَيْهِمْ بَرَكَاتٍ مِنَ السَّمَاءِ وَالْأَرْضِ وَلَٰكِنْ كَذَّبُوا فَأَخَذْنَاهُمْ بِمَا كَانُوا يَكْسِبُونَ', 7, 96, 9, 33, 163, 'و اگر اهل شهرها و آبادیها، ایمان می‌آوردند و تقوا پیشه می‌کردند، برکات آسمان و زمین را بر آنها می‌گشودیم؛ ولی (آنها حق را) تکذیب کردند؛ ما هم آنان را به کیفر اعمالشان مجازات کردیم.'),
(1051, 1051, 'أَفَأَمِنَ أَهْلُ الْقُرَىٰ أَنْ يَأْتِيَهُمْ بَأْسُنَا بَيَاتًا وَهُمْ نَائِمُونَ', 7, 97, 9, 33, 163, 'آیا اهل این آبادیها، از این ایمنند که عذاب ما شبانه به سراغ آنها بیاید در حالی که در خواب باشند؟!'),
(1052, 1052, 'أَوَأَمِنَ أَهْلُ الْقُرَىٰ أَنْ يَأْتِيَهُمْ بَأْسُنَا ضُحًى وَهُمْ يَلْعَبُونَ', 7, 98, 9, 33, 163, 'آیا اهل این آبادیها، از این ایمنند که عذاب ما هنگام روز به سراغشان بیاید در حالی که سرگرم بازی هستند؟!'),
(1053, 1053, 'أَفَأَمِنُوا مَكْرَ اللَّهِ ۚ فَلَا يَأْمَنُ مَكْرَ اللَّهِ إِلَّا الْقَوْمُ الْخَاسِرُونَ', 7, 99, 9, 33, 163, 'آیا آنها خود را از مکر الهی در امان می‌دانند؟! در حالی که جز زیانکاران، خود را از مکر (و مجازات) خدا ایمن نمی‌دانند!'),
(1054, 1054, 'أَوَلَمْ يَهْدِ لِلَّذِينَ يَرِثُونَ الْأَرْضَ مِنْ بَعْدِ أَهْلِهَا أَنْ لَوْ نَشَاءُ أَصَبْنَاهُمْ بِذُنُوبِهِمْ ۚ وَنَطْبَعُ عَلَىٰ قُلُوبِهِمْ فَهُمْ لَا يَسْمَعُونَ', 7, 100, 9, 33, 163, 'آیا کسانی که وارث روی زمین بعد از صاحبان آن می‌شوند، عبرت نمی‌گیرند که اگر بخواهیم، آنها را نیز به گناهانشان هلاک می‌کنیم، و بر دلهایشان مهر می‌نهیم تا (صدای حق را) نشنوند؟!'),
(1055, 1055, 'تِلْكَ الْقُرَىٰ نَقُصُّ عَلَيْكَ مِنْ أَنْبَائِهَا ۚ وَلَقَدْ جَاءَتْهُمْ رُسُلُهُمْ بِالْبَيِّنَاتِ فَمَا كَانُوا لِيُؤْمِنُوا بِمَا كَذَّبُوا مِنْ قَبْلُ ۚ كَذَٰلِكَ يَطْبَعُ اللَّهُ عَلَىٰ قُلُوبِ الْكَافِرِينَ', 7, 101, 9, 33, 163, 'اینها، شهرها و آبادیهایی است که قسمتی از اخبار آن را برای تو شرح می‌دهیم؛ پیامبرانشان دلایل روشن برای آنان آوردند؛ (ولی آنها چنان لجوج بودند که) به آنچه قبلاً تکذیب کرده بودند، ایمان نمی‌آوردند! این‌گونه خداوند بر دلهای کافران مهر می‌نهد (و بر اثر لجاجت و ادامه گناه، حس تشخیصشان را سلب می‌کند)!'),
(1056, 1056, 'وَمَا وَجَدْنَا لِأَكْثَرِهِمْ مِنْ عَهْدٍ ۖ وَإِنْ وَجَدْنَا أَكْثَرَهُمْ لَفَاسِقِينَ', 7, 102, 9, 33, 163, 'و بیشتر آنها را بر سر پیمان خود نیافتیم؛ (بلکه) اکثر آنها را فاسق و گنهکار یافتیم!'),
(1057, 1057, 'ثُمَّ بَعَثْنَا مِنْ بَعْدِهِمْ مُوسَىٰ بِآيَاتِنَا إِلَىٰ فِرْعَوْنَ وَمَلَئِهِ فَظَلَمُوا بِهَا ۖ فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ الْمُفْسِدِينَ', 7, 103, 9, 33, 163, 'سپس بدنبال آنها = پیامبران پیشین‌ موسی را با آیات خویش به سوی فرعون و اطرافیان او فرستادیم؛ اما آنها (با عدم پذیرش)، به آن (آیات) ظلم کردند. ببین عاقبت مفسدان چگونه بود!'),
(1058, 1058, 'وَقَالَ مُوسَىٰ يَا فِرْعَوْنُ إِنِّي رَسُولٌ مِنْ رَبِّ الْعَالَمِينَ', 7, 104, 9, 33, 163, 'و موسی گفت: «ای فرعون! من فرستاده‌ای از سوی پروردگار جهانیانم.'),
(1059, 1059, 'حَقِيقٌ عَلَىٰ أَنْ لَا أَقُولَ عَلَى اللَّهِ إِلَّا الْحَقَّ ۚ قَدْ جِئْتُكُمْ بِبَيِّنَةٍ مِنْ رَبِّكُمْ فَأَرْسِلْ مَعِيَ بَنِي إِسْرَائِيلَ', 7, 105, 9, 33, 164, 'سزاوار است که بر خدا جز حق نگویم. من دلیل روشنی از پروردگارتان برای شما آورده‌ام؛ پس بنی اسرائیل را با من بفرست!»'),
(1060, 1060, 'قَالَ إِنْ كُنْتَ جِئْتَ بِآيَةٍ فَأْتِ بِهَا إِنْ كُنْتَ مِنَ الصَّادِقِينَ', 7, 106, 9, 33, 164, '(فرعون) گفت: «اگر نشانه‌ای آورده‌ای، نشان بده اگر از راستگویانی!»'),
(1061, 1061, 'فَأَلْقَىٰ عَصَاهُ فَإِذَا هِيَ ثُعْبَانٌ مُبِينٌ', 7, 107, 9, 33, 164, '(موسی) عصای خود را افکند؛ ناگهان اژدهای آشکاری شد!'),
(1062, 1062, 'وَنَزَعَ يَدَهُ فَإِذَا هِيَ بَيْضَاءُ لِلنَّاظِرِينَ', 7, 108, 9, 33, 164, 'و دست خود را (از گریبان) بیرون آورد؛ سفید (و درخشان) برای بینندگان بود!'),
(1063, 1063, 'قَالَ الْمَلَأُ مِنْ قَوْمِ فِرْعَوْنَ إِنَّ هَٰذَا لَسَاحِرٌ عَلِيمٌ', 7, 109, 9, 33, 164, 'اطرافیان فرعون گفتند: «بی‌شک، این ساحری ماهر و دانا است!'),
(1064, 1064, 'يُرِيدُ أَنْ يُخْرِجَكُمْ مِنْ أَرْضِكُمْ ۖ فَمَاذَا تَأْمُرُونَ', 7, 110, 9, 33, 164, 'می‌خواهد شما را از سرزمینتان بیرون کند؛ (نظر شما چیست، و) در برابر او چه دستوری دارید؟»'),
(1065, 1065, 'قَالُوا أَرْجِهْ وَأَخَاهُ وَأَرْسِلْ فِي الْمَدَائِنِ حَاشِرِينَ', 7, 111, 9, 33, 164, '(سپس به فرعون) گفتند: «(کار) او و برادرش را به تأخیر انداز، و جمع‌آوری‌کنندگان را به همه شهرها بفرست...'),
(1066, 1066, 'يَأْتُوكَ بِكُلِّ سَاحِرٍ عَلِيمٍ', 7, 112, 9, 33, 164, 'تا هر ساحر دانا (و کارآزموده‌ای) را به خدمت تو بیاورند!»'),
(1067, 1067, 'وَجَاءَ السَّحَرَةُ فِرْعَوْنَ قَالُوا إِنَّ لَنَا لَأَجْرًا إِنْ كُنَّا نَحْنُ الْغَالِبِينَ', 7, 113, 9, 33, 164, 'ساحران نزد فرعون آمدند و گفتند: «آیا اگر ما پیروز گردیم، اجر و پاداش مهمی خواهیم داشت؟!»'),
(1068, 1068, 'قَالَ نَعَمْ وَإِنَّكُمْ لَمِنَ الْمُقَرَّبِينَ', 7, 114, 9, 33, 164, 'گفت: «آری، و شما از مقربان خواهید بود!»'),
(1069, 1069, 'قَالُوا يَا مُوسَىٰ إِمَّا أَنْ تُلْقِيَ وَإِمَّا أَنْ نَكُونَ نَحْنُ الْمُلْقِينَ', 7, 115, 9, 33, 164, '(روز مبارزه فرا رسید. ساحران) گفتند: «ای موسی! یا تو (وسایل سحرت را) بیفکن، یا ما می‌افکنیم!»'),
(1070, 1070, 'قَالَ أَلْقُوا ۖ فَلَمَّا أَلْقَوْا سَحَرُوا أَعْيُنَ النَّاسِ وَاسْتَرْهَبُوهُمْ وَجَاءُوا بِسِحْرٍ عَظِيمٍ', 7, 116, 9, 33, 164, 'گفت: «شما بیفکنید!» و هنگامی (که وسایل سحر خود را) افکندند، مردم را چشم‌بندی کردند و ترساندند؛ و سحر عظیمی پدید آوردند.'),
(1071, 1071, ' وَأَوْحَيْنَا إِلَىٰ مُوسَىٰ أَنْ أَلْقِ عَصَاكَ ۖ فَإِذَا هِيَ تَلْقَفُ مَا يَأْفِكُونَ', 7, 117, 9, 33, 164, '(ما) به موسی وحی کردیم که: «عصای خود را بیفکن!» ناگهان (بصورت مار عظیمی در آمد که) وسایل دروغین آنها را بسرعت برمی‌گرفت.'),
(1072, 1072, 'فَوَقَعَ الْحَقُّ وَبَطَلَ مَا كَانُوا يَعْمَلُونَ', 7, 118, 9, 33, 164, '(در این هنگام،) حق آشکار شد؛ و آنچه آنها ساخته بودند، باطل گشت.'),
(1073, 1073, 'فَغُلِبُوا هُنَالِكَ وَانْقَلَبُوا صَاغِرِينَ', 7, 119, 9, 33, 164, 'و در آنجا (همگی) مغلوب شدند؛ و خوار و کوچک گشتند.'),
(1074, 1074, 'وَأُلْقِيَ السَّحَرَةُ سَاجِدِينَ', 7, 120, 9, 33, 164, 'و ساحران (بی‌اختیار) به سجده افتادند.'),
(1075, 1075, 'قَالُوا آمَنَّا بِرَبِّ الْعَالَمِينَ', 7, 121, 9, 33, 165, 'و گفتند: «ما به پروردگار جهانیان ایمان آوردیم؛'),
(1076, 1076, 'رَبِّ مُوسَىٰ وَهَارُونَ', 7, 122, 9, 33, 165, 'پروردگار موسی و هارون!»'),
(1077, 1077, 'قَالَ فِرْعَوْنُ آمَنْتُمْ بِهِ قَبْلَ أَنْ آذَنَ لَكُمْ ۖ إِنَّ هَٰذَا لَمَكْرٌ مَكَرْتُمُوهُ فِي الْمَدِينَةِ لِتُخْرِجُوا مِنْهَا أَهْلَهَا ۖ فَسَوْفَ تَعْلَمُونَ', 7, 123, 9, 33, 165, 'فرعون گفت: «آیا پیش از آنکه به شما اجازه دهم، به او ایمان آوردید؟! حتماً این نیرنگ و توطئه‌ای است که در این شهر (و دیار) چیده‌اید، تا اهلش را از آن بیرون کنید؛ ولی بزودی خواهید دانست!'),
(1078, 1078, 'لَأُقَطِّعَنَّ أَيْدِيَكُمْ وَأَرْجُلَكُمْ مِنْ خِلَافٍ ثُمَّ لَأُصَلِّبَنَّكُمْ أَجْمَعِينَ', 7, 124, 9, 33, 165, 'سوگند می‌خورم که دستها و پاهای شما را بطور مخالف = دست راست با پای چپ، یا دست چپ با پای راست‌ قطع می‌کنم؛ سپس همگی را به دار می‌آویزم!'),
(1079, 1079, 'قَالُوا إِنَّا إِلَىٰ رَبِّنَا مُنْقَلِبُونَ', 7, 125, 9, 33, 165, '(ساحران) گفتند: «(مهم نیست،) ما به سوی پروردگارمان بازمی‌گردیم!'),
(1080, 1080, 'وَمَا تَنْقِمُ مِنَّا إِلَّا أَنْ آمَنَّا بِآيَاتِ رَبِّنَا لَمَّا جَاءَتْنَا ۚ رَبَّنَا أَفْرِغْ عَلَيْنَا صَبْرًا وَتَوَفَّنَا مُسْلِمِينَ', 7, 126, 9, 33, 165, 'انتقام تو از ما، تنها بخاطر این است که ما به آیات پروردگار خویش -هنگامی که به سراغ ما آمد- ایمان آوردیم. بار الها! صبر و استقامت بر ما فرو ریز! (و آخرین درجه شکیبائی را به ما مرحمت فرما!) و ما را مسلمان بمیران!»'),
(1081, 1081, 'وَقَالَ الْمَلَأُ مِنْ قَوْمِ فِرْعَوْنَ أَتَذَرُ مُوسَىٰ وَقَوْمَهُ لِيُفْسِدُوا فِي الْأَرْضِ وَيَذَرَكَ وَآلِهَتَكَ ۚ قَالَ سَنُقَتِّلُ أَبْنَاءَهُمْ وَنَسْتَحْيِي نِسَاءَهُمْ وَإِنَّا فَوْقَهُمْ قَاهِرُونَ', 7, 127, 9, 33, 165, 'و اشراف قوم فرعون (به او) گفتند: «آیا موسی و قومش را رها می‌کنی که در زمین فساد کنند، و تو و خدایانت را رها سازد؟!» گفت: «بزودی پسرانشان را می‌کشیم، و دخترانشان را زنده نگه می‌داریم (تا به ما خدمت کنند)؛ و ما بر آنها کاملاً مسلّطیم!»'),
(1082, 1082, 'قَالَ مُوسَىٰ لِقَوْمِهِ اسْتَعِينُوا بِاللَّهِ وَاصْبِرُوا ۖ إِنَّ الْأَرْضَ لِلَّهِ يُورِثُهَا مَنْ يَشَاءُ مِنْ عِبَادِهِ ۖ وَالْعَاقِبَةُ لِلْمُتَّقِينَ', 7, 128, 9, 33, 165, 'موسی به قوم خود گفت: «از خدا یاری جویید، و استقامت پیشه کنید، که زمین از آن خداست، و آن را به هر کس از بندگانش که بخواهد، واگذار می‌کند؛ و سرانجام (نیک) برای پرهیزکاران است!»'),
(1083, 1083, 'قَالُوا أُوذِينَا مِنْ قَبْلِ أَنْ تَأْتِيَنَا وَمِنْ بَعْدِ مَا جِئْتَنَا ۚ قَالَ عَسَىٰ رَبُّكُمْ أَنْ يُهْلِكَ عَدُوَّكُمْ وَيَسْتَخْلِفَكُمْ فِي الْأَرْضِ فَيَنْظُرَ كَيْفَ تَعْمَلُونَ', 7, 129, 9, 33, 165, 'گفتند: «پیش از آنکه به سوی ما بیایی آزار دیدیم، (هم اکنون) پس از آمدنت نیز آزار می‌بینیم! (کی این آزارها سر خواهد آمد؟)» گفت: «امید است پروردگارتان دشمن شما را هلاک کند، و شما را در زمین جانشین (آنها) سازد، و بنگرد چگونه عمل می‌کنید!»'),
(1084, 1084, 'وَلَقَدْ أَخَذْنَا آلَ فِرْعَوْنَ بِالسِّنِينَ وَنَقْصٍ مِنَ الثَّمَرَاتِ لَعَلَّهُمْ يَذَّكَّرُونَ', 7, 130, 9, 33, 165, 'و ما نزدیکان فرعون (و قوم او) را به خشکسالی و کمبود میوه‌ها گرفتار کردیم، شاید متذکر گردند!'),
(1085, 1085, 'فَإِذَا جَاءَتْهُمُ الْحَسَنَةُ قَالُوا لَنَا هَٰذِهِ ۖ وَإِنْ تُصِبْهُمْ سَيِّئَةٌ يَطَّيَّرُوا بِمُوسَىٰ وَمَنْ مَعَهُ ۗ أَلَا إِنَّمَا طَائِرُهُمْ عِنْدَ اللَّهِ وَلَٰكِنَّ أَكْثَرَهُمْ لَا يَعْلَمُونَ', 7, 131, 9, 33, 166, '(اما آنها نه تنها پند نگرفتند، بلکه) هنگامی که نیکی (و نعمت) به آنها می‌رسید، می‌گفتند: «بخاطر خود ماست.» ولی موقعی که بدی (و بلا) به آنها می‌رسید، می‌گفتند: «از شومی موسی و کسان اوست»! آگاه باشید سرچشمه همه اینها، نزد خداست؛ ولی بیشتر آنها نمی‌دانند!'),
(1086, 1086, 'وَقَالُوا مَهْمَا تَأْتِنَا بِهِ مِنْ آيَةٍ لِتَسْحَرَنَا بِهَا فَمَا نَحْنُ لَكَ بِمُؤْمِنِينَ', 7, 132, 9, 33, 166, 'و گفتند: «هر زمان نشانه و معجزه‌ای برای ما بیاوری که سحرمان کنی، ما به تو ایمان نمی‌آوریم!»'),
(1087, 1087, 'فَأَرْسَلْنَا عَلَيْهِمُ الطُّوفَانَ وَالْجَرَادَ وَالْقُمَّلَ وَالضَّفَادِعَ وَالدَّمَ آيَاتٍ مُفَصَّلَاتٍ فَاسْتَكْبَرُوا وَكَانُوا قَوْمًا مُجْرِمِينَ', 7, 133, 9, 33, 166, 'سپس (بلاها را پشت سر هم بر آنها نازل کردیم:) طوفان و ملخ و آفت گیاهی و قورباغه‌ها و خون را -که نشانه‌هایی از هم جدا بودند- بر آنها فرستادیم؛ (ولی باز بیدار نشدند، و) تکبر ورزیدند، و جمعیّت گنهکاری بودند!'),
(1088, 1088, 'وَلَمَّا وَقَعَ عَلَيْهِمُ الرِّجْزُ قَالُوا يَا مُوسَى ادْعُ لَنَا رَبَّكَ بِمَا عَهِدَ عِنْدَكَ ۖ لَئِنْ كَشَفْتَ عَنَّا الرِّجْزَ لَنُؤْمِنَنَّ لَكَ وَلَنُرْسِلَنَّ مَعَكَ بَنِي إِسْرَائِيلَ', 7, 134, 9, 33, 166, 'هنگامی که بلا بر آنها مسلط می‌شد، می‌گفتند: «ای موسی! از خدایت برای ما بخواه به عهدی که با تو کرده، رفتار کند! اگر این بلا را از ما مرتفع سازی، قطعاً به تو ایمان می‌آوریم، و بنی اسرائیل را با تو خواهیم فرستاد!»'),
(1089, 1089, 'فَلَمَّا كَشَفْنَا عَنْهُمُ الرِّجْزَ إِلَىٰ أَجَلٍ هُمْ بَالِغُوهُ إِذَا هُمْ يَنْكُثُونَ', 7, 135, 9, 33, 166, 'اما هنگامی که بلا را، پس از مدت معینی که به آن می‌رسیدند، از آنها برمی‌داشتیم، پیمان خویش را می‌شکستند!'),
(1090, 1090, 'فَانْتَقَمْنَا مِنْهُمْ فَأَغْرَقْنَاهُمْ فِي الْيَمِّ بِأَنَّهُمْ كَذَّبُوا بِآيَاتِنَا وَكَانُوا عَنْهَا غَافِلِينَ', 7, 136, 9, 33, 166, 'سرانجام از آنها انتقام گرفتیم، و آنان را در دریا غرق کردیم؛ زیرا آیات ما را تکذیب کردند، و از آن غافل بودند.'),
(1091, 1091, 'وَأَوْرَثْنَا الْقَوْمَ الَّذِينَ كَانُوا يُسْتَضْعَفُونَ مَشَارِقَ الْأَرْضِ وَمَغَارِبَهَا الَّتِي بَارَكْنَا فِيهَا ۖ وَتَمَّتْ كَلِمَتُ رَبِّكَ الْحُسْنَىٰ عَلَىٰ بَنِي إِسْرَائِيلَ بِمَا صَبَرُوا ۖ وَدَمَّرْنَا مَا كَانَ يَصْنَعُ فِرْعَوْنُ وَقَوْمُهُ وَمَا كَانُوا يَعْرِشُونَ', 7, 137, 9, 33, 166, 'و مشرقها و مغربهای پر برکت زمین را به آن قومِ به ضعف کشانده شده (زیر زنجیر ظلم و ستم)، واگذار کردیم؛ و وعده نیک پروردگارت بر بنی اسرائیل، بخاطر صبر و استقامتی که به خرج دادند، تحقّق یافت؛ و آنچه فرعون و فرعونیان (از کاخهای مجلّل) می‌ساختند، و آنچه از باغهای داربست‌دار فراهم ساخته بودند، در هم کوبیدیم!'),
(1092, 1092, 'وَجَاوَزْنَا بِبَنِي إِسْرَائِيلَ الْبَحْرَ فَأَتَوْا عَلَىٰ قَوْمٍ يَعْكُفُونَ عَلَىٰ أَصْنَامٍ لَهُمْ ۚ قَالُوا يَا مُوسَى اجْعَلْ لَنَا إِلَٰهًا كَمَا لَهُمْ آلِهَةٌ ۚ قَالَ إِنَّكُمْ قَوْمٌ تَجْهَلُونَ', 7, 138, 9, 33, 167, 'و بنی اسرائیل را (سالم) از دریا عبور دادیم؛ (ناگاه) در راه خود به گروهی رسیدند که اطراف بتهایشان، با تواضع و خضوع، گرد آمده بودند. (در این هنگام، بنی اسرائیل) به موسی گفتند: «تو هم برای ما معبودی قرار ده، همان‌گونه که آنها معبودان (و خدایانی) دارند!» گفت: «شما جمعیّتی جاهل و نادان هستید!'),
(1093, 1093, 'إِنَّ هَٰؤُلَاءِ مُتَبَّرٌ مَا هُمْ فِيهِ وَبَاطِلٌ مَا كَانُوا يَعْمَلُونَ', 7, 139, 9, 33, 167, 'اینها (را که می‌بینید)، سرانجام کارشان نابودی است؛ و آنچه انجام می‌دهند، باطل (و بیهوده) است.'),
(1094, 1094, 'قَالَ أَغَيْرَ اللَّهِ أَبْغِيكُمْ إِلَٰهًا وَهُوَ فَضَّلَكُمْ عَلَى الْعَالَمِينَ', 7, 140, 9, 33, 167, '(سپس) گفت: «آیا غیر از خداوند، مبعودی برای شما بطلبم؟! خدایی که شما را بر جهانیان (و مردم عصرتان) برتری داد!»'),
(1095, 1095, 'وَإِذْ أَنْجَيْنَاكُمْ مِنْ آلِ فِرْعَوْنَ يَسُومُونَكُمْ سُوءَ الْعَذَابِ ۖ يُقَتِّلُونَ أَبْنَاءَكُمْ وَيَسْتَحْيُونَ نِسَاءَكُمْ ۚ وَفِي ذَٰلِكُمْ بَلَاءٌ مِنْ رَبِّكُمْ عَظِيمٌ', 7, 141, 9, 33, 167, '(به خاطر بیاورید) زمانی را که از (چنگال) فرعونیان نجاتتان بخشیدیم! آنها که پیوسته شما را شکنجه می‌دادند، پسرانتان را می‌کشتند، و زنانتان را (برای خدمتگاری) زنده می‌گذاشتند؛ و در این، آزمایش بزرگی از سوی خدا برای شما بود.'),
(1096, 1096, ' وَوَاعَدْنَا مُوسَىٰ ثَلَاثِينَ لَيْلَةً وَأَتْمَمْنَاهَا بِعَشْرٍ فَتَمَّ مِيقَاتُ رَبِّهِ أَرْبَعِينَ لَيْلَةً ۚ وَقَالَ مُوسَىٰ لِأَخِيهِ هَارُونَ اخْلُفْنِي فِي قَوْمِي وَأَصْلِحْ وَلَا تَتَّبِعْ سَبِيلَ الْمُفْسِدِينَ', 7, 142, 9, 34, 167, 'و ما با موسی، سی شب وعده گذاشتیم؛ سپس آن را با ده شب (دیگر) تکمیل نمودیم؛ به این ترتیب، میعاد پروردگارش (با او)، چهل شب تمام شد. و موسی به برادرش هارون گفت: «جانشین من در میان قومم باش. و (آنها) را اصلاح کن! و از روش مفسدان، پیروی منما!»'),
(1097, 1097, 'وَلَمَّا جَاءَ مُوسَىٰ لِمِيقَاتِنَا وَكَلَّمَهُ رَبُّهُ قَالَ رَبِّ أَرِنِي أَنْظُرْ إِلَيْكَ ۚ قَالَ لَنْ تَرَانِي وَلَٰكِنِ انْظُرْ إِلَى الْجَبَلِ فَإِنِ اسْتَقَرَّ مَكَانَهُ فَسَوْفَ تَرَانِي ۚ فَلَمَّا تَجَلَّىٰ رَبُّهُ لِلْجَبَلِ جَعَلَهُ دَكًّا وَخَرَّ مُوسَىٰ صَعِقًا ۚ فَلَمَّا أَفَاقَ قَالَ سُبْحَانَكَ تُبْتُ إِلَيْكَ وَأَنَا أَوَّلُ الْمُؤْمِنِينَ', 7, 143, 9, 34, 167, 'و هنگامی که موسی به میعادگاه ما آمد، و پروردگارش با او سخن گفت، عرض کرد: «پروردگارا! خودت را به من نشان ده، تا تو را ببینم!» گفت: «هرگز مرا نخواهی دید! ولی به کوه بنگر، اگر در جای خود ثابت ماند، مرا خواهی دید!» اما هنگامی که پروردگارش بر کوه جلوه کرد، آن را همسان خاک قرار داد؛ و موسی مدهوش به زمین افتاد. چون به هوش آمد، عرض کرد: «خداوندا! منزهی تو (از اینکه با چشم تو را ببینم)! من به سوی تو بازگشتم! و من نخستین مؤمنانم!»'),
(1098, 1098, 'قَالَ يَا مُوسَىٰ إِنِّي اصْطَفَيْتُكَ عَلَى النَّاسِ بِرِسَالَاتِي وَبِكَلَامِي فَخُذْ مَا آتَيْتُكَ وَكُنْ مِنَ الشَّاكِرِينَ', 7, 144, 9, 34, 168, '(خداوند) فرمود: «ای موسی! من تو را با رسالتهای خویش، و با سخن‌گفتنم (با تو)، بر مردم برتری دادم و برگزیدم؛ پس آنچه را به تو داده‌ام بگیر و از شکرگزاران باش!»'),
(1099, 1099, 'وَكَتَبْنَا لَهُ فِي الْأَلْوَاحِ مِنْ كُلِّ شَيْءٍ مَوْعِظَةً وَتَفْصِيلًا لِكُلِّ شَيْءٍ فَخُذْهَا بِقُوَّةٍ وَأْمُرْ قَوْمَكَ يَأْخُذُوا بِأَحْسَنِهَا ۚ سَأُرِيكُمْ دَارَ الْفَاسِقِينَ', 7, 145, 9, 34, 168, 'و برای او در الواح اندرزی از هر موضوعی نوشتیم؛ و بیانی از هر چیز کردیم -«پس آن را با جدّیت بگیر! و به قوم خود بگو: به نیکوترین آنها عمل کنند! (و آنها که به مخالفت برخیزند، کیفرشان دوزخ است؛) و بزودی جایگاه فاسقان را به شما نشان خواهم داد!»'),
(1100, 1100, 'سَأَصْرِفُ عَنْ آيَاتِيَ الَّذِينَ يَتَكَبَّرُونَ فِي الْأَرْضِ بِغَيْرِ الْحَقِّ وَإِنْ يَرَوْا كُلَّ آيَةٍ لَا يُؤْمِنُوا بِهَا وَإِنْ يَرَوْا سَبِيلَ الرُّشْدِ لَا يَتَّخِذُوهُ سَبِيلًا وَإِنْ يَرَوْا سَبِيلَ الْغَيِّ يَتَّخِذُوهُ سَبِيلًا ۚ ذَٰلِكَ بِأَنَّهُمْ كَذَّبُوا بِآيَاتِنَا وَكَانُوا عَنْهَا غَافِلِينَ', 7, 146, 9, 34, 168, 'بزودی کسانی را که در روی زمین بناحق تکبّر می‌ورزند، از (ایمان به) آیات خود، منصرف می‌سازم! آنها چنانند که اگر هر آیه و نشانه‌ای را ببینند، به آن ایمان نمی‌آورند؛ اگر راه هدایت را ببینند، آن را راه خود انتخاب نمی‌کنند؛ و اگر طریق گمراهی را ببینند، آن را راه خود انتخاب می‌کنند! (همه اینها) بخاطر آن است که آیات ما را تکذیب کردند، و از آن غافل بودند!'),
(1101, 1101, 'وَالَّذِينَ كَذَّبُوا بِآيَاتِنَا وَلِقَاءِ الْآخِرَةِ حَبِطَتْ أَعْمَالُهُمْ ۚ هَلْ يُجْزَوْنَ إِلَّا مَا كَانُوا يَعْمَلُونَ', 7, 147, 9, 34, 168, 'و کسانی که آیات، و دیدار رستاخیز را تکذیب (و انکار) کنند، اعمالشان نابود می گردد؛ آیا جز آنچه را عمل می‌کردند پاداش داده می‌شوند؟!'),
(1102, 1102, 'وَاتَّخَذَ قَوْمُ مُوسَىٰ مِنْ بَعْدِهِ مِنْ حُلِيِّهِمْ عِجْلًا جَسَدًا لَهُ خُوَارٌ ۚ أَلَمْ يَرَوْا أَنَّهُ لَا يُكَلِّمُهُمْ وَلَا يَهْدِيهِمْ سَبِيلًا ۘ اتَّخَذُوهُ وَكَانُوا ظَالِمِينَ', 7, 148, 9, 34, 168, 'قوم موسی بعد (از رفتن) او (به میعادگاه خدا)، از زیورهای خود گوساله‌ای ساختند؛ جسد بی‌جانی که صدای گوساله داشت! آیا آنها نمی‌دیدند که با آنان سخن نمی‌گوید، و به راه (راست) هدایتشان نمی‌کند؟! آن را (خدای خود) انتخاب کردند، و ظالم بودند!'),
(1103, 1103, 'وَلَمَّا سُقِطَ فِي أَيْدِيهِمْ وَرَأَوْا أَنَّهُمْ قَدْ ضَلُّوا قَالُوا لَئِنْ لَمْ يَرْحَمْنَا رَبُّنَا وَيَغْفِرْ لَنَا لَنَكُونَنَّ مِنَ الْخَاسِرِينَ', 7, 149, 9, 34, 168, 'و هنگامی که حقیقت به دستشان افتاد، و دیدند گمراه شده‌اند، گفتند: «اگر پروردگارمان به ما رحم نکند، و ما را نیامرزد، بطور قطع از زیانکاران خواهیم بود!»'),
(1104, 1104, 'وَلَمَّا رَجَعَ مُوسَىٰ إِلَىٰ قَوْمِهِ غَضْبَانَ أَسِفًا قَالَ بِئْسَمَا خَلَفْتُمُونِي مِنْ بَعْدِي ۖ أَعَجِلْتُمْ أَمْرَ رَبِّكُمْ ۖ وَأَلْقَى الْأَلْوَاحَ وَأَخَذَ بِرَأْسِ أَخِيهِ يَجُرُّهُ إِلَيْهِ ۚ قَالَ ابْنَ أُمَّ إِنَّ الْقَوْمَ اسْتَضْعَفُونِي وَكَادُوا يَقْتُلُونَنِي فَلَا تُشْمِتْ بِيَ الْأَعْدَاءَ وَلَا تَجْعَلْنِي مَعَ الْقَوْمِ الظَّالِمِينَ', 7, 150, 9, 34, 169, 'و هنگامی که موسی خشمگین و اندوهناک به سوی قوم خود بازگشت، گفت: «پس از من، بد جانشینانی برایم بودید (و آیین مرا ضایع کردید)! آیا درمورد فرمان پروردگارتان (و تمدید مدّت میعاد او)، عجله نمودید (و زود قضاوت کردید؟!)» سپس الواح را افکند، و سر برادر خود را گرفت (و با عصبانیت) به سوی خود کشید؛ او گفت: «فرزند مادرم! این گروه، مرا در فشار گذاردند و ناتوان کردند؛ و نزدیک بود مرا بکشند، پس کاری نکن که دشمنان مرا شماتت کنند و مرا با گروه ستمکاران قرار مده!»'),
(1105, 1105, 'قَالَ رَبِّ اغْفِرْ لِي وَلِأَخِي وَأَدْخِلْنَا فِي رَحْمَتِكَ ۖ وَأَنْتَ أَرْحَمُ الرَّاحِمِينَ', 7, 151, 9, 34, 169, '(موسی) گفت: «پروردگارا! من و برادرم را بیامرز، و ما را در رحمت خود داخل فرما، و تو مهربانترین مهربانانی!»'),
(1106, 1106, 'إِنَّ الَّذِينَ اتَّخَذُوا الْعِجْلَ سَيَنَالُهُمْ غَضَبٌ مِنْ رَبِّهِمْ وَذِلَّةٌ فِي الْحَيَاةِ الدُّنْيَا ۚ وَكَذَٰلِكَ نَجْزِي الْمُفْتَرِينَ', 7, 152, 9, 34, 169, 'کسانی که گوساله را (معبود خود) قرار دادند، بزودی خشم پروردگارشان، و ذلّت در زندگی دنیا به آنها می‌رسد؛ و اینچنین، کسانی را که (بر خدا) افترا می‌بندند، کیفر می دهیم!'),
(1107, 1107, 'وَالَّذِينَ عَمِلُوا السَّيِّئَاتِ ثُمَّ تَابُوا مِنْ بَعْدِهَا وَآمَنُوا إِنَّ رَبَّكَ مِنْ بَعْدِهَا لَغَفُورٌ رَحِيمٌ', 7, 153, 9, 34, 169, 'و آنها که گناه کردند، و بعد از آن توبه نمودند و ایمان آوردند، (امید عفو او را دارند؛ زیرا) پروردگار تو، در پی این کار، آمرزنده و مهربان است.'),
(1108, 1108, 'وَلَمَّا سَكَتَ عَنْ مُوسَى الْغَضَبُ أَخَذَ الْأَلْوَاحَ ۖ وَفِي نُسْخَتِهَا هُدًى وَرَحْمَةٌ لِلَّذِينَ هُمْ لِرَبِّهِمْ يَرْهَبُونَ', 7, 154, 9, 34, 169, 'هنگامی که خشم موسی فرو نشست؛ الواح (تورات) را برگرفت؛ و در نوشته‌های آن، هدایت و رحمت برای کسانی بود که از پروردگار خویش می‌ترسند (و از مخالفت فرمانش بیم دارند).'),
(1109, 1109, 'وَاخْتَارَ مُوسَىٰ قَوْمَهُ سَبْعِينَ رَجُلًا لِمِيقَاتِنَا ۖ فَلَمَّا أَخَذَتْهُمُ الرَّجْفَةُ قَالَ رَبِّ لَوْ شِئْتَ أَهْلَكْتَهُمْ مِنْ قَبْلُ وَإِيَّايَ ۖ أَتُهْلِكُنَا بِمَا فَعَلَ السُّفَهَاءُ مِنَّا ۖ إِنْ هِيَ إِلَّا فِتْنَتُكَ تُضِلُّ بِهَا مَنْ تَشَاءُ وَتَهْدِي مَنْ تَشَاءُ ۖ أَنْتَ وَلِيُّنَا فَاغْفِرْ لَنَا وَارْحَمْنَا ۖ وَأَنْتَ خَيْرُ الْغَافِرِينَ', 7, 155, 9, 34, 169, 'موسی از قوم خود، هفتاد تن از مردان را برای میعادگاه ما برگزید؛ و هنگامی که زمین‌لرزه آنها را فرا گرفت (و هلاک شدند)، گفت: «پروردگارا! اگر می‌خواستی، می توانستی آنها و مرا پیش از این نیز هلاک کنی! آیا ما را به آنچه سفیهانمان انجام داده‌اند، (مجازات و) هلاک می‌کنی؟! این، جز آزمایش تو، چیز دیگر نیست؛ که هر کس را بخواهی (و مستحق بدانی)، به وسیله آن گمراه می‌سازی؛ و هر کس را بخواهی (و شایسته ببینی)، هدایت می‌کنی! تو ولیّ مایی، و ما را بیامرز، بر ما رحم کن، و تو بهترین آمرزندگانی!'),
(1110, 1110, ' وَاكْتُبْ لَنَا فِي هَٰذِهِ الدُّنْيَا حَسَنَةً وَفِي الْآخِرَةِ إِنَّا هُدْنَا إِلَيْكَ ۚ قَالَ عَذَابِي أُصِيبُ بِهِ مَنْ أَشَاءُ ۖ وَرَحْمَتِي وَسِعَتْ كُلَّ شَيْءٍ ۚ فَسَأَكْتُبُهَا لِلَّذِينَ يَتَّقُونَ وَيُؤْتُونَ الزَّكَاةَ وَالَّذِينَ هُمْ بِآيَاتِنَا يُؤْمِنُونَ', 7, 156, 9, 34, 170, 'و برای ما، در این دنیا و سرای دیگر، نیکی مقرّر فرما؛ چه اینکه ما به سوی تو بازگشت کرده‌ایم! «(خداوند در برابر این تقاضا، به موسی) گفت:» مجازاتم را به هر کس بخواهم می‌رسانم؛ و رحمتم همه چیز را فراگرفته؛ و آن را برای آنها که تقوا پیشه کنند، و زکات را بپردازند، و آنها که به آیات ما ایمان می‌آورند، مقرّر خواهم داشت!'),
(1111, 1111, 'الَّذِينَ يَتَّبِعُونَ الرَّسُولَ النَّبِيَّ الْأُمِّيَّ الَّذِي يَجِدُونَهُ مَكْتُوبًا عِنْدَهُمْ فِي التَّوْرَاةِ وَالْإِنْجِيلِ يَأْمُرُهُمْ بِالْمَعْرُوفِ وَيَنْهَاهُمْ عَنِ الْمُنْكَرِ وَيُحِلُّ لَهُمُ الطَّيِّبَاتِ وَيُحَرِّمُ عَلَيْهِمُ الْخَبَائِثَ وَيَضَعُ عَنْهُمْ إِصْرَهُمْ وَالْأَغْلَالَ الَّتِي كَانَتْ عَلَيْهِمْ ۚ فَالَّذِينَ آمَنُوا بِهِ وَعَزَّرُوهُ وَنَصَرُوهُ وَاتَّبَعُوا النُّورَ الَّذِي أُنْزِلَ مَعَهُ ۙ أُولَٰئِكَ هُمُ الْمُفْلِحُونَ', 7, 157, 9, 34, 170, 'همانها که از فرستاده (خدا)، پیامبر «امّی» پیروی می‌کنند؛ پیامبری که صفاتش را، در تورات و انجیلی که نزدشان است، می‌یابند؛ آنها را به معروف دستور می‌دهد، و از منکر باز میدارد؛ اشیار پاکیزه را برای آنها حلال می‌شمرد، و ناپاکیها را تحریم می کند؛ و بارهای سنگین، و زنجیرهایی را که بر آنها بود، (از دوش و گردنشان) بر می‌دارد، پس کسانی که به او ایمان آوردند، و حمایت و یاریش کردند، و از نوری که با او نازل شده پیروی نمودند، آنان رستگارانند.'),
(1112, 1112, 'قُلْ يَا أَيُّهَا النَّاسُ إِنِّي رَسُولُ اللَّهِ إِلَيْكُمْ جَمِيعًا الَّذِي لَهُ مُلْكُ السَّمَاوَاتِ وَالْأَرْضِ ۖ لَا إِلَٰهَ إِلَّا هُوَ يُحْيِي وَيُمِيتُ ۖ فَآمِنُوا بِاللَّهِ وَرَسُولِهِ النَّبِيِّ الْأُمِّيِّ الَّذِي يُؤْمِنُ بِاللَّهِ وَكَلِمَاتِهِ وَاتَّبِعُوهُ لَعَلَّكُمْ تَهْتَدُونَ', 7, 158, 9, 34, 170, 'بگو: «ای مردم! من فرستاده خدا به سوی همه شما هستم؛ همان خدایی که حکومت آسمانها و زمین، از آن اوست؛ معبودی جز او نیست؛ زنده می‌کند و می‌میراند؛ پس ایمان بیاورید به خدا و فرستاده‌اش، آن پیامبر درس نخوانده‌ای که به خدا و کلماتش ایمان دارد؛ و از او پیروی کنید تا هدایت یابید!»'),
(1113, 1113, 'وَمِنْ قَوْمِ مُوسَىٰ أُمَّةٌ يَهْدُونَ بِالْحَقِّ وَبِهِ يَعْدِلُونَ', 7, 159, 9, 34, 170, 'و از قوم موسی، گروهی هستند که به سوی حق هدایت می‌کنند؛ و به حق و عدالت حکم می‌نمایند.'),
(1114, 1114, 'وَقَطَّعْنَاهُمُ اثْنَتَيْ عَشْرَةَ أَسْبَاطًا أُمَمًا ۚ وَأَوْحَيْنَا إِلَىٰ مُوسَىٰ إِذِ اسْتَسْقَاهُ قَوْمُهُ أَنِ اضْرِبْ بِعَصَاكَ الْحَجَرَ ۖ فَانْبَجَسَتْ مِنْهُ اثْنَتَا عَشْرَةَ عَيْنًا ۖ قَدْ عَلِمَ كُلُّ أُنَاسٍ مَشْرَبَهُمْ ۚ وَظَلَّلْنَا عَلَيْهِمُ الْغَمَامَ وَأَنْزَلْنَا عَلَيْهِمُ الْمَنَّ وَالسَّلْوَىٰ ۖ كُلُوا مِنْ طَيِّبَاتِ مَا رَزَقْنَاكُمْ ۚ وَمَا ظَلَمُونَا وَلَٰكِنْ كَانُوا أَنْفُسَهُمْ يَظْلِمُونَ', 7, 160, 9, 34, 171, 'ما آنها را به دوازده گروه -که هر یک شاخه‌ای (از دودمان اسرائیل) بود - تقسیم کردیم. و هنگامی که قوم موسی (در بیابان) از او تقاضای آب کردند، به او وحی فرستادیم که: «عصای خود را بر سنگ بزن!» ناگهان دوازده چشمه از آن بیرون جست؛ آنچنان که هر گروه، چشمه و آبشخور خود را می‌شناخت. و ابر را بر سر آنها سایبان ساختیم؛ و بر آنها «مَن» و «سَلوی» فرستادیم؛ (و به آنان گفتیم:) از روزیهای پاکیزه‌ای که به شما داده‌ایم، بخورید! (و شکر خدا را بجا آورید! آنها نافرمانی و ستم کردند؛ ولی) به ما ستم نکردند، لکن به خودشان ستم می‌نمودند.'),
(1115, 1115, 'وَإِذْ قِيلَ لَهُمُ اسْكُنُوا هَٰذِهِ الْقَرْيَةَ وَكُلُوا مِنْهَا حَيْثُ شِئْتُمْ وَقُولُوا حِطَّةٌ وَادْخُلُوا الْبَابَ سُجَّدًا نَغْفِرْ لَكُمْ خَطِيئَاتِكُمْ ۚ سَنَزِيدُ الْمُحْسِنِينَ', 7, 161, 9, 34, 171, 'و (به خاطر بیاورید) هنگامی را که به آنها گفته شد: «در این شهر = بیت المقدّس ساکن شوید، و از هر جا (و به هر کیفیت) بخواهید، از آن بخورید (و بهره گیرید)! و بگویید: خداوندا! گناهان ما را بریز! و از درِ (بیت المقدس) با تواضع وارد شوید! که اگر چنین کنید، گناهان شما را می‌بخشم؛ و نیکوکاران را پاداش بیشتر خواهیم داد.»'),
(1116, 1116, 'فَبَدَّلَ الَّذِينَ ظَلَمُوا مِنْهُمْ قَوْلًا غَيْرَ الَّذِي قِيلَ لَهُمْ فَأَرْسَلْنَا عَلَيْهِمْ رِجْزًا مِنَ السَّمَاءِ بِمَا كَانُوا يَظْلِمُونَ', 7, 162, 9, 34, 171, 'اما ستمگران آنها، این سخن (و آن فرمانها) را، بغیر آنچه به آنها گفته شده بود، تغییر دادند؛ از این‌رو بخاطر ستمی که روا میداشتند، بلایی از آسمان بر آنها فرستادیم (و مجازاتشان کردیم).'),
(1117, 1117, 'وَاسْأَلْهُمْ عَنِ الْقَرْيَةِ الَّتِي كَانَتْ حَاضِرَةَ الْبَحْرِ إِذْ يَعْدُونَ فِي السَّبْتِ إِذْ تَأْتِيهِمْ حِيتَانُهُمْ يَوْمَ سَبْتِهِمْ شُرَّعًا وَيَوْمَ لَا يَسْبِتُونَ ۙ لَا تَأْتِيهِمْ ۚ كَذَٰلِكَ نَبْلُوهُمْ بِمَا كَانُوا يَفْسُقُونَ', 7, 163, 9, 34, 171, 'و از آنها درباره (سرگذشت) شهری که در ساحل دریا بود بپرس! زمانی که آنها در روزهای شنبه، تجاوز (و نافرمانی) خدا می‌کردند؛ همان هنگام که ماهیانشان، روز شنبه (که روز تعطیل و استراحت و عبادت بود، بر سطح آب،) آشکار می‌شدند؛ امّا در غیر روز شنبه، به سراغ آنها نمی‌آمدند؛ این چنین آنها را به چیزی آزمایش کردیم که نافرمانی می‌کردند!'),
(1118, 1118, 'وَإِذْ قَالَتْ أُمَّةٌ مِنْهُمْ لِمَ تَعِظُونَ قَوْمًا ۙ اللَّهُ مُهْلِكُهُمْ أَوْ مُعَذِّبُهُمْ عَذَابًا شَدِيدًا ۖ قَالُوا مَعْذِرَةً إِلَىٰ رَبِّكُمْ وَلَعَلَّهُمْ يَتَّقُونَ', 7, 164, 9, 34, 172, 'و (به یاد آر) هنگامی را که گروهی از آنها (به گروه دیگر) گفتند: «چرا جمعی (گنهکار) را اندرز می‌دهید که سرانجام خداوند آنها را هلاک خواهد کرد، یا به عذاب شدیدی گرفتار خواهد ساخت؟! (آنها را به حال خود واگذارید تا نابود شوند!)» گفتند: «(این اندرزها،) برای اعتذار (و رفع مسؤولیت) در پیشگاه پروردگار شماست؛ بعلاوه شاید آنها (بپذیرند، و از گناه باز ایستند، و) تقوا پیشه کنند!»'),
(1119, 1119, 'فَلَمَّا نَسُوا مَا ذُكِّرُوا بِهِ أَنْجَيْنَا الَّذِينَ يَنْهَوْنَ عَنِ السُّوءِ وَأَخَذْنَا الَّذِينَ ظَلَمُوا بِعَذَابٍ بَئِيسٍ بِمَا كَانُوا يَفْسُقُونَ', 7, 165, 9, 34, 172, 'امّا هنگامی که تذکراتی را که به آنها داده شده بود فراموش کردند، (لحظه عذاب فرا رسید؛ و) نهی‌کنندگان از بدی را رهایی بخشیدیم؛ و کسانی را که ستم کردند، بخاطر نافرمانیشان به عذاب شدیدی گرفتار ساختیم.'),
(1120, 1120, 'فَلَمَّا عَتَوْا عَنْ مَا نُهُوا عَنْهُ قُلْنَا لَهُمْ كُونُوا قِرَدَةً خَاسِئِينَ', 7, 166, 9, 34, 172, '(آری،) هنگامی که در برابر آنچه از آن نهی شده بودند سرکشی کردند، به آنها گفتیم: «به شکل میمونهایی طردشده در آیید!»'),
(1121, 1121, 'وَإِذْ تَأَذَّنَ رَبُّكَ لَيَبْعَثَنَّ عَلَيْهِمْ إِلَىٰ يَوْمِ الْقِيَامَةِ مَنْ يَسُومُهُمْ سُوءَ الْعَذَابِ ۗ إِنَّ رَبَّكَ لَسَرِيعُ الْعِقَابِ ۖ وَإِنَّهُ لَغَفُورٌ رَحِيمٌ', 7, 167, 9, 34, 172, 'و (نیز به خاطر بیاور) هنگامی را که پروردگارت اعلام کرد: تا دامنه قیامت، کسی را بر آنها مسلّط خواهد ساخت که همواره آنها را در عذاب سختی قرار دهد؛ زیرا پروردگارت مجازاتش سریع، (و در عین حال، نسبت به توبه‌کاران) آمرزنده و مهربان است.'),
(1122, 1122, 'وَقَطَّعْنَاهُمْ فِي الْأَرْضِ أُمَمًا ۖ مِنْهُمُ الصَّالِحُونَ وَمِنْهُمْ دُونَ ذَٰلِكَ ۖ وَبَلَوْنَاهُمْ بِالْحَسَنَاتِ وَالسَّيِّئَاتِ لَعَلَّهُمْ يَرْجِعُونَ', 7, 168, 9, 34, 172, 'و آنها را در زمین بصورت گروه‌هایی، پراکنده ساختیم؛ گروهی از آنها صالح، و گروهی ناصالحند. و آنها را با نیکی‌ها و بدی‌ها آزمودیم، شاید بازگردند!'),
(1123, 1123, 'فَخَلَفَ مِنْ بَعْدِهِمْ خَلْفٌ وَرِثُوا الْكِتَابَ يَأْخُذُونَ عَرَضَ هَٰذَا الْأَدْنَىٰ وَيَقُولُونَ سَيُغْفَرُ لَنَا وَإِنْ يَأْتِهِمْ عَرَضٌ مِثْلُهُ يَأْخُذُوهُ ۚ أَلَمْ يُؤْخَذْ عَلَيْهِمْ مِيثَاقُ الْكِتَابِ أَنْ لَا يَقُولُوا عَلَى اللَّهِ إِلَّا الْحَقَّ وَدَرَسُوا مَا فِيهِ ۗ وَالدَّارُ الْآخِرَةُ خَيْرٌ لِلَّذِينَ يَتَّقُونَ ۗ أَفَلَا تَعْقِلُونَ', 7, 169, 9, 34, 172, 'پس از آنها، فرزندانی جای آنها را گرفتند که وارث کتاب (آسمانی، تورات) شدند؛ (امّا با این حال،) متاع این دنیای پست را گرفته، (بر اطاعت فرمان خدا ترجیح می‌دهند) و می‌گویند: «(اگر ما گنهکاریم توبه می‌کنیم و) بزودی بخشیده خواهیم شد!» اما اگر متاع دیگری همانند آن به دستشان بیفتد، آن را (نیز) می‌گیرند، (و باز حکم خدا را پشت سر می‌افکنند.) آیا پیمان کتاب (خدا) از آنها گرفته نشده که بر خدا (دروغ نبندند، و) جز حق نگویند، و آنان بارها آنرا خوانده‌اند؟! و سرای آخرت برای پرهیزگاران بهتر است، آیا نمی‌فهمید؟!'),
(1124, 1124, 'وَالَّذِينَ يُمَسِّكُونَ بِالْكِتَابِ وَأَقَامُوا الصَّلَاةَ إِنَّا لَا نُضِيعُ أَجْرَ الْمُصْلِحِينَ', 7, 170, 9, 34, 172, 'و آنها که به کتاب (خدا) تمسّک جویند، و نماز را برپا دارند، (پاداش بزرگی خواهند داشت؛ زیرا) ما پاداش مصلحان را ضایع نخواهیم کرد!'),
(1125, 1125, ' وَإِذْ نَتَقْنَا الْجَبَلَ فَوْقَهُمْ كَأَنَّهُ ظُلَّةٌ وَظَنُّوا أَنَّهُ وَاقِعٌ بِهِمْ خُذُوا مَا آتَيْنَاكُمْ بِقُوَّةٍ وَاذْكُرُوا مَا فِيهِ لَعَلَّكُمْ تَتَّقُونَ', 7, 171, 9, 35, 173, 'و (نیز به خاطر بیاور) هنگامی که کوه را همچون سایبانی بر فراز آنها بلند کردیم، آنچنان که گمان کردند بر آنان فرود می‌آمد؛ (و در همین حال، از آنها پیمان گرفتیم و گفتیم:) آنچه را (از احکام و دستورها) به شما داده‌ایم، با قوّت (و جدیت) بگیرید! و آنچه در آن است، به یاد داشته باشید، (و عمل کنید،) تا پرهیزگار شوید!'),
(1126, 1126, 'وَإِذْ أَخَذَ رَبُّكَ مِنْ بَنِي آدَمَ مِنْ ظُهُورِهِمْ ذُرِّيَّتَهُمْ وَأَشْهَدَهُمْ عَلَىٰ أَنْفُسِهِمْ أَلَسْتُ بِرَبِّكُمْ ۖ قَالُوا بَلَىٰ ۛ شَهِدْنَا ۛ أَنْ تَقُولُوا يَوْمَ الْقِيَامَةِ إِنَّا كُنَّا عَنْ هَٰذَا غَافِلِينَ', 7, 172, 9, 35, 173, 'و (به خاطر بیاور) زمانی را که پروردگارت از پشت و صلب فرزندان آدم، ذریه آنها را برگرفت؛ و آنها را گواه بر خویشتن ساخت؛ (و فرمود:) «آیا من پروردگار شما نیستم؟» گفتند: «آری، گواهی می‌دهیم!» (چنین کرد مبادا) روز رستاخیز بگویید: «ما از این، غافل بودیم؛ (و از پیمان فطری توحید بی‌خبر ماندیم)»!'),
(1127, 1127, 'أَوْ تَقُولُوا إِنَّمَا أَشْرَكَ آبَاؤُنَا مِنْ قَبْلُ وَكُنَّا ذُرِّيَّةً مِنْ بَعْدِهِمْ ۖ أَفَتُهْلِكُنَا بِمَا فَعَلَ الْمُبْطِلُونَ', 7, 173, 9, 35, 173, 'یا بگویید: «پدرانمان پیش از ما مشرک بودند، ما هم فرزندانی بعد از آنها بودیم؛ (و چاره‌ای جز پیروی از آنان نداشتیم؛) آیا ما را به آنچه باطل‌گرایان انجام دادند مجازات می‌کنی؟!»'),
(1128, 1128, 'وَكَذَٰلِكَ نُفَصِّلُ الْآيَاتِ وَلَعَلَّهُمْ يَرْجِعُونَ', 7, 174, 9, 35, 173, 'این گونه، آیات را توضیح می‌دهیم؛ و شاید به سوی حق بازگردند (و بدانند ندای توحید در درون جانشان، از روز نخست بوده است)!'),
(1129, 1129, 'وَاتْلُ عَلَيْهِمْ نَبَأَ الَّذِي آتَيْنَاهُ آيَاتِنَا فَانْسَلَخَ مِنْهَا فَأَتْبَعَهُ الشَّيْطَانُ فَكَانَ مِنَ الْغَاوِينَ', 7, 175, 9, 35, 173, 'و بر آنها بخوان سرگذشت آن کس را که آیات خود را به او دادیم؛ ولی (سرانجام) خود را از آن تهی ساخت و شیطان در پی او افتاد، و از گمراهان شد!'),
(1130, 1130, 'وَلَوْ شِئْنَا لَرَفَعْنَاهُ بِهَا وَلَٰكِنَّهُ أَخْلَدَ إِلَى الْأَرْضِ وَاتَّبَعَ هَوَاهُ ۚ فَمَثَلُهُ كَمَثَلِ الْكَلْبِ إِنْ تَحْمِلْ عَلَيْهِ يَلْهَثْ أَوْ تَتْرُكْهُ يَلْهَثْ ۚ ذَٰلِكَ مَثَلُ الْقَوْمِ الَّذِينَ كَذَّبُوا بِآيَاتِنَا ۚ فَاقْصُصِ الْقَصَصَ لَعَلَّهُمْ يَتَفَكَّرُونَ', 7, 176, 9, 35, 173, 'و اگر می‌خواستیم، (مقام) او را با این آیات (و علوم و دانشها) بالا می‌بردیم؛ (اما اجبار، بر خلاف سنت ماست؛ پس او را به حال خود رها کردیم) و او به پستی گرایید، و از هوای نفس پیروی کرد! مثل او همچون سگ (هار) است که اگر به او حمله کنی، دهانش را باز، و زبانش را برون می‌آورد، و اگر او را به حال خود واگذاری، باز همین کار را می‌کند؛ (گویی چنان تشنه دنیاپرستی است که هرگز سیراب نمی‌شود! (این مثل گروهی است که آیات ما را تکذیب کردند؛ این داستانها را (برای آنها) بازگو کن، شاید بیندیشند (و بیدار شوند)!'),
(1131, 1131, 'سَاءَ مَثَلًا الْقَوْمُ الَّذِينَ كَذَّبُوا بِآيَاتِنَا وَأَنْفُسَهُمْ كَانُوا يَظْلِمُونَ', 7, 177, 9, 35, 173, 'چه بد مثلی دارند گروهی که آیات ما را تکذیب کردند؛ و آنها تنها به خودشان ستم می‌نمودند!'),
(1132, 1132, 'مَنْ يَهْدِ اللَّهُ فَهُوَ الْمُهْتَدِي ۖ وَمَنْ يُضْلِلْ فَأُولَٰئِكَ هُمُ الْخَاسِرُونَ', 7, 178, 9, 35, 173, 'آن کس را که خدا هدایت کند، هدایت یافته (واقعی) اوست؛ و کسانی را که (بخاطر اعمالشان) گمراه سازد، زیانکاران (واقعی) آنها هستند!'),
(1133, 1133, 'وَلَقَدْ ذَرَأْنَا لِجَهَنَّمَ كَثِيرًا مِنَ الْجِنِّ وَالْإِنْسِ ۖ لَهُمْ قُلُوبٌ لَا يَفْقَهُونَ بِهَا وَلَهُمْ أَعْيُنٌ لَا يُبْصِرُونَ بِهَا وَلَهُمْ آذَانٌ لَا يَسْمَعُونَ بِهَا ۚ أُولَٰئِكَ كَالْأَنْعَامِ بَلْ هُمْ أَضَلُّ ۚ أُولَٰئِكَ هُمُ الْغَافِلُونَ', 7, 179, 9, 35, 174, 'به یقین، گروه بسیاری از جن و انس را برای دوزخ آفریدیم؛ آنها دلها = عقلها یی دارند که با آن (اندیشه نمی‌کنند، و) نمی‌فهمند؛ و چشمانی که با آن نمی‌بینند؛ و گوشهایی که با آن نمی‌شنوند؛ آنها همچون چهارپایانند؛ بلکه گمراهتر! اینان همان غافلانند (چرا که با داشتن همه‌گونه امکانات هدایت، باز هم گمراهند)!'),
(1134, 1134, 'وَلِلَّهِ الْأَسْمَاءُ الْحُسْنَىٰ فَادْعُوهُ بِهَا ۖ وَذَرُوا الَّذِينَ يُلْحِدُونَ فِي أَسْمَائِهِ ۚ سَيُجْزَوْنَ مَا كَانُوا يَعْمَلُونَ', 7, 180, 9, 35, 174, 'و برای خدا، نامهای نیک است؛ خدا را به آن (نامها) بخوانید! و کسانی را که در اسماء خدا تحریف می‌کنند (و بر غیر او می‌نهند، و شریک برایش قائل می‌شوند)، رها سازید! آنها بزودی جزای اعمالی را که انجام می‌دادند، می‌بینند!'),
(1135, 1135, 'وَمِمَّنْ خَلَقْنَا أُمَّةٌ يَهْدُونَ بِالْحَقِّ وَبِهِ يَعْدِلُونَ', 7, 181, 9, 35, 174, 'و از آنها که آفریدیم، گروهی بحق هدایت می‌کنند، و بحق اجرای عدالت می‌نمایند.'),
(1136, 1136, 'وَالَّذِينَ كَذَّبُوا بِآيَاتِنَا سَنَسْتَدْرِجُهُمْ مِنْ حَيْثُ لَا يَعْلَمُونَ', 7, 182, 9, 35, 174, 'و آنها که آیات ما را تکذیب کردند، به تدریج از جائی که نمی‌دانند، گرفتار مجازاتشان خواهیم کرد.'),
(1137, 1137, 'وَأُمْلِي لَهُمْ ۚ إِنَّ كَيْدِي مَتِينٌ', 7, 183, 9, 35, 174, 'و به آنها مهلت می‌دهم (تا مجازاتشان دردناکتر باشد)؛ زیرا طرح و نقشه من، قوی (و حساب شده) است. (و هیچ کس را قدرت فرار از آن نیست.)'),
(1138, 1138, 'أَوَلَمْ يَتَفَكَّرُوا ۗ مَا بِصَاحِبِهِمْ مِنْ جِنَّةٍ ۚ إِنْ هُوَ إِلَّا نَذِيرٌ مُبِينٌ', 7, 184, 9, 35, 174, 'آیا فکر نکردند که همنشین آنها = پیامبر هیچ‌گونه (اثری از) جنون ندارد؟! (پس چگونه چنین نسبت ناروایی به او می‌دهند؟!) او فقط بیم دهنده آشکاری است (که مردم را متوجه وظایفشان می‌سازد).'),
(1139, 1139, 'أَوَلَمْ يَنْظُرُوا فِي مَلَكُوتِ السَّمَاوَاتِ وَالْأَرْضِ وَمَا خَلَقَ اللَّهُ مِنْ شَيْءٍ وَأَنْ عَسَىٰ أَنْ يَكُونَ قَدِ اقْتَرَبَ أَجَلُهُمْ ۖ فَبِأَيِّ حَدِيثٍ بَعْدَهُ يُؤْمِنُونَ', 7, 185, 9, 35, 174, 'آیا در حکومت و نظام آسمانها و زمین، و آنچه خدا آفریده است، (از روی دقت و عبرت) نظر نیفکندند؟! (و آیا در این نیز اندیشه نکردند که) شاید پایان زندگی آنها نزدیک شده باشد؟! (اگر به این کتاب آسمانی روشن ایمان نیاورند،) بعد از آن به کدام سخن ایمان خواهند آورد؟!'),
(1140, 1140, 'مَنْ يُضْلِلِ اللَّهُ فَلَا هَادِيَ لَهُ ۚ وَيَذَرُهُمْ فِي طُغْيَانِهِمْ يَعْمَهُونَ', 7, 186, 9, 35, 174, 'هر کس را خداوند (به جرم اعمال زشتش) گمراه سازد، هدایت کننده‌ای ندارد؛ و آنها را در طغیان و سرکشی‌شان رها می‌سازد، تا سرگردان شوند!'),
(1141, 1141, 'يَسْأَلُونَكَ عَنِ السَّاعَةِ أَيَّانَ مُرْسَاهَا ۖ قُلْ إِنَّمَا عِلْمُهَا عِنْدَ رَبِّي ۖ لَا يُجَلِّيهَا لِوَقْتِهَا إِلَّا هُوَ ۚ ثَقُلَتْ فِي السَّمَاوَاتِ وَالْأَرْضِ ۚ لَا تَأْتِيكُمْ إِلَّا بَغْتَةً ۗ يَسْأَلُونَكَ كَأَنَّكَ حَفِيٌّ عَنْهَا ۖ قُلْ إِنَّمَا عِلْمُهَا عِنْدَ اللَّهِ وَلَٰكِنَّ أَكْثَرَ النَّاسِ لَا يَعْلَمُونَ', 7, 187, 9, 35, 174, 'درباره قیامت از تو سؤال می‌کنند، کی فرامی‌رسد؟! بگو: «علمش فقط نزد پروردگار من است؛ و هیچ‌کس جز او (نمی‌تواند) وقت آن را آشکار سازد؛ (اما قیام قیامت، حتی) در آسمانها و زمین، سنگین (و بسیار پر اهمیت) است؛ و جز بطور ناگهانی، به سراغ شما نمی‌آید!» (باز) از تو سؤال می‌کنند، چنان که گویی تو از زمان وقوع آن باخبری! بگو: «علمش تنها نزد خداست؛ ولی بیشتر مردم نمی‌دانند.»'),
(1142, 1142, 'قُلْ لَا أَمْلِكُ لِنَفْسِي نَفْعًا وَلَا ضَرًّا إِلَّا مَا شَاءَ اللَّهُ ۚ وَلَوْ كُنْتُ أَعْلَمُ الْغَيْبَ لَاسْتَكْثَرْتُ مِنَ الْخَيْرِ وَمَا مَسَّنِيَ السُّوءُ ۚ إِنْ أَنَا إِلَّا نَذِيرٌ وَبَشِيرٌ لِقَوْمٍ يُؤْمِنُونَ', 7, 188, 9, 35, 175, 'بگو: «من مالک سود و زیان خویش نیستم، مگر آنچه را خدا بخواهد؛ (و از غیب و اسرار نهان نیز خبر ندارم، مگر آنچه خداوند اراده کند؛) و اگر از غیب باخبر بودم، سود فراوانی برای خود فراهم می‌کردم، و هیچ بدی (و زیانی) به من نمی‌رسید؛ من فقط بیم‌دهنده و بشارت‌دهنده‌ام برای گروهی که ایمان می‌آورند! (و آماده پذیرش حقند)'),
(1143, 1143, ' هُوَ الَّذِي خَلَقَكُمْ مِنْ نَفْسٍ وَاحِدَةٍ وَجَعَلَ مِنْهَا زَوْجَهَا لِيَسْكُنَ إِلَيْهَا ۖ فَلَمَّا تَغَشَّاهَا حَمَلَتْ حَمْلًا خَفِيفًا فَمَرَّتْ بِهِ ۖ فَلَمَّا أَثْقَلَتْ دَعَوَا اللَّهَ رَبَّهُمَا لَئِنْ آتَيْتَنَا صَالِحًا لَنَكُونَنَّ مِنَ الشَّاكِرِينَ', 7, 189, 9, 35, 175, 'او خدایی است که (همه) شما را از یک فرد آفرید؛ و همسرش را نیز از جنس او قرار داد، تا در کنار او بیاساید. سپس هنگامی که با او آمیزش کرد، حملی سبک برداشت، که با وجود آن، به کارهای خود ادامه می‌داد؛ و چون سنگین شد، هر دو از خداوند و پروردگار خود خواستند «اگر فرزند صالحی به ما دهی، از شاکران خواهیم بود!»'),
(1144, 1144, 'فَلَمَّا آتَاهُمَا صَالِحًا جَعَلَا لَهُ شُرَكَاءَ فِيمَا آتَاهُمَا ۚ فَتَعَالَى اللَّهُ عَمَّا يُشْرِكُونَ', 7, 190, 9, 35, 175, 'اما هنگامی که خداوند فرزند صالحی به آنها داد، (موجودات دیگر را در این موهبت مؤثر دانستند؛ و) برای خدا، در این نعمت که به آنها بخشیده بود، همتایانی قائل شدند؛ خداوند برتر است از آنچه همتای او قرار می‌دهند!'),
(1145, 1145, 'أَيُشْرِكُونَ مَا لَا يَخْلُقُ شَيْئًا وَهُمْ يُخْلَقُونَ', 7, 191, 9, 35, 175, 'آیا موجوداتی را همتای او قرارمی‌دهند که چیزی را نمی‌آفرینند، و خودشان مخلوقند.'),
(1146, 1146, 'وَلَا يَسْتَطِيعُونَ لَهُمْ نَصْرًا وَلَا أَنْفُسَهُمْ يَنْصُرُونَ', 7, 192, 9, 35, 175, 'و نمی‌توانند آنان را یاری کنند، و نه خودشان را یاری می‌دهند.'),
(1147, 1147, 'وَإِنْ تَدْعُوهُمْ إِلَى الْهُدَىٰ لَا يَتَّبِعُوكُمْ ۚ سَوَاءٌ عَلَيْكُمْ أَدَعَوْتُمُوهُمْ أَمْ أَنْتُمْ صَامِتُونَ', 7, 193, 9, 35, 175, 'و هرگاه آنها را به سوی هدایت دعوت کنید، از شما پیروی نمی‌کنند؛ و برای شما یکسان است چه آنها را دعوت کنید و چه خاموش باشید؟!'),
(1148, 1148, 'إِنَّ الَّذِينَ تَدْعُونَ مِنْ دُونِ اللَّهِ عِبَادٌ أَمْثَالُكُمْ ۖ فَادْعُوهُمْ فَلْيَسْتَجِيبُوا لَكُمْ إِنْ كُنْتُمْ صَادِقِينَ', 7, 194, 9, 35, 175, 'آنهایی را که غیر از خدا می‌خوانید (و پرستش می‌کنید)، بندگانی همچون خود شما هستند؛ آنها را بخوانید، و اگر راست می‌گویید باید به شما پاسخ دهند (و تقاضایتان را برآورند)!'),
(1149, 1149, 'أَلَهُمْ أَرْجُلٌ يَمْشُونَ بِهَا ۖ أَمْ لَهُمْ أَيْدٍ يَبْطِشُونَ بِهَا ۖ أَمْ لَهُمْ أَعْيُنٌ يُبْصِرُونَ بِهَا ۖ أَمْ لَهُمْ آذَانٌ يَسْمَعُونَ بِهَا ۗ قُلِ ادْعُوا شُرَكَاءَكُمْ ثُمَّ كِيدُونِ فَلَا تُنْظِرُونِ', 7, 195, 9, 35, 175, 'آیا (آنها حداقل همانند خود شما) پاهایی دارند که با آن راه بروند؟! یا دستهایی دارند که با آن چیزی را بگیرند (و کاری انجام دهند)؟! یا چشمانی دارند که با آن ببینند؟! یا گوشهایی دارند که با آن بشنوند؟! (نه، هرگز، هیچ‌کدام،) بگو: «(اکنون که چنین است،) بتهای خویش را که شریک خدا قرار داده‌اید (بر ضد من) بخوانید، و برای من نقشه بکشید، و لحظه‌ای مهلت ندهید، (تا بدانید کاری از آنها ساخته نیست)!'),
(1150, 1150, 'إِنَّ وَلِيِّيَ اللَّهُ الَّذِي نَزَّلَ الْكِتَابَ ۖ وَهُوَ يَتَوَلَّى الصَّالِحِينَ', 7, 196, 9, 35, 176, 'ولی و سرپرست من، خدایی است که این کتاب را نازل کرده؛ و او همه صالحان را سرپرستی می‌کند.'),
(1151, 1151, 'وَالَّذِينَ تَدْعُونَ مِنْ دُونِهِ لَا يَسْتَطِيعُونَ نَصْرَكُمْ وَلَا أَنْفُسَهُمْ يَنْصُرُونَ', 7, 197, 9, 35, 176, 'و آنهایی را که جز او می‌خوانید، نمی‌توانند یاریتان کنند، و نه (حتی) خودشان را یاری دهند؛'),
(1152, 1152, 'وَإِنْ تَدْعُوهُمْ إِلَى الْهُدَىٰ لَا يَسْمَعُوا ۖ وَتَرَاهُمْ يَنْظُرُونَ إِلَيْكَ وَهُمْ لَا يُبْصِرُونَ', 7, 198, 9, 35, 176, 'و اگر آنها را به هدایت فرا خوانید، سخنانتان را نمی‌شنوند! و آنها را می‌بینی (که با چشمهای مصنوعیشان) به تو نگاه می‌کنند، اما در حقیقت نمی‌بینند!»'),
(1153, 1153, 'خُذِ الْعَفْوَ وَأْمُرْ بِالْعُرْفِ وَأَعْرِضْ عَنِ الْجَاهِلِينَ', 7, 199, 9, 35, 176, '(به هر حال) با آنها مدارا کن و عذرشان را بپذیر، و به نیکی‌ها دعوت نما، و از جاهلان روی بگردان (و با آنان ستیزه مکن)!'),
(1154, 1154, 'وَإِمَّا يَنْزَغَنَّكَ مِنَ الشَّيْطَانِ نَزْغٌ فَاسْتَعِذْ بِاللَّهِ ۚ إِنَّهُ سَمِيعٌ عَلِيمٌ', 7, 200, 9, 35, 176, 'و هرگاه وسوسه‌ای از شیطان به تو رسد، به خدا پناه بر؛ که او شنونده و داناست!'),
(1155, 1155, 'إِنَّ الَّذِينَ اتَّقَوْا إِذَا مَسَّهُمْ طَائِفٌ مِنَ الشَّيْطَانِ تَذَكَّرُوا فَإِذَا هُمْ مُبْصِرُونَ', 7, 201, 9, 35, 176, 'پرهیزگاران هنگامی که گرفتار وسوسه‌های شیطان شوند، به یاد (خدا و پاداش و کیفر او) می‌افتند؛ و (در پرتو یاد او، راه حق را می‌بینند و) ناگهان بینا می‌گردند.'),
(1156, 1156, 'وَإِخْوَانُهُمْ يَمُدُّونَهُمْ فِي الْغَيِّ ثُمَّ لَا يُقْصِرُونَ', 7, 202, 9, 35, 176, 'و (ناپرهیزگاران را) برادرانشان (از شیاطین) پیوسته در گمراهی پیش می‌برند، و باز نمی‌ایستند!'),
(1157, 1157, 'وَإِذَا لَمْ تَأْتِهِمْ بِآيَةٍ قَالُوا لَوْلَا اجْتَبَيْتَهَا ۚ قُلْ إِنَّمَا أَتَّبِعُ مَا يُوحَىٰ إِلَيَّ مِنْ رَبِّي ۚ هَٰذَا بَصَائِرُ مِنْ رَبِّكُمْ وَهُدًى وَرَحْمَةٌ لِقَوْمٍ يُؤْمِنُونَ', 7, 203, 9, 35, 176, 'هنگامی که (در نزول وحی تاخیر افتد، و) آیه‌ای برای آنان نیاوری، می‌گویند: «چرا خودت (از پیش خود) آن را برنگزیدی؟!» بگو: «من تنها از چیزی پیروی می‌کنم که بر من وحی می‌شود؛ این وسیله بینایی از طرف پروردگارتان، و مایه هدایت و رحمت است برای جمعیّتی که ایمان می‌آورند.'),
(1158, 1158, 'وَإِذَا قُرِئَ الْقُرْآنُ فَاسْتَمِعُوا لَهُ وَأَنْصِتُوا لَعَلَّكُمْ تُرْحَمُونَ', 7, 204, 9, 35, 176, 'هنگامی که قرآن خوانده شود، گوش فرا دهید و خاموش باشید؛ شاید مشمول رحمت خدا شوید!'),
(1159, 1159, 'وَاذْكُرْ رَبَّكَ فِي نَفْسِكَ تَضَرُّعًا وَخِيفَةً وَدُونَ الْجَهْرِ مِنَ الْقَوْلِ بِالْغُدُوِّ وَالْآصَالِ وَلَا تَكُنْ مِنَ الْغَافِلِينَ', 7, 205, 9, 35, 176, 'پروردگارت را در دل خود، از روی تضرع و خوف، آهسته و آرام، صبحگاهان و شامگاهان، یاد کن؛ و از غافلان مباش!'),
(1160, 1160, 'إِنَّ الَّذِينَ عِنْدَ رَبِّكَ لَا يَسْتَكْبِرُونَ عَنْ عِبَادَتِهِ وَيُسَبِّحُونَهُ وَلَهُ يَسْجُدُونَ ۩', 7, 206, 9, 35, 176, 'آنها که (در مقام قرب) نزد پروردگار تو هستند، (هیچ‌گاه) از عبادتش تکبر نمی‌ورزند، و او را تسبیح می‌گویند، و برایش سجده می‌کنند.');
INSERT INTO `Quran` (`rowid`, `id`, `Arabic`, `Chapter`, `Verse`, `Juz`, `Hizb`, `Page`, `tr`) VALUES
(1161, 1161, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ يَسْأَلُونَكَ عَنِ الْأَنْفَالِ ۖ قُلِ الْأَنْفَالُ لِلَّهِ وَالرَّسُولِ ۖ فَاتَّقُوا اللَّهَ وَأَصْلِحُوا ذَاتَ بَيْنِكُمْ ۖ وَأَطِيعُوا اللَّهَ وَرَسُولَهُ إِنْ كُنْتُمْ مُؤْمِنِينَ', 8, 1, 9, 36, 177, 'از تو درباره انفال = غنایم، و هرگونه مال بدون مالک مشخص‌ سؤال می‌کنند؛ بگو: «انفال مخصوص خدا و پیامبر است؛ پس، از (مخالفت فرمان) خدا بپرهیزید! و خصومتهایی را که در میان شماست، آشتی دهید! و خدا و پیامبرش را اطاعت کنید اگر ایمان دارید!'),
(1162, 1162, 'إِنَّمَا الْمُؤْمِنُونَ الَّذِينَ إِذَا ذُكِرَ اللَّهُ وَجِلَتْ قُلُوبُهُمْ وَإِذَا تُلِيَتْ عَلَيْهِمْ آيَاتُهُ زَادَتْهُمْ إِيمَانًا وَعَلَىٰ رَبِّهِمْ يَتَوَكَّلُونَ', 8, 2, 9, 36, 177, 'مؤمنان، تنها کسانی هستند که هرگاه نام خدا برده شود، دلهاشان ترسان میگردد؛ و هنگامی که آیات او بر آنها خوانده می‌شود، ایمانشان فزونتر می‌گردد؛ و تنها بر پروردگارشان توکل دارند.'),
(1163, 1163, 'الَّذِينَ يُقِيمُونَ الصَّلَاةَ وَمِمَّا رَزَقْنَاهُمْ يُنْفِقُونَ', 8, 3, 9, 36, 177, 'آنها که نماز را برپا می‌دارند؛ و از آنچه به آنها روزی داده‌ایم، انفاق می‌کنند.'),
(1164, 1164, 'أُولَٰئِكَ هُمُ الْمُؤْمِنُونَ حَقًّا ۚ لَهُمْ دَرَجَاتٌ عِنْدَ رَبِّهِمْ وَمَغْفِرَةٌ وَرِزْقٌ كَرِيمٌ', 8, 4, 9, 36, 177, '(آری،) مؤمنان حقیقی آنها هستند؛ برای آنان درجاتی (مهم) نزد پروردگارشان است؛ و برای آنها، آمرزش و روزی بی‌نقص و عیب است.'),
(1165, 1165, 'كَمَا أَخْرَجَكَ رَبُّكَ مِنْ بَيْتِكَ بِالْحَقِّ وَإِنَّ فَرِيقًا مِنَ الْمُؤْمِنِينَ لَكَارِهُونَ', 8, 5, 9, 36, 177, 'همان‌گونه که خدا تو را بحق از خانه (به سوی میدان بدر،) بیرون فرستاد، در حالی که گروهی از مؤمنان ناخشنود بودند (؛ ولی سرانجامش پیروزی بود! ناخشنودی عده‌ای از چگونگی تقسیم غنایم بدر نیز چنین است)!'),
(1166, 1166, 'يُجَادِلُونَكَ فِي الْحَقِّ بَعْدَمَا تَبَيَّنَ كَأَنَّمَا يُسَاقُونَ إِلَى الْمَوْتِ وَهُمْ يَنْظُرُونَ', 8, 6, 9, 36, 177, 'آنها پس از روشن شدن حق، باز با تو مجادله می‌کردند؛ (و چنان ترس و وحشت آنها را فراگرفته بود، که) گویی به سوی مرگ رانده می‌شوند، و آن را با چشم خود می‌نگرند!'),
(1167, 1167, 'وَإِذْ يَعِدُكُمُ اللَّهُ إِحْدَى الطَّائِفَتَيْنِ أَنَّهَا لَكُمْ وَتَوَدُّونَ أَنَّ غَيْرَ ذَاتِ الشَّوْكَةِ تَكُونُ لَكُمْ وَيُرِيدُ اللَّهُ أَنْ يُحِقَّ الْحَقَّ بِكَلِمَاتِهِ وَيَقْطَعَ دَابِرَ الْكَافِرِينَ', 8, 7, 9, 36, 177, 'و (به یاد آرید) هنگامی را که خداوند به شما وعده داد که یکی از دو گروه = کاروان تجاری قریش، یا لشکر مسلح آنها نصیب شما خواهد بود؛ و شما دوست می‌داشتید که کاروان (غیر مسلح) برای شما باشد (و بر آن پیروز شوید)؛ ولی خداوند می‌خواهد حق را با کلمات خود تقویت، و ریشه کافران را قطع کند؛ (از این رو شما را بر خلاف میلتان با لشکر قریش درگیر ساخت، و آن پیروزی بزرگ نصیبتان شد.)'),
(1168, 1168, 'لِيُحِقَّ الْحَقَّ وَيُبْطِلَ الْبَاطِلَ وَلَوْ كَرِهَ الْمُجْرِمُونَ', 8, 8, 9, 36, 177, 'تا حق را تثبیت کند، و باطل را از میان بردارد، هر چند مجرمان کراهت داشته باشند.'),
(1169, 1169, 'إِذْ تَسْتَغِيثُونَ رَبَّكُمْ فَاسْتَجَابَ لَكُمْ أَنِّي مُمِدُّكُمْ بِأَلْفٍ مِنَ الْمَلَائِكَةِ مُرْدِفِينَ', 8, 9, 9, 36, 178, '(به خاطر بیاورید) زمانی را (که از شدت ناراحتی در میدان بدر،) از پروردگارتان کمک می‌خواستید؛ و او خواسته شما را پذیرفت (و گفت): من شما را با یکهزار از فرشتگان، که پشت سر هم فرود می‌آیند، یاری می‌کنم.'),
(1170, 1170, 'وَمَا جَعَلَهُ اللَّهُ إِلَّا بُشْرَىٰ وَلِتَطْمَئِنَّ بِهِ قُلُوبُكُمْ ۚ وَمَا النَّصْرُ إِلَّا مِنْ عِنْدِ اللَّهِ ۚ إِنَّ اللَّهَ عَزِيزٌ حَكِيمٌ', 8, 10, 9, 36, 178, 'ولی خداوند، این را تنها برای شادی و اطمینان قلب شما قرار داد؛ وگرنه، پیروزی جز از طرف خدا نیست؛ خداوند توانا و حکیم است!'),
(1171, 1171, 'إِذْ يُغَشِّيكُمُ النُّعَاسَ أَمَنَةً مِنْهُ وَيُنَزِّلُ عَلَيْكُمْ مِنَ السَّمَاءِ مَاءً لِيُطَهِّرَكُمْ بِهِ وَيُذْهِبَ عَنْكُمْ رِجْزَ الشَّيْطَانِ وَلِيَرْبِطَ عَلَىٰ قُلُوبِكُمْ وَيُثَبِّتَ بِهِ الْأَقْدَامَ', 8, 11, 9, 36, 178, 'و (یاد آورید) هنگامی را که خواب سبکی که مایه آرامش از سوی خدا بود، شما را فراگرفت؛ و آبی از آسمان برایتان فرستاد، تا شما را با آن پاک کند؛ و پلیدی شیطان را از شما دور سازد؛ و دلهایتان را محکم، و گامها را با آن استوار دارد!'),
(1172, 1172, 'إِذْ يُوحِي رَبُّكَ إِلَى الْمَلَائِكَةِ أَنِّي مَعَكُمْ فَثَبِّتُوا الَّذِينَ آمَنُوا ۚ سَأُلْقِي فِي قُلُوبِ الَّذِينَ كَفَرُوا الرُّعْبَ فَاضْرِبُوا فَوْقَ الْأَعْنَاقِ وَاضْرِبُوا مِنْهُمْ كُلَّ بَنَانٍ', 8, 12, 9, 36, 178, 'و (به یاد آر) موقعی را که پروردگارت به فرشتگان وحی کرد: «من با شما هستم؛ کسانی را که ایمان آورده‌اند، ثابت قدم دارید! بزودی در دلهای کافران ترس و وحشت می‌افکنم؛ ضربه‌ها را بر بالاتر از گردن (بر سرهای دشمنان) فرود آرید! و همه انگشتانشان را قطع کنید!'),
(1173, 1173, 'ذَٰلِكَ بِأَنَّهُمْ شَاقُّوا اللَّهَ وَرَسُولَهُ ۚ وَمَنْ يُشَاقِقِ اللَّهَ وَرَسُولَهُ فَإِنَّ اللَّهَ شَدِيدُ الْعِقَابِ', 8, 13, 9, 36, 178, 'این بخاطر آن است که آنها با خدا و پیامبرش (ص) دشمنی ورزیدند؛ و هر کس با خدا و پیامبرش دشمنی کند، (کیفر شدیدی می‌بیند؛) و خداوند شدید العقاب است!'),
(1174, 1174, 'ذَٰلِكُمْ فَذُوقُوهُ وَأَنَّ لِلْكَافِرِينَ عَذَابَ النَّارِ', 8, 14, 9, 36, 178, 'این (مجازات دنیا) را بچشید! و برای کافران، مجازات آتش (در جهان دیگر) خواهد بود!'),
(1175, 1175, 'يَا أَيُّهَا الَّذِينَ آمَنُوا إِذَا لَقِيتُمُ الَّذِينَ كَفَرُوا زَحْفًا فَلَا تُوَلُّوهُمُ الْأَدْبَارَ', 8, 15, 9, 36, 178, 'ای کسانی که ایمان آورده‌اید! هنگامی که با انبوه کافران در میدان نبرد روبه‌رو شوید، به آنها پشت نکنید (و فرار ننمایید)!'),
(1176, 1176, 'وَمَنْ يُوَلِّهِمْ يَوْمَئِذٍ دُبُرَهُ إِلَّا مُتَحَرِّفًا لِقِتَالٍ أَوْ مُتَحَيِّزًا إِلَىٰ فِئَةٍ فَقَدْ بَاءَ بِغَضَبٍ مِنَ اللَّهِ وَمَأْوَاهُ جَهَنَّمُ ۖ وَبِئْسَ الْمَصِيرُ', 8, 16, 9, 36, 178, 'و هر کس در آن هنگام به آنها پشت کند -مگر آنکه هدفش کناره‌گیری از میدان برای حمله مجدد، و یا به قصد پیوستن به گروهی (از مجاهدان) بوده باشد- (چنین کسی) به غضب خدا گرفتار خواهد شد؛ و جایگاه او جهنم، و چه بد جایگاهی است!'),
(1177, 1177, 'فَلَمْ تَقْتُلُوهُمْ وَلَٰكِنَّ اللَّهَ قَتَلَهُمْ ۚ وَمَا رَمَيْتَ إِذْ رَمَيْتَ وَلَٰكِنَّ اللَّهَ رَمَىٰ ۚ وَلِيُبْلِيَ الْمُؤْمِنِينَ مِنْهُ بَلَاءً حَسَنًا ۚ إِنَّ اللَّهَ سَمِيعٌ عَلِيمٌ', 8, 17, 9, 36, 179, 'این شما نبودید که آنها را کشتید؛ بلکه خداوند آنها را کشت! و این تو نبودی (ای پیامبر که خاک و سنگ به صورت آنها) انداختی؛ بلکه خدا انداخت! و خدا می‌خواست مؤمنان را به این وسیله امتحان خوبی کند؛ خداوند شنوا و داناست.'),
(1178, 1178, 'ذَٰلِكُمْ وَأَنَّ اللَّهَ مُوهِنُ كَيْدِ الْكَافِرِينَ', 8, 18, 9, 36, 179, 'سرنوشت مؤمنان و کافران، همان بود که دیدید! و خداوند سست‌کننده نقشه‌های کافران است.'),
(1179, 1179, 'إِنْ تَسْتَفْتِحُوا فَقَدْ جَاءَكُمُ الْفَتْحُ ۖ وَإِنْ تَنْتَهُوا فَهُوَ خَيْرٌ لَكُمْ ۖ وَإِنْ تَعُودُوا نَعُدْ وَلَنْ تُغْنِيَ عَنْكُمْ فِئَتُكُمْ شَيْئًا وَلَوْ كَثُرَتْ وَأَنَّ اللَّهَ مَعَ الْمُؤْمِنِينَ', 8, 19, 9, 36, 179, 'اگر شما فتح و پیروزی می‌خواهید، پیروزی به سراغ شما آمد! و اگر (از مخالفت) خودداری کنید، برای شما بهتر است! و اگر بازگردید، ما هم باز خواهیم گشت؛ و جمعیت شما هر چند زیاد باشد، شما را (از یاری خدا) بی‌نیاز نخواهد کرد؛ و خداوند با مؤمنان است!'),
(1180, 1180, 'يَا أَيُّهَا الَّذِينَ آمَنُوا أَطِيعُوا اللَّهَ وَرَسُولَهُ وَلَا تَوَلَّوْا عَنْهُ وَأَنْتُمْ تَسْمَعُونَ', 8, 20, 9, 36, 179, 'ای کسانی که ایمان آورده‌اید! خدا و پیامبرش را اطاعت کنید؛ و سرپیچی ننمایید در حالی که (سخنان او را) می‌شنوید!'),
(1181, 1181, 'وَلَا تَكُونُوا كَالَّذِينَ قَالُوا سَمِعْنَا وَهُمْ لَا يَسْمَعُونَ', 8, 21, 9, 36, 179, 'و همانند کسانی نباشید که می‌گفتند: «شنیدیم!» ولی در حقیقت نمی‌شنیدند!'),
(1182, 1182, ' إِنَّ شَرَّ الدَّوَابِّ عِنْدَ اللَّهِ الصُّمُّ الْبُكْمُ الَّذِينَ لَا يَعْقِلُونَ', 8, 22, 9, 36, 179, 'بدترین جنبندگان نزد خدا، افراد کر و لالی هستند که اندیشه نمی‌کنند.'),
(1183, 1183, 'وَلَوْ عَلِمَ اللَّهُ فِيهِمْ خَيْرًا لَأَسْمَعَهُمْ ۖ وَلَوْ أَسْمَعَهُمْ لَتَوَلَّوْا وَهُمْ مُعْرِضُونَ', 8, 23, 9, 36, 179, 'و اگر خداوند خیری در آنها می‌دانست، (حرف حق را) به گوش آنها می‌رساند؛ ولی (با این حال که دارند،) اگر حق را به گوش آنها برساند، سرپیچی کرده و روگردان می‌شوند.'),
(1184, 1184, 'يَا أَيُّهَا الَّذِينَ آمَنُوا اسْتَجِيبُوا لِلَّهِ وَلِلرَّسُولِ إِذَا دَعَاكُمْ لِمَا يُحْيِيكُمْ ۖ وَاعْلَمُوا أَنَّ اللَّهَ يَحُولُ بَيْنَ الْمَرْءِ وَقَلْبِهِ وَأَنَّهُ إِلَيْهِ تُحْشَرُونَ', 8, 24, 9, 36, 179, 'ای کسانی که ایمان آورده‌اید! دعوت خدا و پیامبر را اجابت کنید هنگامی که شما را به سوی چیزی می‌خواند که شما را حیات می‌بخشد! و بدانید خداوند میان انسان و قلب او حایل می‌شود، و همه شما (در قیامت) نزد او گردآوری می‌شوید!'),
(1185, 1185, 'وَاتَّقُوا فِتْنَةً لَا تُصِيبَنَّ الَّذِينَ ظَلَمُوا مِنْكُمْ خَاصَّةً ۖ وَاعْلَمُوا أَنَّ اللَّهَ شَدِيدُ الْعِقَابِ', 8, 25, 9, 36, 179, 'و از فتنه‌ای بپرهیزید که تنها به ستمکاران شما نمی‌رسد؛ (بلکه همه را فرا خواهد گرفت؛ چرا که دیگران سکوت اختیار کردند.) و بدانید خداوند کیفر شدید دارد!'),
(1186, 1186, 'وَاذْكُرُوا إِذْ أَنْتُمْ قَلِيلٌ مُسْتَضْعَفُونَ فِي الْأَرْضِ تَخَافُونَ أَنْ يَتَخَطَّفَكُمُ النَّاسُ فَآوَاكُمْ وَأَيَّدَكُمْ بِنَصْرِهِ وَرَزَقَكُمْ مِنَ الطَّيِّبَاتِ لَعَلَّكُمْ تَشْكُرُونَ', 8, 26, 9, 36, 180, 'و به خاطر بیاورید هنگامی را که شما در روی زمین، گروهی کوچک و اندک و زبون بودید؛ آنچنان که می‌ترسیدید مردم شما را بربایند! ولی او شما را پناه داد؛ و با یاری خود تقویت کرد؛ و از روزیهای پاکیزه بهره‌مند ساخت؛ شاید شکر نعمتش را بجا آورید!'),
(1187, 1187, 'يَا أَيُّهَا الَّذِينَ آمَنُوا لَا تَخُونُوا اللَّهَ وَالرَّسُولَ وَتَخُونُوا أَمَانَاتِكُمْ وَأَنْتُمْ تَعْلَمُونَ', 8, 27, 9, 36, 180, 'ای کسانی که ایمان آورده‌اید! به خدا و پیامبر خیانت نکنید! و (نیز) در امانات خود خیانت روا مدارید، در حالی که میدانید (این کار، گناه بزرگی است)!'),
(1188, 1188, 'وَاعْلَمُوا أَنَّمَا أَمْوَالُكُمْ وَأَوْلَادُكُمْ فِتْنَةٌ وَأَنَّ اللَّهَ عِنْدَهُ أَجْرٌ عَظِيمٌ', 8, 28, 9, 36, 180, 'و بدانید اموال و اولاد شما، وسیله آزمایش است؛ و (برای کسانی که از عهده امتحان برآیند،) پاداش عظیمی نزد خداست!'),
(1189, 1189, 'يَا أَيُّهَا الَّذِينَ آمَنُوا إِنْ تَتَّقُوا اللَّهَ يَجْعَلْ لَكُمْ فُرْقَانًا وَيُكَفِّرْ عَنْكُمْ سَيِّئَاتِكُمْ وَيَغْفِرْ لَكُمْ ۗ وَاللَّهُ ذُو الْفَضْلِ الْعَظِيمِ', 8, 29, 9, 36, 180, 'ای کسانی که ایمان آورده‌اید! اگر از (مخالفت فرمان) خدا بپرهیزید، برای شما وسیله‌ای جهت جدا ساختن حق از باطل قرارمی‌دهد؛ (روشن‌بینی خاصّی که در پرتو آن، حق را از باطل خواهید شناخت؛) و گناهانتان را می‌پوشاند؛ و شما را می‌زمرزد؛ و خداوند صاحب فضل و بخشش عظیم است!'),
(1190, 1190, 'وَإِذْ يَمْكُرُ بِكَ الَّذِينَ كَفَرُوا لِيُثْبِتُوكَ أَوْ يَقْتُلُوكَ أَوْ يُخْرِجُوكَ ۚ وَيَمْكُرُونَ وَيَمْكُرُ اللَّهُ ۖ وَاللَّهُ خَيْرُ الْمَاكِرِينَ', 8, 30, 9, 36, 180, '(به خاطر بیاور) هنگامی را که کافران نقشه می‌کشیدند که تو را به زندان بیفکنند، یا به قتل برسانند، و یا (از مکّه) خارج سازند؛ آنها چاره می‌اندیشیدند (و نقشه می‌کشیدند)، و خداوند هم تدبیر می‌کرد؛ و خدا بهترین چاره جویان و تدبیرکنندگان است!'),
(1191, 1191, 'وَإِذَا تُتْلَىٰ عَلَيْهِمْ آيَاتُنَا قَالُوا قَدْ سَمِعْنَا لَوْ نَشَاءُ لَقُلْنَا مِثْلَ هَٰذَا ۙ إِنْ هَٰذَا إِلَّا أَسَاطِيرُ الْأَوَّلِينَ', 8, 31, 9, 36, 180, 'و هنگامی که آیات ما بر آنها خوانده می‌شود، می‌گویند: «شنیدیم؛ (چیز مهمّی نیست؛) ما هم اگر بخواهیم مثل آن را می‌گوییم؛ اینها همان افسانه‌های پیشینیان است!» (ولی دروغ می‌گویند، و هرگز مثل آن را نمی‌آورند.)'),
(1192, 1192, 'وَإِذْ قَالُوا اللَّهُمَّ إِنْ كَانَ هَٰذَا هُوَ الْحَقَّ مِنْ عِنْدِكَ فَأَمْطِرْ عَلَيْنَا حِجَارَةً مِنَ السَّمَاءِ أَوِ ائْتِنَا بِعَذَابٍ أَلِيمٍ', 8, 32, 9, 36, 180, 'و (به خاطر بیاور) زمانی را که گفتند: «پروردگارا! اگر این حق است و از طرف توست، بارانی از سنگ از آسمان بر ما فرود آر! یا عذاب دردناکی برای ما بفرست!»'),
(1193, 1193, 'وَمَا كَانَ اللَّهُ لِيُعَذِّبَهُمْ وَأَنْتَ فِيهِمْ ۚ وَمَا كَانَ اللَّهُ مُعَذِّبَهُمْ وَهُمْ يَسْتَغْفِرُونَ', 8, 33, 9, 36, 180, 'ولی (ای پیامبر!) تا تو در میان آنها هستی، خداوند آنها را مجازات نخواهد کرد؛ و (نیز) تا استغفار می‌کنند، خدا عذابشان نمی‌کند.'),
(1194, 1194, 'وَمَا لَهُمْ أَلَّا يُعَذِّبَهُمُ اللَّهُ وَهُمْ يَصُدُّونَ عَنِ الْمَسْجِدِ الْحَرَامِ وَمَا كَانُوا أَوْلِيَاءَهُ ۚ إِنْ أَوْلِيَاؤُهُ إِلَّا الْمُتَّقُونَ وَلَٰكِنَّ أَكْثَرَهُمْ لَا يَعْلَمُونَ', 8, 34, 9, 36, 181, 'چرا خدا آنها را مجازات نکند، با اینکه از (عبادت موحدّان در کنار) مسجد الحرام جلوگیری می‌کنند در حالی که سرپرست آن نیستند؟! سرپرست آن، فقط پرهیزگارانند؛ ولی بیشتر آنها نمی‌دانند.'),
(1195, 1195, 'وَمَا كَانَ صَلَاتُهُمْ عِنْدَ الْبَيْتِ إِلَّا مُكَاءً وَتَصْدِيَةً ۚ فَذُوقُوا الْعَذَابَ بِمَا كُنْتُمْ تَكْفُرُونَ', 8, 35, 9, 36, 181, '(آنها که مدّعی هستند ما هم نماز داریم،) نمازشان نزد خانه (خدا)، چیزی جز «سوت کشیدن» «کف زدن» نبود؛ پس بچشید عذاب (الهی) را بخاطر کفرتان!'),
(1196, 1196, 'إِنَّ الَّذِينَ كَفَرُوا يُنْفِقُونَ أَمْوَالَهُمْ لِيَصُدُّوا عَنْ سَبِيلِ اللَّهِ ۚ فَسَيُنْفِقُونَهَا ثُمَّ تَكُونُ عَلَيْهِمْ حَسْرَةً ثُمَّ يُغْلَبُونَ ۗ وَالَّذِينَ كَفَرُوا إِلَىٰ جَهَنَّمَ يُحْشَرُونَ', 8, 36, 9, 36, 181, 'آنها که کافر شدند، اموالشان را برای بازداشتن (مردم) از راه خدا خرج می‌کنند؛ آنان این اموال را (که برای به دست آوردنش زحمت کشیده‌اند، در این راه) مصرف می‌کنند، امّا مایه حسرت و اندوهشان خواهد شد؛ و سپس شکست خواهند خورد؛ و (در جهان دیگر) کافران همگی به سوی دوزخ گردآوری خواهند شد.'),
(1197, 1197, 'لِيَمِيزَ اللَّهُ الْخَبِيثَ مِنَ الطَّيِّبِ وَيَجْعَلَ الْخَبِيثَ بَعْضَهُ عَلَىٰ بَعْضٍ فَيَرْكُمَهُ جَمِيعًا فَيَجْعَلَهُ فِي جَهَنَّمَ ۚ أُولَٰئِكَ هُمُ الْخَاسِرُونَ', 8, 37, 9, 36, 181, '(اینها همه) بخاطر آن است که خداوند (می‌خواهد) ناپاک را از پاک جدا سازد، و ناپاکها را روی هم بگذارد، و همه را متراکم سازد، و یکجا در دوزخ قرار دهد؛ و اینها هستند زیانکاران!'),
(1198, 1198, 'قُلْ لِلَّذِينَ كَفَرُوا إِنْ يَنْتَهُوا يُغْفَرْ لَهُمْ مَا قَدْ سَلَفَ وَإِنْ يَعُودُوا فَقَدْ مَضَتْ سُنَّتُ الْأَوَّلِينَ', 8, 38, 9, 36, 181, 'به آنها که کافر شدند بگو: «چنانچه از مخالفت باز ایستند، (و ایمان آورند،) گذشته آنها بخشوده خواهد شد؛ و اگر به اعمال سابق بازگردند، سنّت خداوند در گذشتگان، درباره آنها جاری می‌شود (؛ و حکم نابودی آنان صادر می‌گردد).'),
(1199, 1199, 'وَقَاتِلُوهُمْ حَتَّىٰ لَا تَكُونَ فِتْنَةٌ وَيَكُونَ الدِّينُ كُلُّهُ لِلَّهِ ۚ فَإِنِ انْتَهَوْا فَإِنَّ اللَّهَ بِمَا يَعْمَلُونَ بَصِيرٌ', 8, 39, 9, 36, 181, 'و با آنها پیکار کنید، تا فتنه = شرک و سلب آزادی‌ برچیده شود، و دین (و پرستش) همه مخصوص خدا باشد! و اگر آنها (از راه شرک و فساد بازگردند، و از اعمال نادرست) خودداری کنند، (خداوند آنها را می‌پذیرد؛) خدا به آنچه انجام می‌دهند بیناست.'),
(1200, 1200, 'وَإِنْ تَوَلَّوْا فَاعْلَمُوا أَنَّ اللَّهَ مَوْلَاكُمْ ۚ نِعْمَ الْمَوْلَىٰ وَنِعْمَ النَّصِيرُ', 8, 40, 9, 36, 181, 'و اگر سرپیچی کنند، بدانید (ضرری به شما نمی‌رسانند؛) خداوند سرپرست شماست! چه سرپرست خوبی! و چه یاور خوبی!'),
(1201, 1201, ' وَاعْلَمُوا أَنَّمَا غَنِمْتُمْ مِنْ شَيْءٍ فَأَنَّ لِلَّهِ خُمُسَهُ وَلِلرَّسُولِ وَلِذِي الْقُرْبَىٰ وَالْيَتَامَىٰ وَالْمَسَاكِينِ وَابْنِ السَّبِيلِ إِنْ كُنْتُمْ آمَنْتُمْ بِاللَّهِ وَمَا أَنْزَلْنَا عَلَىٰ عَبْدِنَا يَوْمَ الْفُرْقَانِ يَوْمَ الْتَقَى الْجَمْعَانِ ۗ وَاللَّهُ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ', 8, 41, 10, 37, 182, 'بدانید هرگونه غنیمتی به دست آورید، خمس آن برای خدا، و برای پیامبر، و برای ذی‌القربی و یتیمان و مسکینان و واماندگان در راه (از آنها) است، اگر به خدا و آنچه بر بنده خود در روز جدایی حق از باطل، روز درگیری دو گروه (باایمان و بی‌ایمان) = روز جنگ بدر نازل کردیم، ایمان آورده‌اید؛ و خداوند بر هر چیزی تواناست!'),
(1202, 1202, 'إِذْ أَنْتُمْ بِالْعُدْوَةِ الدُّنْيَا وَهُمْ بِالْعُدْوَةِ الْقُصْوَىٰ وَالرَّكْبُ أَسْفَلَ مِنْكُمْ ۚ وَلَوْ تَوَاعَدْتُمْ لَاخْتَلَفْتُمْ فِي الْمِيعَادِ ۙ وَلَٰكِنْ لِيَقْضِيَ اللَّهُ أَمْرًا كَانَ مَفْعُولًا لِيَهْلِكَ مَنْ هَلَكَ عَنْ بَيِّنَةٍ وَيَحْيَىٰ مَنْ حَيَّ عَنْ بَيِّنَةٍ ۗ وَإِنَّ اللَّهَ لَسَمِيعٌ عَلِيمٌ', 8, 42, 10, 37, 182, 'در آن هنگام که شما در طرف پایین بودید، و آنها در طرف بالأ (و دشمن بر شما برتری داشت؛) و کاروان (قریش)، پایین تر از شما بود؛ (و وضع چنان سخت بود که) اگر با یکدیگر وعده می‌گذاشتید (که در میدان نبرد حاضر شوید)، در انجام وعده خود اختلاف می‌کردید؛ ولی (همه اینها) برای آن بود که خداوند، کاری را که می‌بایست انجام شود، تحقّق بخشد؛ تا آنها که هلاک (و گمراه) می‌شوند، از روی اتمام حجّت باشد؛ و آنها که زنده می‌شوند (و هدایت می‌یابند)، از روی دلیل روشن باشد؛ و خداوند شنوا و داناست.'),
(1203, 1203, 'إِذْ يُرِيكَهُمُ اللَّهُ فِي مَنَامِكَ قَلِيلًا ۖ وَلَوْ أَرَاكَهُمْ كَثِيرًا لَفَشِلْتُمْ وَلَتَنَازَعْتُمْ فِي الْأَمْرِ وَلَٰكِنَّ اللَّهَ سَلَّمَ ۗ إِنَّهُ عَلِيمٌ بِذَاتِ الصُّدُورِ', 8, 43, 10, 37, 182, 'در آن هنگام که خداوند تعداد آنها را در خواب به تو کم نشان داد؛ و اگر فراوان نشان می‌داد، مسلّماً سست می‌شدید؛ و (درباره شروع جنگ با آنها) کارتان به اختلاف می‌کشید؛ ولی خداوند (شما را از شرّ اینها) سالم نگه داشت؛ خداوند به آنچه درون سینه‌هاست، داناست.'),
(1204, 1204, 'وَإِذْ يُرِيكُمُوهُمْ إِذِ الْتَقَيْتُمْ فِي أَعْيُنِكُمْ قَلِيلًا وَيُقَلِّلُكُمْ فِي أَعْيُنِهِمْ لِيَقْضِيَ اللَّهُ أَمْرًا كَانَ مَفْعُولًا ۗ وَإِلَى اللَّهِ تُرْجَعُ الْأُمُورُ', 8, 44, 10, 37, 182, 'و در آن هنگام (که در میدان نبرد،) با هم روبه‌رو شدید، آنها را به چشم شما کم نشان می‌داد؛ و شما را (نیز) به چشم آنها کم می‌نمود؛ تا خداوند، کاری را که می‌بایست انجام گیرد، صورت بخشد؛ (شما نترسید و اقدام به جنگ کنید، آنها هم وحشت نکنند و حاضر به جنگ شوند، و سرانجام شکست بخورند!) و همه کارها به خداوند باز می‌گردد.'),
(1205, 1205, 'يَا أَيُّهَا الَّذِينَ آمَنُوا إِذَا لَقِيتُمْ فِئَةً فَاثْبُتُوا وَاذْكُرُوا اللَّهَ كَثِيرًا لَعَلَّكُمْ تُفْلِحُونَ', 8, 45, 10, 37, 182, 'ای کسانی که ایمان آورده‌اید! هنگامی که (در میدان نبرد) با گروهی رو به رو می‌شوید، ثابت قدم باشید! و خدا را فراوان یاد کنید، تا رستگار شوید!'),
(1206, 1206, 'وَأَطِيعُوا اللَّهَ وَرَسُولَهُ وَلَا تَنَازَعُوا فَتَفْشَلُوا وَتَذْهَبَ رِيحُكُمْ ۖ وَاصْبِرُوا ۚ إِنَّ اللَّهَ مَعَ الصَّابِرِينَ', 8, 46, 10, 37, 183, 'و (فرمان) خدا و پیامبرش را اطاعت نمایید! و نزاع (و کشمکش) نکنید، تا سست نشوید، و قدرت (و شوکت) شما از میان نرود! و صبر و استقامت کنید که خداوند با استقامت کنندگان است!'),
(1207, 1207, 'وَلَا تَكُونُوا كَالَّذِينَ خَرَجُوا مِنْ دِيَارِهِمْ بَطَرًا وَرِئَاءَ النَّاسِ وَيَصُدُّونَ عَنْ سَبِيلِ اللَّهِ ۚ وَاللَّهُ بِمَا يَعْمَلُونَ مُحِيطٌ', 8, 47, 10, 37, 183, 'و مانند کسانی نباشید که از روی هوی پرستی و غرور و خودنمایی در برابر مردم، از سرزمین خود به (سوی میدان بدر) بیرون آمدند؛ و (مردم را) از راه خدا بازمی‌داشتند؛ (و سرانجام شکست خوردند) و خداوند به آنچه عمل می‌کنند، احاطه (و آگاهی) دارد!'),
(1208, 1208, 'وَإِذْ زَيَّنَ لَهُمُ الشَّيْطَانُ أَعْمَالَهُمْ وَقَالَ لَا غَالِبَ لَكُمُ الْيَوْمَ مِنَ النَّاسِ وَإِنِّي جَارٌ لَكُمْ ۖ فَلَمَّا تَرَاءَتِ الْفِئَتَانِ نَكَصَ عَلَىٰ عَقِبَيْهِ وَقَالَ إِنِّي بَرِيءٌ مِنْكُمْ إِنِّي أَرَىٰ مَا لَا تَرَوْنَ إِنِّي أَخَافُ اللَّهَ ۚ وَاللَّهُ شَدِيدُ الْعِقَابِ', 8, 48, 10, 37, 183, 'و (به یاد آور) هنگامی را که شیطان، اعمال آنها = مشرکان‌ را در نظرشان جلوه داد، و گفت: «امروز هیچ کس از مردم بر شما پیروز نمی‌گردد! و من، همسایه (و پناه‌دهنده) شما هستم!» امّا هنگامی که دو گروه (کافران، و مؤمنان مورد حمایت فرشتگان) در برابر یکدیگر قرار گرفتند، به عقب برگشت و گفت: «من از شما (دوستان و پیروانم) بیزارم! من چیزی می‌بینم که شما نمی‌بینید؛ من از خدا می‌ترسم، خداوند شدیدالعقاب است!»'),
(1209, 1209, 'إِذْ يَقُولُ الْمُنَافِقُونَ وَالَّذِينَ فِي قُلُوبِهِمْ مَرَضٌ غَرَّ هَٰؤُلَاءِ دِينُهُمْ ۗ وَمَنْ يَتَوَكَّلْ عَلَى اللَّهِ فَإِنَّ اللَّهَ عَزِيزٌ حَكِيمٌ', 8, 49, 10, 37, 183, 'و هنگامی را که منافقان، و آنها که در دلهایشان بیماری است می‌گفتند: «این گروه (مسلمانان) را دینشان مغرور ساخته است.» (آنها نمی‌دانستند که) هر کس بر خدا توکّل کند، (پیروز می‌گردد؛) خداوند قدرتمند و حکیم است!'),
(1210, 1210, 'وَلَوْ تَرَىٰ إِذْ يَتَوَفَّى الَّذِينَ كَفَرُوا ۙ الْمَلَائِكَةُ يَضْرِبُونَ وُجُوهَهُمْ وَأَدْبَارَهُمْ وَذُوقُوا عَذَابَ الْحَرِيقِ', 8, 50, 10, 37, 183, 'و اگر ببینی کافران را هنگامی که فرشتگان (مرگ)، جانشان را می‌گیرند و بر صورت و پشت آنها می‌زنند و (می‌گویند:) بچشید عذاب سوزنده را (، به حال آنان تأسف خواهی خورد)!'),
(1211, 1211, 'ذَٰلِكَ بِمَا قَدَّمَتْ أَيْدِيكُمْ وَأَنَّ اللَّهَ لَيْسَ بِظَلَّامٍ لِلْعَبِيدِ', 8, 51, 10, 37, 183, 'این، در مقابل کارهایی است که از پیش فرستاده‌اید؛ و خداوند نسبت به بندگانش، هرگز ستم روا نمی‌دارد!'),
(1212, 1212, 'كَدَأْبِ آلِ فِرْعَوْنَ ۙ وَالَّذِينَ مِنْ قَبْلِهِمْ ۚ كَفَرُوا بِآيَاتِ اللَّهِ فَأَخَذَهُمُ اللَّهُ بِذُنُوبِهِمْ ۗ إِنَّ اللَّهَ قَوِيٌّ شَدِيدُ الْعِقَابِ', 8, 52, 10, 37, 183, '(حال این گروه مشرکان،) همانند حال نزدیکان فرعون، و کسانی است که پیش از آنان بودند؛ آنها آیات خدا را انکار کردند؛ خداوند هم آنان را به گناهانشان کیفر داد؛ خداوند قویّ، و کیفرش شدید است‌'),
(1213, 1213, 'ذَٰلِكَ بِأَنَّ اللَّهَ لَمْ يَكُ مُغَيِّرًا نِعْمَةً أَنْعَمَهَا عَلَىٰ قَوْمٍ حَتَّىٰ يُغَيِّرُوا مَا بِأَنْفُسِهِمْ ۙ وَأَنَّ اللَّهَ سَمِيعٌ عَلِيمٌ', 8, 53, 10, 37, 184, 'این، بخاطر آن است که خداوند، هیچ نعمتی را که به گروهی داده، تغییر نمی‌دهد؛ جز آنکه آنها خودشان را تغییر دهند؛ و خداوند، شنوا و داناست!'),
(1214, 1214, 'كَدَأْبِ آلِ فِرْعَوْنَ ۙ وَالَّذِينَ مِنْ قَبْلِهِمْ ۚ كَذَّبُوا بِآيَاتِ رَبِّهِمْ فَأَهْلَكْنَاهُمْ بِذُنُوبِهِمْ وَأَغْرَقْنَا آلَ فِرْعَوْنَ ۚ وَكُلٌّ كَانُوا ظَالِمِينَ', 8, 54, 10, 37, 184, 'این، (درست) شبیه (حال) فرعونیان و کسانی است که پیش از آنها بودند؛ آیات پروردگارشان را تکذیب کردند؛ ما هم بخاطر گناهانشان، آنها را هلاک کردیم، و فرعونیان را غرق نمودیم؛ و همه آنها ظالم (و ستمگر) بودند!'),
(1215, 1215, 'إِنَّ شَرَّ الدَّوَابِّ عِنْدَ اللَّهِ الَّذِينَ كَفَرُوا فَهُمْ لَا يُؤْمِنُونَ', 8, 55, 10, 37, 184, 'به یقین، بدترین جنبندگان نزد خدا، کسانی هستند که کافر شدند و ایمان نمی‌آورند.'),
(1216, 1216, 'الَّذِينَ عَاهَدْتَ مِنْهُمْ ثُمَّ يَنْقُضُونَ عَهْدَهُمْ فِي كُلِّ مَرَّةٍ وَهُمْ لَا يَتَّقُونَ', 8, 56, 10, 37, 184, 'همان کسانی که با آنها پیمان بستی؛ سپس هر بار عهد و پیمان خود را می‌شکنند؛ و (از پیمان شکنی و خیانت،) پرهیز ندارند.'),
(1217, 1217, 'فَإِمَّا تَثْقَفَنَّهُمْ فِي الْحَرْبِ فَشَرِّدْ بِهِمْ مَنْ خَلْفَهُمْ لَعَلَّهُمْ يَذَّكَّرُونَ', 8, 57, 10, 37, 184, 'اگر آنها را در (میدان) جنگ بیابی، آنچنان به آنها حمله کن که جمعیّتهایی که پشت سر آنها هستند، پراکنده شوند؛ شاید متذکّر گردند (و عبرت گیرند)!'),
(1218, 1218, 'وَإِمَّا تَخَافَنَّ مِنْ قَوْمٍ خِيَانَةً فَانْبِذْ إِلَيْهِمْ عَلَىٰ سَوَاءٍ ۚ إِنَّ اللَّهَ لَا يُحِبُّ الْخَائِنِينَ', 8, 58, 10, 37, 184, 'و هرگاه (با ظهور نشانه‌هایی،) از خیانت گروهی بیم داشته باشی (که عهد خود را شکسته، حمله غافلگیرانه کنند)، بطور عادلانه به آنها اعلام کن که پیمانشان لغو شده است؛ زیرا خداوند، خائنان را دوست نمی‌دارد!'),
(1219, 1219, 'وَلَا يَحْسَبَنَّ الَّذِينَ كَفَرُوا سَبَقُوا ۚ إِنَّهُمْ لَا يُعْجِزُونَ', 8, 59, 10, 37, 184, 'آنها که راه کفر پیش گرفتند، گمان نکنند (با این اعمال،) پیش برده‌اند (و از قلمرو کیفر ما، بیرون رفته‌اند)! آنها هرگز ما را ناتوان نخواهند کرد!'),
(1220, 1220, 'وَأَعِدُّوا لَهُمْ مَا اسْتَطَعْتُمْ مِنْ قُوَّةٍ وَمِنْ رِبَاطِ الْخَيْلِ تُرْهِبُونَ بِهِ عَدُوَّ اللَّهِ وَعَدُوَّكُمْ وَآخَرِينَ مِنْ دُونِهِمْ لَا تَعْلَمُونَهُمُ اللَّهُ يَعْلَمُهُمْ ۚ وَمَا تُنْفِقُوا مِنْ شَيْءٍ فِي سَبِيلِ اللَّهِ يُوَفَّ إِلَيْكُمْ وَأَنْتُمْ لَا تُظْلَمُونَ', 8, 60, 10, 37, 184, 'هر نیرویی در قدرت دارید، برای مقابله با آنها = دشمنان‌، آماده سازید! و (همچنین) اسبهای ورزیده (برای میدان نبرد)، تا به وسیله آن، دشمن خدا و دشمن خویش را بترسانید! و (همچنین) گروه دیگری غیر از اینها را، که شما نمی‌شناسید و خدا آنها را می‌شناسد! و هر چه در راه خدا (و تقویت بنیه دفاعی اسلام) انفاق کنید، بطور کامل به شما بازگردانده می‌شود، و به شما ستم نخواهد شد!'),
(1221, 1221, ' وَإِنْ جَنَحُوا لِلسَّلْمِ فَاجْنَحْ لَهَا وَتَوَكَّلْ عَلَى اللَّهِ ۚ إِنَّهُ هُوَ السَّمِيعُ الْعَلِيمُ', 8, 61, 10, 37, 184, 'و اگر تمایل به صلح نشان دهند، تو نیز از در صلح درآی؛ و بر خدا توکّل کن، که او شنوا و داناست!'),
(1222, 1222, 'وَإِنْ يُرِيدُوا أَنْ يَخْدَعُوكَ فَإِنَّ حَسْبَكَ اللَّهُ ۚ هُوَ الَّذِي أَيَّدَكَ بِنَصْرِهِ وَبِالْمُؤْمِنِينَ', 8, 62, 10, 37, 185, 'و اگر بخواهند تو را فریب دهند، خدا برای تو کافی است؛ او همان کسی است که تو را، با یاری خود و مؤمنان، تقویت کرد...'),
(1223, 1223, 'وَأَلَّفَ بَيْنَ قُلُوبِهِمْ ۚ لَوْ أَنْفَقْتَ مَا فِي الْأَرْضِ جَمِيعًا مَا أَلَّفْتَ بَيْنَ قُلُوبِهِمْ وَلَٰكِنَّ اللَّهَ أَلَّفَ بَيْنَهُمْ ۚ إِنَّهُ عَزِيزٌ حَكِيمٌ', 8, 63, 10, 37, 185, 'و دلهای آنها را با هم، الفت داد! اگر تمام آنچه را روی زمین است صرف می‌کردی که میان دلهای آنان الفت دهی، نمی‌توانستی! ولی خداوند در میان آنها الفت ایجاد کرد! او توانا و حکیم است!'),
(1224, 1224, 'يَا أَيُّهَا النَّبِيُّ حَسْبُكَ اللَّهُ وَمَنِ اتَّبَعَكَ مِنَ الْمُؤْمِنِينَ', 8, 64, 10, 37, 185, 'ای پیامبر! خداوند و مؤمنانی که از تو پیروی می‌کنند، برای حمایت تو کافی است (؛ فقط بر آنها تکیه کن)!'),
(1225, 1225, 'يَا أَيُّهَا النَّبِيُّ حَرِّضِ الْمُؤْمِنِينَ عَلَى الْقِتَالِ ۚ إِنْ يَكُنْ مِنْكُمْ عِشْرُونَ صَابِرُونَ يَغْلِبُوا مِائَتَيْنِ ۚ وَإِنْ يَكُنْ مِنْكُمْ مِائَةٌ يَغْلِبُوا أَلْفًا مِنَ الَّذِينَ كَفَرُوا بِأَنَّهُمْ قَوْمٌ لَا يَفْقَهُونَ', 8, 65, 10, 37, 185, 'ای پیامبر! مؤمنان را به جنگ (با دشمن) تشویق کن! هرگاه بیست نفر با استقامت از شما باشند، بر دویست نفر غلبه می‌کنند؛ و اگر صد نفر باشند، بر هزار نفر از کسانی که کافر شدند، پیروز می‌گردند؛ چرا که آنها گروهی هستند که نمی‌فهمند!'),
(1226, 1226, 'الْآنَ خَفَّفَ اللَّهُ عَنْكُمْ وَعَلِمَ أَنَّ فِيكُمْ ضَعْفًا ۚ فَإِنْ يَكُنْ مِنْكُمْ مِائَةٌ صَابِرَةٌ يَغْلِبُوا مِائَتَيْنِ ۚ وَإِنْ يَكُنْ مِنْكُمْ أَلْفٌ يَغْلِبُوا أَلْفَيْنِ بِإِذْنِ اللَّهِ ۗ وَاللَّهُ مَعَ الصَّابِرِينَ', 8, 66, 10, 37, 185, 'هم اکنون خداوند به شما تخفیف داد، و دانست که در شما ضعفی است؛ بنابراین، هرگاه یکصد نفر با استقامت از شما باشند، بر دویست نفر پیروز می‌شوند؛ و اگر یکهزار نفر باشند، بر دو هزار نفر به فرمان خدا غلبه خواهند کرد! و خدا با صابران است!'),
(1227, 1227, 'مَا كَانَ لِنَبِيٍّ أَنْ يَكُونَ لَهُ أَسْرَىٰ حَتَّىٰ يُثْخِنَ فِي الْأَرْضِ ۚ تُرِيدُونَ عَرَضَ الدُّنْيَا وَاللَّهُ يُرِيدُ الْآخِرَةَ ۗ وَاللَّهُ عَزِيزٌ حَكِيمٌ', 8, 67, 10, 37, 185, 'هیچ پیامبری حق ندارد اسیرانی (از دشمن) بگیرد تا کاملاً بر آنها پیروز گردد (؛ و جای پای خود را در زمین محکم کند)! شما متاع ناپایدار دنیا را می‌خواهید؛ (و مایلید اسیران بیشتری بگیرید، و در برابر گرفتن فدیه آزاد کنید؛ ولی خداوند، سرای دیگر را (برای شما) می‌خواهد؛ و خداوند قادر و حکیم است!'),
(1228, 1228, 'لَوْلَا كِتَابٌ مِنَ اللَّهِ سَبَقَ لَمَسَّكُمْ فِيمَا أَخَذْتُمْ عَذَابٌ عَظِيمٌ', 8, 68, 10, 37, 185, 'اگر فرمان سابق خدا نبود (که بدون ابلاغ، هیچ امّتی را کیفر ندهد)، بخاطر چیزی = اسیرانی‌ که گرفتید، مجازات بزرگی به شما می‌رسید.'),
(1229, 1229, 'فَكُلُوا مِمَّا غَنِمْتُمْ حَلَالًا طَيِّبًا ۚ وَاتَّقُوا اللَّهَ ۚ إِنَّ اللَّهَ غَفُورٌ رَحِيمٌ', 8, 69, 10, 37, 185, 'از آنچه به غنیمت گرفته‌اید، حلال و پاکیزه بخورید؛ و از خدا بپرهیزید؛ خداوند آمرزنده و مهربان است!'),
(1230, 1230, 'يَا أَيُّهَا النَّبِيُّ قُلْ لِمَنْ فِي أَيْدِيكُمْ مِنَ الْأَسْرَىٰ إِنْ يَعْلَمِ اللَّهُ فِي قُلُوبِكُمْ خَيْرًا يُؤْتِكُمْ خَيْرًا مِمَّا أُخِذَ مِنْكُمْ وَيَغْفِرْ لَكُمْ ۗ وَاللَّهُ غَفُورٌ رَحِيمٌ', 8, 70, 10, 37, 186, 'ای پیامبر! به اسیرانی که در دست شما هستند بگو: «اگر خداوند، خیری در دلهای شما بداند، (و نیّات پاکی داشته باشید،) بهتر از آنچه از شما گرفته شده به شما می‌دهد؛ و شما را می‌بخشد؛ و خداوند آمرزنده و مهربان است!»'),
(1231, 1231, 'وَإِنْ يُرِيدُوا خِيَانَتَكَ فَقَدْ خَانُوا اللَّهَ مِنْ قَبْلُ فَأَمْكَنَ مِنْهُمْ ۗ وَاللَّهُ عَلِيمٌ حَكِيمٌ', 8, 71, 10, 37, 186, 'امّا اگر بخواهند با تو خیانت کنند، (تازگی ندارد) آنها پیش از این (نیز) به خدا خیانت کردند؛ و خداوند (شما را) بر آنها پیروز کرد؛ خداوند دانا و حکیم است!'),
(1232, 1232, 'إِنَّ الَّذِينَ آمَنُوا وَهَاجَرُوا وَجَاهَدُوا بِأَمْوَالِهِمْ وَأَنْفُسِهِمْ فِي سَبِيلِ اللَّهِ وَالَّذِينَ آوَوْا وَنَصَرُوا أُولَٰئِكَ بَعْضُهُمْ أَوْلِيَاءُ بَعْضٍ ۚ وَالَّذِينَ آمَنُوا وَلَمْ يُهَاجِرُوا مَا لَكُمْ مِنْ وَلَايَتِهِمْ مِنْ شَيْءٍ حَتَّىٰ يُهَاجِرُوا ۚ وَإِنِ اسْتَنْصَرُوكُمْ فِي الدِّينِ فَعَلَيْكُمُ النَّصْرُ إِلَّا عَلَىٰ قَوْمٍ بَيْنَكُمْ وَبَيْنَهُمْ مِيثَاقٌ ۗ وَاللَّهُ بِمَا تَعْمَلُونَ بَصِيرٌ', 8, 72, 10, 37, 186, 'کسانی که ایمان آوردند و هجرت نمودند و با اموال و جانهای خود در راه خدا جهاد کردند، و آنها که پناه دادند و یاری نمودند، آنها یاران یکدیگرند؛ و آنها که ایمان آوردند و مهاجرت نکردند، هیچ گونه ولایت = دوستی و تعّهدی‌ در برابر آنها ندارید تا هجرت کنند! و (تنها) اگر در (حفظ) دین (خود) از شما یاری طلبند، بر شماست که آنها را یاری کنید، جز بر ضدّ گروهی که میان شما و آنها، پیمان (ترک مخاصمه) است؛ و خداوند به آنچه عمل می‌کنید، بیناست!'),
(1233, 1233, 'وَالَّذِينَ كَفَرُوا بَعْضُهُمْ أَوْلِيَاءُ بَعْضٍ ۚ إِلَّا تَفْعَلُوهُ تَكُنْ فِتْنَةٌ فِي الْأَرْضِ وَفَسَادٌ كَبِيرٌ', 8, 73, 10, 37, 186, 'کسانی که کافر شدند، اولیاء (و یاوران و مدافعان) یکدیگرند؛ اگر (این دستور را) انجام ندهید، فتنه و فساد عظیمی در زمین روی می‌دهد.'),
(1234, 1234, 'وَالَّذِينَ آمَنُوا وَهَاجَرُوا وَجَاهَدُوا فِي سَبِيلِ اللَّهِ وَالَّذِينَ آوَوْا وَنَصَرُوا أُولَٰئِكَ هُمُ الْمُؤْمِنُونَ حَقًّا ۚ لَهُمْ مَغْفِرَةٌ وَرِزْقٌ كَرِيمٌ', 8, 74, 10, 37, 186, 'و آنها که ایمان آوردند و هجرت نمودند و در راه خدا جهاد کردند، و آنها که پناه دادند و یاری نمودند، آنان مؤمنان حقیقی‌اند؛ برای آنها، آمرزش (و رحمت خدا) و روزی شایسته‌ای است.'),
(1235, 1235, 'وَالَّذِينَ آمَنُوا مِنْ بَعْدُ وَهَاجَرُوا وَجَاهَدُوا مَعَكُمْ فَأُولَٰئِكَ مِنْكُمْ ۚ وَأُولُو الْأَرْحَامِ بَعْضُهُمْ أَوْلَىٰ بِبَعْضٍ فِي كِتَابِ اللَّهِ ۗ إِنَّ اللَّهَ بِكُلِّ شَيْءٍ عَلِيمٌ', 8, 75, 10, 37, 186, 'و کسانی که بعداً ایمان آوردند و هجرت کردند و با شما جهاد نمودند، از شما هستند؛ و خویشاوندان نسبت به یکدیگر، در احکامی که خدا مقرّر داشته، (از دیگران) سزاوارترند؛ خداوند به همه چیز داناست.'),
(1236, 1236, 'بَرَاءَةٌ مِنَ اللَّهِ وَرَسُولِهِ إِلَى الَّذِينَ عَاهَدْتُمْ مِنَ الْمُشْرِكِينَ', 9, 1, 10, 38, 187, '(این، اعلام) بیزاری از سوی خدا و پیامبر او، به کسانی از مشرکان است که با آنها عهد بسته‌اید!'),
(1237, 1237, 'فَسِيحُوا فِي الْأَرْضِ أَرْبَعَةَ أَشْهُرٍ وَاعْلَمُوا أَنَّكُمْ غَيْرُ مُعْجِزِي اللَّهِ ۙ وَأَنَّ اللَّهَ مُخْزِي الْكَافِرِينَ', 9, 2, 10, 38, 187, 'با این حال، چهار ماه (مهلت دارید که آزادانه) در زمین سیر کنید (و هر جا می‌خواهید بروید، و بیندیشید)! و بدانید شما نمی‌توانید خدا را ناتوان سازید، (و از قدرت او فرار کنید! و بدانید) خداوند خوارکننده کافران است!'),
(1238, 1238, 'وَأَذَانٌ مِنَ اللَّهِ وَرَسُولِهِ إِلَى النَّاسِ يَوْمَ الْحَجِّ الْأَكْبَرِ أَنَّ اللَّهَ بَرِيءٌ مِنَ الْمُشْرِكِينَ ۙ وَرَسُولُهُ ۚ فَإِنْ تُبْتُمْ فَهُوَ خَيْرٌ لَكُمْ ۖ وَإِنْ تَوَلَّيْتُمْ فَاعْلَمُوا أَنَّكُمْ غَيْرُ مُعْجِزِي اللَّهِ ۗ وَبَشِّرِ الَّذِينَ كَفَرُوا بِعَذَابٍ أَلِيمٍ', 9, 3, 10, 38, 187, 'و این، اعلامی است از ناحیه خدا و پیامبرش به (عموم) مردم در روز حج اکبر = روز عید قربان‌ که: خداوند و پیامبرش از مشرکان بیزارند! با این حال، اگر توبه کنید، برای شما بهتر است! و اگر سرپیچی نمایید، بدانید شما نمی‌توانید خدا را ناتوان سازید (و از قلمرو قدرتش خارج شوید)! و کافران را به مجازات دردناک بشارت ده!'),
(1239, 1239, 'إِلَّا الَّذِينَ عَاهَدْتُمْ مِنَ الْمُشْرِكِينَ ثُمَّ لَمْ يَنْقُصُوكُمْ شَيْئًا وَلَمْ يُظَاهِرُوا عَلَيْكُمْ أَحَدًا فَأَتِمُّوا إِلَيْهِمْ عَهْدَهُمْ إِلَىٰ مُدَّتِهِمْ ۚ إِنَّ اللَّهَ يُحِبُّ الْمُتَّقِينَ', 9, 4, 10, 38, 187, 'مگر کسانی از مشرکان که با آنها عهد بستید، و چیزی از آن را در حقّ شما فروگذار نکردند، و احدی را بر ضدّ شما تقویت ننمودند؛ پیمان آنها را تا پایان مدّتشان محترم بشمرید؛ زیرا خداوند پرهیزگاران را دوست دارد!'),
(1240, 1240, 'فَإِذَا انْسَلَخَ الْأَشْهُرُ الْحُرُمُ فَاقْتُلُوا الْمُشْرِكِينَ حَيْثُ وَجَدْتُمُوهُمْ وَخُذُوهُمْ وَاحْصُرُوهُمْ وَاقْعُدُوا لَهُمْ كُلَّ مَرْصَدٍ ۚ فَإِنْ تَابُوا وَأَقَامُوا الصَّلَاةَ وَآتَوُا الزَّكَاةَ فَخَلُّوا سَبِيلَهُمْ ۚ إِنَّ اللَّهَ غَفُورٌ رَحِيمٌ', 9, 5, 10, 38, 187, '(امّا) وقتی ماه‌های حرام پایان گرفت، مشرکان را هر جا یافتید به قتل برسانید؛ و آنها را اسیر سازید؛ و محاصره کنید؛ و در هر کمینگاه، بر سر راه آنها بنشینید! هرگاه توبه کنند، و نماز را برپا دارند، و زکات را بپردازند، آنها را رها سازید؛ زیرا خداوند آمرزنده و مهربان است!'),
(1241, 1241, 'وَإِنْ أَحَدٌ مِنَ الْمُشْرِكِينَ اسْتَجَارَكَ فَأَجِرْهُ حَتَّىٰ يَسْمَعَ كَلَامَ اللَّهِ ثُمَّ أَبْلِغْهُ مَأْمَنَهُ ۚ ذَٰلِكَ بِأَنَّهُمْ قَوْمٌ لَا يَعْلَمُونَ', 9, 6, 10, 38, 187, 'و اگر یکی از مشرکان از تو پناهندگی بخواهد، به او پناه ده تا سخن خدا را بشنود (و در آن بیندیشد)! سپس او را به محل امنش برسان، چرا که آنها گروهی ناآگاهند!'),
(1242, 1242, 'كَيْفَ يَكُونُ لِلْمُشْرِكِينَ عَهْدٌ عِنْدَ اللَّهِ وَعِنْدَ رَسُولِهِ إِلَّا الَّذِينَ عَاهَدْتُمْ عِنْدَ الْمَسْجِدِ الْحَرَامِ ۖ فَمَا اسْتَقَامُوا لَكُمْ فَاسْتَقِيمُوا لَهُمْ ۚ إِنَّ اللَّهَ يُحِبُّ الْمُتَّقِينَ', 9, 7, 10, 38, 188, 'چگونه برای مشرکان پیمانی نزد خدا و رسول او خواهد بود (در حالی که آنها همواره آماده شکستن پیمانشان هستند)؟! مگر کسانی که نزد مسجد الحرام با آنان پیمان بستید؛ (و پیمان خود را محترم شمردند؛) تا زمانی که در برابر شما وفادار باشند، شما نیز وفاداری کنید، که خداوند پرهیزگاران را دوست دارد!'),
(1243, 1243, 'كَيْفَ وَإِنْ يَظْهَرُوا عَلَيْكُمْ لَا يَرْقُبُوا فِيكُمْ إِلًّا وَلَا ذِمَّةً ۚ يُرْضُونَكُمْ بِأَفْوَاهِهِمْ وَتَأْبَىٰ قُلُوبُهُمْ وَأَكْثَرُهُمْ فَاسِقُونَ', 9, 8, 10, 38, 188, 'چگونه (پیمان مشرکان ارزش دارد)، در حالی که اگر بر شما غالب شوند، نه ملاحظه خویشاوندی با شما را می‌کنند، و نه پیمان را؟! شما را با زبان خود خشنود می‌کنند، ولی دلهایشان ابا دارد؛ و بیشتر آنها فرمانبردار نیستند!'),
(1244, 1244, 'اشْتَرَوْا بِآيَاتِ اللَّهِ ثَمَنًا قَلِيلًا فَصَدُّوا عَنْ سَبِيلِهِ ۚ إِنَّهُمْ سَاءَ مَا كَانُوا يَعْمَلُونَ', 9, 9, 10, 38, 188, 'آنها آیات خدا را به بهای کمی فروختند؛ و (مردم را) از راه او باز داشتند؛ آنها اعمال بدی انجام می‌دادند!'),
(1245, 1245, 'لَا يَرْقُبُونَ فِي مُؤْمِنٍ إِلًّا وَلَا ذِمَّةً ۚ وَأُولَٰئِكَ هُمُ الْمُعْتَدُونَ', 9, 10, 10, 38, 188, '(نه تنها درباره شما،) درباره هیچ فرد باایمانی رعایت خویشاوندی و پیمان را نمی‌کنند؛ و آنها همان تجاوز کارانند!'),
(1246, 1246, 'فَإِنْ تَابُوا وَأَقَامُوا الصَّلَاةَ وَآتَوُا الزَّكَاةَ فَإِخْوَانُكُمْ فِي الدِّينِ ۗ وَنُفَصِّلُ الْآيَاتِ لِقَوْمٍ يَعْلَمُونَ', 9, 11, 10, 38, 188, '(ولی) اگر توبه کنند، نماز را برپا دارند، و زکات را بپردازند، برادر دینی شما هستند؛ و ما آیات خود را برای گروهی که می‌دانند (و می‌اندیشند)، شرح می‌دهیم!'),
(1247, 1247, 'وَإِنْ نَكَثُوا أَيْمَانَهُمْ مِنْ بَعْدِ عَهْدِهِمْ وَطَعَنُوا فِي دِينِكُمْ فَقَاتِلُوا أَئِمَّةَ الْكُفْرِ ۙ إِنَّهُمْ لَا أَيْمَانَ لَهُمْ لَعَلَّهُمْ يَنْتَهُونَ', 9, 12, 10, 38, 188, 'و اگر پیمانهای خود را پس از عهد خویش بشکنند، و آیین شما را مورد طعن قرار دهند، با پیشوایان کفر پیکار کنید؛ چرا که آنها پیمانی ندارند؛ شاید (با شدّت عمل) دست بردارند!'),
(1248, 1248, 'أَلَا تُقَاتِلُونَ قَوْمًا نَكَثُوا أَيْمَانَهُمْ وَهَمُّوا بِإِخْرَاجِ الرَّسُولِ وَهُمْ بَدَءُوكُمْ أَوَّلَ مَرَّةٍ ۚ أَتَخْشَوْنَهُمْ ۚ فَاللَّهُ أَحَقُّ أَنْ تَخْشَوْهُ إِنْ كُنْتُمْ مُؤْمِنِينَ', 9, 13, 10, 38, 188, 'آیا با گروهی که پیمانهای خود را شکستند، و تصمیم به اخراج پیامبر گرفتند، پیکار نمی‌کنید؟! در حالی که آنها نخستین بار (پیکار با شما را) آغاز کردند؛ آیا از آنها می‌ترسید؟! با اینکه خداوند سزاوارتر است که از او بترسید، اگر مؤمن هستید!'),
(1249, 1249, 'قَاتِلُوهُمْ يُعَذِّبْهُمُ اللَّهُ بِأَيْدِيكُمْ وَيُخْزِهِمْ وَيَنْصُرْكُمْ عَلَيْهِمْ وَيَشْفِ صُدُورَ قَوْمٍ مُؤْمِنِينَ', 9, 14, 10, 38, 189, 'با آنها پیکار کنید، که خداوند آنان را به دست شما مجازات می‌کند؛ و آنان را رسوا می‌سازد؛ و سینه گروهی از مؤمنان را شفا می‌بخشد (؛ و بر قلب آنها مرهم می‌نهد)'),
(1250, 1250, 'وَيُذْهِبْ غَيْظَ قُلُوبِهِمْ ۗ وَيَتُوبُ اللَّهُ عَلَىٰ مَنْ يَشَاءُ ۗ وَاللَّهُ عَلِيمٌ حَكِيمٌ', 9, 15, 10, 38, 189, 'و خشم دلهای آنان را از میان می‌برد! و خدا توبه هر کس را بخواهد (و شایسته بداند)، می‌پذیرد؛ و خداوند دانا و حکیم است.'),
(1251, 1251, 'أَمْ حَسِبْتُمْ أَنْ تُتْرَكُوا وَلَمَّا يَعْلَمِ اللَّهُ الَّذِينَ جَاهَدُوا مِنْكُمْ وَلَمْ يَتَّخِذُوا مِنْ دُونِ اللَّهِ وَلَا رَسُولِهِ وَلَا الْمُؤْمِنِينَ وَلِيجَةً ۚ وَاللَّهُ خَبِيرٌ بِمَا تَعْمَلُونَ', 9, 16, 10, 38, 189, 'آیا گمان کردید که (به حال خود) رها می‌شوید در حالی که هنوز کسانی که از شما جهاد کردند، و غیر از خدا و رسولش و مؤمنان را محرم اسرار خویش انتخاب ننمودند، (از دیگران) مشخصّ نشده‌اند؟! (باید آزمون شوید؛ و صفوف از هم جدا گردد؛) و خداوند به آنچه عمل می‌کنید، آگاه است!'),
(1252, 1252, 'مَا كَانَ لِلْمُشْرِكِينَ أَنْ يَعْمُرُوا مَسَاجِدَ اللَّهِ شَاهِدِينَ عَلَىٰ أَنْفُسِهِمْ بِالْكُفْرِ ۚ أُولَٰئِكَ حَبِطَتْ أَعْمَالُهُمْ وَفِي النَّارِ هُمْ خَالِدُونَ', 9, 17, 10, 38, 189, 'مشرکان حق ندارند مساجد خدا را آباد کنند در حالی که به کفر خویش گواهی می‌دهند! آنها اعمالشان نابود (و بی‌ارزش) شده؛ و در آتش (دوزخ)، جاودانه خواهند ماند!'),
(1253, 1253, 'إِنَّمَا يَعْمُرُ مَسَاجِدَ اللَّهِ مَنْ آمَنَ بِاللَّهِ وَالْيَوْمِ الْآخِرِ وَأَقَامَ الصَّلَاةَ وَآتَى الزَّكَاةَ وَلَمْ يَخْشَ إِلَّا اللَّهَ ۖ فَعَسَىٰ أُولَٰئِكَ أَنْ يَكُونُوا مِنَ الْمُهْتَدِينَ', 9, 18, 10, 38, 189, 'مساجد خدا را تنها کسی آباد می‌کند که ایمان به خدا و روز قیامت آورده، و نماز را برپا دارد، و زکات را بپردازد، و جز از خدا نترسد؛ امید است چنین گروهی از هدایت‌یافتگان باشند.'),
(1254, 1254, ' أَجَعَلْتُمْ سِقَايَةَ الْحَاجِّ وَعِمَارَةَ الْمَسْجِدِ الْحَرَامِ كَمَنْ آمَنَ بِاللَّهِ وَالْيَوْمِ الْآخِرِ وَجَاهَدَ فِي سَبِيلِ اللَّهِ ۚ لَا يَسْتَوُونَ عِنْدَ اللَّهِ ۗ وَاللَّهُ لَا يَهْدِي الْقَوْمَ الظَّالِمِينَ', 9, 19, 10, 38, 189, 'آیا سیراب کردن حجاج، و آباد ساختن مسجد الحرام را، همانند (عمل) کسی قرار دادید که به خدا و روز قیامت ایمان آورده، و در راه او جهاد کرده است؟! (این دو،) نزد خدا مساوی نیستند! و خداوند گروه ظالمان را هدایت نمی‌کند!'),
(1255, 1255, 'الَّذِينَ آمَنُوا وَهَاجَرُوا وَجَاهَدُوا فِي سَبِيلِ اللَّهِ بِأَمْوَالِهِمْ وَأَنْفُسِهِمْ أَعْظَمُ دَرَجَةً عِنْدَ اللَّهِ ۚ وَأُولَٰئِكَ هُمُ الْفَائِزُونَ', 9, 20, 10, 38, 189, 'آنها که ایمان آوردند، و هجرت کردند، و با اموال و جانهایشان در راه خدا جهاد نمودند، مقامشان نزد خدا برتر است؛ و آنها پیروز و رستگارند!'),
(1256, 1256, 'يُبَشِّرُهُمْ رَبُّهُمْ بِرَحْمَةٍ مِنْهُ وَرِضْوَانٍ وَجَنَّاتٍ لَهُمْ فِيهَا نَعِيمٌ مُقِيمٌ', 9, 21, 10, 38, 190, 'پروردگارشان آنها را به رحمتی از ناحیه خود، و رضایت (خویش)، و باغهایی از بهشت بشارت می‌دهد که در آن، نعمتهای جاودانه دارند؛'),
(1257, 1257, 'خَالِدِينَ فِيهَا أَبَدًا ۚ إِنَّ اللَّهَ عِنْدَهُ أَجْرٌ عَظِيمٌ', 9, 22, 10, 38, 190, 'همواره و تا ابد در این باغها (و در میان این نعمتها) خواهند بود؛ زیرا پاداش عظیم نزد خداوند است!'),
(1258, 1258, 'يَا أَيُّهَا الَّذِينَ آمَنُوا لَا تَتَّخِذُوا آبَاءَكُمْ وَإِخْوَانَكُمْ أَوْلِيَاءَ إِنِ اسْتَحَبُّوا الْكُفْرَ عَلَى الْإِيمَانِ ۚ وَمَنْ يَتَوَلَّهُمْ مِنْكُمْ فَأُولَٰئِكَ هُمُ الظَّالِمُونَ', 9, 23, 10, 38, 190, 'ای کسانی که ایمان آورده‌اید! هرگاه پدران و برادران شما، کفر را بر ایمان ترجیح دهند، آنها را ولیّ (و یار و یاور و تکیه‌گاه) خود قرار ندهید! و کسانی از شما که آنان را ولیّ خود قرار دهند، ستمگرند!'),
(1259, 1259, 'قُلْ إِنْ كَانَ آبَاؤُكُمْ وَأَبْنَاؤُكُمْ وَإِخْوَانُكُمْ وَأَزْوَاجُكُمْ وَعَشِيرَتُكُمْ وَأَمْوَالٌ اقْتَرَفْتُمُوهَا وَتِجَارَةٌ تَخْشَوْنَ كَسَادَهَا وَمَسَاكِنُ تَرْضَوْنَهَا أَحَبَّ إِلَيْكُمْ مِنَ اللَّهِ وَرَسُولِهِ وَجِهَادٍ فِي سَبِيلِهِ فَتَرَبَّصُوا حَتَّىٰ يَأْتِيَ اللَّهُ بِأَمْرِهِ ۗ وَاللَّهُ لَا يَهْدِي الْقَوْمَ الْفَاسِقِينَ', 9, 24, 10, 38, 190, 'بگو: «اگر پدران و فرزندان و برادران و همسران و طایفه شما، و اموالی که به دست آورده‌اید، و تجارتی که از کساد شدنش می‌ترسید، و خانه هائی که به آن علاقه دارید، در نظرتان از خداوند و پیامبرش و جهاد در راهش محبوبتر است، در انتظار باشید که خداوند عذابش را بر شما نازل کند؛ و خداوند جمعیّت نافرمانبردار را هدایت نمی‌کند!'),
(1260, 1260, 'لَقَدْ نَصَرَكُمُ اللَّهُ فِي مَوَاطِنَ كَثِيرَةٍ ۙ وَيَوْمَ حُنَيْنٍ ۙ إِذْ أَعْجَبَتْكُمْ كَثْرَتُكُمْ فَلَمْ تُغْنِ عَنْكُمْ شَيْئًا وَضَاقَتْ عَلَيْكُمُ الْأَرْضُ بِمَا رَحُبَتْ ثُمَّ وَلَّيْتُمْ مُدْبِرِينَ', 9, 25, 10, 38, 190, 'خداوند شما را در جاهای زیادی یاری کرد (و بر دشمن پیروز شدید)؛ و در روز حنین (نیز یاری نمود)؛ در آن هنگام که فزونی جمعیّتتان شما را مغرور ساخت، ولی (این فزونی جمعیّت) هیچ به دردتان نخورد و زمین با همه وسعتش بر شما تنگ شده؛ سپس پشت (به دشمن) کرده، فرار نمودید!'),
(1261, 1261, 'ثُمَّ أَنْزَلَ اللَّهُ سَكِينَتَهُ عَلَىٰ رَسُولِهِ وَعَلَى الْمُؤْمِنِينَ وَأَنْزَلَ جُنُودًا لَمْ تَرَوْهَا وَعَذَّبَ الَّذِينَ كَفَرُوا ۚ وَذَٰلِكَ جَزَاءُ الْكَافِرِينَ', 9, 26, 10, 38, 190, 'سپس خداوند «سکینه» خود را بر پیامبرش و بر مؤمنان نازل کرد؛ و لشکرهایی فرستاد که شما نمی‌دیدید؛ و کافران را مجازات کرد؛ و این است جزای کافران!'),
(1262, 1262, 'ثُمَّ يَتُوبُ اللَّهُ مِنْ بَعْدِ ذَٰلِكَ عَلَىٰ مَنْ يَشَاءُ ۗ وَاللَّهُ غَفُورٌ رَحِيمٌ', 9, 27, 10, 38, 191, 'سپس خداوند -بعد از آن- توبه هر کس را بخواهد (و شایسته بداند)، می‌پذیرد؛ و خداوند آمرزنده و مهربان است.'),
(1263, 1263, 'يَا أَيُّهَا الَّذِينَ آمَنُوا إِنَّمَا الْمُشْرِكُونَ نَجَسٌ فَلَا يَقْرَبُوا الْمَسْجِدَ الْحَرَامَ بَعْدَ عَامِهِمْ هَٰذَا ۚ وَإِنْ خِفْتُمْ عَيْلَةً فَسَوْفَ يُغْنِيكُمُ اللَّهُ مِنْ فَضْلِهِ إِنْ شَاءَ ۚ إِنَّ اللَّهَ عَلِيمٌ حَكِيمٌ', 9, 28, 10, 38, 191, 'ای کسانی که ایمان آورده‌اید! مشرکان ناپاکند؛ پس نباید بعد از امسال، نزدیک مسجد الحرام شوند! و اگر از فقر می‌ترسید، خداوند هرگاه بخواهد، شما را به کرمش بی‌نیاز می‌سازد؛ (و از راه دیگر جبران می‌کند؛) خداوند دانا و حکیم است.'),
(1264, 1264, 'قَاتِلُوا الَّذِينَ لَا يُؤْمِنُونَ بِاللَّهِ وَلَا بِالْيَوْمِ الْآخِرِ وَلَا يُحَرِّمُونَ مَا حَرَّمَ اللَّهُ وَرَسُولُهُ وَلَا يَدِينُونَ دِينَ الْحَقِّ مِنَ الَّذِينَ أُوتُوا الْكِتَابَ حَتَّىٰ يُعْطُوا الْجِزْيَةَ عَنْ يَدٍ وَهُمْ صَاغِرُونَ', 9, 29, 10, 38, 191, 'با کسانی از اهل کتاب که نه به خدا، و نه به روز جزا ایمان دارند، و نه آنچه را خدا و رسولش تحریم کرده حرام می‌شمرند، و نه آیین حق را می‌پذیرند، پیکار کنید تا زمانی که با خضوع و تسلیم، جزیه را به دست خود بپردازند!'),
(1265, 1265, 'وَقَالَتِ الْيَهُودُ عُزَيْرٌ ابْنُ اللَّهِ وَقَالَتِ النَّصَارَى الْمَسِيحُ ابْنُ اللَّهِ ۖ ذَٰلِكَ قَوْلُهُمْ بِأَفْوَاهِهِمْ ۖ يُضَاهِئُونَ قَوْلَ الَّذِينَ كَفَرُوا مِنْ قَبْلُ ۚ قَاتَلَهُمُ اللَّهُ ۚ أَنَّىٰ يُؤْفَكُونَ', 9, 30, 10, 38, 191, 'یهود گفتند: «عزیر پسر خداست!» و نصاری کفتند: «مسیح پسر خداست!» این سخنی است که با زبان خود می‌گویند، که همانند گفتار کافران پیشین است؛ خدا آنان را بکشد، چگونه از حق انحراف می‌یابند؟!'),
(1266, 1266, 'اتَّخَذُوا أَحْبَارَهُمْ وَرُهْبَانَهُمْ أَرْبَابًا مِنْ دُونِ اللَّهِ وَالْمَسِيحَ ابْنَ مَرْيَمَ وَمَا أُمِرُوا إِلَّا لِيَعْبُدُوا إِلَٰهًا وَاحِدًا ۖ لَا إِلَٰهَ إِلَّا هُوَ ۚ سُبْحَانَهُ عَمَّا يُشْرِكُونَ', 9, 31, 10, 38, 191, '(آنها) دانشمندان و راهبان خویش را معبودهایی در برابر خدا قرار دادند، و (همچنین) مسیح فرزند مریم را؛ در حالی که دستور نداشتند جز خداوند یکتائی را که معبودی جز او نیست، بپرستند، او پاک و منزه است از آنچه همتایش قرار می‌دهند!'),
(1267, 1267, 'يُرِيدُونَ أَنْ يُطْفِئُوا نُورَ اللَّهِ بِأَفْوَاهِهِمْ وَيَأْبَى اللَّهُ إِلَّا أَنْ يُتِمَّ نُورَهُ وَلَوْ كَرِهَ الْكَافِرُونَ', 9, 32, 10, 38, 192, 'آنها می‌خواهند نور خدا را با دهان خود خاموش کنند؛ ولی خدا جز این نمی‌خواهد که نور خود را کامل کند، هر چند کافران ناخشنود باشند!'),
(1268, 1268, 'هُوَ الَّذِي أَرْسَلَ رَسُولَهُ بِالْهُدَىٰ وَدِينِ الْحَقِّ لِيُظْهِرَهُ عَلَى الدِّينِ كُلِّهِ وَلَوْ كَرِهَ الْمُشْرِكُونَ', 9, 33, 10, 38, 192, 'او کسی است که رسولش را با هدایت و آیین حق فرستاد، تا آن را بر همه آیین‌ها غالب گرداند، هر چند مشرکان کراهت داشته باشند!'),
(1269, 1269, ' يَا أَيُّهَا الَّذِينَ آمَنُوا إِنَّ كَثِيرًا مِنَ الْأَحْبَارِ وَالرُّهْبَانِ لَيَأْكُلُونَ أَمْوَالَ النَّاسِ بِالْبَاطِلِ وَيَصُدُّونَ عَنْ سَبِيلِ اللَّهِ ۗ وَالَّذِينَ يَكْنِزُونَ الذَّهَبَ وَالْفِضَّةَ وَلَا يُنْفِقُونَهَا فِي سَبِيلِ اللَّهِ فَبَشِّرْهُمْ بِعَذَابٍ أَلِيمٍ', 9, 34, 10, 39, 192, 'ای کسانی که ایمان آورده‌اید! بسیاری از دانشمندان (اهل کتاب) و راهبان، اموال مردم را بباطل می‌خورند، و (آنان را) از راه خدا بازمی‌دارند! و کسانی که طلا و نقره را گنجینه (و ذخیره و پنهان) می‌سازند، و در راه خدا انفاق نمی‌کنند، به مجازات دردناکی بشارت ده!'),
(1270, 1270, 'يَوْمَ يُحْمَىٰ عَلَيْهَا فِي نَارِ جَهَنَّمَ فَتُكْوَىٰ بِهَا جِبَاهُهُمْ وَجُنُوبُهُمْ وَظُهُورُهُمْ ۖ هَٰذَا مَا كَنَزْتُمْ لِأَنْفُسِكُمْ فَذُوقُوا مَا كُنْتُمْ تَكْنِزُونَ', 9, 35, 10, 39, 192, 'در آن روز که آن را در آتش جهنم، گرم و سوزان کرده، و با آن صورتها و پهلوها و پشتهایشان را داغ می‌کنند؛ (و به آنها می‌گویند): این همان چیزی است که برای خود اندوختید (و گنجینه ساختید)! پس بچشید چیزی را که برای خود می‌اندوختید!'),
(1271, 1271, 'إِنَّ عِدَّةَ الشُّهُورِ عِنْدَ اللَّهِ اثْنَا عَشَرَ شَهْرًا فِي كِتَابِ اللَّهِ يَوْمَ خَلَقَ السَّمَاوَاتِ وَالْأَرْضَ مِنْهَا أَرْبَعَةٌ حُرُمٌ ۚ ذَٰلِكَ الدِّينُ الْقَيِّمُ ۚ فَلَا تَظْلِمُوا فِيهِنَّ أَنْفُسَكُمْ ۚ وَقَاتِلُوا الْمُشْرِكِينَ كَافَّةً كَمَا يُقَاتِلُونَكُمْ كَافَّةً ۚ وَاعْلَمُوا أَنَّ اللَّهَ مَعَ الْمُتَّقِينَ', 9, 36, 10, 39, 192, 'تعداد ماه‌ها نزد خداوند در کتاب الهی، از آن روز که آسمانها و زمین را آفریده، دوازده ماه است؛ که چهار ماه از آن، ماه حرام است؛ (و جنگ در آن ممنوع می‌باشد.) این، آیین ثابت و پابرجا (ی الهی) است! بنابر این، در این ماه‌ها به خود ستم نکنید (و از هرگونه خونریزی بپرهیزید)! و (به هنگام نبرد) با مشرکان، دسته جمعی پیکار کنید، همان گونه که آنها دسته جمعی با شما پیکار می‌کنند؛ و بدانید خداوند با پرهیزگاران است!'),
(1272, 1272, 'إِنَّمَا النَّسِيءُ زِيَادَةٌ فِي الْكُفْرِ ۖ يُضَلُّ بِهِ الَّذِينَ كَفَرُوا يُحِلُّونَهُ عَامًا وَيُحَرِّمُونَهُ عَامًا لِيُوَاطِئُوا عِدَّةَ مَا حَرَّمَ اللَّهُ فَيُحِلُّوا مَا حَرَّمَ اللَّهُ ۚ زُيِّنَ لَهُمْ سُوءُ أَعْمَالِهِمْ ۗ وَاللَّهُ لَا يَهْدِي الْقَوْمَ الْكَافِرِينَ', 9, 37, 10, 39, 193, 'نسی‌ء = جا به جا کردن و تأخیر ماه‌های حرام‌، افزایشی در کفر (مشرکان) است؛ که با آن، کافران گمراه می‌شوند؛ یک سال، آن را حلال، و سال دیگر آن را حرام می کنند، تا به مقدار ماه‌هایی که خداوند تحریم کرده بشود (و عدد چهار ماه، به پندارشان تکمیل گردد)؛ و به این ترتیب، آنچه را خدا حرام کرده، حلال بشمرند. اعمال زشتشان در نظرشان زیبا جلوه داده شده؛ و خداوند جمعیّت کافران را هدایت نمی‌کند!'),
(1273, 1273, 'يَا أَيُّهَا الَّذِينَ آمَنُوا مَا لَكُمْ إِذَا قِيلَ لَكُمُ انْفِرُوا فِي سَبِيلِ اللَّهِ اثَّاقَلْتُمْ إِلَى الْأَرْضِ ۚ أَرَضِيتُمْ بِالْحَيَاةِ الدُّنْيَا مِنَ الْآخِرَةِ ۚ فَمَا مَتَاعُ الْحَيَاةِ الدُّنْيَا فِي الْآخِرَةِ إِلَّا قَلِيلٌ', 9, 38, 10, 39, 193, 'ای کسانی که ایمان آورده‌اید! چرا هنگامی که به شما گفته می‌شود: «به سوی جهاد در راه خدا حرکت کنید!» بر زمین سنگینی می‌کنید (و سستی به خرج می‌دهید)؟! آیا به زندگی دنیا به جای آخرت راضی شده‌اید؟! با اینکه متاع زندگی دنیا، در برابر آخرت، جز اندکی نیست!'),
(1274, 1274, 'إِلَّا تَنْفِرُوا يُعَذِّبْكُمْ عَذَابًا أَلِيمًا وَيَسْتَبْدِلْ قَوْمًا غَيْرَكُمْ وَلَا تَضُرُّوهُ شَيْئًا ۗ وَاللَّهُ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ', 9, 39, 10, 39, 193, 'اگر (به سوی میدان جهاد) حرکت نکنید، شما را مجازات دردناکی می‌کند، و گروه دیگری غیر از شما را به جای شما قرارمی‌دهد؛ و هیچ زیانی به او نمی‌رسانید؛ و خداوند بر هر چیزی تواناست!'),
(1275, 1275, 'إِلَّا تَنْصُرُوهُ فَقَدْ نَصَرَهُ اللَّهُ إِذْ أَخْرَجَهُ الَّذِينَ كَفَرُوا ثَانِيَ اثْنَيْنِ إِذْ هُمَا فِي الْغَارِ إِذْ يَقُولُ لِصَاحِبِهِ لَا تَحْزَنْ إِنَّ اللَّهَ مَعَنَا ۖ فَأَنْزَلَ اللَّهُ سَكِينَتَهُ عَلَيْهِ وَأَيَّدَهُ بِجُنُودٍ لَمْ تَرَوْهَا وَجَعَلَ كَلِمَةَ الَّذِينَ كَفَرُوا السُّفْلَىٰ ۗ وَكَلِمَةُ اللَّهِ هِيَ الْعُلْيَا ۗ وَاللَّهُ عَزِيزٌ حَكِيمٌ', 9, 40, 10, 39, 193, 'اگر او را یاری نکنید، خداوند او را یاری کرد؛ (و در مشکلترین ساعات، او را تنها نگذاشت؛) آن هنگام که کافران او را (از مکّه) بیرون کردند، در حالی که دوّمین نفر بود (و یک نفر بیشتر همراه نداشت)؛ در آن هنگام که آن دو در غار بودند، و او به همراه خود می‌گفت: «غم مخور، خدا با ماست!» در این موقع، خداوند سکینه (و آرامش) خود را بر او فرستاد؛ و با لشکرهایی که مشاهده نمی‌کردید، او را تقویت نمود؛ و گفتار (و هدف) کافران را پایین قرار داد، (و آنها را با شکست مواجه ساخت؛) و سخن خدا (و آیین او)، بالا (و پیروز) است؛ و خداوند عزیز و حکیم است!'),
(1276, 1276, 'انْفِرُوا خِفَافًا وَثِقَالًا وَجَاهِدُوا بِأَمْوَالِكُمْ وَأَنْفُسِكُمْ فِي سَبِيلِ اللَّهِ ۚ ذَٰلِكُمْ خَيْرٌ لَكُمْ إِنْ كُنْتُمْ تَعْلَمُونَ', 9, 41, 10, 39, 194, '(همگی به سوی میدان جهاد) حرکت کنید؛ سبکبار باشید یا سنگین بار! و با اموال و جانهای خود، در راه خدا جهاد نمایید؛ این برای شما بهتر است اگر بدانید!'),
(1277, 1277, 'لَوْ كَانَ عَرَضًا قَرِيبًا وَسَفَرًا قَاصِدًا لَاتَّبَعُوكَ وَلَٰكِنْ بَعُدَتْ عَلَيْهِمُ الشُّقَّةُ ۚ وَسَيَحْلِفُونَ بِاللَّهِ لَوِ اسْتَطَعْنَا لَخَرَجْنَا مَعَكُمْ يُهْلِكُونَ أَنْفُسَهُمْ وَاللَّهُ يَعْلَمُ إِنَّهُمْ لَكَاذِبُونَ', 9, 42, 10, 39, 194, '(امّا گروهی از آنها، چنانند که) اگر غنایمی نزدیک (و در دسترس)، و سفری آسان باشد، (به طمع دنیا) از تو پیروی می‌کنند؛ ولی (اکنون که برای میدان تبوک،) راه بر آنها دور (و پر مشقت) است، (سرباز می‌زنند؛) و بزودی به خدا سوگند یاد می‌کنند که: «اگر توانایی داشتیم، همراه شما حرکت می‌کردیم!» (آنها با این اعمال و این دروغها، در واقع) خود را هلاک می‌کنند؛ و خداوند می‌داند آنها دروغگو هستند!'),
(1278, 1278, 'عَفَا اللَّهُ عَنْكَ لِمَ أَذِنْتَ لَهُمْ حَتَّىٰ يَتَبَيَّنَ لَكَ الَّذِينَ صَدَقُوا وَتَعْلَمَ الْكَاذِبِينَ', 9, 43, 10, 39, 194, 'خداوند تو را بخشید؛ چرا پیش از آنکه راستگویان و دروغگویان را بشناسی، به آنها اجازه دادی؟! (خوب بود صبر می‌کردی، تا هر دو گروه خود را نشان دهند!)'),
(1279, 1279, 'لَا يَسْتَأْذِنُكَ الَّذِينَ يُؤْمِنُونَ بِاللَّهِ وَالْيَوْمِ الْآخِرِ أَنْ يُجَاهِدُوا بِأَمْوَالِهِمْ وَأَنْفُسِهِمْ ۗ وَاللَّهُ عَلِيمٌ بِالْمُتَّقِينَ', 9, 44, 10, 39, 194, 'آنها که به خدا و روز جزا ایمان دارند، هیچ گاه برای ترک جهاد (در راه خدا) با اموال و جانهایشان، از تو اجازه نمی‌گیرند؛ و خداوند پرهیزگاران را می‌شناسد.'),
(1280, 1280, 'إِنَّمَا يَسْتَأْذِنُكَ الَّذِينَ لَا يُؤْمِنُونَ بِاللَّهِ وَالْيَوْمِ الْآخِرِ وَارْتَابَتْ قُلُوبُهُمْ فَهُمْ فِي رَيْبِهِمْ يَتَرَدَّدُونَ', 9, 45, 10, 39, 194, 'تنها کسانی از تو اجازه (این کار را) می‌گیرند که به خدا و روز جزا ایمان ندارند، و دلهایشان با شکّ و تردید آمیخته است؛ آنها در تردید خود سرگردانند.'),
(1281, 1281, ' وَلَوْ أَرَادُوا الْخُرُوجَ لَأَعَدُّوا لَهُ عُدَّةً وَلَٰكِنْ كَرِهَ اللَّهُ انْبِعَاثَهُمْ فَثَبَّطَهُمْ وَقِيلَ اقْعُدُوا مَعَ الْقَاعِدِينَ', 9, 46, 10, 39, 194, 'اگر آنها (راست می‌گفتند، و) اراده داشتند که (بسوی میدان جهاد) خارج شوند، وسیله‌ای برای آن فراهم می‌ساختند! ولی خدا از حرکت آنها کراهت داشت؛ از این رو (توفیقش را از آنان سلب کرد؛ و) آنها را (از جهاد) باز داشت؛ و به آنان گفته شد: «با «قاعدین» = کودکان و پیران و بیماران‌ بنشینید!»'),
(1282, 1282, 'لَوْ خَرَجُوا فِيكُمْ مَا زَادُوكُمْ إِلَّا خَبَالًا وَلَأَوْضَعُوا خِلَالَكُمْ يَبْغُونَكُمُ الْفِتْنَةَ وَفِيكُمْ سَمَّاعُونَ لَهُمْ ۗ وَاللَّهُ عَلِيمٌ بِالظَّالِمِينَ', 9, 47, 10, 39, 194, 'اگر آنها همراه شما (بسوی میدان جهاد) خارج می‌شدند، جز اضطراب و تردید، چیزی بر شما نمی‌افزودند؛ و بسرعت در بین شما به فتنه‌انگیزی (و ایجاد تفرقه و نفاق) می‌پرداختند؛ و در میان شما، افرادی (سست و ضعیف) هستند که به سخنان آنها کاملاً گوش فرامی‌دهند؛ و خداوند، ظالمان را می‌شناسد.'),
(1283, 1283, 'لَقَدِ ابْتَغَوُا الْفِتْنَةَ مِنْ قَبْلُ وَقَلَّبُوا لَكَ الْأُمُورَ حَتَّىٰ جَاءَ الْحَقُّ وَظَهَرَ أَمْرُ اللَّهِ وَهُمْ كَارِهُونَ', 9, 48, 10, 39, 195, 'آنها پیش از این (نیز) در پی فتنه‌انگیزی بودند، و کارها را بر تو دگرگون ساختند (و به هم ریختند)؛ تا آن که حق فرا رسید، و فرمان خدا آشکار گشت (و پیروز شدید)، در حالی که آنها کراهت داشتند.'),
(1284, 1284, 'وَمِنْهُمْ مَنْ يَقُولُ ائْذَنْ لِي وَلَا تَفْتِنِّي ۚ أَلَا فِي الْفِتْنَةِ سَقَطُوا ۗ وَإِنَّ جَهَنَّمَ لَمُحِيطَةٌ بِالْكَافِرِينَ', 9, 49, 10, 39, 195, 'بعضی از آنها می‌گویند: «به ما اجازه ده (تا در جهاد شرکت نکنیم)، و ما را به گناه نیفکن»! آگاه باشید آنها (هم اکنون) در گناه سقوط کرده‌اند؛ و جهنم، کافران را احاطه کرده است!'),
(1285, 1285, 'إِنْ تُصِبْكَ حَسَنَةٌ تَسُؤْهُمْ ۖ وَإِنْ تُصِبْكَ مُصِيبَةٌ يَقُولُوا قَدْ أَخَذْنَا أَمْرَنَا مِنْ قَبْلُ وَيَتَوَلَّوْا وَهُمْ فَرِحُونَ', 9, 50, 10, 39, 195, 'هرگاه نیکی به تو رسد، آنها را ناراحت می‌کند؛ و اگر مصیبتی به تو رسد، می‌گویند: «ما تصمیم خود را از پیش گرفته‌ایم.» و بازمی‌گردند در حالی که خوشحالند!'),
(1286, 1286, 'قُلْ لَنْ يُصِيبَنَا إِلَّا مَا كَتَبَ اللَّهُ لَنَا هُوَ مَوْلَانَا ۚ وَعَلَى اللَّهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُونَ', 9, 51, 10, 39, 195, 'بگو: «هیچ حادثه‌ای برای ما رخ نمی‌دهد، مگر آنچه خداوند برای ما نوشته و مقرّر داشته است؛ او مولا (و سرپرست) ماست؛ و مؤمنان باید تنها بر خدا توکّل کنند!»');
INSERT INTO `Quran` (`rowid`, `id`, `Arabic`, `Chapter`, `Verse`, `Juz`, `Hizb`, `Page`, `tr`) VALUES
(1287, 1287, 'قُلْ هَلْ تَرَبَّصُونَ بِنَا إِلَّا إِحْدَى الْحُسْنَيَيْنِ ۖ وَنَحْنُ نَتَرَبَّصُ بِكُمْ أَنْ يُصِيبَكُمُ اللَّهُ بِعَذَابٍ مِنْ عِنْدِهِ أَوْ بِأَيْدِينَا ۖ فَتَرَبَّصُوا إِنَّا مَعَكُمْ مُتَرَبِّصُونَ', 9, 52, 10, 39, 195, 'بگو: «آیا درباره ما، جز یکی از دو نیکی را انتظار دارید؟! (: یا پیروزی یا شهادت) ولی ما انتظار داریم که خداوند، عذابی از سوی خودش (در آن جهان) به شما برساند، یا (در این جهان) به دست ما (مجازات شوید) اکنون که چنین است، شما انتظار بکشید، ما هم با شما انتظار می‌کشیم!»'),
(1288, 1288, 'قُلْ أَنْفِقُوا طَوْعًا أَوْ كَرْهًا لَنْ يُتَقَبَّلَ مِنْكُمْ ۖ إِنَّكُمْ كُنْتُمْ قَوْمًا فَاسِقِينَ', 9, 53, 10, 39, 195, 'بگو: «انفاق کنید؛ خواه از روی میل باشد یا اکراه، هرگز از شما پذیرفته نمی‌شود؛ چرا که شما قوم فاسقی بودید!»'),
(1289, 1289, 'وَمَا مَنَعَهُمْ أَنْ تُقْبَلَ مِنْهُمْ نَفَقَاتُهُمْ إِلَّا أَنَّهُمْ كَفَرُوا بِاللَّهِ وَبِرَسُولِهِ وَلَا يَأْتُونَ الصَّلَاةَ إِلَّا وَهُمْ كُسَالَىٰ وَلَا يُنْفِقُونَ إِلَّا وَهُمْ كَارِهُونَ', 9, 54, 10, 39, 195, 'هیچ چیز مانع قبول انفاقهای آنها نشد، جز اینکه آنها به خدا و پیامبرش کافر شدند، و نماز بجا نمی‌آورند جز با کسالت، و انفاق نمی‌کنند مگر با کراهت!'),
(1290, 1290, 'فَلَا تُعْجِبْكَ أَمْوَالُهُمْ وَلَا أَوْلَادُهُمْ ۚ إِنَّمَا يُرِيدُ اللَّهُ لِيُعَذِّبَهُمْ بِهَا فِي الْحَيَاةِ الدُّنْيَا وَتَزْهَقَ أَنْفُسُهُمْ وَهُمْ كَافِرُونَ', 9, 55, 10, 39, 196, 'و (فزونی) اموال و اولاد آنها، تو را در شگفتی فرو نبرد؛ خدا می‌خواهد آنان را به وسیله آن، در زندگی دنیا عذاب کند، و در حال کفر بمیرند!'),
(1291, 1291, 'وَيَحْلِفُونَ بِاللَّهِ إِنَّهُمْ لَمِنْكُمْ وَمَا هُمْ مِنْكُمْ وَلَٰكِنَّهُمْ قَوْمٌ يَفْرَقُونَ', 9, 56, 10, 39, 196, 'آنها به خدا سوگند می‌خورند که از شما هستند، در حالی که از شما نیستند؛ ولی آنها گروهی هستند که می‌ترسند (و به خاطر ترس از فاش شدن اسرارشان دروغ می‌گویند)!'),
(1292, 1292, 'لَوْ يَجِدُونَ مَلْجَأً أَوْ مَغَارَاتٍ أَوْ مُدَّخَلًا لَوَلَّوْا إِلَيْهِ وَهُمْ يَجْمَحُونَ', 9, 57, 10, 39, 196, 'اگر پناهگاه یا غارها یا راهی در زیر زمین بیابند، بسوی آن حرکت می‌کنند، و با سرعت و شتاب فرار می‌کنند.'),
(1293, 1293, 'وَمِنْهُمْ مَنْ يَلْمِزُكَ فِي الصَّدَقَاتِ فَإِنْ أُعْطُوا مِنْهَا رَضُوا وَإِنْ لَمْ يُعْطَوْا مِنْهَا إِذَا هُمْ يَسْخَطُونَ', 9, 58, 10, 39, 196, 'و در میان آنها کسانی هستند که در (تقسیم) غنایم به تو خرده میگیرند؛ اگر از آن (غنایم، سهمی) به آنها داده شود، راضی می‌شوند؛ و اگر داده نشود، خشم می‌گیرند (؛ خواه حقّ آنها باشد یا نه)!'),
(1294, 1294, 'وَلَوْ أَنَّهُمْ رَضُوا مَا آتَاهُمُ اللَّهُ وَرَسُولُهُ وَقَالُوا حَسْبُنَا اللَّهُ سَيُؤْتِينَا اللَّهُ مِنْ فَضْلِهِ وَرَسُولُهُ إِنَّا إِلَى اللَّهِ رَاغِبُونَ', 9, 59, 10, 39, 196, '(در حالی که) اگر به آنچه خدا و پیامبرش به آنان داده راضی باشند، و بگویند: «خداوند برای ما کافی است! و بزودی خدا و رسولش، از فضل خود به ما می‌بخشند؛ ما تنها رضای او را می‌طلبیم.» (برای آنها بهتر است)!'),
(1295, 1295, ' إِنَّمَا الصَّدَقَاتُ لِلْفُقَرَاءِ وَالْمَسَاكِينِ وَالْعَامِلِينَ عَلَيْهَا وَالْمُؤَلَّفَةِ قُلُوبُهُمْ وَفِي الرِّقَابِ وَالْغَارِمِينَ وَفِي سَبِيلِ اللَّهِ وَابْنِ السَّبِيلِ ۖ فَرِيضَةً مِنَ اللَّهِ ۗ وَاللَّهُ عَلِيمٌ حَكِيمٌ', 9, 60, 10, 40, 196, 'زکاتها مخصوص فقرا و مساکین و کارکنانی است که برای (جمع آوری) آن زحمت می‌کشند، و کسانی که برای جلب محبّتشان اقدام می‌شود، و برای (آزادی) بردگان، و (ادای دین) بدهکاران، و در راه (تقویت آیین) خدا، و واماندگان در راه؛ این، یک فریضه (مهم) الهی است؛ و خداوند دانا و حکیم است!'),
(1296, 1296, 'وَمِنْهُمُ الَّذِينَ يُؤْذُونَ النَّبِيَّ وَيَقُولُونَ هُوَ أُذُنٌ ۚ قُلْ أُذُنُ خَيْرٍ لَكُمْ يُؤْمِنُ بِاللَّهِ وَيُؤْمِنُ لِلْمُؤْمِنِينَ وَرَحْمَةٌ لِلَّذِينَ آمَنُوا مِنْكُمْ ۚ وَالَّذِينَ يُؤْذُونَ رَسُولَ اللَّهِ لَهُمْ عَذَابٌ أَلِيمٌ', 9, 61, 10, 40, 196, 'از آنها کسانی هستند که پیامبر را آزار می‌دهند و می‌گویند: «او آدم خوش‌باوری است!» بگو: «خوش‌باور بودن او به نفع شماست! (ولی بدانید) او به خدا ایمان دارد؛ و (تنها) مؤمنان را تصدیق می‌کند؛ و رحمت است برای کسانی از شما که ایمان آورده‌اند!» و آنها که رسول خدا را آزار می‌دهند، عذاب دردناکی دارند!'),
(1297, 1297, 'يَحْلِفُونَ بِاللَّهِ لَكُمْ لِيُرْضُوكُمْ وَاللَّهُ وَرَسُولُهُ أَحَقُّ أَنْ يُرْضُوهُ إِنْ كَانُوا مُؤْمِنِينَ', 9, 62, 10, 40, 197, 'آنها برای شما به خدا سوگند یاد می‌کنند، تا شما را راضی سازند؛ در حالی که شایسته‌تر این است که خدا و رسولش را راضی کنند، اگر ایمان دارند!'),
(1298, 1298, 'أَلَمْ يَعْلَمُوا أَنَّهُ مَنْ يُحَادِدِ اللَّهَ وَرَسُولَهُ فَأَنَّ لَهُ نَارَ جَهَنَّمَ خَالِدًا فِيهَا ۚ ذَٰلِكَ الْخِزْيُ الْعَظِيمُ', 9, 63, 10, 40, 197, 'آیا نمی‌دانند هر کس با خدا و رسولش دشمنی کند، برای او آتش دوزخ است؛ جاودانه در آن می‌ماند؟! این، همان رسوایی بزرگ است!'),
(1299, 1299, 'يَحْذَرُ الْمُنَافِقُونَ أَنْ تُنَزَّلَ عَلَيْهِمْ سُورَةٌ تُنَبِّئُهُمْ بِمَا فِي قُلُوبِهِمْ ۚ قُلِ اسْتَهْزِئُوا إِنَّ اللَّهَ مُخْرِجٌ مَا تَحْذَرُونَ', 9, 64, 10, 40, 197, 'منافقان از آن بیم دارند که سوره‌ای بر ضدّ آنان نازل گردد، و به آنها از اسرار درون قلبشان خبر دهد. بگو: «استهزا کنید! خداوند، آنچه را از آن بیم دارید، آشکار می‌سازد!»'),
(1300, 1300, 'وَلَئِنْ سَأَلْتَهُمْ لَيَقُولُنَّ إِنَّمَا كُنَّا نَخُوضُ وَنَلْعَبُ ۚ قُلْ أَبِاللَّهِ وَآيَاتِهِ وَرَسُولِهِ كُنْتُمْ تَسْتَهْزِئُونَ', 9, 65, 10, 40, 197, 'و اگر از آنها بپرسی (: «چرا این اعمال خلاف را انجام دادید؟!»)، می‌گویند: «ما بازی و شوخی می‌کردیم!» بگو: «آیا خدا و آیات او و پیامبرش را مسخره می‌کردید؟!»'),
(1301, 1301, 'لَا تَعْتَذِرُوا قَدْ كَفَرْتُمْ بَعْدَ إِيمَانِكُمْ ۚ إِنْ نَعْفُ عَنْ طَائِفَةٍ مِنْكُمْ نُعَذِّبْ طَائِفَةً بِأَنَّهُمْ كَانُوا مُجْرِمِينَ', 9, 66, 10, 40, 197, '(بگو:) عذر خواهی نکنید (که بیهوده است؛ چرا که) شما پس از ایمان آوردن، کافر شدید! اگر گروهی از شما را (بخاطر توبه) مورد عفو قرار دهیم، گروه دیگری را عذاب خواهیم کرد؛ زیرا مجرم بودند!'),
(1302, 1302, 'الْمُنَافِقُونَ وَالْمُنَافِقَاتُ بَعْضُهُمْ مِنْ بَعْضٍ ۚ يَأْمُرُونَ بِالْمُنْكَرِ وَيَنْهَوْنَ عَنِ الْمَعْرُوفِ وَيَقْبِضُونَ أَيْدِيَهُمْ ۚ نَسُوا اللَّهَ فَنَسِيَهُمْ ۗ إِنَّ الْمُنَافِقِينَ هُمُ الْفَاسِقُونَ', 9, 67, 10, 40, 197, 'مردان منافق و زنان منافق، همه از یک گروهند! آنها امر به منکر، و نهی از معروف می‌کنند؛ و دستهایشان را (از انفاق و بخشش) می‌بندند؛ خدا را فراموش کردند، و خدا (نیز) آنها را فراموش کرد (، و رحمتش را از آنها قطع نمود)؛ به یقین، منافقان همان فاسقانند!'),
(1303, 1303, 'وَعَدَ اللَّهُ الْمُنَافِقِينَ وَالْمُنَافِقَاتِ وَالْكُفَّارَ نَارَ جَهَنَّمَ خَالِدِينَ فِيهَا ۚ هِيَ حَسْبُهُمْ ۚ وَلَعَنَهُمُ اللَّهُ ۖ وَلَهُمْ عَذَابٌ مُقِيمٌ', 9, 68, 10, 40, 197, 'خداوند به مردان و زنان منافق و کفّار، وعده آتش دوزخ داده؛ جاودانه در آن خواهند ماند -همان برای آنها کافی است!- و خدا آنها را از رحمت خود دور ساخته؛ و عذاب همیشگی برای آنهاست!'),
(1304, 1304, 'كَالَّذِينَ مِنْ قَبْلِكُمْ كَانُوا أَشَدَّ مِنْكُمْ قُوَّةً وَأَكْثَرَ أَمْوَالًا وَأَوْلَادًا فَاسْتَمْتَعُوا بِخَلَاقِهِمْ فَاسْتَمْتَعْتُمْ بِخَلَاقِكُمْ كَمَا اسْتَمْتَعَ الَّذِينَ مِنْ قَبْلِكُمْ بِخَلَاقِهِمْ وَخُضْتُمْ كَالَّذِي خَاضُوا ۚ أُولَٰئِكَ حَبِطَتْ أَعْمَالُهُمْ فِي الدُّنْيَا وَالْآخِرَةِ ۖ وَأُولَٰئِكَ هُمُ الْخَاسِرُونَ', 9, 69, 10, 40, 198, '(شما منافقان،) همانند کسانی هستید که قبل از شما بودند (و راه نفاق پیمودند؛ بلکه) آنها از شما نیرومندتر، و اموال و فرزندانشان بیشتر بود! آنها از بهره خود (از مواهب الهی در راه گناه و هوس) استفاده کردند؛ شما نیز از بهره خود، (در این راه) استفاده کردید، همان گونه که آنها استفاده کردند؛ شما (در کفر و نفاق و استهزای مؤمنان) فرو رفتید، همان گونه که آنها فرو رفتند؛ (ولی سرانجام) اعمالشان در دنیا و آخرت نابود شد؛ و آنها همان زیانکارانند!'),
(1305, 1305, 'أَلَمْ يَأْتِهِمْ نَبَأُ الَّذِينَ مِنْ قَبْلِهِمْ قَوْمِ نُوحٍ وَعَادٍ وَثَمُودَ وَقَوْمِ إِبْرَاهِيمَ وَأَصْحَابِ مَدْيَنَ وَالْمُؤْتَفِكَاتِ ۚ أَتَتْهُمْ رُسُلُهُمْ بِالْبَيِّنَاتِ ۖ فَمَا كَانَ اللَّهُ لِيَظْلِمَهُمْ وَلَٰكِنْ كَانُوا أَنْفُسَهُمْ يَظْلِمُونَ', 9, 70, 10, 40, 198, 'آیا خبر کسانی که پیش از آنها بودند، به آنان نرسیده است؟! «قوم نوح» و «عاد» و «ثمود» و «قوم ابراهیم» و «اصحاب مدین» = قوم شعیب‌ و «شهرهای زیر و رو شده» = قوم لوط؛ پیامبرانشان دلایل روشن برای آنان آوردند، (ولی نپذیرفتند؛) خداوند به آنها ستم نکرد، امّا خودشان بر خویشتن ستم می‌کردند!'),
(1306, 1306, 'وَالْمُؤْمِنُونَ وَالْمُؤْمِنَاتُ بَعْضُهُمْ أَوْلِيَاءُ بَعْضٍ ۚ يَأْمُرُونَ بِالْمَعْرُوفِ وَيَنْهَوْنَ عَنِ الْمُنْكَرِ وَيُقِيمُونَ الصَّلَاةَ وَيُؤْتُونَ الزَّكَاةَ وَيُطِيعُونَ اللَّهَ وَرَسُولَهُ ۚ أُولَٰئِكَ سَيَرْحَمُهُمُ اللَّهُ ۗ إِنَّ اللَّهَ عَزِيزٌ حَكِيمٌ', 9, 71, 10, 40, 198, 'مردان و زنان باایمان، ولیّ (و یار و یاور) یکدیگرند؛ امر به معروف، و نهی از منکر می‌کنند؛ نماز را برپا می‌دارند؛ و زکات را می‌پردازند؛ و خدا و رسولش را اطاعت می‌کنند؛ بزودی خدا آنان را مورد رحمت خویش قرارمی‌دهد؛ خداوند توانا و حکیم است!'),
(1307, 1307, 'وَعَدَ اللَّهُ الْمُؤْمِنِينَ وَالْمُؤْمِنَاتِ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهَارُ خَالِدِينَ فِيهَا وَمَسَاكِنَ طَيِّبَةً فِي جَنَّاتِ عَدْنٍ ۚ وَرِضْوَانٌ مِنَ اللَّهِ أَكْبَرُ ۚ ذَٰلِكَ هُوَ الْفَوْزُ الْعَظِيمُ', 9, 72, 10, 40, 198, 'خداوند به مردان و زنان باایمان، باغهایی از بهشت وعده داده که نهرها از زیر درختانش جاری است؛ جاودانه در آن خواهند ماند؛ و مسکن‌های پاکیزه‌ای در بهشتهای جاودان (نصیب آنها ساخته)؛ و (خشنودی و) رضای خدا، (از همه اینها) برتر است؛ و پیروزی بزرگ، همین است!'),
(1308, 1308, 'يَا أَيُّهَا النَّبِيُّ جَاهِدِ الْكُفَّارَ وَالْمُنَافِقِينَ وَاغْلُظْ عَلَيْهِمْ ۚ وَمَأْوَاهُمْ جَهَنَّمُ ۖ وَبِئْسَ الْمَصِيرُ', 9, 73, 10, 40, 199, 'ای پیامبر! با کافران و منافقان جهاد کن، و بر آنها سخت بگیر! جایگاهشان جهنم است؛ و چه بد سرنوشتی دارند!'),
(1309, 1309, 'يَحْلِفُونَ بِاللَّهِ مَا قَالُوا وَلَقَدْ قَالُوا كَلِمَةَ الْكُفْرِ وَكَفَرُوا بَعْدَ إِسْلَامِهِمْ وَهَمُّوا بِمَا لَمْ يَنَالُوا ۚ وَمَا نَقَمُوا إِلَّا أَنْ أَغْنَاهُمُ اللَّهُ وَرَسُولُهُ مِنْ فَضْلِهِ ۚ فَإِنْ يَتُوبُوا يَكُ خَيْرًا لَهُمْ ۖ وَإِنْ يَتَوَلَّوْا يُعَذِّبْهُمُ اللَّهُ عَذَابًا أَلِيمًا فِي الدُّنْيَا وَالْآخِرَةِ ۚ وَمَا لَهُمْ فِي الْأَرْضِ مِنْ وَلِيٍّ وَلَا نَصِيرٍ', 9, 74, 10, 40, 199, 'به خدا سوگند می‌خورند که (در غیاب پیامبر، سخنان نادرست) نگفته‌اند؛ در حالی که قطعاً سخنان کفرآمیز گفته‌اند؛ و پس از اسلام‌آوردنشان، کافر شده‌اند؛ و تصمیم (به کار خطرناکی) گرفتند، که به آن نرسیدند. آنها فقط از این انتقام می‌گیرند که خداوند و رسولش، آنان را به فضل (و کرم) خود، بی‌نیاز ساختند! (با این حال،) اگر توبه کنند، برای آنها بهتر است؛ و اگر روی گردانند، خداوند آنها را در دنیا و آخرت، به مجازات دردناکی کیفر خواهد داد؛ و در سراسر زمین، نه ولیّ و حامی دارند، و نه یاوری!'),
(1310, 1310, ' وَمِنْهُمْ مَنْ عَاهَدَ اللَّهَ لَئِنْ آتَانَا مِنْ فَضْلِهِ لَنَصَّدَّقَنَّ وَلَنَكُونَنَّ مِنَ الصَّالِحِينَ', 9, 75, 10, 40, 199, 'بعضی از آنها با خدا پیمان بسته بودند که: «اگر خداوند ما را از فضل خود روزی دهد، قطعاً صدقه خواهیم داد؛ و از صالحان (و شاکران) خواهیم بود!»'),
(1311, 1311, 'فَلَمَّا آتَاهُمْ مِنْ فَضْلِهِ بَخِلُوا بِهِ وَتَوَلَّوْا وَهُمْ مُعْرِضُونَ', 9, 76, 10, 40, 199, 'امّا هنگامی که خدا از فضل خود به آنها بخشید، بخل ورزیدند و سرپیچی کردند و روی برتافتند!'),
(1312, 1312, 'فَأَعْقَبَهُمْ نِفَاقًا فِي قُلُوبِهِمْ إِلَىٰ يَوْمِ يَلْقَوْنَهُ بِمَا أَخْلَفُوا اللَّهَ مَا وَعَدُوهُ وَبِمَا كَانُوا يَكْذِبُونَ', 9, 77, 10, 40, 199, 'این عمل، (روح) نفاق را، تا روزی که خدا را ملاقات کنند، در دلهایشان برقرار ساخت. این بخاطر آن است که از پیمان الهی تخلّف جستند؛ و بخاطر آن است که دروغ می‌گفتند.'),
(1313, 1313, 'أَلَمْ يَعْلَمُوا أَنَّ اللَّهَ يَعْلَمُ سِرَّهُمْ وَنَجْوَاهُمْ وَأَنَّ اللَّهَ عَلَّامُ الْغُيُوبِ', 9, 78, 10, 40, 199, 'آیا نمی‌دانستند که خداوند، اسرار و سخنان درگوشی آنها را می‌داند؛ و خداوند دانای همه غیبها (و امور پنهانی) است؟!'),
(1314, 1314, 'الَّذِينَ يَلْمِزُونَ الْمُطَّوِّعِينَ مِنَ الْمُؤْمِنِينَ فِي الصَّدَقَاتِ وَالَّذِينَ لَا يَجِدُونَ إِلَّا جُهْدَهُمْ فَيَسْخَرُونَ مِنْهُمْ ۙ سَخِرَ اللَّهُ مِنْهُمْ وَلَهُمْ عَذَابٌ أَلِيمٌ', 9, 79, 10, 40, 199, 'آنهایی که از مؤمنان اطاعت کار، در صدقاتشان عیبجویی می‌کنند، و کسانی را که (برای انفاق در راه خدا) جز به مقدار (ناچیز) توانائی خود دسترسی ندارند، مسخره می‌نمایند، خدا آنها را مسخره می‌کند؛ (و کیفر استهزاکنندگان را به آنها می‌دهد؛) و برای آنها عذاب دردناکی است!'),
(1315, 1315, 'اسْتَغْفِرْ لَهُمْ أَوْ لَا تَسْتَغْفِرْ لَهُمْ إِنْ تَسْتَغْفِرْ لَهُمْ سَبْعِينَ مَرَّةً فَلَنْ يَغْفِرَ اللَّهُ لَهُمْ ۚ ذَٰلِكَ بِأَنَّهُمْ كَفَرُوا بِاللَّهِ وَرَسُولِهِ ۗ وَاللَّهُ لَا يَهْدِي الْقَوْمَ الْفَاسِقِينَ', 9, 80, 10, 40, 200, 'چه برای آنها استغفار کنی، و چه نکنی، (حتّی) اگر هفتاد بار برای آنها استغفار کنی، هرگز خدا آنها را نمی‌آمرزد! چرا که خدا و پیامبرش را انکار کردند؛ و خداوند جمعیّت فاسقان را هدایت نمی‌کند!'),
(1316, 1316, 'فَرِحَ الْمُخَلَّفُونَ بِمَقْعَدِهِمْ خِلَافَ رَسُولِ اللَّهِ وَكَرِهُوا أَنْ يُجَاهِدُوا بِأَمْوَالِهِمْ وَأَنْفُسِهِمْ فِي سَبِيلِ اللَّهِ وَقَالُوا لَا تَنْفِرُوا فِي الْحَرِّ ۗ قُلْ نَارُ جَهَنَّمَ أَشَدُّ حَرًّا ۚ لَوْ كَانُوا يَفْقَهُونَ', 9, 81, 10, 40, 200, 'تخلّف‌جویان (از جنگ تبوک،) از مخالفت با رسول خدا خوشحال شدند؛ و کراهت داشتند که با اموال و جانهای خود، در راه خدا جهاد کنند؛ و (به یکدیگر و به مؤمنان) گفتند: «در این گرما، (بسوی میدان) حرکت نکنید!» (به آنان) بگو: «آتش دوزخ از این هم گرمتر است!» اگر می‌دانستند!'),
(1317, 1317, 'فَلْيَضْحَكُوا قَلِيلًا وَلْيَبْكُوا كَثِيرًا جَزَاءً بِمَا كَانُوا يَكْسِبُونَ', 9, 82, 10, 40, 200, 'از این‌رو آنها باید کمتر بخندند و بسیار بگریند! (چرا که آتش جهنم در انتظارشان است) این، جزای کارهایی است که انجام می‌دادند!'),
(1318, 1318, 'فَإِنْ رَجَعَكَ اللَّهُ إِلَىٰ طَائِفَةٍ مِنْهُمْ فَاسْتَأْذَنُوكَ لِلْخُرُوجِ فَقُلْ لَنْ تَخْرُجُوا مَعِيَ أَبَدًا وَلَنْ تُقَاتِلُوا مَعِيَ عَدُوًّا ۖ إِنَّكُمْ رَضِيتُمْ بِالْقُعُودِ أَوَّلَ مَرَّةٍ فَاقْعُدُوا مَعَ الْخَالِفِينَ', 9, 83, 10, 40, 200, 'هرگاه خداوند تو را بسوی گروهی از آنان بازگرداند، و از تو اجازه خروج (بسوی میدان جهاد) بخواهند، بگو: «هیچ گاه با من خارج نخواهید شد! و هرگز همراه من، با دشمنی نخواهید جنگید! شما نخستین بار به کناره‌گیری راضی شدید، اکنون نیز با متخلّفان بمانید!»'),
(1319, 1319, 'وَلَا تُصَلِّ عَلَىٰ أَحَدٍ مِنْهُمْ مَاتَ أَبَدًا وَلَا تَقُمْ عَلَىٰ قَبْرِهِ ۖ إِنَّهُمْ كَفَرُوا بِاللَّهِ وَرَسُولِهِ وَمَاتُوا وَهُمْ فَاسِقُونَ', 9, 84, 10, 40, 200, 'هرگز بر مرده هیچ یک از آنان، نماز نخوان! و بر کنار قبرش، (برای دعا و طلب آمرزش،) نایست! چرا که آنها به خدا و رسولش کافر شدند؛ و در حالی که فاسق بودند از دنیا رفتند!'),
(1320, 1320, 'وَلَا تُعْجِبْكَ أَمْوَالُهُمْ وَأَوْلَادُهُمْ ۚ إِنَّمَا يُرِيدُ اللَّهُ أَنْ يُعَذِّبَهُمْ بِهَا فِي الدُّنْيَا وَتَزْهَقَ أَنْفُسُهُمْ وَهُمْ كَافِرُونَ', 9, 85, 10, 40, 200, 'مبادا اموال و فرزندانشان، مایه شگفتی تو گردد! (این برای آنها نعمت نیست؛ بلکه) خدا می‌خواهد آنها را به این وسیله در دنیا عذاب کند، و جانشان برآید در حالی که کافرند!'),
(1321, 1321, 'وَإِذَا أُنْزِلَتْ سُورَةٌ أَنْ آمِنُوا بِاللَّهِ وَجَاهِدُوا مَعَ رَسُولِهِ اسْتَأْذَنَكَ أُولُو الطَّوْلِ مِنْهُمْ وَقَالُوا ذَرْنَا نَكُنْ مَعَ الْقَاعِدِينَ', 9, 86, 10, 40, 200, 'و هنگامی که سوره‌ای نازل شود (و به آنان دستور دهد) که: «به خدا ایمان بیاورید! و همراه پیامبرش جهاد کنید!»، افرادی از آنها = گروه منافقان‌ که توانایی دارند، از تو اجازه می‌خواهند و می‌گویند: «بگذار ما با قاعدین = آنها که از جهاد معافند باشیم!»'),
(1322, 1322, 'رَضُوا بِأَنْ يَكُونُوا مَعَ الْخَوَالِفِ وَطُبِعَ عَلَىٰ قُلُوبِهِمْ فَهُمْ لَا يَفْقَهُونَ', 9, 87, 10, 40, 201, '(آری،) آنها راضی شدند که با متخلّفان باشند؛ و بر دلهایشان مهر نهاده شده؛ از این رو (چیزی) نمی‌فهمند!'),
(1323, 1323, 'لَٰكِنِ الرَّسُولُ وَالَّذِينَ آمَنُوا مَعَهُ جَاهَدُوا بِأَمْوَالِهِمْ وَأَنْفُسِهِمْ ۚ وَأُولَٰئِكَ لَهُمُ الْخَيْرَاتُ ۖ وَأُولَٰئِكَ هُمُ الْمُفْلِحُونَ', 9, 88, 10, 40, 201, 'ولی پیامبر و کسانی که با او ایمان آوردند، با اموال و جانهایشان جهاد کردند؛ و همه نیکیها برای آنهاست؛ و آنها همان رستگارانند!'),
(1324, 1324, 'أَعَدَّ اللَّهُ لَهُمْ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهَارُ خَالِدِينَ فِيهَا ۚ ذَٰلِكَ الْفَوْزُ الْعَظِيمُ', 9, 89, 10, 40, 201, 'خداوند برای آنها باغهایی از بهشت فراهم ساخته که نهرها از زیر درختانش جاری است؛ جاودانه در آن خواهند بود؛ و این است رستگاری (و پیروزی) بزرگ!'),
(1325, 1325, 'وَجَاءَ الْمُعَذِّرُونَ مِنَ الْأَعْرَابِ لِيُؤْذَنَ لَهُمْ وَقَعَدَ الَّذِينَ كَذَبُوا اللَّهَ وَرَسُولَهُ ۚ سَيُصِيبُ الَّذِينَ كَفَرُوا مِنْهُمْ عَذَابٌ أَلِيمٌ', 9, 90, 10, 40, 201, 'و عذرآورندگان از اعراب، (نزد تو) آمدند که به آنها اجازه (عدم شرکت در جهاد) داده شود؛ و آنها که به خدا و پیامبرش دروغ گفتند، (بدون هیچ عذری در خانه خود) نشستند؛ بزودی به کسانی از آنها که مخالفت کردند (و معذور نبودند)، عذاب دردناکی خواهد رسید!'),
(1326, 1326, 'لَيْسَ عَلَى الضُّعَفَاءِ وَلَا عَلَى الْمَرْضَىٰ وَلَا عَلَى الَّذِينَ لَا يَجِدُونَ مَا يُنْفِقُونَ حَرَجٌ إِذَا نَصَحُوا لِلَّهِ وَرَسُولِهِ ۚ مَا عَلَى الْمُحْسِنِينَ مِنْ سَبِيلٍ ۚ وَاللَّهُ غَفُورٌ رَحِيمٌ', 9, 91, 10, 40, 201, 'بر ضعیفان و بیماران و آنها که وسیله‌ای برای انفاق (در راه جهاد) ندارند، ایرادی نیست (که در میدان جنگ شرکت نجویند،) هرگاه برای خدا و رسولش خیرخواهی کنند (؛ و از آنچه در توان دارند، مضایقه ننمایند). بر نیکوکاران راه مؤاخذه نیست؛ و خداوند آمرزنده و مهربان است!'),
(1327, 1327, 'وَلَا عَلَى الَّذِينَ إِذَا مَا أَتَوْكَ لِتَحْمِلَهُمْ قُلْتَ لَا أَجِدُ مَا أَحْمِلُكُمْ عَلَيْهِ تَوَلَّوْا وَأَعْيُنُهُمْ تَفِيضُ مِنَ الدَّمْعِ حَزَنًا أَلَّا يَجِدُوا مَا يُنْفِقُونَ', 9, 92, 10, 40, 201, 'و (نیز) ایرادی نیست بر آنها که وقتی نزد تو آمدند که آنان را بر مرکبی (برای جهاد) سوار کنی، گفتی: «مرکبی که شما را بر آن سوار کنم، ندارم!» (از نزد تو) بازگشتند در حالی که چشمانشان از اندوه اشکبار بود؛ زیرا چیزی نداشتند که در راه خدا انفاق کنند (و با آن به میدان بروند)'),
(1328, 1328, ' إِنَّمَا السَّبِيلُ عَلَى الَّذِينَ يَسْتَأْذِنُونَكَ وَهُمْ أَغْنِيَاءُ ۚ رَضُوا بِأَنْ يَكُونُوا مَعَ الْخَوَالِفِ وَطَبَعَ اللَّهُ عَلَىٰ قُلُوبِهِمْ فَهُمْ لَا يَعْلَمُونَ', 9, 93, 11, 41, 201, 'راه مؤاخذه تنها به روی کسانی باز است که از تو اجازه می‌خواهند در حالی که توانگرند؛ (و امکانات کافی برای جهاد دارند؛) آنها راضی شدند که با متخلّفان = زنان و کودکان و بیماران‌ بمانند؛ و خداوند بر دلهایشان مهر نهاده؛ به همین جهت چیزی نمی‌دانند!'),
(1329, 1329, 'يَعْتَذِرُونَ إِلَيْكُمْ إِذَا رَجَعْتُمْ إِلَيْهِمْ ۚ قُلْ لَا تَعْتَذِرُوا لَنْ نُؤْمِنَ لَكُمْ قَدْ نَبَّأَنَا اللَّهُ مِنْ أَخْبَارِكُمْ ۚ وَسَيَرَى اللَّهُ عَمَلَكُمْ وَرَسُولُهُ ثُمَّ تُرَدُّونَ إِلَىٰ عَالِمِ الْغَيْبِ وَالشَّهَادَةِ فَيُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُونَ', 9, 94, 11, 41, 202, 'هنگامی که بسوی آنها (که از جهاد تخلّف کردند) باز گردید، از شما عذرخواهی می‌کنند؛ بگو: «عذرخواهی نکنید، ما هرگز به شما ایمان نخواهیم آورد! چرا که خدا ما را از اخبارتان آگاه ساخته؛ و خدا و رسولش، اعمال شما را می‌بینند؛ سپس به سوی کسی که دانای پنهان و آشکار است بازگشت داده می‌شوید؛ و او شما را به آنچه انجام می‌دادید، آگاه می‌کند (و جزا می‌دهد!)»'),
(1330, 1330, 'سَيَحْلِفُونَ بِاللَّهِ لَكُمْ إِذَا انْقَلَبْتُمْ إِلَيْهِمْ لِتُعْرِضُوا عَنْهُمْ ۖ فَأَعْرِضُوا عَنْهُمْ ۖ إِنَّهُمْ رِجْسٌ ۖ وَمَأْوَاهُمْ جَهَنَّمُ جَزَاءً بِمَا كَانُوا يَكْسِبُونَ', 9, 95, 11, 41, 202, 'هنگامی که بسوی آنان بازگردید، برای شما به خدا سوگند یاد می‌کنند، تا از آنها اعراض (و صرف نظر) کنید؛ از آنها اعراض کنید (و روی بگردانید)؛ چرا که پلیدند! و جایگاهشان دوزخ است، بکیفر اعمالی که انجام می‌دادند.'),
(1331, 1331, 'يَحْلِفُونَ لَكُمْ لِتَرْضَوْا عَنْهُمْ ۖ فَإِنْ تَرْضَوْا عَنْهُمْ فَإِنَّ اللَّهَ لَا يَرْضَىٰ عَنِ الْقَوْمِ الْفَاسِقِينَ', 9, 96, 11, 41, 202, 'برای شما قسم یاد می‌کنند تا از آنها راضی شوید؛ اگر شما از آنها راضی شوید، خداوند (هرگز) از جمعیّت فاسقان راضی نخواهد شد!'),
(1332, 1332, 'الْأَعْرَابُ أَشَدُّ كُفْرًا وَنِفَاقًا وَأَجْدَرُ أَلَّا يَعْلَمُوا حُدُودَ مَا أَنْزَلَ اللَّهُ عَلَىٰ رَسُولِهِ ۗ وَاللَّهُ عَلِيمٌ حَكِيمٌ', 9, 97, 11, 41, 202, 'بادیه‌نشینان عرب، کفر و نفاقشان شدیدتر است؛ و به ناآگاهی از حدود و احکامی که خدا بر پیامبرش نازل کرده، سزاوارترند؛ و خداوند دانا و حکیم است!'),
(1333, 1333, 'وَمِنَ الْأَعْرَابِ مَنْ يَتَّخِذُ مَا يُنْفِقُ مَغْرَمًا وَيَتَرَبَّصُ بِكُمُ الدَّوَائِرَ ۚ عَلَيْهِمْ دَائِرَةُ السَّوْءِ ۗ وَاللَّهُ سَمِيعٌ عَلِيمٌ', 9, 98, 11, 41, 202, 'گروهی از (این) اعراب بادیه‌نشین، چیزی را که (در راه خدا) انفاق می‌کنند، غرامت محسوب می‌دارند؛ و انتظار حوادث دردناکی برای شما می‌کشند؛ حوادث دردناک برای خود آنهاست؛ و خداوند شنوا و داناست!'),
(1334, 1334, 'وَمِنَ الْأَعْرَابِ مَنْ يُؤْمِنُ بِاللَّهِ وَالْيَوْمِ الْآخِرِ وَيَتَّخِذُ مَا يُنْفِقُ قُرُبَاتٍ عِنْدَ اللَّهِ وَصَلَوَاتِ الرَّسُولِ ۚ أَلَا إِنَّهَا قُرْبَةٌ لَهُمْ ۚ سَيُدْخِلُهُمُ اللَّهُ فِي رَحْمَتِهِ ۗ إِنَّ اللَّهَ غَفُورٌ رَحِيمٌ', 9, 99, 11, 41, 202, 'گروهی (دیگر) از عربهای بادیه‌نشین، به خدا و روز رستاخیز ایمان دارند؛ و آنچه را انفاق می‌کنند، مایه تقرّب به خدا، و دعای پیامبر می‌دانند؛ آگاه باشید اینها مایه تقرّب آنهاست! خداوند بزودی آنان را در رحمت خود وارد خواهد ساخت؛ به یقین، خداوند آمرزنده و مهربان است!'),
(1335, 1335, 'وَالسَّابِقُونَ الْأَوَّلُونَ مِنَ الْمُهَاجِرِينَ وَالْأَنْصَارِ وَالَّذِينَ اتَّبَعُوهُمْ بِإِحْسَانٍ رَضِيَ اللَّهُ عَنْهُمْ وَرَضُوا عَنْهُ وَأَعَدَّ لَهُمْ جَنَّاتٍ تَجْرِي تَحْتَهَا الْأَنْهَارُ خَالِدِينَ فِيهَا أَبَدًا ۚ ذَٰلِكَ الْفَوْزُ الْعَظِيمُ', 9, 100, 11, 41, 203, 'پیشگامان نخستین از مهاجرین و انصار، و کسانی که به نیکی از آنها پیروی کردند، خداوند از آنها خشنود گشت، و آنها (نیز) از او خشنود شدند؛ و باغهایی از بهشت برای آنان فراهم ساخته، که نهرها از زیر درختانش جاری است؛ جاودانه در آن خواهند ماند؛ و این است پیروزی بزرگ!'),
(1336, 1336, 'وَمِمَّنْ حَوْلَكُمْ مِنَ الْأَعْرَابِ مُنَافِقُونَ ۖ وَمِنْ أَهْلِ الْمَدِينَةِ ۖ مَرَدُوا عَلَى النِّفَاقِ لَا تَعْلَمُهُمْ ۖ نَحْنُ نَعْلَمُهُمْ ۚ سَنُعَذِّبُهُمْ مَرَّتَيْنِ ثُمَّ يُرَدُّونَ إِلَىٰ عَذَابٍ عَظِيمٍ', 9, 101, 11, 41, 203, 'و از (میان) اعراب بادیه‌نشین که اطراف شما هستند، جمعی منافقند؛ و از اهل مدینه (نیز)، گروهی سخت به نفاق پای بندند. تو آنها را نمی‌شناسی، ولی ما آنها را می شناسیم. بزودی آنها را دو بار مجازات می‌کنیم (: مجازاتی با رسوایی در دنیا، و مجازاتی به هنگام مرگ)؛ سپس بسوی مجازات بزرگی (در قیامت) فرستاده می‌شوند.'),
(1337, 1337, 'وَآخَرُونَ اعْتَرَفُوا بِذُنُوبِهِمْ خَلَطُوا عَمَلًا صَالِحًا وَآخَرَ سَيِّئًا عَسَى اللَّهُ أَنْ يَتُوبَ عَلَيْهِمْ ۚ إِنَّ اللَّهَ غَفُورٌ رَحِيمٌ', 9, 102, 11, 41, 203, 'و گروهی دیگر، به گناهان خود اعتراف کردند؛ و کار خوب و بد را به هم آمیختند؛ امید می‌رود که خداوند توبه آنها را بپذیرد؛ به یقین، خداوند آمرزنده و مهربان است!'),
(1338, 1338, 'خُذْ مِنْ أَمْوَالِهِمْ صَدَقَةً تُطَهِّرُهُمْ وَتُزَكِّيهِمْ بِهَا وَصَلِّ عَلَيْهِمْ ۖ إِنَّ صَلَاتَكَ سَكَنٌ لَهُمْ ۗ وَاللَّهُ سَمِيعٌ عَلِيمٌ', 9, 103, 11, 41, 203, 'از اموال آنها صدقه‌ای (بعنوان زکات) بگیر، تا بوسیله آن، آنها را پاک سازی و پرورش دهی! و (به هنگام گرفتن زکات،) به آنها دعا کن؛ که دعای تو، مایه آرامش آنهاست؛ و خداوند شنوا و داناست!'),
(1339, 1339, 'أَلَمْ يَعْلَمُوا أَنَّ اللَّهَ هُوَ يَقْبَلُ التَّوْبَةَ عَنْ عِبَادِهِ وَيَأْخُذُ الصَّدَقَاتِ وَأَنَّ اللَّهَ هُوَ التَّوَّابُ الرَّحِيمُ', 9, 104, 11, 41, 203, 'آیا نمی‌دانستند که فقط خداوند توبه را از بندگانش می‌پذیرد، و صدقات را می‌گیرد، و خداوند توبه‌پذیر و مهربان است؟!'),
(1340, 1340, 'وَقُلِ اعْمَلُوا فَسَيَرَى اللَّهُ عَمَلَكُمْ وَرَسُولُهُ وَالْمُؤْمِنُونَ ۖ وَسَتُرَدُّونَ إِلَىٰ عَالِمِ الْغَيْبِ وَالشَّهَادَةِ فَيُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُونَ', 9, 105, 11, 41, 203, 'بگو: «عمل کنید! خداوند و فرستاده او و مؤمنان، اعمال شما را می‌بینند! و بزودی، بسوی دانای نهان و آشکار، بازگردانده می‌شوید؛ و شما را به آنچه عمل می‌کردید، خبر می‌دهد!»'),
(1341, 1341, 'وَآخَرُونَ مُرْجَوْنَ لِأَمْرِ اللَّهِ إِمَّا يُعَذِّبُهُمْ وَإِمَّا يَتُوبُ عَلَيْهِمْ ۗ وَاللَّهُ عَلِيمٌ حَكِيمٌ', 9, 106, 11, 41, 203, 'و گروهی دیگر، به فرمان خدا واگذار شده‌اند (و کارشان با خداست)؛ یا آنها را مجازات می‌کند، و یا توبه آنان را می‌پذیرد (، هر طور که شایسته باشند)؛ و خداوند دانا و حکیم است!'),
(1342, 1342, 'وَالَّذِينَ اتَّخَذُوا مَسْجِدًا ضِرَارًا وَكُفْرًا وَتَفْرِيقًا بَيْنَ الْمُؤْمِنِينَ وَإِرْصَادًا لِمَنْ حَارَبَ اللَّهَ وَرَسُولَهُ مِنْ قَبْلُ ۚ وَلَيَحْلِفُنَّ إِنْ أَرَدْنَا إِلَّا الْحُسْنَىٰ ۖ وَاللَّهُ يَشْهَدُ إِنَّهُمْ لَكَاذِبُونَ', 9, 107, 11, 41, 204, '(گروهی دیگر از آنها) کسانی هستند که مسجدی ساختند برای زیان (به مسلمانان)، و (تقوّیت) کفر، و تفرقه‌افکنی میان مؤمنان، و کمینگاه برای کسی که از پیش با خدا و پیامبرش مبارزه کرده بود؛ آنها سوگند یاد می‌کنند که: «جز نیکی (و خدمت)، نظری نداشته‌ایم!» امّا خداوند گواهی می‌دهد که آنها دروغگو هستند!'),
(1343, 1343, 'لَا تَقُمْ فِيهِ أَبَدًا ۚ لَمَسْجِدٌ أُسِّسَ عَلَى التَّقْوَىٰ مِنْ أَوَّلِ يَوْمٍ أَحَقُّ أَنْ تَقُومَ فِيهِ ۚ فِيهِ رِجَالٌ يُحِبُّونَ أَنْ يَتَطَهَّرُوا ۚ وَاللَّهُ يُحِبُّ الْمُطَّهِّرِينَ', 9, 108, 11, 41, 204, 'هرگز در آن (مسجد به عبادت) نایست! آن مسجدی که از روز نخست بر پایه تقوا بنا شده، شایسته‌تر است که در آن (به عبادت) بایستی؛ در آن، مردانی هستند که دوست می‌دارند پاکیزه باشند؛ و خداوند پاکیزگان را دوست دارد!'),
(1344, 1344, 'أَفَمَنْ أَسَّسَ بُنْيَانَهُ عَلَىٰ تَقْوَىٰ مِنَ اللَّهِ وَرِضْوَانٍ خَيْرٌ أَمْ مَنْ أَسَّسَ بُنْيَانَهُ عَلَىٰ شَفَا جُرُفٍ هَارٍ فَانْهَارَ بِهِ فِي نَارِ جَهَنَّمَ ۗ وَاللَّهُ لَا يَهْدِي الْقَوْمَ الظَّالِمِينَ', 9, 109, 11, 41, 204, 'آیا کسی که شالوده آن را بر تقوای الهی و خشنودی او بنا کرده بهتر است، یا کسی که اساس آن را بر کنار پرتگاه سستی بنا نموده که ناگهان در آتش دوزخ فرومی‌ریزد؟ و خداوند گروه ستمگران را هدایت نمی‌کند!'),
(1345, 1345, 'لَا يَزَالُ بُنْيَانُهُمُ الَّذِي بَنَوْا رِيبَةً فِي قُلُوبِهِمْ إِلَّا أَنْ تَقَطَّعَ قُلُوبُهُمْ ۗ وَاللَّهُ عَلِيمٌ حَكِيمٌ', 9, 110, 11, 41, 204, '(امّا) این بنایی را که آنها ساختند، همواره بصورت یک وسیله شک و تردید، در دلهایشان باقی می‌ماند؛ مگر اینکه دلهایشان پاره پاره شود (و بمیرند؛ وگرنه، هرگز از دل آنها بیرون نمی‌رود)؛ و خداوند دانا و حکیم است!'),
(1346, 1346, ' إِنَّ اللَّهَ اشْتَرَىٰ مِنَ الْمُؤْمِنِينَ أَنْفُسَهُمْ وَأَمْوَالَهُمْ بِأَنَّ لَهُمُ الْجَنَّةَ ۚ يُقَاتِلُونَ فِي سَبِيلِ اللَّهِ فَيَقْتُلُونَ وَيُقْتَلُونَ ۖ وَعْدًا عَلَيْهِ حَقًّا فِي التَّوْرَاةِ وَالْإِنْجِيلِ وَالْقُرْآنِ ۚ وَمَنْ أَوْفَىٰ بِعَهْدِهِ مِنَ اللَّهِ ۚ فَاسْتَبْشِرُوا بِبَيْعِكُمُ الَّذِي بَايَعْتُمْ بِهِ ۚ وَذَٰلِكَ هُوَ الْفَوْزُ الْعَظِيمُ', 9, 111, 11, 41, 204, 'خداوند از مؤمنان، جانها و اموالشان را خریداری کرده، که (در برابرش) بهشت برای آنان باشد؛ (به این گونه که:) در راه خدا پیکار می‌کنند، می‌کشند و کشته می‌شوند؛ این وعده حقّی است بر او، که در تورات و انجیل و قرآن ذکر فرموده؛ و چه کسی از خدا به عهدش وفادارتر است؟! اکنون بشارت باد بر شما، به داد و ستدی که با خدا کرده‌اید؛ و این است آن پیروزی بزرگ!'),
(1347, 1347, 'التَّائِبُونَ الْعَابِدُونَ الْحَامِدُونَ السَّائِحُونَ الرَّاكِعُونَ السَّاجِدُونَ الْآمِرُونَ بِالْمَعْرُوفِ وَالنَّاهُونَ عَنِ الْمُنْكَرِ وَالْحَافِظُونَ لِحُدُودِ اللَّهِ ۗ وَبَشِّرِ الْمُؤْمِنِينَ', 9, 112, 11, 41, 205, 'توبه‌کنندگان، عبادت کاران، سپاسگویان، سیاحت کنندگان، رکوع کنندگان، سجده‌آوران، آمران به معروف، نهی کنندگان از منکر، و حافظان حدود (و مرزهای) الهی، (مؤمنان حقیقی‌اند)؛ و بشارت ده به (اینچنین) مؤمنان!'),
(1348, 1348, 'مَا كَانَ لِلنَّبِيِّ وَالَّذِينَ آمَنُوا أَنْ يَسْتَغْفِرُوا لِلْمُشْرِكِينَ وَلَوْ كَانُوا أُولِي قُرْبَىٰ مِنْ بَعْدِ مَا تَبَيَّنَ لَهُمْ أَنَّهُمْ أَصْحَابُ الْجَحِيمِ', 9, 113, 11, 41, 205, 'برای پیامبر و مؤمنان، شایسته نبود که برای مشرکان (از خداوند) طلب آمرزش کنند، هر چند از نزدیکانشان باشند؛ (آن هم) پس از آنکه بر آنها روشن شد که این گروه، اهل دوزخند!'),
(1349, 1349, 'وَمَا كَانَ اسْتِغْفَارُ إِبْرَاهِيمَ لِأَبِيهِ إِلَّا عَنْ مَوْعِدَةٍ وَعَدَهَا إِيَّاهُ فَلَمَّا تَبَيَّنَ لَهُ أَنَّهُ عَدُوٌّ لِلَّهِ تَبَرَّأَ مِنْهُ ۚ إِنَّ إِبْرَاهِيمَ لَأَوَّاهٌ حَلِيمٌ', 9, 114, 11, 41, 205, 'و استغفار ابراهیم برای پدرش = عمویش آزر، فقط بخاطر وعده‌ای بود که به او داده بود (تا وی را بسوی ایمان جذب کند)؛ امّا هنگامی که برای او روشن شد که وی دشمن خداست، از او بیزاری جست؛ به یقین، ابراهیم مهربان و بردبار بود!'),
(1350, 1350, 'وَمَا كَانَ اللَّهُ لِيُضِلَّ قَوْمًا بَعْدَ إِذْ هَدَاهُمْ حَتَّىٰ يُبَيِّنَ لَهُمْ مَا يَتَّقُونَ ۚ إِنَّ اللَّهَ بِكُلِّ شَيْءٍ عَلِيمٌ', 9, 115, 11, 41, 205, 'چنان نبود که خداوند قومی را، پس از آن که آنها را هدایت کرد (و ایمان آوردند) گمراه (و مجازات) کند؛ مگر آنکه اموری را که باید از آن بپرهیزند، برای آنان بیان نماید (و آنها مخالفت کنند)؛ زیرا خداوند به هر چیزی داناست!'),
(1351, 1351, 'إِنَّ اللَّهَ لَهُ مُلْكُ السَّمَاوَاتِ وَالْأَرْضِ ۖ يُحْيِي وَيُمِيتُ ۚ وَمَا لَكُمْ مِنْ دُونِ اللَّهِ مِنْ وَلِيٍّ وَلَا نَصِيرٍ', 9, 116, 11, 41, 205, 'حکومت آسمانها و زمین تنها از آن خداست؛ زنده می‌کند و می‌میراند؛ و جز خدا، ولیّ و یاوری ندارید!'),
(1352, 1352, 'لَقَدْ تَابَ اللَّهُ عَلَى النَّبِيِّ وَالْمُهَاجِرِينَ وَالْأَنْصَارِ الَّذِينَ اتَّبَعُوهُ فِي سَاعَةِ الْعُسْرَةِ مِنْ بَعْدِ مَا كَادَ يَزِيغُ قُلُوبُ فَرِيقٍ مِنْهُمْ ثُمَّ تَابَ عَلَيْهِمْ ۚ إِنَّهُ بِهِمْ رَءُوفٌ رَحِيمٌ', 9, 117, 11, 41, 205, 'مسلّماً خداوند رحمت خود را شامل حال پیامبر و مهاجران و انصار، که در زمان عسرت و شدّت (در جنگ تبوک) از او پیروی کردند، نمود؛ بعد از آنکه نزدیک بود دلهای گروهی از آنها، از حقّ منحرف شود (و از میدان جنگ بازگردند)؛ سپس خدا توبه آنها را پذیرفت، که او نسبت به آنان مهربان و رحیم است!'),
(1353, 1353, 'وَعَلَى الثَّلَاثَةِ الَّذِينَ خُلِّفُوا حَتَّىٰ إِذَا ضَاقَتْ عَلَيْهِمُ الْأَرْضُ بِمَا رَحُبَتْ وَضَاقَتْ عَلَيْهِمْ أَنْفُسُهُمْ وَظَنُّوا أَنْ لَا مَلْجَأَ مِنَ اللَّهِ إِلَّا إِلَيْهِ ثُمَّ تَابَ عَلَيْهِمْ لِيَتُوبُوا ۚ إِنَّ اللَّهَ هُوَ التَّوَّابُ الرَّحِيمُ', 9, 118, 11, 41, 206, '(همچنین) آن سه نفر که (از شرکت در جنگ تبوک) تخلّف جستند، (و مسلمانان با آنان قطع رابطه نمودند،) تا آن حدّ که زمین با همه وسعتش بر آنها تنگ شد؛ (حتّی) در وجود خویش، جایی برای خود نمی‌یافتند؛ (در آن هنگام) دانستند پناهگاهی از خدا جز بسوی او نیست؛ سپس خدا رحمتش را شامل حال آنها نمود، (و به آنان توفیق داد) تا توبه کنند؛ خداوند بسیار توبه‌پذیر و مهربان است!'),
(1354, 1354, 'يَا أَيُّهَا الَّذِينَ آمَنُوا اتَّقُوا اللَّهَ وَكُونُوا مَعَ الصَّادِقِينَ', 9, 119, 11, 41, 206, 'ای کسانی که ایمان آورده‌اید! از (مخالفت فرمان) خدا بپرهیزید، و با صادقان باشید!'),
(1355, 1355, 'مَا كَانَ لِأَهْلِ الْمَدِينَةِ وَمَنْ حَوْلَهُمْ مِنَ الْأَعْرَابِ أَنْ يَتَخَلَّفُوا عَنْ رَسُولِ اللَّهِ وَلَا يَرْغَبُوا بِأَنْفُسِهِمْ عَنْ نَفْسِهِ ۚ ذَٰلِكَ بِأَنَّهُمْ لَا يُصِيبُهُمْ ظَمَأٌ وَلَا نَصَبٌ وَلَا مَخْمَصَةٌ فِي سَبِيلِ اللَّهِ وَلَا يَطَئُونَ مَوْطِئًا يَغِيظُ الْكُفَّارَ وَلَا يَنَالُونَ مِنْ عَدُوٍّ نَيْلًا إِلَّا كُتِبَ لَهُمْ بِهِ عَمَلٌ صَالِحٌ ۚ إِنَّ اللَّهَ لَا يُضِيعُ أَجْرَ الْمُحْسِنِينَ', 9, 120, 11, 41, 206, 'سزاوار نیست که اهل مدینه، و بادیه‌نشینانی که اطراف آنها هستند، از رسول خدا تخلّف جویند؛ و برای حفظ جان خویش، از جان او چشم بپوشند! این بخاطر آن است که هیچ تشنگی و خستگی، و گرسنگی در راه خدا به آنها نمی‌رسد و هیچ گامی که موجب خشم کافران می‌شود برنمی‌دارند، و ضربه‌ای از دشمن نمی‌خورند، مگر اینکه به خاطر آن، عمل صالحی برای آنها نوشته می‌شود؛ زیرا خداوند پاداش نیکوکاران را تباه نمی‌کند!'),
(1356, 1356, 'وَلَا يُنْفِقُونَ نَفَقَةً صَغِيرَةً وَلَا كَبِيرَةً وَلَا يَقْطَعُونَ وَادِيًا إِلَّا كُتِبَ لَهُمْ لِيَجْزِيَهُمُ اللَّهُ أَحْسَنَ مَا كَانُوا يَعْمَلُونَ', 9, 121, 11, 41, 206, 'و هیچ مال کوچک یا بزرگی را (در این راه) انفاق نمی‌کنند، و هیچ سرزمینی را (بسوی میدان جهاد و یا در بازگشت) نمی‌پیمایند، مگر اینکه برای آنها نوشته می‌شود؛ تا خداوند آن را بعنوان بهترین اعمالشان، پاداش دهد.'),
(1357, 1357, ' وَمَا كَانَ الْمُؤْمِنُونَ لِيَنْفِرُوا كَافَّةً ۚ فَلَوْلَا نَفَرَ مِنْ كُلِّ فِرْقَةٍ مِنْهُمْ طَائِفَةٌ لِيَتَفَقَّهُوا فِي الدِّينِ وَلِيُنْذِرُوا قَوْمَهُمْ إِذَا رَجَعُوا إِلَيْهِمْ لَعَلَّهُمْ يَحْذَرُونَ', 9, 122, 11, 42, 206, 'شایسته نیست مؤمنان همگی (بسوی میدان جهاد) کوچ کنند؛ چرا از هر گروهی از آنان، طایفه‌ای کوچ نمی‌کند (و طایفه‌ای در مدینه بماند)، تا در دین (و معارف و احکام اسلام) آگاهی یابند و به هنگام بازگشت بسوی قوم خود، آنها را بیم دهند؟! شاید (از مخالفت فرمان پروردگار) بترسند، و خودداری کنند!'),
(1358, 1358, 'يَا أَيُّهَا الَّذِينَ آمَنُوا قَاتِلُوا الَّذِينَ يَلُونَكُمْ مِنَ الْكُفَّارِ وَلْيَجِدُوا فِيكُمْ غِلْظَةً ۚ وَاعْلَمُوا أَنَّ اللَّهَ مَعَ الْمُتَّقِينَ', 9, 123, 11, 42, 207, 'ای کسانی که ایمان آورده‌اید! با کافرانی که به شما نزدیکترند، پیکار کنید! (و دشمن دورتر، شما را از دشمنان نزدیک غافل نکند!) آنها باید در شما شدّت و خشونت (و قدرت) احساس کنند؛ و بدانید خداوند با پرهیزگاران است!'),
(1359, 1359, 'وَإِذَا مَا أُنْزِلَتْ سُورَةٌ فَمِنْهُمْ مَنْ يَقُولُ أَيُّكُمْ زَادَتْهُ هَٰذِهِ إِيمَانًا ۚ فَأَمَّا الَّذِينَ آمَنُوا فَزَادَتْهُمْ إِيمَانًا وَهُمْ يَسْتَبْشِرُونَ', 9, 124, 11, 42, 207, 'و هنگامی که سوره‌ای نازل می‌شود، بعضی از آنان (به دیگران) می‌گویند: «این سوره، ایمان کدام یک از شما را افزون ساخت؟!» (به آنها بگو:) اما کسانی که ایمان آورده‌اند، بر ایمانشان افزوده؛ و آنها (به فضل و رحمت الهی) خوشحالند.'),
(1360, 1360, 'وَأَمَّا الَّذِينَ فِي قُلُوبِهِمْ مَرَضٌ فَزَادَتْهُمْ رِجْسًا إِلَىٰ رِجْسِهِمْ وَمَاتُوا وَهُمْ كَافِرُونَ', 9, 125, 11, 42, 207, 'و امّا آنها که در دلهایشان بیماری است، پلیدی بر پلیدیشان افزوده؛ و از دنیا رفتند در حالی که کافر بودند.'),
(1361, 1361, 'أَوَلَا يَرَوْنَ أَنَّهُمْ يُفْتَنُونَ فِي كُلِّ عَامٍ مَرَّةً أَوْ مَرَّتَيْنِ ثُمَّ لَا يَتُوبُونَ وَلَا هُمْ يَذَّكَّرُونَ', 9, 126, 11, 42, 207, 'آیا آنها نمی‌بینند که در هر سال، یک یا دو بار آزمایش می‌شوند؟! باز توبه نمی‌کنند، و متذکّر هم نمی‌گردند!'),
(1362, 1362, 'وَإِذَا مَا أُنْزِلَتْ سُورَةٌ نَظَرَ بَعْضُهُمْ إِلَىٰ بَعْضٍ هَلْ يَرَاكُمْ مِنْ أَحَدٍ ثُمَّ انْصَرَفُوا ۚ صَرَفَ اللَّهُ قُلُوبَهُمْ بِأَنَّهُمْ قَوْمٌ لَا يَفْقَهُونَ', 9, 127, 11, 42, 207, 'و هنگامی که سوره‌ای نازل می‌شود، بعضی از آنها = منافقان‌ به یکدیگر نگاه می‌کنند و می‌گویند: «آیا کسی شما را می‌بیند؟ (اگر از حضور پیامبر بیرون رویم، کسی متوجّه ما نمی‌شود!)» سپس منصرف می‌شوند (و بیرون می‌روند)؛ خداوند دلهایشان را (از حق) منصرف ساخته؛ چرا که آنها، گروهی هستند که نمی‌فهمند (و بی‌دانشند)!'),
(1363, 1363, 'لَقَدْ جَاءَكُمْ رَسُولٌ مِنْ أَنْفُسِكُمْ عَزِيزٌ عَلَيْهِ مَا عَنِتُّمْ حَرِيصٌ عَلَيْكُمْ بِالْمُؤْمِنِينَ رَءُوفٌ رَحِيمٌ', 9, 128, 11, 42, 207, 'به یقین، رسولی از خود شما بسویتان آمد که رنجهای شما بر او سخت است؛ و اصرار بر هدایت شما دارد؛ و نسبت به مؤمنان، رئوف و مهربان است!'),
(1364, 1364, 'فَإِنْ تَوَلَّوْا فَقُلْ حَسْبِيَ اللَّهُ لَا إِلَٰهَ إِلَّا هُوَ ۖ عَلَيْهِ تَوَكَّلْتُ ۖ وَهُوَ رَبُّ الْعَرْشِ الْعَظِيمِ', 9, 129, 11, 42, 207, 'اگر آنها (از حق) روی بگردانند، (نگران مباش!) بگو: «خداوند مرا کفایت می‌کند؛ هیچ معبودی جز او نیست؛ بر او توکّل کردم؛ و او صاحب عرش بزرگ است!»'),
(1365, 1365, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ الر ۚ تِلْكَ آيَاتُ الْكِتَابِ الْحَكِيمِ', 10, 1, 11, 42, 208, 'الر، این آیات کتاب استوار و حکمت آمیز است!'),
(1366, 1366, 'أَكَانَ لِلنَّاسِ عَجَبًا أَنْ أَوْحَيْنَا إِلَىٰ رَجُلٍ مِنْهُمْ أَنْ أَنْذِرِ النَّاسَ وَبَشِّرِ الَّذِينَ آمَنُوا أَنَّ لَهُمْ قَدَمَ صِدْقٍ عِنْدَ رَبِّهِمْ ۗ قَالَ الْكَافِرُونَ إِنَّ هَٰذَا لَسَاحِرٌ مُبِينٌ', 10, 2, 11, 42, 208, 'آیا برای مردم، موجب شگفتی بود که به مردی از آنها وحی فرستادیم که مردم را (از عواقب کفر و گناه) بترسان، و به کسانی که ایمان آورده‌اند بشارت ده که برای آنها، سابقه نیک (و پاداشهای مسلّم) نزد پروردگارشان است؟! (امّا) کافران گفتند: «این مرد، ساحر آشکاری است!»'),
(1367, 1367, 'إِنَّ رَبَّكُمُ اللَّهُ الَّذِي خَلَقَ السَّمَاوَاتِ وَالْأَرْضَ فِي سِتَّةِ أَيَّامٍ ثُمَّ اسْتَوَىٰ عَلَى الْعَرْشِ ۖ يُدَبِّرُ الْأَمْرَ ۖ مَا مِنْ شَفِيعٍ إِلَّا مِنْ بَعْدِ إِذْنِهِ ۚ ذَٰلِكُمُ اللَّهُ رَبُّكُمْ فَاعْبُدُوهُ ۚ أَفَلَا تَذَكَّرُونَ', 10, 3, 11, 42, 208, 'پروردگار شما، خداوندی است که آسمانها و زمین را در شش روز = شش دوران‌ آفرید؛ سپس بر تخت (قدرت) قرار گرفت، و به تدبیر کار (جهان) پرداخت؛ هیچ شفاعت کننده‌ای، جز با اذن او نیست؛ این است خداوند، پروردگار شما! پس او را پرستش کنید! آیا متذکّر نمی‌شوید؟!'),
(1368, 1368, 'إِلَيْهِ مَرْجِعُكُمْ جَمِيعًا ۖ وَعْدَ اللَّهِ حَقًّا ۚ إِنَّهُ يَبْدَأُ الْخَلْقَ ثُمَّ يُعِيدُهُ لِيَجْزِيَ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ بِالْقِسْطِ ۚ وَالَّذِينَ كَفَرُوا لَهُمْ شَرَابٌ مِنْ حَمِيمٍ وَعَذَابٌ أَلِيمٌ بِمَا كَانُوا يَكْفُرُونَ', 10, 4, 11, 42, 208, 'بازگشت همه شما بسوی اوست! خداوند وعده حقّی فرموده؛ او آفرینش را آغاز می‌کند، سپس آن را بازمی‌گرداند، تا کسانی را که ایمان آورده و کارهای شایسته انجام داده‌اند، بعدالت جزا دهد؛ و برای کسانی که کافر شدند، نوشیدنی از مایع سوزان است؛ و عذابی دردناک، بخاطر آنکه کفر می‌ورزیدند!'),
(1369, 1369, 'هُوَ الَّذِي جَعَلَ الشَّمْسَ ضِيَاءً وَالْقَمَرَ نُورًا وَقَدَّرَهُ مَنَازِلَ لِتَعْلَمُوا عَدَدَ السِّنِينَ وَالْحِسَابَ ۚ مَا خَلَقَ اللَّهُ ذَٰلِكَ إِلَّا بِالْحَقِّ ۚ يُفَصِّلُ الْآيَاتِ لِقَوْمٍ يَعْلَمُونَ', 10, 5, 11, 42, 208, 'او کسی است که خورشید را روشنایی، و ماه را نور قرار داد؛ و برای آن منزلگاه‌هایی مقدّر کرد، تا عدد سالها و حساب (کارها) را بدانید؛ خداوند این را جز بحق نیافریده؛ او آیات (خود را) برای گروهی که اهل دانشند، شرح می‌دهد!'),
(1370, 1370, 'إِنَّ فِي اخْتِلَافِ اللَّيْلِ وَالنَّهَارِ وَمَا خَلَقَ اللَّهُ فِي السَّمَاوَاتِ وَالْأَرْضِ لَآيَاتٍ لِقَوْمٍ يَتَّقُونَ', 10, 6, 11, 42, 208, 'مسلّماً در آمد و شد شب و روز، و آنچه خداوند در آسمانها و زمین آفریده، آیات (و نشانه‌هایی) است برای گروهی که پرهیزگارند (و حقایق را می‌بینند).'),
(1371, 1371, 'إِنَّ الَّذِينَ لَا يَرْجُونَ لِقَاءَنَا وَرَضُوا بِالْحَيَاةِ الدُّنْيَا وَاطْمَأَنُّوا بِهَا وَالَّذِينَ هُمْ عَنْ آيَاتِنَا غَافِلُونَ', 10, 7, 11, 42, 209, 'آنها که ایمان به ملاقات ما (و روز رستاخیز) ندارند، و به زندگی دنیا خشنود شدند و بر آن تکیه کردند، و آنها که از آیات ما غافلند،'),
(1372, 1372, 'أُولَٰئِكَ مَأْوَاهُمُ النَّارُ بِمَا كَانُوا يَكْسِبُونَ', 10, 8, 11, 42, 209, '(همه) آنها جایگاهشان آتش است، بخاطر کارهایی که انجام می‌دادند!'),
(1373, 1373, 'إِنَّ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ يَهْدِيهِمْ رَبُّهُمْ بِإِيمَانِهِمْ ۖ تَجْرِي مِنْ تَحْتِهِمُ الْأَنْهَارُ فِي جَنَّاتِ النَّعِيمِ', 10, 9, 11, 42, 209, '(ولی) کسانی که ایمان آوردند و کارهای شایسته انجام دادند، پروردگارشان آنها را در پرتو ایمانشان هدایت می‌کند؛ از زیر (قصرهای) آنها در باغهای بهشت، نهرها جاری است.'),
(1374, 1374, 'دَعْوَاهُمْ فِيهَا سُبْحَانَكَ اللَّهُمَّ وَتَحِيَّتُهُمْ فِيهَا سَلَامٌ ۚ وَآخِرُ دَعْوَاهُمْ أَنِ الْحَمْدُ لِلَّهِ رَبِّ الْعَالَمِينَ', 10, 10, 11, 42, 209, 'گفتار (و دعای) آنها در بهشت این است که: «خداوندا، منزهی تو!» و تحیّت آنها در آنجا: سلام؛ و آخرین سخنشان این است که: «حمد، مخصوص پروردگار عالمیان است!»'),
(1375, 1375, ' وَلَوْ يُعَجِّلُ اللَّهُ لِلنَّاسِ الشَّرَّ اسْتِعْجَالَهُمْ بِالْخَيْرِ لَقُضِيَ إِلَيْهِمْ أَجَلُهُمْ ۖ فَنَذَرُ الَّذِينَ لَا يَرْجُونَ لِقَاءَنَا فِي طُغْيَانِهِمْ يَعْمَهُونَ', 10, 11, 11, 42, 209, 'اگر همان گونه که مردم در به دست آوردن «خوبی» ها عجله دارند، خداوند در مجازاتشان شتاب می‌کرد، (بزودی) عمرشان به پایان می‌رسید (و همگی نابود می‌شدند)؛ ولی کسانی را که ایمان به لقای ما ندارند، به حال خود رها می‌کنیم تا در طغیانشان سرگردان شوند!'),
(1376, 1376, 'وَإِذَا مَسَّ الْإِنْسَانَ الضُّرُّ دَعَانَا لِجَنْبِهِ أَوْ قَاعِدًا أَوْ قَائِمًا فَلَمَّا كَشَفْنَا عَنْهُ ضُرَّهُ مَرَّ كَأَنْ لَمْ يَدْعُنَا إِلَىٰ ضُرٍّ مَسَّهُ ۚ كَذَٰلِكَ زُيِّنَ لِلْمُسْرِفِينَ مَا كَانُوا يَعْمَلُونَ', 10, 12, 11, 42, 209, 'هنگامی که به انسان زیان (و ناراحتی) رسد، ما را (در هر حال:) در حالی که به پهلو خوابیده، یا نشسته، یا ایستاده است، می‌خواند؛ امّا هنگامی که ناراحتی را از او برطرف ساختیم، چنان می‌رود که گویی هرگز ما را برای حل مشکلی که به او رسیده بود، نخوانده است! این گونه برای اسرافکاران، اعمالشان زینت داده شده است (که زشتی این عمل را درک نمی‌کنند)!'),
(1377, 1377, 'وَلَقَدْ أَهْلَكْنَا الْقُرُونَ مِنْ قَبْلِكُمْ لَمَّا ظَلَمُوا ۙ وَجَاءَتْهُمْ رُسُلُهُمْ بِالْبَيِّنَاتِ وَمَا كَانُوا لِيُؤْمِنُوا ۚ كَذَٰلِكَ نَجْزِي الْقَوْمَ الْمُجْرِمِينَ', 10, 13, 11, 42, 209, 'ما امّتهای پیش از شما را، هنگامی که ظلم کردند، هلاک نمودیم؛ در حالی که پیامبرانشان دلایل روشن برای آنها آوردند، ولی آنها ایمان نیاوردند؛ این‌گونه گروه مجرمان را کیفر می‌دهیم!'),
(1378, 1378, 'ثُمَّ جَعَلْنَاكُمْ خَلَائِفَ فِي الْأَرْضِ مِنْ بَعْدِهِمْ لِنَنْظُرَ كَيْفَ تَعْمَلُونَ', 10, 14, 11, 42, 209, 'سپس شما را جانشینان آنها در روی زمین -پس از ایشان- قرار دادیم؛ تا ببینیم شما چگونه عمل می‌کنید!'),
(1379, 1379, 'وَإِذَا تُتْلَىٰ عَلَيْهِمْ آيَاتُنَا بَيِّنَاتٍ ۙ قَالَ الَّذِينَ لَا يَرْجُونَ لِقَاءَنَا ائْتِ بِقُرْآنٍ غَيْرِ هَٰذَا أَوْ بَدِّلْهُ ۚ قُلْ مَا يَكُونُ لِي أَنْ أُبَدِّلَهُ مِنْ تِلْقَاءِ نَفْسِي ۖ إِنْ أَتَّبِعُ إِلَّا مَا يُوحَىٰ إِلَيَّ ۖ إِنِّي أَخَافُ إِنْ عَصَيْتُ رَبِّي عَذَابَ يَوْمٍ عَظِيمٍ', 10, 15, 11, 42, 210, 'و هنگامی که آیات روشن ما بر آنها خوانده می‌شود، کسانی که ایمان به لقای ما (و روز رستاخیز) ندارند می‌گویند: «قرآنی غیر از این بیاور، یا آن را تبدیل کن! (و آیات نکوهش بتها را بردار)» بگو: «من حق ندارم که از پیش خود آن را تغییر دهم؛ فقط از چیزی که بر من وحی می‌شود، پیروی می‌کنم! من اگر پروردگارم را نافرمانی کنم، از مجازات روز بزرگ (قیامت) می‌ترسم!»'),
(1380, 1380, 'قُلْ لَوْ شَاءَ اللَّهُ مَا تَلَوْتُهُ عَلَيْكُمْ وَلَا أَدْرَاكُمْ بِهِ ۖ فَقَدْ لَبِثْتُ فِيكُمْ عُمُرًا مِنْ قَبْلِهِ ۚ أَفَلَا تَعْقِلُونَ', 10, 16, 11, 42, 210, 'بگو: «اگر خدا می‌خواست، من این آیات را بر شما نمی‌خواندم؛ و خداوند از آن آگاهتان نمی‌کرد؛ چه اینکه مدّتها پیش از این، در میان شما زندگی نمودم؛ (و هرگز آیه‌ای نیاوردم؛) آیا نمی‌فهمید؟!»'),
(1381, 1381, 'فَمَنْ أَظْلَمُ مِمَّنِ افْتَرَىٰ عَلَى اللَّهِ كَذِبًا أَوْ كَذَّبَ بِآيَاتِهِ ۚ إِنَّهُ لَا يُفْلِحُ الْمُجْرِمُونَ', 10, 17, 11, 42, 210, 'چه کسی ستمکارتر است از آن کس که بر خدا دروغ می‌بندد، یا آیات او را تکذیب می‌کند؟! مسلماً مجرمان رستگار نخواهند شد!'),
(1382, 1382, 'وَيَعْبُدُونَ مِنْ دُونِ اللَّهِ مَا لَا يَضُرُّهُمْ وَلَا يَنْفَعُهُمْ وَيَقُولُونَ هَٰؤُلَاءِ شُفَعَاؤُنَا عِنْدَ اللَّهِ ۚ قُلْ أَتُنَبِّئُونَ اللَّهَ بِمَا لَا يَعْلَمُ فِي السَّمَاوَاتِ وَلَا فِي الْأَرْضِ ۚ سُبْحَانَهُ وَتَعَالَىٰ عَمَّا يُشْرِكُونَ', 10, 18, 11, 42, 210, 'آنها غیر از خدا، چیزهایی را می‌پرستند که نه به آنان زیان می‌رساند، و نه سودی می‌بخشد؛ و می‌گویند: «اینها شفیعان ما نزد خدا هستند!» بگو: «آیا خدا را به چیزی خبر می‌دهید که در آسمانها و زمین سراغ ندارد؟!» منزه است او، و برتر است از آن همتایانی که قرار می‌دهند!'),
(1383, 1383, 'وَمَا كَانَ النَّاسُ إِلَّا أُمَّةً وَاحِدَةً فَاخْتَلَفُوا ۚ وَلَوْلَا كَلِمَةٌ سَبَقَتْ مِنْ رَبِّكَ لَقُضِيَ بَيْنَهُمْ فِيمَا فِيهِ يَخْتَلِفُونَ', 10, 19, 11, 42, 210, '(در آغاز) همه مردم امّت واحدی بودند؛ سپس اختلاف کردند؛ و اگر فرمانی از طرف پروردگارت (درباره عدم مجازات سریع آنان) از قبل صادر نشده بود، در میان آنها در آنچه اختلاف داشتند داوری می‌شد (و سپس همگی به مجازات می‌رسیدند).'),
(1384, 1384, 'وَيَقُولُونَ لَوْلَا أُنْزِلَ عَلَيْهِ آيَةٌ مِنْ رَبِّهِ ۖ فَقُلْ إِنَّمَا الْغَيْبُ لِلَّهِ فَانْتَظِرُوا إِنِّي مَعَكُمْ مِنَ الْمُنْتَظِرِينَ', 10, 20, 11, 42, 210, 'می‌گویند: «چرا معجزه‌ای از پروردگارش بر او نازل نمی‌شود؟!» بگو: «غیب (و معجزات) تنها برای خدا (و به فرمان او) است! شما در انتظار باشید، من هم با شما در انتظارم! (شما در انتظار معجزات بهانه‌جویانه باشید، و من هم در انتظار مجازات شما!)»'),
(1385, 1385, 'وَإِذَا أَذَقْنَا النَّاسَ رَحْمَةً مِنْ بَعْدِ ضَرَّاءَ مَسَّتْهُمْ إِذَا لَهُمْ مَكْرٌ فِي آيَاتِنَا ۚ قُلِ اللَّهُ أَسْرَعُ مَكْرًا ۚ إِنَّ رُسُلَنَا يَكْتُبُونَ مَا تَمْكُرُونَ', 10, 21, 11, 42, 211, 'هنگامی که به مردم، پس از ناراحتی که به آنها رسیده است، رحمتی بچشانیم، در آیات ما نیرنگ می‌کنند (، و برای آن نعمت و رحمت توجیهات ناروا می‌کنند)؛ بگو: «خداوند سریعتر از شما مکر = چاره‌جویی‌ می‌کند؛ و رسولان = فرشتگان ما، آنچه نیرنگ می‌کنید (و نقشه می‌کشید)، می‌نویسند!»'),
(1386, 1386, 'هُوَ الَّذِي يُسَيِّرُكُمْ فِي الْبَرِّ وَالْبَحْرِ ۖ حَتَّىٰ إِذَا كُنْتُمْ فِي الْفُلْكِ وَجَرَيْنَ بِهِمْ بِرِيحٍ طَيِّبَةٍ وَفَرِحُوا بِهَا جَاءَتْهَا رِيحٌ عَاصِفٌ وَجَاءَهُمُ الْمَوْجُ مِنْ كُلِّ مَكَانٍ وَظَنُّوا أَنَّهُمْ أُحِيطَ بِهِمْ ۙ دَعَوُا اللَّهَ مُخْلِصِينَ لَهُ الدِّينَ لَئِنْ أَنْجَيْتَنَا مِنْ هَٰذِهِ لَنَكُونَنَّ مِنَ الشَّاكِرِينَ', 10, 22, 11, 42, 211, 'او کسی است که شما را در خشکی و دریا سیر می‌دهد؛ زمانی که در کشتی قرارمی‌گیرید، و بادهای موافق آنان را (بسوی مقصد) حرکت میدهد و خوشحال می‌شوند، ناگهان طوفان شدیدی می‌وزد؛ و امواج از هر سو به سراغ آنها می‌آید؛ و گمان می‌کنند هلاک خواهند شد؛ در آن هنگام، خدا را از روی اخلاص می‌خوانند که: «اگر ما را از این گرفتاری نجات دهی، حتماً از سپاسگزاران خواهیم بود!»'),
(1387, 1387, 'فَلَمَّا أَنْجَاهُمْ إِذَا هُمْ يَبْغُونَ فِي الْأَرْضِ بِغَيْرِ الْحَقِّ ۗ يَا أَيُّهَا النَّاسُ إِنَّمَا بَغْيُكُمْ عَلَىٰ أَنْفُسِكُمْ ۖ مَتَاعَ الْحَيَاةِ الدُّنْيَا ۖ ثُمَّ إِلَيْنَا مَرْجِعُكُمْ فَنُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُونَ', 10, 23, 11, 42, 211, 'امّا هنگامی که خدا آنها را رهایی بخشید، (باز) به ناحق، در زمین ستم میکنند. ای مردم! ستمهای شما، به زیان خود شماست! از زندگی دنیا بهره (میبرید)، سپس بازگشت شما بسوی ماست؛ و ما، شما را به آنچه عمل میکردید، خبر میدهیم!'),
(1388, 1388, 'إِنَّمَا مَثَلُ الْحَيَاةِ الدُّنْيَا كَمَاءٍ أَنْزَلْنَاهُ مِنَ السَّمَاءِ فَاخْتَلَطَ بِهِ نَبَاتُ الْأَرْضِ مِمَّا يَأْكُلُ النَّاسُ وَالْأَنْعَامُ حَتَّىٰ إِذَا أَخَذَتِ الْأَرْضُ زُخْرُفَهَا وَازَّيَّنَتْ وَظَنَّ أَهْلُهَا أَنَّهُمْ قَادِرُونَ عَلَيْهَا أَتَاهَا أَمْرُنَا لَيْلًا أَوْ نَهَارًا فَجَعَلْنَاهَا حَصِيدًا كَأَنْ لَمْ تَغْنَ بِالْأَمْسِ ۚ كَذَٰلِكَ نُفَصِّلُ الْآيَاتِ لِقَوْمٍ يَتَفَكَّرُونَ', 10, 24, 11, 42, 211, 'مثل زندگی دنیا، همانند آبی است که از آسمان نازل کرده‌ایم؛ که در پی آن، گیاهان (گوناگون) زمین -که مردم و چهارپایان از آن می‌خورند- می‌روید؛ تا زمانی که زمین، زیبایی خود را یافته و آراسته می‌گردد، و اهل آن مطمئن می‌شوند که می‌توانند از آن بهره‌مند گردند، (ناگهان) فرمان ما، شب‌هنگام یا در روز، (برای نابودی آن) فرامی‌رسد؛ (سرما یا صاعقه‌ای را بر آن مسلّط می‌سازیم؛) و آنچنان آن را درو می‌کنیم که گویی دیروز هرگز (چنین کشتزاری) نبوده است! این گونه، آیات خود را برای گروهی که می‌اندیشند، شرح می‌دهیم!'),
(1389, 1389, 'وَاللَّهُ يَدْعُو إِلَىٰ دَارِ السَّلَامِ وَيَهْدِي مَنْ يَشَاءُ إِلَىٰ صِرَاطٍ مُسْتَقِيمٍ', 10, 25, 11, 42, 211, 'و خداوند به سرای صلح و سلامت دعوت می‌کند؛ و هر کس را بخواهد (و شایسته و لایق ببیند)، به راه راست هدایت می‌نماید.'),
(1390, 1390, ' لِلَّذِينَ أَحْسَنُوا الْحُسْنَىٰ وَزِيَادَةٌ ۖ وَلَا يَرْهَقُ وُجُوهَهُمْ قَتَرٌ وَلَا ذِلَّةٌ ۚ أُولَٰئِكَ أَصْحَابُ الْجَنَّةِ ۖ هُمْ فِيهَا خَالِدُونَ', 10, 26, 11, 43, 212, 'کسانی که نیکی کردند، پاداش نیک و افزون بر آن دارند؛ و تاریکی و ذلّت، چهره‌هایشان را نمی‌پوشاند؛ آنها اهل بهشتند، و جاودانه در آن خواهند ماند.'),
(1391, 1391, 'وَالَّذِينَ كَسَبُوا السَّيِّئَاتِ جَزَاءُ سَيِّئَةٍ بِمِثْلِهَا وَتَرْهَقُهُمْ ذِلَّةٌ ۖ مَا لَهُمْ مِنَ اللَّهِ مِنْ عَاصِمٍ ۖ كَأَنَّمَا أُغْشِيَتْ وُجُوهُهُمْ قِطَعًا مِنَ اللَّيْلِ مُظْلِمًا ۚ أُولَٰئِكَ أَصْحَابُ النَّارِ ۖ هُمْ فِيهَا خَالِدُونَ', 10, 27, 11, 43, 212, 'امّا کسانی که مرتکب گناهان شدند، جزای بدی بمقدار آن دارند؛ و ذلّت و خواری، چهره آنان را می‌پوشاند؛ و هیچ چیز نمی‌تواند آنها را از (مجازات) خدا نگه دارد! (چهره‌هایشان آنچنان تاریک است که) گویی با پاره‌هایی از شب تاریک، صورت آنها پوشیده شده! آنها اهل دوزخند؛ و جاودانه در آن خواهند ماند!'),
(1392, 1392, 'وَيَوْمَ نَحْشُرُهُمْ جَمِيعًا ثُمَّ نَقُولُ لِلَّذِينَ أَشْرَكُوا مَكَانَكُمْ أَنْتُمْ وَشُرَكَاؤُكُمْ ۚ فَزَيَّلْنَا بَيْنَهُمْ ۖ وَقَالَ شُرَكَاؤُهُمْ مَا كُنْتُمْ إِيَّانَا تَعْبُدُونَ', 10, 28, 11, 43, 212, '(به خاطر بیاورید) روزی را که همه آنها را جمع می‌کنیم، سپس به مشرکان می‌گوییم: «شما و معبودهایتان در جای خودتان باشید (تا به حسابتان رسیدگی شود!)» سپس آنها را از هم جدا می‌سازیم (و از هر یک جداگانه سؤال می‌کنیم). و معبودهایشان (به آنها) می‌گویند: «شما (هرگز) ما را عبادت نمی‌کردید!»'),
(1393, 1393, 'فَكَفَىٰ بِاللَّهِ شَهِيدًا بَيْنَنَا وَبَيْنَكُمْ إِنْ كُنَّا عَنْ عِبَادَتِكُمْ لَغَافِلِينَ', 10, 29, 11, 43, 212, '(آنها در پاسخ می‌گویند:) همین بس که خدا میان ما و شما گواه باشد، اگر ما از عبادت شما غافل بودیم!'),
(1394, 1394, 'هُنَالِكَ تَبْلُو كُلُّ نَفْسٍ مَا أَسْلَفَتْ ۚ وَرُدُّوا إِلَى اللَّهِ مَوْلَاهُمُ الْحَقِّ ۖ وَضَلَّ عَنْهُمْ مَا كَانُوا يَفْتَرُونَ', 10, 30, 11, 43, 212, 'در آن جا، هر کس عملی را که قبلاً انجام داده است، می‌آزماید. و همگی بسوی «اللّه» -مولا و سرپرستِ حقیقی خود- بازگردانده می‌شوند؛ و چیزهایی را که بدروغ همتای خدا قرار داده بودند، گم و نابود می‌شوند!'),
(1395, 1395, 'قُلْ مَنْ يَرْزُقُكُمْ مِنَ السَّمَاءِ وَالْأَرْضِ أَمَّنْ يَمْلِكُ السَّمْعَ وَالْأَبْصَارَ وَمَنْ يُخْرِجُ الْحَيَّ مِنَ الْمَيِّتِ وَيُخْرِجُ الْمَيِّتَ مِنَ الْحَيِّ وَمَنْ يُدَبِّرُ الْأَمْرَ ۚ فَسَيَقُولُونَ اللَّهُ ۚ فَقُلْ أَفَلَا تَتَّقُونَ', 10, 31, 11, 43, 212, 'بگو: «چه کسی شما را از آسمان و زمین روزی می‌دهد؟ یا چه کسی مالک (و خالق) گوش و چشمهاست؟ و چه کسی زنده را از مرده، و مرده را از زنده بیرون می‌آورد؟ و چه کسی امور (جهان) را تدبیر می‌کند؟» بزودی (در پاسخ) می‌گویند: «خدا»، بگو: «پس چرا تقوا پیشه نمی‌کنید (و از خدا نمی‌ترسید)؟!'),
(1396, 1396, 'فَذَٰلِكُمُ اللَّهُ رَبُّكُمُ الْحَقُّ ۖ فَمَاذَا بَعْدَ الْحَقِّ إِلَّا الضَّلَالُ ۖ فَأَنَّىٰ تُصْرَفُونَ', 10, 32, 11, 43, 212, 'آن است خداوند، پروردگار حقّ شما (دارای همه این صفات)! با این حال، بعد از حق، چه چیزی جز گمراهی وجود دارد؟! پس چرا (از پرستش او) روی گردان می‌شوید؟!'),
(1397, 1397, 'كَذَٰلِكَ حَقَّتْ كَلِمَتُ رَبِّكَ عَلَى الَّذِينَ فَسَقُوا أَنَّهُمْ لَا يُؤْمِنُونَ', 10, 33, 11, 43, 212, 'اینچنین فرمان پروردگارت بر فاسقان مسلّم شده که آنها (پس از این همه لجاجت و گناه)، ایمان نخواهند آورد!'),
(1398, 1398, 'قُلْ هَلْ مِنْ شُرَكَائِكُمْ مَنْ يَبْدَأُ الْخَلْقَ ثُمَّ يُعِيدُهُ ۚ قُلِ اللَّهُ يَبْدَأُ الْخَلْقَ ثُمَّ يُعِيدُهُ ۖ فَأَنَّىٰ تُؤْفَكُونَ', 10, 34, 11, 43, 213, 'بگو: «آیا هیچ یک از معبودهای شما، آفرینش را ایجاد می‌کند و سپس بازمی‌گرداند؟!» بگو: «تنها خدا آفرینش را ایجاد کرده، سپس بازمی‌گرداند؛ با این حال، چرا از حق روی‌گردان می‌شوید؟!»'),
(1399, 1399, 'قُلْ هَلْ مِنْ شُرَكَائِكُمْ مَنْ يَهْدِي إِلَى الْحَقِّ ۚ قُلِ اللَّهُ يَهْدِي لِلْحَقِّ ۗ أَفَمَنْ يَهْدِي إِلَى الْحَقِّ أَحَقُّ أَنْ يُتَّبَعَ أَمَّنْ لَا يَهِدِّي إِلَّا أَنْ يُهْدَىٰ ۖ فَمَا لَكُمْ كَيْفَ تَحْكُمُونَ', 10, 35, 11, 43, 213, 'بگو: «آیا هیچ یک از معبودهای شما، به سوی حق هدایت می‌کند؟! بگو: «تنها خدا به حق هدایت می‌کند! آیا کسی که هدایت به سوی حق می‌کند برای پیروی شایسته‌تر است، یا آن کس که خود هدایت نمی‌شود مگر هدایتش کنند؟ شما را چه می‌شود، چگونه داوری می‌کنید؟!»'),
(1400, 1400, 'وَمَا يَتَّبِعُ أَكْثَرُهُمْ إِلَّا ظَنًّا ۚ إِنَّ الظَّنَّ لَا يُغْنِي مِنَ الْحَقِّ شَيْئًا ۚ إِنَّ اللَّهَ عَلِيمٌ بِمَا يَفْعَلُونَ', 10, 36, 11, 43, 213, 'و بیشتر آنها، جز از گمان (و پندارهای بی‌اساس)، پیروی نمی‌کنند؛ (در حالی که) گمان، هرگز انسان را از حقّ بی‌نیاز نمی‌سازد (و به حق نمی‌رساند)! به یقین، خداوند از آنچه انجام می‌دهند، آگاه است!'),
(1401, 1401, 'وَمَا كَانَ هَٰذَا الْقُرْآنُ أَنْ يُفْتَرَىٰ مِنْ دُونِ اللَّهِ وَلَٰكِنْ تَصْدِيقَ الَّذِي بَيْنَ يَدَيْهِ وَتَفْصِيلَ الْكِتَابِ لَا رَيْبَ فِيهِ مِنْ رَبِّ الْعَالَمِينَ', 10, 37, 11, 43, 213, 'شایسته نبود (و امکان نداشت) که این قرآن، بدون وحی الهی به خدا نسبت داده شود؛ ولی تصدیقی است برای آنچه پیش از آن است (از کتب آسمانی)، و شرح و تفصیلی بر آنها است؛ شکّی در آن نیست، و از طرف پروردگار جهانیان است.'),
(1402, 1402, 'أَمْ يَقُولُونَ افْتَرَاهُ ۖ قُلْ فَأْتُوا بِسُورَةٍ مِثْلِهِ وَادْعُوا مَنِ اسْتَطَعْتُمْ مِنْ دُونِ اللَّهِ إِنْ كُنْتُمْ صَادِقِينَ', 10, 38, 11, 43, 213, 'آیا آنها می‌گویند: «او قرآن را بدروغ به خدا نسبت داده است»؟! بگو: «اگر راست می گویید، یک سوره همانند آن بیاورید؛ و غیر از خدا، هر کس را می‌توانید (به یاری) طلبید!»'),
(1403, 1403, 'بَلْ كَذَّبُوا بِمَا لَمْ يُحِيطُوا بِعِلْمِهِ وَلَمَّا يَأْتِهِمْ تَأْوِيلُهُ ۚ كَذَٰلِكَ كَذَّبَ الَّذِينَ مِنْ قَبْلِهِمْ ۖ فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ الظَّالِمِينَ', 10, 39, 11, 43, 213, '(ولی آنها از روی علم و دانش قرآن را انکار نکردند؛) بلکه چیزی را تکذیب کردند که آگاهی از آن نداشتند، و هنوز واقعیتش بر آنان روشن نشده است! پیشینیان آنها نیز همین گونه تکذیب کردند؛ پس بنگر عاقبت کار ظالمان چگونه بود!'),
(1404, 1404, 'وَمِنْهُمْ مَنْ يُؤْمِنُ بِهِ وَمِنْهُمْ مَنْ لَا يُؤْمِنُ بِهِ ۚ وَرَبُّكَ أَعْلَمُ بِالْمُفْسِدِينَ', 10, 40, 11, 43, 213, 'بعضی از آنها، به آن ایمان می‌آورند؛ و بعضی ایمان نمی‌آورند؛ و پروردگارت به مفسدان آگاهتر است (و آنها را بهتر می‌شناسد)!');
INSERT INTO `Quran` (`rowid`, `id`, `Arabic`, `Chapter`, `Verse`, `Juz`, `Hizb`, `Page`, `tr`) VALUES
(1405, 1405, 'وَإِنْ كَذَّبُوكَ فَقُلْ لِي عَمَلِي وَلَكُمْ عَمَلُكُمْ ۖ أَنْتُمْ بَرِيئُونَ مِمَّا أَعْمَلُ وَأَنَا بَرِيءٌ مِمَّا تَعْمَلُونَ', 10, 41, 11, 43, 213, 'و اگر تو را تکذیب کردند، بگو: «عمل من برای من، و عمل شما برای شماست! شما از آنچه من انجام می‌دهم بیزارید و من (نیز) از آنچه شماانجام می‌دهید بیزارم!»'),
(1406, 1406, 'وَمِنْهُمْ مَنْ يَسْتَمِعُونَ إِلَيْكَ ۚ أَفَأَنْتَ تُسْمِعُ الصُّمَّ وَلَوْ كَانُوا لَا يَعْقِلُونَ', 10, 42, 11, 43, 213, 'گروهی از آنان، بسوی تو گوش فرامی‌دهند (؛ امّا گویی هیچ نمی‌شنوند و کرند)! آیا تو می‌توانی سخن خود را به گوش کران برسانی، هر چند نفهمند؟!'),
(1407, 1407, 'وَمِنْهُمْ مَنْ يَنْظُرُ إِلَيْكَ ۚ أَفَأَنْتَ تَهْدِي الْعُمْيَ وَلَوْ كَانُوا لَا يُبْصِرُونَ', 10, 43, 11, 43, 214, 'و گروهی از آنان، به سوی تو می‌نگرند (امّا گویی هیچ نمی‌بینند)! آیا تو می‌توانی نابینایان را هدایت کنی، هر چند نبینند؟!'),
(1408, 1408, 'إِنَّ اللَّهَ لَا يَظْلِمُ النَّاسَ شَيْئًا وَلَٰكِنَّ النَّاسَ أَنْفُسَهُمْ يَظْلِمُونَ', 10, 44, 11, 43, 214, 'خداوند هیچ به مردم ستم نمی‌کند؛ ولی این مردمند که به خویشتن ستم می‌کند!'),
(1409, 1409, 'وَيَوْمَ يَحْشُرُهُمْ كَأَنْ لَمْ يَلْبَثُوا إِلَّا سَاعَةً مِنَ النَّهَارِ يَتَعَارَفُونَ بَيْنَهُمْ ۚ قَدْ خَسِرَ الَّذِينَ كَذَّبُوا بِلِقَاءِ اللَّهِ وَمَا كَانُوا مُهْتَدِينَ', 10, 45, 11, 43, 214, '(به یاد آور) روزی را که (خداوند) آنها را جمع (و محشور) می‌سازد؛ آنچنان که (احساس می‌کنند) گویی جز ساعتی از روز، (در دنیا) توقّف نکردند؛ به آن مقدار که یکدیگر را (ببینند و) بشناسند! مسلّماً آنها که لقای خداوند (و روز رستاخیز) را تکذیب کردند، زیان بردند و هدایت نیافتند!'),
(1410, 1410, 'وَإِمَّا نُرِيَنَّكَ بَعْضَ الَّذِي نَعِدُهُمْ أَوْ نَتَوَفَّيَنَّكَ فَإِلَيْنَا مَرْجِعُهُمْ ثُمَّ اللَّهُ شَهِيدٌ عَلَىٰ مَا يَفْعَلُونَ', 10, 46, 11, 43, 214, 'اگر ما، پاره‌ای از مجازاتهایی را که به آنها وعده داده‌ایم، (در حال حیات تو (به تو نشان دهیم، و یا) پیش از آنکه گرفتار عذاب شوند،) تو را از دنیا ببریم، در هر حال، بازگشتشان به سوی ماست؛ سپس خداوند بر آنچه آنها انجام می‌دادند گواه است!'),
(1411, 1411, 'وَلِكُلِّ أُمَّةٍ رَسُولٌ ۖ فَإِذَا جَاءَ رَسُولُهُمْ قُضِيَ بَيْنَهُمْ بِالْقِسْطِ وَهُمْ لَا يُظْلَمُونَ', 10, 47, 11, 43, 214, 'برای هر امّتی، رسولی است؛ هنگامی که رسولشان به سوی آنان بیاید، بعدالت در میان آنها داوری می‌شود؛ و ستمی به آنها نخواهد شد!'),
(1412, 1412, 'وَيَقُولُونَ مَتَىٰ هَٰذَا الْوَعْدُ إِنْ كُنْتُمْ صَادِقِينَ', 10, 48, 11, 43, 214, 'و می‌گویند: «اگر راست می‌گوئی، این وعده (مجازات) کی خواهد بود؟»'),
(1413, 1413, 'قُلْ لَا أَمْلِكُ لِنَفْسِي ضَرًّا وَلَا نَفْعًا إِلَّا مَا شَاءَ اللَّهُ ۗ لِكُلِّ أُمَّةٍ أَجَلٌ ۚ إِذَا جَاءَ أَجَلُهُمْ فَلَا يَسْتَأْخِرُونَ سَاعَةً ۖ وَلَا يَسْتَقْدِمُونَ', 10, 49, 11, 43, 214, 'بگو: «من (حتّی) برای خودم زیان و سودی را مالک نیستم، (تا چه رسد برای شما!) مگر آنچه خدا بخواهد. (این مقدار می‌دانم که) برای هر قوم و ملّتی، سرآمدی است؛ هنگامی که اجل آنها فرا رسد، (و فرمان مجازات یا مرگشان صادر شود،) نه ساعتی تأخیر می‌کنند، و نه پیشی می‌گیرند!'),
(1414, 1414, 'قُلْ أَرَأَيْتُمْ إِنْ أَتَاكُمْ عَذَابُهُ بَيَاتًا أَوْ نَهَارًا مَاذَا يَسْتَعْجِلُ مِنْهُ الْمُجْرِمُونَ', 10, 50, 11, 43, 214, 'بگو: «اگر مجازات او، شب‌هنگام یا در روز به سراغ شما آید (، آیا می‌توانید آن را از خود دفع کنید؟!)» پس مجرمان برای چه عجله می‌کنند؟!'),
(1415, 1415, 'أَثُمَّ إِذَا مَا وَقَعَ آمَنْتُمْ بِهِ ۚ آلْآنَ وَقَدْ كُنْتُمْ بِهِ تَسْتَعْجِلُونَ', 10, 51, 11, 43, 214, 'یا اینکه آنگاه که واقع شد، به آن ایمان می‌آورید! (به شما گفته می‌شود:) حالا؟! در حالی که قبلاً برای آن عجله می‌کردید! (ولی اکنون چه سود!)'),
(1416, 1416, 'ثُمَّ قِيلَ لِلَّذِينَ ظَلَمُوا ذُوقُوا عَذَابَ الْخُلْدِ هَلْ تُجْزَوْنَ إِلَّا بِمَا كُنْتُمْ تَكْسِبُونَ', 10, 52, 11, 43, 214, 'سپس به کسانی که ستم کردند گفته می‌شود: عذاب ابدی را بچشید! آیا جز به آنچه انجام می‌دادید کیفر داده می‌شوید؟!»'),
(1417, 1417, ' وَيَسْتَنْبِئُونَكَ أَحَقٌّ هُوَ ۖ قُلْ إِي وَرَبِّي إِنَّهُ لَحَقٌّ ۖ وَمَا أَنْتُمْ بِمُعْجِزِينَ', 10, 53, 11, 43, 214, 'از تو می‌پرسند: «آیا آن (وعده مجازات الهی) حقّ است؟» بگو: «آری، به پروردگارم سوگند، قطعاً حقّ است؛ و شما نمی‌توانید از آن جلوگیری کنید!»'),
(1418, 1418, 'وَلَوْ أَنَّ لِكُلِّ نَفْسٍ ظَلَمَتْ مَا فِي الْأَرْضِ لَافْتَدَتْ بِهِ ۗ وَأَسَرُّوا النَّدَامَةَ لَمَّا رَأَوُا الْعَذَابَ ۖ وَقُضِيَ بَيْنَهُمْ بِالْقِسْطِ ۚ وَهُمْ لَا يُظْلَمُونَ', 10, 54, 11, 43, 215, 'و هر کس که ستم کرده، اگر تمامی آنچه روی زمین است در اختیار داشته باشد، (همه را از هول عذاب،) برای نجات خویش می‌دهد! و هنگامی که عذاب را ببینند، (پشیمان می‌شوند؛ امّا) پشیمانی خود را کتمان می‌کنند (، مبادا رسواتر شوند)! و در میان آنها، بعدالت داوری می‌شود؛ و ستمی بر آنها نخواهد شد!'),
(1419, 1419, 'أَلَا إِنَّ لِلَّهِ مَا فِي السَّمَاوَاتِ وَالْأَرْضِ ۗ أَلَا إِنَّ وَعْدَ اللَّهِ حَقٌّ وَلَٰكِنَّ أَكْثَرَهُمْ لَا يَعْلَمُونَ', 10, 55, 11, 43, 215, 'آگاه باشید آنچه در آسمانها و زمین است، از آن خداست! آگاه باشید وعده خدا حقّ است، ولی بیشتر آنها نمی‌دانند!'),
(1420, 1420, 'هُوَ يُحْيِي وَيُمِيتُ وَإِلَيْهِ تُرْجَعُونَ', 10, 56, 11, 43, 215, 'اوست که زنده می‌کند و می‌میراند، و به سوی او بازگردانده می‌شوید!'),
(1421, 1421, 'يَا أَيُّهَا النَّاسُ قَدْ جَاءَتْكُمْ مَوْعِظَةٌ مِنْ رَبِّكُمْ وَشِفَاءٌ لِمَا فِي الصُّدُورِ وَهُدًى وَرَحْمَةٌ لِلْمُؤْمِنِينَ', 10, 57, 11, 43, 215, 'ای مردم! اندرزی از سوی پروردگارتان برای شما آمده است؛ و درمانی برای آنچه در سینه‌هاست؛ (درمانی برای دلهای شما؛) و هدایت و رحمتی است برای مؤمنان!'),
(1422, 1422, 'قُلْ بِفَضْلِ اللَّهِ وَبِرَحْمَتِهِ فَبِذَٰلِكَ فَلْيَفْرَحُوا هُوَ خَيْرٌ مِمَّا يَجْمَعُونَ', 10, 58, 11, 43, 215, 'بگو: «به فضل و رحمت خدا باید خوشحال شوند؛ که این، از تمام آنچه گردآوری کرده‌اند، بهتر است!»'),
(1423, 1423, 'قُلْ أَرَأَيْتُمْ مَا أَنْزَلَ اللَّهُ لَكُمْ مِنْ رِزْقٍ فَجَعَلْتُمْ مِنْهُ حَرَامًا وَحَلَالًا قُلْ آللَّهُ أَذِنَ لَكُمْ ۖ أَمْ عَلَى اللَّهِ تَفْتَرُونَ', 10, 59, 11, 43, 215, 'بگو: «آیا روزیهایی را که خداوند بر شما نازل کرده دیده‌اید، که بعضی از آن را حلال، و بعضی را حرام نموده‌اید؟!» بگو: «آیا خداوند به شما اجازه داده، یا بر خدا افترا می بندید (و از پیش خود، حلال و حرام می‌کنید؟!)»'),
(1424, 1424, 'وَمَا ظَنُّ الَّذِينَ يَفْتَرُونَ عَلَى اللَّهِ الْكَذِبَ يَوْمَ الْقِيَامَةِ ۗ إِنَّ اللَّهَ لَذُو فَضْلٍ عَلَى النَّاسِ وَلَٰكِنَّ أَكْثَرَهُمْ لَا يَشْكُرُونَ', 10, 60, 11, 43, 215, 'آنها که بر خدا افترا می‌بندند، درباره (مجازات) روز رستاخیز، چه می‌اندیشند؟! خداوند نسبت به همه مردم فضل (و بخشش) دارد، امّا اکثر آنها سپاسگزاری نمی‌کنند!'),
(1425, 1425, 'وَمَا تَكُونُ فِي شَأْنٍ وَمَا تَتْلُو مِنْهُ مِنْ قُرْآنٍ وَلَا تَعْمَلُونَ مِنْ عَمَلٍ إِلَّا كُنَّا عَلَيْكُمْ شُهُودًا إِذْ تُفِيضُونَ فِيهِ ۚ وَمَا يَعْزُبُ عَنْ رَبِّكَ مِنْ مِثْقَالِ ذَرَّةٍ فِي الْأَرْضِ وَلَا فِي السَّمَاءِ وَلَا أَصْغَرَ مِنْ ذَٰلِكَ وَلَا أَكْبَرَ إِلَّا فِي كِتَابٍ مُبِينٍ', 10, 61, 11, 43, 215, 'در هیچ حال (و اندیشه‌ای) نیستی، و هیچ قسمتی از قرآن را تلاوت نمی‌کنی، و هیچ عملی را انجام نمی‌دهید، مگر اینکه ما گواه بر شما هستیم در آن هنگام که وارد آن می شوید! و هیچ چیز در زمین و آسمان، از پروردگار تو مخفی نمی‌ماند؛ حتّی به اندازه سنگینی ذرّه‌ای، و نه کوچکتر از آن و نه بزرگتر، مگر اینکه (همه آنها) در کتاب آشکار (و لوح محفوظ علم خداوند) ثبت است!'),
(1426, 1426, 'أَلَا إِنَّ أَوْلِيَاءَ اللَّهِ لَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَ', 10, 62, 11, 43, 216, 'آگاه باشید (دوستان و) اولیای خدا، نه ترسی دارند و نه غمگین می‌شوند!'),
(1427, 1427, 'الَّذِينَ آمَنُوا وَكَانُوا يَتَّقُونَ', 10, 63, 11, 43, 216, 'همانها که ایمان آوردند، و (از مخالفت فرمان خدا) پرهیز می‌کردند.'),
(1428, 1428, 'لَهُمُ الْبُشْرَىٰ فِي الْحَيَاةِ الدُّنْيَا وَفِي الْآخِرَةِ ۚ لَا تَبْدِيلَ لِكَلِمَاتِ اللَّهِ ۚ ذَٰلِكَ هُوَ الْفَوْزُ الْعَظِيمُ', 10, 64, 11, 43, 216, 'در زندگی دنیا و در آخرت، شاد (و مسرور) ند؛ وعده‌های الهی تخلّف ناپذیر است! این است آن رستگاری بزرگ!'),
(1429, 1429, 'وَلَا يَحْزُنْكَ قَوْلُهُمْ ۘ إِنَّ الْعِزَّةَ لِلَّهِ جَمِيعًا ۚ هُوَ السَّمِيعُ الْعَلِيمُ', 10, 65, 11, 43, 216, 'سخن آنها تو را غمگین نسازد! تمام عزّت (و قدرت)، از آن خداست؛ و او شنوا و داناست!'),
(1430, 1430, 'أَلَا إِنَّ لِلَّهِ مَنْ فِي السَّمَاوَاتِ وَمَنْ فِي الْأَرْضِ ۗ وَمَا يَتَّبِعُ الَّذِينَ يَدْعُونَ مِنْ دُونِ اللَّهِ شُرَكَاءَ ۚ إِنْ يَتَّبِعُونَ إِلَّا الظَّنَّ وَإِنْ هُمْ إِلَّا يَخْرُصُونَ', 10, 66, 11, 43, 216, 'آگاه باشید تمام کسانی که در آسمانها و زمین هستند، از آن خدا می‌باشند! و آنها که غیر خدا را همتای او می‌خوانند، (از منطق و دلیلی) پیروی نمی‌کنند؛ آنها فقط از پندار بی اساس پیروی می‌کنند؛ و آنها فقط دروغ می‌گویند!'),
(1431, 1431, 'هُوَ الَّذِي جَعَلَ لَكُمُ اللَّيْلَ لِتَسْكُنُوا فِيهِ وَالنَّهَارَ مُبْصِرًا ۚ إِنَّ فِي ذَٰلِكَ لَآيَاتٍ لِقَوْمٍ يَسْمَعُونَ', 10, 67, 11, 43, 216, 'او کسی است که شب را برای شما آفرید، تا در آن آرامش بیابید؛ و روز را روشنی بخش (تا به تلاش زندگی پردازید) در اینها نشانه‌هایی است برای کسانی که گوش شنوا دارند!'),
(1432, 1432, 'قَالُوا اتَّخَذَ اللَّهُ وَلَدًا ۗ سُبْحَانَهُ ۖ هُوَ الْغَنِيُّ ۖ لَهُ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ ۚ إِنْ عِنْدَكُمْ مِنْ سُلْطَانٍ بِهَٰذَا ۚ أَتَقُولُونَ عَلَى اللَّهِ مَا لَا تَعْلَمُونَ', 10, 68, 11, 43, 216, 'گفتند: «خداوند فرزندی برای خود انتخاب کرده است»! (از هر عیب و نقص و احتیاجی) منزه است! او بی‌نیاز است! از آن اوست آنچه در آسمانها و آنچه در زمین است! شما هیچ‌گونه دلیلی بر این ادعا ندارید! آیا به خدا نسبتی می‌دهید که نمی‌دانید؟!'),
(1433, 1433, 'قُلْ إِنَّ الَّذِينَ يَفْتَرُونَ عَلَى اللَّهِ الْكَذِبَ لَا يُفْلِحُونَ', 10, 69, 11, 43, 216, 'بگو: «آنها که به خدا دروغ می‌بندند، (هرگز) رستگار نمی‌شوند!'),
(1434, 1434, 'مَتَاعٌ فِي الدُّنْيَا ثُمَّ إِلَيْنَا مَرْجِعُهُمْ ثُمَّ نُذِيقُهُمُ الْعَذَابَ الشَّدِيدَ بِمَا كَانُوا يَكْفُرُونَ', 10, 70, 11, 43, 216, 'بهره‌ای (ناچیز) از دنیا دارند؛ سپس بازگشتشان بسوی ماست؛ و بعد، به آنها مجازات شدید به سزای کفرشان می‌چشانیم!'),
(1435, 1435, ' وَاتْلُ عَلَيْهِمْ نَبَأَ نُوحٍ إِذْ قَالَ لِقَوْمِهِ يَا قَوْمِ إِنْ كَانَ كَبُرَ عَلَيْكُمْ مَقَامِي وَتَذْكِيرِي بِآيَاتِ اللَّهِ فَعَلَى اللَّهِ تَوَكَّلْتُ فَأَجْمِعُوا أَمْرَكُمْ وَشُرَكَاءَكُمْ ثُمَّ لَا يَكُنْ أَمْرُكُمْ عَلَيْكُمْ غُمَّةً ثُمَّ اقْضُوا إِلَيَّ وَلَا تُنْظِرُونِ', 10, 71, 11, 44, 217, 'سرگذشت نوح را بر آنها بخوان! در آن هنگام که به قوم خود گفت: «ای قوم من! اگر تذکّرات من نسبت به آیات الهی، بر شما سنگین (و غیر قابل تحمّل) است، (هر کار از دستتان ساخته است بکنید.) من بر خدا توکّل کرده‌ام! فکر خود، و قدرت معبودهایتان را جمع کنید؛ سپس هیچ چیز بر شما پوشیده نماند؛ (تمام جوانب کارتان را بنگرید؛) سپس به حیات من پایان دهید، و (لحظه‌ای) مهلتم ندهید! (امّا توانایی ندارید!)'),
(1436, 1436, 'فَإِنْ تَوَلَّيْتُمْ فَمَا سَأَلْتُكُمْ مِنْ أَجْرٍ ۖ إِنْ أَجْرِيَ إِلَّا عَلَى اللَّهِ ۖ وَأُمِرْتُ أَنْ أَكُونَ مِنَ الْمُسْلِمِينَ', 10, 72, 11, 44, 217, 'و اگر از قبول دعوتم روی بگردانید، (کار نادرستی کرده‌اید؛ چه اینکه) من از شما مزدی نمی‌خواهم؛ مزد من، تنها بر خداست! و من مأمورم که از مسلمین = تسلیم شدگان در برابر فرمان خدا باشم!»'),
(1437, 1437, 'فَكَذَّبُوهُ فَنَجَّيْنَاهُ وَمَنْ مَعَهُ فِي الْفُلْكِ وَجَعَلْنَاهُمْ خَلَائِفَ وَأَغْرَقْنَا الَّذِينَ كَذَّبُوا بِآيَاتِنَا ۖ فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ الْمُنْذَرِينَ', 10, 73, 11, 44, 217, 'امّا آنها او را تکذیب کردند! و ما، او و کسانی را که با او در کشتی بودند، نجات دادیم؛ و آنان را جانشین (و وارث کافران) قرار دادیم؛ و کسانی را که آیات ما را تکذیب کردند، غرق نمودیم! پس ببین عاقبت کار کسانی که انذار شدند (و به انذار الهی اهمیّت ندادند)، چگونه بود!'),
(1438, 1438, 'ثُمَّ بَعَثْنَا مِنْ بَعْدِهِ رُسُلًا إِلَىٰ قَوْمِهِمْ فَجَاءُوهُمْ بِالْبَيِّنَاتِ فَمَا كَانُوا لِيُؤْمِنُوا بِمَا كَذَّبُوا بِهِ مِنْ قَبْلُ ۚ كَذَٰلِكَ نَطْبَعُ عَلَىٰ قُلُوبِ الْمُعْتَدِينَ', 10, 74, 11, 44, 217, 'سپس بعد از نوح، رسولانی به سوی قومشان فرستادیم؛ آنان دلایل روشن برایشان آوردند؛ امّا آنها، به چیزی که پیش از آن تکذیب کرده بودند، ایمان نیاوردند! اینچنین بر دلهای تجاوزکاران مهر می‌نهیم (تا چیزی را درک نکنند)!'),
(1439, 1439, 'ثُمَّ بَعَثْنَا مِنْ بَعْدِهِمْ مُوسَىٰ وَهَارُونَ إِلَىٰ فِرْعَوْنَ وَمَلَئِهِ بِآيَاتِنَا فَاسْتَكْبَرُوا وَكَانُوا قَوْمًا مُجْرِمِينَ', 10, 75, 11, 44, 217, 'بعد از آنها، موسی و هارون را با آیات خود به سوی فرعون و اطرافیانش فرستادیم؛ اما آنها تکبّر کردند (و زیر بار حق نرفتند؛ چرا که) آنها گروهی مجرم بودند!'),
(1440, 1440, 'فَلَمَّا جَاءَهُمُ الْحَقُّ مِنْ عِنْدِنَا قَالُوا إِنَّ هَٰذَا لَسِحْرٌ مُبِينٌ', 10, 76, 11, 44, 217, 'و هنگامی که حق از نزد ما بسراغ آنها آمد، گفتند: «این، سحری است آشکار!»'),
(1441, 1441, 'قَالَ مُوسَىٰ أَتَقُولُونَ لِلْحَقِّ لَمَّا جَاءَكُمْ ۖ أَسِحْرٌ هَٰذَا وَلَا يُفْلِحُ السَّاحِرُونَ', 10, 77, 11, 44, 217, 'موسی گفت: «آیا درباره حقّ، هنگامی که به سوی شما آمد، (چنین) می‌گویید؟! آیا این سحر است؟! در حالی که ساحران (هرگز) رستگار (و پیروز) نمی‌شوند!'),
(1442, 1442, 'قَالُوا أَجِئْتَنَا لِتَلْفِتَنَا عَمَّا وَجَدْنَا عَلَيْهِ آبَاءَنَا وَتَكُونَ لَكُمَا الْكِبْرِيَاءُ فِي الْأَرْضِ وَمَا نَحْنُ لَكُمَا بِمُؤْمِنِينَ', 10, 78, 11, 44, 217, 'گفتند: «آیا آمده‌ای که ما را، از آنچه پدرانمان را بر آن یافتیم، منصرف سازی؛ و بزرگی (و ریاست) در روی زمین، از آن شما دو تن باشد؟! ما (هرگز) به شما ایمان نمی‌آوریم!»'),
(1443, 1443, 'وَقَالَ فِرْعَوْنُ ائْتُونِي بِكُلِّ سَاحِرٍ عَلِيمٍ', 10, 79, 11, 44, 218, 'فرعون گفت: «(بروید و) هر جادوگر (و ساحر) دانایی را نزد من آورید!»'),
(1444, 1444, 'فَلَمَّا جَاءَ السَّحَرَةُ قَالَ لَهُمْ مُوسَىٰ أَلْقُوا مَا أَنْتُمْ مُلْقُونَ', 10, 80, 11, 44, 218, 'هنگامی که ساحران (به میدان مبارزه) آمدند، موسی به آنها گفت: «آنچه (از وسایل سحر) را می‌توانید بیفکنید، بیفکنید!»'),
(1445, 1445, 'فَلَمَّا أَلْقَوْا قَالَ مُوسَىٰ مَا جِئْتُمْ بِهِ السِّحْرُ ۖ إِنَّ اللَّهَ سَيُبْطِلُهُ ۖ إِنَّ اللَّهَ لَا يُصْلِحُ عَمَلَ الْمُفْسِدِينَ', 10, 81, 11, 44, 218, 'هنگامی که افکندند، موسی گفت: «آنچه شما آوردید، سحر است؛ که خداوند بزودی آن را باطل می‌کند؛ چرا که خداوند (هرگز) عمل مفسدان را اصلاح نمی‌کند!'),
(1446, 1446, 'وَيُحِقُّ اللَّهُ الْحَقَّ بِكَلِمَاتِهِ وَلَوْ كَرِهَ الْمُجْرِمُونَ', 10, 82, 11, 44, 218, 'او حق را به وعده خویش، تحقق می‌بخشد؛ هر چند مجرمان کراهت داشته باشند!»'),
(1447, 1447, 'فَمَا آمَنَ لِمُوسَىٰ إِلَّا ذُرِّيَّةٌ مِنْ قَوْمِهِ عَلَىٰ خَوْفٍ مِنْ فِرْعَوْنَ وَمَلَئِهِمْ أَنْ يَفْتِنَهُمْ ۚ وَإِنَّ فِرْعَوْنَ لَعَالٍ فِي الْأَرْضِ وَإِنَّهُ لَمِنَ الْمُسْرِفِينَ', 10, 83, 11, 44, 218, '(در آغاز،) هیچ کس به موسی ایمان نیاورد، مگر گروهی از فرزندان قوم او؛ (آن هم) با ترس از فرعون و اطرافیانش، مبادا آنها را شکنجه کنند؛ زیرا فرعون، برتری‌جویی در زمین داشت؛ و از اسرافکاران بود!'),
(1448, 1448, 'وَقَالَ مُوسَىٰ يَا قَوْمِ إِنْ كُنْتُمْ آمَنْتُمْ بِاللَّهِ فَعَلَيْهِ تَوَكَّلُوا إِنْ كُنْتُمْ مُسْلِمِينَ', 10, 84, 11, 44, 218, 'موسی گفت: «ای قوم من! اگر شما به خدا ایمان آورده‌اید، بر او توکّل کنید اگر تسلیم فرمان او هستید!»'),
(1449, 1449, 'فَقَالُوا عَلَى اللَّهِ تَوَكَّلْنَا رَبَّنَا لَا تَجْعَلْنَا فِتْنَةً لِلْقَوْمِ الظَّالِمِينَ', 10, 85, 11, 44, 218, 'گفتند: «تنها بر خدا توکل داریم؛ پروردگارا! ما را مورد شکنجه گروه ستمگر قرار مده!'),
(1450, 1450, 'وَنَجِّنَا بِرَحْمَتِكَ مِنَ الْقَوْمِ الْكَافِرِينَ', 10, 86, 11, 44, 218, 'و ما را با رحمتت از (دست) قوم کافر رهایی بخش!'),
(1451, 1451, 'وَأَوْحَيْنَا إِلَىٰ مُوسَىٰ وَأَخِيهِ أَنْ تَبَوَّآ لِقَوْمِكُمَا بِمِصْرَ بُيُوتًا وَاجْعَلُوا بُيُوتَكُمْ قِبْلَةً وَأَقِيمُوا الصَّلَاةَ ۗ وَبَشِّرِ الْمُؤْمِنِينَ', 10, 87, 11, 44, 218, 'و به موسی و برادرش وحی کردیم که: «برای قوم خود، خانه‌هایی در سرزمین مصر انتخاب کنید؛ و خانه‌هایتان را مقابل یکدیگر (و متمرکز) قرار دهید! و نماز را برپا دارید! و به مؤمنان بشارت ده (که سرانجام پیروز می‌شوند!)»'),
(1452, 1452, 'وَقَالَ مُوسَىٰ رَبَّنَا إِنَّكَ آتَيْتَ فِرْعَوْنَ وَمَلَأَهُ زِينَةً وَأَمْوَالًا فِي الْحَيَاةِ الدُّنْيَا رَبَّنَا لِيُضِلُّوا عَنْ سَبِيلِكَ ۖ رَبَّنَا اطْمِسْ عَلَىٰ أَمْوَالِهِمْ وَاشْدُدْ عَلَىٰ قُلُوبِهِمْ فَلَا يُؤْمِنُوا حَتَّىٰ يَرَوُا الْعَذَابَ الْأَلِيمَ', 10, 88, 11, 44, 218, 'موسی گفت: «پروردگارا! تو فرعون و اطرافیانش را زینت و اموالی (سرشار) در زندگی دنیا داده‌ای، پروردگارا! در نتیجه (بندگانت را) از راه تو گمراه می‌سازند! پروردگارا! اموالشان را نابود کن! و (بجرم گناهانشان،) دلهایشان را سخت و سنگین ساز، به گونه‌ای که ایمان نیاورند تا عذاب دردناک را ببینند!»'),
(1453, 1453, 'قَالَ قَدْ أُجِيبَتْ دَعْوَتُكُمَا فَاسْتَقِيمَا وَلَا تَتَّبِعَانِّ سَبِيلَ الَّذِينَ لَا يَعْلَمُونَ', 10, 89, 11, 44, 219, 'فرمود: «دعای شما پذیرفته شد! استقامت به خرج دهید؛ و از راه (و رسم) کسانی که نمی‌دانند، تبعیت نکنید!»'),
(1454, 1454, ' وَجَاوَزْنَا بِبَنِي إِسْرَائِيلَ الْبَحْرَ فَأَتْبَعَهُمْ فِرْعَوْنُ وَجُنُودُهُ بَغْيًا وَعَدْوًا ۖ حَتَّىٰ إِذَا أَدْرَكَهُ الْغَرَقُ قَالَ آمَنْتُ أَنَّهُ لَا إِلَٰهَ إِلَّا الَّذِي آمَنَتْ بِهِ بَنُو إِسْرَائِيلَ وَأَنَا مِنَ الْمُسْلِمِينَ', 10, 90, 11, 44, 219, '(سرانجام) بنی اسرائیل را از دریا = رود عظیم نیل‌ عبور دادیم؛ و فرعون و لشکرش از سر ظلم و تجاوز، به دنبال آنها رفتند؛ هنگامی که غرقاب دامن او را گرفت، گفت: «ایمان آوردم که هیچ معبودی، جز کسی که بنی اسرائیل به او ایمان آورده‌اند، وجود ندارد؛ و من از مسلمین هستم!»'),
(1455, 1455, 'آلْآنَ وَقَدْ عَصَيْتَ قَبْلُ وَكُنْتَ مِنَ الْمُفْسِدِينَ', 10, 91, 11, 44, 219, '(امّا به او خطاب شد:) الآن؟!! در حالی که قبلاً عصیان کردی، و از مفسدان بودی!'),
(1456, 1456, 'فَالْيَوْمَ نُنَجِّيكَ بِبَدَنِكَ لِتَكُونَ لِمَنْ خَلْفَكَ آيَةً ۚ وَإِنَّ كَثِيرًا مِنَ النَّاسِ عَنْ آيَاتِنَا لَغَافِلُونَ', 10, 92, 11, 44, 219, 'ولی امروز، بدنت را (از آب) نجات می‌دهیم، تا عبرتی برای آیندگان باشی! و بسیاری از مردم، از آیات ما غافلند!'),
(1457, 1457, 'وَلَقَدْ بَوَّأْنَا بَنِي إِسْرَائِيلَ مُبَوَّأَ صِدْقٍ وَرَزَقْنَاهُمْ مِنَ الطَّيِّبَاتِ فَمَا اخْتَلَفُوا حَتَّىٰ جَاءَهُمُ الْعِلْمُ ۚ إِنَّ رَبَّكَ يَقْضِي بَيْنَهُمْ يَوْمَ الْقِيَامَةِ فِيمَا كَانُوا فِيهِ يَخْتَلِفُونَ', 10, 93, 11, 44, 219, '(سپس) بنی اسرائیل را در جایگاه صدق (و راستی) منزل دادیم؛ و از روزیهای پاکیزه به آنها عطا کردیم؛ (امّا آنها به نزاع و اختلاف برخاستند!) و اختلاف نکردند، مگر بعد از آنکه علم و آگاهی به سراغشان آمد! پروردگار تو روز قیامت، در آنچه اختلاف می‌کردند، میان آنها داوری می‌کند!'),
(1458, 1458, 'فَإِنْ كُنْتَ فِي شَكٍّ مِمَّا أَنْزَلْنَا إِلَيْكَ فَاسْأَلِ الَّذِينَ يَقْرَءُونَ الْكِتَابَ مِنْ قَبْلِكَ ۚ لَقَدْ جَاءَكَ الْحَقُّ مِنْ رَبِّكَ فَلَا تَكُونَنَّ مِنَ الْمُمْتَرِينَ', 10, 94, 11, 44, 219, 'و اگر در آنچه بر تو نازل کرده‌ایم تردیدی داری، از کسانی که پیش از تو کتاب آسمانی را می‌خواندند بپرس، به یقین، «حق» از طرف پروردگارت به تو رسیده است؛ بنابر این، هرگز از تردیدکنندگان مباش! مسلماً او تردیدی نداشت! این درسی برای مردم بود!'),
(1459, 1459, 'وَلَا تَكُونَنَّ مِنَ الَّذِينَ كَذَّبُوا بِآيَاتِ اللَّهِ فَتَكُونَ مِنَ الْخَاسِرِينَ', 10, 95, 11, 44, 219, 'و از آنها مباش که آیات خدا را تکذیب کردند، که از زیانکاران خواهی بود!'),
(1460, 1460, 'إِنَّ الَّذِينَ حَقَّتْ عَلَيْهِمْ كَلِمَتُ رَبِّكَ لَا يُؤْمِنُونَ', 10, 96, 11, 44, 219, '(و بدان) آنها که فرمان پروردگار تو بر آنان تحقق یافته، (و بجرم اعمالشان، توفیق هدایت را از آنها گرفته هرگز) ایمان نمی‌آورند،'),
(1461, 1461, 'وَلَوْ جَاءَتْهُمْ كُلُّ آيَةٍ حَتَّىٰ يَرَوُا الْعَذَابَ الْأَلِيمَ', 10, 97, 11, 44, 219, 'هر چند تمام آیات (و نشانه‌های الهی) به آنان برسد، تا زمانی که عذاب دردناک را ببینند! (زیرا تاریکی گناه، قلبهایشان را فرا گرفته، و راهی به روشنایی ندارند!)'),
(1462, 1462, 'فَلَوْلَا كَانَتْ قَرْيَةٌ آمَنَتْ فَنَفَعَهَا إِيمَانُهَا إِلَّا قَوْمَ يُونُسَ لَمَّا آمَنُوا كَشَفْنَا عَنْهُمْ عَذَابَ الْخِزْيِ فِي الْحَيَاةِ الدُّنْيَا وَمَتَّعْنَاهُمْ إِلَىٰ حِينٍ', 10, 98, 11, 44, 220, 'چرا هیچ یک از شهرها و آبادیها ایمان نیاوردند که (ایمانشان بموقع باشد، و) به حالشان مفید افتد؟! مگر قوم یونس، هنگامی که آنها ایمان آوردند، عذاب رسوا کننده را در زندگی دنیا از آنان برطرف ساختیم؛ و تا مدّت معیّنی = پایان زندگی و اجلشان‌ آنها را بهره‌مند ساختیم.'),
(1463, 1463, 'وَلَوْ شَاءَ رَبُّكَ لَآمَنَ مَنْ فِي الْأَرْضِ كُلُّهُمْ جَمِيعًا ۚ أَفَأَنْتَ تُكْرِهُ النَّاسَ حَتَّىٰ يَكُونُوا مُؤْمِنِينَ', 10, 99, 11, 44, 220, 'و اگر پروردگار تو می‌خواست، تمام کسانی که روی زمین هستند، همگی به (اجبار) ایمان می‌آوردند؛ آیا تو می‌خواهی مردم را مجبور سازی که ایمان بیاورند؟! (ایمان اجباری چه سودی دارد؟!)'),
(1464, 1464, 'وَمَا كَانَ لِنَفْسٍ أَنْ تُؤْمِنَ إِلَّا بِإِذْنِ اللَّهِ ۚ وَيَجْعَلُ الرِّجْسَ عَلَى الَّذِينَ لَا يَعْقِلُونَ', 10, 100, 11, 44, 220, '(اما) هیچ کس نمی‌تواند ایمان بیاورد، جز به فرمان خدا (و توفیق و یاری و هدایت او)! و پلیدی (کفر و گناه) را بر کسانی قرارمی‌دهد که نمی‌اندیشند.'),
(1465, 1465, 'قُلِ انْظُرُوا مَاذَا فِي السَّمَاوَاتِ وَالْأَرْضِ ۚ وَمَا تُغْنِي الْآيَاتُ وَالنُّذُرُ عَنْ قَوْمٍ لَا يُؤْمِنُونَ', 10, 101, 11, 44, 220, 'بگو: «نگاه کنید چه چیز (از آیات خدا و نشانه‌های توحیدش) در آسمانها و زمین است!» اما این آیات و انذارها به حال کسانی که (به خاطر لجاجت) ایمان نمی‌آورند مفید نخواهد بود!'),
(1466, 1466, 'فَهَلْ يَنْتَظِرُونَ إِلَّا مِثْلَ أَيَّامِ الَّذِينَ خَلَوْا مِنْ قَبْلِهِمْ ۚ قُلْ فَانْتَظِرُوا إِنِّي مَعَكُمْ مِنَ الْمُنْتَظِرِينَ', 10, 102, 11, 44, 220, 'آیا آنها (چیزی) جز همانند روزهای پیشینیان (و بلاها و مجازاتهایشان) را انتظار می‌کشند؟! بگو: «شما انتظار بکشید، من نیز با شما انتظار می‌کشم!»'),
(1467, 1467, 'ثُمَّ نُنَجِّي رُسُلَنَا وَالَّذِينَ آمَنُوا ۚ كَذَٰلِكَ حَقًّا عَلَيْنَا نُنْجِ الْمُؤْمِنِينَ', 10, 103, 11, 44, 220, 'سپس (هنگام نزول بلا و مجازات،) فرستادگان خود و کسانی را که (به آنان) ایمان می‌آورند، نجات می‌دهیم و همین گونه، بر ما حق است که مؤمنان (به تو) را (نیز) رهایی بخشیم!'),
(1468, 1468, 'قُلْ يَا أَيُّهَا النَّاسُ إِنْ كُنْتُمْ فِي شَكٍّ مِنْ دِينِي فَلَا أَعْبُدُ الَّذِينَ تَعْبُدُونَ مِنْ دُونِ اللَّهِ وَلَٰكِنْ أَعْبُدُ اللَّهَ الَّذِي يَتَوَفَّاكُمْ ۖ وَأُمِرْتُ أَنْ أَكُونَ مِنَ الْمُؤْمِنِينَ', 10, 104, 11, 44, 220, 'بگو: «ای مردم! اگر در عقیده من شک دارید، من آنهایی را که جز خدا می‌پرستید، نمی‌پرستم! تنها خداوندی را پرستش می‌کنم که شما را می‌میراند! و من مأمورم که از مؤمنان باشم!'),
(1469, 1469, 'وَأَنْ أَقِمْ وَجْهَكَ لِلدِّينِ حَنِيفًا وَلَا تَكُونَنَّ مِنَ الْمُشْرِكِينَ', 10, 105, 11, 44, 220, 'و (به من دستور داده شده که:) روی خود را به آیینی متوجه ساز که از هر گونه شرک، خالی است؛ و از مشرکان مباش!'),
(1470, 1470, 'وَلَا تَدْعُ مِنْ دُونِ اللَّهِ مَا لَا يَنْفَعُكَ وَلَا يَضُرُّكَ ۖ فَإِنْ فَعَلْتَ فَإِنَّكَ إِذًا مِنَ الظَّالِمِينَ', 10, 106, 11, 44, 220, 'و جز خدا، چیزی را که نه سودی به تو می‌رساند و نه زیانی، مخوان! که اگر چنین کنی، از ستمکاران خواهی بود!'),
(1471, 1471, 'وَإِنْ يَمْسَسْكَ اللَّهُ بِضُرٍّ فَلَا كَاشِفَ لَهُ إِلَّا هُوَ ۖ وَإِنْ يُرِدْكَ بِخَيْرٍ فَلَا رَادَّ لِفَضْلِهِ ۚ يُصِيبُ بِهِ مَنْ يَشَاءُ مِنْ عِبَادِهِ ۚ وَهُوَ الْغَفُورُ الرَّحِيمُ', 10, 107, 11, 44, 221, 'و اگر خداوند، (برای امتحان یا کیفر گناه،) زیانی به تو رساند، هیچ کس جز او آن را برطرف نمی‌سازد؛ و اگر اراده خیری برای تو کند، هیچ کس مانع فضل او نخواهد شد! آنرا به هر کس از بندگانش بخواهد می‌رساند؛ و او غفور و رحیم است!»'),
(1472, 1472, 'قُلْ يَا أَيُّهَا النَّاسُ قَدْ جَاءَكُمُ الْحَقُّ مِنْ رَبِّكُمْ ۖ فَمَنِ اهْتَدَىٰ فَإِنَّمَا يَهْتَدِي لِنَفْسِهِ ۖ وَمَنْ ضَلَّ فَإِنَّمَا يَضِلُّ عَلَيْهَا ۖ وَمَا أَنَا عَلَيْكُمْ بِوَكِيلٍ', 10, 108, 11, 44, 221, 'بگو: «ای مردم! حق از طرف پروردگارتان به سراغ شما آمده؛ هر کس (در پرتو آن) هدایت یابد، برای خود هدایت شده؛ و هر کس گمراه گردد، به زیان خود گمراه می‌گردد؛ و من مأمور (به اجبار) شما نیستم!»'),
(1473, 1473, 'وَاتَّبِعْ مَا يُوحَىٰ إِلَيْكَ وَاصْبِرْ حَتَّىٰ يَحْكُمَ اللَّهُ ۚ وَهُوَ خَيْرُ الْحَاكِمِينَ', 10, 109, 11, 44, 221, 'و از آنچه بر تو وحی می‌شود پیروی کن، و شکیبا باش (و استقامت نما)، تا خداوند فرمان (پیروزی) را صادر کند؛ و او بهترین حاکمان است!'),
(1474, 1474, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ الر ۚ كِتَابٌ أُحْكِمَتْ آيَاتُهُ ثُمَّ فُصِّلَتْ مِنْ لَدُنْ حَكِيمٍ خَبِيرٍ', 11, 1, 11, 44, 221, 'الر، این کتابی است که آیاتش استحکام یافته؛ سپس تشریح شده و از نزد خداوند حکیم و آگاه (نازل گردیده) است!'),
(1475, 1475, 'أَلَّا تَعْبُدُوا إِلَّا اللَّهَ ۚ إِنَّنِي لَكُمْ مِنْهُ نَذِيرٌ وَبَشِيرٌ', 11, 2, 11, 44, 221, '(دعوت من این است) که: جز «اللّه» را نپرستید! من از سوی او برای شما بیم دهنده و بشارت دهنده‌ام!'),
(1476, 1476, 'وَأَنِ اسْتَغْفِرُوا رَبَّكُمْ ثُمَّ تُوبُوا إِلَيْهِ يُمَتِّعْكُمْ مَتَاعًا حَسَنًا إِلَىٰ أَجَلٍ مُسَمًّى وَيُؤْتِ كُلَّ ذِي فَضْلٍ فَضْلَهُ ۖ وَإِنْ تَوَلَّوْا فَإِنِّي أَخَافُ عَلَيْكُمْ عَذَابَ يَوْمٍ كَبِيرٍ', 11, 3, 11, 44, 221, 'و اینکه: از پروردگار خویش آمرزش بطلبید؛ سپس بسوی او بازگردید؛ تا شما را تا مدّت معیّنی، (از مواهب زندگی این جهان،) به خوبی بهره‌مند سازد؛ و به هر صاحب فضیلتی، به مقدار فضیلتش ببخشد! و اگر (از این فرمان) روی گردان شوید، من بر شما از عذاب روز بزرگی بیمناکم!'),
(1477, 1477, 'إِلَى اللَّهِ مَرْجِعُكُمْ ۖ وَهُوَ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ', 11, 4, 11, 44, 221, '(بدانید) بازگشت شما بسوی «اللّه» است، و او بر هر چیز تواناست!'),
(1478, 1478, 'أَلَا إِنَّهُمْ يَثْنُونَ صُدُورَهُمْ لِيَسْتَخْفُوا مِنْهُ ۚ أَلَا حِينَ يَسْتَغْشُونَ ثِيَابَهُمْ يَعْلَمُ مَا يُسِرُّونَ وَمَا يُعْلِنُونَ ۚ إِنَّهُ عَلِيمٌ بِذَاتِ الصُّدُورِ', 11, 5, 11, 44, 221, 'آگاه باشید، آنها (سرها را به هم نزدیک ساخته، و) سینه‌هاشان را در کنار هم قرارمی‌دهند، تا خود (و سخنان خویش) را از او = پیامبر پنهان دارند! آگاه باشید، آنگاه که آنها لباسهایشان را به خود می‌پیچند و خویش را در آن پنهان می‌کنند، (خداوند) می‌داند آنچه را پنهان می‌کنند و آنچه را آشکار می‌سازند؛ چرا که او، از اسرار درون سینه‌ها، آگاه است!'),
(1479, 1479, ' وَمَا مِنْ دَابَّةٍ فِي الْأَرْضِ إِلَّا عَلَى اللَّهِ رِزْقُهَا وَيَعْلَمُ مُسْتَقَرَّهَا وَمُسْتَوْدَعَهَا ۚ كُلٌّ فِي كِتَابٍ مُبِينٍ', 11, 6, 12, 45, 222, 'هیچ جنبنده‌ای در زمین نیست مگر اینکه روزی او بر خداست! او قرارگاه و محل نقل و انتقالش را می‌داند؛ همه اینها در کتاب آشکاری ثبت است! = در لوح محفوظ، در کتاب علم خدا'),
(1480, 1480, 'وَهُوَ الَّذِي خَلَقَ السَّمَاوَاتِ وَالْأَرْضَ فِي سِتَّةِ أَيَّامٍ وَكَانَ عَرْشُهُ عَلَى الْمَاءِ لِيَبْلُوَكُمْ أَيُّكُمْ أَحْسَنُ عَمَلًا ۗ وَلَئِنْ قُلْتَ إِنَّكُمْ مَبْعُوثُونَ مِنْ بَعْدِ الْمَوْتِ لَيَقُولَنَّ الَّذِينَ كَفَرُوا إِنْ هَٰذَا إِلَّا سِحْرٌ مُبِينٌ', 11, 7, 12, 45, 222, 'او کسی است که آسمانها و زمین را در شش روز = شش دوران‌ آفرید؛ و عرش (حکومت) او، بر آب قرار داشت؛ (بخاطر این آفرید) تا شما را بیازماید که کدامیک عملتان بهتر است! و اگر (به آنها) بگویی: «شما بعد از مرگ، برانگیخته می‌شوید! «، مسلّماً» کافران می‌گویند: «این سحری آشکار است!»'),
(1481, 1481, 'وَلَئِنْ أَخَّرْنَا عَنْهُمُ الْعَذَابَ إِلَىٰ أُمَّةٍ مَعْدُودَةٍ لَيَقُولُنَّ مَا يَحْبِسُهُ ۗ أَلَا يَوْمَ يَأْتِيهِمْ لَيْسَ مَصْرُوفًا عَنْهُمْ وَحَاقَ بِهِمْ مَا كَانُوا بِهِ يَسْتَهْزِئُونَ', 11, 8, 12, 45, 222, 'و اگر مجازات را تا زمان محدودی از آنها به تأخیر اندازیم، (از روی استهزا می‌گویند: «چه چیز مانع آن شده است؟!» آگاه باشید، آن روز که (عذاب) به سراغشان آید، از آنها بازگردانده نخواهد شد؛ (و هیچ قدرتی مانع آن نخواهد بود؛) و آنچه را مسخره می‌کردند، دامانشان را می‌گیرد!'),
(1482, 1482, 'وَلَئِنْ أَذَقْنَا الْإِنْسَانَ مِنَّا رَحْمَةً ثُمَّ نَزَعْنَاهَا مِنْهُ إِنَّهُ لَيَئُوسٌ كَفُورٌ', 11, 9, 12, 45, 222, 'و اگر از جانب خویش، نعمتی به انسان بچشانیم، سپس آن را از او بگیریم، بسیار نومید و ناسپاس خواهد بود!'),
(1483, 1483, 'وَلَئِنْ أَذَقْنَاهُ نَعْمَاءَ بَعْدَ ضَرَّاءَ مَسَّتْهُ لَيَقُولَنَّ ذَهَبَ السَّيِّئَاتُ عَنِّي ۚ إِنَّهُ لَفَرِحٌ فَخُورٌ', 11, 10, 12, 45, 222, 'و اگر بعد از شدّت و رنجی که به او رسیده، نعمتهایی به او بچشانیم، می‌گوید: «مشکلات از من برطرف شد، و دیگر باز نخواهد گشت!» و غرق شادی و غفلت و فخرفروشی می‌شود...'),
(1484, 1484, 'إِلَّا الَّذِينَ صَبَرُوا وَعَمِلُوا الصَّالِحَاتِ أُولَٰئِكَ لَهُمْ مَغْفِرَةٌ وَأَجْرٌ كَبِيرٌ', 11, 11, 12, 45, 222, 'مگر آنها که (در سایه ایمان راستین،) صبر و استقامت ورزیدند و کارهای شایسته انجام دادند؛ که برای آنها، آمرزش و اجر بزرگی است!'),
(1485, 1485, 'فَلَعَلَّكَ تَارِكٌ بَعْضَ مَا يُوحَىٰ إِلَيْكَ وَضَائِقٌ بِهِ صَدْرُكَ أَنْ يَقُولُوا لَوْلَا أُنْزِلَ عَلَيْهِ كَنْزٌ أَوْ جَاءَ مَعَهُ مَلَكٌ ۚ إِنَّمَا أَنْتَ نَذِيرٌ ۚ وَاللَّهُ عَلَىٰ كُلِّ شَيْءٍ وَكِيلٌ', 11, 12, 12, 45, 222, 'شاید (ابلاغ) بعض آیاتی را که به تو وحی می‌شود، (بخاطر عدم پذیرش آنها) ترک کنی (و به تأخیر اندازی)؛ و سینه‌ات از این جهت تنگ (و ناراحت) شود که می‌گویند: «چرا گنجی بر او نازل نشده؟! و یا چرا فرشته‌ای همراه او نیامده است؟!» (ابلاغ کن، و نگران و ناراحت مباش! چرا که) تو فقط بیم دهنده‌ای؛ و خداوند، نگاهبان و ناظر بر همه چیز است (؛ و به حساب آنان می‌رسد)!'),
(1486, 1486, 'أَمْ يَقُولُونَ افْتَرَاهُ ۖ قُلْ فَأْتُوا بِعَشْرِ سُوَرٍ مِثْلِهِ مُفْتَرَيَاتٍ وَادْعُوا مَنِ اسْتَطَعْتُمْ مِنْ دُونِ اللَّهِ إِنْ كُنْتُمْ صَادِقِينَ', 11, 13, 12, 45, 223, 'آنها می‌گویند: «او به دروغ این (قرآن) را (به خدا) نسبت داده (و ساختگی است)!» بگو: «اگر راست می‌گویید، شما هم ده سوره ساختگی همانند این قرآن بیاورید؛ و تمام کسانی را که می‌توانید -غیر از خدا- (برای این کار) دعوت کنید!»'),
(1487, 1487, 'فَإِلَّمْ يَسْتَجِيبُوا لَكُمْ فَاعْلَمُوا أَنَّمَا أُنْزِلَ بِعِلْمِ اللَّهِ وَأَنْ لَا إِلَٰهَ إِلَّا هُوَ ۖ فَهَلْ أَنْتُمْ مُسْلِمُونَ', 11, 14, 12, 45, 223, 'و اگر آنها دعوت شما را نپذیرفتند، بدانید (قرآن) تنها با علم الهی نازل شده؛ و هیچ معبودی جز او نیست! آیا با این حال، تسلیم می‌شوید؟'),
(1488, 1488, 'مَنْ كَانَ يُرِيدُ الْحَيَاةَ الدُّنْيَا وَزِينَتَهَا نُوَفِّ إِلَيْهِمْ أَعْمَالَهُمْ فِيهَا وَهُمْ فِيهَا لَا يُبْخَسُونَ', 11, 15, 12, 45, 223, 'کسانی که زندگی دنیا و زینت آن را بخواهند، (نتیجه) اعمالشان را در همین دنیا بطور کامل به آنها می‌دهیم؛ و چیزی کم و کاست از آنها نخواهد شد!'),
(1489, 1489, 'أُولَٰئِكَ الَّذِينَ لَيْسَ لَهُمْ فِي الْآخِرَةِ إِلَّا النَّارُ ۖ وَحَبِطَ مَا صَنَعُوا فِيهَا وَبَاطِلٌ مَا كَانُوا يَعْمَلُونَ', 11, 16, 12, 45, 223, '(ولی) آنها در آخرت، جز آتش، (سهمی) نخواهند داشت؛ و آنچه را در دنیا (برای غیر خدا) انجام دادند، بر باد می‌رود؛ و آنچه را عمل می‌کردند، باطل و بی‌اثر می‌شود!'),
(1490, 1490, 'أَفَمَنْ كَانَ عَلَىٰ بَيِّنَةٍ مِنْ رَبِّهِ وَيَتْلُوهُ شَاهِدٌ مِنْهُ وَمِنْ قَبْلِهِ كِتَابُ مُوسَىٰ إِمَامًا وَرَحْمَةً ۚ أُولَٰئِكَ يُؤْمِنُونَ بِهِ ۚ وَمَنْ يَكْفُرْ بِهِ مِنَ الْأَحْزَابِ فَالنَّارُ مَوْعِدُهُ ۚ فَلَا تَكُ فِي مِرْيَةٍ مِنْهُ ۚ إِنَّهُ الْحَقُّ مِنْ رَبِّكَ وَلَٰكِنَّ أَكْثَرَ النَّاسِ لَا يُؤْمِنُونَ', 11, 17, 12, 45, 223, 'آیا آن کس که دلیل آشکاری از پروردگار خویش دارد، و بدنبال آن، شاهدی از سوی او می‌باشد، و پیش از آن، کتاب موسی که پیشوا و رحمت بود (گواهی بر آن می‌دهد، همچون کسی است که چنین نباشد)؟! آنها = حق‌طلبان و حقیقت‌جویان‌ به او (که دارای این ویژگیهاست،) ایمان می‌آورند! و هر کس از گروه‌های مختلف به او کافر شود، آتش وعده‌گاه اوست! پس، تردیدی در آن نداشته باش که آن حق است از پروردگارت! ولی بیشتر مردم ایمان نمی‌آورند!'),
(1491, 1491, 'وَمَنْ أَظْلَمُ مِمَّنِ افْتَرَىٰ عَلَى اللَّهِ كَذِبًا ۚ أُولَٰئِكَ يُعْرَضُونَ عَلَىٰ رَبِّهِمْ وَيَقُولُ الْأَشْهَادُ هَٰؤُلَاءِ الَّذِينَ كَذَبُوا عَلَىٰ رَبِّهِمْ ۚ أَلَا لَعْنَةُ اللَّهِ عَلَى الظَّالِمِينَ', 11, 18, 12, 45, 223, 'چه کسی ستمکارتر است از کسانی که بر خدا افترا می‌بندند؟! آنان (روز رستاخیز) بر پروردگارشان عرضه می‌شوند، در حالی که شاهدان = پیامبران و فرشتگان‌ می‌گویند: «اینها همانها هستند که به پروردگارشان دروغ بستند! ای لعنت خدا بر ظالمان باد!»'),
(1492, 1492, 'الَّذِينَ يَصُدُّونَ عَنْ سَبِيلِ اللَّهِ وَيَبْغُونَهَا عِوَجًا وَهُمْ بِالْآخِرَةِ هُمْ كَافِرُونَ', 11, 19, 12, 45, 223, 'همانها که (مردم را) از راه خدا بازمی‌دارند؛ و راه حق را کج و معوج نشان می‌دهند؛ و به سرای آخرت کافرند!'),
(1493, 1493, 'أُولَٰئِكَ لَمْ يَكُونُوا مُعْجِزِينَ فِي الْأَرْضِ وَمَا كَانَ لَهُمْ مِنْ دُونِ اللَّهِ مِنْ أَوْلِيَاءَ ۘ يُضَاعَفُ لَهُمُ الْعَذَابُ ۚ مَا كَانُوا يَسْتَطِيعُونَ السَّمْعَ وَمَا كَانُوا يُبْصِرُونَ', 11, 20, 12, 45, 224, 'آنها هیچ گاه توانایی فرار در زمین را ندارند؛ و جز خدا، پشتیبانهایی نمی‌یابند! عذاب خدا برای آنها مضاعف خواهد بود؛ (چرا که هم خودشان گمراه بودند، و هم دیگران را گمراه ساختند؛) آنها هرگز توانایی شنیدن (حق را) نداشتند؛ و (حقیقت را) نمی‌دیدند!'),
(1494, 1494, 'أُولَٰئِكَ الَّذِينَ خَسِرُوا أَنْفُسَهُمْ وَضَلَّ عَنْهُمْ مَا كَانُوا يَفْتَرُونَ', 11, 21, 12, 45, 224, 'آنان کسانی هستند که سرمایه وجود خود را از دست داده‌اند؛ و تمام معبودهای دروغین از نظرشان گم شدند...'),
(1495, 1495, 'لَا جَرَمَ أَنَّهُمْ فِي الْآخِرَةِ هُمُ الْأَخْسَرُونَ', 11, 22, 12, 45, 224, '(به ناچار) آنها در سرای آخرت، قطعاً از همه زیانکارترند!'),
(1496, 1496, 'إِنَّ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ وَأَخْبَتُوا إِلَىٰ رَبِّهِمْ أُولَٰئِكَ أَصْحَابُ الْجَنَّةِ ۖ هُمْ فِيهَا خَالِدُونَ', 11, 23, 12, 45, 224, 'کسانی که ایمان آوردند و کارهای شایسته انجام دادند و در برابر پروردگارشان خضوع و خشوع کردند، آنها اهل بهشتند؛ و جاودانه در آن خواهند ماند!'),
(1497, 1497, ' مَثَلُ الْفَرِيقَيْنِ كَالْأَعْمَىٰ وَالْأَصَمِّ وَالْبَصِيرِ وَالسَّمِيعِ ۚ هَلْ يَسْتَوِيَانِ مَثَلًا ۚ أَفَلَا تَذَكَّرُونَ', 11, 24, 12, 45, 224, 'حال این دو گروه = مؤمنان و منکران‌، حال «نابینا و کر» و «بینا و شنوا» است؛ آیا این دو، همانند یکدیگرند؟! آیا پند نمی‌گیرند؟!'),
(1498, 1498, 'وَلَقَدْ أَرْسَلْنَا نُوحًا إِلَىٰ قَوْمِهِ إِنِّي لَكُمْ نَذِيرٌ مُبِينٌ', 11, 25, 12, 45, 224, 'ما نوح را بسوی قومش فرستادیم (؛ نخستین بار به آنها گفت): «من برای شما بیم‌دهنده‌ای آشکارم!'),
(1499, 1499, 'أَنْ لَا تَعْبُدُوا إِلَّا اللَّهَ ۖ إِنِّي أَخَافُ عَلَيْكُمْ عَذَابَ يَوْمٍ أَلِيمٍ', 11, 26, 12, 45, 224, 'جز «الله» = خدای یگانه یکتا را نپرستید؛ زیرا بر شما از عذاب روز دردناکی می‌ترسم!'),
(1500, 1500, 'فَقَالَ الْمَلَأُ الَّذِينَ كَفَرُوا مِنْ قَوْمِهِ مَا نَرَاكَ إِلَّا بَشَرًا مِثْلَنَا وَمَا نَرَاكَ اتَّبَعَكَ إِلَّا الَّذِينَ هُمْ أَرَاذِلُنَا بَادِيَ الرَّأْيِ وَمَا نَرَىٰ لَكُمْ عَلَيْنَا مِنْ فَضْلٍ بَلْ نَظُنُّكُمْ كَاذِبِينَ', 11, 27, 12, 45, 224, 'اشراف کافر قومش (در پاسخ او) گفتند: «ما تو را جز بشری همچون خودمان نمی‌بینیم! و کسانی را که از تو پیروی کرده‌اند، جز گروهی اراذل ساده‌لوح، مشاهده نمی‌کنیم؛ و برای شما فضیلتی نسبت به خود نمی‌بینیم؛ بلکه شما را دروغگو تصور می‌کنیم!»'),
(1501, 1501, 'قَالَ يَا قَوْمِ أَرَأَيْتُمْ إِنْ كُنْتُ عَلَىٰ بَيِّنَةٍ مِنْ رَبِّي وَآتَانِي رَحْمَةً مِنْ عِنْدِهِ فَعُمِّيَتْ عَلَيْكُمْ أَنُلْزِمُكُمُوهَا وَأَنْتُمْ لَهَا كَارِهُونَ', 11, 28, 12, 45, 224, '(نوح) گفت: «اگر من دلیل روشنی از پروردگارم داشته باشم، و از نزد خودش رحمتی به من داده باشد -و بر شما مخفی مانده- (آیا باز هم رسالت مرا انکار می‌کنید)؟! آیا ما می‌توانیم شما را به پذیرش این دلیل روشن مجبور سازیم، با اینکه شما کراهت دارید؟!'),
(1502, 1502, 'وَيَا قَوْمِ لَا أَسْأَلُكُمْ عَلَيْهِ مَالًا ۖ إِنْ أَجْرِيَ إِلَّا عَلَى اللَّهِ ۚ وَمَا أَنَا بِطَارِدِ الَّذِينَ آمَنُوا ۚ إِنَّهُمْ مُلَاقُو رَبِّهِمْ وَلَٰكِنِّي أَرَاكُمْ قَوْمًا تَجْهَلُونَ', 11, 29, 12, 45, 225, 'ای قوم! من به خاطر این دعوت، اجر و پاداشی از شما نمی‌طلبم؛ اجر من، تنها بر خداست! و من، آنها را که ایمان آورده‌اند، (بخاطر شما) از خود طرد نمی‌کنم؛ چرا که آنها پروردگارشان را ملاقات خواهند کرد؛ (اگر آنها را از خود برانم، در دادگاه قیامت، خصم من خواهند بود؛) ولی شما را قوم جاهلی می‌بینم!'),
(1503, 1503, 'وَيَا قَوْمِ مَنْ يَنْصُرُنِي مِنَ اللَّهِ إِنْ طَرَدْتُهُمْ ۚ أَفَلَا تَذَكَّرُونَ', 11, 30, 12, 45, 225, 'ای قوم! چه کسی مرا در برابر (مجازات) خدا یاری می‌دهد اگر آنان را طرد کنم؟! آیا اندیشه نمی‌کنید؟!'),
(1504, 1504, 'وَلَا أَقُولُ لَكُمْ عِنْدِي خَزَائِنُ اللَّهِ وَلَا أَعْلَمُ الْغَيْبَ وَلَا أَقُولُ إِنِّي مَلَكٌ وَلَا أَقُولُ لِلَّذِينَ تَزْدَرِي أَعْيُنُكُمْ لَنْ يُؤْتِيَهُمُ اللَّهُ خَيْرًا ۖ اللَّهُ أَعْلَمُ بِمَا فِي أَنْفُسِهِمْ ۖ إِنِّي إِذًا لَمِنَ الظَّالِمِينَ', 11, 31, 12, 45, 225, 'من هرگز به شما نمی‌گویم خزائن الهی نزد من است! و غیب هم نمی‌دانم! و نمی‌گویم من فرشته‌ام! و (نیز) نمی‌گویم کسانی که در نظر شما خوار می‌آیند، خداوند خیری به آنها نخواهد داد؛ خدا از دل آنان آگاهتر است! (با این حال، اگر آنها را برانم،) در این صورت از ستمکاران خواهم بود!»'),
(1505, 1505, 'قَالُوا يَا نُوحُ قَدْ جَادَلْتَنَا فَأَكْثَرْتَ جِدَالَنَا فَأْتِنَا بِمَا تَعِدُنَا إِنْ كُنْتَ مِنَ الصَّادِقِينَ', 11, 32, 12, 45, 225, 'گفتند: «ای نوح! با ما جر و بحث کردی، و زیاد هم جر و بحث کردی! (بس است!) اکنون اگر راستی می‌گویی، آنچه را (از عذاب الهی) به ما وعده می‌دهی بیاور!»'),
(1506, 1506, 'قَالَ إِنَّمَا يَأْتِيكُمْ بِهِ اللَّهُ إِنْ شَاءَ وَمَا أَنْتُمْ بِمُعْجِزِينَ', 11, 33, 12, 45, 225, '(نوح) گفت: «اگر خدا اراده کند، خواهد آورد؛ و شما قدرت فرار (از آن را) نخواهید داشت!'),
(1507, 1507, 'وَلَا يَنْفَعُكُمْ نُصْحِي إِنْ أَرَدْتُ أَنْ أَنْصَحَ لَكُمْ إِنْ كَانَ اللَّهُ يُرِيدُ أَنْ يُغْوِيَكُمْ ۚ هُوَ رَبُّكُمْ وَإِلَيْهِ تُرْجَعُونَ', 11, 34, 12, 45, 225, '(اما چه سود که) هرگاه خدا بخواهد شما را (بخاطر گناهانتان) گمراه سازد، و من بخواهم شما را اندرز دهم، اندرز من سودی به حالتان نخواهد داشت! او پروردگار شماست؛ و بسوی او بازگشت داده می‌شوید.»'),
(1508, 1508, 'أَمْ يَقُولُونَ افْتَرَاهُ ۖ قُلْ إِنِ افْتَرَيْتُهُ فَعَلَيَّ إِجْرَامِي وَأَنَا بَرِيءٌ مِمَّا تُجْرِمُونَ', 11, 35, 12, 45, 225, '(مشرکان) می‌گویند: «او = محمد (ص) این سخنان را بدروغ به خدا نسبت داده است! «بگو: «اگر من اینها را از پیش خود ساخته باشم و به او نسبت دهم، گناهش بر عهده من است؛ ولی من از گناهان شما بیزارم!»'),
(1509, 1509, 'وَأُوحِيَ إِلَىٰ نُوحٍ أَنَّهُ لَنْ يُؤْمِنَ مِنْ قَوْمِكَ إِلَّا مَنْ قَدْ آمَنَ فَلَا تَبْتَئِسْ بِمَا كَانُوا يَفْعَلُونَ', 11, 36, 12, 45, 225, 'به نوح وحی شد که: «جز آنها که (تاکنون) ایمان آورده‌اند، دیگر هیچ کس از قوم تو ایمان نخواهد آورد! پس، از کارهایی که می‌کردند، غمگین مباش!'),
(1510, 1510, 'وَاصْنَعِ الْفُلْكَ بِأَعْيُنِنَا وَوَحْيِنَا وَلَا تُخَاطِبْنِي فِي الَّذِينَ ظَلَمُوا ۚ إِنَّهُمْ مُغْرَقُونَ', 11, 37, 12, 45, 225, 'و (اکنون) در حضور ما و طبق وحی ما، کشتی بساز! و درباره آنها که ستم کردند شفاعت مکن، که (همه) آنها غرق شدنی هستند!»'),
(1511, 1511, 'وَيَصْنَعُ الْفُلْكَ وَكُلَّمَا مَرَّ عَلَيْهِ مَلَأٌ مِنْ قَوْمِهِ سَخِرُوا مِنْهُ ۚ قَالَ إِنْ تَسْخَرُوا مِنَّا فَإِنَّا نَسْخَرُ مِنْكُمْ كَمَا تَسْخَرُونَ', 11, 38, 12, 45, 226, 'او مشغول ساختن کشتی بود، و هر زمان گروهی از اشراف قومش بر او می‌گذشتند، او را مسخره می‌کردند؛ (ولی نوح) گفت: «اگر ما را مسخره می‌کنید، ما نیز شما را همین‌گونه مسخره خواهیم کرد!'),
(1512, 1512, 'فَسَوْفَ تَعْلَمُونَ مَنْ يَأْتِيهِ عَذَابٌ يُخْزِيهِ وَيَحِلُّ عَلَيْهِ عَذَابٌ مُقِيمٌ', 11, 39, 12, 45, 226, 'بزودی خواهید دانست چه کسی عذاب خوارکننده به سراغش خواهد آمد، و مجازات جاودان بر او وارد خواهد شد!»'),
(1513, 1513, 'حَتَّىٰ إِذَا جَاءَ أَمْرُنَا وَفَارَ التَّنُّورُ قُلْنَا احْمِلْ فِيهَا مِنْ كُلٍّ زَوْجَيْنِ اثْنَيْنِ وَأَهْلَكَ إِلَّا مَنْ سَبَقَ عَلَيْهِ الْقَوْلُ وَمَنْ آمَنَ ۚ وَمَا آمَنَ مَعَهُ إِلَّا قَلِيلٌ', 11, 40, 12, 45, 226, '(این وضع همچنان ادامه یافت) تا آن زمان که فرمان ما فرا رسید، و تنور جوشیدن گرفت؛ (به نوح) گفتیم: «از هر جفتی از حیوانات (از نر و ماده) یک زوج در آن (کشتی) حمل کن! همچنین خاندانت را (بر آن سوار کن) -مگر آنها که قبلاً وعده هلاک آنان داده شده = همسر و یکی از فرزندانت‌- و همچنین مؤمنان را!» اما جز عده کمی همراه او ایمان نیاوردند!'),
(1514, 1514, ' وَقَالَ ارْكَبُوا فِيهَا بِسْمِ اللَّهِ مَجْرَاهَا وَمُرْسَاهَا ۚ إِنَّ رَبِّي لَغَفُورٌ رَحِيمٌ', 11, 41, 12, 46, 226, 'او گفت: «به نام خدا بر آن سوار شوید! و هنگام حرکت و توقف کشتی، یاد او کنید، که پروردگارم آمرزنده و مهربان است!»'),
(1515, 1515, 'وَهِيَ تَجْرِي بِهِمْ فِي مَوْجٍ كَالْجِبَالِ وَنَادَىٰ نُوحٌ ابْنَهُ وَكَانَ فِي مَعْزِلٍ يَا بُنَيَّ ارْكَبْ مَعَنَا وَلَا تَكُنْ مَعَ الْكَافِرِينَ', 11, 42, 12, 46, 226, 'و آن کشتی، آنها را از میان امواجی همچون کوه‌ها حرکت میداد؛ (در این هنگام،) نوح فرزندش را که در گوشه‌ای بود صدا زد: «پسرم! همراه ما سوار شو، و با کافران مباش!»'),
(1516, 1516, 'قَالَ سَآوِي إِلَىٰ جَبَلٍ يَعْصِمُنِي مِنَ الْمَاءِ ۚ قَالَ لَا عَاصِمَ الْيَوْمَ مِنْ أَمْرِ اللَّهِ إِلَّا مَنْ رَحِمَ ۚ وَحَالَ بَيْنَهُمَا الْمَوْجُ فَكَانَ مِنَ الْمُغْرَقِينَ', 11, 43, 12, 46, 226, 'گفت: «بزودی به کوهی پناه میبرم تا مرا از آب حفظ کند!» (نوح) گفت: «امروز هیچ نگهداری در برابر فرمان خدا نیسست؛ مگر آن کس را که او رحم کند!» در این هنگام، موج در میان آن دو حایل شد؛ و او در زمره غرق‌شدگان قرار گرفت!'),
(1517, 1517, 'وَقِيلَ يَا أَرْضُ ابْلَعِي مَاءَكِ وَيَا سَمَاءُ أَقْلِعِي وَغِيضَ الْمَاءُ وَقُضِيَ الْأَمْرُ وَاسْتَوَتْ عَلَى الْجُودِيِّ ۖ وَقِيلَ بُعْدًا لِلْقَوْمِ الظَّالِمِينَ', 11, 44, 12, 46, 226, 'و گفته شد: «ای زمین، آبت را فرو بر! و ای آسمان، خودداری کن! و آب فرو نشست و کار پایان یافت و (کشتی) بر (دامنه کوه) جودی، پهلو گرفت؛ و (در این هنگام،) گفته شد:» دور باد قوم ستمگر (از سعادت و نجات و رحمت خدا!)»'),
(1518, 1518, 'وَنَادَىٰ نُوحٌ رَبَّهُ فَقَالَ رَبِّ إِنَّ ابْنِي مِنْ أَهْلِي وَإِنَّ وَعْدَكَ الْحَقُّ وَأَنْتَ أَحْكَمُ الْحَاكِمِينَ', 11, 45, 12, 46, 226, 'نوح به پروردگارش عرض کرد: «پروردگارا! پسرم از خاندان من است؛ و وعده تو (در مورد نجات خاندانم) حق است؛ و تو از همه حکم‌کنندگان برتری!»'),
(1519, 1519, 'قَالَ يَا نُوحُ إِنَّهُ لَيْسَ مِنْ أَهْلِكَ ۖ إِنَّهُ عَمَلٌ غَيْرُ صَالِحٍ ۖ فَلَا تَسْأَلْنِ مَا لَيْسَ لَكَ بِهِ عِلْمٌ ۖ إِنِّي أَعِظُكَ أَنْ تَكُونَ مِنَ الْجَاهِلِينَ', 11, 46, 12, 46, 227, 'فرمود: «ای نوح! او از اهل تو نیست! او عمل غیر صالحی است = فرد ناشایسته‌ای است‌! پس، آنچه را از آن آگاه نیستی، از من مخواه! من به تو اندرز می‌دهم تا از جاهلان نباشی!!»'),
(1520, 1520, 'قَالَ رَبِّ إِنِّي أَعُوذُ بِكَ أَنْ أَسْأَلَكَ مَا لَيْسَ لِي بِهِ عِلْمٌ ۖ وَإِلَّا تَغْفِرْ لِي وَتَرْحَمْنِي أَكُنْ مِنَ الْخَاسِرِينَ', 11, 47, 12, 46, 227, 'عرض کرد: «پروردگارا! من به تو پناه می‌برم که از تو چیزی بخواهم که از آن آگاهی ندارم! و اگر مرا نبخشی، و بر من رحم نکنی، از زیانکاران خواهم بود!»'),
(1521, 1521, 'قِيلَ يَا نُوحُ اهْبِطْ بِسَلَامٍ مِنَّا وَبَرَكَاتٍ عَلَيْكَ وَعَلَىٰ أُمَمٍ مِمَّنْ مَعَكَ ۚ وَأُمَمٌ سَنُمَتِّعُهُمْ ثُمَّ يَمَسُّهُمْ مِنَّا عَذَابٌ أَلِيمٌ', 11, 48, 12, 46, 227, '(به نوح) گفته شد: «ای نوح! با سلامت و برکاتی از ناحیه ما بر تو و بر تمام امتهایی که با تواند، فرود آی! و امتهای نیز هستند که ما آنها را از نعمتها بهره‌مند خواهیم ساخت، سپس عذاب دردناکی از سوی ما به آنها می‌رسد (، چرا که این نعمتها را کفران می‌کنند!)»'),
(1522, 1522, 'تِلْكَ مِنْ أَنْبَاءِ الْغَيْبِ نُوحِيهَا إِلَيْكَ ۖ مَا كُنْتَ تَعْلَمُهَا أَنْتَ وَلَا قَوْمُكَ مِنْ قَبْلِ هَٰذَا ۖ فَاصْبِرْ ۖ إِنَّ الْعَاقِبَةَ لِلْمُتَّقِينَ', 11, 49, 12, 46, 227, 'اینها از خبرهای غیب است که به تو (ای پیامبر) وحی می‌کنیم؛ نه تو، و نه قومت، اینها را پیش از این نمی‌دانستید! بنابر این، صبر و استقامت کن، که عاقبت از آن پرهیزگاران است!'),
(1523, 1523, 'وَإِلَىٰ عَادٍ أَخَاهُمْ هُودًا ۚ قَالَ يَا قَوْمِ اعْبُدُوا اللَّهَ مَا لَكُمْ مِنْ إِلَٰهٍ غَيْرُهُ ۖ إِنْ أَنْتُمْ إِلَّا مُفْتَرُونَ', 11, 50, 12, 46, 227, '(ما) به سوی (قوم) عاد، برادرشان «هود» را فرستادیم؛ (به آنها) گفت: «ای قوم من! خدا را پرستش کنید، که معبودی جز او برای شما نیست! شما فقط تهمت می‌زنید (و بتها را شریک او می‌خوانید)!'),
(1524, 1524, 'يَا قَوْمِ لَا أَسْأَلُكُمْ عَلَيْهِ أَجْرًا ۖ إِنْ أَجْرِيَ إِلَّا عَلَى الَّذِي فَطَرَنِي ۚ أَفَلَا تَعْقِلُونَ', 11, 51, 12, 46, 227, 'ای قوم من! من از شما برای این (رسالت)، پاداشی نمی‌طلبم؛ پاداش من، تنها بر کسی است که مرا آفریده است؛ آیا نمی‌فهمید؟!'),
(1525, 1525, 'وَيَا قَوْمِ اسْتَغْفِرُوا رَبَّكُمْ ثُمَّ تُوبُوا إِلَيْهِ يُرْسِلِ السَّمَاءَ عَلَيْكُمْ مِدْرَارًا وَيَزِدْكُمْ قُوَّةً إِلَىٰ قُوَّتِكُمْ وَلَا تَتَوَلَّوْا مُجْرِمِينَ', 11, 52, 12, 46, 227, 'و ای قوم من! از پروردگارتان طلب آمرزش کنید، سپس به سوی او بازگردید، تا (باران) آسمان را پی در پی بر شما بفرستد؛ و نیرویی بر نیرویتان بیفزاید! و گنهکارانه، روی (از حق) بر نتابید!»'),
(1526, 1526, 'قَالُوا يَا هُودُ مَا جِئْتَنَا بِبَيِّنَةٍ وَمَا نَحْنُ بِتَارِكِي آلِهَتِنَا عَنْ قَوْلِكَ وَمَا نَحْنُ لَكَ بِمُؤْمِنِينَ', 11, 53, 12, 46, 227, 'گفتند: «ای هود! تو دلیل روشنی برای ما نیاورده‌ای! و ما خدایان خود را بخاطر حرف تو، رها نخواهیم کرد! و ما (اصلاً) به تو ایمان نمی‌آوریم!'),
(1527, 1527, 'إِنْ نَقُولُ إِلَّا اعْتَرَاكَ بَعْضُ آلِهَتِنَا بِسُوءٍ ۗ قَالَ إِنِّي أُشْهِدُ اللَّهَ وَاشْهَدُوا أَنِّي بَرِيءٌ مِمَّا تُشْرِكُونَ', 11, 54, 12, 46, 228, 'ما (درباره تو) فقط می‌گوییم: بعضی از خدایان ما، به تو زیان رسانده (و عقلت را ربوده) اند!» (هود) گفت: «من خدا را به شهادت می‌طلبم، شما نیز گواه باشید که من بیزارم از آنچه شریک (خدا) قرارمی‌دهید...'),
(1528, 1528, 'مِنْ دُونِهِ ۖ فَكِيدُونِي جَمِيعًا ثُمَّ لَا تُنْظِرُونِ', 11, 55, 12, 46, 228, 'از آنچه غیر او (می‌پرستید)! حال که چنین است، همگی برای من نقشه بکشید؛ و مرا مهلت ندهید! (اما بدانید کاری از دست شما ساخته نیست!)'),
(1529, 1529, 'إِنِّي تَوَكَّلْتُ عَلَى اللَّهِ رَبِّي وَرَبِّكُمْ ۚ مَا مِنْ دَابَّةٍ إِلَّا هُوَ آخِذٌ بِنَاصِيَتِهَا ۚ إِنَّ رَبِّي عَلَىٰ صِرَاطٍ مُسْتَقِيمٍ', 11, 56, 12, 46, 228, 'من، بر «الله» که پروردگار من و شماست، توکل کرده‌ام! هیچ جنبنده‌ای نیست مگر اینکه او بر آن تسلط دارد؛ (اما سلطه‌ای با عدالت! چرا که) پروردگار من بر راه راست است!'),
(1530, 1530, 'فَإِنْ تَوَلَّوْا فَقَدْ أَبْلَغْتُكُمْ مَا أُرْسِلْتُ بِهِ إِلَيْكُمْ ۚ وَيَسْتَخْلِفُ رَبِّي قَوْمًا غَيْرَكُمْ وَلَا تَضُرُّونَهُ شَيْئًا ۚ إِنَّ رَبِّي عَلَىٰ كُلِّ شَيْءٍ حَفِيظٌ', 11, 57, 12, 46, 228, 'پس اگر روی برگردانید، من رسالتی را که مأمور بودم به شما رساندم؛ و پروردگارم گروه دیگری را جانشین شما می‌کند؛ و شما کمترین ضرری به او نمی‌رسایند؛ پروردگارم حافظ و نگاهبان هر چیز است!»'),
(1531, 1531, 'وَلَمَّا جَاءَ أَمْرُنَا نَجَّيْنَا هُودًا وَالَّذِينَ آمَنُوا مَعَهُ بِرَحْمَةٍ مِنَّا وَنَجَّيْنَاهُمْ مِنْ عَذَابٍ غَلِيظٍ', 11, 58, 12, 46, 228, 'و هنگامی که فرمان ما فرا رسید، «هود» و کسانی را که با او ایمان آورده بودند، به رحمت خود نجات دادیم؛ و آنها را از عذاب شدید، رهایی بخشیدیم!'),
(1532, 1532, 'وَتِلْكَ عَادٌ ۖ جَحَدُوا بِآيَاتِ رَبِّهِمْ وَعَصَوْا رُسُلَهُ وَاتَّبَعُوا أَمْرَ كُلِّ جَبَّارٍ عَنِيدٍ', 11, 59, 12, 46, 228, 'و این قوم «عاد» بود که آیات پروردگارشان را انکار کردند؛ و پیامبران او را معصیت نمودند؛ و از فرمان هر ستمگر دشمن حق، پیروی کردند!'),
(1533, 1533, 'وَأُتْبِعُوا فِي هَٰذِهِ الدُّنْيَا لَعْنَةً وَيَوْمَ الْقِيَامَةِ ۗ أَلَا إِنَّ عَادًا كَفَرُوا رَبَّهُمْ ۗ أَلَا بُعْدًا لِعَادٍ قَوْمِ هُودٍ', 11, 60, 12, 46, 228, 'آنان، در این دنیا و روز قیامت، لعنت (و نام ننگینی) بدنبال دارند! بدانید «عاد» نسبت به پروردگارشان کفر ورزیدند! دور باد «عاد» -قوم هود- (از رحمت خدا، و خیر و سعادت)!'),
(1534, 1534, ' وَإِلَىٰ ثَمُودَ أَخَاهُمْ صَالِحًا ۚ قَالَ يَا قَوْمِ اعْبُدُوا اللَّهَ مَا لَكُمْ مِنْ إِلَٰهٍ غَيْرُهُ ۖ هُوَ أَنْشَأَكُمْ مِنَ الْأَرْضِ وَاسْتَعْمَرَكُمْ فِيهَا فَاسْتَغْفِرُوهُ ثُمَّ تُوبُوا إِلَيْهِ ۚ إِنَّ رَبِّي قَرِيبٌ مُجِيبٌ', 11, 61, 12, 46, 228, 'و بسوی قوم «ثمود»، برادرشان «صالح» را (فرستادیم)؛ گفت: «ای قوم من! خدا را پرستش کنید، که معبودی جز او برای شما نیست! اوست که شما را از زمین آفرید، و آبادی آن را به شما واگذاشت! از او آمرزش بطلبید، سپس به سوی او بازگردید، که پروردگارم (به بندگان خود) نزدیک، و اجابت‌کننده (خواسته‌های آنها) است!»'),
(1535, 1535, 'قَالُوا يَا صَالِحُ قَدْ كُنْتَ فِينَا مَرْجُوًّا قَبْلَ هَٰذَا ۖ أَتَنْهَانَا أَنْ نَعْبُدَ مَا يَعْبُدُ آبَاؤُنَا وَإِنَّنَا لَفِي شَكٍّ مِمَّا تَدْعُونَا إِلَيْهِ مُرِيبٍ', 11, 62, 12, 46, 228, 'گفتند: «ای صالح! تو پیش از این، مایه امید ما بودی! آیا ما را از پرستش آنچه پدرانمان می‌پرستیدند، نهی می‌کنی؟! در حالی که ما، در مورد آنچه به سوی آن دعوتمان می‌کنی، در شک و تردید هستیم!»'),
(1536, 1536, 'قَالَ يَا قَوْمِ أَرَأَيْتُمْ إِنْ كُنْتُ عَلَىٰ بَيِّنَةٍ مِنْ رَبِّي وَآتَانِي مِنْهُ رَحْمَةً فَمَنْ يَنْصُرُنِي مِنَ اللَّهِ إِنْ عَصَيْتُهُ ۖ فَمَا تَزِيدُونَنِي غَيْرَ تَخْسِيرٍ', 11, 63, 12, 46, 229, 'گفت: «ای قوم! اگر من دلیل آشکاری از پروردگارم داشته باشم، و رحمتی از جانب خود به من داده باشد (، می‌توانم از ابلاغ رسالت او سرپیچی کنم)؟! اگر من نافرمانی او کنم، چه کسی می‌تواند مرا در برابر وی یاری دهد؟! پس، (سخنان) شما، جز اطمینان به زیانکار بودنتان، چیزی بر من نمی‌افزاید!'),
(1537, 1537, 'وَيَا قَوْمِ هَٰذِهِ نَاقَةُ اللَّهِ لَكُمْ آيَةً فَذَرُوهَا تَأْكُلْ فِي أَرْضِ اللَّهِ وَلَا تَمَسُّوهَا بِسُوءٍ فَيَأْخُذَكُمْ عَذَابٌ قَرِيبٌ', 11, 64, 12, 46, 229, 'ای قوم من! این «ناقه» خداوند است، که برای شما نشانه‌ای است؛ بگذارید در زمین خدا به چرا مشغول شود؛ هیچ گونه آزاری به آن نرسانید، که بزودی عذاب خدا شما را خواهد گرفت!»'),
(1538, 1538, 'فَعَقَرُوهَا فَقَالَ تَمَتَّعُوا فِي دَارِكُمْ ثَلَاثَةَ أَيَّامٍ ۖ ذَٰلِكَ وَعْدٌ غَيْرُ مَكْذُوبٍ', 11, 65, 12, 46, 229, '(اما) آنها آن (ناقه) را از پای در آوردند! و (صالح به آنها) گفت: «(مهلت شما تمام شد!) سه روز در خانه‌هایتان بهره‌مند گردید؛ (و بعد از آن، عذاب الهی فرا خواهد رسید؛) این وعده‌ای است که دروغ نخواهد بود!»'),
(1539, 1539, 'فَلَمَّا جَاءَ أَمْرُنَا نَجَّيْنَا صَالِحًا وَالَّذِينَ آمَنُوا مَعَهُ بِرَحْمَةٍ مِنَّا وَمِنْ خِزْيِ يَوْمِئِذٍ ۗ إِنَّ رَبَّكَ هُوَ الْقَوِيُّ الْعَزِيزُ', 11, 66, 12, 46, 229, 'و هنگامی که فرمان (مجازات) ما فرا رسید، صالح و کسانی را که با او ایمان آورده بودند، به رحمت خود (از آن عذاب) و از رسوایی آن روز، رهایی بخشیدیم؛ چرا که پروردگارت قوی و شکست ناپذیر است!'),
(1540, 1540, 'وَأَخَذَ الَّذِينَ ظَلَمُوا الصَّيْحَةُ فَأَصْبَحُوا فِي دِيَارِهِمْ جَاثِمِينَ', 11, 67, 12, 46, 229, 'و کسانی را که ستم کرده بودند، صیحه (آسمانی) فروگرفت؛ و در خانه‌هایشان به روی افتادند و مردند...'),
(1541, 1541, 'كَأَنْ لَمْ يَغْنَوْا فِيهَا ۗ أَلَا إِنَّ ثَمُودَ كَفَرُوا رَبَّهُمْ ۗ أَلَا بُعْدًا لِثَمُودَ', 11, 68, 12, 46, 229, 'آنچنان که گویی هرگز ساکن آن دیار نبودند! بدانید قوم ثمود، پروردگارشان را انکار کردند! دور باد قوم ثمود (از رحمت پروردگار)!'),
(1542, 1542, 'وَلَقَدْ جَاءَتْ رُسُلُنَا إِبْرَاهِيمَ بِالْبُشْرَىٰ قَالُوا سَلَامًا ۖ قَالَ سَلَامٌ ۖ فَمَا لَبِثَ أَنْ جَاءَ بِعِجْلٍ حَنِيذٍ', 11, 69, 12, 46, 229, 'فرستادگان ما = فرشتگان‌ برای ابراهیم بشارت آوردند؛ گفتند: «سلام!» (او نیز) گفت: «سلام!» و طولی نکشید که گوساله بریانی (برای آنها) آورد.'),
(1543, 1543, 'فَلَمَّا رَأَىٰ أَيْدِيَهُمْ لَا تَصِلُ إِلَيْهِ نَكِرَهُمْ وَأَوْجَسَ مِنْهُمْ خِيفَةً ۚ قَالُوا لَا تَخَفْ إِنَّا أُرْسِلْنَا إِلَىٰ قَوْمِ لُوطٍ', 11, 70, 12, 46, 229, '(اما) هنگامی که دید دست آنها به آن نمی‌رسد (و از آن نمی‌خورند، کار) آنها را زشت شمرد؛ و در دل احساس ترس نمود. به او گفتند: «نترس! ما به سوی قوم لوط فرستاده شده‌ایم!»'),
(1544, 1544, 'وَامْرَأَتُهُ قَائِمَةٌ فَضَحِكَتْ فَبَشَّرْنَاهَا بِإِسْحَاقَ وَمِنْ وَرَاءِ إِسْحَاقَ يَعْقُوبَ', 11, 71, 12, 46, 229, 'و همسرش ایستاده بود، (از خوشحالی) خندید؛ پس او را بشارت به اسحاق، و بعد از او یعقوب دادیم.'),
(1545, 1545, 'قَالَتْ يَا وَيْلَتَىٰ أَأَلِدُ وَأَنَا عَجُوزٌ وَهَٰذَا بَعْلِي شَيْخًا ۖ إِنَّ هَٰذَا لَشَيْءٌ عَجِيبٌ', 11, 72, 12, 46, 230, 'گفت: «ای وای بر من! آیا من فرزند می‌آورم در حالی که پیرزنم، و این شوهرم پیرمردی است؟! این راستی چیز عجیبی است!»'),
(1546, 1546, 'قَالُوا أَتَعْجَبِينَ مِنْ أَمْرِ اللَّهِ ۖ رَحْمَتُ اللَّهِ وَبَرَكَاتُهُ عَلَيْكُمْ أَهْلَ الْبَيْتِ ۚ إِنَّهُ حَمِيدٌ مَجِيدٌ', 11, 73, 12, 46, 230, 'گفتند: «آیا از فرمان خدا تعجب میکنی؟! این رحمت خدا و برکاتش بر شما خانواده است؛ چرا که او ستوده و والا است!»'),
(1547, 1547, 'فَلَمَّا ذَهَبَ عَنْ إِبْرَاهِيمَ الرَّوْعُ وَجَاءَتْهُ الْبُشْرَىٰ يُجَادِلُنَا فِي قَوْمِ لُوطٍ', 11, 74, 12, 46, 230, 'هنگامی که ترس ابراهیم فرو نشست، و بشارت به او رسید، درباره قوم لوط با ما مجادله می‌کرد...'),
(1548, 1548, 'إِنَّ إِبْرَاهِيمَ لَحَلِيمٌ أَوَّاهٌ مُنِيبٌ', 11, 75, 12, 46, 230, 'چرا که ابراهیم، بردبار و دلسوز و بازگشت‌کننده (بسوی خدا) بود!'),
(1549, 1549, 'يَا إِبْرَاهِيمُ أَعْرِضْ عَنْ هَٰذَا ۖ إِنَّهُ قَدْ جَاءَ أَمْرُ رَبِّكَ ۖ وَإِنَّهُمْ آتِيهِمْ عَذَابٌ غَيْرُ مَرْدُودٍ', 11, 76, 12, 46, 230, 'ای ابراهیم! از این (درخواست) صرف‌نظر کن، که فرمان پروردگارت فرا رسیده؛ و بطور قطع عذاب (الهی) به سراغ آنها می‌آید؛ و برگشت ندارد!'),
(1550, 1550, 'وَلَمَّا جَاءَتْ رُسُلُنَا لُوطًا سِيءَ بِهِمْ وَضَاقَ بِهِمْ ذَرْعًا وَقَالَ هَٰذَا يَوْمٌ عَصِيبٌ', 11, 77, 12, 46, 230, 'و هنگامی که رسولان ما = فرشتگان عذاب‌ به سراغ لوط آمدند، از آمدنشان ناراحت شد؛ و قلبش پریشان گشت؛ و گفت: «امروز روز سختی است!» (زیرا آنها را نشناخت؛ و ترسید قوم تبهکار مزاحم آنها شوند.)'),
(1551, 1551, 'وَجَاءَهُ قَوْمُهُ يُهْرَعُونَ إِلَيْهِ وَمِنْ قَبْلُ كَانُوا يَعْمَلُونَ السَّيِّئَاتِ ۚ قَالَ يَا قَوْمِ هَٰؤُلَاءِ بَنَاتِي هُنَّ أَطْهَرُ لَكُمْ ۖ فَاتَّقُوا اللَّهَ وَلَا تُخْزُونِ فِي ضَيْفِي ۖ أَلَيْسَ مِنْكُمْ رَجُلٌ رَشِيدٌ', 11, 78, 12, 46, 230, 'قوم او (بقصد مزاحمت میهمانان) بسرعت به سراغ او آمدند -و قبلاً کارهای بد انجام می‌دادند- گفت: «ای قوم من! اینها دختران منند؛ برای شما پاکیزه‌ترند! (با آنها ازدواج کنید؛ و از زشتکاری چشم بپوشید!) از خدا بترسید؛ و مرا در مورد میهمانانم رسوا نسازید! آیا در میان شما یک مرد فهمیده و آگاه وجود ندارد؟!»');
INSERT INTO `Quran` (`rowid`, `id`, `Arabic`, `Chapter`, `Verse`, `Juz`, `Hizb`, `Page`, `tr`) VALUES
(1552, 1552, 'قَالُوا لَقَدْ عَلِمْتَ مَا لَنَا فِي بَنَاتِكَ مِنْ حَقٍّ وَإِنَّكَ لَتَعْلَمُ مَا نُرِيدُ', 11, 79, 12, 46, 230, 'گفتند: «تو که می‌دانی ما تمایلی به دختران تو نداریم؛ و خوب می‌دانی ما چه می‌خواهیم!»'),
(1553, 1553, 'قَالَ لَوْ أَنَّ لِي بِكُمْ قُوَّةً أَوْ آوِي إِلَىٰ رُكْنٍ شَدِيدٍ', 11, 80, 12, 46, 230, 'گفت: «(افسوس!) ای کاش در برابر شما قدرتی داشتم؛ یا تکیه‌گاه و پشتیبان محکمی در اختیار من بود! (آنگاه می‌دانستم با شما زشت‌سیرتان ددمنش چه کنم!)»'),
(1554, 1554, 'قَالُوا يَا لُوطُ إِنَّا رُسُلُ رَبِّكَ لَنْ يَصِلُوا إِلَيْكَ ۖ فَأَسْرِ بِأَهْلِكَ بِقِطْعٍ مِنَ اللَّيْلِ وَلَا يَلْتَفِتْ مِنْكُمْ أَحَدٌ إِلَّا امْرَأَتَكَ ۖ إِنَّهُ مُصِيبُهَا مَا أَصَابَهُمْ ۚ إِنَّ مَوْعِدَهُمُ الصُّبْحُ ۚ أَلَيْسَ الصُّبْحُ بِقَرِيبٍ', 11, 81, 12, 46, 230, '(فرشتگان عذاب) گفتند: «ای لوط! ما فرستادگان پروردگار توایم! آنها هرگز دسترسی به تو پیدا نخواهند کرد! در دل شب، خانواده‌ات را (از این شهر) حرکت ده! و هیچ یک از شما پشت سرش را نگاه نکند؛ مگر همسرت، که او هم به همان بلایی که آنها گرفتار می‌شوند، گرفتار خواهد شد! موعد آنها صبح است؛ آیا صبح نزدیک نیست؟!»'),
(1555, 1555, 'فَلَمَّا جَاءَ أَمْرُنَا جَعَلْنَا عَالِيَهَا سَافِلَهَا وَأَمْطَرْنَا عَلَيْهَا حِجَارَةً مِنْ سِجِّيلٍ مَنْضُودٍ', 11, 82, 12, 46, 231, 'و هنگامی که فرمان ما فرا رسید، آن (شهر و دیار) را زیر و رو کردیم؛ و بارانی از سنگ = گِلهای متحجر متراکم بر روی هم، بر آنها نازل نمودیم...'),
(1556, 1556, 'مُسَوَّمَةً عِنْدَ رَبِّكَ ۖ وَمَا هِيَ مِنَ الظَّالِمِينَ بِبَعِيدٍ', 11, 83, 12, 46, 231, '(سنگهایی که) نزد پروردگارت نشاندار بود؛ و آن، (از سایر) ستمگران دور نیست!'),
(1557, 1557, ' وَإِلَىٰ مَدْيَنَ أَخَاهُمْ شُعَيْبًا ۚ قَالَ يَا قَوْمِ اعْبُدُوا اللَّهَ مَا لَكُمْ مِنْ إِلَٰهٍ غَيْرُهُ ۖ وَلَا تَنْقُصُوا الْمِكْيَالَ وَالْمِيزَانَ ۚ إِنِّي أَرَاكُمْ بِخَيْرٍ وَإِنِّي أَخَافُ عَلَيْكُمْ عَذَابَ يَوْمٍ مُحِيطٍ', 11, 84, 12, 47, 231, 'و بسوی «مدین» برادرشان شعیب را (فرستادیم)؛ گفت: «ای قوم من! خدا را پرستش کنید، که جز او، معبود دیگری برای شما نیست! پیمانه و وزن را کم نکنید (و دست به کم‌فروشی نزنید)! من (هم اکنون) شما را در نعمت می‌بینم؛ (ولی) از عذاب روز فراگیر، بر شما بیمناکم!'),
(1558, 1558, 'وَيَا قَوْمِ أَوْفُوا الْمِكْيَالَ وَالْمِيزَانَ بِالْقِسْطِ ۖ وَلَا تَبْخَسُوا النَّاسَ أَشْيَاءَهُمْ وَلَا تَعْثَوْا فِي الْأَرْضِ مُفْسِدِينَ', 11, 85, 12, 47, 231, 'و ای قوم من! پیمانه و وزن را با عدالت، تمام دهید! و بر اشیاء (و اجناس) مردم، عیب نگذارید؛ و از حق آنان نکاهید! و در زمین به فساد نکوشید!'),
(1559, 1559, 'بَقِيَّتُ اللَّهِ خَيْرٌ لَكُمْ إِنْ كُنْتُمْ مُؤْمِنِينَ ۚ وَمَا أَنَا عَلَيْكُمْ بِحَفِيظٍ', 11, 86, 12, 47, 231, 'آنچه خداوند برای شما باقی گذارده (از سرمایه‌های حلال)، برایتان بهتر است اگر ایمان داشته باشید! و من، پاسدار شما (و مأمور بر اجبارتان به ایمان) نیستم!'),
(1560, 1560, 'قَالُوا يَا شُعَيْبُ أَصَلَاتُكَ تَأْمُرُكَ أَنْ نَتْرُكَ مَا يَعْبُدُ آبَاؤُنَا أَوْ أَنْ نَفْعَلَ فِي أَمْوَالِنَا مَا نَشَاءُ ۖ إِنَّكَ لَأَنْتَ الْحَلِيمُ الرَّشِيدُ', 11, 87, 12, 47, 231, 'گفتند: «ای شعیب! آیا نمازت به تو دستور می‌دهد که آنچه را پدرانمان می‌پرستیدند، ترک کنیم؛ یا آنچه را می‌خواهیم در اموالمان انجام ندهیم؟! تو که مرد بردبار و فهمیده‌ای هستی!»'),
(1561, 1561, 'قَالَ يَا قَوْمِ أَرَأَيْتُمْ إِنْ كُنْتُ عَلَىٰ بَيِّنَةٍ مِنْ رَبِّي وَرَزَقَنِي مِنْهُ رِزْقًا حَسَنًا ۚ وَمَا أُرِيدُ أَنْ أُخَالِفَكُمْ إِلَىٰ مَا أَنْهَاكُمْ عَنْهُ ۚ إِنْ أُرِيدُ إِلَّا الْإِصْلَاحَ مَا اسْتَطَعْتُ ۚ وَمَا تَوْفِيقِي إِلَّا بِاللَّهِ ۚ عَلَيْهِ تَوَكَّلْتُ وَإِلَيْهِ أُنِيبُ', 11, 88, 12, 47, 231, 'گفت: «ای قوم! به من بگویید، هرگاه من دلیل آشکاری از پروردگارم داشته باشم، و رزق (و موهبت) خوبی به من داده باشد، (آیا می‌توانم بر خلاف فرمان او رفتار کنم؟!) من هرگز نمی‌خواهم چیزی که شما را از آن باز می‌دارم، خودم مرتکب شوم! من جز اصلاح -تا آنجا که توانایی دارم- نمی‌خواهم! و توفیق من، جز به خدا نیست! بر او توکّل کردم؛ و به سوی او بازمی‌گردم!'),
(1562, 1562, 'وَيَا قَوْمِ لَا يَجْرِمَنَّكُمْ شِقَاقِي أَنْ يُصِيبَكُمْ مِثْلُ مَا أَصَابَ قَوْمَ نُوحٍ أَوْ قَوْمَ هُودٍ أَوْ قَوْمَ صَالِحٍ ۚ وَمَا قَوْمُ لُوطٍ مِنْكُمْ بِبَعِيدٍ', 11, 89, 12, 47, 232, 'و ای قوم من! دشمنی و مخالفت با من، سبب نشود که شما به همان سرنوشتی که قوم نوح یا قوم هود یا قوم صالح گرفتار شدند، گرفتار شوید! و قوم لوط از شما چندان دور نیست!'),
(1563, 1563, 'وَاسْتَغْفِرُوا رَبَّكُمْ ثُمَّ تُوبُوا إِلَيْهِ ۚ إِنَّ رَبِّي رَحِيمٌ وَدُودٌ', 11, 90, 12, 47, 232, 'از پروردگار خود، آمرزش بطلبید؛ و به سوی او بازگردید؛ که پروردگارم مهربان و دوستدار (بندگان توبه‌کار) است!»'),
(1564, 1564, 'قَالُوا يَا شُعَيْبُ مَا نَفْقَهُ كَثِيرًا مِمَّا تَقُولُ وَإِنَّا لَنَرَاكَ فِينَا ضَعِيفًا ۖ وَلَوْلَا رَهْطُكَ لَرَجَمْنَاكَ ۖ وَمَا أَنْتَ عَلَيْنَا بِعَزِيزٍ', 11, 91, 12, 47, 232, 'گفتند: «ای شعیب! بسیاری از آنچه را می‌گویی، ما نمی‌فهمیم! و ما تو را در میان خود، ضعیف می‌یابیم؛ و اگر (بخاطر) قبیله کوچکت نبود، تو را سنگسار می‌کردیم؛ و تو در برابر ما قدرتی نداری!»'),
(1565, 1565, 'قَالَ يَا قَوْمِ أَرَهْطِي أَعَزُّ عَلَيْكُمْ مِنَ اللَّهِ وَاتَّخَذْتُمُوهُ وَرَاءَكُمْ ظِهْرِيًّا ۖ إِنَّ رَبِّي بِمَا تَعْمَلُونَ مُحِيطٌ', 11, 92, 12, 47, 232, 'گفت: «ای قوم! آیا قبیله کوچک من، نزد شما عزیزتر از خداوند است؟! در حالی که (فرمان) او را پشت سر انداخته‌اید! پروردگارم به آنچه انجام می‌دهید، احاطه دارد (و آگاه است)!'),
(1566, 1566, 'وَيَا قَوْمِ اعْمَلُوا عَلَىٰ مَكَانَتِكُمْ إِنِّي عَامِلٌ ۖ سَوْفَ تَعْلَمُونَ مَنْ يَأْتِيهِ عَذَابٌ يُخْزِيهِ وَمَنْ هُوَ كَاذِبٌ ۖ وَارْتَقِبُوا إِنِّي مَعَكُمْ رَقِيبٌ', 11, 93, 12, 47, 232, 'ای قوم! هر کاری از دستتان ساخته است، انجام دهید، من هم کار خود را خواهم کرد؛ و بزودی خواهید دانست چه کسی عذاب خوارکننده به سراغش می‌آید، و چه کسی دروغگوست! شما انتظار بکشید، من هم در انتظارم!»'),
(1567, 1567, 'وَلَمَّا جَاءَ أَمْرُنَا نَجَّيْنَا شُعَيْبًا وَالَّذِينَ آمَنُوا مَعَهُ بِرَحْمَةٍ مِنَّا وَأَخَذَتِ الَّذِينَ ظَلَمُوا الصَّيْحَةُ فَأَصْبَحُوا فِي دِيَارِهِمْ جَاثِمِينَ', 11, 94, 12, 47, 232, 'و هنگامی که فرمان ما فرا رسید، شعیب و کسانی را که با او ایمان آورده بودند، به رحمت خود نجات دادیم؛ و آنها را که ستم کردند، صیحه (آسمانی) فرو گرفت؛ و در دیار خود، به رو افتادند (و مردند)...'),
(1568, 1568, 'كَأَنْ لَمْ يَغْنَوْا فِيهَا ۗ أَلَا بُعْدًا لِمَدْيَنَ كَمَا بَعِدَتْ ثَمُودُ', 11, 95, 12, 47, 232, 'آنچنان که گویی هرگز از ساکنان آن (دیار) نبودند! دور باد مدین (و اهل آن) از رحمت خدا، همان گونه که قوم ثمود دور شدند!'),
(1569, 1569, 'وَلَقَدْ أَرْسَلْنَا مُوسَىٰ بِآيَاتِنَا وَسُلْطَانٍ مُبِينٍ', 11, 96, 12, 47, 232, 'ما، موسی را با آیات خود و دلیل آشکاری فرستادیم...'),
(1570, 1570, 'إِلَىٰ فِرْعَوْنَ وَمَلَئِهِ فَاتَّبَعُوا أَمْرَ فِرْعَوْنَ ۖ وَمَا أَمْرُ فِرْعَوْنَ بِرَشِيدٍ', 11, 97, 12, 47, 232, 'بسوی فرعون و اطرافیانش؛ اما آنها از فرمان فرعون پیروی کردند؛ در حالی که فرمان فرعون، مایه رشد و نجات نبود!'),
(1571, 1571, 'يَقْدُمُ قَوْمَهُ يَوْمَ الْقِيَامَةِ فَأَوْرَدَهُمُ النَّارَ ۖ وَبِئْسَ الْوِرْدُ الْمَوْرُودُ', 11, 98, 12, 47, 233, 'روز قیامت، او در پیشاپیش قومش خواهد بود؛ و (به جای چشمه‌های زلال بهشت) آنها را وارد آتش می‌کند! و چه بد آبشخوری است (آتش)، که بر آن وارد می‌شوند!'),
(1572, 1572, 'وَأُتْبِعُوا فِي هَٰذِهِ لَعْنَةً وَيَوْمَ الْقِيَامَةِ ۚ بِئْسَ الرِّفْدُ الْمَرْفُودُ', 11, 99, 12, 47, 233, 'آنان در این جهان و روز قیامت، لعنتی بدنبال دارند؛ و چه بد عطایی است (لعن و دوری از رحمت خدا)، که نصیب آنان می‌شود!'),
(1573, 1573, 'ذَٰلِكَ مِنْ أَنْبَاءِ الْقُرَىٰ نَقُصُّهُ عَلَيْكَ ۖ مِنْهَا قَائِمٌ وَحَصِيدٌ', 11, 100, 12, 47, 233, 'این از اخبار شهرها و آبادیهاست که ما برای تو بازگو می‌کنیم؛ که بعضی (هنوز) برپا هستند، و بعضی درو شده‌اند (و از میان رفته‌اند)!'),
(1574, 1574, 'وَمَا ظَلَمْنَاهُمْ وَلَٰكِنْ ظَلَمُوا أَنْفُسَهُمْ ۖ فَمَا أَغْنَتْ عَنْهُمْ آلِهَتُهُمُ الَّتِي يَدْعُونَ مِنْ دُونِ اللَّهِ مِنْ شَيْءٍ لَمَّا جَاءَ أَمْرُ رَبِّكَ ۖ وَمَا زَادُوهُمْ غَيْرَ تَتْبِيبٍ', 11, 101, 12, 47, 233, 'ما به آنها ستم نکردیم؛ بلکه آنها خودشان بر خویشتن ستم روا داشتند! و هنگامی که فرمان مجازات الهی فرا رسید، معبودانی را که غیر از خدا می‌خواندند، آنها را یاری نکردند؛ و جز بر هلاکت آنان نیفزودند!'),
(1575, 1575, 'وَكَذَٰلِكَ أَخْذُ رَبِّكَ إِذَا أَخَذَ الْقُرَىٰ وَهِيَ ظَالِمَةٌ ۚ إِنَّ أَخْذَهُ أَلِيمٌ شَدِيدٌ', 11, 102, 12, 47, 233, 'و اینچنین است مجازات پروردگار تو، هنگامی که شهرها و آبادیهای ظالم را مجازات می‌کند! (آری،) مجازات او، دردناک و شدید است!'),
(1576, 1576, 'إِنَّ فِي ذَٰلِكَ لَآيَةً لِمَنْ خَافَ عَذَابَ الْآخِرَةِ ۚ ذَٰلِكَ يَوْمٌ مَجْمُوعٌ لَهُ النَّاسُ وَذَٰلِكَ يَوْمٌ مَشْهُودٌ', 11, 103, 12, 47, 233, 'در این، نشانه‌ای است برای کسی که از عذاب آخرت می‌ترسد؛ همان روزی است که مردم در آن جمع می‌شوند، و روزی که همه آن را مشاهده می‌کنند.'),
(1577, 1577, 'وَمَا نُؤَخِّرُهُ إِلَّا لِأَجَلٍ مَعْدُودٍ', 11, 104, 12, 47, 233, 'و ما آن (مجازات) را، جز تا زمان محدودی، تأخیر نمی‌اندازیم!'),
(1578, 1578, 'يَوْمَ يَأْتِ لَا تَكَلَّمُ نَفْسٌ إِلَّا بِإِذْنِهِ ۚ فَمِنْهُمْ شَقِيٌّ وَسَعِيدٌ', 11, 105, 12, 47, 233, 'آن روز که (قیامت و زمان مجازات) فرا رسد، هیچ کس جز به اجازه او سخن نمی‌گوید؛ گروهی بدبختند و گروهی خوشبخت!'),
(1579, 1579, 'فَأَمَّا الَّذِينَ شَقُوا فَفِي النَّارِ لَهُمْ فِيهَا زَفِيرٌ وَشَهِيقٌ', 11, 106, 12, 47, 233, 'امّا آنها که بدبخت شدند، در آتشند؛ و برای آنان در آنجا، «زفیر» و «شهیق» = ناله‌های طولانی دم و بازدم‌ است...'),
(1580, 1580, 'خَالِدِينَ فِيهَا مَا دَامَتِ السَّمَاوَاتُ وَالْأَرْضُ إِلَّا مَا شَاءَ رَبُّكَ ۚ إِنَّ رَبَّكَ فَعَّالٌ لِمَا يُرِيدُ', 11, 107, 12, 47, 233, 'جاودانه در آن خواهند ماند، تا آسمانها و زمین برپاست؛ مگر آنچه پروردگارت بخواهد! پروردگارت هر چه را بخواهد انجام می‌دهد!'),
(1581, 1581, ' وَأَمَّا الَّذِينَ سُعِدُوا فَفِي الْجَنَّةِ خَالِدِينَ فِيهَا مَا دَامَتِ السَّمَاوَاتُ وَالْأَرْضُ إِلَّا مَا شَاءَ رَبُّكَ ۖ عَطَاءً غَيْرَ مَجْذُوذٍ', 11, 108, 12, 47, 233, 'امّا آنها که خوشبخت و سعادتمند شدند، جاودانه در بهشت خواهند ماند، تا آسمانها و زمین برپاست، مگر آنچه پروردگارت بخواهد! بخششی است قطع نشدنی!'),
(1582, 1582, 'فَلَا تَكُ فِي مِرْيَةٍ مِمَّا يَعْبُدُ هَٰؤُلَاءِ ۚ مَا يَعْبُدُونَ إِلَّا كَمَا يَعْبُدُ آبَاؤُهُمْ مِنْ قَبْلُ ۚ وَإِنَّا لَمُوَفُّوهُمْ نَصِيبَهُمْ غَيْرَ مَنْقُوصٍ', 11, 109, 12, 47, 234, 'پس شکّ و تردیدی (در باطل بودن) معبودهایی که آنها می‌پرستند، به خود راه مده! آنها همان‌گونه این معبودها را پرستش می‌کنند که پدرانشان قبلاً می‌پرستیدند، و ما نصیب آنان را بی‌کم و کاست خواهیم داد!'),
(1583, 1583, 'وَلَقَدْ آتَيْنَا مُوسَى الْكِتَابَ فَاخْتُلِفَ فِيهِ ۚ وَلَوْلَا كَلِمَةٌ سَبَقَتْ مِنْ رَبِّكَ لَقُضِيَ بَيْنَهُمْ ۚ وَإِنَّهُمْ لَفِي شَكٍّ مِنْهُ مُرِيبٍ', 11, 110, 12, 47, 234, 'ما به موسی کتاب آسمانی دادیم؛ سپس در آن اختلاف شد؛ و اگر فرمان قبلی خدا (در زمینه آزمایش و اتمام حجّت بر آنها) نبود، در میان آنان داوری می‌شد! و آنها (هنوز) در شکّ‌اند، شکّی آمیخته به بدگمانی!'),
(1584, 1584, 'وَإِنَّ كُلًّا لَمَّا لَيُوَفِّيَنَّهُمْ رَبُّكَ أَعْمَالَهُمْ ۚ إِنَّهُ بِمَا يَعْمَلُونَ خَبِيرٌ', 11, 111, 12, 47, 234, 'و پروردگارت اعمال هر یک را بی‌کم و کاست به آنها خواهد داد؛ او به آنچه عمل می‌کنند آگاه است!'),
(1585, 1585, 'فَاسْتَقِمْ كَمَا أُمِرْتَ وَمَنْ تَابَ مَعَكَ وَلَا تَطْغَوْا ۚ إِنَّهُ بِمَا تَعْمَلُونَ بَصِيرٌ', 11, 112, 12, 47, 234, 'پس همان‌گونه که فرمان یافته‌ای، استقامت کن؛ و همچنین کسانی که با تو بسوی خدا آمده‌اند (باید استقامت کنند)! و طغیان نکنید، که خداوند آنچه را انجام می‌دهید می‌بیند!'),
(1586, 1586, 'وَلَا تَرْكَنُوا إِلَى الَّذِينَ ظَلَمُوا فَتَمَسَّكُمُ النَّارُ وَمَا لَكُمْ مِنْ دُونِ اللَّهِ مِنْ أَوْلِيَاءَ ثُمَّ لَا تُنْصَرُونَ', 11, 113, 12, 47, 234, 'و بر ظالمان تکیه ننمایید، که موجب می‌شود آتش شما را فرا گیرد؛ و در آن حال، هیچ ولیّ و سرپرستی جز خدا نخواهید داشت؛ و یاری نمی‌شوید!'),
(1587, 1587, 'وَأَقِمِ الصَّلَاةَ طَرَفَيِ النَّهَارِ وَزُلَفًا مِنَ اللَّيْلِ ۚ إِنَّ الْحَسَنَاتِ يُذْهِبْنَ السَّيِّئَاتِ ۚ ذَٰلِكَ ذِكْرَىٰ لِلذَّاكِرِينَ', 11, 114, 12, 47, 234, 'در دو طرف روز، و اوایل شب، نماز را برپا دار؛ چرا که حسنات، سیئات (و آثار آنها را) از بین می‌برند؛ این تذکّری است برای کسانی که اهل تذکّرند!'),
(1588, 1588, 'وَاصْبِرْ فَإِنَّ اللَّهَ لَا يُضِيعُ أَجْرَ الْمُحْسِنِينَ', 11, 115, 12, 47, 234, 'و شکیبایی کن، که خداوند پاداش نیکوکاران را ضایع نخواهد کرد!'),
(1589, 1589, 'فَلَوْلَا كَانَ مِنَ الْقُرُونِ مِنْ قَبْلِكُمْ أُولُو بَقِيَّةٍ يَنْهَوْنَ عَنِ الْفَسَادِ فِي الْأَرْضِ إِلَّا قَلِيلًا مِمَّنْ أَنْجَيْنَا مِنْهُمْ ۗ وَاتَّبَعَ الَّذِينَ ظَلَمُوا مَا أُتْرِفُوا فِيهِ وَكَانُوا مُجْرِمِينَ', 11, 116, 12, 47, 234, 'چرا در قرون (و اقوام) قبل از شما، دانشمندان صاحب قدرتی نبودند که از فساد در زمین جلوگیری کنند؟! مگر اندکی از آنها، که نجاتشان دادیم! و آنان که ستم می‌کردند، از تنعّم و کامجوئی پیروی کردند؛ و گناهکار بودند (و نابود شدند)!'),
(1590, 1590, 'وَمَا كَانَ رَبُّكَ لِيُهْلِكَ الْقُرَىٰ بِظُلْمٍ وَأَهْلُهَا مُصْلِحُونَ', 11, 117, 12, 47, 234, 'و چنین نبود که پروردگارت آبادیها را بظلم و ستم نابود کند در حالی که اهلش در صدد اصلاح بوده باشند!'),
(1591, 1591, 'وَلَوْ شَاءَ رَبُّكَ لَجَعَلَ النَّاسَ أُمَّةً وَاحِدَةً ۖ وَلَا يَزَالُونَ مُخْتَلِفِينَ', 11, 118, 12, 47, 235, 'و اگر پروردگارت می‌خواست، همه مردم را یک امّت (بدون هیچ گونه اختلاف) قرار می‌داد؛ ولی آنها همواره مختلفند...'),
(1592, 1592, 'إِلَّا مَنْ رَحِمَ رَبُّكَ ۚ وَلِذَٰلِكَ خَلَقَهُمْ ۗ وَتَمَّتْ كَلِمَةُ رَبِّكَ لَأَمْلَأَنَّ جَهَنَّمَ مِنَ الْجِنَّةِ وَالنَّاسِ أَجْمَعِينَ', 11, 119, 12, 47, 235, 'مگر کسی را که پروردگارت رحم کند! و برای همین (پذیرش رحمت) آنها را آفرید! و فرمان پروردگارت قطعی شده که: جهنّم را از همه (سرکشان و طاغیان) جنّ و انس پر خواهم کرد!'),
(1593, 1593, 'وَكُلًّا نَقُصُّ عَلَيْكَ مِنْ أَنْبَاءِ الرُّسُلِ مَا نُثَبِّتُ بِهِ فُؤَادَكَ ۚ وَجَاءَكَ فِي هَٰذِهِ الْحَقُّ وَمَوْعِظَةٌ وَذِكْرَىٰ لِلْمُؤْمِنِينَ', 11, 120, 12, 47, 235, 'ما از هر یک از سرگذشتهای انبیا برای تو بازگو کردیم، تا به وسیله آن، قلبت را آرامش بخشیم؛ و اراده‌ات قوّی گردد. و در این (اخبار و سرگذشتها،) برای تو حقّ، و برای مؤمنان موعظه و تذکّر آمده است.'),
(1594, 1594, 'وَقُلْ لِلَّذِينَ لَا يُؤْمِنُونَ اعْمَلُوا عَلَىٰ مَكَانَتِكُمْ إِنَّا عَامِلُونَ', 11, 121, 12, 47, 235, 'و به آنها که ایمان نمی‌آورند، بگو: «هر چه در قدرت دارید، انجام دهید! ما هم انجام می‌دهیم!'),
(1595, 1595, 'وَانْتَظِرُوا إِنَّا مُنْتَظِرُونَ', 11, 122, 12, 47, 235, 'و انتظار بکشید! ما هم منتظریم!'),
(1596, 1596, 'وَلِلَّهِ غَيْبُ السَّمَاوَاتِ وَالْأَرْضِ وَإِلَيْهِ يُرْجَعُ الْأَمْرُ كُلُّهُ فَاعْبُدْهُ وَتَوَكَّلْ عَلَيْهِ ۚ وَمَا رَبُّكَ بِغَافِلٍ عَمَّا تَعْمَلُونَ', 11, 123, 12, 47, 235, 'و (آگاهی از) غیب (و اسرار نهان) آسمانها و زمین، تنها از آن خداست؛ و همه کارها به سوی او بازمی‌گردد! پس او را پرستش کن! و بر او توکّل نما! و پروردگارت از کارهایی که می‌کنید، هرگز غافل نیست!'),
(1597, 1597, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ الر ۚ تِلْكَ آيَاتُ الْكِتَابِ الْمُبِينِ', 12, 1, 12, 47, 235, 'الر، آن آیات کتاب آشکار است!'),
(1598, 1598, 'إِنَّا أَنْزَلْنَاهُ قُرْآنًا عَرَبِيًّا لَعَلَّكُمْ تَعْقِلُونَ', 12, 2, 12, 47, 235, 'ما آن را قرآنی عربی نازل کردیم، شاید شما درک کنید (و بیندیشید)!'),
(1599, 1599, 'نَحْنُ نَقُصُّ عَلَيْكَ أَحْسَنَ الْقَصَصِ بِمَا أَوْحَيْنَا إِلَيْكَ هَٰذَا الْقُرْآنَ وَإِنْ كُنْتَ مِنْ قَبْلِهِ لَمِنَ الْغَافِلِينَ', 12, 3, 12, 47, 235, 'ما بهترین سرگذشتها را از طریق این قرآن -که به تو وحی کردیم- بر تو بازگو می‌کنیم؛ و مسلّماً پیش از این، از آن خبر نداشتی!'),
(1600, 1600, 'إِذْ قَالَ يُوسُفُ لِأَبِيهِ يَا أَبَتِ إِنِّي رَأَيْتُ أَحَدَ عَشَرَ كَوْكَبًا وَالشَّمْسَ وَالْقَمَرَ رَأَيْتُهُمْ لِي سَاجِدِينَ', 12, 4, 12, 47, 235, '(به خاطر بیاور) هنگامی را که یوسف به پدرش گفت: «پدرم! من در خواب دیدم که یازده ستاره، و خورشید و ماه در برابرم سجده می‌کنند!»'),
(1601, 1601, 'قَالَ يَا بُنَيَّ لَا تَقْصُصْ رُؤْيَاكَ عَلَىٰ إِخْوَتِكَ فَيَكِيدُوا لَكَ كَيْدًا ۖ إِنَّ الشَّيْطَانَ لِلْإِنْسَانِ عَدُوٌّ مُبِينٌ', 12, 5, 12, 47, 236, 'گفت: «فرزندم! خواب خود را برای برادرانت بازگو مکن، که برای تو نقشه (خطرناکی) می‌کشند؛ چرا که شیطان، دشمن آشکار انسان است!'),
(1602, 1602, 'وَكَذَٰلِكَ يَجْتَبِيكَ رَبُّكَ وَيُعَلِّمُكَ مِنْ تَأْوِيلِ الْأَحَادِيثِ وَيُتِمُّ نِعْمَتَهُ عَلَيْكَ وَعَلَىٰ آلِ يَعْقُوبَ كَمَا أَتَمَّهَا عَلَىٰ أَبَوَيْكَ مِنْ قَبْلُ إِبْرَاهِيمَ وَإِسْحَاقَ ۚ إِنَّ رَبَّكَ عَلِيمٌ حَكِيمٌ', 12, 6, 12, 47, 236, 'و این گونه پروردگارت تو را برمی‌گزیند؛ و از تعبیر خوابها به تو می‌آموزد؛ و نعمتش را بر تو و بر خاندان یعقوب تمام و کامل می‌کند، همان‌گونه که پیش از این، بر پدرانت ابراهیم و اسحاق تمام کرد؛ به یقین، پروردگار تو دانا و حکیم است!»'),
(1603, 1603, ' لَقَدْ كَانَ فِي يُوسُفَ وَإِخْوَتِهِ آيَاتٌ لِلسَّائِلِينَ', 12, 7, 12, 48, 236, 'در (داستان) یوسف و برادرانش، نشانه‌ها (ی هدایت) برای سؤال‌کنندگان بود!'),
(1604, 1604, 'إِذْ قَالُوا لَيُوسُفُ وَأَخُوهُ أَحَبُّ إِلَىٰ أَبِينَا مِنَّا وَنَحْنُ عُصْبَةٌ إِنَّ أَبَانَا لَفِي ضَلَالٍ مُبِينٍ', 12, 8, 12, 48, 236, 'هنگامی که (برادران) گفتند: «یوسف و برادرش = بنیامین‌ نزد پدر، از ما محبوبترند؛ در حالی که ما گروه نیرومندی هستیم! مسلّماً پدر ما، در گمراهی آشکاری است!'),
(1605, 1605, 'اقْتُلُوا يُوسُفَ أَوِ اطْرَحُوهُ أَرْضًا يَخْلُ لَكُمْ وَجْهُ أَبِيكُمْ وَتَكُونُوا مِنْ بَعْدِهِ قَوْمًا صَالِحِينَ', 12, 9, 12, 48, 236, 'یوسف را بکشید؛ یا او را به سرزمین دوردستی بیفکنید؛ تا توجه پدر، فقط به شما باشد؛ و بعد از آن، (از گناه خود توبه می‌کنید؛ و) افراد صالحی خواهید بود!'),
(1606, 1606, 'قَالَ قَائِلٌ مِنْهُمْ لَا تَقْتُلُوا يُوسُفَ وَأَلْقُوهُ فِي غَيَابَتِ الْجُبِّ يَلْتَقِطْهُ بَعْضُ السَّيَّارَةِ إِنْ كُنْتُمْ فَاعِلِينَ', 12, 10, 12, 48, 236, 'یکی از آنها گفت: «یوسف را نکشید! و اگر می‌خواهید کاری انجام دهید، او را در نهانگاه چاه بیفکنید؛ تا بعضی از قافله‌ها او را برگیرند (و با خود به مکان دوری ببرند)!»'),
(1607, 1607, 'قَالُوا يَا أَبَانَا مَا لَكَ لَا تَأْمَنَّا عَلَىٰ يُوسُفَ وَإِنَّا لَهُ لَنَاصِحُونَ', 12, 11, 12, 48, 236, '(و برای انجام این کار، برادران نزد پدر آمدند و) گفتند: «پدرجان! چرا تو درباره (برادرمان) یوسف، به ما اطمینان نمی‌کنی؟! در حالی که ما خیرخواه او هستیم!'),
(1608, 1608, 'أَرْسِلْهُ مَعَنَا غَدًا يَرْتَعْ وَيَلْعَبْ وَإِنَّا لَهُ لَحَافِظُونَ', 12, 12, 12, 48, 236, 'فردا او را با ما (به خارج شهر) بفرست، تا غذای کافی بخورد و تفریح کند؛ و ما نگهبان او هستیم!»'),
(1609, 1609, 'قَالَ إِنِّي لَيَحْزُنُنِي أَنْ تَذْهَبُوا بِهِ وَأَخَافُ أَنْ يَأْكُلَهُ الذِّئْبُ وَأَنْتُمْ عَنْهُ غَافِلُونَ', 12, 13, 12, 48, 236, '(پدر) گفت: «من از بردن او غمگین می‌شوم؛ و از این می‌ترسم که گرگ او را بخورد، و شما از او غافل باشید!»'),
(1610, 1610, 'قَالُوا لَئِنْ أَكَلَهُ الذِّئْبُ وَنَحْنُ عُصْبَةٌ إِنَّا إِذًا لَخَاسِرُونَ', 12, 14, 12, 48, 236, 'گفتند: «با اینکه ما گروه نیرومندی هستیم، اگر گرگ او را بخورد، ما از زیانکاران خواهیم بود (و هرگز چنین چیزی) ممکن نیست!)»'),
(1611, 1611, 'فَلَمَّا ذَهَبُوا بِهِ وَأَجْمَعُوا أَنْ يَجْعَلُوهُ فِي غَيَابَتِ الْجُبِّ ۚ وَأَوْحَيْنَا إِلَيْهِ لَتُنَبِّئَنَّهُمْ بِأَمْرِهِمْ هَٰذَا وَهُمْ لَا يَشْعُرُونَ', 12, 15, 12, 48, 237, 'هنگامی که او را با خود بردند، و تصمیم گرفتند وی را در مخفی‌گاه چاه قرار دهند، (سرانجام مقصد خود را عملی ساختند؛) و به او وحی فرستادیم که آنها را در آینده از این کارشان با خبر خواهی ساخت؛ در حالی که آنها نمی‌دانند!'),
(1612, 1612, 'وَجَاءُوا أَبَاهُمْ عِشَاءً يَبْكُونَ', 12, 16, 12, 48, 237, '(برادران یوسف) شب هنگام، گریان به سراغ پدر آمدند.'),
(1613, 1613, 'قَالُوا يَا أَبَانَا إِنَّا ذَهَبْنَا نَسْتَبِقُ وَتَرَكْنَا يُوسُفَ عِنْدَ مَتَاعِنَا فَأَكَلَهُ الذِّئْبُ ۖ وَمَا أَنْتَ بِمُؤْمِنٍ لَنَا وَلَوْ كُنَّا صَادِقِينَ', 12, 17, 12, 48, 237, 'گفتند: «ای پدر! ما رفتیم و مشغول مسابقه شدیم، و یوسف را نزد اثاث خود گذاردیم؛ و گرگ او را خورد! تو هرگز سخن ما را باور نخواهی کرد، هر چند راستگو باشیم!»'),
(1614, 1614, 'وَجَاءُوا عَلَىٰ قَمِيصِهِ بِدَمٍ كَذِبٍ ۚ قَالَ بَلْ سَوَّلَتْ لَكُمْ أَنْفُسُكُمْ أَمْرًا ۖ فَصَبْرٌ جَمِيلٌ ۖ وَاللَّهُ الْمُسْتَعَانُ عَلَىٰ مَا تَصِفُونَ', 12, 18, 12, 48, 237, 'و پیراهن او را با خونی دروغین (آغشته ساخته، نزد پدر) آوردند؛ گفت: «هوسهای نفسانی شما این کار را برایتان آراسته! من صبر جمیل (و شکیبائی خالی از ناسپاسی) خواهم داشت؛ و در برابر آنچه می‌گویید، از خداوند یاری می‌طلبم!»'),
(1615, 1615, 'وَجَاءَتْ سَيَّارَةٌ فَأَرْسَلُوا وَارِدَهُمْ فَأَدْلَىٰ دَلْوَهُ ۖ قَالَ يَا بُشْرَىٰ هَٰذَا غُلَامٌ ۚ وَأَسَرُّوهُ بِضَاعَةً ۚ وَاللَّهُ عَلِيمٌ بِمَا يَعْمَلُونَ', 12, 19, 12, 48, 237, 'و (در همین حال) کاروانی فرا رسید؛ و مأمور آب را (به سراغ آب) فرستادند؛ او دلو خود را در چاه افکند؛ (ناگهان) صدا زد: «مژده باد! این کودکی است (زیبا و دوست داشتنی!)» و این امر را بعنوان یک سرمایه از دیگران مخفی داشتند. و خداوند به آنچه آنها انجام می‌دادند، آگاه بود.'),
(1616, 1616, 'وَشَرَوْهُ بِثَمَنٍ بَخْسٍ دَرَاهِمَ مَعْدُودَةٍ وَكَانُوا فِيهِ مِنَ الزَّاهِدِينَ', 12, 20, 12, 48, 237, 'و (سرانجام،) او را به بهای کمی -چند درهم- فروختند؛ و نسبت به (فروختن) او، بی رغبت بودند (؛ چرا که می‌ترسیدند رازشان فاش شود).'),
(1617, 1617, 'وَقَالَ الَّذِي اشْتَرَاهُ مِنْ مِصْرَ لِامْرَأَتِهِ أَكْرِمِي مَثْوَاهُ عَسَىٰ أَنْ يَنْفَعَنَا أَوْ نَتَّخِذَهُ وَلَدًا ۚ وَكَذَٰلِكَ مَكَّنَّا لِيُوسُفَ فِي الْأَرْضِ وَلِنُعَلِّمَهُ مِنْ تَأْوِيلِ الْأَحَادِيثِ ۚ وَاللَّهُ غَالِبٌ عَلَىٰ أَمْرِهِ وَلَٰكِنَّ أَكْثَرَ النَّاسِ لَا يَعْلَمُونَ', 12, 21, 12, 48, 237, 'و آن کس که او را از سرزمین مصر خرید = عزیز مصر، به همسرش گفت: «مقام وی را گرامی دار، شاید برای ما سودمند باشد؛ و یا او را بعنوان فرزند انتخاب کنیم!» و اینچنین یوسف را در آن سرزمین متمکّن ساختیم! (ما این کار را کردیم، تا او را بزرگ داریم؛ و) از علم تعبیر خواب به او بیاموزیم؛ خداوند بر کار خود پیروز است، ولی بیشتر مردم نمی‌دانند!'),
(1618, 1618, 'وَلَمَّا بَلَغَ أَشُدَّهُ آتَيْنَاهُ حُكْمًا وَعِلْمًا ۚ وَكَذَٰلِكَ نَجْزِي الْمُحْسِنِينَ', 12, 22, 12, 48, 237, 'و هنگامی که به بلوغ و قوّت رسید، ما «حکم» = نبوّت‌ و «علم» به او دادیم؛ و اینچنین نیکوکاران را پاداش می‌دهیم!'),
(1619, 1619, 'وَرَاوَدَتْهُ الَّتِي هُوَ فِي بَيْتِهَا عَنْ نَفْسِهِ وَغَلَّقَتِ الْأَبْوَابَ وَقَالَتْ هَيْتَ لَكَ ۚ قَالَ مَعَاذَ اللَّهِ ۖ إِنَّهُ رَبِّي أَحْسَنَ مَثْوَايَ ۖ إِنَّهُ لَا يُفْلِحُ الظَّالِمُونَ', 12, 23, 12, 48, 238, 'و آن زن که یوسف در خانه او بود، از او تمنّای کامجویی کرد؛ درها را بست و گفت: «بیا (بسوی آنچه برای تو مهیاست!)» (یوسف) گفت: «پناه می‌برم به خدا! او = عزیز مصر صاحب نعمت من است؛ مقام مرا گرامی داشته؛ (آیا ممکن است به او ظلم و خیانت کنم؟!) مسلّماً ظالمان رستگار نمی‌شوند!»'),
(1620, 1620, 'وَلَقَدْ هَمَّتْ بِهِ ۖ وَهَمَّ بِهَا لَوْلَا أَنْ رَأَىٰ بُرْهَانَ رَبِّهِ ۚ كَذَٰلِكَ لِنَصْرِفَ عَنْهُ السُّوءَ وَالْفَحْشَاءَ ۚ إِنَّهُ مِنْ عِبَادِنَا الْمُخْلَصِينَ', 12, 24, 12, 48, 238, 'آن زن قصد او کرد؛ و او نیز -اگر برهان پروردگار را نمی‌دید- قصد وی می‌نمود! اینچنین کردیم تا بدی و فحشا را از او دور سازیم؛ چرا که او از بندگان مخلص ما بود!'),
(1621, 1621, 'وَاسْتَبَقَا الْبَابَ وَقَدَّتْ قَمِيصَهُ مِنْ دُبُرٍ وَأَلْفَيَا سَيِّدَهَا لَدَى الْبَابِ ۚ قَالَتْ مَا جَزَاءُ مَنْ أَرَادَ بِأَهْلِكَ سُوءًا إِلَّا أَنْ يُسْجَنَ أَوْ عَذَابٌ أَلِيمٌ', 12, 25, 12, 48, 238, 'و هر دو به سوی در، دویدند (در حالی که همسر عزیز، یوسف را تعقیب می‌کرد)؛ و پیراهن او را از پشت (کشید و) پاره کرد. و در این هنگام، آقای آن زن را دم در یافتند! آن زن گفت: «کیفر کسی که بخواهد نسبت به اهل تو خیانت کند، جز زندان و یا عذاب دردناک، چه خواهد بود؟!»'),
(1622, 1622, 'قَالَ هِيَ رَاوَدَتْنِي عَنْ نَفْسِي ۚ وَشَهِدَ شَاهِدٌ مِنْ أَهْلِهَا إِنْ كَانَ قَمِيصُهُ قُدَّ مِنْ قُبُلٍ فَصَدَقَتْ وَهُوَ مِنَ الْكَاذِبِينَ', 12, 26, 12, 48, 238, '(یوسف) گفت: «او مرا با اصرار به سوی خود دعوت کرد!» و در این هنگام، شاهدی از خانواده آن زن شهادت داد که: «اگر پیراهن او از پیش رو پاره شده، آن آن راست می‌گوید، و او از دروغگویان است.'),
(1623, 1623, 'وَإِنْ كَانَ قَمِيصُهُ قُدَّ مِنْ دُبُرٍ فَكَذَبَتْ وَهُوَ مِنَ الصَّادِقِينَ', 12, 27, 12, 48, 238, 'و اگر پیراهنش از پشت پاره شده، آن زن دروغ می‌گوید، و او از راستگویان است.»'),
(1624, 1624, 'فَلَمَّا رَأَىٰ قَمِيصَهُ قُدَّ مِنْ دُبُرٍ قَالَ إِنَّهُ مِنْ كَيْدِكُنَّ ۖ إِنَّ كَيْدَكُنَّ عَظِيمٌ', 12, 28, 12, 48, 238, 'هنگامی که (عزیز مصر) دید پیراهن او = یوسف‌ از پشت پاره شده، گفت: «این از مکر و حیله شما زنان است؛ که مکر و حیله شما زنان، عظیم است!'),
(1625, 1625, 'يُوسُفُ أَعْرِضْ عَنْ هَٰذَا ۚ وَاسْتَغْفِرِي لِذَنْبِكِ ۖ إِنَّكِ كُنْتِ مِنَ الْخَاطِئِينَ', 12, 29, 12, 48, 238, 'یوسف از این موضوع، صرف‌نظر کن! و تو ای زن نیز از گناهت استغفار کن، که از خطاکاران بودی!»'),
(1626, 1626, ' وَقَالَ نِسْوَةٌ فِي الْمَدِينَةِ امْرَأَتُ الْعَزِيزِ تُرَاوِدُ فَتَاهَا عَنْ نَفْسِهِ ۖ قَدْ شَغَفَهَا حُبًّا ۖ إِنَّا لَنَرَاهَا فِي ضَلَالٍ مُبِينٍ', 12, 30, 12, 48, 238, '(این جریان در شهر منعکس شد؛) گروهی از زنان شهر گفتند: «همسر عزیز، جوانش = غلامش‌ را بسوی خود دعوت می‌کند! عشق این جوان، در اعماق قلبش نفوذ کرده، ما او را در گمراهی آشکاری می‌بینیم!»'),
(1627, 1627, 'فَلَمَّا سَمِعَتْ بِمَكْرِهِنَّ أَرْسَلَتْ إِلَيْهِنَّ وَأَعْتَدَتْ لَهُنَّ مُتَّكَأً وَآتَتْ كُلَّ وَاحِدَةٍ مِنْهُنَّ سِكِّينًا وَقَالَتِ اخْرُجْ عَلَيْهِنَّ ۖ فَلَمَّا رَأَيْنَهُ أَكْبَرْنَهُ وَقَطَّعْنَ أَيْدِيَهُنَّ وَقُلْنَ حَاشَ لِلَّهِ مَا هَٰذَا بَشَرًا إِنْ هَٰذَا إِلَّا مَلَكٌ كَرِيمٌ', 12, 31, 12, 48, 239, 'هنگامی که (همسر عزیز) از فکر آنها باخبر شد، به سراغشان فرستاد (و از آنها دعوت کرد)؛ و برای آنها پشتی (گرانبها، و مجلس باشکوهی) فراهم ساخت؛ و به دست هر کدام، چاقویی (برای بریدن میوه) داد؛ و در این موقع (به یوسف) گفت: «وارد مجلس آنان شو!» هنگامی که چشمشان به او افتاد، او را بسیار بزرگ (و زیبا) شمردند؛ و (بی‌توجه) دستهای خود را بریدند؛ و گفتند: «منزّه است خدا! این بشر نیست؛ این یک فرشته بزرگوار است!»'),
(1628, 1628, 'قَالَتْ فَذَٰلِكُنَّ الَّذِي لُمْتُنَّنِي فِيهِ ۖ وَلَقَدْ رَاوَدْتُهُ عَنْ نَفْسِهِ فَاسْتَعْصَمَ ۖ وَلَئِنْ لَمْ يَفْعَلْ مَا آمُرُهُ لَيُسْجَنَنَّ وَلَيَكُونًا مِنَ الصَّاغِرِينَ', 12, 32, 12, 48, 239, '(همسر عزیز) گفت: «این همان کسی است که بخاطر (عشق) او مرا سرزنش کردید! (آری،) من او را به خویشتن دعوت کردم؛ و او خودداری کرد! و اگر آنچه را دستور می‌دهم انجام ندهد، به زندان خواهد افتاد؛ و مسلّماً خوار و ذلیل خواهد شد!»'),
(1629, 1629, 'قَالَ رَبِّ السِّجْنُ أَحَبُّ إِلَيَّ مِمَّا يَدْعُونَنِي إِلَيْهِ ۖ وَإِلَّا تَصْرِفْ عَنِّي كَيْدَهُنَّ أَصْبُ إِلَيْهِنَّ وَأَكُنْ مِنَ الْجَاهِلِينَ', 12, 33, 12, 48, 239, '(یوسف) گفت: «پروردگارا! زندان نزد من محبوبتر است از آنچه اینها مرا بسوی آن می‌خوانند! و اگر مکر و نیرنگ آنها را از من باز نگردانی، بسوی آنان متمایل خواهم شد و از جاهلان خواهم بود!»'),
(1630, 1630, 'فَاسْتَجَابَ لَهُ رَبُّهُ فَصَرَفَ عَنْهُ كَيْدَهُنَّ ۚ إِنَّهُ هُوَ السَّمِيعُ الْعَلِيمُ', 12, 34, 12, 48, 239, 'پروردگارش دعای او را اجابت کرد؛ و مکر آنان را از او بگردانید؛ چرا که او شنوا و داناست!'),
(1631, 1631, 'ثُمَّ بَدَا لَهُمْ مِنْ بَعْدِ مَا رَأَوُا الْآيَاتِ لَيَسْجُنُنَّهُ حَتَّىٰ حِينٍ', 12, 35, 12, 48, 239, 'و بعد از آنکه نشانه‌های (پاکی یوسف) را دیدند، تصمیم گرفتند او را تا مدّتی زندانی کنند!'),
(1632, 1632, 'وَدَخَلَ مَعَهُ السِّجْنَ فَتَيَانِ ۖ قَالَ أَحَدُهُمَا إِنِّي أَرَانِي أَعْصِرُ خَمْرًا ۖ وَقَالَ الْآخَرُ إِنِّي أَرَانِي أَحْمِلُ فَوْقَ رَأْسِي خُبْزًا تَأْكُلُ الطَّيْرُ مِنْهُ ۖ نَبِّئْنَا بِتَأْوِيلِهِ ۖ إِنَّا نَرَاكَ مِنَ الْمُحْسِنِينَ', 12, 36, 12, 48, 239, 'و دو جوان، همراه او وارد زندان شدند؛ یکی از آن دو گفت: «من در خواب دیدم که (انگور برای) شراب می‌فشارم!» و دیگری گفت: «من در خواب دیدم که نان بر سرم حمل می‌کنم؛ و پرندگان از آن می‌خورند؛ ما را از تعبیر این خواب آگاه کن که تو را از نیکوکاران می‌بینیم.»'),
(1633, 1633, 'قَالَ لَا يَأْتِيكُمَا طَعَامٌ تُرْزَقَانِهِ إِلَّا نَبَّأْتُكُمَا بِتَأْوِيلِهِ قَبْلَ أَنْ يَأْتِيَكُمَا ۚ ذَٰلِكُمَا مِمَّا عَلَّمَنِي رَبِّي ۚ إِنِّي تَرَكْتُ مِلَّةَ قَوْمٍ لَا يُؤْمِنُونَ بِاللَّهِ وَهُمْ بِالْآخِرَةِ هُمْ كَافِرُونَ', 12, 37, 12, 48, 239, '(یوسف) گفت: «پیش از آنکه جیره غذایی شما فرا رسد، شما را از تعبیر خوابتان آگاه خواهم ساخت. این، از دانشی است که پروردگارم به من آموخته است. من آیین قومی را که به خدا ایمان ندارند، و به سرای دیگر کافرند، ترک گفتم (و شایسته چنین موهبتی شدم)!'),
(1634, 1634, 'وَاتَّبَعْتُ مِلَّةَ آبَائِي إِبْرَاهِيمَ وَإِسْحَاقَ وَيَعْقُوبَ ۚ مَا كَانَ لَنَا أَنْ نُشْرِكَ بِاللَّهِ مِنْ شَيْءٍ ۚ ذَٰلِكَ مِنْ فَضْلِ اللَّهِ عَلَيْنَا وَعَلَى النَّاسِ وَلَٰكِنَّ أَكْثَرَ النَّاسِ لَا يَشْكُرُونَ', 12, 38, 12, 48, 240, 'من از آیین پدرانم ابراهیم و اسحاق و یعقوب پیروی کردم! برای ما شایسته نبود چیزی را همتای خدا قرار دهیم؛ این از فضل خدا بر ما و بر مردم است؛ ولی بیشتر مردم شکرگزاری نمی‌کنند!'),
(1635, 1635, 'يَا صَاحِبَيِ السِّجْنِ أَأَرْبَابٌ مُتَفَرِّقُونَ خَيْرٌ أَمِ اللَّهُ الْوَاحِدُ الْقَهَّارُ', 12, 39, 12, 48, 240, 'ای دوستان زندانی من! آیا خدایان پراکنده بهترند، یا خداوند یکتای پیروز؟!'),
(1636, 1636, 'مَا تَعْبُدُونَ مِنْ دُونِهِ إِلَّا أَسْمَاءً سَمَّيْتُمُوهَا أَنْتُمْ وَآبَاؤُكُمْ مَا أَنْزَلَ اللَّهُ بِهَا مِنْ سُلْطَانٍ ۚ إِنِ الْحُكْمُ إِلَّا لِلَّهِ ۚ أَمَرَ أَلَّا تَعْبُدُوا إِلَّا إِيَّاهُ ۚ ذَٰلِكَ الدِّينُ الْقَيِّمُ وَلَٰكِنَّ أَكْثَرَ النَّاسِ لَا يَعْلَمُونَ', 12, 40, 12, 48, 240, 'این معبودهایی که غیر از خدا می‌پرستید، چیزی جز اسمهائی (بی‌مسمّا) که شما و پدرانتان آنها را خدا نامیده‌اید، نیست؛ خداوند هیچ دلیلی بر آن نازل نکرده؛ حکم تنها از آن خداست؛ فرمان داده که غیر از او را نپرستید! این است آیین پابرجا؛ ولی بیشتر مردم نمی‌دانند!'),
(1637, 1637, 'يَا صَاحِبَيِ السِّجْنِ أَمَّا أَحَدُكُمَا فَيَسْقِي رَبَّهُ خَمْرًا ۖ وَأَمَّا الْآخَرُ فَيُصْلَبُ فَتَأْكُلُ الطَّيْرُ مِنْ رَأْسِهِ ۚ قُضِيَ الْأَمْرُ الَّذِي فِيهِ تَسْتَفْتِيَانِ', 12, 41, 12, 48, 240, 'ای دوستان زندانی من! امّا یکی از شما (دو نفر، آزاد می‌شود؛ و) ساقی شراب برای صاحب خود خواهد شد؛ و امّا دیگری به دار آویخته می‌شود؛ و پرندگان از سر او می‌خورند! و مطلبی که درباره آن (از من) نظر خواستید، قطعی و حتمی است!»'),
(1638, 1638, 'وَقَالَ لِلَّذِي ظَنَّ أَنَّهُ نَاجٍ مِنْهُمَا اذْكُرْنِي عِنْدَ رَبِّكَ فَأَنْسَاهُ الشَّيْطَانُ ذِكْرَ رَبِّهِ فَلَبِثَ فِي السِّجْنِ بِضْعَ سِنِينَ', 12, 42, 12, 48, 240, 'و به آن یکی از آن دو نفر، که می‌دانست رهایی می‌یابد، گفت: «مرا نزد صاحبت = سلطان مصر یادآوری کن!» ولی شیطان یادآوری او را نزد صاحبش از خاطر وی برد؛ و بدنبال آن، (یوسف) چند سال در زندان باقی ماند.'),
(1639, 1639, 'وَقَالَ الْمَلِكُ إِنِّي أَرَىٰ سَبْعَ بَقَرَاتٍ سِمَانٍ يَأْكُلُهُنَّ سَبْعٌ عِجَافٌ وَسَبْعَ سُنْبُلَاتٍ خُضْرٍ وَأُخَرَ يَابِسَاتٍ ۖ يَا أَيُّهَا الْمَلَأُ أَفْتُونِي فِي رُؤْيَايَ إِنْ كُنْتُمْ لِلرُّؤْيَا تَعْبُرُونَ', 12, 43, 12, 48, 240, 'پادشاه گفت: «من در خواب دیدم هفت گاو چاق را که هفت گاو لاغر آنها را می‌خورند؛ و هفت خوشه سبز و هفت خوشه خشکیده؛ (که خشکیده‌ها بر سبزها پیچیدند؛ و آنها را از بین بردند.) ای جمعیّت اشراف! درباره خواب من نظر دهید، اگر خواب را تعبیر می‌کنید!»'),
(1640, 1640, 'قَالُوا أَضْغَاثُ أَحْلَامٍ ۖ وَمَا نَحْنُ بِتَأْوِيلِ الْأَحْلَامِ بِعَالِمِينَ', 12, 44, 12, 48, 241, 'گفتند: «خوابهای پریشان و پراکنده‌ای است؛ و ما از تعبیر این گونه خوابها آگاه نیستیم!»'),
(1641, 1641, 'وَقَالَ الَّذِي نَجَا مِنْهُمَا وَادَّكَرَ بَعْدَ أُمَّةٍ أَنَا أُنَبِّئُكُمْ بِتَأْوِيلِهِ فَأَرْسِلُونِ', 12, 45, 12, 48, 241, 'و یکی از آن دو که نجات یافته بود -و بعد از مدّتی به خاطرش آمد- گفت: «من تأویل آن را به شما خبر می‌دهم؛ مرا (به سراغ آن جوان زندانی) بفرستید!»'),
(1642, 1642, 'يُوسُفُ أَيُّهَا الصِّدِّيقُ أَفْتِنَا فِي سَبْعِ بَقَرَاتٍ سِمَانٍ يَأْكُلُهُنَّ سَبْعٌ عِجَافٌ وَسَبْعِ سُنْبُلَاتٍ خُضْرٍ وَأُخَرَ يَابِسَاتٍ لَعَلِّي أَرْجِعُ إِلَى النَّاسِ لَعَلَّهُمْ يَعْلَمُونَ', 12, 46, 12, 48, 241, '(او به زندان آمد، و چنین گفت:) یوسف، ای مرد بسیار راستگو! درباره این خواب اظهار نظر کن که هفت گاو چاق را هفت گاو لاغر می‌خورند؛ و هفت خوشه تر، و هفت خوشه خشکیده؛ تا من بسوی مردم بازگردم، شاید (از تعبیر این خواب) آگاه شوند!'),
(1643, 1643, 'قَالَ تَزْرَعُونَ سَبْعَ سِنِينَ دَأَبًا فَمَا حَصَدْتُمْ فَذَرُوهُ فِي سُنْبُلِهِ إِلَّا قَلِيلًا مِمَّا تَأْكُلُونَ', 12, 47, 12, 48, 241, 'گفت: «هفت سال با جدیّت زراعت می‌کنید؛ و آنچه را درو کردید، جز کمی که می‌خورید، در خوشه‌های خود باقی بگذارید (و ذخیره نمایید).'),
(1644, 1644, 'ثُمَّ يَأْتِي مِنْ بَعْدِ ذَٰلِكَ سَبْعٌ شِدَادٌ يَأْكُلْنَ مَا قَدَّمْتُمْ لَهُنَّ إِلَّا قَلِيلًا مِمَّا تُحْصِنُونَ', 12, 48, 12, 48, 241, 'پس از آن، هفت سال سخت (و خشکی و قحطی) می‌آید، که آنچه را برای آن سالها ذخیره کرده‌اید، می‌خورند؛ جز کمی که (برای بذر) ذخیره خواهید کرد.'),
(1645, 1645, 'ثُمَّ يَأْتِي مِنْ بَعْدِ ذَٰلِكَ عَامٌ فِيهِ يُغَاثُ النَّاسُ وَفِيهِ يَعْصِرُونَ', 12, 49, 12, 48, 241, 'سپس سالی فرامی‌رسد که باران فراوان نصیب مردم می‌شود؛ و در آن سال، مردم عصاره (میوه‌ها و دانه‌های روغنی را) می‌گیرند (و سال پر برکتی است.)»'),
(1646, 1646, 'وَقَالَ الْمَلِكُ ائْتُونِي بِهِ ۖ فَلَمَّا جَاءَهُ الرَّسُولُ قَالَ ارْجِعْ إِلَىٰ رَبِّكَ فَاسْأَلْهُ مَا بَالُ النِّسْوَةِ اللَّاتِي قَطَّعْنَ أَيْدِيَهُنَّ ۚ إِنَّ رَبِّي بِكَيْدِهِنَّ عَلِيمٌ', 12, 50, 12, 48, 241, 'پادشاه گفت: «او را نزد من آورید!» ولی هنگامی که فرستاده او نزد وی = یوسف‌ آمد گفت: «به سوی صاحبت بازگرد، و از او بپرس ماجرای زنانی که دستهای خود را بریدند چه بود؟ که خدای من به نیرنگ آنها آگاه است.»'),
(1647, 1647, 'قَالَ مَا خَطْبُكُنَّ إِذْ رَاوَدْتُنَّ يُوسُفَ عَنْ نَفْسِهِ ۚ قُلْنَ حَاشَ لِلَّهِ مَا عَلِمْنَا عَلَيْهِ مِنْ سُوءٍ ۚ قَالَتِ امْرَأَتُ الْعَزِيزِ الْآنَ حَصْحَصَ الْحَقُّ أَنَا رَاوَدْتُهُ عَنْ نَفْسِهِ وَإِنَّهُ لَمِنَ الصَّادِقِينَ', 12, 51, 12, 48, 241, '(پادشاه آن زنان را طلبید و) گفت: «به هنگامی که یوسف را به سوی خویش دعوت کردید، جریان کار شما چه بود؟» گفتند: «منزّه است خدا، ما هیچ عیبی در او نیافتیم!» (در این هنگام) همسر عزیز گفت: «الآن حق آشکار گشت! من بودم که او را به سوی خود دعوت کردم؛ و او از راستگویان است!'),
(1648, 1648, 'ذَٰلِكَ لِيَعْلَمَ أَنِّي لَمْ أَخُنْهُ بِالْغَيْبِ وَأَنَّ اللَّهَ لَا يَهْدِي كَيْدَ الْخَائِنِينَ', 12, 52, 12, 48, 241, 'این سخن را بخاطر آن گفتم تا بداند من در غیاب به او خیانت نکردم؛ و خداوند مکر خائنان را هدایت نمی‌کند!'),
(1649, 1649, ' وَمَا أُبَرِّئُ نَفْسِي ۚ إِنَّ النَّفْسَ لَأَمَّارَةٌ بِالسُّوءِ إِلَّا مَا رَحِمَ رَبِّي ۚ إِنَّ رَبِّي غَفُورٌ رَحِيمٌ', 12, 53, 13, 49, 242, 'من هرگز خودم را تبرئه نمی‌کنم، که نفس (سرکش) بسیار به بدیها امر می‌کند؛ مگر آنچه را پروردگارم رحم کند! پروردگارم آمرزنده و مهربان است.»'),
(1650, 1650, 'وَقَالَ الْمَلِكُ ائْتُونِي بِهِ أَسْتَخْلِصْهُ لِنَفْسِي ۖ فَلَمَّا كَلَّمَهُ قَالَ إِنَّكَ الْيَوْمَ لَدَيْنَا مَكِينٌ أَمِينٌ', 12, 54, 13, 49, 242, 'پادشاه گفت: «او = یوسف‌ را نزد من آورید، تا وی را مخصوص خود گردانم!» هنگامی که (یوسف نزد وی آمد و) با او صحبت کرد، (پادشاه به عقل و درایت او پی برد؛ و) گفت: «تو امروز نزد ما جایگاهی والا داری، و مورد اعتماد هستی!»'),
(1651, 1651, 'قَالَ اجْعَلْنِي عَلَىٰ خَزَائِنِ الْأَرْضِ ۖ إِنِّي حَفِيظٌ عَلِيمٌ', 12, 55, 13, 49, 242, '(یوسف) گفت: «مرا سرپرست خزائن سرزمین (مصر) قرار ده، که نگهدارنده و آگاهم!»'),
(1652, 1652, 'وَكَذَٰلِكَ مَكَّنَّا لِيُوسُفَ فِي الْأَرْضِ يَتَبَوَّأُ مِنْهَا حَيْثُ يَشَاءُ ۚ نُصِيبُ بِرَحْمَتِنَا مَنْ نَشَاءُ ۖ وَلَا نُضِيعُ أَجْرَ الْمُحْسِنِينَ', 12, 56, 13, 49, 242, 'و این‌گونه ما به یوسف در سرزمین (مصر) قدرت دادیم، که هر جا می‌خواست در آن منزل می‌گزید (و تصرّف می‌کرد)! ما رحمت خود را به هر کس بخواهیم (و شایسته بدانیم) میبخشیم؛ و پاداش نیکوکاران را ضایع نمی‌کنیم!'),
(1653, 1653, 'وَلَأَجْرُ الْآخِرَةِ خَيْرٌ لِلَّذِينَ آمَنُوا وَكَانُوا يَتَّقُونَ', 12, 57, 13, 49, 242, '(امّا) پاداش آخرت، برای کسانی که ایمان آورده و پرهیزگاری داشتند، بهتر است!'),
(1654, 1654, 'وَجَاءَ إِخْوَةُ يُوسُفَ فَدَخَلُوا عَلَيْهِ فَعَرَفَهُمْ وَهُمْ لَهُ مُنْكِرُونَ', 12, 58, 13, 49, 242, '(سرزمین کنعان را قحطی فرا گرفت؛) برادران یوسف (در پی موادّ غذایی به مصر) آمدند؛ و بر او وارد شدند. او آنان را شناخت؛ ولی آنها او را نشناختند.'),
(1655, 1655, 'وَلَمَّا جَهَّزَهُمْ بِجَهَازِهِمْ قَالَ ائْتُونِي بِأَخٍ لَكُمْ مِنْ أَبِيكُمْ ۚ أَلَا تَرَوْنَ أَنِّي أُوفِي الْكَيْلَ وَأَنَا خَيْرُ الْمُنْزِلِينَ', 12, 59, 13, 49, 242, 'و هنگامی که (یوسف) بارهای آنان را آماده ساخت، گفت: «(نوبت آینده) آن برادری را که از پدر دارید، نزد من آورید! آیا نمی‌بینید من حق پیمانه را ادا می‌کنم، و من بهترین میزبانان هستم؟!'),
(1656, 1656, 'فَإِنْ لَمْ تَأْتُونِي بِهِ فَلَا كَيْلَ لَكُمْ عِنْدِي وَلَا تَقْرَبُونِ', 12, 60, 13, 49, 242, 'و اگر او را نزد من نیاورید، نه کیل (و پیمانه‌ای از غلّه) نزد من خواهید داشت؛ و نه (اصلاً) به من نزدیک شوید!»'),
(1657, 1657, 'قَالُوا سَنُرَاوِدُ عَنْهُ أَبَاهُ وَإِنَّا لَفَاعِلُونَ', 12, 61, 13, 49, 242, 'گفتند: «ما با پدرش گفتگو خواهیم کرد؛ (و سعی می‌کنیم موافقتش را جلب نمائیم؛) و ما این کار را خواهیم کرد!»'),
(1658, 1658, 'وَقَالَ لِفِتْيَانِهِ اجْعَلُوا بِضَاعَتَهُمْ فِي رِحَالِهِمْ لَعَلَّهُمْ يَعْرِفُونَهَا إِذَا انْقَلَبُوا إِلَىٰ أَهْلِهِمْ لَعَلَّهُمْ يَرْجِعُونَ', 12, 62, 13, 49, 242, '(سپس) به کارگزاران خود گفت: «آنچه را بعنوان قیمت پرداخته‌اند، در بارهایشان بگذارید! شاید پس از بازگشت به خانواده خویش، آن را بشناسند؛ و شاید برگردند!»'),
(1659, 1659, 'فَلَمَّا رَجَعُوا إِلَىٰ أَبِيهِمْ قَالُوا يَا أَبَانَا مُنِعَ مِنَّا الْكَيْلُ فَأَرْسِلْ مَعَنَا أَخَانَا نَكْتَلْ وَإِنَّا لَهُ لَحَافِظُونَ', 12, 63, 13, 49, 242, 'هنگامی که به سوی پدرشان بازگشتند، گفتند: «ای پدر! دستور داده شده که (بدون حضور برادرمان بنیامین) پیمانه‌ای (از غلّه) به ما ندهند؛ پس برادرمان را با ما بفرست، تا سهمی (از غلّه) دریافت داریم؛ و ما او را محافظت خواهیم کرد!»'),
(1660, 1660, 'قَالَ هَلْ آمَنُكُمْ عَلَيْهِ إِلَّا كَمَا أَمِنْتُكُمْ عَلَىٰ أَخِيهِ مِنْ قَبْلُ ۖ فَاللَّهُ خَيْرٌ حَافِظًا ۖ وَهُوَ أَرْحَمُ الرَّاحِمِينَ', 12, 64, 13, 49, 243, 'گفت: «آیا نسبت به او به شما اطمینان کنم همان‌گونه که نسبت به برادرش (یوسف) اطمینان کردم (و دیدید چه شد)؟! و (در هر حال،) خداوند بهترین حافظ، و مهربانترین مهربانان است»'),
(1661, 1661, 'وَلَمَّا فَتَحُوا مَتَاعَهُمْ وَجَدُوا بِضَاعَتَهُمْ رُدَّتْ إِلَيْهِمْ ۖ قَالُوا يَا أَبَانَا مَا نَبْغِي ۖ هَٰذِهِ بِضَاعَتُنَا رُدَّتْ إِلَيْنَا ۖ وَنَمِيرُ أَهْلَنَا وَنَحْفَظُ أَخَانَا وَنَزْدَادُ كَيْلَ بَعِيرٍ ۖ ذَٰلِكَ كَيْلٌ يَسِيرٌ', 12, 65, 13, 49, 243, 'و هنگامی که متاع خود را گشودند، دیدند سرمایه آنها به آنها بازگردانده شده! گفتند: «پدر! ما دیگر چه میخواهیم؟! این سرمایه ماست که به ما باز پس گردانده شده است! (پس چه بهتر که برادر را با ما بفرستی؛) و ما برای خانواده خویش موادّ غذایی می‌آوریم؛ و برادرمان را حفظ خواهیم کرد؛ و یک بار شتر زیادتر دریافت خواهیم داشت؛ این پیمانه (بار) کوچکی است!»'),
(1662, 1662, 'قَالَ لَنْ أُرْسِلَهُ مَعَكُمْ حَتَّىٰ تُؤْتُونِ مَوْثِقًا مِنَ اللَّهِ لَتَأْتُنَّنِي بِهِ إِلَّا أَنْ يُحَاطَ بِكُمْ ۖ فَلَمَّا آتَوْهُ مَوْثِقَهُمْ قَالَ اللَّهُ عَلَىٰ مَا نَقُولُ وَكِيلٌ', 12, 66, 13, 49, 243, 'گفت: «من هرگز او را با شما نخواهم فرستاد، تا پیمان مؤکّد الهی بدهید که او را حتماً نزد من خواهید آورد! مگر اینکه (بر اثر مرگ یا علّت دیگر،) قدرت از شما سلب گردد. و هنگامی که آنها پیمان استوار خود را در اختیار او گذاردند، گفت: «خداوند، نسبت به آنچه می‌گوییم، ناظر و نگهبان است!»'),
(1663, 1663, 'وَقَالَ يَا بَنِيَّ لَا تَدْخُلُوا مِنْ بَابٍ وَاحِدٍ وَادْخُلُوا مِنْ أَبْوَابٍ مُتَفَرِّقَةٍ ۖ وَمَا أُغْنِي عَنْكُمْ مِنَ اللَّهِ مِنْ شَيْءٍ ۖ إِنِ الْحُكْمُ إِلَّا لِلَّهِ ۖ عَلَيْهِ تَوَكَّلْتُ ۖ وَعَلَيْهِ فَلْيَتَوَكَّلِ الْمُتَوَكِّلُونَ', 12, 67, 13, 49, 243, 'و (هنگامی که می‌خواستند حرکت کنند، یعقوب) گفت: «فرزندان من! از یک در وارد نشوید؛ بلکه از درهای متفرّق وارد گردید (تا توجه مردم به سوی شما جلب نشود)! و (من با این دستور،) نمی‌توانم حادثه‌ای را که از سوی خدا حتمی است، از شما دفع کنم! حکم و فرمان، تنها از آنِ خداست! بر او توکّل کرده‌ام؛ و همه متوکّلان باید بر او توکّل کنند!»'),
(1664, 1664, 'وَلَمَّا دَخَلُوا مِنْ حَيْثُ أَمَرَهُمْ أَبُوهُمْ مَا كَانَ يُغْنِي عَنْهُمْ مِنَ اللَّهِ مِنْ شَيْءٍ إِلَّا حَاجَةً فِي نَفْسِ يَعْقُوبَ قَضَاهَا ۚ وَإِنَّهُ لَذُو عِلْمٍ لِمَا عَلَّمْنَاهُ وَلَٰكِنَّ أَكْثَرَ النَّاسِ لَا يَعْلَمُونَ', 12, 68, 13, 49, 243, 'و هنگامی که از همان طریق که پدر به آنها دستور داده بود وارد شدند، این کار هیچ حادثه حتمی الهی را نمی‌توانست از آنها دور سازد، جز حاجتی در دل یعقوب (که از این طریق) انجام شد (و خاطرش آرام گرفت)؛ و او به خاطر تعلیمی که ما به او دادیم، علم فراوانی داشت؛ ولی بیشتر مردم نمی‌دانند!'),
(1665, 1665, 'وَلَمَّا دَخَلُوا عَلَىٰ يُوسُفَ آوَىٰ إِلَيْهِ أَخَاهُ ۖ قَالَ إِنِّي أَنَا أَخُوكَ فَلَا تَبْتَئِسْ بِمَا كَانُوا يَعْمَلُونَ', 12, 69, 13, 49, 243, 'هنگامی که (برادران) بر یوسف وارد شدند، برادرش را نزد خود جای داد و گفت: «من برادر تو هستم، از آنچه آنها انجام می‌دادند، غمگین و ناراحت نباش!»'),
(1666, 1666, 'فَلَمَّا جَهَّزَهُمْ بِجَهَازِهِمْ جَعَلَ السِّقَايَةَ فِي رَحْلِ أَخِيهِ ثُمَّ أَذَّنَ مُؤَذِّنٌ أَيَّتُهَا الْعِيرُ إِنَّكُمْ لَسَارِقُونَ', 12, 70, 13, 49, 244, 'و هنگامی که (مأمور یوسف) بارهای آنها را بست، ظرف آبخوری پادشاه را در بارِ برادرش گذاشت؛ سپس کسی صدا زد؛ «ای اهل قافله، شما دزد هستید!»'),
(1667, 1667, 'قَالُوا وَأَقْبَلُوا عَلَيْهِمْ مَاذَا تَفْقِدُونَ', 12, 71, 13, 49, 244, 'آنها رو به سوی او کردند و گفتند: «چه چیز گم کرده‌اید؟»'),
(1668, 1668, 'قَالُوا نَفْقِدُ صُوَاعَ الْمَلِكِ وَلِمَنْ جَاءَ بِهِ حِمْلُ بَعِيرٍ وَأَنَا بِهِ زَعِيمٌ', 12, 72, 13, 49, 244, 'گفتند: «پیمانه پادشاه را! و هر کس آن را بیاورد، یک بار شتر (غلّه) به او داده می‌شود؛ و من ضامن این (پاداش) هستم!»'),
(1669, 1669, 'قَالُوا تَاللَّهِ لَقَدْ عَلِمْتُمْ مَا جِئْنَا لِنُفْسِدَ فِي الْأَرْضِ وَمَا كُنَّا سَارِقِينَ', 12, 73, 13, 49, 244, 'گفتند: «به خدا سوگند شما می‌دانید ما نیامده‌ایم که در این سرزمین فساد کنیم؛ و ما (هرگز) دزد نبوده‌ایم!»'),
(1670, 1670, 'قَالُوا فَمَا جَزَاؤُهُ إِنْ كُنْتُمْ كَاذِبِينَ', 12, 74, 13, 49, 244, 'آنها گفتند: «اگر دروغگو باشید، کیفرش چیست؟»'),
(1671, 1671, 'قَالُوا جَزَاؤُهُ مَنْ وُجِدَ فِي رَحْلِهِ فَهُوَ جَزَاؤُهُ ۚ كَذَٰلِكَ نَجْزِي الظَّالِمِينَ', 12, 75, 13, 49, 244, 'گفتند: «هر کس (آن پیمانه) در بارِ او پیدا شود، خودش کیفر آن خواهد بود؛ (و بخاطر این کار، برده شما خواهد شد؛) ما این‌گونه ستمگران را کیفر می‌دهیم!»'),
(1672, 1672, 'فَبَدَأَ بِأَوْعِيَتِهِمْ قَبْلَ وِعَاءِ أَخِيهِ ثُمَّ اسْتَخْرَجَهَا مِنْ وِعَاءِ أَخِيهِ ۚ كَذَٰلِكَ كِدْنَا لِيُوسُفَ ۖ مَا كَانَ لِيَأْخُذَ أَخَاهُ فِي دِينِ الْمَلِكِ إِلَّا أَنْ يَشَاءَ اللَّهُ ۚ نَرْفَعُ دَرَجَاتٍ مَنْ نَشَاءُ ۗ وَفَوْقَ كُلِّ ذِي عِلْمٍ عَلِيمٌ', 12, 76, 13, 49, 244, 'در این هنگام، (یوسف) قبل از بار برادرش، به کاوش بارهای آنها پرداخت؛ سپس آن را از بارِ برادرش بیرون آورد؛ این گونه راه چاره را به یوسف یاد دادیم! او هرگز نمی‌توانست برادرش را مطابق آیین پادشاه (مصر) بگیرد، مگر آنکه خدا بخواهد! درجات هر کس را بخواهیم بالا می‌بریم؛ و برتر از هر صاحب علمی، عالمی است!'),
(1673, 1673, ' قَالُوا إِنْ يَسْرِقْ فَقَدْ سَرَقَ أَخٌ لَهُ مِنْ قَبْلُ ۚ فَأَسَرَّهَا يُوسُفُ فِي نَفْسِهِ وَلَمْ يُبْدِهَا لَهُمْ ۚ قَالَ أَنْتُمْ شَرٌّ مَكَانًا ۖ وَاللَّهُ أَعْلَمُ بِمَا تَصِفُونَ', 12, 77, 13, 49, 244, '(برادران) گفتند: «اگر او بنیامین‌ دزدی کند، (جای تعجب نیست؛) برادرش (یوسف) نیز قبل از او دزدی کرد» یوسف (سخت ناراحت شد، و) این (ناراحتی) را در درون خود پنهان داشت، و برای آنها آشکار نکرد؛ (همین اندازه) گفت: «شما (از دیدگاه من،) از نظر منزلت بدّترین مردمید! و خدا از آنچه توصیف می‌کنید، آگاهتر است!»'),
(1674, 1674, 'قَالُوا يَا أَيُّهَا الْعَزِيزُ إِنَّ لَهُ أَبًا شَيْخًا كَبِيرًا فَخُذْ أَحَدَنَا مَكَانَهُ ۖ إِنَّا نَرَاكَ مِنَ الْمُحْسِنِينَ', 12, 78, 13, 49, 244, 'گفتند: «ای عزیز! او پدر پیری دارد (که سخت ناراحت می‌شود)؛ یکی از ما را به جای او بگیر؛ ما تو را از نیکوکاران می‌بینیم!»'),
(1675, 1675, 'قَالَ مَعَاذَ اللَّهِ أَنْ نَأْخُذَ إِلَّا مَنْ وَجَدْنَا مَتَاعَنَا عِنْدَهُ إِنَّا إِذًا لَظَالِمُونَ', 12, 79, 13, 49, 245, 'گفت: «پناه بر خدا که ما غیر از آن کس که متاع خود را نزد او یافته‌ایم بگیریم؛ در آن صورت، از ظالمان خواهیم بود!»'),
(1676, 1676, 'فَلَمَّا اسْتَيْأَسُوا مِنْهُ خَلَصُوا نَجِيًّا ۖ قَالَ كَبِيرُهُمْ أَلَمْ تَعْلَمُوا أَنَّ أَبَاكُمْ قَدْ أَخَذَ عَلَيْكُمْ مَوْثِقًا مِنَ اللَّهِ وَمِنْ قَبْلُ مَا فَرَّطْتُمْ فِي يُوسُفَ ۖ فَلَنْ أَبْرَحَ الْأَرْضَ حَتَّىٰ يَأْذَنَ لِي أَبِي أَوْ يَحْكُمَ اللَّهُ لِي ۖ وَهُوَ خَيْرُ الْحَاكِمِينَ', 12, 80, 13, 49, 245, 'هنگامی که (برادران) از او مأیوس شدند، به کناری رفتند و با هم به نجوا پرداختند؛ (برادر) بزرگشان گفت: «آیا نمی‌دانید پدرتان از شما پیمان الهی گرفته؛ و پیش از این درباره یوسف کوتاهی کردید؟! من از این سرزمین حرکت نمی‌کنم، تا پدرم به من اجازه دهد؛ یا خدا درباره من داوری کند، که او بهترین حکم‌کنندگان است!'),
(1677, 1677, 'ارْجِعُوا إِلَىٰ أَبِيكُمْ فَقُولُوا يَا أَبَانَا إِنَّ ابْنَكَ سَرَقَ وَمَا شَهِدْنَا إِلَّا بِمَا عَلِمْنَا وَمَا كُنَّا لِلْغَيْبِ حَافِظِينَ', 12, 81, 13, 49, 245, 'شما به سوی پدرتان بازگردید و بگویید: پدر (جان)، پسرت دزدی کرد! و ما جز به آنچه می‌دانستیم گواهی ندادیم؛ و ما از غیب آگاه نبودیم!'),
(1678, 1678, 'وَاسْأَلِ الْقَرْيَةَ الَّتِي كُنَّا فِيهَا وَالْعِيرَ الَّتِي أَقْبَلْنَا فِيهَا ۖ وَإِنَّا لَصَادِقُونَ', 12, 82, 13, 49, 245, '(و اگر اطمینان نداری،) از آن شهر که در آن بودیم سؤال کن، و نیز از آن قافله که با آن آمدیم (بپرس)! و ما (در گفتار خود) صادق هستیم!»'),
(1679, 1679, 'قَالَ بَلْ سَوَّلَتْ لَكُمْ أَنْفُسُكُمْ أَمْرًا ۖ فَصَبْرٌ جَمِيلٌ ۖ عَسَى اللَّهُ أَنْ يَأْتِيَنِي بِهِمْ جَمِيعًا ۚ إِنَّهُ هُوَ الْعَلِيمُ الْحَكِيمُ', 12, 83, 13, 49, 245, '(یعقوب) گفت: «(هوای) نفس شما، مسأله را چنین در نظرتان آراسته است! من صبر می‌کنم، صبری زیبا (و خالی از کفران)! امیدوارم خداوند همه آنها را به من بازگرداند؛ چرا که او دانا و حکیم است!'),
(1680, 1680, 'وَتَوَلَّىٰ عَنْهُمْ وَقَالَ يَا أَسَفَىٰ عَلَىٰ يُوسُفَ وَابْيَضَّتْ عَيْنَاهُ مِنَ الْحُزْنِ فَهُوَ كَظِيمٌ', 12, 84, 13, 49, 245, 'و از آنها روی برگرداند و گفت: «وا اسفا بر یوسف!» و چشمان او از اندوه سفید شد، اما خشم خود را فرو می‌برد (و هرگز کفران نمی‌کرد)!'),
(1681, 1681, 'قَالُوا تَاللَّهِ تَفْتَأُ تَذْكُرُ يُوسُفَ حَتَّىٰ تَكُونَ حَرَضًا أَوْ تَكُونَ مِنَ الْهَالِكِينَ', 12, 85, 13, 49, 245, 'گفتند: «به خدا تو آنقدر یاد یوسف می‌کنی تا در آستانه مرگ قرار گیری، یا هلاک گردی!»'),
(1682, 1682, 'قَالَ إِنَّمَا أَشْكُو بَثِّي وَحُزْنِي إِلَى اللَّهِ وَأَعْلَمُ مِنَ اللَّهِ مَا لَا تَعْلَمُونَ', 12, 86, 13, 49, 245, 'گفت: «من غم و اندوهم را تنها به خدا می‌گویم (و شکایت نزد او می‌برم)! و از خدا چیزهایی می‌دانم که شما نمی‌دانید!'),
(1683, 1683, 'يَا بَنِيَّ اذْهَبُوا فَتَحَسَّسُوا مِنْ يُوسُفَ وَأَخِيهِ وَلَا تَيْأَسُوا مِنْ رَوْحِ اللَّهِ ۖ إِنَّهُ لَا يَيْأَسُ مِنْ رَوْحِ اللَّهِ إِلَّا الْقَوْمُ الْكَافِرُونَ', 12, 87, 13, 49, 246, 'پسرانم! بروید، و از یوسف و برادرش جستجو کنید؛ و از رحمت خدا مأیوس نشوید؛ که تنها گروه کافران، از رحمت خدا مأیوس می‌شوند!»'),
(1684, 1684, 'فَلَمَّا دَخَلُوا عَلَيْهِ قَالُوا يَا أَيُّهَا الْعَزِيزُ مَسَّنَا وَأَهْلَنَا الضُّرُّ وَجِئْنَا بِبِضَاعَةٍ مُزْجَاةٍ فَأَوْفِ لَنَا الْكَيْلَ وَتَصَدَّقْ عَلَيْنَا ۖ إِنَّ اللَّهَ يَجْزِي الْمُتَصَدِّقِينَ', 12, 88, 13, 49, 246, 'هنگامی که آنها بر او = یوسف‌ وارد شدند، گفتند: «ای عزیز! ما و خاندان ما را ناراحتی فرا گرفته، و متاع کمی (برای خرید موادّ غذایی) با خود آورده‌ایم؛ پیمانه را برای ما کامل کن؛ و بر ما تصدّق و بخشش نما، که خداوند بخشندگان را پاداش می‌دهد!»'),
(1685, 1685, 'قَالَ هَلْ عَلِمْتُمْ مَا فَعَلْتُمْ بِيُوسُفَ وَأَخِيهِ إِذْ أَنْتُمْ جَاهِلُونَ', 12, 89, 13, 49, 246, 'گفت: «آیا دانستید با یوسف و برادرش چه کردید، آنگاه که جاهل بودید؟!»'),
(1686, 1686, 'قَالُوا أَإِنَّكَ لَأَنْتَ يُوسُفُ ۖ قَالَ أَنَا يُوسُفُ وَهَٰذَا أَخِي ۖ قَدْ مَنَّ اللَّهُ عَلَيْنَا ۖ إِنَّهُ مَنْ يَتَّقِ وَيَصْبِرْ فَإِنَّ اللَّهَ لَا يُضِيعُ أَجْرَ الْمُحْسِنِينَ', 12, 90, 13, 49, 246, 'گفتند: «آیا تو همان یوسفی؟!» گفت: «(آری،) من یوسفم، و این برادر من است! خداوند بر ما منّت گذارد؛ هر کس تقوا پیشه کند، و شکیبایی و استقامت نماید، (سرانجام پیروز می‌شود؛) چرا که خداوند پاداش نیکوکاران را ضایع نمی‌کند!»'),
(1687, 1687, 'قَالُوا تَاللَّهِ لَقَدْ آثَرَكَ اللَّهُ عَلَيْنَا وَإِنْ كُنَّا لَخَاطِئِينَ', 12, 91, 13, 49, 246, 'گفتند: «به خدا سوگند، خداوند تو را بر ما برتری بخشیده؛ و ما خطاکار بودیم!»'),
(1688, 1688, 'قَالَ لَا تَثْرِيبَ عَلَيْكُمُ الْيَوْمَ ۖ يَغْفِرُ اللَّهُ لَكُمْ ۖ وَهُوَ أَرْحَمُ الرَّاحِمِينَ', 12, 92, 13, 49, 246, '(یوسف) گفت: «امروز ملامت و توبیخی بر شما نیست! خداوند شما را می‌بخشد؛ و او مهربانترین مهربانان است!'),
(1689, 1689, 'اذْهَبُوا بِقَمِيصِي هَٰذَا فَأَلْقُوهُ عَلَىٰ وَجْهِ أَبِي يَأْتِ بَصِيرًا وَأْتُونِي بِأَهْلِكُمْ أَجْمَعِينَ', 12, 93, 13, 49, 246, 'این پیراهن مرا ببرید، و بر صورت پدرم بیندازید، بینا می‌شود! و همه نزدیکان خود را نزد من بیاورید!»'),
(1690, 1690, 'وَلَمَّا فَصَلَتِ الْعِيرُ قَالَ أَبُوهُمْ إِنِّي لَأَجِدُ رِيحَ يُوسُفَ ۖ لَوْلَا أَنْ تُفَنِّدُونِ', 12, 94, 13, 49, 246, 'هنگامی که کاروان (از سرزمین مصر) جدا شد، پدرشان = یعقوب‌ گفت: «من بوی یوسف را احساس می‌کنم، اگر مرا به نادانی و کم عقلی نسبت ندهید!»'),
(1691, 1691, 'قَالُوا تَاللَّهِ إِنَّكَ لَفِي ضَلَالِكَ الْقَدِيمِ', 12, 95, 13, 49, 246, 'گفتند: «به خدا تو در همان گمراهی سابقت هستی!»'),
(1692, 1692, 'فَلَمَّا أَنْ جَاءَ الْبَشِيرُ أَلْقَاهُ عَلَىٰ وَجْهِهِ فَارْتَدَّ بَصِيرًا ۖ قَالَ أَلَمْ أَقُلْ لَكُمْ إِنِّي أَعْلَمُ مِنَ اللَّهِ مَا لَا تَعْلَمُونَ', 12, 96, 13, 49, 247, 'امّا هنگامی که بشارت دهنده فرا رسید، آن (پیراهن) را بر صورت او افکند؛ ناگهان بینا شد! گفت: «آیا به شما نگفتم من از خدا چیزهایی می‌دانم که شما نمی‌دانید؟!»'),
(1693, 1693, 'قَالُوا يَا أَبَانَا اسْتَغْفِرْ لَنَا ذُنُوبَنَا إِنَّا كُنَّا خَاطِئِينَ', 12, 97, 13, 49, 247, 'گفتند: «پدر! از خدا آمرزش گناهان ما را بخواه، که ما خطاکار بودیم!»'),
(1694, 1694, 'قَالَ سَوْفَ أَسْتَغْفِرُ لَكُمْ رَبِّي ۖ إِنَّهُ هُوَ الْغَفُورُ الرَّحِيمُ', 12, 98, 13, 49, 247, 'گفت: «بزودی برای شما از پروردگارم آمرزش می‌طلبم، که او آمرزنده و مهربان است!»'),
(1695, 1695, 'فَلَمَّا دَخَلُوا عَلَىٰ يُوسُفَ آوَىٰ إِلَيْهِ أَبَوَيْهِ وَقَالَ ادْخُلُوا مِصْرَ إِنْ شَاءَ اللَّهُ آمِنِينَ', 12, 99, 13, 49, 247, 'و هنگامی که بر یوسف وارد شدند، او پدر و مادر خود را در آغوش گرفت، و گفت: «همگی داخل مصر شوید، که انشاء الله در امن و امان خواهید بود!»'),
(1696, 1696, 'وَرَفَعَ أَبَوَيْهِ عَلَى الْعَرْشِ وَخَرُّوا لَهُ سُجَّدًا ۖ وَقَالَ يَا أَبَتِ هَٰذَا تَأْوِيلُ رُؤْيَايَ مِنْ قَبْلُ قَدْ جَعَلَهَا رَبِّي حَقًّا ۖ وَقَدْ أَحْسَنَ بِي إِذْ أَخْرَجَنِي مِنَ السِّجْنِ وَجَاءَ بِكُمْ مِنَ الْبَدْوِ مِنْ بَعْدِ أَنْ نَزَغَ الشَّيْطَانُ بَيْنِي وَبَيْنَ إِخْوَتِي ۚ إِنَّ رَبِّي لَطِيفٌ لِمَا يَشَاءُ ۚ إِنَّهُ هُوَ الْعَلِيمُ الْحَكِيمُ', 12, 100, 13, 49, 247, 'و پدر و مادر خود را بر تخت نشاند؛ و همگی بخاطر او به سجده افتادند؛ و گفت: «پدر! این تعبیر خوابی است که قبلاً دیدم؛ پروردگارم آن را حقّ قرار داد! و او به من نیکی کرد هنگامی که مرا از زندان بیرون آورد، و شما را از آن بیابان (به اینجا) آورد بعد از آنکه شیطان، میان من و برادرانم فساد کرد. پروردگارم نسبت به آنچه می‌خواهد (و شایسته می‌داند،) صاحب لطف است؛ چرا که او دانا و حکیم است!'),
(1697, 1697, ' رَبِّ قَدْ آتَيْتَنِي مِنَ الْمُلْكِ وَعَلَّمْتَنِي مِنْ تَأْوِيلِ الْأَحَادِيثِ ۚ فَاطِرَ السَّمَاوَاتِ وَالْأَرْضِ أَنْتَ وَلِيِّي فِي الدُّنْيَا وَالْآخِرَةِ ۖ تَوَفَّنِي مُسْلِمًا وَأَلْحِقْنِي بِالصَّالِحِينَ', 12, 101, 13, 50, 247, 'پروردگارا! بخشی (عظیم) از حکومت به من بخشیدی، و مرا از علم تعبیر خوابها آگاه ساختی! ای آفریننده آسمانها و زمین! تو ولیّ و سرپرست من در دنیا و آخرت هستی، مرا مسلمان بمیران؛ و به صالحان ملحق فرما!»'),
(1698, 1698, 'ذَٰلِكَ مِنْ أَنْبَاءِ الْغَيْبِ نُوحِيهِ إِلَيْكَ ۖ وَمَا كُنْتَ لَدَيْهِمْ إِذْ أَجْمَعُوا أَمْرَهُمْ وَهُمْ يَمْكُرُونَ', 12, 102, 13, 50, 247, 'این از خبرهای غیب است که به تو وحی می‌فرستیم! تو (هرگز) نزد آنها نبودی هنگامی که تصمیم می‌گرفتند و نقشه می‌کشیدند!'),
(1699, 1699, 'وَمَا أَكْثَرُ النَّاسِ وَلَوْ حَرَصْتَ بِمُؤْمِنِينَ', 12, 103, 13, 50, 247, 'و بیشتر مردم، هر چند اصرار داشته باشی، ایمان نمی‌آورند!'),
(1700, 1700, 'وَمَا تَسْأَلُهُمْ عَلَيْهِ مِنْ أَجْرٍ ۚ إِنْ هُوَ إِلَّا ذِكْرٌ لِلْعَالَمِينَ', 12, 104, 13, 50, 248, 'و تو (هرگز) از آنها پاداشی نمی‌طلبی؛ آن نیست مگر تذکّری برای جهانیان!'),
(1701, 1701, 'وَكَأَيِّنْ مِنْ آيَةٍ فِي السَّمَاوَاتِ وَالْأَرْضِ يَمُرُّونَ عَلَيْهَا وَهُمْ عَنْهَا مُعْرِضُونَ', 12, 105, 13, 50, 248, 'و چه بسیار نشانه‌ای (از خدا) در آسمانها و زمین که آنها از کنارش می‌گذرند، و از آن رویگردانند!'),
(1702, 1702, 'وَمَا يُؤْمِنُ أَكْثَرُهُمْ بِاللَّهِ إِلَّا وَهُمْ مُشْرِكُونَ', 12, 106, 13, 50, 248, 'و بیشتر آنها که مدعی ایمان به خدا هستند، مشرکند!'),
(1703, 1703, 'أَفَأَمِنُوا أَنْ تَأْتِيَهُمْ غَاشِيَةٌ مِنْ عَذَابِ اللَّهِ أَوْ تَأْتِيَهُمُ السَّاعَةُ بَغْتَةً وَهُمْ لَا يَشْعُرُونَ', 12, 107, 13, 50, 248, 'آیا ایمن از آنند که عذاب فراگیری از سوی خدا به سراغ آنان بیاید، یا ساعت رستاخیز ناگهان فرارسد، در حالی که متوجّه نیستند؟!');
INSERT INTO `Quran` (`rowid`, `id`, `Arabic`, `Chapter`, `Verse`, `Juz`, `Hizb`, `Page`, `tr`) VALUES
(1704, 1704, 'قُلْ هَٰذِهِ سَبِيلِي أَدْعُو إِلَى اللَّهِ ۚ عَلَىٰ بَصِيرَةٍ أَنَا وَمَنِ اتَّبَعَنِي ۖ وَسُبْحَانَ اللَّهِ وَمَا أَنَا مِنَ الْمُشْرِكِينَ', 12, 108, 13, 50, 248, 'بگو: «این راه من است من و پیروانم، و با بصیرت کامل، همه مردم را به سوی خدا دعوت می‌کنیم! منزّه است خدا! و من از مشرکان نیستم!»'),
(1705, 1705, 'وَمَا أَرْسَلْنَا مِنْ قَبْلِكَ إِلَّا رِجَالًا نُوحِي إِلَيْهِمْ مِنْ أَهْلِ الْقُرَىٰ ۗ أَفَلَمْ يَسِيرُوا فِي الْأَرْضِ فَيَنْظُرُوا كَيْفَ كَانَ عَاقِبَةُ الَّذِينَ مِنْ قَبْلِهِمْ ۗ وَلَدَارُ الْآخِرَةِ خَيْرٌ لِلَّذِينَ اتَّقَوْا ۗ أَفَلَا تَعْقِلُونَ', 12, 109, 13, 50, 248, 'و ما نفرستادیم پیش از تو، جز مردانی از اهل آبادیها که به آنها وحی می‌کردیم! آیا (مخالفان دعوت تو،) در زمین سیر نکردند تا ببینند عاقبت کسانی که پیش از آنها بودند چه شد؟! و سرای آخرت برای پرهیزکاران بهتر است! آیا فکر نمی‌کنید؟!'),
(1706, 1706, 'حَتَّىٰ إِذَا اسْتَيْأَسَ الرُّسُلُ وَظَنُّوا أَنَّهُمْ قَدْ كُذِبُوا جَاءَهُمْ نَصْرُنَا فَنُجِّيَ مَنْ نَشَاءُ ۖ وَلَا يُرَدُّ بَأْسُنَا عَنِ الْقَوْمِ الْمُجْرِمِينَ', 12, 110, 13, 50, 248, '(پیامبران به دعوت خود، و دشمنان آنها به مخالفت خود همچنان ادامه دادند) تا آنگاه که رسولان مأیوس شدند، و (مردم) گمان کردند که به آنان دروغ گفته شده است؛ در این هنگام، یاری ما به سراغ آنها آمد؛ آنان را که خواستیم نجات یافتند؛ و مجازات و عذاب ما از قوم گنهکار بازگردانده نمی‌شود!'),
(1707, 1707, 'لَقَدْ كَانَ فِي قَصَصِهِمْ عِبْرَةٌ لِأُولِي الْأَلْبَابِ ۗ مَا كَانَ حَدِيثًا يُفْتَرَىٰ وَلَٰكِنْ تَصْدِيقَ الَّذِي بَيْنَ يَدَيْهِ وَتَفْصِيلَ كُلِّ شَيْءٍ وَهُدًى وَرَحْمَةً لِقَوْمٍ يُؤْمِنُونَ', 12, 111, 13, 50, 248, 'در سرگذشت آنها درس عبرتی برای صاحبان اندیشه بود! اینها داستان دروغین نبود؛ بلکه (وحی آسمانی است، و) هماهنگ است با آنچه پیش روی او (از کتب آسمانی پیشین) قرار دارد؛ و شرح هر چیزی (که پایه سعادت انسان است)؛ و هدایت و رحمتی است برای گروهی که ایمان می‌آورند!'),
(1708, 1708, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ المر ۚ تِلْكَ آيَاتُ الْكِتَابِ ۗ وَالَّذِي أُنْزِلَ إِلَيْكَ مِنْ رَبِّكَ الْحَقُّ وَلَٰكِنَّ أَكْثَرَ النَّاسِ لَا يُؤْمِنُونَ', 13, 1, 13, 50, 249, 'المر، اینها آیات کتاب (آسمانی) است؛ و آنچه از طرف پروردگارت بر تو نازل شده، حقّ است؛ ولی بیشتر مردم ایمان نمی‌آورند!'),
(1709, 1709, 'اللَّهُ الَّذِي رَفَعَ السَّمَاوَاتِ بِغَيْرِ عَمَدٍ تَرَوْنَهَا ۖ ثُمَّ اسْتَوَىٰ عَلَى الْعَرْشِ ۖ وَسَخَّرَ الشَّمْسَ وَالْقَمَرَ ۖ كُلٌّ يَجْرِي لِأَجَلٍ مُسَمًّى ۚ يُدَبِّرُ الْأَمْرَ يُفَصِّلُ الْآيَاتِ لَعَلَّكُمْ بِلِقَاءِ رَبِّكُمْ تُوقِنُونَ', 13, 2, 13, 50, 249, 'خدا همان کسی است که آسمانها را، بدون ستونهایی که برای شما دیدنی باشد، برافراشت، سپس بر عرش استیلا یافت (و زمام تدبیر جهان را در کف قدرت گرفت)؛ و خورشید و ماه را مسخّر ساخت، که هر کدام تا زمان معینی حرکت دارند! کارها را او تدبیر می‌کند؛ آیات را (برای شما) تشریح می‌نماید؛ شاید به لقای پروردگارتان یقین پیدا کنید!'),
(1710, 1710, 'وَهُوَ الَّذِي مَدَّ الْأَرْضَ وَجَعَلَ فِيهَا رَوَاسِيَ وَأَنْهَارًا ۖ وَمِنْ كُلِّ الثَّمَرَاتِ جَعَلَ فِيهَا زَوْجَيْنِ اثْنَيْنِ ۖ يُغْشِي اللَّيْلَ النَّهَارَ ۚ إِنَّ فِي ذَٰلِكَ لَآيَاتٍ لِقَوْمٍ يَتَفَكَّرُونَ', 13, 3, 13, 50, 249, 'و او کسی است که زمین را گسترد؛ و در آن کوه‌ها و نهرهایی قرار داد؛ و در آن از تمام میوه‌ها دو جفت آفرید؛ (پرده سیاه) شب را بر روز می‌پوشاند؛ در اینها آیاتی است برای گروهی که تفکر می‌کنند!'),
(1711, 1711, 'وَفِي الْأَرْضِ قِطَعٌ مُتَجَاوِرَاتٌ وَجَنَّاتٌ مِنْ أَعْنَابٍ وَزَرْعٌ وَنَخِيلٌ صِنْوَانٌ وَغَيْرُ صِنْوَانٍ يُسْقَىٰ بِمَاءٍ وَاحِدٍ وَنُفَضِّلُ بَعْضَهَا عَلَىٰ بَعْضٍ فِي الْأُكُلِ ۚ إِنَّ فِي ذَٰلِكَ لَآيَاتٍ لِقَوْمٍ يَعْقِلُونَ', 13, 4, 13, 50, 249, 'و در روی زمین، قطعاتی در کنار هم قرار دارد که با هم متفاوتند؛ و (نیز) باغهایی از انگور و زراعت و نخلها، (و درختان میوه گوناگون) که گاه بر یک پایه می‌رویند و گاه بر دو پایه؛ (و عجیب‌تر آنکه) همه آنها از یک آب سیراب می‌شوند! و با این حال، بعضی از آنها را از جهت میوه بر دیگری برتری می‌دهیم؛ در اینها نشانه‌هایی است برای گروهی که عقل خویش را به کار می‌گیرند!'),
(1712, 1712, ' وَإِنْ تَعْجَبْ فَعَجَبٌ قَوْلُهُمْ أَإِذَا كُنَّا تُرَابًا أَإِنَّا لَفِي خَلْقٍ جَدِيدٍ ۗ أُولَٰئِكَ الَّذِينَ كَفَرُوا بِرَبِّهِمْ ۖ وَأُولَٰئِكَ الْأَغْلَالُ فِي أَعْنَاقِهِمْ ۖ وَأُولَٰئِكَ أَصْحَابُ النَّارِ ۖ هُمْ فِيهَا خَالِدُونَ', 13, 5, 13, 50, 249, 'و اگر (از چیزی) تعجب می‌کنی، عجیب گفتار آنهاست که می‌گویند: «آیا هنگامی که ما خاک شدیم، (بار دیگر زنده می‌شویم و) به خلقت جدیدی بازمی‌گردیم؟!» آنها کسانی هستند که به پروردگارشان کافر شده‌اند؛ و آنان غل و زنجیرها در گردنشان است؛ و آنها اهل دوزخند، و جاودانه در آن خواهند ماند!'),
(1713, 1713, 'وَيَسْتَعْجِلُونَكَ بِالسَّيِّئَةِ قَبْلَ الْحَسَنَةِ وَقَدْ خَلَتْ مِنْ قَبْلِهِمُ الْمَثُلَاتُ ۗ وَإِنَّ رَبَّكَ لَذُو مَغْفِرَةٍ لِلنَّاسِ عَلَىٰ ظُلْمِهِمْ ۖ وَإِنَّ رَبَّكَ لَشَدِيدُ الْعِقَابِ', 13, 6, 13, 50, 250, 'آنها پیش از حسنه (و رحمت)، از تو تقاضای شتاب در سیّئه (و عذاب) می‌کنند؛ با اینکه پیش از آنها بلاهای عبرت انگیز نازل شده است! و پروردگار تو نسبت به مردم -با اینکه ظلم می‌کنند- دارای مغفرت است؛ و (در عین حال،) پروردگارت دارای عذاب شدید است!'),
(1714, 1714, 'وَيَقُولُ الَّذِينَ كَفَرُوا لَوْلَا أُنْزِلَ عَلَيْهِ آيَةٌ مِنْ رَبِّهِ ۗ إِنَّمَا أَنْتَ مُنْذِرٌ ۖ وَلِكُلِّ قَوْمٍ هَادٍ', 13, 7, 13, 50, 250, 'کسانی که کافر شدند می‌گویند: «چرا آیه (و معجزه‌ای) از پروردگارش بر او نازل نشده؟!» تو فقط بیم دهنده‌ای! و برای هر گروهی هدایت کننده‌ای است (؛ و اینها همه بهانه است، نه برای جستجوی حقیقت)!'),
(1715, 1715, 'اللَّهُ يَعْلَمُ مَا تَحْمِلُ كُلُّ أُنْثَىٰ وَمَا تَغِيضُ الْأَرْحَامُ وَمَا تَزْدَادُ ۖ وَكُلُّ شَيْءٍ عِنْدَهُ بِمِقْدَارٍ', 13, 8, 13, 50, 250, 'خدا از جنین‌هایی که هر (انسان یا حیوان) مادّه‌ای حمل می‌کند آگاه است؛ و نیز از آنچه رحمها کم می‌کنند (و پیش از موعد مقرّر می‌زایند)، و هم از آنچه افزون می‌کنند (و بعد از موقع میزایند)؛ و هر چیز نزد او مقدار معینی دارد.'),
(1716, 1716, 'عَالِمُ الْغَيْبِ وَالشَّهَادَةِ الْكَبِيرُ الْمُتَعَالِ', 13, 9, 13, 50, 250, 'او از غیب و شهود آگاه، و بزرگ و متعالی است!'),
(1717, 1717, 'سَوَاءٌ مِنْكُمْ مَنْ أَسَرَّ الْقَوْلَ وَمَنْ جَهَرَ بِهِ وَمَنْ هُوَ مُسْتَخْفٍ بِاللَّيْلِ وَسَارِبٌ بِالنَّهَارِ', 13, 10, 13, 50, 250, 'برای او یکسان است کسانی از شما که پنهانی سخن بگویند، یا آن را آشکار سازند؛ و کسانی که شبانگاه مخفیانه حرکت می‌کنند، یا در روشنایی روز.'),
(1718, 1718, 'لَهُ مُعَقِّبَاتٌ مِنْ بَيْنِ يَدَيْهِ وَمِنْ خَلْفِهِ يَحْفَظُونَهُ مِنْ أَمْرِ اللَّهِ ۗ إِنَّ اللَّهَ لَا يُغَيِّرُ مَا بِقَوْمٍ حَتَّىٰ يُغَيِّرُوا مَا بِأَنْفُسِهِمْ ۗ وَإِذَا أَرَادَ اللَّهُ بِقَوْمٍ سُوءًا فَلَا مَرَدَّ لَهُ ۚ وَمَا لَهُمْ مِنْ دُونِهِ مِنْ وَالٍ', 13, 11, 13, 50, 250, 'برای انسان، مأمورانی است که پی در پی، از پیش رو، و از پشت سرش او را از فرمان خدا = حوادث غیر حتمی‌ حفظ می‌کنند؛ (امّا) خداوند سرنوشت هیچ قوم (و ملّتی) را تغییر نمی‌دهد مگر آنکه آنان آنچه را در خودشان است تغییر دهند! و هنگامی که خدا اراده سوئی به قومی (بخاطر اعمالشان) کند، هیچ چیز مانع آن نخواهد شد؛ و جز خدا، سرپرستی نخواهند داشت!'),
(1719, 1719, 'هُوَ الَّذِي يُرِيكُمُ الْبَرْقَ خَوْفًا وَطَمَعًا وَيُنْشِئُ السَّحَابَ الثِّقَالَ', 13, 12, 13, 50, 250, 'او کسی است که برق را به شما نشان می‌دهد، که هم مایه ترس است و هم مایه امید؛ و ابرهای سنگین‌بار ایجاد می‌کند!'),
(1720, 1720, 'وَيُسَبِّحُ الرَّعْدُ بِحَمْدِهِ وَالْمَلَائِكَةُ مِنْ خِيفَتِهِ وَيُرْسِلُ الصَّوَاعِقَ فَيُصِيبُ بِهَا مَنْ يَشَاءُ وَهُمْ يُجَادِلُونَ فِي اللَّهِ وَهُوَ شَدِيدُ الْمِحَالِ', 13, 13, 13, 50, 250, 'و رعد، تسبیح و حمد او می‌گوید؛ و (نیز) فرشتگان از ترس او! و صاعقه‌ها را می‌فرستد؛ و هر کس را بخواهد گرفتار آن می‌سازد، (در حالی که آنها با مشاهده این همه آیات الهی، باز هم) درباره خدا به مجادله مشغولند! و او قدرتی بی‌انتها (و مجازاتی دردناک) دارد!'),
(1721, 1721, 'لَهُ دَعْوَةُ الْحَقِّ ۖ وَالَّذِينَ يَدْعُونَ مِنْ دُونِهِ لَا يَسْتَجِيبُونَ لَهُمْ بِشَيْءٍ إِلَّا كَبَاسِطِ كَفَّيْهِ إِلَى الْمَاءِ لِيَبْلُغَ فَاهُ وَمَا هُوَ بِبَالِغِهِ ۚ وَمَا دُعَاءُ الْكَافِرِينَ إِلَّا فِي ضَلَالٍ', 13, 14, 13, 50, 251, 'دعوت حق از آن اوست! و کسانی را که (مشرکان) غیر از خدا می‌خوانند، (هرگز) به دعوت آنها پاسخ نمی‌گویند! آنها همچون کسی هستند که کفهای (دست) خود را به سوی آب می‌گشاید تا آب به دهانش برسد، و هرگز نخواهد رسید! و دعای کافران، جز در ضلال (و گمراهی) نیست!'),
(1722, 1722, 'وَلِلَّهِ يَسْجُدُ مَنْ فِي السَّمَاوَاتِ وَالْأَرْضِ طَوْعًا وَكَرْهًا وَظِلَالُهُمْ بِالْغُدُوِّ وَالْآصَالِ ۩', 13, 15, 13, 50, 251, 'تمام کسانی که در آسمانها و زمین هستند -از روی اطاعت یا اکراه- و همچنین سایه‌هایشان، هر صبح و عصر برای خدا سجده می‌کنند.'),
(1723, 1723, 'قُلْ مَنْ رَبُّ السَّمَاوَاتِ وَالْأَرْضِ قُلِ اللَّهُ ۚ قُلْ أَفَاتَّخَذْتُمْ مِنْ دُونِهِ أَوْلِيَاءَ لَا يَمْلِكُونَ لِأَنْفُسِهِمْ نَفْعًا وَلَا ضَرًّا ۚ قُلْ هَلْ يَسْتَوِي الْأَعْمَىٰ وَالْبَصِيرُ أَمْ هَلْ تَسْتَوِي الظُّلُمَاتُ وَالنُّورُ ۗ أَمْ جَعَلُوا لِلَّهِ شُرَكَاءَ خَلَقُوا كَخَلْقِهِ فَتَشَابَهَ الْخَلْقُ عَلَيْهِمْ ۚ قُلِ اللَّهُ خَالِقُ كُلِّ شَيْءٍ وَهُوَ الْوَاحِدُ الْقَهَّارُ', 13, 16, 13, 50, 251, 'بگو: «چه کسی پروردگار آسمانها و زمین است؟» بگو: «اللّه!» (سپس) بگو: «آیا اولیا (و خدایانی) غیر از او برای خود برگزیده‌اید که (حتّی) مالک سود و زیان خود نیستند (تا چه رسد به شما؟!)» بگو: «آیا نابینا و بینا یکسانند؟! یا ظلمتها و نور برابرند؟! آیا آنها همتایانی برای خدا قرار دادند بخاطر اینکه آنان همانند خدا آفرینشی داشتند، و این آفرینشها بر آنها مشتبه شده است؟!» بگو: «خدا خالق همه چیز است؛ و اوست یکتا و پیروز!»'),
(1724, 1724, 'أَنْزَلَ مِنَ السَّمَاءِ مَاءً فَسَالَتْ أَوْدِيَةٌ بِقَدَرِهَا فَاحْتَمَلَ السَّيْلُ زَبَدًا رَابِيًا ۚ وَمِمَّا يُوقِدُونَ عَلَيْهِ فِي النَّارِ ابْتِغَاءَ حِلْيَةٍ أَوْ مَتَاعٍ زَبَدٌ مِثْلُهُ ۚ كَذَٰلِكَ يَضْرِبُ اللَّهُ الْحَقَّ وَالْبَاطِلَ ۚ فَأَمَّا الزَّبَدُ فَيَذْهَبُ جُفَاءً ۖ وَأَمَّا مَا يَنْفَعُ النَّاسَ فَيَمْكُثُ فِي الْأَرْضِ ۚ كَذَٰلِكَ يَضْرِبُ اللَّهُ الْأَمْثَالَ', 13, 17, 13, 50, 251, 'خداوند از آسمان آبی فرستاد؛ و از هر درّه و رودخانه‌ای به اندازه آنها سیلابی جاری شد؛ سپس سیل بر روی خود کفی حمل کرد؛ و از آنچه (در کوره‌ها،) برای به دست آوردن زینت آلات یا وسایل زندگی، آتش روی آن روشن می‌کنند نیز کفهایی مانند آن به وجود می‌آید -خداوند، حق و باطل را چنین مثل می‌زند!- امّا کفها به بیرون پرتاب می‌شوند، ولی آنچه به مردم سود می‌رساند = آب یا فلز خالص در زمین می‌ماند؛ خداوند اینچنین مثال می‌زند!'),
(1725, 1725, 'لِلَّذِينَ اسْتَجَابُوا لِرَبِّهِمُ الْحُسْنَىٰ ۚ وَالَّذِينَ لَمْ يَسْتَجِيبُوا لَهُ لَوْ أَنَّ لَهُمْ مَا فِي الْأَرْضِ جَمِيعًا وَمِثْلَهُ مَعَهُ لَافْتَدَوْا بِهِ ۚ أُولَٰئِكَ لَهُمْ سُوءُ الْحِسَابِ وَمَأْوَاهُمْ جَهَنَّمُ ۖ وَبِئْسَ الْمِهَادُ', 13, 18, 13, 50, 251, 'برای آنها که دعوت پروردگارشان را اجابت کردند، (سرانجام و) نتیجه نیکوتر است؛ و کسانی که دعوت او را اجابت نکردند، (آنچنان در وحشت عذاب الهی فرو می‌روند، که) اگر تمام آنچه روی زمین است، و همانندش، از آن آنها باشد، همه را برای رهایی از عذاب می‌دهند! (ولی از آنها پذیرفته نخواهد شد!) برای آنها حساب بدی است؛ و جایگاهشان جهنم، و چه بد جایگاهی است!'),
(1726, 1726, ' أَفَمَنْ يَعْلَمُ أَنَّمَا أُنْزِلَ إِلَيْكَ مِنْ رَبِّكَ الْحَقُّ كَمَنْ هُوَ أَعْمَىٰ ۚ إِنَّمَا يَتَذَكَّرُ أُولُو الْأَلْبَابِ', 13, 19, 13, 51, 252, 'آیا کسی که می‌داند آنچه از طرف پروردگارت بر تو نازل شده حق است، همانند کسی است که نابیناست؟! تنها صاحبان اندیشه متذکّر می‌شوند...'),
(1727, 1727, 'الَّذِينَ يُوفُونَ بِعَهْدِ اللَّهِ وَلَا يَنْقُضُونَ الْمِيثَاقَ', 13, 20, 13, 51, 252, 'آنها که به عهد الهی وفا می‌کنند، و پیمان را نمی‌شکنند...'),
(1728, 1728, 'وَالَّذِينَ يَصِلُونَ مَا أَمَرَ اللَّهُ بِهِ أَنْ يُوصَلَ وَيَخْشَوْنَ رَبَّهُمْ وَيَخَافُونَ سُوءَ الْحِسَابِ', 13, 21, 13, 51, 252, 'و آنها که پیوندهایی را که خدا دستور به برقراری آن داده، برقرار میدارند؛ و از پروردگارشان می‌ترسند؛ و از بدی حساب (روز قیامت) بیم دارند...'),
(1729, 1729, 'وَالَّذِينَ صَبَرُوا ابْتِغَاءَ وَجْهِ رَبِّهِمْ وَأَقَامُوا الصَّلَاةَ وَأَنْفَقُوا مِمَّا رَزَقْنَاهُمْ سِرًّا وَعَلَانِيَةً وَيَدْرَءُونَ بِالْحَسَنَةِ السَّيِّئَةَ أُولَٰئِكَ لَهُمْ عُقْبَى الدَّارِ', 13, 22, 13, 51, 252, 'و آنها که بخاطر ذات (پاک) پروردگارشان شکیبایی می‌کنند؛ و نماز را برپا می‌دارند؛ و از آنچه به آنها روزی داده‌ایم، در پنهان و آشکار، انفاق می‌کنند؛ و با حسنات، سیئات را از میان می‌برند؛ پایان نیک سرای دیگر، از آن آنهاست...'),
(1730, 1730, 'جَنَّاتُ عَدْنٍ يَدْخُلُونَهَا وَمَنْ صَلَحَ مِنْ آبَائِهِمْ وَأَزْوَاجِهِمْ وَذُرِّيَّاتِهِمْ ۖ وَالْمَلَائِكَةُ يَدْخُلُونَ عَلَيْهِمْ مِنْ كُلِّ بَابٍ', 13, 23, 13, 51, 252, '(همان) باغهای جاویدان بهشتی که وارد آن می‌شوند؛ و همچنین پدران و همسران و فرزندان صالح آنها؛ و فرشتگان از هر دری بر آنان وارد می‌گردند...'),
(1731, 1731, 'سَلَامٌ عَلَيْكُمْ بِمَا صَبَرْتُمْ ۚ فَنِعْمَ عُقْبَى الدَّارِ', 13, 24, 13, 51, 252, '(و به آنان می‌گویند:) سلام بر شما بخاطر صبر و استقامتتان! چه نیکوست سرانجام آن سرا (ی جاویدان)!'),
(1732, 1732, 'وَالَّذِينَ يَنْقُضُونَ عَهْدَ اللَّهِ مِنْ بَعْدِ مِيثَاقِهِ وَيَقْطَعُونَ مَا أَمَرَ اللَّهُ بِهِ أَنْ يُوصَلَ وَيُفْسِدُونَ فِي الْأَرْضِ ۙ أُولَٰئِكَ لَهُمُ اللَّعْنَةُ وَلَهُمْ سُوءُ الدَّارِ', 13, 25, 13, 51, 252, 'آنها که عهد الهی را پس از محکم کردن می‌شکنند، و پیوندهایی را که خدا دستور به برقراری آن داده قطع می‌کنند، و در روی زمین فساد می‌نمایند، لعنت برای آنهاست؛ و بدی (و مجازات) سرای آخرت!'),
(1733, 1733, 'اللَّهُ يَبْسُطُ الرِّزْقَ لِمَنْ يَشَاءُ وَيَقْدِرُ ۚ وَفَرِحُوا بِالْحَيَاةِ الدُّنْيَا وَمَا الْحَيَاةُ الدُّنْيَا فِي الْآخِرَةِ إِلَّا مَتَاعٌ', 13, 26, 13, 51, 252, 'خدا روزی را برای هر کس بخواهد (و شایسته بداند) وسیع، برای هر کس بخواهد (و مصلحت بداند،) تنگ قرارمی‌دهد؛ ولی آنها = کافران‌ به زندگی دنیا، شاد (و خوشحال) شدند؛ در حالی که زندگی دنیا در برابر آخرت، متاع ناچیزی است!'),
(1734, 1734, 'وَيَقُولُ الَّذِينَ كَفَرُوا لَوْلَا أُنْزِلَ عَلَيْهِ آيَةٌ مِنْ رَبِّهِ ۗ قُلْ إِنَّ اللَّهَ يُضِلُّ مَنْ يَشَاءُ وَيَهْدِي إِلَيْهِ مَنْ أَنَابَ', 13, 27, 13, 51, 252, 'کسانی که کافر شدند می‌گویند: «چرا آیه (و معجزه) ای از پروردگارش بر او نازل نشده است؟!» بگو: «خداوند هر کس را بخواهد گمراه، و هر کس را که بازگردد، به سوی خودش هدایت می‌کند! (کمبودی در معجزه‌ها نیست؛ لجاجت آنها مانع است!)»'),
(1735, 1735, 'الَّذِينَ آمَنُوا وَتَطْمَئِنُّ قُلُوبُهُمْ بِذِكْرِ اللَّهِ ۗ أَلَا بِذِكْرِ اللَّهِ تَطْمَئِنُّ الْقُلُوبُ', 13, 28, 13, 51, 252, 'آنها کسانی هستند که ایمان آورده‌اند، و دلهایشان به یاد خدا مطمئن (و آرام) است؛ آگاه باشید، تنها با یاد خدا دلها آرامش می‌یابد!'),
(1736, 1736, 'الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ طُوبَىٰ لَهُمْ وَحُسْنُ مَآبٍ', 13, 29, 13, 51, 253, 'آنها که ایمان آوردند و کارهای شایسته انجام دادند، پاکیزه‌ترین (زندگی) نصیبشان است؛ و بهترین سرانجامها!'),
(1737, 1737, 'كَذَٰلِكَ أَرْسَلْنَاكَ فِي أُمَّةٍ قَدْ خَلَتْ مِنْ قَبْلِهَا أُمَمٌ لِتَتْلُوَ عَلَيْهِمُ الَّذِي أَوْحَيْنَا إِلَيْكَ وَهُمْ يَكْفُرُونَ بِالرَّحْمَٰنِ ۚ قُلْ هُوَ رَبِّي لَا إِلَٰهَ إِلَّا هُوَ عَلَيْهِ تَوَكَّلْتُ وَإِلَيْهِ مَتَابِ', 13, 30, 13, 51, 253, 'همان گونه (که پیامبران پیشین را مبعوث کردیم،) تو را به میان امّتی فرستادیم که پیش از آنها امّتهای دیگری آمدند و رفتند، تا آنچه را به تو وحی نموده‌ایم بر آنان بخوانی، در حالی که به رحمان = خداوندی که رحمتش همگان را فراگرفته‌ کفر می‌ورزند؛ بگو: «او پروردگار من است! معبودی جز او نیست! بر او توکّل کردم؛ و بازگشتم بسوی اوست!»'),
(1738, 1738, 'وَلَوْ أَنَّ قُرْآنًا سُيِّرَتْ بِهِ الْجِبَالُ أَوْ قُطِّعَتْ بِهِ الْأَرْضُ أَوْ كُلِّمَ بِهِ الْمَوْتَىٰ ۗ بَلْ لِلَّهِ الْأَمْرُ جَمِيعًا ۗ أَفَلَمْ يَيْأَسِ الَّذِينَ آمَنُوا أَنْ لَوْ يَشَاءُ اللَّهُ لَهَدَى النَّاسَ جَمِيعًا ۗ وَلَا يَزَالُ الَّذِينَ كَفَرُوا تُصِيبُهُمْ بِمَا صَنَعُوا قَارِعَةٌ أَوْ تَحُلُّ قَرِيبًا مِنْ دَارِهِمْ حَتَّىٰ يَأْتِيَ وَعْدُ اللَّهِ ۚ إِنَّ اللَّهَ لَا يُخْلِفُ الْمِيعَادَ', 13, 31, 13, 51, 253, 'اگر بوسیله قرآن، کوه‌ها به حرکت درآیند یا زمینها قطعه قطعه شوند، یا بوسیله آن با مردگان سخن گفته شود، (باز هم ایمان نخواهند آورد!) ولی همه کارها در اختیار خداست! آیا آنها که ایمان آورده‌اند نمی‌دانند که اگر خدا بخواهد همه مردم را (به اجبار) هدایت می‌کند (امّا هدایت اجباری سودی ندارد)! و پیوسته بلاهای کوبنده‌ای بر کافران بخاطر اعمالشان وارد می‌شود، و یا بنزدیکی خانه آنها فرود می‌آید، تا وعده (نهایی) خدا فرا رسد؛ به یقین خداوند در وعد؛ خود تخلّف نمی‌کند!'),
(1739, 1739, 'وَلَقَدِ اسْتُهْزِئَ بِرُسُلٍ مِنْ قَبْلِكَ فَأَمْلَيْتُ لِلَّذِينَ كَفَرُوا ثُمَّ أَخَذْتُهُمْ ۖ فَكَيْفَ كَانَ عِقَابِ', 13, 32, 13, 51, 253, '(تنها تو را استهزا نکردند،) پیامبران پیش از تو را نیز مورد استهزا قرار دادند؛ من به کافران مهلت دادم؛ سپس آنها را گرفتم؛ دیدی مجازات من چگونه بود؟!'),
(1740, 1740, 'أَفَمَنْ هُوَ قَائِمٌ عَلَىٰ كُلِّ نَفْسٍ بِمَا كَسَبَتْ ۗ وَجَعَلُوا لِلَّهِ شُرَكَاءَ قُلْ سَمُّوهُمْ ۚ أَمْ تُنَبِّئُونَهُ بِمَا لَا يَعْلَمُ فِي الْأَرْضِ أَمْ بِظَاهِرٍ مِنَ الْقَوْلِ ۗ بَلْ زُيِّنَ لِلَّذِينَ كَفَرُوا مَكْرُهُمْ وَصُدُّوا عَنِ السَّبِيلِ ۗ وَمَنْ يُضْلِلِ اللَّهُ فَمَا لَهُ مِنْ هَادٍ', 13, 33, 13, 51, 253, 'آیا کسی که بالای سر همه ایستاده، (و حافظ و نگهبان و مراقب همه است،) و اعمال همه را می‌بییند (، همچون کسی است که هیچ یک از این صفات را ندارد)؟! آنان برای خدا همتایانی قرار دادند؛ بگو: «آنها را نام ببرید! آیا چیزی را به او خبر می‌دهید که از وجود آن در روی زمین بی‌خبر است، یا سخنان ظاهری (و تو خالی) می‌گویید؟!» (نه، شریکی برای خدا وجود ندارد؛) بلکه در نظر کافران، دروغهایشان زینت داده شده، (و بر اثر ناپاکی درون، چنین می‌پندارند که واقعیتی دارد؛) و آنها از راه (خدا) بازداشته شده‌اند؛ و هر کس را خدا گمراه کند، راهنمایی برای او وجود نخواهد داشت!'),
(1741, 1741, 'لَهُمْ عَذَابٌ فِي الْحَيَاةِ الدُّنْيَا ۖ وَلَعَذَابُ الْآخِرَةِ أَشَقُّ ۖ وَمَا لَهُمْ مِنَ اللَّهِ مِنْ وَاقٍ', 13, 34, 13, 51, 253, 'در دنیا، برای آنها عذابی (دردناک) است؛ و عذاب آخرت سخت‌تر است؛ و در برابر (عذاب) خدا، هیچ کس نمی‌تواند آنها را نگه دارد!'),
(1742, 1742, ' مَثَلُ الْجَنَّةِ الَّتِي وُعِدَ الْمُتَّقُونَ ۖ تَجْرِي مِنْ تَحْتِهَا الْأَنْهَارُ ۖ أُكُلُهَا دَائِمٌ وَظِلُّهَا ۚ تِلْكَ عُقْبَى الَّذِينَ اتَّقَوْا ۖ وَعُقْبَى الْكَافِرِينَ النَّارُ', 13, 35, 13, 51, 254, 'توصیف بهشتی که به پرهیزگاران وعده داده شده، (این است که) نهرهای آب از زیر درختانش جاری است، میوه آن همیشگی، و سایه‌اش دائمی است؛ این سرانجام کسانی است که پرهیزگاری پیشه کردند؛ و سرانجام کافران، آتش است!'),
(1743, 1743, 'وَالَّذِينَ آتَيْنَاهُمُ الْكِتَابَ يَفْرَحُونَ بِمَا أُنْزِلَ إِلَيْكَ ۖ وَمِنَ الْأَحْزَابِ مَنْ يُنْكِرُ بَعْضَهُ ۚ قُلْ إِنَّمَا أُمِرْتُ أَنْ أَعْبُدَ اللَّهَ وَلَا أُشْرِكَ بِهِ ۚ إِلَيْهِ أَدْعُو وَإِلَيْهِ مَآبِ', 13, 36, 13, 51, 254, 'کسانی که کتاب آسمانی به آنان داده‌ایم، از آنچه بر تو نازل شده، خوشحالند؛ و بعضی از احزاب (و گروه‌ها)، قسمتی از آن را انکار می‌کنند؛ بگو: «من مأمورم که «اللّه» را بپرستم؛ و شریکی برای او قائل نشوم! به سوی او دعوت می‌کنم؛ و بازگشت من بسوی اوست!»'),
(1744, 1744, 'وَكَذَٰلِكَ أَنْزَلْنَاهُ حُكْمًا عَرَبِيًّا ۚ وَلَئِنِ اتَّبَعْتَ أَهْوَاءَهُمْ بَعْدَمَا جَاءَكَ مِنَ الْعِلْمِ مَا لَكَ مِنَ اللَّهِ مِنْ وَلِيٍّ وَلَا وَاقٍ', 13, 37, 13, 51, 254, 'همان‌گونه (که به پیامبران پیشین کتاب آسمانی دادیم،) بر تو نیز این (قرآن) را بعنوان فرمان روشن و صریحی نازل کردیم؛ و اگر از هوسهای آنان -بعد از آنکه آگاهی برای تو آمده- پیروی کنی، هیچ کس در برابر خدا، از تو حمایت و جلوگیری نخواهد کرد.'),
(1745, 1745, 'وَلَقَدْ أَرْسَلْنَا رُسُلًا مِنْ قَبْلِكَ وَجَعَلْنَا لَهُمْ أَزْوَاجًا وَذُرِّيَّةً ۚ وَمَا كَانَ لِرَسُولٍ أَنْ يَأْتِيَ بِآيَةٍ إِلَّا بِإِذْنِ اللَّهِ ۗ لِكُلِّ أَجَلٍ كِتَابٌ', 13, 38, 13, 51, 254, 'ما پیش از تو (نیز) رسولانی فرستادیم؛ و برای آنها همسران و فرزندانی قرار دادیم؛ و هیچ رسولی نمی‌توانست ا (ز پیش خود) معجزه‌ای بیاورد، مگر بفرمان خدا! هر زمانی نوشته‌ای دارد (و برای هر کاری، موعدی مقرّر است)!'),
(1746, 1746, 'يَمْحُو اللَّهُ مَا يَشَاءُ وَيُثْبِتُ ۖ وَعِنْدَهُ أُمُّ الْكِتَابِ', 13, 39, 13, 51, 254, 'خداوند هر چه را بخواهد محو، و هر چه را بخواهد اثبات می‌کند؛ و «امّ الکتاب» = لوح محفوظ نزد اوست!'),
(1747, 1747, 'وَإِنْ مَا نُرِيَنَّكَ بَعْضَ الَّذِي نَعِدُهُمْ أَوْ نَتَوَفَّيَنَّكَ فَإِنَّمَا عَلَيْكَ الْبَلَاغُ وَعَلَيْنَا الْحِسَابُ', 13, 40, 13, 51, 254, 'و اگر پاره‌ای از مجازاتها را که به آنها وعده می‌دهیم به تو نشان دهیم، یا (پیش از فرا رسیدن این مجازاتها) تو را بمیرانیم، در هر حال تو فقط مأمور ابلاغ هستی؛ و حساب (آنها) برماست.'),
(1748, 1748, 'أَوَلَمْ يَرَوْا أَنَّا نَأْتِي الْأَرْضَ نَنْقُصُهَا مِنْ أَطْرَافِهَا ۚ وَاللَّهُ يَحْكُمُ لَا مُعَقِّبَ لِحُكْمِهِ ۚ وَهُوَ سَرِيعُ الْحِسَابِ', 13, 41, 13, 51, 254, 'آیا ندیدند که ما پیوسته به سراغ زمین می‌آییم و از اطراف (و جوانب) آن کم می‌کنیم؟! (و جامعه‌ها، تمدّنها، و دانشمندان تدریجاً از میان می‌روند.) و خداوند حکومت می‌کند؛ و هیچ کس را یارای جلوگیری یا ردّ احکام او نیست؛ و او سریع الحساب است!'),
(1749, 1749, 'وَقَدْ مَكَرَ الَّذِينَ مِنْ قَبْلِهِمْ فَلِلَّهِ الْمَكْرُ جَمِيعًا ۖ يَعْلَمُ مَا تَكْسِبُ كُلُّ نَفْسٍ ۗ وَسَيَعْلَمُ الْكُفَّارُ لِمَنْ عُقْبَى الدَّارِ', 13, 42, 13, 51, 254, 'پیش از آنان نیز کسانی طرحها و نقشه‌ها کشیدند؛ ولی تمام طرحها و نقشه‌ها از آن خداست! او از کار هر کس آگاه است؛ و بزودی کفّار می‌دانند سرانجام (نیک و بد) در سرای دیگر از آن کیست!'),
(1750, 1750, 'وَيَقُولُ الَّذِينَ كَفَرُوا لَسْتَ مُرْسَلًا ۚ قُلْ كَفَىٰ بِاللَّهِ شَهِيدًا بَيْنِي وَبَيْنَكُمْ وَمَنْ عِنْدَهُ عِلْمُ الْكِتَابِ', 13, 43, 13, 51, 255, 'آنها که کافر شدند می‌گویند: «تو پیامبر نیستی!» بگو: «کافی است که خداوند، و کسی که علم کتاب (و آگاهی بر قرآن) نزد اوست، میان من و شما گواه باشند!»'),
(1751, 1751, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ الر ۚ كِتَابٌ أَنْزَلْنَاهُ إِلَيْكَ لِتُخْرِجَ النَّاسَ مِنَ الظُّلُمَاتِ إِلَى النُّورِ بِإِذْنِ رَبِّهِمْ إِلَىٰ صِرَاطِ الْعَزِيزِ الْحَمِيدِ', 14, 1, 13, 51, 255, 'الر، (این) کتابی است که بر تو نازل کردیم، تا مردم را از تاریکیها (ی شرک و ظلم و جهل،) به سوی روشنایی (ایمان و عدل و آگاهی،) بفرمان پروردگارشان در آوری، بسوی راه خداوند عزیز و حمید.'),
(1752, 1752, 'اللَّهِ الَّذِي لَهُ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ ۗ وَوَيْلٌ لِلْكَافِرِينَ مِنْ عَذَابٍ شَدِيدٍ', 14, 2, 13, 51, 255, 'همان خدایی که آنچه در آسمانها و آنچه در زمین است، از آن اوست؛ وای بر کافران از مجازات شدید (الهی)!'),
(1753, 1753, 'الَّذِينَ يَسْتَحِبُّونَ الْحَيَاةَ الدُّنْيَا عَلَى الْآخِرَةِ وَيَصُدُّونَ عَنْ سَبِيلِ اللَّهِ وَيَبْغُونَهَا عِوَجًا ۚ أُولَٰئِكَ فِي ضَلَالٍ بَعِيدٍ', 14, 3, 13, 51, 255, 'همانها که زندگی دنیا را بر آخرت ترجیح می‌دهند؛ و (مردم را) از راه خدا باز می‌دارند؛ و می‌خواهند راه حق را منحرف سازند؛ آنها در گمراهی دوری هستند!'),
(1754, 1754, 'وَمَا أَرْسَلْنَا مِنْ رَسُولٍ إِلَّا بِلِسَانِ قَوْمِهِ لِيُبَيِّنَ لَهُمْ ۖ فَيُضِلُّ اللَّهُ مَنْ يَشَاءُ وَيَهْدِي مَنْ يَشَاءُ ۚ وَهُوَ الْعَزِيزُ الْحَكِيمُ', 14, 4, 13, 51, 255, 'ما هیچ پیامبری را، جز به زبان قومش، نفرستادیم؛ تا (حقایق را) برای آنها آشکار سازد؛ سپس خدا هر کس را بخواهد (و مستحق بداند) گمراه، و هر کس را بخواهد (و شایسته بداند) هدایت می‌کند؛ و او توانا و حکیم است.'),
(1755, 1755, 'وَلَقَدْ أَرْسَلْنَا مُوسَىٰ بِآيَاتِنَا أَنْ أَخْرِجْ قَوْمَكَ مِنَ الظُّلُمَاتِ إِلَى النُّورِ وَذَكِّرْهُمْ بِأَيَّامِ اللَّهِ ۚ إِنَّ فِي ذَٰلِكَ لَآيَاتٍ لِكُلِّ صَبَّارٍ شَكُورٍ', 14, 5, 13, 51, 255, 'ما موسی را با آیات خود فرستادیم؛ (و دستور دادیم:) قومت را از ظلمات به نور بیرون آر! و «ایّام اللّه» را به آنان یاد آور! در این، نشانه‌هایی است برای هر صبر کننده شکرگزار!'),
(1756, 1756, 'وَإِذْ قَالَ مُوسَىٰ لِقَوْمِهِ اذْكُرُوا نِعْمَةَ اللَّهِ عَلَيْكُمْ إِذْ أَنْجَاكُمْ مِنْ آلِ فِرْعَوْنَ يَسُومُونَكُمْ سُوءَ الْعَذَابِ وَيُذَبِّحُونَ أَبْنَاءَكُمْ وَيَسْتَحْيُونَ نِسَاءَكُمْ ۚ وَفِي ذَٰلِكُمْ بَلَاءٌ مِنْ رَبِّكُمْ عَظِيمٌ', 14, 6, 13, 51, 256, 'و (به خاطر بیاور) هنگامی را که موسی به قومش گفت: «نعمت خدا را بر خود به یاد داشته باشید، زمانی که شما را از (چنگال) آل فرعون رهایی بخشید! همانها که شما را به بدترین وجهی عذاب می‌کردند؛ پسرانتان را سر می‌بریدند، و زنانتان را (برای خدمتگاری) زنده می‌گذاشتند؛ و در این، آزمایش بزرگی از طرف پروردگارتان برای شما بود!»'),
(1757, 1757, 'وَإِذْ تَأَذَّنَ رَبُّكُمْ لَئِنْ شَكَرْتُمْ لَأَزِيدَنَّكُمْ ۖ وَلَئِنْ كَفَرْتُمْ إِنَّ عَذَابِي لَشَدِيدٌ', 14, 7, 13, 51, 256, 'و (همچنین به خاطر بیاورید) هنگامی را که پروردگارتان اعلام داشت: «اگر شکرگزاری کنید، (نعمت خود را) بر شما خواهم افزود؛ و اگر ناسپاسی کنید، مجازاتم شدید است!»'),
(1758, 1758, 'وَقَالَ مُوسَىٰ إِنْ تَكْفُرُوا أَنْتُمْ وَمَنْ فِي الْأَرْضِ جَمِيعًا فَإِنَّ اللَّهَ لَغَنِيٌّ حَمِيدٌ', 14, 8, 13, 51, 256, 'و موسی (به بنی اسرائیل) گفت: «اگر شما و همه مردم روی زمین کافر شوید، (به خدا زیانی نمی‌رسد؛ چرا که) خداوند، بی‌نیاز و شایسته ستایش است!»'),
(1759, 1759, 'أَلَمْ يَأْتِكُمْ نَبَأُ الَّذِينَ مِنْ قَبْلِكُمْ قَوْمِ نُوحٍ وَعَادٍ وَثَمُودَ ۛ وَالَّذِينَ مِنْ بَعْدِهِمْ ۛ لَا يَعْلَمُهُمْ إِلَّا اللَّهُ ۚ جَاءَتْهُمْ رُسُلُهُمْ بِالْبَيِّنَاتِ فَرَدُّوا أَيْدِيَهُمْ فِي أَفْوَاهِهِمْ وَقَالُوا إِنَّا كَفَرْنَا بِمَا أُرْسِلْتُمْ بِهِ وَإِنَّا لَفِي شَكٍّ مِمَّا تَدْعُونَنَا إِلَيْهِ مُرِيبٍ', 14, 9, 13, 51, 256, 'آیا خبر کسانی که پیش از شما بودند، به شما نرسید؟! «قوم نوح» و «عاد» و «ثمود» و آنها که پس از ایشان بودند؛ همانها که جز خداوند از آنان آگاه نیست؛ پیامبرانشان دلایل روشن برای آنان آوردند، ولی آنها (از روی تعجّب و استهزا) دست بر دهان گرفتند و گفتند: «ما به آنچه شما به آن فرستاده شده‌اید، کافریم! و نسبت به آنچه ما را به سوی آن می‌خوانید، شکّ و تردید داریم!»'),
(1760, 1760, ' قَالَتْ رُسُلُهُمْ أَفِي اللَّهِ شَكٌّ فَاطِرِ السَّمَاوَاتِ وَالْأَرْضِ ۖ يَدْعُوكُمْ لِيَغْفِرَ لَكُمْ مِنْ ذُنُوبِكُمْ وَيُؤَخِّرَكُمْ إِلَىٰ أَجَلٍ مُسَمًّى ۚ قَالُوا إِنْ أَنْتُمْ إِلَّا بَشَرٌ مِثْلُنَا تُرِيدُونَ أَنْ تَصُدُّونَا عَمَّا كَانَ يَعْبُدُ آبَاؤُنَا فَأْتُونَا بِسُلْطَانٍ مُبِينٍ', 14, 10, 13, 52, 256, 'رسولان آنها گفتند: «آیا در خدا شکّ است؟! خدایی که آسمانها و زمین را آفریده؛ او شما را دعوت می‌کند تا گناهانتان را ببخشد، و تا موعد مقرّری شما را باقی گذارد!» آنها گفتند: «(ما اینها را نمی‌فهمیم! همین اندازه می‌دانیم که) شما انسانهایی همانند ما هستید، می‌خواهید ما را از آنچه پدرانمان می‌پرستیدند بازدارید؛ شما دلیل و معجزه روشنی برای ما بیاورید!»'),
(1761, 1761, 'قَالَتْ لَهُمْ رُسُلُهُمْ إِنْ نَحْنُ إِلَّا بَشَرٌ مِثْلُكُمْ وَلَٰكِنَّ اللَّهَ يَمُنُّ عَلَىٰ مَنْ يَشَاءُ مِنْ عِبَادِهِ ۖ وَمَا كَانَ لَنَا أَنْ نَأْتِيَكُمْ بِسُلْطَانٍ إِلَّا بِإِذْنِ اللَّهِ ۚ وَعَلَى اللَّهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُونَ', 14, 11, 13, 52, 257, 'پیامبرانشان به آنها گفتند: «درست است که ما بشری همانند شما هستیم، ولی خداوند بر هر کس از بندگانش بخواهد (و شایسته بداند)، نعمت می‌بخشد (و مقام رسالت عطا می‌کند)! و ما هرگز نمی‌توانیم معجزه‌ای جز بفرمان خدا بیاوریم! (و از تهدیدهای شما نمی‌هراسیم؛) افراد باایمان باید تنها بر خدا توکّل کنند!'),
(1762, 1762, 'وَمَا لَنَا أَلَّا نَتَوَكَّلَ عَلَى اللَّهِ وَقَدْ هَدَانَا سُبُلَنَا ۚ وَلَنَصْبِرَنَّ عَلَىٰ مَا آذَيْتُمُونَا ۚ وَعَلَى اللَّهِ فَلْيَتَوَكَّلِ الْمُتَوَكِّلُونَ', 14, 12, 13, 52, 257, 'و چرا بر خدا توکّل نکنیم، با اینکه ما را به راه‌های (سعادت) رهبری کرده است؟! و ما بطور مسلّم در برابر آزارهای شما صبر خواهیم کرد (و دست از رسالت خویش بر نمی‌داریم)! و توکّل‌کنندگان، باید فقط بر خدا توکّل کنند!»'),
(1763, 1763, 'وَقَالَ الَّذِينَ كَفَرُوا لِرُسُلِهِمْ لَنُخْرِجَنَّكُمْ مِنْ أَرْضِنَا أَوْ لَتَعُودُنَّ فِي مِلَّتِنَا ۖ فَأَوْحَىٰ إِلَيْهِمْ رَبُّهُمْ لَنُهْلِكَنَّ الظَّالِمِينَ', 14, 13, 13, 52, 257, '(ولی) کافران به پیامبران خود گفتند: «ما قطعاً شما را از سرزمین خود بیرون خواهیم کرد، مگر اینکه به آیین ما بازگردید!» در این حال، پروردگارشان به آنها وحی فرستاد که: «ما ظالمان را هلاک می‌کنیم!'),
(1764, 1764, 'وَلَنُسْكِنَنَّكُمُ الْأَرْضَ مِنْ بَعْدِهِمْ ۚ ذَٰلِكَ لِمَنْ خَافَ مَقَامِي وَخَافَ وَعِيدِ', 14, 14, 13, 52, 257, 'و شما را بعد از آنان در زمین سکوت خواهیم داد، این (موفقیّت)، برای کسی است که از مقام (عدالت) من بترسد؛ و از عذاب (من) بیمناک باشد!»'),
(1765, 1765, 'وَاسْتَفْتَحُوا وَخَابَ كُلُّ جَبَّارٍ عَنِيدٍ', 14, 15, 13, 52, 257, 'و آنها (از خدا) تقاضای فتح و پیروزی (بر کفار) کردند؛ و (سرانجام) هر گردنکش منحرفی نومید و نابود شد!'),
(1766, 1766, 'مِنْ وَرَائِهِ جَهَنَّمُ وَيُسْقَىٰ مِنْ مَاءٍ صَدِيدٍ', 14, 16, 13, 52, 257, 'به دنبال او جهنم خواهد بود؛ و از آب بد بوی متعفّنی نوشانده می‌شود!'),
(1767, 1767, 'يَتَجَرَّعُهُ وَلَا يَكَادُ يُسِيغُهُ وَيَأْتِيهِ الْمَوْتُ مِنْ كُلِّ مَكَانٍ وَمَا هُوَ بِمَيِّتٍ ۖ وَمِنْ وَرَائِهِ عَذَابٌ غَلِيظٌ', 14, 17, 13, 52, 257, 'بزحمت جرعه جرعه آن را سرمی‌کشد؛ و هرگز حاضر نیست به میل خود آن را بیاشامد؛ و مرگ از هرجا به سراغ او می‌آید؛ ولی با این همه نمی‌میرد! و بدنبال آن، عذاب شدیدی است!'),
(1768, 1768, 'مَثَلُ الَّذِينَ كَفَرُوا بِرَبِّهِمْ ۖ أَعْمَالُهُمْ كَرَمَادٍ اشْتَدَّتْ بِهِ الرِّيحُ فِي يَوْمٍ عَاصِفٍ ۖ لَا يَقْدِرُونَ مِمَّا كَسَبُوا عَلَىٰ شَيْءٍ ۚ ذَٰلِكَ هُوَ الضَّلَالُ الْبَعِيدُ', 14, 18, 13, 52, 257, 'اعمال کسانی که به پروردگارشان کافر شدند، همچون خاکستری است در برابر تندباد در یک روز طوفانی! آنها توانایی ندارند کمترین چیزی از آنچه را انجام داده‌اند، به دست آورند؛ و این همان گمراهی دور و دراز است!'),
(1769, 1769, 'أَلَمْ تَرَ أَنَّ اللَّهَ خَلَقَ السَّمَاوَاتِ وَالْأَرْضَ بِالْحَقِّ ۚ إِنْ يَشَأْ يُذْهِبْكُمْ وَيَأْتِ بِخَلْقٍ جَدِيدٍ', 14, 19, 13, 52, 258, 'آیا ندیدی خداوند، آسمانها و زمین را بحق آفریده است؟! اگر بخواهد، شما را می‌برد و خلق تازه‌ای می‌آورد!'),
(1770, 1770, 'وَمَا ذَٰلِكَ عَلَى اللَّهِ بِعَزِيزٍ', 14, 20, 13, 52, 258, 'و این کار برای خدا مشکل نیست!'),
(1771, 1771, 'وَبَرَزُوا لِلَّهِ جَمِيعًا فَقَالَ الضُّعَفَاءُ لِلَّذِينَ اسْتَكْبَرُوا إِنَّا كُنَّا لَكُمْ تَبَعًا فَهَلْ أَنْتُمْ مُغْنُونَ عَنَّا مِنْ عَذَابِ اللَّهِ مِنْ شَيْءٍ ۚ قَالُوا لَوْ هَدَانَا اللَّهُ لَهَدَيْنَاكُمْ ۖ سَوَاءٌ عَلَيْنَا أَجَزِعْنَا أَمْ صَبَرْنَا مَا لَنَا مِنْ مَحِيصٍ', 14, 21, 13, 52, 258, 'و (در قیامت)، همه آنها در برابر خدا ظاهر می‌شوند؛ در این هنگام، ضعفا = دنباله‌روان نادان‌ به مستکبران (و رهبران گمراه) می‌گویند: «ما پیروان شما بودیم! آیا (اکنون که بخاطر پیروی از شما گرفتار مجازات الهی شده‌ایم،) شما حاضرید سهمی از عذاب الهی را بپذیرید و از ما بردارید؟» آنها می‌گویند: «اگر خدا ما را هدایت کرده بود، ما نیز شما را هدایت می‌کردیم! (ولی کار از اینها گذشته است،) چه بیتابی کنیم و چه شکیبایی، تفاوتی برای ما ندارد؛ راه گریزی برای ما نیست!»'),
(1772, 1772, 'وَقَالَ الشَّيْطَانُ لَمَّا قُضِيَ الْأَمْرُ إِنَّ اللَّهَ وَعَدَكُمْ وَعْدَ الْحَقِّ وَوَعَدْتُكُمْ فَأَخْلَفْتُكُمْ ۖ وَمَا كَانَ لِيَ عَلَيْكُمْ مِنْ سُلْطَانٍ إِلَّا أَنْ دَعَوْتُكُمْ فَاسْتَجَبْتُمْ لِي ۖ فَلَا تَلُومُونِي وَلُومُوا أَنْفُسَكُمْ ۖ مَا أَنَا بِمُصْرِخِكُمْ وَمَا أَنْتُمْ بِمُصْرِخِيَّ ۖ إِنِّي كَفَرْتُ بِمَا أَشْرَكْتُمُونِ مِنْ قَبْلُ ۗ إِنَّ الظَّالِمِينَ لَهُمْ عَذَابٌ أَلِيمٌ', 14, 22, 13, 52, 258, 'و شیطان، هنگامی که کار تمام می‌شود، می‌گوید: «خداوند به شما وعده حق داد؛ و من به شما وعده (باطل) دادم، و تخلّف کردم! من بر شما تسلّطی نداشتم، جز اینکه دعوتتان کردم و شما دعوت مرا پذیرفتید! بنابر این، مرا سرزنش نکنید؛ خود را سرزنش کنید! نه من فریادرس شما هستم، و نه شما فریادرس من! من نسبت به شرک شما درباره خود، که از قبل داشتید، (و اطاعت مرا همردیف اطاعت خدا قرار دادید) بیزار و کافرم!» مسلّماً ستمکاران عذاب دردناکی دارند!'),
(1773, 1773, 'وَأُدْخِلَ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهَارُ خَالِدِينَ فِيهَا بِإِذْنِ رَبِّهِمْ ۖ تَحِيَّتُهُمْ فِيهَا سَلَامٌ', 14, 23, 13, 52, 258, 'و کسانی را که ایمان آوردند و اعمال صالح انجام دادند، به باغهای بهشت وارد می‌کنند؛ باغهایی که نهرها از زیر درختانش جاری است؛ به اذن پروردگارشان، جاودانه در آن می‌مانند؛ و تحیّت آنها در آن، «سلام» است.'),
(1774, 1774, 'أَلَمْ تَرَ كَيْفَ ضَرَبَ اللَّهُ مَثَلًا كَلِمَةً طَيِّبَةً كَشَجَرَةٍ طَيِّبَةٍ أَصْلُهَا ثَابِتٌ وَفَرْعُهَا فِي السَّمَاءِ', 14, 24, 13, 52, 258, 'آیا ندیدی چگونه خداوند «کلمه طیبه» (و گفتار پاکیزه) را به درخت پاکیزه‌ای تشبیه کرده که ریشه آن (در زمین) ثابت، و شاخه آن در آسمان است؟!'),
(1775, 1775, 'تُؤْتِي أُكُلَهَا كُلَّ حِينٍ بِإِذْنِ رَبِّهَا ۗ وَيَضْرِبُ اللَّهُ الْأَمْثَالَ لِلنَّاسِ لَعَلَّهُمْ يَتَذَكَّرُونَ', 14, 25, 13, 52, 259, 'هر زمان میوه خود را به اذن پروردگارش می‌دهد. و خداوند برای مردم مثلها می‌زند، شاید متذکّر شوند (و پند گیرند)!'),
(1776, 1776, 'وَمَثَلُ كَلِمَةٍ خَبِيثَةٍ كَشَجَرَةٍ خَبِيثَةٍ اجْتُثَّتْ مِنْ فَوْقِ الْأَرْضِ مَا لَهَا مِنْ قَرَارٍ', 14, 26, 13, 52, 259, '(همچنین) «کلمه خبیثه» (و سخن آلوده) را به درخت ناپاکی تشبیه کرده که از روی زمین برکنده شده، و قرار و ثباتی ندارد.'),
(1777, 1777, 'يُثَبِّتُ اللَّهُ الَّذِينَ آمَنُوا بِالْقَوْلِ الثَّابِتِ فِي الْحَيَاةِ الدُّنْيَا وَفِي الْآخِرَةِ ۖ وَيُضِلُّ اللَّهُ الظَّالِمِينَ ۚ وَيَفْعَلُ اللَّهُ مَا يَشَاءُ', 14, 27, 13, 52, 259, 'خداوند کسانی را که ایمان آوردند، به خاطر گفتار و اعتقاد ثابتشان، استوار می‌دارد؛ هم در این جهان، و هم در سرای دیگر! و ستمگران را گمراه می‌سازد، (و لطف خود را از آنها برمی‌گیرد)؛ خداوند هر کار را بخواهد (و مصلحت بداند) انجام می‌دهد!'),
(1778, 1778, ' أَلَمْ تَرَ إِلَى الَّذِينَ بَدَّلُوا نِعْمَتَ اللَّهِ كُفْرًا وَأَحَلُّوا قَوْمَهُمْ دَارَ الْبَوَارِ', 14, 28, 13, 52, 259, 'آیا ندیدی کسانی را که نعمت خدا را به کفران تبدیل کردند، و قوم خود را به سرای نیستی و نابودی کشاندند؟!'),
(1779, 1779, 'جَهَنَّمَ يَصْلَوْنَهَا ۖ وَبِئْسَ الْقَرَارُ', 14, 29, 13, 52, 259, '(سرای نیستی و نابودی، همان) جهنم است که آنها در آتش آن وارد می‌شوند؛ و بد قرارگاهی است!'),
(1780, 1780, 'وَجَعَلُوا لِلَّهِ أَنْدَادًا لِيُضِلُّوا عَنْ سَبِيلِهِ ۗ قُلْ تَمَتَّعُوا فَإِنَّ مَصِيرَكُمْ إِلَى النَّارِ', 14, 30, 13, 52, 259, 'آنها برای خدا همتایانی قرار داده‌اند، تا (مردم را) از راه او (منحرف و) گمراه سازند؛ بگو: «(چند روزی از زندگی دنیا و لذّات آن) بهره گیرید؛ امّا عاقبت کار شما به سوی آتش (دوزخ) است!»'),
(1781, 1781, 'قُلْ لِعِبَادِيَ الَّذِينَ آمَنُوا يُقِيمُوا الصَّلَاةَ وَيُنْفِقُوا مِمَّا رَزَقْنَاهُمْ سِرًّا وَعَلَانِيَةً مِنْ قَبْلِ أَنْ يَأْتِيَ يَوْمٌ لَا بَيْعٌ فِيهِ وَلَا خِلَالٌ', 14, 31, 13, 52, 259, 'به بندگان من که ایمان آورده‌اند بگو نماز را برپا دارند؛ و از آنچه به آنها روزی داده‌ایم، پنهان و آشکار، انفاق کنند؛ پیش از آنکه روزی فرا رسد که نه در آن خرید و فروش است، و نه دوستی! (نه با مال می‌توانند از کیفر خدا رهایی یابند، و نه با پیوندهای مادی!)'),
(1782, 1782, 'اللَّهُ الَّذِي خَلَقَ السَّمَاوَاتِ وَالْأَرْضَ وَأَنْزَلَ مِنَ السَّمَاءِ مَاءً فَأَخْرَجَ بِهِ مِنَ الثَّمَرَاتِ رِزْقًا لَكُمْ ۖ وَسَخَّرَ لَكُمُ الْفُلْكَ لِتَجْرِيَ فِي الْبَحْرِ بِأَمْرِهِ ۖ وَسَخَّرَ لَكُمُ الْأَنْهَارَ', 14, 32, 13, 52, 259, 'خداوند همان کسی است که آسمانها و زمین را آفرید؛ و از آسمان، آبی نازل کرد؛ و با آن، میوه‌ها (ی مختلف) را برای روزی شما (از زمین) بیرون آورد؛ و کشتی‌ها را مسخّر شما گردانید، تا بر صفحه دریا به فرمان او حرکت کنند؛ و نهرها را (نیز) مسخّر شما نمود؛'),
(1783, 1783, 'وَسَخَّرَ لَكُمُ الشَّمْسَ وَالْقَمَرَ دَائِبَيْنِ ۖ وَسَخَّرَ لَكُمُ اللَّيْلَ وَالنَّهَارَ', 14, 33, 13, 52, 259, 'و خورشید و ماه را -که با برنامه منظّمی درکارند- به تسخیر شما درآورد؛ و شب و روز را (نیز) مسخّر شما ساخت؛'),
(1784, 1784, 'وَآتَاكُمْ مِنْ كُلِّ مَا سَأَلْتُمُوهُ ۚ وَإِنْ تَعُدُّوا نِعْمَتَ اللَّهِ لَا تُحْصُوهَا ۗ إِنَّ الْإِنْسَانَ لَظَلُومٌ كَفَّارٌ', 14, 34, 13, 52, 260, 'و از هر چیزی که از او خواستید، به شما داد؛ و اگر نعمتهای خدا را بشمارید، هرگز آنها را شماره نتوانید کرد! انسان، ستمگر و ناسپاس است!'),
(1785, 1785, 'وَإِذْ قَالَ إِبْرَاهِيمُ رَبِّ اجْعَلْ هَٰذَا الْبَلَدَ آمِنًا وَاجْنُبْنِي وَبَنِيَّ أَنْ نَعْبُدَ الْأَصْنَامَ', 14, 35, 13, 52, 260, '(به یاد آورید) زمانی را که ابراهیم گفت: «پروردگارا! این شهر = مکّه‌ را شهر امنی قرار ده! و من و فرزندانم را از پرستش بتها دور نگاه دار!'),
(1786, 1786, 'رَبِّ إِنَّهُنَّ أَضْلَلْنَ كَثِيرًا مِنَ النَّاسِ ۖ فَمَنْ تَبِعَنِي فَإِنَّهُ مِنِّي ۖ وَمَنْ عَصَانِي فَإِنَّكَ غَفُورٌ رَحِيمٌ', 14, 36, 13, 52, 260, 'پروردگارا! آنها = بتها بسیاری از مردم را گمراه ساختند! هر کس از من پیروی کند از من است؛ و هر کس نافرمانی من کند، تو بخشنده و مهربانی!'),
(1787, 1787, 'رَبَّنَا إِنِّي أَسْكَنْتُ مِنْ ذُرِّيَّتِي بِوَادٍ غَيْرِ ذِي زَرْعٍ عِنْدَ بَيْتِكَ الْمُحَرَّمِ رَبَّنَا لِيُقِيمُوا الصَّلَاةَ فَاجْعَلْ أَفْئِدَةً مِنَ النَّاسِ تَهْوِي إِلَيْهِمْ وَارْزُقْهُمْ مِنَ الثَّمَرَاتِ لَعَلَّهُمْ يَشْكُرُونَ', 14, 37, 13, 52, 260, 'پروردگارا! من بعضی از فرزندانم را در سرزمین بی‌آب و علفی، در کنار خانه‌ای که حرم توست، ساکن ساختم تا نماز را برپا دارند؛ تو دلهای گروهی از مردم را متوجّه آنها ساز؛ و از ثمرات به آنها روزی ده؛ شاید آنان شکر تو را بجای آورند!'),
(1788, 1788, 'رَبَّنَا إِنَّكَ تَعْلَمُ مَا نُخْفِي وَمَا نُعْلِنُ ۗ وَمَا يَخْفَىٰ عَلَى اللَّهِ مِنْ شَيْءٍ فِي الْأَرْضِ وَلَا فِي السَّمَاءِ', 14, 38, 13, 52, 260, 'پروردگارا! تو می‌دانی آنچه را ما پنهان و یا آشکار می‌کنیم؛ و چیزی در زمین و آسمان بر خدا پنهان نیست!'),
(1789, 1789, 'الْحَمْدُ لِلَّهِ الَّذِي وَهَبَ لِي عَلَى الْكِبَرِ إِسْمَاعِيلَ وَإِسْحَاقَ ۚ إِنَّ رَبِّي لَسَمِيعُ الدُّعَاءِ', 14, 39, 13, 52, 260, 'حمد خدای را که در پیری، اسماعیل و اسحاق را به من بخشید؛ مسلّماً پروردگار من، شنونده (و اجابت کننده) دعاست.'),
(1790, 1790, 'رَبِّ اجْعَلْنِي مُقِيمَ الصَّلَاةِ وَمِنْ ذُرِّيَّتِي ۚ رَبَّنَا وَتَقَبَّلْ دُعَاءِ', 14, 40, 13, 52, 260, 'پروردگارا: مرا برپا کننده نماز قرار ده، و از فرزندانم (نیز چنین فرما)، پروردگارا: دعای مرا بپذیر!'),
(1791, 1791, 'رَبَّنَا اغْفِرْ لِي وَلِوَالِدَيَّ وَلِلْمُؤْمِنِينَ يَوْمَ يَقُومُ الْحِسَابُ', 14, 41, 13, 52, 260, 'پروردگارا! من و پدر و مادرم و همه مؤمنان را، در آن روز که حساب برپا می‌شود، بیامرز!'),
(1792, 1792, 'وَلَا تَحْسَبَنَّ اللَّهَ غَافِلًا عَمَّا يَعْمَلُ الظَّالِمُونَ ۚ إِنَّمَا يُؤَخِّرُهُمْ لِيَوْمٍ تَشْخَصُ فِيهِ الْأَبْصَارُ', 14, 42, 13, 52, 260, 'گمان مبر که خدا، از آنچه ظالمان انجام می‌دهند، غافل است! (نه، بلکه کیفر) آنها را برای روزی تأخیر انداخته است که چشمها در آن (به خاطر ترس و وحشت) از حرکت بازمی‌ایستد...'),
(1793, 1793, 'مُهْطِعِينَ مُقْنِعِي رُءُوسِهِمْ لَا يَرْتَدُّ إِلَيْهِمْ طَرْفُهُمْ ۖ وَأَفْئِدَتُهُمْ هَوَاءٌ', 14, 43, 13, 52, 261, 'گردنها را کشیده، سرها را به آسمان بلندکرده، حتّی پلک چشمهایشان از حرکت بازمی‌ماند؛ (زیرا به هر طرف نگاه کنند، آثار عذاب آشکار است!) و (در این حال) دلهایشان (فرومی‌ریزد؛ و از اندیشه و امید،) خالی می‌گردد!'),
(1794, 1794, 'وَأَنْذِرِ النَّاسَ يَوْمَ يَأْتِيهِمُ الْعَذَابُ فَيَقُولُ الَّذِينَ ظَلَمُوا رَبَّنَا أَخِّرْنَا إِلَىٰ أَجَلٍ قَرِيبٍ نُجِبْ دَعْوَتَكَ وَنَتَّبِعِ الرُّسُلَ ۗ أَوَلَمْ تَكُونُوا أَقْسَمْتُمْ مِنْ قَبْلُ مَا لَكُمْ مِنْ زَوَالٍ', 14, 44, 13, 52, 261, 'و مردم را از روزی که عذاب الهی به سراغشان می‌آید، بترسان! آن روز که ظالمان می‌گویند: «پروردگارا! مدّت کوتاهی ما را مهلت ده، تا دعوت تو را بپذیریم و از پیامبران پیروی کنیم!» (امّا پاسخ می‌شنوند که:) مگر قبلاً سوگند یاد نکرده بودید که زوال و فنایی برای شما نیست؟!'),
(1795, 1795, 'وَسَكَنْتُمْ فِي مَسَاكِنِ الَّذِينَ ظَلَمُوا أَنْفُسَهُمْ وَتَبَيَّنَ لَكُمْ كَيْفَ فَعَلْنَا بِهِمْ وَضَرَبْنَا لَكُمُ الْأَمْثَالَ', 14, 45, 13, 52, 261, '(آری شما بودید که) در منازل (و کاخهای) کسانی که به خویشتن ستم کردند، ساکن شدید؛ و برای شما آشکار شد چگونه با آنان رفتار کردیم؛ و برای شما، مثلها (از سرگذشت پیشینیان) زدیم (باز هم بیدار نشدید)!'),
(1796, 1796, 'وَقَدْ مَكَرُوا مَكْرَهُمْ وَعِنْدَ اللَّهِ مَكْرُهُمْ وَإِنْ كَانَ مَكْرُهُمْ لِتَزُولَ مِنْهُ الْجِبَالُ', 14, 46, 13, 52, 261, 'آنها نهایت مکر (و نیرنگ) خود را به کار زدند؛ و همه مکرها (و توطئه‌هایشان) نزد خدا آشکار است، هر چند مکرشان چنان باشد که کوه‌ها را از جا برکند!'),
(1797, 1797, 'فَلَا تَحْسَبَنَّ اللَّهَ مُخْلِفَ وَعْدِهِ رُسُلَهُ ۗ إِنَّ اللَّهَ عَزِيزٌ ذُو انْتِقَامٍ', 14, 47, 13, 52, 261, 'پس گمان مبر که خدا وعده‌ای را که به پیامبرانش داده، تخلّف کند! چرا که خداوند قادر و انتقام گیرنده است.'),
(1798, 1798, 'يَوْمَ تُبَدَّلُ الْأَرْضُ غَيْرَ الْأَرْضِ وَالسَّمَاوَاتُ ۖ وَبَرَزُوا لِلَّهِ الْوَاحِدِ الْقَهَّارِ', 14, 48, 13, 52, 261, 'در آن روز که این زمین به زمین دیگر، و آسمانها (به آسمانهای دیگری) مبدل می‌شود، و آنان در پیشگاه خداوند واحد قهار ظاهر می‌گردند!'),
(1799, 1799, 'وَتَرَى الْمُجْرِمِينَ يَوْمَئِذٍ مُقَرَّنِينَ فِي الْأَصْفَادِ', 14, 49, 13, 52, 261, 'و در آن روز، مجرمان را با هم در غل و زنجیر می‌بینی! (که دستها و گردنهایشان را به هم بسته است!)'),
(1800, 1800, 'سَرَابِيلُهُمْ مِنْ قَطِرَانٍ وَتَغْشَىٰ وُجُوهَهُمُ النَّارُ', 14, 50, 13, 52, 261, 'لباسهایشان از قطران = ماده چسبنده بد بوی قابل اشتعال‌ است؛ و صورتهایشان را آتش می‌پوشاند...'),
(1801, 1801, 'لِيَجْزِيَ اللَّهُ كُلَّ نَفْسٍ مَا كَسَبَتْ ۚ إِنَّ اللَّهَ سَرِيعُ الْحِسَابِ', 14, 51, 13, 52, 261, 'تا خداوند هر کس را، هر آنچه انجام داده، جزا دهد! به یقین، خداوند سریع الحساب است!'),
(1802, 1802, 'هَٰذَا بَلَاغٌ لِلنَّاسِ وَلِيُنْذَرُوا بِهِ وَلِيَعْلَمُوا أَنَّمَا هُوَ إِلَٰهٌ وَاحِدٌ وَلِيَذَّكَّرَ أُولُو الْأَلْبَابِ', 14, 52, 13, 52, 261, 'این (قرآن،) پیام (و ابلاغی) برای (عموم) مردم است؛ تا همه به وسیله آن انذار شوند، و بدانند او خدا یکتاست؛ و تا صاحبان مغز (و اندیشه) پند گیرند!'),
(1803, 1803, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ الر ۚ تِلْكَ آيَاتُ الْكِتَابِ وَقُرْآنٍ مُبِينٍ', 15, 1, 14, 53, 262, 'الر، این آیات کتاب، و قرآن مبین است.'),
(1804, 1804, 'رُبَمَا يَوَدُّ الَّذِينَ كَفَرُوا لَوْ كَانُوا مُسْلِمِينَ', 15, 2, 14, 53, 262, 'کافران (هنگامی که آثار شوم اعمال خود را ببینند،) چه بسا آرزو می‌کنند که ای کاش مسلمان بودند!'),
(1805, 1805, 'ذَرْهُمْ يَأْكُلُوا وَيَتَمَتَّعُوا وَيُلْهِهِمُ الْأَمَلُ ۖ فَسَوْفَ يَعْلَمُونَ', 15, 3, 14, 53, 262, 'بگذار آنها بخورند، و بهره گیرند، و آرزوها آنان را غافل سازد؛ ولی بزودی خواهند فهمید!'),
(1806, 1806, 'وَمَا أَهْلَكْنَا مِنْ قَرْيَةٍ إِلَّا وَلَهَا كِتَابٌ مَعْلُومٌ', 15, 4, 14, 53, 262, 'ما اهل هیچ شهر و دیاری را هلاک نکردیم مگر اینکه اجل معیّن (و زمان تغییر ناپذیری) داشتند!'),
(1807, 1807, 'مَا تَسْبِقُ مِنْ أُمَّةٍ أَجَلَهَا وَمَا يَسْتَأْخِرُونَ', 15, 5, 14, 53, 262, 'هیچ گروهی از اجل خود پیشی نمی‌گیرد؛ و از آن عقب نخواهد افتاد!'),
(1808, 1808, 'وَقَالُوا يَا أَيُّهَا الَّذِي نُزِّلَ عَلَيْهِ الذِّكْرُ إِنَّكَ لَمَجْنُونٌ', 15, 6, 14, 53, 262, 'و گفتند: «ای کسی که «ذکر» = قرآن‌ بر او نازل شده، مسلماً تو دیوانه‌ای!'),
(1809, 1809, 'لَوْ مَا تَأْتِينَا بِالْمَلَائِكَةِ إِنْ كُنْتَ مِنَ الصَّادِقِينَ', 15, 7, 14, 53, 262, 'اگر راست می‌گویی، چرا فرشتگان را نزد ما نمی‌زوری؟!»'),
(1810, 1810, 'مَا نُنَزِّلُ الْمَلَائِكَةَ إِلَّا بِالْحَقِّ وَمَا كَانُوا إِذًا مُنْظَرِينَ', 15, 8, 14, 53, 262, '(امّا اینها باید بدانند) ما فرشتگان را، جز بحق، نازل نمی‌کنیم،؛ و هرگاه نازل شوند، دیگر به اینها مهلت داده نمی‌شود (؛ و در صورت انکار، به عذاب الهی نابود می‌گردند)!'),
(1811, 1811, 'إِنَّا نَحْنُ نَزَّلْنَا الذِّكْرَ وَإِنَّا لَهُ لَحَافِظُونَ', 15, 9, 14, 53, 262, 'ما قرآن را نازل کردیم؛ و ما بطور قطع نگهدار آنیم!'),
(1812, 1812, 'وَلَقَدْ أَرْسَلْنَا مِنْ قَبْلِكَ فِي شِيَعِ الْأَوَّلِينَ', 15, 10, 14, 53, 262, 'ما پیش از تو (نیز) پیامبرانی در میان امّتهای نخستین فرستادیم.'),
(1813, 1813, 'وَمَا يَأْتِيهِمْ مِنْ رَسُولٍ إِلَّا كَانُوا بِهِ يَسْتَهْزِئُونَ', 15, 11, 14, 53, 262, 'هیچ پیامبری به سراغ آنها نمی‌آمد مگر اینکه او را مسخره می‌کردند.'),
(1814, 1814, 'كَذَٰلِكَ نَسْلُكُهُ فِي قُلُوبِ الْمُجْرِمِينَ', 15, 12, 14, 53, 262, 'ما اینچنین (و از هر طریق ممکن) قرآن را به درون دلهای مجرمان راه می‌دهیم!'),
(1815, 1815, 'لَا يُؤْمِنُونَ بِهِ ۖ وَقَدْ خَلَتْ سُنَّةُ الْأَوَّلِينَ', 15, 13, 14, 53, 262, '(اما با این حال،) آنها به آن ایمان نمی‌آورند؛ روش اقوام پیشین نیز چنین بود!'),
(1816, 1816, 'وَلَوْ فَتَحْنَا عَلَيْهِمْ بَابًا مِنَ السَّمَاءِ فَظَلُّوا فِيهِ يَعْرُجُونَ', 15, 14, 14, 53, 262, 'و اگر دری از آسمان به روی آنان بگشاییم، و آنها پیوسته در آن بالا روند...'),
(1817, 1817, 'لَقَالُوا إِنَّمَا سُكِّرَتْ أَبْصَارُنَا بَلْ نَحْنُ قَوْمٌ مَسْحُورُونَ', 15, 15, 14, 53, 262, 'باز می‌گویند: «ما را چشم‌بندی کرده‌اند؛ بلکه ما (سر تا پا) سحر شده‌ایم!»'),
(1818, 1818, 'وَلَقَدْ جَعَلْنَا فِي السَّمَاءِ بُرُوجًا وَزَيَّنَّاهَا لِلنَّاظِرِينَ', 15, 16, 14, 53, 263, 'ما در آسمان برجهایی قرار دادیم؛ و آن را برای بینندگان آراستیم.'),
(1819, 1819, 'وَحَفِظْنَاهَا مِنْ كُلِّ شَيْطَانٍ رَجِيمٍ', 15, 17, 14, 53, 263, 'و آن را از هر شیطان رانده شده‌ای حفظ کردیم؛'),
(1820, 1820, 'إِلَّا مَنِ اسْتَرَقَ السَّمْعَ فَأَتْبَعَهُ شِهَابٌ مُبِينٌ', 15, 18, 14, 53, 263, 'مگر آن کس که استراق سمع کند (و دزدانه گوش فرا دهد) که «شهاب مبین» او را تعقیب می‌کند (و می‌راند)'),
(1821, 1821, 'وَالْأَرْضَ مَدَدْنَاهَا وَأَلْقَيْنَا فِيهَا رَوَاسِيَ وَأَنْبَتْنَا فِيهَا مِنْ كُلِّ شَيْءٍ مَوْزُونٍ', 15, 19, 14, 53, 263, 'و زمین را گستردیم؛ و در آن کوه‌های ثابتی افکندیم؛ و از هر گیاه موزون، در آن رویاندیم؛'),
(1822, 1822, 'وَجَعَلْنَا لَكُمْ فِيهَا مَعَايِشَ وَمَنْ لَسْتُمْ لَهُ بِرَازِقِينَ', 15, 20, 14, 53, 263, 'و برای شما انواع وسایل زندگی در آن قرار دادیم؛ همچنین برای کسانی که شما نمی‌توانید به آنها روزی دهید!'),
(1823, 1823, 'وَإِنْ مِنْ شَيْءٍ إِلَّا عِنْدَنَا خَزَائِنُهُ وَمَا نُنَزِّلُهُ إِلَّا بِقَدَرٍ مَعْلُومٍ', 15, 21, 14, 53, 263, 'و خزائن همه چیز، تنها نزد ماست؛ ولی ما جز به اندازه معیّن آن را نازل نمی‌کنیم!'),
(1824, 1824, 'وَأَرْسَلْنَا الرِّيَاحَ لَوَاقِحَ فَأَنْزَلْنَا مِنَ السَّمَاءِ مَاءً فَأَسْقَيْنَاكُمُوهُ وَمَا أَنْتُمْ لَهُ بِخَازِنِينَ', 15, 22, 14, 53, 263, 'ما بادها را برای بارور ساختن (ابرها و گیاهان) فرستادیم؛ و از آسمان آبی نازل کردیم، و شما را با آن سیراب ساختیم؛ در حالی که شما توانایی حفظ و نگهداری آن را نداشتید!'),
(1825, 1825, 'وَإِنَّا لَنَحْنُ نُحْيِي وَنُمِيتُ وَنَحْنُ الْوَارِثُونَ', 15, 23, 14, 53, 263, 'ماییم که زنده می‌کنیم و می‌میرانیم؛ و ماییم وارث (همه جهان)!'),
(1826, 1826, 'وَلَقَدْ عَلِمْنَا الْمُسْتَقْدِمِينَ مِنْكُمْ وَلَقَدْ عَلِمْنَا الْمُسْتَأْخِرِينَ', 15, 24, 14, 53, 263, 'ما، هم پیشینیان شما را دانستیم؛ و هم متأخّران را!'),
(1827, 1827, 'وَإِنَّ رَبَّكَ هُوَ يَحْشُرُهُمْ ۚ إِنَّهُ حَكِيمٌ عَلِيمٌ', 15, 25, 14, 53, 263, 'پروردگار تو، قطعاً آنها را (در قیامت) جمع و محشور می‌کند؛ چرا که او حکیم و داناست!'),
(1828, 1828, 'وَلَقَدْ خَلَقْنَا الْإِنْسَانَ مِنْ صَلْصَالٍ مِنْ حَمَإٍ مَسْنُونٍ', 15, 26, 14, 53, 263, 'ما انسان را از گِل خشکیده‌ای (همچون سفال) که از گِل بد بوی (تیره رنگی) گرفته شده بود آفریدیم!'),
(1829, 1829, 'وَالْجَانَّ خَلَقْنَاهُ مِنْ قَبْلُ مِنْ نَارِ السَّمُومِ', 15, 27, 14, 53, 263, 'و جن را پیش از آن، از آتش گرم و سوزان خلق کردیم!'),
(1830, 1830, 'وَإِذْ قَالَ رَبُّكَ لِلْمَلَائِكَةِ إِنِّي خَالِقٌ بَشَرًا مِنْ صَلْصَالٍ مِنْ حَمَإٍ مَسْنُونٍ', 15, 28, 14, 53, 263, 'و (به خاطر بیاور) هنگامی که پروردگارت به فرشتگان گفت: «من بشری را از گل خشکیده‌ای که از گل بدبویی گرفته شده، می‌آفرینم.'),
(1831, 1831, 'فَإِذَا سَوَّيْتُهُ وَنَفَخْتُ فِيهِ مِنْ رُوحِي فَقَعُوا لَهُ سَاجِدِينَ', 15, 29, 14, 53, 263, 'هنگامی که کار آن را به پایان رساندم، و در او از روح خود (یک روح شایسته و بزرگ) دمیدم، همگی برای او سجده کنید!»'),
(1832, 1832, 'فَسَجَدَ الْمَلَائِكَةُ كُلُّهُمْ أَجْمَعُونَ', 15, 30, 14, 53, 263, 'همه فرشتگان، بی استثنا، سجده کردند...'),
(1833, 1833, 'إِلَّا إِبْلِيسَ أَبَىٰ أَنْ يَكُونَ مَعَ السَّاجِدِينَ', 15, 31, 14, 53, 263, 'جز ابلیس، که ابا کرد از اینکه با سجده‌کنندگان باشد.'),
(1834, 1834, 'قَالَ يَا إِبْلِيسُ مَا لَكَ أَلَّا تَكُونَ مَعَ السَّاجِدِينَ', 15, 32, 14, 53, 264, '(خداوند) فرمود: «ای ابلیس! چرا با سجده‌کنندگان نیستی؟!»'),
(1835, 1835, 'قَالَ لَمْ أَكُنْ لِأَسْجُدَ لِبَشَرٍ خَلَقْتَهُ مِنْ صَلْصَالٍ مِنْ حَمَإٍ مَسْنُونٍ', 15, 33, 14, 53, 264, 'گفت: «من هرگز برای بشری که او را از گل خشکیده‌ای که از گل بدبویی گرفته شده است آفریده‌ای، سجده نخواهم کرد!»'),
(1836, 1836, 'قَالَ فَاخْرُجْ مِنْهَا فَإِنَّكَ رَجِيمٌ', 15, 34, 14, 53, 264, 'فرمود: «از صف آنها = فرشتگان‌ بیرون رو، که رانده‌شده‌ای (از درگاه ما!).'),
(1837, 1837, 'وَإِنَّ عَلَيْكَ اللَّعْنَةَ إِلَىٰ يَوْمِ الدِّينِ', 15, 35, 14, 53, 264, 'و لعنت (و دوری از رحمت حق) تا روز قیامت بر تو خواهد بود!»'),
(1838, 1838, 'قَالَ رَبِّ فَأَنْظِرْنِي إِلَىٰ يَوْمِ يُبْعَثُونَ', 15, 36, 14, 53, 264, 'گفت: «پروردگارا! مرا تا روز رستاخیز مهلت ده (و زنده بگذار!)»'),
(1839, 1839, 'قَالَ فَإِنَّكَ مِنَ الْمُنْظَرِينَ', 15, 37, 14, 53, 264, 'فرمود: «تو از مهلت یافتگانی!'),
(1840, 1840, 'إِلَىٰ يَوْمِ الْوَقْتِ الْمَعْلُومِ', 15, 38, 14, 53, 264, '(امّا نه تا روز رستاخیز، بلکه) تا روز وقت معیّنی.»'),
(1841, 1841, 'قَالَ رَبِّ بِمَا أَغْوَيْتَنِي لَأُزَيِّنَنَّ لَهُمْ فِي الْأَرْضِ وَلَأُغْوِيَنَّهُمْ أَجْمَعِينَ', 15, 39, 14, 53, 264, 'گفت: «پروردگارا! چون مرا گمراه ساختی، من (نعمتهای مادّی را) در زمین در نظر آنها زینت می‌دهم، و همگی را گمراه خواهم ساخت،'),
(1842, 1842, 'إِلَّا عِبَادَكَ مِنْهُمُ الْمُخْلَصِينَ', 15, 40, 14, 53, 264, 'مگر بندگان مخلصت را.»'),
(1843, 1843, 'قَالَ هَٰذَا صِرَاطٌ عَلَيَّ مُسْتَقِيمٌ', 15, 41, 14, 53, 264, 'فرمود: «این راه مستقیمی است که بر عهده من است (و سنّت همیشگیم)...'),
(1844, 1844, 'إِنَّ عِبَادِي لَيْسَ لَكَ عَلَيْهِمْ سُلْطَانٌ إِلَّا مَنِ اتَّبَعَكَ مِنَ الْغَاوِينَ', 15, 42, 14, 53, 264, 'که بر بندگانم تسلّط نخواهی یافت؛ مگر گمراهانی که از تو پیروی می‌کنند؛'),
(1845, 1845, 'وَإِنَّ جَهَنَّمَ لَمَوْعِدُهُمْ أَجْمَعِينَ', 15, 43, 14, 53, 264, 'و دوزخ، میعادگاه همه آنهاست!'),
(1846, 1846, 'لَهَا سَبْعَةُ أَبْوَابٍ لِكُلِّ بَابٍ مِنْهُمْ جُزْءٌ مَقْسُومٌ', 15, 44, 14, 53, 264, 'هفت در دارد؛ و برای هر دری، گروه معیّنی از آنها تقسیم شده‌اند!'),
(1847, 1847, 'إِنَّ الْمُتَّقِينَ فِي جَنَّاتٍ وَعُيُونٍ', 15, 45, 14, 53, 264, 'به یقین، پرهیزگاران در باغها (ی سرسبز بهشت) و در کنار چشمه‌ها هستند.'),
(1848, 1848, 'ادْخُلُوهَا بِسَلَامٍ آمِنِينَ', 15, 46, 14, 53, 264, '(فرشتگان به آنها می‌گویند:) داخل این باغها شوید با سلامت و امنیّت!'),
(1849, 1849, 'وَنَزَعْنَا مَا فِي صُدُورِهِمْ مِنْ غِلٍّ إِخْوَانًا عَلَىٰ سُرُرٍ مُتَقَابِلِينَ', 15, 47, 14, 53, 264, 'هر گونه غلِّ = حسد و کینه و دشمنی‌ را از سینه آنها برمی‌کنیم (و روحشان را پاک می‌سازیم)؛ در حالی که همه برابرند، و بر تختها روبه‌روی یکدیگر قرار دارند.'),
(1850, 1850, 'لَا يَمَسُّهُمْ فِيهَا نَصَبٌ وَمَا هُمْ مِنْهَا بِمُخْرَجِينَ', 15, 48, 14, 53, 264, 'هیچ خستگی و تعبی در آنجا به آنها نمی‌رسد، و هیچ گاه از آن اخراج نمی‌گردند!'),
(1851, 1851, ' نَبِّئْ عِبَادِي أَنِّي أَنَا الْغَفُورُ الرَّحِيمُ', 15, 49, 14, 53, 264, 'بندگانم را آگاه کن که من بخشنده مهربانم!'),
(1852, 1852, 'وَأَنَّ عَذَابِي هُوَ الْعَذَابُ الْأَلِيمُ', 15, 50, 14, 53, 264, 'و (اینکه) عذاب و کیفر من، همان عذاب دردناک است!'),
(1853, 1853, 'وَنَبِّئْهُمْ عَنْ ضَيْفِ إِبْرَاهِيمَ', 15, 51, 14, 53, 264, 'و به آنها از مهمانهای ابراهیم خبر ده!'),
(1854, 1854, 'إِذْ دَخَلُوا عَلَيْهِ فَقَالُوا سَلَامًا قَالَ إِنَّا مِنْكُمْ وَجِلُونَ', 15, 52, 14, 53, 265, 'هنگامی که بر او وارد شدند و سلام کردند؛ (ابراهیم) گفت: «ما از شما بیمناکیم!»'),
(1855, 1855, 'قَالُوا لَا تَوْجَلْ إِنَّا نُبَشِّرُكَ بِغُلَامٍ عَلِيمٍ', 15, 53, 14, 53, 265, 'گفتند: «نترس، ما تو را به پسری دانا بشارت می‌دهیم!»'),
(1856, 1856, 'قَالَ أَبَشَّرْتُمُونِي عَلَىٰ أَنْ مَسَّنِيَ الْكِبَرُ فَبِمَ تُبَشِّرُونَ', 15, 54, 14, 53, 265, 'گفت: «آیا به من (چنین) بشارت می‌دهید با اینکه پیر شده‌ام؟! به چه چیز بشارت می‌دهید؟!»'),
(1857, 1857, 'قَالُوا بَشَّرْنَاكَ بِالْحَقِّ فَلَا تَكُنْ مِنَ الْقَانِطِينَ', 15, 55, 14, 53, 265, 'گفتند: «تو را به حق بشارت دادیم؛ از مأیوسان مباش!»'),
(1858, 1858, 'قَالَ وَمَنْ يَقْنَطُ مِنْ رَحْمَةِ رَبِّهِ إِلَّا الضَّالُّونَ', 15, 56, 14, 53, 265, 'گفت: «جز گمراهان، چه کسی از رحمت پروردگارش مأیوس می‌شود؟!»'),
(1859, 1859, 'قَالَ فَمَا خَطْبُكُمْ أَيُّهَا الْمُرْسَلُونَ', 15, 57, 14, 53, 265, '(سپس) گفت: «مأموریت شما چیست ای فرستادگان خدا؟»'),
(1860, 1860, 'قَالُوا إِنَّا أُرْسِلْنَا إِلَىٰ قَوْمٍ مُجْرِمِينَ', 15, 58, 14, 53, 265, 'گفتند: «ما به سوی قومی گنهکار مأموریت یافته‌ایم (تا آنها را هلاک کنیم)!'),
(1861, 1861, 'إِلَّا آلَ لُوطٍ إِنَّا لَمُنَجُّوهُمْ أَجْمَعِينَ', 15, 59, 14, 53, 265, 'مگر خاندان لوط، که همگی آنها را نجات خواهیم داد...'),
(1862, 1862, 'إِلَّا امْرَأَتَهُ قَدَّرْنَا ۙ إِنَّهَا لَمِنَ الْغَابِرِينَ', 15, 60, 14, 53, 265, 'بجز همسرش، که مقدّر داشتیم از بازماندگان (در شهر، و هلاک‌شوندگان) باشد!»'),
(1863, 1863, 'فَلَمَّا جَاءَ آلَ لُوطٍ الْمُرْسَلُونَ', 15, 61, 14, 53, 265, 'هنگامی که فرستادگان (خدا) به سراغ خاندان لوط آمدند...'),
(1864, 1864, 'قَالَ إِنَّكُمْ قَوْمٌ مُنْكَرُونَ', 15, 62, 14, 53, 265, '(لوط) گفت: «شما گروه ناشناسی هستید!»'),
(1865, 1865, 'قَالُوا بَلْ جِئْنَاكَ بِمَا كَانُوا فِيهِ يَمْتَرُونَ', 15, 63, 14, 53, 265, 'گفتند: «ما همان چیزی را برای تو آورده‌ایم که آنها = کافران‌ در آن تردید داشتند (آری، ما مأمور عذابیم)!'),
(1866, 1866, 'وَأَتَيْنَاكَ بِالْحَقِّ وَإِنَّا لَصَادِقُونَ', 15, 64, 14, 53, 265, 'ما واقعیّت مسلّمی را برای تو آورده‌ایم؛ و راست می‌گوییم!');
INSERT INTO `Quran` (`rowid`, `id`, `Arabic`, `Chapter`, `Verse`, `Juz`, `Hizb`, `Page`, `tr`) VALUES
(1867, 1867, 'فَأَسْرِ بِأَهْلِكَ بِقِطْعٍ مِنَ اللَّيْلِ وَاتَّبِعْ أَدْبَارَهُمْ وَلَا يَلْتَفِتْ مِنْكُمْ أَحَدٌ وَامْضُوا حَيْثُ تُؤْمَرُونَ', 15, 65, 14, 53, 265, 'پس، خانواده‌ات را در اواخر شب با خود بردار، و از اینجا ببر؛ و خودت به دنبال آنها حرکت کن؛ و کسی از شما به پشت سر خویش ننگرد؛ به همانجا که مأمور هستید بروید!»'),
(1868, 1868, 'وَقَضَيْنَا إِلَيْهِ ذَٰلِكَ الْأَمْرَ أَنَّ دَابِرَ هَٰؤُلَاءِ مَقْطُوعٌ مُصْبِحِينَ', 15, 66, 14, 53, 265, 'و ما به لوط این موضوع را وحی فرستادیم که صبحگاهان، همه آنها ریشه‌کن خواهند شد.'),
(1869, 1869, 'وَجَاءَ أَهْلُ الْمَدِينَةِ يَسْتَبْشِرُونَ', 15, 67, 14, 53, 265, '(از سوی دیگر،) اهل شهر (از ورود میهمانان با خبر شدند، و بطرف خانه لوط) آمدند در حالی که شادمان بودند.'),
(1870, 1870, 'قَالَ إِنَّ هَٰؤُلَاءِ ضَيْفِي فَلَا تَفْضَحُونِ', 15, 68, 14, 53, 265, '(لوط) گفت: «اینها میهمانان منند؛ آبروی مرا نریزید!'),
(1871, 1871, 'وَاتَّقُوا اللَّهَ وَلَا تُخْزُونِ', 15, 69, 14, 53, 265, 'و از خدا بترسید، و مرا شرمنده نسازید!»'),
(1872, 1872, 'قَالُوا أَوَلَمْ نَنْهَكَ عَنِ الْعَالَمِينَ', 15, 70, 14, 53, 265, 'گفتند: «مگر ما تو را از جهانیان نهی نکردیم (و نگفتیم کسی را به میهمانی نپذیر؟!»'),
(1873, 1873, 'قَالَ هَٰؤُلَاءِ بَنَاتِي إِنْ كُنْتُمْ فَاعِلِينَ', 15, 71, 14, 53, 266, 'گفت: «دختران من حاضرند، اگر می‌خواهید کار صحیحی انجام دهید (با آنها ازدواج کنید، و از گناه و آلودگی بپرهیزید!)»'),
(1874, 1874, 'لَعَمْرُكَ إِنَّهُمْ لَفِي سَكْرَتِهِمْ يَعْمَهُونَ', 15, 72, 14, 53, 266, 'به جان تو سوگند، اینها در مستی خود سرگردانند (و عقل و شعور خود را از دست داده‌اند)!'),
(1875, 1875, 'فَأَخَذَتْهُمُ الصَّيْحَةُ مُشْرِقِينَ', 15, 73, 14, 53, 266, 'سرانجام، هنگام طلوع آفتاب، صیحه (مرگبار -بصورت صاعقه یا زمین‌لرزه-) آنها را فراگرفت!'),
(1876, 1876, 'فَجَعَلْنَا عَالِيَهَا سَافِلَهَا وَأَمْطَرْنَا عَلَيْهِمْ حِجَارَةً مِنْ سِجِّيلٍ', 15, 74, 14, 53, 266, 'سپس (شهر و آبادی آنها را زیر و رو کردیم؛) بالای آن را پایین قرار دادیم؛ و بارانی از سنگ بر آنها فرو ریختیم!'),
(1877, 1877, 'إِنَّ فِي ذَٰلِكَ لَآيَاتٍ لِلْمُتَوَسِّمِينَ', 15, 75, 14, 53, 266, 'در این (سرگذشت عبرت‌انگیز)، نشانه‌هایی است برای هوشیاران!'),
(1878, 1878, 'وَإِنَّهَا لَبِسَبِيلٍ مُقِيمٍ', 15, 76, 14, 53, 266, 'و ویرانه‌های سرزمین آنها، بر سر راه (کاروانها)، همواره ثابت و برقرار است؛'),
(1879, 1879, 'إِنَّ فِي ذَٰلِكَ لَآيَةً لِلْمُؤْمِنِينَ', 15, 77, 14, 53, 266, 'در این، نشانه‌ای است برای مؤمنان!'),
(1880, 1880, 'وَإِنْ كَانَ أَصْحَابُ الْأَيْكَةِ لَظَالِمِينَ', 15, 78, 14, 53, 266, '«اصحاب الأیکه» = صاحبان سرزمینهای پردرخت = قوم شعیب‌ مسلماً قوم ستمگری بودند!'),
(1881, 1881, 'فَانْتَقَمْنَا مِنْهُمْ وَإِنَّهُمَا لَبِإِمَامٍ مُبِينٍ', 15, 79, 14, 53, 266, 'ما از آنها انتقام گرفتیم؛ و (شهرهای ویران شده) این دو = قوم لوط و اصحاب الأیکه‌ بر سر راه (شما در سفرهای شام)، آشکار است!'),
(1882, 1882, 'وَلَقَدْ كَذَّبَ أَصْحَابُ الْحِجْرِ الْمُرْسَلِينَ', 15, 80, 14, 53, 266, 'و «اصحاب حجر» = قوم ثمود پیامبران را تکذیب کردند!'),
(1883, 1883, 'وَآتَيْنَاهُمْ آيَاتِنَا فَكَانُوا عَنْهَا مُعْرِضِينَ', 15, 81, 14, 53, 266, 'ما آیات خود را به آنان دادیم؛ ولی آنها از آن روی گرداندند!'),
(1884, 1884, 'وَكَانُوا يَنْحِتُونَ مِنَ الْجِبَالِ بُيُوتًا آمِنِينَ', 15, 82, 14, 53, 266, 'آنها خانه‌های امن در دل کوه‌ها می‌تراشیدند.'),
(1885, 1885, 'فَأَخَذَتْهُمُ الصَّيْحَةُ مُصْبِحِينَ', 15, 83, 14, 53, 266, 'امّا سرانجام صیحه (مرگبار)، صبحگاهان آنان را فرا گرفت؛'),
(1886, 1886, 'فَمَا أَغْنَىٰ عَنْهُمْ مَا كَانُوا يَكْسِبُونَ', 15, 84, 14, 53, 266, 'و آنچه را به دست آورده بودند، آنها را از عذاب الهی نجات نداد!'),
(1887, 1887, 'وَمَا خَلَقْنَا السَّمَاوَاتِ وَالْأَرْضَ وَمَا بَيْنَهُمَا إِلَّا بِالْحَقِّ ۗ وَإِنَّ السَّاعَةَ لَآتِيَةٌ ۖ فَاصْفَحِ الصَّفْحَ الْجَمِيلَ', 15, 85, 14, 53, 266, 'ما آسمانها و زمین و آنچه را میان آن دو است، جز بحق نیافریدیم؛ و ساعت موعود = قیامت‌ قطعاً فرا خواهد رسید (و جزای هر کس به او می‌رسد)! پس، از آنها به طرز شایسته‌ای صرف‌نظر کن (و آنها را بر نادانیهایشان ملامت ننما)!'),
(1888, 1888, 'إِنَّ رَبَّكَ هُوَ الْخَلَّاقُ الْعَلِيمُ', 15, 86, 14, 53, 266, 'به یقین، پروردگار تو، آفریننده آگاه است!'),
(1889, 1889, 'وَلَقَدْ آتَيْنَاكَ سَبْعًا مِنَ الْمَثَانِي وَالْقُرْآنَ الْعَظِيمَ', 15, 87, 14, 53, 266, 'ما به تو سوره حمد و قرآن عظیم دادیم!'),
(1890, 1890, 'لَا تَمُدَّنَّ عَيْنَيْكَ إِلَىٰ مَا مَتَّعْنَا بِهِ أَزْوَاجًا مِنْهُمْ وَلَا تَحْزَنْ عَلَيْهِمْ وَاخْفِضْ جَنَاحَكَ لِلْمُؤْمِنِينَ', 15, 88, 14, 53, 266, '(بنابر این،) هرگز چشم خود را به نعمتهای (مادّی)، که به گروه‌هایی از آنها = کفّار دادیم، میفکن! و بخاطر آنچه آنها دارند، غمگین مباش! و بال (عطوفت) خود را برای مؤمنین فرود آر!'),
(1891, 1891, 'وَقُلْ إِنِّي أَنَا النَّذِيرُ الْمُبِينُ', 15, 89, 14, 53, 266, 'و بگو: «من انذارکننده آشکارم!»'),
(1892, 1892, 'كَمَا أَنْزَلْنَا عَلَى الْمُقْتَسِمِينَ', 15, 90, 14, 53, 266, '(ما بر آنها عذابی می‌فرستیم) همان گونه که بر تجزیه‌گران (آیات الهی) فرستادیم!'),
(1893, 1893, 'الَّذِينَ جَعَلُوا الْقُرْآنَ عِضِينَ', 15, 91, 14, 53, 267, 'همانها که قرآن را تقسیم کردند (؛ آنچه را به سودشان بود پذیرفتند، و آنچه را بر خلاف هوسهایشان بود رها نمودند)!'),
(1894, 1894, 'فَوَرَبِّكَ لَنَسْأَلَنَّهُمْ أَجْمَعِينَ', 15, 92, 14, 53, 267, 'به پروردگارت سوگند، (در قیامت) از همه آنها سؤال خواهیم کرد...'),
(1895, 1895, 'عَمَّا كَانُوا يَعْمَلُونَ', 15, 93, 14, 53, 267, 'از آنچه عمل می‌کردند!'),
(1896, 1896, 'فَاصْدَعْ بِمَا تُؤْمَرُ وَأَعْرِضْ عَنِ الْمُشْرِكِينَ', 15, 94, 14, 53, 267, 'آنچه را مأموریت داری، آشکارا بیان کن! و از مشرکان روی گردان (و به آنها اعتنا نکن)!'),
(1897, 1897, 'إِنَّا كَفَيْنَاكَ الْمُسْتَهْزِئِينَ', 15, 95, 14, 53, 267, 'ما شرّ استهزاکنندگان را از تو دفع خواهیم کرد؛'),
(1898, 1898, 'الَّذِينَ يَجْعَلُونَ مَعَ اللَّهِ إِلَٰهًا آخَرَ ۚ فَسَوْفَ يَعْلَمُونَ', 15, 96, 14, 53, 267, 'همانها که معبود دیگری با خدا قرار دادند؛ امّا بزودی می‌فهمند!'),
(1899, 1899, 'وَلَقَدْ نَعْلَمُ أَنَّكَ يَضِيقُ صَدْرُكَ بِمَا يَقُولُونَ', 15, 97, 14, 53, 267, 'ما می‌دانیم سینه‌ات از آنچه آنها می‌گویند تنگ می‌شود (و تو را سخت ناراحت می‌کنند).'),
(1900, 1900, 'فَسَبِّحْ بِحَمْدِ رَبِّكَ وَكُنْ مِنَ السَّاجِدِينَ', 15, 98, 14, 53, 267, '(برای دفع ناراحتی آنان) پروردگارت را تسبیح و حمد گو! و از سجده‌کنندگان باش!'),
(1901, 1901, 'وَاعْبُدْ رَبَّكَ حَتَّىٰ يَأْتِيَكَ الْيَقِينُ', 15, 99, 14, 53, 267, 'و پروردگارت را عبادت کن تا یقین = مرگ‌ تو فرا رسد!'),
(1902, 1902, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ أَتَىٰ أَمْرُ اللَّهِ فَلَا تَسْتَعْجِلُوهُ ۚ سُبْحَانَهُ وَتَعَالَىٰ عَمَّا يُشْرِكُونَ', 16, 1, 14, 54, 267, 'فرمان خدا (برای مجازات مشرکان و مجرمان،) فرا رسیده است؛ برای آن عجله نکنید! منزّه و برتر است خداوند از آنچه همتای او قرارمی‌دهند!'),
(1903, 1903, 'يُنَزِّلُ الْمَلَائِكَةَ بِالرُّوحِ مِنْ أَمْرِهِ عَلَىٰ مَنْ يَشَاءُ مِنْ عِبَادِهِ أَنْ أَنْذِرُوا أَنَّهُ لَا إِلَٰهَ إِلَّا أَنَا فَاتَّقُونِ', 16, 2, 14, 54, 267, 'فرشتگان را با روح (الهی) بفرمانش بر هر کس از بندگانش بخواهد نازل می‌کند؛ (و به آنها دستور می‌دهد) که مردم را انذار کنید؛ (و بگویید:) معبودی جز من نیست؛ از (مخالفت دستور) من، بپرهیزید!'),
(1904, 1904, 'خَلَقَ السَّمَاوَاتِ وَالْأَرْضَ بِالْحَقِّ ۚ تَعَالَىٰ عَمَّا يُشْرِكُونَ', 16, 3, 14, 54, 267, 'آسمانها و زمین را بحق آفرید؛ او برتر است از اینکه همتایی برای او قرار می‌دهند!'),
(1905, 1905, 'خَلَقَ الْإِنْسَانَ مِنْ نُطْفَةٍ فَإِذَا هُوَ خَصِيمٌ مُبِينٌ', 16, 4, 14, 54, 267, 'انسان را از نطفه بی‌ارزشی آفرید؛ و سرانجام (او موجودی فصیح، و) مدافع آشکار از خویشتن گردید!'),
(1906, 1906, 'وَالْأَنْعَامَ خَلَقَهَا ۗ لَكُمْ فِيهَا دِفْءٌ وَمَنَافِعُ وَمِنْهَا تَأْكُلُونَ', 16, 5, 14, 54, 267, 'و چهارپایان را آفرید؛ در حالی که در آنها، برای شما وسیله پوشش، و منافع دیگری است؛ و از گوشت آنها می‌خورید!'),
(1907, 1907, 'وَلَكُمْ فِيهَا جَمَالٌ حِينَ تُرِيحُونَ وَحِينَ تَسْرَحُونَ', 16, 6, 14, 54, 267, 'و در آنها برای شما زینت و شکوه است به هنگامی که آنها را به استراحتگاهشان بازمی‌گردانید، و هنگامی که (صبحگاهان) به صحرا می‌فرستید!'),
(1908, 1908, 'وَتَحْمِلُ أَثْقَالَكُمْ إِلَىٰ بَلَدٍ لَمْ تَكُونُوا بَالِغِيهِ إِلَّا بِشِقِّ الْأَنْفُسِ ۚ إِنَّ رَبَّكُمْ لَرَءُوفٌ رَحِيمٌ', 16, 7, 14, 54, 268, 'آنها بارهای سنگین شما را به شهری حمل می‌کنند که جز با مشقّت زیاد، به آن نمی‌رسیدید؛ پروردگارتان رؤوف و رحیم است (که این وسایل حیات را در اختیارتان قرار داده)!'),
(1909, 1909, 'وَالْخَيْلَ وَالْبِغَالَ وَالْحَمِيرَ لِتَرْكَبُوهَا وَزِينَةً ۚ وَيَخْلُقُ مَا لَا تَعْلَمُونَ', 16, 8, 14, 54, 268, 'همچنین اسبها و استرها و الاغها را آفرید؛ تا بر آنها سوار شوید و زینت شما باشد، و چیزهایی می‌آفریند که نمی‌دانید.'),
(1910, 1910, 'وَعَلَى اللَّهِ قَصْدُ السَّبِيلِ وَمِنْهَا جَائِرٌ ۚ وَلَوْ شَاءَ لَهَدَاكُمْ أَجْمَعِينَ', 16, 9, 14, 54, 268, 'و بر خداست که راه راست را (به بندگان) نشان دهد؛ امّا بعضی از راه‌ها بیراهه است! و اگر خدا بخواهد، همه شما را (به اجبار) هدایت می‌کند (؛ ولی اجبار سودی ندارد).'),
(1911, 1911, 'هُوَ الَّذِي أَنْزَلَ مِنَ السَّمَاءِ مَاءً ۖ لَكُمْ مِنْهُ شَرَابٌ وَمِنْهُ شَجَرٌ فِيهِ تُسِيمُونَ', 16, 10, 14, 54, 268, 'او کسی است که از آسمان، آبی فرستاد، که نوشیدن شما از آن است؛ و (همچنین) گیاهان و درختانی که حیوانات خود را در آن به چرا می‌برید، نیز از آن است.'),
(1912, 1912, 'يُنْبِتُ لَكُمْ بِهِ الزَّرْعَ وَالزَّيْتُونَ وَالنَّخِيلَ وَالْأَعْنَابَ وَمِنْ كُلِّ الثَّمَرَاتِ ۗ إِنَّ فِي ذَٰلِكَ لَآيَةً لِقَوْمٍ يَتَفَكَّرُونَ', 16, 11, 14, 54, 268, 'خداوند با آن (آب باران)، برای شما زراعت و زیتون و نخل و انگور، و از همه میوه‌ها می‌رویاند؛ مسلماً در این، نشانه روشنی برای اندیشمندان است.'),
(1913, 1913, 'وَسَخَّرَ لَكُمُ اللَّيْلَ وَالنَّهَارَ وَالشَّمْسَ وَالْقَمَرَ ۖ وَالنُّجُومُ مُسَخَّرَاتٌ بِأَمْرِهِ ۗ إِنَّ فِي ذَٰلِكَ لَآيَاتٍ لِقَوْمٍ يَعْقِلُونَ', 16, 12, 14, 54, 268, 'او شب و روز و خورشید و ماه را مسخّر شما ساخت؛ و ستارگان نیز به فرمان او مسخّر شمایند؛ در این، نشانه‌هایی است (از عظمت خدا،) برای گروهی که عقل خود را به کار می‌گیرند!'),
(1914, 1914, 'وَمَا ذَرَأَ لَكُمْ فِي الْأَرْضِ مُخْتَلِفًا أَلْوَانُهُ ۗ إِنَّ فِي ذَٰلِكَ لَآيَةً لِقَوْمٍ يَذَّكَّرُونَ', 16, 13, 14, 54, 268, '(علاوه بر این،) مخلوقاتی را که در زمین به رنگهای گوناگون آفریده نیز مسخّر (فرمان شما) ساخت؛ در این، نشانه روشنی است برای گروهی که متذکّر می‌شوند!'),
(1915, 1915, 'وَهُوَ الَّذِي سَخَّرَ الْبَحْرَ لِتَأْكُلُوا مِنْهُ لَحْمًا طَرِيًّا وَتَسْتَخْرِجُوا مِنْهُ حِلْيَةً تَلْبَسُونَهَا وَتَرَى الْفُلْكَ مَوَاخِرَ فِيهِ وَلِتَبْتَغُوا مِنْ فَضْلِهِ وَلَعَلَّكُمْ تَشْكُرُونَ', 16, 14, 14, 54, 268, 'او کسی است که دریا را مسخّر (شما) ساخت تا از آن، گوشت تازه بخورید؛ و زیوری برای پوشیدن (مانند مروارید) از آن استخراج کنید؛ و کشتیها را می‌بینی که سینه دریا را می‌شکافند تا شما (به تجارت پردازید و) از فضل خدا بهره گیرید؛ شاید شکر نعمتهای او را بجا آورید!'),
(1916, 1916, 'وَأَلْقَىٰ فِي الْأَرْضِ رَوَاسِيَ أَنْ تَمِيدَ بِكُمْ وَأَنْهَارًا وَسُبُلًا لَعَلَّكُمْ تَهْتَدُونَ', 16, 15, 14, 54, 269, 'و در زمین، کوه‌های ثابت و محکمی افکند تا لرزش آن را نسبت به شما بگیرد؛ و نهرها و راه‌هایی ایجاد کرد، تا هدایت شوید.'),
(1917, 1917, 'وَعَلَامَاتٍ ۚ وَبِالنَّجْمِ هُمْ يَهْتَدُونَ', 16, 16, 14, 54, 269, 'و (نیز) علاماتی قرار داد؛ و (شب هنگام) به وسیله ستارگان هدایت می‌شوند.'),
(1918, 1918, 'أَفَمَنْ يَخْلُقُ كَمَنْ لَا يَخْلُقُ ۗ أَفَلَا تَذَكَّرُونَ', 16, 17, 14, 54, 269, 'آیا کسی که (این گونه مخلوقات را) می‌آفریند، همچون کسی است که نمی‌آفریند؟! آیا متذکّر نمی‌شوید؟!'),
(1919, 1919, 'وَإِنْ تَعُدُّوا نِعْمَةَ اللَّهِ لَا تُحْصُوهَا ۗ إِنَّ اللَّهَ لَغَفُورٌ رَحِيمٌ', 16, 18, 14, 54, 269, 'و اگر نعمتهای خدا را بشمارید، هرگز نمی‌توانید آنها را احصا کنید؛ خداوند بخشنده و مهربان است!'),
(1920, 1920, 'وَاللَّهُ يَعْلَمُ مَا تُسِرُّونَ وَمَا تُعْلِنُونَ', 16, 19, 14, 54, 269, 'خداوند آنچه را پنهان می‌دارید و آنچه را آشکار می‌سازید، می‌داند.'),
(1921, 1921, 'وَالَّذِينَ يَدْعُونَ مِنْ دُونِ اللَّهِ لَا يَخْلُقُونَ شَيْئًا وَهُمْ يُخْلَقُونَ', 16, 20, 14, 54, 269, 'معبودهایی را که غیر از خدا می‌خوانند، چیزی را خلق نمی‌کنند؛ بلکه خودشان هم مخلوقند!'),
(1922, 1922, 'أَمْوَاتٌ غَيْرُ أَحْيَاءٍ ۖ وَمَا يَشْعُرُونَ أَيَّانَ يُبْعَثُونَ', 16, 21, 14, 54, 269, 'آنها مردگانی هستند که هرگز استعداد حیات ندارند؛ و نمی‌دانند (عبادت‌کنندگانشان) در چه زمانی محشور می‌شوند!'),
(1923, 1923, 'إِلَٰهُكُمْ إِلَٰهٌ وَاحِدٌ ۚ فَالَّذِينَ لَا يُؤْمِنُونَ بِالْآخِرَةِ قُلُوبُهُمْ مُنْكِرَةٌ وَهُمْ مُسْتَكْبِرُونَ', 16, 22, 14, 54, 269, 'معبود شما خداوند یگانه است؛ امّا کسانی که به آخرت ایمان نمی‌آورند، دلهایشان (حق را) انکار می‌کند و مستکبرند.'),
(1924, 1924, 'لَا جَرَمَ أَنَّ اللَّهَ يَعْلَمُ مَا يُسِرُّونَ وَمَا يُعْلِنُونَ ۚ إِنَّهُ لَا يُحِبُّ الْمُسْتَكْبِرِينَ', 16, 23, 14, 54, 269, 'قطعاً خداوند از آنچه پنهان می‌دارند و آنچه آشکار می‌سازند با خبر است؛ او مستکبران را دوست نمی‌دارد!'),
(1925, 1925, 'وَإِذَا قِيلَ لَهُمْ مَاذَا أَنْزَلَ رَبُّكُمْ ۙ قَالُوا أَسَاطِيرُ الْأَوَّلِينَ', 16, 24, 14, 54, 269, 'و هنگامی که به آنها گفته شود: «پروردگار شما چه نازل کرده است؟» می‌گویند: «اینها (وحی الهی نیست؛) همان افسانه‌های دروغین پیشینیان است!»'),
(1926, 1926, 'لِيَحْمِلُوا أَوْزَارَهُمْ كَامِلَةً يَوْمَ الْقِيَامَةِ ۙ وَمِنْ أَوْزَارِ الَّذِينَ يُضِلُّونَهُمْ بِغَيْرِ عِلْمٍ ۗ أَلَا سَاءَ مَا يَزِرُونَ', 16, 25, 14, 54, 269, 'آنها باید روز قیامت، (هم) بار گناهان خود را بطور کامل بر دوش کشند؛ و هم سهمی از گناهان کسانی که بخاطر جهل، گمراهشان می‌سازند! بدانید آنها بار سنگین بدی بر دوش می‌کشند!'),
(1927, 1927, 'قَدْ مَكَرَ الَّذِينَ مِنْ قَبْلِهِمْ فَأَتَى اللَّهُ بُنْيَانَهُمْ مِنَ الْقَوَاعِدِ فَخَرَّ عَلَيْهِمُ السَّقْفُ مِنْ فَوْقِهِمْ وَأَتَاهُمُ الْعَذَابُ مِنْ حَيْثُ لَا يَشْعُرُونَ', 16, 26, 14, 54, 269, 'کسانی که قبل از ایشان بودند (نیز) از این توطئه‌ها داشتند؛ ولی خداوند به سراغ شالوده (زندگی) آنها رفت؛ و آن را از اساس ویران کرد؛ و سقف از بالا بر سرشان فرو ریخت؛ و عذاب (الهی) از آن جایی که نمی‌دانستند به سراغشان آمد!'),
(1928, 1928, 'ثُمَّ يَوْمَ الْقِيَامَةِ يُخْزِيهِمْ وَيَقُولُ أَيْنَ شُرَكَائِيَ الَّذِينَ كُنْتُمْ تُشَاقُّونَ فِيهِمْ ۚ قَالَ الَّذِينَ أُوتُوا الْعِلْمَ إِنَّ الْخِزْيَ الْيَوْمَ وَالسُّوءَ عَلَى الْكَافِرِينَ', 16, 27, 14, 54, 270, 'سپس روز قیامت خدا آنها را رسوا می‌سازد؛ و می‌گوید: «شریکانی که شما برای من ساختید، و بخاطر آنها با دیگران دشمنی می‌کردید، کجا هستید؟!» (در این هنگام،) کسانی که به آنها علم داده شده می‌گویند: «رسوایی و بدبختی، امروز بر کافران است!»'),
(1929, 1929, 'الَّذِينَ تَتَوَفَّاهُمُ الْمَلَائِكَةُ ظَالِمِي أَنْفُسِهِمْ ۖ فَأَلْقَوُا السَّلَمَ مَا كُنَّا نَعْمَلُ مِنْ سُوءٍ ۚ بَلَىٰ إِنَّ اللَّهَ عَلِيمٌ بِمَا كُنْتُمْ تَعْمَلُونَ', 16, 28, 14, 54, 270, 'همانها که فرشتگان (مرگ) روحشان را می‌گیرند در حالی که به خود ظلم کرده بودند! در این موقع آنها تسلیم می‌شوند (و بدروغ می‌گویند:) ما کار بدی انجام نمی‌دادیم! آری، خداوند به آنچه انجام می‌دادید عالم است!'),
(1930, 1930, 'فَادْخُلُوا أَبْوَابَ جَهَنَّمَ خَالِدِينَ فِيهَا ۖ فَلَبِئْسَ مَثْوَى الْمُتَكَبِّرِينَ', 16, 29, 14, 54, 270, '(به آنها گفته می‌شود:) اکنون از درهای جهنم وارد شوید در حالی که جاودانه در آن خواهید بود! چه جای بدی است جایگاه مستکبران!'),
(1931, 1931, ' وَقِيلَ لِلَّذِينَ اتَّقَوْا مَاذَا أَنْزَلَ رَبُّكُمْ ۚ قَالُوا خَيْرًا ۗ لِلَّذِينَ أَحْسَنُوا فِي هَٰذِهِ الدُّنْيَا حَسَنَةٌ ۚ وَلَدَارُ الْآخِرَةِ خَيْرٌ ۚ وَلَنِعْمَ دَارُ الْمُتَّقِينَ', 16, 30, 14, 54, 270, '(ولی هنگامی که) به پرهیزگاران گفته می‌شد: «پروردگار شما چه چیز نازل کرده است؟» می‌گفتند: «خیر (و سعادت)» (آری،) برای کسانی که نیکی کردند، در این دنیا نیکی است؛ و سرای آخرت از آن هم بهتر است؛ و چه خوب است سرای پرهیزگاران!'),
(1932, 1932, 'جَنَّاتُ عَدْنٍ يَدْخُلُونَهَا تَجْرِي مِنْ تَحْتِهَا الْأَنْهَارُ ۖ لَهُمْ فِيهَا مَا يَشَاءُونَ ۚ كَذَٰلِكَ يَجْزِي اللَّهُ الْمُتَّقِينَ', 16, 31, 14, 54, 270, 'باغهایی از بهشت جاویدان است که همگی وارد آن می‌شوند؛ نهرها از زیر درختانش می‌گذرد؛ هر چه بخواهند در آنجا هست؛ خداوند پرهیزگاران را چنین پاداش می‌دهد!'),
(1933, 1933, 'الَّذِينَ تَتَوَفَّاهُمُ الْمَلَائِكَةُ طَيِّبِينَ ۙ يَقُولُونَ سَلَامٌ عَلَيْكُمُ ادْخُلُوا الْجَنَّةَ بِمَا كُنْتُمْ تَعْمَلُونَ', 16, 32, 14, 54, 270, 'همانها که فرشتگان (مرگ) روحشان را می‌گیرند در حالی که پاک و پاکیزه‌اند؛ به آنها می‌گویند: «سلام بر شما! وارد بهشت شوید به خاطر اعمالی که انجام می‌دادید!»'),
(1934, 1934, 'هَلْ يَنْظُرُونَ إِلَّا أَنْ تَأْتِيَهُمُ الْمَلَائِكَةُ أَوْ يَأْتِيَ أَمْرُ رَبِّكَ ۚ كَذَٰلِكَ فَعَلَ الَّذِينَ مِنْ قَبْلِهِمْ ۚ وَمَا ظَلَمَهُمُ اللَّهُ وَلَٰكِنْ كَانُوا أَنْفُسَهُمْ يَظْلِمُونَ', 16, 33, 14, 54, 270, 'آیا آنها انتظاری جز این دارند که فرشتگان (قبض ارواح) به سراغشان بیایند، یا فرمان پروردگارت (برای مجازاتشان) فرا رسد (آنگاه توبه کنند؟! ولی توبه آنها در آن زمان بی اثر است! آری،) کسانی که پیش از ایشان بودند نیز چنین کردند! خداوند به آنها ستم نکرد؛ ولی آنان به خویشتن ستم می‌نمودند!'),
(1935, 1935, 'فَأَصَابَهُمْ سَيِّئَاتُ مَا عَمِلُوا وَحَاقَ بِهِمْ مَا كَانُوا بِهِ يَسْتَهْزِئُونَ', 16, 34, 14, 54, 270, 'و سرانجام بدیهای اعمالشان به آنها رسید؛ و آنچه را (از وعده‌های عذاب) استهزا می‌کردند، بر آنان وارد شد.'),
(1936, 1936, 'وَقَالَ الَّذِينَ أَشْرَكُوا لَوْ شَاءَ اللَّهُ مَا عَبَدْنَا مِنْ دُونِهِ مِنْ شَيْءٍ نَحْنُ وَلَا آبَاؤُنَا وَلَا حَرَّمْنَا مِنْ دُونِهِ مِنْ شَيْءٍ ۚ كَذَٰلِكَ فَعَلَ الَّذِينَ مِنْ قَبْلِهِمْ ۚ فَهَلْ عَلَى الرُّسُلِ إِلَّا الْبَلَاغُ الْمُبِينُ', 16, 35, 14, 54, 271, 'مشرکان گفتند: «اگر خدا می‌خواست، نه ما و نه پدران ما، غیر او را پرستش نمی‌کردیم؛ و چیزی را بدون اجازه او حرام نمی‌ساختیم!» (آری،) کسانی که پیش از ایشان بودند نیز همین کارها را انجام دادند؛ ولی آیا پیامبران وظیفه‌ای جز ابلاغ آشکار دارند؟!'),
(1937, 1937, 'وَلَقَدْ بَعَثْنَا فِي كُلِّ أُمَّةٍ رَسُولًا أَنِ اعْبُدُوا اللَّهَ وَاجْتَنِبُوا الطَّاغُوتَ ۖ فَمِنْهُمْ مَنْ هَدَى اللَّهُ وَمِنْهُمْ مَنْ حَقَّتْ عَلَيْهِ الضَّلَالَةُ ۚ فَسِيرُوا فِي الْأَرْضِ فَانْظُرُوا كَيْفَ كَانَ عَاقِبَةُ الْمُكَذِّبِينَ', 16, 36, 14, 54, 271, 'ما در هر امتی رسولی برانگیختیم که: «خدای یکتا را بپرستید؛ و از طاغوت اجتناب کنید!» خداوند گروهی را هدایت کرد؛ و گروهی ضلالت و گمراهی دامانشان را گرفت؛ پس در روی زمین بگردید و ببینید عاقبت تکذیب‌کنندگان چگونه بود!'),
(1938, 1938, 'إِنْ تَحْرِصْ عَلَىٰ هُدَاهُمْ فَإِنَّ اللَّهَ لَا يَهْدِي مَنْ يُضِلُّ ۖ وَمَا لَهُمْ مِنْ نَاصِرِينَ', 16, 37, 14, 54, 271, 'هر قدر بر هدایت آنها حریص باشی، (سودی ندارد؛ چرا) که خداوند کسی را که گمراه ساخت، هدایت نمی‌کند؛ و آنها یاورانی نخواهند داشت!'),
(1939, 1939, 'وَأَقْسَمُوا بِاللَّهِ جَهْدَ أَيْمَانِهِمْ ۙ لَا يَبْعَثُ اللَّهُ مَنْ يَمُوتُ ۚ بَلَىٰ وَعْدًا عَلَيْهِ حَقًّا وَلَٰكِنَّ أَكْثَرَ النَّاسِ لَا يَعْلَمُونَ', 16, 38, 14, 54, 271, 'آنها سوگندهای شدید به خدا یاد کردند که: «هرگز خداوند کسی را که می‌میرد، برنمی‌انگیزد!» آری، این وعده قطعی خداست (که همه مردگان را برای جزا بازمی‌گرداند)؛ ولی بیشتر مردم نمی‌دانند!'),
(1940, 1940, 'لِيُبَيِّنَ لَهُمُ الَّذِي يَخْتَلِفُونَ فِيهِ وَلِيَعْلَمَ الَّذِينَ كَفَرُوا أَنَّهُمْ كَانُوا كَاذِبِينَ', 16, 39, 14, 54, 271, 'هدف این است که آنچه را در آن اختلاف داشتند، برای آنها روشن سازد؛ و کسانی که منکر شدند، بدانند دروغ می‌گفتند!'),
(1941, 1941, 'إِنَّمَا قَوْلُنَا لِشَيْءٍ إِذَا أَرَدْنَاهُ أَنْ نَقُولَ لَهُ كُنْ فَيَكُونُ', 16, 40, 14, 54, 271, '(رستاخیز مردگان برای ما مشکل نیست؛ زیرا) وقتی چیزی را اراده می‌کنیم، فقط به آن می‌گوییم: «موجود باش!» بلافاصله موجود می‌شود.'),
(1942, 1942, 'وَالَّذِينَ هَاجَرُوا فِي اللَّهِ مِنْ بَعْدِ مَا ظُلِمُوا لَنُبَوِّئَنَّهُمْ فِي الدُّنْيَا حَسَنَةً ۖ وَلَأَجْرُ الْآخِرَةِ أَكْبَرُ ۚ لَوْ كَانُوا يَعْلَمُونَ', 16, 41, 14, 54, 271, 'آنها که پس از ستم دیدن در راه خدا، هجرت کردند، در این دنیا جایگاه (و مقام) خوبی به آنها می‌دهیم؛ و پاداش آخرت، از آن هم بزرگتر است اگر می‌دانستند!'),
(1943, 1943, 'الَّذِينَ صَبَرُوا وَعَلَىٰ رَبِّهِمْ يَتَوَكَّلُونَ', 16, 42, 14, 54, 271, 'آنها کسانی هستند که صبر و استقامت پیشه کردند، و تنها بر پروردگارشان توکّل می‌کنند.'),
(1944, 1944, 'وَمَا أَرْسَلْنَا مِنْ قَبْلِكَ إِلَّا رِجَالًا نُوحِي إِلَيْهِمْ ۚ فَاسْأَلُوا أَهْلَ الذِّكْرِ إِنْ كُنْتُمْ لَا تَعْلَمُونَ', 16, 43, 14, 54, 272, 'و پیش از تو، جز مردانی که به آنها وحی می‌کردیم، نفرستادیم! اگر نمی‌دانید، از آگاهان بپرسید (تا تعجب نکنید از اینکه پیامبر اسلام از میان همین مردان برانگیخته شده است)!'),
(1945, 1945, 'بِالْبَيِّنَاتِ وَالزُّبُرِ ۗ وَأَنْزَلْنَا إِلَيْكَ الذِّكْرَ لِتُبَيِّنَ لِلنَّاسِ مَا نُزِّلَ إِلَيْهِمْ وَلَعَلَّهُمْ يَتَفَكَّرُونَ', 16, 44, 14, 54, 272, '(از آنها بپرسید که) از دلایل روشن و کتب (پیامبران پیشین آگاهند!) و ما این ذکر = قرآن‌ را بر تو نازل کردیم، تا آنچه به سوی مردم نازل شده است برای آنها روشن سازی؛ و شاید اندیشه کنند!'),
(1946, 1946, 'أَفَأَمِنَ الَّذِينَ مَكَرُوا السَّيِّئَاتِ أَنْ يَخْسِفَ اللَّهُ بِهِمُ الْأَرْضَ أَوْ يَأْتِيَهُمُ الْعَذَابُ مِنْ حَيْثُ لَا يَشْعُرُونَ', 16, 45, 14, 54, 272, 'آیا توطئه‌گران از این ایمن گشتند که ممکن است خدا آنها را در زمین فروبرد، و یا مجازات (الهی)، از آن جا که انتظارش را ندارند، به سراغشان آید؟!...'),
(1947, 1947, 'أَوْ يَأْخُذَهُمْ فِي تَقَلُّبِهِمْ فَمَا هُمْ بِمُعْجِزِينَ', 16, 46, 14, 54, 272, 'یا به هنگامی (که برای کسب مال و ثروت افزونتر) در رفت و آمدند، دامانشان را بگیرد در حالی که قادر به فرار نیستند؟!...'),
(1948, 1948, 'أَوْ يَأْخُذَهُمْ عَلَىٰ تَخَوُّفٍ فَإِنَّ رَبَّكُمْ لَرَءُوفٌ رَحِيمٌ', 16, 47, 14, 54, 272, 'یا بطور تدریجی، با هشدارهای خوف‌انگیز آنان را گرفتار سازد؟! چرا که پروردگار شما، رؤوف و رحیم است.'),
(1949, 1949, 'أَوَلَمْ يَرَوْا إِلَىٰ مَا خَلَقَ اللَّهُ مِنْ شَيْءٍ يَتَفَيَّأُ ظِلَالُهُ عَنِ الْيَمِينِ وَالشَّمَائِلِ سُجَّدًا لِلَّهِ وَهُمْ دَاخِرُونَ', 16, 48, 14, 54, 272, 'آیا آنها مخلوقات خدا را ندیدند که سایه‌هایشان از راست و چپ حرکت دارند، و با خضوع برای خدا سجده می‌کنند؟!'),
(1950, 1950, 'وَلِلَّهِ يَسْجُدُ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ مِنْ دَابَّةٍ وَالْمَلَائِكَةُ وَهُمْ لَا يَسْتَكْبِرُونَ', 16, 49, 14, 54, 272, '(نه تنها سایه‌ها، بلکه) تمام آنچه در آسمانها و زمین از جنبندگان وجود دارد، و همچنین فرشتگان، برای خدا سجده می‌کنند و تکبّر نمی‌ورزند.'),
(1951, 1951, 'يَخَافُونَ رَبَّهُمْ مِنْ فَوْقِهِمْ وَيَفْعَلُونَ مَا يُؤْمَرُونَ ۩', 16, 50, 14, 54, 272, 'آنها (تنها) از (مخالفت) پروردگارشان، که حاکم بر آنهاست، می‌ترسند؛ و آنچه را مأموریت دارند انجام می‌دهند.'),
(1952, 1952, ' وَقَالَ اللَّهُ لَا تَتَّخِذُوا إِلَٰهَيْنِ اثْنَيْنِ ۖ إِنَّمَا هُوَ إِلَٰهٌ وَاحِدٌ ۖ فَإِيَّايَ فَارْهَبُونِ', 16, 51, 14, 55, 272, 'خداوند فرمان داده: «دو معبود (برای خود) انتخاب نکنید؛ معبود (شما) همان خدای یگانه است؛ تنها از (کیفر) من بترسید!»'),
(1953, 1953, 'وَلَهُ مَا فِي السَّمَاوَاتِ وَالْأَرْضِ وَلَهُ الدِّينُ وَاصِبًا ۚ أَفَغَيْرَ اللَّهِ تَتَّقُونَ', 16, 52, 14, 55, 272, 'آنچه در آسمانها و زمین است، از آن اوست؛ و دین خالص (نیز) همواره از آن او می‌باشد؛ آیا از غیر او می‌ترسید؟!'),
(1954, 1954, 'وَمَا بِكُمْ مِنْ نِعْمَةٍ فَمِنَ اللَّهِ ۖ ثُمَّ إِذَا مَسَّكُمُ الضُّرُّ فَإِلَيْهِ تَجْأَرُونَ', 16, 53, 14, 55, 272, 'آنچه از نعمتها دارید، همه از سوی خداست! و هنگامی که ناراحتی به شما رسد، فقط او را می‌خوانید!'),
(1955, 1955, 'ثُمَّ إِذَا كَشَفَ الضُّرَّ عَنْكُمْ إِذَا فَرِيقٌ مِنْكُمْ بِرَبِّهِمْ يُشْرِكُونَ', 16, 54, 14, 55, 272, '(امّا) هنگامی که ناراحتی و رنج را از شما برطرف می‌سازد، ناگاه گروهی از شما برای پروردگارشان همتا قائل می‌شوند.'),
(1956, 1956, 'لِيَكْفُرُوا بِمَا آتَيْنَاهُمْ ۚ فَتَمَتَّعُوا ۖ فَسَوْفَ تَعْلَمُونَ', 16, 55, 14, 55, 273, '(بگذار) تا نعمتهایی را که به آنها داده‌ایم کفران کنند! (اکنون) چند روزی (از متاع دنیا) بهره گیرید، امّا بزودی خواهید دانست (سرانجام کارتان به کجا خواهد کشید)!'),
(1957, 1957, 'وَيَجْعَلُونَ لِمَا لَا يَعْلَمُونَ نَصِيبًا مِمَّا رَزَقْنَاهُمْ ۗ تَاللَّهِ لَتُسْأَلُنَّ عَمَّا كُنْتُمْ تَفْتَرُونَ', 16, 56, 14, 55, 273, 'آنان برای بتهایی که هیچ گونه سود و زیانی از آنها سراغ ندارند، سهمی از آنچه به آنان روزی داده‌ایم قرارمی‌دهند؛ به خدا سوگند، (در دادگاه قیامت،) از این افتراها که می‌بندید، بازپرسی خواهید شد!'),
(1958, 1958, 'وَيَجْعَلُونَ لِلَّهِ الْبَنَاتِ سُبْحَانَهُ ۙ وَلَهُمْ مَا يَشْتَهُونَ', 16, 57, 14, 55, 273, 'آنها (در پندار خود،) برای خداوند دختران قرارمی‌دهند؛ -منزّه است (از اینکه فرزندی داشته باشد)- ولی برای خودشان، آنچه را میل دارند قائل می‌شوند...'),
(1959, 1959, 'وَإِذَا بُشِّرَ أَحَدُهُمْ بِالْأُنْثَىٰ ظَلَّ وَجْهُهُ مُسْوَدًّا وَهُوَ كَظِيمٌ', 16, 58, 14, 55, 273, 'در حالی که هرگاه به یکی از آنها بشارت دهند دختر نصیب تو شده، صورتش (از فرط ناراحتی) سیاه می‌شود؛ و به شدّت خشمگین می‌گردد؛...'),
(1960, 1960, 'يَتَوَارَىٰ مِنَ الْقَوْمِ مِنْ سُوءِ مَا بُشِّرَ بِهِ ۚ أَيُمْسِكُهُ عَلَىٰ هُونٍ أَمْ يَدُسُّهُ فِي التُّرَابِ ۗ أَلَا سَاءَ مَا يَحْكُمُونَ', 16, 59, 14, 55, 273, 'بخاطر بشارت بدی که به او داده شده، از قوم و قبیله خود متواری می‌گردد؛ (و نمی‌داند) آیا او را با قبول ننگ نگهدارد، یا در خاک پنهانش کند؟! چه بد حکم می‌کنند!'),
(1961, 1961, 'لِلَّذِينَ لَا يُؤْمِنُونَ بِالْآخِرَةِ مَثَلُ السَّوْءِ ۖ وَلِلَّهِ الْمَثَلُ الْأَعْلَىٰ ۚ وَهُوَ الْعَزِيزُ الْحَكِيمُ', 16, 60, 14, 55, 273, 'برای آنها که به سرای آخرت ایمان ندارند، صفات زشت است؛ و برای خدا، صفات عالی است؛ و او قدرتمند و حکیم است.'),
(1962, 1962, 'وَلَوْ يُؤَاخِذُ اللَّهُ النَّاسَ بِظُلْمِهِمْ مَا تَرَكَ عَلَيْهَا مِنْ دَابَّةٍ وَلَٰكِنْ يُؤَخِّرُهُمْ إِلَىٰ أَجَلٍ مُسَمًّى ۖ فَإِذَا جَاءَ أَجَلُهُمْ لَا يَسْتَأْخِرُونَ سَاعَةً ۖ وَلَا يَسْتَقْدِمُونَ', 16, 61, 14, 55, 273, 'و اگر خداوند مردم را بخاطر ظلمشان مجازات می‌کرد، جنبنده‌ای را بر پشت زمین باقی نمی‌گذارد؛ ولی آنها را تا زمان معیّنی به تأخیر می‌اندازد. و هنگامی که اجلشان فرا رسد، نه ساعتی تأخیر می‌کنند، و نه ساعتی پیشی می‌گیرند.'),
(1963, 1963, 'وَيَجْعَلُونَ لِلَّهِ مَا يَكْرَهُونَ وَتَصِفُ أَلْسِنَتُهُمُ الْكَذِبَ أَنَّ لَهُمُ الْحُسْنَىٰ ۖ لَا جَرَمَ أَنَّ لَهُمُ النَّارَ وَأَنَّهُمْ مُفْرَطُونَ', 16, 62, 14, 55, 273, 'آنها برای خدا چیزهایی قرارمی‌دهند که خودشان از آن کراهت دارند = فرزندان دختر؛ با این حال زبانشان به دروغ می‌گوید سرانجام نیکی دارند! از این رو برای آنان آتش است؛ و آنها از پیشگامان (دوزخ) اند.'),
(1964, 1964, 'تَاللَّهِ لَقَدْ أَرْسَلْنَا إِلَىٰ أُمَمٍ مِنْ قَبْلِكَ فَزَيَّنَ لَهُمُ الشَّيْطَانُ أَعْمَالَهُمْ فَهُوَ وَلِيُّهُمُ الْيَوْمَ وَلَهُمْ عَذَابٌ أَلِيمٌ', 16, 63, 14, 55, 273, 'به خدا سوگند، به سوی امتهای پیش از تو پیامبرانی فرستادیم؛ امّا شیطان اعمالشان را در نظرشان آراست؛ و امروز او ولیّ و سرپرستشان است؛ و مجازات دردناکی برای آنهاست!'),
(1965, 1965, 'وَمَا أَنْزَلْنَا عَلَيْكَ الْكِتَابَ إِلَّا لِتُبَيِّنَ لَهُمُ الَّذِي اخْتَلَفُوا فِيهِ ۙ وَهُدًى وَرَحْمَةً لِقَوْمٍ يُؤْمِنُونَ', 16, 64, 14, 55, 273, 'ما قرآن را بر تو نازل نکردیم مگر برای اینکه آنچه را در آن اختلاف دارند، برای آنها روشن کنی؛ و (این قرآن) مایه هدایت و رحمت است برای قومی که ایمان می‌آورند!'),
(1966, 1966, 'وَاللَّهُ أَنْزَلَ مِنَ السَّمَاءِ مَاءً فَأَحْيَا بِهِ الْأَرْضَ بَعْدَ مَوْتِهَا ۚ إِنَّ فِي ذَٰلِكَ لَآيَةً لِقَوْمٍ يَسْمَعُونَ', 16, 65, 14, 55, 274, 'خداوند از آسمان، آبی فرستاد؛ و زمین را، پس از آنکه مرده بود، حیات بخشید! در این، نشانه روشنی است برای جمعیّتی که گوش شنوا دارند!'),
(1967, 1967, 'وَإِنَّ لَكُمْ فِي الْأَنْعَامِ لَعِبْرَةً ۖ نُسْقِيكُمْ مِمَّا فِي بُطُونِهِ مِنْ بَيْنِ فَرْثٍ وَدَمٍ لَبَنًا خَالِصًا سَائِغًا لِلشَّارِبِينَ', 16, 66, 14, 55, 274, 'و در وجود چهارپایان، برای شما (درسهای) عبرتی است: از درون شکم آنها، از میان غذاهای هضم‌شده و خون، شیر خالص و گوارا به شما می‌نوشانیم!'),
(1968, 1968, 'وَمِنْ ثَمَرَاتِ النَّخِيلِ وَالْأَعْنَابِ تَتَّخِذُونَ مِنْهُ سَكَرًا وَرِزْقًا حَسَنًا ۗ إِنَّ فِي ذَٰلِكَ لَآيَةً لِقَوْمٍ يَعْقِلُونَ', 16, 67, 14, 55, 274, 'و از میوه‌های درختان نخل و انگور، مسکرات (ناپاک) و روزی خوب و پاکیزه می‌گیرید؛ در این، نشانه روشنی است برای جمعیّتی که اندیشه می‌کنند!'),
(1969, 1969, 'وَأَوْحَىٰ رَبُّكَ إِلَى النَّحْلِ أَنِ اتَّخِذِي مِنَ الْجِبَالِ بُيُوتًا وَمِنَ الشَّجَرِ وَمِمَّا يَعْرِشُونَ', 16, 68, 14, 55, 274, 'و پروردگار تو به زنبور عسل «وحی» (و الهام غریزی) نمود که: «از کوه‌ها و درختان و داربستهایی که مردم می‌سازند، خانه‌هایی برگزین!'),
(1970, 1970, 'ثُمَّ كُلِي مِنْ كُلِّ الثَّمَرَاتِ فَاسْلُكِي سُبُلَ رَبِّكِ ذُلُلًا ۚ يَخْرُجُ مِنْ بُطُونِهَا شَرَابٌ مُخْتَلِفٌ أَلْوَانُهُ فِيهِ شِفَاءٌ لِلنَّاسِ ۗ إِنَّ فِي ذَٰلِكَ لَآيَةً لِقَوْمٍ يَتَفَكَّرُونَ', 16, 69, 14, 55, 274, 'سپس از تمام ثمرات (و شیره گلها) بخور و راه‌هایی را که پروردگارت برای تو تعیین کرده است، براحتی بپیما! «از درون شکم آنها، نوشیدنی با رنگهای مختلف خارج می‌شود که در آن، شفا برای مردم است؛ به یقین در این امر، نشانه روشنی است برای جمعیّتی که می‌اندیشند.'),
(1971, 1971, 'وَاللَّهُ خَلَقَكُمْ ثُمَّ يَتَوَفَّاكُمْ ۚ وَمِنْكُمْ مَنْ يُرَدُّ إِلَىٰ أَرْذَلِ الْعُمُرِ لِكَيْ لَا يَعْلَمَ بَعْدَ عِلْمٍ شَيْئًا ۚ إِنَّ اللَّهَ عَلِيمٌ قَدِيرٌ', 16, 70, 14, 55, 274, 'خداوند شما را آفرید؛ سپس شما را می‌میراند؛ بعضی از شما به نامطلوب‌ترین سنین بالای عمر می‌رسند، تا بعد از علم و آگاهی، چیزی ندانند (و همه چیز را فراموش کنند)؛ خداوند دانا و تواناست!'),
(1972, 1972, 'وَاللَّهُ فَضَّلَ بَعْضَكُمْ عَلَىٰ بَعْضٍ فِي الرِّزْقِ ۚ فَمَا الَّذِينَ فُضِّلُوا بِرَادِّي رِزْقِهِمْ عَلَىٰ مَا مَلَكَتْ أَيْمَانُهُمْ فَهُمْ فِيهِ سَوَاءٌ ۚ أَفَبِنِعْمَةِ اللَّهِ يَجْحَدُونَ', 16, 71, 14, 55, 274, 'خداوند بعضی از شما را بر بعضی دیگر از نظر روزی برتری داد (چرا که استعدادها و تلاشهایتان متفاوت است)! اما آنها که برتری داده شده‌اند، حاضر نیستند از روزی خود به بردگانشان بدهند و همگی در آن مساوی گردند؛ آیا آنان نعمت خدا را انکار می‌نمایند (که شکر او را ادا نمی‌کنند)؟!'),
(1973, 1973, 'وَاللَّهُ جَعَلَ لَكُمْ مِنْ أَنْفُسِكُمْ أَزْوَاجًا وَجَعَلَ لَكُمْ مِنْ أَزْوَاجِكُمْ بَنِينَ وَحَفَدَةً وَرَزَقَكُمْ مِنَ الطَّيِّبَاتِ ۚ أَفَبِالْبَاطِلِ يُؤْمِنُونَ وَبِنِعْمَتِ اللَّهِ هُمْ يَكْفُرُونَ', 16, 72, 14, 55, 274, 'خداوند برای شما از جنس خودتان همسرانی قرار داد؛ و از همسرانتان برای شما فرزندان و نوه‌هایی به وجود آورد؛ و از پاکیزه‌ها به شما روزی داد؛ آیا به باطل ایمان می‌آورند، و نعمت خدا را انکار می‌کنند؟!'),
(1974, 1974, 'وَيَعْبُدُونَ مِنْ دُونِ اللَّهِ مَا لَا يَمْلِكُ لَهُمْ رِزْقًا مِنَ السَّمَاوَاتِ وَالْأَرْضِ شَيْئًا وَلَا يَسْتَطِيعُونَ', 16, 73, 14, 55, 275, 'آنها غیر از خدا، موجوداتی را می‌پرستند که هیچ رزقی را برای آنان از آسمانها و زمین در اختیار ندارند؛ و توان این کار را نیز ندارند.'),
(1975, 1975, 'فَلَا تَضْرِبُوا لِلَّهِ الْأَمْثَالَ ۚ إِنَّ اللَّهَ يَعْلَمُ وَأَنْتُمْ لَا تَعْلَمُونَ', 16, 74, 14, 55, 275, 'پس، برای خدا امثال (و شبیه‌ها) قائل نشوید! خدا می‌داند، و شما نمی‌دانید.'),
(1976, 1976, ' ضَرَبَ اللَّهُ مَثَلًا عَبْدًا مَمْلُوكًا لَا يَقْدِرُ عَلَىٰ شَيْءٍ وَمَنْ رَزَقْنَاهُ مِنَّا رِزْقًا حَسَنًا فَهُوَ يُنْفِقُ مِنْهُ سِرًّا وَجَهْرًا ۖ هَلْ يَسْتَوُونَ ۚ الْحَمْدُ لِلَّهِ ۚ بَلْ أَكْثَرُهُمْ لَا يَعْلَمُونَ', 16, 75, 14, 55, 275, 'خداوند مثالی زده: برده مملوکی را که قادر بر هیچ چیز نیست؛ و انسان (با ایمانی) را که از جانب خود، رزقی نیکو به او بخشیده‌ایم، و او پنهان و آشکار از آنچه خدا به او داده، انفاق می‌کند؛ آیا این دو نفر یکسانند؟! شکر مخصوص خداست، ولی اکثر آنها نمی‌دانند!'),
(1977, 1977, 'وَضَرَبَ اللَّهُ مَثَلًا رَجُلَيْنِ أَحَدُهُمَا أَبْكَمُ لَا يَقْدِرُ عَلَىٰ شَيْءٍ وَهُوَ كَلٌّ عَلَىٰ مَوْلَاهُ أَيْنَمَا يُوَجِّهْهُ لَا يَأْتِ بِخَيْرٍ ۖ هَلْ يَسْتَوِي هُوَ وَمَنْ يَأْمُرُ بِالْعَدْلِ ۙ وَهُوَ عَلَىٰ صِرَاطٍ مُسْتَقِيمٍ', 16, 76, 14, 55, 275, 'خداوند مثالی (دیگر) زده است: دو نفر را، که یکی از آن دو، گنگ مادرزاد است؛ و قادر بر هیچ کاری نیست؛ و سربار صاحبش می‌باشد؛ او را در پی هر کاری بفرستد، خوب انجام نمی‌دهد؛ آیا چنین انسانی، با کسی که امر به عدل و داد می‌کند، و بر راهی راست قرار دارد، برابر است؟!'),
(1978, 1978, 'وَلِلَّهِ غَيْبُ السَّمَاوَاتِ وَالْأَرْضِ ۚ وَمَا أَمْرُ السَّاعَةِ إِلَّا كَلَمْحِ الْبَصَرِ أَوْ هُوَ أَقْرَبُ ۚ إِنَّ اللَّهَ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ', 16, 77, 14, 55, 275, 'غیب آسمانها و زمین، مخصوص خداست (و او همه را می‌داند)؛ و امر قیامت (بقدری نزدیک و آسان است) درست همانند چشم برهم زدن، و یا از آن هم نزدیکتر؛ چرا که خدا بر هر چیزی تواناست!'),
(1979, 1979, 'وَاللَّهُ أَخْرَجَكُمْ مِنْ بُطُونِ أُمَّهَاتِكُمْ لَا تَعْلَمُونَ شَيْئًا وَجَعَلَ لَكُمُ السَّمْعَ وَالْأَبْصَارَ وَالْأَفْئِدَةَ ۙ لَعَلَّكُمْ تَشْكُرُونَ', 16, 78, 14, 55, 275, 'و خداوند شما را از شکم مادرانتان خارج نمود در حالی که هیچ چیز نمی‌دانستید؛ و برای شما، گوش و چشم و عقل قرار داد، تا شکر نعمت او را بجا آورید!'),
(1980, 1980, 'أَلَمْ يَرَوْا إِلَى الطَّيْرِ مُسَخَّرَاتٍ فِي جَوِّ السَّمَاءِ مَا يُمْسِكُهُنَّ إِلَّا اللَّهُ ۗ إِنَّ فِي ذَٰلِكَ لَآيَاتٍ لِقَوْمٍ يُؤْمِنُونَ', 16, 79, 14, 55, 275, 'آیا آنها به پرندگانی که بر فراز آسمانها نگه‌داشته شده، نظر نیفکندند؟ هیچ کس جز خدا آنها را نگاه نمی‌دارد؛ در این امر، نشانه‌هایی (از عظمت و قدرت خدا) است برای کسانی که ایمان می‌آورند!'),
(1981, 1981, 'وَاللَّهُ جَعَلَ لَكُمْ مِنْ بُيُوتِكُمْ سَكَنًا وَجَعَلَ لَكُمْ مِنْ جُلُودِ الْأَنْعَامِ بُيُوتًا تَسْتَخِفُّونَهَا يَوْمَ ظَعْنِكُمْ وَيَوْمَ إِقَامَتِكُمْ ۙ وَمِنْ أَصْوَافِهَا وَأَوْبَارِهَا وَأَشْعَارِهَا أَثَاثًا وَمَتَاعًا إِلَىٰ حِينٍ', 16, 80, 14, 55, 276, 'و خدا برای شما از خانه‌هایتان محل سکونت (و آرامش) قرار داد؛ و از پوست چهارپایان نیز برای شما خانه‌هایی قرار داد که روز کوچ کردن و روز اقامتتان، به آسانی می‌توانید آنها را جا به جا کنید؛ و از پشم و کرک و موی آنها، برای شما اثاث و متاع (و وسایل مختلف زندگی) تا زمان معیّنی قرار داد.'),
(1982, 1982, 'وَاللَّهُ جَعَلَ لَكُمْ مِمَّا خَلَقَ ظِلَالًا وَجَعَلَ لَكُمْ مِنَ الْجِبَالِ أَكْنَانًا وَجَعَلَ لَكُمْ سَرَابِيلَ تَقِيكُمُ الْحَرَّ وَسَرَابِيلَ تَقِيكُمْ بَأْسَكُمْ ۚ كَذَٰلِكَ يُتِمُّ نِعْمَتَهُ عَلَيْكُمْ لَعَلَّكُمْ تُسْلِمُونَ', 16, 81, 14, 55, 276, 'و (نیز) خداوند از آنچه آفریده است سایه‌هایی برای شما قرار داده؛ و از کوه‌ها پناهگاه‌هایی؛ و برای شما پیراهنهایی آفریده، که شما را از گرما (و سرما) حفظ می‌کند؛ و پیراهنهایی که به هنگام جنگ، حافظ شماست؛ این گونه نعمتهایش را بر شما کامل می‌کند، شاید تسلیم فرمان او شوید!'),
(1983, 1983, 'فَإِنْ تَوَلَّوْا فَإِنَّمَا عَلَيْكَ الْبَلَاغُ الْمُبِينُ', 16, 82, 14, 55, 276, '(با این همه،) اگر روی برتابند، (نگران مباش؛) تو فقط وظیفه ابلاغ آشکار داری.'),
(1984, 1984, 'يَعْرِفُونَ نِعْمَتَ اللَّهِ ثُمَّ يُنْكِرُونَهَا وَأَكْثَرُهُمُ الْكَافِرُونَ', 16, 83, 14, 55, 276, 'آنها نعمت خدا را می‌شناسند؛ سپس آن را انکار می‌کنند؛ و اکثرشان کافرند!'),
(1985, 1985, 'وَيَوْمَ نَبْعَثُ مِنْ كُلِّ أُمَّةٍ شَهِيدًا ثُمَّ لَا يُؤْذَنُ لِلَّذِينَ كَفَرُوا وَلَا هُمْ يُسْتَعْتَبُونَ', 16, 84, 14, 55, 276, '(به خاطر بیاورید) روزی را که از هر امّتی گواهی بر آنان برمی‌انگیزیم؛ سپس به آنان که کفر ورزیدند، اجازه (سخن گفتن) داده نمی‌شود؛ (بلکه دست و پا و گوش و چشم، حتّی پوست تن آنها گواهی می‌دهند!) و (نیز) اجازه عذرخواهی و تقاضای عفو به آنان نمی‌دهند!'),
(1986, 1986, 'وَإِذَا رَأَى الَّذِينَ ظَلَمُوا الْعَذَابَ فَلَا يُخَفَّفُ عَنْهُمْ وَلَا هُمْ يُنْظَرُونَ', 16, 85, 14, 55, 276, 'و هنگامی که ظالمان عذاب را ببینند، نه به آنها تخفیف داده می‌شود، و نه مهلت!'),
(1987, 1987, 'وَإِذَا رَأَى الَّذِينَ أَشْرَكُوا شُرَكَاءَهُمْ قَالُوا رَبَّنَا هَٰؤُلَاءِ شُرَكَاؤُنَا الَّذِينَ كُنَّا نَدْعُو مِنْ دُونِكَ ۖ فَأَلْقَوْا إِلَيْهِمُ الْقَوْلَ إِنَّكُمْ لَكَاذِبُونَ', 16, 86, 14, 55, 276, 'و هنگامی که مشرکان معبودهایی را که همتای خدا قرار دادند می‌بینند، می‌گویند: «پروردگارا! اینها همتایانی هستند که ما به جای تو، آنها را می‌خواندیم! «در این هنگام، معبودان به آنها می‌گویند: «شما دروغگو هستید! (شما هوای نفس خود را پرستش می‌کردید!)»'),
(1988, 1988, 'وَأَلْقَوْا إِلَى اللَّهِ يَوْمَئِذٍ السَّلَمَ ۖ وَضَلَّ عَنْهُمْ مَا كَانُوا يَفْتَرُونَ', 16, 87, 14, 55, 276, 'و در آن روز، همگی (ناگزیر) در پیشگاه خدا تسلیم می‌شوند؛ و تمام آنچه را (نسبت به خدا) دروغ می‌بستند، گم و نابود می‌شود!'),
(1989, 1989, 'الَّذِينَ كَفَرُوا وَصَدُّوا عَنْ سَبِيلِ اللَّهِ زِدْنَاهُمْ عَذَابًا فَوْقَ الْعَذَابِ بِمَا كَانُوا يُفْسِدُونَ', 16, 88, 14, 55, 277, 'کسانی که کافر شدند و (مردم را) از راه خدا بازداشتند، بخاطر فسادی که می‌کردند، عذابی بر عذابشان می‌افزاییم!'),
(1990, 1990, 'وَيَوْمَ نَبْعَثُ فِي كُلِّ أُمَّةٍ شَهِيدًا عَلَيْهِمْ مِنْ أَنْفُسِهِمْ ۖ وَجِئْنَا بِكَ شَهِيدًا عَلَىٰ هَٰؤُلَاءِ ۚ وَنَزَّلْنَا عَلَيْكَ الْكِتَابَ تِبْيَانًا لِكُلِّ شَيْءٍ وَهُدًى وَرَحْمَةً وَبُشْرَىٰ لِلْمُسْلِمِينَ', 16, 89, 14, 55, 277, '(به یاد آورید) روزی را که از هر امتی، گواهی از خودشان بر آنها برمی‌انگیزیم؛ و تو را گواه بر آنان قرارمی‌دهیم! و ما این کتاب را بر تو نازل کردیم که بیانگر همه چیز، و مایه هدایت و رحمت و بشارت برای مسلمانان است!'),
(1991, 1991, ' إِنَّ اللَّهَ يَأْمُرُ بِالْعَدْلِ وَالْإِحْسَانِ وَإِيتَاءِ ذِي الْقُرْبَىٰ وَيَنْهَىٰ عَنِ الْفَحْشَاءِ وَالْمُنْكَرِ وَالْبَغْيِ ۚ يَعِظُكُمْ لَعَلَّكُمْ تَذَكَّرُونَ', 16, 90, 14, 56, 277, 'خداوند به عدل و احسان و بخشش به نزدیکان فرمان می‌دهد؛ و از فحشا و منکر و ستم، نهی می‌کند؛ خداوند به شما اندرز می‌دهد، شاید متذکّر شوید!'),
(1992, 1992, 'وَأَوْفُوا بِعَهْدِ اللَّهِ إِذَا عَاهَدْتُمْ وَلَا تَنْقُضُوا الْأَيْمَانَ بَعْدَ تَوْكِيدِهَا وَقَدْ جَعَلْتُمُ اللَّهَ عَلَيْكُمْ كَفِيلًا ۚ إِنَّ اللَّهَ يَعْلَمُ مَا تَفْعَلُونَ', 16, 91, 14, 56, 277, 'و هنگامی که با خدا عهد بستید، به عهد او وفا کنید! و سوگندها را بعد از محکم ساختن نشکنید، در حالی که خدا را کفیل و ضامن بر (سوگند) خود قرار داده‌اید، به یقین خداوند از آنچه انجام می‌دهید، آگاه است!'),
(1993, 1993, 'وَلَا تَكُونُوا كَالَّتِي نَقَضَتْ غَزْلَهَا مِنْ بَعْدِ قُوَّةٍ أَنْكَاثًا تَتَّخِذُونَ أَيْمَانَكُمْ دَخَلًا بَيْنَكُمْ أَنْ تَكُونَ أُمَّةٌ هِيَ أَرْبَىٰ مِنْ أُمَّةٍ ۚ إِنَّمَا يَبْلُوكُمُ اللَّهُ بِهِ ۚ وَلَيُبَيِّنَنَّ لَكُمْ يَوْمَ الْقِيَامَةِ مَا كُنْتُمْ فِيهِ تَخْتَلِفُونَ', 16, 92, 14, 56, 277, 'همانند آن زن (سبک مغز) نباشید که پشمهای تابیده خود را، پس از استحکام، وامی‌تابید! در حالی که (سوگند و پیمان) خود را وسیله خیانت و فساد قرار می‌دهید؛ بخاطر اینکه گروهی، جمعیّتشان از گروه دیگر بیشتر است (و کثرت دشمن را بهانه‌ای برای شکستن بیعت با پیامبر می‌شمرید)! خدا فقط شما را با این وسیله آزمایش می‌کند؛ و به یقین روز قیامت، آنچه را در آن اختلاف داشتید، برای شما روشن می‌سازد!'),
(1994, 1994, 'وَلَوْ شَاءَ اللَّهُ لَجَعَلَكُمْ أُمَّةً وَاحِدَةً وَلَٰكِنْ يُضِلُّ مَنْ يَشَاءُ وَيَهْدِي مَنْ يَشَاءُ ۚ وَلَتُسْأَلُنَّ عَمَّا كُنْتُمْ تَعْمَلُونَ', 16, 93, 14, 56, 277, 'و اگر خدا می‌خواست، همه شما را امت واحدی قرارمی‌داد؛ (و همه را به اجبار وادار به ایمان می‌کرد؛ امّا ایمان اجباری فایده‌ای ندارد!) ولی خدا هر کس را بخواهد (و شایسته بداند) گمراه، و هر کس را بخواهد (و لایق بداند) هدایت می‌کند! (به گروهی توفیق هدایت داده، و از گروهی سلب می‌کند!) و یقیناً شما از آنچه انجام می‌دادید، بازپرسی خواهید شد!'),
(1995, 1995, 'وَلَا تَتَّخِذُوا أَيْمَانَكُمْ دَخَلًا بَيْنَكُمْ فَتَزِلَّ قَدَمٌ بَعْدَ ثُبُوتِهَا وَتَذُوقُوا السُّوءَ بِمَا صَدَدْتُمْ عَنْ سَبِيلِ اللَّهِ ۖ وَلَكُمْ عَذَابٌ عَظِيمٌ', 16, 94, 14, 56, 278, 'سوگندهایتان را وسیله تقلّب و خیانت در میان خود قرار ندهید، مبادا گامی بعد از ثابت‌گشتن (بر ایمان) متزلزل شود؛ و به خاطر بازداشتن (مردم) از راه خدا، آثار سوء آن را بچشید! و برای شما، عذاب عظیمی خواهد بود!'),
(1996, 1996, 'وَلَا تَشْتَرُوا بِعَهْدِ اللَّهِ ثَمَنًا قَلِيلًا ۚ إِنَّمَا عِنْدَ اللَّهِ هُوَ خَيْرٌ لَكُمْ إِنْ كُنْتُمْ تَعْلَمُونَ', 16, 95, 14, 56, 278, 'و (هرگز) پیمان الهی را با بهای کمی مبادله نکنید (و هر بهایی در برابر آن ناچیز است!) آنچه نزد خداست، برای شما بهتر است اگر می‌دانستید.'),
(1997, 1997, 'مَا عِنْدَكُمْ يَنْفَدُ ۖ وَمَا عِنْدَ اللَّهِ بَاقٍ ۗ وَلَنَجْزِيَنَّ الَّذِينَ صَبَرُوا أَجْرَهُمْ بِأَحْسَنِ مَا كَانُوا يَعْمَلُونَ', 16, 96, 14, 56, 278, 'آنچه نزد شماست فانی می‌شود؛ امّا آنچه نزد خداست باقی است؛ و به کسانی که صبر و استقامت پیشه کنند، مطابق بهترین اعمالی که انجام می‌دادند پاداش خواهیم داد.'),
(1998, 1998, 'مَنْ عَمِلَ صَالِحًا مِنْ ذَكَرٍ أَوْ أُنْثَىٰ وَهُوَ مُؤْمِنٌ فَلَنُحْيِيَنَّهُ حَيَاةً طَيِّبَةً ۖ وَلَنَجْزِيَنَّهُمْ أَجْرَهُمْ بِأَحْسَنِ مَا كَانُوا يَعْمَلُونَ', 16, 97, 14, 56, 278, 'هر کس کار شایسته‌ای انجام دهد، خواه مرد باشد یا زن، در حالی که مؤمن است، او را به حیاتی پاک زنده می‌داریم؛ و پاداش آنها را به بهترین اعمالی که انجام می‌دادند، خواهیم داد.'),
(1999, 1999, 'فَإِذَا قَرَأْتَ الْقُرْآنَ فَاسْتَعِذْ بِاللَّهِ مِنَ الشَّيْطَانِ الرَّجِيمِ', 16, 98, 14, 56, 278, 'هنگامی که قرآن می‌خوانی، از شرّ شیطان مطرود، به خدا پناه بر!'),
(2000, 2000, 'إِنَّهُ لَيْسَ لَهُ سُلْطَانٌ عَلَى الَّذِينَ آمَنُوا وَعَلَىٰ رَبِّهِمْ يَتَوَكَّلُونَ', 16, 99, 14, 56, 278, 'چرا که او، بر کسانی که ایمان دارند و بر پروردگارشان توکّل می‌کنند، تسلّطی ندارد.'),
(2001, 2001, 'إِنَّمَا سُلْطَانُهُ عَلَى الَّذِينَ يَتَوَلَّوْنَهُ وَالَّذِينَ هُمْ بِهِ مُشْرِكُونَ', 16, 100, 14, 56, 278, 'تسلّط او تنها بر کسانی است که او را به سرپرستی خود برگزیده‌اند، و آنها که نسبت به او = خدا شرک می‌ورزند (و فرمان شیطان را به جای فرمان خدا، گردن می‌نهند)'),
(2002, 2002, 'وَإِذَا بَدَّلْنَا آيَةً مَكَانَ آيَةٍ ۙ وَاللَّهُ أَعْلَمُ بِمَا يُنَزِّلُ قَالُوا إِنَّمَا أَنْتَ مُفْتَرٍ ۚ بَلْ أَكْثَرُهُمْ لَا يَعْلَمُونَ', 16, 101, 14, 56, 278, 'و هنگامی که آیه‌ای را به آیه دیگر مبدّل کنیم = حکمی را نسخ نماییم‌ -و خدا بهتر می‌داند چه حکمی را نازل کند- آنها می‌گویند: «تو افترا می‌بندی!» امّا بیشترشان (حقیقت را) نمی‌دانند!'),
(2003, 2003, 'قُلْ نَزَّلَهُ رُوحُ الْقُدُسِ مِنْ رَبِّكَ بِالْحَقِّ لِيُثَبِّتَ الَّذِينَ آمَنُوا وَهُدًى وَبُشْرَىٰ لِلْمُسْلِمِينَ', 16, 102, 14, 56, 278, 'بگو، روح القدس آن را از جانب پروردگارت بحقّ نازل کرده، تا افراد باایمان را ثابت‌قدم گرداند؛ و هدایت و بشارتی است برای عموم مسلمانان!'),
(2004, 2004, 'وَلَقَدْ نَعْلَمُ أَنَّهُمْ يَقُولُونَ إِنَّمَا يُعَلِّمُهُ بَشَرٌ ۗ لِسَانُ الَّذِي يُلْحِدُونَ إِلَيْهِ أَعْجَمِيٌّ وَهَٰذَا لِسَانٌ عَرَبِيٌّ مُبِينٌ', 16, 103, 14, 56, 279, 'ما می‌دانیم که آنها می‌گویند: «این آیات را انسانی به او تعلیم می‌دهد!» در حالی که زبان کسی که اینها را به او نسبت می‌دهند عجمی است؛ ولی این (قرآن)، زبان عربی آشکار است!'),
(2005, 2005, 'إِنَّ الَّذِينَ لَا يُؤْمِنُونَ بِآيَاتِ اللَّهِ لَا يَهْدِيهِمُ اللَّهُ وَلَهُمْ عَذَابٌ أَلِيمٌ', 16, 104, 14, 56, 279, 'به یقین، کسانی که به آیات الهی ایمان نمی‌آورند، خدا آنها را هدایت نمی‌کند؛ و برای آنان عذاب دردناکی است.'),
(2006, 2006, 'إِنَّمَا يَفْتَرِي الْكَذِبَ الَّذِينَ لَا يُؤْمِنُونَ بِآيَاتِ اللَّهِ ۖ وَأُولَٰئِكَ هُمُ الْكَاذِبُونَ', 16, 105, 14, 56, 279, 'تنها کسانی دروغ می‌بندند که به آیات خدا ایمان ندارند؛ (آری،) دروغگویان واقعی آنها هستند!'),
(2007, 2007, 'مَنْ كَفَرَ بِاللَّهِ مِنْ بَعْدِ إِيمَانِهِ إِلَّا مَنْ أُكْرِهَ وَقَلْبُهُ مُطْمَئِنٌّ بِالْإِيمَانِ وَلَٰكِنْ مَنْ شَرَحَ بِالْكُفْرِ صَدْرًا فَعَلَيْهِمْ غَضَبٌ مِنَ اللَّهِ وَلَهُمْ عَذَابٌ عَظِيمٌ', 16, 106, 14, 56, 279, 'کسانی که بعد از ایمان کافر شوند -بجز آنها که تحت فشار واقع شده‌اند در حالی که قلبشان آرام و با ایمان است- آری، آنها که سینه خود را برای پذیرش کفر گشوده‌اند، غضب خدا بر آنهاست؛ و عذاب عظیمی در انتظارشان!'),
(2008, 2008, 'ذَٰلِكَ بِأَنَّهُمُ اسْتَحَبُّوا الْحَيَاةَ الدُّنْيَا عَلَى الْآخِرَةِ وَأَنَّ اللَّهَ لَا يَهْدِي الْقَوْمَ الْكَافِرِينَ', 16, 107, 14, 56, 279, 'این به خاطر آن است که زندگی دنیا (و پست را) بر آخرت ترجیح دادند؛ و خداوند افراد بی‌ایمان (لجوج) را هدایت نمی‌کند.'),
(2009, 2009, 'أُولَٰئِكَ الَّذِينَ طَبَعَ اللَّهُ عَلَىٰ قُلُوبِهِمْ وَسَمْعِهِمْ وَأَبْصَارِهِمْ ۖ وَأُولَٰئِكَ هُمُ الْغَافِلُونَ', 16, 108, 14, 56, 279, 'آنها کسانی هستند که (بر اثر فزونی گناه،) خدا بر قلب و گوش و چشمانشان مهر نهاده؛ (به همین دلیل نمی‌فهمند،) و غافلان واقعی همانها هستند!'),
(2010, 2010, 'لَا جَرَمَ أَنَّهُمْ فِي الْآخِرَةِ هُمُ الْخَاسِرُونَ', 16, 109, 14, 56, 279, 'و ناچار آنها در آخرت زیانکارند.'),
(2011, 2011, 'ثُمَّ إِنَّ رَبَّكَ لِلَّذِينَ هَاجَرُوا مِنْ بَعْدِ مَا فُتِنُوا ثُمَّ جَاهَدُوا وَصَبَرُوا إِنَّ رَبَّكَ مِنْ بَعْدِهَا لَغَفُورٌ رَحِيمٌ', 16, 110, 14, 56, 279, 'امّا پروردگار تو نسبت به کسانی که بعد از فریب‌خوردن، (به ایمان بازگشتند و) هجرت کردند؛ سپس جهاد کردند و در راه خدا استقامت نمودند؛ پروردگارت، بعد از انجام این کارها، بخشنده و مهربان است (و آنها را مشمول رحمت خود می‌سازد).'),
(2012, 2012, ' يَوْمَ تَأْتِي كُلُّ نَفْسٍ تُجَادِلُ عَنْ نَفْسِهَا وَتُوَفَّىٰ كُلُّ نَفْسٍ مَا عَمِلَتْ وَهُمْ لَا يُظْلَمُونَ', 16, 111, 14, 56, 280, '(به یاد آورید) روزی را که هر کس (در فکر خویشتن است؛ و تنها) به دفاع از خود برمی‌خیزد؛ و نتیجه اعمال هر کسی، بی‌کم و کاست، به او داده می‌شود؛ و به آنها ظلم نخواهد شد!'),
(2013, 2013, 'وَضَرَبَ اللَّهُ مَثَلًا قَرْيَةً كَانَتْ آمِنَةً مُطْمَئِنَّةً يَأْتِيهَا رِزْقُهَا رَغَدًا مِنْ كُلِّ مَكَانٍ فَكَفَرَتْ بِأَنْعُمِ اللَّهِ فَأَذَاقَهَا اللَّهُ لِبَاسَ الْجُوعِ وَالْخَوْفِ بِمَا كَانُوا يَصْنَعُونَ', 16, 112, 14, 56, 280, 'خداوند (برای آنان که کفران نعمت می‌کنند،) مثلی زده است: منطقه آبادی که امن و آرام و مطمئن بود؛ و همواره روزیش از هر جا می‌رسید؛ امّا به نعمتهای خدا ناسپاسی کردند؛ و خداوند به خاطر اعمالی که انجام می‌دادند، لباس گرسنگی و ترس را بر اندامشان پوشانید!'),
(2014, 2014, 'وَلَقَدْ جَاءَهُمْ رَسُولٌ مِنْهُمْ فَكَذَّبُوهُ فَأَخَذَهُمُ الْعَذَابُ وَهُمْ ظَالِمُونَ', 16, 113, 14, 56, 280, 'پیامبری از خودشان به سراغ آنها آمد، او را تکذیب کردند؛ از این رو عذاب الهی آنها را فراگرفت در حالی که ظالم بودند!'),
(2015, 2015, 'فَكُلُوا مِمَّا رَزَقَكُمُ اللَّهُ حَلَالًا طَيِّبًا وَاشْكُرُوا نِعْمَتَ اللَّهِ إِنْ كُنْتُمْ إِيَّاهُ تَعْبُدُونَ', 16, 114, 14, 56, 280, 'پس، از آنچه خدا روزیتان کرده است، حلال و پاکیزه بخورید؛ و شکر نعمت خدا را بجا آورید اگر او را می‌پرستید!'),
(2016, 2016, 'إِنَّمَا حَرَّمَ عَلَيْكُمُ الْمَيْتَةَ وَالدَّمَ وَلَحْمَ الْخِنْزِيرِ وَمَا أُهِلَّ لِغَيْرِ اللَّهِ بِهِ ۖ فَمَنِ اضْطُرَّ غَيْرَ بَاغٍ وَلَا عَادٍ فَإِنَّ اللَّهَ غَفُورٌ رَحِيمٌ', 16, 115, 14, 56, 280, 'خداوند، تنها مردار، خون، گوشت خوک و آنچه را با نام غیر خدا سر بریده‌اند، بر شما حرام کرده است؛ امّا کسانی که ناچار شوند، در حالی که تجاوز و تعدّی از حدّ ننمایند، (خدا آنها را می‌بخشد؛ چرا که) خدا بخشنده و مهربان است.'),
(2017, 2017, 'وَلَا تَقُولُوا لِمَا تَصِفُ أَلْسِنَتُكُمُ الْكَذِبَ هَٰذَا حَلَالٌ وَهَٰذَا حَرَامٌ لِتَفْتَرُوا عَلَى اللَّهِ الْكَذِبَ ۚ إِنَّ الَّذِينَ يَفْتَرُونَ عَلَى اللَّهِ الْكَذِبَ لَا يُفْلِحُونَ', 16, 116, 14, 56, 280, 'به خاطر دروغی که بر زبانتان جاری می‌شود (و چیزی را مجاز و چیزی را ممنوع می‌کنید،) نگویید: «این حلال است و آن حرام»، تا بر خدا افترا ببندید به یقین کسانی که به خدا دروغ می‌بندند، رستگار نخواهند شد!'),
(2018, 2018, 'مَتَاعٌ قَلِيلٌ وَلَهُمْ عَذَابٌ أَلِيمٌ', 16, 117, 14, 56, 280, 'بهره کمی است (که در این دنیا نصیبشان می‌شود)؛ و عذاب دردناکی در انتظار آنان است!'),
(2019, 2019, 'وَعَلَى الَّذِينَ هَادُوا حَرَّمْنَا مَا قَصَصْنَا عَلَيْكَ مِنْ قَبْلُ ۖ وَمَا ظَلَمْنَاهُمْ وَلَٰكِنْ كَانُوا أَنْفُسَهُمْ يَظْلِمُونَ', 16, 118, 14, 56, 280, 'چیزهایی را که پیش از این برای تو شرح دادیم، بر یهود حرام کردیم؛ ما به آنها ستم نکردیم، اما آنها به خودشان ظلم و ستم می‌کردند!'),
(2020, 2020, 'ثُمَّ إِنَّ رَبَّكَ لِلَّذِينَ عَمِلُوا السُّوءَ بِجَهَالَةٍ ثُمَّ تَابُوا مِنْ بَعْدِ ذَٰلِكَ وَأَصْلَحُوا إِنَّ رَبَّكَ مِنْ بَعْدِهَا لَغَفُورٌ رَحِيمٌ', 16, 119, 14, 56, 281, 'امّا پروردگارت نسبت به آنها که از روی جهالت، بدی کرده‌اند، سپس توبه کرده و در مقام جبران برآمده‌اند، پروردگارت بعد از آن آمرزنده و مهربان است.'),
(2021, 2021, 'إِنَّ إِبْرَاهِيمَ كَانَ أُمَّةً قَانِتًا لِلَّهِ حَنِيفًا وَلَمْ يَكُ مِنَ الْمُشْرِكِينَ', 16, 120, 14, 56, 281, 'ابراهیم (به تنهایی) امّتی بود مطیع فرمان خدا؛ خالی از هر گونه انحراف؛ و از مشرکان نبود؛'),
(2022, 2022, 'شَاكِرًا لِأَنْعُمِهِ ۚ اجْتَبَاهُ وَهَدَاهُ إِلَىٰ صِرَاطٍ مُسْتَقِيمٍ', 16, 121, 14, 56, 281, 'شکرگزار نعمتهای پروردگار بود؛ خدا او را برگزید؛ و به راهی راست هدایت نمود!'),
(2023, 2023, 'وَآتَيْنَاهُ فِي الدُّنْيَا حَسَنَةً ۖ وَإِنَّهُ فِي الْآخِرَةِ لَمِنَ الصَّالِحِينَ', 16, 122, 14, 56, 281, 'ما در دنیا به او (همّت) نیکویی دادیم؛ و در آخرت از نیکان است!'),
(2024, 2024, 'ثُمَّ أَوْحَيْنَا إِلَيْكَ أَنِ اتَّبِعْ مِلَّةَ إِبْرَاهِيمَ حَنِيفًا ۖ وَمَا كَانَ مِنَ الْمُشْرِكِينَ', 16, 123, 14, 56, 281, 'سپس به تو وحی فرستادیم که از آیین ابراهیم -که ایمانی خالص داشت و از مشرکان نبود- پیروی کن!'),
(2025, 2025, 'إِنَّمَا جُعِلَ السَّبْتُ عَلَى الَّذِينَ اخْتَلَفُوا فِيهِ ۚ وَإِنَّ رَبَّكَ لَيَحْكُمُ بَيْنَهُمْ يَوْمَ الْقِيَامَةِ فِيمَا كَانُوا فِيهِ يَخْتَلِفُونَ', 16, 124, 14, 56, 281, '(تحریمهای) روز شنبه (برای یهود) فقط بعنوان یک مجازات بود، که در آن هم اختلاف کردند؛ و پروردگارت روز قیامت، در آنچه اختلاف داشتند، میان آنها داوری می‌کند!'),
(2026, 2026, 'ادْعُ إِلَىٰ سَبِيلِ رَبِّكَ بِالْحِكْمَةِ وَالْمَوْعِظَةِ الْحَسَنَةِ ۖ وَجَادِلْهُمْ بِالَّتِي هِيَ أَحْسَنُ ۚ إِنَّ رَبَّكَ هُوَ أَعْلَمُ بِمَنْ ضَلَّ عَنْ سَبِيلِهِ ۖ وَهُوَ أَعْلَمُ بِالْمُهْتَدِينَ', 16, 125, 14, 56, 281, 'با حکمت و اندرز نیکو، به راه پروردگارت دعوت نما! و با آنها به روشی که نیکوتر است، استدلال و مناظره کن! پروردگارت، از هر کسی بهتر می‌داند چه کسی از راه او گمراه شده است؛ و او به هدایت‌یافتگان داناتر است.'),
(2027, 2027, 'وَإِنْ عَاقَبْتُمْ فَعَاقِبُوا بِمِثْلِ مَا عُوقِبْتُمْ بِهِ ۖ وَلَئِنْ صَبَرْتُمْ لَهُوَ خَيْرٌ لِلصَّابِرِينَ', 16, 126, 14, 56, 281, 'و هر گاه خواستید مجازات کنید، تنها بمقداری که به شما تعدّی شده کیفر دهید! و اگر شکیبایی کنید، این کار برای شکیبایان بهتر است.'),
(2028, 2028, 'وَاصْبِرْ وَمَا صَبْرُكَ إِلَّا بِاللَّهِ ۚ وَلَا تَحْزَنْ عَلَيْهِمْ وَلَا تَكُ فِي ضَيْقٍ مِمَّا يَمْكُرُونَ', 16, 127, 14, 56, 281, 'صبر کن، و صبر تو فقط برای خدا و به توفیق خدا باشد! و بخاطر (کارهای) آنها، اندوهگین و دلسرد مشو! و از توطئه‌های آنها، در تنگنا قرار مگیر!'),
(2029, 2029, 'إِنَّ اللَّهَ مَعَ الَّذِينَ اتَّقَوْا وَالَّذِينَ هُمْ مُحْسِنُونَ', 16, 128, 14, 56, 281, 'خداوند با کسانی است که تقوا پیشه کرده‌اند، و کسانی که نیکوکارند.'),
(2030, 2030, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ سُبْحَانَ الَّذِي أَسْرَىٰ بِعَبْدِهِ لَيْلًا مِنَ الْمَسْجِدِ الْحَرَامِ إِلَى الْمَسْجِدِ الْأَقْصَى الَّذِي بَارَكْنَا حَوْلَهُ لِنُرِيَهُ مِنْ آيَاتِنَا ۚ إِنَّهُ هُوَ السَّمِيعُ الْبَصِيرُ', 17, 1, 15, 57, 282, 'پاک و منزّه است خدایی که بنده‌اش را در یک شب، از مسجد الحرام به مسجد الاقصی -که گرداگردش را پربرکت ساخته‌ایم- برد، تا برخی از آیات خود را به او نشان دهیم؛ چرا که او شنوا و بیناست.'),
(2031, 2031, 'وَآتَيْنَا مُوسَى الْكِتَابَ وَجَعَلْنَاهُ هُدًى لِبَنِي إِسْرَائِيلَ أَلَّا تَتَّخِذُوا مِنْ دُونِي وَكِيلًا', 17, 2, 15, 57, 282, 'ما به موسی کتاب آسمانی دادیم؛ و آن را وسیله هدایت بنی اسرائیل ساختیم؛ (و گفتیم:) غیر ما را تکیه‌گاه خود قرار ندهید!'),
(2032, 2032, 'ذُرِّيَّةَ مَنْ حَمَلْنَا مَعَ نُوحٍ ۚ إِنَّهُ كَانَ عَبْدًا شَكُورًا', 17, 3, 15, 57, 282, 'ای فرزندان کسانی که با نوح (بر کشتی) سوار کردیم! او بنده شکرگزاری بود. (شما هم مانند او باشید، تا نجات یابید!)'),
(2033, 2033, 'وَقَضَيْنَا إِلَىٰ بَنِي إِسْرَائِيلَ فِي الْكِتَابِ لَتُفْسِدُنَّ فِي الْأَرْضِ مَرَّتَيْنِ وَلَتَعْلُنَّ عُلُوًّا كَبِيرًا', 17, 4, 15, 57, 282, 'ما به بنی اسرائیل در کتاب (تورات) اعلام کردیم که دوبار در زمین فساد خواهید کرد، و برتری‌جویی بزرگی خواهید نمود.'),
(2034, 2034, 'فَإِذَا جَاءَ وَعْدُ أُولَاهُمَا بَعَثْنَا عَلَيْكُمْ عِبَادًا لَنَا أُولِي بَأْسٍ شَدِيدٍ فَجَاسُوا خِلَالَ الدِّيَارِ ۚ وَكَانَ وَعْدًا مَفْعُولًا', 17, 5, 15, 57, 282, 'هنگامی که نخستین وعده فرا رسد، گروهی از بندگان پیکارجوی خود را بر ضدّ شما میانگیزیم (تا شما را سخت در هم کوبند؛ حتی برای به دست آوردن مجرمان)، خانه‌ها را جستجو می‌کنند؛ و این وعده‌ای است قطعی!'),
(2035, 2035, 'ثُمَّ رَدَدْنَا لَكُمُ الْكَرَّةَ عَلَيْهِمْ وَأَمْدَدْنَاكُمْ بِأَمْوَالٍ وَبَنِينَ وَجَعَلْنَاكُمْ أَكْثَرَ نَفِيرًا', 17, 6, 15, 57, 282, 'سپس شما را بر آنها چیره می‌کنیم؛ و شما را به وسیله داراییها و فرزندانی کمک خواهیم کرد؛ و نفرات شما را بیشتر (از دشمن) قرارمی‌دهیم.'),
(2036, 2036, 'إِنْ أَحْسَنْتُمْ أَحْسَنْتُمْ لِأَنْفُسِكُمْ ۖ وَإِنْ أَسَأْتُمْ فَلَهَا ۚ فَإِذَا جَاءَ وَعْدُ الْآخِرَةِ لِيَسُوءُوا وُجُوهَكُمْ وَلِيَدْخُلُوا الْمَسْجِدَ كَمَا دَخَلُوهُ أَوَّلَ مَرَّةٍ وَلِيُتَبِّرُوا مَا عَلَوْا تَتْبِيرًا', 17, 7, 15, 57, 282, 'اگر نیکی کنید، به خودتان نیکی می‌کنید؛ و اگر بدی کنید باز هم به خود می‌کنید. و هنگامی که وعده دوم فرا رسد، (آنچنان دشمن بر شما سخت خواهد گرفت که) آثار غم و اندوه در صورتهایتان ظاهر می‌شود؛ و داخل مسجد (الاقصی) می‌شوند همان گونه که بار اول وارد شدند؛ و آنچه را زیر سلطه خود می‌گیرند، در هم می‌کوبند.'),
(2037, 2037, 'عَسَىٰ رَبُّكُمْ أَنْ يَرْحَمَكُمْ ۚ وَإِنْ عُدْتُمْ عُدْنَا ۘ وَجَعَلْنَا جَهَنَّمَ لِلْكَافِرِينَ حَصِيرًا', 17, 8, 15, 57, 283, 'امید است پروردگارتان به شما رحم کند! هرگاه برگردید، ما هم بازمی‌گردیم؛ و جهنّم را برای کافران، زندان سختی قرار دادیم.');
INSERT INTO `Quran` (`rowid`, `id`, `Arabic`, `Chapter`, `Verse`, `Juz`, `Hizb`, `Page`, `tr`) VALUES
(2038, 2038, 'إِنَّ هَٰذَا الْقُرْآنَ يَهْدِي لِلَّتِي هِيَ أَقْوَمُ وَيُبَشِّرُ الْمُؤْمِنِينَ الَّذِينَ يَعْمَلُونَ الصَّالِحَاتِ أَنَّ لَهُمْ أَجْرًا كَبِيرًا', 17, 9, 15, 57, 283, 'این قرآن، به راهی که استوارترین راه‌هاست، هدایت می‌کند؛ و به مؤمنانی که اعمال صالح انجام می‌دهند، بشارت می‌دهد که برای آنها پاداش بزرگی است.'),
(2039, 2039, 'وَأَنَّ الَّذِينَ لَا يُؤْمِنُونَ بِالْآخِرَةِ أَعْتَدْنَا لَهُمْ عَذَابًا أَلِيمًا', 17, 10, 15, 57, 283, 'و اینکه آنها که به قیامت ایمان نمی‌آورند، عذاب دردناکی برای آنان آماده ساخته‌ایم.'),
(2040, 2040, 'وَيَدْعُ الْإِنْسَانُ بِالشَّرِّ دُعَاءَهُ بِالْخَيْرِ ۖ وَكَانَ الْإِنْسَانُ عَجُولًا', 17, 11, 15, 57, 283, 'انسان (بر اثر شتابزدگی)، بدیها را طلب می‌کند آن گونه که نیکیها را می‌طلبد؛ و انسان، همیشه عجول بوده است!'),
(2041, 2041, 'وَجَعَلْنَا اللَّيْلَ وَالنَّهَارَ آيَتَيْنِ ۖ فَمَحَوْنَا آيَةَ اللَّيْلِ وَجَعَلْنَا آيَةَ النَّهَارِ مُبْصِرَةً لِتَبْتَغُوا فَضْلًا مِنْ رَبِّكُمْ وَلِتَعْلَمُوا عَدَدَ السِّنِينَ وَالْحِسَابَ ۚ وَكُلَّ شَيْءٍ فَصَّلْنَاهُ تَفْصِيلًا', 17, 12, 15, 57, 283, 'ما شب و روز را دو نشانه توحید و عظمت خود قرار دادیم؛ سپس نشانه شب را محو کرده، و نشانه روز را روشنی‌بخش ساختیم تا (در پرتو آن،) فضل پروردگارتان را بطلبید (و به تلاش زندگی برخیزید)، و عدد سالها و حساب را بدانید؛ و هر چیزی را بطور مشخّص و آشکار، بیان کردیم.'),
(2042, 2042, 'وَكُلَّ إِنْسَانٍ أَلْزَمْنَاهُ طَائِرَهُ فِي عُنُقِهِ ۖ وَنُخْرِجُ لَهُ يَوْمَ الْقِيَامَةِ كِتَابًا يَلْقَاهُ مَنْشُورًا', 17, 13, 15, 57, 283, 'و هر انسانی، اعمالش را بر گردنش آویخته‌ایم؛ و روز قیامت، کتابی برای او بیرون می‌آوریم که آن را در برابر خود، گشوده می‌بیند! (این همان نامه اعمال اوست!)'),
(2043, 2043, 'اقْرَأْ كِتَابَكَ كَفَىٰ بِنَفْسِكَ الْيَوْمَ عَلَيْكَ حَسِيبًا', 17, 14, 15, 57, 283, '(و به او می‌گوییم:) کتابت را بخوان، کافی است که امروز، خود حسابگر خویش باشی!'),
(2044, 2044, 'مَنِ اهْتَدَىٰ فَإِنَّمَا يَهْتَدِي لِنَفْسِهِ ۖ وَمَنْ ضَلَّ فَإِنَّمَا يَضِلُّ عَلَيْهَا ۚ وَلَا تَزِرُ وَازِرَةٌ وِزْرَ أُخْرَىٰ ۗ وَمَا كُنَّا مُعَذِّبِينَ حَتَّىٰ نَبْعَثَ رَسُولًا', 17, 15, 15, 57, 283, 'هر کس هدایت شود، برای خود هدایت یافته؛ و آن کس که گمراه گردد، به زیان خود گمراه شده است؛ و هیچ کس بار گناه دیگری را به دوش نمی‌کشد؛ و ما هرگز (قومی را) مجازات نخواهیم کرد، مگر آنکه پیامبری مبعوث کرده باشیم (تا وظایفشان را بیان کند.)'),
(2045, 2045, 'وَإِذَا أَرَدْنَا أَنْ نُهْلِكَ قَرْيَةً أَمَرْنَا مُتْرَفِيهَا فَفَسَقُوا فِيهَا فَحَقَّ عَلَيْهَا الْقَوْلُ فَدَمَّرْنَاهَا تَدْمِيرًا', 17, 16, 15, 57, 283, 'و هنگامی که بخواهیم شهر و دیاری را هلاک کنیم، نخست اوامر خود را برای «مترفین» (و ثروتمندان مست شهوت) آنجا، بیان می‌داریم، سپس هنگامی که به مخالفت برخاستند و استحقاق مجازات یافتند، آنها را به شدّت درهم می‌کوبیم.'),
(2046, 2046, 'وَكَمْ أَهْلَكْنَا مِنَ الْقُرُونِ مِنْ بَعْدِ نُوحٍ ۗ وَكَفَىٰ بِرَبِّكَ بِذُنُوبِ عِبَادِهِ خَبِيرًا بَصِيرًا', 17, 17, 15, 57, 283, 'چه بسیار مردمی که در قرون بعد از نوح، زندگی می‌کردند؛ (و طبق همین سنت،) آنها را هلاک کردیم! و کافی است که پروردگارت از گناهان بندگانش آگاه، و نسبت به آن بیناست.'),
(2047, 2047, 'مَنْ كَانَ يُرِيدُ الْعَاجِلَةَ عَجَّلْنَا لَهُ فِيهَا مَا نَشَاءُ لِمَنْ نُرِيدُ ثُمَّ جَعَلْنَا لَهُ جَهَنَّمَ يَصْلَاهَا مَذْمُومًا مَدْحُورًا', 17, 18, 15, 57, 284, 'آن کس که (تنها) زندگی زودگذر (دنیا) را می‌طلبد، آن مقدار از آن را که بخواهیم -و به هر کس اراده کنیم- می‌دهیم؛ سپس دوزخ را برای او قرار خواهیم داد، که در آتش سوزانش می‌سوزد در حالی که نکوهیده و رانده (درگاه خدا) است.'),
(2048, 2048, 'وَمَنْ أَرَادَ الْآخِرَةَ وَسَعَىٰ لَهَا سَعْيَهَا وَهُوَ مُؤْمِنٌ فَأُولَٰئِكَ كَانَ سَعْيُهُمْ مَشْكُورًا', 17, 19, 15, 57, 284, 'و آن کس که سرای آخرت را بطلبد، و برای آن سعی و کوشش کند -در حالی که ایمان داشته باشد- سعی و تلاش او، (از سوی خدا) پاداش داده خواهد شد.'),
(2049, 2049, 'كُلًّا نُمِدُّ هَٰؤُلَاءِ وَهَٰؤُلَاءِ مِنْ عَطَاءِ رَبِّكَ ۚ وَمَا كَانَ عَطَاءُ رَبِّكَ مَحْظُورًا', 17, 20, 15, 57, 284, 'هر یک از این دو گروه را از عطای پروردگارت، بهره و کمک می‌دهیم؛ و عطای پروردگارت هرگز (از کسی) منع نشده است.'),
(2050, 2050, 'انْظُرْ كَيْفَ فَضَّلْنَا بَعْضَهُمْ عَلَىٰ بَعْضٍ ۚ وَلَلْآخِرَةُ أَكْبَرُ دَرَجَاتٍ وَأَكْبَرُ تَفْضِيلًا', 17, 21, 15, 57, 284, 'ببین چگونه بعضی را (در دنیا بخاطر تلاششان) بر بعضی دیگر برتری بخشیده‌ایم؛ درجات آخرت و برتریهایش، از این هم بیشتر است!'),
(2051, 2051, 'لَا تَجْعَلْ مَعَ اللَّهِ إِلَٰهًا آخَرَ فَتَقْعُدَ مَذْمُومًا مَخْذُولًا', 17, 22, 15, 57, 284, 'هرگز معبود دیگری را با خدا قرار مده، که نکوهیده و بی‌یار و یاور خواهی نشست!'),
(2052, 2052, ' وَقَضَىٰ رَبُّكَ أَلَّا تَعْبُدُوا إِلَّا إِيَّاهُ وَبِالْوَالِدَيْنِ إِحْسَانًا ۚ إِمَّا يَبْلُغَنَّ عِنْدَكَ الْكِبَرَ أَحَدُهُمَا أَوْ كِلَاهُمَا فَلَا تَقُلْ لَهُمَا أُفٍّ وَلَا تَنْهَرْهُمَا وَقُلْ لَهُمَا قَوْلًا كَرِيمًا', 17, 23, 15, 57, 284, 'و پروردگارت فرمان داده: جز او را نپرستید! و به پدر و مادر نیکی کنید! هرگاه یکی از آن دو، یا هر دوی آنها، نزد تو به سن پیری رسند، کمترین اهانتی به آنها روا مدار! و بر آنها فریاد مزن! و گفتار لطیف و سنجیده و بزرگوارانه به آنها بگو!'),
(2053, 2053, 'وَاخْفِضْ لَهُمَا جَنَاحَ الذُّلِّ مِنَ الرَّحْمَةِ وَقُلْ رَبِّ ارْحَمْهُمَا كَمَا رَبَّيَانِي صَغِيرًا', 17, 24, 15, 57, 284, 'و بالهای تواضع خویش را از محبّت و لطف، در برابر آنان فرود آر! و بگو: «پروردگارا! همان‌گونه که آنها مرا در کوچکی تربیت کردند، مشمول رحمتشان قرار ده!»'),
(2054, 2054, 'رَبُّكُمْ أَعْلَمُ بِمَا فِي نُفُوسِكُمْ ۚ إِنْ تَكُونُوا صَالِحِينَ فَإِنَّهُ كَانَ لِلْأَوَّابِينَ غَفُورًا', 17, 25, 15, 57, 284, 'پروردگار شما از درون دلهایتان آگاهتر است؛ (اگر لغزشی در این زمینه داشتید) هر گاه صالح باشید (و جبران کنید) او بازگشت‌کنندگان را می‌بخشد.'),
(2055, 2055, 'وَآتِ ذَا الْقُرْبَىٰ حَقَّهُ وَالْمِسْكِينَ وَابْنَ السَّبِيلِ وَلَا تُبَذِّرْ تَبْذِيرًا', 17, 26, 15, 57, 284, 'و حقّ نزدیکان را بپرداز، و (همچنین حق) مستمند و وامانده در راه را! و هرگز اسراف و تبذیر مکن،'),
(2056, 2056, 'إِنَّ الْمُبَذِّرِينَ كَانُوا إِخْوَانَ الشَّيَاطِينِ ۖ وَكَانَ الشَّيْطَانُ لِرَبِّهِ كَفُورًا', 17, 27, 15, 57, 284, 'چرا که تبذیرکنندگان، برادران شیاطینند؛ و شیطان در برابر پروردگارش، بسیار ناسپاس بود!'),
(2057, 2057, 'وَإِمَّا تُعْرِضَنَّ عَنْهُمُ ابْتِغَاءَ رَحْمَةٍ مِنْ رَبِّكَ تَرْجُوهَا فَقُلْ لَهُمْ قَوْلًا مَيْسُورًا', 17, 28, 15, 57, 285, 'و هرگاه از آنان = مستمندان‌ روی برتابی، و انتظار رحمت پروردگارت را داشته باشی (تا گشایشی در کارت پدید آید و به آنها کمک کنی)، با گفتار نرم و آمیخته با لطف با آنها سخن بگو!'),
(2058, 2058, 'وَلَا تَجْعَلْ يَدَكَ مَغْلُولَةً إِلَىٰ عُنُقِكَ وَلَا تَبْسُطْهَا كُلَّ الْبَسْطِ فَتَقْعُدَ مَلُومًا مَحْسُورًا', 17, 29, 15, 57, 285, 'هرگز دستت را بر گردنت زنجیر مکن، (و ترک انفاق و بخشش منما) و بیش از حدّ (نیز) دست خود را مگشای، تا مورد سرزنش قرار گیری و از کار فرومانی!'),
(2059, 2059, 'إِنَّ رَبَّكَ يَبْسُطُ الرِّزْقَ لِمَنْ يَشَاءُ وَيَقْدِرُ ۚ إِنَّهُ كَانَ بِعِبَادِهِ خَبِيرًا بَصِيرًا', 17, 30, 15, 57, 285, 'به یقین، پروردگارت روزی را برای هر کس بخواهد، گشاده یا تنگ می‌دارد؛ او نسبت به بندگانش، آگاه و بیناست.'),
(2060, 2060, 'وَلَا تَقْتُلُوا أَوْلَادَكُمْ خَشْيَةَ إِمْلَاقٍ ۖ نَحْنُ نَرْزُقُهُمْ وَإِيَّاكُمْ ۚ إِنَّ قَتْلَهُمْ كَانَ خِطْئًا كَبِيرًا', 17, 31, 15, 57, 285, 'و فرزندانتان را از ترس فقر، نکشید! ما آنها و شما را روزی می‌دهیم؛ مسلماً کشتن آنها گناه بزرگی است!'),
(2061, 2061, 'وَلَا تَقْرَبُوا الزِّنَا ۖ إِنَّهُ كَانَ فَاحِشَةً وَسَاءَ سَبِيلًا', 17, 32, 15, 57, 285, 'و نزدیک زنا نشوید، که کار بسیار زشت، و بد راهی است!'),
(2062, 2062, 'وَلَا تَقْتُلُوا النَّفْسَ الَّتِي حَرَّمَ اللَّهُ إِلَّا بِالْحَقِّ ۗ وَمَنْ قُتِلَ مَظْلُومًا فَقَدْ جَعَلْنَا لِوَلِيِّهِ سُلْطَانًا فَلَا يُسْرِفْ فِي الْقَتْلِ ۖ إِنَّهُ كَانَ مَنْصُورًا', 17, 33, 15, 57, 285, 'و کسی را که خداوند خونش را حرام شمرده، نکشید، جز بحق! و آن کس که مظلوم کشته شده، برای ولیش سلطه (و حق قصاص) قرار دادیم؛ اما در قتل اسراف نکند، چرا که او مورد حمایت است!'),
(2063, 2063, 'وَلَا تَقْرَبُوا مَالَ الْيَتِيمِ إِلَّا بِالَّتِي هِيَ أَحْسَنُ حَتَّىٰ يَبْلُغَ أَشُدَّهُ ۚ وَأَوْفُوا بِالْعَهْدِ ۖ إِنَّ الْعَهْدَ كَانَ مَسْئُولًا', 17, 34, 15, 57, 285, 'و به مال یتیم، جز به بهترین راه نزدیک نشوید، تا به سر حد بلوغ رسد! و به عهد (خود) وفا کنید، که از عهد سؤال می‌شود!'),
(2064, 2064, 'وَأَوْفُوا الْكَيْلَ إِذَا كِلْتُمْ وَزِنُوا بِالْقِسْطَاسِ الْمُسْتَقِيمِ ۚ ذَٰلِكَ خَيْرٌ وَأَحْسَنُ تَأْوِيلًا', 17, 35, 15, 57, 285, 'و هنگامی که پیمانه می‌کنید، حق پیمانه را ادا نمایید، و با ترازوی درست وزن کنید! این برای شما بهتر، و عاقبتش نیکوتر است.'),
(2065, 2065, 'وَلَا تَقْفُ مَا لَيْسَ لَكَ بِهِ عِلْمٌ ۚ إِنَّ السَّمْعَ وَالْبَصَرَ وَالْفُؤَادَ كُلُّ أُولَٰئِكَ كَانَ عَنْهُ مَسْئُولًا', 17, 36, 15, 57, 285, 'از آنچه به آن آگاهی نداری، پیروی مکن، چرا که گوش و چشم و دل، همه مسؤولند.'),
(2066, 2066, 'وَلَا تَمْشِ فِي الْأَرْضِ مَرَحًا ۖ إِنَّكَ لَنْ تَخْرِقَ الْأَرْضَ وَلَنْ تَبْلُغَ الْجِبَالَ طُولًا', 17, 37, 15, 57, 285, 'و روی زمین، با تکبر راه مرو! تو نمی‌توانی زمین را بشکافی، و طول قامتت هرگز به کوه‌ها نمی‌رسد!'),
(2067, 2067, 'كُلُّ ذَٰلِكَ كَانَ سَيِّئُهُ عِنْدَ رَبِّكَ مَكْرُوهًا', 17, 38, 15, 57, 285, 'همه اینها گناهش نزد پروردگار تو ناپسند است.'),
(2068, 2068, 'ذَٰلِكَ مِمَّا أَوْحَىٰ إِلَيْكَ رَبُّكَ مِنَ الْحِكْمَةِ ۗ وَلَا تَجْعَلْ مَعَ اللَّهِ إِلَٰهًا آخَرَ فَتُلْقَىٰ فِي جَهَنَّمَ مَلُومًا مَدْحُورًا', 17, 39, 15, 57, 286, 'این (احکام)، از حکمتهایی است که پروردگارت به تو وحی فرستاده؛ و هرگز معبودی با خدا قرار مده، که در جهنم افکنده می‌شوی، در حالی که سرزنش شده، و رانده (درگاه خدا) خواهی بود!'),
(2069, 2069, 'أَفَأَصْفَاكُمْ رَبُّكُمْ بِالْبَنِينَ وَاتَّخَذَ مِنَ الْمَلَائِكَةِ إِنَاثًا ۚ إِنَّكُمْ لَتَقُولُونَ قَوْلًا عَظِيمًا', 17, 40, 15, 57, 286, 'آیا پروردگارتان فرزندان پسر را مخصوص شما ساخته، و خودش دخترانی از فرشتگان برگزیده است؟! شما سخن بزرگ (و بسیار زشتی) می‌گویید!'),
(2070, 2070, 'وَلَقَدْ صَرَّفْنَا فِي هَٰذَا الْقُرْآنِ لِيَذَّكَّرُوا وَمَا يَزِيدُهُمْ إِلَّا نُفُورًا', 17, 41, 15, 57, 286, 'ما در این قرآن، انواع بیانات مؤثر را آوردیم تا متذکر شوند! ولی (گروهی از کوردلان،) جز بر نفرتشان نمی‌افزاید.'),
(2071, 2071, 'قُلْ لَوْ كَانَ مَعَهُ آلِهَةٌ كَمَا يَقُولُونَ إِذًا لَابْتَغَوْا إِلَىٰ ذِي الْعَرْشِ سَبِيلًا', 17, 42, 15, 57, 286, 'بگو: «اگر آنچنان که آنها می‌گویند با او خدایانی بود، در این صورت، (خدایان) سعی می‌کردند راهی به سوی (خداوند) صاحب عرش پیدا کنند.»'),
(2072, 2072, 'سُبْحَانَهُ وَتَعَالَىٰ عَمَّا يَقُولُونَ عُلُوًّا كَبِيرًا', 17, 43, 15, 57, 286, 'او پاک و برتر است از آنچه آنها می‌گویند، بسیار برتر و منزه‌تر!'),
(2073, 2073, 'تُسَبِّحُ لَهُ السَّمَاوَاتُ السَّبْعُ وَالْأَرْضُ وَمَنْ فِيهِنَّ ۚ وَإِنْ مِنْ شَيْءٍ إِلَّا يُسَبِّحُ بِحَمْدِهِ وَلَٰكِنْ لَا تَفْقَهُونَ تَسْبِيحَهُمْ ۗ إِنَّهُ كَانَ حَلِيمًا غَفُورًا', 17, 44, 15, 57, 286, 'آسمانهای هفتگانه و زمین و کسانی که در آنها هستند، همه تسبیح او می‌گویند؛ و هر موجودی، تسبیح و حمد او می‌گوید؛ ولی شما تسبیح آنها را نمی‌فهمید؛ او بردبار و آمرزنده است.'),
(2074, 2074, 'وَإِذَا قَرَأْتَ الْقُرْآنَ جَعَلْنَا بَيْنَكَ وَبَيْنَ الَّذِينَ لَا يُؤْمِنُونَ بِالْآخِرَةِ حِجَابًا مَسْتُورًا', 17, 45, 15, 57, 286, 'و هنگامی که قرآن می‌خوانی، میان تو و آنها که به آخرت ایمان نمی‌آورند، حجاب ناپیدایی قرارمی‌دهیم؛'),
(2075, 2075, 'وَجَعَلْنَا عَلَىٰ قُلُوبِهِمْ أَكِنَّةً أَنْ يَفْقَهُوهُ وَفِي آذَانِهِمْ وَقْرًا ۚ وَإِذَا ذَكَرْتَ رَبَّكَ فِي الْقُرْآنِ وَحْدَهُ وَلَّوْا عَلَىٰ أَدْبَارِهِمْ نُفُورًا', 17, 46, 15, 57, 286, 'و بر دلهایشان پوششهایی، تا آن را نفهمند؛ و در گوشهایشان سنگینی؛ و هنگامی که پروردگارت را در قرآن به یگانگی یاد می‌کنی، آنها پشت می‌کنند و از تو روی بر می‌گردانند.'),
(2076, 2076, 'نَحْنُ أَعْلَمُ بِمَا يَسْتَمِعُونَ بِهِ إِذْ يَسْتَمِعُونَ إِلَيْكَ وَإِذْ هُمْ نَجْوَىٰ إِذْ يَقُولُ الظَّالِمُونَ إِنْ تَتَّبِعُونَ إِلَّا رَجُلًا مَسْحُورًا', 17, 47, 15, 57, 286, 'هنگامی که به سخنان تو گوش فرامی‌دهند، ما بهتر می‌دانیم برای چه گوش فرا می‌دهند؛ (و همچنین) در آن هنگام که با هم نجوا می‌کنند؛ آنگاه که ستمگران می‌گویند: «شما جز از انسانی که افسون شده، پیروی نمی‌کنید!»'),
(2077, 2077, 'انْظُرْ كَيْفَ ضَرَبُوا لَكَ الْأَمْثَالَ فَضَلُّوا فَلَا يَسْتَطِيعُونَ سَبِيلًا', 17, 48, 15, 57, 286, 'ببین چگونه برای تو مثلها زدند! در نتیجه گمراه شدند، و نمی‌توانند راه حق را پیدا کنند.'),
(2078, 2078, 'وَقَالُوا أَإِذَا كُنَّا عِظَامًا وَرُفَاتًا أَإِنَّا لَمَبْعُوثُونَ خَلْقًا جَدِيدًا', 17, 49, 15, 57, 286, 'و گفتند: «آیا هنگامی که ما، استخوانهای پوسیده و پراکنده‌ای شدیم، دگر بار آفرینش تازه‌ای خواهیم یافت؟!»'),
(2079, 2079, ' قُلْ كُونُوا حِجَارَةً أَوْ حَدِيدًا', 17, 50, 15, 58, 287, 'بگو: «شما سنگ باشید یا آهن،'),
(2080, 2080, 'أَوْ خَلْقًا مِمَّا يَكْبُرُ فِي صُدُورِكُمْ ۚ فَسَيَقُولُونَ مَنْ يُعِيدُنَا ۖ قُلِ الَّذِي فَطَرَكُمْ أَوَّلَ مَرَّةٍ ۚ فَسَيُنْغِضُونَ إِلَيْكَ رُءُوسَهُمْ وَيَقُولُونَ مَتَىٰ هُوَ ۖ قُلْ عَسَىٰ أَنْ يَكُونَ قَرِيبًا', 17, 51, 15, 58, 287, 'یا هر مخلوقی که در نظر شما، از آن هم سخت‌تر است (و از حیات و زندگی دورتر می‌باشد، باز خدا قادر است شما را به زندگی مجدد بازگرداند). آنها بزودی می گویند: «چه کسی ما را بازمی‌گرداند؟!» بگو: «همان کسی که نخستین بار شما را آفرید.» آنان سر خود را (از روی تعجب و انکار،) به سوی تو خم می‌کنند و می‌گویند: «در چه زمانی خواهد بود؟!» بگو: «شاید نزدیک باشد!'),
(2081, 2081, 'يَوْمَ يَدْعُوكُمْ فَتَسْتَجِيبُونَ بِحَمْدِهِ وَتَظُنُّونَ إِنْ لَبِثْتُمْ إِلَّا قَلِيلًا', 17, 52, 15, 58, 287, 'همان روز که شما را (از قبرهایتان) فرامی‌خواند؛ شما هم اجابت می‌کنید در حالی که حمد او را می‌گویید؛ می‌پندارید تنها مدت کوتاهی (در جهان برزخ) درنگ کرده‌اید!»'),
(2082, 2082, 'وَقُلْ لِعِبَادِي يَقُولُوا الَّتِي هِيَ أَحْسَنُ ۚ إِنَّ الشَّيْطَانَ يَنْزَغُ بَيْنَهُمْ ۚ إِنَّ الشَّيْطَانَ كَانَ لِلْإِنْسَانِ عَدُوًّا مُبِينًا', 17, 53, 15, 58, 287, 'به بندگانم بگو: «سخنی بگویند که بهترین باشد! چرا که (شیطان بوسیله سخنان ناموزون)، میان آنها فتنه و فساد می‌کند؛ همیشه شیطان دشمن آشکاری برای انسان بوده است!'),
(2083, 2083, 'رَبُّكُمْ أَعْلَمُ بِكُمْ ۖ إِنْ يَشَأْ يَرْحَمْكُمْ أَوْ إِنْ يَشَأْ يُعَذِّبْكُمْ ۚ وَمَا أَرْسَلْنَاكَ عَلَيْهِمْ وَكِيلًا', 17, 54, 15, 58, 287, 'پروردگار شما، از (نیات و اعمال) شما آگاهتر است؛ اگر بخواهد (و شایسته بداند)، شما را مشمول رحمت خود می‌سازد؛ و اگر بخواهد، مجازات می‌کند؛ و ما تو را بعنوان مأمور بر آنان نفرستاده‌ایم (که آنان را مجبور به ایمان کنی!)'),
(2084, 2084, 'وَرَبُّكَ أَعْلَمُ بِمَنْ فِي السَّمَاوَاتِ وَالْأَرْضِ ۗ وَلَقَدْ فَضَّلْنَا بَعْضَ النَّبِيِّينَ عَلَىٰ بَعْضٍ ۖ وَآتَيْنَا دَاوُودَ زَبُورًا', 17, 55, 15, 58, 287, 'پروردگار تو، از حال همه کسانی که در آسمانها و زمین هستند، آگاهتر است؛ و (اگر تو را بر دیگران برتری دادیم، بخاطر شایستگی توست،) ما بعضی از پیامبران را بر بعضی دیگر برتری دادیم؛ و به داوود، زبور بخشیدیم.'),
(2085, 2085, 'قُلِ ادْعُوا الَّذِينَ زَعَمْتُمْ مِنْ دُونِهِ فَلَا يَمْلِكُونَ كَشْفَ الضُّرِّ عَنْكُمْ وَلَا تَحْوِيلًا', 17, 56, 15, 58, 287, 'بگو: «کسانی را که غیر از خدا (معبود خود) می‌پندارید، بخوانید! آنها نه می‌توانند مشکلی را از شما برطرف سازند، و نه تغییری در آن ایجاد کنند.»'),
(2086, 2086, 'أُولَٰئِكَ الَّذِينَ يَدْعُونَ يَبْتَغُونَ إِلَىٰ رَبِّهِمُ الْوَسِيلَةَ أَيُّهُمْ أَقْرَبُ وَيَرْجُونَ رَحْمَتَهُ وَيَخَافُونَ عَذَابَهُ ۚ إِنَّ عَذَابَ رَبِّكَ كَانَ مَحْذُورًا', 17, 57, 15, 58, 287, 'کسانی را که آنان می‌خوانند، خودشان وسیله‌ای (برای تقرب) به پروردگارشان می‌جویند، وسیله‌ای هر چه نزدیکتر؛ و به رحمت او امیدوارند؛ و از عذاب او می‌ترسند؛ چرا که عذاب پروردگارت، همواره در خور پرهیز و وحشت است!'),
(2087, 2087, 'وَإِنْ مِنْ قَرْيَةٍ إِلَّا نَحْنُ مُهْلِكُوهَا قَبْلَ يَوْمِ الْقِيَامَةِ أَوْ مُعَذِّبُوهَا عَذَابًا شَدِيدًا ۚ كَانَ ذَٰلِكَ فِي الْكِتَابِ مَسْطُورًا', 17, 58, 15, 58, 287, 'هیچ شهر و آبادی نیست مگر اینکه آن را پیش از روز قیامت هلاک می‌کنیم؛ یا (اگر گناهکارند،) به عذاب شدیدی گرفتارشان خواهیم ساخت؛ این، در کتاب الهی = لوح محفوظ ثبت است.'),
(2088, 2088, 'وَمَا مَنَعَنَا أَنْ نُرْسِلَ بِالْآيَاتِ إِلَّا أَنْ كَذَّبَ بِهَا الْأَوَّلُونَ ۚ وَآتَيْنَا ثَمُودَ النَّاقَةَ مُبْصِرَةً فَظَلَمُوا بِهَا ۚ وَمَا نُرْسِلُ بِالْآيَاتِ إِلَّا تَخْوِيفًا', 17, 59, 15, 58, 288, 'هیچ چیز مانع ما نبود که این معجزات (درخواستی بهانه‌جویان) را بفرستیم جز اینکه پیشینیان (که همین درخواستها را داشتند، و با ایشان هماهنگ بودند)، آن را تکذیب کردند؛ (از جمله،) ما به (قوم) ثمود، ناقه دادیم؛ (معجزه‌ای) که روشنگر بود؛ اما بر آن ستم کردند (و ناقه را کشتند). ما معجزات را فقط برای بیم دادن (و اتمام حجت) می‌فرستیم.'),
(2089, 2089, 'وَإِذْ قُلْنَا لَكَ إِنَّ رَبَّكَ أَحَاطَ بِالنَّاسِ ۚ وَمَا جَعَلْنَا الرُّؤْيَا الَّتِي أَرَيْنَاكَ إِلَّا فِتْنَةً لِلنَّاسِ وَالشَّجَرَةَ الْمَلْعُونَةَ فِي الْقُرْآنِ ۚ وَنُخَوِّفُهُمْ فَمَا يَزِيدُهُمْ إِلَّا طُغْيَانًا كَبِيرًا', 17, 60, 15, 58, 288, '(به یاد آور) زمانی را که به تو گفتیم: «پروردگارت احاطه کامل به مردم دارد؛ (و از وضعشان کاملاً آگاه است.) و ما آن رؤیایی را که به تو نشان دادیم، فقط برای آزمایش مردم بود؛ همچنین شجره ملعونه = درخت نفرین شده‌ را که در قرآن ذکر کرده‌ایم. ما آنها را بیم داده (و انذار) می‌کنیم؛ اما جز طغیان عظیم، چیزی بر آنها نمی‌افزاید!»'),
(2090, 2090, 'وَإِذْ قُلْنَا لِلْمَلَائِكَةِ اسْجُدُوا لِآدَمَ فَسَجَدُوا إِلَّا إِبْلِيسَ قَالَ أَأَسْجُدُ لِمَنْ خَلَقْتَ طِينًا', 17, 61, 15, 58, 288, '(به یاد آورید) زمانی را که به فرشتگان گفتیم: «برای آدم سجده کنید!» آنها همگی سجده کردند، جز ابلیس که گفت: «آیا برای کسی سجده کنم که او را از خاک آفریده‌ای؟!»'),
(2091, 2091, 'قَالَ أَرَأَيْتَكَ هَٰذَا الَّذِي كَرَّمْتَ عَلَيَّ لَئِنْ أَخَّرْتَنِ إِلَىٰ يَوْمِ الْقِيَامَةِ لَأَحْتَنِكَنَّ ذُرِّيَّتَهُ إِلَّا قَلِيلًا', 17, 62, 15, 58, 288, '(سپس) گفت: «به من بگو، این کسی را که بر من برتری داده‌ای (به چه دلیل بوده است؟) اگر مرا تا روز قیامت زنده بگذاری، همه فرزندانش را، جز عده کمی، گمراه و ریشه‌کن خواهم ساخت!»'),
(2092, 2092, 'قَالَ اذْهَبْ فَمَنْ تَبِعَكَ مِنْهُمْ فَإِنَّ جَهَنَّمَ جَزَاؤُكُمْ جَزَاءً مَوْفُورًا', 17, 63, 15, 58, 288, 'فرمود: «برو! هر کس از آنان از تو تبعیت کند، جهنم کیفر شماست، کیفری است فراوان!'),
(2093, 2093, 'وَاسْتَفْزِزْ مَنِ اسْتَطَعْتَ مِنْهُمْ بِصَوْتِكَ وَأَجْلِبْ عَلَيْهِمْ بِخَيْلِكَ وَرَجِلِكَ وَشَارِكْهُمْ فِي الْأَمْوَالِ وَالْأَوْلَادِ وَعِدْهُمْ ۚ وَمَا يَعِدُهُمُ الشَّيْطَانُ إِلَّا غُرُورًا', 17, 64, 15, 58, 288, 'هر کدام از آنها را می‌توانی با صدایت تحریک کن! و لشکر سواره و پیاده‌ات را بر آنها گسیل دار! و در ثروت و فرزندانشان شرکت جوی! و آنان را با وعده‌ها سرگرم کن! -ولی شیطان، جز فریب و دروغ، وعده‌ای به آنها نمی‌دهد-'),
(2094, 2094, 'إِنَّ عِبَادِي لَيْسَ لَكَ عَلَيْهِمْ سُلْطَانٌ ۚ وَكَفَىٰ بِرَبِّكَ وَكِيلًا', 17, 65, 15, 58, 288, '(اما بدان) تو هرگز سلطه‌ای بر بندگان من، نخواهی یافت (و آنها هیچ‌گاه به دام تو گرفتار نمی‌شوند)! همین قدر کافی است که پروردگارت حافظ آنها باشد.»'),
(2095, 2095, 'رَبُّكُمُ الَّذِي يُزْجِي لَكُمُ الْفُلْكَ فِي الْبَحْرِ لِتَبْتَغُوا مِنْ فَضْلِهِ ۚ إِنَّهُ كَانَ بِكُمْ رَحِيمًا', 17, 66, 15, 58, 288, 'پروردگارتان کسی است که کشتی را در دریا برای شما به حرکت درمی‌آورد، تا از نعمت او بهره‌مند شوید؛ او نسبت به شما مهربان است.'),
(2096, 2096, 'وَإِذَا مَسَّكُمُ الضُّرُّ فِي الْبَحْرِ ضَلَّ مَنْ تَدْعُونَ إِلَّا إِيَّاهُ ۖ فَلَمَّا نَجَّاكُمْ إِلَى الْبَرِّ أَعْرَضْتُمْ ۚ وَكَانَ الْإِنْسَانُ كَفُورًا', 17, 67, 15, 58, 289, 'و هنگامی که در دریا ناراحتی به شما برسد، جز او، تمام کسانی را که (برای حل مشکلات خود) می‌خوانید، فراموش می‌کنید؛ اما هنگامی که شما را به خشکی نجات دهد، روی می‌گردانید؛ و انسان، بسیار ناسپاس است!'),
(2097, 2097, 'أَفَأَمِنْتُمْ أَنْ يَخْسِفَ بِكُمْ جَانِبَ الْبَرِّ أَوْ يُرْسِلَ عَلَيْكُمْ حَاصِبًا ثُمَّ لَا تَجِدُوا لَكُمْ وَكِيلًا', 17, 68, 15, 58, 289, 'آیا از این ایمن هستید که در خشکی (با یک زلزله شدید) شما را در زمین فرو ببرد، یا طوفانی از سنگریزه بر شما بفرستد (و در آن مدفونتان کند)، سپس حافظ (و یاوری) برای خود نیابید؟!'),
(2098, 2098, 'أَمْ أَمِنْتُمْ أَنْ يُعِيدَكُمْ فِيهِ تَارَةً أُخْرَىٰ فَيُرْسِلَ عَلَيْكُمْ قَاصِفًا مِنَ الرِّيحِ فَيُغْرِقَكُمْ بِمَا كَفَرْتُمْ ۙ ثُمَّ لَا تَجِدُوا لَكُمْ عَلَيْنَا بِهِ تَبِيعًا', 17, 69, 15, 58, 289, 'یا اینکه ایمن هستید که بار دیگر شما را به دریا بازگرداند، و تندباد کوبنده‌ای بر شما بفرستد، و شما را بخاطر کفرتان غرق کند، سپس دادخواه و خونخواهی در برابر ما پیدا نکنید؟!'),
(2099, 2099, ' وَلَقَدْ كَرَّمْنَا بَنِي آدَمَ وَحَمَلْنَاهُمْ فِي الْبَرِّ وَالْبَحْرِ وَرَزَقْنَاهُمْ مِنَ الطَّيِّبَاتِ وَفَضَّلْنَاهُمْ عَلَىٰ كَثِيرٍ مِمَّنْ خَلَقْنَا تَفْضِيلًا', 17, 70, 15, 58, 289, 'ما آدمیزادگان را گرامی داشتیم؛ و آنها را در خشکی و دریا، (بر مرکبهای راهوار) حمل کردیم؛ و از انواع روزیهای پاکیزه به آنان روزی دادیم؛ و آنها را بر بسیاری از موجوداتی که خلق کرده‌ایم، برتری بخشیدیم.'),
(2100, 2100, 'يَوْمَ نَدْعُو كُلَّ أُنَاسٍ بِإِمَامِهِمْ ۖ فَمَنْ أُوتِيَ كِتَابَهُ بِيَمِينِهِ فَأُولَٰئِكَ يَقْرَءُونَ كِتَابَهُمْ وَلَا يُظْلَمُونَ فَتِيلًا', 17, 71, 15, 58, 289, '(به یاد آورید) روزی را که هر گروهی را با پیشوایشان می‌خوانیم! کسانی که نامه عملشان به دست راستشان داده شود، آن را (با شادی و سرور) می‌خوانند؛ و بقدر رشته شکاف هسته خرمایی به آنان ستم نمی‌شود!'),
(2101, 2101, 'وَمَنْ كَانَ فِي هَٰذِهِ أَعْمَىٰ فَهُوَ فِي الْآخِرَةِ أَعْمَىٰ وَأَضَلُّ سَبِيلًا', 17, 72, 15, 58, 289, 'اما کسی که در این جهان (از دیدن چهره حق) نابینا بوده است، در آخرت نیز نابینا و گمراهتر است!'),
(2102, 2102, 'وَإِنْ كَادُوا لَيَفْتِنُونَكَ عَنِ الَّذِي أَوْحَيْنَا إِلَيْكَ لِتَفْتَرِيَ عَلَيْنَا غَيْرَهُ ۖ وَإِذًا لَاتَّخَذُوكَ خَلِيلًا', 17, 73, 15, 58, 289, 'نزدیک بود آنها تو را (با وسوسه‌های خود) از آنچه بر تو وحی کرده‌ایم بفریبند، تا غیر آن را به ما نسبت دهی؛ و در آن صورت، تو را به دوستی خود برمیگزینند!'),
(2103, 2103, 'وَلَوْلَا أَنْ ثَبَّتْنَاكَ لَقَدْ كِدْتَ تَرْكَنُ إِلَيْهِمْ شَيْئًا قَلِيلًا', 17, 74, 15, 58, 289, 'و اگر ما تو را ثابت قدم نمی‌ساختیم (و در پرتو مقام عصمت، مصون از انحراف نبودی)، نزدیک بود به آنان تمایل کنی.'),
(2104, 2104, 'إِذًا لَأَذَقْنَاكَ ضِعْفَ الْحَيَاةِ وَضِعْفَ الْمَمَاتِ ثُمَّ لَا تَجِدُ لَكَ عَلَيْنَا نَصِيرًا', 17, 75, 15, 58, 289, 'اگر چنین می‌کردی، ما دو برابر مجازات (مشرکان) در زندگی دنیا، و دو برابر (مجازات) آنها را بعد از مرگ، به تو می‌چشاندیم؛ سپس در برابر ما، یاوری برای خود نمی‌یافتی!'),
(2105, 2105, 'وَإِنْ كَادُوا لَيَسْتَفِزُّونَكَ مِنَ الْأَرْضِ لِيُخْرِجُوكَ مِنْهَا ۖ وَإِذًا لَا يَلْبَثُونَ خِلَافَكَ إِلَّا قَلِيلًا', 17, 76, 15, 58, 290, 'و نزدیک بود (با نیرنگ و توطئه) تو را از این سرزمین بلغزانند، تا از آن بیرونت کنند! و هرگاه چنین می‌کردند، (گرفتار مجازات سخت الهی شده،) و پس از تو، جز مدت کمی باقی نمی‌ماندند!'),
(2106, 2106, 'سُنَّةَ مَنْ قَدْ أَرْسَلْنَا قَبْلَكَ مِنْ رُسُلِنَا ۖ وَلَا تَجِدُ لِسُنَّتِنَا تَحْوِيلًا', 17, 77, 15, 58, 290, 'این سنت (ما در مورد) پیامبرانی است که پیش از تو فرستادیم؛ و هرگز برای سنت ما تغییر و دگرگونی نخواهی یافت!'),
(2107, 2107, 'أَقِمِ الصَّلَاةَ لِدُلُوكِ الشَّمْسِ إِلَىٰ غَسَقِ اللَّيْلِ وَقُرْآنَ الْفَجْرِ ۖ إِنَّ قُرْآنَ الْفَجْرِ كَانَ مَشْهُودًا', 17, 78, 15, 58, 290, 'نماز را از زوال خورشید (هنگام ظهر) تا نهایت تاریکی شب = نیمه شب‌ برپا دار؛ و همچنین قرآن فجر = نماز صبح‌ را؛ چرا که قرآن فجر، مشهود (فرشتگان شب و روز) است!'),
(2108, 2108, 'وَمِنَ اللَّيْلِ فَتَهَجَّدْ بِهِ نَافِلَةً لَكَ عَسَىٰ أَنْ يَبْعَثَكَ رَبُّكَ مَقَامًا مَحْمُودًا', 17, 79, 15, 58, 290, 'و پاسی از شب را (از خواب برخیز، و) قرآن (و نماز) بخوان! این یک وظیفه اضافی برای توست؛ امید است پروردگارت تو را به مقامی در خور ستایش برانگیزد!'),
(2109, 2109, 'وَقُلْ رَبِّ أَدْخِلْنِي مُدْخَلَ صِدْقٍ وَأَخْرِجْنِي مُخْرَجَ صِدْقٍ وَاجْعَلْ لِي مِنْ لَدُنْكَ سُلْطَانًا نَصِيرًا', 17, 80, 15, 58, 290, 'و بگو: «پروردگارا! مرا (در هر کار،) با صداقت وارد کن، و با صداقت خارج ساز! و از سوی خود، حجتی یاری کننده برایم قرار ده!»'),
(2110, 2110, 'وَقُلْ جَاءَ الْحَقُّ وَزَهَقَ الْبَاطِلُ ۚ إِنَّ الْبَاطِلَ كَانَ زَهُوقًا', 17, 81, 15, 58, 290, 'و بگو: «حق آمد، و باطل نابود شد؛ یقیناً باطل نابود شدنی است!»'),
(2111, 2111, 'وَنُنَزِّلُ مِنَ الْقُرْآنِ مَا هُوَ شِفَاءٌ وَرَحْمَةٌ لِلْمُؤْمِنِينَ ۙ وَلَا يَزِيدُ الظَّالِمِينَ إِلَّا خَسَارًا', 17, 82, 15, 58, 290, 'و از قرآن، آنچه شفا و رحمت است برای مؤمنان، نازل می‌کنیم؛ و ستمگران را جز خسران (و زیان) نمی‌افزاید.'),
(2112, 2112, 'وَإِذَا أَنْعَمْنَا عَلَى الْإِنْسَانِ أَعْرَضَ وَنَأَىٰ بِجَانِبِهِ ۖ وَإِذَا مَسَّهُ الشَّرُّ كَانَ يَئُوسًا', 17, 83, 15, 58, 290, 'هنگامی که به انسان نعمت می‌بخشیم، (از حق) روی می‌گرداند و متکبرانه دور می‌شود؛ و هنگامی که (کمترین) بدی به او می‌رسد، (از همه چیز) مایوس می‌گردد!'),
(2113, 2113, 'قُلْ كُلٌّ يَعْمَلُ عَلَىٰ شَاكِلَتِهِ فَرَبُّكُمْ أَعْلَمُ بِمَنْ هُوَ أَهْدَىٰ سَبِيلًا', 17, 84, 15, 58, 290, 'بگو: «هر کس طبق روش (و خلق و خوی) خود عمل می‌کند؛ و پروردگارتان کسانی را که راهشان نیکوتر است، بهتر می‌شناسد.»'),
(2114, 2114, 'وَيَسْأَلُونَكَ عَنِ الرُّوحِ ۖ قُلِ الرُّوحُ مِنْ أَمْرِ رَبِّي وَمَا أُوتِيتُمْ مِنَ الْعِلْمِ إِلَّا قَلِيلًا', 17, 85, 15, 58, 290, 'و از تو درباره «روح» سؤال می‌کنند، بگو: «روح از فرمان پروردگار من است؛ و جز اندکی از دانش، به شما داده نشده است!»'),
(2115, 2115, 'وَلَئِنْ شِئْنَا لَنَذْهَبَنَّ بِالَّذِي أَوْحَيْنَا إِلَيْكَ ثُمَّ لَا تَجِدُ لَكَ بِهِ عَلَيْنَا وَكِيلًا', 17, 86, 15, 58, 290, 'و اگر بخواهیم، آنچه را بر تو وحی فرستاده‌ایم، از تو می‌گیریم؛ سپس کسی را نمی‌یابی که در برابر ما، از تو دفاع کند...'),
(2116, 2116, 'إِلَّا رَحْمَةً مِنْ رَبِّكَ ۚ إِنَّ فَضْلَهُ كَانَ عَلَيْكَ كَبِيرًا', 17, 87, 15, 58, 291, 'مگر رحمت پروردگارت (شامل حالت گردد،) که فضل پروردگارت بر تو بزرگ بوده است!'),
(2117, 2117, 'قُلْ لَئِنِ اجْتَمَعَتِ الْإِنْسُ وَالْجِنُّ عَلَىٰ أَنْ يَأْتُوا بِمِثْلِ هَٰذَا الْقُرْآنِ لَا يَأْتُونَ بِمِثْلِهِ وَلَوْ كَانَ بَعْضُهُمْ لِبَعْضٍ ظَهِيرًا', 17, 88, 15, 58, 291, 'بگو: «اگر انسانها و پریان (جن و انس) اتفاق کنند که همانند این قرآن را بیاورند، همانند آن را نخواهند آورد؛ هر چند یکدیگر را (در این کار) کمک کنند.'),
(2118, 2118, 'وَلَقَدْ صَرَّفْنَا لِلنَّاسِ فِي هَٰذَا الْقُرْآنِ مِنْ كُلِّ مَثَلٍ فَأَبَىٰ أَكْثَرُ النَّاسِ إِلَّا كُفُورًا', 17, 89, 15, 58, 291, 'ما در این قرآن، برای مردم از هر چیز نمونه‌ای آوردیم (و همه معارف در آن جمع است)؛ اما بیشتر مردم (در برابر آن، از هر کاری) جز انکار، ابا داشتند!'),
(2119, 2119, 'وَقَالُوا لَنْ نُؤْمِنَ لَكَ حَتَّىٰ تَفْجُرَ لَنَا مِنَ الْأَرْضِ يَنْبُوعًا', 17, 90, 15, 58, 291, 'و گفتند: «ما هرگز به تو ایمان نمی‌آوریم تا اینکه چشمه‌جوشانی از این سرزمین (خشک و سوزان) برای ما خارج سازی...'),
(2120, 2120, 'أَوْ تَكُونَ لَكَ جَنَّةٌ مِنْ نَخِيلٍ وَعِنَبٍ فَتُفَجِّرَ الْأَنْهَارَ خِلَالَهَا تَفْجِيرًا', 17, 91, 15, 58, 291, 'یا باغی از نخل و انگور از آن تو باشد؛ و نهرها در لابه‌لای آن جاری کنی...'),
(2121, 2121, 'أَوْ تُسْقِطَ السَّمَاءَ كَمَا زَعَمْتَ عَلَيْنَا كِسَفًا أَوْ تَأْتِيَ بِاللَّهِ وَالْمَلَائِكَةِ قَبِيلًا', 17, 92, 15, 58, 291, 'یا قطعات (سنگهای) آسمان را -آنچنان که می‌پنداری- بر سر ما فرود آری؛ یا خداوند و فرشتگان را در برابر ما بیاوری...'),
(2122, 2122, 'أَوْ يَكُونَ لَكَ بَيْتٌ مِنْ زُخْرُفٍ أَوْ تَرْقَىٰ فِي السَّمَاءِ وَلَنْ نُؤْمِنَ لِرُقِيِّكَ حَتَّىٰ تُنَزِّلَ عَلَيْنَا كِتَابًا نَقْرَؤُهُ ۗ قُلْ سُبْحَانَ رَبِّي هَلْ كُنْتُ إِلَّا بَشَرًا رَسُولًا', 17, 93, 15, 58, 291, 'یا برای تو خانه‌ای پر نقش و نگار از طلا باشد؛ یا به آسمان بالا روی؛ حتی اگر به آسمان روی، ایمان نمی‌آوریم مگر آنکه نامه‌ای بر ما فرود آوری که آن را بخوانیم! «بگو:» منزه است پروردگارم (از این سخنان بی‌معنی)! مگر من جز انسانی فرستاده خدا هستم؟!'),
(2123, 2123, 'وَمَا مَنَعَ النَّاسَ أَنْ يُؤْمِنُوا إِذْ جَاءَهُمُ الْهُدَىٰ إِلَّا أَنْ قَالُوا أَبَعَثَ اللَّهُ بَشَرًا رَسُولًا', 17, 94, 15, 58, 291, 'تنها چیزی که بعد از آمدن هدایت مانع شد مردم ایمان بیاورند، این بود (که از روی نادانی و بی‌خبری) گفتند: «آیا خداوند بشری را بعنوان رسول فرستاده است؟!»'),
(2124, 2124, 'قُلْ لَوْ كَانَ فِي الْأَرْضِ مَلَائِكَةٌ يَمْشُونَ مُطْمَئِنِّينَ لَنَزَّلْنَا عَلَيْهِمْ مِنَ السَّمَاءِ مَلَكًا رَسُولًا', 17, 95, 15, 58, 291, 'بگو: «(حتی) اگر در روی زمین فرشتگانی (زندگی می‌کردند، و) با آرامش گام برمی‌داشتند، ما فرشته‌ای را به عنوان رسول، بر آنها می‌فرستادیم!» (چرا که رهنمای هر گروهی باید از جنس خودشان باشد).'),
(2125, 2125, 'قُلْ كَفَىٰ بِاللَّهِ شَهِيدًا بَيْنِي وَبَيْنَكُمْ ۚ إِنَّهُ كَانَ بِعِبَادِهِ خَبِيرًا بَصِيرًا', 17, 96, 15, 58, 291, 'بگو: «همین کافی است که خداوند، میان من و شما گواه باشد؛ چرا که او نسبت به بندگانش آگاه و بیناست!'),
(2126, 2126, 'وَمَنْ يَهْدِ اللَّهُ فَهُوَ الْمُهْتَدِ ۖ وَمَنْ يُضْلِلْ فَلَنْ تَجِدَ لَهُمْ أَوْلِيَاءَ مِنْ دُونِهِ ۖ وَنَحْشُرُهُمْ يَوْمَ الْقِيَامَةِ عَلَىٰ وُجُوهِهِمْ عُمْيًا وَبُكْمًا وَصُمًّا ۖ مَأْوَاهُمْ جَهَنَّمُ ۖ كُلَّمَا خَبَتْ زِدْنَاهُمْ سَعِيرًا', 17, 97, 15, 58, 292, 'هر کس را خدا هدایت کند، هدایت یافته واقعی اوست؛ و هر کس را (بخاطر اعمالش) گمراه سازد، هادیان و سرپرستانی غیر خدا برای او نخواهی یافت؛ و روز قیامت، آنها را بر صورتهایشان محشور می‌کنیم، در حالی که نابینا و گنگ و کرند؛ جایگاهشان دوزخ است؛ هر زمان آتش آن فرونشیند، شعله تازه‌ای بر آنان می‌افزاییم!'),
(2127, 2127, 'ذَٰلِكَ جَزَاؤُهُمْ بِأَنَّهُمْ كَفَرُوا بِآيَاتِنَا وَقَالُوا أَإِذَا كُنَّا عِظَامًا وَرُفَاتًا أَإِنَّا لَمَبْعُوثُونَ خَلْقًا جَدِيدًا', 17, 98, 15, 58, 292, 'این کیفر آنهاست، بخاطر اینکه نسبت به آیات ما کافر شدند و گفتند: «آیا هنگامی که ما استخوانهای پوسیده و خاکهای پراکنده‌ای شدیم، بار دیگر آفرینش تازه‌ای خواهیم یافت؟!»'),
(2128, 2128, ' أَوَلَمْ يَرَوْا أَنَّ اللَّهَ الَّذِي خَلَقَ السَّمَاوَاتِ وَالْأَرْضَ قَادِرٌ عَلَىٰ أَنْ يَخْلُقَ مِثْلَهُمْ وَجَعَلَ لَهُمْ أَجَلًا لَا رَيْبَ فِيهِ فَأَبَى الظَّالِمُونَ إِلَّا كُفُورًا', 17, 99, 15, 59, 292, 'آیا نمی‌دانند خدایی که آسمانها و زمین را آفریده، قادر است مثل آنان را بیافریند (و به زندگی جدید بازشان گرداند)؟! و برای آنان سرآمدی قطعی -که شکّی در آن نیست- قرار داده؛ امّا ظالمان، جز کفر و انکار را پذیرا نیستند!'),
(2129, 2129, 'قُلْ لَوْ أَنْتُمْ تَمْلِكُونَ خَزَائِنَ رَحْمَةِ رَبِّي إِذًا لَأَمْسَكْتُمْ خَشْيَةَ الْإِنْفَاقِ ۚ وَكَانَ الْإِنْسَانُ قَتُورًا', 17, 100, 15, 59, 292, 'بگو: «اگر شما مالک خزائن رحمت پروردگار من بودید. در آن صورت، (بخاطر تنگ نظری) امساک می‌کردید، مبادا انفاق، مایه تنگدستی شما شود» و انسان تنگ نظر است!'),
(2130, 2130, 'وَلَقَدْ آتَيْنَا مُوسَىٰ تِسْعَ آيَاتٍ بَيِّنَاتٍ ۖ فَاسْأَلْ بَنِي إِسْرَائِيلَ إِذْ جَاءَهُمْ فَقَالَ لَهُ فِرْعَوْنُ إِنِّي لَأَظُنُّكَ يَا مُوسَىٰ مَسْحُورًا', 17, 101, 15, 59, 292, 'ما به موسی نه معجزه روشن دادیم؛ پس از بنی اسرائیل سؤال کن آن زمان که این (معجزات نه گانه) به سراغ آنها آمد (چگونه بودند)؟! فرعون به او گفت: «ای موسی! گمان می‌کنم تو دیوانه (یا ساحری)!»'),
(2131, 2131, 'قَالَ لَقَدْ عَلِمْتَ مَا أَنْزَلَ هَٰؤُلَاءِ إِلَّا رَبُّ السَّمَاوَاتِ وَالْأَرْضِ بَصَائِرَ وَإِنِّي لَأَظُنُّكَ يَا فِرْعَوْنُ مَثْبُورًا', 17, 102, 15, 59, 292, '(موسی) گفت: «تو می‌دانی این آیات را جز پروردگار آسمانها و زمین -برای روشنی دلها- نفرستاده؛ و من گمان می‌کنم ای فرعون، تو (بزودی) هلاک خواهی شد!»'),
(2132, 2132, 'فَأَرَادَ أَنْ يَسْتَفِزَّهُمْ مِنَ الْأَرْضِ فَأَغْرَقْنَاهُ وَمَنْ مَعَهُ جَمِيعًا', 17, 103, 15, 59, 292, 'پس (فرعون) تصمیم گرفت آنان را از آن سرزمین ریشه کن سازد؛ ولی ما، او و تمام کسانی را که با او بودند، غرق کردیم.'),
(2133, 2133, 'وَقُلْنَا مِنْ بَعْدِهِ لِبَنِي إِسْرَائِيلَ اسْكُنُوا الْأَرْضَ فَإِذَا جَاءَ وَعْدُ الْآخِرَةِ جِئْنَا بِكُمْ لَفِيفًا', 17, 104, 15, 59, 292, 'و بعد از آن به بنی اسرائیل گفتیم: «در این سرزمین = مصر و شام‌ ساکن شوید! امّا هنگامی که وعده آخرت فرا رسد، همه شما را دسته جمعی (به آن دادگاه عدل) می‌آوریم»'),
(2134, 2134, 'وَبِالْحَقِّ أَنْزَلْنَاهُ وَبِالْحَقِّ نَزَلَ ۗ وَمَا أَرْسَلْنَاكَ إِلَّا مُبَشِّرًا وَنَذِيرًا', 17, 105, 15, 59, 293, 'و ما قرآن را بحق نازل کردیم؛ و بحق نازل شد؛ و تو را، جز بعنوان بشارت‌دهنده و بیم‌دهنده، نفرستادیم!'),
(2135, 2135, 'وَقُرْآنًا فَرَقْنَاهُ لِتَقْرَأَهُ عَلَى النَّاسِ عَلَىٰ مُكْثٍ وَنَزَّلْنَاهُ تَنْزِيلًا', 17, 106, 15, 59, 293, 'و قرآنی که آیاتش را از هم جدا کردیم، تا آن را با درنگ بر مردم بخوانی؛ و آن را بتدریج نازل کردیم.'),
(2136, 2136, 'قُلْ آمِنُوا بِهِ أَوْ لَا تُؤْمِنُوا ۚ إِنَّ الَّذِينَ أُوتُوا الْعِلْمَ مِنْ قَبْلِهِ إِذَا يُتْلَىٰ عَلَيْهِمْ يَخِرُّونَ لِلْأَذْقَانِ سُجَّدًا', 17, 107, 15, 59, 293, 'بگو: «خواه به آن ایمان بیاورید، و خواه ایمان نیاورید، کسانی که پیش از آن به آنها دانش داده شده، هنگامی که (این آیات) بر آنان خوانده می‌شود، سجده‌کنان به خاک می‌افتند...'),
(2137, 2137, 'وَيَقُولُونَ سُبْحَانَ رَبِّنَا إِنْ كَانَ وَعْدُ رَبِّنَا لَمَفْعُولًا', 17, 108, 15, 59, 293, 'و می‌گویند: «منزّه است پروردگار ما، که وعده‌هایش به یقین انجام‌شدنی است!»'),
(2138, 2138, 'وَيَخِرُّونَ لِلْأَذْقَانِ يَبْكُونَ وَيَزِيدُهُمْ خُشُوعًا ۩', 17, 109, 15, 59, 293, 'آنها (بی‌اختیار) به زمین می‌افتند و گریه می‌کنند؛ و (تلاوت این آیات، همواره) بر خشوعشان می‌افزاید.'),
(2139, 2139, 'قُلِ ادْعُوا اللَّهَ أَوِ ادْعُوا الرَّحْمَٰنَ ۖ أَيًّا مَا تَدْعُوا فَلَهُ الْأَسْمَاءُ الْحُسْنَىٰ ۚ وَلَا تَجْهَرْ بِصَلَاتِكَ وَلَا تُخَافِتْ بِهَا وَابْتَغِ بَيْنَ ذَٰلِكَ سَبِيلًا', 17, 110, 15, 59, 293, 'بگو: ««اللّه» را بخوانید یا «رحمان» را، هر کدام را بخوانید، (ذات پاکش یکی است؛ و) برای او بهترین نامهاست!» و نمازت را زیاد بلند، یا خیلی آهسته نخوان؛ و در میان آن دو، راهی (معتدل) انتخاب کن!'),
(2140, 2140, 'وَقُلِ الْحَمْدُ لِلَّهِ الَّذِي لَمْ يَتَّخِذْ وَلَدًا وَلَمْ يَكُنْ لَهُ شَرِيكٌ فِي الْمُلْكِ وَلَمْ يَكُنْ لَهُ وَلِيٌّ مِنَ الذُّلِّ ۖ وَكَبِّرْهُ تَكْبِيرًا', 17, 111, 15, 59, 293, 'و بگو: «ستایش برای خداوندی است که نه فرزندی برای خود انتخاب کرده، و نه شریکی در حکومت دارد، و نه بخاطر ضعف و ذلّت، (حامی و) سرپرستی برای اوست!» و او را بسیار بزرگ بشمار!'),
(2141, 2141, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ الْحَمْدُ لِلَّهِ الَّذِي أَنْزَلَ عَلَىٰ عَبْدِهِ الْكِتَابَ وَلَمْ يَجْعَلْ لَهُ عِوَجًا ۜ', 18, 1, 15, 59, 293, 'حمد مخصوص خدایی است که این کتاب (آسمانی) را بر بنده (برگزیده) اش نازل کرد، و هیچ گونه کژی در آن قرار نداد...'),
(2142, 2142, 'قَيِّمًا لِيُنْذِرَ بَأْسًا شَدِيدًا مِنْ لَدُنْهُ وَيُبَشِّرَ الْمُؤْمِنِينَ الَّذِينَ يَعْمَلُونَ الصَّالِحَاتِ أَنَّ لَهُمْ أَجْرًا حَسَنًا', 18, 2, 15, 59, 293, 'در حالی که ثابت و مستقیم و نگاهبان کتابهای (آسمانی) دیگر است؛ تا (بدکاران را) از عذاب شدید او بترساند؛ و مؤمنانی را که کارهای شایسته انجام می‌دهند، بشارت دهد که پاداش نیکویی برای آنهاست...'),
(2143, 2143, 'مَاكِثِينَ فِيهِ أَبَدًا', 18, 3, 15, 59, 293, '(همان بهشت برین) که جاودانه در آن خواهند ماند!'),
(2144, 2144, 'وَيُنْذِرَ الَّذِينَ قَالُوا اتَّخَذَ اللَّهُ وَلَدًا', 18, 4, 15, 59, 293, 'و (نیز) آنها را که گفتند: «خداوند، فرزندی (برای خود) انتخاب کرده است»، انذار کند.'),
(2145, 2145, 'مَا لَهُمْ بِهِ مِنْ عِلْمٍ وَلَا لِآبَائِهِمْ ۚ كَبُرَتْ كَلِمَةً تَخْرُجُ مِنْ أَفْوَاهِهِمْ ۚ إِنْ يَقُولُونَ إِلَّا كَذِبًا', 18, 5, 15, 59, 294, 'نه آنها (هرگز) به این سخن یقین دارند، و نه پدرانشان! سخن بزرگی از دهانشان خارج می‌شود! آنها فقط دروغ می‌گویند!'),
(2146, 2146, 'فَلَعَلَّكَ بَاخِعٌ نَفْسَكَ عَلَىٰ آثَارِهِمْ إِنْ لَمْ يُؤْمِنُوا بِهَٰذَا الْحَدِيثِ أَسَفًا', 18, 6, 15, 59, 294, ' گویی می‌خواهی بخاطر اعمال آنان، خود را از غم و اندوه هلاک کنی اگر به این گفتار ایمان نیاورند!'),
(2147, 2147, 'إِنَّا جَعَلْنَا مَا عَلَى الْأَرْضِ زِينَةً لَهَا لِنَبْلُوَهُمْ أَيُّهُمْ أَحْسَنُ عَمَلًا', 18, 7, 15, 59, 294, 'ما آنچه را روی زمین است زینت آن قرار دادیم، تا آنها را بیازماییم که کدامینشان بهتر عمل می‌کنند!'),
(2148, 2148, 'وَإِنَّا لَجَاعِلُونَ مَا عَلَيْهَا صَعِيدًا جُرُزًا', 18, 8, 15, 59, 294, '(ولی) این زرق و برقها پایدار نیست، و ما (سرانجام) قشر روی زمین را خاک بی گیاهی قرار می‌دهیم!'),
(2149, 2149, 'أَمْ حَسِبْتَ أَنَّ أَصْحَابَ الْكَهْفِ وَالرَّقِيمِ كَانُوا مِنْ آيَاتِنَا عَجَبًا', 18, 9, 15, 59, 294, 'آیا گمان کردی اصحاب کهف و رقیم از آیات عجیب ما بودند؟!'),
(2150, 2150, 'إِذْ أَوَى الْفِتْيَةُ إِلَى الْكَهْفِ فَقَالُوا رَبَّنَا آتِنَا مِنْ لَدُنْكَ رَحْمَةً وَهَيِّئْ لَنَا مِنْ أَمْرِنَا رَشَدًا', 18, 10, 15, 59, 294, 'زمانی را به خاطر بیاور که آن جوانان به غار پناه بردند، و گفتند: «پروردگارا! ما را از سوی خودت رحمتی عطا کن، و راه نجاتی برای ما فراهم ساز!»'),
(2151, 2151, 'فَضَرَبْنَا عَلَىٰ آذَانِهِمْ فِي الْكَهْفِ سِنِينَ عَدَدًا', 18, 11, 15, 59, 294, 'ما (پرده خواب را) در غار بر گوششان زدیم، و سالها در خواب فرو رفتند.'),
(2152, 2152, 'ثُمَّ بَعَثْنَاهُمْ لِنَعْلَمَ أَيُّ الْحِزْبَيْنِ أَحْصَىٰ لِمَا لَبِثُوا أَمَدًا', 18, 12, 15, 59, 294, 'سپس آنان را برانگیختیم تا بدانیم (و این امر آشکار گردد که) کدام یک از آن دو گروه، مدّت خواب خود را بهتر حساب کرده‌اند.'),
(2153, 2153, 'نَحْنُ نَقُصُّ عَلَيْكَ نَبَأَهُمْ بِالْحَقِّ ۚ إِنَّهُمْ فِتْيَةٌ آمَنُوا بِرَبِّهِمْ وَزِدْنَاهُمْ هُدًى', 18, 13, 15, 59, 294, 'ما داستان آنان را بحق برای تو بازگو می‌کنیم؛ آنها جوانانی بودند که به پروردگارشان ایمان آوردند، و ما بر هدایتشان افزودیم.'),
(2154, 2154, 'وَرَبَطْنَا عَلَىٰ قُلُوبِهِمْ إِذْ قَامُوا فَقَالُوا رَبُّنَا رَبُّ السَّمَاوَاتِ وَالْأَرْضِ لَنْ نَدْعُوَ مِنْ دُونِهِ إِلَٰهًا ۖ لَقَدْ قُلْنَا إِذًا شَطَطًا', 18, 14, 15, 59, 294, 'و دلهایشان را محکم ساختیم در آن موقع که قیام کردند و گفتند: «پروردگار ما، پروردگار آسمانها و زمین است؛ هرگز غیر او معبودی را نمی‌خوانیم؛ که اگر چنین کنیم، سخنی بگزاف گفته‌ایم.'),
(2155, 2155, 'هَٰؤُلَاءِ قَوْمُنَا اتَّخَذُوا مِنْ دُونِهِ آلِهَةً ۖ لَوْلَا يَأْتُونَ عَلَيْهِمْ بِسُلْطَانٍ بَيِّنٍ ۖ فَمَنْ أَظْلَمُ مِمَّنِ افْتَرَىٰ عَلَى اللَّهِ كَذِبًا', 18, 15, 15, 59, 294, 'این قوم ما هستند که معبودهایی جز خدا انتخاب کرده‌اند؛ چرا دلیل آشکاری (بر این کار) نمی‌آورند؟! و چه کسی ظالمتر است از آن کس که بر خدا دروغ ببندد؟!»'),
(2156, 2156, 'وَإِذِ اعْتَزَلْتُمُوهُمْ وَمَا يَعْبُدُونَ إِلَّا اللَّهَ فَأْوُوا إِلَى الْكَهْفِ يَنْشُرْ لَكُمْ رَبُّكُمْ مِنْ رَحْمَتِهِ وَيُهَيِّئْ لَكُمْ مِنْ أَمْرِكُمْ مِرْفَقًا', 18, 16, 15, 59, 295, 'و (به آنها گفتیم:) هنگامی که از آنان و آنچه جز خدا می‌پرستند کناره‌گیری کردید، به غار پناه برید؛ که پروردگارتان (سایه) رحمتش را بر شما می‌گستراند؛ و در این امر، آرامشی برای شما فراهم می‌سازد!'),
(2157, 2157, ' وَتَرَى الشَّمْسَ إِذَا طَلَعَتْ تَزَاوَرُ عَنْ كَهْفِهِمْ ذَاتَ الْيَمِينِ وَإِذَا غَرَبَتْ تَقْرِضُهُمْ ذَاتَ الشِّمَالِ وَهُمْ فِي فَجْوَةٍ مِنْهُ ۚ ذَٰلِكَ مِنْ آيَاتِ اللَّهِ ۗ مَنْ يَهْدِ اللَّهُ فَهُوَ الْمُهْتَدِ ۖ وَمَنْ يُضْلِلْ فَلَنْ تَجِدَ لَهُ وَلِيًّا مُرْشِدًا', 18, 17, 15, 59, 295, 'و (اگر در آنجا بودی) خورشید را می‌دیدی که به هنگام طلوع، به سمت راست غارشان متمایل می‌گردد؛ و به هنگام غروب، به سمت چپ؛ و آنها در محل وسیعی از آن (غار) قرار داشتند؛ این از آیات خداست! هر کس را خدا هدایت کند، هدایت یافته واقعی اوست؛ و هر کس را گمراه نماید، هرگز ولیّ و راهنمایی برای او نخواهی یافت!'),
(2158, 2158, 'وَتَحْسَبُهُمْ أَيْقَاظًا وَهُمْ رُقُودٌ ۚ وَنُقَلِّبُهُمْ ذَاتَ الْيَمِينِ وَذَاتَ الشِّمَالِ ۖ وَكَلْبُهُمْ بَاسِطٌ ذِرَاعَيْهِ بِالْوَصِيدِ ۚ لَوِ اطَّلَعْتَ عَلَيْهِمْ لَوَلَّيْتَ مِنْهُمْ فِرَارًا وَلَمُلِئْتَ مِنْهُمْ رُعْبًا', 18, 18, 15, 59, 295, 'و (اگر به آنها نگاه می‌کردی) می‌پنداشتی بیدارند؛ در حالی که در خواب فرو رفته بودند! و ما آنها را به سمت راست و چپ میگرداندیم (تا بدنشان سالم بماند). و سگ آنها دستهای خود را بر دهانه غار گشوده بود (و نگهبانی می‌کرد). اگر نگاهشان می‌کردی، از آنان می‌گریختی؛ و سر تا پای تو از ترس و وحشت پر می‌شد!'),
(2159, 2159, 'وَكَذَٰلِكَ بَعَثْنَاهُمْ لِيَتَسَاءَلُوا بَيْنَهُمْ ۚ قَالَ قَائِلٌ مِنْهُمْ كَمْ لَبِثْتُمْ ۖ قَالُوا لَبِثْنَا يَوْمًا أَوْ بَعْضَ يَوْمٍ ۚ قَالُوا رَبُّكُمْ أَعْلَمُ بِمَا لَبِثْتُمْ فَابْعَثُوا أَحَدَكُمْ بِوَرِقِكُمْ هَٰذِهِ إِلَى الْمَدِينَةِ فَلْيَنْظُرْ أَيُّهَا أَزْكَىٰ طَعَامًا فَلْيَأْتِكُمْ بِرِزْقٍ مِنْهُ وَلْيَتَلَطَّفْ وَلَا يُشْعِرَنَّ بِكُمْ أَحَدًا', 18, 19, 15, 59, 295, 'این‌گونه آنها را (از خواب) برانگیختیم تا از یکدیگر سؤال کنند؛ یکی از آنها گفت: «چه مدّت خوابیدید؟!» گفتند: «یک روز، یا بخشی از یک روز!» (و چون نتوانستند مدّت خوابشان را دقیقاً بدانند) گفتند: «پروردگارتان از مدّت خوابتان آگاهتر است! اکنون یک نفر از خودتان را با این سکّه‌ای که دارید به شهر بفرستید، تا بنگرد کدام یک از آنها غذای پاکیزه‌تری دارند، و مقداری از آن برای روزی شما بیاورد. امّا باید دقّت کند، و هیچ کس را از وضع شما آگاه نسازد...'),
(2160, 2160, 'إِنَّهُمْ إِنْ يَظْهَرُوا عَلَيْكُمْ يَرْجُمُوكُمْ أَوْ يُعِيدُوكُمْ فِي مِلَّتِهِمْ وَلَنْ تُفْلِحُوا إِذًا أَبَدًا', 18, 20, 15, 59, 295, 'چرا که اگر آنان از وضع شما آگاه شوند، سنگسارتان می‌کنند؛ یا شما را به آیین خویش بازمی‌گردانند؛ و در آن صورت، هرگز روی رستگاری را نخواهید دید!»'),
(2161, 2161, 'وَكَذَٰلِكَ أَعْثَرْنَا عَلَيْهِمْ لِيَعْلَمُوا أَنَّ وَعْدَ اللَّهِ حَقٌّ وَأَنَّ السَّاعَةَ لَا رَيْبَ فِيهَا إِذْ يَتَنَازَعُونَ بَيْنَهُمْ أَمْرَهُمْ ۖ فَقَالُوا ابْنُوا عَلَيْهِمْ بُنْيَانًا ۖ رَبُّهُمْ أَعْلَمُ بِهِمْ ۚ قَالَ الَّذِينَ غَلَبُوا عَلَىٰ أَمْرِهِمْ لَنَتَّخِذَنَّ عَلَيْهِمْ مَسْجِدًا', 18, 21, 15, 59, 296, 'و اینچنین مردم را متوّجه حال آنها کردیم، تا بدانند که وعده خداوند (در مورد رستاخیز) حقّ است؛ و در پایان جهان و قیام قیامت شکّی نیست! در آن هنگام که میان خود درباره کار خویش نزاع داشتند، گروهی می‌گفتند: «بنایی بر آنان بسازید (تا برای همیشه از نظر پنهان شوند! و از آنها سخن نگویید که) پروردگارشان از وضع آنها آگاهتر است!» ولی آنها که از رازشان آگاهی یافتند (و آن را دلیلی بر رستاخیز دیدند) گفتند: «ما مسجدی در کنار (مدفن) آنها می‌سازیم (تا خاطره آنان فراموش نشود.)»'),
(2162, 2162, 'سَيَقُولُونَ ثَلَاثَةٌ رَابِعُهُمْ كَلْبُهُمْ وَيَقُولُونَ خَمْسَةٌ سَادِسُهُمْ كَلْبُهُمْ رَجْمًا بِالْغَيْبِ ۖ وَيَقُولُونَ سَبْعَةٌ وَثَامِنُهُمْ كَلْبُهُمْ ۚ قُلْ رَبِّي أَعْلَمُ بِعِدَّتِهِمْ مَا يَعْلَمُهُمْ إِلَّا قَلِيلٌ ۗ فَلَا تُمَارِ فِيهِمْ إِلَّا مِرَاءً ظَاهِرًا وَلَا تَسْتَفْتِ فِيهِمْ مِنْهُمْ أَحَدًا', 18, 22, 15, 59, 296, 'گروهی خواهند گفت: «آنها سه نفر بودند، که چهارمین آنها سگشان بود!» و گروهی می‌گویند: «پنچ نفر بودند، که ششمین آنها سگشان بود.» -همه اینها سخنانی بی‌دلیل است- و گروهی می‌گویند: «آنها هفت نفر بودند، و هشتمین آنها سگشان بود.» بگو: «پروردگار من از تعدادشان آگاهتر است!» جز گروه کمی، تعداد آنها را نمی‌دانند. پس درباره آنان جز با دلیل سخن مگو؛ و از هیچ کس درباره آنها سؤال مکن!'),
(2163, 2163, 'وَلَا تَقُولَنَّ لِشَيْءٍ إِنِّي فَاعِلٌ ذَٰلِكَ غَدًا', 18, 23, 15, 59, 296, 'و هرگز در مورد کاری نگو: «من فردا آن را انجام می‌دهم»...'),
(2164, 2164, 'إِلَّا أَنْ يَشَاءَ اللَّهُ ۚ وَاذْكُرْ رَبَّكَ إِذَا نَسِيتَ وَقُلْ عَسَىٰ أَنْ يَهْدِيَنِ رَبِّي لِأَقْرَبَ مِنْ هَٰذَا رَشَدًا', 18, 24, 15, 59, 296, 'مگر اینکه خدا بخواهد! و هرگاه فراموش کردی، (جبران کن) و پروردگارت را به خاطر بیاور؛ و بگو: «امیدوارم که پروردگارم مرا به راهی روشنتر از این هدایت کند!»'),
(2165, 2165, 'وَلَبِثُوا فِي كَهْفِهِمْ ثَلَاثَ مِائَةٍ سِنِينَ وَازْدَادُوا تِسْعًا', 18, 25, 15, 59, 296, 'آنها در غارشان سیصد سال درنگ کردند، و نه سال (نیز) بر آن افزودند.'),
(2166, 2166, 'قُلِ اللَّهُ أَعْلَمُ بِمَا لَبِثُوا ۖ لَهُ غَيْبُ السَّمَاوَاتِ وَالْأَرْضِ ۖ أَبْصِرْ بِهِ وَأَسْمِعْ ۚ مَا لَهُمْ مِنْ دُونِهِ مِنْ وَلِيٍّ وَلَا يُشْرِكُ فِي حُكْمِهِ أَحَدًا', 18, 26, 15, 59, 296, 'بگو: «خداوند از مدّت توقفشان آگاهتر است؛ غیب آسمانها و زمین از آن اوست! راستی چه بینا و شنواست! آنها هیچ ولیّ و سرپرستی جز او ندارند! و او هیچ کس را در حکم خود شرکت نمی‌دهد!»'),
(2167, 2167, 'وَاتْلُ مَا أُوحِيَ إِلَيْكَ مِنْ كِتَابِ رَبِّكَ ۖ لَا مُبَدِّلَ لِكَلِمَاتِهِ وَلَنْ تَجِدَ مِنْ دُونِهِ مُلْتَحَدًا', 18, 27, 15, 59, 296, 'آنچه را از کتاب پروردگارت به تو وحی شده تلاوت کن! هیچ چیز سخنان او را دگرگون نمی‌سازد؛ و هرگز پناهگاهی جز او نمی‌یابی!'),
(2168, 2168, 'وَاصْبِرْ نَفْسَكَ مَعَ الَّذِينَ يَدْعُونَ رَبَّهُمْ بِالْغَدَاةِ وَالْعَشِيِّ يُرِيدُونَ وَجْهَهُ ۖ وَلَا تَعْدُ عَيْنَاكَ عَنْهُمْ تُرِيدُ زِينَةَ الْحَيَاةِ الدُّنْيَا ۖ وَلَا تُطِعْ مَنْ أَغْفَلْنَا قَلْبَهُ عَنْ ذِكْرِنَا وَاتَّبَعَ هَوَاهُ وَكَانَ أَمْرُهُ فُرُطًا', 18, 28, 15, 59, 297, 'با کسانی باش که پروردگار خود را صبح و عصر می‌خوانند، و تنها رضای او را می‌طلبند! و هرگز بخاطر زیورهای دنیا، چشمان خود را از آنها برمگیر! و از کسانی که قلبشان را از یاد خود غافل ساختیم اطاعت مکن! همانها که از هوای نفس پیروی کردند، و کارهایشان افراطی است.'),
(2169, 2169, 'وَقُلِ الْحَقُّ مِنْ رَبِّكُمْ ۖ فَمَنْ شَاءَ فَلْيُؤْمِنْ وَمَنْ شَاءَ فَلْيَكْفُرْ ۚ إِنَّا أَعْتَدْنَا لِلظَّالِمِينَ نَارًا أَحَاطَ بِهِمْ سُرَادِقُهَا ۚ وَإِنْ يَسْتَغِيثُوا يُغَاثُوا بِمَاءٍ كَالْمُهْلِ يَشْوِي الْوُجُوهَ ۚ بِئْسَ الشَّرَابُ وَسَاءَتْ مُرْتَفَقًا', 18, 29, 15, 59, 297, 'بگو: «این حقّ است از سوی پروردگارتان! هر کس می‌خواهد ایمان بیاورد (و این حقیقت را پذیرا شود)، و هر کس میخواهد کافر گردد!» ما برای ستمگران آتشی آماده کردیم که سراپرده‌اش آنان را از هر سو احاطه کرده است! و اگر تقاضای آب کنند، آبی برای آنان میاورند که همچون فلز گداخته صورتها را بریان می‌کند! چه بد نوشیدنی، و چه بد محل اجتماعی است!'),
(2170, 2170, 'إِنَّ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ إِنَّا لَا نُضِيعُ أَجْرَ مَنْ أَحْسَنَ عَمَلًا', 18, 30, 15, 59, 297, 'مسلّماً کسانی که ایمان آوردند و کارهای شایسته انجام دادند، ما پاداش نیکوکاران را ضایع نخواهیم کرد!'),
(2171, 2171, 'أُولَٰئِكَ لَهُمْ جَنَّاتُ عَدْنٍ تَجْرِي مِنْ تَحْتِهِمُ الْأَنْهَارُ يُحَلَّوْنَ فِيهَا مِنْ أَسَاوِرَ مِنْ ذَهَبٍ وَيَلْبَسُونَ ثِيَابًا خُضْرًا مِنْ سُنْدُسٍ وَإِسْتَبْرَقٍ مُتَّكِئِينَ فِيهَا عَلَى الْأَرَائِكِ ۚ نِعْمَ الثَّوَابُ وَحَسُنَتْ مُرْتَفَقًا', 18, 31, 15, 59, 297, 'آنها کسانی هستند که بهشت جاودان برای آنان است؛ باغهایی از بهشت که نهرها از زیر درختان و قصرهایش جاری است؛ در آنجا با دستبندهایی از طلا آراسته می‌شوند؛ و لباسهایی (فاخر) به رنگ سبز، از حریر نازک و ضخیم، دربر می‌کنند؛ در حالی که بر تختها تکیه کرده‌اند. چه پاداش خوبی، و چه جمع نیکویی!'),
(2172, 2172, ' وَاضْرِبْ لَهُمْ مَثَلًا رَجُلَيْنِ جَعَلْنَا لِأَحَدِهِمَا جَنَّتَيْنِ مِنْ أَعْنَابٍ وَحَفَفْنَاهُمَا بِنَخْلٍ وَجَعَلْنَا بَيْنَهُمَا زَرْعًا', 18, 32, 15, 60, 297, '(ای پیامبر!) برای آنان مثالی بزن: آن دو مرد، که برای یکی از آنها دو باغ از انواع انگورها قرار دادیم؛ و گرداگرد آن دو (باغ) را با درختان نخل پوشاندیم؛ و در میانشان زراعت پر برکتی قراردادیم.'),
(2173, 2173, 'كِلْتَا الْجَنَّتَيْنِ آتَتْ أُكُلَهَا وَلَمْ تَظْلِمْ مِنْهُ شَيْئًا ۚ وَفَجَّرْنَا خِلَالَهُمَا نَهَرًا', 18, 33, 15, 60, 297, 'هر دو باغ، میوه آورده بود، (میوه‌های فراوان،) و چیزی فروگذار نکرده بود؛ و میان آن دو، نهر بزرگی جاری ساخته بودیم.'),
(2174, 2174, 'وَكَانَ لَهُ ثَمَرٌ فَقَالَ لِصَاحِبِهِ وَهُوَ يُحَاوِرُهُ أَنَا أَكْثَرُ مِنْكَ مَالًا وَأَعَزُّ نَفَرًا', 18, 34, 15, 60, 297, 'صاحب این باغ، درآمد فراوانی داشت؛ به همین جهت، به دوستش -در حالی که با او گفتگو می‌کرد- چنین گفت: «من از نظر ثروت از تو برتر، و از نظر نفرات نیرومندترم!»'),
(2175, 2175, 'وَدَخَلَ جَنَّتَهُ وَهُوَ ظَالِمٌ لِنَفْسِهِ قَالَ مَا أَظُنُّ أَنْ تَبِيدَ هَٰذِهِ أَبَدًا', 18, 35, 15, 60, 298, 'و در حالی که نسبت به خود ستمکار بود، در باغ خویش گام نهاد، و گفت: «من گمان نمی‌کنم هرگز این باغ نابود شود!'),
(2176, 2176, 'وَمَا أَظُنُّ السَّاعَةَ قَائِمَةً وَلَئِنْ رُدِدْتُ إِلَىٰ رَبِّي لَأَجِدَنَّ خَيْرًا مِنْهَا مُنْقَلَبًا', 18, 36, 15, 60, 298, 'و باور نمی‌کنم قیامت برپا گردد! و اگر به سوی پروردگارم بازگردانده شوم (و قیامتی در کار باشد)، جایگاهی بهتر از این جا خواهم یافت!»'),
(2177, 2177, 'قَالَ لَهُ صَاحِبُهُ وَهُوَ يُحَاوِرُهُ أَكَفَرْتَ بِالَّذِي خَلَقَكَ مِنْ تُرَابٍ ثُمَّ مِنْ نُطْفَةٍ ثُمَّ سَوَّاكَ رَجُلًا', 18, 37, 15, 60, 298, 'دوست (با ایمان) وی -در حالی که با او گفتگو می‌کرد- گفت: «آیا به خدایی که تو را از خاک، و سپس از نطفه آفرید، و پس از آن تو را مرد کاملی قرار داد، کافر شدی؟!'),
(2178, 2178, 'لَٰكِنَّا هُوَ اللَّهُ رَبِّي وَلَا أُشْرِكُ بِرَبِّي أَحَدًا', 18, 38, 15, 60, 298, 'ولی من کسی هستم که «اللّه» پروردگار من است؛ و هیچ کس را شریک پروردگارم قرارنمی‌دهم!'),
(2179, 2179, 'وَلَوْلَا إِذْ دَخَلْتَ جَنَّتَكَ قُلْتَ مَا شَاءَ اللَّهُ لَا قُوَّةَ إِلَّا بِاللَّهِ ۚ إِنْ تَرَنِ أَنَا أَقَلَّ مِنْكَ مَالًا وَوَلَدًا', 18, 39, 15, 60, 298, 'چرا هنگامی که وارد باغت شدی، نگفتی این نعمتی است که خدا خواسته است؟! قوّت (و نیرویی) جز از ناحیه خدا نیست! و اگر می‌بینی من از نظر مال و فرزند از تو کمترم (مطلب مهمّی نیست)!'),
(2180, 2180, 'فَعَسَىٰ رَبِّي أَنْ يُؤْتِيَنِ خَيْرًا مِنْ جَنَّتِكَ وَيُرْسِلَ عَلَيْهَا حُسْبَانًا مِنَ السَّمَاءِ فَتُصْبِحَ صَعِيدًا زَلَقًا', 18, 40, 15, 60, 298, 'شاید پروردگارم بهتر از باغ تو به من بدهد؛ و مجازات حساب شده‌ای از آسمان بر باغ تو فروفرستد، بگونه‌ای که آن را به زمین بی‌گیاه لغزنده‌ای مبدّل کند!'),
(2181, 2181, 'أَوْ يُصْبِحَ مَاؤُهَا غَوْرًا فَلَنْ تَسْتَطِيعَ لَهُ طَلَبًا', 18, 41, 15, 60, 298, 'و یا آب آن در اعمال زمین فرو رود، آن گونه که هرگز نتوانی آن را به دست آوری!»'),
(2182, 2182, 'وَأُحِيطَ بِثَمَرِهِ فَأَصْبَحَ يُقَلِّبُ كَفَّيْهِ عَلَىٰ مَا أَنْفَقَ فِيهَا وَهِيَ خَاوِيَةٌ عَلَىٰ عُرُوشِهَا وَيَقُولُ يَا لَيْتَنِي لَمْ أُشْرِكْ بِرَبِّي أَحَدًا', 18, 42, 15, 60, 298, '(به هر حال عذاب الهی فرا رسید،) و تمام میوه‌های آن نابود شد؛ و او بخاطر هزینه هایی که در آن صرف کرده بود، پیوسته دستهای خود را به هم می‌مالید -در حالی که تمام باغ بر داربستهایش فرو ریخته بود- و می‌گفت: «ای کاش کسی را همتای پروردگارم قرار نداده بودم!»'),
(2183, 2183, 'وَلَمْ تَكُنْ لَهُ فِئَةٌ يَنْصُرُونَهُ مِنْ دُونِ اللَّهِ وَمَا كَانَ مُنْتَصِرًا', 18, 43, 15, 60, 298, 'و گروهی نداشت که او را در برابر (عذاب) خداوند یاری دهند؛ و از خودش (نیز) نمی‌توانست یاری گیرد.'),
(2184, 2184, 'هُنَالِكَ الْوَلَايَةُ لِلَّهِ الْحَقِّ ۚ هُوَ خَيْرٌ ثَوَابًا وَخَيْرٌ عُقْبًا', 18, 44, 15, 60, 298, 'در آنجا ثابت شد که ولایت (و قدرت) از آن خداوند بر حق است! اوست که برترین ثواب، و بهترین عاقبت را (برای مطیعان) دارد!'),
(2185, 2185, 'وَاضْرِبْ لَهُمْ مَثَلَ الْحَيَاةِ الدُّنْيَا كَمَاءٍ أَنْزَلْنَاهُ مِنَ السَّمَاءِ فَاخْتَلَطَ بِهِ نَبَاتُ الْأَرْضِ فَأَصْبَحَ هَشِيمًا تَذْرُوهُ الرِّيَاحُ ۗ وَكَانَ اللَّهُ عَلَىٰ كُلِّ شَيْءٍ مُقْتَدِرًا', 18, 45, 15, 60, 298, '(ای پیامبر!) زندگی دنیا را برای آنان به آبی تشبیه کن که از آسمان فرو می‌فرستیم؛ و بوسیله آن، گیاهان زمین (سرسبز می‌شود و) در هم فرومیرود. امّا بعد از مدتی می‌خشکد؛ و بادها آن را به هر سو پراکنده می‌کند؛ و خداوند بر همه چیز تواناست!'),
(2186, 2186, 'الْمَالُ وَالْبَنُونَ زِينَةُ الْحَيَاةِ الدُّنْيَا ۖ وَالْبَاقِيَاتُ الصَّالِحَاتُ خَيْرٌ عِنْدَ رَبِّكَ ثَوَابًا وَخَيْرٌ أَمَلًا', 18, 46, 15, 60, 299, 'مال و فرزند، زینت زندگی دنیاست؛ و باقیات صالحات = ارزشهای پایدار و شایسته‌ ثوابش نزد پروردگارت بهتر و امیدبخش‌تر است!'),
(2187, 2187, 'وَيَوْمَ نُسَيِّرُ الْجِبَالَ وَتَرَى الْأَرْضَ بَارِزَةً وَحَشَرْنَاهُمْ فَلَمْ نُغَادِرْ مِنْهُمْ أَحَدًا', 18, 47, 15, 60, 299, 'و روزی را به خاطر بیاور که کوه‌ها را به حرکت درآوریم؛ و زمین را آشکار (و مسطح) می‌بینی؛ و همه آنان = انسانها را برمی‌انگیزیم، و احدی از ایشان را فروگذار نخواهیم کرد!'),
(2188, 2188, 'وَعُرِضُوا عَلَىٰ رَبِّكَ صَفًّا لَقَدْ جِئْتُمُونَا كَمَا خَلَقْنَاكُمْ أَوَّلَ مَرَّةٍ ۚ بَلْ زَعَمْتُمْ أَلَّنْ نَجْعَلَ لَكُمْ مَوْعِدًا', 18, 48, 15, 60, 299, 'آنها همه در یک صف به (پیشگاه) پروردگارت عرضه می‌شوند؛ (و به آنان گفته می‌شود:) همگی نزد ما آمدید، همان گونه که نخستین بار شما را آفریدیم! اما شما گمان می‌کردید ما هرگز موعدی برایتان قرار نخواهیم داد!'),
(2189, 2189, 'وَوُضِعَ الْكِتَابُ فَتَرَى الْمُجْرِمِينَ مُشْفِقِينَ مِمَّا فِيهِ وَيَقُولُونَ يَا وَيْلَتَنَا مَالِ هَٰذَا الْكِتَابِ لَا يُغَادِرُ صَغِيرَةً وَلَا كَبِيرَةً إِلَّا أَحْصَاهَا ۚ وَوَجَدُوا مَا عَمِلُوا حَاضِرًا ۗ وَلَا يَظْلِمُ رَبُّكَ أَحَدًا', 18, 49, 15, 60, 299, 'و کتاب = کتابی که نامه اعمال همه انسانهاست‌ در آن جا گذارده می‌شود، پس گنهکاران را می‌بینی که از آنچه در آن است، ترسان و هراسانند؛ و می‌گویند: «ای وای بر ما! این چه کتابی است که هیچ عمل کوچک و بزرگی را فرونگذاشته مگر اینکه آن را به شمار آورده است؟! و (این در حالی است که) همه اعمال خود را حاضر می‌بینند؛ و پروردگارت به هیچ کس ستم نمی‌کند.'),
(2190, 2190, 'وَإِذْ قُلْنَا لِلْمَلَائِكَةِ اسْجُدُوا لِآدَمَ فَسَجَدُوا إِلَّا إِبْلِيسَ كَانَ مِنَ الْجِنِّ فَفَسَقَ عَنْ أَمْرِ رَبِّهِ ۗ أَفَتَتَّخِذُونَهُ وَذُرِّيَّتَهُ أَوْلِيَاءَ مِنْ دُونِي وَهُمْ لَكُمْ عَدُوٌّ ۚ بِئْسَ لِلظَّالِمِينَ بَدَلًا', 18, 50, 15, 60, 299, 'به یاد آرید زمانی را که به فرشتگان گفتیم: «برای آدم سجده کنید!» آنها همگی سجده کردند جز ابلیس -که از جن بود- و از فرمان پروردگارش بیرون شد آیا (با این حال،) او و فرزندانش را به جای من اولیای خود انتخاب می‌کنید، در حالی که آنها دشمن شما هستند؟! (فرمانبرداری از شیطان و فرزندانش به جای اطاعت خدا،) چه جایگزینی بدی است برای ستمکاران!');
INSERT INTO `Quran` (`rowid`, `id`, `Arabic`, `Chapter`, `Verse`, `Juz`, `Hizb`, `Page`, `tr`) VALUES
(2191, 2191, ' مَا أَشْهَدْتُهُمْ خَلْقَ السَّمَاوَاتِ وَالْأَرْضِ وَلَا خَلْقَ أَنْفُسِهِمْ وَمَا كُنْتُ مُتَّخِذَ الْمُضِلِّينَ عَضُدًا', 18, 51, 15, 60, 299, 'من هرگز آنها = ابلیس و فرزندانش‌ را به هنگام آفرینش آسمانها و زمین، و نه به هنگام آفرینش خودشان، حاضر نساختم! و من هیچ گاه گمراه‌کنندگان را دستیار خود قرار نمی‌دهم!'),
(2192, 2192, 'وَيَوْمَ يَقُولُ نَادُوا شُرَكَائِيَ الَّذِينَ زَعَمْتُمْ فَدَعَوْهُمْ فَلَمْ يَسْتَجِيبُوا لَهُمْ وَجَعَلْنَا بَيْنَهُمْ مَوْبِقًا', 18, 52, 15, 60, 299, 'به خاطر بیاورید روزی را که (خداوند) می‌گوید: «همتایانی را که برای من می‌پنداشتید، بخوانید (تا به کمک شما بشتابند!)» ولی هر چه آنها را می‌خوانند، جوابشان نمی‌دهند؛ و در میان این دو گروه، کانون هلاکتی قرارداده‌ایم!'),
(2193, 2193, 'وَرَأَى الْمُجْرِمُونَ النَّارَ فَظَنُّوا أَنَّهُمْ مُوَاقِعُوهَا وَلَمْ يَجِدُوا عَنْهَا مَصْرِفًا', 18, 53, 15, 60, 299, 'و گنهکاران، آتش (دوزخ) را می‌بینند؛ و یقین می‌کنند که با آن درمی‌آمیزند، و هیچ گونه راه گریزی از آن نخواهند یافت.'),
(2194, 2194, 'وَلَقَدْ صَرَّفْنَا فِي هَٰذَا الْقُرْآنِ لِلنَّاسِ مِنْ كُلِّ مَثَلٍ ۚ وَكَانَ الْإِنْسَانُ أَكْثَرَ شَيْءٍ جَدَلًا', 18, 54, 15, 60, 300, 'و در این قرآن، از هر گونه مثلی برای مردم بیان کرده‌ایم؛ ولی انسان بیش از هر چیز، به مجادله می‌پردازد!'),
(2195, 2195, 'وَمَا مَنَعَ النَّاسَ أَنْ يُؤْمِنُوا إِذْ جَاءَهُمُ الْهُدَىٰ وَيَسْتَغْفِرُوا رَبَّهُمْ إِلَّا أَنْ تَأْتِيَهُمْ سُنَّةُ الْأَوَّلِينَ أَوْ يَأْتِيَهُمُ الْعَذَابُ قُبُلًا', 18, 55, 15, 60, 300, 'و چیزی مردم را بازنداشت از اینکه -وقتی هدایت به سراغشان آمد- ایمان بیاورند و از پروردگارشان طلب آمرزش کنند، جز اینکه (خیره‌سری کردند؛ گویی می‌خواستند) سرنوشت پیشینیان برای آنان بیاید، یا عذاب (الهی) در برابرشان قرار گیرد!'),
(2196, 2196, 'وَمَا نُرْسِلُ الْمُرْسَلِينَ إِلَّا مُبَشِّرِينَ وَمُنْذِرِينَ ۚ وَيُجَادِلُ الَّذِينَ كَفَرُوا بِالْبَاطِلِ لِيُدْحِضُوا بِهِ الْحَقَّ ۖ وَاتَّخَذُوا آيَاتِي وَمَا أُنْذِرُوا هُزُوًا', 18, 56, 15, 60, 300, 'ما پیامبران را، جز بعنوان بشارت دهنده و انذار کننده، نمی‌فرستیم؛ اما کافران همواره مجادله به باطل می‌کنند، تا (به گمان خود،) حق را بوسیله آن از میان بردارند! و آیات ما، و مجازاتهایی را که به آنان وعده داده شده است، به باد مسخره گرفتند!'),
(2197, 2197, 'وَمَنْ أَظْلَمُ مِمَّنْ ذُكِّرَ بِآيَاتِ رَبِّهِ فَأَعْرَضَ عَنْهَا وَنَسِيَ مَا قَدَّمَتْ يَدَاهُ ۚ إِنَّا جَعَلْنَا عَلَىٰ قُلُوبِهِمْ أَكِنَّةً أَنْ يَفْقَهُوهُ وَفِي آذَانِهِمْ وَقْرًا ۖ وَإِنْ تَدْعُهُمْ إِلَى الْهُدَىٰ فَلَنْ يَهْتَدُوا إِذًا أَبَدًا', 18, 57, 15, 60, 300, 'چه کسی ستمکارتر است از آن کس که آیات پروردگارش به او تذکّر داده شده، و از آن روی گرداند، و آنچه را با دستهای خود پیش فرستاد فراموش کرد؟! ما بر دلهای اینها پرده‌هایی افکنده‌ایم تا نفهمند؛ و در گوشهایشان سنگینی قرار داده‌ایم (تا صدای حق را نشنوند)! و از این رو اگر آنها را به سوی هدایت بخوانی، هرگز هدایت نمی‌شوند!'),
(2198, 2198, 'وَرَبُّكَ الْغَفُورُ ذُو الرَّحْمَةِ ۖ لَوْ يُؤَاخِذُهُمْ بِمَا كَسَبُوا لَعَجَّلَ لَهُمُ الْعَذَابَ ۚ بَلْ لَهُمْ مَوْعِدٌ لَنْ يَجِدُوا مِنْ دُونِهِ مَوْئِلًا', 18, 58, 15, 60, 300, 'و پروردگارت، آمرزنده و صاحب رحمت است؛ اگر می‌خواست آنان را به خاطر اعمالشان مجازات کند، عذاب را هر چه زودتر برای آنها می‌فرستاد؛ ولی برای آنان موعدی است که هرگز از آن راه فراری نخواهند داشت!'),
(2199, 2199, 'وَتِلْكَ الْقُرَىٰ أَهْلَكْنَاهُمْ لَمَّا ظَلَمُوا وَجَعَلْنَا لِمَهْلِكِهِمْ مَوْعِدًا', 18, 59, 15, 60, 300, 'این شهرها و آبادیهایی است که ما آنها را هنگامی که ستم کردند هلاک نمودیم؛ و برای هلاکتشان موعدی قرار دادیم! (آنها ویرانه‌هایش را با چشم می‌بینند، و عبرت نمی‌گیرند!)'),
(2200, 2200, 'وَإِذْ قَالَ مُوسَىٰ لِفَتَاهُ لَا أَبْرَحُ حَتَّىٰ أَبْلُغَ مَجْمَعَ الْبَحْرَيْنِ أَوْ أَمْضِيَ حُقُبًا', 18, 60, 15, 60, 300, 'به خاطر بیاور هنگامی را که موسی به دوست خود گفت: دست از جستجو برنمی‌دارم تا به محل تلاقی دو دریا برسم؛ هر چند مدت طولانی به راه خود ادامه دهم!'),
(2201, 2201, 'فَلَمَّا بَلَغَا مَجْمَعَ بَيْنِهِمَا نَسِيَا حُوتَهُمَا فَاتَّخَذَ سَبِيلَهُ فِي الْبَحْرِ سَرَبًا', 18, 61, 15, 60, 300, '(ولی) هنگامی که به محل تلاقی آن دو دریا رسیدند، ماهی خود را (که برای تغذیه همراه داشتند) فراموش کردند؛ و ماهی راه خود را در دریا پیش گرفت (و روان شد).'),
(2202, 2202, 'فَلَمَّا جَاوَزَا قَالَ لِفَتَاهُ آتِنَا غَدَاءَنَا لَقَدْ لَقِينَا مِنْ سَفَرِنَا هَٰذَا نَصَبًا', 18, 62, 15, 60, 301, 'هنگامی که از آن جا گذشتند، (موسی) به یار همسفرش گفت: «غذای ما را بیاور، که سخت از این سفر خسته شده‌ایم!»'),
(2203, 2203, 'قَالَ أَرَأَيْتَ إِذْ أَوَيْنَا إِلَى الصَّخْرَةِ فَإِنِّي نَسِيتُ الْحُوتَ وَمَا أَنْسَانِيهُ إِلَّا الشَّيْطَانُ أَنْ أَذْكُرَهُ ۚ وَاتَّخَذَ سَبِيلَهُ فِي الْبَحْرِ عَجَبًا', 18, 63, 15, 60, 301, 'گفت: «به خاطر داری هنگامی که ما (برای استراحت) به کنار آن صخره پناه بردیم، من (در آن جا) فراموش کردم جریان ماهی را بازگو کنم -و فقط شیطان بود که آن را از خاطر من برد- و ماهی بطرز شگفت‌آوری راه خود را در دریا پیش گرفت!»'),
(2204, 2204, 'قَالَ ذَٰلِكَ مَا كُنَّا نَبْغِ ۚ فَارْتَدَّا عَلَىٰ آثَارِهِمَا قَصَصًا', 18, 64, 15, 60, 301, '(موسی) گفت: «آن همان بود که ما می‌خواستیم!» سپس از همان راه بازگشتند، در حالی که پی‌جویی می‌کردند.'),
(2205, 2205, 'فَوَجَدَا عَبْدًا مِنْ عِبَادِنَا آتَيْنَاهُ رَحْمَةً مِنْ عِنْدِنَا وَعَلَّمْنَاهُ مِنْ لَدُنَّا عِلْمًا', 18, 65, 15, 60, 301, '(در آن جا) بنده‌ای از بندگان ما را یافتند که رحمت (و موهبت عظیمی) از سوی خود به او داده، و علم فراوانی از نزد خود به او آموخته بودیم.'),
(2206, 2206, 'قَالَ لَهُ مُوسَىٰ هَلْ أَتَّبِعُكَ عَلَىٰ أَنْ تُعَلِّمَنِ مِمَّا عُلِّمْتَ رُشْدًا', 18, 66, 15, 60, 301, 'موسی به او گفت: «آیا از تو پیروی کنم تا از آنچه به تو تعلیم داده شده و مایه رشد و صلاح است، به من بیاموزی؟»'),
(2207, 2207, 'قَالَ إِنَّكَ لَنْ تَسْتَطِيعَ مَعِيَ صَبْرًا', 18, 67, 15, 60, 301, 'گفت: «تو هرگز نمی‌توانی با من شکیبایی کنی!'),
(2208, 2208, 'وَكَيْفَ تَصْبِرُ عَلَىٰ مَا لَمْ تُحِطْ بِهِ خُبْرًا', 18, 68, 15, 60, 301, 'و چگونه می‌توانی در برابر چیزی که از رموزش آگاه نیستی شکیبا باشی؟!'),
(2209, 2209, 'قَالَ سَتَجِدُنِي إِنْ شَاءَ اللَّهُ صَابِرًا وَلَا أَعْصِي لَكَ أَمْرًا', 18, 69, 15, 60, 301, '(موسی) گفت: «به خواست خدا مرا شکیبا خواهی یافت؛ و در هیچ کاری مخالفت فرمان تو نخواهم کرد!»'),
(2210, 2210, 'قَالَ فَإِنِ اتَّبَعْتَنِي فَلَا تَسْأَلْنِي عَنْ شَيْءٍ حَتَّىٰ أُحْدِثَ لَكَ مِنْهُ ذِكْرًا', 18, 70, 15, 60, 301, '(خضر) گفت: «پس اگر می‌خواهی بدنبال من بیایی، از هیچ چیز مپرس تا خودم (به موقع) آن را برای تو بازگو کنم.»'),
(2211, 2211, 'فَانْطَلَقَا حَتَّىٰ إِذَا رَكِبَا فِي السَّفِينَةِ خَرَقَهَا ۖ قَالَ أَخَرَقْتَهَا لِتُغْرِقَ أَهْلَهَا لَقَدْ جِئْتَ شَيْئًا إِمْرًا', 18, 71, 15, 60, 301, 'آن دو به راه افتادند؛ تا آن که سوار کشتی شدند، (خضر) کشتی را سوراخ کرد. (موسی) گفت: «آیا آن را سوراخ کردی که اهلش را غرق کنی؟! راستی که چه کار بدی انجام دادی!»'),
(2212, 2212, 'قَالَ أَلَمْ أَقُلْ إِنَّكَ لَنْ تَسْتَطِيعَ مَعِيَ صَبْرًا', 18, 72, 15, 60, 301, 'گفت: «آیا نگفتم تو هرگز نمی‌توانی با من شکیبایی کنی؟!»'),
(2213, 2213, 'قَالَ لَا تُؤَاخِذْنِي بِمَا نَسِيتُ وَلَا تُرْهِقْنِي مِنْ أَمْرِي عُسْرًا', 18, 73, 15, 60, 301, '(موسی) گفت: «مرا بخاطر این فراموشکاریم مؤاخذه مکن و از این کارم بر من سخت مگیر!»'),
(2214, 2214, 'فَانْطَلَقَا حَتَّىٰ إِذَا لَقِيَا غُلَامًا فَقَتَلَهُ قَالَ أَقَتَلْتَ نَفْسًا زَكِيَّةً بِغَيْرِ نَفْسٍ لَقَدْ جِئْتَ شَيْئًا نُكْرًا', 18, 74, 15, 60, 301, 'باز به راه خود ادامه دادند، تا اینکه نوجوانی را دیدند؛ و او آن نوجوان را کشت. (موسی) گفت: «آیا انسان پاکی را، بی آنکه قتلی کرده باشد، کشتی؟! براستی کار زشتی انجام دادی!»'),
(2215, 2215, ' قَالَ أَلَمْ أَقُلْ لَكَ إِنَّكَ لَنْ تَسْتَطِيعَ مَعِيَ صَبْرًا', 18, 75, 16, 61, 302, '(باز آن مرد عالم) گفت: «آیا به تو نگفتم که تو هرگز نمی‌توانی با من صبر کنی؟!»'),
(2216, 2216, 'قَالَ إِنْ سَأَلْتُكَ عَنْ شَيْءٍ بَعْدَهَا فَلَا تُصَاحِبْنِي ۖ قَدْ بَلَغْتَ مِنْ لَدُنِّي عُذْرًا', 18, 76, 16, 61, 302, '(موسی) گفت: «بعد از این اگر درباره چیزی از تو سؤال کردم، دیگر با من همراهی نکن؛ (زیرا) از سوی من معذور خواهی بود!»'),
(2217, 2217, 'فَانْطَلَقَا حَتَّىٰ إِذَا أَتَيَا أَهْلَ قَرْيَةٍ اسْتَطْعَمَا أَهْلَهَا فَأَبَوْا أَنْ يُضَيِّفُوهُمَا فَوَجَدَا فِيهَا جِدَارًا يُرِيدُ أَنْ يَنْقَضَّ فَأَقَامَهُ ۖ قَالَ لَوْ شِئْتَ لَاتَّخَذْتَ عَلَيْهِ أَجْرًا', 18, 77, 16, 61, 302, 'باز به راه خود ادامه دادند تا به مردم قریه‌ای رسیدند؛ از آنان خواستند که به ایشان غذا دهند؛ ولی آنان از مهمان کردنشان خودداری نمودند؛ (با این حال) در آن جا دیواری یافتند که می‌خواست فروریزد؛ و (آن مرد عالم) آن را برپا داشت. (موسی) گفت: «(لااقل) می‌خواستی در مقابل این کار مزدی بگیری!»'),
(2218, 2218, 'قَالَ هَٰذَا فِرَاقُ بَيْنِي وَبَيْنِكَ ۚ سَأُنَبِّئُكَ بِتَأْوِيلِ مَا لَمْ تَسْتَطِعْ عَلَيْهِ صَبْرًا', 18, 78, 16, 61, 302, 'او گفت: «اینک زمان جدایی من و تو فرا رسیده؛ اما بزودی راز آنچه را که نتوانستی در برابر آن صبر کنی، به تو خبر می‌دهم.'),
(2219, 2219, 'أَمَّا السَّفِينَةُ فَكَانَتْ لِمَسَاكِينَ يَعْمَلُونَ فِي الْبَحْرِ فَأَرَدْتُ أَنْ أَعِيبَهَا وَكَانَ وَرَاءَهُمْ مَلِكٌ يَأْخُذُ كُلَّ سَفِينَةٍ غَصْبًا', 18, 79, 16, 61, 302, 'اما آن کشتی مال گروهی از مستمندان بود که با آن در دریا کار می‌کردند؛ و من خواستم آن را معیوب کنم؛ (چرا که) پشت سرشان پادشاهی (ستمگر) بود که هر کشتی (سالمی) را بزور میگرفت!'),
(2220, 2220, 'وَأَمَّا الْغُلَامُ فَكَانَ أَبَوَاهُ مُؤْمِنَيْنِ فَخَشِينَا أَنْ يُرْهِقَهُمَا طُغْيَانًا وَكُفْرًا', 18, 80, 16, 61, 302, 'و اما آن نوجوان، پدر و مادرش با ایمان بودند؛ و بیم داشتیم که آنان را به طغیان و کفر وادارد!'),
(2221, 2221, 'فَأَرَدْنَا أَنْ يُبْدِلَهُمَا رَبُّهُمَا خَيْرًا مِنْهُ زَكَاةً وَأَقْرَبَ رُحْمًا', 18, 81, 16, 61, 302, 'از این رو، خواستیم که پروردگارشان به جای او، فرزندی پاکتر و با محبت‌تر به آن دو بدهد!'),
(2222, 2222, 'وَأَمَّا الْجِدَارُ فَكَانَ لِغُلَامَيْنِ يَتِيمَيْنِ فِي الْمَدِينَةِ وَكَانَ تَحْتَهُ كَنْزٌ لَهُمَا وَكَانَ أَبُوهُمَا صَالِحًا فَأَرَادَ رَبُّكَ أَنْ يَبْلُغَا أَشُدَّهُمَا وَيَسْتَخْرِجَا كَنْزَهُمَا رَحْمَةً مِنْ رَبِّكَ ۚ وَمَا فَعَلْتُهُ عَنْ أَمْرِي ۚ ذَٰلِكَ تَأْوِيلُ مَا لَمْ تَسْطِعْ عَلَيْهِ صَبْرًا', 18, 82, 16, 61, 302, 'و اما آن دیوار، از آن دو نوجوان یتیم در آن شهر بود؛ و زیر آن، گنجی متعلق به آن دو وجود داشت؛ و پدرشان مرد صالحی بود؛ و پروردگار تو می‌خواست آنها به حد بلوغ برسند و گنجشان را استخراج کنند؛ این رحمتی از پروردگارت بود؛ و من آن (کارها) را خودسرانه انجام ندادم؛ این بود راز کارهایی که نتوانستی در برابر آنها شکیبایی به خرج دهی!»'),
(2223, 2223, 'وَيَسْأَلُونَكَ عَنْ ذِي الْقَرْنَيْنِ ۖ قُلْ سَأَتْلُو عَلَيْكُمْ مِنْهُ ذِكْرًا', 18, 83, 16, 61, 302, 'و از تو درباره «ذو القرنین» می‌پرسند؛ بگو: «بزودی بخشی از سرگذشت او را برای شما بازگو خواهم کرد.»'),
(2224, 2224, 'إِنَّا مَكَّنَّا لَهُ فِي الْأَرْضِ وَآتَيْنَاهُ مِنْ كُلِّ شَيْءٍ سَبَبًا', 18, 84, 16, 61, 303, 'ما به او در روی زمین، قدرت و حکومت دادیم؛ و اسباب هر چیز را در اختیارش گذاشتیم.'),
(2225, 2225, 'فَأَتْبَعَ سَبَبًا', 18, 85, 16, 61, 303, 'او از این اسباب، (پیروی و استفاده) کرد...'),
(2226, 2226, 'حَتَّىٰ إِذَا بَلَغَ مَغْرِبَ الشَّمْسِ وَجَدَهَا تَغْرُبُ فِي عَيْنٍ حَمِئَةٍ وَوَجَدَ عِنْدَهَا قَوْمًا ۗ قُلْنَا يَا ذَا الْقَرْنَيْنِ إِمَّا أَنْ تُعَذِّبَ وَإِمَّا أَنْ تَتَّخِذَ فِيهِمْ حُسْنًا', 18, 86, 16, 61, 303, 'تا به غروبگاه آفتاب رسید؛ (در آن جا) احساس کرد (و در نظرش مجسّم شد) که خورشید در چشمه تیره و گل‌آلودی فرو می‌رود؛ و در آن جا قومی را یافت؛ گفتیم: «ای ذو القرنین! آیا می‌خواهی (آنان) را مجازات کنی، و یا روش نیکویی در مورد آنها انتخاب نمایی؟»'),
(2227, 2227, 'قَالَ أَمَّا مَنْ ظَلَمَ فَسَوْفَ نُعَذِّبُهُ ثُمَّ يُرَدُّ إِلَىٰ رَبِّهِ فَيُعَذِّبُهُ عَذَابًا نُكْرًا', 18, 87, 16, 61, 303, 'گفت: «اما کسی را که ستم کرده است، مجازات خواهیم کرد؛ سپس به سوی پروردگارش بازمی‌گردد، و خدا او را مجازات شدیدی خواهد کرد!'),
(2228, 2228, 'وَأَمَّا مَنْ آمَنَ وَعَمِلَ صَالِحًا فَلَهُ جَزَاءً الْحُسْنَىٰ ۖ وَسَنَقُولُ لَهُ مِنْ أَمْرِنَا يُسْرًا', 18, 88, 16, 61, 303, 'و اما کسی که ایمان آورد و عمل صالح انجام دهد، پاداشی نیکوتر خواهد داشت؛ و ما دستور آسانی به او خواهیم داد.»'),
(2229, 2229, 'ثُمَّ أَتْبَعَ سَبَبًا', 18, 89, 16, 61, 303, 'سپس (بار دیگر) از اسبابی (که در اختیار داشت) بهره گرفت...'),
(2230, 2230, 'حَتَّىٰ إِذَا بَلَغَ مَطْلِعَ الشَّمْسِ وَجَدَهَا تَطْلُعُ عَلَىٰ قَوْمٍ لَمْ نَجْعَلْ لَهُمْ مِنْ دُونِهَا سِتْرًا', 18, 90, 16, 61, 303, 'تا به خاستگاه خورشید رسید؛ (در آن جا) دید خورشید بر جمعیّتی طلوع می‌کند که در برابر (تابش) آفتاب، پوششی برای آنها قرار نداده بودیم (و هیچ گونه سایبانی نداشتند).'),
(2231, 2231, 'كَذَٰلِكَ وَقَدْ أَحَطْنَا بِمَا لَدَيْهِ خُبْرًا', 18, 91, 16, 61, 303, '(آری) اینچنین بود (کار ذو القرنین)! و ما بخوبی از امکاناتی که نزد او بود آگاه بودیم!'),
(2232, 2232, 'ثُمَّ أَتْبَعَ سَبَبًا', 18, 92, 16, 61, 303, '(باز) از اسباب مهمّی (که در اختیار داشت) استفاده کرد...'),
(2233, 2233, 'حَتَّىٰ إِذَا بَلَغَ بَيْنَ السَّدَّيْنِ وَجَدَ مِنْ دُونِهِمَا قَوْمًا لَا يَكَادُونَ يَفْقَهُونَ قَوْلًا', 18, 93, 16, 61, 303, '(و همچنان به راه خود ادامه داد) تا به میان دو کوه رسید؛ و در کنار آن دو (کوه) قومی را یافت که هیچ سخنی را نمی‌فهمیدند (و زبانشان مخصوص خودشان بود)!'),
(2234, 2234, 'قَالُوا يَا ذَا الْقَرْنَيْنِ إِنَّ يَأْجُوجَ وَمَأْجُوجَ مُفْسِدُونَ فِي الْأَرْضِ فَهَلْ نَجْعَلُ لَكَ خَرْجًا عَلَىٰ أَنْ تَجْعَلَ بَيْنَنَا وَبَيْنَهُمْ سَدًّا', 18, 94, 16, 61, 303, '(آن گروه به او) گفتند: «ای ذو القرنین یأجوج و مأجوج در این سرزمین فساد می‌کنند؛ آیا ممکن است ما هزینه‌ای برای تو قرار دهیم، که میان ما و آنها سدّی ایجاد کنی؟!»'),
(2235, 2235, 'قَالَ مَا مَكَّنِّي فِيهِ رَبِّي خَيْرٌ فَأَعِينُونِي بِقُوَّةٍ أَجْعَلْ بَيْنَكُمْ وَبَيْنَهُمْ رَدْمًا', 18, 95, 16, 61, 303, '(ذو القرنین) گفت: «آنچه پروردگارم در اختیار من گذارده، بهتر است (از آنچه شما پیشنهاد می‌کنید)! مرا با نیرویی یاری دهید، تا میان شما و آنها سدّ محکمی قرار دهم!'),
(2236, 2236, 'آتُونِي زُبَرَ الْحَدِيدِ ۖ حَتَّىٰ إِذَا سَاوَىٰ بَيْنَ الصَّدَفَيْنِ قَالَ انْفُخُوا ۖ حَتَّىٰ إِذَا جَعَلَهُ نَارًا قَالَ آتُونِي أُفْرِغْ عَلَيْهِ قِطْرًا', 18, 96, 16, 61, 303, 'قطعات بزرگ آهن برایم بیاورید (و آنها را روی هم بچینید)!» تا وقتی که کاملاً میان دو کوه را پوشانید، گفت: «(در اطراف آن آتش بیفروزید، و) در آن بدمید!» (آنها دمیدند) تا قطعات آهن را سرخ و گداخته کرد، و گفت: «(اکنون) مس مذاب برایم بیاورید تا بر روی آن بریزم!»'),
(2237, 2237, 'فَمَا اسْطَاعُوا أَنْ يَظْهَرُوهُ وَمَا اسْتَطَاعُوا لَهُ نَقْبًا', 18, 97, 16, 61, 303, '(سرانجام چنان سدّ نیرومندی ساخت) که آنها = طایفه یأجوج و مأجوج‌ قادر نبودند از آن بالا روند؛ و نمی‌توانستند نقبی در آن ایجاد کنند.'),
(2238, 2238, 'قَالَ هَٰذَا رَحْمَةٌ مِنْ رَبِّي ۖ فَإِذَا جَاءَ وَعْدُ رَبِّي جَعَلَهُ دَكَّاءَ ۖ وَكَانَ وَعْدُ رَبِّي حَقًّا', 18, 98, 16, 61, 304, '(آنگاه) گفت: «این از رحمت پروردگار من است! امّا هنگامی که وعده پروردگارم فرا رسد، آن را در هم می‌کوبد؛ و وعده پروردگارم حق است!»'),
(2239, 2239, ' وَتَرَكْنَا بَعْضَهُمْ يَوْمَئِذٍ يَمُوجُ فِي بَعْضٍ ۖ وَنُفِخَ فِي الصُّورِ فَجَمَعْنَاهُمْ جَمْعًا', 18, 99, 16, 61, 304, 'و در آن روز (که جهان پایان می‌گیرد)، ما آنان را چنان رها می‌کنیم که درهم موج می‌زنند؛ و در صور = شیپور دمیده می‌شود؛ و ما همه را جمع می‌کنیم!'),
(2240, 2240, 'وَعَرَضْنَا جَهَنَّمَ يَوْمَئِذٍ لِلْكَافِرِينَ عَرْضًا', 18, 100, 16, 61, 304, 'در آن روز، جهنم را بر کافران عرضه می‌داریم!'),
(2241, 2241, 'الَّذِينَ كَانَتْ أَعْيُنُهُمْ فِي غِطَاءٍ عَنْ ذِكْرِي وَكَانُوا لَا يَسْتَطِيعُونَ سَمْعًا', 18, 101, 16, 61, 304, 'همانها که پرده‌ای چشمانشان را از یاد من پوشانده بود، و قدرت شنوایی نداشتند!'),
(2242, 2242, 'أَفَحَسِبَ الَّذِينَ كَفَرُوا أَنْ يَتَّخِذُوا عِبَادِي مِنْ دُونِي أَوْلِيَاءَ ۚ إِنَّا أَعْتَدْنَا جَهَنَّمَ لِلْكَافِرِينَ نُزُلًا', 18, 102, 16, 61, 304, 'آیا کافران پنداشتند می‌توانند بندگانم را به جای من اولیای خود انتخاب کنند؟! ما جهنم را برای پذیرایی کافران آماده کرده‌ایم!'),
(2243, 2243, 'قُلْ هَلْ نُنَبِّئُكُمْ بِالْأَخْسَرِينَ أَعْمَالًا', 18, 103, 16, 61, 304, 'بگو: «آیا به شما خبر دهیم که زیانکارترین (مردم) در کارها، چه کسانی هستند؟'),
(2244, 2244, 'الَّذِينَ ضَلَّ سَعْيُهُمْ فِي الْحَيَاةِ الدُّنْيَا وَهُمْ يَحْسَبُونَ أَنَّهُمْ يُحْسِنُونَ صُنْعًا', 18, 104, 16, 61, 304, 'آنها که تلاشهایشان در زندگی دنیا گم (و نابود) شده؛ با این حال، می‌پندارند کار نیک انجام می‌دهند!»'),
(2245, 2245, 'أُولَٰئِكَ الَّذِينَ كَفَرُوا بِآيَاتِ رَبِّهِمْ وَلِقَائِهِ فَحَبِطَتْ أَعْمَالُهُمْ فَلَا نُقِيمُ لَهُمْ يَوْمَ الْقِيَامَةِ وَزْنًا', 18, 105, 16, 61, 304, 'آنها کسانی هستند که به آیات پروردگارشان و لقای او کافر شدند؛ به همین جهت، اعمالشان حبط و نابود شد! از این رو روز قیامت، میزانی برای آنها برپا نخواهیم کرد!'),
(2246, 2246, 'ذَٰلِكَ جَزَاؤُهُمْ جَهَنَّمُ بِمَا كَفَرُوا وَاتَّخَذُوا آيَاتِي وَرُسُلِي هُزُوًا', 18, 106, 16, 61, 304, '(آری،) این گونه است! کیفرشان دوزخ است، بخاطر آنکه کافر شدند، و آیات من و پیامبرانم را به سخریه گرفتند!'),
(2247, 2247, 'إِنَّ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ كَانَتْ لَهُمْ جَنَّاتُ الْفِرْدَوْسِ نُزُلًا', 18, 107, 16, 61, 304, 'امّا کسانی که ایمان آوردند و کارهای شایسته انجام دادند، باغهای بهشت برین محل پذیرایی آنان خواهد بود.'),
(2248, 2248, 'خَالِدِينَ فِيهَا لَا يَبْغُونَ عَنْهَا حِوَلًا', 18, 108, 16, 61, 304, 'آنها جاودانه در آن خواهند ماند؛ و هرگز تقاضای نقل مکان از آن جا نمی‌کنند!'),
(2249, 2249, 'قُلْ لَوْ كَانَ الْبَحْرُ مِدَادًا لِكَلِمَاتِ رَبِّي لَنَفِدَ الْبَحْرُ قَبْلَ أَنْ تَنْفَدَ كَلِمَاتُ رَبِّي وَلَوْ جِئْنَا بِمِثْلِهِ مَدَدًا', 18, 109, 16, 61, 304, 'بگو: «اگر دریاها برای (نوشتن) کلمات پروردگارم مرکّب شود، دریاها پایان می‌گیرد. پیش از آنکه کلمات پروردگارم پایان یابد؛ هر چند همانند آن (دریاها) را کمک آن قرار دهیم!»'),
(2250, 2250, 'قُلْ إِنَّمَا أَنَا بَشَرٌ مِثْلُكُمْ يُوحَىٰ إِلَيَّ أَنَّمَا إِلَٰهُكُمْ إِلَٰهٌ وَاحِدٌ ۖ فَمَنْ كَانَ يَرْجُو لِقَاءَ رَبِّهِ فَلْيَعْمَلْ عَمَلًا صَالِحًا وَلَا يُشْرِكْ بِعِبَادَةِ رَبِّهِ أَحَدًا', 18, 110, 16, 61, 304, 'بگو: «من فقط بشری هستم مثل شما؛ (امتیازم این است که) به من وحی می‌شود که تنها معبودتان معبود یگانه است؛ پس هر که به لقای پروردگارش امید دارد، باید کاری شایسته انجام دهد، و هیچ کس را در عبادت پروردگارش شریک نکند!'),
(2251, 2251, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ كهيعص', 19, 1, 16, 61, 305, 'کهیعص‌'),
(2252, 2252, 'ذِكْرُ رَحْمَتِ رَبِّكَ عَبْدَهُ زَكَرِيَّا', 19, 2, 16, 61, 305, '(این) یادی است از رحمت پروردگار تو نسبت به بنده‌اش زکریا...'),
(2253, 2253, 'إِذْ نَادَىٰ رَبَّهُ نِدَاءً خَفِيًّا', 19, 3, 16, 61, 305, 'در آن هنگام که پروردگارش را در خلوتگاه (عبادت) پنهان خواند...'),
(2254, 2254, 'قَالَ رَبِّ إِنِّي وَهَنَ الْعَظْمُ مِنِّي وَاشْتَعَلَ الرَّأْسُ شَيْبًا وَلَمْ أَكُنْ بِدُعَائِكَ رَبِّ شَقِيًّا', 19, 4, 16, 61, 305, 'گفت: «پروردگارا! استخوانم سست شده؛ و شعله پیری تمام سرم را فراگرفته؛ و من هرگز در دعای تو، از اجابت محروم نبوده‌ام!'),
(2255, 2255, 'وَإِنِّي خِفْتُ الْمَوَالِيَ مِنْ وَرَائِي وَكَانَتِ امْرَأَتِي عَاقِرًا فَهَبْ لِي مِنْ لَدُنْكَ وَلِيًّا', 19, 5, 16, 61, 305, 'و من از بستگانم بعد از خودم بیمناکم (که حق پاسداری از آیین تو را نگاه ندارند)! و (از طرفی) همسرم نازا و عقیم است؛ تو از نزد خود جانشینی به من ببخش...'),
(2256, 2256, 'يَرِثُنِي وَيَرِثُ مِنْ آلِ يَعْقُوبَ ۖ وَاجْعَلْهُ رَبِّ رَضِيًّا', 19, 6, 16, 61, 305, 'که وارث من و دودمان یعقوب باشد؛ و او را مورد رضایتت قرار ده!»'),
(2257, 2257, 'يَا زَكَرِيَّا إِنَّا نُبَشِّرُكَ بِغُلَامٍ اسْمُهُ يَحْيَىٰ لَمْ نَجْعَلْ لَهُ مِنْ قَبْلُ سَمِيًّا', 19, 7, 16, 61, 305, 'ای زکریا! ما تو را به فرزندی بشارت می‌دهیم که نامش «یحیی» است؛ و پیش از این، همنامی برای او قرار نداده‌ایم!'),
(2258, 2258, 'قَالَ رَبِّ أَنَّىٰ يَكُونُ لِي غُلَامٌ وَكَانَتِ امْرَأَتِي عَاقِرًا وَقَدْ بَلَغْتُ مِنَ الْكِبَرِ عِتِيًّا', 19, 8, 16, 61, 305, 'گفت: «پروردگارا! چگونه برای من فرزندی خواهد بود؟! در حالی که همسرم نازا و عقیم است، و من نیز از شدّت پیری افتاده شده‌ام!»'),
(2259, 2259, 'قَالَ كَذَٰلِكَ قَالَ رَبُّكَ هُوَ عَلَيَّ هَيِّنٌ وَقَدْ خَلَقْتُكَ مِنْ قَبْلُ وَلَمْ تَكُ شَيْئًا', 19, 9, 16, 61, 305, 'فرمود: «پروردگارت این گونه گفته (و اراده کرده)! این بر من آسان است؛ و قبلاً تو را آفریدم در حالی که چیزی نبودی!»'),
(2260, 2260, 'قَالَ رَبِّ اجْعَلْ لِي آيَةً ۚ قَالَ آيَتُكَ أَلَّا تُكَلِّمَ النَّاسَ ثَلَاثَ لَيَالٍ سَوِيًّا', 19, 10, 16, 61, 305, 'عرض کرد: «پروردگارا! نشانه‌ای برای من قرار ده!» فرمود: «نشانه تو این است که سه شبانه روز قدرت تکلّم (با مردم) نخواهی داشت؛ در حالی که زبانت سالم است!»'),
(2261, 2261, 'فَخَرَجَ عَلَىٰ قَوْمِهِ مِنَ الْمِحْرَابِ فَأَوْحَىٰ إِلَيْهِمْ أَنْ سَبِّحُوا بُكْرَةً وَعَشِيًّا', 19, 11, 16, 61, 305, 'او از محراب عبادتش به سوی مردم بیرون آمد؛ و با اشاره به آنها گفت: «(بشکرانه این موهبت،) صبح و شام خدا را تسبیح گویید!»'),
(2262, 2262, 'يَا يَحْيَىٰ خُذِ الْكِتَابَ بِقُوَّةٍ ۖ وَآتَيْنَاهُ الْحُكْمَ صَبِيًّا', 19, 12, 16, 61, 306, 'ای یحیی! کتاب (خدا) را با قوّت بگیر! و ما فرمان نبوّت (و عقل کافی) در کودکی به او دادیم!'),
(2263, 2263, 'وَحَنَانًا مِنْ لَدُنَّا وَزَكَاةً ۖ وَكَانَ تَقِيًّا', 19, 13, 16, 61, 306, 'و رحمت و محبتّی از ناحیه خود به او بخشیدیم، و پاکی (دل و جان)! و او پرهیزگار بود!'),
(2264, 2264, 'وَبَرًّا بِوَالِدَيْهِ وَلَمْ يَكُنْ جَبَّارًا عَصِيًّا', 19, 14, 16, 61, 306, 'او نسبت به پدر و مادرش نیکوکار بود؛ و جبّار (و متکّبر) و عصیانگر نبود!'),
(2265, 2265, 'وَسَلَامٌ عَلَيْهِ يَوْمَ وُلِدَ وَيَوْمَ يَمُوتُ وَيَوْمَ يُبْعَثُ حَيًّا', 19, 15, 16, 61, 306, 'سلام بر او، آن روز که تولّد یافت، و آن روز که می‌میرد، و آن روز که زنده برانگیخته می‌شود!'),
(2266, 2266, 'وَاذْكُرْ فِي الْكِتَابِ مَرْيَمَ إِذِ انْتَبَذَتْ مِنْ أَهْلِهَا مَكَانًا شَرْقِيًّا', 19, 16, 16, 61, 306, 'و در این کتاب (آسمانی)، مریم را یاد کن، آن هنگام که از خانواده‌اش جدا شد، و در ناحیه شرقی (بیت المقدس) قرار گرفت؛'),
(2267, 2267, 'فَاتَّخَذَتْ مِنْ دُونِهِمْ حِجَابًا فَأَرْسَلْنَا إِلَيْهَا رُوحَنَا فَتَمَثَّلَ لَهَا بَشَرًا سَوِيًّا', 19, 17, 16, 61, 306, 'و میان خود و آنان حجابی افکند (تا خلوتگاهش از هر نظر برای عبادت آماده باشد). در این هنگام، ما روح خود را بسوی او فرستادیم؛ و او در شکل انسانی بی‌عیب و نقص، بر مریم ظاهر شد!'),
(2268, 2268, 'قَالَتْ إِنِّي أَعُوذُ بِالرَّحْمَٰنِ مِنْكَ إِنْ كُنْتَ تَقِيًّا', 19, 18, 16, 61, 306, 'او (سخت ترسید و) گفت: «من از شرّ تو، به خدای رحمان پناه می‌برم اگر پرهیزگاری!'),
(2269, 2269, 'قَالَ إِنَّمَا أَنَا رَسُولُ رَبِّكِ لِأَهَبَ لَكِ غُلَامًا زَكِيًّا', 19, 19, 16, 61, 306, 'گفت: «من فرستاده پروردگار توام؛ (آمده‌ام) تا پسر پاکیزه‌ای به تو ببخشم!»'),
(2270, 2270, 'قَالَتْ أَنَّىٰ يَكُونُ لِي غُلَامٌ وَلَمْ يَمْسَسْنِي بَشَرٌ وَلَمْ أَكُ بَغِيًّا', 19, 20, 16, 61, 306, 'گفت: «چگونه ممکن است فرزندی برای من باشد؟! در حالی که تاکنون انسانی با من تماس نداشته، و زن آلوده‌ای هم نبوده‌ام!»'),
(2271, 2271, 'قَالَ كَذَٰلِكِ قَالَ رَبُّكِ هُوَ عَلَيَّ هَيِّنٌ ۖ وَلِنَجْعَلَهُ آيَةً لِلنَّاسِ وَرَحْمَةً مِنَّا ۚ وَكَانَ أَمْرًا مَقْضِيًّا', 19, 21, 16, 61, 306, 'گفت: «مطلب همین است! پروردگارت فرموده: این کار بر من آسان است! (ما او را می‌آفرینیم، تا قدرت خویش را آشکار سازیم؛) و او را برای مردم نشانه‌ای قرار دهیم؛ و رحمتی باشد از سوی ما! و این امری است پایان یافته (و جای گفتگو ندارد)!»'),
(2272, 2272, ' فَحَمَلَتْهُ فَانْتَبَذَتْ بِهِ مَكَانًا قَصِيًّا', 19, 22, 16, 62, 306, 'سرانجام (مریم) به او باردار شد؛ و او را به نقطه دور دستی برد (و خلوت گزید)'),
(2273, 2273, 'فَأَجَاءَهَا الْمَخَاضُ إِلَىٰ جِذْعِ النَّخْلَةِ قَالَتْ يَا لَيْتَنِي مِتُّ قَبْلَ هَٰذَا وَكُنْتُ نَسْيًا مَنْسِيًّا', 19, 23, 16, 62, 306, 'درد زایمان او را به کنار تنه درخت خرمایی کشاند؛ (آنقدر ناراحت شد که) گفت: «ای کاش پیش از این مرده بودم، و بکلّی فراموش می‌شدم!»'),
(2274, 2274, 'فَنَادَاهَا مِنْ تَحْتِهَا أَلَّا تَحْزَنِي قَدْ جَعَلَ رَبُّكِ تَحْتَكِ سَرِيًّا', 19, 24, 16, 62, 306, 'ناگهان از طرف پایین پایش او را صدا زد که: «غمگین مباش! پروردگارت زیر پای تو چشمه آبی (گوارا) قرار داده است!'),
(2275, 2275, 'وَهُزِّي إِلَيْكِ بِجِذْعِ النَّخْلَةِ تُسَاقِطْ عَلَيْكِ رُطَبًا جَنِيًّا', 19, 25, 16, 62, 306, 'و این تنه نخل را به طرف خود تکان ده، رطب تازه‌ای بر تو فرو می‌ریزد!'),
(2276, 2276, 'فَكُلِي وَاشْرَبِي وَقَرِّي عَيْنًا ۖ فَإِمَّا تَرَيِنَّ مِنَ الْبَشَرِ أَحَدًا فَقُولِي إِنِّي نَذَرْتُ لِلرَّحْمَٰنِ صَوْمًا فَلَنْ أُكَلِّمَ الْيَوْمَ إِنْسِيًّا', 19, 26, 16, 62, 307, '(از این غذای لذیذ) بخور؛ و (از آن آب گوارا) بنوش؛ و چشمت را (به این مولود جدید) روشن دار! و هرگاه کسی از انسانها را دیدی، (با اشاره) بگو: من برای خداوند رحمان روزه‌ای نذر کرده‌ام؛ بنابراین امروز با هیچ انسانی هیچ سخن نمی‌گویم! (و بدان که این نوزاد، خودش از تو دفاع خواهد کرد!)»'),
(2277, 2277, 'فَأَتَتْ بِهِ قَوْمَهَا تَحْمِلُهُ ۖ قَالُوا يَا مَرْيَمُ لَقَدْ جِئْتِ شَيْئًا فَرِيًّا', 19, 27, 16, 62, 307, '(مریم) در حالی که او را در آغوش گرفته بود، نزد قومش آورد؛ گفتند: «ای مریم! کار بسیار عجیب و بدی انجام دادی!'),
(2278, 2278, 'يَا أُخْتَ هَارُونَ مَا كَانَ أَبُوكِ امْرَأَ سَوْءٍ وَمَا كَانَتْ أُمُّكِ بَغِيًّا', 19, 28, 16, 62, 307, 'ای خواهر هارون! نه پدرت مرد بدی بود، و نه مادرت زن بد کاره‌ای!!»'),
(2279, 2279, 'فَأَشَارَتْ إِلَيْهِ ۖ قَالُوا كَيْفَ نُكَلِّمُ مَنْ كَانَ فِي الْمَهْدِ صَبِيًّا', 19, 29, 16, 62, 307, '(مریم) به او اشاره کرد؛ گفتند: «چگونه با کودکی که در گاهواره است سخن بگوییم؟!»'),
(2280, 2280, 'قَالَ إِنِّي عَبْدُ اللَّهِ آتَانِيَ الْكِتَابَ وَجَعَلَنِي نَبِيًّا', 19, 30, 16, 62, 307, '(ناگهان عیسی زبان به سخن گشود و) گفت: «من بنده خدایم؛ او کتاب (آسمانی) به من داده؛ و مرا پیامبر قرار داده است!'),
(2281, 2281, 'وَجَعَلَنِي مُبَارَكًا أَيْنَ مَا كُنْتُ وَأَوْصَانِي بِالصَّلَاةِ وَالزَّكَاةِ مَا دُمْتُ حَيًّا', 19, 31, 16, 62, 307, 'و مرا -هر جا که باشم- وجودی پربرکت قرار داده؛ و تا زمانی که زنده‌ام، مرا به نماز و زکات توصیه کرده است!'),
(2282, 2282, 'وَبَرًّا بِوَالِدَتِي وَلَمْ يَجْعَلْنِي جَبَّارًا شَقِيًّا', 19, 32, 16, 62, 307, 'و مرا نسبت به مادرم نیکوکار قرار داده؛ و جبّار و شقی قرار نداده است!'),
(2283, 2283, 'وَالسَّلَامُ عَلَيَّ يَوْمَ وُلِدْتُ وَيَوْمَ أَمُوتُ وَيَوْمَ أُبْعَثُ حَيًّا', 19, 33, 16, 62, 307, 'و سلام (خدا) بر من، در آن روز که متولّد شدم، و در آن روز که می‌میرم، و آن روز که زنده برانگیخته خواهم شد!»'),
(2284, 2284, 'ذَٰلِكَ عِيسَى ابْنُ مَرْيَمَ ۚ قَوْلَ الْحَقِّ الَّذِي فِيهِ يَمْتَرُونَ', 19, 34, 16, 62, 307, 'این است عیسی پسر مریم؛ گفتار حقّی که در آن تردید می‌کنند!'),
(2285, 2285, 'مَا كَانَ لِلَّهِ أَنْ يَتَّخِذَ مِنْ وَلَدٍ ۖ سُبْحَانَهُ ۚ إِذَا قَضَىٰ أَمْرًا فَإِنَّمَا يَقُولُ لَهُ كُنْ فَيَكُونُ', 19, 35, 16, 62, 307, 'هرگز برای خدا شایسته نبود که فرزندی اختیار کند! منزّه است او! هرگاه چیزی را فرمان دهد، می‌گوید: «موجود باش!» همان دم موجود می‌شود!'),
(2286, 2286, 'وَإِنَّ اللَّهَ رَبِّي وَرَبُّكُمْ فَاعْبُدُوهُ ۚ هَٰذَا صِرَاطٌ مُسْتَقِيمٌ', 19, 36, 16, 62, 307, 'و خداوند، پروردگار من و شماست! او را پرستش کنید؛ این است راه راست!'),
(2287, 2287, 'فَاخْتَلَفَ الْأَحْزَابُ مِنْ بَيْنِهِمْ ۖ فَوَيْلٌ لِلَّذِينَ كَفَرُوا مِنْ مَشْهَدِ يَوْمٍ عَظِيمٍ', 19, 37, 16, 62, 307, 'ولی (بعد از او) گروه‌هایی از میان پیروانش اختلاف کردند؛ وای به حال کافران از مشاهده روز بزرگ (رستاخیز)!'),
(2288, 2288, 'أَسْمِعْ بِهِمْ وَأَبْصِرْ يَوْمَ يَأْتُونَنَا ۖ لَٰكِنِ الظَّالِمُونَ الْيَوْمَ فِي ضَلَالٍ مُبِينٍ', 19, 38, 16, 62, 307, 'در آن روز که نزد ما می‌آیند، چه گوشهای شنوا و چه چشمهای بینایی پیدا می‌کنند! ولی این ستمگران امروز در گمراهی آشکارند!'),
(2289, 2289, 'وَأَنْذِرْهُمْ يَوْمَ الْحَسْرَةِ إِذْ قُضِيَ الْأَمْرُ وَهُمْ فِي غَفْلَةٍ وَهُمْ لَا يُؤْمِنُونَ', 19, 39, 16, 62, 308, 'آنان را از روز حسرت = روز رستاخیز که برای همه مایه تأسف است‌ بترسان، در آن هنگام که همه چیز پایان می‌یابد! و آنها در غفلتند و ایمان نمی‌آورند!'),
(2290, 2290, 'إِنَّا نَحْنُ نَرِثُ الْأَرْضَ وَمَنْ عَلَيْهَا وَإِلَيْنَا يُرْجَعُونَ', 19, 40, 16, 62, 308, 'ما، زمین و تمام کسانی را که بر آن هستند، به ارث می‌بریم؛ و همگی بسوی ما بازگردانده می‌شوند!'),
(2291, 2291, 'وَاذْكُرْ فِي الْكِتَابِ إِبْرَاهِيمَ ۚ إِنَّهُ كَانَ صِدِّيقًا نَبِيًّا', 19, 41, 16, 62, 308, 'در این کتاب، ابراهیم را یاد کن، که او بسیار راستگو، و پیامبر (خدا) بود!'),
(2292, 2292, 'إِذْ قَالَ لِأَبِيهِ يَا أَبَتِ لِمَ تَعْبُدُ مَا لَا يَسْمَعُ وَلَا يُبْصِرُ وَلَا يُغْنِي عَنْكَ شَيْئًا', 19, 42, 16, 62, 308, 'هنگامی که به پدرش گفت: «ای پدر! چرا چیزی را می‌پرستی که نه می‌شنود، و نه می‌بیند، و نه هیچ مشکلی را از تو حلّ می‌کند؟!'),
(2293, 2293, 'يَا أَبَتِ إِنِّي قَدْ جَاءَنِي مِنَ الْعِلْمِ مَا لَمْ يَأْتِكَ فَاتَّبِعْنِي أَهْدِكَ صِرَاطًا سَوِيًّا', 19, 43, 16, 62, 308, 'ای پدر! دانشی برای من آمده که برای تو نیامده است؛ بنابر این از من پیروی کن، تا تو را به راه راست هدایت کنم!'),
(2294, 2294, 'يَا أَبَتِ لَا تَعْبُدِ الشَّيْطَانَ ۖ إِنَّ الشَّيْطَانَ كَانَ لِلرَّحْمَٰنِ عَصِيًّا', 19, 44, 16, 62, 308, 'ای پدر! شیطان را پرستش مکن، که شیطان نسبت به خداوند رحمان، عصیانگر بود!'),
(2295, 2295, 'يَا أَبَتِ إِنِّي أَخَافُ أَنْ يَمَسَّكَ عَذَابٌ مِنَ الرَّحْمَٰنِ فَتَكُونَ لِلشَّيْطَانِ وَلِيًّا', 19, 45, 16, 62, 308, 'ای پدر! من از این می‌ترسم که از سوی خداوند رحمان عذابی به تو رسد، در نتیجه از دوستان شیطان باشی!»'),
(2296, 2296, 'قَالَ أَرَاغِبٌ أَنْتَ عَنْ آلِهَتِي يَا إِبْرَاهِيمُ ۖ لَئِنْ لَمْ تَنْتَهِ لَأَرْجُمَنَّكَ ۖ وَاهْجُرْنِي مَلِيًّا', 19, 46, 16, 62, 308, 'گفت: «ای ابراهیم! آیا تو از معبودهای من روی گردانی؟! اگر (از این کار) دست برنداری، تو را سنگسار می‌کنم! و برای مدّتی طولانی از من دور شو!»'),
(2297, 2297, 'قَالَ سَلَامٌ عَلَيْكَ ۖ سَأَسْتَغْفِرُ لَكَ رَبِّي ۖ إِنَّهُ كَانَ بِي حَفِيًّا', 19, 47, 16, 62, 308, '(ابراهیم) گفت: «سلام بر تو! من بزودی از پروردگارم برایت تقاضای عفو می‌کنم؛ چرا که او همواره نسبت به من مهربان بوده است!'),
(2298, 2298, 'وَأَعْتَزِلُكُمْ وَمَا تَدْعُونَ مِنْ دُونِ اللَّهِ وَأَدْعُو رَبِّي عَسَىٰ أَلَّا أَكُونَ بِدُعَاءِ رَبِّي شَقِيًّا', 19, 48, 16, 62, 308, 'و از شما، و آنچه غیر خدا می‌خوانید، کناره‌گیری می‌کنم؛ و پروردگارم را می‌خوانم؛ و امیدوارم در خواندن پروردگارم بی‌پاسخ نمانم!»'),
(2299, 2299, 'فَلَمَّا اعْتَزَلَهُمْ وَمَا يَعْبُدُونَ مِنْ دُونِ اللَّهِ وَهَبْنَا لَهُ إِسْحَاقَ وَيَعْقُوبَ ۖ وَكُلًّا جَعَلْنَا نَبِيًّا', 19, 49, 16, 62, 308, 'هنگامی که از آنان و آنچه غیر خدا می‌پرستیدند کناره‌گیری کرد، ما اسحاق و یعقوب را به او بخشیدیم؛ و هر یک را پیامبری (بزرگ) قرار دادیم!'),
(2300, 2300, 'وَوَهَبْنَا لَهُمْ مِنْ رَحْمَتِنَا وَجَعَلْنَا لَهُمْ لِسَانَ صِدْقٍ عَلِيًّا', 19, 50, 16, 62, 308, 'و از رحمت خود به آنان عطا کردیم؛ و برای آنها نام نیک و مقام برجسته‌ای (در میان همه امّتها) قرار دادیم!'),
(2301, 2301, 'وَاذْكُرْ فِي الْكِتَابِ مُوسَىٰ ۚ إِنَّهُ كَانَ مُخْلَصًا وَكَانَ رَسُولًا نَبِيًّا', 19, 51, 16, 62, 308, 'و در این کتاب (آسمانی) از موسی یاد کن، که او مخلص بود، و رسول و پیامبری والا مقام!'),
(2302, 2302, 'وَنَادَيْنَاهُ مِنْ جَانِبِ الطُّورِ الْأَيْمَنِ وَقَرَّبْنَاهُ نَجِيًّا', 19, 52, 16, 62, 309, 'ما او را از طرف راست (کوه) طور فراخواندیم؛ و نجواکنان او را (به خود) نزدیک ساختیم؛'),
(2303, 2303, 'وَوَهَبْنَا لَهُ مِنْ رَحْمَتِنَا أَخَاهُ هَارُونَ نَبِيًّا', 19, 53, 16, 62, 309, 'و ما از رحمت خود، برادرش هارون را -که پیامبر بود- به او بخشیدیم.'),
(2304, 2304, 'وَاذْكُرْ فِي الْكِتَابِ إِسْمَاعِيلَ ۚ إِنَّهُ كَانَ صَادِقَ الْوَعْدِ وَكَانَ رَسُولًا نَبِيًّا', 19, 54, 16, 62, 309, 'و در این کتاب (آسمانی) از اسماعیل (نیز) یاد کن، که او در وعده‌هایش صادق، و رسول و پیامبری (بزرگ) بود!'),
(2305, 2305, 'وَكَانَ يَأْمُرُ أَهْلَهُ بِالصَّلَاةِ وَالزَّكَاةِ وَكَانَ عِنْدَ رَبِّهِ مَرْضِيًّا', 19, 55, 16, 62, 309, 'او همواره خانواده‌اش را به نماز و زکات فرمان می‌داد؛ و همواره مورد رضایت پروردگارش بود.'),
(2306, 2306, 'وَاذْكُرْ فِي الْكِتَابِ إِدْرِيسَ ۚ إِنَّهُ كَانَ صِدِّيقًا نَبِيًّا', 19, 56, 16, 62, 309, 'و در این کتاب، از ادریس (نیز) یاد کن، او بسیار راستگو و پیامبر (بزرگی) بود.'),
(2307, 2307, 'وَرَفَعْنَاهُ مَكَانًا عَلِيًّا', 19, 57, 16, 62, 309, 'و ما او را به مقام والایی رساندیم.'),
(2308, 2308, 'أُولَٰئِكَ الَّذِينَ أَنْعَمَ اللَّهُ عَلَيْهِمْ مِنَ النَّبِيِّينَ مِنْ ذُرِّيَّةِ آدَمَ وَمِمَّنْ حَمَلْنَا مَعَ نُوحٍ وَمِنْ ذُرِّيَّةِ إِبْرَاهِيمَ وَإِسْرَائِيلَ وَمِمَّنْ هَدَيْنَا وَاجْتَبَيْنَا ۚ إِذَا تُتْلَىٰ عَلَيْهِمْ آيَاتُ الرَّحْمَٰنِ خَرُّوا سُجَّدًا وَبُكِيًّا ۩', 19, 58, 16, 62, 309, 'آنها پیامبرانی بودند که خداوند مشمول نعمتشان قرار داده بود، از فرزندان آدم، و از کسانی که با نوح بر کشتی سوار کردیم، و از دودمان ابراهیم و یعقوب، و از کسانی که هدایت کردیم و برگزیدیم. آنها کسانی بودند که وقتی آیات خداوند رحمان بر آنان خوانده می‌شد به خاک می‌افتادند، در حالی که سجده می‌کردند و گریان بودند.'),
(2309, 2309, ' فَخَلَفَ مِنْ بَعْدِهِمْ خَلْفٌ أَضَاعُوا الصَّلَاةَ وَاتَّبَعُوا الشَّهَوَاتِ ۖ فَسَوْفَ يَلْقَوْنَ غَيًّا', 19, 59, 16, 62, 309, 'امّا پس از آنان، فرزندان ناشایسته‌ای روی کار آمدند که نماز را تباه کردند، و از شهوات پیروی نمودند؛ و بزودی (مجازات) گمراهی خود را خواهند دید!'),
(2310, 2310, 'إِلَّا مَنْ تَابَ وَآمَنَ وَعَمِلَ صَالِحًا فَأُولَٰئِكَ يَدْخُلُونَ الْجَنَّةَ وَلَا يُظْلَمُونَ شَيْئًا', 19, 60, 16, 62, 309, 'مگر آنان که توبه کنند، و ایمان بیاورند، و کار شایسته انجام دهند؛ چنین کسانی داخل بهشت می‌شوند، و کمترین ستمی به آنان نخواهد شد.'),
(2311, 2311, 'جَنَّاتِ عَدْنٍ الَّتِي وَعَدَ الرَّحْمَٰنُ عِبَادَهُ بِالْغَيْبِ ۚ إِنَّهُ كَانَ وَعْدُهُ مَأْتِيًّا', 19, 61, 16, 62, 309, 'وارد باغهایی جاودانی می‌شوند که خداوند رحمان بندگانش را به آن وعده داده است؛ هر چند آن را ندیده‌اند؛ مسلماً وعده خدا تحقق یافتنی است!'),
(2312, 2312, 'لَا يَسْمَعُونَ فِيهَا لَغْوًا إِلَّا سَلَامًا ۖ وَلَهُمْ رِزْقُهُمْ فِيهَا بُكْرَةً وَعَشِيًّا', 19, 62, 16, 62, 309, 'در آن جا هرگز گفتار لغو و بیهوده‌ای نمی‌شنوند؛ و جز سلام در آن جا سخنی نیست؛ و هر صبح و شام، روزی آنان در بهشت مقرّر است.'),
(2313, 2313, 'تِلْكَ الْجَنَّةُ الَّتِي نُورِثُ مِنْ عِبَادِنَا مَنْ كَانَ تَقِيًّا', 19, 63, 16, 62, 309, 'این همان بهشتی است که به بندگان پرهیزگار خود، به ارث می‌دهیم.'),
(2314, 2314, 'وَمَا نَتَنَزَّلُ إِلَّا بِأَمْرِ رَبِّكَ ۖ لَهُ مَا بَيْنَ أَيْدِينَا وَمَا خَلْفَنَا وَمَا بَيْنَ ذَٰلِكَ ۚ وَمَا كَانَ رَبُّكَ نَسِيًّا', 19, 64, 16, 62, 309, '(پس از تأخیر وحی، جبرئیل به پیامبر عرض کرد:) ما جز بفرمان پروردگار تو، نازل نمی‌شویم؛ آنچه پیش روی ما، و پشت سر ما، و آنچه میان این دو می‌باشد، همه از آن اوست؛ و پروردگارت هرگز فراموشکار نبوده (و نیست)!'),
(2315, 2315, 'رَبُّ السَّمَاوَاتِ وَالْأَرْضِ وَمَا بَيْنَهُمَا فَاعْبُدْهُ وَاصْطَبِرْ لِعِبَادَتِهِ ۚ هَلْ تَعْلَمُ لَهُ سَمِيًّا', 19, 65, 16, 62, 310, 'همان پروردگار آسمانها و زمین، و آنچه میان آن دو قرار دارد! او را پرستش کن؛ و در راه عبادتش شکیبا باش! آیا مثل و مانندی برای او می‌یابی؟!'),
(2316, 2316, 'وَيَقُولُ الْإِنْسَانُ أَإِذَا مَا مِتُّ لَسَوْفَ أُخْرَجُ حَيًّا', 19, 66, 16, 62, 310, 'انسان می‌گوید: «آیا پس از مردن، زنده (از قبر) بیرون خواهم آمد؟!»'),
(2317, 2317, 'أَوَلَا يَذْكُرُ الْإِنْسَانُ أَنَّا خَلَقْنَاهُ مِنْ قَبْلُ وَلَمْ يَكُ شَيْئًا', 19, 67, 16, 62, 310, 'آیا انسان به خاطر نمی‌آورد که ما پیش از این او را آفریدیم در حالی که چیزی نبود؟!'),
(2318, 2318, 'فَوَرَبِّكَ لَنَحْشُرَنَّهُمْ وَالشَّيَاطِينَ ثُمَّ لَنُحْضِرَنَّهُمْ حَوْلَ جَهَنَّمَ جِثِيًّا', 19, 68, 16, 62, 310, 'سوگند به پروردگارت که همه آنها را همراه با شیاطین در قیامت جمع می‌کنیم؛ سپس همه را -در حالی که به زانو درآمده‌اند- گرداگرد جهنم حاضر می‌سازیم.'),
(2319, 2319, 'ثُمَّ لَنَنْزِعَنَّ مِنْ كُلِّ شِيعَةٍ أَيُّهُمْ أَشَدُّ عَلَى الرَّحْمَٰنِ عِتِيًّا', 19, 69, 16, 62, 310, 'سپس از هر گروه و جمعیّتی، کسانی را که در برابر خداوند رحمان از همه سرکش تر بوده‌اند، جدا می‌کنیم.'),
(2320, 2320, 'ثُمَّ لَنَحْنُ أَعْلَمُ بِالَّذِينَ هُمْ أَوْلَىٰ بِهَا صِلِيًّا', 19, 70, 16, 62, 310, 'بعد از آن، ما بخوبی از کسانی که برای سوختن در آتش سزاوارترند، آگاهتریم!'),
(2321, 2321, 'وَإِنْ مِنْكُمْ إِلَّا وَارِدُهَا ۚ كَانَ عَلَىٰ رَبِّكَ حَتْمًا مَقْضِيًّا', 19, 71, 16, 62, 310, 'و همه شما (بدون استثنا) وارد جهنم می‌شوید؛ این امری است حتمی و قطعی بر پروردگارت!'),
(2322, 2322, 'ثُمَّ نُنَجِّي الَّذِينَ اتَّقَوْا وَنَذَرُ الظَّالِمِينَ فِيهَا جِثِيًّا', 19, 72, 16, 62, 310, 'سپس آنها را که تقوا پیشه کردند از آن رهایی می‌بخشیم؛ و ظالمان را -در حالی که (از ضعف و ذلّت) به زانو درآمده‌اند- در آن رها می‌سازیم.'),
(2323, 2323, 'وَإِذَا تُتْلَىٰ عَلَيْهِمْ آيَاتُنَا بَيِّنَاتٍ قَالَ الَّذِينَ كَفَرُوا لِلَّذِينَ آمَنُوا أَيُّ الْفَرِيقَيْنِ خَيْرٌ مَقَامًا وَأَحْسَنُ نَدِيًّا', 19, 73, 16, 62, 310, 'و هنگامی که آیات روشن ما بر آنان خوانده می‌شود، کافران به مؤمنان می‌گویند: «کدام یک از دو گروه = ما و شما جایگاهش بهتر، و جلسات انس و مشورتش زیباتر، و بخشش او بیشتر است؟!»'),
(2324, 2324, 'وَكَمْ أَهْلَكْنَا قَبْلَهُمْ مِنْ قَرْنٍ هُمْ أَحْسَنُ أَثَاثًا وَرِئْيًا', 19, 74, 16, 62, 310, 'چه بسیار اقوامی را پیش از آنان نابود کردیم که هم مال و ثروتشان از آنها بهتر بود، و هم ظاهرشان آراسته‌تر!'),
(2325, 2325, 'قُلْ مَنْ كَانَ فِي الضَّلَالَةِ فَلْيَمْدُدْ لَهُ الرَّحْمَٰنُ مَدًّا ۚ حَتَّىٰ إِذَا رَأَوْا مَا يُوعَدُونَ إِمَّا الْعَذَابَ وَإِمَّا السَّاعَةَ فَسَيَعْلَمُونَ مَنْ هُوَ شَرٌّ مَكَانًا وَأَضْعَفُ جُنْدًا', 19, 75, 16, 62, 310, 'بگو: «کسی که در گمراهی است، باید خداوند به او مهلت دهد تا زمانی که وعده الهی را با چشم خود ببینند: یا عذاب (این دنیا)، یا (عذاب) قیامت! (آن روز) خواهند دانست چه کسی جایش بدتر، و لشکرش ناتوانتر است!»'),
(2326, 2326, 'وَيَزِيدُ اللَّهُ الَّذِينَ اهْتَدَوْا هُدًى ۗ وَالْبَاقِيَاتُ الصَّالِحَاتُ خَيْرٌ عِنْدَ رَبِّكَ ثَوَابًا وَخَيْرٌ مَرَدًّا', 19, 76, 16, 62, 310, '(امّا) کسانی که در راه هدایت گام نهادند، خداوند بر هدایتشان می‌افزاید؛ و آثار شایسته‌ای که (از انسان) باقی می‌ماند، ثوابش در پیشگاه پروردگارت بهتر، و عاقبتش خوبتر است!'),
(2327, 2327, 'أَفَرَأَيْتَ الَّذِي كَفَرَ بِآيَاتِنَا وَقَالَ لَأُوتَيَنَّ مَالًا وَوَلَدًا', 19, 77, 16, 62, 311, 'آیا دیدی کسی را که به آیات ما کافر شد، و گفت: «اموال و فرزندان فراوانی به من داده خواهد شد»؟!'),
(2328, 2328, 'أَطَّلَعَ الْغَيْبَ أَمِ اتَّخَذَ عِنْدَ الرَّحْمَٰنِ عَهْدًا', 19, 78, 16, 62, 311, 'آیا او از غیب آگاه گشته، یا نزد خدا عهد و پیمانی گرفته است؟!'),
(2329, 2329, 'كَلَّا ۚ سَنَكْتُبُ مَا يَقُولُ وَنَمُدُّ لَهُ مِنَ الْعَذَابِ مَدًّا', 19, 79, 16, 62, 311, 'هرگز چنین نیست! ما بزودی آنچه را می‌گوید می‌نویسیم و عذاب را بر او مستمرّ خواهیم داشت!'),
(2330, 2330, 'وَنَرِثُهُ مَا يَقُولُ وَيَأْتِينَا فَرْدًا', 19, 80, 16, 62, 311, 'آنچه را او می‌گوید (از اموال و فرزندان)، از او به ارث می‌بریم، و به صورت تنها نزد ما خواهد آمد!'),
(2331, 2331, 'وَاتَّخَذُوا مِنْ دُونِ اللَّهِ آلِهَةً لِيَكُونُوا لَهُمْ عِزًّا', 19, 81, 16, 62, 311, 'و آنان غیر از خدا، معبودانی را برای خود برگزیدند تا مایه عزّتشان باشد! (چه پندار خامی!)'),
(2332, 2332, 'كَلَّا ۚ سَيَكْفُرُونَ بِعِبَادَتِهِمْ وَيَكُونُونَ عَلَيْهِمْ ضِدًّا', 19, 82, 16, 62, 311, 'هرگز چنین نیست! به زودی (معبودها) منکر عبادت آنان خواهند شد؛ (بلکه) بر ضدّشان قیام می‌کنند!'),
(2333, 2333, 'أَلَمْ تَرَ أَنَّا أَرْسَلْنَا الشَّيَاطِينَ عَلَى الْكَافِرِينَ تَؤُزُّهُمْ أَزًّا', 19, 83, 16, 62, 311, 'آیا ندیدی که ما شیاطین را بسوی کافران فرستادیم تا آنان را شدیداً تحریک کنند؟!'),
(2334, 2334, 'فَلَا تَعْجَلْ عَلَيْهِمْ ۖ إِنَّمَا نَعُدُّ لَهُمْ عَدًّا', 19, 84, 16, 62, 311, 'پس درباره آنان شتاب مکن؛ ما آنها (و اعمالشان) را به دقت شماره میکنیم!'),
(2335, 2335, 'يَوْمَ نَحْشُرُ الْمُتَّقِينَ إِلَى الرَّحْمَٰنِ وَفْدًا', 19, 85, 16, 62, 311, 'در آن روز که پرهیزگاران را دسته جمعی بسوی خداوند رحمان (و پاداشهای او) محشور می‌کنیم...'),
(2336, 2336, 'وَنَسُوقُ الْمُجْرِمِينَ إِلَىٰ جَهَنَّمَ وِرْدًا', 19, 86, 16, 62, 311, 'و مجرمان را (همچون شتران تشنه‌کامی که به سوی آبگاه می‌روند) به جهنّم می‌رانیم...'),
(2337, 2337, 'لَا يَمْلِكُونَ الشَّفَاعَةَ إِلَّا مَنِ اتَّخَذَ عِنْدَ الرَّحْمَٰنِ عَهْدًا', 19, 87, 16, 62, 311, 'آنان هرگز مالک شفاعت نیستند؛ مگر کسی که نزد خداوند رحمان، عهد و پیمانی دارد.'),
(2338, 2338, 'وَقَالُوا اتَّخَذَ الرَّحْمَٰنُ وَلَدًا', 19, 88, 16, 62, 311, 'و گفتند: «خداوند رحمان فرزندی برای خود برگزیده است».'),
(2339, 2339, 'لَقَدْ جِئْتُمْ شَيْئًا إِدًّا', 19, 89, 16, 62, 311, 'راستی مطلب زشت و زننده‌ای گفتید!'),
(2340, 2340, 'تَكَادُ السَّمَاوَاتُ يَتَفَطَّرْنَ مِنْهُ وَتَنْشَقُّ الْأَرْضُ وَتَخِرُّ الْجِبَالُ هَدًّا', 19, 90, 16, 62, 311, 'نزدیک است به خاطر این سخن آسمانها از هم متلاشی گردد، و زمین شکافته شود، و کوه‌ها بشدّت فرو ریزد...'),
(2341, 2341, 'أَنْ دَعَوْا لِلرَّحْمَٰنِ وَلَدًا', 19, 91, 16, 62, 311, 'از این رو که برای خداوند رحمان فرزندی قائل شدند!'),
(2342, 2342, 'وَمَا يَنْبَغِي لِلرَّحْمَٰنِ أَنْ يَتَّخِذَ وَلَدًا', 19, 92, 16, 62, 311, 'در حالی که هرگز برای خداوند رحمان سزاوار نیست که فرزندی برگزیند!'),
(2343, 2343, 'إِنْ كُلُّ مَنْ فِي السَّمَاوَاتِ وَالْأَرْضِ إِلَّا آتِي الرَّحْمَٰنِ عَبْدًا', 19, 93, 16, 62, 311, 'تمام کسانی که در آسمانها و زمین هستند، بنده اویند!'),
(2344, 2344, 'لَقَدْ أَحْصَاهُمْ وَعَدَّهُمْ عَدًّا', 19, 94, 16, 62, 311, 'خداوند همه آنها را احصا کرده، و به دقّت شمرده است!'),
(2345, 2345, 'وَكُلُّهُمْ آتِيهِ يَوْمَ الْقِيَامَةِ فَرْدًا', 19, 95, 16, 62, 311, 'و همگی روز رستاخیز، تک و تنها نزد او حاضر می‌شوند!'),
(2346, 2346, 'إِنَّ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ سَيَجْعَلُ لَهُمُ الرَّحْمَٰنُ وُدًّا', 19, 96, 16, 62, 312, 'مسلّماً کسانی که ایمان آورده و کارهای شایسته انجام داده‌اند، خداوند رحمان محبّتی برای آنان در دلها قرار می‌دهد!'),
(2347, 2347, 'فَإِنَّمَا يَسَّرْنَاهُ بِلِسَانِكَ لِتُبَشِّرَ بِهِ الْمُتَّقِينَ وَتُنْذِرَ بِهِ قَوْمًا لُدًّا', 19, 97, 16, 62, 312, 'و ما فقط آن = قرآن‌ را بر زبان تو آسان ساختیم تا پرهیزگاران را بوسیله آن بشارت دهی، و دشمنان سرسخت را با آنان انذار کنی.'),
(2348, 2348, 'وَكَمْ أَهْلَكْنَا قَبْلَهُمْ مِنْ قَرْنٍ هَلْ تُحِسُّ مِنْهُمْ مِنْ أَحَدٍ أَوْ تَسْمَعُ لَهُمْ رِكْزًا', 19, 98, 16, 62, 312, 'چه بسیار اقوام (بی‌ایمان و گنهکاری) را که پیش از آنان هلاک کردیم؛ آیا احدی از آنها را احساس می‌کنی؟! یا کمترین صدایی از آنان می‌شنوی؟!'),
(2349, 2349, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ طه', 20, 1, 16, 63, 312, 'طه‌'),
(2350, 2350, 'مَا أَنْزَلْنَا عَلَيْكَ الْقُرْآنَ لِتَشْقَىٰ', 20, 2, 16, 63, 312, 'ما قرآن را بر تو نازل نکردیم که خود را به زحمت بیفکنی!'),
(2351, 2351, 'إِلَّا تَذْكِرَةً لِمَنْ يَخْشَىٰ', 20, 3, 16, 63, 312, 'آن را فقط برای یادآوری کسانی که (از خدا) می‌ترسند نازل ساختیم.'),
(2352, 2352, 'تَنْزِيلًا مِمَّنْ خَلَقَ الْأَرْضَ وَالسَّمَاوَاتِ الْعُلَى', 20, 4, 16, 63, 312, '(این قرآن) از سوی کسی نازل شده که زمین و آسمانهای بلند را آفریده است.'),
(2353, 2353, 'الرَّحْمَٰنُ عَلَى الْعَرْشِ اسْتَوَىٰ', 20, 5, 16, 63, 312, 'همان بخشنده‌ای که بر عرش مسلّط است.'),
(2354, 2354, 'لَهُ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ وَمَا بَيْنَهُمَا وَمَا تَحْتَ الثَّرَىٰ', 20, 6, 16, 63, 312, 'از آن اوست آنچه در آسمانها، و آنچه در زمین، و آنچه میان آن دو، و آنچه در زیر خاک (پنهان) است!'),
(2355, 2355, 'وَإِنْ تَجْهَرْ بِالْقَوْلِ فَإِنَّهُ يَعْلَمُ السِّرَّ وَأَخْفَى', 20, 7, 16, 63, 312, 'اگر سخن آشکارا بگویی (یا مخفی کنی)، او اسرار -و حتی پنهان‌تر از آن- را نیز می‌داند!'),
(2356, 2356, 'اللَّهُ لَا إِلَٰهَ إِلَّا هُوَ ۖ لَهُ الْأَسْمَاءُ الْحُسْنَىٰ', 20, 8, 16, 63, 312, 'او خداوندی است که معبودی جز او نیست؛ و نامهای نیکوتر از آن اوست!'),
(2357, 2357, 'وَهَلْ أَتَاكَ حَدِيثُ مُوسَىٰ', 20, 9, 16, 63, 312, 'و آیا خبر موسی به تو رسیده است؟'),
(2358, 2358, 'إِذْ رَأَىٰ نَارًا فَقَالَ لِأَهْلِهِ امْكُثُوا إِنِّي آنَسْتُ نَارًا لَعَلِّي آتِيكُمْ مِنْهَا بِقَبَسٍ أَوْ أَجِدُ عَلَى النَّارِ هُدًى', 20, 10, 16, 63, 312, 'هنگامی که (از دور) آتشی مشاهده کرد، و به خانواده خود گفت: «(اندکی) درنگ کنید که من آتشی دیدم! شاید شعله‌ای از آن برای شما بیاورم؛ یا بوسیله این آتش راه را پیدا کنم!»'),
(2359, 2359, 'فَلَمَّا أَتَاهَا نُودِيَ يَا مُوسَىٰ', 20, 11, 16, 63, 312, 'هنگامی که نزد آتش آمد، ندا داده شد که: «ای موسی!'),
(2360, 2360, 'إِنِّي أَنَا رَبُّكَ فَاخْلَعْ نَعْلَيْكَ ۖ إِنَّكَ بِالْوَادِ الْمُقَدَّسِ طُوًى', 20, 12, 16, 63, 312, 'من پروردگار توام! کفشهایت را بیرون آر، که تو در سرزمین مقدّس «طوی» هستی!'),
(2361, 2361, 'وَأَنَا اخْتَرْتُكَ فَاسْتَمِعْ لِمَا يُوحَىٰ', 20, 13, 16, 63, 313, 'و من تو را (برای مقام رسالت) برگزیدم؛ اکنون به آنچه بر تو وحی می‌شود، گوش فراده!'),
(2362, 2362, 'إِنَّنِي أَنَا اللَّهُ لَا إِلَٰهَ إِلَّا أَنَا فَاعْبُدْنِي وَأَقِمِ الصَّلَاةَ لِذِكْرِي', 20, 14, 16, 63, 313, 'من «اللّه» هستم؛ معبودی جز من نیست! مرا بپرست، و نماز را برای یاد من بپادار!'),
(2363, 2363, 'إِنَّ السَّاعَةَ آتِيَةٌ أَكَادُ أُخْفِيهَا لِتُجْزَىٰ كُلُّ نَفْسٍ بِمَا تَسْعَىٰ', 20, 15, 16, 63, 313, 'بطور قطع رستاخیز خواهد آمد! می‌خواهم آن را پنهان کنم، تا هر کس در برابر سعی و کوشش خود، جزا داده شود!'),
(2364, 2364, 'فَلَا يَصُدَّنَّكَ عَنْهَا مَنْ لَا يُؤْمِنُ بِهَا وَاتَّبَعَ هَوَاهُ فَتَرْدَىٰ', 20, 16, 16, 63, 313, 'پس مبادا کسی که به آن ایمان ندارد و از هوسهای خویش پیروی می‌کند، تو را از آن بازدارد؛ که هلاک خواهی شد!'),
(2365, 2365, 'وَمَا تِلْكَ بِيَمِينِكَ يَا مُوسَىٰ', 20, 17, 16, 63, 313, 'و آن چیست در دست راست تو، ای موسی؟!'),
(2366, 2366, 'قَالَ هِيَ عَصَايَ أَتَوَكَّأُ عَلَيْهَا وَأَهُشُّ بِهَا عَلَىٰ غَنَمِي وَلِيَ فِيهَا مَآرِبُ أُخْرَىٰ', 20, 18, 16, 63, 313, 'گفت: «این عصای من است؛ بر آن تکیه می‌کنم، برگ درختان را با آن برای گوسفندانم فرومی‌ریزم؛ و مرا با آن کارها و نیازهای دیگری است.'),
(2367, 2367, 'قَالَ أَلْقِهَا يَا مُوسَىٰ', 20, 19, 16, 63, 313, 'گفت: «ای موسی! آن را بیفکن.»'),
(2368, 2368, 'فَأَلْقَاهَا فَإِذَا هِيَ حَيَّةٌ تَسْعَىٰ', 20, 20, 16, 63, 313, 'پس موسی آن (عصا) را افکند، که ناگهان اژدهایی شد که به هر سو می‌شتافت.'),
(2369, 2369, 'قَالَ خُذْهَا وَلَا تَخَفْ ۖ سَنُعِيدُهَا سِيرَتَهَا الْأُولَىٰ', 20, 21, 16, 63, 313, 'گفت: «آن را بگیر و نترس، ما آن را به صورت اولش بازمی‌گردانیم.'),
(2370, 2370, 'وَاضْمُمْ يَدَكَ إِلَىٰ جَنَاحِكَ تَخْرُجْ بَيْضَاءَ مِنْ غَيْرِ سُوءٍ آيَةً أُخْرَىٰ', 20, 22, 16, 63, 313, 'و دستت را به گریبانت ببر، تا سفید و بی‌عیب بیرون آید؛ این نشانه دیگری (از سوی خداوند) است.'),
(2371, 2371, 'لِنُرِيَكَ مِنْ آيَاتِنَا الْكُبْرَى', 20, 23, 16, 63, 313, 'تا از نشانه‌های بزرگ خویش به تو نشان دهیم.'),
(2372, 2372, 'اذْهَبْ إِلَىٰ فِرْعَوْنَ إِنَّهُ طَغَىٰ', 20, 24, 16, 63, 313, 'اینک به سوی فرعون برو، که او طغیان کرده است.»'),
(2373, 2373, 'قَالَ رَبِّ اشْرَحْ لِي صَدْرِي', 20, 25, 16, 63, 313, '(موسی) گفت: «پروردگارا! سینه‌ام را گشاده کن؛'),
(2374, 2374, 'وَيَسِّرْ لِي أَمْرِي', 20, 26, 16, 63, 313, 'و کارم را برایم آسان گردان!'),
(2375, 2375, 'وَاحْلُلْ عُقْدَةً مِنْ لِسَانِي', 20, 27, 16, 63, 313, 'و گره از زبانم بگشای؛'),
(2376, 2376, 'يَفْقَهُوا قَوْلِي', 20, 28, 16, 63, 313, 'تا سخنان مرا بفهمند!'),
(2377, 2377, 'وَاجْعَلْ لِي وَزِيرًا مِنْ أَهْلِي', 20, 29, 16, 63, 313, 'و وزیری از خاندانم برای من قرار ده...'),
(2378, 2378, 'هَارُونَ أَخِي', 20, 30, 16, 63, 313, 'برادرم هارون را!'),
(2379, 2379, 'اشْدُدْ بِهِ أَزْرِي', 20, 31, 16, 63, 313, 'با او پشتم را محکم کن؛'),
(2380, 2380, 'وَأَشْرِكْهُ فِي أَمْرِي', 20, 32, 16, 63, 313, 'و او را در کارم شریک ساز؛'),
(2381, 2381, 'كَيْ نُسَبِّحَكَ كَثِيرًا', 20, 33, 16, 63, 313, 'تا تو را بسیار تسبیح گوییم؛'),
(2382, 2382, 'وَنَذْكُرَكَ كَثِيرًا', 20, 34, 16, 63, 313, 'و تو را بسیار یاد کنیم؛'),
(2383, 2383, 'إِنَّكَ كُنْتَ بِنَا بَصِيرًا', 20, 35, 16, 63, 313, 'چرا که تو همیشه از حال ما آگاه بوده‌ای!»'),
(2384, 2384, 'قَالَ قَدْ أُوتِيتَ سُؤْلَكَ يَا مُوسَىٰ', 20, 36, 16, 63, 313, 'فرمود: «ای موسی! آنچه را خواستی به تو داده شد!'),
(2385, 2385, 'وَلَقَدْ مَنَنَّا عَلَيْكَ مَرَّةً أُخْرَىٰ', 20, 37, 16, 63, 313, 'و ما بار دیگر تو را مشمول نعمت خود ساختیم...'),
(2386, 2386, 'إِذْ أَوْحَيْنَا إِلَىٰ أُمِّكَ مَا يُوحَىٰ', 20, 38, 16, 63, 314, 'آن زمان که به مادرت آنچه لازم بود الهام کردیم...'),
(2387, 2387, 'أَنِ اقْذِفِيهِ فِي التَّابُوتِ فَاقْذِفِيهِ فِي الْيَمِّ فَلْيُلْقِهِ الْيَمُّ بِالسَّاحِلِ يَأْخُذْهُ عَدُوٌّ لِي وَعَدُوٌّ لَهُ ۚ وَأَلْقَيْتُ عَلَيْكَ مَحَبَّةً مِنِّي وَلِتُصْنَعَ عَلَىٰ عَيْنِي', 20, 39, 16, 63, 314, 'که: «او را در صندوقی بیفکن، و آن صندوق را به دریا بینداز، تا دریا آن را به ساحل افکند؛ و دشمن من و دشمن او، آن را برگیرد!» و من محبّتی از خودم بر تو افکندم، تا در برابر دیدگان = علم‌ من، ساخته شوی (و پرورش یابی)!'),
(2388, 2388, 'إِذْ تَمْشِي أُخْتُكَ فَتَقُولُ هَلْ أَدُلُّكُمْ عَلَىٰ مَنْ يَكْفُلُهُ ۖ فَرَجَعْنَاكَ إِلَىٰ أُمِّكَ كَيْ تَقَرَّ عَيْنُهَا وَلَا تَحْزَنَ ۚ وَقَتَلْتَ نَفْسًا فَنَجَّيْنَاكَ مِنَ الْغَمِّ وَفَتَنَّاكَ فُتُونًا ۚ فَلَبِثْتَ سِنِينَ فِي أَهْلِ مَدْيَنَ ثُمَّ جِئْتَ عَلَىٰ قَدَرٍ يَا مُوسَىٰ', 20, 40, 16, 63, 314, 'در آن هنگام که خواهرت (در نزدیکی کاخ فرعون) راه می‌رفت و می‌گفت: «آیا کسی را به شما نشان دهم که این نوزاد را کفالت می‌کند (و دایه خوبی برای او خواهد بود)!» پس تو را به مادرت بازگرداندیم، تا چشمش به تو روشن شود؛ و غمگین نگردد! و تو یکی (از فرعونیان) را کشتی؛ اما ما تو را از اندوه نجات دادیم! و بارها تو را آزمودیم! پس از آن، سالیانی در میان مردم «مدین» توقف نمودی؛ سپس در زمان مقدّر (برای فرمان رسالت) به این جا آمدی، ای موسی!'),
(2389, 2389, 'وَاصْطَنَعْتُكَ لِنَفْسِي', 20, 41, 16, 63, 314, 'و من تو را برای خودم ساختم (و پرورش دادم)!'),
(2390, 2390, 'اذْهَبْ أَنْتَ وَأَخُوكَ بِآيَاتِي وَلَا تَنِيَا فِي ذِكْرِي', 20, 42, 16, 63, 314, '(اکنون) تو و برادرت با آیات من بروید، و در یاد من کوتاهی نکنید!'),
(2391, 2391, 'اذْهَبَا إِلَىٰ فِرْعَوْنَ إِنَّهُ طَغَىٰ', 20, 43, 16, 63, 314, 'بسوی فرعون بروید؛ که طغیان کرده است!'),
(2392, 2392, 'فَقُولَا لَهُ قَوْلًا لَيِّنًا لَعَلَّهُ يَتَذَكَّرُ أَوْ يَخْشَىٰ', 20, 44, 16, 63, 314, 'اما بنرمی با او سخن بگویید؛ شاید متذکّر شود، یا (از خدا) بترسد!'),
(2393, 2393, 'قَالَا رَبَّنَا إِنَّنَا نَخَافُ أَنْ يَفْرُطَ عَلَيْنَا أَوْ أَنْ يَطْغَىٰ', 20, 45, 16, 63, 314, '(موسی و هارون) گفتند: «پروردگارا! از این می‌ترسیم که بر ما پیشی گیرد (و قبل از بیان حق، ما را آزار دهد)؛ یا طغیان کند (و نپذیرد)!»'),
(2394, 2394, 'قَالَ لَا تَخَافَا ۖ إِنَّنِي مَعَكُمَا أَسْمَعُ وَأَرَىٰ', 20, 46, 16, 63, 314, 'فرمود: «نترسید! من با شما هستم؛ (همه چیز را) می‌شنوم و می‌بینم!'),
(2395, 2395, 'فَأْتِيَاهُ فَقُولَا إِنَّا رَسُولَا رَبِّكَ فَأَرْسِلْ مَعَنَا بَنِي إِسْرَائِيلَ وَلَا تُعَذِّبْهُمْ ۖ قَدْ جِئْنَاكَ بِآيَةٍ مِنْ رَبِّكَ ۖ وَالسَّلَامُ عَلَىٰ مَنِ اتَّبَعَ الْهُدَىٰ', 20, 47, 16, 63, 314, 'به سراغ او بروید و بگویید: «ما فرستادگان پروردگار توئیم! بنی اسرائیل را با ما بفرست؛ و آنان را شکنجه و آزار مکن! ما نشانه روشنی از سوی پروردگارت برای تو آورده‌ایم! و درود بر آن کس باد که از هدایت پیروی می‌کند!'),
(2396, 2396, 'إِنَّا قَدْ أُوحِيَ إِلَيْنَا أَنَّ الْعَذَابَ عَلَىٰ مَنْ كَذَّبَ وَتَوَلَّىٰ', 20, 48, 16, 63, 314, 'به ما وحی شده که عذاب بر کسی است که (آیات الهی را) تکذیب کند و سرپیچی نماید!»'),
(2397, 2397, 'قَالَ فَمَنْ رَبُّكُمَا يَا مُوسَىٰ', 20, 49, 16, 63, 314, '(فرعون) گفت: «پروردگار شما کیست، ای موسی؟!»'),
(2398, 2398, 'قَالَ رَبُّنَا الَّذِي أَعْطَىٰ كُلَّ شَيْءٍ خَلْقَهُ ثُمَّ هَدَىٰ', 20, 50, 16, 63, 314, 'گفت: «پروردگار ما همان کسی است که به هر موجودی، آنچه را لازمه آفرینش او بوده داده؛ سپس هدایت کرده است!»'),
(2399, 2399, 'قَالَ فَمَا بَالُ الْقُرُونِ الْأُولَىٰ', 20, 51, 16, 63, 314, 'گفت: «پس تکلیف نسلهای گذشته (که به اینها ایمان نداشتند) چه خواهد شد؟!»'),
(2400, 2400, 'قَالَ عِلْمُهَا عِنْدَ رَبِّي فِي كِتَابٍ ۖ لَا يَضِلُّ رَبِّي وَلَا يَنْسَى', 20, 52, 16, 63, 315, 'گفت: «آگاهی مربوط به آنها، نزد پروردگارم در کتابی ثبت است؛ پروردگارم هرگز گمراه نمی‌شود، و فراموش نمی‌کند (و آنچه شایسته آنهاست به ایشان می‌دهد)!'),
(2401, 2401, 'الَّذِي جَعَلَ لَكُمُ الْأَرْضَ مَهْدًا وَسَلَكَ لَكُمْ فِيهَا سُبُلًا وَأَنْزَلَ مِنَ السَّمَاءِ مَاءً فَأَخْرَجْنَا بِهِ أَزْوَاجًا مِنْ نَبَاتٍ شَتَّىٰ', 20, 53, 16, 63, 315, 'همان خداوندی که زمین را برای شما محل آسایش قرار داد؛ و راه‌هایی در آن ایجاد کرد؛ و از آسمان، آبی فرستاد!» که با آن، انواع گوناگون گیاهان را (از خاک تیره) برآوردیم.'),
(2402, 2402, 'كُلُوا وَارْعَوْا أَنْعَامَكُمْ ۗ إِنَّ فِي ذَٰلِكَ لَآيَاتٍ لِأُولِي النُّهَىٰ', 20, 54, 16, 63, 315, 'هم خودتان بخورید؛ و هم چهارپایانتان را در آن به چرا برید! مسلّماً در اینها نشانه‌های روشنی برای خردمندان است!'),
(2403, 2403, ' مِنْهَا خَلَقْنَاكُمْ وَفِيهَا نُعِيدُكُمْ وَمِنْهَا نُخْرِجُكُمْ تَارَةً أُخْرَىٰ', 20, 55, 16, 63, 315, 'ما شما را از آن = زمین‌ آفریدیم؛ و در آن بازمی‌گردانیم؛ و بار دیگر (در قیامت) شما را از آن بیرون می‌آوریم!'),
(2404, 2404, 'وَلَقَدْ أَرَيْنَاهُ آيَاتِنَا كُلَّهَا فَكَذَّبَ وَأَبَىٰ', 20, 56, 16, 63, 315, 'ما همه آیات خود را به او نشان دادیم؛ اما او تکذیب کرد و سرباز زد!'),
(2405, 2405, 'قَالَ أَجِئْتَنَا لِتُخْرِجَنَا مِنْ أَرْضِنَا بِسِحْرِكَ يَا مُوسَىٰ', 20, 57, 16, 63, 315, 'گفت: «ای موسی! آیا آمده‌ای که با سحر خود، ما را از سرزمینمان بیرون کنی؟!');
INSERT INTO `Quran` (`rowid`, `id`, `Arabic`, `Chapter`, `Verse`, `Juz`, `Hizb`, `Page`, `tr`) VALUES
(2406, 2406, 'فَلَنَأْتِيَنَّكَ بِسِحْرٍ مِثْلِهِ فَاجْعَلْ بَيْنَنَا وَبَيْنَكَ مَوْعِدًا لَا نُخْلِفُهُ نَحْنُ وَلَا أَنْتَ مَكَانًا سُوًى', 20, 58, 16, 63, 315, 'قطعاً ما هم سحری همانند آن برای تو خواهیم آورد! هم اکنون (تاریخش را تعیین کن، و) موعدی میان ما و خودت قرار ده که نه ما و نه تو، از آن تخلّف نکنیم؛ آن هم در مکانی که نسبت به همه یکسان باشد!»'),
(2407, 2407, 'قَالَ مَوْعِدُكُمْ يَوْمُ الزِّينَةِ وَأَنْ يُحْشَرَ النَّاسُ ضُحًى', 20, 59, 16, 63, 315, 'گفت: «میعاد ما و شما روز زینت = روز عید است؛ به شرط اینکه همه مردم، هنگامی که روز، بالا می‌آید، جمع شوند!»'),
(2408, 2408, 'فَتَوَلَّىٰ فِرْعَوْنُ فَجَمَعَ كَيْدَهُ ثُمَّ أَتَىٰ', 20, 60, 16, 63, 315, 'فرعون آن مجلس را ترک گفت؛ و تمام مکر و فریب خود را جمع کرد؛ و سپس همه را (در روز موعود) آورد.'),
(2409, 2409, 'قَالَ لَهُمْ مُوسَىٰ وَيْلَكُمْ لَا تَفْتَرُوا عَلَى اللَّهِ كَذِبًا فَيُسْحِتَكُمْ بِعَذَابٍ ۖ وَقَدْ خَابَ مَنِ افْتَرَىٰ', 20, 61, 16, 63, 315, 'موسی به آنان گفت: «وای بر شما! دروغ بر خدا نبندید، که شما را با عذابی نابود می‌سازد! و هر کس که (بر خدا) دروغ ببندد، نومید (و شکست خورده) می‌شود!»'),
(2410, 2410, 'فَتَنَازَعُوا أَمْرَهُمْ بَيْنَهُمْ وَأَسَرُّوا النَّجْوَىٰ', 20, 62, 16, 63, 315, 'آنها در میان خود، در مورد ادامه راهشان به نزاع برخاستند؛ و مخفیانه و درگوشی با هم سخن گفتند.'),
(2411, 2411, 'قَالُوا إِنْ هَٰذَانِ لَسَاحِرَانِ يُرِيدَانِ أَنْ يُخْرِجَاكُمْ مِنْ أَرْضِكُمْ بِسِحْرِهِمَا وَيَذْهَبَا بِطَرِيقَتِكُمُ الْمُثْلَىٰ', 20, 63, 16, 63, 315, 'گفتند: «این دو (نفر) مسلّماً ساحرند! می‌خواهند با سحرشان شما را از سرزمینتان بیرون کنند و راه و رسم نمونه شما را از بین ببرند!'),
(2412, 2412, 'فَأَجْمِعُوا كَيْدَكُمْ ثُمَّ ائْتُوا صَفًّا ۚ وَقَدْ أَفْلَحَ الْيَوْمَ مَنِ اسْتَعْلَىٰ', 20, 64, 16, 63, 315, 'اکنون که چنین است، تمام نیرو و نقشه خود را جمع کنید، و در یک صف (به میدان مبارزه) بیایید؛ امروز رستگاری از آن کسی است که برتری خود را اثبات کند!'),
(2413, 2413, 'قَالُوا يَا مُوسَىٰ إِمَّا أَنْ تُلْقِيَ وَإِمَّا أَنْ نَكُونَ أَوَّلَ مَنْ أَلْقَىٰ', 20, 65, 16, 63, 316, '(ساحران) گفتند: «ای موسی! آیا تو اول (عصای خود را) می‌افکنی، یا ما کسانی باشیم که اول بیفکنیم؟!»'),
(2414, 2414, 'قَالَ بَلْ أَلْقُوا ۖ فَإِذَا حِبَالُهُمْ وَعِصِيُّهُمْ يُخَيَّلُ إِلَيْهِ مِنْ سِحْرِهِمْ أَنَّهَا تَسْعَىٰ', 20, 66, 16, 63, 316, 'گفت: «شما اول بیفکنید!» در این هنگام طنابها و عصاهای آنان بر اثر سحرشان چنان به نظر می‌رسید که حرکت می‌کند!'),
(2415, 2415, 'فَأَوْجَسَ فِي نَفْسِهِ خِيفَةً مُوسَىٰ', 20, 67, 16, 63, 316, 'موسی ترس خفیفی در دل احساس کرد (مبادا مردم گمراه شوند)!'),
(2416, 2416, 'قُلْنَا لَا تَخَفْ إِنَّكَ أَنْتَ الْأَعْلَىٰ', 20, 68, 16, 63, 316, 'گفتیم: «نترس! تو مسلّماً (پیروز و) برتری!'),
(2417, 2417, 'وَأَلْقِ مَا فِي يَمِينِكَ تَلْقَفْ مَا صَنَعُوا ۖ إِنَّمَا صَنَعُوا كَيْدُ سَاحِرٍ ۖ وَلَا يُفْلِحُ السَّاحِرُ حَيْثُ أَتَىٰ', 20, 69, 16, 63, 316, 'و آنچه را در دست راست داری بیفکن، تمام آنچه را ساخته‌اند می‌بلعد! آنچه ساخته‌اند تنها مکر ساحر است؛ و ساحر هر جا رود رستگار نخواهد شد!»'),
(2418, 2418, 'فَأُلْقِيَ السَّحَرَةُ سُجَّدًا قَالُوا آمَنَّا بِرَبِّ هَارُونَ وَمُوسَىٰ', 20, 70, 16, 63, 316, '(موسی عصای خود را افکند، و آنچه را که آنها ساخته بودند بلعید.) ساحران همگی به سجده افتادند و گفتند: «ما به پروردگار هارون و موسی ایمان آوردیم!»'),
(2419, 2419, 'قَالَ آمَنْتُمْ لَهُ قَبْلَ أَنْ آذَنَ لَكُمْ ۖ إِنَّهُ لَكَبِيرُكُمُ الَّذِي عَلَّمَكُمُ السِّحْرَ ۖ فَلَأُقَطِّعَنَّ أَيْدِيَكُمْ وَأَرْجُلَكُمْ مِنْ خِلَافٍ وَلَأُصَلِّبَنَّكُمْ فِي جُذُوعِ النَّخْلِ وَلَتَعْلَمُنَّ أَيُّنَا أَشَدُّ عَذَابًا وَأَبْقَىٰ', 20, 71, 16, 63, 316, '(فرعون) گفت: «آیا پیش از آنکه به شما اذن دهم به او ایمان آوردید؟! مسلّماً او بزرگ شماست که به شما سحر آموخته است! به یقین دستها و پاهایتان را بطور مخالف قطع می‌کنم؛ و شما را از تنه‌های نخل به دار می‌آویزم؛ و خواهید دانست مجازات کدام یک از ما دردناکتر و پایدارتر است!»'),
(2420, 2420, 'قَالُوا لَنْ نُؤْثِرَكَ عَلَىٰ مَا جَاءَنَا مِنَ الْبَيِّنَاتِ وَالَّذِي فَطَرَنَا ۖ فَاقْضِ مَا أَنْتَ قَاضٍ ۖ إِنَّمَا تَقْضِي هَٰذِهِ الْحَيَاةَ الدُّنْيَا', 20, 72, 16, 63, 316, 'گفتند: «سوگند به آن کسی که ما را آفریده، هرگز تو را بر دلایل روشنی که برای ما آمده، مقدّم نخواهیم داشت! هر حکمی می‌خواهی بکن؛ تو تنها در این زندگی دنیا می‌توانی حکم کنی!'),
(2421, 2421, 'إِنَّا آمَنَّا بِرَبِّنَا لِيَغْفِرَ لَنَا خَطَايَانَا وَمَا أَكْرَهْتَنَا عَلَيْهِ مِنَ السِّحْرِ ۗ وَاللَّهُ خَيْرٌ وَأَبْقَىٰ', 20, 73, 16, 63, 316, 'ما به پروردگارمان ایمان آوردیم تا گناهانمان و آنچه را از سحر بر ما تحمیل کردی ببخشاید؛ و خدا بهتر و پایدارتر است!»'),
(2422, 2422, 'إِنَّهُ مَنْ يَأْتِ رَبَّهُ مُجْرِمًا فَإِنَّ لَهُ جَهَنَّمَ لَا يَمُوتُ فِيهَا وَلَا يَحْيَىٰ', 20, 74, 16, 63, 316, 'هر کس در محضر پروردگارش خطاکار حاضر شود، آتش دوزخ برای اوست؛ در آن جا، نه می‌میرد و نه زندگی می‌کند!'),
(2423, 2423, 'وَمَنْ يَأْتِهِ مُؤْمِنًا قَدْ عَمِلَ الصَّالِحَاتِ فَأُولَٰئِكَ لَهُمُ الدَّرَجَاتُ الْعُلَىٰ', 20, 75, 16, 63, 316, 'و هر کس با ایمان نزد او آید، و اعمال صالح انجام داده باشد، چنین کسانی درجات عالی دارند...'),
(2424, 2424, 'جَنَّاتُ عَدْنٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهَارُ خَالِدِينَ فِيهَا ۚ وَذَٰلِكَ جَزَاءُ مَنْ تَزَكَّىٰ', 20, 76, 16, 63, 316, 'باغهای جاویدان بهشت، که نهرها از زیر درختانش جاری است، در حالی که همیشه در آن خواهند بود؛ این است پاداش کسی که خود را پاک نماید!'),
(2425, 2425, 'وَلَقَدْ أَوْحَيْنَا إِلَىٰ مُوسَىٰ أَنْ أَسْرِ بِعِبَادِي فَاضْرِبْ لَهُمْ طَرِيقًا فِي الْبَحْرِ يَبَسًا لَا تَخَافُ دَرَكًا وَلَا تَخْشَىٰ', 20, 77, 16, 63, 317, 'ما به موسی وحی فرستادیم که: «شبانه بندگانم را (از مصر) با خود ببر؛ و برای آنها راهی خشک در دریا بگشا؛ که نه از تعقیب (فرعونیان) خواهی ترسید، و نه از غرق شدن در دریا!»'),
(2426, 2426, 'فَأَتْبَعَهُمْ فِرْعَوْنُ بِجُنُودِهِ فَغَشِيَهُمْ مِنَ الْيَمِّ مَا غَشِيَهُمْ', 20, 78, 16, 63, 317, '(به این ترتیب) فرعون با لشکریانش آنها را دنبال کردند؛ و دریا آنان را (در میان امواج خروشان خود) بطور کامل پوشانید!'),
(2427, 2427, 'وَأَضَلَّ فِرْعَوْنُ قَوْمَهُ وَمَا هَدَىٰ', 20, 79, 16, 63, 317, 'فرعون قوم خود را گمراه ساخت؛ و هرگز هدایت نکرد!'),
(2428, 2428, 'يَا بَنِي إِسْرَائِيلَ قَدْ أَنْجَيْنَاكُمْ مِنْ عَدُوِّكُمْ وَوَاعَدْنَاكُمْ جَانِبَ الطُّورِ الْأَيْمَنَ وَنَزَّلْنَا عَلَيْكُمُ الْمَنَّ وَالسَّلْوَىٰ', 20, 80, 16, 63, 317, 'ای بنی اسرائیل! ما شما را از چنگال دشمنتان نجات دادیم؛ و در طرف راست کوه طور، با شما وعده گذاردیم؛ و «منّ» و «سلوی» بر شما نازل کردیم!'),
(2429, 2429, 'كُلُوا مِنْ طَيِّبَاتِ مَا رَزَقْنَاكُمْ وَلَا تَطْغَوْا فِيهِ فَيَحِلَّ عَلَيْكُمْ غَضَبِي ۖ وَمَنْ يَحْلِلْ عَلَيْهِ غَضَبِي فَقَدْ هَوَىٰ', 20, 81, 16, 63, 317, 'بخورید از روزیهای پاکیزه‌ای که به شما داده‌ایم؛ و در آن طغیان نکنید، که غضب من بر شما وارد شود و هر کس غضبم بر او وارد شود، سقوب می‌کند!'),
(2430, 2430, 'وَإِنِّي لَغَفَّارٌ لِمَنْ تَابَ وَآمَنَ وَعَمِلَ صَالِحًا ثُمَّ اهْتَدَىٰ', 20, 82, 16, 63, 317, 'و من هر که را توبه کند، و ایمان آورد، و عمل صالح انجام دهد، سپس هدایت شود، می‌آمرزم!'),
(2431, 2431, ' وَمَا أَعْجَلَكَ عَنْ قَوْمِكَ يَا مُوسَىٰ', 20, 83, 16, 64, 317, 'ای موسی! چه چیز سبب شد که از قومت پیشی گیری، و (برای آمدن به کوه طور) عجله کنی؟!'),
(2432, 2432, 'قَالَ هُمْ أُولَاءِ عَلَىٰ أَثَرِي وَعَجِلْتُ إِلَيْكَ رَبِّ لِتَرْضَىٰ', 20, 84, 16, 64, 317, 'عرض کرد: «پروردگارا! آنان در پی منند؛ و من به سوی تو شتاب کردم، تا از من خشنود شوی!»'),
(2433, 2433, 'قَالَ فَإِنَّا قَدْ فَتَنَّا قَوْمَكَ مِنْ بَعْدِكَ وَأَضَلَّهُمُ السَّامِرِيُّ', 20, 85, 16, 64, 317, 'فرمود: «ما قوم تو را بعد از تو، آزمودیم و سامری آنها را گمراه ساخت!»'),
(2434, 2434, 'فَرَجَعَ مُوسَىٰ إِلَىٰ قَوْمِهِ غَضْبَانَ أَسِفًا ۚ قَالَ يَا قَوْمِ أَلَمْ يَعِدْكُمْ رَبُّكُمْ وَعْدًا حَسَنًا ۚ أَفَطَالَ عَلَيْكُمُ الْعَهْدُ أَمْ أَرَدْتُمْ أَنْ يَحِلَّ عَلَيْكُمْ غَضَبٌ مِنْ رَبِّكُمْ فَأَخْلَفْتُمْ مَوْعِدِي', 20, 86, 16, 64, 317, 'موسی خشمگین و اندوهناک به سوی قوم خود بازگشت و گفت: «ای قوم من! مگر پروردگارتان وعده نیکویی به شما نداد؟! آیا مدّت جدایی من از شما به طول انجامید، یا می‌خواستید غضب پروردگارتان بر شما نازل شود که با وعده من مخالفت کردید؟!»'),
(2435, 2435, 'قَالُوا مَا أَخْلَفْنَا مَوْعِدَكَ بِمَلْكِنَا وَلَٰكِنَّا حُمِّلْنَا أَوْزَارًا مِنْ زِينَةِ الْقَوْمِ فَقَذَفْنَاهَا فَكَذَٰلِكَ أَلْقَى السَّامِرِيُّ', 20, 87, 16, 64, 317, 'گفتند: «ما به میل و اراده خود از وعده تو تخلّف نکردیم؛ بلکه مقداری از زیورهای قوم را که با خود داشتیم افکندیم!» و سامری اینچنین القا کرد...'),
(2436, 2436, 'فَأَخْرَجَ لَهُمْ عِجْلًا جَسَدًا لَهُ خُوَارٌ فَقَالُوا هَٰذَا إِلَٰهُكُمْ وَإِلَٰهُ مُوسَىٰ فَنَسِيَ', 20, 88, 16, 64, 318, 'و برای آنان مجسّمه گوساله‌ای که صدایی همچون صدای گوساله (واقعی) داشت پدید آورد؛ و (به یکدیگر) گفتند: «این خدای شما، و خدای موسی است!» و او فراموش کرد (پیمانی را که با خدا بسته بود)!'),
(2437, 2437, 'أَفَلَا يَرَوْنَ أَلَّا يَرْجِعُ إِلَيْهِمْ قَوْلًا وَلَا يَمْلِكُ لَهُمْ ضَرًّا وَلَا نَفْعًا', 20, 89, 16, 64, 318, 'آیا نمی‌بینند که (این گوساله) هیچ پاسخی به آنان نمی‌دهد، و مالک هیچ گونه سود و زیانی برای آنها نیست؟!'),
(2438, 2438, 'وَلَقَدْ قَالَ لَهُمْ هَارُونُ مِنْ قَبْلُ يَا قَوْمِ إِنَّمَا فُتِنْتُمْ بِهِ ۖ وَإِنَّ رَبَّكُمُ الرَّحْمَٰنُ فَاتَّبِعُونِي وَأَطِيعُوا أَمْرِي', 20, 90, 16, 64, 318, 'و پیش از آن، هارون به آنها گفته بود: «ای قوم من! شما به این وسیله مورد آزمایش قرار گرفته‌اید! پروردگار شما خداوند رحمان است! پس، از من پیروی کنید، و فرمانم را اطاعت نمایید!»'),
(2439, 2439, 'قَالُوا لَنْ نَبْرَحَ عَلَيْهِ عَاكِفِينَ حَتَّىٰ يَرْجِعَ إِلَيْنَا مُوسَىٰ', 20, 91, 16, 64, 318, 'ولی آنها گفتند: «ما همچنان گرد آن می‌گردیم (و به پرستش گوساله ادامه می‌دهیم) تا موسی به سوی ما بازگردد!»'),
(2440, 2440, 'قَالَ يَا هَارُونُ مَا مَنَعَكَ إِذْ رَأَيْتَهُمْ ضَلُّوا', 20, 92, 16, 64, 318, '(موسی) گفت: «ای هارون! چرا هنگامی که دیدی آنها گمراه شدند...'),
(2441, 2441, 'أَلَّا تَتَّبِعَنِ ۖ أَفَعَصَيْتَ أَمْرِي', 20, 93, 16, 64, 318, 'از من پیروی نکردی؟! آیا فرمان مرا عصیان نمودی؟!»'),
(2442, 2442, 'قَالَ يَا ابْنَ أُمَّ لَا تَأْخُذْ بِلِحْيَتِي وَلَا بِرَأْسِي ۖ إِنِّي خَشِيتُ أَنْ تَقُولَ فَرَّقْتَ بَيْنَ بَنِي إِسْرَائِيلَ وَلَمْ تَرْقُبْ قَوْلِي', 20, 94, 16, 64, 318, '(هارون) گفت: «ای فرزند مادرم! = ای برادر! ریش و سر مرا مگیر! من ترسیدم بگویی تو میان بنی اسرائیل تفرقه انداختی، و سفارش مرا به کار نبستی!»'),
(2443, 2443, 'قَالَ فَمَا خَطْبُكَ يَا سَامِرِيُّ', 20, 95, 16, 64, 318, '(موسی رو به سامری کرد و) گفت: «تو چرا این کار را کردی، ای سامری؟!»'),
(2444, 2444, 'قَالَ بَصُرْتُ بِمَا لَمْ يَبْصُرُوا بِهِ فَقَبَضْتُ قَبْضَةً مِنْ أَثَرِ الرَّسُولِ فَنَبَذْتُهَا وَكَذَٰلِكَ سَوَّلَتْ لِي نَفْسِي', 20, 96, 16, 64, 318, 'گفت: «من چیزی دیدم که آنها ندیدند؛ من قسمتی از آثار رسول (و فرستاده خدا) را گرفتم، سپس آن را افکندم، و اینچنین (هوای) نفس من این کار را در نظرم جلوه داد!»'),
(2445, 2445, 'قَالَ فَاذْهَبْ فَإِنَّ لَكَ فِي الْحَيَاةِ أَنْ تَقُولَ لَا مِسَاسَ ۖ وَإِنَّ لَكَ مَوْعِدًا لَنْ تُخْلَفَهُ ۖ وَانْظُرْ إِلَىٰ إِلَٰهِكَ الَّذِي ظَلْتَ عَلَيْهِ عَاكِفًا ۖ لَنُحَرِّقَنَّهُ ثُمَّ لَنَنْسِفَنَّهُ فِي الْيَمِّ نَسْفًا', 20, 97, 16, 64, 318, '(موسی) گفت: «برو، که بهره تو در زندگی دنیا این است که (هر کس با تو نزدیک شود) بگوئی «با من تماس نگیر!» و تو میعادی (از عذاب خدا) داری، که هرگز تخلّف نخواهد شد! (اکنون) بنگر به این معبودت که پیوسته آن را پرستش می‌کردی! و ببین ما آن را نخست می‌سوزانیم؛ سپس ذرّات آن را به دریا می‌پاشیم!'),
(2446, 2446, 'إِنَّمَا إِلَٰهُكُمُ اللَّهُ الَّذِي لَا إِلَٰهَ إِلَّا هُوَ ۚ وَسِعَ كُلَّ شَيْءٍ عِلْمًا', 20, 98, 16, 64, 318, 'معبود شما تنها خداوندی است که جز او معبودی نیست؛ و علم او همه چیز را فرا گرفته است!»'),
(2447, 2447, 'كَذَٰلِكَ نَقُصُّ عَلَيْكَ مِنْ أَنْبَاءِ مَا قَدْ سَبَقَ ۚ وَقَدْ آتَيْنَاكَ مِنْ لَدُنَّا ذِكْرًا', 20, 99, 16, 64, 319, 'این گونه بخشی از اخبار پیشین را برای تو بازگو می‌کنیم؛ و ما از نزد خود، ذکر (و قرآنی) به تو دادیم!'),
(2448, 2448, 'مَنْ أَعْرَضَ عَنْهُ فَإِنَّهُ يَحْمِلُ يَوْمَ الْقِيَامَةِ وِزْرًا', 20, 100, 16, 64, 319, 'هر کس از آن روی گردان شود، روز قیامت بار سنگینی (از گناه و مسؤولیّت) بر دوش خواهد داشت!'),
(2449, 2449, 'خَالِدِينَ فِيهِ ۖ وَسَاءَ لَهُمْ يَوْمَ الْقِيَامَةِ حِمْلًا', 20, 101, 16, 64, 319, 'در حالی که جاودانه در آن خواهند ماند؛ و بد باری است برای آنها در روز قیامت!'),
(2450, 2450, 'يَوْمَ يُنْفَخُ فِي الصُّورِ ۚ وَنَحْشُرُ الْمُجْرِمِينَ يَوْمَئِذٍ زُرْقًا', 20, 102, 16, 64, 319, 'همان روزی که در «صور» دمیده می‌شود؛ و مجرمان را با بدنهای کبود، در آن روز جمع می‌کنیم!'),
(2451, 2451, 'يَتَخَافَتُونَ بَيْنَهُمْ إِنْ لَبِثْتُمْ إِلَّا عَشْرًا', 20, 103, 16, 64, 319, 'آنها آهسته با هم گفتگو می‌کنند؛ (بعضی می‌گویند:) شما فقط ده (شبانه روز در عالم برزخ) توقّف کردید! (و نمی‌دانند چقدر طولانی بوده است!)'),
(2452, 2452, 'نَحْنُ أَعْلَمُ بِمَا يَقُولُونَ إِذْ يَقُولُ أَمْثَلُهُمْ طَرِيقَةً إِنْ لَبِثْتُمْ إِلَّا يَوْمًا', 20, 104, 16, 64, 319, 'ما به آنچه آنها می‌گویند آگاهتریم، هنگامی که نیکوروش‌ترین آنها می‌گوید: «شما تنها یک روز درنگ کردید!»'),
(2453, 2453, 'وَيَسْأَلُونَكَ عَنِ الْجِبَالِ فَقُلْ يَنْسِفُهَا رَبِّي نَسْفًا', 20, 105, 16, 64, 319, 'و از تو درباره کوه‌ها سؤال می‌کنند؛ بگو: «پروردگارم آنها را (متلاشی کرده) برباد می‌دهد!'),
(2454, 2454, 'فَيَذَرُهَا قَاعًا صَفْصَفًا', 20, 106, 16, 64, 319, 'سپس زمین را صاف و هموار و بی‌آب و گیاه رها می‌سازد...'),
(2455, 2455, 'لَا تَرَىٰ فِيهَا عِوَجًا وَلَا أَمْتًا', 20, 107, 16, 64, 319, 'به گونه‌ای که در آن، هیچ پستی و بلندی نمی‌بینی!»'),
(2456, 2456, 'يَوْمَئِذٍ يَتَّبِعُونَ الدَّاعِيَ لَا عِوَجَ لَهُ ۖ وَخَشَعَتِ الْأَصْوَاتُ لِلرَّحْمَٰنِ فَلَا تَسْمَعُ إِلَّا هَمْسًا', 20, 108, 16, 64, 319, 'در آن روز، همه از دعوت کننده الهی پیروی نموده، و قدرت بر مخالفت او نخواهند داشت (و همگی از قبرها برمی‌خیزند)؛ و همه صداها در برابر (عظمت) خداوند رحمان، خاضع می‌شود؛ و جز صدای آهسته چیزی نمی‌شنوی!'),
(2457, 2457, 'يَوْمَئِذٍ لَا تَنْفَعُ الشَّفَاعَةُ إِلَّا مَنْ أَذِنَ لَهُ الرَّحْمَٰنُ وَرَضِيَ لَهُ قَوْلًا', 20, 109, 16, 64, 319, 'در آن روز، شفاعت هیچ کس سودی نمی‌بخشد، جز کسی که خداوند رحمان به او اجازه داده، و به گفتار او راضی است.'),
(2458, 2458, 'يَعْلَمُ مَا بَيْنَ أَيْدِيهِمْ وَمَا خَلْفَهُمْ وَلَا يُحِيطُونَ بِهِ عِلْمًا', 20, 110, 16, 64, 319, 'آنچه را پیش رو دارند، و آنچه را (در دنیا) پشت سرگذاشته‌اند می‌داند؛ ولی آنها به (علم) او احاطه ندارند!'),
(2459, 2459, ' وَعَنَتِ الْوُجُوهُ لِلْحَيِّ الْقَيُّومِ ۖ وَقَدْ خَابَ مَنْ حَمَلَ ظُلْمًا', 20, 111, 16, 64, 319, 'و (در آن روز) همه چهره‌ها در برابر خداوند حیّ قیوم، خاضع می‌شود؛ و مأیوس (و زیانکار) است آن که بار ستمی بر دوش دارد!'),
(2460, 2460, 'وَمَنْ يَعْمَلْ مِنَ الصَّالِحَاتِ وَهُوَ مُؤْمِنٌ فَلَا يَخَافُ ظُلْمًا وَلَا هَضْمًا', 20, 112, 16, 64, 319, '(امّا) آن کس که کارهای شایسته انجام دهد، در حالی که مؤمن باشد، نه از ظلمی می‌ترسد، و نه از نقصان حقش.'),
(2461, 2461, 'وَكَذَٰلِكَ أَنْزَلْنَاهُ قُرْآنًا عَرَبِيًّا وَصَرَّفْنَا فِيهِ مِنَ الْوَعِيدِ لَعَلَّهُمْ يَتَّقُونَ أَوْ يُحْدِثُ لَهُمْ ذِكْرًا', 20, 113, 16, 64, 319, 'و این گونه آن را قرآنی عربی = فصیح و گویا نازل کردیم، و انواع وعیدها (و انذارها) را در آن بازگو نمودیم، شاید تقوا پیشه کنند؛ یا برای آنان تذکّری پدید آورد!'),
(2462, 2462, 'فَتَعَالَى اللَّهُ الْمَلِكُ الْحَقُّ ۗ وَلَا تَعْجَلْ بِالْقُرْآنِ مِنْ قَبْلِ أَنْ يُقْضَىٰ إِلَيْكَ وَحْيُهُ ۖ وَقُلْ رَبِّ زِدْنِي عِلْمًا', 20, 114, 16, 64, 320, 'پس بلندمرتبه است خداوندی که سلطان حقّ است! پس نسبت به (تلاوت) قرآن عجله مکن، پیش از آنکه وحی آن بر تو تمام شود؛ و بگو: «پروردگارا! علم مرا افزون کن!»'),
(2463, 2463, 'وَلَقَدْ عَهِدْنَا إِلَىٰ آدَمَ مِنْ قَبْلُ فَنَسِيَ وَلَمْ نَجِدْ لَهُ عَزْمًا', 20, 115, 16, 64, 320, 'پیش از این، از آدم پیمان گرفته بودیم؛ امّا او فراموش کرد؛ و عزم استواری برای او نیافتیم!'),
(2464, 2464, 'وَإِذْ قُلْنَا لِلْمَلَائِكَةِ اسْجُدُوا لِآدَمَ فَسَجَدُوا إِلَّا إِبْلِيسَ أَبَىٰ', 20, 116, 16, 64, 320, 'و به یاد آور هنگامی را که به فرشتگان گفتیم: «برای آدم سجده کنید!» همگی سجده کردند؛ جز ابلیس که سرباز زد (و سجده نکرد)!'),
(2465, 2465, 'فَقُلْنَا يَا آدَمُ إِنَّ هَٰذَا عَدُوٌّ لَكَ وَلِزَوْجِكَ فَلَا يُخْرِجَنَّكُمَا مِنَ الْجَنَّةِ فَتَشْقَىٰ', 20, 117, 16, 64, 320, 'پس گفتیم: «ای آدم! این (ابلیس) دشمن تو و (دشمن) همسر توست! مبادا شما را از بهشت بیرون کند؛ که به زحمت و رنج خواهی افتاد!'),
(2466, 2466, 'إِنَّ لَكَ أَلَّا تَجُوعَ فِيهَا وَلَا تَعْرَىٰ', 20, 118, 16, 64, 320, '(امّا تو در بهشت راحت هستی! و مزیّتش) برای تو این است که در آن گرسنه و برهنه نخواهی شد؛'),
(2467, 2467, 'وَأَنَّكَ لَا تَظْمَأُ فِيهَا وَلَا تَضْحَىٰ', 20, 119, 16, 64, 320, 'و در آن تشنه نمی‌شوی، و حرارت آفتاب آزارت نمی‌دهد!»'),
(2468, 2468, 'فَوَسْوَسَ إِلَيْهِ الشَّيْطَانُ قَالَ يَا آدَمُ هَلْ أَدُلُّكَ عَلَىٰ شَجَرَةِ الْخُلْدِ وَمُلْكٍ لَا يَبْلَىٰ', 20, 120, 16, 64, 320, 'ولی شیطان او را وسوسه کرد و گفت: «ای آدم! آیا می‌خواهی تو را به درخت زندگی جاوید، و ملکی بی‌زوال راهنمایی کنم؟!»'),
(2469, 2469, 'فَأَكَلَا مِنْهَا فَبَدَتْ لَهُمَا سَوْآتُهُمَا وَطَفِقَا يَخْصِفَانِ عَلَيْهِمَا مِنْ وَرَقِ الْجَنَّةِ ۚ وَعَصَىٰ آدَمُ رَبَّهُ فَغَوَىٰ', 20, 121, 16, 64, 320, 'سرانجام هر دو از آن خوردند، (و لباس بهشتیشان فرو ریخت،) و عورتشان آشکار گشت و برای پوشاندن خود، از برگهای (درختان) بهشتی جامه دوختند! (آری) آدم پروردگارش را نافرمانی کرد، و از پاداش او محروم شد!'),
(2470, 2470, 'ثُمَّ اجْتَبَاهُ رَبُّهُ فَتَابَ عَلَيْهِ وَهَدَىٰ', 20, 122, 16, 64, 320, 'سپس پروردگارش او را برگزید، و توبه‌اش را پذیرفت، و هدایتش نمود.'),
(2471, 2471, 'قَالَ اهْبِطَا مِنْهَا جَمِيعًا ۖ بَعْضُكُمْ لِبَعْضٍ عَدُوٌّ ۖ فَإِمَّا يَأْتِيَنَّكُمْ مِنِّي هُدًى فَمَنِ اتَّبَعَ هُدَايَ فَلَا يَضِلُّ وَلَا يَشْقَىٰ', 20, 123, 16, 64, 320, '(خداوند) فرمود: «هر دو از آن (بهشت) فرود آیید، در حالی که دشمن یکدیگر خواهید بود! ولی هرگاه هدایت من به سراغ شما آید، هر کس از هدایت من پیروی کند، نه گمراه می‌شود، و نه در رنج خواهد بود!'),
(2472, 2472, 'وَمَنْ أَعْرَضَ عَنْ ذِكْرِي فَإِنَّ لَهُ مَعِيشَةً ضَنْكًا وَنَحْشُرُهُ يَوْمَ الْقِيَامَةِ أَعْمَىٰ', 20, 124, 16, 64, 320, 'و هر کس از یاد من روی گردان شود، زندگی (سخت و) تنگی خواهد داشت؛ و روز قیامت، او را نابینا محشور می‌کنیم!»'),
(2473, 2473, 'قَالَ رَبِّ لِمَ حَشَرْتَنِي أَعْمَىٰ وَقَدْ كُنْتُ بَصِيرًا', 20, 125, 16, 64, 320, 'می‌گوید: «پروردگارا! چرا نابینا محشورم کردی؟! من که بینا بودم!»'),
(2474, 2474, 'قَالَ كَذَٰلِكَ أَتَتْكَ آيَاتُنَا فَنَسِيتَهَا ۖ وَكَذَٰلِكَ الْيَوْمَ تُنْسَىٰ', 20, 126, 16, 64, 321, 'می‌فرماید: «آن گونه که آیات من برای تو آمد، و تو آنها را فراموش کردی؛ امروز نیز تو فراموش خواهی شد!»'),
(2475, 2475, 'وَكَذَٰلِكَ نَجْزِي مَنْ أَسْرَفَ وَلَمْ يُؤْمِنْ بِآيَاتِ رَبِّهِ ۚ وَلَعَذَابُ الْآخِرَةِ أَشَدُّ وَأَبْقَىٰ', 20, 127, 16, 64, 321, 'و این گونه جزا می‌دهیم کسی را که اسراف کند، و به آیات پروردگارش ایمان نیاورد! و عذاب آخرت، شدیدتر و پایدارتر است!'),
(2476, 2476, 'أَفَلَمْ يَهْدِ لَهُمْ كَمْ أَهْلَكْنَا قَبْلَهُمْ مِنَ الْقُرُونِ يَمْشُونَ فِي مَسَاكِنِهِمْ ۗ إِنَّ فِي ذَٰلِكَ لَآيَاتٍ لِأُولِي النُّهَىٰ', 20, 128, 16, 64, 321, 'آیا برای هدایت آنان کافی نیست که بسیاری از نسلهای پیشین را (که طغیان و فساد کردند) هلاک نمودیم، و اینها در مسکنهای (ویران شده) آنان راه می‌روند! مسلّماً در این امر، نشانه‌های روشنی برای خردمندان است.'),
(2477, 2477, 'وَلَوْلَا كَلِمَةٌ سَبَقَتْ مِنْ رَبِّكَ لَكَانَ لِزَامًا وَأَجَلٌ مُسَمًّى', 20, 129, 16, 64, 321, 'و اگر سنّت و تقدیر پروردگارت و ملاحظه زمان مقرّر نبود، عذاب الهی بزودی دامان آنان را می‌گرفت!'),
(2478, 2478, 'فَاصْبِرْ عَلَىٰ مَا يَقُولُونَ وَسَبِّحْ بِحَمْدِ رَبِّكَ قَبْلَ طُلُوعِ الشَّمْسِ وَقَبْلَ غُرُوبِهَا ۖ وَمِنْ آنَاءِ اللَّيْلِ فَسَبِّحْ وَأَطْرَافَ النَّهَارِ لَعَلَّكَ تَرْضَىٰ', 20, 130, 16, 64, 321, 'پس در برابر آنچه می‌گویند، صبر کن! و پیش از طلوع آفتاب، و قبل از غروب آن؛ تسبیح و حمد پروردگارت را بجا آور؛ و همچنین (برخی) از ساعات شب و اطراف روز (پروردگارت را) تسبیح گوی؛ باشد که (از الطاف الهی) خشنود شوی!'),
(2479, 2479, 'وَلَا تَمُدَّنَّ عَيْنَيْكَ إِلَىٰ مَا مَتَّعْنَا بِهِ أَزْوَاجًا مِنْهُمْ زَهْرَةَ الْحَيَاةِ الدُّنْيَا لِنَفْتِنَهُمْ فِيهِ ۚ وَرِزْقُ رَبِّكَ خَيْرٌ وَأَبْقَىٰ', 20, 131, 16, 64, 321, 'و هرگز چشمان خود را به نعمتهای مادّی، که به گروه‌هایی از آنان داده‌ایم، میفکن! اینها شکوفه‌های زندگی دنیاست؛ تا آنان را در آن بیازماییم؛ و روزی پروردگارت بهتر و پایدارتر است!'),
(2480, 2480, 'وَأْمُرْ أَهْلَكَ بِالصَّلَاةِ وَاصْطَبِرْ عَلَيْهَا ۖ لَا نَسْأَلُكَ رِزْقًا ۖ نَحْنُ نَرْزُقُكَ ۗ وَالْعَاقِبَةُ لِلتَّقْوَىٰ', 20, 132, 16, 64, 321, 'خانواده خود را به نماز فرمان ده؛ و بر انجام آن شکیبا باش! از تو روزی نمی‌خواهیم؛ (بلکه) ما به تو روزی می‌دهیم؛ و عاقبت نیک برای تقواست!'),
(2481, 2481, 'وَقَالُوا لَوْلَا يَأْتِينَا بِآيَةٍ مِنْ رَبِّهِ ۚ أَوَلَمْ تَأْتِهِمْ بَيِّنَةُ مَا فِي الصُّحُفِ الْأُولَىٰ', 20, 133, 16, 64, 321, 'گفتند: «چرا (پیامبر) معجزه و نشانه‌ای از سوی پروردگارش برای ما نمی‌آورد؟! (بگو:) آیا خبرهای روشنی که در کتابهای (آسمانی) نخستین بوده، برای آنها نیامد؟!'),
(2482, 2482, 'وَلَوْ أَنَّا أَهْلَكْنَاهُمْ بِعَذَابٍ مِنْ قَبْلِهِ لَقَالُوا رَبَّنَا لَوْلَا أَرْسَلْتَ إِلَيْنَا رَسُولًا فَنَتَّبِعَ آيَاتِكَ مِنْ قَبْلِ أَنْ نَذِلَّ وَنَخْزَىٰ', 20, 134, 16, 64, 321, 'اگر ما آنان را پیش از آن (که قرآن نازل شود) با عذابی هلاک می‌کردیم، (در قیامت) می‌گفتند: «پروردگارا! چرا پیامبری برای ما نفرستادی تا از آیات تو پیروی کنیم، پیش از آنکه ذلیل و رسوا شویم!»'),
(2483, 2483, 'قُلْ كُلٌّ مُتَرَبِّصٌ فَتَرَبَّصُوا ۖ فَسَتَعْلَمُونَ مَنْ أَصْحَابُ الصِّرَاطِ السَّوِيِّ وَمَنِ اهْتَدَىٰ', 20, 135, 16, 64, 321, 'بگو: «همه (ما و شما) در انتظاریم! (ما در انتظار وعده پیروزی، و شما در انتظار شکست ما!) حال که چنین است، انتظار بکشید! امّا بزودی می‌دانید چه کسی از اصحاب صراط مستقیم، و چه کسی هدایت یافته است!'),
(2484, 2484, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ اقْتَرَبَ لِلنَّاسِ حِسَابُهُمْ وَهُمْ فِي غَفْلَةٍ مُعْرِضُونَ', 21, 1, 17, 65, 322, 'حساب مردم به آنان نزدیک شده، در حالی که در غفلتند و روی گردانند!'),
(2485, 2485, 'مَا يَأْتِيهِمْ مِنْ ذِكْرٍ مِنْ رَبِّهِمْ مُحْدَثٍ إِلَّا اسْتَمَعُوهُ وَهُمْ يَلْعَبُونَ', 21, 2, 17, 65, 322, 'هیچ یادآوری تازه‌ای از طرف پروردگارشان برای آنها نمی‌آید، مگر آنکه با بازی (و شوخی) به آن گوش می‌دهند!'),
(2486, 2486, 'لَاهِيَةً قُلُوبُهُمْ ۗ وَأَسَرُّوا النَّجْوَى الَّذِينَ ظَلَمُوا هَلْ هَٰذَا إِلَّا بَشَرٌ مِثْلُكُمْ ۖ أَفَتَأْتُونَ السِّحْرَ وَأَنْتُمْ تُبْصِرُونَ', 21, 3, 17, 65, 322, 'این در حالی است که دلهایشان در لهو و بی‌خبری فرو رفته است! و ستمگران پنهانی نجوا کردند (و گفتند): «آیا جز این است که او بشری همانند شماست؟! آیا به سراغ سحر می‌روید، با اینکه (چشم دارید و) می‌بینید؟!'),
(2487, 2487, 'قَالَ رَبِّي يَعْلَمُ الْقَوْلَ فِي السَّمَاءِ وَالْأَرْضِ ۖ وَهُوَ السَّمِيعُ الْعَلِيمُ', 21, 4, 17, 65, 322, '(پیامبر) گفت: «پروردگارم همه سخنان را، چه در آسمان باشد و چه در زمین، می‌داند؛ و او شنوا و داناست!»'),
(2488, 2488, 'بَلْ قَالُوا أَضْغَاثُ أَحْلَامٍ بَلِ افْتَرَاهُ بَلْ هُوَ شَاعِرٌ فَلْيَأْتِنَا بِآيَةٍ كَمَا أُرْسِلَ الْأَوَّلُونَ', 21, 5, 17, 65, 322, 'آنها گفتند: «(آنچه محمّد (ص) آورده وحی نیست؛) بلکه خوابهایی آشفته است! اصلاً آن را بدروغ به خدا بسته؛ نه، بلکه او یک شاعر است! (اگر راست می‌گوید) باید معجزه‌ای برای ما بیاورد؛ همان گونه که پیامبران پیشین (با معجزات) فرستاده شدند!»'),
(2489, 2489, 'مَا آمَنَتْ قَبْلَهُمْ مِنْ قَرْيَةٍ أَهْلَكْنَاهَا ۖ أَفَهُمْ يُؤْمِنُونَ', 21, 6, 17, 65, 322, 'تمام آبادیهایی که پیش از اینها هلاک کردیم (تقاضای معجزات گوناگون کردند، و خواسته آنان عملی شد، ولی) هرگز ایمان نیاوردند؛ آیا اینها ایمان می‌آورند؟!'),
(2490, 2490, 'وَمَا أَرْسَلْنَا قَبْلَكَ إِلَّا رِجَالًا نُوحِي إِلَيْهِمْ ۖ فَاسْأَلُوا أَهْلَ الذِّكْرِ إِنْ كُنْتُمْ لَا تَعْلَمُونَ', 21, 7, 17, 65, 322, 'ما پیش از تو، جز مردانی که به آنان وحی می‌کردیم، نفرستادیم! (همه انسان بودند، و از جنس بشر!) اگر نمی‌دانید، از آگاهان بپرسید.'),
(2491, 2491, 'وَمَا جَعَلْنَاهُمْ جَسَدًا لَا يَأْكُلُونَ الطَّعَامَ وَمَا كَانُوا خَالِدِينَ', 21, 8, 17, 65, 322, 'آنان را پیکرهایی که غذا نخورند قرار ندادیم! عمر جاویدان هم نداشتند!'),
(2492, 2492, 'ثُمَّ صَدَقْنَاهُمُ الْوَعْدَ فَأَنْجَيْنَاهُمْ وَمَنْ نَشَاءُ وَأَهْلَكْنَا الْمُسْرِفِينَ', 21, 9, 17, 65, 322, 'سپس وعده‌ای را که به آنان داده بودیم، وفا کردیم! آنها و هر کس را که می‌خواستیم (از چنگ دشمنانشان) نجات دادیم؛ و مسرفان را هلاک نمودیم!'),
(2493, 2493, 'لَقَدْ أَنْزَلْنَا إِلَيْكُمْ كِتَابًا فِيهِ ذِكْرُكُمْ ۖ أَفَلَا تَعْقِلُونَ', 21, 10, 17, 65, 322, 'ما بر شما کتابی نازل کردیم که وسیله تذکّر (و بیداری) شما در آن است! آیا نمی‌فهمید؟!'),
(2494, 2494, 'وَكَمْ قَصَمْنَا مِنْ قَرْيَةٍ كَانَتْ ظَالِمَةً وَأَنْشَأْنَا بَعْدَهَا قَوْمًا آخَرِينَ', 21, 11, 17, 65, 323, 'چه بسیار آبادیهای ستمگری را در هم شکستیم؛ و بعد از آنها، قوم دیگری روی کار آوردیم!'),
(2495, 2495, 'فَلَمَّا أَحَسُّوا بَأْسَنَا إِذَا هُمْ مِنْهَا يَرْكُضُونَ', 21, 12, 17, 65, 323, 'هنگامی که عذاب ما را احساس کردند، ناگهان پا به فرار گذاشتند!'),
(2496, 2496, 'لَا تَرْكُضُوا وَارْجِعُوا إِلَىٰ مَا أُتْرِفْتُمْ فِيهِ وَمَسَاكِنِكُمْ لَعَلَّكُمْ تُسْأَلُونَ', 21, 13, 17, 65, 323, '(گفتیم:) فرار نکنید؛ و به زندگی پر ناز و نعمت، و به مسکنهای پر زرق و برقتان بازگردید! شاید (سائلان بیایند و) از شما تقاضا کنند (شما هم آنان را محروم بازگردانید)!'),
(2497, 2497, 'قَالُوا يَا وَيْلَنَا إِنَّا كُنَّا ظَالِمِينَ', 21, 14, 17, 65, 323, 'گفتند: «ای وای بر ما! به یقین ما ستمگر بودیم!»'),
(2498, 2498, 'فَمَا زَالَتْ تِلْكَ دَعْوَاهُمْ حَتَّىٰ جَعَلْنَاهُمْ حَصِيدًا خَامِدِينَ', 21, 15, 17, 65, 323, 'و همچنان این سخن را تکرار می‌کردند، تا آنها را درو کرده و خاموش ساختیم!'),
(2499, 2499, 'وَمَا خَلَقْنَا السَّمَاءَ وَالْأَرْضَ وَمَا بَيْنَهُمَا لَاعِبِينَ', 21, 16, 17, 65, 323, 'ما آسمان و زمین، و آنچه را در میان آنهاست از روی بازی نیافریدیم!'),
(2500, 2500, 'لَوْ أَرَدْنَا أَنْ نَتَّخِذَ لَهْوًا لَاتَّخَذْنَاهُ مِنْ لَدُنَّا إِنْ كُنَّا فَاعِلِينَ', 21, 17, 17, 65, 323, '(بفرض محال) اگر می‌خواستیم سرگرمی انتخاب کنیم، چیزی متناسب خود انتخاب می‌کردیم!'),
(2501, 2501, 'بَلْ نَقْذِفُ بِالْحَقِّ عَلَى الْبَاطِلِ فَيَدْمَغُهُ فَإِذَا هُوَ زَاهِقٌ ۚ وَلَكُمُ الْوَيْلُ مِمَّا تَصِفُونَ', 21, 18, 17, 65, 323, 'بلکه ما حق را بر سر باطل می‌کوبیم تا آن را هلاک سازد؛ و این گونه، باطل محو و نابود می‌شود! امّا وای بر شما از توصیفی که (درباره خدا و هدف آفرینش) می‌کنید!'),
(2502, 2502, 'وَلَهُ مَنْ فِي السَّمَاوَاتِ وَالْأَرْضِ ۚ وَمَنْ عِنْدَهُ لَا يَسْتَكْبِرُونَ عَنْ عِبَادَتِهِ وَلَا يَسْتَحْسِرُونَ', 21, 19, 17, 65, 323, 'از آن اوست آنان که در آسمانها و زمینند! و آنها که نزد اویند = فرشتگان‌ هیچ گاه از عبادتش استکبار نمی‌ورزند، و هرگز خسته نمی‌شوند.'),
(2503, 2503, 'يُسَبِّحُونَ اللَّيْلَ وَالنَّهَارَ لَا يَفْتُرُونَ', 21, 20, 17, 65, 323, '(تمام) شب و روز را تسبیح می‌گویند؛ و سست نمی‌گردند.'),
(2504, 2504, 'أَمِ اتَّخَذُوا آلِهَةً مِنَ الْأَرْضِ هُمْ يُنْشِرُونَ', 21, 21, 17, 65, 323, 'آیا آنها خدایانی از زمین برگزیدند که (خلق می‌کنند و) منتشر می‌سازند؟!'),
(2505, 2505, 'لَوْ كَانَ فِيهِمَا آلِهَةٌ إِلَّا اللَّهُ لَفَسَدَتَا ۚ فَسُبْحَانَ اللَّهِ رَبِّ الْعَرْشِ عَمَّا يَصِفُونَ', 21, 22, 17, 65, 323, 'اگر در آسمان و زمین، جز «اللّه» خدایان دیگری بود، فاسد می‌شدند (و نظام جهان به هم می‌خورد)! منزه است خداوند پروردگار عرش، از توصیفی که آنها می‌کنند!'),
(2506, 2506, 'لَا يُسْأَلُ عَمَّا يَفْعَلُ وَهُمْ يُسْأَلُونَ', 21, 23, 17, 65, 323, 'هیچ کس نمی‌تواند بر کار او خرده بگیرد؛ ولی در کارهای آنها، جای سؤال و ایراد است!'),
(2507, 2507, 'أَمِ اتَّخَذُوا مِنْ دُونِهِ آلِهَةً ۖ قُلْ هَاتُوا بُرْهَانَكُمْ ۖ هَٰذَا ذِكْرُ مَنْ مَعِيَ وَذِكْرُ مَنْ قَبْلِي ۗ بَلْ أَكْثَرُهُمْ لَا يَعْلَمُونَ الْحَقَّ ۖ فَهُمْ مُعْرِضُونَ', 21, 24, 17, 65, 323, 'آیا آنها معبودانی جز خدا برگزیدند؟! بگو: «دلیلتان را بیاورید! این سخن کسانی است که با من هستند، و سخن کسانی = پیامبرانی‌ است که پیش از من بودند!» امّا بیشتر آنها حق را نمی‌دانند؛ و به همین دلیل (از آن) روی گردانند.'),
(2508, 2508, 'وَمَا أَرْسَلْنَا مِنْ قَبْلِكَ مِنْ رَسُولٍ إِلَّا نُوحِي إِلَيْهِ أَنَّهُ لَا إِلَٰهَ إِلَّا أَنَا فَاعْبُدُونِ', 21, 25, 17, 65, 324, 'ما پیش از تو هیچ پیامبری را نفرستادیم مگر اینکه به او وحی کردیم که: «معبودی جز من نیست؛ پس تنها مرا پرستش کنید.»'),
(2509, 2509, 'وَقَالُوا اتَّخَذَ الرَّحْمَٰنُ وَلَدًا ۗ سُبْحَانَهُ ۚ بَلْ عِبَادٌ مُكْرَمُونَ', 21, 26, 17, 65, 324, 'آنها گفتند: «خداوند رحمان فرزندی برای خود انتخاب کرده است»! او منزه است (از این عیب و نقص)؛ آنها = فرشتگان‌ بندگان شایسته اویند.'),
(2510, 2510, 'لَا يَسْبِقُونَهُ بِالْقَوْلِ وَهُمْ بِأَمْرِهِ يَعْمَلُونَ', 21, 27, 17, 65, 324, 'هرگز در سخن بر او پیشی نمی‌گیرند؛ و (پیوسته) به فرمان او عمل می‌کنند.'),
(2511, 2511, 'يَعْلَمُ مَا بَيْنَ أَيْدِيهِمْ وَمَا خَلْفَهُمْ وَلَا يَشْفَعُونَ إِلَّا لِمَنِ ارْتَضَىٰ وَهُمْ مِنْ خَشْيَتِهِ مُشْفِقُونَ', 21, 28, 17, 65, 324, 'او اعمال امروز و آینده و اعمال گذشته آنها را می‌داند؛ و آنها جز برای کسی که خدا راضی (به شفاعت برای او) است شفاعت نمی‌کنند؛ و از ترس او بیمناکند.'),
(2512, 2512, ' وَمَنْ يَقُلْ مِنْهُمْ إِنِّي إِلَٰهٌ مِنْ دُونِهِ فَذَٰلِكَ نَجْزِيهِ جَهَنَّمَ ۚ كَذَٰلِكَ نَجْزِي الظَّالِمِينَ', 21, 29, 17, 65, 324, 'و هر کس از آنها بگوید: «من جز خدا، معبودی دیگرم»، کیفر او را جهنم می‌دهیم! و ستمگران را این گونه کیفر خواهیم داد.'),
(2513, 2513, 'أَوَلَمْ يَرَ الَّذِينَ كَفَرُوا أَنَّ السَّمَاوَاتِ وَالْأَرْضَ كَانَتَا رَتْقًا فَفَتَقْنَاهُمَا ۖ وَجَعَلْنَا مِنَ الْمَاءِ كُلَّ شَيْءٍ حَيٍّ ۖ أَفَلَا يُؤْمِنُونَ', 21, 30, 17, 65, 324, 'آیا کافران ندیدند که آسمانها و زمین به هم پیوسته بودند، و ما آنها را از یکدیگر باز کردیم؛ و هر چیز زنده‌ای را از آب قرار دادیم؟! آیا ایمان نمی‌آورند؟!'),
(2514, 2514, 'وَجَعَلْنَا فِي الْأَرْضِ رَوَاسِيَ أَنْ تَمِيدَ بِهِمْ وَجَعَلْنَا فِيهَا فِجَاجًا سُبُلًا لَعَلَّهُمْ يَهْتَدُونَ', 21, 31, 17, 65, 324, 'و در زمین، کوه‌های ثابت و پابرجایی قرار دادیم، مبادا آنها را بلرزاند! و در آن، درّه‌ها و راه‌هایی قرار دادیم تا هدایت شوند!'),
(2515, 2515, 'وَجَعَلْنَا السَّمَاءَ سَقْفًا مَحْفُوظًا ۖ وَهُمْ عَنْ آيَاتِهَا مُعْرِضُونَ', 21, 32, 17, 65, 324, 'و آسمان را سقف محفوظی قرار دادیم؛ ولی آنها از آیات آن روی‌گردانند.'),
(2516, 2516, 'وَهُوَ الَّذِي خَلَقَ اللَّيْلَ وَالنَّهَارَ وَالشَّمْسَ وَالْقَمَرَ ۖ كُلٌّ فِي فَلَكٍ يَسْبَحُونَ', 21, 33, 17, 65, 324, 'او کسی است که شب و روز و خورشید و ماه را آفرید؛ هر یک در مداری در حرکتند!'),
(2517, 2517, 'وَمَا جَعَلْنَا لِبَشَرٍ مِنْ قَبْلِكَ الْخُلْدَ ۖ أَفَإِنْ مِتَّ فَهُمُ الْخَالِدُونَ', 21, 34, 17, 65, 324, 'پیش از تو (نیز) برای هیچ انسانی جاودانگی قرار ندادیم؛ (وانگهی آنها که انتظار مرگ تو را می‌کشند،) آیا اگر تو بمیری، آنان جاوید خواهند بود؟!'),
(2518, 2518, 'كُلُّ نَفْسٍ ذَائِقَةُ الْمَوْتِ ۗ وَنَبْلُوكُمْ بِالشَّرِّ وَالْخَيْرِ فِتْنَةً ۖ وَإِلَيْنَا تُرْجَعُونَ', 21, 35, 17, 65, 324, 'هر انسانی طعم مرگ را می‌چشد! و شما را با بدیها و خوبیها آزمایش می‌کنیم؛ و سرانجام بسوی ما بازگردانده می‌شوید!'),
(2519, 2519, 'وَإِذَا رَآكَ الَّذِينَ كَفَرُوا إِنْ يَتَّخِذُونَكَ إِلَّا هُزُوًا أَهَٰذَا الَّذِي يَذْكُرُ آلِهَتَكُمْ وَهُمْ بِذِكْرِ الرَّحْمَٰنِ هُمْ كَافِرُونَ', 21, 36, 17, 65, 325, 'هنگامی که کافران تو را می‌بینند، کاری جز استهزا کردن تو ندارند؛ (و می‌گویند:) آیا این همان کسی است که سخن از خدایان شما می‌گوید؟! در حالی که خودشان ذکر خداوند رحمان را انکار می‌کنند.'),
(2520, 2520, 'خُلِقَ الْإِنْسَانُ مِنْ عَجَلٍ ۚ سَأُرِيكُمْ آيَاتِي فَلَا تَسْتَعْجِلُونِ', 21, 37, 17, 65, 325, '(آری،) انسان از عجله آفریده شده؛ ولی عجله نکنید؛ بزودی آیاتم را به شما نشان خواهم داد!'),
(2521, 2521, 'وَيَقُولُونَ مَتَىٰ هَٰذَا الْوَعْدُ إِنْ كُنْتُمْ صَادِقِينَ', 21, 38, 17, 65, 325, 'آنها می‌گویند: «اگر راست میگویید، این وعده (قیامت) کی فرا می‌رسد؟!»'),
(2522, 2522, 'لَوْ يَعْلَمُ الَّذِينَ كَفَرُوا حِينَ لَا يَكُفُّونَ عَنْ وُجُوهِهِمُ النَّارَ وَلَا عَنْ ظُهُورِهِمْ وَلَا هُمْ يُنْصَرُونَ', 21, 39, 17, 65, 325, 'ولی اگر کافران می‌دانستند زمانی که (فرا می‌رسد) نمی‌توانند شعله‌های آتش را از صورت و از پشتهای خود دور کنند، و هیچ کس آنان را یاری نمی‌کند (این قدر درباره قیامت شتاب نمی‌کردند)!'),
(2523, 2523, 'بَلْ تَأْتِيهِمْ بَغْتَةً فَتَبْهَتُهُمْ فَلَا يَسْتَطِيعُونَ رَدَّهَا وَلَا هُمْ يُنْظَرُونَ', 21, 40, 17, 65, 325, '(آری، این مجازات الهی) بطور ناگهانی به سراغشان می‌آید و مبهوتشان می‌کند؛ آنچنان که توانایی دفع آن را ندارند، و به آنها مهلت داده نمی‌شود!'),
(2524, 2524, 'وَلَقَدِ اسْتُهْزِئَ بِرُسُلٍ مِنْ قَبْلِكَ فَحَاقَ بِالَّذِينَ سَخِرُوا مِنْهُمْ مَا كَانُوا بِهِ يَسْتَهْزِئُونَ', 21, 41, 17, 65, 325, '(اگر تو را استهزا کنند نگران نباش،) پیامبران پیش از تو را (نیز) استهزا کردند؛ امّا سرانجام، آنچه را استهزا می‌کردند دامان مسخره‌کنندگان را گرفت (و مجازات الهی آنها را در هم کوبید)!'),
(2525, 2525, 'قُلْ مَنْ يَكْلَؤُكُمْ بِاللَّيْلِ وَالنَّهَارِ مِنَ الرَّحْمَٰنِ ۗ بَلْ هُمْ عَنْ ذِكْرِ رَبِّهِمْ مُعْرِضُونَ', 21, 42, 17, 65, 325, 'بگو: «چه کسی شما را در شب و روز از (مجازات) خداوند بخشنده نگاه می‌دارد؟!» ولی آنان از یاد پروردگارشان روی‌گردانند!'),
(2526, 2526, 'أَمْ لَهُمْ آلِهَةٌ تَمْنَعُهُمْ مِنْ دُونِنَا ۚ لَا يَسْتَطِيعُونَ نَصْرَ أَنْفُسِهِمْ وَلَا هُمْ مِنَّا يُصْحَبُونَ', 21, 43, 17, 65, 325, 'آیا آنها خدایانی دارند که می‌توانند در برابر ما از آنان دفاع کنند؟! (این خدایان ساختگی، حتّی) نمی‌توانند خودشان را یاری دهند (تا چه رسد به دیگران)؛ و نه از ناحیه ما با نیرویی یاری می‌شوند!'),
(2527, 2527, 'بَلْ مَتَّعْنَا هَٰؤُلَاءِ وَآبَاءَهُمْ حَتَّىٰ طَالَ عَلَيْهِمُ الْعُمُرُ ۗ أَفَلَا يَرَوْنَ أَنَّا نَأْتِي الْأَرْضَ نَنْقُصُهَا مِنْ أَطْرَافِهَا ۚ أَفَهُمُ الْغَالِبُونَ', 21, 44, 17, 65, 325, 'ما آنها و پدرانشان را (از نعمتها) بهره‌مند ساختیم، تا آنجا که عمر طولانی پیدا کردند (و مایه غرور و طغیانشان شد)؛ آیا نمی‌بینند که ما پیوسته به سراغ زمین آمده، و از آن (و اهلش) می‌کاهیم؟! آیا آنها غالبند (یا ما)؟!'),
(2528, 2528, 'قُلْ إِنَّمَا أُنْذِرُكُمْ بِالْوَحْيِ ۚ وَلَا يَسْمَعُ الصُّمُّ الدُّعَاءَ إِذَا مَا يُنْذَرُونَ', 21, 45, 17, 65, 326, 'بگو: «من تنها بوسیله وحی شما را انذار می‌کنم!» ولی آنها که گوشهایشان کر است، هنگامی که انذار می‌شوند، سخنان را نمی‌شنوند!'),
(2529, 2529, 'وَلَئِنْ مَسَّتْهُمْ نَفْحَةٌ مِنْ عَذَابِ رَبِّكَ لَيَقُولُنَّ يَا وَيْلَنَا إِنَّا كُنَّا ظَالِمِينَ', 21, 46, 17, 65, 326, 'اگر کمترین عذاب پروردگارت به آنان برسد، فریادشان بلند می‌شود که: «ای وای بر ما! ما همگی ستمگر بودیم!»'),
(2530, 2530, 'وَنَضَعُ الْمَوَازِينَ الْقِسْطَ لِيَوْمِ الْقِيَامَةِ فَلَا تُظْلَمُ نَفْسٌ شَيْئًا ۖ وَإِنْ كَانَ مِثْقَالَ حَبَّةٍ مِنْ خَرْدَلٍ أَتَيْنَا بِهَا ۗ وَكَفَىٰ بِنَا حَاسِبِينَ', 21, 47, 17, 65, 326, 'ما ترازوهای عدل را در روز قیامت برپا می‌کنیم؛ پس به هیچ کس کمترین ستمی نمی‌شود؛ و اگر بمقدار سنگینی یک دانه خردل (کار نیک و بدی) باشد، ما آن را حاضر می‌کنیم؛ و کافی است که ما حساب‌کننده باشیم!'),
(2531, 2531, 'وَلَقَدْ آتَيْنَا مُوسَىٰ وَهَارُونَ الْفُرْقَانَ وَضِيَاءً وَذِكْرًا لِلْمُتَّقِينَ', 21, 48, 17, 65, 326, 'ما به موسی و هارون، «فرقان» = وسیله جدا کردن حق از باطل‌ و نور، و آنچه مایه یادآوری برای پرهیزگاران است، دادیم.'),
(2532, 2532, 'الَّذِينَ يَخْشَوْنَ رَبَّهُمْ بِالْغَيْبِ وَهُمْ مِنَ السَّاعَةِ مُشْفِقُونَ', 21, 49, 17, 65, 326, 'همانان که از پروردگارشان در نهان می‌ترسند، و از قیامت بیم دارند!'),
(2533, 2533, 'وَهَٰذَا ذِكْرٌ مُبَارَكٌ أَنْزَلْنَاهُ ۚ أَفَأَنْتُمْ لَهُ مُنْكِرُونَ', 21, 50, 17, 65, 326, 'و این (قرآن) ذکر مبارکی است که (بر شما) نازل کردیم؛ آیا شما آن را انکار می‌کنید؟!'),
(2534, 2534, ' وَلَقَدْ آتَيْنَا إِبْرَاهِيمَ رُشْدَهُ مِنْ قَبْلُ وَكُنَّا بِهِ عَالِمِينَ', 21, 51, 17, 66, 326, 'ما وسیله رشد ابراهیم را از قبل به او دادیم؛ و از (شایستگی) او آگاه بودیم...'),
(2535, 2535, 'إِذْ قَالَ لِأَبِيهِ وَقَوْمِهِ مَا هَٰذِهِ التَّمَاثِيلُ الَّتِي أَنْتُمْ لَهَا عَاكِفُونَ', 21, 52, 17, 66, 326, 'آن هنگام که به پدرش (آزر) و قوم او گفت: «این مجسمه‌های بی‌روح چیست که شما همواره آنها را پرستش می‌کنید؟!»'),
(2536, 2536, 'قَالُوا وَجَدْنَا آبَاءَنَا لَهَا عَابِدِينَ', 21, 53, 17, 66, 326, 'گفتند: «ما پدران خود را دیدیم که آنها را عبادت می‌کنند.»'),
(2537, 2537, 'قَالَ لَقَدْ كُنْتُمْ أَنْتُمْ وَآبَاؤُكُمْ فِي ضَلَالٍ مُبِينٍ', 21, 54, 17, 66, 326, 'گفت: «مسلّماً هم شما و هم پدرانتان، در گمراهی آشکاری بوده‌اید!»'),
(2538, 2538, 'قَالُوا أَجِئْتَنَا بِالْحَقِّ أَمْ أَنْتَ مِنَ اللَّاعِبِينَ', 21, 55, 17, 66, 326, 'گفتند: «آیا مطلب حقّی برای ما آورده‌ای، یا شوخی می‌کنی؟!»'),
(2539, 2539, 'قَالَ بَلْ رَبُّكُمْ رَبُّ السَّمَاوَاتِ وَالْأَرْضِ الَّذِي فَطَرَهُنَّ وَأَنَا عَلَىٰ ذَٰلِكُمْ مِنَ الشَّاهِدِينَ', 21, 56, 17, 66, 326, 'گفت: «(کاملاً حقّ آورده‌ام) پروردگار شما همان پروردگار آسمانها و زمین است که آنها را ایجاد کرده؛ و من بر این امر، از گواهانم!'),
(2540, 2540, 'وَتَاللَّهِ لَأَكِيدَنَّ أَصْنَامَكُمْ بَعْدَ أَنْ تُوَلُّوا مُدْبِرِينَ', 21, 57, 17, 66, 326, 'و به خدا سوگند، در غیاب شما، نقشه‌ای برای نابودی بتهایتان می‌کشم!»'),
(2541, 2541, 'فَجَعَلَهُمْ جُذَاذًا إِلَّا كَبِيرًا لَهُمْ لَعَلَّهُمْ إِلَيْهِ يَرْجِعُونَ', 21, 58, 17, 66, 327, 'سرانجام (با استفاده از یک فرصت مناسب)، همه آنها -جز بت بزرگشان- را قطعه قطعه کرد؛ شاید سراغ او بیایند (و او حقایق را بازگو کند)!'),
(2542, 2542, 'قَالُوا مَنْ فَعَلَ هَٰذَا بِآلِهَتِنَا إِنَّهُ لَمِنَ الظَّالِمِينَ', 21, 59, 17, 66, 327, '(هنگامی که منظره بتها را دیدند،) گفتند: «هر کس با خدایان ما چنین کرده، قطعاً از ستمگران است (و باید کیفر سخت ببیند)!»'),
(2543, 2543, 'قَالُوا سَمِعْنَا فَتًى يَذْكُرُهُمْ يُقَالُ لَهُ إِبْرَاهِيمُ', 21, 60, 17, 66, 327, '(گروهی) گفتند: «شنیدیم نوجوانی از (مخالفت با) بتها سخن می‌گفت که او را ابراهیم می‌گویند.»'),
(2544, 2544, 'قَالُوا فَأْتُوا بِهِ عَلَىٰ أَعْيُنِ النَّاسِ لَعَلَّهُمْ يَشْهَدُونَ', 21, 61, 17, 66, 327, '(جمعیّت) گفتند: «او را در برابر دیدگان مردم بیاورید، تا گواهی دهند!»'),
(2545, 2545, 'قَالُوا أَأَنْتَ فَعَلْتَ هَٰذَا بِآلِهَتِنَا يَا إِبْرَاهِيمُ', 21, 62, 17, 66, 327, '(هنگامی که ابراهیم را حاضر کردند،) گفتند: «تو این کار را با خدایان ما کرده‌ای، ای ابراهیم؟!»'),
(2546, 2546, 'قَالَ بَلْ فَعَلَهُ كَبِيرُهُمْ هَٰذَا فَاسْأَلُوهُمْ إِنْ كَانُوا يَنْطِقُونَ', 21, 63, 17, 66, 327, 'گفت: «بلکه این کار را بزرگشان کرده است! از آنها بپرسید اگر سخن می‌گویند!»'),
(2547, 2547, 'فَرَجَعُوا إِلَىٰ أَنْفُسِهِمْ فَقَالُوا إِنَّكُمْ أَنْتُمُ الظَّالِمُونَ', 21, 64, 17, 66, 327, 'آنها به وجدان خویش بازگشتند؛ و (به خود) گفتند: «حقّا که شما ستمگرید!»'),
(2548, 2548, 'ثُمَّ نُكِسُوا عَلَىٰ رُءُوسِهِمْ لَقَدْ عَلِمْتَ مَا هَٰؤُلَاءِ يَنْطِقُونَ', 21, 65, 17, 66, 327, 'سپس بر سرهایشان واژگونه شدند؛ (و حکم وجدان را بکلّی فراموش کردند و گفتند:) تو می‌دانی که اینها سخن نمی‌گویند!'),
(2549, 2549, 'قَالَ أَفَتَعْبُدُونَ مِنْ دُونِ اللَّهِ مَا لَا يَنْفَعُكُمْ شَيْئًا وَلَا يَضُرُّكُمْ', 21, 66, 17, 66, 327, '(ابراهیم) گفت: «آیا جز خدا چیزی را می‌پرستید که نه کمترین سودی برای شما دارد، و نه زیانی به شما می‌رساند! (نه امیدی به سودشان دارید، و نه ترسی از زیانشان!)'),
(2550, 2550, 'أُفٍّ لَكُمْ وَلِمَا تَعْبُدُونَ مِنْ دُونِ اللَّهِ ۖ أَفَلَا تَعْقِلُونَ', 21, 67, 17, 66, 327, 'اف بر شما و بر آنچه جز خدا می‌پرستید! آیا اندیشه نمی‌کنید (و عقل ندارید)؟!'),
(2551, 2551, 'قَالُوا حَرِّقُوهُ وَانْصُرُوا آلِهَتَكُمْ إِنْ كُنْتُمْ فَاعِلِينَ', 21, 68, 17, 66, 327, 'گفتند: «او را بسوزانید و خدایان خود را یاری کنید، اگر کاری از شما ساخته است!»'),
(2552, 2552, 'قُلْنَا يَا نَارُ كُونِي بَرْدًا وَسَلَامًا عَلَىٰ إِبْرَاهِيمَ', 21, 69, 17, 66, 327, '(سرانجام او را به آتش افکندند؛ ولی ما) گفتیم: «ای آتش! بر ابراهیم سرد و سالم باش!»'),
(2553, 2553, 'وَأَرَادُوا بِهِ كَيْدًا فَجَعَلْنَاهُمُ الْأَخْسَرِينَ', 21, 70, 17, 66, 327, 'آنها می‌خواستند ابراهیم را با این نقشه نابود کنند؛ ولی ما آنها را زیانکارترین مردم قرار دادیم!'),
(2554, 2554, 'وَنَجَّيْنَاهُ وَلُوطًا إِلَى الْأَرْضِ الَّتِي بَارَكْنَا فِيهَا لِلْعَالَمِينَ', 21, 71, 17, 66, 327, 'و او و لوط را به سرزمین (شام) -که آن را برای همه جهانیان پربرکت ساختیم- نجات دادیم!'),
(2555, 2555, 'وَوَهَبْنَا لَهُ إِسْحَاقَ وَيَعْقُوبَ نَافِلَةً ۖ وَكُلًّا جَعَلْنَا صَالِحِينَ', 21, 72, 17, 66, 327, 'و اسحاق، و علاوه بر او، یعقوب را به وی بخشیدیم؛ و همه آنان را مردانی صالح قرار دادیم!'),
(2556, 2556, 'وَجَعَلْنَاهُمْ أَئِمَّةً يَهْدُونَ بِأَمْرِنَا وَأَوْحَيْنَا إِلَيْهِمْ فِعْلَ الْخَيْرَاتِ وَإِقَامَ الصَّلَاةِ وَإِيتَاءَ الزَّكَاةِ ۖ وَكَانُوا لَنَا عَابِدِينَ', 21, 73, 17, 66, 328, 'و آنان را پیشوایانی قرار دادیم که به فرمان ما، (مردم را) هدایت می‌کردند؛ و انجام کارهای نیک و برپاداشتن نماز و ادای زکات را به آنها وحی کردیم؛ و تنها ما را عبادت می‌کردند.'),
(2557, 2557, 'وَلُوطًا آتَيْنَاهُ حُكْمًا وَعِلْمًا وَنَجَّيْنَاهُ مِنَ الْقَرْيَةِ الَّتِي كَانَتْ تَعْمَلُ الْخَبَائِثَ ۗ إِنَّهُمْ كَانُوا قَوْمَ سَوْءٍ فَاسِقِينَ', 21, 74, 17, 66, 328, 'و لوط را (به یاد آور) که به او حکومت و علم دادیم؛ و از شهری که اعمال زشت و کثیف انجام می‌دادند، رهایی بخشیدیم؛ چرا که آنها مردم بد و فاسقی بودند!'),
(2558, 2558, 'وَأَدْخَلْنَاهُ فِي رَحْمَتِنَا ۖ إِنَّهُ مِنَ الصَّالِحِينَ', 21, 75, 17, 66, 328, 'و او را در رحمت خود داخل کردیم؛ و او از صالحان بود.'),
(2559, 2559, 'وَنُوحًا إِذْ نَادَىٰ مِنْ قَبْلُ فَاسْتَجَبْنَا لَهُ فَنَجَّيْنَاهُ وَأَهْلَهُ مِنَ الْكَرْبِ الْعَظِيمِ', 21, 76, 17, 66, 328, 'و نوح را (به یاد آور) هنگامی که پیش از آن (زمان، پروردگار خود را) خواند! ما دعای او را مستجاب کردیم؛ و او و خاندانش را از اندوه بزرگ نجات دادیم؛'),
(2560, 2560, 'وَنَصَرْنَاهُ مِنَ الْقَوْمِ الَّذِينَ كَذَّبُوا بِآيَاتِنَا ۚ إِنَّهُمْ كَانُوا قَوْمَ سَوْءٍ فَأَغْرَقْنَاهُمْ أَجْمَعِينَ', 21, 77, 17, 66, 328, 'و او را در برابر جمعیّتی که آیات ما را تکذیب کرده بودند یاری دادیم؛ چرا که قوم بدی بودند؛ از این رو همه آنها را غرق کردیم!'),
(2561, 2561, 'وَدَاوُودَ وَسُلَيْمَانَ إِذْ يَحْكُمَانِ فِي الْحَرْثِ إِذْ نَفَشَتْ فِيهِ غَنَمُ الْقَوْمِ وَكُنَّا لِحُكْمِهِمْ شَاهِدِينَ', 21, 78, 17, 66, 328, 'و داوود و سلیمان را (به خاطر بیاور) هنگامی که درباره کشتزاری که گوسفندان بی شبان قوم، شبانگاه در آن چریده (و آن را تباه کرده) بودند، داوری می‌کردند؛ و ما بر حکم آنان شاهد بودیم.'),
(2562, 2562, 'فَفَهَّمْنَاهَا سُلَيْمَانَ ۚ وَكُلًّا آتَيْنَا حُكْمًا وَعِلْمًا ۚ وَسَخَّرْنَا مَعَ دَاوُودَ الْجِبَالَ يُسَبِّحْنَ وَالطَّيْرَ ۚ وَكُنَّا فَاعِلِينَ', 21, 79, 17, 66, 328, 'ما (حکم واقعی) آن را به سلیمان فهماندیم؛ و به هر یک از آنان (شایستگی) داوری، و علم فراوانی دادیم؛ و کوه‌ها و پرندگان را با داوود مسخّر ساختیم، که (همراه او) تسبیح (خدا) می‌گفتند؛ و ما این کار را انجام دادیم!'),
(2563, 2563, 'وَعَلَّمْنَاهُ صَنْعَةَ لَبُوسٍ لَكُمْ لِتُحْصِنَكُمْ مِنْ بَأْسِكُمْ ۖ فَهَلْ أَنْتُمْ شَاكِرُونَ', 21, 80, 17, 66, 328, 'و ساختن زره را بخاطر شما به او تعلیم دادیم، تا شما را در جنگهایتان حفظ کند؛ آیا شکرگزار (این نعمتهای خدا) هستید؟'),
(2564, 2564, 'وَلِسُلَيْمَانَ الرِّيحَ عَاصِفَةً تَجْرِي بِأَمْرِهِ إِلَى الْأَرْضِ الَّتِي بَارَكْنَا فِيهَا ۚ وَكُنَّا بِكُلِّ شَيْءٍ عَالِمِينَ', 21, 81, 17, 66, 328, 'و تندباد را مسخّر سلیمان ساختیم، که بفرمان او بسوی سرزمینی که آن را پربرکت کرده بودیم جریان می‌یافت؛ و ما از همه چیز آگاه بوده‌ایم.'),
(2565, 2565, 'وَمِنَ الشَّيَاطِينِ مَنْ يَغُوصُونَ لَهُ وَيَعْمَلُونَ عَمَلًا دُونَ ذَٰلِكَ ۖ وَكُنَّا لَهُمْ حَافِظِينَ', 21, 82, 17, 66, 329, 'و گروهی از شیاطین (را نیز مسخّر او قرار دادیم، که در دریا) برایش غوّاصی می‌کردند؛ و کارهایی غیر از این (نیز) برای او انجام می‌دادند؛ و ما آنها را (از سرکشی) حفظ می‌کردیم!'),
(2566, 2566, ' وَأَيُّوبَ إِذْ نَادَىٰ رَبَّهُ أَنِّي مَسَّنِيَ الضُّرُّ وَأَنْتَ أَرْحَمُ الرَّاحِمِينَ', 21, 83, 17, 66, 329, 'و ایّوب را (به یاد آور) هنگامی که پروردگارش را خواند (و عرضه داشت): «بدحالی و مشکلات به من روی آورده؛ و تو مهربانترین مهربانانی!»'),
(2567, 2567, 'فَاسْتَجَبْنَا لَهُ فَكَشَفْنَا مَا بِهِ مِنْ ضُرٍّ ۖ وَآتَيْنَاهُ أَهْلَهُ وَمِثْلَهُمْ مَعَهُمْ رَحْمَةً مِنْ عِنْدِنَا وَذِكْرَىٰ لِلْعَابِدِينَ', 21, 84, 17, 66, 329, 'ما دعای او را مستجاب کردیم؛ و ناراحتیهایی را که داشت برطرف ساختیم؛ و خاندانش را به او بازگرداندیم؛ و همانندشان را بر آنها افزودیم؛ تا رحمتی از سوی ما و تذکّری برای عبادت‌کنندگان باشد.'),
(2568, 2568, 'وَإِسْمَاعِيلَ وَإِدْرِيسَ وَذَا الْكِفْلِ ۖ كُلٌّ مِنَ الصَّابِرِينَ', 21, 85, 17, 66, 329, 'و اسماعیل و ادریس و ذاالکفل را (به یاد آور) که همه از صابران بودند.'),
(2569, 2569, 'وَأَدْخَلْنَاهُمْ فِي رَحْمَتِنَا ۖ إِنَّهُمْ مِنَ الصَّالِحِينَ', 21, 86, 17, 66, 329, 'و ما آنان را در رحمت خود وارد ساختیم؛ چرا که آنها از صالحان بودند.'),
(2570, 2570, 'وَذَا النُّونِ إِذْ ذَهَبَ مُغَاضِبًا فَظَنَّ أَنْ لَنْ نَقْدِرَ عَلَيْهِ فَنَادَىٰ فِي الظُّلُمَاتِ أَنْ لَا إِلَٰهَ إِلَّا أَنْتَ سُبْحَانَكَ إِنِّي كُنْتُ مِنَ الظَّالِمِينَ', 21, 87, 17, 66, 329, 'و ذاالنون = یونس‌ را (به یاد آور) در آن هنگام که خشمگین (از میان قوم خود) رفت؛ و چنین می‌پنداشت که ما بر او تنگ نخواهیم گرفت؛ (امّا موقعی که در کام نهنگ فرو رفت،) در آن ظلمتها (ی متراکم) صدا زد: «(خداوندا!) جز تو معبودی نیست! منزّهی تو! من از ستمکاران بودم!»'),
(2571, 2571, 'فَاسْتَجَبْنَا لَهُ وَنَجَّيْنَاهُ مِنَ الْغَمِّ ۚ وَكَذَٰلِكَ نُنْجِي الْمُؤْمِنِينَ', 21, 88, 17, 66, 329, 'ما دعای او را به اجابت رساندیم؛ و از آن اندوه نجاتش بخشیدیم؛ و این گونه مؤمنان را نجات می‌دهیم!'),
(2572, 2572, 'وَزَكَرِيَّا إِذْ نَادَىٰ رَبَّهُ رَبِّ لَا تَذَرْنِي فَرْدًا وَأَنْتَ خَيْرُ الْوَارِثِينَ', 21, 89, 17, 66, 329, 'و زکریا را (به یاد آور) در آن هنگام که پروردگارش را خواند (و عرض کرد): «پروردگار من! مرا تنها مگذار (و فرزند برومندی به من عطا کن)؛ و تو بهترین وارثانی!»'),
(2573, 2573, 'فَاسْتَجَبْنَا لَهُ وَوَهَبْنَا لَهُ يَحْيَىٰ وَأَصْلَحْنَا لَهُ زَوْجَهُ ۚ إِنَّهُمْ كَانُوا يُسَارِعُونَ فِي الْخَيْرَاتِ وَيَدْعُونَنَا رَغَبًا وَرَهَبًا ۖ وَكَانُوا لَنَا خَاشِعِينَ', 21, 90, 17, 66, 329, 'ما هم دعای او را پذیرفتیم، و یحیی را به او بخشیدیم؛ و همسرش را (که نازا بود) برایش آماده (بارداری) کردیم؛ چرا که آنان (خاندانی بودند که) همواره در کارهای خیر بسرعت اقدام می‌کردند؛ و در حال بیم و امید ما را می‌خواندند؛ و پیوسته برای ما (خاضع و) خاشع بودند.'),
(2574, 2574, 'وَالَّتِي أَحْصَنَتْ فَرْجَهَا فَنَفَخْنَا فِيهَا مِنْ رُوحِنَا وَجَعَلْنَاهَا وَابْنَهَا آيَةً لِلْعَالَمِينَ', 21, 91, 17, 66, 330, 'و به یاد آور زنی را که دامان خود را پاک نگه داشت؛ و ما از روح خود در او دمیدیم؛ و او و فرزندش = مسیح‌ را نشانه بزرگی برای جهانیان قرار دادیم!'),
(2575, 2575, 'إِنَّ هَٰذِهِ أُمَّتُكُمْ أُمَّةً وَاحِدَةً وَأَنَا رَبُّكُمْ فَاعْبُدُونِ', 21, 92, 17, 66, 330, 'این (پیامبران بزرگ و پیروانشان) همه امّت واحدی بودند (و پیرو یک هدف)؛ و من پروردگار شما هستم؛ پس مرا پرستش کنید!'),
(2576, 2576, 'وَتَقَطَّعُوا أَمْرَهُمْ بَيْنَهُمْ ۖ كُلٌّ إِلَيْنَا رَاجِعُونَ', 21, 93, 17, 66, 330, '(گروهی از پیروان ناآگاه آنها) کار خود را به تفرقه در میان خود کشاندند؛ (ولی سرانجام) همگی بسوی ما بازمی‌گردند!'),
(2577, 2577, 'فَمَنْ يَعْمَلْ مِنَ الصَّالِحَاتِ وَهُوَ مُؤْمِنٌ فَلَا كُفْرَانَ لِسَعْيِهِ وَإِنَّا لَهُ كَاتِبُونَ', 21, 94, 17, 66, 330, 'و هر کس چیزی از اعمال شایسته بجا آورد، در حالی که ایمان داشته باشد، کوشش او ناسپاسی نخواهد شد؛ و ما تمام اعمال او را (برای پاداش) می‌نویسیم.'),
(2578, 2578, 'وَحَرَامٌ عَلَىٰ قَرْيَةٍ أَهْلَكْنَاهَا أَنَّهُمْ لَا يَرْجِعُونَ', 21, 95, 17, 66, 330, 'و حرام است بر شهرها و آبادیهایی که (بر اثر گناه) نابودشان کردیم (که به دنیا بازگردند؛) آنها هرگز باز نخواهند گشت!'),
(2579, 2579, 'حَتَّىٰ إِذَا فُتِحَتْ يَأْجُوجُ وَمَأْجُوجُ وَهُمْ مِنْ كُلِّ حَدَبٍ يَنْسِلُونَ', 21, 96, 17, 66, 330, 'تا آن زمان که «یأجوج» و «مأجوج» گشوده شوند؛ و آنها از هر محلّ مرتفعی بسرعت عبور می‌کنند.'),
(2580, 2580, 'وَاقْتَرَبَ الْوَعْدُ الْحَقُّ فَإِذَا هِيَ شَاخِصَةٌ أَبْصَارُ الَّذِينَ كَفَرُوا يَا وَيْلَنَا قَدْ كُنَّا فِي غَفْلَةٍ مِنْ هَٰذَا بَلْ كُنَّا ظَالِمِينَ', 21, 97, 17, 66, 330, 'و وعده حقّ = قیامت‌ نزدیک می‌شود؛ در آن هنگام چشمهای کافران از وحشت از حرکت بازمی‌ماند؛ (می‌گویند:) ای وای بر ما که از این (جریان) در غفلت بودیم؛ بلکه ما ستمکار بودیم!'),
(2581, 2581, 'إِنَّكُمْ وَمَا تَعْبُدُونَ مِنْ دُونِ اللَّهِ حَصَبُ جَهَنَّمَ أَنْتُمْ لَهَا وَارِدُونَ', 21, 98, 17, 66, 330, 'شما و آنچه غیر خدا می‌پرستید، هیزم جهنّم خواهید بود؛ و همگی در آن وارد می‌شوید.'),
(2582, 2582, 'لَوْ كَانَ هَٰؤُلَاءِ آلِهَةً مَا وَرَدُوهَا ۖ وَكُلٌّ فِيهَا خَالِدُونَ', 21, 99, 17, 66, 330, 'اگر اینها خدایانی بودند، هرگز وارد آن نمی‌شدند! در حالی که همگی در آن جاودانه خواهند بود.'),
(2583, 2583, 'لَهُمْ فِيهَا زَفِيرٌ وَهُمْ فِيهَا لَا يَسْمَعُونَ', 21, 100, 17, 66, 330, 'برای آنان در آن = دوزخ‌ ناله‌های دردناکی است و چیزی نمی‌شنوند.'),
(2584, 2584, 'إِنَّ الَّذِينَ سَبَقَتْ لَهُمْ مِنَّا الْحُسْنَىٰ أُولَٰئِكَ عَنْهَا مُبْعَدُونَ', 21, 101, 17, 66, 330, '(امّا) کسانی که از قبل، وعده نیک از سوی ما به آنها داده شده = مؤمنان صالح‌ از آن دور نگاهداشته می‌شوند.'),
(2585, 2585, 'لَا يَسْمَعُونَ حَسِيسَهَا ۖ وَهُمْ فِي مَا اشْتَهَتْ أَنْفُسُهُمْ خَالِدُونَ', 21, 102, 17, 66, 331, 'آنها صدای آتش دوزخ را نمی‌شوند؛ و در آنچه دلشان بخواهد، جاودانه متنعّم هستند.'),
(2586, 2586, 'لَا يَحْزُنُهُمُ الْفَزَعُ الْأَكْبَرُ وَتَتَلَقَّاهُمُ الْمَلَائِكَةُ هَٰذَا يَوْمُكُمُ الَّذِي كُنْتُمْ تُوعَدُونَ', 21, 103, 17, 66, 331, 'وحشت بزرگ، آنها را اندوهگین نمی‌کند؛ و فرشتگان به استقبالشان می‌آیند، (و می‌گویند:) این همان روزی است که به شما وعده داده می‌شد!'),
(2587, 2587, 'يَوْمَ نَطْوِي السَّمَاءَ كَطَيِّ السِّجِلِّ لِلْكُتُبِ ۚ كَمَا بَدَأْنَا أَوَّلَ خَلْقٍ نُعِيدُهُ ۚ وَعْدًا عَلَيْنَا ۚ إِنَّا كُنَّا فَاعِلِينَ', 21, 104, 17, 66, 331, 'در آن روز که آسمان را چون طوماری در هم می‌پیچیم، (سپس) همان گونه که آفرینش را آغاز کردیم، آن را بازمی‌گردانیم؛ این وعده‌ای است بر ما، و قطعاً آن را انجام خواهیم داد.'),
(2588, 2588, 'وَلَقَدْ كَتَبْنَا فِي الزَّبُورِ مِنْ بَعْدِ الذِّكْرِ أَنَّ الْأَرْضَ يَرِثُهَا عِبَادِيَ الصَّالِحُونَ', 21, 105, 17, 66, 331, 'در «زبور» بعد از ذکر (تورات) نوشتیم: «بندگان شایسته‌ام وارث (حکومت) زمین خواهند شد!»'),
(2589, 2589, 'إِنَّ فِي هَٰذَا لَبَلَاغًا لِقَوْمٍ عَابِدِينَ', 21, 106, 17, 66, 331, 'در این، ابلاغ روشنی است برای جمعیّت عبادت‌کنندگان!'),
(2590, 2590, 'وَمَا أَرْسَلْنَاكَ إِلَّا رَحْمَةً لِلْعَالَمِينَ', 21, 107, 17, 66, 331, 'ما تو را جز برای رحمت جهانیان نفرستادیم.'),
(2591, 2591, 'قُلْ إِنَّمَا يُوحَىٰ إِلَيَّ أَنَّمَا إِلَٰهُكُمْ إِلَٰهٌ وَاحِدٌ ۖ فَهَلْ أَنْتُمْ مُسْلِمُونَ', 21, 108, 17, 66, 331, 'بگو: «تنها چیزی که به من وحی می‌شود این است که معبود شما خدای یگانه است؛ آیا (با این حال) تسلیم (حقّ) می‌شوید؟ (و بتها را کنار می‌گذارید؟)»'),
(2592, 2592, 'فَإِنْ تَوَلَّوْا فَقُلْ آذَنْتُكُمْ عَلَىٰ سَوَاءٍ ۖ وَإِنْ أَدْرِي أَقَرِيبٌ أَمْ بَعِيدٌ مَا تُوعَدُونَ', 21, 109, 17, 66, 331, 'اگر باز (روی گردان شوند، بگو: «من به همه شما یکسان اعلام خطر می‌کنم؛ و نمی‌دانم آیا وعده (عذاب خدا) که به شما داده می‌شود نزدیک است یا دور!'),
(2593, 2593, 'إِنَّهُ يَعْلَمُ الْجَهْرَ مِنَ الْقَوْلِ وَيَعْلَمُ مَا تَكْتُمُونَ', 21, 110, 17, 66, 331, 'او سخنان آشکار را می‌داند، و آنچه را کتمان می‌کنید (نیز) می‌داند (و چیزی بر او پوشیده نیست)!'),
(2594, 2594, 'وَإِنْ أَدْرِي لَعَلَّهُ فِتْنَةٌ لَكُمْ وَمَتَاعٌ إِلَىٰ حِينٍ', 21, 111, 17, 66, 331, 'و من نمی‌دانم شاید این آزمایشی برای شماست؛ و مایه بهره‌گیری تا مدّتی (معیّن)!'),
(2595, 2595, 'قَالَ رَبِّ احْكُمْ بِالْحَقِّ ۗ وَرَبُّنَا الرَّحْمَٰنُ الْمُسْتَعَانُ عَلَىٰ مَا تَصِفُونَ', 21, 112, 17, 66, 331, '(و پیامبر) گفت: «پروردگارا! بحق داوری فرما (و این طغیانگران را کیفر ده)! و پروردگار ما (خداوند) رحمان است که در برابر نسبتهای ناروای شما، از او استمداد می‌طلبم!»'),
(2596, 2596, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ يَا أَيُّهَا النَّاسُ اتَّقُوا رَبَّكُمْ ۚ إِنَّ زَلْزَلَةَ السَّاعَةِ شَيْءٌ عَظِيمٌ', 22, 1, 17, 67, 332, 'ای مردم! از (عذاب) پروردگارتان بترسید، که زلزله رستاخیز امر عظیمی است!'),
(2597, 2597, 'يَوْمَ تَرَوْنَهَا تَذْهَلُ كُلُّ مُرْضِعَةٍ عَمَّا أَرْضَعَتْ وَتَضَعُ كُلُّ ذَاتِ حَمْلٍ حَمْلَهَا وَتَرَى النَّاسَ سُكَارَىٰ وَمَا هُمْ بِسُكَارَىٰ وَلَٰكِنَّ عَذَابَ اللَّهِ شَدِيدٌ', 22, 2, 17, 67, 332, 'روزی که آن را می‌بینید، (آنچنان وحشت سراپای همه را فرامی‌گیرد که) هر مادر شیردهی، کودک شیرخوارش را فراموش می‌کند؛ و هر بارداری جنین خود را بر زمین می‌نهد؛ و مردم را مست می‌بینی، در حالی که مست نیستند؛ ولی عذاب خدا شدید است!'),
(2598, 2598, 'وَمِنَ النَّاسِ مَنْ يُجَادِلُ فِي اللَّهِ بِغَيْرِ عِلْمٍ وَيَتَّبِعُ كُلَّ شَيْطَانٍ مَرِيدٍ', 22, 3, 17, 67, 332, 'گروهی از مردم، بدون هیچ علم و دانشی، به مجادله درباره خدا برمی‌خیزند؛ و از هر شیطان سرکشی پیروی می‌کنند.'),
(2599, 2599, 'كُتِبَ عَلَيْهِ أَنَّهُ مَنْ تَوَلَّاهُ فَأَنَّهُ يُضِلُّهُ وَيَهْدِيهِ إِلَىٰ عَذَابِ السَّعِيرِ', 22, 4, 17, 67, 332, 'بر او نوشته شده که هر کس ولایتش را بر گردن نهد، بطور مسلّم گمراهش می‌سازد، و به آتش سوزان راهنماییش می‌کند!');
INSERT INTO `Quran` (`rowid`, `id`, `Arabic`, `Chapter`, `Verse`, `Juz`, `Hizb`, `Page`, `tr`) VALUES
(2600, 2600, 'يَا أَيُّهَا النَّاسُ إِنْ كُنْتُمْ فِي رَيْبٍ مِنَ الْبَعْثِ فَإِنَّا خَلَقْنَاكُمْ مِنْ تُرَابٍ ثُمَّ مِنْ نُطْفَةٍ ثُمَّ مِنْ عَلَقَةٍ ثُمَّ مِنْ مُضْغَةٍ مُخَلَّقَةٍ وَغَيْرِ مُخَلَّقَةٍ لِنُبَيِّنَ لَكُمْ ۚ وَنُقِرُّ فِي الْأَرْحَامِ مَا نَشَاءُ إِلَىٰ أَجَلٍ مُسَمًّى ثُمَّ نُخْرِجُكُمْ طِفْلًا ثُمَّ لِتَبْلُغُوا أَشُدَّكُمْ ۖ وَمِنْكُمْ مَنْ يُتَوَفَّىٰ وَمِنْكُمْ مَنْ يُرَدُّ إِلَىٰ أَرْذَلِ الْعُمُرِ لِكَيْلَا يَعْلَمَ مِنْ بَعْدِ عِلْمٍ شَيْئًا ۚ وَتَرَى الْأَرْضَ هَامِدَةً فَإِذَا أَنْزَلْنَا عَلَيْهَا الْمَاءَ اهْتَزَّتْ وَرَبَتْ وَأَنْبَتَتْ مِنْ كُلِّ زَوْجٍ بَهِيجٍ', 22, 5, 17, 67, 332, 'ای مردم! اگر در رستاخیز شک دارید، (به این نکته توجّه کنید که:) ما شما را از خاک آفریدیم، سپس از نطفه، و بعد از خون بسته شده، سپس از «مضغه» = چیزی شبیه گوشت جویده شده‌، که بعضی دارای شکل و خلقت است و بعضی بدون شکل؛ تا برای شما روشن سازیم (که بر هر چیز قادریم)! و جنین‌هایی را که بخواهیم تا مدّت معیّنی در رحم (مادران) قرارمی‌دهیم؛ (و آنچه را بخواهیم ساقظ می‌کنیم؛) بعد شما را بصورت طفل بیرون می‌آوریم؛ سپس هدف این است که به حدّ رشد و بلوغ خویش برسید. در این میان بعضی از شما می‌میرند؛ و بعضی آن قدر عمر می‌کنند که به بدترین مرحله زندگی (و پیری) می‌رسند؛ آنچنان که بعد از علم و آگاهی، چیزی نمی‌دانند! (از سوی دیگر،) زمین را (در فصل زمستان) خشک و مرده می‌بینی، اما هنگامی که آب باران بر آن فرو می‌فرستیم، به حرکت درمی‌آید و می‌روید؛ و از هر نوع گیاهان زیبا می‌رویاند!'),
(2601, 2601, 'ذَٰلِكَ بِأَنَّ اللَّهَ هُوَ الْحَقُّ وَأَنَّهُ يُحْيِي الْمَوْتَىٰ وَأَنَّهُ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ', 22, 6, 17, 67, 333, 'این بخاطر آن است که (بدانید) خداوند حق است؛ و اوست که مردگان را زنده می‌کند؛ و بر هر چیزی تواناست.'),
(2602, 2602, 'وَأَنَّ السَّاعَةَ آتِيَةٌ لَا رَيْبَ فِيهَا وَأَنَّ اللَّهَ يَبْعَثُ مَنْ فِي الْقُبُورِ', 22, 7, 17, 67, 333, 'و اینکه رستاخیز آمدنی است، و شکّی در آن نیست؛ و خداوند تمام کسانی را که در قبرها هستند زنده می‌کند.'),
(2603, 2603, 'وَمِنَ النَّاسِ مَنْ يُجَادِلُ فِي اللَّهِ بِغَيْرِ عِلْمٍ وَلَا هُدًى وَلَا كِتَابٍ مُنِيرٍ', 22, 8, 17, 67, 333, 'و گروهی از مردم، بدون هیچ دانش و هیچ هدایت و کتاب روشنی بخشی، درباره خدا مجادله می‌کنند!'),
(2604, 2604, 'ثَانِيَ عِطْفِهِ لِيُضِلَّ عَنْ سَبِيلِ اللَّهِ ۖ لَهُ فِي الدُّنْيَا خِزْيٌ ۖ وَنُذِيقُهُ يَوْمَ الْقِيَامَةِ عَذَابَ الْحَرِيقِ', 22, 9, 17, 67, 333, 'آنها با تکبّر و بی‌اعتنایی (نسبت به سخنان الهی)، می‌خواهند مردم را از راه خدا گمراه سازند! برای آنان در دنیا رسوایی است؛ و در قیامت، عذاب سوزان به آنها می‌چشانیم!'),
(2605, 2605, 'ذَٰلِكَ بِمَا قَدَّمَتْ يَدَاكَ وَأَنَّ اللَّهَ لَيْسَ بِظَلَّامٍ لِلْعَبِيدِ', 22, 10, 17, 67, 333, '(و به آنان می‌گوییم:) این در برابر چیزی است که دستهایتان از پیش برای شما فرستاده؛ و خداوند هرگز به بندگان ظلم نمی‌کند!'),
(2606, 2606, 'وَمِنَ النَّاسِ مَنْ يَعْبُدُ اللَّهَ عَلَىٰ حَرْفٍ ۖ فَإِنْ أَصَابَهُ خَيْرٌ اطْمَأَنَّ بِهِ ۖ وَإِنْ أَصَابَتْهُ فِتْنَةٌ انْقَلَبَ عَلَىٰ وَجْهِهِ خَسِرَ الدُّنْيَا وَالْآخِرَةَ ۚ ذَٰلِكَ هُوَ الْخُسْرَانُ الْمُبِينُ', 22, 11, 17, 67, 333, 'بعضی از مردم خدا را تنها با زبان می‌پرستند (و ایمان قلبیشان بسیار ضعیف است)؛ همین که (دنیا به آنها رو کند و نفع و) خیری به آنان برسد، حالت اطمینان پیدا می‌کنند؛ اما اگر مصیبتی برای امتحان به آنها برسد، دگرگون می‌شوند (و به کفر رومی‌آورند)! (به این ترتیب) هم دنیا را از دست داده‌اند، و هم آخرت را؛ و این همان خسران و زیان آشکار است!'),
(2607, 2607, 'يَدْعُو مِنْ دُونِ اللَّهِ مَا لَا يَضُرُّهُ وَمَا لَا يَنْفَعُهُ ۚ ذَٰلِكَ هُوَ الضَّلَالُ الْبَعِيدُ', 22, 12, 17, 67, 333, 'او جز خدا کسی را می‌خواند که نه زیانی به او می‌رساند، و نه سودی؛ این همان گمراهی بسیار عمیق است.'),
(2608, 2608, 'يَدْعُو لَمَنْ ضَرُّهُ أَقْرَبُ مِنْ نَفْعِهِ ۚ لَبِئْسَ الْمَوْلَىٰ وَلَبِئْسَ الْعَشِيرُ', 22, 13, 17, 67, 333, 'او کسی را می‌خواند که زیانش از نفعش نزدیکتر است؛ چه بد مولا و یاوری، و چه بد مونس و معاشری!'),
(2609, 2609, 'إِنَّ اللَّهَ يُدْخِلُ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهَارُ ۚ إِنَّ اللَّهَ يَفْعَلُ مَا يُرِيدُ', 22, 14, 17, 67, 333, 'خداوند کسانی را که ایمان آورده و اعمال صالح انجام داده‌اند، در باغهایی از بهشت وارد می‌کند که نهرها زیر درختانش جاری است؛ (آری،) خدا هر چه را اراده کند انجام می‌دهد!'),
(2610, 2610, 'مَنْ كَانَ يَظُنُّ أَنْ لَنْ يَنْصُرَهُ اللَّهُ فِي الدُّنْيَا وَالْآخِرَةِ فَلْيَمْدُدْ بِسَبَبٍ إِلَى السَّمَاءِ ثُمَّ لْيَقْطَعْ فَلْيَنْظُرْ هَلْ يُذْهِبَنَّ كَيْدُهُ مَا يَغِيظُ', 22, 15, 17, 67, 333, 'هر کس گمان می‌کند که خدا پیامبرش را در دنیا و آخرت یاری نخواهد کرد (و از این نظر عصبانی است، هر کاری از دستش ساخته است بکند)، ریسمانی به سقف خانه خود بیاویزد، و خود را حلق آویز و نفس خود را قطع کند (و تا لبه پرتگاه مرگ پیش رود)؛ ببیند آیا این کار خشم او را فرو می‌نشاند؟!'),
(2611, 2611, 'وَكَذَٰلِكَ أَنْزَلْنَاهُ آيَاتٍ بَيِّنَاتٍ وَأَنَّ اللَّهَ يَهْدِي مَنْ يُرِيدُ', 22, 16, 17, 67, 334, 'این گونه ما آن = قرآن‌ را بصورت آیات روشنی نازل کردیم؛ و خداوند هر کس را بخواهد هدایت می‌کند.'),
(2612, 2612, 'إِنَّ الَّذِينَ آمَنُوا وَالَّذِينَ هَادُوا وَالصَّابِئِينَ وَالنَّصَارَىٰ وَالْمَجُوسَ وَالَّذِينَ أَشْرَكُوا إِنَّ اللَّهَ يَفْصِلُ بَيْنَهُمْ يَوْمَ الْقِيَامَةِ ۚ إِنَّ اللَّهَ عَلَىٰ كُلِّ شَيْءٍ شَهِيدٌ', 22, 17, 17, 67, 334, 'مسلّماً کسانی که ایمان آورده‌اند، و یهود و صابئان = ستاره‌پرستان‌ و نصاری و مجوس و مشرکان، خداوند در میان آنان روز قیامت داوری می‌کند؛ (و حق را از باطل جدا می‌سازد؛) خداوند بر هر چیز گواه (و از همه چیز آگاه) است.'),
(2613, 2613, 'أَلَمْ تَرَ أَنَّ اللَّهَ يَسْجُدُ لَهُ مَنْ فِي السَّمَاوَاتِ وَمَنْ فِي الْأَرْضِ وَالشَّمْسُ وَالْقَمَرُ وَالنُّجُومُ وَالْجِبَالُ وَالشَّجَرُ وَالدَّوَابُّ وَكَثِيرٌ مِنَ النَّاسِ ۖ وَكَثِيرٌ حَقَّ عَلَيْهِ الْعَذَابُ ۗ وَمَنْ يُهِنِ اللَّهُ فَمَا لَهُ مِنْ مُكْرِمٍ ۚ إِنَّ اللَّهَ يَفْعَلُ مَا يَشَاءُ ۩', 22, 18, 17, 67, 334, 'آیا ندیدی که تمام کسانی که در آسمانها و کسانی که در زمینند برای خدا سجده می‌کنند؟! و (همچنین) خورشید و ماه و ستارگان و کوه‌ها و درختان و جنبندگان، و بسیاری از مردم! امّا بسیاری (ابا دارند، و) فرمان عذاب درباره آنان حتمی است؛ و هر کس را خدا خوار کند، کسی او را گرامی نخواهد داشت! خداوند هر کار را بخواهد (و صلاح بداند) انجام می‌دهد!'),
(2614, 2614, ' هَٰذَانِ خَصْمَانِ اخْتَصَمُوا فِي رَبِّهِمْ ۖ فَالَّذِينَ كَفَرُوا قُطِّعَتْ لَهُمْ ثِيَابٌ مِنْ نَارٍ يُصَبُّ مِنْ فَوْقِ رُءُوسِهِمُ الْحَمِيمُ', 22, 19, 17, 67, 334, 'اینان دو گروهند که درباره پروردگارشان به مخاصمه و جدال پرداختند؛ کسانی که کافر شدند، لباسهایی از آتش برای آنها بریده شده، و مایع سوزان و جوشان بر سرشان ریخته می‌شود؛'),
(2615, 2615, 'يُصْهَرُ بِهِ مَا فِي بُطُونِهِمْ وَالْجُلُودُ', 22, 20, 17, 67, 334, 'آنچنان که هم درونشان با آن آب می‌شود، و هم پوستهایشان.'),
(2616, 2616, 'وَلَهُمْ مَقَامِعُ مِنْ حَدِيدٍ', 22, 21, 17, 67, 334, 'و برای آنان گرزهایی از آهن (سوزان) است.'),
(2617, 2617, 'كُلَّمَا أَرَادُوا أَنْ يَخْرُجُوا مِنْهَا مِنْ غَمٍّ أُعِيدُوا فِيهَا وَذُوقُوا عَذَابَ الْحَرِيقِ', 22, 22, 17, 67, 334, 'هر گاه بخواهند از غم و اندوه‌های دوزخ خارج شوند، آنها را به آن بازمی‌گردانند؛ و (به آنان گفته می‌شود:) بچشید عذاب سوزان را!'),
(2618, 2618, 'إِنَّ اللَّهَ يُدْخِلُ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهَارُ يُحَلَّوْنَ فِيهَا مِنْ أَسَاوِرَ مِنْ ذَهَبٍ وَلُؤْلُؤًا ۖ وَلِبَاسُهُمْ فِيهَا حَرِيرٌ', 22, 23, 17, 67, 334, 'خداوند کسانی را که ایمان آورده و اعمال صالح انجام داده‌اند، در باغهایی از بهشت وارد می‌کند که از زیر درختانش نهرها جاری است؛ آنان با دستبندهایی از طلا و مروارید زینت می‌شوند؛ و در آنجا لباسهایشان از حریر است.'),
(2619, 2619, 'وَهُدُوا إِلَى الطَّيِّبِ مِنَ الْقَوْلِ وَهُدُوا إِلَىٰ صِرَاطِ الْحَمِيدِ', 22, 24, 17, 67, 335, 'و بسوی سخنان پاکیزه هدایت می‌شوند، و به راه خداوند شایسته ستایش، راهنمایی می‌گردند.'),
(2620, 2620, 'إِنَّ الَّذِينَ كَفَرُوا وَيَصُدُّونَ عَنْ سَبِيلِ اللَّهِ وَالْمَسْجِدِ الْحَرَامِ الَّذِي جَعَلْنَاهُ لِلنَّاسِ سَوَاءً الْعَاكِفُ فِيهِ وَالْبَادِ ۚ وَمَنْ يُرِدْ فِيهِ بِإِلْحَادٍ بِظُلْمٍ نُذِقْهُ مِنْ عَذَابٍ أَلِيمٍ', 22, 25, 17, 67, 335, 'کسانی که کافر شدند، و مؤمنان را از راه خدا بازداشتند، و (همچنین) از مسجد الحرام، که آن را برای همه مردم، برابر قرار دادیم، چه کسانی که در آنجا زندگی می‌کنند یا از نقاط دور وارد می‌شوند (، مستحقّ عذابی دردناکند)؛ و هر کس بخواهد در این سرزمین از راه حق منحرف گردد و دست به ستم زند، ما از عذابی دردناک به او می‌چشانیم!'),
(2621, 2621, 'وَإِذْ بَوَّأْنَا لِإِبْرَاهِيمَ مَكَانَ الْبَيْتِ أَنْ لَا تُشْرِكْ بِي شَيْئًا وَطَهِّرْ بَيْتِيَ لِلطَّائِفِينَ وَالْقَائِمِينَ وَالرُّكَّعِ السُّجُودِ', 22, 26, 17, 67, 335, '(به خاطر بیاور) زمانی را که جای خانه (کعبه) را برای ابراهیم آماده ساختیم (تا خانه را بنا کند؛ و به او گفتیم:) چیزی را همتای من قرار مده! و خانه‌ام را برای طواف‌کنندگان و قیام‌کنندگان و رکوع‌کنندگان و سجودکنندگان (از آلودگی بتها و از هر گونه آلودگی) پاک ساز!'),
(2622, 2622, 'وَأَذِّنْ فِي النَّاسِ بِالْحَجِّ يَأْتُوكَ رِجَالًا وَعَلَىٰ كُلِّ ضَامِرٍ يَأْتِينَ مِنْ كُلِّ فَجٍّ عَمِيقٍ', 22, 27, 17, 67, 335, 'و مردم را دعوت عمومی به حج کن؛ تا پیاده و سواره بر مرکبهای لاغر از هر راه دوری بسوی تو بیایند...'),
(2623, 2623, 'لِيَشْهَدُوا مَنَافِعَ لَهُمْ وَيَذْكُرُوا اسْمَ اللَّهِ فِي أَيَّامٍ مَعْلُومَاتٍ عَلَىٰ مَا رَزَقَهُمْ مِنْ بَهِيمَةِ الْأَنْعَامِ ۖ فَكُلُوا مِنْهَا وَأَطْعِمُوا الْبَائِسَ الْفَقِيرَ', 22, 28, 17, 67, 335, 'تا شاهد منافع گوناگون خویش (در این برنامه حیاتبخش) باشند؛ و در ایّام معیّنی نام خدا را، بر چهارپایانی که به آنان داده است، (به هنگام قربانی‌کردن) ببرند؛ پس از گوشت آنها بخورید؛ و بینوای فقیر را نیز اطعام نمایید!'),
(2624, 2624, 'ثُمَّ لْيَقْضُوا تَفَثَهُمْ وَلْيُوفُوا نُذُورَهُمْ وَلْيَطَّوَّفُوا بِالْبَيْتِ الْعَتِيقِ', 22, 29, 17, 67, 335, 'سپس، باید آلودگیهایشان را برطرف سازند؛ و به نذرهای خود وفا کنند؛ و بر گرد خانه گرامی کعبه، طواف کنند.'),
(2625, 2625, 'ذَٰلِكَ وَمَنْ يُعَظِّمْ حُرُمَاتِ اللَّهِ فَهُوَ خَيْرٌ لَهُ عِنْدَ رَبِّهِ ۗ وَأُحِلَّتْ لَكُمُ الْأَنْعَامُ إِلَّا مَا يُتْلَىٰ عَلَيْكُمْ ۖ فَاجْتَنِبُوا الرِّجْسَ مِنَ الْأَوْثَانِ وَاجْتَنِبُوا قَوْلَ الزُّورِ', 22, 30, 17, 67, 335, '(مناسک حج) این است! و هر کس برنامه‌های الهی را بزرگ دارد، نزد پروردگارش برای او بهتر است! و چهارپایان برای شما حلال شده، مگر آنچه (ممنوع بودنش) بر شما خوانده می‌شود. از پلیدیهای بتها اجتناب کنید! و از سخن باطل بپرهیزید!'),
(2626, 2626, 'حُنَفَاءَ لِلَّهِ غَيْرَ مُشْرِكِينَ بِهِ ۚ وَمَنْ يُشْرِكْ بِاللَّهِ فَكَأَنَّمَا خَرَّ مِنَ السَّمَاءِ فَتَخْطَفُهُ الطَّيْرُ أَوْ تَهْوِي بِهِ الرِّيحُ فِي مَكَانٍ سَحِيقٍ', 22, 31, 17, 67, 336, '(برنامه و مناسک حج را انجام دهید) در حالی که همگی خالص برای خدا باشد! هیچ گونه همتایی برای او قائل نشوید! و هر کس همتایی برای خدا قرار دهد، گویی از آسمان سقوب کرده، و پرندگان (در وسط هوا) او را می‌ربایند؛ و یا تندباد او را به جای دوردستی پرتاب می‌کند!'),
(2627, 2627, 'ذَٰلِكَ وَمَنْ يُعَظِّمْ شَعَائِرَ اللَّهِ فَإِنَّهَا مِنْ تَقْوَى الْقُلُوبِ', 22, 32, 17, 67, 336, 'این است (مناسک حج)! و هر کس شعائر الهی را بزرگ دارد، این کار نشانه تقوای دلهاست.'),
(2628, 2628, 'لَكُمْ فِيهَا مَنَافِعُ إِلَىٰ أَجَلٍ مُسَمًّى ثُمَّ مَحِلُّهَا إِلَى الْبَيْتِ الْعَتِيقِ', 22, 33, 17, 67, 336, 'در آن (حیوانات قربانی)، منافعی برای شماست تا زمان معیّنی = روز ذبح آنها سپس محل آن، خانه قدیمی و گرامی (کعبه) است.'),
(2629, 2629, 'وَلِكُلِّ أُمَّةٍ جَعَلْنَا مَنْسَكًا لِيَذْكُرُوا اسْمَ اللَّهِ عَلَىٰ مَا رَزَقَهُمْ مِنْ بَهِيمَةِ الْأَنْعَامِ ۗ فَإِلَٰهُكُمْ إِلَٰهٌ وَاحِدٌ فَلَهُ أَسْلِمُوا ۗ وَبَشِّرِ الْمُخْبِتِينَ', 22, 34, 17, 67, 336, 'برای هر امّتی قربانگاهی قرار دادیم، تا نام خدا را (به هنگام قربانی) بر چهارپایانی که به آنان روزی داده‌ایم ببرند، و خدای شما معبود واحدی است؛ در برابر (فرمان) او تسلیم شوید و بشارت ده متواضعان و تسلیم‌شوندگان را.'),
(2630, 2630, 'الَّذِينَ إِذَا ذُكِرَ اللَّهُ وَجِلَتْ قُلُوبُهُمْ وَالصَّابِرِينَ عَلَىٰ مَا أَصَابَهُمْ وَالْمُقِيمِي الصَّلَاةِ وَمِمَّا رَزَقْنَاهُمْ يُنْفِقُونَ', 22, 35, 17, 67, 336, 'همانها که چون نام خدا برده می‌شود، دلهایشان پر از خوف (پروردگار) می‌گردد؛ و شکیبایان در برابر مصیبتهایی که به آنان می‌رسد؛ و آنها که نماز را برپا می‌دارند، و از آنچه به آنان روزی داده‌ایم انفاق می‌کنند.'),
(2631, 2631, 'وَالْبُدْنَ جَعَلْنَاهَا لَكُمْ مِنْ شَعَائِرِ اللَّهِ لَكُمْ فِيهَا خَيْرٌ ۖ فَاذْكُرُوا اسْمَ اللَّهِ عَلَيْهَا صَوَافَّ ۖ فَإِذَا وَجَبَتْ جُنُوبُهَا فَكُلُوا مِنْهَا وَأَطْعِمُوا الْقَانِعَ وَالْمُعْتَرَّ ۚ كَذَٰلِكَ سَخَّرْنَاهَا لَكُمْ لَعَلَّكُمْ تَشْكُرُونَ', 22, 36, 17, 67, 336, 'و شترهای چاق و فربه را (در مراسم حج) برای شما از شعائر الهی قرار دادیم؛ در آنها برای شما خیر و برکت است؛ نام خدا را (هنگام قربانی کردن) در حالی که به صف ایستاده‌اند بر آنها ببرید؛ و هنگامی که پهلوهایشان آرام گرفت (و جان دادند)، از گوشت آنها بخورید، و مستمندان قانع و فقیران را نیز از آن اطعام کنید! این گونه ما آنها را مسخّرتان ساختیم، تا شکر خدا را بجا آورید.'),
(2632, 2632, 'لَنْ يَنَالَ اللَّهَ لُحُومُهَا وَلَا دِمَاؤُهَا وَلَٰكِنْ يَنَالُهُ التَّقْوَىٰ مِنْكُمْ ۚ كَذَٰلِكَ سَخَّرَهَا لَكُمْ لِتُكَبِّرُوا اللَّهَ عَلَىٰ مَا هَدَاكُمْ ۗ وَبَشِّرِ الْمُحْسِنِينَ', 22, 37, 17, 67, 336, 'نه گوشتها و نه خونهای آنها، هرگز به خدا نمی‌رسد. آنچه به او می‌رسد، تقوا و پرهیزگاری شماست. این گونه خداوند آنها را مسخّر شما ساخته، تا او را بخاطر آنکه شما را هدایت کرده است بزرگ بشمرید؛ و بشارت ده نیکوکاران را!'),
(2633, 2633, ' إِنَّ اللَّهَ يُدَافِعُ عَنِ الَّذِينَ آمَنُوا ۗ إِنَّ اللَّهَ لَا يُحِبُّ كُلَّ خَوَّانٍ كَفُورٍ', 22, 38, 17, 68, 336, 'خداوند از کسانی که ایمان آورده‌اند دفاع می‌کند؛ خداوند هیچ خیانتکار ناسپاسی را دوست ندارد!'),
(2634, 2634, 'أُذِنَ لِلَّذِينَ يُقَاتَلُونَ بِأَنَّهُمْ ظُلِمُوا ۚ وَإِنَّ اللَّهَ عَلَىٰ نَصْرِهِمْ لَقَدِيرٌ', 22, 39, 17, 68, 337, 'به کسانی که جنگ بر آنان تحمیل گردیده، اجازه جهاد داده شده است؛ چرا که مورد ستم قرار گرفته‌اند؛ و خدا بر یاری آنها تواناست.'),
(2635, 2635, 'الَّذِينَ أُخْرِجُوا مِنْ دِيَارِهِمْ بِغَيْرِ حَقٍّ إِلَّا أَنْ يَقُولُوا رَبُّنَا اللَّهُ ۗ وَلَوْلَا دَفْعُ اللَّهِ النَّاسَ بَعْضَهُمْ بِبَعْضٍ لَهُدِّمَتْ صَوَامِعُ وَبِيَعٌ وَصَلَوَاتٌ وَمَسَاجِدُ يُذْكَرُ فِيهَا اسْمُ اللَّهِ كَثِيرًا ۗ وَلَيَنْصُرَنَّ اللَّهُ مَنْ يَنْصُرُهُ ۗ إِنَّ اللَّهَ لَقَوِيٌّ عَزِيزٌ', 22, 40, 17, 68, 337, 'همانها که از خانه و شهر خود، به ناحق رانده شدند، جز اینکه می‌گفتند: «پروردگار ما، خدای یکتاست!» و اگر خداوند بعضی از مردم را بوسیله بعضی دیگر دفع نکند، دیرها و صومعه‌ها، و معابد یهود و نصارا، و مساجدی که نام خدا در آن بسیار برده می‌شود، ویران می‌گردد! و خداوند کسانی را که یاری او کنند (و از آیینش دفاع نمایند) یاری می‌کند؛ خداوند قوی و شکست ناپذیر است.'),
(2636, 2636, 'الَّذِينَ إِنْ مَكَّنَّاهُمْ فِي الْأَرْضِ أَقَامُوا الصَّلَاةَ وَآتَوُا الزَّكَاةَ وَأَمَرُوا بِالْمَعْرُوفِ وَنَهَوْا عَنِ الْمُنْكَرِ ۗ وَلِلَّهِ عَاقِبَةُ الْأُمُورِ', 22, 41, 17, 68, 337, 'همان کسانی که هر گاه در زمین به آنها قدرت بخشیدیم، نماز را برپا می‌دارند، و زکات می‌دهند، و امر به معروف و نهی از منکر می‌کنند، و پایان همه کارها از آن خداست!'),
(2637, 2637, 'وَإِنْ يُكَذِّبُوكَ فَقَدْ كَذَّبَتْ قَبْلَهُمْ قَوْمُ نُوحٍ وَعَادٌ وَثَمُودُ', 22, 42, 17, 68, 337, 'اگر تو را تکذیب کنند، (امر تازه‌ای نیست؛) پیش از آنها قوم نوح و عاد و ثمود (پیامبرانشان را) تکذیب کردند.'),
(2638, 2638, 'وَقَوْمُ إِبْرَاهِيمَ وَقَوْمُ لُوطٍ', 22, 43, 17, 68, 337, 'و همچنین قوم ابراهیم و قوم لوط؛'),
(2639, 2639, 'وَأَصْحَابُ مَدْيَنَ ۖ وَكُذِّبَ مُوسَىٰ فَأَمْلَيْتُ لِلْكَافِرِينَ ثُمَّ أَخَذْتُهُمْ ۖ فَكَيْفَ كَانَ نَكِيرِ', 22, 44, 17, 68, 337, 'و اصحاب مدین (قوم شعیب)؛ و نیز موسی (از سوی فرعونیان) تکذیب شد؛ امّا من به کافران مهلت دادم، سپس آنها را مجازات کردم. دیدی چگونه (عمل آنها را) انکار نمودم (و چگونه به آنان پاسخ گفتم)؟!'),
(2640, 2640, 'فَكَأَيِّنْ مِنْ قَرْيَةٍ أَهْلَكْنَاهَا وَهِيَ ظَالِمَةٌ فَهِيَ خَاوِيَةٌ عَلَىٰ عُرُوشِهَا وَبِئْرٍ مُعَطَّلَةٍ وَقَصْرٍ مَشِيدٍ', 22, 45, 17, 68, 337, 'چه بسیار شهرها و آبادیهایی که آنها را نابود و هلاک کردیم در حالی که (مردمش) ستمگر بودند، بگونه‌ای که بر سقفهای خود فروریخت! (نخست سقفها ویران گشت؛ و بعد دیوارها بر روی سقفها!) و چه بسیار چاه پر آب که بی‌صاحب ماند؛ و چه بسیار قصرهای محکم و مرتفع!'),
(2641, 2641, 'أَفَلَمْ يَسِيرُوا فِي الْأَرْضِ فَتَكُونَ لَهُمْ قُلُوبٌ يَعْقِلُونَ بِهَا أَوْ آذَانٌ يَسْمَعُونَ بِهَا ۖ فَإِنَّهَا لَا تَعْمَى الْأَبْصَارُ وَلَٰكِنْ تَعْمَى الْقُلُوبُ الَّتِي فِي الصُّدُورِ', 22, 46, 17, 68, 337, 'آیا آنان در زمین سیر نکردند، تا دلهایی داشته باشند که حقیقت را با آن درک کنند؛ یا گوشهای شنوایی که با آن (ندای حق را) بشنوند؟! چرا که چشمهای ظاهر نابینا نمی‌شود، بلکه دلهایی که در سینه‌هاست کور می‌شود.'),
(2642, 2642, 'وَيَسْتَعْجِلُونَكَ بِالْعَذَابِ وَلَنْ يُخْلِفَ اللَّهُ وَعْدَهُ ۚ وَإِنَّ يَوْمًا عِنْدَ رَبِّكَ كَأَلْفِ سَنَةٍ مِمَّا تَعُدُّونَ', 22, 47, 17, 68, 338, 'آنان از تو تقاضای شتاب در عذاب می‌کنند؛ در حالی که خداوند هرگز از وعده خود تخلّف نخواهد کرد! و یک روز نزد پروردگارت، همانند هزار سال از سالهایی است که شما می‌شمرید!'),
(2643, 2643, 'وَكَأَيِّنْ مِنْ قَرْيَةٍ أَمْلَيْتُ لَهَا وَهِيَ ظَالِمَةٌ ثُمَّ أَخَذْتُهَا وَإِلَيَّ الْمَصِيرُ', 22, 48, 17, 68, 338, 'و چه بسیار شهرها و آبادیهایی که به آنها مهلت دادم، در حالی که ستمگر بودند؛ (امّا از این مهلت برای اصلاح خویش استفاده نکردند.) سپس آنها را مجازات کردم؛ و بازگشت، تنها بسوی من است!'),
(2644, 2644, 'قُلْ يَا أَيُّهَا النَّاسُ إِنَّمَا أَنَا لَكُمْ نَذِيرٌ مُبِينٌ', 22, 49, 17, 68, 338, 'بگو: «ای مردم! من برای شما بیم‌دهنده آشکاری هستم!'),
(2645, 2645, 'فَالَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ لَهُمْ مَغْفِرَةٌ وَرِزْقٌ كَرِيمٌ', 22, 50, 17, 68, 338, 'آنها که ایمان آوردند و اعمال صالح انجام دادند، آمرزش و روزی پر ارزشی برای آنهاست!'),
(2646, 2646, 'وَالَّذِينَ سَعَوْا فِي آيَاتِنَا مُعَاجِزِينَ أُولَٰئِكَ أَصْحَابُ الْجَحِيمِ', 22, 51, 17, 68, 338, 'و آنها که در (محو) آیات ما تلاش کردند، و چنین می‌پنداشتند که می‌توانند بر اراده حتمی ما غالب شوند، اصحاب دوزخند!»'),
(2647, 2647, 'وَمَا أَرْسَلْنَا مِنْ قَبْلِكَ مِنْ رَسُولٍ وَلَا نَبِيٍّ إِلَّا إِذَا تَمَنَّىٰ أَلْقَى الشَّيْطَانُ فِي أُمْنِيَّتِهِ فَيَنْسَخُ اللَّهُ مَا يُلْقِي الشَّيْطَانُ ثُمَّ يُحْكِمُ اللَّهُ آيَاتِهِ ۗ وَاللَّهُ عَلِيمٌ حَكِيمٌ', 22, 52, 17, 68, 338, 'هیچ پیامبری را پیش از تو نفرستادیم مگر اینکه هرگاه آرزو می‌کرد (و طرحی برای پیشبرد اهداف الهی خود می‌ریخت)، شیطان القائاتی در آن می‌کرد؛ امّا خداوند القائات شیطان را از میان می‌برد، سپس آیات خود را استحکام می‌بخشید؛ و خداوند علیم و حکیم است.'),
(2648, 2648, 'لِيَجْعَلَ مَا يُلْقِي الشَّيْطَانُ فِتْنَةً لِلَّذِينَ فِي قُلُوبِهِمْ مَرَضٌ وَالْقَاسِيَةِ قُلُوبُهُمْ ۗ وَإِنَّ الظَّالِمِينَ لَفِي شِقَاقٍ بَعِيدٍ', 22, 53, 17, 68, 338, 'هدف این بود که خداوند القای شیطان را آزمونی قرار دهد برای آنها که در دلهایشان بیماری است، و آنها که سنگدلند؛ و ظالمان در عداوت شدید دور از حقّ قرار گرفته‌اند!'),
(2649, 2649, 'وَلِيَعْلَمَ الَّذِينَ أُوتُوا الْعِلْمَ أَنَّهُ الْحَقُّ مِنْ رَبِّكَ فَيُؤْمِنُوا بِهِ فَتُخْبِتَ لَهُ قُلُوبُهُمْ ۗ وَإِنَّ اللَّهَ لَهَادِ الَّذِينَ آمَنُوا إِلَىٰ صِرَاطٍ مُسْتَقِيمٍ', 22, 54, 17, 68, 338, 'و (نیز) هدف این بود که آگاهان بدانند این حقّی است از سوی پروردگارت، و در نتیجه به آن ایمان بیاورند، و دلهایشان در برابر آن خاضع گردد؛ و خداوند کسانی را که ایمان آوردند، بسوی صراط مستقیم هدایت می‌کند.'),
(2650, 2650, 'وَلَا يَزَالُ الَّذِينَ كَفَرُوا فِي مِرْيَةٍ مِنْهُ حَتَّىٰ تَأْتِيَهُمُ السَّاعَةُ بَغْتَةً أَوْ يَأْتِيَهُمْ عَذَابُ يَوْمٍ عَقِيمٍ', 22, 55, 17, 68, 338, 'کافران همواره در باره قرآن در شکّ هستند، تا آنکه روز قیامت بطور ناگهانی فرارسد، یا عذاب روز عقیم = روزی که قادر بر جبران گذشته نیستند به سراغشان آید!'),
(2651, 2651, 'الْمُلْكُ يَوْمَئِذٍ لِلَّهِ يَحْكُمُ بَيْنَهُمْ ۚ فَالَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ فِي جَنَّاتِ النَّعِيمِ', 22, 56, 17, 68, 339, 'حکومت و فرمانروایی در آن روز از آن خداست؛ و میان آنها داوری می‌کند: کسانی که ایمان آورده و کارهای شایسته انجام داده‌اند، در باغهای پرنعمت بهشتند؛'),
(2652, 2652, 'وَالَّذِينَ كَفَرُوا وَكَذَّبُوا بِآيَاتِنَا فَأُولَٰئِكَ لَهُمْ عَذَابٌ مُهِينٌ', 22, 57, 17, 68, 339, 'و کسانی که کافر شدند و آیات ما را تکذیب کردند، عذاب خوارکننده‌ای برای آنهاست!'),
(2653, 2653, 'وَالَّذِينَ هَاجَرُوا فِي سَبِيلِ اللَّهِ ثُمَّ قُتِلُوا أَوْ مَاتُوا لَيَرْزُقَنَّهُمُ اللَّهُ رِزْقًا حَسَنًا ۚ وَإِنَّ اللَّهَ لَهُوَ خَيْرُ الرَّازِقِينَ', 22, 58, 17, 68, 339, 'و کسانی که در راه خدا هجرت کردند، سپس کشته شدند یا به مرگ طبیعی از دنیا رفتند، خداوند به آنها روزی نیکویی می‌دهد؛ که او بهترین روزی‌دهندگان است!'),
(2654, 2654, 'لَيُدْخِلَنَّهُمْ مُدْخَلًا يَرْضَوْنَهُ ۗ وَإِنَّ اللَّهَ لَعَلِيمٌ حَلِيمٌ', 22, 59, 17, 68, 339, 'خداوند آنان را در محلی وارد می‌کند که از آن خشنود خواهند بود؛ و خداوند دانا و بردبار است.'),
(2655, 2655, ' ذَٰلِكَ وَمَنْ عَاقَبَ بِمِثْلِ مَا عُوقِبَ بِهِ ثُمَّ بُغِيَ عَلَيْهِ لَيَنْصُرَنَّهُ اللَّهُ ۗ إِنَّ اللَّهَ لَعَفُوٌّ غَفُورٌ', 22, 60, 17, 68, 339, '(آری،) مطلب چنین است! و هر کس به همان مقدار که به او ستم شده مجازات کند، سپس مورد تعدّی قرار گیرد، خدا او را یاری خواهد کرد؛ یقیناً خداوند بخشنده و آمرزنده است!'),
(2656, 2656, 'ذَٰلِكَ بِأَنَّ اللَّهَ يُولِجُ اللَّيْلَ فِي النَّهَارِ وَيُولِجُ النَّهَارَ فِي اللَّيْلِ وَأَنَّ اللَّهَ سَمِيعٌ بَصِيرٌ', 22, 61, 17, 68, 339, 'این (وعده نصرت الهی) بخاطر آن است (که او بر هر چیز قادر است؛ خداوندی) که شب را در روز، و روز را در شب داخل می‌کند؛ و خداوند شنوا و بیناست!'),
(2657, 2657, 'ذَٰلِكَ بِأَنَّ اللَّهَ هُوَ الْحَقُّ وَأَنَّ مَا يَدْعُونَ مِنْ دُونِهِ هُوَ الْبَاطِلُ وَأَنَّ اللَّهَ هُوَ الْعَلِيُّ الْكَبِيرُ', 22, 62, 17, 68, 339, 'این بخاطر آن است که خداوند حقّ است؛ و آنچه را غیر از او می‌خوانند باطل است؛ و خداوند بلندمقام و بزرگ است!'),
(2658, 2658, 'أَلَمْ تَرَ أَنَّ اللَّهَ أَنْزَلَ مِنَ السَّمَاءِ مَاءً فَتُصْبِحُ الْأَرْضُ مُخْضَرَّةً ۗ إِنَّ اللَّهَ لَطِيفٌ خَبِيرٌ', 22, 63, 17, 68, 339, 'آیا ندیدی خداوند از آسمان، آبی فرستاد، و زمین (بر اثر آن) سرسبز و خرّم می‌گردد؟! و خداوند لطیف و آگاه است.'),
(2659, 2659, 'لَهُ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ ۗ وَإِنَّ اللَّهَ لَهُوَ الْغَنِيُّ الْحَمِيدُ', 22, 64, 17, 68, 339, 'آنچه در آسمانها و آنچه در زمین است از آن اوست؛ و خداوند بی‌نیاز، و شایسته هر گونه ستایش است!'),
(2660, 2660, 'أَلَمْ تَرَ أَنَّ اللَّهَ سَخَّرَ لَكُمْ مَا فِي الْأَرْضِ وَالْفُلْكَ تَجْرِي فِي الْبَحْرِ بِأَمْرِهِ وَيُمْسِكُ السَّمَاءَ أَنْ تَقَعَ عَلَى الْأَرْضِ إِلَّا بِإِذْنِهِ ۗ إِنَّ اللَّهَ بِالنَّاسِ لَرَءُوفٌ رَحِيمٌ', 22, 65, 17, 68, 340, 'آیا ندیدی که خداوند آنچه را در زمین است مسخّر شما کرد؛ و (نیز) کشتیهایی را که به فرمان او بر صفحه اقیانوسها حرکت می‌کنند؛ و آسمان = کرات و سنگهای آسمانی‌ را نگه می‌دارد، تا جز بفرمان او، بر زمین فرو نیفتند؟ خداوند نسبت به مردم رحیم و مهربان است!'),
(2661, 2661, 'وَهُوَ الَّذِي أَحْيَاكُمْ ثُمَّ يُمِيتُكُمْ ثُمَّ يُحْيِيكُمْ ۗ إِنَّ الْإِنْسَانَ لَكَفُورٌ', 22, 66, 17, 68, 340, 'و او کسی است که شما را زنده کرد، سپس می‌میراند، بار دیگر زنده می‌کند، امّا این انسان بسیار ناسپاس است.'),
(2662, 2662, 'لِكُلِّ أُمَّةٍ جَعَلْنَا مَنْسَكًا هُمْ نَاسِكُوهُ ۖ فَلَا يُنَازِعُنَّكَ فِي الْأَمْرِ ۚ وَادْعُ إِلَىٰ رَبِّكَ ۖ إِنَّكَ لَعَلَىٰ هُدًى مُسْتَقِيمٍ', 22, 67, 17, 68, 340, 'برای هر امّتی عبادتی قرار دادیم، تا آن عبادت را (در پیشگاه خدا) انجام دهند؛ پس نباید در این امر با تو به نزاع برخیزند! بسوی پروردگارت دعوت کن، که بر هدایت مستقیم قرار داری (و راه راست همین است که تو می‌پویی).'),
(2663, 2663, 'وَإِنْ جَادَلُوكَ فَقُلِ اللَّهُ أَعْلَمُ بِمَا تَعْمَلُونَ', 22, 68, 17, 68, 340, 'و اگر آنان با تو به جدال برخیزند، بگو: «خدا از کارهایی که شما انجام می‌دهید آگاهتر است!'),
(2664, 2664, 'اللَّهُ يَحْكُمُ بَيْنَكُمْ يَوْمَ الْقِيَامَةِ فِيمَا كُنْتُمْ فِيهِ تَخْتَلِفُونَ', 22, 69, 17, 68, 340, 'و خداوند در روز قیامت، میان شما در آنچه اختلاف می‌کردید، داوری می‌کند!»'),
(2665, 2665, 'أَلَمْ تَعْلَمْ أَنَّ اللَّهَ يَعْلَمُ مَا فِي السَّمَاءِ وَالْأَرْضِ ۗ إِنَّ ذَٰلِكَ فِي كِتَابٍ ۚ إِنَّ ذَٰلِكَ عَلَى اللَّهِ يَسِيرٌ', 22, 70, 17, 68, 340, 'آیا نمی‌دانستی خداوند آنچه را در آسمان و زمین است می‌داند؟! همه اینها در کتابی ثبت است (همان کتاب علم بی‌پایان پروردگار)؛ و این بر خداوند آسان است!'),
(2666, 2666, 'وَيَعْبُدُونَ مِنْ دُونِ اللَّهِ مَا لَمْ يُنَزِّلْ بِهِ سُلْطَانًا وَمَا لَيْسَ لَهُمْ بِهِ عِلْمٌ ۗ وَمَا لِلظَّالِمِينَ مِنْ نَصِيرٍ', 22, 71, 17, 68, 340, 'آنها غیر از خداوند، چیزهایی را می‌پرستند که او هیچ گونه دلیلی بر آن نازل نکرده است، و چیزهایی را که علم و آگاهی به آن ندارند! و برای ستمگران، یاور و راهنمایی نیست!'),
(2667, 2667, 'وَإِذَا تُتْلَىٰ عَلَيْهِمْ آيَاتُنَا بَيِّنَاتٍ تَعْرِفُ فِي وُجُوهِ الَّذِينَ كَفَرُوا الْمُنْكَرَ ۖ يَكَادُونَ يَسْطُونَ بِالَّذِينَ يَتْلُونَ عَلَيْهِمْ آيَاتِنَا ۗ قُلْ أَفَأُنَبِّئُكُمْ بِشَرٍّ مِنْ ذَٰلِكُمُ ۗ النَّارُ وَعَدَهَا اللَّهُ الَّذِينَ كَفَرُوا ۖ وَبِئْسَ الْمَصِيرُ', 22, 72, 17, 68, 340, 'و هنگامی که آیات روشن ما بر آنان خوانده می‌شود، در چهره کافران آثار انکار مشاهده می‌کنی، آنچنان که نزدیک است برخیزند و با مشت به کسانی که آیات ما را بر آنها میخوانند حمله کنند! بگو: «آیا شما را به بدتر از این خبر دهم؟ همان آتش سوزنده = دوزخ‌ که خدا به کافران وعده داده؛ و بد سرانجامی است!»'),
(2668, 2668, 'يَا أَيُّهَا النَّاسُ ضُرِبَ مَثَلٌ فَاسْتَمِعُوا لَهُ ۚ إِنَّ الَّذِينَ تَدْعُونَ مِنْ دُونِ اللَّهِ لَنْ يَخْلُقُوا ذُبَابًا وَلَوِ اجْتَمَعُوا لَهُ ۖ وَإِنْ يَسْلُبْهُمُ الذُّبَابُ شَيْئًا لَا يَسْتَنْقِذُوهُ مِنْهُ ۚ ضَعُفَ الطَّالِبُ وَالْمَطْلُوبُ', 22, 73, 17, 68, 341, 'ای مردم! مثلی زده شده است، به آن گوش فرا دهید: کسانی را که غیر از خدا می‌خوانید، هرگز نمی‌توانند مگسی بیافرینند، هر چند برای این کار دست به دست هم دهند! و هرگاه مگس چیزی از آنها برباید، نمی‌توانند آن را باز پس گیرند! هم این طلب‌کنندگان ناتوانند، و هم آن مطلوبان (هم این عابدان، و هم آن معبودان)!'),
(2669, 2669, 'مَا قَدَرُوا اللَّهَ حَقَّ قَدْرِهِ ۗ إِنَّ اللَّهَ لَقَوِيٌّ عَزِيزٌ', 22, 74, 17, 68, 341, 'خدا را آن گونه که باید بشناسند نشناختند؛ خداوند قوّی و شکست‌ناپذیر است!'),
(2670, 2670, 'اللَّهُ يَصْطَفِي مِنَ الْمَلَائِكَةِ رُسُلًا وَمِنَ النَّاسِ ۚ إِنَّ اللَّهَ سَمِيعٌ بَصِيرٌ', 22, 75, 17, 68, 341, 'خداوند از فرشتگان رسولانی برمی‌گزیند، و همچنین از مردم؛ خداوند شنوا و بیناست!'),
(2671, 2671, 'يَعْلَمُ مَا بَيْنَ أَيْدِيهِمْ وَمَا خَلْفَهُمْ ۗ وَإِلَى اللَّهِ تُرْجَعُ الْأُمُورُ', 22, 76, 17, 68, 341, 'آنچه را در پیش روی آنها و پشت سر آنهاست می‌داند؛ و همه امور بسوی خدا بازمی‌گردد.'),
(2672, 2672, 'يَا أَيُّهَا الَّذِينَ آمَنُوا ارْكَعُوا وَاسْجُدُوا وَاعْبُدُوا رَبَّكُمْ وَافْعَلُوا الْخَيْرَ لَعَلَّكُمْ تُفْلِحُونَ ۩', 22, 77, 17, 68, 341, 'ای کسانی که ایمان آورده‌اید! رکوع کنید، و سجود بجا آورید، و پروردگارتان را عبادت کنید، و کار نیک انجام دهید، شاید رستگار شوید!'),
(2673, 2673, 'وَجَاهِدُوا فِي اللَّهِ حَقَّ جِهَادِهِ ۚ هُوَ اجْتَبَاكُمْ وَمَا جَعَلَ عَلَيْكُمْ فِي الدِّينِ مِنْ حَرَجٍ ۚ مِلَّةَ أَبِيكُمْ إِبْرَاهِيمَ ۚ هُوَ سَمَّاكُمُ الْمُسْلِمِينَ مِنْ قَبْلُ وَفِي هَٰذَا لِيَكُونَ الرَّسُولُ شَهِيدًا عَلَيْكُمْ وَتَكُونُوا شُهَدَاءَ عَلَى النَّاسِ ۚ فَأَقِيمُوا الصَّلَاةَ وَآتُوا الزَّكَاةَ وَاعْتَصِمُوا بِاللَّهِ هُوَ مَوْلَاكُمْ ۖ فَنِعْمَ الْمَوْلَىٰ وَنِعْمَ النَّصِيرُ', 22, 78, 17, 68, 341, 'و در راه خدا جهاد کنید، و حقّ جهادش را ادا نمایید! او شما را برگزید، و در دین (اسلام) کار سنگین و سختی بر شما قرار ندارد؛ از آیین پدرتان ابراهیم پیروی کنید؛ خداوند شما را در کتابهای پیشین و در این کتاب آسمانی «مسلمان» نامید، تا پیامبر گواه بر شما باشد، و شما گواهان بر مردم! پس نماز را برپا دارید، و زکات را بدهید، و به خدا تمسّک جویید، که او مولا و سرپرست شماست! چه مولای خوب، و چه یاور شایسته‌ای!'),
(2674, 2674, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ قَدْ أَفْلَحَ الْمُؤْمِنُونَ', 23, 1, 18, 69, 342, 'مؤمنان رستگار شدند؛'),
(2675, 2675, 'الَّذِينَ هُمْ فِي صَلَاتِهِمْ خَاشِعُونَ', 23, 2, 18, 69, 342, 'آنها که در نمازشان خشوع دارند؛'),
(2676, 2676, 'وَالَّذِينَ هُمْ عَنِ اللَّغْوِ مُعْرِضُونَ', 23, 3, 18, 69, 342, 'و آنها که از لغو و بیهودگی روی‌گردانند؛'),
(2677, 2677, 'وَالَّذِينَ هُمْ لِلزَّكَاةِ فَاعِلُونَ', 23, 4, 18, 69, 342, 'و آنها که زکات را انجام می‌دهند؛'),
(2678, 2678, 'وَالَّذِينَ هُمْ لِفُرُوجِهِمْ حَافِظُونَ', 23, 5, 18, 69, 342, 'و آنها که دامان خود را (از آلوده‌شدن به بی‌عفتی) حفظ می‌کنند؛'),
(2679, 2679, 'إِلَّا عَلَىٰ أَزْوَاجِهِمْ أَوْ مَا مَلَكَتْ أَيْمَانُهُمْ فَإِنَّهُمْ غَيْرُ مَلُومِينَ', 23, 6, 18, 69, 342, 'تنها آمیزش جنسی با همسران و کنیزانشان دارند، که در بهره‌گیری از آنان ملامت نمی‌شوند؛'),
(2680, 2680, 'فَمَنِ ابْتَغَىٰ وَرَاءَ ذَٰلِكَ فَأُولَٰئِكَ هُمُ الْعَادُونَ', 23, 7, 18, 69, 342, 'و کسانی که غیر از این طریق را طلب کنند، تجاوزگرند!'),
(2681, 2681, 'وَالَّذِينَ هُمْ لِأَمَانَاتِهِمْ وَعَهْدِهِمْ رَاعُونَ', 23, 8, 18, 69, 342, 'و آنها که امانتها و عهد خود را رعایت می‌کنند؛'),
(2682, 2682, 'وَالَّذِينَ هُمْ عَلَىٰ صَلَوَاتِهِمْ يُحَافِظُونَ', 23, 9, 18, 69, 342, 'و آنها که بر نمازهایشان مواظبت می‌نمایند؛'),
(2683, 2683, 'أُولَٰئِكَ هُمُ الْوَارِثُونَ', 23, 10, 18, 69, 342, '(آری،) آنها وارثانند!'),
(2684, 2684, 'الَّذِينَ يَرِثُونَ الْفِرْدَوْسَ هُمْ فِيهَا خَالِدُونَ', 23, 11, 18, 69, 342, '(وارثانی) که بهشت برین را ارث می‌برند، و جاودانه در آن خواهند ماند!'),
(2685, 2685, 'وَلَقَدْ خَلَقْنَا الْإِنْسَانَ مِنْ سُلَالَةٍ مِنْ طِينٍ', 23, 12, 18, 69, 342, 'و ما انسان را از عصاره‌ای از گِل آفریدیم؛'),
(2686, 2686, 'ثُمَّ جَعَلْنَاهُ نُطْفَةً فِي قَرَارٍ مَكِينٍ', 23, 13, 18, 69, 342, 'سپس او را نطفه‌ای در قرارگاه مطمئن = رحم‌ قرار دادیم؛'),
(2687, 2687, 'ثُمَّ خَلَقْنَا النُّطْفَةَ عَلَقَةً فَخَلَقْنَا الْعَلَقَةَ مُضْغَةً فَخَلَقْنَا الْمُضْغَةَ عِظَامًا فَكَسَوْنَا الْعِظَامَ لَحْمًا ثُمَّ أَنْشَأْنَاهُ خَلْقًا آخَرَ ۚ فَتَبَارَكَ اللَّهُ أَحْسَنُ الْخَالِقِينَ', 23, 14, 18, 69, 342, 'سپس نطفه را بصورت علقه = خون بسته‌، و علقه را بصورت مضغه = چیزی شبیه گوشت جویده شده‌، و مضغه را بصورت استخوانهایی درآوردیم؛ و بر استخوانها گوشت پوشاندیم؛ سپس آن را آفرینش تازه‌ای دادیم؛ پس بزرگ است خدایی که بهترین آفرینندگان است!'),
(2688, 2688, 'ثُمَّ إِنَّكُمْ بَعْدَ ذَٰلِكَ لَمَيِّتُونَ', 23, 15, 18, 69, 342, 'سپس شما بعد از آن می‌میرید؛'),
(2689, 2689, 'ثُمَّ إِنَّكُمْ يَوْمَ الْقِيَامَةِ تُبْعَثُونَ', 23, 16, 18, 69, 342, 'سپس در روز قیامت برانگیخته می‌شوید!'),
(2690, 2690, 'وَلَقَدْ خَلَقْنَا فَوْقَكُمْ سَبْعَ طَرَائِقَ وَمَا كُنَّا عَنِ الْخَلْقِ غَافِلِينَ', 23, 17, 18, 69, 342, 'ما بر بالای سر شما هفت راه = طبقات هفتگانه آسمان‌ قرار دادیم؛ و ما (هرگز) از خلق (خود) غافل نبوده‌ایم!'),
(2691, 2691, 'وَأَنْزَلْنَا مِنَ السَّمَاءِ مَاءً بِقَدَرٍ فَأَسْكَنَّاهُ فِي الْأَرْضِ ۖ وَإِنَّا عَلَىٰ ذَهَابٍ بِهِ لَقَادِرُونَ', 23, 18, 18, 69, 343, 'و از آسمان، آبی به اندازه معیّن نازل کردیم؛ و آن را در زمین (در جایگاه مخصوصی) ساکن نمودیم؛ و ما بر از بین بردن آن کاملاً قادریم!'),
(2692, 2692, 'فَأَنْشَأْنَا لَكُمْ بِهِ جَنَّاتٍ مِنْ نَخِيلٍ وَأَعْنَابٍ لَكُمْ فِيهَا فَوَاكِهُ كَثِيرَةٌ وَمِنْهَا تَأْكُلُونَ', 23, 19, 18, 69, 343, 'سپس بوسیله آن باغهایی از درختان نخل و انگور برای شما ایجاد کردیم؛ باغهایی که در آن میوه‌های بسیار است؛ و از آن میخورید!'),
(2693, 2693, 'وَشَجَرَةً تَخْرُجُ مِنْ طُورِ سَيْنَاءَ تَنْبُتُ بِالدُّهْنِ وَصِبْغٍ لِلْآكِلِينَ', 23, 20, 18, 69, 343, 'و (نیز) درختی را که از طور سینا می‌روید = درخت زیتون‌، و از آن روغن و «نان خورش» برای خورندگان فراهم می‌گردد (آفریدیم)!'),
(2694, 2694, 'وَإِنَّ لَكُمْ فِي الْأَنْعَامِ لَعِبْرَةً ۖ نُسْقِيكُمْ مِمَّا فِي بُطُونِهَا وَلَكُمْ فِيهَا مَنَافِعُ كَثِيرَةٌ وَمِنْهَا تَأْكُلُونَ', 23, 21, 18, 69, 343, 'و برای شما در چهارپایان عبرتی است؛ از آنچه در درون آنهاست = از شیر شما را سیراب می‌کنیم؛ و برای شما در آنها منافع بسیاری است؛ و از گوشت آنها می‌خورید؛'),
(2695, 2695, 'وَعَلَيْهَا وَعَلَى الْفُلْكِ تُحْمَلُونَ', 23, 22, 18, 69, 343, 'و بر آنها و بر کشتیها سوار می‌شوید!'),
(2696, 2696, 'وَلَقَدْ أَرْسَلْنَا نُوحًا إِلَىٰ قَوْمِهِ فَقَالَ يَا قَوْمِ اعْبُدُوا اللَّهَ مَا لَكُمْ مِنْ إِلَٰهٍ غَيْرُهُ ۖ أَفَلَا تَتَّقُونَ', 23, 23, 18, 69, 343, 'و ما نوح را بسوی قومش فرستادیم؛ او به آنها گفت: «ای قوم من! خداوند یکتا را بپرستید، که جز او معبودی برای شما نیست! آیا (از پرستش بتها) پرهیز نمی‌کنید؟!'),
(2697, 2697, 'فَقَالَ الْمَلَأُ الَّذِينَ كَفَرُوا مِنْ قَوْمِهِ مَا هَٰذَا إِلَّا بَشَرٌ مِثْلُكُمْ يُرِيدُ أَنْ يَتَفَضَّلَ عَلَيْكُمْ وَلَوْ شَاءَ اللَّهُ لَأَنْزَلَ مَلَائِكَةً مَا سَمِعْنَا بِهَٰذَا فِي آبَائِنَا الْأَوَّلِينَ', 23, 24, 18, 69, 343, 'جمعیّت اشرافی (و مغرور) از قوم نوح که کافر بودند گفتند: «این مرد جز بشری همچون شما نیست، که می‌خواهد بر شما برتری جوید! اگر خدا می‌خواست (پیامبری بفرستد) فرشتگانی نازل می‌کرد؛ ما چنین چیزی را هرگز در نیاکان خود نشنیده‌ایم!'),
(2698, 2698, 'إِنْ هُوَ إِلَّا رَجُلٌ بِهِ جِنَّةٌ فَتَرَبَّصُوا بِهِ حَتَّىٰ حِينٍ', 23, 25, 18, 69, 343, 'او فقط مردی است که به نوعی جنون مبتلاست! پس مدّتی درباره او صبر کنید (تا مرگش فرا رسد، یا از این بیماری رهایی یابد!)»'),
(2699, 2699, 'قَالَ رَبِّ انْصُرْنِي بِمَا كَذَّبُونِ', 23, 26, 18, 69, 343, '(نوح) گفت: «پروردگارا! مرا در برابر تکذیبهای آنان یاری کن!»'),
(2700, 2700, 'فَأَوْحَيْنَا إِلَيْهِ أَنِ اصْنَعِ الْفُلْكَ بِأَعْيُنِنَا وَوَحْيِنَا فَإِذَا جَاءَ أَمْرُنَا وَفَارَ التَّنُّورُ ۙ فَاسْلُكْ فِيهَا مِنْ كُلٍّ زَوْجَيْنِ اثْنَيْنِ وَأَهْلَكَ إِلَّا مَنْ سَبَقَ عَلَيْهِ الْقَوْلُ مِنْهُمْ ۖ وَلَا تُخَاطِبْنِي فِي الَّذِينَ ظَلَمُوا ۖ إِنَّهُمْ مُغْرَقُونَ', 23, 27, 18, 69, 343, 'ما به نوح وحی کردیم که: «کشتی را در حضور ما، و مطابق وحی ما بساز. و هنگامی که فرمان ما (برای غرق آنان) فرا رسد، و آب از تنور بجوشد (که نشانه فرا رسیدن طوفان است)، از هر یک از انواع حیوانات یک جفت در کشتی سوار کن؛ و همچنین خانواده‌ات را، مگر آنانی که قبلاً وعده هلاکشان داده شده = همسر و فرزند کافرت‌؛ و دیگر درباره ستمگران با من سخن مگو، که آنان همگی هلاک خواهند شد.'),
(2701, 2701, 'فَإِذَا اسْتَوَيْتَ أَنْتَ وَمَنْ مَعَكَ عَلَى الْفُلْكِ فَقُلِ الْحَمْدُ لِلَّهِ الَّذِي نَجَّانَا مِنَ الْقَوْمِ الظَّالِمِينَ', 23, 28, 18, 69, 344, 'و هنگامی که تو و همه کسانی که با تو هستند بر کشتی سوار شدید، بگو: «ستایش برای خدایی است که ما را از قوم ستمگر نجات بخشید!»'),
(2702, 2702, 'وَقُلْ رَبِّ أَنْزِلْنِي مُنْزَلًا مُبَارَكًا وَأَنْتَ خَيْرُ الْمُنْزِلِينَ', 23, 29, 18, 69, 344, 'و بگو: «پروردگارا! ما را در منزلگاهی پربرکت فرود آر، و تو بهترین فرودآورندگانی!»'),
(2703, 2703, 'إِنَّ فِي ذَٰلِكَ لَآيَاتٍ وَإِنْ كُنَّا لَمُبْتَلِينَ', 23, 30, 18, 69, 344, '(آری،) در این ماجرا (برای صاحبان عقل و اندیشه) آیات و نشانه‌هایی است؛ و ما مسلّماً همگان را آزمایش می‌کنیم!'),
(2704, 2704, 'ثُمَّ أَنْشَأْنَا مِنْ بَعْدِهِمْ قَرْنًا آخَرِينَ', 23, 31, 18, 69, 344, 'سپس جمعیّت دیگری را بعد از آنها به وجود آوردیم.'),
(2705, 2705, 'فَأَرْسَلْنَا فِيهِمْ رَسُولًا مِنْهُمْ أَنِ اعْبُدُوا اللَّهَ مَا لَكُمْ مِنْ إِلَٰهٍ غَيْرُهُ ۖ أَفَلَا تَتَّقُونَ', 23, 32, 18, 69, 344, 'و در میان آنان رسولی از خودشان فرستادیم که: «خدا را بپرستید؛ جز او معبودی برای شما نیست؛ آیا (با این همه، از شرک و بت پرستی) پرهیز نمی‌کنید؟!»'),
(2706, 2706, 'وَقَالَ الْمَلَأُ مِنْ قَوْمِهِ الَّذِينَ كَفَرُوا وَكَذَّبُوا بِلِقَاءِ الْآخِرَةِ وَأَتْرَفْنَاهُمْ فِي الْحَيَاةِ الدُّنْيَا مَا هَٰذَا إِلَّا بَشَرٌ مِثْلُكُمْ يَأْكُلُ مِمَّا تَأْكُلُونَ مِنْهُ وَيَشْرَبُ مِمَّا تَشْرَبُونَ', 23, 33, 18, 69, 344, 'ولی اشرافیان (خودخواه) از قوم او که کافر بودند، و دیدار آخرت را تکذیب می‌کردند، و در زندگی دنیا به آنان ناز و نعمت داده بودیم، گفتند: «این بشری است مثل شما؛ از آنچه می‌خورید می‌خورد؛ و از آنچه می‌نوشید می‌نوشد! (پس چگونه می‌تواند پیامبر باشد؟!)'),
(2707, 2707, 'وَلَئِنْ أَطَعْتُمْ بَشَرًا مِثْلَكُمْ إِنَّكُمْ إِذًا لَخَاسِرُونَ', 23, 34, 18, 69, 344, 'و اگر از بشری همانند خودتان اطاعت کنید، مسلّماً زیانکارید.'),
(2708, 2708, 'أَيَعِدُكُمْ أَنَّكُمْ إِذَا مِتُّمْ وَكُنْتُمْ تُرَابًا وَعِظَامًا أَنَّكُمْ مُخْرَجُونَ', 23, 35, 18, 69, 344, 'آیا او به شما وعده می‌دهد هنگامی که مردید و خاک و استخوانهایی (پوسیده) شدید، بار دیگر (از قبرها) بیرون آورده می‌شوید؟!'),
(2709, 2709, ' هَيْهَاتَ هَيْهَاتَ لِمَا تُوعَدُونَ', 23, 36, 18, 69, 344, 'هیهات، هیهات از این وعده‌هایی که به شما داده می‌شود!'),
(2710, 2710, 'إِنْ هِيَ إِلَّا حَيَاتُنَا الدُّنْيَا نَمُوتُ وَنَحْيَا وَمَا نَحْنُ بِمَبْعُوثِينَ', 23, 37, 18, 69, 344, 'مسلّماً غیر از این زندگی دنیای ما، چیزی در کار نیست؛ پیوسته گروهی از ما می‌میریم، و نسل دیگری جای ما را می‌گیرد؛ و ما هرگز برانگیخته نخواهیم شد!'),
(2711, 2711, 'إِنْ هُوَ إِلَّا رَجُلٌ افْتَرَىٰ عَلَى اللَّهِ كَذِبًا وَمَا نَحْنُ لَهُ بِمُؤْمِنِينَ', 23, 38, 18, 69, 344, 'او فقط مردی دروغگوست که بر خدا افترا بسته؛ و ما هرگز به او ایمان نخواهیم آورد!»'),
(2712, 2712, 'قَالَ رَبِّ انْصُرْنِي بِمَا كَذَّبُونِ', 23, 39, 18, 69, 344, '(پیامبرشان) گفت: «پروردگارا! مرا در برابر تکذیبهای آنان یاری کن!»'),
(2713, 2713, 'قَالَ عَمَّا قَلِيلٍ لَيُصْبِحُنَّ نَادِمِينَ', 23, 40, 18, 69, 344, '(خداوند) فرمود: «بزودی از کار خود پشیمان خواهند شد! (امّا زمانی که دیگر سودی به حالشان ندارد.)»'),
(2714, 2714, 'فَأَخَذَتْهُمُ الصَّيْحَةُ بِالْحَقِّ فَجَعَلْنَاهُمْ غُثَاءً ۚ فَبُعْدًا لِلْقَوْمِ الظَّالِمِينَ', 23, 41, 18, 69, 344, 'سرانجام صیحه آسمانی آنها را بحق فرو گرفت؛ و ما آنها را همچون خاشاکی بر سیلاب قرار دادیم؛ دور باد قوم ستمگر (از رحمت خدا)!'),
(2715, 2715, 'ثُمَّ أَنْشَأْنَا مِنْ بَعْدِهِمْ قُرُونًا آخَرِينَ', 23, 42, 18, 69, 344, 'سپس اقوام دیگری را پس از آنها پدید آوردیم.'),
(2716, 2716, 'مَا تَسْبِقُ مِنْ أُمَّةٍ أَجَلَهَا وَمَا يَسْتَأْخِرُونَ', 23, 43, 18, 69, 345, 'هیچ امتی بر اجل و سر رسید حتمی خود پیشی نمی‌گیرد، و از آن تأخیر نیز نمی‌کند.'),
(2717, 2717, 'ثُمَّ أَرْسَلْنَا رُسُلَنَا تَتْرَىٰ ۖ كُلَّ مَا جَاءَ أُمَّةً رَسُولُهَا كَذَّبُوهُ ۚ فَأَتْبَعْنَا بَعْضَهُمْ بَعْضًا وَجَعَلْنَاهُمْ أَحَادِيثَ ۚ فَبُعْدًا لِقَوْمٍ لَا يُؤْمِنُونَ', 23, 44, 18, 69, 345, 'سپس رسولان خود را یکی پس از دیگری فرستادیم؛ هر زمان رسولی برای (هدایت) قومی می‌آمد، او را تکذیب می‌کردند؛ ولی ما این امّتهای سرکش را یکی پس از دیگری هلاک نمودیم، و آنها را احادیثی قرار دادیم (چنان محو شدند که تنها نام و گفتگویی از آنان باقی ماند.) دور باد (از رحمت خدا) قومی که ایمان نمی‌آورند!'),
(2718, 2718, 'ثُمَّ أَرْسَلْنَا مُوسَىٰ وَأَخَاهُ هَارُونَ بِآيَاتِنَا وَسُلْطَانٍ مُبِينٍ', 23, 45, 18, 69, 345, 'سپس موسی و برادرش هارون را با آیات خود و دلیلی روشن فرستادیم...'),
(2719, 2719, 'إِلَىٰ فِرْعَوْنَ وَمَلَئِهِ فَاسْتَكْبَرُوا وَكَانُوا قَوْمًا عَالِينَ', 23, 46, 18, 69, 345, 'بسوی فرعون و اطرافیان اشرافی او؛ امّا آنها تکبّر کردند، و آنها مردمی برتری‌جوی بودند.'),
(2720, 2720, 'فَقَالُوا أَنُؤْمِنُ لِبَشَرَيْنِ مِثْلِنَا وَقَوْمُهُمَا لَنَا عَابِدُونَ', 23, 47, 18, 69, 345, 'آنها گفتند: «آیا ما به دو انسان همانند خودمان ایمان بیاوریم، در حالی که قوم آنها بردگان ما هستند؟!»'),
(2721, 2721, 'فَكَذَّبُوهُمَا فَكَانُوا مِنَ الْمُهْلَكِينَ', 23, 48, 18, 69, 345, '(آری،) آنها این دو را تکذیب کردند؛ و سرانجام همگی هلاک شدند.'),
(2722, 2722, 'وَلَقَدْ آتَيْنَا مُوسَى الْكِتَابَ لَعَلَّهُمْ يَهْتَدُونَ', 23, 49, 18, 69, 345, 'و ما به موسی کتاب (آسمانی) دادیم؛ شاید آنان = بنی اسرائیل‌ هدایت شوند.'),
(2723, 2723, 'وَجَعَلْنَا ابْنَ مَرْيَمَ وَأُمَّهُ آيَةً وَآوَيْنَاهُمَا إِلَىٰ رَبْوَةٍ ذَاتِ قَرَارٍ وَمَعِينٍ', 23, 50, 18, 69, 345, 'و ما فرزند مریم = عیسی‌ و مادرش را آیت و نشانه‌ای قرار دادیم؛ و آنها را در سرزمین مرتفعی که دارای امنیّت و آب جاری بود جای دادیم.'),
(2724, 2724, 'يَا أَيُّهَا الرُّسُلُ كُلُوا مِنَ الطَّيِّبَاتِ وَاعْمَلُوا صَالِحًا ۖ إِنِّي بِمَا تَعْمَلُونَ عَلِيمٌ', 23, 51, 18, 69, 345, 'ای پیامبران! از غذاهای پاکیزه بخورید، و عمل صالح انجام دهید، که من به آنچه انجام می‌دهید آگاهم.'),
(2725, 2725, 'وَإِنَّ هَٰذِهِ أُمَّتُكُمْ أُمَّةً وَاحِدَةً وَأَنَا رَبُّكُمْ فَاتَّقُونِ', 23, 52, 18, 69, 345, 'و این امّت شما امّت واحدی است؛ و من پروردگار شما هستم؛ پس، از مخالفت فرمان من بپرهیزید!'),
(2726, 2726, 'فَتَقَطَّعُوا أَمْرَهُمْ بَيْنَهُمْ زُبُرًا ۖ كُلُّ حِزْبٍ بِمَا لَدَيْهِمْ فَرِحُونَ', 23, 53, 18, 69, 345, 'امّا آنها کارهای خود را در میان خویش به پراکندگی کشاندند، و هر گروهی به راهی رفتند؛ (و عجب اینکه) هر گروه به آنچه نزد خود دارند خوشحالند!'),
(2727, 2727, 'فَذَرْهُمْ فِي غَمْرَتِهِمْ حَتَّىٰ حِينٍ', 23, 54, 18, 69, 345, 'آنها را در جهل و غفلتشان بگذار تا زمانی (که مرگشان فرا رسد یا گرفتار عذاب الهی شوند).'),
(2728, 2728, 'أَيَحْسَبُونَ أَنَّمَا نُمِدُّهُمْ بِهِ مِنْ مَالٍ وَبَنِينَ', 23, 55, 18, 69, 345, 'آنها گمان می‌کنند اموال و فرزندانی که بعنوان کمک به آنان می‌دهیم...'),
(2729, 2729, 'نُسَارِعُ لَهُمْ فِي الْخَيْرَاتِ ۚ بَلْ لَا يَشْعُرُونَ', 23, 56, 18, 69, 345, 'برای این است که درهای خیرات را با شتاب به روی آنها بگشاییم!! (چنین نیست) بلکه آنها نمی‌فهمند (که این وسیله امتحانشان است).'),
(2730, 2730, 'إِنَّ الَّذِينَ هُمْ مِنْ خَشْيَةِ رَبِّهِمْ مُشْفِقُونَ', 23, 57, 18, 69, 345, 'مسلّماً کسانی که از خوف پروردگارشان بیمناکند،'),
(2731, 2731, 'وَالَّذِينَ هُمْ بِآيَاتِ رَبِّهِمْ يُؤْمِنُونَ', 23, 58, 18, 69, 345, 'و آنان که به آیات پروردگارشان ایمان می‌آورند،'),
(2732, 2732, 'وَالَّذِينَ هُمْ بِرَبِّهِمْ لَا يُشْرِكُونَ', 23, 59, 18, 69, 345, 'و آنها که به پروردگارشان شرک نمی‌ورزند،'),
(2733, 2733, 'وَالَّذِينَ يُؤْتُونَ مَا آتَوْا وَقُلُوبُهُمْ وَجِلَةٌ أَنَّهُمْ إِلَىٰ رَبِّهِمْ رَاجِعُونَ', 23, 60, 18, 69, 346, 'و آنها که نهایت کوشش را در انجام طاعات به خرج می‌دهند و با این حال، دلهایشان هراسناک است از اینکه سرانجام بسوی پروردگارشان بازمی‌گردند،'),
(2734, 2734, 'أُولَٰئِكَ يُسَارِعُونَ فِي الْخَيْرَاتِ وَهُمْ لَهَا سَابِقُونَ', 23, 61, 18, 69, 346, '(آری) چنین کسانی در خیرات سرعت می‌کنند و از دیگران پیشی می‌گیرند (و مشمول عنایات ما هستند).'),
(2735, 2735, 'وَلَا نُكَلِّفُ نَفْسًا إِلَّا وُسْعَهَا ۖ وَلَدَيْنَا كِتَابٌ يَنْطِقُ بِالْحَقِّ ۚ وَهُمْ لَا يُظْلَمُونَ', 23, 62, 18, 69, 346, 'و ما هیچ کس را جز به اندازه تواناییش تکلیف نمی‌کنیم؛ و نزد ما کتابی است که (تمام اعمال بندگان را ثبت کرده و) بحق سخن می‌گوید؛ و به آنان هیچ ستمی نمی‌شود.'),
(2736, 2736, 'بَلْ قُلُوبُهُمْ فِي غَمْرَةٍ مِنْ هَٰذَا وَلَهُمْ أَعْمَالٌ مِنْ دُونِ ذَٰلِكَ هُمْ لَهَا عَامِلُونَ', 23, 63, 18, 69, 346, 'ولی دلهای آنها از این نامه اعمال (و روز حساب و آیات قرآن) در بی‌خبری فرورفته؛ و اعمال (زشت) دیگری جز این دارند که پیوسته آن را انجام می‌دهند...'),
(2737, 2737, 'حَتَّىٰ إِذَا أَخَذْنَا مُتْرَفِيهِمْ بِالْعَذَابِ إِذَا هُمْ يَجْأَرُونَ', 23, 64, 18, 69, 346, 'تا زمانی که متنعّمان مغرور آنها را در چنگال عذاب گرفتار سازیم؛ در این هنگام، ناله‌های دردناک و استغاثه‌آمیز سرمی‌دهند!'),
(2738, 2738, 'لَا تَجْأَرُوا الْيَوْمَ ۖ إِنَّكُمْ مِنَّا لَا تُنْصَرُونَ', 23, 65, 18, 69, 346, '(امّا به آنها گفته می‌شود:) امروز فریاد نکنید، زیرا از سوی ما یاری نخواهید شد!'),
(2739, 2739, 'قَدْ كَانَتْ آيَاتِي تُتْلَىٰ عَلَيْكُمْ فَكُنْتُمْ عَلَىٰ أَعْقَابِكُمْ تَنْكِصُونَ', 23, 66, 18, 69, 346, '(آیا فراموش کرده‌اید که) در گذشته آیات من پیوسته بر شما خوانده می‌شد؛ امّا شما اعراض کرده به عقب بازمی‌گشتید؟!'),
(2740, 2740, 'مُسْتَكْبِرِينَ بِهِ سَامِرًا تَهْجُرُونَ', 23, 67, 18, 69, 346, 'در حالی که در برابر او =پیامبر استکبار می‌کردید، و شبها در جلسات خود به بدگویی می‌پرداختید؟!'),
(2741, 2741, 'أَفَلَمْ يَدَّبَّرُوا الْقَوْلَ أَمْ جَاءَهُمْ مَا لَمْ يَأْتِ آبَاءَهُمُ الْأَوَّلِينَ', 23, 68, 18, 69, 346, 'آیا آنها در این گفتار نیندیشیدند، یا اینکه چیزی برای آنان آمده که برای نیاکانشان نیامده است؟!'),
(2742, 2742, 'أَمْ لَمْ يَعْرِفُوا رَسُولَهُمْ فَهُمْ لَهُ مُنْكِرُونَ', 23, 69, 18, 69, 346, 'یا اینکه پیامبرشان را نشناختند (و از سوابق او آگاه نیستند)، از این رو او را انکار می‌کنند؟!'),
(2743, 2743, 'أَمْ يَقُولُونَ بِهِ جِنَّةٌ ۚ بَلْ جَاءَهُمْ بِالْحَقِّ وَأَكْثَرُهُمْ لِلْحَقِّ كَارِهُونَ', 23, 70, 18, 69, 346, 'یا می‌گویند او دیوانه است؟! ولی او حق را برای آنان آورده؛ امّا بیشترشان از حق کراهت دارند (و گریزانند).'),
(2744, 2744, 'وَلَوِ اتَّبَعَ الْحَقُّ أَهْوَاءَهُمْ لَفَسَدَتِ السَّمَاوَاتُ وَالْأَرْضُ وَمَنْ فِيهِنَّ ۚ بَلْ أَتَيْنَاهُمْ بِذِكْرِهِمْ فَهُمْ عَنْ ذِكْرِهِمْ مُعْرِضُونَ', 23, 71, 18, 69, 346, 'و اگر حق از هوسهای آنها پیروی کند، آسمانها و زمین و همه کسانی که در آنها هستند تباه می‌شوند! ولی ما قرآنی به آنها دادیم که مایه یادآوری (و عزّت و شرف) برای آنهاست، امّا آنان از (آنچه مایه) یادآوریشان (است) رویگردانند!'),
(2745, 2745, 'أَمْ تَسْأَلُهُمْ خَرْجًا فَخَرَاجُ رَبِّكَ خَيْرٌ ۖ وَهُوَ خَيْرُ الرَّازِقِينَ', 23, 72, 18, 69, 346, 'یا اینکه تو از آنها مزد و هزینه‌ای (در برابر دعوتت) می‌خواهی؟ با اینکه مزد پروردگارت بهتر، و او بهترین روزی دهندگان است!'),
(2746, 2746, 'وَإِنَّكَ لَتَدْعُوهُمْ إِلَىٰ صِرَاطٍ مُسْتَقِيمٍ', 23, 73, 18, 69, 346, 'بطور قطع و یقین، تو آنان را به راه راست دعوت می‌کنی.'),
(2747, 2747, 'وَإِنَّ الَّذِينَ لَا يُؤْمِنُونَ بِالْآخِرَةِ عَنِ الصِّرَاطِ لَنَاكِبُونَ', 23, 74, 18, 69, 346, 'امّا کسانی که به آخرت ایمان ندارند از این راه منحرفند!'),
(2748, 2748, ' وَلَوْ رَحِمْنَاهُمْ وَكَشَفْنَا مَا بِهِمْ مِنْ ضُرٍّ لَلَجُّوا فِي طُغْيَانِهِمْ يَعْمَهُونَ', 23, 75, 18, 70, 347, 'و اگر به آنان رحم کنید و گرفتاریها و مشکلاتشان را برطرف سازیم، (نه تنها بیدار نمی‌شوند، بلکه) در طغیانشان لجاجت می‌ورزند و (در این وادی) سرگردان می‌مانند!'),
(2749, 2749, 'وَلَقَدْ أَخَذْنَاهُمْ بِالْعَذَابِ فَمَا اسْتَكَانُوا لِرَبِّهِمْ وَمَا يَتَضَرَّعُونَ', 23, 76, 18, 70, 347, 'ما آنها را به عذاب و بلا گرفتار ساختیم (تا بیدار شوند)، امّا آنان نه در برابر پروردگارشان تواضع کردند، و نه به درگاهش تضرّع می‌کنند!'),
(2750, 2750, 'حَتَّىٰ إِذَا فَتَحْنَا عَلَيْهِمْ بَابًا ذَا عَذَابٍ شَدِيدٍ إِذَا هُمْ فِيهِ مُبْلِسُونَ', 23, 77, 18, 70, 347, '(این وضع همچنان ادامه می‌یابد) تا زمانی که دری از عذاب شدید به روی آنان بگشاییم، (و چنان گرفتار می‌شوند که) ناگهان بکلّی مأیوس گردند.'),
(2751, 2751, 'وَهُوَ الَّذِي أَنْشَأَ لَكُمُ السَّمْعَ وَالْأَبْصَارَ وَالْأَفْئِدَةَ ۚ قَلِيلًا مَا تَشْكُرُونَ', 23, 78, 18, 70, 347, 'و او کسی است که برای شما گوش و چشم و قلب =عقل‌ ایجاد کرد؛ امّا کمتر شکر او را بجا می‌آورید.'),
(2752, 2752, 'وَهُوَ الَّذِي ذَرَأَكُمْ فِي الْأَرْضِ وَإِلَيْهِ تُحْشَرُونَ', 23, 79, 18, 70, 347, 'و او کسی است که شما را در زمین آفرید؛ و به سوی او محشور می‌شوید!'),
(2753, 2753, 'وَهُوَ الَّذِي يُحْيِي وَيُمِيتُ وَلَهُ اخْتِلَافُ اللَّيْلِ وَالنَّهَارِ ۚ أَفَلَا تَعْقِلُونَ', 23, 80, 18, 70, 347, 'و او کسی است که زنده می‌کند و می‌میراند؛ و رفت و آمد شب و روز از آن اوست؛ آیا اندیشه نمی‌کنید؟!'),
(2754, 2754, 'بَلْ قَالُوا مِثْلَ مَا قَالَ الْأَوَّلُونَ', 23, 81, 18, 70, 347, '(نه،) بلکه آنان نیز مثل آنچه پیشینیان گفته بودند گفتند.'),
(2755, 2755, 'قَالُوا أَإِذَا مِتْنَا وَكُنَّا تُرَابًا وَعِظَامًا أَإِنَّا لَمَبْعُوثُونَ', 23, 82, 18, 70, 347, 'آنها گفتند: «آیا هنگامی که مردیم و خاک و استخوانهایی (پوسیده) شدیم، آیا بار دیگر برانگیخته خواهیم شد؟!'),
(2756, 2756, 'لَقَدْ وُعِدْنَا نَحْنُ وَآبَاؤُنَا هَٰذَا مِنْ قَبْلُ إِنْ هَٰذَا إِلَّا أَسَاطِيرُ الْأَوَّلِينَ', 23, 83, 18, 70, 347, 'این وعده به ما و پدرانمان از قبل داده شده؛ این فقط افسانه‌های پیشینیان است!»'),
(2757, 2757, 'قُلْ لِمَنِ الْأَرْضُ وَمَنْ فِيهَا إِنْ كُنْتُمْ تَعْلَمُونَ', 23, 84, 18, 70, 347, 'بگو: «زمین و کسانی که در آن هستند از آن کیست، اگر شما می‌دانید؟!»'),
(2758, 2758, 'سَيَقُولُونَ لِلَّهِ ۚ قُلْ أَفَلَا تَذَكَّرُونَ', 23, 85, 18, 70, 347, 'بزودی (در پاسخ تو) می‌گویند: «همه از آن خداست!» بگو: «آیا متذکّر نمی‌شوید؟!»'),
(2759, 2759, 'قُلْ مَنْ رَبُّ السَّمَاوَاتِ السَّبْعِ وَرَبُّ الْعَرْشِ الْعَظِيمِ', 23, 86, 18, 70, 347, 'بگو: «چه کسی پروردگار آسمانهای هفتگانه، و پروردگار عرش عظیم است؟»'),
(2760, 2760, 'سَيَقُولُونَ لِلَّهِ ۚ قُلْ أَفَلَا تَتَّقُونَ', 23, 87, 18, 70, 347, 'بزودی خواهند گفت: «همه اینها از آن خداست!» بگو: «آیا تقوا پیشه نمی‌کنید (و از خدا نمی‌ترسید و دست از شرک برنمی‌دارید)؟!»'),
(2761, 2761, 'قُلْ مَنْ بِيَدِهِ مَلَكُوتُ كُلِّ شَيْءٍ وَهُوَ يُجِيرُ وَلَا يُجَارُ عَلَيْهِ إِنْ كُنْتُمْ تَعْلَمُونَ', 23, 88, 18, 70, 347, 'بگو: «اگر می‌دانید، چه کسی حکومت همه موجودات را در دست دارد، و به بی پناهان پناه می‌دهد، و نیاز به پناه‌دادن ندارد؟!»'),
(2762, 2762, 'سَيَقُولُونَ لِلَّهِ ۚ قُلْ فَأَنَّىٰ تُسْحَرُونَ', 23, 89, 18, 70, 347, 'خواهند گفت: «(همه اینها) از آن خداست» بگو: «با این حال چگونه می‌گویید سحر شده‌اید (و این سخنان سحر و افسون است)؟!»'),
(2763, 2763, 'بَلْ أَتَيْنَاهُمْ بِالْحَقِّ وَإِنَّهُمْ لَكَاذِبُونَ', 23, 90, 18, 70, 348, 'نه، (واقع این است که) ما حقّ را برای آنها آوردیم؛ و آنان دروغ می‌گویند!'),
(2764, 2764, 'مَا اتَّخَذَ اللَّهُ مِنْ وَلَدٍ وَمَا كَانَ مَعَهُ مِنْ إِلَٰهٍ ۚ إِذًا لَذَهَبَ كُلُّ إِلَٰهٍ بِمَا خَلَقَ وَلَعَلَا بَعْضُهُمْ عَلَىٰ بَعْضٍ ۚ سُبْحَانَ اللَّهِ عَمَّا يَصِفُونَ', 23, 91, 18, 70, 348, 'خدا هرگز فرزندی برای خود انتخاب نکرده؛ و معبود دیگری با او نیست؛ که اگر چنین می‌شد، هر یک از خدایان مخلوقات خود را تدبیر و اداره می‌کردند و بعضی بر بعضی دیگر برتری می‌جستند (و جهان هستی به تباهی کشیده می‌شد)؛ منزّه است خدا از آنچه آنان توصیف می‌کنند!'),
(2765, 2765, 'عَالِمِ الْغَيْبِ وَالشَّهَادَةِ فَتَعَالَىٰ عَمَّا يُشْرِكُونَ', 23, 92, 18, 70, 348, 'او دانای نهان و آشکار است؛ پس برتر است از آنچه برای او همتا قرار می‌دهند!'),
(2766, 2766, 'قُلْ رَبِّ إِمَّا تُرِيَنِّي مَا يُوعَدُونَ', 23, 93, 18, 70, 348, 'بگو: «پروردگار من! اگر عذابهایی را که به آنان وعده داده می‌شود به من نشان دهی (و در زندگیم آن را ببینم)...'),
(2767, 2767, 'رَبِّ فَلَا تَجْعَلْنِي فِي الْقَوْمِ الظَّالِمِينَ', 23, 94, 18, 70, 348, 'پروردگار من! مرا (در این عذابها) با گروه ستمگران قرار مده!»'),
(2768, 2768, 'وَإِنَّا عَلَىٰ أَنْ نُرِيَكَ مَا نَعِدُهُمْ لَقَادِرُونَ', 23, 95, 18, 70, 348, 'و ما تواناییم که آنچه را به آنها وعده می‌دهیم به تو نشان دهیم!'),
(2769, 2769, 'ادْفَعْ بِالَّتِي هِيَ أَحْسَنُ السَّيِّئَةَ ۚ نَحْنُ أَعْلَمُ بِمَا يَصِفُونَ', 23, 96, 18, 70, 348, 'بدی را به بهترین راه و روش دفع کن (و پاسخ بدی را به نیکی ده)! ما به آنچه توصیف می‌کنند آگاهتریم!'),
(2770, 2770, 'وَقُلْ رَبِّ أَعُوذُ بِكَ مِنْ هَمَزَاتِ الشَّيَاطِينِ', 23, 97, 18, 70, 348, 'و بگو: «پروردگارا! از وسوسه‌های شیاطین به تو پناه می‌برم!'),
(2771, 2771, 'وَأَعُوذُ بِكَ رَبِّ أَنْ يَحْضُرُونِ', 23, 98, 18, 70, 348, 'و از اینکه آنان نزد من حاضر شوند (نیز) -ای پروردگار من- به تو پناه می‌برم!»'),
(2772, 2772, 'حَتَّىٰ إِذَا جَاءَ أَحَدَهُمُ الْمَوْتُ قَالَ رَبِّ ارْجِعُونِ', 23, 99, 18, 70, 348, '(آنها همچنان به راه غلط خود ادامه می‌دهند) تا زمانی که مرگ یکی از آنان فرارسد، می‌گوید: «پروردگار من! مرا بازگردانید!'),
(2773, 2773, 'لَعَلِّي أَعْمَلُ صَالِحًا فِيمَا تَرَكْتُ ۚ كَلَّا ۚ إِنَّهَا كَلِمَةٌ هُوَ قَائِلُهَا ۖ وَمِنْ وَرَائِهِمْ بَرْزَخٌ إِلَىٰ يَوْمِ يُبْعَثُونَ', 23, 100, 18, 70, 348, 'شاید در آنچه ترک کردم (و کوتاهی نمودم) عمل صالحی انجام دهم!» (ولی به او می‌گویند:) چنین نیست! این سخنی است که او به زبان می‌گوید (و اگر بازگردد، کارش همچون گذشته است)! و پشت سر آنان برزخی است تا روزی که برانگیخته شوند!'),
(2774, 2774, 'فَإِذَا نُفِخَ فِي الصُّورِ فَلَا أَنْسَابَ بَيْنَهُمْ يَوْمَئِذٍ وَلَا يَتَسَاءَلُونَ', 23, 101, 18, 70, 348, 'هنگامی که در «صور» دمیده شود، هیچ یک از پیوندهای خویشاوندی میان آنها در آن روز نخواهد بود؛ و از یکدیگر تقاضای کمک نمی‌کنند (چون کاری از کسی ساخته نیست)!'),
(2775, 2775, 'فَمَنْ ثَقُلَتْ مَوَازِينُهُ فَأُولَٰئِكَ هُمُ الْمُفْلِحُونَ', 23, 102, 18, 70, 348, 'و کسانی که وزنه اعمالشان سنگین است، همان رستگارانند!'),
(2776, 2776, 'وَمَنْ خَفَّتْ مَوَازِينُهُ فَأُولَٰئِكَ الَّذِينَ خَسِرُوا أَنْفُسَهُمْ فِي جَهَنَّمَ خَالِدُونَ', 23, 103, 18, 70, 348, 'و آنان که وزنه اعمالشان سبک باشد، کسانی هستند که سرمایه وجود خود را از دست داده، در جهنم جاودانه خواهند ماند!'),
(2777, 2777, 'تَلْفَحُ وُجُوهَهُمُ النَّارُ وَهُمْ فِيهَا كَالِحُونَ', 23, 104, 18, 70, 348, 'شعله‌های سوزان آتش همچون شمشیر به صورتهایشان نواخته می‌شود؛ و در دوزخ چهره‌ای عبوس دارند.'),
(2778, 2778, 'أَلَمْ تَكُنْ آيَاتِي تُتْلَىٰ عَلَيْكُمْ فَكُنْتُمْ بِهَا تُكَذِّبُونَ', 23, 105, 18, 70, 349, '(به آنها گفته می‌شود:) آیا آیات من بر شما خوانده نمی‌شد، پس آن را تکذیب می‌کردید؟!'),
(2779, 2779, 'قَالُوا رَبَّنَا غَلَبَتْ عَلَيْنَا شِقْوَتُنَا وَكُنَّا قَوْمًا ضَالِّينَ', 23, 106, 18, 70, 349, 'می‌گویند: «پروردگارا! بدبختی ما بر ما چیره شد، و ما قوم گمراهی بودیم!'),
(2780, 2780, 'رَبَّنَا أَخْرِجْنَا مِنْهَا فَإِنْ عُدْنَا فَإِنَّا ظَالِمُونَ', 23, 107, 18, 70, 349, 'پروردگارا! ما را از این (دوزخ) بیرون آر، اگر بار دیگر تکرار کردیم قطعاً ستمگریم (و مستحق عذاب)!»'),
(2781, 2781, 'قَالَ اخْسَئُوا فِيهَا وَلَا تُكَلِّمُونِ', 23, 108, 18, 70, 349, '(خداوند) می‌گوید: «دور شوید در دوزخ، و با من سخن مگویید!');
INSERT INTO `Quran` (`rowid`, `id`, `Arabic`, `Chapter`, `Verse`, `Juz`, `Hizb`, `Page`, `tr`) VALUES
(2782, 2782, 'إِنَّهُ كَانَ فَرِيقٌ مِنْ عِبَادِي يَقُولُونَ رَبَّنَا آمَنَّا فَاغْفِرْ لَنَا وَارْحَمْنَا وَأَنْتَ خَيْرُ الرَّاحِمِينَ', 23, 109, 18, 70, 349, '(فراموش کرده‌اید) گروهی از بندگانم می‌گفتند: پروردگارا! ما ایمان آوردیم؛ ما را ببخش و بر ما رحم کن؛ و تو بهترین رحم کنندگانی!'),
(2783, 2783, 'فَاتَّخَذْتُمُوهُمْ سِخْرِيًّا حَتَّىٰ أَنْسَوْكُمْ ذِكْرِي وَكُنْتُمْ مِنْهُمْ تَضْحَكُونَ', 23, 110, 18, 70, 349, 'امّا شما آنها را به باد مسخره گرفتید تا شما را از یاد من غافل کردند؛ و شما به آنان می‌خندیدید!'),
(2784, 2784, 'إِنِّي جَزَيْتُهُمُ الْيَوْمَ بِمَا صَبَرُوا أَنَّهُمْ هُمُ الْفَائِزُونَ', 23, 111, 18, 70, 349, 'ولی من امروز آنها را بخاطر صبر و استقامتشان پاداش دادم؛ آنها پیروز و رستگارند!»'),
(2785, 2785, 'قَالَ كَمْ لَبِثْتُمْ فِي الْأَرْضِ عَدَدَ سِنِينَ', 23, 112, 18, 70, 349, '(خداوند) می‌گوید: «چند سال در روی زمین توقّف کردید؟»'),
(2786, 2786, 'قَالُوا لَبِثْنَا يَوْمًا أَوْ بَعْضَ يَوْمٍ فَاسْأَلِ الْعَادِّينَ', 23, 113, 18, 70, 349, '(در پاسخ) می‌گویند: «تنها به اندازه یک روز، یا قسمتی از یک روز! از آنها که می‌توانند بشمارند بپرس!»'),
(2787, 2787, 'قَالَ إِنْ لَبِثْتُمْ إِلَّا قَلِيلًا ۖ لَوْ أَنَّكُمْ كُنْتُمْ تَعْلَمُونَ', 23, 114, 18, 70, 349, 'می‌گوید: «(آری،) شما مقدار کمی توقّف نمودید اگر می‌دانستید!»'),
(2788, 2788, 'أَفَحَسِبْتُمْ أَنَّمَا خَلَقْنَاكُمْ عَبَثًا وَأَنَّكُمْ إِلَيْنَا لَا تُرْجَعُونَ', 23, 115, 18, 70, 349, 'آیا گمان کردید شما را بیهوده آفریده‌ایم، و بسوی ما باز نمی‌گردید؟'),
(2789, 2789, 'فَتَعَالَى اللَّهُ الْمَلِكُ الْحَقُّ ۖ لَا إِلَٰهَ إِلَّا هُوَ رَبُّ الْعَرْشِ الْكَرِيمِ', 23, 116, 18, 70, 349, 'پس برتر است خداوندی که فرمانروای حقّ است (از اینکه شما را بی‌هدف آفریده باشد)! معبودی جز او نیست؛ و او پروردگار عرش کریم است!'),
(2790, 2790, 'وَمَنْ يَدْعُ مَعَ اللَّهِ إِلَٰهًا آخَرَ لَا بُرْهَانَ لَهُ بِهِ فَإِنَّمَا حِسَابُهُ عِنْدَ رَبِّهِ ۚ إِنَّهُ لَا يُفْلِحُ الْكَافِرُونَ', 23, 117, 18, 70, 349, 'و هر کس معبود دیگری را با خدا بخواند -و مسلّماً هیچ دلیلی بر آن نخواهد داشت- حساب او نزد پروردگارش خواهد بود؛ یقیناً کافران رستگار نخواهند شد!'),
(2791, 2791, 'وَقُلْ رَبِّ اغْفِرْ وَارْحَمْ وَأَنْتَ خَيْرُ الرَّاحِمِينَ', 23, 118, 18, 70, 349, 'و بگو: «پروردگارا! مرا ببخش و رحمت کن؛ و تو بهترین رحم کنندگانی!'),
(2792, 2792, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ سُورَةٌ أَنْزَلْنَاهَا وَفَرَضْنَاهَا وَأَنْزَلْنَا فِيهَا آيَاتٍ بَيِّنَاتٍ لَعَلَّكُمْ تَذَكَّرُونَ', 24, 1, 18, 70, 350, '(این) سوره‌ای است که آن را فرو فرستادیم، و (عمل به آن را) واجب نمودیم، و در آن آیات روشنی نازل کردیم، شاید شما متذکّر شوید!'),
(2793, 2793, 'الزَّانِيَةُ وَالزَّانِي فَاجْلِدُوا كُلَّ وَاحِدٍ مِنْهُمَا مِائَةَ جَلْدَةٍ ۖ وَلَا تَأْخُذْكُمْ بِهِمَا رَأْفَةٌ فِي دِينِ اللَّهِ إِنْ كُنْتُمْ تُؤْمِنُونَ بِاللَّهِ وَالْيَوْمِ الْآخِرِ ۖ وَلْيَشْهَدْ عَذَابَهُمَا طَائِفَةٌ مِنَ الْمُؤْمِنِينَ', 24, 2, 18, 70, 350, 'هر یک از زن و مرد زناکار را صد تازیانه بزنید؛ و نباید رأفت (و محبّت کاذب) نسبت به آن دو شما را از اجرای حکم الهی مانع شود، اگر به خدا و روز جزا ایمان دارید! و باید گروهی از مؤمنان مجازاتشان را مشاهده کنند!'),
(2794, 2794, 'الزَّانِي لَا يَنْكِحُ إِلَّا زَانِيَةً أَوْ مُشْرِكَةً وَالزَّانِيَةُ لَا يَنْكِحُهَا إِلَّا زَانٍ أَوْ مُشْرِكٌ ۚ وَحُرِّمَ ذَٰلِكَ عَلَى الْمُؤْمِنِينَ', 24, 3, 18, 70, 350, 'مرد زناکار جز با زن زناکار یا مشرک ازدواج نمی‌کند؛ و زن زناکار را، جز مرد زناکار یا مشرک، به ازدواج خود درنمی‌آورد؛ و این کار بر مؤمنان حرام شده است!'),
(2795, 2795, 'وَالَّذِينَ يَرْمُونَ الْمُحْصَنَاتِ ثُمَّ لَمْ يَأْتُوا بِأَرْبَعَةِ شُهَدَاءَ فَاجْلِدُوهُمْ ثَمَانِينَ جَلْدَةً وَلَا تَقْبَلُوا لَهُمْ شَهَادَةً أَبَدًا ۚ وَأُولَٰئِكَ هُمُ الْفَاسِقُونَ', 24, 4, 18, 70, 350, 'و کسانی که زنان پاکدامن را متهمّ می‌کنند، سپس چهار شاهد (بر مدّعای خود) نمی‌آورند، آنها را هشتاد تازیانه بزنید و شهادتشان را هرگز نپذیرید؛ و آنها همان فاسقانند!'),
(2796, 2796, 'إِلَّا الَّذِينَ تَابُوا مِنْ بَعْدِ ذَٰلِكَ وَأَصْلَحُوا فَإِنَّ اللَّهَ غَفُورٌ رَحِيمٌ', 24, 5, 18, 70, 350, 'مگر کسانی که بعد از آن توبه کنند و جبران نمایند (که خداوند آنها را می‌بخشد) زیرا خداوند آمرزنده و مهربان است.'),
(2797, 2797, 'وَالَّذِينَ يَرْمُونَ أَزْوَاجَهُمْ وَلَمْ يَكُنْ لَهُمْ شُهَدَاءُ إِلَّا أَنْفُسُهُمْ فَشَهَادَةُ أَحَدِهِمْ أَرْبَعُ شَهَادَاتٍ بِاللَّهِ ۙ إِنَّهُ لَمِنَ الصَّادِقِينَ', 24, 6, 18, 70, 350, 'و کسانی که همسران خود را (به عمل منافی عفّت) متهمّ می‌کنند، و گواهانی جز خودشان ندارند، هر یک از آنها باید چهار مرتبه به نام خدا شهادت دهد که از راستگویان است؛'),
(2798, 2798, 'وَالْخَامِسَةُ أَنَّ لَعْنَتَ اللَّهِ عَلَيْهِ إِنْ كَانَ مِنَ الْكَاذِبِينَ', 24, 7, 18, 70, 350, 'و در پنجمین بار بگوید که لعنت خدا بر او باد اگر از دروغگویان باشد.'),
(2799, 2799, 'وَيَدْرَأُ عَنْهَا الْعَذَابَ أَنْ تَشْهَدَ أَرْبَعَ شَهَادَاتٍ بِاللَّهِ ۙ إِنَّهُ لَمِنَ الْكَاذِبِينَ', 24, 8, 18, 70, 350, 'آن زن نیز می‌تواند کیفر (زنا) را از خود دور کند، به این طریق که چهار بار خدا را به شهادت طلبد که آن مرد (در این نسبتی که به او می‌دهد) از دروغگویان است.'),
(2800, 2800, 'وَالْخَامِسَةَ أَنَّ غَضَبَ اللَّهِ عَلَيْهَا إِنْ كَانَ مِنَ الصَّادِقِينَ', 24, 9, 18, 70, 350, 'و بار پنجم بگوید که غضب خدا بر او باد اگر آن مرد از راستگویان باشد.'),
(2801, 2801, 'وَلَوْلَا فَضْلُ اللَّهِ عَلَيْكُمْ وَرَحْمَتُهُ وَأَنَّ اللَّهَ تَوَّابٌ حَكِيمٌ', 24, 10, 18, 70, 350, 'و اگر فضل و رحمت خدا شامل حال شما نبود و اینکه او توبه‌پذیر و حکیم است (بسیاری از شما گرفتار مجازات سخت الهی می‌شدید)!'),
(2802, 2802, 'إِنَّ الَّذِينَ جَاءُوا بِالْإِفْكِ عُصْبَةٌ مِنْكُمْ ۚ لَا تَحْسَبُوهُ شَرًّا لَكُمْ ۖ بَلْ هُوَ خَيْرٌ لَكُمْ ۚ لِكُلِّ امْرِئٍ مِنْهُمْ مَا اكْتَسَبَ مِنَ الْإِثْمِ ۚ وَالَّذِي تَوَلَّىٰ كِبْرَهُ مِنْهُمْ لَهُ عَذَابٌ عَظِيمٌ', 24, 11, 18, 70, 351, 'مسلّماً کسانی که آن تهمت عظیم را عنوان کردند گروهی (متشکّل و توطئه‌گر) از شما بودند؛ امّا گمان نکنید این ماجرا برای شما بد است، بلکه خیر شما در آن است؛ آنها هر کدام سهم خود را از این گناهی که مرتکب شدند دارند؛ و از آنان کسی که بخش مهمّ آن را بر عهده داشت عذاب عظیمی برای اوست!'),
(2803, 2803, 'لَوْلَا إِذْ سَمِعْتُمُوهُ ظَنَّ الْمُؤْمِنُونَ وَالْمُؤْمِنَاتُ بِأَنْفُسِهِمْ خَيْرًا وَقَالُوا هَٰذَا إِفْكٌ مُبِينٌ', 24, 12, 18, 70, 351, 'چرا هنگامی که این (تهمت) را شنیدید، مردان و زنان با ایمان نسبت به خود (و کسی که همچون خود آنها بود) گمان خیر نبردند و نگفتند این دروغی بزرگ و آشکار است؟!'),
(2804, 2804, 'لَوْلَا جَاءُوا عَلَيْهِ بِأَرْبَعَةِ شُهَدَاءَ ۚ فَإِذْ لَمْ يَأْتُوا بِالشُّهَدَاءِ فَأُولَٰئِكَ عِنْدَ اللَّهِ هُمُ الْكَاذِبُونَ', 24, 13, 18, 70, 351, 'چرا چهار شاهد برای آن نیاوردند؟! اکنون که این گواهان را نیاوردند، آنان در پیشگاه خدا دروغگویانند!'),
(2805, 2805, 'وَلَوْلَا فَضْلُ اللَّهِ عَلَيْكُمْ وَرَحْمَتُهُ فِي الدُّنْيَا وَالْآخِرَةِ لَمَسَّكُمْ فِي مَا أَفَضْتُمْ فِيهِ عَذَابٌ عَظِيمٌ', 24, 14, 18, 70, 351, 'و اگر فضل و رحمت الهی در دنیا و آخرت شامل شما نمی‌شد، بخاطر این گناهی که کردید عذاب سختی به شما می‌رسید!'),
(2806, 2806, 'إِذْ تَلَقَّوْنَهُ بِأَلْسِنَتِكُمْ وَتَقُولُونَ بِأَفْوَاهِكُمْ مَا لَيْسَ لَكُمْ بِهِ عِلْمٌ وَتَحْسَبُونَهُ هَيِّنًا وَهُوَ عِنْدَ اللَّهِ عَظِيمٌ', 24, 15, 18, 70, 351, 'به خاطر بیاورید زمانی را که این شایعه را از زبان یکدیگر می‌گرفتید، و با دهان خود سخنی می‌گفتید که به آن یقین نداشتید؛ و آن را کوچک می‌پنداشتید در حالی که نزد خدا بزرگ است!'),
(2807, 2807, 'وَلَوْلَا إِذْ سَمِعْتُمُوهُ قُلْتُمْ مَا يَكُونُ لَنَا أَنْ نَتَكَلَّمَ بِهَٰذَا سُبْحَانَكَ هَٰذَا بُهْتَانٌ عَظِيمٌ', 24, 16, 18, 70, 351, 'چرا هنگامی که آن را شنیدید نگفتید: «ما حق نداریم که به این سخن تکلّم کنیم؛ خداوندا منزّهی تو، این بهتان بزرگی است»؟!'),
(2808, 2808, 'يَعِظُكُمُ اللَّهُ أَنْ تَعُودُوا لِمِثْلِهِ أَبَدًا إِنْ كُنْتُمْ مُؤْمِنِينَ', 24, 17, 18, 70, 351, 'خداوند شما را اندرز می‌دهد که هرگز چنین کاری را تکرار نکنید اگر ایمان دارید!'),
(2809, 2809, 'وَيُبَيِّنُ اللَّهُ لَكُمُ الْآيَاتِ ۚ وَاللَّهُ عَلِيمٌ حَكِيمٌ', 24, 18, 18, 70, 351, 'و خداوند آیات را برای شما بیان می‌کند، و خدا دانا و حکیم است.'),
(2810, 2810, 'إِنَّ الَّذِينَ يُحِبُّونَ أَنْ تَشِيعَ الْفَاحِشَةُ فِي الَّذِينَ آمَنُوا لَهُمْ عَذَابٌ أَلِيمٌ فِي الدُّنْيَا وَالْآخِرَةِ ۚ وَاللَّهُ يَعْلَمُ وَأَنْتُمْ لَا تَعْلَمُونَ', 24, 19, 18, 70, 351, 'کسانی که دوست دارند زشتیها در میان مردم با ایمان شیوع یابد، عذاب دردناکی برای آنان در دنیا و آخرت است؛ و خداوند می‌داند و شما نمی‌دانید!'),
(2811, 2811, 'وَلَوْلَا فَضْلُ اللَّهِ عَلَيْكُمْ وَرَحْمَتُهُ وَأَنَّ اللَّهَ رَءُوفٌ رَحِيمٌ', 24, 20, 18, 70, 351, 'و اگر فضل و رحمت الهی شامل حال شما نبود و اینکه خدا مهربان و رحیم است (مجازات سختی دامانتان را می‌گرفت)!'),
(2812, 2812, ' يَا أَيُّهَا الَّذِينَ آمَنُوا لَا تَتَّبِعُوا خُطُوَاتِ الشَّيْطَانِ ۚ وَمَنْ يَتَّبِعْ خُطُوَاتِ الشَّيْطَانِ فَإِنَّهُ يَأْمُرُ بِالْفَحْشَاءِ وَالْمُنْكَرِ ۚ وَلَوْلَا فَضْلُ اللَّهِ عَلَيْكُمْ وَرَحْمَتُهُ مَا زَكَىٰ مِنْكُمْ مِنْ أَحَدٍ أَبَدًا وَلَٰكِنَّ اللَّهَ يُزَكِّي مَنْ يَشَاءُ ۗ وَاللَّهُ سَمِيعٌ عَلِيمٌ', 24, 21, 18, 71, 352, 'ای کسانی که ایمان آورده‌اید! از گامهای شیطان پیروی نکنید! هر کس پیرو شیطان شود (گمراهش می‌سازد، زیرا) او به فحشا و منکر فرمان می‌دهد! و اگر فضل و رحمت الهی بر شما نبود، هرگز احدی از شما پاک نمی‌شد؛ ولی خداوند هر که را بخواهد تزکیه می‌کند، و خدا شنوا و داناست!'),
(2813, 2813, 'وَلَا يَأْتَلِ أُولُو الْفَضْلِ مِنْكُمْ وَالسَّعَةِ أَنْ يُؤْتُوا أُولِي الْقُرْبَىٰ وَالْمَسَاكِينَ وَالْمُهَاجِرِينَ فِي سَبِيلِ اللَّهِ ۖ وَلْيَعْفُوا وَلْيَصْفَحُوا ۗ أَلَا تُحِبُّونَ أَنْ يَغْفِرَ اللَّهُ لَكُمْ ۗ وَاللَّهُ غَفُورٌ رَحِيمٌ', 24, 22, 18, 71, 352, 'آنها که از میان شما دارای برتری (مالی) و وسعت زندگی هستند نباید سوگند یاد کنند که از انفاق نسبت به نزدیکان و مستمندان و مهاجران در راه خدا دریغ نمایند؛ آنها باید عفو کنند و چشم بپوشند؛ آیا دوست نمی‌دارید خداوند شما را ببخشد؟! و خداوند آمرزنده و مهربان است!'),
(2814, 2814, 'إِنَّ الَّذِينَ يَرْمُونَ الْمُحْصَنَاتِ الْغَافِلَاتِ الْمُؤْمِنَاتِ لُعِنُوا فِي الدُّنْيَا وَالْآخِرَةِ وَلَهُمْ عَذَابٌ عَظِيمٌ', 24, 23, 18, 71, 352, 'کسانی که زنان پاکدامن و بی‌خبر (از هرگونه آلودگی) و مؤمن را متهم می‌سازند، در دنیا و آخرت از رحمت الهی بدورند و عذاب بزرگی برای آنهاست.'),
(2815, 2815, 'يَوْمَ تَشْهَدُ عَلَيْهِمْ أَلْسِنَتُهُمْ وَأَيْدِيهِمْ وَأَرْجُلُهُمْ بِمَا كَانُوا يَعْمَلُونَ', 24, 24, 18, 71, 352, 'در آن روز زبانها و دستها و پاهایشان بر ضدّ آنها به اعمالی که مرتکب می‌شدند گواهی می‌دهد!'),
(2816, 2816, 'يَوْمَئِذٍ يُوَفِّيهِمُ اللَّهُ دِينَهُمُ الْحَقَّ وَيَعْلَمُونَ أَنَّ اللَّهَ هُوَ الْحَقُّ الْمُبِينُ', 24, 25, 18, 71, 352, 'آن روز، خداوند جزای واقعی آنان را بی‌کم و کاست می‌دهد؛ و می‌دانند که خداوند حق آشکار است!'),
(2817, 2817, 'الْخَبِيثَاتُ لِلْخَبِيثِينَ وَالْخَبِيثُونَ لِلْخَبِيثَاتِ ۖ وَالطَّيِّبَاتُ لِلطَّيِّبِينَ وَالطَّيِّبُونَ لِلطَّيِّبَاتِ ۚ أُولَٰئِكَ مُبَرَّءُونَ مِمَّا يَقُولُونَ ۖ لَهُمْ مَغْفِرَةٌ وَرِزْقٌ كَرِيمٌ', 24, 26, 18, 71, 352, 'زنان ناپاک از آن مردان ناپاکند، و مردان ناپاک نیز به زنان ناپاک تعلّق دارند؛ و زنان پاک از آن مردان پاک، و مردان پاک از آن زنان پاکند! اینان از نسبتهای ناروایی که (ناپاکان) به آنان می‌دهند مبرّا هستند؛ و برای آنان آمرزش (الهی) و روزی پرارزشی است!'),
(2818, 2818, 'يَا أَيُّهَا الَّذِينَ آمَنُوا لَا تَدْخُلُوا بُيُوتًا غَيْرَ بُيُوتِكُمْ حَتَّىٰ تَسْتَأْنِسُوا وَتُسَلِّمُوا عَلَىٰ أَهْلِهَا ۚ ذَٰلِكُمْ خَيْرٌ لَكُمْ لَعَلَّكُمْ تَذَكَّرُونَ', 24, 27, 18, 71, 352, 'ای کسانی که ایمان آورده‌اید! در خانه‌هایی غیر از خانه خود وارد نشوید تا اجازه بگیرید و بر اهل آن خانه سلام کنید؛ این برای شما بهتر است؛ شاید متذکّر شوید!'),
(2819, 2819, 'فَإِنْ لَمْ تَجِدُوا فِيهَا أَحَدًا فَلَا تَدْخُلُوهَا حَتَّىٰ يُؤْذَنَ لَكُمْ ۖ وَإِنْ قِيلَ لَكُمُ ارْجِعُوا فَارْجِعُوا ۖ هُوَ أَزْكَىٰ لَكُمْ ۚ وَاللَّهُ بِمَا تَعْمَلُونَ عَلِيمٌ', 24, 28, 18, 71, 353, 'و اگر کسی را در آن نیافتید، وارد نشوید تا به شما اجازه داده شود؛ و اگر گفته شد: «بازگردید!» بازگردید؛ این برای شما پاکیزه‌تر است؛ و خداوند به آنچه انجام می‌دهید آگاه است!'),
(2820, 2820, 'لَيْسَ عَلَيْكُمْ جُنَاحٌ أَنْ تَدْخُلُوا بُيُوتًا غَيْرَ مَسْكُونَةٍ فِيهَا مَتَاعٌ لَكُمْ ۚ وَاللَّهُ يَعْلَمُ مَا تُبْدُونَ وَمَا تَكْتُمُونَ', 24, 29, 18, 71, 353, '(ولی) گناهی بر شما نیست که وارد خانه‌های غیر مسکونی بشوید که در آن متاعی متعلّق به شما وجود دارد؛ و خدا آنچه را آشکار می‌کنید و آنچه را پنهان می‌دارید، می‌داند!'),
(2821, 2821, 'قُلْ لِلْمُؤْمِنِينَ يَغُضُّوا مِنْ أَبْصَارِهِمْ وَيَحْفَظُوا فُرُوجَهُمْ ۚ ذَٰلِكَ أَزْكَىٰ لَهُمْ ۗ إِنَّ اللَّهَ خَبِيرٌ بِمَا يَصْنَعُونَ', 24, 30, 18, 71, 353, 'به مؤمنان بگو چشمهای خود را (از نگاه به نامحرمان) فروگیرند، و عفاف خود را حفظ کنند؛ این برای آنان پاکیزه‌تر است؛ خداوند از آنچه انجام می‌دهید آگاه است!'),
(2822, 2822, 'وَقُلْ لِلْمُؤْمِنَاتِ يَغْضُضْنَ مِنْ أَبْصَارِهِنَّ وَيَحْفَظْنَ فُرُوجَهُنَّ وَلَا يُبْدِينَ زِينَتَهُنَّ إِلَّا مَا ظَهَرَ مِنْهَا ۖ وَلْيَضْرِبْنَ بِخُمُرِهِنَّ عَلَىٰ جُيُوبِهِنَّ ۖ وَلَا يُبْدِينَ زِينَتَهُنَّ إِلَّا لِبُعُولَتِهِنَّ أَوْ آبَائِهِنَّ أَوْ آبَاءِ بُعُولَتِهِنَّ أَوْ أَبْنَائِهِنَّ أَوْ أَبْنَاءِ بُعُولَتِهِنَّ أَوْ إِخْوَانِهِنَّ أَوْ بَنِي إِخْوَانِهِنَّ أَوْ بَنِي أَخَوَاتِهِنَّ أَوْ نِسَائِهِنَّ أَوْ مَا مَلَكَتْ أَيْمَانُهُنَّ أَوِ التَّابِعِينَ غَيْرِ أُولِي الْإِرْبَةِ مِنَ الرِّجَالِ أَوِ الطِّفْلِ الَّذِينَ لَمْ يَظْهَرُوا عَلَىٰ عَوْرَاتِ النِّسَاءِ ۖ وَلَا يَضْرِبْنَ بِأَرْجُلِهِنَّ لِيُعْلَمَ مَا يُخْفِينَ مِنْ زِينَتِهِنَّ ۚ وَتُوبُوا إِلَى اللَّهِ جَمِيعًا أَيُّهَ الْمُؤْمِنُونَ لَعَلَّكُمْ تُفْلِحُونَ', 24, 31, 18, 71, 353, 'و به زنان با ایمان بگو چشمهای خود را (از نگاه هوس‌آلود) فروگیرند، و دامان خویش را حفظ کنند و زینت خود را -جز آن مقدار که نمایان است- آشکار ننمایند و (اطراف) روسری‌های خود را بر سینه خود افکنند (تا گردن و سینه با آن پوشانده شود)، و زینت خود را آشکار نسازند مگر برای شوهرانشان، یا پدرانشان، یا پدر شوهرانشان، یا پسرانشان، یا پسران همسرانشان، یا برادرانشان، یا پسران برادرانشان، یا پسران خواهرانشان، یا زنان هم‌کیششان، یا بردگانشان =کنیزانشان‌، یا افراد سفیه که تمایلی به زن ندارند، یا کودکانی که از امور جنسی مربوط به زنان آگاه نیستند؛ و هنگام راه رفتن پاهای خود را به زمین نزنند تا زینت پنهانیشان دانسته شود (و صدای خلخال که برپا دارند به گوش رسد). و همگی بسوی خدا بازگردید ای مؤمنان، تا رستگار شوید!'),
(2823, 2823, 'وَأَنْكِحُوا الْأَيَامَىٰ مِنْكُمْ وَالصَّالِحِينَ مِنْ عِبَادِكُمْ وَإِمَائِكُمْ ۚ إِنْ يَكُونُوا فُقَرَاءَ يُغْنِهِمُ اللَّهُ مِنْ فَضْلِهِ ۗ وَاللَّهُ وَاسِعٌ عَلِيمٌ', 24, 32, 18, 71, 354, 'مردان و زنان بی‌همسر خود را همسر دهید، همچنین غلامان و کنیزان صالح و درستکارتان را؛ اگر فقیر و تنگدست باشند، خداوند از فضل خود آنان را بی‌نیاز می‌سازد؛ خداوند گشایش‌دهنده و آگاه است!'),
(2824, 2824, 'وَلْيَسْتَعْفِفِ الَّذِينَ لَا يَجِدُونَ نِكَاحًا حَتَّىٰ يُغْنِيَهُمُ اللَّهُ مِنْ فَضْلِهِ ۗ وَالَّذِينَ يَبْتَغُونَ الْكِتَابَ مِمَّا مَلَكَتْ أَيْمَانُكُمْ فَكَاتِبُوهُمْ إِنْ عَلِمْتُمْ فِيهِمْ خَيْرًا ۖ وَآتُوهُمْ مِنْ مَالِ اللَّهِ الَّذِي آتَاكُمْ ۚ وَلَا تُكْرِهُوا فَتَيَاتِكُمْ عَلَى الْبِغَاءِ إِنْ أَرَدْنَ تَحَصُّنًا لِتَبْتَغُوا عَرَضَ الْحَيَاةِ الدُّنْيَا ۚ وَمَنْ يُكْرِهْهُنَّ فَإِنَّ اللَّهَ مِنْ بَعْدِ إِكْرَاهِهِنَّ غَفُورٌ رَحِيمٌ', 24, 33, 18, 71, 354, 'و کسانی که امکانی برای ازدواج نمی‌یابند، باید پاکدامنی پیشه کنند تا خداوند از فضل خود آنان را بی‌نیاز گرداند! و آن بردگانتان که خواستار مکاتبه =قرار داد مخصوص برای آزاد شدن‌ هستند، با آنان قرار داد ببندید اگر رشد و صلاح در آنان احساس می‌کنید (که بعد از آزادی، توانایی زندگی مستقل را دارند)؛ و چیزی از مال خدا را که به شما داده است به آنان بدهید! و کنیزان خود را برای دستیابی متاع ناپایدار زندگی دنیا مجبور به خود فروشی نکنید اگر خودشان می‌خواهند پاک بمانند! و هر کس آنها را (بر این کار) اجبار کند، (سپس پشیمان گردد،) خداوند بعد از این اجبار آنها غفور و رحیم است! (توبه کنید و بازگردید، تا خدا شما را ببخشد!)'),
(2825, 2825, 'وَلَقَدْ أَنْزَلْنَا إِلَيْكُمْ آيَاتٍ مُبَيِّنَاتٍ وَمَثَلًا مِنَ الَّذِينَ خَلَوْا مِنْ قَبْلِكُمْ وَمَوْعِظَةً لِلْمُتَّقِينَ', 24, 34, 18, 71, 354, 'ما بر شما آیاتی فرستادیم که حقایق بسیاری را تبیین می‌کند، و اخباری از کسانی که پیش از شما بودند، و موعظه و اندرزی برای پرهیزگاران!'),
(2826, 2826, ' اللَّهُ نُورُ السَّمَاوَاتِ وَالْأَرْضِ ۚ مَثَلُ نُورِهِ كَمِشْكَاةٍ فِيهَا مِصْبَاحٌ ۖ الْمِصْبَاحُ فِي زُجَاجَةٍ ۖ الزُّجَاجَةُ كَأَنَّهَا كَوْكَبٌ دُرِّيٌّ يُوقَدُ مِنْ شَجَرَةٍ مُبَارَكَةٍ زَيْتُونَةٍ لَا شَرْقِيَّةٍ وَلَا غَرْبِيَّةٍ يَكَادُ زَيْتُهَا يُضِيءُ وَلَوْ لَمْ تَمْسَسْهُ نَارٌ ۚ نُورٌ عَلَىٰ نُورٍ ۗ يَهْدِي اللَّهُ لِنُورِهِ مَنْ يَشَاءُ ۚ وَيَضْرِبُ اللَّهُ الْأَمْثَالَ لِلنَّاسِ ۗ وَاللَّهُ بِكُلِّ شَيْءٍ عَلِيمٌ', 24, 35, 18, 71, 354, 'خداوند نور آسمانها و زمین است؛ مثل نور خداوند همانند چراغدانی است که در آن چراغی (پر فروغ) باشد، آن چراغ در حبابی قرار گیرد، حبابی شفاف و درخشنده همچون یک ستاره فروزان، این چراغ با روغنی افروخته می‌شود که از درخت پربرکت زیتونی گرفته شده که نه شرقی است و نه غربی؛ (روغنش آنچنان صاف و خالص است که) نزدیک است بدون تماس با آتش شعله‌ور شود؛ نوری است بر فراز نوری؛ و خدا هر کس را بخواهد به نور خود هدایت می‌کند، و خداوند به هر چیزی داناست.'),
(2827, 2827, 'فِي بُيُوتٍ أَذِنَ اللَّهُ أَنْ تُرْفَعَ وَيُذْكَرَ فِيهَا اسْمُهُ يُسَبِّحُ لَهُ فِيهَا بِالْغُدُوِّ وَالْآصَالِ', 24, 36, 18, 71, 354, '(این چراغ پرفروغ) در خانه‌هایی قرار دارد که خداوند اذن فرموده دیوارهای آن را بالا برند (تا از دستبرد شیاطین و هوسبازان در امان باشد)؛ خانه‌هایی که نام خدا در آنها برده می‌شود، و صبح و شام در آنها تسبیح او می‌گویند...'),
(2828, 2828, 'رِجَالٌ لَا تُلْهِيهِمْ تِجَارَةٌ وَلَا بَيْعٌ عَنْ ذِكْرِ اللَّهِ وَإِقَامِ الصَّلَاةِ وَإِيتَاءِ الزَّكَاةِ ۙ يَخَافُونَ يَوْمًا تَتَقَلَّبُ فِيهِ الْقُلُوبُ وَالْأَبْصَارُ', 24, 37, 18, 71, 355, 'مردانی که نه تجارت و نه معامله‌ای آنان را از یاد خدا و برپاداشتن نماز و ادای زکات غافل نمی‌کند؛ آنها از روزی می‌ترسند که در آن، دلها و چشمها زیر و رو می‌شود.'),
(2829, 2829, 'لِيَجْزِيَهُمُ اللَّهُ أَحْسَنَ مَا عَمِلُوا وَيَزِيدَهُمْ مِنْ فَضْلِهِ ۗ وَاللَّهُ يَرْزُقُ مَنْ يَشَاءُ بِغَيْرِ حِسَابٍ', 24, 38, 18, 71, 355, '(آنها به سراغ این کارها می‌روند) تا خداوند آنان را به بهترین اعمالی که انجام داده‌اند پاداش دهد، و از فضل خود بر پاداششان بیفزاید؛ و خداوند به هر کس بخواهد بی حساب روزی می‌دهد (و از مواهب بی‌انتهای خویش بهره‌مند می‌سازد).'),
(2830, 2830, 'وَالَّذِينَ كَفَرُوا أَعْمَالُهُمْ كَسَرَابٍ بِقِيعَةٍ يَحْسَبُهُ الظَّمْآنُ مَاءً حَتَّىٰ إِذَا جَاءَهُ لَمْ يَجِدْهُ شَيْئًا وَوَجَدَ اللَّهَ عِنْدَهُ فَوَفَّاهُ حِسَابَهُ ۗ وَاللَّهُ سَرِيعُ الْحِسَابِ', 24, 39, 18, 71, 355, 'کسانی که کافر شدند، اعمالشان همچون سرابی است در یک کویر که انسان تشنه از دور آن را آب می‌پندارد؛ امّا هنگامی که به سراغ آن می‌آید چیزی نمی‌یابد، و خدا را نزد آن می‌یابد که حساب او را بطور کامل می‌دهد؛ و خداوند سریع الحساب است!'),
(2831, 2831, 'أَوْ كَظُلُمَاتٍ فِي بَحْرٍ لُجِّيٍّ يَغْشَاهُ مَوْجٌ مِنْ فَوْقِهِ مَوْجٌ مِنْ فَوْقِهِ سَحَابٌ ۚ ظُلُمَاتٌ بَعْضُهَا فَوْقَ بَعْضٍ إِذَا أَخْرَجَ يَدَهُ لَمْ يَكَدْ يَرَاهَا ۗ وَمَنْ لَمْ يَجْعَلِ اللَّهُ لَهُ نُورًا فَمَا لَهُ مِنْ نُورٍ', 24, 40, 18, 71, 355, 'یا همچون ظلماتی در یک دریای عمیق و پهناور که موج آن را پوشانده، و بر فراز آن موج دیگری، و بر فراز آن ابری تاریک است؛ ظلمتهایی است یکی بر فراز دیگری، آن گونه که هر گاه دست خود را خارج کند ممکن نیست آن را ببیند! و کسی که خدا نوری برای او قرار نداده، نوری برای او نیست!'),
(2832, 2832, 'أَلَمْ تَرَ أَنَّ اللَّهَ يُسَبِّحُ لَهُ مَنْ فِي السَّمَاوَاتِ وَالْأَرْضِ وَالطَّيْرُ صَافَّاتٍ ۖ كُلٌّ قَدْ عَلِمَ صَلَاتَهُ وَتَسْبِيحَهُ ۗ وَاللَّهُ عَلِيمٌ بِمَا يَفْعَلُونَ', 24, 41, 18, 71, 355, 'آیا ندیدی تمام آنان که در آسمانها و زمینند برای خدا تسبیح می‌کنند، و همچنین پرندگان به هنگامی که بر فراز آسمان بال گسترده‌اند؟! هر یک از آنها نماز و تسبیح خود را می‌داند؛ و خداوند به آنچه انجام میدهند داناست!'),
(2833, 2833, 'وَلِلَّهِ مُلْكُ السَّمَاوَاتِ وَالْأَرْضِ ۖ وَإِلَى اللَّهِ الْمَصِيرُ', 24, 42, 18, 71, 355, 'و از برای خداست حکومت و مالکیّت آسمانها و زمین؛ و بازگشت (تمامی موجودات) بسوی اوست!'),
(2834, 2834, 'أَلَمْ تَرَ أَنَّ اللَّهَ يُزْجِي سَحَابًا ثُمَّ يُؤَلِّفُ بَيْنَهُ ثُمَّ يَجْعَلُهُ رُكَامًا فَتَرَى الْوَدْقَ يَخْرُجُ مِنْ خِلَالِهِ وَيُنَزِّلُ مِنَ السَّمَاءِ مِنْ جِبَالٍ فِيهَا مِنْ بَرَدٍ فَيُصِيبُ بِهِ مَنْ يَشَاءُ وَيَصْرِفُهُ عَنْ مَنْ يَشَاءُ ۖ يَكَادُ سَنَا بَرْقِهِ يَذْهَبُ بِالْأَبْصَارِ', 24, 43, 18, 71, 355, 'آیا ندیدی که خداوند ابرهایی را به آرامی می‌راند، سپس میان آنها پیوند می‌دهد، و بعد آن را متراکم می‌سازد؟! در این حال، دانه‌های باران را می‌بینی که از لابه‌لای آن خارج می‌شود؛ و از آسمان -از کوه‌هایی که در آن است =ابرهایی که همچون کوه‌ها انباشته شده‌اند- دانه‌های تگرگ نازل می‌کند، و هر کس را بخواهد بوسیله آن زیان می‌رساند، و از هر کس بخواهد این زیان را برطرف می‌کند؛ نزدیک است درخشندگی برق آن (ابرها) چشمها را ببرد!'),
(2835, 2835, 'يُقَلِّبُ اللَّهُ اللَّيْلَ وَالنَّهَارَ ۚ إِنَّ فِي ذَٰلِكَ لَعِبْرَةً لِأُولِي الْأَبْصَارِ', 24, 44, 18, 71, 356, 'خداوند شب و روز را دگرگون می‌سازد؛ در این عبرتی است برای صاحبان بصیرت!'),
(2836, 2836, 'وَاللَّهُ خَلَقَ كُلَّ دَابَّةٍ مِنْ مَاءٍ ۖ فَمِنْهُمْ مَنْ يَمْشِي عَلَىٰ بَطْنِهِ وَمِنْهُمْ مَنْ يَمْشِي عَلَىٰ رِجْلَيْنِ وَمِنْهُمْ مَنْ يَمْشِي عَلَىٰ أَرْبَعٍ ۚ يَخْلُقُ اللَّهُ مَا يَشَاءُ ۚ إِنَّ اللَّهَ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ', 24, 45, 18, 71, 356, 'و خداوند هر جنبنده‌ای را از آبی آفرید؛ گروهی از آنها بر شکم خود راه می‌روند، و گروهی بر دو پای خود، و گروهی بر چهار پا راه می‌روند؛ خداوند هر چه را بخواهد می‌آفریند، زیرا خدا بر همه چیز تواناست!'),
(2837, 2837, 'لَقَدْ أَنْزَلْنَا آيَاتٍ مُبَيِّنَاتٍ ۚ وَاللَّهُ يَهْدِي مَنْ يَشَاءُ إِلَىٰ صِرَاطٍ مُسْتَقِيمٍ', 24, 46, 18, 71, 356, 'ما آیات روشنگری نازل کردیم؛ و خدا هر که را بخواهد به صراط مستقیم هدایت می‌کند!'),
(2838, 2838, 'وَيَقُولُونَ آمَنَّا بِاللَّهِ وَبِالرَّسُولِ وَأَطَعْنَا ثُمَّ يَتَوَلَّىٰ فَرِيقٌ مِنْهُمْ مِنْ بَعْدِ ذَٰلِكَ ۚ وَمَا أُولَٰئِكَ بِالْمُؤْمِنِينَ', 24, 47, 18, 71, 356, 'آنها می‌گویند: «به خدا و پیامبر ایمان داریم و اطاعت می‌کنیم!» ولی بعد از این ادّعا، گروهی از آنان رویگردان می‌شوند؛ آنها (در حقیقت) مؤمن نیستند!'),
(2839, 2839, 'وَإِذَا دُعُوا إِلَى اللَّهِ وَرَسُولِهِ لِيَحْكُمَ بَيْنَهُمْ إِذَا فَرِيقٌ مِنْهُمْ مُعْرِضُونَ', 24, 48, 18, 71, 356, 'و هنگامی که از آنان دعوت شود که بسوی خدا و پیامبرش بیایند تا در میانشان داوری کند، ناگهان گروهی از آنان رویگردان می‌شوند!'),
(2840, 2840, 'وَإِنْ يَكُنْ لَهُمُ الْحَقُّ يَأْتُوا إِلَيْهِ مُذْعِنِينَ', 24, 49, 18, 71, 356, 'ولی اگر حق داشته باشند (و داوری به نفع آنان شود) با سرعت و تسلیم بسوی او می‌آیند!'),
(2841, 2841, 'أَفِي قُلُوبِهِمْ مَرَضٌ أَمِ ارْتَابُوا أَمْ يَخَافُونَ أَنْ يَحِيفَ اللَّهُ عَلَيْهِمْ وَرَسُولُهُ ۚ بَلْ أُولَٰئِكَ هُمُ الظَّالِمُونَ', 24, 50, 18, 71, 356, 'آیا در دلهای آنان بیماری است، یا شکّ و تردید دارند، یا می‌ترسند خدا و رسولش بر آنان ستم کنند؟! نه، بلکه آنها خودشان ستمگرند!'),
(2842, 2842, 'إِنَّمَا كَانَ قَوْلَ الْمُؤْمِنِينَ إِذَا دُعُوا إِلَى اللَّهِ وَرَسُولِهِ لِيَحْكُمَ بَيْنَهُمْ أَنْ يَقُولُوا سَمِعْنَا وَأَطَعْنَا ۚ وَأُولَٰئِكَ هُمُ الْمُفْلِحُونَ', 24, 51, 18, 71, 356, 'سخن مؤمنان، هنگامی که بسوی خدا و رسولش دعوت شوند تا میان آنان داوری کند، تنها این است که می‌گویند: «شنیدیم و اطاعت کردیم!» و اینها همان رستگاران واقعی هستند.'),
(2843, 2843, 'وَمَنْ يُطِعِ اللَّهَ وَرَسُولَهُ وَيَخْشَ اللَّهَ وَيَتَّقْهِ فَأُولَٰئِكَ هُمُ الْفَائِزُونَ', 24, 52, 18, 71, 356, 'و هر کس خدا و پیامبرش را اطاعت کند، و از خدا بترسد و از مخالفت فرمانش بپرهیزد، چنین کسانی همان پیروزمندان واقعی هستند!'),
(2844, 2844, ' وَأَقْسَمُوا بِاللَّهِ جَهْدَ أَيْمَانِهِمْ لَئِنْ أَمَرْتَهُمْ لَيَخْرُجُنَّ ۖ قُلْ لَا تُقْسِمُوا ۖ طَاعَةٌ مَعْرُوفَةٌ ۚ إِنَّ اللَّهَ خَبِيرٌ بِمَا تَعْمَلُونَ', 24, 53, 18, 72, 356, 'آنها با نهایت تأکید سوگند یاد کردند که اگر به آنان فرمان دهی، (از خانه و اموال خود) بیرون می‌روند (و جان را در طبق اخلاص گذارده تقدیم می‌کنند)؛ بگو: «سوگند یاد نکنید؛ شما طاعت خالصانه نشان دهید که خداوند به آنچه انجام می‌دهید آگاه است!»'),
(2845, 2845, 'قُلْ أَطِيعُوا اللَّهَ وَأَطِيعُوا الرَّسُولَ ۖ فَإِنْ تَوَلَّوْا فَإِنَّمَا عَلَيْهِ مَا حُمِّلَ وَعَلَيْكُمْ مَا حُمِّلْتُمْ ۖ وَإِنْ تُطِيعُوهُ تَهْتَدُوا ۚ وَمَا عَلَى الرَّسُولِ إِلَّا الْبَلَاغُ الْمُبِينُ', 24, 54, 18, 72, 357, 'بگو: «خدا را اطاعت کنید، و از پیامبرش فرمان برید! و اگر سرپیچی نمایید، پیامبر مسؤول اعمال خویش است و شما مسؤول اعمال خود! امّا اگر از او اطاعت کنید، هدایت خواهید شد؛ و بر پیامبر چیزی جز رساندن آشکار نیست!»'),
(2846, 2846, 'وَعَدَ اللَّهُ الَّذِينَ آمَنُوا مِنْكُمْ وَعَمِلُوا الصَّالِحَاتِ لَيَسْتَخْلِفَنَّهُمْ فِي الْأَرْضِ كَمَا اسْتَخْلَفَ الَّذِينَ مِنْ قَبْلِهِمْ وَلَيُمَكِّنَنَّ لَهُمْ دِينَهُمُ الَّذِي ارْتَضَىٰ لَهُمْ وَلَيُبَدِّلَنَّهُمْ مِنْ بَعْدِ خَوْفِهِمْ أَمْنًا ۚ يَعْبُدُونَنِي لَا يُشْرِكُونَ بِي شَيْئًا ۚ وَمَنْ كَفَرَ بَعْدَ ذَٰلِكَ فَأُولَٰئِكَ هُمُ الْفَاسِقُونَ', 24, 55, 18, 72, 357, 'خداوند به کسانی از شما که ایمان آورده و کارهای شایسته انجام داده‌اند وعده می‌دهد که قطعاً آنان را حکمران روی زمین خواهد کرد، همان گونه که به پیشینیان آنها خلافت روی زمین را بخشید؛ و دین و آیینی را که برای آنان پسندیده، پابرجا و ریشه‌دار خواهد ساخت؛ و ترسشان را به امنیّت و آرامش مبدّل می‌کند، آنچنان که تنها مرا می پرستند و چیزی را شریک من نخواهند ساخت. و کسانی که پس از آن کافر شوند، آنها فاسقانند.'),
(2847, 2847, 'وَأَقِيمُوا الصَّلَاةَ وَآتُوا الزَّكَاةَ وَأَطِيعُوا الرَّسُولَ لَعَلَّكُمْ تُرْحَمُونَ', 24, 56, 18, 72, 357, 'و نماز را برپا دارید، و زکات را بدهید، و رسول (خدا) را اطاعت کنید تا مشمول رحمت (او) شوید.'),
(2848, 2848, 'لَا تَحْسَبَنَّ الَّذِينَ كَفَرُوا مُعْجِزِينَ فِي الْأَرْضِ ۚ وَمَأْوَاهُمُ النَّارُ ۖ وَلَبِئْسَ الْمَصِيرُ', 24, 57, 18, 72, 357, 'گمان مبر کافران می‌توانند از چنگال مجازات الهی در زمین فرار کنند! جایگاه آنان آتش است، و چه بد جایگاهی است!'),
(2849, 2849, 'يَا أَيُّهَا الَّذِينَ آمَنُوا لِيَسْتَأْذِنْكُمُ الَّذِينَ مَلَكَتْ أَيْمَانُكُمْ وَالَّذِينَ لَمْ يَبْلُغُوا الْحُلُمَ مِنْكُمْ ثَلَاثَ مَرَّاتٍ ۚ مِنْ قَبْلِ صَلَاةِ الْفَجْرِ وَحِينَ تَضَعُونَ ثِيَابَكُمْ مِنَ الظَّهِيرَةِ وَمِنْ بَعْدِ صَلَاةِ الْعِشَاءِ ۚ ثَلَاثُ عَوْرَاتٍ لَكُمْ ۚ لَيْسَ عَلَيْكُمْ وَلَا عَلَيْهِمْ جُنَاحٌ بَعْدَهُنَّ ۚ طَوَّافُونَ عَلَيْكُمْ بَعْضُكُمْ عَلَىٰ بَعْضٍ ۚ كَذَٰلِكَ يُبَيِّنُ اللَّهُ لَكُمُ الْآيَاتِ ۗ وَاللَّهُ عَلِيمٌ حَكِيمٌ', 24, 58, 18, 72, 357, 'ای کسانی که ایمان آورده‌اید! بردگان شما، و همچنین کودکانتان که به حدّ بلوغ نرسیده‌اند، در سه وقت باید از شما اجازه بگیرند: پیش از نماز صبح، و نیمروز هنگامی که لباسهای (معمولی) خود را بیرون می‌آورید، و بعد از نماز عشا؛ این سه وقت خصوصی برای شماست؛ امّا بعد از این سه وقت، گناهی بر شما و بر آنان نیست (که بدون اذن وارد شوند) و بر گرد یکدیگر بگردید (و با صفا و صمیمیّت به یکدیگر خدمت نمایید). این گونه خداوند آیات را برای شما بیان می‌کند، و خداوند دانا و حکیم است!'),
(2850, 2850, 'وَإِذَا بَلَغَ الْأَطْفَالُ مِنْكُمُ الْحُلُمَ فَلْيَسْتَأْذِنُوا كَمَا اسْتَأْذَنَ الَّذِينَ مِنْ قَبْلِهِمْ ۚ كَذَٰلِكَ يُبَيِّنُ اللَّهُ لَكُمْ آيَاتِهِ ۗ وَاللَّهُ عَلِيمٌ حَكِيمٌ', 24, 59, 18, 72, 358, 'و هنگامی که اطفال شما به سنّ بلوغ رسند باید اجازه بگیرند، همان گونه که اشخاصی که پیش از آنان بودند اجازه می‌گرفتند؛ اینچنین خداوند آیاتش را برای شما بیان می‌کند، و خدا دانا و حکیم است!'),
(2851, 2851, 'وَالْقَوَاعِدُ مِنَ النِّسَاءِ اللَّاتِي لَا يَرْجُونَ نِكَاحًا فَلَيْسَ عَلَيْهِنَّ جُنَاحٌ أَنْ يَضَعْنَ ثِيَابَهُنَّ غَيْرَ مُتَبَرِّجَاتٍ بِزِينَةٍ ۖ وَأَنْ يَسْتَعْفِفْنَ خَيْرٌ لَهُنَّ ۗ وَاللَّهُ سَمِيعٌ عَلِيمٌ', 24, 60, 18, 72, 358, 'و زنان از کارافتاده‌ای که امید به ازدواج ندارند، گناهی بر آنان نیست که لباسهای (رویین) خود را بر زمین بگذارند، بشرط اینکه در برابر مردم خودآرایی نکنند؛ و اگر خود را بپوشانند برای آنان بهتر است؛ و خداوند شنوا و داناست.'),
(2852, 2852, 'لَيْسَ عَلَى الْأَعْمَىٰ حَرَجٌ وَلَا عَلَى الْأَعْرَجِ حَرَجٌ وَلَا عَلَى الْمَرِيضِ حَرَجٌ وَلَا عَلَىٰ أَنْفُسِكُمْ أَنْ تَأْكُلُوا مِنْ بُيُوتِكُمْ أَوْ بُيُوتِ آبَائِكُمْ أَوْ بُيُوتِ أُمَّهَاتِكُمْ أَوْ بُيُوتِ إِخْوَانِكُمْ أَوْ بُيُوتِ أَخَوَاتِكُمْ أَوْ بُيُوتِ أَعْمَامِكُمْ أَوْ بُيُوتِ عَمَّاتِكُمْ أَوْ بُيُوتِ أَخْوَالِكُمْ أَوْ بُيُوتِ خَالَاتِكُمْ أَوْ مَا مَلَكْتُمْ مَفَاتِحَهُ أَوْ صَدِيقِكُمْ ۚ لَيْسَ عَلَيْكُمْ جُنَاحٌ أَنْ تَأْكُلُوا جَمِيعًا أَوْ أَشْتَاتًا ۚ فَإِذَا دَخَلْتُمْ بُيُوتًا فَسَلِّمُوا عَلَىٰ أَنْفُسِكُمْ تَحِيَّةً مِنْ عِنْدِ اللَّهِ مُبَارَكَةً طَيِّبَةً ۚ كَذَٰلِكَ يُبَيِّنُ اللَّهُ لَكُمُ الْآيَاتِ لَعَلَّكُمْ تَعْقِلُونَ', 24, 61, 18, 72, 358, 'بر نابینا و افراد لنگ و بیمار گناهی نیست (که با شما هم غذا شوند)، و بر شما نیز گناهی نیست که از خانه‌های خودتان =خانه‌های فرزندان یا همسرانتان که خانه خود شما محسوب می‌شود بدون اجازه خاصّی‌ غذا بخورید؛ و همچنین خانه‌های پدرانتان، یا خانه‌های مادرانتان، یا خانه‌های برادرانتان، یا خانه‌های خواهرانتان، یا خانه‌های عموهایتان، یا خانه‌های عمّه‌هایتان، یا خانه‌های داییهایتان، یا خانه‌های خاله‌هایتان، یا خانه‌ای که کلیدش در اختیار شماست، یا خانه‌های دوستانتان، بر شما گناهی نیست که بطور دسته‌جمعی یا جداگانه غذا بخورید؛ و هنگامی که داخل خانه‌ای شدید، بر خویشتن سلام کنید، سلام و تحیّتی از سوی خداوند، سلامی پربرکت و پاکیزه! این گونه خداوند آیات را برای شما روشن می‌کند، باشد که بیندیشید!'),
(2853, 2853, 'إِنَّمَا الْمُؤْمِنُونَ الَّذِينَ آمَنُوا بِاللَّهِ وَرَسُولِهِ وَإِذَا كَانُوا مَعَهُ عَلَىٰ أَمْرٍ جَامِعٍ لَمْ يَذْهَبُوا حَتَّىٰ يَسْتَأْذِنُوهُ ۚ إِنَّ الَّذِينَ يَسْتَأْذِنُونَكَ أُولَٰئِكَ الَّذِينَ يُؤْمِنُونَ بِاللَّهِ وَرَسُولِهِ ۚ فَإِذَا اسْتَأْذَنُوكَ لِبَعْضِ شَأْنِهِمْ فَأْذَنْ لِمَنْ شِئْتَ مِنْهُمْ وَاسْتَغْفِرْ لَهُمُ اللَّهَ ۚ إِنَّ اللَّهَ غَفُورٌ رَحِيمٌ', 24, 62, 18, 72, 359, 'مؤمنان واقعی کسانی هستند که به خدا و رسولش ایمان آورده‌اند و هنگامی که در کار مهمّی با او باشند، بی‌اجازه او جایی نمی‌روند؛ کسانی که از تو اجازه می‌گیرند، براستی به خدا و پیامبرش ایمان آورده‌اند! در این صورت، هر گاه برای بعضی کارهای مهمّ خود از تو اجازه بخواهند، به هر یک از آنان که می‌خواهی (و صلاح می‌بینی) اجازه ده، و برایشان از خدا آمرزش بخواه که خداوند آمرزنده و مهربان است!'),
(2854, 2854, 'لَا تَجْعَلُوا دُعَاءَ الرَّسُولِ بَيْنَكُمْ كَدُعَاءِ بَعْضِكُمْ بَعْضًا ۚ قَدْ يَعْلَمُ اللَّهُ الَّذِينَ يَتَسَلَّلُونَ مِنْكُمْ لِوَاذًا ۚ فَلْيَحْذَرِ الَّذِينَ يُخَالِفُونَ عَنْ أَمْرِهِ أَنْ تُصِيبَهُمْ فِتْنَةٌ أَوْ يُصِيبَهُمْ عَذَابٌ أَلِيمٌ', 24, 63, 18, 72, 359, 'صدا کردن پیامبر را در میان خود، مانند صدا کردن یکدیگر قرار ندهی؛ خداوند کسانی از شما را که پشت سر دیگران پنهان می‌شوند، و یکی پس از دیگری فرار می‌کنند می‌داند! پس آنان که فرمان او را مخالفت می‌کنند، باید بترسند از اینکه فتنه‌ای دامنشان را بگیرد، یا عذابی دردناک به آنها برسد!'),
(2855, 2855, 'أَلَا إِنَّ لِلَّهِ مَا فِي السَّمَاوَاتِ وَالْأَرْضِ ۖ قَدْ يَعْلَمُ مَا أَنْتُمْ عَلَيْهِ وَيَوْمَ يُرْجَعُونَ إِلَيْهِ فَيُنَبِّئُهُمْ بِمَا عَمِلُوا ۗ وَاللَّهُ بِكُلِّ شَيْءٍ عَلِيمٌ', 24, 64, 18, 72, 359, 'آگاه باشید که برای خداست آنچه در آسمانها و زمین است؛ او می‌داند آنچه را که شما بر آن هستید، و (می‌داند) روزی را که بسوی او بازمی‌گردند؛ و (در آن روز) آنها را از اعمالی که انجام دادند آگاه می‌سازد؛ و خداوند به هر چیزی داناست!'),
(2856, 2856, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ تَبَارَكَ الَّذِي نَزَّلَ الْفُرْقَانَ عَلَىٰ عَبْدِهِ لِيَكُونَ لِلْعَالَمِينَ نَذِيرًا', 25, 1, 18, 72, 359, 'زوال ناپذیر و پر برکت است کسی که قرآن را بر بنده‌اش نازل کرد تا بیم‌دهنده جهانیان باشد.'),
(2857, 2857, 'الَّذِي لَهُ مُلْكُ السَّمَاوَاتِ وَالْأَرْضِ وَلَمْ يَتَّخِذْ وَلَدًا وَلَمْ يَكُنْ لَهُ شَرِيكٌ فِي الْمُلْكِ وَخَلَقَ كُلَّ شَيْءٍ فَقَدَّرَهُ تَقْدِيرًا', 25, 2, 18, 72, 359, 'خداوندی که حکومت آسمانها و زمین از آن اوست، و فرزندی برای خود انتخاب نکرد، و همتایی در حکومت و مالکیّت ندارد، و همه چیز را آفرید، و به دقّت اندازه‌گیری نمود!'),
(2858, 2858, 'وَاتَّخَذُوا مِنْ دُونِهِ آلِهَةً لَا يَخْلُقُونَ شَيْئًا وَهُمْ يُخْلَقُونَ وَلَا يَمْلِكُونَ لِأَنْفُسِهِمْ ضَرًّا وَلَا نَفْعًا وَلَا يَمْلِكُونَ مَوْتًا وَلَا حَيَاةً وَلَا نُشُورًا', 25, 3, 18, 72, 360, 'آنان غیر از خداوند معبودانی برای خود برگزیدند؛ معبودانی که چیزی را نمی‌آفرینند، بلکه خودشان مخلوقند، و مالک زیان و سود خویش نیستند، و نه مالک مرگ و حیات و رستاخیز خویشند.'),
(2859, 2859, 'وَقَالَ الَّذِينَ كَفَرُوا إِنْ هَٰذَا إِلَّا إِفْكٌ افْتَرَاهُ وَأَعَانَهُ عَلَيْهِ قَوْمٌ آخَرُونَ ۖ فَقَدْ جَاءُوا ظُلْمًا وَزُورًا', 25, 4, 18, 72, 360, 'و کافران گفتند: «این فقط دروغی است که او ساخته، و گروهی دیگر او را بر این کار یاری داده‌اند.» آنها (با این سخن،) ظلم و دروغ بزرگی را مرتکب شدند.'),
(2860, 2860, 'وَقَالُوا أَسَاطِيرُ الْأَوَّلِينَ اكْتَتَبَهَا فَهِيَ تُمْلَىٰ عَلَيْهِ بُكْرَةً وَأَصِيلًا', 25, 5, 18, 72, 360, 'و گفتند: «این همان افسانه‌های پیشینیان است که وی آن را رونویس کرده، و هر صبح و شام بر او املا می‌شود.»'),
(2861, 2861, 'قُلْ أَنْزَلَهُ الَّذِي يَعْلَمُ السِّرَّ فِي السَّمَاوَاتِ وَالْأَرْضِ ۚ إِنَّهُ كَانَ غَفُورًا رَحِيمًا', 25, 6, 18, 72, 360, 'بگو: «کسی آن را نازل کرده که اسرار آسمانها و زمین را می‌داند؛ او (همیشه) آمرزنده و مهربان بوده است!»'),
(2862, 2862, 'وَقَالُوا مَالِ هَٰذَا الرَّسُولِ يَأْكُلُ الطَّعَامَ وَيَمْشِي فِي الْأَسْوَاقِ ۙ لَوْلَا أُنْزِلَ إِلَيْهِ مَلَكٌ فَيَكُونَ مَعَهُ نَذِيرًا', 25, 7, 18, 72, 360, 'و گفتند: «چرا این پیامبر غذا می‌خورد و در بازارها راه می‌رود؟! (نه سنّت فرشتگان را دارد و نه روش شاهان را!) چرا فرشته‌ای بر او نازل نشده که همراه وی مردم را انذار کند (و گواه صدق دعوی او باشد)؟!'),
(2863, 2863, 'أَوْ يُلْقَىٰ إِلَيْهِ كَنْزٌ أَوْ تَكُونُ لَهُ جَنَّةٌ يَأْكُلُ مِنْهَا ۚ وَقَالَ الظَّالِمُونَ إِنْ تَتَّبِعُونَ إِلَّا رَجُلًا مَسْحُورًا', 25, 8, 18, 72, 360, 'یا گنجی (از آسمان) برای او فرستاده شود، یا باغی داشته باشد که از (میوه) آن بخورد (و امرار معاش کند)؟!» و ستمگران گفتند: «شما تنها از مردی مجنون پیروی می‌کنید!»'),
(2864, 2864, 'انْظُرْ كَيْفَ ضَرَبُوا لَكَ الْأَمْثَالَ فَضَلُّوا فَلَا يَسْتَطِيعُونَ سَبِيلًا', 25, 9, 18, 72, 360, 'ببین چگونه برای تو مثلها زدند و گمراه شدند، آن گونه که قدرت پیدا کردن راه را ندارند!'),
(2865, 2865, 'تَبَارَكَ الَّذِي إِنْ شَاءَ جَعَلَ لَكَ خَيْرًا مِنْ ذَٰلِكَ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهَارُ وَيَجْعَلْ لَكَ قُصُورًا', 25, 10, 18, 72, 360, 'زوال ناپذیر و بزرگ است خدایی که اگر بخواهد برای تو بهتر از این قرار می‌دهد: باغهایی که نهرها از زیر درختانش جاری است، و (اگر بخواهد) برای تو کاخهایی مجلّل قرارمی‌دهد.'),
(2866, 2866, 'بَلْ كَذَّبُوا بِالسَّاعَةِ ۖ وَأَعْتَدْنَا لِمَنْ كَذَّبَ بِالسَّاعَةِ سَعِيرًا', 25, 11, 18, 72, 360, '(اینها همه بهانه است) بلکه آنان قیامت را تکذیب کرده‌اند؛ و ما برای کسی که قیامت را تکذیب کند، آتشی شعله‌ور و سوزان فراهم کرده‌ایم!'),
(2867, 2867, 'إِذَا رَأَتْهُمْ مِنْ مَكَانٍ بَعِيدٍ سَمِعُوا لَهَا تَغَيُّظًا وَزَفِيرًا', 25, 12, 18, 72, 361, 'هنگامی که این آتش آنان را از مکانی دور ببیند، صدای وحشتناک و خشم آلودش را که با نفس‌زدن شدید همراه است می‌شنوند.'),
(2868, 2868, 'وَإِذَا أُلْقُوا مِنْهَا مَكَانًا ضَيِّقًا مُقَرَّنِينَ دَعَوْا هُنَالِكَ ثُبُورًا', 25, 13, 18, 72, 361, 'و هنگامی که در جای تنگ و محدودی از آن افکنده شوند در حالی که در غل و زنجیرند، فریاد واویلای آنان بلند می‌شود!'),
(2869, 2869, 'لَا تَدْعُوا الْيَوْمَ ثُبُورًا وَاحِدًا وَادْعُوا ثُبُورًا كَثِيرًا', 25, 14, 18, 72, 361, '(به آنان گفته می‌شود:) امروز یک بار واویلا نگویید، بلکه بسیار واویلا بگویید!'),
(2870, 2870, 'قُلْ أَذَٰلِكَ خَيْرٌ أَمْ جَنَّةُ الْخُلْدِ الَّتِي وُعِدَ الْمُتَّقُونَ ۚ كَانَتْ لَهُمْ جَزَاءً وَمَصِيرًا', 25, 15, 18, 72, 361, '(ای پیامبر!) بگو: «آیا این بهتر است یا بهشت جاویدانی که به پرهیزگاران وعده داده شده؟! بهشتی که پاداش اعمال آنها، و قرارگاهشان است.»'),
(2871, 2871, 'لَهُمْ فِيهَا مَا يَشَاءُونَ خَالِدِينَ ۚ كَانَ عَلَىٰ رَبِّكَ وَعْدًا مَسْئُولًا', 25, 16, 18, 72, 361, 'هر چه بخواهند در آنجا برایشان فراهم است؛ جاودانه در آن خواهند ماند؛ این وعده‌ای است مسلّم که پروردگارت بر عهده گرفته است!'),
(2872, 2872, 'وَيَوْمَ يَحْشُرُهُمْ وَمَا يَعْبُدُونَ مِنْ دُونِ اللَّهِ فَيَقُولُ أَأَنْتُمْ أَضْلَلْتُمْ عِبَادِي هَٰؤُلَاءِ أَمْ هُمْ ضَلُّوا السَّبِيلَ', 25, 17, 18, 72, 361, '(به خاطر بیاور) روزی را که همه آنان و معبودهایی را که غیر از خدا می‌پرستند جمع می‌کند، آنگاه به آنها می‌گوید: «آیا شما این بندگان مرا گمراه کردید یا خود آنان راه را گم کردند؟!»'),
(2873, 2873, 'قَالُوا سُبْحَانَكَ مَا كَانَ يَنْبَغِي لَنَا أَنْ نَتَّخِذَ مِنْ دُونِكَ مِنْ أَوْلِيَاءَ وَلَٰكِنْ مَتَّعْتَهُمْ وَآبَاءَهُمْ حَتَّىٰ نَسُوا الذِّكْرَ وَكَانُوا قَوْمًا بُورًا', 25, 18, 18, 72, 361, '(در پاسخ) می‌گویند: «منزّهی تو! برای ما شایسته نبود که غیر از تو اولیایی برگزینیم، ولی آنان و پدرانشان را از نعمتها برخوردار نمودی تا اینکه (به جای شکر نعمت) یاد تو را فراموش کردند و تباه و هلاک شدند.»'),
(2874, 2874, 'فَقَدْ كَذَّبُوكُمْ بِمَا تَقُولُونَ فَمَا تَسْتَطِيعُونَ صَرْفًا وَلَا نَصْرًا ۚ وَمَنْ يَظْلِمْ مِنْكُمْ نُذِقْهُ عَذَابًا كَبِيرًا', 25, 19, 18, 72, 361, '(خداوند به آنان می‌گوید: ببینید) این معبودان، شما را در آنچه می‌گویید تکذیب کردند! اکنون نمی‌توانید عذاب الهی را برطرف بسازید، یا از کسی یاری بطلبید! و هر کس از شما ستم کند، عذاب شدیدی به او می‌چشانیم!'),
(2875, 2875, 'وَمَا أَرْسَلْنَا قَبْلَكَ مِنَ الْمُرْسَلِينَ إِلَّا إِنَّهُمْ لَيَأْكُلُونَ الطَّعَامَ وَيَمْشُونَ فِي الْأَسْوَاقِ ۗ وَجَعَلْنَا بَعْضَكُمْ لِبَعْضٍ فِتْنَةً أَتَصْبِرُونَ ۗ وَكَانَ رَبُّكَ بَصِيرًا', 25, 20, 18, 72, 361, 'ما هیچ یک از رسولان را پیش از تو نفرستادیم مگر اینکه غذا می‌خوردند و در بازارها راه می‌رفتند؛ و بعضی از شما را وسیله امتحان بعضی دیگر قراردادیم، آیا صبر و شکیبایی می‌کنید (و از عهده امتحان برمی‌آیید)؟! و پروردگار تو همواره بصیر و بینا بوده است.'),
(2876, 2876, ' وَقَالَ الَّذِينَ لَا يَرْجُونَ لِقَاءَنَا لَوْلَا أُنْزِلَ عَلَيْنَا الْمَلَائِكَةُ أَوْ نَرَىٰ رَبَّنَا ۗ لَقَدِ اسْتَكْبَرُوا فِي أَنْفُسِهِمْ وَعَتَوْا عُتُوًّا كَبِيرًا', 25, 21, 19, 73, 362, 'و کسانی که امیدی به دیدار ما ندارند (و رستاخیز را انکار می‌کنند) گفتند: «چرا فرشتگان بر ما نازل نشدند و یا پروردگارمان را با چشم خود نمی‌بینیم؟!» آنها درباره خود تکبّر ورزیدند و طغیان بزرگی کردند!'),
(2877, 2877, 'يَوْمَ يَرَوْنَ الْمَلَائِكَةَ لَا بُشْرَىٰ يَوْمَئِذٍ لِلْمُجْرِمِينَ وَيَقُولُونَ حِجْرًا مَحْجُورًا', 25, 22, 19, 73, 362, '(آنها به آرزوی خود می‌رسند،) امّا روزی که فرشتگان را می‌بینند، روز بشارت برای مجرمان نخواهد بود (بلکه روز مجازات و کیفر آنان است)! و می‌گویند: «ما را امان دهید، ما را معاف دارید!» (امّا سودی ندارد!)'),
(2878, 2878, 'وَقَدِمْنَا إِلَىٰ مَا عَمِلُوا مِنْ عَمَلٍ فَجَعَلْنَاهُ هَبَاءً مَنْثُورًا', 25, 23, 19, 73, 362, 'و ما به سراغ اعمالی که انجام داده‌اند می‌رویم، و همه را همچون ذرّات غبار پراکنده در هوا قرار می‌دهیم!'),
(2879, 2879, 'أَصْحَابُ الْجَنَّةِ يَوْمَئِذٍ خَيْرٌ مُسْتَقَرًّا وَأَحْسَنُ مَقِيلًا', 25, 24, 19, 73, 362, 'بهشتیان در آن روز قرارگاهشان از همه بهتر، و استراحتگاهشان نیکوتر است!'),
(2880, 2880, 'وَيَوْمَ تَشَقَّقُ السَّمَاءُ بِالْغَمَامِ وَنُزِّلَ الْمَلَائِكَةُ تَنْزِيلًا', 25, 25, 19, 73, 362, 'و (بخاطر آور) روزی را که آسمان با ابرها شکافته می‌شود، و فرشتگان نازل می‌گردند.'),
(2881, 2881, 'الْمُلْكُ يَوْمَئِذٍ الْحَقُّ لِلرَّحْمَٰنِ ۚ وَكَانَ يَوْمًا عَلَى الْكَافِرِينَ عَسِيرًا', 25, 26, 19, 73, 362, 'حکومت در آن روز از آن خداوند رحمان است؛ و آن روز، روز سختی برای کافران خواهد بود!'),
(2882, 2882, 'وَيَوْمَ يَعَضُّ الظَّالِمُ عَلَىٰ يَدَيْهِ يَقُولُ يَا لَيْتَنِي اتَّخَذْتُ مَعَ الرَّسُولِ سَبِيلًا', 25, 27, 19, 73, 362, 'و (به خاطر آور) روزی را که ستمکار دست خود را (از شدّت حسرت) به دندان می‌گزد و می‌گوید: «ای کاش با رسول (خدا) راهی برگزیده بودم!'),
(2883, 2883, 'يَا وَيْلَتَىٰ لَيْتَنِي لَمْ أَتَّخِذْ فُلَانًا خَلِيلًا', 25, 28, 19, 73, 362, 'ای وای بر من، کاش فلان (شخص گمراه) را دوست خود انتخاب نکرده بودم!'),
(2884, 2884, 'لَقَدْ أَضَلَّنِي عَنِ الذِّكْرِ بَعْدَ إِذْ جَاءَنِي ۗ وَكَانَ الشَّيْطَانُ لِلْإِنْسَانِ خَذُولًا', 25, 29, 19, 73, 362, 'او مرا از یادآوری (حق) گمراه ساخت بعد از آنکه (یاد حق) به سراغ من آمده بود!» و شیطان همیشه خوارکنند* انسان بوده است!'),
(2885, 2885, 'وَقَالَ الرَّسُولُ يَا رَبِّ إِنَّ قَوْمِي اتَّخَذُوا هَٰذَا الْقُرْآنَ مَهْجُورًا', 25, 30, 19, 73, 362, 'و پیامبر عرضه داشت: «پروردگارا! قوم من قرآن را رها کردند».'),
(2886, 2886, 'وَكَذَٰلِكَ جَعَلْنَا لِكُلِّ نَبِيٍّ عَدُوًّا مِنَ الْمُجْرِمِينَ ۗ وَكَفَىٰ بِرَبِّكَ هَادِيًا وَنَصِيرًا', 25, 31, 19, 73, 362, '(آری،) این گونه برای هر پیامبری دشمنی از مجرمان قرار دادیم؛ امّا (برای تو) همین بس که پروردگارت هادی و یاور (تو) باشد!'),
(2887, 2887, 'وَقَالَ الَّذِينَ كَفَرُوا لَوْلَا نُزِّلَ عَلَيْهِ الْقُرْآنُ جُمْلَةً وَاحِدَةً ۚ كَذَٰلِكَ لِنُثَبِّتَ بِهِ فُؤَادَكَ ۖ وَرَتَّلْنَاهُ تَرْتِيلًا', 25, 32, 19, 73, 362, 'و کافران گفتند: «چرا قرآن یکجا بر او نازل نمی‌شود؟!» این بخاطر آن است که قلب تو را بوسیله آن محکم داریم، و (از این رو) آن را به تدریج بر تو خواندیم.'),
(2888, 2888, 'وَلَا يَأْتُونَكَ بِمَثَلٍ إِلَّا جِئْنَاكَ بِالْحَقِّ وَأَحْسَنَ تَفْسِيرًا', 25, 33, 19, 73, 363, 'آنان هیچ مثلی برای تو نمی‌آورند مگر اینکه ما حق را برای تو می‌آوریم، و تفسیری بهتر (و پاسخی دندان شکن که در برابر آن ناتوان شوند)!'),
(2889, 2889, 'الَّذِينَ يُحْشَرُونَ عَلَىٰ وُجُوهِهِمْ إِلَىٰ جَهَنَّمَ أُولَٰئِكَ شَرٌّ مَكَانًا وَأَضَلُّ سَبِيلًا', 25, 34, 19, 73, 363, '(تو گمراه نیستی،) آنان که بر صورتهایشان بسوی جهنم محشور می‌شوند، بدترین محل را دارند و گمراه‌ترین افرادند!'),
(2890, 2890, 'وَلَقَدْ آتَيْنَا مُوسَى الْكِتَابَ وَجَعَلْنَا مَعَهُ أَخَاهُ هَارُونَ وَزِيرًا', 25, 35, 19, 73, 363, 'و ما به موسی کتاب (آسمانی) دادیم؛ و برادرش هارون را یاور او قرار دادیم؛'),
(2891, 2891, 'فَقُلْنَا اذْهَبَا إِلَى الْقَوْمِ الَّذِينَ كَذَّبُوا بِآيَاتِنَا فَدَمَّرْنَاهُمْ تَدْمِيرًا', 25, 36, 19, 73, 363, 'و گفتیم: «به سوی این قوم که آیات ما را تکذیب کردند بروید!» (امّا آن مردم به مخالفت برخاستند) و ما به شدّت آنان را درهم کوبیدیم!'),
(2892, 2892, 'وَقَوْمَ نُوحٍ لَمَّا كَذَّبُوا الرُّسُلَ أَغْرَقْنَاهُمْ وَجَعَلْنَاهُمْ لِلنَّاسِ آيَةً ۖ وَأَعْتَدْنَا لِلظَّالِمِينَ عَذَابًا أَلِيمًا', 25, 37, 19, 73, 363, 'و قوم نوح را هنگامی که رسولان (ما) را تکذیب کردند غرق نمودیم، و آنان را درس عبرتی برای مردم قرار دادیم؛ و برای ستمگران عذاب دردناکی فراهم ساخته‌ایم!'),
(2893, 2893, 'وَعَادًا وَثَمُودَ وَأَصْحَابَ الرَّسِّ وَقُرُونًا بَيْنَ ذَٰلِكَ كَثِيرًا', 25, 38, 19, 73, 363, '(همچنین) قوم عاد و ثمود و اصحاب الرّس = گروهی که درختان صنوبر را می‌پرستیدند و اقوام بسیار دیگری را که در این میان بودند، هلاک کردیم!'),
(2894, 2894, 'وَكُلًّا ضَرَبْنَا لَهُ الْأَمْثَالَ ۖ وَكُلًّا تَبَّرْنَا تَتْبِيرًا', 25, 39, 19, 73, 363, 'و برای هر یک از آنها مثلها زدیم؛ و (چون سودی نداد،) همگی را نابود کردیم!'),
(2895, 2895, 'وَلَقَدْ أَتَوْا عَلَى الْقَرْيَةِ الَّتِي أُمْطِرَتْ مَطَرَ السَّوْءِ ۚ أَفَلَمْ يَكُونُوا يَرَوْنَهَا ۚ بَلْ كَانُوا لَا يَرْجُونَ نُشُورًا', 25, 40, 19, 73, 363, 'آنها = مشرکان مکّه‌ از کنار شهری که باران شرّ = بارانی از سنگهای آسمانی‌ بر آن باریده بود = دیار قوم لوط گذشتند؛ آیا آن را نمی‌دیدند؟! (آری، می‌دیدند) ولی به رستاخیز ایمان نداشتند!'),
(2896, 2896, 'وَإِذَا رَأَوْكَ إِنْ يَتَّخِذُونَكَ إِلَّا هُزُوًا أَهَٰذَا الَّذِي بَعَثَ اللَّهُ رَسُولًا', 25, 41, 19, 73, 363, 'و هنگامی که تو را می‌بینند، تنها به باد استهزایت می‌گیرند (و می‌گویند:) آیا این همان کسی است که خدا او را بعنوان پیامبر برانگیخته است؟!'),
(2897, 2897, 'إِنْ كَادَ لَيُضِلُّنَا عَنْ آلِهَتِنَا لَوْلَا أَنْ صَبَرْنَا عَلَيْهَا ۚ وَسَوْفَ يَعْلَمُونَ حِينَ يَرَوْنَ الْعَذَابَ مَنْ أَضَلُّ سَبِيلًا', 25, 42, 19, 73, 363, 'اگر ما بر پرستش خدایانمان استقامت نمی‌کردیم، بیم آن می‌رفت که ما را گمراه سازد! امّا هنگامی که عذاب الهی را ببینند، بزودی می‌فهمند چه کسی گمراهتر بوده است!'),
(2898, 2898, 'أَرَأَيْتَ مَنِ اتَّخَذَ إِلَٰهَهُ هَوَاهُ أَفَأَنْتَ تَكُونُ عَلَيْهِ وَكِيلًا', 25, 43, 19, 73, 363, 'آیا دیدی کسی را که هوای نفسش را معبود خود برگزیده است؟! آیا تو می‌توانی او را هدایت کنی (یا به دفاع از او برخیزی)؟!'),
(2899, 2899, 'أَمْ تَحْسَبُ أَنَّ أَكْثَرَهُمْ يَسْمَعُونَ أَوْ يَعْقِلُونَ ۚ إِنْ هُمْ إِلَّا كَالْأَنْعَامِ ۖ بَلْ هُمْ أَضَلُّ سَبِيلًا', 25, 44, 19, 73, 364, 'آیا گمان می‌بری بیشتر آنان می‌شنوند یا می‌فهمند؟! آنان فقط همچون چهارپایانند، بلکه گمراهترند!'),
(2900, 2900, 'أَلَمْ تَرَ إِلَىٰ رَبِّكَ كَيْفَ مَدَّ الظِّلَّ وَلَوْ شَاءَ لَجَعَلَهُ سَاكِنًا ثُمَّ جَعَلْنَا الشَّمْسَ عَلَيْهِ دَلِيلًا', 25, 45, 19, 73, 364, 'آیا ندیدی چگونه پروردگارت سایه را گسترده ساخت؟! و اگر می‌خواست آن را ساکن قرار می‌داد؛ سپس خورشید را بر وجود آن دلیل قرار دادیم!'),
(2901, 2901, 'ثُمَّ قَبَضْنَاهُ إِلَيْنَا قَبْضًا يَسِيرًا', 25, 46, 19, 73, 364, 'سپس آن را آهسته جمع می‌کنیم (و نظام سایه و آفتاب را حاکم می‌سازیم)!'),
(2902, 2902, 'وَهُوَ الَّذِي جَعَلَ لَكُمُ اللَّيْلَ لِبَاسًا وَالنَّوْمَ سُبَاتًا وَجَعَلَ النَّهَارَ نُشُورًا', 25, 47, 19, 73, 364, 'او کسی است که شب را برای شما لباس قرار داد، و خواب را مایه استراحت، و روز را وسیله حرکت و حیات!'),
(2903, 2903, 'وَهُوَ الَّذِي أَرْسَلَ الرِّيَاحَ بُشْرًا بَيْنَ يَدَيْ رَحْمَتِهِ ۚ وَأَنْزَلْنَا مِنَ السَّمَاءِ مَاءً طَهُورًا', 25, 48, 19, 73, 364, 'او کسی است که بادها را بشارتگرانی پیش از رحمتش فرستاد، و از آسمان آبی پاک‌کننده نازل کردیم...'),
(2904, 2904, 'لِنُحْيِيَ بِهِ بَلْدَةً مَيْتًا وَنُسْقِيَهُ مِمَّا خَلَقْنَا أَنْعَامًا وَأَنَاسِيَّ كَثِيرًا', 25, 49, 19, 73, 364, 'تا بوسیله آن، سرزمین مرده‌ای را زنده کنیم؛ و آن را به مخلوقاتی که آفریده‌ایم -چهارپایان و انسانهای بسیار- می‌نوشانیم.'),
(2905, 2905, 'وَلَقَدْ صَرَّفْنَاهُ بَيْنَهُمْ لِيَذَّكَّرُوا فَأَبَىٰ أَكْثَرُ النَّاسِ إِلَّا كُفُورًا', 25, 50, 19, 73, 364, 'ما این آیات را بصورتهای گوناگون برای آنان بیان کردیم تا متذکّر شوند، ولی بیشتر مردم از هر کاری جز انکار و کفر ابا دارند.'),
(2906, 2906, 'وَلَوْ شِئْنَا لَبَعَثْنَا فِي كُلِّ قَرْيَةٍ نَذِيرًا', 25, 51, 19, 73, 364, 'و اگر می‌خواستیم، در هر شهر و دیاری بیم‌دهنده‌ای برمی‌انگیختیم (ولی این کار لزومی نداشت).'),
(2907, 2907, 'فَلَا تُطِعِ الْكَافِرِينَ وَجَاهِدْهُمْ بِهِ جِهَادًا كَبِيرًا', 25, 52, 19, 73, 364, 'بنابر این از کافران اطاعت مکن، و بوسیله آن = قرآن‌ با آنان جهاد بزرگی بنما!'),
(2908, 2908, ' وَهُوَ الَّذِي مَرَجَ الْبَحْرَيْنِ هَٰذَا عَذْبٌ فُرَاتٌ وَهَٰذَا مِلْحٌ أُجَاجٌ وَجَعَلَ بَيْنَهُمَا بَرْزَخًا وَحِجْرًا مَحْجُورًا', 25, 53, 19, 73, 364, 'او کسی است که دو دریا را در کنار هم قرار داد؛ یکی گوارا و شیرین، و دیگر شور و تلخ؛ و در میان آنها برزخی قرار داد تا با هم مخلوط نشوند (گویی هر یک به دیگری می‌گوید:) دور باش و نزدیک نیا!'),
(2909, 2909, 'وَهُوَ الَّذِي خَلَقَ مِنَ الْمَاءِ بَشَرًا فَجَعَلَهُ نَسَبًا وَصِهْرًا ۗ وَكَانَ رَبُّكَ قَدِيرًا', 25, 54, 19, 73, 364, 'او کسی است که از آب، انسانی را آفرید؛ سپس او را نسب و سبب قرار داد (و نسل او را از این دو طریق گسترش داد)؛ و پروردگار تو همواره توانا بوده است.'),
(2910, 2910, 'وَيَعْبُدُونَ مِنْ دُونِ اللَّهِ مَا لَا يَنْفَعُهُمْ وَلَا يَضُرُّهُمْ ۗ وَكَانَ الْكَافِرُ عَلَىٰ رَبِّهِ ظَهِيرًا', 25, 55, 19, 73, 364, 'آنان جز خدا چیزهایی را می‌پرستند که نه به آنان سودی می‌رساند و نه زیانی؛ و کافران همیشه در برابر پروردگارشان (در طریق کفر) پشتیبان یکدیگرند.'),
(2911, 2911, 'وَمَا أَرْسَلْنَاكَ إِلَّا مُبَشِّرًا وَنَذِيرًا', 25, 56, 19, 73, 365, '(ای پیامبر!) ما تو را جز بعنوان بشارت دهنده و انذار کننده نفرستادیم!'),
(2912, 2912, 'قُلْ مَا أَسْأَلُكُمْ عَلَيْهِ مِنْ أَجْرٍ إِلَّا مَنْ شَاءَ أَنْ يَتَّخِذَ إِلَىٰ رَبِّهِ سَبِيلًا', 25, 57, 19, 73, 365, 'بگو: «من در برابر آن (ابلاغ آیین خدا) هیچ گونه پاداشی از شما نمی‌طلبم؛ مگر کسی که بخواهد راهی بسوی پروردگارش برگزیند (این پاداش من است.)»'),
(2913, 2913, 'وَتَوَكَّلْ عَلَى الْحَيِّ الَّذِي لَا يَمُوتُ وَسَبِّحْ بِحَمْدِهِ ۚ وَكَفَىٰ بِهِ بِذُنُوبِ عِبَادِهِ خَبِيرًا', 25, 58, 19, 73, 365, 'و توکّل کن بر آن زنده‌ای که هرگز نمی‌میرد؛ و تسبیح و حمد او را بجا آور؛ و همین بس که او از گناهان بندگانش آگاه است!'),
(2914, 2914, 'الَّذِي خَلَقَ السَّمَاوَاتِ وَالْأَرْضَ وَمَا بَيْنَهُمَا فِي سِتَّةِ أَيَّامٍ ثُمَّ اسْتَوَىٰ عَلَى الْعَرْشِ ۚ الرَّحْمَٰنُ فَاسْأَلْ بِهِ خَبِيرًا', 25, 59, 19, 73, 365, 'همان (خدایی) که آسمانها و زمین و آنچه را میان این دو وجود دارد، در شش روز = شش دوران‌ آفرید؛ سپس بر عرش (قدرت) قرار گرفت (و به تدبیر جهان پرداخت، او خداوند) رحمان است؛ از او بخواه که از همه چیز آگاه است!'),
(2915, 2915, 'وَإِذَا قِيلَ لَهُمُ اسْجُدُوا لِلرَّحْمَٰنِ قَالُوا وَمَا الرَّحْمَٰنُ أَنَسْجُدُ لِمَا تَأْمُرُنَا وَزَادَهُمْ نُفُورًا ۩', 25, 60, 19, 73, 365, 'و هنگامی که به آنان گفته شود: «برای خداوند رحمان سجده کنید!» می‌گویند: «رحمان چیست؟! (ما اصلاً رحمان را نمی‌شناسیم!) آیا برای چیزی سجده کنیم که تو به ما دستور می‌دهی؟!» (این سخن را می‌گویند) و بر نفرتشان افزوده می‌شود!'),
(2916, 2916, 'تَبَارَكَ الَّذِي جَعَلَ فِي السَّمَاءِ بُرُوجًا وَجَعَلَ فِيهَا سِرَاجًا وَقَمَرًا مُنِيرًا', 25, 61, 19, 73, 365, 'جاودان و پربرکت است آن (خدایی) که در آسمان منزلگاه‌هائی برای ستارگان قرار داد؛ و در میان آن، چراغ روشن و ماه تابانی آفرید!'),
(2917, 2917, 'وَهُوَ الَّذِي جَعَلَ اللَّيْلَ وَالنَّهَارَ خِلْفَةً لِمَنْ أَرَادَ أَنْ يَذَّكَّرَ أَوْ أَرَادَ شُكُورًا', 25, 62, 19, 73, 365, 'و او همان کسی است که شب و روز را جانشین یکدیگر قرار داد برای کسی که بخواهد متذکّر شود یا شکرگزاری کند (و آنچه را در روز کوتاهی کرده در شب انجام دهد و به عکس).'),
(2918, 2918, 'وَعِبَادُ الرَّحْمَٰنِ الَّذِينَ يَمْشُونَ عَلَى الْأَرْضِ هَوْنًا وَإِذَا خَاطَبَهُمُ الْجَاهِلُونَ قَالُوا سَلَامًا', 25, 63, 19, 73, 365, 'بندگان (خاص خداوند) رحمان، کسانی هستند که با آرامش و بی‌تکبّر بر زمین راه می‌روند؛ و هنگامی که جاهلان آنها را مخاطب سازند (و سخنان نابخردانه گویند)، به آنها سلام می‌گویند (و با بی‌اعتنایی و بزرگواری می‌گذرند)؛'),
(2919, 2919, 'وَالَّذِينَ يَبِيتُونَ لِرَبِّهِمْ سُجَّدًا وَقِيَامًا', 25, 64, 19, 73, 365, 'کسانی که شبانگاه برای پروردگارشان سجده و قیام می‌کنند؛'),
(2920, 2920, 'وَالَّذِينَ يَقُولُونَ رَبَّنَا اصْرِفْ عَنَّا عَذَابَ جَهَنَّمَ ۖ إِنَّ عَذَابَهَا كَانَ غَرَامًا', 25, 65, 19, 73, 365, 'و کسانی که می‌گویند: «پروردگارا! عذاب جهنم را از ما برطرف گردان، که عذابش سخت و پر دوام است!'),
(2921, 2921, 'إِنَّهَا سَاءَتْ مُسْتَقَرًّا وَمُقَامًا', 25, 66, 19, 73, 365, 'مسلّماً آن (جهنم)، بد جایگاه و بد محلّ اقامتی است!»'),
(2922, 2922, 'وَالَّذِينَ إِذَا أَنْفَقُوا لَمْ يُسْرِفُوا وَلَمْ يَقْتُرُوا وَكَانَ بَيْنَ ذَٰلِكَ قَوَامًا', 25, 67, 19, 73, 365, 'و کسانی که هرگاه انفاق کنند، نه اسراف می‌نمایند و نه سخت‌گیری؛ بلکه در میان این دو، حدّ اعتدالی دارند.'),
(2923, 2923, 'وَالَّذِينَ لَا يَدْعُونَ مَعَ اللَّهِ إِلَٰهًا آخَرَ وَلَا يَقْتُلُونَ النَّفْسَ الَّتِي حَرَّمَ اللَّهُ إِلَّا بِالْحَقِّ وَلَا يَزْنُونَ ۚ وَمَنْ يَفْعَلْ ذَٰلِكَ يَلْقَ أَثَامًا', 25, 68, 19, 73, 366, 'و کسانی که معبود دیگری را با خداوند نمی‌خوانند؛ و انسانی را که خداوند خونش را حرام شمرده، جز بحق نمی‌کشند؛ و زنا نمی‌کنند؛ و هر کس چنین کند، مجازات سختی خواهد دید!'),
(2924, 2924, 'يُضَاعَفْ لَهُ الْعَذَابُ يَوْمَ الْقِيَامَةِ وَيَخْلُدْ فِيهِ مُهَانًا', 25, 69, 19, 73, 366, 'عذاب او در قیامت مضاعف می‌گردد، و همیشه با خواری در آن خواهد ماند!'),
(2925, 2925, 'إِلَّا مَنْ تَابَ وَآمَنَ وَعَمِلَ عَمَلًا صَالِحًا فَأُولَٰئِكَ يُبَدِّلُ اللَّهُ سَيِّئَاتِهِمْ حَسَنَاتٍ ۗ وَكَانَ اللَّهُ غَفُورًا رَحِيمًا', 25, 70, 19, 73, 366, 'مگر کسانی که توبه کنند و ایمان آورند و عمل صالح انجام دهند، که خداوند گناهان آنان را به حسنات مبدّل می‌کند؛ و خداوند همواره آمرزنده و مهربان بوده است!'),
(2926, 2926, 'وَمَنْ تَابَ وَعَمِلَ صَالِحًا فَإِنَّهُ يَتُوبُ إِلَى اللَّهِ مَتَابًا', 25, 71, 19, 73, 366, 'و کسی که توبه کند و عمل صالح انجام دهد، بسوی خدا بازگشت می‌کند (و پاداش خود را از او می‌گیرد.)'),
(2927, 2927, 'وَالَّذِينَ لَا يَشْهَدُونَ الزُّورَ وَإِذَا مَرُّوا بِاللَّغْوِ مَرُّوا كِرَامًا', 25, 72, 19, 73, 366, 'و کسانی که شهادت به باطل نمی‌دهند (و در مجالس باطل شرکت نمی‌کنند)؛ و هنگامی که با لغو و بیهودگی برخورد کنند، بزرگوارانه از آن می‌گذرند.'),
(2928, 2928, 'وَالَّذِينَ إِذَا ذُكِّرُوا بِآيَاتِ رَبِّهِمْ لَمْ يَخِرُّوا عَلَيْهَا صُمًّا وَعُمْيَانًا', 25, 73, 19, 73, 366, 'و کسانی که هرگاه آیات پروردگارشان به آنان گوشزد شود، کر و کور روی آن نمی افتند.'),
(2929, 2929, 'وَالَّذِينَ يَقُولُونَ رَبَّنَا هَبْ لَنَا مِنْ أَزْوَاجِنَا وَذُرِّيَّاتِنَا قُرَّةَ أَعْيُنٍ وَاجْعَلْنَا لِلْمُتَّقِينَ إِمَامًا', 25, 74, 19, 73, 366, 'و کسانی که می‌گویند: «پروردگارا! از همسران و فرزندانمان مایه روشنی چشم ما قرارده، و ما را برای پرهیزگاران پیشوا گردان!»');
INSERT INTO `Quran` (`rowid`, `id`, `Arabic`, `Chapter`, `Verse`, `Juz`, `Hizb`, `Page`, `tr`) VALUES
(2930, 2930, 'أُولَٰئِكَ يُجْزَوْنَ الْغُرْفَةَ بِمَا صَبَرُوا وَيُلَقَّوْنَ فِيهَا تَحِيَّةً وَسَلَامًا', 25, 75, 19, 73, 366, '(آری،) آنها هستند که درجات عالی بهشت در برابر شکیباییشان به آنان پاداش داده می‌شود؛ و در آن، با تحیّت و سلام روبه‌رو می‌شوند.'),
(2931, 2931, 'خَالِدِينَ فِيهَا ۚ حَسُنَتْ مُسْتَقَرًّا وَمُقَامًا', 25, 76, 19, 73, 366, 'در حالی که جاودانه در آن خواهند ماند؛ چه قرارگاه و محلّ اقامت خوبی!'),
(2932, 2932, 'قُلْ مَا يَعْبَأُ بِكُمْ رَبِّي لَوْلَا دُعَاؤُكُمْ ۖ فَقَدْ كَذَّبْتُمْ فَسَوْفَ يَكُونُ لِزَامًا', 25, 77, 19, 73, 366, 'بگو: «پروردگارم برای شما ارجی قائل نیست اگر دعای شما نباشد؛ شما (آیات خدا و پیامبران را) تکذیب کردید، و (این عمل) دامان شما را خواهد گرفت و از شما جدا نخواهد شد!» ظ'),
(2933, 2933, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ طسم', 26, 1, 19, 74, 367, 'طسم‌'),
(2934, 2934, 'تِلْكَ آيَاتُ الْكِتَابِ الْمُبِينِ', 26, 2, 19, 74, 367, 'این آیات کتاب روشنگر است.'),
(2935, 2935, 'لَعَلَّكَ بَاخِعٌ نَفْسَكَ أَلَّا يَكُونُوا مُؤْمِنِينَ', 26, 3, 19, 74, 367, 'گویی می‌خواهی جان خود را از شدّت اندوه از دست دهی بخاطر اینکه آنها ایمان نمی‌آورند!'),
(2936, 2936, 'إِنْ نَشَأْ نُنَزِّلْ عَلَيْهِمْ مِنَ السَّمَاءِ آيَةً فَظَلَّتْ أَعْنَاقُهُمْ لَهَا خَاضِعِينَ', 26, 4, 19, 74, 367, 'اگر ما اراده کنیم، از آسمان بر آنان آیه‌ای نازل می‌کنیم که گردنهایشان در برابر آن خاضع گردد!'),
(2937, 2937, 'وَمَا يَأْتِيهِمْ مِنْ ذِكْرٍ مِنَ الرَّحْمَٰنِ مُحْدَثٍ إِلَّا كَانُوا عَنْهُ مُعْرِضِينَ', 26, 5, 19, 74, 367, 'و هیچ ذکر تازه‌ای از سوی خداوند مهربان برای آنها نمی‌آید مگر اینکه از آن روی‌گردان می‌شوند!'),
(2938, 2938, 'فَقَدْ كَذَّبُوا فَسَيَأْتِيهِمْ أَنْبَاءُ مَا كَانُوا بِهِ يَسْتَهْزِئُونَ', 26, 6, 19, 74, 367, 'آنان تکذیب کردند؛ امّا بزودی اخبار (کیفر) آنچه را استهزا می‌کردند به آنان می‌رسد!'),
(2939, 2939, 'أَوَلَمْ يَرَوْا إِلَى الْأَرْضِ كَمْ أَنْبَتْنَا فِيهَا مِنْ كُلِّ زَوْجٍ كَرِيمٍ', 26, 7, 19, 74, 367, 'آیا آنان به زمین نگاه نکردند که چقدر از انواع گیاهان پرارزش در آن رویاندیم؟!'),
(2940, 2940, 'إِنَّ فِي ذَٰلِكَ لَآيَةً ۖ وَمَا كَانَ أَكْثَرُهُمْ مُؤْمِنِينَ', 26, 8, 19, 74, 367, 'در این، نشانه روشنی است (بر وجود خدا)؛ ولی بیشترشان هرگز مؤمن نبوده‌اند!'),
(2941, 2941, 'وَإِنَّ رَبَّكَ لَهُوَ الْعَزِيزُ الرَّحِيمُ', 26, 9, 19, 74, 367, 'و پروردگار تو عزیز و رحیم است!'),
(2942, 2942, 'وَإِذْ نَادَىٰ رَبُّكَ مُوسَىٰ أَنِ ائْتِ الْقَوْمَ الظَّالِمِينَ', 26, 10, 19, 74, 367, '(به خاطر بیاور) هنگامی را که پروردگارت موسی را ندا داد که به سراغ قوم ستمگر برو...'),
(2943, 2943, 'قَوْمَ فِرْعَوْنَ ۚ أَلَا يَتَّقُونَ', 26, 11, 19, 74, 367, 'قوم فرعون، آیا آنان (از مخالفت فرمان پروردگار) پرهیز نمی‌کنند؟!'),
(2944, 2944, 'قَالَ رَبِّ إِنِّي أَخَافُ أَنْ يُكَذِّبُونِ', 26, 12, 19, 74, 367, '(موسی) عرض کرد: «پروردگارا! از آن بیم دارم که مرا تکذیب کنند،'),
(2945, 2945, 'وَيَضِيقُ صَدْرِي وَلَا يَنْطَلِقُ لِسَانِي فَأَرْسِلْ إِلَىٰ هَارُونَ', 26, 13, 19, 74, 367, 'و سینه‌ام تنگ شود، و زبانم بقدر کافی گویا نیست؛ (برادرم) هارون را نیز رسالت ده (تا مرا یاری کند)!'),
(2946, 2946, 'وَلَهُمْ عَلَيَّ ذَنْبٌ فَأَخَافُ أَنْ يَقْتُلُونِ', 26, 14, 19, 74, 367, 'و آنان (به اعتقاد خودشان) بر گردن من گناهی دارند؛ می‌ترسم مرا بکشند (و این رسالت به پایان نرسد)!'),
(2947, 2947, 'قَالَ كَلَّا ۖ فَاذْهَبَا بِآيَاتِنَا ۖ إِنَّا مَعَكُمْ مُسْتَمِعُونَ', 26, 15, 19, 74, 367, 'فرمود: «چنین نیست، (آنان کاری نمی‌توانند انجام دهند)! شما هر دو با آیات ما (برای هدایتشان) بروید؛ ما با شما هستیم و (سخنانتان را) می‌شنویم!'),
(2948, 2948, 'فَأْتِيَا فِرْعَوْنَ فَقُولَا إِنَّا رَسُولُ رَبِّ الْعَالَمِينَ', 26, 16, 19, 74, 367, 'به سراغ فرعون بروید و بگویید: ما فرستاده پروردگار جهانیان هستیم؛'),
(2949, 2949, 'أَنْ أَرْسِلْ مَعَنَا بَنِي إِسْرَائِيلَ', 26, 17, 19, 74, 367, 'بنی اسرائیل را با ما بفرست!» (آنها به سراغ فرعون آمدند)؛'),
(2950, 2950, 'قَالَ أَلَمْ نُرَبِّكَ فِينَا وَلِيدًا وَلَبِثْتَ فِينَا مِنْ عُمُرِكَ سِنِينَ', 26, 18, 19, 74, 367, '(فرعون) گفت: «آیا ما تو را در کودکی در میان خود پرورش ندادیم، و سالهایی از زندگیت را در میان ما نبودی؟!'),
(2951, 2951, 'وَفَعَلْتَ فَعْلَتَكَ الَّتِي فَعَلْتَ وَأَنْتَ مِنَ الْكَافِرِينَ', 26, 19, 19, 74, 367, 'و سرانجام، آن کارت را (که نمی‌بایست انجام دهی) انجام دادی (و یک نفر از ما را کشتی)، و تو از ناسپاسانی!»'),
(2952, 2952, 'قَالَ فَعَلْتُهَا إِذًا وَأَنَا مِنَ الضَّالِّينَ', 26, 20, 19, 74, 368, '(موسی) گفت: «من آن کار را انجام دادم در حالی که از بی‌خبران بودم!'),
(2953, 2953, 'فَفَرَرْتُ مِنْكُمْ لَمَّا خِفْتُكُمْ فَوَهَبَ لِي رَبِّي حُكْمًا وَجَعَلَنِي مِنَ الْمُرْسَلِينَ', 26, 21, 19, 74, 368, 'پس هنگامی که از شما ترسیدم فرار کردم؛ و پروردگارم به من حکمت و دانش بخشید، و مرا از پیامبران قرار داد!'),
(2954, 2954, 'وَتِلْكَ نِعْمَةٌ تَمُنُّهَا عَلَيَّ أَنْ عَبَّدْتَ بَنِي إِسْرَائِيلَ', 26, 22, 19, 74, 368, 'آیا این منتّی است که تو بر من می‌گذاری که بنی اسرائیل را برده خود ساخته‌ای؟!»'),
(2955, 2955, 'قَالَ فِرْعَوْنُ وَمَا رَبُّ الْعَالَمِينَ', 26, 23, 19, 74, 368, 'فرعون گفت: «پروردگار عالمیان چیست؟!»'),
(2956, 2956, 'قَالَ رَبُّ السَّمَاوَاتِ وَالْأَرْضِ وَمَا بَيْنَهُمَا ۖ إِنْ كُنْتُمْ مُوقِنِينَ', 26, 24, 19, 74, 368, '(موسی) گفت: «پروردگار آسمانها و زمین و آنچه میان آن دو است، اگر اهل یقین هستید!».'),
(2957, 2957, 'قَالَ لِمَنْ حَوْلَهُ أَلَا تَسْتَمِعُونَ', 26, 25, 19, 74, 368, '(فرعون) به اطرافیانش گفت: «آیا نمی‌شنوید (این مرد چه می‌گوید)؟!»'),
(2958, 2958, 'قَالَ رَبُّكُمْ وَرَبُّ آبَائِكُمُ الْأَوَّلِينَ', 26, 26, 19, 74, 368, '(موسی) گفت: «او پروردگار شما و پروردگار نیاکان شماست!»'),
(2959, 2959, 'قَالَ إِنَّ رَسُولَكُمُ الَّذِي أُرْسِلَ إِلَيْكُمْ لَمَجْنُونٌ', 26, 27, 19, 74, 368, '(فرعون) گفت: «پیامبری که بسوی شما فرستاده شده مسلّماً دیوانه است!»'),
(2960, 2960, 'قَالَ رَبُّ الْمَشْرِقِ وَالْمَغْرِبِ وَمَا بَيْنَهُمَا ۖ إِنْ كُنْتُمْ تَعْقِلُونَ', 26, 28, 19, 74, 368, '(موسی) گفت: «او پروردگار مشرق و مغرب و آنچه میان آن دو است می‌باشد، اگر شما عقل و اندیشه خود را به کار می‌گرفتید!»'),
(2961, 2961, 'قَالَ لَئِنِ اتَّخَذْتَ إِلَٰهًا غَيْرِي لَأَجْعَلَنَّكَ مِنَ الْمَسْجُونِينَ', 26, 29, 19, 74, 368, '(فرعون خشمگین شد و) گفت: «اگر معبودی غیر از من برگزینی، تو را از زندانیان قرار خواهم داد!»'),
(2962, 2962, 'قَالَ أَوَلَوْ جِئْتُكَ بِشَيْءٍ مُبِينٍ', 26, 30, 19, 74, 368, '(موسی) گفت: «حتّی اگر نشانه آشکاری برای تو بیاورم (باز ایمان نمی‌آوری)؟!»'),
(2963, 2963, 'قَالَ فَأْتِ بِهِ إِنْ كُنْتَ مِنَ الصَّادِقِينَ', 26, 31, 19, 74, 368, 'گفت: «اگر راست می‌گویی آن را بیاور!»'),
(2964, 2964, 'فَأَلْقَىٰ عَصَاهُ فَإِذَا هِيَ ثُعْبَانٌ مُبِينٌ', 26, 32, 19, 74, 368, 'در این هنگام موسی عصای خود را افکند، و ناگهان مار عظیم و آشکاری شد؛'),
(2965, 2965, 'وَنَزَعَ يَدَهُ فَإِذَا هِيَ بَيْضَاءُ لِلنَّاظِرِينَ', 26, 33, 19, 74, 368, 'و دست خود را (در گریبان فرو برد و) بیرون آورد، و در برابر بینندگان سفید و روشن بود.'),
(2966, 2966, 'قَالَ لِلْمَلَإِ حَوْلَهُ إِنَّ هَٰذَا لَسَاحِرٌ عَلِيمٌ', 26, 34, 19, 74, 368, '(فرعون) به گروهی که اطراف او بودند گفت: «این ساحر آگاه و ماهری است!'),
(2967, 2967, 'يُرِيدُ أَنْ يُخْرِجَكُمْ مِنْ أَرْضِكُمْ بِسِحْرِهِ فَمَاذَا تَأْمُرُونَ', 26, 35, 19, 74, 368, 'او می‌خواهد با سحرش شما را از سرزمینتان بیرون کند! شما چه نظر می‌دهید؟»'),
(2968, 2968, 'قَالُوا أَرْجِهْ وَأَخَاهُ وَابْعَثْ فِي الْمَدَائِنِ حَاشِرِينَ', 26, 36, 19, 74, 368, 'گفتند: «او و برادرش را مهلت ده؛ و مأموران را برای بسیج به تمام شهرها اعزام کن،'),
(2969, 2969, 'يَأْتُوكَ بِكُلِّ سَحَّارٍ عَلِيمٍ', 26, 37, 19, 74, 368, 'تا هر ساحر ماهر و دانایی را نزد تو آورند!»'),
(2970, 2970, 'فَجُمِعَ السَّحَرَةُ لِمِيقَاتِ يَوْمٍ مَعْلُومٍ', 26, 38, 19, 74, 368, 'سرانجام ساحران برای وعده‌گاه روز معیّنی جمع‌آوری شدند.'),
(2971, 2971, 'وَقِيلَ لِلنَّاسِ هَلْ أَنْتُمْ مُجْتَمِعُونَ', 26, 39, 19, 74, 368, 'و به مردم گفته شد: «آیا شما نیز (در این صحنه) اجتماع می‌کنید...'),
(2972, 2972, 'لَعَلَّنَا نَتَّبِعُ السَّحَرَةَ إِنْ كَانُوا هُمُ الْغَالِبِينَ', 26, 40, 19, 74, 369, 'تا اگر ساحران پیروز شوند، از آنان پیروی کنیم؟!»'),
(2973, 2973, 'فَلَمَّا جَاءَ السَّحَرَةُ قَالُوا لِفِرْعَوْنَ أَئِنَّ لَنَا لَأَجْرًا إِنْ كُنَّا نَحْنُ الْغَالِبِينَ', 26, 41, 19, 74, 369, 'هنگامی که ساحران آمدند، به فرعون گفتند: «آیا اگر ما پیروز شویم، پاداش مهمّی خواهیم داشت؟»'),
(2974, 2974, 'قَالَ نَعَمْ وَإِنَّكُمْ إِذًا لَمِنَ الْمُقَرَّبِينَ', 26, 42, 19, 74, 369, 'گفت: «(آری،) و در آن صورت شما از مقرّبان خواهید بود!»'),
(2975, 2975, 'قَالَ لَهُمْ مُوسَىٰ أَلْقُوا مَا أَنْتُمْ مُلْقُونَ', 26, 43, 19, 74, 369, '(روز موعود فرا رسید و همگی جمع شدند؛) موسی به ساحران گفت: «آنچه را می‌خواهید بیفکنید، بیفکنید!»'),
(2976, 2976, 'فَأَلْقَوْا حِبَالَهُمْ وَعِصِيَّهُمْ وَقَالُوا بِعِزَّةِ فِرْعَوْنَ إِنَّا لَنَحْنُ الْغَالِبُونَ', 26, 44, 19, 74, 369, 'آنها طنابها و عصاهای خود را افکندند و گفتند: «به عزّت فرعون، ما قطعاً پیروزیم!»'),
(2977, 2977, 'فَأَلْقَىٰ مُوسَىٰ عَصَاهُ فَإِذَا هِيَ تَلْقَفُ مَا يَأْفِكُونَ', 26, 45, 19, 74, 369, 'سپس موسی عصایش را افکند، ناگهان تمام وسایل دروغین آنها را بلعید!'),
(2978, 2978, 'فَأُلْقِيَ السَّحَرَةُ سَاجِدِينَ', 26, 46, 19, 74, 369, 'فوراً همه ساحران به سجده افتادند.'),
(2979, 2979, 'قَالُوا آمَنَّا بِرَبِّ الْعَالَمِينَ', 26, 47, 19, 74, 369, 'گفتند: «ما به پروردگار عالمیان ایمان آوردیم،'),
(2980, 2980, 'رَبِّ مُوسَىٰ وَهَارُونَ', 26, 48, 19, 74, 369, 'پروردگار موسی و هارون!»'),
(2981, 2981, 'قَالَ آمَنْتُمْ لَهُ قَبْلَ أَنْ آذَنَ لَكُمْ ۖ إِنَّهُ لَكَبِيرُكُمُ الَّذِي عَلَّمَكُمُ السِّحْرَ فَلَسَوْفَ تَعْلَمُونَ ۚ لَأُقَطِّعَنَّ أَيْدِيَكُمْ وَأَرْجُلَكُمْ مِنْ خِلَافٍ وَلَأُصَلِّبَنَّكُمْ أَجْمَعِينَ', 26, 49, 19, 74, 369, '(فرعون) گفت: «آیا پیش از اینکه به شما اجازه دهم به او ایمان آوردید؟! مسلّماً او بزرگ و استاد شماست که به شما سحر آموخته (و این یک توطئه است)! امّا بزودی خواهید دانست! دستها و پاهای شما را بعکس یکدیگر قطع می‌کنم، و همه شما را به دار می‌آویزم!»'),
(2982, 2982, 'قَالُوا لَا ضَيْرَ ۖ إِنَّا إِلَىٰ رَبِّنَا مُنْقَلِبُونَ', 26, 50, 19, 74, 369, 'گفتند: «مهّم نیست، (هر کاری از دستت ساخته است بکن)! ما بسوی پروردگارمان بازمی‌گردیم!'),
(2983, 2983, 'إِنَّا نَطْمَعُ أَنْ يَغْفِرَ لَنَا رَبُّنَا خَطَايَانَا أَنْ كُنَّا أَوَّلَ الْمُؤْمِنِينَ', 26, 51, 19, 74, 369, 'ما امیدواریم که پروردگارمان خطاهای ما را ببخشد، چرا که ما نخستین ایمان‌آورندگان بودیم!»'),
(2984, 2984, ' وَأَوْحَيْنَا إِلَىٰ مُوسَىٰ أَنْ أَسْرِ بِعِبَادِي إِنَّكُمْ مُتَّبَعُونَ', 26, 52, 19, 74, 369, 'و به موسی وحی کردیم که شبانه بندگانم را (از مصر) کوچ ده، زیرا شما مورد تعقیب هستید!»'),
(2985, 2985, 'فَأَرْسَلَ فِرْعَوْنُ فِي الْمَدَائِنِ حَاشِرِينَ', 26, 53, 19, 74, 369, 'فرعون (از این ماجرا آگاه شد و) مأموران بسیج نیرو را به شهرها فرستاد،'),
(2986, 2986, 'إِنَّ هَٰؤُلَاءِ لَشِرْذِمَةٌ قَلِيلُونَ', 26, 54, 19, 74, 369, '(و گفت:) اینها مسلّماً گروهی اندکند؛'),
(2987, 2987, 'وَإِنَّهُمْ لَنَا لَغَائِظُونَ', 26, 55, 19, 74, 369, 'و اینها ما را به خشم آورده‌اند؛'),
(2988, 2988, 'وَإِنَّا لَجَمِيعٌ حَاذِرُونَ', 26, 56, 19, 74, 369, 'و ما همگی آماده پیکاریم!»'),
(2989, 2989, 'فَأَخْرَجْنَاهُمْ مِنْ جَنَّاتٍ وَعُيُونٍ', 26, 57, 19, 74, 369, '(سرانجام فرعونیان مغلوب شدند،) و ما آنها را از باغها و چشمه‌ها بیرون راندیم،'),
(2990, 2990, 'وَكُنُوزٍ وَمَقَامٍ كَرِيمٍ', 26, 58, 19, 74, 369, 'و از گنجها و قصرهای مجلّل!'),
(2991, 2991, 'كَذَٰلِكَ وَأَوْرَثْنَاهَا بَنِي إِسْرَائِيلَ', 26, 59, 19, 74, 369, '(آری،) اینچنین کردیم! و بنی اسرائیل را وارث آنها ساختیم!'),
(2992, 2992, 'فَأَتْبَعُوهُمْ مُشْرِقِينَ', 26, 60, 19, 74, 369, 'آنان به تعقیب بنی اسرائیل پرداختند، و به هنگام طلوع آفتاب به آنها رسیدند.'),
(2993, 2993, 'فَلَمَّا تَرَاءَى الْجَمْعَانِ قَالَ أَصْحَابُ مُوسَىٰ إِنَّا لَمُدْرَكُونَ', 26, 61, 19, 74, 370, 'هنگامی که دو گروه یکدیگر را دیدند، یاران موسی گفتند: «ما در چنگال فرعونیان گرفتار شدیم!»'),
(2994, 2994, 'قَالَ كَلَّا ۖ إِنَّ مَعِيَ رَبِّي سَيَهْدِينِ', 26, 62, 19, 74, 370, '(موسی) گفت: «چنین نیست! یقیناً پروردگارم با من است، بزودی مرا هدایت خواهد کرد!»'),
(2995, 2995, 'فَأَوْحَيْنَا إِلَىٰ مُوسَىٰ أَنِ اضْرِبْ بِعَصَاكَ الْبَحْرَ ۖ فَانْفَلَقَ فَكَانَ كُلُّ فِرْقٍ كَالطَّوْدِ الْعَظِيمِ', 26, 63, 19, 74, 370, 'و بدنبال آن به موسی وحی کردیم: «عصایت را به دریا بزن!» (عصایش را به دریا زد،) و دریا از هم شکافته شد، و هر بخشی همچون کوه عظیمی بود!'),
(2996, 2996, 'وَأَزْلَفْنَا ثَمَّ الْآخَرِينَ', 26, 64, 19, 74, 370, 'و در آنجا دیگران = لشکر فرعون‌ را نیز (به دریا) نزدیک ساختیم!'),
(2997, 2997, 'وَأَنْجَيْنَا مُوسَىٰ وَمَنْ مَعَهُ أَجْمَعِينَ', 26, 65, 19, 74, 370, 'و موسی و تمام کسانی را که با او بودند نجات دادیم!'),
(2998, 2998, 'ثُمَّ أَغْرَقْنَا الْآخَرِينَ', 26, 66, 19, 74, 370, 'سپس دیگران را غرق کردیم!'),
(2999, 2999, 'إِنَّ فِي ذَٰلِكَ لَآيَةً ۖ وَمَا كَانَ أَكْثَرُهُمْ مُؤْمِنِينَ', 26, 67, 19, 74, 370, 'در این جریان، نشانه روشنی است ولی بیشترشان ایمان نیاوردند! (چرا که طالب حق نبودند)'),
(3000, 3000, 'وَإِنَّ رَبَّكَ لَهُوَ الْعَزِيزُ الرَّحِيمُ', 26, 68, 19, 74, 370, 'و پروردگارت شکست‌ناپذیر و مهربان است!'),
(3001, 3001, 'وَاتْلُ عَلَيْهِمْ نَبَأَ إِبْرَاهِيمَ', 26, 69, 19, 74, 370, 'و بر آنان خبر ابراهیم را بخوان،'),
(3002, 3002, 'إِذْ قَالَ لِأَبِيهِ وَقَوْمِهِ مَا تَعْبُدُونَ', 26, 70, 19, 74, 370, 'هنگامی که به پدر و قومش گفت: «چه چیز را می‌پرستید؟!»'),
(3003, 3003, 'قَالُوا نَعْبُدُ أَصْنَامًا فَنَظَلُّ لَهَا عَاكِفِينَ', 26, 71, 19, 74, 370, 'گفتند: «بتهایی را می‌پرستیم، و همه روز ملازم عبادت آنهاییم.»'),
(3004, 3004, 'قَالَ هَلْ يَسْمَعُونَكُمْ إِذْ تَدْعُونَ', 26, 72, 19, 74, 370, 'گفت: «آیا هنگامی که آنها را می‌خوانید صدای شما را می‌شنوند؟!'),
(3005, 3005, 'أَوْ يَنْفَعُونَكُمْ أَوْ يَضُرُّونَ', 26, 73, 19, 74, 370, 'یا سود و زیانی به شما می‌رسانند؟!»'),
(3006, 3006, 'قَالُوا بَلْ وَجَدْنَا آبَاءَنَا كَذَٰلِكَ يَفْعَلُونَ', 26, 74, 19, 74, 370, 'گفتند: «ما فقط نیاکان خود را یافتیم که چنین می‌کنند.»'),
(3007, 3007, 'قَالَ أَفَرَأَيْتُمْ مَا كُنْتُمْ تَعْبُدُونَ', 26, 75, 19, 74, 370, 'گفت: «آیا دیدید (این) چیزهایی را که پیوسته پرستش می‌کردید...'),
(3008, 3008, 'أَنْتُمْ وَآبَاؤُكُمُ الْأَقْدَمُونَ', 26, 76, 19, 74, 370, 'شما و پدران پیشین شما،'),
(3009, 3009, 'فَإِنَّهُمْ عَدُوٌّ لِي إِلَّا رَبَّ الْعَالَمِينَ', 26, 77, 19, 74, 370, 'همه آنها دشمن من هستند (و من دشمن آنها)، مگر پروردگار عالمیان!'),
(3010, 3010, 'الَّذِي خَلَقَنِي فَهُوَ يَهْدِينِ', 26, 78, 19, 74, 370, 'همان کسی که مرا آفرید، و پیوسته راهنمائیم می‌کند،'),
(3011, 3011, 'وَالَّذِي هُوَ يُطْعِمُنِي وَيَسْقِينِ', 26, 79, 19, 74, 370, 'و کسی که مرا غذا می‌دهد و سیراب می‌نماید،'),
(3012, 3012, 'وَإِذَا مَرِضْتُ فَهُوَ يَشْفِينِ', 26, 80, 19, 74, 370, 'و هنگامی که بیمار شوم مرا شفا می‌دهد،'),
(3013, 3013, 'وَالَّذِي يُمِيتُنِي ثُمَّ يُحْيِينِ', 26, 81, 19, 74, 370, 'و کسی که مرا می‌میراند و سپس زنده می‌کند،'),
(3014, 3014, 'وَالَّذِي أَطْمَعُ أَنْ يَغْفِرَ لِي خَطِيئَتِي يَوْمَ الدِّينِ', 26, 82, 19, 74, 370, 'و کسی که امید دارم گناهم را در روز جزا ببخشد!'),
(3015, 3015, 'رَبِّ هَبْ لِي حُكْمًا وَأَلْحِقْنِي بِالصَّالِحِينَ', 26, 83, 19, 74, 370, 'پروردگارا! به من علم و دانش ببخش، و مرا به صالحان ملحق کن!'),
(3016, 3016, 'وَاجْعَلْ لِي لِسَانَ صِدْقٍ فِي الْآخِرِينَ', 26, 84, 19, 74, 371, 'و برای من در میان امّتهای آینده، زبان صدق (و ذکر خیری) قرار ده!'),
(3017, 3017, 'وَاجْعَلْنِي مِنْ وَرَثَةِ جَنَّةِ النَّعِيمِ', 26, 85, 19, 74, 371, 'و مرا وارثان بهشت پرنعمت گردان!'),
(3018, 3018, 'وَاغْفِرْ لِأَبِي إِنَّهُ كَانَ مِنَ الضَّالِّينَ', 26, 86, 19, 74, 371, 'و پدرم = عمویم‌ را بیامرز، که او از گمراهان بود!'),
(3019, 3019, 'وَلَا تُخْزِنِي يَوْمَ يُبْعَثُونَ', 26, 87, 19, 74, 371, 'و در آن روز که مردم برانگیخته می‌شوند، مرا شرمنده و رسوا مکن!'),
(3020, 3020, 'يَوْمَ لَا يَنْفَعُ مَالٌ وَلَا بَنُونَ', 26, 88, 19, 74, 371, 'در آن روز که مال و فرزندان سودی نمی‌بخشد،'),
(3021, 3021, 'إِلَّا مَنْ أَتَى اللَّهَ بِقَلْبٍ سَلِيمٍ', 26, 89, 19, 74, 371, 'مگر کسی که با قلب سلیم به پیشگاه خدا آید!»'),
(3022, 3022, 'وَأُزْلِفَتِ الْجَنَّةُ لِلْمُتَّقِينَ', 26, 90, 19, 74, 371, '(در آن روز،) بهشت برای پرهیزکاران نزدیک می‌شود،'),
(3023, 3023, 'وَبُرِّزَتِ الْجَحِيمُ لِلْغَاوِينَ', 26, 91, 19, 74, 371, 'و دوزخ برای گمراهان آشکار می‌گردد،'),
(3024, 3024, 'وَقِيلَ لَهُمْ أَيْنَ مَا كُنْتُمْ تَعْبُدُونَ', 26, 92, 19, 74, 371, 'و به آنان گفته می‌شود: «کجا هستند معبودانی که آنها را پرستش می‌کردید...'),
(3025, 3025, 'مِنْ دُونِ اللَّهِ هَلْ يَنْصُرُونَكُمْ أَوْ يَنْتَصِرُونَ', 26, 93, 19, 74, 371, 'معبودهایی غیر از خدا؟! آیا آنها شما را یاری می‌کنند، یا کسی به یاری آنها می‌آید؟!»'),
(3026, 3026, 'فَكُبْكِبُوا فِيهَا هُمْ وَالْغَاوُونَ', 26, 94, 19, 74, 371, 'در آن هنگام همه آن معبودان با عابدان گمراه به دوزخ افکنده می‌شوند؛'),
(3027, 3027, 'وَجُنُودُ إِبْلِيسَ أَجْمَعُونَ', 26, 95, 19, 74, 371, 'و همچنین همگی لشکریان ابلیس!'),
(3028, 3028, 'قَالُوا وَهُمْ فِيهَا يَخْتَصِمُونَ', 26, 96, 19, 74, 371, 'آنها در آنجا در حالی که به مخاصمه برخاسته‌اند می‌گویند:'),
(3029, 3029, 'تَاللَّهِ إِنْ كُنَّا لَفِي ضَلَالٍ مُبِينٍ', 26, 97, 19, 74, 371, '«به خدا سوگند که ما در گمراهی آشکاری بودیم،'),
(3030, 3030, 'إِذْ نُسَوِّيكُمْ بِرَبِّ الْعَالَمِينَ', 26, 98, 19, 74, 371, 'چون شما را با پروردگار عالمیان برابر می‌شمردیم!'),
(3031, 3031, 'وَمَا أَضَلَّنَا إِلَّا الْمُجْرِمُونَ', 26, 99, 19, 74, 371, 'امّا کسی جز مجرمان ما را گمراه نکرد!'),
(3032, 3032, 'فَمَا لَنَا مِنْ شَافِعِينَ', 26, 100, 19, 74, 371, '(افسوس که امروز) شفاعت‌کنندگانی برای ما وجود ندارد،'),
(3033, 3033, 'وَلَا صَدِيقٍ حَمِيمٍ', 26, 101, 19, 74, 371, 'و نه دوست گرم و پرمحبّتی!'),
(3034, 3034, 'فَلَوْ أَنَّ لَنَا كَرَّةً فَنَكُونَ مِنَ الْمُؤْمِنِينَ', 26, 102, 19, 74, 371, 'ای کاش بار دیگر (به دنیا) بازگردیم و از مؤمنان باشیم!»'),
(3035, 3035, 'إِنَّ فِي ذَٰلِكَ لَآيَةً ۖ وَمَا كَانَ أَكْثَرُهُمْ مُؤْمِنِينَ', 26, 103, 19, 74, 371, 'در این ماجرا، نشانه (و عبرتی) است؛ ولی بیشتر آنان مؤمن نبودند!'),
(3036, 3036, 'وَإِنَّ رَبَّكَ لَهُوَ الْعَزِيزُ الرَّحِيمُ', 26, 104, 19, 74, 371, 'و پروردگار تو عزیز و رحیم است!'),
(3037, 3037, 'كَذَّبَتْ قَوْمُ نُوحٍ الْمُرْسَلِينَ', 26, 105, 19, 74, 371, 'قوم نوح رسولان را تکذیب کردند،'),
(3038, 3038, 'إِذْ قَالَ لَهُمْ أَخُوهُمْ نُوحٌ أَلَا تَتَّقُونَ', 26, 106, 19, 74, 371, 'هنگامی که برادرشان نوح به آنان گفت: «آیا تقوا پیشه نمی‌کنید؟!'),
(3039, 3039, 'إِنِّي لَكُمْ رَسُولٌ أَمِينٌ', 26, 107, 19, 74, 371, 'مسلّماً من برای شما پیامبری امین هستم!'),
(3040, 3040, 'فَاتَّقُوا اللَّهَ وَأَطِيعُونِ', 26, 108, 19, 74, 371, 'تقوای الهی پیشه کنید و مرا اطاعت نمایید!'),
(3041, 3041, 'وَمَا أَسْأَلُكُمْ عَلَيْهِ مِنْ أَجْرٍ ۖ إِنْ أَجْرِيَ إِلَّا عَلَىٰ رَبِّ الْعَالَمِينَ', 26, 109, 19, 74, 371, 'من برای این دعوت، هیچ مزدی از شما نمی‌طلبم؛ اجر من تنها بر پروردگار عالمیان است!'),
(3042, 3042, 'فَاتَّقُوا اللَّهَ وَأَطِيعُونِ', 26, 110, 19, 74, 371, 'پس، تقوای الهی پیشه کنید و مرا اطاعت نمایید!»'),
(3043, 3043, ' قَالُوا أَنُؤْمِنُ لَكَ وَاتَّبَعَكَ الْأَرْذَلُونَ', 26, 111, 19, 75, 371, 'گفتند: «آیا ما به تو ایمان بیاوریم در حالی که افراد پست و بی‌ارزش از تو پیروی کرده‌اند؟!»'),
(3044, 3044, 'قَالَ وَمَا عِلْمِي بِمَا كَانُوا يَعْمَلُونَ', 26, 112, 19, 75, 372, '(نوح) گفت: «من چه می‌دانم آنها چه کاری داشته‌اند!'),
(3045, 3045, 'إِنْ حِسَابُهُمْ إِلَّا عَلَىٰ رَبِّي ۖ لَوْ تَشْعُرُونَ', 26, 113, 19, 75, 372, 'حساب آنها تنها با پروردگار من است اگر شما می‌فهمیدید!'),
(3046, 3046, 'وَمَا أَنَا بِطَارِدِ الْمُؤْمِنِينَ', 26, 114, 19, 75, 372, 'و من هرگز مؤمنان را طرد نخواهم کرد.'),
(3047, 3047, 'إِنْ أَنَا إِلَّا نَذِيرٌ مُبِينٌ', 26, 115, 19, 75, 372, 'من تنها انذارکننده‌ای آشکارم.»'),
(3048, 3048, 'قَالُوا لَئِنْ لَمْ تَنْتَهِ يَا نُوحُ لَتَكُونَنَّ مِنَ الْمَرْجُومِينَ', 26, 116, 19, 75, 372, 'گفتند: «ای نوح! اگر (از حرفهایت) دست برنداری، سنگباران خواهی شد!»'),
(3049, 3049, 'قَالَ رَبِّ إِنَّ قَوْمِي كَذَّبُونِ', 26, 117, 19, 75, 372, 'گفت: «پروردگارا! قوم من، مرا تکذیب کردند!'),
(3050, 3050, 'فَافْتَحْ بَيْنِي وَبَيْنَهُمْ فَتْحًا وَنَجِّنِي وَمَنْ مَعِيَ مِنَ الْمُؤْمِنِينَ', 26, 118, 19, 75, 372, 'اکنون میان من و آنها جدایی بیفکن؛ و مرا و مؤمنانی را که با من هستند رهایی بخش!»'),
(3051, 3051, 'فَأَنْجَيْنَاهُ وَمَنْ مَعَهُ فِي الْفُلْكِ الْمَشْحُونِ', 26, 119, 19, 75, 372, 'ما، او و کسانی را که با او بودند، در آن کشتی که پر (از انسان و انواع حیوانات) بود، رهایی بخشیدیم!'),
(3052, 3052, 'ثُمَّ أَغْرَقْنَا بَعْدُ الْبَاقِينَ', 26, 120, 19, 75, 372, 'سپس بقیه را غرق کردیم!'),
(3053, 3053, 'إِنَّ فِي ذَٰلِكَ لَآيَةً ۖ وَمَا كَانَ أَكْثَرُهُمْ مُؤْمِنِينَ', 26, 121, 19, 75, 372, 'در این ماجرا نشانه روشنی است؛ امّا بیشتر آنان مؤمن نبودند.'),
(3054, 3054, 'وَإِنَّ رَبَّكَ لَهُوَ الْعَزِيزُ الرَّحِيمُ', 26, 122, 19, 75, 372, 'و پروردگار تو عزیز و رحیم است.'),
(3055, 3055, 'كَذَّبَتْ عَادٌ الْمُرْسَلِينَ', 26, 123, 19, 75, 372, 'قوم عاد (نیز) رسولان (خدا) را تکذیب کردند،'),
(3056, 3056, 'إِذْ قَالَ لَهُمْ أَخُوهُمْ هُودٌ أَلَا تَتَّقُونَ', 26, 124, 19, 75, 372, 'هنگامی که برادرشان هود گفت: «آیا تقوا پیشه نمی‌کنید؟!'),
(3057, 3057, 'إِنِّي لَكُمْ رَسُولٌ أَمِينٌ', 26, 125, 19, 75, 372, 'مسلماً من برای شما پیامبری امین هستم.'),
(3058, 3058, 'فَاتَّقُوا اللَّهَ وَأَطِيعُونِ', 26, 126, 19, 75, 372, 'پس تقوای الهی پیشه کنید و مرا اطاعت نمایید!'),
(3059, 3059, 'وَمَا أَسْأَلُكُمْ عَلَيْهِ مِنْ أَجْرٍ ۖ إِنْ أَجْرِيَ إِلَّا عَلَىٰ رَبِّ الْعَالَمِينَ', 26, 127, 19, 75, 372, 'من در برابر این دعوت، هیچ اجر و پاداشی از شما نمی‌طلبم؛ اجر و پاداش من تنها بر پروردگار عالمیان است.'),
(3060, 3060, 'أَتَبْنُونَ بِكُلِّ رِيعٍ آيَةً تَعْبَثُونَ', 26, 128, 19, 75, 372, 'آیا شما بر هر مکان مرتفعی نشانه‌ای از روی هوا و هوس می‌سازید؟!'),
(3061, 3061, 'وَتَتَّخِذُونَ مَصَانِعَ لَعَلَّكُمْ تَخْلُدُونَ', 26, 129, 19, 75, 372, 'و قصرها و قلعه‌های زیبا و محکم بنا می‌کنید شاید در دنیا جاودانه بمانید؟!'),
(3062, 3062, 'وَإِذَا بَطَشْتُمْ بَطَشْتُمْ جَبَّارِينَ', 26, 130, 19, 75, 372, 'و هنگامی که کسی را مجازات می‌کنید همچون جبّاران کیفر می‌دهید!'),
(3063, 3063, 'فَاتَّقُوا اللَّهَ وَأَطِيعُونِ', 26, 131, 19, 75, 372, 'پس تقوای الهی پیشه کنید و مرا اطاعت نمایید!'),
(3064, 3064, 'وَاتَّقُوا الَّذِي أَمَدَّكُمْ بِمَا تَعْلَمُونَ', 26, 132, 19, 75, 372, 'و از (نافرمانی) خدایی بپرهیزید که شما را به نعمتهایی که می‌دانید امداد کرده؛'),
(3065, 3065, 'أَمَدَّكُمْ بِأَنْعَامٍ وَبَنِينَ', 26, 133, 19, 75, 372, 'شما را به چهارپایان و نیز پسران (لایق و برومند) امداد فرموده؛'),
(3066, 3066, 'وَجَنَّاتٍ وَعُيُونٍ', 26, 134, 19, 75, 372, 'همچنین به باغها و چشمه‌ها!'),
(3067, 3067, 'إِنِّي أَخَافُ عَلَيْكُمْ عَذَابَ يَوْمٍ عَظِيمٍ', 26, 135, 19, 75, 372, '(اگر کفران کنید،) من بر شما از عذاب روزی بزرگ می‌ترسم!»'),
(3068, 3068, 'قَالُوا سَوَاءٌ عَلَيْنَا أَوَعَظْتَ أَمْ لَمْ تَكُنْ مِنَ الْوَاعِظِينَ', 26, 136, 19, 75, 372, 'آنها = قوم عاد گفتند: «برای ما تفاوت نمی‌کند، چه ما را انذار کنی یا نکنی؛ (بیهوده خود را خسته مکن)!'),
(3069, 3069, 'إِنْ هَٰذَا إِلَّا خُلُقُ الْأَوَّلِينَ', 26, 137, 19, 75, 373, 'این همان روش (و افسانه‌های) پیشینیان است!'),
(3070, 3070, 'وَمَا نَحْنُ بِمُعَذَّبِينَ', 26, 138, 19, 75, 373, 'و ما هرگز مجازات نخواهیم شد!»'),
(3071, 3071, 'فَكَذَّبُوهُ فَأَهْلَكْنَاهُمْ ۗ إِنَّ فِي ذَٰلِكَ لَآيَةً ۖ وَمَا كَانَ أَكْثَرُهُمْ مُؤْمِنِينَ', 26, 139, 19, 75, 373, 'آنان هود را تکذیب کردند، ما هم نابودشان کردیم؛ و در این، آیت و نشانه‌ای است (برای آگاهان)؛ ولی بیشتر آنان مؤمن نبودند!'),
(3072, 3072, 'وَإِنَّ رَبَّكَ لَهُوَ الْعَزِيزُ الرَّحِيمُ', 26, 140, 19, 75, 373, 'و پروردگار تو عزیز و رحیم است!'),
(3073, 3073, 'كَذَّبَتْ ثَمُودُ الْمُرْسَلِينَ', 26, 141, 19, 75, 373, 'قوم ثمود رسولان (خدا) را تکذیب کردند،'),
(3074, 3074, 'إِذْ قَالَ لَهُمْ أَخُوهُمْ صَالِحٌ أَلَا تَتَّقُونَ', 26, 142, 19, 75, 373, 'هنگامی که صالح به آنان گفت: «آیا تقوا پیشه نمی‌کنید؟!'),
(3075, 3075, 'إِنِّي لَكُمْ رَسُولٌ أَمِينٌ', 26, 143, 19, 75, 373, 'من برای شما پیامبری امین هستم!'),
(3076, 3076, 'فَاتَّقُوا اللَّهَ وَأَطِيعُونِ', 26, 144, 19, 75, 373, 'پس تقوای الهی پیشه کنید و مرا اطاعت نمایید!'),
(3077, 3077, 'وَمَا أَسْأَلُكُمْ عَلَيْهِ مِنْ أَجْرٍ ۖ إِنْ أَجْرِيَ إِلَّا عَلَىٰ رَبِّ الْعَالَمِينَ', 26, 145, 19, 75, 373, 'من در برابر این دعوت، اجر و پاداشی از شما نمی‌طلبم؛ اجر من تنها بر پروردگار عالمیان است!'),
(3078, 3078, 'أَتُتْرَكُونَ فِي مَا هَاهُنَا آمِنِينَ', 26, 146, 19, 75, 373, 'آیا شما تصوّر می‌کنید همیشه در نهایت امنیّت در نعمتهایی که اینجاست می‌مانید،'),
(3079, 3079, 'فِي جَنَّاتٍ وَعُيُونٍ', 26, 147, 19, 75, 373, 'در این باغها و چشمه‌ها،'),
(3080, 3080, 'وَزُرُوعٍ وَنَخْلٍ طَلْعُهَا هَضِيمٌ', 26, 148, 19, 75, 373, 'در این زراعتها و نخلهایی که میوه‌هایش شیرین و رسیده است؟!'),
(3081, 3081, 'وَتَنْحِتُونَ مِنَ الْجِبَالِ بُيُوتًا فَارِهِينَ', 26, 149, 19, 75, 373, 'و از کوه‌ها خانه‌هایی می‌تراشید، و در آن به عیش و نوش می‌پردازید!'),
(3082, 3082, 'فَاتَّقُوا اللَّهَ وَأَطِيعُونِ', 26, 150, 19, 75, 373, 'پس از خدا بترسید و مرا اطاعت کنید!'),
(3083, 3083, 'وَلَا تُطِيعُوا أَمْرَ الْمُسْرِفِينَ', 26, 151, 19, 75, 373, 'و فرمان مسرفان را اطاعت نکنید!'),
(3084, 3084, 'الَّذِينَ يُفْسِدُونَ فِي الْأَرْضِ وَلَا يُصْلِحُونَ', 26, 152, 19, 75, 373, 'همانها که در زمین فساد می‌کنند و اصلاح نمی‌کنند!»'),
(3085, 3085, 'قَالُوا إِنَّمَا أَنْتَ مِنَ الْمُسَحَّرِينَ', 26, 153, 19, 75, 373, 'گفتند: «(ای صالح!) تو از افسون شدگانی (و عقل خود را از دست داده‌ای!)'),
(3086, 3086, 'مَا أَنْتَ إِلَّا بَشَرٌ مِثْلُنَا فَأْتِ بِآيَةٍ إِنْ كُنْتَ مِنَ الصَّادِقِينَ', 26, 154, 19, 75, 373, 'تو فقط بشری همچون مائی؛ اگر راست می‌گویی آیت و نشانه‌ای بیاور!»'),
(3087, 3087, 'قَالَ هَٰذِهِ نَاقَةٌ لَهَا شِرْبٌ وَلَكُمْ شِرْبُ يَوْمٍ مَعْلُومٍ', 26, 155, 19, 75, 373, 'گفت: «این ناقه‌ای است (که آیت الهی است) برای او سهمی (از آب قریه)، و برای شما سهم روز معینّی است!'),
(3088, 3088, 'وَلَا تَمَسُّوهَا بِسُوءٍ فَيَأْخُذَكُمْ عَذَابُ يَوْمٍ عَظِيمٍ', 26, 156, 19, 75, 373, 'کمترین آزاری به آن نرسانید، که عذاب روزی بزرگ شما را فرا خواهد گرفت!»'),
(3089, 3089, 'فَعَقَرُوهَا فَأَصْبَحُوا نَادِمِينَ', 26, 157, 19, 75, 373, 'سرانجام بر آن (ناقه) حمله نموده آن را «پی» کردند؛ (و هلاک نمودند) سپس از کرده خود پشیمان شدند!'),
(3090, 3090, 'فَأَخَذَهُمُ الْعَذَابُ ۗ إِنَّ فِي ذَٰلِكَ لَآيَةً ۖ وَمَا كَانَ أَكْثَرُهُمْ مُؤْمِنِينَ', 26, 158, 19, 75, 373, 'و عذاب الهی آنان را فرا گرفت؛ در این، آیت و نشانه‌ای است؛ ولی بیشتر آنان مؤمن نبودند!'),
(3091, 3091, 'وَإِنَّ رَبَّكَ لَهُوَ الْعَزِيزُ الرَّحِيمُ', 26, 159, 19, 75, 373, 'و پروردگار تو عزیز و رحیم است!'),
(3092, 3092, 'كَذَّبَتْ قَوْمُ لُوطٍ الْمُرْسَلِينَ', 26, 160, 19, 75, 374, 'قوم لوط فرستادگان (خدا) را تکذیب کردند،'),
(3093, 3093, 'إِذْ قَالَ لَهُمْ أَخُوهُمْ لُوطٌ أَلَا تَتَّقُونَ', 26, 161, 19, 75, 374, 'هنگامی که برادرشان لوط به آنان گفت: «آیا تقوا پیشه نمی‌کنید؟!'),
(3094, 3094, 'إِنِّي لَكُمْ رَسُولٌ أَمِينٌ', 26, 162, 19, 75, 374, 'من برای شما پیامبری امین هستم!'),
(3095, 3095, 'فَاتَّقُوا اللَّهَ وَأَطِيعُونِ', 26, 163, 19, 75, 374, 'پس تقوای الهی پیشه کنید و مرا اطاعت نمایید!'),
(3096, 3096, 'وَمَا أَسْأَلُكُمْ عَلَيْهِ مِنْ أَجْرٍ ۖ إِنْ أَجْرِيَ إِلَّا عَلَىٰ رَبِّ الْعَالَمِينَ', 26, 164, 19, 75, 374, 'من در برابر این دعوت، اجری از شما نمی‌طلبم، اجر من فقط بر پروردگار عالمیان است!'),
(3097, 3097, 'أَتَأْتُونَ الذُّكْرَانَ مِنَ الْعَالَمِينَ', 26, 165, 19, 75, 374, 'آیا در میان جهانیان، شما به سراغ جنس ذکور می‌روید (و همجنس بازی می‌کنید، آیا این زشت و ننگین نیست؟!)'),
(3098, 3098, 'وَتَذَرُونَ مَا خَلَقَ لَكُمْ رَبُّكُمْ مِنْ أَزْوَاجِكُمْ ۚ بَلْ أَنْتُمْ قَوْمٌ عَادُونَ', 26, 166, 19, 75, 374, 'و همسرانی را که پروردگارتان برای شما آفریده است رها می‌کنید؟! (حقّاً) شما قوم تجاوزگری هستید!»'),
(3099, 3099, 'قَالُوا لَئِنْ لَمْ تَنْتَهِ يَا لُوطُ لَتَكُونَنَّ مِنَ الْمُخْرَجِينَ', 26, 167, 19, 75, 374, 'گفتند: «ای لوط! اگر (از این سخنان) دست برنداری، به یقین از اخراج شدگان خواهی بود!»'),
(3100, 3100, 'قَالَ إِنِّي لِعَمَلِكُمْ مِنَ الْقَالِينَ', 26, 168, 19, 75, 374, 'گفت: «من دشمن سرسخت اعمال شما هستم!'),
(3101, 3101, 'رَبِّ نَجِّنِي وَأَهْلِي مِمَّا يَعْمَلُونَ', 26, 169, 19, 75, 374, 'پروردگارا! من و خاندانم را از آنچه اینها انجام می‌دهند رهایی بخش!»'),
(3102, 3102, 'فَنَجَّيْنَاهُ وَأَهْلَهُ أَجْمَعِينَ', 26, 170, 19, 75, 374, 'ما او و تمامی خاندانش را نجات دادیم،'),
(3103, 3103, 'إِلَّا عَجُوزًا فِي الْغَابِرِينَ', 26, 171, 19, 75, 374, 'جز پیرزنی که در میان بازماندگان بود!'),
(3104, 3104, 'ثُمَّ دَمَّرْنَا الْآخَرِينَ', 26, 172, 19, 75, 374, 'سپس دیگران را هلاک کردیم!'),
(3105, 3105, 'وَأَمْطَرْنَا عَلَيْهِمْ مَطَرًا ۖ فَسَاءَ مَطَرُ الْمُنْذَرِينَ', 26, 173, 19, 75, 374, 'و بارانی (از سنگ) بر آنها فرستادیم؛ چه باران بدی بود باران انذارشدگان!'),
(3106, 3106, 'إِنَّ فِي ذَٰلِكَ لَآيَةً ۖ وَمَا كَانَ أَكْثَرُهُمْ مُؤْمِنِينَ', 26, 174, 19, 75, 374, 'در این (ماجرای قوم لوط و سرنوشت شوم آنها) آیتی است؛ امّا بیشترشان مؤمن نبودند.'),
(3107, 3107, 'وَإِنَّ رَبَّكَ لَهُوَ الْعَزِيزُ الرَّحِيمُ', 26, 175, 19, 75, 374, 'و پروردگار تو عزیز و رحیم است!'),
(3108, 3108, 'كَذَّبَ أَصْحَابُ الْأَيْكَةِ الْمُرْسَلِينَ', 26, 176, 19, 75, 374, 'اصحاب ایکه = شهری نزدیک مدین‌ رسولان (خدا) را تکذیب کردند،'),
(3109, 3109, 'إِذْ قَالَ لَهُمْ شُعَيْبٌ أَلَا تَتَّقُونَ', 26, 177, 19, 75, 374, 'هنگامی که شعیب به آنها گفت: «آیا تقوا پیشه نمی‌کنید؟!'),
(3110, 3110, 'إِنِّي لَكُمْ رَسُولٌ أَمِينٌ', 26, 178, 19, 75, 374, 'مسلّماً من برای شما پیامبری امین هستم!'),
(3111, 3111, 'فَاتَّقُوا اللَّهَ وَأَطِيعُونِ', 26, 179, 19, 75, 374, 'پس تقوای الهی پیشه کنید و مرا اطاعت نمایید!'),
(3112, 3112, 'وَمَا أَسْأَلُكُمْ عَلَيْهِ مِنْ أَجْرٍ ۖ إِنْ أَجْرِيَ إِلَّا عَلَىٰ رَبِّ الْعَالَمِينَ', 26, 180, 19, 75, 374, 'من در برابر این دعوت، پاداشی از شما نمی‌طلبم؛ اجر من تنها بر پروردگار عالمیان است!'),
(3113, 3113, ' أَوْفُوا الْكَيْلَ وَلَا تَكُونُوا مِنَ الْمُخْسِرِينَ', 26, 181, 19, 75, 374, 'حق پیمانه را ادا کنید (و کم فروشی نکنید)، و دیگران را به خسارت نیفکنید!'),
(3114, 3114, 'وَزِنُوا بِالْقِسْطَاسِ الْمُسْتَقِيمِ', 26, 182, 19, 75, 374, 'و با ترازوی صحیح وزن کنید!'),
(3115, 3115, 'وَلَا تَبْخَسُوا النَّاسَ أَشْيَاءَهُمْ وَلَا تَعْثَوْا فِي الْأَرْضِ مُفْسِدِينَ', 26, 183, 19, 75, 374, 'و حق مردم را کم نگذارید، و در زمین تلاش برای فساد نکنید!'),
(3116, 3116, 'وَاتَّقُوا الَّذِي خَلَقَكُمْ وَالْجِبِلَّةَ الْأَوَّلِينَ', 26, 184, 19, 75, 375, 'و از (نافرمانی) کسی که شما و اقوام پیشین را آفرید بپرهیزید!»'),
(3117, 3117, 'قَالُوا إِنَّمَا أَنْتَ مِنَ الْمُسَحَّرِينَ', 26, 185, 19, 75, 375, 'آنها گفتند: «تو فقط از افسون‌شدگانی!'),
(3118, 3118, 'وَمَا أَنْتَ إِلَّا بَشَرٌ مِثْلُنَا وَإِنْ نَظُنُّكَ لَمِنَ الْكَاذِبِينَ', 26, 186, 19, 75, 375, 'تو بشری همچون مائی، تنها گمانی که درباره تو داریم این است که از دروغگویانی!'),
(3119, 3119, 'فَأَسْقِطْ عَلَيْنَا كِسَفًا مِنَ السَّمَاءِ إِنْ كُنْتَ مِنَ الصَّادِقِينَ', 26, 187, 19, 75, 375, 'اگر راست می‌گویی، سنگهایی از آسمان بر سر ما بباران!»'),
(3120, 3120, 'قَالَ رَبِّي أَعْلَمُ بِمَا تَعْمَلُونَ', 26, 188, 19, 75, 375, '(شعیب) گفت: «پروردگار من به اعمالی که شما انجام می‌دهید داناتر است!»'),
(3121, 3121, 'فَكَذَّبُوهُ فَأَخَذَهُمْ عَذَابُ يَوْمِ الظُّلَّةِ ۚ إِنَّهُ كَانَ عَذَابَ يَوْمٍ عَظِيمٍ', 26, 189, 19, 75, 375, 'سرانجام او را تکذیب کردند، و عذاب روز سایبان (سایبانی از ابر صاعقه‌خیز) آنها را فراگرفت؛ یقیناً آن عذاب روز بزرگی بود!'),
(3122, 3122, 'إِنَّ فِي ذَٰلِكَ لَآيَةً ۖ وَمَا كَانَ أَكْثَرُهُمْ مُؤْمِنِينَ', 26, 190, 19, 75, 375, 'در این ماجرا، آیت و نشانه‌ای است؛ ولی بیشتر آنها مؤمن نبودند.'),
(3123, 3123, 'وَإِنَّ رَبَّكَ لَهُوَ الْعَزِيزُ الرَّحِيمُ', 26, 191, 19, 75, 375, 'و پروردگار تو عزیز و رحیم است!'),
(3124, 3124, 'وَإِنَّهُ لَتَنْزِيلُ رَبِّ الْعَالَمِينَ', 26, 192, 19, 75, 375, 'مسلّماً این (قرآن) از سوی پروردگار جهانیان نازل شده است!'),
(3125, 3125, 'نَزَلَ بِهِ الرُّوحُ الْأَمِينُ', 26, 193, 19, 75, 375, 'روح الامین آن را نازل کرده است...'),
(3126, 3126, 'عَلَىٰ قَلْبِكَ لِتَكُونَ مِنَ الْمُنْذِرِينَ', 26, 194, 19, 75, 375, 'بر قلب (پاک) تو، تا از انذارکنندگان باشی!'),
(3127, 3127, 'بِلِسَانٍ عَرَبِيٍّ مُبِينٍ', 26, 195, 19, 75, 375, 'آن را به زبان عربی آشکار (نازل کرد)!'),
(3128, 3128, 'وَإِنَّهُ لَفِي زُبُرِ الْأَوَّلِينَ', 26, 196, 19, 75, 375, 'و توصیف آن در کتابهای پیشینیان نیز آمده است!'),
(3129, 3129, 'أَوَلَمْ يَكُنْ لَهُمْ آيَةً أَنْ يَعْلَمَهُ عُلَمَاءُ بَنِي إِسْرَائِيلَ', 26, 197, 19, 75, 375, 'آیا همین نشانه برای آنها کافی نیست که علمای بنی اسرائیل بخوبی از آن آگاهند؟!'),
(3130, 3130, 'وَلَوْ نَزَّلْنَاهُ عَلَىٰ بَعْضِ الْأَعْجَمِينَ', 26, 198, 19, 75, 375, 'هرگاه ما آن را بر بعضی از عجم = غیر عرب‌ ها نازل می‌کردیم...'),
(3131, 3131, 'فَقَرَأَهُ عَلَيْهِمْ مَا كَانُوا بِهِ مُؤْمِنِينَ', 26, 199, 19, 75, 375, 'و او آن را بر ایشان می‌خواند، به آن ایمان نمی‌آورند!'),
(3132, 3132, 'كَذَٰلِكَ سَلَكْنَاهُ فِي قُلُوبِ الْمُجْرِمِينَ', 26, 200, 19, 75, 375, '(آری،) این گونه (با بیانی رسا) قرآن را در دلهای مجرمان وارد می‌کنیم!'),
(3133, 3133, 'لَا يُؤْمِنُونَ بِهِ حَتَّىٰ يَرَوُا الْعَذَابَ الْأَلِيمَ', 26, 201, 19, 75, 375, '(امّا) به آن ایمان نمی‌آورند تا عذاب دردناک را با چشم خود ببینند!'),
(3134, 3134, 'فَيَأْتِيَهُمْ بَغْتَةً وَهُمْ لَا يَشْعُرُونَ', 26, 202, 19, 75, 375, 'ناگهان به سراغشان می‌آید، در حالی که توجّه ندارند!'),
(3135, 3135, 'فَيَقُولُوا هَلْ نَحْنُ مُنْظَرُونَ', 26, 203, 19, 75, 375, 'و (در آن هنگام) می‌گویند: «آیا به ما مهلتی داده خواهد شد؟!»'),
(3136, 3136, 'أَفَبِعَذَابِنَا يَسْتَعْجِلُونَ', 26, 204, 19, 75, 375, 'آیا برای عذاب ما عجله می‌کنند؟!'),
(3137, 3137, 'أَفَرَأَيْتَ إِنْ مَتَّعْنَاهُمْ سِنِينَ', 26, 205, 19, 75, 375, 'به ما خبر ده، اگر (باز هم) سالیانی آنها را از این زندگی بهره‌مند سازیم...'),
(3138, 3138, 'ثُمَّ جَاءَهُمْ مَا كَانُوا يُوعَدُونَ', 26, 206, 19, 75, 375, 'سپس عذابی که به آنها وعده داده شده به سراغشان بیاید...'),
(3139, 3139, 'مَا أَغْنَىٰ عَنْهُمْ مَا كَانُوا يُمَتَّعُونَ', 26, 207, 19, 75, 376, 'این تمتع و بهره‌گیری از دنیا برای آنها سودی نخواهد داشت!'),
(3140, 3140, 'وَمَا أَهْلَكْنَا مِنْ قَرْيَةٍ إِلَّا لَهَا مُنْذِرُونَ', 26, 208, 19, 75, 376, 'ما هیچ شهر و دیاری را هلاک نکردیم مگر اینکه انذارکنندگانی (از پیامبران الهی) داشتند.'),
(3141, 3141, 'ذِكْرَىٰ وَمَا كُنَّا ظَالِمِينَ', 26, 209, 19, 75, 376, 'تا متذکّر شوند؛ و ما هرگز ستمکار نبودیم! (که بدون اتمام حجّت مجازات کنیم)'),
(3142, 3142, 'وَمَا تَنَزَّلَتْ بِهِ الشَّيَاطِينُ', 26, 210, 19, 75, 376, 'شیاطین و جنیّان (هرگز) این آیات را نازل نکردند!'),
(3143, 3143, 'وَمَا يَنْبَغِي لَهُمْ وَمَا يَسْتَطِيعُونَ', 26, 211, 19, 75, 376, 'و برای آنها سزاوار نیست؛ و قدرت ندارند!'),
(3144, 3144, 'إِنَّهُمْ عَنِ السَّمْعِ لَمَعْزُولُونَ', 26, 212, 19, 75, 376, 'آنها از استراق سمع (و شنیدن اخبار آسمانها) برکنارند!'),
(3145, 3145, 'فَلَا تَدْعُ مَعَ اللَّهِ إِلَٰهًا آخَرَ فَتَكُونَ مِنَ الْمُعَذَّبِينَ', 26, 213, 19, 75, 376, '(ای پیامبر!) هیچ معبودی را با خداوند مخوان، که از معذّبین خواهی بود!'),
(3146, 3146, 'وَأَنْذِرْ عَشِيرَتَكَ الْأَقْرَبِينَ', 26, 214, 19, 75, 376, 'و خویشاوندان نزدیکت را انذار کن!'),
(3147, 3147, 'وَاخْفِضْ جَنَاحَكَ لِمَنِ اتَّبَعَكَ مِنَ الْمُؤْمِنِينَ', 26, 215, 19, 75, 376, 'و بال و پر خود را برای مؤمنانی که از تو پیروی می‌کنند بگستر!'),
(3148, 3148, 'فَإِنْ عَصَوْكَ فَقُلْ إِنِّي بَرِيءٌ مِمَّا تَعْمَلُونَ', 26, 216, 19, 75, 376, 'اگر تو را نافرمانی کنند بگو: «من از آنچه شما انجام می‌دهید بیزارم!»'),
(3149, 3149, 'وَتَوَكَّلْ عَلَى الْعَزِيزِ الرَّحِيمِ', 26, 217, 19, 75, 376, 'و بر خداوند عزیز و رحیم توکّل کن!'),
(3150, 3150, 'الَّذِي يَرَاكَ حِينَ تَقُومُ', 26, 218, 19, 75, 376, 'همان کسی که تو را به هنگامی که (برای عبادت) برمی‌خیزی می‌بیند؛'),
(3151, 3151, 'وَتَقَلُّبَكَ فِي السَّاجِدِينَ', 26, 219, 19, 75, 376, 'و (نیز) حرکت تو را در میان سجده‌کنندگان!'),
(3152, 3152, 'إِنَّهُ هُوَ السَّمِيعُ الْعَلِيمُ', 26, 220, 19, 75, 376, 'اوست خدای شنوا و دانا.'),
(3153, 3153, 'هَلْ أُنَبِّئُكُمْ عَلَىٰ مَنْ تَنَزَّلُ الشَّيَاطِينُ', 26, 221, 19, 75, 376, 'آیا به شما خبر دهم که شیاطین بر چه کسی نازل می‌شوند؟!'),
(3154, 3154, 'تَنَزَّلُ عَلَىٰ كُلِّ أَفَّاكٍ أَثِيمٍ', 26, 222, 19, 75, 376, 'آنها بر هر دروغگوی گنهکار نازل می‌گردند؛'),
(3155, 3155, 'يُلْقُونَ السَّمْعَ وَأَكْثَرُهُمْ كَاذِبُونَ', 26, 223, 19, 75, 376, 'آنچه را می‌شنوند (به دیگران) القا می‌کنند؛ و بیشترشان دروغگو هستند!'),
(3156, 3156, 'وَالشُّعَرَاءُ يَتَّبِعُهُمُ الْغَاوُونَ', 26, 224, 19, 75, 376, '(پیامبر اسلام شاعر نیست؛) شاعران کسانی هستند که گمراهان از آنان پیروی می‌کنند.'),
(3157, 3157, 'أَلَمْ تَرَ أَنَّهُمْ فِي كُلِّ وَادٍ يَهِيمُونَ', 26, 225, 19, 75, 376, 'آیا نمی‌بینی آنها در هر وادی سرگردانند؟'),
(3158, 3158, 'وَأَنَّهُمْ يَقُولُونَ مَا لَا يَفْعَلُونَ', 26, 226, 19, 75, 376, 'و سخنانی می‌گویند که (به آنها) عمل نمی‌کنند؟!'),
(3159, 3159, 'إِلَّا الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ وَذَكَرُوا اللَّهَ كَثِيرًا وَانْتَصَرُوا مِنْ بَعْدِ مَا ظُلِمُوا ۗ وَسَيَعْلَمُ الَّذِينَ ظَلَمُوا أَيَّ مُنْقَلَبٍ يَنْقَلِبُونَ', 26, 227, 19, 75, 376, 'مگر کسانی که ایمان آورده و کارهای شایسته انجام می‌دهند و خدا را بسیار یاد می کنند، و به هنگامی که مورد ستم واقع می‌شوند به دفاع از خویشتن (و مؤمنان) برمی‌خیزند (و از شعر در این راه کمک می‌گیرند)؛ آنها که ستم کردند به زودی می‌دانند که بازگشتشان به کجاست!'),
(3160, 3160, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ طس ۚ تِلْكَ آيَاتُ الْقُرْآنِ وَكِتَابٍ مُبِينٍ', 27, 1, 19, 76, 377, 'طس - این آیات قرآن و کتاب مبین است،'),
(3161, 3161, 'هُدًى وَبُشْرَىٰ لِلْمُؤْمِنِينَ', 27, 2, 19, 76, 377, 'وسیله هدایت و بشارت برای مؤمنان است؛'),
(3162, 3162, 'الَّذِينَ يُقِيمُونَ الصَّلَاةَ وَيُؤْتُونَ الزَّكَاةَ وَهُمْ بِالْآخِرَةِ هُمْ يُوقِنُونَ', 27, 3, 19, 76, 377, 'همان کسانی که نماز را برپا می‌دارند، و زکات را ادا می‌کنند، و آنان به آخرت یقین دارند.'),
(3163, 3163, 'إِنَّ الَّذِينَ لَا يُؤْمِنُونَ بِالْآخِرَةِ زَيَّنَّا لَهُمْ أَعْمَالَهُمْ فَهُمْ يَعْمَهُونَ', 27, 4, 19, 76, 377, 'کسانی که به آخرت ایمان ندارند، اعمال (بد) شان را برای آنان زینت می‌دهیم بطوری که سرگردان می‌شوند.'),
(3164, 3164, 'أُولَٰئِكَ الَّذِينَ لَهُمْ سُوءُ الْعَذَابِ وَهُمْ فِي الْآخِرَةِ هُمُ الْأَخْسَرُونَ', 27, 5, 19, 76, 377, 'آنان کسانی هستند که عذاب بد (و دردناک) برای آنهاست؛ و آنها در آخرت، زیانکارترین مردمند!'),
(3165, 3165, 'وَإِنَّكَ لَتُلَقَّى الْقُرْآنَ مِنْ لَدُنْ حَكِيمٍ عَلِيمٍ', 27, 6, 19, 76, 377, 'به یقین این قرآن از سوی حکیم و دانایی بر تو القا می‌شود.'),
(3166, 3166, 'إِذْ قَالَ مُوسَىٰ لِأَهْلِهِ إِنِّي آنَسْتُ نَارًا سَآتِيكُمْ مِنْهَا بِخَبَرٍ أَوْ آتِيكُمْ بِشِهَابٍ قَبَسٍ لَعَلَّكُمْ تَصْطَلُونَ', 27, 7, 19, 76, 377, '(به خاطر بیاور) هنگامی را که موسی به خانواده خود گفت: «من آتشی از دور دیدم؛ (همین جا توقف کنید؛) بزودی خبری از آن برای شما می‌آورم، یا شعله آتشی تا گرم شوید.»'),
(3167, 3167, 'فَلَمَّا جَاءَهَا نُودِيَ أَنْ بُورِكَ مَنْ فِي النَّارِ وَمَنْ حَوْلَهَا وَسُبْحَانَ اللَّهِ رَبِّ الْعَالَمِينَ', 27, 8, 19, 76, 377, 'هنگامی که نزد آتش آمد، ندایی برخاست که: «مبارک باد آن کس که در آتش است و کسی که در اطراف آن است = فرشتگان و موسی‌ و منزّه است خداوندی که پروردگار جهانیان است!'),
(3168, 3168, 'يَا مُوسَىٰ إِنَّهُ أَنَا اللَّهُ الْعَزِيزُ الْحَكِيمُ', 27, 9, 19, 76, 377, 'ای موسی! من خداوند عزیز و حکیمم!'),
(3169, 3169, 'وَأَلْقِ عَصَاكَ ۚ فَلَمَّا رَآهَا تَهْتَزُّ كَأَنَّهَا جَانٌّ وَلَّىٰ مُدْبِرًا وَلَمْ يُعَقِّبْ ۚ يَا مُوسَىٰ لَا تَخَفْ إِنِّي لَا يَخَافُ لَدَيَّ الْمُرْسَلُونَ', 27, 10, 19, 76, 377, 'و عصایت را بیفکن! -هنگامی که (موسی) به آن نگاه کرد، دید (با سرعت) همچون ماری به هر سو می‌دود (ترسید و) به عقب برگشت، و حتی پشت سر خود را نگاه نکرد- ای موسی! نترس، که رسولان در نزد من نمی‌ترسند!'),
(3170, 3170, 'إِلَّا مَنْ ظَلَمَ ثُمَّ بَدَّلَ حُسْنًا بَعْدَ سُوءٍ فَإِنِّي غَفُورٌ رَحِيمٌ', 27, 11, 19, 76, 377, 'مگر کسی که ستم کند؛ سپس بدی را به نیکی تبدیل نماید، که (توبه او را می‌پذیرم، و) من غفور و رحیمم!'),
(3171, 3171, 'وَأَدْخِلْ يَدَكَ فِي جَيْبِكَ تَخْرُجْ بَيْضَاءَ مِنْ غَيْرِ سُوءٍ ۖ فِي تِسْعِ آيَاتٍ إِلَىٰ فِرْعَوْنَ وَقَوْمِهِ ۚ إِنَّهُمْ كَانُوا قَوْمًا فَاسِقِينَ', 27, 12, 19, 76, 377, 'و دستت را در گریبانت داخل کن؛ هنگامی که خارج می‌شود، سفید و درخشنده است بی آنکه عیبی در آن باشد؛ این در زمره معجزات نه‌گانه‌ای است که تو با آنها بسوی فرعون و قومش فرستاده می‌شوی؛ آنان قومی فاسق و طغیانگرند!»'),
(3172, 3172, 'فَلَمَّا جَاءَتْهُمْ آيَاتُنَا مُبْصِرَةً قَالُوا هَٰذَا سِحْرٌ مُبِينٌ', 27, 13, 19, 76, 377, 'و هنگامی که آیات روشنی‌بخش ما به سراغ آنها آمد گفتند: «این سحری است آشکار!»'),
(3173, 3173, 'وَجَحَدُوا بِهَا وَاسْتَيْقَنَتْهَا أَنْفُسُهُمْ ظُلْمًا وَعُلُوًّا ۚ فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ الْمُفْسِدِينَ', 27, 14, 19, 76, 378, 'و آن را از روی ظلم و سرکشی انکار کردند، در حالی که در دل به آن یقین داشتند! پس بنگر سرانجام تبهکاران (و مفسدان) چگونه بود!'),
(3174, 3174, 'وَلَقَدْ آتَيْنَا دَاوُودَ وَسُلَيْمَانَ عِلْمًا ۖ وَقَالَا الْحَمْدُ لِلَّهِ الَّذِي فَضَّلَنَا عَلَىٰ كَثِيرٍ مِنْ عِبَادِهِ الْمُؤْمِنِينَ', 27, 15, 19, 76, 378, 'و ما به داوود و سلیمان، دانشی عظیم دادیم؛ و آنان گفتند: «ستایش از آن خداوندی است که ما را بر بسیاری از بندگان مؤمنش برتری بخشید.»'),
(3175, 3175, 'وَوَرِثَ سُلَيْمَانُ دَاوُودَ ۖ وَقَالَ يَا أَيُّهَا النَّاسُ عُلِّمْنَا مَنْطِقَ الطَّيْرِ وَأُوتِينَا مِنْ كُلِّ شَيْءٍ ۖ إِنَّ هَٰذَا لَهُوَ الْفَضْلُ الْمُبِينُ', 27, 16, 19, 76, 378, 'و سلیمان وارث داوود شد، و گفت: «ای مردم! زبان پرندگان به ما تعلیم داده شده، و از هر چیز به ما عطا گردیده؛ این فضیلت آشکاری است.»'),
(3176, 3176, 'وَحُشِرَ لِسُلَيْمَانَ جُنُودُهُ مِنَ الْجِنِّ وَالْإِنْسِ وَالطَّيْرِ فَهُمْ يُوزَعُونَ', 27, 17, 19, 76, 378, 'لشکریان سلیمان، از جنّ و انس و پرندگان، نزد او جمع شدند؛ آنقدر زیاد بودند که باید توقّف می‌کردند تا به هم ملحق شوند!'),
(3177, 3177, 'حَتَّىٰ إِذَا أَتَوْا عَلَىٰ وَادِ النَّمْلِ قَالَتْ نَمْلَةٌ يَا أَيُّهَا النَّمْلُ ادْخُلُوا مَسَاكِنَكُمْ لَا يَحْطِمَنَّكُمْ سُلَيْمَانُ وَجُنُودُهُ وَهُمْ لَا يَشْعُرُونَ', 27, 18, 19, 76, 378, '(آنها حرکت کردند) تا به سرزمین مورچگان رسیدند؛ مورچه‌ای گفت: «به لانه‌های خود بروید تا سلیمان و لشکرش شما را پایمال نکنند در حالی که نمی‌فهمند!»'),
(3178, 3178, 'فَتَبَسَّمَ ضَاحِكًا مِنْ قَوْلِهَا وَقَالَ رَبِّ أَوْزِعْنِي أَنْ أَشْكُرَ نِعْمَتَكَ الَّتِي أَنْعَمْتَ عَلَيَّ وَعَلَىٰ وَالِدَيَّ وَأَنْ أَعْمَلَ صَالِحًا تَرْضَاهُ وَأَدْخِلْنِي بِرَحْمَتِكَ فِي عِبَادِكَ الصَّالِحِينَ', 27, 19, 19, 76, 378, 'سلیمان از سخن او تبسّمی کرد و خندید و گفت: «پروردگارا! شکر نعمتهایی را که بر من و پدر و مادرم ارزانی داشته‌ای به من الهام کن، و توفیق ده تا عمل صالحی که موجب رضای توست انجام دهم، و مرا برحمت خود در زمره بندگان صالحت وارد کن!»'),
(3179, 3179, 'وَتَفَقَّدَ الطَّيْرَ فَقَالَ مَا لِيَ لَا أَرَى الْهُدْهُدَ أَمْ كَانَ مِنَ الْغَائِبِينَ', 27, 20, 19, 76, 378, '(سلیمان) در جستجوی آن پرنده = هدهد برآمد و گفت: «چرا هدهد را نمی‌بینم، یا اینکه او از غایبان است؟!'),
(3180, 3180, 'لَأُعَذِّبَنَّهُ عَذَابًا شَدِيدًا أَوْ لَأَذْبَحَنَّهُ أَوْ لَيَأْتِيَنِّي بِسُلْطَانٍ مُبِينٍ', 27, 21, 19, 76, 378, 'قطعاً او را کیفر شدیدی خواهم داد، یا او را ذبح می‌کنم، یا باید دلیل روشنی (برای غیبتش) برای من بیاورد!'),
(3181, 3181, 'فَمَكَثَ غَيْرَ بَعِيدٍ فَقَالَ أَحَطْتُ بِمَا لَمْ تُحِطْ بِهِ وَجِئْتُكَ مِنْ سَبَإٍ بِنَبَإٍ يَقِينٍ', 27, 22, 19, 76, 378, 'چندان درنگ نکرد (که هدهد آمد و) گفت: «من بر چیزی آگاهی یافتم که تو بر آن آگاهی نیافتی؛ من از سرزمین «سبا» یک خبر قطعی برای تو آورده‌ام!'),
(3182, 3182, 'إِنِّي وَجَدْتُ امْرَأَةً تَمْلِكُهُمْ وَأُوتِيَتْ مِنْ كُلِّ شَيْءٍ وَلَهَا عَرْشٌ عَظِيمٌ', 27, 23, 19, 76, 379, 'من زنی را دیدم که بر آنان حکومت می‌کند، و همه چیز در اختیار دارد، و (به خصوص) تخت عظیمی دارد!'),
(3183, 3183, 'وَجَدْتُهَا وَقَوْمَهَا يَسْجُدُونَ لِلشَّمْسِ مِنْ دُونِ اللَّهِ وَزَيَّنَ لَهُمُ الشَّيْطَانُ أَعْمَالَهُمْ فَصَدَّهُمْ عَنِ السَّبِيلِ فَهُمْ لَا يَهْتَدُونَ', 27, 24, 19, 76, 379, 'او و قومش را دیدم که برای غیر خدا -خورشید- سجده می‌کنند؛ و شیطان اعمالشان را در نظرشان جلوه داده، و آنها را از راه بازداشته؛ و از این رو هدایت نمی‌شوند!»'),
(3184, 3184, 'أَلَّا يَسْجُدُوا لِلَّهِ الَّذِي يُخْرِجُ الْخَبْءَ فِي السَّمَاوَاتِ وَالْأَرْضِ وَيَعْلَمُ مَا تُخْفُونَ وَمَا تُعْلِنُونَ', 27, 25, 19, 76, 379, 'چرا برای خداوندی سجده نمی‌کنند که آنچه را در آسمانها و زمین پنهان است خارج (و آشکار) می‌سازد، و آنچه را پنهان می‌دارید یا آشکار می‌کنید می‌داند؟!'),
(3185, 3185, 'اللَّهُ لَا إِلَٰهَ إِلَّا هُوَ رَبُّ الْعَرْشِ الْعَظِيمِ ۩', 27, 26, 19, 76, 379, 'خداوندی که معبودی جز او نیست، و پروردگار عرش عظیم است!'),
(3186, 3186, ' قَالَ سَنَنْظُرُ أَصَدَقْتَ أَمْ كُنْتَ مِنَ الْكَاذِبِينَ', 27, 27, 19, 76, 379, '(سلیمان) گفت: «ما تحقیق می‌کنیم ببینیم راست گفتی یا از دروغگویان هستی؟'),
(3187, 3187, 'اذْهَبْ بِكِتَابِي هَٰذَا فَأَلْقِهْ إِلَيْهِمْ ثُمَّ تَوَلَّ عَنْهُمْ فَانْظُرْ مَاذَا يَرْجِعُونَ', 27, 28, 19, 76, 379, 'این نامه مرا ببر و بر آنان بیفکن؛ سپس برگرد (و در گوشه‌ای توقّف کن) ببین آنها چه عکس العملی نشان می‌دهند!'),
(3188, 3188, 'قَالَتْ يَا أَيُّهَا الْمَلَأُ إِنِّي أُلْقِيَ إِلَيَّ كِتَابٌ كَرِيمٌ', 27, 29, 19, 76, 379, '(ملکه سبا) گفت: «ای اشراف! نامه پرارزشی به سوی من افکنده شده!'),
(3189, 3189, 'إِنَّهُ مِنْ سُلَيْمَانَ وَإِنَّهُ بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ', 27, 30, 19, 76, 379, 'این نامه از سلیمان است، و چنین می‌باشد: به نام خداوند بخشنده مهربان‌'),
(3190, 3190, 'أَلَّا تَعْلُوا عَلَيَّ وَأْتُونِي مُسْلِمِينَ', 27, 31, 19, 76, 379, 'توصیه من این است که نسبت به من برتری‌جویی نکنید، و بسوی من آیید در حالی که تسلیم حقّ هستید!»'),
(3191, 3191, 'قَالَتْ يَا أَيُّهَا الْمَلَأُ أَفْتُونِي فِي أَمْرِي مَا كُنْتُ قَاطِعَةً أَمْرًا حَتَّىٰ تَشْهَدُونِ', 27, 32, 19, 76, 379, '(سپس) گفت: «ای اشراف (و ای بزرگان)! نظر خود را در این امر مهمّ به من بازگو کنید، که من هیچ کار مهمّی را بدون حضور (و مشورت) شما انجام نداده‌ام!'),
(3192, 3192, 'قَالُوا نَحْنُ أُولُو قُوَّةٍ وَأُولُو بَأْسٍ شَدِيدٍ وَالْأَمْرُ إِلَيْكِ فَانْظُرِي مَاذَا تَأْمُرِينَ', 27, 33, 19, 76, 379, 'گفتند: «ما دارای نیروی کافی و قدرت جنگی فراوان هستیم، ولی تصمیم نهایی با توست؛ ببین چه دستور می‌دهی!»'),
(3193, 3193, 'قَالَتْ إِنَّ الْمُلُوكَ إِذَا دَخَلُوا قَرْيَةً أَفْسَدُوهَا وَجَعَلُوا أَعِزَّةَ أَهْلِهَا أَذِلَّةً ۖ وَكَذَٰلِكَ يَفْعَلُونَ', 27, 34, 19, 76, 379, 'گفت: پادشاهان هنگامی که وارد منطقه آبادی شوند آن را به فساد و تباهی می‌کشند، و عزیزان آنجا را ذلیل می‌کنند؛ (آری) کار آنان همین‌گونه است!'),
(3194, 3194, 'وَإِنِّي مُرْسِلَةٌ إِلَيْهِمْ بِهَدِيَّةٍ فَنَاظِرَةٌ بِمَ يَرْجِعُ الْمُرْسَلُونَ', 27, 35, 19, 76, 379, 'و من (اکنون جنگ را صلاح نمی‌بینم،) هدیه گرانبهایی برای آنان می‌فرستم تا ببینم فرستادگان من چه خبر می‌آورند (و از این طریق آنها را بیازمایم)!»'),
(3195, 3195, 'فَلَمَّا جَاءَ سُلَيْمَانَ قَالَ أَتُمِدُّونَنِ بِمَالٍ فَمَا آتَانِيَ اللَّهُ خَيْرٌ مِمَّا آتَاكُمْ بَلْ أَنْتُمْ بِهَدِيَّتِكُمْ تَفْرَحُونَ', 27, 36, 19, 76, 380, 'هنگامی که (فرستاده ملکه سبا) نزد سلیمان آمد، گفت: «می‌خواهید مرا با مال کمک کنید (و فریب دهید)؟! آنچه خدا به من داده، بهتر است از آنچه به شما داده است؛ بلکه شما هستید که به هدیه‌هایتان خوشحال می‌شوید!'),
(3196, 3196, 'ارْجِعْ إِلَيْهِمْ فَلَنَأْتِيَنَّهُمْ بِجُنُودٍ لَا قِبَلَ لَهُمْ بِهَا وَلَنُخْرِجَنَّهُمْ مِنْهَا أَذِلَّةً وَهُمْ صَاغِرُونَ', 27, 37, 19, 76, 380, 'بسوی آنان بازگرد (و اعلام کن) با لشکریانی به سراغ آنان می‌آییم که قدرت مقابله با آن را نداشته باشند؛ و آنان را از آن (سرزمین آباد) با ذلّت و خواری بیرون می‌رانیم!»'),
(3197, 3197, 'قَالَ يَا أَيُّهَا الْمَلَأُ أَيُّكُمْ يَأْتِينِي بِعَرْشِهَا قَبْلَ أَنْ يَأْتُونِي مُسْلِمِينَ', 27, 38, 19, 76, 380, '(سلیمان) گفت: «ای بزرگان! کدام یک از شما تخت او را برای من می‌آورد پیش از آنکه به حال تسلیم نزد من آیند؟»'),
(3198, 3198, 'قَالَ عِفْرِيتٌ مِنَ الْجِنِّ أَنَا آتِيكَ بِهِ قَبْلَ أَنْ تَقُومَ مِنْ مَقَامِكَ ۖ وَإِنِّي عَلَيْهِ لَقَوِيٌّ أَمِينٌ', 27, 39, 19, 76, 380, 'عفریتی از جنّ گفت: «من آن را نزد تو می‌آورم پیش از آنکه از مجلست برخیزی و من نسبت به این امر، توانا و امینم!»'),
(3199, 3199, 'قَالَ الَّذِي عِنْدَهُ عِلْمٌ مِنَ الْكِتَابِ أَنَا آتِيكَ بِهِ قَبْلَ أَنْ يَرْتَدَّ إِلَيْكَ طَرْفُكَ ۚ فَلَمَّا رَآهُ مُسْتَقِرًّا عِنْدَهُ قَالَ هَٰذَا مِنْ فَضْلِ رَبِّي لِيَبْلُوَنِي أَأَشْكُرُ أَمْ أَكْفُرُ ۖ وَمَنْ شَكَرَ فَإِنَّمَا يَشْكُرُ لِنَفْسِهِ ۖ وَمَنْ كَفَرَ فَإِنَّ رَبِّي غَنِيٌّ كَرِيمٌ', 27, 40, 19, 76, 380, '(امّا) کسی که دانشی از کتاب (آسمانی) داشت گفت: «پیش از آنکه چشم بر هم زنی، آن را نزد تو خواهم آورد!» و هنگامی که (سلیمان) آن (تخت) را نزد خود ثابت و پابرجا دید گفت: «این از فضل پروردگار من است، تا مرا آزمایش کند که آیا شکر او را بجا می‌آورم یا کفران می‌کنم؟! و هر کس شکر کند، به نفع خود شکر می‌کند؛ و هر کس کفران نماید (بزیان خویش نموده است، که) پروردگار من، غنیّ و کریم است!»'),
(3200, 3200, 'قَالَ نَكِّرُوا لَهَا عَرْشَهَا نَنْظُرْ أَتَهْتَدِي أَمْ تَكُونُ مِنَ الَّذِينَ لَا يَهْتَدُونَ', 27, 41, 19, 76, 380, '(سلیمان) گفت: «تخت او را برایش ناشناس سازید؛ ببینم آیا متوجّه می‌شود یا از کسانی است که هدایت نخواهند شد؟!'),
(3201, 3201, 'فَلَمَّا جَاءَتْ قِيلَ أَهَٰكَذَا عَرْشُكِ ۖ قَالَتْ كَأَنَّهُ هُوَ ۚ وَأُوتِينَا الْعِلْمَ مِنْ قَبْلِهَا وَكُنَّا مُسْلِمِينَ', 27, 42, 19, 76, 380, 'هنگامی که آمد، به او گفته شد: «آیا تخت تو این گونه است؟» گفت: گویا خود آن است! و ما پیش از این هم آگاه بودیم و اسلام آورده بودیم!»'),
(3202, 3202, 'وَصَدَّهَا مَا كَانَتْ تَعْبُدُ مِنْ دُونِ اللَّهِ ۖ إِنَّهَا كَانَتْ مِنْ قَوْمٍ كَافِرِينَ', 27, 43, 19, 76, 380, 'و او را از آنچه غیر از خدا میپرستید بازداشت، که او = ملکه سبا از قوم کافران بود.'),
(3203, 3203, 'قِيلَ لَهَا ادْخُلِي الصَّرْحَ ۖ فَلَمَّا رَأَتْهُ حَسِبَتْهُ لُجَّةً وَكَشَفَتْ عَنْ سَاقَيْهَا ۚ قَالَ إِنَّهُ صَرْحٌ مُمَرَّدٌ مِنْ قَوَارِيرَ ۗ قَالَتْ رَبِّ إِنِّي ظَلَمْتُ نَفْسِي وَأَسْلَمْتُ مَعَ سُلَيْمَانَ لِلَّهِ رَبِّ الْعَالَمِينَ', 27, 44, 19, 76, 380, 'به او گفته شد: «داخل حیاط (قصر) شو!» هنگامی که نظر به آن افکند، پنداشت نهر آبی است و ساق پاهای خود را برهنه کرد (تا از آب بگذرد؛ امّا سلیمان) گفت: «(این آب نیست،) بلکه قصری است از بلور صاف!» (ملکه سبا) گفت: «پروردگارا! من به خود ستم کردم؛ و (اینک) با سلیمان برای خداوندی که پروردگار عالمیان است اسلام آوردم!»'),
(3204, 3204, 'وَلَقَدْ أَرْسَلْنَا إِلَىٰ ثَمُودَ أَخَاهُمْ صَالِحًا أَنِ اعْبُدُوا اللَّهَ فَإِذَا هُمْ فَرِيقَانِ يَخْتَصِمُونَ', 27, 45, 19, 76, 381, 'ما به سوی «ثمود»، برادرشان «صالح» را فرستادیم که: خدای یگانه را بپرستید! امّا آنان به دو گروه تقسیم شدند که به مخاصمه پرداختند.'),
(3205, 3205, 'قَالَ يَا قَوْمِ لِمَ تَسْتَعْجِلُونَ بِالسَّيِّئَةِ قَبْلَ الْحَسَنَةِ ۖ لَوْلَا تَسْتَغْفِرُونَ اللَّهَ لَعَلَّكُمْ تُرْحَمُونَ', 27, 46, 19, 76, 381, '(صالح) گفت: «ای قوم من! چرا برای بدی قبل از نیکی عجله می‌کنید (و عذاب الهی را می‌طلبید نه رحمت او را)؟! چرا از خداوند تقاضای آمرزش نمی‌کنید تا شاید مشمول رحمت (او) شوید؟!»'),
(3206, 3206, 'قَالُوا اطَّيَّرْنَا بِكَ وَبِمَنْ مَعَكَ ۚ قَالَ طَائِرُكُمْ عِنْدَ اللَّهِ ۖ بَلْ أَنْتُمْ قَوْمٌ تُفْتَنُونَ', 27, 47, 19, 76, 381, 'آنها گفتند: «ما تو را و کسانی که با تو هستند به فال بد گرفتیم!» (صالح) گفت: «فال (نیک و) بد شما نزد خداست (و همه مقدّرات به قدرت او تعیین می‌گردد)؛ بلکه شما گروهی هستید فریب‌خورده!'),
(3207, 3207, 'وَكَانَ فِي الْمَدِينَةِ تِسْعَةُ رَهْطٍ يُفْسِدُونَ فِي الْأَرْضِ وَلَا يُصْلِحُونَ', 27, 48, 19, 76, 381, 'و در آن شهر، نه گروهک بودند که در زمین فساد می‌کردند و اصلاح نمی‌کردند.'),
(3208, 3208, 'قَالُوا تَقَاسَمُوا بِاللَّهِ لَنُبَيِّتَنَّهُ وَأَهْلَهُ ثُمَّ لَنَقُولَنَّ لِوَلِيِّهِ مَا شَهِدْنَا مَهْلِكَ أَهْلِهِ وَإِنَّا لَصَادِقُونَ', 27, 49, 19, 76, 381, 'آنها گفتند: «بیایید قسم یاد کنید به خدا که بر او = صالح‌ و خانواده اش شبیخون می زنیم (و آنها را به قتل می‌رسانیم؛) سپس به ولیّ دم او می‌گوییم: ما هرگز از هلاکت خانواده او خبر نداشتیم و در این گفتار خود صادق هستیم!»'),
(3209, 3209, 'وَمَكَرُوا مَكْرًا وَمَكَرْنَا مَكْرًا وَهُمْ لَا يَشْعُرُونَ', 27, 50, 19, 76, 381, 'آنها نقشه مهمّی کشیدند، و ما هم نقشه مهمّی؛ در حالی که آنها درک نمی‌کردند!'),
(3210, 3210, 'فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ مَكْرِهِمْ أَنَّا دَمَّرْنَاهُمْ وَقَوْمَهُمْ أَجْمَعِينَ', 27, 51, 19, 76, 381, 'بنگر عاقبت توطئه آنها چه شد، که ما آنها و قومشان همگی را نابود کردیم؛'),
(3211, 3211, 'فَتِلْكَ بُيُوتُهُمْ خَاوِيَةً بِمَا ظَلَمُوا ۗ إِنَّ فِي ذَٰلِكَ لَآيَةً لِقَوْمٍ يَعْلَمُونَ', 27, 52, 19, 76, 381, 'این خانه‌های آنهاست که بخاطر ظلم و ستمشان خالی مانده؛ و در این نشانه روشنی است برای کسانی که آگاهند!'),
(3212, 3212, 'وَأَنْجَيْنَا الَّذِينَ آمَنُوا وَكَانُوا يَتَّقُونَ', 27, 53, 19, 76, 381, 'و کسانی را که ایمان آورده و تقوا پیشه کرده بودند نجات دادیم!');
INSERT INTO `Quran` (`rowid`, `id`, `Arabic`, `Chapter`, `Verse`, `Juz`, `Hizb`, `Page`, `tr`) VALUES
(3213, 3213, 'وَلُوطًا إِذْ قَالَ لِقَوْمِهِ أَتَأْتُونَ الْفَاحِشَةَ وَأَنْتُمْ تُبْصِرُونَ', 27, 54, 19, 76, 381, 'و لوط را (به یاد آور) هنگامی که به قومش گفت: «آیا شما به سراغ کار بسیار زشتی می‌روید در حالی که (نتایج شوم آن را) می‌بینید؟!'),
(3214, 3214, 'أَئِنَّكُمْ لَتَأْتُونَ الرِّجَالَ شَهْوَةً مِنْ دُونِ النِّسَاءِ ۚ بَلْ أَنْتُمْ قَوْمٌ تَجْهَلُونَ', 27, 55, 19, 76, 381, 'آیا شما بجای زنان، از روی شهوت به سراغ مردان می‌روید؟! شما قومی نادانید!»'),
(3215, 3215, ' فَمَا كَانَ جَوَابَ قَوْمِهِ إِلَّا أَنْ قَالُوا أَخْرِجُوا آلَ لُوطٍ مِنْ قَرْيَتِكُمْ ۖ إِنَّهُمْ أُنَاسٌ يَتَطَهَّرُونَ', 27, 56, 20, 77, 382, 'آنها پاسخی جز این نداشتند که (به یکدیگر) گفتند: «خاندان لوط را از شهر و دیار خود بیرون کنید، که اینها افرادی پاکدامن هستند!»'),
(3216, 3216, 'فَأَنْجَيْنَاهُ وَأَهْلَهُ إِلَّا امْرَأَتَهُ قَدَّرْنَاهَا مِنَ الْغَابِرِينَ', 27, 57, 20, 77, 382, 'ما او و خانواده‌اش را نجات دادیم، بجز همسرش که مقدّر کردیم جزء باقی‌ماندگان (در آن شهر) باشد!'),
(3217, 3217, 'وَأَمْطَرْنَا عَلَيْهِمْ مَطَرًا ۖ فَسَاءَ مَطَرُ الْمُنْذَرِينَ', 27, 58, 20, 77, 382, 'سپس بارانی (از سنگ) بر سر آنها باراندیم (و همگی زیر آن مدفون شدند)؛ و چه بد است باران انذارشدگان!'),
(3218, 3218, 'قُلِ الْحَمْدُ لِلَّهِ وَسَلَامٌ عَلَىٰ عِبَادِهِ الَّذِينَ اصْطَفَىٰ ۗ آللَّهُ خَيْرٌ أَمَّا يُشْرِكُونَ', 27, 59, 20, 77, 382, 'بگو: «حمد مخصوص خداست؛ و سلام بر بندگان برگزیده‌اش!» آیا خداوند بهتر است یا بتهایی که همتای او قرارمی‌دهند؟!'),
(3219, 3219, 'أَمَّنْ خَلَقَ السَّمَاوَاتِ وَالْأَرْضَ وَأَنْزَلَ لَكُمْ مِنَ السَّمَاءِ مَاءً فَأَنْبَتْنَا بِهِ حَدَائِقَ ذَاتَ بَهْجَةٍ مَا كَانَ لَكُمْ أَنْ تُنْبِتُوا شَجَرَهَا ۗ أَإِلَٰهٌ مَعَ اللَّهِ ۚ بَلْ هُمْ قَوْمٌ يَعْدِلُونَ', 27, 60, 20, 77, 382, '(آیا بتهایی که معبود شما هستند بهترند) یا کسی که آسمانها و زمین را آفریده؟! و برای شما از آسمان، آبی فرستاد که با آن، باغهایی زیبا و سرورانگیز رویاندیم؛ شما هرگز قدرت نداشتید درختان آن را برویانید! آیا معبود دیگری با خداست؟! نه، بلکه آنها گروهی هستند که (از روی نادانی، مخلوقات را) همطراز (پروردگارشان) قرار می‌دهند!'),
(3220, 3220, 'أَمَّنْ جَعَلَ الْأَرْضَ قَرَارًا وَجَعَلَ خِلَالَهَا أَنْهَارًا وَجَعَلَ لَهَا رَوَاسِيَ وَجَعَلَ بَيْنَ الْبَحْرَيْنِ حَاجِزًا ۗ أَإِلَٰهٌ مَعَ اللَّهِ ۚ بَلْ أَكْثَرُهُمْ لَا يَعْلَمُونَ', 27, 61, 20, 77, 382, 'یا کسی که زمین را مستقرّ و آرام قرار داد، و میان آن نهرهایی روان ساخت، و برای آن کوه‌های ثابت و پابرجا ایجاد کرد، و میان دو دریا مانعی قرار داد (تا با هم مخلوط نشوند؛ با این حال) آیا معبودی با خداست؟! نه، بلکه بیشتر آنان نمی‌دانند (و جاهلند)!'),
(3221, 3221, 'أَمَّنْ يُجِيبُ الْمُضْطَرَّ إِذَا دَعَاهُ وَيَكْشِفُ السُّوءَ وَيَجْعَلُكُمْ خُلَفَاءَ الْأَرْضِ ۗ أَإِلَٰهٌ مَعَ اللَّهِ ۚ قَلِيلًا مَا تَذَكَّرُونَ', 27, 62, 20, 77, 382, 'یا کسی که دعای مضطرّ را اجابت می‌کند و گرفتاری را برطرف می‌سازد، و شما را خلفای زمین قرارمی‌دهد؛ آیا معبودی با خداست؟! کمتر متذکّر می‌شوید!'),
(3222, 3222, 'أَمَّنْ يَهْدِيكُمْ فِي ظُلُمَاتِ الْبَرِّ وَالْبَحْرِ وَمَنْ يُرْسِلُ الرِّيَاحَ بُشْرًا بَيْنَ يَدَيْ رَحْمَتِهِ ۗ أَإِلَٰهٌ مَعَ اللَّهِ ۚ تَعَالَى اللَّهُ عَمَّا يُشْرِكُونَ', 27, 63, 20, 77, 382, 'یا کسی که شما را در تاریکیهای صحرا و دریا هدایت می‌کند، و کسی که بادها را بعنوان بشارت پیش از نزول رحمتش می‌فرستد؛ آیا معبودی با خداست؟! خداوند برتر است از آنچه برای او شریک قرارمی‌دهند!'),
(3223, 3223, 'أَمَّنْ يَبْدَأُ الْخَلْقَ ثُمَّ يُعِيدُهُ وَمَنْ يَرْزُقُكُمْ مِنَ السَّمَاءِ وَالْأَرْضِ ۗ أَإِلَٰهٌ مَعَ اللَّهِ ۚ قُلْ هَاتُوا بُرْهَانَكُمْ إِنْ كُنْتُمْ صَادِقِينَ', 27, 64, 20, 77, 383, 'یا کسی که آفرینش را آغاز کرد، سپس آن را تجدید می‌کند، و کسی که شما را از آسمان و زمین روزی می‌دهد؛ آیا معبودی با خداست؟! بگو: «دلیلتان را بیاورید اگر راست می‌گویید!»'),
(3224, 3224, 'قُلْ لَا يَعْلَمُ مَنْ فِي السَّمَاوَاتِ وَالْأَرْضِ الْغَيْبَ إِلَّا اللَّهُ ۚ وَمَا يَشْعُرُونَ أَيَّانَ يُبْعَثُونَ', 27, 65, 20, 77, 383, 'بگو: «کسانی که در آسمانها و زمین هستند غیب نمی‌دانند جز خدا، و نمی‌دانند کی برانگیخته می‌شوند!»'),
(3225, 3225, 'بَلِ ادَّارَكَ عِلْمُهُمْ فِي الْآخِرَةِ ۚ بَلْ هُمْ فِي شَكٍّ مِنْهَا ۖ بَلْ هُمْ مِنْهَا عَمُونَ', 27, 66, 20, 77, 383, 'آنها = مشرکان‌ اطلاع صحیحی درباره آخرت ندارند؛ بلکه در اصل آن شکّ دارند؛ بلکه نسبت به آن نابینایند!'),
(3226, 3226, 'وَقَالَ الَّذِينَ كَفَرُوا أَإِذَا كُنَّا تُرَابًا وَآبَاؤُنَا أَئِنَّا لَمُخْرَجُونَ', 27, 67, 20, 77, 383, 'و کافران گفتند: «آیا هنگامی که ما و پدرانمان خاک شدیم، (زنده می‌شویم و) از دل خاک بیرون می‌آییم؟!'),
(3227, 3227, 'لَقَدْ وُعِدْنَا هَٰذَا نَحْنُ وَآبَاؤُنَا مِنْ قَبْلُ إِنْ هَٰذَا إِلَّا أَسَاطِيرُ الْأَوَّلِينَ', 27, 68, 20, 77, 383, 'این وعده‌ای است که به ما و پدرانمان از پیش داده شده؛ اینها همان افسانه‌های خرافی پیشینیان است!»'),
(3228, 3228, 'قُلْ سِيرُوا فِي الْأَرْضِ فَانْظُرُوا كَيْفَ كَانَ عَاقِبَةُ الْمُجْرِمِينَ', 27, 69, 20, 77, 383, 'بگو: «در روی زمین سیر کنید و ببینید عاقبت کار مجرمان به کجا رسید!»'),
(3229, 3229, 'وَلَا تَحْزَنْ عَلَيْهِمْ وَلَا تَكُنْ فِي ضَيْقٍ مِمَّا يَمْكُرُونَ', 27, 70, 20, 77, 383, 'از (تکذیب و انکار) آنان غمگین مباش، و سینه‌ات از توطئه آنان تنگ نشود!'),
(3230, 3230, 'وَيَقُولُونَ مَتَىٰ هَٰذَا الْوَعْدُ إِنْ كُنْتُمْ صَادِقِينَ', 27, 71, 20, 77, 383, 'آنها می‌گویند: «این وعده (عذاب که به ما می‌دهید) کی خواهد آمد اگر راست می‌گویید؟!»'),
(3231, 3231, 'قُلْ عَسَىٰ أَنْ يَكُونَ رَدِفَ لَكُمْ بَعْضُ الَّذِي تَسْتَعْجِلُونَ', 27, 72, 20, 77, 383, 'بگو: «شاید پاره‌ای از آنچه درباره آن شتاب می‌کنید، نزدیک و در کنار شما باشد!»'),
(3232, 3232, 'وَإِنَّ رَبَّكَ لَذُو فَضْلٍ عَلَى النَّاسِ وَلَٰكِنَّ أَكْثَرَهُمْ لَا يَشْكُرُونَ', 27, 73, 20, 77, 383, 'مسلّماً پروردگار تو نسبت به مردم، فضل (و رحمت) دارد؛ ولی بیشترشان شکرگزار نیستند!'),
(3233, 3233, 'وَإِنَّ رَبَّكَ لَيَعْلَمُ مَا تُكِنُّ صُدُورُهُمْ وَمَا يُعْلِنُونَ', 27, 74, 20, 77, 383, 'و پروردگارت آنچه را در سینه‌هایشان پنهان می‌دارند و آنچه را آشکار می‌کنند بخوبی می‌داند!'),
(3234, 3234, 'وَمَا مِنْ غَائِبَةٍ فِي السَّمَاءِ وَالْأَرْضِ إِلَّا فِي كِتَابٍ مُبِينٍ', 27, 75, 20, 77, 383, 'و هیچ موجود پنهانی در آسمان و زمین نیست مگر اینکه در کتاب مبین (در لوح محفوظ و علم بی‌پایان پروردگار) ثبت است!'),
(3235, 3235, 'إِنَّ هَٰذَا الْقُرْآنَ يَقُصُّ عَلَىٰ بَنِي إِسْرَائِيلَ أَكْثَرَ الَّذِي هُمْ فِيهِ يَخْتَلِفُونَ', 27, 76, 20, 77, 383, 'این قرآن اکثر چیزهایی را که بنی اسرائیل در آن اختلاف دارند برای آنان بیان می‌کند؛'),
(3236, 3236, 'وَإِنَّهُ لَهُدًى وَرَحْمَةٌ لِلْمُؤْمِنِينَ', 27, 77, 20, 77, 384, 'و مایه هدایت و رحمت برای مؤمنان است!'),
(3237, 3237, 'إِنَّ رَبَّكَ يَقْضِي بَيْنَهُمْ بِحُكْمِهِ ۚ وَهُوَ الْعَزِيزُ الْعَلِيمُ', 27, 78, 20, 77, 384, 'پروردگار تو میان آنها در قیامت به حکم خود داوری می‌کند؛ و اوست قادر دانا.'),
(3238, 3238, 'فَتَوَكَّلْ عَلَى اللَّهِ ۖ إِنَّكَ عَلَى الْحَقِّ الْمُبِينِ', 27, 79, 20, 77, 384, 'پس بر خدا توکّل کن، که تو بر حقّ آشکار هستی!'),
(3239, 3239, 'إِنَّكَ لَا تُسْمِعُ الْمَوْتَىٰ وَلَا تُسْمِعُ الصُّمَّ الدُّعَاءَ إِذَا وَلَّوْا مُدْبِرِينَ', 27, 80, 20, 77, 384, 'مسلّماً تو نمی‌توانی سخنت را به گوش مردگان برسانی، و نمی‌توانی کران را هنگامی که روی برمی‌گردانند و پشت می‌کنند فراخوانی!'),
(3240, 3240, 'وَمَا أَنْتَ بِهَادِي الْعُمْيِ عَنْ ضَلَالَتِهِمْ ۖ إِنْ تُسْمِعُ إِلَّا مَنْ يُؤْمِنُ بِآيَاتِنَا فَهُمْ مُسْلِمُونَ', 27, 81, 20, 77, 384, 'و نیز نمی‌توانی کوران را از گمراهیشان برهانی؛ تو فقط میتوانی سخن خود را به گوش کسانی برسانی که آماده پذیرش ایمان به آیات ما هستند و در برابر حق تسلیمند!'),
(3241, 3241, ' وَإِذَا وَقَعَ الْقَوْلُ عَلَيْهِمْ أَخْرَجْنَا لَهُمْ دَابَّةً مِنَ الْأَرْضِ تُكَلِّمُهُمْ أَنَّ النَّاسَ كَانُوا بِآيَاتِنَا لَا يُوقِنُونَ', 27, 82, 20, 77, 384, 'و هنگامی که فرمان عذاب آنها رسد (و در آستانه رستاخیز قرار گیرند)، جنبنده‌ای را از زمین برای آنها خارج می‌کنیم که با آنان تکلّم می‌کند (و می‌گوید) که مردم به آیات ما ایمان نمی‌آوردند.'),
(3242, 3242, 'وَيَوْمَ نَحْشُرُ مِنْ كُلِّ أُمَّةٍ فَوْجًا مِمَّنْ يُكَذِّبُ بِآيَاتِنَا فَهُمْ يُوزَعُونَ', 27, 83, 20, 77, 384, '(به خاطر آور) روزی را که ما از هر امّتی، گروهی را از کسانی که آیات ما را تکذیب می‌کردند محشور می‌کنیم؛ و آنها را نگه می‌داریم تا به یکدیگر ملحق شوند!'),
(3243, 3243, 'حَتَّىٰ إِذَا جَاءُوا قَالَ أَكَذَّبْتُمْ بِآيَاتِي وَلَمْ تُحِيطُوا بِهَا عِلْمًا أَمَّاذَا كُنْتُمْ تَعْمَلُونَ', 27, 84, 20, 77, 384, 'تا زمانی که (به پای حساب) می‌آیند، (به آنان) می‌گوید: «آیا آیات مرا تکذیب کردید و در صدد تحقیق برنیامدید؟! شما چه اعمالی انجام می‌دادید؟!»'),
(3244, 3244, 'وَوَقَعَ الْقَوْلُ عَلَيْهِمْ بِمَا ظَلَمُوا فَهُمْ لَا يَنْطِقُونَ', 27, 85, 20, 77, 384, 'در این هنگام، فرمان عذاب بخاطر ظلمشان بر آنها واقع می‌شود، و آنها سخنی ندارند که بگویند!'),
(3245, 3245, 'أَلَمْ يَرَوْا أَنَّا جَعَلْنَا اللَّيْلَ لِيَسْكُنُوا فِيهِ وَالنَّهَارَ مُبْصِرًا ۚ إِنَّ فِي ذَٰلِكَ لَآيَاتٍ لِقَوْمٍ يُؤْمِنُونَ', 27, 86, 20, 77, 384, 'آیا ندیدند که ما شب را برای آرامش آنها قرار دادیم و روز را روشنی‌بخش؟! در این امور نشانه‌های روشنی است برای کسانی که ایمان می‌آورند (و آماده قبول حقند).'),
(3246, 3246, 'وَيَوْمَ يُنْفَخُ فِي الصُّورِ فَفَزِعَ مَنْ فِي السَّمَاوَاتِ وَمَنْ فِي الْأَرْضِ إِلَّا مَنْ شَاءَ اللَّهُ ۚ وَكُلٌّ أَتَوْهُ دَاخِرِينَ', 27, 87, 20, 77, 384, 'و (به خاطر آورید) روزی را که در «صور» دمیده می‌شود، و تمام کسانی که در آسمانها و زمین هستند در وحشت فرو می‌روند، جز کسانی که خدا خواسته؛ و همگی با خضوع در پیشگاه او حاضر می‌شوند!'),
(3247, 3247, 'وَتَرَى الْجِبَالَ تَحْسَبُهَا جَامِدَةً وَهِيَ تَمُرُّ مَرَّ السَّحَابِ ۚ صُنْعَ اللَّهِ الَّذِي أَتْقَنَ كُلَّ شَيْءٍ ۚ إِنَّهُ خَبِيرٌ بِمَا تَفْعَلُونَ', 27, 88, 20, 77, 384, 'کوه‌ها را می‌بینی، و آنها را ساکن و جامد می‌پنداری، در حالی که مانند ابر در حرکتند؛ این صنع و آفرینش خداوندی است که همه چیز را متقن آفریده؛ او از کارهایی که شما انجام می‌دهید مسلّماً آگاه است!'),
(3248, 3248, 'مَنْ جَاءَ بِالْحَسَنَةِ فَلَهُ خَيْرٌ مِنْهَا وَهُمْ مِنْ فَزَعٍ يَوْمَئِذٍ آمِنُونَ', 27, 89, 20, 77, 385, 'کسانی که کار نیکی انجام دهند پاداشی بهتر از آن خواهند داشت؛ و آنان از وحشت آن روز درامانند!'),
(3249, 3249, 'وَمَنْ جَاءَ بِالسَّيِّئَةِ فَكُبَّتْ وُجُوهُهُمْ فِي النَّارِ هَلْ تُجْزَوْنَ إِلَّا مَا كُنْتُمْ تَعْمَلُونَ', 27, 90, 20, 77, 385, 'و آنها که اعمال بدی انجام دهند، به صورت در آتش افکنده می‌شوند؛ آیا جزایی جز آنچه عمل می‌کردید خواهید داشت؟!'),
(3250, 3250, 'إِنَّمَا أُمِرْتُ أَنْ أَعْبُدَ رَبَّ هَٰذِهِ الْبَلْدَةِ الَّذِي حَرَّمَهَا وَلَهُ كُلُّ شَيْءٍ ۖ وَأُمِرْتُ أَنْ أَكُونَ مِنَ الْمُسْلِمِينَ', 27, 91, 20, 77, 385, '(بگو:) من مأمورم پروردگار این شهر (مقدّس مکّه) را عبادت کنم، همان کسی که این شهر را حرمت بخشیده؛ در حالی که همه چیز از آن اوست! و من مأمورم که از مسلمین باشم؛'),
(3251, 3251, 'وَأَنْ أَتْلُوَ الْقُرْآنَ ۖ فَمَنِ اهْتَدَىٰ فَإِنَّمَا يَهْتَدِي لِنَفْسِهِ ۖ وَمَنْ ضَلَّ فَقُلْ إِنَّمَا أَنَا مِنَ الْمُنْذِرِينَ', 27, 92, 20, 77, 385, 'و اینکه قرآن را تلاوت کنم! هر کس هدایت شود بسود خود هدایت شده؛ و هر کس گمراه گردد (زیانش متوجّه خود اوست؛) بگو: «من فقط از انذارکنندگانم!»'),
(3252, 3252, 'وَقُلِ الْحَمْدُ لِلَّهِ سَيُرِيكُمْ آيَاتِهِ فَتَعْرِفُونَهَا ۚ وَمَا رَبُّكَ بِغَافِلٍ عَمَّا تَعْمَلُونَ', 27, 93, 20, 77, 385, 'بگو: «حمد و ستایش مخصوص ذات خداست؛ بزودی آیاتش را به شما نشان می‌دهد تا آن را بشناسید؛ و پروردگار تو از آنچه انجام می‌دهید غافل نیست!'),
(3253, 3253, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ طسم', 28, 1, 20, 77, 385, 'طسم!'),
(3254, 3254, 'تِلْكَ آيَاتُ الْكِتَابِ الْمُبِينِ', 28, 2, 20, 77, 385, 'اینها از آیات کتاب مبین است!'),
(3255, 3255, 'نَتْلُو عَلَيْكَ مِنْ نَبَإِ مُوسَىٰ وَفِرْعَوْنَ بِالْحَقِّ لِقَوْمٍ يُؤْمِنُونَ', 28, 3, 20, 77, 385, 'ما از داستان موسی و فرعون بحقّ بر تو می‌خوانیم، برای گروهی که (طالب حقّند و) ایمان می‌آورند!'),
(3256, 3256, 'إِنَّ فِرْعَوْنَ عَلَا فِي الْأَرْضِ وَجَعَلَ أَهْلَهَا شِيَعًا يَسْتَضْعِفُ طَائِفَةً مِنْهُمْ يُذَبِّحُ أَبْنَاءَهُمْ وَيَسْتَحْيِي نِسَاءَهُمْ ۚ إِنَّهُ كَانَ مِنَ الْمُفْسِدِينَ', 28, 4, 20, 77, 385, 'فرعون در زمین برتری‌جویی کرد، و اهل آن را به گروه‌های مختلفی تقسیم نمود؛ گروهی را به ضعف و ناتوانی می‌کشاند، پسرانشان را سر می‌برید و زنانشان را (برای کنیزی و خدمت) زنده نگه می‌داشت؛ او به یقین از مفسدان بود!'),
(3257, 3257, 'وَنُرِيدُ أَنْ نَمُنَّ عَلَى الَّذِينَ اسْتُضْعِفُوا فِي الْأَرْضِ وَنَجْعَلَهُمْ أَئِمَّةً وَنَجْعَلَهُمُ الْوَارِثِينَ', 28, 5, 20, 77, 385, 'ما می‌خواهیم بر مستضعفان زمین منّت نهیم و آنان را پیشوایان و وارثان روی زمین قرار دهیم!'),
(3258, 3258, 'وَنُمَكِّنَ لَهُمْ فِي الْأَرْضِ وَنُرِيَ فِرْعَوْنَ وَهَامَانَ وَجُنُودَهُمَا مِنْهُمْ مَا كَانُوا يَحْذَرُونَ', 28, 6, 20, 77, 386, 'و حکومتشان را در زمین پابرجا سازیم؛ و به فرعون و هامان و لشکریانشان، آنچه را از آنها = بنی اسرائیل‌ بیم داشتند نشان دهیم!'),
(3259, 3259, 'وَأَوْحَيْنَا إِلَىٰ أُمِّ مُوسَىٰ أَنْ أَرْضِعِيهِ ۖ فَإِذَا خِفْتِ عَلَيْهِ فَأَلْقِيهِ فِي الْيَمِّ وَلَا تَخَافِي وَلَا تَحْزَنِي ۖ إِنَّا رَادُّوهُ إِلَيْكِ وَجَاعِلُوهُ مِنَ الْمُرْسَلِينَ', 28, 7, 20, 77, 386, 'ما به مادر موسی الهام کردیم که: «او را شیر ده؛ و هنگامی که بر او ترسیدی، وی را در دریا (ی نیل) بیفکن؛ و نترس و غمگین مباش، که ما او را به تو بازمی‌گردانیم، و او را از رسولان قرار می‌دهیم!»'),
(3260, 3260, 'فَالْتَقَطَهُ آلُ فِرْعَوْنَ لِيَكُونَ لَهُمْ عَدُوًّا وَحَزَنًا ۗ إِنَّ فِرْعَوْنَ وَهَامَانَ وَجُنُودَهُمَا كَانُوا خَاطِئِينَ', 28, 8, 20, 77, 386, '(هنگامی که مادر بفرمان خدا او را به دریا افکند) خاندان فرعون او را از آب گرفتند، تا سرانجام دشمن آنان و مایه اندوهشان گردد! مسلّماً فرعون و هامان و لشکریانشان خطاکار بودند.'),
(3261, 3261, 'وَقَالَتِ امْرَأَتُ فِرْعَوْنَ قُرَّتُ عَيْنٍ لِي وَلَكَ ۖ لَا تَقْتُلُوهُ عَسَىٰ أَنْ يَنْفَعَنَا أَوْ نَتَّخِذَهُ وَلَدًا وَهُمْ لَا يَشْعُرُونَ', 28, 9, 20, 77, 386, 'همسر فرعون (چون دید آنها قصد کشتن کودک را دارند) گفت: «نور چشم من و توست! او را نکشید شاید برای ما مفید باشد، یا او را بعنوان پسر خود برگزینیم!» و آنها نمی‌فهمیدند (که دشمن اصلی خود را در آغوش خویش می‌پرورانند)!'),
(3262, 3262, 'وَأَصْبَحَ فُؤَادُ أُمِّ مُوسَىٰ فَارِغًا ۖ إِنْ كَادَتْ لَتُبْدِي بِهِ لَوْلَا أَنْ رَبَطْنَا عَلَىٰ قَلْبِهَا لِتَكُونَ مِنَ الْمُؤْمِنِينَ', 28, 10, 20, 77, 386, '(سرانجام) قلب مادر موسی از همه چیز (جز یاد فرزندش) تهی گشت؛ و اگر دل او را (بوسیله ایمان و امید) محکم نکرده بودیم، نزدیک بود مطلب را افشا کند!'),
(3263, 3263, 'وَقَالَتْ لِأُخْتِهِ قُصِّيهِ ۖ فَبَصُرَتْ بِهِ عَنْ جُنُبٍ وَهُمْ لَا يَشْعُرُونَ', 28, 11, 20, 77, 386, 'و (مادر موسی) به خواهر او گفت: «وضع حال او را پیگیری کن!» او نیز از دور ماجرا را مشاهده کرد در حالی که آنان بی‌خبر بودند.'),
(3264, 3264, ' وَحَرَّمْنَا عَلَيْهِ الْمَرَاضِعَ مِنْ قَبْلُ فَقَالَتْ هَلْ أَدُلُّكُمْ عَلَىٰ أَهْلِ بَيْتٍ يَكْفُلُونَهُ لَكُمْ وَهُمْ لَهُ نَاصِحُونَ', 28, 12, 20, 78, 386, 'ما همه زنان شیرده را از پیش بر او حرام کردیم (تا تنها به آغوش مادر بازگردد)؛ و خواهرش (که بیتابی مأموران را برای پیدا کردن دایه مشاهده کرد) گفت: «آیا شما را به خانواده‌ای راهنمایی کنم که می‌توانند این نوزاد را برای شما کفالت کنند و خیرخواه او باشند؟!»'),
(3265, 3265, 'فَرَدَدْنَاهُ إِلَىٰ أُمِّهِ كَيْ تَقَرَّ عَيْنُهَا وَلَا تَحْزَنَ وَلِتَعْلَمَ أَنَّ وَعْدَ اللَّهِ حَقٌّ وَلَٰكِنَّ أَكْثَرَهُمْ لَا يَعْلَمُونَ', 28, 13, 20, 78, 386, 'ما او را به مادرش بازگرداندیم تا چشمش روشن شود و غمگین نباشد و بداند که وعده الهی حق است؛ ولی بیشتر آنان نمی‌دانند!'),
(3266, 3266, 'وَلَمَّا بَلَغَ أَشُدَّهُ وَاسْتَوَىٰ آتَيْنَاهُ حُكْمًا وَعِلْمًا ۚ وَكَذَٰلِكَ نَجْزِي الْمُحْسِنِينَ', 28, 14, 20, 78, 387, 'و هنگامی که (موسی) نیرومند و کامل شد، حکمت و دانش به او دادیم؛ و این گونه نیکوکاران را جزا می‌دهیم!'),
(3267, 3267, 'وَدَخَلَ الْمَدِينَةَ عَلَىٰ حِينِ غَفْلَةٍ مِنْ أَهْلِهَا فَوَجَدَ فِيهَا رَجُلَيْنِ يَقْتَتِلَانِ هَٰذَا مِنْ شِيعَتِهِ وَهَٰذَا مِنْ عَدُوِّهِ ۖ فَاسْتَغَاثَهُ الَّذِي مِنْ شِيعَتِهِ عَلَى الَّذِي مِنْ عَدُوِّهِ فَوَكَزَهُ مُوسَىٰ فَقَضَىٰ عَلَيْهِ ۖ قَالَ هَٰذَا مِنْ عَمَلِ الشَّيْطَانِ ۖ إِنَّهُ عَدُوٌّ مُضِلٌّ مُبِينٌ', 28, 15, 20, 78, 387, 'او به هنگامی که اهل شهر در غفلت بودند وارد شهر شد؛ ناگهان دو مرد را دید که به جنگ و نزاع مشغولند؛ یکی از پیروان او بود (و از بنی اسرائیل)، و دیگری از دشمنانش، آن که از پیروان او بود در برابر دشمنش از وی تقاضای کمک نمود؛ موسی مشت محکمی بر سینه او زد و کار او را ساخت (و بر زمین افتاد و مرد)؛ موسی گفت: «این (نزاع شما) از عمل شیطان بود، که او دشمن و گمراه‌کننده آشکاری است»'),
(3268, 3268, 'قَالَ رَبِّ إِنِّي ظَلَمْتُ نَفْسِي فَاغْفِرْ لِي فَغَفَرَ لَهُ ۚ إِنَّهُ هُوَ الْغَفُورُ الرَّحِيمُ', 28, 16, 20, 78, 387, '(سپس) عرض کرد: «پروردگارا! من به خویشتن ستم کردم؛ مرا ببخش!» خداوند او را بخشید، که او غفور و رحیم است!'),
(3269, 3269, 'قَالَ رَبِّ بِمَا أَنْعَمْتَ عَلَيَّ فَلَنْ أَكُونَ ظَهِيرًا لِلْمُجْرِمِينَ', 28, 17, 20, 78, 387, 'عرض کرد: «پروردگارا! بشکرانه نعمتی که به من دادی، هرگز پشتیبان مجرمان نخواهم بود!»'),
(3270, 3270, 'فَأَصْبَحَ فِي الْمَدِينَةِ خَائِفًا يَتَرَقَّبُ فَإِذَا الَّذِي اسْتَنْصَرَهُ بِالْأَمْسِ يَسْتَصْرِخُهُ ۚ قَالَ لَهُ مُوسَىٰ إِنَّكَ لَغَوِيٌّ مُبِينٌ', 28, 18, 20, 78, 387, 'موسی در شهر ترسان بود و هر لحظه در انتظار حادثه‌ای (و در جستجوی اخبار)؛ ناگهان دید همان کسی که دیروز از او یاری طلبیده بود فریاد می‌زند و از او کمک می‌خواهد، موسی به او گفت: «تو آشکارا انسان (ماجراجو و) گمراهی هستی!»'),
(3271, 3271, 'فَلَمَّا أَنْ أَرَادَ أَنْ يَبْطِشَ بِالَّذِي هُوَ عَدُوٌّ لَهُمَا قَالَ يَا مُوسَىٰ أَتُرِيدُ أَنْ تَقْتُلَنِي كَمَا قَتَلْتَ نَفْسًا بِالْأَمْسِ ۖ إِنْ تُرِيدُ إِلَّا أَنْ تَكُونَ جَبَّارًا فِي الْأَرْضِ وَمَا تُرِيدُ أَنْ تَكُونَ مِنَ الْمُصْلِحِينَ', 28, 19, 20, 78, 387, 'و هنگامی که خواست با کسی که دشمن هر دوی آنها بود درگیر شود و با قدرت مانع او گردد، (فریادش بلند شد،) گفت: «ای موسی می‌خواهی! مرا بکشی همان گونه که دیروز انسانی را کشتی؟! تو فقط می‌خواهی جبّاری در روی زمین باشی، و نمی‌خواهی از مصلحان باشی!»'),
(3272, 3272, 'وَجَاءَ رَجُلٌ مِنْ أَقْصَى الْمَدِينَةِ يَسْعَىٰ قَالَ يَا مُوسَىٰ إِنَّ الْمَلَأَ يَأْتَمِرُونَ بِكَ لِيَقْتُلُوكَ فَاخْرُجْ إِنِّي لَكَ مِنَ النَّاصِحِينَ', 28, 20, 20, 78, 387, '(در این هنگام) مردی با سرعت از دورترین نقطه شهر = مرکز فرعونیان‌ آمد و گفت: «ای موسی! این جمعیّت برای کشتن تو به مشورت نشسته‌اند؛ فوراً از شهر خارج شو، که من از خیرخواهان توام!»'),
(3273, 3273, 'فَخَرَجَ مِنْهَا خَائِفًا يَتَرَقَّبُ ۖ قَالَ رَبِّ نَجِّنِي مِنَ الْقَوْمِ الظَّالِمِينَ', 28, 21, 20, 78, 387, 'موسی از شهر خارج شد در حالی که ترسان بود و هر لحظه در انتظار حادثه‌ای؛ عرض کرد: «پروردگارا! مرا از این قوم ظالم رهایی بخش!»'),
(3274, 3274, 'وَلَمَّا تَوَجَّهَ تِلْقَاءَ مَدْيَنَ قَالَ عَسَىٰ رَبِّي أَنْ يَهْدِيَنِي سَوَاءَ السَّبِيلِ', 28, 22, 20, 78, 388, 'و هنگامی که متوجّه جانب مدین شد گفت: «امیدوارم پروردگارم مرا به راه راست هدایت کند!»'),
(3275, 3275, 'وَلَمَّا وَرَدَ مَاءَ مَدْيَنَ وَجَدَ عَلَيْهِ أُمَّةً مِنَ النَّاسِ يَسْقُونَ وَوَجَدَ مِنْ دُونِهِمُ امْرَأَتَيْنِ تَذُودَانِ ۖ قَالَ مَا خَطْبُكُمَا ۖ قَالَتَا لَا نَسْقِي حَتَّىٰ يُصْدِرَ الرِّعَاءُ ۖ وَأَبُونَا شَيْخٌ كَبِيرٌ', 28, 23, 20, 78, 388, 'و هنگامی که به (چاه) آب مدین رسید، گروهی از مردم را در آنجا دید که چهارپایان خود را سیراب می‌کنند؛ و در کنار آنان دو زن را دید که مراقب گوسفندان خویشند (و به چاه نزدیک نمی‌شوند؛ موسی) به آن دو گفت: «کار شما چیست؟ (چرا گوسفندان خود را آب نمی‌دهید؟!)» گفتند: «ما آنها را آب نمی‌دهیم تا چوپانها همگی خارج شوند؛ و پدر ما پیرمرد کهنسالی است (و قادر بر این کارها نیست.)!»'),
(3276, 3276, 'فَسَقَىٰ لَهُمَا ثُمَّ تَوَلَّىٰ إِلَى الظِّلِّ فَقَالَ رَبِّ إِنِّي لِمَا أَنْزَلْتَ إِلَيَّ مِنْ خَيْرٍ فَقِيرٌ', 28, 24, 20, 78, 388, 'موسی برای (گوسفندان) آن دو آب کشید؛ سپس رو به سایه آورد و عرض کرد: «پروردگارا! هر خیر و نیکی بر من فرستی، به آن نیازمندم!»'),
(3277, 3277, 'فَجَاءَتْهُ إِحْدَاهُمَا تَمْشِي عَلَى اسْتِحْيَاءٍ قَالَتْ إِنَّ أَبِي يَدْعُوكَ لِيَجْزِيَكَ أَجْرَ مَا سَقَيْتَ لَنَا ۚ فَلَمَّا جَاءَهُ وَقَصَّ عَلَيْهِ الْقَصَصَ قَالَ لَا تَخَفْ ۖ نَجَوْتَ مِنَ الْقَوْمِ الظَّالِمِينَ', 28, 25, 20, 78, 388, 'ناگهان یکی از آن دو (زن) به سراغ او آمد در حالی که با نهایت حیا گام برمی‌داشت، گفت: «پدرم از تو دعوت می‌کند تا مزد آب دادن (به گوسفندان) را که برای ما انجام دادی به تو بپردازد.» هنگامی که موسی نزد او = شعیب‌ آمد و سرگذشت خود را شرح داد، گفت: «نترس، از قوم ظالم نجات یافتی!»'),
(3278, 3278, 'قَالَتْ إِحْدَاهُمَا يَا أَبَتِ اسْتَأْجِرْهُ ۖ إِنَّ خَيْرَ مَنِ اسْتَأْجَرْتَ الْقَوِيُّ الْأَمِينُ', 28, 26, 20, 78, 388, 'یکی از آن دو (دختر) گفت: «پدرم! او را استخدام کن، زیرا بهترین کسی را که می‌توانی استخدام کنی آن کسی است که قویّ و امین باشد (و او همین مرد است)!»'),
(3279, 3279, 'قَالَ إِنِّي أُرِيدُ أَنْ أُنْكِحَكَ إِحْدَى ابْنَتَيَّ هَاتَيْنِ عَلَىٰ أَنْ تَأْجُرَنِي ثَمَانِيَ حِجَجٍ ۖ فَإِنْ أَتْمَمْتَ عَشْرًا فَمِنْ عِنْدِكَ ۖ وَمَا أُرِيدُ أَنْ أَشُقَّ عَلَيْكَ ۚ سَتَجِدُنِي إِنْ شَاءَ اللَّهُ مِنَ الصَّالِحِينَ', 28, 27, 20, 78, 388, '(شعیب) گفت: «من می‌خواهم یکی از این دو دخترم را به همسری تو درآورم به این شرط که هشت سال برای من کار کنی؛ و اگر آن را تا ده سال افزایش دهی، محبّتی از ناحیه توست؛ من نمی‌خواهم کار سنگینی بر دوش تو بگذارم؛ و ان شاء الله مرا از صالحان خواهی یافت»'),
(3280, 3280, 'قَالَ ذَٰلِكَ بَيْنِي وَبَيْنَكَ ۖ أَيَّمَا الْأَجَلَيْنِ قَضَيْتُ فَلَا عُدْوَانَ عَلَيَّ ۖ وَاللَّهُ عَلَىٰ مَا نَقُولُ وَكِيلٌ', 28, 28, 20, 78, 388, '(موسی) گفت: «(مانعی ندارد،) این قراردادی میان من و تو باشد؛ البته هر کدام از این دو مدّت را انجام دهم ستمی بر من نخواهد بود (و من در انتخاب آن آزادم)! و خدا بر آنچه ما می‌گوییم گواه است!»'),
(3281, 3281, ' فَلَمَّا قَضَىٰ مُوسَى الْأَجَلَ وَسَارَ بِأَهْلِهِ آنَسَ مِنْ جَانِبِ الطُّورِ نَارًا قَالَ لِأَهْلِهِ امْكُثُوا إِنِّي آنَسْتُ نَارًا لَعَلِّي آتِيكُمْ مِنْهَا بِخَبَرٍ أَوْ جَذْوَةٍ مِنَ النَّارِ لَعَلَّكُمْ تَصْطَلُونَ', 28, 29, 20, 78, 389, 'هنگامی که موسی مدّت خود را به پایان رسانید و همراه خانواده‌اش (از مدین به سوی مصر) حرکت کرد، از جانب طور آتشی دید! به خانواده‌اش گفت: «درنگ کنید که من آتشی دیدم! (می‌روم) شاید خبری از آن برای شما بیاورم، یا شعله‌ای از آتش تا با آن گرم شوید!»'),
(3282, 3282, 'فَلَمَّا أَتَاهَا نُودِيَ مِنْ شَاطِئِ الْوَادِ الْأَيْمَنِ فِي الْبُقْعَةِ الْمُبَارَكَةِ مِنَ الشَّجَرَةِ أَنْ يَا مُوسَىٰ إِنِّي أَنَا اللَّهُ رَبُّ الْعَالَمِينَ', 28, 30, 20, 78, 389, 'هنگامی که به سراغ آتش آمد، از کرانه راست درّه، در آن سرزمین پر برکت، از میان یک درخت ندا داده شد که: «ای موسی! منم خداوند، پروردگار جهانیان!'),
(3283, 3283, 'وَأَنْ أَلْقِ عَصَاكَ ۖ فَلَمَّا رَآهَا تَهْتَزُّ كَأَنَّهَا جَانٌّ وَلَّىٰ مُدْبِرًا وَلَمْ يُعَقِّبْ ۚ يَا مُوسَىٰ أَقْبِلْ وَلَا تَخَفْ ۖ إِنَّكَ مِنَ الْآمِنِينَ', 28, 31, 20, 78, 389, 'عصایت را بیفکن!» هنگامی که (عصا را افکند و) دید همچون ماری با سرعت حرکت می‌کند، ترسید و به عقب برگشت، و حتّی پشت سر خود را نگاه نکرد! ندا آمد: «برگرد و نترس، تو در امان هستی!'),
(3284, 3284, 'اسْلُكْ يَدَكَ فِي جَيْبِكَ تَخْرُجْ بَيْضَاءَ مِنْ غَيْرِ سُوءٍ وَاضْمُمْ إِلَيْكَ جَنَاحَكَ مِنَ الرَّهْبِ ۖ فَذَانِكَ بُرْهَانَانِ مِنْ رَبِّكَ إِلَىٰ فِرْعَوْنَ وَمَلَئِهِ ۚ إِنَّهُمْ كَانُوا قَوْمًا فَاسِقِينَ', 28, 32, 20, 78, 389, 'دستت را در گریبان خود فروبر، هنگامی که خارج می‌شود سفید و درخشنده است بدون عیب و نقص؛ و دستهایت را بر سینه‌ات بگذار، تا ترس و وحشت از تو دور شود! این دو = معجزه عصا و ید بیضا برهان روشن از پروردگارت بسوی فرعون و اطرافیان اوست، که آنان قوم فاسقی هستند!»'),
(3285, 3285, 'قَالَ رَبِّ إِنِّي قَتَلْتُ مِنْهُمْ نَفْسًا فَأَخَافُ أَنْ يَقْتُلُونِ', 28, 33, 20, 78, 389, 'عرض کرد: «پروردگارا! من یک تن از آنان را کشته‌ام؛ می‌ترسم مرا به قتل برسانند!'),
(3286, 3286, 'وَأَخِي هَارُونُ هُوَ أَفْصَحُ مِنِّي لِسَانًا فَأَرْسِلْهُ مَعِيَ رِدْءًا يُصَدِّقُنِي ۖ إِنِّي أَخَافُ أَنْ يُكَذِّبُونِ', 28, 34, 20, 78, 389, 'و برادرم هارون زبانش از من فصیحتر است؛ او را همراه من بفرست تا یاور من باشد و مرا تصدیق کند؛ می‌ترسم مرا تکذیب کنند!»'),
(3287, 3287, 'قَالَ سَنَشُدُّ عَضُدَكَ بِأَخِيكَ وَنَجْعَلُ لَكُمَا سُلْطَانًا فَلَا يَصِلُونَ إِلَيْكُمَا ۚ بِآيَاتِنَا أَنْتُمَا وَمَنِ اتَّبَعَكُمَا الْغَالِبُونَ', 28, 35, 20, 78, 389, 'فرمود: «بزودی بازوان تو را بوسیله برادرت محکم (و نیرومند) می‌کنیم، و برای شما سلطه و برتری قرارمی‌دهیم؛ و به برکت آیات ما، بر شما دست نمی‌یابند؛ شما و پیروانتان پیروزید!»'),
(3288, 3288, 'فَلَمَّا جَاءَهُمْ مُوسَىٰ بِآيَاتِنَا بَيِّنَاتٍ قَالُوا مَا هَٰذَا إِلَّا سِحْرٌ مُفْتَرًى وَمَا سَمِعْنَا بِهَٰذَا فِي آبَائِنَا الْأَوَّلِينَ', 28, 36, 20, 78, 390, 'هنگامی که موسی معجزات روشن ما را برای آنان آورد، گفتند: «این چیزی جز سحر نیست که بدروغ به خدا بسته شده؛ ما هرگز چنین چیزی را در نیاکان خود نشنیده‌ایم!»'),
(3289, 3289, 'وَقَالَ مُوسَىٰ رَبِّي أَعْلَمُ بِمَنْ جَاءَ بِالْهُدَىٰ مِنْ عِنْدِهِ وَمَنْ تَكُونُ لَهُ عَاقِبَةُ الدَّارِ ۖ إِنَّهُ لَا يُفْلِحُ الظَّالِمُونَ', 28, 37, 20, 78, 390, 'موسی گفت: «پروردگارم از حال کسانی که هدایت را از نزد او آورده‌اند، و کسانی که عاقبت نیک سرا (ی دنیا و آخرت) از آن آنهاست آگاهتر است! مسلّماً ظالمان رستگار نخواهند شد!»'),
(3290, 3290, 'وَقَالَ فِرْعَوْنُ يَا أَيُّهَا الْمَلَأُ مَا عَلِمْتُ لَكُمْ مِنْ إِلَٰهٍ غَيْرِي فَأَوْقِدْ لِي يَا هَامَانُ عَلَى الطِّينِ فَاجْعَلْ لِي صَرْحًا لَعَلِّي أَطَّلِعُ إِلَىٰ إِلَٰهِ مُوسَىٰ وَإِنِّي لَأَظُنُّهُ مِنَ الْكَاذِبِينَ', 28, 38, 20, 78, 390, 'فرعون گفت: «ای جمعیت اشراف! من خدایی جز خودم برای شما سراغ ندارم. (امّا برای تحقیق بیشتر،) ای هامان، برایم آتشی بر گل بیفروز (و آجرهای محکم بساز)، و برای من برج بلندی ترتیب ده تا از خدای موسی خبر گیرم؛ هر چند من گمان می‌کنم او از دروغگویان است!»'),
(3291, 3291, 'وَاسْتَكْبَرَ هُوَ وَجُنُودُهُ فِي الْأَرْضِ بِغَيْرِ الْحَقِّ وَظَنُّوا أَنَّهُمْ إِلَيْنَا لَا يُرْجَعُونَ', 28, 39, 20, 78, 390, '(سرانجام) فرعون و لشکریانش بدون حقّ در زمین استکبار کردند، و پنداشتند بسوی ما بازگردانده نمی‌شوند!'),
(3292, 3292, 'فَأَخَذْنَاهُ وَجُنُودَهُ فَنَبَذْنَاهُمْ فِي الْيَمِّ ۖ فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ الظَّالِمِينَ', 28, 40, 20, 78, 390, 'ما نیز او و لشکریانش را گرفتیم و به دریا افکندیم؛ اکنون بنگر پایان کار ظالمان چگونه بود!'),
(3293, 3293, 'وَجَعَلْنَاهُمْ أَئِمَّةً يَدْعُونَ إِلَى النَّارِ ۖ وَيَوْمَ الْقِيَامَةِ لَا يُنْصَرُونَ', 28, 41, 20, 78, 390, 'و آنان = فرعونیان‌ را پیشوایانی قرار دادیم که به آتش (دوزخ) دعوت می‌کنند؛ و روز رستاخیز یاری نخواهند شد!'),
(3294, 3294, 'وَأَتْبَعْنَاهُمْ فِي هَٰذِهِ الدُّنْيَا لَعْنَةً ۖ وَيَوْمَ الْقِيَامَةِ هُمْ مِنَ الْمَقْبُوحِينَ', 28, 42, 20, 78, 390, 'و در این دنیا نیز لعنتی بدنبال آنان قرار دادیم؛ و روز قیامت از زشت‌رویانند!'),
(3295, 3295, 'وَلَقَدْ آتَيْنَا مُوسَى الْكِتَابَ مِنْ بَعْدِ مَا أَهْلَكْنَا الْقُرُونَ الْأُولَىٰ بَصَائِرَ لِلنَّاسِ وَهُدًى وَرَحْمَةً لَعَلَّهُمْ يَتَذَكَّرُونَ', 28, 43, 20, 78, 390, 'و ما به موسی کتاب آسمانی دادیم بعد از آنکه اقوام قرون نخستین را هلاک نمودیم؛ کتابی که برای مردم بصیرت‌آفرین بود، و مایه هدایت و رحمت؛ شاید متذکر شوند!'),
(3296, 3296, 'وَمَا كُنْتَ بِجَانِبِ الْغَرْبِيِّ إِذْ قَضَيْنَا إِلَىٰ مُوسَى الْأَمْرَ وَمَا كُنْتَ مِنَ الشَّاهِدِينَ', 28, 44, 20, 78, 391, 'تو در جانب غربی نبودی هنگامی که ما فرمان نبوّت را به موسی دادیم؛ و تو از شاهدان نبودی (در آن هنگام که معجزات را در اختیار موسی گذاردیم)!'),
(3297, 3297, 'وَلَٰكِنَّا أَنْشَأْنَا قُرُونًا فَتَطَاوَلَ عَلَيْهِمُ الْعُمُرُ ۚ وَمَا كُنْتَ ثَاوِيًا فِي أَهْلِ مَدْيَنَ تَتْلُو عَلَيْهِمْ آيَاتِنَا وَلَٰكِنَّا كُنَّا مُرْسِلِينَ', 28, 45, 20, 78, 391, 'ولی ما اقوامی را در اعصار مختلف خلق کردیم، و زمانهای طولانی بر آنها گذشت (که آثار انبیا از دلهایشان محو شد؛ پس تو را با کتاب آسمانیت فرستادیم)! تو هرگز در میان مردم مدین اقامت نداشتی تا (از وضع آنان آگاه باشی و) آیات ما را برای آنها = مشرکان مکّه‌ بخوانی، ولی ما بودیم که تو را فرستادیم (و این آیات را در اختیارت قرار دادیم)!'),
(3298, 3298, 'وَمَا كُنْتَ بِجَانِبِ الطُّورِ إِذْ نَادَيْنَا وَلَٰكِنْ رَحْمَةً مِنْ رَبِّكَ لِتُنْذِرَ قَوْمًا مَا أَتَاهُمْ مِنْ نَذِيرٍ مِنْ قَبْلِكَ لَعَلَّهُمْ يَتَذَكَّرُونَ', 28, 46, 20, 78, 391, 'تو در کنار طور نبودی زمانی که ما ندا دادیم؛ ولی این رحمتی از سوی پروردگارت بود (که این اخبار را در اختیار تو نهاد) تا بوسیله آن قومی را انذار کنی که پیش از تو هیچ انذارکننده‌ای برای آنان نیامده است؛ شاید متذکّر شوند!'),
(3299, 3299, 'وَلَوْلَا أَنْ تُصِيبَهُمْ مُصِيبَةٌ بِمَا قَدَّمَتْ أَيْدِيهِمْ فَيَقُولُوا رَبَّنَا لَوْلَا أَرْسَلْتَ إِلَيْنَا رَسُولًا فَنَتَّبِعَ آيَاتِكَ وَنَكُونَ مِنَ الْمُؤْمِنِينَ', 28, 47, 20, 78, 391, 'هرگاه (پیش از فرستادن پیامبری) مجازات و مصیبتی بر اثر اعمالشان به آنان می‌رسید، می‌گفتند: «پروردگارا! چرا رسولی برای ما نفرستادی تا از آیات تو پیروی کنیم و از مؤمنان باشیم؟!»'),
(3300, 3300, 'فَلَمَّا جَاءَهُمُ الْحَقُّ مِنْ عِنْدِنَا قَالُوا لَوْلَا أُوتِيَ مِثْلَ مَا أُوتِيَ مُوسَىٰ ۚ أَوَلَمْ يَكْفُرُوا بِمَا أُوتِيَ مُوسَىٰ مِنْ قَبْلُ ۖ قَالُوا سِحْرَانِ تَظَاهَرَا وَقَالُوا إِنَّا بِكُلٍّ كَافِرُونَ', 28, 48, 20, 78, 391, 'ولی هنگامی که حقّ از نزد ما برای آنها آمد گفتند: «چرا مثل همان چیزی که به موسی داده شد به این پیامبر داده نشده است؟!» مگر بهانه‌جویانی همانند آنان، معجزاتی را که در گذشته به موسی داده شد، انکار نکردند و گفتند: «این دو نفر = موسی و هارون‌ دو ساحرند که دست به دست هم داده‌اند (تا ما را گمراه کنند) و ما به هر دو کافریم»؟!'),
(3301, 3301, 'قُلْ فَأْتُوا بِكِتَابٍ مِنْ عِنْدِ اللَّهِ هُوَ أَهْدَىٰ مِنْهُمَا أَتَّبِعْهُ إِنْ كُنْتُمْ صَادِقِينَ', 28, 49, 20, 78, 391, 'بگو: «اگر راست می‌گویید (که تورات و قرآن از سوی خدا نیست)، کتابی هدایت‌بخش‌تر از این دو از نزد خدا بیاورید، تا من از آن پیروی کنم!»'),
(3302, 3302, 'فَإِنْ لَمْ يَسْتَجِيبُوا لَكَ فَاعْلَمْ أَنَّمَا يَتَّبِعُونَ أَهْوَاءَهُمْ ۚ وَمَنْ أَضَلُّ مِمَّنِ اتَّبَعَ هَوَاهُ بِغَيْرِ هُدًى مِنَ اللَّهِ ۚ إِنَّ اللَّهَ لَا يَهْدِي الْقَوْمَ الظَّالِمِينَ', 28, 50, 20, 78, 391, 'اگر این پیشنهاد تو را نپذیرند، بدان که آنان تنها از هوسهای خود پیروی می‌کنند! و آیا گمراهتر از آن کس که پیروی هوای نفس خویش کرده و هیچ هدایت الهی را نپذیرفته، کسی پیدا می‌شود؟! مسلّماً خداوند قوم ستمگر را هدایت نمی‌کند!'),
(3303, 3303, ' وَلَقَدْ وَصَّلْنَا لَهُمُ الْقَوْلَ لَعَلَّهُمْ يَتَذَكَّرُونَ', 28, 51, 20, 79, 392, 'ما آیات قرآن را یکی پس از دیگری برای آنان آوردیم شاید متذکّر شوند!'),
(3304, 3304, 'الَّذِينَ آتَيْنَاهُمُ الْكِتَابَ مِنْ قَبْلِهِ هُمْ بِهِ يُؤْمِنُونَ', 28, 52, 20, 79, 392, 'کسانی که قبلاً کتاب آسمانی به آنان داده‌ایم به آن = قرآن‌ ایمان می‌آورند!'),
(3305, 3305, 'وَإِذَا يُتْلَىٰ عَلَيْهِمْ قَالُوا آمَنَّا بِهِ إِنَّهُ الْحَقُّ مِنْ رَبِّنَا إِنَّا كُنَّا مِنْ قَبْلِهِ مُسْلِمِينَ', 28, 53, 20, 79, 392, 'و هنگامی که بر آنان خوانده شود می‌گویند: «به آن ایمان آوردیم؛ اینها همه حق است و از سوی پروردگار ماست؛ ما پیش از این هم مسلمان بودیم!»'),
(3306, 3306, 'أُولَٰئِكَ يُؤْتَوْنَ أَجْرَهُمْ مَرَّتَيْنِ بِمَا صَبَرُوا وَيَدْرَءُونَ بِالْحَسَنَةِ السَّيِّئَةَ وَمِمَّا رَزَقْنَاهُمْ يُنْفِقُونَ', 28, 54, 20, 79, 392, 'آنها کسانی هستند که بخاطر شکیباییشان، اجر و پادششان را دو بار دریافت می‌دارند؛ و بوسیله نیکیها بدیها را دفع می‌کنند؛ و از آنچه به آنان روزی داده‌ایم انفاق می‌نمایند؛'),
(3307, 3307, 'وَإِذَا سَمِعُوا اللَّغْوَ أَعْرَضُوا عَنْهُ وَقَالُوا لَنَا أَعْمَالُنَا وَلَكُمْ أَعْمَالُكُمْ سَلَامٌ عَلَيْكُمْ لَا نَبْتَغِي الْجَاهِلِينَ', 28, 55, 20, 79, 392, 'و هرگاه سخن لغو و بیهوده بشنوند، از آن روی می‌گردانند و می‌گویند: «اعمال ما از آن ماست و اعمال شما از آن خودتان؛ سلام بر شما (سلام وداع)؛ ما خواهان جاهلان نیستیم!»'),
(3308, 3308, 'إِنَّكَ لَا تَهْدِي مَنْ أَحْبَبْتَ وَلَٰكِنَّ اللَّهَ يَهْدِي مَنْ يَشَاءُ ۚ وَهُوَ أَعْلَمُ بِالْمُهْتَدِينَ', 28, 56, 20, 79, 392, 'تو نمی‌توانی کسی را که دوست داری هدایت کنی؛ ولی خداوند هر کس را بخواهد هدایت می‌کند؛ و او به هدایت یافتگان آگاهتر است!'),
(3309, 3309, 'وَقَالُوا إِنْ نَتَّبِعِ الْهُدَىٰ مَعَكَ نُتَخَطَّفْ مِنْ أَرْضِنَا ۚ أَوَلَمْ نُمَكِّنْ لَهُمْ حَرَمًا آمِنًا يُجْبَىٰ إِلَيْهِ ثَمَرَاتُ كُلِّ شَيْءٍ رِزْقًا مِنْ لَدُنَّا وَلَٰكِنَّ أَكْثَرَهُمْ لَا يَعْلَمُونَ', 28, 57, 20, 79, 392, 'آنها گفتند: «ما اگر هدایت را همراه تو پذیرا شویم، ما را از سرزمینمان می‌ربایند!» آیا ما حرم امنی در اختیار آنها قرار ندادیم که ثمرات هر چیزی (از هر شهر و دیاری) بسوی آن آورده می‌شود؟! رزقی است از جانب ما؛ ولی بیشتر آنان نمی‌دانند!'),
(3310, 3310, 'وَكَمْ أَهْلَكْنَا مِنْ قَرْيَةٍ بَطِرَتْ مَعِيشَتَهَا ۖ فَتِلْكَ مَسَاكِنُهُمْ لَمْ تُسْكَنْ مِنْ بَعْدِهِمْ إِلَّا قَلِيلًا ۖ وَكُنَّا نَحْنُ الْوَارِثِينَ', 28, 58, 20, 79, 392, 'و چه بسیار از شهرها و آبادیهایی را که بر اثر فراوانی نعمت، مست و مغرور شده بودند هلاک کردیم! این خانه‌های آنهاست (که ویران شده)، و بعد از آنان جز اندکی کسی در آنها سکونت نکرد؛ و ما وارث آنان بودیم!'),
(3311, 3311, 'وَمَا كَانَ رَبُّكَ مُهْلِكَ الْقُرَىٰ حَتَّىٰ يَبْعَثَ فِي أُمِّهَا رَسُولًا يَتْلُو عَلَيْهِمْ آيَاتِنَا ۚ وَمَا كُنَّا مُهْلِكِي الْقُرَىٰ إِلَّا وَأَهْلُهَا ظَالِمُونَ', 28, 59, 20, 79, 392, 'و پروردگار تو هرگز شهرها و آبادیها را هلاک نمی‌کرد تا اینکه در کانون آنها پیامبری مبعوث کند که آیات ما را بر آنان بخواند؛ و ما هرگز آبادیها و شهرها را هلاک نکردیم مگر آنکه اهلش ظالم بودند!'),
(3312, 3312, 'وَمَا أُوتِيتُمْ مِنْ شَيْءٍ فَمَتَاعُ الْحَيَاةِ الدُّنْيَا وَزِينَتُهَا ۚ وَمَا عِنْدَ اللَّهِ خَيْرٌ وَأَبْقَىٰ ۚ أَفَلَا تَعْقِلُونَ', 28, 60, 20, 79, 393, 'آنچه به شما داده شده، متاع زندگی دنیا و زینت آن است؛ و آنچه نزد خداست بهتر و پایدارتر است؛ آیا اندیشه نمی‌کنید؟!'),
(3313, 3313, 'أَفَمَنْ وَعَدْنَاهُ وَعْدًا حَسَنًا فَهُوَ لَاقِيهِ كَمَنْ مَتَّعْنَاهُ مَتَاعَ الْحَيَاةِ الدُّنْيَا ثُمَّ هُوَ يَوْمَ الْقِيَامَةِ مِنَ الْمُحْضَرِينَ', 28, 61, 20, 79, 393, 'آیا کسی که به او وعده نیکو داده‌ایم و به آن خواهد رسید، همانند کسی است که متاع زندگی دنیا به او داده‌ایم سپس روز قیامت (برای حساب و جزا) از احضارشدگان خواهد بود؟!'),
(3314, 3314, 'وَيَوْمَ يُنَادِيهِمْ فَيَقُولُ أَيْنَ شُرَكَائِيَ الَّذِينَ كُنْتُمْ تَزْعُمُونَ', 28, 62, 20, 79, 393, 'روزی را (به خاطر بیاورید) که خداوند آنان را ندا میدهد و میگوید: «کجا هستند همتایانی که برای من می‌پنداشتید؟!»'),
(3315, 3315, 'قَالَ الَّذِينَ حَقَّ عَلَيْهِمُ الْقَوْلُ رَبَّنَا هَٰؤُلَاءِ الَّذِينَ أَغْوَيْنَا أَغْوَيْنَاهُمْ كَمَا غَوَيْنَا ۖ تَبَرَّأْنَا إِلَيْكَ ۖ مَا كَانُوا إِيَّانَا يَعْبُدُونَ', 28, 63, 20, 79, 393, 'گروهی (از معبودان) که فرمان عذاب درباره آنها مسلم شده است میگویند: «پروردگارا! ما اینها = عابدان‌ را گمراه کردیم؛ (آری) ما آنها را گمراه کردیم همان‌گونه که خودمان گمراه شدیم؛ ما از آنان به سوی تو بیزاری می‌جوییم؛ آنان در حقیقت ما را نمی‌پرستیدند (بلکه هوای نفس خود را پرستش می‌کردند)!»'),
(3316, 3316, 'وَقِيلَ ادْعُوا شُرَكَاءَكُمْ فَدَعَوْهُمْ فَلَمْ يَسْتَجِيبُوا لَهُمْ وَرَأَوُا الْعَذَابَ ۚ لَوْ أَنَّهُمْ كَانُوا يَهْتَدُونَ', 28, 64, 20, 79, 393, 'و به آنها = عابدان‌ گفته می‌شود: «معبودهایتان را که همتای خدا می‌پنداشتید بخوانید (تا شما را یاری کنند)!» معبودهایشان را می‌خوانند، ولی جوابی به آنان نمی‌دهند! و (در این هنگام) عذاب الهی را (با چشم خود) می‌بینند، و آرزو می‌کنند ای کاش هدایت یافته بودند!'),
(3317, 3317, 'وَيَوْمَ يُنَادِيهِمْ فَيَقُولُ مَاذَا أَجَبْتُمُ الْمُرْسَلِينَ', 28, 65, 20, 79, 393, '(به خاطر آورید) روزی را که خداوند آنان را ندا می‌دهد و می‌گوید: «چه پاسخی به پیامبران (من) گفتید؟!»'),
(3318, 3318, 'فَعَمِيَتْ عَلَيْهِمُ الْأَنْبَاءُ يَوْمَئِذٍ فَهُمْ لَا يَتَسَاءَلُونَ', 28, 66, 20, 79, 393, 'در آن روز، همه اخبار به آنان پوشیده می‌ماند، (حتی نمی‌توانند) از یکدیگر سؤالی کنند!'),
(3319, 3319, 'فَأَمَّا مَنْ تَابَ وَآمَنَ وَعَمِلَ صَالِحًا فَعَسَىٰ أَنْ يَكُونَ مِنَ الْمُفْلِحِينَ', 28, 67, 20, 79, 393, 'اما کسی که توبه کند، و ایمان آورد و عمل صالحی انجام دهد، امید است از رستگاران باشد!'),
(3320, 3320, 'وَرَبُّكَ يَخْلُقُ مَا يَشَاءُ وَيَخْتَارُ ۗ مَا كَانَ لَهُمُ الْخِيَرَةُ ۚ سُبْحَانَ اللَّهِ وَتَعَالَىٰ عَمَّا يُشْرِكُونَ', 28, 68, 20, 79, 393, 'پروردگار تو هر چه بخواهد می‌آفریند، و هر چه بخواهد برمی‌گزیند؛ آنان (در برابر او) اختیاری ندارند؛ منزه است خداوند، و برتر است از همتایانی که برای او قائل می‌شوند!'),
(3321, 3321, 'وَرَبُّكَ يَعْلَمُ مَا تُكِنُّ صُدُورُهُمْ وَمَا يُعْلِنُونَ', 28, 69, 20, 79, 393, 'و پروردگار تو می‌داند آنچه را که سینه‌هایشان پنهان می‌دارد و آنچه را آشکار می‌سازند!'),
(3322, 3322, 'وَهُوَ اللَّهُ لَا إِلَٰهَ إِلَّا هُوَ ۖ لَهُ الْحَمْدُ فِي الْأُولَىٰ وَالْآخِرَةِ ۖ وَلَهُ الْحُكْمُ وَإِلَيْهِ تُرْجَعُونَ', 28, 70, 20, 79, 393, 'و او خدایی است که معبودی جز او نیست؛ ستایش برای اوست در این جهان و در جهان دیگر؛ حاکمیت (نیز) از آن اوست؛ و همه شما به سوی او بازگردانده می‌شوید!'),
(3323, 3323, 'قُلْ أَرَأَيْتُمْ إِنْ جَعَلَ اللَّهُ عَلَيْكُمُ اللَّيْلَ سَرْمَدًا إِلَىٰ يَوْمِ الْقِيَامَةِ مَنْ إِلَٰهٌ غَيْرُ اللَّهِ يَأْتِيكُمْ بِضِيَاءٍ ۖ أَفَلَا تَسْمَعُونَ', 28, 71, 20, 79, 394, 'بگو: «به من خبر دهید اگر خداوند شب را تا قیامت بر شما جاودان سازد، آیا معبودی جز خدا می‌تواند روشنایی برای شما بیاورد؟! آیا نمی‌شنوید؟!»'),
(3324, 3324, 'قُلْ أَرَأَيْتُمْ إِنْ جَعَلَ اللَّهُ عَلَيْكُمُ النَّهَارَ سَرْمَدًا إِلَىٰ يَوْمِ الْقِيَامَةِ مَنْ إِلَٰهٌ غَيْرُ اللَّهِ يَأْتِيكُمْ بِلَيْلٍ تَسْكُنُونَ فِيهِ ۖ أَفَلَا تُبْصِرُونَ', 28, 72, 20, 79, 394, 'بگو: «به من خبر دهید اگر خداوند روز را تا قیامت بر شما جاودان کند، کدام معبود غیر از خداست که شبی برای شما بیاورد تا در آن آرامش یابید؟ آیا نمی‌بینید؟!»'),
(3325, 3325, 'وَمِنْ رَحْمَتِهِ جَعَلَ لَكُمُ اللَّيْلَ وَالنَّهَارَ لِتَسْكُنُوا فِيهِ وَلِتَبْتَغُوا مِنْ فَضْلِهِ وَلَعَلَّكُمْ تَشْكُرُونَ', 28, 73, 20, 79, 394, 'و از رحمت اوست که برای شما شب و روز قرار داد تا هم در آن آرامش داشته باشید و هم برای بهره‌گیری از فضل خدا تلاش کنید، و شاید شکر نعمت او را بجا آورید!'),
(3326, 3326, 'وَيَوْمَ يُنَادِيهِمْ فَيَقُولُ أَيْنَ شُرَكَائِيَ الَّذِينَ كُنْتُمْ تَزْعُمُونَ', 28, 74, 20, 79, 394, '(به خاطر آورید) روزی را که آنها را ندا می‌دهد و می‌گوید: «کجایند همتایانی که برای من می‌پنداشتید؟!»'),
(3327, 3327, 'وَنَزَعْنَا مِنْ كُلِّ أُمَّةٍ شَهِيدًا فَقُلْنَا هَاتُوا بُرْهَانَكُمْ فَعَلِمُوا أَنَّ الْحَقَّ لِلَّهِ وَضَلَّ عَنْهُمْ مَا كَانُوا يَفْتَرُونَ', 28, 75, 20, 79, 394, '(در آن روز) از هر امتی گواهی برمی‌گزینیم و (به مشرکان) می‌گوییم: «دلیل خود را بیاورید!» اما آنها می‌دانند که حق از آن خداست، و تمام آنچه را افترا می‌بستند از (نظر) آنها گم خواهد شد!'),
(3328, 3328, ' إِنَّ قَارُونَ كَانَ مِنْ قَوْمِ مُوسَىٰ فَبَغَىٰ عَلَيْهِمْ ۖ وَآتَيْنَاهُ مِنَ الْكُنُوزِ مَا إِنَّ مَفَاتِحَهُ لَتَنُوءُ بِالْعُصْبَةِ أُولِي الْقُوَّةِ إِذْ قَالَ لَهُ قَوْمُهُ لَا تَفْرَحْ ۖ إِنَّ اللَّهَ لَا يُحِبُّ الْفَرِحِينَ', 28, 76, 20, 79, 394, 'قارون از قوم موسی بود، اما بر آنان ستم کرد؛ ما آنقدر از گنجها به او داده بودیم که حمل کلیدهای آن برای یک گروه زورمند مشکل بود! (به خاطر آورید) هنگامی را که قومش به او گفتند: «این همه شادی مغرورانه مکن، که خداوند شادی‌کنندگان مغرور را دوست نمی‌دارد!'),
(3329, 3329, 'وَابْتَغِ فِيمَا آتَاكَ اللَّهُ الدَّارَ الْآخِرَةَ ۖ وَلَا تَنْسَ نَصِيبَكَ مِنَ الدُّنْيَا ۖ وَأَحْسِنْ كَمَا أَحْسَنَ اللَّهُ إِلَيْكَ ۖ وَلَا تَبْغِ الْفَسَادَ فِي الْأَرْضِ ۖ إِنَّ اللَّهَ لَا يُحِبُّ الْمُفْسِدِينَ', 28, 77, 20, 79, 394, 'و در آنچه خدا به تو داده، سرای آخرت را بطلب؛ و بهره‌ات را از دنیا فراموش مکن؛ و همان‌گونه که خدا به تو نیکی کرده نیکی کن؛ و هرگز در زمین در جستجوی فساد مباش، که خدا مفسدان را دوست ندارد!'),
(3330, 3330, 'قَالَ إِنَّمَا أُوتِيتُهُ عَلَىٰ عِلْمٍ عِنْدِي ۚ أَوَلَمْ يَعْلَمْ أَنَّ اللَّهَ قَدْ أَهْلَكَ مِنْ قَبْلِهِ مِنَ الْقُرُونِ مَنْ هُوَ أَشَدُّ مِنْهُ قُوَّةً وَأَكْثَرُ جَمْعًا ۚ وَلَا يُسْأَلُ عَنْ ذُنُوبِهِمُ الْمُجْرِمُونَ', 28, 78, 20, 79, 395, '(قارون) گفت: «این ثروت را بوسیله دانشی که نزد من است به دست آورده‌ام!» آیا او نمی‌دانست که خداوند اقوامی را پیش از او هلاک کرد که نیرومندتر و ثروتمندتر از او بودند؟! (و هنگامی که عذاب الهی فرا رسد،) مجرمان از گناهانشان سؤال نمی‌شوند.'),
(3331, 3331, 'فَخَرَجَ عَلَىٰ قَوْمِهِ فِي زِينَتِهِ ۖ قَالَ الَّذِينَ يُرِيدُونَ الْحَيَاةَ الدُّنْيَا يَا لَيْتَ لَنَا مِثْلَ مَا أُوتِيَ قَارُونُ إِنَّهُ لَذُو حَظٍّ عَظِيمٍ', 28, 79, 20, 79, 395, '(روزی قارون) با تمام زینت خود در برابر قومش ظاهر شد، آنها که خواهان زندگی دنیا بودند گفتند: «ای کاش همانند آنچه به قارون داده شده است ما نیز داشتیم! به راستی که او بهره عظیمی دارد!»'),
(3332, 3332, 'وَقَالَ الَّذِينَ أُوتُوا الْعِلْمَ وَيْلَكُمْ ثَوَابُ اللَّهِ خَيْرٌ لِمَنْ آمَنَ وَعَمِلَ صَالِحًا وَلَا يُلَقَّاهَا إِلَّا الصَّابِرُونَ', 28, 80, 20, 79, 395, 'اما کسانی که علم و دانش به آنها داده شده بود گفتند: «وای بر شما ثواب الهی برای کسانی که ایمان آورده‌اند و عمل صالح انجام می‌دهند بهتر است، اما جز صابران آن را دریافت نمی‌کنند.»'),
(3333, 3333, 'فَخَسَفْنَا بِهِ وَبِدَارِهِ الْأَرْضَ فَمَا كَانَ لَهُ مِنْ فِئَةٍ يَنْصُرُونَهُ مِنْ دُونِ اللَّهِ وَمَا كَانَ مِنَ الْمُنْتَصِرِينَ', 28, 81, 20, 79, 395, 'سپس ما، او و خانه‌اش را در زمین فرو بردیم، و گروهی نداشت که او را در برابر عذاب الهی یاری کنند، و خود نیز نمی‌توانست خویشتن را یاری دهد.'),
(3334, 3334, 'وَأَصْبَحَ الَّذِينَ تَمَنَّوْا مَكَانَهُ بِالْأَمْسِ يَقُولُونَ وَيْكَأَنَّ اللَّهَ يَبْسُطُ الرِّزْقَ لِمَنْ يَشَاءُ مِنْ عِبَادِهِ وَيَقْدِرُ ۖ لَوْلَا أَنْ مَنَّ اللَّهُ عَلَيْنَا لَخَسَفَ بِنَا ۖ وَيْكَأَنَّهُ لَا يُفْلِحُ الْكَافِرُونَ', 28, 82, 20, 79, 395, 'و آنها که دیروز آرزو می‌کردند بجای او باشند (هنگامی که این صحنه را دیدند) گفتند: «وای بر ما! گویی خدا روزی را بر هر کس از بندگانش بخواهد گسترش می‌دهد یا تنگ می‌گیرد! اگر خدا بر ما منت ننهاده بود، ما را نیز به قعر زمین فرو می برد! ای وای گویی کافران هرگز رستگار نمی‌شوند!'),
(3335, 3335, 'تِلْكَ الدَّارُ الْآخِرَةُ نَجْعَلُهَا لِلَّذِينَ لَا يُرِيدُونَ عُلُوًّا فِي الْأَرْضِ وَلَا فَسَادًا ۚ وَالْعَاقِبَةُ لِلْمُتَّقِينَ', 28, 83, 20, 79, 395, '(آری،) این سرای آخرت را (تنها) برای کسانی قرارمی‌دهیم که اراده برتری‌جویی در زمین و فساد را ندارند؛ و عاقبت نیک برای پرهیزگاران است!'),
(3336, 3336, 'مَنْ جَاءَ بِالْحَسَنَةِ فَلَهُ خَيْرٌ مِنْهَا ۖ وَمَنْ جَاءَ بِالسَّيِّئَةِ فَلَا يُجْزَى الَّذِينَ عَمِلُوا السَّيِّئَاتِ إِلَّا مَا كَانُوا يَعْمَلُونَ', 28, 84, 20, 79, 395, 'کسی که کار نیکی انجام دهد، برای او پاداشی بهتر از آن است؛ و به کسانی که کارهای بد انجام دهند، مجازات بدکاران جز (به مقدار) اعمالشان نخواهد بود.'),
(3337, 3337, 'إِنَّ الَّذِي فَرَضَ عَلَيْكَ الْقُرْآنَ لَرَادُّكَ إِلَىٰ مَعَادٍ ۚ قُلْ رَبِّي أَعْلَمُ مَنْ جَاءَ بِالْهُدَىٰ وَمَنْ هُوَ فِي ضَلَالٍ مُبِينٍ', 28, 85, 20, 79, 396, 'آن کس که قرآن را بر تو فرض کرد، تو را به جایگاهت = زادگاهت‌ بازمی‌گرداند! بگو: «پروردگار من از همه بهتر می‌داند چه کسی (برنامه) هدایت آورده، و چه کسی در گمراهی آشکار است!»'),
(3338, 3338, 'وَمَا كُنْتَ تَرْجُو أَنْ يُلْقَىٰ إِلَيْكَ الْكِتَابُ إِلَّا رَحْمَةً مِنْ رَبِّكَ ۖ فَلَا تَكُونَنَّ ظَهِيرًا لِلْكَافِرِينَ', 28, 86, 20, 79, 396, 'و تو هرگز امید نداشتی که این کتاب آسمانی به تو القا گردد؛ ولی رحمت پروردگارت چنین ایجاب کرد! اکنون که چنین است، هرگز از کافران پشتیبانی مکن!'),
(3339, 3339, 'وَلَا يَصُدُّنَّكَ عَنْ آيَاتِ اللَّهِ بَعْدَ إِذْ أُنْزِلَتْ إِلَيْكَ ۖ وَادْعُ إِلَىٰ رَبِّكَ ۖ وَلَا تَكُونَنَّ مِنَ الْمُشْرِكِينَ', 28, 87, 20, 79, 396, 'و هرگز (آنها) تو را از آیات خداوند، بعد از آنکه بر تو نازل گشت، بازندارند! و بسوی پروردگارت دعوت کن، و هرگز از مشرکان مباش!'),
(3340, 3340, 'وَلَا تَدْعُ مَعَ اللَّهِ إِلَٰهًا آخَرَ ۘ لَا إِلَٰهَ إِلَّا هُوَ ۚ كُلُّ شَيْءٍ هَالِكٌ إِلَّا وَجْهَهُ ۚ لَهُ الْحُكْمُ وَإِلَيْهِ تُرْجَعُونَ', 28, 88, 20, 79, 396, 'معبود دیگری را با خدا مخوان، که هیچ معبودی جز او نیست؛ همه چیز جز ذات (پاک) او فانی می‌شود؛ حاکمیت تنها از آن اوست؛ و همه بسوی او بازگردانده می شوید!'),
(3341, 3341, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ الم', 29, 1, 20, 80, 396, 'الم‌'),
(3342, 3342, 'أَحَسِبَ النَّاسُ أَنْ يُتْرَكُوا أَنْ يَقُولُوا آمَنَّا وَهُمْ لَا يُفْتَنُونَ', 29, 2, 20, 80, 396, 'آیا مردم گمان کردند همین که بگویند: «ایمان آوردیم»، به حال خود رها می‌شوند و آزمایش نخواهند شد؟!'),
(3343, 3343, 'وَلَقَدْ فَتَنَّا الَّذِينَ مِنْ قَبْلِهِمْ ۖ فَلَيَعْلَمَنَّ اللَّهُ الَّذِينَ صَدَقُوا وَلَيَعْلَمَنَّ الْكَاذِبِينَ', 29, 3, 20, 80, 396, 'ما کسانی را که پیش از آنان بودند آزمودیم (و اینها را نیز امتحان می‌کنیم)؛ باید علم خدا درباره کسانی که راست می‌گویند و کسانی که دروغ می‌گویند تحقق یابد!'),
(3344, 3344, 'أَمْ حَسِبَ الَّذِينَ يَعْمَلُونَ السَّيِّئَاتِ أَنْ يَسْبِقُونَا ۚ سَاءَ مَا يَحْكُمُونَ', 29, 4, 20, 80, 396, 'آیا کسانی که اعمال بد انجام می‌دهند گمان کردند بر قدرت ما چیره خواهند شد؟! چه بد داوری می‌کنند!'),
(3345, 3345, 'مَنْ كَانَ يَرْجُو لِقَاءَ اللَّهِ فَإِنَّ أَجَلَ اللَّهِ لَآتٍ ۚ وَهُوَ السَّمِيعُ الْعَلِيمُ', 29, 5, 20, 80, 396, 'کسی که امید به لقاء الله (و رستاخیز) دارد (باید در اطاعت فرمان او بکوشد!) زیرا سرآمدی را که خدا تعیین کرده فرامی‌رسد؛ و او شنوا و داناست!'),
(3346, 3346, 'وَمَنْ جَاهَدَ فَإِنَّمَا يُجَاهِدُ لِنَفْسِهِ ۚ إِنَّ اللَّهَ لَغَنِيٌّ عَنِ الْعَالَمِينَ', 29, 6, 20, 80, 396, 'کسی که جهاد و تلاش کند، برای خود جهاد می‌کند؛ چرا که خداوند از همه جهانیان بی نیاز است.'),
(3347, 3347, 'وَالَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ لَنُكَفِّرَنَّ عَنْهُمْ سَيِّئَاتِهِمْ وَلَنَجْزِيَنَّهُمْ أَحْسَنَ الَّذِي كَانُوا يَعْمَلُونَ', 29, 7, 20, 80, 397, 'و کسانی که ایمان آورده و کارهای شایسته انجام دادند، گناهان آنان را می‌پوشانیم (و می‌بخشیم) و آنان را به بهترین اعمالی که انجام می‌دادند پاداش می‌دهیم.'),
(3348, 3348, 'وَوَصَّيْنَا الْإِنْسَانَ بِوَالِدَيْهِ حُسْنًا ۖ وَإِنْ جَاهَدَاكَ لِتُشْرِكَ بِي مَا لَيْسَ لَكَ بِهِ عِلْمٌ فَلَا تُطِعْهُمَا ۚ إِلَيَّ مَرْجِعُكُمْ فَأُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُونَ', 29, 8, 20, 80, 397, 'ما به انسان توصیه کردیم که به پدر و مادرش نیکی کند، و اگر آن دو (مشرک باشند و) تلاش کنند که برای من همتایی قائل شوی که به آن علم نداری، از آنها پیروی مکن! بازگشت شما به سوی من است، و شما را از آنچه انجام می‌دادید با خبر خواهم ساخت!'),
(3349, 3349, 'وَالَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ لَنُدْخِلَنَّهُمْ فِي الصَّالِحِينَ', 29, 9, 20, 80, 397, 'و کسانی که ایمان آورده و کارهای شایسته انجام دادند، آنها را در زمره صالحان وارد خواهیم کرد!'),
(3350, 3350, 'وَمِنَ النَّاسِ مَنْ يَقُولُ آمَنَّا بِاللَّهِ فَإِذَا أُوذِيَ فِي اللَّهِ جَعَلَ فِتْنَةَ النَّاسِ كَعَذَابِ اللَّهِ وَلَئِنْ جَاءَ نَصْرٌ مِنْ رَبِّكَ لَيَقُولُنَّ إِنَّا كُنَّا مَعَكُمْ ۚ أَوَلَيْسَ اللَّهُ بِأَعْلَمَ بِمَا فِي صُدُورِ الْعَالَمِينَ', 29, 10, 20, 80, 397, 'و از مردم کسانی هستند که می‌گویند: «به خدا ایمان آورده‌ایم!» اما هنگامی که در راه خدا شکنجه و آزار می‌بینند، آزار مردم را همچون عذاب الهی می‌شمارند (و از آن سخت وحشت می‌کنند)؛ ولی هنگامی که پیروزی از سوی پروردگارت (برای شما) بیاید، می‌گویند: «ما هم با شما بودیم (و در این پیروزی شریکیم)»!! آیا خداوند به آنچه در سینه‌های جهانیان است آگاه‌تر نیست؟!'),
(3351, 3351, 'وَلَيَعْلَمَنَّ اللَّهُ الَّذِينَ آمَنُوا وَلَيَعْلَمَنَّ الْمُنَافِقِينَ', 29, 11, 20, 80, 397, 'مسلماً خداوند مؤمنان را می‌شناسد، و به یقین منافقان را (نیز) می‌شناسد.'),
(3352, 3352, 'وَقَالَ الَّذِينَ كَفَرُوا لِلَّذِينَ آمَنُوا اتَّبِعُوا سَبِيلَنَا وَلْنَحْمِلْ خَطَايَاكُمْ وَمَا هُمْ بِحَامِلِينَ مِنْ خَطَايَاهُمْ مِنْ شَيْءٍ ۖ إِنَّهُمْ لَكَاذِبُونَ', 29, 12, 20, 80, 397, 'و کافران به مؤمنان گفتند: «شما از راه ما پیروی کنید، (و اگر گناهی دارد) ما گناهانتان را بر عهده خواهیم گرفت!» آنان هرگز چیزی از گناهان اینها را بر دوش نخواهند گرفت؛ آنان به یقین دروغگو هستند!'),
(3353, 3353, 'وَلَيَحْمِلُنَّ أَثْقَالَهُمْ وَأَثْقَالًا مَعَ أَثْقَالِهِمْ ۖ وَلَيُسْأَلُنَّ يَوْمَ الْقِيَامَةِ عَمَّا كَانُوا يَفْتَرُونَ', 29, 13, 20, 80, 397, 'آنها بار سنگین (گناهان) خویش را بر دوش می‌کشند، و (همچنین) بارهای سنگین دیگری را اضافه بر بارهای سنگین خود؛ و روز قیامت به یقین از تهمتهائی که می‌بستند سؤال خواهند شد!'),
(3354, 3354, 'وَلَقَدْ أَرْسَلْنَا نُوحًا إِلَىٰ قَوْمِهِ فَلَبِثَ فِيهِمْ أَلْفَ سَنَةٍ إِلَّا خَمْسِينَ عَامًا فَأَخَذَهُمُ الطُّوفَانُ وَهُمْ ظَالِمُونَ', 29, 14, 20, 80, 397, 'و ما نوح را بسوی قومش فرستادیم؛ و او را در میان آنان هزار سال مگر پنجاه سال، درنگ کرد؛ اما سرانجام طوفان و سیلاب آنان را فراگرفت در حالی که ظالم بودند.'),
(3355, 3355, 'فَأَنْجَيْنَاهُ وَأَصْحَابَ السَّفِينَةِ وَجَعَلْنَاهَا آيَةً لِلْعَالَمِينَ', 29, 15, 20, 80, 398, 'ما او و سرنشینان کشتی را رهایی بخشیدیم، و آن را آیتی برای جهانیان قرار دادیم!'),
(3356, 3356, 'وَإِبْرَاهِيمَ إِذْ قَالَ لِقَوْمِهِ اعْبُدُوا اللَّهَ وَاتَّقُوهُ ۖ ذَٰلِكُمْ خَيْرٌ لَكُمْ إِنْ كُنْتُمْ تَعْلَمُونَ', 29, 16, 20, 80, 398, 'ما ابراهیم را (نیز) فرستادیم، هنگامی که به قومش گفت: «خدا را پرستش کنید و از (عذاب) او بپرهیزید که این برای شما بهتر است اگر بدانید!'),
(3357, 3357, 'إِنَّمَا تَعْبُدُونَ مِنْ دُونِ اللَّهِ أَوْثَانًا وَتَخْلُقُونَ إِفْكًا ۚ إِنَّ الَّذِينَ تَعْبُدُونَ مِنْ دُونِ اللَّهِ لَا يَمْلِكُونَ لَكُمْ رِزْقًا فَابْتَغُوا عِنْدَ اللَّهِ الرِّزْقَ وَاعْبُدُوهُ وَاشْكُرُوا لَهُ ۖ إِلَيْهِ تُرْجَعُونَ', 29, 17, 20, 80, 398, 'شما غیر از خدا فقط بتهایی (از سنگ و چوب) را می‌پرستید و دروغی به هم می‌بافید؛ آنهایی را که غیر از خدا پرستش می‌کنید، مالک هیچ رزقی برای شما نیستند؛ روزی را تنها نزد خدا بطلبید و او را پرستش کنید و شکر او را بجا آورید که بسوی او بازگشت داده می‌شوید!'),
(3358, 3358, 'وَإِنْ تُكَذِّبُوا فَقَدْ كَذَّبَ أُمَمٌ مِنْ قَبْلِكُمْ ۖ وَمَا عَلَى الرَّسُولِ إِلَّا الْبَلَاغُ الْمُبِينُ', 29, 18, 20, 80, 398, 'اگر شما (مرا) تکذیب کنید (جای تعجب نیست)، امتهایی پیش از شما نیز (پیامبرانشان را) تکذیب کردند؛ وظیفه فرستاده (خدا) جز ابلاغ آشکار نیست».'),
(3359, 3359, 'أَوَلَمْ يَرَوْا كَيْفَ يُبْدِئُ اللَّهُ الْخَلْقَ ثُمَّ يُعِيدُهُ ۚ إِنَّ ذَٰلِكَ عَلَى اللَّهِ يَسِيرٌ', 29, 19, 20, 80, 398, 'آیا آنان ندیدند چگونه خداوند آفرینش را آغاز می‌کند، سپس بازمی‌گرداند؟! این کار برای خدا آسان است!'),
(3360, 3360, 'قُلْ سِيرُوا فِي الْأَرْضِ فَانْظُرُوا كَيْفَ بَدَأَ الْخَلْقَ ۚ ثُمَّ اللَّهُ يُنْشِئُ النَّشْأَةَ الْآخِرَةَ ۚ إِنَّ اللَّهَ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ', 29, 20, 20, 80, 398, 'بگو: «در زمین بگردید و بنگرید خداوند چگونه آفرینش را آغاز کرده است؟ سپس خداوند (به همین‌گونه) جهان آخرت را ایجاد می‌کند؛ یقیناً خدا بر هر چیز توانا است!'),
(3361, 3361, 'يُعَذِّبُ مَنْ يَشَاءُ وَيَرْحَمُ مَنْ يَشَاءُ ۖ وَإِلَيْهِ تُقْلَبُونَ', 29, 21, 20, 80, 398, 'هر کس را بخواهد (و مستحق بداند) مجازات می‌کند، و هر کس را بخواهد مورد رحمت قرارمی‌دهد؛ و شما را به سوی او بازمی‌گردانند.'),
(3362, 3362, 'وَمَا أَنْتُمْ بِمُعْجِزِينَ فِي الْأَرْضِ وَلَا فِي السَّمَاءِ ۖ وَمَا لَكُمْ مِنْ دُونِ اللَّهِ مِنْ وَلِيٍّ وَلَا نَصِيرٍ', 29, 22, 20, 80, 398, 'شما هرگز نمی‌توانید بر اراده خدا چیره شوید و از حوزه قدرت او در زمین و آسمان بگریزید؛ و برای شما جز خدا، ولی و یاوری نیست!»'),
(3363, 3363, 'وَالَّذِينَ كَفَرُوا بِآيَاتِ اللَّهِ وَلِقَائِهِ أُولَٰئِكَ يَئِسُوا مِنْ رَحْمَتِي وَأُولَٰئِكَ لَهُمْ عَذَابٌ أَلِيمٌ', 29, 23, 20, 80, 398, 'کسانی که به آیات خدا و دیدار او کافر شدند، از رحمت من مأیوسند؛ و برای آنها عذاب دردناکی است!'),
(3364, 3364, 'فَمَا كَانَ جَوَابَ قَوْمِهِ إِلَّا أَنْ قَالُوا اقْتُلُوهُ أَوْ حَرِّقُوهُ فَأَنْجَاهُ اللَّهُ مِنَ النَّارِ ۚ إِنَّ فِي ذَٰلِكَ لَآيَاتٍ لِقَوْمٍ يُؤْمِنُونَ', 29, 24, 20, 80, 399, 'اما جواب قوم او = ابراهیم‌ جز این نبود که گفتند: «او را بکشید یا بسوزانید!» ولی خداوند او را از آتش رهایی بخشید؛ در این ماجرا نشانه‌هایی است برای کسانی که ایمان می‌آورند.'),
(3365, 3365, 'وَقَالَ إِنَّمَا اتَّخَذْتُمْ مِنْ دُونِ اللَّهِ أَوْثَانًا مَوَدَّةَ بَيْنِكُمْ فِي الْحَيَاةِ الدُّنْيَا ۖ ثُمَّ يَوْمَ الْقِيَامَةِ يَكْفُرُ بَعْضُكُمْ بِبَعْضٍ وَيَلْعَنُ بَعْضُكُمْ بَعْضًا وَمَأْوَاكُمُ النَّارُ وَمَا لَكُمْ مِنْ نَاصِرِينَ', 29, 25, 20, 80, 399, '(ابراهیم) گفت: «شما غیر از خدا بتهایی برای خود انتخاب کرده‌اید که مایه دوستی و محبت میان شما در زندگی دنیا باشد؛ سپس روز قیامت از یکدیگر بیزاری می‌جویید و یکدیگر را لعن می‌کنید؛ و جایگاه (همه) شما آتش است و هیچ یار و یاوری برای شما نخواهد بود!»'),
(3366, 3366, ' فَآمَنَ لَهُ لُوطٌ ۘ وَقَالَ إِنِّي مُهَاجِرٌ إِلَىٰ رَبِّي ۖ إِنَّهُ هُوَ الْعَزِيزُ الْحَكِيمُ', 29, 26, 20, 80, 399, 'و لوط به او = ابراهیم‌ ایمان آورد، و (ابراهیم) گفت: «من بسوی پروردگارم هجرت می‌کنم که او صاحب قدرت و حکیم است!»'),
(3367, 3367, 'وَوَهَبْنَا لَهُ إِسْحَاقَ وَيَعْقُوبَ وَجَعَلْنَا فِي ذُرِّيَّتِهِ النُّبُوَّةَ وَالْكِتَابَ وَآتَيْنَاهُ أَجْرَهُ فِي الدُّنْيَا ۖ وَإِنَّهُ فِي الْآخِرَةِ لَمِنَ الصَّالِحِينَ', 29, 27, 20, 80, 399, 'و (در اواخر عمر،) اسحاق و یعقوب را به او بخشیدیم و نبوت و کتاب آسمانی را در دودمانش قرار دادیم و پاداش او را در دنیا دادیم و او در آخرت از صالحان است!'),
(3368, 3368, 'وَلُوطًا إِذْ قَالَ لِقَوْمِهِ إِنَّكُمْ لَتَأْتُونَ الْفَاحِشَةَ مَا سَبَقَكُمْ بِهَا مِنْ أَحَدٍ مِنَ الْعَالَمِينَ', 29, 28, 20, 80, 399, 'و لوط را فرستادیم هنگامی که به قوم خود گفت: «شما عمل بسیار زشتی انجام می‌دهید که هیچ یک از مردم جهان پیش از شما آن را انجام نداده است!');
INSERT INTO `Quran` (`rowid`, `id`, `Arabic`, `Chapter`, `Verse`, `Juz`, `Hizb`, `Page`, `tr`) VALUES
(3369, 3369, 'أَئِنَّكُمْ لَتَأْتُونَ الرِّجَالَ وَتَقْطَعُونَ السَّبِيلَ وَتَأْتُونَ فِي نَادِيكُمُ الْمُنْكَرَ ۖ فَمَا كَانَ جَوَابَ قَوْمِهِ إِلَّا أَنْ قَالُوا ائْتِنَا بِعَذَابِ اللَّهِ إِنْ كُنْتَ مِنَ الصَّادِقِينَ', 29, 29, 20, 80, 399, 'آیا شما به سراغ مردان می‌روید و راه (تداوم نسل انسان) را قطع می‌کنید و در مجلستان اعمال ناپسند انجام می‌دهید؟! «اما پاسخ قومش جز این نبود که گفتند:» اگر راست می‌گویی عذاب الهی را برای ما بیاور!'),
(3370, 3370, 'قَالَ رَبِّ انْصُرْنِي عَلَى الْقَوْمِ الْمُفْسِدِينَ', 29, 30, 20, 80, 399, '(لوط) عرض کرد: «پروردگارا! مرا در برابر این قوم تبهکار یاری فرما!»'),
(3371, 3371, 'وَلَمَّا جَاءَتْ رُسُلُنَا إِبْرَاهِيمَ بِالْبُشْرَىٰ قَالُوا إِنَّا مُهْلِكُو أَهْلِ هَٰذِهِ الْقَرْيَةِ ۖ إِنَّ أَهْلَهَا كَانُوا ظَالِمِينَ', 29, 31, 20, 80, 400, 'و هنگامی که فرستادگان ما (از فرشتگان) بشارت (تولد فرزند) برای ابراهیم آوردند، گفتند: «ما اهل این شهر و آبادی را و به شهرهای قوم لوط اشاره کردند هلاک خواهیم کرد، چرا که اهل آن ستمگرند!»'),
(3372, 3372, 'قَالَ إِنَّ فِيهَا لُوطًا ۚ قَالُوا نَحْنُ أَعْلَمُ بِمَنْ فِيهَا ۖ لَنُنَجِّيَنَّهُ وَأَهْلَهُ إِلَّا امْرَأَتَهُ كَانَتْ مِنَ الْغَابِرِينَ', 29, 32, 20, 80, 400, '(ابراهیم) گفت: «در این آبادی لوط است!» گفتند: «ما به کسانی که در آن هستند آگاهتریم! او و خانواده‌اش را نجات می‌دهیم؛ جز همسرش که در میان قوم (گنهکار) باقی خواهد ماند.»'),
(3373, 3373, 'وَلَمَّا أَنْ جَاءَتْ رُسُلُنَا لُوطًا سِيءَ بِهِمْ وَضَاقَ بِهِمْ ذَرْعًا وَقَالُوا لَا تَخَفْ وَلَا تَحْزَنْ ۖ إِنَّا مُنَجُّوكَ وَأَهْلَكَ إِلَّا امْرَأَتَكَ كَانَتْ مِنَ الْغَابِرِينَ', 29, 33, 20, 80, 400, 'هنگامی که فرستادگان ما نزد لوط آمدند، از دیدن آنها بدحال و دلتنگ شد؛ گفتند: «نترس و غمگین مباش، ما تو و خانواده‌ات را نجات خواهیم داد، جز همسرت که در میان قوم باقی می‌ماند.'),
(3374, 3374, 'إِنَّا مُنْزِلُونَ عَلَىٰ أَهْلِ هَٰذِهِ الْقَرْيَةِ رِجْزًا مِنَ السَّمَاءِ بِمَا كَانُوا يَفْسُقُونَ', 29, 34, 20, 80, 400, 'ما بر اهل این شهر و آبادی به خاطر گناهانشان، عذابی از آسمان فرو خواهیم ریخت!»'),
(3375, 3375, 'وَلَقَدْ تَرَكْنَا مِنْهَا آيَةً بَيِّنَةً لِقَوْمٍ يَعْقِلُونَ', 29, 35, 20, 80, 400, 'و از این آبادی نشانه روشنی (و درس عبرتی) برای کسانی که می‌اندیشند باقی گذاردیم!'),
(3376, 3376, 'وَإِلَىٰ مَدْيَنَ أَخَاهُمْ شُعَيْبًا فَقَالَ يَا قَوْمِ اعْبُدُوا اللَّهَ وَارْجُوا الْيَوْمَ الْآخِرَ وَلَا تَعْثَوْا فِي الْأَرْضِ مُفْسِدِينَ', 29, 36, 20, 80, 400, 'و ما بسوی «مدین»، برادرشان «شعیب» را فرستادیم؛ گفت: «ای قوم من! خدا را بپرستید، و به روز بازپسین امیدوار باشید، و در زمین فساد نکنید!»'),
(3377, 3377, 'فَكَذَّبُوهُ فَأَخَذَتْهُمُ الرَّجْفَةُ فَأَصْبَحُوا فِي دَارِهِمْ جَاثِمِينَ', 29, 37, 20, 80, 400, '(ولی) آنها او را تکذیب کردند، و به این سبب زلزله آنان را فراگرفت، و بامدادان در خانه‌های خود به رو در افتاده و مرده بودند!'),
(3378, 3378, 'وَعَادًا وَثَمُودَ وَقَدْ تَبَيَّنَ لَكُمْ مِنْ مَسَاكِنِهِمْ ۖ وَزَيَّنَ لَهُمُ الشَّيْطَانُ أَعْمَالَهُمْ فَصَدَّهُمْ عَنِ السَّبِيلِ وَكَانُوا مُسْتَبْصِرِينَ', 29, 38, 20, 80, 400, 'ما طایفه «عاد» و «ثمود» را نیز (هلاک کردیم)، و مساکن (ویران شده) آنان برای شما آشکار است؛ شیطان اعمالشان را برای آنان آراسته بود، از این رو آنان را از راه (خدا) بازداشت در حالی که بینا بودند.'),
(3379, 3379, 'وَقَارُونَ وَفِرْعَوْنَ وَهَامَانَ ۖ وَلَقَدْ جَاءَهُمْ مُوسَىٰ بِالْبَيِّنَاتِ فَاسْتَكْبَرُوا فِي الْأَرْضِ وَمَا كَانُوا سَابِقِينَ', 29, 39, 20, 80, 401, 'و «قارون» و «فرعون» و «هامان» را نیز هلاک کردیم؛ موسی با دلایل روشن به سراغشان آمد، امّا آنان در زمین برتری جویی کردند، ولی نتوانستند بر خدا پیشی گیرند!'),
(3380, 3380, 'فَكُلًّا أَخَذْنَا بِذَنْبِهِ ۖ فَمِنْهُمْ مَنْ أَرْسَلْنَا عَلَيْهِ حَاصِبًا وَمِنْهُمْ مَنْ أَخَذَتْهُ الصَّيْحَةُ وَمِنْهُمْ مَنْ خَسَفْنَا بِهِ الْأَرْضَ وَمِنْهُمْ مَنْ أَغْرَقْنَا ۚ وَمَا كَانَ اللَّهُ لِيَظْلِمَهُمْ وَلَٰكِنْ كَانُوا أَنْفُسَهُمْ يَظْلِمُونَ', 29, 40, 20, 80, 401, 'ما هر یک از آنان را به گناهانشان گرفتیم، بر بعضی از آنها طوفانی از سنگریزه فرستادیم، و بعضی از آنان را صیحه آسمانی فروگرفت، و بعضی دیگر را در زمین فرو بردیم، و بعضی را غرق کردیم؛ خداوند هرگز به آنها ستم نکرد، ولی آنها خودشان بر خود ستم می‌کردند!'),
(3381, 3381, 'مَثَلُ الَّذِينَ اتَّخَذُوا مِنْ دُونِ اللَّهِ أَوْلِيَاءَ كَمَثَلِ الْعَنْكَبُوتِ اتَّخَذَتْ بَيْتًا ۖ وَإِنَّ أَوْهَنَ الْبُيُوتِ لَبَيْتُ الْعَنْكَبُوتِ ۖ لَوْ كَانُوا يَعْلَمُونَ', 29, 41, 20, 80, 401, 'مثَل کسانی که غیر از خدا را اولیای خود برگزیدند، مثَل عنکبوت است که خانه‌ای برای خود انتخاب کرده؛ در حالی که سست‌ترین خانه‌های خانه عنکبوت است اگر می‌دانستند!'),
(3382, 3382, 'إِنَّ اللَّهَ يَعْلَمُ مَا يَدْعُونَ مِنْ دُونِهِ مِنْ شَيْءٍ ۚ وَهُوَ الْعَزِيزُ الْحَكِيمُ', 29, 42, 20, 80, 401, 'خداوند آنچه را غیر از او می‌خوانند می‌داند، و او شکست‌ناپذیر و حکیم است.'),
(3383, 3383, 'وَتِلْكَ الْأَمْثَالُ نَضْرِبُهَا لِلنَّاسِ ۖ وَمَا يَعْقِلُهَا إِلَّا الْعَالِمُونَ', 29, 43, 20, 80, 401, 'اینها مثالهایی است که ما برای مردم می‌زنیم، و جز دانایان آن را درک نمی‌کنند.'),
(3384, 3384, 'خَلَقَ اللَّهُ السَّمَاوَاتِ وَالْأَرْضَ بِالْحَقِّ ۚ إِنَّ فِي ذَٰلِكَ لَآيَةً لِلْمُؤْمِنِينَ', 29, 44, 20, 80, 401, 'خداوند، آسمانها و زمین را بحق آفرید؛ و در این آیتی است برای مؤمنان.'),
(3385, 3385, 'اتْلُ مَا أُوحِيَ إِلَيْكَ مِنَ الْكِتَابِ وَأَقِمِ الصَّلَاةَ ۖ إِنَّ الصَّلَاةَ تَنْهَىٰ عَنِ الْفَحْشَاءِ وَالْمُنْكَرِ ۗ وَلَذِكْرُ اللَّهِ أَكْبَرُ ۗ وَاللَّهُ يَعْلَمُ مَا تَصْنَعُونَ', 29, 45, 20, 80, 401, 'آنچه را از کتاب (آسمانی) به تو وحی شده تلاوت کن، و نماز را برپا دار، که نماز (انسان را) از زشتیها و گناه بازمی‌دارد، و یاد خدا بزرگتر است؛ و خداوند می‌داند شما چه کارهایی انجام می‌دهید!'),
(3386, 3386, ' وَلَا تُجَادِلُوا أَهْلَ الْكِتَابِ إِلَّا بِالَّتِي هِيَ أَحْسَنُ إِلَّا الَّذِينَ ظَلَمُوا مِنْهُمْ ۖ وَقُولُوا آمَنَّا بِالَّذِي أُنْزِلَ إِلَيْنَا وَأُنْزِلَ إِلَيْكُمْ وَإِلَٰهُنَا وَإِلَٰهُكُمْ وَاحِدٌ وَنَحْنُ لَهُ مُسْلِمُونَ', 29, 46, 21, 81, 402, 'با اهل کتاب جز به روشی که از همه نیکوتر است مجادله نکنید، مگر کسانی از آنان که ستم کردند؛ و (به آنها) بگویید: «ما به تمام آنچه از سوی خدا بر ما و شما نازل شده ایمان آورده‌ایم، و معبود ما و شما یکی است، و ما در برابر او تسلیم هستیم!»'),
(3387, 3387, 'وَكَذَٰلِكَ أَنْزَلْنَا إِلَيْكَ الْكِتَابَ ۚ فَالَّذِينَ آتَيْنَاهُمُ الْكِتَابَ يُؤْمِنُونَ بِهِ ۖ وَمِنْ هَٰؤُلَاءِ مَنْ يُؤْمِنُ بِهِ ۚ وَمَا يَجْحَدُ بِآيَاتِنَا إِلَّا الْكَافِرُونَ', 29, 47, 21, 81, 402, 'و این گونه، کتاب = قرآن‌ را بر تو نازل کردیم، کسانی که کتاب (آسمانی) به آنها داده‌ایم به این کتاب ایمان می‌آورند؛ و بعضی از این گروه = مشرکان‌ نیز به آن مؤمن می‌شوند؛ و آیات ما را جز کافران انکار نمی‌کنند.'),
(3388, 3388, 'وَمَا كُنْتَ تَتْلُو مِنْ قَبْلِهِ مِنْ كِتَابٍ وَلَا تَخُطُّهُ بِيَمِينِكَ ۖ إِذًا لَارْتَابَ الْمُبْطِلُونَ', 29, 48, 21, 81, 402, 'تو هرگز پیش از این کتابی نمی‌خواندی، و با دست خود چیزی نمی‌نوشتی، مبادا کسانی که در صدد (تکذیب و) ابطال سخنان تو هستند، شک و تردید کنند!'),
(3389, 3389, 'بَلْ هُوَ آيَاتٌ بَيِّنَاتٌ فِي صُدُورِ الَّذِينَ أُوتُوا الْعِلْمَ ۚ وَمَا يَجْحَدُ بِآيَاتِنَا إِلَّا الظَّالِمُونَ', 29, 49, 21, 81, 402, 'ولی این آیات روشنی است که در سینه دانشوران جای دارد؛ و آیات ما را جز ستمگران انکار نمی‌کنند!'),
(3390, 3390, 'وَقَالُوا لَوْلَا أُنْزِلَ عَلَيْهِ آيَاتٌ مِنْ رَبِّهِ ۖ قُلْ إِنَّمَا الْآيَاتُ عِنْدَ اللَّهِ وَإِنَّمَا أَنَا نَذِيرٌ مُبِينٌ', 29, 50, 21, 81, 402, 'گفتند: «چرا معجزاتی از سوی پروردگارش بر او نازل نشده؟!» بگو: «معجزات همه نزد خداست (و به فرمان او نازل می‌شود، نه به میل من و شما)؛ من تنها بیم دهنده‌ای آشکارم!'),
(3391, 3391, 'أَوَلَمْ يَكْفِهِمْ أَنَّا أَنْزَلْنَا عَلَيْكَ الْكِتَابَ يُتْلَىٰ عَلَيْهِمْ ۚ إِنَّ فِي ذَٰلِكَ لَرَحْمَةً وَذِكْرَىٰ لِقَوْمٍ يُؤْمِنُونَ', 29, 51, 21, 81, 402, 'آیا برای آنان کافی نیست که این کتاب را بر تو نازل کردیم که پیوسته بر آنها تلاوت می‌شود؟! در این، رحمت و تذکّری است برای کسانی که ایمان می‌آورند (و این معجزه بسیار واضحی است).'),
(3392, 3392, 'قُلْ كَفَىٰ بِاللَّهِ بَيْنِي وَبَيْنَكُمْ شَهِيدًا ۖ يَعْلَمُ مَا فِي السَّمَاوَاتِ وَالْأَرْضِ ۗ وَالَّذِينَ آمَنُوا بِالْبَاطِلِ وَكَفَرُوا بِاللَّهِ أُولَٰئِكَ هُمُ الْخَاسِرُونَ', 29, 52, 21, 81, 402, 'بگو: «همین بس که خدا میان من و شما گواه است؛ آنچه را در آسمانها و زمین است می‌داند؛ و کسانی که به باطل ایمان آوردند و به خدا کافر شدند زیانکاران واقعی هستند!'),
(3393, 3393, 'وَيَسْتَعْجِلُونَكَ بِالْعَذَابِ ۚ وَلَوْلَا أَجَلٌ مُسَمًّى لَجَاءَهُمُ الْعَذَابُ وَلَيَأْتِيَنَّهُمْ بَغْتَةً وَهُمْ لَا يَشْعُرُونَ', 29, 53, 21, 81, 403, 'آنان با شتاب از تو عذاب را می‌طلبند؛ و اگر موعد مقرّری تعیین نشده بود، عذاب (الهی) به سراغ آنان می‌آمد؛ و سرانجام این عذاب بطور ناگهانی بر آنها نازل می‌شود در حالی که نمی‌دانند (و غافلند).'),
(3394, 3394, 'يَسْتَعْجِلُونَكَ بِالْعَذَابِ وَإِنَّ جَهَنَّمَ لَمُحِيطَةٌ بِالْكَافِرِينَ', 29, 54, 21, 81, 403, 'آنان با عجله از تو عذاب می‌طلبند، در حالی که جهنم به کافران احاطه دارد!'),
(3395, 3395, 'يَوْمَ يَغْشَاهُمُ الْعَذَابُ مِنْ فَوْقِهِمْ وَمِنْ تَحْتِ أَرْجُلِهِمْ وَيَقُولُ ذُوقُوا مَا كُنْتُمْ تَعْمَلُونَ', 29, 55, 21, 81, 403, 'آن روز که عذاب (الهی) آنها را از بالای سر و پایین پایشان فرامی‌گیرد و به آنها می‌گوید: «بچشید آنچه را عمل می‌کردید» (روز سخت و دردناکی برای آنهاست!)'),
(3396, 3396, 'يَا عِبَادِيَ الَّذِينَ آمَنُوا إِنَّ أَرْضِي وَاسِعَةٌ فَإِيَّايَ فَاعْبُدُونِ', 29, 56, 21, 81, 403, 'ای بندگان من که ایمان آورده‌اید! زمین من وسیع است، پس تنها مرا بپرستید (و در برابر فشارهای دشمنان تسلیم نشوید)!'),
(3397, 3397, 'كُلُّ نَفْسٍ ذَائِقَةُ الْمَوْتِ ۖ ثُمَّ إِلَيْنَا تُرْجَعُونَ', 29, 57, 21, 81, 403, 'هر انسانی مرگ را می‌چشد، سپس شما را بسوی ما بازمی‌گردانند.'),
(3398, 3398, 'وَالَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ لَنُبَوِّئَنَّهُمْ مِنَ الْجَنَّةِ غُرَفًا تَجْرِي مِنْ تَحْتِهَا الْأَنْهَارُ خَالِدِينَ فِيهَا ۚ نِعْمَ أَجْرُ الْعَامِلِينَ', 29, 58, 21, 81, 403, 'و کسانی که ایمان آورده و کارهای شایسته انجام دادند، آنان را در غرفه‌هایی از بهشت جای می‌دهیم که نهرها در زیر آن جاری است؛ جاودانه در آن خواهند ماند؛ چه خوب است پاداش عمل‌کنندگان!'),
(3399, 3399, 'الَّذِينَ صَبَرُوا وَعَلَىٰ رَبِّهِمْ يَتَوَكَّلُونَ', 29, 59, 21, 81, 403, 'همانها که (در برابر مشکلات) صبر (و استقامت) کردند و بر پروردگارشان توکّل می‌کنند.'),
(3400, 3400, 'وَكَأَيِّنْ مِنْ دَابَّةٍ لَا تَحْمِلُ رِزْقَهَا اللَّهُ يَرْزُقُهَا وَإِيَّاكُمْ ۚ وَهُوَ السَّمِيعُ الْعَلِيمُ', 29, 60, 21, 81, 403, 'چه بسا جنبنده‌ای که قدرت حمل روزی خود را ندارد، خداوند او و شما را روزی می‌دهد؛ و او شنوا و داناست.'),
(3401, 3401, 'وَلَئِنْ سَأَلْتَهُمْ مَنْ خَلَقَ السَّمَاوَاتِ وَالْأَرْضَ وَسَخَّرَ الشَّمْسَ وَالْقَمَرَ لَيَقُولُنَّ اللَّهُ ۖ فَأَنَّىٰ يُؤْفَكُونَ', 29, 61, 21, 81, 403, 'و هر گاه از آنان بپرسی: «چه کسی آسمانها و زمین را آفریده، و خورشید و ماه را مسخّر کرده است؟» می‌گویند: «اللّه»! پس با این حال چگونه آنان را (از عبادت خدا) منحرف می‌سازند؟!'),
(3402, 3402, 'اللَّهُ يَبْسُطُ الرِّزْقَ لِمَنْ يَشَاءُ مِنْ عِبَادِهِ وَيَقْدِرُ لَهُ ۚ إِنَّ اللَّهَ بِكُلِّ شَيْءٍ عَلِيمٌ', 29, 62, 21, 81, 403, 'خداوند روزی را برای هر کس از بندگانش بخواهد گسترده می‌کند، و برای هر کس بخواهد محدود می‌سازد؛ خداوند به همه چیز داناست!'),
(3403, 3403, 'وَلَئِنْ سَأَلْتَهُمْ مَنْ نَزَّلَ مِنَ السَّمَاءِ مَاءً فَأَحْيَا بِهِ الْأَرْضَ مِنْ بَعْدِ مَوْتِهَا لَيَقُولُنَّ اللَّهُ ۚ قُلِ الْحَمْدُ لِلَّهِ ۚ بَلْ أَكْثَرُهُمْ لَا يَعْقِلُونَ', 29, 63, 21, 81, 403, 'و اگر از آنان بپرسی: «چه کسی از آسمان آبی فرستاد و بوسیله آن زمین را پس از مردنش زنده کرد؟ می‌گویند: «اللّه»! بگو: «حمد و ستایش مخصوص خداست!» امّا بیشتر آنها نمی‌دانند.'),
(3404, 3404, 'وَمَا هَٰذِهِ الْحَيَاةُ الدُّنْيَا إِلَّا لَهْوٌ وَلَعِبٌ ۚ وَإِنَّ الدَّارَ الْآخِرَةَ لَهِيَ الْحَيَوَانُ ۚ لَوْ كَانُوا يَعْلَمُونَ', 29, 64, 21, 81, 404, 'این زندگی دنیا چیزی جز سرگرمی و بازی نیست؛ و زندگی واقعی سرای آخرت است، اگر می‌دانستند!'),
(3405, 3405, 'فَإِذَا رَكِبُوا فِي الْفُلْكِ دَعَوُا اللَّهَ مُخْلِصِينَ لَهُ الدِّينَ فَلَمَّا نَجَّاهُمْ إِلَى الْبَرِّ إِذَا هُمْ يُشْرِكُونَ', 29, 65, 21, 81, 404, 'هنگامی که بر سوار بر کشتی شوند، خدا را با اخلاص می‌خوانند (و غیر او را فراموش می‌کنند)؛ امّا هنگامی که خدا آنان را به خشکی رساند و نجات داد، باز مشرک می‌شوند!'),
(3406, 3406, 'لِيَكْفُرُوا بِمَا آتَيْنَاهُمْ وَلِيَتَمَتَّعُوا ۖ فَسَوْفَ يَعْلَمُونَ', 29, 66, 21, 81, 404, '(بگذار) آنچه را (از آیات) به آنها داده‌ایم انکار کنند و از لذّات زودگذر زندگی بهره گیرند؛ امّا بزودی خواهند فهمید!'),
(3407, 3407, 'أَوَلَمْ يَرَوْا أَنَّا جَعَلْنَا حَرَمًا آمِنًا وَيُتَخَطَّفُ النَّاسُ مِنْ حَوْلِهِمْ ۚ أَفَبِالْبَاطِلِ يُؤْمِنُونَ وَبِنِعْمَةِ اللَّهِ يَكْفُرُونَ', 29, 67, 21, 81, 404, 'آیا ندیدند که ما حرم امنی (برای آنها) قرار دادیم در حالی که مردم را در اطراف آنان (در بیرون این حرم) می‌ربایند؟! آیا به باطل ایمان می‌آورند و نعمت خدا را کفران می‌کنند؟!'),
(3408, 3408, 'وَمَنْ أَظْلَمُ مِمَّنِ افْتَرَىٰ عَلَى اللَّهِ كَذِبًا أَوْ كَذَّبَ بِالْحَقِّ لَمَّا جَاءَهُ ۚ أَلَيْسَ فِي جَهَنَّمَ مَثْوًى لِلْكَافِرِينَ', 29, 68, 21, 81, 404, 'چه کسی ستمکارتر از آن کس است که بر خدا دروغ بسته یا حق را پس از آنکه به سراغش آمده تکذیب نماید؟! آیا جایگاه کافران در دوزخ نیست؟!'),
(3409, 3409, 'وَالَّذِينَ جَاهَدُوا فِينَا لَنَهْدِيَنَّهُمْ سُبُلَنَا ۚ وَإِنَّ اللَّهَ لَمَعَ الْمُحْسِنِينَ', 29, 69, 21, 81, 404, 'و آنها که در راه ما (با خلوص نیّت) جهاد کنند، قطعاً به راه‌های خود، هدایتشان خواهیم کرد؛ و خداوند با نیکوکاران است.'),
(3410, 3410, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ الم', 30, 1, 21, 81, 404, 'الم‌'),
(3411, 3411, 'غُلِبَتِ الرُّومُ', 30, 2, 21, 81, 404, 'رومیان مغلوب شدند!'),
(3412, 3412, 'فِي أَدْنَى الْأَرْضِ وَهُمْ مِنْ بَعْدِ غَلَبِهِمْ سَيَغْلِبُونَ', 30, 3, 21, 81, 404, '(و این شکست) در سرزمین نزدیکی رخ داد؛ امّا آنان پس از (این) مغلوبیّت بزودی غلبه خواهند کرد...'),
(3413, 3413, 'فِي بِضْعِ سِنِينَ ۗ لِلَّهِ الْأَمْرُ مِنْ قَبْلُ وَمِنْ بَعْدُ ۚ وَيَوْمَئِذٍ يَفْرَحُ الْمُؤْمِنُونَ', 30, 4, 21, 81, 404, 'در چند سال همه کارها از آن خداست؛ چه قبل و چه بعد (از این شکست و پیروزی)؛ و در آن روز، مؤمنان (بخاطر پیروزی دیگری) خوشحال خواهند شد...'),
(3414, 3414, 'بِنَصْرِ اللَّهِ ۚ يَنْصُرُ مَنْ يَشَاءُ ۖ وَهُوَ الْعَزِيزُ الرَّحِيمُ', 30, 5, 21, 81, 404, 'به سبب یاری خداوند؛ و او هر کس را بخواهد یاری می‌دهد؛ و او صاحب قدرت و رحیم است!'),
(3415, 3415, 'وَعْدَ اللَّهِ ۖ لَا يُخْلِفُ اللَّهُ وَعْدَهُ وَلَٰكِنَّ أَكْثَرَ النَّاسِ لَا يَعْلَمُونَ', 30, 6, 21, 81, 405, 'این وعده‌ای است که خدا کرده؛ و خداوند هرگز از وعده‌اش تخلّف نمی‌کند؛ ولی بیشتر مردم نمی‌دانند!'),
(3416, 3416, 'يَعْلَمُونَ ظَاهِرًا مِنَ الْحَيَاةِ الدُّنْيَا وَهُمْ عَنِ الْآخِرَةِ هُمْ غَافِلُونَ', 30, 7, 21, 81, 405, 'آنها فقط ظاهری از زندگی دنیا را می‌دانند، و از آخرت (و پایان کار) غافلند!'),
(3417, 3417, 'أَوَلَمْ يَتَفَكَّرُوا فِي أَنْفُسِهِمْ ۗ مَا خَلَقَ اللَّهُ السَّمَاوَاتِ وَالْأَرْضَ وَمَا بَيْنَهُمَا إِلَّا بِالْحَقِّ وَأَجَلٍ مُسَمًّى ۗ وَإِنَّ كَثِيرًا مِنَ النَّاسِ بِلِقَاءِ رَبِّهِمْ لَكَافِرُونَ', 30, 8, 21, 81, 405, 'آیا آنان با خود نیندیشیدند که خداوند، آسمانها و زمین و آنچه را میان آن دو است جز بحق و برای زمان معیّنی نیافریده است؟! ولی بسیاری از مردم (رستاخیز و) لقای پروردگارشان را منکرند!'),
(3418, 3418, 'أَوَلَمْ يَسِيرُوا فِي الْأَرْضِ فَيَنْظُرُوا كَيْفَ كَانَ عَاقِبَةُ الَّذِينَ مِنْ قَبْلِهِمْ ۚ كَانُوا أَشَدَّ مِنْهُمْ قُوَّةً وَأَثَارُوا الْأَرْضَ وَعَمَرُوهَا أَكْثَرَ مِمَّا عَمَرُوهَا وَجَاءَتْهُمْ رُسُلُهُمْ بِالْبَيِّنَاتِ ۖ فَمَا كَانَ اللَّهُ لِيَظْلِمَهُمْ وَلَٰكِنْ كَانُوا أَنْفُسَهُمْ يَظْلِمُونَ', 30, 9, 21, 81, 405, 'آیا در زمین گردش نکردند تا ببینند عاقبت کسانی که قبل از آنان بودند چگونه بود؟! آنها نیرومندتر از اینان بودند، و زمین را (برای زراعت و آبادی) بیش از اینان دگرگون ساختند و آباد کردند، و پیامبرانشان با دلایل روشن به سراغشان آمدند (امّا آنها انکار کردند و کیفر خود را دیدند)؛ خداوند هرگز به آنان ستم نکرد، آنها به خودشان ستم می‌کردند!'),
(3419, 3419, 'ثُمَّ كَانَ عَاقِبَةَ الَّذِينَ أَسَاءُوا السُّوأَىٰ أَنْ كَذَّبُوا بِآيَاتِ اللَّهِ وَكَانُوا بِهَا يَسْتَهْزِئُونَ', 30, 10, 21, 81, 405, 'سپس سرانجام کسانی که اعمال بد مرتکب شدند به جایی رسید که آیات خدا را تکذیب کردند و آن را به مسخره گرفتند!'),
(3420, 3420, 'اللَّهُ يَبْدَأُ الْخَلْقَ ثُمَّ يُعِيدُهُ ثُمَّ إِلَيْهِ تُرْجَعُونَ', 30, 11, 21, 81, 405, 'خداوند آفرینش را آغاز می‌کند، سپس آن را بازمی‌گرداند، سپس شما را بسوی او باز می‌گردانند!'),
(3421, 3421, 'وَيَوْمَ تَقُومُ السَّاعَةُ يُبْلِسُ الْمُجْرِمُونَ', 30, 12, 21, 81, 405, 'آن روز که قیامت برپا می‌شود، مجرمان در نومیدی و غم و اندوه فرو می‌روند!'),
(3422, 3422, 'وَلَمْ يَكُنْ لَهُمْ مِنْ شُرَكَائِهِمْ شُفَعَاءُ وَكَانُوا بِشُرَكَائِهِمْ كَافِرِينَ', 30, 13, 21, 81, 405, 'و برای آنان شفیعانی از معبودانشان نخواهد بود، و نسبت به معبودهایی که آنها را همتای خدا قرار داده بودند کافر می‌شوند!'),
(3423, 3423, 'وَيَوْمَ تَقُومُ السَّاعَةُ يَوْمَئِذٍ يَتَفَرَّقُونَ', 30, 14, 21, 81, 405, 'آن روز که قیامت برپا می‌گردد، (مردم) از هم جدا می‌شوند؛'),
(3424, 3424, 'فَأَمَّا الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ فَهُمْ فِي رَوْضَةٍ يُحْبَرُونَ', 30, 15, 21, 81, 405, 'امّا آنان که ایمان آورده و اعمال صالح انجام دادند، در باغی از بهشت شاد و مسرور خواهند بود.'),
(3425, 3425, 'وَأَمَّا الَّذِينَ كَفَرُوا وَكَذَّبُوا بِآيَاتِنَا وَلِقَاءِ الْآخِرَةِ فَأُولَٰئِكَ فِي الْعَذَابِ مُحْضَرُونَ', 30, 16, 21, 81, 406, 'و امّا آنان که به آیات ما و لقای آخرت کافر شدند، در عذاب الهی احضار می‌شوند.'),
(3426, 3426, 'فَسُبْحَانَ اللَّهِ حِينَ تُمْسُونَ وَحِينَ تُصْبِحُونَ', 30, 17, 21, 81, 406, 'منزّه است خداوند به هنگامی که شام می‌کنید و صبح می‌کنید؛'),
(3427, 3427, 'وَلَهُ الْحَمْدُ فِي السَّمَاوَاتِ وَالْأَرْضِ وَعَشِيًّا وَحِينَ تُظْهِرُونَ', 30, 18, 21, 81, 406, 'و حمد و ستایش مخصوص اوست در آسمان و زمین، و به هنگام عصر و هنگامی که ظهر می‌کنید.'),
(3428, 3428, 'يُخْرِجُ الْحَيَّ مِنَ الْمَيِّتِ وَيُخْرِجُ الْمَيِّتَ مِنَ الْحَيِّ وَيُحْيِي الْأَرْضَ بَعْدَ مَوْتِهَا ۚ وَكَذَٰلِكَ تُخْرَجُونَ', 30, 19, 21, 81, 406, 'او زنده را از مرده بیرون می‌آورد، و مرده را از زنده، و زمین را پس از مردنش حیات می‌بخشد، و به همین گونه روز قیامت (از گورها) بیرون آورده می‌شوید!'),
(3429, 3429, 'وَمِنْ آيَاتِهِ أَنْ خَلَقَكُمْ مِنْ تُرَابٍ ثُمَّ إِذَا أَنْتُمْ بَشَرٌ تَنْتَشِرُونَ', 30, 20, 21, 81, 406, 'از نشانه‌های او این است که شما را از خاک آفرید، سپس بناگاه انسانهایی شدید و در روی زمین گسترش یافتید!'),
(3430, 3430, 'وَمِنْ آيَاتِهِ أَنْ خَلَقَ لَكُمْ مِنْ أَنْفُسِكُمْ أَزْوَاجًا لِتَسْكُنُوا إِلَيْهَا وَجَعَلَ بَيْنَكُمْ مَوَدَّةً وَرَحْمَةً ۚ إِنَّ فِي ذَٰلِكَ لَآيَاتٍ لِقَوْمٍ يَتَفَكَّرُونَ', 30, 21, 21, 81, 406, 'و از نشانه‌های او اینکه همسرانی از جنس خودتان برای شما آفرید تا در کنار آنان آرامش یابید، و در میانتان مودّت و رحمت قرار داد؛ در این نشانه‌هایی است برای گروهی که تفکّر می‌کنند!'),
(3431, 3431, 'وَمِنْ آيَاتِهِ خَلْقُ السَّمَاوَاتِ وَالْأَرْضِ وَاخْتِلَافُ أَلْسِنَتِكُمْ وَأَلْوَانِكُمْ ۚ إِنَّ فِي ذَٰلِكَ لَآيَاتٍ لِلْعَالِمِينَ', 30, 22, 21, 81, 406, 'و از آیات او آفرینش آسمانها و زمین، و تفاوت زبانها و رنگهای شماست؛ در این نشانه‌هایی است برای عالمان!'),
(3432, 3432, 'وَمِنْ آيَاتِهِ مَنَامُكُمْ بِاللَّيْلِ وَالنَّهَارِ وَابْتِغَاؤُكُمْ مِنْ فَضْلِهِ ۚ إِنَّ فِي ذَٰلِكَ لَآيَاتٍ لِقَوْمٍ يَسْمَعُونَ', 30, 23, 21, 81, 406, 'و از نشانه‌های او خواب شما در شب و روز است و تلاش و کوششتان برای بهره‌گیری از فضل پروردگار (و تأمین معاش)؛ در این امور نشانه‌هایی است برای آنان که گوش شنوا دارند!'),
(3433, 3433, 'وَمِنْ آيَاتِهِ يُرِيكُمُ الْبَرْقَ خَوْفًا وَطَمَعًا وَيُنَزِّلُ مِنَ السَّمَاءِ مَاءً فَيُحْيِي بِهِ الْأَرْضَ بَعْدَ مَوْتِهَا ۚ إِنَّ فِي ذَٰلِكَ لَآيَاتٍ لِقَوْمٍ يَعْقِلُونَ', 30, 24, 21, 81, 406, 'و از آیات او این است که برق و رعد را به شما نشان می‌دهد که هم مایه ترس و هم امید است (ترس از صاعقه، و امید به نزول باران)، و از آسمان آبی فرو می‌فرستد که زمین را بعد از مردنش بوسیله آن زنده می‌کند؛ در این نشانه‌هایی است برای جمعیّتی که می‌اندیشند!'),
(3434, 3434, 'وَمِنْ آيَاتِهِ أَنْ تَقُومَ السَّمَاءُ وَالْأَرْضُ بِأَمْرِهِ ۚ ثُمَّ إِذَا دَعَاكُمْ دَعْوَةً مِنَ الْأَرْضِ إِذَا أَنْتُمْ تَخْرُجُونَ', 30, 25, 21, 81, 407, 'و از آیات او این است که آسمان و زمین به فرمان او برپاست؛ سپس هنگامی که شما را (در قیامت) از زمین فراخواند، ناگهان همه خارج می‌شوید (و در صحنه محشر حضور می‌یابید)!'),
(3435, 3435, 'وَلَهُ مَنْ فِي السَّمَاوَاتِ وَالْأَرْضِ ۖ كُلٌّ لَهُ قَانِتُونَ', 30, 26, 21, 81, 407, 'و از آن اوست تمام کسانی که در آسمانها و زمین‌اند و همگی در برابر او خاضع و مطیع‌اند!'),
(3436, 3436, 'وَهُوَ الَّذِي يَبْدَأُ الْخَلْقَ ثُمَّ يُعِيدُهُ وَهُوَ أَهْوَنُ عَلَيْهِ ۚ وَلَهُ الْمَثَلُ الْأَعْلَىٰ فِي السَّمَاوَاتِ وَالْأَرْضِ ۚ وَهُوَ الْعَزِيزُ الْحَكِيمُ', 30, 27, 21, 81, 407, 'او کسی است که آفرینش را آغاز می‌کند، سپس آن را بازمی‌گرداند، و این کار برای او آسانتر می‌باشد؛ و برای اوست توصیف برتر در آسمانها و زمین؛ و اوست توانمند و حکیم!'),
(3437, 3437, 'ضَرَبَ لَكُمْ مَثَلًا مِنْ أَنْفُسِكُمْ ۖ هَلْ لَكُمْ مِنْ مَا مَلَكَتْ أَيْمَانُكُمْ مِنْ شُرَكَاءَ فِي مَا رَزَقْنَاكُمْ فَأَنْتُمْ فِيهِ سَوَاءٌ تَخَافُونَهُمْ كَخِيفَتِكُمْ أَنْفُسَكُمْ ۚ كَذَٰلِكَ نُفَصِّلُ الْآيَاتِ لِقَوْمٍ يَعْقِلُونَ', 30, 28, 21, 81, 407, 'خداوند مثالی از خودتان، برای شما زده است: آیا (اگر مملوک و برده‌ای داشته باشید)، این برده‌های شما هرگز در روزیهایی که به شما داده‌ایم شریک شما می‌باشند؛ آنچنان که هر دو مساوی بوده و از تصرّف مستقل و بدون اجازه آنان بیم داشته باشید، آن گونه که در مورد شرکای آزاد خود بیم دارید؟! اینچنین آیات خود را برای کسانی که تعقّل می‌کنند شرح می‌دهیم.'),
(3438, 3438, 'بَلِ اتَّبَعَ الَّذِينَ ظَلَمُوا أَهْوَاءَهُمْ بِغَيْرِ عِلْمٍ ۖ فَمَنْ يَهْدِي مَنْ أَضَلَّ اللَّهُ ۖ وَمَا لَهُمْ مِنْ نَاصِرِينَ', 30, 29, 21, 81, 407, 'ولی ظالمان بدون علم و آگاهی، از هوی و هوسهای خود پیروی کردند! پس چه کسی می‌تواند آنان را که خدا گمراه کرده است هدایت کند؟! و برای آنها هیچ یاوری نخواهد بود!'),
(3439, 3439, 'فَأَقِمْ وَجْهَكَ لِلدِّينِ حَنِيفًا ۚ فِطْرَتَ اللَّهِ الَّتِي فَطَرَ النَّاسَ عَلَيْهَا ۚ لَا تَبْدِيلَ لِخَلْقِ اللَّهِ ۚ ذَٰلِكَ الدِّينُ الْقَيِّمُ وَلَٰكِنَّ أَكْثَرَ النَّاسِ لَا يَعْلَمُونَ', 30, 30, 21, 81, 407, 'پس روی خود را متوجّه آیین خالص پروردگار کن! این فطرتی است که خداوند، انسانها را بر آن آفریده؛ دگرگونی در آفرینش الهی نیست؛ این است آیین استوار؛ ولی اکثر مردم نمی‌دانند!'),
(3440, 3440, ' مُنِيبِينَ إِلَيْهِ وَاتَّقُوهُ وَأَقِيمُوا الصَّلَاةَ وَلَا تَكُونُوا مِنَ الْمُشْرِكِينَ', 30, 31, 21, 82, 407, 'این باید در حالی باشد که شما بسوی او بازگشت می‌کنید و از (مخالفت فرمان) او بپرهیزید، نماز را برپا دارید و از مشرکان نباشید...'),
(3441, 3441, 'مِنَ الَّذِينَ فَرَّقُوا دِينَهُمْ وَكَانُوا شِيَعًا ۖ كُلُّ حِزْبٍ بِمَا لَدَيْهِمْ فَرِحُونَ', 30, 32, 21, 82, 407, 'از کسانی که دین خود را پراکنده ساختند و به دسته‌ها و گروه‌ها تقسیم شدند! و (عجب اینکه) هر گروهی به آنچه نزد آنهاست (دلبسته و) خوشحالند!'),
(3442, 3442, 'وَإِذَا مَسَّ النَّاسَ ضُرٌّ دَعَوْا رَبَّهُمْ مُنِيبِينَ إِلَيْهِ ثُمَّ إِذَا أَذَاقَهُمْ مِنْهُ رَحْمَةً إِذَا فَرِيقٌ مِنْهُمْ بِرَبِّهِمْ يُشْرِكُونَ', 30, 33, 21, 82, 408, 'هنگامی که رنج و زیانی به مردم برسد، پروردگار خود را می‌خوانند و توبه‌کنان بسوی او بازمی‌گردند؛ امّا همین که رحمتی از خودش به آنان بچشاند، بناگاه گروهی از آنان نسبت به پروردگارشان مشرک می‌شوند.'),
(3443, 3443, 'لِيَكْفُرُوا بِمَا آتَيْنَاهُمْ ۚ فَتَمَتَّعُوا فَسَوْفَ تَعْلَمُونَ', 30, 34, 21, 82, 408, '(بگذار) نعمتهایی را که ما به آنها داده‌ایم کفران کنند! و (از نعمتهای زودگذر دنیا هر چه می‌توانید) بهره گیرید؛ امّا بزودی خواهید دانست (که نتیجه کفران و کامجوییهای بی حساب شما چه بوده است)!'),
(3444, 3444, 'أَمْ أَنْزَلْنَا عَلَيْهِمْ سُلْطَانًا فَهُوَ يَتَكَلَّمُ بِمَا كَانُوا بِهِ يُشْرِكُونَ', 30, 35, 21, 82, 408, 'آیا ما دلیل محکمی بر آنان فرستادیم که از شرکشان سخن می‌گوید (و آن را موجّه می‌شمارد)؟!'),
(3445, 3445, 'وَإِذَا أَذَقْنَا النَّاسَ رَحْمَةً فَرِحُوا بِهَا ۖ وَإِنْ تُصِبْهُمْ سَيِّئَةٌ بِمَا قَدَّمَتْ أَيْدِيهِمْ إِذَا هُمْ يَقْنَطُونَ', 30, 36, 21, 82, 408, 'و هنگامی که رحمتی به مردم بچشانیم، از آن خوشحال می‌شوند؛ و هرگاه رنج و مصیبتی بخاطر اعمالی که انجام داده‌اند به آنان رسد، ناگهان مأیوس می‌شوند!'),
(3446, 3446, 'أَوَلَمْ يَرَوْا أَنَّ اللَّهَ يَبْسُطُ الرِّزْقَ لِمَنْ يَشَاءُ وَيَقْدِرُ ۚ إِنَّ فِي ذَٰلِكَ لَآيَاتٍ لِقَوْمٍ يُؤْمِنُونَ', 30, 37, 21, 82, 408, 'آیا ندیدند که خداوند روزی را برای هر کس بخواهد گسترده یا تنگ می‌سازد؟! در این نشانه‌هایی است برای گروهی که ایمان می‌آورند.'),
(3447, 3447, 'فَآتِ ذَا الْقُرْبَىٰ حَقَّهُ وَالْمِسْكِينَ وَابْنَ السَّبِيلِ ۚ ذَٰلِكَ خَيْرٌ لِلَّذِينَ يُرِيدُونَ وَجْهَ اللَّهِ ۖ وَأُولَٰئِكَ هُمُ الْمُفْلِحُونَ', 30, 38, 21, 82, 408, 'پس حقّ نزدیکان و مسکینان و در راه‌ماندگان را ادا کن! این برای آنها که رضای خدا را می‌طلبند بهتر است، و چنین کسانی رستگارانند.'),
(3448, 3448, 'وَمَا آتَيْتُمْ مِنْ رِبًا لِيَرْبُوَ فِي أَمْوَالِ النَّاسِ فَلَا يَرْبُو عِنْدَ اللَّهِ ۖ وَمَا آتَيْتُمْ مِنْ زَكَاةٍ تُرِيدُونَ وَجْهَ اللَّهِ فَأُولَٰئِكَ هُمُ الْمُضْعِفُونَ', 30, 39, 21, 82, 408, 'آنچه بعنوان ربا می‌پردازید تا در اموال مردم فزونی یابد، نزد خدا فزونی نخواهد یافت؛ و آنچه را بعنوان زکات می‌پردازید و تنها رضای خدا را می‌طلبید (مایه برکت است؛ و) کسانی که چنین می‌کنند دارای پاداش مضاعفند.'),
(3449, 3449, 'اللَّهُ الَّذِي خَلَقَكُمْ ثُمَّ رَزَقَكُمْ ثُمَّ يُمِيتُكُمْ ثُمَّ يُحْيِيكُمْ ۖ هَلْ مِنْ شُرَكَائِكُمْ مَنْ يَفْعَلُ مِنْ ذَٰلِكُمْ مِنْ شَيْءٍ ۚ سُبْحَانَهُ وَتَعَالَىٰ عَمَّا يُشْرِكُونَ', 30, 40, 21, 82, 408, 'خداوند همان کسی است که شما را آفرید، سپس روزی داد، بعد می‌میراند، سپس زنده می‌کند؛ آیا هیچ یک از همتایانی که برای خدا قرار داده‌اید چیزی از این کارها را می‌توانند انجام دهند؟! او منزّه و برتر است از آنچه همتای او قرار می‌دهند.'),
(3450, 3450, 'ظَهَرَ الْفَسَادُ فِي الْبَرِّ وَالْبَحْرِ بِمَا كَسَبَتْ أَيْدِي النَّاسِ لِيُذِيقَهُمْ بَعْضَ الَّذِي عَمِلُوا لَعَلَّهُمْ يَرْجِعُونَ', 30, 41, 21, 82, 408, 'فساد، در خشکی و دریا بخاطر کارهایی که مردم انجام داده‌اند آشکار شده است؛ خدا می‌خواهد نتیجه بعضی از اعمالشان را به آنان بچشاند، شاید (بسوی حق) بازگردند!'),
(3451, 3451, 'قُلْ سِيرُوا فِي الْأَرْضِ فَانْظُرُوا كَيْفَ كَانَ عَاقِبَةُ الَّذِينَ مِنْ قَبْلُ ۚ كَانَ أَكْثَرُهُمْ مُشْرِكِينَ', 30, 42, 21, 82, 409, 'بگو: «در زمین سیر کنید و بنگرید عاقبت کسانی که قبل از شما بودند چگونه بود؟ بیشتر آنها مشرک بودند!'),
(3452, 3452, 'فَأَقِمْ وَجْهَكَ لِلدِّينِ الْقَيِّمِ مِنْ قَبْلِ أَنْ يَأْتِيَ يَوْمٌ لَا مَرَدَّ لَهُ مِنَ اللَّهِ ۖ يَوْمَئِذٍ يَصَّدَّعُونَ', 30, 43, 21, 82, 409, 'روی خود را بسوی آیین مستقیم و پایدار بدار، پیش از آنکه روزی فرا رسد که هیچ کس نمی‌تواند آن را از خدا بازگرداند؛ در آن روز مردم به گروه‌هایی تقسیم می‌شوند:'),
(3453, 3453, 'مَنْ كَفَرَ فَعَلَيْهِ كُفْرُهُ ۖ وَمَنْ عَمِلَ صَالِحًا فَلِأَنْفُسِهِمْ يَمْهَدُونَ', 30, 44, 21, 82, 409, 'هر کس کافر شود، کفرش بر زیان خود اوست؛ و آنها که کار شایسته انجام دهند، به سود خودشان آماده می‌سازند.'),
(3454, 3454, 'لِيَجْزِيَ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ مِنْ فَضْلِهِ ۚ إِنَّهُ لَا يُحِبُّ الْكَافِرِينَ', 30, 45, 21, 82, 409, 'این برای آن است که خداوند کسانی را که ایمان آورده و اعمال صالح انجام داده‌اند، از فضلش پاداش دهد؛ او کافران را دوست نمی‌دارد!'),
(3455, 3455, 'وَمِنْ آيَاتِهِ أَنْ يُرْسِلَ الرِّيَاحَ مُبَشِّرَاتٍ وَلِيُذِيقَكُمْ مِنْ رَحْمَتِهِ وَلِتَجْرِيَ الْفُلْكُ بِأَمْرِهِ وَلِتَبْتَغُوا مِنْ فَضْلِهِ وَلَعَلَّكُمْ تَشْكُرُونَ', 30, 46, 21, 82, 409, 'و از آیات (عظمت) خدا این است که بادها را بعنوان بشارتگرانی می‌فرستد تا شما را از رحمتش بچشاند (و سیراب کند) و کشتیها بفرمانش حرکت کنند و از فضل او بهره گیرید؛ شاید شکرگزاری کنید.'),
(3456, 3456, 'وَلَقَدْ أَرْسَلْنَا مِنْ قَبْلِكَ رُسُلًا إِلَىٰ قَوْمِهِمْ فَجَاءُوهُمْ بِالْبَيِّنَاتِ فَانْتَقَمْنَا مِنَ الَّذِينَ أَجْرَمُوا ۖ وَكَانَ حَقًّا عَلَيْنَا نَصْرُ الْمُؤْمِنِينَ', 30, 47, 21, 82, 409, 'و پیش از تو پیامبرانی را بسوی قومشان فرستادیم؛ آنها با دلایل روشن به سراغ قوم خود رفتند، ولی (هنگامی که اندرزها سودی نداد) از مجرمان انتقام گرفتیم (و مؤمنان را یاری کردیم)؛ و یاری مؤمنان، همواره حقّی است بر عهده ما!'),
(3457, 3457, 'اللَّهُ الَّذِي يُرْسِلُ الرِّيَاحَ فَتُثِيرُ سَحَابًا فَيَبْسُطُهُ فِي السَّمَاءِ كَيْفَ يَشَاءُ وَيَجْعَلُهُ كِسَفًا فَتَرَى الْوَدْقَ يَخْرُجُ مِنْ خِلَالِهِ ۖ فَإِذَا أَصَابَ بِهِ مَنْ يَشَاءُ مِنْ عِبَادِهِ إِذَا هُمْ يَسْتَبْشِرُونَ', 30, 48, 21, 82, 409, 'خداوند همان کسی است که بادها را می‌فرستد تا ابرهایی را به حرکت در آورند، سپس آنها را در پهنه آسمان آن گونه که بخواهد می‌گستراند و متراکم می‌سازد؛ در این هنگام دانه‌های باران را می‌بینی که از لا به لای آن خارج می‌شود، هنگامی که این (باران حیاتبخش) را به هر کس از بندگانش که بخواهد می‌رساند، ناگهان خوشحال می‌شوند...'),
(3458, 3458, 'وَإِنْ كَانُوا مِنْ قَبْلِ أَنْ يُنَزَّلَ عَلَيْهِمْ مِنْ قَبْلِهِ لَمُبْلِسِينَ', 30, 49, 21, 82, 409, 'و قطعاً پیش از آنکه بر آنان نازل شود مایوس بودند!'),
(3459, 3459, 'فَانْظُرْ إِلَىٰ آثَارِ رَحْمَتِ اللَّهِ كَيْفَ يُحْيِي الْأَرْضَ بَعْدَ مَوْتِهَا ۚ إِنَّ ذَٰلِكَ لَمُحْيِي الْمَوْتَىٰ ۖ وَهُوَ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ', 30, 50, 21, 82, 409, 'به آثار رحمت الهی بنگر که چگونه زمین را بعد از مردنش زنده می‌کند؛ چنین کسی (که زمین مرده را زنده کرد) زنده‌کننده مردگان (در قیامت) است؛ و او بر همه چیز تواناست!'),
(3460, 3460, 'وَلَئِنْ أَرْسَلْنَا رِيحًا فَرَأَوْهُ مُصْفَرًّا لَظَلُّوا مِنْ بَعْدِهِ يَكْفُرُونَ', 30, 51, 21, 82, 410, 'و اگر ما بادی بفرستیم (داغ و سوزان)، و بر اثر آن زراعت و باغ خود را زرد و پژمرده ببینند، (مأیوس شده و) پس از آن راه کفران پیش می‌گیرند!'),
(3461, 3461, 'فَإِنَّكَ لَا تُسْمِعُ الْمَوْتَىٰ وَلَا تُسْمِعُ الصُّمَّ الدُّعَاءَ إِذَا وَلَّوْا مُدْبِرِينَ', 30, 52, 21, 82, 410, 'تو نمی‌توانی صدای خود را به گوش مردگان برسانی، و نه سخنت را به گوش کران هنگامی که روی برگردانند و دور شوند!'),
(3462, 3462, 'وَمَا أَنْتَ بِهَادِ الْعُمْيِ عَنْ ضَلَالَتِهِمْ ۖ إِنْ تُسْمِعُ إِلَّا مَنْ يُؤْمِنُ بِآيَاتِنَا فَهُمْ مُسْلِمُونَ', 30, 53, 21, 82, 410, 'و (نیز) نمی‌توانی نابینایان را از گمراهیشان هدایت کنی؛ تو تنها سخنت را به گوش کسانی می‌رسانی که ایمان به آیات ما می‌آورند و در برابر حق تسلیمند!'),
(3463, 3463, ' اللَّهُ الَّذِي خَلَقَكُمْ مِنْ ضَعْفٍ ثُمَّ جَعَلَ مِنْ بَعْدِ ضَعْفٍ قُوَّةً ثُمَّ جَعَلَ مِنْ بَعْدِ قُوَّةٍ ضَعْفًا وَشَيْبَةً ۚ يَخْلُقُ مَا يَشَاءُ ۖ وَهُوَ الْعَلِيمُ الْقَدِيرُ', 30, 54, 21, 82, 410, 'خدا همان کسی است که شما را آفرید در حالی که ضعیف بودید؛ سپس بعد از ناتوانی، قوّت بخشید و باز بعد از قوّت، ضعف و پیری قرار داد؛ او هر چه بخواهد می‌آفریند، و دانا و تواناست.'),
(3464, 3464, 'وَيَوْمَ تَقُومُ السَّاعَةُ يُقْسِمُ الْمُجْرِمُونَ مَا لَبِثُوا غَيْرَ سَاعَةٍ ۚ كَذَٰلِكَ كَانُوا يُؤْفَكُونَ', 30, 55, 21, 82, 410, 'و روزی که قیامت برپا شود، مجرمان سوگند یاد می‌کنند که جز ساعتی (در عالم برزخ) درنگ نکردند! اینچنین از درک حقیقت بازگردانده می‌شوند.'),
(3465, 3465, 'وَقَالَ الَّذِينَ أُوتُوا الْعِلْمَ وَالْإِيمَانَ لَقَدْ لَبِثْتُمْ فِي كِتَابِ اللَّهِ إِلَىٰ يَوْمِ الْبَعْثِ ۖ فَهَٰذَا يَوْمُ الْبَعْثِ وَلَٰكِنَّكُمْ كُنْتُمْ لَا تَعْلَمُونَ', 30, 56, 21, 82, 410, 'ولی کسانی که علم و ایمان به آنان داده شده می‌گویند: «شما بفرمان خدا تا روز قیامت (در عالم برزخ) درنگ کردید، و اکنون روز رستاخیز است، امّا شما نمی‌دانستید!»'),
(3466, 3466, 'فَيَوْمَئِذٍ لَا يَنْفَعُ الَّذِينَ ظَلَمُوا مَعْذِرَتُهُمْ وَلَا هُمْ يُسْتَعْتَبُونَ', 30, 57, 21, 82, 410, 'آن روز عذرخواهی ظالمان سودی به حالشان ندارد، و توبه آنان پذیرفته نمی‌شود.'),
(3467, 3467, 'وَلَقَدْ ضَرَبْنَا لِلنَّاسِ فِي هَٰذَا الْقُرْآنِ مِنْ كُلِّ مَثَلٍ ۚ وَلَئِنْ جِئْتَهُمْ بِآيَةٍ لَيَقُولَنَّ الَّذِينَ كَفَرُوا إِنْ أَنْتُمْ إِلَّا مُبْطِلُونَ', 30, 58, 21, 82, 410, 'ما برای مردم در این قرآن از هر گونه مثال و مطلبی بیان کردیم؛ و اگر آیه‌ای برای آنان بیاوری، کافران می‌گویند: «شما اهل باطلید (و اینها سحر و جادو است)!»'),
(3468, 3468, 'كَذَٰلِكَ يَطْبَعُ اللَّهُ عَلَىٰ قُلُوبِ الَّذِينَ لَا يَعْلَمُونَ', 30, 59, 21, 82, 410, 'این گونه خداوند بر دلهای آنان که آگاهی ندارند مهر می‌نهد!'),
(3469, 3469, 'فَاصْبِرْ إِنَّ وَعْدَ اللَّهِ حَقٌّ ۖ وَلَا يَسْتَخِفَّنَّكَ الَّذِينَ لَا يُوقِنُونَ', 30, 60, 21, 82, 410, 'اکنون که چنین است صبر پیشه کن که وعده خدا حق است؛ و هرگز کسانی که ایمان ندارند تو را خشمگین نسازند (و از راه خود منحرف نکنند)!'),
(3470, 3470, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ الم', 31, 1, 21, 82, 411, 'الم‌'),
(3471, 3471, 'تِلْكَ آيَاتُ الْكِتَابِ الْحَكِيمِ', 31, 2, 21, 82, 411, 'این آیات کتاب حکیم است (کتابی پرمحتوا و استوار)!'),
(3472, 3472, 'هُدًى وَرَحْمَةً لِلْمُحْسِنِينَ', 31, 3, 21, 82, 411, 'مایه هدایت و رحمت برای نیکوکاران است.'),
(3473, 3473, 'الَّذِينَ يُقِيمُونَ الصَّلَاةَ وَيُؤْتُونَ الزَّكَاةَ وَهُمْ بِالْآخِرَةِ هُمْ يُوقِنُونَ', 31, 4, 21, 82, 411, 'همانان که نماز را برپا می‌دارند، و زکات را می‌پردازند و آنها به آخرت یقین دارند.'),
(3474, 3474, 'أُولَٰئِكَ عَلَىٰ هُدًى مِنْ رَبِّهِمْ ۖ وَأُولَٰئِكَ هُمُ الْمُفْلِحُونَ', 31, 5, 21, 82, 411, 'آنان بر طریق هدایت از پروردگارشانند، و آنانند رستگاران!'),
(3475, 3475, 'وَمِنَ النَّاسِ مَنْ يَشْتَرِي لَهْوَ الْحَدِيثِ لِيُضِلَّ عَنْ سَبِيلِ اللَّهِ بِغَيْرِ عِلْمٍ وَيَتَّخِذَهَا هُزُوًا ۚ أُولَٰئِكَ لَهُمْ عَذَابٌ مُهِينٌ', 31, 6, 21, 82, 411, 'و بعضی از مردم سخنان بیهوده را می‌خرند تا مردم را از روی نادانی، از راه خدا گمراه سازند و آیات الهی را به استهزا گیرند؛ برای آنان عذابی خوارکننده است!'),
(3476, 3476, 'وَإِذَا تُتْلَىٰ عَلَيْهِ آيَاتُنَا وَلَّىٰ مُسْتَكْبِرًا كَأَنْ لَمْ يَسْمَعْهَا كَأَنَّ فِي أُذُنَيْهِ وَقْرًا ۖ فَبَشِّرْهُ بِعَذَابٍ أَلِيمٍ', 31, 7, 21, 82, 411, 'و هنگامی که آیات ما بر او خوانده می‌شود، مستکبرانه روی برمی‌گرداند، گویی آن را نشنیده است؛ گویی اصلاً گوشهایش سنگین است! او را به عذابی دردناک بشارت ده!'),
(3477, 3477, 'إِنَّ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ لَهُمْ جَنَّاتُ النَّعِيمِ', 31, 8, 21, 82, 411, '(ولی) کسانی که ایمان آورده و اعمال صالح انجام داده‌اند، باغهای پرنعمت بهشت از آن آنهاست؛'),
(3478, 3478, 'خَالِدِينَ فِيهَا ۖ وَعْدَ اللَّهِ حَقًّا ۚ وَهُوَ الْعَزِيزُ الْحَكِيمُ', 31, 9, 21, 82, 411, 'جاودانه در آن خواهند ماند؛ این وعده حتمی الهی است؛ و اوست عزیز و حکیم (شکست‌ناپذیر و دانا).'),
(3479, 3479, 'خَلَقَ السَّمَاوَاتِ بِغَيْرِ عَمَدٍ تَرَوْنَهَا ۖ وَأَلْقَىٰ فِي الْأَرْضِ رَوَاسِيَ أَنْ تَمِيدَ بِكُمْ وَبَثَّ فِيهَا مِنْ كُلِّ دَابَّةٍ ۚ وَأَنْزَلْنَا مِنَ السَّمَاءِ مَاءً فَأَنْبَتْنَا فِيهَا مِنْ كُلِّ زَوْجٍ كَرِيمٍ', 31, 10, 21, 82, 411, '(او) آسمانها را بدون ستونی که آن را ببینید آفرید، و در زمین کوه‌هایی افکند تا شما را نلرزاند (و جایگاه شما آرام باشد) و از هر گونه جنبنده‌ای روی آن منتشر ساخت؛ و از آسمان آبی نازل کردیم و بوسیله آن در روی زمین انواع گوناگونی از جفتهای گیاهان پر ارزش رویاندیم.'),
(3480, 3480, 'هَٰذَا خَلْقُ اللَّهِ فَأَرُونِي مَاذَا خَلَقَ الَّذِينَ مِنْ دُونِهِ ۚ بَلِ الظَّالِمُونَ فِي ضَلَالٍ مُبِينٍ', 31, 11, 21, 82, 411, 'این آفرینش خداست؛ امّا به من نشان دهید معبودانی غیر او چه چیز را آفریده‌اند؟! ولی ظالمان در گمراهی آشکارند.'),
(3481, 3481, 'وَلَقَدْ آتَيْنَا لُقْمَانَ الْحِكْمَةَ أَنِ اشْكُرْ لِلَّهِ ۚ وَمَنْ يَشْكُرْ فَإِنَّمَا يَشْكُرُ لِنَفْسِهِ ۖ وَمَنْ كَفَرَ فَإِنَّ اللَّهَ غَنِيٌّ حَمِيدٌ', 31, 12, 21, 82, 412, 'ما به لقمان حکمت دادیم؛ (و به او گفتیم:) شکر خدا را بجای آور هر کس شکرگزاری کند، تنها به سود خویش شکر کرده؛ و آن کس که کفران کند، (زیانی به خدا نمی‌رساند)؛ چرا که خداوند بی‌نیاز و ستوده است.'),
(3482, 3482, 'وَإِذْ قَالَ لُقْمَانُ لِابْنِهِ وَهُوَ يَعِظُهُ يَا بُنَيَّ لَا تُشْرِكْ بِاللَّهِ ۖ إِنَّ الشِّرْكَ لَظُلْمٌ عَظِيمٌ', 31, 13, 21, 82, 412, '(به خاطر بیاور) هنگامی را که لقمان به فرزندش -در حالی که او را موعظه می‌کرد- گفت: «پسرم! چیزی را همتای خدا قرار مده که شرک، ظلم بزرگی است.»'),
(3483, 3483, 'وَوَصَّيْنَا الْإِنْسَانَ بِوَالِدَيْهِ حَمَلَتْهُ أُمُّهُ وَهْنًا عَلَىٰ وَهْنٍ وَفِصَالُهُ فِي عَامَيْنِ أَنِ اشْكُرْ لِي وَلِوَالِدَيْكَ إِلَيَّ الْمَصِيرُ', 31, 14, 21, 82, 412, 'و ما به انسان درباره پدر و مادرش سفارش کردیم؛ مادرش او را با ناتوانی روی ناتوانی حمل کرد (به هنگام بارداری هر روز رنج و ناراحتی تازه‌ای را متحمّل می‌شد)، و دوران شیرخوارگی او در دو سال پایان می‌یابد؛ (آری به او توصیه کردم) که برای من و برای پدر و مادرت شکر بجا آور که بازگشت (همه شما) به سوی من است!'),
(3484, 3484, 'وَإِنْ جَاهَدَاكَ عَلَىٰ أَنْ تُشْرِكَ بِي مَا لَيْسَ لَكَ بِهِ عِلْمٌ فَلَا تُطِعْهُمَا ۖ وَصَاحِبْهُمَا فِي الدُّنْيَا مَعْرُوفًا ۖ وَاتَّبِعْ سَبِيلَ مَنْ أَنَابَ إِلَيَّ ۚ ثُمَّ إِلَيَّ مَرْجِعُكُمْ فَأُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُونَ', 31, 15, 21, 82, 412, 'و هرگاه آن دو، تلاش کنند که تو چیزی را همتای من قرار دهی، که از آن آگاهی نداری (بلکه می‌دانی باطل است)، از ایشان اطاعت مکن، ولی با آن دو، در دنیا به طرز شایسته‌ای رفتار کن؛ و از راه کسانی پیروی کن که توبه‌کنان به سوی من آمده‌اند؛ سپس بازگشت همه شما به سوی من است و من شما را از آنچه عمل می‌کردید آگاه می‌کنم.'),
(3485, 3485, 'يَا بُنَيَّ إِنَّهَا إِنْ تَكُ مِثْقَالَ حَبَّةٍ مِنْ خَرْدَلٍ فَتَكُنْ فِي صَخْرَةٍ أَوْ فِي السَّمَاوَاتِ أَوْ فِي الْأَرْضِ يَأْتِ بِهَا اللَّهُ ۚ إِنَّ اللَّهَ لَطِيفٌ خَبِيرٌ', 31, 16, 21, 82, 412, 'پسرم! اگر به اندازه سنگینی دانه خردلی (کار نیک یا بد) باشد، و در دل سنگی یا در (گوشه‌ای از) آسمانها و زمین قرار گیرد، خداوند آن را (در قیامت برای حساب) می‌آورد؛ خداوند دقیق و آگاه است!'),
(3486, 3486, 'يَا بُنَيَّ أَقِمِ الصَّلَاةَ وَأْمُرْ بِالْمَعْرُوفِ وَانْهَ عَنِ الْمُنْكَرِ وَاصْبِرْ عَلَىٰ مَا أَصَابَكَ ۖ إِنَّ ذَٰلِكَ مِنْ عَزْمِ الْأُمُورِ', 31, 17, 21, 82, 412, 'پسرم! نماز را برپا دار، و امر به معروف و نهی از منکر کن، و در برابر مصایبی که به تو می‌رسد شکیبا باش که این از کارهای مهمّ است!'),
(3487, 3487, 'وَلَا تُصَعِّرْ خَدَّكَ لِلنَّاسِ وَلَا تَمْشِ فِي الْأَرْضِ مَرَحًا ۖ إِنَّ اللَّهَ لَا يُحِبُّ كُلَّ مُخْتَالٍ فَخُورٍ', 31, 18, 21, 82, 412, '(پسرم!) با بی‌اعتنایی از مردم روی مگردان، و مغرورانه بر زمین راه مرو که خداوند هیچ متکبّر مغروری را دوست ندارد.'),
(3488, 3488, 'وَاقْصِدْ فِي مَشْيِكَ وَاغْضُضْ مِنْ صَوْتِكَ ۚ إِنَّ أَنْكَرَ الْأَصْوَاتِ لَصَوْتُ الْحَمِيرِ', 31, 19, 21, 82, 412, '(پسرم!) در راه‌رفتن، اعتدال را رعایت کن؛ از صدای خود بکاه (و هرگز فریاد مزن) که زشت‌ترین صداها صدای خران است.'),
(3489, 3489, 'أَلَمْ تَرَوْا أَنَّ اللَّهَ سَخَّرَ لَكُمْ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ وَأَسْبَغَ عَلَيْكُمْ نِعَمَهُ ظَاهِرَةً وَبَاطِنَةً ۗ وَمِنَ النَّاسِ مَنْ يُجَادِلُ فِي اللَّهِ بِغَيْرِ عِلْمٍ وَلَا هُدًى وَلَا كِتَابٍ مُنِيرٍ', 31, 20, 21, 82, 413, 'آیا ندیدید خداوند آنچه را در آسمانها و زمین است مسخّر شما کرده، و نعمتهای آشکار و پنهان خود را به طور فراوان بر شما ارزانی داشته است؟! ولی بعضی از مردم بدون هیچ دانش و هدایت و کتاب روشنگری درباره خدا مجادله می‌کنند!'),
(3490, 3490, 'وَإِذَا قِيلَ لَهُمُ اتَّبِعُوا مَا أَنْزَلَ اللَّهُ قَالُوا بَلْ نَتَّبِعُ مَا وَجَدْنَا عَلَيْهِ آبَاءَنَا ۚ أَوَلَوْ كَانَ الشَّيْطَانُ يَدْعُوهُمْ إِلَىٰ عَذَابِ السَّعِيرِ', 31, 21, 21, 82, 413, 'و هنگامی که به آنان گفته شود: «از آنچه خدا نازل کرده پیروی کنید!»، می‌گویند: «نه، بلکه ما از چیزی پیروی می‌کنیم که پدران خود را بر آن یافتیم!» آیا حتّی اگر شیطان آنان را دعوت به عذاب آتش فروزان کند (باز هم تبعیّت می‌کنند)؟!'),
(3491, 3491, ' وَمَنْ يُسْلِمْ وَجْهَهُ إِلَى اللَّهِ وَهُوَ مُحْسِنٌ فَقَدِ اسْتَمْسَكَ بِالْعُرْوَةِ الْوُثْقَىٰ ۗ وَإِلَى اللَّهِ عَاقِبَةُ الْأُمُورِ', 31, 22, 21, 83, 413, 'کسی که روی خود را تسلیم خدا کند در حالی که نیکوکار باشد، به دستگیره محکمی چنگ زده (و به تکیه‌گاه مطمئنی تکیه کرده است)؛ و عاقبت همه کارها به سوی خداست.'),
(3492, 3492, 'وَمَنْ كَفَرَ فَلَا يَحْزُنْكَ كُفْرُهُ ۚ إِلَيْنَا مَرْجِعُهُمْ فَنُنَبِّئُهُمْ بِمَا عَمِلُوا ۚ إِنَّ اللَّهَ عَلِيمٌ بِذَاتِ الصُّدُورِ', 31, 23, 21, 83, 413, 'و کسی که کافر شود، کفر او تو را غمگین نسازد؛ بازگشت همه آنان به سوی ماست و ما آنها را از اعمالی که انجام داده‌اند (و نتایج شوم آن) آگاه خواهیم ساخت؛ خداوند به آنچه درون سینه‌هاست آگاه است.'),
(3493, 3493, 'نُمَتِّعُهُمْ قَلِيلًا ثُمَّ نَضْطَرُّهُمْ إِلَىٰ عَذَابٍ غَلِيظٍ', 31, 24, 21, 83, 413, 'ما اندکی آنها را از متاع دنیا بهره‌مند می‌کنیم، سپس آنها را به تحمّل عذاب شدیدی وادار می‌سازیم!'),
(3494, 3494, 'وَلَئِنْ سَأَلْتَهُمْ مَنْ خَلَقَ السَّمَاوَاتِ وَالْأَرْضَ لَيَقُولُنَّ اللَّهُ ۚ قُلِ الْحَمْدُ لِلَّهِ ۚ بَلْ أَكْثَرُهُمْ لَا يَعْلَمُونَ', 31, 25, 21, 83, 413, 'و هرگاه از آنان سؤال کنی: «چه کسی آسمانها و زمین را آفریده است؟» مسلّماً می‌گویند: «اللّه»، بگو: «الحمد للّه (که خود شما معترفید)!» ولی بیشتر آنان نمی‌دانند!'),
(3495, 3495, 'لِلَّهِ مَا فِي السَّمَاوَاتِ وَالْأَرْضِ ۚ إِنَّ اللَّهَ هُوَ الْغَنِيُّ الْحَمِيدُ', 31, 26, 21, 83, 413, 'آنچه در آسمانها و زمین است از آن خداست، چرا که خداوند بی‌نیاز و شایسته ستایش است!'),
(3496, 3496, 'وَلَوْ أَنَّمَا فِي الْأَرْضِ مِنْ شَجَرَةٍ أَقْلَامٌ وَالْبَحْرُ يَمُدُّهُ مِنْ بَعْدِهِ سَبْعَةُ أَبْحُرٍ مَا نَفِدَتْ كَلِمَاتُ اللَّهِ ۗ إِنَّ اللَّهَ عَزِيزٌ حَكِيمٌ', 31, 27, 21, 83, 413, 'و اگر همه درختان روی زمین قلم شود، و دریا برای آن مرکّب گردد، و هفت دریاچه به آن افزوده شود، اینها همه تمام می‌شود ولی کلمات خدا پایان نمی‌گیرد؛ خداوند عزیز و حکیم است.'),
(3497, 3497, 'مَا خَلْقُكُمْ وَلَا بَعْثُكُمْ إِلَّا كَنَفْسٍ وَاحِدَةٍ ۗ إِنَّ اللَّهَ سَمِيعٌ بَصِيرٌ', 31, 28, 21, 83, 413, 'آفرینش و برانگیختن (و زندگی دوباره) همه شما (در قیامت) همانند یک فرد بیش نیست؛ خداوند شنوا و بیناست!'),
(3498, 3498, 'أَلَمْ تَرَ أَنَّ اللَّهَ يُولِجُ اللَّيْلَ فِي النَّهَارِ وَيُولِجُ النَّهَارَ فِي اللَّيْلِ وَسَخَّرَ الشَّمْسَ وَالْقَمَرَ كُلٌّ يَجْرِي إِلَىٰ أَجَلٍ مُسَمًّى وَأَنَّ اللَّهَ بِمَا تَعْمَلُونَ خَبِيرٌ', 31, 29, 21, 83, 414, 'آیا ندیدی که خداوند شب را در روز، و روز را در شب داخل می‌کند، و خورشید و ماه را مسخّر ساخته و هر کدام تا سرآمد معینی به حرکت خود ادامه می‌دهند؟! خداوند به آنچه انجام می‌دهید آگاه است.'),
(3499, 3499, 'ذَٰلِكَ بِأَنَّ اللَّهَ هُوَ الْحَقُّ وَأَنَّ مَا يَدْعُونَ مِنْ دُونِهِ الْبَاطِلُ وَأَنَّ اللَّهَ هُوَ الْعَلِيُّ الْكَبِيرُ', 31, 30, 21, 83, 414, 'اینها همه دلیل بر آن است که خداوند حقّ است، و آنچه غیر از او می‌خوانند باطل است، و خداوند بلند مقام و بزرگ مرتبه است!'),
(3500, 3500, 'أَلَمْ تَرَ أَنَّ الْفُلْكَ تَجْرِي فِي الْبَحْرِ بِنِعْمَتِ اللَّهِ لِيُرِيَكُمْ مِنْ آيَاتِهِ ۚ إِنَّ فِي ذَٰلِكَ لَآيَاتٍ لِكُلِّ صَبَّارٍ شَكُورٍ', 31, 31, 21, 83, 414, 'آیا ندیدی کشتیها بر (صفحه) دریاها به فرمان خدا، و به (برکت) نعمت او حرکت می‌کنند تا بخشی از آیاتش را به شما نشان دهد؟! در اینها نشانه‌هایی است برای کسانی که شکیبا و شکرگزارند!'),
(3501, 3501, 'وَإِذَا غَشِيَهُمْ مَوْجٌ كَالظُّلَلِ دَعَوُا اللَّهَ مُخْلِصِينَ لَهُ الدِّينَ فَلَمَّا نَجَّاهُمْ إِلَى الْبَرِّ فَمِنْهُمْ مُقْتَصِدٌ ۚ وَمَا يَجْحَدُ بِآيَاتِنَا إِلَّا كُلُّ خَتَّارٍ كَفُورٍ', 31, 32, 21, 83, 414, 'و هنگامی که (در سفر دریا) موجی همچون ابرها آنان را بپوشاند (و بالا رود و بالای سرشان قرار گیرد)، خدا را با اخلاص می‌خوانند؛ امّا وقتی آنها را به خشکی رساند و نجات داد، بعضی راه اعتدال را پیش می‌گیرند (و به ایمان خود وفادار می‌مانند، در حالی که بعضی دیگر فراموش کرده راه کفر پیش می‌گیرند)؛ ولی آیات ما را هیچ کس جز پیمان‌شکنان ناسپاس انکار نمی‌کنند!'),
(3502, 3502, 'يَا أَيُّهَا النَّاسُ اتَّقُوا رَبَّكُمْ وَاخْشَوْا يَوْمًا لَا يَجْزِي وَالِدٌ عَنْ وَلَدِهِ وَلَا مَوْلُودٌ هُوَ جَازٍ عَنْ وَالِدِهِ شَيْئًا ۚ إِنَّ وَعْدَ اللَّهِ حَقٌّ ۖ فَلَا تَغُرَّنَّكُمُ الْحَيَاةُ الدُّنْيَا وَلَا يَغُرَّنَّكُمْ بِاللَّهِ الْغَرُورُ', 31, 33, 21, 83, 414, 'ای مردم! تقوای الهی پیشه کنید و بترسید از روزی که نه پدر کیفر اعمال فرزندش را تحمّل می‌کند، و نه فرزند چیزی از کیفر (اعمال) پدرش را؛ به یقین وعده الهی حقّ است؛ پس مبادا زندگانی دنیا شما را بفریبد، و مبادا (شیطان) فریبکار شما را (به کرم) خدا مغرور سازد!'),
(3503, 3503, 'إِنَّ اللَّهَ عِنْدَهُ عِلْمُ السَّاعَةِ وَيُنَزِّلُ الْغَيْثَ وَيَعْلَمُ مَا فِي الْأَرْحَامِ ۖ وَمَا تَدْرِي نَفْسٌ مَاذَا تَكْسِبُ غَدًا ۖ وَمَا تَدْرِي نَفْسٌ بِأَيِّ أَرْضٍ تَمُوتُ ۚ إِنَّ اللَّهَ عَلِيمٌ خَبِيرٌ', 31, 34, 21, 83, 414, 'آگاهی از زمان قیام قیامت مخصوص خداست، و اوست که باران را نازل می‌کند، و آنچه را که در رحم‌ها (ی مادران) است می‌داند، و هیچ کس نمی‌داند فردا چه به دست می‌آورد، و هیچ کس نمی‌داند در چه سرزمینی می‌میرد؟ خداوند عالم و آگاه است!'),
(3504, 3504, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ الم', 32, 1, 21, 83, 415, 'الم‌'),
(3505, 3505, 'تَنْزِيلُ الْكِتَابِ لَا رَيْبَ فِيهِ مِنْ رَبِّ الْعَالَمِينَ', 32, 2, 21, 83, 415, 'این کتابی است که از سوی پروردگار جهانیان نازل شده، و شک و تردیدی در آن نیست!'),
(3506, 3506, 'أَمْ يَقُولُونَ افْتَرَاهُ ۚ بَلْ هُوَ الْحَقُّ مِنْ رَبِّكَ لِتُنْذِرَ قَوْمًا مَا أَتَاهُمْ مِنْ نَذِيرٍ مِنْ قَبْلِكَ لَعَلَّهُمْ يَهْتَدُونَ', 32, 3, 21, 83, 415, 'ولی آنان می‌گویند: «(محمّد) آن را بدروغ به خدا بسته است»، امّا این سخن حقّی است از سوی پروردگارت تا گروهی را انذار کنی که پیش از تو هیچ انذارکننده‌ای برای آنان نیامده است، شاید (پند گیرند و) هدایت شوند!'),
(3507, 3507, 'اللَّهُ الَّذِي خَلَقَ السَّمَاوَاتِ وَالْأَرْضَ وَمَا بَيْنَهُمَا فِي سِتَّةِ أَيَّامٍ ثُمَّ اسْتَوَىٰ عَلَى الْعَرْشِ ۖ مَا لَكُمْ مِنْ دُونِهِ مِنْ وَلِيٍّ وَلَا شَفِيعٍ ۚ أَفَلَا تَتَذَكَّرُونَ', 32, 4, 21, 83, 415, 'خداوند کسی است که آسمانها و زمین و آنچه را میان این دو است در شش روز = شش دوران‌ آفرید، سپس بر عرش (قدرت) قرار گرفت؛ هیچ سرپرست و شفاعت کننده‌ای برای شما جز او نیست؛ آیا متذکّر نمی‌شوید؟!'),
(3508, 3508, 'يُدَبِّرُ الْأَمْرَ مِنَ السَّمَاءِ إِلَى الْأَرْضِ ثُمَّ يَعْرُجُ إِلَيْهِ فِي يَوْمٍ كَانَ مِقْدَارُهُ أَلْفَ سَنَةٍ مِمَّا تَعُدُّونَ', 32, 5, 21, 83, 415, 'امور این جهان را از آسمان به سوی زمین تدبیر می‌کند؛ سپس در روزی که مقدار آن هزار سال از سالهایی است که شما می‌شمرید بسوی او بالا می‌رود (و دنیا پایان می‌یابد).'),
(3509, 3509, 'ذَٰلِكَ عَالِمُ الْغَيْبِ وَالشَّهَادَةِ الْعَزِيزُ الرَّحِيمُ', 32, 6, 21, 83, 415, 'او خداوندی است که از پنهان و آشکار با خبر است، و شکست‌ناپذیر و مهربان است!'),
(3510, 3510, 'الَّذِي أَحْسَنَ كُلَّ شَيْءٍ خَلَقَهُ ۖ وَبَدَأَ خَلْقَ الْإِنْسَانِ مِنْ طِينٍ', 32, 7, 21, 83, 415, 'او همان کسی است که هر چه را آفرید نیکو آفرید؛ و آفرینش انسان را از گِل آغاز کرد؛'),
(3511, 3511, 'ثُمَّ جَعَلَ نَسْلَهُ مِنْ سُلَالَةٍ مِنْ مَاءٍ مَهِينٍ', 32, 8, 21, 83, 415, 'سپس نسل او را از عصاره‌ای از آب ناچیز و بی‌قدر آفرید.'),
(3512, 3512, 'ثُمَّ سَوَّاهُ وَنَفَخَ فِيهِ مِنْ رُوحِهِ ۖ وَجَعَلَ لَكُمُ السَّمْعَ وَالْأَبْصَارَ وَالْأَفْئِدَةَ ۚ قَلِيلًا مَا تَشْكُرُونَ', 32, 9, 21, 83, 415, 'سپس (اندام) او را موزون ساخت و از روح خویش در وی دمید؛ و برای شما گوش و چشمها و دلها قرار داد؛ امّا کمتر شکر نعمتهای او را بجا می‌آورید!'),
(3513, 3513, 'وَقَالُوا أَإِذَا ضَلَلْنَا فِي الْأَرْضِ أَإِنَّا لَفِي خَلْقٍ جَدِيدٍ ۚ بَلْ هُمْ بِلِقَاءِ رَبِّهِمْ كَافِرُونَ', 32, 10, 21, 83, 415, 'آنها گفتند: «آیا هنگامی که ما (مردیم و) در زمین گم شدیم، آفرینش تازه‌ای خواهیم یافت؟!» ولی آنان لقای پروردگارشان را انکار می‌کنند (و می‌خواهند با انکار معاد، آزادانه به هوسرانی خویش ادامه دهند).'),
(3514, 3514, ' قُلْ يَتَوَفَّاكُمْ مَلَكُ الْمَوْتِ الَّذِي وُكِّلَ بِكُمْ ثُمَّ إِلَىٰ رَبِّكُمْ تُرْجَعُونَ', 32, 11, 21, 83, 415, 'بگو: «فرشته مرگ که بر شما مأمور شده، (روح) شما را می‌گیرد؛ سپس شما را بسوی پروردگارتان بازمی‌گردانند.»'),
(3515, 3515, 'وَلَوْ تَرَىٰ إِذِ الْمُجْرِمُونَ نَاكِسُو رُءُوسِهِمْ عِنْدَ رَبِّهِمْ رَبَّنَا أَبْصَرْنَا وَسَمِعْنَا فَارْجِعْنَا نَعْمَلْ صَالِحًا إِنَّا مُوقِنُونَ', 32, 12, 21, 83, 416, 'و اگر ببینی مجرمان را هنگامی که در پیشگاه پروردگارشان سر به زیر افکنده، می‌گویند: «پروردگارا! آنچه وعده کرده بودی دیدیم و شنیدیم؛ ما را بازگردان تا کار شایسته‌ای انجام دهیم؛ ما (به قیامت) یقین داریم!»'),
(3516, 3516, 'وَلَوْ شِئْنَا لَآتَيْنَا كُلَّ نَفْسٍ هُدَاهَا وَلَٰكِنْ حَقَّ الْقَوْلُ مِنِّي لَأَمْلَأَنَّ جَهَنَّمَ مِنَ الْجِنَّةِ وَالنَّاسِ أَجْمَعِينَ', 32, 13, 21, 83, 416, 'و اگر می‌خواستیم به هر انسانی هدایت لازمش را (از روی اجبار بدهیم) می‌دادیم؛ ولی (من آنها را آزاد گذارده‌ام و) سخن و وعده‌ام حق است که دوزخ را (از افراد بی‌ایمان و گنهکار) از جنّ و انس همگی پر کنم!'),
(3517, 3517, 'فَذُوقُوا بِمَا نَسِيتُمْ لِقَاءَ يَوْمِكُمْ هَٰذَا إِنَّا نَسِينَاكُمْ ۖ وَذُوقُوا عَذَابَ الْخُلْدِ بِمَا كُنْتُمْ تَعْمَلُونَ', 32, 14, 21, 83, 416, '(و به آنها می‌گویم:) بچشید (عذاب جهنم را)! بخاطر اینکه دیدار امروزتان را فراموش کردید، ما نیز شما را فراموش کردیم؛ و بچشید عذاب جاودان را به خاطر اعمالی که انجام می‌دادید!'),
(3518, 3518, 'إِنَّمَا يُؤْمِنُ بِآيَاتِنَا الَّذِينَ إِذَا ذُكِّرُوا بِهَا خَرُّوا سُجَّدًا وَسَبَّحُوا بِحَمْدِ رَبِّهِمْ وَهُمْ لَا يَسْتَكْبِرُونَ ۩', 32, 15, 21, 83, 416, 'تنها کسانی که به آیات ما ایمان می‌آورند که هر وقت این آیات به آنان یادآوری شود به سجده می‌افتند و تسبیح و حمد پروردگارشان را بجا می‌آورند، و تکبّر نمی‌کنند.'),
(3519, 3519, 'تَتَجَافَىٰ جُنُوبُهُمْ عَنِ الْمَضَاجِعِ يَدْعُونَ رَبَّهُمْ خَوْفًا وَطَمَعًا وَمِمَّا رَزَقْنَاهُمْ يُنْفِقُونَ', 32, 16, 21, 83, 416, 'پهلوهایشان از بسترها در دل شب دور می‌شود (و بپا می‌خیزند و رو به درگاه خدا می آورند) و پروردگار خود را با بیم و امید می‌خوانند، و از آنچه به آنان روزی داده‌ایم انفاق می‌کنند!'),
(3520, 3520, 'فَلَا تَعْلَمُ نَفْسٌ مَا أُخْفِيَ لَهُمْ مِنْ قُرَّةِ أَعْيُنٍ جَزَاءً بِمَا كَانُوا يَعْمَلُونَ', 32, 17, 21, 83, 416, 'هیچ کس نمی‌داند چه پاداشهای مهمّی که مایه روشنی چشمهاست برای آنها نهفته شده، این پاداش کارهائی است که انجام می‌دادند!'),
(3521, 3521, 'أَفَمَنْ كَانَ مُؤْمِنًا كَمَنْ كَانَ فَاسِقًا ۚ لَا يَسْتَوُونَ', 32, 18, 21, 83, 416, 'آیا کسی که باایمان باشد همچون کسی است که فاسق است؟! نه، هرگز این دو برابر نیستند.'),
(3522, 3522, 'أَمَّا الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ فَلَهُمْ جَنَّاتُ الْمَأْوَىٰ نُزُلًا بِمَا كَانُوا يَعْمَلُونَ', 32, 19, 21, 83, 416, 'امّا کسانی که ایمان آوردند و کارهای شایسته انجام دادند، باغهای بهشت جاویدان از آن آنها خواهد بود، این وسیله پذیرایی (خداوند) از آنهاست به پاداش آنچه انجام می‌دادند.'),
(3523, 3523, 'وَأَمَّا الَّذِينَ فَسَقُوا فَمَأْوَاهُمُ النَّارُ ۖ كُلَّمَا أَرَادُوا أَنْ يَخْرُجُوا مِنْهَا أُعِيدُوا فِيهَا وَقِيلَ لَهُمْ ذُوقُوا عَذَابَ النَّارِ الَّذِي كُنْتُمْ بِهِ تُكَذِّبُونَ', 32, 20, 21, 83, 416, 'و امّا کسانی که فاسق شدند (و از اطاعت خدا سرباز زدند)، جایگاه همیشگی آنها آتش است؛ هر زمان بخواهند از آن خارج شوند، آنها را به آن بازمی‌گردانند و به آنان گفته می‌شود: «بچشید عذاب آتشی را که انکار می‌کردید!»'),
(3524, 3524, 'وَلَنُذِيقَنَّهُمْ مِنَ الْعَذَابِ الْأَدْنَىٰ دُونَ الْعَذَابِ الْأَكْبَرِ لَعَلَّهُمْ يَرْجِعُونَ', 32, 21, 21, 83, 417, 'به آنان از عذاب نزدیک (عذاب این دنیا) پیش از عذاب بزرگ (آخرت) می‌چشانیم، شاید بازگردند!'),
(3525, 3525, 'وَمَنْ أَظْلَمُ مِمَّنْ ذُكِّرَ بِآيَاتِ رَبِّهِ ثُمَّ أَعْرَضَ عَنْهَا ۚ إِنَّا مِنَ الْمُجْرِمِينَ مُنْتَقِمُونَ', 32, 22, 21, 83, 417, 'چه کسی ستمکارتر است از آن کس که آیات پروردگارش به او یادآوری شده و او از آن اعراض کرده است؟! مسلّماً ما از مجرمان انتقام خواهیم گرفت!'),
(3526, 3526, 'وَلَقَدْ آتَيْنَا مُوسَى الْكِتَابَ فَلَا تَكُنْ فِي مِرْيَةٍ مِنْ لِقَائِهِ ۖ وَجَعَلْنَاهُ هُدًى لِبَنِي إِسْرَائِيلَ', 32, 23, 21, 83, 417, 'ما به موسی کتاب آسمانی دادیم؛ و شک نداشته باش که او آیات الهی را دریافت داشت؛ و ما آن را وسیله هدایت بنی اسرائیل قرار دادیم!'),
(3527, 3527, 'وَجَعَلْنَا مِنْهُمْ أَئِمَّةً يَهْدُونَ بِأَمْرِنَا لَمَّا صَبَرُوا ۖ وَكَانُوا بِآيَاتِنَا يُوقِنُونَ', 32, 24, 21, 83, 417, 'و از آنان امامان (و پیشوایانی) قرار دادیم که به فرمان ما (مردم را) هدایت می‌کردند؛ چون شکیبایی نمودند، و به آیات ما یقین داشتند.'),
(3528, 3528, 'إِنَّ رَبَّكَ هُوَ يَفْصِلُ بَيْنَهُمْ يَوْمَ الْقِيَامَةِ فِيمَا كَانُوا فِيهِ يَخْتَلِفُونَ', 32, 25, 21, 83, 417, 'البتّه پروردگار تو میان آنان روز قیامت در آنچه اختلاف داشتند داوری می‌کند (و هر کس را به سزای اعمالش می‌رساند)!'),
(3529, 3529, 'أَوَلَمْ يَهْدِ لَهُمْ كَمْ أَهْلَكْنَا مِنْ قَبْلِهِمْ مِنَ الْقُرُونِ يَمْشُونَ فِي مَسَاكِنِهِمْ ۚ إِنَّ فِي ذَٰلِكَ لَآيَاتٍ ۖ أَفَلَا يَسْمَعُونَ', 32, 26, 21, 83, 417, 'آیا برای هدایت آنها همین کافی نیست که افراد زیادی را که در قرن پیش از آنان زندگی داشتند هلاک کردیم؟! اینها در مساکن (ویران شده) آنان راه می‌روند؛ در این آیاتی است (از قدرت خداوند و مجازات دردناک مجرمان)؛ آیا نمی‌شنوند؟!'),
(3530, 3530, 'أَوَلَمْ يَرَوْا أَنَّا نَسُوقُ الْمَاءَ إِلَى الْأَرْضِ الْجُرُزِ فَنُخْرِجُ بِهِ زَرْعًا تَأْكُلُ مِنْهُ أَنْعَامُهُمْ وَأَنْفُسُهُمْ ۖ أَفَلَا يُبْصِرُونَ', 32, 27, 21, 83, 417, 'آیا ندیدند که ما آب را بسوی زمینهای خشک می‌رانیم و بوسیله آن زراعتهایی می‌رویانیم که هم چهارپایانشان از آن می‌خورند و هم خودشان تغذیه می‌کنند؛ آیا نمی بینند؟!'),
(3531, 3531, 'وَيَقُولُونَ مَتَىٰ هَٰذَا الْفَتْحُ إِنْ كُنْتُمْ صَادِقِينَ', 32, 28, 21, 83, 417, 'آنان می‌گویند: «اگر راست می‌گویید، این پیروزی شما کی خواهد بود؟!»');
INSERT INTO `Quran` (`rowid`, `id`, `Arabic`, `Chapter`, `Verse`, `Juz`, `Hizb`, `Page`, `tr`) VALUES
(3532, 3532, 'قُلْ يَوْمَ الْفَتْحِ لَا يَنْفَعُ الَّذِينَ كَفَرُوا إِيمَانُهُمْ وَلَا هُمْ يُنْظَرُونَ', 32, 29, 21, 83, 417, 'بگو: «روز پیروزی، ایمان آوردن، سودی به حال کافران نخواهد داشت؛ و به آنها هیچ مهلت داده نمی‌شود!»'),
(3533, 3533, 'فَأَعْرِضْ عَنْهُمْ وَانْتَظِرْ إِنَّهُمْ مُنْتَظِرُونَ', 32, 30, 21, 83, 417, 'حال که چنین است، از آنها روی بگردان و منتظر باش؛ آنها نیز منتظرند! (تو منتظر رحمت خدا و آنها هم منتظر عذاب او!)'),
(3534, 3534, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ يَا أَيُّهَا النَّبِيُّ اتَّقِ اللَّهَ وَلَا تُطِعِ الْكَافِرِينَ وَالْمُنَافِقِينَ ۗ إِنَّ اللَّهَ كَانَ عَلِيمًا حَكِيمًا', 33, 1, 21, 84, 418, 'ای پیامبر! تقوای الهی پیشه کن و از کافران و منافقان اطاعت مکن که خداوند عالم و حکیم است.'),
(3535, 3535, 'وَاتَّبِعْ مَا يُوحَىٰ إِلَيْكَ مِنْ رَبِّكَ ۚ إِنَّ اللَّهَ كَانَ بِمَا تَعْمَلُونَ خَبِيرًا', 33, 2, 21, 84, 418, 'و از آنچه از سوی پروردگارت به تو وحی می‌شود پیروی کن که خداوند به آنچه انجام می‌دهید آگاه است.'),
(3536, 3536, 'وَتَوَكَّلْ عَلَى اللَّهِ ۚ وَكَفَىٰ بِاللَّهِ وَكِيلًا', 33, 3, 21, 84, 418, 'و بر خدا توکّل کن، و همین بس که خداوند حافظ و مدافع (انسان) باشد!'),
(3537, 3537, 'مَا جَعَلَ اللَّهُ لِرَجُلٍ مِنْ قَلْبَيْنِ فِي جَوْفِهِ ۚ وَمَا جَعَلَ أَزْوَاجَكُمُ اللَّائِي تُظَاهِرُونَ مِنْهُنَّ أُمَّهَاتِكُمْ ۚ وَمَا جَعَلَ أَدْعِيَاءَكُمْ أَبْنَاءَكُمْ ۚ ذَٰلِكُمْ قَوْلُكُمْ بِأَفْوَاهِكُمْ ۖ وَاللَّهُ يَقُولُ الْحَقَّ وَهُوَ يَهْدِي السَّبِيلَ', 33, 4, 21, 84, 418, 'خداوند برای هیچ کس دو دل در درونش نیافریده؛ و هرگز همسرانتان را که مورد «ظهار» قرارمی‌دهید مادران شما قرار نداده؛ و (نیز) فرزندخوانده‌های شما را فرزند حقیقی شما قرارنداده است؛ این سخن شماست که به دهان خود می‌گویید (سخنی باطل و بی‌پایه)؛ امّا خداوند حقّ را می‌گوید و او به راه راست هدایت می‌کند.'),
(3538, 3538, 'ادْعُوهُمْ لِآبَائِهِمْ هُوَ أَقْسَطُ عِنْدَ اللَّهِ ۚ فَإِنْ لَمْ تَعْلَمُوا آبَاءَهُمْ فَإِخْوَانُكُمْ فِي الدِّينِ وَمَوَالِيكُمْ ۚ وَلَيْسَ عَلَيْكُمْ جُنَاحٌ فِيمَا أَخْطَأْتُمْ بِهِ وَلَٰكِنْ مَا تَعَمَّدَتْ قُلُوبُكُمْ ۚ وَكَانَ اللَّهُ غَفُورًا رَحِيمًا', 33, 5, 21, 84, 418, 'آنها را به نام پدرانشان بخوانید که این کار نزد خدا عادلانه‌تر است؛ و اگر پدرانشان را نمی‌شناسید، آنها برادران دینی و موالی شما هستند؛ امّا گناهی بر شما نیست در خطاهایی که از شما سرمی‌زند (و بی‌توجّه آنها را به نام دیگران صدا می‌زنید)، ولی آنچه را از روی عمد می‌گویید (مورد حساب قرار خواهد داد)؛ و خداوند آمرزنده و رحیم است.'),
(3539, 3539, 'النَّبِيُّ أَوْلَىٰ بِالْمُؤْمِنِينَ مِنْ أَنْفُسِهِمْ ۖ وَأَزْوَاجُهُ أُمَّهَاتُهُمْ ۗ وَأُولُو الْأَرْحَامِ بَعْضُهُمْ أَوْلَىٰ بِبَعْضٍ فِي كِتَابِ اللَّهِ مِنَ الْمُؤْمِنِينَ وَالْمُهَاجِرِينَ إِلَّا أَنْ تَفْعَلُوا إِلَىٰ أَوْلِيَائِكُمْ مَعْرُوفًا ۚ كَانَ ذَٰلِكَ فِي الْكِتَابِ مَسْطُورًا', 33, 6, 21, 84, 418, 'پیامبر نسبت به مؤمنان از خودشان سزاوارتر است؛ و همسران او مادران آنها = مؤمنان‌ محسوب می‌شوند؛ و خویشاوندان نسبت به یکدیگر از مؤمنان و مهاجران در آنچه خدا مقرّر داشته اولی هستند، مگر اینکه بخواهید نسبت به دوستانتان نیکی کنید (و سهمی از اموال خود را به آنها بدهید)؛ این حکم در کتاب (الهی) نوشته شده است.'),
(3540, 3540, 'وَإِذْ أَخَذْنَا مِنَ النَّبِيِّينَ مِيثَاقَهُمْ وَمِنْكَ وَمِنْ نُوحٍ وَإِبْرَاهِيمَ وَمُوسَىٰ وَعِيسَى ابْنِ مَرْيَمَ ۖ وَأَخَذْنَا مِنْهُمْ مِيثَاقًا غَلِيظًا', 33, 7, 21, 84, 419, '(به خاطر آور) هنگامی را که از پیامبران پیمان گرفتیم، و (همچنین) از تو و از نوح و ابراهیم و موسی و عیسی بن مریم، و ما از همه آنان پیمان محکمی گرفتیم (که در ادای مسؤولیت تبلیغ و رسالت کوتاهی نکنند)!'),
(3541, 3541, 'لِيَسْأَلَ الصَّادِقِينَ عَنْ صِدْقِهِمْ ۚ وَأَعَدَّ لِلْكَافِرِينَ عَذَابًا أَلِيمًا', 33, 8, 21, 84, 419, 'به این منظور که خدا راستگویان را از صدقشان (در ایمان و عمل صالح) سؤال کند؛ و برای کافران عذابی دردناک آماده ساخته است.'),
(3542, 3542, 'يَا أَيُّهَا الَّذِينَ آمَنُوا اذْكُرُوا نِعْمَةَ اللَّهِ عَلَيْكُمْ إِذْ جَاءَتْكُمْ جُنُودٌ فَأَرْسَلْنَا عَلَيْهِمْ رِيحًا وَجُنُودًا لَمْ تَرَوْهَا ۚ وَكَانَ اللَّهُ بِمَا تَعْمَلُونَ بَصِيرًا', 33, 9, 21, 84, 419, 'ای کسانی که ایمان آورده‌اید! نعمت خدا را بر خود به یاد آورید در آن هنگام که لشکرهایی (عظیم) به سراغ شما آمدند؛ ولی ما باد و طوفان سختی بر آنان فرستادیم و لشکریانی که آنها را نمی‌دیدید (و به این وسیله آنها را در هم شکستیم)؛ و خداوند همیشه به آنچه انجام می‌دهید بینا بوده است.'),
(3543, 3543, 'إِذْ جَاءُوكُمْ مِنْ فَوْقِكُمْ وَمِنْ أَسْفَلَ مِنْكُمْ وَإِذْ زَاغَتِ الْأَبْصَارُ وَبَلَغَتِ الْقُلُوبُ الْحَنَاجِرَ وَتَظُنُّونَ بِاللَّهِ الظُّنُونَا', 33, 10, 21, 84, 419, '(به خاطر بیاورید) زمانی را که آنها از طرف بالا و پایین (شهر) بر شما وارد شدند (و مدینه را محاصره کردند) و زمانی را که چشمها از شدّت وحشت خیره شده و جانها به لب رسیده بود، و گمانهای گوناگون بدی به خدا می‌بردید.'),
(3544, 3544, 'هُنَالِكَ ابْتُلِيَ الْمُؤْمِنُونَ وَزُلْزِلُوا زِلْزَالًا شَدِيدًا', 33, 11, 21, 84, 419, 'آنجا بود که مؤمنان آزمایش شدند و تکان سختی خوردند!'),
(3545, 3545, 'وَإِذْ يَقُولُ الْمُنَافِقُونَ وَالَّذِينَ فِي قُلُوبِهِمْ مَرَضٌ مَا وَعَدَنَا اللَّهُ وَرَسُولُهُ إِلَّا غُرُورًا', 33, 12, 21, 84, 419, 'و (نیز) به خاطر آورید زمانی را که منافقان و بیماردلان می‌گفتند: «خدا و پیامبرش جز وعده‌های دروغین به ما نداده‌اند!»'),
(3546, 3546, 'وَإِذْ قَالَتْ طَائِفَةٌ مِنْهُمْ يَا أَهْلَ يَثْرِبَ لَا مُقَامَ لَكُمْ فَارْجِعُوا ۚ وَيَسْتَأْذِنُ فَرِيقٌ مِنْهُمُ النَّبِيَّ يَقُولُونَ إِنَّ بُيُوتَنَا عَوْرَةٌ وَمَا هِيَ بِعَوْرَةٍ ۖ إِنْ يُرِيدُونَ إِلَّا فِرَارًا', 33, 13, 21, 84, 419, 'و (نیز) به خاطر آورید زمانی را که گروهی از آنها گفتند: «ای اهل یثرب (ای مردم مدینه)! اینجا جای توقف شما نیست؛ به خانه‌های خود بازگردید!» و گروهی از آنان از پیامبر اجازه بازگشت می‌خواستند و می‌گفتند: «خانه‌های ما بی‌حفاظ است!»، در حالی که بی‌حفاظ نبود؛ آنها فقط می‌خواستند (از جنگ) فرار کنند.'),
(3547, 3547, 'وَلَوْ دُخِلَتْ عَلَيْهِمْ مِنْ أَقْطَارِهَا ثُمَّ سُئِلُوا الْفِتْنَةَ لَآتَوْهَا وَمَا تَلَبَّثُوا بِهَا إِلَّا يَسِيرًا', 33, 14, 21, 84, 419, 'آنها چنان بودند که اگر دشمنان از اطراف مدینه بر آنان وارد می‌شدند و پیشنهاد بازگشت به سوی شرک به آنان می‌کردند می‌پذیرفتند، و جز مدّت کمی (برای انتخاب این راه) درنگ نمی‌کردند!'),
(3548, 3548, 'وَلَقَدْ كَانُوا عَاهَدُوا اللَّهَ مِنْ قَبْلُ لَا يُوَلُّونَ الْأَدْبَارَ ۚ وَكَانَ عَهْدُ اللَّهِ مَسْئُولًا', 33, 15, 21, 84, 419, '(در حالی که) آنان قبل از این با خدا عهد کرده بودند که پشت به دشمن نکنند؛ و عهد الهی مورد سؤال قرار خواهد گرفت (و در برابر آن مسؤولند)!'),
(3549, 3549, 'قُلْ لَنْ يَنْفَعَكُمُ الْفِرَارُ إِنْ فَرَرْتُمْ مِنَ الْمَوْتِ أَوِ الْقَتْلِ وَإِذًا لَا تُمَتَّعُونَ إِلَّا قَلِيلًا', 33, 16, 21, 84, 420, 'بگو: «اگر از مرگ یا کشته‌شدن فرار کنید، سودی به حال شما نخواهد داشت؛ و در آن هنگام جز بهره کمی از زندگانی نخواهید گرفت!»'),
(3550, 3550, 'قُلْ مَنْ ذَا الَّذِي يَعْصِمُكُمْ مِنَ اللَّهِ إِنْ أَرَادَ بِكُمْ سُوءًا أَوْ أَرَادَ بِكُمْ رَحْمَةً ۚ وَلَا يَجِدُونَ لَهُمْ مِنْ دُونِ اللَّهِ وَلِيًّا وَلَا نَصِيرًا', 33, 17, 21, 84, 420, 'بگو: «چه کسی می‌تواند شما را در برابر اراده خدا حفظ کند اگر او بدی یا رحمتی را برای شما اراده کند؟!» و آنها جز خدا هیچ سرپرست و یاوری برای خود نخواهند یافت.'),
(3551, 3551, ' قَدْ يَعْلَمُ اللَّهُ الْمُعَوِّقِينَ مِنْكُمْ وَالْقَائِلِينَ لِإِخْوَانِهِمْ هَلُمَّ إِلَيْنَا ۖ وَلَا يَأْتُونَ الْبَأْسَ إِلَّا قَلِيلًا', 33, 18, 21, 84, 420, 'خداوند کسانی که مردم را از جنگ بازمی‌داشتند و کسانی را که به برادران خود می‌گفتند: «بسوی ما بیایید (و خود را از معرکه بیرون کشید)» بخوبی می‌شناسد؛ و آنها (مردمی ضعیفند و) جز اندکی پیکار نمی‌کنند!'),
(3552, 3552, 'أَشِحَّةً عَلَيْكُمْ ۖ فَإِذَا جَاءَ الْخَوْفُ رَأَيْتَهُمْ يَنْظُرُونَ إِلَيْكَ تَدُورُ أَعْيُنُهُمْ كَالَّذِي يُغْشَىٰ عَلَيْهِ مِنَ الْمَوْتِ ۖ فَإِذَا ذَهَبَ الْخَوْفُ سَلَقُوكُمْ بِأَلْسِنَةٍ حِدَادٍ أَشِحَّةً عَلَى الْخَيْرِ ۚ أُولَٰئِكَ لَمْ يُؤْمِنُوا فَأَحْبَطَ اللَّهُ أَعْمَالَهُمْ ۚ وَكَانَ ذَٰلِكَ عَلَى اللَّهِ يَسِيرًا', 33, 19, 21, 84, 420, 'آنها در همه چیز نسبت به شما بخیلند؛ و هنگامی که (لحظات) ترس (و بحرانی) پیش آید، می‌بینی آنچنان به تو نگاه می‌کنند، و چشمهایشان در حدقه می‌چرخد، که گویی می‌خواهند قالب تهی کنند! امّا وقتی حالت خوف و ترس فرو نشست، زبانهای تند و خشن خود را با انبوهی از خشم و عصبانیت بر شما می‌گشایند (و سهم خود را از غنایم مطالبه می‌کنند!) در حالی که در آن نیز حریص و بخیلند؛ آنها (هرگز) ایمان نیاورده‌اند، از این رو خداوند اعمالشان را حبط و نابود کرد؛ و این کار بر خدا آسان است.'),
(3553, 3553, 'يَحْسَبُونَ الْأَحْزَابَ لَمْ يَذْهَبُوا ۖ وَإِنْ يَأْتِ الْأَحْزَابُ يَوَدُّوا لَوْ أَنَّهُمْ بَادُونَ فِي الْأَعْرَابِ يَسْأَلُونَ عَنْ أَنْبَائِكُمْ ۖ وَلَوْ كَانُوا فِيكُمْ مَا قَاتَلُوا إِلَّا قَلِيلًا', 33, 20, 21, 84, 420, 'آنها گمان می‌کنند هنوز لشکر احزاب نرفته‌اند؛ و اگر برگردند (از ترس آنان) دوست می‌دارند در میان اعراب بادیه‌نشین پراکنده (و پنهان) شوند و از اخبار شما جویا گردند؛ و اگر در میان شما باشند جز اندکی پیکار نمی‌کنند!'),
(3554, 3554, 'لَقَدْ كَانَ لَكُمْ فِي رَسُولِ اللَّهِ أُسْوَةٌ حَسَنَةٌ لِمَنْ كَانَ يَرْجُو اللَّهَ وَالْيَوْمَ الْآخِرَ وَذَكَرَ اللَّهَ كَثِيرًا', 33, 21, 21, 84, 420, 'مسلّماً برای شما در زندگی رسول خدا سرمشق نیکویی بود، برای آنها که امید به رحمت خدا و روز رستاخیز دارند و خدا را بسیار یاد می‌کنند.'),
(3555, 3555, 'وَلَمَّا رَأَى الْمُؤْمِنُونَ الْأَحْزَابَ قَالُوا هَٰذَا مَا وَعَدَنَا اللَّهُ وَرَسُولُهُ وَصَدَقَ اللَّهُ وَرَسُولُهُ ۚ وَمَا زَادَهُمْ إِلَّا إِيمَانًا وَتَسْلِيمًا', 33, 22, 21, 84, 420, '(امّا) مؤمنان وقتی لشکر احزاب را دیدند گفتند: «این همان است که خدا و رسولش به ما وعده داده، و خدا و رسولش راست گفته‌اند!» و این موضوع جز بر ایمان و تسلیم آنان نیفزود.'),
(3556, 3556, 'مِنَ الْمُؤْمِنِينَ رِجَالٌ صَدَقُوا مَا عَاهَدُوا اللَّهَ عَلَيْهِ ۖ فَمِنْهُمْ مَنْ قَضَىٰ نَحْبَهُ وَمِنْهُمْ مَنْ يَنْتَظِرُ ۖ وَمَا بَدَّلُوا تَبْدِيلًا', 33, 23, 21, 84, 421, 'در میان مؤمنان مردانی هستند که بر سر عهدی که با خدا بستند صادقانه ایستاده‌اند؛ بعضی پیمان خود را به آخر بردند (و در راه او شربت شهادت نوشیدند)، و بعضی دیگر در انتظارند؛ و هرگز تغییر و تبدیلی در عهد و پیمان خود ندادند.'),
(3557, 3557, 'لِيَجْزِيَ اللَّهُ الصَّادِقِينَ بِصِدْقِهِمْ وَيُعَذِّبَ الْمُنَافِقِينَ إِنْ شَاءَ أَوْ يَتُوبَ عَلَيْهِمْ ۚ إِنَّ اللَّهَ كَانَ غَفُورًا رَحِيمًا', 33, 24, 21, 84, 421, 'هدف این است که خداوند صادقان را بخاطر صدقشان پاداش دهد، و منافقان را هرگاه اراده کند عذاب نماید یا (اگر توبه کنند) توبه آنها را بپذیرد؛ چرا که خداوند آمرزنده و رحیم است.'),
(3558, 3558, 'وَرَدَّ اللَّهُ الَّذِينَ كَفَرُوا بِغَيْظِهِمْ لَمْ يَنَالُوا خَيْرًا ۚ وَكَفَى اللَّهُ الْمُؤْمِنِينَ الْقِتَالَ ۚ وَكَانَ اللَّهُ قَوِيًّا عَزِيزًا', 33, 25, 21, 84, 421, 'خدا کافران را با دلی پر از خشم بازگرداند بی‌آنکه نتیجه‌ای از کار خود گرفته باشند؛ و خداوند (در این میدان)، مؤمنان را از جنگ بی‌نیاز ساخت (و پیروزی را نصیبشان کرد)؛ و خدا قوّی و شکست‌ناپذیر است!'),
(3559, 3559, 'وَأَنْزَلَ الَّذِينَ ظَاهَرُوهُمْ مِنْ أَهْلِ الْكِتَابِ مِنْ صَيَاصِيهِمْ وَقَذَفَ فِي قُلُوبِهِمُ الرُّعْبَ فَرِيقًا تَقْتُلُونَ وَتَأْسِرُونَ فَرِيقًا', 33, 26, 21, 84, 421, 'و خداوند گروهی از اهل کتاب = یهود را که از آنان = مشرکان عرب‌ حمایت کردند از قلعه‌های محکمشان پایین کشید و در دلهایشان رعب افکند؛ (و کارشان به جایی رسید که) گروهی را به قتل می‌رساندید و گروهی را اسیر می‌کردید!'),
(3560, 3560, 'وَأَوْرَثَكُمْ أَرْضَهُمْ وَدِيَارَهُمْ وَأَمْوَالَهُمْ وَأَرْضًا لَمْ تَطَئُوهَا ۚ وَكَانَ اللَّهُ عَلَىٰ كُلِّ شَيْءٍ قَدِيرًا', 33, 27, 21, 84, 421, 'و زمینها و خانه‌ها و اموالشان را در اختیار شما گذاشت، و (همچنین) زمینی را که هرگز در آن گام ننهاده بودید؛ و خداوند بر هر چیز تواناست!'),
(3561, 3561, 'يَا أَيُّهَا النَّبِيُّ قُلْ لِأَزْوَاجِكَ إِنْ كُنْتُنَّ تُرِدْنَ الْحَيَاةَ الدُّنْيَا وَزِينَتَهَا فَتَعَالَيْنَ أُمَتِّعْكُنَّ وَأُسَرِّحْكُنَّ سَرَاحًا جَمِيلًا', 33, 28, 21, 84, 421, 'ای پیامبر! به همسرانت بگو: «اگر شما زندگی دنیا و زرق و برق آن را می‌خواهید بیایید با هدیه‌ای شما را بهره‌مند سازم و شما را بطرز نیکویی رها سازم!'),
(3562, 3562, 'وَإِنْ كُنْتُنَّ تُرِدْنَ اللَّهَ وَرَسُولَهُ وَالدَّارَ الْآخِرَةَ فَإِنَّ اللَّهَ أَعَدَّ لِلْمُحْسِنَاتِ مِنْكُنَّ أَجْرًا عَظِيمًا', 33, 29, 21, 84, 421, 'و اگر شما خدا و پیامبرش و سرای آخرت را می‌خواهید، خداوند برای نیکوکاران شما پاداش عظیمی آماده ساخته است.»'),
(3563, 3563, 'يَا نِسَاءَ النَّبِيِّ مَنْ يَأْتِ مِنْكُنَّ بِفَاحِشَةٍ مُبَيِّنَةٍ يُضَاعَفْ لَهَا الْعَذَابُ ضِعْفَيْنِ ۚ وَكَانَ ذَٰلِكَ عَلَى اللَّهِ يَسِيرًا', 33, 30, 21, 84, 421, 'ای همسران پیامبر! هر کدام از شما گناه آشکار و فاحشی مرتکب شود، عذاب او دوچندان خواهد بود؛ و این برای خدا آسان است.'),
(3564, 3564, ' وَمَنْ يَقْنُتْ مِنْكُنَّ لِلَّهِ وَرَسُولِهِ وَتَعْمَلْ صَالِحًا نُؤْتِهَا أَجْرَهَا مَرَّتَيْنِ وَأَعْتَدْنَا لَهَا رِزْقًا كَرِيمًا', 33, 31, 22, 85, 422, 'و هر کس از شما برای خدا و پیامبرش خضوع کند و عمل صالح انجام دهد، پاداش او را دو چندان خواهیم ساخت، و روزی پرارزشی برای او آماده کرده‌ایم.'),
(3565, 3565, 'يَا نِسَاءَ النَّبِيِّ لَسْتُنَّ كَأَحَدٍ مِنَ النِّسَاءِ ۚ إِنِ اتَّقَيْتُنَّ فَلَا تَخْضَعْنَ بِالْقَوْلِ فَيَطْمَعَ الَّذِي فِي قَلْبِهِ مَرَضٌ وَقُلْنَ قَوْلًا مَعْرُوفًا', 33, 32, 22, 85, 422, 'ای همسران پیامبر! شما همچون یکی از زنان معمولی نیستید اگر تقوا پیشه کنید؛ پس به گونه‌ای هوس‌انگیز سخن نگویید که بیماردلان در شما طمع کنند، و سخن شایسته بگویید!'),
(3566, 3566, 'وَقَرْنَ فِي بُيُوتِكُنَّ وَلَا تَبَرَّجْنَ تَبَرُّجَ الْجَاهِلِيَّةِ الْأُولَىٰ ۖ وَأَقِمْنَ الصَّلَاةَ وَآتِينَ الزَّكَاةَ وَأَطِعْنَ اللَّهَ وَرَسُولَهُ ۚ إِنَّمَا يُرِيدُ اللَّهُ لِيُذْهِبَ عَنْكُمُ الرِّجْسَ أَهْلَ الْبَيْتِ وَيُطَهِّرَكُمْ تَطْهِيرًا', 33, 33, 22, 85, 422, 'و در خانه‌های خود بمانید، و همچون دوران جاهلیّت نخستین (در میان مردم) ظاهر نشوید، و نماز را برپا دارید، و زکات را بپردازید، و خدا و رسولش را اطاعت کنید؛ خداوند فقط می‌خواهد پلیدی و گناه را از شما اهل بیت دور کند و کاملاً شما را پاک سازد.'),
(3567, 3567, 'وَاذْكُرْنَ مَا يُتْلَىٰ فِي بُيُوتِكُنَّ مِنْ آيَاتِ اللَّهِ وَالْحِكْمَةِ ۚ إِنَّ اللَّهَ كَانَ لَطِيفًا خَبِيرًا', 33, 34, 22, 85, 422, 'آنچه را در خانه‌های شما از آیات خداوند و حکمت و دانش خوانده می‌شود یاد کنید؛ خداوند لطیف و خبیر است!'),
(3568, 3568, 'إِنَّ الْمُسْلِمِينَ وَالْمُسْلِمَاتِ وَالْمُؤْمِنِينَ وَالْمُؤْمِنَاتِ وَالْقَانِتِينَ وَالْقَانِتَاتِ وَالصَّادِقِينَ وَالصَّادِقَاتِ وَالصَّابِرِينَ وَالصَّابِرَاتِ وَالْخَاشِعِينَ وَالْخَاشِعَاتِ وَالْمُتَصَدِّقِينَ وَالْمُتَصَدِّقَاتِ وَالصَّائِمِينَ وَالصَّائِمَاتِ وَالْحَافِظِينَ فُرُوجَهُمْ وَالْحَافِظَاتِ وَالذَّاكِرِينَ اللَّهَ كَثِيرًا وَالذَّاكِرَاتِ أَعَدَّ اللَّهُ لَهُمْ مَغْفِرَةً وَأَجْرًا عَظِيمًا', 33, 35, 22, 85, 422, 'به یقین، مردان مسلمان و زنان مسلمان، مردان با ایمان و زنان با ایمان، مردان مطیع فرمان خدا و زنان مطیع فرمان خدا، مردان راستگو و زنان راستگو، مردان صابر و شکیبا و زنان صابر و شکیبا، مردان با خشوع و زنان با خشوع، مردان انفاق کننده و زنان انفاق کننده، مردان روزه‌دار و زنان روزه‌دار، مردان پاکدامن و زنان پاکدامن و مردانی که بسیار به یاد خدا هستند و زنانی که بسیار یاد خدا می‌کنند، خداوند برای همه آنان مغفرت و پاداش عظیمی فراهم ساخته است.'),
(3569, 3569, 'وَمَا كَانَ لِمُؤْمِنٍ وَلَا مُؤْمِنَةٍ إِذَا قَضَى اللَّهُ وَرَسُولُهُ أَمْرًا أَنْ يَكُونَ لَهُمُ الْخِيَرَةُ مِنْ أَمْرِهِمْ ۗ وَمَنْ يَعْصِ اللَّهَ وَرَسُولَهُ فَقَدْ ضَلَّ ضَلَالًا مُبِينًا', 33, 36, 22, 85, 423, 'هیچ مرد و زن با ایمانی حق ندارد هنگامی که خدا و پیامبرش امری را لازم بدانند، اختیاری (در برابر فرمان خدا) داشته باشد؛ و هر کس نافرمانی خدا و رسولش را کند، به گمراهی آشکاری گرفتار شده است!'),
(3570, 3570, 'وَإِذْ تَقُولُ لِلَّذِي أَنْعَمَ اللَّهُ عَلَيْهِ وَأَنْعَمْتَ عَلَيْهِ أَمْسِكْ عَلَيْكَ زَوْجَكَ وَاتَّقِ اللَّهَ وَتُخْفِي فِي نَفْسِكَ مَا اللَّهُ مُبْدِيهِ وَتَخْشَى النَّاسَ وَاللَّهُ أَحَقُّ أَنْ تَخْشَاهُ ۖ فَلَمَّا قَضَىٰ زَيْدٌ مِنْهَا وَطَرًا زَوَّجْنَاكَهَا لِكَيْ لَا يَكُونَ عَلَى الْمُؤْمِنِينَ حَرَجٌ فِي أَزْوَاجِ أَدْعِيَائِهِمْ إِذَا قَضَوْا مِنْهُنَّ وَطَرًا ۚ وَكَانَ أَمْرُ اللَّهِ مَفْعُولًا', 33, 37, 22, 85, 423, '(به خاطر بیاور) زمانی را که به آن کس که خداوند به او نعمت داده بود و تو نیز به او نعمت داده بودی به فرزند خوانده‌ات «زید» می‌گفتی: «همسرت را نگاه‌دار و از خدا بپرهیز!» (و پیوسته این امر را تکرار می‌کردی)؛ و در دل چیزی را پنهان می‌داشتی که خداوند آن را آشکار می‌کند؛ و از مردم می‌ترسیدی در حالی که خداوند سزاوارتر است که از او بترسی! هنگامی که زید نیازش را از آن زن به سرآورد (و از او جدا شد)، ما او را به همسری تو درآوردیم تا مشکلی برای مؤمنان در ازدواج با همسران پسر خوانده‌هایشان -هنگامی که طلاق گیرند- نباشد؛ و فرمان خدا انجام شدنی است (و سنّت غلط تحریم این زنان باید شکسته شود).'),
(3571, 3571, 'مَا كَانَ عَلَى النَّبِيِّ مِنْ حَرَجٍ فِيمَا فَرَضَ اللَّهُ لَهُ ۖ سُنَّةَ اللَّهِ فِي الَّذِينَ خَلَوْا مِنْ قَبْلُ ۚ وَكَانَ أَمْرُ اللَّهِ قَدَرًا مَقْدُورًا', 33, 38, 22, 85, 423, 'هیچ گونه منعی بر پیامبر در آنچه خدا بر او واجب کرده نیست؛ این سنّت الهی در مورد کسانی که پیش از این بوده‌اند نیز جاری بوده؛ و فرمان خدا روی حساب و برنامه دقیقی است!'),
(3572, 3572, 'الَّذِينَ يُبَلِّغُونَ رِسَالَاتِ اللَّهِ وَيَخْشَوْنَهُ وَلَا يَخْشَوْنَ أَحَدًا إِلَّا اللَّهَ ۗ وَكَفَىٰ بِاللَّهِ حَسِيبًا', 33, 39, 22, 85, 423, '(پیامبران) پیشین کسانی بودند که تبلیغ رسالتهای الهی می‌کردند و (تنها) از او می ترسیدند، و از هیچ کس جز خدا بیم نداشتند؛ و همین بس که خداوند حسابگر (و پاداش‌دهنده اعمال آنها) است!'),
(3573, 3573, 'مَا كَانَ مُحَمَّدٌ أَبَا أَحَدٍ مِنْ رِجَالِكُمْ وَلَٰكِنْ رَسُولَ اللَّهِ وَخَاتَمَ النَّبِيِّينَ ۗ وَكَانَ اللَّهُ بِكُلِّ شَيْءٍ عَلِيمًا', 33, 40, 22, 85, 423, 'محمّد (ص) پدر هیچ یک از مردان شما نبوده و نیست؛ ولی رسول خدا و ختم‌کننده و آخرین پیامبران است؛ و خداوند به همه چیز آگاه است!'),
(3574, 3574, 'يَا أَيُّهَا الَّذِينَ آمَنُوا اذْكُرُوا اللَّهَ ذِكْرًا كَثِيرًا', 33, 41, 22, 85, 423, 'ای کسانی که ایمان آورده‌اید! خدا را بسیار یاد کنید،'),
(3575, 3575, 'وَسَبِّحُوهُ بُكْرَةً وَأَصِيلًا', 33, 42, 22, 85, 423, 'و صبح و شام او را تسبیح گویید!'),
(3576, 3576, 'هُوَ الَّذِي يُصَلِّي عَلَيْكُمْ وَمَلَائِكَتُهُ لِيُخْرِجَكُمْ مِنَ الظُّلُمَاتِ إِلَى النُّورِ ۚ وَكَانَ بِالْمُؤْمِنِينَ رَحِيمًا', 33, 43, 22, 85, 423, 'او کسی است که بر شما درود و رحمت می‌فرستد، و فرشتگان او (نیز) برای شما تقاضای رحمت می‌کنند تا شما را از ظلمات (جهل و شرک گناه) به سوی نور (ایمان و علم و تقوا) رهنمون گردد؛ او نسبت به مؤمنان همواره مهربان بوده است!'),
(3577, 3577, 'تَحِيَّتُهُمْ يَوْمَ يَلْقَوْنَهُ سَلَامٌ ۚ وَأَعَدَّ لَهُمْ أَجْرًا كَرِيمًا', 33, 44, 22, 85, 424, 'تحیّت آنان در روزی که او را دیدار می‌کنند سلام است؛ و برای آنها پاداش پرارزشی فراهم ساخته است.'),
(3578, 3578, 'يَا أَيُّهَا النَّبِيُّ إِنَّا أَرْسَلْنَاكَ شَاهِدًا وَمُبَشِّرًا وَنَذِيرًا', 33, 45, 22, 85, 424, 'ای پیامبر! ما تو را گواه فرستادیم و بشارت‌دهنده و انذارکننده!'),
(3579, 3579, 'وَدَاعِيًا إِلَى اللَّهِ بِإِذْنِهِ وَسِرَاجًا مُنِيرًا', 33, 46, 22, 85, 424, 'و تو را دعوت‌کننده بسوی خدا به فرمان او قرار دادیم، و چراغی روشنی‌بخش!'),
(3580, 3580, 'وَبَشِّرِ الْمُؤْمِنِينَ بِأَنَّ لَهُمْ مِنَ اللَّهِ فَضْلًا كَبِيرًا', 33, 47, 22, 85, 424, 'و مؤمنان را بشارت ده که برای آنان از سوی خدا فضل بزرگی است.'),
(3581, 3581, 'وَلَا تُطِعِ الْكَافِرِينَ وَالْمُنَافِقِينَ وَدَعْ أَذَاهُمْ وَتَوَكَّلْ عَلَى اللَّهِ ۚ وَكَفَىٰ بِاللَّهِ وَكِيلًا', 33, 48, 22, 85, 424, 'و از کافران و منافقان اطاعت مکن، و به آزارهای آنها اعتنا منما، و بر خدا توکّل کن، و همین بس که خدا حامی و مدافع (تو) است!'),
(3582, 3582, 'يَا أَيُّهَا الَّذِينَ آمَنُوا إِذَا نَكَحْتُمُ الْمُؤْمِنَاتِ ثُمَّ طَلَّقْتُمُوهُنَّ مِنْ قَبْلِ أَنْ تَمَسُّوهُنَّ فَمَا لَكُمْ عَلَيْهِنَّ مِنْ عِدَّةٍ تَعْتَدُّونَهَا ۖ فَمَتِّعُوهُنَّ وَسَرِّحُوهُنَّ سَرَاحًا جَمِيلًا', 33, 49, 22, 85, 424, 'ای کسانی که ایمان آورده‌اید! هنگامی که با زنان با ایمان ازدواج کردید و قبل از همبستر شدن طلاق دادید، عدّه‌ای برای شما بر آنها نیست که بخواهید حساب آن را نگاه دارید؛ آنها را با هدیه مناسبی بهره‌مند سازید و بطرز شایسته‌ای رهایشان کنید.'),
(3583, 3583, 'يَا أَيُّهَا النَّبِيُّ إِنَّا أَحْلَلْنَا لَكَ أَزْوَاجَكَ اللَّاتِي آتَيْتَ أُجُورَهُنَّ وَمَا مَلَكَتْ يَمِينُكَ مِمَّا أَفَاءَ اللَّهُ عَلَيْكَ وَبَنَاتِ عَمِّكَ وَبَنَاتِ عَمَّاتِكَ وَبَنَاتِ خَالِكَ وَبَنَاتِ خَالَاتِكَ اللَّاتِي هَاجَرْنَ مَعَكَ وَامْرَأَةً مُؤْمِنَةً إِنْ وَهَبَتْ نَفْسَهَا لِلنَّبِيِّ إِنْ أَرَادَ النَّبِيُّ أَنْ يَسْتَنْكِحَهَا خَالِصَةً لَكَ مِنْ دُونِ الْمُؤْمِنِينَ ۗ قَدْ عَلِمْنَا مَا فَرَضْنَا عَلَيْهِمْ فِي أَزْوَاجِهِمْ وَمَا مَلَكَتْ أَيْمَانُهُمْ لِكَيْلَا يَكُونَ عَلَيْكَ حَرَجٌ ۗ وَكَانَ اللَّهُ غَفُورًا رَحِيمًا', 33, 50, 22, 85, 424, 'ای پیامبر! ما همسران تو را که مهرشان را پرداخته‌ای برای تو حلال کردیم، و همچنین کنیزانی که از طریق غنایمی که خدا به تو بخشیده است مالک شده‌ای و دختران عموی تو، و دختران عمّه‌ها، و دختران دایی تو، و دختران خاله‌ها که با تو مهاجرت کردند (ازدواج با آنها برای تو حلال است) و هرگاه زن با ایمانی خود را به پیامبر ببخشد (و مهری برای خود نخواهد) چنانچه پیامبر بخواهد می‌تواند او را به همسری برگزیند؛ امّا چنین ازدواجی تنها برای تو مجاز است نه دیگر مؤمنان؛ ما می‌دانیم برای آنان در مورد همسرانشان و کنیزانشان چه حکمی مقرّر داشته‌ایم (و مصلحت آنان چه حکمی را ایجاب می‌کند)؛ این بخاطر آن است که مشکلی (در ادای رسالت) بر تو نباشد (و از این راه حامیان فزونتری فراهم سازی)؛ و خداوند آمرزنده و مهربان است!'),
(3584, 3584, ' تُرْجِي مَنْ تَشَاءُ مِنْهُنَّ وَتُؤْوِي إِلَيْكَ مَنْ تَشَاءُ ۖ وَمَنِ ابْتَغَيْتَ مِمَّنْ عَزَلْتَ فَلَا جُنَاحَ عَلَيْكَ ۚ ذَٰلِكَ أَدْنَىٰ أَنْ تَقَرَّ أَعْيُنُهُنَّ وَلَا يَحْزَنَّ وَيَرْضَيْنَ بِمَا آتَيْتَهُنَّ كُلُّهُنَّ ۚ وَاللَّهُ يَعْلَمُ مَا فِي قُلُوبِكُمْ ۚ وَكَانَ اللَّهُ عَلِيمًا حَلِيمًا', 33, 51, 22, 85, 425, '(موعد) هر یک از همسرانت را بخواهی می‌توانی به تأخیر اندازی، و هر کدام را بخواهی نزد خود جای دهی؛ و هرگاه بعضی از آنان را که برکنار ساخته‌ای بخواهی نزد خود جای دهی، گناهی بر تو نیست؛ این حکم الهی برای روشنی چشم آنان، و اینکه غمگین نباشند و به آنچه به آنان می‌دهی همگی راضی شوند نزدیکتر است؛ و خدا آنچه را در قلوب شماست می‌داند، و خداوند دانا و بردبار است (از مصالح بندگان خود با خبر است، و در کیفر آنها عجله نمی‌کند)!'),
(3585, 3585, 'لَا يَحِلُّ لَكَ النِّسَاءُ مِنْ بَعْدُ وَلَا أَنْ تَبَدَّلَ بِهِنَّ مِنْ أَزْوَاجٍ وَلَوْ أَعْجَبَكَ حُسْنُهُنَّ إِلَّا مَا مَلَكَتْ يَمِينُكَ ۗ وَكَانَ اللَّهُ عَلَىٰ كُلِّ شَيْءٍ رَقِيبًا', 33, 52, 22, 85, 425, 'بعد از این دیگر زنی بر تو حلال نیست، و نمی‌توانی همسرانت را به همسران دیگری مبدّل کنی = بعضی را طلاق دهی و همسر دیگری به جای او برگزینی‌ هر چند جمال آنها مورد توجّه تو واقع شود، مگر آنچه که بصورت کنیز در ملک تو درآید! و خداوند ناظر و مراقب هر چیز است (و با این حکم فشار قبایل عرب را در اختیار همسر از آنان، از تو برداشتیم).'),
(3586, 3586, 'يَا أَيُّهَا الَّذِينَ آمَنُوا لَا تَدْخُلُوا بُيُوتَ النَّبِيِّ إِلَّا أَنْ يُؤْذَنَ لَكُمْ إِلَىٰ طَعَامٍ غَيْرَ نَاظِرِينَ إِنَاهُ وَلَٰكِنْ إِذَا دُعِيتُمْ فَادْخُلُوا فَإِذَا طَعِمْتُمْ فَانْتَشِرُوا وَلَا مُسْتَأْنِسِينَ لِحَدِيثٍ ۚ إِنَّ ذَٰلِكُمْ كَانَ يُؤْذِي النَّبِيَّ فَيَسْتَحْيِي مِنْكُمْ ۖ وَاللَّهُ لَا يَسْتَحْيِي مِنَ الْحَقِّ ۚ وَإِذَا سَأَلْتُمُوهُنَّ مَتَاعًا فَاسْأَلُوهُنَّ مِنْ وَرَاءِ حِجَابٍ ۚ ذَٰلِكُمْ أَطْهَرُ لِقُلُوبِكُمْ وَقُلُوبِهِنَّ ۚ وَمَا كَانَ لَكُمْ أَنْ تُؤْذُوا رَسُولَ اللَّهِ وَلَا أَنْ تَنْكِحُوا أَزْوَاجَهُ مِنْ بَعْدِهِ أَبَدًا ۚ إِنَّ ذَٰلِكُمْ كَانَ عِنْدَ اللَّهِ عَظِيمًا', 33, 53, 22, 85, 425, 'ای کسانی که ایمان آورده‌اید! در خانه‌های پیامبر داخل نشوید مگر به شما برای صرف غذا اجازه داده شود، در حالی که (قبل از موعد نیایید و) در انتظار وقت غذا ننشینید؛ امّا هنگامی که دعوت شدید داخل شوید؛ و وقتی غذا خوردید پراکنده شوید، و (بعد از صرف غذا) به بحث و صحبت ننشینید؛ این عمل، پیامبر را ناراحت می‌نماید، ولی از شما شرم می‌کند (و چیزی نمی‌گوید)؛ امّا خداوند از (بیان) حق شرم ندارد! و هنگامی که چیزی از وسایل زندگی را (بعنوان عاریت) از آنان = همسران پیامبر می‌خواهید از پشت پرده بخواهید؛ این کار برای پاکی دلهای شما و آنها بهتر است! و شما حق ندارید رسول خدا را آزار دهید، و نه هرگز همسران او را بعد از او به همسری خود درآورید که این کار نزد خدا بزرگ است!'),
(3587, 3587, 'إِنْ تُبْدُوا شَيْئًا أَوْ تُخْفُوهُ فَإِنَّ اللَّهَ كَانَ بِكُلِّ شَيْءٍ عَلِيمًا', 33, 54, 22, 85, 425, 'اگر چیزی را آشکار کنید یا آن را پنهان دارید، خداوند از همه چیز آگاه است!'),
(3588, 3588, 'لَا جُنَاحَ عَلَيْهِنَّ فِي آبَائِهِنَّ وَلَا أَبْنَائِهِنَّ وَلَا إِخْوَانِهِنَّ وَلَا أَبْنَاءِ إِخْوَانِهِنَّ وَلَا أَبْنَاءِ أَخَوَاتِهِنَّ وَلَا نِسَائِهِنَّ وَلَا مَا مَلَكَتْ أَيْمَانُهُنَّ ۗ وَاتَّقِينَ اللَّهَ ۚ إِنَّ اللَّهَ كَانَ عَلَىٰ كُلِّ شَيْءٍ شَهِيدًا', 33, 55, 22, 85, 426, 'بر آنان = همسران پیامبر گناهی نیست در مورد پدران و فرزندان و برادران و فرزندان برادران و فرزندان خواهران خود و زنان مسلمان و بردگان خویش (که بدون حجاب و پرده با آنها تماس بگیرند)؛ و تقوای الهی را پیشه کنید که خداوند نسبت به هر چیزی شاهد و آگاه است.'),
(3589, 3589, 'إِنَّ اللَّهَ وَمَلَائِكَتَهُ يُصَلُّونَ عَلَى النَّبِيِّ ۚ يَا أَيُّهَا الَّذِينَ آمَنُوا صَلُّوا عَلَيْهِ وَسَلِّمُوا تَسْلِيمًا', 33, 56, 22, 85, 426, 'خدا و فرشتگانش بر پیامبر درود می‌فرستند؛ ای کسانی که ایمان آورده‌اید، بر او درود فرستید و سلام گویید و کاملاً تسلیم (فرمان او) باشید.'),
(3590, 3590, 'إِنَّ الَّذِينَ يُؤْذُونَ اللَّهَ وَرَسُولَهُ لَعَنَهُمُ اللَّهُ فِي الدُّنْيَا وَالْآخِرَةِ وَأَعَدَّ لَهُمْ عَذَابًا مُهِينًا', 33, 57, 22, 85, 426, 'آنها که خدا و پیامبرش را آزار می‌دهند، خداوند آنان را از رحمت خود در دنیا و آخرت دور ساخته، و برای آنها عذاب خوارکننده‌ای آماده کرده است.'),
(3591, 3591, 'وَالَّذِينَ يُؤْذُونَ الْمُؤْمِنِينَ وَالْمُؤْمِنَاتِ بِغَيْرِ مَا اكْتَسَبُوا فَقَدِ احْتَمَلُوا بُهْتَانًا وَإِثْمًا مُبِينًا', 33, 58, 22, 85, 426, 'و آنان که مردان و زنان باایمان را به خاطر کاری که انجام نداده‌اند آزار می‌دهند؛ بار بهتان و گناه آشکاری را به دوش کشیده‌اند.'),
(3592, 3592, 'يَا أَيُّهَا النَّبِيُّ قُلْ لِأَزْوَاجِكَ وَبَنَاتِكَ وَنِسَاءِ الْمُؤْمِنِينَ يُدْنِينَ عَلَيْهِنَّ مِنْ جَلَابِيبِهِنَّ ۚ ذَٰلِكَ أَدْنَىٰ أَنْ يُعْرَفْنَ فَلَا يُؤْذَيْنَ ۗ وَكَانَ اللَّهُ غَفُورًا رَحِيمًا', 33, 59, 22, 85, 426, 'ای پیامبر! به همسران و دخترانت و زنان مؤمنان بگو: «جلبابها = روسری‌های بلند خود را بر خویش فروافکنند، این کار برای اینکه شناخته شوند و مورد آزار قرار نگیرند بهتر است؛ (و اگر تاکنون خطا و کوتاهی از آنها سر زده توبه کنند) خداوند همواره آمرزنده رحیم است.'),
(3593, 3593, ' لَئِنْ لَمْ يَنْتَهِ الْمُنَافِقُونَ وَالَّذِينَ فِي قُلُوبِهِمْ مَرَضٌ وَالْمُرْجِفُونَ فِي الْمَدِينَةِ لَنُغْرِيَنَّكَ بِهِمْ ثُمَّ لَا يُجَاوِرُونَكَ فِيهَا إِلَّا قَلِيلًا', 33, 60, 22, 86, 426, 'اگر منافقان و بیماردلان و آنها که اخبار دروغ و شایعات بی‌اساس در مدینه پخش می کنند دست از کار خود بر ندارند، تو را بر ضدّ آنان می‌شورانیم، سپس جز مدّت کوتاهی نمی‌توانند در کنار تو در این شهر بمانند!'),
(3594, 3594, 'مَلْعُونِينَ ۖ أَيْنَمَا ثُقِفُوا أُخِذُوا وَقُتِّلُوا تَقْتِيلًا', 33, 61, 22, 86, 426, 'و از همه جا طرد می‌شوند، و هر جا یافته شوند گرفته خواهند شد و به سختی به قتل خواهند رسید!'),
(3595, 3595, 'سُنَّةَ اللَّهِ فِي الَّذِينَ خَلَوْا مِنْ قَبْلُ ۖ وَلَنْ تَجِدَ لِسُنَّةِ اللَّهِ تَبْدِيلًا', 33, 62, 22, 86, 426, 'این سنّت خداوند در اقوام پیشین است، و برای سنّت الهی هیچ گونه تغییر نخواهی یافت!'),
(3596, 3596, 'يَسْأَلُكَ النَّاسُ عَنِ السَّاعَةِ ۖ قُلْ إِنَّمَا عِلْمُهَا عِنْدَ اللَّهِ ۚ وَمَا يُدْرِيكَ لَعَلَّ السَّاعَةَ تَكُونُ قَرِيبًا', 33, 63, 22, 86, 427, 'مردم از تو درباره (زمان قیام) قیامت سؤال می‌کنند، بگو: «علم آن تنها نزد خداست!» و چه می‌دانی شاید قیامت نزدیک باشد!'),
(3597, 3597, 'إِنَّ اللَّهَ لَعَنَ الْكَافِرِينَ وَأَعَدَّ لَهُمْ سَعِيرًا', 33, 64, 22, 86, 427, 'خداوند کافران را لعن کرده (و از رحمت خود دور داشته) و برای آنان آتش سوزاننده‌ای آماده نموده است،'),
(3598, 3598, 'خَالِدِينَ فِيهَا أَبَدًا ۖ لَا يَجِدُونَ وَلِيًّا وَلَا نَصِيرًا', 33, 65, 22, 86, 427, 'در حالی که همواره در آن تا ابد می‌مانند، و ولیّ و یاوری نخواهند یافت!'),
(3599, 3599, 'يَوْمَ تُقَلَّبُ وُجُوهُهُمْ فِي النَّارِ يَقُولُونَ يَا لَيْتَنَا أَطَعْنَا اللَّهَ وَأَطَعْنَا الرَّسُولَا', 33, 66, 22, 86, 427, 'در آن روز که صورتهای آنان در آتش (دوزخ) دگرگون خواهد شد (از کار خویش پشیمان می‌شوند و) می‌گویند: «ای کاش خدا و پیامبر را اطاعت کرده بودیم!»'),
(3600, 3600, 'وَقَالُوا رَبَّنَا إِنَّا أَطَعْنَا سَادَتَنَا وَكُبَرَاءَنَا فَأَضَلُّونَا السَّبِيلَا', 33, 67, 22, 86, 427, 'و می‌گویند: «پروردگارا! ما از سران و بزرگان خود اطاعت کردیم و ما را گمراه ساختند!'),
(3601, 3601, 'رَبَّنَا آتِهِمْ ضِعْفَيْنِ مِنَ الْعَذَابِ وَالْعَنْهُمْ لَعْنًا كَبِيرًا', 33, 68, 22, 86, 427, 'پروردگارا! آنان را از عذاب، دو چندان ده و آنها را لعن بزرگی فرما!»'),
(3602, 3602, 'يَا أَيُّهَا الَّذِينَ آمَنُوا لَا تَكُونُوا كَالَّذِينَ آذَوْا مُوسَىٰ فَبَرَّأَهُ اللَّهُ مِمَّا قَالُوا ۚ وَكَانَ عِنْدَ اللَّهِ وَجِيهًا', 33, 69, 22, 86, 427, 'ای کسانی که ایمان آورده‌اید! همانند کسانی نباشید که موسی را آزار دادند؛ و خداوند او را از آنچه در حق او می‌گفتند مبرا ساخت؛ و او نزد خداوند، آبرومند (و گرانقدر) بود!'),
(3603, 3603, 'يَا أَيُّهَا الَّذِينَ آمَنُوا اتَّقُوا اللَّهَ وَقُولُوا قَوْلًا سَدِيدًا', 33, 70, 22, 86, 427, 'ای کسانی که ایمان آورده‌اید! تقوای الهی پیشه کنید و سخن حق بگویید...'),
(3604, 3604, 'يُصْلِحْ لَكُمْ أَعْمَالَكُمْ وَيَغْفِرْ لَكُمْ ذُنُوبَكُمْ ۗ وَمَنْ يُطِعِ اللَّهَ وَرَسُولَهُ فَقَدْ فَازَ فَوْزًا عَظِيمًا', 33, 71, 22, 86, 427, 'تا خدا کارهای شما را اصلاح کند و گناهانتان را بیامرزد؛ و هر کس اطاعت خدا و رسولش کند، به رستگاری (و پیروزی) عظیمی دست یافته است!'),
(3605, 3605, 'إِنَّا عَرَضْنَا الْأَمَانَةَ عَلَى السَّمَاوَاتِ وَالْأَرْضِ وَالْجِبَالِ فَأَبَيْنَ أَنْ يَحْمِلْنَهَا وَأَشْفَقْنَ مِنْهَا وَحَمَلَهَا الْإِنْسَانُ ۖ إِنَّهُ كَانَ ظَلُومًا جَهُولًا', 33, 72, 22, 86, 427, 'ما امانت (تعهّد، تکلیف، و ولایت الهیّه) را بر آسمانها و زمین و کوه‌ها عرضه داشتیم، آنها از حمل آن سر برتافتند، و از آن هراسیدند؛ امّا انسان آن را بر دوش کشید؛ او بسیار ظالم و جاهل بود، (چون قدر این مقام عظیم را نشناخت و به خود ستم کرد)!'),
(3606, 3606, 'لِيُعَذِّبَ اللَّهُ الْمُنَافِقِينَ وَالْمُنَافِقَاتِ وَالْمُشْرِكِينَ وَالْمُشْرِكَاتِ وَيَتُوبَ اللَّهُ عَلَى الْمُؤْمِنِينَ وَالْمُؤْمِنَاتِ ۗ وَكَانَ اللَّهُ غَفُورًا رَحِيمًا', 33, 73, 22, 86, 427, 'هدف این بود که خداوند مردان و زنان منافق و مردان و زنان مشرک را (از مؤمنان جدا سازد و آنان را) عذاب کند، و خدا رحمت خود را بر مردان و زنان باایمان بفرستد؛ خداوند همواره آمرزنده و رحیم است!'),
(3607, 3607, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ الْحَمْدُ لِلَّهِ الَّذِي لَهُ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ وَلَهُ الْحَمْدُ فِي الْآخِرَةِ ۚ وَهُوَ الْحَكِيمُ الْخَبِيرُ', 34, 1, 22, 86, 428, 'حمد (و ستایش) مخصوص خداوندی است که تمام آنچه در آسمانها و زمین است از آن اوست؛ و (نیز) حمد (و سپاس) برای اوست در سرای آخرت؛ و او حکیم و آگاه است.'),
(3608, 3608, 'يَعْلَمُ مَا يَلِجُ فِي الْأَرْضِ وَمَا يَخْرُجُ مِنْهَا وَمَا يَنْزِلُ مِنَ السَّمَاءِ وَمَا يَعْرُجُ فِيهَا ۚ وَهُوَ الرَّحِيمُ الْغَفُورُ', 34, 2, 22, 86, 428, 'آنچه در زمین فرومی‌رود و آنچه را از آن برمی‌آید می‌داند، و (همچنین) آنچه از آسمان نازل می‌شود و آنچه بر آن بالا می‌رود؛ و او مهربان و آمرزنده است.'),
(3609, 3609, 'وَقَالَ الَّذِينَ كَفَرُوا لَا تَأْتِينَا السَّاعَةُ ۖ قُلْ بَلَىٰ وَرَبِّي لَتَأْتِيَنَّكُمْ عَالِمِ الْغَيْبِ ۖ لَا يَعْزُبُ عَنْهُ مِثْقَالُ ذَرَّةٍ فِي السَّمَاوَاتِ وَلَا فِي الْأَرْضِ وَلَا أَصْغَرُ مِنْ ذَٰلِكَ وَلَا أَكْبَرُ إِلَّا فِي كِتَابٍ مُبِينٍ', 34, 3, 22, 86, 428, 'کافران گفتند: «قیامت هرگز به سراغ ما نخواهد آمد!» بگو: «آری به پروردگارم سوگند که به سراغ شما خواهد آمد، خداوندی که از غیب آگاه است و به اندازه سنگینی ذرّه‌ای در آسمانها و زمین از علم او دور نخواهد ماند، و نه کوچکتر از آن و نه بزرگتر، مگر اینکه در کتابی آشکار ثبت است!»'),
(3610, 3610, 'لِيَجْزِيَ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ ۚ أُولَٰئِكَ لَهُمْ مَغْفِرَةٌ وَرِزْقٌ كَرِيمٌ', 34, 4, 22, 86, 428, 'تا کسانی را که ایمان آورده و کارهای شایسته انجام داده‌اند پاداش دهد؛ برای آنان مغفرت و روزی پرارزشی است!'),
(3611, 3611, 'وَالَّذِينَ سَعَوْا فِي آيَاتِنَا مُعَاجِزِينَ أُولَٰئِكَ لَهُمْ عَذَابٌ مِنْ رِجْزٍ أَلِيمٌ', 34, 5, 22, 86, 428, 'و کسانی که سعی در (تکذیب) آیات ما داشتند و گمان کردند می‌توانند از حوزه قدرت ما بگریزند، عذابی بد و دردناک خواهند داشت!'),
(3612, 3612, 'وَيَرَى الَّذِينَ أُوتُوا الْعِلْمَ الَّذِي أُنْزِلَ إِلَيْكَ مِنْ رَبِّكَ هُوَ الْحَقَّ وَيَهْدِي إِلَىٰ صِرَاطِ الْعَزِيزِ الْحَمِيدِ', 34, 6, 22, 86, 428, 'کسانی که به ایشان علم داده شده، آنچه را از سوی پروردگارت بر تو نازل شده حق می دانند و به راه خداوند عزیز و حمید هدایت می‌کند.'),
(3613, 3613, 'وَقَالَ الَّذِينَ كَفَرُوا هَلْ نَدُلُّكُمْ عَلَىٰ رَجُلٍ يُنَبِّئُكُمْ إِذَا مُزِّقْتُمْ كُلَّ مُمَزَّقٍ إِنَّكُمْ لَفِي خَلْقٍ جَدِيدٍ', 34, 7, 22, 86, 428, 'و کافران گفتند: «آیا مردی را به شما نشان دهیم که به شما خبر می‌دهد هنگامی که (مُردید و) سخت از هم متلاشی شدید، (بار دیگر) آفرینش تازه‌ای خواهید یافت؟!'),
(3614, 3614, 'أَفْتَرَىٰ عَلَى اللَّهِ كَذِبًا أَمْ بِهِ جِنَّةٌ ۗ بَلِ الَّذِينَ لَا يُؤْمِنُونَ بِالْآخِرَةِ فِي الْعَذَابِ وَالضَّلَالِ الْبَعِيدِ', 34, 8, 22, 86, 429, 'آیا او بر خدا دروغ بسته یا به نوعی جنون گرفتار است؟!» (چنین نیست)، بلکه کسانی که به آخرت ایمان نمی‌آورند، در عذاب و گمراهی دوری هستند (و نشانه گمراهی آنها همین انکار شدید است).'),
(3615, 3615, 'أَفَلَمْ يَرَوْا إِلَىٰ مَا بَيْنَ أَيْدِيهِمْ وَمَا خَلْفَهُمْ مِنَ السَّمَاءِ وَالْأَرْضِ ۚ إِنْ نَشَأْ نَخْسِفْ بِهِمُ الْأَرْضَ أَوْ نُسْقِطْ عَلَيْهِمْ كِسَفًا مِنَ السَّمَاءِ ۚ إِنَّ فِي ذَٰلِكَ لَآيَةً لِكُلِّ عَبْدٍ مُنِيبٍ', 34, 9, 22, 86, 429, 'آیا به آنچه پیش رو و پشت سر آنان از آسمان و زمین قرار دارد نگاه نکردند (تا به قدرت خدا بر همه چیز واقف شوند)؟! اگر ما بخواهیم آنها را (با یک زمین‌لرزه) در زمین فرومی‌بریم، یا قطعه‌هایی از سنگهای آسمانی را بر آنها فرومی‌ریزیم؛ در این نشانه‌ای است (بر قدرت خدا) برای هر بنده توبه‌کار!'),
(3616, 3616, ' وَلَقَدْ آتَيْنَا دَاوُودَ مِنَّا فَضْلًا ۖ يَا جِبَالُ أَوِّبِي مَعَهُ وَالطَّيْرَ ۖ وَأَلَنَّا لَهُ الْحَدِيدَ', 34, 10, 22, 86, 429, 'و ما به داوود از سوی خود فضیلتی بزرگ بخشیدیم؛ (ما به کوه‌ها و پرندگان گفتیم:) ای کوه‌ها و ای پرندگان! با او هم‌آواز شوید و همراه او تسبیح خدا گویید! و آهن را برای او نرم کردیم.'),
(3617, 3617, 'أَنِ اعْمَلْ سَابِغَاتٍ وَقَدِّرْ فِي السَّرْدِ ۖ وَاعْمَلُوا صَالِحًا ۖ إِنِّي بِمَا تَعْمَلُونَ بَصِيرٌ', 34, 11, 22, 86, 429, '(و به او گفتیم:) زره‌های کامل و فراخ بساز، و حلقه‌ها را به اندازه و متناسب کن! و عمل صالح بجا آورید که من به آنچه انجام میدهید بینا هستم!'),
(3618, 3618, 'وَلِسُلَيْمَانَ الرِّيحَ غُدُوُّهَا شَهْرٌ وَرَوَاحُهَا شَهْرٌ ۖ وَأَسَلْنَا لَهُ عَيْنَ الْقِطْرِ ۖ وَمِنَ الْجِنِّ مَنْ يَعْمَلُ بَيْنَ يَدَيْهِ بِإِذْنِ رَبِّهِ ۖ وَمَنْ يَزِغْ مِنْهُمْ عَنْ أَمْرِنَا نُذِقْهُ مِنْ عَذَابِ السَّعِيرِ', 34, 12, 22, 86, 429, 'و برای سلیمان باد را مسخّر ساختیم که صبحگاهان مسیر یک ماه را می‌پیمود و عصرگاهان مسیر یک ماه را؛ و چشمه مس (مذاب) را برای او روان ساختیم؛ و گروهی از جنّ پیش روی او به اذن پروردگارش کار می‌کردند؛ و هر کدام از آنها که از فرمان ما سرپیچی می‌کرد، او را عذاب آتش سوزان می‌چشاندیم!'),
(3619, 3619, 'يَعْمَلُونَ لَهُ مَا يَشَاءُ مِنْ مَحَارِيبَ وَتَمَاثِيلَ وَجِفَانٍ كَالْجَوَابِ وَقُدُورٍ رَاسِيَاتٍ ۚ اعْمَلُوا آلَ دَاوُودَ شُكْرًا ۚ وَقَلِيلٌ مِنْ عِبَادِيَ الشَّكُورُ', 34, 13, 22, 86, 429, 'آنها هر چه سلیمان می‌خواست برایش درست می‌کردند: معبدها، تمثالها، ظروف بزرگ غذا همانند حوضها، و دیگهای ثابت (که از بزرگی قابل حمل و نقل نبود؛ و به آنان گفتیم:) ای آل داوود! شکر (این همه نعمت را) بجا آورید؛ ولی عده کمی از بندگان من شکرگزارند!'),
(3620, 3620, 'فَلَمَّا قَضَيْنَا عَلَيْهِ الْمَوْتَ مَا دَلَّهُمْ عَلَىٰ مَوْتِهِ إِلَّا دَابَّةُ الْأَرْضِ تَأْكُلُ مِنْسَأَتَهُ ۖ فَلَمَّا خَرَّ تَبَيَّنَتِ الْجِنُّ أَنْ لَوْ كَانُوا يَعْلَمُونَ الْغَيْبَ مَا لَبِثُوا فِي الْعَذَابِ الْمُهِينِ', 34, 14, 22, 86, 429, '(با این همه جلال و شکوه سلیمان) هنگامی که مرگ را بر او مقرّر داشتیم، کسی آنها را از مرگ وی آگاه نساخت مگر جنبنده زمین = موریانه‌ که عصای او را می‌خورد (تا شکست و پیکر سلیمان فرو افتاد)؛ هنگامی که بر زمین افتاد جنّیان فهمیدند که اگر از غیب آگاه بودند در عذاب خوارکننده باقی نمی‌ماندند!'),
(3621, 3621, 'لَقَدْ كَانَ لِسَبَإٍ فِي مَسْكَنِهِمْ آيَةٌ ۖ جَنَّتَانِ عَنْ يَمِينٍ وَشِمَالٍ ۖ كُلُوا مِنْ رِزْقِ رَبِّكُمْ وَاشْكُرُوا لَهُ ۚ بَلْدَةٌ طَيِّبَةٌ وَرَبٌّ غَفُورٌ', 34, 15, 22, 86, 430, 'برای قوم «سبا» در محل سکونتشان نشانه‌ای (از قدرت الهی) بود: دو باغ (بزرگ و گسترده) از راست و چپ (رودخانه عظیم با میوه‌های فراوان؛ و به آنها گفتیم:) از روزی پروردگارتان بخورید و شکر او را بجا آورید؛ شهری است پاک و پاکیزه، و پروردگاری آمرزنده (و مهربان)!'),
(3622, 3622, 'فَأَعْرَضُوا فَأَرْسَلْنَا عَلَيْهِمْ سَيْلَ الْعَرِمِ وَبَدَّلْنَاهُمْ بِجَنَّتَيْهِمْ جَنَّتَيْنِ ذَوَاتَيْ أُكُلٍ خَمْطٍ وَأَثْلٍ وَشَيْءٍ مِنْ سِدْرٍ قَلِيلٍ', 34, 16, 22, 86, 430, 'امّا آنها (از خدا) روی‌گردان شدند، و ما سیل ویرانگر را بر آنان فرستادیم، و دو باغ (پربرکت) شان را به دو باغ (بی‌ارزش) با میوه‌های تلخ و درختان شوره گز و اندکی درخت سدر مبدّل ساختیم!'),
(3623, 3623, 'ذَٰلِكَ جَزَيْنَاهُمْ بِمَا كَفَرُوا ۖ وَهَلْ نُجَازِي إِلَّا الْكَفُورَ', 34, 17, 22, 86, 430, 'این کیفر را بخاطر کفرانشان به آنها دادیم؛ و آیا جز کفران کننده را کیفر می‌دهیم؟!'),
(3624, 3624, 'وَجَعَلْنَا بَيْنَهُمْ وَبَيْنَ الْقُرَى الَّتِي بَارَكْنَا فِيهَا قُرًى ظَاهِرَةً وَقَدَّرْنَا فِيهَا السَّيْرَ ۖ سِيرُوا فِيهَا لَيَالِيَ وَأَيَّامًا آمِنِينَ', 34, 18, 22, 86, 430, 'و میان آنها و شهرهایی که برکت داده بودیم، آبادیهای آشکاری قرار دادیم؛ و سفر در میان آنها را بطور متناسب (با فاصله نزدیک) مقرّر داشتیم؛ (و به آنان گفتیم:) شبها و روزها در این آبادیها با ایمنی (کامل) سفر کنید!'),
(3625, 3625, 'فَقَالُوا رَبَّنَا بَاعِدْ بَيْنَ أَسْفَارِنَا وَظَلَمُوا أَنْفُسَهُمْ فَجَعَلْنَاهُمْ أَحَادِيثَ وَمَزَّقْنَاهُمْ كُلَّ مُمَزَّقٍ ۚ إِنَّ فِي ذَٰلِكَ لَآيَاتٍ لِكُلِّ صَبَّارٍ شَكُورٍ', 34, 19, 22, 86, 430, 'ولی (این ناسپاس مردم) گفتند: «پروردگارا! میان سفرهای ما دوری بیفکن» (تا بینوایان نتوانند دوش به دوش اغنیا سفر کنند! و به این طریق) آنها به خویشتن ستم کردند! و ما آنان را داستانهایی (برای عبرت دیگران) قرار دادیم و جمعیّتشان را متلاشی ساختیم؛ در این ماجرا، نشانه‌های عبرتی برای هر صابر شکرگزار است.'),
(3626, 3626, 'وَلَقَدْ صَدَّقَ عَلَيْهِمْ إِبْلِيسُ ظَنَّهُ فَاتَّبَعُوهُ إِلَّا فَرِيقًا مِنَ الْمُؤْمِنِينَ', 34, 20, 22, 86, 430, '(آری) بیقین، ابلیس گمان خود را درباره آنها محقّق یافت که همگی از او پیروی کردند جز گروه اندکی از مؤمنان!'),
(3627, 3627, 'وَمَا كَانَ لَهُ عَلَيْهِمْ مِنْ سُلْطَانٍ إِلَّا لِنَعْلَمَ مَنْ يُؤْمِنُ بِالْآخِرَةِ مِمَّنْ هُوَ مِنْهَا فِي شَكٍّ ۗ وَرَبُّكَ عَلَىٰ كُلِّ شَيْءٍ حَفِيظٌ', 34, 21, 22, 86, 430, 'او سلطه بر آنان نداشت جز برای اینکه مؤمنان به آخرت را از آنها که در شکّ هستند باز شناسیم؛ و پروردگار تو، نگاهبان همه چیز است!'),
(3628, 3628, 'قُلِ ادْعُوا الَّذِينَ زَعَمْتُمْ مِنْ دُونِ اللَّهِ ۖ لَا يَمْلِكُونَ مِثْقَالَ ذَرَّةٍ فِي السَّمَاوَاتِ وَلَا فِي الْأَرْضِ وَمَا لَهُمْ فِيهِمَا مِنْ شِرْكٍ وَمَا لَهُ مِنْهُمْ مِنْ ظَهِيرٍ', 34, 22, 22, 86, 430, 'بگو: «کسانی را که غیر از خدا (معبود خود) می‌پندارید بخوانید! (آنها هرگز گرهی از کار شما نمی‌گشایند، چرا که) آنها به اندازه ذرّه‌ای در آسمانها و زمین مالک نیستند، و نه در (خلقت و مالکیّت) آنها شریکند، و نه یاور او (در آفرینش) بودند.'),
(3629, 3629, 'وَلَا تَنْفَعُ الشَّفَاعَةُ عِنْدَهُ إِلَّا لِمَنْ أَذِنَ لَهُ ۚ حَتَّىٰ إِذَا فُزِّعَ عَنْ قُلُوبِهِمْ قَالُوا مَاذَا قَالَ رَبُّكُمْ ۖ قَالُوا الْحَقَّ ۖ وَهُوَ الْعَلِيُّ الْكَبِيرُ', 34, 23, 22, 86, 431, 'هیچ شفاعتی نزد او، جز برای کسانی که اذن داده، سودی ندارد! (در آن روز همه در اضطرابند) تا زمانی که اضطراب از دلهای آنان زایل گردد (و فرمان از ناحیه او صادر شود؛ در این هنگام مجرمان به شفیعان) می‌گویند: «پروردگارتان چه دستوری داده؟» می‌گویند: «حقّ را (بیان کرد و اجازه شفاعت درباره مستحقّان داد)؛ و اوست بلندمقام و بزرگ‌مرتبه!»'),
(3630, 3630, ' قُلْ مَنْ يَرْزُقُكُمْ مِنَ السَّمَاوَاتِ وَالْأَرْضِ ۖ قُلِ اللَّهُ ۖ وَإِنَّا أَوْ إِيَّاكُمْ لَعَلَىٰ هُدًى أَوْ فِي ضَلَالٍ مُبِينٍ', 34, 24, 22, 87, 431, 'بگو: «چه کسی شما را از آسمانها و زمین روزی می‌دهد؟» بگو: «اللّه! و ما یا شما بر (طریق) هدایت یا در ضلالت آشکاری هستیم!»'),
(3631, 3631, 'قُلْ لَا تُسْأَلُونَ عَمَّا أَجْرَمْنَا وَلَا نُسْأَلُ عَمَّا تَعْمَلُونَ', 34, 25, 22, 87, 431, 'بگو: «شما از گناهی که ما کرده‌ایم سؤال نخواهید شد، (همان گونه که) ما در برابر اعمال شما مسؤول نیستیم!»'),
(3632, 3632, 'قُلْ يَجْمَعُ بَيْنَنَا رَبُّنَا ثُمَّ يَفْتَحُ بَيْنَنَا بِالْحَقِّ وَهُوَ الْفَتَّاحُ الْعَلِيمُ', 34, 26, 22, 87, 431, 'بگو: «پروردگار ما همه ما را جمع می‌کند، سپس در میان ما بحق داوری می‌نماید (و صفوف مجرمان را از نیکوکاران جدا می‌سازد)، و اوست داور (و جداکننده) آگاه!»'),
(3633, 3633, 'قُلْ أَرُونِيَ الَّذِينَ أَلْحَقْتُمْ بِهِ شُرَكَاءَ ۖ كَلَّا ۚ بَلْ هُوَ اللَّهُ الْعَزِيزُ الْحَكِيمُ', 34, 27, 22, 87, 431, 'بگو: «کسانی را که بعنوان شریک به او ملحق ساخته‌اید به من نشان دهید! هرگز چنین نیست! (او شریک و شبیهی ندارد)، بلکه او خداوند عزیز و حکیم است!'),
(3634, 3634, 'وَمَا أَرْسَلْنَاكَ إِلَّا كَافَّةً لِلنَّاسِ بَشِيرًا وَنَذِيرًا وَلَٰكِنَّ أَكْثَرَ النَّاسِ لَا يَعْلَمُونَ', 34, 28, 22, 87, 431, 'و ما تو را جز برای همه مردم نفرستادیم تا (آنها را به پاداشهای الهی) بشارت دهی و (از عذاب او) بترسانی؛ ولی بیشتر مردم نمی‌دانند!'),
(3635, 3635, 'وَيَقُولُونَ مَتَىٰ هَٰذَا الْوَعْدُ إِنْ كُنْتُمْ صَادِقِينَ', 34, 29, 22, 87, 431, 'می‌گویند: «اگر راست می‌گویید، این وعده (رستاخیز) کی خواهد بود؟!»'),
(3636, 3636, 'قُلْ لَكُمْ مِيعَادُ يَوْمٍ لَا تَسْتَأْخِرُونَ عَنْهُ سَاعَةً وَلَا تَسْتَقْدِمُونَ', 34, 30, 22, 87, 431, 'بگو: «وعده شما روزی خواهد بود که نه ساعتی از آن تأخیر می‌کنید و نه (بر آن) پیشی خواهید گرفت!»'),
(3637, 3637, 'وَقَالَ الَّذِينَ كَفَرُوا لَنْ نُؤْمِنَ بِهَٰذَا الْقُرْآنِ وَلَا بِالَّذِي بَيْنَ يَدَيْهِ ۗ وَلَوْ تَرَىٰ إِذِ الظَّالِمُونَ مَوْقُوفُونَ عِنْدَ رَبِّهِمْ يَرْجِعُ بَعْضُهُمْ إِلَىٰ بَعْضٍ الْقَوْلَ يَقُولُ الَّذِينَ اسْتُضْعِفُوا لِلَّذِينَ اسْتَكْبَرُوا لَوْلَا أَنْتُمْ لَكُنَّا مُؤْمِنِينَ', 34, 31, 22, 87, 431, 'کافران گفتند: «ما هرگز به این قرآن و کتابهای دیگری که پیش از آن بوده ایمان نخواهیم آورد!» اگر ببینی هنگامی که این ستمگران در پیشگاه پروردگارشان (برای حساب و جزا) نگه داشته شده‌اند در حالی که هر کدام گناه خود را به گردن دیگری می‌اندازد (از وضع آنها تعجّب می‌کنی)! مستضعفان به مستکبران می‌گویند: «اگر شما نبودید ما مؤمن بودیم!»'),
(3638, 3638, 'قَالَ الَّذِينَ اسْتَكْبَرُوا لِلَّذِينَ اسْتُضْعِفُوا أَنَحْنُ صَدَدْنَاكُمْ عَنِ الْهُدَىٰ بَعْدَ إِذْ جَاءَكُمْ ۖ بَلْ كُنْتُمْ مُجْرِمِينَ', 34, 32, 22, 87, 432, '(امّا) مستکبران به مستضعفان پاسخ می‌دهند: «آیا ما شما را از هدایت بازداشتیم بعد از آنکه به سراغ شما آمد (و آن را بخوبی دریافتید)؟! بلکه شما خود مجرم بودید!»'),
(3639, 3639, 'وَقَالَ الَّذِينَ اسْتُضْعِفُوا لِلَّذِينَ اسْتَكْبَرُوا بَلْ مَكْرُ اللَّيْلِ وَالنَّهَارِ إِذْ تَأْمُرُونَنَا أَنْ نَكْفُرَ بِاللَّهِ وَنَجْعَلَ لَهُ أَنْدَادًا ۚ وَأَسَرُّوا النَّدَامَةَ لَمَّا رَأَوُا الْعَذَابَ وَجَعَلْنَا الْأَغْلَالَ فِي أَعْنَاقِ الَّذِينَ كَفَرُوا ۚ هَلْ يُجْزَوْنَ إِلَّا مَا كَانُوا يَعْمَلُونَ', 34, 33, 22, 87, 432, 'و مستضعفان به مستکبران می‌گویند: «وسوسه‌های فریبکارانه شما در شب و روز (مایه گمراهی ما شد)، هنگامی که به ما دستور می‌دادید که به خداوند کافر شویم و همتایانی برای او قرار دهیم!» و آنان هنگامی که عذاب (الهی) را می‌بینند پشیمانی خود را پنهان می‌کنند (تا بیشتر رسوا نشوند)! و ما غل و زنجیرها در گردن کافران می نهیم؛ آیا جز آنچه عمل می‌کردند به آنها جزا داده می‌شود؟!'),
(3640, 3640, 'وَمَا أَرْسَلْنَا فِي قَرْيَةٍ مِنْ نَذِيرٍ إِلَّا قَالَ مُتْرَفُوهَا إِنَّا بِمَا أُرْسِلْتُمْ بِهِ كَافِرُونَ', 34, 34, 22, 87, 432, 'و ما در هیچ شهر و دیاری پیامبری بیم‌دهنده نفرستادیم مگر اینکه مترفین آنها (که مست ناز و نعمت بودند) گفتند: «ما به آنچه فرستاده شده‌اید کافریم!»'),
(3641, 3641, 'وَقَالُوا نَحْنُ أَكْثَرُ أَمْوَالًا وَأَوْلَادًا وَمَا نَحْنُ بِمُعَذَّبِينَ', 34, 35, 22, 87, 432, 'و گفتند: «اموال و اولاد ما (از همه) بیشتر است (و این نشانه علاقه خدا به ماست!)؛ و ما هرگز مجازات نخواهیم شد!»'),
(3642, 3642, 'قُلْ إِنَّ رَبِّي يَبْسُطُ الرِّزْقَ لِمَنْ يَشَاءُ وَيَقْدِرُ وَلَٰكِنَّ أَكْثَرَ النَّاسِ لَا يَعْلَمُونَ', 34, 36, 22, 87, 432, 'بگو: «پروردگار من روزی را برای هر کس بخواهد وسیع یا تنگ می‌کند، (این ربطی به قرب در درگاه او ندارد)؛ ولی بیشتر مردم نمی‌دانند!'),
(3643, 3643, 'وَمَا أَمْوَالُكُمْ وَلَا أَوْلَادُكُمْ بِالَّتِي تُقَرِّبُكُمْ عِنْدَنَا زُلْفَىٰ إِلَّا مَنْ آمَنَ وَعَمِلَ صَالِحًا فَأُولَٰئِكَ لَهُمْ جَزَاءُ الضِّعْفِ بِمَا عَمِلُوا وَهُمْ فِي الْغُرُفَاتِ آمِنُونَ', 34, 37, 22, 87, 432, 'اموال و فرزندانتان هرگز شما را نزد ما مقرّب نمی‌سازد، جز کسانی که ایمان بیاورند و عمل صالحی انجام دهند که برای آنان پاداش مضاعف در برابر کارهایی است که انجام داده‌اند؛ و آنها در غرفه‌های (بهشتی) در (نهایت) امنیّت خواهند بود!'),
(3644, 3644, 'وَالَّذِينَ يَسْعَوْنَ فِي آيَاتِنَا مُعَاجِزِينَ أُولَٰئِكَ فِي الْعَذَابِ مُحْضَرُونَ', 34, 38, 22, 87, 432, 'و کسانی که برای انکار و ابطال آیات ما تلاش می‌کنند و می‌پندارند از چنگ قدرت ما فرار خواهند کرد، در عذاب (الهی) احضار می‌شوند!'),
(3645, 3645, 'قُلْ إِنَّ رَبِّي يَبْسُطُ الرِّزْقَ لِمَنْ يَشَاءُ مِنْ عِبَادِهِ وَيَقْدِرُ لَهُ ۚ وَمَا أَنْفَقْتُمْ مِنْ شَيْءٍ فَهُوَ يُخْلِفُهُ ۖ وَهُوَ خَيْرُ الرَّازِقِينَ', 34, 39, 22, 87, 432, 'بگو: «پروردگارم روزی را برای هر کس بخواهد وسعت می‌بخشد، و برای هر کس بخواهد تنگ (و محدود) می‌سازد؛ و هر چیزی را (در راه او) انفاق کنید، عوض آن را می‌دهد (و جای آن را پر می‌کند)؛ و او بهترین روزی‌دهندگان است!»'),
(3646, 3646, 'وَيَوْمَ يَحْشُرُهُمْ جَمِيعًا ثُمَّ يَقُولُ لِلْمَلَائِكَةِ أَهَٰؤُلَاءِ إِيَّاكُمْ كَانُوا يَعْبُدُونَ', 34, 40, 22, 87, 433, '(به خاطر بیاور) روزی را که خداوند همه آنان را بر می‌انگیزد، سپس به فرشتگان می‌گوید: «آیا اینها شما را پرستش می‌کردند؟!»'),
(3647, 3647, 'قَالُوا سُبْحَانَكَ أَنْتَ وَلِيُّنَا مِنْ دُونِهِمْ ۖ بَلْ كَانُوا يَعْبُدُونَ الْجِنَّ ۖ أَكْثَرُهُمْ بِهِمْ مُؤْمِنُونَ', 34, 41, 22, 87, 433, 'آنها می‌گویند: «منزّهی (از اینکه همتایی داشته باشی)! تنها تو ولیّ مائی، نه آنها؛ (آنها ما را پرستش نمی‌کردند) بلکه جنّ را پرستش می‌نمودند؛ و اکثرشان به آنها ایمان داشتند!»'),
(3648, 3648, 'فَالْيَوْمَ لَا يَمْلِكُ بَعْضُكُمْ لِبَعْضٍ نَفْعًا وَلَا ضَرًّا وَنَقُولُ لِلَّذِينَ ظَلَمُوا ذُوقُوا عَذَابَ النَّارِ الَّتِي كُنْتُمْ بِهَا تُكَذِّبُونَ', 34, 42, 22, 87, 433, '(آری) امروز هیچ یک از شما نسبت به دیگری مالک سود و زیانی نیست! و به ظالمان می‌گوییم: «بچشید عذاب آتشی را که تکذیب می‌کردید!»'),
(3649, 3649, 'وَإِذَا تُتْلَىٰ عَلَيْهِمْ آيَاتُنَا بَيِّنَاتٍ قَالُوا مَا هَٰذَا إِلَّا رَجُلٌ يُرِيدُ أَنْ يَصُدَّكُمْ عَمَّا كَانَ يَعْبُدُ آبَاؤُكُمْ وَقَالُوا مَا هَٰذَا إِلَّا إِفْكٌ مُفْتَرًى ۚ وَقَالَ الَّذِينَ كَفَرُوا لِلْحَقِّ لَمَّا جَاءَهُمْ إِنْ هَٰذَا إِلَّا سِحْرٌ مُبِينٌ', 34, 43, 22, 87, 433, 'و هنگامی که آیات روشنگر ما بر آنان خوانده می‌شود، می‌گویند: «او فقط مردی است که می‌خواهد شما را از آنچه پدرانتان می‌پرستیدند بازدارد!» و می‌گویند: «این جز دروغ بزرگی که (به خدا) بسته شده چیز دیگری نیست!» و کافران هنگامی که حق به سراغشان آمد گفتند: «این، جز افسونی آشکار نیست!»'),
(3650, 3650, 'وَمَا آتَيْنَاهُمْ مِنْ كُتُبٍ يَدْرُسُونَهَا ۖ وَمَا أَرْسَلْنَا إِلَيْهِمْ قَبْلَكَ مِنْ نَذِيرٍ', 34, 44, 22, 87, 433, 'ما (قبلا) ً چیزی از کتابهای آسمانی را به آنان نداده‌ایم که آن را بخوانند (و به اتکّای آن سخنان تو را تکذیب کنند)، و پیش از تو هیچ بیم‌دهنده = پیامبری برای آنان نفرستادیم!'),
(3651, 3651, 'وَكَذَّبَ الَّذِينَ مِنْ قَبْلِهِمْ وَمَا بَلَغُوا مِعْشَارَ مَا آتَيْنَاهُمْ فَكَذَّبُوا رُسُلِي ۖ فَكَيْفَ كَانَ نَكِيرِ', 34, 45, 22, 87, 433, 'کسانی که پیش از آنان بودند (نیز آیات الهی را) تکذیب کردند، در حالی که اینها به یک دهم آنچه به آنان دادیم نمی‌رسند! (آری) آنها رسولان مرا تکذیب کردند؛ پس ببین مجازات من (نسبت به آنها) چگونه بود!'),
(3652, 3652, ' قُلْ إِنَّمَا أَعِظُكُمْ بِوَاحِدَةٍ ۖ أَنْ تَقُومُوا لِلَّهِ مَثْنَىٰ وَفُرَادَىٰ ثُمَّ تَتَفَكَّرُوا ۚ مَا بِصَاحِبِكُمْ مِنْ جِنَّةٍ ۚ إِنْ هُوَ إِلَّا نَذِيرٌ لَكُمْ بَيْنَ يَدَيْ عَذَابٍ شَدِيدٍ', 34, 46, 22, 87, 433, 'بگو: «شما را تنها به یک چیز اندرز می‌دهم، و آن اینکه: دو نفر دو نفر یا یک نفر یک نفر برای خدا قیام کنید، سپس بیندیشید این دوست و همنشین شما = محمّد هیچ گونه جنونی ندارد؛ او فقط بیم‌دهنده شما در برابر عذاب شدید (الهی) است!»'),
(3653, 3653, 'قُلْ مَا سَأَلْتُكُمْ مِنْ أَجْرٍ فَهُوَ لَكُمْ ۖ إِنْ أَجْرِيَ إِلَّا عَلَى اللَّهِ ۖ وَهُوَ عَلَىٰ كُلِّ شَيْءٍ شَهِيدٌ', 34, 47, 22, 87, 433, 'بگو: «هر اجر و پاداشی از شما خواسته‌ام برای خود شماست؛ اجر من تنها بر خداوند است، و او بر همه چیز گواه است!»'),
(3654, 3654, 'قُلْ إِنَّ رَبِّي يَقْذِفُ بِالْحَقِّ عَلَّامُ الْغُيُوبِ', 34, 48, 22, 87, 433, 'بگو: «پروردگار من حق را (بر دل پیامبران خود) می‌افکند، که او دانای غیبها (و اسرار نهان) است.»'),
(3655, 3655, 'قُلْ جَاءَ الْحَقُّ وَمَا يُبْدِئُ الْبَاطِلُ وَمَا يُعِيدُ', 34, 49, 22, 87, 434, 'بگو: «حق آمد! و باطل (کاری از آن ساخته نیست و) نمی‌تواند آغازگر چیزی باشد و نه تجدیدکننده آن!»'),
(3656, 3656, 'قُلْ إِنْ ضَلَلْتُ فَإِنَّمَا أَضِلُّ عَلَىٰ نَفْسِي ۖ وَإِنِ اهْتَدَيْتُ فَبِمَا يُوحِي إِلَيَّ رَبِّي ۚ إِنَّهُ سَمِيعٌ قَرِيبٌ', 34, 50, 22, 87, 434, 'بگو: «اگر من گمراه شوم، از ناحیه خود گمراه می‌شوم؛ و اگر هدایت یابم، به وسیله آنچه پروردگارم به من وحی می‌کند هدایت می‌یابم؛ او شنوای نزدیک است!»'),
(3657, 3657, 'وَلَوْ تَرَىٰ إِذْ فَزِعُوا فَلَا فَوْتَ وَأُخِذُوا مِنْ مَكَانٍ قَرِيبٍ', 34, 51, 22, 87, 434, 'اگر ببینی هنگامی که فریادشان بلند می‌شود امّا نمی‌توانند (از عذاب الهی) بگریزند، و آنها را از جای نزدیکی (که حتّی انتظارش را ندارند) می‌گیرند (از درماندگی آنها تعجّب خواهی کرد)!'),
(3658, 3658, 'وَقَالُوا آمَنَّا بِهِ وَأَنَّىٰ لَهُمُ التَّنَاوُشُ مِنْ مَكَانٍ بَعِيدٍ', 34, 52, 22, 87, 434, 'و (در آن حال) می‌گویند: «به حقّ ایمان آوردیم!»، ولی چگونه می‌توانند از فاصله دور به آن دسترسی پیدا کنند!'),
(3659, 3659, 'وَقَدْ كَفَرُوا بِهِ مِنْ قَبْلُ ۖ وَيَقْذِفُونَ بِالْغَيْبِ مِنْ مَكَانٍ بَعِيدٍ', 34, 53, 22, 87, 434, 'آنها پیش از این (که در نهایت آزادی بودند) به آن کافر شدند و دورا دور، و غائبانه (و بدون آگاهی). نسبت‌های ناروا می‌دادند.'),
(3660, 3660, 'وَحِيلَ بَيْنَهُمْ وَبَيْنَ مَا يَشْتَهُونَ كَمَا فُعِلَ بِأَشْيَاعِهِمْ مِنْ قَبْلُ ۚ إِنَّهُمْ كَانُوا فِي شَكٍّ مُرِيبٍ', 34, 54, 22, 87, 434, '(سرانجام) میان آنها و خواسته‌هایشان جدایی افکنده شد، همان گونه که با پیروان (و هم‌مسلکان) آنها از قبل عمل شد، چرا که آنها در شک و تردید بودند!'),
(3661, 3661, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ الْحَمْدُ لِلَّهِ فَاطِرِ السَّمَاوَاتِ وَالْأَرْضِ جَاعِلِ الْمَلَائِكَةِ رُسُلًا أُولِي أَجْنِحَةٍ مَثْنَىٰ وَثُلَاثَ وَرُبَاعَ ۚ يَزِيدُ فِي الْخَلْقِ مَا يَشَاءُ ۚ إِنَّ اللَّهَ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ', 35, 1, 22, 87, 434, 'ستایش مخصوص خداوندی است آفریننده آسمانها و زمین، که فرشتگان را رسولانی قرار داد دارای بالهای دوگانه و سه‌گانه و چهارگانه، او هر چه بخواهد در آفرینش می‌افزاید، و او بر هر چیزی تواناست!'),
(3662, 3662, 'مَا يَفْتَحِ اللَّهُ لِلنَّاسِ مِنْ رَحْمَةٍ فَلَا مُمْسِكَ لَهَا ۖ وَمَا يُمْسِكْ فَلَا مُرْسِلَ لَهُ مِنْ بَعْدِهِ ۚ وَهُوَ الْعَزِيزُ الْحَكِيمُ', 35, 2, 22, 87, 434, 'هر رحمتی را خدا به روی مردم بگشاید، کسی نمی‌تواند جلو آن را بگیرد؛ و هر چه را امساک کند، کسی غیر از او قادر به فرستادن آن نیست؛ و او عزیز و حکیم است!'),
(3663, 3663, 'يَا أَيُّهَا النَّاسُ اذْكُرُوا نِعْمَتَ اللَّهِ عَلَيْكُمْ ۚ هَلْ مِنْ خَالِقٍ غَيْرُ اللَّهِ يَرْزُقُكُمْ مِنَ السَّمَاءِ وَالْأَرْضِ ۚ لَا إِلَٰهَ إِلَّا هُوَ ۖ فَأَنَّىٰ تُؤْفَكُونَ', 35, 3, 22, 87, 434, 'ای مردم! به یاد آورید نعمت خدا را بر شما؛ آیا آفریننده‌ای جز خدا هست که شما را از آسمان و زمین روزی دهد؟! هیچ معبودی جز او نیست؛ با این حال چگونه به سوی باطل منحرف می‌شوید؟!'),
(3664, 3664, 'وَإِنْ يُكَذِّبُوكَ فَقَدْ كُذِّبَتْ رُسُلٌ مِنْ قَبْلِكَ ۚ وَإِلَى اللَّهِ تُرْجَعُ الْأُمُورُ', 35, 4, 22, 87, 435, 'اگر تو را تکذیب کنند (غم مخور، موضوع تازه‌ای نیست) پیامبران پیش از تو نیز تکذیب شدند؛ و همه کارها بسوی خدا بازمی‌گردد.'),
(3665, 3665, 'يَا أَيُّهَا النَّاسُ إِنَّ وَعْدَ اللَّهِ حَقٌّ ۖ فَلَا تَغُرَّنَّكُمُ الْحَيَاةُ الدُّنْيَا ۖ وَلَا يَغُرَّنَّكُمْ بِاللَّهِ الْغَرُورُ', 35, 5, 22, 87, 435, 'ای مردم! وعده خداوند حقّ است؛ مبادا زندگی دنیا شما را بفریبد، و مبادا شیطان شما را فریب دهد و به (کرم) خدا مغرور سازد!');
INSERT INTO `Quran` (`rowid`, `id`, `Arabic`, `Chapter`, `Verse`, `Juz`, `Hizb`, `Page`, `tr`) VALUES
(3666, 3666, 'إِنَّ الشَّيْطَانَ لَكُمْ عَدُوٌّ فَاتَّخِذُوهُ عَدُوًّا ۚ إِنَّمَا يَدْعُو حِزْبَهُ لِيَكُونُوا مِنْ أَصْحَابِ السَّعِيرِ', 35, 6, 22, 87, 435, 'البتّه شیطان دشمن شماست، پس او را دشمن بدانید؛ او فقط حزبش را به این دعوت می‌کند که اهل آتش سوزان (جهنّم) باشند!'),
(3667, 3667, 'الَّذِينَ كَفَرُوا لَهُمْ عَذَابٌ شَدِيدٌ ۖ وَالَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ لَهُمْ مَغْفِرَةٌ وَأَجْرٌ كَبِيرٌ', 35, 7, 22, 87, 435, 'کسانی که راه کفر پیش گرفتند، برای آنان عذابی سخت است؛ و کسانی که ایمان آوردند و کارهای شایسته انجام دادند، آمرزش و پاداش بزرگ از آن آنهاست!'),
(3668, 3668, 'أَفَمَنْ زُيِّنَ لَهُ سُوءُ عَمَلِهِ فَرَآهُ حَسَنًا ۖ فَإِنَّ اللَّهَ يُضِلُّ مَنْ يَشَاءُ وَيَهْدِي مَنْ يَشَاءُ ۖ فَلَا تَذْهَبْ نَفْسُكَ عَلَيْهِمْ حَسَرَاتٍ ۚ إِنَّ اللَّهَ عَلِيمٌ بِمَا يَصْنَعُونَ', 35, 8, 22, 87, 435, 'آیا کسی که عمل بدش برای او آراسته شده و آن را خوب و زیبا می‌بیند (همانند کسی است که واقع را آنچنان که هست می‌یابد)؟! خداوند هر کس را بخواهد گمراه می سازد و هر کس را بخواهد هدایت می‌کند؛ پس جانت به خاطر شدّت تأسف بر آنان از دست نرود؛ خداوند به آنچه انجام می‌دهند داناست!'),
(3669, 3669, 'وَاللَّهُ الَّذِي أَرْسَلَ الرِّيَاحَ فَتُثِيرُ سَحَابًا فَسُقْنَاهُ إِلَىٰ بَلَدٍ مَيِّتٍ فَأَحْيَيْنَا بِهِ الْأَرْضَ بَعْدَ مَوْتِهَا ۚ كَذَٰلِكَ النُّشُورُ', 35, 9, 22, 87, 435, 'خداوند کسی است که بادها را فرستاد تا ابرهایی را به حرکت درآورند؛ سپس ما این ابرها را به سوی زمین مرده‌ای راندیم و به وسیله آن، زمین را پس از مردنش زنده می‌کنیم؛ رستاخیز نیز همین گونه است!'),
(3670, 3670, 'مَنْ كَانَ يُرِيدُ الْعِزَّةَ فَلِلَّهِ الْعِزَّةُ جَمِيعًا ۚ إِلَيْهِ يَصْعَدُ الْكَلِمُ الطَّيِّبُ وَالْعَمَلُ الصَّالِحُ يَرْفَعُهُ ۚ وَالَّذِينَ يَمْكُرُونَ السَّيِّئَاتِ لَهُمْ عَذَابٌ شَدِيدٌ ۖ وَمَكْرُ أُولَٰئِكَ هُوَ يَبُورُ', 35, 10, 22, 87, 435, 'کسی که خواهان عزّت است (باید از خدا بخواهد چرا که) تمام عزّت برای خداست؛ سخنان پاکیزه به سوی او صعود می‌کند، و عمل صالح را بالا می‌برد؛ و آنها که نقشه‌های بد می‌کشند، عذاب سختی برای آنهاست و مکر (و تلاش افسادگرانه) آنان نابود می‌شود (و به جایی نمی‌رسد)!'),
(3671, 3671, 'وَاللَّهُ خَلَقَكُمْ مِنْ تُرَابٍ ثُمَّ مِنْ نُطْفَةٍ ثُمَّ جَعَلَكُمْ أَزْوَاجًا ۚ وَمَا تَحْمِلُ مِنْ أُنْثَىٰ وَلَا تَضَعُ إِلَّا بِعِلْمِهِ ۚ وَمَا يُعَمَّرُ مِنْ مُعَمَّرٍ وَلَا يُنْقَصُ مِنْ عُمُرِهِ إِلَّا فِي كِتَابٍ ۚ إِنَّ ذَٰلِكَ عَلَى اللَّهِ يَسِيرٌ', 35, 11, 22, 87, 435, 'خداوند شما را از خاکی آفرید، سپس از نطفه‌ای؛ سپس شما را بصورت زوجهایی قرار داد؛ هیچ جنس ماده‌ای باردار نمی‌شود و وضع حمل نمی‌کند مگر به علم او، و هیچ کس عمر طولانی نمی‌کند، یا از عمرش کاسته نمی‌شود مگر اینکه در کتاب (علم خداوند) ثبت است؛ اینها همه برای خداوند آسان است.'),
(3672, 3672, 'وَمَا يَسْتَوِي الْبَحْرَانِ هَٰذَا عَذْبٌ فُرَاتٌ سَائِغٌ شَرَابُهُ وَهَٰذَا مِلْحٌ أُجَاجٌ ۖ وَمِنْ كُلٍّ تَأْكُلُونَ لَحْمًا طَرِيًّا وَتَسْتَخْرِجُونَ حِلْيَةً تَلْبَسُونَهَا ۖ وَتَرَى الْفُلْكَ فِيهِ مَوَاخِرَ لِتَبْتَغُوا مِنْ فَضْلِهِ وَلَعَلَّكُمْ تَشْكُرُونَ', 35, 12, 22, 87, 436, 'دو دریا یکسان نیستند: این یکی دریایی است که آبش گوارا و شیرین و نوشیدنش خوشگوار است، و آن یکی شور و تلخ و گلوگیر؛ (امّا) از هر دو گوشتی تازه می خورید و وسایل زینتی استخراج کرده می‌پوشید؛ و کشتیها را در آن می‌بینی که آنها را می‌شکافند (و به سوی مقصد پیش می‌روند) تا از فضل خداوند بهره‌گیرید، و شاید شکر (نعمتهای او را) بجا آورید!'),
(3673, 3673, 'يُولِجُ اللَّيْلَ فِي النَّهَارِ وَيُولِجُ النَّهَارَ فِي اللَّيْلِ وَسَخَّرَ الشَّمْسَ وَالْقَمَرَ كُلٌّ يَجْرِي لِأَجَلٍ مُسَمًّى ۚ ذَٰلِكُمُ اللَّهُ رَبُّكُمْ لَهُ الْمُلْكُ ۚ وَالَّذِينَ تَدْعُونَ مِنْ دُونِهِ مَا يَمْلِكُونَ مِنْ قِطْمِيرٍ', 35, 13, 22, 87, 436, 'او شب را در روز داخل می‌کند و روز را در شب؛ و خورشید و ماه را مسخّر (شما) کرده، هر یک تا سرآمد معیّنی به حرکت خود ادامه می‌دهد؛ این است خداوند، پروردگار شما؛ حاکمیّت (در سراسر عالم) از آن اوست: و کسانی را که جز او می‌خوانید (و می‌پرستید) حتی به اندازه پوست نازک هسته خرما مالک نیستند!'),
(3674, 3674, 'إِنْ تَدْعُوهُمْ لَا يَسْمَعُوا دُعَاءَكُمْ وَلَوْ سَمِعُوا مَا اسْتَجَابُوا لَكُمْ ۖ وَيَوْمَ الْقِيَامَةِ يَكْفُرُونَ بِشِرْكِكُمْ ۚ وَلَا يُنَبِّئُكَ مِثْلُ خَبِيرٍ', 35, 14, 22, 87, 436, 'اگر آنها را بخوانید صدای شما را نمی‌شنوند، و اگر بشنوند به شما پاسخ نمی‌گویند؛ و روز قیامت، شرک (و پرستش) شما را منکر می‌شوند، و هیچ کس مانند (خداوند آگاه و) خبیر تو را (از حقایق) با خبر نمی‌سازد!'),
(3675, 3675, ' يَا أَيُّهَا النَّاسُ أَنْتُمُ الْفُقَرَاءُ إِلَى اللَّهِ ۖ وَاللَّهُ هُوَ الْغَنِيُّ الْحَمِيدُ', 35, 15, 22, 88, 436, 'ای مردم شما (همگی) نیازمند به خدائید؛ تنها خداوند است که بی‌نیاز و شایسته هر گونه حمد و ستایش است!'),
(3676, 3676, 'إِنْ يَشَأْ يُذْهِبْكُمْ وَيَأْتِ بِخَلْقٍ جَدِيدٍ', 35, 16, 22, 88, 436, 'اگر بخواهد شما را می‌برد و خلق جدیدی می‌آورد؛'),
(3677, 3677, 'وَمَا ذَٰلِكَ عَلَى اللَّهِ بِعَزِيزٍ', 35, 17, 22, 88, 436, 'و این برای خداوند مشکل نیست!'),
(3678, 3678, 'وَلَا تَزِرُ وَازِرَةٌ وِزْرَ أُخْرَىٰ ۚ وَإِنْ تَدْعُ مُثْقَلَةٌ إِلَىٰ حِمْلِهَا لَا يُحْمَلْ مِنْهُ شَيْءٌ وَلَوْ كَانَ ذَا قُرْبَىٰ ۗ إِنَّمَا تُنْذِرُ الَّذِينَ يَخْشَوْنَ رَبَّهُمْ بِالْغَيْبِ وَأَقَامُوا الصَّلَاةَ ۚ وَمَنْ تَزَكَّىٰ فَإِنَّمَا يَتَزَكَّىٰ لِنَفْسِهِ ۚ وَإِلَى اللَّهِ الْمَصِيرُ', 35, 18, 22, 88, 436, 'هیچ گنهکاری بار گناه دیگری را بر دوش نمی‌کشد؛ و اگر شخص سنگین‌باری دیگری را برای حمل گناه خود بخواند، چیزی از آن را بر دوش نخواهد گرفت، هر چند از نزدیکان او باشد! تو فقط کسانی را بیم‌می‌دهی که از پروردگار خود در پنهانی می‌ترسند و نماز را برپا می‌دارند؛ و هر کس پاکی (و تقوا) پیشه کند، نتیجه آن به خودش بازمی‌گردد؛ و بازگشت (همگان) به سوی خداست!'),
(3679, 3679, 'وَمَا يَسْتَوِي الْأَعْمَىٰ وَالْبَصِيرُ', 35, 19, 22, 88, 437, 'و نابینا و بینا هرگز برابر نیستند،'),
(3680, 3680, 'وَلَا الظُّلُمَاتُ وَلَا النُّورُ', 35, 20, 22, 88, 437, 'و نه ظلمتها و روشنایی،'),
(3681, 3681, 'وَلَا الظِّلُّ وَلَا الْحَرُورُ', 35, 21, 22, 88, 437, 'و نه سایه (آرامبخش) و باد داغ و سوزان!'),
(3682, 3682, 'وَمَا يَسْتَوِي الْأَحْيَاءُ وَلَا الْأَمْوَاتُ ۚ إِنَّ اللَّهَ يُسْمِعُ مَنْ يَشَاءُ ۖ وَمَا أَنْتَ بِمُسْمِعٍ مَنْ فِي الْقُبُورِ', 35, 22, 22, 88, 437, 'و هرگز مردگان و زندگان یکسان نیستند! خداوند پیام خود را به گوش هر کس بخواهد می‌رساند، و تو نمی‌توانی سخن خود را به گوش آنان که در گور خفته‌اند برسانی!'),
(3683, 3683, 'إِنْ أَنْتَ إِلَّا نَذِيرٌ', 35, 23, 22, 88, 437, 'تو فقط انذارکننده‌ای، (اگر ایمان نیاورند نگران نباش، وظیفه‌ات را انجام ده.)'),
(3684, 3684, 'إِنَّا أَرْسَلْنَاكَ بِالْحَقِّ بَشِيرًا وَنَذِيرًا ۚ وَإِنْ مِنْ أُمَّةٍ إِلَّا خَلَا فِيهَا نَذِيرٌ', 35, 24, 22, 88, 437, 'ما تو را بحق برای بشارت و انذار فرستادیم؛ و هر امّتی در گذشته انذارکننده‌ای داشته است!'),
(3685, 3685, 'وَإِنْ يُكَذِّبُوكَ فَقَدْ كَذَّبَ الَّذِينَ مِنْ قَبْلِهِمْ جَاءَتْهُمْ رُسُلُهُمْ بِالْبَيِّنَاتِ وَبِالزُّبُرِ وَبِالْكِتَابِ الْمُنِيرِ', 35, 25, 22, 88, 437, 'اگر تو را تکذیب کنند (عجیب نیست)؛ کسانی که پیش از آنان بودند (نیز پیامبران خود را) تکذیب کردند؛ آنها با دلایل روشن و کتابهای پند و موعظه و کتب آسمانی روشنگر (مشتمل بر معارف و احکام) به سراغ آنان آمدند (امّا کوردلان ایمان نیاوردند).'),
(3686, 3686, 'ثُمَّ أَخَذْتُ الَّذِينَ كَفَرُوا ۖ فَكَيْفَ كَانَ نَكِيرِ', 35, 26, 22, 88, 437, 'سپس من کافران را (بعد از اتمام حجّت) گرفتم (و سخت مجازات کردم)؛ مجازات من نسبت به آنان چگونه بود؟!'),
(3687, 3687, 'أَلَمْ تَرَ أَنَّ اللَّهَ أَنْزَلَ مِنَ السَّمَاءِ مَاءً فَأَخْرَجْنَا بِهِ ثَمَرَاتٍ مُخْتَلِفًا أَلْوَانُهَا ۚ وَمِنَ الْجِبَالِ جُدَدٌ بِيضٌ وَحُمْرٌ مُخْتَلِفٌ أَلْوَانُهَا وَغَرَابِيبُ سُودٌ', 35, 27, 22, 88, 437, 'آیا ندیدی خداوند از آسمان آبی فرو فرستاد که بوسیله آن میوه‌هایی رنگارنگ (از زمین) خارج ساختیم و از کوه‌ها نیز (به لطف پروردگار) جاده‌هایی آفریده شده سفید و سرخ و به رنگهای مختلف و گاه به رنگ کاملاً سیاه!'),
(3688, 3688, 'وَمِنَ النَّاسِ وَالدَّوَابِّ وَالْأَنْعَامِ مُخْتَلِفٌ أَلْوَانُهُ كَذَٰلِكَ ۗ إِنَّمَا يَخْشَى اللَّهَ مِنْ عِبَادِهِ الْعُلَمَاءُ ۗ إِنَّ اللَّهَ عَزِيزٌ غَفُورٌ', 35, 28, 22, 88, 437, 'و از انسانها و جنبندگان و چهارپایان انواعی با رنگهای مختلف، (آری) حقیقت چنین است: از میان بندگان خدا، تنها دانشمندان از او می‌ترسند؛ خداوند عزیز و غفور است!'),
(3689, 3689, 'إِنَّ الَّذِينَ يَتْلُونَ كِتَابَ اللَّهِ وَأَقَامُوا الصَّلَاةَ وَأَنْفَقُوا مِمَّا رَزَقْنَاهُمْ سِرًّا وَعَلَانِيَةً يَرْجُونَ تِجَارَةً لَنْ تَبُورَ', 35, 29, 22, 88, 437, 'کسانی که کتاب الهی را تلاوت می‌کنند و نماز را برپا می‌دارند و از آنچه به آنان روزی داده‌ایم پنهان و آشکار انفاق می‌کنند، تجارتی (پرسود و) بی‌زیان و خالی از کساد را امید دارند.'),
(3690, 3690, 'لِيُوَفِّيَهُمْ أُجُورَهُمْ وَيَزِيدَهُمْ مِنْ فَضْلِهِ ۚ إِنَّهُ غَفُورٌ شَكُورٌ', 35, 30, 22, 88, 437, '(آنها این اعمال صالح را انجام می‌دهند) تا خداوند اجر و پاداش کامل به آنها دهد و از فضلش بر آنها بیفزاید که او آمرزنده و شکرگزار است!'),
(3691, 3691, 'وَالَّذِي أَوْحَيْنَا إِلَيْكَ مِنَ الْكِتَابِ هُوَ الْحَقُّ مُصَدِّقًا لِمَا بَيْنَ يَدَيْهِ ۗ إِنَّ اللَّهَ بِعِبَادِهِ لَخَبِيرٌ بَصِيرٌ', 35, 31, 22, 88, 438, 'و آنچه از کتاب به تو وحی کردیم حق است و تصدیق‌کننده و هماهنگ با کتب پیش از آن؛ خداوند نسبت به بندگانش خبیر و بیناست!'),
(3692, 3692, 'ثُمَّ أَوْرَثْنَا الْكِتَابَ الَّذِينَ اصْطَفَيْنَا مِنْ عِبَادِنَا ۖ فَمِنْهُمْ ظَالِمٌ لِنَفْسِهِ وَمِنْهُمْ مُقْتَصِدٌ وَمِنْهُمْ سَابِقٌ بِالْخَيْرَاتِ بِإِذْنِ اللَّهِ ۚ ذَٰلِكَ هُوَ الْفَضْلُ الْكَبِيرُ', 35, 32, 22, 88, 438, 'سپس این کتاب (آسمانی) را به گروهی از بندگان برگزیده خود به میراث دادیم؛ (امّا) از میان آنها عده‌ای بر خود ستم کردند، و عده‌ای میانه رو بودند، و گروهی به اذن خدا در نیکیها (از همه) پیشی گرفتند، و این، همان فضیلت بزرگ است!'),
(3693, 3693, 'جَنَّاتُ عَدْنٍ يَدْخُلُونَهَا يُحَلَّوْنَ فِيهَا مِنْ أَسَاوِرَ مِنْ ذَهَبٍ وَلُؤْلُؤًا ۖ وَلِبَاسُهُمْ فِيهَا حَرِيرٌ', 35, 33, 22, 88, 438, '(پاداش آنان) باغهای جاویدان بهشت است که در آن وارد می‌شوند در حالی که با دستبندهایی از طلا و مروارید آراسته‌اند، و لباسشان در آنجا حریر است!'),
(3694, 3694, 'وَقَالُوا الْحَمْدُ لِلَّهِ الَّذِي أَذْهَبَ عَنَّا الْحَزَنَ ۖ إِنَّ رَبَّنَا لَغَفُورٌ شَكُورٌ', 35, 34, 22, 88, 438, 'آنها می‌گویند: «حمد (و ستایش) برای خداوندی است که اندوه را از ما برطرف ساخت؛ پروردگار ما آمرزنده و سپاسگزار است!'),
(3695, 3695, 'الَّذِي أَحَلَّنَا دَارَ الْمُقَامَةِ مِنْ فَضْلِهِ لَا يَمَسُّنَا فِيهَا نَصَبٌ وَلَا يَمَسُّنَا فِيهَا لُغُوبٌ', 35, 35, 22, 88, 438, 'همان کسی که با فضل خود ما را در این سرای اقامت (جاویدان) جای داد که نه در آن رنجی به ما می‌رسد و نه سستی و واماندگی!'),
(3696, 3696, 'وَالَّذِينَ كَفَرُوا لَهُمْ نَارُ جَهَنَّمَ لَا يُقْضَىٰ عَلَيْهِمْ فَيَمُوتُوا وَلَا يُخَفَّفُ عَنْهُمْ مِنْ عَذَابِهَا ۚ كَذَٰلِكَ نَجْزِي كُلَّ كَفُورٍ', 35, 36, 22, 88, 438, 'و کسانی که کافر شدند، آتش دوزخ برای آنهاست؛ هرگز فرمان مرگشان صادر نمی‌شود تا بمیرند، و نه چیزی از عذابش از آنان تخفیف داده می‌شود؛ این گونه هر کفران‌کننده‌ای را کیفر می‌دهیم!'),
(3697, 3697, 'وَهُمْ يَصْطَرِخُونَ فِيهَا رَبَّنَا أَخْرِجْنَا نَعْمَلْ صَالِحًا غَيْرَ الَّذِي كُنَّا نَعْمَلُ ۚ أَوَلَمْ نُعَمِّرْكُمْ مَا يَتَذَكَّرُ فِيهِ مَنْ تَذَكَّرَ وَجَاءَكُمُ النَّذِيرُ ۖ فَذُوقُوا فَمَا لِلظَّالِمِينَ مِنْ نَصِيرٍ', 35, 37, 22, 88, 438, 'آنها در دوزخ فریاد می‌زنند: «پروردگارا! ما را خارج کن تا عمل صالحی انجام دهیم غیر از آنچه انجام می‌دادیم!» (در پاسخ به آنان گفته می‌شود:) آیا شما را به اندازه‌ای که هر کس اهل تذکّر است در آن متذکّر می‌شود عمر ندادیم، و انذارکننده (الهی) به سراغ شما نیامد؟! اکنون بچشید که برای ظالمان هیچ یاوری نیست!'),
(3698, 3698, 'إِنَّ اللَّهَ عَالِمُ غَيْبِ السَّمَاوَاتِ وَالْأَرْضِ ۚ إِنَّهُ عَلِيمٌ بِذَاتِ الصُّدُورِ', 35, 38, 22, 88, 438, 'خداوند از غیب آسمانها و زمین آگاه است، و آنچه را در درون دلهاست می‌داند!'),
(3699, 3699, 'هُوَ الَّذِي جَعَلَكُمْ خَلَائِفَ فِي الْأَرْضِ ۚ فَمَنْ كَفَرَ فَعَلَيْهِ كُفْرُهُ ۖ وَلَا يَزِيدُ الْكَافِرِينَ كُفْرُهُمْ عِنْدَ رَبِّهِمْ إِلَّا مَقْتًا ۖ وَلَا يَزِيدُ الْكَافِرِينَ كُفْرُهُمْ إِلَّا خَسَارًا', 35, 39, 22, 88, 439, 'اوست که شما را جانشینانی در زمین قرار داد؛ هر کس کافر شود، کفر او به زیان خودش خواهد بود، و کافران را کفرشان جز خشم و غضب در نزد پروردگار چیزی نمی‌افزاید، و (نیز) کفرشان جز زیان و خسران چیزی بر آنها اضافه نمی‌کند!'),
(3700, 3700, 'قُلْ أَرَأَيْتُمْ شُرَكَاءَكُمُ الَّذِينَ تَدْعُونَ مِنْ دُونِ اللَّهِ أَرُونِي مَاذَا خَلَقُوا مِنَ الْأَرْضِ أَمْ لَهُمْ شِرْكٌ فِي السَّمَاوَاتِ أَمْ آتَيْنَاهُمْ كِتَابًا فَهُمْ عَلَىٰ بَيِّنَتٍ مِنْهُ ۚ بَلْ إِنْ يَعِدُ الظَّالِمُونَ بَعْضُهُمْ بَعْضًا إِلَّا غُرُورًا', 35, 40, 22, 88, 439, 'بگو: «این معبودانی را که جز خدا می‌خوانید به من نشان دهید چه چیزی از زمین را آفریده‌اند، یا اینکه شرکتی در (آفرینش و مالکیّت) آسمانها دارند؟! یا به آنان کتابی (آسمانی) داده‌ایم و دلیلی از آن برای (شرک) خود دارند؟!» نه هیچ یک از اینها نیست، ظالمان فقط وعده‌های دروغین به یکدیگر می‌دهند!'),
(3701, 3701, ' إِنَّ اللَّهَ يُمْسِكُ السَّمَاوَاتِ وَالْأَرْضَ أَنْ تَزُولَا ۚ وَلَئِنْ زَالَتَا إِنْ أَمْسَكَهُمَا مِنْ أَحَدٍ مِنْ بَعْدِهِ ۚ إِنَّهُ كَانَ حَلِيمًا غَفُورًا', 35, 41, 22, 88, 439, 'خداوند آسمانها و زمین را نگاه می‌دارد تا از نظام خود منحرف نشوند؛ و هرگاه منحرف گردند، کسی جز او نمی‌تواند آنها را نگاه دارد، او بردبار و غفور است!'),
(3702, 3702, 'وَأَقْسَمُوا بِاللَّهِ جَهْدَ أَيْمَانِهِمْ لَئِنْ جَاءَهُمْ نَذِيرٌ لَيَكُونُنَّ أَهْدَىٰ مِنْ إِحْدَى الْأُمَمِ ۖ فَلَمَّا جَاءَهُمْ نَذِيرٌ مَا زَادَهُمْ إِلَّا نُفُورًا', 35, 42, 22, 88, 439, 'آنان با نهایت تأکید به خدا سوگند خوردند که اگر پیامبری انذارکننده به سراغشان آید، هدایت یافته‌ترین امّتها خواهند بود؛ امّا چون پیامبری برای آنان آمد، جز فرار و فاصله‌گرفتن از (حق) چیزی بر آنها نیفزود!'),
(3703, 3703, 'اسْتِكْبَارًا فِي الْأَرْضِ وَمَكْرَ السَّيِّئِ ۚ وَلَا يَحِيقُ الْمَكْرُ السَّيِّئُ إِلَّا بِأَهْلِهِ ۚ فَهَلْ يَنْظُرُونَ إِلَّا سُنَّتَ الْأَوَّلِينَ ۚ فَلَنْ تَجِدَ لِسُنَّتِ اللَّهِ تَبْدِيلًا ۖ وَلَنْ تَجِدَ لِسُنَّتِ اللَّهِ تَحْوِيلًا', 35, 43, 22, 88, 439, 'اینها همه بخاطر استکبار در زمین و نیرنگهای بدشان بود؛ امّا این نیرنگها تنها دامان صاحبانش را می‌گیرد؛ آیا آنها چیزی جز سنّت پیشینیان و (عذابهای دردناک آنان) را انتظار دارند؟! هرگز برای سنّت خدا تبدیل نخواهی یافت، و هرگز برای سنّت الهی تغییری نمی‌یابی!'),
(3704, 3704, 'أَوَلَمْ يَسِيرُوا فِي الْأَرْضِ فَيَنْظُرُوا كَيْفَ كَانَ عَاقِبَةُ الَّذِينَ مِنْ قَبْلِهِمْ وَكَانُوا أَشَدَّ مِنْهُمْ قُوَّةً ۚ وَمَا كَانَ اللَّهُ لِيُعْجِزَهُ مِنْ شَيْءٍ فِي السَّمَاوَاتِ وَلَا فِي الْأَرْضِ ۚ إِنَّهُ كَانَ عَلِيمًا قَدِيرًا', 35, 44, 22, 88, 439, 'آیا آنان در زمین نگشتند تا ببینند عاقبت کسانی که پیش از آنها بودند چگونه بود؟! همانها که از اینان قویتر (و نیرومندتر) بودند؛ نه چیزی در آسمانها و نه چیزی در زمین از حوزه قدرت او بیرون نخواهد رفت؛ او دانا و تواناست!'),
(3705, 3705, 'وَلَوْ يُؤَاخِذُ اللَّهُ النَّاسَ بِمَا كَسَبُوا مَا تَرَكَ عَلَىٰ ظَهْرِهَا مِنْ دَابَّةٍ وَلَٰكِنْ يُؤَخِّرُهُمْ إِلَىٰ أَجَلٍ مُسَمًّى ۖ فَإِذَا جَاءَ أَجَلُهُمْ فَإِنَّ اللَّهَ كَانَ بِعِبَادِهِ بَصِيرًا', 35, 45, 22, 88, 440, 'اگر خداوند مردم را به سبب کارهایی که انجام داده‌اند مجازات کند، جنبنده‌ای را بر پشت زمین باقی نخواهد گذاشت! ولی (به لطفش) آنها را تا سرآمد معیّنی تأخیر می‌اندازد (و مهلت اصلاح می‌دهد) امّا هنگامی که اجل آنان فرا رسد، (خداوند هر کس را به مقتضای عملش جزا می‌دهد) او نسبت به بندگانش بیناست (و از اعمال و نیّات همه آگاه است)!'),
(3706, 3706, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ يس', 36, 1, 22, 88, 440, 'یس.'),
(3707, 3707, 'وَالْقُرْآنِ الْحَكِيمِ', 36, 2, 22, 88, 440, 'سوگند به قرآن حکیم.'),
(3708, 3708, 'إِنَّكَ لَمِنَ الْمُرْسَلِينَ', 36, 3, 22, 88, 440, 'که تو قطعاً از رسولان (خداوند) هستی،'),
(3709, 3709, 'عَلَىٰ صِرَاطٍ مُسْتَقِيمٍ', 36, 4, 22, 88, 440, 'بر راهی راست (قرار داری)؛'),
(3710, 3710, 'تَنْزِيلَ الْعَزِيزِ الرَّحِيمِ', 36, 5, 22, 88, 440, 'این قرآنی است که از سوی خداوند عزیز و رحیم نازل شده است‌'),
(3711, 3711, 'لِتُنْذِرَ قَوْمًا مَا أُنْذِرَ آبَاؤُهُمْ فَهُمْ غَافِلُونَ', 36, 6, 22, 88, 440, 'تا قومی را بیم دهی که پدرانشان انذار نشدند، از این رو آنان غافلند!'),
(3712, 3712, 'لَقَدْ حَقَّ الْقَوْلُ عَلَىٰ أَكْثَرِهِمْ فَهُمْ لَا يُؤْمِنُونَ', 36, 7, 22, 88, 440, 'فرمان (الهی) درباره بیشتر آنها تحقق یافته، به همین جهت ایمان نمی‌آورند!'),
(3713, 3713, 'إِنَّا جَعَلْنَا فِي أَعْنَاقِهِمْ أَغْلَالًا فَهِيَ إِلَى الْأَذْقَانِ فَهُمْ مُقْمَحُونَ', 36, 8, 22, 88, 440, 'ما در گردنهای آنان غلهایی قرار دادیم که تا چانه‌ها ادامه دارد و سرهای آنان را به بالا نگاه داشته است!'),
(3714, 3714, 'وَجَعَلْنَا مِنْ بَيْنِ أَيْدِيهِمْ سَدًّا وَمِنْ خَلْفِهِمْ سَدًّا فَأَغْشَيْنَاهُمْ فَهُمْ لَا يُبْصِرُونَ', 36, 9, 22, 88, 440, 'و در پیش روی آنان سدّی قرار دادیم، و در پشت سرشان سدّی؛ و چشمانشان را پوشانده‌ایم، لذا نمی‌بینند!'),
(3715, 3715, 'وَسَوَاءٌ عَلَيْهِمْ أَأَنْذَرْتَهُمْ أَمْ لَمْ تُنْذِرْهُمْ لَا يُؤْمِنُونَ', 36, 10, 22, 88, 440, 'برای آنان یکسان است: چه انذارشان کنی یا نکنی، ایمان نمی‌آورند!'),
(3716, 3716, 'إِنَّمَا تُنْذِرُ مَنِ اتَّبَعَ الذِّكْرَ وَخَشِيَ الرَّحْمَٰنَ بِالْغَيْبِ ۖ فَبَشِّرْهُ بِمَغْفِرَةٍ وَأَجْرٍ كَرِيمٍ', 36, 11, 22, 88, 440, 'تو فقط کسی را انذار می‌کنی که از این یادآوری (الهی) پیروی کند و از خداوند رحمان در نهان بترسد؛ چنین کسی را به آمرزش و پاداشی پرارزش بشارت ده!'),
(3717, 3717, 'إِنَّا نَحْنُ نُحْيِي الْمَوْتَىٰ وَنَكْتُبُ مَا قَدَّمُوا وَآثَارَهُمْ ۚ وَكُلَّ شَيْءٍ أَحْصَيْنَاهُ فِي إِمَامٍ مُبِينٍ', 36, 12, 22, 88, 440, 'به یقین ما مردگان را زنده می‌کنیم و آنچه را از پیش فرستاده‌اند و تمام آثار آنها را می نویسیم؛ و همه چیز را در کتاب آشکار کننده‌ای برشمرده‌ایم!'),
(3718, 3718, 'وَاضْرِبْ لَهُمْ مَثَلًا أَصْحَابَ الْقَرْيَةِ إِذْ جَاءَهَا الْمُرْسَلُونَ', 36, 13, 22, 88, 441, 'و برای آنها، اصحاب قریه (انطاکیه) را مثال بزن هنگامی که فرستادگان خدا به سوی آنان آمدند؛'),
(3719, 3719, 'إِذْ أَرْسَلْنَا إِلَيْهِمُ اثْنَيْنِ فَكَذَّبُوهُمَا فَعَزَّزْنَا بِثَالِثٍ فَقَالُوا إِنَّا إِلَيْكُمْ مُرْسَلُونَ', 36, 14, 22, 88, 441, 'هنگامی که دو نفر از رسولان را بسوی آنها فرستادیم، امّا آنان رسولان (ما) را تکذیب کردند؛ پس برای تقویت آن دو، شخص سوّمی فرستادیم، آنها همگی گفتند: «ما فرستادگان (خدا) به سوی شما هستیم!»'),
(3720, 3720, 'قَالُوا مَا أَنْتُمْ إِلَّا بَشَرٌ مِثْلُنَا وَمَا أَنْزَلَ الرَّحْمَٰنُ مِنْ شَيْءٍ إِنْ أَنْتُمْ إِلَّا تَكْذِبُونَ', 36, 15, 22, 88, 441, 'امّا آنان (در جواب) گفتند: «شما جز بشری همانند ما نیستید، و خداوند رحمان چیزی نازل نکرده، شما فقط دروغ می‌گویید!»'),
(3721, 3721, 'قَالُوا رَبُّنَا يَعْلَمُ إِنَّا إِلَيْكُمْ لَمُرْسَلُونَ', 36, 16, 22, 88, 441, '(رسولان ما) گفتند: «پروردگار ما آگاه است که ما قطعاً فرستادگان (او) به سوی شما هستیم،'),
(3722, 3722, 'وَمَا عَلَيْنَا إِلَّا الْبَلَاغُ الْمُبِينُ', 36, 17, 22, 88, 441, 'و بر عهده ما چیزی جز ابلاغ آشکار نیست!»'),
(3723, 3723, 'قَالُوا إِنَّا تَطَيَّرْنَا بِكُمْ ۖ لَئِنْ لَمْ تَنْتَهُوا لَنَرْجُمَنَّكُمْ وَلَيَمَسَّنَّكُمْ مِنَّا عَذَابٌ أَلِيمٌ', 36, 18, 22, 88, 441, 'آنان گفتند: «ما شما را به فال بد گرفته‌ایم (و وجود شما را شوم می‌دانیم)، و اگر (از این سخنان) دست برندارید شما را سنگسار خواهیم کرد و شکنجه دردناکی از ما به شما خواهد رسید!'),
(3724, 3724, 'قَالُوا طَائِرُكُمْ مَعَكُمْ ۚ أَئِنْ ذُكِّرْتُمْ ۚ بَلْ أَنْتُمْ قَوْمٌ مُسْرِفُونَ', 36, 19, 22, 88, 441, '(رسولان) گفتند: «شومی شما از خودتان است اگر درست بیندیشید، بلکه شما گروهی اسرافکارید!»'),
(3725, 3725, 'وَجَاءَ مِنْ أَقْصَى الْمَدِينَةِ رَجُلٌ يَسْعَىٰ قَالَ يَا قَوْمِ اتَّبِعُوا الْمُرْسَلِينَ', 36, 20, 22, 88, 441, 'و مردی (با ایمان) از دورترین نقطه شهر با شتاب فرا رسید، گفت: «ای قوم من! از فرستادگان (خدا) پیروی کنید!'),
(3726, 3726, 'اتَّبِعُوا مَنْ لَا يَسْأَلُكُمْ أَجْرًا وَهُمْ مُهْتَدُونَ', 36, 21, 22, 88, 441, 'از کسانی پیروی کنید که از شما مزدی نمی‌خواهند و خود هدایت یافته‌اند!'),
(3727, 3727, 'وَمَا لِيَ لَا أَعْبُدُ الَّذِي فَطَرَنِي وَإِلَيْهِ تُرْجَعُونَ', 36, 22, 22, 88, 441, 'من چرا کسی را پرستش نکنم که مرا آفریده، و همگی به سوی او بازگشت داده می‌شوید؟!'),
(3728, 3728, 'أَأَتَّخِذُ مِنْ دُونِهِ آلِهَةً إِنْ يُرِدْنِ الرَّحْمَٰنُ بِضُرٍّ لَا تُغْنِ عَنِّي شَفَاعَتُهُمْ شَيْئًا وَلَا يُنْقِذُونِ', 36, 23, 22, 88, 441, 'آیا غیر از او معبودانی را انتخاب کنم که اگر خداوند رحمان بخواهد زیانی به من برساند، شفاعت آنها کمترین فایده‌ای برای من ندارد و مرا (از مجازات او) نجات نخواهند داد!'),
(3729, 3729, 'إِنِّي إِذًا لَفِي ضَلَالٍ مُبِينٍ', 36, 24, 22, 88, 441, 'اگر چنین کنم، من در گمراهی آشکاری خواهم بود!'),
(3730, 3730, 'إِنِّي آمَنْتُ بِرَبِّكُمْ فَاسْمَعُونِ', 36, 25, 22, 88, 441, '(به همین دلیل) من به پروردگارتان ایمان آوردم؛ پس به سخنان من گوش فرا دهید!»'),
(3731, 3731, 'قِيلَ ادْخُلِ الْجَنَّةَ ۖ قَالَ يَا لَيْتَ قَوْمِي يَعْلَمُونَ', 36, 26, 22, 88, 441, '(سرانجام او را شهید کردند و) به او گفته شد: «وارد بهشت شو!» گفت: «ای کاش قوم من می‌دانستند...'),
(3732, 3732, 'بِمَا غَفَرَ لِي رَبِّي وَجَعَلَنِي مِنَ الْمُكْرَمِينَ', 36, 27, 22, 88, 441, 'که پروردگارم مرا آمرزیده و از گرامی‌داشتگان قرار داده است!»'),
(3733, 3733, ' وَمَا أَنْزَلْنَا عَلَىٰ قَوْمِهِ مِنْ بَعْدِهِ مِنْ جُنْدٍ مِنَ السَّمَاءِ وَمَا كُنَّا مُنْزِلِينَ', 36, 28, 23, 89, 442, 'و ما بعد از او بر قومش هیچ لشکری از آسمان نفرستادیم، و هرگز سنت ما بر این نبود؛'),
(3734, 3734, 'إِنْ كَانَتْ إِلَّا صَيْحَةً وَاحِدَةً فَإِذَا هُمْ خَامِدُونَ', 36, 29, 23, 89, 442, '(بلکه) فقط یک صیحه آسمانی بود، ناگهان همگی خاموش شدند!'),
(3735, 3735, 'يَا حَسْرَةً عَلَى الْعِبَادِ ۚ مَا يَأْتِيهِمْ مِنْ رَسُولٍ إِلَّا كَانُوا بِهِ يَسْتَهْزِئُونَ', 36, 30, 23, 89, 442, 'افسوس بر این بندگان که هیچ پیامبری برای هدایت آنان نیامد مگر اینکه او را استهزا می‌کردند!'),
(3736, 3736, 'أَلَمْ يَرَوْا كَمْ أَهْلَكْنَا قَبْلَهُمْ مِنَ الْقُرُونِ أَنَّهُمْ إِلَيْهِمْ لَا يَرْجِعُونَ', 36, 31, 23, 89, 442, 'آیا ندیدند چقدر از اقوام پیش از آنان را (بخاطر گناهانشان) هلاک کردیم، آنها هرگز به سوی ایشان بازنمی‌گردند (و زنده نمی‌شوند)!'),
(3737, 3737, 'وَإِنْ كُلٌّ لَمَّا جَمِيعٌ لَدَيْنَا مُحْضَرُونَ', 36, 32, 23, 89, 442, 'و همه آنان (روز قیامت) نزد ما احضار می‌شوند!'),
(3738, 3738, 'وَآيَةٌ لَهُمُ الْأَرْضُ الْمَيْتَةُ أَحْيَيْنَاهَا وَأَخْرَجْنَا مِنْهَا حَبًّا فَمِنْهُ يَأْكُلُونَ', 36, 33, 23, 89, 442, 'زمین مرده برای آنها آیتی است، ما آن را زنده کردیم و دانه‌های (غذایی) از آن خارج ساختیم که از آن می‌خورند؛'),
(3739, 3739, 'وَجَعَلْنَا فِيهَا جَنَّاتٍ مِنْ نَخِيلٍ وَأَعْنَابٍ وَفَجَّرْنَا فِيهَا مِنَ الْعُيُونِ', 36, 34, 23, 89, 442, 'و در آن باغهایی از نخلها و انگورها قرار دادیم و چشمه‌هایی از آن جاری ساختیم،'),
(3740, 3740, 'لِيَأْكُلُوا مِنْ ثَمَرِهِ وَمَا عَمِلَتْهُ أَيْدِيهِمْ ۖ أَفَلَا يَشْكُرُونَ', 36, 35, 23, 89, 442, 'تا از میوه آن بخورند در حالی که دست آنان هیچ دخالتی در ساختن آن نداشته است! آیا شکر خدا را بجا نمی‌آورند؟!'),
(3741, 3741, 'سُبْحَانَ الَّذِي خَلَقَ الْأَزْوَاجَ كُلَّهَا مِمَّا تُنْبِتُ الْأَرْضُ وَمِنْ أَنْفُسِهِمْ وَمِمَّا لَا يَعْلَمُونَ', 36, 36, 23, 89, 442, 'منزه است کسی که تمام زوجها را آفرید، از آنچه زمین می‌رویاند، و از خودشان، و از آنچه نمی‌دانند!'),
(3742, 3742, 'وَآيَةٌ لَهُمُ اللَّيْلُ نَسْلَخُ مِنْهُ النَّهَارَ فَإِذَا هُمْ مُظْلِمُونَ', 36, 37, 23, 89, 442, 'شب (نیز) برای آنها نشانه‌ای است (از عظمت خدا)؛ ما روز را از آن برمی‌گیریم، ناگهان تاریکی آنان را فرا می‌گیرد!'),
(3743, 3743, 'وَالشَّمْسُ تَجْرِي لِمُسْتَقَرٍّ لَهَا ۚ ذَٰلِكَ تَقْدِيرُ الْعَزِيزِ الْعَلِيمِ', 36, 38, 23, 89, 442, 'و خورشید (نیز برای آنها آیتی است) که پیوسته بسوی قرارگاهش در حرکت است؛ این تقدیر خداوند قادر و داناست.'),
(3744, 3744, 'وَالْقَمَرَ قَدَّرْنَاهُ مَنَازِلَ حَتَّىٰ عَادَ كَالْعُرْجُونِ الْقَدِيمِ', 36, 39, 23, 89, 442, 'و برای ماه منزلگاه‌هایی قرار دادیم، (و هنگامی که این منازل را طی کرد) سرانجام بصورت «شاخه کهنه قوسی شکل و زرد رنگ خرما» در می‌آید.'),
(3745, 3745, 'لَا الشَّمْسُ يَنْبَغِي لَهَا أَنْ تُدْرِكَ الْقَمَرَ وَلَا اللَّيْلُ سَابِقُ النَّهَارِ ۚ وَكُلٌّ فِي فَلَكٍ يَسْبَحُونَ', 36, 40, 23, 89, 442, 'نه خورشید را سزاست که به ماه رسد، و نه شب بر روز پیشی می‌گیرد؛ و هر کدام در مسیر خود شناورند.'),
(3746, 3746, 'وَآيَةٌ لَهُمْ أَنَّا حَمَلْنَا ذُرِّيَّتَهُمْ فِي الْفُلْكِ الْمَشْحُونِ', 36, 41, 23, 89, 443, 'نشانه‌ای (دیگر از عظمت پروردگار) برای آنان است که ما فرزندانشان را در کشتیهایی پر (از وسایل و بارها) حمل کردیم.'),
(3747, 3747, 'وَخَلَقْنَا لَهُمْ مِنْ مِثْلِهِ مَا يَرْكَبُونَ', 36, 42, 23, 89, 443, 'و برای آنها مرکبهای دیگری همانند آن آفریدیم.'),
(3748, 3748, 'وَإِنْ نَشَأْ نُغْرِقْهُمْ فَلَا صَرِيخَ لَهُمْ وَلَا هُمْ يُنْقَذُونَ', 36, 43, 23, 89, 443, 'و اگر بخواهیم آنها را غرق می‌کنیم بطوری که نه فریادرسی داشته باشند و نه نجات داده شوند!'),
(3749, 3749, 'إِلَّا رَحْمَةً مِنَّا وَمَتَاعًا إِلَىٰ حِينٍ', 36, 44, 23, 89, 443, 'مگر اینکه رحمت ما شامل حال آنان شود، و تا زمان معیّنی از این زندگی بهره گیرند!'),
(3750, 3750, 'وَإِذَا قِيلَ لَهُمُ اتَّقُوا مَا بَيْنَ أَيْدِيكُمْ وَمَا خَلْفَكُمْ لَعَلَّكُمْ تُرْحَمُونَ', 36, 45, 23, 89, 443, 'و هرگاه به آنها گفته شود: «از آنچه پیش رو و پشت سر شماست = از عذابهای الهی‌ بترسید تا مشمول رحمت الهی شوید!» (اعتنا نمی‌کنند).'),
(3751, 3751, 'وَمَا تَأْتِيهِمْ مِنْ آيَةٍ مِنْ آيَاتِ رَبِّهِمْ إِلَّا كَانُوا عَنْهَا مُعْرِضِينَ', 36, 46, 23, 89, 443, 'و هیچ آیه‌ای از آیات پروردگارشان برای آنها نمی‌آید مگر اینکه از آن روی‌گردان می شوند.'),
(3752, 3752, 'وَإِذَا قِيلَ لَهُمْ أَنْفِقُوا مِمَّا رَزَقَكُمُ اللَّهُ قَالَ الَّذِينَ كَفَرُوا لِلَّذِينَ آمَنُوا أَنُطْعِمُ مَنْ لَوْ يَشَاءُ اللَّهُ أَطْعَمَهُ إِنْ أَنْتُمْ إِلَّا فِي ضَلَالٍ مُبِينٍ', 36, 47, 23, 89, 443, 'و هنگامی که به آنان گفته شود: «از آنچه خدا به شما روزی کرده انفاق کنید!»، کافران به مؤمنان می‌گویند: «آیا ما کسی را اطعام کنیم که اگر خدا می‌خواست او را اطعام می‌کرد؟! (پس خدا خواسته است او گرسنه باشد)، شما فقط در گمراهی آشکارید»!'),
(3753, 3753, 'وَيَقُولُونَ مَتَىٰ هَٰذَا الْوَعْدُ إِنْ كُنْتُمْ صَادِقِينَ', 36, 48, 23, 89, 443, 'آنها می‌گویند: «اگر راست می‌گویید، این وعده (قیامت) کی خواهد بود؟!'),
(3754, 3754, 'مَا يَنْظُرُونَ إِلَّا صَيْحَةً وَاحِدَةً تَأْخُذُهُمْ وَهُمْ يَخِصِّمُونَ', 36, 49, 23, 89, 443, '(امّا) جز این انتظار نمی‌کشند که یک صیحه عظیم (آسمانی) آنها را فراگیرد، در حالی که مشغول جدال (در امور دنیا) هستند.'),
(3755, 3755, 'فَلَا يَسْتَطِيعُونَ تَوْصِيَةً وَلَا إِلَىٰ أَهْلِهِمْ يَرْجِعُونَ', 36, 50, 23, 89, 443, '(چنان غافلگیر می‌شوند که حتّی) نمی‌توانند وصیّتی کنند یا به سوی خانواده خود بازگردند!'),
(3756, 3756, 'وَنُفِخَ فِي الصُّورِ فَإِذَا هُمْ مِنَ الْأَجْدَاثِ إِلَىٰ رَبِّهِمْ يَنْسِلُونَ', 36, 51, 23, 89, 443, '(بار دیگر) در «صور» دمیده می‌شود، ناگهان آنها از قبرها، شتابان به سوی (دادگاه) پروردگارشان می‌روند!'),
(3757, 3757, 'قَالُوا يَا وَيْلَنَا مَنْ بَعَثَنَا مِنْ مَرْقَدِنَا ۜ ۗ هَٰذَا مَا وَعَدَ الرَّحْمَٰنُ وَصَدَقَ الْمُرْسَلُونَ', 36, 52, 23, 89, 443, 'می‌گویند: «ای وای بر ما! چه کسی ما را از خوابگاهمان برانگیخت؟! (آری) این همان است که خداوند رحمان وعده داده، و فرستادگان (او) راست گفتند!»'),
(3758, 3758, 'إِنْ كَانَتْ إِلَّا صَيْحَةً وَاحِدَةً فَإِذَا هُمْ جَمِيعٌ لَدَيْنَا مُحْضَرُونَ', 36, 53, 23, 89, 443, 'صیحه واحدی بیش نیست، (فریادی عظیم برمی‌خیزد) ناگهان همگی نزد ما احضار می‌شوند!'),
(3759, 3759, 'فَالْيَوْمَ لَا تُظْلَمُ نَفْسٌ شَيْئًا وَلَا تُجْزَوْنَ إِلَّا مَا كُنْتُمْ تَعْمَلُونَ', 36, 54, 23, 89, 443, '(و به آنها گفته می‌شود:) امروز به هیچ کس ذرّه‌ای ستم نمی‌شود، و جز آنچه را عمل می‌کردید جزا داده نمی‌شوید!'),
(3760, 3760, 'إِنَّ أَصْحَابَ الْجَنَّةِ الْيَوْمَ فِي شُغُلٍ فَاكِهُونَ', 36, 55, 23, 89, 444, 'بهشتیان، امروز به نعمتهای خدا مشغول و مسرورند.'),
(3761, 3761, 'هُمْ وَأَزْوَاجُهُمْ فِي ظِلَالٍ عَلَى الْأَرَائِكِ مُتَّكِئُونَ', 36, 56, 23, 89, 444, 'آنها و همسرانشان در سایه‌های (قصرها و درختان بهشتی) بر تختها تکیه زده‌اند.'),
(3762, 3762, 'لَهُمْ فِيهَا فَاكِهَةٌ وَلَهُمْ مَا يَدَّعُونَ', 36, 57, 23, 89, 444, 'برای آنها در بهشت میوه بسیار لذّت‌بخشی است، و هر چه بخواهند در اختیار آنان خواهد بود!'),
(3763, 3763, 'سَلَامٌ قَوْلًا مِنْ رَبٍّ رَحِيمٍ', 36, 58, 23, 89, 444, 'بر آنها سلام (و درود الهی) است؛ این سخنی است از سوی پروردگاری مهربان!'),
(3764, 3764, 'وَامْتَازُوا الْيَوْمَ أَيُّهَا الْمُجْرِمُونَ', 36, 59, 23, 89, 444, '(و به آنها می‌گویند:) جدا شوید امروز ای گنهکاران!'),
(3765, 3765, ' أَلَمْ أَعْهَدْ إِلَيْكُمْ يَا بَنِي آدَمَ أَنْ لَا تَعْبُدُوا الشَّيْطَانَ ۖ إِنَّهُ لَكُمْ عَدُوٌّ مُبِينٌ', 36, 60, 23, 89, 444, 'آیا با شما عهد نکردم ای فرزندان آدم که شیطان را نپرستید، که او برای شما دشمن آشکاری است؟!'),
(3766, 3766, 'وَأَنِ اعْبُدُونِي ۚ هَٰذَا صِرَاطٌ مُسْتَقِيمٌ', 36, 61, 23, 89, 444, 'و اینکه مرا بپرستید که راه مستقیم این است؟!'),
(3767, 3767, 'وَلَقَدْ أَضَلَّ مِنْكُمْ جِبِلًّا كَثِيرًا ۖ أَفَلَمْ تَكُونُوا تَعْقِلُونَ', 36, 62, 23, 89, 444, 'او گروه زیادی از شما را گمراه کرد، آیا اندیشه نکردید؟!'),
(3768, 3768, 'هَٰذِهِ جَهَنَّمُ الَّتِي كُنْتُمْ تُوعَدُونَ', 36, 63, 23, 89, 444, 'این همان دوزخی است که به شما وعده داده می‌شد!'),
(3769, 3769, 'اصْلَوْهَا الْيَوْمَ بِمَا كُنْتُمْ تَكْفُرُونَ', 36, 64, 23, 89, 444, 'امروز وارد آن شوید و به خاطر کفری که داشتید به آتش آن بسوزید!'),
(3770, 3770, 'الْيَوْمَ نَخْتِمُ عَلَىٰ أَفْوَاهِهِمْ وَتُكَلِّمُنَا أَيْدِيهِمْ وَتَشْهَدُ أَرْجُلُهُمْ بِمَا كَانُوا يَكْسِبُونَ', 36, 65, 23, 89, 444, 'امروز بر دهانشان مُهر می‌نهیم، و دستهایشان با ما سخن می‌گویند و پاهایشان کارهایی را که انجام می‌دادند شهادت می‌دهند!'),
(3771, 3771, 'وَلَوْ نَشَاءُ لَطَمَسْنَا عَلَىٰ أَعْيُنِهِمْ فَاسْتَبَقُوا الصِّرَاطَ فَأَنَّىٰ يُبْصِرُونَ', 36, 66, 23, 89, 444, 'و اگر بخواهیم چشمانشان را محو کنیم؛ سپس برای عبور از راه، می‌خواهند بر یکدیگر پیشی بگیرند، امّا چگونه می‌توانند ببینند؟!'),
(3772, 3772, 'وَلَوْ نَشَاءُ لَمَسَخْنَاهُمْ عَلَىٰ مَكَانَتِهِمْ فَمَا اسْتَطَاعُوا مُضِيًّا وَلَا يَرْجِعُونَ', 36, 67, 23, 89, 444, 'و اگر بخواهیم آنها را در جای خود مسخ می‌کنیم (و به مجسمه‌هایی بی‌روح مبدّل می‌سازیم) تا نتوانند راه خود را ادامه دهند یا به عقب برگردند!'),
(3773, 3773, 'وَمَنْ نُعَمِّرْهُ نُنَكِّسْهُ فِي الْخَلْقِ ۖ أَفَلَا يَعْقِلُونَ', 36, 68, 23, 89, 444, 'هر کس را طول عمر دهیم، در آفرینش واژگونه‌اش می‌کنیم (و به ناتوانی کودکی باز می‌گردانیم)؛ آیا اندیشه نمی‌کنند؟!'),
(3774, 3774, 'وَمَا عَلَّمْنَاهُ الشِّعْرَ وَمَا يَنْبَغِي لَهُ ۚ إِنْ هُوَ إِلَّا ذِكْرٌ وَقُرْآنٌ مُبِينٌ', 36, 69, 23, 89, 444, 'ما هرگز شعر به او = پیامبر نیاموختیم، و شایسته او نیست (شاعر باشد)؛ این (کتاب آسمانی) فقط ذکر و قرآن مبین است!'),
(3775, 3775, 'لِيُنْذِرَ مَنْ كَانَ حَيًّا وَيَحِقَّ الْقَوْلُ عَلَى الْكَافِرِينَ', 36, 70, 23, 89, 444, 'تا افرادی را که زنده‌اند بیم دهد (و بر کافران اتمام حجّت شود) و فرمان عذاب بر آنان مسلّم گردد!'),
(3776, 3776, 'أَوَلَمْ يَرَوْا أَنَّا خَلَقْنَا لَهُمْ مِمَّا عَمِلَتْ أَيْدِينَا أَنْعَامًا فَهُمْ لَهَا مَالِكُونَ', 36, 71, 23, 89, 445, 'آیا ندیدند که از آنچه با قدرت خود به عمل آورده‌ایم چهارپایانی برای آنان آفریدیم که آنان مالک آن هستند؟!'),
(3777, 3777, 'وَذَلَّلْنَاهَا لَهُمْ فَمِنْهَا رَكُوبُهُمْ وَمِنْهَا يَأْكُلُونَ', 36, 72, 23, 89, 445, 'و آنها را رام ایشان ساختیم، هم مرکب آنان از آن است و هم از آن تغذیه می‌کنند؛'),
(3778, 3778, 'وَلَهُمْ فِيهَا مَنَافِعُ وَمَشَارِبُ ۖ أَفَلَا يَشْكُرُونَ', 36, 73, 23, 89, 445, 'و برای آنان بهره‌های دیگری در آن (حیوانات) است و نوشیدنیهائی گوارا؛ آیا با این حال شکرگزاری نمی‌کنند؟!'),
(3779, 3779, 'وَاتَّخَذُوا مِنْ دُونِ اللَّهِ آلِهَةً لَعَلَّهُمْ يُنْصَرُونَ', 36, 74, 23, 89, 445, 'آنان غیر از خدا معبودانی برای خویش برگزیدند به این امید که یاری شوند!'),
(3780, 3780, 'لَا يَسْتَطِيعُونَ نَصْرَهُمْ وَهُمْ لَهُمْ جُنْدٌ مُحْضَرُونَ', 36, 75, 23, 89, 445, 'ولی آنها قادر به یاری ایشان نیستند، و این (عبادت‌کنندگان در قیامت) لشکری برای آنها خواهند بود که در آتش دوزخ احضار می‌شوند!'),
(3781, 3781, 'فَلَا يَحْزُنْكَ قَوْلُهُمْ ۘ إِنَّا نَعْلَمُ مَا يُسِرُّونَ وَمَا يُعْلِنُونَ', 36, 76, 23, 89, 445, 'پس سخنانشان تو را غمگین نسازد، ما آنچه را پنهان می‌دارند و آنچه را آشکار می‌کنند می‌دانیم!'),
(3782, 3782, 'أَوَلَمْ يَرَ الْإِنْسَانُ أَنَّا خَلَقْنَاهُ مِنْ نُطْفَةٍ فَإِذَا هُوَ خَصِيمٌ مُبِينٌ', 36, 77, 23, 89, 445, 'آیا انسان نمی‌داند که ما او را از نطفه‌ای بی‌ارزش آفریدیم؟! و او (چنان صاحب قدرت و شعور و نطق شد که) به مخاصمه آشکار (با ما) برخاست!'),
(3783, 3783, 'وَضَرَبَ لَنَا مَثَلًا وَنَسِيَ خَلْقَهُ ۖ قَالَ مَنْ يُحْيِي الْعِظَامَ وَهِيَ رَمِيمٌ', 36, 78, 23, 89, 445, 'و برای ما مثالی زد و آفرینش خود را فراموش کرد و گفت: «چه کسی این استخوانها را زنده می‌کند در حالی که پوسیده است؟!»'),
(3784, 3784, 'قُلْ يُحْيِيهَا الَّذِي أَنْشَأَهَا أَوَّلَ مَرَّةٍ ۖ وَهُوَ بِكُلِّ خَلْقٍ عَلِيمٌ', 36, 79, 23, 89, 445, 'بگو: «همان کسی آن را زنده می‌کند که نخستین بار آن را آفرید؛ و او به هر مخلوقی داناست!'),
(3785, 3785, 'الَّذِي جَعَلَ لَكُمْ مِنَ الشَّجَرِ الْأَخْضَرِ نَارًا فَإِذَا أَنْتُمْ مِنْهُ تُوقِدُونَ', 36, 80, 23, 89, 445, 'همان کسی که برای شما از درخت سبز، آتش آفرید و شما بوسیله آن، آتش می‌افروزید!»'),
(3786, 3786, 'أَوَلَيْسَ الَّذِي خَلَقَ السَّمَاوَاتِ وَالْأَرْضَ بِقَادِرٍ عَلَىٰ أَنْ يَخْلُقَ مِثْلَهُمْ ۚ بَلَىٰ وَهُوَ الْخَلَّاقُ الْعَلِيمُ', 36, 81, 23, 89, 445, 'آیا کسی که آسمانها و زمین را آفرید، نمی‌تواند همانند آنان = انسانهای خاک شده‌ را بیافریند؟! آری (می‌تواند)، و او آفریدگار داناست!'),
(3787, 3787, 'إِنَّمَا أَمْرُهُ إِذَا أَرَادَ شَيْئًا أَنْ يَقُولَ لَهُ كُنْ فَيَكُونُ', 36, 82, 23, 89, 445, 'فرمان او چنین است که هرگاه چیزی را اراده کند، تنها به آن می‌گوید: «موجود باش!»، آن نیز بی‌درنگ موجود می‌شود!'),
(3788, 3788, 'فَسُبْحَانَ الَّذِي بِيَدِهِ مَلَكُوتُ كُلِّ شَيْءٍ وَإِلَيْهِ تُرْجَعُونَ', 36, 83, 23, 89, 445, 'پس منزّه است خداوندی که مالکیّت و حاکمیّت همه چیز در دست اوست؛ و شما را به سوی او بازمی‌گردانند!'),
(3789, 3789, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ وَالصَّافَّاتِ صَفًّا', 37, 1, 23, 89, 446, 'سوگند به (فرشتگان) صف‌کشیده (و منظّم)'),
(3790, 3790, 'فَالزَّاجِرَاتِ زَجْرًا', 37, 2, 23, 89, 446, 'و به نهی‌کنندگان و (بازدارندگان)'),
(3791, 3791, 'فَالتَّالِيَاتِ ذِكْرًا', 37, 3, 23, 89, 446, 'و تلاوت‌کنندگان پیاپی آیات الهی...'),
(3792, 3792, 'إِنَّ إِلَٰهَكُمْ لَوَاحِدٌ', 37, 4, 23, 89, 446, 'که معبود شما یگانه است؛'),
(3793, 3793, 'رَبُّ السَّمَاوَاتِ وَالْأَرْضِ وَمَا بَيْنَهُمَا وَرَبُّ الْمَشَارِقِ', 37, 5, 23, 89, 446, 'پروردگار آسمانها و زمین و آنچه میان آنهاست، و پروردگار مشرقها!'),
(3794, 3794, 'إِنَّا زَيَّنَّا السَّمَاءَ الدُّنْيَا بِزِينَةٍ الْكَوَاكِبِ', 37, 6, 23, 89, 446, 'ما آسمان نزدیک = پایین‌ را با ستارگان آراستیم،'),
(3795, 3795, 'وَحِفْظًا مِنْ كُلِّ شَيْطَانٍ مَارِدٍ', 37, 7, 23, 89, 446, 'تا آن را از هر شیطان خبیثی حفظ کنیم!'),
(3796, 3796, 'لَا يَسَّمَّعُونَ إِلَى الْمَلَإِ الْأَعْلَىٰ وَيُقْذَفُونَ مِنْ كُلِّ جَانِبٍ', 37, 8, 23, 89, 446, 'آنها نمی‌توانند به (سخنان) فرشتگان عالم بالا گوش فرادهند، (و هرگاه چنین کنند) از هر سو هدف قرارمی‌گیرند!'),
(3797, 3797, 'دُحُورًا ۖ وَلَهُمْ عَذَابٌ وَاصِبٌ', 37, 9, 23, 89, 446, 'آنها به شدّت به عقب رانده می‌شوند؛ و برای آنان مجازاتی دائم است!'),
(3798, 3798, 'إِلَّا مَنْ خَطِفَ الْخَطْفَةَ فَأَتْبَعَهُ شِهَابٌ ثَاقِبٌ', 37, 10, 23, 89, 446, 'مگر آنها که در لحظه‌ای کوتاه برای استراق سمع به آسمان نزدیک شوند، که «شهاب ثاقب» آنها را تعقیب می‌کند!'),
(3799, 3799, 'فَاسْتَفْتِهِمْ أَهُمْ أَشَدُّ خَلْقًا أَمْ مَنْ خَلَقْنَا ۚ إِنَّا خَلَقْنَاهُمْ مِنْ طِينٍ لَازِبٍ', 37, 11, 23, 89, 446, 'از آنان بپرس: «آیا آفرینش (و معاد) آنان سخت‌تر است یا آفرینش فرشتگان (و آسمانها و زمین)؟! ما آنان را از گل چسبنده‌ای آفریدیم!'),
(3800, 3800, 'بَلْ عَجِبْتَ وَيَسْخَرُونَ', 37, 12, 23, 89, 446, 'تو از انکارشان تعجّب می‌کنی، ولی آنها مسخره می‌کنند!'),
(3801, 3801, 'وَإِذَا ذُكِّرُوا لَا يَذْكُرُونَ', 37, 13, 23, 89, 446, 'و هنگامی که به آنان تذکّر داده شود، هرگز متذکّر نمی‌شوند!'),
(3802, 3802, 'وَإِذَا رَأَوْا آيَةً يَسْتَسْخِرُونَ', 37, 14, 23, 89, 446, 'و هنگامی که معجزه‌ای را ببینند، دیگران را نیز به استهزا دعوت می‌کنند!'),
(3803, 3803, 'وَقَالُوا إِنْ هَٰذَا إِلَّا سِحْرٌ مُبِينٌ', 37, 15, 23, 89, 446, 'و می‌گویند: «این فقط سحری آشکار است!'),
(3804, 3804, 'أَإِذَا مِتْنَا وَكُنَّا تُرَابًا وَعِظَامًا أَإِنَّا لَمَبْعُوثُونَ', 37, 16, 23, 89, 446, 'آیا هنگامی که ما مُردیم و به خاک و استخوان مبدّل شدیم، بار دیگر برانگیخته خواهیم شد؟!'),
(3805, 3805, 'أَوَآبَاؤُنَا الْأَوَّلُونَ', 37, 17, 23, 89, 446, 'یا پدران نخستین ما (بازمی‌گردند)؟!»'),
(3806, 3806, 'قُلْ نَعَمْ وَأَنْتُمْ دَاخِرُونَ', 37, 18, 23, 89, 446, 'بگو: «آری، همه شما زنده می‌شوید در حالی که خوار و کوچک خواهید بود!'),
(3807, 3807, 'فَإِنَّمَا هِيَ زَجْرَةٌ وَاحِدَةٌ فَإِذَا هُمْ يَنْظُرُونَ', 37, 19, 23, 89, 446, 'تنها یک صیحه عظیم واقع می‌شود، ناگهان همه (از قبرها برمی‌خیزند و) نگاه می‌کنند!'),
(3808, 3808, 'وَقَالُوا يَا وَيْلَنَا هَٰذَا يَوْمُ الدِّينِ', 37, 20, 23, 89, 446, 'و می‌گویند: «ای وای بر ما، این روز جزاست!»'),
(3809, 3809, 'هَٰذَا يَوْمُ الْفَصْلِ الَّذِي كُنْتُمْ بِهِ تُكَذِّبُونَ', 37, 21, 23, 89, 446, '(آری) این همان روز جدایی (حقّ از باطل) است که شما آن را تکذیب می‌کردید!'),
(3810, 3810, ' احْشُرُوا الَّذِينَ ظَلَمُوا وَأَزْوَاجَهُمْ وَمَا كَانُوا يَعْبُدُونَ', 37, 22, 23, 90, 446, '(در این هنگام به فرشتگان دستور داده می‌شود:) ظالمان و همردیفانشان و آنچه را می‌پرستیدند...'),
(3811, 3811, 'مِنْ دُونِ اللَّهِ فَاهْدُوهُمْ إِلَىٰ صِرَاطِ الْجَحِيمِ', 37, 23, 23, 90, 446, '(آری آنچه را) جز خدا می‌پرستیدند جمع کنید و بسوی راه دوزخ هدایتشان کنید!'),
(3812, 3812, 'وَقِفُوهُمْ ۖ إِنَّهُمْ مَسْئُولُونَ', 37, 24, 23, 90, 446, 'آنها را نگهدارید که باید بازپرسی شوند!'),
(3813, 3813, 'مَا لَكُمْ لَا تَنَاصَرُونَ', 37, 25, 23, 90, 447, 'شما را چه شده که از هم یاری نمی‌طلبید؟!'),
(3814, 3814, 'بَلْ هُمُ الْيَوْمَ مُسْتَسْلِمُونَ', 37, 26, 23, 90, 447, 'ولی آنان در آن روز تسلیم قدرت خداوندند!'),
(3815, 3815, 'وَأَقْبَلَ بَعْضُهُمْ عَلَىٰ بَعْضٍ يَتَسَاءَلُونَ', 37, 27, 23, 90, 447, '(و در این حال) رو به یکدیگر کرده و از هم می‌پرسند...'),
(3816, 3816, 'قَالُوا إِنَّكُمْ كُنْتُمْ تَأْتُونَنَا عَنِ الْيَمِينِ', 37, 28, 23, 90, 447, 'گروهی (می‌گویند: «شما رهبران گمراهی بودید که به ظاهر) از طریق خیرخواهی و نیکی وارد شدید امّا جز فریب چیزی در کارتان نبود)!»'),
(3817, 3817, 'قَالُوا بَلْ لَمْ تَكُونُوا مُؤْمِنِينَ', 37, 29, 23, 90, 447, '(آنها در جواب) می‌گویند: «شما خودتان اهل ایمان نبودید (تقصیر ما چیست)؟!'),
(3818, 3818, 'وَمَا كَانَ لَنَا عَلَيْكُمْ مِنْ سُلْطَانٍ ۖ بَلْ كُنْتُمْ قَوْمًا طَاغِينَ', 37, 30, 23, 90, 447, 'ما هیچ گونه سلطه‌ای بر شما نداشتیم، بلکه شما خود قومی طغیانگر بودید!'),
(3819, 3819, 'فَحَقَّ عَلَيْنَا قَوْلُ رَبِّنَا ۖ إِنَّا لَذَائِقُونَ', 37, 31, 23, 90, 447, 'اکنون فرمان پروردگارمان بر همه ما مسلّم شده، و همگی از عذاب او می‌چشیم!'),
(3820, 3820, 'فَأَغْوَيْنَاكُمْ إِنَّا كُنَّا غَاوِينَ', 37, 32, 23, 90, 447, 'ما شما را گمراه کردیم، همان گونه که خود گمراه بودیم!'),
(3821, 3821, 'فَإِنَّهُمْ يَوْمَئِذٍ فِي الْعَذَابِ مُشْتَرِكُونَ', 37, 33, 23, 90, 447, '(آری) همه آنها = پیشوایان و پیروان گمراه‌ در آن روز در عذاب الهی مشترکند!'),
(3822, 3822, 'إِنَّا كَذَٰلِكَ نَفْعَلُ بِالْمُجْرِمِينَ', 37, 34, 23, 90, 447, 'ما این گونه با مجرمان رفتار می‌کنیم!'),
(3823, 3823, 'إِنَّهُمْ كَانُوا إِذَا قِيلَ لَهُمْ لَا إِلَٰهَ إِلَّا اللَّهُ يَسْتَكْبِرُونَ', 37, 35, 23, 90, 447, 'چرا که وقتی به آنها گفته می‌شد: «معبودی جز خدا وجود ندارد»، تکبّر و سرکشی می‌کردند...'),
(3824, 3824, 'وَيَقُولُونَ أَئِنَّا لَتَارِكُو آلِهَتِنَا لِشَاعِرٍ مَجْنُونٍ', 37, 36, 23, 90, 447, 'و پیوسته می‌گفتند: «آیا ما معبودان خود را بخاطر شاعری دیوانه رها کنیم؟!»'),
(3825, 3825, 'بَلْ جَاءَ بِالْحَقِّ وَصَدَّقَ الْمُرْسَلِينَ', 37, 37, 23, 90, 447, 'چنین نیست، او حقّ را آورده و پیامبران پیشین را تصدیق کرده است!'),
(3826, 3826, 'إِنَّكُمْ لَذَائِقُو الْعَذَابِ الْأَلِيمِ', 37, 38, 23, 90, 447, 'اما شما (مستکبران کوردل) بطور مسلّم عذاب دردناک (الهی) را خواهید چشید!'),
(3827, 3827, 'وَمَا تُجْزَوْنَ إِلَّا مَا كُنْتُمْ تَعْمَلُونَ', 37, 39, 23, 90, 447, 'و جز به آنچه انجام می‌دادید کیفر داده نمی‌شوید،'),
(3828, 3828, 'إِلَّا عِبَادَ اللَّهِ الْمُخْلَصِينَ', 37, 40, 23, 90, 447, 'جز بندگان مخلص خدا (که از این کیفرها برکنارند)!'),
(3829, 3829, 'أُولَٰئِكَ لَهُمْ رِزْقٌ مَعْلُومٌ', 37, 41, 23, 90, 447, 'برای آنان = بندگان مخلص‌ روزی معیّن و ویژه‌ای است،'),
(3830, 3830, 'فَوَاكِهُ ۖ وَهُمْ مُكْرَمُونَ', 37, 42, 23, 90, 447, 'میوه‌ها (ی گوناگون پر ارزش)، و آنها گرامی داشته می‌شوند...'),
(3831, 3831, 'فِي جَنَّاتِ النَّعِيمِ', 37, 43, 23, 90, 447, 'در باغهای پر نعمت بهشت؛'),
(3832, 3832, 'عَلَىٰ سُرُرٍ مُتَقَابِلِينَ', 37, 44, 23, 90, 447, 'در حالی که بر تختها رو به روی یکدیگر تکیه زده‌اند،'),
(3833, 3833, 'يُطَافُ عَلَيْهِمْ بِكَأْسٍ مِنْ مَعِينٍ', 37, 45, 23, 90, 447, 'و گرداگردشان قدحهای لبریز از شراب طهور را می‌گردانند؛'),
(3834, 3834, 'بَيْضَاءَ لَذَّةٍ لِلشَّارِبِينَ', 37, 46, 23, 90, 447, 'شرابی سفید و درخشنده، و لذّتبخش برای نوشندگان؛'),
(3835, 3835, 'لَا فِيهَا غَوْلٌ وَلَا هُمْ عَنْهَا يُنْزَفُونَ', 37, 47, 23, 90, 447, 'شرابی که نه در آن مایه تباهی عقل است و نه از آن مست می‌شوند!'),
(3836, 3836, 'وَعِنْدَهُمْ قَاصِرَاتُ الطَّرْفِ عِينٌ', 37, 48, 23, 90, 447, 'و نزد آنها همسرانی زیبا چشم است که جز به شوهران خود عشق نمی‌ورزند.'),
(3837, 3837, 'كَأَنَّهُنَّ بَيْضٌ مَكْنُونٌ', 37, 49, 23, 90, 447, 'گویی از (لطافت و سفیدی) همچون تخم‌مرغهایی هستند که (در زیر بال و پر مرغ) پنهان مانده (و دست انسانی هرگز آن را لمس نکرده است)!'),
(3838, 3838, 'فَأَقْبَلَ بَعْضُهُمْ عَلَىٰ بَعْضٍ يَتَسَاءَلُونَ', 37, 50, 23, 90, 447, '(در حالی که آنها غرق گفتگو هستند) بعضی رو به بعضی دیگر کرده می‌پرسند...'),
(3839, 3839, 'قَالَ قَائِلٌ مِنْهُمْ إِنِّي كَانَ لِي قَرِينٌ', 37, 51, 23, 90, 447, 'کسی از آنها می‌گوید: «من همنشینی داشتم...'),
(3840, 3840, 'يَقُولُ أَإِنَّكَ لَمِنَ الْمُصَدِّقِينَ', 37, 52, 23, 90, 448, 'که پیوسته می‌گفت: آیا (به راستی) تو این سخن را باور کرده‌ای...'),
(3841, 3841, 'أَإِذَا مِتْنَا وَكُنَّا تُرَابًا وَعِظَامًا أَإِنَّا لَمَدِينُونَ', 37, 53, 23, 90, 448, 'که وقتی ما مُردیم و به خاک و استخوان مبدّل شدیم، (بار دیگر) زنده می‌شویم و جزا داده خواهیم شد؟!»'),
(3842, 3842, 'قَالَ هَلْ أَنْتُمْ مُطَّلِعُونَ', 37, 54, 23, 90, 448, '(سپس) می‌گوید: «آیا شما می‌توانید از او خبری بگیرید؟»'),
(3843, 3843, 'فَاطَّلَعَ فَرَآهُ فِي سَوَاءِ الْجَحِيمِ', 37, 55, 23, 90, 448, 'اینجاست که نگاهی می‌کند، ناگهان او را در میان دوزخ می‌بیند.'),
(3844, 3844, 'قَالَ تَاللَّهِ إِنْ كِدْتَ لَتُرْدِينِ', 37, 56, 23, 90, 448, 'می‌گوید: «به خدا سوگند نزدیک بود مرا (نیز) به هلاکت بکشانی!'),
(3845, 3845, 'وَلَوْلَا نِعْمَةُ رَبِّي لَكُنْتُ مِنَ الْمُحْضَرِينَ', 37, 57, 23, 90, 448, 'و اگر نعمت پروردگارم نبود، من نیز از احضارشدگان (در دوزخ) بودم!'),
(3846, 3846, 'أَفَمَا نَحْنُ بِمَيِّتِينَ', 37, 58, 23, 90, 448, '(سپس به یاران خود می‌گوید: ای دوستان!) آیا ما هرگز نمی‌میریم (و در بهشت جاودانه خواهیم بود)،'),
(3847, 3847, 'إِلَّا مَوْتَتَنَا الْأُولَىٰ وَمَا نَحْنُ بِمُعَذَّبِينَ', 37, 59, 23, 90, 448, 'و جز همان مرگ اول، مرگی به سراغ ما نخواهد آمد، و ما هرگز عذاب نخواهیم شد.'),
(3848, 3848, 'إِنَّ هَٰذَا لَهُوَ الْفَوْزُ الْعَظِيمُ', 37, 60, 23, 90, 448, 'راستی این همان پیروزی بزرگ است!'),
(3849, 3849, 'لِمِثْلِ هَٰذَا فَلْيَعْمَلِ الْعَامِلُونَ', 37, 61, 23, 90, 448, 'آری، برای مثل این، باید عمل‌کنندگان عمل کنند!'),
(3850, 3850, 'أَذَٰلِكَ خَيْرٌ نُزُلًا أَمْ شَجَرَةُ الزَّقُّومِ', 37, 62, 23, 90, 448, 'آیا این (نعمتهای جاویدان بهشتی) بهتر است یا درخت (نفرت‌انگیز) زقّوم؟!'),
(3851, 3851, 'إِنَّا جَعَلْنَاهَا فِتْنَةً لِلظَّالِمِينَ', 37, 63, 23, 90, 448, 'ما آن را مایه درد و رنج ظالمان قرار دادیم!'),
(3852, 3852, 'إِنَّهَا شَجَرَةٌ تَخْرُجُ فِي أَصْلِ الْجَحِيمِ', 37, 64, 23, 90, 448, 'آن درختی است که از قعر جهنّم می‌روید!'),
(3853, 3853, 'طَلْعُهَا كَأَنَّهُ رُءُوسُ الشَّيَاطِينِ', 37, 65, 23, 90, 448, 'شکوفه آن مانند سرهای شیاطین است!'),
(3854, 3854, 'فَإِنَّهُمْ لَآكِلُونَ مِنْهَا فَمَالِئُونَ مِنْهَا الْبُطُونَ', 37, 66, 23, 90, 448, 'آنها = مجرمان‌ از آن می‌خورند و شکمها را از آن پر می‌کنند!'),
(3855, 3855, 'ثُمَّ إِنَّ لَهُمْ عَلَيْهَا لَشَوْبًا مِنْ حَمِيمٍ', 37, 67, 23, 90, 448, 'سپس روی آن آب داغ متعفّنی می‌نوشند!'),
(3856, 3856, 'ثُمَّ إِنَّ مَرْجِعَهُمْ لَإِلَى الْجَحِيمِ', 37, 68, 23, 90, 448, 'سپس بازگشت آنها به سوی جهنّم است!'),
(3857, 3857, 'إِنَّهُمْ أَلْفَوْا آبَاءَهُمْ ضَالِّينَ', 37, 69, 23, 90, 448, 'چرا که آنها پدران خود را گمراه یافتند،'),
(3858, 3858, 'فَهُمْ عَلَىٰ آثَارِهِمْ يُهْرَعُونَ', 37, 70, 23, 90, 448, 'با این حال به سرعت بدنبال آنان کشانده می‌شوند!'),
(3859, 3859, 'وَلَقَدْ ضَلَّ قَبْلَهُمْ أَكْثَرُ الْأَوَّلِينَ', 37, 71, 23, 90, 448, 'و قبل از آنها بیشتر پیشینیان (نیز) گمراه شدند!'),
(3860, 3860, 'وَلَقَدْ أَرْسَلْنَا فِيهِمْ مُنْذِرِينَ', 37, 72, 23, 90, 448, 'ما در میان آنها انذارکنندگانی فرستادیم،'),
(3861, 3861, 'فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ الْمُنْذَرِينَ', 37, 73, 23, 90, 448, 'ولی بنگر عاقبت انذارشوندگان چگونه بود!'),
(3862, 3862, 'إِلَّا عِبَادَ اللَّهِ الْمُخْلَصِينَ', 37, 74, 23, 90, 448, 'مگر بندگان مخلص خدا!'),
(3863, 3863, 'وَلَقَدْ نَادَانَا نُوحٌ فَلَنِعْمَ الْمُجِيبُونَ', 37, 75, 23, 90, 448, 'و نوح، ما را خواند (و ما دعای او را اجابت کردیم)؛ و چه خوب اجابت کننده‌ای هستیم!'),
(3864, 3864, 'وَنَجَّيْنَاهُ وَأَهْلَهُ مِنَ الْكَرْبِ الْعَظِيمِ', 37, 76, 23, 90, 448, 'و او و خاندانش را از اندوه بزرگ رهایی بخشیدیم،'),
(3865, 3865, 'وَجَعَلْنَا ذُرِّيَّتَهُ هُمُ الْبَاقِينَ', 37, 77, 23, 90, 449, 'و فرزندانش را همان بازماندگان (روی زمین) قرار دادیم،'),
(3866, 3866, 'وَتَرَكْنَا عَلَيْهِ فِي الْآخِرِينَ', 37, 78, 23, 90, 449, 'و نام نیک او را در میان امّتهای بعد باقی نهادیم.'),
(3867, 3867, 'سَلَامٌ عَلَىٰ نُوحٍ فِي الْعَالَمِينَ', 37, 79, 23, 90, 449, 'سلام بر نوح در میان جهانیان باد!'),
(3868, 3868, 'إِنَّا كَذَٰلِكَ نَجْزِي الْمُحْسِنِينَ', 37, 80, 23, 90, 449, 'ما این گونه نیکوکاران را پاداش می‌دهیم!'),
(3869, 3869, 'إِنَّهُ مِنْ عِبَادِنَا الْمُؤْمِنِينَ', 37, 81, 23, 90, 449, 'چرا که او از بندگان باایمان ما بود!'),
(3870, 3870, 'ثُمَّ أَغْرَقْنَا الْآخَرِينَ', 37, 82, 23, 90, 449, 'سپس دیگران = دشمنان او را غرق کردیم!'),
(3871, 3871, ' وَإِنَّ مِنْ شِيعَتِهِ لَإِبْرَاهِيمَ', 37, 83, 23, 90, 449, 'و از پیروان او ابراهیم بود؛'),
(3872, 3872, 'إِذْ جَاءَ رَبَّهُ بِقَلْبٍ سَلِيمٍ', 37, 84, 23, 90, 449, '(به خاطر بیاور) هنگامی را که با قلب سلیم به پیشگاه پروردگارش آمد؛'),
(3873, 3873, 'إِذْ قَالَ لِأَبِيهِ وَقَوْمِهِ مَاذَا تَعْبُدُونَ', 37, 85, 23, 90, 449, 'هنگامی که به پدر و قومش گفت: «اینها چیست که می‌پرستید؟!'),
(3874, 3874, 'أَئِفْكًا آلِهَةً دُونَ اللَّهِ تُرِيدُونَ', 37, 86, 23, 90, 449, 'آیا غیر از خدا به سراغ این معبودان دروغین می‌روید؟!'),
(3875, 3875, 'فَمَا ظَنُّكُمْ بِرَبِّ الْعَالَمِينَ', 37, 87, 23, 90, 449, 'شما درباره پروردگار عالمیان چه گمان می‌برید؟!'),
(3876, 3876, 'فَنَظَرَ نَظْرَةً فِي النُّجُومِ', 37, 88, 23, 90, 449, '(سپس) نگاهی به ستارگان افکند...'),
(3877, 3877, 'فَقَالَ إِنِّي سَقِيمٌ', 37, 89, 23, 90, 449, 'و گفت: «من بیمارم (و با شما به مراسم جشن نمی‌آیم)!»'),
(3878, 3878, 'فَتَوَلَّوْا عَنْهُ مُدْبِرِينَ', 37, 90, 23, 90, 449, 'آنها از او روی برتافته و به او پشت کردند (و بسرعت دور شدند.)'),
(3879, 3879, 'فَرَاغَ إِلَىٰ آلِهَتِهِمْ فَقَالَ أَلَا تَأْكُلُونَ', 37, 91, 23, 90, 449, '(او وارد بتخانه شد) مخفیانه نگاهی به معبودانشان کرد و از روی تمسخر گفت: «چرا (از این غذاها) نمی‌خورید؟!'),
(3880, 3880, 'مَا لَكُمْ لَا تَنْطِقُونَ', 37, 92, 23, 90, 449, '(اصلاً) چرا سخن نمی‌گویید؟!»'),
(3881, 3881, 'فَرَاغَ عَلَيْهِمْ ضَرْبًا بِالْيَمِينِ', 37, 93, 23, 90, 449, 'سپس بسوی آنها رفت و ضربه‌ای محکم با دست راست بر پیکر آنها فرود آورد (و جز بت بزرگ، همه را درهم شکست).'),
(3882, 3882, 'فَأَقْبَلُوا إِلَيْهِ يَزِفُّونَ', 37, 94, 23, 90, 449, 'آنها با سرعت به او روی آوردند.'),
(3883, 3883, 'قَالَ أَتَعْبُدُونَ مَا تَنْحِتُونَ', 37, 95, 23, 90, 449, 'گفت: «آیا چیزی را می‌پرستید که با دست خود می‌تراشید؟!'),
(3884, 3884, 'وَاللَّهُ خَلَقَكُمْ وَمَا تَعْمَلُونَ', 37, 96, 23, 90, 449, 'با اینکه خداوند هم شما را آفریده و هم بتهایی که می‌سازید!»'),
(3885, 3885, 'قَالُوا ابْنُوا لَهُ بُنْيَانًا فَأَلْقُوهُ فِي الْجَحِيمِ', 37, 97, 23, 90, 449, '(بت‌پرستان) گفتند: «بنای مرتفعی برای او بسازید و او را در جهنّمی از آتش بیفکنید!»'),
(3886, 3886, 'فَأَرَادُوا بِهِ كَيْدًا فَجَعَلْنَاهُمُ الْأَسْفَلِينَ', 37, 98, 23, 90, 449, 'آنها طرحی برای نابودی ابراهیم ریخته بودند، ولی ما آنان را پست و مغلوب ساختیم!'),
(3887, 3887, 'وَقَالَ إِنِّي ذَاهِبٌ إِلَىٰ رَبِّي سَيَهْدِينِ', 37, 99, 23, 90, 449, '(او از این مهلکه بسلامت بیرون آمد) و گفت: «من به سوی پروردگارم می‌روم، او مرا هدایت خواهد کرد!'),
(3888, 3888, 'رَبِّ هَبْ لِي مِنَ الصَّالِحِينَ', 37, 100, 23, 90, 449, 'پروردگارا! به من از صالحان = فرزندان صالح‌ ببخش!»'),
(3889, 3889, 'فَبَشَّرْنَاهُ بِغُلَامٍ حَلِيمٍ', 37, 101, 23, 90, 449, 'ما او = ابراهیم‌ را به نوجوانی بردبار و صبور بشارت دادیم!'),
(3890, 3890, 'فَلَمَّا بَلَغَ مَعَهُ السَّعْيَ قَالَ يَا بُنَيَّ إِنِّي أَرَىٰ فِي الْمَنَامِ أَنِّي أَذْبَحُكَ فَانْظُرْ مَاذَا تَرَىٰ ۚ قَالَ يَا أَبَتِ افْعَلْ مَا تُؤْمَرُ ۖ سَتَجِدُنِي إِنْ شَاءَ اللَّهُ مِنَ الصَّابِرِينَ', 37, 102, 23, 90, 449, 'هنگامی که با او به مقام سعی و کوشش رسید، گفت: «پسرم! من در خواب دیدم که تو را ذبح می‌کنم، نظر تو چیست؟» گفت «پدرم! هر چه دستور داری اجرا کن، به خواست خدا مرا از صابران خواهی یافت!»'),
(3891, 3891, 'فَلَمَّا أَسْلَمَا وَتَلَّهُ لِلْجَبِينِ', 37, 103, 23, 90, 450, 'هنگامی که هر دو تسلیم شدند و ابراهیم جبین او را بر خاک نهاد...'),
(3892, 3892, 'وَنَادَيْنَاهُ أَنْ يَا إِبْرَاهِيمُ', 37, 104, 23, 90, 450, 'او را ندا دادیم که: «ای ابراهیم!'),
(3893, 3893, 'قَدْ صَدَّقْتَ الرُّؤْيَا ۚ إِنَّا كَذَٰلِكَ نَجْزِي الْمُحْسِنِينَ', 37, 105, 23, 90, 450, 'آن رؤیا را تحقق بخشیدی (و به مأموریت خود عمل کردی)!» ما این گونه، نیکوکاران را جزا می‌دهیم!'),
(3894, 3894, 'إِنَّ هَٰذَا لَهُوَ الْبَلَاءُ الْمُبِينُ', 37, 106, 23, 90, 450, 'این مسلّماً همان امتحان آشکار است!'),
(3895, 3895, 'وَفَدَيْنَاهُ بِذِبْحٍ عَظِيمٍ', 37, 107, 23, 90, 450, 'ما ذبح عظیمی را فدای او کردیم،'),
(3896, 3896, 'وَتَرَكْنَا عَلَيْهِ فِي الْآخِرِينَ', 37, 108, 23, 90, 450, 'و نام نیک او را در امّتهای بعد باقی نهادیم!'),
(3897, 3897, 'سَلَامٌ عَلَىٰ إِبْرَاهِيمَ', 37, 109, 23, 90, 450, 'سلام بر ابراهیم!'),
(3898, 3898, 'كَذَٰلِكَ نَجْزِي الْمُحْسِنِينَ', 37, 110, 23, 90, 450, 'این گونه نیکوکاران را پاداش می‌دهیم!'),
(3899, 3899, 'إِنَّهُ مِنْ عِبَادِنَا الْمُؤْمِنِينَ', 37, 111, 23, 90, 450, 'او از بندگان باایمان ما است!'),
(3900, 3900, 'وَبَشَّرْنَاهُ بِإِسْحَاقَ نَبِيًّا مِنَ الصَّالِحِينَ', 37, 112, 23, 90, 450, 'ما او را به اسحاق -پیامبری از شایستگان- بشارت دادیم!'),
(3901, 3901, 'وَبَارَكْنَا عَلَيْهِ وَعَلَىٰ إِسْحَاقَ ۚ وَمِنْ ذُرِّيَّتِهِمَا مُحْسِنٌ وَظَالِمٌ لِنَفْسِهِ مُبِينٌ', 37, 113, 23, 90, 450, 'ما به او و اسحاق برکت دادیم؛ و از دودمان آن دو، افرادی بودند نیکوکار و افرادی آشکارا به خود ستم کردند!'),
(3902, 3902, 'وَلَقَدْ مَنَنَّا عَلَىٰ مُوسَىٰ وَهَارُونَ', 37, 114, 23, 90, 450, 'ما به موسی و هارون نعمت بخشیدیم!'),
(3903, 3903, 'وَنَجَّيْنَاهُمَا وَقَوْمَهُمَا مِنَ الْكَرْبِ الْعَظِيمِ', 37, 115, 23, 90, 450, 'و آن دو و قومشان را از اندوه بزرگ نجات دادیم!'),
(3904, 3904, 'وَنَصَرْنَاهُمْ فَكَانُوا هُمُ الْغَالِبِينَ', 37, 116, 23, 90, 450, 'و آنها را یاری کردیم تا بر دشمنان خود پیروز شدند!'),
(3905, 3905, 'وَآتَيْنَاهُمَا الْكِتَابَ الْمُسْتَبِينَ', 37, 117, 23, 90, 450, 'ما به آن دو، کتاب روشنگر دادیم،'),
(3906, 3906, 'وَهَدَيْنَاهُمَا الصِّرَاطَ الْمُسْتَقِيمَ', 37, 118, 23, 90, 450, 'و آن دو را به راه راست هدایت نمودیم!'),
(3907, 3907, 'وَتَرَكْنَا عَلَيْهِمَا فِي الْآخِرِينَ', 37, 119, 23, 90, 450, 'و نام نیکشان را در اقوام بعد باقی گذاردیم!'),
(3908, 3908, 'سَلَامٌ عَلَىٰ مُوسَىٰ وَهَارُونَ', 37, 120, 23, 90, 450, 'سلام بر موسی و هارون!'),
(3909, 3909, 'إِنَّا كَذَٰلِكَ نَجْزِي الْمُحْسِنِينَ', 37, 121, 23, 90, 450, 'ما این گونه نیکوکاران را پاداش می‌دهیم!'),
(3910, 3910, 'إِنَّهُمَا مِنْ عِبَادِنَا الْمُؤْمِنِينَ', 37, 122, 23, 90, 450, 'آن دو از بندگان مؤمن ما بودند!'),
(3911, 3911, 'وَإِنَّ إِلْيَاسَ لَمِنَ الْمُرْسَلِينَ', 37, 123, 23, 90, 450, 'و الیاس از رسولان (ما) بود!'),
(3912, 3912, 'إِذْ قَالَ لِقَوْمِهِ أَلَا تَتَّقُونَ', 37, 124, 23, 90, 450, 'به خاطر بیاور هنگامی را که به قومش گفت: «آیا تقوا پیشه نمی‌کنید؟!'),
(3913, 3913, 'أَتَدْعُونَ بَعْلًا وَتَذَرُونَ أَحْسَنَ الْخَالِقِينَ', 37, 125, 23, 90, 450, 'آیا بت «بعل» را می‌خوانید و بهترین آفریدگارها را رها می‌سازید؟!'),
(3914, 3914, 'اللَّهَ رَبَّكُمْ وَرَبَّ آبَائِكُمُ الْأَوَّلِينَ', 37, 126, 23, 90, 450, 'خدایی که پروردگار شما و پروردگار نیاکان شماست!»');
INSERT INTO `Quran` (`rowid`, `id`, `Arabic`, `Chapter`, `Verse`, `Juz`, `Hizb`, `Page`, `tr`) VALUES
(3915, 3915, 'فَكَذَّبُوهُ فَإِنَّهُمْ لَمُحْضَرُونَ', 37, 127, 23, 90, 451, 'امّا آنها او را تکذیب کردند؛ ولی به یقین همگی (در دادگاه عدل الهی) احضار می‌شوند!'),
(3916, 3916, 'إِلَّا عِبَادَ اللَّهِ الْمُخْلَصِينَ', 37, 128, 23, 90, 451, 'مگر بندگان مخلص خدا!'),
(3917, 3917, 'وَتَرَكْنَا عَلَيْهِ فِي الْآخِرِينَ', 37, 129, 23, 90, 451, 'ما نام نیک او را در میان امّتهای بعد باقی گذاردیم!'),
(3918, 3918, 'سَلَامٌ عَلَىٰ إِلْ يَاسِينَ', 37, 130, 23, 90, 451, 'سلام بر الیاسین!'),
(3919, 3919, 'إِنَّا كَذَٰلِكَ نَجْزِي الْمُحْسِنِينَ', 37, 131, 23, 90, 451, 'ما این گونه نیکوکاران را پاداش می‌دهیم!'),
(3920, 3920, 'إِنَّهُ مِنْ عِبَادِنَا الْمُؤْمِنِينَ', 37, 132, 23, 90, 451, 'او از بندگان مؤمن ما است!'),
(3921, 3921, 'وَإِنَّ لُوطًا لَمِنَ الْمُرْسَلِينَ', 37, 133, 23, 90, 451, 'و لوط از رسولان (ما) است!'),
(3922, 3922, 'إِذْ نَجَّيْنَاهُ وَأَهْلَهُ أَجْمَعِينَ', 37, 134, 23, 90, 451, 'و به خاطر بیاور زمانی را که او و خاندانش را همگی نجات دادیم،'),
(3923, 3923, 'إِلَّا عَجُوزًا فِي الْغَابِرِينَ', 37, 135, 23, 90, 451, 'مگر پیرزنی که از بازماندگان بود (و به سرنوشت آنان گرفتار شد)!'),
(3924, 3924, 'ثُمَّ دَمَّرْنَا الْآخَرِينَ', 37, 136, 23, 90, 451, 'سپس بقیه را نابود کردیم!'),
(3925, 3925, 'وَإِنَّكُمْ لَتَمُرُّونَ عَلَيْهِمْ مُصْبِحِينَ', 37, 137, 23, 90, 451, 'و شما پیوسته صبحگاهان از کنار (ویرانه‌های شهرهای) آنها می‌گذرید...'),
(3926, 3926, 'وَبِاللَّيْلِ ۗ أَفَلَا تَعْقِلُونَ', 37, 138, 23, 90, 451, 'و (همچنین) شبانگاه؛ آیا نمی‌اندیشید؟!'),
(3927, 3927, 'وَإِنَّ يُونُسَ لَمِنَ الْمُرْسَلِينَ', 37, 139, 23, 90, 451, 'و یونس از رسولان (ما) است!'),
(3928, 3928, 'إِذْ أَبَقَ إِلَى الْفُلْكِ الْمَشْحُونِ', 37, 140, 23, 90, 451, 'به خاطر بیاور زمانی را که به سوی کشتی پر (از جمعیّت و بار) فرار کرد.'),
(3929, 3929, 'فَسَاهَمَ فَكَانَ مِنَ الْمُدْحَضِينَ', 37, 141, 23, 90, 451, 'و با آنها قرعه افکند، (و قرعه به نام او افتاد و) مغلوب شد!'),
(3930, 3930, 'فَالْتَقَمَهُ الْحُوتُ وَهُوَ مُلِيمٌ', 37, 142, 23, 90, 451, '(او را به دریا افکندند) و ماهی عظیمی او را بلعید، در حالی که مستحقّ سرزنش بود!'),
(3931, 3931, 'فَلَوْلَا أَنَّهُ كَانَ مِنَ الْمُسَبِّحِينَ', 37, 143, 23, 90, 451, 'و اگر او از تسبیح‌کنندگان نبود...'),
(3932, 3932, 'لَلَبِثَ فِي بَطْنِهِ إِلَىٰ يَوْمِ يُبْعَثُونَ', 37, 144, 23, 90, 451, 'تا روز قیامت در شکم ماهی می‌ماند!'),
(3933, 3933, ' فَنَبَذْنَاهُ بِالْعَرَاءِ وَهُوَ سَقِيمٌ', 37, 145, 23, 91, 451, '(به هر حال ما او را رهایی بخشیدیم و) او را در یک سرزمین خشک خالی از گیاه افکندیم در حالی که بیمار بود!'),
(3934, 3934, 'وَأَنْبَتْنَا عَلَيْهِ شَجَرَةً مِنْ يَقْطِينٍ', 37, 146, 23, 91, 451, 'و بوته کدوئی بر او رویاندیم (تا در سایه برگهای پهن و مرطوبش آرامش یابد)!'),
(3935, 3935, 'وَأَرْسَلْنَاهُ إِلَىٰ مِائَةِ أَلْفٍ أَوْ يَزِيدُونَ', 37, 147, 23, 91, 451, 'و او را به سوی جمعیّت یکصد هزار نفری -یا بیشتر- فرستادیم!'),
(3936, 3936, 'فَآمَنُوا فَمَتَّعْنَاهُمْ إِلَىٰ حِينٍ', 37, 148, 23, 91, 451, 'آنها ایمان آوردند، از این رو تا مدّت معلومی آنان را از مواهب زندگی بهره‌مند ساختیم!'),
(3937, 3937, 'فَاسْتَفْتِهِمْ أَلِرَبِّكَ الْبَنَاتُ وَلَهُمُ الْبَنُونَ', 37, 149, 23, 91, 451, 'از آنان بپرس: آیا پروردگارت دخترانی دارد و پسران از آن آنهاست؟!'),
(3938, 3938, 'أَمْ خَلَقْنَا الْمَلَائِكَةَ إِنَاثًا وَهُمْ شَاهِدُونَ', 37, 150, 23, 91, 451, 'آیا ما فرشتگان را مؤنث آفریدیم و آنها ناظر بودند؟!'),
(3939, 3939, 'أَلَا إِنَّهُمْ مِنْ إِفْكِهِمْ لَيَقُولُونَ', 37, 151, 23, 91, 451, 'بدانید آنها با این تهمت بزرگشان می‌گویند:'),
(3940, 3940, 'وَلَدَ اللَّهُ وَإِنَّهُمْ لَكَاذِبُونَ', 37, 152, 23, 91, 451, '«خداوند فرزند آورده!» ولی آنها به یقین دروغ می‌گویند!'),
(3941, 3941, 'أَصْطَفَى الْبَنَاتِ عَلَى الْبَنِينَ', 37, 153, 23, 91, 451, 'آیا دختران را بر پسران ترجیح داده است؟!'),
(3942, 3942, 'مَا لَكُمْ كَيْفَ تَحْكُمُونَ', 37, 154, 23, 91, 452, 'شما را چه شده است؟! چگونه حکم می‌کنید؟! (هیچ می‌فهمید چه می‌گویید؟!)'),
(3943, 3943, 'أَفَلَا تَذَكَّرُونَ', 37, 155, 23, 91, 452, 'آیا متذکّر نمی‌شوید؟!'),
(3944, 3944, 'أَمْ لَكُمْ سُلْطَانٌ مُبِينٌ', 37, 156, 23, 91, 452, 'یا شما دلیل روشنی در این باره دارید؟'),
(3945, 3945, 'فَأْتُوا بِكِتَابِكُمْ إِنْ كُنْتُمْ صَادِقِينَ', 37, 157, 23, 91, 452, 'کتابتان را بیاورید اگر راست می‌گویید!'),
(3946, 3946, 'وَجَعَلُوا بَيْنَهُ وَبَيْنَ الْجِنَّةِ نَسَبًا ۚ وَلَقَدْ عَلِمَتِ الْجِنَّةُ إِنَّهُمْ لَمُحْضَرُونَ', 37, 158, 23, 91, 452, 'آنها = مشرکان‌ میان او = خداوند و جنّ، (خویشاوندی و) نسبتی قائل شدند؛ در حالی که جنّیان بخوبی می‌دانند که این بت‌پرستان در دادگاه الهی احضار می‌شوند!'),
(3947, 3947, 'سُبْحَانَ اللَّهِ عَمَّا يَصِفُونَ', 37, 159, 23, 91, 452, 'منزّه است خداوند از آنچه توصیف می‌کنند،'),
(3948, 3948, 'إِلَّا عِبَادَ اللَّهِ الْمُخْلَصِينَ', 37, 160, 23, 91, 452, 'مگر بندگان مخلص خدا!'),
(3949, 3949, 'فَإِنَّكُمْ وَمَا تَعْبُدُونَ', 37, 161, 23, 91, 452, 'شما و آنچه را پرستش می‌کنید،'),
(3950, 3950, 'مَا أَنْتُمْ عَلَيْهِ بِفَاتِنِينَ', 37, 162, 23, 91, 452, 'هرگز نمی‌توانید کسی را (با آن) فریب دهید،'),
(3951, 3951, 'إِلَّا مَنْ هُوَ صَالِ الْجَحِيمِ', 37, 163, 23, 91, 452, 'مگر آنها که در آتش دوزخ وارد می‌شوند!'),
(3952, 3952, 'وَمَا مِنَّا إِلَّا لَهُ مَقَامٌ مَعْلُومٌ', 37, 164, 23, 91, 452, 'و هیچ یک از ما نیست جز آنکه مقام معلومی دارد؛'),
(3953, 3953, 'وَإِنَّا لَنَحْنُ الصَّافُّونَ', 37, 165, 23, 91, 452, 'و ما همگی (برای اطاعت فرمان خداوند) به صف ایستاده‌ایم؛'),
(3954, 3954, 'وَإِنَّا لَنَحْنُ الْمُسَبِّحُونَ', 37, 166, 23, 91, 452, 'و ما همه تسبیح‌گوی او هستیم!'),
(3955, 3955, 'وَإِنْ كَانُوا لَيَقُولُونَ', 37, 167, 23, 91, 452, 'آنها پیوسته می‌گفتند:'),
(3956, 3956, 'لَوْ أَنَّ عِنْدَنَا ذِكْرًا مِنَ الْأَوَّلِينَ', 37, 168, 23, 91, 452, '«اگر یکی از کتابهای پیشینیان نزد ما بود،'),
(3957, 3957, 'لَكُنَّا عِبَادَ اللَّهِ الْمُخْلَصِينَ', 37, 169, 23, 91, 452, 'به یقین، ما بندگان مخلص خدا بودیم!»'),
(3958, 3958, 'فَكَفَرُوا بِهِ ۖ فَسَوْفَ يَعْلَمُونَ', 37, 170, 23, 91, 452, '(امّا هنگامی که این کتاب بزرگ آسمانی بر آنها نازل شد،) به آن کافر شدند؛ ولی بزودی (نتیجه کار خود را) خواهند دانست!'),
(3959, 3959, 'وَلَقَدْ سَبَقَتْ كَلِمَتُنَا لِعِبَادِنَا الْمُرْسَلِينَ', 37, 171, 23, 91, 452, 'وعده قطعی ما برای بندگان فرستاده ما از پیش مسلّم شده...'),
(3960, 3960, 'إِنَّهُمْ لَهُمُ الْمَنْصُورُونَ', 37, 172, 23, 91, 452, 'که آنان یاری شدگانند،'),
(3961, 3961, 'وَإِنَّ جُنْدَنَا لَهُمُ الْغَالِبُونَ', 37, 173, 23, 91, 452, 'و لشکر ما پیروزند!'),
(3962, 3962, 'فَتَوَلَّ عَنْهُمْ حَتَّىٰ حِينٍ', 37, 174, 23, 91, 452, 'از آنها = کافران‌ روی بگردان تا زمان معیّنی (که فرمان جهاد فرارسد)!'),
(3963, 3963, 'وَأَبْصِرْهُمْ فَسَوْفَ يُبْصِرُونَ', 37, 175, 23, 91, 452, 'و وضع آنها را بنگر (چه بی‌محتواست) اما بزودی (نتیجه اعمال خود را) می‌بینند!'),
(3964, 3964, 'أَفَبِعَذَابِنَا يَسْتَعْجِلُونَ', 37, 176, 23, 91, 452, 'آیا آنها برای عذاب ما شتاب می‌کنند؟!'),
(3965, 3965, 'فَإِذَا نَزَلَ بِسَاحَتِهِمْ فَسَاءَ صَبَاحُ الْمُنْذَرِينَ', 37, 177, 23, 91, 452, 'امّا هنگامی که عذاب ما در آستانه خانه‌هایشان فرود آید، انذارشدگان صبحگاه بدی خواهند داشت!'),
(3966, 3966, 'وَتَوَلَّ عَنْهُمْ حَتَّىٰ حِينٍ', 37, 178, 23, 91, 452, 'از آنان روی بگردان تا زمان معیّنی!'),
(3967, 3967, 'وَأَبْصِرْ فَسَوْفَ يُبْصِرُونَ', 37, 179, 23, 91, 452, 'و وضع کارشان را ببین؛ آنها نیز به زودی (نتیجه اعمال خود را) می‌بینند!'),
(3968, 3968, 'سُبْحَانَ رَبِّكَ رَبِّ الْعِزَّةِ عَمَّا يَصِفُونَ', 37, 180, 23, 91, 452, 'منزّه است پروردگار تو، پروردگار عزّت (و قدرت) از آنچه آنان توصیف می‌کنند.'),
(3969, 3969, 'وَسَلَامٌ عَلَى الْمُرْسَلِينَ', 37, 181, 23, 91, 452, 'و سلام بر رسولان!'),
(3970, 3970, 'وَالْحَمْدُ لِلَّهِ رَبِّ الْعَالَمِينَ', 37, 182, 23, 91, 452, 'حمد و ستایش مخصوص خداوندی است که پروردگار جهانیان است!'),
(3971, 3971, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ ص ۚ وَالْقُرْآنِ ذِي الذِّكْرِ', 38, 1, 23, 91, 453, 'ص، سوگند به قرآنی که دارای ذکر است (که این کتاب، معجزه الهی است).'),
(3972, 3972, 'بَلِ الَّذِينَ كَفَرُوا فِي عِزَّةٍ وَشِقَاقٍ', 38, 2, 23, 91, 453, 'ولی کافران گرفتار غرور اختلافند!'),
(3973, 3973, 'كَمْ أَهْلَكْنَا مِنْ قَبْلِهِمْ مِنْ قَرْنٍ فَنَادَوْا وَلَاتَ حِينَ مَنَاصٍ', 38, 3, 23, 91, 453, 'چه بسیار اقوامی را که پیش از آنها هلاک کردیم؛ و به هنگام نزول عذاب فریاد می‌زدند (و کمک می‌خواستند) ولی وقت نجات گذشته بود!'),
(3974, 3974, 'وَعَجِبُوا أَنْ جَاءَهُمْ مُنْذِرٌ مِنْهُمْ ۖ وَقَالَ الْكَافِرُونَ هَٰذَا سَاحِرٌ كَذَّابٌ', 38, 4, 23, 91, 453, 'آنها تعجّب کردند که پیامبر بیم‌دهنده‌ای از میان آنان به سویشان آمده؛ و کافران گفتند: این ساحر بسیار دروغگویی است!'),
(3975, 3975, 'أَجَعَلَ الْآلِهَةَ إِلَٰهًا وَاحِدًا ۖ إِنَّ هَٰذَا لَشَيْءٌ عُجَابٌ', 38, 5, 23, 91, 453, 'آیا او بجای اینهمه خدایان، خدای واحدی قرار داده؟! این براستی چیز عجیبی است!»'),
(3976, 3976, 'وَانْطَلَقَ الْمَلَأُ مِنْهُمْ أَنِ امْشُوا وَاصْبِرُوا عَلَىٰ آلِهَتِكُمْ ۖ إِنَّ هَٰذَا لَشَيْءٌ يُرَادُ', 38, 6, 23, 91, 453, 'سرکردگان آنها بیرون آمدند و گفتند: «بروید و خدایانتان را محکم بچسبید، این چیزی است که خواسته‌اند (شما را گمراه کنند)!'),
(3977, 3977, 'مَا سَمِعْنَا بِهَٰذَا فِي الْمِلَّةِ الْآخِرَةِ إِنْ هَٰذَا إِلَّا اخْتِلَاقٌ', 38, 7, 23, 91, 453, 'ما هرگز چنین چیزی در آیین دیگری نشنیده‌ایم؛ این تنها یک آئین ساختگی است!'),
(3978, 3978, 'أَأُنْزِلَ عَلَيْهِ الذِّكْرُ مِنْ بَيْنِنَا ۚ بَلْ هُمْ فِي شَكٍّ مِنْ ذِكْرِي ۖ بَلْ لَمَّا يَذُوقُوا عَذَابِ', 38, 8, 23, 91, 453, 'آیا از میان همه ما، قرآن تنها بر او = محمّد نازل شده؟!» آنها در حقیقت در اصل وحی من تردید دارند، بلکه آنان هنوز عذاب الهی را نچشیده‌اند (که این چنین گستاخانه سخن می‌گویند)!'),
(3979, 3979, 'أَمْ عِنْدَهُمْ خَزَائِنُ رَحْمَةِ رَبِّكَ الْعَزِيزِ الْوَهَّابِ', 38, 9, 23, 91, 453, 'مگر خزاین رحمت پروردگار توانا و بخشنده‌ات نزد آنهاست (تا به هر کس میل دارند بدهند)؟!'),
(3980, 3980, 'أَمْ لَهُمْ مُلْكُ السَّمَاوَاتِ وَالْأَرْضِ وَمَا بَيْنَهُمَا ۖ فَلْيَرْتَقُوا فِي الْأَسْبَابِ', 38, 10, 23, 91, 453, 'یا اینکه مالکیّت و حاکمیّت آسمانها و زمین و آنچه میان این دو است از آن آنهاست؟! (اگر چنین است) با هر وسیله ممکن به آسمانها بروند (و جلو نزول وحی را بر قلب پاک محمّد بگیرند)!'),
(3981, 3981, 'جُنْدٌ مَا هُنَالِكَ مَهْزُومٌ مِنَ الْأَحْزَابِ', 38, 11, 23, 91, 453, '(آری) اینها لشکر کوچک شکست‌خورده‌ای از احزابند!'),
(3982, 3982, 'كَذَّبَتْ قَبْلَهُمْ قَوْمُ نُوحٍ وَعَادٌ وَفِرْعَوْنُ ذُو الْأَوْتَادِ', 38, 12, 23, 91, 453, 'پیش از آنان قوم نوح و عاد و فرعون صاحب قدرت (پیامبران ما را) تکذیب کردند!'),
(3983, 3983, 'وَثَمُودُ وَقَوْمُ لُوطٍ وَأَصْحَابُ الْأَيْكَةِ ۚ أُولَٰئِكَ الْأَحْزَابُ', 38, 13, 23, 91, 453, 'و (نیز) قوم ثمود و لوط و اصحاب الأیکه = قوم شعیب‌، اینها احزابی بودند (که به تکذیب پیامبران برخاستند)!'),
(3984, 3984, 'إِنْ كُلٌّ إِلَّا كَذَّبَ الرُّسُلَ فَحَقَّ عِقَابِ', 38, 14, 23, 91, 453, 'هر یک (از این گروه‌ها) رسولان را تکذیب کردند، و عذاب الهی درباره آنان تحقق یافت!'),
(3985, 3985, 'وَمَا يَنْظُرُ هَٰؤُلَاءِ إِلَّا صَيْحَةً وَاحِدَةً مَا لَهَا مِنْ فَوَاقٍ', 38, 15, 23, 91, 453, 'اینها (با این اعمالشان) جز یک صیحه آسمانی را انتظار نمی‌کشند که هیچ مهلت و بازگشتی برای آن وجود ندارد (و همگی را نابود می‌سازد)!'),
(3986, 3986, 'وَقَالُوا رَبَّنَا عَجِّلْ لَنَا قِطَّنَا قَبْلَ يَوْمِ الْحِسَابِ', 38, 16, 23, 91, 453, 'آنها (از روی خیره‌سری) گفتند: «پروردگارا! بهره ما را از عذاب هر چه زودتر قبل از روز حساب به ما ده!»'),
(3987, 3987, 'اصْبِرْ عَلَىٰ مَا يَقُولُونَ وَاذْكُرْ عَبْدَنَا دَاوُودَ ذَا الْأَيْدِ ۖ إِنَّهُ أَوَّابٌ', 38, 17, 23, 91, 454, 'در برابر آنچه می‌گویند شکیبا باش، و به خاطر بیاور بنده ما داوود صاحب قدرت را، که او بسیار توبه‌کننده بود!'),
(3988, 3988, 'إِنَّا سَخَّرْنَا الْجِبَالَ مَعَهُ يُسَبِّحْنَ بِالْعَشِيِّ وَالْإِشْرَاقِ', 38, 18, 23, 91, 454, 'ما کوه‌ها را مسخّر او ساختیم که هر شامگاه و صبحگاه با او تسبیح می‌گفتند!'),
(3989, 3989, 'وَالطَّيْرَ مَحْشُورَةً ۖ كُلٌّ لَهُ أَوَّابٌ', 38, 19, 23, 91, 454, 'پرندگان را نیز دسته جمعی مسخّر او کردیم (تا همراه او تسبیح خدا گویند)؛ و همه اینها بازگشت‌کننده به سوی او بودند!'),
(3990, 3990, 'وَشَدَدْنَا مُلْكَهُ وَآتَيْنَاهُ الْحِكْمَةَ وَفَصْلَ الْخِطَابِ', 38, 20, 23, 91, 454, 'و حکومت او را استحکام بخشیدیم، (هم) دانش به او دادیم و (هم) داوری عادلانه!'),
(3991, 3991, ' وَهَلْ أَتَاكَ نَبَأُ الْخَصْمِ إِذْ تَسَوَّرُوا الْمِحْرَابَ', 38, 21, 23, 91, 454, 'آیا داستان شاکیان هنگامی که از محراب (داوود) بالا رفتند به تو رسیده است؟!'),
(3992, 3992, 'إِذْ دَخَلُوا عَلَىٰ دَاوُودَ فَفَزِعَ مِنْهُمْ ۖ قَالُوا لَا تَخَفْ ۖ خَصْمَانِ بَغَىٰ بَعْضُنَا عَلَىٰ بَعْضٍ فَاحْكُمْ بَيْنَنَا بِالْحَقِّ وَلَا تُشْطِطْ وَاهْدِنَا إِلَىٰ سَوَاءِ الصِّرَاطِ', 38, 22, 23, 91, 454, 'در آن هنگام که (بی‌مقدمه) بر او وارد شدند و او از دیدن آنها وحشت کرد؛ گفتند: «نترس، دو نفر شاکی هستیم که یکی از ما بر دیگری ستم کرده؛ اکنون در میان ما بحق داوری کن و ستم روا مدار و ما را به راه راست هدایت کن!'),
(3993, 3993, 'إِنَّ هَٰذَا أَخِي لَهُ تِسْعٌ وَتِسْعُونَ نَعْجَةً وَلِيَ نَعْجَةٌ وَاحِدَةٌ فَقَالَ أَكْفِلْنِيهَا وَعَزَّنِي فِي الْخِطَابِ', 38, 23, 23, 91, 454, 'این برادر من است؛ و او نود و نه میش دارد و من یکی بیش ندارم امّا او اصرار می‌کند که: این یکی را هم به من واگذار؛ و در سخن بر من غلبه کرده است!»'),
(3994, 3994, 'قَالَ لَقَدْ ظَلَمَكَ بِسُؤَالِ نَعْجَتِكَ إِلَىٰ نِعَاجِهِ ۖ وَإِنَّ كَثِيرًا مِنَ الْخُلَطَاءِ لَيَبْغِي بَعْضُهُمْ عَلَىٰ بَعْضٍ إِلَّا الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ وَقَلِيلٌ مَا هُمْ ۗ وَظَنَّ دَاوُودُ أَنَّمَا فَتَنَّاهُ فَاسْتَغْفَرَ رَبَّهُ وَخَرَّ رَاكِعًا وَأَنَابَ ۩', 38, 24, 23, 91, 454, '(داوود) گفت: «مسلّماً او با درخواست یک میش تو برای افزودن آن به میشهایش، بر تو ستم نموده؛ و بسیاری از شریکان (و دوستان) به یکدیگر ستم می‌کنند، مگر کسانی که ایمان آورده و اعمال صالح انجام داده‌اند؛ امّا عدّه آنان کم است!» داوود دانست که ما او را (با این ماجرا) آزموده‌ایم، از این رو از پروردگارش طلب آمرزش نمود و به سجده افتاد و توبه کرد.'),
(3995, 3995, 'فَغَفَرْنَا لَهُ ذَٰلِكَ ۖ وَإِنَّ لَهُ عِنْدَنَا لَزُلْفَىٰ وَحُسْنَ مَآبٍ', 38, 25, 23, 91, 454, 'ما این عمل را بر او بخشیدیم؛ و او نزد ما دارای مقامی والا و سرانجامی نیکوست!'),
(3996, 3996, 'يَا دَاوُودُ إِنَّا جَعَلْنَاكَ خَلِيفَةً فِي الْأَرْضِ فَاحْكُمْ بَيْنَ النَّاسِ بِالْحَقِّ وَلَا تَتَّبِعِ الْهَوَىٰ فَيُضِلَّكَ عَنْ سَبِيلِ اللَّهِ ۚ إِنَّ الَّذِينَ يَضِلُّونَ عَنْ سَبِيلِ اللَّهِ لَهُمْ عَذَابٌ شَدِيدٌ بِمَا نَسُوا يَوْمَ الْحِسَابِ', 38, 26, 23, 91, 454, 'ای داوود! ما تو را خلیفه و (نماینده خود) در زمین قرار دادیم؛ پس در میان مردم بحق داوری کن، و از هوای نفس پیروی مکن که تو را از راه خدا منحرف سازد؛ کسانی که از راه خدا گمراه شوند، عذاب شدیدی بخاطر فراموش کردن روز حساب دارند!'),
(3997, 3997, 'وَمَا خَلَقْنَا السَّمَاءَ وَالْأَرْضَ وَمَا بَيْنَهُمَا بَاطِلًا ۚ ذَٰلِكَ ظَنُّ الَّذِينَ كَفَرُوا ۚ فَوَيْلٌ لِلَّذِينَ كَفَرُوا مِنَ النَّارِ', 38, 27, 23, 91, 455, 'ما آسمان و زمین و آنچه را میان آنهاست بیهوده نیافریدیم؛ این گمان کافران است؛ وای بر کافران از آتش (دوزخ)!'),
(3998, 3998, 'أَمْ نَجْعَلُ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ كَالْمُفْسِدِينَ فِي الْأَرْضِ أَمْ نَجْعَلُ الْمُتَّقِينَ كَالْفُجَّارِ', 38, 28, 23, 91, 455, 'آیا کسانی را که ایمان آورده و کارهای شایسته انجام داده‌اند همچون مفسدان در زمین قرار می‌دهیم، یا پرهیزگاران را همچون فاجران؟!'),
(3999, 3999, 'كِتَابٌ أَنْزَلْنَاهُ إِلَيْكَ مُبَارَكٌ لِيَدَّبَّرُوا آيَاتِهِ وَلِيَتَذَكَّرَ أُولُو الْأَلْبَابِ', 38, 29, 23, 91, 455, 'این کتابی است پربرکت که بر تو نازل کرده‌ایم تا در آیات آن تدبّر کنند و خردمندان متذکّر شوند!'),
(4000, 4000, 'وَوَهَبْنَا لِدَاوُودَ سُلَيْمَانَ ۚ نِعْمَ الْعَبْدُ ۖ إِنَّهُ أَوَّابٌ', 38, 30, 23, 91, 455, 'ما سلیمان را به داوود بخشیدیم؛ چه بنده خوبی! زیرا همواره به سوی خدا بازگشت می‌کرد (و به یاد او بود)!'),
(4001, 4001, 'إِذْ عُرِضَ عَلَيْهِ بِالْعَشِيِّ الصَّافِنَاتُ الْجِيَادُ', 38, 31, 23, 91, 455, 'به خاطر بیاور هنگامی را که عصرگاهان اسبان چابک تندرو را بر او عرضه داشتند،'),
(4002, 4002, 'فَقَالَ إِنِّي أَحْبَبْتُ حُبَّ الْخَيْرِ عَنْ ذِكْرِ رَبِّي حَتَّىٰ تَوَارَتْ بِالْحِجَابِ', 38, 32, 23, 91, 455, 'گفت: «من این اسبان را بخاطر پروردگارم دوست دارم (و می‌خواهم از آنها در جهاد استفاده کنم»، او همچنان به آنها نگاه می‌کرد) تا از دیدگانش پنهان شدند.'),
(4003, 4003, 'رُدُّوهَا عَلَيَّ ۖ فَطَفِقَ مَسْحًا بِالسُّوقِ وَالْأَعْنَاقِ', 38, 33, 23, 91, 455, '(آنها به قدری جالب بودند که گفت:) بار دیگر آنها را نزد من بازگردانید! و دست به ساقها و گردنهای آنها کشید (و آنها را نوازش داد).'),
(4004, 4004, 'وَلَقَدْ فَتَنَّا سُلَيْمَانَ وَأَلْقَيْنَا عَلَىٰ كُرْسِيِّهِ جَسَدًا ثُمَّ أَنَابَ', 38, 34, 23, 91, 455, 'ما سلیمان را آزمودیم و بر تخت او جسدی افکندیم؛ سپس او به درگاه خداوند توبه کرد.'),
(4005, 4005, 'قَالَ رَبِّ اغْفِرْ لِي وَهَبْ لِي مُلْكًا لَا يَنْبَغِي لِأَحَدٍ مِنْ بَعْدِي ۖ إِنَّكَ أَنْتَ الْوَهَّابُ', 38, 35, 23, 91, 455, 'گفت: پروردگارا! مرا ببخش و حکومتی به من عطا کن که بعد از من سزاوار هیچ کس نباشد، که تو بسیار بخشنده‌ای!'),
(4006, 4006, 'فَسَخَّرْنَا لَهُ الرِّيحَ تَجْرِي بِأَمْرِهِ رُخَاءً حَيْثُ أَصَابَ', 38, 36, 23, 91, 455, 'پس ما باد را مسخّر او ساختیم تا به فرمانش بنرمی حرکت کند و به هر جا او می‌خواهد برود!'),
(4007, 4007, 'وَالشَّيَاطِينَ كُلَّ بَنَّاءٍ وَغَوَّاصٍ', 38, 37, 23, 91, 455, 'و شیاطین را مسخّر او کردیم، هر بنّا و غوّاصی از آنها را!'),
(4008, 4008, 'وَآخَرِينَ مُقَرَّنِينَ فِي الْأَصْفَادِ', 38, 38, 23, 91, 455, 'و گروه دیگری (از شیاطین) را در غل و زنجیر (تحت سلطه او) قرار دادیم،'),
(4009, 4009, 'هَٰذَا عَطَاؤُنَا فَامْنُنْ أَوْ أَمْسِكْ بِغَيْرِ حِسَابٍ', 38, 39, 23, 91, 455, '(و به او گفتیم:) این عطای ما است، به هر کس می‌خواهی (و صلاح می‌بینی) ببخش، و از هر کس می‌خواهی امساک کن، و حسابی بر تو نیست (تو امین هستی)!'),
(4010, 4010, 'وَإِنَّ لَهُ عِنْدَنَا لَزُلْفَىٰ وَحُسْنَ مَآبٍ', 38, 40, 23, 91, 455, 'و برای او = سلیمان‌ نزد ما مقامی ارجمند و سرانجامی نیکوست!'),
(4011, 4011, 'وَاذْكُرْ عَبْدَنَا أَيُّوبَ إِذْ نَادَىٰ رَبَّهُ أَنِّي مَسَّنِيَ الشَّيْطَانُ بِنُصْبٍ وَعَذَابٍ', 38, 41, 23, 91, 455, 'و به خاطر بیاور بنده ما ایّوب را، هنگامی که پروردگارش را خواند (و گفت: پروردگارا!) شیطان مرا به رنج و عذاب افکنده است.'),
(4012, 4012, 'ارْكُضْ بِرِجْلِكَ ۖ هَٰذَا مُغْتَسَلٌ بَارِدٌ وَشَرَابٌ', 38, 42, 23, 91, 455, '(به او گفتیم:) پای خود را بر زمین بکوب! این چشمه آبی خنک برای شستشو و نوشیدن است!'),
(4013, 4013, 'وَوَهَبْنَا لَهُ أَهْلَهُ وَمِثْلَهُمْ مَعَهُمْ رَحْمَةً مِنَّا وَذِكْرَىٰ لِأُولِي الْأَلْبَابِ', 38, 43, 23, 91, 456, 'و خانواده‌اش را به او بخشیدیم، و همانند آنها را بر آنان افزودیم، تا رحمتی از سوی ما باشد و تذکّری برای اندیشمندان.'),
(4014, 4014, 'وَخُذْ بِيَدِكَ ضِغْثًا فَاضْرِبْ بِهِ وَلَا تَحْنَثْ ۗ إِنَّا وَجَدْنَاهُ صَابِرًا ۚ نِعْمَ الْعَبْدُ ۖ إِنَّهُ أَوَّابٌ', 38, 44, 23, 91, 456, '(و به او گفتیم:) بسته‌ای از ساقه‌های گندم (یا مانند آن) را برگیر و با آن (همسرت را) بزن و سوگند خود را مشکن! ما او را شکیبا یافتیم؛ چه بنده خوبی که بسیار بازگشت‌کننده (به سوی خدا) بود!'),
(4015, 4015, 'وَاذْكُرْ عِبَادَنَا إِبْرَاهِيمَ وَإِسْحَاقَ وَيَعْقُوبَ أُولِي الْأَيْدِي وَالْأَبْصَارِ', 38, 45, 23, 91, 456, 'و به خاطر بیاور بندگان ما ابراهیم و اسحاق و یعقوب را، صاحبان دستها (ی نیرومند) و چشمها (ی بینا)!'),
(4016, 4016, 'إِنَّا أَخْلَصْنَاهُمْ بِخَالِصَةٍ ذِكْرَى الدَّارِ', 38, 46, 23, 91, 456, 'ما آنها را با خلوص ویژه‌ای خالص کردیم، و آن یادآوری سرای آخرت بود!'),
(4017, 4017, 'وَإِنَّهُمْ عِنْدَنَا لَمِنَ الْمُصْطَفَيْنَ الْأَخْيَارِ', 38, 47, 23, 91, 456, 'و آنها نزد ما از برگزیدگان و نیکانند!'),
(4018, 4018, 'وَاذْكُرْ إِسْمَاعِيلَ وَالْيَسَعَ وَذَا الْكِفْلِ ۖ وَكُلٌّ مِنَ الْأَخْيَارِ', 38, 48, 23, 91, 456, 'و به خاطر بیاور «اسماعیل» و «الیسع» و «ذا الکفل» را که همه از نیکان بودند!'),
(4019, 4019, 'هَٰذَا ذِكْرٌ ۚ وَإِنَّ لِلْمُتَّقِينَ لَحُسْنَ مَآبٍ', 38, 49, 23, 91, 456, 'این یک یادآوری است، و برای پرهیزکاران فرجام نیکویی است:'),
(4020, 4020, 'جَنَّاتِ عَدْنٍ مُفَتَّحَةً لَهُمُ الْأَبْوَابُ', 38, 50, 23, 91, 456, 'باغهای جاویدان بهشتی که درهایش به روی آنان گشوده است،'),
(4021, 4021, 'مُتَّكِئِينَ فِيهَا يَدْعُونَ فِيهَا بِفَاكِهَةٍ كَثِيرَةٍ وَشَرَابٍ', 38, 51, 23, 91, 456, 'در حالی که در آن بر تختها تکیه کرده‌اند و میوه‌های بسیار و نوشیدنیها در اختیار آنان است!'),
(4022, 4022, ' وَعِنْدَهُمْ قَاصِرَاتُ الطَّرْفِ أَتْرَابٌ', 38, 52, 23, 92, 456, 'و نزد آنان همسرانی است که تنها چشم به شوهرانشان دوخته‌اند، و همسن و سالند!'),
(4023, 4023, 'هَٰذَا مَا تُوعَدُونَ لِيَوْمِ الْحِسَابِ', 38, 53, 23, 92, 456, 'این همان است که برای روز حساب به شما وعده داده می‌شود (وعده‌ای تخلّف ناپذیر)!'),
(4024, 4024, 'إِنَّ هَٰذَا لَرِزْقُنَا مَا لَهُ مِنْ نَفَادٍ', 38, 54, 23, 92, 456, 'این روزی ما است که هرگز آن را پایانی نیست!'),
(4025, 4025, 'هَٰذَا ۚ وَإِنَّ لِلطَّاغِينَ لَشَرَّ مَآبٍ', 38, 55, 23, 92, 456, 'این (پاداش پرهیزگاران است)، و برای طغیانگران بدترین محل بازگشت است:'),
(4026, 4026, 'جَهَنَّمَ يَصْلَوْنَهَا فَبِئْسَ الْمِهَادُ', 38, 56, 23, 92, 456, 'دوزخ، که در آن وارد می‌شوند؛ و چه بستر بدی است!'),
(4027, 4027, 'هَٰذَا فَلْيَذُوقُوهُ حَمِيمٌ وَغَسَّاقٌ', 38, 57, 23, 92, 456, 'این نوشابه «حمیم» و «غسّاق» است = دو مایع سوزان و تیره رنگ‌ که باید از آن بچشند!'),
(4028, 4028, 'وَآخَرُ مِنْ شَكْلِهِ أَزْوَاجٌ', 38, 58, 23, 92, 456, 'و جز اینها کیفرهای دیگری همانند آن دارند!'),
(4029, 4029, 'هَٰذَا فَوْجٌ مُقْتَحِمٌ مَعَكُمْ ۖ لَا مَرْحَبًا بِهِمْ ۚ إِنَّهُمْ صَالُو النَّارِ', 38, 59, 23, 92, 456, '(به آنان گفته می‌شود:) این گروهی است که همراه شما وارد دوزخ می‌شوند (اینها همان سران گمراهیند)؛ خوشامد بر آنها مباد، همگی در آتش خواهند سوخت!'),
(4030, 4030, 'قَالُوا بَلْ أَنْتُمْ لَا مَرْحَبًا بِكُمْ ۖ أَنْتُمْ قَدَّمْتُمُوهُ لَنَا ۖ فَبِئْسَ الْقَرَارُ', 38, 60, 23, 92, 456, 'آنها (به رؤسای خود) می‌گویند: «بلکه خوشامد بر شما مباد که این عذاب را شما برای ما فراهم ساختید! چه بد قرارگاهی است اینجا!»'),
(4031, 4031, 'قَالُوا رَبَّنَا مَنْ قَدَّمَ لَنَا هَٰذَا فَزِدْهُ عَذَابًا ضِعْفًا فِي النَّارِ', 38, 61, 23, 92, 456, '(سپس) می‌گویند: «پروردگارا! هر کس این عذاب را برای ما فراهم ساخته، عذابی مضاعف در آتش بر او بیفزا!»'),
(4032, 4032, 'وَقَالُوا مَا لَنَا لَا نَرَىٰ رِجَالًا كُنَّا نَعُدُّهُمْ مِنَ الْأَشْرَارِ', 38, 62, 23, 92, 457, 'آنها می‌گویند: «چرا مردانی را که ما از اشرار می‌شمردیم (در اینجا، در آتش دوزخ) نمی‌بینیم؟!'),
(4033, 4033, 'أَتَّخَذْنَاهُمْ سِخْرِيًّا أَمْ زَاغَتْ عَنْهُمُ الْأَبْصَارُ', 38, 63, 23, 92, 457, 'آیا ما آنان را به مسخره گرفتیم یا (به اندازه‌ای حقیرند که) چشمها آنها را نمی‌بیند؟!'),
(4034, 4034, 'إِنَّ ذَٰلِكَ لَحَقٌّ تَخَاصُمُ أَهْلِ النَّارِ', 38, 64, 23, 92, 457, 'این یک واقعیت است گفتگوهای خصمانه دوزخیان!'),
(4035, 4035, 'قُلْ إِنَّمَا أَنَا مُنْذِرٌ ۖ وَمَا مِنْ إِلَٰهٍ إِلَّا اللَّهُ الْوَاحِدُ الْقَهَّارُ', 38, 65, 23, 92, 457, 'بگو: «من تنها یک بیم‌دهنده‌ام؛ و هیچ معبودی جز خداوند یگانه قهّار نیست!'),
(4036, 4036, 'رَبُّ السَّمَاوَاتِ وَالْأَرْضِ وَمَا بَيْنَهُمَا الْعَزِيزُ الْغَفَّارُ', 38, 66, 23, 92, 457, 'پروردگار آسمانها و زمین و آنچه میان آن دو است، پروردگار عزیز و غفّار!»'),
(4037, 4037, 'قُلْ هُوَ نَبَأٌ عَظِيمٌ', 38, 67, 23, 92, 457, 'بگو: «این خبری بزرگ است،'),
(4038, 4038, 'أَنْتُمْ عَنْهُ مُعْرِضُونَ', 38, 68, 23, 92, 457, 'که شما از آن روی‌گردانید!'),
(4039, 4039, 'مَا كَانَ لِيَ مِنْ عِلْمٍ بِالْمَلَإِ الْأَعْلَىٰ إِذْ يَخْتَصِمُونَ', 38, 69, 23, 92, 457, 'من از ملأ اعلی (و فرشتگان عالم بالا) به هنگامی که (درباره آفرینش آدم) مخاصمه می‌کردند خبر ندارم!'),
(4040, 4040, 'إِنْ يُوحَىٰ إِلَيَّ إِلَّا أَنَّمَا أَنَا نَذِيرٌ مُبِينٌ', 38, 70, 23, 92, 457, 'تنها چیزی که به من وحی می‌شود این است که من انذارکننده آشکاری هستم!»'),
(4041, 4041, 'إِذْ قَالَ رَبُّكَ لِلْمَلَائِكَةِ إِنِّي خَالِقٌ بَشَرًا مِنْ طِينٍ', 38, 71, 23, 92, 457, 'و به خاطر بیاور هنگامی را که پروردگارت به فرشتگان گفت: «من بشری را از گل می‌آفرینم!'),
(4042, 4042, 'فَإِذَا سَوَّيْتُهُ وَنَفَخْتُ فِيهِ مِنْ رُوحِي فَقَعُوا لَهُ سَاجِدِينَ', 38, 72, 23, 92, 457, 'هنگامی که آن را نظام بخشیدم و از روح خود در آن دمیدم، برای او به سجده افتید!»'),
(4043, 4043, 'فَسَجَدَ الْمَلَائِكَةُ كُلُّهُمْ أَجْمَعُونَ', 38, 73, 23, 92, 457, 'در آن هنگام همه فرشتگان سجده کردند،'),
(4044, 4044, 'إِلَّا إِبْلِيسَ اسْتَكْبَرَ وَكَانَ مِنَ الْكَافِرِينَ', 38, 74, 23, 92, 457, 'جز ابلیس که تکبّر ورزید و از کافران بود!'),
(4045, 4045, 'قَالَ يَا إِبْلِيسُ مَا مَنَعَكَ أَنْ تَسْجُدَ لِمَا خَلَقْتُ بِيَدَيَّ ۖ أَسْتَكْبَرْتَ أَمْ كُنْتَ مِنَ الْعَالِينَ', 38, 75, 23, 92, 457, 'گفت: «ای ابلیس! چه چیز مانع تو شد که بر مخلوقی که با قدرت خود او را آفریدم سجده کنی؟! آیا تکبّر کردی یا از برترینها بودی؟! (برتر از اینکه فرمان سجود به تو داده شود!)»'),
(4046, 4046, 'قَالَ أَنَا خَيْرٌ مِنْهُ ۖ خَلَقْتَنِي مِنْ نَارٍ وَخَلَقْتَهُ مِنْ طِينٍ', 38, 76, 23, 92, 457, 'گفت: «من از او بهترم؛ مرا از آتش آفریده‌ای و او را از گل!»'),
(4047, 4047, 'قَالَ فَاخْرُجْ مِنْهَا فَإِنَّكَ رَجِيمٌ', 38, 77, 23, 92, 457, 'فرمود: «از آسمانها (و صفوف ملائکه) خارج شو، که تو رانده درگاه منی!'),
(4048, 4048, 'وَإِنَّ عَلَيْكَ لَعْنَتِي إِلَىٰ يَوْمِ الدِّينِ', 38, 78, 23, 92, 457, 'و مسلّماً لعنت من بر تو تا روز قیامت خواهد بود!'),
(4049, 4049, 'قَالَ رَبِّ فَأَنْظِرْنِي إِلَىٰ يَوْمِ يُبْعَثُونَ', 38, 79, 23, 92, 457, 'گفت: «پروردگارا! مرا تا روزی که انسانها برانگیخته می‌شوند مهلت ده!»'),
(4050, 4050, 'قَالَ فَإِنَّكَ مِنَ الْمُنْظَرِينَ', 38, 80, 23, 92, 457, 'فرمود: «تو از مهلت داده‌شدگانی،'),
(4051, 4051, 'إِلَىٰ يَوْمِ الْوَقْتِ الْمَعْلُومِ', 38, 81, 23, 92, 457, 'ولی تا روز و زمان معیّن!»'),
(4052, 4052, 'قَالَ فَبِعِزَّتِكَ لَأُغْوِيَنَّهُمْ أَجْمَعِينَ', 38, 82, 23, 92, 457, 'گفت: «به عزّتت سوگند، همه آنان را گمراه خواهم کرد،'),
(4053, 4053, 'إِلَّا عِبَادَكَ مِنْهُمُ الْمُخْلَصِينَ', 38, 83, 23, 92, 457, 'مگر بندگان خالص تو، از میان آنها!»'),
(4054, 4054, 'قَالَ فَالْحَقُّ وَالْحَقَّ أَقُولُ', 38, 84, 23, 92, 458, 'فرمود: «به حق سوگند، و حق می‌گویم،'),
(4055, 4055, 'لَأَمْلَأَنَّ جَهَنَّمَ مِنْكَ وَمِمَّنْ تَبِعَكَ مِنْهُمْ أَجْمَعِينَ', 38, 85, 23, 92, 458, 'که جهنّم را از تو و هر کدام از آنان که از تو پیروی کند، پر خواهم کرد!»'),
(4056, 4056, 'قُلْ مَا أَسْأَلُكُمْ عَلَيْهِ مِنْ أَجْرٍ وَمَا أَنَا مِنَ الْمُتَكَلِّفِينَ', 38, 86, 23, 92, 458, '(ای پیامبر!) بگو: «من برای دعوت نبوّت هیچ پاداشی از شما نمی‌طلبم، و من از متکلّفین نیستم! (سخنانم روشن و همراه با دلیل است!)»'),
(4057, 4057, 'إِنْ هُوَ إِلَّا ذِكْرٌ لِلْعَالَمِينَ', 38, 87, 23, 92, 458, 'این (قرآن) تذکّری برای همه جهانیان است؛'),
(4058, 4058, 'وَلَتَعْلَمُنَّ نَبَأَهُ بَعْدَ حِينٍ', 38, 88, 23, 92, 458, 'و خبر آن را بعد از مدّتی می‌شنوید!'),
(4059, 4059, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ تَنْزِيلُ الْكِتَابِ مِنَ اللَّهِ الْعَزِيزِ الْحَكِيمِ', 39, 1, 23, 92, 458, 'این کتابی است که از سوی خداوند عزیز و حکیم نازل شده است.'),
(4060, 4060, 'إِنَّا أَنْزَلْنَا إِلَيْكَ الْكِتَابَ بِالْحَقِّ فَاعْبُدِ اللَّهَ مُخْلِصًا لَهُ الدِّينَ', 39, 2, 23, 92, 458, 'ما این کتاب را بحقّ بر تو نازل کردیم؛ پس خدا را پرستش کن و دین خود را برای او خالص گردان!'),
(4061, 4061, 'أَلَا لِلَّهِ الدِّينُ الْخَالِصُ ۚ وَالَّذِينَ اتَّخَذُوا مِنْ دُونِهِ أَوْلِيَاءَ مَا نَعْبُدُهُمْ إِلَّا لِيُقَرِّبُونَا إِلَى اللَّهِ زُلْفَىٰ إِنَّ اللَّهَ يَحْكُمُ بَيْنَهُمْ فِي مَا هُمْ فِيهِ يَخْتَلِفُونَ ۗ إِنَّ اللَّهَ لَا يَهْدِي مَنْ هُوَ كَاذِبٌ كَفَّارٌ', 39, 3, 23, 92, 458, 'آگاه باشید که دین خالص از آن خداست، و آنها که غیر خدا را اولیای خود قرار دادند و دلیلشان این بود که: «اینها را نمی‌پرستیم مگر بخاطر اینکه ما را به خداوند نزدیک کنند»، خداوند روز قیامت میان آنان در آنچه اختلاف داشتند داوری می‌کند؛ خداوند آن کس را که دروغگو و کفران‌کننده است هرگز هدایت نمی‌کند!'),
(4062, 4062, 'لَوْ أَرَادَ اللَّهُ أَنْ يَتَّخِذَ وَلَدًا لَاصْطَفَىٰ مِمَّا يَخْلُقُ مَا يَشَاءُ ۚ سُبْحَانَهُ ۖ هُوَ اللَّهُ الْوَاحِدُ الْقَهَّارُ', 39, 4, 23, 92, 458, 'اگر (بفرض محال) خدا می‌خواست فرزندی انتخاب کند، از میان مخلوقاتش آنچه را می‌خواست برمیگزید؛ منزّه است (از اینکه فرزندی داشته باشد)! او خداوند یکتای پیروز است!'),
(4063, 4063, 'خَلَقَ السَّمَاوَاتِ وَالْأَرْضَ بِالْحَقِّ ۖ يُكَوِّرُ اللَّيْلَ عَلَى النَّهَارِ وَيُكَوِّرُ النَّهَارَ عَلَى اللَّيْلِ ۖ وَسَخَّرَ الشَّمْسَ وَالْقَمَرَ ۖ كُلٌّ يَجْرِي لِأَجَلٍ مُسَمًّى ۗ أَلَا هُوَ الْعَزِيزُ الْغَفَّارُ', 39, 5, 23, 92, 458, 'آسمانها و زمین را بحقّ آفرید؛ شب را بر روز می‌پیچد و روز را بر شب؛ و خورشید و ماه را مسخّر فرمان خویش قرار داد؛ هر کدام تا سرآمد معیّنی به حرکت خود ادامه می‌دهند؛ آگاه باشید که او قادر و آمرزنده است!'),
(4064, 4064, 'خَلَقَكُمْ مِنْ نَفْسٍ وَاحِدَةٍ ثُمَّ جَعَلَ مِنْهَا زَوْجَهَا وَأَنْزَلَ لَكُمْ مِنَ الْأَنْعَامِ ثَمَانِيَةَ أَزْوَاجٍ ۚ يَخْلُقُكُمْ فِي بُطُونِ أُمَّهَاتِكُمْ خَلْقًا مِنْ بَعْدِ خَلْقٍ فِي ظُلُمَاتٍ ثَلَاثٍ ۚ ذَٰلِكُمُ اللَّهُ رَبُّكُمْ لَهُ الْمُلْكُ ۖ لَا إِلَٰهَ إِلَّا هُوَ ۖ فَأَنَّىٰ تُصْرَفُونَ', 39, 6, 23, 92, 459, 'او شما را از یک نفس آفرید، و همسرش را از (باقیمانده گِل) او خلق کرد؛ و برای شما هشت زوج از چهارپایان ایجاد کرد؛ او شما را در شکم مادرانتان آفرینشی بعد از آفرینش دیگر، در میان تاریکیهای سه گانه، می‌بخشد! این است خداوند، پروردگار شما که حکومت (عالم هستی) از آن اوست؛ هیچ معبودی جز او نیست؛ پس چگونه از راه حق منحرف می‌شوید؟!'),
(4065, 4065, 'إِنْ تَكْفُرُوا فَإِنَّ اللَّهَ غَنِيٌّ عَنْكُمْ ۖ وَلَا يَرْضَىٰ لِعِبَادِهِ الْكُفْرَ ۖ وَإِنْ تَشْكُرُوا يَرْضَهُ لَكُمْ ۗ وَلَا تَزِرُ وَازِرَةٌ وِزْرَ أُخْرَىٰ ۗ ثُمَّ إِلَىٰ رَبِّكُمْ مَرْجِعُكُمْ فَيُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُونَ ۚ إِنَّهُ عَلِيمٌ بِذَاتِ الصُّدُورِ', 39, 7, 23, 92, 459, 'اگر کفران کنید، خداوند از شما بی‌نیاز است و هرگز کفران را برای بندگانش نمی پسندد؛ و اگر شکر او را بجا آورید آن را برای شما می‌پسندد! و هیچ گنهکاری گناه دیگری را بر دوش نمی‌کشد! سپس بازگشت همه شما به سوی پروردگارتان است، و شما را از آنچه انجام می‌دادید آگاه می‌سازد؛ چرا که او به آنچه در سینه‌هاست آگاه است!'),
(4066, 4066, ' وَإِذَا مَسَّ الْإِنْسَانَ ضُرٌّ دَعَا رَبَّهُ مُنِيبًا إِلَيْهِ ثُمَّ إِذَا خَوَّلَهُ نِعْمَةً مِنْهُ نَسِيَ مَا كَانَ يَدْعُو إِلَيْهِ مِنْ قَبْلُ وَجَعَلَ لِلَّهِ أَنْدَادًا لِيُضِلَّ عَنْ سَبِيلِهِ ۚ قُلْ تَمَتَّعْ بِكُفْرِكَ قَلِيلًا ۖ إِنَّكَ مِنْ أَصْحَابِ النَّارِ', 39, 8, 23, 92, 459, 'هنگامی که انسان را زیانی رسد، پروردگار خود را می‌خواند و بسوی او باز می گردد؛ امّا هنگامی که نعمتی از خود به او عطا کند، آنچه را به خاطر آن قبلاً خدا را می خواند از یاد می‌برد و برای خداوند همتایانی قرارمی‌دهد تا مردم را از راه او منحرف سازد؛ بگو: «چند روزی از کفرت بهره‌گیر که از دوزخیانی!»'),
(4067, 4067, 'أَمَّنْ هُوَ قَانِتٌ آنَاءَ اللَّيْلِ سَاجِدًا وَقَائِمًا يَحْذَرُ الْآخِرَةَ وَيَرْجُو رَحْمَةَ رَبِّهِ ۗ قُلْ هَلْ يَسْتَوِي الَّذِينَ يَعْلَمُونَ وَالَّذِينَ لَا يَعْلَمُونَ ۗ إِنَّمَا يَتَذَكَّرُ أُولُو الْأَلْبَابِ', 39, 9, 23, 92, 459, '(آیا چنین کسی با ارزش است) یا کسی که در ساعات شب به عبادت مشغول است و در حال سجده و قیام، از عذاب آخرت می‌ترسد و به رحمت پروردگارش امیدوار است؟! بگو: «آیا کسانی که می‌دانند با کسانی که نمی‌دانند یکسانند؟! تنها خردمندان متذکّر می‌شوند!»'),
(4068, 4068, 'قُلْ يَا عِبَادِ الَّذِينَ آمَنُوا اتَّقُوا رَبَّكُمْ ۚ لِلَّذِينَ أَحْسَنُوا فِي هَٰذِهِ الدُّنْيَا حَسَنَةٌ ۗ وَأَرْضُ اللَّهِ وَاسِعَةٌ ۗ إِنَّمَا يُوَفَّى الصَّابِرُونَ أَجْرَهُمْ بِغَيْرِ حِسَابٍ', 39, 10, 23, 92, 459, 'بگو: «ای بندگان من که ایمان آورده‌اید! از (مخالفت) پروردگارتان بپرهیزید! برای کسانی که در این دنیا نیکی کرده‌اند پاداش نیکی است! و زمین خدا وسیع است، (اگر تحت فشار سران کفر بودید مهاجرت کنید) که صابران اجر و پاداش خود را بی‌حساب دریافت می‌دارند!'),
(4069, 4069, 'قُلْ إِنِّي أُمِرْتُ أَنْ أَعْبُدَ اللَّهَ مُخْلِصًا لَهُ الدِّينَ', 39, 11, 23, 92, 460, 'بگو: «من مأمورم که خدا را پرستش کنم در حالی که دینم را برای او خالص کرده باشم،'),
(4070, 4070, 'وَأُمِرْتُ لِأَنْ أَكُونَ أَوَّلَ الْمُسْلِمِينَ', 39, 12, 23, 92, 460, 'و مأمورم که نخستین مسلمان باشم!'),
(4071, 4071, 'قُلْ إِنِّي أَخَافُ إِنْ عَصَيْتُ رَبِّي عَذَابَ يَوْمٍ عَظِيمٍ', 39, 13, 23, 92, 460, 'بگو: «من اگر نافرمانی پروردگارم کنم، از عذاب روز بزرگ (قیامت) می‌ترسم!»'),
(4072, 4072, 'قُلِ اللَّهَ أَعْبُدُ مُخْلِصًا لَهُ دِينِي', 39, 14, 23, 92, 460, 'بگو: «من تنها خدا را می‌پرستم در حالی که دینم را برای او خالص می‌کنم.'),
(4073, 4073, 'فَاعْبُدُوا مَا شِئْتُمْ مِنْ دُونِهِ ۗ قُلْ إِنَّ الْخَاسِرِينَ الَّذِينَ خَسِرُوا أَنْفُسَهُمْ وَأَهْلِيهِمْ يَوْمَ الْقِيَامَةِ ۗ أَلَا ذَٰلِكَ هُوَ الْخُسْرَانُ الْمُبِينُ', 39, 15, 23, 92, 460, 'شما هر چه را جز او می‌خواهید بپرستید!» بگو: «زیانکاران واقعی آنانند که سرمایه وجود خویش و بستگانشان را در روز قیامت از دست داده‌اند! آگاه باشید زیان آشکار همین است!»'),
(4074, 4074, 'لَهُمْ مِنْ فَوْقِهِمْ ظُلَلٌ مِنَ النَّارِ وَمِنْ تَحْتِهِمْ ظُلَلٌ ۚ ذَٰلِكَ يُخَوِّفُ اللَّهُ بِهِ عِبَادَهُ ۚ يَا عِبَادِ فَاتَّقُونِ', 39, 16, 23, 92, 460, 'برای آنان از بالای سرشان سایبانهایی از آتش، و در زیر پایشان نیز سایبانهایی از آتش است؛ این چیزی است که خداوند با آن بندگانش را می‌ترساند! ای بندگان من! از نافرمانی من بپرهیزید!'),
(4075, 4075, 'وَالَّذِينَ اجْتَنَبُوا الطَّاغُوتَ أَنْ يَعْبُدُوهَا وَأَنَابُوا إِلَى اللَّهِ لَهُمُ الْبُشْرَىٰ ۚ فَبَشِّرْ عِبَادِ', 39, 17, 23, 92, 460, 'و کسانی که از عبادت طاغوت پرهیز کردند و به سوی خداوند بازگشتند، بشارت از آن آنهاست؛ پس بندگان مرا بشارت ده!'),
(4076, 4076, 'الَّذِينَ يَسْتَمِعُونَ الْقَوْلَ فَيَتَّبِعُونَ أَحْسَنَهُ ۚ أُولَٰئِكَ الَّذِينَ هَدَاهُمُ اللَّهُ ۖ وَأُولَٰئِكَ هُمْ أُولُو الْأَلْبَابِ', 39, 18, 23, 92, 460, 'همان کسانی که سخنان را می‌شنوند و از نیکوترین آنها پیروی می‌کنند؛ آنان کسانی هستند که خدا هدایتشان کرده، و آنها خردمندانند.'),
(4077, 4077, 'أَفَمَنْ حَقَّ عَلَيْهِ كَلِمَةُ الْعَذَابِ أَفَأَنْتَ تُنْقِذُ مَنْ فِي النَّارِ', 39, 19, 23, 92, 460, 'آیا تو می‌توانی کسی را که فرمان عذاب درباره او قطعی شده رهایی بخشی؟! آیا تو میتوانی کسی را که در درون آتش است برگیری و نجات دهی؟!'),
(4078, 4078, 'لَٰكِنِ الَّذِينَ اتَّقَوْا رَبَّهُمْ لَهُمْ غُرَفٌ مِنْ فَوْقِهَا غُرَفٌ مَبْنِيَّةٌ تَجْرِي مِنْ تَحْتِهَا الْأَنْهَارُ ۖ وَعْدَ اللَّهِ ۖ لَا يُخْلِفُ اللَّهُ الْمِيعَادَ', 39, 20, 23, 92, 460, 'ولی آنها که تقوای الهی پیشه کردند، غرفه‌هایی در بهشت دارند که بر فراز آنها غرفه‌های دیگری بنا شده و از زیر آنها نهرها جاری است این وعده الهی است، و خداوند در وعده خود تخلّف نمی‌کند!'),
(4079, 4079, 'أَلَمْ تَرَ أَنَّ اللَّهَ أَنْزَلَ مِنَ السَّمَاءِ مَاءً فَسَلَكَهُ يَنَابِيعَ فِي الْأَرْضِ ثُمَّ يُخْرِجُ بِهِ زَرْعًا مُخْتَلِفًا أَلْوَانُهُ ثُمَّ يَهِيجُ فَتَرَاهُ مُصْفَرًّا ثُمَّ يَجْعَلُهُ حُطَامًا ۚ إِنَّ فِي ذَٰلِكَ لَذِكْرَىٰ لِأُولِي الْأَلْبَابِ', 39, 21, 23, 92, 460, 'آیا ندیدی که خداوند از آسمان آبی فرستاد و آن را بصورت چشمه‌هایی در زمین وارد نمود، سپس با آن زراعتی را خارج می‌سازد که رنگهای مختلف دارد؛ بعد آن گیاه خشک می‌شود، بگونه‌ای که آن را زرد و بی‌روح می‌بینی؛ سپس آن را در هم می‌شکند و خرد می‌کند؛ در این مثال تذکّری است برای خردمندان (از ناپایداری دنیا)!'),
(4080, 4080, 'أَفَمَنْ شَرَحَ اللَّهُ صَدْرَهُ لِلْإِسْلَامِ فَهُوَ عَلَىٰ نُورٍ مِنْ رَبِّهِ ۚ فَوَيْلٌ لِلْقَاسِيَةِ قُلُوبُهُمْ مِنْ ذِكْرِ اللَّهِ ۚ أُولَٰئِكَ فِي ضَلَالٍ مُبِينٍ', 39, 22, 23, 92, 461, 'آیا کسی که خدا سینه‌اش را برای اسلام گشاده است و بر فراز مرکبی از نور الهی قرار گرفته (همچون کوردلان گمراه است؟!) وای بر آنان که قلبهایی سخت در برابر ذکر خدا دارند! آنها در گمراهی آشکاری هستند!'),
(4081, 4081, 'اللَّهُ نَزَّلَ أَحْسَنَ الْحَدِيثِ كِتَابًا مُتَشَابِهًا مَثَانِيَ تَقْشَعِرُّ مِنْهُ جُلُودُ الَّذِينَ يَخْشَوْنَ رَبَّهُمْ ثُمَّ تَلِينُ جُلُودُهُمْ وَقُلُوبُهُمْ إِلَىٰ ذِكْرِ اللَّهِ ۚ ذَٰلِكَ هُدَى اللَّهِ يَهْدِي بِهِ مَنْ يَشَاءُ ۚ وَمَنْ يُضْلِلِ اللَّهُ فَمَا لَهُ مِنْ هَادٍ', 39, 23, 23, 92, 461, 'خداوند بهترین سخن را نازل کرده، کتابی که آیاتش (در لطف و زیبایی و عمق و محتوا) همانند یکدیگر است؛ آیاتی مکرّر دارد (با تکراری شوق‌انگیز) که از شنیدن آیاتش لرزه بر اندام کسانی که از پروردگارشان می‌ترسند می‌افتد؛ سپس برون و درونشان نرم و متوجّه ذکر خدا می‌شود؛ این هدایت الهی است که هر کس را بخواهد با آن راهنمایی می‌کند؛ و هر کس را خداوند گمراه سازد، راهنمایی برای او نخواهد بود!'),
(4082, 4082, 'أَفَمَنْ يَتَّقِي بِوَجْهِهِ سُوءَ الْعَذَابِ يَوْمَ الْقِيَامَةِ ۚ وَقِيلَ لِلظَّالِمِينَ ذُوقُوا مَا كُنْتُمْ تَكْسِبُونَ', 39, 24, 23, 92, 461, 'آیا کسی که با صورت خود عذاب دردناک (الهی) را در روز قیامت دور می‌سازد (همانند کسی است که هرگز آتش دوزخ به او نمی‌رسد)؟! و به ظالمان گفته می شود: «بچشید آنچه را به دست می‌آوردید (و انجام می‌دادید)!»'),
(4083, 4083, 'كَذَّبَ الَّذِينَ مِنْ قَبْلِهِمْ فَأَتَاهُمُ الْعَذَابُ مِنْ حَيْثُ لَا يَشْعُرُونَ', 39, 25, 23, 92, 461, 'کسانی که قبل از آنها بودند نیز (آیات ما را) تکذیب نمودند، و عذاب (الهی) از جایی که فکر نمی‌کردند به سراغشان آمد!'),
(4084, 4084, 'فَأَذَاقَهُمُ اللَّهُ الْخِزْيَ فِي الْحَيَاةِ الدُّنْيَا ۖ وَلَعَذَابُ الْآخِرَةِ أَكْبَرُ ۚ لَوْ كَانُوا يَعْلَمُونَ', 39, 26, 23, 92, 461, 'پس خداوند خواری را در زندگی این دنیا به آنها چشانید، و عذاب آخرت شدیدتر است اگر می‌دانستند.'),
(4085, 4085, 'وَلَقَدْ ضَرَبْنَا لِلنَّاسِ فِي هَٰذَا الْقُرْآنِ مِنْ كُلِّ مَثَلٍ لَعَلَّهُمْ يَتَذَكَّرُونَ', 39, 27, 23, 92, 461, 'ما برای مردم در این قرآن از هر نوع مثَلی زدیم، شاید متذکّر شوند.'),
(4086, 4086, 'قُرْآنًا عَرَبِيًّا غَيْرَ ذِي عِوَجٍ لَعَلَّهُمْ يَتَّقُونَ', 39, 28, 23, 92, 461, 'قرآنی است فصیح و خالی از هر گونه کجی و نادرستی، شاید آنان پرهیزگاری پیشه کنند!'),
(4087, 4087, 'ضَرَبَ اللَّهُ مَثَلًا رَجُلًا فِيهِ شُرَكَاءُ مُتَشَاكِسُونَ وَرَجُلًا سَلَمًا لِرَجُلٍ هَلْ يَسْتَوِيَانِ مَثَلًا ۚ الْحَمْدُ لِلَّهِ ۚ بَلْ أَكْثَرُهُمْ لَا يَعْلَمُونَ', 39, 29, 23, 92, 461, 'خداوند مثالی زده است: مردی را که مملوک شریکانی است که درباره او پیوسته با هم به مشاجره مشغولند، و مردی که تنها تسلیم یک نفر است؛ آیا این دو یکسانند؟! حمد، مخصوص خداست، ولی بیشتر آنان نمی‌دانند.'),
(4088, 4088, 'إِنَّكَ مَيِّتٌ وَإِنَّهُمْ مَيِّتُونَ', 39, 30, 23, 92, 461, 'تو می‌میری و آنها نیز خواهند مرد!'),
(4089, 4089, 'ثُمَّ إِنَّكُمْ يَوْمَ الْقِيَامَةِ عِنْدَ رَبِّكُمْ تَخْتَصِمُونَ', 39, 31, 23, 92, 461, 'سپس شما روز قیامت نزد پروردگارتان مخاصمه می‌کنید.'),
(4090, 4090, ' فَمَنْ أَظْلَمُ مِمَّنْ كَذَبَ عَلَى اللَّهِ وَكَذَّبَ بِالصِّدْقِ إِذْ جَاءَهُ ۚ أَلَيْسَ فِي جَهَنَّمَ مَثْوًى لِلْكَافِرِينَ', 39, 32, 24, 93, 462, 'پس چه کسی ستمکارتر است از آن کسی که بر خدا دروغ ببندد و سخن راست را هنگامی که به سراغ او آمده تکذیب کند؟! آیا در جهنّم جایگاهی برای کافران نیست؟!'),
(4091, 4091, 'وَالَّذِي جَاءَ بِالصِّدْقِ وَصَدَّقَ بِهِ ۙ أُولَٰئِكَ هُمُ الْمُتَّقُونَ', 39, 33, 24, 93, 462, 'امّا کسی که سخن راست بیاورد و کسی که آن را تصدیق کند، آنان پرهیزگارانند!'),
(4092, 4092, 'لَهُمْ مَا يَشَاءُونَ عِنْدَ رَبِّهِمْ ۚ ذَٰلِكَ جَزَاءُ الْمُحْسِنِينَ', 39, 34, 24, 93, 462, 'آنچه بخواهند نزد پروردگارشان برای آنان موجود است؛ و این است جزای نیکوکاران!'),
(4093, 4093, 'لِيُكَفِّرَ اللَّهُ عَنْهُمْ أَسْوَأَ الَّذِي عَمِلُوا وَيَجْزِيَهُمْ أَجْرَهُمْ بِأَحْسَنِ الَّذِي كَانُوا يَعْمَلُونَ', 39, 35, 24, 93, 462, 'تا خداوند بدترین اعمالی را که انجام داده‌اند (در سایه ایمان و صداقت آنها) بیامرزد، و آنها را به بهترین اعمالی که انجام می‌دادند پاداش دهد!'),
(4094, 4094, 'أَلَيْسَ اللَّهُ بِكَافٍ عَبْدَهُ ۖ وَيُخَوِّفُونَكَ بِالَّذِينَ مِنْ دُونِهِ ۚ وَمَنْ يُضْلِلِ اللَّهُ فَمَا لَهُ مِنْ هَادٍ', 39, 36, 24, 93, 462, 'آیا خداوند برای (نجات و دفاع از) بنده‌اش کافی نیست؟! امّا آنها تو را از غیر او می‌ترسانند. و هر کس را خداوند گمراه کند، هیچ هدایت‌کننده‌ای ندارد!'),
(4095, 4095, 'وَمَنْ يَهْدِ اللَّهُ فَمَا لَهُ مِنْ مُضِلٍّ ۗ أَلَيْسَ اللَّهُ بِعَزِيزٍ ذِي انْتِقَامٍ', 39, 37, 24, 93, 462, 'و هر کس را خدا هدایت کند، هیچ گمراه‌کننده‌ای نخواهد داشت آیا خداوند توانا و دارای مجازات نیست؟!'),
(4096, 4096, 'وَلَئِنْ سَأَلْتَهُمْ مَنْ خَلَقَ السَّمَاوَاتِ وَالْأَرْضَ لَيَقُولُنَّ اللَّهُ ۚ قُلْ أَفَرَأَيْتُمْ مَا تَدْعُونَ مِنْ دُونِ اللَّهِ إِنْ أَرَادَنِيَ اللَّهُ بِضُرٍّ هَلْ هُنَّ كَاشِفَاتُ ضُرِّهِ أَوْ أَرَادَنِي بِرَحْمَةٍ هَلْ هُنَّ مُمْسِكَاتُ رَحْمَتِهِ ۚ قُلْ حَسْبِيَ اللَّهُ ۖ عَلَيْهِ يَتَوَكَّلُ الْمُتَوَكِّلُونَ', 39, 38, 24, 93, 462, 'و اگر از آنها بپرسی: «چه کسی آسمانها و زمین را آفریده؟» حتماً می‌گویند: «خدا!» بگو: «آیا هیچ درباره معبودانی که غیر از خدا می‌خوانید اندیشه می‌کنید که اگر خدا زیانی برای من بخواهد، آیا آنها می‌توانند گزند او را برطرف سازند؟! و یا اگر رحمتی برای من بخواهد، آیا آنها می‌توانند جلو رحمت او را بگیرند؟!» بگو: «خدا مرا کافی است؛ و همه متوکّلان تنها بر او توکّل می‌کنند!»'),
(4097, 4097, 'قُلْ يَا قَوْمِ اعْمَلُوا عَلَىٰ مَكَانَتِكُمْ إِنِّي عَامِلٌ ۖ فَسَوْفَ تَعْلَمُونَ', 39, 39, 24, 93, 462, 'بگو: «ای قوم من! شما هر چه در توان دارید انجام دهید، من نیز به وظیفه خود عمل می‌کنم؛ امّا بزودی خواهید دانست...'),
(4098, 4098, 'مَنْ يَأْتِيهِ عَذَابٌ يُخْزِيهِ وَيَحِلُّ عَلَيْهِ عَذَابٌ مُقِيمٌ', 39, 40, 24, 93, 462, 'چه کسی عذاب خوارکننده‌ای (در دنیا) به سراغش می‌آید، و (سپس) عذابی جاویدان (در آخرت) بر او وارد می‌گردد!»'),
(4099, 4099, 'إِنَّا أَنْزَلْنَا عَلَيْكَ الْكِتَابَ لِلنَّاسِ بِالْحَقِّ ۖ فَمَنِ اهْتَدَىٰ فَلِنَفْسِهِ ۖ وَمَنْ ضَلَّ فَإِنَّمَا يَضِلُّ عَلَيْهَا ۖ وَمَا أَنْتَ عَلَيْهِمْ بِوَكِيلٍ', 39, 41, 24, 93, 463, 'ما این کتاب (آسمانی) را برای مردم بحق بر تو نازل کردیم؛ هر کس هدایت را پذیرد به نفع خود اوست؛ و هر کس گمراهی را برگزیند، تنها به زیان خود گمراه می‌گردد؛ و تو مأمور اجبار آنها به هدایت نیستی.'),
(4100, 4100, 'اللَّهُ يَتَوَفَّى الْأَنْفُسَ حِينَ مَوْتِهَا وَالَّتِي لَمْ تَمُتْ فِي مَنَامِهَا ۖ فَيُمْسِكُ الَّتِي قَضَىٰ عَلَيْهَا الْمَوْتَ وَيُرْسِلُ الْأُخْرَىٰ إِلَىٰ أَجَلٍ مُسَمًّى ۚ إِنَّ فِي ذَٰلِكَ لَآيَاتٍ لِقَوْمٍ يَتَفَكَّرُونَ', 39, 42, 24, 93, 463, 'خداوند ارواح را به هنگام مرگ قبض می‌کند، و ارواحی را که نمرده‌اند نیز به هنگام خواب می‌گیرد؛ سپس ارواح کسانی که فرمان مرگشان را صادر کرده نگه می‌دارد و ارواح دیگری را (که باید زنده بمانند) بازمی‌گرداند تا سرآمدی معیّن؛ در این امر نشانه‌های روشنی است برای کسانی که اندیشه می‌کنند!'),
(4101, 4101, 'أَمِ اتَّخَذُوا مِنْ دُونِ اللَّهِ شُفَعَاءَ ۚ قُلْ أَوَلَوْ كَانُوا لَا يَمْلِكُونَ شَيْئًا وَلَا يَعْقِلُونَ', 39, 43, 24, 93, 463, 'آیا آنان غیر از خدا شفیعانی گرفته‌اند؟! به آنان بگو: «آیا (از آنها شفاعت می‌طلبید) هر چند مالک چیزی نباشند و درک و شعوری برای آنها نباشد؟!»'),
(4102, 4102, 'قُلْ لِلَّهِ الشَّفَاعَةُ جَمِيعًا ۖ لَهُ مُلْكُ السَّمَاوَاتِ وَالْأَرْضِ ۖ ثُمَّ إِلَيْهِ تُرْجَعُونَ', 39, 44, 24, 93, 463, 'بگو: «تمام شفاعت از آن خداست، (زیرا) حاکمیّت آسمانها و زمین از آن اوست و سپس همه شما را به سوی او بازمی‌گردانند!»'),
(4103, 4103, 'وَإِذَا ذُكِرَ اللَّهُ وَحْدَهُ اشْمَأَزَّتْ قُلُوبُ الَّذِينَ لَا يُؤْمِنُونَ بِالْآخِرَةِ ۖ وَإِذَا ذُكِرَ الَّذِينَ مِنْ دُونِهِ إِذَا هُمْ يَسْتَبْشِرُونَ', 39, 45, 24, 93, 463, 'هنگامی که خداوند به یگانگی یاد می‌شود، دلهای کسانی که به آخرت ایمان ندارند مشمئزّ (و متنفّر) می‌گردد؛ امّا هنگامی که از معبودهای دیگر یاد می‌شود، آنان خوشحال می‌شوند.'),
(4104, 4104, 'قُلِ اللَّهُمَّ فَاطِرَ السَّمَاوَاتِ وَالْأَرْضِ عَالِمَ الْغَيْبِ وَالشَّهَادَةِ أَنْتَ تَحْكُمُ بَيْنَ عِبَادِكَ فِي مَا كَانُوا فِيهِ يَخْتَلِفُونَ', 39, 46, 24, 93, 463, 'بگو: «خداوندا! ای آفریننده آسمانها و زمین، و آگاه از اسرار نهان و آشکار، تو در میان بندگانت در آنچه اختلاف داشتند داوری خواهی کرد!»'),
(4105, 4105, 'وَلَوْ أَنَّ لِلَّذِينَ ظَلَمُوا مَا فِي الْأَرْضِ جَمِيعًا وَمِثْلَهُ مَعَهُ لَافْتَدَوْا بِهِ مِنْ سُوءِ الْعَذَابِ يَوْمَ الْقِيَامَةِ ۚ وَبَدَا لَهُمْ مِنَ اللَّهِ مَا لَمْ يَكُونُوا يَحْتَسِبُونَ', 39, 47, 24, 93, 463, 'اگر ستمکاران تمام آنچه را روی زمین است مالک باشند و همانند آن بر آن افزوده شود، حاضرند همه را فدا کنند تا از عذاب شدید روز قیامت رهایی یابند؛ و از سوی خدا برای آنها اموری ظاهر می‌شود که هرگز گمان نمی‌کردند!'),
(4106, 4106, 'وَبَدَا لَهُمْ سَيِّئَاتُ مَا كَسَبُوا وَحَاقَ بِهِمْ مَا كَانُوا بِهِ يَسْتَهْزِئُونَ', 39, 48, 24, 93, 464, 'در آن روز اعمال بدی را که انجام داده‌اند برای آنها آشکار می‌شود، و آنچه را استهزا می‌کردند بر آنها واقع می‌گردد.'),
(4107, 4107, 'فَإِذَا مَسَّ الْإِنْسَانَ ضُرٌّ دَعَانَا ثُمَّ إِذَا خَوَّلْنَاهُ نِعْمَةً مِنَّا قَالَ إِنَّمَا أُوتِيتُهُ عَلَىٰ عِلْمٍ ۚ بَلْ هِيَ فِتْنَةٌ وَلَٰكِنَّ أَكْثَرَهُمْ لَا يَعْلَمُونَ', 39, 49, 24, 93, 464, 'هنگامی که انسان را زیانی رسد، ما را (برای حلّ مشکلش) می‌خواند؛ سپس هنگامی که از جانب خود به او نعمتی دهیم، می‌گوید: «این نعمت را بخاطر کاردانی خودم به من داده‌اند»؛ ولی این وسیله آزمایش (آنها) است، امّا بیشترشان نمی‌دانند.'),
(4108, 4108, 'قَدْ قَالَهَا الَّذِينَ مِنْ قَبْلِهِمْ فَمَا أَغْنَىٰ عَنْهُمْ مَا كَانُوا يَكْسِبُونَ', 39, 50, 24, 93, 464, 'این سخن را کسانی که قبل از آنها بودند نیز گفتند، ولی آنچه را به دست می‌آوردند برای آنها سودی نداشت!'),
(4109, 4109, 'فَأَصَابَهُمْ سَيِّئَاتُ مَا كَسَبُوا ۚ وَالَّذِينَ ظَلَمُوا مِنْ هَٰؤُلَاءِ سَيُصِيبُهُمْ سَيِّئَاتُ مَا كَسَبُوا وَمَا هُمْ بِمُعْجِزِينَ', 39, 51, 24, 93, 464, 'سپس بدیهای اعمالشان به آنها رسید؛ و ظالمان این گروه = اهل مکّه‌ نیز بزودی گرفتار بدیهای اعمالی که انجام داده‌اند خواهند شد، و هرگز نمی‌توانند از چنگال عذاب الهی بگریزند.'),
(4110, 4110, 'أَوَلَمْ يَعْلَمُوا أَنَّ اللَّهَ يَبْسُطُ الرِّزْقَ لِمَنْ يَشَاءُ وَيَقْدِرُ ۚ إِنَّ فِي ذَٰلِكَ لَآيَاتٍ لِقَوْمٍ يُؤْمِنُونَ', 39, 52, 24, 93, 464, 'آیا آنها ندانستند که خداوند روزی را برای هر کس بخواهد گسترده یا تنگ می‌سازد؟! در این، آیات و نشانه‌هایی است برای گروهی که ایمان می‌آورند.'),
(4111, 4111, ' قُلْ يَا عِبَادِيَ الَّذِينَ أَسْرَفُوا عَلَىٰ أَنْفُسِهِمْ لَا تَقْنَطُوا مِنْ رَحْمَةِ اللَّهِ ۚ إِنَّ اللَّهَ يَغْفِرُ الذُّنُوبَ جَمِيعًا ۚ إِنَّهُ هُوَ الْغَفُورُ الرَّحِيمُ', 39, 53, 24, 93, 464, 'بگو: «ای بندگان من که بر خود اسراف و ستم کرده‌اید! از رحمت خداوند نومید نشوید که خدا همه گناهان را می‌آمرزد، زیرا او بسیار آمرزنده و مهربان است.'),
(4112, 4112, 'وَأَنِيبُوا إِلَىٰ رَبِّكُمْ وَأَسْلِمُوا لَهُ مِنْ قَبْلِ أَنْ يَأْتِيَكُمُ الْعَذَابُ ثُمَّ لَا تُنْصَرُونَ', 39, 54, 24, 93, 464, 'و به درگاه پروردگارتان بازگردید و در برابر او تسلیم شوید، پیش از آنکه عذاب به سراغ شما آید، سپس از سوی هیچ کس یاری نشوید!'),
(4113, 4113, 'وَاتَّبِعُوا أَحْسَنَ مَا أُنْزِلَ إِلَيْكُمْ مِنْ رَبِّكُمْ مِنْ قَبْلِ أَنْ يَأْتِيَكُمُ الْعَذَابُ بَغْتَةً وَأَنْتُمْ لَا تَشْعُرُونَ', 39, 55, 24, 93, 464, 'و از بهترین دستورهائی که از سوی پروردگارتان بر شما نازل شده پیروی کنید پیش از آنکه عذاب (الهی) ناگهان به سراغ شما آید در حالی که از آن خبر ندارید!»'),
(4114, 4114, 'أَنْ تَقُولَ نَفْسٌ يَا حَسْرَتَا عَلَىٰ مَا فَرَّطْتُ فِي جَنْبِ اللَّهِ وَإِنْ كُنْتُ لَمِنَ السَّاخِرِينَ', 39, 56, 24, 93, 464, '(این دستورها برای آن است که) مبادا کسی روز قیامت بگوید: «افسوس بر من از کوتاهیهایی که در اطاعت فرمان خدا کردم و از مسخره‌کنندگان (آیات او) بودم!»'),
(4115, 4115, 'أَوْ تَقُولَ لَوْ أَنَّ اللَّهَ هَدَانِي لَكُنْتُ مِنَ الْمُتَّقِينَ', 39, 57, 24, 93, 465, 'یا بگوید: «اگر خداوند مرا هدایت می‌کرد، از پرهیزگاران بودم!»'),
(4116, 4116, 'أَوْ تَقُولَ حِينَ تَرَى الْعَذَابَ لَوْ أَنَّ لِي كَرَّةً فَأَكُونَ مِنَ الْمُحْسِنِينَ', 39, 58, 24, 93, 465, 'یا هنگامی که عذاب را می‌بیند بگوید: «ای کاش بار دیگر (به دنیا) بازمی‌گشتم و از نیکوکاران بودم!»'),
(4117, 4117, 'بَلَىٰ قَدْ جَاءَتْكَ آيَاتِي فَكَذَّبْتَ بِهَا وَاسْتَكْبَرْتَ وَكُنْتَ مِنَ الْكَافِرِينَ', 39, 59, 24, 93, 465, 'آری، آیات من به سراغ تو آمد، امّا آن را تکذیب کردی و تکبّر نمودی و از کافران بودی!'),
(4118, 4118, 'وَيَوْمَ الْقِيَامَةِ تَرَى الَّذِينَ كَذَبُوا عَلَى اللَّهِ وُجُوهُهُمْ مُسْوَدَّةٌ ۚ أَلَيْسَ فِي جَهَنَّمَ مَثْوًى لِلْمُتَكَبِّرِينَ', 39, 60, 24, 93, 465, 'و روز قیامت کسانی را که بر خدا دروغ بستند می‌بینی که صورتهایشان سیاه است؛ آیا در جهنّم جایگاهی برای متکبّران نیست؟!'),
(4119, 4119, 'وَيُنَجِّي اللَّهُ الَّذِينَ اتَّقَوْا بِمَفَازَتِهِمْ لَا يَمَسُّهُمُ السُّوءُ وَلَا هُمْ يَحْزَنُونَ', 39, 61, 24, 93, 465, 'و خداوند کسانی را که تقوا پیشه کردند با رستگاری رهایی می‌بخشد؛ هیچ بدی به آنان نمی‌رسد و هرگز غمگین نخواهند شد.'),
(4120, 4120, 'اللَّهُ خَالِقُ كُلِّ شَيْءٍ ۖ وَهُوَ عَلَىٰ كُلِّ شَيْءٍ وَكِيلٌ', 39, 62, 24, 93, 465, 'خداوند آفریدگار همه چیز است و حافظ و ناظر بر همه اشیا است.'),
(4121, 4121, 'لَهُ مَقَالِيدُ السَّمَاوَاتِ وَالْأَرْضِ ۗ وَالَّذِينَ كَفَرُوا بِآيَاتِ اللَّهِ أُولَٰئِكَ هُمُ الْخَاسِرُونَ', 39, 63, 24, 93, 465, 'کلیدهای آسمانها و زمین از آن اوست؛ و کسانی که به آیات خداوند کافر شدند زیانکارانند.'),
(4122, 4122, 'قُلْ أَفَغَيْرَ اللَّهِ تَأْمُرُونِّي أَعْبُدُ أَيُّهَا الْجَاهِلُونَ', 39, 64, 24, 93, 465, 'بگو: «آیا به من دستور می‌دهید که غیر خدا را بپرستم ای جاهلان؟!»'),
(4123, 4123, 'وَلَقَدْ أُوحِيَ إِلَيْكَ وَإِلَى الَّذِينَ مِنْ قَبْلِكَ لَئِنْ أَشْرَكْتَ لَيَحْبَطَنَّ عَمَلُكَ وَلَتَكُونَنَّ مِنَ الْخَاسِرِينَ', 39, 65, 24, 93, 465, 'به تو و همه پیامبران پیشین وحی شده که اگر مشرک شوی، تمام اعمالت تباه می‌شود و از زیانکاران خواهی بود!'),
(4124, 4124, 'بَلِ اللَّهَ فَاعْبُدْ وَكُنْ مِنَ الشَّاكِرِينَ', 39, 66, 24, 93, 465, 'بلکه تنها خداوند را عبادت کن و از شکرگزاران باش!'),
(4125, 4125, 'وَمَا قَدَرُوا اللَّهَ حَقَّ قَدْرِهِ وَالْأَرْضُ جَمِيعًا قَبْضَتُهُ يَوْمَ الْقِيَامَةِ وَالسَّمَاوَاتُ مَطْوِيَّاتٌ بِيَمِينِهِ ۚ سُبْحَانَهُ وَتَعَالَىٰ عَمَّا يُشْرِكُونَ', 39, 67, 24, 93, 465, 'آنها خدا را آن گونه که شایسته است نشناختند، در حالی که تمام زمین در روز قیامت در قبضه اوست و آسمانها پیچیده در دست او؛ خداوند منزّه و بلندمقام است از شریکیهایی که برای او می‌پندارند'),
(4126, 4126, 'وَنُفِخَ فِي الصُّورِ فَصَعِقَ مَنْ فِي السَّمَاوَاتِ وَمَنْ فِي الْأَرْضِ إِلَّا مَنْ شَاءَ اللَّهُ ۖ ثُمَّ نُفِخَ فِيهِ أُخْرَىٰ فَإِذَا هُمْ قِيَامٌ يَنْظُرُونَ', 39, 68, 24, 93, 466, 'و در «صور» دمیده می‌شود، پس همه کسانی که در آسمانها و زمینند می‌میرند، مگر کسانی که خدا بخواهد؛ سپس بار دیگر در «صور» دمیده می‌شود، ناگهان همگی به پا می‌خیزند و در انتظار (حساب و جزا) هستند.'),
(4127, 4127, 'وَأَشْرَقَتِ الْأَرْضُ بِنُورِ رَبِّهَا وَوُضِعَ الْكِتَابُ وَجِيءَ بِالنَّبِيِّينَ وَالشُّهَدَاءِ وَقُضِيَ بَيْنَهُمْ بِالْحَقِّ وَهُمْ لَا يُظْلَمُونَ', 39, 69, 24, 93, 466, 'و زمین (در آن روز) به نور پروردگارش روشن می‌شود، و نامه‌های اعمال را پیش می‌نهند و پیامبران و گواهان را حاضر می‌سازند، و میان آنها بحق داوری می‌شود و به آنان ستم نخواهد شد!'),
(4128, 4128, 'وَوُفِّيَتْ كُلُّ نَفْسٍ مَا عَمِلَتْ وَهُوَ أَعْلَمُ بِمَا يَفْعَلُونَ', 39, 70, 24, 93, 466, 'و به هر کس آنچه انجام داده است بی‌کم و کاست داده می‌شود؛ و او نسبت به آنچه انجام می‌دادند از همه آگاهتر است.'),
(4129, 4129, 'وَسِيقَ الَّذِينَ كَفَرُوا إِلَىٰ جَهَنَّمَ زُمَرًا ۖ حَتَّىٰ إِذَا جَاءُوهَا فُتِحَتْ أَبْوَابُهَا وَقَالَ لَهُمْ خَزَنَتُهَا أَلَمْ يَأْتِكُمْ رُسُلٌ مِنْكُمْ يَتْلُونَ عَلَيْكُمْ آيَاتِ رَبِّكُمْ وَيُنْذِرُونَكُمْ لِقَاءَ يَوْمِكُمْ هَٰذَا ۚ قَالُوا بَلَىٰ وَلَٰكِنْ حَقَّتْ كَلِمَةُ الْعَذَابِ عَلَى الْكَافِرِينَ', 39, 71, 24, 93, 466, 'و کسانی که کافر شدند گروه گروه به سوی جهنّم رانده می‌شوند؛ وقتی به دوزخ می‌رسند، درهای آن گشوده می‌شود و نگهبانان دوزخ به آنها می‌گویند: «آیا رسولانی از میان شما به سویتان نیامدند که آیات پروردگارتان را برای شما بخوانند و از ملاقات این روز شما را بر حذر دارند؟!» می‌گویند: «آری، (پیامبران آمدند و آیات الهی را بر ما خواندند، و ما مخالفت کردیم!) ولی فرمان عذاب الهی بر کافران مسلّم شده است.'),
(4130, 4130, 'قِيلَ ادْخُلُوا أَبْوَابَ جَهَنَّمَ خَالِدِينَ فِيهَا ۖ فَبِئْسَ مَثْوَى الْمُتَكَبِّرِينَ', 39, 72, 24, 93, 466, 'به آنان گفته می‌شود: «از درهای جهنّم وارد شوید، جاودانه در آن بمانید؛ چه بد جایگاهی است جایگاه متکبّران!»'),
(4131, 4131, 'وَسِيقَ الَّذِينَ اتَّقَوْا رَبَّهُمْ إِلَى الْجَنَّةِ زُمَرًا ۖ حَتَّىٰ إِذَا جَاءُوهَا وَفُتِحَتْ أَبْوَابُهَا وَقَالَ لَهُمْ خَزَنَتُهَا سَلَامٌ عَلَيْكُمْ طِبْتُمْ فَادْخُلُوهَا خَالِدِينَ', 39, 73, 24, 93, 466, 'و کسانی که تقوای الهی پیشه کردند گروه گروه به سوی بهشت برده می‌شوند؛ هنگامی که به آن می‌رسند درهای بهشت گشوده می‌شود و نگهبانان به آنان می‌گویند: «سلام بر شما! گوارایتان باد این نعمتها! داخل بهشت شوید و جاودانه بمانید!»'),
(4132, 4132, 'وَقَالُوا الْحَمْدُ لِلَّهِ الَّذِي صَدَقَنَا وَعْدَهُ وَأَوْرَثَنَا الْأَرْضَ نَتَبَوَّأُ مِنَ الْجَنَّةِ حَيْثُ نَشَاءُ ۖ فَنِعْمَ أَجْرُ الْعَامِلِينَ', 39, 74, 24, 93, 466, 'آنها می‌گویند: «حمد و ستایش مخصوص خداوندی است که به وعده خویش درباره ما وفا کرد و زمین (بهشت) را میراث ما قرار داد که هر جا را بخواهیم منزلگاه خود قرار دهیم؛ چه نیکوست پاداش عمل کنندگان!»'),
(4133, 4133, 'وَتَرَى الْمَلَائِكَةَ حَافِّينَ مِنْ حَوْلِ الْعَرْشِ يُسَبِّحُونَ بِحَمْدِ رَبِّهِمْ ۖ وَقُضِيَ بَيْنَهُمْ بِالْحَقِّ وَقِيلَ الْحَمْدُ لِلَّهِ رَبِّ الْعَالَمِينَ', 39, 75, 24, 93, 467, '(در آن روز) فرشتگان را می‌بینی که بر گرد عرش خدا حلقه زده‌اند و با ستایش پروردگارشان تسبیح می‌گویند؛ و در میان بندگان بحق داوری می‌شود؛ و (سرانجام) گفته خواهد شد: «حمد مخصوص خدا پروردگار جهانیان است!»');
INSERT INTO `Quran` (`rowid`, `id`, `Arabic`, `Chapter`, `Verse`, `Juz`, `Hizb`, `Page`, `tr`) VALUES
(4134, 4134, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ حم', 40, 1, 24, 94, 467, 'حم.'),
(4135, 4135, 'تَنْزِيلُ الْكِتَابِ مِنَ اللَّهِ الْعَزِيزِ الْعَلِيمِ', 40, 2, 24, 94, 467, 'این کتابی است که از سوی خداوند قادر و دانا نازل شده است.'),
(4136, 4136, 'غَافِرِ الذَّنْبِ وَقَابِلِ التَّوْبِ شَدِيدِ الْعِقَابِ ذِي الطَّوْلِ ۖ لَا إِلَٰهَ إِلَّا هُوَ ۖ إِلَيْهِ الْمَصِيرُ', 40, 3, 24, 94, 467, 'خداوندی که آمرزنده گناه، پذیرنده توبه، دارای مجازات سخت، و صاحب نعمت فراوان است؛ هیچ معبودی جز او نیست؛ و بازگشت (همه شما) تنها بسوی اوست.'),
(4137, 4137, 'مَا يُجَادِلُ فِي آيَاتِ اللَّهِ إِلَّا الَّذِينَ كَفَرُوا فَلَا يَغْرُرْكَ تَقَلُّبُهُمْ فِي الْبِلَادِ', 40, 4, 24, 94, 467, 'تنها کسانی در آیات ما مجادله می‌کنند که (از روی عناد) کافر شده‌اند؛ پس مبادا رفت و آمد آنان در شهرها (و قدرت‌نمایی آنان) تو را بفریبد!'),
(4138, 4138, 'كَذَّبَتْ قَبْلَهُمْ قَوْمُ نُوحٍ وَالْأَحْزَابُ مِنْ بَعْدِهِمْ ۖ وَهَمَّتْ كُلُّ أُمَّةٍ بِرَسُولِهِمْ لِيَأْخُذُوهُ ۖ وَجَادَلُوا بِالْبَاطِلِ لِيُدْحِضُوا بِهِ الْحَقَّ فَأَخَذْتُهُمْ ۖ فَكَيْفَ كَانَ عِقَابِ', 40, 5, 24, 94, 467, 'پیش از آنها قوم نوح و اقوامی که بعد از ایشان بودند (پیامبرانشان را) تکذیب کردند؛ و هر امّتی در پی آن بود که توطئه کند و پیامبرش را بگیرد (و آزار دهد)، و برای محو حق به مجادله باطل دست زدند؛ امّا من آنها را گرفتم (و سخت مجازات کردم)؛ ببین که مجازات من چگونه بود!'),
(4139, 4139, 'وَكَذَٰلِكَ حَقَّتْ كَلِمَتُ رَبِّكَ عَلَى الَّذِينَ كَفَرُوا أَنَّهُمْ أَصْحَابُ النَّارِ', 40, 6, 24, 94, 467, 'و این گونه فرمان پروردگارت درباره کسانی که کافر شدند مسلّم شده که آنها همه اهل آتشند!'),
(4140, 4140, 'الَّذِينَ يَحْمِلُونَ الْعَرْشَ وَمَنْ حَوْلَهُ يُسَبِّحُونَ بِحَمْدِ رَبِّهِمْ وَيُؤْمِنُونَ بِهِ وَيَسْتَغْفِرُونَ لِلَّذِينَ آمَنُوا رَبَّنَا وَسِعْتَ كُلَّ شَيْءٍ رَحْمَةً وَعِلْمًا فَاغْفِرْ لِلَّذِينَ تَابُوا وَاتَّبَعُوا سَبِيلَكَ وَقِهِمْ عَذَابَ الْجَحِيمِ', 40, 7, 24, 94, 467, 'فرشتگانی که حاملان عرشند و آنها که گرداگرد آن (طواف می‌کنند) تسبیح و حمد پروردگارشان را می‌گویند و به او ایمان دارند و برای مؤمنان استغفار می‌کنند (و می گویند:) پروردگارا! رحمت و علم تو همه چیز را فراگرفته است؛ پس کسانی را که توبه کرده و راه تو را پیروی می‌کنند بیامرز، و آنان را از عذاب دوزخ نگاه دار!'),
(4141, 4141, 'رَبَّنَا وَأَدْخِلْهُمْ جَنَّاتِ عَدْنٍ الَّتِي وَعَدْتَهُمْ وَمَنْ صَلَحَ مِنْ آبَائِهِمْ وَأَزْوَاجِهِمْ وَذُرِّيَّاتِهِمْ ۚ إِنَّكَ أَنْتَ الْعَزِيزُ الْحَكِيمُ', 40, 8, 24, 94, 468, 'پروردگارا! آنها را در باغهای جاویدان بهشت که به آنها وعده فرموده‌ای وارد کن، همچنین از پدران و همسران و فرزندانشان هر کدام که صالح بودند، که تو توانا و حکیمی!'),
(4142, 4142, 'وَقِهِمُ السَّيِّئَاتِ ۚ وَمَنْ تَقِ السَّيِّئَاتِ يَوْمَئِذٍ فَقَدْ رَحِمْتَهُ ۚ وَذَٰلِكَ هُوَ الْفَوْزُ الْعَظِيمُ', 40, 9, 24, 94, 468, 'و آنان را از بدیها نگاه دار، و هر کس را که در آن روز از بدیها نگاه داری، مشمول رحمتت ساخته‌ای؛ و این است همان رستگاری عظیم!'),
(4143, 4143, 'إِنَّ الَّذِينَ كَفَرُوا يُنَادَوْنَ لَمَقْتُ اللَّهِ أَكْبَرُ مِنْ مَقْتِكُمْ أَنْفُسَكُمْ إِذْ تُدْعَوْنَ إِلَى الْإِيمَانِ فَتَكْفُرُونَ', 40, 10, 24, 94, 468, 'کسانی را که کافر شدند روز قیامت صدا می‌زنند که عداوت و خشم خداوند نسبت به شما از عداوت و خشم خودتان نسبت به خودتان بیشتر است، چرا که بسوی ایمان دعوت می‌شدید، ولی انکار می‌کردید!'),
(4144, 4144, 'قَالُوا رَبَّنَا أَمَتَّنَا اثْنَتَيْنِ وَأَحْيَيْتَنَا اثْنَتَيْنِ فَاعْتَرَفْنَا بِذُنُوبِنَا فَهَلْ إِلَىٰ خُرُوجٍ مِنْ سَبِيلٍ', 40, 11, 24, 94, 468, 'آنها می‌گویند: «پروردگارا! ما را دو بار میراندی و دو بار زنده کردی؛ اکنون به گناهان خود معترفیم؛ آیا راهی برای خارج شدن (از دوزخ) وجود دارد؟»'),
(4145, 4145, 'ذَٰلِكُمْ بِأَنَّهُ إِذَا دُعِيَ اللَّهُ وَحْدَهُ كَفَرْتُمْ ۖ وَإِنْ يُشْرَكْ بِهِ تُؤْمِنُوا ۚ فَالْحُكْمُ لِلَّهِ الْعَلِيِّ الْكَبِيرِ', 40, 12, 24, 94, 468, 'این بخاطر آن است که وقتی خداوند به یگانگی خوانده می‌شد انکار می‌کردید، و اگر برای او همتایی می‌پنداشتند ایمان می‌آوردید؛ اکنون داوری مخصوص خداوند بلندمرتبه و بزرگ است (و شما را مطابق عدل خود کیفر می‌دهد).'),
(4146, 4146, 'هُوَ الَّذِي يُرِيكُمْ آيَاتِهِ وَيُنَزِّلُ لَكُمْ مِنَ السَّمَاءِ رِزْقًا ۚ وَمَا يَتَذَكَّرُ إِلَّا مَنْ يُنِيبُ', 40, 13, 24, 94, 468, 'او کسی است که آیات خود را به شما نشان می‌دهد و از آسمان برای شما روزی (با ارزشی) می‌فرستد؛ تنها کسانی متذکّر این حقایق می‌شوند که بسوی خدا باز می‌گردند.'),
(4147, 4147, 'فَادْعُوا اللَّهَ مُخْلِصِينَ لَهُ الدِّينَ وَلَوْ كَرِهَ الْكَافِرُونَ', 40, 14, 24, 94, 468, '(تنها) خدا را بخوانید و دین خود را برای او خالص کنید، هرچند کافران ناخشنود باشند!'),
(4148, 4148, 'رَفِيعُ الدَّرَجَاتِ ذُو الْعَرْشِ يُلْقِي الرُّوحَ مِنْ أَمْرِهِ عَلَىٰ مَنْ يَشَاءُ مِنْ عِبَادِهِ لِيُنْذِرَ يَوْمَ التَّلَاقِ', 40, 15, 24, 94, 468, 'او درجات (بندگان صالح) را بالا می‌برد، او صاحب عرش است، روح (مقدّس) را به فرمانش بر هر کس از بندگانش که بخواهد القاء می‌کند تا (مردم را) از روز ملاقات = روز رستاخیز بیم دهد!'),
(4149, 4149, 'يَوْمَ هُمْ بَارِزُونَ ۖ لَا يَخْفَىٰ عَلَى اللَّهِ مِنْهُمْ شَيْءٌ ۚ لِمَنِ الْمُلْكُ الْيَوْمَ ۖ لِلَّهِ الْوَاحِدِ الْقَهَّارِ', 40, 16, 24, 94, 468, 'روزی که همه آنان آشکار می‌شوند و چیزی از آنها بر خدا پنهان نخواهد ماند؛ (و گفته می‌شود:) حکومت امروز برای کیست؟ برای خداوند یکتای قهّار است!'),
(4150, 4150, 'الْيَوْمَ تُجْزَىٰ كُلُّ نَفْسٍ بِمَا كَسَبَتْ ۚ لَا ظُلْمَ الْيَوْمَ ۚ إِنَّ اللَّهَ سَرِيعُ الْحِسَابِ', 40, 17, 24, 94, 469, 'امروز هر کس در برابر کاری که انجام داده است پاداش داده می‌شود؛ امروز هیچ ظلمی نیست؛ خداوند سریع الحساب است!'),
(4151, 4151, 'وَأَنْذِرْهُمْ يَوْمَ الْآزِفَةِ إِذِ الْقُلُوبُ لَدَى الْحَنَاجِرِ كَاظِمِينَ ۚ مَا لِلظَّالِمِينَ مِنْ حَمِيمٍ وَلَا شَفِيعٍ يُطَاعُ', 40, 18, 24, 94, 469, 'و آنها را از روز نزدیک بترسان، هنگامی که از شدّت وحشت دلها به گلوگاه می‌رسد و تمامی وجود آنها مملوّ از اندوه می‌گردد؛ برای ستمکاران دوستی وجود ندارد، و نه شفاعت کننده‌ای که شفاعتش پذیرفته شود.'),
(4152, 4152, 'يَعْلَمُ خَائِنَةَ الْأَعْيُنِ وَمَا تُخْفِي الصُّدُورُ', 40, 19, 24, 94, 469, 'او چشمهایی را که به خیانت می‌گردد و آنچه را سینه‌ها پنهان می‌دارند، می‌داند.'),
(4153, 4153, 'وَاللَّهُ يَقْضِي بِالْحَقِّ ۖ وَالَّذِينَ يَدْعُونَ مِنْ دُونِهِ لَا يَقْضُونَ بِشَيْءٍ ۗ إِنَّ اللَّهَ هُوَ السَّمِيعُ الْبَصِيرُ', 40, 20, 24, 94, 469, 'خداوند بحقّ داوری می‌کند، و معبودهایی را که غیر از او می‌خوانند هیچ گونه داوری ندارند؛ خداوند شنوا و بیناست!'),
(4154, 4154, ' أَوَلَمْ يَسِيرُوا فِي الْأَرْضِ فَيَنْظُرُوا كَيْفَ كَانَ عَاقِبَةُ الَّذِينَ كَانُوا مِنْ قَبْلِهِمْ ۚ كَانُوا هُمْ أَشَدَّ مِنْهُمْ قُوَّةً وَآثَارًا فِي الْأَرْضِ فَأَخَذَهُمُ اللَّهُ بِذُنُوبِهِمْ وَمَا كَانَ لَهُمْ مِنَ اللَّهِ مِنْ وَاقٍ', 40, 21, 24, 94, 469, 'آیا آنها روی زمین سیر نکردند تا ببینند عاقبت کسانی که پیش از آنان بودند چگونه بود؟! آنها در قدرت و ایجاد آثار مهمّ در زمین از اینها برتر بودند؛ ولی خداوند ایشان را به گناهانشان گرفت، و در برابر عذاب او مدافعی نداشتند!'),
(4155, 4155, 'ذَٰلِكَ بِأَنَّهُمْ كَانَتْ تَأْتِيهِمْ رُسُلُهُمْ بِالْبَيِّنَاتِ فَكَفَرُوا فَأَخَذَهُمُ اللَّهُ ۚ إِنَّهُ قَوِيٌّ شَدِيدُ الْعِقَابِ', 40, 22, 24, 94, 469, 'این برای آن بود که پیامبرانشان پیوسته با دلایل روشن به سراغشان می‌آمدند، ولی آنها انکار می‌کردند؛ خداوند هم آنان را گرفت (و کیفر داد) که او قوّی و مجازاتش شدید است!'),
(4156, 4156, 'وَلَقَدْ أَرْسَلْنَا مُوسَىٰ بِآيَاتِنَا وَسُلْطَانٍ مُبِينٍ', 40, 23, 24, 94, 469, 'ما موسی را با آیات خود و دلیل روشن فرستادیم...'),
(4157, 4157, 'إِلَىٰ فِرْعَوْنَ وَهَامَانَ وَقَارُونَ فَقَالُوا سَاحِرٌ كَذَّابٌ', 40, 24, 24, 94, 469, 'بسوی فرعون و هامان و قارون؛ ولی آنها گفتند: «او ساحری بسیار دروغگو است!»'),
(4158, 4158, 'فَلَمَّا جَاءَهُمْ بِالْحَقِّ مِنْ عِنْدِنَا قَالُوا اقْتُلُوا أَبْنَاءَ الَّذِينَ آمَنُوا مَعَهُ وَاسْتَحْيُوا نِسَاءَهُمْ ۚ وَمَا كَيْدُ الْكَافِرِينَ إِلَّا فِي ضَلَالٍ', 40, 25, 24, 94, 469, 'و هنگامی که حقّ را از سوی ما برای آنها آورد، گفتند: «پسران کسانی را که با موسی ایمان آورده‌اند بکشید و زنانشان را (برای اسارت و خدمت) زنده بگذارید!» امّا نقشه کافران جز در گمراهی نیست (و نقش بر آب می‌شود).'),
(4159, 4159, 'وَقَالَ فِرْعَوْنُ ذَرُونِي أَقْتُلْ مُوسَىٰ وَلْيَدْعُ رَبَّهُ ۖ إِنِّي أَخَافُ أَنْ يُبَدِّلَ دِينَكُمْ أَوْ أَنْ يُظْهِرَ فِي الْأَرْضِ الْفَسَادَ', 40, 26, 24, 94, 470, 'و فرعون گفت: «بگذارید موسی را بکشم، و او پروردگارش را بخواند (تا نجاتش دهد)! زیرا من می‌ترسم که آیین شما را دگرگون سازد، و یا در این سرزمین فساد بر پا کند!»'),
(4160, 4160, 'وَقَالَ مُوسَىٰ إِنِّي عُذْتُ بِرَبِّي وَرَبِّكُمْ مِنْ كُلِّ مُتَكَبِّرٍ لَا يُؤْمِنُ بِيَوْمِ الْحِسَابِ', 40, 27, 24, 94, 470, 'موسی گفت: «من به پروردگارم و پروردگار شما پناه می‌برم از هر متکبّری که به روز حساب ایمان نمی‌آورد!»'),
(4161, 4161, 'وَقَالَ رَجُلٌ مُؤْمِنٌ مِنْ آلِ فِرْعَوْنَ يَكْتُمُ إِيمَانَهُ أَتَقْتُلُونَ رَجُلًا أَنْ يَقُولَ رَبِّيَ اللَّهُ وَقَدْ جَاءَكُمْ بِالْبَيِّنَاتِ مِنْ رَبِّكُمْ ۖ وَإِنْ يَكُ كَاذِبًا فَعَلَيْهِ كَذِبُهُ ۖ وَإِنْ يَكُ صَادِقًا يُصِبْكُمْ بَعْضُ الَّذِي يَعِدُكُمْ ۖ إِنَّ اللَّهَ لَا يَهْدِي مَنْ هُوَ مُسْرِفٌ كَذَّابٌ', 40, 28, 24, 94, 470, 'و مرد مؤمنی از آل فرعون که ایمان خود را پنهان می‌داشت گفت: «آیا می‌خواهید مردی را بکشید بخاطر اینکه می‌گوید: پروردگار من «اللّه» است، در حالی که دلایل روشنی از سوی پروردگارتان برای شما آورده است؟! اگر دروغگو باشد، دروغش دامن خودش را خواهد گرفت؛ و اگر راستگو باشد، (لااقل) بعضی از عذابهایی را که وعده می‌دهد به شما خواهد رسید؛ خداوند کسی را که اسرافکار و بسیار دروغگوست هدایت نمی‌کند.'),
(4162, 4162, 'يَا قَوْمِ لَكُمُ الْمُلْكُ الْيَوْمَ ظَاهِرِينَ فِي الْأَرْضِ فَمَنْ يَنْصُرُنَا مِنْ بَأْسِ اللَّهِ إِنْ جَاءَنَا ۚ قَالَ فِرْعَوْنُ مَا أُرِيكُمْ إِلَّا مَا أَرَىٰ وَمَا أَهْدِيكُمْ إِلَّا سَبِيلَ الرَّشَادِ', 40, 29, 24, 94, 470, 'ای قوم من! امروز حکومت از آن شماست و در این سرزمین پیروزید؛ اگر عذاب خدا به سراغ ما آید، چه کسی ما را یاری خواهد کرد؟!» فرعون گفت: «من جز آنچه را معتقدم به شما ارائه نمی‌دهم، و شما را جز به راه صحیح راهنمایی نمی‌کنم! (دستور، همان قتل موسی است!)»'),
(4163, 4163, 'وَقَالَ الَّذِي آمَنَ يَا قَوْمِ إِنِّي أَخَافُ عَلَيْكُمْ مِثْلَ يَوْمِ الْأَحْزَابِ', 40, 30, 24, 94, 470, 'آن مرد باایمان گفت: «ای قوم من! من بر شما از روزی همانند روز (عذاب) اقوام پیشین بیمناکم!'),
(4164, 4164, 'مِثْلَ دَأْبِ قَوْمِ نُوحٍ وَعَادٍ وَثَمُودَ وَالَّذِينَ مِنْ بَعْدِهِمْ ۚ وَمَا اللَّهُ يُرِيدُ ظُلْمًا لِلْعِبَادِ', 40, 31, 24, 94, 470, 'و از عادتی همچون عادت قوم نوح و عاد و ثمود و کسانی که بعد از آنان بودند (از شرک و کفر و طغیان) می‌ترسم؛ و خداوند ظلم و ستمی بر بندگانش نمی‌خواهد.'),
(4165, 4165, 'وَيَا قَوْمِ إِنِّي أَخَافُ عَلَيْكُمْ يَوْمَ التَّنَادِ', 40, 32, 24, 94, 470, 'ای قوم من! من بر شما از روزی که مردم یکدیگر را صدا می‌زنند (و از هم یاری می‌طلبند و صدایشان به جایی نمی‌رسد) بیمناکم!'),
(4166, 4166, 'يَوْمَ تُوَلُّونَ مُدْبِرِينَ مَا لَكُمْ مِنَ اللَّهِ مِنْ عَاصِمٍ ۗ وَمَنْ يُضْلِلِ اللَّهُ فَمَا لَهُ مِنْ هَادٍ', 40, 33, 24, 94, 470, 'همان روزی که روی می‌گردانید و فرار می‌کنید؛ امّا هیچ پناهگاهی در برابر عذاب خداوند برای شما نیست؛ و هر کس را خداوند (بخاطر اعمالش) گمراه سازد، هدایت‌کننده‌ای برای او نیست!'),
(4167, 4167, 'وَلَقَدْ جَاءَكُمْ يُوسُفُ مِنْ قَبْلُ بِالْبَيِّنَاتِ فَمَا زِلْتُمْ فِي شَكٍّ مِمَّا جَاءَكُمْ بِهِ ۖ حَتَّىٰ إِذَا هَلَكَ قُلْتُمْ لَنْ يَبْعَثَ اللَّهُ مِنْ بَعْدِهِ رَسُولًا ۚ كَذَٰلِكَ يُضِلُّ اللَّهُ مَنْ هُوَ مُسْرِفٌ مُرْتَابٌ', 40, 34, 24, 94, 471, 'پیش از این یوسف دلایل روشن برای شما آورد، ولی شما همچنان در آنچه او برای شما آورده بود تردید داشتید؛ تا زمانی که از دنیا رفت، گفتید: هرگز خداوند بعد از او پیامبری مبعوث نخواهد کرد! این گونه خداوند هر اسرافکار تردیدکننده‌ای را گمراه می‌سازد!'),
(4168, 4168, 'الَّذِينَ يُجَادِلُونَ فِي آيَاتِ اللَّهِ بِغَيْرِ سُلْطَانٍ أَتَاهُمْ ۖ كَبُرَ مَقْتًا عِنْدَ اللَّهِ وَعِنْدَ الَّذِينَ آمَنُوا ۚ كَذَٰلِكَ يَطْبَعُ اللَّهُ عَلَىٰ كُلِّ قَلْبِ مُتَكَبِّرٍ جَبَّارٍ', 40, 35, 24, 94, 471, 'همانها که در آیات خدا بی‌آنکه دلیلی برایشان آمده باشد به مجادله برمی‌خیزند؛ (این کارشان) خشم عظیمی نزد خداوند و نزد آنان که ایمان آورده‌اند به بار می‌آورد؛ این گونه خداوند بر دل هر متکبّر جبّاری مُهر می‌نهد!»'),
(4169, 4169, 'وَقَالَ فِرْعَوْنُ يَا هَامَانُ ابْنِ لِي صَرْحًا لَعَلِّي أَبْلُغُ الْأَسْبَابَ', 40, 36, 24, 94, 471, 'فرعون گفت: «ای هامان! برای من بنای مرتفعی بساز، شاید به وسایلی دست یابم،'),
(4170, 4170, 'أَسْبَابَ السَّمَاوَاتِ فَأَطَّلِعَ إِلَىٰ إِلَٰهِ مُوسَىٰ وَإِنِّي لَأَظُنُّهُ كَاذِبًا ۚ وَكَذَٰلِكَ زُيِّنَ لِفِرْعَوْنَ سُوءُ عَمَلِهِ وَصُدَّ عَنِ السَّبِيلِ ۚ وَمَا كَيْدُ فِرْعَوْنَ إِلَّا فِي تَبَابٍ', 40, 37, 24, 94, 471, 'وسایل (صعود به) آسمانها تا از خدای موسی آگاه شوم؛ هر چند گمان می‌کنم او دروغگو باشد!» اینچنین اعمال بد فرعون در نظرش آراسته جلوه کرد و از راه حق باز داشته شد؛ و توطئه فرعون (و همفکران او) جز به نابودی نمی‌انجامد!'),
(4171, 4171, 'وَقَالَ الَّذِي آمَنَ يَا قَوْمِ اتَّبِعُونِ أَهْدِكُمْ سَبِيلَ الرَّشَادِ', 40, 38, 24, 94, 471, 'کسی که (از قوم فرعون) ایمان آورده بود گفت: «ای قوم من! از من پیروی کنید تا شما را به راه درست هدایت کنم.'),
(4172, 4172, 'يَا قَوْمِ إِنَّمَا هَٰذِهِ الْحَيَاةُ الدُّنْيَا مَتَاعٌ وَإِنَّ الْآخِرَةَ هِيَ دَارُ الْقَرَارِ', 40, 39, 24, 94, 471, 'ای قوم من! این زندگی دنیا، تنها متاع زودگذری است؛ و آخرت سرای همیشگی است!'),
(4173, 4173, 'مَنْ عَمِلَ سَيِّئَةً فَلَا يُجْزَىٰ إِلَّا مِثْلَهَا ۖ وَمَنْ عَمِلَ صَالِحًا مِنْ ذَكَرٍ أَوْ أُنْثَىٰ وَهُوَ مُؤْمِنٌ فَأُولَٰئِكَ يَدْخُلُونَ الْجَنَّةَ يُرْزَقُونَ فِيهَا بِغَيْرِ حِسَابٍ', 40, 40, 24, 94, 471, 'هر کس بدی کند، جز بمانند آن کیفر داده نمی‌شود؛ ولی هر کس کار شایسته‌ای انجام دهد -خواه مرد یا زن- در حالی که مؤمن باشد آنها وارد بهشت می‌شوند و در آن روزی بی‌حسابی به آنها داده خواهد شد.'),
(4174, 4174, ' وَيَا قَوْمِ مَا لِي أَدْعُوكُمْ إِلَى النَّجَاةِ وَتَدْعُونَنِي إِلَى النَّارِ', 40, 41, 24, 95, 472, 'ای قوم من! چرا من شما را به سوی نجات دعوت می‌کنم، امّا شما مرا بسوی آتش فرا می‌خوانید؟!'),
(4175, 4175, 'تَدْعُونَنِي لِأَكْفُرَ بِاللَّهِ وَأُشْرِكَ بِهِ مَا لَيْسَ لِي بِهِ عِلْمٌ وَأَنَا أَدْعُوكُمْ إِلَى الْعَزِيزِ الْغَفَّارِ', 40, 42, 24, 95, 472, 'مرا دعوت می‌کنید که به خداوند یگانه کافر شوم و همتایی که به آن علم ندارم برای او قرار دهم، در حالی که من شما را بسوی خداوند عزیز غفّار دعوت می‌کنم!'),
(4176, 4176, 'لَا جَرَمَ أَنَّمَا تَدْعُونَنِي إِلَيْهِ لَيْسَ لَهُ دَعْوَةٌ فِي الدُّنْيَا وَلَا فِي الْآخِرَةِ وَأَنَّ مَرَدَّنَا إِلَى اللَّهِ وَأَنَّ الْمُسْرِفِينَ هُمْ أَصْحَابُ النَّارِ', 40, 43, 24, 95, 472, 'قطعاً آنچه مرا بسوی آن می‌خوانید، نه دعوت (و حاکمیّتی) در دنیا دارد و نه در آخرت؛ و تنها بازگشت ما در قیامت بسوی خداست؛ و مسرفان اهل آتشند!'),
(4177, 4177, 'فَسَتَذْكُرُونَ مَا أَقُولُ لَكُمْ ۚ وَأُفَوِّضُ أَمْرِي إِلَى اللَّهِ ۚ إِنَّ اللَّهَ بَصِيرٌ بِالْعِبَادِ', 40, 44, 24, 95, 472, 'و بزودی آنچه را به شما می‌گویم به خاطر خواهید آورد! من کار خود را به خدا واگذارم که خداوند نسبت به بندگانش بیناست!»'),
(4178, 4178, 'فَوَقَاهُ اللَّهُ سَيِّئَاتِ مَا مَكَرُوا ۖ وَحَاقَ بِآلِ فِرْعَوْنَ سُوءُ الْعَذَابِ', 40, 45, 24, 95, 472, 'خداوند او را از نقشه‌های سوء آنها نگه داشت، و عذاب شدید بر آل فرعون وارد شد!'),
(4179, 4179, 'النَّارُ يُعْرَضُونَ عَلَيْهَا غُدُوًّا وَعَشِيًّا ۖ وَيَوْمَ تَقُومُ السَّاعَةُ أَدْخِلُوا آلَ فِرْعَوْنَ أَشَدَّ الْعَذَابِ', 40, 46, 24, 95, 472, 'عذاب آنها آتش است که هر صبح و شام بر آن عرضه می‌شوند؛ و روزی که قیامت برپا شود (می‌فرماید:) «آل فرعون را در سخت‌ترین عذابها وارد کنید!»'),
(4180, 4180, 'وَإِذْ يَتَحَاجُّونَ فِي النَّارِ فَيَقُولُ الضُّعَفَاءُ لِلَّذِينَ اسْتَكْبَرُوا إِنَّا كُنَّا لَكُمْ تَبَعًا فَهَلْ أَنْتُمْ مُغْنُونَ عَنَّا نَصِيبًا مِنَ النَّارِ', 40, 47, 24, 95, 472, 'به خاطر بیاور هنگامی را که در آتش دوزخ با هم محاجّه می‌کنند؛ ضعیفان به مستکبران می‌گویند: «ما پیرو شما بودیم، آیا شما (امروز) سهمی از آتش را بجای ما پذیرا می‌شوید؟!»'),
(4181, 4181, 'قَالَ الَّذِينَ اسْتَكْبَرُوا إِنَّا كُلٌّ فِيهَا إِنَّ اللَّهَ قَدْ حَكَمَ بَيْنَ الْعِبَادِ', 40, 48, 24, 95, 472, 'مستکبران می‌گویند: «ما همگی در آن هستیم، زیرا خداوند در میان بندگانش (بعدالت) حکم کرده است!»'),
(4182, 4182, 'وَقَالَ الَّذِينَ فِي النَّارِ لِخَزَنَةِ جَهَنَّمَ ادْعُوا رَبَّكُمْ يُخَفِّفْ عَنَّا يَوْمًا مِنَ الْعَذَابِ', 40, 49, 24, 95, 472, 'و آنها که در آتشند به مأموران دوزخ می‌گویند: «از پروردگارتان بخواهید یک روز عذاب را از ما بردارد!»'),
(4183, 4183, 'قَالُوا أَوَلَمْ تَكُ تَأْتِيكُمْ رُسُلُكُمْ بِالْبَيِّنَاتِ ۖ قَالُوا بَلَىٰ ۚ قَالُوا فَادْعُوا ۗ وَمَا دُعَاءُ الْكَافِرِينَ إِلَّا فِي ضَلَالٍ', 40, 50, 24, 95, 473, 'آنها می‌گویند: «آیا پیامبران شما دلایل روشن برایتان نیاوردند؟!» می‌گویند: «آری!» آنها می‌گویند: «پس هر چه می‌خواهید (خدا را) بخوانید؛ ولی دعای کافران (به جایی نمی‌رسد و) جز در ضلالت نیست!»'),
(4184, 4184, 'إِنَّا لَنَنْصُرُ رُسُلَنَا وَالَّذِينَ آمَنُوا فِي الْحَيَاةِ الدُّنْيَا وَيَوْمَ يَقُومُ الْأَشْهَادُ', 40, 51, 24, 95, 473, 'ما به یقین پیامبران خود و کسانی را که ایمان آورده‌اند، در زندگی دنیا و (در آخرت) روزی که گواهان به پا می‌خیزند یاری می‌دهیم!'),
(4185, 4185, 'يَوْمَ لَا يَنْفَعُ الظَّالِمِينَ مَعْذِرَتُهُمْ ۖ وَلَهُمُ اللَّعْنَةُ وَلَهُمْ سُوءُ الدَّارِ', 40, 52, 24, 95, 473, 'روزی که عذرخواهی ظالمان سودی به حالشان نمی‌بخشد؛ و لعنت خدا برای آنها، و خانه (و جایگاه) بد نیز برای آنان است.'),
(4186, 4186, 'وَلَقَدْ آتَيْنَا مُوسَى الْهُدَىٰ وَأَوْرَثْنَا بَنِي إِسْرَائِيلَ الْكِتَابَ', 40, 53, 24, 95, 473, 'و ما به موسی هدایت بخشیدیم، و بنی اسرائیل را وارثان کتاب (تورات) قرار دادیم.'),
(4187, 4187, 'هُدًى وَذِكْرَىٰ لِأُولِي الْأَلْبَابِ', 40, 54, 24, 95, 473, 'کتابی که مایه هدایت و تذکّر برای صاحبان عقل بود!'),
(4188, 4188, 'فَاصْبِرْ إِنَّ وَعْدَ اللَّهِ حَقٌّ وَاسْتَغْفِرْ لِذَنْبِكَ وَسَبِّحْ بِحَمْدِ رَبِّكَ بِالْعَشِيِّ وَالْإِبْكَارِ', 40, 55, 24, 95, 473, 'پس (ای پیامبر!) صبر و شکیبایی پیشه کن که وعده خدا حقّ است، و برای گناهت استغفار کن، و هر صبح و شام تسبیح و حمد پروردگارت را بجا آور!'),
(4189, 4189, 'إِنَّ الَّذِينَ يُجَادِلُونَ فِي آيَاتِ اللَّهِ بِغَيْرِ سُلْطَانٍ أَتَاهُمْ ۙ إِنْ فِي صُدُورِهِمْ إِلَّا كِبْرٌ مَا هُمْ بِبَالِغِيهِ ۚ فَاسْتَعِذْ بِاللَّهِ ۖ إِنَّهُ هُوَ السَّمِيعُ الْبَصِيرُ', 40, 56, 24, 95, 473, 'کسانی که در آیات خداوند بدون دلیلی که برای آنها آمده باشد ستیزه‌جویی می‌کنند، در سینه‌هایشان فقط تکبّر (و غرور) است، و هرگز به خواسته خود نخواهند رسید، پس به خدا پناه بر که او شنوا و بیناست!'),
(4190, 4190, 'لَخَلْقُ السَّمَاوَاتِ وَالْأَرْضِ أَكْبَرُ مِنْ خَلْقِ النَّاسِ وَلَٰكِنَّ أَكْثَرَ النَّاسِ لَا يَعْلَمُونَ', 40, 57, 24, 95, 473, 'آفرینش آسمانها و زمین از آفرینش انسانها مهمتر است، ولی بیشتر مردم نمی‌دانند.'),
(4191, 4191, 'وَمَا يَسْتَوِي الْأَعْمَىٰ وَالْبَصِيرُ وَالَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ وَلَا الْمُسِيءُ ۚ قَلِيلًا مَا تَتَذَكَّرُونَ', 40, 58, 24, 95, 473, 'هرگز نابینا و بینا یکسان نیستند؛ همچنین کسانی که ایمان آورده، و اعمال صالح انجام داده‌اند با بدکاران یکسان نخواهند بود؛ امّا کمتر متذکّر می‌شوید!'),
(4192, 4192, 'إِنَّ السَّاعَةَ لَآتِيَةٌ لَا رَيْبَ فِيهَا وَلَٰكِنَّ أَكْثَرَ النَّاسِ لَا يُؤْمِنُونَ', 40, 59, 24, 95, 474, 'روز قیامت به یقین آمدنی است، و شکّی در آن نیست؛ ولی اکثر مردم ایمان نمی آورند!'),
(4193, 4193, 'وَقَالَ رَبُّكُمُ ادْعُونِي أَسْتَجِبْ لَكُمْ ۚ إِنَّ الَّذِينَ يَسْتَكْبِرُونَ عَنْ عِبَادَتِي سَيَدْخُلُونَ جَهَنَّمَ دَاخِرِينَ', 40, 60, 24, 95, 474, 'پروردگار شما گفته است: «مرا بخوانید تا (دعای) شما را بپذیرم! کسانی که از عبادت من تکبّر می‌ورزند به زودی با ذلّت وارد دوزخ می‌شوند!»'),
(4194, 4194, 'اللَّهُ الَّذِي جَعَلَ لَكُمُ اللَّيْلَ لِتَسْكُنُوا فِيهِ وَالنَّهَارَ مُبْصِرًا ۚ إِنَّ اللَّهَ لَذُو فَضْلٍ عَلَى النَّاسِ وَلَٰكِنَّ أَكْثَرَ النَّاسِ لَا يَشْكُرُونَ', 40, 61, 24, 95, 474, 'خداوند کسی است که شب را برای شما آفرید تا در آن بیاسایید، و روز را روشنی‌بخش قرار داد؛ خداوند نسبت به مردم صاحب فضل و کرم است؛ ولی بیشتر مردم شکرگزاری نمی‌کنند!'),
(4195, 4195, 'ذَٰلِكُمُ اللَّهُ رَبُّكُمْ خَالِقُ كُلِّ شَيْءٍ لَا إِلَٰهَ إِلَّا هُوَ ۖ فَأَنَّىٰ تُؤْفَكُونَ', 40, 62, 24, 95, 474, 'این است خداوند، پروردگار شما که آفریننده همه چیز است؛ هیچ معبودی جز او نیست؛ با این حال چگونه از راه حق منحرف می‌شوید؟!'),
(4196, 4196, 'كَذَٰلِكَ يُؤْفَكُ الَّذِينَ كَانُوا بِآيَاتِ اللَّهِ يَجْحَدُونَ', 40, 63, 24, 95, 474, 'اینچنین کسانی که آیات خدا را انکار می‌کردند (از راه راست) منحرف می‌شوند!'),
(4197, 4197, 'اللَّهُ الَّذِي جَعَلَ لَكُمُ الْأَرْضَ قَرَارًا وَالسَّمَاءَ بِنَاءً وَصَوَّرَكُمْ فَأَحْسَنَ صُوَرَكُمْ وَرَزَقَكُمْ مِنَ الطَّيِّبَاتِ ۚ ذَٰلِكُمُ اللَّهُ رَبُّكُمْ ۖ فَتَبَارَكَ اللَّهُ رَبُّ الْعَالَمِينَ', 40, 64, 24, 95, 474, 'خداوند کسی است که زمین را برای شما جایگاه امن و آرامش قرار داد و آسمان را همچون سقفی (بالای سرتان)؛ و شما را صورتگری کرد، و صورتتان را نیکو آفرید؛ و از چیزهایی پاکیزه به شما روزی داد؛ این است خداوند پروردگار شما! جاوید و پربرکت است خداوندی که پروردگار عالمیان است!'),
(4198, 4198, 'هُوَ الْحَيُّ لَا إِلَٰهَ إِلَّا هُوَ فَادْعُوهُ مُخْلِصِينَ لَهُ الدِّينَ ۗ الْحَمْدُ لِلَّهِ رَبِّ الْعَالَمِينَ', 40, 65, 24, 95, 474, 'زنده (واقعی) اوست؛ معبودی جز او نیست؛ پس او را بخوانید در حالی که دین خود را برای او خالص کرده‌اید! ستایش مخصوص خداوندی است که پروردگار جهانیان است!'),
(4199, 4199, ' قُلْ إِنِّي نُهِيتُ أَنْ أَعْبُدَ الَّذِينَ تَدْعُونَ مِنْ دُونِ اللَّهِ لَمَّا جَاءَنِيَ الْبَيِّنَاتُ مِنْ رَبِّي وَأُمِرْتُ أَنْ أُسْلِمَ لِرَبِّ الْعَالَمِينَ', 40, 66, 24, 95, 474, 'بگو: «من نهی شده‌ام از اینکه معبودهایی را که شما غیر از خدا می‌خوانید بپرستم، چون دلایل روشن از جانب پروردگارم برای من آمده است؛ و مأمورم که تنها در برابر پروردگار عالمیان تسلیم باشم!»'),
(4200, 4200, 'هُوَ الَّذِي خَلَقَكُمْ مِنْ تُرَابٍ ثُمَّ مِنْ نُطْفَةٍ ثُمَّ مِنْ عَلَقَةٍ ثُمَّ يُخْرِجُكُمْ طِفْلًا ثُمَّ لِتَبْلُغُوا أَشُدَّكُمْ ثُمَّ لِتَكُونُوا شُيُوخًا ۚ وَمِنْكُمْ مَنْ يُتَوَفَّىٰ مِنْ قَبْلُ ۖ وَلِتَبْلُغُوا أَجَلًا مُسَمًّى وَلَعَلَّكُمْ تَعْقِلُونَ', 40, 67, 24, 95, 475, 'او کسی است که شما را از خاک آفرید، سپس از نطفه، سپس از علقه (خون بسته شده)، سپس شما را بصورت طفلی (از شکم مادر) بیرون می‌فرستد، بعد به مرحله کمال قوّت خود می‌رسید، و بعد از آن پیر می‌شوید و (در این میان) گروهی از شما پیش از رسیدن به این مرحله می‌میرند و در نهایت به سرآمد عمر خود می‌رسید؛ و شاید تعقّل کنید!'),
(4201, 4201, 'هُوَ الَّذِي يُحْيِي وَيُمِيتُ ۖ فَإِذَا قَضَىٰ أَمْرًا فَإِنَّمَا يَقُولُ لَهُ كُنْ فَيَكُونُ', 40, 68, 24, 95, 475, 'او کسی است که زنده می‌کند و می‌میراند؛ و هنگامی که کاری را مقرّر کند، تنها به آن می‌گوید: «موجود باش!» بی‌درنگ موجود می‌شود!'),
(4202, 4202, 'أَلَمْ تَرَ إِلَى الَّذِينَ يُجَادِلُونَ فِي آيَاتِ اللَّهِ أَنَّىٰ يُصْرَفُونَ', 40, 69, 24, 95, 475, 'آیا ندیدی کسانی را که در آیات خدا مجادله می‌کنند، چگونه از راه حقّ منحرف می شوند؟!'),
(4203, 4203, 'الَّذِينَ كَذَّبُوا بِالْكِتَابِ وَبِمَا أَرْسَلْنَا بِهِ رُسُلَنَا ۖ فَسَوْفَ يَعْلَمُونَ', 40, 70, 24, 95, 475, 'همان کسانی که کتاب (آسمانی) و آنچه رسولان خود را بدان فرستاده‌ایم تکذیب کردند؛ امّا بزودی (نتیجه کار خود را) می‌دانند!'),
(4204, 4204, 'إِذِ الْأَغْلَالُ فِي أَعْنَاقِهِمْ وَالسَّلَاسِلُ يُسْحَبُونَ', 40, 71, 24, 95, 475, 'در آن هنگام که غل و زنجیرها بر گردن آنان قرار گرفته و آنها را می‌کشند...'),
(4205, 4205, 'فِي الْحَمِيمِ ثُمَّ فِي النَّارِ يُسْجَرُونَ', 40, 72, 24, 95, 475, 'و در آب جوشان وارد می‌کنند؛ سپس در آتش دوزخ افروخته می‌شوند!'),
(4206, 4206, 'ثُمَّ قِيلَ لَهُمْ أَيْنَ مَا كُنْتُمْ تُشْرِكُونَ', 40, 73, 24, 95, 475, 'سپس به آنها گفته می‌شود: «کجایند آنچه را همتای خدا قرار می‌دادید،'),
(4207, 4207, 'مِنْ دُونِ اللَّهِ ۖ قَالُوا ضَلُّوا عَنَّا بَلْ لَمْ نَكُنْ نَدْعُو مِنْ قَبْلُ شَيْئًا ۚ كَذَٰلِكَ يُضِلُّ اللَّهُ الْكَافِرِينَ', 40, 74, 24, 95, 475, 'همان معبودهایی را که جز خدا پرستش می‌کردید؟!» آنها می‌گویند: «همه از نظر ما پنهان و گم شدند؛ بلکه ما اصلاً پیش از این چیزی را پرستش نمی‌کردیم»! این گونه خداوند کافران را گمراه می‌سازد!'),
(4208, 4208, 'ذَٰلِكُمْ بِمَا كُنْتُمْ تَفْرَحُونَ فِي الْأَرْضِ بِغَيْرِ الْحَقِّ وَبِمَا كُنْتُمْ تَمْرَحُونَ', 40, 75, 24, 95, 475, 'این (عذاب) بخاطر آن است که بناحقّ در زمین شادی می‌کردید و از روی غرور و مستی به خوشحالی می‌پرداختید!'),
(4209, 4209, 'ادْخُلُوا أَبْوَابَ جَهَنَّمَ خَالِدِينَ فِيهَا ۖ فَبِئْسَ مَثْوَى الْمُتَكَبِّرِينَ', 40, 76, 24, 95, 475, 'از درهای جهنّم وارد شوید و جاودانه در آن بمانید؛ و چه بد است جایگاه متکبّران!'),
(4210, 4210, 'فَاصْبِرْ إِنَّ وَعْدَ اللَّهِ حَقٌّ ۚ فَإِمَّا نُرِيَنَّكَ بَعْضَ الَّذِي نَعِدُهُمْ أَوْ نَتَوَفَّيَنَّكَ فَإِلَيْنَا يُرْجَعُونَ', 40, 77, 24, 95, 475, 'پس (ای پیامبر) صبر کن که وعده خدا حقّ است؛ و هرگاه قسمتی از مجازاتهایی را که به آنها وعده داده‌ایم در حال حیاتت به تو ارائه دهیم، یا تو را (پیش از آن) از دنیا ببریم (مهمّ نیست)؛ چرا که همه آنان را تنها بسوی ما باز می‌گردانند!'),
(4211, 4211, 'وَلَقَدْ أَرْسَلْنَا رُسُلًا مِنْ قَبْلِكَ مِنْهُمْ مَنْ قَصَصْنَا عَلَيْكَ وَمِنْهُمْ مَنْ لَمْ نَقْصُصْ عَلَيْكَ ۗ وَمَا كَانَ لِرَسُولٍ أَنْ يَأْتِيَ بِآيَةٍ إِلَّا بِإِذْنِ اللَّهِ ۚ فَإِذَا جَاءَ أَمْرُ اللَّهِ قُضِيَ بِالْحَقِّ وَخَسِرَ هُنَالِكَ الْمُبْطِلُونَ', 40, 78, 24, 95, 476, 'ما پیش از تو رسولانی فرستادیم؛ سرگذشت گروهی از آنان را برای تو بازگفته، و گروهی را برای تو بازگو نکرده‌ایم؛ و هیچ پیامبری حق نداشت معجزه‌ای جز بفرمان خدا بیاورد و هنگامی که فرمان خداوند (برای مجازات آنها) صادر شود، بحق داوری خواهد شد؛ و آنجا اهل باطل زیان خواهند کرد!'),
(4212, 4212, 'اللَّهُ الَّذِي جَعَلَ لَكُمُ الْأَنْعَامَ لِتَرْكَبُوا مِنْهَا وَمِنْهَا تَأْكُلُونَ', 40, 79, 24, 95, 476, 'خداوند کسی است که چهارپایان را برای شما آفرید تا بعضی را سوار شوید و از بعضی تغذیه کنید.'),
(4213, 4213, 'وَلَكُمْ فِيهَا مَنَافِعُ وَلِتَبْلُغُوا عَلَيْهَا حَاجَةً فِي صُدُورِكُمْ وَعَلَيْهَا وَعَلَى الْفُلْكِ تُحْمَلُونَ', 40, 80, 24, 95, 476, 'و برای شما در آنها منافع بسیاری (جز اینها) است، تا بوسیله آنها به مقصدی که در دل دارید برسید؛ و بر آنها و بر کشتیها سوار می‌شوید.'),
(4214, 4214, 'وَيُرِيكُمْ آيَاتِهِ فَأَيَّ آيَاتِ اللَّهِ تُنْكِرُونَ', 40, 81, 24, 95, 476, 'او آیاتش را همواره به شما نشان می‌دهد؛ پس کدام یک از آیات او را انکار می‌کنید؟!'),
(4215, 4215, 'أَفَلَمْ يَسِيرُوا فِي الْأَرْضِ فَيَنْظُرُوا كَيْفَ كَانَ عَاقِبَةُ الَّذِينَ مِنْ قَبْلِهِمْ ۚ كَانُوا أَكْثَرَ مِنْهُمْ وَأَشَدَّ قُوَّةً وَآثَارًا فِي الْأَرْضِ فَمَا أَغْنَىٰ عَنْهُمْ مَا كَانُوا يَكْسِبُونَ', 40, 82, 24, 95, 476, 'آیا روی زمین سیر نکردند تا ببینند عاقبت کسانی که پیش از آنها بودند چه شد؟! همانها که نفراتشان از اینها بیشتر، و نیرو و آثارشان در زمین فزونتر بود؛ امّا هرگز آنچه را به دست می‌آوردند نتوانست آنها را بی‌نیاز سازد (و عذاب الهی را از آنان دور کند)!'),
(4216, 4216, 'فَلَمَّا جَاءَتْهُمْ رُسُلُهُمْ بِالْبَيِّنَاتِ فَرِحُوا بِمَا عِنْدَهُمْ مِنَ الْعِلْمِ وَحَاقَ بِهِمْ مَا كَانُوا بِهِ يَسْتَهْزِئُونَ', 40, 83, 24, 95, 476, 'هنگامی که رسولانشان دلایل روشنی برای آنان آوردند، به دانشی که خود داشتند خوشحال بودند (و غیر آن را هیچ می‌شمردند)؛ ولی آنچه را (از عذاب) به تمسخر می‌گرفتند آنان را فراگرفت!'),
(4217, 4217, 'فَلَمَّا رَأَوْا بَأْسَنَا قَالُوا آمَنَّا بِاللَّهِ وَحْدَهُ وَكَفَرْنَا بِمَا كُنَّا بِهِ مُشْرِكِينَ', 40, 84, 24, 95, 476, 'هنگامی که عذاب (شدید) ما را دیدند گفتند: «هم اکنون به خداوند یگانه ایمان آوردیم و به معبودهایی که همتای او می‌شمردیم کافر شدیم!»'),
(4218, 4218, 'فَلَمْ يَكُ يَنْفَعُهُمْ إِيمَانُهُمْ لَمَّا رَأَوْا بَأْسَنَا ۖ سُنَّتَ اللَّهِ الَّتِي قَدْ خَلَتْ فِي عِبَادِهِ ۖ وَخَسِرَ هُنَالِكَ الْكَافِرُونَ', 40, 85, 24, 95, 476, 'امّا هنگامی که عذاب ما را مشاهده کردند، ایمانشان برای آنها سودی نداشت! این سنّت خداوند است که همواره در میان بندگانش اجرا شده، و آنجا کافران زیانکار شدند!'),
(4219, 4219, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ حم', 41, 1, 24, 95, 477, 'حم‌'),
(4220, 4220, 'تَنْزِيلٌ مِنَ الرَّحْمَٰنِ الرَّحِيمِ', 41, 2, 24, 95, 477, 'این کتابی است که از سوی خداوند رحمان و رحیم نازل شده است؛'),
(4221, 4221, 'كِتَابٌ فُصِّلَتْ آيَاتُهُ قُرْآنًا عَرَبِيًّا لِقَوْمٍ يَعْلَمُونَ', 41, 3, 24, 95, 477, 'کتابی که آیاتش هر مطلبی را در جای خود بازگو کرده، در حالی که فصیح و گویاست برای جمعیّتی که آگاهند!'),
(4222, 4222, 'بَشِيرًا وَنَذِيرًا فَأَعْرَضَ أَكْثَرُهُمْ فَهُمْ لَا يَسْمَعُونَ', 41, 4, 24, 95, 477, 'قرآنی که بشارت دهنده و بیم دهنده است؛ ولی بیشتر آنان روی گردان شدند؛ از این رو چیزی نمی‌شنوند!'),
(4223, 4223, 'وَقَالُوا قُلُوبُنَا فِي أَكِنَّةٍ مِمَّا تَدْعُونَا إِلَيْهِ وَفِي آذَانِنَا وَقْرٌ وَمِنْ بَيْنِنَا وَبَيْنِكَ حِجَابٌ فَاعْمَلْ إِنَّنَا عَامِلُونَ', 41, 5, 24, 95, 477, 'آنها گفتند: «قلبهای ما نسبت به آنچه ما را به آن دعوت می‌کنی در پوششهایی قرار گرفته و در گوشهای ما سنگینی است، و میان ما و تو حجابی وجود دارد؛ پس تو بدنبال عمل خود باش، ما هم برای خود عمل می‌کنیم!»'),
(4224, 4224, 'قُلْ إِنَّمَا أَنَا بَشَرٌ مِثْلُكُمْ يُوحَىٰ إِلَيَّ أَنَّمَا إِلَٰهُكُمْ إِلَٰهٌ وَاحِدٌ فَاسْتَقِيمُوا إِلَيْهِ وَاسْتَغْفِرُوهُ ۗ وَوَيْلٌ لِلْمُشْرِكِينَ', 41, 6, 24, 95, 477, 'بگو: من فقط انسانی مثل شما هستم؛ این حقیقت بر من وحی می‌شود که معبود شما معبودی یگانه است؛ پس تمام توجّه خویش را به او کنید و از وی آمرزش طلبید؛ وای بر مشرکان!'),
(4225, 4225, 'الَّذِينَ لَا يُؤْتُونَ الزَّكَاةَ وَهُمْ بِالْآخِرَةِ هُمْ كَافِرُونَ', 41, 7, 24, 95, 477, 'همانها که زکات را نمی‌پردازند، و آخرت را منکرند!'),
(4226, 4226, 'إِنَّ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ لَهُمْ أَجْرٌ غَيْرُ مَمْنُونٍ', 41, 8, 24, 95, 477, 'امّا کسانی که ایمان آورده و کارهای شایسته انجام دادند، پاداشی دائمی دارند!'),
(4227, 4227, ' قُلْ أَئِنَّكُمْ لَتَكْفُرُونَ بِالَّذِي خَلَقَ الْأَرْضَ فِي يَوْمَيْنِ وَتَجْعَلُونَ لَهُ أَنْدَادًا ۚ ذَٰلِكَ رَبُّ الْعَالَمِينَ', 41, 9, 24, 96, 477, 'بگو: آیا شما به آن کس که زمین را در دو روز آفرید کافر هستید و برای او همانندهایی قرارمی‌دهید؟! او پروردگار جهانیان است!'),
(4228, 4228, 'وَجَعَلَ فِيهَا رَوَاسِيَ مِنْ فَوْقِهَا وَبَارَكَ فِيهَا وَقَدَّرَ فِيهَا أَقْوَاتَهَا فِي أَرْبَعَةِ أَيَّامٍ سَوَاءً لِلسَّائِلِينَ', 41, 10, 24, 96, 477, 'او در زمین کوه‌های استواری قرار داد و برکاتی در آن آفرید و موادّ غذایی آن را مقدّر فرمود، - ینها همه در چهار روز بود- درست به اندازه نیاز تقاضا کنندگان!'),
(4229, 4229, 'ثُمَّ اسْتَوَىٰ إِلَى السَّمَاءِ وَهِيَ دُخَانٌ فَقَالَ لَهَا وَلِلْأَرْضِ ائْتِيَا طَوْعًا أَوْ كَرْهًا قَالَتَا أَتَيْنَا طَائِعِينَ', 41, 11, 24, 96, 477, 'سپس به آفرینش آسمان پرداخت، در حالی که بصورت دود بود؛ به آن و به زمین دستور داد: «به وجود آیید (و شکل گیرید)، خواه از روی اطاعت و خواه اکراه!» آنها گفتند: «ما از روی طاعت می‌آییم (و شکل می‌گیریم)!»'),
(4230, 4230, 'فَقَضَاهُنَّ سَبْعَ سَمَاوَاتٍ فِي يَوْمَيْنِ وَأَوْحَىٰ فِي كُلِّ سَمَاءٍ أَمْرَهَا ۚ وَزَيَّنَّا السَّمَاءَ الدُّنْيَا بِمَصَابِيحَ وَحِفْظًا ۚ ذَٰلِكَ تَقْدِيرُ الْعَزِيزِ الْعَلِيمِ', 41, 12, 24, 96, 478, 'در این هنگام آنها را بصورت هفت آسمان در دو روز آفرید، و در هر آسمانی کار آن (آسمان) را وحی (و مقرّر) فرمود، و آسمان پایین را با چراغهایی = ستارگان‌ زینت بخشیدیم، و (با شهابها از رخنه شیاطین) حفظ کردیم، این است تقدیر خداوند توانا و دانا!'),
(4231, 4231, 'فَإِنْ أَعْرَضُوا فَقُلْ أَنْذَرْتُكُمْ صَاعِقَةً مِثْلَ صَاعِقَةِ عَادٍ وَثَمُودَ', 41, 13, 24, 96, 478, 'اگر آنها روی‌گردان شوند، بگو: من شما را از صاعقه‌ای همانند صاعقه عاد و ثمود می‌ترسانم!'),
(4232, 4232, 'إِذْ جَاءَتْهُمُ الرُّسُلُ مِنْ بَيْنِ أَيْدِيهِمْ وَمِنْ خَلْفِهِمْ أَلَّا تَعْبُدُوا إِلَّا اللَّهَ ۖ قَالُوا لَوْ شَاءَ رَبُّنَا لَأَنْزَلَ مَلَائِكَةً فَإِنَّا بِمَا أُرْسِلْتُمْ بِهِ كَافِرُونَ', 41, 14, 24, 96, 478, 'در آن هنگام که رسولان از پیش رو و پشت سر (و از هر سو) به سراغشان آمدند (و آنان را دعوت کردند) که جز خدا را نپرستید آنها گفتند: «اگر پروردگار ما می‌خواست فرشتگانی نازل می‌کرد؛ از این رو ما به آنچه شما مبعوث به آن هستید کافریم!»'),
(4233, 4233, 'فَأَمَّا عَادٌ فَاسْتَكْبَرُوا فِي الْأَرْضِ بِغَيْرِ الْحَقِّ وَقَالُوا مَنْ أَشَدُّ مِنَّا قُوَّةً ۖ أَوَلَمْ يَرَوْا أَنَّ اللَّهَ الَّذِي خَلَقَهُمْ هُوَ أَشَدُّ مِنْهُمْ قُوَّةً ۖ وَكَانُوا بِآيَاتِنَا يَجْحَدُونَ', 41, 15, 24, 96, 478, 'امّا قوم عاد بناحق در زمین تکبّر ورزیدند و گفتند: «چه کسی از ما نیرومندتر است؟!» آیا نمی‌دانستند خداوندی که آنان را آفریده از آنها قویتر است؟ و (به خاطر این پندار) پیوسته آیات ما را انکار می‌کردند.'),
(4234, 4234, 'فَأَرْسَلْنَا عَلَيْهِمْ رِيحًا صَرْصَرًا فِي أَيَّامٍ نَحِسَاتٍ لِنُذِيقَهُمْ عَذَابَ الْخِزْيِ فِي الْحَيَاةِ الدُّنْيَا ۖ وَلَعَذَابُ الْآخِرَةِ أَخْزَىٰ ۖ وَهُمْ لَا يُنْصَرُونَ', 41, 16, 24, 96, 478, 'سرانجام تندبادی شدید و هول‌انگیز و سرد و سخت در روزهایی شوم و پرغبار بر آنها فرستادیم تا عذاب خوارکننده را در زندگی دنیا به آنها بچشانیم؛ و عذاب آخرت از آن هم خوارکننده‌تر است، و (از هیچ طرف) یاری نمی‌شوند!'),
(4235, 4235, 'وَأَمَّا ثَمُودُ فَهَدَيْنَاهُمْ فَاسْتَحَبُّوا الْعَمَىٰ عَلَى الْهُدَىٰ فَأَخَذَتْهُمْ صَاعِقَةُ الْعَذَابِ الْهُونِ بِمَا كَانُوا يَكْسِبُونَ', 41, 17, 24, 96, 478, 'امّا ثمود را هدایت کردیم، ولی آنها نابینایی را بر هدایت ترجیح دادند؛ به همین جهت صاعقه -آن عذاب خوارکننده- به خاطر اعمالی که انجام می‌دادند آنها را فروگرفت!'),
(4236, 4236, 'وَنَجَّيْنَا الَّذِينَ آمَنُوا وَكَانُوا يَتَّقُونَ', 41, 18, 24, 96, 478, 'و کسانی را که ایمان آوردند و پرهیزگار بودند نجات بخشیدیم!'),
(4237, 4237, 'وَيَوْمَ يُحْشَرُ أَعْدَاءُ اللَّهِ إِلَى النَّارِ فَهُمْ يُوزَعُونَ', 41, 19, 24, 96, 478, 'به خاطر بیاورید روزی را که دشمنان خدا را جمع کرده به سوی دوزخ می‌برند، و صفوف پیشین را نگه می‌دارند (تا صفهای بعد به آنها ملحق شوند!)'),
(4238, 4238, 'حَتَّىٰ إِذَا مَا جَاءُوهَا شَهِدَ عَلَيْهِمْ سَمْعُهُمْ وَأَبْصَارُهُمْ وَجُلُودُهُمْ بِمَا كَانُوا يَعْمَلُونَ', 41, 20, 24, 96, 478, 'وقتی به آن می‌رسند، گوشها و چشمها و پوستهای تنشان به آنچه می‌کردند گواهی می‌دهند.'),
(4239, 4239, 'وَقَالُوا لِجُلُودِهِمْ لِمَ شَهِدْتُمْ عَلَيْنَا ۖ قَالُوا أَنْطَقَنَا اللَّهُ الَّذِي أَنْطَقَ كُلَّ شَيْءٍ وَهُوَ خَلَقَكُمْ أَوَّلَ مَرَّةٍ وَإِلَيْهِ تُرْجَعُونَ', 41, 21, 24, 96, 479, 'آنها به پوستهای تنشان می‌گویند: «چرا بر ضدّ ما گواهی دادید؟!» آنها جواب می‌دهند: «همان خدایی که هر موجودی را به نطق درآورده ما را گویا ساخته؛ و او شما را نخستین بار آفرید، و بازگشتتان بسوی اوست!'),
(4240, 4240, 'وَمَا كُنْتُمْ تَسْتَتِرُونَ أَنْ يَشْهَدَ عَلَيْكُمْ سَمْعُكُمْ وَلَا أَبْصَارُكُمْ وَلَا جُلُودُكُمْ وَلَٰكِنْ ظَنَنْتُمْ أَنَّ اللَّهَ لَا يَعْلَمُ كَثِيرًا مِمَّا تَعْمَلُونَ', 41, 22, 24, 96, 479, 'شما اگر گناهانتان را مخفی می‌کردید نه بخاطر این بود که از شهادت گوش و چشمها و پوستهای تنتان بیم داشتید، بلکه شما گمان می‌کردید که خداوند بسیاری از اعمالی را که انجام می‌دهید نمی‌داند!'),
(4241, 4241, 'وَذَٰلِكُمْ ظَنُّكُمُ الَّذِي ظَنَنْتُمْ بِرَبِّكُمْ أَرْدَاكُمْ فَأَصْبَحْتُمْ مِنَ الْخَاسِرِينَ', 41, 23, 24, 96, 479, 'آری این گمان بدی بود که درباره پروردگارتان داشتید و همان موجب هلاکت شما گردید، و سرانجام از زیانکاران شدید!'),
(4242, 4242, 'فَإِنْ يَصْبِرُوا فَالنَّارُ مَثْوًى لَهُمْ ۖ وَإِنْ يَسْتَعْتِبُوا فَمَا هُمْ مِنَ الْمُعْتَبِينَ', 41, 24, 24, 96, 479, 'اگر صبر کنند (یا نکنند، به هر حال) دوزخ جایگاه آنهاست؛ و اگر تقاضای عفو کنند، مورد عفو قرار نمی‌گیرند!'),
(4243, 4243, ' وَقَيَّضْنَا لَهُمْ قُرَنَاءَ فَزَيَّنُوا لَهُمْ مَا بَيْنَ أَيْدِيهِمْ وَمَا خَلْفَهُمْ وَحَقَّ عَلَيْهِمُ الْقَوْلُ فِي أُمَمٍ قَدْ خَلَتْ مِنْ قَبْلِهِمْ مِنَ الْجِنِّ وَالْإِنْسِ ۖ إِنَّهُمْ كَانُوا خَاسِرِينَ', 41, 25, 24, 96, 479, 'ما برای آنها همنشینانی (زشت‌سیرت) قرار دادیم که زشتیها را از پیش رو و پشت سر آنها در نظرشان جلوه دادند؛ و فرمان الهی در باره آنان تحقق یافت و به سرنوشت اقوام گمراهی از جنّ و انس که قبل از آنها بودند گرفتار شدند؛ آنها مسلّماً زیانکار بودند!'),
(4244, 4244, 'وَقَالَ الَّذِينَ كَفَرُوا لَا تَسْمَعُوا لِهَٰذَا الْقُرْآنِ وَالْغَوْا فِيهِ لَعَلَّكُمْ تَغْلِبُونَ', 41, 26, 24, 96, 479, 'کافران گفتند: «گوش به این قرآن فراندهید؛ و به هنگام تلاوت آن جنجال کنید، شاید پیروز شوید!»'),
(4245, 4245, 'فَلَنُذِيقَنَّ الَّذِينَ كَفَرُوا عَذَابًا شَدِيدًا وَلَنَجْزِيَنَّهُمْ أَسْوَأَ الَّذِي كَانُوا يَعْمَلُونَ', 41, 27, 24, 96, 479, 'به یقین به کافران عذاب شدیدی می‌چشمانیم، و آنها را به بدترین اعمالی که انجام می دادند کیفر می‌دهیم!'),
(4246, 4246, 'ذَٰلِكَ جَزَاءُ أَعْدَاءِ اللَّهِ النَّارُ ۖ لَهُمْ فِيهَا دَارُ الْخُلْدِ ۖ جَزَاءً بِمَا كَانُوا بِآيَاتِنَا يَجْحَدُونَ', 41, 28, 24, 96, 479, 'این آتش کیفر دشمنان خدا است، سرای جاویدشان در آن خواهد بود، کیفری است بخاطر اینکه آیات ما را انکار می‌کردند.'),
(4247, 4247, 'وَقَالَ الَّذِينَ كَفَرُوا رَبَّنَا أَرِنَا اللَّذَيْنِ أَضَلَّانَا مِنَ الْجِنِّ وَالْإِنْسِ نَجْعَلْهُمَا تَحْتَ أَقْدَامِنَا لِيَكُونَا مِنَ الْأَسْفَلِينَ', 41, 29, 24, 96, 479, 'کافران گفتند: «پروردگارا! آنهایی که از جنّ و انس ما را گمراه کردند به ما نشان ده تا زیر پای خود نهیم (و لگدمالشان کنیم) تا از پست ترین مردم باشند!»'),
(4248, 4248, 'إِنَّ الَّذِينَ قَالُوا رَبُّنَا اللَّهُ ثُمَّ اسْتَقَامُوا تَتَنَزَّلُ عَلَيْهِمُ الْمَلَائِكَةُ أَلَّا تَخَافُوا وَلَا تَحْزَنُوا وَأَبْشِرُوا بِالْجَنَّةِ الَّتِي كُنْتُمْ تُوعَدُونَ', 41, 30, 24, 96, 480, 'به یقین کسانی که گفتند: «پروردگار ما خداوند یگانه است!» سپس استقامت کردند، فرشتگان بر آنان نازل می‌شوند که: «نترسید و غمگین مباشید، و بشارت باد بر شما به آن بهشتی که به شما وعده داده شده است!'),
(4249, 4249, 'نَحْنُ أَوْلِيَاؤُكُمْ فِي الْحَيَاةِ الدُّنْيَا وَفِي الْآخِرَةِ ۖ وَلَكُمْ فِيهَا مَا تَشْتَهِي أَنْفُسُكُمْ وَلَكُمْ فِيهَا مَا تَدَّعُونَ', 41, 31, 24, 96, 480, 'ما یاران و مددکاران شما در زندگی دنیا و آخرت هستیم؛ و برای شما هر چه دلتان بخواهد در بهشت فراهم است، و هر چه طلب کنید به شما داده می‌شود!'),
(4250, 4250, 'نُزُلًا مِنْ غَفُورٍ رَحِيمٍ', 41, 32, 24, 96, 480, 'اینها وسیله پذیرایی از سوی خداوند غفور و رحیم است!»'),
(4251, 4251, 'وَمَنْ أَحْسَنُ قَوْلًا مِمَّنْ دَعَا إِلَى اللَّهِ وَعَمِلَ صَالِحًا وَقَالَ إِنَّنِي مِنَ الْمُسْلِمِينَ', 41, 33, 24, 96, 480, 'چه کسی خوش گفتارتر است از آن کس که دعوت به سوی خدا می‌کند و عمل صالح انجام می‌دهد و می‌گوید: «من از مسلمانانم»؟!'),
(4252, 4252, 'وَلَا تَسْتَوِي الْحَسَنَةُ وَلَا السَّيِّئَةُ ۚ ادْفَعْ بِالَّتِي هِيَ أَحْسَنُ فَإِذَا الَّذِي بَيْنَكَ وَبَيْنَهُ عَدَاوَةٌ كَأَنَّهُ وَلِيٌّ حَمِيمٌ', 41, 34, 24, 96, 480, 'هرگز نیکی و بدی یکسان نیست؛ بدی را با نیکی دفع کن، ناگاه (خواهی دید) همان کس که میان تو و او دشمنی است، گویی دوستی گرم و صمیمی است!'),
(4253, 4253, 'وَمَا يُلَقَّاهَا إِلَّا الَّذِينَ صَبَرُوا وَمَا يُلَقَّاهَا إِلَّا ذُو حَظٍّ عَظِيمٍ', 41, 35, 24, 96, 480, 'امّا جز کسانی که دارای صبر و استقامتند به این مقام نمی‌رسند، و جز کسانی که بهره عظیمی (از ایمان و تقوا) دارند به آن نایل نمی‌گردند!'),
(4254, 4254, 'وَإِمَّا يَنْزَغَنَّكَ مِنَ الشَّيْطَانِ نَزْغٌ فَاسْتَعِذْ بِاللَّهِ ۖ إِنَّهُ هُوَ السَّمِيعُ الْعَلِيمُ', 41, 36, 24, 96, 480, 'و هرگاه وسوسه‌هایی از شیطان متوجّه تو گردد، از خدا پناه بخواه که او شنوده و داناست!'),
(4255, 4255, 'وَمِنْ آيَاتِهِ اللَّيْلُ وَالنَّهَارُ وَالشَّمْسُ وَالْقَمَرُ ۚ لَا تَسْجُدُوا لِلشَّمْسِ وَلَا لِلْقَمَرِ وَاسْجُدُوا لِلَّهِ الَّذِي خَلَقَهُنَّ إِنْ كُنْتُمْ إِيَّاهُ تَعْبُدُونَ', 41, 37, 24, 96, 480, 'و از نشانه‌های او، شب و روز و خورشید و ماه است؛ برای خورشید و ماه سجده نکنید، برای خدایی که آفریننده آنهاست سجده کنید اگر می‌خواهید او را بپرستید!'),
(4256, 4256, 'فَإِنِ اسْتَكْبَرُوا فَالَّذِينَ عِنْدَ رَبِّكَ يُسَبِّحُونَ لَهُ بِاللَّيْلِ وَالنَّهَارِ وَهُمْ لَا يَسْأَمُونَ ۩', 41, 38, 24, 96, 480, 'و اگر (از عبادت پروردگار) تکبّر کنند، کسانی که نزد پروردگار تو هستند شب و روز برای او تسبیح می‌گویند و خسته نمی‌شوند!'),
(4257, 4257, 'وَمِنْ آيَاتِهِ أَنَّكَ تَرَى الْأَرْضَ خَاشِعَةً فَإِذَا أَنْزَلْنَا عَلَيْهَا الْمَاءَ اهْتَزَّتْ وَرَبَتْ ۚ إِنَّ الَّذِي أَحْيَاهَا لَمُحْيِي الْمَوْتَىٰ ۚ إِنَّهُ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ', 41, 39, 24, 96, 481, 'و از آیات او این است که زمین را خشک (و بی‌جان) می‌بینی، امّا هنگامی که آب (باران) بر آن می‌فرستیم به جنبش درمی‌آید و نموّ می‌کند؛ همان کسی که آن را زنده کرد، مردگان را نیز زنده می‌کند؛ او بر هر چیز تواناست!'),
(4258, 4258, 'إِنَّ الَّذِينَ يُلْحِدُونَ فِي آيَاتِنَا لَا يَخْفَوْنَ عَلَيْنَا ۗ أَفَمَنْ يُلْقَىٰ فِي النَّارِ خَيْرٌ أَمْ مَنْ يَأْتِي آمِنًا يَوْمَ الْقِيَامَةِ ۚ اعْمَلُوا مَا شِئْتُمْ ۖ إِنَّهُ بِمَا تَعْمَلُونَ بَصِيرٌ', 41, 40, 24, 96, 481, 'کسانی که آیات ما را تحریف می‌کنند بر ما پوشیده نخواهند بود! آیا کسی که در آتش افکنده می‌شود بهتر است یا کسی که در نهایت امن و امان در قیامت به عرصه محشر می‌آید؟! هر کاری می‌خواهید بکنید، او به آنچه انجام می‌دهید بیناست!'),
(4259, 4259, 'إِنَّ الَّذِينَ كَفَرُوا بِالذِّكْرِ لَمَّا جَاءَهُمْ ۖ وَإِنَّهُ لَكِتَابٌ عَزِيزٌ', 41, 41, 24, 96, 481, 'کسانی که به این ذکر = قرآن‌ هنگامی که به سراغشان آمد کافر شدند (نیز بر ما مخفی نخواهد ماند)! و این کتابی است قطعاً شکست ناپذیر...'),
(4260, 4260, 'لَا يَأْتِيهِ الْبَاطِلُ مِنْ بَيْنِ يَدَيْهِ وَلَا مِنْ خَلْفِهِ ۖ تَنْزِيلٌ مِنْ حَكِيمٍ حَمِيدٍ', 41, 42, 24, 96, 481, 'که هیچ گونه باطلی، نه از پیش رو و نه از پشت سر، به سراغ آن نمی‌آید؛ چرا که از سوی خداوند حکیم و شایسته ستایش نازل شده است!'),
(4261, 4261, 'مَا يُقَالُ لَكَ إِلَّا مَا قَدْ قِيلَ لِلرُّسُلِ مِنْ قَبْلِكَ ۚ إِنَّ رَبَّكَ لَذُو مَغْفِرَةٍ وَذُو عِقَابٍ أَلِيمٍ', 41, 43, 24, 96, 481, 'آنچه به ناروا درباره تو می‌گویند همان است که درباره پیامبران قبل از تو نیز گفته شده؛ پروردگار تو دارای مغفرت و (هم) دارای مجازات دردناکی است!'),
(4262, 4262, 'وَلَوْ جَعَلْنَاهُ قُرْآنًا أَعْجَمِيًّا لَقَالُوا لَوْلَا فُصِّلَتْ آيَاتُهُ ۖ أَأَعْجَمِيٌّ وَعَرَبِيٌّ ۗ قُلْ هُوَ لِلَّذِينَ آمَنُوا هُدًى وَشِفَاءٌ ۖ وَالَّذِينَ لَا يُؤْمِنُونَ فِي آذَانِهِمْ وَقْرٌ وَهُوَ عَلَيْهِمْ عَمًى ۚ أُولَٰئِكَ يُنَادَوْنَ مِنْ مَكَانٍ بَعِيدٍ', 41, 44, 24, 96, 481, 'هرگاه آن را قرآنی عجمی قرار می‌دادیم حتماً می‌گفتند: «چرا آیاتش روشن نیست؟! قرآن عجمی از پیغمبری عربی؟!» بگو: «این (کتاب) برای کسانی که ایمان آورده‌اند هدایت و درمان است؛ ولی کسانی که ایمان نمی‌آورند، در گوشهایشان سنگینی است و گویی نابینا هستند و آن را نمی‌بینند؛ آنها (همچون کسانی هستند که گوئی) از راه دور صدا زده می‌شوند!»'),
(4263, 4263, 'وَلَقَدْ آتَيْنَا مُوسَى الْكِتَابَ فَاخْتُلِفَ فِيهِ ۗ وَلَوْلَا كَلِمَةٌ سَبَقَتْ مِنْ رَبِّكَ لَقُضِيَ بَيْنَهُمْ ۚ وَإِنَّهُمْ لَفِي شَكٍّ مِنْهُ مُرِيبٍ', 41, 45, 24, 96, 481, 'ما به موسی کتاب آسمانی دادیم؛ سپس در آن اختلاف شد؛ و اگر فرمانی از ناحیه پروردگارت در این باره صادر نشده بود (که باید به آنان مهلت داد تا اتمام حجّت شود)، در میان آنها داوری می‌شد (و به کیفر می‌رسیدند)؛ ولی آنها هنوز درباره آن شکّی تهمت‌انگیز دارند!'),
(4264, 4264, 'مَنْ عَمِلَ صَالِحًا فَلِنَفْسِهِ ۖ وَمَنْ أَسَاءَ فَعَلَيْهَا ۗ وَمَا رَبُّكَ بِظَلَّامٍ لِلْعَبِيدِ', 41, 46, 24, 96, 481, 'کسی که عمل صالحی انجام دهد، سودش برای خود اوست؛ و هر کس بدی کند، به خویشتن بدی کرده است؛ و پروردگارت هرگز به بندگان ستم نمی‌کند!'),
(4265, 4265, ' إِلَيْهِ يُرَدُّ عِلْمُ السَّاعَةِ ۚ وَمَا تَخْرُجُ مِنْ ثَمَرَاتٍ مِنْ أَكْمَامِهَا وَمَا تَحْمِلُ مِنْ أُنْثَىٰ وَلَا تَضَعُ إِلَّا بِعِلْمِهِ ۚ وَيَوْمَ يُنَادِيهِمْ أَيْنَ شُرَكَائِي قَالُوا آذَنَّاكَ مَا مِنَّا مِنْ شَهِيدٍ', 41, 47, 25, 97, 482, 'علم به قیامت (و لحظه وقوع آن) تنها به خدا بازمی‌گردد؛ هیچ میوه‌ای از غلاف خود خارج نمی‌شود، و هیچ زنی باردار نمی‌گردد و وضع حمل نمی‌کند مگر به علم او؛ و آن روز که آنها را ندا می‌دهد (و می‌گوید:) کجایند شریکانی که برای من می‌پنداشتید؟! می‌گویند: «(پروردگارا!) ما عرضه داشتیم که هیچ گواهی بر گفته خود نداریم!»'),
(4266, 4266, 'وَضَلَّ عَنْهُمْ مَا كَانُوا يَدْعُونَ مِنْ قَبْلُ ۖ وَظَنُّوا مَا لَهُمْ مِنْ مَحِيصٍ', 41, 48, 25, 97, 482, 'و همه معبودانی را که قبلاً می‌خواندند محو و گم می‌شوند؛ و می‌دانند هیچ گریزگاهی ندارند!'),
(4267, 4267, 'لَا يَسْأَمُ الْإِنْسَانُ مِنْ دُعَاءِ الْخَيْرِ وَإِنْ مَسَّهُ الشَّرُّ فَيَئُوسٌ قَنُوطٌ', 41, 49, 25, 97, 482, 'انسان هرگز از تقاضای نیکی (و نعمت) خسته نمی‌شود؛ و هرگاه شرّ و بدی به او رسد، بسیار مأیوس و نومید می‌گردد!'),
(4268, 4268, 'وَلَئِنْ أَذَقْنَاهُ رَحْمَةً مِنَّا مِنْ بَعْدِ ضَرَّاءَ مَسَّتْهُ لَيَقُولَنَّ هَٰذَا لِي وَمَا أَظُنُّ السَّاعَةَ قَائِمَةً وَلَئِنْ رُجِعْتُ إِلَىٰ رَبِّي إِنَّ لِي عِنْدَهُ لَلْحُسْنَىٰ ۚ فَلَنُنَبِّئَنَّ الَّذِينَ كَفَرُوا بِمَا عَمِلُوا وَلَنُذِيقَنَّهُمْ مِنْ عَذَابٍ غَلِيظٍ', 41, 50, 25, 97, 482, 'و هرگاه او را رحمتی از سوی خود بعد از ناراحتی که به او رسیده بچشانیم می‌گوید: «این بخاطر شایستگی و استحقاق من بوده، و گمان نمی‌کنم قیامت برپا شود؛ و (بفرض که قیامتی باشد،) هرگاه بسوی پروردگارم بازگردانده شوم، برای من نزد او پاداشهای نیک است. ما کافران را از اعمالی که انجام داده‌اند (بزودی) آگاه خواهیم کرد و از عذاب شدید به آنها می‌چشانیم.'),
(4269, 4269, 'وَإِذَا أَنْعَمْنَا عَلَى الْإِنْسَانِ أَعْرَضَ وَنَأَىٰ بِجَانِبِهِ وَإِذَا مَسَّهُ الشَّرُّ فَذُو دُعَاءٍ عَرِيضٍ', 41, 51, 25, 97, 482, 'و هرگاه به انسان (غافل و بی‌خبر) نعمت دهیم، روی می‌گرداند و به حال تکبّر از حق دور می‌شود؛ ولی هرگاه مختصر ناراحتی به او رسد، تقاضای فراوان و مستمرّ (برای بر طرف شدن آن) دارد!'),
(4270, 4270, 'قُلْ أَرَأَيْتُمْ إِنْ كَانَ مِنْ عِنْدِ اللَّهِ ثُمَّ كَفَرْتُمْ بِهِ مَنْ أَضَلُّ مِمَّنْ هُوَ فِي شِقَاقٍ بَعِيدٍ', 41, 52, 25, 97, 482, 'بگو: «به من خبر دهید اگر این قرآن از سوی خداوند باشد و شما به آن کافر شوید، چه کسی گمراهتر خواهد بود از کسی که در مخالفت شدیدی قرار دارد؟!'),
(4271, 4271, 'سَنُرِيهِمْ آيَاتِنَا فِي الْآفَاقِ وَفِي أَنْفُسِهِمْ حَتَّىٰ يَتَبَيَّنَ لَهُمْ أَنَّهُ الْحَقُّ ۗ أَوَلَمْ يَكْفِ بِرَبِّكَ أَنَّهُ عَلَىٰ كُلِّ شَيْءٍ شَهِيدٌ', 41, 53, 25, 97, 482, 'به زودی نشانه‌های خود را در اطراف جهان و در درون جانشان به آنها نشان می‌دهیم تا برای آنان آشکار گردد که او حق است؛ آیا کافی نیست که پروردگارت بر همه چیز شاهد و گواه است؟!'),
(4272, 4272, 'أَلَا إِنَّهُمْ فِي مِرْيَةٍ مِنْ لِقَاءِ رَبِّهِمْ ۗ أَلَا إِنَّهُ بِكُلِّ شَيْءٍ مُحِيطٌ', 41, 54, 25, 97, 482, 'آگاه باشید که آنها از لقای پروردگارشان در شکّ و تردیدند؛ و آگاه باشید که خداوند به همه چیز احاطه دارد!'),
(4273, 4273, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ حم', 42, 1, 25, 97, 483, 'حم.'),
(4274, 4274, 'عسق', 42, 2, 25, 97, 483, 'عسق.'),
(4275, 4275, 'كَذَٰلِكَ يُوحِي إِلَيْكَ وَإِلَى الَّذِينَ مِنْ قَبْلِكَ اللَّهُ الْعَزِيزُ الْحَكِيمُ', 42, 3, 25, 97, 483, 'این گونه خداوند عزیز و حکیم به تو و پیامبرانی که پیش از تو بودند وحی می‌کند.'),
(4276, 4276, 'لَهُ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ ۖ وَهُوَ الْعَلِيُّ الْعَظِيمُ', 42, 4, 25, 97, 483, 'آنچه در آسمانها و آنچه در زمین است از آن اوست؛ و او بلندمرتبه و بزرگ است!'),
(4277, 4277, 'تَكَادُ السَّمَاوَاتُ يَتَفَطَّرْنَ مِنْ فَوْقِهِنَّ ۚ وَالْمَلَائِكَةُ يُسَبِّحُونَ بِحَمْدِ رَبِّهِمْ وَيَسْتَغْفِرُونَ لِمَنْ فِي الْأَرْضِ ۗ أَلَا إِنَّ اللَّهَ هُوَ الْغَفُورُ الرَّحِيمُ', 42, 5, 25, 97, 483, 'نزدیک است آسمانها (بخاطر نسبتهای ناروای مشرکان) از بالا متلاشی شوند و فرشتگان پیوسته تسبیح و حمد پروردگارشان را بجا می‌آورند و برای کسانی که در زمین هستند استغفار می‌کنند؛ آگاه باشید خداوند آمرزنده و مهربان است.'),
(4278, 4278, 'وَالَّذِينَ اتَّخَذُوا مِنْ دُونِهِ أَوْلِيَاءَ اللَّهُ حَفِيظٌ عَلَيْهِمْ وَمَا أَنْتَ عَلَيْهِمْ بِوَكِيلٍ', 42, 6, 25, 97, 483, 'کسانی که غیر خدا را ولیّ خود انتخاب کردند، خداوند حساب همه اعمال آنها را نگه می‌دارد؛ و تو مأمور نیستی که آنان را مجبور به قبول حقّ کنی!'),
(4279, 4279, 'وَكَذَٰلِكَ أَوْحَيْنَا إِلَيْكَ قُرْآنًا عَرَبِيًّا لِتُنْذِرَ أُمَّ الْقُرَىٰ وَمَنْ حَوْلَهَا وَتُنْذِرَ يَوْمَ الْجَمْعِ لَا رَيْبَ فِيهِ ۚ فَرِيقٌ فِي الْجَنَّةِ وَفَرِيقٌ فِي السَّعِيرِ', 42, 7, 25, 97, 483, 'و این گونه قرآنی عربی = فصیح و گویا را بر تو وحی کردیم تا «امّ‌القری» = مکّه‌ و مردم پیرامون آن را انذار کنی و آنها را از روزی که همه خلایق در آن روز جمع می‌شوند و شکّ و تردید در آن نیست بترسانی؛ گروهی در بهشتند و گروهی در آتش سوزان!'),
(4280, 4280, 'وَلَوْ شَاءَ اللَّهُ لَجَعَلَهُمْ أُمَّةً وَاحِدَةً وَلَٰكِنْ يُدْخِلُ مَنْ يَشَاءُ فِي رَحْمَتِهِ ۚ وَالظَّالِمُونَ مَا لَهُمْ مِنْ وَلِيٍّ وَلَا نَصِيرٍ', 42, 8, 25, 97, 483, 'و اگر خدا می‌خواست همه آنها را امّت واحدی قرار می‌داد (و به زور هدایت می‌کرد، ولی هدایت اجباری سودی ندارد)؛ امّا خداوند هر کس را بخواهد در رحمتش وارد می‌کند، و برای ظالمان ولیّ و یاوری نیست.'),
(4281, 4281, 'أَمِ اتَّخَذُوا مِنْ دُونِهِ أَوْلِيَاءَ ۖ فَاللَّهُ هُوَ الْوَلِيُّ وَهُوَ يُحْيِي الْمَوْتَىٰ وَهُوَ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ', 42, 9, 25, 97, 483, 'آیا آنها غیر از خدا را ولیّ خود برگزیدند؟! در حالی که «ولیّ» فقط خداوند است و اوست که مردگان را زنده می‌کند، و اوست که بر هر چیزی تواناست!'),
(4282, 4282, 'وَمَا اخْتَلَفْتُمْ فِيهِ مِنْ شَيْءٍ فَحُكْمُهُ إِلَى اللَّهِ ۚ ذَٰلِكُمُ اللَّهُ رَبِّي عَلَيْهِ تَوَكَّلْتُ وَإِلَيْهِ أُنِيبُ', 42, 10, 25, 97, 483, 'در هر چیز اختلاف کنید، داوریش با خداست؛ این است خداوند، پروردگار من، بر او توکّل کرده‌ام و به سوی او بازمی‌گردم!'),
(4283, 4283, 'فَاطِرُ السَّمَاوَاتِ وَالْأَرْضِ ۚ جَعَلَ لَكُمْ مِنْ أَنْفُسِكُمْ أَزْوَاجًا وَمِنَ الْأَنْعَامِ أَزْوَاجًا ۖ يَذْرَؤُكُمْ فِيهِ ۚ لَيْسَ كَمِثْلِهِ شَيْءٌ ۖ وَهُوَ السَّمِيعُ الْبَصِيرُ', 42, 11, 25, 97, 484, 'او آفریننده آسمانها و زمین است و از جنس شما همسرانی برای شما قرار داد و جفتهایی از چهارپایان آفرید؛ و شما را به این وسیله = بوسیله همسران‌ زیاد می کند؛ هیچ چیز همانند او نیست و او شنوا و بیناست!'),
(4284, 4284, 'لَهُ مَقَالِيدُ السَّمَاوَاتِ وَالْأَرْضِ ۖ يَبْسُطُ الرِّزْقَ لِمَنْ يَشَاءُ وَيَقْدِرُ ۚ إِنَّهُ بِكُلِّ شَيْءٍ عَلِيمٌ', 42, 12, 25, 97, 484, 'کلیدهای آسمانها و زمین از آن اوست؛ روزی را برای هر کس بخواهد گسترش می دهد یا محدود می‌سازد؛ او به همه چیز داناست.'),
(4285, 4285, ' شَرَعَ لَكُمْ مِنَ الدِّينِ مَا وَصَّىٰ بِهِ نُوحًا وَالَّذِي أَوْحَيْنَا إِلَيْكَ وَمَا وَصَّيْنَا بِهِ إِبْرَاهِيمَ وَمُوسَىٰ وَعِيسَىٰ ۖ أَنْ أَقِيمُوا الدِّينَ وَلَا تَتَفَرَّقُوا فِيهِ ۚ كَبُرَ عَلَى الْمُشْرِكِينَ مَا تَدْعُوهُمْ إِلَيْهِ ۚ اللَّهُ يَجْتَبِي إِلَيْهِ مَنْ يَشَاءُ وَيَهْدِي إِلَيْهِ مَنْ يُنِيبُ', 42, 13, 25, 97, 484, 'آیینی را برای شما تشریع کرد که به نوح توصیه کرده بود؛ و آنچه را بر تو وحی فرستادیم و به ابراهیم و موسی و عیسی سفارش کردیم این بود که: دین را برپا دارید و در آن تفرقه ایجاد نکنید! و بر مشرکان گران است آنچه شما آنان را به سویش دعوت می کنید! خداوند هر کس را بخواهد برمی‌گزیند، و کسی را که به سوی او بازگردد هدایت می‌کند.'),
(4286, 4286, 'وَمَا تَفَرَّقُوا إِلَّا مِنْ بَعْدِ مَا جَاءَهُمُ الْعِلْمُ بَغْيًا بَيْنَهُمْ ۚ وَلَوْلَا كَلِمَةٌ سَبَقَتْ مِنْ رَبِّكَ إِلَىٰ أَجَلٍ مُسَمًّى لَقُضِيَ بَيْنَهُمْ ۚ وَإِنَّ الَّذِينَ أُورِثُوا الْكِتَابَ مِنْ بَعْدِهِمْ لَفِي شَكٍّ مِنْهُ مُرِيبٍ', 42, 14, 25, 97, 484, 'آنان پراکنده نشدند مگر بعد از آنکه علم و آگاهی به سراغشان آمد؛ و این تفرقه جویی بخاطر انحراف از حق (و عداوت و حسد) بود؛ و اگر فرمانی از سوی پروردگارت صادر نشده بود که تا سرآمد معیّنی (زنده و آزاد) باشند، در میان آنها داوری می‌شد؛ و کسانی که بعد از آنها وارثان کتاب شدند نسبت به آن در شک و تردیدند، شکی همراه با بدبینی!'),
(4287, 4287, 'فَلِذَٰلِكَ فَادْعُ ۖ وَاسْتَقِمْ كَمَا أُمِرْتَ ۖ وَلَا تَتَّبِعْ أَهْوَاءَهُمْ ۖ وَقُلْ آمَنْتُ بِمَا أَنْزَلَ اللَّهُ مِنْ كِتَابٍ ۖ وَأُمِرْتُ لِأَعْدِلَ بَيْنَكُمُ ۖ اللَّهُ رَبُّنَا وَرَبُّكُمْ ۖ لَنَا أَعْمَالُنَا وَلَكُمْ أَعْمَالُكُمْ ۖ لَا حُجَّةَ بَيْنَنَا وَبَيْنَكُمُ ۖ اللَّهُ يَجْمَعُ بَيْنَنَا ۖ وَإِلَيْهِ الْمَصِيرُ', 42, 15, 25, 97, 484, 'پس به همین خاطر تو نیز آنان را به سوی این آیین واحد الهی دعوت کن و آنچنان که مأمور شده‌ای استقامت نما، و از هوی و هوسهای آنان پیروی مکن، و بگو: «به هر کتابی که خدا نازل کرده ایمان آورده‌ام و مأمورم در میان شما عدالت کنم؛ خداوند پروردگار ما و شماست؛ نتیجه اعمال ما از آن ما است و نتیجه اعمال شما از آن شما، خصومت شخصی در میان ما نیست؛ و خداوند ما و شما را در یکجا جمع می‌کند، و بازگشت (همه) به سوی اوست!»'),
(4288, 4288, 'وَالَّذِينَ يُحَاجُّونَ فِي اللَّهِ مِنْ بَعْدِ مَا اسْتُجِيبَ لَهُ حُجَّتُهُمْ دَاحِضَةٌ عِنْدَ رَبِّهِمْ وَعَلَيْهِمْ غَضَبٌ وَلَهُمْ عَذَابٌ شَدِيدٌ', 42, 16, 25, 97, 485, 'کسانی که (از روی لجاجت) درباره خدا بعد از پذیرفتن (و ایمان به) او، محاجّه می کنند، دلیلشان نزد پروردگارشان باطل و بی‌پایه است؛ و غضب بر آنهاست و عذابی شدید دارند.'),
(4289, 4289, 'اللَّهُ الَّذِي أَنْزَلَ الْكِتَابَ بِالْحَقِّ وَالْمِيزَانَ ۗ وَمَا يُدْرِيكَ لَعَلَّ السَّاعَةَ قَرِيبٌ', 42, 17, 25, 97, 485, 'خداوند کسی است که کتاب را بحق نازل کرد و میزان (سنجش حق و باطل و خبر قیامت) را نیز؛ تو چه می‌دانی شاید ساعت (قیام قیامت) نزدیک باشد!'),
(4290, 4290, 'يَسْتَعْجِلُ بِهَا الَّذِينَ لَا يُؤْمِنُونَ بِهَا ۖ وَالَّذِينَ آمَنُوا مُشْفِقُونَ مِنْهَا وَيَعْلَمُونَ أَنَّهَا الْحَقُّ ۗ أَلَا إِنَّ الَّذِينَ يُمَارُونَ فِي السَّاعَةِ لَفِي ضَلَالٍ بَعِيدٍ', 42, 18, 25, 97, 485, 'کسانی که به قیامت ایمان ندارند درباره آن شتاب می‌کنند؛ ولی آنها که ایمان آورده‌اند پیوسته از آن هراسانند، و می‌دانند آن حق است؛ آگاه باشید کسانی که در قیامت تردید می‌کنند، در گمراهی عمیقی هستند.');
INSERT INTO `Quran` (`rowid`, `id`, `Arabic`, `Chapter`, `Verse`, `Juz`, `Hizb`, `Page`, `tr`) VALUES
(4291, 4291, 'اللَّهُ لَطِيفٌ بِعِبَادِهِ يَرْزُقُ مَنْ يَشَاءُ ۖ وَهُوَ الْقَوِيُّ الْعَزِيزُ', 42, 19, 25, 97, 485, 'خداوند نسبت به بندگانش لطف (و آگاهی) دارد؛ هر کس را بخواهد روزی می‌دهد و او قوّی و شکست‌ناپذیر است!'),
(4292, 4292, 'مَنْ كَانَ يُرِيدُ حَرْثَ الْآخِرَةِ نَزِدْ لَهُ فِي حَرْثِهِ ۖ وَمَنْ كَانَ يُرِيدُ حَرْثَ الدُّنْيَا نُؤْتِهِ مِنْهَا وَمَا لَهُ فِي الْآخِرَةِ مِنْ نَصِيبٍ', 42, 20, 25, 97, 485, 'کسی که زراعت آخرت را بخواهد، به کشت او برکت و افزایش می‌دهیم و بر محصولش می‌افزاییم؛ و کسی که فقط کشت دنیا را بطلبد، کمی از آن به او می‌دهیم امّا در آخرت هیچ بهره‌ای ندارد!'),
(4293, 4293, 'أَمْ لَهُمْ شُرَكَاءُ شَرَعُوا لَهُمْ مِنَ الدِّينِ مَا لَمْ يَأْذَنْ بِهِ اللَّهُ ۚ وَلَوْلَا كَلِمَةُ الْفَصْلِ لَقُضِيَ بَيْنَهُمْ ۗ وَإِنَّ الظَّالِمِينَ لَهُمْ عَذَابٌ أَلِيمٌ', 42, 21, 25, 97, 485, 'آیا معبودانی دارند که بی‌اذن خداوند آیینی برای آنها ساخته‌اند؟! اگر مهلت معیّنی برای آنها نبود، در میانشان داوری می‌شد (و دستور عذاب صادر می‌گشت) و برای ظالمان عذاب دردناکی است!'),
(4294, 4294, 'تَرَى الظَّالِمِينَ مُشْفِقِينَ مِمَّا كَسَبُوا وَهُوَ وَاقِعٌ بِهِمْ ۗ وَالَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ فِي رَوْضَاتِ الْجَنَّاتِ ۖ لَهُمْ مَا يَشَاءُونَ عِنْدَ رَبِّهِمْ ۚ ذَٰلِكَ هُوَ الْفَضْلُ الْكَبِيرُ', 42, 22, 25, 97, 485, '(در آن روز) ستمگران را می‌بینی که از اعمالی که انجام داده‌اند سخت بیمناکند، ولی آنها را فرامی‌گیرد! امّا کسانی که ایمان آورده و کارهای شایسته انجام داده‌اند در باغهای بهشتند و هر چه بخواهند نزد پروردگارشان برای آنها فراهم است؛ این است فضل (و بخشش) بزرگ!'),
(4295, 4295, 'ذَٰلِكَ الَّذِي يُبَشِّرُ اللَّهُ عِبَادَهُ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ ۗ قُلْ لَا أَسْأَلُكُمْ عَلَيْهِ أَجْرًا إِلَّا الْمَوَدَّةَ فِي الْقُرْبَىٰ ۗ وَمَنْ يَقْتَرِفْ حَسَنَةً نَزِدْ لَهُ فِيهَا حُسْنًا ۚ إِنَّ اللَّهَ غَفُورٌ شَكُورٌ', 42, 23, 25, 97, 486, 'این همان چیزی است که خداوند بندگانش را که ایمان آورده و اعمال صالح انجام داده‌اند به آن نوید می‌دهد! بگو: «من هیچ پاداشی از شما بر رسالتم درخواست نمی‌کنم جز دوست‌داشتن نزدیکانم = اهل بیتم‌؛ و هر کس کار نیکی انجام دهد، بر نیکی‌اش می‌افزاییم؛ چرا که خداوند آمرزنده و سپاسگزار است.'),
(4296, 4296, 'أَمْ يَقُولُونَ افْتَرَىٰ عَلَى اللَّهِ كَذِبًا ۖ فَإِنْ يَشَإِ اللَّهُ يَخْتِمْ عَلَىٰ قَلْبِكَ ۗ وَيَمْحُ اللَّهُ الْبَاطِلَ وَيُحِقُّ الْحَقَّ بِكَلِمَاتِهِ ۚ إِنَّهُ عَلِيمٌ بِذَاتِ الصُّدُورِ', 42, 24, 25, 97, 486, 'آیا می‌گویند: «او بر خدا دروغ بسته است»؟! در حالی که اگر خدا بخواهد بر قلب تو مُهر می‌نهد (و اگر خلاف بگوئی قدرت اظهار این آیات را از تو می‌گیرد) و باطل را محو می‌کند و حقّ را بفرمانش پابرجا می‌سازد؛ چرا که او از آنچه درون سینه‌هاست آگاه است.'),
(4297, 4297, 'وَهُوَ الَّذِي يَقْبَلُ التَّوْبَةَ عَنْ عِبَادِهِ وَيَعْفُو عَنِ السَّيِّئَاتِ وَيَعْلَمُ مَا تَفْعَلُونَ', 42, 25, 25, 97, 486, 'او کسی است که توبه را از بندگانش می‌پذیرد و بدیها را می‌بخشد، و آنچه را انجام می‌دهید می‌داند.'),
(4298, 4298, 'وَيَسْتَجِيبُ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ وَيَزِيدُهُمْ مِنْ فَضْلِهِ ۚ وَالْكَافِرُونَ لَهُمْ عَذَابٌ شَدِيدٌ', 42, 26, 25, 97, 486, 'و درخواست کسانی را که ایمان آورده و کارهای نیک انجام داده‌اند می‌پذیرد و از فضل خود بر آنها می‌افزاید؛ امّا برای کافران عذاب شدیدی است!'),
(4299, 4299, ' وَلَوْ بَسَطَ اللَّهُ الرِّزْقَ لِعِبَادِهِ لَبَغَوْا فِي الْأَرْضِ وَلَٰكِنْ يُنَزِّلُ بِقَدَرٍ مَا يَشَاءُ ۚ إِنَّهُ بِعِبَادِهِ خَبِيرٌ بَصِيرٌ', 42, 27, 25, 98, 486, 'هرگاه خداوند روزی را برای بندگانش وسعت بخشد، در زمین طغیان و ستم می‌کنند؛ از این‌رو بمقداری که می‌خواهد (و مصلحت می‌داند) نازل می‌کند، که نسبه به بندگانش آگاه و بیناست!'),
(4300, 4300, 'وَهُوَ الَّذِي يُنَزِّلُ الْغَيْثَ مِنْ بَعْدِ مَا قَنَطُوا وَيَنْشُرُ رَحْمَتَهُ ۚ وَهُوَ الْوَلِيُّ الْحَمِيدُ', 42, 28, 25, 98, 486, 'او کسی است که باران سودمند را پس از آنکه مأیوس شدند نازل می‌کند و رحمت خویش را می‌گستراند؛ و او ولیّ و (سرپرست) و ستوده است!'),
(4301, 4301, 'وَمِنْ آيَاتِهِ خَلْقُ السَّمَاوَاتِ وَالْأَرْضِ وَمَا بَثَّ فِيهِمَا مِنْ دَابَّةٍ ۚ وَهُوَ عَلَىٰ جَمْعِهِمْ إِذَا يَشَاءُ قَدِيرٌ', 42, 29, 25, 98, 486, 'و از آیات اوست آفرینش آسمانها و زمین و آنچه از جنبندگان در آنها منتشر نموده؛ و او هرگاه بخواهد بر جمع آنها تواناست!'),
(4302, 4302, 'وَمَا أَصَابَكُمْ مِنْ مُصِيبَةٍ فَبِمَا كَسَبَتْ أَيْدِيكُمْ وَيَعْفُو عَنْ كَثِيرٍ', 42, 30, 25, 98, 486, 'هر مصیبتی به شما رسد بخاطر اعمالی است که انجام داده‌اید، و بسیاری را نیز عفو می‌کند!'),
(4303, 4303, 'وَمَا أَنْتُمْ بِمُعْجِزِينَ فِي الْأَرْضِ ۖ وَمَا لَكُمْ مِنْ دُونِ اللَّهِ مِنْ وَلِيٍّ وَلَا نَصِيرٍ', 42, 31, 25, 98, 486, 'و شما هرگز نمی‌توانید در زمین از قدرت خداوند فرار کنید؛ و غیر از خدا هیچ ولیّ و یاوری برای شما نیست.'),
(4304, 4304, 'وَمِنْ آيَاتِهِ الْجَوَارِ فِي الْبَحْرِ كَالْأَعْلَامِ', 42, 32, 25, 98, 487, 'از نشانه‌های او کشتیهایی است که در دریا همچون کوه‌ها به نظر می‌رسند!'),
(4305, 4305, 'إِنْ يَشَأْ يُسْكِنِ الرِّيحَ فَيَظْلَلْنَ رَوَاكِدَ عَلَىٰ ظَهْرِهِ ۚ إِنَّ فِي ذَٰلِكَ لَآيَاتٍ لِكُلِّ صَبَّارٍ شَكُورٍ', 42, 33, 25, 98, 487, 'اگر او اراده کند، باد را ساکن می‌سازد تا آنها بر پشت دریا بی‌حرکت بمانند؛ در این نشانه‌هایی است برای هر صبرکننده شکرگزار!'),
(4306, 4306, 'أَوْ يُوبِقْهُنَّ بِمَا كَسَبُوا وَيَعْفُ عَنْ كَثِيرٍ', 42, 34, 25, 98, 487, 'یا اگر بخواهد آنها را بخاطر اعمالی که سرنشینانش مرتکب شده‌اند نابود می‌سازد؛ و در عین حال بسیاری را می‌بخشد.'),
(4307, 4307, 'وَيَعْلَمَ الَّذِينَ يُجَادِلُونَ فِي آيَاتِنَا مَا لَهُمْ مِنْ مَحِيصٍ', 42, 35, 25, 98, 487, 'کسانی که در آیات ما مجادله می‌کنند بدانند هیچ گریزگاهی ندارند!'),
(4308, 4308, 'فَمَا أُوتِيتُمْ مِنْ شَيْءٍ فَمَتَاعُ الْحَيَاةِ الدُّنْيَا ۖ وَمَا عِنْدَ اللَّهِ خَيْرٌ وَأَبْقَىٰ لِلَّذِينَ آمَنُوا وَعَلَىٰ رَبِّهِمْ يَتَوَكَّلُونَ', 42, 36, 25, 98, 487, 'آنچه به شما عطا شده متاع زودگذر زندگی دنیاست، و آنچه نزد خداست برای کسانی که ایمان آورده و بر پروردگارشان توکّل می‌کنند بهتر و پایدارتر است.'),
(4309, 4309, 'وَالَّذِينَ يَجْتَنِبُونَ كَبَائِرَ الْإِثْمِ وَالْفَوَاحِشَ وَإِذَا مَا غَضِبُوا هُمْ يَغْفِرُونَ', 42, 37, 25, 98, 487, 'همان کسانی که از گناهان بزرگ و اعمال زشت اجتناب می‌ورزند، و هنگامی که خشمگین شوند عفو می‌کنند.'),
(4310, 4310, 'وَالَّذِينَ اسْتَجَابُوا لِرَبِّهِمْ وَأَقَامُوا الصَّلَاةَ وَأَمْرُهُمْ شُورَىٰ بَيْنَهُمْ وَمِمَّا رَزَقْنَاهُمْ يُنْفِقُونَ', 42, 38, 25, 98, 487, 'و کسانی که دعوت پروردگارشان را اجابت کرده و نماز را برپا می‌دارند و کارهایشان به صورت مشورت در میان آنهاست و از آنچه به آنها روزی داده‌ایم انفاق می‌کنند،'),
(4311, 4311, 'وَالَّذِينَ إِذَا أَصَابَهُمُ الْبَغْيُ هُمْ يَنْتَصِرُونَ', 42, 39, 25, 98, 487, 'و کسانی که هرگاه ستمی به آنها رسد، (تسلیم ظلم نمی‌شوند و) یاری می‌طلبند!'),
(4312, 4312, 'وَجَزَاءُ سَيِّئَةٍ سَيِّئَةٌ مِثْلُهَا ۖ فَمَنْ عَفَا وَأَصْلَحَ فَأَجْرُهُ عَلَى اللَّهِ ۚ إِنَّهُ لَا يُحِبُّ الظَّالِمِينَ', 42, 40, 25, 98, 487, 'کیفر بدی، مجازاتی است همانند آن؛ و هر کس عفو و اصلاح کند، پاداش او با خداست؛ خداوند ظالمان را دوست ندارد!'),
(4313, 4313, 'وَلَمَنِ انْتَصَرَ بَعْدَ ظُلْمِهِ فَأُولَٰئِكَ مَا عَلَيْهِمْ مِنْ سَبِيلٍ', 42, 41, 25, 98, 487, 'و کسی که بعد از مظلوم‌شدن یاری طلبد، ایرادی بر او نیست؛'),
(4314, 4314, 'إِنَّمَا السَّبِيلُ عَلَى الَّذِينَ يَظْلِمُونَ النَّاسَ وَيَبْغُونَ فِي الْأَرْضِ بِغَيْرِ الْحَقِّ ۚ أُولَٰئِكَ لَهُمْ عَذَابٌ أَلِيمٌ', 42, 42, 25, 98, 487, 'ایراد و مجازات بر کسانی است که به مردم ستم می‌کنند و در زمین بناحق ظلم روا می‌دارند؛ برای آنان عذاب دردناکی است!'),
(4315, 4315, 'وَلَمَنْ صَبَرَ وَغَفَرَ إِنَّ ذَٰلِكَ لَمِنْ عَزْمِ الْأُمُورِ', 42, 43, 25, 98, 487, 'امّا کسانی که شکیبایی و عفو کنند، این از کارهای پرارزش است!'),
(4316, 4316, 'وَمَنْ يُضْلِلِ اللَّهُ فَمَا لَهُ مِنْ وَلِيٍّ مِنْ بَعْدِهِ ۗ وَتَرَى الظَّالِمِينَ لَمَّا رَأَوُا الْعَذَابَ يَقُولُونَ هَلْ إِلَىٰ مَرَدٍّ مِنْ سَبِيلٍ', 42, 44, 25, 98, 487, 'کسی را که خدا گمراه کند، ولیّ و یاوری جز او نخواهد داشت؛ و ظالمان را (روز قیامت) می‌بینی هنگامی که عذاب الهی را مشاهده می‌کنند میگویند: «آیا راهی به سوی بازگشت (و جبران) وجود دارد؟!»'),
(4317, 4317, 'وَتَرَاهُمْ يُعْرَضُونَ عَلَيْهَا خَاشِعِينَ مِنَ الذُّلِّ يَنْظُرُونَ مِنْ طَرْفٍ خَفِيٍّ ۗ وَقَالَ الَّذِينَ آمَنُوا إِنَّ الْخَاسِرِينَ الَّذِينَ خَسِرُوا أَنْفُسَهُمْ وَأَهْلِيهِمْ يَوْمَ الْقِيَامَةِ ۗ أَلَا إِنَّ الظَّالِمِينَ فِي عَذَابٍ مُقِيمٍ', 42, 45, 25, 98, 488, 'و آنها را می‌بینی که بر آتش عرضه می‌شوند در حالی که از شدّت مذلّت خاشعند و زیر چشمی (به آن) نگاه می‌کنند؛ و کسانی که ایمان آورده‌اند می‌گویند: «زیانکاران واقعی آنانند که خود و خانواده خویش را روز قیامت از دست داده‌اند؛ آگاه باشید که ظالمان (آن روز) در عذاب دائمند!»'),
(4318, 4318, 'وَمَا كَانَ لَهُمْ مِنْ أَوْلِيَاءَ يَنْصُرُونَهُمْ مِنْ دُونِ اللَّهِ ۗ وَمَنْ يُضْلِلِ اللَّهُ فَمَا لَهُ مِنْ سَبِيلٍ', 42, 46, 25, 98, 488, 'آنها جز خدا اولیا و یاورانی ندارند که یاریشان کنند؛ و هر کس را خدا گمراه سازد، هیچ راه نجاتی برای او نیست!'),
(4319, 4319, 'اسْتَجِيبُوا لِرَبِّكُمْ مِنْ قَبْلِ أَنْ يَأْتِيَ يَوْمٌ لَا مَرَدَّ لَهُ مِنَ اللَّهِ ۚ مَا لَكُمْ مِنْ مَلْجَإٍ يَوْمَئِذٍ وَمَا لَكُمْ مِنْ نَكِيرٍ', 42, 47, 25, 98, 488, 'اجابت کنید دعوت پروردگار خود را پیش از آنکه روزی فرا رسد که بازگشتی برای آن در برابر اراده خدا نیست؛ و در آن روز، نه پناهگاهی دارید و نه مدافعی!'),
(4320, 4320, 'فَإِنْ أَعْرَضُوا فَمَا أَرْسَلْنَاكَ عَلَيْهِمْ حَفِيظًا ۖ إِنْ عَلَيْكَ إِلَّا الْبَلَاغُ ۗ وَإِنَّا إِذَا أَذَقْنَا الْإِنْسَانَ مِنَّا رَحْمَةً فَرِحَ بِهَا ۖ وَإِنْ تُصِبْهُمْ سَيِّئَةٌ بِمَا قَدَّمَتْ أَيْدِيهِمْ فَإِنَّ الْإِنْسَانَ كَفُورٌ', 42, 48, 25, 98, 488, 'و اگر روی‌گردان شوند (غمگین مباش)، ما تو را حافظ آنان (و مأمور اجبارشان) قرار نداده‌ایم؛ وظیف‌ه تو تنها ابلاغ رسالت است! و هنگامی که ما رحمتی از سوی خود به انسان بچشانیم به آن دلخوش می‌شود، و اگر بلایی بخاطر اعمالی که انجام داده‌اند به آنها رسد (به کفران می‌پردازند)، چرا که انسان بسیار کفران‌کننده است!'),
(4321, 4321, 'لِلَّهِ مُلْكُ السَّمَاوَاتِ وَالْأَرْضِ ۚ يَخْلُقُ مَا يَشَاءُ ۚ يَهَبُ لِمَنْ يَشَاءُ إِنَاثًا وَيَهَبُ لِمَنْ يَشَاءُ الذُّكُورَ', 42, 49, 25, 98, 488, 'مالکیّت و حاکمیّت آسمانها و زمین از آن خداست؛ هر چه را بخواهد می‌آفریند؛ به هر کس اراده کند دختر می‌بخشد و به هر کس بخواهد پسر،'),
(4322, 4322, 'أَوْ يُزَوِّجُهُمْ ذُكْرَانًا وَإِنَاثًا ۖ وَيَجْعَلُ مَنْ يَشَاءُ عَقِيمًا ۚ إِنَّهُ عَلِيمٌ قَدِيرٌ', 42, 50, 25, 98, 488, 'یا (اگر بخواهد) پسر و دختر -هر دو- را برای آنان جمع میکند و هر کس را بخواهد عقیم می‌گذارد؛ زیرا که او دانا و قادر است.'),
(4323, 4323, ' وَمَا كَانَ لِبَشَرٍ أَنْ يُكَلِّمَهُ اللَّهُ إِلَّا وَحْيًا أَوْ مِنْ وَرَاءِ حِجَابٍ أَوْ يُرْسِلَ رَسُولًا فَيُوحِيَ بِإِذْنِهِ مَا يَشَاءُ ۚ إِنَّهُ عَلِيٌّ حَكِيمٌ', 42, 51, 25, 98, 488, 'و شایسته هیچ انسانی نیست که خدا با او سخن گوید، مگر از راه وحی یا از پشت حجاب، یا رسولی می‌فرستد و بفرمان او آنچه را بخواهد وحی می‌کند؛ چرا که او بلندمقام و حکیم است!'),
(4324, 4324, 'وَكَذَٰلِكَ أَوْحَيْنَا إِلَيْكَ رُوحًا مِنْ أَمْرِنَا ۚ مَا كُنْتَ تَدْرِي مَا الْكِتَابُ وَلَا الْإِيمَانُ وَلَٰكِنْ جَعَلْنَاهُ نُورًا نَهْدِي بِهِ مَنْ نَشَاءُ مِنْ عِبَادِنَا ۚ وَإِنَّكَ لَتَهْدِي إِلَىٰ صِرَاطٍ مُسْتَقِيمٍ', 42, 52, 25, 98, 489, 'همان گونه (که بر پیامبران پیشین وحی فرستادیم) بر تو نیز روحی را بفرمان خود وحی کردیم؛ تو پیش از این نمی‌دانستی کتاب و ایمان چیست (و از محتوای قرآن آگاه نبودی)؛ ولی ما آن را نوری قرار دادیم که بوسیله آن هر کس از بندگان خویش را بخواهیم هدایت می‌کنیم؛ و تو مسلّماً به سوی راه راست هدایت می‌کنی.'),
(4325, 4325, 'صِرَاطِ اللَّهِ الَّذِي لَهُ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ ۗ أَلَا إِلَى اللَّهِ تَصِيرُ الْأُمُورُ', 42, 53, 25, 98, 489, 'راه خداوندی که تمامی آنچه در آسمانها و آنچه در زمین است از آن اوست؛ آگاه باشید که همه کارها تنها بسوی خدا بازمی‌گردد!'),
(4326, 4326, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ حم', 43, 1, 25, 98, 489, 'حم.'),
(4327, 4327, 'وَالْكِتَابِ الْمُبِينِ', 43, 2, 25, 98, 489, 'سوگند به کتاب مبین (و روشنگر)،'),
(4328, 4328, 'إِنَّا جَعَلْنَاهُ قُرْآنًا عَرَبِيًّا لَعَلَّكُمْ تَعْقِلُونَ', 43, 3, 25, 98, 489, 'که ما آن را قرآنی فصیح و عربی قرار دادیم، شاید شما (آن را) درک کنید!'),
(4329, 4329, 'وَإِنَّهُ فِي أُمِّ الْكِتَابِ لَدَيْنَا لَعَلِيٌّ حَكِيمٌ', 43, 4, 25, 98, 489, 'و آن در «امّ‌الکتاب» = لوح محفوظ نزد ما بلندپایه و استوار است!'),
(4330, 4330, 'أَفَنَضْرِبُ عَنْكُمُ الذِّكْرَ صَفْحًا أَنْ كُنْتُمْ قَوْمًا مُسْرِفِينَ', 43, 5, 25, 98, 489, 'آیا این ذکر = قرآن‌ را از شما بازگیریم بخاطر اینکه قومی اسرافکارید؟!'),
(4331, 4331, 'وَكَمْ أَرْسَلْنَا مِنْ نَبِيٍّ فِي الْأَوَّلِينَ', 43, 6, 25, 98, 489, 'چه بسیار پیامبرانی که (برای هدایت) در میان اقوام پیشین فرستادیم؛'),
(4332, 4332, 'وَمَا يَأْتِيهِمْ مِنْ نَبِيٍّ إِلَّا كَانُوا بِهِ يَسْتَهْزِئُونَ', 43, 7, 25, 98, 489, 'ولی هیچ پیامبری به سوی آنها نمی‌آمد مگر اینکه او را استهزا می‌کردند.'),
(4333, 4333, 'فَأَهْلَكْنَا أَشَدَّ مِنْهُمْ بَطْشًا وَمَضَىٰ مَثَلُ الْأَوَّلِينَ', 43, 8, 25, 98, 489, 'ولی ما کسانی را که نیرومندتر از آنها بودند هلاک کردیم، و داستان پیشینیان گذشت.'),
(4334, 4334, 'وَلَئِنْ سَأَلْتَهُمْ مَنْ خَلَقَ السَّمَاوَاتِ وَالْأَرْضَ لَيَقُولُنَّ خَلَقَهُنَّ الْعَزِيزُ الْعَلِيمُ', 43, 9, 25, 98, 489, 'هر گاه از آنان بپرسی: «چه کسی آسمانها و زمین را آفریده است؟» مسلّماً می‌گویند: «خداوند قادر و دانا آنها را آفریده است»!'),
(4335, 4335, 'الَّذِي جَعَلَ لَكُمُ الْأَرْضَ مَهْدًا وَجَعَلَ لَكُمْ فِيهَا سُبُلًا لَعَلَّكُمْ تَهْتَدُونَ', 43, 10, 25, 98, 489, 'همان کسی که زمین را محل آرامش شما قرار داد، و برای شما در آن راه‌هایی آفرید باشد، که هدایت شوید (و به مقصد برسید)!'),
(4336, 4336, 'وَالَّذِي نَزَّلَ مِنَ السَّمَاءِ مَاءً بِقَدَرٍ فَأَنْشَرْنَا بِهِ بَلْدَةً مَيْتًا ۚ كَذَٰلِكَ تُخْرَجُونَ', 43, 11, 25, 98, 490, 'همان کسی که از آسمان آبی فرستاد بمقدار معیّن، و بوسیله آن سرزمین مرده را حیات بخشیدیم؛ همین گونه (در قیامت از قبرها) شما را خارج می‌سازند!'),
(4337, 4337, 'وَالَّذِي خَلَقَ الْأَزْوَاجَ كُلَّهَا وَجَعَلَ لَكُمْ مِنَ الْفُلْكِ وَالْأَنْعَامِ مَا تَرْكَبُونَ', 43, 12, 25, 98, 490, 'و همان کسی که همه زوجها را آفرید، و برای شما از کشتیها و چهارپایان مرکبهایی قرارداد که بر آن سوار می‌شوید،'),
(4338, 4338, 'لِتَسْتَوُوا عَلَىٰ ظُهُورِهِ ثُمَّ تَذْكُرُوا نِعْمَةَ رَبِّكُمْ إِذَا اسْتَوَيْتُمْ عَلَيْهِ وَتَقُولُوا سُبْحَانَ الَّذِي سَخَّرَ لَنَا هَٰذَا وَمَا كُنَّا لَهُ مُقْرِنِينَ', 43, 13, 25, 98, 490, 'تا بر پشت آنها بخوبی قرار گیرید؛ سپس هنگامی که بر آنها سوار شدید، نعمت پروردگارتان را متذکّر شوید و بگویید: «پاک و منزّه است کسی که این را مسخّر ما ساخت، وگرنه ما توانایی تسخیر آن را نداشتیم؛'),
(4339, 4339, 'وَإِنَّا إِلَىٰ رَبِّنَا لَمُنْقَلِبُونَ', 43, 14, 25, 98, 490, 'و ما به سوی پروردگارمان بازمی‌گردیم!»'),
(4340, 4340, 'وَجَعَلُوا لَهُ مِنْ عِبَادِهِ جُزْءًا ۚ إِنَّ الْإِنْسَانَ لَكَفُورٌ مُبِينٌ', 43, 15, 25, 98, 490, 'آنها برای خداوند از میان بندگانش جزئی قرار دادند (و ملائکه را دختران خدا خواندند)؛ انسان کفران‌کننده آشکاری است!'),
(4341, 4341, 'أَمِ اتَّخَذَ مِمَّا يَخْلُقُ بَنَاتٍ وَأَصْفَاكُمْ بِالْبَنِينَ', 43, 16, 25, 98, 490, 'آیا از میان مخلوقاتش دختران را برای خود انتخاب کرده و پسران را برای شما برگزیده است؟!'),
(4342, 4342, 'وَإِذَا بُشِّرَ أَحَدُهُمْ بِمَا ضَرَبَ لِلرَّحْمَٰنِ مَثَلًا ظَلَّ وَجْهُهُ مُسْوَدًّا وَهُوَ كَظِيمٌ', 43, 17, 25, 98, 490, 'در حالی که هرگاه یکی از آنها را به همان چیزی که برای خداوند رحمان شبیه قرار داده = به تولّد دختر بشارت دهند، صورتش (از فرط ناراحتی) سیاه می‌شود و خشمگین می‌گردد!'),
(4343, 4343, 'أَوَمَنْ يُنَشَّأُ فِي الْحِلْيَةِ وَهُوَ فِي الْخِصَامِ غَيْرُ مُبِينٍ', 43, 18, 25, 98, 490, 'آیا کسی را که در لابلای زینتها پرورش می‌یابد و به هنگام جدال قادر به تبیین مقصود خود نیست (فرزند خدا می‌خوانید)؟!'),
(4344, 4344, 'وَجَعَلُوا الْمَلَائِكَةَ الَّذِينَ هُمْ عِبَادُ الرَّحْمَٰنِ إِنَاثًا ۚ أَشَهِدُوا خَلْقَهُمْ ۚ سَتُكْتَبُ شَهَادَتُهُمْ وَيُسْأَلُونَ', 43, 19, 25, 98, 490, 'آنها فرشتگان را که بندگان خداوند رحمانند مؤنّث پنداشتند؛ آیا شاهد آفرینش آنها بوده اند؟! گواهی آنان نوشته می‌شود و (از آن) بازخواست خواهند شد!'),
(4345, 4345, 'وَقَالُوا لَوْ شَاءَ الرَّحْمَٰنُ مَا عَبَدْنَاهُمْ ۗ مَا لَهُمْ بِذَٰلِكَ مِنْ عِلْمٍ ۖ إِنْ هُمْ إِلَّا يَخْرُصُونَ', 43, 20, 25, 98, 490, 'آنان گفتند: «اگر خداوند رحمان می‌خواست ما آنها را پرستش نمی‌کردیم!» ولی به این امر هیچ گونه علم و یقین ندارند و جز دروغ چیزی نمی‌گویند!'),
(4346, 4346, 'أَمْ آتَيْنَاهُمْ كِتَابًا مِنْ قَبْلِهِ فَهُمْ بِهِ مُسْتَمْسِكُونَ', 43, 21, 25, 98, 490, 'یا اینکه ما کتابی پیش از این به آنان داده‌ایم و آنها به آن تمسّک می‌جویند؟!'),
(4347, 4347, 'بَلْ قَالُوا إِنَّا وَجَدْنَا آبَاءَنَا عَلَىٰ أُمَّةٍ وَإِنَّا عَلَىٰ آثَارِهِمْ مُهْتَدُونَ', 43, 22, 25, 98, 490, 'بلکه آنها می‌گویند: «ما نیاکان خود را بر آئینی یافتیم، و ما نیز به پیروی آنان هدایت یافته‌ایم.»'),
(4348, 4348, 'وَكَذَٰلِكَ مَا أَرْسَلْنَا مِنْ قَبْلِكَ فِي قَرْيَةٍ مِنْ نَذِيرٍ إِلَّا قَالَ مُتْرَفُوهَا إِنَّا وَجَدْنَا آبَاءَنَا عَلَىٰ أُمَّةٍ وَإِنَّا عَلَىٰ آثَارِهِمْ مُقْتَدُونَ', 43, 23, 25, 98, 491, 'و این‌گونه در هیچ شهر و دیاری پیش از تو پیامبر انذارکننده‌ای نفرستادیم مگر اینکه ثروتمندان مست و مغرور آن گفتند: «ما پدران خود را بر آئینی یافتیم و به آثار آنان اقتدا می‌کنیم.»'),
(4349, 4349, ' قَالَ أَوَلَوْ جِئْتُكُمْ بِأَهْدَىٰ مِمَّا وَجَدْتُمْ عَلَيْهِ آبَاءَكُمْ ۖ قَالُوا إِنَّا بِمَا أُرْسِلْتُمْ بِهِ كَافِرُونَ', 43, 24, 25, 99, 491, '(پیامبرشان) گفت: «آیا اگر من آیینی هدایت‌بخش‌تر از آنچه پدرانتان را بر آن یافتید آورده باشم (باز هم انکار می‌کنید)؟!» گفتند: «(آری،) ما به آنچه شما به آن فرستاده شده‌اید کافریم!»'),
(4350, 4350, 'فَانْتَقَمْنَا مِنْهُمْ ۖ فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ الْمُكَذِّبِينَ', 43, 25, 25, 99, 491, 'به همین جهت از آنها انتقام گرفتیم؛ بنگر پایان کار تکذیب‌کنندگان چگونه بود!'),
(4351, 4351, 'وَإِذْ قَالَ إِبْرَاهِيمُ لِأَبِيهِ وَقَوْمِهِ إِنَّنِي بَرَاءٌ مِمَّا تَعْبُدُونَ', 43, 26, 25, 99, 491, 'و به خاطر بیاور هنگامی را که ابراهیم به پدرش = عمویش آزر و قومش گفت: «من از آنچه شما می‌پرستید بیزارم.'),
(4352, 4352, 'إِلَّا الَّذِي فَطَرَنِي فَإِنَّهُ سَيَهْدِينِ', 43, 27, 25, 99, 491, 'مگر آن کسی که مرا آفریده، که او هدایتم خواهد کرد!»'),
(4353, 4353, 'وَجَعَلَهَا كَلِمَةً بَاقِيَةً فِي عَقِبِهِ لَعَلَّهُمْ يَرْجِعُونَ', 43, 28, 25, 99, 491, 'او کلمه توحید را کلمه پاینده‌ای در نسلهای بعد از خود قرار داد، شاید به سوی خدا باز گردند!'),
(4354, 4354, 'بَلْ مَتَّعْتُ هَٰؤُلَاءِ وَآبَاءَهُمْ حَتَّىٰ جَاءَهُمُ الْحَقُّ وَرَسُولٌ مُبِينٌ', 43, 29, 25, 99, 491, 'ولی من این گروه و پدرانشان را از مواهب دنیا بهره‌مند ساختم تا حق و فرستاده آشکار (الهی) به سراغشان آمد؛'),
(4355, 4355, 'وَلَمَّا جَاءَهُمُ الْحَقُّ قَالُوا هَٰذَا سِحْرٌ وَإِنَّا بِهِ كَافِرُونَ', 43, 30, 25, 99, 491, 'هنگامی که حق به سراغشان آمد؛ گفتند: «این سحر است، و ما نسبت به آن کافریم»!'),
(4356, 4356, 'وَقَالُوا لَوْلَا نُزِّلَ هَٰذَا الْقُرْآنُ عَلَىٰ رَجُلٍ مِنَ الْقَرْيَتَيْنِ عَظِيمٍ', 43, 31, 25, 99, 491, 'و گفتند: «چرا این قرآن بر مرد بزرگ (و ثروتمندی) از این دو شهر (مکه و طائف) ناز ل نشده است؟!»'),
(4357, 4357, 'أَهُمْ يَقْسِمُونَ رَحْمَتَ رَبِّكَ ۚ نَحْنُ قَسَمْنَا بَيْنَهُمْ مَعِيشَتَهُمْ فِي الْحَيَاةِ الدُّنْيَا ۚ وَرَفَعْنَا بَعْضَهُمْ فَوْقَ بَعْضٍ دَرَجَاتٍ لِيَتَّخِذَ بَعْضُهُمْ بَعْضًا سُخْرِيًّا ۗ وَرَحْمَتُ رَبِّكَ خَيْرٌ مِمَّا يَجْمَعُونَ', 43, 32, 25, 99, 491, 'آیا آنان رحمت پروردگارت را تقسیم می‌کنند؟! ما معیشت آنها را در حیات دنیا در میانشان تقسیم کردیم و بعضی را بر بعضی برتری دادیم تا یکدیگر را مسخر کرده (و با هم تعاون نمایند)؛ و رحمت پروردگارت از تمام آنچه جمع‌آوری می‌کنند بهتر است!'),
(4358, 4358, 'وَلَوْلَا أَنْ يَكُونَ النَّاسُ أُمَّةً وَاحِدَةً لَجَعَلْنَا لِمَنْ يَكْفُرُ بِالرَّحْمَٰنِ لِبُيُوتِهِمْ سُقُفًا مِنْ فِضَّةٍ وَمَعَارِجَ عَلَيْهَا يَظْهَرُونَ', 43, 33, 25, 99, 491, 'اگر (تمکّن کفّار از مواهب مادی) سبب نمی‌شد که همه مردم امت واحد (گمراهی) شوند، ما برای کسانی که به (خداوند) رحمان کافر می‌شدند خانه‌هایی قرار می‌دادیم با سقفهایی از نقره و نردبانهایی که از آن بالا روند،'),
(4359, 4359, 'وَلِبُيُوتِهِمْ أَبْوَابًا وَسُرُرًا عَلَيْهَا يَتَّكِئُونَ', 43, 34, 25, 99, 492, 'و برای خانه‌هایشان درها و تختهایی (زیبا و نقره‌ای) قرار می‌دادیم که بر آن تکیه کنند؛'),
(4360, 4360, 'وَزُخْرُفًا ۚ وَإِنْ كُلُّ ذَٰلِكَ لَمَّا مَتَاعُ الْحَيَاةِ الدُّنْيَا ۚ وَالْآخِرَةُ عِنْدَ رَبِّكَ لِلْمُتَّقِينَ', 43, 35, 25, 99, 492, 'و انواع زیورها؛ ولی تمام اینها بهره زندگی دنیاست، و آخرت نزد پروردگارت از آن پرهیزگاران است!'),
(4361, 4361, 'وَمَنْ يَعْشُ عَنْ ذِكْرِ الرَّحْمَٰنِ نُقَيِّضْ لَهُ شَيْطَانًا فَهُوَ لَهُ قَرِينٌ', 43, 36, 25, 99, 492, 'و هر کس از یاد خدا روی‌گردان شود شیطان را به سراغ او میفرستیم پس همواره قرین اوست!'),
(4362, 4362, 'وَإِنَّهُمْ لَيَصُدُّونَهُمْ عَنِ السَّبِيلِ وَيَحْسَبُونَ أَنَّهُمْ مُهْتَدُونَ', 43, 37, 25, 99, 492, 'و آنها = شیاطین‌ این گروه را از راه خدا بازمی‌دارند، در حالی که گمان می‌کنند هدایت‌یافتگان حقیقی آنها هستند!'),
(4363, 4363, 'حَتَّىٰ إِذَا جَاءَنَا قَالَ يَا لَيْتَ بَيْنِي وَبَيْنَكَ بُعْدَ الْمَشْرِقَيْنِ فَبِئْسَ الْقَرِينُ', 43, 38, 25, 99, 492, 'تا زمانی که (در قیامت) نزد ما حاضر شود می‌گوید: ای کاش میان من و تو فاصله مشرق و مغرب بود؛ چه بد همنشینی بودی!'),
(4364, 4364, 'وَلَنْ يَنْفَعَكُمُ الْيَوْمَ إِذْ ظَلَمْتُمْ أَنَّكُمْ فِي الْعَذَابِ مُشْتَرِكُونَ', 43, 39, 25, 99, 492, '(ولی به آنها می‌گوییم:) هرگز این گفتگوها امروز به حال شما سودی ندارد، چرا که ظلم کردید؛ و همه در عذاب مشترکید!'),
(4365, 4365, 'أَفَأَنْتَ تُسْمِعُ الصُّمَّ أَوْ تَهْدِي الْعُمْيَ وَمَنْ كَانَ فِي ضَلَالٍ مُبِينٍ', 43, 40, 25, 99, 492, '(ای پیامبر!) آیا تو می‌توانی سخن خود را به گوش کران برسانی، یا کوران و کسانی را که در گمراهی آشکاری هستند هدایت کنی؟!'),
(4366, 4366, 'فَإِمَّا نَذْهَبَنَّ بِكَ فَإِنَّا مِنْهُمْ مُنْتَقِمُونَ', 43, 41, 25, 99, 492, 'و هرگاه تو را از میان آنها ببریم، حتماً از آنان انتقام خواهیم گرفت؛'),
(4367, 4367, 'أَوْ نُرِيَنَّكَ الَّذِي وَعَدْنَاهُمْ فَإِنَّا عَلَيْهِمْ مُقْتَدِرُونَ', 43, 42, 25, 99, 492, 'یا اگر (زنده بمانی و) و آنچه را (از عذاب) به آنان وعده داده‌ایم به تو نشان دهیم، باز ما بر آنها مسلّطیم!'),
(4368, 4368, 'فَاسْتَمْسِكْ بِالَّذِي أُوحِيَ إِلَيْكَ ۖ إِنَّكَ عَلَىٰ صِرَاطٍ مُسْتَقِيمٍ', 43, 43, 25, 99, 492, 'آنچه را بر تو وحی شده محکم بگیر که تو بر صراط مستقیمی.'),
(4369, 4369, 'وَإِنَّهُ لَذِكْرٌ لَكَ وَلِقَوْمِكَ ۖ وَسَوْفَ تُسْأَلُونَ', 43, 44, 25, 99, 492, 'و این مایه یادآوری (و عظمت) تو و قوم تو است و بزودی سؤال خواهید شد.'),
(4370, 4370, 'وَاسْأَلْ مَنْ أَرْسَلْنَا مِنْ قَبْلِكَ مِنْ رُسُلِنَا أَجَعَلْنَا مِنْ دُونِ الرَّحْمَٰنِ آلِهَةً يُعْبَدُونَ', 43, 45, 25, 99, 492, 'از رسولانی که پیش از تو فرستادیم بپرس: آیا غیر از خداوند رحمان معبودانی برای پرستش قرار دادیم؟!'),
(4371, 4371, 'وَلَقَدْ أَرْسَلْنَا مُوسَىٰ بِآيَاتِنَا إِلَىٰ فِرْعَوْنَ وَمَلَئِهِ فَقَالَ إِنِّي رَسُولُ رَبِّ الْعَالَمِينَ', 43, 46, 25, 99, 492, 'ما موسی را با آیات خود به سوی فرعون و درباریان او فرستادیم؛ (موسی به آنها) گفت: «من فرستاده پروردگار جهانیانم»'),
(4372, 4372, 'فَلَمَّا جَاءَهُمْ بِآيَاتِنَا إِذَا هُمْ مِنْهَا يَضْحَكُونَ', 43, 47, 25, 99, 492, 'ولی هنگامی که او آیات ما را برای آنها آورد، به آن می‌خندیدند!'),
(4373, 4373, 'وَمَا نُرِيهِمْ مِنْ آيَةٍ إِلَّا هِيَ أَكْبَرُ مِنْ أُخْتِهَا ۖ وَأَخَذْنَاهُمْ بِالْعَذَابِ لَعَلَّهُمْ يَرْجِعُونَ', 43, 48, 25, 99, 493, 'ما هیچ آیه (و معجزه‌ای) به آنان نشان نمی‌دادیم مگر اینکه از دیگری بزرگتر (و مهمتر) بود؛ و آنها را به (انواع) عذاب گرفتار کردیم شاید بازگردند!'),
(4374, 4374, 'وَقَالُوا يَا أَيُّهَ السَّاحِرُ ادْعُ لَنَا رَبَّكَ بِمَا عَهِدَ عِنْدَكَ إِنَّنَا لَمُهْتَدُونَ', 43, 49, 25, 99, 493, '(وقتی گرفتار بلا می‌شدند می) گفتند: «ای ساحر! پروردگارت را به عهدی که با تو کرده بخوان (تا ما را از این بلا برهاند) که ما هدایت خواهیم یافت (و ایمان می‌آوریم)!»'),
(4375, 4375, 'فَلَمَّا كَشَفْنَا عَنْهُمُ الْعَذَابَ إِذَا هُمْ يَنْكُثُونَ', 43, 50, 25, 99, 493, 'امّا هنگامی که عذاب را از آنها برطرف می‌ساختیم پیمان خود را می‌شکستند!'),
(4376, 4376, 'وَنَادَىٰ فِرْعَوْنُ فِي قَوْمِهِ قَالَ يَا قَوْمِ أَلَيْسَ لِي مُلْكُ مِصْرَ وَهَٰذِهِ الْأَنْهَارُ تَجْرِي مِنْ تَحْتِي ۖ أَفَلَا تُبْصِرُونَ', 43, 51, 25, 99, 493, 'فرعون در میان قوم خود ندا داد و گفت: «ای قوم من! آیا حکومت مصر از آن من نیست، و این نهرها تحت فرمان من جریان ندارد؟ آیا نمی‌بینید؟'),
(4377, 4377, 'أَمْ أَنَا خَيْرٌ مِنْ هَٰذَا الَّذِي هُوَ مَهِينٌ وَلَا يَكَادُ يُبِينُ', 43, 52, 25, 99, 493, 'مگر نه این است که من از این مردی که از خانواده و طبقه پستی است و هرگز نمی‌تواند فصیح سخن بگوید برترم؟'),
(4378, 4378, 'فَلَوْلَا أُلْقِيَ عَلَيْهِ أَسْوِرَةٌ مِنْ ذَهَبٍ أَوْ جَاءَ مَعَهُ الْمَلَائِكَةُ مُقْتَرِنِينَ', 43, 53, 25, 99, 493, '(اگر راست می‌گوید) چرا دستبندهای طلا به او داده نشده، یا اینکه چرا فرشتگان دوشادوش او نیامده‌اند (تا گفتارش را تأیید کنند)؟!'),
(4379, 4379, 'فَاسْتَخَفَّ قَوْمَهُ فَأَطَاعُوهُ ۚ إِنَّهُمْ كَانُوا قَوْمًا فَاسِقِينَ', 43, 54, 25, 99, 493, '(فرعون) قوم خود را سبک شمرد، در نتیجه از او اطاعت کردند؛ آنان قومی فاسق بودند!'),
(4380, 4380, 'فَلَمَّا آسَفُونَا انْتَقَمْنَا مِنْهُمْ فَأَغْرَقْنَاهُمْ أَجْمَعِينَ', 43, 55, 25, 99, 493, 'امّا هنگامی که ما را به خشم آوردند، از آنها انتقام گرفتیم و همه را غرق کردیم.'),
(4381, 4381, 'فَجَعَلْنَاهُمْ سَلَفًا وَمَثَلًا لِلْآخِرِينَ', 43, 56, 25, 99, 493, 'و آنها را پیشگامان (در عذاب) و عبرتی برای دیگران قرار دادیم.'),
(4382, 4382, ' وَلَمَّا ضُرِبَ ابْنُ مَرْيَمَ مَثَلًا إِذَا قَوْمُكَ مِنْهُ يَصِدُّونَ', 43, 57, 25, 99, 493, 'و هنگامی که درباره فرزند مریم مثلی زده شد، ناگهان قوم تو بخاطر آن داد و فریاد راه انداختند.'),
(4383, 4383, 'وَقَالُوا أَآلِهَتُنَا خَيْرٌ أَمْ هُوَ ۚ مَا ضَرَبُوهُ لَكَ إِلَّا جَدَلًا ۚ بَلْ هُمْ قَوْمٌ خَصِمُونَ', 43, 58, 25, 99, 493, 'و گفتند: «آیا خدایان ما بهترند یا او = مسیح‌؟! (اگر معبودان ما در دوزخند، مسیح نیز در دوزخ است، چرا که معبود واقع شده)!» ولی آنها این مثل را جز از طریق جدال (و لجاج) برای تو نزدند؛ آنان گروهی کینه‌توز و پرخاشگرند!'),
(4384, 4384, 'إِنْ هُوَ إِلَّا عَبْدٌ أَنْعَمْنَا عَلَيْهِ وَجَعَلْنَاهُ مَثَلًا لِبَنِي إِسْرَائِيلَ', 43, 59, 25, 99, 493, 'مسیح فقط بنده‌ای بود که ما نعمت به او بخشیدیم و او را نمونه و الگوئی برای بنی اسرائیل قرار دادیم.'),
(4385, 4385, 'وَلَوْ نَشَاءُ لَجَعَلْنَا مِنْكُمْ مَلَائِكَةً فِي الْأَرْضِ يَخْلُفُونَ', 43, 60, 25, 99, 493, 'و هرگاه بخواهیم به جای شما در زمین فرشتگانی قرار می‌دهیم که جانشین (شما) گردند!'),
(4386, 4386, 'وَإِنَّهُ لَعِلْمٌ لِلسَّاعَةِ فَلَا تَمْتَرُنَّ بِهَا وَاتَّبِعُونِ ۚ هَٰذَا صِرَاطٌ مُسْتَقِيمٌ', 43, 61, 25, 99, 494, 'و او = مسیح‌ سبب آگاهی بر روز قیامت است. (زیرا نزول عیسی گواه نزدیکی رستاخیز است)؛ هرگز در آن تردید نکنید؛ و از من پیروی کنید که این راه مستقیم است!'),
(4387, 4387, 'وَلَا يَصُدَّنَّكُمُ الشَّيْطَانُ ۖ إِنَّهُ لَكُمْ عَدُوٌّ مُبِينٌ', 43, 62, 25, 99, 494, 'و شیطان شما را (از راه خدا) باز ندارد، که او دشمن آشکار شماست!'),
(4388, 4388, 'وَلَمَّا جَاءَ عِيسَىٰ بِالْبَيِّنَاتِ قَالَ قَدْ جِئْتُكُمْ بِالْحِكْمَةِ وَلِأُبَيِّنَ لَكُمْ بَعْضَ الَّذِي تَخْتَلِفُونَ فِيهِ ۖ فَاتَّقُوا اللَّهَ وَأَطِيعُونِ', 43, 63, 25, 99, 494, 'و هنگامی که عیسی دلایل روشن (برای آنها) آورد گفت: «من برای شما حکمت آورده‌ام، و آمده‌ام تا برخی از آنچه را که در آن اختلاف دارید روشن کنم؛ پس تقوای الهی پیشه کنید و از من اطاعت نمایید!'),
(4389, 4389, 'إِنَّ اللَّهَ هُوَ رَبِّي وَرَبُّكُمْ فَاعْبُدُوهُ ۚ هَٰذَا صِرَاطٌ مُسْتَقِيمٌ', 43, 64, 25, 99, 494, 'خداوند پروردگار من و پروردگار شماست؛ (تنها) او را پرستش کنید که راه راست همین است!»'),
(4390, 4390, 'فَاخْتَلَفَ الْأَحْزَابُ مِنْ بَيْنِهِمْ ۖ فَوَيْلٌ لِلَّذِينَ ظَلَمُوا مِنْ عَذَابِ يَوْمٍ أَلِيمٍ', 43, 65, 25, 99, 494, 'ولی گروه‌هایی از میان آنها (درباره مسیح) اختلاف کردند (و بعضی او را خدا پنداشتند)؛ وای بر کسانی که ستم کردند از عذاب روزی دردناک!'),
(4391, 4391, 'هَلْ يَنْظُرُونَ إِلَّا السَّاعَةَ أَنْ تَأْتِيَهُمْ بَغْتَةً وَهُمْ لَا يَشْعُرُونَ', 43, 66, 25, 99, 494, 'آیا جز این انتظار دارند که قیامت ناگهان به سراغشان آید در حالی که نمی‌فهمند؟'),
(4392, 4392, 'الْأَخِلَّاءُ يَوْمَئِذٍ بَعْضُهُمْ لِبَعْضٍ عَدُوٌّ إِلَّا الْمُتَّقِينَ', 43, 67, 25, 99, 494, 'دوستان در آن روز دشمن یکدیگرند، مگر پرهیزگاران!'),
(4393, 4393, 'يَا عِبَادِ لَا خَوْفٌ عَلَيْكُمُ الْيَوْمَ وَلَا أَنْتُمْ تَحْزَنُونَ', 43, 68, 25, 99, 494, 'ای بندگان من! امروز نه ترسی بر شماست و نه اندوهگین می‌شوید!'),
(4394, 4394, 'الَّذِينَ آمَنُوا بِآيَاتِنَا وَكَانُوا مُسْلِمِينَ', 43, 69, 25, 99, 494, 'همان کسانی که به آیات ما ایمان آوردند و تسلیم بودند.'),
(4395, 4395, 'ادْخُلُوا الْجَنَّةَ أَنْتُمْ وَأَزْوَاجُكُمْ تُحْبَرُونَ', 43, 70, 25, 99, 494, '(به آنها خطاب می‌شود:) شما و همسرانتان در نهایت شادمانی وارد بهشت شوید!'),
(4396, 4396, 'يُطَافُ عَلَيْهِمْ بِصِحَافٍ مِنْ ذَهَبٍ وَأَكْوَابٍ ۖ وَفِيهَا مَا تَشْتَهِيهِ الْأَنْفُسُ وَتَلَذُّ الْأَعْيُنُ ۖ وَأَنْتُمْ فِيهَا خَالِدُونَ', 43, 71, 25, 99, 494, '(این در حالی است که) ظرفها (ی غذا) و جامهای طلائی (شراب طهور) را گرداگرد آنها می‌گردانند؛ و در آن (بهشت) آنچه دلها می‌خواهد و چشمها از آن لذت می‌برد موجود است؛ و شما همیشه در آن خواهید ماند!'),
(4397, 4397, 'وَتِلْكَ الْجَنَّةُ الَّتِي أُورِثْتُمُوهَا بِمَا كُنْتُمْ تَعْمَلُونَ', 43, 72, 25, 99, 494, 'این بهشتی است که شما وارث آن می‌شوید بخاطر اعمالی که انجام می‌دادید!'),
(4398, 4398, 'لَكُمْ فِيهَا فَاكِهَةٌ كَثِيرَةٌ مِنْهَا تَأْكُلُونَ', 43, 73, 25, 99, 494, 'و در آن برای شما میوه‌های فراوان است که از آن می‌خورید.'),
(4399, 4399, 'إِنَّ الْمُجْرِمِينَ فِي عَذَابِ جَهَنَّمَ خَالِدُونَ', 43, 74, 25, 99, 495, '(ولی) مجرمان در عذاب دوزخ جاودانه می‌مانند.'),
(4400, 4400, 'لَا يُفَتَّرُ عَنْهُمْ وَهُمْ فِيهِ مُبْلِسُونَ', 43, 75, 25, 99, 495, 'هرگز عذاب آنان تخفیف نمی‌یابد، و در آنجا از همه چیز مأیوسند.'),
(4401, 4401, 'وَمَا ظَلَمْنَاهُمْ وَلَٰكِنْ كَانُوا هُمُ الظَّالِمِينَ', 43, 76, 25, 99, 495, 'ما به آنها ستم نکردیم، آنان خود ستمکار بودند!'),
(4402, 4402, 'وَنَادَوْا يَا مَالِكُ لِيَقْضِ عَلَيْنَا رَبُّكَ ۖ قَالَ إِنَّكُمْ مَاكِثُونَ', 43, 77, 25, 99, 495, 'آنها فریاد می‌کشند: «ای مالک دوزخ! (ای کاش) پروردگارت ما را بمیراند (تا آسوده شویم)!» می‌گوید: «شما در این جا ماندنی هستید!»'),
(4403, 4403, 'لَقَدْ جِئْنَاكُمْ بِالْحَقِّ وَلَٰكِنَّ أَكْثَرَكُمْ لِلْحَقِّ كَارِهُونَ', 43, 78, 25, 99, 495, 'ما حق را برای شما آوردیم؛ ولی بیشتر شما از حق کراهت داشتید!'),
(4404, 4404, 'أَمْ أَبْرَمُوا أَمْرًا فَإِنَّا مُبْرِمُونَ', 43, 79, 25, 99, 495, 'بلکه آنها تصمیم محکم بر توطئه گرفتند؛ ما نیز اراده محکمی (درباره آنها) داریم!'),
(4405, 4405, 'أَمْ يَحْسَبُونَ أَنَّا لَا نَسْمَعُ سِرَّهُمْ وَنَجْوَاهُمْ ۚ بَلَىٰ وَرُسُلُنَا لَدَيْهِمْ يَكْتُبُونَ', 43, 80, 25, 99, 495, 'آیا آنان می‌پندارند که ما اسرار نهانی و سخنان درگوشی آنان را نمی‌شنویم؟ آری، رسولان (و فرشتگان) ما نزد آنها هستند و می‌نویسند!'),
(4406, 4406, 'قُلْ إِنْ كَانَ لِلرَّحْمَٰنِ وَلَدٌ فَأَنَا أَوَّلُ الْعَابِدِينَ', 43, 81, 25, 99, 495, 'بگو: «اگر برای خداوند فرزندی بود، من نخستین پرستنده او بودم!»'),
(4407, 4407, 'سُبْحَانَ رَبِّ السَّمَاوَاتِ وَالْأَرْضِ رَبِّ الْعَرْشِ عَمَّا يَصِفُونَ', 43, 82, 25, 99, 495, 'منزه است پروردگار آسمانها و زمین، پروردگار عرش، از توصیفی که آنها می‌کنند!'),
(4408, 4408, 'فَذَرْهُمْ يَخُوضُوا وَيَلْعَبُوا حَتَّىٰ يُلَاقُوا يَوْمَهُمُ الَّذِي يُوعَدُونَ', 43, 83, 25, 99, 495, 'آنان را به حال خود واگذار تا در باطل غوطه‌ور باشند و سرگرم بازی شوند تا روزی را که به آنها وعده داده شده است ملاقات کنند (و نتیجه کار خود را ببینند)!'),
(4409, 4409, 'وَهُوَ الَّذِي فِي السَّمَاءِ إِلَٰهٌ وَفِي الْأَرْضِ إِلَٰهٌ ۚ وَهُوَ الْحَكِيمُ الْعَلِيمُ', 43, 84, 25, 99, 495, 'او کسی است که در آسمان معبود است و در زمین معبود؛ و او حکیم و علیم است!'),
(4410, 4410, 'وَتَبَارَكَ الَّذِي لَهُ مُلْكُ السَّمَاوَاتِ وَالْأَرْضِ وَمَا بَيْنَهُمَا وَعِنْدَهُ عِلْمُ السَّاعَةِ وَإِلَيْهِ تُرْجَعُونَ', 43, 85, 25, 99, 495, 'پر برکت و پایدار است کسی که حکومت آسمانها و زمین و آنچه در میان آن دو است از آن اوست؛ و آگاهی از قیام قیامت نزد اوست و به سوی او بازگردانده می‌شوید!'),
(4411, 4411, 'وَلَا يَمْلِكُ الَّذِينَ يَدْعُونَ مِنْ دُونِهِ الشَّفَاعَةَ إِلَّا مَنْ شَهِدَ بِالْحَقِّ وَهُمْ يَعْلَمُونَ', 43, 86, 25, 99, 495, 'کسانی را که غیر از او می‌خوانند قادر بر شفاعت نیستند؛ مگر آنها که شهادت به حق داده‌اند و بخوبی آگاهند!'),
(4412, 4412, 'وَلَئِنْ سَأَلْتَهُمْ مَنْ خَلَقَهُمْ لَيَقُولُنَّ اللَّهُ ۖ فَأَنَّىٰ يُؤْفَكُونَ', 43, 87, 25, 99, 495, 'و اگر از آنها بپرسی چه کسی آنان را آفریده، قطعاً می‌گویند: خدا؛ پس چگونه از عبادت او منحرف می‌شوند؟!'),
(4413, 4413, 'وَقِيلِهِ يَا رَبِّ إِنَّ هَٰؤُلَاءِ قَوْمٌ لَا يُؤْمِنُونَ', 43, 88, 25, 99, 495, 'آنها چگونه از شکایت پیامبر که می‌گوید: «پروردگارا! اینها قومی هستند که ایمان نمی آورند» (غافل می‌شوند؟!)'),
(4414, 4414, 'فَاصْفَحْ عَنْهُمْ وَقُلْ سَلَامٌ ۚ فَسَوْفَ يَعْلَمُونَ', 43, 89, 25, 99, 495, 'پس (اکنون که چنین است) از آنان روی برگردان و بگو: «سلام بر شما»، امّا بزودی خواهند دانست!'),
(4415, 4415, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ حم', 44, 1, 25, 99, 496, 'حم‌'),
(4416, 4416, 'وَالْكِتَابِ الْمُبِينِ', 44, 2, 25, 99, 496, 'سوگند به این کتاب روشنگر،'),
(4417, 4417, 'إِنَّا أَنْزَلْنَاهُ فِي لَيْلَةٍ مُبَارَكَةٍ ۚ إِنَّا كُنَّا مُنْذِرِينَ', 44, 3, 25, 99, 496, 'که ما آن را در شبی پر برکت نازل کردیم؛ ما همواره انذارکننده بوده‌ایم!'),
(4418, 4418, 'فِيهَا يُفْرَقُ كُلُّ أَمْرٍ حَكِيمٍ', 44, 4, 25, 99, 496, 'در آن شب هر امری بر اساس حکمت (الهی) تدبیر و جدا می‌گردد.'),
(4419, 4419, 'أَمْرًا مِنْ عِنْدِنَا ۚ إِنَّا كُنَّا مُرْسِلِينَ', 44, 5, 25, 99, 496, '(آری، نزول قرآن) فرمانی بود از سوی ما؛ ما (محمد (ص) را) فرستادیم!'),
(4420, 4420, 'رَحْمَةً مِنْ رَبِّكَ ۚ إِنَّهُ هُوَ السَّمِيعُ الْعَلِيمُ', 44, 6, 25, 99, 496, 'اینها همه بخاطر رحمتی است از سوی پروردگارت، که شنونده و داناست!'),
(4421, 4421, 'رَبِّ السَّمَاوَاتِ وَالْأَرْضِ وَمَا بَيْنَهُمَا ۖ إِنْ كُنْتُمْ مُوقِنِينَ', 44, 7, 25, 99, 496, '(همان) پروردگار آسمانها و زمین و آنچه در میان آنهاست، اگر اهل یقین هستید!'),
(4422, 4422, 'لَا إِلَٰهَ إِلَّا هُوَ يُحْيِي وَيُمِيتُ ۖ رَبُّكُمْ وَرَبُّ آبَائِكُمُ الْأَوَّلِينَ', 44, 8, 25, 99, 496, 'هیچ معبودی جز او نیست؛ زنده می‌کند و می‌میراند؛ او پروردگار شما و پروردگار پدران نخستین شماست!'),
(4423, 4423, 'بَلْ هُمْ فِي شَكٍّ يَلْعَبُونَ', 44, 9, 25, 99, 496, 'ولی آنها در شکند و (با حقایق) بازی می‌کنند.'),
(4424, 4424, 'فَارْتَقِبْ يَوْمَ تَأْتِي السَّمَاءُ بِدُخَانٍ مُبِينٍ', 44, 10, 25, 99, 496, 'پس منتظر روزی باش که آسمان دود آشکاری پدید آورد...'),
(4425, 4425, 'يَغْشَى النَّاسَ ۖ هَٰذَا عَذَابٌ أَلِيمٌ', 44, 11, 25, 99, 496, 'که همه مردم را فرامی‌گیرد؛ این عذاب دردناکی است!'),
(4426, 4426, 'رَبَّنَا اكْشِفْ عَنَّا الْعَذَابَ إِنَّا مُؤْمِنُونَ', 44, 12, 25, 99, 496, '(می‌گویند:) پروردگارا! عذاب را از ما برطرف کن که ایمان می‌آوریم.'),
(4427, 4427, 'أَنَّىٰ لَهُمُ الذِّكْرَىٰ وَقَدْ جَاءَهُمْ رَسُولٌ مُبِينٌ', 44, 13, 25, 99, 496, 'چگونه: و از کجا متذکر می‌شوند با اینکه رسول روشنگر (با معجزات و منطق روشن) به سراغشان آمد!'),
(4428, 4428, 'ثُمَّ تَوَلَّوْا عَنْهُ وَقَالُوا مُعَلَّمٌ مَجْنُونٌ', 44, 14, 25, 99, 496, 'سپس از او روی گردان شدند و گفتند: «او تعلیم یافته‌ای دیوانه است!»'),
(4429, 4429, 'إِنَّا كَاشِفُو الْعَذَابِ قَلِيلًا ۚ إِنَّكُمْ عَائِدُونَ', 44, 15, 25, 99, 496, 'ما عذاب را کمی برطرف می‌سازیم، ولی باز به کارهای خود بازمی‌گردید!'),
(4430, 4430, 'يَوْمَ نَبْطِشُ الْبَطْشَةَ الْكُبْرَىٰ إِنَّا مُنْتَقِمُونَ', 44, 16, 25, 99, 496, '(ما از آنها انتقام می‌گیریم) در آن روز که آنها را با قدرت خواهیم گرفت؛ آری ما انتقام گیرنده‌ایم!'),
(4431, 4431, ' وَلَقَدْ فَتَنَّا قَبْلَهُمْ قَوْمَ فِرْعَوْنَ وَجَاءَهُمْ رَسُولٌ كَرِيمٌ', 44, 17, 25, 100, 496, 'ما پیش از اینها قوم فرعون را آزمودیم و رسول بزرگواری به سراغشان آمد،'),
(4432, 4432, 'أَنْ أَدُّوا إِلَيَّ عِبَادَ اللَّهِ ۖ إِنِّي لَكُمْ رَسُولٌ أَمِينٌ', 44, 18, 25, 100, 496, '(و به آنان گفت: امور) بندگان خدا را به من واگذارید که من فرستاده امینی برای شما هستم!'),
(4433, 4433, 'وَأَنْ لَا تَعْلُوا عَلَى اللَّهِ ۖ إِنِّي آتِيكُمْ بِسُلْطَانٍ مُبِينٍ', 44, 19, 25, 100, 497, 'و در برابر خداوند تکبر نورزید که من برای شما دلیل روشنی آورده‌ام!'),
(4434, 4434, 'وَإِنِّي عُذْتُ بِرَبِّي وَرَبِّكُمْ أَنْ تَرْجُمُونِ', 44, 20, 25, 100, 497, 'و من به پروردگار خود و پروردگار شما پناه می‌برم از اینکه مرا متهم کنید!'),
(4435, 4435, 'وَإِنْ لَمْ تُؤْمِنُوا لِي فَاعْتَزِلُونِ', 44, 21, 25, 100, 497, 'و اگر به من ایمان نمی‌آورید، از من کناره‌گیری کنید (و مانع ایمان آوردن مردم نشوید)!'),
(4436, 4436, 'فَدَعَا رَبَّهُ أَنَّ هَٰؤُلَاءِ قَوْمٌ مُجْرِمُونَ', 44, 22, 25, 100, 497, '(آنها هیچ یک از این پندها را نپذیرفتند، و موسی) به پیشگاه پروردگارش عرضه داشت: اینها قومی مجرمند!'),
(4437, 4437, 'فَأَسْرِ بِعِبَادِي لَيْلًا إِنَّكُمْ مُتَّبَعُونَ', 44, 23, 25, 100, 497, '(به او دستور داده شد:) بندگان مرا شبانه حرکت ده که شما تعقیب می‌شوید!'),
(4438, 4438, 'وَاتْرُكِ الْبَحْرَ رَهْوًا ۖ إِنَّهُمْ جُنْدٌ مُغْرَقُونَ', 44, 24, 25, 100, 497, '(هنگامی که از دریا گذشتید) دریا را آرام و گشاده بگذار (و بگذر) که آنها لشکری غرق‌شده خواهند بود!'),
(4439, 4439, 'كَمْ تَرَكُوا مِنْ جَنَّاتٍ وَعُيُونٍ', 44, 25, 25, 100, 497, '(سرانجام همگی نابود شدند و) چه بسیار باغها و چشمه‌ها که از خود به جای گذاشتند،'),
(4440, 4440, 'وَزُرُوعٍ وَمَقَامٍ كَرِيمٍ', 44, 26, 25, 100, 497, 'و زراعتها و قصرهای زیبا و گرانقیمت،'),
(4441, 4441, 'وَنَعْمَةٍ كَانُوا فِيهَا فَاكِهِينَ', 44, 27, 25, 100, 497, 'و نعمتهای فراوان دیگر که در آن (غرق) بودند!'),
(4442, 4442, 'كَذَٰلِكَ ۖ وَأَوْرَثْنَاهَا قَوْمًا آخَرِينَ', 44, 28, 25, 100, 497, 'اینچنین بود ماجرای آنان! و ما (اموال و حکومت) اینها را میراث برای اقوام دیگری قرار دادیم!'),
(4443, 4443, 'فَمَا بَكَتْ عَلَيْهِمُ السَّمَاءُ وَالْأَرْضُ وَمَا كَانُوا مُنْظَرِينَ', 44, 29, 25, 100, 497, 'نه آسمان بر آنان گریست و نه زمین، و نه به آنها مهلتی داده شد!'),
(4444, 4444, 'وَلَقَدْ نَجَّيْنَا بَنِي إِسْرَائِيلَ مِنَ الْعَذَابِ الْمُهِينِ', 44, 30, 25, 100, 497, 'ما بنی اسرائیل را از عذاب ذلّت‌بار رهایی بخشیدیم؛'),
(4445, 4445, 'مِنْ فِرْعَوْنَ ۚ إِنَّهُ كَانَ عَالِيًا مِنَ الْمُسْرِفِينَ', 44, 31, 25, 100, 497, 'از فرعون که مردی متکبر و از اسرافکاران بود!'),
(4446, 4446, 'وَلَقَدِ اخْتَرْنَاهُمْ عَلَىٰ عِلْمٍ عَلَى الْعَالَمِينَ', 44, 32, 25, 100, 497, 'ما آنها را با علم (خویش) بر جهانیان برگزیدیم و برتری دادیم،'),
(4447, 4447, 'وَآتَيْنَاهُمْ مِنَ الْآيَاتِ مَا فِيهِ بَلَاءٌ مُبِينٌ', 44, 33, 25, 100, 497, 'و آیاتی (از قدرت خویش) را به آنها دادیم که آزمایش آشکاری در آن بود (ولی آنان کفران کردند و مجازات شدند)!'),
(4448, 4448, 'إِنَّ هَٰؤُلَاءِ لَيَقُولُونَ', 44, 34, 25, 100, 497, 'اینها = مشرکان‌ می‌گویند:'),
(4449, 4449, 'إِنْ هِيَ إِلَّا مَوْتَتُنَا الْأُولَىٰ وَمَا نَحْنُ بِمُنْشَرِينَ', 44, 35, 25, 100, 497, '«مرگ ما جز همان مرگ اول نیست و هرگز برانگیخته نخواهیم شد!'),
(4450, 4450, 'فَأْتُوا بِآبَائِنَا إِنْ كُنْتُمْ صَادِقِينَ', 44, 36, 25, 100, 497, 'اگر راست می‌گویید پدران ما را (زنده کنید و) بیاورید (تا گواهی دهند)!»'),
(4451, 4451, 'أَهُمْ خَيْرٌ أَمْ قَوْمُ تُبَّعٍ وَالَّذِينَ مِنْ قَبْلِهِمْ ۚ أَهْلَكْنَاهُمْ ۖ إِنَّهُمْ كَانُوا مُجْرِمِينَ', 44, 37, 25, 100, 497, 'آیا آنان بهترند یا قوم «تبّع» و کسانی که پیش از آنها بودند؟! ما آنان را هلاک کردیم، چرا که مجرم بودند!'),
(4452, 4452, 'وَمَا خَلَقْنَا السَّمَاوَاتِ وَالْأَرْضَ وَمَا بَيْنَهُمَا لَاعِبِينَ', 44, 38, 25, 100, 497, 'ما آسمانها و زمین و آنچه را که در میان این دو است به بازی (و بی‌هدف) نیافریدیم!'),
(4453, 4453, 'مَا خَلَقْنَاهُمَا إِلَّا بِالْحَقِّ وَلَٰكِنَّ أَكْثَرَهُمْ لَا يَعْلَمُونَ', 44, 39, 25, 100, 497, 'ما آن دو را جز بحق نیافریدیم؛ ولی بیشتر آنان نمی‌دانند!'),
(4454, 4454, 'إِنَّ يَوْمَ الْفَصْلِ مِيقَاتُهُمْ أَجْمَعِينَ', 44, 40, 25, 100, 498, 'روز جدایی (حق از باطل) وعده‌گاه همه آنهاست!'),
(4455, 4455, 'يَوْمَ لَا يُغْنِي مَوْلًى عَنْ مَوْلًى شَيْئًا وَلَا هُمْ يُنْصَرُونَ', 44, 41, 25, 100, 498, 'روزی که هیچ دوستی کمترین کمکی به دوستش نمی‌کند، و از هیچ‌سو یاری نمی‌شوند؛'),
(4456, 4456, 'إِلَّا مَنْ رَحِمَ اللَّهُ ۚ إِنَّهُ هُوَ الْعَزِيزُ الرَّحِيمُ', 44, 42, 25, 100, 498, 'مگر کسی که خدا او را مورد رحمت قرار داده، چرا که او عزیز و رحیم است!'),
(4457, 4457, 'إِنَّ شَجَرَتَ الزَّقُّومِ', 44, 43, 25, 100, 498, 'مسلّماً درخت زقّوم...'),
(4458, 4458, 'طَعَامُ الْأَثِيمِ', 44, 44, 25, 100, 498, 'غذای گنهکاران است،'),
(4459, 4459, 'كَالْمُهْلِ يَغْلِي فِي الْبُطُونِ', 44, 45, 25, 100, 498, 'همانند فلز گداخته در شکمها می‌جوشد؛'),
(4460, 4460, 'كَغَلْيِ الْحَمِيمِ', 44, 46, 25, 100, 498, 'جوششی همچون آب سوزان!'),
(4461, 4461, 'خُذُوهُ فَاعْتِلُوهُ إِلَىٰ سَوَاءِ الْجَحِيمِ', 44, 47, 25, 100, 498, '(آنگاه به مأموران دوزخ خطاب می‌شود:) این کافر مجرم را بگیرید و به میان دوزخ پرتابش کنید!'),
(4462, 4462, 'ثُمَّ صُبُّوا فَوْقَ رَأْسِهِ مِنْ عَذَابِ الْحَمِيمِ', 44, 48, 25, 100, 498, 'سپس بر سر او از عذاب جوشان بریزید!'),
(4463, 4463, 'ذُقْ إِنَّكَ أَنْتَ الْعَزِيزُ الْكَرِيمُ', 44, 49, 25, 100, 498, '(به او گفته می‌شود:) بچش که (به پندار خود) بسیار قدرتمند و محترم بودی!'),
(4464, 4464, 'إِنَّ هَٰذَا مَا كُنْتُمْ بِهِ تَمْتَرُونَ', 44, 50, 25, 100, 498, 'این همان چیزی است که پیوسته در آن تردید می‌کردید!'),
(4465, 4465, 'إِنَّ الْمُتَّقِينَ فِي مَقَامٍ أَمِينٍ', 44, 51, 25, 100, 498, '(ولی) پرهیزگاران در جایگاه امنی قرار دارند،'),
(4466, 4466, 'فِي جَنَّاتٍ وَعُيُونٍ', 44, 52, 25, 100, 498, 'در میان باغها و چشمه‌ها؛'),
(4467, 4467, 'يَلْبَسُونَ مِنْ سُنْدُسٍ وَإِسْتَبْرَقٍ مُتَقَابِلِينَ', 44, 53, 25, 100, 498, 'آنها لباسهایی از حریر نازک و ضخیم می‌پوشند و در مقابل یکدیگر می‌نشینند؛'),
(4468, 4468, 'كَذَٰلِكَ وَزَوَّجْنَاهُمْ بِحُورٍ عِينٍ', 44, 54, 25, 100, 498, 'اینچنین‌اند بهشتیان؛ و آنها را با «حور العین» تزویج می‌کنیم!'),
(4469, 4469, 'يَدْعُونَ فِيهَا بِكُلِّ فَاكِهَةٍ آمِنِينَ', 44, 55, 25, 100, 498, 'آنها در آنجا هر نوع میوه‌ای را بخواهند در اختیارشان قرارمی‌گیرد، و در نهایت امنیّت به سر می‌برند!'),
(4470, 4470, 'لَا يَذُوقُونَ فِيهَا الْمَوْتَ إِلَّا الْمَوْتَةَ الْأُولَىٰ ۖ وَوَقَاهُمْ عَذَابَ الْجَحِيمِ', 44, 56, 25, 100, 498, 'هرگز مرگی جز همان مرگ اوّل (که در دنیا چشیده‌اند) نخواهند چشید، و خداوند آنها را از عذاب دوزخ حفظ می‌کند؛'),
(4471, 4471, 'فَضْلًا مِنْ رَبِّكَ ۚ ذَٰلِكَ هُوَ الْفَوْزُ الْعَظِيمُ', 44, 57, 25, 100, 498, 'این فضل و بخششی است از سوی پروردگارت، این همان رستگاری بزرگ است!'),
(4472, 4472, 'فَإِنَّمَا يَسَّرْنَاهُ بِلِسَانِكَ لَعَلَّهُمْ يَتَذَكَّرُونَ', 44, 58, 25, 100, 498, 'ما آن = قرآن‌ را بر زبان تو آسان ساختیم، شاید آنان متذکّر شوند!'),
(4473, 4473, 'فَارْتَقِبْ إِنَّهُمْ مُرْتَقِبُونَ', 44, 59, 25, 100, 498, '(امّا اگر نپذیرفتند) منتظر باش، آنها نیز منتظرند (تو منتظر پیروزی الهی و آنها منتظر عذاب و شکست)!'),
(4474, 4474, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ حم', 45, 1, 25, 100, 499, 'حم‌'),
(4475, 4475, 'تَنْزِيلُ الْكِتَابِ مِنَ اللَّهِ الْعَزِيزِ الْحَكِيمِ', 45, 2, 25, 100, 499, 'این کتاب از سوی خداوند عزیز و حکیم نازل شده است!'),
(4476, 4476, 'إِنَّ فِي السَّمَاوَاتِ وَالْأَرْضِ لَآيَاتٍ لِلْمُؤْمِنِينَ', 45, 3, 25, 100, 499, 'بی‌شکّ در آسمانها و زمین نشانه‌های (فراوانی) برای مؤمنان وجود دارد؛'),
(4477, 4477, 'وَفِي خَلْقِكُمْ وَمَا يَبُثُّ مِنْ دَابَّةٍ آيَاتٌ لِقَوْمٍ يُوقِنُونَ', 45, 4, 25, 100, 499, 'و نیز در آفرینش شما و جنبندگانی که (در سراسر زمین) پراکنده ساخته، نشانه‌هایی است برای جمعیّتی که اهل یقینند.'),
(4478, 4478, 'وَاخْتِلَافِ اللَّيْلِ وَالنَّهَارِ وَمَا أَنْزَلَ اللَّهُ مِنَ السَّمَاءِ مِنْ رِزْقٍ فَأَحْيَا بِهِ الْأَرْضَ بَعْدَ مَوْتِهَا وَتَصْرِيفِ الرِّيَاحِ آيَاتٌ لِقَوْمٍ يَعْقِلُونَ', 45, 5, 25, 100, 499, 'و نیز در آمد و شد شب و روز، و رزق (و بارانی) که خداوند از آسمان نازل کرده و بوسیله آن زمین را بعد از مردنش حیات بخشیده و همچنین در وزش بادها، نشانه‌های روشنی است برای گروهی که اهل تفکّرند!'),
(4479, 4479, 'تِلْكَ آيَاتُ اللَّهِ نَتْلُوهَا عَلَيْكَ بِالْحَقِّ ۖ فَبِأَيِّ حَدِيثٍ بَعْدَ اللَّهِ وَآيَاتِهِ يُؤْمِنُونَ', 45, 6, 25, 100, 499, 'اینها آیات خداوند است که ما آن را بحقّ بر تو تلاوت می‌کنیم؛ اگر آنها به این آیات ایمان نیاورند، به کدام سخن بعد از سخن خدا و آیاتش ایمان می‌آورند؟!'),
(4480, 4480, 'وَيْلٌ لِكُلِّ أَفَّاكٍ أَثِيمٍ', 45, 7, 25, 100, 499, 'وای بر هر دروغگوی گنهکار...'),
(4481, 4481, 'يَسْمَعُ آيَاتِ اللَّهِ تُتْلَىٰ عَلَيْهِ ثُمَّ يُصِرُّ مُسْتَكْبِرًا كَأَنْ لَمْ يَسْمَعْهَا ۖ فَبَشِّرْهُ بِعَذَابٍ أَلِيمٍ', 45, 8, 25, 100, 499, 'که پیوسته آیات خدا را می‌شنود که بر او تلاوت می‌شود، امّا از روی تکبّر اصرار بر مخالفت دارد؛ گویی اصلاً آن را هیچ نشنیده است؛ چنین کسی را به عذابی دردناک بشارت ده!'),
(4482, 4482, 'وَإِذَا عَلِمَ مِنْ آيَاتِنَا شَيْئًا اتَّخَذَهَا هُزُوًا ۚ أُولَٰئِكَ لَهُمْ عَذَابٌ مُهِينٌ', 45, 9, 25, 100, 499, 'و هرگاه از بعضی آیات ما آگاه شود، آن را به باد استهزا می‌گیرد؛ برای آنان عذاب خوارکننده‌ای است!'),
(4483, 4483, 'مِنْ وَرَائِهِمْ جَهَنَّمُ ۖ وَلَا يُغْنِي عَنْهُمْ مَا كَسَبُوا شَيْئًا وَلَا مَا اتَّخَذُوا مِنْ دُونِ اللَّهِ أَوْلِيَاءَ ۖ وَلَهُمْ عَذَابٌ عَظِيمٌ', 45, 10, 25, 100, 499, 'و پشت سرشان دوزخ است؛ و هرگز آنچه را به دست آورده‌اند آنها را (از عذاب الهی) رهایی نمی‌بخشد، و نه اولیایی که غیر از خدا برای خود برگزیدند (مایه نجاتشان خواهند بود)؛ و عذاب بزرگی برای آنهاست!'),
(4484, 4484, 'هَٰذَا هُدًى ۖ وَالَّذِينَ كَفَرُوا بِآيَاتِ رَبِّهِمْ لَهُمْ عَذَابٌ مِنْ رِجْزٍ أَلِيمٌ', 45, 11, 25, 100, 499, 'این (قرآن) مایه هدایت است، و کسانی که به آیات پروردگارشان کافر شدند، عذابی سخت و دردناک دارند!'),
(4485, 4485, ' اللَّهُ الَّذِي سَخَّرَ لَكُمُ الْبَحْرَ لِتَجْرِيَ الْفُلْكُ فِيهِ بِأَمْرِهِ وَلِتَبْتَغُوا مِنْ فَضْلِهِ وَلَعَلَّكُمْ تَشْكُرُونَ', 45, 12, 25, 100, 499, 'خداوند همان کسی است که دریا را مسخّر شما کرد تا کشتیها بفرمانش در آن حرکت کنند و بتوانند از فضل او بهره گیرید، و شاید شکر نعمتهایش را بجا آورید!'),
(4486, 4486, 'وَسَخَّرَ لَكُمْ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ جَمِيعًا مِنْهُ ۚ إِنَّ فِي ذَٰلِكَ لَآيَاتٍ لِقَوْمٍ يَتَفَكَّرُونَ', 45, 13, 25, 100, 499, 'او آنچه در آسمانها و آنچه در زمین است همه را از سوی خودش مسخّر شما ساخته؛ در این نشانه‌های (مهمّی) است برای کسانی که اندیشه می‌کنند!'),
(4487, 4487, 'قُلْ لِلَّذِينَ آمَنُوا يَغْفِرُوا لِلَّذِينَ لَا يَرْجُونَ أَيَّامَ اللَّهِ لِيَجْزِيَ قَوْمًا بِمَا كَانُوا يَكْسِبُونَ', 45, 14, 25, 100, 500, 'به مؤمنان بگو: «کسانی را که امید به ایّام اللّه = روز رستاخیز ندارند مورد عفو قرار دهند تا خداوند هر قومی را به اعمالی که انجام می‌دادند جزا دهد»!'),
(4488, 4488, 'مَنْ عَمِلَ صَالِحًا فَلِنَفْسِهِ ۖ وَمَنْ أَسَاءَ فَعَلَيْهَا ۖ ثُمَّ إِلَىٰ رَبِّكُمْ تُرْجَعُونَ', 45, 15, 25, 100, 500, 'هر کس کار شایسته‌ای بجا آورد، برای خود بجا آورده است؛ و کسی که کار بد می‌کند، به زیان خود اوست؛ سپس همه شما به سوی پروردگارتان بازگردانده می‌شوید!'),
(4489, 4489, 'وَلَقَدْ آتَيْنَا بَنِي إِسْرَائِيلَ الْكِتَابَ وَالْحُكْمَ وَالنُّبُوَّةَ وَرَزَقْنَاهُمْ مِنَ الطَّيِّبَاتِ وَفَضَّلْنَاهُمْ عَلَى الْعَالَمِينَ', 45, 16, 25, 100, 500, 'ما بنی اسرائیل را کتاب (آسمانی) و حکومت و نبّوت بخشیدیم و از روزیهای پاکیزه به آنها عطا کردیم و آنان را بر جهانیان (و مردم عصر خویش) برتری بخشیدیم؛'),
(4490, 4490, 'وَآتَيْنَاهُمْ بَيِّنَاتٍ مِنَ الْأَمْرِ ۖ فَمَا اخْتَلَفُوا إِلَّا مِنْ بَعْدِ مَا جَاءَهُمُ الْعِلْمُ بَغْيًا بَيْنَهُمْ ۚ إِنَّ رَبَّكَ يَقْضِي بَيْنَهُمْ يَوْمَ الْقِيَامَةِ فِيمَا كَانُوا فِيهِ يَخْتَلِفُونَ', 45, 17, 25, 100, 500, 'و دلایل روشنی از امر نبّوت و شریعت در اختیارشان قرار دادیم؛ آنها اختلاف نکردند مگر بعد از علم و آگاهی؛ و این اختلاف بخاطر ستم و برتری‌جویی آنان بود؛ امّا پروردگارت روز قیامت در میان آنها در آنچه اختلاف داشتند داوری می‌کند.'),
(4491, 4491, 'ثُمَّ جَعَلْنَاكَ عَلَىٰ شَرِيعَةٍ مِنَ الْأَمْرِ فَاتَّبِعْهَا وَلَا تَتَّبِعْ أَهْوَاءَ الَّذِينَ لَا يَعْلَمُونَ', 45, 18, 25, 100, 500, 'سپس تو را بر شریعت و آیین حقّی قرار دادیم؛ از آن پیروی کن و از هوسهای کسانی که آگاهی ندارند پیروی مکن!'),
(4492, 4492, 'إِنَّهُمْ لَنْ يُغْنُوا عَنْكَ مِنَ اللَّهِ شَيْئًا ۚ وَإِنَّ الظَّالِمِينَ بَعْضُهُمْ أَوْلِيَاءُ بَعْضٍ ۖ وَاللَّهُ وَلِيُّ الْمُتَّقِينَ', 45, 19, 25, 100, 500, 'آنها هرگز نمی‌توانند تو را در برابر خداوند بی‌نیاز کنند (و از عذابش برهانند)؛ و ظالمان یار و یاور یکدیگرند، امّا خداوند یار و یاور پرهیزگاران است!'),
(4493, 4493, 'هَٰذَا بَصَائِرُ لِلنَّاسِ وَهُدًى وَرَحْمَةٌ لِقَوْمٍ يُوقِنُونَ', 45, 20, 25, 100, 500, 'این (قرآن و شریعت آسمانی) وسایل بینایی و مایه هدایت و رحمت است برای مردمی که (به آن) یقین دارند!'),
(4494, 4494, 'أَمْ حَسِبَ الَّذِينَ اجْتَرَحُوا السَّيِّئَاتِ أَنْ نَجْعَلَهُمْ كَالَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ سَوَاءً مَحْيَاهُمْ وَمَمَاتُهُمْ ۚ سَاءَ مَا يَحْكُمُونَ', 45, 21, 25, 100, 500, 'آیا کسانی که مرتکب بدیها و گناهان شدند گمان کردند که ما آنها را همچون کسانی قرارمی‌دهیم که ایمان آورده و اعمال صالح انجام داده‌اند که حیات و مرگشان یکسان باشد؟! چه بد داوری می‌کنند!'),
(4495, 4495, 'وَخَلَقَ اللَّهُ السَّمَاوَاتِ وَالْأَرْضَ بِالْحَقِّ وَلِتُجْزَىٰ كُلُّ نَفْسٍ بِمَا كَسَبَتْ وَهُمْ لَا يُظْلَمُونَ', 45, 22, 25, 100, 500, 'و خداوند آسمانها و زمین را بحقّ آفریده است تا هر کس در برابر اعمالی که انجام داده است جزا داده شود؛ و به آنها ستمی نخواهد شد!'),
(4496, 4496, 'أَفَرَأَيْتَ مَنِ اتَّخَذَ إِلَٰهَهُ هَوَاهُ وَأَضَلَّهُ اللَّهُ عَلَىٰ عِلْمٍ وَخَتَمَ عَلَىٰ سَمْعِهِ وَقَلْبِهِ وَجَعَلَ عَلَىٰ بَصَرِهِ غِشَاوَةً فَمَنْ يَهْدِيهِ مِنْ بَعْدِ اللَّهِ ۚ أَفَلَا تَذَكَّرُونَ', 45, 23, 25, 100, 501, 'آیا دیدی کسی را که معبود خود را هوای نفس خویش قرار داده و خداوند او را با آگاهی (بر اینکه شایسته هدایت نیست) گمراه ساخته و بر گوش و قلبش مُهر زده و بر چشمش پرده‌ای افکنده است؟! با این حال چه کسی می‌تواند غیر از خدا او را هدایت کند؟! آیا متذکّر نمی‌شوید؟!'),
(4497, 4497, 'وَقَالُوا مَا هِيَ إِلَّا حَيَاتُنَا الدُّنْيَا نَمُوتُ وَنَحْيَا وَمَا يُهْلِكُنَا إِلَّا الدَّهْرُ ۚ وَمَا لَهُمْ بِذَٰلِكَ مِنْ عِلْمٍ ۖ إِنْ هُمْ إِلَّا يَظُنُّونَ', 45, 24, 25, 100, 501, 'آنها گفتند: «چیزی جز همین زندگی دنیای ما در کار نیست؛ گروهی از ما می‌میرند و گروهی جای آنها را می‌گیرند؛ و جز طبیعت و روزگار ما را هلاک نمی‌کند!» آنان به این سخن که می‌گویند علمی ندارند، بلکه تنها حدس می‌زنند (و گمانی بی‌پایه دارند)!'),
(4498, 4498, 'وَإِذَا تُتْلَىٰ عَلَيْهِمْ آيَاتُنَا بَيِّنَاتٍ مَا كَانَ حُجَّتَهُمْ إِلَّا أَنْ قَالُوا ائْتُوا بِآبَائِنَا إِنْ كُنْتُمْ صَادِقِينَ', 45, 25, 25, 100, 501, 'و هنگامی که آیات روشن ما بر آنها خوانده می‌شود، دلیلی در برابر آن ندارند جز اینکه می‌گویند: «اگر راست می‌گویید پدران ما را (زنده کنید) و بیاورید (تا گواهی دهند)!»'),
(4499, 4499, 'قُلِ اللَّهُ يُحْيِيكُمْ ثُمَّ يُمِيتُكُمْ ثُمَّ يَجْمَعُكُمْ إِلَىٰ يَوْمِ الْقِيَامَةِ لَا رَيْبَ فِيهِ وَلَٰكِنَّ أَكْثَرَ النَّاسِ لَا يَعْلَمُونَ', 45, 26, 25, 100, 501, 'بگو: «خداوند شما را زنده می‌کند، سپس می‌میراند، بار دیگر در روز قیامت که در آن تردیدی نیست گردآوری می‌کند؛ ولی بیشتر مردم نمی‌دانند.»'),
(4500, 4500, 'وَلِلَّهِ مُلْكُ السَّمَاوَاتِ وَالْأَرْضِ ۚ وَيَوْمَ تَقُومُ السَّاعَةُ يَوْمَئِذٍ يَخْسَرُ الْمُبْطِلُونَ', 45, 27, 25, 100, 501, 'مالکیّت و حاکمیّت آسمانها و زمین برای خداست؛ و آن روز که قیامت برپا شود اهل باطل زیان می‌بینند!'),
(4501, 4501, 'وَتَرَىٰ كُلَّ أُمَّةٍ جَاثِيَةً ۚ كُلُّ أُمَّةٍ تُدْعَىٰ إِلَىٰ كِتَابِهَا الْيَوْمَ تُجْزَوْنَ مَا كُنْتُمْ تَعْمَلُونَ', 45, 28, 25, 100, 501, 'در آن روز هر امّتی را می‌بینی (که از شدّت ترس و وحشت) بر زانو نشسته؛ هر امّتی بسوی کتابش خوانده می‌شود، و (به آنها می‌گویند:) امروز جزای آنچه را انجام می‌دادید به شما می‌دهند!'),
(4502, 4502, 'هَٰذَا كِتَابُنَا يَنْطِقُ عَلَيْكُمْ بِالْحَقِّ ۚ إِنَّا كُنَّا نَسْتَنْسِخُ مَا كُنْتُمْ تَعْمَلُونَ', 45, 29, 25, 100, 501, 'این کتاب ما است که بحق با شما سخن می‌گوید (و اعمال شما را بازگو می‌کند)؛ ما آنچه را انجام می‌دادید می‌نوشتیم!'),
(4503, 4503, 'فَأَمَّا الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ فَيُدْخِلُهُمْ رَبُّهُمْ فِي رَحْمَتِهِ ۚ ذَٰلِكَ هُوَ الْفَوْزُ الْمُبِينُ', 45, 30, 25, 100, 501, 'امّا کسانی که ایمان آوردند و اعمال صالح انجام دادند، پروردگارشان آنها را در رحمت خود وارد می‌کند؛ این همان پیروزی بزرگ است!'),
(4504, 4504, 'وَأَمَّا الَّذِينَ كَفَرُوا أَفَلَمْ تَكُنْ آيَاتِي تُتْلَىٰ عَلَيْكُمْ فَاسْتَكْبَرْتُمْ وَكُنْتُمْ قَوْمًا مُجْرِمِينَ', 45, 31, 25, 100, 501, 'امّا کسانی که کافر شدند (به آنها گفته می‌شود:) مگر آیات من بر شما خوانده نمی‌شد و شما استکبار کردید و قوم مجرمی بودید؟!'),
(4505, 4505, 'وَإِذَا قِيلَ إِنَّ وَعْدَ اللَّهِ حَقٌّ وَالسَّاعَةُ لَا رَيْبَ فِيهَا قُلْتُمْ مَا نَدْرِي مَا السَّاعَةُ إِنْ نَظُنُّ إِلَّا ظَنًّا وَمَا نَحْنُ بِمُسْتَيْقِنِينَ', 45, 32, 25, 100, 501, 'و هنگامی که گفته می‌شد: «وعده خداوند حقّ است، و در قیامت هیچ شکّی نیست»، شما می‌گفتید: «ما نمی‌دانیم قیامت چیست؟ ما تنها گمانی در این باره داریم، و به هیچ‌وجه یقین نداریم!»');
INSERT INTO `Quran` (`rowid`, `id`, `Arabic`, `Chapter`, `Verse`, `Juz`, `Hizb`, `Page`, `tr`) VALUES
(4506, 4506, 'وَبَدَا لَهُمْ سَيِّئَاتُ مَا عَمِلُوا وَحَاقَ بِهِمْ مَا كَانُوا بِهِ يَسْتَهْزِئُونَ', 45, 33, 25, 100, 502, 'و بدیهای اعمالشان برای آنان آشکار می‌شود، و سرانجام آنچه را استهزا می‌کردند آنها را فرامی‌گیرد!'),
(4507, 4507, 'وَقِيلَ الْيَوْمَ نَنْسَاكُمْ كَمَا نَسِيتُمْ لِقَاءَ يَوْمِكُمْ هَٰذَا وَمَأْوَاكُمُ النَّارُ وَمَا لَكُمْ مِنْ نَاصِرِينَ', 45, 34, 25, 100, 502, 'و به آنها گفته می‌شود: «امروز شما را فراموش می‌کنیم همان‌گونه که شما دیدار امروزتان را فراموش کردید؛ و جایگاه شما دوزخ است و هیچ یاوری ندارید!'),
(4508, 4508, 'ذَٰلِكُمْ بِأَنَّكُمُ اتَّخَذْتُمْ آيَاتِ اللَّهِ هُزُوًا وَغَرَّتْكُمُ الْحَيَاةُ الدُّنْيَا ۚ فَالْيَوْمَ لَا يُخْرَجُونَ مِنْهَا وَلَا هُمْ يُسْتَعْتَبُونَ', 45, 35, 25, 100, 502, 'این بخاطر آن است که شما آیات خدا را به مسخره گرفتید و زندگی دنیا شما را فریب داد! «امروز نه آنان را از دوزخ بیرون می‌آورند، و نه هیچ‌گونه عذری از آنها پذیرفته می‌شود!'),
(4509, 4509, 'فَلِلَّهِ الْحَمْدُ رَبِّ السَّمَاوَاتِ وَرَبِّ الْأَرْضِ رَبِّ الْعَالَمِينَ', 45, 36, 25, 100, 502, 'پس حمد و ستایش مخصوص خداست، پروردگار آسمانها و پروردگار زمین و پروردگار همه جهانیان!'),
(4510, 4510, 'وَلَهُ الْكِبْرِيَاءُ فِي السَّمَاوَاتِ وَالْأَرْضِ ۖ وَهُوَ الْعَزِيزُ الْحَكِيمُ', 45, 37, 25, 100, 502, 'و برای اوست کبریا و عظمت در آسمانها و زمین، و اوست عزیز و حکیم!'),
(4511, 4511, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ حم', 46, 1, 26, 101, 502, 'حم‌'),
(4512, 4512, 'تَنْزِيلُ الْكِتَابِ مِنَ اللَّهِ الْعَزِيزِ الْحَكِيمِ', 46, 2, 26, 101, 502, 'این کتاب از سوی خداوند عزیز و حکیم نازل شده است!'),
(4513, 4513, 'مَا خَلَقْنَا السَّمَاوَاتِ وَالْأَرْضَ وَمَا بَيْنَهُمَا إِلَّا بِالْحَقِّ وَأَجَلٍ مُسَمًّى ۚ وَالَّذِينَ كَفَرُوا عَمَّا أُنْذِرُوا مُعْرِضُونَ', 46, 3, 26, 101, 502, 'ما آسمانها و زمین و آنچه را در میان این دو است جز بحق و برای سرآمد معیّنی نیافریدیم؛ امّا کافران از آنچه انذار می‌شوند روی گردانند!'),
(4514, 4514, 'قُلْ أَرَأَيْتُمْ مَا تَدْعُونَ مِنْ دُونِ اللَّهِ أَرُونِي مَاذَا خَلَقُوا مِنَ الْأَرْضِ أَمْ لَهُمْ شِرْكٌ فِي السَّمَاوَاتِ ۖ ائْتُونِي بِكِتَابٍ مِنْ قَبْلِ هَٰذَا أَوْ أَثَارَةٍ مِنْ عِلْمٍ إِنْ كُنْتُمْ صَادِقِينَ', 46, 4, 26, 101, 502, 'به آنان بگو: «این معبودهایی را که غیر از خدا پرستش می‌کنید به من نشان دهید چه چیزی از زمین را آفریده‌اند، یا شرکتی در آفرینش آسمانها دارند؟ کتابی آسمانی پیش از این، یا اثر علمی از گذشتگان برای من بیاورید (که دلیل صدق گفتار شما باشد) اگر راست می‌گویید!»'),
(4515, 4515, 'وَمَنْ أَضَلُّ مِمَّنْ يَدْعُو مِنْ دُونِ اللَّهِ مَنْ لَا يَسْتَجِيبُ لَهُ إِلَىٰ يَوْمِ الْقِيَامَةِ وَهُمْ عَنْ دُعَائِهِمْ غَافِلُونَ', 46, 5, 26, 101, 502, 'چه کسی گمراهتر است از آن کس که معبودی غیر خدا را می‌خواند که تا قیامت هم به او پاسخ نمی‌گوید و از خواندن آنها (کاملا) ً بی‌خبر است؟! (و صدای آنها را هیچ نمی‌شنود!)'),
(4516, 4516, 'وَإِذَا حُشِرَ النَّاسُ كَانُوا لَهُمْ أَعْدَاءً وَكَانُوا بِعِبَادَتِهِمْ كَافِرِينَ', 46, 6, 26, 101, 503, 'و هنگامی که مردم محشور می‌شوند، معبودهای آنها دشمنانشان خواهند بود؛ حتّی عبادت آنها را انکار می‌کنند!'),
(4517, 4517, 'وَإِذَا تُتْلَىٰ عَلَيْهِمْ آيَاتُنَا بَيِّنَاتٍ قَالَ الَّذِينَ كَفَرُوا لِلْحَقِّ لَمَّا جَاءَهُمْ هَٰذَا سِحْرٌ مُبِينٌ', 46, 7, 26, 101, 503, 'هنگامی که آیات روشن ما بر آنان خوانده می‌شود، کافران در برابر حقّی که برای آنها آمده می‌گویند: «این سحری آشکار است!»'),
(4518, 4518, 'أَمْ يَقُولُونَ افْتَرَاهُ ۖ قُلْ إِنِ افْتَرَيْتُهُ فَلَا تَمْلِكُونَ لِي مِنَ اللَّهِ شَيْئًا ۖ هُوَ أَعْلَمُ بِمَا تُفِيضُونَ فِيهِ ۖ كَفَىٰ بِهِ شَهِيدًا بَيْنِي وَبَيْنَكُمْ ۖ وَهُوَ الْغَفُورُ الرَّحِيمُ', 46, 8, 26, 101, 503, 'بلکه می‌گویند: «این آیات را بر خدا افترا بسته است!» بگو: «اگر من آن را بدروغ به خدا نسبت داده باشم (لازم است مرا رسوا کند و) شما نمی‌توانید در برابر خداوند از من دفاع کنید! او کارهایی را که شما در آن وارد می‌شوید بهتر می‌داند؛ همین بس که خداوند گواه میان من و شما باشد؛ و او آمرزنده و مهربان است!»'),
(4519, 4519, 'قُلْ مَا كُنْتُ بِدْعًا مِنَ الرُّسُلِ وَمَا أَدْرِي مَا يُفْعَلُ بِي وَلَا بِكُمْ ۖ إِنْ أَتَّبِعُ إِلَّا مَا يُوحَىٰ إِلَيَّ وَمَا أَنَا إِلَّا نَذِيرٌ مُبِينٌ', 46, 9, 26, 101, 503, 'بگو: «من پیامبر نوظهوری نیستم؛ و نمی‌دانم با من و شما چه خواهد شد؛ من تنها از آنچه بر من وحی می‌شود پیروی می‌کنم، و جز بیم‌دهنده آشکاری نیستم!»'),
(4520, 4520, 'قُلْ أَرَأَيْتُمْ إِنْ كَانَ مِنْ عِنْدِ اللَّهِ وَكَفَرْتُمْ بِهِ وَشَهِدَ شَاهِدٌ مِنْ بَنِي إِسْرَائِيلَ عَلَىٰ مِثْلِهِ فَآمَنَ وَاسْتَكْبَرْتُمْ ۖ إِنَّ اللَّهَ لَا يَهْدِي الْقَوْمَ الظَّالِمِينَ', 46, 10, 26, 101, 503, 'بگو: «به من خبر دهید اگر این قرآن از سوی خدا باشد و شما به آن کافر شوید، در حالی که شاهدی از بنی اسرائیل بر آن شهادت دهد، و او ایمان آورد و شما استکبار کنید (چه کسی گمراهتر از شما خواهد بود)؟! خداوند گروه ستمگر را هدایت نمی‌کند!»'),
(4521, 4521, 'وَقَالَ الَّذِينَ كَفَرُوا لِلَّذِينَ آمَنُوا لَوْ كَانَ خَيْرًا مَا سَبَقُونَا إِلَيْهِ ۚ وَإِذْ لَمْ يَهْتَدُوا بِهِ فَسَيَقُولُونَ هَٰذَا إِفْكٌ قَدِيمٌ', 46, 11, 26, 101, 503, 'کافران درباره مؤمنان چنین گفتند: «اگر (اسلام) چیز خوبی بود، هرگز آنها (در پذیرش آن) بر ما پیشی نمی‌گرفتند!» و چون خودشان بوسیله آن هدایت نشدند می‌گویند: «این یک دروغ قدیمی است!»'),
(4522, 4522, 'وَمِنْ قَبْلِهِ كِتَابُ مُوسَىٰ إِمَامًا وَرَحْمَةً ۚ وَهَٰذَا كِتَابٌ مُصَدِّقٌ لِسَانًا عَرَبِيًّا لِيُنْذِرَ الَّذِينَ ظَلَمُوا وَبُشْرَىٰ لِلْمُحْسِنِينَ', 46, 12, 26, 101, 503, 'و پیش از آن، کتاب موسی که پیشوا و رحمت بود (نشانه‌های آن را بیان کرده)، و این کتاب هماهنگ با نشانه‌های تورات است در حالی که به زبان عربی و فصیح و گویاست، تا ظالمان را بیم دهد و برای نیکوکاران بشارتی باشد!'),
(4523, 4523, 'إِنَّ الَّذِينَ قَالُوا رَبُّنَا اللَّهُ ثُمَّ اسْتَقَامُوا فَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَ', 46, 13, 26, 101, 503, 'کسانی که گفتند: «پروردگار ما اللّه است»، سپس استقامت کردند، نه ترسی برای آنان است و نه اندوهگین می‌شوند.'),
(4524, 4524, 'أُولَٰئِكَ أَصْحَابُ الْجَنَّةِ خَالِدِينَ فِيهَا جَزَاءً بِمَا كَانُوا يَعْمَلُونَ', 46, 14, 26, 101, 503, 'آنها اهل بهشتند و جاودانه در آن می‌مانند؛ این پاداش اعمالی است که انجام می‌دادند.'),
(4525, 4525, 'وَوَصَّيْنَا الْإِنْسَانَ بِوَالِدَيْهِ إِحْسَانًا ۖ حَمَلَتْهُ أُمُّهُ كُرْهًا وَوَضَعَتْهُ كُرْهًا ۖ وَحَمْلُهُ وَفِصَالُهُ ثَلَاثُونَ شَهْرًا ۚ حَتَّىٰ إِذَا بَلَغَ أَشُدَّهُ وَبَلَغَ أَرْبَعِينَ سَنَةً قَالَ رَبِّ أَوْزِعْنِي أَنْ أَشْكُرَ نِعْمَتَكَ الَّتِي أَنْعَمْتَ عَلَيَّ وَعَلَىٰ وَالِدَيَّ وَأَنْ أَعْمَلَ صَالِحًا تَرْضَاهُ وَأَصْلِحْ لِي فِي ذُرِّيَّتِي ۖ إِنِّي تُبْتُ إِلَيْكَ وَإِنِّي مِنَ الْمُسْلِمِينَ', 46, 15, 26, 101, 504, 'ما به انسان توصیه کردیم که به پدر و مادرش نیکی کند، مادرش او را با ناراحتی حمل می‌کند و با ناراحتی بر زمین می‌گذارد؛ و دوران حمل و از شیر بازگرفتنش سی ماه است؛ تا زمانی که به کمال قدرت و رشد برسد و به چهل سالگی بالغ گردد می‌گوید: «پروردگارا! مرا توفیق ده تا شکر نعمتی را که به من و پدر و مادرم دادی بجا آورم و کار شایسته‌ای انجام دهم که از آن خشنود باشی، و فرزندان مرا صالح گردان؛ من به سوی تو بازمی‌گردم و توبه می‌کنم، و من از مسلمانانم!»'),
(4526, 4526, 'أُولَٰئِكَ الَّذِينَ نَتَقَبَّلُ عَنْهُمْ أَحْسَنَ مَا عَمِلُوا وَنَتَجَاوَزُ عَنْ سَيِّئَاتِهِمْ فِي أَصْحَابِ الْجَنَّةِ ۖ وَعْدَ الصِّدْقِ الَّذِي كَانُوا يُوعَدُونَ', 46, 16, 26, 101, 504, 'آنها کسانی هستند که ما بهترین اعمالشان را قبول می‌کنیم و از گناهانشان می‌گذریم و در میان بهشتیان جای دارند؛ این وعده راستی است که وعده داده می‌شدند.'),
(4527, 4527, 'وَالَّذِي قَالَ لِوَالِدَيْهِ أُفٍّ لَكُمَا أَتَعِدَانِنِي أَنْ أُخْرَجَ وَقَدْ خَلَتِ الْقُرُونُ مِنْ قَبْلِي وَهُمَا يَسْتَغِيثَانِ اللَّهَ وَيْلَكَ آمِنْ إِنَّ وَعْدَ اللَّهِ حَقٌّ فَيَقُولُ مَا هَٰذَا إِلَّا أَسَاطِيرُ الْأَوَّلِينَ', 46, 17, 26, 101, 504, 'و کسی که به پدر و مادرش می‌گوید: «اف بر شما! آیا به من وعده می‌دهید که من روز قیامت مبعوث می‌شوم؟! در حالی که پیش از من اقوام زیادی بودند (و هرگز مبعوث نشدند)! و آن دو پیوسته فریاد می‌کشند و خدا را به یاری می‌طلبند که: وای بر تو، ایمان بیاور که وعده خدا حق است امّا او پیوسته می‌گوید:» اینها چیزی جز افسانه‌های پیشینیان نیست!'),
(4528, 4528, 'أُولَٰئِكَ الَّذِينَ حَقَّ عَلَيْهِمُ الْقَوْلُ فِي أُمَمٍ قَدْ خَلَتْ مِنْ قَبْلِهِمْ مِنَ الْجِنِّ وَالْإِنْسِ ۖ إِنَّهُمْ كَانُوا خَاسِرِينَ', 46, 18, 26, 101, 504, 'آنها کسانی هستند که فرمان عذاب درباره آنان همراه اقوام (کافری) که پیش از آنان از جنّ و انس بودند مسلّم شده، چرا که همگی زیانکار بودند!'),
(4529, 4529, 'وَلِكُلٍّ دَرَجَاتٌ مِمَّا عَمِلُوا ۖ وَلِيُوَفِّيَهُمْ أَعْمَالَهُمْ وَهُمْ لَا يُظْلَمُونَ', 46, 19, 26, 101, 504, 'و برای هر کدام از آنها درجاتی است بر طبق اعمالی که انجام داده‌اند، تا خداوند کارهایشان را بی‌کم و کاست به آنان تحویل دهد؛ و به آنها هیچ ستمی نخواهد شد!'),
(4530, 4530, 'وَيَوْمَ يُعْرَضُ الَّذِينَ كَفَرُوا عَلَى النَّارِ أَذْهَبْتُمْ طَيِّبَاتِكُمْ فِي حَيَاتِكُمُ الدُّنْيَا وَاسْتَمْتَعْتُمْ بِهَا فَالْيَوْمَ تُجْزَوْنَ عَذَابَ الْهُونِ بِمَا كُنْتُمْ تَسْتَكْبِرُونَ فِي الْأَرْضِ بِغَيْرِ الْحَقِّ وَبِمَا كُنْتُمْ تَفْسُقُونَ', 46, 20, 26, 101, 504, 'آن روز که کافران را بر آتش عرضه می‌کنند (به آنها گفته می‌شود:) از طیّبات و لذائذ در زندگی دنیا خود استفاده کردید و از آن بهره گرفتید؛ اما امروز عذاب ذلّت‌بار بخاطر استکباری که در زمین بناحق کردید و بخاطر گناهانی که انجام می‌دادید؛ جزای شما خواهد بود!'),
(4531, 4531, ' وَاذْكُرْ أَخَا عَادٍ إِذْ أَنْذَرَ قَوْمَهُ بِالْأَحْقَافِ وَقَدْ خَلَتِ النُّذُرُ مِنْ بَيْنِ يَدَيْهِ وَمِنْ خَلْفِهِ أَلَّا تَعْبُدُوا إِلَّا اللَّهَ إِنِّي أَخَافُ عَلَيْكُمْ عَذَابَ يَوْمٍ عَظِيمٍ', 46, 21, 26, 101, 505, '(سرگذشت هود) برادر قوم عاد را یاد کن، آن زمان که قومش را در سرزمین «احقاف» بیم داد در حالی که پیامبران زیادی قبل از او در گذشته‌های دور و نزدیک آمده بودند که: جز خدای یگانه را نپرستید! (و گفت:) من بر شما از عذاب روزی بزرگ می‌ترسم!'),
(4532, 4532, 'قَالُوا أَجِئْتَنَا لِتَأْفِكَنَا عَنْ آلِهَتِنَا فَأْتِنَا بِمَا تَعِدُنَا إِنْ كُنْتَ مِنَ الصَّادِقِينَ', 46, 22, 26, 101, 505, 'آنها گفتند: «آیا آمده‌ای که ما را (با دروغهایت) از معبودانمان بازگردانی؟! اگر راست می‌گویی عذابی را که به ما وعده می‌دهی بیاور!»'),
(4533, 4533, 'قَالَ إِنَّمَا الْعِلْمُ عِنْدَ اللَّهِ وَأُبَلِّغُكُمْ مَا أُرْسِلْتُ بِهِ وَلَٰكِنِّي أَرَاكُمْ قَوْمًا تَجْهَلُونَ', 46, 23, 26, 101, 505, 'گفت: «علم (آن) تنها نزد خداست (و او می‌داند چه زمانی شما را مجازات کند)؛ من آنچه را به آن فرستاده شده‌ام به شما می‌رسانم، (وظیفه من همین است!) ولی شما را قومی می‌بینیم که پیوسته در نادانی هستید!»'),
(4534, 4534, 'فَلَمَّا رَأَوْهُ عَارِضًا مُسْتَقْبِلَ أَوْدِيَتِهِمْ قَالُوا هَٰذَا عَارِضٌ مُمْطِرُنَا ۚ بَلْ هُوَ مَا اسْتَعْجَلْتُمْ بِهِ ۖ رِيحٌ فِيهَا عَذَابٌ أَلِيمٌ', 46, 24, 26, 101, 505, 'هنگامی که آن (عذاب الهی) را بصورت ابر گسترده‌ای دیدند که بسوی درّه‌ها و آبگیرهای آنان در حرکت است (خوشحال شدند) گفتند: «این ابری است که بر ما می‌بارد!» (ولی به آنها گفته شد:) این همان چیزی است که برای آمدنش شتاب می‌کردید، تندبادی است (وحشتناک) که عذاب دردناکی در آن است!'),
(4535, 4535, 'تُدَمِّرُ كُلَّ شَيْءٍ بِأَمْرِ رَبِّهَا فَأَصْبَحُوا لَا يُرَىٰ إِلَّا مَسَاكِنُهُمْ ۚ كَذَٰلِكَ نَجْزِي الْقَوْمَ الْمُجْرِمِينَ', 46, 25, 26, 101, 505, 'همه چیز را بفرمان پروردگارش در هم میکوبد و نابود میکند (آری) آنها صبح کردند در حالی که چیزی جز خانه‌هایشان به چشم نمی‌خورد؛ ما این‌گونه گروه مجرمان را کیفر می‌دهیم!'),
(4536, 4536, 'وَلَقَدْ مَكَّنَّاهُمْ فِيمَا إِنْ مَكَّنَّاكُمْ فِيهِ وَجَعَلْنَا لَهُمْ سَمْعًا وَأَبْصَارًا وَأَفْئِدَةً فَمَا أَغْنَىٰ عَنْهُمْ سَمْعُهُمْ وَلَا أَبْصَارُهُمْ وَلَا أَفْئِدَتُهُمْ مِنْ شَيْءٍ إِذْ كَانُوا يَجْحَدُونَ بِآيَاتِ اللَّهِ وَحَاقَ بِهِمْ مَا كَانُوا بِهِ يَسْتَهْزِئُونَ', 46, 26, 26, 101, 505, 'ما به آنها = قوم عاد قدرتی دادیم که به شما ندادیم، و برای آنان گوش و چشم و دل قرار دادیم؛ (امّا به هنگام نزول عذاب) نه گوشها و چشمها و نه عقلهایشان برای آنان هیچ سودی نداشت، چرا که آیات خدا را انکار می‌کردند؛ و سرانجام آنچه را استهزا می‌کردند بر آنها وارد شد!'),
(4537, 4537, 'وَلَقَدْ أَهْلَكْنَا مَا حَوْلَكُمْ مِنَ الْقُرَىٰ وَصَرَّفْنَا الْآيَاتِ لَعَلَّهُمْ يَرْجِعُونَ', 46, 27, 26, 101, 505, 'ما آبادیهایی را که پیرامون شما بودند نابود ساختیم، و آیات خود را بصورتهای گوناگون (برای مردم آنها) بیان کردیم شاید بازگردند!'),
(4538, 4538, 'فَلَوْلَا نَصَرَهُمُ الَّذِينَ اتَّخَذُوا مِنْ دُونِ اللَّهِ قُرْبَانًا آلِهَةً ۖ بَلْ ضَلُّوا عَنْهُمْ ۚ وَذَٰلِكَ إِفْكُهُمْ وَمَا كَانُوا يَفْتَرُونَ', 46, 28, 26, 101, 505, 'پس چرا معبودانی را که غیر از خدا برگزیدند -به گمان اینکه به خدا نزدیکشان سازد- آنها را یاری نکردند؟! بلکه از میانشان گم شدند! این بود نتیجه دروغ آنها و آنچه افترا می‌بستند!'),
(4539, 4539, 'وَإِذْ صَرَفْنَا إِلَيْكَ نَفَرًا مِنَ الْجِنِّ يَسْتَمِعُونَ الْقُرْآنَ فَلَمَّا حَضَرُوهُ قَالُوا أَنْصِتُوا ۖ فَلَمَّا قُضِيَ وَلَّوْا إِلَىٰ قَوْمِهِمْ مُنْذِرِينَ', 46, 29, 26, 101, 506, '(به یاد آور) هنگامی که گروهی از جنّ را به سوی تو متوجّه ساختیم که قرآن را بشنوند؛ وقتی حضور یافتند به یکدیگر گفتند: «خاموش باشید و بشنوید!» و هنگامی که پایان گرفت، به سوی قوم خود بازگشتند و آنها را بیم دادند!'),
(4540, 4540, 'قَالُوا يَا قَوْمَنَا إِنَّا سَمِعْنَا كِتَابًا أُنْزِلَ مِنْ بَعْدِ مُوسَىٰ مُصَدِّقًا لِمَا بَيْنَ يَدَيْهِ يَهْدِي إِلَى الْحَقِّ وَإِلَىٰ طَرِيقٍ مُسْتَقِيمٍ', 46, 30, 26, 101, 506, 'گفتند: «ای قوم ما! ما کتابی را شنیدیم که بعد از موسی نازل شده، هماهنگ با نشانه‌های کتابهای پیش از آن، که به سوی حقّ و راه راست هدایت می‌کند.'),
(4541, 4541, 'يَا قَوْمَنَا أَجِيبُوا دَاعِيَ اللَّهِ وَآمِنُوا بِهِ يَغْفِرْ لَكُمْ مِنْ ذُنُوبِكُمْ وَيُجِرْكُمْ مِنْ عَذَابٍ أَلِيمٍ', 46, 31, 26, 101, 506, 'ای قوم ما! دعوت کننده الهی را اجابت کنید و به او ایمان آورید تا گناهانتان را ببخشد و شما را از عذابی دردناک پناه دهد!'),
(4542, 4542, 'وَمَنْ لَا يُجِبْ دَاعِيَ اللَّهِ فَلَيْسَ بِمُعْجِزٍ فِي الْأَرْضِ وَلَيْسَ لَهُ مِنْ دُونِهِ أَوْلِيَاءُ ۚ أُولَٰئِكَ فِي ضَلَالٍ مُبِينٍ', 46, 32, 26, 101, 506, 'و هر کس به دعوت کننده الهی پاسخ نگوید، هرگز نمی‌تواند از چنگال عذاب الهی در زمین فرار کند، و غیر از خدا یار و یاوری برای او نیست؛ چنین کسانی در گمراهی آشکارند!»'),
(4543, 4543, 'أَوَلَمْ يَرَوْا أَنَّ اللَّهَ الَّذِي خَلَقَ السَّمَاوَاتِ وَالْأَرْضَ وَلَمْ يَعْيَ بِخَلْقِهِنَّ بِقَادِرٍ عَلَىٰ أَنْ يُحْيِيَ الْمَوْتَىٰ ۚ بَلَىٰ إِنَّهُ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ', 46, 33, 26, 101, 506, 'آیا آنها نمی‌دانند خداوندی که آسمانها و زمین را آفریده و از آفرینش آنها ناتوان نشده است، می‌تواند مُردگان را زنده کند؟! آری او بر هر چیز تواناست!'),
(4544, 4544, 'وَيَوْمَ يُعْرَضُ الَّذِينَ كَفَرُوا عَلَى النَّارِ أَلَيْسَ هَٰذَا بِالْحَقِّ ۖ قَالُوا بَلَىٰ وَرَبِّنَا ۚ قَالَ فَذُوقُوا الْعَذَابَ بِمَا كُنْتُمْ تَكْفُرُونَ', 46, 34, 26, 101, 506, 'روزی را به یاد آور که کافران را بر آتش عرضه می‌دارند (و به آنها گفته می‌شود:) آیا این حقّ نیست؟! می‌گویند: «آری، به پروردگارمان سوگند (که حقّ است)!» (در این هنگام خداوند) می‌گوید: «پس عذاب را بخاطر کفرتان بچشید!»'),
(4545, 4545, 'فَاصْبِرْ كَمَا صَبَرَ أُولُو الْعَزْمِ مِنَ الرُّسُلِ وَلَا تَسْتَعْجِلْ لَهُمْ ۚ كَأَنَّهُمْ يَوْمَ يَرَوْنَ مَا يُوعَدُونَ لَمْ يَلْبَثُوا إِلَّا سَاعَةً مِنْ نَهَارٍ ۚ بَلَاغٌ ۚ فَهَلْ يُهْلَكُ إِلَّا الْقَوْمُ الْفَاسِقُونَ', 46, 35, 26, 101, 506, 'پس صبر کن آن‌گونه که پیامبران «اُولو العزم» صبر کردند، و برای (عذاب) آنان شتاب مکن! هنگامی که وعده‌هایی را که به آنها داده می‌شود ببینند، احساس می‌کنند که گویی فقط ساعتی از یک روز (در دنیا) توقّف داشتند؛ این ابلاغی است برای همگان؛ آیا جز قوم فاسق هلاک می‌شوند؟!'),
(4546, 4546, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ الَّذِينَ كَفَرُوا وَصَدُّوا عَنْ سَبِيلِ اللَّهِ أَضَلَّ أَعْمَالَهُمْ', 47, 1, 26, 101, 507, 'کسانی که کافر شدند و (مردم را) از راه خدا بازداشتند، (خداوند) اعمالشان را نابود می‌کند!'),
(4547, 4547, 'وَالَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ وَآمَنُوا بِمَا نُزِّلَ عَلَىٰ مُحَمَّدٍ وَهُوَ الْحَقُّ مِنْ رَبِّهِمْ ۙ كَفَّرَ عَنْهُمْ سَيِّئَاتِهِمْ وَأَصْلَحَ بَالَهُمْ', 47, 2, 26, 101, 507, 'و کسانی که ایمان آوردند و کارهای شایسته انجام دادند و به آنچه بر محمد (ص) نازل شده -و همه حقّ است و از سوی پروردگارشان- نیز ایمان آوردند، خداوند گناهانشان را می‌بخشد و کارشان را اصلاح می‌کند!'),
(4548, 4548, 'ذَٰلِكَ بِأَنَّ الَّذِينَ كَفَرُوا اتَّبَعُوا الْبَاطِلَ وَأَنَّ الَّذِينَ آمَنُوا اتَّبَعُوا الْحَقَّ مِنْ رَبِّهِمْ ۚ كَذَٰلِكَ يَضْرِبُ اللَّهُ لِلنَّاسِ أَمْثَالَهُمْ', 47, 3, 26, 101, 507, 'این بخاطر آن است که کافران از باطل پیروی کردند، و مؤمنان از حقّی که از سوی پروردگارشان بود تبعیّت نمودند؛ این‌گونه خداوند برای مردم مثَلهای (زندگی) آنان را بیان می‌کند!'),
(4549, 4549, 'فَإِذَا لَقِيتُمُ الَّذِينَ كَفَرُوا فَضَرْبَ الرِّقَابِ حَتَّىٰ إِذَا أَثْخَنْتُمُوهُمْ فَشُدُّوا الْوَثَاقَ فَإِمَّا مَنًّا بَعْدُ وَإِمَّا فِدَاءً حَتَّىٰ تَضَعَ الْحَرْبُ أَوْزَارَهَا ۚ ذَٰلِكَ وَلَوْ يَشَاءُ اللَّهُ لَانْتَصَرَ مِنْهُمْ وَلَٰكِنْ لِيَبْلُوَ بَعْضَكُمْ بِبَعْضٍ ۗ وَالَّذِينَ قُتِلُوا فِي سَبِيلِ اللَّهِ فَلَنْ يُضِلَّ أَعْمَالَهُمْ', 47, 4, 26, 101, 507, 'و هنگامی که با کافران (جنایت‌پیشه) در میدان جنگ روبه‌رو شدید گردنهایشان را بزنید، (و این کار را همچنان ادامه دهید) تا به اندازه کافی دشمن را در هم بکوبید؛ در این هنگام اسیران را محکم ببندید؛ سپس یا بر آنان منّت گذارید (و آزادشان کنید) یا در برابر آزادی از آنان فدیه = غرامت‌ بگیرید؛ (و این وضع باید همچنان ادامه یابد) تا جنگ بار سنگین خود را بر زمین نهد، (آری) برنامه این است! و اگر خدا می‌خواست خودش آنها را مجازات می‌کرد، اما می‌خواهد بعضی از شما را با بعضی دیگر بیازماید؛ و کسانی که در راه خدا کشته شدند، خداوند هرگز اعمالشان را از بین نمی‌برد!'),
(4550, 4550, 'سَيَهْدِيهِمْ وَيُصْلِحُ بَالَهُمْ', 47, 5, 26, 101, 507, 'بزودی آنان را هدایت نموده و کارشان را اصلاح می‌کند؛'),
(4551, 4551, 'وَيُدْخِلُهُمُ الْجَنَّةَ عَرَّفَهَا لَهُمْ', 47, 6, 26, 101, 507, 'و آنها را در بهشت (جاویدانش) که اوصاف آن را برای آنان بازگو کرده وارد می‌کند.'),
(4552, 4552, 'يَا أَيُّهَا الَّذِينَ آمَنُوا إِنْ تَنْصُرُوا اللَّهَ يَنْصُرْكُمْ وَيُثَبِّتْ أَقْدَامَكُمْ', 47, 7, 26, 101, 507, 'ای کسانی که ایمان آورده‌اید! اگر (آیین) خدا را یاری کنید، شما را یاری می‌کند و گامهایتان را استوار می‌دارد.'),
(4553, 4553, 'وَالَّذِينَ كَفَرُوا فَتَعْسًا لَهُمْ وَأَضَلَّ أَعْمَالَهُمْ', 47, 8, 26, 101, 507, 'و کسانی که کافر شدند، مرگ بر آنان! و اعمالشان نابود باد!'),
(4554, 4554, 'ذَٰلِكَ بِأَنَّهُمْ كَرِهُوا مَا أَنْزَلَ اللَّهُ فَأَحْبَطَ أَعْمَالَهُمْ', 47, 9, 26, 101, 507, 'این بخاطر آن است که از آنچه خداوند نازل کرده کراهت داشتند؛ از این رو خدا اعمالشان را حبط و نابود کرد!'),
(4555, 4555, ' أَفَلَمْ يَسِيرُوا فِي الْأَرْضِ فَيَنْظُرُوا كَيْفَ كَانَ عَاقِبَةُ الَّذِينَ مِنْ قَبْلِهِمْ ۚ دَمَّرَ اللَّهُ عَلَيْهِمْ ۖ وَلِلْكَافِرِينَ أَمْثَالُهَا', 47, 10, 26, 102, 507, 'آیا در زمین سیر نکردند تا ببینند عاقبت کسانی که قبل از آنان بودند چگونه بود؟! خداوند آنها را هلاک کرد؛ و برای کافران امثال این مجازاتها خواهد بود!'),
(4556, 4556, 'ذَٰلِكَ بِأَنَّ اللَّهَ مَوْلَى الَّذِينَ آمَنُوا وَأَنَّ الْكَافِرِينَ لَا مَوْلَىٰ لَهُمْ', 47, 11, 26, 102, 507, 'این برای آن است که خداوند مولا و سرپرست کسانی است که ایمان آوردند؛ امّا کافران مولایی ندارند!'),
(4557, 4557, 'إِنَّ اللَّهَ يُدْخِلُ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهَارُ ۖ وَالَّذِينَ كَفَرُوا يَتَمَتَّعُونَ وَيَأْكُلُونَ كَمَا تَأْكُلُ الْأَنْعَامُ وَالنَّارُ مَثْوًى لَهُمْ', 47, 12, 26, 102, 508, 'خداوند کسانی را که ایمان آوردند و اعمال صالح انجام دادند وارد باغهایی از بهشت می‌کند که نهرها از زیر (درختانش) جاری است؛ در حالی که کافران از متاع زودگذر دنیا بهره می‌گیرند و همچون چهارپایان می‌خورند، و سرانجام آتش دوزخ جایگاه آنهاست!'),
(4558, 4558, 'وَكَأَيِّنْ مِنْ قَرْيَةٍ هِيَ أَشَدُّ قُوَّةً مِنْ قَرْيَتِكَ الَّتِي أَخْرَجَتْكَ أَهْلَكْنَاهُمْ فَلَا نَاصِرَ لَهُمْ', 47, 13, 26, 102, 508, 'و چه بسیار شهرهایی که از شهری که تو را بیرون کرد نیرومندتر بودند؛ ما همه آنها را نابود کردیم و هیچ یاوری نداشتند!'),
(4559, 4559, 'أَفَمَنْ كَانَ عَلَىٰ بَيِّنَةٍ مِنْ رَبِّهِ كَمَنْ زُيِّنَ لَهُ سُوءُ عَمَلِهِ وَاتَّبَعُوا أَهْوَاءَهُمْ', 47, 14, 26, 102, 508, 'آیا کسی که دلیل روشنی از سوی پروردگارش دارد، همانند کسی است که زشتی اعمالش در نظرش آراسته شده و از هوای نفسشان پیروی می‌کنند؟!'),
(4560, 4560, 'مَثَلُ الْجَنَّةِ الَّتِي وُعِدَ الْمُتَّقُونَ ۖ فِيهَا أَنْهَارٌ مِنْ مَاءٍ غَيْرِ آسِنٍ وَأَنْهَارٌ مِنْ لَبَنٍ لَمْ يَتَغَيَّرْ طَعْمُهُ وَأَنْهَارٌ مِنْ خَمْرٍ لَذَّةٍ لِلشَّارِبِينَ وَأَنْهَارٌ مِنْ عَسَلٍ مُصَفًّى ۖ وَلَهُمْ فِيهَا مِنْ كُلِّ الثَّمَرَاتِ وَمَغْفِرَةٌ مِنْ رَبِّهِمْ ۖ كَمَنْ هُوَ خَالِدٌ فِي النَّارِ وَسُقُوا مَاءً حَمِيمًا فَقَطَّعَ أَمْعَاءَهُمْ', 47, 15, 26, 102, 508, 'توصیف بهشتی که به پرهیزگاران وعده داده شده، چنین است: در آن نهرهایی از آب صاف و خالص که بدبو نشده، و نهرهایی از شیر که طعم آن دگرگون نگشته، و نهرهایی از شراب (طهور) که مایه لذّت نوشندگان است، و نهرهایی از عسل مصفّاست، و برای آنها در آن از همه انواع میوه‌ها وجود دارد؛ و (از همه بالاتر) آمرزشی است از سوی پروردگارشان! آیا اینها همانند کسانی هستند که همیشه در آتش دوزخند و از آب جوشان نوشانده می‌شوند که اندرونشان را از هم متلاشی می‌کند؟!'),
(4561, 4561, 'وَمِنْهُمْ مَنْ يَسْتَمِعُ إِلَيْكَ حَتَّىٰ إِذَا خَرَجُوا مِنْ عِنْدِكَ قَالُوا لِلَّذِينَ أُوتُوا الْعِلْمَ مَاذَا قَالَ آنِفًا ۚ أُولَٰئِكَ الَّذِينَ طَبَعَ اللَّهُ عَلَىٰ قُلُوبِهِمْ وَاتَّبَعُوا أَهْوَاءَهُمْ', 47, 16, 26, 102, 508, 'گروهی از آنان به سخنانت گوش فرامی‌دهند، امّا هنگامی که از نزد تو خارج می‌شوند به کسانی که علم و دانش به آنان بخشیده شده (از روی استهزا) می‌گویند: «(این مرد) الان چه گفت؟!» آنها کسانی هستند که خداوند بر دلهایشان مُهر نهاده و از هوای نفسشان پیروی کرده‌اند (از این رو چیزی نمی‌فهمند)!'),
(4562, 4562, 'وَالَّذِينَ اهْتَدَوْا زَادَهُمْ هُدًى وَآتَاهُمْ تَقْوَاهُمْ', 47, 17, 26, 102, 508, 'کسانی که هدایت یافته‌اند، خداوند بر هدایتشان می‌افزاید و روح تقوا به آنان می‌بخشد!'),
(4563, 4563, 'فَهَلْ يَنْظُرُونَ إِلَّا السَّاعَةَ أَنْ تَأْتِيَهُمْ بَغْتَةً ۖ فَقَدْ جَاءَ أَشْرَاطُهَا ۚ فَأَنَّىٰ لَهُمْ إِذَا جَاءَتْهُمْ ذِكْرَاهُمْ', 47, 18, 26, 102, 508, 'آیا آنها = کافران‌ جز این انتظاری دارند که قیامت ناگهان فرا رسد (آنگاه ایمان آورند)، در حالی که هم‌اکنون نشانه‌های آن آمده است؛ اما هنگامی که بیاید، تذکّر (و ایمان) آنها سودی نخواهد داشت!'),
(4564, 4564, 'فَاعْلَمْ أَنَّهُ لَا إِلَٰهَ إِلَّا اللَّهُ وَاسْتَغْفِرْ لِذَنْبِكَ وَلِلْمُؤْمِنِينَ وَالْمُؤْمِنَاتِ ۗ وَاللَّهُ يَعْلَمُ مُتَقَلَّبَكُمْ وَمَثْوَاكُمْ', 47, 19, 26, 102, 508, 'پس بدان که معبودی جز «اللّه» نیست؛ و برای گناه خود و مردان و زنان باایمان استغفار کن! و خداوند محل حرکت و قرارگاه شما را می‌داند!'),
(4565, 4565, 'وَيَقُولُ الَّذِينَ آمَنُوا لَوْلَا نُزِّلَتْ سُورَةٌ ۖ فَإِذَا أُنْزِلَتْ سُورَةٌ مُحْكَمَةٌ وَذُكِرَ فِيهَا الْقِتَالُ ۙ رَأَيْتَ الَّذِينَ فِي قُلُوبِهِمْ مَرَضٌ يَنْظُرُونَ إِلَيْكَ نَظَرَ الْمَغْشِيِّ عَلَيْهِ مِنَ الْمَوْتِ ۖ فَأَوْلَىٰ لَهُمْ', 47, 20, 26, 102, 509, 'کسانی که ایمان آورده‌اند می‌گویند: «چرا سوره‌ای نازل نمی‌شود (که در آن فرمان جهاد باشد)؟!» امّا هنگامی که سوره واضح و روشنی نازل می‌گردد که در آن سخنی از جنگ است، منافقان بیماردل را می‌بینی که همچون کسی که در آستانه مرگ قرار گرفته به تو نگاه می‌کنند؛ پس مرگ و نابودی برای آنان سزاوارتر است!'),
(4566, 4566, 'طَاعَةٌ وَقَوْلٌ مَعْرُوفٌ ۚ فَإِذَا عَزَمَ الْأَمْرُ فَلَوْ صَدَقُوا اللَّهَ لَكَانَ خَيْرًا لَهُمْ', 47, 21, 26, 102, 509, '(ولی) اطاعت و سخن سنجیده برای آنان بهتر است؛ و اگر هنگامی که فرمان جهاد قطعی می‌شود به خدا راست گویند (و از در صدق و صفا درآیند) برای آنها بهتر می‌باشد!'),
(4567, 4567, 'فَهَلْ عَسَيْتُمْ إِنْ تَوَلَّيْتُمْ أَنْ تُفْسِدُوا فِي الْأَرْضِ وَتُقَطِّعُوا أَرْحَامَكُمْ', 47, 22, 26, 102, 509, 'اگر (از این دستورها) روی گردان شوید، جز این انتظار می‌رود که در زمین فساد و قطع پیوند خویشاوندی کنید؟!'),
(4568, 4568, 'أُولَٰئِكَ الَّذِينَ لَعَنَهُمُ اللَّهُ فَأَصَمَّهُمْ وَأَعْمَىٰ أَبْصَارَهُمْ', 47, 23, 26, 102, 509, 'آنها کسانی هستند که خداوند از رحمت خویش دورشان ساخته، گوشهایشان را کر و چشمهایشان را کور کرده است!'),
(4569, 4569, 'أَفَلَا يَتَدَبَّرُونَ الْقُرْآنَ أَمْ عَلَىٰ قُلُوبٍ أَقْفَالُهَا', 47, 24, 26, 102, 509, 'آیا آنها در قرآن تدبّر نمی‌کنند، یا بر دلهایشان قفل نهاده شده است؟!'),
(4570, 4570, 'إِنَّ الَّذِينَ ارْتَدُّوا عَلَىٰ أَدْبَارِهِمْ مِنْ بَعْدِ مَا تَبَيَّنَ لَهُمُ الْهُدَى ۙ الشَّيْطَانُ سَوَّلَ لَهُمْ وَأَمْلَىٰ لَهُمْ', 47, 25, 26, 102, 509, 'کسانی که بعد از روشن شدن هدایت برای آنها، پشت به حق کردند، شیطان اعمال زشتشان را در نظرشان زینت داده و آنان را با آرزوهای طولانی فریفته است!'),
(4571, 4571, 'ذَٰلِكَ بِأَنَّهُمْ قَالُوا لِلَّذِينَ كَرِهُوا مَا نَزَّلَ اللَّهُ سَنُطِيعُكُمْ فِي بَعْضِ الْأَمْرِ ۖ وَاللَّهُ يَعْلَمُ إِسْرَارَهُمْ', 47, 26, 26, 102, 509, 'این بخاطر آن است که آنان به کسانی که نزول وحی الهی را کراهت داشتند گفتند: «ما در بعضی از امور از شما پیروی می‌کنیم؟» در حالی که خداوند پنهانکاری آنان را می‌داند.'),
(4572, 4572, 'فَكَيْفَ إِذَا تَوَفَّتْهُمُ الْمَلَائِكَةُ يَضْرِبُونَ وُجُوهَهُمْ وَأَدْبَارَهُمْ', 47, 27, 26, 102, 509, 'حال آنها چگونه خواهد بود هنگامی که فرشتگان (مرگ) بر صورت و پشت آنان می‌زنند و جانشان را می‌گیرند؟!'),
(4573, 4573, 'ذَٰلِكَ بِأَنَّهُمُ اتَّبَعُوا مَا أَسْخَطَ اللَّهَ وَكَرِهُوا رِضْوَانَهُ فَأَحْبَطَ أَعْمَالَهُمْ', 47, 28, 26, 102, 509, 'این بخاطر آن است که آنها از آنچه خداوند را به خشم می‌آورد پیروی کردند، و آنچه را موجب خشنودی اوست کراهت داشتند؛ از این رو (خداوند) اعمالشان را نابود کرد!'),
(4574, 4574, 'أَمْ حَسِبَ الَّذِينَ فِي قُلُوبِهِمْ مَرَضٌ أَنْ لَنْ يُخْرِجَ اللَّهُ أَضْغَانَهُمْ', 47, 29, 26, 102, 509, 'آیا کسانی که در دلهایشان بیماری است گمان کردند خدا کینه‌هایشان را آشکار نمی‌کند؟!'),
(4575, 4575, 'وَلَوْ نَشَاءُ لَأَرَيْنَاكَهُمْ فَلَعَرَفْتَهُمْ بِسِيمَاهُمْ ۚ وَلَتَعْرِفَنَّهُمْ فِي لَحْنِ الْقَوْلِ ۚ وَاللَّهُ يَعْلَمُ أَعْمَالَكُمْ', 47, 30, 26, 102, 510, 'و اگر ما بخواهیم آنها را به تو نشان می‌دهیم تا آنان را با قیافه‌هایشان بشناسی، هر چند می‌توانی آنها را از طرز سخنانشان بشناسی؛ و خداوند اعمال شما را می‌داند!'),
(4576, 4576, 'وَلَنَبْلُوَنَّكُمْ حَتَّىٰ نَعْلَمَ الْمُجَاهِدِينَ مِنْكُمْ وَالصَّابِرِينَ وَنَبْلُوَ أَخْبَارَكُمْ', 47, 31, 26, 102, 510, 'ما همه شما را قطعاً می‌آزمائیم تا معلوم شود مجاهدان واقعی و صابران از میان شما کیانند، و اخبار شما را بیازماییم!'),
(4577, 4577, 'إِنَّ الَّذِينَ كَفَرُوا وَصَدُّوا عَنْ سَبِيلِ اللَّهِ وَشَاقُّوا الرَّسُولَ مِنْ بَعْدِ مَا تَبَيَّنَ لَهُمُ الْهُدَىٰ لَنْ يَضُرُّوا اللَّهَ شَيْئًا وَسَيُحْبِطُ أَعْمَالَهُمْ', 47, 32, 26, 102, 510, 'آنان که کافر شدند و (مردم را) از راه خدا بازداشتند و بعد از روشن‌شدن هدایت برای آنان (باز) به مخالفت با رسول (خدا) برخاستند، هرگز زیانی به خدا نمی‌رسانند و (خداوند) بزودی اعمالشان را نابود می‌کند!'),
(4578, 4578, ' يَا أَيُّهَا الَّذِينَ آمَنُوا أَطِيعُوا اللَّهَ وَأَطِيعُوا الرَّسُولَ وَلَا تُبْطِلُوا أَعْمَالَكُمْ', 47, 33, 26, 102, 510, 'ای کسانی که ایمان آورده‌اید! اطاعت کنید خدا را، و اطاعت کنید رسول (خدا) را، و اعمال خود را باطل نسازید!'),
(4579, 4579, 'إِنَّ الَّذِينَ كَفَرُوا وَصَدُّوا عَنْ سَبِيلِ اللَّهِ ثُمَّ مَاتُوا وَهُمْ كُفَّارٌ فَلَنْ يَغْفِرَ اللَّهُ لَهُمْ', 47, 34, 26, 102, 510, 'کسانی که کافر شدند و (مردم را) از راه خدا بازداشتند سپس در حال کفر از دنیا رفتند، خدا هرگز آنها را نخواهد بخشید.'),
(4580, 4580, 'فَلَا تَهِنُوا وَتَدْعُوا إِلَى السَّلْمِ وَأَنْتُمُ الْأَعْلَوْنَ وَاللَّهُ مَعَكُمْ وَلَنْ يَتِرَكُمْ أَعْمَالَكُمْ', 47, 35, 26, 102, 510, 'پس هرگز سست نشوید و (دشمنان را) به صلح (ذلّت‌بار) دعوت نکنید در حالی که شما برترید، و خداوند با شماست و چیزی از (ثواب) اعمالتان را کم نمی‌کند!'),
(4581, 4581, 'إِنَّمَا الْحَيَاةُ الدُّنْيَا لَعِبٌ وَلَهْوٌ ۚ وَإِنْ تُؤْمِنُوا وَتَتَّقُوا يُؤْتِكُمْ أُجُورَكُمْ وَلَا يَسْأَلْكُمْ أَمْوَالَكُمْ', 47, 36, 26, 102, 510, 'زندگی دنیا تنها بازی و سرگرمی است؛ و اگر ایمان آورید و تقوا پیشه کنید، پاداشهای شما را می‌دهد و اموال شما را نمی‌طلبد،'),
(4582, 4582, 'إِنْ يَسْأَلْكُمُوهَا فَيُحْفِكُمْ تَبْخَلُوا وَيُخْرِجْ أَضْغَانَكُمْ', 47, 37, 26, 102, 510, 'چرا که هر گاه اموال شما را مطالبه کند و حتّی اصرار نماید، بخل می‌ورزید؛ و کینه و خشم شما را آشکار می‌سازد!'),
(4583, 4583, 'هَا أَنْتُمْ هَٰؤُلَاءِ تُدْعَوْنَ لِتُنْفِقُوا فِي سَبِيلِ اللَّهِ فَمِنْكُمْ مَنْ يَبْخَلُ ۖ وَمَنْ يَبْخَلْ فَإِنَّمَا يَبْخَلُ عَنْ نَفْسِهِ ۚ وَاللَّهُ الْغَنِيُّ وَأَنْتُمُ الْفُقَرَاءُ ۚ وَإِنْ تَتَوَلَّوْا يَسْتَبْدِلْ قَوْمًا غَيْرَكُمْ ثُمَّ لَا يَكُونُوا أَمْثَالَكُمْ', 47, 38, 26, 102, 510, 'آری، شما همان گروهی هستید که برای انفاق در راه خدا دعوت می‌شوید، بعضی از شما بخل می‌ورزند؛ و هر کس بخل ورزد، نسبت به خود بخل کرده است؛ و خداوند بی‌نیاز است و شما همه نیازمندید؛ و هرگاه سرپیچی کنید، خداوند گروه دیگری را جای شما می‌آورد پس آنها مانند شما نخواهند بود (و سخاوتمندانه در راه خدا انفاق می‌کنند).'),
(4584, 4584, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ إِنَّا فَتَحْنَا لَكَ فَتْحًا مُبِينًا', 48, 1, 26, 102, 511, 'ما برای تو پیروزی آشکاری فراهم ساختیم!...'),
(4585, 4585, 'لِيَغْفِرَ لَكَ اللَّهُ مَا تَقَدَّمَ مِنْ ذَنْبِكَ وَمَا تَأَخَّرَ وَيُتِمَّ نِعْمَتَهُ عَلَيْكَ وَيَهْدِيَكَ صِرَاطًا مُسْتَقِيمًا', 48, 2, 26, 102, 511, 'تا خداوند گناهان گذشته و آینده‌ای را که به تو نسبت می‌دادند ببخشد (و حقّانیت تو را ثابت نموده) و نعمتش را بر تو تمام کند و به راه راست هدایتت فرماید؛'),
(4586, 4586, 'وَيَنْصُرَكَ اللَّهُ نَصْرًا عَزِيزًا', 48, 3, 26, 102, 511, 'و پیروزی شکست‌ناپذیری نصیب تو کند.'),
(4587, 4587, 'هُوَ الَّذِي أَنْزَلَ السَّكِينَةَ فِي قُلُوبِ الْمُؤْمِنِينَ لِيَزْدَادُوا إِيمَانًا مَعَ إِيمَانِهِمْ ۗ وَلِلَّهِ جُنُودُ السَّمَاوَاتِ وَالْأَرْضِ ۚ وَكَانَ اللَّهُ عَلِيمًا حَكِيمًا', 48, 4, 26, 102, 511, 'او کسی است که آرامش را در دلهای مؤمنان نازل کرد تا ایمانی بر ایمانشان بیفزایند؛ لشکریان آسمانها و زمین از آن خداست، و خداوند دانا و حکیم است.'),
(4588, 4588, 'لِيُدْخِلَ الْمُؤْمِنِينَ وَالْمُؤْمِنَاتِ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهَارُ خَالِدِينَ فِيهَا وَيُكَفِّرَ عَنْهُمْ سَيِّئَاتِهِمْ ۚ وَكَانَ ذَٰلِكَ عِنْدَ اللَّهِ فَوْزًا عَظِيمًا', 48, 5, 26, 102, 511, 'هدف (دیگر از آن فتح مبین) این بود که مردان و زنان با ایمان را در باغهایی (از بهشت) وارد کند که نهرها از زیر (درختانش) جاری است، در حالی که جاودانه در آن می‌مانند، و گناهانشان را می‌بخشد، و این نزد خدا رستگاری بزرگی است!'),
(4589, 4589, 'وَيُعَذِّبَ الْمُنَافِقِينَ وَالْمُنَافِقَاتِ وَالْمُشْرِكِينَ وَالْمُشْرِكَاتِ الظَّانِّينَ بِاللَّهِ ظَنَّ السَّوْءِ ۚ عَلَيْهِمْ دَائِرَةُ السَّوْءِ ۖ وَغَضِبَ اللَّهُ عَلَيْهِمْ وَلَعَنَهُمْ وَأَعَدَّ لَهُمْ جَهَنَّمَ ۖ وَسَاءَتْ مَصِيرًا', 48, 6, 26, 102, 511, 'و (نیز) مردان و زنان منافق و مردان و زنان مشرک را که به خدا گمان بد می‌برند مجازات کند؛ (آری) حوادث ناگواری (که برای مؤمنان انتظار می‌کشند) تنها بر خودشان نازل می‌شود! خداوند بر آنان غضب کرده و از رحمت خود دورشان ساخته و جهنم را برای آنان آماده کرده؛ و چه بد سرانجامی است!'),
(4590, 4590, 'وَلِلَّهِ جُنُودُ السَّمَاوَاتِ وَالْأَرْضِ ۚ وَكَانَ اللَّهُ عَزِيزًا حَكِيمًا', 48, 7, 26, 102, 511, 'لشکریان آسمانها و زمین تنها از آن خداست؛ و خداوند شکست‌ناپذیر و حکیم است.!'),
(4591, 4591, 'إِنَّا أَرْسَلْنَاكَ شَاهِدًا وَمُبَشِّرًا وَنَذِيرًا', 48, 8, 26, 102, 511, 'به یقین ما تو را گواه (بر اعمال آنها) و بشارت‌دهنده و بیم‌دهنده فرستادیم،'),
(4592, 4592, 'لِتُؤْمِنُوا بِاللَّهِ وَرَسُولِهِ وَتُعَزِّرُوهُ وَتُوَقِّرُوهُ وَتُسَبِّحُوهُ بُكْرَةً وَأَصِيلًا', 48, 9, 26, 102, 511, 'تا (شما مردم) به خدا و رسولش ایمان بیاورید و از او دفاع کنید و او را بزرگ دارید، و خدا را صبح و شام تسبیح گویید.'),
(4593, 4593, 'إِنَّ الَّذِينَ يُبَايِعُونَكَ إِنَّمَا يُبَايِعُونَ اللَّهَ يَدُ اللَّهِ فَوْقَ أَيْدِيهِمْ ۚ فَمَنْ نَكَثَ فَإِنَّمَا يَنْكُثُ عَلَىٰ نَفْسِهِ ۖ وَمَنْ أَوْفَىٰ بِمَا عَاهَدَ عَلَيْهُ اللَّهَ فَسَيُؤْتِيهِ أَجْرًا عَظِيمًا', 48, 10, 26, 102, 512, 'کسانی که با تو بیعت می‌کنند (در حقیّقت) تنها با خدا بیعت می‌نمایند، و دست خدا بالای دست آنهاست؛ پس هر کس پیمان‌شکنی کند، تنها به زیان خود پیمان شکسته است؛ و آن کس که نسبت به عهدی که با خدا بسته وفا کند، بزودی پاداش عظیمی به او خواهد داد.'),
(4594, 4594, 'سَيَقُولُ لَكَ الْمُخَلَّفُونَ مِنَ الْأَعْرَابِ شَغَلَتْنَا أَمْوَالُنَا وَأَهْلُونَا فَاسْتَغْفِرْ لَنَا ۚ يَقُولُونَ بِأَلْسِنَتِهِمْ مَا لَيْسَ فِي قُلُوبِهِمْ ۚ قُلْ فَمَنْ يَمْلِكُ لَكُمْ مِنَ اللَّهِ شَيْئًا إِنْ أَرَادَ بِكُمْ ضَرًّا أَوْ أَرَادَ بِكُمْ نَفْعًا ۚ بَلْ كَانَ اللَّهُ بِمَا تَعْمَلُونَ خَبِيرًا', 48, 11, 26, 102, 512, 'بزودی متخلّفان از اعراب بادیه‌نشین (عذرتراشی کرده) می‌گویند: «(حفظ) اموال و خانواده‌های ما، ما را به خود مشغول داشت (و نتوانستیم در سفر حدیبیّه تو را همراهی کنیم)، برای ما طلب آمرزش کن!» آنها به زبان خود چیزی می‌گویند که در دل ندارند! بگو: «چه کسی می‌تواند در برابر خداوند از شما دفاع کند هرگاه زیانی برای شما بخواهد، و یا اگر نفعی اراده کند (مانع گردد)؟! و خداوند به همه کارهایی که انجام می‌دهید آگاه است!»'),
(4595, 4595, 'بَلْ ظَنَنْتُمْ أَنْ لَنْ يَنْقَلِبَ الرَّسُولُ وَالْمُؤْمِنُونَ إِلَىٰ أَهْلِيهِمْ أَبَدًا وَزُيِّنَ ذَٰلِكَ فِي قُلُوبِكُمْ وَظَنَنْتُمْ ظَنَّ السَّوْءِ وَكُنْتُمْ قَوْمًا بُورًا', 48, 12, 26, 102, 512, 'ولی شما گمان کردید پیامبر و مؤمنان هرگز به خانواده‌های خود بازنخواهند گشت؛ و این (پندار غلط) در دلهای شما زینت یافته بود و گمان بد کردید؛ و سرانجام (در دام شیطان افتادید و) هلاک شدید!'),
(4596, 4596, 'وَمَنْ لَمْ يُؤْمِنْ بِاللَّهِ وَرَسُولِهِ فَإِنَّا أَعْتَدْنَا لِلْكَافِرِينَ سَعِيرًا', 48, 13, 26, 102, 512, 'آن کس که به خدا و پیامبرش ایمان نیاورده (سرنوشتش دوزخ است)، چرا که ما برای کافران آتش فروزان آماده کرده‌ایم!'),
(4597, 4597, 'وَلِلَّهِ مُلْكُ السَّمَاوَاتِ وَالْأَرْضِ ۚ يَغْفِرُ لِمَنْ يَشَاءُ وَيُعَذِّبُ مَنْ يَشَاءُ ۚ وَكَانَ اللَّهُ غَفُورًا رَحِيمًا', 48, 14, 26, 102, 512, 'مالکیت و حاکمیت آسمانها و زمین از آن خداست؛ هر کس را بخواهد (و شایسته بداند) می‌بخشد، و هر کس را بخواهد مجازات می‌کند؛ و خداوند آمرزنده و رحیم است.'),
(4598, 4598, 'سَيَقُولُ الْمُخَلَّفُونَ إِذَا انْطَلَقْتُمْ إِلَىٰ مَغَانِمَ لِتَأْخُذُوهَا ذَرُونَا نَتَّبِعْكُمْ ۖ يُرِيدُونَ أَنْ يُبَدِّلُوا كَلَامَ اللَّهِ ۚ قُلْ لَنْ تَتَّبِعُونَا كَذَٰلِكُمْ قَالَ اللَّهُ مِنْ قَبْلُ ۖ فَسَيَقُولُونَ بَلْ تَحْسُدُونَنَا ۚ بَلْ كَانُوا لَا يَفْقَهُونَ إِلَّا قَلِيلًا', 48, 15, 26, 102, 512, 'هنگامی که شما برای به دست آوردن غنایمی حرکت کنید، متخلّفان (حدیبیّه) می‌گویند: «بگذارید ما هم در پی شما بیائیم، آنها می‌خواهند کلام خدا را تغییر دهند؛ بگو: «هرگز نباید بدنبال ما بیایید؛ این گونه خداوند از قبل گفته است!» آنها به زودی می‌گویند: «شما نسبت به ما حسد می‌ورزید!» ولی آنها جز اندکی نمی‌فهمند!'),
(4599, 4599, 'قُلْ لِلْمُخَلَّفِينَ مِنَ الْأَعْرَابِ سَتُدْعَوْنَ إِلَىٰ قَوْمٍ أُولِي بَأْسٍ شَدِيدٍ تُقَاتِلُونَهُمْ أَوْ يُسْلِمُونَ ۖ فَإِنْ تُطِيعُوا يُؤْتِكُمُ اللَّهُ أَجْرًا حَسَنًا ۖ وَإِنْ تَتَوَلَّوْا كَمَا تَوَلَّيْتُمْ مِنْ قَبْلُ يُعَذِّبْكُمْ عَذَابًا أَلِيمًا', 48, 16, 26, 102, 513, 'به متخلّفان از اعراب بگو: «بزودی از شما دعوت می‌شود که بسوی قومی نیرومند و جنگجو بروید و با آنها پیکار کنید تا اسلام بیاورند؛ اگر اطاعت کنید، خداوند پاداش نیکی به شما می‌دهد؛ و اگر سرپیچی نمایید -همان گونه که در گذشته نیز سرپیچی کردید- شما را با عذاب دردناکی کیفر می‌دهد!»'),
(4600, 4600, 'لَيْسَ عَلَى الْأَعْمَىٰ حَرَجٌ وَلَا عَلَى الْأَعْرَجِ حَرَجٌ وَلَا عَلَى الْمَرِيضِ حَرَجٌ ۗ وَمَنْ يُطِعِ اللَّهَ وَرَسُولَهُ يُدْخِلْهُ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهَارُ ۖ وَمَنْ يَتَوَلَّ يُعَذِّبْهُ عَذَابًا أَلِيمًا', 48, 17, 26, 102, 513, 'بر نابینا و لنگ و بیمار گناهی نیست (اگر در میدان جهاد شرکت نکنند)؛ و هر کس خدا و رسولش را اطاعت نماید، او را در باغهای (از بهشت) وارد می‌کند که نهرها از زیر (درختانش) جاری است؛ و آن کس که سرپیچی کند، او را به عذاب دردناکی گرفتار می‌سازد!'),
(4601, 4601, ' لَقَدْ رَضِيَ اللَّهُ عَنِ الْمُؤْمِنِينَ إِذْ يُبَايِعُونَكَ تَحْتَ الشَّجَرَةِ فَعَلِمَ مَا فِي قُلُوبِهِمْ فَأَنْزَلَ السَّكِينَةَ عَلَيْهِمْ وَأَثَابَهُمْ فَتْحًا قَرِيبًا', 48, 18, 26, 103, 513, 'خداوند از مؤمنان -هنگامی که در زیر آن درخت با تو بیعت کردند- راضی و خشنود شد؛ خدا آنچه را در درون دلهایشان (از ایمان و صداقت) نهفته بود می‌دانست؛ از این رو آرامش را بر دلهایشان نازل کرد و پیروزی نزدیکی بعنوان پاداش نصیب آنها فرمود؛'),
(4602, 4602, 'وَمَغَانِمَ كَثِيرَةً يَأْخُذُونَهَا ۗ وَكَانَ اللَّهُ عَزِيزًا حَكِيمًا', 48, 19, 26, 103, 513, 'و (همچنین) غنایم بسیاری که آن را به دست می‌آورید؛ و خداوند شکست ناپذیر و حکیم است!'),
(4603, 4603, 'وَعَدَكُمُ اللَّهُ مَغَانِمَ كَثِيرَةً تَأْخُذُونَهَا فَعَجَّلَ لَكُمْ هَٰذِهِ وَكَفَّ أَيْدِيَ النَّاسِ عَنْكُمْ وَلِتَكُونَ آيَةً لِلْمُؤْمِنِينَ وَيَهْدِيَكُمْ صِرَاطًا مُسْتَقِيمًا', 48, 20, 26, 103, 513, 'خداوند غنایم فراوانی به شما وعده داده بود که آنها را به دست می‌آورید، ولی این یکی را زودتر برای شما فراهم ساخت؛ و دست تعدّی مردم = دشمنان‌ را از شما بازداشت تا نشانه‌ای برای مؤمنان باشد و شما را به راه راست هدایت کند!'),
(4604, 4604, 'وَأُخْرَىٰ لَمْ تَقْدِرُوا عَلَيْهَا قَدْ أَحَاطَ اللَّهُ بِهَا ۚ وَكَانَ اللَّهُ عَلَىٰ كُلِّ شَيْءٍ قَدِيرًا', 48, 21, 26, 103, 513, 'و نیز غنایم و فتوحات دیگری (نصیبتان می‌کند) که شما توانایی آن را ندارید، ولی قدرت خدا به آن احاطه دارد؛ و خداوند بر همه چیز تواناست!'),
(4605, 4605, 'وَلَوْ قَاتَلَكُمُ الَّذِينَ كَفَرُوا لَوَلَّوُا الْأَدْبَارَ ثُمَّ لَا يَجِدُونَ وَلِيًّا وَلَا نَصِيرًا', 48, 22, 26, 103, 513, 'و اگر کافران (در سرزمین حدیبیّه) با شما پیکار می‌کردند بزودی فرار می‌کردند، سپس ولیّ و یاوری نمی‌یافتند!'),
(4606, 4606, 'سُنَّةَ اللَّهِ الَّتِي قَدْ خَلَتْ مِنْ قَبْلُ ۖ وَلَنْ تَجِدَ لِسُنَّةِ اللَّهِ تَبْدِيلًا', 48, 23, 26, 103, 513, 'این سنّت الهی است که در گذشته نیز بوده است؛ و هرگز برای سنّت الهی تغییر و تبدیلی نخواهی یافت!'),
(4607, 4607, 'وَهُوَ الَّذِي كَفَّ أَيْدِيَهُمْ عَنْكُمْ وَأَيْدِيَكُمْ عَنْهُمْ بِبَطْنِ مَكَّةَ مِنْ بَعْدِ أَنْ أَظْفَرَكُمْ عَلَيْهِمْ ۚ وَكَانَ اللَّهُ بِمَا تَعْمَلُونَ بَصِيرًا', 48, 24, 26, 103, 514, 'او کسی است که دست آنها را از شما، و دست شما را از آنان در دل مکّه کوتاه کرد، بعد از آنکه شما را بر آنها پیروز ساخت؛ و خداوند به آنچه انجام می‌دهید بیناست!'),
(4608, 4608, 'هُمُ الَّذِينَ كَفَرُوا وَصَدُّوكُمْ عَنِ الْمَسْجِدِ الْحَرَامِ وَالْهَدْيَ مَعْكُوفًا أَنْ يَبْلُغَ مَحِلَّهُ ۚ وَلَوْلَا رِجَالٌ مُؤْمِنُونَ وَنِسَاءٌ مُؤْمِنَاتٌ لَمْ تَعْلَمُوهُمْ أَنْ تَطَئُوهُمْ فَتُصِيبَكُمْ مِنْهُمْ مَعَرَّةٌ بِغَيْرِ عِلْمٍ ۖ لِيُدْخِلَ اللَّهُ فِي رَحْمَتِهِ مَنْ يَشَاءُ ۚ لَوْ تَزَيَّلُوا لَعَذَّبْنَا الَّذِينَ كَفَرُوا مِنْهُمْ عَذَابًا أَلِيمًا', 48, 25, 26, 103, 514, 'آنها کسانی هستند که کافر شدند و شما را از (زیارت) مسجد الحرام و رسیدن قربانیهایتان به محل قربانگاه بازداشتند؛ و هرگاه مردان و زنان با ایمانی در این میان بدون آگاهی شما، زیر دست و پا، از بین نمی‌رفتند که از این راه عیب و عاری ناآگاهانه به شما می‌رسید، (خداوند هرگز مانع این جنگ نمی‌شد)! هدف این بود که خدا هر کس را می‌خواهد در رحمت خود وارد کند؛ و اگر مؤمنان و کفّار (در مکه) از هم جدا می‌شدند، کافران را عذاب دردناکی می‌کردیم!'),
(4609, 4609, 'إِذْ جَعَلَ الَّذِينَ كَفَرُوا فِي قُلُوبِهِمُ الْحَمِيَّةَ حَمِيَّةَ الْجَاهِلِيَّةِ فَأَنْزَلَ اللَّهُ سَكِينَتَهُ عَلَىٰ رَسُولِهِ وَعَلَى الْمُؤْمِنِينَ وَأَلْزَمَهُمْ كَلِمَةَ التَّقْوَىٰ وَكَانُوا أَحَقَّ بِهَا وَأَهْلَهَا ۚ وَكَانَ اللَّهُ بِكُلِّ شَيْءٍ عَلِيمًا', 48, 26, 26, 103, 514, '(به خاطر بیاورید) هنگامی را که کافران در دلهای خود خشم و نخوت جاهلیّت داشتند؛ و (در مقابل،) خداوند آرامش و سکینه خود را بر فرستاده خویش و مؤمنان نازل فرمود و آنها را به حقیقت تقوا ملزم ساخت، و آنان از هر کس شایسته‌تر و اهل آن بودند؛ و خداوند به همه چیز دانا است.'),
(4610, 4610, 'لَقَدْ صَدَقَ اللَّهُ رَسُولَهُ الرُّؤْيَا بِالْحَقِّ ۖ لَتَدْخُلُنَّ الْمَسْجِدَ الْحَرَامَ إِنْ شَاءَ اللَّهُ آمِنِينَ مُحَلِّقِينَ رُءُوسَكُمْ وَمُقَصِّرِينَ لَا تَخَافُونَ ۖ فَعَلِمَ مَا لَمْ تَعْلَمُوا فَجَعَلَ مِنْ دُونِ ذَٰلِكَ فَتْحًا قَرِيبًا', 48, 27, 26, 103, 514, 'خداوند آنچه را به پیامبرش در عالم خواب نشان داد راست گفت؛ بطور قطع همه شما بخواست خدا وارد مسجد الحرام می‌شوید در نهایت امنیّت و در حالی که سرهای خود را تراشیده یا کوتاه کرده‌اید و از هیچ کس ترس و وحشتی ندارید؛ ولی خداوند چیزهایی را می‌دانست که شما نمی‌دانستید (و در این تأخیر حکمتی بود)؛ و قبل از آن، فتح نزدیکی (برای شما) قرار داده است.'),
(4611, 4611, 'هُوَ الَّذِي أَرْسَلَ رَسُولَهُ بِالْهُدَىٰ وَدِينِ الْحَقِّ لِيُظْهِرَهُ عَلَى الدِّينِ كُلِّهِ ۚ وَكَفَىٰ بِاللَّهِ شَهِيدًا', 48, 28, 26, 103, 514, 'او کسی است که رسولش را با هدایت و دین حق فرستاده تا آن را بر همه ادایان پیروز کند؛ و کافی است که خدا گواه این موضوع باشد!'),
(4612, 4612, 'مُحَمَّدٌ رَسُولُ اللَّهِ ۚ وَالَّذِينَ مَعَهُ أَشِدَّاءُ عَلَى الْكُفَّارِ رُحَمَاءُ بَيْنَهُمْ ۖ تَرَاهُمْ رُكَّعًا سُجَّدًا يَبْتَغُونَ فَضْلًا مِنَ اللَّهِ وَرِضْوَانًا ۖ سِيمَاهُمْ فِي وُجُوهِهِمْ مِنْ أَثَرِ السُّجُودِ ۚ ذَٰلِكَ مَثَلُهُمْ فِي التَّوْرَاةِ ۚ وَمَثَلُهُمْ فِي الْإِنْجِيلِ كَزَرْعٍ أَخْرَجَ شَطْأَهُ فَآزَرَهُ فَاسْتَغْلَظَ فَاسْتَوَىٰ عَلَىٰ سُوقِهِ يُعْجِبُ الزُّرَّاعَ لِيَغِيظَ بِهِمُ الْكُفَّارَ ۗ وَعَدَ اللَّهُ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ مِنْهُمْ مَغْفِرَةً وَأَجْرًا عَظِيمًا', 48, 29, 26, 103, 515, 'محمّد (ص) فرستاده خداست؛ و کسانی که با او هستند در برابر کفّار سرسخت و شدید، و در میان خود مهربانند؛ پیوسته آنها را در حال رکوع و سجود می‌بینی در حالی که همواره فضل خدا و رضای او را می‌طلبند؛ نشانه آنها در صورتشان از اثر سجده نمایان است؛ این توصیف آنان در تورات و توصیف آنان در انجیل است، همانند زراعتی که جوانه‌های خود را خارج ساخته، سپس به تقویت آن پرداخته تا محکم شده و بر پای خود ایستاده است و بقدری نموّ و رشد کرده که زارعان را به شگفتی وامی‌دارد؛ این برای آن است که کافران را به خشم آورد (ولی) کسانی از آنها را که ایمان آورده و کارهای شایسته انجام داده‌اند، خداوند وعده آمرزش و اجر عظیمی داده است.'),
(4613, 4613, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ يَا أَيُّهَا الَّذِينَ آمَنُوا لَا تُقَدِّمُوا بَيْنَ يَدَيِ اللَّهِ وَرَسُولِهِ ۖ وَاتَّقُوا اللَّهَ ۚ إِنَّ اللَّهَ سَمِيعٌ عَلِيمٌ', 49, 1, 26, 103, 515, 'ای کسانی که ایمان آورده‌اید! چیزی را بر خدا و رسولش مقدّم نشمرید (و پیشی مگیرید)، و تقوای الهی پیشه کنید که خداوند شنوا و داناست!'),
(4614, 4614, 'يَا أَيُّهَا الَّذِينَ آمَنُوا لَا تَرْفَعُوا أَصْوَاتَكُمْ فَوْقَ صَوْتِ النَّبِيِّ وَلَا تَجْهَرُوا لَهُ بِالْقَوْلِ كَجَهْرِ بَعْضِكُمْ لِبَعْضٍ أَنْ تَحْبَطَ أَعْمَالُكُمْ وَأَنْتُمْ لَا تَشْعُرُونَ', 49, 2, 26, 103, 515, 'ای کسانی که ایمان آورده‌اید! صدای خود را فراتر از صدای پیامبر نکنید، و در برابر او بلند سخن مگویید (و داد و فریاد نزنید) آن گونه که بعضی از شما در برابر بعضی بلند صدا می‌کنند، مبادا اعمال شما نابود گردد در حالی که نمی‌دانید!'),
(4615, 4615, 'إِنَّ الَّذِينَ يَغُضُّونَ أَصْوَاتَهُمْ عِنْدَ رَسُولِ اللَّهِ أُولَٰئِكَ الَّذِينَ امْتَحَنَ اللَّهُ قُلُوبَهُمْ لِلتَّقْوَىٰ ۚ لَهُمْ مَغْفِرَةٌ وَأَجْرٌ عَظِيمٌ', 49, 3, 26, 103, 515, 'آنها که صدای خود را نزد رسول خدا کوتاه می‌کنند همان کسانی هستند که خداوند دلهایشان را برای تقوا خالص نموده، و برای آنان آمرزش و پاداش عظیمی است!'),
(4616, 4616, 'إِنَّ الَّذِينَ يُنَادُونَكَ مِنْ وَرَاءِ الْحُجُرَاتِ أَكْثَرُهُمْ لَا يَعْقِلُونَ', 49, 4, 26, 103, 515, '(ولی) کسانی که تو را از پشت حجره‌ها بلند صدا می‌زنند، بیشترشان نمی‌فهمند!'),
(4617, 4617, 'وَلَوْ أَنَّهُمْ صَبَرُوا حَتَّىٰ تَخْرُجَ إِلَيْهِمْ لَكَانَ خَيْرًا لَهُمْ ۚ وَاللَّهُ غَفُورٌ رَحِيمٌ', 49, 5, 26, 103, 516, 'اگر آنها صبر می‌کردند تا خود به سراغشان آیی، برای آنان بهتر بود؛ و خداوند آمرزنده و رحیم است!'),
(4618, 4618, 'يَا أَيُّهَا الَّذِينَ آمَنُوا إِنْ جَاءَكُمْ فَاسِقٌ بِنَبَإٍ فَتَبَيَّنُوا أَنْ تُصِيبُوا قَوْمًا بِجَهَالَةٍ فَتُصْبِحُوا عَلَىٰ مَا فَعَلْتُمْ نَادِمِينَ', 49, 6, 26, 103, 516, 'ای کسانی که ایمان آورده‌اید! اگر شخص فاسقی خبری برای شما بیاورد، درباره آن تحقیق کنید، مبادا به گروهی از روی نادانی آسیب برسانید و از کرده خود پشیمان شوید!'),
(4619, 4619, 'وَاعْلَمُوا أَنَّ فِيكُمْ رَسُولَ اللَّهِ ۚ لَوْ يُطِيعُكُمْ فِي كَثِيرٍ مِنَ الْأَمْرِ لَعَنِتُّمْ وَلَٰكِنَّ اللَّهَ حَبَّبَ إِلَيْكُمُ الْإِيمَانَ وَزَيَّنَهُ فِي قُلُوبِكُمْ وَكَرَّهَ إِلَيْكُمُ الْكُفْرَ وَالْفُسُوقَ وَالْعِصْيَانَ ۚ أُولَٰئِكَ هُمُ الرَّاشِدُونَ', 49, 7, 26, 103, 516, 'و بدانید رسول خدا در میان شماست؛ هرگاه در بسیاری از کارها از شما اطاعت کند، به مشقّت خواهید افتاد؛ ولی خداوند ایمان را محبوب شما قرار داده و آن را در دلهایتان زینت بخشیده، و (به عکس) کفر و فسق و گناه را منفورتان قرار داده است؛ کسانی که دارای این صفاتند هدایت یافتگانند!'),
(4620, 4620, 'فَضْلًا مِنَ اللَّهِ وَنِعْمَةً ۚ وَاللَّهُ عَلِيمٌ حَكِيمٌ', 49, 8, 26, 103, 516, '(و این برای شما بعنوان) فضل و نعمتی از سوی خداست؛ و خداوند دانا و حکیم است.!'),
(4621, 4621, 'وَإِنْ طَائِفَتَانِ مِنَ الْمُؤْمِنِينَ اقْتَتَلُوا فَأَصْلِحُوا بَيْنَهُمَا ۖ فَإِنْ بَغَتْ إِحْدَاهُمَا عَلَى الْأُخْرَىٰ فَقَاتِلُوا الَّتِي تَبْغِي حَتَّىٰ تَفِيءَ إِلَىٰ أَمْرِ اللَّهِ ۚ فَإِنْ فَاءَتْ فَأَصْلِحُوا بَيْنَهُمَا بِالْعَدْلِ وَأَقْسِطُوا ۖ إِنَّ اللَّهَ يُحِبُّ الْمُقْسِطِينَ', 49, 9, 26, 103, 516, 'و هرگاه دو گروه از مؤمنان با هم به نزاع و جنگ پردازند، آنها را آشتی دهید؛ و اگر یکی از آن دو بر دیگری تجاوز کند، با گروه متجاوز پیکار کنید تا به فرمان خدا بازگردد؛ و هرگاه بازگشت (و زمینه صلح فراهم شد)، در میان آن دو به عدالت صلح برقرار سازید؛ و عدالت پیشه کنید که خداوند عدالت پیشگان را دوست می‌دارد.'),
(4622, 4622, 'إِنَّمَا الْمُؤْمِنُونَ إِخْوَةٌ فَأَصْلِحُوا بَيْنَ أَخَوَيْكُمْ ۚ وَاتَّقُوا اللَّهَ لَعَلَّكُمْ تُرْحَمُونَ', 49, 10, 26, 103, 516, 'مؤمنان برادر یکدیگرند؛ پس دو برادر خود را صلح و آشتی دهید و تقوای الهی پیشه کنید، باشد که مشمول رحمت او شوید!'),
(4623, 4623, 'يَا أَيُّهَا الَّذِينَ آمَنُوا لَا يَسْخَرْ قَوْمٌ مِنْ قَوْمٍ عَسَىٰ أَنْ يَكُونُوا خَيْرًا مِنْهُمْ وَلَا نِسَاءٌ مِنْ نِسَاءٍ عَسَىٰ أَنْ يَكُنَّ خَيْرًا مِنْهُنَّ ۖ وَلَا تَلْمِزُوا أَنْفُسَكُمْ وَلَا تَنَابَزُوا بِالْأَلْقَابِ ۖ بِئْسَ الِاسْمُ الْفُسُوقُ بَعْدَ الْإِيمَانِ ۚ وَمَنْ لَمْ يَتُبْ فَأُولَٰئِكَ هُمُ الظَّالِمُونَ', 49, 11, 26, 103, 516, 'ای کسانی که ایمان آورده‌اید! نباید گروهی از مردان شما گروه دیگر را مسخره کنند، شاید آنها از اینها بهتر باشند؛ و نه زنانی زنان دیگر را، شاید آنان بهتر از اینان باشند؛ و یکدیگر را مورد طعن و عیبجویی قرار ندهید و با القاب زشت و ناپسند یکدیگر را یاد نکنید، بسیار بد است که بر کسی پس از ایمان نام کفرآمیز بگذارید؛ و آنها که توبه نکنند، ظالم و ستمگرند!'),
(4624, 4624, 'يَا أَيُّهَا الَّذِينَ آمَنُوا اجْتَنِبُوا كَثِيرًا مِنَ الظَّنِّ إِنَّ بَعْضَ الظَّنِّ إِثْمٌ ۖ وَلَا تَجَسَّسُوا وَلَا يَغْتَبْ بَعْضُكُمْ بَعْضًا ۚ أَيُحِبُّ أَحَدُكُمْ أَنْ يَأْكُلَ لَحْمَ أَخِيهِ مَيْتًا فَكَرِهْتُمُوهُ ۚ وَاتَّقُوا اللَّهَ ۚ إِنَّ اللَّهَ تَوَّابٌ رَحِيمٌ', 49, 12, 26, 103, 517, 'ای کسانی که ایمان آورده‌اید! از بسیاری از گمانها بپرهیزید، چرا که بعضی از گمانها گناه است؛ و هرگز (در کار دیگران) تجسّس نکنید؛ و هیچ یک از شما دیگری را غیبت نکند، آیا کسی از شما دوست دارد که گوشت برادر مرده خود را بخورد؟! (به یقین) همه شما از این امر کراهت دارید؛ تقوای الهی پیشه کنید که خداوند توبه‌پذیر و مهربان است!'),
(4625, 4625, 'يَا أَيُّهَا النَّاسُ إِنَّا خَلَقْنَاكُمْ مِنْ ذَكَرٍ وَأُنْثَىٰ وَجَعَلْنَاكُمْ شُعُوبًا وَقَبَائِلَ لِتَعَارَفُوا ۚ إِنَّ أَكْرَمَكُمْ عِنْدَ اللَّهِ أَتْقَاكُمْ ۚ إِنَّ اللَّهَ عَلِيمٌ خَبِيرٌ', 49, 13, 26, 103, 517, 'ای مردم! ما شما را از یک مرد و زن آفریدیم و شما را تیره‌ها و قبیله‌ها قرار دادیم تا یکدیگر را بشناسید؛ (اینها ملاک امتیاز نیست،) گرامی‌ترین شما نزد خداوند با تقواترین شماست؛ خداوند دانا و آگاه است!'),
(4626, 4626, ' قَالَتِ الْأَعْرَابُ آمَنَّا ۖ قُلْ لَمْ تُؤْمِنُوا وَلَٰكِنْ قُولُوا أَسْلَمْنَا وَلَمَّا يَدْخُلِ الْإِيمَانُ فِي قُلُوبِكُمْ ۖ وَإِنْ تُطِيعُوا اللَّهَ وَرَسُولَهُ لَا يَلِتْكُمْ مِنْ أَعْمَالِكُمْ شَيْئًا ۚ إِنَّ اللَّهَ غَفُورٌ رَحِيمٌ', 49, 14, 26, 104, 517, 'عربهای بادیه‌نشین گفتند: «ایمان آورده‌ایم» بگو: «شما ایمان نیاورده‌اید، ولی بگویید اسلام آورده‌ایم، امّا هنوز ایمان وارد قلب شما نشده است! و اگر از خدا و رسولش اطاعت کنید، چیزی از پاداش کارهای شما را فروگذار نمی‌کند، خداوند، آمرزنده مهربان است.»'),
(4627, 4627, 'إِنَّمَا الْمُؤْمِنُونَ الَّذِينَ آمَنُوا بِاللَّهِ وَرَسُولِهِ ثُمَّ لَمْ يَرْتَابُوا وَجَاهَدُوا بِأَمْوَالِهِمْ وَأَنْفُسِهِمْ فِي سَبِيلِ اللَّهِ ۚ أُولَٰئِكَ هُمُ الصَّادِقُونَ', 49, 15, 26, 104, 517, 'مؤمنان واقعی تنها کسانی هستند که به خدا و رسولش ایمان آورده‌اند، سپس هرگز شکّ و تردیدی به خود راه نداده و با اموال و جانهای خود در راه خدا جهاد کرده‌اند؛ آنها راستگویانند.'),
(4628, 4628, 'قُلْ أَتُعَلِّمُونَ اللَّهَ بِدِينِكُمْ وَاللَّهُ يَعْلَمُ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ ۚ وَاللَّهُ بِكُلِّ شَيْءٍ عَلِيمٌ', 49, 16, 26, 104, 517, 'بگو: «آیا خدا را از ایمان خود با خبر می‌سازید؟! او تمام آنچه را در آسمانها و زمین است می‌داند؛ و خداوند از همه چیز آگاه است!»'),
(4629, 4629, 'يَمُنُّونَ عَلَيْكَ أَنْ أَسْلَمُوا ۖ قُلْ لَا تَمُنُّوا عَلَيَّ إِسْلَامَكُمْ ۖ بَلِ اللَّهُ يَمُنُّ عَلَيْكُمْ أَنْ هَدَاكُمْ لِلْإِيمَانِ إِنْ كُنْتُمْ صَادِقِينَ', 49, 17, 26, 104, 517, 'آنها بر تو منّت می‌نهند که اسلام آورده‌اند؛ بگو: «اسلام آوردن خود را بر من منّت نگذارید، بلکه خداوند بر شما منّت می‌نهد که شما را به سوی ایمان هدایت کرده است، اگر (در ادّعای ایمان) راستگو هستید!'),
(4630, 4630, 'إِنَّ اللَّهَ يَعْلَمُ غَيْبَ السَّمَاوَاتِ وَالْأَرْضِ ۚ وَاللَّهُ بَصِيرٌ بِمَا تَعْمَلُونَ', 49, 18, 26, 104, 517, 'خداوند غیبت آسمانها و زمین را می‌داند و نسبت به آنچه انجام می‌دهید بیناست!»'),
(4631, 4631, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ ق ۚ وَالْقُرْآنِ الْمَجِيدِ', 50, 1, 26, 104, 518, 'ق، سوگند به قرآن مجید (که قیامت و رستاخیز حقّ است)!'),
(4632, 4632, 'بَلْ عَجِبُوا أَنْ جَاءَهُمْ مُنْذِرٌ مِنْهُمْ فَقَالَ الْكَافِرُونَ هَٰذَا شَيْءٌ عَجِيبٌ', 50, 2, 26, 104, 518, 'آنها تعجّب کردند که پیامبری انذارگر از میان خودشان آمده؛ و کافران گفتند: «این چیز عجیبی است!'),
(4633, 4633, 'أَإِذَا مِتْنَا وَكُنَّا تُرَابًا ۖ ذَٰلِكَ رَجْعٌ بَعِيدٌ', 50, 3, 26, 104, 518, 'آیا هنگامی که مُردیم و خاک شدیم (دوباره به زندگی بازمی‌گردیم)؟! این بازگشتی بعید است!»'),
(4634, 4634, 'قَدْ عَلِمْنَا مَا تَنْقُصُ الْأَرْضُ مِنْهُمْ ۖ وَعِنْدَنَا كِتَابٌ حَفِيظٌ', 50, 4, 26, 104, 518, 'ولی ما می‌دانیم آنچه را زمین از بدن آنها می‌کاهد؛ و نزد ما کتابی است که همه چیز در آن محفوظ است!'),
(4635, 4635, 'بَلْ كَذَّبُوا بِالْحَقِّ لَمَّا جَاءَهُمْ فَهُمْ فِي أَمْرٍ مَرِيجٍ', 50, 5, 26, 104, 518, 'آنها حقّ را هنگامی که به سراغشان آمد تکذیب کردند؛ از این رو پیوسته در کار پراکنده خود متحیّرند!'),
(4636, 4636, 'أَفَلَمْ يَنْظُرُوا إِلَى السَّمَاءِ فَوْقَهُمْ كَيْفَ بَنَيْنَاهَا وَزَيَّنَّاهَا وَمَا لَهَا مِنْ فُرُوجٍ', 50, 6, 26, 104, 518, 'آیا آنان به آسمان بالای سرشان نگاه نکردند که چگونه ما آن را بنا کرده‌ایم، و چگونه آن را (بوسیله ستارگان) زینت بخشیده‌ایم و هیچ شکاف و شکستی در آن نیست؟!'),
(4637, 4637, 'وَالْأَرْضَ مَدَدْنَاهَا وَأَلْقَيْنَا فِيهَا رَوَاسِيَ وَأَنْبَتْنَا فِيهَا مِنْ كُلِّ زَوْجٍ بَهِيجٍ', 50, 7, 26, 104, 518, 'و زمین را گسترش دادیم و در آن کوه‌هائی عظیم و استوار افکندیم و از هر نوع گیاه بهجت‌انگیز در آن رویاندیم،'),
(4638, 4638, 'تَبْصِرَةً وَذِكْرَىٰ لِكُلِّ عَبْدٍ مُنِيبٍ', 50, 8, 26, 104, 518, 'تا وسیله بینایی و یادآوری برای هر بنده توبه کاری باشد!'),
(4639, 4639, 'وَنَزَّلْنَا مِنَ السَّمَاءِ مَاءً مُبَارَكًا فَأَنْبَتْنَا بِهِ جَنَّاتٍ وَحَبَّ الْحَصِيدِ', 50, 9, 26, 104, 518, 'و از آسمان، آبی پربرکت نازل کردیم، و بوسیله آن باغها و دانه‌هایی را که درو می‌کند رویاندیم،'),
(4640, 4640, 'وَالنَّخْلَ بَاسِقَاتٍ لَهَا طَلْعٌ نَضِيدٌ', 50, 10, 26, 104, 518, 'و نخلهای بلندقامت که میوه‌های متراکم دارند؛'),
(4641, 4641, 'رِزْقًا لِلْعِبَادِ ۖ وَأَحْيَيْنَا بِهِ بَلْدَةً مَيْتًا ۚ كَذَٰلِكَ الْخُرُوجُ', 50, 11, 26, 104, 518, 'همه اینها برای روزی‌بخشیدن به بندگان است و بوسیله باران سرزمین مرده را زنده کردیم؛ (آری) زنده‌شدن مُردگان نیز همین گونه است!'),
(4642, 4642, 'كَذَّبَتْ قَبْلَهُمْ قَوْمُ نُوحٍ وَأَصْحَابُ الرَّسِّ وَثَمُودُ', 50, 12, 26, 104, 518, 'پیش از آنان قوم نوح و «اصحاب الرسّ» = قومی که در یمامه زندگی می‌کردند و پیامبری به نام حنظله داشتند و قوم ثمود (پیامبرانشان را) تکذیب کردند،'),
(4643, 4643, 'وَعَادٌ وَفِرْعَوْنُ وَإِخْوَانُ لُوطٍ', 50, 13, 26, 104, 518, 'و همچنین قوم عاد و فرعون و قوم لوط،'),
(4644, 4644, 'وَأَصْحَابُ الْأَيْكَةِ وَقَوْمُ تُبَّعٍ ۚ كُلٌّ كَذَّبَ الرُّسُلَ فَحَقَّ وَعِيدِ', 50, 14, 26, 104, 518, 'و «اصحاب الایکه» = قوم شعیب‌ و قوم تبّع (که در سرزمین یمن زندگی می‌کردند)، هر یک از آنها فرستادگان الهی را تکذیب کردند و وعده عذاب درباره آنان تحقّق یافت!'),
(4645, 4645, 'أَفَعَيِينَا بِالْخَلْقِ الْأَوَّلِ ۚ بَلْ هُمْ فِي لَبْسٍ مِنْ خَلْقٍ جَدِيدٍ', 50, 15, 26, 104, 518, 'آیا ما از آفرینش نخستین عاجز ماندیم (که قادر بر آفرینش رستاخیز نباشیم)؟! ولی آنها (با این همه دلایل روشن) باز در آفرینش جدید تردید دارند!');
INSERT INTO `Quran` (`rowid`, `id`, `Arabic`, `Chapter`, `Verse`, `Juz`, `Hizb`, `Page`, `tr`) VALUES
(4646, 4646, 'وَلَقَدْ خَلَقْنَا الْإِنْسَانَ وَنَعْلَمُ مَا تُوَسْوِسُ بِهِ نَفْسُهُ ۖ وَنَحْنُ أَقْرَبُ إِلَيْهِ مِنْ حَبْلِ الْوَرِيدِ', 50, 16, 26, 104, 519, 'ما انسان را آفریدیم و وسوسه‌های نفس او را می‌دانیم، و ما به او از رگ قلبش نزدیکتریم!'),
(4647, 4647, 'إِذْ يَتَلَقَّى الْمُتَلَقِّيَانِ عَنِ الْيَمِينِ وَعَنِ الشِّمَالِ قَعِيدٌ', 50, 17, 26, 104, 519, '(به خاطر بیاورید) هنگامی را که دو فرشته راست و چپ که ملازم انسانند اعمال او را دریافت می‌دارند؛'),
(4648, 4648, 'مَا يَلْفِظُ مِنْ قَوْلٍ إِلَّا لَدَيْهِ رَقِيبٌ عَتِيدٌ', 50, 18, 26, 104, 519, 'انسان هیچ سخنی را بر زبان نمی‌آورد مگر اینکه همان دم، فرشته‌ای مراقب و آماده برای انجام مأموریت (و ضبط آن) است!'),
(4649, 4649, 'وَجَاءَتْ سَكْرَةُ الْمَوْتِ بِالْحَقِّ ۖ ذَٰلِكَ مَا كُنْتَ مِنْهُ تَحِيدُ', 50, 19, 26, 104, 519, 'و سرانجام، سکرات (و بیخودی در آستانه) مرگ بحق فرامی‌رسد (و به انسان گفته می‌شود:) این همان چیزی است که تو از آن می‌گریختی!'),
(4650, 4650, 'وَنُفِخَ فِي الصُّورِ ۚ ذَٰلِكَ يَوْمُ الْوَعِيدِ', 50, 20, 26, 104, 519, 'و در «صور» دمیده می‌شود؛ آن روز، روز تحقّق وعده وحشتناک است!'),
(4651, 4651, 'وَجَاءَتْ كُلُّ نَفْسٍ مَعَهَا سَائِقٌ وَشَهِيدٌ', 50, 21, 26, 104, 519, 'هر انسانی وارد محشر می‌گردد در حالی که همراه او حرکت دهنده و گواهی است!'),
(4652, 4652, 'لَقَدْ كُنْتَ فِي غَفْلَةٍ مِنْ هَٰذَا فَكَشَفْنَا عَنْكَ غِطَاءَكَ فَبَصَرُكَ الْيَوْمَ حَدِيدٌ', 50, 22, 26, 104, 519, '(به او خطاب می‌شود:) تو از این صحنه (و دادگاه بزرگ) غافل بودی و ما پرده را از چشم تو کنار زدیم، و امروز چشمت کاملاً تیزبین است!'),
(4653, 4653, 'وَقَالَ قَرِينُهُ هَٰذَا مَا لَدَيَّ عَتِيدٌ', 50, 23, 26, 104, 519, 'فرشته همنشین او می‌گوید: «این نامه اعمال اوست که نزد من حاضر و آماده است!»'),
(4654, 4654, 'أَلْقِيَا فِي جَهَنَّمَ كُلَّ كَفَّارٍ عَنِيدٍ', 50, 24, 26, 104, 519, '(خداوند فرمان می‌دهد:) هر کافر متکبّر لجوج را در جهنّم افکنید!'),
(4655, 4655, 'مَنَّاعٍ لِلْخَيْرِ مُعْتَدٍ مُرِيبٍ', 50, 25, 26, 104, 519, 'آن کسی که به شدّت مانع خیر و متجاوز و در شکّ و تردید است (حتی دیگران را به تردید می افکند)؛'),
(4656, 4656, 'الَّذِي جَعَلَ مَعَ اللَّهِ إِلَٰهًا آخَرَ فَأَلْقِيَاهُ فِي الْعَذَابِ الشَّدِيدِ', 50, 26, 26, 104, 519, 'همان کسی که معبود دیگری با خدا قرار داده، (آری) او را در عذاب شدید بیفکنید!'),
(4657, 4657, ' قَالَ قَرِينُهُ رَبَّنَا مَا أَطْغَيْتُهُ وَلَٰكِنْ كَانَ فِي ضَلَالٍ بَعِيدٍ', 50, 27, 26, 104, 519, 'و همنشینش (از شیاطین) می‌گوید: «پروردگارا! من او را به طغیان وانداشتم، لکن او خود در گمراهی دور و درازی بود!»'),
(4658, 4658, 'قَالَ لَا تَخْتَصِمُوا لَدَيَّ وَقَدْ قَدَّمْتُ إِلَيْكُمْ بِالْوَعِيدِ', 50, 28, 26, 104, 519, '(خداوند) می‌گوید: «نزد من جدال و مخاصمه نکنید؛ من پیشتر به شما هشدار داده‌ام (و اتمام حجّت کرده‌ام)!'),
(4659, 4659, 'مَا يُبَدَّلُ الْقَوْلُ لَدَيَّ وَمَا أَنَا بِظَلَّامٍ لِلْعَبِيدِ', 50, 29, 26, 104, 519, 'سخن من تغییر ناپذیر است، و من هرگز به بندگان ستم نخواهم کرد!»'),
(4660, 4660, 'يَوْمَ نَقُولُ لِجَهَنَّمَ هَلِ امْتَلَأْتِ وَتَقُولُ هَلْ مِنْ مَزِيدٍ', 50, 30, 26, 104, 519, '(به خاطر بیاورید) روزی را که به جهنّم می‌گوییم: «آیا پر شده‌ای؟» و او می‌گوید: «آیا افزون بر این هم هست؟!»'),
(4661, 4661, 'وَأُزْلِفَتِ الْجَنَّةُ لِلْمُتَّقِينَ غَيْرَ بَعِيدٍ', 50, 31, 26, 104, 519, '(در آن روز) بهشت را به پرهیزگاران نزدیک می‌کنند، و فاصله‌ای از آنان ندارد!'),
(4662, 4662, 'هَٰذَا مَا تُوعَدُونَ لِكُلِّ أَوَّابٍ حَفِيظٍ', 50, 32, 26, 104, 519, 'این چیزی است که به شما وعده داده می‌شود، و برای کسانی است که بسوی خدا بازمی‌گردند و پیمانها و احکام او را حفظ می‌کنند،'),
(4663, 4663, 'مَنْ خَشِيَ الرَّحْمَٰنَ بِالْغَيْبِ وَجَاءَ بِقَلْبٍ مُنِيبٍ', 50, 33, 26, 104, 519, 'آن کس که از خداوند رحمان در نهان بترسد و با قلبی پرانابه در محضر او حاضر شود!'),
(4664, 4664, 'ادْخُلُوهَا بِسَلَامٍ ۖ ذَٰلِكَ يَوْمُ الْخُلُودِ', 50, 34, 26, 104, 519, '(به آنان گفته می‌شود:) بسلامت وارد بهشت شوید، امروز روز جاودانگی است!'),
(4665, 4665, 'لَهُمْ مَا يَشَاءُونَ فِيهَا وَلَدَيْنَا مَزِيدٌ', 50, 35, 26, 104, 519, 'هر چه بخواهند در آنجا برای آنها هست، و نزد ما نعمتهای بیشتری است (که به فکر هیچ کس نمی‌رسد)!'),
(4666, 4666, 'وَكَمْ أَهْلَكْنَا قَبْلَهُمْ مِنْ قَرْنٍ هُمْ أَشَدُّ مِنْهُمْ بَطْشًا فَنَقَّبُوا فِي الْبِلَادِ هَلْ مِنْ مَحِيصٍ', 50, 36, 26, 104, 520, 'چه بسیار اقوامی را که پیش از آنها هلاک کردیم، اقوامی که از آنان قویتر بودند و شهرها (و کشورها) را گشودند؛ آیا راه فراری (از عذاب الهی) وجود دارد!'),
(4667, 4667, 'إِنَّ فِي ذَٰلِكَ لَذِكْرَىٰ لِمَنْ كَانَ لَهُ قَلْبٌ أَوْ أَلْقَى السَّمْعَ وَهُوَ شَهِيدٌ', 50, 37, 26, 104, 520, 'در این تذکّری است برای آن کس که عقل دارد، یا گوش دل فرادهد در حالی که حاضر باشد!'),
(4668, 4668, 'وَلَقَدْ خَلَقْنَا السَّمَاوَاتِ وَالْأَرْضَ وَمَا بَيْنَهُمَا فِي سِتَّةِ أَيَّامٍ وَمَا مَسَّنَا مِنْ لُغُوبٍ', 50, 38, 26, 104, 520, 'ما آسمانها و زمین و آنچه را در میان آنهاست در شش روز = شش دوران‌ آفریدیم، و هیچ گونه رنج و سختی به ما نرسید! (با این حال چگونه زنده‌کردن مُردگان برای ما مشکل است؟!)'),
(4669, 4669, 'فَاصْبِرْ عَلَىٰ مَا يَقُولُونَ وَسَبِّحْ بِحَمْدِ رَبِّكَ قَبْلَ طُلُوعِ الشَّمْسِ وَقَبْلَ الْغُرُوبِ', 50, 39, 26, 104, 520, 'در برابر آنچه آنها می‌گویند شکیبا باش، و پیش از طلوع آفتاب و پیش از غروب تسبیح و حمد پروردگارت را بجا آور،'),
(4670, 4670, 'وَمِنَ اللَّيْلِ فَسَبِّحْهُ وَأَدْبَارَ السُّجُودِ', 50, 40, 26, 104, 520, 'و در بخشی از شب او را تسبیح کن، و بعد از سجده‌ها!'),
(4671, 4671, 'وَاسْتَمِعْ يَوْمَ يُنَادِ الْمُنَادِ مِنْ مَكَانٍ قَرِيبٍ', 50, 41, 26, 104, 520, 'و گوش فرا ده و منتظر روزی باش که منادی از مکانی نزدیک ندا می‌دهد،'),
(4672, 4672, 'يَوْمَ يَسْمَعُونَ الصَّيْحَةَ بِالْحَقِّ ۚ ذَٰلِكَ يَوْمُ الْخُرُوجِ', 50, 42, 26, 104, 520, 'روزی که همگان صیحه رستاخیز را بحق می‌شنوند؛ آن روز، روز خروج (از قبرها) است!'),
(4673, 4673, 'إِنَّا نَحْنُ نُحْيِي وَنُمِيتُ وَإِلَيْنَا الْمَصِيرُ', 50, 43, 26, 104, 520, 'ماییم که زنده می‌کنیم و می‌میرانیم، و بازگشت تنها بسوی ماست!'),
(4674, 4674, 'يَوْمَ تَشَقَّقُ الْأَرْضُ عَنْهُمْ سِرَاعًا ۚ ذَٰلِكَ حَشْرٌ عَلَيْنَا يَسِيرٌ', 50, 44, 26, 104, 520, 'روزی که زمین به سرعت از روی آنها شکافته می‌شود و (از قبرها) خارج می‌گردند؛ و این جمع کردن برای ما آسان است!'),
(4675, 4675, 'نَحْنُ أَعْلَمُ بِمَا يَقُولُونَ ۖ وَمَا أَنْتَ عَلَيْهِمْ بِجَبَّارٍ ۖ فَذَكِّرْ بِالْقُرْآنِ مَنْ يَخَافُ وَعِيدِ', 50, 45, 26, 104, 520, 'ما به آنچه آنها می‌گویند آگاهتریم، و تو مأمور به اجبار آنها (به ایمان) نیستی؛ پس بوسیله قرآن، کسانی را که از عذاب من می‌ترسند متذکّر ساز (وظیفه تو همین است)!'),
(4676, 4676, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ وَالذَّارِيَاتِ ذَرْوًا', 51, 1, 26, 104, 520, 'سوگند به بادهایی که (ابرها را) به حرکت درمی‌آورند،'),
(4677, 4677, 'فَالْحَامِلَاتِ وِقْرًا', 51, 2, 26, 104, 520, 'سوگند به آن ابرها که بار سنگینی (از باران را) با خود حمل می‌کنند،'),
(4678, 4678, 'فَالْجَارِيَاتِ يُسْرًا', 51, 3, 26, 104, 520, 'و سوگند به کشتیهایی که به آسانی به حرکت درمی‌آیند،'),
(4679, 4679, 'فَالْمُقَسِّمَاتِ أَمْرًا', 51, 4, 26, 104, 520, 'و سوگند به فرشتگانی که کارها را تقسیم می‌کنند،'),
(4680, 4680, 'إِنَّمَا تُوعَدُونَ لَصَادِقٌ', 51, 5, 26, 104, 520, '(آری سوگند به همه اینها) که آنچه به شما وعده شده قطعاً راست است؛'),
(4681, 4681, 'وَإِنَّ الدِّينَ لَوَاقِعٌ', 51, 6, 26, 104, 520, 'و بی‌شکّ (رستاخیز) و جزای اعمال واقع‌شدنی است!'),
(4682, 4682, 'وَالسَّمَاءِ ذَاتِ الْحُبُكِ', 51, 7, 26, 104, 521, 'قسم به آسمان که دارای چین و شکنهای زیباست،'),
(4683, 4683, 'إِنَّكُمْ لَفِي قَوْلٍ مُخْتَلِفٍ', 51, 8, 26, 104, 521, 'که شما (درباره قیامت) در گفتاری مختلف و گوناگونید!'),
(4684, 4684, 'يُؤْفَكُ عَنْهُ مَنْ أُفِكَ', 51, 9, 26, 104, 521, '(تنها) کسی از ایمان به آن منحرف می‌شود که از قبول حقّ سرباز می‌زند!'),
(4685, 4685, 'قُتِلَ الْخَرَّاصُونَ', 51, 10, 26, 104, 521, 'کشته باد دروغگویان (و مرگ بر آنها)!'),
(4686, 4686, 'الَّذِينَ هُمْ فِي غَمْرَةٍ سَاهُونَ', 51, 11, 26, 104, 521, 'همانها که در جهل و غفلت فرو رفته‌اند،'),
(4687, 4687, 'يَسْأَلُونَ أَيَّانَ يَوْمُ الدِّينِ', 51, 12, 26, 104, 521, 'و پیوسته سؤال می‌کنند: «روز جزا چه موقع است؟!»'),
(4688, 4688, 'يَوْمَ هُمْ عَلَى النَّارِ يُفْتَنُونَ', 51, 13, 26, 104, 521, '(آری) همان روزی است که آنها را بر آتش می‌سوزانند!'),
(4689, 4689, 'ذُوقُوا فِتْنَتَكُمْ هَٰذَا الَّذِي كُنْتُمْ بِهِ تَسْتَعْجِلُونَ', 51, 14, 26, 104, 521, '(و گفته می‌شود:) بچشید عذاب خود را، این همان چیزی است که برای آن شتاب داشتید!'),
(4690, 4690, 'إِنَّ الْمُتَّقِينَ فِي جَنَّاتٍ وَعُيُونٍ', 51, 15, 26, 104, 521, 'به یقین، پرهیزگاران در باغهای بهشت و در میان چشمه‌ها قرار دارند،'),
(4691, 4691, 'آخِذِينَ مَا آتَاهُمْ رَبُّهُمْ ۚ إِنَّهُمْ كَانُوا قَبْلَ ذَٰلِكَ مُحْسِنِينَ', 51, 16, 26, 104, 521, 'و آنچه پروردگارشان به آنها بخشیده دریافت می‌دارند، زیرا پیش از آن (در سرای دنیا) از نیکوکاران بودند!'),
(4692, 4692, 'كَانُوا قَلِيلًا مِنَ اللَّيْلِ مَا يَهْجَعُونَ', 51, 17, 26, 104, 521, 'آنها کمی از شب را می‌خوابیدند،'),
(4693, 4693, 'وَبِالْأَسْحَارِ هُمْ يَسْتَغْفِرُونَ', 51, 18, 26, 104, 521, 'و در سحرگاهان استغفار می‌کردند،'),
(4694, 4694, 'وَفِي أَمْوَالِهِمْ حَقٌّ لِلسَّائِلِ وَالْمَحْرُومِ', 51, 19, 26, 104, 521, 'و در اموال آنها حقّی برای سائل و محروم بود!'),
(4695, 4695, 'وَفِي الْأَرْضِ آيَاتٌ لِلْمُوقِنِينَ', 51, 20, 26, 104, 521, 'و در زمین آیاتی برای جویندگان یقین است،'),
(4696, 4696, 'وَفِي أَنْفُسِكُمْ ۚ أَفَلَا تُبْصِرُونَ', 51, 21, 26, 104, 521, 'و در وجود خود شما (نیز آیاتی است)؛ آیا نمی‌بینید؟!'),
(4697, 4697, 'وَفِي السَّمَاءِ رِزْقُكُمْ وَمَا تُوعَدُونَ', 51, 22, 26, 104, 521, 'و روزی شما در آسمان است و آنچه به شما وعده داده می‌شود!'),
(4698, 4698, 'فَوَرَبِّ السَّمَاءِ وَالْأَرْضِ إِنَّهُ لَحَقٌّ مِثْلَ مَا أَنَّكُمْ تَنْطِقُونَ', 51, 23, 26, 104, 521, 'سوگند به پروردگار آسمان و زمین که این مطلب حقّ است همان گونه که شما سخن می‌گویید!'),
(4699, 4699, 'هَلْ أَتَاكَ حَدِيثُ ضَيْفِ إِبْرَاهِيمَ الْمُكْرَمِينَ', 51, 24, 26, 104, 521, 'آیا خبر مهمانهای بزرگوار ابراهیم به تو رسیده است؟'),
(4700, 4700, 'إِذْ دَخَلُوا عَلَيْهِ فَقَالُوا سَلَامًا ۖ قَالَ سَلَامٌ قَوْمٌ مُنْكَرُونَ', 51, 25, 26, 104, 521, 'در آن زمان که بر او وارد شدند و گفتند: «سلام بر تو!» او گفت: «سلام بر شما که جمعیّتی ناشناخته‌اید!»'),
(4701, 4701, 'فَرَاغَ إِلَىٰ أَهْلِهِ فَجَاءَ بِعِجْلٍ سَمِينٍ', 51, 26, 26, 104, 521, 'سپس پنهانی به سوی خانواده خود رفت و گوساله فربه (و بریان شده‌ای را برای آنها) آورد،'),
(4702, 4702, 'فَقَرَّبَهُ إِلَيْهِمْ قَالَ أَلَا تَأْكُلُونَ', 51, 27, 26, 104, 521, 'و نزدیک آنها گذارد، (ولی با تعجّب دید دست بسوی غذا نمی‌برند) گفت: «آیا شما غذا نمی‌خورید؟!»'),
(4703, 4703, 'فَأَوْجَسَ مِنْهُمْ خِيفَةً ۖ قَالُوا لَا تَخَفْ ۖ وَبَشَّرُوهُ بِغُلَامٍ عَلِيمٍ', 51, 28, 26, 104, 521, 'و از آنها احساس وحشت کرد، گفتند: «نترس (ما رسولان و فرشتگان پروردگار توایم)!» و او را بشارت به تولّد پسری دانا دادند.'),
(4704, 4704, 'فَأَقْبَلَتِ امْرَأَتُهُ فِي صَرَّةٍ فَصَكَّتْ وَجْهَهَا وَقَالَتْ عَجُوزٌ عَقِيمٌ', 51, 29, 26, 104, 521, 'در این هنگام همسرش جلو آمد در حالی که (از خوشحالی و تعجّب) فریاد می‌کشید به صورت خود زد و گفت: «(آیا پسری خواهم آورد در حالی که) پیرزنی نازا هستم؟!»'),
(4705, 4705, 'قَالُوا كَذَٰلِكِ قَالَ رَبُّكِ ۖ إِنَّهُ هُوَ الْحَكِيمُ الْعَلِيمُ', 51, 30, 26, 104, 521, 'گفتند: «پروردگارت چنین گفته است، و او حکیم و داناست!»'),
(4706, 4706, ' قَالَ فَمَا خَطْبُكُمْ أَيُّهَا الْمُرْسَلُونَ', 51, 31, 27, 105, 522, '(ابراهیم) گفت: «مأموریت شما چیست ای فرستادگان (خدا)؟»'),
(4707, 4707, 'قَالُوا إِنَّا أُرْسِلْنَا إِلَىٰ قَوْمٍ مُجْرِمِينَ', 51, 32, 27, 105, 522, 'گفتند: «ما به سوی قوم مجرمی فرستاده شده‌ایم...'),
(4708, 4708, 'لِنُرْسِلَ عَلَيْهِمْ حِجَارَةً مِنْ طِينٍ', 51, 33, 27, 105, 522, 'تا بارانی از «سنگ - گِل» بر آنها بفرستیم؛'),
(4709, 4709, 'مُسَوَّمَةً عِنْدَ رَبِّكَ لِلْمُسْرِفِينَ', 51, 34, 27, 105, 522, 'سنگهایی که از ناحیه پروردگارت برای اسرافکاران نشان گذاشته شده است!»'),
(4710, 4710, 'فَأَخْرَجْنَا مَنْ كَانَ فِيهَا مِنَ الْمُؤْمِنِينَ', 51, 35, 27, 105, 522, 'ما مؤمنانی را که در آن شهرها (ی قوم لوط) زندگی می‌کردند (قبل از نزول عذاب) خارج کردیم،'),
(4711, 4711, 'فَمَا وَجَدْنَا فِيهَا غَيْرَ بَيْتٍ مِنَ الْمُسْلِمِينَ', 51, 36, 27, 105, 522, 'ولی جز یک خانواده باایمان در تمام آنها نیافتیم!'),
(4712, 4712, 'وَتَرَكْنَا فِيهَا آيَةً لِلَّذِينَ يَخَافُونَ الْعَذَابَ الْأَلِيمَ', 51, 37, 27, 105, 522, 'و در آن (شهرهای بلا دیده) نشانه‌ای روشن برای کسانی که از عذاب دردناک می‌ترسند به جای گذاردیم.'),
(4713, 4713, 'وَفِي مُوسَىٰ إِذْ أَرْسَلْنَاهُ إِلَىٰ فِرْعَوْنَ بِسُلْطَانٍ مُبِينٍ', 51, 38, 27, 105, 522, 'و در (زندگی) موسی نیز (نشانه و درس عبرتی بود) هنگامی که او را با دلیلی آشکار به سوی فرعون فرستادیم؛'),
(4714, 4714, 'فَتَوَلَّىٰ بِرُكْنِهِ وَقَالَ سَاحِرٌ أَوْ مَجْنُونٌ', 51, 39, 27, 105, 522, 'امّا او با تمام وجودش از وی روی برتافت و گفت: «این مرد یا ساحر است یا دیوانه!»'),
(4715, 4715, 'فَأَخَذْنَاهُ وَجُنُودَهُ فَنَبَذْنَاهُمْ فِي الْيَمِّ وَهُوَ مُلِيمٌ', 51, 40, 27, 105, 522, 'از این رو ما او و لشکریانش را گرفتیم و به دریا افکندیم در حالی که در خور سرزنش بود!'),
(4716, 4716, 'وَفِي عَادٍ إِذْ أَرْسَلْنَا عَلَيْهِمُ الرِّيحَ الْعَقِيمَ', 51, 41, 27, 105, 522, 'و (همچنین) در سرگذشت «عاد» (آیتی است) در آن هنگام که تندبادی بی‌باران بر آنها فرستادیم،'),
(4717, 4717, 'مَا تَذَرُ مِنْ شَيْءٍ أَتَتْ عَلَيْهِ إِلَّا جَعَلَتْهُ كَالرَّمِيمِ', 51, 42, 27, 105, 522, 'که بر هیچ چیز نمی‌گذشت مگر اینکه آن را همچون استخوانهای پوسیده می‌ساخت.'),
(4718, 4718, 'وَفِي ثَمُودَ إِذْ قِيلَ لَهُمْ تَمَتَّعُوا حَتَّىٰ حِينٍ', 51, 43, 27, 105, 522, 'و نیز در سرگذشت قوم «ثمود» عبرتی است در آن هنگام که به آنان گفته شد: «مدّتی کوتاه بهره‌مند باشید (و سپس منتظر عذاب)!»'),
(4719, 4719, 'فَعَتَوْا عَنْ أَمْرِ رَبِّهِمْ فَأَخَذَتْهُمُ الصَّاعِقَةُ وَهُمْ يَنْظُرُونَ', 51, 44, 27, 105, 522, 'آنها از فرمان پروردگارشان سرباز زدند، و صاعقه آنان را فراگرفت در حالی که (خیره خیره) نگاه می‌کردند (بی‌آنکه قدرت دفاع داشته باشند)!'),
(4720, 4720, 'فَمَا اسْتَطَاعُوا مِنْ قِيَامٍ وَمَا كَانُوا مُنْتَصِرِينَ', 51, 45, 27, 105, 522, 'چنان بر زمین افتادند که توان برخاستن نداشتند و نتوانستند از کسی یاری طلبند!'),
(4721, 4721, 'وَقَوْمَ نُوحٍ مِنْ قَبْلُ ۖ إِنَّهُمْ كَانُوا قَوْمًا فَاسِقِينَ', 51, 46, 27, 105, 522, 'همچنین قوم نوح را پیش از آنها هلاک کردیم، چرا که قوم فاسقی بودند!'),
(4722, 4722, 'وَالسَّمَاءَ بَنَيْنَاهَا بِأَيْدٍ وَإِنَّا لَمُوسِعُونَ', 51, 47, 27, 105, 522, 'و ما آسمان را با قدرت بنا کردیم، و همواره آن را وسعت می‌بخشیم!'),
(4723, 4723, 'وَالْأَرْضَ فَرَشْنَاهَا فَنِعْمَ الْمَاهِدُونَ', 51, 48, 27, 105, 522, 'و زمین را گستردیم، و چه خوب گستراننده‌ای هستیم!'),
(4724, 4724, 'وَمِنْ كُلِّ شَيْءٍ خَلَقْنَا زَوْجَيْنِ لَعَلَّكُمْ تَذَكَّرُونَ', 51, 49, 27, 105, 522, 'و از هر چیز دو جفت آفریدیم، شاید متذکّر شوید!'),
(4725, 4725, 'فَفِرُّوا إِلَى اللَّهِ ۖ إِنِّي لَكُمْ مِنْهُ نَذِيرٌ مُبِينٌ', 51, 50, 27, 105, 522, 'پس به سوی خدا بگریزید، که من از سوی او برای شما بیم‌دهنده‌ای آشکارم!'),
(4726, 4726, 'وَلَا تَجْعَلُوا مَعَ اللَّهِ إِلَٰهًا آخَرَ ۖ إِنِّي لَكُمْ مِنْهُ نَذِيرٌ مُبِينٌ', 51, 51, 27, 105, 522, 'و با خدا معبود دیگری قرار ندهید، که من برای شما از سوی او بیم‌دهنده‌ای آشکارم!'),
(4727, 4727, 'كَذَٰلِكَ مَا أَتَى الَّذِينَ مِنْ قَبْلِهِمْ مِنْ رَسُولٍ إِلَّا قَالُوا سَاحِرٌ أَوْ مَجْنُونٌ', 51, 52, 27, 105, 523, 'این گونه است که هیچ پیامبری قبل از اینها بسوی قومی فرستاده نشد مگر اینکه گفتند: «او ساحر است یا دیوانه!»'),
(4728, 4728, 'أَتَوَاصَوْا بِهِ ۚ بَلْ هُمْ قَوْمٌ طَاغُونَ', 51, 53, 27, 105, 523, 'آیا یکدیگر را به آن سفارش می‌کردند (که همه چنین تهمتی بزنند)؟! نه، بلکه آنها قومی طغیانگرند.'),
(4729, 4729, 'فَتَوَلَّ عَنْهُمْ فَمَا أَنْتَ بِمَلُومٍ', 51, 54, 27, 105, 523, 'حال که چنین است از آنها روی بگردان که هرگز در خور ملامت نخواهی بود؛'),
(4730, 4730, 'وَذَكِّرْ فَإِنَّ الذِّكْرَىٰ تَنْفَعُ الْمُؤْمِنِينَ', 51, 55, 27, 105, 523, 'و پیوسته تذکّر ده، زیرا تذکّر مؤمنان را سود می‌بخشد.'),
(4731, 4731, 'وَمَا خَلَقْتُ الْجِنَّ وَالْإِنْسَ إِلَّا لِيَعْبُدُونِ', 51, 56, 27, 105, 523, 'من جنّ و انس را نیافریدم جز برای اینکه عبادتم کنند (و از این راه تکامل یابند و به من نزدیک شوند)!'),
(4732, 4732, 'مَا أُرِيدُ مِنْهُمْ مِنْ رِزْقٍ وَمَا أُرِيدُ أَنْ يُطْعِمُونِ', 51, 57, 27, 105, 523, 'هرگز از آنها روزی نمی‌خواهم، و نمی‌خواهم مرا اطعام کنند!'),
(4733, 4733, 'إِنَّ اللَّهَ هُوَ الرَّزَّاقُ ذُو الْقُوَّةِ الْمَتِينُ', 51, 58, 27, 105, 523, 'خداوند روزی‌دهنده و صاحب قوّت و قدرت است!'),
(4734, 4734, 'فَإِنَّ لِلَّذِينَ ظَلَمُوا ذَنُوبًا مِثْلَ ذَنُوبِ أَصْحَابِهِمْ فَلَا يَسْتَعْجِلُونِ', 51, 59, 27, 105, 523, 'و برای کسانی که ستم کردند، سهم بزرگی از عذاب است همانند سهم یارانشان (از اقوام ستمگر پیشین)؛ بنابر این عجله نکنند!'),
(4735, 4735, 'فَوَيْلٌ لِلَّذِينَ كَفَرُوا مِنْ يَوْمِهِمُ الَّذِي يُوعَدُونَ', 51, 60, 27, 105, 523, 'پس وای بر کسانی که کافر شدند از روزی که به آنها وعده داده می‌شود!'),
(4736, 4736, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ وَالطُّورِ', 52, 1, 27, 105, 523, 'سوگند به کوه طور،'),
(4737, 4737, 'وَكِتَابٍ مَسْطُورٍ', 52, 2, 27, 105, 523, 'و کتابی که نوشته شده،'),
(4738, 4738, 'فِي رَقٍّ مَنْشُورٍ', 52, 3, 27, 105, 523, 'در صفحه‌ای گسترده،'),
(4739, 4739, 'وَالْبَيْتِ الْمَعْمُورِ', 52, 4, 27, 105, 523, 'و سوگند به «بیت المعمور»،'),
(4740, 4740, 'وَالسَّقْفِ الْمَرْفُوعِ', 52, 5, 27, 105, 523, 'و سقف برافراشته،'),
(4741, 4741, 'وَالْبَحْرِ الْمَسْجُورِ', 52, 6, 27, 105, 523, 'و دریای مملوّ و برافروخته،'),
(4742, 4742, 'إِنَّ عَذَابَ رَبِّكَ لَوَاقِعٌ', 52, 7, 27, 105, 523, 'که عذاب پروردگارت واقع می‌شود،'),
(4743, 4743, 'مَا لَهُ مِنْ دَافِعٍ', 52, 8, 27, 105, 523, 'و چیزی از آن مانع نخواهد بود!'),
(4744, 4744, 'يَوْمَ تَمُورُ السَّمَاءُ مَوْرًا', 52, 9, 27, 105, 523, '(این عذاب الهی) در آن روزی است که آسمان به شدّت به حرکت درمی‌آید،'),
(4745, 4745, 'وَتَسِيرُ الْجِبَالُ سَيْرًا', 52, 10, 27, 105, 523, 'و کوه‌ها از جا کنده و متحرّک می‌شوند!'),
(4746, 4746, 'فَوَيْلٌ يَوْمَئِذٍ لِلْمُكَذِّبِينَ', 52, 11, 27, 105, 523, 'وای در آن روز بر تکذیب‌کنندگان،'),
(4747, 4747, 'الَّذِينَ هُمْ فِي خَوْضٍ يَلْعَبُونَ', 52, 12, 27, 105, 523, 'همانها که در سخنان باطل به بازی مشغولند!'),
(4748, 4748, 'يَوْمَ يُدَعُّونَ إِلَىٰ نَارِ جَهَنَّمَ دَعًّا', 52, 13, 27, 105, 523, 'در آن روز که آنها را بزور به سوی آتش دوزخ می‌رانند!'),
(4749, 4749, 'هَٰذِهِ النَّارُ الَّتِي كُنْتُمْ بِهَا تُكَذِّبُونَ', 52, 14, 27, 105, 523, '(به آنها می‌گویند:) این همان آتشی است که آن را انکار می‌کردید.'),
(4750, 4750, 'أَفَسِحْرٌ هَٰذَا أَمْ أَنْتُمْ لَا تُبْصِرُونَ', 52, 15, 27, 105, 524, 'آیا این سحر است یا شما نمی‌بینید؟!'),
(4751, 4751, 'اصْلَوْهَا فَاصْبِرُوا أَوْ لَا تَصْبِرُوا سَوَاءٌ عَلَيْكُمْ ۖ إِنَّمَا تُجْزَوْنَ مَا كُنْتُمْ تَعْمَلُونَ', 52, 16, 27, 105, 524, 'در آن وارد شوید و بسوزید؛ می‌خواهید صبر کنید یا نکنید، برای شما یکسان است؛ چرا که تنها به اعمالتان جزا داده می‌شوید!'),
(4752, 4752, 'إِنَّ الْمُتَّقِينَ فِي جَنَّاتٍ وَنَعِيمٍ', 52, 17, 27, 105, 524, 'ولی پرهیزگاران در میان باغهای بهشت و نعمتهای فراوان جای دارند،'),
(4753, 4753, 'فَاكِهِينَ بِمَا آتَاهُمْ رَبُّهُمْ وَوَقَاهُمْ رَبُّهُمْ عَذَابَ الْجَحِيمِ', 52, 18, 27, 105, 524, 'و از آنچه پروردگارشان به آنها داده و آنان را از عذاب دوزخ نگاه داشته است شاد و مسرورند!'),
(4754, 4754, 'كُلُوا وَاشْرَبُوا هَنِيئًا بِمَا كُنْتُمْ تَعْمَلُونَ', 52, 19, 27, 105, 524, '(به آنها گفته می‌شود:) بخورید و بیاشامید گوارا؛ اینها در برابر اعمالی است که انجام می‌دادید!'),
(4755, 4755, 'مُتَّكِئِينَ عَلَىٰ سُرُرٍ مَصْفُوفَةٍ ۖ وَزَوَّجْنَاهُمْ بِحُورٍ عِينٍ', 52, 20, 27, 105, 524, 'این در حالی که بر تختهای صف‌کشیده در کنار هم تکیه می‌زنند، و «حور العین» را به همسری آنها درمی‌آوریم!'),
(4756, 4756, 'وَالَّذِينَ آمَنُوا وَاتَّبَعَتْهُمْ ذُرِّيَّتُهُمْ بِإِيمَانٍ أَلْحَقْنَا بِهِمْ ذُرِّيَّتَهُمْ وَمَا أَلَتْنَاهُمْ مِنْ عَمَلِهِمْ مِنْ شَيْءٍ ۚ كُلُّ امْرِئٍ بِمَا كَسَبَ رَهِينٌ', 52, 21, 27, 105, 524, 'کسانی که ایمان آوردند و فرزندانشان به پیروی از آنان ایمان اختیار کردند، فرزندانشان را (در بهشت) به آنان ملحق می‌کنیم؛ و از (پاداش) عملشان چیزی نمی‌کاهیم؛ و هر کس در گرو اعمال خویش است!'),
(4757, 4757, 'وَأَمْدَدْنَاهُمْ بِفَاكِهَةٍ وَلَحْمٍ مِمَّا يَشْتَهُونَ', 52, 22, 27, 105, 524, 'و همواره از انواع میوه‌ها و گوشتها -از هر نوع که بخواهند- در اختیارشان می‌گذاریم!'),
(4758, 4758, 'يَتَنَازَعُونَ فِيهَا كَأْسًا لَا لَغْوٌ فِيهَا وَلَا تَأْثِيمٌ', 52, 23, 27, 105, 524, 'آنها در بهشت جامهای پر از شراب طهور را که نه بیهوده‌گویی در آن است و نه گناه، از یکدیگر می‌گیرند!'),
(4759, 4759, ' وَيَطُوفُ عَلَيْهِمْ غِلْمَانٌ لَهُمْ كَأَنَّهُمْ لُؤْلُؤٌ مَكْنُونٌ', 52, 24, 27, 105, 524, 'و پیوسته بر گردشان نوجوانانی برای (خدمت) آنان گردش می‌کنند که همچون مرواریدهای درون صدفند!'),
(4760, 4760, 'وَأَقْبَلَ بَعْضُهُمْ عَلَىٰ بَعْضٍ يَتَسَاءَلُونَ', 52, 25, 27, 105, 524, 'در این هنگام رو به یکدیگر کرده (از گذشته) سؤال می‌نمایند؛'),
(4761, 4761, 'قَالُوا إِنَّا كُنَّا قَبْلُ فِي أَهْلِنَا مُشْفِقِينَ', 52, 26, 27, 105, 524, 'می‌گویند: «ما در میان خانواده خود ترسان بودیم (مبادا گناهان آنها دامن ما را بگیرد)!'),
(4762, 4762, 'فَمَنَّ اللَّهُ عَلَيْنَا وَوَقَانَا عَذَابَ السَّمُومِ', 52, 27, 27, 105, 524, 'امّا خداوند بر ما منّت نهاد و از عذاب‌کشنده ما را حفظ کرد!'),
(4763, 4763, 'إِنَّا كُنَّا مِنْ قَبْلُ نَدْعُوهُ ۖ إِنَّهُ هُوَ الْبَرُّ الرَّحِيمُ', 52, 28, 27, 105, 524, 'ما از پیش او را می‌خواندیم (و می‌پرستیدیم)، که اوست نیکوکار و مهربان!»'),
(4764, 4764, 'فَذَكِّرْ فَمَا أَنْتَ بِنِعْمَتِ رَبِّكَ بِكَاهِنٍ وَلَا مَجْنُونٍ', 52, 29, 27, 105, 524, 'پس تذکّر ده، که به لطف پروردگارت تو کاهن و مجنون نیستی!'),
(4765, 4765, 'أَمْ يَقُولُونَ شَاعِرٌ نَتَرَبَّصُ بِهِ رَيْبَ الْمَنُونِ', 52, 30, 27, 105, 524, 'بلکه آنها می‌گویند: «او شاعری است که ما انتظار مرگش را می‌کشیم!»'),
(4766, 4766, 'قُلْ تَرَبَّصُوا فَإِنِّي مَعَكُمْ مِنَ الْمُتَرَبِّصِينَ', 52, 31, 27, 105, 524, 'بگو: «انتظار بکشید که من هم با (شما انتظار می‌کشم شما انتظار مرگ مرا، و من انتظار نابودی شما را با عذاب الهی)!»'),
(4767, 4767, 'أَمْ تَأْمُرُهُمْ أَحْلَامُهُمْ بِهَٰذَا ۚ أَمْ هُمْ قَوْمٌ طَاغُونَ', 52, 32, 27, 105, 525, 'آیا عقلهایشان آنها را به این اعمال دستور می‌دهد، یا قومی طغیانگرند؟'),
(4768, 4768, 'أَمْ يَقُولُونَ تَقَوَّلَهُ ۚ بَلْ لَا يُؤْمِنُونَ', 52, 33, 27, 105, 525, 'یا می‌گویند: «قرآن را به خدا افترا بسته»، ولی آنان ایمان ندارند.'),
(4769, 4769, 'فَلْيَأْتُوا بِحَدِيثٍ مِثْلِهِ إِنْ كَانُوا صَادِقِينَ', 52, 34, 27, 105, 525, 'اگر راست می‌گویند سخنی همانند آن بیاورند!'),
(4770, 4770, 'أَمْ خُلِقُوا مِنْ غَيْرِ شَيْءٍ أَمْ هُمُ الْخَالِقُونَ', 52, 35, 27, 105, 525, 'یا آنها بی هیچ آفریده شده‌اند، یا خود خالق خویشند؟!'),
(4771, 4771, 'أَمْ خَلَقُوا السَّمَاوَاتِ وَالْأَرْضَ ۚ بَلْ لَا يُوقِنُونَ', 52, 36, 27, 105, 525, 'آیا آنها آسمانها و زمین را آفریده‌اند؟! بلکه آنها جویای یقین نیستند!'),
(4772, 4772, 'أَمْ عِنْدَهُمْ خَزَائِنُ رَبِّكَ أَمْ هُمُ الْمُصَيْطِرُونَ', 52, 37, 27, 105, 525, 'آیا خزاین پروردگارت نزد آنهاست؟! یا بر همه چیز عالم سیطره دارند؟!'),
(4773, 4773, 'أَمْ لَهُمْ سُلَّمٌ يَسْتَمِعُونَ فِيهِ ۖ فَلْيَأْتِ مُسْتَمِعُهُمْ بِسُلْطَانٍ مُبِينٍ', 52, 38, 27, 105, 525, 'آیا نردبانی دارند (که به آسمان بالا می‌روند) و بوسیله آن اسرار وحی را می‌شنوند؟! کسی که از آنها این ادّعا را دارد دلیل روشنی بیاورد!'),
(4774, 4774, 'أَمْ لَهُ الْبَنَاتُ وَلَكُمُ الْبَنُونَ', 52, 39, 27, 105, 525, 'آیا سهم خدا دختران است و سهم شما پسران (که فرشتگان را دختران خدا می‌نامید)؟!'),
(4775, 4775, 'أَمْ تَسْأَلُهُمْ أَجْرًا فَهُمْ مِنْ مَغْرَمٍ مُثْقَلُونَ', 52, 40, 27, 105, 525, 'آیا تو از آنها پاداشی می‌طلبی که در زیر بار گران آن قرار دارند؟!'),
(4776, 4776, 'أَمْ عِنْدَهُمُ الْغَيْبُ فَهُمْ يَكْتُبُونَ', 52, 41, 27, 105, 525, 'آیا اسرار غیب نزد آنهاست و از روی آن می‌نویسند؟!'),
(4777, 4777, 'أَمْ يُرِيدُونَ كَيْدًا ۖ فَالَّذِينَ كَفَرُوا هُمُ الْمَكِيدُونَ', 52, 42, 27, 105, 525, 'آیا می‌خواهند نقشه شیطانی برای تو بکشند؟! ولی بدانند خود کافران در دام این نقشه‌ها گرفتار می‌شوند!'),
(4778, 4778, 'أَمْ لَهُمْ إِلَٰهٌ غَيْرُ اللَّهِ ۚ سُبْحَانَ اللَّهِ عَمَّا يُشْرِكُونَ', 52, 43, 27, 105, 525, 'یا معبودی غیر خداوند دارند (که قول یاری به آنها داده)؟! منزّه است خدا از آنچه همتای او قرارمی‌دهند!'),
(4779, 4779, 'وَإِنْ يَرَوْا كِسْفًا مِنَ السَّمَاءِ سَاقِطًا يَقُولُوا سَحَابٌ مَرْكُومٌ', 52, 44, 27, 105, 525, 'آنها (چنان لجوجند که) اگر ببینند قطعه سنگی از آسمان (برای عذابشان) سقوط می‌کند می‌گویند: «این ابر متراکمی است!»'),
(4780, 4780, 'فَذَرْهُمْ حَتَّىٰ يُلَاقُوا يَوْمَهُمُ الَّذِي فِيهِ يُصْعَقُونَ', 52, 45, 27, 105, 525, 'حال که چنین است آنها را رها کن تا روز مرگ خود را ملاقات کنند؛'),
(4781, 4781, 'يَوْمَ لَا يُغْنِي عَنْهُمْ كَيْدُهُمْ شَيْئًا وَلَا هُمْ يُنْصَرُونَ', 52, 46, 27, 105, 525, 'روزی که نقشه‌های آنان سودی به حالشان نخواهد داشت و (از هیچ سو) یاری نمی‌شوند!'),
(4782, 4782, 'وَإِنَّ لِلَّذِينَ ظَلَمُوا عَذَابًا دُونَ ذَٰلِكَ وَلَٰكِنَّ أَكْثَرَهُمْ لَا يَعْلَمُونَ', 52, 47, 27, 105, 525, 'و برای ستمگران عذابی قبل از آن است (در همین جهان)؛ ولی بیشترشان نمی‌دانند!'),
(4783, 4783, 'وَاصْبِرْ لِحُكْمِ رَبِّكَ فَإِنَّكَ بِأَعْيُنِنَا ۖ وَسَبِّحْ بِحَمْدِ رَبِّكَ حِينَ تَقُومُ', 52, 48, 27, 105, 525, 'در راه ابلاغ حکم پروردگارت صبر و استقامت کن، چرا که تو در حفاظت کامل ما قرار داری! و هنگامی که برمی‌خیزی پروردگارت را تسبیح و حمد گوی!'),
(4784, 4784, 'وَمِنَ اللَّيْلِ فَسَبِّحْهُ وَإِدْبَارَ النُّجُومِ', 52, 49, 27, 105, 525, '(همچنین) به هنگام شب او را تسبیح کن و به هنگام پشت کردن ستارگان (و طلوع صبح)!'),
(4785, 4785, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ وَالنَّجْمِ إِذَا هَوَىٰ', 53, 1, 27, 105, 526, 'سوگند به ستاره هنگامی که افول می‌کند،'),
(4786, 4786, 'مَا ضَلَّ صَاحِبُكُمْ وَمَا غَوَىٰ', 53, 2, 27, 105, 526, 'که هرگز دوست شما = محمّد «ص» منحرف نشده و مقصد را گم نکرده است،'),
(4787, 4787, 'وَمَا يَنْطِقُ عَنِ الْهَوَىٰ', 53, 3, 27, 105, 526, 'و هرگز از روی هوای نفس سخن نمی‌گوید!'),
(4788, 4788, 'إِنْ هُوَ إِلَّا وَحْيٌ يُوحَىٰ', 53, 4, 27, 105, 526, 'آنچه می‌گوید چیزی جز وحی که بر او نازل شده نیست!'),
(4789, 4789, 'عَلَّمَهُ شَدِيدُ الْقُوَىٰ', 53, 5, 27, 105, 526, 'آن کس که قدرت عظیمی دارد = جبرئیل امین‌ او را تعلیم داده است؛'),
(4790, 4790, 'ذُو مِرَّةٍ فَاسْتَوَىٰ', 53, 6, 27, 105, 526, 'همان کس که توانایی فوق العاده دارد؛ او سلطه یافت...'),
(4791, 4791, 'وَهُوَ بِالْأُفُقِ الْأَعْلَىٰ', 53, 7, 27, 105, 526, 'در حالی که در اُفق اعلی قرار داشت!'),
(4792, 4792, 'ثُمَّ دَنَا فَتَدَلَّىٰ', 53, 8, 27, 105, 526, 'سپس نزدیکتر و نزدیکتر شد...'),
(4793, 4793, 'فَكَانَ قَابَ قَوْسَيْنِ أَوْ أَدْنَىٰ', 53, 9, 27, 105, 526, 'تا آنکه فاصله او (با پیامبر) به اندازه فاصله دو کمان یا کمتر بود؛'),
(4794, 4794, 'فَأَوْحَىٰ إِلَىٰ عَبْدِهِ مَا أَوْحَىٰ', 53, 10, 27, 105, 526, 'در اینجا خداوند آنچه را وحی کردنی بود به بنده‌اش وحی نمود.'),
(4795, 4795, 'مَا كَذَبَ الْفُؤَادُ مَا رَأَىٰ', 53, 11, 27, 105, 526, 'قلب (پاک او) در آنچه دید هرگز دروغ نگفت.'),
(4796, 4796, 'أَفَتُمَارُونَهُ عَلَىٰ مَا يَرَىٰ', 53, 12, 27, 105, 526, 'آیا با او درباره آنچه (با چشم خود) دیده مجادله می‌کنید؟!'),
(4797, 4797, 'وَلَقَدْ رَآهُ نَزْلَةً أُخْرَىٰ', 53, 13, 27, 105, 526, 'و بار دیگر نیز او را مشاهده کرد،'),
(4798, 4798, 'عِنْدَ سِدْرَةِ الْمُنْتَهَىٰ', 53, 14, 27, 105, 526, 'نزد «سدرة المنتهی»،'),
(4799, 4799, 'عِنْدَهَا جَنَّةُ الْمَأْوَىٰ', 53, 15, 27, 105, 526, 'که «جنت المأوی» در آنجاست!'),
(4800, 4800, 'إِذْ يَغْشَى السِّدْرَةَ مَا يَغْشَىٰ', 53, 16, 27, 105, 526, 'در آن هنگام که چیزی = نور خیره‌کننده‌ای‌ سدرة المنتهی را پوشانده بود،'),
(4801, 4801, 'مَا زَاغَ الْبَصَرُ وَمَا طَغَىٰ', 53, 17, 27, 105, 526, 'چشم او هرگز منحرف نشد و طغیان نکرد (آنچه دید واقعیّت بود)!'),
(4802, 4802, 'لَقَدْ رَأَىٰ مِنْ آيَاتِ رَبِّهِ الْكُبْرَىٰ', 53, 18, 27, 105, 526, 'او پاره‌ای از آیات و نشانه‌های بزرگ پروردگارش را دید!'),
(4803, 4803, 'أَفَرَأَيْتُمُ اللَّاتَ وَالْعُزَّىٰ', 53, 19, 27, 105, 526, 'به من خبر دهید آیا بتهای «لات» و «عزّی»...'),
(4804, 4804, 'وَمَنَاةَ الثَّالِثَةَ الْأُخْرَىٰ', 53, 20, 27, 105, 526, 'و «منات» که سوّمین آنهاست (دختران خدا هستند)؟!'),
(4805, 4805, 'أَلَكُمُ الذَّكَرُ وَلَهُ الْأُنْثَىٰ', 53, 21, 27, 105, 526, 'آیا سهم شما پسر است و سهم او دختر؟! (در حالی که بزعم شما دختران کم ارزش‌ترند!)'),
(4806, 4806, 'تِلْكَ إِذًا قِسْمَةٌ ضِيزَىٰ', 53, 22, 27, 105, 526, 'در این صورت این تقسیمی ناعادلانه است!'),
(4807, 4807, 'إِنْ هِيَ إِلَّا أَسْمَاءٌ سَمَّيْتُمُوهَا أَنْتُمْ وَآبَاؤُكُمْ مَا أَنْزَلَ اللَّهُ بِهَا مِنْ سُلْطَانٍ ۚ إِنْ يَتَّبِعُونَ إِلَّا الظَّنَّ وَمَا تَهْوَى الْأَنْفُسُ ۖ وَلَقَدْ جَاءَهُمْ مِنْ رَبِّهِمُ الْهُدَىٰ', 53, 23, 27, 105, 526, 'اینها فقط نامهایی است که شما و پدرانتان بر آنها گذاشته‌اید (نامهایی بی‌محتوا و اسمهایی بی مسمّا)، و هرگز خداوند دلیل و حجتی بر آن نازل نکرده؛ آنان فقط از گمانهای بی‌اساس و هوای نفس پیروی می‌کنند در حالی که هدایت از سوی پروردگارشان برای آنها آمده است!'),
(4808, 4808, 'أَمْ لِلْإِنْسَانِ مَا تَمَنَّىٰ', 53, 24, 27, 105, 526, 'یا آنچه انسان تمنّا دارد به آن می‌رسد؟!'),
(4809, 4809, 'فَلِلَّهِ الْآخِرَةُ وَالْأُولَىٰ', 53, 25, 27, 105, 526, 'در حالی که آخرت و دنیا از آن خداست!'),
(4810, 4810, ' وَكَمْ مِنْ مَلَكٍ فِي السَّمَاوَاتِ لَا تُغْنِي شَفَاعَتُهُمْ شَيْئًا إِلَّا مِنْ بَعْدِ أَنْ يَأْذَنَ اللَّهُ لِمَنْ يَشَاءُ وَيَرْضَىٰ', 53, 26, 27, 106, 526, 'و چه بسیار فرشتگان آسمانها که شفاعت آنها سودی نمی‌بخشد مگر پس از آنکه خدا برای هر کس بخواهد و راضی باشد اجازه (شفاعت) دهد!'),
(4811, 4811, 'إِنَّ الَّذِينَ لَا يُؤْمِنُونَ بِالْآخِرَةِ لَيُسَمُّونَ الْمَلَائِكَةَ تَسْمِيَةَ الْأُنْثَىٰ', 53, 27, 27, 106, 527, 'کسانی که به آخرت ایمان ندارند، فرشتگان را دختر (خدا) نامگذاری می‌کنند!'),
(4812, 4812, 'وَمَا لَهُمْ بِهِ مِنْ عِلْمٍ ۖ إِنْ يَتَّبِعُونَ إِلَّا الظَّنَّ ۖ وَإِنَّ الظَّنَّ لَا يُغْنِي مِنَ الْحَقِّ شَيْئًا', 53, 28, 27, 106, 527, 'آنها هرگز به این سخن دانشی ندارند، تنها از گمان بی‌پایه پیروی می‌کنند با اینکه «گمان» هرگز انسان را از حقّ بی‌نیاز نمی‌کند!'),
(4813, 4813, 'فَأَعْرِضْ عَنْ مَنْ تَوَلَّىٰ عَنْ ذِكْرِنَا وَلَمْ يُرِدْ إِلَّا الْحَيَاةَ الدُّنْيَا', 53, 29, 27, 106, 527, 'حال که چنین است از کسی که از یاد ما روی می‌گرداند و جز زندگی مادی دنیا را نمی‌طلبد، اعراض کن!'),
(4814, 4814, 'ذَٰلِكَ مَبْلَغُهُمْ مِنَ الْعِلْمِ ۚ إِنَّ رَبَّكَ هُوَ أَعْلَمُ بِمَنْ ضَلَّ عَنْ سَبِيلِهِ وَهُوَ أَعْلَمُ بِمَنِ اهْتَدَىٰ', 53, 30, 27, 106, 527, 'این آخرین حدّ آگاهی آنهاست؛ پروردگار تو کسانی را که از راه او گمراه شده‌اند بهتر می‌شناسد، و (همچنین) هدایت‌یافتگان را از همه بهتر می‌شناسد!'),
(4815, 4815, 'وَلِلَّهِ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ لِيَجْزِيَ الَّذِينَ أَسَاءُوا بِمَا عَمِلُوا وَيَجْزِيَ الَّذِينَ أَحْسَنُوا بِالْحُسْنَى', 53, 31, 27, 106, 527, 'و برای خداست آنچه در آسمانها و آنچه در زمین است تا بدکاران را به کیفر کارهای بدشان برساند و نیکوکاران را در برابر اعمال نیکشان پاداش دهد!'),
(4816, 4816, 'الَّذِينَ يَجْتَنِبُونَ كَبَائِرَ الْإِثْمِ وَالْفَوَاحِشَ إِلَّا اللَّمَمَ ۚ إِنَّ رَبَّكَ وَاسِعُ الْمَغْفِرَةِ ۚ هُوَ أَعْلَمُ بِكُمْ إِذْ أَنْشَأَكُمْ مِنَ الْأَرْضِ وَإِذْ أَنْتُمْ أَجِنَّةٌ فِي بُطُونِ أُمَّهَاتِكُمْ ۖ فَلَا تُزَكُّوا أَنْفُسَكُمْ ۖ هُوَ أَعْلَمُ بِمَنِ اتَّقَىٰ', 53, 32, 27, 106, 527, 'همانها که از گناهان بزرگ و اعمال زشت دوری می‌کنند، جز گناهان صغیره (که گاه آلوده آن می‌شوند)؛ آمرزش پروردگار تو گسترده است؛ او نسبت به شما از همه آگاهتر است از آن هنگام که شما را از زمین آفرید و در آن موقع که بصورت جنینهایی در شکم مادرانتان بودید؛ پس خودستایی نکنید، او پرهیزگاران را بهتر می‌شناسد!'),
(4817, 4817, 'أَفَرَأَيْتَ الَّذِي تَوَلَّىٰ', 53, 33, 27, 106, 527, 'آیا دیدی آن کس را که (از اسلام -یا انفاق-) روی گردان شد؟!'),
(4818, 4818, 'وَأَعْطَىٰ قَلِيلًا وَأَكْدَىٰ', 53, 34, 27, 106, 527, 'و کمی عطا کرد، و از بیشتر امساک نمود!'),
(4819, 4819, 'أَعِنْدَهُ عِلْمُ الْغَيْبِ فَهُوَ يَرَىٰ', 53, 35, 27, 106, 527, 'آیا نزد او علم غیب است و می‌بیند (که دیگران می‌توانند گناهان او را بر دوش گیرند)؟!'),
(4820, 4820, 'أَمْ لَمْ يُنَبَّأْ بِمَا فِي صُحُفِ مُوسَىٰ', 53, 36, 27, 106, 527, 'یا از آنچه در کتب موسی نازل گردیده با خبر نشده است؟!'),
(4821, 4821, 'وَإِبْرَاهِيمَ الَّذِي وَفَّىٰ', 53, 37, 27, 106, 527, 'و در کتب ابراهیم، همان کسی که وظیفه خود را بطور کامل ادا کرد،'),
(4822, 4822, 'أَلَّا تَزِرُ وَازِرَةٌ وِزْرَ أُخْرَىٰ', 53, 38, 27, 106, 527, 'که هیچ کس بار گناه دیگری را بر دوش نمی‌گیرد،'),
(4823, 4823, 'وَأَنْ لَيْسَ لِلْإِنْسَانِ إِلَّا مَا سَعَىٰ', 53, 39, 27, 106, 527, 'و اینکه برای انسان بهره‌ای جز سعی و کوشش او نیست،'),
(4824, 4824, 'وَأَنَّ سَعْيَهُ سَوْفَ يُرَىٰ', 53, 40, 27, 106, 527, 'و اینکه تلاش او بزودی دیده می‌شود،'),
(4825, 4825, 'ثُمَّ يُجْزَاهُ الْجَزَاءَ الْأَوْفَىٰ', 53, 41, 27, 106, 527, 'سپس به او جزای کافی داده خواهد شد!'),
(4826, 4826, 'وَأَنَّ إِلَىٰ رَبِّكَ الْمُنْتَهَىٰ', 53, 42, 27, 106, 527, '(و آیا از کتب پیشین انبیا به او نرسیده است) که همه امور به پروردگارت منتهی می‌گردد؟!'),
(4827, 4827, 'وَأَنَّهُ هُوَ أَضْحَكَ وَأَبْكَىٰ', 53, 43, 27, 106, 527, 'و اینکه اوست که خنداند و گریاند،'),
(4828, 4828, 'وَأَنَّهُ هُوَ أَمَاتَ وَأَحْيَا', 53, 44, 27, 106, 527, 'و اوست که میراند و زنده کرد،'),
(4829, 4829, 'وَأَنَّهُ خَلَقَ الزَّوْجَيْنِ الذَّكَرَ وَالْأُنْثَىٰ', 53, 45, 27, 106, 528, 'و اوست که دو زوج نر و مادّه را آفرید...'),
(4830, 4830, 'مِنْ نُطْفَةٍ إِذَا تُمْنَىٰ', 53, 46, 27, 106, 528, 'از نطفه‌ای هنگامی که خارج می‌شود (و در رحم می‌ریزد)،'),
(4831, 4831, 'وَأَنَّ عَلَيْهِ النَّشْأَةَ الْأُخْرَىٰ', 53, 47, 27, 106, 528, 'و اینکه بر خداست ایجاد عالم دیگر (تا عدالت اجرا گردد)،'),
(4832, 4832, 'وَأَنَّهُ هُوَ أَغْنَىٰ وَأَقْنَىٰ', 53, 48, 27, 106, 528, 'و اینکه اوست که بی‌نیاز کرد و سرمایه باقی بخشید،'),
(4833, 4833, 'وَأَنَّهُ هُوَ رَبُّ الشِّعْرَىٰ', 53, 49, 27, 106, 528, 'و اینکه اوست پروردگار ستاره «شعرا»!'),
(4834, 4834, 'وَأَنَّهُ أَهْلَكَ عَادًا الْأُولَىٰ', 53, 50, 27, 106, 528, '(و آیا به انسان نرسیده است که در کتب انبیای پیشین آمده) که خداوند قوم «عاد نخستین» را هلاک کرد؟!'),
(4835, 4835, 'وَثَمُودَ فَمَا أَبْقَىٰ', 53, 51, 27, 106, 528, 'و همچنین قوم «ثمود» را، و کسی از آنان را باقی نگذارد!'),
(4836, 4836, 'وَقَوْمَ نُوحٍ مِنْ قَبْلُ ۖ إِنَّهُمْ كَانُوا هُمْ أَظْلَمَ وَأَطْغَىٰ', 53, 52, 27, 106, 528, 'و نیز قوم نوح را پیش از آنها، چرا که آنان از همه ظالمتر و طغیانگرتر بودند!'),
(4837, 4837, 'وَالْمُؤْتَفِكَةَ أَهْوَىٰ', 53, 53, 27, 106, 528, 'و نیز شهرهای زیر و رو شده (قوم لوط) را فرو کوبید،'),
(4838, 4838, 'فَغَشَّاهَا مَا غَشَّىٰ', 53, 54, 27, 106, 528, 'سپس آنها را با عذاب سنگین پوشانید!'),
(4839, 4839, 'فَبِأَيِّ آلَاءِ رَبِّكَ تَتَمَارَىٰ', 53, 55, 27, 106, 528, '(بگو:) در کدام یک از نعمتهای پروردگارت تردید داری؟!'),
(4840, 4840, 'هَٰذَا نَذِيرٌ مِنَ النُّذُرِ الْأُولَىٰ', 53, 56, 27, 106, 528, 'این (پیامبر) بیم‌دهنده‌ای از بیم‌دهندگان پیشین است!'),
(4841, 4841, 'أَزِفَتِ الْآزِفَةُ', 53, 57, 27, 106, 528, 'آنچه باید نزدیک شود، نزدیک شده است (و قیامت فرامی‌رسد)،'),
(4842, 4842, 'لَيْسَ لَهَا مِنْ دُونِ اللَّهِ كَاشِفَةٌ', 53, 58, 27, 106, 528, 'و هیچ کس جز خدا نمی‌تواند سختیهای آن را برطرف سازد!'),
(4843, 4843, 'أَفَمِنْ هَٰذَا الْحَدِيثِ تَعْجَبُونَ', 53, 59, 27, 106, 528, 'آیا از این سخن تعجّب می‌کنید،'),
(4844, 4844, 'وَتَضْحَكُونَ وَلَا تَبْكُونَ', 53, 60, 27, 106, 528, 'و می‌خندید و نمی‌گریید،'),
(4845, 4845, 'وَأَنْتُمْ سَامِدُونَ', 53, 61, 27, 106, 528, 'و پیوسته در غفلت و هوسرانی به سر می‌برید؟!'),
(4846, 4846, 'فَاسْجُدُوا لِلَّهِ وَاعْبُدُوا ۩', 53, 62, 27, 106, 528, 'حال که چنین است همه برای خدا سجده کنید و او را بپرستید!'),
(4847, 4847, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ اقْتَرَبَتِ السَّاعَةُ وَانْشَقَّ الْقَمَرُ', 54, 1, 27, 106, 528, 'قیامت نزدیک شد و ماه از هم شکافت!'),
(4848, 4848, 'وَإِنْ يَرَوْا آيَةً يُعْرِضُوا وَيَقُولُوا سِحْرٌ مُسْتَمِرٌّ', 54, 2, 27, 106, 528, 'و هرگاه نشانه و معجزه‌ای را ببینند روی گردانده، می‌گویند: «این سحری مستمر است»!'),
(4849, 4849, 'وَكَذَّبُوا وَاتَّبَعُوا أَهْوَاءَهُمْ ۚ وَكُلُّ أَمْرٍ مُسْتَقِرٌّ', 54, 3, 27, 106, 528, 'آنها (آیات خدا را) تکذیب کردند و از هوای نفسشان پیروی نمودند؛ و هر امری قرارگاهی دارد!'),
(4850, 4850, 'وَلَقَدْ جَاءَهُمْ مِنَ الْأَنْبَاءِ مَا فِيهِ مُزْدَجَرٌ', 54, 4, 27, 106, 528, 'به اندازه کافی برای بازداشتن از بدیها اخبار (انبیا و امّتهای پیشین) به آنان رسیده است!'),
(4851, 4851, 'حِكْمَةٌ بَالِغَةٌ ۖ فَمَا تُغْنِ النُّذُرُ', 54, 5, 27, 106, 528, 'این آیات، حکمت بالغه الهی است؛ امّا انذارها (برای افراد لجوج) فایده نمی‌دهد!'),
(4852, 4852, 'فَتَوَلَّ عَنْهُمْ ۘ يَوْمَ يَدْعُ الدَّاعِ إِلَىٰ شَيْءٍ نُكُرٍ', 54, 6, 27, 106, 528, 'بنابر این از آنها روی بگردان، و روزی را به یاد آور که دعوت کننده الهی مردم را به امر وحشتناکی دعوت می‌کند (دعوت به حساب اعمال)!'),
(4853, 4853, 'خُشَّعًا أَبْصَارُهُمْ يَخْرُجُونَ مِنَ الْأَجْدَاثِ كَأَنَّهُمْ جَرَادٌ مُنْتَشِرٌ', 54, 7, 27, 106, 529, 'آنان در حالی که چشمهایشان از شدّت وحشت به زیر افتاده، همچون ملخهای پراکنده از قبرها خارج می‌شوند،'),
(4854, 4854, 'مُهْطِعِينَ إِلَى الدَّاعِ ۖ يَقُولُ الْكَافِرُونَ هَٰذَا يَوْمٌ عَسِرٌ', 54, 8, 27, 106, 529, 'در حالی که (بر اثر وحشت و اضطراب) بسوی این دعوت کننده گردن می‌کشند؛ کافران می‌گویند: «امروز روز سخت و دردناکی است!»'),
(4855, 4855, ' كَذَّبَتْ قَبْلَهُمْ قَوْمُ نُوحٍ فَكَذَّبُوا عَبْدَنَا وَقَالُوا مَجْنُونٌ وَازْدُجِرَ', 54, 9, 27, 106, 529, 'پیش از آنها قوم نوح تکذیب کردند، (آری) بنده ما (نوح) را تکذیب کرده و گفتند: «او دیوانه است!» و (با انواع آزارها از ادامه رسالتش) بازداشته شد.'),
(4856, 4856, 'فَدَعَا رَبَّهُ أَنِّي مَغْلُوبٌ فَانْتَصِرْ', 54, 10, 27, 106, 529, 'او به درگاه پروردگار عرضه داشت: «من مغلوب (این قوم طغیانگر) شده‌ام، انتقام مرا از آنها بگیر!»'),
(4857, 4857, 'فَفَتَحْنَا أَبْوَابَ السَّمَاءِ بِمَاءٍ مُنْهَمِرٍ', 54, 11, 27, 106, 529, 'در این هنگام درهای آسمان را با آبی فراوان و پی‌درپی گشودیم؛'),
(4858, 4858, 'وَفَجَّرْنَا الْأَرْضَ عُيُونًا فَالْتَقَى الْمَاءُ عَلَىٰ أَمْرٍ قَدْ قُدِرَ', 54, 12, 27, 106, 529, 'و زمین را شکافتیم و چشمه‌های زیادی بیرون فرستادیم؛ و این دو آب به اندازه مقدّر با هم درآمیختند (و دریای وحشتناکی شد)!'),
(4859, 4859, 'وَحَمَلْنَاهُ عَلَىٰ ذَاتِ أَلْوَاحٍ وَدُسُرٍ', 54, 13, 27, 106, 529, 'و او را بر مرکبی از الواح و میخهایی ساخته شده سوار کردیم؛'),
(4860, 4860, 'تَجْرِي بِأَعْيُنِنَا جَزَاءً لِمَنْ كَانَ كُفِرَ', 54, 14, 27, 106, 529, 'مرکبی که زیر نظر ما حرکت می‌کرد! این کیفری بود برای کسانی که (به او) کافر شده بودند!'),
(4861, 4861, 'وَلَقَدْ تَرَكْنَاهَا آيَةً فَهَلْ مِنْ مُدَّكِرٍ', 54, 15, 27, 106, 529, 'ما این ماجرا را بعنوان نشانه‌ای در میان امّتها باقی گذاردیم؛ آیا کسی هست که پند گیرد؟!'),
(4862, 4862, 'فَكَيْفَ كَانَ عَذَابِي وَنُذُرِ', 54, 16, 27, 106, 529, '(اکنون بنگرید) عذاب و انذارهای من چگونه بود!'),
(4863, 4863, 'وَلَقَدْ يَسَّرْنَا الْقُرْآنَ لِلذِّكْرِ فَهَلْ مِنْ مُدَّكِرٍ', 54, 17, 27, 106, 529, 'ما قرآن را برای تذکّر آسان ساختیم؛ آیا کسی هست که متذکّر شود؟!'),
(4864, 4864, 'كَذَّبَتْ عَادٌ فَكَيْفَ كَانَ عَذَابِي وَنُذُرِ', 54, 18, 27, 106, 529, 'قوم عاد (نیز پیامبر خود را) تکذیب کردند؛ پس (ببینید) عذاب و انذارهای من چگونه بود!'),
(4865, 4865, 'إِنَّا أَرْسَلْنَا عَلَيْهِمْ رِيحًا صَرْصَرًا فِي يَوْمِ نَحْسٍ مُسْتَمِرٍّ', 54, 19, 27, 106, 529, 'ما تندباد وحشتناک و سردی را در یک روز شوم مستمر بر آنان فرستادیم...'),
(4866, 4866, 'تَنْزِعُ النَّاسَ كَأَنَّهُمْ أَعْجَازُ نَخْلٍ مُنْقَعِرٍ', 54, 20, 27, 106, 529, 'که مردم را همچون تنه‌های نخل ریشه‌کن شده از جا برمی‌کند!'),
(4867, 4867, 'فَكَيْفَ كَانَ عَذَابِي وَنُذُرِ', 54, 21, 27, 106, 529, 'پس (ببینید) عذاب و انذارهای من چگونه بود!'),
(4868, 4868, 'وَلَقَدْ يَسَّرْنَا الْقُرْآنَ لِلذِّكْرِ فَهَلْ مِنْ مُدَّكِرٍ', 54, 22, 27, 106, 529, 'ما قرآن را برای تذکّر آسان ساختیم؛ آیا کسی هست که متذکّر شود!'),
(4869, 4869, 'كَذَّبَتْ ثَمُودُ بِالنُّذُرِ', 54, 23, 27, 106, 529, 'طایفه ثمود (نیز) انذارهای الهی را تکذیب کردند،'),
(4870, 4870, 'فَقَالُوا أَبَشَرًا مِنَّا وَاحِدًا نَتَّبِعُهُ إِنَّا إِذًا لَفِي ضَلَالٍ وَسُعُرٍ', 54, 24, 27, 106, 529, 'و گفتند: «آیا ما از بشری از جنس خود پیروی کنیم؟! اگر چنین کنیم در گمراهی و جنون خواهیم بود!'),
(4871, 4871, 'أَأُلْقِيَ الذِّكْرُ عَلَيْهِ مِنْ بَيْنِنَا بَلْ هُوَ كَذَّابٌ أَشِرٌ', 54, 25, 27, 106, 529, 'آیا از میان ما تنها بر او وحی نازل شده؟! نه، او آدم بسیار دروغگوی هوسبازی است!'),
(4872, 4872, 'سَيَعْلَمُونَ غَدًا مَنِ الْكَذَّابُ الْأَشِرُ', 54, 26, 27, 106, 529, 'ولی فردا می‌فهمند چه کسی دروغگوی هوسباز است!'),
(4873, 4873, 'إِنَّا مُرْسِلُو النَّاقَةِ فِتْنَةً لَهُمْ فَارْتَقِبْهُمْ وَاصْطَبِرْ', 54, 27, 27, 106, 529, 'ما «ناقه» را برای آزمایش آنها می‌فرستیم؛ در انتظار پایان کار آنان باش و صبر کن!'),
(4874, 4874, 'وَنَبِّئْهُمْ أَنَّ الْمَاءَ قِسْمَةٌ بَيْنَهُمْ ۖ كُلُّ شِرْبٍ مُحْتَضَرٌ', 54, 28, 27, 106, 530, 'و به آنها خبر ده که آب (قریه) باید در میانشان تقسیم شود، (یک روز سهم ناقه، و یک روز برای آنها) و هر یک در نوبت خود باید حاضر شوند!'),
(4875, 4875, 'فَنَادَوْا صَاحِبَهُمْ فَتَعَاطَىٰ فَعَقَرَ', 54, 29, 27, 106, 530, 'آنها یکی از یاران خود را صدا زدند، او به سراغ این کار آمد و (ناقه را) پی کرد!'),
(4876, 4876, 'فَكَيْفَ كَانَ عَذَابِي وَنُذُرِ', 54, 30, 27, 106, 530, 'پس (بنگرید) عذاب و انذارهای من چگونه بود!'),
(4877, 4877, 'إِنَّا أَرْسَلْنَا عَلَيْهِمْ صَيْحَةً وَاحِدَةً فَكَانُوا كَهَشِيمِ الْمُحْتَظِرِ', 54, 31, 27, 106, 530, 'ما فقط یک صیحه = صاعقه عظیم‌ بر آنها فرستادیم و بدنبال آن همگی بصورت گیاه خشکی درآمدند که صاحب چهارپایان (در آغل) جمع‌آوری می‌کند!'),
(4878, 4878, 'وَلَقَدْ يَسَّرْنَا الْقُرْآنَ لِلذِّكْرِ فَهَلْ مِنْ مُدَّكِرٍ', 54, 32, 27, 106, 530, 'ما قرآن را برای یادآوری آسان ساختیم؛ آیا کسی هست که متذکّر شود؟!'),
(4879, 4879, 'كَذَّبَتْ قَوْمُ لُوطٍ بِالنُّذُرِ', 54, 33, 27, 106, 530, 'قوم لوط انذارها(ی پی‌درپی پیامبرشان) را تکذیب کردند؛'),
(4880, 4880, 'إِنَّا أَرْسَلْنَا عَلَيْهِمْ حَاصِبًا إِلَّا آلَ لُوطٍ ۖ نَجَّيْنَاهُمْ بِسَحَرٍ', 54, 34, 27, 106, 530, 'ما بر آنها تندبادی که ریگها را به حرکت درمی‌آورد فرستادیم (و همه را هلاک کردیم)، جز خاندان لوط را که سحرگاهان نجاتشان دادیم!'),
(4881, 4881, 'نِعْمَةً مِنْ عِنْدِنَا ۚ كَذَٰلِكَ نَجْزِي مَنْ شَكَرَ', 54, 35, 27, 106, 530, 'این نعمتی بود از ناحیه ما؛ این گونه هر کسی را که شکر کند پاداش می‌دهیم!'),
(4882, 4882, 'وَلَقَدْ أَنْذَرَهُمْ بَطْشَتَنَا فَتَمَارَوْا بِالنُّذُرِ', 54, 36, 27, 106, 530, 'او آنها را از مجازات ما بیم داد، ولی بر آنها اصرار بر مجادله و القای شکّ داشتند!'),
(4883, 4883, 'وَلَقَدْ رَاوَدُوهُ عَنْ ضَيْفِهِ فَطَمَسْنَا أَعْيُنَهُمْ فَذُوقُوا عَذَابِي وَنُذُرِ', 54, 37, 27, 106, 530, 'آنها از لوط خواستند میهمانانش را در اختیارشان بگذارد؛ ولی ما چشمانشان را نابینا و محو کردیم (و گفتیم:) بچشید عذاب و انذارهای مرا!'),
(4884, 4884, 'وَلَقَدْ صَبَّحَهُمْ بُكْرَةً عَذَابٌ مُسْتَقِرٌّ', 54, 38, 27, 106, 530, 'سرانجام صبحگاهان و در اول روز عذابی پایدار و ثابت به سراغشان آمد!'),
(4885, 4885, 'فَذُوقُوا عَذَابِي وَنُذُرِ', 54, 39, 27, 106, 530, '(و گفتیم:) پس بچشید عذاب و انذارهای مرا!'),
(4886, 4886, 'وَلَقَدْ يَسَّرْنَا الْقُرْآنَ لِلذِّكْرِ فَهَلْ مِنْ مُدَّكِرٍ', 54, 40, 27, 106, 530, 'ما قرآن را برای یادآوری آسان ساختیم؛ آیا کسی هست که متذکّر شود؟!'),
(4887, 4887, 'وَلَقَدْ جَاءَ آلَ فِرْعَوْنَ النُّذُرُ', 54, 41, 27, 106, 530, 'و (همچنین) انذارها و هشدارها (یکی پس از دیگری) به سراغ آل فرعون آمد،'),
(4888, 4888, 'كَذَّبُوا بِآيَاتِنَا كُلِّهَا فَأَخَذْنَاهُمْ أَخْذَ عَزِيزٍ مُقْتَدِرٍ', 54, 42, 27, 106, 530, 'امّا آنها همه آیات ما را تکذیب کردند، و ما آنها را گرفتیم و مجازات کردیم، گرفتن شخصی قدرتمند و توانا!'),
(4889, 4889, 'أَكُفَّارُكُمْ خَيْرٌ مِنْ أُولَٰئِكُمْ أَمْ لَكُمْ بَرَاءَةٌ فِي الزُّبُرِ', 54, 43, 27, 106, 530, 'آیا کفّار شما بهتر از آنانند یا برای شما امان‌نامه‌ای در کتب آسمانی نازل شده است؟!'),
(4890, 4890, 'أَمْ يَقُولُونَ نَحْنُ جَمِيعٌ مُنْتَصِرٌ', 54, 44, 27, 106, 530, 'یا می‌گویند: «ما جماعتی متحّد و نیرومند و پیروزیم»؟!'),
(4891, 4891, 'سَيُهْزَمُ الْجَمْعُ وَيُوَلُّونَ الدُّبُرَ', 54, 45, 27, 106, 530, '(ولی بدانند) که بزودی جمعشان شکست می‌خورد و پا به فرارمی‌گذارند!'),
(4892, 4892, 'بَلِ السَّاعَةُ مَوْعِدُهُمْ وَالسَّاعَةُ أَدْهَىٰ وَأَمَرُّ', 54, 46, 27, 106, 530, '(علاوه بر این) رستاخیز موعد آنهاست، و مجازات قیامت هولناکتر و تلختر است!'),
(4893, 4893, 'إِنَّ الْمُجْرِمِينَ فِي ضَلَالٍ وَسُعُرٍ', 54, 47, 27, 106, 530, 'مجرمان در گمراهی و شعله‌های آتشند،'),
(4894, 4894, 'يَوْمَ يُسْحَبُونَ فِي النَّارِ عَلَىٰ وُجُوهِهِمْ ذُوقُوا مَسَّ سَقَرَ', 54, 48, 27, 106, 530, 'در آن روز که در آتش دوزخ به صورتشان کشیده می‌شوند (و به آنها گفته می‌شود:) بچشید آتش دوزخ را!'),
(4895, 4895, 'إِنَّا كُلَّ شَيْءٍ خَلَقْنَاهُ بِقَدَرٍ', 54, 49, 27, 106, 530, 'البته ما هر چیز را به اندازه آفریدیم!'),
(4896, 4896, 'وَمَا أَمْرُنَا إِلَّا وَاحِدَةٌ كَلَمْحٍ بِالْبَصَرِ', 54, 50, 27, 106, 531, 'و فرمان ما یک امر بیش نیست، همچون یک چشم بر هم زدن!'),
(4897, 4897, 'وَلَقَدْ أَهْلَكْنَا أَشْيَاعَكُمْ فَهَلْ مِنْ مُدَّكِرٍ', 54, 51, 27, 106, 531, 'ما کسانی را که در گذشته شبیه شما بودند هلاک کردیم؛ آیا کسی هست که پند گیرد؟!'),
(4898, 4898, 'وَكُلُّ شَيْءٍ فَعَلُوهُ فِي الزُّبُرِ', 54, 52, 27, 106, 531, 'و هر کاری را انجام دادند در نامه‌های اعمالشان ثبت است،'),
(4899, 4899, 'وَكُلُّ صَغِيرٍ وَكَبِيرٍ مُسْتَطَرٌ', 54, 53, 27, 106, 531, 'و هر کار کوچک و بزرگی نوشته شده است.'),
(4900, 4900, 'إِنَّ الْمُتَّقِينَ فِي جَنَّاتٍ وَنَهَرٍ', 54, 54, 27, 106, 531, 'یقیناً پرهیزگاران در باغها و نهرهای بهشتی جای دارند،'),
(4901, 4901, 'فِي مَقْعَدِ صِدْقٍ عِنْدَ مَلِيكٍ مُقْتَدِرٍ', 54, 55, 27, 106, 531, 'در جایگاه صدق نزد خداوند مالک مقتدر!'),
(4902, 4902, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ الرَّحْمَٰنُ', 55, 1, 27, 107, 531, 'خداوند رحمان،'),
(4903, 4903, 'عَلَّمَ الْقُرْآنَ', 55, 2, 27, 107, 531, 'قرآن را تعلیم فرمود،'),
(4904, 4904, 'خَلَقَ الْإِنْسَانَ', 55, 3, 27, 107, 531, 'انسان را آفرید،'),
(4905, 4905, 'عَلَّمَهُ الْبَيَانَ', 55, 4, 27, 107, 531, 'و به او «بیان» را آموخت.'),
(4906, 4906, 'الشَّمْسُ وَالْقَمَرُ بِحُسْبَانٍ', 55, 5, 27, 107, 531, 'خورشید و ماه با حساب منظّمی می‌گردند،'),
(4907, 4907, 'وَالنَّجْمُ وَالشَّجَرُ يَسْجُدَانِ', 55, 6, 27, 107, 531, 'و گیاه و درخت برای او سجده می‌کنند!'),
(4908, 4908, 'وَالسَّمَاءَ رَفَعَهَا وَوَضَعَ الْمِيزَانَ', 55, 7, 27, 107, 531, 'و آسمان را برافراشت، و میزان و قانون (در آن) گذاشت،'),
(4909, 4909, 'أَلَّا تَطْغَوْا فِي الْمِيزَانِ', 55, 8, 27, 107, 531, 'تا در میزان طغیان نکنید (و از مسیر عدالت منحرف نشوید)،'),
(4910, 4910, 'وَأَقِيمُوا الْوَزْنَ بِالْقِسْطِ وَلَا تُخْسِرُوا الْمِيزَانَ', 55, 9, 27, 107, 531, 'و وزن را بر اساس عدل برپا دارید و میزان را کم نگذارید!'),
(4911, 4911, 'وَالْأَرْضَ وَضَعَهَا لِلْأَنَامِ', 55, 10, 27, 107, 531, 'زمین را برای خلایق آفرید،'),
(4912, 4912, 'فِيهَا فَاكِهَةٌ وَالنَّخْلُ ذَاتُ الْأَكْمَامِ', 55, 11, 27, 107, 531, 'که در آن میوه‌ها و نخلهای پرشکوفه است،'),
(4913, 4913, 'وَالْحَبُّ ذُو الْعَصْفِ وَالرَّيْحَانُ', 55, 12, 27, 107, 531, 'و دانه‌هایی که همراه با ساقه و برگی است که بصورت کاه درمی‌آید، و گیاهان خوشبو!'),
(4914, 4914, 'فَبِأَيِّ آلَاءِ رَبِّكُمَا تُكَذِّبَانِ', 55, 13, 27, 107, 531, 'پس کدامین نعمتهای پروردگارتان را تکذیب می‌کنید (شما ای گروه جنّ و انس)؟!'),
(4915, 4915, 'خَلَقَ الْإِنْسَانَ مِنْ صَلْصَالٍ كَالْفَخَّارِ', 55, 14, 27, 107, 531, 'انسان را از گِل خشکیده‌ای همچون سفال آفرید،'),
(4916, 4916, 'وَخَلَقَ الْجَانَّ مِنْ مَارِجٍ مِنْ نَارٍ', 55, 15, 27, 107, 531, 'و جنّ را از شعله‌های مختلط و متحرّک آتش خلق کرد!'),
(4917, 4917, 'فَبِأَيِّ آلَاءِ رَبِّكُمَا تُكَذِّبَانِ', 55, 16, 27, 107, 531, 'پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟!'),
(4918, 4918, 'رَبُّ الْمَشْرِقَيْنِ وَرَبُّ الْمَغْرِبَيْنِ', 55, 17, 27, 107, 532, 'او پروردگار دو مشرق و پروردگار دو مغرب است!'),
(4919, 4919, 'فَبِأَيِّ آلَاءِ رَبِّكُمَا تُكَذِّبَانِ', 55, 18, 27, 107, 532, 'پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟!'),
(4920, 4920, 'مَرَجَ الْبَحْرَيْنِ يَلْتَقِيَانِ', 55, 19, 27, 107, 532, 'دو دریای مختلف (شور و شیرین، گرم و سرد) را در کنار هم قرار داد، در حالی که با هم تماس دارند؛'),
(4921, 4921, 'بَيْنَهُمَا بَرْزَخٌ لَا يَبْغِيَانِ', 55, 20, 27, 107, 532, 'در میان آن دو برزخی است که یکی بر دیگری غلبه نمی‌کند (و به هم نمی‌آمیزند)!'),
(4922, 4922, 'فَبِأَيِّ آلَاءِ رَبِّكُمَا تُكَذِّبَانِ', 55, 21, 27, 107, 532, 'پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟!'),
(4923, 4923, 'يَخْرُجُ مِنْهُمَا اللُّؤْلُؤُ وَالْمَرْجَانُ', 55, 22, 27, 107, 532, 'از آن دو، لؤلؤ و مرجان خارج می‌شود.'),
(4924, 4924, 'فَبِأَيِّ آلَاءِ رَبِّكُمَا تُكَذِّبَانِ', 55, 23, 27, 107, 532, 'پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟!'),
(4925, 4925, 'وَلَهُ الْجَوَارِ الْمُنْشَآتُ فِي الْبَحْرِ كَالْأَعْلَامِ', 55, 24, 27, 107, 532, 'و برای اوست کشتیهای ساخته شده که در دریا به حرکت درمی‌آیند و همچون کوهی هستند!'),
(4926, 4926, 'فَبِأَيِّ آلَاءِ رَبِّكُمَا تُكَذِّبَانِ', 55, 25, 27, 107, 532, 'پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟!'),
(4927, 4927, 'كُلُّ مَنْ عَلَيْهَا فَانٍ', 55, 26, 27, 107, 532, 'همه کسانی که روی آن = زمین‌ هستند فانی می‌شوند،'),
(4928, 4928, 'وَيَبْقَىٰ وَجْهُ رَبِّكَ ذُو الْجَلَالِ وَالْإِكْرَامِ', 55, 27, 27, 107, 532, 'و تنها ذات ذوالجلال و گرامی پروردگارت باقی می‌ماند!'),
(4929, 4929, 'فَبِأَيِّ آلَاءِ رَبِّكُمَا تُكَذِّبَانِ', 55, 28, 27, 107, 532, 'پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟!'),
(4930, 4930, 'يَسْأَلُهُ مَنْ فِي السَّمَاوَاتِ وَالْأَرْضِ ۚ كُلَّ يَوْمٍ هُوَ فِي شَأْنٍ', 55, 29, 27, 107, 532, 'تمام کسانی که در آسمانها و زمین هستند از او تقاضا می‌کنند، و او هر روز در شأن و کاری است!'),
(4931, 4931, 'فَبِأَيِّ آلَاءِ رَبِّكُمَا تُكَذِّبَانِ', 55, 30, 27, 107, 532, 'پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟!'),
(4932, 4932, 'سَنَفْرُغُ لَكُمْ أَيُّهَ الثَّقَلَانِ', 55, 31, 27, 107, 532, 'بزودی به حساب شما می‌پردازیم ای دو گروه انس و جنّ!'),
(4933, 4933, 'فَبِأَيِّ آلَاءِ رَبِّكُمَا تُكَذِّبَانِ', 55, 32, 27, 107, 532, 'پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟!'),
(4934, 4934, 'يَا مَعْشَرَ الْجِنِّ وَالْإِنْسِ إِنِ اسْتَطَعْتُمْ أَنْ تَنْفُذُوا مِنْ أَقْطَارِ السَّمَاوَاتِ وَالْأَرْضِ فَانْفُذُوا ۚ لَا تَنْفُذُونَ إِلَّا بِسُلْطَانٍ', 55, 33, 27, 107, 532, 'ای گروه جنّ و انس! اگر می‌توانید از مرزهای آسمانها و زمین بگذرید، پس بگذرید، ولی هرگز نمی‌توانید، مگر با نیرویی (فوق العاده)!'),
(4935, 4935, 'فَبِأَيِّ آلَاءِ رَبِّكُمَا تُكَذِّبَانِ', 55, 34, 27, 107, 532, 'پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟!'),
(4936, 4936, 'يُرْسَلُ عَلَيْكُمَا شُوَاظٌ مِنْ نَارٍ وَنُحَاسٌ فَلَا تَنْتَصِرَانِ', 55, 35, 27, 107, 532, 'شعله‌هایی از آتش بی‌دود، و دودهایی متراکم بر شما فرستاده می‌شود؛ و نمی‌توانید از کسی یاری بطلبید!'),
(4937, 4937, 'فَبِأَيِّ آلَاءِ رَبِّكُمَا تُكَذِّبَانِ', 55, 36, 27, 107, 532, 'پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟!'),
(4938, 4938, 'فَإِذَا انْشَقَّتِ السَّمَاءُ فَكَانَتْ وَرْدَةً كَالدِّهَانِ', 55, 37, 27, 107, 532, 'در آن هنگام که آسمان شکافته شود و همچون روغن مذاب گلگون گردد (حوادث هولناکی رخ می‌دهد که تاب تحمل آن را نخواهید داشت)!'),
(4939, 4939, 'فَبِأَيِّ آلَاءِ رَبِّكُمَا تُكَذِّبَانِ', 55, 38, 27, 107, 532, 'پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟!'),
(4940, 4940, 'فَيَوْمَئِذٍ لَا يُسْأَلُ عَنْ ذَنْبِهِ إِنْسٌ وَلَا جَانٌّ', 55, 39, 27, 107, 532, 'در آن روز هیچ کس از انس و جنّ از گناهش سؤال نمی‌شود (و همه چیز روشن است)!'),
(4941, 4941, 'فَبِأَيِّ آلَاءِ رَبِّكُمَا تُكَذِّبَانِ', 55, 40, 27, 107, 532, 'پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟!'),
(4942, 4942, 'يُعْرَفُ الْمُجْرِمُونَ بِسِيمَاهُمْ فَيُؤْخَذُ بِالنَّوَاصِي وَالْأَقْدَامِ', 55, 41, 27, 107, 533, 'مجرمان از چهره‌هایشان شناخته می‌شوند؛ و آنگاه آنها را از موهای پیش سر، و پاهایشان می‌گیرند (و به دوزخ می‌افکنند)!'),
(4943, 4943, 'فَبِأَيِّ آلَاءِ رَبِّكُمَا تُكَذِّبَانِ', 55, 42, 27, 107, 533, 'پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟!'),
(4944, 4944, 'هَٰذِهِ جَهَنَّمُ الَّتِي يُكَذِّبُ بِهَا الْمُجْرِمُونَ', 55, 43, 27, 107, 533, 'این همان دوزخی است که مجرمان آن را انکار می‌کردند!'),
(4945, 4945, 'يَطُوفُونَ بَيْنَهَا وَبَيْنَ حَمِيمٍ آنٍ', 55, 44, 27, 107, 533, 'امروز در میان آن و آب سوزان در رفت و آمدند!'),
(4946, 4946, 'فَبِأَيِّ آلَاءِ رَبِّكُمَا تُكَذِّبَانِ', 55, 45, 27, 107, 533, 'پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟!'),
(4947, 4947, 'وَلِمَنْ خَافَ مَقَامَ رَبِّهِ جَنَّتَانِ', 55, 46, 27, 107, 533, 'و برای کسی که از مقام پروردگارش بترسد، دو باغ بهشتی است!'),
(4948, 4948, 'فَبِأَيِّ آلَاءِ رَبِّكُمَا تُكَذِّبَانِ', 55, 47, 27, 107, 533, 'پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟!'),
(4949, 4949, 'ذَوَاتَا أَفْنَانٍ', 55, 48, 27, 107, 533, '(آن دو باغ بهشتی) دارای انواع نعمتها و درختان پرطراوت است!');
INSERT INTO `Quran` (`rowid`, `id`, `Arabic`, `Chapter`, `Verse`, `Juz`, `Hizb`, `Page`, `tr`) VALUES
(4950, 4950, 'فَبِأَيِّ آلَاءِ رَبِّكُمَا تُكَذِّبَانِ', 55, 49, 27, 107, 533, 'پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟!'),
(4951, 4951, 'فِيهِمَا عَيْنَانِ تَجْرِيَانِ', 55, 50, 27, 107, 533, 'در آنها دو چشمه همیشه جاری است!'),
(4952, 4952, 'فَبِأَيِّ آلَاءِ رَبِّكُمَا تُكَذِّبَانِ', 55, 51, 27, 107, 533, 'پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟!'),
(4953, 4953, 'فِيهِمَا مِنْ كُلِّ فَاكِهَةٍ زَوْجَانِ', 55, 52, 27, 107, 533, 'در آن دو، از هر میوه‌ای دو نوع وجود دارد (هر یک از دیگری بهتر)!'),
(4954, 4954, 'فَبِأَيِّ آلَاءِ رَبِّكُمَا تُكَذِّبَانِ', 55, 53, 27, 107, 533, 'پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟!'),
(4955, 4955, 'مُتَّكِئِينَ عَلَىٰ فُرُشٍ بَطَائِنُهَا مِنْ إِسْتَبْرَقٍ ۚ وَجَنَى الْجَنَّتَيْنِ دَانٍ', 55, 54, 27, 107, 533, 'این در حالی است که آنها بر فرشهایی تکیه کرده‌اند با آسترهائی از دیبا و ابریشم، و میوه‌های رسیده آن دو باغ بهشتی در دسترس است!'),
(4956, 4956, 'فَبِأَيِّ آلَاءِ رَبِّكُمَا تُكَذِّبَانِ', 55, 55, 27, 107, 533, 'پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟!'),
(4957, 4957, 'فِيهِنَّ قَاصِرَاتُ الطَّرْفِ لَمْ يَطْمِثْهُنَّ إِنْسٌ قَبْلَهُمْ وَلَا جَانٌّ', 55, 56, 27, 107, 533, 'در آن باغهای بهشتی زنانی هستند که جز به همسران خود عشق نمی‌ورزند؛ و هیچ انس و جنّ پیش از اینها با آنان تماس نگرفته است.'),
(4958, 4958, 'فَبِأَيِّ آلَاءِ رَبِّكُمَا تُكَذِّبَانِ', 55, 57, 27, 107, 533, 'پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟!'),
(4959, 4959, 'كَأَنَّهُنَّ الْيَاقُوتُ وَالْمَرْجَانُ', 55, 58, 27, 107, 533, 'آنها همچون یاقوت و مرجانند!'),
(4960, 4960, 'فَبِأَيِّ آلَاءِ رَبِّكُمَا تُكَذِّبَانِ', 55, 59, 27, 107, 533, 'پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟!'),
(4961, 4961, 'هَلْ جَزَاءُ الْإِحْسَانِ إِلَّا الْإِحْسَانُ', 55, 60, 27, 107, 533, 'آیا جزای نیکی جز نیکی است؟!'),
(4962, 4962, 'فَبِأَيِّ آلَاءِ رَبِّكُمَا تُكَذِّبَانِ', 55, 61, 27, 107, 533, 'پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟!'),
(4963, 4963, 'وَمِنْ دُونِهِمَا جَنَّتَانِ', 55, 62, 27, 107, 533, 'و پایین تر از آنها، دو باغ بهشتی دیگر است.'),
(4964, 4964, 'فَبِأَيِّ آلَاءِ رَبِّكُمَا تُكَذِّبَانِ', 55, 63, 27, 107, 533, 'پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟!'),
(4965, 4965, 'مُدْهَامَّتَانِ', 55, 64, 27, 107, 533, 'هر دو خرّم و سرسبزند!'),
(4966, 4966, 'فَبِأَيِّ آلَاءِ رَبِّكُمَا تُكَذِّبَانِ', 55, 65, 27, 107, 533, 'پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟!'),
(4967, 4967, 'فِيهِمَا عَيْنَانِ نَضَّاخَتَانِ', 55, 66, 27, 107, 533, 'در آنها دو چشمه جوشنده است!'),
(4968, 4968, 'فَبِأَيِّ آلَاءِ رَبِّكُمَا تُكَذِّبَانِ', 55, 67, 27, 107, 533, 'پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟!'),
(4969, 4969, 'فِيهِمَا فَاكِهَةٌ وَنَخْلٌ وَرُمَّانٌ', 55, 68, 27, 107, 534, 'در آنها میوه‌های فراوان و درخت خرما و انار است!'),
(4970, 4970, 'فَبِأَيِّ آلَاءِ رَبِّكُمَا تُكَذِّبَانِ', 55, 69, 27, 107, 534, 'پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟!'),
(4971, 4971, 'فِيهِنَّ خَيْرَاتٌ حِسَانٌ', 55, 70, 27, 107, 534, 'و در آن باغهای بهشتی زنانی نیکو خلق و زیبایند!'),
(4972, 4972, 'فَبِأَيِّ آلَاءِ رَبِّكُمَا تُكَذِّبَانِ', 55, 71, 27, 107, 534, 'پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟!'),
(4973, 4973, 'حُورٌ مَقْصُورَاتٌ فِي الْخِيَامِ', 55, 72, 27, 107, 534, 'حوریانی که در خیمه‌های بهشتی مستورند!'),
(4974, 4974, 'فَبِأَيِّ آلَاءِ رَبِّكُمَا تُكَذِّبَانِ', 55, 73, 27, 107, 534, 'پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟!'),
(4975, 4975, 'لَمْ يَطْمِثْهُنَّ إِنْسٌ قَبْلَهُمْ وَلَا جَانٌّ', 55, 74, 27, 107, 534, 'هیچ انس و جنّ پیش از ایشان با آنها تماس نگرفته (و دوشیزه‌اند)!'),
(4976, 4976, 'فَبِأَيِّ آلَاءِ رَبِّكُمَا تُكَذِّبَانِ', 55, 75, 27, 107, 534, 'پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟!'),
(4977, 4977, 'مُتَّكِئِينَ عَلَىٰ رَفْرَفٍ خُضْرٍ وَعَبْقَرِيٍّ حِسَانٍ', 55, 76, 27, 107, 534, 'این در حالی است که بهشتیان بر تختهایی تکیه زده‌اند که با بهترین و زیباترین پارچه‌های سبزرنگ پوشانده شده است.'),
(4978, 4978, 'فَبِأَيِّ آلَاءِ رَبِّكُمَا تُكَذِّبَانِ', 55, 77, 27, 107, 534, 'پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟!'),
(4979, 4979, 'تَبَارَكَ اسْمُ رَبِّكَ ذِي الْجَلَالِ وَالْإِكْرَامِ', 55, 78, 27, 107, 534, 'پربرکت و زوال‌ناپذیر است نام پروردگار صاحب جلال و بزرگوار تو!'),
(4980, 4980, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ إِذَا وَقَعَتِ الْوَاقِعَةُ', 56, 1, 27, 107, 534, 'هنگامی که واقعه عظیم (قیامت) واقع شود،'),
(4981, 4981, 'لَيْسَ لِوَقْعَتِهَا كَاذِبَةٌ', 56, 2, 27, 107, 534, 'هیچ کس نمی‌تواند آن را انکار کند!'),
(4982, 4982, 'خَافِضَةٌ رَافِعَةٌ', 56, 3, 27, 107, 534, '(این واقعه) گروهی را پایین می‌آورد و گروهی را بالا می‌برد!'),
(4983, 4983, 'إِذَا رُجَّتِ الْأَرْضُ رَجًّا', 56, 4, 27, 107, 534, 'در آن هنگام که زمین بشدّت به لرزه درمی‌آید،'),
(4984, 4984, 'وَبُسَّتِ الْجِبَالُ بَسًّا', 56, 5, 27, 107, 534, 'و کوه‌ها در هم کوبیده می‌شود،'),
(4985, 4985, 'فَكَانَتْ هَبَاءً مُنْبَثًّا', 56, 6, 27, 107, 534, 'و بصورت غبار پراکنده درمی‌آید،'),
(4986, 4986, 'وَكُنْتُمْ أَزْوَاجًا ثَلَاثَةً', 56, 7, 27, 107, 534, 'و شما سه گروه خواهید بود!'),
(4987, 4987, 'فَأَصْحَابُ الْمَيْمَنَةِ مَا أَصْحَابُ الْمَيْمَنَةِ', 56, 8, 27, 107, 534, '(نخست) سعادتمندان و خجستگان (هستند)؛ چه سعادتمندان و خجستگانی!'),
(4988, 4988, 'وَأَصْحَابُ الْمَشْأَمَةِ مَا أَصْحَابُ الْمَشْأَمَةِ', 56, 9, 27, 107, 534, 'گروه دیگر شقاوتمندان و شومانند، چه شقاوتمندان و شومانی!'),
(4989, 4989, 'وَالسَّابِقُونَ السَّابِقُونَ', 56, 10, 27, 107, 534, 'و (سومین گروه) پیشگامان پیشگامند،'),
(4990, 4990, 'أُولَٰئِكَ الْمُقَرَّبُونَ', 56, 11, 27, 107, 534, 'آنها مقرّبانند!'),
(4991, 4991, 'فِي جَنَّاتِ النَّعِيمِ', 56, 12, 27, 107, 534, 'در باغهای پرنعمت بهشت (جای دارند)!'),
(4992, 4992, 'ثُلَّةٌ مِنَ الْأَوَّلِينَ', 56, 13, 27, 107, 534, 'گروه زیادی (از آنها) از امّتهای نخستینند،'),
(4993, 4993, 'وَقَلِيلٌ مِنَ الْآخِرِينَ', 56, 14, 27, 107, 534, 'و اندکی از امّت آخرین!'),
(4994, 4994, 'عَلَىٰ سُرُرٍ مَوْضُونَةٍ', 56, 15, 27, 107, 534, 'آنها = مقرّبان‌ بر تختهایی که صف‌کشیده و به هم پیوسته است قراردارند،'),
(4995, 4995, 'مُتَّكِئِينَ عَلَيْهَا مُتَقَابِلِينَ', 56, 16, 27, 107, 534, 'در حالی که بر آن تکیه زده و رو به روی یکدیگرند!'),
(4996, 4996, 'يَطُوفُ عَلَيْهِمْ وِلْدَانٌ مُخَلَّدُونَ', 56, 17, 27, 107, 535, 'نوجوانانی جاودان (در شکوه و طراوت) پیوسته گرداگرد آنان می‌گردند،'),
(4997, 4997, 'بِأَكْوَابٍ وَأَبَارِيقَ وَكَأْسٍ مِنْ مَعِينٍ', 56, 18, 27, 107, 535, 'با قدحها و کوزه‌ها و جامهایی از نهرهای جاری بهشتی (و شراب طهور)!'),
(4998, 4998, 'لَا يُصَدَّعُونَ عَنْهَا وَلَا يُنْزِفُونَ', 56, 19, 27, 107, 535, 'امّا شرابی که از آن درد سر نمی‌گیرند و نه مست می‌شوند!'),
(4999, 4999, 'وَفَاكِهَةٍ مِمَّا يَتَخَيَّرُونَ', 56, 20, 27, 107, 535, 'و میوه‌هایی از هر نوع که انتخاب کنند،'),
(5000, 5000, 'وَلَحْمِ طَيْرٍ مِمَّا يَشْتَهُونَ', 56, 21, 27, 107, 535, 'و گوشت پرنده از هر نوع که مایل باشند!'),
(5001, 5001, 'وَحُورٌ عِينٌ', 56, 22, 27, 107, 535, 'و همسرانی از حور العین دارند،'),
(5002, 5002, 'كَأَمْثَالِ اللُّؤْلُؤِ الْمَكْنُونِ', 56, 23, 27, 107, 535, 'همچون مروارید در صدف پنهان!'),
(5003, 5003, 'جَزَاءً بِمَا كَانُوا يَعْمَلُونَ', 56, 24, 27, 107, 535, 'اینها پاداشی است در برابر اعمالی که انجام می‌دادند!'),
(5004, 5004, 'لَا يَسْمَعُونَ فِيهَا لَغْوًا وَلَا تَأْثِيمًا', 56, 25, 27, 107, 535, 'در آن (باغهای بهشتی) نه لغو و بیهوده‌ای می‌شنوند نه سخنان گناه آلود؛'),
(5005, 5005, 'إِلَّا قِيلًا سَلَامًا سَلَامًا', 56, 26, 27, 107, 535, 'تنها چیزی که می‌شنوند «سلام» است «سلام»!'),
(5006, 5006, 'وَأَصْحَابُ الْيَمِينِ مَا أَصْحَابُ الْيَمِينِ', 56, 27, 27, 107, 535, 'و اصحاب یمین و خجستگان، چه اصحاب یمین و خجستگانی!'),
(5007, 5007, 'فِي سِدْرٍ مَخْضُودٍ', 56, 28, 27, 107, 535, 'آنها در سایه درختان «سِدر» بی‌خار قرار دارند،'),
(5008, 5008, 'وَطَلْحٍ مَنْضُودٍ', 56, 29, 27, 107, 535, 'و در سایه درخت «طلح» پربرگ = درختی خوشرنگ و خوشبو،'),
(5009, 5009, 'وَظِلٍّ مَمْدُودٍ', 56, 30, 27, 107, 535, 'و سایه کشیده و گسترده،'),
(5010, 5010, 'وَمَاءٍ مَسْكُوبٍ', 56, 31, 27, 107, 535, 'و در کنار آبشارها،'),
(5011, 5011, 'وَفَاكِهَةٍ كَثِيرَةٍ', 56, 32, 27, 107, 535, 'و میوه‌های فراوان،'),
(5012, 5012, 'لَا مَقْطُوعَةٍ وَلَا مَمْنُوعَةٍ', 56, 33, 27, 107, 535, 'که هرگز قطع و ممنوع نمی‌شود،'),
(5013, 5013, 'وَفُرُشٍ مَرْفُوعَةٍ', 56, 34, 27, 107, 535, 'و همسرانی بلندمرتبه!'),
(5014, 5014, 'إِنَّا أَنْشَأْنَاهُنَّ إِنْشَاءً', 56, 35, 27, 107, 535, 'ما آنها را آفرینش نوینی بخشیدیم،'),
(5015, 5015, 'فَجَعَلْنَاهُنَّ أَبْكَارًا', 56, 36, 27, 107, 535, 'و همه را دوشیزه قرار دادیم،'),
(5016, 5016, 'عُرُبًا أَتْرَابًا', 56, 37, 27, 107, 535, 'زنانی که تنها به همسرشان عشق می‌ورزند و خوش زبان و فصیح و هم سن و سالند!'),
(5017, 5017, 'لِأَصْحَابِ الْيَمِينِ', 56, 38, 27, 107, 535, 'اینها همه برای اصحاب یمین است،'),
(5018, 5018, 'ثُلَّةٌ مِنَ الْأَوَّلِينَ', 56, 39, 27, 107, 535, 'که گروهی از امّتهای نخستینند،'),
(5019, 5019, 'وَثُلَّةٌ مِنَ الْآخِرِينَ', 56, 40, 27, 107, 535, 'و گروهی از امّتهای آخرین!'),
(5020, 5020, 'وَأَصْحَابُ الشِّمَالِ مَا أَصْحَابُ الشِّمَالِ', 56, 41, 27, 107, 535, 'و اصحاب شمال، چه اصحاب شمالی (که نامه اعمالشان به نشانه جرمشان به دست چپ آنها داده می‌شود)!'),
(5021, 5021, 'فِي سَمُومٍ وَحَمِيمٍ', 56, 42, 27, 107, 535, 'آنها در میان بادهای کشنده و آب سوزان قرار دارند،'),
(5022, 5022, 'وَظِلٍّ مِنْ يَحْمُومٍ', 56, 43, 27, 107, 535, 'و در سایه دودهای متراکم و آتشزا!'),
(5023, 5023, 'لَا بَارِدٍ وَلَا كَرِيمٍ', 56, 44, 27, 107, 535, 'سایه‌ای که نه خنک است و نه آرامبخش!'),
(5024, 5024, 'إِنَّهُمْ كَانُوا قَبْلَ ذَٰلِكَ مُتْرَفِينَ', 56, 45, 27, 107, 535, 'آنها پیش از این (در عالم دنیا) مست و مغرور نعمت بودند،'),
(5025, 5025, 'وَكَانُوا يُصِرُّونَ عَلَى الْحِنْثِ الْعَظِيمِ', 56, 46, 27, 107, 535, 'و بر گناهان بزرگ اصرار می‌ورزیدند،'),
(5026, 5026, 'وَكَانُوا يَقُولُونَ أَئِذَا مِتْنَا وَكُنَّا تُرَابًا وَعِظَامًا أَإِنَّا لَمَبْعُوثُونَ', 56, 47, 27, 107, 535, 'و می‌گفتند: «هنگامی که ما مُردیم و خاک و استخوان شدیم، آیا برانگیخته خواهیم شد؟!'),
(5027, 5027, 'أَوَآبَاؤُنَا الْأَوَّلُونَ', 56, 48, 27, 107, 535, 'یا نیاکان نخستین ما (برانگیخته می‌شوند)؟!»'),
(5028, 5028, 'قُلْ إِنَّ الْأَوَّلِينَ وَالْآخِرِينَ', 56, 49, 27, 107, 535, 'بگو: «اوّلین و آخرین،'),
(5029, 5029, 'لَمَجْمُوعُونَ إِلَىٰ مِيقَاتِ يَوْمٍ مَعْلُومٍ', 56, 50, 27, 107, 535, 'همگی در موعد روز معیّنی گردآوری می‌شوند،'),
(5030, 5030, 'ثُمَّ إِنَّكُمْ أَيُّهَا الضَّالُّونَ الْمُكَذِّبُونَ', 56, 51, 27, 107, 536, 'سپس شما ای گمراهان تکذیب‌کننده!'),
(5031, 5031, 'لَآكِلُونَ مِنْ شَجَرٍ مِنْ زَقُّومٍ', 56, 52, 27, 107, 536, 'قطعاً از درخت زقّوم می‌خورید،'),
(5032, 5032, 'فَمَالِئُونَ مِنْهَا الْبُطُونَ', 56, 53, 27, 107, 536, 'و شکمها را از آن پر می‌کنید،'),
(5033, 5033, 'فَشَارِبُونَ عَلَيْهِ مِنَ الْحَمِيمِ', 56, 54, 27, 107, 536, 'و روی آن از آب سوزان می‌نوشید،'),
(5034, 5034, 'فَشَارِبُونَ شُرْبَ الْهِيمِ', 56, 55, 27, 107, 536, 'و همچون شتران مبتلا به بیماری عطش، از آن می‌آشامید!'),
(5035, 5035, 'هَٰذَا نُزُلُهُمْ يَوْمَ الدِّينِ', 56, 56, 27, 107, 536, 'این است وسیله پذیرایی از آنها در قیامت!'),
(5036, 5036, 'نَحْنُ خَلَقْنَاكُمْ فَلَوْلَا تُصَدِّقُونَ', 56, 57, 27, 107, 536, '» ما شما را آفریدیم؛ پس چرا (آفرینش مجدّد را) تصدیق نمی‌کنید؟!'),
(5037, 5037, 'أَفَرَأَيْتُمْ مَا تُمْنُونَ', 56, 58, 27, 107, 536, 'آیا از نطفه‌ای که در رحم می‌ریزید آگاهید؟!'),
(5038, 5038, 'أَأَنْتُمْ تَخْلُقُونَهُ أَمْ نَحْنُ الْخَالِقُونَ', 56, 59, 27, 107, 536, 'آیا شما آن را (در دوران جنینی) آفرینش (پی در پی) می‌دهید یا ما آفریدگاریم؟!'),
(5039, 5039, 'نَحْنُ قَدَّرْنَا بَيْنَكُمُ الْمَوْتَ وَمَا نَحْنُ بِمَسْبُوقِينَ', 56, 60, 27, 107, 536, 'ما در میان شما مرگ را مقدّر ساختیم؛ و هرگز کسی بر ما پیشی نمی‌گیرد!'),
(5040, 5040, 'عَلَىٰ أَنْ نُبَدِّلَ أَمْثَالَكُمْ وَنُنْشِئَكُمْ فِي مَا لَا تَعْلَمُونَ', 56, 61, 27, 107, 536, 'تا گروهی را به جای گروه دیگری بیاوریم و شما را در جهانی که نمی‌دانید آفرینش تازه‌ای بخشیم!'),
(5041, 5041, 'وَلَقَدْ عَلِمْتُمُ النَّشْأَةَ الْأُولَىٰ فَلَوْلَا تَذَكَّرُونَ', 56, 62, 27, 107, 536, 'شما عالم نخستین را دانستید؛ چگونه متذکّر نمی‌شوید (که جهانی بعد از آن است)؟!'),
(5042, 5042, 'أَفَرَأَيْتُمْ مَا تَحْرُثُونَ', 56, 63, 27, 107, 536, 'آیا هیچ درباره آنچه کشت می‌کنید اندیشیده‌اید؟!'),
(5043, 5043, 'أَأَنْتُمْ تَزْرَعُونَهُ أَمْ نَحْنُ الزَّارِعُونَ', 56, 64, 27, 107, 536, 'آیا شما آن را می‌رویانید یا ما می‌رویانیم؟!'),
(5044, 5044, 'لَوْ نَشَاءُ لَجَعَلْنَاهُ حُطَامًا فَظَلْتُمْ تَفَكَّهُونَ', 56, 65, 27, 107, 536, 'هرگاه بخواهیم آن را مبدّل به کاه در هم کوبیده می‌کنیم که تعجّب کنید!'),
(5045, 5045, 'إِنَّا لَمُغْرَمُونَ', 56, 66, 27, 107, 536, '(بگونه‌ای که بگویید:) براستی ما زیان کرده‌ایم،'),
(5046, 5046, 'بَلْ نَحْنُ مَحْرُومُونَ', 56, 67, 27, 107, 536, 'بلکه ما بکلّی محرومیم!'),
(5047, 5047, 'أَفَرَأَيْتُمُ الْمَاءَ الَّذِي تَشْرَبُونَ', 56, 68, 27, 107, 536, 'آیا به آبی که می‌نوشید اندیشیده‌اید؟!'),
(5048, 5048, 'أَأَنْتُمْ أَنْزَلْتُمُوهُ مِنَ الْمُزْنِ أَمْ نَحْنُ الْمُنْزِلُونَ', 56, 69, 27, 107, 536, 'آیا شما آن را از ابر نازل کرده‌اید یا ما نازل می‌کنیم؟!'),
(5049, 5049, 'لَوْ نَشَاءُ جَعَلْنَاهُ أُجَاجًا فَلَوْلَا تَشْكُرُونَ', 56, 70, 27, 107, 536, 'هرگاه بخواهیم، این آب گوارا را تلخ و شور قرار می‌دهیم؛ پس چرا شکر نمی‌کنید؟!'),
(5050, 5050, 'أَفَرَأَيْتُمُ النَّارَ الَّتِي تُورُونَ', 56, 71, 27, 107, 536, 'آیا درباره آتشی که می‌افروزید فکر کرده‌اید؟!'),
(5051, 5051, 'أَأَنْتُمْ أَنْشَأْتُمْ شَجَرَتَهَا أَمْ نَحْنُ الْمُنْشِئُونَ', 56, 72, 27, 107, 536, 'آیا شما درخت آن را آفریده‌اید یا ما آفریده‌ایم؟!'),
(5052, 5052, 'نَحْنُ جَعَلْنَاهَا تَذْكِرَةً وَمَتَاعًا لِلْمُقْوِينَ', 56, 73, 27, 107, 536, 'ما آن را وسیله یادآوری (برای همگان) و وسیله زندگی برای مسافران قرار داده‌ایم!'),
(5053, 5053, 'فَسَبِّحْ بِاسْمِ رَبِّكَ الْعَظِيمِ', 56, 74, 27, 107, 536, 'حال که چنین است به نام پروردگار بزرگت تسبیح کن (و او را پاک و منزّه بشمار)!'),
(5054, 5054, ' فَلَا أُقْسِمُ بِمَوَاقِعِ النُّجُومِ', 56, 75, 27, 108, 536, 'سوگند به جایگاه ستارگان (و محل طلوع و غروب آنها)!'),
(5055, 5055, 'وَإِنَّهُ لَقَسَمٌ لَوْ تَعْلَمُونَ عَظِيمٌ', 56, 76, 27, 108, 536, 'و این سوگندی است بسیار بزرگ، اگر بدانید!'),
(5056, 5056, 'إِنَّهُ لَقُرْآنٌ كَرِيمٌ', 56, 77, 27, 108, 537, 'که آن، قرآن کریمی است،'),
(5057, 5057, 'فِي كِتَابٍ مَكْنُونٍ', 56, 78, 27, 108, 537, 'که در کتاب محفوظی جای دارد،'),
(5058, 5058, 'لَا يَمَسُّهُ إِلَّا الْمُطَهَّرُونَ', 56, 79, 27, 108, 537, 'و جز پاکان نمی‌توانند به آن دست زنند = دست یابند.'),
(5059, 5059, 'تَنْزِيلٌ مِنْ رَبِّ الْعَالَمِينَ', 56, 80, 27, 108, 537, 'آن از سوی پروردگار عالمیان نازل شده؛'),
(5060, 5060, 'أَفَبِهَٰذَا الْحَدِيثِ أَنْتُمْ مُدْهِنُونَ', 56, 81, 27, 108, 537, 'آیا این سخن را = این قرآن را با اوصافی که گفته شد سست و کوچک می‌شمرید،'),
(5061, 5061, 'وَتَجْعَلُونَ رِزْقَكُمْ أَنَّكُمْ تُكَذِّبُونَ', 56, 82, 27, 108, 537, 'و به جای شکر روزیهایی که به شما داده شده آن را تکذیب می‌کنید؟!'),
(5062, 5062, 'فَلَوْلَا إِذَا بَلَغَتِ الْحُلْقُومَ', 56, 83, 27, 108, 537, 'پس چرا هنگامی که جان به گلوگاه می‌رسد (توانایی بازگرداندن آن را ندارید)؟!'),
(5063, 5063, 'وَأَنْتُمْ حِينَئِذٍ تَنْظُرُونَ', 56, 84, 27, 108, 537, 'و شما در این حال نظاره می‌کنید (و کاری از دستتان ساخته نیست)؛'),
(5064, 5064, 'وَنَحْنُ أَقْرَبُ إِلَيْهِ مِنْكُمْ وَلَٰكِنْ لَا تُبْصِرُونَ', 56, 85, 27, 108, 537, 'و ما از شما به او نزدیکتریم ولی نمی‌بینید!'),
(5065, 5065, 'فَلَوْلَا إِنْ كُنْتُمْ غَيْرَ مَدِينِينَ', 56, 86, 27, 108, 537, 'اگر هرگز در برابر اعمالتان جزا داده نمی‌شوید،'),
(5066, 5066, 'تَرْجِعُونَهَا إِنْ كُنْتُمْ صَادِقِينَ', 56, 87, 27, 108, 537, 'پس آن (روح) را بازگردانید اگر راست می‌گویید!'),
(5067, 5067, 'فَأَمَّا إِنْ كَانَ مِنَ الْمُقَرَّبِينَ', 56, 88, 27, 108, 537, 'پس اگر او از مقرّبان باشد،'),
(5068, 5068, 'فَرَوْحٌ وَرَيْحَانٌ وَجَنَّتُ نَعِيمٍ', 56, 89, 27, 108, 537, 'در رَوح و ریحان و بهشت پرنعمت است!'),
(5069, 5069, 'وَأَمَّا إِنْ كَانَ مِنْ أَصْحَابِ الْيَمِينِ', 56, 90, 27, 108, 537, 'امّا اگر از اصحاب یمین باشد،'),
(5070, 5070, 'فَسَلَامٌ لَكَ مِنْ أَصْحَابِ الْيَمِينِ', 56, 91, 27, 108, 537, '(به او گفته می‌شود:) سلام بر تو از سوی دوستانت که از اصحاب یمینند!'),
(5071, 5071, 'وَأَمَّا إِنْ كَانَ مِنَ الْمُكَذِّبِينَ الضَّالِّينَ', 56, 92, 27, 108, 537, 'امّا اگر او از تکذیب‌کنندگان گمراه باشد،'),
(5072, 5072, 'فَنُزُلٌ مِنْ حَمِيمٍ', 56, 93, 27, 108, 537, 'با آب جوشان دوزخ از او پذیرایی می‌شوید!'),
(5073, 5073, 'وَتَصْلِيَةُ جَحِيمٍ', 56, 94, 27, 108, 537, 'و سرنوشت او ورود در آتش جهنّم است،'),
(5074, 5074, 'إِنَّ هَٰذَا لَهُوَ حَقُّ الْيَقِينِ', 56, 95, 27, 108, 537, 'این مطلب حقّ و یقین است!'),
(5075, 5075, 'فَسَبِّحْ بِاسْمِ رَبِّكَ الْعَظِيمِ', 56, 96, 27, 108, 537, 'پس به نام پروردگار بزرگت تسبیح کن (و او را منزّه بشمار)!'),
(5076, 5076, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ سَبَّحَ لِلَّهِ مَا فِي السَّمَاوَاتِ وَالْأَرْضِ ۖ وَهُوَ الْعَزِيزُ الْحَكِيمُ', 57, 1, 27, 108, 537, 'آنچه در آسمانها و زمین است برای خدا تسبیح می‌گویند؛ و او عزیز و حکیم است.'),
(5077, 5077, 'لَهُ مُلْكُ السَّمَاوَاتِ وَالْأَرْضِ ۖ يُحْيِي وَيُمِيتُ ۖ وَهُوَ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ', 57, 2, 27, 108, 537, 'مالکیّت (و حاکمیّت) آسمانها و زمین از آن اوست؛ زنده می‌کند و می‌میراند؛ و او بر هر چیز توانا است!'),
(5078, 5078, 'هُوَ الْأَوَّلُ وَالْآخِرُ وَالظَّاهِرُ وَالْبَاطِنُ ۖ وَهُوَ بِكُلِّ شَيْءٍ عَلِيمٌ', 57, 3, 27, 108, 537, 'اوّل و آخر و پیدا و پنهان اوست؛ و او به هر چیز داناست.'),
(5079, 5079, 'هُوَ الَّذِي خَلَقَ السَّمَاوَاتِ وَالْأَرْضَ فِي سِتَّةِ أَيَّامٍ ثُمَّ اسْتَوَىٰ عَلَى الْعَرْشِ ۚ يَعْلَمُ مَا يَلِجُ فِي الْأَرْضِ وَمَا يَخْرُجُ مِنْهَا وَمَا يَنْزِلُ مِنَ السَّمَاءِ وَمَا يَعْرُجُ فِيهَا ۖ وَهُوَ مَعَكُمْ أَيْنَ مَا كُنْتُمْ ۚ وَاللَّهُ بِمَا تَعْمَلُونَ بَصِيرٌ', 57, 4, 27, 108, 538, 'او کسی است که آسمانها و زمین را در شش روز = شش دوران‌ آفرید؛ سپس بر تخت قدرت قرار گرفت (و به تدبیر جهان پرداخت)؛ آنچه را در زمین فرو می‌رود می‌داند، و آنچه را از آن خارج می‌شود و آنچه از آسمان نازل می‌گردد و آنچه به آسمان بالا می‌رود؛ و هر جا باشید او با شما است، و خداوند نسبت به آنچه انجام می‌دهید بیناست!'),
(5080, 5080, 'لَهُ مُلْكُ السَّمَاوَاتِ وَالْأَرْضِ ۚ وَإِلَى اللَّهِ تُرْجَعُ الْأُمُورُ', 57, 5, 27, 108, 538, 'مالکیّت آسمانها و زمین از آن اوست؛ و همه کارها به سوی او بازمی‌گردد.'),
(5081, 5081, 'يُولِجُ اللَّيْلَ فِي النَّهَارِ وَيُولِجُ النَّهَارَ فِي اللَّيْلِ ۚ وَهُوَ عَلِيمٌ بِذَاتِ الصُّدُورِ', 57, 6, 27, 108, 538, 'شب را در روز می‌کند و روز را در شب؛ و او به آنچه در دل سینه‌ها وجود دارد داناست.'),
(5082, 5082, 'آمِنُوا بِاللَّهِ وَرَسُولِهِ وَأَنْفِقُوا مِمَّا جَعَلَكُمْ مُسْتَخْلَفِينَ فِيهِ ۖ فَالَّذِينَ آمَنُوا مِنْكُمْ وَأَنْفَقُوا لَهُمْ أَجْرٌ كَبِيرٌ', 57, 7, 27, 108, 538, 'به خدا و رسولش ایمان بیاورید و از آنچه شما را جانشین و نماینده (خود) در آن قرار داده انفاق کنید؛ (زیرا) کسانی که از شما ایمان بیاورند و انفاق کنند، اجر بزرگی دارند!'),
(5083, 5083, 'وَمَا لَكُمْ لَا تُؤْمِنُونَ بِاللَّهِ ۙ وَالرَّسُولُ يَدْعُوكُمْ لِتُؤْمِنُوا بِرَبِّكُمْ وَقَدْ أَخَذَ مِيثَاقَكُمْ إِنْ كُنْتُمْ مُؤْمِنِينَ', 57, 8, 27, 108, 538, 'چرا به خدا ایمان نیاورید در حالی که رسول (او) شما را می‌خواند که به پروردگارتان ایمان بیاورید، و از شما پیمان گرفته است (پیمانی از طریق فطرت و خِرد)، اگر آماده ایمان‌آوردنید.'),
(5084, 5084, 'هُوَ الَّذِي يُنَزِّلُ عَلَىٰ عَبْدِهِ آيَاتٍ بَيِّنَاتٍ لِيُخْرِجَكُمْ مِنَ الظُّلُمَاتِ إِلَى النُّورِ ۚ وَإِنَّ اللَّهَ بِكُمْ لَرَءُوفٌ رَحِيمٌ', 57, 9, 27, 108, 538, 'او کسی است که آیات روشنی بر بنده‌اش = محمد نازل می‌کند تا شما را از تاریکیها به سوی نور برد؛ و خداوند نسبت به شما مهربان و رحیم است.'),
(5085, 5085, 'وَمَا لَكُمْ أَلَّا تُنْفِقُوا فِي سَبِيلِ اللَّهِ وَلِلَّهِ مِيرَاثُ السَّمَاوَاتِ وَالْأَرْضِ ۚ لَا يَسْتَوِي مِنْكُمْ مَنْ أَنْفَقَ مِنْ قَبْلِ الْفَتْحِ وَقَاتَلَ ۚ أُولَٰئِكَ أَعْظَمُ دَرَجَةً مِنَ الَّذِينَ أَنْفَقُوا مِنْ بَعْدُ وَقَاتَلُوا ۚ وَكُلًّا وَعَدَ اللَّهُ الْحُسْنَىٰ ۚ وَاللَّهُ بِمَا تَعْمَلُونَ خَبِيرٌ', 57, 10, 27, 108, 538, 'چرا در راه خدا انفاق نکنید در حالی که میراث آسمانها و زمین همه از آن خداست (و کسی چیزی را با خود نمی‌برد)! کسانی که قبل از پیروزی انفاق کردند و جنگیدند (با کسانی که پس از پیروزی انفاق کردند) یکسان نیستند؛ آنها بلندمقامتر از کسانی هستند که بعد از فتح انفاق نمودند و جهاد کردند؛ و خداوند به هر دو وعده نیک داده؛ و خدا به آنچه انجام می‌دهید آگاه است.'),
(5086, 5086, 'مَنْ ذَا الَّذِي يُقْرِضُ اللَّهَ قَرْضًا حَسَنًا فَيُضَاعِفَهُ لَهُ وَلَهُ أَجْرٌ كَرِيمٌ', 57, 11, 27, 108, 538, 'کیست که به خدا وام نیکو دهد (و از اموالی که به او ارزانی داشته انفاق کند) تا خداوند آن را برای او چندین برابر کند؟ و برای او پاداش پرارزشی است!'),
(5087, 5087, 'يَوْمَ تَرَى الْمُؤْمِنِينَ وَالْمُؤْمِنَاتِ يَسْعَىٰ نُورُهُمْ بَيْنَ أَيْدِيهِمْ وَبِأَيْمَانِهِمْ بُشْرَاكُمُ الْيَوْمَ جَنَّاتٌ تَجْرِي مِنْ تَحْتِهَا الْأَنْهَارُ خَالِدِينَ فِيهَا ۚ ذَٰلِكَ هُوَ الْفَوْزُ الْعَظِيمُ', 57, 12, 27, 108, 539, '(این پاداش بزرگ) در روزی است که مردان و زنان باایمان را می‌نگری که نورشان پیش‌رو و در سمت راستشان بسرعت حرکت می‌کند (و به آنها می‌گویند:) بشارت باد بر شما امروز به باغهایی از بهشت که نهرها زیر (درختان) آن جاری است؛ جاودانه در آن خواهید ماند! و این همان رستگاری بزرگ است!'),
(5088, 5088, 'يَوْمَ يَقُولُ الْمُنَافِقُونَ وَالْمُنَافِقَاتُ لِلَّذِينَ آمَنُوا انْظُرُونَا نَقْتَبِسْ مِنْ نُورِكُمْ قِيلَ ارْجِعُوا وَرَاءَكُمْ فَالْتَمِسُوا نُورًا فَضُرِبَ بَيْنَهُمْ بِسُورٍ لَهُ بَابٌ بَاطِنُهُ فِيهِ الرَّحْمَةُ وَظَاهِرُهُ مِنْ قِبَلِهِ الْعَذَابُ', 57, 13, 27, 108, 539, 'روزی که مردان و زنان منافق به مؤمنان می‌گویند: «نظری به ما بیفکنید تا از نور شما پرتوی برگیریم!» به آنها گفته می‌شود: «به پشت سر خود بازگردید و کسب نور کنید!» در این هنگام دیواری میان آنها زده می‌شود که دری دارد، درونش رحمت است و برونش عذاب!'),
(5089, 5089, 'يُنَادُونَهُمْ أَلَمْ نَكُنْ مَعَكُمْ ۖ قَالُوا بَلَىٰ وَلَٰكِنَّكُمْ فَتَنْتُمْ أَنْفُسَكُمْ وَتَرَبَّصْتُمْ وَارْتَبْتُمْ وَغَرَّتْكُمُ الْأَمَانِيُّ حَتَّىٰ جَاءَ أَمْرُ اللَّهِ وَغَرَّكُمْ بِاللَّهِ الْغَرُورُ', 57, 14, 27, 108, 539, 'آنها را صدا می‌زنند: «مگر ما با شما نبودیم؟!» می‌گویند: «آری، ولی شما خود را به هلاکت افکندید و انتظار (مرگ پیامبر را) کشیدید، و (در همه چیز) شکّ و تردید داشتید، و آرزوهای دور و دراز شما را فریب داد تا فرمان خدا فرا رسید، و شیطان فریبکار شما را در برابر (فرمان) خداوند فریب داد!'),
(5090, 5090, 'فَالْيَوْمَ لَا يُؤْخَذُ مِنْكُمْ فِدْيَةٌ وَلَا مِنَ الَّذِينَ كَفَرُوا ۚ مَأْوَاكُمُ النَّارُ ۖ هِيَ مَوْلَاكُمْ ۖ وَبِئْسَ الْمَصِيرُ', 57, 15, 27, 108, 539, 'پس امروز نه از شما فدیه‌ای پذیرفته می‌شود، و نه از کافران؛ و جایگاهتان آتش است و همان سرپرستتان می‌باشد؛ و چه بد جایگاهی است!'),
(5091, 5091, ' أَلَمْ يَأْنِ لِلَّذِينَ آمَنُوا أَنْ تَخْشَعَ قُلُوبُهُمْ لِذِكْرِ اللَّهِ وَمَا نَزَلَ مِنَ الْحَقِّ وَلَا يَكُونُوا كَالَّذِينَ أُوتُوا الْكِتَابَ مِنْ قَبْلُ فَطَالَ عَلَيْهِمُ الْأَمَدُ فَقَسَتْ قُلُوبُهُمْ ۖ وَكَثِيرٌ مِنْهُمْ فَاسِقُونَ', 57, 16, 27, 108, 539, 'آیا وقت آن نرسیده است که دلهای مؤمنان در برابر ذکر خدا و آنچه از حقّ نازل کرده است خاشع گردد؟! و مانند کسانی نباشند که در گذشته به آنها کتاب آسمانی داده شد، سپس زمانی طولانی بر آنها گذشت و قلبهایشان قساوت پیدا کرد؛ و بسیاری از آنها گنهکارند!'),
(5092, 5092, 'اعْلَمُوا أَنَّ اللَّهَ يُحْيِي الْأَرْضَ بَعْدَ مَوْتِهَا ۚ قَدْ بَيَّنَّا لَكُمُ الْآيَاتِ لَعَلَّكُمْ تَعْقِلُونَ', 57, 17, 27, 108, 539, 'بدانید خداوند زمین را بعد از مرگ آن زنده می‌کند! ما آیات (خود) را برای شما بیان کردیم، شاید اندیشه کنید!'),
(5093, 5093, 'إِنَّ الْمُصَّدِّقِينَ وَالْمُصَّدِّقَاتِ وَأَقْرَضُوا اللَّهَ قَرْضًا حَسَنًا يُضَاعَفُ لَهُمْ وَلَهُمْ أَجْرٌ كَرِيمٌ', 57, 18, 27, 108, 539, 'مردان و زنان انفاق‌کننده، و آنها که (از این راه) به خدا «قرض الحسنه» دهند، (این قرض الحسنه) برای آنان مضاعف می‌شود و پاداش پرارزشی دارند!'),
(5094, 5094, 'وَالَّذِينَ آمَنُوا بِاللَّهِ وَرُسُلِهِ أُولَٰئِكَ هُمُ الصِّدِّيقُونَ ۖ وَالشُّهَدَاءُ عِنْدَ رَبِّهِمْ لَهُمْ أَجْرُهُمْ وَنُورُهُمْ ۖ وَالَّذِينَ كَفَرُوا وَكَذَّبُوا بِآيَاتِنَا أُولَٰئِكَ أَصْحَابُ الْجَحِيمِ', 57, 19, 27, 108, 540, 'کسانی که به خدا و رسولانش ایمان آوردند، آنها صدّیقین و شهدا نزد پروردگارشانند؛ برای آنان است پاداش (اعمال) شان و نور (ایمان) شان؛ و کسانی که کافر شدند و آیات ما را تکذیب کردند، آنها دوزخیانند.'),
(5095, 5095, 'اعْلَمُوا أَنَّمَا الْحَيَاةُ الدُّنْيَا لَعِبٌ وَلَهْوٌ وَزِينَةٌ وَتَفَاخُرٌ بَيْنَكُمْ وَتَكَاثُرٌ فِي الْأَمْوَالِ وَالْأَوْلَادِ ۖ كَمَثَلِ غَيْثٍ أَعْجَبَ الْكُفَّارَ نَبَاتُهُ ثُمَّ يَهِيجُ فَتَرَاهُ مُصْفَرًّا ثُمَّ يَكُونُ حُطَامًا ۖ وَفِي الْآخِرَةِ عَذَابٌ شَدِيدٌ وَمَغْفِرَةٌ مِنَ اللَّهِ وَرِضْوَانٌ ۚ وَمَا الْحَيَاةُ الدُّنْيَا إِلَّا مَتَاعُ الْغُرُورِ', 57, 20, 27, 108, 540, 'بدانید زندگی دنیا تنها بازی و سرگرمی و تجمّل پرستی و فخرفروشی در میان شما و افزون طلبی در اموال و فرزندان است، همانند بارانی که محصولش کشاورزان را در شگفتی فرو می‌برد، سپس خشک می‌شود بگونه‌ای که آن را زردرنگ می‌بینی؛ سپس تبدیل به کاه می‌شود! و در آخرت، عذاب شدید است یا مغفرت و رضای الهی؛ و (به هر حال) زندگی دنیا چیزی جز متاع فریب نیست!'),
(5096, 5096, 'سَابِقُوا إِلَىٰ مَغْفِرَةٍ مِنْ رَبِّكُمْ وَجَنَّةٍ عَرْضُهَا كَعَرْضِ السَّمَاءِ وَالْأَرْضِ أُعِدَّتْ لِلَّذِينَ آمَنُوا بِاللَّهِ وَرُسُلِهِ ۚ ذَٰلِكَ فَضْلُ اللَّهِ يُؤْتِيهِ مَنْ يَشَاءُ ۚ وَاللَّهُ ذُو الْفَضْلِ الْعَظِيمِ', 57, 21, 27, 108, 540, 'به پیش تازید برای رسیدن به مغفرت پروردگارتان و بهشتی که پهنه آن مانند پهنه آسمان و زمین است و برای کسانی که به خدا و رسولانش ایمان آورده‌اند؛ آماده شده است، این فضل خداوند است که به هر کس بخواهد می‌دهد؛ و خداوند صاحب فضل عظیم است!'),
(5097, 5097, 'مَا أَصَابَ مِنْ مُصِيبَةٍ فِي الْأَرْضِ وَلَا فِي أَنْفُسِكُمْ إِلَّا فِي كِتَابٍ مِنْ قَبْلِ أَنْ نَبْرَأَهَا ۚ إِنَّ ذَٰلِكَ عَلَى اللَّهِ يَسِيرٌ', 57, 22, 27, 108, 540, 'هیچ مصیبتی (ناخواسته) در زمین و نه در وجود شما روی نمی‌دهد مگر اینکه همه آنها قبل از آنکه زمین را بیافرینیم در لوح محفوظ ثبت است؛ و این امر برای خدا آسان است!'),
(5098, 5098, 'لِكَيْلَا تَأْسَوْا عَلَىٰ مَا فَاتَكُمْ وَلَا تَفْرَحُوا بِمَا آتَاكُمْ ۗ وَاللَّهُ لَا يُحِبُّ كُلَّ مُخْتَالٍ فَخُورٍ', 57, 23, 27, 108, 540, 'این بخاطر آن است که برای آنچه از دست داده‌اید تأسف نخورید، و به آنچه به شما داده است دلبسته و شادمان نباشید؛ و خداوند هیچ متکبّر فخرفروشی را دوست ندارد!'),
(5099, 5099, 'الَّذِينَ يَبْخَلُونَ وَيَأْمُرُونَ النَّاسَ بِالْبُخْلِ ۗ وَمَنْ يَتَوَلَّ فَإِنَّ اللَّهَ هُوَ الْغَنِيُّ الْحَمِيدُ', 57, 24, 27, 108, 540, 'همانها که بخل می‌ورزند و مردم را به بخل دعوت می‌کنند؛ و هر کس (از این فرمان) روی‌گردان شود، (به خود زیان می‌رساند نه به خدا)، چرا که خداوند بی‌نیاز و شایسته ستایش است!'),
(5100, 5100, 'لَقَدْ أَرْسَلْنَا رُسُلَنَا بِالْبَيِّنَاتِ وَأَنْزَلْنَا مَعَهُمُ الْكِتَابَ وَالْمِيزَانَ لِيَقُومَ النَّاسُ بِالْقِسْطِ ۖ وَأَنْزَلْنَا الْحَدِيدَ فِيهِ بَأْسٌ شَدِيدٌ وَمَنَافِعُ لِلنَّاسِ وَلِيَعْلَمَ اللَّهُ مَنْ يَنْصُرُهُ وَرُسُلَهُ بِالْغَيْبِ ۚ إِنَّ اللَّهَ قَوِيٌّ عَزِيزٌ', 57, 25, 27, 108, 541, 'ما رسولان خود را با دلایل روشن فرستادیم، و با آنها کتاب (آسمانی) و میزان (شناسایی حقّ از باطل و قوانین عادلانه) نازل کردیم تا مردم قیام به عدالت کنند؛ و آهن را نازل کردیم که در آن نیروی شدید و منافعی برای مردم است، تا خداوند بداند چه کسی او و رسولانش را یاری می‌کند بی‌آنکه او را ببینند؛ خداوند قوّی و شکست‌ناپذیر است!'),
(5101, 5101, 'وَلَقَدْ أَرْسَلْنَا نُوحًا وَإِبْرَاهِيمَ وَجَعَلْنَا فِي ذُرِّيَّتِهِمَا النُّبُوَّةَ وَالْكِتَابَ ۖ فَمِنْهُمْ مُهْتَدٍ ۖ وَكَثِيرٌ مِنْهُمْ فَاسِقُونَ', 57, 26, 27, 108, 541, 'ما نوح و ابراهیم را فرستادیم، و در دودمان آن دو نبوّت و کتاب قرار دادیم؛ بعضی از آنها هدایت یافته‌اند و بسیاری از آنها گنهکارند.'),
(5102, 5102, 'ثُمَّ قَفَّيْنَا عَلَىٰ آثَارِهِمْ بِرُسُلِنَا وَقَفَّيْنَا بِعِيسَى ابْنِ مَرْيَمَ وَآتَيْنَاهُ الْإِنْجِيلَ وَجَعَلْنَا فِي قُلُوبِ الَّذِينَ اتَّبَعُوهُ رَأْفَةً وَرَحْمَةً وَرَهْبَانِيَّةً ابْتَدَعُوهَا مَا كَتَبْنَاهَا عَلَيْهِمْ إِلَّا ابْتِغَاءَ رِضْوَانِ اللَّهِ فَمَا رَعَوْهَا حَقَّ رِعَايَتِهَا ۖ فَآتَيْنَا الَّذِينَ آمَنُوا مِنْهُمْ أَجْرَهُمْ ۖ وَكَثِيرٌ مِنْهُمْ فَاسِقُونَ', 57, 27, 27, 108, 541, 'سپس در پی آنان رسولان دیگر خود را فرستادیم، و بعد از آنان عیسی بن مریم را مبعوث کردیم و به او انجیل عطا کردیم، و در دل کسانی که از او پیروی کردند رأفت و رحمت قرار دادیم؛ و رهبانیّتی را که ابداع کرده بودند، ما بر آنان مقرّر نداشته بودیم؛ گرچه هدفشان جلب خشنودی خدا بود، ولی حقّ آن را رعایت نکردند؛ از این‌رو ما به کسانی از آنها که ایمان آوردند پاداششان را دادیم؛ و بسیاری از آنها فاسقند!'),
(5103, 5103, 'يَا أَيُّهَا الَّذِينَ آمَنُوا اتَّقُوا اللَّهَ وَآمِنُوا بِرَسُولِهِ يُؤْتِكُمْ كِفْلَيْنِ مِنْ رَحْمَتِهِ وَيَجْعَلْ لَكُمْ نُورًا تَمْشُونَ بِهِ وَيَغْفِرْ لَكُمْ ۚ وَاللَّهُ غَفُورٌ رَحِيمٌ', 57, 28, 27, 108, 541, 'ای کسانی که ایمان آورده‌اید! تقوای الهی پیشه کنید و به رسولش ایمان بیاورید تا دو سهم از رحمتش به شما ببخشد و برای شما نوری قرار دهد که با آن (در میان مردم و در مسیر زندگی خود) راه بروید و گناهان شما را ببخشد؛ و خداوند غفور و رحیم است.'),
(5104, 5104, 'لِئَلَّا يَعْلَمَ أَهْلُ الْكِتَابِ أَلَّا يَقْدِرُونَ عَلَىٰ شَيْءٍ مِنْ فَضْلِ اللَّهِ ۙ وَأَنَّ الْفَضْلَ بِيَدِ اللَّهِ يُؤْتِيهِ مَنْ يَشَاءُ ۚ وَاللَّهُ ذُو الْفَضْلِ الْعَظِيمِ', 57, 29, 27, 108, 541, 'تا اهل کتاب بدانند که قادر بر چیزی از فضل خدا نیستند، و تمام فضل (و رحمت) به دست اوست، به هر کس بخواهد آن را می‌بخشد؛ و خداوند دارای فضل عظیم است!'),
(5105, 5105, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ قَدْ سَمِعَ اللَّهُ قَوْلَ الَّتِي تُجَادِلُكَ فِي زَوْجِهَا وَتَشْتَكِي إِلَى اللَّهِ وَاللَّهُ يَسْمَعُ تَحَاوُرَكُمَا ۚ إِنَّ اللَّهَ سَمِيعٌ بَصِيرٌ', 58, 1, 28, 109, 542, 'خداوند سخن زنی را که درباره شوهرش به تو مراجعه کرده بود و به خداوند شکایت می‌کرد شنید (و تقاضای او را اجابت کرد)؛ خداوند گفتگوی شما را با هم (و اصرار آن زن را درباره حل مشکلش) می‌شنید؛ و خداوند شنوا و بیناست.'),
(5106, 5106, 'الَّذِينَ يُظَاهِرُونَ مِنْكُمْ مِنْ نِسَائِهِمْ مَا هُنَّ أُمَّهَاتِهِمْ ۖ إِنْ أُمَّهَاتُهُمْ إِلَّا اللَّائِي وَلَدْنَهُمْ ۚ وَإِنَّهُمْ لَيَقُولُونَ مُنْكَرًا مِنَ الْقَوْلِ وَزُورًا ۚ وَإِنَّ اللَّهَ لَعَفُوٌّ غَفُورٌ', 58, 2, 28, 109, 542, 'کسانی که از شما نسبت به همسرانشان «ظهار» می‌کنند (و می‌گویند: «أَنتِ عَلیَّ کَظهَرِ أُمّی = تو نسبت به من بمنزله مادرم هستی»)، آنان هرگز مادرانشان نیستند؛ مادرانشان تنها کسانی‌اند که آنها را به دنیا آورده‌اند! آنها سخنی زشت و باطل می‌گویند؛ و خداوند بخشنده و آمرزنده است!'),
(5107, 5107, 'وَالَّذِينَ يُظَاهِرُونَ مِنْ نِسَائِهِمْ ثُمَّ يَعُودُونَ لِمَا قَالُوا فَتَحْرِيرُ رَقَبَةٍ مِنْ قَبْلِ أَنْ يَتَمَاسَّا ۚ ذَٰلِكُمْ تُوعَظُونَ بِهِ ۚ وَاللَّهُ بِمَا تَعْمَلُونَ خَبِيرٌ', 58, 3, 28, 109, 542, 'کسانی که همسران خود را «ظِهار» می‌کنند، سپس از گفته خود بازمی‌گردند، باید پیش از آمیزش جنسی با هم، برده‌ای را آزاد کنند؛ این دستوری است که به آن اندرز داده می‌شوید؛ و خداوند به آنچه انجام می‌دهید آگاه است!'),
(5108, 5108, 'فَمَنْ لَمْ يَجِدْ فَصِيَامُ شَهْرَيْنِ مُتَتَابِعَيْنِ مِنْ قَبْلِ أَنْ يَتَمَاسَّا ۖ فَمَنْ لَمْ يَسْتَطِعْ فَإِطْعَامُ سِتِّينَ مِسْكِينًا ۚ ذَٰلِكَ لِتُؤْمِنُوا بِاللَّهِ وَرَسُولِهِ ۚ وَتِلْكَ حُدُودُ اللَّهِ ۗ وَلِلْكَافِرِينَ عَذَابٌ أَلِيمٌ', 58, 4, 28, 109, 542, 'و کسی که توانایی (آزاد کردن برده‌ای) نداشته باشد، دو ماه پیاپی قبل از آمیزش روزه بگیرد؛ و کسی که این را هم نتواند، شصت مسکین را اطعام کند؛ این برای آن است که به خدا و رسولش ایمان بیاورید؛ اینها مرزهای الهی است؛ و کسانی که با آن مخالفت کنند، عذاب دردناکی دارند!'),
(5109, 5109, 'إِنَّ الَّذِينَ يُحَادُّونَ اللَّهَ وَرَسُولَهُ كُبِتُوا كَمَا كُبِتَ الَّذِينَ مِنْ قَبْلِهِمْ ۚ وَقَدْ أَنْزَلْنَا آيَاتٍ بَيِّنَاتٍ ۚ وَلِلْكَافِرِينَ عَذَابٌ مُهِينٌ', 58, 5, 28, 109, 542, 'کسانی که با خدا و رسولش دشمنی می‌کنند خوار و ذلیل شدند آن‌گونه که پیشینیان خوار و ذلیل شدند؛ ما آیات روشنی نازل کردیم، و برای کافران عذاب خوارکننده‌ای است...'),
(5110, 5110, 'يَوْمَ يَبْعَثُهُمُ اللَّهُ جَمِيعًا فَيُنَبِّئُهُمْ بِمَا عَمِلُوا ۚ أَحْصَاهُ اللَّهُ وَنَسُوهُ ۚ وَاللَّهُ عَلَىٰ كُلِّ شَيْءٍ شَهِيدٌ', 58, 6, 28, 109, 542, 'در آن روز که خداوند همه آنها را برمی‌انگیزد و از اعمالی که انجام دادند با خبر می‌سازد، اعمالی که خداوند حساب آن را نگه داشته و آنها فراموشش کردند؛ و خداوند بر هر چیز شاهد و ناظر است!'),
(5111, 5111, 'أَلَمْ تَرَ أَنَّ اللَّهَ يَعْلَمُ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ ۖ مَا يَكُونُ مِنْ نَجْوَىٰ ثَلَاثَةٍ إِلَّا هُوَ رَابِعُهُمْ وَلَا خَمْسَةٍ إِلَّا هُوَ سَادِسُهُمْ وَلَا أَدْنَىٰ مِنْ ذَٰلِكَ وَلَا أَكْثَرَ إِلَّا هُوَ مَعَهُمْ أَيْنَ مَا كَانُوا ۖ ثُمَّ يُنَبِّئُهُمْ بِمَا عَمِلُوا يَوْمَ الْقِيَامَةِ ۚ إِنَّ اللَّهَ بِكُلِّ شَيْءٍ عَلِيمٌ', 58, 7, 28, 109, 543, 'آیا نمی‌دانی که خداوند آنچه را در آسمانها و آنچه را در زمین است می‌داند؛ هیچ گاه سه نفر با هم نجوا نمی‌کنند مگر اینکه خداوند چهارمین آنهاست، و هیچ گاه پنج نفر با هم نجوا نمی‌کنند مگر اینکه خداوند ششمین آنهاست، و نه تعدادی کمتر و نه بیشتر از آن مگر اینکه او همراه آنهاست هر جا که باشند، سپس روز قیامت آنها را از اعمالشان آگاه می‌سازد، چرا که خداوند به هر چیزی داناست!'),
(5112, 5112, 'أَلَمْ تَرَ إِلَى الَّذِينَ نُهُوا عَنِ النَّجْوَىٰ ثُمَّ يَعُودُونَ لِمَا نُهُوا عَنْهُ وَيَتَنَاجَوْنَ بِالْإِثْمِ وَالْعُدْوَانِ وَمَعْصِيَتِ الرَّسُولِ وَإِذَا جَاءُوكَ حَيَّوْكَ بِمَا لَمْ يُحَيِّكَ بِهِ اللَّهُ وَيَقُولُونَ فِي أَنْفُسِهِمْ لَوْلَا يُعَذِّبُنَا اللَّهُ بِمَا نَقُولُ ۚ حَسْبُهُمْ جَهَنَّمُ يَصْلَوْنَهَا ۖ فَبِئْسَ الْمَصِيرُ', 58, 8, 28, 109, 543, 'آیا ندیدی کسانی را که از نجوا = سخنان درگوشی‌ نهی شدند، سپس به کاری که از آن نهی شده بودند بازمی‌گردند و برای انجام گناه و تعدّی و نافرمانی رسول خدا به نجوا می‌پردازند و هنگامی که نزد تو می‌آیند تو را تحیّتی (و خوشامدی) می‌گویند که خدا به تو نگفته است، و در دل می‌گویند: «چرا خداوند ما را بخاطر گفته‌هایمان عذاب نمی‌کند؟!» جهنم برای آنان کافی است، وارد آن می‌شوند، و چه بد فرجامی است!'),
(5113, 5113, 'يَا أَيُّهَا الَّذِينَ آمَنُوا إِذَا تَنَاجَيْتُمْ فَلَا تَتَنَاجَوْا بِالْإِثْمِ وَالْعُدْوَانِ وَمَعْصِيَتِ الرَّسُولِ وَتَنَاجَوْا بِالْبِرِّ وَالتَّقْوَىٰ ۖ وَاتَّقُوا اللَّهَ الَّذِي إِلَيْهِ تُحْشَرُونَ', 58, 9, 28, 109, 543, 'ای کسانی که ایمان آورده‌اید! هنگامی که نجوا می‌کنید، و به گناه و تعدّی و نافرمانی رسول (خدا) نجوا نکنید، و به کار نیک و تقوا نجوا کنید، و از خدایی که همگی نزد او جمع می‌شوید بپرهیزید!'),
(5114, 5114, 'إِنَّمَا النَّجْوَىٰ مِنَ الشَّيْطَانِ لِيَحْزُنَ الَّذِينَ آمَنُوا وَلَيْسَ بِضَارِّهِمْ شَيْئًا إِلَّا بِإِذْنِ اللَّهِ ۚ وَعَلَى اللَّهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُونَ', 58, 10, 28, 109, 543, 'نجوا تنها از سوی شیطان است؛ می‌خواهد با آن مؤمنان غمگین شوند؛ ولی نمی تواند هیچ گونه ضرری به آنها برساند جز بفرمان خدا؛ پس مؤمنان تنها بر خدا توکّل کنند!'),
(5115, 5115, 'يَا أَيُّهَا الَّذِينَ آمَنُوا إِذَا قِيلَ لَكُمْ تَفَسَّحُوا فِي الْمَجَالِسِ فَافْسَحُوا يَفْسَحِ اللَّهُ لَكُمْ ۖ وَإِذَا قِيلَ انْشُزُوا فَانْشُزُوا يَرْفَعِ اللَّهُ الَّذِينَ آمَنُوا مِنْكُمْ وَالَّذِينَ أُوتُوا الْعِلْمَ دَرَجَاتٍ ۚ وَاللَّهُ بِمَا تَعْمَلُونَ خَبِيرٌ', 58, 11, 28, 109, 543, 'ای کسانی که ایمان آورده‌اید! هنگامی که به شما گفته شود: «مجلس را وسعت بخشید (و به تازه‌واردها جا دهید)»، وسعت بخشید، خداوند (بهشت را) برای شما وسعت می‌بخشد؛ و هنگامی که گفته شود: «برخیزید»، برخیزید؛ اگر چنین کنید، خداوند کسانی را که ایمان آورده‌اند و کسانی را که علم به آنان داده شده درجات عظیمی می‌بخشد؛ و خداوند به آنچه انجام می‌دهید آگاه است!'),
(5116, 5116, 'يَا أَيُّهَا الَّذِينَ آمَنُوا إِذَا نَاجَيْتُمُ الرَّسُولَ فَقَدِّمُوا بَيْنَ يَدَيْ نَجْوَاكُمْ صَدَقَةً ۚ ذَٰلِكَ خَيْرٌ لَكُمْ وَأَطْهَرُ ۚ فَإِنْ لَمْ تَجِدُوا فَإِنَّ اللَّهَ غَفُورٌ رَحِيمٌ', 58, 12, 28, 109, 544, 'ای کسانی که ایمان آورده‌اید! هنگامی که می‌خواهید با رسول خدا نجوا کنید (و سخنان درگوشی بگویید)، قبل از آن صدقه‌ای (در راه خدا) بدهید؛ این برای شما بهتر و پاکیزه‌تر است و اگر توانایی نداشته باشید، خداوند غفور و رحیم است!'),
(5117, 5117, 'أَأَشْفَقْتُمْ أَنْ تُقَدِّمُوا بَيْنَ يَدَيْ نَجْوَاكُمْ صَدَقَاتٍ ۚ فَإِذْ لَمْ تَفْعَلُوا وَتَابَ اللَّهُ عَلَيْكُمْ فَأَقِيمُوا الصَّلَاةَ وَآتُوا الزَّكَاةَ وَأَطِيعُوا اللَّهَ وَرَسُولَهُ ۚ وَاللَّهُ خَبِيرٌ بِمَا تَعْمَلُونَ', 58, 13, 28, 109, 544, 'آیا ترسیدید فقیر شوید که از دادن صدقات قبل از نجوا خودداری کردید؟! اکنون که این کار را نکردید و خداوند توبه شما را پذیرفت، نماز را برپا دارید و زکات را ادا کنید و خدا و پیامبرش را اطاعت نمایید و (بدانید) خداوند از آنچه انجام می‌دهید با خبر است!'),
(5118, 5118, ' أَلَمْ تَرَ إِلَى الَّذِينَ تَوَلَّوْا قَوْمًا غَضِبَ اللَّهُ عَلَيْهِمْ مَا هُمْ مِنْكُمْ وَلَا مِنْهُمْ وَيَحْلِفُونَ عَلَى الْكَذِبِ وَهُمْ يَعْلَمُونَ', 58, 14, 28, 109, 544, 'آیا ندیدی کسانی را که طرح دوستی با گروهی که مورد غضب خدا بودند ریختند؟! آنها نه از شما هستند و نه از آنان! سوگند دروغ یاد می‌کنند (که از شما هستند) در حالی که خودشان می‌دانند (دروغ نمی‌گویند)!'),
(5119, 5119, 'أَعَدَّ اللَّهُ لَهُمْ عَذَابًا شَدِيدًا ۖ إِنَّهُمْ سَاءَ مَا كَانُوا يَعْمَلُونَ', 58, 15, 28, 109, 544, 'خداوند عذاب شدیدی برای آنان فراهم ساخته، چرا که اعمال بدی انجام می‌دادند!'),
(5120, 5120, 'اتَّخَذُوا أَيْمَانَهُمْ جُنَّةً فَصَدُّوا عَنْ سَبِيلِ اللَّهِ فَلَهُمْ عَذَابٌ مُهِينٌ', 58, 16, 28, 109, 544, 'آنها سوگندهای خود را سپری قرار دادند و مردم را از راه خدا بازداشتند؛ از این‌رو برای آنان عذاب خوارکننده‌ای است!'),
(5121, 5121, 'لَنْ تُغْنِيَ عَنْهُمْ أَمْوَالُهُمْ وَلَا أَوْلَادُهُمْ مِنَ اللَّهِ شَيْئًا ۚ أُولَٰئِكَ أَصْحَابُ النَّارِ ۖ هُمْ فِيهَا خَالِدُونَ', 58, 17, 28, 109, 544, 'هرگز اموال و اولادشان آنها را از عذاب الهی حفظ نمی‌کند؛ آنها اهل آتشند و جاودانه در آن می‌مانند!'),
(5122, 5122, 'يَوْمَ يَبْعَثُهُمُ اللَّهُ جَمِيعًا فَيَحْلِفُونَ لَهُ كَمَا يَحْلِفُونَ لَكُمْ ۖ وَيَحْسَبُونَ أَنَّهُمْ عَلَىٰ شَيْءٍ ۚ أَلَا إِنَّهُمْ هُمُ الْكَاذِبُونَ', 58, 18, 28, 109, 544, '(به خاطر بیاورید) روزی را که خداوند همه آنها را برمی‌انگیزد، آنها برای خدا نیز سوگند (دروغ) یاد می‌کنند همان‌گونه که (امروز) برای شما یاد می‌کنند؛ و گمان می‌کنند کاری می‌توانند انجام دهند؛ بدانید آنها دروغگویانند!'),
(5123, 5123, 'اسْتَحْوَذَ عَلَيْهِمُ الشَّيْطَانُ فَأَنْسَاهُمْ ذِكْرَ اللَّهِ ۚ أُولَٰئِكَ حِزْبُ الشَّيْطَانِ ۚ أَلَا إِنَّ حِزْبَ الشَّيْطَانِ هُمُ الْخَاسِرُونَ', 58, 19, 28, 109, 544, 'شیطان بر آنان مسلّط شده و یاد خدا را از خاطر آنها برده؛ آنان حزب شیطانند! بدانید حزب شیطان زیانکارانند!'),
(5124, 5124, 'إِنَّ الَّذِينَ يُحَادُّونَ اللَّهَ وَرَسُولَهُ أُولَٰئِكَ فِي الْأَذَلِّينَ', 58, 20, 28, 109, 544, 'کسانی که با خدا و رسولش دشمنی می‌کنند، آنها در زُمره ذلیل‌ترین افرادند.!'),
(5125, 5125, 'كَتَبَ اللَّهُ لَأَغْلِبَنَّ أَنَا وَرُسُلِي ۚ إِنَّ اللَّهَ قَوِيٌّ عَزِيزٌ', 58, 21, 28, 109, 544, 'خداوند چنین مقرر داشته که من و رسولانم پیروز می‌شویم؛ چرا که خداوند قوّی و شکست‌ناپذیر است!'),
(5126, 5126, 'لَا تَجِدُ قَوْمًا يُؤْمِنُونَ بِاللَّهِ وَالْيَوْمِ الْآخِرِ يُوَادُّونَ مَنْ حَادَّ اللَّهَ وَرَسُولَهُ وَلَوْ كَانُوا آبَاءَهُمْ أَوْ أَبْنَاءَهُمْ أَوْ إِخْوَانَهُمْ أَوْ عَشِيرَتَهُمْ ۚ أُولَٰئِكَ كَتَبَ فِي قُلُوبِهِمُ الْإِيمَانَ وَأَيَّدَهُمْ بِرُوحٍ مِنْهُ ۖ وَيُدْخِلُهُمْ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهَارُ خَالِدِينَ فِيهَا ۚ رَضِيَ اللَّهُ عَنْهُمْ وَرَضُوا عَنْهُ ۚ أُولَٰئِكَ حِزْبُ اللَّهِ ۚ أَلَا إِنَّ حِزْبَ اللَّهِ هُمُ الْمُفْلِحُونَ', 58, 22, 28, 109, 545, 'هیچ قومی را که ایمان به خدا و روز رستاخیز دارند نمی‌یابی که با دشمنان خدا و رسولش دوستی کنند، هر چند پدران یا فرزندان یا برادران یا خویشاوندانشان باشند؛ آنان کسانی هستند که خدا ایمان را بر صفحه دلهایشان نوشته و با روحی از ناحیه خودش آنها را تقویت فرموده، و آنها را در باغهایی از بهشت وارد می‌کند که نهرها از زیر (درختانش) جاری است، جاودانه در آن می‌مانند؛ خدا از آنها خشنود است، و آنان نیز از خدا خشنودند؛ آنها «حزب اللّه» اند؛ بدانید «حزب اللّه» پیروزان و رستگارانند.'),
(5127, 5127, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ سَبَّحَ لِلَّهِ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ ۖ وَهُوَ الْعَزِيزُ الْحَكِيمُ', 59, 1, 28, 109, 545, 'آنچه در آسمانها و آنچه در زمین است، برای خدا تسبیح می‌گوید؛ و او عزیز و حکیم است!'),
(5128, 5128, 'هُوَ الَّذِي أَخْرَجَ الَّذِينَ كَفَرُوا مِنْ أَهْلِ الْكِتَابِ مِنْ دِيَارِهِمْ لِأَوَّلِ الْحَشْرِ ۚ مَا ظَنَنْتُمْ أَنْ يَخْرُجُوا ۖ وَظَنُّوا أَنَّهُمْ مَانِعَتُهُمْ حُصُونُهُمْ مِنَ اللَّهِ فَأَتَاهُمُ اللَّهُ مِنْ حَيْثُ لَمْ يَحْتَسِبُوا ۖ وَقَذَفَ فِي قُلُوبِهِمُ الرُّعْبَ ۚ يُخْرِبُونَ بُيُوتَهُمْ بِأَيْدِيهِمْ وَأَيْدِي الْمُؤْمِنِينَ فَاعْتَبِرُوا يَا أُولِي الْأَبْصَارِ', 59, 2, 28, 109, 545, 'او کسی است که کافران اهل کتاب را در نخستین برخورد (با مسلمانان) از خانه‌هایشان بیرون راند! گمان نمی‌کردید آنان خارج شوند، و خودشان نیز گمان می‌کردند که دِژهای محکمشان آنها را از عذاب الهی مانع می‌شود؛ امّا خداوند از آنجا که گمان نمی‌کردند به سراغشان آمد و در دلهایشان ترس و وحشت افکند، بگونه‌ای که خانه‌های خود را با دست خویش و با دست مؤمنان ویران می‌کردند؛ پس عبرت بگیرید ای صاحبان چشم!'),
(5129, 5129, 'وَلَوْلَا أَنْ كَتَبَ اللَّهُ عَلَيْهِمُ الْجَلَاءَ لَعَذَّبَهُمْ فِي الدُّنْيَا ۖ وَلَهُمْ فِي الْآخِرَةِ عَذَابُ النَّارِ', 59, 3, 28, 109, 545, 'و اگر نه این بود که خداوند ترک وطن را بر آنان مقرّر داشته بود، آنها را در همین دنیا مجازات می‌کرد؛ و برای آنان در آخرت نیز عذاب آتش است!'),
(5130, 5130, 'ذَٰلِكَ بِأَنَّهُمْ شَاقُّوا اللَّهَ وَرَسُولَهُ ۖ وَمَنْ يُشَاقِّ اللَّهَ فَإِنَّ اللَّهَ شَدِيدُ الْعِقَابِ', 59, 4, 28, 109, 546, 'این به خاطر آن است که آنها با خدا و رسولش دشمنی کردند؛ و هر کس با خدا دشمنی کند (باید بداند) که خدا مجازات شدیدی دارد!'),
(5131, 5131, 'مَا قَطَعْتُمْ مِنْ لِينَةٍ أَوْ تَرَكْتُمُوهَا قَائِمَةً عَلَىٰ أُصُولِهَا فَبِإِذْنِ اللَّهِ وَلِيُخْزِيَ الْفَاسِقِينَ', 59, 5, 28, 109, 546, 'هر درخت باارزش نخل را قطع کردید یا آن را به حال خود واگذاشتید، همه به فرمان خدا بود؛ و برای این بود که فاسقان را خوار و رسوا کند!'),
(5132, 5132, 'وَمَا أَفَاءَ اللَّهُ عَلَىٰ رَسُولِهِ مِنْهُمْ فَمَا أَوْجَفْتُمْ عَلَيْهِ مِنْ خَيْلٍ وَلَا رِكَابٍ وَلَٰكِنَّ اللَّهَ يُسَلِّطُ رُسُلَهُ عَلَىٰ مَنْ يَشَاءُ ۚ وَاللَّهُ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ', 59, 6, 28, 109, 546, 'و آنچه را خدا از آنان = یهود به رسولش بازگردانده (و بخشیده) چیزی است که شما برای به دست آوردن آن (زحمتی نکشیدید،) نه اسبی تاختید و نه شتری؛ ولی خداوند رسولان خود را بر هر کس بخواهد مسلّط می‌سازد؛ و خدا بر هر چیز توانا است!'),
(5133, 5133, 'مَا أَفَاءَ اللَّهُ عَلَىٰ رَسُولِهِ مِنْ أَهْلِ الْقُرَىٰ فَلِلَّهِ وَلِلرَّسُولِ وَلِذِي الْقُرْبَىٰ وَالْيَتَامَىٰ وَالْمَسَاكِينِ وَابْنِ السَّبِيلِ كَيْ لَا يَكُونَ دُولَةً بَيْنَ الْأَغْنِيَاءِ مِنْكُمْ ۚ وَمَا آتَاكُمُ الرَّسُولُ فَخُذُوهُ وَمَا نَهَاكُمْ عَنْهُ فَانْتَهُوا ۚ وَاتَّقُوا اللَّهَ ۖ إِنَّ اللَّهَ شَدِيدُ الْعِقَابِ', 59, 7, 28, 109, 546, 'آنچه را خداوند از اهل این آبادیها به رسولش بازگرداند، از آن خدا و رسول و خویشاوندان او، و یتیمان و مستمندان و در راه ماندگان است، تا (این اموال عظیم) در میان ثروتمندان شما دست به دست نگردد! آنچه را رسول خدا برای شما آورده بگیرید (و اجرا کنید)، و از آنچه نهی کرده خودداری نمایید؛ و از (مخالفت) خدا بپرهیزید که خداوند کیفرش شدید است!'),
(5134, 5134, 'لِلْفُقَرَاءِ الْمُهَاجِرِينَ الَّذِينَ أُخْرِجُوا مِنْ دِيَارِهِمْ وَأَمْوَالِهِمْ يَبْتَغُونَ فَضْلًا مِنَ اللَّهِ وَرِضْوَانًا وَيَنْصُرُونَ اللَّهَ وَرَسُولَهُ ۚ أُولَٰئِكَ هُمُ الصَّادِقُونَ', 59, 8, 28, 109, 546, 'این اموال برای فقیران مهاجرانی است که از خانه و کاشانه و اموال خود بیرون رانده شدند در حالی که فضل الهی و رضای او را می‌طلبند و خدا و رسولش را یاری می‌کنند؛ و آنها راستگویانند!'),
(5135, 5135, 'وَالَّذِينَ تَبَوَّءُوا الدَّارَ وَالْإِيمَانَ مِنْ قَبْلِهِمْ يُحِبُّونَ مَنْ هَاجَرَ إِلَيْهِمْ وَلَا يَجِدُونَ فِي صُدُورِهِمْ حَاجَةً مِمَّا أُوتُوا وَيُؤْثِرُونَ عَلَىٰ أَنْفُسِهِمْ وَلَوْ كَانَ بِهِمْ خَصَاصَةٌ ۚ وَمَنْ يُوقَ شُحَّ نَفْسِهِ فَأُولَٰئِكَ هُمُ الْمُفْلِحُونَ', 59, 9, 28, 109, 546, 'و برای کسانی است که در این سرا = سرزمین مدینه‌ و در سرای ایمان پیش از مهاجران مسکن گزیدند و کسانی را که به سویشان هجرت کنند دوست می‌دارند، و در دل خود نیازی به آنچه به مهاجران داده شده احساس نمی‌کنند و آنها را بر خود مقدّم می‌دارند هر چند خودشان بسیار نیازمند باشند؛ کسانی که از بخل و حرص نفس خویش باز داشته شده‌اند رستگارانند!'),
(5136, 5136, 'وَالَّذِينَ جَاءُوا مِنْ بَعْدِهِمْ يَقُولُونَ رَبَّنَا اغْفِرْ لَنَا وَلِإِخْوَانِنَا الَّذِينَ سَبَقُونَا بِالْإِيمَانِ وَلَا تَجْعَلْ فِي قُلُوبِنَا غِلًّا لِلَّذِينَ آمَنُوا رَبَّنَا إِنَّكَ رَءُوفٌ رَحِيمٌ', 59, 10, 28, 109, 547, '(همچنین) کسانی که بعد از آنها = بعد از مهاجران و انصار آمدند و می‌گویند: «پروردگارا! ما و برادرانمان را که در ایمان بر ما پیشی گرفتند بیامرز، و در دلهایمان حسد و کینه‌ای نسبت به مؤمنان قرار مده! پروردگارا، تو مهربان و رحیمی!»'),
(5137, 5137, ' أَلَمْ تَرَ إِلَى الَّذِينَ نَافَقُوا يَقُولُونَ لِإِخْوَانِهِمُ الَّذِينَ كَفَرُوا مِنْ أَهْلِ الْكِتَابِ لَئِنْ أُخْرِجْتُمْ لَنَخْرُجَنَّ مَعَكُمْ وَلَا نُطِيعُ فِيكُمْ أَحَدًا أَبَدًا وَإِنْ قُوتِلْتُمْ لَنَنْصُرَنَّكُمْ وَاللَّهُ يَشْهَدُ إِنَّهُمْ لَكَاذِبُونَ', 59, 11, 28, 110, 547, 'آیا منافقان را ندیدی که پیوسته به برادران کفارشان از اهل کتاب می‌گفتند: «هرگاه شما را (از وطن) بیرون کنند، ما هم با شما بیرون خواهیم رفت و هرگز سخن هیچ کس را درباره شما اطاعت نخواهیم کرد؛ و اگر با شما پیکار شود، یاریتان خواهیم نمود!» خداوند شهادت می‌دهد که آنها دروغگویانند!'),
(5138, 5138, 'لَئِنْ أُخْرِجُوا لَا يَخْرُجُونَ مَعَهُمْ وَلَئِنْ قُوتِلُوا لَا يَنْصُرُونَهُمْ وَلَئِنْ نَصَرُوهُمْ لَيُوَلُّنَّ الْأَدْبَارَ ثُمَّ لَا يُنْصَرُونَ', 59, 12, 28, 110, 547, 'اگر آنها را بیرون کنند با آنان بیرون نمی‌روند، و اگر با آنها پیکار شود یاریشان نخواهند کرد، و اگر یاریشان کنند پشت به میدان کرده فرار می‌کنند؛ سپس کسی آنان را یاری نمی‌کند!'),
(5139, 5139, 'لَأَنْتُمْ أَشَدُّ رَهْبَةً فِي صُدُورِهِمْ مِنَ اللَّهِ ۚ ذَٰلِكَ بِأَنَّهُمْ قَوْمٌ لَا يَفْقَهُونَ', 59, 13, 28, 110, 547, 'وحشت از شما در دلهای آنها بیش از ترس از خداست؛ این به خاطر آن است که آنها گروهی نادانند!'),
(5140, 5140, 'لَا يُقَاتِلُونَكُمْ جَمِيعًا إِلَّا فِي قُرًى مُحَصَّنَةٍ أَوْ مِنْ وَرَاءِ جُدُرٍ ۚ بَأْسُهُمْ بَيْنَهُمْ شَدِيدٌ ۚ تَحْسَبُهُمْ جَمِيعًا وَقُلُوبُهُمْ شَتَّىٰ ۚ ذَٰلِكَ بِأَنَّهُمْ قَوْمٌ لَا يَعْقِلُونَ', 59, 14, 28, 110, 547, 'آنها هرگز با شما بصورت گروهی نمی‌جنگند جز در دژهای محکم یا از پشت دیوارها! پیکارشان در میان خودشان شدید است، (امّا در برابر شما ضعیف!) آنها را متّحد می‌پنداری، در حالی که دلهایشان پراکنده است؛ این به خاطر آن است که آنها قومی هستند که تعقّل نمی‌کنند!'),
(5141, 5141, 'كَمَثَلِ الَّذِينَ مِنْ قَبْلِهِمْ قَرِيبًا ۖ ذَاقُوا وَبَالَ أَمْرِهِمْ وَلَهُمْ عَذَابٌ أَلِيمٌ', 59, 15, 28, 110, 547, 'کار این گروه از یهود همانند کسانی است که کمی قبل از آنان بودند، طعم تلخ کار خود را چشیدند و برای آنها عذابی دردناک است!'),
(5142, 5142, 'كَمَثَلِ الشَّيْطَانِ إِذْ قَالَ لِلْإِنْسَانِ اكْفُرْ فَلَمَّا كَفَرَ قَالَ إِنِّي بَرِيءٌ مِنْكَ إِنِّي أَخَافُ اللَّهَ رَبَّ الْعَالَمِينَ', 59, 16, 28, 110, 547, 'کار آنها همچون شیطان است که به انسان گفت: «کافر شو (تا مشکلات تو را حل کنم)!» امّا هنگامی که کافر شد گفت: «من از تو بیزارم، من از خداوندی که پروردگار عالمیان است بیم دارم!»'),
(5143, 5143, 'فَكَانَ عَاقِبَتَهُمَا أَنَّهُمَا فِي النَّارِ خَالِدَيْنِ فِيهَا ۚ وَذَٰلِكَ جَزَاءُ الظَّالِمِينَ', 59, 17, 28, 110, 548, 'سرانجام کارشان این شد که هر دو در آتش دوزخ خواهند بود، جاودانه در آن می‌مانند؛ و این است کیفر ستمکاران!'),
(5144, 5144, 'يَا أَيُّهَا الَّذِينَ آمَنُوا اتَّقُوا اللَّهَ وَلْتَنْظُرْ نَفْسٌ مَا قَدَّمَتْ لِغَدٍ ۖ وَاتَّقُوا اللَّهَ ۚ إِنَّ اللَّهَ خَبِيرٌ بِمَا تَعْمَلُونَ', 59, 18, 28, 110, 548, 'ای کسانی که ایمان آورده‌اید از (مخالفت) خدا بپرهیزید؛ و هر کس باید بنگرد تا برای فردایش چه چیز از پیش فرستاده؛ و از خدا بپرهیزید که خداوند از آنچه انجام می‌دهید آگاه است!'),
(5145, 5145, 'وَلَا تَكُونُوا كَالَّذِينَ نَسُوا اللَّهَ فَأَنْسَاهُمْ أَنْفُسَهُمْ ۚ أُولَٰئِكَ هُمُ الْفَاسِقُونَ', 59, 19, 28, 110, 548, 'و همچون کسانی نباشید که خدا را فراموش کردند و خدا نیز آنها را به «خود فراموشی» گرفتار کرد، آنها فاسقانند.'),
(5146, 5146, 'لَا يَسْتَوِي أَصْحَابُ النَّارِ وَأَصْحَابُ الْجَنَّةِ ۚ أَصْحَابُ الْجَنَّةِ هُمُ الْفَائِزُونَ', 59, 20, 28, 110, 548, 'هرگز دوزخیان و بهشتیان یکسان نیستند؛ اصحاب بهشت رستگار و پیروزند!'),
(5147, 5147, 'لَوْ أَنْزَلْنَا هَٰذَا الْقُرْآنَ عَلَىٰ جَبَلٍ لَرَأَيْتَهُ خَاشِعًا مُتَصَدِّعًا مِنْ خَشْيَةِ اللَّهِ ۚ وَتِلْكَ الْأَمْثَالُ نَضْرِبُهَا لِلنَّاسِ لَعَلَّهُمْ يَتَفَكَّرُونَ', 59, 21, 28, 110, 548, 'اگر این قرآن را بر کوهی نازل می‌کردیم، می‌دیدی که در برابر آن خاشع می‌شود و از خوف خدا می‌شکافد! اینها مثالهایی است که برای مردم می‌زنیم، شاید در آن بیندیشید!'),
(5148, 5148, 'هُوَ اللَّهُ الَّذِي لَا إِلَٰهَ إِلَّا هُوَ ۖ عَالِمُ الْغَيْبِ وَالشَّهَادَةِ ۖ هُوَ الرَّحْمَٰنُ الرَّحِيمُ', 59, 22, 28, 110, 548, 'او خدایی است که معبودی جز او نیست، دانای آشکار و نهان است، و او رحمان و رحیم است!'),
(5149, 5149, 'هُوَ اللَّهُ الَّذِي لَا إِلَٰهَ إِلَّا هُوَ الْمَلِكُ الْقُدُّوسُ السَّلَامُ الْمُؤْمِنُ الْمُهَيْمِنُ الْعَزِيزُ الْجَبَّارُ الْمُتَكَبِّرُ ۚ سُبْحَانَ اللَّهِ عَمَّا يُشْرِكُونَ', 59, 23, 28, 110, 548, 'و خدایی است که معبودی جز او نیست، حاکم و مالک اصلی اوست، از هر عیب منزّه است، به کسی یتم نمی‌کند، امنیّت بخش است، مراقب همه چیز است، قدرتمندی شکست‌ناپذیر که با اراده نافذ خود هر امری را اصلاح می‌کند، و شایسته عظمت است؛ خداوند منزّه است از آنچه شریک برای او قرارمی‌دهند!'),
(5150, 5150, 'هُوَ اللَّهُ الْخَالِقُ الْبَارِئُ الْمُصَوِّرُ ۖ لَهُ الْأَسْمَاءُ الْحُسْنَىٰ ۚ يُسَبِّحُ لَهُ مَا فِي السَّمَاوَاتِ وَالْأَرْضِ ۖ وَهُوَ الْعَزِيزُ الْحَكِيمُ', 59, 24, 28, 110, 548, 'او خداوندی است خالق، آفریننده‌ای بی‌سابقه، و صورتگری (بی‌نظیر)؛ برای او نامهای نیک است؛ آنچه در آسمانها و زمین است تسبیح او می‌گویند؛ و او عزیز و حکیم است!'),
(5151, 5151, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ يَا أَيُّهَا الَّذِينَ آمَنُوا لَا تَتَّخِذُوا عَدُوِّي وَعَدُوَّكُمْ أَوْلِيَاءَ تُلْقُونَ إِلَيْهِمْ بِالْمَوَدَّةِ وَقَدْ كَفَرُوا بِمَا جَاءَكُمْ مِنَ الْحَقِّ يُخْرِجُونَ الرَّسُولَ وَإِيَّاكُمْ ۙ أَنْ تُؤْمِنُوا بِاللَّهِ رَبِّكُمْ إِنْ كُنْتُمْ خَرَجْتُمْ جِهَادًا فِي سَبِيلِي وَابْتِغَاءَ مَرْضَاتِي ۚ تُسِرُّونَ إِلَيْهِمْ بِالْمَوَدَّةِ وَأَنَا أَعْلَمُ بِمَا أَخْفَيْتُمْ وَمَا أَعْلَنْتُمْ ۚ وَمَنْ يَفْعَلْهُ مِنْكُمْ فَقَدْ ضَلَّ سَوَاءَ السَّبِيلِ', 60, 1, 28, 110, 549, 'ای کسانی که ایمان آورده‌اید! دشمن من و دشمن خودتان را دوست نگیرید! شما نسبت به آنان اظهار محبّت می‌کنید، در حالی که آنها به آنچه از حقّ برای شما آمده کافر شده‌اند و رسول اللّه و شما را به خاطر ایمان به خداوندی که پروردگار همه شماست از شهر و دیارتان بیرون می‌رانند؛ اگر شما برای جهاد در راه من و جلب خشنودیم هجرت کرده‌اید؛ (پیوند دوستی با آنان برقرار نسازید!) شما مخفیانه با آنها رابطه دوستی برقرار می‌کنید در حالی که من به آنچه پنهان یا آشکار می‌سازید از همه داناترم! و هر کس از شما چنین کاری کند، از راه راست گمراه شده است!'),
(5152, 5152, 'إِنْ يَثْقَفُوكُمْ يَكُونُوا لَكُمْ أَعْدَاءً وَيَبْسُطُوا إِلَيْكُمْ أَيْدِيَهُمْ وَأَلْسِنَتَهُمْ بِالسُّوءِ وَوَدُّوا لَوْ تَكْفُرُونَ', 60, 2, 28, 110, 549, 'اگر آنها بر شما مسلّط شوند، دشمنانتان خواهند بود و دست و زبان خود را به بدی کردن نسبت به شما می‌گشایند، و دوست دارند شما به کفر بازگردید!'),
(5153, 5153, 'لَنْ تَنْفَعَكُمْ أَرْحَامُكُمْ وَلَا أَوْلَادُكُمْ ۚ يَوْمَ الْقِيَامَةِ يَفْصِلُ بَيْنَكُمْ ۚ وَاللَّهُ بِمَا تَعْمَلُونَ بَصِيرٌ', 60, 3, 28, 110, 549, 'هرگز بستگان و فرزندانتان روز قیامت سودی به حالتان نخواهند داشت؛ میان شما جدایی می‌افکند؛ و خداوند به آنچه انجام می‌دهید بیناست.');
INSERT INTO `Quran` (`rowid`, `id`, `Arabic`, `Chapter`, `Verse`, `Juz`, `Hizb`, `Page`, `tr`) VALUES
(5154, 5154, 'قَدْ كَانَتْ لَكُمْ أُسْوَةٌ حَسَنَةٌ فِي إِبْرَاهِيمَ وَالَّذِينَ مَعَهُ إِذْ قَالُوا لِقَوْمِهِمْ إِنَّا بُرَآءُ مِنْكُمْ وَمِمَّا تَعْبُدُونَ مِنْ دُونِ اللَّهِ كَفَرْنَا بِكُمْ وَبَدَا بَيْنَنَا وَبَيْنَكُمُ الْعَدَاوَةُ وَالْبَغْضَاءُ أَبَدًا حَتَّىٰ تُؤْمِنُوا بِاللَّهِ وَحْدَهُ إِلَّا قَوْلَ إِبْرَاهِيمَ لِأَبِيهِ لَأَسْتَغْفِرَنَّ لَكَ وَمَا أَمْلِكُ لَكَ مِنَ اللَّهِ مِنْ شَيْءٍ ۖ رَبَّنَا عَلَيْكَ تَوَكَّلْنَا وَإِلَيْكَ أَنَبْنَا وَإِلَيْكَ الْمَصِيرُ', 60, 4, 28, 110, 549, 'برای شما سرمشق خوبی در زندگی ابراهیم و کسانی که با او بودند وجود داشت، در آن هنگامی که به قوم (مشرک) خود گفتند: «ما از شما و آنچه غیر از خدا می‌پرستید بیزاریم؛ ما نسبت به شما کافریم؛ و میان ما و شما عداوت و دشمنی همیشگی آشکار شده است؛ تا آن زمان که به خدای یگانه ایمان بیاورید! -جز آن سخن ابراهیم که به پدرش = عمویش آزر گفت (و وعده داد) که برای تو آمرزش طلب می‌کنم، و در عین حال در برابر خداوند برای تو مالک چیزی نیستم (و اختیاری ندارم)!- پروردگارا! ما بر تو توکّل کردیم و به سوی تو بازگشتیم، و همه فرجامها بسوی تو است!'),
(5155, 5155, 'رَبَّنَا لَا تَجْعَلْنَا فِتْنَةً لِلَّذِينَ كَفَرُوا وَاغْفِرْ لَنَا رَبَّنَا ۖ إِنَّكَ أَنْتَ الْعَزِيزُ الْحَكِيمُ', 60, 5, 28, 110, 549, 'پروردگارا! ما را مایه گمراهی کافران قرار مده، و ما را ببخش، ای پروردگار ما که تو عزیز و حکیمی!»'),
(5156, 5156, 'لَقَدْ كَانَ لَكُمْ فِيهِمْ أُسْوَةٌ حَسَنَةٌ لِمَنْ كَانَ يَرْجُو اللَّهَ وَالْيَوْمَ الْآخِرَ ۚ وَمَنْ يَتَوَلَّ فَإِنَّ اللَّهَ هُوَ الْغَنِيُّ الْحَمِيدُ', 60, 6, 28, 110, 550, '(آری) برای شما در زندگی آنها اسوه حسنه (و سرمشق نیکویی) بود، برای کسانی که امید به خدا و روز قیامت دارند؛ و هر کس سرپیچی کند به خویشتن ضرر زده است، زیرا خداوند بی‌نیاز و شایسته ستایش است!'),
(5157, 5157, ' عَسَى اللَّهُ أَنْ يَجْعَلَ بَيْنَكُمْ وَبَيْنَ الَّذِينَ عَادَيْتُمْ مِنْهُمْ مَوَدَّةً ۚ وَاللَّهُ قَدِيرٌ ۚ وَاللَّهُ غَفُورٌ رَحِيمٌ', 60, 7, 28, 110, 550, 'امید است خدا میان شما و کسانی از مشرکین که با شما دشمنی کردند (از راه اسلام) پیوند محبّت برقرار کند؛ خداوند تواناست و خداوند آمرزنده و مهربان است.'),
(5158, 5158, 'لَا يَنْهَاكُمُ اللَّهُ عَنِ الَّذِينَ لَمْ يُقَاتِلُوكُمْ فِي الدِّينِ وَلَمْ يُخْرِجُوكُمْ مِنْ دِيَارِكُمْ أَنْ تَبَرُّوهُمْ وَتُقْسِطُوا إِلَيْهِمْ ۚ إِنَّ اللَّهَ يُحِبُّ الْمُقْسِطِينَ', 60, 8, 28, 110, 550, 'خدا شما را از نیکی کردن و رعایت عدالت نسبت به کسانی که در راه دین با شما پیکار نکردند و از خانه و دیارتان بیرون نراندند نهی نمی‌کند؛ چرا که خداوند عدالت‌پیشگان را دوست دارد.'),
(5159, 5159, 'إِنَّمَا يَنْهَاكُمُ اللَّهُ عَنِ الَّذِينَ قَاتَلُوكُمْ فِي الدِّينِ وَأَخْرَجُوكُمْ مِنْ دِيَارِكُمْ وَظَاهَرُوا عَلَىٰ إِخْرَاجِكُمْ أَنْ تَوَلَّوْهُمْ ۚ وَمَنْ يَتَوَلَّهُمْ فَأُولَٰئِكَ هُمُ الظَّالِمُونَ', 60, 9, 28, 110, 550, 'تنها شما را از دوستی و رابطه با کسانی نهی می‌کند که در امر دین با شما پیکار کردند و شما را از خانه هایتان بیرون راندند یا به بیرون‌راندن شما کمک کردند و هر کس با آنان رابطه دوستی داشته باشد ظالم و ستمگر است!'),
(5160, 5160, 'يَا أَيُّهَا الَّذِينَ آمَنُوا إِذَا جَاءَكُمُ الْمُؤْمِنَاتُ مُهَاجِرَاتٍ فَامْتَحِنُوهُنَّ ۖ اللَّهُ أَعْلَمُ بِإِيمَانِهِنَّ ۖ فَإِنْ عَلِمْتُمُوهُنَّ مُؤْمِنَاتٍ فَلَا تَرْجِعُوهُنَّ إِلَى الْكُفَّارِ ۖ لَا هُنَّ حِلٌّ لَهُمْ وَلَا هُمْ يَحِلُّونَ لَهُنَّ ۖ وَآتُوهُمْ مَا أَنْفَقُوا ۚ وَلَا جُنَاحَ عَلَيْكُمْ أَنْ تَنْكِحُوهُنَّ إِذَا آتَيْتُمُوهُنَّ أُجُورَهُنَّ ۚ وَلَا تُمْسِكُوا بِعِصَمِ الْكَوَافِرِ وَاسْأَلُوا مَا أَنْفَقْتُمْ وَلْيَسْأَلُوا مَا أَنْفَقُوا ۚ ذَٰلِكُمْ حُكْمُ اللَّهِ ۖ يَحْكُمُ بَيْنَكُمْ ۚ وَاللَّهُ عَلِيمٌ حَكِيمٌ', 60, 10, 28, 110, 550, 'ای کسانی که ایمان آورده‌اید! هنگامی که زنان باایمان بعنوان هجرت نزد شما آیند، آنها را آزمایش کنید -خداوند به ایمانشان آگاهتر است- هرگاه آنان را مؤمن یافتید، آنها را بسوی کفّار بازنگردانید؛ نه آنها برای کفّار حلالند و نه کفّار برای آنها حلال؛ و آنچه را همسران آنها (برای ازدواج با این زنان) پرداخته‌اند به آنان بپردازید؛ و گناهی بر شما نیست که با آنها ازدواج کنید هرگاه مَهرشان را به آنان بدهید و هرگز زنان کافر را در همسری خود نگه ندارید (و اگر کسی از زنان شما کافر شد و به بلاد کفر فرار کرد،) حق دارید مَهری را که پرداخته‌اید مطالبه کنید همان‌گونه که آنها حق دارند مهر زنانشان را که از آنان جدا شده‌اند) از شما مطالبه کنند؛ این حکم خداوند است که در میان شما حکم می‌کند، و خداوند دانا و حکیم است!'),
(5161, 5161, 'وَإِنْ فَاتَكُمْ شَيْءٌ مِنْ أَزْوَاجِكُمْ إِلَى الْكُفَّارِ فَعَاقَبْتُمْ فَآتُوا الَّذِينَ ذَهَبَتْ أَزْوَاجُهُمْ مِثْلَ مَا أَنْفَقُوا ۚ وَاتَّقُوا اللَّهَ الَّذِي أَنْتُمْ بِهِ مُؤْمِنُونَ', 60, 11, 28, 110, 550, 'و اگر بعضی از همسران شما از دستتان بروند (و به سوی کفار بازگردند) و شما در جنگی بر آنان پیروز شدید و غنایمی گرفتید، به کسانی که همسرانشان رفته‌اند، همانند مهری را که پرداخته‌اند بدهید؛ و از (مخالفت) خداوندی که همه به او ایمان دارید بپرهیزید!'),
(5162, 5162, 'يَا أَيُّهَا النَّبِيُّ إِذَا جَاءَكَ الْمُؤْمِنَاتُ يُبَايِعْنَكَ عَلَىٰ أَنْ لَا يُشْرِكْنَ بِاللَّهِ شَيْئًا وَلَا يَسْرِقْنَ وَلَا يَزْنِينَ وَلَا يَقْتُلْنَ أَوْلَادَهُنَّ وَلَا يَأْتِينَ بِبُهْتَانٍ يَفْتَرِينَهُ بَيْنَ أَيْدِيهِنَّ وَأَرْجُلِهِنَّ وَلَا يَعْصِينَكَ فِي مَعْرُوفٍ ۙ فَبَايِعْهُنَّ وَاسْتَغْفِرْ لَهُنَّ اللَّهَ ۖ إِنَّ اللَّهَ غَفُورٌ رَحِيمٌ', 60, 12, 28, 110, 551, 'ای پیامبر! هنگامی که زنان مؤمن نزد تو آیند و با تو بیعت کنند که چیزی را شریک خدا قرار ندهند، دزدی و زنا نکنند، فرزندان خود را نکشند، تهمت و افترایی پیش دست و پای خود نیاورند و در هیچ کار شایسته‌ای مخالفت فرمان تو نکنند، با آنها بیعت کن و برای آنان از درگاه خداوند آمرزش بطلب که خداوند آمرزنده و مهربان است!'),
(5163, 5163, 'يَا أَيُّهَا الَّذِينَ آمَنُوا لَا تَتَوَلَّوْا قَوْمًا غَضِبَ اللَّهُ عَلَيْهِمْ قَدْ يَئِسُوا مِنَ الْآخِرَةِ كَمَا يَئِسَ الْكُفَّارُ مِنْ أَصْحَابِ الْقُبُورِ', 60, 13, 28, 110, 551, 'ای کسانی که ایمان آورده‌اید! با قومی که خداوند آنان را مورد غضب قرار داده دوستی نکنید؛ آنان از آخرت مأیوسند همان‌گونه که کفار مدفون در قبرها مأیوس می‌باشند!'),
(5164, 5164, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ سَبَّحَ لِلَّهِ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ ۖ وَهُوَ الْعَزِيزُ الْحَكِيمُ', 61, 1, 28, 110, 551, 'آنچه در آسمانها و آنچه در زمین است همه تسبیح خدا می‌گویند؛ و او شکست‌ناپذیر و حکیم است!'),
(5165, 5165, 'يَا أَيُّهَا الَّذِينَ آمَنُوا لِمَ تَقُولُونَ مَا لَا تَفْعَلُونَ', 61, 2, 28, 110, 551, 'ای کسانی که ایمان آورده‌اید! چرا سخنی می‌گویید که عمل نمی‌کنید؟!'),
(5166, 5166, 'كَبُرَ مَقْتًا عِنْدَ اللَّهِ أَنْ تَقُولُوا مَا لَا تَفْعَلُونَ', 61, 3, 28, 110, 551, 'نزد خدا بسیار موجب خشم است که سخنی بگویید که عمل نمی‌کنید!'),
(5167, 5167, 'إِنَّ اللَّهَ يُحِبُّ الَّذِينَ يُقَاتِلُونَ فِي سَبِيلِهِ صَفًّا كَأَنَّهُمْ بُنْيَانٌ مَرْصُوصٌ', 61, 4, 28, 110, 551, 'خداوند کسانی را دوست می‌دارد که در راه او پیکار می‌کنند گوئی بنایی آهنین‌اند!'),
(5168, 5168, 'وَإِذْ قَالَ مُوسَىٰ لِقَوْمِهِ يَا قَوْمِ لِمَ تُؤْذُونَنِي وَقَدْ تَعْلَمُونَ أَنِّي رَسُولُ اللَّهِ إِلَيْكُمْ ۖ فَلَمَّا زَاغُوا أَزَاغَ اللَّهُ قُلُوبَهُمْ ۚ وَاللَّهُ لَا يَهْدِي الْقَوْمَ الْفَاسِقِينَ', 61, 5, 28, 110, 551, '(به یاد آورید) هنگامی را که موسی به قومش گفت: «ای قوم من! چرا مرا آزار می‌دهید با اینکه می‌دانید من فرستاده خدا به سوی شما هستم؟!» هنگامی که آنها از حق منحرف شدند، خداوند قلوبشان را منحرف ساخت؛ و خدا فاسقان را هدایت نمی‌کند!'),
(5169, 5169, 'وَإِذْ قَالَ عِيسَى ابْنُ مَرْيَمَ يَا بَنِي إِسْرَائِيلَ إِنِّي رَسُولُ اللَّهِ إِلَيْكُمْ مُصَدِّقًا لِمَا بَيْنَ يَدَيَّ مِنَ التَّوْرَاةِ وَمُبَشِّرًا بِرَسُولٍ يَأْتِي مِنْ بَعْدِي اسْمُهُ أَحْمَدُ ۖ فَلَمَّا جَاءَهُمْ بِالْبَيِّنَاتِ قَالُوا هَٰذَا سِحْرٌ مُبِينٌ', 61, 6, 28, 110, 552, 'و (به یاد آورید) هنگامی را که عیسی بن مریم گفت: «ای بنی اسرائیل! من فرستاده خدا به سوی شما هستم در حالی که تصدیق‌کننده کتابی که قبل از من فرستاده شده = تورات‌ می‌باشم، و بشارت‌دهنده به رسولی که بعد از من می‌آید و نام او احمد است!» هنگامی که او = احمد با معجزات و دلایل روشن به سراغ آنان آمد، گفتند: «این سحری است آشکار»!'),
(5170, 5170, 'وَمَنْ أَظْلَمُ مِمَّنِ افْتَرَىٰ عَلَى اللَّهِ الْكَذِبَ وَهُوَ يُدْعَىٰ إِلَى الْإِسْلَامِ ۚ وَاللَّهُ لَا يَهْدِي الْقَوْمَ الظَّالِمِينَ', 61, 7, 28, 110, 552, 'چه کسی ظالمتر است از آن کس که بر خدا دروغ بسته در حالی که دعوت به اسلام می‌شود؟! خداوند گروه ستمکاران را هدایت نمی‌کند!'),
(5171, 5171, 'يُرِيدُونَ لِيُطْفِئُوا نُورَ اللَّهِ بِأَفْوَاهِهِمْ وَاللَّهُ مُتِمُّ نُورِهِ وَلَوْ كَرِهَ الْكَافِرُونَ', 61, 8, 28, 110, 552, 'آنان می‌خواهند نور خدا را با دهان خود خاموش سازند؛ ولی خدا نور خود را کامل می‌کند هر چند کافران خوش نداشته باشند!'),
(5172, 5172, 'هُوَ الَّذِي أَرْسَلَ رَسُولَهُ بِالْهُدَىٰ وَدِينِ الْحَقِّ لِيُظْهِرَهُ عَلَى الدِّينِ كُلِّهِ وَلَوْ كَرِهَ الْمُشْرِكُونَ', 61, 9, 28, 110, 552, 'او کسی است که رسول خود را با هدایت و دین حق فرستاد تا او را بر همه ادیان غالب سازد، هر چند مشرکان کراهت داشته باشند!'),
(5173, 5173, 'يَا أَيُّهَا الَّذِينَ آمَنُوا هَلْ أَدُلُّكُمْ عَلَىٰ تِجَارَةٍ تُنْجِيكُمْ مِنْ عَذَابٍ أَلِيمٍ', 61, 10, 28, 110, 552, 'ای کسانی که ایمان آورده‌اید! آیا شما را به تجارتی راهنمائی کنم که شما را از عذاب دردناک رهایی می‌بخشد؟!'),
(5174, 5174, 'تُؤْمِنُونَ بِاللَّهِ وَرَسُولِهِ وَتُجَاهِدُونَ فِي سَبِيلِ اللَّهِ بِأَمْوَالِكُمْ وَأَنْفُسِكُمْ ۚ ذَٰلِكُمْ خَيْرٌ لَكُمْ إِنْ كُنْتُمْ تَعْلَمُونَ', 61, 11, 28, 110, 552, 'به خدا و رسولش ایمان بیاورید و با اموال و جانهایتان در راه خدا جهاد کنید؛ این برای شما (از هر چیز) بهتر است اگر بدانید!'),
(5175, 5175, 'يَغْفِرْ لَكُمْ ذُنُوبَكُمْ وَيُدْخِلْكُمْ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهَارُ وَمَسَاكِنَ طَيِّبَةً فِي جَنَّاتِ عَدْنٍ ۚ ذَٰلِكَ الْفَوْزُ الْعَظِيمُ', 61, 12, 28, 110, 552, '(اگر چنین کنید) گناهانتان را می‌بخشد و شما را در باغهایی از بهشت داخل می‌کند که نهرها از زیر درختانش جاری است و در مسکنهای پاکیزه در بهشت جاویدان جای می‌دهد؛ و این پیروزی عظیم است!'),
(5176, 5176, 'وَأُخْرَىٰ تُحِبُّونَهَا ۖ نَصْرٌ مِنَ اللَّهِ وَفَتْحٌ قَرِيبٌ ۗ وَبَشِّرِ الْمُؤْمِنِينَ', 61, 13, 28, 110, 552, 'و (نعمت) دیگری که آن را دوست دارید به شما می‌بخشد، و آن یاری خداوند و پیروزی نزدیک است؛ و مؤمنان را بشارت ده (به این پیروزی بزرگ)!'),
(5177, 5177, 'يَا أَيُّهَا الَّذِينَ آمَنُوا كُونُوا أَنْصَارَ اللَّهِ كَمَا قَالَ عِيسَى ابْنُ مَرْيَمَ لِلْحَوَارِيِّينَ مَنْ أَنْصَارِي إِلَى اللَّهِ ۖ قَالَ الْحَوَارِيُّونَ نَحْنُ أَنْصَارُ اللَّهِ ۖ فَآمَنَتْ طَائِفَةٌ مِنْ بَنِي إِسْرَائِيلَ وَكَفَرَتْ طَائِفَةٌ ۖ فَأَيَّدْنَا الَّذِينَ آمَنُوا عَلَىٰ عَدُوِّهِمْ فَأَصْبَحُوا ظَاهِرِينَ', 61, 14, 28, 110, 552, 'ای کسانی که ایمان آورده‌اید! یاوران خدا باشید همان‌گونه که عیسی بن مریم به حواریون گفت: «چه کسانی در راه خدا یاوران من هستند؟!» حواریون گفتند: «ما یاوران خدائیم» در این هنگام گروهی از بنی اسرائیل ایمان آوردند و گروهی کافر شدند؛ ما کسانی را که ایمان آورده بودند در برابر دشمنانشان تأیید کردیم و سرانجام بر آنان پیروز شدند!'),
(5178, 5178, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ يُسَبِّحُ لِلَّهِ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ الْمَلِكِ الْقُدُّوسِ الْعَزِيزِ الْحَكِيمِ', 62, 1, 28, 111, 553, 'آنچه در آسمانها و آنچه در زمین است همواره تسبیح خدا می‌گویند، خداوندی که مالک و حاکم است و از هر عیب و نقصی مبرا، و عزیز و حکیم است!'),
(5179, 5179, 'هُوَ الَّذِي بَعَثَ فِي الْأُمِّيِّينَ رَسُولًا مِنْهُمْ يَتْلُو عَلَيْهِمْ آيَاتِهِ وَيُزَكِّيهِمْ وَيُعَلِّمُهُمُ الْكِتَابَ وَالْحِكْمَةَ وَإِنْ كَانُوا مِنْ قَبْلُ لَفِي ضَلَالٍ مُبِينٍ', 62, 2, 28, 111, 553, 'و کسی است که در میان جمعیت درس نخوانده رسولی از خودشان برانگیخت که آیاتش را بر آنها می‌خواند و آنها را تزکیه می‌کند و به آنان کتاب (قرآن) و حکمت می‌آموزد هر چند پیش از آن در گمراهی آشکاری بودند!'),
(5180, 5180, 'وَآخَرِينَ مِنْهُمْ لَمَّا يَلْحَقُوا بِهِمْ ۚ وَهُوَ الْعَزِيزُ الْحَكِيمُ', 62, 3, 28, 111, 553, 'و (همچنین) رسول است بر گروه دیگری که هنوز به آنها ملحق نشده‌اند؛ و او عزیز و حکیم است!'),
(5181, 5181, 'ذَٰلِكَ فَضْلُ اللَّهِ يُؤْتِيهِ مَنْ يَشَاءُ ۚ وَاللَّهُ ذُو الْفَضْلِ الْعَظِيمِ', 62, 4, 28, 111, 553, 'این فضل خداست که به هر کس بخواهد (و شایسته بداند) می‌بخشد؛ و خداوند صاحب فضل عظیم است!'),
(5182, 5182, 'مَثَلُ الَّذِينَ حُمِّلُوا التَّوْرَاةَ ثُمَّ لَمْ يَحْمِلُوهَا كَمَثَلِ الْحِمَارِ يَحْمِلُ أَسْفَارًا ۚ بِئْسَ مَثَلُ الْقَوْمِ الَّذِينَ كَذَّبُوا بِآيَاتِ اللَّهِ ۚ وَاللَّهُ لَا يَهْدِي الْقَوْمَ الظَّالِمِينَ', 62, 5, 28, 111, 553, 'کسانی که مکلف به تورات شدند ولی حق آن را ادا نکردند، مانند درازگوشی هستند که کتابهایی حمل می‌کند، (آن را بر دوش می‌کشد اما چیزی از آن نمی‌فهمد)! گروهی که آیات خدا را انکار کردند مثال بدی دارند، و خداوند قوم ستمگر را هدایت نمی‌کند!'),
(5183, 5183, 'قُلْ يَا أَيُّهَا الَّذِينَ هَادُوا إِنْ زَعَمْتُمْ أَنَّكُمْ أَوْلِيَاءُ لِلَّهِ مِنْ دُونِ النَّاسِ فَتَمَنَّوُا الْمَوْتَ إِنْ كُنْتُمْ صَادِقِينَ', 62, 6, 28, 111, 553, 'بگو: «ای یهودیان! اگر گمان می‌کنید که (فقط) شما دوستان خدائید نه سایر مردم، پس آرزوی مرگ کنید اگر راست می‌گویید (تا به لقای محبوبتان برسید)!»'),
(5184, 5184, 'وَلَا يَتَمَنَّوْنَهُ أَبَدًا بِمَا قَدَّمَتْ أَيْدِيهِمْ ۚ وَاللَّهُ عَلِيمٌ بِالظَّالِمِينَ', 62, 7, 28, 111, 553, 'ولی آنان هرگز تمنای مرگ نمی‌کنند بخاطر اعمالی که از پیش فرستاده‌اند؛ و خداوند ظالمان را بخوبی می‌شناسد!'),
(5185, 5185, 'قُلْ إِنَّ الْمَوْتَ الَّذِي تَفِرُّونَ مِنْهُ فَإِنَّهُ مُلَاقِيكُمْ ۖ ثُمَّ تُرَدُّونَ إِلَىٰ عَالِمِ الْغَيْبِ وَالشَّهَادَةِ فَيُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُونَ', 62, 8, 28, 111, 553, 'بگو: «این مرگی که از آن فرار می‌کنید سرانجام با شما ملاقات خواهد کرد؛ سپس به سوی کسی که دانای پنهان و آشکار است بازگردانده می‌شوید؛ آنگاه شما را از آنچه انجام می‌دادید خبر می‌دهد!»'),
(5186, 5186, 'يَا أَيُّهَا الَّذِينَ آمَنُوا إِذَا نُودِيَ لِلصَّلَاةِ مِنْ يَوْمِ الْجُمُعَةِ فَاسْعَوْا إِلَىٰ ذِكْرِ اللَّهِ وَذَرُوا الْبَيْعَ ۚ ذَٰلِكُمْ خَيْرٌ لَكُمْ إِنْ كُنْتُمْ تَعْلَمُونَ', 62, 9, 28, 111, 554, 'ای کسانی که ایمان آورده‌اید! هنگامی که برای نماز روز جمعه اذان گفته شود، به سوی ذکر خدا بشتابید و خرید و فروش را رها کنید که این برای شما بهتر است اگر می‌دانستید!'),
(5187, 5187, 'فَإِذَا قُضِيَتِ الصَّلَاةُ فَانْتَشِرُوا فِي الْأَرْضِ وَابْتَغُوا مِنْ فَضْلِ اللَّهِ وَاذْكُرُوا اللَّهَ كَثِيرًا لَعَلَّكُمْ تُفْلِحُونَ', 62, 10, 28, 111, 554, 'و هنگامی که نماز پایان گرفت (شما آزادید) در زمین پراکنده شوید و از فضل خدا بطلبید، و خدا را بسیار یاد کنید شاید رستگار شوید!'),
(5188, 5188, 'وَإِذَا رَأَوْا تِجَارَةً أَوْ لَهْوًا انْفَضُّوا إِلَيْهَا وَتَرَكُوكَ قَائِمًا ۚ قُلْ مَا عِنْدَ اللَّهِ خَيْرٌ مِنَ اللَّهْوِ وَمِنَ التِّجَارَةِ ۚ وَاللَّهُ خَيْرُ الرَّازِقِينَ', 62, 11, 28, 111, 554, 'هنگامی که آنها تجارت یا سرگرمی و لهوی را ببینند پراکنده می‌شوند و به سوی آن می روند و تو را ایستاده به حال خود رها می‌کنند؛ بگو: آنچه نزد خداست بهتر از لهو و تجارت است، و خداوند بهترین روزی‌دهندگان است.'),
(5189, 5189, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ إِذَا جَاءَكَ الْمُنَافِقُونَ قَالُوا نَشْهَدُ إِنَّكَ لَرَسُولُ اللَّهِ ۗ وَاللَّهُ يَعْلَمُ إِنَّكَ لَرَسُولُهُ وَاللَّهُ يَشْهَدُ إِنَّ الْمُنَافِقِينَ لَكَاذِبُونَ', 63, 1, 28, 111, 554, 'هنگامی که منافقان نزد تو آیند می‌گویند: «ما شهادت می‌دهیم که یقیناً تو رسول خدایی!» خداوند می‌داند که تو رسول او هستی، ولی خداوند شهادت می‌دهد که منافقان دروغگو هستند (و به گفته خود ایمان ندارند).'),
(5190, 5190, 'اتَّخَذُوا أَيْمَانَهُمْ جُنَّةً فَصَدُّوا عَنْ سَبِيلِ اللَّهِ ۚ إِنَّهُمْ سَاءَ مَا كَانُوا يَعْمَلُونَ', 63, 2, 28, 111, 554, 'آنها سوگندهایشان را سپر ساخته‌اند تا مردم را از راه خدا باز دارند، و کارهای بسیار بدی انجام می‌دهند!'),
(5191, 5191, 'ذَٰلِكَ بِأَنَّهُمْ آمَنُوا ثُمَّ كَفَرُوا فَطُبِعَ عَلَىٰ قُلُوبِهِمْ فَهُمْ لَا يَفْقَهُونَ', 63, 3, 28, 111, 554, 'این بخاطر آن است که نخست ایمان آوردند سپس کافر شدند؛ از این رو بر دلهای آنان مهر نهاده شده، و حقیقت را درک نمی‌کنند!'),
(5192, 5192, ' وَإِذَا رَأَيْتَهُمْ تُعْجِبُكَ أَجْسَامُهُمْ ۖ وَإِنْ يَقُولُوا تَسْمَعْ لِقَوْلِهِمْ ۖ كَأَنَّهُمْ خُشُبٌ مُسَنَّدَةٌ ۖ يَحْسَبُونَ كُلَّ صَيْحَةٍ عَلَيْهِمْ ۚ هُمُ الْعَدُوُّ فَاحْذَرْهُمْ ۚ قَاتَلَهُمُ اللَّهُ ۖ أَنَّىٰ يُؤْفَكُونَ', 63, 4, 28, 111, 554, 'هنگامی که آنها را می‌بینی، جسم و قیافه آنان تو را در شگفتی فرو می‌برد؛ و اگر سخن بگویند، به سخنانشان گوش فرا می‌دهی؛ اما گویی چوبهای خشکی هستند که به دیوار تکیه داده شده‌اند! هر فریادی از هر جا بلند شود بر ضد خود می‌پندارند؛ آنها دشمنان واقعی تو هستند، پس از آنان بر حذر باش! خداوند آنها را بکشد، چگونه از حق منحرف می‌شوند؟!'),
(5193, 5193, 'وَإِذَا قِيلَ لَهُمْ تَعَالَوْا يَسْتَغْفِرْ لَكُمْ رَسُولُ اللَّهِ لَوَّوْا رُءُوسَهُمْ وَرَأَيْتَهُمْ يَصُدُّونَ وَهُمْ مُسْتَكْبِرُونَ', 63, 5, 28, 111, 555, 'هنگامی که به آنان گفته شود: «بیایید تا رسول خدا برای شما استغفار کند!»، سرهای خود را (از روی استهزا و کبر و غرور) تکان می‌دهند؛ و آنها را می‌بینی که از سخنان تو اعراض کرده و تکبر می‌ورزند!'),
(5194, 5194, 'سَوَاءٌ عَلَيْهِمْ أَسْتَغْفَرْتَ لَهُمْ أَمْ لَمْ تَسْتَغْفِرْ لَهُمْ لَنْ يَغْفِرَ اللَّهُ لَهُمْ ۚ إِنَّ اللَّهَ لَا يَهْدِي الْقَوْمَ الْفَاسِقِينَ', 63, 6, 28, 111, 555, 'برای آنها تفاوت نمی‌کند، خواه استغفار برایشان کنی یا نکنی، هرگز خداوند آنان را نمی‌بخشد؛ زیرا خداوند قوم فاسق را هدایت نمی‌کند!'),
(5195, 5195, 'هُمُ الَّذِينَ يَقُولُونَ لَا تُنْفِقُوا عَلَىٰ مَنْ عِنْدَ رَسُولِ اللَّهِ حَتَّىٰ يَنْفَضُّوا ۗ وَلِلَّهِ خَزَائِنُ السَّمَاوَاتِ وَالْأَرْضِ وَلَٰكِنَّ الْمُنَافِقِينَ لَا يَفْقَهُونَ', 63, 7, 28, 111, 555, 'آنها کسانی هستند که می‌گویند: «به افرادی که نزد رسول خدا هستند انفاق نکنید تا پراکنده شوند!» (غافل از اینکه) خزاین آسمانها و زمین از آن خداست، ولی منافقان نمی‌فهمند!'),
(5196, 5196, 'يَقُولُونَ لَئِنْ رَجَعْنَا إِلَى الْمَدِينَةِ لَيُخْرِجَنَّ الْأَعَزُّ مِنْهَا الْأَذَلَّ ۚ وَلِلَّهِ الْعِزَّةُ وَلِرَسُولِهِ وَلِلْمُؤْمِنِينَ وَلَٰكِنَّ الْمُنَافِقِينَ لَا يَعْلَمُونَ', 63, 8, 28, 111, 555, 'آنها می‌گویند: «اگر به مدینه بازگردیم، عزیزان ذلیلان را بیرون می‌کنند!» در حالی که عزت مخصوص خدا و رسول او و مؤمنان است؛ ولی منافقان نمی‌دانند!'),
(5197, 5197, 'يَا أَيُّهَا الَّذِينَ آمَنُوا لَا تُلْهِكُمْ أَمْوَالُكُمْ وَلَا أَوْلَادُكُمْ عَنْ ذِكْرِ اللَّهِ ۚ وَمَنْ يَفْعَلْ ذَٰلِكَ فَأُولَٰئِكَ هُمُ الْخَاسِرُونَ', 63, 9, 28, 111, 555, 'ای کسانی که ایمان آورده‌اید! اموال و فرزندانتان شما را از یاد خدا غافل نکند! و کسانی که چنین کنند، زیانکارانند!'),
(5198, 5198, 'وَأَنْفِقُوا مِنْ مَا رَزَقْنَاكُمْ مِنْ قَبْلِ أَنْ يَأْتِيَ أَحَدَكُمُ الْمَوْتُ فَيَقُولَ رَبِّ لَوْلَا أَخَّرْتَنِي إِلَىٰ أَجَلٍ قَرِيبٍ فَأَصَّدَّقَ وَأَكُنْ مِنَ الصَّالِحِينَ', 63, 10, 28, 111, 555, 'از آنچه به شما روزی داده‌ایم انفاق کنید، پیش از آنکه مرگ یکی از شما فرا رسد و بگوید: «پروردگارا! چرا (مرگ) مرا مدت کمی به تأخیر نینداختی تا (در راه خدا) صدقه دهم و از صالحان باشم؟!»'),
(5199, 5199, 'وَلَنْ يُؤَخِّرَ اللَّهُ نَفْسًا إِذَا جَاءَ أَجَلُهَا ۚ وَاللَّهُ خَبِيرٌ بِمَا تَعْمَلُونَ', 63, 11, 28, 111, 555, 'خداوند هرگز مرگ کسی را هنگامی که اجلش فرا رسد به تأخیر نمی‌اندازد، و خداوند به آنچه انجام می‌دهید آگاه است.'),
(5200, 5200, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ يُسَبِّحُ لِلَّهِ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ ۖ لَهُ الْمُلْكُ وَلَهُ الْحَمْدُ ۖ وَهُوَ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ', 64, 1, 28, 111, 556, 'آنچه در آسمانها و آنچه در زمین است برای خدا تسبیح می‌گویند؛ مالکیت و حکومت از آن اوست و ستایش از آن او؛ و او بر همه چیز تواناست!'),
(5201, 5201, 'هُوَ الَّذِي خَلَقَكُمْ فَمِنْكُمْ كَافِرٌ وَمِنْكُمْ مُؤْمِنٌ ۚ وَاللَّهُ بِمَا تَعْمَلُونَ بَصِيرٌ', 64, 2, 28, 111, 556, 'او کسی است که شما را آفرید (و به شما آزادی و اختیار داد)؛ گروهی از شما کافرید و گروهی مؤمن؛ و خداوند به آنچه انجام می‌دهید بیناست!'),
(5202, 5202, 'خَلَقَ السَّمَاوَاتِ وَالْأَرْضَ بِالْحَقِّ وَصَوَّرَكُمْ فَأَحْسَنَ صُوَرَكُمْ ۖ وَإِلَيْهِ الْمَصِيرُ', 64, 3, 28, 111, 556, 'آسمانها و زمین را بحق آفرید؛ و شما را (در عالم جنین) تصویر کرد، تصویری زیبا و دلپذیر؛ و سرانجام (همه) بسوی اوست.'),
(5203, 5203, 'يَعْلَمُ مَا فِي السَّمَاوَاتِ وَالْأَرْضِ وَيَعْلَمُ مَا تُسِرُّونَ وَمَا تُعْلِنُونَ ۚ وَاللَّهُ عَلِيمٌ بِذَاتِ الصُّدُورِ', 64, 4, 28, 111, 556, 'آنچه را در آسمانها و زمین است می‌داند، و از آنچه پنهان یا آشکار می‌کنید با خبر است؛ و خداوند از آنچه در درون سینه‌هاست آگاه است.'),
(5204, 5204, 'أَلَمْ يَأْتِكُمْ نَبَأُ الَّذِينَ كَفَرُوا مِنْ قَبْلُ فَذَاقُوا وَبَالَ أَمْرِهِمْ وَلَهُمْ عَذَابٌ أَلِيمٌ', 64, 5, 28, 111, 556, 'آیا خبر کسانی که پیش از این کافر شدند به شما نرسیده است؟! (آری) آنها طعم کیفر گناهان بزرگ خود را چشیدند؛ و عذاب دردناک برای آنهاست!'),
(5205, 5205, 'ذَٰلِكَ بِأَنَّهُ كَانَتْ تَأْتِيهِمْ رُسُلُهُمْ بِالْبَيِّنَاتِ فَقَالُوا أَبَشَرٌ يَهْدُونَنَا فَكَفَرُوا وَتَوَلَّوْا ۚ وَاسْتَغْنَى اللَّهُ ۚ وَاللَّهُ غَنِيٌّ حَمِيدٌ', 64, 6, 28, 111, 556, 'این بخاطر آن است که رسولان آنها (پیوسته) با دلایل روشن به سراغشان می‌آمدند، ولی آنها (از روی کبر و غرور) گفتند: «آیا بشرهایی (مثل ما) می‌خواهند ما را هدایت کنند؟!» از این رو کافر شدند و روی برگرداندند؛ و خداوند (از ایمان و طاعتشان) بی نیاز بود، و خدا غنی و شایسته ستایش است!'),
(5206, 5206, 'زَعَمَ الَّذِينَ كَفَرُوا أَنْ لَنْ يُبْعَثُوا ۚ قُلْ بَلَىٰ وَرَبِّي لَتُبْعَثُنَّ ثُمَّ لَتُنَبَّؤُنَّ بِمَا عَمِلْتُمْ ۚ وَذَٰلِكَ عَلَى اللَّهِ يَسِيرٌ', 64, 7, 28, 111, 556, 'کافران پنداشتند که هرگز برانگیخته نخواهند شد، بگو: «آری به پروردگارم سوگند که همه شما (در قیامت) برانگیخته خواهید شد، سپس آنچه را عمل می‌کردید به شما خبر داده می‌شود، و این برای خداوند آسان است!»'),
(5207, 5207, 'فَآمِنُوا بِاللَّهِ وَرَسُولِهِ وَالنُّورِ الَّذِي أَنْزَلْنَا ۚ وَاللَّهُ بِمَا تَعْمَلُونَ خَبِيرٌ', 64, 8, 28, 111, 556, 'حال که چنین است، به خدا و رسول او و نوری که نازل کرده‌ایم ایمان بیاورید؛ و بدانید خدا به آنچه انجام می‌دهید آگاه است!'),
(5208, 5208, 'يَوْمَ يَجْمَعُكُمْ لِيَوْمِ الْجَمْعِ ۖ ذَٰلِكَ يَوْمُ التَّغَابُنِ ۗ وَمَنْ يُؤْمِنْ بِاللَّهِ وَيَعْمَلْ صَالِحًا يُكَفِّرْ عَنْهُ سَيِّئَاتِهِ وَيُدْخِلْهُ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهَارُ خَالِدِينَ فِيهَا أَبَدًا ۚ ذَٰلِكَ الْفَوْزُ الْعَظِيمُ', 64, 9, 28, 111, 556, 'این در زمانی خواهد بود که همه شما را در روز اجتماع = روز رستاخیز گردآوری می‌کند؛ آن روز روز تغابن است (روز احساس خسارت و پشیمانی)! و هر کس به خدا ایمان بیاورد و عمل صالح انجام دهد، گناهان او را می‌بخشد و او را در باغهایی از بهشت که نهرها از زیر درختانش جاری است وارد می‌کند، جاودانه در آن می مانند؛ و این پیروزی بزرگ است!'),
(5209, 5209, 'وَالَّذِينَ كَفَرُوا وَكَذَّبُوا بِآيَاتِنَا أُولَٰئِكَ أَصْحَابُ النَّارِ خَالِدِينَ فِيهَا ۖ وَبِئْسَ الْمَصِيرُ', 64, 10, 28, 111, 557, 'اما کسانی که کافر شدند و آیات ما را تکذیب کردند اصحاب دوزخند، جاودانه در آن می‌مانند، و (سرانجام آنها) سرانجام بدی است!'),
(5210, 5210, 'مَا أَصَابَ مِنْ مُصِيبَةٍ إِلَّا بِإِذْنِ اللَّهِ ۗ وَمَنْ يُؤْمِنْ بِاللَّهِ يَهْدِ قَلْبَهُ ۚ وَاللَّهُ بِكُلِّ شَيْءٍ عَلِيمٌ', 64, 11, 28, 111, 557, 'هیچ مصیبتی رخ نمی‌دهد مگر به اذن خدا! و هر کس به خدا ایمان آورد، خداوند قلبش را هدایت می‌کند؛ و خدا به هر چیز داناست!'),
(5211, 5211, 'وَأَطِيعُوا اللَّهَ وَأَطِيعُوا الرَّسُولَ ۚ فَإِنْ تَوَلَّيْتُمْ فَإِنَّمَا عَلَىٰ رَسُولِنَا الْبَلَاغُ الْمُبِينُ', 64, 12, 28, 111, 557, 'اطاعت کنید خدا را، و اطاعت کنید پیامبر را؛ و اگر روی‌گردان شوید، رسول ما جز ابلاغ آشکار وظیفه‌ای ندارد!'),
(5212, 5212, 'اللَّهُ لَا إِلَٰهَ إِلَّا هُوَ ۚ وَعَلَى اللَّهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُونَ', 64, 13, 28, 111, 557, 'خداوند کسی است که هیچ معبودی جز او نیست، و مؤمنان باید فقط بر او توکل کنند!'),
(5213, 5213, 'يَا أَيُّهَا الَّذِينَ آمَنُوا إِنَّ مِنْ أَزْوَاجِكُمْ وَأَوْلَادِكُمْ عَدُوًّا لَكُمْ فَاحْذَرُوهُمْ ۚ وَإِنْ تَعْفُوا وَتَصْفَحُوا وَتَغْفِرُوا فَإِنَّ اللَّهَ غَفُورٌ رَحِيمٌ', 64, 14, 28, 111, 557, 'ای کسانی که ایمان آورده‌اید! بعضی از همسران و فرزندانتان دشمنان شما هستند، از آنها برحذر باشید؛ و اگر عفو کنید و چشم بپوشید و ببخشید، (خدا شما را می‌بخشد)؛ چرا که خداوند بخشنده و مهربان است!'),
(5214, 5214, 'إِنَّمَا أَمْوَالُكُمْ وَأَوْلَادُكُمْ فِتْنَةٌ ۚ وَاللَّهُ عِنْدَهُ أَجْرٌ عَظِيمٌ', 64, 15, 28, 111, 557, 'اموال و فرزندانتان فقط وسیله آزمایش شما هستند؛ و خداست که پاداش عظیم نزد اوست!'),
(5215, 5215, 'فَاتَّقُوا اللَّهَ مَا اسْتَطَعْتُمْ وَاسْمَعُوا وَأَطِيعُوا وَأَنْفِقُوا خَيْرًا لِأَنْفُسِكُمْ ۗ وَمَنْ يُوقَ شُحَّ نَفْسِهِ فَأُولَٰئِكَ هُمُ الْمُفْلِحُونَ', 64, 16, 28, 111, 557, 'پس تا می‌توانید تقوای الهی پیشه کنید و گوش دهید و اطاعت نمایید و انفاق کنید که برای شما بهتر است؛ و کسانی که از بخل و حرص خویشتن مصون بمانند رستگارانند!'),
(5216, 5216, 'إِنْ تُقْرِضُوا اللَّهَ قَرْضًا حَسَنًا يُضَاعِفْهُ لَكُمْ وَيَغْفِرْ لَكُمْ ۚ وَاللَّهُ شَكُورٌ حَلِيمٌ', 64, 17, 28, 111, 557, 'اگر به خدا قرض‌الحسنه دهید، آن را برای شما مضاعف می‌سازد و شما را می‌بخشد؛ و خداوند شکرکننده و بردبار است!'),
(5217, 5217, 'عَالِمُ الْغَيْبِ وَالشَّهَادَةِ الْعَزِيزُ الْحَكِيمُ', 64, 18, 28, 111, 557, 'او دانای پنهان و آشکار است؛ و او عزیز و حکیم است!'),
(5218, 5218, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ يَا أَيُّهَا النَّبِيُّ إِذَا طَلَّقْتُمُ النِّسَاءَ فَطَلِّقُوهُنَّ لِعِدَّتِهِنَّ وَأَحْصُوا الْعِدَّةَ ۖ وَاتَّقُوا اللَّهَ رَبَّكُمْ ۖ لَا تُخْرِجُوهُنَّ مِنْ بُيُوتِهِنَّ وَلَا يَخْرُجْنَ إِلَّا أَنْ يَأْتِينَ بِفَاحِشَةٍ مُبَيِّنَةٍ ۚ وَتِلْكَ حُدُودُ اللَّهِ ۚ وَمَنْ يَتَعَدَّ حُدُودَ اللَّهِ فَقَدْ ظَلَمَ نَفْسَهُ ۚ لَا تَدْرِي لَعَلَّ اللَّهَ يُحْدِثُ بَعْدَ ذَٰلِكَ أَمْرًا', 65, 1, 28, 112, 558, 'ای پیامبر! هر زمان خواستید زنان را طلاق دهید، در زمان عدّه، آنها را طلاق گویید = زمانی که از عادت ماهانه پاک شده و با همسرشان نزدیکی نکرده باشند، و حساب عدّه را نگه دارید؛ و از خدایی که پروردگار شماست بپرهیزید؛ نه شما آنها را از خانه‌هایشان بیرون کنید و نه آنها (در دوران عدّه) بیرون روند، مگر آنکه کار زشت آشکاری انجام دهند؛ این حدود خداست، و هر کس از حدود الهی تجاوز کند به خویشتن ستم کرده؛ تو نمی‌دانی شاید خداوند بعد از این، وضع تازه (و وسیله اصلاحی) فراهم کند!'),
(5219, 5219, 'فَإِذَا بَلَغْنَ أَجَلَهُنَّ فَأَمْسِكُوهُنَّ بِمَعْرُوفٍ أَوْ فَارِقُوهُنَّ بِمَعْرُوفٍ وَأَشْهِدُوا ذَوَيْ عَدْلٍ مِنْكُمْ وَأَقِيمُوا الشَّهَادَةَ لِلَّهِ ۚ ذَٰلِكُمْ يُوعَظُ بِهِ مَنْ كَانَ يُؤْمِنُ بِاللَّهِ وَالْيَوْمِ الْآخِرِ ۚ وَمَنْ يَتَّقِ اللَّهَ يَجْعَلْ لَهُ مَخْرَجًا', 65, 2, 28, 112, 558, 'و چون عده آنها سرآمد، آنها را بطرز شایسته‌ای نگه دارید یا بطرز شایسته‌ای از آنان جدا شوید؛ و دو مرد عادل از خودتان را گواه گیرید؛ و شهادت را برای خدا برپا دارید؛ این چیزی است که مؤمنان به خدا و روز قیامت به آن اندرز داده می‌شوند! و هر کس تقوای الهی پیشه کند، خداوند راه نجاتی برای او فراهم می‌کند،'),
(5220, 5220, 'وَيَرْزُقْهُ مِنْ حَيْثُ لَا يَحْتَسِبُ ۚ وَمَنْ يَتَوَكَّلْ عَلَى اللَّهِ فَهُوَ حَسْبُهُ ۚ إِنَّ اللَّهَ بَالِغُ أَمْرِهِ ۚ قَدْ جَعَلَ اللَّهُ لِكُلِّ شَيْءٍ قَدْرًا', 65, 3, 28, 112, 558, 'و او را از جایی که گمان ندارد روزی می‌دهد؛ و هر کس بر خدا توکّل کند، کفایت امرش را می‌کند؛ خداوند فرمان خود را به انجام می‌رساند؛ و خدا برای هر چیزی اندازه‌ای قرار داده است!'),
(5221, 5221, 'وَاللَّائِي يَئِسْنَ مِنَ الْمَحِيضِ مِنْ نِسَائِكُمْ إِنِ ارْتَبْتُمْ فَعِدَّتُهُنَّ ثَلَاثَةُ أَشْهُرٍ وَاللَّائِي لَمْ يَحِضْنَ ۚ وَأُولَاتُ الْأَحْمَالِ أَجَلُهُنَّ أَنْ يَضَعْنَ حَمْلَهُنَّ ۚ وَمَنْ يَتَّقِ اللَّهَ يَجْعَلْ لَهُ مِنْ أَمْرِهِ يُسْرًا', 65, 4, 28, 112, 558, 'و از زنانتان، آنان که از عادت ماهانه مأیوسند، اگر در وضع آنها (از نظر بارداری) شک کنید، عده آنان سه ماه است، و همچنین آنها که عادت ماهانه ندیده‌اند؛ و عده زنان باردار این است که بار خود را بر زمین بگذارند؛ و هر کس تقوای الهی پیشه کند، خداوند کار را بر او آسان می‌سازد!'),
(5222, 5222, 'ذَٰلِكَ أَمْرُ اللَّهِ أَنْزَلَهُ إِلَيْكُمْ ۚ وَمَنْ يَتَّقِ اللَّهَ يُكَفِّرْ عَنْهُ سَيِّئَاتِهِ وَيُعْظِمْ لَهُ أَجْرًا', 65, 5, 28, 112, 558, 'این فرمان خداست که بر شما نازل کرده؛ و هر کس تقوای الهی پیشه کند، خداوند گناهانش را می‌بخشد و پاداش او را بزرگ می‌دارد!'),
(5223, 5223, 'أَسْكِنُوهُنَّ مِنْ حَيْثُ سَكَنْتُمْ مِنْ وُجْدِكُمْ وَلَا تُضَارُّوهُنَّ لِتُضَيِّقُوا عَلَيْهِنَّ ۚ وَإِنْ كُنَّ أُولَاتِ حَمْلٍ فَأَنْفِقُوا عَلَيْهِنَّ حَتَّىٰ يَضَعْنَ حَمْلَهُنَّ ۚ فَإِنْ أَرْضَعْنَ لَكُمْ فَآتُوهُنَّ أُجُورَهُنَّ ۖ وَأْتَمِرُوا بَيْنَكُمْ بِمَعْرُوفٍ ۖ وَإِنْ تَعَاسَرْتُمْ فَسَتُرْضِعُ لَهُ أُخْرَىٰ', 65, 6, 28, 112, 559, 'آنها = زنان مطلّقه‌ را هر جا خودتان سکونت دارید و در توانایی شماست سکونت دهید؛ و به آنها زیان نرسانید تا کار را بر آنان تنگ کنید (و مجبور به ترک منزل شوند)؛ و اگر باردار باشند، نفقه آنها را بپردازید تا وضع حمل کنند؛ و اگر برای شما (فرزند را) شیر می‌دهند، پاداش آنها را بپردازید؛ و (درباره فرزندان، کار را) با مشاوره شایسته انجام دهید؛ و اگر به توافق نرسیدید، آن دیگری شیردادن آن بچه را بر عهده می‌گیرد.'),
(5224, 5224, 'لِيُنْفِقْ ذُو سَعَةٍ مِنْ سَعَتِهِ ۖ وَمَنْ قُدِرَ عَلَيْهِ رِزْقُهُ فَلْيُنْفِقْ مِمَّا آتَاهُ اللَّهُ ۚ لَا يُكَلِّفُ اللَّهُ نَفْسًا إِلَّا مَا آتَاهَا ۚ سَيَجْعَلُ اللَّهُ بَعْدَ عُسْرٍ يُسْرًا', 65, 7, 28, 112, 559, 'آنان که امکانات وسیعی دارند، باید از امکانات وسیع خود انفاق کنند و آنها که تنگدستند، از آنچه که خدا به آنها داده انفاق نمایند؛ خداوند هیچ کس را جز به مقدار توانایی که به او داده تکلیف نمی‌کند؛ خداوند بزودی بعد از سختیها آسانی قرار می‌دهد!'),
(5225, 5225, 'وَكَأَيِّنْ مِنْ قَرْيَةٍ عَتَتْ عَنْ أَمْرِ رَبِّهَا وَرُسُلِهِ فَحَاسَبْنَاهَا حِسَابًا شَدِيدًا وَعَذَّبْنَاهَا عَذَابًا نُكْرًا', 65, 8, 28, 112, 559, 'چه بسیار شهرها و آبادیها که اهل آن از فرمان خدا و رسولانش سرپیچی کردند و ما بشدّت به حسابشان رسیدیم و به مجازات کم‌نظیری گرفتار ساختیم!'),
(5226, 5226, 'فَذَاقَتْ وَبَالَ أَمْرِهَا وَكَانَ عَاقِبَةُ أَمْرِهَا خُسْرًا', 65, 9, 28, 112, 559, 'آنها آثار سوء کار خود را چشیدند؛ و عاقبت کارشان خسران بود!'),
(5227, 5227, 'أَعَدَّ اللَّهُ لَهُمْ عَذَابًا شَدِيدًا ۖ فَاتَّقُوا اللَّهَ يَا أُولِي الْأَلْبَابِ الَّذِينَ آمَنُوا ۚ قَدْ أَنْزَلَ اللَّهُ إِلَيْكُمْ ذِكْرًا', 65, 10, 28, 112, 559, 'خداوند عذاب سختی برای آنها فراهم ساخته؛ پس از (مخالفت فرمان) خدا بپرهیزید ای خردمندانی که ایمان آورده‌اید! (زیرا) خداوند چیزی که مایه تذکّر است بر شما نازل کرده؛'),
(5228, 5228, 'رَسُولًا يَتْلُو عَلَيْكُمْ آيَاتِ اللَّهِ مُبَيِّنَاتٍ لِيُخْرِجَ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ مِنَ الظُّلُمَاتِ إِلَى النُّورِ ۚ وَمَنْ يُؤْمِنْ بِاللَّهِ وَيَعْمَلْ صَالِحًا يُدْخِلْهُ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهَارُ خَالِدِينَ فِيهَا أَبَدًا ۖ قَدْ أَحْسَنَ اللَّهُ لَهُ رِزْقًا', 65, 11, 28, 112, 559, 'رسولی به سوی شما فرستاده که آیات روشن خدا را بر شما تلاوت می‌کند تا کسانی را که ایمان آورده و کارهای شایسته انجام داده‌اند، از تاریکیها بسوی نور خارج سازد! و هر کس به خدا ایمان آورده و اعمال صالح انجام دهد، او را در باغهایی از بهشت وارد سازد که از زیر (درختانش) نهرها جاری است، جاودانه در آن می‌مانند، و خداوند روزی نیکویی برای او قرار داده است!'),
(5229, 5229, 'اللَّهُ الَّذِي خَلَقَ سَبْعَ سَمَاوَاتٍ وَمِنَ الْأَرْضِ مِثْلَهُنَّ يَتَنَزَّلُ الْأَمْرُ بَيْنَهُنَّ لِتَعْلَمُوا أَنَّ اللَّهَ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ وَأَنَّ اللَّهَ قَدْ أَحَاطَ بِكُلِّ شَيْءٍ عِلْمًا', 65, 12, 28, 112, 559, 'خداوند همان کسی است که هفت آسمان را آفرید، و از زمین نیز همانند آنها را؛ فرمان او در میان آنها پیوسته فرود می‌آید تا بدانید خداوند بر هر چیز تواناست و اینکه علم او به همه چیز احاطه دارد!'),
(5230, 5230, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ يَا أَيُّهَا النَّبِيُّ لِمَ تُحَرِّمُ مَا أَحَلَّ اللَّهُ لَكَ ۖ تَبْتَغِي مَرْضَاتَ أَزْوَاجِكَ ۚ وَاللَّهُ غَفُورٌ رَحِيمٌ', 66, 1, 28, 112, 560, 'ای پیامبر! چرا چیزی را که خدا بر تو حلال کرده بخاطر جلب رضایت همسرانت بر خود حرام می‌کنی؟! و خداوند آمرزنده و رحیم است.'),
(5231, 5231, 'قَدْ فَرَضَ اللَّهُ لَكُمْ تَحِلَّةَ أَيْمَانِكُمْ ۚ وَاللَّهُ مَوْلَاكُمْ ۖ وَهُوَ الْعَلِيمُ الْحَكِيمُ', 66, 2, 28, 112, 560, 'خداوند راه گشودن سوگندهایتان را (در این گونه موارد) روشن ساخته؛ و خداوند مولای شماست و او دانا و حکیم است.'),
(5232, 5232, 'وَإِذْ أَسَرَّ النَّبِيُّ إِلَىٰ بَعْضِ أَزْوَاجِهِ حَدِيثًا فَلَمَّا نَبَّأَتْ بِهِ وَأَظْهَرَهُ اللَّهُ عَلَيْهِ عَرَّفَ بَعْضَهُ وَأَعْرَضَ عَنْ بَعْضٍ ۖ فَلَمَّا نَبَّأَهَا بِهِ قَالَتْ مَنْ أَنْبَأَكَ هَٰذَا ۖ قَالَ نَبَّأَنِيَ الْعَلِيمُ الْخَبِيرُ', 66, 3, 28, 112, 560, '(به خاطر بیاورید) هنگامی را که پیامبر یکی از رازهای خود را به بعضی از همسرانش گفت، ولی هنگامی که وی آن را افشا کرد و خداوند پیامبرش را از آن آگاه ساخت، قسمتی از آن را برای او بازگو کرد و از قسمت دیگر خودداری نمود؛ هنگامی که پیامبر همسرش را از آن خبر داد، گفت: «چه کسی تو را از این راز آگاه ساخت؟» فرمود: «خداوند عالم و آگاه مرا باخبر ساخت!»'),
(5233, 5233, 'إِنْ تَتُوبَا إِلَى اللَّهِ فَقَدْ صَغَتْ قُلُوبُكُمَا ۖ وَإِنْ تَظَاهَرَا عَلَيْهِ فَإِنَّ اللَّهَ هُوَ مَوْلَاهُ وَجِبْرِيلُ وَصَالِحُ الْمُؤْمِنِينَ ۖ وَالْمَلَائِكَةُ بَعْدَ ذَٰلِكَ ظَهِيرٌ', 66, 4, 28, 112, 560, 'اگر شما (همسران پیامبر) از کار خود توبه کنید (به نفع شماست، زیرا) دلهایتان از حق منحرف گشته؛ و اگر بر ضدّ او دست به دست هم دهید، (کاری از پیش نخواهید برد) زیرا خداوند یاور اوست و همچنین جبرئیل و مؤمنان صالح، و فرشتگان بعد از آنان پشتیبان اویند.'),
(5234, 5234, 'عَسَىٰ رَبُّهُ إِنْ طَلَّقَكُنَّ أَنْ يُبْدِلَهُ أَزْوَاجًا خَيْرًا مِنْكُنَّ مُسْلِمَاتٍ مُؤْمِنَاتٍ قَانِتَاتٍ تَائِبَاتٍ عَابِدَاتٍ سَائِحَاتٍ ثَيِّبَاتٍ وَأَبْكَارًا', 66, 5, 28, 112, 560, 'امید است که اگر او شما را طلاق دهد، پروردگارش به جای شما همسرانی بهتر برای او قرار دهد، همسرانی مسلمان، مؤمن، متواضع، توبه کار، عابد، هجرت‌کننده، زنانی غیرباکره و باکره!'),
(5235, 5235, 'يَا أَيُّهَا الَّذِينَ آمَنُوا قُوا أَنْفُسَكُمْ وَأَهْلِيكُمْ نَارًا وَقُودُهَا النَّاسُ وَالْحِجَارَةُ عَلَيْهَا مَلَائِكَةٌ غِلَاظٌ شِدَادٌ لَا يَعْصُونَ اللَّهَ مَا أَمَرَهُمْ وَيَفْعَلُونَ مَا يُؤْمَرُونَ', 66, 6, 28, 112, 560, 'ای کسانی که ایمان آورده‌اید خود و خانواده خویش را از آتشی که هیزم آن انسانها و سنگهاست نگه دارید؛ آتشی که فرشتگانی بر آن گمارده شده که خشن و سختگیرند و هرگز فرمان خدا را مخالفت نمی‌کنند و آنچه را فرمان داده شده‌اند (به طور کامل) اجرا می‌نمایند!'),
(5236, 5236, 'يَا أَيُّهَا الَّذِينَ كَفَرُوا لَا تَعْتَذِرُوا الْيَوْمَ ۖ إِنَّمَا تُجْزَوْنَ مَا كُنْتُمْ تَعْمَلُونَ', 66, 7, 28, 112, 560, 'ای کسانی که کافر شده‌اید امروز عذرخواهی نکنید، چرا که تنها به اعمالتان جزا داده می‌شوید!'),
(5237, 5237, 'يَا أَيُّهَا الَّذِينَ آمَنُوا تُوبُوا إِلَى اللَّهِ تَوْبَةً نَصُوحًا عَسَىٰ رَبُّكُمْ أَنْ يُكَفِّرَ عَنْكُمْ سَيِّئَاتِكُمْ وَيُدْخِلَكُمْ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهَارُ يَوْمَ لَا يُخْزِي اللَّهُ النَّبِيَّ وَالَّذِينَ آمَنُوا مَعَهُ ۖ نُورُهُمْ يَسْعَىٰ بَيْنَ أَيْدِيهِمْ وَبِأَيْمَانِهِمْ يَقُولُونَ رَبَّنَا أَتْمِمْ لَنَا نُورَنَا وَاغْفِرْ لَنَا ۖ إِنَّكَ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ', 66, 8, 28, 112, 561, 'ای کسانی که ایمان آورده‌اید بسوی خدا توبه کنید، توبه‌ای خالص؛ امید است (با این کار) پروردگارتان گناهانتان را ببخشد و شما را در باغهایی از بهشت که نهرها از زیر درختانش جاری است وارد کند، در آن روزی که خداوند پیامبر و کسانی را که با او ایمان آوردند خوار نمی‌کند؛ این در حالی است که نورشان پیشاپیش آنان و از سوی راستشان در حرکت است، و می‌گویند: «پروردگارا! نور ما را کامل کن و ما را ببخش که تو بر هر چیز توانائی!»'),
(5238, 5238, 'يَا أَيُّهَا النَّبِيُّ جَاهِدِ الْكُفَّارَ وَالْمُنَافِقِينَ وَاغْلُظْ عَلَيْهِمْ ۚ وَمَأْوَاهُمْ جَهَنَّمُ ۖ وَبِئْسَ الْمَصِيرُ', 66, 9, 28, 112, 561, 'ای پیامبر! با کفّار و منافقین پیکار کن و بر آنان سخت بگیر! جایگاهشان جهنم است، و بد فرجامی است!'),
(5239, 5239, 'ضَرَبَ اللَّهُ مَثَلًا لِلَّذِينَ كَفَرُوا امْرَأَتَ نُوحٍ وَامْرَأَتَ لُوطٍ ۖ كَانَتَا تَحْتَ عَبْدَيْنِ مِنْ عِبَادِنَا صَالِحَيْنِ فَخَانَتَاهُمَا فَلَمْ يُغْنِيَا عَنْهُمَا مِنَ اللَّهِ شَيْئًا وَقِيلَ ادْخُلَا النَّارَ مَعَ الدَّاخِلِينَ', 66, 10, 28, 112, 561, 'خداوند برای کسانی که کافر شده‌اند به همسر نوح و همسر لوط مثَل زده است، آن دو تحت سرپرستی دو بنده از بندگان صالح ما بودند، ولی به آن دو خیانت کردند و ارتباط با این دو (پیامبر) سودی به حالشان (در برابر عذاب الهی) نداشت، و به آنها گفته شد: «وارد آتش شوید همراه کسانی که وارد می‌شوند!»'),
(5240, 5240, 'وَضَرَبَ اللَّهُ مَثَلًا لِلَّذِينَ آمَنُوا امْرَأَتَ فِرْعَوْنَ إِذْ قَالَتْ رَبِّ ابْنِ لِي عِنْدَكَ بَيْتًا فِي الْجَنَّةِ وَنَجِّنِي مِنْ فِرْعَوْنَ وَعَمَلِهِ وَنَجِّنِي مِنَ الْقَوْمِ الظَّالِمِينَ', 66, 11, 28, 112, 561, 'و خداوند برای مؤمنان، به همسر فرعون مثَل زده است، در آن هنگام که گفت: «پروردگارا! خانه‌ای برای من نزد خودت در بهشت بساز، و مرا از فرعون و کار او نجات ده و مرا از گروه ستمگران رهایی بخش!»'),
(5241, 5241, 'وَمَرْيَمَ ابْنَتَ عِمْرَانَ الَّتِي أَحْصَنَتْ فَرْجَهَا فَنَفَخْنَا فِيهِ مِنْ رُوحِنَا وَصَدَّقَتْ بِكَلِمَاتِ رَبِّهَا وَكُتُبِهِ وَكَانَتْ مِنَ الْقَانِتِينَ', 66, 12, 28, 112, 561, 'و همچنین به مریم دختر عمران که دامان خود را پاک نگه داشت، و ما را از روح خود در آن دمیدیم؛ او کلمات پروردگار و کتابهایش را تصدیق کرد و از مطیعان فرمان خدا بود!'),
(5242, 5242, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ تَبَارَكَ الَّذِي بِيَدِهِ الْمُلْكُ وَهُوَ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ', 67, 1, 29, 113, 562, 'پربرکت و زوال‌ناپذیر است کسی که حکومت جهان هستی به دست اوست، و او بر هر چیز تواناست.'),
(5243, 5243, 'الَّذِي خَلَقَ الْمَوْتَ وَالْحَيَاةَ لِيَبْلُوَكُمْ أَيُّكُمْ أَحْسَنُ عَمَلًا ۚ وَهُوَ الْعَزِيزُ الْغَفُورُ', 67, 2, 29, 113, 562, 'آن کس که مرگ و حیات را آفرید تا شما را بیازماید که کدام یک از شما بهتر عمل می‌کنید، و او شکست‌ناپذیر و بخشنده است.'),
(5244, 5244, 'الَّذِي خَلَقَ سَبْعَ سَمَاوَاتٍ طِبَاقًا ۖ مَا تَرَىٰ فِي خَلْقِ الرَّحْمَٰنِ مِنْ تَفَاوُتٍ ۖ فَارْجِعِ الْبَصَرَ هَلْ تَرَىٰ مِنْ فُطُورٍ', 67, 3, 29, 113, 562, 'همان کسی که هفت آسمان را بر فراز یکدیگر آفرید؛ در آفرینش خداوند رحمان هیچ تضادّ و عیبی نمی‌بینی! بار دیگر نگاه کن، آیا هیچ شکاف و خللی مشاهده می‌کنی؟!'),
(5245, 5245, 'ثُمَّ ارْجِعِ الْبَصَرَ كَرَّتَيْنِ يَنْقَلِبْ إِلَيْكَ الْبَصَرُ خَاسِئًا وَهُوَ حَسِيرٌ', 67, 4, 29, 113, 562, 'بار دیگر (به عالم هستی) نگاه کن، سرانجام چشمانت (در جستجوی خلل و نقصان ناکام مانده) به سوی تو باز می‌گردد در حالی که خسته و ناتوان است!'),
(5246, 5246, 'وَلَقَدْ زَيَّنَّا السَّمَاءَ الدُّنْيَا بِمَصَابِيحَ وَجَعَلْنَاهَا رُجُومًا لِلشَّيَاطِينِ ۖ وَأَعْتَدْنَا لَهُمْ عَذَابَ السَّعِيرِ', 67, 5, 29, 113, 562, 'ما آسمان پایین (نزدیک) را با چراغهای فروزانی زینت بخشیدیم، و آنها = شهابها را تیرهایی برای شیاطین قرار دادیم، و برای آنان عذاب آتش فروزان فراهم ساختیم!'),
(5247, 5247, 'وَلِلَّذِينَ كَفَرُوا بِرَبِّهِمْ عَذَابُ جَهَنَّمَ ۖ وَبِئْسَ الْمَصِيرُ', 67, 6, 29, 113, 562, 'و برای کسانی که به پروردگارشان کافر شدند عذاب جهنّم است، و بد فرجامی است!'),
(5248, 5248, 'إِذَا أُلْقُوا فِيهَا سَمِعُوا لَهَا شَهِيقًا وَهِيَ تَفُورُ', 67, 7, 29, 113, 562, 'هنگامی که در آن افکنده شوند صدای وحشتناکی از آن می‌شنوند، و این در حالی است که پیوسته می‌جوشد!'),
(5249, 5249, 'تَكَادُ تَمَيَّزُ مِنَ الْغَيْظِ ۖ كُلَّمَا أُلْقِيَ فِيهَا فَوْجٌ سَأَلَهُمْ خَزَنَتُهَا أَلَمْ يَأْتِكُمْ نَذِيرٌ', 67, 8, 29, 113, 562, 'نزدیک است (دوزخ) از شدّت غضب پاره پاره شود؛ هر زمان که گروهی در آن افکنده می‌شوند، نگهبانان دوزخ از آنها می‌پرسند: «مگر بیم‌دهنده الهی به سراغ شما نیامد؟!»'),
(5250, 5250, 'قَالُوا بَلَىٰ قَدْ جَاءَنَا نَذِيرٌ فَكَذَّبْنَا وَقُلْنَا مَا نَزَّلَ اللَّهُ مِنْ شَيْءٍ إِنْ أَنْتُمْ إِلَّا فِي ضَلَالٍ كَبِيرٍ', 67, 9, 29, 113, 562, 'می‌گویند: «آری، بیم‌دهنده به سراغ ما آمد، ولی ما او را تکذیب کردیم و گفتیم: خداوند هرگز چیزی نازل نکرده، و شما در گمراهی بزرگی هستید!»'),
(5251, 5251, 'وَقَالُوا لَوْ كُنَّا نَسْمَعُ أَوْ نَعْقِلُ مَا كُنَّا فِي أَصْحَابِ السَّعِيرِ', 67, 10, 29, 113, 562, 'و می‌گویند: «اگر ما گوش شنوا داشتیم یا تعقّل می‌کردیم، در میان دوزخیان نبودیم!»'),
(5252, 5252, 'فَاعْتَرَفُوا بِذَنْبِهِمْ فَسُحْقًا لِأَصْحَابِ السَّعِيرِ', 67, 11, 29, 113, 562, 'اینجاست که به گناه خود اعتراف می‌کنند؛ دور باشند دوزخیان از رحمت خدا!'),
(5253, 5253, 'إِنَّ الَّذِينَ يَخْشَوْنَ رَبَّهُمْ بِالْغَيْبِ لَهُمْ مَغْفِرَةٌ وَأَجْرٌ كَبِيرٌ', 67, 12, 29, 113, 562, '(امّا) کسانی که از پروردگارشان در نهان می‌ترسند، مسلّماً آمرزش و پاداش بزرگی دارند!'),
(5254, 5254, 'وَأَسِرُّوا قَوْلَكُمْ أَوِ اجْهَرُوا بِهِ ۖ إِنَّهُ عَلِيمٌ بِذَاتِ الصُّدُورِ', 67, 13, 29, 113, 563, 'گفتار خود را پنهان کنید یا آشکار (تفاوتی نمی‌کند)، او به آنچه در سینه‌هاست آگاه است!'),
(5255, 5255, 'أَلَا يَعْلَمُ مَنْ خَلَقَ وَهُوَ اللَّطِيفُ الْخَبِيرُ', 67, 14, 29, 113, 563, 'آیا آن کسی که موجودات را آفریده از حال آنها آگاه نیست؟! در حالی که او (از اسرار دقیق) باخبر و آگاه است!'),
(5256, 5256, 'هُوَ الَّذِي جَعَلَ لَكُمُ الْأَرْضَ ذَلُولًا فَامْشُوا فِي مَنَاكِبِهَا وَكُلُوا مِنْ رِزْقِهِ ۖ وَإِلَيْهِ النُّشُورُ', 67, 15, 29, 113, 563, 'او کسی است که زمین را برای شما رام کرد، بر شانه‌های آن راه بروید و از روزیهای خداوند بخورید؛ و بازگشت و اجتماع همه به سوی اوست!'),
(5257, 5257, 'أَأَمِنْتُمْ مَنْ فِي السَّمَاءِ أَنْ يَخْسِفَ بِكُمُ الْأَرْضَ فَإِذَا هِيَ تَمُورُ', 67, 16, 29, 113, 563, 'آیا خود را از عذاب کسی که حاکم بر آسمان است در امان می‌دانید که دستور دهد زمین بشکافد و شما را فرو برد و به لرزش خود ادامه دهد؟!'),
(5258, 5258, 'أَمْ أَمِنْتُمْ مَنْ فِي السَّمَاءِ أَنْ يُرْسِلَ عَلَيْكُمْ حَاصِبًا ۖ فَسَتَعْلَمُونَ كَيْفَ نَذِيرِ', 67, 17, 29, 113, 563, 'یا خود را از عذاب خداوند آسمان در امان می‌دانید که تندبادی پر از سنگریزه بر شما فرستد؟! و بزودی خواهید دانست تهدیدهای من چگونه است!'),
(5259, 5259, 'وَلَقَدْ كَذَّبَ الَّذِينَ مِنْ قَبْلِهِمْ فَكَيْفَ كَانَ نَكِيرِ', 67, 18, 29, 113, 563, 'کسانی که پیش از آنان بودند (آیات الهی را) تکذیب کردند، امّا (ببین) مجازات من چگونه بود!'),
(5260, 5260, 'أَوَلَمْ يَرَوْا إِلَى الطَّيْرِ فَوْقَهُمْ صَافَّاتٍ وَيَقْبِضْنَ ۚ مَا يُمْسِكُهُنَّ إِلَّا الرَّحْمَٰنُ ۚ إِنَّهُ بِكُلِّ شَيْءٍ بَصِيرٌ', 67, 19, 29, 113, 563, 'آیا به پرندگانی که بالای سرشان است، و گاه بالهای خود را گسترده و گاه جمع می‌کنند، نگاه نکردند؟! جز خداوند رحمان کسی آنها را بر فراز آسمان نگه نمی‌دارد، چرا که او به هر چیز بیناست!'),
(5261, 5261, 'أَمَّنْ هَٰذَا الَّذِي هُوَ جُنْدٌ لَكُمْ يَنْصُرُكُمْ مِنْ دُونِ الرَّحْمَٰنِ ۚ إِنِ الْكَافِرُونَ إِلَّا فِي غُرُورٍ', 67, 20, 29, 113, 563, 'آیا این کسی که لشکر شماست می‌تواند شما را در برابر خداوند یاری دهد؟ ولی کافران تنها گرفتار فریبند!'),
(5262, 5262, 'أَمَّنْ هَٰذَا الَّذِي يَرْزُقُكُمْ إِنْ أَمْسَكَ رِزْقَهُ ۚ بَلْ لَجُّوا فِي عُتُوٍّ وَنُفُورٍ', 67, 21, 29, 113, 563, 'یا آن کسی که شما را روزی می‌دهد، اگر روزیش را بازدارد (چه کسی می‌تواند نیاز شما را تأمین کند)؟! ولی آنها در سرکشی و فرار از حقیقت لجاجت می‌ورزند!'),
(5263, 5263, 'أَفَمَنْ يَمْشِي مُكِبًّا عَلَىٰ وَجْهِهِ أَهْدَىٰ أَمَّنْ يَمْشِي سَوِيًّا عَلَىٰ صِرَاطٍ مُسْتَقِيمٍ', 67, 22, 29, 113, 563, 'آیا کسی که به رو افتاده حرکت می‌کند به هدایت نزدیکتر است یا کسی که راست‌قامت در صراط مستقیم گام برمی‌دارد؟!'),
(5264, 5264, 'قُلْ هُوَ الَّذِي أَنْشَأَكُمْ وَجَعَلَ لَكُمُ السَّمْعَ وَالْأَبْصَارَ وَالْأَفْئِدَةَ ۖ قَلِيلًا مَا تَشْكُرُونَ', 67, 23, 29, 113, 563, 'بگو: «او کسی است که شما را آفرید و برای شما گوش و چشم و قلب قرار داد؛ امّا کمتر سپاسگزاری می‌کنید!»'),
(5265, 5265, 'قُلْ هُوَ الَّذِي ذَرَأَكُمْ فِي الْأَرْضِ وَإِلَيْهِ تُحْشَرُونَ', 67, 24, 29, 113, 563, 'بگو: «او کسی است که شما را در زمین آفرید و به سوی او محشور می‌شوید!»'),
(5266, 5266, 'وَيَقُولُونَ مَتَىٰ هَٰذَا الْوَعْدُ إِنْ كُنْتُمْ صَادِقِينَ', 67, 25, 29, 113, 563, 'آنها می‌گویند: «اگر راست می‌گویید این وعده قیامت چه زمانی است؟!»'),
(5267, 5267, 'قُلْ إِنَّمَا الْعِلْمُ عِنْدَ اللَّهِ وَإِنَّمَا أَنَا نَذِيرٌ مُبِينٌ', 67, 26, 29, 113, 563, 'بگو: «علم آن تنها نزد خداست؛ و من فقط بیم‌دهنده آشکاری هستم!»'),
(5268, 5268, 'فَلَمَّا رَأَوْهُ زُلْفَةً سِيئَتْ وُجُوهُ الَّذِينَ كَفَرُوا وَقِيلَ هَٰذَا الَّذِي كُنْتُمْ بِهِ تَدَّعُونَ', 67, 27, 29, 113, 564, 'هنگامی که آن (وعده الهی) را از نزدیک می‌بینند، صورت کافران زشت و سیاه می‌گردد، و به آنها گفته می‌شود: «این همان چیزی است که تقاضای آن را داشتید»!'),
(5269, 5269, 'قُلْ أَرَأَيْتُمْ إِنْ أَهْلَكَنِيَ اللَّهُ وَمَنْ مَعِيَ أَوْ رَحِمَنَا فَمَنْ يُجِيرُ الْكَافِرِينَ مِنْ عَذَابٍ أَلِيمٍ', 67, 28, 29, 113, 564, 'بگو: «به من خبر دهید اگر خداوند مرا و تمام کسانی را که با من هستند هلاک کند، یا مورد ترحّم قرار دهد، چه کسی کافران را از عذاب دردناک پناه می‌دهد؟!»'),
(5270, 5270, 'قُلْ هُوَ الرَّحْمَٰنُ آمَنَّا بِهِ وَعَلَيْهِ تَوَكَّلْنَا ۖ فَسَتَعْلَمُونَ مَنْ هُوَ فِي ضَلَالٍ مُبِينٍ', 67, 29, 29, 113, 564, 'بگو: «او خداوند رحمان است، ما به او ایمان آورده و بر او توکّل کرده‌ایم؛ و بزودی می‌دانید چه کسی در گمراهی آشکار است!»'),
(5271, 5271, 'قُلْ أَرَأَيْتُمْ إِنْ أَصْبَحَ مَاؤُكُمْ غَوْرًا فَمَنْ يَأْتِيكُمْ بِمَاءٍ مَعِينٍ', 67, 30, 29, 113, 564, 'بگو: «به من خبر دهید اگر آبهای (سرزمین) شما در زمین فرو رود، چه کسی می‌تواند آب جاری و گوارا در دسترس شما قرار دهد؟!»'),
(5272, 5272, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ ن ۚ وَالْقَلَمِ وَمَا يَسْطُرُونَ', 68, 1, 29, 113, 564, 'ن، سوگند به قلم و آنچه مینویسند،'),
(5273, 5273, 'مَا أَنْتَ بِنِعْمَةِ رَبِّكَ بِمَجْنُونٍ', 68, 2, 29, 113, 564, 'که به نعمت پروردگارت تو مجنون نیستی،'),
(5274, 5274, 'وَإِنَّ لَكَ لَأَجْرًا غَيْرَ مَمْنُونٍ', 68, 3, 29, 113, 564, 'و برای تو پاداشی عظیم و همیشگی است!'),
(5275, 5275, 'وَإِنَّكَ لَعَلَىٰ خُلُقٍ عَظِيمٍ', 68, 4, 29, 113, 564, 'و تو اخلاق عظیم و برجسته‌ای داری!'),
(5276, 5276, 'فَسَتُبْصِرُ وَيُبْصِرُونَ', 68, 5, 29, 113, 564, 'و بزودی تو می‌بینی و آنان نیز می‌بینند،'),
(5277, 5277, 'بِأَيْيِكُمُ الْمَفْتُونُ', 68, 6, 29, 113, 564, 'که کدام یک از شما مجنونند!'),
(5278, 5278, 'إِنَّ رَبَّكَ هُوَ أَعْلَمُ بِمَنْ ضَلَّ عَنْ سَبِيلِهِ وَهُوَ أَعْلَمُ بِالْمُهْتَدِينَ', 68, 7, 29, 113, 564, 'پروردگارت بهتر از هر کس می‌داند چه کسی از راه او گمراه شده، و هدایت‌یافتگان را نیز بهتر می‌شناسد!'),
(5279, 5279, 'فَلَا تُطِعِ الْمُكَذِّبِينَ', 68, 8, 29, 113, 564, 'حال که چنین است از تکذیب‌کنندگان اطاعت مکن!'),
(5280, 5280, 'وَدُّوا لَوْ تُدْهِنُ فَيُدْهِنُونَ', 68, 9, 29, 113, 564, 'آنها دوست دارند نرمش نشان دهی تا آنها (هم) نرمش نشان دهند (نرمشی توأم با انحراف از مسیر حق)!'),
(5281, 5281, 'وَلَا تُطِعْ كُلَّ حَلَّافٍ مَهِينٍ', 68, 10, 29, 113, 564, 'و از کسی که بسیار سوگند یاد می‌کند و پست است اطاعت مکن،'),
(5282, 5282, 'هَمَّازٍ مَشَّاءٍ بِنَمِيمٍ', 68, 11, 29, 113, 564, 'کسی که بسیار عیبجوست و به سخن چینی آمد و شد می‌کند،'),
(5283, 5283, 'مَنَّاعٍ لِلْخَيْرِ مُعْتَدٍ أَثِيمٍ', 68, 12, 29, 113, 564, 'و بسیار مانع کار خیر، و متجاوز و گناهکار است؛'),
(5284, 5284, 'عُتُلٍّ بَعْدَ ذَٰلِكَ زَنِيمٍ', 68, 13, 29, 113, 564, 'علاوه بر اینها کینه توز و پرخور و خشن و بدنام است!'),
(5285, 5285, 'أَنْ كَانَ ذَا مَالٍ وَبَنِينَ', 68, 14, 29, 113, 564, 'مبادا بخاطر اینکه صاحب مال و فرزندان فراوان است (از او پیروی کنی)!'),
(5286, 5286, 'إِذَا تُتْلَىٰ عَلَيْهِ آيَاتُنَا قَالَ أَسَاطِيرُ الْأَوَّلِينَ', 68, 15, 29, 113, 564, 'هنگامی که آیات ما بر او خوانده می‌شود می‌گوید: «اینها افسانه‌های خرافی پیشینیان است!»'),
(5287, 5287, 'سَنَسِمُهُ عَلَى الْخُرْطُومِ', 68, 16, 29, 113, 565, '(ولی) ما بزودی بر بینی او علامت و داغ ننگ می‌نهیم!'),
(5288, 5288, 'إِنَّا بَلَوْنَاهُمْ كَمَا بَلَوْنَا أَصْحَابَ الْجَنَّةِ إِذْ أَقْسَمُوا لَيَصْرِمُنَّهَا مُصْبِحِينَ', 68, 17, 29, 113, 565, 'ما آنها را آزمودیم، همان گونه که «صاحبان باغ» را آزمایش کردیم، هنگامی که سوگند یاد کردند که میوه‌های باغ را صبحگاهان (دور از چشم مستمندان) بچینند.'),
(5289, 5289, 'وَلَا يَسْتَثْنُونَ', 68, 18, 29, 113, 565, 'و هیچ از آن استثنا نکنند؛'),
(5290, 5290, 'فَطَافَ عَلَيْهَا طَائِفٌ مِنْ رَبِّكَ وَهُمْ نَائِمُونَ', 68, 19, 29, 113, 565, 'امّا عذابی فراگیر (شب هنگام) بر (تمام) باغ آنها فرود آمد در حالی که همه در خواب بودند،'),
(5291, 5291, 'فَأَصْبَحَتْ كَالصَّرِيمِ', 68, 20, 29, 113, 565, 'و آن باغ سرسبز همچون شب سیاه و ظلمانی شد!'),
(5292, 5292, 'فَتَنَادَوْا مُصْبِحِينَ', 68, 21, 29, 113, 565, 'صبحگاهان یکدیگر را صدا زدند،'),
(5293, 5293, 'أَنِ اغْدُوا عَلَىٰ حَرْثِكُمْ إِنْ كُنْتُمْ صَارِمِينَ', 68, 22, 29, 113, 565, 'که بسوی کشتزار و باغ خود حرکت کنید اگر قصد چیدن میوه‌ها را دارید!'),
(5294, 5294, 'فَانْطَلَقُوا وَهُمْ يَتَخَافَتُونَ', 68, 23, 29, 113, 565, 'آنها حرکت کردند در حالی که آهسته با هم می‌گفتند:»'),
(5295, 5295, 'أَنْ لَا يَدْخُلَنَّهَا الْيَوْمَ عَلَيْكُمْ مِسْكِينٌ', 68, 24, 29, 113, 565, '«مواظب باشید امروز حتی یک فقیر وارد بر شما نشود!»'),
(5296, 5296, 'وَغَدَوْا عَلَىٰ حَرْدٍ قَادِرِينَ', 68, 25, 29, 113, 565, '(آری) آنها صبحگاهان تصمیم داشتند که با قدرت از مستمندان جلوگیری کنند.'),
(5297, 5297, 'فَلَمَّا رَأَوْهَا قَالُوا إِنَّا لَضَالُّونَ', 68, 26, 29, 113, 565, 'هنگامی که (وارد باغ شدند و) آن را دیدند گفتند: «حقّاً» ما گمراهیم!'),
(5298, 5298, 'بَلْ نَحْنُ مَحْرُومُونَ', 68, 27, 29, 113, 565, '(آری، همه چیز از دست ما رفته) بلکه ما محرومیم!»'),
(5299, 5299, 'قَالَ أَوْسَطُهُمْ أَلَمْ أَقُلْ لَكُمْ لَوْلَا تُسَبِّحُونَ', 68, 28, 29, 113, 565, 'یکی از آنها که از همه عاقلتر بود گفت: «آیا به شما نگفتم چرا تسبیح خدا نمی‌گویید؟!'),
(5300, 5300, 'قَالُوا سُبْحَانَ رَبِّنَا إِنَّا كُنَّا ظَالِمِينَ', 68, 29, 29, 113, 565, 'گفتند: «منزّه است پروردگار ما، مسلّماً ما ظالم بودیم!»'),
(5301, 5301, 'فَأَقْبَلَ بَعْضُهُمْ عَلَىٰ بَعْضٍ يَتَلَاوَمُونَ', 68, 30, 29, 113, 565, 'سپس رو به یکدیگر کرده به ملامت هم پرداختند،'),
(5302, 5302, 'قَالُوا يَا وَيْلَنَا إِنَّا كُنَّا طَاغِينَ', 68, 31, 29, 113, 565, '(و فریادشان بلند شد) گفتند: «وای بر ما که طغیانگر بودیم!'),
(5303, 5303, 'عَسَىٰ رَبُّنَا أَنْ يُبْدِلَنَا خَيْرًا مِنْهَا إِنَّا إِلَىٰ رَبِّنَا رَاغِبُونَ', 68, 32, 29, 113, 565, 'امیدواریم پروردگارمان (ما را ببخشد و) بهتر از آن به جای آن به ما بدهد، چرا که ما به او علاقه‌مندیم!»'),
(5304, 5304, 'كَذَٰلِكَ الْعَذَابُ ۖ وَلَعَذَابُ الْآخِرَةِ أَكْبَرُ ۚ لَوْ كَانُوا يَعْلَمُونَ', 68, 33, 29, 113, 565, 'این گونه است عذاب (خداوند در دنیا)، و عذاب آخرت از آن هم بزرگتر است اگر می‌دانستند!'),
(5305, 5305, 'إِنَّ لِلْمُتَّقِينَ عِنْدَ رَبِّهِمْ جَنَّاتِ النَّعِيمِ', 68, 34, 29, 113, 565, 'مسلّماً برای پرهیزگاران نزد پروردگارشان باغهای پر نعمت بهشت است!'),
(5306, 5306, 'أَفَنَجْعَلُ الْمُسْلِمِينَ كَالْمُجْرِمِينَ', 68, 35, 29, 113, 565, 'آیا مؤمنان را همچون مجرمان قرار می‌دهیم؟!'),
(5307, 5307, 'مَا لَكُمْ كَيْفَ تَحْكُمُونَ', 68, 36, 29, 113, 565, 'شما را چه می‌شود؟! چگونه داوری می‌کنید؟!'),
(5308, 5308, 'أَمْ لَكُمْ كِتَابٌ فِيهِ تَدْرُسُونَ', 68, 37, 29, 113, 565, 'آیا کتابی دارید که از آن درس می‌خوانید...'),
(5309, 5309, 'إِنَّ لَكُمْ فِيهِ لَمَا تَخَيَّرُونَ', 68, 38, 29, 113, 565, 'که آنچه را شما انتخاب می‌کنید از آن شماست؟!'),
(5310, 5310, 'أَمْ لَكُمْ أَيْمَانٌ عَلَيْنَا بَالِغَةٌ إِلَىٰ يَوْمِ الْقِيَامَةِ ۙ إِنَّ لَكُمْ لَمَا تَحْكُمُونَ', 68, 39, 29, 113, 565, 'یا اینکه عهد و پیمان مؤکّد و مستمرّی تا روز قیامت بر ما دارید که هر چه را حکم کنید برای شما باشد؟!'),
(5311, 5311, 'سَلْهُمْ أَيُّهُمْ بِذَٰلِكَ زَعِيمٌ', 68, 40, 29, 113, 565, 'از آنها بپرس کدام یک از آنان چنین چیزی را تضمین می‌کند؟!'),
(5312, 5312, 'أَمْ لَهُمْ شُرَكَاءُ فَلْيَأْتُوا بِشُرَكَائِهِمْ إِنْ كَانُوا صَادِقِينَ', 68, 41, 29, 113, 565, 'یا اینکه معبودانی دارند که آنها را شریک خدا قرار داده‌اند (و برای آنان شفاعت می‌کنند)؟! اگر راست می‌گویند معبودان خود را بیاورند!'),
(5313, 5313, 'يَوْمَ يُكْشَفُ عَنْ سَاقٍ وَيُدْعَوْنَ إِلَى السُّجُودِ فَلَا يَسْتَطِيعُونَ', 68, 42, 29, 113, 565, '(به خاطر بیاورید) روزی را که ساق پاها (از وحشت) برهنه می‌گردد و دعوت به سجود می‌شوند، امّا نمی‌توانند (سجود کنند).'),
(5314, 5314, 'خَاشِعَةً أَبْصَارُهُمْ تَرْهَقُهُمْ ذِلَّةٌ ۖ وَقَدْ كَانُوا يُدْعَوْنَ إِلَى السُّجُودِ وَهُمْ سَالِمُونَ', 68, 43, 29, 113, 566, 'این در حالی است که چشمهایشان (از شدّت شرمساری) به زیر افتاده، و ذلّت و خواری وجودشان را فراگرفته؛ آنها پیش از این دعوت به سجود می‌شدند در حالی که سالم بودند (ولی امروز دیگر توانایی آن را ندارند)!'),
(5315, 5315, 'فَذَرْنِي وَمَنْ يُكَذِّبُ بِهَٰذَا الْحَدِيثِ ۖ سَنَسْتَدْرِجُهُمْ مِنْ حَيْثُ لَا يَعْلَمُونَ', 68, 44, 29, 113, 566, 'اکنون مرا با آنها که این سخن را تکذیب می‌کنند واگذار! ما آنان را از آنجا که نمی‌دانند به تدریج به سوی عذاب پیش می‌بریم.'),
(5316, 5316, 'وَأُمْلِي لَهُمْ ۚ إِنَّ كَيْدِي مَتِينٌ', 68, 45, 29, 113, 566, 'و به آنها مهلت (بازگشت) می‌دهم؛ چرا که نقشه‌های من محکم و دقیق است!'),
(5317, 5317, 'أَمْ تَسْأَلُهُمْ أَجْرًا فَهُمْ مِنْ مَغْرَمٍ مُثْقَلُونَ', 68, 46, 29, 113, 566, 'یا اینکه تو از آنها مُزدی می‌طلبی که پرداختش برای آنها سنگین است؟!'),
(5318, 5318, 'أَمْ عِنْدَهُمُ الْغَيْبُ فَهُمْ يَكْتُبُونَ', 68, 47, 29, 113, 566, 'یا اسرار غیب نزد آنهاست و آن را می‌نویسند (و به یکدیگر می‌دهند)؟!');
INSERT INTO `Quran` (`rowid`, `id`, `Arabic`, `Chapter`, `Verse`, `Juz`, `Hizb`, `Page`, `tr`) VALUES
(5319, 5319, 'فَاصْبِرْ لِحُكْمِ رَبِّكَ وَلَا تَكُنْ كَصَاحِبِ الْحُوتِ إِذْ نَادَىٰ وَهُوَ مَكْظُومٌ', 68, 48, 29, 113, 566, 'اکنون که چنین است صبر کن و منتظر فرمان پروردگارت باش، و مانند صاحب ماهی = یونس‌ مباش (که در تقاضای مجازات قومش عجله کرد و گرفتار مجازات ترک اولی شد) در آن زمان که با نهایت اندوه خدا را خواند.'),
(5320, 5320, 'لَوْلَا أَنْ تَدَارَكَهُ نِعْمَةٌ مِنْ رَبِّهِ لَنُبِذَ بِالْعَرَاءِ وَهُوَ مَذْمُومٌ', 68, 49, 29, 113, 566, 'و اگر رحمت خدا به یاریش نیامده بود، (از شکم ماهی) بیرون افکنده می‌شد در حالی که نکوهیده بود!'),
(5321, 5321, 'فَاجْتَبَاهُ رَبُّهُ فَجَعَلَهُ مِنَ الصَّالِحِينَ', 68, 50, 29, 113, 566, 'ولی پروردگارش او را برگزید و از صالحان قرار داد!'),
(5322, 5322, 'وَإِنْ يَكَادُ الَّذِينَ كَفَرُوا لَيُزْلِقُونَكَ بِأَبْصَارِهِمْ لَمَّا سَمِعُوا الذِّكْرَ وَيَقُولُونَ إِنَّهُ لَمَجْنُونٌ', 68, 51, 29, 113, 566, 'نزدیک است کافران هنگامی که آیات قرآن را می‌شنوند با چشم‌زخم خود تو را از بین ببرند، و می‌گویند: «او دیوانه است!»'),
(5323, 5323, 'وَمَا هُوَ إِلَّا ذِكْرٌ لِلْعَالَمِينَ', 68, 52, 29, 113, 566, 'در حالی که این (قرآن) جز مایه بیداری برای جهانیان نیست!'),
(5324, 5324, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ الْحَاقَّةُ', 69, 1, 29, 114, 566, '(روز رستاخیز) روزی است که مسلّماً واقع می‌شود!'),
(5325, 5325, 'مَا الْحَاقَّةُ', 69, 2, 29, 114, 566, 'چه روز واقع شدنی!'),
(5326, 5326, 'وَمَا أَدْرَاكَ مَا الْحَاقَّةُ', 69, 3, 29, 114, 566, 'و تو چه می‌دانی آن روز واقع شدنی چیست؟!'),
(5327, 5327, 'كَذَّبَتْ ثَمُودُ وَعَادٌ بِالْقَارِعَةِ', 69, 4, 29, 114, 566, 'قوم «ثمود» و «عاد» عذاب کوبنده الهی را انکار کردند (و نتیجه شومش را دیدند)!'),
(5328, 5328, 'فَأَمَّا ثَمُودُ فَأُهْلِكُوا بِالطَّاغِيَةِ', 69, 5, 29, 114, 566, 'امّا قوم «ثمود» با عذابی سرکش هلاک شدند!'),
(5329, 5329, 'وَأَمَّا عَادٌ فَأُهْلِكُوا بِرِيحٍ صَرْصَرٍ عَاتِيَةٍ', 69, 6, 29, 114, 566, 'و امّا قوم «عاد» با تندبادی طغیانگر و سرد و پرصدا به هلاکت رسیدند،'),
(5330, 5330, 'سَخَّرَهَا عَلَيْهِمْ سَبْعَ لَيَالٍ وَثَمَانِيَةَ أَيَّامٍ حُسُومًا فَتَرَى الْقَوْمَ فِيهَا صَرْعَىٰ كَأَنَّهُمْ أَعْجَازُ نَخْلٍ خَاوِيَةٍ', 69, 7, 29, 114, 566, '(خداوند) این تندباد بنیان‌کن را هفت شب و هشت روز پی در پی بر آنها مسلّط ساخت، (و اگر آنجا بودی) می‌دیدی که آن قوم همچون تنه‌های پوسیده و تو خالی درختان نخل در میان این تند باد روی زمین افتاده و هلاک شده‌اند!'),
(5331, 5331, 'فَهَلْ تَرَىٰ لَهُمْ مِنْ بَاقِيَةٍ', 69, 8, 29, 114, 566, 'آیا کسی از آنها را باقی می‌بینی؟!'),
(5332, 5332, 'وَجَاءَ فِرْعَوْنُ وَمَنْ قَبْلَهُ وَالْمُؤْتَفِكَاتُ بِالْخَاطِئَةِ', 69, 9, 29, 114, 567, 'و فرعون و کسانی که پیش از او بودند و همچنین اهل شهرهای زیر و رو شده =قوم لوط مرتکب گناهان بزرگ شدند،'),
(5333, 5333, 'فَعَصَوْا رَسُولَ رَبِّهِمْ فَأَخَذَهُمْ أَخْذَةً رَابِيَةً', 69, 10, 29, 114, 567, 'و با فرستاده پروردگارشان مخالفت کردند؛ و خداوند (نیز) آنها را به عذاب شدیدی گرفتار ساخت!'),
(5334, 5334, 'إِنَّا لَمَّا طَغَى الْمَاءُ حَمَلْنَاكُمْ فِي الْجَارِيَةِ', 69, 11, 29, 114, 567, 'و هنگامی که آب طغیان کرد، ما شما را سوار بر کشتی کردیم،'),
(5335, 5335, 'لِنَجْعَلَهَا لَكُمْ تَذْكِرَةً وَتَعِيَهَا أُذُنٌ وَاعِيَةٌ', 69, 12, 29, 114, 567, 'تا آن را وسیله تذکّری برای شما قرار دهیم و گوشهای شنوا آن را دریابد و بفهمد.'),
(5336, 5336, 'فَإِذَا نُفِخَ فِي الصُّورِ نَفْخَةٌ وَاحِدَةٌ', 69, 13, 29, 114, 567, 'به محض اینکه یک بار در «صور» دمیده شود،'),
(5337, 5337, 'وَحُمِلَتِ الْأَرْضُ وَالْجِبَالُ فَدُكَّتَا دَكَّةً وَاحِدَةً', 69, 14, 29, 114, 567, 'و زمین و کوه‌ها از جا برداشته شوند و یکباره در هم کوبیده و متلاشی گردند،'),
(5338, 5338, 'فَيَوْمَئِذٍ وَقَعَتِ الْوَاقِعَةُ', 69, 15, 29, 114, 567, 'در آن روز «واقعه عظیم» روی می‌دهد،'),
(5339, 5339, 'وَانْشَقَّتِ السَّمَاءُ فَهِيَ يَوْمَئِذٍ وَاهِيَةٌ', 69, 16, 29, 114, 567, 'و آسمان از هم می‌شکافد و سست می‌گردد و فرومی‌ریزد!'),
(5340, 5340, 'وَالْمَلَكُ عَلَىٰ أَرْجَائِهَا ۚ وَيَحْمِلُ عَرْشَ رَبِّكَ فَوْقَهُمْ يَوْمَئِذٍ ثَمَانِيَةٌ', 69, 17, 29, 114, 567, 'فرشتگان در اطراف آسمان قرارمی‌گیرند (و برای انجام مأموریتها آماده می‌شوند)؛ و آن روز عرش پروردگارت را هشت فرشته بر فراز همه آنها حمل می‌کنند!'),
(5341, 5341, 'يَوْمَئِذٍ تُعْرَضُونَ لَا تَخْفَىٰ مِنْكُمْ خَافِيَةٌ', 69, 18, 29, 114, 567, 'در آن روز همگی به پیشگاه خدا عرضه می‌شوید و چیزی از کارهای شما پنهان نمی‌ماند!'),
(5342, 5342, 'فَأَمَّا مَنْ أُوتِيَ كِتَابَهُ بِيَمِينِهِ فَيَقُولُ هَاؤُمُ اقْرَءُوا كِتَابِيَهْ', 69, 19, 29, 114, 567, 'پس کسی که نامه اعمالش را به دست راستش دهند (از شدّت شادی و مباهات) فریاد می‌زند که: «(ای اهل محشر!) نامه اعمال مرا بگیرید و بخوانید!'),
(5343, 5343, 'إِنِّي ظَنَنْتُ أَنِّي مُلَاقٍ حِسَابِيَهْ', 69, 20, 29, 114, 567, 'من یقین داشتم که (قیامتی در کار است و) به حساب اعمالم می‌رسم!»'),
(5344, 5344, 'فَهُوَ فِي عِيشَةٍ رَاضِيَةٍ', 69, 21, 29, 114, 567, 'او در یک زندگی (کاملاً) رضایتبخش قرار خواهد داشت،'),
(5345, 5345, 'فِي جَنَّةٍ عَالِيَةٍ', 69, 22, 29, 114, 567, 'در بهشتی عالی،'),
(5346, 5346, 'قُطُوفُهَا دَانِيَةٌ', 69, 23, 29, 114, 567, 'که میوه هایش در دسترس است!'),
(5347, 5347, 'كُلُوا وَاشْرَبُوا هَنِيئًا بِمَا أَسْلَفْتُمْ فِي الْأَيَّامِ الْخَالِيَةِ', 69, 24, 29, 114, 567, '(و به آنان گفته می‌شود:) بخورید و بیاشامید گوارا در برابر اعمالی که در ایّام گذشته انجام دادید!'),
(5348, 5348, 'وَأَمَّا مَنْ أُوتِيَ كِتَابَهُ بِشِمَالِهِ فَيَقُولُ يَا لَيْتَنِي لَمْ أُوتَ كِتَابِيَهْ', 69, 25, 29, 114, 567, 'امّا کسی که نامه اعمالش را به دست چپش بدهند می‌گوید: «ای کاش هرگز نامه اعمالم را به من نمی‌دادند.'),
(5349, 5349, 'وَلَمْ أَدْرِ مَا حِسَابِيَهْ', 69, 26, 29, 114, 567, 'و نمی‌دانستم حساب من چیست!'),
(5350, 5350, 'يَا لَيْتَهَا كَانَتِ الْقَاضِيَةَ', 69, 27, 29, 114, 567, 'ای کاش مرگم فرا می‌رسید!'),
(5351, 5351, 'مَا أَغْنَىٰ عَنِّي مَالِيَهْ ۜ', 69, 28, 29, 114, 567, 'مال و ثروتم هرگز مرا بی‌نیاز نکرد،'),
(5352, 5352, 'هَلَكَ عَنِّي سُلْطَانِيَهْ', 69, 29, 29, 114, 567, 'قدرت من نیز از دست رفت!»'),
(5353, 5353, 'خُذُوهُ فَغُلُّوهُ', 69, 30, 29, 114, 567, 'او را بگیرید و دربند و زنجیرش کنید!'),
(5354, 5354, 'ثُمَّ الْجَحِيمَ صَلُّوهُ', 69, 31, 29, 114, 567, 'سپس او را در دوزخ بیفکنید!'),
(5355, 5355, 'ثُمَّ فِي سِلْسِلَةٍ ذَرْعُهَا سَبْعُونَ ذِرَاعًا فَاسْلُكُوهُ', 69, 32, 29, 114, 567, 'بعد او را به زنجیری که هفتاد ذراع است ببندید؛'),
(5356, 5356, 'إِنَّهُ كَانَ لَا يُؤْمِنُ بِاللَّهِ الْعَظِيمِ', 69, 33, 29, 114, 567, 'چرا که او هرگز به خداوند بزرگ ایمان نمی‌آورد،'),
(5357, 5357, 'وَلَا يَحُضُّ عَلَىٰ طَعَامِ الْمِسْكِينِ', 69, 34, 29, 114, 567, 'و هرگز مردم را بر اطعام مستمندان تشویق نمی‌نمود؛'),
(5358, 5358, 'فَلَيْسَ لَهُ الْيَوْمَ هَاهُنَا حَمِيمٌ', 69, 35, 29, 114, 568, 'از این رو امروز هم در اینجا یار مهربانی ندارد،'),
(5359, 5359, 'وَلَا طَعَامٌ إِلَّا مِنْ غِسْلِينٍ', 69, 36, 29, 114, 568, 'و نه طعامی، جز از چرک و خون!'),
(5360, 5360, 'لَا يَأْكُلُهُ إِلَّا الْخَاطِئُونَ', 69, 37, 29, 114, 568, 'غذایی که جز خطاکاران آن را نمی‌خورند!'),
(5361, 5361, 'فَلَا أُقْسِمُ بِمَا تُبْصِرُونَ', 69, 38, 29, 114, 568, 'سوگند به آنچه می‌بینید،'),
(5362, 5362, 'وَمَا لَا تُبْصِرُونَ', 69, 39, 29, 114, 568, 'و آنچه نمی‌بینید،'),
(5363, 5363, 'إِنَّهُ لَقَوْلُ رَسُولٍ كَرِيمٍ', 69, 40, 29, 114, 568, 'که این قرآن گفتار رسول بزرگواری است،'),
(5364, 5364, 'وَمَا هُوَ بِقَوْلِ شَاعِرٍ ۚ قَلِيلًا مَا تُؤْمِنُونَ', 69, 41, 29, 114, 568, 'و گفته شاعری نیست، امّا کمتر ایمان می‌آورید!'),
(5365, 5365, 'وَلَا بِقَوْلِ كَاهِنٍ ۚ قَلِيلًا مَا تَذَكَّرُونَ', 69, 42, 29, 114, 568, 'و نه گفته کاهنی، هر چند کمتر متذکّر می‌شوید!'),
(5366, 5366, 'تَنْزِيلٌ مِنْ رَبِّ الْعَالَمِينَ', 69, 43, 29, 114, 568, 'کلامی است که از سوی پروردگار عالمیان نازل شده است!'),
(5367, 5367, 'وَلَوْ تَقَوَّلَ عَلَيْنَا بَعْضَ الْأَقَاوِيلِ', 69, 44, 29, 114, 568, 'اگر او سخنی دروغ بر ما می‌بست،'),
(5368, 5368, 'لَأَخَذْنَا مِنْهُ بِالْيَمِينِ', 69, 45, 29, 114, 568, 'ما او را با قدرت می‌گرفتیم،'),
(5369, 5369, 'ثُمَّ لَقَطَعْنَا مِنْهُ الْوَتِينَ', 69, 46, 29, 114, 568, 'سپس رگ قلبش را قطع می‌کردیم،'),
(5370, 5370, 'فَمَا مِنْكُمْ مِنْ أَحَدٍ عَنْهُ حَاجِزِينَ', 69, 47, 29, 114, 568, 'و هیچ کس از شما نمی‌توانست از (مجازات) او مانع شود!'),
(5371, 5371, 'وَإِنَّهُ لَتَذْكِرَةٌ لِلْمُتَّقِينَ', 69, 48, 29, 114, 568, 'و آن مسلّماً تذکری برای پرهیزگاران است!'),
(5372, 5372, 'وَإِنَّا لَنَعْلَمُ أَنَّ مِنْكُمْ مُكَذِّبِينَ', 69, 49, 29, 114, 568, 'و ما می‌دانیم که بعضی از شما (آن را) تکذیب می‌کنید!'),
(5373, 5373, 'وَإِنَّهُ لَحَسْرَةٌ عَلَى الْكَافِرِينَ', 69, 50, 29, 114, 568, 'و آن مایه حسرت کافران است!'),
(5374, 5374, 'وَإِنَّهُ لَحَقُّ الْيَقِينِ', 69, 51, 29, 114, 568, 'و آن یقین خالص است!'),
(5375, 5375, 'فَسَبِّحْ بِاسْمِ رَبِّكَ الْعَظِيمِ', 69, 52, 29, 114, 568, 'حال که چنین است به نام پروردگار بزرگت تسبیح گوی!'),
(5376, 5376, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ سَأَلَ سَائِلٌ بِعَذَابٍ وَاقِعٍ', 70, 1, 29, 114, 568, 'تقاضاکننده‌ای تقاضای عذابی کرد که واقع شد!'),
(5377, 5377, 'لِلْكَافِرِينَ لَيْسَ لَهُ دَافِعٌ', 70, 2, 29, 114, 568, 'این عذاب مخصوص کافران است، و هیچ کس نمی‌تواند آن را دفع کند،'),
(5378, 5378, 'مِنَ اللَّهِ ذِي الْمَعَارِجِ', 70, 3, 29, 114, 568, 'از سوی خداوند ذی المعارج = خداوندی که فرشتگانش بر آسمانها صعود و عروج می‌کنند!'),
(5379, 5379, 'تَعْرُجُ الْمَلَائِكَةُ وَالرُّوحُ إِلَيْهِ فِي يَوْمٍ كَانَ مِقْدَارُهُ خَمْسِينَ أَلْفَ سَنَةٍ', 70, 4, 29, 114, 568, 'فرشتگان و روح = فرشته مقرّب خداوند بسوی او عروج می‌کنند در آن روزی که مقدارش پنجاه هزار سال است!'),
(5380, 5380, 'فَاصْبِرْ صَبْرًا جَمِيلًا', 70, 5, 29, 114, 568, 'پس صبر جمیل پیشه کن،'),
(5381, 5381, 'إِنَّهُمْ يَرَوْنَهُ بَعِيدًا', 70, 6, 29, 114, 568, 'زیرا آنها آن روز را دور می‌بینند،'),
(5382, 5382, 'وَنَرَاهُ قَرِيبًا', 70, 7, 29, 114, 568, 'و ما آن را نزدیک می‌بینیم!'),
(5383, 5383, 'يَوْمَ تَكُونُ السَّمَاءُ كَالْمُهْلِ', 70, 8, 29, 114, 568, 'همان روز که آسمان همچون فلز گداخته می‌شود،'),
(5384, 5384, 'وَتَكُونُ الْجِبَالُ كَالْعِهْنِ', 70, 9, 29, 114, 568, 'و کوه‌ها مانند پشم رنگین متلاشی خواهد بود،'),
(5385, 5385, 'وَلَا يَسْأَلُ حَمِيمٌ حَمِيمًا', 70, 10, 29, 114, 568, 'و هیچ دوست صمیمی سراغ دوستش را نمی‌گیرد!'),
(5386, 5386, 'يُبَصَّرُونَهُمْ ۚ يَوَدُّ الْمُجْرِمُ لَوْ يَفْتَدِي مِنْ عَذَابِ يَوْمِئِذٍ بِبَنِيهِ', 70, 11, 29, 114, 569, 'آنها را نشانشان می‌دهند (ولی هر کس گرفتار کار خویشتن است)، چنان است که گنهکار دوست می‌دارد فرزندان خود را در برابر عذاب آن روز فدا کند،'),
(5387, 5387, 'وَصَاحِبَتِهِ وَأَخِيهِ', 70, 12, 29, 114, 569, 'و همسر و برادرش را،'),
(5388, 5388, 'وَفَصِيلَتِهِ الَّتِي تُؤْوِيهِ', 70, 13, 29, 114, 569, 'و قبیله‌اش را که همیشه از او حمایت می‌کرد،'),
(5389, 5389, 'وَمَنْ فِي الْأَرْضِ جَمِيعًا ثُمَّ يُنْجِيهِ', 70, 14, 29, 114, 569, 'و همه مردم روی زمین را تا مایه نجاتش گردند؛'),
(5390, 5390, 'كَلَّا ۖ إِنَّهَا لَظَىٰ', 70, 15, 29, 114, 569, 'امّا هرگز چنین نیست (که با اینها بتوان نجات یافت، آری) شعله‌های سوزان آتش است،'),
(5391, 5391, 'نَزَّاعَةً لِلشَّوَىٰ', 70, 16, 29, 114, 569, 'دست و پا و پوست سر را می‌کند و می‌برد!'),
(5392, 5392, 'تَدْعُو مَنْ أَدْبَرَ وَتَوَلَّىٰ', 70, 17, 29, 114, 569, 'و کسانی را که به فرمان خدا پشت کردند صدا می‌زند،'),
(5393, 5393, 'وَجَمَعَ فَأَوْعَىٰ', 70, 18, 29, 114, 569, 'و (همچنین آنها که) اموال را جمع و ذخیره کردند!'),
(5394, 5394, ' إِنَّ الْإِنْسَانَ خُلِقَ هَلُوعًا', 70, 19, 29, 114, 569, 'به یقین انسان حریص و کم‌طاقت آفریده شده است،'),
(5395, 5395, 'إِذَا مَسَّهُ الشَّرُّ جَزُوعًا', 70, 20, 29, 114, 569, 'هنگامی که بدی به او رسد بیتابی می‌کند،'),
(5396, 5396, 'وَإِذَا مَسَّهُ الْخَيْرُ مَنُوعًا', 70, 21, 29, 114, 569, 'و هنگامی که خوبی به او رسد مانع دیگران می‌شود (و بخل می‌ورزد)،'),
(5397, 5397, 'إِلَّا الْمُصَلِّينَ', 70, 22, 29, 114, 569, 'مگر نمازگزاران،'),
(5398, 5398, 'الَّذِينَ هُمْ عَلَىٰ صَلَاتِهِمْ دَائِمُونَ', 70, 23, 29, 114, 569, 'آنها که نمازها را پیوسته بجا می‌آورند،'),
(5399, 5399, 'وَالَّذِينَ فِي أَمْوَالِهِمْ حَقٌّ مَعْلُومٌ', 70, 24, 29, 114, 569, 'و آنها که در اموالشان حق معلومی است...'),
(5400, 5400, 'لِلسَّائِلِ وَالْمَحْرُومِ', 70, 25, 29, 114, 569, 'برای تقاضاکننده و محروم،'),
(5401, 5401, 'وَالَّذِينَ يُصَدِّقُونَ بِيَوْمِ الدِّينِ', 70, 26, 29, 114, 569, 'و آنها که به روز جزا ایمان دارند،'),
(5402, 5402, 'وَالَّذِينَ هُمْ مِنْ عَذَابِ رَبِّهِمْ مُشْفِقُونَ', 70, 27, 29, 114, 569, 'و آنها که از عذاب پروردگارشان بیمناکند،'),
(5403, 5403, 'إِنَّ عَذَابَ رَبِّهِمْ غَيْرُ مَأْمُونٍ', 70, 28, 29, 114, 569, 'چرا که هیچ کس از عذاب پروردگارش در امان نیست،'),
(5404, 5404, 'وَالَّذِينَ هُمْ لِفُرُوجِهِمْ حَافِظُونَ', 70, 29, 29, 114, 569, 'و آنها که دامان خویش را (از بی‌عفّتی) حفظ می‌کنند،'),
(5405, 5405, 'إِلَّا عَلَىٰ أَزْوَاجِهِمْ أَوْ مَا مَلَكَتْ أَيْمَانُهُمْ فَإِنَّهُمْ غَيْرُ مَلُومِينَ', 70, 30, 29, 114, 569, 'جز با همسران و کنیزان (که در حکم همسرند آمیزش ندارند)، چرا که در بهره‌گیری از اینها مورد سرزنش نخواهند بود!'),
(5406, 5406, 'فَمَنِ ابْتَغَىٰ وَرَاءَ ذَٰلِكَ فَأُولَٰئِكَ هُمُ الْعَادُونَ', 70, 31, 29, 114, 569, 'و هر کس جز اینها را طلب کند، متجاوز است!'),
(5407, 5407, 'وَالَّذِينَ هُمْ لِأَمَانَاتِهِمْ وَعَهْدِهِمْ رَاعُونَ', 70, 32, 29, 114, 569, 'و آنها که امانتها و عهد خود را رعایت می‌کنند،'),
(5408, 5408, 'وَالَّذِينَ هُمْ بِشَهَادَاتِهِمْ قَائِمُونَ', 70, 33, 29, 114, 569, 'و آنها که با ادای شهادتشان قیام می‌نمایند،'),
(5409, 5409, 'وَالَّذِينَ هُمْ عَلَىٰ صَلَاتِهِمْ يُحَافِظُونَ', 70, 34, 29, 114, 569, 'و آنها که بر نماز مواظبت دارند،'),
(5410, 5410, 'أُولَٰئِكَ فِي جَنَّاتٍ مُكْرَمُونَ', 70, 35, 29, 114, 569, 'آنان در باغهای بهشتی (پذیرایی و) گرامی داشته می‌شوند.'),
(5411, 5411, 'فَمَالِ الَّذِينَ كَفَرُوا قِبَلَكَ مُهْطِعِينَ', 70, 36, 29, 114, 569, 'این کافران را چه می‌شود که با سرعت نزد تو می‌آیند...'),
(5412, 5412, 'عَنِ الْيَمِينِ وَعَنِ الشِّمَالِ عِزِينَ', 70, 37, 29, 114, 569, 'از راست و چپ، گروه گروه (و آرزوی بهشت دارند)!'),
(5413, 5413, 'أَيَطْمَعُ كُلُّ امْرِئٍ مِنْهُمْ أَنْ يُدْخَلَ جَنَّةَ نَعِيمٍ', 70, 38, 29, 114, 569, 'آیا هر یک از آنها (با این اعمال زشتش) طمع دارد که او را در بهشت پر نعمت الهی وارد کنند؟!'),
(5414, 5414, 'كَلَّا ۖ إِنَّا خَلَقْنَاهُمْ مِمَّا يَعْلَمُونَ', 70, 39, 29, 114, 569, 'هرگز چنین نیست؛ ما آنها را از آنچه خودشان می‌دانند آفریده‌ایم!'),
(5415, 5415, 'فَلَا أُقْسِمُ بِرَبِّ الْمَشَارِقِ وَالْمَغَارِبِ إِنَّا لَقَادِرُونَ', 70, 40, 29, 114, 570, 'سوگند به پروردگار مشرقها و مغربها که ما قادریم...'),
(5416, 5416, 'عَلَىٰ أَنْ نُبَدِّلَ خَيْرًا مِنْهُمْ وَمَا نَحْنُ بِمَسْبُوقِينَ', 70, 41, 29, 114, 570, 'که جای آنان را به کسانی بدهیم که از آنها بهترند؛ و ما هرگز مغلوب نخواهیم شد!'),
(5417, 5417, 'فَذَرْهُمْ يَخُوضُوا وَيَلْعَبُوا حَتَّىٰ يُلَاقُوا يَوْمَهُمُ الَّذِي يُوعَدُونَ', 70, 42, 29, 114, 570, 'آنان را به حال خود واگذار تا در باطل خود فروروند و بازی کنند تا زمانی که روز موعود خود را ملاقات نمایند!'),
(5418, 5418, 'يَوْمَ يَخْرُجُونَ مِنَ الْأَجْدَاثِ سِرَاعًا كَأَنَّهُمْ إِلَىٰ نُصُبٍ يُوفِضُونَ', 70, 43, 29, 114, 570, 'همان روز که از قبرها بسرعت خارج می‌شوند، گویی به سوی بتها می‌دوند...'),
(5419, 5419, 'خَاشِعَةً أَبْصَارُهُمْ تَرْهَقُهُمْ ذِلَّةٌ ۚ ذَٰلِكَ الْيَوْمُ الَّذِي كَانُوا يُوعَدُونَ', 70, 44, 29, 114, 570, 'در حالی که چشمهایشان از شرم و وحشت به زیر افتاده، و پرده‌ای از ذلّت و خواری آنها را پوشانده است! این همان روزی است که به آنها وعده داده می‌شد!'),
(5420, 5420, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ إِنَّا أَرْسَلْنَا نُوحًا إِلَىٰ قَوْمِهِ أَنْ أَنْذِرْ قَوْمَكَ مِنْ قَبْلِ أَنْ يَأْتِيَهُمْ عَذَابٌ أَلِيمٌ', 71, 1, 29, 114, 570, 'ما نوح را به سوی قومش فرستادیم و گفتیم: «قوم خود را انذار کن پیش از آنکه عذاب دردناک به سراغشان آید!»'),
(5421, 5421, 'قَالَ يَا قَوْمِ إِنِّي لَكُمْ نَذِيرٌ مُبِينٌ', 71, 2, 29, 114, 570, 'گفت: «ای قوم! من برای شما بیم‌دهنده آشکاری هستم،'),
(5422, 5422, 'أَنِ اعْبُدُوا اللَّهَ وَاتَّقُوهُ وَأَطِيعُونِ', 71, 3, 29, 114, 570, 'که خدا را پرستش کنید و از مخالفت او بپرهیزید و مرا اطاعت نمایید!'),
(5423, 5423, 'يَغْفِرْ لَكُمْ مِنْ ذُنُوبِكُمْ وَيُؤَخِّرْكُمْ إِلَىٰ أَجَلٍ مُسَمًّى ۚ إِنَّ أَجَلَ اللَّهِ إِذَا جَاءَ لَا يُؤَخَّرُ ۖ لَوْ كُنْتُمْ تَعْلَمُونَ', 71, 4, 29, 114, 570, 'اگر چنین کنید، خدا گناهانتان را می‌آمرزد و تا زمان معیّنی شما را عمر می‌دهد؛ زیرا هنگامی که اجل الهی فرا رسد، تأخیری نخواهد داشت اگر می‌دانستید!»'),
(5424, 5424, 'قَالَ رَبِّ إِنِّي دَعَوْتُ قَوْمِي لَيْلًا وَنَهَارًا', 71, 5, 29, 114, 570, '(نوح) گفت: «پروردگارا! من قوم خود را شب و روز (بسوی تو) دعوت کردم،'),
(5425, 5425, 'فَلَمْ يَزِدْهُمْ دُعَائِي إِلَّا فِرَارًا', 71, 6, 29, 114, 570, 'امّا دعوت من چیزی جز فرار از حقّ بر آنان نیفزود!'),
(5426, 5426, 'وَإِنِّي كُلَّمَا دَعَوْتُهُمْ لِتَغْفِرَ لَهُمْ جَعَلُوا أَصَابِعَهُمْ فِي آذَانِهِمْ وَاسْتَغْشَوْا ثِيَابَهُمْ وَأَصَرُّوا وَاسْتَكْبَرُوا اسْتِكْبَارًا', 71, 7, 29, 114, 570, 'و من هر زمان آنها را دعوت کردم که (ایمان بیاورند و) تو آنها را بیامرزی، انگشتان خویش را در گوشهایشان قرار داده و لباسهایشان را بر خود پیچیدند، و در مخالفت اصرار ورزیدند و به شدّت استکبار کردند!'),
(5427, 5427, 'ثُمَّ إِنِّي دَعَوْتُهُمْ جِهَارًا', 71, 8, 29, 114, 570, 'سپس من آنها را با صدای بلند (به اطاعت فرمان تو) دعوت کردم،'),
(5428, 5428, 'ثُمَّ إِنِّي أَعْلَنْتُ لَهُمْ وَأَسْرَرْتُ لَهُمْ إِسْرَارًا', 71, 9, 29, 114, 570, 'سپس آشکارا و نهان (حقیقت توحید و ایمان را) برای آنان بیان داشتم!'),
(5429, 5429, 'فَقُلْتُ اسْتَغْفِرُوا رَبَّكُمْ إِنَّهُ كَانَ غَفَّارًا', 71, 10, 29, 114, 570, 'به آنها گفتم: «از پروردگار خویش آمرزش بطلبید که او بسیار آمرزنده است...'),
(5430, 5430, 'يُرْسِلِ السَّمَاءَ عَلَيْكُمْ مِدْرَارًا', 71, 11, 29, 114, 571, 'تا بارانهای پربرکت آسمان را پی در پی بر شما فرستد،'),
(5431, 5431, 'وَيُمْدِدْكُمْ بِأَمْوَالٍ وَبَنِينَ وَيَجْعَلْ لَكُمْ جَنَّاتٍ وَيَجْعَلْ لَكُمْ أَنْهَارًا', 71, 12, 29, 114, 571, 'و شما را با اموال و فرزندان فراوان کمک کند و باغهای سرسبز و نهرهای جاری در اختیارتان قرار دهد!'),
(5432, 5432, 'مَا لَكُمْ لَا تَرْجُونَ لِلَّهِ وَقَارًا', 71, 13, 29, 114, 571, 'چرا شما برای خدا عظمت قائل نیستید؟!'),
(5433, 5433, 'وَقَدْ خَلَقَكُمْ أَطْوَارًا', 71, 14, 29, 114, 571, 'در حالی که شما را در مراحل مختلف آفرید (تا از نطفه به انسان کامل رسیدید)!'),
(5434, 5434, 'أَلَمْ تَرَوْا كَيْفَ خَلَقَ اللَّهُ سَبْعَ سَمَاوَاتٍ طِبَاقًا', 71, 15, 29, 114, 571, 'آیا نمی‌دانید چگونه خداوند هفت آسمان را یکی بالای دیگری آفریده است،'),
(5435, 5435, 'وَجَعَلَ الْقَمَرَ فِيهِنَّ نُورًا وَجَعَلَ الشَّمْسَ سِرَاجًا', 71, 16, 29, 114, 571, 'و ماه را در میان آسمانها مایه روشنایی، و خورشید را چراغ فروزانی قرار داده است؟!'),
(5436, 5436, 'وَاللَّهُ أَنْبَتَكُمْ مِنَ الْأَرْضِ نَبَاتًا', 71, 17, 29, 114, 571, 'و خداوند شما را همچون گیاهی از زمین رویانید،'),
(5437, 5437, 'ثُمَّ يُعِيدُكُمْ فِيهَا وَيُخْرِجُكُمْ إِخْرَاجًا', 71, 18, 29, 114, 571, 'سپس شما را به همان زمین بازمی‌گرداند، و بار دیگر شما را خارج می‌سازد!'),
(5438, 5438, 'وَاللَّهُ جَعَلَ لَكُمُ الْأَرْضَ بِسَاطًا', 71, 19, 29, 114, 571, 'و خداوند زمین را برای شما فرش گسترده‌ای قرار داد...'),
(5439, 5439, 'لِتَسْلُكُوا مِنْهَا سُبُلًا فِجَاجًا', 71, 20, 29, 114, 571, 'تا از راههای وسیع و دره‌های آن بگذرید (و به هر جا می‌خواهید بروید)!»'),
(5440, 5440, 'قَالَ نُوحٌ رَبِّ إِنَّهُمْ عَصَوْنِي وَاتَّبَعُوا مَنْ لَمْ يَزِدْهُ مَالُهُ وَوَلَدُهُ إِلَّا خَسَارًا', 71, 21, 29, 114, 571, 'نوح (بعد از نومیدی از هدایت آنان) گفت: «پروردگارا! آنها نافرمانی من کردند و از کسانی پیروی نمودند که اموال و فرزندانشان چیزی جز زیانکاری بر آنها نیفزوده است!'),
(5441, 5441, 'وَمَكَرُوا مَكْرًا كُبَّارًا', 71, 22, 29, 114, 571, 'و (این رهبران گمراه) مکر عظیمی به کار بردند...'),
(5442, 5442, 'وَقَالُوا لَا تَذَرُنَّ آلِهَتَكُمْ وَلَا تَذَرُنَّ وَدًّا وَلَا سُوَاعًا وَلَا يَغُوثَ وَيَعُوقَ وَنَسْرًا', 71, 23, 29, 114, 571, 'و گفتند: دست از خدایان و بتهای خود برندارید (به خصوص) بتهای «وَد»، «سواع»، «یغوث»، «یعوق» و «نسر» را رها نکنید!'),
(5443, 5443, 'وَقَدْ أَضَلُّوا كَثِيرًا ۖ وَلَا تَزِدِ الظَّالِمِينَ إِلَّا ضَلَالًا', 71, 24, 29, 114, 571, 'و آنها گروه بسیاری را گمراه کردند! خداوندا، ظالمان را جز ضلالت میفزا!»'),
(5444, 5444, 'مِمَّا خَطِيئَاتِهِمْ أُغْرِقُوا فَأُدْخِلُوا نَارًا فَلَمْ يَجِدُوا لَهُمْ مِنْ دُونِ اللَّهِ أَنْصَارًا', 71, 25, 29, 114, 571, '(آری، سرانجام) همگی بخاطر گناهانشان غرق شدند و در آتش دوزخ وارد گشتند، و جز خدا یاورانی برای خود نیافتند!'),
(5445, 5445, 'وَقَالَ نُوحٌ رَبِّ لَا تَذَرْ عَلَى الْأَرْضِ مِنَ الْكَافِرِينَ دَيَّارًا', 71, 26, 29, 114, 571, 'نوح گفت: «پروردگارا! هیچ یک از کافران را بر روی زمین باقی مگذار!'),
(5446, 5446, 'إِنَّكَ إِنْ تَذَرْهُمْ يُضِلُّوا عِبَادَكَ وَلَا يَلِدُوا إِلَّا فَاجِرًا كَفَّارًا', 71, 27, 29, 114, 571, 'چرا که اگر آنها را باقی بگذاری، بندگانت را گمراه می‌کنند و جز نسلی فاجر و کافر به وجود نمی‌آورند!'),
(5447, 5447, 'رَبِّ اغْفِرْ لِي وَلِوَالِدَيَّ وَلِمَنْ دَخَلَ بَيْتِيَ مُؤْمِنًا وَلِلْمُؤْمِنِينَ وَالْمُؤْمِنَاتِ وَلَا تَزِدِ الظَّالِمِينَ إِلَّا تَبَارًا', 71, 28, 29, 114, 571, 'پروردگارا! مرا، و پدر و مادرم و تمام کسانی را که با ایمان وارد خانه من شدند، و جمیع مردان و زنان باایمان را بیامرز؛ و ظالمان را جز هلاکت میفزا!»'),
(5448, 5448, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ قُلْ أُوحِيَ إِلَيَّ أَنَّهُ اسْتَمَعَ نَفَرٌ مِنَ الْجِنِّ فَقَالُوا إِنَّا سَمِعْنَا قُرْآنًا عَجَبًا', 72, 1, 29, 115, 572, 'بگو: به من وحی شده است که جمعی از جنّ به سخنانم گوش فراداده‌اند، سپس گفته‌اند: «ما قرآن عجیبی شنیده‌ایم...'),
(5449, 5449, 'يَهْدِي إِلَى الرُّشْدِ فَآمَنَّا بِهِ ۖ وَلَنْ نُشْرِكَ بِرَبِّنَا أَحَدًا', 72, 2, 29, 115, 572, 'که به راه راست هدایت می‌کند، پس ما به آن ایمان آورده‌ایم و هرگز کسی را شریک پروردگارمان قرارنمی‌دهیم!'),
(5450, 5450, 'وَأَنَّهُ تَعَالَىٰ جَدُّ رَبِّنَا مَا اتَّخَذَ صَاحِبَةً وَلَا وَلَدًا', 72, 3, 29, 115, 572, 'و اینکه بلند است مقام باعظمت پروردگار ما، و او هرگز برای خود همسر و فرزندی انتخاب نکرده است!'),
(5451, 5451, 'وَأَنَّهُ كَانَ يَقُولُ سَفِيهُنَا عَلَى اللَّهِ شَطَطًا', 72, 4, 29, 115, 572, 'و اینکه سفیه ما (ابلیس) درباره خداوند سخنان ناروا می‌گفت!'),
(5452, 5452, 'وَأَنَّا ظَنَنَّا أَنْ لَنْ تَقُولَ الْإِنْسُ وَالْجِنُّ عَلَى اللَّهِ كَذِبًا', 72, 5, 29, 115, 572, 'و اینکه ما گمان می‌کردیم که انس و جنّ هرگز بر خدا دروغ نمی‌بندند!'),
(5453, 5453, 'وَأَنَّهُ كَانَ رِجَالٌ مِنَ الْإِنْسِ يَعُوذُونَ بِرِجَالٍ مِنَ الْجِنِّ فَزَادُوهُمْ رَهَقًا', 72, 6, 29, 115, 572, 'و اینکه مردانی از بشر به مردانی از جنّ پناه می‌بردند، و آنها سبب افزایش گمراهی و طغیانشان می‌شدند!'),
(5454, 5454, 'وَأَنَّهُمْ ظَنُّوا كَمَا ظَنَنْتُمْ أَنْ لَنْ يَبْعَثَ اللَّهُ أَحَدًا', 72, 7, 29, 115, 572, 'و اینکه آنها گمان کردند -همان‌گونه که شما گمان می‌کردید- که خداوند هرگز کسی را (به نبوّت) مبعوث نمی‌کند!'),
(5455, 5455, 'وَأَنَّا لَمَسْنَا السَّمَاءَ فَوَجَدْنَاهَا مُلِئَتْ حَرَسًا شَدِيدًا وَشُهُبًا', 72, 8, 29, 115, 572, 'و اینکه ما آسمان را جستجو کردیم و همه را پر از محافظان قوّی و تیرهای شهاب یافتیم!'),
(5456, 5456, 'وَأَنَّا كُنَّا نَقْعُدُ مِنْهَا مَقَاعِدَ لِلسَّمْعِ ۖ فَمَنْ يَسْتَمِعِ الْآنَ يَجِدْ لَهُ شِهَابًا رَصَدًا', 72, 9, 29, 115, 572, 'و اینکه ما پیش از این به استراق سمع در آسمانها می‌نشستیم؛ امّا اکنون هر کس بخواهد استراق سمع کند، شهابی را در کمین خود می‌یابد!'),
(5457, 5457, 'وَأَنَّا لَا نَدْرِي أَشَرٌّ أُرِيدَ بِمَنْ فِي الْأَرْضِ أَمْ أَرَادَ بِهِمْ رَبُّهُمْ رَشَدًا', 72, 10, 29, 115, 572, 'و اینکه (با این اوضاع) ما نمی‌دانیم آیا اراده شرّی درباره اهل زمین شده یا پروردگارشان خواسته است آنان را هدایت کند؟!'),
(5458, 5458, 'وَأَنَّا مِنَّا الصَّالِحُونَ وَمِنَّا دُونَ ذَٰلِكَ ۖ كُنَّا طَرَائِقَ قِدَدًا', 72, 11, 29, 115, 572, 'و اینکه در میان ما، افرادی صالح و افرادی غیر صالحند؛ و ما گروه‌های متفاوتی هستیم!'),
(5459, 5459, 'وَأَنَّا ظَنَنَّا أَنْ لَنْ نُعْجِزَ اللَّهَ فِي الْأَرْضِ وَلَنْ نُعْجِزَهُ هَرَبًا', 72, 12, 29, 115, 572, 'و اینکه ما یقین داریم هرگز نمی‌توانیم بر اراده خداوند در زمین غالب شویم و نمی‌توانیم از (پنجه قدرت) او بگریزیم!'),
(5460, 5460, 'وَأَنَّا لَمَّا سَمِعْنَا الْهُدَىٰ آمَنَّا بِهِ ۖ فَمَنْ يُؤْمِنْ بِرَبِّهِ فَلَا يَخَافُ بَخْسًا وَلَا رَهَقًا', 72, 13, 29, 115, 572, 'و اینکه ما هنگامی که هدایت قرآن را شنیدیم به آن ایمان آوردیم؛ و هر کس به پروردگارش ایمان بیاورد، نه از نقصان می‌ترسد و نه از ظلم!'),
(5461, 5461, 'وَأَنَّا مِنَّا الْمُسْلِمُونَ وَمِنَّا الْقَاسِطُونَ ۖ فَمَنْ أَسْلَمَ فَأُولَٰئِكَ تَحَرَّوْا رَشَدًا', 72, 14, 29, 115, 573, 'و اینکه گروهی از ما مسلمان و گروهی ظالمند؛ هر کس اسلام را اختیار کند راه راست را برگزیده است،'),
(5462, 5462, 'وَأَمَّا الْقَاسِطُونَ فَكَانُوا لِجَهَنَّمَ حَطَبًا', 72, 15, 29, 115, 573, 'و امّا ظالمان آتشگیره و هیزم دوزخند!'),
(5463, 5463, 'وَأَنْ لَوِ اسْتَقَامُوا عَلَى الطَّرِيقَةِ لَأَسْقَيْنَاهُمْ مَاءً غَدَقًا', 72, 16, 29, 115, 573, 'و اینکه اگر آنها = جنّ و انس‌ در راه (ایمان) استقامت ورزند، با آب فراوان سیرابشان می‌کنیم!'),
(5464, 5464, 'لِنَفْتِنَهُمْ فِيهِ ۚ وَمَنْ يُعْرِضْ عَنْ ذِكْرِ رَبِّهِ يَسْلُكْهُ عَذَابًا صَعَدًا', 72, 17, 29, 115, 573, 'هدف این است که ما آنها را با این نعمت فراوان بیازماییم؛ و هر کس از یاد پروردگارش روی گرداند، او را به عذاب شدید و فزاینده‌ای گرفتار می‌سازد!'),
(5465, 5465, 'وَأَنَّ الْمَسَاجِدَ لِلَّهِ فَلَا تَدْعُوا مَعَ اللَّهِ أَحَدًا', 72, 18, 29, 115, 573, 'و اینکه مساجد از آن خداست، پس هیچ کس را با خدا نخوانید!'),
(5466, 5466, 'وَأَنَّهُ لَمَّا قَامَ عَبْدُ اللَّهِ يَدْعُوهُ كَادُوا يَكُونُونَ عَلَيْهِ لِبَدًا', 72, 19, 29, 115, 573, 'و اینکه هنگامی که بنده خدا = محمّد (ص) به عبادت برمی‌خاست و او را می‌خواند، گروهی پیرامون او بشدّت ازدحام می‌کردند!»'),
(5467, 5467, 'قُلْ إِنَّمَا أَدْعُو رَبِّي وَلَا أُشْرِكُ بِهِ أَحَدًا', 72, 20, 29, 115, 573, 'بگو: «من تنها پروردگارم را می‌خوانم و هیچ کس را شریک او قرار نمی‌دهم!»'),
(5468, 5468, 'قُلْ إِنِّي لَا أَمْلِكُ لَكُمْ ضَرًّا وَلَا رَشَدًا', 72, 21, 29, 115, 573, 'بگو: «من مالک زیان و هدایتی برای شما نیستم!»'),
(5469, 5469, 'قُلْ إِنِّي لَنْ يُجِيرَنِي مِنَ اللَّهِ أَحَدٌ وَلَنْ أَجِدَ مِنْ دُونِهِ مُلْتَحَدًا', 72, 22, 29, 115, 573, 'بگو: «(اگر من نیز بر خلاف فرمانش رفتار کنم) هیچ کس مرا در برابر او حمایت نمی‌کند و پناهگاهی جز او نمی‌یابم؛'),
(5470, 5470, 'إِلَّا بَلَاغًا مِنَ اللَّهِ وَرِسَالَاتِهِ ۚ وَمَنْ يَعْصِ اللَّهَ وَرَسُولَهُ فَإِنَّ لَهُ نَارَ جَهَنَّمَ خَالِدِينَ فِيهَا أَبَدًا', 72, 23, 29, 115, 573, 'تنها وظیفه من ابلاغ از سوی خدا و رساندن رسالات اوست؛ و هر کس نافرمانی خدا و رسولش کند، آتش دوزخ از آن اوست و جاودانه در آن می‌مانند!'),
(5471, 5471, 'حَتَّىٰ إِذَا رَأَوْا مَا يُوعَدُونَ فَسَيَعْلَمُونَ مَنْ أَضْعَفُ نَاصِرًا وَأَقَلُّ عَدَدًا', 72, 24, 29, 115, 573, '(این کار شکنی کفّار همچنان ادامه می‌یابد) تا آنچه را به آنها وعده داده شده ببینند؛ آنگاه می‌دانند چه کسی یاورش ضعیفتر و جمعیّتش کمتر است!'),
(5472, 5472, 'قُلْ إِنْ أَدْرِي أَقَرِيبٌ مَا تُوعَدُونَ أَمْ يَجْعَلُ لَهُ رَبِّي أَمَدًا', 72, 25, 29, 115, 573, 'بگو: «من نمی‌دانم آنچه به شما وعده داده شده نزدیک است یا پروردگارم زمانی برای آن قرارمی‌دهد؟!'),
(5473, 5473, 'عَالِمُ الْغَيْبِ فَلَا يُظْهِرُ عَلَىٰ غَيْبِهِ أَحَدًا', 72, 26, 29, 115, 573, 'دانای غیب اوست و هیچ کس را بر اسرار غیبش آگاه نمی‌سازد،'),
(5474, 5474, 'إِلَّا مَنِ ارْتَضَىٰ مِنْ رَسُولٍ فَإِنَّهُ يَسْلُكُ مِنْ بَيْنِ يَدَيْهِ وَمِنْ خَلْفِهِ رَصَدًا', 72, 27, 29, 115, 573, 'مگر رسولانی که آنان را برگزیده و مراقبینی از پیش رو و پشت سر برای آنها قرار می‌دهد...'),
(5475, 5475, 'لِيَعْلَمَ أَنْ قَدْ أَبْلَغُوا رِسَالَاتِ رَبِّهِمْ وَأَحَاطَ بِمَا لَدَيْهِمْ وَأَحْصَىٰ كُلَّ شَيْءٍ عَدَدًا', 72, 28, 29, 115, 573, 'تا بداند پیامبرانش رسالتهای پروردگارشان را ابلاغ کرده‌اند؛ و او به آنچه نزد آنهاست احاطه دارد و همه چیز را احصاء کرده است!»'),
(5476, 5476, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ يَا أَيُّهَا الْمُزَّمِّلُ', 73, 1, 29, 115, 574, 'ای جامه به خود پیچیده!'),
(5477, 5477, 'قُمِ اللَّيْلَ إِلَّا قَلِيلًا', 73, 2, 29, 115, 574, 'شب را، جز کمی، بپاخیز!'),
(5478, 5478, 'نِصْفَهُ أَوِ انْقُصْ مِنْهُ قَلِيلًا', 73, 3, 29, 115, 574, 'نیمی از شب را، یا کمی از آن کم کن،'),
(5479, 5479, 'أَوْ زِدْ عَلَيْهِ وَرَتِّلِ الْقُرْآنَ تَرْتِيلًا', 73, 4, 29, 115, 574, 'یا بر نصف آن بیفزا، و قرآن را با دقّت و تأمّل بخوان؛'),
(5480, 5480, 'إِنَّا سَنُلْقِي عَلَيْكَ قَوْلًا ثَقِيلًا', 73, 5, 29, 115, 574, 'چرا که ما بزودی سخنی سنگین به تو القا خواهیم کرد!'),
(5481, 5481, 'إِنَّ نَاشِئَةَ اللَّيْلِ هِيَ أَشَدُّ وَطْئًا وَأَقْوَمُ قِيلًا', 73, 6, 29, 115, 574, 'مسلّماً نماز و عبادت شبانه پابرجاتر و با استقامت‌تر است!'),
(5482, 5482, 'إِنَّ لَكَ فِي النَّهَارِ سَبْحًا طَوِيلًا', 73, 7, 29, 115, 574, 'و تو در روز تلاش مستمر و طولانی خواهی داشت!'),
(5483, 5483, 'وَاذْكُرِ اسْمَ رَبِّكَ وَتَبَتَّلْ إِلَيْهِ تَبْتِيلًا', 73, 8, 29, 115, 574, 'و نام پروردگارت را یاد کن و تنها به او دل ببند!'),
(5484, 5484, 'رَبُّ الْمَشْرِقِ وَالْمَغْرِبِ لَا إِلَٰهَ إِلَّا هُوَ فَاتَّخِذْهُ وَكِيلًا', 73, 9, 29, 115, 574, 'همان پروردگار شرق و غرب که معبودی جز او نیست، او را نگاهبان و وکیل خود انتخاب کن،'),
(5485, 5485, 'وَاصْبِرْ عَلَىٰ مَا يَقُولُونَ وَاهْجُرْهُمْ هَجْرًا جَمِيلًا', 73, 10, 29, 115, 574, 'و در برابر آنچه (دشمنان) می‌گویند شکیبا باش و بطرزی شایسته از آنان دوری گزین!'),
(5486, 5486, 'وَذَرْنِي وَالْمُكَذِّبِينَ أُولِي النَّعْمَةِ وَمَهِّلْهُمْ قَلِيلًا', 73, 11, 29, 115, 574, 'و مرا با تکذیب‌کنندگان صاحب نعمت واگذار، و آنها را کمی مهلت ده،'),
(5487, 5487, 'إِنَّ لَدَيْنَا أَنْكَالًا وَجَحِيمًا', 73, 12, 29, 115, 574, 'که نزد ما غل و زنجیرها و (آتش) دوزخ است،'),
(5488, 5488, 'وَطَعَامًا ذَا غُصَّةٍ وَعَذَابًا أَلِيمًا', 73, 13, 29, 115, 574, 'و غذایی گلوگیر، و عذابی دردناک،'),
(5489, 5489, 'يَوْمَ تَرْجُفُ الْأَرْضُ وَالْجِبَالُ وَكَانَتِ الْجِبَالُ كَثِيبًا مَهِيلًا', 73, 14, 29, 115, 574, 'در آن روز که زمین و کوه‌ها سخت به لرزه درمی‌آید، و کوه‌ها (چنان درهم کوبیده می‌شود که) به شکل توده‌هایی از شن نرم درمی‌آید!'),
(5490, 5490, 'إِنَّا أَرْسَلْنَا إِلَيْكُمْ رَسُولًا شَاهِدًا عَلَيْكُمْ كَمَا أَرْسَلْنَا إِلَىٰ فِرْعَوْنَ رَسُولًا', 73, 15, 29, 115, 574, 'ما پیامبری به سوی شما فرستادیم که گواه بر شماست، همان گونه که به سوی فرعون رسولی فرستادیم!'),
(5491, 5491, 'فَعَصَىٰ فِرْعَوْنُ الرَّسُولَ فَأَخَذْنَاهُ أَخْذًا وَبِيلًا', 73, 16, 29, 115, 574, '(ولی) فرعون به مخالفت و نافرمانی آن رسول برخاست، و ما او را سخت مجازات کردیم!'),
(5492, 5492, 'فَكَيْفَ تَتَّقُونَ إِنْ كَفَرْتُمْ يَوْمًا يَجْعَلُ الْوِلْدَانَ شِيبًا', 73, 17, 29, 115, 574, 'شما (نیز) اگر کافر شوید، چگونه خود را (از عذاب الهی) بر کنار می‌دارید؟! در آن روز که کودکان را پیر می‌کند،'),
(5493, 5493, 'السَّمَاءُ مُنْفَطِرٌ بِهِ ۚ كَانَ وَعْدُهُ مَفْعُولًا', 73, 18, 29, 115, 574, 'و آسمان از هم شکافته می‌شود، و وعده او شدنی و حتمی است.'),
(5494, 5494, 'إِنَّ هَٰذِهِ تَذْكِرَةٌ ۖ فَمَنْ شَاءَ اتَّخَذَ إِلَىٰ رَبِّهِ سَبِيلًا', 73, 19, 29, 115, 574, 'این هشدار و تذکّری است، پس هر کس بخواهد راهی به سوی پروردگارش برمی‌گزیند!'),
(5495, 5495, ' إِنَّ رَبَّكَ يَعْلَمُ أَنَّكَ تَقُومُ أَدْنَىٰ مِنْ ثُلُثَيِ اللَّيْلِ وَنِصْفَهُ وَثُلُثَهُ وَطَائِفَةٌ مِنَ الَّذِينَ مَعَكَ ۚ وَاللَّهُ يُقَدِّرُ اللَّيْلَ وَالنَّهَارَ ۚ عَلِمَ أَنْ لَنْ تُحْصُوهُ فَتَابَ عَلَيْكُمْ ۖ فَاقْرَءُوا مَا تَيَسَّرَ مِنَ الْقُرْآنِ ۚ عَلِمَ أَنْ سَيَكُونُ مِنْكُمْ مَرْضَىٰ ۙ وَآخَرُونَ يَضْرِبُونَ فِي الْأَرْضِ يَبْتَغُونَ مِنْ فَضْلِ اللَّهِ ۙ وَآخَرُونَ يُقَاتِلُونَ فِي سَبِيلِ اللَّهِ ۖ فَاقْرَءُوا مَا تَيَسَّرَ مِنْهُ ۚ وَأَقِيمُوا الصَّلَاةَ وَآتُوا الزَّكَاةَ وَأَقْرِضُوا اللَّهَ قَرْضًا حَسَنًا ۚ وَمَا تُقَدِّمُوا لِأَنْفُسِكُمْ مِنْ خَيْرٍ تَجِدُوهُ عِنْدَ اللَّهِ هُوَ خَيْرًا وَأَعْظَمَ أَجْرًا ۚ وَاسْتَغْفِرُوا اللَّهَ ۖ إِنَّ اللَّهَ غَفُورٌ رَحِيمٌ', 73, 20, 29, 115, 575, 'پروردگارت می‌داند که تو و گروهی از آنها که با تو هستند نزدیک دو سوم از شب یا نصف یا ثلث آن را به پا می‌خیزند؛ خداوند شب و روز را اندازه‌گیری می‌کند؛ او می‌داند که شما نمی‌توانید مقدار آن را (به دقّت) اندازه‌گیری کنید (برای عبادت کردن)، پس شما را بخشید؛ اکنون آنچه برای شما میسّر است قرآن بخوانید او می‌داند بزودی گروهی از شما بیمار می‌شوند، و گروهی دیگر برای به دست آوردن فضل الهی (و کسب روزی) به سفر می‌روند، و گروهی دیگر در راه خدا جهاد می‌کنند (و از تلاوت قرآن بازمی‌مانند)، پس به اندازه‌ای که برای شما ممکن است از آن تلاوت کنید و نماز را بر پا دارید و زکات بپردازید و به خدا «قرض الحسنه» دهید = در راه او انفاق نمایید و (بدانید) آنچه را از کارهای نیک برای خود از پیش می‌فرستید نزد خدا به بهترین وجه و بزرگترین پاداش خواهید یافت؛ و از خدا آمرزش بطلبید که خداوند آمرزنده و مهربان است!'),
(5496, 5496, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ يَا أَيُّهَا الْمُدَّثِّرُ', 74, 1, 29, 115, 575, 'ای جامه خواب به خود پیچیده (و در بستر آرمیده)!'),
(5497, 5497, 'قُمْ فَأَنْذِرْ', 74, 2, 29, 115, 575, 'برخیز و انذار کن (و عالمیان را بیم ده)،'),
(5498, 5498, 'وَرَبَّكَ فَكَبِّرْ', 74, 3, 29, 115, 575, 'و پروردگارت را بزرگ بشمار،'),
(5499, 5499, 'وَثِيَابَكَ فَطَهِّرْ', 74, 4, 29, 115, 575, 'و لباست را پاک کن،'),
(5500, 5500, 'وَالرُّجْزَ فَاهْجُرْ', 74, 5, 29, 115, 575, 'و از پلیدی دوری کن،'),
(5501, 5501, 'وَلَا تَمْنُنْ تَسْتَكْثِرُ', 74, 6, 29, 115, 575, 'و منّت مگذار و فزونی مطلب،'),
(5502, 5502, 'وَلِرَبِّكَ فَاصْبِرْ', 74, 7, 29, 115, 575, 'و بخاطر پروردگارت شکیبایی کن!'),
(5503, 5503, 'فَإِذَا نُقِرَ فِي النَّاقُورِ', 74, 8, 29, 115, 575, 'هنگامی که در «صور» دمیده شود،'),
(5504, 5504, 'فَذَٰلِكَ يَوْمَئِذٍ يَوْمٌ عَسِيرٌ', 74, 9, 29, 115, 575, 'آن روز، روز سختی است،'),
(5505, 5505, 'عَلَى الْكَافِرِينَ غَيْرُ يَسِيرٍ', 74, 10, 29, 115, 575, 'و برای کافران آسان نیست!'),
(5506, 5506, 'ذَرْنِي وَمَنْ خَلَقْتُ وَحِيدًا', 74, 11, 29, 115, 575, 'مرا با کسی که او را خود به تنهایی آفریده‌ام واگذار!'),
(5507, 5507, 'وَجَعَلْتُ لَهُ مَالًا مَمْدُودًا', 74, 12, 29, 115, 575, 'همان کسی که برای او مال گسترده‌ای قرار دادم،'),
(5508, 5508, 'وَبَنِينَ شُهُودًا', 74, 13, 29, 115, 575, 'و فرزندانی که همواره نزد او (و در خدمت او) هستند،'),
(5509, 5509, 'وَمَهَّدْتُ لَهُ تَمْهِيدًا', 74, 14, 29, 115, 575, 'و وسایل زندگی را از هر نظر برای وی فراهم ساختم!'),
(5510, 5510, 'ثُمَّ يَطْمَعُ أَنْ أَزِيدَ', 74, 15, 29, 115, 575, 'باز هم طمع دارد که بر او بیفزایم!'),
(5511, 5511, 'كَلَّا ۖ إِنَّهُ كَانَ لِآيَاتِنَا عَنِيدًا', 74, 16, 29, 115, 575, 'هرگز چنین نخواهد شد؛ چرا که او نسبت به آیات ما دشمنی می‌ورزد!'),
(5512, 5512, 'سَأُرْهِقُهُ صَعُودًا', 74, 17, 29, 115, 575, 'و بزودی او را مجبور می‌کنم که از قلّه زندگی بالا رود (سپس او را به زیر می‌افکنم)!'),
(5513, 5513, 'إِنَّهُ فَكَّرَ وَقَدَّرَ', 74, 18, 29, 115, 576, 'او (برای مبارزه با قرآن) اندیشه کرد و مطلب را آماده ساخت!'),
(5514, 5514, 'فَقُتِلَ كَيْفَ قَدَّرَ', 74, 19, 29, 115, 576, 'مرگ بر او باد! چگونه (برای مبارزه با حق) مطلب را آماده کرد!'),
(5515, 5515, 'ثُمَّ قُتِلَ كَيْفَ قَدَّرَ', 74, 20, 29, 115, 576, 'باز هم مرگ بر او، چگونه مطلب (و نقشه شیطانی خود را) آماده نمود!'),
(5516, 5516, 'ثُمَّ نَظَرَ', 74, 21, 29, 115, 576, 'سپس نگاهی افکند،'),
(5517, 5517, 'ثُمَّ عَبَسَ وَبَسَرَ', 74, 22, 29, 115, 576, 'بعد چهره درهم کشید و عجولانه دست به کار شد؛'),
(5518, 5518, 'ثُمَّ أَدْبَرَ وَاسْتَكْبَرَ', 74, 23, 29, 115, 576, 'سپس پشت (به حقّ) کرد و تکبّر ورزید،'),
(5519, 5519, 'فَقَالَ إِنْ هَٰذَا إِلَّا سِحْرٌ يُؤْثَرُ', 74, 24, 29, 115, 576, 'و سرانجام گفت: «این (قرآن) چیزی جز افسون و سحری همچون سحرهای پیشینیان نیست!'),
(5520, 5520, 'إِنْ هَٰذَا إِلَّا قَوْلُ الْبَشَرِ', 74, 25, 29, 115, 576, 'این فقط سخن انسان است (نه گفتار خدا)!»'),
(5521, 5521, 'سَأُصْلِيهِ سَقَرَ', 74, 26, 29, 115, 576, '(امّا) بزودی او را وارد سَقَر = دوزخ‌ می‌کنم!'),
(5522, 5522, 'وَمَا أَدْرَاكَ مَا سَقَرُ', 74, 27, 29, 115, 576, 'و تو نمی‌دانی «سقر» چیست!'),
(5523, 5523, 'لَا تُبْقِي وَلَا تَذَرُ', 74, 28, 29, 115, 576, '(آتشی است که) نه چیزی را باقی می‌گذارد و نه چیزی را رها می‌سازد!'),
(5524, 5524, 'لَوَّاحَةٌ لِلْبَشَرِ', 74, 29, 29, 115, 576, 'پوست تن را بکلّی دگرگون می‌کند!'),
(5525, 5525, 'عَلَيْهَا تِسْعَةَ عَشَرَ', 74, 30, 29, 115, 576, 'نوزده نفر (از فرشتگان عذاب) بر آن گمارده شده‌اند!'),
(5526, 5526, 'وَمَا جَعَلْنَا أَصْحَابَ النَّارِ إِلَّا مَلَائِكَةً ۙ وَمَا جَعَلْنَا عِدَّتَهُمْ إِلَّا فِتْنَةً لِلَّذِينَ كَفَرُوا لِيَسْتَيْقِنَ الَّذِينَ أُوتُوا الْكِتَابَ وَيَزْدَادَ الَّذِينَ آمَنُوا إِيمَانًا ۙ وَلَا يَرْتَابَ الَّذِينَ أُوتُوا الْكِتَابَ وَالْمُؤْمِنُونَ ۙ وَلِيَقُولَ الَّذِينَ فِي قُلُوبِهِمْ مَرَضٌ وَالْكَافِرُونَ مَاذَا أَرَادَ اللَّهُ بِهَٰذَا مَثَلًا ۚ كَذَٰلِكَ يُضِلُّ اللَّهُ مَنْ يَشَاءُ وَيَهْدِي مَنْ يَشَاءُ ۚ وَمَا يَعْلَمُ جُنُودَ رَبِّكَ إِلَّا هُوَ ۚ وَمَا هِيَ إِلَّا ذِكْرَىٰ لِلْبَشَرِ', 74, 31, 29, 115, 576, 'مأموران دوزخ را فقط فرشتگان (عذاب) قرار دادیم، و تعداد آنها را جز برای آزمایش کافران معیّن نکردیم تا اهل کتاب = یهود و نصاری‌ یقین پیدا کنند و بر ایمان مؤمنان بیفزاید، و اهل کتاب و مؤمنان (در حقّانیّت این کتاب آسمانی) تردید به خود راه ندهند، و بیماردلان و کافران بگویند: «خدا از این توصیف چه منظوری دارد؟!» (آری) این گونه خداوند هر کس را بخواهد گمراه می‌سازد و هر کس را بخواهد هدایت می‌کند! و لشکریان پروردگارت را جز او کسی نمی‌داند، و این جز هشدار و تذکّری برای انسانها نیست!'),
(5527, 5527, 'كَلَّا وَالْقَمَرِ', 74, 32, 29, 115, 576, 'اینچنین نیست که آنها تصوّر می‌کنند سوگند به ماه،'),
(5528, 5528, 'وَاللَّيْلِ إِذْ أَدْبَرَ', 74, 33, 29, 115, 576, 'و به شب، هنگامی که (دامن برچیند و) پشت کند،'),
(5529, 5529, 'وَالصُّبْحِ إِذَا أَسْفَرَ', 74, 34, 29, 115, 576, 'و به صبح هنگامی که چهره بگشاید،'),
(5530, 5530, 'إِنَّهَا لَإِحْدَى الْكُبَرِ', 74, 35, 29, 115, 576, 'که آن (حوادث هولناک قیامت) از مسائل مهم است!'),
(5531, 5531, 'نَذِيرًا لِلْبَشَرِ', 74, 36, 29, 115, 576, 'هشدار و انذاری است برای همه انسانها،'),
(5532, 5532, 'لِمَنْ شَاءَ مِنْكُمْ أَنْ يَتَقَدَّمَ أَوْ يَتَأَخَّرَ', 74, 37, 29, 115, 576, 'برای کسانی از شما که می‌خواهند پیش افتند یا عقب بمانند = بسوی هدایت و نیکی پیش روند یا نروند!'),
(5533, 5533, 'كُلُّ نَفْسٍ بِمَا كَسَبَتْ رَهِينَةٌ', 74, 38, 29, 115, 576, '(آری) هر کس در گرو اعمال خویش است،'),
(5534, 5534, 'إِلَّا أَصْحَابَ الْيَمِينِ', 74, 39, 29, 115, 576, 'مگر «اصحاب یمین» (که نامه اعمالشان را به نشانه ایمان و تقوایشان به دست راستشان می‌دهند)!'),
(5535, 5535, 'فِي جَنَّاتٍ يَتَسَاءَلُونَ', 74, 40, 29, 115, 576, 'آنها در باغهای بهشتند، و سؤال می‌کنند...'),
(5536, 5536, 'عَنِ الْمُجْرِمِينَ', 74, 41, 29, 115, 576, 'از مجرمان:'),
(5537, 5537, 'مَا سَلَكَكُمْ فِي سَقَرَ', 74, 42, 29, 115, 576, 'چه چیز شما را به دوزخ وارد ساخت؟!»'),
(5538, 5538, 'قَالُوا لَمْ نَكُ مِنَ الْمُصَلِّينَ', 74, 43, 29, 115, 576, 'می‌گویند: «ما از نمازگزاران نبودیم،'),
(5539, 5539, 'وَلَمْ نَكُ نُطْعِمُ الْمِسْكِينَ', 74, 44, 29, 115, 576, 'و اطعام مستمند نمی‌کردیم،'),
(5540, 5540, 'وَكُنَّا نَخُوضُ مَعَ الْخَائِضِينَ', 74, 45, 29, 115, 576, 'و پیوسته با اهل باطل همنشین و همصدا بودیم،'),
(5541, 5541, 'وَكُنَّا نُكَذِّبُ بِيَوْمِ الدِّينِ', 74, 46, 29, 115, 576, 'و همواره روز جزا را انکار می‌کردیم،'),
(5542, 5542, 'حَتَّىٰ أَتَانَا الْيَقِينُ', 74, 47, 29, 115, 576, 'تا زمانی که مرگ ما فرا رسید!»'),
(5543, 5543, 'فَمَا تَنْفَعُهُمْ شَفَاعَةُ الشَّافِعِينَ', 74, 48, 29, 115, 577, 'از این رو شفاعت شفاعت‌کنندگان به حال آنها سودی نمی‌بخشد.'),
(5544, 5544, 'فَمَا لَهُمْ عَنِ التَّذْكِرَةِ مُعْرِضِينَ', 74, 49, 29, 115, 577, 'چرا آنها از تذکّر روی گردانند؟!'),
(5545, 5545, 'كَأَنَّهُمْ حُمُرٌ مُسْتَنْفِرَةٌ', 74, 50, 29, 115, 577, 'گویی گورخرانی رمیده‌اند،'),
(5546, 5546, 'فَرَّتْ مِنْ قَسْوَرَةٍ', 74, 51, 29, 115, 577, 'که از (مقابل) شیری فرار کرده‌اند!'),
(5547, 5547, 'بَلْ يُرِيدُ كُلُّ امْرِئٍ مِنْهُمْ أَنْ يُؤْتَىٰ صُحُفًا مُنَشَّرَةً', 74, 52, 29, 115, 577, 'بلکه هر کدام از آنها انتظار دارد نامه جداگانه‌ای (از سوی خدا) برای او فرستاده شود!'),
(5548, 5548, 'كَلَّا ۖ بَلْ لَا يَخَافُونَ الْآخِرَةَ', 74, 53, 29, 115, 577, 'چنین نیست که آنان می‌گویند، بلکه آنها از آخرت نمی‌ترسند!'),
(5549, 5549, 'كَلَّا إِنَّهُ تَذْكِرَةٌ', 74, 54, 29, 115, 577, 'چنین نیست که آنها می‌گویند، آن (قرآن) یک تذکّر و یادآوری است!'),
(5550, 5550, 'فَمَنْ شَاءَ ذَكَرَهُ', 74, 55, 29, 115, 577, 'هر کس بخواهد از آن پند می‌گیرد؛'),
(5551, 5551, 'وَمَا يَذْكُرُونَ إِلَّا أَنْ يَشَاءَ اللَّهُ ۚ هُوَ أَهْلُ التَّقْوَىٰ وَأَهْلُ الْمَغْفِرَةِ', 74, 56, 29, 115, 577, 'و هیچ کس پند نمی‌گیرد مگر اینکه خدا بخواهد؛ او اهل تقوا و اهل آمرزش است!'),
(5552, 5552, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ لَا أُقْسِمُ بِيَوْمِ الْقِيَامَةِ', 75, 1, 29, 116, 577, 'سوگند به روز قیامت،'),
(5553, 5553, 'وَلَا أُقْسِمُ بِالنَّفْسِ اللَّوَّامَةِ', 75, 2, 29, 116, 577, 'و سوگند به (نفس لوّامه و) وجدان بیدار و ملامتگر (که رستاخیز حقّ است)!'),
(5554, 5554, 'أَيَحْسَبُ الْإِنْسَانُ أَلَّنْ نَجْمَعَ عِظَامَهُ', 75, 3, 29, 116, 577, 'آیا انسان می‌پندارد که هرگز استخوانهای او را جمع نخواهیم کرد؟!'),
(5555, 5555, 'بَلَىٰ قَادِرِينَ عَلَىٰ أَنْ نُسَوِّيَ بَنَانَهُ', 75, 4, 29, 116, 577, 'آری قادریم که (حتّی خطوط سر) انگشتان او را موزون و مرتّب کنیم!'),
(5556, 5556, 'بَلْ يُرِيدُ الْإِنْسَانُ لِيَفْجُرَ أَمَامَهُ', 75, 5, 29, 116, 577, '(انسان شک در معاد ندارد) بلکه او می‌خواهد (آزاد باشد و بدون ترس از دادگاه قیامت) در تمام عمر گناه کند!'),
(5557, 5557, 'يَسْأَلُ أَيَّانَ يَوْمُ الْقِيَامَةِ', 75, 6, 29, 116, 577, '(از این رو) می‌پرسد: «قیامت کی خواهد بود»!'),
(5558, 5558, 'فَإِذَا بَرِقَ الْبَصَرُ', 75, 7, 29, 116, 577, '(بگو:) در آن هنگام که چشمها از شدّت وحشت به گردش درآید،'),
(5559, 5559, 'وَخَسَفَ الْقَمَرُ', 75, 8, 29, 116, 577, 'و ماه بی‌نور گردد،'),
(5560, 5560, 'وَجُمِعَ الشَّمْسُ وَالْقَمَرُ', 75, 9, 29, 116, 577, 'و خورشید و ماه یک جا جمع شوند،'),
(5561, 5561, 'يَقُولُ الْإِنْسَانُ يَوْمَئِذٍ أَيْنَ الْمَفَرُّ', 75, 10, 29, 116, 577, 'آن روز انسان می‌گوید: «راه فرار کجاست؟!»'),
(5562, 5562, 'كَلَّا لَا وَزَرَ', 75, 11, 29, 116, 577, 'هرگز چنین نیست، راه فرار و پناهگاهی وجود ندارد!'),
(5563, 5563, 'إِلَىٰ رَبِّكَ يَوْمَئِذٍ الْمُسْتَقَرُّ', 75, 12, 29, 116, 577, 'آن روز قرارگاه نهایی تنها بسوی پروردگار تو است؛'),
(5564, 5564, 'يُنَبَّأُ الْإِنْسَانُ يَوْمَئِذٍ بِمَا قَدَّمَ وَأَخَّرَ', 75, 13, 29, 116, 577, 'و در آن روز انسان را از تمام کارهایی که از پیش یا پس فرستاده آگاه می‌کنند!'),
(5565, 5565, 'بَلِ الْإِنْسَانُ عَلَىٰ نَفْسِهِ بَصِيرَةٌ', 75, 14, 29, 116, 577, 'بلکه انسان خودش از وضع خود آگاه است،'),
(5566, 5566, 'وَلَوْ أَلْقَىٰ مَعَاذِيرَهُ', 75, 15, 29, 116, 577, 'هر چند (در ظاهر) برای خود عذرهایی بتراشد!'),
(5567, 5567, 'لَا تُحَرِّكْ بِهِ لِسَانَكَ لِتَعْجَلَ بِهِ', 75, 16, 29, 116, 577, 'زبانت را بخاطر عجله برای خواندن آن = قرآن‌ حرکت مده،'),
(5568, 5568, 'إِنَّ عَلَيْنَا جَمْعَهُ وَقُرْآنَهُ', 75, 17, 29, 116, 577, 'چرا که جمع‌کردن و خواندن آن بر عهده ماست!'),
(5569, 5569, 'فَإِذَا قَرَأْنَاهُ فَاتَّبِعْ قُرْآنَهُ', 75, 18, 29, 116, 577, 'پس هر گاه آن را خواندیم، از خواندن آن پیروی کن!'),
(5570, 5570, 'ثُمَّ إِنَّ عَلَيْنَا بَيَانَهُ', 75, 19, 29, 116, 577, 'سپس بیان و (توضیح) آن (نیز) بر عهده ماست!'),
(5571, 5571, 'كَلَّا بَلْ تُحِبُّونَ الْعَاجِلَةَ', 75, 20, 29, 116, 578, 'چنین نیست که شما می‌پندارید (و دلایل معاد را کافی نمی‌دانید)؛ بلکه شما دنیای زودگذر را دوست دارید (و هوسرانی بی‌قید و شرط را)!'),
(5572, 5572, 'وَتَذَرُونَ الْآخِرَةَ', 75, 21, 29, 116, 578, 'و آخرت را رها می‌کنید!'),
(5573, 5573, 'وُجُوهٌ يَوْمَئِذٍ نَاضِرَةٌ', 75, 22, 29, 116, 578, '(آری) در آن روز صورتهایی شاداب و مسرور است،'),
(5574, 5574, 'إِلَىٰ رَبِّهَا نَاظِرَةٌ', 75, 23, 29, 116, 578, 'و به پروردگارش می‌نگرد!'),
(5575, 5575, 'وَوُجُوهٌ يَوْمَئِذٍ بَاسِرَةٌ', 75, 24, 29, 116, 578, 'و در آن روز صورتهایی عبوس و در هم کشیده است،'),
(5576, 5576, 'تَظُنُّ أَنْ يُفْعَلَ بِهَا فَاقِرَةٌ', 75, 25, 29, 116, 578, 'زیرا می‌داند عذابی در پیش دارد که پشت را در هم می‌شکند!'),
(5577, 5577, 'كَلَّا إِذَا بَلَغَتِ التَّرَاقِيَ', 75, 26, 29, 116, 578, 'چنین نیست (که انسان می‌پندارد! او ایمان نمی‌آورد) تا موقعی که جان به گلوگاهش رسد،'),
(5578, 5578, 'وَقِيلَ مَنْ ۜ رَاقٍ', 75, 27, 29, 116, 578, 'و گفته شود: «آیا کسی هست که (این بیمار را از مرگ) نجات دهد؟!»'),
(5579, 5579, 'وَظَنَّ أَنَّهُ الْفِرَاقُ', 75, 28, 29, 116, 578, 'و به جدائی از دنیا یقین پیدا کند،'),
(5580, 5580, 'وَالْتَفَّتِ السَّاقُ بِالسَّاقِ', 75, 29, 29, 116, 578, 'و ساق پاها (از سختی جان‌دادن) به هم بپیچد!'),
(5581, 5581, 'إِلَىٰ رَبِّكَ يَوْمَئِذٍ الْمَسَاقُ', 75, 30, 29, 116, 578, '(آری) در آن روز مسیر همه بسوی (دادگاه) پروردگارت خواهد بود!'),
(5582, 5582, 'فَلَا صَدَّقَ وَلَا صَلَّىٰ', 75, 31, 29, 116, 578, '(در آن روز گفته می‌شود:) او هرگز ایمان نیاورد و نماز نخواند،'),
(5583, 5583, 'وَلَٰكِنْ كَذَّبَ وَتَوَلَّىٰ', 75, 32, 29, 116, 578, 'بلکه تکذیب کرد و روی‌گردان شد،'),
(5584, 5584, 'ثُمَّ ذَهَبَ إِلَىٰ أَهْلِهِ يَتَمَطَّىٰ', 75, 33, 29, 116, 578, 'سپس بسوی خانواده خود بازگشت در حالی که متکبّرانه قدم برمی‌داشت!'),
(5585, 5585, 'أَوْلَىٰ لَكَ فَأَوْلَىٰ', 75, 34, 29, 116, 578, '(با این اعمال) عذاب الهی برای تو شایسته‌تر است، شایسته‌تر!'),
(5586, 5586, 'ثُمَّ أَوْلَىٰ لَكَ فَأَوْلَىٰ', 75, 35, 29, 116, 578, 'سپس عذاب الهی برای تو شایسته‌تر است، شایسته‌تر!'),
(5587, 5587, 'أَيَحْسَبُ الْإِنْسَانُ أَنْ يُتْرَكَ سُدًى', 75, 36, 29, 116, 578, 'آیا انسان گمان می‌کند بی‌هدف رها می‌شود؟!'),
(5588, 5588, 'أَلَمْ يَكُ نُطْفَةً مِنْ مَنِيٍّ يُمْنَىٰ', 75, 37, 29, 116, 578, 'آیا او نطفه‌ای از منی که در رحم ریخته می‌شود نبود؟!'),
(5589, 5589, 'ثُمَّ كَانَ عَلَقَةً فَخَلَقَ فَسَوَّىٰ', 75, 38, 29, 116, 578, 'سپس بصورت خون بسته در آمد، و خداوند او را آفرید و موزون ساخت،'),
(5590, 5590, 'فَجَعَلَ مِنْهُ الزَّوْجَيْنِ الذَّكَرَ وَالْأُنْثَىٰ', 75, 39, 29, 116, 578, 'و از او دو زوج مرد و زن آفرید!'),
(5591, 5591, 'أَلَيْسَ ذَٰلِكَ بِقَادِرٍ عَلَىٰ أَنْ يُحْيِيَ الْمَوْتَىٰ', 75, 40, 29, 116, 578, 'آیا چنین کسی قادر نیست که مردگان را زنده کند؟!'),
(5592, 5592, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ هَلْ أَتَىٰ عَلَى الْإِنْسَانِ حِينٌ مِنَ الدَّهْرِ لَمْ يَكُنْ شَيْئًا مَذْكُورًا', 76, 1, 29, 116, 578, 'آیا زمانی طولانی بر انسان گذشت که چیز قابل ذکری نبود؟!'),
(5593, 5593, 'إِنَّا خَلَقْنَا الْإِنْسَانَ مِنْ نُطْفَةٍ أَمْشَاجٍ نَبْتَلِيهِ فَجَعَلْنَاهُ سَمِيعًا بَصِيرًا', 76, 2, 29, 116, 578, 'ما انسان را از نطفه مختلطی آفریدیم، و او را می‌آزماییم؛ (بدین جهت) او را شنوا و بینا قرار دادیم!'),
(5594, 5594, 'إِنَّا هَدَيْنَاهُ السَّبِيلَ إِمَّا شَاكِرًا وَإِمَّا كَفُورًا', 76, 3, 29, 116, 578, 'ما راه را به او نشان دادیم، خواه شاکر باشد (و پذیرا گردد) یا ناسپاس!'),
(5595, 5595, 'إِنَّا أَعْتَدْنَا لِلْكَافِرِينَ سَلَاسِلَ وَأَغْلَالًا وَسَعِيرًا', 76, 4, 29, 116, 578, 'ما برای کافران، زنجیرها و غلها و شعله‌های سوزان آتش آماده کرده‌ایم!'),
(5596, 5596, 'إِنَّ الْأَبْرَارَ يَشْرَبُونَ مِنْ كَأْسٍ كَانَ مِزَاجُهَا كَافُورًا', 76, 5, 29, 116, 578, 'به یقین ابرار (و نیکان) از جامی می‌نوشند که با عطر خوشی آمیخته است،'),
(5597, 5597, 'عَيْنًا يَشْرَبُ بِهَا عِبَادُ اللَّهِ يُفَجِّرُونَهَا تَفْجِيرًا', 76, 6, 29, 116, 579, 'از چشمه‌ای که بندگان خاص خدا از آن می‌نوشند، و از هر جا بخواهند آن را جاری می‌سازند!'),
(5598, 5598, 'يُوفُونَ بِالنَّذْرِ وَيَخَافُونَ يَوْمًا كَانَ شَرُّهُ مُسْتَطِيرًا', 76, 7, 29, 116, 579, 'آنها به نذر خود وفا می‌کنند، و از روزی که شرّ و عذابش گسترده است می‌ترسند،'),
(5599, 5599, 'وَيُطْعِمُونَ الطَّعَامَ عَلَىٰ حُبِّهِ مِسْكِينًا وَيَتِيمًا وَأَسِيرًا', 76, 8, 29, 116, 579, 'و غذای (خود) را با اینکه به آن علاقه (و نیاز) دارند، به «مسکین» و «یتیم» و «اسیر» می‌دهند!'),
(5600, 5600, 'إِنَّمَا نُطْعِمُكُمْ لِوَجْهِ اللَّهِ لَا نُرِيدُ مِنْكُمْ جَزَاءً وَلَا شُكُورًا', 76, 9, 29, 116, 579, '(و می‌گویند:) ما شما را بخاطر خدا اطعام می‌کنیم، و هیچ پاداش و سپاسی از شما نمی‌خواهیم!'),
(5601, 5601, 'إِنَّا نَخَافُ مِنْ رَبِّنَا يَوْمًا عَبُوسًا قَمْطَرِيرًا', 76, 10, 29, 116, 579, 'ما از پروردگارمان خائفیم در آن روزی که عبوس و سخت است!'),
(5602, 5602, 'فَوَقَاهُمُ اللَّهُ شَرَّ ذَٰلِكَ الْيَوْمِ وَلَقَّاهُمْ نَضْرَةً وَسُرُورًا', 76, 11, 29, 116, 579, '(بخاطر این عقیده و عمل) خداوند آنان را از شرّ آن روز نگه می‌دارد و آنها را می‌پذیرد در حالی که غرق شادی و سرورند!'),
(5603, 5603, 'وَجَزَاهُمْ بِمَا صَبَرُوا جَنَّةً وَحَرِيرًا', 76, 12, 29, 116, 579, 'و در برابر صبرشان، بهشت و لباسهای حریر بهشتی را به آنها پاداش می‌دهد!'),
(5604, 5604, 'مُتَّكِئِينَ فِيهَا عَلَى الْأَرَائِكِ ۖ لَا يَرَوْنَ فِيهَا شَمْسًا وَلَا زَمْهَرِيرًا', 76, 13, 29, 116, 579, 'این در حالی است که در بهشت بر تختهای زیبا تکیه کرده‌اند، نه آفتاب را در آنجا می‌بینند و نه سرما را!'),
(5605, 5605, 'وَدَانِيَةً عَلَيْهِمْ ظِلَالُهَا وَذُلِّلَتْ قُطُوفُهَا تَذْلِيلًا', 76, 14, 29, 116, 579, 'و در حالی است که سایه‌های درختان بهشتی بر آنها فرو افتاده و چیدن میوه‌هایش بسیار آسان است!'),
(5606, 5606, 'وَيُطَافُ عَلَيْهِمْ بِآنِيَةٍ مِنْ فِضَّةٍ وَأَكْوَابٍ كَانَتْ قَوَارِيرَا', 76, 15, 29, 116, 579, 'و در گرداگرد آنها ظرفهایی سیمین و قدحهایی بلورین می‌گردانند (پر از بهترین غذاها و نوشیدنی‌ها)،'),
(5607, 5607, 'قَوَارِيرَ مِنْ فِضَّةٍ قَدَّرُوهَا تَقْدِيرًا', 76, 16, 29, 116, 579, 'ظرفهای بلورینی از نقره، که آنها را به اندازه مناسب آماده کرده‌اند!'),
(5608, 5608, 'وَيُسْقَوْنَ فِيهَا كَأْسًا كَانَ مِزَاجُهَا زَنْجَبِيلًا', 76, 17, 29, 116, 579, 'و در آنجا از جامهایی سیراب می‌شوند که لبریز از شراب طهوری آمیخته با زنجبیل است،'),
(5609, 5609, 'عَيْنًا فِيهَا تُسَمَّىٰ سَلْسَبِيلًا', 76, 18, 29, 116, 579, 'از چشمه‌ای در بهشت که نامش سلسبیل است!'),
(5610, 5610, ' وَيَطُوفُ عَلَيْهِمْ وِلْدَانٌ مُخَلَّدُونَ إِذَا رَأَيْتَهُمْ حَسِبْتَهُمْ لُؤْلُؤًا مَنْثُورًا', 76, 19, 29, 116, 579, 'و بر گردشان (برای پذیرایی) نوجوانانی جاودانی می‌گردند که هرگاه آنها را ببینی گمان می‌کنی مروارید پراکنده‌اند!'),
(5611, 5611, 'وَإِذَا رَأَيْتَ ثَمَّ رَأَيْتَ نَعِيمًا وَمُلْكًا كَبِيرًا', 76, 20, 29, 116, 579, 'و هنگامی که آنجا را ببینی نعمتها و ملک عظیمی را می‌بینی!'),
(5612, 5612, 'عَالِيَهُمْ ثِيَابُ سُنْدُسٍ خُضْرٌ وَإِسْتَبْرَقٌ ۖ وَحُلُّوا أَسَاوِرَ مِنْ فِضَّةٍ وَسَقَاهُمْ رَبُّهُمْ شَرَابًا طَهُورًا', 76, 21, 29, 116, 579, 'بر اندام آنها = بهشتیان‌ لباسهایی است از حریر نازک سبزرنگ، و از دیبای ضخیم، و با دستبندهایی از نقره آراسته‌اند، و پروردگارشان شراب طهور به آنان می‌نوشاند!'),
(5613, 5613, 'إِنَّ هَٰذَا كَانَ لَكُمْ جَزَاءً وَكَانَ سَعْيُكُمْ مَشْكُورًا', 76, 22, 29, 116, 579, 'این پاداش شماست، و سعی و تلاش شما مورد قدردانی است!'),
(5614, 5614, 'إِنَّا نَحْنُ نَزَّلْنَا عَلَيْكَ الْقُرْآنَ تَنْزِيلًا', 76, 23, 29, 116, 579, 'مسلّماً ما قرآن را بر تو نازل کردیم!'),
(5615, 5615, 'فَاصْبِرْ لِحُكْمِ رَبِّكَ وَلَا تُطِعْ مِنْهُمْ آثِمًا أَوْ كَفُورًا', 76, 24, 29, 116, 579, 'پس در (تبلیغ و اجرای) حکم پروردگارت شکیبا (و با استقامت) باش، و از هیچ گنهکار یا کافری از آنان اطاعت مکن!'),
(5616, 5616, 'وَاذْكُرِ اسْمَ رَبِّكَ بُكْرَةً وَأَصِيلًا', 76, 25, 29, 116, 579, 'و نام پروردگارت را هر صبح و شام به یاد آور!'),
(5617, 5617, 'وَمِنَ اللَّيْلِ فَاسْجُدْ لَهُ وَسَبِّحْهُ لَيْلًا طَوِيلًا', 76, 26, 29, 116, 580, 'و در شبانگاه برای او سجده کن، و مقداری طولانی از شب، او را تسبیح گوی!'),
(5618, 5618, 'إِنَّ هَٰؤُلَاءِ يُحِبُّونَ الْعَاجِلَةَ وَيَذَرُونَ وَرَاءَهُمْ يَوْمًا ثَقِيلًا', 76, 27, 29, 116, 580, 'آنها زندگی زودگذر دنیا را دوست دارند، در حالی که روز سختی را پشت سر خود رها می‌کنند!'),
(5619, 5619, 'نَحْنُ خَلَقْنَاهُمْ وَشَدَدْنَا أَسْرَهُمْ ۖ وَإِذَا شِئْنَا بَدَّلْنَا أَمْثَالَهُمْ تَبْدِيلًا', 76, 28, 29, 116, 580, 'ما آنها را آفریدیم و پیوندهای وجودشان را محکم کردیم، و هر زمان بخواهیم جای آنان را به گروه دیگری می‌دهیم!'),
(5620, 5620, 'إِنَّ هَٰذِهِ تَذْكِرَةٌ ۖ فَمَنْ شَاءَ اتَّخَذَ إِلَىٰ رَبِّهِ سَبِيلًا', 76, 29, 29, 116, 580, 'این یک تذکر و یادآوری است، و هر کس بخواهد (با استفاده از آن) راهی به سوی پروردگارش برمی‌گزیند!'),
(5621, 5621, 'وَمَا تَشَاءُونَ إِلَّا أَنْ يَشَاءَ اللَّهُ ۚ إِنَّ اللَّهَ كَانَ عَلِيمًا حَكِيمًا', 76, 30, 29, 116, 580, 'و شما هیچ چیز را نمی‌خواهید مگر اینکه خدا بخواهد، خداوند دانا و حکیم بوده و هست!'),
(5622, 5622, 'يُدْخِلُ مَنْ يَشَاءُ فِي رَحْمَتِهِ ۚ وَالظَّالِمِينَ أَعَدَّ لَهُمْ عَذَابًا أَلِيمًا', 76, 31, 29, 116, 580, 'و هر کس را بخواهد (و شایسته بداند) در رحمت (وسیع) خود وارد می‌کند، و برای ظالمان عذاب دردناکی آماده ساخته است!'),
(5623, 5623, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ وَالْمُرْسَلَاتِ عُرْفًا', 77, 1, 29, 116, 580, 'سوگند به فرشتگانی که پی در پی فرستاده می‌شوند،'),
(5624, 5624, 'فَالْعَاصِفَاتِ عَصْفًا', 77, 2, 29, 116, 580, 'و آنها که همچون تند باد حرکت می‌کنند،'),
(5625, 5625, 'وَالنَّاشِرَاتِ نَشْرًا', 77, 3, 29, 116, 580, 'و سوگند به آنها که (ابرها را) می‌گسترانند،'),
(5626, 5626, 'فَالْفَارِقَاتِ فَرْقًا', 77, 4, 29, 116, 580, 'و آنها که جدا می‌کنند،'),
(5627, 5627, 'فَالْمُلْقِيَاتِ ذِكْرًا', 77, 5, 29, 116, 580, 'و سوگند به آنها که آیات بیدارگر (الهی) را (به انبیا) القا می‌نمایند،');
INSERT INTO `Quran` (`rowid`, `id`, `Arabic`, `Chapter`, `Verse`, `Juz`, `Hizb`, `Page`, `tr`) VALUES
(5628, 5628, 'عُذْرًا أَوْ نُذْرًا', 77, 6, 29, 116, 580, 'برای اتمام حجّت یا برای انذار،'),
(5629, 5629, 'إِنَّمَا تُوعَدُونَ لَوَاقِعٌ', 77, 7, 29, 116, 580, 'که آنچه به شما (درباره قیامت) وعده داده می‌شود، یقیناً واقع‌شدنی است!'),
(5630, 5630, 'فَإِذَا النُّجُومُ طُمِسَتْ', 77, 8, 29, 116, 580, 'در آن هنگام که ستارگان محو و تاریک شوند،'),
(5631, 5631, 'وَإِذَا السَّمَاءُ فُرِجَتْ', 77, 9, 29, 116, 580, 'و (کرات) آسمان از هم بشکافند،'),
(5632, 5632, 'وَإِذَا الْجِبَالُ نُسِفَتْ', 77, 10, 29, 116, 580, 'و در آن زمان که کوه‌ها از جا کنده شوند،'),
(5633, 5633, 'وَإِذَا الرُّسُلُ أُقِّتَتْ', 77, 11, 29, 116, 580, 'و در آن هنگام که برای پیامبران (بمنظور ادای شهادت) تعیین وقت شود!'),
(5634, 5634, 'لِأَيِّ يَوْمٍ أُجِّلَتْ', 77, 12, 29, 116, 580, '(این امر) برای چه روزی به تأخیر افتاده؟'),
(5635, 5635, 'لِيَوْمِ الْفَصْلِ', 77, 13, 29, 116, 580, 'برای روز جدایی (حق از باطل)!'),
(5636, 5636, 'وَمَا أَدْرَاكَ مَا يَوْمُ الْفَصْلِ', 77, 14, 29, 116, 580, 'تو چه می‌دانی روز جدایی چیست!'),
(5637, 5637, 'وَيْلٌ يَوْمَئِذٍ لِلْمُكَذِّبِينَ', 77, 15, 29, 116, 580, 'وای در آن روز بر تکذیب‌کنندگان!'),
(5638, 5638, 'أَلَمْ نُهْلِكِ الْأَوَّلِينَ', 77, 16, 29, 116, 580, 'آیا ما اقوام (مجرم) نخستین را هلاک نکردیم؟!'),
(5639, 5639, 'ثُمَّ نُتْبِعُهُمُ الْآخِرِينَ', 77, 17, 29, 116, 580, 'سپس دیگر (مجرمان) را به دنبال آنها می‌فرستیم!'),
(5640, 5640, 'كَذَٰلِكَ نَفْعَلُ بِالْمُجْرِمِينَ', 77, 18, 29, 116, 580, '(آری) این گونه با مجرمان رفتار می‌کنیم!'),
(5641, 5641, 'وَيْلٌ يَوْمَئِذٍ لِلْمُكَذِّبِينَ', 77, 19, 29, 116, 580, 'وای در آن روز بر تکذیب‌کنندگان!'),
(5642, 5642, 'أَلَمْ نَخْلُقْكُمْ مِنْ مَاءٍ مَهِينٍ', 77, 20, 29, 116, 581, 'آیا شما را از آبی پست و ناچیز نیافریدیم،'),
(5643, 5643, 'فَجَعَلْنَاهُ فِي قَرَارٍ مَكِينٍ', 77, 21, 29, 116, 581, 'سپس آن را در قرارگاهی محفوظ و آماده قرار دادیم،'),
(5644, 5644, 'إِلَىٰ قَدَرٍ مَعْلُومٍ', 77, 22, 29, 116, 581, 'تا مدّتی معیّن؟!'),
(5645, 5645, 'فَقَدَرْنَا فَنِعْمَ الْقَادِرُونَ', 77, 23, 29, 116, 581, 'ما قدرت بر این کار داشتیم، پس ما قدرتمند خوبی هستیم (و امر معاد برای ما آسان است)!'),
(5646, 5646, 'وَيْلٌ يَوْمَئِذٍ لِلْمُكَذِّبِينَ', 77, 24, 29, 116, 581, 'وای در آن روز بر تکذیب‌کنندگان!'),
(5647, 5647, 'أَلَمْ نَجْعَلِ الْأَرْضَ كِفَاتًا', 77, 25, 29, 116, 581, 'آیا زمین را مرکز اجتماع انسانها قرار ندادیم،'),
(5648, 5648, 'أَحْيَاءً وَأَمْوَاتًا', 77, 26, 29, 116, 581, 'هم در حال حیاتشان و هم مرگشان؟!'),
(5649, 5649, 'وَجَعَلْنَا فِيهَا رَوَاسِيَ شَامِخَاتٍ وَأَسْقَيْنَاكُمْ مَاءً فُرَاتًا', 77, 27, 29, 116, 581, 'و در آن کوه‌های استوار و بلندی قرار دادیم، و آبی گوارا به شما نوشاندیم!'),
(5650, 5650, 'وَيْلٌ يَوْمَئِذٍ لِلْمُكَذِّبِينَ', 77, 28, 29, 116, 581, 'وای در آن روز بر تکذیب‌کنندگان!'),
(5651, 5651, 'انْطَلِقُوا إِلَىٰ مَا كُنْتُمْ بِهِ تُكَذِّبُونَ', 77, 29, 29, 116, 581, '(در آن روز به آنها گفته می‌شود:) بی‌درنگ، به سوی همان چیزی که پیوسته آن را تکذیب می‌کردید بروید!'),
(5652, 5652, 'انْطَلِقُوا إِلَىٰ ظِلٍّ ذِي ثَلَاثِ شُعَبٍ', 77, 30, 29, 116, 581, 'بروید به سوی سایه سه شاخه (دودهای خفقان‌بار و آتش‌زا)!'),
(5653, 5653, 'لَا ظَلِيلٍ وَلَا يُغْنِي مِنَ اللَّهَبِ', 77, 31, 29, 116, 581, 'سایه‌ای که نه آرامبخش است و نه از شعله‌های آتش جلوگیری می‌کند!'),
(5654, 5654, 'إِنَّهَا تَرْمِي بِشَرَرٍ كَالْقَصْرِ', 77, 32, 29, 116, 581, 'شراره‌هایی از خود پرتاب می‌کند مانند یک کاخ!'),
(5655, 5655, 'كَأَنَّهُ جِمَالَتٌ صُفْرٌ', 77, 33, 29, 116, 581, 'گویی (در سرعت و کثرت) همچون شتران زردرنگی هستند (که به هر سو پراکنده می‌شوند)!'),
(5656, 5656, 'وَيْلٌ يَوْمَئِذٍ لِلْمُكَذِّبِينَ', 77, 34, 29, 116, 581, 'وای در آن روز بر تکذیب‌کنندگان!'),
(5657, 5657, 'هَٰذَا يَوْمُ لَا يَنْطِقُونَ', 77, 35, 29, 116, 581, 'امروز روزی است که سخن نمی‌گویند (و قادر بر دفاع از خویشتن نیستند)،'),
(5658, 5658, 'وَلَا يُؤْذَنُ لَهُمْ فَيَعْتَذِرُونَ', 77, 36, 29, 116, 581, 'و به آنها اجازه داده نمی‌شود که عذرخواهی کنند!'),
(5659, 5659, 'وَيْلٌ يَوْمَئِذٍ لِلْمُكَذِّبِينَ', 77, 37, 29, 116, 581, 'وای در آن روز بر تکذیب‌کنندگان!'),
(5660, 5660, 'هَٰذَا يَوْمُ الْفَصْلِ ۖ جَمَعْنَاكُمْ وَالْأَوَّلِينَ', 77, 38, 29, 116, 581, '(و به آنها گفته می‌شود:) امروز همان روز جدایی (حق از باطل) است که شما و پیشینیان را در آن جمع کرده‌ایم!'),
(5661, 5661, 'فَإِنْ كَانَ لَكُمْ كَيْدٌ فَكِيدُونِ', 77, 39, 29, 116, 581, 'اگر چاره‌ای در برابر من (برای فرار از چنگال مجازات) دارید انجام دهید!'),
(5662, 5662, 'وَيْلٌ يَوْمَئِذٍ لِلْمُكَذِّبِينَ', 77, 40, 29, 116, 581, 'وای در آن روز بر تکذیب‌کنندگان!'),
(5663, 5663, 'إِنَّ الْمُتَّقِينَ فِي ظِلَالٍ وَعُيُونٍ', 77, 41, 29, 116, 581, '(در آن روز) پرهیزگاران در سایه‌های (درختان بهشتی) و در میان چشمه‌ها قرار دارند،'),
(5664, 5664, 'وَفَوَاكِهَ مِمَّا يَشْتَهُونَ', 77, 42, 29, 116, 581, 'و میوه‌هایی از آنچه مایل باشند!'),
(5665, 5665, 'كُلُوا وَاشْرَبُوا هَنِيئًا بِمَا كُنْتُمْ تَعْمَلُونَ', 77, 43, 29, 116, 581, 'بخورید و بنوشید گوارا، اینها در برابر اعمالی است که انجام می‌دادید!'),
(5666, 5666, 'إِنَّا كَذَٰلِكَ نَجْزِي الْمُحْسِنِينَ', 77, 44, 29, 116, 581, 'ما این گونه نیکوکاران را پاداش می‌دهیم!'),
(5667, 5667, 'وَيْلٌ يَوْمَئِذٍ لِلْمُكَذِّبِينَ', 77, 45, 29, 116, 581, 'وای در آن روز بر تکذیب‌کنندگان!'),
(5668, 5668, 'كُلُوا وَتَمَتَّعُوا قَلِيلًا إِنَّكُمْ مُجْرِمُونَ', 77, 46, 29, 116, 581, '(و به مجرمان بگو:) بخورید و بهره گیرید در این مدت کم (از زندگی دنیا، ولی بدانید عذاب الهی در انتظار شماست) چرا که شما مجرمید!'),
(5669, 5669, 'وَيْلٌ يَوْمَئِذٍ لِلْمُكَذِّبِينَ', 77, 47, 29, 116, 581, 'وای در آن روز بر تکذیب‌کنندگان!'),
(5670, 5670, 'وَإِذَا قِيلَ لَهُمُ ارْكَعُوا لَا يَرْكَعُونَ', 77, 48, 29, 116, 581, 'و هنگامی که به آنها گفته شود رکوع کنید رکوع نمی‌کنند!'),
(5671, 5671, 'وَيْلٌ يَوْمَئِذٍ لِلْمُكَذِّبِينَ', 77, 49, 29, 116, 581, 'وای در آن روز بر تکذیب‌کنندگان!'),
(5672, 5672, 'فَبِأَيِّ حَدِيثٍ بَعْدَهُ يُؤْمِنُونَ', 77, 50, 29, 116, 581, '(و اگر آنها به این قرآن ایمان نمی‌آورند) پس به کدام سخن بعد از آن ایمان می‌آورند؟!'),
(5673, 5673, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ عَمَّ يَتَسَاءَلُونَ', 78, 1, 30, 117, 582, 'آنها از چه چیز از یکدیگر سؤال می‌کنند؟!'),
(5674, 5674, 'عَنِ النَّبَإِ الْعَظِيمِ', 78, 2, 30, 117, 582, 'از خبر بزرگ و پراهمیّت (رستاخیز)!'),
(5675, 5675, 'الَّذِي هُمْ فِيهِ مُخْتَلِفُونَ', 78, 3, 30, 117, 582, 'همان خبری که پیوسته در آن اختلاف دارند!'),
(5676, 5676, 'كَلَّا سَيَعْلَمُونَ', 78, 4, 30, 117, 582, 'چنین نیست که آنها فکر می‌کنند، و بزودی می‌فهمند!'),
(5677, 5677, 'ثُمَّ كَلَّا سَيَعْلَمُونَ', 78, 5, 30, 117, 582, 'باز هم چنین نیست که آنها می‌پندارند، و بزودی می‌فهمند (که قیامت حق است)!'),
(5678, 5678, 'أَلَمْ نَجْعَلِ الْأَرْضَ مِهَادًا', 78, 6, 30, 117, 582, 'آیا زمین را محل آرامش (شما) قرار ندادیم؟!'),
(5679, 5679, 'وَالْجِبَالَ أَوْتَادًا', 78, 7, 30, 117, 582, 'و کوه‌ها را میخهای زمین؟!'),
(5680, 5680, 'وَخَلَقْنَاكُمْ أَزْوَاجًا', 78, 8, 30, 117, 582, 'و شما را بصورت زوجها آفریدیم!'),
(5681, 5681, 'وَجَعَلْنَا نَوْمَكُمْ سُبَاتًا', 78, 9, 30, 117, 582, 'و خواب شما را مایه آرامشتان قرار دادیم،'),
(5682, 5682, 'وَجَعَلْنَا اللَّيْلَ لِبَاسًا', 78, 10, 30, 117, 582, 'و شب را پوششی (برای شما)،'),
(5683, 5683, 'وَجَعَلْنَا النَّهَارَ مَعَاشًا', 78, 11, 30, 117, 582, 'و روز را وسیله‌ای برای زندگی و معاش!'),
(5684, 5684, 'وَبَنَيْنَا فَوْقَكُمْ سَبْعًا شِدَادًا', 78, 12, 30, 117, 582, 'و بر فراز شما هفت (آسمان) محکم بنا کردیم!'),
(5685, 5685, 'وَجَعَلْنَا سِرَاجًا وَهَّاجًا', 78, 13, 30, 117, 582, 'و چراغی روشن و حرارت‌بخش آفریدیم!'),
(5686, 5686, 'وَأَنْزَلْنَا مِنَ الْمُعْصِرَاتِ مَاءً ثَجَّاجًا', 78, 14, 30, 117, 582, 'و از ابرهای باران‌زا آبی فراوان نازل کردیم،'),
(5687, 5687, 'لِنُخْرِجَ بِهِ حَبًّا وَنَبَاتًا', 78, 15, 30, 117, 582, 'تا بوسیله آن دانه و گیاه بسیار برویانیم،'),
(5688, 5688, 'وَجَنَّاتٍ أَلْفَافًا', 78, 16, 30, 117, 582, 'و باغهایی پردرخت!'),
(5689, 5689, 'إِنَّ يَوْمَ الْفَصْلِ كَانَ مِيقَاتًا', 78, 17, 30, 117, 582, '(آری) روز جدایی، میعاد همگان است!'),
(5690, 5690, 'يَوْمَ يُنْفَخُ فِي الصُّورِ فَتَأْتُونَ أَفْوَاجًا', 78, 18, 30, 117, 582, 'روزی که در «صور» دمیده می‌شود و شما فوج فوج (به محشر) می‌آیید!'),
(5691, 5691, 'وَفُتِحَتِ السَّمَاءُ فَكَانَتْ أَبْوَابًا', 78, 19, 30, 117, 582, 'و آسمان گشوده می‌شود و بصورت درهای متعددی درمی‌آید!'),
(5692, 5692, 'وَسُيِّرَتِ الْجِبَالُ فَكَانَتْ سَرَابًا', 78, 20, 30, 117, 582, 'و کوه‌ها به حرکت درمی‌آید و بصورت سرابی می‌شود!'),
(5693, 5693, 'إِنَّ جَهَنَّمَ كَانَتْ مِرْصَادًا', 78, 21, 30, 117, 582, 'مسلّماً (در آن روز) جهنّم کمینگاهی است بزرگ،'),
(5694, 5694, 'لِلطَّاغِينَ مَآبًا', 78, 22, 30, 117, 582, 'و محل بازگشتی برای طغیانگران!'),
(5695, 5695, 'لَابِثِينَ فِيهَا أَحْقَابًا', 78, 23, 30, 117, 582, 'مدّتهای طولانی در آن می‌مانند!'),
(5696, 5696, 'لَا يَذُوقُونَ فِيهَا بَرْدًا وَلَا شَرَابًا', 78, 24, 30, 117, 582, 'در آنجا نه چیز خنکی می‌چشند و نه نوشیدنی گوارایی،'),
(5697, 5697, 'إِلَّا حَمِيمًا وَغَسَّاقًا', 78, 25, 30, 117, 582, 'جز آبی سوزان و مایعی از چرک و خون!'),
(5698, 5698, 'جَزَاءً وِفَاقًا', 78, 26, 30, 117, 582, 'این مجازاتی است موافق و مناسب (اعمالشان)!'),
(5699, 5699, 'إِنَّهُمْ كَانُوا لَا يَرْجُونَ حِسَابًا', 78, 27, 30, 117, 582, 'چرا که آنها هیچ امیدی به حساب نداشتند،'),
(5700, 5700, 'وَكَذَّبُوا بِآيَاتِنَا كِذَّابًا', 78, 28, 30, 117, 582, 'و آیات ما را بکلی تکذیب کردند!'),
(5701, 5701, 'وَكُلَّ شَيْءٍ أَحْصَيْنَاهُ كِتَابًا', 78, 29, 30, 117, 582, 'و ما همه چیز را شمارش و ثبت کرده‌ایم!'),
(5702, 5702, 'فَذُوقُوا فَلَنْ نَزِيدَكُمْ إِلَّا عَذَابًا', 78, 30, 30, 117, 582, 'پس بچشید که چیزی جز عذاب بر شما نمی‌افزاییم!'),
(5703, 5703, 'إِنَّ لِلْمُتَّقِينَ مَفَازًا', 78, 31, 30, 117, 583, 'مسلّماً برای پرهیزگاران نجات و پیروزی بزرگی است:'),
(5704, 5704, 'حَدَائِقَ وَأَعْنَابًا', 78, 32, 30, 117, 583, 'باغهایی سرسبز، و انواع انگورها،'),
(5705, 5705, 'وَكَوَاعِبَ أَتْرَابًا', 78, 33, 30, 117, 583, 'و حوریانی بسیار جوان و هم‌سن و سال،'),
(5706, 5706, 'وَكَأْسًا دِهَاقًا', 78, 34, 30, 117, 583, 'و جامهایی لبریز و پیاپی (از شراب طهور)!'),
(5707, 5707, 'لَا يَسْمَعُونَ فِيهَا لَغْوًا وَلَا كِذَّابًا', 78, 35, 30, 117, 583, 'در آنجا نه سخن لغو و بیهوده‌ای می‌شنوند و نه دروغی!'),
(5708, 5708, 'جَزَاءً مِنْ رَبِّكَ عَطَاءً حِسَابًا', 78, 36, 30, 117, 583, 'این کیفری است از سوی پروردگارت و عطیه‌ای است کافی!'),
(5709, 5709, 'رَبِّ السَّمَاوَاتِ وَالْأَرْضِ وَمَا بَيْنَهُمَا الرَّحْمَٰنِ ۖ لَا يَمْلِكُونَ مِنْهُ خِطَابًا', 78, 37, 30, 117, 583, 'همان پروردگار آسمانها و زمین و آنچه در میان آن دو است، پروردگار رحمان! و (در آن روز) هیچ کس حق ندارد بی اجازه او سخنی بگوید (یا شفاعتی کند)!'),
(5710, 5710, 'يَوْمَ يَقُومُ الرُّوحُ وَالْمَلَائِكَةُ صَفًّا ۖ لَا يَتَكَلَّمُونَ إِلَّا مَنْ أَذِنَ لَهُ الرَّحْمَٰنُ وَقَالَ صَوَابًا', 78, 38, 30, 117, 583, 'روزی که «روح» و «ملائکه» در یک صف می‌ایستند و هیچ یک، جز به اذن خداوند رحمان، سخن نمی‌گویند، و (آنگاه که می‌گویند) درست می‌گویند!'),
(5711, 5711, 'ذَٰلِكَ الْيَوْمُ الْحَقُّ ۖ فَمَنْ شَاءَ اتَّخَذَ إِلَىٰ رَبِّهِ مَآبًا', 78, 39, 30, 117, 583, 'آن روز حق است؛ هر کس بخواهد راهی به سوی پروردگارش برمی‌گزیند!'),
(5712, 5712, 'إِنَّا أَنْذَرْنَاكُمْ عَذَابًا قَرِيبًا يَوْمَ يَنْظُرُ الْمَرْءُ مَا قَدَّمَتْ يَدَاهُ وَيَقُولُ الْكَافِرُ يَا لَيْتَنِي كُنْتُ تُرَابًا', 78, 40, 30, 117, 583, 'و ما شما را از عذاب نزدیکی بیم دادیم! این عذاب در روزی خواهد بود که انسان آنچه را از قبل با دستهای خود فرستاده می‌بیند، و کافر می‌گوید: «ای کاش خاک بودم (و گرفتار عذاب نمی‌شدم)!»'),
(5713, 5713, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ وَالنَّازِعَاتِ غَرْقًا', 79, 1, 30, 117, 583, 'سوگند به فرشتگانی که (جان مجرمان را بشدّت از بدنهایشان) برمی‌کشند،'),
(5714, 5714, 'وَالنَّاشِطَاتِ نَشْطًا', 79, 2, 30, 117, 583, 'و فرشتگانی که (روح مؤمنان) را با مدارا و نشاط جدا می‌سازند،'),
(5715, 5715, 'وَالسَّابِحَاتِ سَبْحًا', 79, 3, 30, 117, 583, 'و سوگند به فرشتگانی که (در اجرای فرمان الهی) با سرعت حرکت می‌کنند،'),
(5716, 5716, 'فَالسَّابِقَاتِ سَبْقًا', 79, 4, 30, 117, 583, 'و سپس بر یکدیگر سبقت می‌گیرند،'),
(5717, 5717, 'فَالْمُدَبِّرَاتِ أَمْرًا', 79, 5, 30, 117, 583, 'و آنها که امور را تدبیر می‌کنند!'),
(5718, 5718, 'يَوْمَ تَرْجُفُ الرَّاجِفَةُ', 79, 6, 30, 117, 583, 'آن روز که زلزله‌های وحشتناک همه چیز را به لرزه درمی‌آورد،'),
(5719, 5719, 'تَتْبَعُهَا الرَّادِفَةُ', 79, 7, 30, 117, 583, 'و بدنبال آن، حادثه دومین = صیحه عظیم محشر رخ می‌دهد،'),
(5720, 5720, 'قُلُوبٌ يَوْمَئِذٍ وَاجِفَةٌ', 79, 8, 30, 117, 583, 'دلهایی در آن روز سخت مضطرب است،'),
(5721, 5721, 'أَبْصَارُهَا خَاشِعَةٌ', 79, 9, 30, 117, 583, 'و چشمهای آنان از شدّت ترس فروافتاده است!'),
(5722, 5722, 'يَقُولُونَ أَإِنَّا لَمَرْدُودُونَ فِي الْحَافِرَةِ', 79, 10, 30, 117, 583, '(ولی امروز) می‌گویند: «آیا ما به زندگی مجدّد بازمی‌گردیم؟!'),
(5723, 5723, 'أَإِذَا كُنَّا عِظَامًا نَخِرَةً', 79, 11, 30, 117, 583, 'آیا هنگامی که استخوانهای پوسیده‌ای شدیم (ممکن است زنده شویم)؟!»'),
(5724, 5724, 'قَالُوا تِلْكَ إِذًا كَرَّةٌ خَاسِرَةٌ', 79, 12, 30, 117, 583, 'می‌گویند: «اگر قیامتی در کار باشد، بازگشتی است زیانبار!»'),
(5725, 5725, 'فَإِنَّمَا هِيَ زَجْرَةٌ وَاحِدَةٌ', 79, 13, 30, 117, 583, 'ولی (بدانید) این بازگشت تنها با یک صیحه عظیم است!'),
(5726, 5726, 'فَإِذَا هُمْ بِالسَّاهِرَةِ', 79, 14, 30, 117, 583, 'ناگهان همگی بر عرصه زمین ظاهر می‌گردند!'),
(5727, 5727, 'هَلْ أَتَاكَ حَدِيثُ مُوسَىٰ', 79, 15, 30, 117, 583, 'آیا داستان موسی به تو رسیده است؟!'),
(5728, 5728, 'إِذْ نَادَاهُ رَبُّهُ بِالْوَادِ الْمُقَدَّسِ طُوًى', 79, 16, 30, 117, 584, 'در آن هنگام که پروردگارش او را در سرزمین مقدّس «طوی» ندا داد (و گفت):'),
(5729, 5729, 'اذْهَبْ إِلَىٰ فِرْعَوْنَ إِنَّهُ طَغَىٰ', 79, 17, 30, 117, 584, 'به سوی فرعون برو که طغیان کرده است!'),
(5730, 5730, 'فَقُلْ هَلْ لَكَ إِلَىٰ أَنْ تَزَكَّىٰ', 79, 18, 30, 117, 584, 'و به او بگو: «آیا می‌خواهی پاکیزه شوی؟!'),
(5731, 5731, 'وَأَهْدِيَكَ إِلَىٰ رَبِّكَ فَتَخْشَىٰ', 79, 19, 30, 117, 584, 'و من تو را به سوی پروردگارت هدایت کنم تا از او بترسی (و گناه نکنی)؟!»'),
(5732, 5732, 'فَأَرَاهُ الْآيَةَ الْكُبْرَىٰ', 79, 20, 30, 117, 584, 'سپس موسی بزرگترین معجزه را به او نشان داد!'),
(5733, 5733, 'فَكَذَّبَ وَعَصَىٰ', 79, 21, 30, 117, 584, 'امّا او تکذیب و عصیان کرد!'),
(5734, 5734, 'ثُمَّ أَدْبَرَ يَسْعَىٰ', 79, 22, 30, 117, 584, 'سپس پشت کرد و پیوسته (برای محو آیین حق) تلاش نمود!'),
(5735, 5735, 'فَحَشَرَ فَنَادَىٰ', 79, 23, 30, 117, 584, 'و ساحران را جمع کرد و مردم را دعوت نمود،'),
(5736, 5736, 'فَقَالَ أَنَا رَبُّكُمُ الْأَعْلَىٰ', 79, 24, 30, 117, 584, 'و گفت: «من پروردگار برتر شما هستم!»'),
(5737, 5737, 'فَأَخَذَهُ اللَّهُ نَكَالَ الْآخِرَةِ وَالْأُولَىٰ', 79, 25, 30, 117, 584, 'از این رو خداوند او را به عذاب آخرت و دنیا گرفتار ساخت!'),
(5738, 5738, 'إِنَّ فِي ذَٰلِكَ لَعِبْرَةً لِمَنْ يَخْشَىٰ', 79, 26, 30, 117, 584, 'در این عبرتی است برای کسی که (از خدا) بترسد!'),
(5739, 5739, 'أَأَنْتُمْ أَشَدُّ خَلْقًا أَمِ السَّمَاءُ ۚ بَنَاهَا', 79, 27, 30, 117, 584, 'آیا آفرینش شما (بعد از مرگ) مشکل‌تر است یا آفرینش آسمان که خداوند آن را بنا نهاد؟!'),
(5740, 5740, 'رَفَعَ سَمْكَهَا فَسَوَّاهَا', 79, 28, 30, 117, 584, 'سقف آن را برافراشت و آن را منظّم ساخت،'),
(5741, 5741, 'وَأَغْطَشَ لَيْلَهَا وَأَخْرَجَ ضُحَاهَا', 79, 29, 30, 117, 584, 'و شبش را تاریک و روزش را آشکار نمود!'),
(5742, 5742, 'وَالْأَرْضَ بَعْدَ ذَٰلِكَ دَحَاهَا', 79, 30, 30, 117, 584, 'و زمین را بعد از آن گسترش داد،'),
(5743, 5743, 'أَخْرَجَ مِنْهَا مَاءَهَا وَمَرْعَاهَا', 79, 31, 30, 117, 584, 'و از آن آب و چراگاهش را بیرون آورد،'),
(5744, 5744, 'وَالْجِبَالَ أَرْسَاهَا', 79, 32, 30, 117, 584, 'و کوه‌ها را ثابت و محکم نمود!'),
(5745, 5745, 'مَتَاعًا لَكُمْ وَلِأَنْعَامِكُمْ', 79, 33, 30, 117, 584, 'همه اینها برای بهره‌گیری شما و چهارپایانتان است!'),
(5746, 5746, 'فَإِذَا جَاءَتِ الطَّامَّةُ الْكُبْرَىٰ', 79, 34, 30, 117, 584, 'هنگامی که آن حادثه بزرگ رخ دهد،'),
(5747, 5747, 'يَوْمَ يَتَذَكَّرُ الْإِنْسَانُ مَا سَعَىٰ', 79, 35, 30, 117, 584, 'در آن روز انسان به یاد کوششهایش می‌افتد،'),
(5748, 5748, 'وَبُرِّزَتِ الْجَحِيمُ لِمَنْ يَرَىٰ', 79, 36, 30, 117, 584, 'و جهنّم برای هر بیننده‌ای آشکار می‌گردد،'),
(5749, 5749, 'فَأَمَّا مَنْ طَغَىٰ', 79, 37, 30, 117, 584, 'امّا آن کسی که طغیان کرده،'),
(5750, 5750, 'وَآثَرَ الْحَيَاةَ الدُّنْيَا', 79, 38, 30, 117, 584, 'و زندگی دنیا را مقدّم داشته،'),
(5751, 5751, 'فَإِنَّ الْجَحِيمَ هِيَ الْمَأْوَىٰ', 79, 39, 30, 117, 584, 'مسلّماً دوزخ جایگاه اوست!'),
(5752, 5752, 'وَأَمَّا مَنْ خَافَ مَقَامَ رَبِّهِ وَنَهَى النَّفْسَ عَنِ الْهَوَىٰ', 79, 40, 30, 117, 584, 'و آن کس که از مقام پروردگارش ترسان باشد و نفس را از هوی بازدارد،'),
(5753, 5753, 'فَإِنَّ الْجَنَّةَ هِيَ الْمَأْوَىٰ', 79, 41, 30, 117, 584, 'قطعاً بهشت جایگاه اوست!'),
(5754, 5754, 'يَسْأَلُونَكَ عَنِ السَّاعَةِ أَيَّانَ مُرْسَاهَا', 79, 42, 30, 117, 584, 'و از تو درباره قیامت می‌پرسند که در چه زمانی واقع می‌شود؟!'),
(5755, 5755, 'فِيمَ أَنْتَ مِنْ ذِكْرَاهَا', 79, 43, 30, 117, 584, 'تو را با یادآوری این سخن چه کار؟!'),
(5756, 5756, 'إِلَىٰ رَبِّكَ مُنْتَهَاهَا', 79, 44, 30, 117, 584, 'نهایت آن به سوی پروردگار تو است (و هیچ کس جز خدا از زمانش آگاه نیست)!'),
(5757, 5757, 'إِنَّمَا أَنْتَ مُنْذِرُ مَنْ يَخْشَاهَا', 79, 45, 30, 117, 584, 'کار تو فقط بیم‌دادن کسانی است که از آن می‌ترسند!'),
(5758, 5758, 'كَأَنَّهُمْ يَوْمَ يَرَوْنَهَا لَمْ يَلْبَثُوا إِلَّا عَشِيَّةً أَوْ ضُحَاهَا', 79, 46, 30, 117, 584, 'آنها در آن روز که قیام قیامت را می‌بینند چنین احساس می‌کنند که گویی توقّفشان (در دنیا و برزخ) جز شامگاهی یا صبح آن بیشتر نبوده است!'),
(5759, 5759, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ عَبَسَ وَتَوَلَّىٰ', 80, 1, 30, 117, 585, 'چهره در هم کشید و روی برتافت...'),
(5760, 5760, 'أَنْ جَاءَهُ الْأَعْمَىٰ', 80, 2, 30, 117, 585, 'از اینکه نابینایی به سراغ او آمده بود!'),
(5761, 5761, 'وَمَا يُدْرِيكَ لَعَلَّهُ يَزَّكَّىٰ', 80, 3, 30, 117, 585, 'تو چه می‌دانی شاید او پاکی و تقوا پیشه کند،'),
(5762, 5762, 'أَوْ يَذَّكَّرُ فَتَنْفَعَهُ الذِّكْرَىٰ', 80, 4, 30, 117, 585, 'یا متذکّر گردد و این تذکّر به حال او مفید باشد!'),
(5763, 5763, 'أَمَّا مَنِ اسْتَغْنَىٰ', 80, 5, 30, 117, 585, 'امّا آن کس که توانگر است،'),
(5764, 5764, 'فَأَنْتَ لَهُ تَصَدَّىٰ', 80, 6, 30, 117, 585, 'تو به او روی می‌آوری،'),
(5765, 5765, 'وَمَا عَلَيْكَ أَلَّا يَزَّكَّىٰ', 80, 7, 30, 117, 585, 'در حالی که اگر او خود را پاک نسازد، چیزی بر تو نیست!'),
(5766, 5766, 'وَأَمَّا مَنْ جَاءَكَ يَسْعَىٰ', 80, 8, 30, 117, 585, 'امّا کسی که به سراغ تو می‌آید و کوشش می‌کند،'),
(5767, 5767, 'وَهُوَ يَخْشَىٰ', 80, 9, 30, 117, 585, 'و از خدا ترسان است،'),
(5768, 5768, 'فَأَنْتَ عَنْهُ تَلَهَّىٰ', 80, 10, 30, 117, 585, 'تو از او غافل می‌شوی!'),
(5769, 5769, 'كَلَّا إِنَّهَا تَذْكِرَةٌ', 80, 11, 30, 117, 585, 'هرگز چنین نیست که آنها می‌پندارند؛ این (قرآن) تذکّر و یادآوری است،'),
(5770, 5770, 'فَمَنْ شَاءَ ذَكَرَهُ', 80, 12, 30, 117, 585, 'و هر کس بخواهد از آن پند می‌گیرد!'),
(5771, 5771, 'فِي صُحُفٍ مُكَرَّمَةٍ', 80, 13, 30, 117, 585, 'در الواح پرارزشی ثبت است،'),
(5772, 5772, 'مَرْفُوعَةٍ مُطَهَّرَةٍ', 80, 14, 30, 117, 585, 'الواحی والاقدر و پاکیزه،'),
(5773, 5773, 'بِأَيْدِي سَفَرَةٍ', 80, 15, 30, 117, 585, 'به دست سفیرانی است‌'),
(5774, 5774, 'كِرَامٍ بَرَرَةٍ', 80, 16, 30, 117, 585, 'والا مقام و فرمانبردار و نیکوکار!'),
(5775, 5775, 'قُتِلَ الْإِنْسَانُ مَا أَكْفَرَهُ', 80, 17, 30, 117, 585, 'مرگ بر این انسان، چقدر کافر و ناسپاس است!'),
(5776, 5776, 'مِنْ أَيِّ شَيْءٍ خَلَقَهُ', 80, 18, 30, 117, 585, '(خداوند) او را از چه چیز آفریده است؟!'),
(5777, 5777, 'مِنْ نُطْفَةٍ خَلَقَهُ فَقَدَّرَهُ', 80, 19, 30, 117, 585, 'او را از نطفه ناچیزی آفرید، سپس اندازه‌گیری کرد و موزون ساخت،'),
(5778, 5778, 'ثُمَّ السَّبِيلَ يَسَّرَهُ', 80, 20, 30, 117, 585, 'سپس راه را برای او آسان کرد،'),
(5779, 5779, 'ثُمَّ أَمَاتَهُ فَأَقْبَرَهُ', 80, 21, 30, 117, 585, 'بعد او را میراند و در قبر پنهان نمود،'),
(5780, 5780, 'ثُمَّ إِذَا شَاءَ أَنْشَرَهُ', 80, 22, 30, 117, 585, 'سپس هرگاه بخواهد او را زنده می‌کند!'),
(5781, 5781, 'كَلَّا لَمَّا يَقْضِ مَا أَمَرَهُ', 80, 23, 30, 117, 585, 'چنین نیست که او می‌پندارد؛ او هنوز آنچه را (خدا) فرمان داده، اطاعت نکرده است!'),
(5782, 5782, 'فَلْيَنْظُرِ الْإِنْسَانُ إِلَىٰ طَعَامِهِ', 80, 24, 30, 117, 585, 'انسان باید به غذای خویش (و آفرینش آن) بنگرد!'),
(5783, 5783, 'أَنَّا صَبَبْنَا الْمَاءَ صَبًّا', 80, 25, 30, 117, 585, 'ما آب فراوان از آسمان فرو ریختیم،'),
(5784, 5784, 'ثُمَّ شَقَقْنَا الْأَرْضَ شَقًّا', 80, 26, 30, 117, 585, 'سپس زمین را از هم شکافتیم،'),
(5785, 5785, 'فَأَنْبَتْنَا فِيهَا حَبًّا', 80, 27, 30, 117, 585, 'و در آن دانه‌های فراوانی رویاندیم،'),
(5786, 5786, 'وَعِنَبًا وَقَضْبًا', 80, 28, 30, 117, 585, 'و انگور و سبزی بسیار،'),
(5787, 5787, 'وَزَيْتُونًا وَنَخْلًا', 80, 29, 30, 117, 585, 'و زیتون و نخل فراوان،'),
(5788, 5788, 'وَحَدَائِقَ غُلْبًا', 80, 30, 30, 117, 585, 'و باغهای پردرخت،'),
(5789, 5789, 'وَفَاكِهَةً وَأَبًّا', 80, 31, 30, 117, 585, 'و میوه و چراگاه،'),
(5790, 5790, 'مَتَاعًا لَكُمْ وَلِأَنْعَامِكُمْ', 80, 32, 30, 117, 585, 'تا وسیله‌ای برای بهره‌گیری شما و چهارپایانتان باشد!'),
(5791, 5791, 'فَإِذَا جَاءَتِ الصَّاخَّةُ', 80, 33, 30, 117, 585, 'هنگامی که آن صدای مهیب = صیحه رستاخیز بیاید، (کافران در اندوه عمیقی فرومی‌روند)!'),
(5792, 5792, 'يَوْمَ يَفِرُّ الْمَرْءُ مِنْ أَخِيهِ', 80, 34, 30, 117, 585, 'در آن روز که انسان از برادر خود می‌گریزد،'),
(5793, 5793, 'وَأُمِّهِ وَأَبِيهِ', 80, 35, 30, 117, 585, 'و از مادر و پدرش،'),
(5794, 5794, 'وَصَاحِبَتِهِ وَبَنِيهِ', 80, 36, 30, 117, 585, 'و زن و فرزندانش؛'),
(5795, 5795, 'لِكُلِّ امْرِئٍ مِنْهُمْ يَوْمَئِذٍ شَأْنٌ يُغْنِيهِ', 80, 37, 30, 117, 585, 'در آن روز هر کدام از آنها وضعی دارد که او را کاملاً به خود مشغول می‌سازد!'),
(5796, 5796, 'وُجُوهٌ يَوْمَئِذٍ مُسْفِرَةٌ', 80, 38, 30, 117, 585, 'چهره‌هائی در آن روز گشاده و نورانی است،'),
(5797, 5797, 'ضَاحِكَةٌ مُسْتَبْشِرَةٌ', 80, 39, 30, 117, 585, 'خندان و مسرور است؛'),
(5798, 5798, 'وَوُجُوهٌ يَوْمَئِذٍ عَلَيْهَا غَبَرَةٌ', 80, 40, 30, 117, 585, 'و صورتهایی در آن روز غبارآلود است،'),
(5799, 5799, 'تَرْهَقُهَا قَتَرَةٌ', 80, 41, 30, 117, 585, 'و دود تاریکی آنها را پوشانده است،'),
(5800, 5800, 'أُولَٰئِكَ هُمُ الْكَفَرَةُ الْفَجَرَةُ', 80, 42, 30, 117, 585, 'آنان همان کافران فاجرند!'),
(5801, 5801, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ إِذَا الشَّمْسُ كُوِّرَتْ', 81, 1, 30, 117, 586, 'در آن هنگام که خورشید در هم پیچیده شود،'),
(5802, 5802, 'وَإِذَا النُّجُومُ انْكَدَرَتْ', 81, 2, 30, 117, 586, 'و در آن هنگام که ستارگان بی‌فروغ شوند،'),
(5803, 5803, 'وَإِذَا الْجِبَالُ سُيِّرَتْ', 81, 3, 30, 117, 586, 'و در آن هنگام که کوه‌ها به حرکت درآیند،'),
(5804, 5804, 'وَإِذَا الْعِشَارُ عُطِّلَتْ', 81, 4, 30, 117, 586, 'و در آن هنگام که باارزش‌ترین اموال به دست فراموشی سپرده شود،'),
(5805, 5805, 'وَإِذَا الْوُحُوشُ حُشِرَتْ', 81, 5, 30, 117, 586, 'و در آن هنگام که وحوش جمع شوند،'),
(5806, 5806, 'وَإِذَا الْبِحَارُ سُجِّرَتْ', 81, 6, 30, 117, 586, 'و در آن هنگام که دریاها برافروخته شوند،'),
(5807, 5807, 'وَإِذَا النُّفُوسُ زُوِّجَتْ', 81, 7, 30, 117, 586, 'و در آن هنگام که هر کس با همسان خود قرین گردد،'),
(5808, 5808, 'وَإِذَا الْمَوْءُودَةُ سُئِلَتْ', 81, 8, 30, 117, 586, 'و در آن هنگام که از دختران زنده به گور شده سؤال شود:'),
(5809, 5809, 'بِأَيِّ ذَنْبٍ قُتِلَتْ', 81, 9, 30, 117, 586, 'به کدامین گناه کشته شدند؟!'),
(5810, 5810, 'وَإِذَا الصُّحُفُ نُشِرَتْ', 81, 10, 30, 117, 586, 'و در آن هنگام که نامه‌های اعمال گشوده شود،'),
(5811, 5811, 'وَإِذَا السَّمَاءُ كُشِطَتْ', 81, 11, 30, 117, 586, 'و در آن هنگام که پرده از روی آسمان برگرفته شود،'),
(5812, 5812, 'وَإِذَا الْجَحِيمُ سُعِّرَتْ', 81, 12, 30, 117, 586, 'و در آن هنگام که دوزخ شعله‌ور گردد،'),
(5813, 5813, 'وَإِذَا الْجَنَّةُ أُزْلِفَتْ', 81, 13, 30, 117, 586, 'و در آن هنگام که بهشت نزدیک شود،'),
(5814, 5814, 'عَلِمَتْ نَفْسٌ مَا أَحْضَرَتْ', 81, 14, 30, 117, 586, '(آری در آن هنگام) هر کس می‌داند چه چیزی را آماده کرده است!'),
(5815, 5815, 'فَلَا أُقْسِمُ بِالْخُنَّسِ', 81, 15, 30, 117, 586, 'سوگند به ستارگانی که بازمی‌گردند،'),
(5816, 5816, 'الْجَوَارِ الْكُنَّسِ', 81, 16, 30, 117, 586, 'حرکت می‌کنند و از دیده‌ها پنهان می‌شوند،'),
(5817, 5817, 'وَاللَّيْلِ إِذَا عَسْعَسَ', 81, 17, 30, 117, 586, 'و قسم به شب، هنگامی که پشت کند و به آخر رسد،'),
(5818, 5818, 'وَالصُّبْحِ إِذَا تَنَفَّسَ', 81, 18, 30, 117, 586, 'و به صبح، هنگامی که تنفّس کند،'),
(5819, 5819, 'إِنَّهُ لَقَوْلُ رَسُولٍ كَرِيمٍ', 81, 19, 30, 117, 586, 'که این (قرآن) کلام فرستاده بزرگواری است = جبرئیل امین‌'),
(5820, 5820, 'ذِي قُوَّةٍ عِنْدَ ذِي الْعَرْشِ مَكِينٍ', 81, 20, 30, 117, 586, 'که صاحب قدرت است و نزد (خداوند) صاحب عرش، مقام والائی دارد!'),
(5821, 5821, 'مُطَاعٍ ثَمَّ أَمِينٍ', 81, 21, 30, 117, 586, 'در آسمانها مورد اطاعت (فرشتگان) و امین است!'),
(5822, 5822, 'وَمَا صَاحِبُكُمْ بِمَجْنُونٍ', 81, 22, 30, 117, 586, 'و مصاحب شما = پیامبر دیوانه نیست!'),
(5823, 5823, 'وَلَقَدْ رَآهُ بِالْأُفُقِ الْمُبِينِ', 81, 23, 30, 117, 586, 'او (جبرئیل) را در اُفق روشن دیده است!'),
(5824, 5824, 'وَمَا هُوَ عَلَى الْغَيْبِ بِضَنِينٍ', 81, 24, 30, 117, 586, 'و او نسبت به آنچه از طریق وحی دریافت داشته بخل ندارد!'),
(5825, 5825, 'وَمَا هُوَ بِقَوْلِ شَيْطَانٍ رَجِيمٍ', 81, 25, 30, 117, 586, 'این (قرآن) گفته شیطان رجیم نیست!'),
(5826, 5826, 'فَأَيْنَ تَذْهَبُونَ', 81, 26, 30, 117, 586, 'پس به کجا می‌روید؟!'),
(5827, 5827, 'إِنْ هُوَ إِلَّا ذِكْرٌ لِلْعَالَمِينَ', 81, 27, 30, 117, 586, 'این قرآن چیزی جز تذکّری برای جهانیان نیست،'),
(5828, 5828, 'لِمَنْ شَاءَ مِنْكُمْ أَنْ يَسْتَقِيمَ', 81, 28, 30, 117, 586, 'برای کسی از شما که بخواهد راه مستقیم در پیش گیرد!'),
(5829, 5829, 'وَمَا تَشَاءُونَ إِلَّا أَنْ يَشَاءَ اللَّهُ رَبُّ الْعَالَمِينَ', 81, 29, 30, 117, 586, 'و شما اراده نمی‌کنید مگر اینکه خداوند -پروردگار جهانیان- اراده کند و بخواهد!'),
(5830, 5830, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ إِذَا السَّمَاءُ انْفَطَرَتْ', 82, 1, 30, 118, 587, 'آن زمان که آسمان = کرات آسمانی‌ از هم شکافته شود،'),
(5831, 5831, 'وَإِذَا الْكَوَاكِبُ انْتَثَرَتْ', 82, 2, 30, 118, 587, 'و آن زمان که ستارگان پراکنده شوند و فرو ریزند،'),
(5832, 5832, 'وَإِذَا الْبِحَارُ فُجِّرَتْ', 82, 3, 30, 118, 587, 'و آن زمان که دریاها به هم پیوسته شود،'),
(5833, 5833, 'وَإِذَا الْقُبُورُ بُعْثِرَتْ', 82, 4, 30, 118, 587, 'و آن زمان که قبرها زیر و رو گردد (و مردگان خارج شوند)،'),
(5834, 5834, 'عَلِمَتْ نَفْسٌ مَا قَدَّمَتْ وَأَخَّرَتْ', 82, 5, 30, 118, 587, '(در آن زمان) هر کس می‌داند آنچه را از پیش فرستاده و آنچه را برای بعد گذاشته است.'),
(5835, 5835, 'يَا أَيُّهَا الْإِنْسَانُ مَا غَرَّكَ بِرَبِّكَ الْكَرِيمِ', 82, 6, 30, 118, 587, 'ای انسان! چه چیز تو را در برابر پروردگار کریمت مغرور ساخته است؟!'),
(5836, 5836, 'الَّذِي خَلَقَكَ فَسَوَّاكَ فَعَدَلَكَ', 82, 7, 30, 118, 587, 'همان خدایی که تو را آفرید و سامان داد و منظّم ساخت،'),
(5837, 5837, 'فِي أَيِّ صُورَةٍ مَا شَاءَ رَكَّبَكَ', 82, 8, 30, 118, 587, 'و در هر صورتی که خواست تو را ترکیب نمود.'),
(5838, 5838, 'كَلَّا بَلْ تُكَذِّبُونَ بِالدِّينِ', 82, 9, 30, 118, 587, '(آری) آن گونه که شما می‌پندارید نیست؛ بلکه شما روز جزا را منکرید!'),
(5839, 5839, 'وَإِنَّ عَلَيْكُمْ لَحَافِظِينَ', 82, 10, 30, 118, 587, 'و بی‌شک نگاهبانانی بر شما گمارده شده...'),
(5840, 5840, 'كِرَامًا كَاتِبِينَ', 82, 11, 30, 118, 587, 'والا مقام و نویسنده (اعمال نیک و بد شما)،'),
(5841, 5841, 'يَعْلَمُونَ مَا تَفْعَلُونَ', 82, 12, 30, 118, 587, 'که می‌دانند شما چه می‌کنید!'),
(5842, 5842, 'إِنَّ الْأَبْرَارَ لَفِي نَعِيمٍ', 82, 13, 30, 118, 587, 'به یقین نیکان در نعمتی فراوانند.'),
(5843, 5843, 'وَإِنَّ الْفُجَّارَ لَفِي جَحِيمٍ', 82, 14, 30, 118, 587, 'و بدکاران در دوزخند،'),
(5844, 5844, 'يَصْلَوْنَهَا يَوْمَ الدِّينِ', 82, 15, 30, 118, 587, 'روز جزا وارد آن می‌شوند و می‌سوزند،'),
(5845, 5845, 'وَمَا هُمْ عَنْهَا بِغَائِبِينَ', 82, 16, 30, 118, 587, 'و آنان هرگز از آن غایب و دور نیستند!'),
(5846, 5846, 'وَمَا أَدْرَاكَ مَا يَوْمُ الدِّينِ', 82, 17, 30, 118, 587, 'تو چه می‌دانی روز جزا چیست؟!'),
(5847, 5847, 'ثُمَّ مَا أَدْرَاكَ مَا يَوْمُ الدِّينِ', 82, 18, 30, 118, 587, 'باز چه می‌دانی روز جزا چیست؟!'),
(5848, 5848, 'يَوْمَ لَا تَمْلِكُ نَفْسٌ لِنَفْسٍ شَيْئًا ۖ وَالْأَمْرُ يَوْمَئِذٍ لِلَّهِ', 82, 19, 30, 118, 587, 'روزی است که هیچ کس قادر بر انجام کاری به سود دیگری نیست، و همه امور در آن روز از آن خداست!'),
(5849, 5849, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ وَيْلٌ لِلْمُطَفِّفِينَ', 83, 1, 30, 118, 587, 'وای بر کم‌فروشان!'),
(5850, 5850, 'الَّذِينَ إِذَا اكْتَالُوا عَلَى النَّاسِ يَسْتَوْفُونَ', 83, 2, 30, 118, 587, 'آنان که وقتی برای خود پیمانه می‌کنند، حق خود را بطور کامل می‌گیرند؛'),
(5851, 5851, 'وَإِذَا كَالُوهُمْ أَوْ وَزَنُوهُمْ يُخْسِرُونَ', 83, 3, 30, 118, 587, 'امّا هنگامی که می‌خواهند برای دیگران پیمانه یا وزن کنند، کم می‌گذارند!'),
(5852, 5852, 'أَلَا يَظُنُّ أُولَٰئِكَ أَنَّهُمْ مَبْعُوثُونَ', 83, 4, 30, 118, 587, 'آیا آنها گمان نمی‌کنند که برانگیخته می‌شوند،'),
(5853, 5853, 'لِيَوْمٍ عَظِيمٍ', 83, 5, 30, 118, 587, 'در روزی بزرگ؛'),
(5854, 5854, 'يَوْمَ يَقُومُ النَّاسُ لِرَبِّ الْعَالَمِينَ', 83, 6, 30, 118, 587, 'روزی که مردم در پیشگاه پروردگار جهانیان می‌ایستند.'),
(5855, 5855, 'كَلَّا إِنَّ كِتَابَ الْفُجَّارِ لَفِي سِجِّينٍ', 83, 7, 30, 118, 588, 'چنین نیست که آنها (درباره قیامت) می‌پندارند، به یقین نامه اعمال بدکاران در «سجّین» است!'),
(5856, 5856, 'وَمَا أَدْرَاكَ مَا سِجِّينٌ', 83, 8, 30, 118, 588, 'تو چه می‌دانی «سجّین» چیست؟'),
(5857, 5857, 'كِتَابٌ مَرْقُومٌ', 83, 9, 30, 118, 588, 'نامه‌ای است رقم زده شده و سرنوشتی است حتمی!'),
(5858, 5858, 'وَيْلٌ يَوْمَئِذٍ لِلْمُكَذِّبِينَ', 83, 10, 30, 118, 588, 'وای در آن روز بر تکذیب‌کنندگان!'),
(5859, 5859, 'الَّذِينَ يُكَذِّبُونَ بِيَوْمِ الدِّينِ', 83, 11, 30, 118, 588, 'همانها که روز جزا را انکار می‌کنند.'),
(5860, 5860, 'وَمَا يُكَذِّبُ بِهِ إِلَّا كُلُّ مُعْتَدٍ أَثِيمٍ', 83, 12, 30, 118, 588, 'تنها کسی آن را انکار می‌کند که متجاوز و گنهکار است!'),
(5861, 5861, 'إِذَا تُتْلَىٰ عَلَيْهِ آيَاتُنَا قَالَ أَسَاطِيرُ الْأَوَّلِينَ', 83, 13, 30, 118, 588, '(همان کسی که) وقتی آیات ما بر او خوانده می‌شود می‌گوید: «این افسانه‌های پیشینیان است!»'),
(5862, 5862, 'كَلَّا ۖ بَلْ ۜ رَانَ عَلَىٰ قُلُوبِهِمْ مَا كَانُوا يَكْسِبُونَ', 83, 14, 30, 118, 588, 'چنین نیست که آنها می‌پندارند، بلکه اعمالشان چون زنگاری بر دلهایشان نشسته است!'),
(5863, 5863, 'كَلَّا إِنَّهُمْ عَنْ رَبِّهِمْ يَوْمَئِذٍ لَمَحْجُوبُونَ', 83, 15, 30, 118, 588, 'چنین نیست که می‌پندارند، بلکه آنها در آن روز از پروردگارشان محجوبند!'),
(5864, 5864, 'ثُمَّ إِنَّهُمْ لَصَالُو الْجَحِيمِ', 83, 16, 30, 118, 588, 'سپس آنها به یقین وارد دوزخ می‌شوند!'),
(5865, 5865, 'ثُمَّ يُقَالُ هَٰذَا الَّذِي كُنْتُمْ بِهِ تُكَذِّبُونَ', 83, 17, 30, 118, 588, 'بعد به آنها گفته می‌شود: «این همان چیزی است که آن را انکار می‌کردید!»'),
(5866, 5866, 'كَلَّا إِنَّ كِتَابَ الْأَبْرَارِ لَفِي عِلِّيِّينَ', 83, 18, 30, 118, 588, 'چنان نیست که آنها (درباره معاد) می‌پندارند، بلکه نامه اعمال نیکان در «علیّین» است!'),
(5867, 5867, 'وَمَا أَدْرَاكَ مَا عِلِّيُّونَ', 83, 19, 30, 118, 588, 'و تو چه می‌دانی «علیّین» چیست!'),
(5868, 5868, 'كِتَابٌ مَرْقُومٌ', 83, 20, 30, 118, 588, 'نامه‌ای است رقم‌خورده و سرنوشتی است قطعی،'),
(5869, 5869, 'يَشْهَدُهُ الْمُقَرَّبُونَ', 83, 21, 30, 118, 588, 'که مقربان شاهد آنند!'),
(5870, 5870, 'إِنَّ الْأَبْرَارَ لَفِي نَعِيمٍ', 83, 22, 30, 118, 588, 'مسلّماً نیکان در انواع نعمت‌اند:'),
(5871, 5871, 'عَلَى الْأَرَائِكِ يَنْظُرُونَ', 83, 23, 30, 118, 588, 'بر تختهای زیبای بهشتی تکیه کرده و (به زیباییهای بهشت) می‌نگرند!'),
(5872, 5872, 'تَعْرِفُ فِي وُجُوهِهِمْ نَضْرَةَ النَّعِيمِ', 83, 24, 30, 118, 588, 'در چهره‌هایشان طراوت و نشاط نعمت را می‌بینی و می‌شناسی!'),
(5873, 5873, 'يُسْقَوْنَ مِنْ رَحِيقٍ مَخْتُومٍ', 83, 25, 30, 118, 588, 'آنها از شراب (طهور) زلال دست‌نخورده و سربسته‌ای سیراب می‌شوند!'),
(5874, 5874, 'خِتَامُهُ مِسْكٌ ۚ وَفِي ذَٰلِكَ فَلْيَتَنَافَسِ الْمُتَنَافِسُونَ', 83, 26, 30, 118, 588, 'مهری که بر آن نهاده شده از مشک است؛ و در این نعمتهای بهشتی راغبان باید بر یکدیگر پیشی گیرند!'),
(5875, 5875, 'وَمِزَاجُهُ مِنْ تَسْنِيمٍ', 83, 27, 30, 118, 588, 'این شراب (طهور) آمیخته با «تسنیم» است،'),
(5876, 5876, 'عَيْنًا يَشْرَبُ بِهَا الْمُقَرَّبُونَ', 83, 28, 30, 118, 588, 'همان چشمه‌ای که مقرّبان از آن می‌نوشند.'),
(5877, 5877, 'إِنَّ الَّذِينَ أَجْرَمُوا كَانُوا مِنَ الَّذِينَ آمَنُوا يَضْحَكُونَ', 83, 29, 30, 118, 588, 'بدکاران (در دنیا) پیوسته به مؤمنان می‌خندیدند،'),
(5878, 5878, 'وَإِذَا مَرُّوا بِهِمْ يَتَغَامَزُونَ', 83, 30, 30, 118, 588, 'و هنگامی که از کنارشان می‌گذشتند آنان را با اشاره تمسخر می‌کردند،'),
(5879, 5879, 'وَإِذَا انْقَلَبُوا إِلَىٰ أَهْلِهِمُ انْقَلَبُوا فَكِهِينَ', 83, 31, 30, 118, 588, 'و چون به سوی خانواده خود بازمی‌گشتند مسرور و خندان بودند،'),
(5880, 5880, 'وَإِذَا رَأَوْهُمْ قَالُوا إِنَّ هَٰؤُلَاءِ لَضَالُّونَ', 83, 32, 30, 118, 588, 'و هنگامی که آنها را می‌دیدند می‌گفتند: «اینها گمراهانند!»'),
(5881, 5881, 'وَمَا أُرْسِلُوا عَلَيْهِمْ حَافِظِينَ', 83, 33, 30, 118, 588, 'در حالی که هرگز مأمور مراقبت و متکفّل آنان = مؤمنان‌ نبودند!'),
(5882, 5882, 'فَالْيَوْمَ الَّذِينَ آمَنُوا مِنَ الْكُفَّارِ يَضْحَكُونَ', 83, 34, 30, 118, 588, 'ولی امروز مؤمنان به کفار می‌خندند،'),
(5883, 5883, 'عَلَى الْأَرَائِكِ يَنْظُرُونَ', 83, 35, 30, 118, 589, 'در حالی که بر تختهای آراسته بهشتی نشسته و (به سرنوشت شوم آنها) می‌نگرند!'),
(5884, 5884, 'هَلْ ثُوِّبَ الْكُفَّارُ مَا كَانُوا يَفْعَلُونَ', 83, 36, 30, 118, 589, 'آیا (با این حال) کافران پاداش اعمال خود را گرفتند؟!'),
(5885, 5885, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ إِذَا السَّمَاءُ انْشَقَّتْ', 84, 1, 30, 118, 589, 'در آن هنگام که آسمان = کرات آسمانی‌ شکافته شود،'),
(5886, 5886, 'وَأَذِنَتْ لِرَبِّهَا وَحُقَّتْ', 84, 2, 30, 118, 589, 'و تسلیم فرمان پروردگارش شود -و سزاوار است چنین باشد-'),
(5887, 5887, 'وَإِذَا الْأَرْضُ مُدَّتْ', 84, 3, 30, 118, 589, 'و در آن هنگام که زمین گسترده شود،'),
(5888, 5888, 'وَأَلْقَتْ مَا فِيهَا وَتَخَلَّتْ', 84, 4, 30, 118, 589, 'و آنچه در درون دارد بیرون افکنده و خالی شود،'),
(5889, 5889, 'وَأَذِنَتْ لِرَبِّهَا وَحُقَّتْ', 84, 5, 30, 118, 589, 'و تسلیم فرمان پروردگارش گردد -و شایسته است که چنین باشد-'),
(5890, 5890, 'يَا أَيُّهَا الْإِنْسَانُ إِنَّكَ كَادِحٌ إِلَىٰ رَبِّكَ كَدْحًا فَمُلَاقِيهِ', 84, 6, 30, 118, 589, 'ای انسان! تو با تلاش و رنج بسوی پروردگارت می‌روی و او را ملاقات خواهی کرد!'),
(5891, 5891, 'فَأَمَّا مَنْ أُوتِيَ كِتَابَهُ بِيَمِينِهِ', 84, 7, 30, 118, 589, 'پس کسی که نامه اعمالش به دست راستش داده شود،'),
(5892, 5892, 'فَسَوْفَ يُحَاسَبُ حِسَابًا يَسِيرًا', 84, 8, 30, 118, 589, 'بزودی حساب آسانی برای او می‌شود،'),
(5893, 5893, 'وَيَنْقَلِبُ إِلَىٰ أَهْلِهِ مَسْرُورًا', 84, 9, 30, 118, 589, 'و خوشحال به اهل و خانواده‌اش بازمی‌گردد.'),
(5894, 5894, 'وَأَمَّا مَنْ أُوتِيَ كِتَابَهُ وَرَاءَ ظَهْرِهِ', 84, 10, 30, 118, 589, 'و امّا کسی که نامه اعمالش به پشت سرش داده شود،'),
(5895, 5895, 'فَسَوْفَ يَدْعُو ثُبُورًا', 84, 11, 30, 118, 589, 'بزودی فریاد می‌زند وای بر من که هلاک شدم!'),
(5896, 5896, 'وَيَصْلَىٰ سَعِيرًا', 84, 12, 30, 118, 589, 'و در شعله‌های سوزان آتش می‌سوزد.'),
(5897, 5897, 'إِنَّهُ كَانَ فِي أَهْلِهِ مَسْرُورًا', 84, 13, 30, 118, 589, 'چرا که او در میان خانواده‌اش پیوسته (از کفر و گناه خود) مسرور بود!'),
(5898, 5898, 'إِنَّهُ ظَنَّ أَنْ لَنْ يَحُورَ', 84, 14, 30, 118, 589, 'او گمان می‌کرد هرگز بازگشت نمی‌کند!'),
(5899, 5899, 'بَلَىٰ إِنَّ رَبَّهُ كَانَ بِهِ بَصِيرًا', 84, 15, 30, 118, 589, 'آری، پروردگارش نسبت به او بینا بود (و اعمالش را برای حساب ثبت کرد)!'),
(5900, 5900, 'فَلَا أُقْسِمُ بِالشَّفَقِ', 84, 16, 30, 118, 589, 'سوگند به شفق،'),
(5901, 5901, 'وَاللَّيْلِ وَمَا وَسَقَ', 84, 17, 30, 118, 589, 'و سوگند به شب و آنچه را جمع‌آوری می‌کند،'),
(5902, 5902, 'وَالْقَمَرِ إِذَا اتَّسَقَ', 84, 18, 30, 118, 589, 'و سوگند به ماه آنگاه که بَدر کامل می‌شود،'),
(5903, 5903, 'لَتَرْكَبُنَّ طَبَقًا عَنْ طَبَقٍ', 84, 19, 30, 118, 589, 'که همه شما پیوسته از حالی به حال دیگر منتقل می‌شوید (تا به کمال برسید).'),
(5904, 5904, 'فَمَا لَهُمْ لَا يُؤْمِنُونَ', 84, 20, 30, 118, 589, 'پس چرا آنان ایمان نمی‌آورند؟!'),
(5905, 5905, 'وَإِذَا قُرِئَ عَلَيْهِمُ الْقُرْآنُ لَا يَسْجُدُونَ ۩', 84, 21, 30, 118, 589, 'و هنگامی که قرآن بر آنها خوانده می‌شود سجده نمی‌کنند؟!'),
(5906, 5906, 'بَلِ الَّذِينَ كَفَرُوا يُكَذِّبُونَ', 84, 22, 30, 118, 589, 'بلکه کافران پیوسته آیات الهی را انکار می‌کنند!'),
(5907, 5907, 'وَاللَّهُ أَعْلَمُ بِمَا يُوعُونَ', 84, 23, 30, 118, 589, 'و خداوند آنچه را در دل پنهان می‌دارند بخوبی می‌داند!'),
(5908, 5908, 'فَبَشِّرْهُمْ بِعَذَابٍ أَلِيمٍ', 84, 24, 30, 118, 589, 'پس آنها را به عذابی دردناک بشارت ده!'),
(5909, 5909, 'إِلَّا الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ لَهُمْ أَجْرٌ غَيْرُ مَمْنُونٍ', 84, 25, 30, 118, 589, 'مگر کسانی که ایمان آورده و اعمال صالح انجام داده‌اند، که برای آنان پاداشی است قطع‌نشدنی!'),
(5910, 5910, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ وَالسَّمَاءِ ذَاتِ الْبُرُوجِ', 85, 1, 30, 118, 590, 'سوگند به آسمان که دارای برجهای بسیار است،'),
(5911, 5911, 'وَالْيَوْمِ الْمَوْعُودِ', 85, 2, 30, 118, 590, 'و سوگند به آن روز موعود،'),
(5912, 5912, 'وَشَاهِدٍ وَمَشْهُودٍ', 85, 3, 30, 118, 590, 'و سوگند به «شاهد» و «مشهود»! «شاهد»: پیامبر و گواهان اعمال، و «مشهود»: اعمال امّت است‌'),
(5913, 5913, 'قُتِلَ أَصْحَابُ الْأُخْدُودِ', 85, 4, 30, 118, 590, 'مرگ بر شکنجه‌گران صاحب گودال (آتش)،'),
(5914, 5914, 'النَّارِ ذَاتِ الْوَقُودِ', 85, 5, 30, 118, 590, 'آتشی عظیم و شعله‌ور!'),
(5915, 5915, 'إِذْ هُمْ عَلَيْهَا قُعُودٌ', 85, 6, 30, 118, 590, 'هنگامی که در کنار آن نشسته بودند،'),
(5916, 5916, 'وَهُمْ عَلَىٰ مَا يَفْعَلُونَ بِالْمُؤْمِنِينَ شُهُودٌ', 85, 7, 30, 118, 590, 'و آنچه را با مؤمنان انجام می‌دادند (با خونسردی و قساوت) تماشا می‌کردند!'),
(5917, 5917, 'وَمَا نَقَمُوا مِنْهُمْ إِلَّا أَنْ يُؤْمِنُوا بِاللَّهِ الْعَزِيزِ الْحَمِيدِ', 85, 8, 30, 118, 590, 'آنها هیچ ایرادی بر مؤمنان نداشتند جز اینکه به خداوند عزیز و حمید ایمان آورده بودند؛'),
(5918, 5918, 'الَّذِي لَهُ مُلْكُ السَّمَاوَاتِ وَالْأَرْضِ ۚ وَاللَّهُ عَلَىٰ كُلِّ شَيْءٍ شَهِيدٌ', 85, 9, 30, 118, 590, 'همان کسی که حکومت آسمانها و زمین از آن اوست و خداوند بر همه چیز گواه است!'),
(5919, 5919, 'إِنَّ الَّذِينَ فَتَنُوا الْمُؤْمِنِينَ وَالْمُؤْمِنَاتِ ثُمَّ لَمْ يَتُوبُوا فَلَهُمْ عَذَابُ جَهَنَّمَ وَلَهُمْ عَذَابُ الْحَرِيقِ', 85, 10, 30, 118, 590, 'کسانی که مردان و زنان باایمان را شکنجه دادند سپس توبه نکردند، برای آنها عذاب دوزخ و عذاب آتش سوزان است!'),
(5920, 5920, 'إِنَّ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ لَهُمْ جَنَّاتٌ تَجْرِي مِنْ تَحْتِهَا الْأَنْهَارُ ۚ ذَٰلِكَ الْفَوْزُ الْكَبِيرُ', 85, 11, 30, 118, 590, 'و برای کسانی که ایمان آوردند و اعمال شایسته انجام دادند، باغهایی از بهشت است که نهرها زیر درختانش جاری است؛ و این نجات و پیروزی بزرگ است!'),
(5921, 5921, 'إِنَّ بَطْشَ رَبِّكَ لَشَدِيدٌ', 85, 12, 30, 118, 590, 'گرفتن قهرآمیز و مجازات پروردگارت به یقین بسیار شدید است!'),
(5922, 5922, 'إِنَّهُ هُوَ يُبْدِئُ وَيُعِيدُ', 85, 13, 30, 118, 590, 'اوست که آفرینش را آغاز می‌کند و بازمی‌گرداند،'),
(5923, 5923, 'وَهُوَ الْغَفُورُ الْوَدُودُ', 85, 14, 30, 118, 590, 'و او آمرزنده و دوستدار (مؤمنان) است،'),
(5924, 5924, 'ذُو الْعَرْشِ الْمَجِيدُ', 85, 15, 30, 118, 590, 'صاحب عرش و دارای مجد و عظمت است،'),
(5925, 5925, 'فَعَّالٌ لِمَا يُرِيدُ', 85, 16, 30, 118, 590, 'و آنچه را می‌خواهد انجام می‌دهد!'),
(5926, 5926, 'هَلْ أَتَاكَ حَدِيثُ الْجُنُودِ', 85, 17, 30, 118, 590, 'آیا داستان لشکرها به تو رسیده است،'),
(5927, 5927, 'فِرْعَوْنَ وَثَمُودَ', 85, 18, 30, 118, 590, 'لشکریان فرعون و ثمود؟!'),
(5928, 5928, 'بَلِ الَّذِينَ كَفَرُوا فِي تَكْذِيبٍ', 85, 19, 30, 118, 590, 'ولی کافران پیوسته در تکذیب حقند،'),
(5929, 5929, 'وَاللَّهُ مِنْ وَرَائِهِمْ مُحِيطٌ', 85, 20, 30, 118, 590, 'و خداوند به همه آنها احاطه دارد!'),
(5930, 5930, 'بَلْ هُوَ قُرْآنٌ مَجِيدٌ', 85, 21, 30, 118, 590, '(این آیات، سحر و دروغ نیست،) بلکه قرآن باعظمت است...'),
(5931, 5931, 'فِي لَوْحٍ مَحْفُوظٍ', 85, 22, 30, 118, 590, 'که در لوح محفوظ جای دارد!'),
(5932, 5932, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ وَالسَّمَاءِ وَالطَّارِقِ', 86, 1, 30, 118, 591, 'سوگند به آسمان و کوبنده شب!'),
(5933, 5933, 'وَمَا أَدْرَاكَ مَا الطَّارِقُ', 86, 2, 30, 118, 591, 'و تو نمی‌دانی کوبنده شب چیست!'),
(5934, 5934, 'النَّجْمُ الثَّاقِبُ', 86, 3, 30, 118, 591, 'همان ستاره درخشان و شکافنده تاریکیهاست!'),
(5935, 5935, 'إِنْ كُلُّ نَفْسٍ لَمَّا عَلَيْهَا حَافِظٌ', 86, 4, 30, 118, 591, '(به این آیت بزرگ الهی سوگند) که هر کس مراقب و محافظی دارد!'),
(5936, 5936, 'فَلْيَنْظُرِ الْإِنْسَانُ مِمَّ خُلِقَ', 86, 5, 30, 118, 591, 'انسان باید بنگرد که از چه چیز آفریده شده است!'),
(5937, 5937, 'خُلِقَ مِنْ مَاءٍ دَافِقٍ', 86, 6, 30, 118, 591, 'از یک آب جهنده آفریده شده است،'),
(5938, 5938, 'يَخْرُجُ مِنْ بَيْنِ الصُّلْبِ وَالتَّرَائِبِ', 86, 7, 30, 118, 591, 'آبی که از میان پشت و سینه‌ها خارج می‌شود!'),
(5939, 5939, 'إِنَّهُ عَلَىٰ رَجْعِهِ لَقَادِرٌ', 86, 8, 30, 118, 591, 'مسلّماً او = خدائی که انسان را از چنین چیز پستی آفرید می‌تواند او را بازگرداند!'),
(5940, 5940, 'يَوْمَ تُبْلَى السَّرَائِرُ', 86, 9, 30, 118, 591, 'در آن روز که اسرار نهان (انسان) آشکار میشود،'),
(5941, 5941, 'فَمَا لَهُ مِنْ قُوَّةٍ وَلَا نَاصِرٍ', 86, 10, 30, 118, 591, 'و برای او هیچ نیرو و یاوری نیست!'),
(5942, 5942, 'وَالسَّمَاءِ ذَاتِ الرَّجْعِ', 86, 11, 30, 118, 591, 'سوگند به آسمان پرباران،'),
(5943, 5943, 'وَالْأَرْضِ ذَاتِ الصَّدْعِ', 86, 12, 30, 118, 591, 'و سوگند به زمین پرشکاف (که گیاهان از آن سر برمی‌آورند)،'),
(5944, 5944, 'إِنَّهُ لَقَوْلٌ فَصْلٌ', 86, 13, 30, 118, 591, 'که این (قرآن) سخنی است که حقّ را از باطل جدا می‌کند،'),
(5945, 5945, 'وَمَا هُوَ بِالْهَزْلِ', 86, 14, 30, 118, 591, 'و هرگز شوخی نیست!'),
(5946, 5946, 'إِنَّهُمْ يَكِيدُونَ كَيْدًا', 86, 15, 30, 118, 591, 'آنها پیوسته حیله می‌کنند،'),
(5947, 5947, 'وَأَكِيدُ كَيْدًا', 86, 16, 30, 118, 591, 'و من هم در برابر آنها چاره می‌کنم!'),
(5948, 5948, 'فَمَهِّلِ الْكَافِرِينَ أَمْهِلْهُمْ رُوَيْدًا', 86, 17, 30, 118, 591, 'حال که چنین است کافران را (فقط) اندکی مهلت ده (تا سزای اعمالشان را ببینند)!'),
(5949, 5949, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ سَبِّحِ اسْمَ رَبِّكَ الْأَعْلَى', 87, 1, 30, 119, 591, 'منزّه شمار نام پروردگار بلندمرتبه‌ات را!'),
(5950, 5950, 'الَّذِي خَلَقَ فَسَوَّىٰ', 87, 2, 30, 119, 591, 'همان خداوندی که آفرید و منظم کرد،'),
(5951, 5951, 'وَالَّذِي قَدَّرَ فَهَدَىٰ', 87, 3, 30, 119, 591, 'و همان که اندازه‌گیری کرد و هدایت نمود،'),
(5952, 5952, 'وَالَّذِي أَخْرَجَ الْمَرْعَىٰ', 87, 4, 30, 119, 591, 'و آن کس را که چراگاه را به وجود آورد،'),
(5953, 5953, 'فَجَعَلَهُ غُثَاءً أَحْوَىٰ', 87, 5, 30, 119, 591, 'سپس آن را خشک و تیره قرار داد!'),
(5954, 5954, 'سَنُقْرِئُكَ فَلَا تَنْسَىٰ', 87, 6, 30, 119, 591, 'ما بزودی (قرآن را) بر تو می‌خوانیم و هرگز فراموش نخواهی کرد،'),
(5955, 5955, 'إِلَّا مَا شَاءَ اللَّهُ ۚ إِنَّهُ يَعْلَمُ الْجَهْرَ وَمَا يَخْفَىٰ', 87, 7, 30, 119, 591, 'مگر آنچه را خدا بخواهد، که او آشکار و نهان را می‌داند!'),
(5956, 5956, 'وَنُيَسِّرُكَ لِلْيُسْرَىٰ', 87, 8, 30, 119, 591, 'و ما تو را برای انجام هر کار خیر آماده می‌کنیم!'),
(5957, 5957, 'فَذَكِّرْ إِنْ نَفَعَتِ الذِّكْرَىٰ', 87, 9, 30, 119, 591, 'پس تذکّر ده اگر تذکّر مفید باشد!'),
(5958, 5958, 'سَيَذَّكَّرُ مَنْ يَخْشَىٰ', 87, 10, 30, 119, 591, 'و بزودی کسی که از خدا می‌ترسد متذکّر می‌شود.'),
(5959, 5959, 'وَيَتَجَنَّبُهَا الْأَشْقَى', 87, 11, 30, 119, 591, 'امّا بدبخت‌ترین افراد از آن دوری می‌گزیند،'),
(5960, 5960, 'الَّذِي يَصْلَى النَّارَ الْكُبْرَىٰ', 87, 12, 30, 119, 591, 'همان کسی که در آتش بزرگ وارد می‌شود،'),
(5961, 5961, 'ثُمَّ لَا يَمُوتُ فِيهَا وَلَا يَحْيَىٰ', 87, 13, 30, 119, 591, 'سپس در آن آتش نه می‌میرد و نه زنده می‌شود!'),
(5962, 5962, 'قَدْ أَفْلَحَ مَنْ تَزَكَّىٰ', 87, 14, 30, 119, 591, 'به یقین کسی که پاکی جست (و خود را تزکیه کرد)، رستگار شد.'),
(5963, 5963, 'وَذَكَرَ اسْمَ رَبِّهِ فَصَلَّىٰ', 87, 15, 30, 119, 591, 'و (آن که) نام پروردگارش را یاد کرد سپس نماز خواند!'),
(5964, 5964, 'بَلْ تُؤْثِرُونَ الْحَيَاةَ الدُّنْيَا', 87, 16, 30, 119, 592, 'ولی شما زندگی دنیا را مقدم می‌دارید،'),
(5965, 5965, 'وَالْآخِرَةُ خَيْرٌ وَأَبْقَىٰ', 87, 17, 30, 119, 592, 'در حالی که آخرت بهتر و پایدارتر است!'),
(5966, 5966, 'إِنَّ هَٰذَا لَفِي الصُّحُفِ الْأُولَىٰ', 87, 18, 30, 119, 592, 'این دستورها در کتب آسمانی پیشین (نیز) آمده است،'),
(5967, 5967, 'صُحُفِ إِبْرَاهِيمَ وَمُوسَىٰ', 87, 19, 30, 119, 592, 'در کتب ابراهیم و موسی.'),
(5968, 5968, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ هَلْ أَتَاكَ حَدِيثُ الْغَاشِيَةِ', 88, 1, 30, 119, 592, 'آیا داستان غاشیه = روز قیامت که حوادث وحشتناکش همه را می‌پوشاند به تو رسیده است؟!'),
(5969, 5969, 'وُجُوهٌ يَوْمَئِذٍ خَاشِعَةٌ', 88, 2, 30, 119, 592, 'چهره‌هایی در آن روز خاشع و ذلّت‌بارند،'),
(5970, 5970, 'عَامِلَةٌ نَاصِبَةٌ', 88, 3, 30, 119, 592, 'آنها که پیوسته عمل کرده و خسته شده‌اند (و نتیجه‌ای عایدشان نشده است)،'),
(5971, 5971, 'تَصْلَىٰ نَارًا حَامِيَةً', 88, 4, 30, 119, 592, 'و در آتش سوزان وارد می‌گردند؛'),
(5972, 5972, 'تُسْقَىٰ مِنْ عَيْنٍ آنِيَةٍ', 88, 5, 30, 119, 592, 'از چشمه‌ای بسیار داغ به آنان می‌نوشانند؛'),
(5973, 5973, 'لَيْسَ لَهُمْ طَعَامٌ إِلَّا مِنْ ضَرِيعٍ', 88, 6, 30, 119, 592, 'غذائی جز از ضَریع = خار خشک تلخ و بدبو ندارند؛'),
(5974, 5974, 'لَا يُسْمِنُ وَلَا يُغْنِي مِنْ جُوعٍ', 88, 7, 30, 119, 592, 'غذایی که نه آنها را فربه می‌کند و نه از گرسنگی می‌رهاند!'),
(5975, 5975, 'وُجُوهٌ يَوْمَئِذٍ نَاعِمَةٌ', 88, 8, 30, 119, 592, 'چهره‌هایی در آن روز شاداب و باطراوتند،'),
(5976, 5976, 'لِسَعْيِهَا رَاضِيَةٌ', 88, 9, 30, 119, 592, 'و از سعی و تلاش خود خشنودند،'),
(5977, 5977, 'فِي جَنَّةٍ عَالِيَةٍ', 88, 10, 30, 119, 592, 'در بهشتی عالی جای دارند،'),
(5978, 5978, 'لَا تَسْمَعُ فِيهَا لَاغِيَةً', 88, 11, 30, 119, 592, 'که در آن هیچ سخن لغو و بیهوده‌ای نمی‌شنوند!'),
(5979, 5979, 'فِيهَا عَيْنٌ جَارِيَةٌ', 88, 12, 30, 119, 592, 'در آن چشمه‌ای جاری است،'),
(5980, 5980, 'فِيهَا سُرُرٌ مَرْفُوعَةٌ', 88, 13, 30, 119, 592, 'در آن تختهای زیبای بلندی است،'),
(5981, 5981, 'وَأَكْوَابٌ مَوْضُوعَةٌ', 88, 14, 30, 119, 592, 'و قدحهایی (که در کنار این چشمه) نهاده،'),
(5982, 5982, 'وَنَمَارِقُ مَصْفُوفَةٌ', 88, 15, 30, 119, 592, 'و بالشها و پشتیهای صف‌داده شده،'),
(5983, 5983, 'وَزَرَابِيُّ مَبْثُوثَةٌ', 88, 16, 30, 119, 592, 'و فرشهای فاخر گسترده!'),
(5984, 5984, 'أَفَلَا يَنْظُرُونَ إِلَى الْإِبِلِ كَيْفَ خُلِقَتْ', 88, 17, 30, 119, 592, 'آیا آنان به شتر نمی‌نگرند که چگونه آفریده شده است؟!'),
(5985, 5985, 'وَإِلَى السَّمَاءِ كَيْفَ رُفِعَتْ', 88, 18, 30, 119, 592, 'و به آسمان نگاه نمی‌کنند که چگونه برافراشته شده؟!'),
(5986, 5986, 'وَإِلَى الْجِبَالِ كَيْفَ نُصِبَتْ', 88, 19, 30, 119, 592, 'و به کوه‌ها که چگونه در جای خود نصب گردیده!'),
(5987, 5987, 'وَإِلَى الْأَرْضِ كَيْفَ سُطِحَتْ', 88, 20, 30, 119, 592, 'و به زمین که چگونه گسترده و هموار گشته است؟!'),
(5988, 5988, 'فَذَكِّرْ إِنَّمَا أَنْتَ مُذَكِّرٌ', 88, 21, 30, 119, 592, 'پس تذکّر ده که تو فقط تذکّر دهنده‌ای!'),
(5989, 5989, 'لَسْتَ عَلَيْهِمْ بِمُصَيْطِرٍ', 88, 22, 30, 119, 592, 'تو سلطه‌گر بر آنان نیستی که (بر ایمان) مجبورشان کنی،'),
(5990, 5990, 'إِلَّا مَنْ تَوَلَّىٰ وَكَفَرَ', 88, 23, 30, 119, 592, 'مگر کسی که پشت کند و کافر شود،'),
(5991, 5991, 'فَيُعَذِّبُهُ اللَّهُ الْعَذَابَ الْأَكْبَرَ', 88, 24, 30, 119, 592, 'که خداوند او را به عذاب بزرگ مجازات می‌کند!'),
(5992, 5992, 'إِنَّ إِلَيْنَا إِيَابَهُمْ', 88, 25, 30, 119, 592, 'به یقین بازگشت (همه) آنان به سوی ماست،'),
(5993, 5993, 'ثُمَّ إِنَّ عَلَيْنَا حِسَابَهُمْ', 88, 26, 30, 119, 592, 'و مسلّماً حسابشان (نیز) با ماست!'),
(5994, 5994, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ وَالْفَجْرِ', 89, 1, 30, 119, 593, 'به سپیده دم سوگند،'),
(5995, 5995, 'وَلَيَالٍ عَشْرٍ', 89, 2, 30, 119, 593, 'و به شبهای دهگانه،'),
(5996, 5996, 'وَالشَّفْعِ وَالْوَتْرِ', 89, 3, 30, 119, 593, 'و به زوج و فرد،'),
(5997, 5997, 'وَاللَّيْلِ إِذَا يَسْرِ', 89, 4, 30, 119, 593, 'و به شب، هنگامی که (به سوی روشنایی روز) حرکت می‌کند سوگند (که پروردگارت در کمین ظالمان است)!'),
(5998, 5998, 'هَلْ فِي ذَٰلِكَ قَسَمٌ لِذِي حِجْرٍ', 89, 5, 30, 119, 593, 'آیا در آنچه گفته شد، سوگند مهمّی برای صاحبان خرد نیست؟!'),
(5999, 5999, 'أَلَمْ تَرَ كَيْفَ فَعَلَ رَبُّكَ بِعَادٍ', 89, 6, 30, 119, 593, 'آیا ندیدی پروردگارت با قوم «عاد» چه کرد؟!'),
(6000, 6000, 'إِرَمَ ذَاتِ الْعِمَادِ', 89, 7, 30, 119, 593, 'و با آن شهر «اِرَم» باعظمت،'),
(6001, 6001, 'الَّتِي لَمْ يُخْلَقْ مِثْلُهَا فِي الْبِلَادِ', 89, 8, 30, 119, 593, 'همان شهری که مانندش در شهرها آفریده نشده بود!'),
(6002, 6002, 'وَثَمُودَ الَّذِينَ جَابُوا الصَّخْرَ بِالْوَادِ', 89, 9, 30, 119, 593, 'و قوم «ثمود» که صخره‌های عظیم را از (کنار) درّه می‌بریدند (و از آن خانه و کاخ می‌ساختند)!'),
(6003, 6003, 'وَفِرْعَوْنَ ذِي الْأَوْتَادِ', 89, 10, 30, 119, 593, 'و فرعونی که قدرتمند و شکنجه‌گر بود،'),
(6004, 6004, 'الَّذِينَ طَغَوْا فِي الْبِلَادِ', 89, 11, 30, 119, 593, 'همان اقوامی که در شهرها طغیان کردند،'),
(6005, 6005, 'فَأَكْثَرُوا فِيهَا الْفَسَادَ', 89, 12, 30, 119, 593, 'و فساد فراوان در آنها به بار آوردند؛'),
(6006, 6006, 'فَصَبَّ عَلَيْهِمْ رَبُّكَ سَوْطَ عَذَابٍ', 89, 13, 30, 119, 593, 'به همین سبب خداوند تازیانه عذاب را بر آنان فرو ریخت!'),
(6007, 6007, 'إِنَّ رَبَّكَ لَبِالْمِرْصَادِ', 89, 14, 30, 119, 593, 'به یقین پروردگار تو در کمینگاه (ستمگران) است!'),
(6008, 6008, 'فَأَمَّا الْإِنْسَانُ إِذَا مَا ابْتَلَاهُ رَبُّهُ فَأَكْرَمَهُ وَنَعَّمَهُ فَيَقُولُ رَبِّي أَكْرَمَنِ', 89, 15, 30, 119, 593, 'امّا انسان هنگامی که پروردگارش او را برای آزمایش، اکرام می‌کند و نعمت می‌بخشد (مغرور می‌شود و) می‌گوید: «پروردگارم مرا گرامی داشته است!»'),
(6009, 6009, 'وَأَمَّا إِذَا مَا ابْتَلَاهُ فَقَدَرَ عَلَيْهِ رِزْقَهُ فَيَقُولُ رَبِّي أَهَانَنِ', 89, 16, 30, 119, 593, 'و امّا هنگامی که برای امتحان، روزیش را بر او تنگ می‌گیرد (مأیوس می‌شود و) می‌گوید: «پروردگارم مرا خوار کرده است!»'),
(6010, 6010, 'كَلَّا ۖ بَلْ لَا تُكْرِمُونَ الْيَتِيمَ', 89, 17, 30, 119, 593, 'چنان نیست که شما می‌پندارید؛ شما یتیمان را گرامی نمی‌دارید،'),
(6011, 6011, 'وَلَا تَحَاضُّونَ عَلَىٰ طَعَامِ الْمِسْكِينِ', 89, 18, 30, 119, 593, 'و یکدیگر را بر اطعام مستمندان تشویق نمی‌کنید،'),
(6012, 6012, 'وَتَأْكُلُونَ التُّرَاثَ أَكْلًا لَمًّا', 89, 19, 30, 119, 593, 'و میراث را (از راه مشروع و نامشروع) جمع کرده می‌خورید،'),
(6013, 6013, 'وَتُحِبُّونَ الْمَالَ حُبًّا جَمًّا', 89, 20, 30, 119, 593, 'و مال و ثروت را بسیار دوست دارید (و بخاطر آن گناهان زیادی مرتکب می‌شوید)!'),
(6014, 6014, 'كَلَّا إِذَا دُكَّتِ الْأَرْضُ دَكًّا دَكًّا', 89, 21, 30, 119, 593, 'چنان نیست که آنها می‌پندارند! در آن هنگام که زمین سخت در هم کوبیده شود،'),
(6015, 6015, 'وَجَاءَ رَبُّكَ وَالْمَلَكُ صَفًّا صَفًّا', 89, 22, 30, 119, 593, 'و فرمان پروردگارت فرا رسد و فرشتگان صف در صف حاضر شوند،'),
(6016, 6016, 'وَجِيءَ يَوْمَئِذٍ بِجَهَنَّمَ ۚ يَوْمَئِذٍ يَتَذَكَّرُ الْإِنْسَانُ وَأَنَّىٰ لَهُ الذِّكْرَىٰ', 89, 23, 30, 119, 593, 'و در آن روز جهنم را حاضر می‌کنند؛ (آری) در آن روز انسان متذکّر می‌شود؛ امّا این تذکّر چه سودی برای او دارد؟!'),
(6017, 6017, 'يَقُولُ يَا لَيْتَنِي قَدَّمْتُ لِحَيَاتِي', 89, 24, 30, 119, 594, 'می‌گوید: «ای کاش برای (این) زندگیم چیزی از پیش فرستاده بودم!»'),
(6018, 6018, 'فَيَوْمَئِذٍ لَا يُعَذِّبُ عَذَابَهُ أَحَدٌ', 89, 25, 30, 119, 594, 'در آن روز هیچ کس همانند او = خدا عذاب نمی‌کند،'),
(6019, 6019, 'وَلَا يُوثِقُ وَثَاقَهُ أَحَدٌ', 89, 26, 30, 119, 594, 'و هیچ کس همچون او کسی را به بند نمی‌کشد!'),
(6020, 6020, 'يَا أَيَّتُهَا النَّفْسُ الْمُطْمَئِنَّةُ', 89, 27, 30, 119, 594, 'تو ای روح آرام‌یافته!'),
(6021, 6021, 'ارْجِعِي إِلَىٰ رَبِّكِ رَاضِيَةً مَرْضِيَّةً', 89, 28, 30, 119, 594, 'به سوی پروردگارت بازگرد در حالی که هم تو از او خشنودی و هم او از تو خشنود است،'),
(6022, 6022, 'فَادْخُلِي فِي عِبَادِي', 89, 29, 30, 119, 594, 'پس در سلک بندگانم درآی،'),
(6023, 6023, 'وَادْخُلِي جَنَّتِي', 89, 30, 30, 119, 594, 'و در بهشتم وارد شو!'),
(6024, 6024, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ لَا أُقْسِمُ بِهَٰذَا الْبَلَدِ', 90, 1, 30, 119, 594, 'قسم به این شهر مقدّس = مکّه‌،'),
(6025, 6025, 'وَأَنْتَ حِلٌّ بِهَٰذَا الْبَلَدِ', 90, 2, 30, 119, 594, 'شهری که تو در آن ساکنی،'),
(6026, 6026, 'وَوَالِدٍ وَمَا وَلَدَ', 90, 3, 30, 119, 594, 'و قسم به پدر و فرزندش = ابراهیم خلیل و فرزندش اسماعیل ذبیح‌،'),
(6027, 6027, 'لَقَدْ خَلَقْنَا الْإِنْسَانَ فِي كَبَدٍ', 90, 4, 30, 119, 594, 'که ما انسان را در رنج آفریدیم (و زندگی او پر از رنجهاست)!'),
(6028, 6028, 'أَيَحْسَبُ أَنْ لَنْ يَقْدِرَ عَلَيْهِ أَحَدٌ', 90, 5, 30, 119, 594, 'آیا او گمان می‌کند که هیچ کس نمی‌تواند بر او دست یابد؟!'),
(6029, 6029, 'يَقُولُ أَهْلَكْتُ مَالًا لُبَدًا', 90, 6, 30, 119, 594, 'می‌گوید: «مال زیادی را (در کارهای خیر) نابود کرده‌ام!»');
INSERT INTO `Quran` (`rowid`, `id`, `Arabic`, `Chapter`, `Verse`, `Juz`, `Hizb`, `Page`, `tr`) VALUES
(6030, 6030, 'أَيَحْسَبُ أَنْ لَمْ يَرَهُ أَحَدٌ', 90, 7, 30, 119, 594, 'آیا (انسان) گمان می‌کند هیچ کس او را ندیده (که عمل خیری انجام نداده) است؟!'),
(6031, 6031, 'أَلَمْ نَجْعَلْ لَهُ عَيْنَيْنِ', 90, 8, 30, 119, 594, 'آیا برای او دو چشم قرار ندادیم،'),
(6032, 6032, 'وَلِسَانًا وَشَفَتَيْنِ', 90, 9, 30, 119, 594, 'و یک زبان و دو لب؟!'),
(6033, 6033, 'وَهَدَيْنَاهُ النَّجْدَيْنِ', 90, 10, 30, 119, 594, 'و او را به راه خیر و شرّ هدایت کردیم!'),
(6034, 6034, 'فَلَا اقْتَحَمَ الْعَقَبَةَ', 90, 11, 30, 119, 594, 'ولی او از آن گردنه مهمّ نگذشت!'),
(6035, 6035, 'وَمَا أَدْرَاكَ مَا الْعَقَبَةُ', 90, 12, 30, 119, 594, 'و تو نمی‌دانی آن گردنه چیست!'),
(6036, 6036, 'فَكُّ رَقَبَةٍ', 90, 13, 30, 119, 594, 'آزادکردن برده‌ای،'),
(6037, 6037, 'أَوْ إِطْعَامٌ فِي يَوْمٍ ذِي مَسْغَبَةٍ', 90, 14, 30, 119, 594, 'یا غذا دادن در روز گرسنگی...'),
(6038, 6038, 'يَتِيمًا ذَا مَقْرَبَةٍ', 90, 15, 30, 119, 594, 'یتیمی از خویشاوندان،'),
(6039, 6039, 'أَوْ مِسْكِينًا ذَا مَتْرَبَةٍ', 90, 16, 30, 119, 594, 'یا مستمندی خاک‌نشین را،'),
(6040, 6040, 'ثُمَّ كَانَ مِنَ الَّذِينَ آمَنُوا وَتَوَاصَوْا بِالصَّبْرِ وَتَوَاصَوْا بِالْمَرْحَمَةِ', 90, 17, 30, 119, 594, 'سپس از کسانی باشد که ایمان آورده و یکدیگر را به شکیبایی و رحمت توصیه می‌کنند!'),
(6041, 6041, 'أُولَٰئِكَ أَصْحَابُ الْمَيْمَنَةِ', 90, 18, 30, 119, 594, 'آنها «اصحاب الیمین» اند (که نامه اعمالشان را به دست راستشان می‌دهند)!'),
(6042, 6042, 'وَالَّذِينَ كَفَرُوا بِآيَاتِنَا هُمْ أَصْحَابُ الْمَشْأَمَةِ', 90, 19, 30, 119, 594, 'و کسانی که آیات ما را انکار کرده‌اند افرادی شومند (که نامه اعمالشان به دست چپشان داده می‌شود).'),
(6043, 6043, 'عَلَيْهِمْ نَارٌ مُؤْصَدَةٌ', 90, 20, 30, 119, 594, 'بر آنها آتشی است فروبسته (که راه فراری از آن نیست)!'),
(6044, 6044, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ وَالشَّمْسِ وَضُحَاهَا', 91, 1, 30, 119, 595, 'به خورشید و گسترش نور آن سوگند،'),
(6045, 6045, 'وَالْقَمَرِ إِذَا تَلَاهَا', 91, 2, 30, 119, 595, 'و به ماه هنگامی که بعد از آن درآید،'),
(6046, 6046, 'وَالنَّهَارِ إِذَا جَلَّاهَا', 91, 3, 30, 119, 595, 'و به روز هنگامی که صفحه زمین را روشن سازد،'),
(6047, 6047, 'وَاللَّيْلِ إِذَا يَغْشَاهَا', 91, 4, 30, 119, 595, 'و به شب آن هنگام که زمین را بپوشاند،'),
(6048, 6048, 'وَالسَّمَاءِ وَمَا بَنَاهَا', 91, 5, 30, 119, 595, 'و قسم به آسمان و کسی که آسمان را بنا کرده،'),
(6049, 6049, 'وَالْأَرْضِ وَمَا طَحَاهَا', 91, 6, 30, 119, 595, 'و به زمین و کسی که آن را گسترانیده،'),
(6050, 6050, 'وَنَفْسٍ وَمَا سَوَّاهَا', 91, 7, 30, 119, 595, 'و قسم به جان آدمی و آن کس که آن را (آفریده و) منظّم ساخته،'),
(6051, 6051, 'فَأَلْهَمَهَا فُجُورَهَا وَتَقْوَاهَا', 91, 8, 30, 119, 595, 'سپس فجور و تقوا (شرّ و خیرش) را به او الهام کرده است،'),
(6052, 6052, 'قَدْ أَفْلَحَ مَنْ زَكَّاهَا', 91, 9, 30, 119, 595, 'که هر کس نفس خود را پاک و تزکیه کرده، رستگار شده؛'),
(6053, 6053, 'وَقَدْ خَابَ مَنْ دَسَّاهَا', 91, 10, 30, 119, 595, 'و آن کس که نفس خویش را با معصیت و گناه آلوده ساخته، نومید و محروم گشته است!'),
(6054, 6054, 'كَذَّبَتْ ثَمُودُ بِطَغْوَاهَا', 91, 11, 30, 119, 595, 'قوم «ثمود» بر اثر طغیان، (پیامبرشان را) تکذیب کردند،'),
(6055, 6055, 'إِذِ انْبَعَثَ أَشْقَاهَا', 91, 12, 30, 119, 595, 'آنگاه که شقی‌ترین آنها بپاخاست،'),
(6056, 6056, 'فَقَالَ لَهُمْ رَسُولُ اللَّهِ نَاقَةَ اللَّهِ وَسُقْيَاهَا', 91, 13, 30, 119, 595, 'و فرستاده الهی = صالح‌ به آنان گفت: «ناقه خدا = همان شتری که معجزه الهی بود را با آبشخورش واگذارید (و مزاحم آن نشوید)!»'),
(6057, 6057, 'فَكَذَّبُوهُ فَعَقَرُوهَا فَدَمْدَمَ عَلَيْهِمْ رَبُّهُمْ بِذَنْبِهِمْ فَسَوَّاهَا', 91, 14, 30, 119, 595, 'ولی آنها او را تکذیب و ناقه را پی کردند (و به هلاکت رساندند)؛ از این رو پروردگارشان آنها (و سرزمینشان) را بخاطر گناهانشان در هم کوبید و با خاک یکسان و صاف کرد!'),
(6058, 6058, 'وَلَا يَخَافُ عُقْبَاهَا', 91, 15, 30, 119, 595, 'و او هرگز از فرجام این کار = مجازات ستمگران‌ بیم ندارد!'),
(6059, 6059, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ وَاللَّيْلِ إِذَا يَغْشَىٰ', 92, 1, 30, 119, 595, 'قسم به شب در آن هنگام که (جهان را) بپوشاند،'),
(6060, 6060, 'وَالنَّهَارِ إِذَا تَجَلَّىٰ', 92, 2, 30, 119, 595, 'و قسم به روز هنگامی که تجلّی کند،'),
(6061, 6061, 'وَمَا خَلَقَ الذَّكَرَ وَالْأُنْثَىٰ', 92, 3, 30, 119, 595, 'و قسم به آن کس که جنس مذکّر و مؤنّث را آفرید،'),
(6062, 6062, 'إِنَّ سَعْيَكُمْ لَشَتَّىٰ', 92, 4, 30, 119, 595, 'که سعی و تلاش شما مختلف است:'),
(6063, 6063, 'فَأَمَّا مَنْ أَعْطَىٰ وَاتَّقَىٰ', 92, 5, 30, 119, 595, 'امّا آن کس که (در راه خدا) انفاق کند و پرهیزگاری پیش گیرد،'),
(6064, 6064, 'وَصَدَّقَ بِالْحُسْنَىٰ', 92, 6, 30, 119, 595, 'و جزای نیک (الهی) را تصدیق کند،'),
(6065, 6065, 'فَسَنُيَسِّرُهُ لِلْيُسْرَىٰ', 92, 7, 30, 119, 595, 'ما او را در مسیر آسانی قرار می‌دهیم!'),
(6066, 6066, 'وَأَمَّا مَنْ بَخِلَ وَاسْتَغْنَىٰ', 92, 8, 30, 119, 595, 'امّا کسی که بخل ورزد و (از این راه) بی‌نیازی طلبد،'),
(6067, 6067, 'وَكَذَّبَ بِالْحُسْنَىٰ', 92, 9, 30, 119, 595, 'و پاداش نیک (الهی) را انکار کند،'),
(6068, 6068, 'فَسَنُيَسِّرُهُ لِلْعُسْرَىٰ', 92, 10, 30, 119, 595, 'بزودی او را در مسیر دشواری قرار می‌دهیم؛'),
(6069, 6069, 'وَمَا يُغْنِي عَنْهُ مَالُهُ إِذَا تَرَدَّىٰ', 92, 11, 30, 119, 595, 'و در آن هنگام که (در جهنّم) سقوط می‌کند، اموالش به حال او سودی نخواهد داشت!'),
(6070, 6070, 'إِنَّ عَلَيْنَا لَلْهُدَىٰ', 92, 12, 30, 119, 595, 'به یقین هدایت کردن بر ماست،'),
(6071, 6071, 'وَإِنَّ لَنَا لَلْآخِرَةَ وَالْأُولَىٰ', 92, 13, 30, 119, 595, 'و آخرت و دنیا از آن ماست،'),
(6072, 6072, 'فَأَنْذَرْتُكُمْ نَارًا تَلَظَّىٰ', 92, 14, 30, 119, 595, 'و من شما را از آتشی که زبانه می‌کشد بیم می‌دهم،'),
(6073, 6073, 'لَا يَصْلَاهَا إِلَّا الْأَشْقَى', 92, 15, 30, 119, 596, 'کسی جز بدبخت‌ترین مردم وارد آن نمی‌شود؛'),
(6074, 6074, 'الَّذِي كَذَّبَ وَتَوَلَّىٰ', 92, 16, 30, 119, 596, 'همان کس که (آیات خدا را) تکذیب کرد و به آن پشت نمود!'),
(6075, 6075, 'وَسَيُجَنَّبُهَا الْأَتْقَى', 92, 17, 30, 119, 596, 'و بزودی با تقواترین مردم از آن دور داشته می‌شود،'),
(6076, 6076, 'الَّذِي يُؤْتِي مَالَهُ يَتَزَكَّىٰ', 92, 18, 30, 119, 596, 'همان کس که مال خود را (در راه خدا) می‌بخشد تا پاک شود.'),
(6077, 6077, 'وَمَا لِأَحَدٍ عِنْدَهُ مِنْ نِعْمَةٍ تُجْزَىٰ', 92, 19, 30, 119, 596, 'و هیچ کس را نزد او حق نعمتی نیست تا بخواهد (به این وسیله) او را جزا دهد،'),
(6078, 6078, 'إِلَّا ابْتِغَاءَ وَجْهِ رَبِّهِ الْأَعْلَىٰ', 92, 20, 30, 119, 596, 'بلکه تنها هدفش جلب رضای پروردگار بزرگ اوست؛'),
(6079, 6079, 'وَلَسَوْفَ يَرْضَىٰ', 92, 21, 30, 119, 596, 'و بزودی راضی و خشنود می‌شود!'),
(6080, 6080, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ وَالضُّحَىٰ', 93, 1, 30, 119, 596, 'قسم به روز در آن هنگام که آفتاب برآید (و همه جا را فراگیرد)،'),
(6081, 6081, 'وَاللَّيْلِ إِذَا سَجَىٰ', 93, 2, 30, 119, 596, 'و سوگند به شب در آن هنگام که آرام گیرد،'),
(6082, 6082, 'مَا وَدَّعَكَ رَبُّكَ وَمَا قَلَىٰ', 93, 3, 30, 119, 596, 'که خداوند هرگز تو را وانگذاشته و مورد خشم قرار نداده است!'),
(6083, 6083, 'وَلَلْآخِرَةُ خَيْرٌ لَكَ مِنَ الْأُولَىٰ', 93, 4, 30, 119, 596, 'و مسلّماً آخرت برای تو از دنیا بهتر است!'),
(6084, 6084, 'وَلَسَوْفَ يُعْطِيكَ رَبُّكَ فَتَرْضَىٰ', 93, 5, 30, 119, 596, 'و بزودی پروردگارت آنقدر به تو عطا خواهد کرد که خشنود شوی!'),
(6085, 6085, 'أَلَمْ يَجِدْكَ يَتِيمًا فَآوَىٰ', 93, 6, 30, 119, 596, 'آیا او تو را یتیم نیافت و پناه داد؟!'),
(6086, 6086, 'وَوَجَدَكَ ضَالًّا فَهَدَىٰ', 93, 7, 30, 119, 596, 'و تو را گمشده یافت و هدایت کرد،'),
(6087, 6087, 'وَوَجَدَكَ عَائِلًا فَأَغْنَىٰ', 93, 8, 30, 119, 596, 'و تو را فقیر یافت و بی‌نیاز نمود،'),
(6088, 6088, 'فَأَمَّا الْيَتِيمَ فَلَا تَقْهَرْ', 93, 9, 30, 119, 596, 'حال که چنین است یتیم را تحقیر مکن،'),
(6089, 6089, 'وَأَمَّا السَّائِلَ فَلَا تَنْهَرْ', 93, 10, 30, 119, 596, 'و سؤال‌کننده را از خود مران،'),
(6090, 6090, 'وَأَمَّا بِنِعْمَةِ رَبِّكَ فَحَدِّثْ', 93, 11, 30, 119, 596, 'و نعمتهای پروردگارت را بازگو کن!'),
(6091, 6091, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ أَلَمْ نَشْرَحْ لَكَ صَدْرَكَ', 94, 1, 30, 120, 596, 'آیا ما سینه تو را گشاده نساختیم،'),
(6092, 6092, 'وَوَضَعْنَا عَنْكَ وِزْرَكَ', 94, 2, 30, 120, 596, 'و بار سنگین تو را از تو برنداشتیم؟!'),
(6093, 6093, 'الَّذِي أَنْقَضَ ظَهْرَكَ', 94, 3, 30, 120, 596, 'همان باری که سخت بر پشت تو سنگینی می‌کرد!'),
(6094, 6094, 'وَرَفَعْنَا لَكَ ذِكْرَكَ', 94, 4, 30, 120, 596, 'و آوازه تو را بلند ساختیم!'),
(6095, 6095, 'فَإِنَّ مَعَ الْعُسْرِ يُسْرًا', 94, 5, 30, 120, 596, 'به یقین با (هر) سختی آسانی است!'),
(6096, 6096, 'إِنَّ مَعَ الْعُسْرِ يُسْرًا', 94, 6, 30, 120, 596, '(آری) مسلّماً با (هر) سختی آسانی است،'),
(6097, 6097, 'فَإِذَا فَرَغْتَ فَانْصَبْ', 94, 7, 30, 120, 596, 'پس هنگامی که از کار مهمّی فارغ می‌شوی به مهم دیگری پرداز،'),
(6098, 6098, 'وَإِلَىٰ رَبِّكَ فَارْغَبْ', 94, 8, 30, 120, 596, 'و به سوی پروردگارت توجّه کن!'),
(6099, 6099, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ وَالتِّينِ وَالزَّيْتُونِ', 95, 1, 30, 120, 597, 'قسم به انجیر و زیتون یا: قسم به سرزمین شام و بیت المقدّس‌،'),
(6100, 6100, 'وَطُورِ سِينِينَ', 95, 2, 30, 120, 597, 'و سوگند به «طور سینین»،'),
(6101, 6101, 'وَهَٰذَا الْبَلَدِ الْأَمِينِ', 95, 3, 30, 120, 597, 'و قسم به این شهر امن = مکّه‌،'),
(6102, 6102, 'لَقَدْ خَلَقْنَا الْإِنْسَانَ فِي أَحْسَنِ تَقْوِيمٍ', 95, 4, 30, 120, 597, 'که ما انسان را در بهترین صورت و نظام آفریدیم،'),
(6103, 6103, 'ثُمَّ رَدَدْنَاهُ أَسْفَلَ سَافِلِينَ', 95, 5, 30, 120, 597, 'سپس او را به پایین‌ترین مرحله بازگرداندیم،'),
(6104, 6104, 'إِلَّا الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ فَلَهُمْ أَجْرٌ غَيْرُ مَمْنُونٍ', 95, 6, 30, 120, 597, 'مگر کسانی که ایمان آورده و اعمال صالح انجام داده‌اند که برای آنها پاداشی تمام‌نشدنی است!'),
(6105, 6105, 'فَمَا يُكَذِّبُكَ بَعْدُ بِالدِّينِ', 95, 7, 30, 120, 597, 'پس چه چیز سبب می‌شود که بعد از این همه (دلایل روشن) روز جزا را انکار کنی؟!'),
(6106, 6106, 'أَلَيْسَ اللَّهُ بِأَحْكَمِ الْحَاكِمِينَ', 95, 8, 30, 120, 597, 'آیا خداوند بهترین حکم‌کنندگان نیست؟!'),
(6107, 6107, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ اقْرَأْ بِاسْمِ رَبِّكَ الَّذِي خَلَقَ', 96, 1, 30, 120, 597, 'بخوان به نام پروردگارت که (جهان را) آفرید،'),
(6108, 6108, 'خَلَقَ الْإِنْسَانَ مِنْ عَلَقٍ', 96, 2, 30, 120, 597, 'همان کس که انسان را از خون بسته‌ای خلق کرد!'),
(6109, 6109, 'اقْرَأْ وَرَبُّكَ الْأَكْرَمُ', 96, 3, 30, 120, 597, 'بخوان که پروردگارت (از همه) بزرگوارتر است،'),
(6110, 6110, 'الَّذِي عَلَّمَ بِالْقَلَمِ', 96, 4, 30, 120, 597, 'همان کسی که بوسیله قلم تعلیم نمود،'),
(6111, 6111, 'عَلَّمَ الْإِنْسَانَ مَا لَمْ يَعْلَمْ', 96, 5, 30, 120, 597, 'و به انسان آنچه را نمی‌دانست یاد داد!'),
(6112, 6112, 'كَلَّا إِنَّ الْإِنْسَانَ لَيَطْغَىٰ', 96, 6, 30, 120, 597, 'چنین نیست (که شما می‌پندارید) به یقین انسان طغیان می‌کند،'),
(6113, 6113, 'أَنْ رَآهُ اسْتَغْنَىٰ', 96, 7, 30, 120, 597, 'از اینکه خود را بی‌نیاز ببیند!'),
(6114, 6114, 'إِنَّ إِلَىٰ رَبِّكَ الرُّجْعَىٰ', 96, 8, 30, 120, 597, 'و به یقین بازگشت (همه) به سوی پروردگار تو است!'),
(6115, 6115, 'أَرَأَيْتَ الَّذِي يَنْهَىٰ', 96, 9, 30, 120, 597, 'به من خبر ده آیا کسی که نهی می‌کند،'),
(6116, 6116, 'عَبْدًا إِذَا صَلَّىٰ', 96, 10, 30, 120, 597, 'بنده‌ای را به هنگامی که نماز می‌خواند (آیا مستحق عذاب الهی نیست)؟!'),
(6117, 6117, 'أَرَأَيْتَ إِنْ كَانَ عَلَى الْهُدَىٰ', 96, 11, 30, 120, 597, 'به من خبر ده اگر این بنده به راه هدایت باشد،'),
(6118, 6118, 'أَوْ أَمَرَ بِالتَّقْوَىٰ', 96, 12, 30, 120, 597, 'یا مردم را به تقوا فرمان دهد (آیا نهی کردن او سزاوار است)؟!'),
(6119, 6119, 'أَرَأَيْتَ إِنْ كَذَّبَ وَتَوَلَّىٰ', 96, 13, 30, 120, 597, 'به من خبر ده اگر (این طغیانگر) حق را انکار کند و به آن پشت نماید (آیا مستحق مجازات الهی نیست)؟!'),
(6120, 6120, 'أَلَمْ يَعْلَمْ بِأَنَّ اللَّهَ يَرَىٰ', 96, 14, 30, 120, 597, 'آیا او ندانست که خداوند (همه اعمالش را) می‌بیند؟!'),
(6121, 6121, 'كَلَّا لَئِنْ لَمْ يَنْتَهِ لَنَسْفَعًا بِالنَّاصِيَةِ', 96, 15, 30, 120, 597, 'چنان نیست که او خیال می‌کند، اگر دست از کار خود برندارد، ناصیه‌اش = موی پیش سرش‌ را گرفته (و به سوی عذاب می‌کشانیم)،'),
(6122, 6122, 'نَاصِيَةٍ كَاذِبَةٍ خَاطِئَةٍ', 96, 16, 30, 120, 597, 'همان ناصیه دروغگوی خطاکار را!'),
(6123, 6123, 'فَلْيَدْعُ نَادِيَهُ', 96, 17, 30, 120, 597, 'سپس هر که را می‌خواهد صدا بزند (تا یاریش کند)!'),
(6124, 6124, 'سَنَدْعُ الزَّبَانِيَةَ', 96, 18, 30, 120, 597, 'ما هم بزودی مأموران دوزخ را صدا می‌زنیم (تا او را به دوزخ افکنند)!'),
(6125, 6125, 'كَلَّا لَا تُطِعْهُ وَاسْجُدْ وَاقْتَرِبْ ۩', 96, 19, 30, 120, 597, 'چنان نیست (که آن طغیانگر می‌پندارد)؛ هرگز او را اطاعت مکن، و سجده نما و (به خدا) تقرّب جوی!'),
(6126, 6126, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ إِنَّا أَنْزَلْنَاهُ فِي لَيْلَةِ الْقَدْرِ', 97, 1, 30, 120, 598, 'ما آن = قرآن‌ را در شب قدر نازل کردیم!'),
(6127, 6127, 'وَمَا أَدْرَاكَ مَا لَيْلَةُ الْقَدْرِ', 97, 2, 30, 120, 598, 'و تو چه می‌دانی شب قدر چیست؟!'),
(6128, 6128, 'لَيْلَةُ الْقَدْرِ خَيْرٌ مِنْ أَلْفِ شَهْرٍ', 97, 3, 30, 120, 598, 'شب قدر بهتر از هزار ماه است!'),
(6129, 6129, 'تَنَزَّلُ الْمَلَائِكَةُ وَالرُّوحُ فِيهَا بِإِذْنِ رَبِّهِمْ مِنْ كُلِّ أَمْرٍ', 97, 4, 30, 120, 598, 'فرشتگان و «روح» در آن شب به اذن پروردگارشان برای (تقدیر) هر کاری نازل می‌شوند.'),
(6130, 6130, 'سَلَامٌ هِيَ حَتَّىٰ مَطْلَعِ الْفَجْرِ', 97, 5, 30, 120, 598, 'شبی است سرشار از سلامت (و برکت و رحمت) تا طلوع سپیده!'),
(6131, 6131, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ لَمْ يَكُنِ الَّذِينَ كَفَرُوا مِنْ أَهْلِ الْكِتَابِ وَالْمُشْرِكِينَ مُنْفَكِّينَ حَتَّىٰ تَأْتِيَهُمُ الْبَيِّنَةُ', 98, 1, 30, 120, 598, 'کافران از اهل کتاب و مشرکان (می‌گفتند:) دست از آیین خود برنمی‌دارند تا دلیل روشنی برای آنها بیاید،'),
(6132, 6132, 'رَسُولٌ مِنَ اللَّهِ يَتْلُو صُحُفًا مُطَهَّرَةً', 98, 2, 30, 120, 598, 'پیامبری از سوی خدا (بیاید) که صحیفه‌های پاکی را (بر آنها) بخواند،'),
(6133, 6133, 'فِيهَا كُتُبٌ قَيِّمَةٌ', 98, 3, 30, 120, 598, 'و در آن نوشته‌های صحیح و پرارزشی باشد! (ولی هنگامی که آمد ایمان نیاوردند، مانند اهل کتاب).'),
(6134, 6134, 'وَمَا تَفَرَّقَ الَّذِينَ أُوتُوا الْكِتَابَ إِلَّا مِنْ بَعْدِ مَا جَاءَتْهُمُ الْبَيِّنَةُ', 98, 4, 30, 120, 598, 'اهل کتاب (نیز در دین خدا) اختلاف نکردند مگر بعد از آنکه دلیل روشن برای آنان آمد!'),
(6135, 6135, 'وَمَا أُمِرُوا إِلَّا لِيَعْبُدُوا اللَّهَ مُخْلِصِينَ لَهُ الدِّينَ حُنَفَاءَ وَيُقِيمُوا الصَّلَاةَ وَيُؤْتُوا الزَّكَاةَ ۚ وَذَٰلِكَ دِينُ الْقَيِّمَةِ', 98, 5, 30, 120, 598, 'و به آنها دستوری داده نشده بود جز اینکه خدا را بپرستند در حالی که دین خود را برای او خالص کنند و از شرک به توحید بازگردند، نماز را برپا دارند و زکات را بپردازند؛ و این است آیین مستقیم و پایدار!'),
(6136, 6136, 'إِنَّ الَّذِينَ كَفَرُوا مِنْ أَهْلِ الْكِتَابِ وَالْمُشْرِكِينَ فِي نَارِ جَهَنَّمَ خَالِدِينَ فِيهَا ۚ أُولَٰئِكَ هُمْ شَرُّ الْبَرِيَّةِ', 98, 6, 30, 120, 598, 'کافران از اهل کتاب و مشرکان در آتش دوزخند، جاودانه در آن می‌مانند؛ آنها بدترین مخلوقاتند!'),
(6137, 6137, 'إِنَّ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ أُولَٰئِكَ هُمْ خَيْرُ الْبَرِيَّةِ', 98, 7, 30, 120, 598, '(امّا) کسانی که ایمان آوردند و اعمال صالح انجام دادند، بهترین مخلوقات (خدا) یند!'),
(6138, 6138, 'جَزَاؤُهُمْ عِنْدَ رَبِّهِمْ جَنَّاتُ عَدْنٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهَارُ خَالِدِينَ فِيهَا أَبَدًا ۖ رَضِيَ اللَّهُ عَنْهُمْ وَرَضُوا عَنْهُ ۚ ذَٰلِكَ لِمَنْ خَشِيَ رَبَّهُ', 98, 8, 30, 120, 599, 'پاداش آنها نزد پروردگارشان باغهای بهشت جاویدان است که نهرها از زیر درختانش جاری است؛ همیشه در آن می‌مانند! (هم) خدا از آنها خشنود است و (هم) آنها از خدا خشنودند؛ و این (مقام والا) برای کسی است که از پروردگارش بترسد!'),
(6139, 6139, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ إِذَا زُلْزِلَتِ الْأَرْضُ زِلْزَالَهَا', 99, 1, 30, 120, 599, 'هنگامی که زمین شدیداً به لرزه درآید،'),
(6140, 6140, 'وَأَخْرَجَتِ الْأَرْضُ أَثْقَالَهَا', 99, 2, 30, 120, 599, 'و زمین بارهای سنگینش را خارج سازد!'),
(6141, 6141, 'وَقَالَ الْإِنْسَانُ مَا لَهَا', 99, 3, 30, 120, 599, 'و انسان می‌گوید: «زمین را چه می‌شود (که این گونه می‌لرزد)؟!»'),
(6142, 6142, 'يَوْمَئِذٍ تُحَدِّثُ أَخْبَارَهَا', 99, 4, 30, 120, 599, 'در آن روز زمین تمام خبرهایش را بازگو می‌کند؛'),
(6143, 6143, 'بِأَنَّ رَبَّكَ أَوْحَىٰ لَهَا', 99, 5, 30, 120, 599, 'چرا که پروردگارت به او وحی کرده است!'),
(6144, 6144, 'يَوْمَئِذٍ يَصْدُرُ النَّاسُ أَشْتَاتًا لِيُرَوْا أَعْمَالَهُمْ', 99, 6, 30, 120, 599, 'در آن روز مردم بصورت گروه‌های پراکنده (از قبرها) خارج می‌شوند تا اعمالشان به آنها نشان داده شود!'),
(6145, 6145, 'فَمَنْ يَعْمَلْ مِثْقَالَ ذَرَّةٍ خَيْرًا يَرَهُ', 99, 7, 30, 120, 599, 'پس هر کس هموزن ذرّه‌ای کار خیر انجام دهد آن را می‌بیند!'),
(6146, 6146, 'وَمَنْ يَعْمَلْ مِثْقَالَ ذَرَّةٍ شَرًّا يَرَهُ', 99, 8, 30, 120, 599, 'و هر کس هموزن ذرّه‌ای کار بد کرده آن را می‌بیند!'),
(6147, 6147, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ وَالْعَادِيَاتِ ضَبْحًا', 100, 1, 30, 120, 599, 'سوگند به اسبان دونده (مجاهدان) در حالی که نفس‌زنان به پیش می‌رفتند،'),
(6148, 6148, 'فَالْمُورِيَاتِ قَدْحًا', 100, 2, 30, 120, 599, 'و سوگند به افروزندگان جرقه آتش (در برخورد سمهایشان با سنگهای بیابان)،'),
(6149, 6149, 'فَالْمُغِيرَاتِ صُبْحًا', 100, 3, 30, 120, 599, 'و سوگند به هجوم آوران سپیده دم‌'),
(6150, 6150, 'فَأَثَرْنَ بِهِ نَقْعًا', 100, 4, 30, 120, 599, 'که گرد و غبار به هر سو پراکندند،'),
(6151, 6151, 'فَوَسَطْنَ بِهِ جَمْعًا', 100, 5, 30, 120, 599, 'و (ناگهان) در میان دشمن ظاهر شدند،'),
(6152, 6152, 'إِنَّ الْإِنْسَانَ لِرَبِّهِ لَكَنُودٌ', 100, 6, 30, 120, 599, 'که انسان در برابر نعمتهای پروردگارش بسیار ناسپاس و بخیل است؛'),
(6153, 6153, 'وَإِنَّهُ عَلَىٰ ذَٰلِكَ لَشَهِيدٌ', 100, 7, 30, 120, 599, 'و او خود (نیز) بر این معنی گواه است!'),
(6154, 6154, 'وَإِنَّهُ لِحُبِّ الْخَيْرِ لَشَدِيدٌ', 100, 8, 30, 120, 599, 'و او علاقه شدید به مال دارد!'),
(6155, 6155, ' أَفَلَا يَعْلَمُ إِذَا بُعْثِرَ مَا فِي الْقُبُورِ', 100, 9, 30, 120, 599, 'آیا نمی‌داند در آن روز که تمام کسانی که در قبرها هستند برانگیخته می‌شوند،'),
(6156, 6156, 'وَحُصِّلَ مَا فِي الصُّدُورِ', 100, 10, 30, 120, 600, 'و آنچه در درون سینه‌هاست آشکار می‌گردد،'),
(6157, 6157, 'إِنَّ رَبَّهُمْ بِهِمْ يَوْمَئِذٍ لَخَبِيرٌ', 100, 11, 30, 120, 600, 'در آن روز پروردگارشان از آنها کاملاً باخبر است!'),
(6158, 6158, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ الْقَارِعَةُ', 101, 1, 30, 120, 600, 'آن حادثه کوبنده،'),
(6159, 6159, 'مَا الْقَارِعَةُ', 101, 2, 30, 120, 600, 'و چه حادثه کوبنده‌ای!'),
(6160, 6160, 'وَمَا أَدْرَاكَ مَا الْقَارِعَةُ', 101, 3, 30, 120, 600, 'و تو چه می‌دانی که حادثه کوبنده چیست؟! (آن حادثه همان روز قیامت است!)'),
(6161, 6161, 'يَوْمَ يَكُونُ النَّاسُ كَالْفَرَاشِ الْمَبْثُوثِ', 101, 4, 30, 120, 600, 'روزی که مردم مانند پروانه‌های پراکنده خواهند بود،'),
(6162, 6162, 'وَتَكُونُ الْجِبَالُ كَالْعِهْنِ الْمَنْفُوشِ', 101, 5, 30, 120, 600, 'و کوه‌ها مانند پشم رنگین حلاّجی‌شده می‌گردد!'),
(6163, 6163, 'فَأَمَّا مَنْ ثَقُلَتْ مَوَازِينُهُ', 101, 6, 30, 120, 600, 'امّا کسی که (در آن روز) ترازوهای اعمالش سنگین است،'),
(6164, 6164, 'فَهُوَ فِي عِيشَةٍ رَاضِيَةٍ', 101, 7, 30, 120, 600, 'در یک زندگی خشنودکننده خواهد بود!'),
(6165, 6165, 'وَأَمَّا مَنْ خَفَّتْ مَوَازِينُهُ', 101, 8, 30, 120, 600, 'و امّا کسی که ترازوهایش سبک است،'),
(6166, 6166, 'فَأُمُّهُ هَاوِيَةٌ', 101, 9, 30, 120, 600, 'پناهگاهش «هاویه» = دوزخ‌ است!'),
(6167, 6167, 'وَمَا أَدْرَاكَ مَا هِيَهْ', 101, 10, 30, 120, 600, 'و تو چه می‌دانی «هاویه» چیست؟!'),
(6168, 6168, 'نَارٌ حَامِيَةٌ', 101, 11, 30, 120, 600, 'آتشی است سوزان!'),
(6169, 6169, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ أَلْهَاكُمُ التَّكَاثُرُ', 102, 1, 30, 120, 600, 'افزون طلبی (و تفاخر) شما را به خود مشغول داشته (و از خدا غافل نموده) است.'),
(6170, 6170, 'حَتَّىٰ زُرْتُمُ الْمَقَابِرَ', 102, 2, 30, 120, 600, 'تا آنجا که به دیدار قبرها رفتید (و قبور مردگان خود را برشمردید و به آن افتخار کردید)!'),
(6171, 6171, 'كَلَّا سَوْفَ تَعْلَمُونَ', 102, 3, 30, 120, 600, 'چنین نیست که می‌پندارید، (آری) بزودی خواهید دانست!'),
(6172, 6172, 'ثُمَّ كَلَّا سَوْفَ تَعْلَمُونَ', 102, 4, 30, 120, 600, 'باز چنان نیست که شما می‌پندارید؛ بزودی خواهید دانست!'),
(6173, 6173, 'كَلَّا لَوْ تَعْلَمُونَ عِلْمَ الْيَقِينِ', 102, 5, 30, 120, 600, 'چنان نیست که شما خیال می‌کنید؛ اگر شما علم الیقین (به آخرت) داشتید (افزون طلبی شما را از خدا غافل نمی‌کرد)!'),
(6174, 6174, 'لَتَرَوُنَّ الْجَحِيمَ', 102, 6, 30, 120, 600, 'قطعاً شما جهنّم را خواهید دید!'),
(6175, 6175, 'ثُمَّ لَتَرَوُنَّهَا عَيْنَ الْيَقِينِ', 102, 7, 30, 120, 600, 'سپس (با ورود در آن) آن را به عین الیقین خواهید دید.'),
(6176, 6176, 'ثُمَّ لَتُسْأَلُنَّ يَوْمَئِذٍ عَنِ النَّعِيمِ', 102, 8, 30, 120, 600, 'سپس در آن روز (همه شما) از نعمتهایی که داشته‌اید بازپرسی خواهید شد!'),
(6177, 6177, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ وَالْعَصْرِ', 103, 1, 30, 120, 601, 'به عصر سوگند،'),
(6178, 6178, 'إِنَّ الْإِنْسَانَ لَفِي خُسْرٍ', 103, 2, 30, 120, 601, 'که انسانها همه در زیانند؛'),
(6179, 6179, 'إِلَّا الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ وَتَوَاصَوْا بِالْحَقِّ وَتَوَاصَوْا بِالصَّبْرِ', 103, 3, 30, 120, 601, 'مگر کسانی که ایمان آورده و اعمال صالح انجام داده‌اند، و یکدیگر را به حق سفارش کرده و یکدیگر را به شکیبایی و استقامت توصیه نموده‌اند!'),
(6180, 6180, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ وَيْلٌ لِكُلِّ هُمَزَةٍ لُمَزَةٍ', 104, 1, 30, 120, 601, 'وای بر هر عیبجوی مسخره‌کننده‌ای!'),
(6181, 6181, 'الَّذِي جَمَعَ مَالًا وَعَدَّدَهُ', 104, 2, 30, 120, 601, 'همان کس که مال فراوانی جمع‌آوری و شماره کرده (بی‌آنکه مشروع و نامشروع آن را حساب کند)!'),
(6182, 6182, 'يَحْسَبُ أَنَّ مَالَهُ أَخْلَدَهُ', 104, 3, 30, 120, 601, 'او گمان می‌کند که اموالش او را جاودانه می‌سازد!'),
(6183, 6183, 'كَلَّا ۖ لَيُنْبَذَنَّ فِي الْحُطَمَةِ', 104, 4, 30, 120, 601, 'چنین نیست که می‌پندارد؛ بزودی در «حُطَمه» = آتشی خردکننده‌ پرتاب می‌شود!'),
(6184, 6184, 'وَمَا أَدْرَاكَ مَا الْحُطَمَةُ', 104, 5, 30, 120, 601, 'و تو چه می‌دانی «حُطمه» چیست؟!'),
(6185, 6185, 'نَارُ اللَّهِ الْمُوقَدَةُ', 104, 6, 30, 120, 601, 'آتش برافروخته الهی است،'),
(6186, 6186, 'الَّتِي تَطَّلِعُ عَلَى الْأَفْئِدَةِ', 104, 7, 30, 120, 601, 'آتشی که از دلها سرمی‌زند!'),
(6187, 6187, 'إِنَّهَا عَلَيْهِمْ مُؤْصَدَةٌ', 104, 8, 30, 120, 601, 'این آتش بر آنها فروبسته شده،'),
(6188, 6188, 'فِي عَمَدٍ مُمَدَّدَةٍ', 104, 9, 30, 120, 601, 'در ستونهای کشیده و طولانی!'),
(6189, 6189, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ أَلَمْ تَرَ كَيْفَ فَعَلَ رَبُّكَ بِأَصْحَابِ الْفِيلِ', 105, 1, 30, 120, 601, 'آیا ندیدی پروردگارت با فیل سواران = لشکر ابرهه که برای نابودی کعبه آمده بودند چه کرد؟!'),
(6190, 6190, 'أَلَمْ يَجْعَلْ كَيْدَهُمْ فِي تَضْلِيلٍ', 105, 2, 30, 120, 601, 'آیا نقشه آنها را در ضلالت و تباهی قرار نداد؟!'),
(6191, 6191, 'وَأَرْسَلَ عَلَيْهِمْ طَيْرًا أَبَابِيلَ', 105, 3, 30, 120, 601, 'و بر سر آنها پرندگانی را گروه گروه فرستاد،'),
(6192, 6192, 'تَرْمِيهِمْ بِحِجَارَةٍ مِنْ سِجِّيلٍ', 105, 4, 30, 120, 601, 'که با سنگهای کوچکی آنان را هدف قرارمی‌دادند؛'),
(6193, 6193, 'فَجَعَلَهُمْ كَعَصْفٍ مَأْكُولٍ', 105, 5, 30, 120, 601, 'سرانجام آنها را همچون کاه خورده‌شده (و متلاشی) قرار داد!'),
(6194, 6194, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ لِإِيلَافِ قُرَيْشٍ', 106, 1, 30, 120, 602, '(کیفر لشکر فیل‌سواران) بخاطر این بود که قریش (به این سرزمین مقدس) الفت گیرند (و زمینه ظهور پیامبر فراهم شود)!'),
(6195, 6195, 'إِيلَافِهِمْ رِحْلَةَ الشِّتَاءِ وَالصَّيْفِ', 106, 2, 30, 120, 602, 'الفت آنها در سفرهای زمستانه و تابستانه (و بخاطر این الفت به آن بازگردند)!'),
(6196, 6196, 'فَلْيَعْبُدُوا رَبَّ هَٰذَا الْبَيْتِ', 106, 3, 30, 120, 602, 'پس (بشکرانه این نعمت بزرگ) باید پروردگار این خانه را عبادت کنند،'),
(6197, 6197, 'الَّذِي أَطْعَمَهُمْ مِنْ جُوعٍ وَآمَنَهُمْ مِنْ خَوْفٍ', 106, 4, 30, 120, 602, 'همان کس که آنها را از گرسنگی نجات داد و از ترس و ناامنی ایمن ساخت.'),
(6198, 6198, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ أَرَأَيْتَ الَّذِي يُكَذِّبُ بِالدِّينِ', 107, 1, 30, 120, 602, 'آیا کسی که روز جزا را پیوسته انکار می‌کند دیدی؟'),
(6199, 6199, 'فَذَٰلِكَ الَّذِي يَدُعُّ الْيَتِيمَ', 107, 2, 30, 120, 602, 'او همان کسی است که یتیم را با خشونت می‌راند،'),
(6200, 6200, 'وَلَا يَحُضُّ عَلَىٰ طَعَامِ الْمِسْكِينِ', 107, 3, 30, 120, 602, 'و (دیگران را) به اطعام مسکین تشویق نمی‌کند!'),
(6201, 6201, 'فَوَيْلٌ لِلْمُصَلِّينَ', 107, 4, 30, 120, 602, 'پس وای بر نمازگزارانی که...'),
(6202, 6202, 'الَّذِينَ هُمْ عَنْ صَلَاتِهِمْ سَاهُونَ', 107, 5, 30, 120, 602, 'در نماز خود سهل‌انگاری می‌کنند،'),
(6203, 6203, 'الَّذِينَ هُمْ يُرَاءُونَ', 107, 6, 30, 120, 602, 'همان کسانی که ریا می‌کنند،'),
(6204, 6204, 'وَيَمْنَعُونَ الْمَاعُونَ', 107, 7, 30, 120, 602, 'و دیگران را از وسایل ضروری زندگی منع می‌نمایند!'),
(6205, 6205, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ إِنَّا أَعْطَيْنَاكَ الْكَوْثَرَ', 108, 1, 30, 120, 602, 'ما به تو کوثر = خیر و برکت فراوان‌ عطا کردیم!'),
(6206, 6206, 'فَصَلِّ لِرَبِّكَ وَانْحَرْ', 108, 2, 30, 120, 602, 'پس برای پروردگارت نماز بخوان و قربانی کن!'),
(6207, 6207, 'إِنَّ شَانِئَكَ هُوَ الْأَبْتَرُ', 108, 3, 30, 120, 602, '(و بدان) دشمن تو قطعاً بریده‌نسل و بی‌عقب است!'),
(6208, 6208, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ قُلْ يَا أَيُّهَا الْكَافِرُونَ', 109, 1, 30, 120, 603, 'بگو: ای کافران!'),
(6209, 6209, 'لَا أَعْبُدُ مَا تَعْبُدُونَ', 109, 2, 30, 120, 603, 'آنچه را شما می‌پرستید من نمی‌پرستم!'),
(6210, 6210, 'وَلَا أَنْتُمْ عَابِدُونَ مَا أَعْبُدُ', 109, 3, 30, 120, 603, 'و نه شما آنچه را من می‌پرستم می‌پرستید،'),
(6211, 6211, 'وَلَا أَنَا عَابِدٌ مَا عَبَدْتُمْ', 109, 4, 30, 120, 603, 'و نه من هرگز آنچه را شما پرستش کرده‌اید می‌پرستم،'),
(6212, 6212, 'وَلَا أَنْتُمْ عَابِدُونَ مَا أَعْبُدُ', 109, 5, 30, 120, 603, 'و نه شما آنچه را که من می‌پرستم پرستش می‌کنید؛'),
(6213, 6213, 'لَكُمْ دِينُكُمْ وَلِيَ دِينِ', 109, 6, 30, 120, 603, '(حال که چنین است) آیین شما برای خودتان، و آیین من برای خودم!'),
(6214, 6214, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ إِذَا جَاءَ نَصْرُ اللَّهِ وَالْفَتْحُ', 110, 1, 30, 120, 603, 'هنگامی که یاری خدا و پیروزی فرارسد،'),
(6215, 6215, 'وَرَأَيْتَ النَّاسَ يَدْخُلُونَ فِي دِينِ اللَّهِ أَفْوَاجًا', 110, 2, 30, 120, 603, 'و ببینی مردم گروه گروه وارد دین خدا می‌شوند،'),
(6216, 6216, 'فَسَبِّحْ بِحَمْدِ رَبِّكَ وَاسْتَغْفِرْهُ ۚ إِنَّهُ كَانَ تَوَّابًا', 110, 3, 30, 120, 603, 'پروردگارت را تسبیح و حمد کن و از او آمرزش بخواه که او بسیار توبه‌پذیر است!'),
(6217, 6217, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ تَبَّتْ يَدَا أَبِي لَهَبٍ وَتَبَّ', 111, 1, 30, 120, 603, 'بریده باد هر دو دست ابولهب (و مرگ بر او باد)!'),
(6218, 6218, 'مَا أَغْنَىٰ عَنْهُ مَالُهُ وَمَا كَسَبَ', 111, 2, 30, 120, 603, 'هرگز مال و ثروتش و آنچه را به دست آورد به حالش سودی نبخشید!'),
(6219, 6219, 'سَيَصْلَىٰ نَارًا ذَاتَ لَهَبٍ', 111, 3, 30, 120, 603, 'و بزودی وارد آتشی شعله‌ور و پرلهیب می‌شود؛'),
(6220, 6220, 'وَامْرَأَتُهُ حَمَّالَةَ الْحَطَبِ', 111, 4, 30, 120, 603, 'و (نیز) همسرش، در حالی که هیزم‌کش (دوزخ) است،'),
(6221, 6221, 'فِي جِيدِهَا حَبْلٌ مِنْ مَسَدٍ', 111, 5, 30, 120, 603, 'و در گردنش طنابی است از لیف خرما!'),
(6222, 6222, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ قُلْ هُوَ اللَّهُ أَحَدٌ', 112, 1, 30, 120, 604, 'بگو: خداوند، یکتا و یگانه است؛'),
(6223, 6223, 'اللَّهُ الصَّمَدُ', 112, 2, 30, 120, 604, 'خداوندی است که همه نیازمندان قصد او می‌کنند؛'),
(6224, 6224, 'لَمْ يَلِدْ وَلَمْ يُولَدْ', 112, 3, 30, 120, 604, '(هرگز) نزاد، و زاده نشد،'),
(6225, 6225, 'وَلَمْ يَكُنْ لَهُ كُفُوًا أَحَدٌ', 112, 4, 30, 120, 604, 'و برای او هیچگاه شبیه و مانندی نبوده است!'),
(6226, 6226, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ قُلْ أَعُوذُ بِرَبِّ الْفَلَقِ', 113, 1, 30, 120, 604, 'بگو: پناه می‌برم به پروردگار سپیده صبح،'),
(6227, 6227, 'مِنْ شَرِّ مَا خَلَقَ', 113, 2, 30, 120, 604, 'از شرّ تمام آنچه آفریده است؛'),
(6228, 6228, 'وَمِنْ شَرِّ غَاسِقٍ إِذَا وَقَبَ', 113, 3, 30, 120, 604, 'و از شرّ هر موجود شرور هنگامی که شبانه وارد می‌شود؛'),
(6229, 6229, 'وَمِنْ شَرِّ النَّفَّاثَاتِ فِي الْعُقَدِ', 113, 4, 30, 120, 604, 'و از شرّ آنها که با افسون در گره‌ها می‌دمند (و هر تصمیمی را سست می‌کنند)؛'),
(6230, 6230, 'وَمِنْ شَرِّ حَاسِدٍ إِذَا حَسَدَ', 113, 5, 30, 120, 604, 'و از شرّ هر حسودی هنگامی که حسد می‌ورزد!'),
(6231, 6231, 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ قُلْ أَعُوذُ بِرَبِّ النَّاسِ', 114, 1, 30, 120, 604, 'بگو: پناه می‌برم به پروردگار مردم،'),
(6232, 6232, 'مَلِكِ النَّاسِ', 114, 2, 30, 120, 604, 'به مالک و حاکم مردم،'),
(6233, 6233, 'إِلَٰهِ النَّاسِ', 114, 3, 30, 120, 604, 'به (خدا و) معبود مردم،'),
(6234, 6234, 'مِنْ شَرِّ الْوَسْوَاسِ الْخَنَّاسِ', 114, 4, 30, 120, 604, 'از شرّ وسوسه‌گر پنهانکار،'),
(6235, 6235, 'الَّذِي يُوَسْوِسُ فِي صُدُورِ النَّاسِ \r\nمِنَ الْجِنَّةِ وَالنَّاسِ', 114, 5, 30, 120, 604, 'که در درون سینه انسانها وسوسه می‌کند،\r\nخواه از جنّ باشد یا از انسان!');
